"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3592, 'rst66', 'lev', 27, 22, 'А если кто посвятит Господу поле купленное, которое не из полей его владения,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3591, 'rst66', 'lev', 27, 21, 'поле то, когда оно в юбилей отойдет, будет святынею Господу, как бы поле заклятое; священнику достанется оно во владение.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3590, 'rst66', 'lev', 27, 20, 'если же он не выкупит поля, и будет продано поле другому человеку, то уже нельзя выкупить:');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3589, 'rst66', 'lev', 27, 19, 'если же захочет выкупить поле посвятивший его, то пусть он прибавит пятую часть серебра оценки твоей, и оно останется за ним;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3588, 'rst66', 'lev', 27, 18, 'если же после юбилея посвящает кто поле свое, то священник должен рассчитать серебро по мере лет, оставшихся до юбилейного года, и должно убавить из оценки твоей;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3587, 'rst66', 'lev', 27, 17, 'если от юбилейного года посвящает кто поле свое, &mdash; должно состояться по оценке твоей;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3586, 'rst66', 'lev', 27, 16, 'Если поле из своего владения посвятит кто Господу, то оценка твоя должна быть по мере посева: за посев хомера ячменя пятьдесят сиклей серебра;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3585, 'rst66', 'lev', 27, 15, 'если же посвятивший захочет выкупить дом свой, то пусть прибавит пятую часть серебра оценки твоей, и <i>тогда</i> будет его.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3584, 'rst66', 'lev', 27, 14, 'Если кто посвящает дом свой в святыню Господу, то священник должен оценить его, хорош ли он, или худ, и как оценит его священник, так и состоится;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3583, 'rst66', 'lev', 27, 13, 'если же кто хочет выкупить ее, то пусть прибавит пятую долю к оценке твоей.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3582, 'rst66', 'lev', 27, 12, 'и священник оценит ее, хороша ли она, или худа, и как оценит священник, так и должно быть;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3581, 'rst66', 'lev', 27, 11, 'Если же то будет какая‐нибудь скотина нечистая, которую не приносят в жертву Господу, то должно представить скотину священнику,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3580, 'rst66', 'lev', 27, 10, 'не должно выменивать его и заменять хорошее худым, или худое хорошим; если же станет кто заменять скотину скотиною, то и она, и замена ее будет святынею.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3579, 'rst66', 'lev', 27, 9, 'Если же то будет скот, который приносят в жертву Господу, то все, что дано Господу, должно быть свято:');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3578, 'rst66', 'lev', 27, 8, 'Если же он беден и не в силах <i>отдать</i> по оценке твоей, то пусть представят его священнику, и священник пусть оценит его: соразмерно с состоянием давшего обет пусть оценит его священник.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3577, 'rst66', 'lev', 27, 7, 'от шестидесяти лет и выше мужчине оценка твоя должна быть пятнадцать сиклей серебра, а женщине десять сиклей.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3576, 'rst66', 'lev', 27, 6, 'а от месяца до пяти лет оценка твоя мужчине должна быть пять сиклей серебра, а женщине оценка твоя три сикля серебра;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3575, 'rst66', 'lev', 27, 5, 'от пяти лет до двадцати оценка твоя мужчине должна быть двадцать сиклей, а женщине десять сиклей;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3574, 'rst66', 'lev', 27, 4, 'если же это женщина, то оценка твоя должна быть тридцать сиклей;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3573, 'rst66', 'lev', 27, 3, 'то оценка твоя мужчине от двадцати лет до шестидесяти должна быть пятьдесят сиклей серебряных, по сиклю священному;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3572, 'rst66', 'lev', 27, 2, 'объяви сынам Израилевым и скажи им: если кто дает обет посвятить душу Господу по оценке твоей,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3571, 'rst66', 'lev', 27, 1, 'И сказал Господь Моисею, говоря:');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3570, 'rst66', 'lev', 26, 46, 'Вот постановления, и определения, и законы, которые постановил Господь между Собою и между сынами Израилевыми на горе Синае, через Моисея.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3569, 'rst66', 'lev', 26, 45, 'вспомню для них завет с предками, которых вывел Я из земли Египетской пред глазами народов, чтоб быть их Богом. Я Господь.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3568, 'rst66', 'lev', 26, 44, 'и тогда как они будут в земле врагов их, &mdash; Я не презрю их и не возгнушаюсь ими до того, чтоб истребить их, чтоб разрушить завет Мой с ними, ибо Я Господь, Бог их;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3567, 'rst66', 'lev', 26, 43, 'тогда как земля оставлена будет ими и будет удовлетворять себя за субботы свои, опустев от них, и они будут терпеть за свое беззаконие, за то, что презирали законы Мои и душа их гнушалась постановлениями Моими,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3566, 'rst66', 'lev', 26, 42, 'И Я вспомню завет Мой с Иаковом, и завет Мой с Исааком, и завет Мой с Авраамом вспомню, и землю вспомню;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3565, 'rst66', 'lev', 26, 41, '<i>за что</i> и Я шел против них и ввел их в землю врагов их; тогда покорится необрезанное сердце их, и тогда потерпят они за беззакония свои.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3564, 'rst66', 'lev', 26, 40, 'Тогда признаются они в беззаконии своем и в беззаконии отцов своих, как они совершали преступления против Меня и шли против Меня,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3563, 'rst66', 'lev', 26, 39, 'а оставшиеся из вас исчахнут за свои беззакония в землях врагов ваших и за беззакония отцов своих исчахнут.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3562, 'rst66', 'lev', 26, 38, 'и погибнете между народами, и пожрет вас земля врагов ваших;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3561, 'rst66', 'lev', 26, 37, 'и споткнутся друг на друга, как от меча, между тем как никто не преследует, и не будет у вас силы противостоять врагам вашим;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3560, 'rst66', 'lev', 26, 36, 'Оставшимся из вас пошлю в сердца робость в земле врагов их, и шум колеблющегося листа погонит их, и побегут, как от меча, и падут, когда никто не преследует,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3559, 'rst66', 'lev', 26, 35, 'во все дни запустения <i>своего</i> будет она покоиться, сколько не покоилась в субботы ваши, когда вы жили на ней.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3558, 'rst66', 'lev', 26, 34, 'Тогда удовлетворит себя земля за субботы свои во все дни запустения <i>своего</i>; когда вы будете в земле врагов ваших, тогда будет покоиться земля и удовлетворит себя за субботы свои;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3557, 'rst66', 'lev', 26, 33, 'а вас рассею между народами и обнажу вслед вас меч, и будет земля ваша пуста и города ваши разрушены.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3556, 'rst66', 'lev', 26, 32, 'опустошу землю <i>вашу</i>, так что изумятся о ней враги ваши, поселившиеся на ней;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3555, 'rst66', 'lev', 26, 31, 'города ваши сделаю пустынею, и опустошу святилища ваши, и не буду обонять приятного благоухания <i>жертв</i> ваших;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3554, 'rst66', 'lev', 26, 30, 'разорю высоты ваши и разрушу столбы ваши, и повергну трупы ваши на обломки идолов ваших, и возгнушается душа Моя вами;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3553, 'rst66', 'lev', 26, 29, 'и будете есть плоть сынов ваших, и плоть дочерей ваших будете есть;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3552, 'rst66', 'lev', 26, 28, 'то и Я в ярости пойду против вас и накажу вас всемеро за грехи ваши,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3551, 'rst66', 'lev', 26, 27, 'Если же и после сего не послушаете Меня и пойдете против Меня,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3550, 'rst66', 'lev', 26, 26, 'хлеб, подкрепляющий <i>человека</i>, истреблю у вас; десять женщин будут печь хлеб ваш в одной печи и будут отдавать хлеб ваш весом; вы будете есть и не будете сыты.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3549, 'rst66', 'lev', 26, 25, 'и наведу на вас мстительный меч в отмщение за завет; если же вы укроетесь в города ваши, то пошлю на вас язву, и преданы будете в руки врага;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3548, 'rst66', 'lev', 26, 24, 'то и Я пойду против вас и поражу вас всемеро за грехи ваши,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3547, 'rst66', 'lev', 26, 23, 'Если и после сего не исправитесь и пойдете против Меня,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3546, 'rst66', 'lev', 26, 22, 'пошлю на вас зверей полевых, которые лишат вас детей, истребят скот ваш и вас уменьшат, так что опустеют дороги ваши.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3545, 'rst66', 'lev', 26, 21, 'Если же <i>после сего</i> пойдете против Меня и не захотите слушать Меня, то Я прибавлю вам ударов всемеро за грехи ваши:');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3544, 'rst66', 'lev', 26, 20, 'и напрасно будет истощаться сила ваша, и земля ваша не даст произрастений своих, и дерева земли не дадут плодов своих.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3543, 'rst66', 'lev', 26, 19, 'и сломлю гордое упорство ваше, и небо ваше сделаю, как железо, и землю вашу, как медь;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3542, 'rst66', 'lev', 26, 18, 'Если и при всем том не послушаете Меня, то Я всемеро увеличу наказание за грехи ваши,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3541, 'rst66', 'lev', 26, 17, 'обращу лицо Мое на вас, и падете пред врагами вашими, и будут господствовать над вами неприятели ваши, и побежите, когда никто не гонится за вами.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3540, 'rst66', 'lev', 26, 16, 'то и Я поступлю с вами так: пошлю на вас ужас, чахлость и горячку, от которых истомятся глаза и измучится душа, и будете сеять семена ваши напрасно, и враги ваши съедят их;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3539, 'rst66', 'lev', 26, 15, 'и если презрите Мои постановления, и если душа ваша возгнушается Моими законами, так что вы не будете исполнять всех заповедей Моих, нарушив завет Мой,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3538, 'rst66', 'lev', 26, 14, 'Если же не послушаете Меня и не будете исполнять всех заповедей сих,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3537, 'rst66', 'lev', 26, 13, 'Я Господь, Бог ваш, Который вывел вас из земли Египетской, чтоб вы не были там рабами, и сокрушил узы ярма вашего, и повел вас с поднятою головою.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3536, 'rst66', 'lev', 26, 12, 'и буду ходить среди вас, и буду вашим Богом, а вы будете Моим народом.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3535, 'rst66', 'lev', 26, 11, 'и поставлю жилище Мое среди вас, и душа Моя не возгнушается вами;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3534, 'rst66', 'lev', 26, 10, 'и будете есть старое прошлогоднее, и выбросите старое ради нового;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3533, 'rst66', 'lev', 26, 9, 'призрю на вас, и плодородными сделаю вас, и размножу вас, и буду тверд в завете Моем с вами;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3532, 'rst66', 'lev', 26, 8, 'пятеро из вас прогонят сто, и сто из вас прогонят тьму, и падут враги ваши пред вами от меча;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3531, 'rst66', 'lev', 26, 7, 'и будете прогонять врагов ваших, и падут они пред вами от меча;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3530, 'rst66', 'lev', 26, 6, 'пошлю мир на землю <i>вашу</i>, ляжете, и никто вас не обеспокоит, сгоню лютых зверей с земли <i>вашей</i>, и меч не пройдет по земле вашей;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3529, 'rst66', 'lev', 26, 5, 'и молотьба <i>хлеба</i> будет достигать у вас собирания винограда, собирание винограда будет достигать посева, и будете есть хлеб свой досыта, и будете жить на земле <i>вашей</i> безопасно;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3528, 'rst66', 'lev', 26, 4, 'то Я дам вам дожди в свое время, и земля даст произрастения свои, и дерева полевые дадут плод свой;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3527, 'rst66', 'lev', 26, 3, 'Если вы будете поступать по уставам Моим и заповеди Мои будете хранить и исполнять их,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3526, 'rst66', 'lev', 26, 2, 'Субботы Мои соблюдайте и святилище Мое чтите: Я Господь.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3525, 'rst66', 'lev', 26, 1, 'Не делайте себе кумиров, и изваяний, и столбов не ставьте у себя, и камней с изображениями не кладите в земле вашей, чтобы кланяться пред ними, ибо Я Господь, Бог ваш.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3524, 'rst66', 'lev', 25, 55, 'потому что сыны Израилевы Мои рабы; они Мои рабы, которых Я вывел из земли Египетской. Я Господь, Бог ваш.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3523, 'rst66', 'lev', 25, 54, 'Если же он не выкупится таким образом, то в юбилейный год отойдет сам и дети его с ним,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3522, 'rst66', 'lev', 25, 53, 'Он должен быть у него как наемник во все годы; он не должен господствовать над ним с жестокостью в глазах твоих.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3521, 'rst66', 'lev', 25, 52, 'если же мало остается лет до юбилейного года, то он должен сосчитать и по мере лет отдать за себя выкуп.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3520, 'rst66', 'lev', 25, 51, 'и если еще много <i>остается</i> лет, то по мере их он должен отдать в выкуп за себя серебро, за которое он куплен;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3519, 'rst66', 'lev', 25, 50, 'И он должен рассчитаться с купившим его, <i>начиная</i> от того года, когда он продал себя, до года юбилейного, и серебро, за которое он продал себя, должно отдать ему по числу лет; как временный наемник он должен быть у него;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3518, 'rst66', 'lev', 25, 49, 'или дядя его, или сын дяди его должен выкупить его, или кто‐нибудь из родства его, из племени его, должен выкупить его; или, если будет иметь достаток, сам выкупится.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3517, 'rst66', 'lev', 25, 48, 'то после продажи можно выкупить его; кто‐нибудь из братьев его должен выкупить его,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3516, 'rst66', 'lev', 25, 47, 'Если пришелец или поселенец твой будет иметь достаток, а брат твой пред ним обеднеет и продастся пришельцу, поселившемуся у тебя, или кому‐нибудь из племени пришельца,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3515, 'rst66', 'lev', 25, 46, 'можете передавать их в наследство и сынам вашим по себе, как имение; вечно владейте ими, как рабами. А над братьями вашими, сынами Израилевыми, друг над другом, не господствуйте с жестокостью.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3514, 'rst66', 'lev', 25, 45, 'также и из детей поселенцев, поселившихся у вас, можете покупать, и из племени их, которое у вас, которое у них родилось в земле вашей, и они могут быть вашей собственностью;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3513, 'rst66', 'lev', 25, 44, 'А чтобы раб твой и рабыня твоя были у тебя, то покупайте себе раба и рабыню у народов, которые вокруг вас;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3512, 'rst66', 'lev', 25, 43, 'не господствуй над ним с жестокостью и бойся Бога твоего.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3511, 'rst66', 'lev', 25, 42, 'потому что они &mdash; Мои рабы, которых Я вывел из земли Египетской: не должно продавать их, как продают рабов;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3510, 'rst66', 'lev', 25, 41, 'а <i>тогда</i> пусть отойдет он от тебя, сам и дети его с ним, и возвратится в племя свое, и вступит опять во владение отцов своих,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3509, 'rst66', 'lev', 25, 40, 'он должен быть у тебя как наемник, как поселенец; до юбилейного года пусть работает у тебя,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3508, 'rst66', 'lev', 25, 39, 'Когда обеднеет у тебя брат твой и продан будет тебе, то не налагай на него работы рабской:');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3507, 'rst66', 'lev', 25, 38, 'Я Господь, Бог ваш, Который вывел вас из земли Египетской, чтобы дать вам землю Ханаанскую, чтоб быть вашим Богом.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3506, 'rst66', 'lev', 25, 37, 'серебра твоего не отдавай ему в рост и хлеба твоего не отдавай ему для <i>получения</i> прибыли.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3505, 'rst66', 'lev', 25, 36, 'не бери от него роста и прибыли и бойся Бога твоего; чтоб жил брат твой с тобою;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3504, 'rst66', 'lev', 25, 35, 'Если брат твой обеднеет и придет в упадок у тебя, то поддержи его, пришлец ли он, или поселенец, чтоб он жил с тобою;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3503, 'rst66', 'lev', 25, 34, 'и полей вокруг городов их продавать нельзя, потому что это вечное владение их.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3502, 'rst66', 'lev', 25, 33, 'а кто из левитов не выкупит, то проданный дом в городе владения их в юбилей отойдет, потому что домы в городах левитских составляют их владение среди сынов Израилевых;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3501, 'rst66', 'lev', 25, 32, 'А города левитов, домы в городах владения их, левитам всегда можно выкупать;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3500, 'rst66', 'lev', 25, 31, 'А домы в селениях, вокруг которых нет стены, должно считать наравне с полем земли: выкупать их <i>всегда</i> можно, и в юбилей они отходят.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3499, 'rst66', 'lev', 25, 30, 'если же не будет он выкуплен до истечения целого года, то дом, который в городе, имеющем стену, останется навсегда у купившего его в роды его, и в юбилей не отойдет <i>от него</i>.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3498, 'rst66', 'lev', 25, 29, 'Если кто продаст жилой дом в городе, <i>огражденном</i> стеною, то выкупить его можно до истечения года от продажи его: в течение года выкупить его можно;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3497, 'rst66', 'lev', 25, 28, 'если же не найдет рука его, сколько нужно возвратить ему, то проданное им останется в руках покупщика до юбилейного года, а в юбилейный год отойдет оно, и он опять вступит во владение свое.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3496, 'rst66', 'lev', 25, 27, 'то пусть он расчислит годы продажи своей и возвратит остальное тому, кому он продал, и вступит опять во владение свое;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3495, 'rst66', 'lev', 25, 26, 'если же некому за него выкупить, но сам он будет иметь достаток и найдет, сколько нужно на выкуп,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3494, 'rst66', 'lev', 25, 25, 'Если брат твой обеднеет и продаст от владения своего, то придет близкий его родственник и выкупит проданное братом его;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3493, 'rst66', 'lev', 25, 24, 'по всей земле владения вашего дозволяйте выкуп земли.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3492, 'rst66', 'lev', 25, 23, 'Землю не должно продавать навсегда, ибо Моя земля: вы пришельцы и поселенцы у Меня;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3491, 'rst66', 'lev', 25, 22, 'и будете сеять в восьмой год, но есть будете произведения старые до девятого года; доколе не поспеют произведения его, будете есть старое.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3490, 'rst66', 'lev', 25, 21, 'Я пошлю благословение Мое на вас в шестой год, и он принесет произведений на три года;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3489, 'rst66', 'lev', 25, 20, 'Если скажете: «что же нам есть в седьмой год, когда мы не будем ни сеять, ни собирать произведений наших?»');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3488, 'rst66', 'lev', 25, 19, 'и будет земля давать плод свой, и будете есть досыта, и будете жить спокойно на ней.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3487, 'rst66', 'lev', 25, 18, 'Исполняйте постановления Мои, и храните законы Мои, и исполняйте их, и будете жить спокойно на земле;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3486, 'rst66', 'lev', 25, 17, 'Не обижайте один другого; бойся Бога твоего, ибо Я Господь, Бог ваш.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3485, 'rst66', 'lev', 25, 16, 'если много <i>остается</i> лет, умножь цену; а если мало лет <i>остается</i>, уменьши цену, ибо известное число <i>лет</i> жатв он продает тебе.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3484, 'rst66', 'lev', 25, 15, 'по расчислению лет после юбилея ты должен покупать у ближнего твоего, и по расчислению лет дохода он должен продавать тебе;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3483, 'rst66', 'lev', 25, 14, 'Если будешь продавать что ближнему твоему, или будешь покупать что у ближнего твоего, не обижайте друг друга;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3482, 'rst66', 'lev', 25, 13, 'В юбилейный год возвратитесь каждый во владение свое.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3481, 'rst66', 'lev', 25, 12, 'ибо это юбилей: священным да будет он для вас; с поля ешьте произведения ее.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3480, 'rst66', 'lev', 25, 11, 'Пятидесятый год да будет у вас юбилей: не сейте и не жните, что само вырастет на земле, и не снимайте ягод с необрезанных <i>лоз</i> ее,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3479, 'rst66', 'lev', 25, 10, 'и освятите пятидесятый год, и объявите свободу на земле всем жителям ее: да будет это у вас юбилей; и возвратитесь каждый во владение свое, и каждый возвратитесь в свое племя.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3478, 'rst66', 'lev', 25, 9, 'и воструби трубою в седьмой месяц, в десятый <i>день</i> месяца, в день очищения вострубите трубою по всей земле вашей;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3477, 'rst66', 'lev', 25, 8, 'И насчитай себе семь субботних лет, семь раз по семи лет, чтоб было у тебя в семи субботних годах сорок девять лет;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3476, 'rst66', 'lev', 25, 7, 'и скоту твоему, и зверям, которые на земле твоей, да будут все произведения ее в пищу.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3475, 'rst66', 'lev', 25, 6, 'и будет это в продолжение субботы земли <i>всем</i> вам в пищу, тебе и рабу твоему, и рабе твоей, и наемнику твоему, и поселенцу твоему, поселившемуся у тебя;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3474, 'rst66', 'lev', 25, 5, 'что само вырастет на жатве твоей, не сжинай, и гроздов с необрезанных лоз твоих не снимай; да будет это год покоя земли;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3473, 'rst66', 'lev', 25, 4, 'а в седьмой год да будет суббота покоя земли, суббота Господня: поля твоего не засевай и виноградника твоего не обрезывай;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3472, 'rst66', 'lev', 25, 3, 'шесть лет засевай поле твое, и шесть лет обрезывай виноградник твой, и собирай произведения их,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3471, 'rst66', 'lev', 25, 2, 'объяви сынам Израилевым и скажи им: когда придете в землю, которую Я даю вам, тогда земля должна покоиться в субботу Господню;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3470, 'rst66', 'lev', 25, 1, 'И сказал Господь Моисею на горе Синае, говоря:');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3469, 'rst66', 'lev', 24, 23, 'И сказал Моисей сынам Израилевым; и вывели злословившего вон из стана, и побили его камнями, и сделали сыны Израилевы, как повелел Господь Моисею.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3468, 'rst66', 'lev', 24, 22, 'Один суд должен быть у вас как для пришельца, так и для туземца; ибо Я Господь, Бог ваш.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3467, 'rst66', 'lev', 24, 21, 'Кто убьет скотину, должен заплатить за нее; а кто убьет человека, того должно предать смерти.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3466, 'rst66', 'lev', 24, 20, 'перелом за перелом, око за око, зуб за зуб; как он сделал повреждение на <i>теле</i> человека, так и ему должно сделать.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3465, 'rst66', 'lev', 24, 19, 'Кто сделает повреждение на теле ближнего своего, тому должно сделать то же, что он сделал:');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3464, 'rst66', 'lev', 24, 18, 'Кто убьет скотину, должен заплатить за нее, скотину за скотину.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3463, 'rst66', 'lev', 24, 17, 'Кто убьет какого‐либо человека, тот предан будет смерти.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3462, 'rst66', 'lev', 24, 16, 'и хулитель имени Господня должен умереть, камнями побьет его все общество: пришелец ли, туземец ли станет хулить имя <i>Господне</i>, предан будет смерти.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3461, 'rst66', 'lev', 24, 15, 'и сынам Израилевым скажи: кто будет злословить Бога своего, тот понесет грех свой;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3460, 'rst66', 'lev', 24, 14, 'выведи злословившего вон из стана, и все слышавшие пусть положат руки свои на голову его, и все общество побьет его камнями;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3459, 'rst66', 'lev', 24, 13, 'И сказал Господь Моисею, говоря:');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3458, 'rst66', 'lev', 24, 12, 'и посадили его под стражу, доколе не будет объявлена им воля Господня.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3457, 'rst66', 'lev', 24, 11, 'хулил сын Израильтянки имя <i>Господне</i> и злословил. И привели его к Моисею (имя же матери его Саломиф, дочь Давриина, из племени Данова);');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3456, 'rst66', 'lev', 24, 10, 'И вышел сын одной Израильтянки, родившийся от Египтянина, к сынам Израилевым, и поссорился в стане сын Израильтянки с Израильтянином;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3455, 'rst66', 'lev', 24, 9, 'они будут принадлежать Аарону и сынам его, которые будут есть их на святом месте, ибо это великая святыня для них из жертв Господних: <i>это</i> постановление вечное.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3454, 'rst66', 'lev', 24, 8, 'в каждый день субботы, постоянно, должно полагать их пред Господом от сынов Израилевых: это завет вечный;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3453, 'rst66', 'lev', 24, 7, 'и положи на <i>каждый</i> ряд чистого ливана, и будет это при хлебе, в память, в жертву Господу;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3452, 'rst66', 'lev', 24, 6, 'и положи их в два ряда, по шести в ряд, на чистом столе пред Господом;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3451, 'rst66', 'lev', 24, 5, 'И возьми пшеничной муки, и испеки из нее двенадцать хлебов; в каждом хлебе должны быть две десятых <i>ефы;</i>');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3450, 'rst66', 'lev', 24, 4, 'на подсвечнике чистом должны они ставить светильник пред Господом всегда.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3449, 'rst66', 'lev', 24, 3, 'вне завесы <i>ковчега</i> откровения в скинии собрания Аарон <i>и сыны его</i> должны ставить оный пред Господом от вечера до утра всегда: это вечное постановление в роды ваши;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3448, 'rst66', 'lev', 24, 2, 'прикажи сынам Израилевым, чтоб они принесли тебе елея чистого, выбитого, для освещения, чтобы непрестанно горел светильник;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3447, 'rst66', 'lev', 24, 1, 'И сказал Господь Моисею, говоря:');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3446, 'rst66', 'lev', 23, 44, 'И объявил Моисей сынам Израилевым о праздниках Господних.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3445, 'rst66', 'lev', 23, 43, 'чтобы знали роды ваши, что в кущах поселил Я сынов Израилевых, когда вывел их из земли Египетской. Я Господь, Бог ваш.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3444, 'rst66', 'lev', 23, 42, 'в кущах живите семь дней; всякий туземец Израильтянин должен жить в кущах,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3443, 'rst66', 'lev', 23, 41, 'и празднуйте этот праздник Господень семь дней в году: это постановление вечное в роды ваши; в седьмой месяц празднуйте его;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3442, 'rst66', 'lev', 23, 40, 'в первый день возьмите себе ветви красивых дерев, ветви пальмовые и ветви дерев широколиственных и верб речных, и веселитесь пред Господом, Богом вашим, семь дней;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3441, 'rst66', 'lev', 23, 39, 'А в пятнадцатый день седьмого месяца, когда вы собираете произведения земли, празднуйте праздник Господень семь дней: в первый день покой и в восьмой день покой;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3440, 'rst66', 'lev', 23, 38, 'кроме суббот Господних, и кроме даров ваших, и кроме всех обетов ваших, и кроме всего <i>приносимого</i> по усердию вашему, что вы даете Господу.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3439, 'rst66', 'lev', 23, 37, 'Вот праздники Господни, в которые должно созывать священные собрания, чтобы приносить в жертву Господу всесожжение, хлебное приношение, закалаемые жертвы и возлияния, каждое в свой день,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3438, 'rst66', 'lev', 23, 36, 'в <i>течение</i> семи дней приносите жертву Господу; в восьмой день священное собрание да будет у вас, и приносите жертву Господу: это отдание праздника, никакой работы не работайте.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3437, 'rst66', 'lev', 23, 35, 'в первый день священное собрание, никакой работы не работайте;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3436, 'rst66', 'lev', 23, 34, 'скажи сынам Израилевым: с пятнадцатого дня того же седьмого месяца праздник кущей, семь дней Господу;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3435, 'rst66', 'lev', 23, 33, 'И сказал Господь Моисею, говоря:');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3434, 'rst66', 'lev', 23, 32, 'это для вас суббота покоя, и смиряйте души ваши, с вечера девятого <i>дня</i> месяца; от вечера до вечера празднуйте субботу вашу.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3433, 'rst66', 'lev', 23, 31, 'никакого дела не делайте: это постановление вечное в роды ваши, во всех жилищах ваших;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3432, 'rst66', 'lev', 23, 30, 'и если какая душа будет делать какое‐нибудь дело в день сей, Я истреблю ту душу из народа ее;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3431, 'rst66', 'lev', 23, 29, 'а всякая душа, которая не смирит себя в этот день, истребится из народа своего;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3430, 'rst66', 'lev', 23, 28, 'никакого дела не делайте в день сей, ибо это день очищения, дабы очистить вас пред лицом Господа, Бога вашего;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3429, 'rst66', 'lev', 23, 27, 'также в девятый <i>день</i> седьмого месяца сего, день очищения, да будет у вас священное собрание; смиряйте души ваши и приносите жертву Господу;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3428, 'rst66', 'lev', 23, 26, 'И сказал Господь Моисею, говоря:');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3427, 'rst66', 'lev', 23, 25, 'Никакой работы не работайте и приносите жертву Господу.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3426, 'rst66', 'lev', 23, 24, 'скажи сынам Израилевым: в седьмой месяц, в первый <i>день</i> месяца да будет у вас покой, праздник труб, священное собрание.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3425, 'rst66', 'lev', 23, 23, 'И сказал Господь Моисею, говоря:');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3424, 'rst66', 'lev', 23, 22, 'Когда будете жать жатву на земле вашей, не дожинай до края поля твоего, когда жнешь, и оставшегося от жатвы твоей не подбирай; бедному и пришельцу оставь это. Я Господь, Бог ваш.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3423, 'rst66', 'lev', 23, 21, 'и созывайте <i>народ</i> в сей день, священное собрание да будет у вас, никакой работы не работайте: это постановление вечное во всех жилищах ваших в роды ваши.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3422, 'rst66', 'lev', 23, 20, 'священник должен принести это, потрясая пред Господом, вместе с потрясаемыми хлебами первого плода и с двумя агнцами, и это будет святынею Господу; священнику, <i>который приносит, это принадлежит</i>;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3421, 'rst66', 'lev', 23, 19, 'Приготовьте также из <i>стада</i> коз одного козла в жертву за грех и двух однолетних агнцев в жертву мирную;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3420, 'rst66', 'lev', 23, 18, 'вместе с хлебами представьте семь агнцев без порока, однолетних, и из крупного скота одного тельца и двух овнов; да будет это во всесожжение Господу, и хлебное приношение и возлияние к ним, в жертву, в приятное благоухание Господу.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3419, 'rst66', 'lev', 23, 17, 'от жилищ ваших приносите два хлеба возношения, которые должны состоять из двух десятых частей <i>ефы</i> пшеничной муки и должны быть испечены кислые, <i>как</i> первый плод Господу;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3418, 'rst66', 'lev', 23, 16, 'до первого дня после седьмой недели отсчитайте пятьдесят дней, <i>и тогда</i> принесите новое хлебное приношение Господу:');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3417, 'rst66', 'lev', 23, 15, 'Отсчитайте себе от первого дня после праздника, от того дня, в который приносите сноп потрясания, семь полных недель,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3416, 'rst66', 'lev', 23, 14, 'никакого <i>нового</i> хлеба, ни сушеных зерен, ни зерен сырых не ешьте до того дня, в который принесете приношения Богу вашему: это вечное постановление в роды ваши во всех жилищах ваших.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3415, 'rst66', 'lev', 23, 13, 'и с ним хлебного приношения две десятых части <i>ефы</i> пшеничной муки, смешанной с елеем, в жертву Господу, в приятное благоухание, и возлияния к нему четверть гина вина;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3414, 'rst66', 'lev', 23, 12, 'и в день возношения снопа принесите во всесожжение Господу агнца однолетнего, без порока,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3413, 'rst66', 'lev', 23, 11, 'он вознесет этот сноп пред Господом, чтобы вам приобрести благоволение; на другой день праздника вознесет его священник;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3412, 'rst66', 'lev', 23, 10, 'объяви сынам Израилевым и скажи им: когда придете в землю, которую Я даю вам, и будете жать на ней жатву, то принесите первый сноп жатвы вашей к священнику;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3411, 'rst66', 'lev', 23, 9, 'И сказал Господь Моисею, говоря:');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3410, 'rst66', 'lev', 23, 8, 'и в течение семи дней приносите жертвы Господу; в седьмой день также священное собрание; никакой работы не работайте.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3409, 'rst66', 'lev', 23, 7, 'в первый день да будет у вас священное собрание; никакой работы не работайте;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3408, 'rst66', 'lev', 23, 6, 'и в пятнадцатый день того же месяца праздник опресноков Господу; семь дней ешьте опресноки;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3407, 'rst66', 'lev', 23, 5, 'в первый месяц, в четырнадцатый <i>день</i> месяца, вечером Пасха Господня;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3406, 'rst66', 'lev', 23, 4, 'Вот праздники Господни, священные собрания, которые вы должны созывать в свое время:');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3405, 'rst66', 'lev', 23, 3, 'шесть дней можно делать дела, а в седьмой день суббота покоя, священное собрание; никакого дела не делайте; это суббота Господня во всех жилищах ваших.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3404, 'rst66', 'lev', 23, 2, 'объяви сынам Израилевым и скажи им о праздниках Господних, в которые должно созывать священные собрания. Вот праздники Мои:');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3403, 'rst66', 'lev', 23, 1, 'И сказал Господь Моисею, говоря:');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3402, 'rst66', 'lev', 22, 33, 'Который вывел вас из земли Египетской, чтоб быть вашим Богом. Я Господь.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3401, 'rst66', 'lev', 22, 32, 'Не бесчестите святого имени Моего, чтоб Я был святим среди сынов Израилевых. Я Господь, освящающий вас,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3400, 'rst66', 'lev', 22, 31, 'И соблюдайте заповеди Мои, и исполняйте их. Я Господь.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3399, 'rst66', 'lev', 22, 30, 'в тот же день должно съесть ее, не оставляйте от нее до утра. Я Господь.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3398, 'rst66', 'lev', 22, 29, 'Если приносите Господу жертву благодарения, то приносите ее так, чтоб она приобрела вам благоволение;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3397, 'rst66', 'lev', 22, 28, 'но ни коровы, ни овцы не закалайте в один день с порождением ее.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3396, 'rst66', 'lev', 22, 27, 'когда родится теленок, или ягненок, или козленок, то семь дней он должен пробыть при матери своей, а от восьмого дня и далее будет благоугоден для приношения в жертву Господу;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3395, 'rst66', 'lev', 22, 26, 'И сказал Господь Моисею, говоря:');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3394, 'rst66', 'lev', 22, 25, 'и из рук иноземцев не приносите всех таковых <i>животных</i> в дар Богу вашему, потому что на них повреждение, порок на них: не приобретут они вам благоволения.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3393, 'rst66', 'lev', 22, 24, '<i>животного</i>, у которого ятра раздавлены, разбиты, оторваны или вырезаны, не приносите Господу и в земле вашей не делайте <i>сего;</i>');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3392, 'rst66', 'lev', 22, 23, 'тельца и агнца с членами, несоразмерно длинными или короткими, в жертву усердия принести можешь; а если по обету, то это не угодно будет <i>Богу;</i>');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3391, 'rst66', 'lev', 22, 22, '<i>животного</i> слепого, или поврежденного, или уродливого, или больного, или коростового, или паршивого, таких не приносите Господу и в жертву не давайте их на жертвенник Господень;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3390, 'rst66', 'lev', 22, 21, 'И если кто приносит мирную жертву Господу, исполняя обет, или по усердию, из крупного скота или из мелкого, то <i>жертва должна быть</i> без порока, чтоб быть угодною <i>Богу:</i> никакого порока не должно быть на ней;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3389, 'rst66', 'lev', 22, 20, 'никакого <i>животного</i>, на котором есть порок, не приносите, ибо это не приобретет вам благоволения.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3388, 'rst66', 'lev', 22, 19, 'то, чтобы сим приобрести благоволение <i>от Бога, жертва должна быть</i> без порока, мужеского пола, из крупного скота, из овец и из коз;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3387, 'rst66', 'lev', 22, 18, 'объяви Аарону, и сынам его, и всем сынам Израилевым и скажи им: если кто из дома Израилева, или из пришельцев, <i>поселившихся</i> между Израильтянами, по обету ли какому, или по усердию приносит жертву свою, которую приносят Господу во всесожжение,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3386, 'rst66', 'lev', 22, 17, 'И сказал Господь Моисею, говоря:');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3385, 'rst66', 'lev', 22, 16, 'и не должны навлекать на себя вину в преступлении, когда будут есть святыни свои, ибо Я Господь, освящающий их.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3384, 'rst66', 'lev', 22, 15, '<i>Священники</i> сами не должны порочить святыни сынов Израилевых, которые они приносят Господу,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3383, 'rst66', 'lev', 22, 14, 'Кто по ошибке съест <i>что‐нибудь</i> из святыни, тот должен отдать священнику святыню и приложить к ней пятую ее долю.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3382, 'rst66', 'lev', 22, 13, 'когда же дочь священника будет вдова, или разведенная, и детей нет у нее, и возвратится в дом отца своего, как <i>была</i> в юности своей, тогда она может есть хлеб отца своего; а посторонний никто не должен есть его.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3381, 'rst66', 'lev', 22, 12, 'Если дочь священника выйдет в замужество за постороннего, то она не должна есть приносимых святынь;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3380, 'rst66', 'lev', 22, 11, 'если же священник купит себе человека за серебро, то сей может есть оную; также и домочадцы его могут есть хлеб его.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3379, 'rst66', 'lev', 22, 10, 'Никто посторонний не должен есть святыни; поселившийся у священника и наемник не должен есть святыни;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3378, 'rst66', 'lev', 22, 9, 'Да соблюдают они повеления Мои, чтобы не понести на себе греха и не умереть в нем, когда нарушат сие. Я &mdash; Господь, освящающий их.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3377, 'rst66', 'lev', 22, 8, 'Мертвечины и звероядины он не должен есть, чтобы не оскверниться этим. Я Господь.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3376, 'rst66', 'lev', 22, 7, 'но когда зайдет солнце и он очистится, тогда может он есть святыни, ибо это его пища.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3375, 'rst66', 'lev', 22, 6, 'тот, прикоснувшийся к сему, нечист будет до вечера и не должен есть святынь, прежде нежели омоет тело свое водою;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3374, 'rst66', 'lev', 22, 5, 'или кто прикоснется к какому‐нибудь гаду, от которого он сделается нечист, или к человеку, от которого он сделается нечист какою бы то ни было нечистотою, &mdash;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3373, 'rst66', 'lev', 22, 4, 'Кто из семени Ааронова прокажен, или имеет истечение, тот не должен есть святынь, пока не очистится; и кто прикоснется к чему‐нибудь нечистому от мертвого, или у кого случится излияние семени,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3372, 'rst66', 'lev', 22, 3, 'Скажи им: если кто из всего потомства вашего в роды ваши, имея на себе нечистоту, приступит к святыням, которые посвящают сыны Израилевы Господу, то истребится душа та от лица Моего. Я Господь.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3371, 'rst66', 'lev', 22, 2, 'скажи Аарону и сынам его, чтоб они осторожно поступали со святынями сынов Израилевых и не бесчестили святого имени Моего в том, что они посвящают Мне. Я Господь.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3370, 'rst66', 'lev', 22, 1, 'И сказал Господь Моисею, говоря:');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3369, 'rst66', 'lev', 21, 24, 'И объявил <i>это</i> Моисей Аарону и сынам его и всем сынам Израилевым.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3368, 'rst66', 'lev', 21, 23, 'но к завесе не должен он приходить и к жертвеннику не должен приступать, потому что недостаток на нем: не должен он бесчестить святилища Моего, ибо Я Господь, освящающий их.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3367, 'rst66', 'lev', 21, 22, 'хлеб Бога своего из великих святынь и из святынь он может есть;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3366, 'rst66', 'lev', 21, 21, 'ни один человек из семени Аарона священника, у которого <i>на теле</i> есть недостаток, не должен приступать, чтобы приносить жертвы Господу; недостаток <i>на нем</i>, поэтому не должен он приступать, чтобы приносить хлеб Богу своему;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3365, 'rst66', 'lev', 21, 20, 'ни горбатый, ни с сухим членом, ни с бельмом на глазу, ни коростовый, ни паршивый, ни с поврежденными ятрами;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3364, 'rst66', 'lev', 21, 19, 'ни такой, у которого переломлена нога или переломлена рука,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3363, 'rst66', 'lev', 21, 18, 'никто, у кого на теле есть недостаток, не должен приступать, ни слепой, ни хромой, ни уродливый,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3362, 'rst66', 'lev', 21, 17, 'скажи Аарону: никто из семени твоего во <i>все</i> роды их, у которого <i>на теле</i> будет недостаток, не должен приступать, чтобы приносить хлеб Богу своему;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3361, 'rst66', 'lev', 21, 16, 'И сказал Господь Моисею, говоря:');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3360, 'rst66', 'lev', 21, 15, 'он не должен порочить семени своего в народе своем, ибо Я &mdash; Господь, освящающий его.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3359, 'rst66', 'lev', 21, 14, 'вдову, или отверженную, или опороченную, <i>или</i> блудницу не должен он брать, но девицу из народа своего должен он брать в жену;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3358, 'rst66', 'lev', 21, 13, 'В жену он должен брать девицу:');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3357, 'rst66', 'lev', 21, 12, 'И от святилища он не должен отходить и бесчестить святилище Бога своего, ибо освящение елеем помазания Бога его на нем. Я Господь.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3356, 'rst66', 'lev', 21, 11, 'и ни к какому умершему не должен он приступать: даже <i>прикосновением к умершему</i> отцу своему и матери своей он не должен осквернять себя.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3355, 'rst66', 'lev', 21, 10, 'Великий же священник из братьев своих, на голову которого возлит елей помазания, и который освящен, чтобы облачаться в <i>священные</i> одежды, не должен обнажать головы своей и раздирать одежд своих;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3354, 'rst66', 'lev', 21, 9, 'Если дочь священника осквернит себя блудодеянием, то она бесчестит отца своего; огнем должно сжечь ее.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3353, 'rst66', 'lev', 21, 8, 'Святи его, ибо он приносит хлеб Богу твоему: да будет он у тебя свят, ибо свят Я Господь, освящающий вас.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3352, 'rst66', 'lev', 21, 7, 'Они не должны брать за себя блудницу и опороченную, не должны брать и жену, отверженную мужем своим, ибо они святы Богу своему.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3351, 'rst66', 'lev', 21, 6, 'Они должны быть святы Богу своему и не должны бесчестить имени Бога своего, ибо они приносят жертвы Господу, хлеб Богу своему, и потому должны быть святы.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3350, 'rst66', 'lev', 21, 5, 'Они не должны брить головы своей и подстригать края бороды своей и делать нарезы на теле своем.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3349, 'rst66', 'lev', 21, 4, 'и <i>прикосновением</i> к кому бы то ни было в народе своем не должен он осквернять себя, чтобы не сделаться нечистым.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3348, 'rst66', 'lev', 21, 3, 'и к сестре своей, девице, живущей при нем и не бывшей замужем, можно ему <i>прикасаться</i>, не оскверняя себя;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3347, 'rst66', 'lev', 21, 2, 'только к ближнему родственнику своему, к матери своей и к отцу своему, к сыну своему и дочери своей, к брату своему');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3346, 'rst66', 'lev', 21, 1, 'И сказал Господь Моисею: объяви священникам, сынам Аароновым, и скажи им: да не оскверняют себя <i>прикосновением</i> к умершему из народа своего;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3345, 'rst66', 'lev', 20, 27, 'Мужчина ли или женщина, если будут они вызывать мертвых или волхвовать, да будут преданы смерти: камнями должно побить их, кровь их на них.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3344, 'rst66', 'lev', 20, 26, 'Будьте предо Мною святы, ибо Я свят Господь, и Я отделил вас от народов, чтобы вы были Мои.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3343, 'rst66', 'lev', 20, 25, 'Отличайте скот чистый от нечистого и птицу чистую от нечистой и не оскверняйте душ ваших скотом и птицею и всем пресмыкающимся по земле, что отличил Я как нечистое.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3342, 'rst66', 'lev', 20, 24, 'и сказал Я вам: вы владейте землею их, и вам отдаю в наследие землю, в которой течет молоко и мед. Я Господь, Бог ваш, Который отделил вас от всех народов.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3341, 'rst66', 'lev', 20, 23, 'Не поступайте по обычаям народа, который Я прогоняю от вас; ибо они всё это делали, и Я вознегодовал на них,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3340, 'rst66', 'lev', 20, 22, 'Соблюдайте все уставы Мои и все законы Мои и исполняйте их, &mdash; и не свергнет вас с себя земля, в которую Я веду вас жить.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3339, 'rst66', 'lev', 20, 21, 'Если кто возьмет жену брата своего: это гнусно; он открыл наготу брата своего, бездетны будут они.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3338, 'rst66', 'lev', 20, 20, 'Кто ляжет с теткою своею, тот открыл наготу дяди своего; грех свой понесут они, бездетными умрут.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3337, 'rst66', 'lev', 20, 19, 'Наготы сестры матери твоей и сестры отца твоего не открывай, ибо таковой обнажает плоть свою: грех свой понесут они.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3336, 'rst66', 'lev', 20, 18, 'Если кто ляжет с женою во время болезни <i>кровеочищения</i> и откроет наготу ее, то он обнажил истечения ее, и она открыла течение кровей своих: оба они да будут истреблены из народа своего.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3335, 'rst66', 'lev', 20, 17, 'Если кто возьмет сестру свою, дочь отца своего или дочь матери своей, и увидит наготу ее, и она увидит наготу его: это срам, да будут они истреблены пред глазами сынов народа своего; он открыл наготу сестры своей: грех свой понесет он.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3334, 'rst66', 'lev', 20, 16, 'Если женщина пойдет к какой‐нибудь скотине, чтобы совокупиться с нею, то убей женщину и скотину: да будут они преданы смерти, кровь их на них.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3333, 'rst66', 'lev', 20, 15, 'Кто смесится со скотиною, того предать смерти, и скотину убейте.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3332, 'rst66', 'lev', 20, 14, 'Если кто возьмет себе жену и мать ее: это беззаконие; на огне должно сжечь его и их, чтобы не было беззакония между вами.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3331, 'rst66', 'lev', 20, 13, 'Если кто ляжет с мужчиною, как с женщиною, то оба они сделали мерзость: да будут преданы смерти, кровь их на них.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3330, 'rst66', 'lev', 20, 12, 'Если кто ляжет с невесткою своею, то оба они да будут преданы смерти: мерзость сделали они, кровь их на них.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3329, 'rst66', 'lev', 20, 11, 'Кто ляжет с женою отца своего, тот открыл наготу отца своего: оба они да будут преданы смерти, кровь их на них.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3328, 'rst66', 'lev', 20, 10, 'Если кто будет прелюбодействовать с женой замужнею, если кто будет прелюбодействовать с женою ближнего своего, &mdash; да будут преданы смерти и прелюбодей и прелюбодейка.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3327, 'rst66', 'lev', 20, 9, 'Кто будет злословить отца своего или мать свою, тот да будет предан смерти; отца своего и мать свою он злословил: кровь его на нем.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3326, 'rst66', 'lev', 20, 8, 'Соблюдайте постановления Мои и исполняйте их, ибо Я Господь, освящающий вас.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3325, 'rst66', 'lev', 20, 7, 'Освящайте себя и будьте святы, ибо Я Господь, Бог ваш.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3324, 'rst66', 'lev', 20, 6, 'И если какая душа обратится к вызывающим мертвых и к волшебникам, чтобы блудно ходить вслед их, то Я обращу лицо Мое на ту душу и истреблю ее из народа ее.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3323, 'rst66', 'lev', 20, 5, 'то Я обращу лицо Мое на человека того и на род его и истреблю его из народа его, и всех, блудящих по следам его, чтобы блудно ходить вслед Молоха.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3322, 'rst66', 'lev', 20, 4, 'и если народ земли не обратит очей своих на человека того, когда он даст из детей своих Молоху, и не умертвит его,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3321, 'rst66', 'lev', 20, 3, 'и Я обращу лицо Мое на человека того и истреблю его из народа его за то, что он дал из детей своих Молоху, чтоб осквернить святилище Мое и обесчестить святое имя Мое;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3320, 'rst66', 'lev', 20, 2, 'скажи сие сынам Израилевым: кто из сынов Израилевых и из пришельцев, живущих между Израильтянами, даст из детей своих Молоху, тот да будет предан смерти: народ земли да побьет его камнями;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3319, 'rst66', 'lev', 20, 1, 'И сказал Господь Моисею, говоря:');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3318, 'rst66', 'lev', 19, 37, 'Соблюдайте все уставы Мои и все законы Мои и исполняйте их. Я Господь.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3317, 'rst66', 'lev', 19, 36, 'да будут у вас весы верные, гири верные, ефа верная и гин верный. Я Господь, Бог ваш, Который вывел вас из земли Египетской.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3316, 'rst66', 'lev', 19, 35, 'Не делайте неправды в суде, в мере, в весе и в измерении:');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3315, 'rst66', 'lev', 19, 34, 'пришелец, поселившийся у вас, да будет для вас то же, что туземец ваш; люби его, как себя; ибо и вы были пришельцами в земле Египетской. Я Господь, Бог ваш.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3314, 'rst66', 'lev', 19, 33, 'Когда поселится пришелец в земле вашей, не притесняйте его:');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3313, 'rst66', 'lev', 19, 32, 'Пред лицом седого вставай и почитай лицо старца, и бойся Бога твоего. Я Господь.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3312, 'rst66', 'lev', 19, 31, 'Не обращайтесь к вызывающим мертвых, и к волшебникам не ходите, и не доводите себя до осквернения от них. Я Господь, Бог ваш.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3311, 'rst66', 'lev', 19, 30, 'Субботы Мои храните и святилище Мое чтите. Я Господь.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3310, 'rst66', 'lev', 19, 29, 'Не оскверняй дочери твоей, допуская ее до блуда, чтобы не блудодействовала земля и не наполнилась земля развратом.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3309, 'rst66', 'lev', 19, 28, 'Ради умершего не делайте нарезов на теле вашем и не накалывайте на себе письмен. Я Господь.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3308, 'rst66', 'lev', 19, 27, 'Не стригите головы вашей кругом, и не порти края бороды твоей.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3307, 'rst66', 'lev', 19, 26, 'Не ешьте с кровью; не ворожите и не гадайте.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3306, 'rst66', 'lev', 19, 25, 'в пятый же год вы можете есть плоды его и собирать себе все произведения его. Я Господь, Бог ваш.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3305, 'rst66', 'lev', 19, 24, 'а в четвертый год все плоды его должны быть посвящены для празднеств Господних;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3304, 'rst66', 'lev', 19, 23, 'Когда придете в землю, <i>которую Господь Бог даст вам,</i> и посадите какое‐либо плодовое дерево, то плоды его почитайте за необрезанные: три года должно почитать их за необрезанные, не должно есть их;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3303, 'rst66', 'lev', 19, 22, 'и очистит его священник овном повинности пред Господом от греха, которым он согрешил, и прощен будет ему грех, которым он согрешил.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3302, 'rst66', 'lev', 19, 21, 'пусть приведет он Господу ко входу скинии собрания жертву повинности, овна в жертву повинности своей;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3301, 'rst66', 'lev', 19, 20, 'Если кто переспит с женщиною, а она раба, обрученная мужу, но еще не выкупленная, или свобода еще не дана ей, то должно наказать их, но не смертью, потому что она несвободная:');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3300, 'rst66', 'lev', 19, 19, 'Уставы Мои соблюдайте; скота твоего не своди с иною породою; поля твоего не засевай двумя родами <i>семян;</i> в одежду из разнородных нитей, из шерсти и льна, не одевайся.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3299, 'rst66', 'lev', 19, 18, 'Не мсти и не имей злобы на сынов народа твоего, но люби ближнего твоего, как самого себя. Я &mdash; Господь.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3298, 'rst66', 'lev', 19, 17, 'Не враждуй на брата твоего в сердце твоем; обличи ближнего твоего, и не понесешь за него греха.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3297, 'rst66', 'lev', 19, 16, 'Не ходи переносчиком в народе твоем и не восставай на жизнь ближнего твоего. Я &mdash; Господь.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3296, 'rst66', 'lev', 19, 15, 'Не делайте неправды на суде; не будь лицеприятен к нищему и не угождай лицу великого; по правде суди ближнего твоего.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3295, 'rst66', 'lev', 19, 14, 'Не злословь глухого и пред слепым не клади ничего, чтобы преткнуться ему; бойся Бога твоего. Я Господь.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3294, 'rst66', 'lev', 19, 13, 'Не обижай ближнего твоего и не грабительствуй. Плата наемнику не должна оставаться у тебя до утра.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3293, 'rst66', 'lev', 19, 12, 'Не клянитесь именем Моим во лжи, и не бесчести имени Бога твоего. Я &mdash; Господь.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3292, 'rst66', 'lev', 19, 11, 'Не крадите, не лгите и не обманывайте друг друга.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3291, 'rst66', 'lev', 19, 10, 'и виноградника твоего не обирай дочиста, и попадавших ягод в винограднике не подбирай; оставь это бедному и пришельцу. Я Господь, Бог ваш.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3290, 'rst66', 'lev', 19, 9, 'Когда будете жать жатву на земле вашей, не дожинай до края поля твоего, и оставшегося от жатвы твоей не подбирай,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3289, 'rst66', 'lev', 19, 8, 'кто станет есть ее, тот понесет на себе грех, ибо он осквернил святыню Господню, и истребится душа та из народа своего.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3288, 'rst66', 'lev', 19, 7, 'если же кто станет есть ее на третий день, это гнусно, это не будет благоприятно;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3287, 'rst66', 'lev', 19, 6, 'в день жертвоприношения вашего и на другой день должно есть ее, а оставшееся к третьему дню должно сжечь на огне;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3286, 'rst66', 'lev', 19, 5, 'Когда будете приносить Господу жертву мирную, то приносите ее, чтобы приобрести себе благоволение:');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3285, 'rst66', 'lev', 19, 4, 'Не обращайтесь к идолам и богов литых не делайте себе. Я Господь, Бог ваш.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3284, 'rst66', 'lev', 19, 3, 'Бойтесь каждый матери своей и отца своего и субботы Мои храните. Я Господь, Бог ваш.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3283, 'rst66', 'lev', 19, 2, 'объяви всему обществу сынов Израилевых и скажи им: святы будьте, ибо свят Я, Господь, Бог ваш.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3282, 'rst66', 'lev', 19, 1, 'И сказал Господь Моисею, говоря:');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3281, 'rst66', 'lev', 18, 30, 'Итак соблюдайте повеления Мои, чтобы не поступать по гнусным обычаям, по которым поступали прежде вас, и чтобы не оскверняться ими. Я Господь, Бог ваш.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3280, 'rst66', 'lev', 18, 29, 'ибо если кто будет делать все эти мерзости, то души делающих это истреблены будут из народа своего.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3279, 'rst66', 'lev', 18, 28, 'чтоб и вас не свергнула с себя земля, когда вы станете осквернять ее, как она свергнула народы, бывшие прежде вас;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3278, 'rst66', 'lev', 18, 27, 'ибо все эти мерзости делали люди сей земли, что пред вами, и осквернилась земля;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3277, 'rst66', 'lev', 18, 26, 'А вы соблюдайте постановления Мои и законы Мои и не делайте всех этих мерзостей, ни туземец, ни пришелец, живущий между вами,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3276, 'rst66', 'lev', 18, 25, 'и осквернилась земля, и Я воззрел на беззаконие ее, и свергнула с себя земля живущих на ней.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3275, 'rst66', 'lev', 18, 24, 'Не оскверняйте себя ничем этим, ибо всем этим осквернили себя народы, которых Я прогоняю от вас:');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3274, 'rst66', 'lev', 18, 23, 'И ни с каким скотом не ложись, чтоб излить <i>семя</i> и оскверниться от него; и женщина не должна становиться пред скотом для совокупления с ним: это гнусно.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3273, 'rst66', 'lev', 18, 22, 'Не ложись с мужчиною, как с женщиною: это мерзость.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3272, 'rst66', 'lev', 18, 21, 'Из детей твоих не отдавай на служение Молоху и не бесчести имени Бога твоего. Я Господь.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3271, 'rst66', 'lev', 18, 20, 'И с женою ближнего твоего не ложись, чтобы излить семя и оскверниться с нею.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3270, 'rst66', 'lev', 18, 19, 'И к жене во время очищения нечистот ее не приближайся, чтоб открыть наготу ее.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3269, 'rst66', 'lev', 18, 18, 'Не бери жены вместе с сестрою ее, чтобы сделать ее соперницей, чтоб открыть наготу ее при ней, при жизни ее.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3268, 'rst66', 'lev', 18, 17, 'Наготы жены и дочери ее не открывай; дочери сына ее и дочери дочери ее не бери, чтоб открыть наготу их, они единокровные ее; это беззаконие.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3267, 'rst66', 'lev', 18, 16, 'Наготы жены брата твоего не открывай, это нагота брата твоего.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3266, 'rst66', 'lev', 18, 15, 'Наготы невестки твоей не открывай: она жена сына твоего, не открывай наготы ее.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3265, 'rst66', 'lev', 18, 14, 'Наготы брата отца твоего не открывай и к жене его не приближайся: она тетка твоя.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3264, 'rst66', 'lev', 18, 13, 'Наготы сестры матери твоей не открывай, ибо она единокровная матери твоей.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3263, 'rst66', 'lev', 18, 12, 'Наготы сестры отца твоего не открывай, она единокровная отцу твоему.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3262, 'rst66', 'lev', 18, 11, 'Наготы дочери жены отца твоего, родившейся от отца твоего, она сестра твоя <i>по отцу</i>, не открывай наготы ее.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3261, 'rst66', 'lev', 18, 10, 'Наготы дочери сына твоего или дочери дочери твоей, не открывай наготы их, ибо они твоя нагота.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3260, 'rst66', 'lev', 18, 9, 'Наготы сестры твоей, дочери отца твоего или дочери матери твоей, родившейся в доме или вне дома, не открывай наготы их.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3259, 'rst66', 'lev', 18, 8, 'Наготы жены отца твоего не открывай: это нагота отца твоего.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3258, 'rst66', 'lev', 18, 7, 'Наготы отца твоего и наготы матери твоей не открывай: она мать твоя, не открывай наготы ее.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3257, 'rst66', 'lev', 18, 6, 'Никто ни к какой родственнице по плоти не должен приближаться с тем, чтобы открыть наготу. Я Господь.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3256, 'rst66', 'lev', 18, 5, 'Соблюдайте постановления Мои и законы Мои, которые исполняя, человек будет жив. Я &mdash; Господь.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3255, 'rst66', 'lev', 18, 4, 'Мои законы исполняйте и Мои постановления соблюдайте, поступая по ним. Я Господь, Бог ваш.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3254, 'rst66', 'lev', 18, 3, 'По делам земли Египетской, в которой вы жили, не поступайте, и по делам земли Ханаанской, в которую Я веду вас, не поступайте, и по установлениям их не ходите:');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3253, 'rst66', 'lev', 18, 2, 'объяви сынам Израилевым и скажи им: Я Господь, Бог ваш.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3252, 'rst66', 'lev', 18, 1, 'И сказал Господь Моисею, говоря:');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3251, 'rst66', 'lev', 17, 16, 'если же не вымоет <i>одежд своих</i> и не омоет тела своего, то понесет на себе беззаконие свое.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3250, 'rst66', 'lev', 17, 15, 'И всякий, кто будет есть мертвечину или растерзанное зверем, туземец или пришелец, должен вымыть одежды свои и омыться водою, и нечист будет до вечера, а <i>потом</i> будет чист;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3249, 'rst66', 'lev', 17, 14, 'ибо душа всякого тела <i>есть</i> кровь его, она душа его; потому Я сказал сынам Израилевым: не ешьте крови ни из какого тела, потому что душа всякого тела есть кровь его: всякий, кто будет есть ее, истребится.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3248, 'rst66', 'lev', 17, 13, 'Если кто из сынов Израилевых и из пришельцев, живущих между вами, на ловле поймает зверя или птицу, которую можно есть, то он должен дать вытечь крови ее и покрыть ее землею,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3247, 'rst66', 'lev', 17, 12, 'потому Я и сказал сынам Израилевым: ни одна душа из вас не должна есть крови, и пришелец, живущий между вами, не должен есть крови.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3246, 'rst66', 'lev', 17, 11, 'потому что душа тела в крови, и Я назначил ее вам для жертвенника, чтобы очищать души ваши, ибо кровь сия душу очищает;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3245, 'rst66', 'lev', 17, 10, 'Если кто из дома Израилева и из пришельцев, которые живут между вами, будет есть какую‐нибудь кровь, то обращу лицо Мое на душу того, кто будет есть кровь, и истреблю ее из народа ее,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3244, 'rst66', 'lev', 17, 9, 'и не приведет ко входу скинии собрания, чтобы совершить ее Господу, то истребится человек тот из народа своего.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3243, 'rst66', 'lev', 17, 8, '<i>Еще</i> скажи им: если кто из дома Израилева и из пришельцев, которые живут между вами, приносит всесожжение или жертву');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3242, 'rst66', 'lev', 17, 7, 'чтоб они впредь не приносили жертв своих идолам, за которыми блудно ходят они. Сие да будет для них постановлением вечным в роды их.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3241, 'rst66', 'lev', 17, 6, 'и покропит священник кровью на жертвенник Господень у входа скинии собрания, и воскурит тук в приятное благоухание Господу,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3240, 'rst66', 'lev', 17, 5, '<i>это</i> для того, чтобы приводили сыны Израилевы жертвы свои, которые они закалают на поле, чтобы приводили их пред Господа ко входу скинии собрания, к священнику, и закалали их Господу в жертвы мирные;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3239, 'rst66', 'lev', 17, 4, 'и не приведет ко входу скинии собрания, чтобы представить в жертву Господу пред жилищем Господним, то человеку тому вменена будет кровь: он пролил кровь, и истребится человек тот из народа своего;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3238, 'rst66', 'lev', 17, 3, 'если кто из дома Израилева заколет тельца, или овцу, или козу в стане, или если кто заколет вне стана');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3237, 'rst66', 'lev', 17, 2, 'объяви Аарону, и сынам его, и всем сынам Израилевым, и скажи им: вот что повелевает Господь:');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3236, 'rst66', 'lev', 17, 1, 'И сказал Господь Моисею, говоря:');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3235, 'rst66', 'lev', 16, 34, 'И да будет сие для вас вечным постановлением: очищать сынов Израилевых от всех грехов их однажды в году. И сделал он так, как повелел Господь Моисею.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3234, 'rst66', 'lev', 16, 33, 'и очистит Святое Святых и скинию собрания, и жертвенник очистит, и священников и весь народ общества очистит.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3233, 'rst66', 'lev', 16, 32, 'Очищать же должен священник, который помазан и который посвящен, чтобы священнодействовать ему вместо отца своего. И наденет он льняные одежды, одежды священные,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3232, 'rst66', 'lev', 16, 31, 'это суббота покоя для вас, смиряйте души ваши: это постановление вечное.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3231, 'rst66', 'lev', 16, 30, 'ибо в сей день очищают вас, чтобы сделать вас чистыми от всех грехов ваших, чтобы вы были чисты пред лицом Господним;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3230, 'rst66', 'lev', 16, 29, 'И да будет сие для вас вечным постановлением: в седьмой месяц, в десятый <i>день</i> месяца смиряйте души ваши и никакого дела не делайте, ни туземец, ни пришелец, поселившийся между вами,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3229, 'rst66', 'lev', 16, 28, 'кто сожжет их, тот должен вымыть одежды свои и омыть тело свое водою, и после того может войти в стан.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3228, 'rst66', 'lev', 16, 27, 'А тельца за грех и козла за грех, которых кровь внесена была для очищения святилища, пусть вынесут вон из стана и сожгут на огне кожи их, и мясо их, и нечистоту их;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3227, 'rst66', 'lev', 16, 26, 'И тот, кто отводил козла для отпущения, должен вымыть одежды свои, омыть тело свое водою, и потом может войти в стан.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3226, 'rst66', 'lev', 16, 25, 'а тук жертвы за грех воскурит на жертвеннике.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3225, 'rst66', 'lev', 16, 24, 'и омоет тело свое водою на святом месте, и наденет одежды свои, и выйдет, и совершит всесожжение за себя и всесожжение за народ, и очистит себя, и народ;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3224, 'rst66', 'lev', 16, 23, 'И войдет Аарон в скинию собрания, и снимет льняные одежды, которые надевал, входя во святилище, и оставит их там,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3223, 'rst66', 'lev', 16, 22, 'и понесет козел на себе все беззакония их в землю непроходимую, и пустит он козла в пустыню.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3222, 'rst66', 'lev', 16, 21, 'и возложит Аарон обе руки свои на голову живого козла, и исповедает над ним все беззакония сынов Израилевых, и все преступления их, и все грехи их, и возложит их на голову козла, и отошлет с нарочным человеком в пустыню:');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3221, 'rst66', 'lev', 16, 20, 'И совершив очищение святилища, скинии собрания и жертвенника, приведет он живого козла,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3220, 'rst66', 'lev', 16, 19, 'и покропит на него кровью с перста своего семь раз, и очистит его, и освятит его от нечистот сынов Израилевых.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3219, 'rst66', 'lev', 16, 18, 'И выйдет он к жертвеннику, который пред лицом Господним, и очистит его, и возьмет крови тельца и крови козла, и возложит на роги жертвенника со всех сторон,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3218, 'rst66', 'lev', 16, 17, 'Ни один человек не должен быть в скинии собрания, когда входит он для очищения святилища, до самого выхода его. И так очистит он себя, дом свой и все общество Израилево.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3217, 'rst66', 'lev', 16, 16, 'и очистит святилище от нечистот сынов Израилевых и от преступлений их, во всех грехах их. Так должен поступить он и со скинией собрания, находящейся у них, среди нечистот их.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3216, 'rst66', 'lev', 16, 15, 'И заколет козла в жертву за грех за народ, и внесет кровь его за завесу, и сделает с кровью его то же, что делал с кровью тельца, и покропит ею на крышку и перед крышкою, &mdash;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3215, 'rst66', 'lev', 16, 14, 'и возьмет крови тельца, и покропит перстом своим на крышку спереди и перед крышкою, семь раз покропит кровью с перста своего.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3214, 'rst66', 'lev', 16, 13, 'и положит курение на огонь пред лицом Господним, и облако курения покроет крышку, которая над <i>ковчегом</i> откровения, дабы ему не умереть;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3213, 'rst66', 'lev', 16, 12, 'и возьмет горящих угольев полную кадильницу с жертвенника, который пред лицом Господним, и благовонного мелко истолченного курения полные горсти, и внесет за завесу;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3212, 'rst66', 'lev', 16, 11, 'И приведет Аарон тельца в жертву за грех за себя, и очистит себя и дом свой, и заколет тельца в жертву за грех за себя;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3211, 'rst66', 'lev', 16, 10, 'а козла, на которого вышел жребий для отпущения, поставит живого пред Господом, чтобы совершить над ним очищение и отослать его в пустыню для отпущения.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3210, 'rst66', 'lev', 16, 9, 'и приведет Аарон козла, на которого вышел жребий для Господа, и принесет его в жертву за грех,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3209, 'rst66', 'lev', 16, 8, 'и бросит Аарон об обоих козлах жребии: один жребий для Господа, а другой жребий для отпущения;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3208, 'rst66', 'lev', 16, 7, 'И возьмет двух козлов и поставит их пред лицом Господним, у входа скинии собрания;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3207, 'rst66', 'lev', 16, 6, 'И принесет Аарон тельца в жертву за грех за себя и очистит себя и дом свой.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3206, 'rst66', 'lev', 16, 5, 'и от общества сынов Израилевых пусть возьмет двух козлов в жертву за грех и одного овна во всесожжение.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3205, 'rst66', 'lev', 16, 4, 'священный льняной хитон должен надевать он, нижнее платье льняное да будет на теле его, и льняным поясом пусть опоясывается, и льняной кидар надевает: это священные одежды; и пусть омывает он тело свое водою и надевает их;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3204, 'rst66', 'lev', 16, 3, 'Вот с чем должен входить Аарон во святилище: с тельцом в жертву за грех и с овном во всесожжение;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3203, 'rst66', 'lev', 16, 2, 'и сказал Господь Моисею: скажи Аарону, брату твоему, чтоб он не во всякое время входил во святилище за завесу пред крышку, что на ковчеге, дабы ему не умереть, ибо над крышкою Я буду являться в облаке.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3202, 'rst66', 'lev', 16, 1, 'И говорил Господь Моисею по смерти двух сынов Аароновых, когда они, приступив <i>с чуждым огнем</i> пред лицо Господне, умерли,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3201, 'rst66', 'lev', 15, 33, 'и о страдающей очищением своим, и о имеющих истечение, мужчине или женщине, и о муже, который переспит с нечистою.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3200, 'rst66', 'lev', 15, 32, 'Вот закон об имеющем истечение и о том, у кого случится излияние семени, делающее его нечистым,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3199, 'rst66', 'lev', 15, 31, 'Так предохраняйте сынов Израилевых от нечистоты их, чтоб они не умерли в нечистоте своей, оскверняя жилище Мое, которое среди них:');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3198, 'rst66', 'lev', 15, 30, 'и принесет священник одну <i>из птиц</i> в жертву за грех, а другую во всесожжение, и очистит ее священник пред Господом от истечения нечистоты ее.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3197, 'rst66', 'lev', 15, 29, 'в восьмой день возьмет она себе двух горлиц или двух молодых голубей и принесет их к священнику, ко входу скинии собрания;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3196, 'rst66', 'lev', 15, 28, 'А когда она освободится от истечения своего, тогда должна отсчитать себе семь дней, и потом будет чиста;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3195, 'rst66', 'lev', 15, 27, 'и всякий, кто прикоснется к ним, будет нечист, и должен вымыть одежды свои и омыться водою, и нечист будет до вечера.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3194, 'rst66', 'lev', 15, 26, 'всякая постель, на которой она ляжет во все время истечения своего, будет <i>нечиста</i>, подобно как постель в продолжение очищения ее; и всякая вещь, на которую она сядет, будет нечиста, как нечисто это во время очищения ее;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3193, 'rst66', 'lev', 15, 25, 'Если у женщины течет кровь многие дни не во время очищения ее, или если она имеет истечение долее <i>обыкновенного</i> очищения ее, то во все время истечения нечистоты ее, подобно как в продолжение очищения своего, она нечиста;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3192, 'rst66', 'lev', 15, 24, 'если переспит с нею муж, то нечистота ее будет на нем; он нечист будет семь дней, и всякая постель, на которой он ляжет, будет нечиста.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3191, 'rst66', 'lev', 15, 23, 'и если кто прикоснется к чему‐нибудь на постели или на той вещи, на которой она сидела, нечист будет до вечера;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3190, 'rst66', 'lev', 15, 22, 'и всякий, кто прикоснется к какой‐нибудь вещи, на которой она сидела, должен вымыть одежды свои и омыться водою, и нечист будет до вечера;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3189, 'rst66', 'lev', 15, 21, 'и всякий, кто прикоснется к постели ее, должен вымыть одежды свои и омыться водою, и нечист будет до вечера;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3188, 'rst66', 'lev', 15, 20, 'и всё, на чем она ляжет в продолжение очищения своего, нечисто; и всё, на чем сядет, нечисто;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3187, 'rst66', 'lev', 15, 19, 'Если женщина имеет истечение крови, текущей из тела ее, то она должна сидеть семь дней во время очищения своего, и всякий, кто прикоснется к ней, нечист будет до вечера;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3186, 'rst66', 'lev', 15, 18, 'если мужчина ляжет с женщиной и <i>будет</i> у него излияние семени, то они должны омыться водою, и нечисты будут до вечера.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3185, 'rst66', 'lev', 15, 17, 'и всякая одежда и всякая кожа, на которую попадет семя, должна быть вымыта водою, и нечиста будет до вечера;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3184, 'rst66', 'lev', 15, 16, 'Если у кого случится излияние семени, то он должен омыть водою все тело свое, и нечист будет до вечера;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3183, 'rst66', 'lev', 15, 15, 'и принесет священник из сих <i>птиц</i> одну в жертву за грех, а другую во всесожжение, и очистит его священник пред Господом от истечения его.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3182, 'rst66', 'lev', 15, 14, 'и в восьмой день возьмет он себе двух горлиц или двух молодых голубей, и придет пред лицо Господне, ко входу скинии собрания, и отдаст их священнику;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3181, 'rst66', 'lev', 15, 13, 'А когда имеющий истечение освободится от истечения своего, тогда должен он отсчитать себе семь дней для очищения своего и вымыть одежды свои, и омыть тело свое живою водою, и будет чист;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3180, 'rst66', 'lev', 15, 12, 'глиняный сосуд, к которому прикоснется имеющий истечение, должно разбить, а всякий деревянный сосуд должно вымыть водою.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3179, 'rst66', 'lev', 15, 11, 'и всякий, к кому прикоснется имеющий истечение, не омыв рук своих водою, должен вымыть одежды свои и омыться водою, и нечист будет до вечера;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3178, 'rst66', 'lev', 15, 10, 'и всякий, кто прикоснется к чему‐нибудь, что было под ним, нечист будет до вечера; и кто понесет это, должен вымыть одежды свои и омыться водою, и нечист будет до вечера;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3177, 'rst66', 'lev', 15, 9, 'и всякая повозка, в которой ехал имеющий истечение, нечиста <i>будет до вечера</i>;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3176, 'rst66', 'lev', 15, 8, 'если имеющий истечение плюнет на чистого, то сей должен вымыть одежды свои и омыться водою, и нечист будет до вечера;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3175, 'rst66', 'lev', 15, 7, 'и кто прикоснется к телу имеющего истечение, тот должен вымыть одежды свои и омыться водою, и нечист будет до вечера;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3174, 'rst66', 'lev', 15, 6, 'кто сядет на какую‐либо вещь, на которой сидел имеющий истечение, тот должен вымыть одежды свои и омыться водою, и нечист будет до вечера;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3173, 'rst66', 'lev', 15, 5, 'и кто прикоснется к постели его, тот должен вымыть одежды свои и омыться водою, и нечист будет до вечера;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3172, 'rst66', 'lev', 15, 4, 'всякая постель, на которой ляжет имеющий истечение, нечиста, и всякая вещь, на которую сядет, нечиста;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3171, 'rst66', 'lev', 15, 3, 'И вот <i>закон</i> о нечистоте его от истечения его: когда течет из тела его истечение его, и когда задерживается в теле его истечение его, это нечистота его;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3170, 'rst66', 'lev', 15, 2, 'объявите сынам Израилевым и скажите им: если у кого будет истечение из тела его, то от истечения своего он нечист.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3169, 'rst66', 'lev', 15, 1, 'И сказал Господь Моисею и Аарону, говоря:');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3168, 'rst66', 'lev', 14, 56, 'чтобы указать, когда это нечисто и когда чисто. Вот закон о проказе.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3167, 'rst66', 'lev', 14, 55, 'и о проказе на одежде и на доме, и об опухоли, и о лишаях, и о пятнах, &mdash;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3166, 'rst66', 'lev', 14, 54, 'Вот закон о всякой язве проказы, и о паршивости,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3165, 'rst66', 'lev', 14, 53, 'и пустит живую птицу вне города, в поле, и очистит дом, и будет чист.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3164, 'rst66', 'lev', 14, 52, 'и очистит дом кровью птицы, и живою водою, и живою птицею, и кедровым деревом, и иссопом, и червленою нитью;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3163, 'rst66', 'lev', 14, 51, 'и возьмет кедровое дерево и иссоп, и червленую нить, и живую птицу, и омочит их в крови птицы заколотой, и в живой воде, и покропит дом семь раз;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3162, 'rst66', 'lev', 14, 50, 'и заколет одну птицу над глиняным сосудом, над живою водою;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3161, 'rst66', 'lev', 14, 49, 'И чтобы очистить дом, возьмет он двух птиц, кедрового дерева, червленую нить и иссопа,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3160, 'rst66', 'lev', 14, 48, 'Если же священник придет и увидит, что язва на доме не распространилась после того, как обмазали дом, то священник объявит дом чистым, потому что язва прошла.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3159, 'rst66', 'lev', 14, 47, 'и кто спит в доме том, тот должен вымыть одежды свои; и кто ест в доме том, тот должен вымыть одежды свои.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3158, 'rst66', 'lev', 14, 46, 'кто входит в дом во все время, когда он заперт, тот нечист до вечера;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3157, 'rst66', 'lev', 14, 45, 'должно разломать сей дом, и камни его, и дерево его, и всю обмазку дома вынести вне города на место нечистое;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3156, 'rst66', 'lev', 14, 44, 'то священник придет и осмотрит, и если язва на доме распространилась, то это едкая проказа на доме, нечист он;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3155, 'rst66', 'lev', 14, 43, 'Если язва опять появится и будет цвести на доме после того, как выломали камни, и оскоблили дом, и обмазали,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3154, 'rst66', 'lev', 14, 42, 'и возьмут другие камни, и вставят вместо тех камней, и возьмут другую обмазку, и обмажут дом.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3153, 'rst66', 'lev', 14, 41, 'а дом внутри пусть весь оскоблят, и обмазку, которую отскоблят, высыпят вне города на место нечистое;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3152, 'rst66', 'lev', 14, 40, 'то священник прикажет выломать камни, на которых язва, и бросить их вне города на место нечистое;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3151, 'rst66', 'lev', 14, 39, 'В седьмой день опять придет священник, и если увидит, что язва распространилась по стенам дома,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3150, 'rst66', 'lev', 14, 38, 'то священник выйдет из дома к дверям дома и запрет дом на семь дней.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3149, 'rst66', 'lev', 14, 37, 'Если он, осмотрев язву, увидит, что язва на стенах дома состоит из зеленоватых или красноватых ямин, которые окажутся углубленными в стене,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3148, 'rst66', 'lev', 14, 36, 'Священник прикажет опорожнить дом, прежде нежели войдет священник осматривать язву, чтобы не сделалось нечистым все, что в доме; после сего придет священник осматривать дом.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3147, 'rst66', 'lev', 14, 35, 'тогда тот, чей дом, должен пойти и сказать священнику: у меня на доме показалась как бы язва.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3146, 'rst66', 'lev', 14, 34, 'когда войдете в землю Ханаанскую, которую Я даю вам во владение, и Я наведу язву проказы на домы в земле владения вашего,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3145, 'rst66', 'lev', 14, 33, 'И сказал Господь Моисею и Аарону, говоря:');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3144, 'rst66', 'lev', 14, 32, 'Вот закон о прокаженном, который во время очищения своего не имеет достатка.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3143, 'rst66', 'lev', 14, 31, 'из того, что достанет рука его, одну <i>птицу</i> в жертву за грех, а другую во всесожжение, вместе с приношением хлебным; и очистит священник очищаемого пред лицом Господа.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3142, 'rst66', 'lev', 14, 30, 'и принесет одну из горлиц или одного из молодых голубей, что достанет рука <i>очищаемого</i>,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3141, 'rst66', 'lev', 14, 29, 'а остальной елей, который на ладони священника, возложит он на голову очищаемого, чтоб очистить его пред лицом Господа;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3140, 'rst66', 'lev', 14, 28, 'и возложит священник елея, который на ладони его, на край правого уха очищаемого, на большой палец правой руки его и на большой палец правой ноги его, на места, <i>где</i> кровь жертвы повинности;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3139, 'rst66', 'lev', 14, 27, 'и елеем, который на левой ладони его, покропит священник с правого перста своего семь раз пред лицом Господним;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3138, 'rst66', 'lev', 14, 26, 'и нальет священник елея на левую свою ладонь,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3137, 'rst66', 'lev', 14, 25, 'и заколет овна в жертву повинности, и возьмет священник крови жертвы повинности, и возложит на край правого уха очищаемого, и на большой палец правой руки его, и на большой палец правой ноги его;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3136, 'rst66', 'lev', 14, 24, 'священник возьмет овна жертвы повинности и лог елея, и принесет это священник, потрясая пред Господом;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3135, 'rst66', 'lev', 14, 23, 'и принесет их в восьмой день очищения своего к священнику ко входу скинии собрания, пред лицо Господа;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3134, 'rst66', 'lev', 14, 22, 'и двух горлиц или двух молодых голубей, что достанет рука его, одну <i>из птиц</i> в жертву за грех, а другую во всесожжение;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3133, 'rst66', 'lev', 14, 21, 'Если же он беден и не имеет достатка, то пусть возьмет одного овна в жертву повинности для потрясания, чтоб очистить себя, и одну десятую часть <i>ефы</i> пшеничной муки, смешанной с елеем, в приношение хлебное, и лог елея,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3132, 'rst66', 'lev', 14, 20, 'и возложит священник всесожжение и приношение хлебное на жертвенник; и очистит его священник, и он будет чист.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3131, 'rst66', 'lev', 14, 19, 'И совершит священник жертву за грех, и очистит очищаемого от нечистоты его; после того заколет <i>жертву</i> всесожжения;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3130, 'rst66', 'lev', 14, 18, 'а остальной елей, который на ладони священника, возложит он на голову очищаемого, и очистит его священник пред лицом Господа.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3129, 'rst66', 'lev', 14, 17, 'оставшийся же елей, который на ладони его, возложит священник на край правого уха очищаемого, на большой палец правой руки его и на большой палец правой ноги его, на <i>места</i>, <i>где</i> кровь жертвы повинности;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3128, 'rst66', 'lev', 14, 16, 'и омочит священник правый перст свой в елее, который на левой ладони его, и покропит елеем с перста своего семь раз пред лицом Господа;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3127, 'rst66', 'lev', 14, 15, 'и возьмет священник из лога елея, и польет на левую свою ладонь;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3126, 'rst66', 'lev', 14, 14, 'и возьмет священник крови жертвы повинности, и возложит священник на край правого уха очищаемого, и на большой палец правой руки его, и на большой палец правой ноги его;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3125, 'rst66', 'lev', 14, 13, 'и заколет овна на том месте, где закалают жертву за грех и всесожжение, на месте святом, ибо сия жертва повинности, подобно жертве за грех, принадлежит священнику: это великая святыня;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3124, 'rst66', 'lev', 14, 12, 'и возьмет священник одного овна, и представит его в жертву повинности, и лог елея, и принесет это, потрясая пред Господом;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3123, 'rst66', 'lev', 14, 11, 'священник очищающий поставит очищаемого человека с ними пред Господом у входа скинии собрания;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3122, 'rst66', 'lev', 14, 10, 'В восьмой день возьмет он двух овнов без порока, и одну овцу однолетнюю без порока, и три десятых части ефы пшеничной муки, смешанной с елеем, в приношение хлебное, и один лог елея;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3121, 'rst66', 'lev', 14, 9, 'в седьмой день обреет все волосы свои, голову свою, бороду свою, брови глаз своих, все волосы свои обреет, и омоет одежды свои, и омоет тело свое водою, и будет чист.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3120, 'rst66', 'lev', 14, 8, 'Очищаемый омоет одежды свои, острижет все волосы свои, омоется водою, и будет чист; потом войдет в стан и пробудет семь дней вне шатра своего;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3119, 'rst66', 'lev', 14, 7, 'и покропит на очищаемого от проказы семь раз, и объявит его чистым, и пустит живую птицу в поле.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3118, 'rst66', 'lev', 14, 6, 'а сам он возьмет живую птицу, кедровое дерево, червленую нить и иссоп, и омочит их и живую птицу в крови птицы, заколотой над живою водою,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3117, 'rst66', 'lev', 14, 5, 'и прикажет священник заколоть одну птицу над глиняным сосудом, над живою водою;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3116, 'rst66', 'lev', 14, 4, 'то священник прикажет взять для очищаемого двух птиц живых, чистых, кедрового дерева, червленую нить и иссопа,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3115, 'rst66', 'lev', 14, 3, 'священник выйдет вон из стана, и если священник увидит, что прокаженный исцелился от болезни прокажения,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3114, 'rst66', 'lev', 14, 2, 'вот закон о прокаженном, когда надобно его очистить: приведут его к священнику;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3113, 'rst66', 'lev', 14, 1, 'И сказал Господь Моисею, говоря:');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3112, 'rst66', 'lev', 13, 59, 'Вот закон о язве проказы на одежде шерстяной или льняной, или на основе и на утоке, или на какой‐нибудь кожаной вещи, как объявлять ее чистою или нечистою.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3111, 'rst66', 'lev', 13, 58, 'Если же одежду, или основу, или уток, или какую‐нибудь кожаную вещь вымоешь, и сойдет с них язва, то должно вымыть их вторично, и они будут чисты.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3110, 'rst66', 'lev', 13, 57, 'Если же она опять покажется на одежде, или на основе, или на утоке, или на какой‐нибудь кожаной вещи, то это расцветающая язва: сожги на огне то, на чем язва.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3109, 'rst66', 'lev', 13, 56, 'если же священник увидит, что язва по омытии ее сделалась менее приметна, то священник пусть оторвет ее от одежды, или от кожи, или от основы, или от утока.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3108, 'rst66', 'lev', 13, 55, 'если по омытии зараженной <i>вещи</i> священник увидит, что язва не изменила вида своего, и не распространилась язва, то она нечиста, сожги ее на огне; это выеденная ямина на лицевой стороне или на изнанке;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3107, 'rst66', 'lev', 13, 54, 'то священник прикажет омыть то, на чем язва, и вторично заключит на семь дней;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3106, 'rst66', 'lev', 13, 53, 'Если же священник увидит, что язва не распространилась по одежде, или по основе, или по утоку, или по какой бы то ни было кожаной вещи,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3105, 'rst66', 'lev', 13, 52, 'он должен сжечь одежду, или основу, или уток шерстяной или льняной, или какую бы то ни было кожаную вещь, на которой будет язва, ибо это проказа едкая: должно сжечь на огне.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3104, 'rst66', 'lev', 13, 51, 'в седьмой день осмотрит священник зараженное, и если язва распространилась по одежде, или по основе, или по утоку, или по коже, или по какому‐либо изделию, сделанному из кожи, то это проказа едкая, язва нечистая;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3103, 'rst66', 'lev', 13, 50, 'священник осмотрит язву и заключит зараженное язвою на семь дней;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3102, 'rst66', 'lev', 13, 49, 'и пятно будет зеленоватое или красноватое на одежде, или на коже, или на основе, или на утоке, или на какой‐нибудь кожаной вещи, &mdash; то это язва проказы: должно показать ее священнику;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3101, 'rst66', 'lev', 13, 48, 'или на основе, или на утоке из льна или шерсти, или на коже, или на каком‐нибудь изделии кожаном,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3100, 'rst66', 'lev', 13, 47, 'Если язва проказы будет на одежде, на одежде шерстяной, или на одежде льняной,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3099, 'rst66', 'lev', 13, 46, 'Во все дни, доколе на нем язва, он должен быть нечист, нечист он; он должен жить отдельно, вне стана жилище его.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3098, 'rst66', 'lev', 13, 45, 'У прокаженного, на котором эта язва, должна быть разодрана одежда, и голова его должна быть не покрыта, и до уст он должен быть закрыт, и кричать: «нечист! нечист!»');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3097, 'rst66', 'lev', 13, 44, 'то он прокаженный, нечист он; священник должен объявить его нечистым, у него на голове язва.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3096, 'rst66', 'lev', 13, 43, 'священник осмотрит его, и если увидит, что опухоль язвы бела <i>или</i> красновата на плеши его или на лысине его, видом похожа на проказу кожи тела,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3095, 'rst66', 'lev', 13, 42, 'Если же на плеши или на лысине будет белое или красноватое пятно, то на плеши его или на лысине его расцвела проказа;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3094, 'rst66', 'lev', 13, 41, 'а если на передней стороне головы вылезли <i>волосы</i>, то это лысый: он чист.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3093, 'rst66', 'lev', 13, 40, 'Если у кого на голове вылезли <i>волосы</i>, то это плешивый: он чист;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3092, 'rst66', 'lev', 13, 39, 'и священник увидит, что на коже тела их пятна бледно‐белые, то это лишай, расцветший на коже: он чист.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3091, 'rst66', 'lev', 13, 38, 'Если у мужчины или у женщины на коже тела их будут пятна, пятна белые,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3090, 'rst66', 'lev', 13, 37, 'Если же паршивость остается в своем виде, и показывается на ней волос черный, то паршивость прошла, он чист; священник объявит его чистым.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3089, 'rst66', 'lev', 13, 36, 'и священник увидит, что паршивость распространяется по коже, то священник пусть не ищет желтоватого волоса: он нечист.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3088, 'rst66', 'lev', 13, 35, 'Если же после очищения его будет очень распространяться паршивость по коже,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3087, 'rst66', 'lev', 13, 34, 'в седьмой день священник осмотрит паршивость, и если паршивость не распространяется по коже и не окажется углубленною в коже, то священник объявит его чистым; пусть он омоет одежды свои, и будет чист.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3086, 'rst66', 'lev', 13, 33, 'то <i>больного</i> должно остричь, но паршивого места не остригать, и священник должен паршивого вторично заключить на семь дней;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3085, 'rst66', 'lev', 13, 32, 'в седьмой день священник осмотрит язву, и если паршивость не распространяется, и нет на ней желтоватого волоса, и паршивость не окажется углубленною в коже,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3084, 'rst66', 'lev', 13, 31, 'если же священник осмотрит язву паршивости и она не окажется углубленною в коже, и волос на ней не черный, то священник <i>имеющего</i> язву паршивости заключит на семь дней;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3083, 'rst66', 'lev', 13, 30, 'и осмотрит священник язву, и она окажется углубленною в коже, и волос на ней желтоватый, тонкий, то священник объявит их нечистыми: это паршивость, это проказа на голове или на бороде;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3082, 'rst66', 'lev', 13, 29, 'Если у мужчины или у женщины будет язва на голове или на бороде,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3081, 'rst66', 'lev', 13, 28, 'если же пятно остается на своем месте и не распространяется по коже, и притом мало приметно, то это опухоль от ожога; священник объявит его чистым, ибо это воспаление от ожога.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3080, 'rst66', 'lev', 13, 27, 'в седьмой день священник осмотрит его, и если оно очень распространяется по коже, то священник объявит его нечистым: это язва проказы;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3079, 'rst66', 'lev', 13, 26, 'если же священник увидит, что волос на пятне не бел, и оно не ниже кожи, и притом мало приметно, то священник заключит его на семь дней;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3078, 'rst66', 'lev', 13, 25, 'и священник увидит, что волос на пятне изменился в белый, и оно окажется углубленным в коже, то это проказа, она расцвела на ожоге; и священник объявит его нечистым: это язва проказы;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3077, 'rst66', 'lev', 13, 24, 'Или если у кого на коже тела будет ожог, и на зажившем ожоге окажется красноватое или белое пятно,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3076, 'rst66', 'lev', 13, 23, 'если же пятно остается на своем месте и не распространяется, то это воспаление нарыва, и священник объявит его чистым.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3075, 'rst66', 'lev', 13, 22, 'если она станет очень распространяться по коже, то священник объявит его нечистым: это язва;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3074, 'rst66', 'lev', 13, 21, 'если же священник увидит, что волос на ней не бел, и она не ниже кожи, и притом мало приметна, то священник заключит его на семь дней;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3073, 'rst66', 'lev', 13, 20, 'священник осмотрит его, и если оно окажется ниже кожи, и волос его изменился в белый, то священник объявит его нечистым: это язва проказы, она расцвела на нарыве;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3072, 'rst66', 'lev', 13, 19, 'и на месте нарыва появилась белая опухоль, или пятно белое или красноватое, то он должен явиться к священнику;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3071, 'rst66', 'lev', 13, 18, 'Если у кого на коже тела был нарыв и зажил,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3070, 'rst66', 'lev', 13, 17, 'священник осмотрит его, и если язва обратилась в белое, священник объявит больного чистым; он чист.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3069, 'rst66', 'lev', 13, 16, 'Если же живое мясо изменится и обратится в белое, пусть он придет к священнику;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3068, 'rst66', 'lev', 13, 15, 'священник, увидев живое мясо, объявит его нечистым; живое мясо нечисто: это проказа.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3067, 'rst66', 'lev', 13, 14, 'Когда же окажется на нем живое мясо, то он нечист;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3066, 'rst66', 'lev', 13, 13, 'и увидит священник, что проказа покрыла все тело его, то он объявит больного чистым, потому что все превратилось в белое: он чист.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3065, 'rst66', 'lev', 13, 12, 'Если же проказа расцветет на коже, и покроет проказа всю кожу больного от головы его до ног, сколько могут видеть глаза священника,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3064, 'rst66', 'lev', 13, 11, 'то это застарелая проказа на коже тела его; и священник объявит его нечистым и заключит его, ибо он нечист.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3063, 'rst66', 'lev', 13, 10, 'священник осмотрит, и если опухоль на коже бела, и волос изменился в белый, и на опухоли живое мясо,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3062, 'rst66', 'lev', 13, 9, 'Если будет на ком язва проказы, то должно привести его к священнику;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3061, 'rst66', 'lev', 13, 8, 'священник, увидев, что лишаи распространяются по коже, объявит его нечистым: это проказа.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3060, 'rst66', 'lev', 13, 7, 'Если же лишаи станут распространяться по коже, после того как он являлся к священнику для очищения, то он вторично должен явиться к священнику;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3059, 'rst66', 'lev', 13, 6, 'в седьмой день опять священник осмотрит его, и если язва менее приметна, и не распространилась язва по коже, то священник должен объявить его чистым: это лишаи, и пусть он омоет одежды свои, и будет чист.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3058, 'rst66', 'lev', 13, 5, 'в седьмой день священник осмотрит его, и если язва остается в своем виде и не распространяется язва по коже, то священник должен заключить его на другие семь дней;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3057, 'rst66', 'lev', 13, 4, 'А если на коже тела его пятно белое, но оно не окажется углубленным в кожу, и волосы на нем не изменились в белые, то священник <i>имеющего</i> язву должен заключить на семь дней;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3056, 'rst66', 'lev', 13, 3, 'священник осмотрит язву на коже тела, и если волосы на язве изменились в белые, и язва оказывается углубленною в кожу тела его, то это язва проказы; священник, осмотрев его, объявит его нечистым.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3055, 'rst66', 'lev', 13, 2, 'когда у кого появится на коже тела его опухоль, или лишаи, или пятно, и на коже тела его сделается как бы язва проказы, то должно привести его к Аарону священнику, или к одному из сынов его, священников;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3054, 'rst66', 'lev', 13, 1, 'И сказал Господь Моисею и Аарону, говоря:');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3053, 'rst66', 'lev', 12, 8, 'Если же она не в состоянии принести агнца, то пусть возьмет двух горлиц или двух молодых голубей, одного во всесожжение, а другого в жертву за грех, и очистит ее священник, и она будет чиста.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3052, 'rst66', 'lev', 12, 7, 'он принесет это пред Господа и очистит ее, и она будет чиста от течения кровей ее. Вот закон о родившей <i>младенца</i> мужеского или женского пола.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3051, 'rst66', 'lev', 12, 6, 'По окончании дней очищения своего за сына или за дочь она должна принести однолетнего агнца во всесожжение и молодого голубя или горлицу в жертву за грех, ко входу скинии собрания, к священнику;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3050, 'rst66', 'lev', 12, 5, 'Если же она родит <i>младенца</i> женского пола, то во время очищения своего она будет нечиста две недели, и шестьдесят шесть дней должна сидеть, очищаясь от кровей своих.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3049, 'rst66', 'lev', 12, 4, 'и тридцать три дня должна она сидеть, очищаясь от кровей своих; ни к чему священному не должна прикасаться и к святилищу не должна приходить, пока не исполнятся дни очищения ее.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3048, 'rst66', 'lev', 12, 3, 'в восьмой же день обрежется у него крайняя плоть его;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3047, 'rst66', 'lev', 12, 2, 'скажи сынам Израилевым: если женщина зачнет и родит <i>младенца</i> мужеского пола, то она нечиста будет семь дней; как во дни страдания ее очищением, она будет нечиста;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3046, 'rst66', 'lev', 12, 1, 'И сказал Господь Моисею, говоря:');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3045, 'rst66', 'lev', 11, 47, 'чтобы отличать нечистое от чистого, и животных, которых можно есть, от животных, которых есть не должно.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3044, 'rst66', 'lev', 11, 46, 'Вот закон о скоте, о птицах, о всех животных, живущих в водах, и о всех животных, пресмыкающихся по земле,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3043, 'rst66', 'lev', 11, 45, 'ибо Я &mdash; Господь, выведший вас из земли Египетской, чтобы быть вашим Богом. Итак, будьте святы, потому что Я свят.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3042, 'rst66', 'lev', 11, 44, 'ибо Я &mdash; Господь, Бог ваш: освящайтесь и будьте святы, ибо Я свят; и не оскверняйте душ ваших каким‐либо животным, ползающим по земле,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3041, 'rst66', 'lev', 11, 43, 'не оскверняйте душ ваших каким‐либо животным пресмыкающимся и не делайте себя чрез них нечистыми, чтоб быть чрез них нечистыми,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3040, 'rst66', 'lev', 11, 42, 'всего, ползающего на чреве, и всего, ходящего на четырех ногах, и многоножных из животных, пресмыкающихся по земле, не ешьте, ибо они скверны;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3039, 'rst66', 'lev', 11, 41, 'Всякое животное, пресмыкающееся по земле, скверно для вас, не должно есть <i>его;</i>');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3038, 'rst66', 'lev', 11, 40, 'и тот, кто будет есть мертвечину его, должен омыть одежды свои, и нечист будет до вечера; и тот, кто понесет труп его, должен омыть одежды свои, и нечист будет до вечера.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3037, 'rst66', 'lev', 11, 39, 'И когда умрет какой‐либо скот, который употребляется вами в пищу, то прикоснувшийся к трупу его нечист будет до вечера;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3036, 'rst66', 'lev', 11, 38, 'если же тогда, как вода налита на семя, упадет на него что‐нибудь от трупа их, то оно нечисто для вас.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3035, 'rst66', 'lev', 11, 37, 'И если что‐нибудь от трупа их упадет на какое‐либо семя, которое сеют, то оно чисто;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3034, 'rst66', 'lev', 11, 36, 'только источник и колодезь, вмещающий воду, остаются чистыми; а кто прикоснется к трупу их, тот нечист.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3033, 'rst66', 'lev', 11, 35, 'Всё, на что упадет что‐нибудь от трупа их, нечисто будет: печь и очаг должно разломать, они нечисты; и они должны быть нечисты для вас;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3032, 'rst66', 'lev', 11, 34, 'Всякая пища, которую едят, на которой была вода <i>из такого сосуда</i>, нечиста будет, и всякое питье, которое пьют, во всяком <i>таком</i> сосуде нечисто будет.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3031, 'rst66', 'lev', 11, 33, 'если же которое‐нибудь из них упадет в какой‐нибудь глиняный сосуд, то находящееся в нем будет нечисто, и самый <i>сосуд</i> разбейте.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3030, 'rst66', 'lev', 11, 32, 'И всё, на что упадет которое‐нибудь из них мертвое, всякий деревянный сосуд, или одежда, или кожа, или мешок, и всякая вещь, которая употребляется на дело, будут нечисты: в воду должно положить их, и нечисты будут до вечера, потом будут чисты;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3029, 'rst66', 'lev', 11, 31, 'Сии нечисты для вас из всех пресмыкающихся: всякий, кто прикоснется к ним мертвым, нечист будет до вечера.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3028, 'rst66', 'lev', 11, 30, 'анака, хамелеон, летаа, хомет и тиншемет.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3027, 'rst66', 'lev', 11, 29, 'Вот что нечисто для вас из животных, пресмыкающихся по земле: крот, мышь, ящерица с ее породою,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3026, 'rst66', 'lev', 11, 28, 'кто возьмет труп их, тот должен омыть одежды свои, и нечист будет до вечера: нечисты они для вас.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3025, 'rst66', 'lev', 11, 27, 'Из всех зверей четвероногих те, которые ходят на лапах, нечисты для вас: всякий, кто прикоснется к трупу их, нечист будет до вечера;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3024, 'rst66', 'lev', 11, 26, 'Всякий скот, у которого копыта раздвоены, но нет глубокого разреза, и который не жует жвачки, нечист для вас: всякий, кто прикоснется к нему, будет нечист.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3023, 'rst66', 'lev', 11, 25, 'и всякий, кто возьмет труп их, должен омыть одежду свою и нечист будет до вечера.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3022, 'rst66', 'lev', 11, 24, 'от них вы будете нечисты: всякий, кто прикоснется к трупу их, нечист будет до вечера;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3021, 'rst66', 'lev', 11, 23, 'Всякое <i>другое</i> пресмыкающееся, крылатое, у которого четыре ноги, скверно для вас;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3020, 'rst66', 'lev', 11, 22, 'сих ешьте из них: саранчу с ее породою, солам с ее породою, харгол с ее породою и хагаб с ее породою.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3019, 'rst66', 'lev', 11, 21, 'из всех пресмыкающихся, крылатых, ходящих на четырех <i>ногах</i>, тех только ешьте, у которых есть голени выше ног, чтобы скакать ими по земле;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3018, 'rst66', 'lev', 11, 20, 'Все <i>животные</i> пресмыкающиеся, крылатые, ходящие на четырех <i>ногах</i>, скверны для вас;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3017, 'rst66', 'lev', 11, 19, 'цапли, зуя с породою его, удода и нетопыря.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3016, 'rst66', 'lev', 11, 18, 'лебедя, пеликана и сипа,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3015, 'rst66', 'lev', 11, 17, 'филина, рыболова и ибиса,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3014, 'rst66', 'lev', 11, 16, 'страуса, совы, чайки и ястреба с породою его,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3013, 'rst66', 'lev', 11, 15, 'всякого ворона с породою его,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3012, 'rst66', 'lev', 11, 14, 'коршуна и сокола с породою его,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3011, 'rst66', 'lev', 11, 13, 'Из птиц же гнушайтесь сих (не должно их есть, скверны они): орла, грифа и морского орла,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3010, 'rst66', 'lev', 11, 12, 'все <i>животные</i>, у которых нет перьев и чешуи, в воде, скверны для вас.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3009, 'rst66', 'lev', 11, 11, 'они должны быть скверны для вас: мяса их не ешьте и трупов их гнушайтесь;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3008, 'rst66', 'lev', 11, 10, 'а все те, у которых нет перьев и чешуи (в морях ли, или реках, из всех, плавающих в водах, и из всего, живущего в водах), скверны для вас;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3007, 'rst66', 'lev', 11, 9, 'Из всех <i>животных</i>, которые в воде, ешьте сих: у которых есть перья и чешуя (в воде, в морях ли, или реках), тех ешьте;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3006, 'rst66', 'lev', 11, 8, 'мяса их не ешьте и к трупам их не прикасайтесь; нечисты они для вас.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3005, 'rst66', 'lev', 11, 7, 'и свиньи, потому что копыта у нее раздвоены и на копытах разрез глубокий, но она не жует жвачки, нечиста она для вас;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3004, 'rst66', 'lev', 11, 6, 'и зайца, потому что он жует жвачку, но копыта у него не раздвоены, нечист он для вас;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3003, 'rst66', 'lev', 11, 5, 'и тушканчика, потому что он жует жвачку, но копыта у него не раздвоены, нечист он для вас;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3002, 'rst66', 'lev', 11, 4, 'только сих не ешьте из жующих жвачку и имеющих раздвоенные копыта: верблюда, потому что он жует жвачку, но копыта у него не раздвоены, нечист он для вас;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3001, 'rst66', 'lev', 11, 3, 'всякий скот, у которого раздвоены копыта, и на копытах глубокий разрез, и который жует жвачку, ешьте;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3000, 'rst66', 'lev', 11, 2, 'скажите сынам Израилевым: вот животные, которых можно вам есть из всего скота на земле:');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (2999, 'rst66', 'lev', 11, 1, 'И сказал Господь Моисею и Аарону, говоря им:');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (2998, 'rst66', 'lev', 10, 20, 'И услышал Моисей, и одобрил.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (2997, 'rst66', 'lev', 10, 19, 'Аарон сказал Моисею: вот, сегодня принесли они жертву свою за грех и всесожжение свое пред Господом, и это случилось со мною. Если я сегодня съем жертву за грех, будет ли это угодно Господу?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (2996, 'rst66', 'lev', 10, 18, 'вот, кровь ее не внесена внутрь святилища, а вы должны были есть ее на святом месте, как повелено мне.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (2995, 'rst66', 'lev', 10, 17, 'почему вы не ели жертвы за грех на святом месте? Ибо она святыня великая, и она дана вам, чтобы снимать грехи с общества и очищать их пред Господом;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (2994, 'rst66', 'lev', 10, 16, 'И козла жертвы за грех искал Моисей &mdash; и вот он сожжен. И разгневался <i>Моисей</i> на Елеазара и Ифамара, оставшихся сынов Аароновых, и сказал:');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (2993, 'rst66', 'lev', 10, 15, 'плечо возношения и грудь потрясания должны они приносить с жертвами тука, потрясая пред лицом Господним, и да будет это вечным участком тебе и сыновьям твоим с тобою, как повелел Господь.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (2992, 'rst66', 'lev', 10, 14, 'и грудь потрясания и плечо возношения ешьте на чистом месте, ты и сыновья твои и дочери твои с тобою, ибо это дано в участок тебе и в участок сынам твоим из мирных жертв сынов Израилевых;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (2991, 'rst66', 'lev', 10, 13, 'и ешьте его на святом месте, ибо это участок твой и участок сынов твоих из жертв Господних: так мне повелено <i>от Господа;</i>');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (2990, 'rst66', 'lev', 10, 12, 'И сказал Моисей Аарону и Елеазару и Ифамару, оставшимся сынам его: возьмите приношение хлебное, оставшееся от жертв Господних, и ешьте его, пресное, у жертвенника, ибо это великая святыня;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (2989, 'rst66', 'lev', 10, 11, 'и научать сынов Израилевых всем уставам, которые изрек им Господь чрез Моисея.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (2988, 'rst66', 'lev', 10, 10, 'чтобы вы могли отличать священное от несвященного и нечистое от чистого,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (2987, 'rst66', 'lev', 10, 9, 'вина и крепких напитков не пей ты и сыны твои с тобою, когда входите в скинию собрания, чтобы не умереть. <i>Это</i> вечное постановление в роды ваши,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (2986, 'rst66', 'lev', 10, 8, 'И сказал Господь Аарону, говоря:');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (2985, 'rst66', 'lev', 10, 7, 'и из дверей скинии собрания не выходите, чтобы не умереть вам, ибо на вас елей помазания Господня. И сделали по слову Моисея.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (2984, 'rst66', 'lev', 10, 6, 'Аарону же, и Елеазару, и Ифамару, сынам его, Моисей сказал: голов ваших не обнажайте и одежд ваших не раздирайте, чтобы вам не умереть и не навести гнева на все общество; но братья ваши, весь дом Израилев, могут плакать о сожженных, которых сожег Господь,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (2983, 'rst66', 'lev', 10, 5, 'И пошли, и вынесли их в хитонах их за стан, как сказал Моисей.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (2982, 'rst66', 'lev', 10, 4, 'И позвал Моисей Мисаила и Елцафана, сынов Узиила, дяди Ааронова, и сказал им: пойдите, вынесите братьев ваших из святилища за стан.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (2981, 'rst66', 'lev', 10, 3, 'И сказал Моисей Аарону: вот о чем говорил Господь, когда сказал: в приближающихся ко Мне освящусь и пред всем народом прославлюсь. Аарон молчал.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (2980, 'rst66', 'lev', 10, 2, 'и вышел огонь от Господа, и сжег их, и умерли они пред лицом Господним.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (2979, 'rst66', 'lev', 10, 1, 'Надав и Авиуд, сыны Аароновы, взяли каждый свою кадильницу, и положили в них огня, и вложили в него курений, и принесли пред Господа огонь чуждый, которого Он не велел им;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (2978, 'rst66', 'lev', 9, 24, 'и вышел огонь от Господа, и сжег на жертвеннике всесожжение и тук; и видел весь народ, и воскликнул от радости, и пал на лицо свое.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (2977, 'rst66', 'lev', 9, 23, 'И вошли Моисей и Аарон в скинию собрания, и вышли, и благословили народ. И явилась слава Господня всему народу:');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (2976, 'rst66', 'lev', 9, 22, 'И поднял Аарон руки свои, <i>обратившись</i> к народу, и благословил его, и сошел, совершив жертву за грех, всесожжение и жертву мирную.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (2975, 'rst66', 'lev', 9, 21, 'грудь же и правое плечо принес Аарон, потрясая пред лицом Господним, как повелел Моисей.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (2974, 'rst66', 'lev', 9, 20, 'и положили тук на грудь, и он сжег тук на жертвеннике;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (2973, 'rst66', 'lev', 9, 19, '<i>поднесли</i> и тук из вола, и из овна курдюк, и <i>тук</i>, покрывающий <i>внутренности</i>, почки и сальник на печени,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (2972, 'rst66', 'lev', 9, 18, 'И заколол вола и овна, которые от народа, в жертву мирную; и сыны Аарона поднесли ему кровь, и он покропил ею на жертвенник со всех сторон;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (2971, 'rst66', 'lev', 9, 17, 'И принес приношение хлебное, и наполнил им руки свои, и сжег на жертвеннике сверх утреннего всесожжения.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (2970, 'rst66', 'lev', 9, 16, 'И принес всесожжение, и совершил его по уставу.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (2969, 'rst66', 'lev', 9, 15, 'И принес приношение от народа, и взял от народа козла за грех, и заколол его, и принес его в жертву за грех, как и прежнего.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (2968, 'rst66', 'lev', 9, 14, 'а внутренности и ноги омыл и сжег со всесожжением на жертвеннике.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (2967, 'rst66', 'lev', 9, 13, 'и принесли ему всесожжение в кусках и голову, и он сжег на жертвеннике,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (2966, 'rst66', 'lev', 9, 12, 'И заколол всесожжение, и сыны Аарона поднесли ему кровь; он покропил ею на жертвенник со всех сторон;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (2965, 'rst66', 'lev', 9, 11, 'мясо же и кожу сжег на огне вне стана.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (2964, 'rst66', 'lev', 9, 10, 'а тук, и почки, и сальник на печени от жертвы за грех сжег на жертвеннике, как повелел Господь Моисею;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (2963, 'rst66', 'lev', 9, 9, 'сыны Аарона поднесли ему кровь, и он омочил перст свой в крови и возложил на роги жертвенника, а <i>остальную</i> кровь вылил к подножию жертвенника,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (2962, 'rst66', 'lev', 9, 8, 'И приступил Аарон к жертвеннику, и заколол тельца, который за него, в жертву за грех;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (2961, 'rst66', 'lev', 9, 7, 'И сказал Моисей Аарону: приступи к жертвеннику и соверши жертву твою о грехе и всесожжение твое, и очисти себя и народ, и сделай приношение от народа, и очисти их, как повелел Господь.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (2960, 'rst66', 'lev', 9, 6, 'И сказал Моисей: вот что повелел Господь сделать, и явится вам слава Господня.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (2959, 'rst66', 'lev', 9, 5, 'И принесли то, что приказал Моисей, пред скинию собрания, и пришло все общество, и стало пред лицом Господним.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (2958, 'rst66', 'lev', 9, 4, 'и вола, и овна в жертву мирную, чтобы совершить жертвоприношение пред лицом Господним, и приношение хлебное, смешанное с елеем, ибо сегодня Господь явится вам.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (2957, 'rst66', 'lev', 9, 3, 'и сынам Израилевым скажи: возьмите козла в жертву за грех, и тельца, и агнца, однолетних, без порока, во всесожжение,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (2956, 'rst66', 'lev', 9, 2, 'и сказал Аарону: возьми себе из волов тельца в жертву за грех и овна во всесожжение, обоих без порока, и представь пред лицо Господне;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (2955, 'rst66', 'lev', 9, 1, 'В восьмой день призвал Моисей Аарона, и сынов его, и старейшин Израилевых');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (2954, 'rst66', 'lev', 8, 36, 'И исполнил Аарон и сыны его все, что повелел Господь чрез Моисея.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (2953, 'rst66', 'lev', 8, 35, 'у входа скинии собрания будьте день и ночь в продолжение семи дней и будьте на страже у Господа, чтобы не умереть, ибо так мне повелено <i>от Господа Бога</i>.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (2952, 'rst66', 'lev', 8, 34, 'как сегодня было сделано, так повелел Господь делать для очищения вас;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (2951, 'rst66', 'lev', 8, 33, 'Семь дней не отходите от дверей скинии собрания, пока не исполнятся дни посвящения вашего, ибо семь дней должно совершаться посвящение ваше;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (2950, 'rst66', 'lev', 8, 32, 'а остатки мяса и хлеба сожгите на огне.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (2949, 'rst66', 'lev', 8, 31, 'И сказал Моисей Аарону и сынам его: сварите мясо у входа скинии собрания и там ешьте его с хлебом, который в корзине посвящения, как мне повелено и сказано: Аарон и сыны его должны есть его;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (2948, 'rst66', 'lev', 8, 30, 'И взял Моисей елея помазания и крови, которая на жертвеннике, и покропил Аарона и одежды его, и сынов его, и одежды сынов его с ним; и так освятил Аарона и одежды его, и сынов его, и одежды сынов его с ним.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (2947, 'rst66', 'lev', 8, 29, 'И взял Моисей грудь, и принес ее, потрясая пред лицом Господним: это была доля Моисеева от овна посвящения, как повелел Господь Моисею.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (2946, 'rst66', 'lev', 8, 28, 'и взял это Моисей с рук их, и сжег на жертвеннике со всесожжением: это жертва посвящения в приятное благоухание, это жертва Господу.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (2945, 'rst66', 'lev', 8, 27, 'и положил все это на руки Аарону и на руки сынам его, и принес это, потрясая пред лицом Господним;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (2944, 'rst66', 'lev', 8, 26, 'и из корзины с опресноками, которая пред Господом, взял один опреснок, и один хлеб с елеем, и одну лепешку, и возложил на тук и на правое плечо;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (2943, 'rst66', 'lev', 8, 25, 'И взял тук, и курдюк, и весь тук, который на внутренностях, и сальник на печени, и обе почки, и тук их, и правое плечо;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (2942, 'rst66', 'lev', 8, 24, 'И привел Моисей сынов Аароновых, и возложил крови на край правого уха их, и на большой палец правой руки их, и на большой палец правой ноги их, и покропил Моисей кровью на жертвенник со всех сторон.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (2941, 'rst66', 'lev', 8, 23, 'и заколол <i>его</i> Моисей, и взял крови его, и возложил на край правого уха Ааронова, и на большой палец правой руки его, и на большой палец правой ноги его.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (2940, 'rst66', 'lev', 8, 22, 'И привел другого овна, овна посвящения, и возложили Аарон и сыны его руки свои на голову овна;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (2939, 'rst66', 'lev', 8, 21, 'а внутренности и ноги вымыл водою, и сжег Моисей всего овна на жертвеннике: это всесожжение в приятное благоухание, это жертва Господу, как повелел Господь Моисею.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (2938, 'rst66', 'lev', 8, 20, 'и рассек овна на части, и сжег Моисей голову, и части, и тук,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (2937, 'rst66', 'lev', 8, 19, 'и заколол <i>его</i> Моисей, и покропил кровью на жертвенник со всех сторон;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (2936, 'rst66', 'lev', 8, 18, 'И привел овна для всесожжения, и возложили Аарон и сыны его руки свои на голову овна;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (2935, 'rst66', 'lev', 8, 17, 'а тельца, и кожу его, и мясо его, и нечистоту его сжег на огне вне стана, как повелел Господь Моисею.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (2934, 'rst66', 'lev', 8, 16, 'И взял весь тук, который на внутренностях, и сальник на печени, и обе почки, и тук их, и сжег Моисей на жертвеннике;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (2933, 'rst66', 'lev', 8, 15, 'и заколол <i>его</i> Моисей, и взял крови, и перстом своим возложил на роги жертвенника со всех сторон, и очистил жертвенник, а <i>остальную</i> кровь вылил к подножию жертвенника, и освятил его, чтобы сделать его чистым.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (2932, 'rst66', 'lev', 8, 14, 'И привел тельца для жертвы за грех, и Аарон и сыны его возложили руки свои на голову тельца за грех;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (2931, 'rst66', 'lev', 8, 13, 'И привел Моисей сынов Аароновых, и одел их в хитоны, и опоясал их поясом, и возложил на них кидары, как повелел Господь Моисею.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (2930, 'rst66', 'lev', 8, 12, 'и возлил елей помазания на голову Аарона, и помазал его, чтоб освятить его.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (2929, 'rst66', 'lev', 8, 11, 'и покропил им на жертвенник семь раз, и помазал жертвенник и все принадлежности его, и умывальницу, и подножие ее, чтобы освятить их;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (2928, 'rst66', 'lev', 8, 10, 'И взял Моисей елей помазания, и помазал скинию и все, что в ней, и освятил это;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (2927, 'rst66', 'lev', 8, 9, 'и возложил на голову его кидар, а на кидар с передней стороны его возложил полированную дощечку, диадиму святыни, как повелел Господь Моисею.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (2926, 'rst66', 'lev', 8, 8, 'и возложил на него наперсник, и на наперсник положил урим и туммим,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (2925, 'rst66', 'lev', 8, 7, 'и возложил на него хитон, и опоясал его поясом, и надел на него верхнюю ризу, и возложил на него ефод, и опоясал его поясом ефода, и прикрепил им ефод на нем,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (2924, 'rst66', 'lev', 8, 6, 'И привел Моисей Аарона и сынов его, и омыл их водою;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (2923, 'rst66', 'lev', 8, 5, 'И сказал Моисей к обществу: вот что повелел Господь сделать.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (2922, 'rst66', 'lev', 8, 4, 'Моисей сделал так, как повелел ему Господь, и собралось общество ко входу скинии собрания.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (2921, 'rst66', 'lev', 8, 3, 'и собери все общество ко входу скинии собрания.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (2920, 'rst66', 'lev', 8, 2, 'возьми Аарона и сынов его с ним, и одежды, и елей помазания, и тельца для жертвы за грех, и двух овнов, и корзину опресноков,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (2919, 'rst66', 'lev', 8, 1, 'И сказал Господь Моисею, говоря:');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (2918, 'rst66', 'lev', 7, 38, 'который дал Господь Моисею на горе Синае, когда повелел сынам Израилевым, в пустыне Синайской, приносить Господу приношения их.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (2917, 'rst66', 'lev', 7, 37, 'Вот закон о всесожжении, о приношении хлебном, о жертве за грех, о жертве повинности, о жертве посвящения и о жертве мирной,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (2916, 'rst66', 'lev', 7, 36, 'который повелел Господь давать им со дня помазания их от сынов Израилевых. <i>Это</i> вечное постановление в роды их.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (2915, 'rst66', 'lev', 7, 35, 'Вот участок Аарону и участок сынам его из жертв Господних со дня, когда они предстанут пред Господа для священнодействия,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (2914, 'rst66', 'lev', 7, 34, 'ибо Я беру от сынов Израилевых из мирных жертв их грудь потрясания и плечо возношения, и отдаю их Аарону священнику и сынам его в вечный участок от сынов Израилевых.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (2913, 'rst66', 'lev', 7, 33, 'кто из сынов Аароновых приносит кровь из мирной жертвы и тук, тому и правое плечо на долю;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (2912, 'rst66', 'lev', 7, 32, 'и правое плечо, как возношение, из мирных жертв ваших отдавайте священнику:');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (2911, 'rst66', 'lev', 7, 31, 'тук сожжет священник на жертвеннике, а грудь принадлежит Аарону и сынам его;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (2910, 'rst66', 'lev', 7, 30, 'своими руками должен он принести в жертву Господу: тук с грудью должен он принести, потрясая грудь пред лицом Господним;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (2909, 'rst66', 'lev', 7, 29, 'скажи сынам Израилевым: кто представляет мирную жертву свою Господу, тот из мирной жертвы часть должен принести в приношение Господу;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (2908, 'rst66', 'lev', 7, 28, 'И сказал Господь Моисею, говоря:');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (2907, 'rst66', 'lev', 7, 27, 'а кто будет есть какую‐нибудь кровь, истребится душа та из народа своего.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (2906, 'rst66', 'lev', 7, 26, 'и никакой крови не ешьте во всех жилищах ваших ни из птиц, ни из скота;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (2905, 'rst66', 'lev', 7, 25, 'ибо кто будет есть тук из скота, который приносится в жертву Господу, истребится душа та из народа своего;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (2904, 'rst66', 'lev', 7, 24, 'Тук из мертвого и тук из растерзанного зверем можно употреблять на всякое дело; а есть не ешьте его;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (2903, 'rst66', 'lev', 7, 23, 'скажи сынам Израилевым: никакого тука ни из вола, ни из овцы, ни из козла не ешьте.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (2902, 'rst66', 'lev', 7, 22, 'И сказал Господь Моисею, говоря:');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (2901, 'rst66', 'lev', 7, 21, 'и если какая душа, прикоснувшись к чему‐нибудь нечистому, к нечистоте человеческой, или к нечистому скоту, или какому‐нибудь нечистому гаду, будет есть мясо мирной жертвы Господней, то истребится душа та из народа своего.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (2900, 'rst66', 'lev', 7, 20, 'если же какая душа, имея на себе нечистоту, будет есть мясо мирной жертвы Господней, то истребится душа та из народа своего;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (2899, 'rst66', 'lev', 7, 19, 'мяса сего, если оно прикоснется к чему‐либо нечистому, не должно есть, но должно сжечь его на огне; а мясо чистое может есть всякий чистый;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (2898, 'rst66', 'lev', 7, 18, 'если же будут есть мясо мирной жертвы на третий день, то она не будет благоприятна; кто ее принесет, тому ни во что не вменится: это осквернение, и кто будет есть ее, тот понесет на себе грех;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (2897, 'rst66', 'lev', 7, 17, 'а оставшееся от жертвенного мяса к третьему дню должно сжечь на огне;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (2896, 'rst66', 'lev', 7, 16, 'Если же кто приносит жертву по обету или от усердия, то жертву его должно есть в день приношения, и на другой день оставшееся от нее есть можно,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (2895, 'rst66', 'lev', 7, 15, 'мясо мирной жертвы благодарности должно съесть в день приношения ее, не должно оставлять от него до утра.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (2894, 'rst66', 'lev', 7, 14, 'одно что‐нибудь из всего приношения своего пусть принесет он в возношение Господу: это принадлежит священнику, кропящему кровью мирной жертвы;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (2893, 'rst66', 'lev', 7, 13, 'кроме лепешек пусть он приносит в приношение свое квасный хлеб, при мирной жертве благодарной;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (2892, 'rst66', 'lev', 7, 12, 'если кто в благодарность приносит ее, то при жертве благодарности он должен принести пресные хлебы, смешанные с елеем, и пресные лепешки, помазанные елеем, и пшеничную муку, напитанную <i>елеем</i>, хлебы, смешанные с елеем;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (2891, 'rst66', 'lev', 7, 11, 'Вот закон о жертве мирной, которую приносят Господу:');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (2890, 'rst66', 'lev', 7, 10, 'и всякое приношение хлебное, смешанное с елеем и сухое, принадлежит всем сынам Аароновым, как одному, так и другому.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (2889, 'rst66', 'lev', 7, 9, 'и всякое приношение хлебное, которое печено в печи, и всякое приготовленное в горшке или на сковороде, принадлежит священнику, приносящему его;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (2888, 'rst66', 'lev', 7, 8, 'И когда священник приносит чью‐нибудь жертву всесожжения, кожа от <i>жертвы</i> всесожжения, которое он приносит, принадлежит священнику;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (2887, 'rst66', 'lev', 7, 7, 'Как о жертве за грех, так и о жертве повинности закон один: она принадлежит священнику, который очищает посредством ее.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (2886, 'rst66', 'lev', 7, 6, 'Весь мужеский пол священнического рода может есть ее; на святом месте должно есть ее: это великая святыня.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (2885, 'rst66', 'lev', 7, 5, 'и сожжет сие священник на жертвеннике в жертву Господу: это жертва повинности.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (2884, 'rst66', 'lev', 7, 4, 'и обе почки, и тук, который на них, который на стегнах, и сальник, который на печени; с почками пусть он отделит сие;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (2883, 'rst66', 'lev', 7, 3, '<i>приносящий</i> должен представить из нее весь тук, курдюк и тук, покрывающий внутренности,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (2882, 'rst66', 'lev', 7, 2, 'жертву повинности должно закалать на том месте, где закалается всесожжение, и кровью ее кропить на жертвенник со всех сторон;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (2881, 'rst66', 'lev', 7, 1, 'Вот закон о жертве повинности: это великая святыня;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (2880, 'rst66', 'lev', 6, 30, 'а всякая жертва за грех, от которой кровь вносится в скинию собрания для очищения во святилище, не должна быть съедаема; ее должно сожигать на огне.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (2879, 'rst66', 'lev', 6, 29, 'весь мужеский пол священнического рода может есть ее: это великая святыня;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (2878, 'rst66', 'lev', 6, 28, 'глиняный сосуд, в котором она варилась, должно разбить; если же она варилась в медном сосуде, то должно его вычистить и вымыть водою;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (2877, 'rst66', 'lev', 6, 27, 'все, что прикоснется к мясу ее, освятится; и если кровью ее обрызгана будет одежда, то обрызганное омой на святом месте;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (2876, 'rst66', 'lev', 6, 26, 'священник, совершающий жертву за грех, должен есть ее; она должна быть съедаема на святом месте, на дворе скинии собрания;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (2875, 'rst66', 'lev', 6, 25, 'скажи Аарону и сынам его: вот закон о жертве за грех: жертва за грех должна быть закалаема пред Господом на том месте, где закалается всесожжение; это великая святыня;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (2874, 'rst66', 'lev', 6, 24, 'И сказал Господь Моисею, говоря:');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (2873, 'rst66', 'lev', 6, 23, 'и всякое хлебное приношение от священника все да будет сожигаемо, а не съедаемо.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (2872, 'rst66', 'lev', 6, 22, 'и священник, помазанный на место его из сынов его, должен совершать сие: это вечный устав Господа. Вся она должна быть сожжена;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (2871, 'rst66', 'lev', 6, 21, 'на сковороде в елее она должна быть приготовлена; напитанную <i>елеем</i> приноси ее в кусках, как разламывается в куски приношение хлебное; приноси ее в приятное благоухание Господу;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (2870, 'rst66', 'lev', 6, 20, 'вот приношение от Аарона и сынов его, которое принесут они Господу в день помазания его: десятая часть ефы пшеничной муки в жертву постоянную, половина сего для утра и половина для вечера;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (2869, 'rst66', 'lev', 6, 19, 'И сказал Господь Моисею, говоря:');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (2868, 'rst66', 'lev', 6, 18, 'Все потомки Аароновы мужеского пола могут есть ее. Это вечный участок в роды ваши из жертв Господних. Все, прикасающееся к ним, освятится.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (2867, 'rst66', 'lev', 6, 17, 'не должно печь его квасным. Сие даю Я им в долю из жертв Моих. Это великая святыня, подобно как жертва за грех и жертва повинности.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (2866, 'rst66', 'lev', 6, 16, 'а остальное из него пусть едят Аарон и сыны его. Пресным должно есть его на святом месте, на дворе скинии собрания пусть едят его;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (2865, 'rst66', 'lev', 6, 15, 'и пусть возьмет <i>священник</i> горстью своею из приношения хлебного и пшеничной муки, и елея, и весь ливан, который на жертве, и сожжет на жертвеннике: <i>это</i> приятное благоухание, в память пред Господом;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (2864, 'rst66', 'lev', 6, 14, 'Вот закон о приношении хлебном: сыны Аароновы должны приносить его пред Господа к жертвеннику;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (2863, 'rst66', 'lev', 6, 13, 'огонь непрестанно пусть горит на жертвеннике <i>и</i> не угасает.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (2862, 'rst66', 'lev', 6, 12, 'а огонь на жертвеннике пусть горит, не угасает; и пусть священник зажигает на нем дрова каждое утро, и раскладывает на нем всесожжение, и сожигает на нем тук мирной жертвы;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (2861, 'rst66', 'lev', 6, 11, 'и пусть снимет с себя одежды свои, и наденет другие одежды, и вынесет пепел вне стана на чистое место;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (2860, 'rst66', 'lev', 6, 10, 'и пусть священник оденется в льняную одежду свою, и наденет на тело свое льняное нижнее платье, и снимет пепел от всесожжения, которое сжег огонь на жертвеннике, и положит его подле жертвенника;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (2859, 'rst66', 'lev', 6, 9, 'заповедай Аарону и сынам его: вот закон всесожжения: всесожжение пусть остается на месте сожигания на жертвеннике всю ночь до утра, и огонь жертвенника пусть горит на нем;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (2858, 'rst66', 'lev', 6, 8, 'И сказал Господь Моисею, говоря:');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (2857, 'rst66', 'lev', 6, 7, 'и очистит его священник пред Господом, и прощено будет ему, что бы он ни сделал, все, в чем он сделался виновным.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (2856, 'rst66', 'lev', 6, 6, 'и за вину свою пусть принесет Господу к священнику в жертву повинности из стада овец овна без порока, по оценке твоей;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (2855, 'rst66', 'lev', 6, 5, 'или если он в чем поклялся ложно, то должен отдать сполна, и приложить к тому пятую долю, и отдать тому, кому принадлежит, в день приношения жертвы повинности;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (2854, 'rst66', 'lev', 6, 4, 'то, согрешив и сделавшись виновным, он должен возвратить похищенное, что похитил, или отнятое, что отнял, или порученное, что ему поручено, или потерянное, что он нашел;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (2853, 'rst66', 'lev', 6, 3, 'или найдет потерянное, и запрется в том, и поклянется ложно в чем‐нибудь, что люди делают и тем грешат, &mdash;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (2852, 'rst66', 'lev', 6, 2, 'если кто согрешит, и сделает преступление пред Господом, и запрется пред ближним своим в том, что ему поручено, или у него положено, или им похищено, или обманет ближнего своего,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (2851, 'rst66', 'lev', 6, 1, 'И сказал Господь Моисею, говоря:');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (2850, 'rst66', 'lev', 5, 19, 'Это жертва повинности, <i>которою</i> он провинился пред Господом.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (2849, 'rst66', 'lev', 5, 18, 'пусть принесет к священнику в жертву повинности овна без порока, по оценке твоей, и загладит священник проступок его, в чем он преступил по неведению, и прощено будет ему.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (2848, 'rst66', 'lev', 5, 17, 'Если кто согрешит и сделает что‐нибудь против заповедей Господних, чего не надлежало делать, и по неведению сделается виновным и понесет на себе грех,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (2847, 'rst66', 'lev', 5, 16, 'за ту святыню, против которой он согрешил, пусть воздаст и прибавит к тому пятую долю, и отдаст сие священнику, и священник очистит его овном жертвы повинности, и прощено будет ему.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (2846, 'rst66', 'lev', 5, 15, 'если кто сделает преступление и по ошибке согрешит против посвященного Господу, пусть за вину свою принесет Господу из стада овец овна без порока, по твоей оценке, серебряными сиклями по сиклю священному, в жертву повинности;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (2845, 'rst66', 'lev', 5, 14, 'И сказал Господь Моисею, говоря:');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (2844, 'rst66', 'lev', 5, 13, 'и так очистит его священник от греха его, которым он согрешил в котором‐нибудь из оных <i>случаев</i>, и прощено будет ему; <i>остаток</i> же принадлежит священнику, как приношение хлебное.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (2843, 'rst66', 'lev', 5, 12, 'и принесет ее к священнику, а священник возьмет из нее полную горсть в память и сожжет на жертвеннике в жертву Господу: это жертва за грех;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (2842, 'rst66', 'lev', 5, 11, 'Если же он не в состоянии принести двух горлиц или двух молодых голубей, пусть принесет за то, что согрешил, десятую часть ефы пшеничной муки в жертву за грех; пусть не льет на нее елея, и ливана пусть не кладет на нее, ибо это жертва за грех;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (2841, 'rst66', 'lev', 5, 10, 'а другую употребит во всесожжение по установлению; и так очистит его священник от греха его, которым он согрешил, и прощено будет ему.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (2840, 'rst66', 'lev', 5, 9, 'и покропит кровью сей жертвы за грех на стену жертвенника, а остальную кровь выцедит к подножию жертвенника: это жертва за грех;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (2839, 'rst66', 'lev', 5, 8, 'пусть принесет их к священнику, и <i>священник</i> представит прежде ту <i>из сих птиц</i>, которая за грех, и надломит голову ее от шеи ее, но не отделит;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (2838, 'rst66', 'lev', 5, 7, 'Если же он не в состоянии принести овцы, то в повинность за грех свой пусть принесет Господу двух горлиц или двух молодых голубей, одного в жертву за грех, а другого во всесожжение;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (2837, 'rst66', 'lev', 5, 6, 'то пусть принесет Господу за грех свой, которым он согрешил, жертву повинности из мелкого скота, овцу или козу, за грех, и очистит его священник от греха его.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (2836, 'rst66', 'lev', 5, 5, 'Если он виновен в чем‐нибудь из сих, и исповедается, в чем он согрешил,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (2835, 'rst66', 'lev', 5, 4, 'Или если кто безрассудно устами своими поклянется сделать что‐нибудь худое или доброе, какое бы то ни было дело, в котором люди безрассудно клянутся, и он не знал того, но после узнает, то он виновен в том.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (2834, 'rst66', 'lev', 5, 3, 'Или если прикоснется к нечистоте человеческой, какая бы то ни была нечистота, от которой оскверняются, и он не знал того, но после узнает, то он виновен.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (2833, 'rst66', 'lev', 5, 2, 'Или если прикоснется к чему‐нибудь нечистому, или к трупу зверя нечистого, или к трупу скота нечистого, или к трупу гада нечистого, но не знал того, то он нечист и виновен.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (2832, 'rst66', 'lev', 5, 1, 'Если кто согрешит тем, что слышал голос проклятия и был свидетелем, или видел, или знал, но не объявил, то он понесет на себе грех.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (2831, 'rst66', 'lev', 4, 35, 'и весь тук ее отделит, как отделяется тук овцы из жертвы мирной, и сожжет сие священник на жертвеннике в жертву Господу; и так очистит его священник от греха, которым он согрешил, и прощено будет ему.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (2830, 'rst66', 'lev', 4, 34, 'и возьмет священник перстом своим крови от сей жертвы за грех, и возложит на роги жертвенника всесожжения, а остальную кровь ее выльет к подножию жертвенника;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (2829, 'rst66', 'lev', 4, 33, 'и возложит руку свою на голову жертвы за грех, и заколет ее в жертву за грех на том месте, где закалают жертву всесожжения;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (2828, 'rst66', 'lev', 4, 32, 'А если из стада овец захочет он принести жертву за грех, пусть принесет женского пола, без порока,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (2827, 'rst66', 'lev', 4, 31, 'и весь тук ее отделит, подобно как отделяется тук из жертвы мирной, и сожжет <i>его</i> священник на жертвеннике в приятное благоухание Господу; и так очистит его священник, и прощено будет ему.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (2826, 'rst66', 'lev', 4, 30, 'и возьмет священник крови ее перстом своим, и возложит на роги жертвенника всесожжения, а остальную кровь ее выльет к подножию жертвенника;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (2825, 'rst66', 'lev', 4, 29, 'и возложит руку свою на голову жертвы за грех, и заколют <i>козу</i> в жертву за грех на месте, <i>где закалают</i> жертву всесожжения;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (2824, 'rst66', 'lev', 4, 28, 'то, когда узнан будет им грех, которым он согрешил, пусть приведет он в жертву козу без порока за грех свой, которым он согрешил,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (2823, 'rst66', 'lev', 4, 27, 'Если же кто из народа земли согрешит по ошибке и сделает что‐нибудь против заповедей Господних, чего не надлежало делать, и виновен будет,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (2822, 'rst66', 'lev', 4, 26, 'и весь тук его сожжет на жертвеннике, подобно как тук жертвы мирной, и так очистит его священник от греха его, и прощено будет ему.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (2821, 'rst66', 'lev', 4, 25, 'и возьмет священник перстом своим крови от жертвы за грех, и возложит на роги жертвенника всесожжения, а остальную кровь его выльет к подножию жертвенника всесожжения;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (2820, 'rst66', 'lev', 4, 24, 'и возложит руку свою на голову козла, и заколет его на месте, где закалаются всесожжения пред Господом: это жертва за грех;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (2819, 'rst66', 'lev', 4, 23, 'то, когда узнан будет им грех, которым он согрешил, пусть приведет он в жертву козла без порока,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (2818, 'rst66', 'lev', 4, 22, 'А если согрешит начальник и сделает по ошибке что‐нибудь против заповедей Господа, Бога своего, чего не надлежало делать, и будет виновен,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (2817, 'rst66', 'lev', 4, 21, 'и вынесет тельца вне стана, и сожжет его так, как сожег прежнего тельца. Это жертва за грех общества.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (2816, 'rst66', 'lev', 4, 20, 'и сделает с тельцом то, что делается с тельцом за грех; так должен сделать с ним, и так очистит их священник, и прощено будет им;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (2815, 'rst66', 'lev', 4, 19, 'и весь тук его вынет из него и сожжет на жертвеннике;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (2814, 'rst66', 'lev', 4, 18, 'и возложит крови на роги жертвенника, который пред лицом Господним в скинии собрания, а остальную кровь выльет к подножию жертвенника всесожжений, который у входа скинии собрания;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (2813, 'rst66', 'lev', 4, 17, 'и омочит священник перст свой в кровь, и покропит семь раз пред Господом, пред завесою <i>святилища</i>,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (2812, 'rst66', 'lev', 4, 16, 'И внесет священник помазанный крови тельца в скинию собрания,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (2811, 'rst66', 'lev', 4, 15, 'и возложат старейшины общества руки свои на голову тельца пред Господом, и заколют тельца пред Господом.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (2810, 'rst66', 'lev', 4, 14, 'то, когда узнан будет грех, которым они согрешили, пусть от всего общества представят они из крупного скота тельца в жертву за грех, и приведут его пред скинию собрания;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (2809, 'rst66', 'lev', 4, 13, 'Если же все общество Израилево согрешит по ошибке, и скрыто будет дело от глаз собрания, и сделает что‐нибудь против заповедей Господних, чего не надлежало делать, и будет виновно,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (2808, 'rst66', 'lev', 4, 12, 'всего тельца пусть вынесет вне стана на чистое место, где высыпается пепел, и сожжет его огнем на дровах; где высыпается пепел, там пусть сожжен будет.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (2807, 'rst66', 'lev', 4, 11, 'а кожу тельца и все мясо его с головою и с ногами его, и внутренности его, и нечистоту его,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (2806, 'rst66', 'lev', 4, 10, 'как отделяется из тельца жертвы мирной; и сожжет их священник на жертвеннике всесожжения;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (2805, 'rst66', 'lev', 4, 9, 'и обе почки, и тук, который на них, который на стегнах, и сальник на печени; с почками отделит он это,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (2804, 'rst66', 'lev', 4, 8, 'и вынет из тельца за грех весь тук его, тук, покрывающий внутренности, и весь тук, который на внутренностях,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (2803, 'rst66', 'lev', 4, 7, 'и возложит священник крови <i>тельца</i> пред Господом на роги жертвенника благовонных курений, который в скинии собрания, а остальную кровь тельца выльет к подножию жертвенника всесожжений, который у входа скинии собрания;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (2802, 'rst66', 'lev', 4, 6, 'и омочит священник перст свой в кровь, и покропит кровью семь раз пред Господом пред завесою святилища;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (2801, 'rst66', 'lev', 4, 5, 'и возьмет священник помазанный крови тельца, и внесет ее в скинию собрания,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (2800, 'rst66', 'lev', 4, 4, 'и приведет тельца к дверям скинии собрания пред Господа, и возложит руки свои на голову тельца, и заколет тельца пред Господом;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (2799, 'rst66', 'lev', 4, 3, 'если священник помазанный согрешит и сделает виновным народ, &mdash; то за грех свой, которым согрешил, пусть представит из крупного скота тельца, без порока, Господу в жертву о грехе,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (2798, 'rst66', 'lev', 4, 2, 'скажи сынам Израилевым: если какая душа согрешит по ошибке против каких‐либо заповедей Господних и сделает что‐нибудь, чего не должно делать;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (2797, 'rst66', 'lev', 4, 1, 'И сказал Господь Моисею, говоря:');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (2796, 'rst66', 'lev', 3, 17, 'Это постановление вечное в роды ваши, во всех жилищах ваших; никакого тука и никакой крови не ешьте.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (2795, 'rst66', 'lev', 3, 16, 'и сожжет их священник на жертвеннике: <i>это</i> пища огня &mdash; приятное благоухание <i>Господу</i>; весь тук Господу.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (2794, 'rst66', 'lev', 3, 15, 'и обе почки, и тук, который на них, который на стегнах, и сальник, который на печени; с почками он отделит это;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (2793, 'rst66', 'lev', 3, 14, 'и принесет из нее в приношение, в жертву Господу, тук, покрывающий внутренности, и весь тук, который на внутренностях,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (2792, 'rst66', 'lev', 3, 13, 'и возложит руку свою на голову ее, и заколет ее перед скинией собрания, и покропят сыны Аароновы кровью ее на жертвенник со всех сторон;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (2791, 'rst66', 'lev', 3, 12, 'А если он приносит жертву из коз, пусть представит ее пред Господа,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (2790, 'rst66', 'lev', 3, 11, 'священник сожжет это на жертвеннике; <i>это</i> пища огня &mdash; жертва Господу.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (2789, 'rst66', 'lev', 3, 10, 'и обе почки и тук, который на них, который на стегнах, и сальник, который на печени; с почками он отделит это;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (2788, 'rst66', 'lev', 3, 9, 'и пусть принесет из мирной жертвы в жертву Господу тук ее, весь курдюк, отрезав его по самую хребтовую кость, и тук, покрывающий внутренности, и весь тук, который на внутренностях,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (2787, 'rst66', 'lev', 3, 8, 'и возложит руку свою на голову жертвы своей, и заколет ее пред скинией собрания, и сыны Аароновы покропят кровью ее на жертвенник со всех сторон;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (2786, 'rst66', 'lev', 3, 7, 'Если из овец приносит он жертву свою, пусть представит ее пред Господа,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (2785, 'rst66', 'lev', 3, 6, 'А если из мелкого скота приносит он мирную жертву Господу, мужеского или женского пола, пусть принесет ее, не имеющую порока.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (2784, 'rst66', 'lev', 3, 5, 'и сыны Аароновы сожгут это на жертвеннике вместе со всесожжением, которое на дровах, на огне: <i>это</i> жертва, благоухание, приятное Господу.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (2783, 'rst66', 'lev', 3, 4, 'и обе почки и тук, который на них, который на стегнах, и сальник, который на печени; с почками он отделит это;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (2782, 'rst66', 'lev', 3, 3, 'и принесет он из мирной жертвы в жертву Господу тук, покрывающий внутренности, и весь тук, который на внутренностях,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (2781, 'rst66', 'lev', 3, 2, 'и возложит руку свою на голову жертвы своей, и заколет ее у дверей скинии собрания; сыны же Аароновы, священники, покропят кровью на жертвенник со всех сторон;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (2780, 'rst66', 'lev', 3, 1, 'Если жертва его жертва мирная и если он приносит из крупного скота, мужеского или женского пола, пусть принесет ее Господу, не имеющую порока,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (2779, 'rst66', 'lev', 2, 16, 'и сожжет священник в память часть зерен и елея со всем ливаном: <i>это</i> жертва Господу.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (2778, 'rst66', 'lev', 2, 15, 'и влей на них елея, и положи на них ливана: это приношение хлебное;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (2777, 'rst66', 'lev', 2, 14, 'Если приносишь Господу приношение хлебное из первых плодов, приноси в дар от первых плодов твоих из колосьев, высушенных на огне, растолченные зерна,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (2776, 'rst66', 'lev', 2, 13, 'Всякое приношение твое хлебное соли солью и не оставляй жертвы твоей без соли завета Бога твоего: при всяком приношении твоем приноси соль.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (2775, 'rst66', 'lev', 2, 12, 'как приношение начатков приносите их Господу, а на жертвенник не должно возносить их в приятное благоухание.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (2774, 'rst66', 'lev', 2, 11, 'Никакого приношения хлебного, которое приносите Господу, не делайте квасного, ибо ни квасного, ни меду не должны вы сожигать в жертву Господу;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (2773, 'rst66', 'lev', 2, 10, 'а остатки приношения хлебного Аарону и сынам его: <i>это</i> великая святыня из жертв Господних.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (2772, 'rst66', 'lev', 2, 9, 'и возьмет священник из сей жертвы часть в память, и сожжет на жертвеннике: <i>это</i> жертва, благоухание, приятное Господу;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (2771, 'rst66', 'lev', 2, 8, 'и принеси приношение, которое из сего составлено, Господу; представь оное священнику, а он принесет его к жертвеннику;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (2770, 'rst66', 'lev', 2, 7, 'Если жертва твоя приношение хлебное из горшка, то должно сделать оное из пшеничной муки с елеем,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (2769, 'rst66', 'lev', 2, 6, 'разломи ее на куски и влей на нее елея: это приношение хлебное.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (2768, 'rst66', 'lev', 2, 5, 'Если жертва твоя приношение хлебное со сковороды, то это должна быть пшеничная мука, смешанная с елеем, пресная;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (2767, 'rst66', 'lev', 2, 4, 'Если же приносишь жертву приношения хлебного из печенного в печи, <i>то приноси</i> пшеничные хлебы пресные, смешанные с елеем, и лепешки пресные, помазанные елеем.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (2766, 'rst66', 'lev', 2, 3, 'а остатки от приношения хлебного Аарону и сынам его: <i>это</i> великая святыня из жертв Господних.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (2765, 'rst66', 'lev', 2, 2, 'и принесет ее к сынам Аароновым, священникам, и возьмет полную горсть муки с елеем и со всем ливаном, и сожжет сие священник в память на жертвеннике; <i>это</i> жертва, благоухание, приятное Господу;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (2764, 'rst66', 'lev', 2, 1, 'Если какая душа хочет принести Господу жертву приношения хлебного, пусть принесет пшеничной муки, и вольет на нее елея, и положит на нее ливана,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (2763, 'rst66', 'lev', 1, 17, 'и надломит ее в крыльях ее, не отделяя их, и сожжет ее священник на жертвеннике, на дровах, которые на огне: это всесожжение, жертва, благоухание, приятное Господу.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (2762, 'rst66', 'lev', 1, 16, 'зоб ее с перьями ее отнимет и бросит его подле жертвенника на восточную сторону, где пепел;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (2761, 'rst66', 'lev', 1, 15, 'священник принесет ее к жертвеннику, и свернет ей голову, и сожжет на жертвеннике, а кровь выцедит к стене жертвенника;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (2760, 'rst66', 'lev', 1, 14, 'Если же из птиц приносит он Господу всесожжение, пусть принесет жертву свою из горлиц или из молодых голубей;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (2759, 'rst66', 'lev', 1, 13, 'а внутренности и ноги вымоет водою, и принесет священник все и сожжет на жертвеннике: <i>это</i> всесожжение, жертва, благоухание, приятное Господу.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (2758, 'rst66', 'lev', 1, 12, 'и рассекут ее на части, <i>отделив</i> голову ее и тук ее, и разложит их священник на дровах, которые на огне, на жертвеннике,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (2757, 'rst66', 'lev', 1, 11, 'и заколет ее пред Господом на северной стороне жертвенника, и сыны Аароновы, священники, покропят кровью ее на жертвенник со всех сторон;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (2756, 'rst66', 'lev', 1, 10, 'Если жертва всесожжения его из мелкого скота, из овец или из коз, пусть принесет ее мужеского пола, без порока,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (2755, 'rst66', 'lev', 1, 9, 'а внутренности <i>жертвы</i> и ноги ее вымоет он водою, и сожжет священник все на жертвеннике: <i>это</i> всесожжение, жертва, благоухание, приятное Господу.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (2754, 'rst66', 'lev', 1, 8, 'и разложат сыны Аароновы, священники, части, голову и тук на дровах, которые на огне, на жертвеннике;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (2753, 'rst66', 'lev', 1, 7, 'сыны же Аароновы, священники, положат на жертвенник огонь, и на огне разложат дрова;');",
