"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3868, 'rst66', 'num', 7, 18, 'Во второй день принес Нафанаил, сын Цуара, начальник <i>колена</i> Иссахарова;');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3867, 'rst66', 'num', 7, 17, 'и в жертву мирную два вола, пять овнов, пять козлов, пять однолетних агнцев; вот приношение Наассона, сына Аминадавова.');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3866, 'rst66', 'num', 7, 16, 'один козел в жертву за грех,');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3865, 'rst66', 'num', 7, 15, 'один телец, один овен, один однолетний агнец, во всесожжение,');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3864, 'rst66', 'num', 7, 14, 'одна золотая кадильница в десять <i>сиклей</i>, наполненная курением,');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3863, 'rst66', 'num', 7, 13, 'приношение его было: одно серебряное блюдо, весом в сто тридцать <i>сиклей</i>, одна серебряная чаша в семьдесят сиклей, по сиклю священному, наполненные пшеничною мукою, смешанною с елеем, в приношение хлебное,');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3862, 'rst66', 'num', 7, 12, 'В первый день принес приношение свое Наассон, сын Аминадавов, от колена Иудина;');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3861, 'rst66', 'num', 7, 11, 'И сказал Господь Моисею: по одному начальнику в день пусть приносят приношение свое для освящения жертвенника.');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3860, 'rst66', 'num', 7, 10, 'И принесли начальники жертвы освящения жертвенника в день помазания его, и представили начальники приношение свое пред жертвенник.');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3859, 'rst66', 'num', 7, 9, 'а сынам Каафовым не дал, потому что служба их &mdash; <i>носить</i> святилище; на плечах они должны носить.');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3858, 'rst66', 'num', 7, 8, 'и четыре повозки и восемь волов отдал сынам Мерариным, по роду служб их, под надзором Ифамара, сына Аарона, священника;');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3857, 'rst66', 'num', 7, 7, 'две повозки и четырех волов отдал сынам Гирсоновым, по роду служб их,');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3856, 'rst66', 'num', 7, 6, 'И взял Моисей повозки и волов, и отдал их левитам:');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3855, 'rst66', 'num', 7, 5, 'возьми от них; это будет для отправления работ при скинии собрания; и отдай это левитам, смотря по роду службы их.');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3854, 'rst66', 'num', 7, 4, 'И сказал Господь Моисею, говоря:');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3853, 'rst66', 'num', 7, 3, 'и представили приношение свое пред Господа, шесть крытых повозок и двенадцать волов, по одной повозке от двух начальников и по одному волу от каждого, и представили сие пред скинию.');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3852, 'rst66', 'num', 7, 2, 'тогда пришли начальники Израилевы главы семейств их, начальники колен, заведовавшие исчислением,');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3851, 'rst66', 'num', 7, 1, 'Когда Моисей поставил скинию, и помазал ее, и освятил ее и все принадлежности ее, и жертвенник и все принадлежности его, и помазал их, и освятил их,');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3850, 'rst66', 'num', 6, 27, 'Так пусть призывают имя Мое на сынов Израилевых, и Я благословлю их.');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3849, 'rst66', 'num', 6, 26, '«Да обратит Господь лицо Свое на тебя и даст тебе мир!»');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3848, 'rst66', 'num', 6, 25, '«Да призрит на тебя Господь светлым лицом Своим и помилует тебя!»');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3847, 'rst66', 'num', 6, 24, '«Да благословит тебя Господь и сохранит тебя!»');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3846, 'rst66', 'num', 6, 23, 'скажи Аарону и сынам его: так благословляйте сынов Израилевых, говоря им:');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3845, 'rst66', 'num', 6, 22, 'И сказал Господь Моисею, говоря:');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3844, 'rst66', 'num', 6, 21, 'Вот закон о назорее, который дал обет, и жертва его Господу за назорейство свое, кроме того, что позволит ему достаток его; по обету своему, какой он даст, так и должен он делать, сверх узаконенного о назорействе его.');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3843, 'rst66', 'num', 6, 20, 'и вознесет сие священник, потрясая пред Господом: эта святыня &mdash; для священника, сверх груди потрясания и сверх плеча возношения. После сего назорей может пить вино.');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3842, 'rst66', 'num', 6, 19, 'И возьмет священник сваренное плечо овна и один пресный пирог из корзины и одну пресную лепешку, и положит на руки назорею, после того, как острижет он голову назорейства своего;');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3841, 'rst66', 'num', 6, 18, 'и острижет назорей у входа скинии собрания голову назорейства своего, и возьмет волосы головы назорейства своего, и положит на огонь, который под мирною жертвою.');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3840, 'rst66', 'num', 6, 17, 'овна принесет в жертву мирную Господу с корзиною опресноков, также совершит священник хлебное приношение его и возлияние его;');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3839, 'rst66', 'num', 6, 16, 'И представит <i>сие</i> священник пред Господа, и принесет жертву его за грех и всесожжение его;');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3838, 'rst66', 'num', 6, 15, 'и корзину опресноков из пшеничной муки, хлебов, испеченных с елеем, и пресных лепешек, помазанных елеем, и при них хлебное приношение и возлияние.');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3837, 'rst66', 'num', 6, 14, 'и он принесет в жертву Господу одного однолетнего агнца без порока во всесожжение, и одну однолетнюю агницу без порока в жертву за грех, и одного овна без порока в жертву мирную,');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3836, 'rst66', 'num', 6, 13, 'И вот закон о назорее, когда исполнятся дни назорейства его: должно привести его ко входу скинии собрания,');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3835, 'rst66', 'num', 6, 12, 'и должен он снова начать посвященные Господу дни назорейства своего и принести однолетнего агнца в жертву повинности; прежние же дни пропали, потому что назорейство его осквернено.');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3834, 'rst66', 'num', 6, 11, 'священник одну <i>из птиц</i> принесет в жертву за грех, а другую во всесожжение, и очистит его от осквернения мертвым телом, и освятит голову его в тот день;');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3833, 'rst66', 'num', 6, 10, 'и в восьмой день должен принести двух горлиц или двух молодых голубей к священнику, ко входу скинии собрания;');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3832, 'rst66', 'num', 6, 9, 'Если же умрет при нем кто‐нибудь вдруг, нечаянно, и он осквернит тем голову назорейства своего, то он должен остричь голову свою в день очищения его, в седьмой день должен остричь ее,');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3831, 'rst66', 'num', 6, 8, 'во все дни назорейства своего свят он Господу.');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3830, 'rst66', 'num', 6, 7, '<i>прикосновением</i> к отцу своему, и матери своей, и брату своему, и сестре своей не должен он оскверняться, когда они умрут, потому что посвящение Богу его на главе его;');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3829, 'rst66', 'num', 6, 6, 'Во все дни, на которые он посвятил себя в назореи Господу, не должен он подходить к мертвому телу:');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3828, 'rst66', 'num', 6, 5, 'Во все дни обета назорейства его бритва не должна касаться головы его; до исполнения дней, на которые он посвятил себя в назореи Господу, свят он: должен растить волосы на голове своей.');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3827, 'rst66', 'num', 6, 4, 'во все дни назорейства своего не должен он есть ничего, что делается из винограда, от зерен до кожи.');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3826, 'rst66', 'num', 6, 3, 'то он должен воздержаться от вина и <i>крепкого</i> напитка, и не должен употреблять ни уксусу из вина, ни уксусу из напитка, и ничего, приготовленного из винограда, не должен пить, и не должен есть ни сырых, ни сушеных виноградных ягод;');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3825, 'rst66', 'num', 6, 2, 'объяви сынам Израилевым и скажи им: если мужчина или женщина решится дать обет назорейства, чтобы посвятить себя в назореи Господу,');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3824, 'rst66', 'num', 6, 1, 'И сказал Господь Моисею, говоря:');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3823, 'rst66', 'num', 5, 31, 'и будет муж чист от греха, а жена понесет на себе грех свой.');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3822, 'rst66', 'num', 5, 30, 'или когда на мужа найдет дух ревности, и он будет ревновать жену свою, тогда пусть он поставит жену пред лицом Господа, и сделает с нею священник все по сему закону, &mdash;');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3821, 'rst66', 'num', 5, 29, 'Вот закон о ревновании, когда жена изменит мужу своему и осквернится,');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3820, 'rst66', 'num', 5, 28, 'если же жена не осквернилась и была чиста, то останется невредимою и будет оплодотворяема семенем.');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3819, 'rst66', 'num', 5, 27, 'и когда напоит ее водою, тогда, если она нечиста и сделала преступление против мужа своего, горькая вода, наводящая проклятие, войдет в нее ко вреду ее, и опухнет чрево ее, и опадет лоно ее, и будет эта жена проклятою в народе своем;');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3818, 'rst66', 'num', 5, 26, 'и возьмет священник горстью из хлебного приношения часть в память, и сожжет на жертвеннике, и потом даст жене выпить воды;');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3817, 'rst66', 'num', 5, 25, 'И возьмет священник из рук жены хлебное приношение ревнования, и вознесет сие приношение пред Господом, и отнесет его к жертвеннику;');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3816, 'rst66', 'num', 5, 24, 'и даст жене выпить горькую воду, наводящую проклятие, и войдет в нее вода, наводящая проклятие, ко вреду ее.');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3815, 'rst66', 'num', 5, 23, 'И напишет священник заклинания сии на свитке, и смоет их в горькую воду;');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3814, 'rst66', 'num', 5, 22, 'и да пройдет вода сия, наводящая проклятие, во внутренность твою, чтобы опух живот <i>твой</i> и опало лоно <i>твое</i>. И скажет жена: аминь, аминь.');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3813, 'rst66', 'num', 5, 21, 'тогда священник пусть заклянет жену клятвою проклятия и скажет священник жене: да предаст тебя Господь проклятию и клятве в народе твоем, и да соделает Господь лоно твое опавшим и живот твой опухшим;');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3812, 'rst66', 'num', 5, 20, 'но если ты изменила мужу твоему и осквернилась, и если кто переспал с тобою, кроме мужа твоего, &mdash;');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3811, 'rst66', 'num', 5, 19, 'И заклянет ее священник, и скажет жене: если никто не переспал с тобою, и ты не осквернилась, и не изменила мужу своему, то невредима будешь от сей горькой воды, наводящей проклятие;');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3810, 'rst66', 'num', 5, 18, 'и поставит священник жену пред лицо Господне, и обнажит голову жены, и даст ей в руки приношение воспоминания, &mdash; это приношение ревнования, в руке же у священника будет горькая вода, наводящая проклятие.');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3809, 'rst66', 'num', 5, 17, 'и возьмет священник святой воды в глиняный сосуд, и возьмет священник земли с полу скинии и положит в воду;');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3808, 'rst66', 'num', 5, 16, 'А священник пусть приведет и поставит ее пред лицо Господне,');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3807, 'rst66', 'num', 5, 15, 'пусть приведет муж жену свою к священнику и принесет за нее в жертву десятую часть ефы ячменной муки, но не возливает на нее елея и не кладет ливана, потому что это приношение ревнования, приношение воспоминания, напоминающее о беззаконии.');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3806, 'rst66', 'num', 5, 14, 'и найдет на него дух ревности, и будет ревновать жену свою, когда она осквернена, или найдет на него дух ревности, и он будет ревновать жену свою, когда она не осквернена, &mdash;');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3805, 'rst66', 'num', 5, 13, 'и переспит кто с нею, и излиет семя, и это будет скрыто от глаз мужа ее, и она осквернится тайно, и не будет на нее свидетеля, и не будет уличена,');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3804, 'rst66', 'num', 5, 12, 'объяви сынам Израилевым и скажи им: если изменит кому жена, и нарушит верность к нему,');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3803, 'rst66', 'num', 5, 11, 'И сказал Господь Моисею, говоря:');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3802, 'rst66', 'num', 5, 10, 'и посвященное кем‐либо ему принадлежит; все, что даст кто священнику, ему принадлежит.');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3801, 'rst66', 'num', 5, 9, 'и всякое возношение из всех святынь сынов Израилевых, которые они приносят к священнику, ему принадлежит,');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3800, 'rst66', 'num', 5, 8, 'если же у него нет наследника, которому следовало бы возвратить за вину, то посвятить это Господу; пусть будет это священнику, сверх овна очищения, которым он очистит его;');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3799, 'rst66', 'num', 5, 7, 'то пусть исповедаются во грехе своем, который они сделали, и возвратят сполна то, в чем виновны, и прибавят к тому пятую часть, и отдадут тому, против кого согрешили;');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3798, 'rst66', 'num', 5, 6, 'скажи сынам Израилевым: если мужчина или женщина сделает какой‐либо грех против человека, и чрез это сделает преступление против Господа, и виновна будет душа та,');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3797, 'rst66', 'num', 5, 5, 'И сказал Господь Моисею, говоря:');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3796, 'rst66', 'num', 5, 4, 'И сделали так сыны Израилевы, и выслали их вон из стана; как говорил Господь Моисею, так и сделали сыны Израилевы.');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3795, 'rst66', 'num', 5, 3, 'и мужчин, и женщин вышлите, за стан вышлите их, чтобы не оскверняли они станов своих, среди которых Я живу.');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3794, 'rst66', 'num', 5, 2, 'повели сынам Израилевым выслать из стана всех прокаженных, и всех, имеющих истечение, и всех, осквернившихся от мертвого,');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3793, 'rst66', 'num', 5, 1, 'И сказал Господь Моисею, говоря:');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3792, 'rst66', 'num', 4, 49, 'по повелению Господню чрез Моисея определены они каждый к своей работе и ношению, и исчислены, как повелел Господь Моисею.');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3791, 'rst66', 'num', 4, 48, 'и было исчислено их восемь тысяч пятьсот восемьдесят;');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3790, 'rst66', 'num', 4, 47, 'от тридцати лет и выше, до пятидесяти лет, все, способные <i>к службе</i>, для работ и ношения в скинии собрания;');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3789, 'rst66', 'num', 4, 46, 'И исчисленные все левиты, которых исчислил Моисей и Аарон и начальники Израиля по родам их и по семействам их,');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3788, 'rst66', 'num', 4, 45, 'это &mdash; исчисленные из родов сынов Мерариных, которых исчислил Моисей и Аарон по повелению Господню, <i>данному</i> чрез Моисея.');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3787, 'rst66', 'num', 4, 44, 'и было исчислено, по родам их, три тысячи двести:');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3786, 'rst66', 'num', 4, 43, 'от тридцати лет и выше, до пятидесяти лет, все, способные к службе, для работ при скинии собрания;');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3785, 'rst66', 'num', 4, 42, 'И исчислены роды сынов Мерариных по родам их, по семействам их,');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3784, 'rst66', 'num', 4, 41, 'это &mdash; исчисленные из родов сынов Гирсона, все, служащие при скинии собрания, которых исчислил Моисей и Аарон по повелению Господню.');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3783, 'rst66', 'num', 4, 40, 'и было исчислено по родам их, по семействам их, две тысячи шестьсот тридцать:');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3782, 'rst66', 'num', 4, 39, 'от тридцати лет и выше, до пятидесяти лет, все, способные к службе, для работ в скинии собрания;');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3781, 'rst66', 'num', 4, 38, 'И исчислены сыны Гирсона по родам их и по семействам их,');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3780, 'rst66', 'num', 4, 37, 'это &mdash; исчисленные из родов Каафовых, все служащие при скинии собрания, которых исчислил Моисей и Аарон по повелению Господню, <i>данному</i> чрез Моисея.');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3779, 'rst66', 'num', 4, 36, 'и было исчислено, по родам их, две тысячи семьсот пятьдесят:');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3778, 'rst66', 'num', 4, 35, 'от тридцати лет и выше, до пятидесяти лет, всех, способных к службе, для работ в скинии собрания;');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3777, 'rst66', 'num', 4, 34, 'И исчислили Моисей и Аарон и начальники общества сынов Каафовых по родам их и по семействам их,');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3776, 'rst66', 'num', 4, 33, 'вот работы родов сынов Мерариных по службе их при скинии собрания под надзором Ифамара, сына Аарона, священника.');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3775, 'rst66', 'num', 4, 32, 'и столбы двора со всех сторон, и подножия их, и колья их, и веревки их, и все вещи при них, и все принадлежности их; и поименно сосчитайте вещи, которые они обязаны носить;');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3774, 'rst66', 'num', 4, 31, 'Вот что они должны носить по службе их при скинии собрания: брусья скинии и шесты ее, и столбы ее, и подножия ее,');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3773, 'rst66', 'num', 4, 30, 'от тридцати лет и выше, до пятидесяти лет, исчисли всех, способных на службу, чтобы отправлять работы при скинии собрания.');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3772, 'rst66', 'num', 4, 29, 'Сынов Мерариных по родам их, по семействам их исчисли,');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3771, 'rst66', 'num', 4, 28, 'вот службы родов сынов Гирсоновых в скинии собрания, и вот что поручается их хранению под надзором Ифамара, сына Аарона, священника.');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3770, 'rst66', 'num', 4, 27, 'по повелению Аарона и сынов его должны производиться все службы сынов Гирсоновых при всяком ношении тяжестей и всякой работе их, и поручите их хранению все, что они носят;');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3769, 'rst66', 'num', 4, 26, 'и завесы двора, и завесу входа во двор, который вокруг скинии и жертвенника, и веревки их, и все вещи, принадлежащие к ним; и все, что делается при них, они должны работать;');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3768, 'rst66', 'num', 4, 25, 'они должны носить покровы скинии и скинию собрания, и покров ее, и покров кожаный синий, который поверх его, и завесу входа скинии собрания,');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3767, 'rst66', 'num', 4, 24, 'Вот работы семейств Гирсоновых при их служении и ношении тяжестей:');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3766, 'rst66', 'num', 4, 23, 'от тридцати лет и выше, до пятидесяти лет, исчисли их всех, способных к службе, чтобы отправлять работы при скинии собрания.');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3765, 'rst66', 'num', 4, 22, 'исчисли и сынов Гирсона по семействам их, по родам их,');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3764, 'rst66', 'num', 4, 21, 'И сказал Господь Моисею, говоря:');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3763, 'rst66', 'num', 4, 20, 'но сами они не должны подходить смотреть святыню, когда покрывают ее, чтобы не умереть.');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3762, 'rst66', 'num', 4, 19, 'но вот что сделайте им, чтобы они были живы и не умерли, когда приступают к Святому Святых: Аарон и сыны его пусть придут и поставят их каждого в служении его и у ноши его;');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3761, 'rst66', 'num', 4, 18, 'не погубите колена племен Каафовых из среды левитов,');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3760, 'rst66', 'num', 4, 17, 'И сказал Господь Моисею и Аарону, говоря:');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3759, 'rst66', 'num', 4, 16, 'Елеазару, сыну Аарона священника, поручается елей для светильника и благовонное курение, и всегдашнее хлебное приношение, и елей помазания, &mdash; поручается вся скиния и все, что в ней, святилище и принадлежности его.');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3758, 'rst66', 'num', 4, 15, 'Когда, при отправлении в путь стана, Аарон и сыны его покроют всё святилище и все вещи святилища, тогда сыны Каафа подойдут, чтобы нести; но не должны они касаться святилища, чтобы не умереть. Сии <i>части</i> скинии собрания должны носить сыны Каафовы.');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3757, 'rst66', 'num', 4, 14, 'и положат на него все сосуды его, которые употребляются для служения при нем &mdash; угольницы, вилки, лопатки и чаши, все сосуды жертвенника &mdash; и покроют его покровом из кож синих, и вложат шесты его.');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3756, 'rst66', 'num', 4, 13, 'И очистят жертвенник от пепла, и накроют его одеждою пурпуровою;');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3755, 'rst66', 'num', 4, 12, 'И возьмут все вещи служебные, которые употребляются для служения во святилище, и положат в одежду из голубой <i>шерсти</i>, и покроют их покровом из кож синих, и положат на носилки.');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3754, 'rst66', 'num', 4, 11, 'И на золотой жертвенник возложат одежду из голубой <i>шерсти</i>, и покроют его покровом из кож синих, и вложат шесты его.');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3753, 'rst66', 'num', 4, 10, 'и покроют его и все принадлежности его покровом из кож синих, и положат на носилки.');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3752, 'rst66', 'num', 4, 9, 'И возьмут одежду из голубой <i>шерсти</i>, и покроют светильник и лампады его, и щипцы его, и лотки его, и все сосуды для елея, которые употребляют при нем,');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3751, 'rst66', 'num', 4, 8, 'и возложат на них одежду багряную, и покроют ее покровом из кожи синего цвета, и вложат шесты его.');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3750, 'rst66', 'num', 4, 7, 'И стол <i>хлебов</i> предложения накроют одеждою из голубой <i>шерсти</i>, и поставят на нем блюда, тарелки, чаши и кружки для возлияния, и хлеб <i>его</i> всегдашний должен быть на нем;');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3749, 'rst66', 'num', 4, 6, 'и положат на нее покров из кож синего цвета, и сверх его накинут покрывало всё из голубой <i>шерсти</i>, и вложат шесты его.');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3748, 'rst66', 'num', 4, 5, 'Когда стану надобно подняться в путь, Аарон и сыны его войдут, и снимут завесу закрывающую, и покроют ею ковчег откровения;');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3747, 'rst66', 'num', 4, 4, 'Вот служение сынов Каафовых в скинии собрания: <i>носить</i> Святое Святых.');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3746, 'rst66', 'num', 4, 3, 'от тридцати лет и выше, до пятидесяти лет, всех способных к службе, чтобы отправлять работы в скинии собрания.');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3745, 'rst66', 'num', 4, 2, 'исчисли сынов Каафовых из сынов Левия по родам их, по семействам их,');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3744, 'rst66', 'num', 4, 1, 'И сказал Господь Моисею и Аарону, говоря:');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3743, 'rst66', 'num', 3, 51, 'и отдал Моисей серебро выкупа Аарону и сынам его по слову Господню, как повелел Господь Моисею.');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3742, 'rst66', 'num', 3, 50, 'от первенцев Израилевых взял серебра тысячу триста шестьдесят пять <i>сиклей</i>, по сиклю священному,');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3741, 'rst66', 'num', 3, 49, 'И взял Моисей серебро выкупа за излишних против <i>числа</i> замененных левитами,');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3740, 'rst66', 'num', 3, 48, 'и отдай серебро сие Аарону и сынам его в выкуп за излишних против <i>числа</i> их.');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3739, 'rst66', 'num', 3, 47, 'возьми по пяти сиклей за человека, по сиклю священному возьми, двадцать гер в сикле,');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3738, 'rst66', 'num', 3, 46, 'А в выкуп двухсот семидесяти трех, которые лишние против <i>числа</i> левитов, из первенцев Израильских,');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3737, 'rst66', 'num', 3, 45, 'возьми левитов вместо всех первенцев из сынов Израиля и скот левитов вместо скота их; пусть левиты будут Мои. Я Господь.');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3736, 'rst66', 'num', 3, 44, 'И сказал Господь Моисею, говоря:');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3735, 'rst66', 'num', 3, 43, 'и было всех первенцев мужеского пола, по числу имен, от одного месяца и выше, двадцать две тысячи двести семьдесят три.');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3734, 'rst66', 'num', 3, 42, 'И исчислил Моисей, как повелел ему Господь, всех первенцев из сынов Израилевых,');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3733, 'rst66', 'num', 3, 41, 'и возьми левитов для Меня, &mdash; Я Господь, &mdash; вместо всех первенцев из сынов Израиля, а скот левитов вместо всего первородного скота сынов Израилевых.');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3732, 'rst66', 'num', 3, 40, 'И сказал Господь Моисею: исчисли всех первенцев мужеского пола из сынов Израилевых, от одного месяца и выше, и пересчитай их поименно;');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3731, 'rst66', 'num', 3, 39, 'Всех исчисленных левитов, которых исчислил Моисей и Аарон по повелению Господню, по родам их, всех мужеского пола, от одного месяца и выше, двадцать две тысячи.');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3730, 'rst66', 'num', 3, 38, 'А с передней стороны скинии, к востоку пред скиниею собрания, должны ставить стан Моисей и Аарон и сыны его, которым вверено хранение святилища за сынов Израилевых; а если приступит кто посторонний, предан будет смерти.');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3729, 'rst66', 'num', 3, 37, 'и столбы двора со всех сторон, и подножия их, и колья их, и веревки их.');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3728, 'rst66', 'num', 3, 36, 'хранению сынов Мерари поручаются брусья скинии и шесты ее, и столбы ее, и подножия ее, и все вещи ее, со всем устройством их,');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3727, 'rst66', 'num', 3, 35, 'начальник поколения родов Мерари Цуриил, сын Авихаила; они должны ставить стан свой на северной стороне скинии;');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3726, 'rst66', 'num', 3, 34, 'исчисленных по числу всех мужеского пола, от одного месяца и выше &mdash; шесть тысяч двести;');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3725, 'rst66', 'num', 3, 33, 'От Мерари род Махли и род Муши: это роды Мерари;');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3724, 'rst66', 'num', 3, 32, 'Начальник над начальниками левитов Елеазар, сын Аарона священника; под его надзором те, которым вверено хранение святилища.');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3723, 'rst66', 'num', 3, 31, 'в хранении у них ковчег, стол, светильник, жертвенники, священные сосуды, которые употребляются при служении, и завеса со всеми принадлежностями ее.');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3722, 'rst66', 'num', 3, 30, 'начальник же поколения родов Каафовых Елцафан, сын Узиила;');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3721, 'rst66', 'num', 3, 29, 'Роды сынов Каафовых должны ставить стан свой на южной стороне скинии;');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3720, 'rst66', 'num', 3, 28, 'По счету всех мужеского пола, от одного месяца и выше, восемь тысяч шестьсот, которые охраняли святилище.');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3719, 'rst66', 'num', 3, 27, 'От Каафа род Амрама и род Ицгара, и род Хеврона, и род Узиила: это роды Каафа.');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3718, 'rst66', 'num', 3, 26, 'и завесы двора, и завеса входа двора, который вокруг скинии и жертвенника, и веревки ее, со всеми их принадлежностями.');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3717, 'rst66', 'num', 3, 25, 'хранению сынов Гирсоновых в скинии собрания поручается скиния и покров ее, и завеса входа скинии собрания,');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3716, 'rst66', 'num', 3, 24, 'начальник поколения сынов Гирсоновых Елиасаф, сын Лаелов;');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3715, 'rst66', 'num', 3, 23, 'Роды Гирсоновы должны становиться станом позади скинии на запад;');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3714, 'rst66', 'num', 3, 22, 'Исчисленных было всех мужеского пола, от одного месяца и выше, семь тысяч пятьсот.');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3713, 'rst66', 'num', 3, 21, 'От Гирсона род Ливни и род Шимея: это роды Гирсоновы.');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3712, 'rst66', 'num', 3, 20, 'И сыны Мерари по родам их: Махли и Муши. Вот роды Левиины по семействам их.');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3711, 'rst66', 'num', 3, 19, 'И сыны Каафа по родам их: Амрам и Ицгар, Хеврон и Узиил.');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3710, 'rst66', 'num', 3, 18, 'И вот имена сынов Гирсоновых по родам их: Ливни и Шимей.');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3709, 'rst66', 'num', 3, 17, 'И вот сыны Левиины по именам их: Гирсон, Кааф и Мерари.');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3708, 'rst66', 'num', 3, 16, 'И исчислил их Моисей по слову Господню, как повелено.');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3707, 'rst66', 'num', 3, 15, 'исчисли сынов Левииных по семействам их, по родам их; всех мужеского пола, от одного месяца и выше, исчисли.');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3706, 'rst66', 'num', 3, 14, 'И сказал Господь Моисею в пустыне Синайской, говоря:');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3705, 'rst66', 'num', 3, 13, 'ибо все первенцы &mdash; Мои; в тот день, когда поразил Я всех первенцев в земле Египетской, освятил Я Себе всех первенцев Израилевых от человека до скота; они должны быть Мои. Я Господь.');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3704, 'rst66', 'num', 3, 12, 'вот, Я взял левитов из сынов Израилевых вместо всех первенцев, разверзающих ложесна, из сынов Израилевых; левиты должны быть Мои,');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3703, 'rst66', 'num', 3, 11, 'И сказал Господь Моисею, говоря:');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3702, 'rst66', 'num', 3, 10, 'Аарону же и сынам его поручи, чтобы они наблюдали священническую должность свою; а если приступит кто посторонний, предан будет смерти.');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3701, 'rst66', 'num', 3, 9, 'отдай левитов Аарону и сынам его <i>в распоряжение:</i> да будут они отданы ему из сынов Израилевых;');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3700, 'rst66', 'num', 3, 8, 'и пусть хранят все вещи скинии собрания, и будут на страже за сынов Израилевых, чтобы отправлять службы при скинии;');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3699, 'rst66', 'num', 3, 7, 'и пусть они будут на страже за него и на страже за все общество при скинии собрания, чтобы отправлять службы при скинии;');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3698, 'rst66', 'num', 3, 6, 'приведи колено Левиино и поставь его пред Аароном священником, чтоб они служили ему;');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3697, 'rst66', 'num', 3, 5, 'И сказал Господь Моисею, говоря:');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3696, 'rst66', 'num', 3, 4, 'но Надав и Авиуд умерли пред лицом Господа, когда они принесли огонь чуждый пред лицо Господа в пустыне Синайской, детей же у них не было; и остались священниками Елеазар и Ифамар при Аароне, отце своем.');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3695, 'rst66', 'num', 3, 3, 'это имена сынов Аарона, священников, помазанных, которых он посвятил, чтобы священнодействовать;');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3694, 'rst66', 'num', 3, 2, 'и вот имена сынов Аарона: первенец Надав, Авиуд, Елеазар и Ифамар;');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3693, 'rst66', 'num', 3, 1, 'Вот родословие Аарона и Моисея, когда говорил Господь Моисею на горе Синае,');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3692, 'rst66', 'num', 2, 34, 'И сделали сыны Израилевы всё, что повелел Господь Моисею: так становились станами при знаменах своих, и так шли каждый по племенам своим, по семействам своим.');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3691, 'rst66', 'num', 2, 33, 'А левиты не вошли в исчисление вместе с сынами Израиля, как повелел Господь Моисею.');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3690, 'rst66', 'num', 2, 32, 'Вот вошедшие в исчисление сыны Израиля по семействам их. Всех, вошедших в исчисление в станах, по ополчениям их, шестьсот три тысячи пятьсот пятьдесят.');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3689, 'rst66', 'num', 2, 31, 'всех, вошедших в исчисление к стану Дана, сто пятьдесят семь тысяч шестьсот; они должны идти последними при знаменах своих.');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3688, 'rst66', 'num', 2, 30, 'и воинства его, вошедших в исчисление его, пятьдесят три тысячи четыреста;');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3687, 'rst66', 'num', 2, 29, 'далее колено Неффалима, и начальник сынов Неффалима Ахира, сын Енана,');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3686, 'rst66', 'num', 2, 28, 'и воинства его, вошедших в исчисление его, сорок одна тысяча пятьсот;');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3685, 'rst66', 'num', 2, 27, 'подле него ставит стан колено Асирово, и начальник сынов Асира Пагиил, сын Охрана,');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3684, 'rst66', 'num', 2, 26, 'и воинства его, вошедших в исчисление его, шестьдесят две тысячи семьсот;');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3683, 'rst66', 'num', 2, 25, 'Знамя стана Данова к северу, по ополчениям их, и начальник сынов Дана Ахиезер, сын Аммишаддая,');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3682, 'rst66', 'num', 2, 24, 'всех, вошедших в исчисление к стану Ефрема, сто восемь тысяч сто, по ополчениям их; третьими они должны отправляться.');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3681, 'rst66', 'num', 2, 23, 'и воинства его, вошедших в исчисление его, тридцать пять тысяч четыреста;');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3680, 'rst66', 'num', 2, 22, 'потом колено Вениамина, и начальник сынов Вениамина Авидан, сын Гидеония,');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3679, 'rst66', 'num', 2, 21, 'и воинства его, вошедших в исчисление его, тридцать две тысячи двести;');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3678, 'rst66', 'num', 2, 20, 'подле него колено Манассиино, и начальник сынов Манассии Гамалиил, сын Педацура,');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3677, 'rst66', 'num', 2, 19, 'и воинства его, вошедших в исчисление его, сорок тысяч пятьсот;');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3676, 'rst66', 'num', 2, 18, 'Знамя стана Ефремова по ополчениям их к западу, и начальник сынов Ефрема Елишама, сын Аммиуда,');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3675, 'rst66', 'num', 2, 17, 'Когда пойдет скиния собрания, стан левитов будет в середине станов. Как стоят, так и должны идти, каждый на своем месте, при знаменах своих.');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3674, 'rst66', 'num', 2, 16, 'всех, вошедших в исчисление к стану Рувима, сто пятьдесят одна тысяча четыреста пятьдесят, по ополчениям их; вторыми они должны отправляться.');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3673, 'rst66', 'num', 2, 15, 'и воинства его, вошедших в исчисление его, сорок пять тысяч шестьсот пятьдесят;');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3672, 'rst66', 'num', 2, 14, 'потом колено Гада, и начальник сынов Гада Елиасаф, сын Регуила,');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3671, 'rst66', 'num', 2, 13, 'и воинства его, вошедших в исчисление его, пятьдесят девять тысяч триста;');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3670, 'rst66', 'num', 2, 12, 'подле него ставит стан колено Симеоново, и начальник сынов Симеона Шелумиил, сын Цуришаддая,');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3669, 'rst66', 'num', 2, 11, 'и воинства его, вошедших в исчисление его, сорок шесть тысяч пятьсот;');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3668, 'rst66', 'num', 2, 10, 'Знамя стана Рувимова к югу, по ополчениям их, и начальник сынов Рувимовых Елицур, сын Шедеура,');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3667, 'rst66', 'num', 2, 9, 'всех, вошедших в исчисление к стану Иуды, сто восемьдесят шесть тысяч четыреста, по ополчениям их; первыми они должны отправляться.');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3666, 'rst66', 'num', 2, 8, 'и воинства его, вошедших в исчисление его, пятьдесят семь тысяч четыреста;');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3665, 'rst66', 'num', 2, 7, '<i>далее ставит стан</i> колено Завулона, и начальник сынов Завулона Елиав, сын Хелона,');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3664, 'rst66', 'num', 2, 6, 'и воинства его, вошедших в исчисление его, пятьдесят четыре тысячи четыреста;');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3663, 'rst66', 'num', 2, 5, 'после него ставит стан колено Иссахарово, и начальник сынов Иссахара Нафанаил, сын Цуара,');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3662, 'rst66', 'num', 2, 4, 'и воинства его, вошедших в исчисление его, семьдесят четыре тысячи шестьсот;');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3661, 'rst66', 'num', 2, 3, 'С передней стороны к востоку ставят стан: знамя стана Иудина по ополчениям их, и начальник сынов Иуды Наассон, сын Аминадава,');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3660, 'rst66', 'num', 2, 2, 'сыны Израилевы должны каждый ставить стан свой при знамени своем, при знаках семейств своих; пред скиниею собрания вокруг должны ставить стан свой.');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3659, 'rst66', 'num', 2, 1, 'И сказал Господь Моисею и Аарону, говоря:');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3658, 'rst66', 'num', 1, 54, 'И сделали сыны Израилевы; как повелел Господь Моисею, так они и сделали.');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3657, 'rst66', 'num', 1, 53, 'а левиты должны ставить стан около скинии откровения, чтобы не было гнева на общество сынов Израилевых, и будут левиты стоять на страже у скинии откровения.');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3656, 'rst66', 'num', 1, 52, 'Сыны Израилевы должны становиться каждый в стане своем, и каждый при своем знамени, по ополчениям своим;');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3655, 'rst66', 'num', 1, 51, 'и когда надобно переносить скинию, пусть поднимают ее левиты, и когда надобно остановиться скинии, пусть ставят ее левиты; а если приступит кто посторонний, предан будет смерти.');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3654, 'rst66', 'num', 1, 50, 'но поручи левитам скинию откровения, и все принадлежности ее, и всё, что при ней; пусть они носят скинию и все принадлежности ее, и служат при ней, и около скинии пусть ставят стан свой;');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3653, 'rst66', 'num', 1, 49, 'только колена Левиина не вноси в перепись, и не исчисляй их вместе с сынами Израиля;');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3652, 'rst66', 'num', 1, 48, 'И сказал Господь Моисею, говоря:');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3651, 'rst66', 'num', 1, 47, 'А левиты по поколениям отцов их не были исчислены между ними.');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3650, 'rst66', 'num', 1, 46, 'и было всех, вошедших в исчисление, шестьсот три тысячи пятьсот пятьдесят.');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3649, 'rst66', 'num', 1, 45, 'И было всех, вошедших в исчисление, сынов Израилевых, по семействам их, от двадцати лет и выше, всех годных для войны у Израиля,');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3648, 'rst66', 'num', 1, 44, 'Вот вошедшие в исчисление, которых исчислил Моисей и Аарон и начальники Израиля &mdash; двенадцать человек, по одному человеку из каждого племени.');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3647, 'rst66', 'num', 1, 43, 'исчислено в колене Неффалимовом пятьдесят три тысячи четыреста.');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3646, 'rst66', 'num', 1, 42, 'Сынов Неффалима по родам их, по племенам их, по семействам их, по числу имен, от двадцати лет и выше, всех годных для войны,');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (3645, 'rst66', 'num', 1, 41, 'исчислено в колене Асировом сорок одна тысяча пятьсот.');",
