        $list = [
            "INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5851, 'rst66', 'deu', 34, 12, 'и по руке сильной и по великим чудесам, которые Моисей совершил пред глазами всего Израиля.');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5850, 'rst66', 'deu', 34, 11, 'по всем знамениям и чудесам, которые послал его Господь сделать в земле Египетской над фараоном и над всеми рабами его и над всею землею его,');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5849, 'rst66', 'deu', 34, 10, 'И не было более у Израиля пророка такого, как Моисей, которого Господь знал лицом к лицу,');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5848, 'rst66', 'deu', 34, 9, 'И Иисус, сын Навин, исполнился духа премудрости, потому что Моисей возложил на него руки свои, и повиновались ему сыны Израилевы и делали так, как повелел Господь Моисею.');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5847, 'rst66', 'deu', 34, 8, 'И оплакивали Моисея сыны Израилевы на равнинах Моавитских тридцать дней. И прошли дни плача и сетования о Моисее.');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5846, 'rst66', 'deu', 34, 7, 'Моисею было сто двадцать лет, когда он умер; но зрение его не притупилось, и крепость в нем не истощилась.');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5845, 'rst66', 'deu', 34, 6, 'и погребен на долине в земле Моавитской против Беф‐Фегора, и никто не знает <i>места</i> погребения его даже до сего дня.');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5844, 'rst66', 'deu', 34, 5, 'И умер там Моисей, раб Господень, в земле Моавитской, по слову Господню;');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5843, 'rst66', 'deu', 34, 4, 'И сказал ему Господь: вот земля, о которой Я клялся Аврааму, Исааку и Иакову, говоря: «семени твоему дам ее»; Я дал тебе увидеть ее глазами твоими, но в нее ты не войдешь.');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5842, 'rst66', 'deu', 34, 3, 'и полуденную страну, и равнину долины Иерихона, город Пальм, до Сигора.');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5841, 'rst66', 'deu', 34, 2, 'и всю <i>землю</i> Неффалимову, и <i>всю</i> землю Ефремову и Манассиину, и всю землю Иудину, даже до самого западного моря,');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5840, 'rst66', 'deu', 34, 1, 'И взошел Моисей с равнин Моавитских на гору Нево, на вершину Фасги, что против Иерихона, и показал ему Господь всю землю Галаад до самого Дана,');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5839, 'rst66', 'deu', 33, 29, 'Блажен ты, Израиль! кто подобен тебе, народ, хранимый Господом, Который есть щит, охраняющий тебя, и меч славы твоей? Враги твои раболепствуют тебе, и ты попираешь выи их.');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5838, 'rst66', 'deu', 33, 28, 'Израиль живет безопасно, один; око Иакова <i>видит пред собою</i> землю, обильную хлебом и вином, и небеса его каплют росу.');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5837, 'rst66', 'deu', 33, 27, 'прибежище <i>твое</i> &mdash; Бог древний, и <i>ты</i> под мышцами вечными; Он прогонит врагов от лица твоего и скажет: «истребляй!»');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5836, 'rst66', 'deu', 33, 26, 'Нет подобного Богу Израилеву, Который по небесам принесся на помощь тебе и во славе Своей на облаках;');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5835, 'rst66', 'deu', 33, 25, 'железо и медь &mdash; запоры твои; как дни твои, <i>будет умножаться</i> богатство твое.');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5834, 'rst66', 'deu', 33, 24, 'Об Асире сказал: благословен между сынами Асир, он будет любим братьями своими, и окунет в елей ногу свою;');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5833, 'rst66', 'deu', 33, 23, 'О Неффалиме сказал: Неффалим насыщен благоволением и исполнен благословения Господа; море и юг во владении <i>его</i>.');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5832, 'rst66', 'deu', 33, 22, 'О Дане сказал: Дан молодой лев, который выбегает из Васана.');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5831, 'rst66', 'deu', 33, 21, 'он избрал себе начаток <i>земли</i>, там почтен уделом от законодателя, и пришел с главами народа, и исполнил правду Господа и суды с Израилем.');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5830, 'rst66', 'deu', 33, 20, 'О Гаде сказал: благословен распространивший Гада; он покоится, как лев, и сокрушает и мышцу и голову;');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5829, 'rst66', 'deu', 33, 19, 'созывают они народ на гору, там закалают законные жертвы, ибо они питаются богатством моря и сокровищами, сокрытыми в песке.');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5828, 'rst66', 'deu', 33, 18, 'О Завулоне сказал: веселись, Завулон, в путях твоих, и Иссахар, в шатрах твоих;');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5827, 'rst66', 'deu', 33, 17, 'крепость его, как первородного тельца, и роги его, как роги буйвола; ими избодет он народы все до пределов земли: это тьмы Ефремовы, это тысячи Манассиины.');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5826, 'rst66', 'deu', 33, 16, 'и вожделенными дарами земли и того, что наполняет ее; благословение Явившегося в терновом кусте да приидет на главу Иосифа и на темя наилучшего из братьев своих;');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5825, 'rst66', 'deu', 33, 15, 'превосходнейшими произведениями гор древних и вожделенными дарами холмов вечных,');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5824, 'rst66', 'deu', 33, 14, 'вожделенными плодами от солнца и вожделенными произведениями луны,');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5823, 'rst66', 'deu', 33, 13, 'Об Иосифе сказал: да благословит Господь землю его вожделенными дарами неба, росою и <i>дарами</i> бездны, лежащей внизу,');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5822, 'rst66', 'deu', 33, 12, 'О Вениамине сказал: возлюбленный Господом обитает у Него безопасно, <i>Бог</i> покровительствует ему всякий день, и он покоится между раменами Его.');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5821, 'rst66', 'deu', 33, 11, 'благослови, Господи, силу его и о деле рук его благоволи, порази чресла восстающих на него и ненавидящих его, чтобы они не могли стоять.');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5820, 'rst66', 'deu', 33, 10, 'учат законам Твоим Иакова и заповедям Твоим Израиля, возлагают курение пред лицо Твое и всесожжения на жертвенник Твой;');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5819, 'rst66', 'deu', 33, 9, 'который говорит об отце своем и матери своей: «я на них не смотрю», и братьев своих не признает, и сыновей своих не знает; ибо они, <i>левиты</i>, слова Твои хранят и завет Твой соблюдают,');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5818, 'rst66', 'deu', 33, 8, 'И о Левии сказал: туммим Твой и урим Твой на святом муже Твоем, которого Ты искусил в Массе, с которым Ты препирался при водах Меривы,');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5817, 'rst66', 'deu', 33, 7, 'Но об Иуде сказал сие: услыши, Господи, глас Иуды и приведи его к народу его; руками своими да защитит он себя, и Ты будь помощником против врагов его.');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5816, 'rst66', 'deu', 33, 6, 'Да живет Рувим и да не умирает, и да <i>не</i> будет малочислен!');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5815, 'rst66', 'deu', 33, 5, 'И он был царь Израиля, когда собирались главы народа вместе с коленами Израилевыми.');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5814, 'rst66', 'deu', 33, 4, 'Закон дал нам Моисей, наследие обществу Иакова.');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5813, 'rst66', 'deu', 33, 3, 'Истинно Он любит народ <i>Свой</i>; все святые его в руке Твоей, и они припали к стопам Твоим, чтобы внимать словам Твоим.');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5812, 'rst66', 'deu', 33, 2, 'Он сказал: Господь пришел от Синая, открылся им от Сеира, воссиял от горы Фарана и шел со тьмами святых; одесную Его огнь закона.');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5811, 'rst66', 'deu', 33, 1, 'Вот благословение, которым Моисей, человек Божий, благословил сынов Израилевых пред смертью своею.');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5810, 'rst66', 'deu', 32, 52, 'пред <i>собою</i> ты увидишь землю, а не войдешь туда, в землю, которую Я даю сынам Израилевым.');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5809, 'rst66', 'deu', 32, 51, 'за то, что вы согрешили против Меня среди сынов Израилевых при водах Меривы в Кадесе, в пустыне Син, за то, что не явили святости Моей среди сынов Израилевых;');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5808, 'rst66', 'deu', 32, 50, 'и умри на горе, на которую ты взойдешь, и приложись к народу твоему, как умер Аарон, брат твой, на горе Ор, и приложился к народу своему,');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5807, 'rst66', 'deu', 32, 49, 'взойди на сию гору Аварим, на гору Нево, которая в земле Моавитской, против Иерихона, и посмотри на землю Ханаанскую, которую я даю во владение сынам Израилевым;');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5806, 'rst66', 'deu', 32, 48, 'И говорил Господь Моисею в тот же самый день и сказал:');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5805, 'rst66', 'deu', 32, 47, 'ибо это не пустое для вас, но это жизнь ваша, и чрез это вы долгое время пробудете на той земле, в которую вы идете чрез Иордан, чтоб овладеть ею.');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5804, 'rst66', 'deu', 32, 46, 'тогда сказал им: положите на сердце ваше все слова, которые я объявил вам сегодня, и завещевайте их детям своим, чтобы они старались исполнять все слова закона сего;');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5803, 'rst66', 'deu', 32, 45, 'Когда Моисей изрек все слова сии всему Израилю,');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5802, 'rst66', 'deu', 32, 44, 'И пришел Моисей <i>к народу</i> и изрек все слова песни сей вслух народа, он и Иисус, сын Навин.');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5801, 'rst66', 'deu', 32, 43, 'Веселитесь, язычники, с народом Его; ибо Он отмстит за кровь рабов Своих, и воздаст мщение врагам Своим, и очистит землю Свою <i>и</i> народ Свой!');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5800, 'rst66', 'deu', 32, 42, 'упою стрелы Мои кровью, и меч Мой насытится плотью, кровью убитых и пленных, головами начальников врага.');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5799, 'rst66', 'deu', 32, 41, 'Когда изострю сверкающий меч Мой, и рука Моя приимет суд, то отмщу врагам Моим и ненавидящим Меня воздам;');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5798, 'rst66', 'deu', 32, 40, 'Я подъемлю к небесам руку Мою и говорю: живу Я вовек!');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5797, 'rst66', 'deu', 32, 39, 'Видите ныне, что это Я, Я &mdash; и нет Бога, кроме Меня: Я умерщвляю и оживляю, Я поражаю и Я исцеляю, и никто не избавит от руки Моей.');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5796, 'rst66', 'deu', 32, 38, 'которые ели тук жертв их <i>и</i> пили вино возлияний их? пусть они восстанут и помогут вам, пусть будут для вас покровом!');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5795, 'rst66', 'deu', 32, 37, 'Тогда скажет <i>Господь</i>: где боги их, твердыня, на которую они надеялись,');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5794, 'rst66', 'deu', 32, 36, 'Но Господь будет судить народ Свой и над рабами Своими умилосердится, когда Он увидит, что рука их ослабела, и не стало ни заключенных, ни оставшихся <i>вне</i>.');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5793, 'rst66', 'deu', 32, 35, 'У Меня отмщение и воздаяние, когда поколеблется нога их; ибо близок день погибели их, скоро наступит уготованное для них.');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5792, 'rst66', 'deu', 32, 34, 'Не сокрыто ли это у Меня? не запечатано ли в хранилищах Моих?');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5791, 'rst66', 'deu', 32, 33, 'вино их яд драконов и гибельная отрава аспидов.');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5790, 'rst66', 'deu', 32, 32, 'Ибо виноград их от виноградной лозы Содомской и с полей Гоморрских; ягоды их ягоды ядовитые, грозди их горькие;');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5789, 'rst66', 'deu', 32, 31, 'Ибо заступник их не таков, как наш Заступник; сами враги наши судьи в том.');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5788, 'rst66', 'deu', 32, 30, 'Как бы мог один преследовать тысячу и двое прогонять тьму, если бы Заступник их не предал их, и Господь не отдал их!');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5787, 'rst66', 'deu', 32, 29, 'О, если бы они рассудили, подумали о сем, уразумели, что с ними будет!');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5786, 'rst66', 'deu', 32, 28, 'Ибо они народ, потерявший рассудок, и нет в них смысла.');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5785, 'rst66', 'deu', 32, 27, 'но отложил это ради озлобления врагов, чтобы враги его не возомнили и не сказали: «наша рука высока, и не Господь сделал все сие».');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5784, 'rst66', 'deu', 32, 26, 'Я сказал бы: «рассею их и изглажу из среды людей память о них»;');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5783, 'rst66', 'deu', 32, 25, 'извне будет губить их меч, а в домах ужас &mdash; и юношу, и девицу, и грудного младенца, и покрытого сединою старца.');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5782, 'rst66', 'deu', 32, 24, '<i>будут</i> истощены голодом, истреблены горячкою и лютою заразою; и пошлю на них зубы зверей и яд ползающих по земле;');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5781, 'rst66', 'deu', 32, 23, 'соберу на них бедствия и истощу на них стрелы Мои:');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5780, 'rst66', 'deu', 32, 22, 'ибо огонь возгорелся во гневе Моем, жжет до ада преисподнего, и поядает землю и произведения ее, и попаляет основания гор;');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5779, 'rst66', 'deu', 32, 21, 'они раздражили Меня не богом, суетными своими огорчили Меня: и Я раздражу их не народом, народом бессмысленным огорчу их;');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5778, 'rst66', 'deu', 32, 20, 'и сказал: сокрою лицо Мое от них <i>и</i> увижу, какой будет конец их; ибо они род развращенный; дети, в которых нет верности;');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5777, 'rst66', 'deu', 32, 19, 'Господь увидел, и в негодовании пренебрег сынов Своих и дочерей Своих,');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5776, 'rst66', 'deu', 32, 18, 'А Заступника, родившего тебя, ты забыл, и не помнил Бога, создавшего тебя.');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5775, 'rst66', 'deu', 32, 17, 'приносили жертвы бесам, а не Богу, богам, которых они не знали, новым, <i>которые</i> пришли от соседей и о которых не помышляли отцы ваши.');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5774, 'rst66', 'deu', 32, 16, '<i>Богами</i> чуждыми они раздражили Его и мерзостями разгневали Его:');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5773, 'rst66', 'deu', 32, 15, 'И утучнел Израиль, и стал упрям; утучнел, отолстел и разжирел; и оставил он Бога, создавшего его, и презрел твердыню спасения своего.');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5772, 'rst66', 'deu', 32, 14, 'маслом коровьим и молоком овечьим, и туком агнцев и овнов Васанских и козлов, и тучною пшеницею, и ты пил вино, кровь виноградных ягод.');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5771, 'rst66', 'deu', 32, 13, 'Он вознес его на высоту земли и кормил произведениями полей, и питал его медом из камня и елеем из твердой скалы,');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5770, 'rst66', 'deu', 32, 12, 'так Господь один водил его, и не было с Ним чужого бога.');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5769, 'rst66', 'deu', 32, 11, 'как орел вызывает гнездо свое, носится над птенцами своими, распростирает крылья свои, берет их и носит их на перьях своих,');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5768, 'rst66', 'deu', 32, 10, 'Он нашел его в пустыне, в степи печальной и дикой, ограждал его, смотрел за ним, хранил его, как зеницу ока Своего;');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5767, 'rst66', 'deu', 32, 9, 'ибо часть Господа народ Его, Иаков наследственный удел Его.');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5766, 'rst66', 'deu', 32, 8, 'Когда Всевышний давал уделы народам и расселял сынов человеческих, тогда поставил пределы народов по числу сынов Израилевых;');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5765, 'rst66', 'deu', 32, 7, 'Вспомни дни древние, помысли о летах прежних родов; спроси отца твоего, и он возвестит тебе, старцев твоих, и они скажут тебе.');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5764, 'rst66', 'deu', 32, 6, 'Сие ли воздаете вы Господу, народ глупый и несмысленный? не Он ли Отец твой, <i>Который</i> усвоил тебя, создал тебя и устроил тебя?');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5763, 'rst66', 'deu', 32, 5, 'но они развратились пред Ним, они не дети Его по своим порокам, род строптивый и развращенный.');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5762, 'rst66', 'deu', 32, 4, 'Он твердыня; совершенны дела Его, и все пути Его праведны; Бог верен, и нет неправды <i>в Нем</i>; Он праведен и истинен;');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5761, 'rst66', 'deu', 32, 3, 'Имя Господа прославляю; воздайте славу Богу нашему.');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5760, 'rst66', 'deu', 32, 2, 'Польется, как дождь, учение мое, как роса, речь моя, как мелкий дождь на зелень, как ливень на траву.');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5759, 'rst66', 'deu', 32, 1, 'Внимай, небо, я буду говорить; и слушай, земля, слова уст моих.');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5758, 'rst66', 'deu', 31, 30, 'И изрек Моисей вслух всего собрания Израильтян слова песни сей до конца:');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5757, 'rst66', 'deu', 31, 29, 'ибо я знаю, что по смерти моей вы развратитесь и уклонитесь от пути, который я завещал вам, и в последствие времени постигнут вас бедствия за то, что вы будете делать зло пред очами Господа, раздражая Его делами рук своих.');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5756, 'rst66', 'deu', 31, 28, 'Соберите ко мне всех старейшин колен ваших и надзирателей ваших, и я скажу вслух их слова сии и призову во свидетельство на них небо и землю;');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5755, 'rst66', 'deu', 31, 27, 'ибо я знаю упорство твое и жестоковыйность твою: вот и теперь, когда я живу с вами ныне, вы упорны пред Господом; не тем ли более по смерти моей?');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5754, 'rst66', 'deu', 31, 26, 'возьмите сию книгу закона и положите ее одесную ковчега завета Господа, Бога вашего, и она там будет свидетельством против тебя;');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5753, 'rst66', 'deu', 31, 25, 'тогда Моисей повелел левитам, носящим ковчег завета Господня, сказав:');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5752, 'rst66', 'deu', 31, 24, 'Когда Моисей вписал в книгу все слова закона сего до конца,');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5751, 'rst66', 'deu', 31, 23, 'И заповедал Господь Иисусу, сыну Навину, и сказал <i>ему</i>: будь тверд и мужествен, ибо ты введешь сынов Израилевых в землю, о которой Я клялся им, и Я буду с тобою.');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5750, 'rst66', 'deu', 31, 22, 'И написал Моисей песнь сию в тот день и научил ей сынов Израилевых.');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5749, 'rst66', 'deu', 31, 21, 'и когда постигнут их многие бедствия и скорби, тогда песнь сия будет против них свидетельством, ибо она не выйдет из уст потомства их. Я знаю мысли их, которые они имеют ныне, прежде нежели Я ввел их в землю, о которой Я клялся.');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5748, 'rst66', 'deu', 31, 20, 'ибо Я введу их в землю, как Я клялся отцам их, где течет молоко и мед, и они будут есть и насыщаться, и утучнеют, и обратятся к иным богам, и будут служить им, а Меня отвергнут и нарушат завет Мой;');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5747, 'rst66', 'deu', 31, 19, 'Итак, напишите себе <i>слова</i> песни сей, и научи ей сынов Израилевых, и вложи ее в уста их, чтобы песнь сия была Мне свидетельством на сынов Израилевых;');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5746, 'rst66', 'deu', 31, 18, 'И Я сокрою лицо Мое <i>от него</i> в тот день за все беззакония его, которые он сделает, обратившись к иным богам.');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5745, 'rst66', 'deu', 31, 17, 'и возгорится гнев Мой на него в тот день, и Я оставлю их и сокрою лицо Мое от них, и он истреблен будет, и постигнут его многие бедствия и скорби, и скажет он в тот день: «не потому ли постигли меня сии бедствия, что нет Бога моего среди меня?»');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5744, 'rst66', 'deu', 31, 16, 'И сказал Господь Моисею: вот, ты почиешь с отцами твоими, и станет народ сей блудно ходить вслед чужих богов той земли, в которую он вступает, и оставит Меня, и нарушит завет Мой, который Я поставил с ним;');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5743, 'rst66', 'deu', 31, 15, 'И явился Господь в скинии в столпе облачном, и стал столп облачный у входа скинии.');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5742, 'rst66', 'deu', 31, 14, 'И сказал Господь Моисею: вот, дни твои приблизились к смерти; призови Иисуса, и станьте у скинии собрания, и Я дам ему наставления. И пришел Моисей и Иисус, и стали у скинии собрания.');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5741, 'rst66', 'deu', 31, 13, 'и сыны их, которые не знают <i>сего</i>, услышат и научатся бояться Господа, Бога вашего, во все дни, доколе вы будете жить на земле, в которую вы переходите за Иордан, чтоб овладеть ею.');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5740, 'rst66', 'deu', 31, 12, 'собери народ, мужей и жен, и детей, и пришельцев твоих, которые будут в жилищах твоих, чтоб они слушали и учились, и чтобы боялись Господа, Бога вашего, и старались исполнять все слова закона сего;');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5739, 'rst66', 'deu', 31, 11, 'когда весь Израиль придет явиться пред лицо Господа, Бога твоего, на место, которое изберет <i>Господь</i>, читай сей закон пред всем Израилем вслух его;');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5738, 'rst66', 'deu', 31, 10, 'И завещал им Моисей и сказал: по прошествии семи лет, в год отпущения, в праздник кущей,');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5737, 'rst66', 'deu', 31, 9, 'И написал Моисей закон сей, и отдал его священникам, сынам Левииным, носящим ковчег завета Господня, и всем старейшинам <i>сынов</i> Израилевых.');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5736, 'rst66', 'deu', 31, 8, 'Господь Сам пойдет пред тобою, Сам будет с тобою, не отступит от тебя и не оставит тебя, не бойся и не ужасайся.');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5735, 'rst66', 'deu', 31, 7, 'И призвал Моисей Иисуса и пред очами всех Израильтян сказал ему: будь тверд и мужествен, ибо ты войдешь с народом сим в землю, которую Господь клялся отцам его дать ему, и ты разделишь ее на уделы ему;');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5734, 'rst66', 'deu', 31, 6, 'будьте тверды и мужественны, не бойтесь и не страшитесь их, ибо Господь, Бог твой, Сам пойдет с тобою, <i>и</i> не отступит от тебя и не оставит тебя.');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5733, 'rst66', 'deu', 31, 5, 'и предаст их Господь вам, и вы поступите с ними по всем заповедям, какие заповедал я вам;');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5732, 'rst66', 'deu', 31, 4, 'и поступит Господь с ними так же, как Он поступил с Сигоном и Огом, царями Аморрейскими, и с землею их, которых он истребил;');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5731, 'rst66', 'deu', 31, 3, 'Господь, Бог твой, Сам пойдет пред тобою; Он истребит народы сии от лица твоего, и ты овладеешь ими; Иисус пойдет пред тобою, как говорил Господь;');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5730, 'rst66', 'deu', 31, 2, 'и сказал им: теперь мне сто двадцать лет, я не могу уже выходить и входить, и Господь сказал мне: «ты не перейдешь Иордан сей»;');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5729, 'rst66', 'deu', 31, 1, 'И пошел Моисей, и говорил слова сии всем <i>сынам</i> Израиля,');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5728, 'rst66', 'deu', 30, 20, 'любил Господа, Бога твоего, слушал глас Его и прилеплялся к Нему; ибо в этом жизнь твоя и долгота дней твоих, чтобы пребывать тебе на земле, которую Господь с клятвою обещал отцам твоим Аврааму, Исааку и Иакову дать им.');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5727, 'rst66', 'deu', 30, 19, 'Во свидетели пред вами призываю сегодня небо и землю: жизнь и смерть предложил я тебе, благословение и проклятие. Избери жизнь, дабы жил ты и потомство твое,');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5726, 'rst66', 'deu', 30, 18, 'то я возвещаю вам сегодня, что вы погибнете и не пробудете долго на земле, для овладения которою ты переходишь Иордан.');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5725, 'rst66', 'deu', 30, 17, 'если же отвратится сердце твое, и не будешь слушать, и заблудишь, и станешь поклоняться иным богам и будешь служить им,');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5724, 'rst66', 'deu', 30, 16, '[Если будешь слушать заповеди Господа, Бога твоего,] которые я заповедую тебе сегодня &mdash; любить Господа, Бога твоего, ходить по путям Его и исполнять заповеди Его и постановления Его и законы Его; и будешь <i>ты</i> жить и размножишься, и благословит тебя Господь, Бог твой, на земле, в которую ты идешь, чтоб овладеть ею;');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5723, 'rst66', 'deu', 30, 15, 'Вот, я сегодня предложил тебе жизнь и добро, смерть и зло.');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5722, 'rst66', 'deu', 30, 14, 'Но весьма близко к тебе слово сие: <i>оно</i> в устах твоих и в сердце твоем, чтобы исполнять его.');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5721, 'rst66', 'deu', 30, 13, 'И не за морем она, чтобы можно <i>было</i> говорить: «кто сходил бы для нас за море и принес бы ее нам, и дал бы нам услышать ее, и мы исполнили бы ее?»');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5720, 'rst66', 'deu', 30, 12, 'она не на небе, чтобы можно <i>было</i> говорить: «кто взошел бы для нас на небо и принес бы ее нам, и дал бы нам услышать ее, и мы исполнили бы ее?»');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5719, 'rst66', 'deu', 30, 11, 'Ибо заповедь сия, которую я заповедую тебе сегодня, не недоступна для тебя и не далека;');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5718, 'rst66', 'deu', 30, 10, 'если будешь слушать гласа Господа, Бога твоего, соблюдая заповеди Его и постановления Его, написанные в сей книге закона, и если обратишься к Господу, Богу твоему, всем сердцем твоим и всею душою твоею.');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5717, 'rst66', 'deu', 30, 9, 'С избытком даст тебе Господь, Бог твой, успех во всяком деле рук твоих, в плоде чрева твоего, в плоде скота твоего, в плоде земли твоей; ибо снова радоваться будет Господь о тебе, благодетельствуя <i>тебе</i>, как Он радовался об отцах твоих,');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5716, 'rst66', 'deu', 30, 8, 'а ты обратишься и будешь слушать гласа Господа и исполнять все заповеди Его, которые заповедую тебе сегодня.');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5715, 'rst66', 'deu', 30, 7, 'тогда Господь, Бог твой, все проклятия сии обратит на врагов твоих и ненавидящих тебя, которые гнали тебя,');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5714, 'rst66', 'deu', 30, 6, 'и обрежет Господь, Бог твой, сердце твое и сердце потомства твоего, чтобы ты любил Господа, Бога твоего, от всего сердца твоего и от всей души твоей, дабы жить тебе;');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5713, 'rst66', 'deu', 30, 5, 'и приведет тебя Господь, Бог твой, в землю, которою владели отцы твои, и получишь ее во владение, и облагодетельствует тебя и размножит тебя более отцов твоих;');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5712, 'rst66', 'deu', 30, 4, 'Хотя бы ты был рассеян до края неба, и оттуда соберет тебя Господь, Бог твой, и оттуда возьмет тебя,');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5711, 'rst66', 'deu', 30, 3, 'тогда Господь, Бог твой, возвратит пленных твоих и умилосердится над тобою, и опять соберет тебя от всех народов, между которыми рассеет тебя Господь, Бог твой.');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5710, 'rst66', 'deu', 30, 2, 'и обратишься к Господу, Богу твоему, и послушаешь гласа Его, как я заповедую тебе сегодня, ты и сыны твои от всего сердца твоего и от всей души твоей, &mdash;');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5709, 'rst66', 'deu', 30, 1, 'Когда придут на тебя все слова сии &mdash; благословение и проклятие, которые изложил я тебе, и примешь <i>их</i> к сердцу своему среди всех народов, в которых рассеет тебя Господь, Бог твой,');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5708, 'rst66', 'deu', 29, 29, 'Сокрытое <i>принадлежит</i> Господу, Богу нашему, а открытое &mdash; нам и сынам нашим до века, чтобы мы исполняли все слова закона сего.');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5707, 'rst66', 'deu', 29, 28, 'и извергнул их Господь из земли их в гневе, ярости и великом негодовании, и поверг их на другую землю, как ныне <i>видим</i>».');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5706, 'rst66', 'deu', 29, 27, '<i>за то</i> возгорелся гнев Господа на землю сию, и навел Он на нее все проклятия <i>завета</i>, написанные в сей книге;');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5705, 'rst66', 'deu', 29, 26, 'и пошли и стали служить иным богам и поклоняться им, богам, которых они не знали и <i>которых</i> Он не назначал им:');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5704, 'rst66', 'deu', 29, 25, 'И скажут: «за то, что они оставили завет Господа, Бога отцов своих, который Он поставил с ними, когда вывел их из земли Египетской,');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5703, 'rst66', 'deu', 29, 24, 'И скажут все народы: «за что Господь так поступил с сею землею? какая великая ярость гнева Его!»');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5702, 'rst66', 'deu', 29, 23, '«Сера и соль, пожарище &mdash; вся земля; не засевается и не произращает она, и не выходит на ней никакой травы, как по истреблении Содома, Гоморры, Адмы и Севоима, которые ниспроверг Господь во гневе Своем и в ярости Своей».');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5701, 'rst66', 'deu', 29, 22, 'И скажет последующий род, дети ваши, которые будут после вас, и чужеземец, который придет из земли дальней, увидев поражение земли сей и болезни, которыми изнурит ее Господь:');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5700, 'rst66', 'deu', 29, 21, 'и отделит его Господь на погибель от всех колен Израилевых, сообразно со всеми проклятиями завета, написанными в сей книге закона.');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5699, 'rst66', 'deu', 29, 20, 'не простит Господь такому, но тотчас возгорится гнев Господа и ярость Его на такого человека, и падет на него все проклятие <i>завета сего</i>, написанное в сей книге, и изгладит Господь имя его из поднебесной;');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5698, 'rst66', 'deu', 29, 19, 'такого человека, который, услышав слова проклятия сего, похвалялся бы в сердце своем, говоря: «я буду счастлив, несмотря на то, что буду ходить по произволу сердца моего»; и пропадет таким образом сытый с голодным;');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5697, 'rst66', 'deu', 29, 18, 'Да не будет между вами мужчины или женщины, или рода или колена, которых сердце уклонилось бы ныне от Господа, Бога нашего, чтобы ходить служить богам тех народов; да не будет между вами корня, произращающего яд и полынь,');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5696, 'rst66', 'deu', 29, 17, 'и видели мерзости их и кумиры их, деревянные и каменные, серебряные и золотые, которые у них.');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5695, 'rst66', 'deu', 29, 16, 'Ибо вы знаете, как мы жили в земле Египетской и как мы проходили посреди народов, чрез которые вы прошли,');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5694, 'rst66', 'deu', 29, 15, 'но как с теми, которые сегодня здесь с нами стоят пред лицом Господа, Бога нашего, так и с теми, которых нет здесь с нами сегодня.');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5693, 'rst66', 'deu', 29, 14, 'Не с вами только одними я поставляю сей завет и сей клятвенный договор,');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5692, 'rst66', 'deu', 29, 13, 'дабы соделать тебя сегодня Его народом, и Ему быть тебе Богом, как Он говорил тебе и как клялся отцам твоим Аврааму, Исааку и Иакову.');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5691, 'rst66', 'deu', 29, 12, 'чтобы вступить тебе в завет Господа, Бога твоего, и в клятвенный договор с Ним, который Господь, Бог твой, сегодня поставляет с тобою,');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5690, 'rst66', 'deu', 29, 11, 'дети ваши, жены ваши и пришельцы твои, находящиеся в стане твоем, от секущего дрова твои до черпающего воду твою,');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5689, 'rst66', 'deu', 29, 10, 'Все вы сегодня стоите пред лицом Господа, Бога вашего, начальники колен ваших, старейшины ваши, надзиратели ваши, все Израильтяне,');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5688, 'rst66', 'deu', 29, 9, 'Соблюдайте же слова завета сего и исполняйте их, чтобы вам иметь успех во всем, что ни будете делать.');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5687, 'rst66', 'deu', 29, 8, 'и взяли землю их, и отдали ее в удел <i>колену</i> Рувимову и Гадову и половине колена Манассиина.');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5686, 'rst66', 'deu', 29, 7, 'И когда пришли вы на место сие, выступили против нас Сигон, царь Есевонский, и Ог, царь Васанский, чтобы сразиться <i>с нами</i>, и мы поразили их;');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5685, 'rst66', 'deu', 29, 6, 'хлеба вы не ели и вина и сикера не пили, дабы вы знали, что Я Господь, Бог ваш.');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5684, 'rst66', 'deu', 29, 5, 'Сорок лет водил вас по пустыне, и одежды ваши на вас не обветшали, и обувь твоя не обветшала на ноге твоей;');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5683, 'rst66', 'deu', 29, 4, 'но до сего дня не дал вам Господь сердца, чтобы разуметь, очей, чтобы видеть, и ушей, чтобы слышать.');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5682, 'rst66', 'deu', 29, 3, 'те великие казни, которые видели глаза твои, и те великие знамения и чудеса;');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5681, 'rst66', 'deu', 29, 2, 'И созвал Моисей всех <i>сынов</i> Израилевых и сказал им: вы видели всё, что сделал Господь пред глазами вашими в земле Египетской с фараоном и всеми рабами его и всею землею его;');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5680, 'rst66', 'deu', 29, 1, 'Вот слова завета, который Господь повелел Моисею поставить с сынами Израилевыми в земле Моавитской, кроме завета, который Господь поставил с ними на Хориве.');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5679, 'rst66', 'deu', 28, 68, 'И возвратит тебя Господь в Египет на кораблях тем путем, о котором я сказал тебе: «ты более не увидишь его»; и там будете продаваться врагам вашим в рабов и в рабынь, и не будет покупающего.');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5678, 'rst66', 'deu', 28, 67, 'от трепета сердца твоего, которым ты будешь объят, и от того, что ты будешь видеть глазами твоими, утром ты скажешь: «о, если бы пришел вечер!», а вечером скажешь: «о, если бы наступило утро!»');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5677, 'rst66', 'deu', 28, 66, 'жизнь твоя будет висеть пред тобою, и будешь трепетать ночью и днем, и не будешь уверен в жизни твоей;');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5676, 'rst66', 'deu', 28, 65, 'Но и между этими народами не успокоишься, и не будет места покоя для ноги твоей, и Господь даст тебе там трепещущее сердце, истаевание очей и изнывание души;');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5675, 'rst66', 'deu', 28, 64, 'И рассеет тебя Господь по всем народам, от края земли до края земли, и будешь там служить иным богам, которых не знал ни ты, ни отцы твои, дереву и камням.');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5674, 'rst66', 'deu', 28, 63, 'И как радовался Господь, делая вам добро и умножая вас, так будет радоваться Господь, погубляя вас и истребляя вас, и извержены будете из земли, в которую ты идешь, чтобы владеть ею.');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5673, 'rst66', 'deu', 28, 62, 'и останется вас немного, тогда как множеством вы подобны были звездам небесным, ибо ты не слушал гласа Господа, Бога твоего.');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5672, 'rst66', 'deu', 28, 61, 'и всякую болезнь и всякую язву, не написанную в книге закона сего, Господь наведет на тебя, доколе не будешь истреблен;');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5671, 'rst66', 'deu', 28, 60, 'и наведет на тебя все язвы Египетские, которых ты боялся, и они прилипнут к тебе;');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5670, 'rst66', 'deu', 28, 59, 'то Господь поразит тебя и потомство твое необычайными язвами, язвами великими и постоянными, и болезнями злыми и постоянными;');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5669, 'rst66', 'deu', 28, 58, 'Если не будешь стараться исполнять все слова закона сего, написанные в книге сей, и не будешь бояться сего славного и страшного имени Господа, Бога твоего,');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5668, 'rst66', 'deu', 28, 57, 'и <i>не даст</i> им последа, выходящего из среды ног ее, и детей, которых она родит; потому что она, при недостатке во всем, тайно будет есть их, в осаде и стеснении, в котором стеснит тебя враг твой в жилищах твоих.');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5667, 'rst66', 'deu', 28, 56, '<i>Женщина</i>, жившая у тебя в неге и роскоши, которая никогда ноги своей не ставила на землю по причине роскоши и изнеженности, будет безжалостным оком смотреть на мужа недра своего и на сына своего и на дочь свою');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5666, 'rst66', 'deu', 28, 55, 'и не даст ни одному из них плоти детей своих, которых он будет есть, потому что у него не останется ничего в осаде и в стеснении, в котором стеснит тебя враг твой во всех жилищах твоих.');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5665, 'rst66', 'deu', 28, 54, 'Муж, изнеженный и живший между вами в великой роскоши, безжалостным оком будет смотреть на брата своего, на жену недра своего и на остальных детей своих, которые останутся у него,');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5664, 'rst66', 'deu', 28, 53, 'И ты будешь есть плод чрева твоего, плоть сынов твоих и дочерей твоих, которых Господь, Бог твой, дал тебе, в осаде и в стеснении, в котором стеснит тебя враг твой.');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5663, 'rst66', 'deu', 28, 52, 'и будет теснить тебя во всех жилищах твоих, доколе во всей земле твоей не разрушит высоких и крепких стен твоих, на которые ты надеешься; и будет теснить тебя во всех жилищах твоих, во всей земле твоей, которую Господь, Бог твой, дал тебе.');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5662, 'rst66', 'deu', 28, 51, 'и будет он есть плод скота твоего и плод земли твоей, доколе не разорит тебя, так что не оставит тебе ни хлеба, ни вина, ни елея, ни плода волов твоих, ни плода овец твоих, доколе не погубит тебя;');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5661, 'rst66', 'deu', 28, 50, 'народ наглый, который не уважит старца и не пощадит юноши;');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5660, 'rst66', 'deu', 28, 49, 'Пошлет на тебя Господь народ издалека, от края земли: как орел налетит народ, которого языка ты не разумеешь,');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5659, 'rst66', 'deu', 28, 48, 'будешь служить врагу твоему, которого пошлет на тебя Господь, в голоде, и жажде, и наготе, и во всяком недостатке; он возложит на шею твою железное ярмо, так что измучит тебя.');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5658, 'rst66', 'deu', 28, 47, 'За то, что ты не служил Господу, Богу твоему, с веселием и радостью сердца, при изобилии всего,');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5657, 'rst66', 'deu', 28, 46, 'они будут знамением и указанием на тебе и на семени твоем вовек.');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5656, 'rst66', 'deu', 28, 45, 'И придут на тебя все проклятия сии, и будут преследовать тебя и постигнут тебя, доколе не будешь истреблен, за то, что ты не слушал гласа Господа, Бога твоего, и не соблюдал заповедей Его и постановлений Его, которые Он заповедал тебе:');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5655, 'rst66', 'deu', 28, 44, 'он будет давать тебе взаймы, а ты не будешь давать ему взаймы; он будет главою, а ты будешь хвостом.');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5654, 'rst66', 'deu', 28, 43, 'Пришелец, который среди тебя, будет возвышаться над тобою выше и выше, а ты опускаться будешь ниже и ниже;');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5653, 'rst66', 'deu', 28, 42, 'Все дерева твои и плоды земли твоей погубит ржавчина.');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5652, 'rst66', 'deu', 28, 41, 'Сынов и дочерей родишь, но их не будет у тебя, потому что пойдут в плен.');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5651, 'rst66', 'deu', 28, 40, 'Маслины будут у тебя во всех пределах твоих, но елеем не помажешься, потому что осыплется маслина твоя.');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5650, 'rst66', 'deu', 28, 39, 'Виноградники будешь садить и возделывать, а вина не будешь пить, и не соберешь <i>плодов их</i>, потому что поест их червь.');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5649, 'rst66', 'deu', 28, 38, 'Семян много вынесешь в поле, а соберешь мало, потому что поест их саранча.');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5648, 'rst66', 'deu', 28, 37, 'и будешь ужасом, притчею и посмешищем у всех народов, к которым отведет тебя Господь.');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5647, 'rst66', 'deu', 28, 36, 'Отведет Господь тебя и царя твоего, которого ты поставишь над собою, к народу, которого не знал ни ты, ни отцы твои, и там будешь служить иным богам, деревянным и каменным;');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5646, 'rst66', 'deu', 28, 35, 'Поразит тебя Господь злою проказою на коленях и голенях, от которой ты не возможешь исцелиться, от подошвы ноги твоей до самого темени <i>головы</i> твоей.');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5645, 'rst66', 'deu', 28, 34, 'И сойдешь с ума от того, что будут видеть глаза твои.');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5644, 'rst66', 'deu', 28, 33, 'Плоды земли твоей и все труды твои будет есть народ, которого ты не знал; и ты будешь только притесняем и мучим во все дни.');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5643, 'rst66', 'deu', 28, 32, 'Сыновья твои и дочери твои будут отданы другому народу; глаза твои будут видеть и всякий день истаевать о них, и не будет силы в руках твоих.');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5642, 'rst66', 'deu', 28, 31, 'Вола твоего заколют в глазах твоих, и не будешь есть его; осла твоего уведут от тебя и не возвратят тебе; овцы твои отданы будут врагам твоим, и никто не защитит тебя.');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5641, 'rst66', 'deu', 28, 30, 'С женою обручишься, и другой будет спать с нею; дом построишь, и не будешь жить в нем; виноградник насадишь, и не будешь пользоваться им.');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5640, 'rst66', 'deu', 28, 29, 'И ты будешь ощупью ходить в полдень, как слепой ощупью ходит впотьмах, и не будешь иметь успеха в путях твоих, и будут теснить и обижать тебя всякий день, и никто не защитит тебя.');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5639, 'rst66', 'deu', 28, 28, 'поразит тебя Господь сумасшествием, слепотою и оцепенением сердца.');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5638, 'rst66', 'deu', 28, 27, 'Поразит тебя Господь проказою Египетскою, почечуем, коростою и чесоткою, от которых ты не возможешь исцелиться;');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5637, 'rst66', 'deu', 28, 26, 'И будут трупы твои пищею всем птицам небесным и зверям, и не будет отгоняющего их.');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5636, 'rst66', 'deu', 28, 25, 'Предаст тебя Господь на поражение врагам твоим; одним путем выступишь против них, а семью путями побежишь от них; и будешь рассеян по всем царствам земли.');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5635, 'rst66', 'deu', 28, 24, 'вместо дождя Господь даст земле твоей пыль, и прах с неба будет падать, падать на тебя, доколе не будешь истреблен.');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5634, 'rst66', 'deu', 28, 23, 'И небеса твои, которые над головою твоею, сделаются медью, и земля под тобою железом;');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5633, 'rst66', 'deu', 28, 22, 'Поразит тебя Господь чахлостью, горячкою, лихорадкою, воспалением, засухою, палящим ветром и ржавчиною, и они будут преследовать тебя, доколе не погибнешь.');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5632, 'rst66', 'deu', 28, 21, 'Пошлет Господь на тебя моровую язву, доколе не истребит Он тебя с земли, в которую ты идешь, чтобы владеть ею.');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5631, 'rst66', 'deu', 28, 20, 'Пошлет Господь на тебя проклятие, смятение и несчастье во всяком деле рук твоих, какое ни станешь ты делать, доколе не будешь истреблен, &mdash; и ты скоро погибнешь за злые дела твои, за то, что ты оставил Меня.');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5630, 'rst66', 'deu', 28, 19, 'Проклят ты <i>будешь</i> при входе твоем и проклят при выходе твоем.');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5629, 'rst66', 'deu', 28, 18, 'Проклят <i>будет</i> плод чрева твоего и плод земли твоей, плод твоих волов и плод овец твоих.');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5628, 'rst66', 'deu', 28, 17, 'Прокляты <i>будут</i> житницы твои и кладовые твои.');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5627, 'rst66', 'deu', 28, 16, 'Проклят ты <i>будешь</i> в городе и проклят ты <i>будешь</i> на поле.');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5626, 'rst66', 'deu', 28, 15, 'Если же не будешь слушать гласа Господа, Бога твоего, и не будешь стараться исполнять все заповеди Его и постановления Его, которые я заповедую тебе сегодня, то придут на тебя все проклятия сии и постигнут тебя.');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5625, 'rst66', 'deu', 28, 14, 'и не отступишь от всех слов, которые заповедую вам сегодня, ни направо, ни налево, чтобы пойти вслед иных богов <i>и</i> служить им.');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5624, 'rst66', 'deu', 28, 13, 'Сделает тебя Господь главою, а не хвостом, и будешь только на высоте, а не будешь внизу, если будешь повиноваться заповедям Господа, Бога твоего, которые заповедую тебе сегодня хранить и исполнять,');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5623, 'rst66', 'deu', 28, 12, 'Откроет тебе Господь добрую сокровищницу Свою, небо, чтоб оно давало дождь земле твоей во время свое, и чтобы благословлять все дела рук твоих: и будешь давать взаймы многим народам, а сам не будешь брать взаймы.');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5622, 'rst66', 'deu', 28, 11, 'И даст тебе Господь изобилие во всех благах, в плоде чрева твоего, и в плоде скота твоего, и в плоде полей твоих на земле, которую Господь клялся отцам твоим дать тебе.');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5621, 'rst66', 'deu', 28, 10, 'и увидят все народы земли, что имя Господа нарицается на тебе, и убоятся тебя.');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5620, 'rst66', 'deu', 28, 9, 'Поставит тебя Господь народом святым Своим, как Он клялся тебе, если ты будешь соблюдать заповеди Господа, Бога твоего, и будешь ходить путями Его;');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5619, 'rst66', 'deu', 28, 8, 'Пошлет Господь тебе благословение в житницах твоих и во всяком деле рук твоих; и благословит тебя на земле, которую Господь, Бог твой, дает тебе.');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5618, 'rst66', 'deu', 28, 7, 'Поразит пред тобою Господь врагов твоих, восстающих на тебя; одним путем они выступят против тебя, а семью путями побегут от тебя.');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5617, 'rst66', 'deu', 28, 6, 'Благословен ты при входе твоем и благословен ты при выходе твоем.');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5616, 'rst66', 'deu', 28, 5, 'Благословенны житницы твои и кладовые твои.');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5615, 'rst66', 'deu', 28, 4, 'Благословен плод чрева твоего, и плод земли твоей, и плод скота твоего, и плод твоих волов, и плод овец твоих.');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5614, 'rst66', 'deu', 28, 3, 'Благословен ты в городе и благословен на поле.');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5613, 'rst66', 'deu', 28, 2, 'и придут на тебя все благословения сии и исполнятся на тебе, если будешь слушать гласа Господа, Бога твоего.');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5612, 'rst66', 'deu', 28, 1, 'Если ты будешь слушать гласа Господа, Бога твоего, тщательно исполнять все заповеди Его, которые заповедую тебе сегодня, то Господь, Бог твой, поставит тебя выше всех народов земли;');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5611, 'rst66', 'deu', 27, 26, '«Проклят, кто не исполнит слов закона сего и не будет поступать по ним!» И весь народ скажет: «аминь».');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5610, 'rst66', 'deu', 27, 25, '«Проклят, кто берет подкуп, чтоб убить душу <i>и пролить</i> кровь невинную!» И весь народ скажет: «аминь».');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5609, 'rst66', 'deu', 27, 24, '«Проклят, кто тайно убивает ближнего своего!» И весь народ скажет: «аминь».');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5608, 'rst66', 'deu', 27, 23, '«Проклят, кто ляжет с тещею своею!» И весь народ скажет: «аминь».');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5607, 'rst66', 'deu', 27, 22, '«Проклят, кто ляжет с сестрою своею, с дочерью отца своего, или дочерью матери своей!» И весь народ скажет: «аминь».');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5606, 'rst66', 'deu', 27, 21, '«Проклят, кто ляжет с каким‐либо скотом!» И весь народ скажет: «аминь».');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5605, 'rst66', 'deu', 27, 20, '«Проклят, кто ляжет с женою отца своего, ибо он открыл край <i>одежды</i> отца своего!» И весь народ скажет: «аминь».');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5604, 'rst66', 'deu', 27, 19, '«Проклят, кто превратно судит пришельца, сироту и вдову!» И весь народ скажет: «аминь».');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5603, 'rst66', 'deu', 27, 18, '«Проклят, кто слепого сбивает с пути!» И весь народ скажет: «аминь».');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5602, 'rst66', 'deu', 27, 17, '«Проклят нарушающий межи ближнего своего!» И весь народ скажет: «аминь».');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5601, 'rst66', 'deu', 27, 16, '«Проклят злословящий отца своего или матерь свою!» И весь народ скажет: «аминь».');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5600, 'rst66', 'deu', 27, 15, '«Проклят, кто сделает изваянный или литой кумир, мерзость пред Господом, произведение рук художника, и поставит его в тайном месте!» Весь народ возгласит и скажет: «аминь».');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5599, 'rst66', 'deu', 27, 14, 'Левиты возгласят и скажут всем Израильтянам громким голосом:');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5598, 'rst66', 'deu', 27, 13, 'а сии должны стать на горе Гевал, чтобы <i>произносить</i> проклятие: Рувим, Гад, Асир, Завулон, Дан и Неффалим.');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5597, 'rst66', 'deu', 27, 12, 'сии должны стать на горе Гаризим, чтобы благословлять народ, когда перейдете Иордан: Симеон, Левий, Иуда, Иссахар, Иосиф и Вениамин;');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5596, 'rst66', 'deu', 27, 11, 'И заповедал Моисей народу в день тот, говоря:');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5595, 'rst66', 'deu', 27, 10, 'итак, слушай гласа Господа, Бога твоего, и исполняй заповеди Его и постановления Его, которые заповедую тебе сегодня.');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5594, 'rst66', 'deu', 27, 9, 'И сказал Моисей и священники левиты всему Израилю, говоря: внимай и слушай, Израиль: в день сей ты сделался народом Господа, Бога твоего;');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5593, 'rst66', 'deu', 27, 8, 'и напиши на камнях все слова закона сего очень явственно.');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5592, 'rst66', 'deu', 27, 7, 'и приноси жертвы мирные, и ешь там, и веселись пред Господом, Богом твоим;');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5591, 'rst66', 'deu', 27, 6, 'из камней цельных устрой жертвенник Господа, Бога твоего, и возноси на нем всесожжения Господу, Богу твоему,');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5590, 'rst66', 'deu', 27, 5, 'и устрой там жертвенник Господу, Богу твоему, жертвенник из камней, не поднимая на них железа;');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5589, 'rst66', 'deu', 27, 4, 'Когда перейдете Иордан, поставьте камни те, как я повелеваю вам сегодня, на горе Гевал, и обмажьте их известью;');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5588, 'rst66', 'deu', 27, 3, 'и напиши на <i>камнях</i> сих все слова закона сего, когда перейдешь <i>Иордан</i>, чтобы вступить в землю, которую Господь, Бог твой, дает тебе, в землю, где течет молоко и мед, как говорил тебе Господь, Бог отцов твоих.');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5587, 'rst66', 'deu', 27, 2, 'И когда перейдете за Иордан, в землю, которую Господь, Бог твой, дает тебе, тогда поставь себе большие камни и обмажь их известью;');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5586, 'rst66', 'deu', 27, 1, 'И заповедал Моисей и старейшины <i>сынов</i> Израилевых народу, говоря: исполняйте все заповеди, которые заповедую вам ныне.');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5585, 'rst66', 'deu', 26, 19, 'и что Он поставит тебя выше всех народов, которых Он сотворил, в чести, славе и великолепии, и что ты будешь святым народом у Господа, Бога твоего, как Он говорил.');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5584, 'rst66', 'deu', 26, 18, 'и Господь обещал тебе ныне, что ты будешь собственным Его народом, как Он говорил тебе, если ты будешь хранить все заповеди Его,');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5583, 'rst66', 'deu', 26, 17, 'Господу сказал ты ныне, что Он будет твоим Богом, и что ты будешь ходить путями Его и хранить постановления Его и заповеди Его и законы Его, и слушать гласа Его;');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5582, 'rst66', 'deu', 26, 16, 'В день сей Господь, Бог твой, завещевает тебе исполнять постановления сии и законы: соблюдай и исполняй их от всего сердца твоего и от всей души твоей.');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5581, 'rst66', 'deu', 26, 15, 'призри от святого жилища Твоего, с небес, и благослови народ Твой, Израиля, и землю, которую Ты дал нам &mdash; так как Ты клялся отцам нашим <i>дать нам</i> землю, в которой течет молоко и мед».');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5580, 'rst66', 'deu', 26, 14, 'я не ел от нее в печали моей, и не отделял ее в нечистоте, и не давал из нее для мертвого; я повиновался гласу Господа, Бога моего, исполнил все, что Ты заповедал мне;');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5579, 'rst66', 'deu', 26, 13, 'тогда скажи пред Господом, Богом твоим: «я отобрал от дома <i>моего</i> святыню и отдал ее левиту, пришельцу, сироте и вдове, по всем повелениям Твоим, которые Ты заповедал мне: я не преступил заповедей Твоих и не забыл;');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5578, 'rst66', 'deu', 26, 12, 'Когда ты отделишь все десятины произведений <i>земли</i> твоей в третий год, год десятин, и отдашь левиту, пришельцу, сироте и вдове, чтоб они ели в жилищах твоих и насыщались,');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5577, 'rst66', 'deu', 26, 11, 'и веселись о всех благах, которые Господь, Бог твой, дал тебе и дому твоему, ты и левит, и пришелец, который будет у тебя.');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5576, 'rst66', 'deu', 26, 10, 'итак, вот, я принес начатки плодов от земли, которую Ты, Господи, дал мне». И поставь это пред Господом, Богом твоим, и поклонись пред Господом, Богом твоим,');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5575, 'rst66', 'deu', 26, 9, 'и привел нас на место сие, и дал нам землю сию, землю, в которой течет молоко и мед;');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5574, 'rst66', 'deu', 26, 8, 'и вывел нас Господь из Египта рукою сильною и мышцею простертою, великим ужасом, знамениями и чудесами,');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5573, 'rst66', 'deu', 26, 7, 'и возопили мы к Господу Богу отцов наших, и услышал Господь вопль наш и увидел бедствие наше, труды наши и угнетение наше;');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5572, 'rst66', 'deu', 26, 6, 'но Египтяне худо поступали с нами, и притесняли нас, и налагали на нас тяжкие работы;');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5571, 'rst66', 'deu', 26, 5, 'Ты же отвечай и скажи пред Господом, Богом твоим: «отец мой был странствующий Арамеянин, и пошел в Египет и поселился там с немногими людьми, и произошел там от него народ великий, сильный и многочисленный;');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5570, 'rst66', 'deu', 26, 4, 'Священник возьмет корзину из руки твоей и поставит ее пред жертвенником Господа, Бога твоего.');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5569, 'rst66', 'deu', 26, 3, 'и приди к священнику, который будет в те дни, и скажи ему: «сегодня исповедую пред Господом, Богом твоим, что я вошел в ту землю, которую Господь клялся отцам нашим дать нам».');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5568, 'rst66', 'deu', 26, 2, 'то возьми начатков всех плодов земли, которые ты получишь от земли твоей, которую Господь, Бог твой, дает тебе, и положи в корзину, и пойди на то место, которое Господь, Бог твой, изберет, чтобы пребывало там имя Его;');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5567, 'rst66', 'deu', 26, 1, 'Когда ты придешь в землю, которую Господь, Бог твой, дает тебе в удел, и овладеешь ею, и поселишься в ней,');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5566, 'rst66', 'deu', 25, 19, 'итак, когда Господь, Бог твой, успокоит тебя от всех врагов твоих со всех сторон, на земле, которую Господь, Бог твой, дает тебе в удел, чтоб овладеть ею, изгладь память Амалика из поднебесной; не забудь.');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5565, 'rst66', 'deu', 25, 18, 'как он встретил тебя на пути, и побил сзади тебя всех ослабевших, когда ты устал и утомился, и не побоялся он Бога;');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5564, 'rst66', 'deu', 25, 17, 'Помни, как поступил с тобою Амалик на пути, когда вы шли из Египта:');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5563, 'rst66', 'deu', 25, 16, 'ибо мерзок пред Господом, Богом твоим, всякий, делающий неправду.');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5562, 'rst66', 'deu', 25, 15, 'гиря у тебя должна быть точная и правильная, и ефа у тебя должна быть точная и правильная, чтобы продлились дни твои на земле, которую Господь, Бог твой, дает тебе;');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5561, 'rst66', 'deu', 25, 14, 'в доме твоем не должна быть двоякая ефа, большая и меньшая;');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5560, 'rst66', 'deu', 25, 13, 'В кисе твоей не должны быть двоякие гири, большие и меньшие;');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5559, 'rst66', 'deu', 25, 12, 'то отсеки руку ее: да не пощадит <i>ее</i> глаз твой.');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5558, 'rst66', 'deu', 25, 11, 'Когда дерутся между собою мужчины и жена одного подойдет, чтобы отнять мужа своего из рук бьющего его, и, протянув руку свою, схватит его за срамный уд,');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5557, 'rst66', 'deu', 25, 10, 'и нарекут ему имя в Израиле: «дом разутого».');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5556, 'rst66', 'deu', 25, 9, '<i>тогда</i> невестка его пусть пойдет к нему в глазах старейшин, и снимет сапог его с ноги его, и плюнет в лицо его, и скажет: «так поступают с человеком, который не созидает дома брату своему»;');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5555, 'rst66', 'deu', 25, 8, 'тогда старейшины города его должны призвать его и уговаривать его, и если он станет и скажет: «не хочу взять ее»,');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5554, 'rst66', 'deu', 25, 7, 'Если же он не захочет взять невестку свою, то невестка его пойдет к воротам, к старейшинам, и скажет: «деверь мой отказывается восставить имя брата своего в Израиле, не хочет жениться на мне»;');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5553, 'rst66', 'deu', 25, 6, 'И первенец, которого она родит, останется с именем брата его умершего, чтобы имя его не изгладилось в Израиле.');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5552, 'rst66', 'deu', 25, 5, 'Если братья живут вместе и один из них умрет, не имея у себя сына, то жена умершего не должна выходить на сторону за человека чужого, но деверь ее должен войти к ней и взять ее себе в жену, и жить с нею.');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5551, 'rst66', 'deu', 25, 4, 'Не заграждай рта волу, когда он молотит.');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5550, 'rst66', 'deu', 25, 3, 'сорок ударов можно дать ему, а не более, чтобы от многих ударов брат твой не был обезображен пред глазами твоими.');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5549, 'rst66', 'deu', 25, 2, 'и если виновный достоин будет побоев, то судья пусть прикажет положить его и бить при себе, смотря по вине его, по счету;');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5548, 'rst66', 'deu', 25, 1, 'Если будет тяжба между людьми, то пусть приведут их в суд и рассудят их, правого пусть оправдают, а виновного осудят;');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5547, 'rst66', 'deu', 24, 22, 'и помни, что ты был рабом в земле Египетской: посему я и повелеваю тебе делать сие.');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5546, 'rst66', 'deu', 24, 21, 'Когда будешь снимать плоды в винограднике твоем, не собирай остатков за собою: пусть остается пришельцу, сироте и вдове;');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5545, 'rst66', 'deu', 24, 20, 'Когда будешь обивать маслину твою, то не пересматривай за собою ветвей: пусть остается пришельцу, сироте и вдове.');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5544, 'rst66', 'deu', 24, 19, 'Когда будешь жать на поле твоем, и забудешь сноп на поле, то не возвращайся взять его; пусть он остается пришельцу, сироте и вдове, чтобы Господь, Бог твой, благословил тебя во всех делах рук твоих.');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5543, 'rst66', 'deu', 24, 18, 'помни, что и ты был рабом в Египте, и Господь, Бог твой, освободил тебя оттуда: посему я и повелеваю тебе делать сие.');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5542, 'rst66', 'deu', 24, 17, 'Не суди превратно пришельца, сироту, и у вдовы не бери одежды в залог;');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5541, 'rst66', 'deu', 24, 16, 'Отцы не должны быть наказываемы смертью за детей, и дети не должны быть наказываемы смертью за отцов; каждый должен быть наказываем смертью за свое преступление.');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5540, 'rst66', 'deu', 24, 15, 'в тот же день отдай плату его, чтобы солнце не зашло прежде того, ибо он беден, и ждет ее душа его; чтоб он не возопил на тебя к Господу, и не было на тебе греха.');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5539, 'rst66', 'deu', 24, 14, 'Не обижай наемника, бедного и нищего, из братьев твоих или из пришельцев твоих, которые в земле твоей, в жилищах твоих;');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5538, 'rst66', 'deu', 24, 13, 'возврати ему залог при захождении солнца, чтоб он лег спать в одежде своей и благословил тебя, &mdash; и тебе поставится <i>сие</i> в праведность пред Господом, Богом твоим.');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5537, 'rst66', 'deu', 24, 12, 'если же он будет человек бедный, то ты не ложись спать, имея залог его:');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5536, 'rst66', 'deu', 24, 11, 'постой на улице, а тот, которому ты дал взаймы, вынесет тебе залог свой на улицу;');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5535, 'rst66', 'deu', 24, 10, 'Если ты ближнему твоему дашь что‐нибудь взаймы, то не ходи к нему в дом, чтобы взять у него залог,');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5534, 'rst66', 'deu', 24, 9, 'помни, что Господь, Бог твой, сделал Мариами на пути, когда вы шли из Египта.');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5533, 'rst66', 'deu', 24, 8, 'Смотри, в язве проказы тщательно соблюдай и исполняй весь <i>закон</i> которому научат вас священники левиты; тщательно исполняйте, что я повелел им;');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5532, 'rst66', 'deu', 24, 7, 'Если найдут кого, что он украл кого‐нибудь из братьев своих, из сынов Израилевых, и поработил его, и продал его, то такого вора должно предать смерти; так истреби зло из среды себя.');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5531, 'rst66', 'deu', 24, 6, 'Никто не должен брать в залог верхнего и нижнего жернова, ибо таковой берет в залог душу.');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5530, 'rst66', 'deu', 24, 5, 'Если кто взял жену недавно, то пусть не идет на войну, и ничего не должно возлагать на него; пусть он остается свободен в доме своем в продолжение одного года и увеселяет жену свою, которую взял.');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5529, 'rst66', 'deu', 24, 4, 'то не может первый ее муж, отпустивший ее, опять взять ее себе в жену, после того как она осквернена, ибо сие есть мерзость пред Господом, и не порочь земли, которую Господь, Бог твой, дает тебе в удел.');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5528, 'rst66', 'deu', 24, 3, 'но и сей последний муж возненавидит ее и напишет ей разводное письмо, и даст ей в руки, и отпустит ее из дома своего, или умрет сей последний муж ее, взявший ее себе в жену, &mdash;');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5527, 'rst66', 'deu', 24, 2, 'и она выйдет из дома его, пойдет, и выйдет за другого мужа,');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5526, 'rst66', 'deu', 24, 1, 'Если кто возьмет жену и сделается ее мужем, и она не найдет благоволения в глазах его, потому что он находит в ней что‐нибудь противное, и напишет ей разводное письмо, и даст ей в руки, и отпустит ее из дома своего,');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5525, 'rst66', 'deu', 23, 25, 'Когда придешь на жатву ближнего твоего, срывай колосья руками твоими, но серпа не заноси на жатву ближнего твоего.');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5524, 'rst66', 'deu', 23, 24, 'Когда войдешь в виноградник ближнего твоего, можешь есть ягоды досыта, сколько <i>хочет</i> душа твоя, а в сосуд твой не клади.');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5523, 'rst66', 'deu', 23, 23, 'Что вышло из уст твоих, соблюдай и исполняй так, как обещал ты Господу, Богу твоему, добровольное приношение, о котором сказал ты устами своими.');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5522, 'rst66', 'deu', 23, 22, 'если же ты не дал обета, то не будет на тебе греха.');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5521, 'rst66', 'deu', 23, 21, 'Если дашь обет Господу, Богу твоему, немедленно исполни его, ибо Господь, Бог твой, взыщет его с тебя, и на тебе будет грех;');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5520, 'rst66', 'deu', 23, 20, 'иноземцу отдавай в рост, а брату твоему не отдавай в рост, чтобы Господь, Бог твой, благословил тебя во всем, что делается руками твоими, на земле, в которую ты идешь, чтобы овладеть ею.');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5519, 'rst66', 'deu', 23, 19, 'Не отдавай в рост брату твоему ни серебра, ни хлеба, ни чего‐либо другого, что <i>можно</i> отдавать в рост;');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5518, 'rst66', 'deu', 23, 18, 'Не вноси платы блудницы и цены пса в дом Господа, Бога твоего, ни по какому обету, ибо то и другое есть мерзость пред Господом Богом твоим.');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5517, 'rst66', 'deu', 23, 17, 'Не должно быть блудницы из дочерей Израилевых и не должно быть блудника из сынов Израилевых.');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5516, 'rst66', 'deu', 23, 16, 'пусть он у тебя живет, среди вас, на месте, которое он изберет в каком‐нибудь из жилищ твоих, где ему понравится; не притесняй его.');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5515, 'rst66', 'deu', 23, 15, 'Не выдавай раба господину его, когда он прибежит к тебе от господина своего;');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5514, 'rst66', 'deu', 23, 14, 'ибо Господь, Бог твой, ходит среди стана твоего, чтобы избавлять тебя и предавать врагов твоих <i>в руки твои</i>, а <i>посему</i> стан твой должен быть свят, чтобы Он не увидел у тебя чего срамного и не отступил от тебя.');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5513, 'rst66', 'deu', 23, 13, 'кроме оружия твоего должна быть у тебя лопатка; и когда будешь садиться вне <i>стана</i>, выкопай ею <i>яму</i> и опять зарой <i>ею</i> испражнение твое;');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5512, 'rst66', 'deu', 23, 12, 'Место должно быть у тебя вне стана, куда бы тебе выходить;');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5511, 'rst66', 'deu', 23, 11, 'а при наступлении вечера должен омыть <i>тело свое</i> водою, и по захождении солнца может войти в стан.');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5510, 'rst66', 'deu', 23, 10, 'Если у тебя будет кто нечист от случившегося <i>ему</i> ночью, то он должен выйти вон из стана и не входить в стан,');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5509, 'rst66', 'deu', 23, 9, 'Когда пойдешь в поход против врагов твоих, берегись всего худого.');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5508, 'rst66', 'deu', 23, 8, 'дети, которые у них родятся, в третьем поколении могут войти в общество Господне.');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5507, 'rst66', 'deu', 23, 7, 'Не гнушайся Идумеянином, ибо он брат твой; не гнушайся Египтянином, ибо ты был пришельцем в земле его;');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5506, 'rst66', 'deu', 23, 6, 'Не желай им мира и благополучия во все дни твои, вовеки.');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5505, 'rst66', 'deu', 23, 5, 'но Господь, Бог твой, не восхотел слушать Валаама и обратил Господь, Бог твой, проклятие его в благословение тебе, ибо Господь, Бог твой, любит тебя.');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5504, 'rst66', 'deu', 23, 4, 'потому что они не встретили вас с хлебом и водою на пути, когда вы шли из Египта, и потому что они наняли против тебя Валаама, сына Веорова, из Пефора Месопотамского, чтобы проклясть тебя;');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5503, 'rst66', 'deu', 23, 3, 'Аммонитянин и Моавитянин не может войти в общество Господне, и десятое поколение их не может войти в общество Господне вовеки,');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5502, 'rst66', 'deu', 23, 2, 'Сын блудницы не может войти в общество Господне, и десятое поколение его не может войти в общество Господне.');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5501, 'rst66', 'deu', 23, 1, 'У кого раздавлены ятра или отрезан детородный член, тот не может войти в общество Господне.');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5500, 'rst66', 'deu', 22, 30, 'Никто не должен брать жены отца своего и открывать край <i>одежды</i> отца своего.');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5499, 'rst66', 'deu', 22, 29, 'то лежавший с нею должен дать отцу отроковицы пятьдесят <i>сиклей</i> серебра, а она пусть будет его женою, потому что он опорочил ее; во всю жизнь свою он не может развестись с нею.');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5498, 'rst66', 'deu', 22, 28, 'Если кто‐нибудь встретится с девицею необрученною, и схватит ее и ляжет с нею, и застанут их,');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5497, 'rst66', 'deu', 22, 27, 'ибо он встретился с нею в поле, и <i>хотя</i> отроковица обрученная кричала, но некому было спасти ее.');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5496, 'rst66', 'deu', 22, 26, 'а отроковице ничего не делай; на отроковице нет преступления смертного: ибо это то же, как если бы кто восстал на ближнего своего и убил его;');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5495, 'rst66', 'deu', 22, 25, 'Если же кто в поле встретится с отроковицею обрученною и, схватив ее, ляжет с нею, то должно предать смерти только мужчину, лежавшего с нею,');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5494, 'rst66', 'deu', 22, 24, 'то обоих их приведите к воротам того города, и побейте их камнями до смерти: отроковицу за то, что она не кричала в городе, а мужчину за то, что он опорочил жену ближнего своего; и <i>так</i> истреби зло из среды себя.');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5493, 'rst66', 'deu', 22, 23, 'Если будет молодая девица обручена мужу, и кто‐нибудь встретится с нею в городе и ляжет с нею,');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5492, 'rst66', 'deu', 22, 22, 'Если найден будет кто лежащий с женою замужнею, то должно предать смерти обоих: и мужчину, лежавшего с женщиною, и женщину; и <i>так</i> истреби зло от Израиля.');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5491, 'rst66', 'deu', 22, 21, 'то отроковицу пусть приведут к дверям дома отца ее, и жители города ее побьют ее камнями до смерти, ибо она сделала срамное дело среди Израиля, блудодействовав в доме отца своего; и <i>так</i> истреби зло из среды себя.');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5490, 'rst66', 'deu', 22, 20, 'Если же сказанное будет истинно, и не найдется девства у отроковицы,');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5489, 'rst66', 'deu', 22, 19, 'и наложат на него сто <i>сиклей</i> серебра пени и отдадут отцу отроковицы за то, что он пустил худую молву о девице Израильской; она же пусть останется его женою, и он не может развестись с нею во всю жизнь свою.');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5488, 'rst66', 'deu', 22, 18, 'Тогда старейшины того города пусть возьмут мужа и накажут его,');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5487, 'rst66', 'deu', 22, 17, 'и вот, он взводит <i>на нее</i> порочные дела, говоря: «я не нашел у дочери твоей девства»; но вот признаки девства дочери моей. И расстелют одежду пред старейшинами города.');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5486, 'rst66', 'deu', 22, 16, 'и отец отроковицы скажет старейшинам: дочь мою я отдал в жену сему человеку, и <i>ныне</i> он возненавидел ее,');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5485, 'rst66', 'deu', 22, 15, 'то отец отроковицы и мать ее пусть возьмут и вынесут <i>признаки</i> девства отроковицы к старейшинам города, к воротам;');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5484, 'rst66', 'deu', 22, 14, 'и будет возводить на нее порочные дела, и пустит о ней худую молву, и скажет: «я взял сию жену, и вошел к ней, и не нашел у нее девства»,');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5483, 'rst66', 'deu', 22, 13, 'Если кто возьмет жену, и войдет к ней, и возненавидит ее,');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5482, 'rst66', 'deu', 22, 12, 'Сделай себе кисточки на четырех углах покрывала твоего, которым ты покрываешься.');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5481, 'rst66', 'deu', 22, 11, 'Не надевай одежды, сделанной из разных веществ, из шерсти и льна вместе.');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5480, 'rst66', 'deu', 22, 10, 'Не паши на воле и осле вместе.');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5479, 'rst66', 'deu', 22, 9, 'Не засевай виноградника своего двумя родами семян, чтобы не сделать тебе заклятым сбора семян, которые ты посеешь вместе с плодами виноградника <i>своего</i>.');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5478, 'rst66', 'deu', 22, 8, 'Если будешь строить новый дом, то сделай перила около кровли твоей, чтобы не навести тебе крови на дом твой, когда кто‐нибудь упадет с него.');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5477, 'rst66', 'deu', 22, 7, 'мать пусти, а детей возьми себе, чтобы тебе было хорошо и чтобы продлились дни твои.');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5476, 'rst66', 'deu', 22, 6, 'Если попадется тебе на дороге птичье гнездо на каком‐либо дереве или на земле, с птенцами или яйцами, и мать сидит на птенцах или на яйцах, то не бери матери вместе с детьми:');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5475, 'rst66', 'deu', 22, 5, 'На женщине не должно быть мужской одежды, и мужчина не должен одеваться в женское платье, ибо мерзок пред Господом, Богом твоим, всякий, делающий сие.');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5474, 'rst66', 'deu', 22, 4, 'Когда увидишь осла брата твоего или вола его, упадших на пути, не оставляй их, но подними их с ним вместе.');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5473, 'rst66', 'deu', 22, 3, 'так поступай и с ослом его, так поступай с одеждой его, так поступай со всякою потерянною <i>вещью</i> брата твоего, которая будет им потеряна и которую ты найдешь; нельзя тебе уклоняться <i>от сего</i>.');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5472, 'rst66', 'deu', 22, 2, 'если же не близко будет к тебе брат твой, или ты не знаешь его, то прибери их в дом свой, и пусть они будут у тебя, доколе брат твой не будет искать их, и тогда возврати ему их;');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5471, 'rst66', 'deu', 22, 1, 'Когда увидишь вола брата твоего или овцу его, заблудившихся, не оставляй их, но возврати их брату твоему;');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5470, 'rst66', 'deu', 21, 23, 'то тело его не должно ночевать на дереве, но погреби его в тот же день, ибо проклят пред Богом <i>всякий</i> повешенный <i>на дереве</i>, и не оскверняй земли твоей, которую Господь, Бог твой, дает тебе в удел.');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5469, 'rst66', 'deu', 21, 22, 'Если в ком найдется преступление, достойное смерти, и он будет умерщвлен, и ты повесишь его на дереве,');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5468, 'rst66', 'deu', 21, 21, 'тогда все жители города его пусть побьют его камнями до смерти; и <i>так</i> истреби зло из среды себя, и все Израильтяне услышат и убоятся.');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5467, 'rst66', 'deu', 21, 20, 'и скажут старейшинам города своего: «сей сын наш буен и непокорен, не слушает слов наших, мот и пьяница»;');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5466, 'rst66', 'deu', 21, 19, 'то отец его и мать его пусть возьмут его и приведут его к старейшинам города своего и к воротам своего местопребывания');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5465, 'rst66', 'deu', 21, 18, 'Если у кого будет сын буйный и непокорный, не повинующийся голосу отца своего и голосу матери своей, и они наказывали его, но он не слушает их, &mdash;');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5464, 'rst66', 'deu', 21, 17, 'но первенцем должен признать сына нелюбимой <i>и</i> дать ему двойную часть из всего, что у него найдется, ибо он есть начаток силы его, ему <i>принадлежит</i> право первородства.');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5463, 'rst66', 'deu', 21, 16, 'то, при разделе сыновьям своим имения своего, он не может сыну жены любимой дать первенство пред первородным сыном нелюбимой;');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5462, 'rst66', 'deu', 21, 15, 'Если у кого будут две жены &mdash; одна любимая, а другая нелюбимая, и как любимая, <i>так</i> и нелюбимая родят ему сыновей, и первенцем будет сын нелюбимой, &mdash;');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5461, 'rst66', 'deu', 21, 14, 'если же она <i>после</i> не понравится тебе, то отпусти ее, <i>куда</i> она захочет, но не продавай ее за серебро и не обращай ее в рабство, потому что ты смирил ее.');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5460, 'rst66', 'deu', 21, 13, 'и снимет с себя пленническую одежду свою, и живет в доме твоем, и оплакивает отца своего и матерь свою в продолжение месяца; и после того ты можешь войти к ней и сделаться ее мужем, и она будет твоею женою;');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5459, 'rst66', 'deu', 21, 12, 'то приведи ее в дом свой, и пусть она острижет голову свою и обрежет ногти свои,');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5458, 'rst66', 'deu', 21, 11, 'и увидишь между пленными женщину, красивую видом, и полюбишь ее, и захочешь взять ее себе в жену,');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5457, 'rst66', 'deu', 21, 10, 'Когда выйдешь на войну против врагов твоих, и Господь, Бог твой, предаст их в руки твои, и возьмешь их в плен,');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5456, 'rst66', 'deu', 21, 9, '<i>Так</i> должен ты смывать у себя кровь невинного, если хочешь делать <i>доброе и</i> справедливое пред очами Господа.');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5455, 'rst66', 'deu', 21, 8, 'очисти народ Твой, Израиля, который Ты, Господи, освободил, и не вмени народу Твоему, Израилю, невинной крови». И они очистятся от крови.');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5454, 'rst66', 'deu', 21, 7, 'и объявят и скажут: «руки наши не пролили крови сей, и глаза наши не видели;');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5453, 'rst66', 'deu', 21, 6, 'и все старейшины города того, ближайшие к убитому, пусть омоют руки свои над <i>головою</i> телицы, зарезанной в долине,');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5452, 'rst66', 'deu', 21, 5, 'и придут священники, сыны Левиины (ибо их избрал Господь Бог твой служить Ему и благословлять именем Господа, и по слову их должно <i>решить</i> всякое спорное дело и всякий причиненный вред),');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5451, 'rst66', 'deu', 21, 4, 'и пусть старейшины того города отведут сию телицу в дикую долину, которая не разработана и не засеяна, и заколют там телицу в долине;');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5450, 'rst66', 'deu', 21, 3, 'и старейшины города того, который будет ближайшим к убитому, пусть возьмут телицу, на которой не работали <i>и</i> которая не носила ярма,');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5449, 'rst66', 'deu', 21, 2, 'то пусть выйдут старейшины твои и судьи твои и измерят <i>расстояние</i> до городов, которые вокруг убитого;');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5448, 'rst66', 'deu', 21, 1, 'Если в земле, которую Господь, Бог твой, дает тебе во владение, найден будет убитый, лежащий на поле, и неизвестно, кто убил его,');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5447, 'rst66', 'deu', 20, 20, 'только те дерева, о которых ты знаешь, что они ничего не приносят в пищу, можешь портить и рубить, и строить укрепление против города, который ведет с тобою войну, доколе не покоришь его.');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5446, 'rst66', 'deu', 20, 19, 'Если долгое время будешь держать в осаде <i>какой‐нибудь</i> город, чтобы завоевать его и взять его, то не порти дерев его, от которых можно питаться, и не опустошай окрестностей, ибо дерево на поле не человек, чтобы могло уйти от тебя в укрепление;');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5445, 'rst66', 'deu', 20, 18, 'дабы они не научили вас делать такие же мерзости, какие они делали для богов своих, и дабы вы не грешили пред Господом, Богом вашим.');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5444, 'rst66', 'deu', 20, 17, 'но предай их заклятию: Хеттеев и Аморреев, и Хананеев, и Ферезеев, и Евеев, и Иевусеев, как повелел тебе Господь, Бог твой,');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5443, 'rst66', 'deu', 20, 16, 'А в городах сих народов, которых Господь, Бог твой, дает тебе во владение, не оставляй в живых ни одной души,');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5442, 'rst66', 'deu', 20, 15, 'так поступай со всеми городами, которые от тебя весьма далеко, которые не из <i>числа</i> городов народов сих.');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5441, 'rst66', 'deu', 20, 14, 'только жен и детей, и скот, и все, что в городе, всю добычу его возьми себе и пользуйся добычею врагов твоих, которых предал тебе Господь, Бог твой;');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5440, 'rst66', 'deu', 20, 13, 'и <i>когда</i> Господь, Бог твой, предаст его в руки твои, порази в нем весь мужеский пол острием меча;');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5439, 'rst66', 'deu', 20, 12, 'если же он не согласится на мир с тобою и будет вести с тобою войну, то осади его,');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5438, 'rst66', 'deu', 20, 11, 'если он согласится на мир с тобою и отворит тебе <i>ворота</i>, то весь народ, который найдется в нем, будет платить тебе дань и служить тебе;');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5437, 'rst66', 'deu', 20, 10, 'Когда подойдешь к городу, чтобы завоевать его, предложи ему мир;');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5436, 'rst66', 'deu', 20, 9, 'Когда надзиратели скажут все это народу, тогда должно поставить военных начальников в вожди народу.');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5435, 'rst66', 'deu', 20, 8, 'И еще объявят надзиратели народу, и скажут: «кто боязлив и малодушен, тот пусть идет и возвратится в дом свой, дабы он не сделал робкими сердца братьев его, как его сердце».');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5434, 'rst66', 'deu', 20, 7, 'и кто обручился с женою и не взял ее, тот пусть идет и возвратится в дом свой, дабы не умер на сражении, и другой не взял ее».');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5433, 'rst66', 'deu', 20, 6, 'и кто насадил виноградник и не пользовался им, тот пусть идет и возвратится в дом свой, дабы не умер на сражении, и другой не воспользовался им;');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5432, 'rst66', 'deu', 20, 5, 'Надзиратели же пусть объявят народу, говоря: «кто построил новый дом и не обновил его, тот пусть идет и возвратится в дом свой, дабы не умер на сражении, и другой не обновил его;');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5431, 'rst66', 'deu', 20, 4, 'ибо Господь, Бог ваш, идет с вами, чтобы сразиться за вас с врагами вашими <i>и</i> спасти вас».');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5430, 'rst66', 'deu', 20, 3, 'и скажет ему: «слушай, Израиль! вы сегодня вступаете в сражение с врагами вашими, да не ослабеет сердце ваше, не бойтесь, не смущайтесь и не ужасайтесь их,');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5429, 'rst66', 'deu', 20, 2, 'Когда же приступаете к сражению, тогда пусть подойдет священник, и говорит народу,');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5428, 'rst66', 'deu', 20, 1, 'Когда ты выйдешь на войну против врага твоего и увидишь коней и колесницы <i>, и</i> народа более, нежели у тебя, то не бойся их, ибо с тобою Господь, Бог твой, Который вывел тебя из земли Египетской.');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5427, 'rst66', 'deu', 19, 21, 'да не пощадит <i>его</i> глаз твой: душу за душу, глаз за глаз, зуб за зуб, руку за руку, ногу за ногу.');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5426, 'rst66', 'deu', 19, 20, 'и прочие услышат, и убоятся, и не станут впредь делать такое зло среди тебя;');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5425, 'rst66', 'deu', 19, 19, 'то сделайте ему то, что он умышлял сделать брату своему; и <i>так</i> истреби зло из среды себя;');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5424, 'rst66', 'deu', 19, 18, 'судьи должны хорошо исследовать, и если свидетель тот свидетель ложный, ложно донес на брата своего,');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5423, 'rst66', 'deu', 19, 17, 'то пусть предстанут оба сии человека, у которых тяжба, пред Господа, пред священников и пред судей, которые будут в те дни;');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5422, 'rst66', 'deu', 19, 16, 'Если выступит против кого свидетель несправедливый, обвиняя его в преступлении,');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5421, 'rst66', 'deu', 19, 15, 'Недостаточно одного свидетеля против кого‐либо в какой‐нибудь вине и в каком‐нибудь преступлении и в каком‐нибудь грехе, которым он согрешит: при словах двух свидетелей, или при словах трех свидетелей состоится дело.');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5420, 'rst66', 'deu', 19, 14, 'Не нарушай межи ближнего твоего, которую положили предки в уделе твоем, доставшемся тебе в земле, которую Господь, Бог твой, дает тебе во владение.');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5419, 'rst66', 'deu', 19, 13, 'да не пощадит его глаз твой; смой с Израиля кровь невинного, и будет тебе хорошо.');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5418, 'rst66', 'deu', 19, 12, 'то старейшины города его должны послать, чтобы взять его оттуда и предать его в руки мстителя за кровь, чтоб он умер;');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5417, 'rst66', 'deu', 19, 11, 'Но если кто будет врагом ближнему своему и будет подстерегать его, и восстанет на него и убьет его до смерти, и убежит в один из городов тех,');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5416, 'rst66', 'deu', 19, 10, 'дабы не проливалась кровь невинного среди земли твоей, которую Господь, Бог твой, дает тебе в удел, и чтобы не было на тебе <i>вины</i> крови.');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5415, 'rst66', 'deu', 19, 9, 'если ты будешь стараться исполнять все сии заповеди, которые я заповедую тебе сегодня, любить Господа, Бога твоего, и ходить путями Его во все дни, &mdash; тогда к сим трем городам прибавь еще три города,');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5414, 'rst66', 'deu', 19, 8, 'Когда же Господь, Бог твой, распространит пределы твои, как Он клялся отцам твоим, и даст тебе всю землю, которую Он обещал дать отцам твоим,');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5413, 'rst66', 'deu', 19, 7, 'посему я и дал тебе повеление, говоря: отдели себе три города.');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5412, 'rst66', 'deu', 19, 6, 'дабы мститель за кровь в горячности сердца своего не погнался за убийцею и не настиг его, если далек будет путь, и не убил его, между тем как он не <i>подлежит</i> осуждению на смерть, ибо не был врагом ему вчера и третьего дня;');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5411, 'rst66', 'deu', 19, 5, 'кто пойдет с ближним своим в лес рубить дрова, и размахнется рука его с топором, чтобы срубить дерево, и соскочит железо с топорища и попадет в ближнего, и он умрет, &mdash; такой пусть убежит в один из городов тех, чтоб остаться живым,');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5410, 'rst66', 'deu', 19, 4, 'И вот какой убийца может убегать туда и остаться жив: кто убьет ближнего своего без намерения, не быв врагом ему вчера и третьего дня;');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5409, 'rst66', 'deu', 19, 3, 'устрой себе дорогу и раздели на три части всю землю твою, которую Господь, Бог твой, дает тебе в удел; они будут служить убежищем всякому убийце.');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5408, 'rst66', 'deu', 19, 2, 'тогда отдели себе три города среди земли твоей, которую Господь, Бог твой, дает тебе во владение;');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5407, 'rst66', 'deu', 19, 1, 'Когда Господь, Бог твой, истребит народы, которых землю дает тебе Господь, Бог твой, и ты вступишь в наследие после них, и поселишься в городах их и домах их,');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5406, 'rst66', 'deu', 18, 22, 'Если пророк скажет именем Господа, но слово то не сбудется и не исполнится, то не Господь говорил сие слово, но говорил сие пророк по дерзости своей, &mdash; не бойся его.');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5405, 'rst66', 'deu', 18, 21, 'И если скажешь в сердце твоем: «как мы узнаем слово, которое не Господь говорил?»');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5404, 'rst66', 'deu', 18, 20, 'но пророка, который дерзнет говорить Моим именем то, чего Я не повелел ему говорить, и который будет говорить именем богов иных, такого пророка предайте смерти.');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5403, 'rst66', 'deu', 18, 19, 'а кто не послушает слов Моих, которые <i>Пророк тот</i> будет говорить Моим именем, с того Я взыщу;');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5402, 'rst66', 'deu', 18, 18, 'Я воздвигну им Пророка из среды братьев их, такого, как ты, и вложу слова Мои в уста Его, и Он будет говорить им все, что Я повелю Ему;');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5401, 'rst66', 'deu', 18, 17, 'И сказал мне Господь: хорошо то, что они говорили;');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5400, 'rst66', 'deu', 18, 16, 'так как ты просил у Господа, Бога твоего, при Хориве в день собрания, говоря: «да не услышу впредь гласа Господа, Бога моего, и огня сего великого да не увижу более, дабы мне не умереть».');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5399, 'rst66', 'deu', 18, 15, 'Пророка из среды тебя, из братьев твоих, как меня, воздвигнет тебе Господь, Бог твой, &mdash; Его слушайте,');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5398, 'rst66', 'deu', 18, 14, 'ибо народы сии, которых ты изгоняешь, слушают гадателей и прорицателей, а тебе не то дал Господь, Бог твой.');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5397, 'rst66', 'deu', 18, 13, 'будь непорочен пред Господом, Богом твоим;');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5396, 'rst66', 'deu', 18, 12, 'ибо мерзок пред Господом всякий, делающий это, и за сии‐то мерзости Господь, Бог твой, изгоняет их от лица твоего;');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5395, 'rst66', 'deu', 18, 11, 'обаятель, вызывающий духов, волшебник и вопрошающий мертвых;');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5394, 'rst66', 'deu', 18, 10, 'не должен находиться у тебя проводящий сына своего или дочь свою чрез огонь, прорицатель, гадатель, ворожея, чародей,');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5393, 'rst66', 'deu', 18, 9, 'Когда ты войдешь в землю, которую дает тебе Господь, Бог твой, тогда не научись делать мерзости, какие делали народы сии:');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5392, 'rst66', 'deu', 18, 8, 'то пусть они пользуются одинаковою частью, сверх полученного от продажи отцовского <i>имущества</i>.');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5391, 'rst66', 'deu', 18, 7, 'и будет служить во имя Господа, Бога своего, как и все братья его левиты, предстоящие там пред Господом, &mdash;');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5390, 'rst66', 'deu', 18, 6, 'И если левит придет из одного из жилищ твоих, из всей <i>земли сынов</i> Израилевых, где он жил, и придет по желанию души своей на место, которое изберет Господь,');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5389, 'rst66', 'deu', 18, 5, 'ибо его избрал Господь, Бог твой, из всех колен твоих, чтобы он предстоял <i>пред Господом, Богом твоим</i>, служил во имя Господа, сам и сыны его во все дни.');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5388, 'rst66', 'deu', 18, 4, 'также начатки от хлеба твоего, вина твоего и елея твоего, и начатки от шерсти овец твоих отдавай ему,');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5387, 'rst66', 'deu', 18, 3, 'Вот что должно быть положено священникам от народа, от приносящих в жертву волов или овец: должно отдавать священнику плечо, челюсти и желудок;');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5386, 'rst66', 'deu', 18, 2, 'удела же не будет ему между братьями его: Сам Господь удел его, как говорил Он ему.');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5385, 'rst66', 'deu', 18, 1, 'Священникам левитам, всему колену Левиину, не будет части и удела с Израилем: они должны питаться жертвами Господа и Его частью;');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5384, 'rst66', 'deu', 17, 20, 'чтобы не надмевалось сердце его пред братьями его, и чтобы не уклонялся он от закона ни направо, ни налево, дабы долгие дни пребыл на царстве своем он и сыновья его посреди Израиля.');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5383, 'rst66', 'deu', 17, 19, 'и пусть он будет у него, и пусть он читает его во все дни жизни своей, дабы научался бояться Господа, Бога своего, и старался исполнять все слова закона сего и постановления сии;');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5382, 'rst66', 'deu', 17, 18, 'Но когда он сядет на престоле царства своего, должен списать для себя список закона сего с книги, <i>находящейся</i> у священников левитов,');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5381, 'rst66', 'deu', 17, 17, 'и чтобы не умножал себе жен, дабы не развратилось сердце его, и чтобы серебра и золота не умножал себе чрезмерно.');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5380, 'rst66', 'deu', 17, 16, 'Только чтоб он не умножал себе коней и не возвращал народа в Египет для умножения себе коней, ибо Господь сказал вам: «не возвращайтесь более путем сим»;');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5379, 'rst66', 'deu', 17, 15, 'то поставь над собою царя, которого изберет Господь, Бог твой; из среды братьев твоих поставь над собою царя; не можешь поставить над собою <i>царем</i> иноземца, который не брат тебе.');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5378, 'rst66', 'deu', 17, 14, 'Когда ты придешь в землю, которую Господь, Бог твой, дает тебе, и овладеешь ею, и поселишься на ней, и скажешь: «поставлю я над собою царя, подобно прочим народам, которые вокруг меня»,');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5377, 'rst66', 'deu', 17, 13, 'и весь народ услышит и убоится, и не будут впредь поступать дерзко.');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5376, 'rst66', 'deu', 17, 12, 'А кто поступит так дерзко, что не послушает священника, стоящего там на служении пред Господом, Богом твоим, или судьи, тот должен умереть, &mdash; и <i>так</i> истреби зло от Израиля;');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5375, 'rst66', 'deu', 17, 11, 'по закону, которому научат они тебя, и по определению, какое они скажут тебе, поступи, и не уклоняйся ни направо, ни налево от того, что они скажут тебе.');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5374, 'rst66', 'deu', 17, 10, 'и поступи по слову, какое они скажут тебе, на том месте, которое изберет Господь, и постарайся исполнить все, чему они научат тебя;');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5373, 'rst66', 'deu', 17, 9, 'и приди к священникам левитам и к судье, который будет в те дни, и спроси их, и они скажут тебе, как рассудить;');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5372, 'rst66', 'deu', 17, 8, 'Если по какому делу затруднительным будет для тебя рассудить между кровью и кровью, между судом и судом, между побоями и побоями, и <i>будут</i> несогласные мнения в воротах твоих, то встань и пойди на место, которое изберет Господь, Бог твой,');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5371, 'rst66', 'deu', 17, 7, 'рука свидетелей должна быть на нем прежде <i>всех</i>, чтоб убить его, потом рука всего народа; и <i>так</i> истреби зло из среды себя.');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5370, 'rst66', 'deu', 17, 6, 'По словам двух свидетелей или трех свидетелей, должен умереть осуждаемый на смерть: не должно предавать смерти по словам одного свидетеля;');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5369, 'rst66', 'deu', 17, 5, 'то выведи мужчину того, или женщину ту, которые сделали зло сие, к воротам твоим и побей их камнями до смерти.');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5368, 'rst66', 'deu', 17, 4, 'и тебе возвещено будет, и ты услышишь, то ты хорошо разыщи; и если это точная правда, если сделана мерзость сия в Израиле,');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5367, 'rst66', 'deu', 17, 3, 'и пойдет и станет служить иным богам, и поклонится им, или солнцу, или луне, или всему воинству небесному, чего я не повелел,');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5366, 'rst66', 'deu', 17, 2, 'Если найдется среди тебя в каком‐либо из жилищ твоих, которые Господь, Бог твой, дает тебе, мужчина или женщина, кто сделает зло пред очами Господа, Бога твоего, преступив завет Его,');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5365, 'rst66', 'deu', 17, 1, 'Не приноси в жертву Господу, Богу твоему, вола или овцы, на которой будет порок, <i>или</i> что‐нибудь худое, ибо это мерзость для Господа, Бога твоего.');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5364, 'rst66', 'deu', 16, 22, 'и не ставь себе столба, что ненавидит Господь, Бог твой.');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5363, 'rst66', 'deu', 16, 21, 'Не сади себе рощи из каких‐либо дерев при жертвеннике Господа, Бога твоего, который ты сделаешь себе,');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5362, 'rst66', 'deu', 16, 20, 'правды, правды ищи, дабы ты был жив и овладел землею, которую Господь, Бог твой, дает тебе.');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5361, 'rst66', 'deu', 16, 19, 'не извращай закона, не смотри на лица и не бери даров, ибо дары ослепляют глаза мудрых и превращают дело правых;');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5360, 'rst66', 'deu', 16, 18, 'Во всех жилищах твоих, которые Господь, Бог твой, даст тебе, поставь себе судей и надзирателей по коленам твоим, чтоб они судили народ судом праведным;');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5359, 'rst66', 'deu', 16, 17, 'но каждый с даром в руке своей, смотря по благословению Господа, Бога твоего, какое Он дал тебе.');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5358, 'rst66', 'deu', 16, 16, 'Три раза в году весь мужеский пол должен являться пред лицо Господа, Бога твоего, на место, которое изберет Он: в праздник опресноков, в праздник седмиц и в праздник кущей; и <i>никто</i> не должен являться пред лицо Господа с пустыми <i>руками</i>,');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5357, 'rst66', 'deu', 16, 15, 'семь дней празднуй Господу, Богу твоему, на месте, которое изберет Господь, Бог твой, ибо благословит тебя Господь, Бог твой, во всех произведениях твоих и во всяком деле рук твоих, и ты будешь только веселиться.');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5356, 'rst66', 'deu', 16, 14, 'и веселись в праздник твой ты и сын твой, и дочь твоя, и раб твой, и раба твоя, и левит, и пришелец, и сирота, и вдова, которые в жилищах твоих;');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5355, 'rst66', 'deu', 16, 13, 'Праздник кущей совершай у себя семь дней, когда уберешь с гумна твоего и из точила твоего;');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5354, 'rst66', 'deu', 16, 12, 'помни, что ты был рабом в Египте, и соблюдай и исполняй постановления сии.');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5353, 'rst66', 'deu', 16, 11, 'и веселись пред Господом, Богом твоим, ты, и сын твой, и дочь твоя, и раб твой, и раба твоя, и левит, который в жилищах твоих, и пришелец, и сирота, и вдова, которые среди тебя, на месте, которое изберет Господь, Бог твой, чтобы пребывало там имя Его;');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5352, 'rst66', 'deu', 16, 10, 'тогда совершай праздник седмиц Господу, Богу твоему, по усердию руки твоей, сколько ты дашь, смотря по тому, чем благословит тебя Господь, Бог твой;');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5351, 'rst66', 'deu', 16, 9, 'Семь седмиц отсчитай себе; начинай считать семь седмиц с того времени, как появится серп на жатве;');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5350, 'rst66', 'deu', 16, 8, 'Шесть дней ешь пресные хлебы, а в седьмой день отдание праздника Господу, Богу твоему; не занимайся работою.');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5349, 'rst66', 'deu', 16, 7, 'и испеки и съешь на том месте, которое изберет Господь, Бог твой, а на другой день можешь возвратиться и войти в шатры твои.');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5348, 'rst66', 'deu', 16, 6, 'но только на том месте, которое изберет Господь, Бог твой, чтобы пребывало там имя Его, закалай Пасху вечером при захождении солнца, в то самое время, в которое ты вышел из Египта;');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5347, 'rst66', 'deu', 16, 5, 'Не можешь ты закалать Пасху в котором‐нибудь из жилищ твоих, которые Господь, Бог твой, даст тебе;');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5346, 'rst66', 'deu', 16, 4, 'не должно находиться у тебя ничто квасное во всем уделе твоем в продолжение семи дней, и из мяса, которое ты принес в жертву вечером в первый день, ничто не должно оставаться до утра.');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5345, 'rst66', 'deu', 16, 3, 'Не ешь с нею квасного; семь дней ешь с нею опресноки, хлебы бедствия, ибо ты с поспешностью вышел из земли Египетской, дабы ты помнил день исшествия своего из земли Египетской во все дни жизни твоей;');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5344, 'rst66', 'deu', 16, 2, 'И закалай Пасху Господу, Богу твоему, из мелкого и крупного скота на месте, которое изберет Господь, чтобы пребывало там имя Его.');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5343, 'rst66', 'deu', 16, 1, 'Наблюдай месяц Авив и совершай Пасху Господу, Богу твоему, потому что в месяце Авиве вывел тебя Господь, Бог твой, из Египта ночью.');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5342, 'rst66', 'deu', 15, 23, 'только крови его не ешь: на землю выливай ее, как воду.');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5341, 'rst66', 'deu', 15, 22, 'но в жилищах твоих ешь его; нечистый, как и чистый, <i>могут есть,</i> как серну и как оленя;');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5340, 'rst66', 'deu', 15, 21, 'если же будет на нем порок, хромота, или слепота, <i>или</i> другой какой‐нибудь порок, то не приноси его в жертву Господу, Богу твоему,');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5339, 'rst66', 'deu', 15, 20, 'пред Господом, Богом твоим, каждогодно съедай это ты и семейство твое, на месте, которое изберет Господь;');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5338, 'rst66', 'deu', 15, 19, 'Все первородное мужеского пола, что родится от крупного скота твоего и от мелкого скота твоего, посвящай Господу, Богу твоему: не работай на первородном воле твоем и не стриги первородного из мелкого скота твоего;');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5337, 'rst66', 'deu', 15, 18, 'Не считай этого для себя тяжким, что ты должен отпустить его от себя на свободу, ибо он в шесть лет заработал тебе вдвое против платы наемника; и благословит тебя Господь, Бог твой, во всем, что ни будешь делать.');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5336, 'rst66', 'deu', 15, 17, 'то возьми шило и проколи ухо его к двери; и будет он рабом твоим навек. Так поступай и с рабою твоею.');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5335, 'rst66', 'deu', 15, 16, 'Если же он скажет тебе: «не пойду я от тебя, потому что я люблю тебя и дом твой», потому что хорошо ему у тебя,');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5334, 'rst66', 'deu', 15, 15, 'помни, что и ты был рабом в земле Египетской и избавил тебя Господь, Бог твой, потому я сегодня и заповедую тебе сие.');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5333, 'rst66', 'deu', 15, 14, 'но снабди его от стад твоих, от гумна твоего и от точила твоего: дай ему, чем благословил тебя Господь, Бог твой:');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5332, 'rst66', 'deu', 15, 13, 'когда же будешь отпускать его от себя на свободу, не отпусти его с пустыми <i>руками</i>,');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5331, 'rst66', 'deu', 15, 12, 'Если продастся тебе брат твой, Еврей или Евреянка, то шесть лет должен он быть рабом тебе, а в седьмой год отпусти его от себя на свободу;');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5330, 'rst66', 'deu', 15, 11, 'ибо нищие всегда будут среди земли <i>твоей</i>; потому я и повелеваю тебе: отверзай руку твою брату твоему, бедному твоему и нищему твоему на земле твоей.');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5329, 'rst66', 'deu', 15, 10, 'дай ему <i>взаймы</i>, и когда будешь давать ему, не должно скорбеть сердце твое, ибо за то благословит тебя Господь, Бог твой, во всех делах твоих и во всем, что будет делаться твоими руками;');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5328, 'rst66', 'deu', 15, 9, 'берегись, чтобы не вошла в сердце твое беззаконная мысль: «приближается седьмой год, год прощения», и чтоб <i>от того</i> глаз твой не сделался немилостив к нищему брату твоему, и ты не отказал ему; ибо он возопиет на тебя к Господу, и будет на тебе грех;');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5327, 'rst66', 'deu', 15, 8, 'но открой ему руку твою и дай ему взаймы, смотря по его нужде, в чем он нуждается;');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5326, 'rst66', 'deu', 15, 7, 'Если же будет у тебя нищий кто‐либо из братьев твоих, в одном из жилищ твоих, на земле твоей, которую Господь, Бог твой, дает тебе, то не ожесточи сердца твоего и не сожми руки твоей пред нищим братом твоим,');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5325, 'rst66', 'deu', 15, 6, 'ибо Господь, Бог твой, благословит тебя, как Он говорил тебе, и ты будешь давать взаймы многим народам, а сам не будешь брать взаймы; и господствовать будешь над многими народами, а они над тобою не будут господствовать.');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5324, 'rst66', 'deu', 15, 5, 'если только будешь слушать гласа Господа, Бога твоего, и стараться исполнять все заповеди сии, которые я сегодня заповедую тебе;');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5323, 'rst66', 'deu', 15, 4, 'Разве только не будет у тебя нищего: ибо благословит тебя Господь на той земле, которую Господь, Бог твой, дает тебе в удел, чтобы ты взял ее в наследство,');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5322, 'rst66', 'deu', 15, 3, 'с иноземца взыскивай, а что будет твое у брата твоего, прости.');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5321, 'rst66', 'deu', 15, 2, 'Прощение же состоит в том, чтобы всякий заимодавец, который дал взаймы ближнему своему, простил <i>долг</i> и не взыскивал с ближнего своего или с брата своего, ибо провозглашено прощение ради Господа;');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5320, 'rst66', 'deu', 15, 1, 'В седьмой год делай прощение.');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5319, 'rst66', 'deu', 14, 29, 'и пусть придет левит, ибо ему нет части и удела с тобою, и пришелец, и сирота, и вдова, которые <i>находятся</i> в жилищах твоих, и пусть едят и насыщаются, дабы благословил тебя Господь, Бог твой, во всяком деле рук твоих, которое ты будешь делать.');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5318, 'rst66', 'deu', 14, 28, 'По прошествии же трех лет отделяй все десятины произведений твоих в тот год и клади <i>сие</i> в жилищах твоих;');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5317, 'rst66', 'deu', 14, 27, 'И левита, который в жилищах твоих, не оставь, ибо нет ему части и удела с тобою.');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5316, 'rst66', 'deu', 14, 26, 'и покупай на серебро сие всего, чего пожелает душа твоя, волов, овец, вина, сикера и всего, чего потребует от тебя душа твоя; и ешь там пред Господом, Богом твоим, и веселись ты и семейство твое.');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5315, 'rst66', 'deu', 14, 25, 'то променяй это на серебро и возьми серебро в руку твою и приходи на место, которое изберет Господь, Бог твой;');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5314, 'rst66', 'deu', 14, 24, 'Если же длинна будет для тебя дорога, так что ты не можешь нести сего, потому что далеко от тебя то место, которое изберет Господь, Бог твой, чтоб положить там имя Свое, и Господь, Бог твой, благословил тебя,');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5313, 'rst66', 'deu', 14, 23, 'и ешь пред Господом, Богом твоим, на том месте, которое изберет Он, чтобы пребывать имени Его там, десятину хлеба твоего, вина твоего и елея твоего, и первенцев крупного скота твоего и мелкого скота твоего, дабы ты научился бояться Господа, Бога твоего, во все дни.');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5312, 'rst66', 'deu', 14, 22, 'Отделяй десятину от всего произведения семян твоих, которое приходит с поля <i>твоего</i> каждогодно,');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5311, 'rst66', 'deu', 14, 21, 'Не ешьте никакой мертвечины; иноземцу, который <i>случится</i> в жилищах твоих, отдай ее, он пусть ест ее, или продай ему, ибо ты народ святой у Господа, Бога твоего. Не вари козленка в молоке матери его.');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5310, 'rst66', 'deu', 14, 20, 'Всякую птицу чистую ешьте.');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5309, 'rst66', 'deu', 14, 19, 'Все крылатые пресмыкающиеся нечисты для вас, не ешьте <i>их</i>.');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5308, 'rst66', 'deu', 14, 18, 'и цапли, и зуя с породою его, и удода, и нетопыря.');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5307, 'rst66', 'deu', 14, 17, 'и пеликана, и сипа, и рыболова,');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5306, 'rst66', 'deu', 14, 16, 'и филина, и ибиса, и лебедя,');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5305, 'rst66', 'deu', 14, 15, 'и страуса, и совы, и чайки, и ястреба с породою его,');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5304, 'rst66', 'deu', 14, 14, 'и всякого ворона с породою его,');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5303, 'rst66', 'deu', 14, 13, 'и коршуна, и сокола, и кречета с породою их,');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5302, 'rst66', 'deu', 14, 12, 'но сих не должно вам есть из них: орла, грифа и морского орла,');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5301, 'rst66', 'deu', 14, 11, 'Всякую птицу чистую ешьте;');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5300, 'rst66', 'deu', 14, 10, 'а всех тех, у которых нет перьев и чешуи, не ешьте: нечисто это для вас.');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5299, 'rst66', 'deu', 14, 9, 'Из всех <i>животных</i>, которые в воде, ешьте всех, у которых есть перья и чешуя;');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5298, 'rst66', 'deu', 14, 8, 'и свиньи, потому что копыта у нее раздвоены, но не жует жвачки: нечиста она для вас; не ешьте мяса их, и к трупам их не прикасайтесь.');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5297, 'rst66', 'deu', 14, 7, 'только сих не ешьте из жующих жвачку и имеющих раздвоенные копыта с глубоким разрезом: верблюда, зайца и тушканчика, потому что, хотя они жуют жвачку, но копыта у них не раздвоены: нечисты они для вас;');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5296, 'rst66', 'deu', 14, 6, 'Всякий скот, у которого раздвоены копыта и на обоих копытах глубокий разрез, и который скот жует жвачку, тот ешьте;');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5295, 'rst66', 'deu', 14, 5, 'олень и серна, и буйвол, и лань, и зубр, и орикс, и камелопард.');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5294, 'rst66', 'deu', 14, 4, 'Вот скот, который вам можно есть: волы, овцы, козы,');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5293, 'rst66', 'deu', 14, 3, 'Не ешь никакой мерзости.');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5292, 'rst66', 'deu', 14, 2, 'ибо ты народ святой у Господа, Бога твоего, и тебя избрал Господь, чтобы ты был собственным Его народом из всех народов, которые на земле.');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5291, 'rst66', 'deu', 14, 1, 'Вы сыны Господа, Бога вашего; не делайте нарезов <i>на теле вашем</i> и не выстригайте волос над глазами вашими по умершем;');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5290, 'rst66', 'deu', 13, 18, 'если будешь слушать гласа Господа, Бога твоего, соблюдая все заповеди Его, которые ныне заповедую тебе, делая угодное пред очами Господа, Бога твоего.');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5289, 'rst66', 'deu', 13, 17, 'ничто из заклятого да не прилипнет к руке твоей, дабы укротил Господь ярость гнева Своего, и дал тебе милость и помиловал тебя, и размножил тебя, как клялся отцам твоим,');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5288, 'rst66', 'deu', 13, 16, 'всю же добычу его собери на средину площади его и сожги огнем город и всю добычу его во всесожжение Господу, Богу твоему, и да будет он вечно в развалинах, не должно никогда вновь созидать его;');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5287, 'rst66', 'deu', 13, 15, 'порази жителей того города острием меча, предай заклятию его и все, что в нем, и скот его <i>порази</i> острием меча;');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5286, 'rst66', 'deu', 13, 14, 'то ты разыщи, исследуй и хорошо расспроси; и если это точная правда, что случилась мерзость сия среди тебя,');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5285, 'rst66', 'deu', 13, 13, 'что появились в нем нечестивые люди из среды тебя и соблазнили жителей города их, говоря: «пойдем и будем служить богам иным, которых вы не знали», &mdash;');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5284, 'rst66', 'deu', 13, 12, 'Если услышишь о каком‐либо из городов твоих, которые Господь, Бог твой, дает тебе для жительства,');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5283, 'rst66', 'deu', 13, 11, 'весь Израиль услышит сие и убоится, и не станут впредь делать среди тебя такого зла.');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5282, 'rst66', 'deu', 13, 10, 'побей его камнями до смерти, ибо он покушался отвратить тебя от Господа, Бога твоего, Который вывел тебя из земли Египетской, из дома рабства;');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5281, 'rst66', 'deu', 13, 9, 'но убей его; твоя рука прежде <i>всех</i> должна быть на нем, чтоб убить его, а потом руки всего народа;');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5280, 'rst66', 'deu', 13, 8, 'то не соглашайся с ним и не слушай его; и да не пощадит его глаз твой, не жалей его и не прикрывай его,');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5279, 'rst66', 'deu', 13, 7, 'богам тех народов, которые вокруг тебя, близких к тебе или отдаленных от тебя, от одного края земли до другого,');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5278, 'rst66', 'deu', 13, 6, 'Если будет уговаривать тебя тайно брат твой, сын матери твоей, или сын твой, или дочь твоя, или жена на лоне твоем, или друг твой, который для тебя, как душа твоя, говоря: «пойдем и будем служить богам иным, которых не знал ты и отцы твои»,');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5277, 'rst66', 'deu', 13, 5, 'а пророка того или сновидца того должно предать смерти за то, что он уговаривал вас отступить от Господа, Бога вашего, выведшего вас из земли Египетской и избавившего тебя из дома рабства, желая совратить тебя с пути, по которому заповедал тебе идти Господь, Бог твой; и <i>так</i> истреби зло из среды себя.');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5276, 'rst66', 'deu', 13, 4, 'Господу, Богу вашему, последуйте и Его бойтесь, заповеди Его соблюдайте и гласа Его слушайте, и Ему служите, и к Нему прилепляйтесь;');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5275, 'rst66', 'deu', 13, 3, 'то не слушай слов пророка сего, или сновидца сего; ибо <i>чрез сие</i> искушает вас Господь, Бог ваш, чтобы узнать, любите ли вы Господа, Бога вашего, от всего сердца вашего и от всей души вашей;');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5274, 'rst66', 'deu', 13, 2, 'и сбудется то знамение или чудо, о котором он говорил тебе, и скажет притом: «пойдем вслед богов иных, которых ты не знаешь, и будем служить им», &mdash;');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5273, 'rst66', 'deu', 13, 1, 'Если восстанет среди тебя пророк, или сновидец, и представит тебе знамение или чудо,');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5272, 'rst66', 'deu', 12, 32, 'Все, что я заповедую вам, старайтесь исполнять; не прибавляй к тому и не убавляй от того.');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5271, 'rst66', 'deu', 12, 31, 'не делай так Господу, Богу твоему, ибо все, чего гнушается Господь, что ненавидит Он, они делают богам своим: они и сыновей своих и дочерей своих сожигают на огне богам своим.');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5270, 'rst66', 'deu', 12, 30, 'тогда берегись, чтобы ты не попал в сеть, последуя им, по истреблении их от лица твоего, и не искал богов их, говоря: «как служили народы сии богам своим, так буду и я делать»;');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5269, 'rst66', 'deu', 12, 29, 'Когда Господь, Бог твой, истребит от лица твоего народы, к которым ты идешь, чтобы взять их во владение, и ты, взяв их, поселишься в земле их,');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5268, 'rst66', 'deu', 12, 28, 'Слушай и исполняй все слова сии, которые заповедую тебе, дабы хорошо было тебе и детям твоим после тебя вовек, если будешь делать доброе и угодное пред очами Господа, Бога твоего.');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5267, 'rst66', 'deu', 12, 27, 'и совершай всесожжения твои, мясо и кровь, на жертвеннике Господа, Бога твоего; но кровь <i>других</i> жертв твоих должна быть проливаема у жертвенника Господа, Бога твоего, а мясо ешь.');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5266, 'rst66', 'deu', 12, 26, 'Только святыни твои, какие будут у тебя, и обеты твои приноси, и приходи на то место, которое изберет Господь;');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5265, 'rst66', 'deu', 12, 25, 'не ешь ее, дабы хорошо было тебе и детям твоим после тебя, если будешь делать справедливое пред очами Господа.');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5264, 'rst66', 'deu', 12, 24, 'не ешь ее: выливай ее на землю, как воду;');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5263, 'rst66', 'deu', 12, 23, 'только строго наблюдай, чтобы не есть крови, потому что кровь есть душа: не ешь души вместе с мясом;');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5262, 'rst66', 'deu', 12, 22, 'но ешь их так, как едят серну и оленя; нечистый, как и чистый, могут есть сие;');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5261, 'rst66', 'deu', 12, 21, 'Если далеко будет от тебя то место, которое изберет Господь, Бог твой, чтобы пребывать имени Его там, то закалай из крупного и мелкого скота твоего, который дал тебе Господь, как я повелел тебе, и ешь в жилищах твоих, по желанию души твоей;');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5260, 'rst66', 'deu', 12, 20, 'Когда распространит Господь, Бог твой, пределы твои, как Он говорил тебе, и ты скажешь: «поем я мяса», потому что душа твоя пожелает есть мяса, &mdash; тогда, по желанию души твоей, ешь мясо.');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5259, 'rst66', 'deu', 12, 19, 'Смотри, не оставляй левита во все дни, <i>которые будешь жить</i> на земле твоей.');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5258, 'rst66', 'deu', 12, 18, 'но ешь сие пред Господом, Богом твоим, на том месте, которое изберет Господь, Бог твой, &mdash; ты и сын твой, и дочь твоя, и раб твой, и раба твоя, и левит, и <i>пришелец</i>, который в жилищах твоих, и веселись пред Господом, Богом твоим, о всем, что делалось руками твоими.');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5257, 'rst66', 'deu', 12, 17, 'Нельзя тебе есть в жилищах твоих десятины хлеба твоего, и вина твоего, и елея твоего, и первенцев крупного скота твоего и мелкого скота твоего, и всех обетов твоих, которые ты обещал, и добровольных приношений твоих, и возношения рук твоих;');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5256, 'rst66', 'deu', 12, 16, 'только крови не ешьте: на землю выливайте ее, как воду.');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5255, 'rst66', 'deu', 12, 15, 'Впрочем, когда только пожелает душа твоя, можешь закалать и есть, по благословению Господа, Бога твоего, мясо, которое Он дал тебе, во всех жилищах твоих: нечистый и чистый могут есть сие, как серну и как оленя;');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5254, 'rst66', 'deu', 12, 14, 'но на том только месте, которое изберет Господь в одном из колен твоих, приноси всесожжения твои и делай все, что заповедую тебе.');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5253, 'rst66', 'deu', 12, 13, 'Берегись приносить всесожжения твои на всяком месте, которое ты увидишь;');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5252, 'rst66', 'deu', 12, 12, 'И веселитесь пред Господом, Богом вашим, вы и сыны ваши, и дочери ваши, и рабы ваши, и рабыни ваши, и левит, который посреди жилищ ваших, ибо нет ему части и удела с вами.');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5251, 'rst66', 'deu', 12, 11, 'тогда, какое место изберет Господь, Бог ваш, чтобы пребывать имени Его там, туда приносите всё, что я заповедую вам: всесожжения ваши и жертвы ваши, десятины ваши и возношение рук ваших, и все, избранное по обетам вашим, что вы обещали Господу.');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5250, 'rst66', 'deu', 12, 10, 'Но когда перейдете Иордан и поселитесь на земле, которую Господь, Бог ваш, дает вам в удел, и когда Он успокоит вас от всех врагов ваших, окружающих <i>вас</i>, и будете жить безопасно,');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5249, 'rst66', 'deu', 12, 9, 'ибо вы ныне еще не вступили в место покоя и в удел, который Господь, Бог твой, дает тебе.');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5248, 'rst66', 'deu', 12, 8, 'Там вы не должны делать всего, как мы теперь здесь делаем, каждый, что ему кажется правильным;');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5247, 'rst66', 'deu', 12, 7, 'и ешьте там пред Господом, Богом вашим, и веселитесь вы и семейства ваши о всем, что делалось руками вашими, чем благословил тебя Господь, Бог твой.');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5246, 'rst66', 'deu', 12, 6, 'и туда приносите всесожжения ваши, и жертвы ваши, и десятины ваши, и возношение рук ваших, и обеты ваши, и добровольные приношения ваши, и первенцев крупного скота вашего и мелкого скота вашего;');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5245, 'rst66', 'deu', 12, 5, 'но к месту, какое изберет Господь, Бог ваш, из всех колен ваших, чтобы пребывать имени Его там, обращайтесь и туда приходите,');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5244, 'rst66', 'deu', 12, 4, 'Не то должны вы делать для Господа, Бога вашего;');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5243, 'rst66', 'deu', 12, 3, 'и разрушьте жертвенники их, и сокрушите столбы их, и сожгите огнем рощи их, и разбейте истуканы богов их, и истребите имя их от места того.');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5242, 'rst66', 'deu', 12, 2, 'Истребите все места, где народы, которыми вы овладеете, служили богам своим, на высоких горах и на холмах, и под всяким ветвистым деревом;');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5241, 'rst66', 'deu', 12, 1, 'Вот постановления и законы, которые вы должны стараться исполнять в земле, которую Господь, Бог отцов твоих, дает тебе во владение, во все дни, которые вы будете жить на той земле.');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5240, 'rst66', 'deu', 11, 32, 'Итак, старайтесь соблюдать все постановления и законы <i>Его</i>, которые предлагаю я вам сегодня.');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5239, 'rst66', 'deu', 11, 31, 'Ибо вы переходите Иордан, чтобы пойти овладеть землею, которую Господь, Бог ваш, дает вам, и овладеете ею и будете жить на ней.');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5238, 'rst66', 'deu', 11, 30, 'вот они за Иорданом, по дороге к захождению солнца, в земле Хананеев, живущих на равнине, против Галгала, близ дубравы Море&#769;.');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5237, 'rst66', 'deu', 11, 29, 'Когда введет тебя Господь, Бог твой, в ту землю, в которую ты идешь, чтоб овладеть ею, тогда произнеси благословение на горе Гаризим, а проклятие на горе Гевал:');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5236, 'rst66', 'deu', 11, 28, 'а проклятие, если не послушаете заповедей Господа, Бога вашего, и уклонитесь от пути, который заповедую вам сегодня, и пойдете вслед богов иных, которых вы не знаете.');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5235, 'rst66', 'deu', 11, 27, 'благословение, если послушаете заповедей Господа, Бога вашего, которые я заповедую вам сегодня,');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5234, 'rst66', 'deu', 11, 26, 'Вот, я предлагаю вам сегодня благословение и проклятие:');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5233, 'rst66', 'deu', 11, 25, 'никто не устоит против вас: Господь, Бог ваш, наведет страх и трепет пред вами на всякую землю, на которую вы ступите, как Он говорил вам.');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5232, 'rst66', 'deu', 11, 24, 'всякое место, на которое ступит нога ваша, будет ваше; от пустыни и Ливана, от реки, реки Евфрата, даже до моря западного будут пределы ваши;');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5231, 'rst66', 'deu', 11, 23, 'то изгонит Господь все народы сии от лица вашего, и вы овладеете народами, которые больше и сильнее вас;');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5230, 'rst66', 'deu', 11, 22, 'Ибо если вы будете соблюдать все заповеди сии, которые заповедую вам исполнять, будете любить Господа, Бога вашего, ходить всеми путями Его и прилепляться к Нему,');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5229, 'rst66', 'deu', 11, 21, 'дабы столько же много было дней ваших и дней детей ваших на той земле, которую Господь клялся дать отцам вашим, сколько дней небо будет над землею.');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5228, 'rst66', 'deu', 11, 20, 'и напиши их на косяках дома твоего и на воротах твоих,');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5227, 'rst66', 'deu', 11, 19, 'и учите им сыновей своих, говоря о них, когда ты сидишь в доме твоем, и когда идешь дорогою, и когда ложишься, и когда встаешь;');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5226, 'rst66', 'deu', 11, 18, 'Итак, положите сии слова Мои в сердце ваше и в душу вашу, и навяжите их в знак на руку свою, и да будут они повязкою над глазами вашими;');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5225, 'rst66', 'deu', 11, 17, 'и тогда воспламенится гнев Господа на вас, и заключит Он небо, и не будет дождя, и земля не принесет произведений своих, и вы скоро погибнете с доброй земли, которую Господь дает вам.');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5224, 'rst66', 'deu', 11, 16, 'Берегитесь, чтобы не обольстилось сердце ваше, и вы не уклонились и не стали служить иным богам и не поклонились им;');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5223, 'rst66', 'deu', 11, 15, 'и дам траву на поле твоем для скота твоего, и будешь есть и насыщаться.');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5222, 'rst66', 'deu', 11, 14, 'то дам земле вашей дождь в свое время, ранний и поздний; и ты соберешь хлеб твой и вино твое, и елей твой;');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5221, 'rst66', 'deu', 11, 13, 'Если вы будете слушать заповеди Мои, которые заповедую вам сегодня, любить Господа, Бога вашего, и служить Ему от всего сердца вашего и от всей души вашей,');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5220, 'rst66', 'deu', 11, 12, 'земля, о которой Господь, Бог твой, печется: очи Господа, Бога твоего, непрестанно на ней, от начала года и до конца года.');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5219, 'rst66', 'deu', 11, 11, 'но земля, в которую вы переходите, чтоб овладеть ею, есть земля с горами и долинами, и от дождя небесного напояется водою,');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5218, 'rst66', 'deu', 11, 10, 'Ибо земля, в которую ты идешь, чтоб овладеть ею, не такова, как земля Египетская, из которой вышли вы, где ты, посеяв семя твое, поливал <i>ее</i> при помощи ног твоих, как масличный сад;');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5217, 'rst66', 'deu', 11, 9, 'и дабы вы жили много времени на той земле, которую клялся Господь отцам вашим дать, им и семени их, на земле, в которой течет молоко и мед.');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5216, 'rst66', 'deu', 11, 8, 'Итак, соблюдайте все заповеди <i>Его</i>, которые я заповедую вам сегодня, дабы вы укрепились и пошли и овладели землею, в которую вы переходите, чтоб овладеть ею;');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5215, 'rst66', 'deu', 11, 7, 'ибо глаза ваши видели все великие дела Господа, которые Он сделал.');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5214, 'rst66', 'deu', 11, 6, 'и что Он сделал с Дафаном и Авироном, сынами Елиава, сына Рувимова, когда земля разверзла уста свои и среди всего Израиля поглотила их и семейства их, и шатры их, и все имущество их, которое было у них;');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5213, 'rst66', 'deu', 11, 5, 'и что Он делал для вас в пустыне, доколе вы не дошли до места сего,');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5212, 'rst66', 'deu', 11, 4, 'и что Он сделал с войском Египетским, с конями его и колесницами его, которых Он потопил в водах Чермного моря, когда они гнались за вами, &mdash; и погубил их Господь даже до сего дня;');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5211, 'rst66', 'deu', 11, 3, 'знамения Его и дела Его, которые Он сделал среди Египта с фараоном, царем Египетским, и со всею землею его,');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5210, 'rst66', 'deu', 11, 2, 'И вспомните ныне, &mdash; ибо <i>я говорю</i> не с сынами вашими, которые не знают и не видели наказания Господа, Бога вашего, &mdash; Его величие, Его крепкую руку и высокую мышцу его,');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5209, 'rst66', 'deu', 11, 1, 'Итак, люби Господа, Бога твоего, и соблюдай, что повелено Им соблюдать, и постановления Его, и законы Его, и заповеди Его во все дни.');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5208, 'rst66', 'deu', 10, 22, 'в семидесяти душах пришли отцы твои в Египет, а ныне Господь, Бог твой, сделал тебя многочисленным, как звезды небесные.');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5207, 'rst66', 'deu', 10, 21, 'Он хвала твоя и Он Бог твой, Который сделал с тобою те великие и страшные <i>дела</i>, какие видели глаза твои;');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5206, 'rst66', 'deu', 10, 20, 'Господа, Бога твоего, бойся и Ему <i>одному</i> служи, и к Нему прилепись и Его именем клянись:');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5205, 'rst66', 'deu', 10, 19, 'Любите и вы пришельца, ибо <i>сами</i> были пришельцами в земле Египетской.');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5204, 'rst66', 'deu', 10, 18, 'Который дает суд сироте и вдове, и любит пришельца, и дает ему хлеб и одежду.');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5203, 'rst66', 'deu', 10, 17, 'ибо Господь, Бог ваш, есть Бог богов и Владыка владык, Бог великий, сильный и страшный, Который не смотрит на лица и не берет даров,');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5202, 'rst66', 'deu', 10, 16, 'Итак, обрежьте крайнюю плоть сердца вашего и не будьте впредь жестоковыйны;');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5201, 'rst66', 'deu', 10, 15, 'но только отцов твоих принял Господь и возлюбил их, и избрал вас, семя их после них, из всех народов, как ныне <i>видишь</i>.');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5200, 'rst66', 'deu', 10, 14, 'Вот, у Господа, Бога твоего, небо и небеса небес, земля и все, что на ней;');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5199, 'rst66', 'deu', 10, 13, 'чтобы соблюдал заповеди Господа и постановления Его, которые сегодня заповедую тебе, дабы тебе было хорошо.');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5198, 'rst66', 'deu', 10, 12, 'Итак, Израиль, чего требует от тебя Господь, Бог твой? Того только, чтобы ты боялся Господа, Бога твоего, ходил всеми путями Его, и любил Его, и служил Господу, Богу твоему, от всего сердца твоего и от всей души твоей,');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5197, 'rst66', 'deu', 10, 11, 'и сказал мне Господь: встань, пойди в путь пред народом <i>сим</i>; пусть они пойдут и овладеют землею, которую Я клялся отцам их дать им.');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5196, 'rst66', 'deu', 10, 10, 'И пробыл я на горе, как и в прежнее время, сорок дней и сорок ночей; и послушал меня Господь и на сей раз, и не восхотел Господь погубить тебя;');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5195, 'rst66', 'deu', 10, 9, 'потому нет левиту части и удела с братьями его: Сам Господь есть удел его, как говорил ему Господь, Бог твой.');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5194, 'rst66', 'deu', 10, 8, 'В то время отделил Господь колено Левиино, чтобы носить ковчег завета Господня, предстоять пред Господом, служить Ему и благословлять именем Его, <i>как это продолжается</i> до сего дня;');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5193, 'rst66', 'deu', 10, 7, 'Оттуда отправились в Гудгод, из Гудгода в Иотвафу, в землю, где потоки вод.');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5192, 'rst66', 'deu', 10, 6, 'И сыны Израилевы отправились из Беероф‐Бене‐Яакана в Мозер; там умер Аарон и погребен там, и стал священником вместо него сын его Елеазар.');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5191, 'rst66', 'deu', 10, 5, 'И обратился я, и сошел с горы, и положил скрижали в ковчег, который я сделал, чтоб они там были, как повелел мне Господь.');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5190, 'rst66', 'deu', 10, 4, 'И написал Он на скрижалях, как написано было прежде, те десять слов, которые изрек вам Господь на горе из среды огня в день собрания, и отдал их Господь мне.');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5189, 'rst66', 'deu', 10, 3, 'И сделал я ковчег из дерева ситтим, и вытесал две каменные скрижали, как прежние, и пошел на гору; и две сии скрижали <i>были</i> в руках моих.');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5188, 'rst66', 'deu', 10, 2, 'и Я напишу на скрижалях те слова, которые были на прежних скрижалях, которые ты разбил; и положи их в ковчег.');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5187, 'rst66', 'deu', 10, 1, 'В то время сказал мне Господь: вытеши себе две скрижали каменные, подобные первым, и взойди ко Мне на гору, и сделай себе деревянный ковчег;');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5186, 'rst66', 'deu', 9, 29, 'А они Твой народ и Твой удел, который Ты вывел <i>из Египта</i> силою Твоею великою и мышцею Твоею высокою.');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5185, 'rst66', 'deu', 9, 28, 'дабы <i>живущие</i> в той земле, откуда Ты вывел нас, не сказали: «Господь не мог ввести их в землю, которую обещал им, и, ненавидя их, вывел Он их, чтоб умертвить их в пустыне».');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5184, 'rst66', 'deu', 9, 27, 'вспомни рабов Твоих, Авраама, Исаака и Иакова, не смотри на ожесточение народа сего и на нечестие его и на грехи его,');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5183, 'rst66', 'deu', 9, 26, 'и молился я Господу и сказал: Владыка Господи, не погубляй народа Твоего и удела Твоего, который Ты избавил величием <i>крепости</i> Твоей, который вывел Ты из Египта рукою сильною;');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5182, 'rst66', 'deu', 9, 25, 'И повергшись пред Господом, умолял я сорок дней и сорок ночей, в которые я молился, ибо Господь хотел погубить вас;');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5181, 'rst66', 'deu', 9, 24, 'Вы были непокорны Господу с того самого дня, как я стал знать вас.');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5180, 'rst66', 'deu', 9, 23, 'И когда посылал вас Господь из Кадес‐Варни, говоря: «пойдите, овладейте землею, которую Я даю вам», &mdash; то вы воспротивились повелению Господа, Бога вашего, и не поверили Ему, и не послушали гласа Его.');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5179, 'rst66', 'deu', 9, 22, 'И в Тавере, в Массе и в Киброт‐Гаттааве вы раздражили Господа.');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5178, 'rst66', 'deu', 9, 21, 'Грех же ваш, который вы сделали, &mdash; тельца я взял, сожег его в огне, разбил его и всего истер до того, что он стал мелок, как прах, и я бросил прах сей в поток, текущий с горы.');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5177, 'rst66', 'deu', 9, 20, 'И на Аарона весьма прогневался Господь <i>и хотел</i> погубить его; но я молился и за Аарона в то время.');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5176, 'rst66', 'deu', 9, 19, 'ибо я страшился гнева и ярости, которыми Господь прогневался на вас <i>и хотел</i> погубить вас. И послушал меня Господь и на сей раз.');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5175, 'rst66', 'deu', 9, 18, 'И, повергшись пред Господом, молился я, как прежде, сорок дней и сорок ночей, хлеба не ел и воды не пил, за все грехи ваши, которыми вы согрешили, сделав зло в очах Господа и раздражив Его;');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5174, 'rst66', 'deu', 9, 17, 'и взял я обе скрижали, и бросил их из обеих рук своих, и разбил их пред глазами вашими.');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5173, 'rst66', 'deu', 9, 16, 'и видел я, что вы согрешили против Господа, Бога вашего, сделали себе литого тельца, скоро уклонились от пути, которого <i>держаться</i> заповедал вам Господь;');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5172, 'rst66', 'deu', 9, 15, 'Я обратился и пошел с горы, гора же горела огнем; две скрижали завета <i>были</i> в обеих руках моих;');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5171, 'rst66', 'deu', 9, 14, 'не удерживай Меня, и Я истреблю их, и изглажу имя их из поднебесной, а от тебя произведу народ, <i>который будет</i> сильнее и многочисленнее их.');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5170, 'rst66', 'deu', 9, 13, 'И сказал мне Господь: вижу Я народ сей, вот он народ жестоковыйный;');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5169, 'rst66', 'deu', 9, 12, 'и сказал мне Господь: встань, пойди скорее отсюда, ибо развратился народ твой, который ты вывел из Египта; скоро уклонились они от пути, который Я заповедал им; они сделали себе литой истукан.');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5168, 'rst66', 'deu', 9, 11, 'По окончании же сорока дней и сорока ночей дал мне Господь две скрижали каменные, скрижали завета,');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5167, 'rst66', 'deu', 9, 10, 'и дал мне Господь две скрижали каменные, написанные перстом Божиим, а на них все слова, которые изрек вам Господь на горе из среды огня в день собрания.');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5166, 'rst66', 'deu', 9, 9, 'когда я взошел на гору, чтобы принять скрижали каменные, скрижали завета, который поставил Господь с вами, и пробыл на горе сорок дней и сорок ночей, хлеба не ел и воды не пил,');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5165, 'rst66', 'deu', 9, 8, 'И при Хориве вы раздражили Господа, и прогневался на вас Господь, так что <i>хотел</i> истребить вас,');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5164, 'rst66', 'deu', 9, 7, 'Помни, не забудь, сколько ты раздражал Господа, Бога твоего, в пустыне: с самого того дня, как вышел ты из земли Египетской, и до самого прихода вашего на место сие вы противились Господу.');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5163, 'rst66', 'deu', 9, 6, 'посему знай, что не за праведность твою Господь, Бог твой, дает тебе овладеть сею доброю землею, ибо ты народ жестоковыйный.');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5162, 'rst66', 'deu', 9, 5, 'не за праведность твою и не за правоту сердца твоего идешь ты наследовать землю их, но за нечестие народов сих Господь, Бог твой, изгоняет их от лица твоего, и дабы исполнить слово, которым клялся Господь отцам твоим Аврааму, Исааку и Иакову;');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5161, 'rst66', 'deu', 9, 4, 'Когда будет изгонять их Господь, Бог твой, от лица твоего, не говори в сердце твоем, что за праведность мою привел меня Господь овладеть сею землею, и что за нечестие народов сих Господь изгоняет их от лица твоего;');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5160, 'rst66', 'deu', 9, 3, 'Знай же ныне, что Господь, Бог твой, идет пред тобою, <i>как</i> огонь поядающий; Он будет истреблять их и низлагать их пред тобою, и ты изгонишь их, и погубишь их скоро, как говорил тебе Господь.');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5159, 'rst66', 'deu', 9, 2, 'народом многочисленным и великорослым, сынами Енаковыми, о которых ты знаешь и слышал: «кто устоит против сынов Енаковых?»');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5158, 'rst66', 'deu', 9, 1, 'Слушай, Израиль: ты теперь идешь за Иордан, чтобы пойти овладеть народами, которые больше и сильнее тебя, городами большими, с укреплениями до небес,');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5157, 'rst66', 'deu', 8, 20, 'как народы, которые Господь истребляет от лица вашего, так погибнете <i>и вы</i> за то, что не послушаете гласа Господа, Бога вашего.');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5156, 'rst66', 'deu', 8, 19, 'Если же ты забудешь Господа, Бога твоего, и пойдешь вслед богов других, и будешь служить им и поклоняться им, то свидетельствуюсь вам сегодня, что вы погибнете;');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5155, 'rst66', 'deu', 8, 18, 'но чтобы помнил Господа, Бога твоего, ибо Он дает тебе силу приобретать богатство, дабы исполнить, как ныне, завет Свой, который Он клятвою утвердил отцам твоим.');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5154, 'rst66', 'deu', 8, 17, 'и чтобы ты не сказал в сердце твоем: «моя сила и крепость руки моей приобрели мне богатство сие»,');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5153, 'rst66', 'deu', 8, 16, 'питал тебя в пустыне манною, которой не знали отцы твои, дабы смирить тебя и испытать тебя, чтобы впоследствии сделать тебе добро,');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5152, 'rst66', 'deu', 8, 15, 'Который провел тебя по пустыне великой и страшной, <i>где</i> змеи, василиски, скорпионы и места сухие, на которых нет воды; Который источил для тебя <i>источник</i> воды из скалы гранитной,');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5151, 'rst66', 'deu', 8, 14, 'то смотри, чтобы не надмилось сердце твое и не забыл ты Господа, Бога твоего, Который вывел тебя из земли Египетской, из дома рабства;');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5150, 'rst66', 'deu', 8, 13, 'и когда будет у тебя много крупного и мелкого скота, и будет много серебра и золота, и всего у тебя будет много, &mdash;');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5149, 'rst66', 'deu', 8, 12, 'Когда будешь есть и насыщаться, и построишь хорошие дома и будешь жить <i>в них</i>');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5148, 'rst66', 'deu', 8, 11, 'Берегись, чтобы ты не забыл Господа, Бога твоего, не соблюдая заповедей Его, и законов Его, и постановлений Его, которые сегодня заповедую тебе.');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5147, 'rst66', 'deu', 8, 10, 'И когда будешь есть и насыщаться, тогда благословляй Господа, Бога твоего, за добрую землю, которую Он дал тебе.');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5146, 'rst66', 'deu', 8, 9, 'в землю, в которой без скудости будешь есть хлеб твой и ни в чем не будешь иметь недостатка, в землю, в которой камни &mdash; железо, и из гор которой будешь высекать медь.');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5145, 'rst66', 'deu', 8, 8, 'в землю, <i>где</i> пшеница, ячмень, виноградные лозы, смоковницы и гранатовые деревья, в землю, <i>где</i> масличные деревья и мед,');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5144, 'rst66', 'deu', 8, 7, 'Ибо Господь, Бог твой, ведет тебя в землю добрую, в землю, <i>где</i> потоки вод, источники и озера выходят из долин и гор,');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5143, 'rst66', 'deu', 8, 6, 'Итак, храни заповеди Господа, Бога твоего, ходя путями Его и боясь Его.');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5142, 'rst66', 'deu', 8, 5, 'И знай в сердце твоем, что Господь, Бог твой, учит тебя, как человек учит сына своего.');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5141, 'rst66', 'deu', 8, 4, 'одежда твоя не ветшала на тебе, и нога твоя не пухла вот уже сорок лет.');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5140, 'rst66', 'deu', 8, 3, 'Он смирял тебя, томил тебя голодом и питал тебя манною, которой не знал ты и не знали отцы твои, дабы показать тебе, что не одним хлебом живет человек, но всяким <i>словом</i>, исходящим из уст Господа, живет человек;');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5139, 'rst66', 'deu', 8, 2, 'И помни весь путь, которым вел тебя Господь, Бог твой, по пустыне вот уже сорок лет, чтобы смирить тебя, чтобы испытать тебя и узнать, что в сердце твоем, будешь ли хранить заповеди Его или нет;');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5138, 'rst66', 'deu', 8, 1, 'Все заповеди, которые я заповедую вам сегодня, старайтесь исполнять, дабы вы были живы и размножились, и пошли, и завладели землею, которую с клятвою обещал Господь отцам вашим.');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5137, 'rst66', 'deu', 7, 26, 'и не вноси мерзости в дом твой, дабы не подпасть заклятию, как она; отвращайся сего и гнушайся сего, ибо это заклятое.');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5136, 'rst66', 'deu', 7, 25, 'Кумиры богов их сожгите огнем; не пожелай взять себе серебра или золота, которое на них, дабы это не было для тебя сетью, ибо это мерзость для Господа, Бога твоего;');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5135, 'rst66', 'deu', 7, 24, 'и предаст царей их в руки твои, и ты истребишь имя их из поднебесной: не устоит никто против тебя, доколе не искоренишь их.');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5134, 'rst66', 'deu', 7, 23, 'но предаст их тебе Господь, Бог твой, и приведет их в великое смятение, так что они погибнут;');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5133, 'rst66', 'deu', 7, 22, 'И будет Господь, Бог твой, изгонять пред тобою народы сии мало‐помалу; не можешь ты истребить их скоро, чтобы не умножились против тебя полевые звери;');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5132, 'rst66', 'deu', 7, 21, 'не страшись их, ибо Господь, Бог твой, среди тебя, Бог великий и страшный.');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5131, 'rst66', 'deu', 7, 20, 'и шершней нашлет Господь, Бог твой, на них, доколе не погибнут оставшиеся и скрывшиеся от лица твоего;');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5130, 'rst66', 'deu', 7, 19, 'те великие испытания, которые видели глаза твои, знамения, чудеса, и руку крепкую, и мышцу высокую, с какими вывел тебя Господь, Бог твой; то же сделает Господь, Бог твой, со всеми народами, которых ты боишься;');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5129, 'rst66', 'deu', 7, 18, 'Не бойся их, вспомни то, что сделал Господь, Бог твой, с фараоном и всем Египтом,');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5128, 'rst66', 'deu', 7, 17, 'Если скажешь в сердце твоем: «народы сии многочисленнее меня; как я могу изгнать их?»');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5127, 'rst66', 'deu', 7, 16, 'и истребишь все народы, которые Господь, Бог твой, дает тебе: да не пощадит их глаз твой; и не служи богам их, ибо это сеть для тебя.');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5126, 'rst66', 'deu', 7, 15, 'и отдалит от тебя Господь всякую немощь, и никаких лютых болезней Египетских, которые ты знаешь, не наведет на тебя, но наведет их на всех, ненавидящих тебя;');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5125, 'rst66', 'deu', 7, 14, 'благословен ты будешь больше всех народов; не будет ни бесплодного, ни бесплодной, ни у тебя, ни в скоте твоем;');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5124, 'rst66', 'deu', 7, 13, 'и возлюбит тебя, и благословит тебя, и размножит тебя, и благословит плод чрева твоего и плод земли твоей, и хлеб твой, и вино твое, и елей твой, рождаемое от крупного скота твоего и от стада овец твоих, на той земле, которую Он клялся отцам твоим дать тебе;');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5123, 'rst66', 'deu', 7, 12, 'И если вы будете слушать законы сии, и хранить, и исполнять их, то Господь, Бог твой, будет хранить завет и милость к тебе, как Он клялся отцам твоим,');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5122, 'rst66', 'deu', 7, 11, 'Итак, соблюдай заповеди и постановления и законы, которые сегодня заповедую тебе исполнять.');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5121, 'rst66', 'deu', 7, 10, 'и воздает ненавидящим Его в лицо их, погубляя их; Он не замедлит, ненавидящему Его самому лично воздаст.');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5120, 'rst66', 'deu', 7, 9, 'Итак, знай, что Господь, Бог твой, есть Бог, Бог верный, Который хранит завет <i>Свой</i> и милость к любящим Его и сохраняющим заповеди Его до тысячи родов,');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5119, 'rst66', 'deu', 7, 8, 'но потому, что любит вас Господь, и для того, чтобы сохранить клятву, которою Он клялся отцам вашим, вывел вас Господь рукою крепкою и освободил тебя из дома рабства, из руки фараона, царя Египетского.');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5118, 'rst66', 'deu', 7, 7, 'Не потому, чтобы вы были многочисленнее всех народов, принял вас Господь и избрал вас, ибо вы малочисленнее всех народов,');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5117, 'rst66', 'deu', 7, 6, 'ибо ты народ святой у Господа, Бога твоего: тебя избрал Господь, Бог твой, чтобы ты был собственным Его народом из всех народов, которые на земле.');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5116, 'rst66', 'deu', 7, 5, 'Но поступите с ними так: жертвенники их разрушьте, столбы их сокрушите, и рощи их вырубите, и истуканов их сожгите огнем;');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5115, 'rst66', 'deu', 7, 4, 'ибо они отвратят сынов твоих от Меня, чтобы служить иным богам, и <i>тогда</i> воспламенится на вас гнев Господа, и Он скоро истребит тебя.');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5114, 'rst66', 'deu', 7, 3, 'и не вступай с ними в родство: дочери твоей не отдавай за сына его, и дочери его не бери за сына твоего;');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5113, 'rst66', 'deu', 7, 2, 'и предаст их тебе Господь, Бог твой, и поразишь их, тогда предай их заклятию, не вступай с ними в союз и не щади их;');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5112, 'rst66', 'deu', 7, 1, 'Когда введет тебя Господь, Бог твой, в землю, в которую ты идешь, чтоб овладеть ею, и изгонит от лица твоего многочисленные народы, Хеттеев, Гергесеев, Аморреев, Хананеев, Ферезеев, Евеев и Иевусеев, семь народов, которые многочисленнее и сильнее тебя,');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5111, 'rst66', 'deu', 6, 25, 'и в сем будет наша праведность, если мы будем стараться исполнять все сии заповеди пред лицом Господа, Бога нашего, как Он заповедал нам».');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5110, 'rst66', 'deu', 6, 24, 'и заповедал нам Господь исполнять все постановления сии, чтобы мы боялись Господа, Бога нашего, дабы хорошо было нам во все дни, дабы сохранить нашу жизнь, как и теперь;');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5109, 'rst66', 'deu', 6, 23, 'а нас вывел оттуда, чтобы ввести нас и дать нам землю, которую клялся отцам нашим <i>дать нам</i>;');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5108, 'rst66', 'deu', 6, 22, 'и явил Господь знамения и чудеса великие и казни над Египтом, над фараоном и над всем домом его пред глазами нашими;');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5107, 'rst66', 'deu', 6, 21, 'То скажи сыну твоему: «рабами были мы у фараона в Египте, но Господь вывел нас из Египта рукою крепкою,');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5106, 'rst66', 'deu', 6, 20, 'Если спросит у тебя сын твой в последующее время, говоря: «что <i>значат</i> сии уставы, постановления и законы, которые заповедал вам Господь, Бог ваш?»');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5105, 'rst66', 'deu', 6, 19, 'и чтобы Он прогнал всех врагов твоих от лица твоего, как говорил Господь.');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5104, 'rst66', 'deu', 6, 18, 'и делай справедливое и доброе пред очами Господа, дабы хорошо тебе было, и дабы ты вошел и овладел доброю землею, которую Господь с клятвою обещал отцам твоим,');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5103, 'rst66', 'deu', 6, 17, 'Твердо храните заповеди Господа, Бога вашего, и уставы Его, и постановления, которые Он заповедал тебе;');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5102, 'rst66', 'deu', 6, 16, 'Не искушайте Господа, Бога вашего, как вы искушали Его в Массе.');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5101, 'rst66', 'deu', 6, 15, 'ибо Господь, Бог твой, Который среди тебя, есть Бог ревнитель; чтобы не воспламенился гнев Господа, Бога твоего, на тебя, и не истребил Он тебя с лица земли.');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5100, 'rst66', 'deu', 6, 14, 'Не последуйте иным богам, богам тех народов, которые будут вокруг вас;');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5099, 'rst66', 'deu', 6, 13, 'Господа, Бога твоего, бойся, и Ему <i>одному</i> служи, и Его именем клянись.');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5098, 'rst66', 'deu', 6, 12, 'тогда берегись, чтобы не забыл ты Господа, Который вывел тебя из земли Египетской, из дома рабства.');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5097, 'rst66', 'deu', 6, 11, 'и с домами, наполненными всяким добром, которых ты не наполнял, и с колодезями, высеченными <i>из камня</i>, которых ты не высекал, с виноградниками и маслинами, которых ты не садил, и будешь есть и насыщаться,');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5096, 'rst66', 'deu', 6, 10, 'Когда же введет тебя Господь, Бог твой, в ту землю, которую Он клялся отцам твоим, Аврааму, Исааку и Иакову, дать тебе с большими и хорошими городами, которых ты не строил,');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5095, 'rst66', 'deu', 6, 9, 'и напиши их на косяках дома твоего и на воротах твоих.');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5094, 'rst66', 'deu', 6, 8, 'и навяжи их в знак на руку твою, и да будут они повязкою над глазами твоими,');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5093, 'rst66', 'deu', 6, 7, 'и внушай их детям твоим, и говори о них, сидя в доме твоем и идя дорогою, и ложась, и вставая;');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5092, 'rst66', 'deu', 6, 6, 'И да будут слова сии, которые Я заповедую тебе сегодня, в сердце твоем;');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5091, 'rst66', 'deu', 6, 5, 'и люби Господа, Бога твоего, всем сердцем твоим, и всею душою твоею, и всеми силами твоими.');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5090, 'rst66', 'deu', 6, 4, 'Слушай, Израиль: Господь, Бог наш, Господь един есть;');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5089, 'rst66', 'deu', 6, 3, 'Итак, слушай, Израиль, и старайся исполнить это, чтобы тебе хорошо было, и чтобы вы весьма размножились, как Господь, Бог отцов твоих, говорил тебе, <i>что Он даст тебе</i> землю, где течет молоко и мед.');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5088, 'rst66', 'deu', 6, 2, 'дабы ты боялся Господа, Бога твоего, и все постановления Его и заповеди Его, которые заповедую тебе, соблюдал ты и сыны твои и сыны сынов твоих во все дни жизни твоей, дабы продлились дни твои.');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5087, 'rst66', 'deu', 6, 1, 'Вот заповеди, постановления и законы, которым повелел Господь, Бог ваш, научить вас, чтобы вы поступали <i>так</i> в той земле, в которую вы идете, чтоб овладеть ею;');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5086, 'rst66', 'deu', 5, 33, 'ходите по тому пути, по которому повелел вам Господь, Бог ваш, дабы вы были живы, и хорошо было вам, и прожили много времени на той земле, которую получите во владение.');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5085, 'rst66', 'deu', 5, 32, 'Смотрите, поступайте так, как повелел вам Господь, Бог ваш; не уклоняйтесь ни направо, ни налево;');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5084, 'rst66', 'deu', 5, 31, 'а ты здесь останься со Мною, и Я изреку тебе все заповеди и постановления и законы, которым ты должен научить их, чтобы они <i>так</i> поступали на той земле, которую Я даю им во владение.');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5083, 'rst66', 'deu', 5, 30, 'Пойди, скажи им: «возвратитесь в шатры свои»;');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5082, 'rst66', 'deu', 5, 29, 'о, если бы сердце их было у них таково, чтобы бояться Меня и соблюдать все заповеди Мои во все дни, дабы хорошо было им и сынам их вовек!');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5081, 'rst66', 'deu', 5, 28, 'И Господь услышал слова ваши, как вы разговаривали со мною, и сказал мне Господь: слышал Я слова народа сего, которые они говорили тебе; все, что ни говорили они, хорошо;');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5080, 'rst66', 'deu', 5, 27, 'Приступи ты и слушай все, что скажет <i>тебе</i> Господь, Бог наш, и пересказывай нам все, что будет говорить тебе Господь, Бог наш, и мы будем слушать и исполнять.');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5079, 'rst66', 'deu', 5, 26, 'ибо есть ли какая плоть, которая слышала бы глас Бога живого, говорящего из среды огня, как мы, и осталась жива?');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5078, 'rst66', 'deu', 5, 25, 'но теперь для чего нам умирать? ибо великий огонь сей пожрет нас; если мы еще услышим глас Господа, Бога нашего, то умрем,');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5077, 'rst66', 'deu', 5, 24, 'и сказали: вот, показал нам Господь, Бог наш, славу Свою и величие Свое, и глас Его слышали мы из среды огня; сегодня видели мы, что Бог говорит с человеком, и сей остается жив;');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5076, 'rst66', 'deu', 5, 23, 'И когда вы услышали глас из среды мрака, и гора горела огнем, то вы подошли ко мне, все начальники колен ваших и старейшины ваши,');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5075, 'rst66', 'deu', 5, 22, 'Слова сии изрек Господь ко всему собранию вашему на горе из среды огня, облака и мрака громогласно, и более не говорил, и написал их на двух каменных скрижалях, и дал их мне.');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5074, 'rst66', 'deu', 5, 21, 'Не желай жены ближнего твоего и не желай дома ближнего твоего, ни поля его, ни раба его, ни рабы его, ни вола его, ни осла его, ни всего, что есть у ближнего твоего.');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5073, 'rst66', 'deu', 5, 20, 'Не произноси ложного свидетельства на ближнего твоего.');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5072, 'rst66', 'deu', 5, 19, 'Не кради.');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5071, 'rst66', 'deu', 5, 18, 'Не прелюбодействуй.');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5070, 'rst66', 'deu', 5, 17, 'Не убивай.');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5069, 'rst66', 'deu', 5, 16, 'Почитай отца твоего и матерь твою, как повелел тебе Господь, Бог твой, чтобы продлились дни твои, и чтобы хорошо тебе было на той земле, которую Господь, Бог твой, дает тебе.');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5068, 'rst66', 'deu', 5, 15, 'и помни, что <i>ты</i> был рабом в земле Египетской, но Господь, Бог твой, вывел тебя оттуда рукою крепкою и мышцею высокою, потому и повелел тебе Господь, Бог твой, соблюдать день субботний.');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5067, 'rst66', 'deu', 5, 14, 'а день седьмой &mdash; суббота Господу, Богу твоему. Не делай <i>в оный</i> никакого дела, ни ты, ни сын твой, ни дочь твоя, ни раб твой, ни раба твоя, ни вол твой, ни осел твой, ни всякий скот твой, ни пришелец твой, который у тебя, чтобы отдохнул раб твой, и раба твоя как и ты;');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5066, 'rst66', 'deu', 5, 13, 'шесть дней работай и делай всякие дела твои,');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5065, 'rst66', 'deu', 5, 12, 'Наблюдай день субботний, чтобы свято хранить его, как заповедал тебе Господь, Бог твой;');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5064, 'rst66', 'deu', 5, 11, 'Не произноси имени Господа, Бога твоего, напрасно; ибо не оставит Господь без наказания того, кто употребляет имя Его напрасно.');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5063, 'rst66', 'deu', 5, 10, 'и творящий милость до тысячи <i>родов</i> любящим Меня и соблюдающим заповеди Мои.');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5062, 'rst66', 'deu', 5, 9, 'не поклоняйся им и не служи им; ибо Я Господь, Бог твой, Бог ревнитель, за вину отцов наказывающий детей до третьего и четвертого рода, ненавидящих Меня,');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5061, 'rst66', 'deu', 5, 8, 'Не делай себе кумира и никакого изображения того, что на небе вверху и что на земле внизу, и что в водах ниже земли,');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5060, 'rst66', 'deu', 5, 7, 'Да не будет у тебя других богов перед лицом Моим.');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5059, 'rst66', 'deu', 5, 6, 'Я Господь, Бог твой, Который вывел тебя из земли Египетской, из дома рабства.');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5058, 'rst66', 'deu', 5, 5, 'я же стоял между Господом и между вами в то время, дабы пересказывать вам слово Господа, ибо вы боялись огня и не восходили на гору. Он <i>тогда</i> сказал:');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5057, 'rst66', 'deu', 5, 4, 'Лицом к лицу говорил Господь с вами на горе из среды огня;');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5056, 'rst66', 'deu', 5, 3, 'не с отцами нашими поставил Господь завет сей, но с нами, <i>которые</i> здесь сегодня все живы.');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5055, 'rst66', 'deu', 5, 2, 'Господь, Бог наш, поставил с нами завет на Хориве;');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5054, 'rst66', 'deu', 5, 1, 'И созвал Моисей весь Израиль, и сказал им: слушай, Израиль, постановления и законы, которые я изреку сегодня в уши ваши, и выучите их, и старайтесь исполнять их.');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5053, 'rst66', 'deu', 4, 49, 'и всею равниною по эту сторону Иордана к востоку, до самого моря равнины при подошве Фасги.');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5052, 'rst66', 'deu', 4, 48, '<i>начиная</i> от Ароера, который <i>лежит</i> на берегу потока Арнона, до горы Сиона, она же Ермон,');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5051, 'rst66', 'deu', 4, 47, 'И овладели они землею его и землею Ога, царя Васанского, двух царей Аморрейских, которая за Иорданом к востоку солнца,');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5050, 'rst66', 'deu', 4, 46, 'за Иорданом, на долине против Беф‐Фегора, в земле Сигона, царя Аморрейского, жившего в Есевоне, которого поразил Моисей с сынами Израилевыми, по исшествии их из Египта.');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5049, 'rst66', 'deu', 4, 45, 'вот повеления, постановления и уставы, которые изрек Моисей сынам Израилевым по исшествии их из Египта,');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5048, 'rst66', 'deu', 4, 44, 'Вот закон, который предложил Моисей сынам Израилевым;');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5047, 'rst66', 'deu', 4, 43, 'Бецер в пустыне, на равнине в <i>колене</i> Рувимовом, и Рамоф в Галааде в <i>колене</i> Гадовом, и Голан в Васане в <i>колене</i> Манассиином.');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5046, 'rst66', 'deu', 4, 42, 'чтоб убегал туда убийца, который убьет ближнего своего без намерения, не быв врагом ему ни вчера, ни третьего дня, <i>и</i> чтоб, убежав в один из этих городов, остался жив:');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5045, 'rst66', 'deu', 4, 41, 'Тогда отделил Моисей три города по эту сторону Иордана на восток солнца,');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5044, 'rst66', 'deu', 4, 40, 'и храни постановления Его и заповеди Его, которые я заповедую тебе ныне, чтобы хорошо было тебе и сынам твоим после тебя, и чтобы ты много времени пробыл на той земле, которую Господь, Бог твой, дает тебе навсегда.');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5043, 'rst66', 'deu', 4, 39, 'Итак знай ныне и положи на сердце твое, что Господь есть Бог на небе вверху и на земле внизу, <i>и</i> нет еще <i>кроме Его</i>;');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5042, 'rst66', 'deu', 4, 38, 'чтобы прогнать от лица твоего народы, которые больше и сильнее тебя, <i>и</i> ввести тебя <i>и</i> дать тебе землю их в удел, как это ныне <i>видно</i>.');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5041, 'rst66', 'deu', 4, 37, 'и так как Он возлюбил отцов твоих и избрал <i>вас,</i> потомство их после них, то и вывел тебя Сам великою силою Своею из Египта,');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5040, 'rst66', 'deu', 4, 36, 'с неба дал Он слышать тебе глас Свой, дабы научить тебя, и на земле показал тебе великий огонь Свой, и ты слышал слова Его из среды огня;');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5039, 'rst66', 'deu', 4, 35, 'Тебе дано видеть <i>это</i>, чтобы ты знал, что <i>только</i> Господь есть Бог, <i>и</i> нет еще кроме Его;');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5038, 'rst66', 'deu', 4, 34, 'Или покушался ли <i>какой</i> бог пойти взять себе народ из среды <i>другого</i> народа казнями, знамениями и чудесами, и войною, и рукою крепкою, и мышцею высокою, и великими ужасами, как сделал для вас Господь, Бог ваш, в Египте пред глазами твоими?');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5037, 'rst66', 'deu', 4, 33, 'Слышал ли <i>какой</i> народ глас Бога, говорящего из среды огня, и остался жив, как слышал ты?');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5036, 'rst66', 'deu', 4, 32, 'Ибо спроси у времен прежних, бывших прежде тебя, с того дня, в который сотворил Бог человека на земле, и от края неба до края неба: бывало ли что‐нибудь такое, как сие великое дело, или слыхано ли подобное сему?');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5035, 'rst66', 'deu', 4, 31, 'Господь, Бог твой, есть Бог милосердый; Он не оставит тебя и не погубит тебя, и не забудет завета с отцами твоими, который Он клятвою утвердил им.');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5034, 'rst66', 'deu', 4, 30, 'Когда ты будешь в скорби, и когда все это постигнет тебя в последствие времени, то обратишься к Господу, Богу твоему, и послушаешь гласа Его.');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5033, 'rst66', 'deu', 4, 29, 'Но когда ты взыщешь там Господа, Бога твоего, то найдешь <i>Его</i>, если будешь искать Его всем сердцем твоим и всею душою твоею.');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5032, 'rst66', 'deu', 4, 28, 'и будете там служить богам, сделанным руками человеческими из дерева и камня, которые не видят и не слышат, и не едят, и не обоняют.');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5031, 'rst66', 'deu', 4, 27, 'и рассеет вас Господь по <i>всем</i> народам, и останетесь в малом числе между народами, к которым отведет вас Господь;');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5030, 'rst66', 'deu', 4, 26, 'то свидетельствуюсь вам сегодня небом и землею, что скоро потеряете землю, для наследования которой вы переходите за Иордан; не пробудете много времени на ней, но погибнете;');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5029, 'rst66', 'deu', 4, 25, 'Если же родятся у тебя сыны и сыны у сынов <i>твоих</i>, и, долго жив на земле, вы развратитесь и сделаете изваяние, изображающее что‐либо, и сделаете зло сие пред очами Господа, Бога вашего, и раздражите Его,');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5028, 'rst66', 'deu', 4, 24, 'ибо Господь, Бог твой, есть огнь поядающий, Бог ревнитель.');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5027, 'rst66', 'deu', 4, 23, 'Берегитесь, чтобы не забыть вам завета Господа, Бога вашего, который Он поставил с вами, и чтобы не делать себе кумиров, изображающих что‐либо, как повелел тебе Господь, Бог твой;');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5026, 'rst66', 'deu', 4, 22, 'я умру в сей земле, не перейдя за Иордан, а вы перейдете и овладеете тою доброю землею.');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5025, 'rst66', 'deu', 4, 21, 'И Господь прогневался на меня за вас, и клялся, что я не перейду за Иордан и не войду в ту добрую землю, которую Господь, Бог твой, дает тебе в удел;');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5024, 'rst66', 'deu', 4, 20, 'А вас взял Господь и вывел вас из печи железной, из Египта, дабы вы были народом Его удела, как это ныне <i>видно</i>.');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5023, 'rst66', 'deu', 4, 19, 'и дабы ты, взглянув на небо и увидев солнце, луну и звезды <i>и</i> все воинство небесное, не прельстился и не поклонился им и не служил им, так как Господь, Бог твой, уделил их всем народам под всем небом.');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5022, 'rst66', 'deu', 4, 18, 'изображения какого‐либо <i>гада,</i> ползающего по земле, изображения какой‐либо рыбы, которая в водах ниже земли;');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5021, 'rst66', 'deu', 4, 17, 'изображения какого‐либо скота, который на земле, изображения какой‐либо птицы крылатой, которая летает под небесами,');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5020, 'rst66', 'deu', 4, 16, 'дабы вы не развратились и не сделали себе изваяний, изображений какого‐либо кумира, представляющих мужчину или женщину,');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5019, 'rst66', 'deu', 4, 15, 'Твердо держите в душах ваших, что вы не видели никакого образа в тот день, когда говорил к вам Господь на Хориве из среды огня,');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5018, 'rst66', 'deu', 4, 14, 'и повелел мне Господь в то время научить вас постановлениям и законам, дабы вы исполняли их в той земле, в которую вы входите, чтоб овладеть ею.');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5017, 'rst66', 'deu', 4, 13, 'и объявил Он вам завет Свой, который повелел вам исполнять, десятословие, и написал его на двух каменных скрижалях;');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5016, 'rst66', 'deu', 4, 12, 'И говорил Господь к вам из среды огня; глас слов <i>Его</i> вы слышали, но образа не видели, а только глас;');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5015, 'rst66', 'deu', 4, 11, 'Вы приблизились и стали под горою, а гора горела огнем до самых небес, <i>и была</i> тьма, облако и мрак.');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5014, 'rst66', 'deu', 4, 10, 'о том дне, когда ты стоял пред Господом, Богом твоим, при Хориве и когда сказал Господь мне: собери ко Мне народ, и Я возвещу им слова Мои, из которых они научатся бояться Меня во все дни жизни своей на земле и научат сыновей своих.');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5013, 'rst66', 'deu', 4, 9, 'Только берегись и тщательно храни душу твою, чтобы тебе не забыть тех дел, которые видели глаза твои, и чтобы они не выходили из сердца твоего во все дни жизни твоей; и поведай о них сынам твоим и сынам сынов твоих, &mdash;');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5012, 'rst66', 'deu', 4, 8, 'И есть ли какой великий народ, у которого были бы такие справедливые постановления и законы, как весь закон сей, который я предлагаю вам сегодня?');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5011, 'rst66', 'deu', 4, 7, 'Ибо есть ли какой великий народ, к которому боги <i>его</i> были бы столь близки, как близок к нам Господь, Бог наш, когда ни призовем Его?');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5010, 'rst66', 'deu', 4, 6, 'итак храните и исполняйте их, ибо в этом мудрость ваша и разум ваш пред глазами народов, которые, услышав о всех сих постановлениях, скажут: «только этот великий народ есть народ мудрый и разумный».');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5009, 'rst66', 'deu', 4, 5, 'Вот, я научил вас постановлениям и законам, как повелел мне Господь, Бог мой, дабы вы так поступали в той земле, в которую вы вступаете, чтоб овладеть ею;');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5008, 'rst66', 'deu', 4, 4, 'а вы, прилепившиеся к Господу, Богу вашему, живы все доныне.');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5007, 'rst66', 'deu', 4, 3, 'Глаза ваши видели, что сделал Господь с Ваал‐Фегором: всякого человека, последовавшего Ваал‐Фегору, истребил Господь, Бог твой, из среды тебя;');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5006, 'rst66', 'deu', 4, 2, 'не прибавляйте к тому, что я заповедую вам, и не убавляйте от того; соблюдайте заповеди Господа, Бога вашего, которые я вам заповедую.');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5005, 'rst66', 'deu', 4, 1, 'Итак, Израиль, слушай постановления и законы, которые я научаю вас исполнять, дабы вы были живы, и пошли и наследовали ту землю, которую Господь, Бог отцов ваших, дает вам;');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5004, 'rst66', 'deu', 3, 29, 'И остановились мы на долине, напротив Беф‐Фегора.');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5003, 'rst66', 'deu', 3, 28, 'и дай наставление Иисусу, и укрепи его, и утверди его; ибо он будет предшествовать народу сему и он разделит им на уделы землю, на которую ты посмотришь.');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5002, 'rst66', 'deu', 3, 27, 'взойди на вершину Фасги и взгляни глазами твоими к морю, и к северу, и к югу, и к востоку, и посмотри глазами твоими, потому что ты не перейдешь за Иордан сей;');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5001, 'rst66', 'deu', 3, 26, 'Но Господь гневался на меня за вас и не послушал меня, и сказал мне Господь: полно тебе, впредь не говори Мне более об этом;');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (5000, 'rst66', 'deu', 3, 25, 'Дай мне перейти и увидеть ту добрую землю, которая за Иорданом, и ту прекрасную гору и Ливан.');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (4999, 'rst66', 'deu', 3, 24, 'Владыка Господи, Ты начал показывать рабу Твоему величие Твое и крепкую руку Твою; ибо какой бог есть на небе, или на земле, который мог бы делать такие дела, как Твои, и с могуществом таким, как Твое?');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (4998, 'rst66', 'deu', 3, 23, 'И молился я Господу в то время, говоря:');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (4997, 'rst66', 'deu', 3, 22, 'не бойтесь их, ибо Господь, Бог ваш, Сам сражается за вас.');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (4996, 'rst66', 'deu', 3, 21, 'И Иисусу заповедал я в то время, говоря: глаза твои видели все, что сделал Господь, Бог ваш, с двумя царями сими; то же сделает Господь со всеми царствами, которые ты будешь проходить;');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (4995, 'rst66', 'deu', 3, 20, 'доколе Господь не даст покоя братьям вашим, как вам, и доколе и они не получат во владение землю, которую Господь, Бог ваш, дает им за Иорданом; тогда возвратитесь каждый в свое владение, которое я дал вам.');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (4994, 'rst66', 'deu', 3, 19, 'только жены ваши и дети ваши и скот ваш, (<i>ибо</i> я знаю, что скота у вас много) пусть останутся в городах ваших, которые я дал вам,');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (4993, 'rst66', 'deu', 3, 18, 'И дал я вам в то время повеление, говоря: Господь, Бог ваш, дал вам землю сию во владение; все способные к войне, вооружившись, идите впереди братьев ваших, сынов Израилевых;');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (4992, 'rst66', 'deu', 3, 17, 'также равнину и Иордан, <i>который есть</i> и предел, от Киннерефа до моря равнины, моря Соленого, при подошве <i>горы</i> Фасги к востоку.');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (4991, 'rst66', 'deu', 3, 16, 'а <i>колену</i> Рувимову и Гадову дал от Галаада до потока Арнона, <i>землю</i> между потоком и пределом, до потока Иавока, предела сынов Аммоновых,');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (4990, 'rst66', 'deu', 3, 15, 'Махиру дал я Галаад;');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (4989, 'rst66', 'deu', 3, 14, 'Иаир, сын Манассиин, взял всю область Аргов, до пределов Гесурских и Маахских, и назвал Васан, по имени своему, селениями Иаировыми, что и доныне;');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (4988, 'rst66', 'deu', 3, 13, 'а остаток Галаада и весь Васан, царство Ога, отдал я половине колена Манассиина, всю область Аргов со всем Васаном. (Она называется землею Рефаимов.)');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (4987, 'rst66', 'deu', 3, 12, 'Землю сию взяли мы в то время начиная от Ароера, который у потока Арнона; и половину горы Галаада с городами ее отдал я <i>колену</i> Рувимову и Гадову;');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (4986, 'rst66', 'deu', 3, 11, 'ибо только Ог, царь Васанский, оставался из Рефаимов. Вот, одр его, одр железный, и теперь в Равве, у сынов Аммоновых: длина его девять локтей, а ширина его четыре локтя, локтей мужеских.');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (4985, 'rst66', 'deu', 3, 10, 'все города на равнине, весь Галаад и весь Васан до Салхи и Едреи, города царства Ога Васанского;');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (4984, 'rst66', 'deu', 3, 9, 'Сидоняне Ермон называют Сирионом, а Аморреи называют его Сениром, &mdash;');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (4983, 'rst66', 'deu', 3, 8, 'И взяли мы в то время из руки двух царей Аморрейских землю сию, которая по эту сторону Иордана, от потока Арнона до горы Ермона, &mdash;');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (4982, 'rst66', 'deu', 3, 7, 'но весь скот и захваченное в городах взяли себе в добычу.');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (4981, 'rst66', 'deu', 3, 6, 'и предали мы их заклятию, как поступили с Сигоном, царем Есевонским, предав заклятию всякий город с мужчинами, женщинами и детьми;');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (4980, 'rst66', 'deu', 3, 5, 'все эти города укреплены были высокими стенами, воротами и запорами, кроме городов неукрепленных, весьма многих;');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (4979, 'rst66', 'deu', 3, 4, 'и взяли мы в то время все города его; не было города, которого мы не взяли бы у них: шестьдесят городов, всю область Аргов, царство Ога Васанского;');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (4978, 'rst66', 'deu', 3, 3, 'И предал Господь, Бог наш, в руки наши и Ога, царя Васанского, и весь народ его; и мы поразили его, так что никого не осталось у него в живых;');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (4977, 'rst66', 'deu', 3, 2, 'И сказал мне Господь: не бойся его, ибо Я отдам в руку твою его, и весь народ его, и всю землю его, и ты поступишь с ним так, как поступил с Сигоном, царем Аморрейским, который жил в Есевоне.');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (4976, 'rst66', 'deu', 3, 1, 'И обратились мы оттуда, и шли к Васану, и выступил против нас на войну Ог, царь Васанский, со всем народом своим, при Едреи.');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (4975, 'rst66', 'deu', 2, 37, 'Только к земле Аммонитян ты не подходил, ни к <i>местам</i> близ потока Иавока, ни к городам, на горе, ни ко всему, к чему не повелел <i>нам</i> Господь, Бог наш.');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (4974, 'rst66', 'deu', 2, 36, 'От Ароера, который на берегу потока Арнона, и от города, который на долине, до Галаада не было города, который был бы неприступен для нас: всё предал Господь, Бог наш, <i>в руки наши</i>.');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (4973, 'rst66', 'deu', 2, 35, 'только взяли мы себе в добычу скот их и захваченное во взятых нами городах.');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (4972, 'rst66', 'deu', 2, 34, 'и взяли в то время все города его, и предали заклятию все города, мужчин и женщин и детей, не оставили никого в живых;');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (4971, 'rst66', 'deu', 2, 33, 'и предал его Господь, Бог наш, <i>в руки наши,</i> и мы поразили его и сынов его и весь народ его,');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (4970, 'rst66', 'deu', 2, 32, 'И Сигон со всем народом своим выступил против нас на сражение к Яаце;');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (4969, 'rst66', 'deu', 2, 31, 'И сказал мне Господь: вот, Я начинаю предавать тебе Сигона и землю его; начинай овладевать землею его.');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (4968, 'rst66', 'deu', 2, 30, 'Но Сигон, царь Есевонский, не согласился позволить пройти нам через свою <i>землю</i>, потому что Господь, Бог твой, ожесточил дух его и сердце его сделал упорным, чтобы предать его в руку твою, как <i>это видно</i> ныне.');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (4967, 'rst66', 'deu', 2, 29, 'так, как сделали мне сыны Исава, живущие на Сеире, и Моавитяне, живущие в Аре, доколе не перейду чрез Иордан в землю, которую Господь, Бог наш, дает нам.');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (4966, 'rst66', 'deu', 2, 28, 'пищу продавай мне за серебро, и я буду есть, и воду для питья давай мне за серебро, и я буду пить, только ногами моими пройду &mdash;');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (4965, 'rst66', 'deu', 2, 27, 'позволь пройти мне землею твоею; я пойду дорогою, не сойду ни направо, ни налево;');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (4964, 'rst66', 'deu', 2, 26, 'И послал я послов из пустыни Кедемоф к Сигону, царю Есевонскому, с словами мирными, чтобы сказать:');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (4963, 'rst66', 'deu', 2, 25, 'с сего дня Я начну распространять страх и ужас пред тобою на народы под всем небом; те, которые услышат о тебе, вострепещут и ужаснутся тебя.');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (4962, 'rst66', 'deu', 2, 24, 'Встаньте, отправьтесь и перейдите поток Арнон; вот, Я предаю в руку твою Сигона, царя Есевонского, Аморреянина, и землю его; начинай овладевать ею, и веди с ним войну;');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (4961, 'rst66', 'deu', 2, 23, 'и Аввеев, живших в селениях до самой Газы, Кафторимы, исшедшие из Кафтора, истребили и поселились на месте их.');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (4960, 'rst66', 'deu', 2, 22, 'как Он сделал для сынов Исавовых, живущих на Сеире, истребив пред лицом их Хорреев, и они изгнали их, и поселились на месте их, и <i>живут</i> до сего дня;');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (4959, 'rst66', 'deu', 2, 21, 'народ великий, многочисленный и высокий, как <i>сыны</i> Енаковы, и истребил их Господь пред лицом их, и изгнали они их, и поселились на месте их,');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (4958, 'rst66', 'deu', 2, 20, 'и она считалась землею Рефаимов; прежде жили на ней Рефаимы; Аммонитяне же называют их Замзумимами;');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (4957, 'rst66', 'deu', 2, 19, 'и приблизился к Аммонитянам; не вступай с ними во вражду и не начинай с ними войны, ибо Я не дам тебе ничего от земли сынов Аммоновых во владение, потому что Я отдал ее во владение сынам Лотовым;');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (4956, 'rst66', 'deu', 2, 18, 'ты проходишь ныне мимо пределов Моава, мимо Ара,');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (4955, 'rst66', 'deu', 2, 17, 'тогда сказал мне Господь, говоря:');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (4954, 'rst66', 'deu', 2, 16, 'Когда же перевелись все ходящие на войну и вымерли из среды народа,');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (4953, 'rst66', 'deu', 2, 15, 'да и рука Господня была на них, чтоб истреблять их из среды стана, пока не вымерли.');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (4952, 'rst66', 'deu', 2, 14, 'С тех пор, как мы пошли в Кадес‐Варни и как прошли долину Заред, <i>минуло</i> тридцать восемь лет, и у нас перевелся из среды стана весь род ходящих на войну, как клялся им Господь;');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (4951, 'rst66', 'deu', 2, 13, 'итак, встаньте и пройдите долину Заред. И прошли мы долину Заред.');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (4950, 'rst66', 'deu', 2, 12, 'а на Сеире жили прежде Хорреи; но сыны Исавовы прогнали их и истребили их от лица своего и поселились вместо их &mdash; так, как поступил Израиль с землею наследия своего, которую дал им Господь;');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (4949, 'rst66', 'deu', 2, 11, 'и они считались между Рефаимами как <i>сыны</i> Енаковы; Моавитяне же называют их Емимами;');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (4948, 'rst66', 'deu', 2, 10, 'прежде жили там Емимы, народ великий, многочисленный и высокий, как <i>сыны</i> Енаковы,');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (4947, 'rst66', 'deu', 2, 9, 'И сказал мне Господь: не вступай во вражду с Моавом и не начинай с ними войны; ибо Я не дам тебе ничего от земли его во владение, потому что Ар отдал Я во владение сынам Лотовым;');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (4946, 'rst66', 'deu', 2, 8, 'И шли мы мимо братьев наших, сынов Исавовых, живущих на Сеире, путем равнины, от Елафа и Ецион‐Гавера, и поворотили, и шли к пустыне Моава.');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (4945, 'rst66', 'deu', 2, 7, 'ибо Господь, Бог твой, благословил тебя во всяком деле рук твоих, покровительствовал <i>тебе</i> во время путешествия твоего по великой пустыне сей; вот, сорок лет Господь, Бог твой, с тобою; ты ни в чем не терпел недостатка.');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (4944, 'rst66', 'deu', 2, 6, 'пищу покупайте у них за серебро и ешьте; и воду покупайте у них за серебро и пейте;');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (4943, 'rst66', 'deu', 2, 5, 'начинать с ними войну, ибо Я не дам вам земли их ни на стопу ноги, потому что гору Сеир Я дал во владение Исаву;');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (4942, 'rst66', 'deu', 2, 4, 'и народу дай повеление и скажи: вы будете проходить пределы братьев ваших, сынов Исавовых, живущих на Сеире, и они убоятся вас; но остерегайтесь');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (4941, 'rst66', 'deu', 2, 3, 'полно вам ходить вокруг этой горы, обратитесь к северу;');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (4940, 'rst66', 'deu', 2, 2, 'И сказал мне Господь, говоря:');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (4939, 'rst66', 'deu', 2, 1, 'И обратились мы и отправились в пустыню к Чермному морю, как говорил мне Господь, и много времени ходили вокруг горы Сеира.');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (4938, 'rst66', 'deu', 1, 46, 'И пробыли вы в Кадесе много времени, сколько времени вы <i>там</i> были.');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (4937, 'rst66', 'deu', 1, 45, 'И возвратились вы и плакали пред Господом, но Господь не услышал вопля вашего и не внял вам.');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (4936, 'rst66', 'deu', 1, 44, 'И выступил против вас Аморрей, живший на горе той, и преследовали вас так, как делают пчелы, и поражали вас на Сеире до самой Хормы.');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (4935, 'rst66', 'deu', 1, 43, 'И я говорил вам, но вы не послушали и воспротивились повелению Господню и по упорству своему взошли на гору.');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (4934, 'rst66', 'deu', 1, 42, 'Но Господь сказал мне: скажи им: «не всходите и не сражайтесь, потому что нет Меня среди вас, чтобы не поразили вас враги ваши».');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (4933, 'rst66', 'deu', 1, 41, 'И вы отвечали тогда и сказали мне: согрешили мы пред Господом, пойдем и сразимся, как повелел нам Господь, Бог наш. И препоясались вы, каждый, ратным оружием своим, и безрассудно решились взойти на гору.');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (4932, 'rst66', 'deu', 1, 40, 'а вы обратитесь и отправьтесь в пустыню по дороге к Чермному морю.');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (4931, 'rst66', 'deu', 1, 39, 'дети ваши, о которых вы говорили, что они достанутся в добычу <i>врагам</i>, и сыновья ваши, которые не знают ныне ни добра, ни зла, они войдут туда, им дам ее, и они овладеют ею;');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (4930, 'rst66', 'deu', 1, 38, 'Иисус, сын Навин, который при тебе, он войдет туда; его утверди, ибо он введет Израиля во владение ею;');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (4929, 'rst66', 'deu', 1, 37, 'И на меня прогневался Господь за вас, говоря: и ты не войдешь туда;');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (4928, 'rst66', 'deu', 1, 36, 'только Халев, сын Иефонниин, увидит ее; ему дам Я землю, по которой он проходил, и сынам его, за то, что он повиновался Господу.');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (4927, 'rst66', 'deu', 1, 35, 'никто из людей сих, из сего злого рода, не увидит доброй земли, которую Я клялся дать отцам вашим;');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (4926, 'rst66', 'deu', 1, 34, 'И Господь услышал слова ваши, и разгневался, и поклялся, говоря:');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (4925, 'rst66', 'deu', 1, 33, 'Который шел перед вами путем &mdash; искать вам места, где остановиться вам, ночью в огне, чтобы указывать вам дорогу, по которой идти, а днем в облаке.');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (4924, 'rst66', 'deu', 1, 32, 'Но и при этом вы не верили Господу, Богу вашему,');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (4923, 'rst66', 'deu', 1, 31, 'и в пустыне сей, где, как ты видел, Господь, Бог твой, носил тебя, как человек носит сына своего, на всем пути, которым вы проходили до пришествия вашего на сие место.');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (4922, 'rst66', 'deu', 1, 30, 'Господь, Бог ваш, идет перед вами; Он будет сражаться за вас, как Он сделал с вами в Египте, пред глазами вашими,');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (4921, 'rst66', 'deu', 1, 29, 'И я сказал вам: не страшитесь и не бойтесь их;');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (4920, 'rst66', 'deu', 1, 28, 'куда мы пойдем? Братья наши расслабили сердце наше, говоря: «народ тот более и выше нас, города <i>там</i> большие и с укреплениями до небес, да и сынов Енаковых видели мы там».');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (4919, 'rst66', 'deu', 1, 27, 'и роптали в шатрах ваших, и говорили: Господь, по ненависти к нам, вывел нас из земли Египетской, чтоб отдать нас в руки Аморреев <i>и</i> истребить нас;');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (4918, 'rst66', 'deu', 1, 26, 'Но вы не захотели идти и воспротивились повелению Господа, Бога вашего,');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (4917, 'rst66', 'deu', 1, 25, 'и взяли в руки свои плодов земли, и доставили нам, и принесли нам известие, и сказали: хороша земля, которую Господь, Бог наш, дает нам.');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (4916, 'rst66', 'deu', 1, 24, 'Они пошли, взошли на гору и дошли до долины Есхол, и обозрели ее;');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (4915, 'rst66', 'deu', 1, 23, 'Слово это мне понравилось, и я взял из вас двенадцать человек, по одному человеку от <i>каждого</i> колена.');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (4914, 'rst66', 'deu', 1, 22, 'Но вы все подошли ко мне и сказали: пошлем пред собою людей, чтоб они исследовали нам землю и принесли нам известие о дороге, по которой идти нам, и о городах, в которые идти нам.');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (4913, 'rst66', 'deu', 1, 21, 'вот, Господь, Бог твой, отдает тебе землю сию, иди, возьми ее во владение, как говорил тебе Господь, Бог отцов твоих, не бойся и не ужасайся.');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (4912, 'rst66', 'deu', 1, 20, 'И сказал я вам: вы пришли к горе Аморрейской, которую Господь, Бог наш, дает нам;');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (4911, 'rst66', 'deu', 1, 19, 'И отправились мы от Хорива, и шли по всей этой великой и страшной пустыне, которую вы видели, по пути к горе Аморрейской, как повелел Господь, Бог наш, и пришли в Кадес‐Варни.');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (4910, 'rst66', 'deu', 1, 18, 'И дал я вам в то время повеления обо всем, что надлежит вам делать.');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (4909, 'rst66', 'deu', 1, 17, 'не различайте лиц на суде, как малого, так и великого выслушивайте: не бойтесь лица человеческого, ибо суд &mdash; дело Божие; а дело, которое для вас трудно, доводите до меня, и я выслушаю его.');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (4908, 'rst66', 'deu', 1, 16, 'И дал я повеление судьям вашим в то время, говоря: выслушивайте братьев ваших и судите справедливо, как брата с братом, так и пришельца его;');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (4907, 'rst66', 'deu', 1, 15, 'И взял я главных из колен ваших, мужей мудрых и испытанных, и сделал их начальниками над вами, тысяченачальниками, стоначальниками, пятидесятиначальниками, десятиначальниками и надзирателями по коленам вашим.');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (4906, 'rst66', 'deu', 1, 14, 'Вы отвечали мне и сказали: хорошее дело велишь ты сделать.');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (4905, 'rst66', 'deu', 1, 13, 'Изберите себе по коленам вашим мужей мудрых, разумных и испытанных, и я поставлю их начальниками вашими.');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (4904, 'rst66', 'deu', 1, 12, 'как же мне одному носить тягости ваши, бремена ваши и распри ваши?');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (4903, 'rst66', 'deu', 1, 11, 'Господь, Бог отцов ваших, да умножит вас в тысячу крат против того, сколько вас <i>теперь</i>, и да благословит вас, как Он говорил вам:');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (4902, 'rst66', 'deu', 1, 10, 'Господь, Бог ваш, размножил вас, и вот, вы ныне многочисленны, как звезды небесные;');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (4901, 'rst66', 'deu', 1, 9, 'И я сказал вам в то время: не могу один водить вас;');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (4900, 'rst66', 'deu', 1, 8, 'вот, Я даю вам землю сию, пойдите, возьмите в наследие землю, которую Господь с клятвою обещал дать отцам вашим, Аврааму, Исааку и Иакову, им и потомству их».');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (4899, 'rst66', 'deu', 1, 7, 'Обратитесь, отправьтесь в путь и пойдите на гору Аморреев и ко всем соседям их, на равнину, на гору, на низкие места, и на южный край, и к берегам моря, в землю Ханаанскую, и к Ливану, даже до реки великой, реки Евфрата;');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (4898, 'rst66', 'deu', 1, 6, 'Господь, Бог наш, говорил нам в Хориве и сказал: «полно вам жить на горе сей!');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (4897, 'rst66', 'deu', 1, 5, 'за Иорданом, в земле Моавитской, начал Моисей изъяснять закон сей и сказал:');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (4896, 'rst66', 'deu', 1, 4, 'По убиении им Сигона, царя Аморрейского, который жил в Есевоне, и Ога, царя Васанского, который жил в Аштерофе в Едреи,');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (4895, 'rst66', 'deu', 1, 3, 'Сорокового года, одиннадцатого месяца, в первый <i>день</i> месяца говорил Моисей сынам Израилевым все, что заповедал ему Господь о них.');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (4894, 'rst66', 'deu', 1, 2, 'в расстоянии одиннадцати дней пути от Хорива, по дороге от горы Сеир к Кадес‐Варни.');",
"INSERT INTO  bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (4893, 'rst66', 'deu', 1, 1, 'Сии суть слова, которые говорил Моисей всем Израильтянам за Иорданом в пустыне на равнине против Суфа, между Фараном и Тофелом, и Лаваном, и Асирофом, и Дизагавом,');",
        ];

