        $list = [
            "INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (14062, 'rst66', '2sa', 24, 25, 'И соорудил там Давид жертвенник Господу и принес всесожжения и мирные жертвы. И умилостивился Господь над страною, и прекратилось поражение Израильтян.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (14061, 'rst66', '2sa', 24, 24, 'Но царь сказал Орне: нет, я заплачу тебе, что стоит, и не вознесу Господу, Богу моему, жертвы, <i>взятой</i> даром. И купил Давид гумно и волов за пятьдесят сиклей серебра.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (14060, 'rst66', '2sa', 24, 23, 'Все это, царь, Орна отдает царю. Еще сказал Орна царю: Господь, Бог твой, да будет милостив к тебе!');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (14059, 'rst66', '2sa', 24, 22, 'И сказал Орна Давиду: пусть возьмет и вознесет <i>в жертву</i> господин мой, царь, что ему угодно. Вот волы для всесожжения и повозки и упряжь воловья на дрова.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (14058, 'rst66', '2sa', 24, 21, 'И сказал Орна: зачем пришел господин мой, царь, к рабу своему? И сказал Давид: купить у тебя гумно для устроения жертвенника Господу, чтобы прекратилось поражение народа.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (14057, 'rst66', '2sa', 24, 20, 'И взглянул Орна и увидел царя и слуг его, шедших к нему, и вышел Орна и поклонился царю лицом своим до земли.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (14056, 'rst66', '2sa', 24, 19, 'И пошел Давид по слову Гада, как повелел Господь.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (14055, 'rst66', '2sa', 24, 18, 'И пришел в тот день Гад к Давиду и сказал: иди, поставь жертвенник Господу на гумне Орны Иевусеянина.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (14054, 'rst66', '2sa', 24, 17, 'И сказал Давид Господу, когда увидел Ангела, поражавшего народ, говоря: вот, я согрешил, я поступил беззаконно; а эти овцы, что сделали они? пусть же рука Твоя обратится на меня и на дом отца моего.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (14053, 'rst66', '2sa', 24, 16, 'И простер Ангел руку свою на Иерусалим, чтобы опустошить его; но Господь пожалел о бедствии и сказал Ангелу, поражавшему народ: довольно, теперь опусти руку твою. Ангел же Господень был тогда у гумна Орны Иевусеянина.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (14052, 'rst66', '2sa', 24, 15, 'И послал Господь язву на Израильтян от утра до назначенного времени; и умерло из народа, от Дана до Вирсавии, семьдесят тысяч человек.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (14051, 'rst66', '2sa', 24, 14, 'И сказал Давид Гаду: тяжело мне очень; но пусть впаду я в руки Господа, ибо велико милосердие Его; только бы в руки человеческие не впасть мне.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (14050, 'rst66', '2sa', 24, 13, 'И пришел Гад к Давиду, и возвестил ему, и сказал ему: избирай себе, быть ли голоду в стране твоей семь лет, или чтобы ты три месяца бегал от неприятелей твоих, и они преследовали тебя, или чтобы в продолжение трех дней была моровая язва в стране твоей. Теперь рассуди и реши, что мне отвечать Пославшему меня.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (14049, 'rst66', '2sa', 24, 12, 'пойди и скажи Давиду: так говорит Господь: три <i>наказания</i> предлагаю Я тебе; выбери себе одно из них, которое совершилось бы над тобою.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (14048, 'rst66', '2sa', 24, 11, 'Когда Давид встал на другой день утром, то было слово Господа к Гаду пророку, прозорливцу Давида:');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (14047, 'rst66', '2sa', 24, 10, 'И вздрогнуло сердце Давидово после того, как он сосчитал народ. И сказал Давид Господу: тяжко согрешил я, поступив так; и ныне молю Тебя, Господи, прости грех раба Твоего, ибо крайне неразумно поступил я.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (14046, 'rst66', '2sa', 24, 9, 'И подал Иоав список народной переписи царю; и оказалось, что Израильтян было восемьсот тысяч мужей сильных, способных к войне, а Иудеян пятьсот тысяч.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (14045, 'rst66', '2sa', 24, 8, 'и обошли всю землю и пришли чрез девять месяцев и двадцать дней в Иерусалим.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (14044, 'rst66', '2sa', 24, 7, 'и пришли к укреплению Тира и во все города Хивеян и Хананеян и вышли на юг Иудеи в Вирсавию;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (14043, 'rst66', '2sa', 24, 6, 'и пришли в Галаад и в землю Тахтим‐Ходши; и пришли в Дан‐Яан и обошли Сидон;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (14042, 'rst66', '2sa', 24, 5, 'И перешли они Иордан и остановились в Ароере, на правой стороне города, который среди долины Гадовой, к Иазеру;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (14041, 'rst66', '2sa', 24, 4, 'Но слово царя Иоаву и военачальникам превозмогло; и пошел Иоав с военачальниками от царя считать народ Израильский.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (14040, 'rst66', '2sa', 24, 3, 'И сказал Иоав царю: Господь, Бог твой, да умножит столько народа, сколько есть, и еще во сто раз столько, а очи господина моего, царя, да увидят <i>это;</i> но для чего господин мой, царь, желает этого дела?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (14039, 'rst66', '2sa', 24, 2, 'И сказал царь Иоаву военачальнику, который был при нем: пройди по всем коленам Израилевым от Дана до Вирсавии, и исчислите народ, чтобы мне знать число народа.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (14038, 'rst66', '2sa', 24, 1, 'Гнев Господень опять возгорелся на Израильтян, и возбудил он в них Давида сказать: пойди, исчисли Израиля и Иуду.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (14037, 'rst66', '2sa', 23, 39, 'Урия Хеттеянин. Всех тридцать семь.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (14036, 'rst66', '2sa', 23, 38, 'Ира Итритянин, Гареб Итритянин,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (14035, 'rst66', '2sa', 23, 37, 'Целек Аммонитянин, Нахарай Беротянин, оруженосец Иоава, сына Саруи,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (14034, 'rst66', '2sa', 23, 36, 'Игал, сын Нафана, из Цобы, Бани Гадитянин,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (14033, 'rst66', '2sa', 23, 35, 'Хецрай Кармилитянин, Паарай Арбитянин,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (14032, 'rst66', '2sa', 23, 34, 'Елифелет, сын Ахасбая, сына Магахати, Елиам, сын Ахитофела, Гилонянин,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (14031, 'rst66', '2sa', 23, 33, 'Шама Гараритянин, Ахиам, сын Шарара, Араритянин,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (14030, 'rst66', '2sa', 23, 32, 'Елияхба Шаалбонянин; из сыновей Яшена &mdash; Ионафан,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (14029, 'rst66', '2sa', 23, 31, 'Ави‐Албон Арбатитянин, Азмавет Бархюмитянин,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (14028, 'rst66', '2sa', 23, 30, 'Ванея Пирафонянин, Иддай из Нахле‐Гааша,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (14027, 'rst66', '2sa', 23, 29, 'Хелев, сын Бааны, Нетофафянин, Иттай, сын Рибая, из Гивы сынов Вениаминовых,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (14026, 'rst66', '2sa', 23, 28, 'Цалмон Ахохитянин, Магарай Нетофафянин,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (14025, 'rst66', '2sa', 23, 27, 'Евиезер Анафофянин, Мебуннай Хушатянин,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (14024, 'rst66', '2sa', 23, 26, 'Херец Палтитянин, Ира, сын Икеша, Фекоитянин,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (14023, 'rst66', '2sa', 23, 25, 'Шамма Хародитянин, Елика Хародитянин,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (14022, 'rst66', '2sa', 23, 24, 'Асаил, брат Иоава &mdash; в числе тридцати; Елханан, сын Додо, из Вифлеема,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (14021, 'rst66', '2sa', 23, 23, 'он был знатнее тридцати, но с теми тремя не равнялся. И поставил его Давид ближайшим исполнителем своих приказаний.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (14020, 'rst66', '2sa', 23, 22, 'вот что сделал Ванея, сын Иодаев, и он был в славе у трех храбрых;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (14019, 'rst66', '2sa', 23, 21, 'он же убил одного Египтянина, человека видного; в руке Египтянина было копье, а он пошел к нему с палкою и отнял копье из руки Египтянина, и убил его собственным его копьем:');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (14018, 'rst66', '2sa', 23, 20, 'Ванея, сын Иодая, мужа храброго, великий по делам, из Кавцеила; он поразил двух сыновей Ариила Моавитского; он же сошел и убил льва во рве в снежное время;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (14017, 'rst66', '2sa', 23, 19, 'Из трех он был знатнейшим и был начальником, но с теми тремя не равнялся.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (14016, 'rst66', '2sa', 23, 18, 'И Авесса, брат Иоава, сын Саруин, был главным из трех; он убил копьем своим триста человек и был в славе у тех троих.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (14015, 'rst66', '2sa', 23, 17, 'и сказал: сохрани меня Господь, чтоб я сделал это! не кровь ли это людей, ходивших с опасностью собственной жизни? И не захотел пить ее. Вот что сделали эти трое храбрых!');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (14014, 'rst66', '2sa', 23, 16, 'Тогда трое этих храбрых пробились сквозь стан Филистимский и почерпнули воды из колодезя Вифлеемского, что у ворот, и взяли и принесли Давиду. Но он не захотел пить ее и вылил ее во славу Господа,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (14013, 'rst66', '2sa', 23, 15, 'И захотел Давид пить, и сказал: кто напоит меня водою из колодезя Вифлеемского, что у ворот?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (14012, 'rst66', '2sa', 23, 14, 'Давид был тогда в укрепленном месте, а отряд Филистимлян &mdash; в Вифлееме.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (14011, 'rst66', '2sa', 23, 13, 'Трое сих главных из тридцати вождей пошли и вошли во время жатвы к Давиду в пещеру Одоллам, когда толпы Филистимлян стояли в долине Рефаимов.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (14010, 'rst66', '2sa', 23, 12, 'то он стал среди поля и сберег его и поразил Филистимлян. И даровал тогда Господь великую победу.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (14009, 'rst66', '2sa', 23, 11, 'За ним Шамма, сын Аге, Гараритянин. Когда Филистимляне собрались в Фирию, где было поле, засеянное чечевицею, и народ побежал от Филистимлян,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (14008, 'rst66', '2sa', 23, 10, 'Израильтяне вышли против них, и он стал и поражал Филистимлян до того, что рука его утомилась и прилипла к мечу. И даровал Господь в тот день великую победу, и народ последовал за ним для того только, чтоб обирать <i>убитых</i>.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (14007, 'rst66', '2sa', 23, 9, 'По нем Елеазар, сын Додо, сына Ахохи, из трех храбрых, бывших с Давидом, когда они порицанием вызывали Филистимлян, собравшихся на войну;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (14006, 'rst66', '2sa', 23, 8, 'Вот имена храбрых у Давида: Исбосеф Ахаманитянин, главный из трех; он поднял копье свое на восемьсот человек и поразил их в один раз.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (14005, 'rst66', '2sa', 23, 7, 'но кто касается его, вооружается железом или деревом копья, и огнем сожигают его на месте.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (14004, 'rst66', '2sa', 23, 6, 'А нечестивые будут, как выброшенное терние, которого не берут рукою;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (14003, 'rst66', '2sa', 23, 5, 'Не так ли дом мой у Бога? Ибо завет вечный положил Он со мною, твердый и непреложный. Не так ли исходит от Него все спасение мое и все хотение мое?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (14002, 'rst66', '2sa', 23, 4, 'И как на рассвете утра, при восходе солнца на безоблачном небе, от сияния после дождя вырастает трава из земли,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (14001, 'rst66', '2sa', 23, 3, 'Сказал Бог Израилев, говорил о мне скала Израилева: владычествующий над людьми будет праведен, владычествуя в страхе Божием.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (14000, 'rst66', '2sa', 23, 2, 'Дух Господень говорит во мне, и слово Его на языке у меня.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13999, 'rst66', '2sa', 23, 1, 'Вот последние слова Давида, изречение Давида, сына Иессеева, изречение мужа, поставленного высоко, помазанника Бога Иаковлева и сладкого певца Израилева:');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13998, 'rst66', '2sa', 22, 51, 'величественно спасающий царя Своего и творящий милость помазаннику Своему Давиду и потомству его вовеки!');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13997, 'rst66', '2sa', 22, 50, 'За то я буду славить Тебя, Господи, между иноплеменниками и буду петь имени Твоему,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13996, 'rst66', '2sa', 22, 49, 'и избавляющий меня от врагов моих! Над восстающими против меня Ты возвысил меня; от человека жестокого Ты избавил меня.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13995, 'rst66', '2sa', 22, 48, 'Бог, мстящий за меня и покоряющий мне народы');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13994, 'rst66', '2sa', 22, 47, 'Жив Господь и благословен защитник мой! Да будет превознесен Бог, убежище спасения моего,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13993, 'rst66', '2sa', 22, 46, 'Иноплеменники бледнеют и трепещут в укреплениях своих.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13992, 'rst66', '2sa', 22, 45, 'Иноплеменники ласкательствуют предо мною; по слуху <i>обо мне</i> повинуются мне.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13991, 'rst66', '2sa', 22, 44, 'Ты избавил меня от мятежа народа моего; Ты сохранил меня, чтоб быть мне главою над иноплеменниками; народ, которого я не знал, служит мне.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13990, 'rst66', '2sa', 22, 43, 'Я рассеваю их, как прах земной, как грязь уличную мну их и топчу их.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13989, 'rst66', '2sa', 22, 42, 'Они взывают, но нет спасающего, &mdash; ко Господу, но Он не внемлет им.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13988, 'rst66', '2sa', 22, 41, 'Ты обращаешь ко мне тыл врагов моих, и я истребляю ненавидящих меня.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13987, 'rst66', '2sa', 22, 40, 'Ты препоясываешь меня силою для войны и низлагаешь предо мною восстающих на меня;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13986, 'rst66', '2sa', 22, 39, 'и истребляю их и поражаю их, и не встают и падают под ноги мои.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13985, 'rst66', '2sa', 22, 38, 'Я гоняюсь за врагами моими и истребляю их, и не возвращаюсь, доколе не уничтожу их;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13984, 'rst66', '2sa', 22, 37, 'Ты расширяешь шаг мой подо мною, и не колеблются ноги мои.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13983, 'rst66', '2sa', 22, 36, 'Ты даешь мне щит спасения Твоего, и милость Твоя возвеличивает меня.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13982, 'rst66', '2sa', 22, 35, 'научает руки мои брани и мышцы мои напрягает, как медный лук.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13981, 'rst66', '2sa', 22, 34, 'делает ноги мои, как оленьи, и на высотах поставляет меня;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13980, 'rst66', '2sa', 22, 33, 'Бог препоясует меня силою, устрояет мне верный путь;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13979, 'rst66', '2sa', 22, 32, 'Ибо кто Бог, кроме Господа, и кто защита, кроме Бога нашего?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13978, 'rst66', '2sa', 22, 31, 'Бог! &mdash; непорочен путь Его, чисто слово Господа, щит Он для всех, надеющихся на Него.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13977, 'rst66', '2sa', 22, 30, 'С Тобою я поражаю войско; с Богом моим восхожу на стену.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13976, 'rst66', '2sa', 22, 29, 'Ты, Господи, светильник мой; Господь просвещает тьму мою.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13975, 'rst66', '2sa', 22, 28, 'Людей угнетенных Ты спасаешь и взором Своим унижаешь надменных.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13974, 'rst66', '2sa', 22, 27, 'с чистым &mdash; чисто, а с лукавым &mdash; по лукавству его.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13973, 'rst66', '2sa', 22, 26, 'С милостивым Ты поступаешь милостиво, с мужем искренним &mdash; искренно,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13972, 'rst66', '2sa', 22, 25, 'И воздал мне Господь по правде моей, по чистоте моей пред очами Его.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13971, 'rst66', '2sa', 22, 24, 'и был непорочен пред Ним, и остерегался, чтобы не согрешить мне.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13970, 'rst66', '2sa', 22, 23, 'ибо все заповеди Его предо мною, и от уставов Его я не отступал,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13969, 'rst66', '2sa', 22, 22, 'Ибо я хранил пути Господа и не был нечестивым пред Богом моим,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13968, 'rst66', '2sa', 22, 21, 'Воздал мне Господь по правде моей, по чистоте рук моих вознаградил меня.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13967, 'rst66', '2sa', 22, 20, 'и вывел меня на пространное место, избавил меня, ибо Он благоволит ко мне.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13966, 'rst66', '2sa', 22, 19, 'Они восстали на меня в день бедствия моего; но Господь был опорою для меня');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13965, 'rst66', '2sa', 22, 18, 'избавил меня от врага моего сильного, от ненавидящих меня, которые были сильнее меня.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13964, 'rst66', '2sa', 22, 17, 'Простер Он <i>руку</i> с высоты и взял меня, и извлек меня из вод многих;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13963, 'rst66', '2sa', 22, 16, 'И открылись источники моря, обнажились основания вселенной от грозного гласа Господа, от дуновения духа гнева Его.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13962, 'rst66', '2sa', 22, 15, 'пустил стрелы и рассеял их; <i>блеснул</i> молниею и истребил их.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13961, 'rst66', '2sa', 22, 14, 'Возгремел с небес Господь, и Всевышний дал глас Свой;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13960, 'rst66', '2sa', 22, 13, 'от блистания пред Ним разгорались угли огненные.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13959, 'rst66', '2sa', 22, 12, 'и мраком покрыл Себя, как сению, сгустив воды облаков небесных;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13958, 'rst66', '2sa', 22, 11, 'и воссел на херувимов, и полетел, и понесся на крыльях ветра;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13957, 'rst66', '2sa', 22, 10, 'Наклонил Он небеса и сошел; и мрак под ногами Его;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13956, 'rst66', '2sa', 22, 9, 'Поднялся дым от гнева Его и из уст Его огонь поядающий; горящие угли сыпались от Него.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13955, 'rst66', '2sa', 22, 8, 'Потряслась, всколебалась земля, дрогнули и подвиглись основания небес, ибо разгневался Он.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13954, 'rst66', '2sa', 22, 7, 'Но в тесноте моей я призвал Господа и к Богу моему воззвал, и Он услышал из чертога Своего голос мой, и вопль мой <i>дошел</i> до слуха Его.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13953, 'rst66', '2sa', 22, 6, 'цепи ада облегли меня, и сети смерти опутали меня.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13952, 'rst66', '2sa', 22, 5, 'Объяли меня волны смерти, и потоки беззакония устрашили меня;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13951, 'rst66', '2sa', 22, 4, 'Призову Господа достопоклоняемого и от врагов моих спасусь.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13950, 'rst66', '2sa', 22, 3, 'Бог мой &mdash; скала моя; на Него я уповаю; щит мой, рог спасения моего, ограждение мое и убежище мое; Спаситель мой, от бед Ты избавил меня!');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13949, 'rst66', '2sa', 22, 2, 'Господь &mdash; твердыня моя и крепость моя и избавитель мой.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13948, 'rst66', '2sa', 22, 1, 'И воспел Давид песнь Господу в день, когда Господь избавил его от руки всех врагов его и от руки Саула, и сказал:');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13947, 'rst66', '2sa', 21, 22, 'Эти четыре были из рода Рефаимов в Гефе, и они пали от руки Давида и слуг его.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13946, 'rst66', '2sa', 21, 21, 'и он поносил Израильтян; но его убил Ионафан, сын Сафая, брата Давидова.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13945, 'rst66', '2sa', 21, 20, 'Было еще сражение в Гефе; и был <i>там</i> один человек рослый, имевший по шести пальцев на руках и на ногах, всего двадцать четыре, также из потомков Рефаимов,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13944, 'rst66', '2sa', 21, 19, 'Было и другое сражение в Гобе; тогда убил Елханан, сын Ягаре‐Оргима Вифлеемского, Голиафа Гефянина, у которого древко копья было как навой у ткачей.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13943, 'rst66', '2sa', 21, 18, 'Потом была снова война с Филистимлянами в Гобе; тогда Совохай Хушатянин убил Сафута, одного из потомков Рефаимов.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13942, 'rst66', '2sa', 21, 17, 'Но ему помог Авесса, сын Саруин, и поразил Филистимлянина и умертвил его. Тогда люди Давидовы поклялись, говоря: не выйдешь ты больше с нами на войну, чтобы не угас светильник Израиля.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13941, 'rst66', '2sa', 21, 16, 'Тогда Иесвий, один из потомков Рефаимов, у которого копье было весом в триста сиклей меди и который опоясан был новым мечом, хотел поразить Давида.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13940, 'rst66', '2sa', 21, 15, 'И открылась снова война между Филистимлянами и Израильтянами. И вышел Давид и слуги его с ним, и воевали с Филистимлянами; и Давид утомился.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13939, 'rst66', '2sa', 21, 14, 'И похоронили кости Саула и Ионафана, сына его, в земле Вениаминовой, в Цела, во гробе Киса, отца его. И сделали всё, что повелел царь, и умилостивился Бог над страною после того.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13938, 'rst66', '2sa', 21, 13, 'И перенес он оттуда кости Саула и кости Ионафана, сына его; и собрали кости повешенных.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13937, 'rst66', '2sa', 21, 12, 'И пошел Давид и взял кости Саула и кости Ионафана, сына его, у жителей Иависа Галаадского, которые тайно взяли их с площади Беф‐Сана, где они были повешены Филистимлянами, когда убили Филистимляне Саула на Гелвуе.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13936, 'rst66', '2sa', 21, 11, 'И донесли Давиду, что сделала Рицпа, дочь Айя, наложница Саула.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13935, 'rst66', '2sa', 21, 10, 'Тогда Рицпа, дочь Айя, взяла вретище и разостлала его себе на той горе <i>и сидела</i> от начала жатвы до того времени, пока не полились на них воды Божии с неба, и не допускала касаться их птицам небесным днем и зверям полевым ночью.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13934, 'rst66', '2sa', 21, 9, 'и отдал их в руки Гаваонитян, и они повесили их на горе пред Господом. И погибли все семь вместе; они умерщвлены в первые дни жатвы, в начале жатвы ячменя.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13933, 'rst66', '2sa', 21, 8, 'И взял царь двух сыновей Рицпы, дочери Айя, которая родила Саулу Армона и Мемфивосфея, и пять сыновей Мелхолы, дочери Сауловой, которых она родила Адриэлу, сыну Верзеллия из Мехолы,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13932, 'rst66', '2sa', 21, 7, 'Но пощадил царь Мемфивосфея, сына Ионафана, сына Саулова, ради клятвы именем Господним, которая была между ними, между Давидом и Ионафаном, сыном Сауловым.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13931, 'rst66', '2sa', 21, 6, 'из его потомков выдай нам семь человек, и мы повесим их пред Господом в Гиве Саула, избранного Господом. И сказал царь: я выдам.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13930, 'rst66', '2sa', 21, 5, 'И сказали они царю: того человека, который губил нас и хотел истребить нас, чтобы не было нас ни в одном из пределов Израилевых, &mdash;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13929, 'rst66', '2sa', 21, 4, 'И сказали ему Гаваонитяне: не нужно нам ни серебра, ни золота от Саула или от дома его, и не нужно нам, чтоб умертвили кого в Израиле. Он сказал: чего же вы хотите? я сделаю для вас.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13928, 'rst66', '2sa', 21, 3, 'И сказал Давид Гаваонитянам: что мне сделать для вас, и чем примирить вас, чтобы вы благословили наследие Господне?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13927, 'rst66', '2sa', 21, 2, 'Тогда царь призвал Гаваонитян и говорил с ними. Гаваонитяне были не из сынов Израилевых, но из остатков Аморреев; Израильтяне же дали им клятву, но Саул хотел истребить их по ревности своей о потомках Израиля и Иуды.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13926, 'rst66', '2sa', 21, 1, 'Был голод на земле во дни Давида три года, год за годом. И вопросил Давид Господа. И сказал Господь: это ради Саула и кровожадного дома его, за то, что он умертвил Гаваонитян.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13925, 'rst66', '2sa', 20, 26, 'также и Ира Иаритянин был священником у Давида.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13924, 'rst66', '2sa', 20, 25, 'Суса &mdash; писцом; Садок и Авиафар &mdash; священниками;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13923, 'rst66', '2sa', 20, 24, 'Адорам &mdash; над сбором податей; Иосафат, сын Ахилуда, &mdash; дееписателем;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13922, 'rst66', '2sa', 20, 23, 'И был Иоав <i>поставлен</i> над всем войском Израильским, а Ванея, сын Иодаев, &mdash; над Хелефеями и над Фелефеями;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13921, 'rst66', '2sa', 20, 22, 'И пошла женщина по всему народу со своим умным словом; и отсекли голову Савею, сыну Бихри, и бросили Иоаву. Тогда Иоав затрубил трубою, и разошлись от города все <i>люди</i> по своим шатрам; Иоав же возвратился в Иерусалим к царю.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13920, 'rst66', '2sa', 20, 21, 'Это не так; но человек с горы Ефремовой, по имени Савей, сын Бихри, поднял руку свою на царя Давида; выдайте мне его одного, и я отступлю от города. И сказала женщина Иоаву: вот, голова его <i>будет</i> тебе брошена со стены.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13919, 'rst66', '2sa', 20, 20, 'И отвечал Иоав и сказал: да не будет этого от меня, чтобы я уничтожил или разрушил!');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13918, 'rst66', '2sa', 20, 19, 'Я из мирных, верных <i>городов</i> Израиля; а ты хочешь уничтожить город, и <i>притом</i> мать <i>городов</i> в Израиле; для чего тебе разрушать наследие Господне?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13917, 'rst66', '2sa', 20, 18, 'Она сказала: прежде говаривали: «кто хочет спросить, спроси в Авеле»; и так решали дело.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13916, 'rst66', '2sa', 20, 17, 'И подошел к ней Иоав, и сказала женщина: ты ли Иоав? И сказал: я. Она сказала: послушай слов рабы твоей. И сказал он: слушаю.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13915, 'rst66', '2sa', 20, 16, '<i>Тогда</i> одна умная женщина закричала со стены города: послушайте, послушайте, скажите Иоаву, чтобы он подошел сюда, и я поговорю с ним.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13914, 'rst66', '2sa', 20, 15, 'И пришли и осадили его в Авеле‐Беф‐Маахе; и насыпали вал пред городом и подступили к стене, и все люди, бывшие с Иоавом, старались разрушить стену.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13913, 'rst66', '2sa', 20, 14, 'А он прошел чрез все колена Израильские до Авела‐Беф‐Мааха и чрез весь Берим; и люди собирались и шли за ним.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13912, 'rst66', '2sa', 20, 13, 'Но когда он был стащен с дороги, то весь народ Израильский пошел вслед за Иоавом преследовать Савея, сына Бихри.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13911, 'rst66', '2sa', 20, 12, 'Амессай же лежал в крови среди дороги; и тот человек, увидев, что весь народ останавливается над ним, стащил Амессая с дороги в поле и набросил на него одежду, так как он видел, что всякий проходящий останавливался над ним.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13910, 'rst66', '2sa', 20, 11, 'Один из отроков Иоавовых стоял над <i>Амессаем</i> и говорил: тот, кто предан Иоаву и кто за Давида, <i>пусть идет</i> за Иоавом!');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13909, 'rst66', '2sa', 20, 10, 'Амессай же не остерегся меча, бывшего в руке Иоава, и тот поразил его им в живот, так что выпали внутренности его на землю, и не повторил ему <i>удара</i>, и он умер. Иоав и Авесса, брат его, погнались за Савеем, сыном Бихри.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13908, 'rst66', '2sa', 20, 9, 'И сказал Иоав Амессаю: здоров ли ты, брат мой? И взял Иоав правою рукою Амессая за бороду, чтобы поцеловать его.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13907, 'rst66', '2sa', 20, 8, 'И когда они были близ большого камня, что у Гаваона, то встретился с ними Амессай. Иоав был одет в воинское одеяние свое и препоясан мечом, который висел при бедре в ножнах и который легко выходил из них и входил.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13906, 'rst66', '2sa', 20, 7, 'И вышли за ним люди Иоавовы, и Хелефеи и Фелефеи, и все храбрые пошли из Иерусалима преследовать Савея, сына Бихри.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13905, 'rst66', '2sa', 20, 6, 'Тогда Давид сказал Авессе: теперь наделает нам зла Савей, сын Бихри, больше, нежели Авессалом; возьми ты слуг господина твоего и преследуй его, чтобы он не нашел себе укрепленных городов и не скрылся от глаз наших.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13904, 'rst66', '2sa', 20, 5, 'И пошел Амессай созвать Иудеев, но промедлил более назначенного ему времени.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13903, 'rst66', '2sa', 20, 4, 'И сказал Давид Амессаю: созови ко мне Иудеев в течение трех дней и сам явись сюда.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13902, 'rst66', '2sa', 20, 3, 'И пришел Давид в свой дом в Иерусалиме, и взял царь десять жен наложниц, которых он оставлял стеречь дом, и поместил их в особый дом под надзор, и содержал их, но не ходил к ним. И содержались они там до дня смерти своей, живя как вдовы.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13901, 'rst66', '2sa', 20, 2, 'И отделились все Израильтяне от Давида <i>и пошли</i> за Савеем, сыном Бихри; Иудеи же остались на стороне царя своего, от Иордана до Иерусалима.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13900, 'rst66', '2sa', 20, 1, 'Там случайно находился один негодный человек, по имени Савей, сын Бихри, Вениамитянин; он затрубил трубою и сказал: нет нам части в Давиде, и нет нам доли в сыне Иессеевом; все по шатрам своим, Израильтяне!');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13899, 'rst66', '2sa', 19, 43, 'И отвечали Израильтяне мужам Иудиным и сказали: мы десять частей у царя, также и у Давида мы более, нежели вы; зачем же вы унизили нас? Не нам ли принадлежало первое слово о том, чтобы возвратить нашего царя? Но слово мужей Иудиных было сильнее, нежели слово Израильтян.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13898, 'rst66', '2sa', 19, 42, 'И отвечали все мужи Иудины Израильтянам: затем, что царь ближний нам; и из‐за чего сердиться вам на это? Разве мы что‐нибудь съели у царя или получили от него подарки?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13897, 'rst66', '2sa', 19, 41, 'И вот, все Израильтяне пришли к царю и сказали царю: зачем братья наши, мужи Иудины, похитили тебя и проводили царя и дом его и всех людей Давида с ним через Иордан?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13896, 'rst66', '2sa', 19, 40, 'И отправился царь в Галгал, отправился с ним и Кимгам; и весь народ Иудейский провожал царя, и половина народа Израильского.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13895, 'rst66', '2sa', 19, 39, 'И перешел весь народ Иордан, и царь <i>также</i>. И поцеловал царь Верзеллия и благословил его, и он возвратился в место свое.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13894, 'rst66', '2sa', 19, 38, 'И сказал царь: пусть идет со мною Кимгам, и я сделаю для него, что тебе угодно; и все, чего бы ни пожелал ты от меня, я сделаю для тебя.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13893, 'rst66', '2sa', 19, 37, 'Позволь рабу твоему возвратиться, чтобы умереть в своем городе, около гроба отца моего и матери моей. Но вот, раб твой Кимгам пусть пойдет с господином моим, царем, и поступи с ним, как тебе угодно.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13892, 'rst66', '2sa', 19, 36, 'Еще немного пройдет раб твой с царем за Иордан; за что же царю награждать меня такою милостью?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13891, 'rst66', '2sa', 19, 35, 'Мне теперь восемьдесят лет; различу ли хорошее от худого? Узнает ли раб твой вкус в том, что буду есть, и в том, что буду пить? И буду ли в состоянии слышать голос певцов и певиц? Зачем же рабу твоему быть в тягость господину моему, царю?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13890, 'rst66', '2sa', 19, 34, 'Но Верзеллий отвечал царю: долго ли мне осталось жить, чтоб идти с царем в Иерусалим?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13889, 'rst66', '2sa', 19, 33, 'И сказал царь Верзеллию: иди со мною, и я буду продовольствовать тебя в Иерусалиме.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13888, 'rst66', '2sa', 19, 32, 'Верзеллий же был очень стар, лет восьмидесяти. Он продовольствовал царя в пребывание его в Маханаиме, потому что был человек богатый.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13887, 'rst66', '2sa', 19, 31, 'И Верзеллий Галаадитянин пришел из Роглима и перешел с царем Иордан, чтобы проводить его за Иордан.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13886, 'rst66', '2sa', 19, 30, 'Но Мемфивосфей отвечал царю: пусть он возьмет даже все, после того как господин мой, царь, с миром возвратился в дом свой.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13885, 'rst66', '2sa', 19, 29, 'И сказал ему царь: к чему ты говоришь все это? Я сказал, чтобы ты и Сива разделили <i>между собою</i> поля.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13884, 'rst66', '2sa', 19, 28, 'хотя весь дом отца моего был повинен смерти пред господином моим, царем, но ты посадил раба твоего между ядущими за столом твоим; какое же имею я право жаловаться еще пред царем?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13883, 'rst66', '2sa', 19, 27, 'А он оклеветал раба твоего пред господином моим, царем. Но господин мой, царь, как Ангел Божий; делай, что тебе угодно;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13882, 'rst66', '2sa', 19, 26, 'Тот отвечал: господин мой, царь! слуга мой обманул меня; ибо я, раб твой, говорил: «оседлаю себе осла, и сяду на нем, и поеду с царем», так как раб твой хром.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13881, 'rst66', '2sa', 19, 25, 'Когда он вышел из Иерусалима навстречу царю, царь сказал ему: почему ты, Мемфивосфей, не пошел со мною?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13880, 'rst66', '2sa', 19, 24, 'И Мемфивосфей, сын <i>Ионафана, сына</i> Саулова, вышел навстречу царю. Он не омывал ног своих, не заботился о бороде своей и не мыл одежд своих с того дня, как вышел царь, до дня, когда он возвратился с миром.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13879, 'rst66', '2sa', 19, 23, 'И сказал царь Семею: ты не умрешь. И поклялся ему царь.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13878, 'rst66', '2sa', 19, 22, 'И сказал Давид: что мне и вам, сыны Саруины, что вы делаетесь ныне мне наветниками? Ныне ли умерщвлять кого‐либо в Израиле? Не вижу ли я, что ныне я &mdash; царь над Израилем?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13877, 'rst66', '2sa', 19, 21, 'И отвечал Авесса, сын Саруин, и сказал: неужели Семей не умрет за то, что злословил помазанника Господня?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13876, 'rst66', '2sa', 19, 20, 'ибо знает раб твой, что согрешил, и вот, ныне я пришел первый из всего дома Иосифова, чтобы выйти навстречу господину моему, царю.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13875, 'rst66', '2sa', 19, 19, 'и сказал царю: не поставь мне, господин мой, в преступление, и не помяни того, чем согрешил раб твой в тот день, когда господин мой, царь, выходил из Иерусалима, и не держи <i>того</i>, царь, на сердце своем;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13874, 'rst66', '2sa', 19, 18, 'Когда переправили судно, чтобы перевезти дом царя и послужить ему, тогда Семей, сын Геры, пал пред царем, как только он перешел Иордан,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13873, 'rst66', '2sa', 19, 17, 'и тысяча человек из Вениамитян с ним, и Сива, слуга дома Саулова, с пятнадцатью сыновьями своими и двадцатью рабами своими; и перешли они Иордан пред лицом царя.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13872, 'rst66', '2sa', 19, 16, 'И поспешил Семей, сын Геры, Вениамитянин из Бахурима, и пошел с Иудеями навстречу царю Давиду,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13871, 'rst66', '2sa', 19, 15, 'И возвратился царь, и пришел к Иордану, а Иудеи пришли в Галгал, чтобы встретить царя и перевезти царя чрез Иордан.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13870, 'rst66', '2sa', 19, 14, 'И склонил он сердце всех Иудеев, как одного человека; и послали они к царю <i>сказать:</i> возвратись ты и все слуги твои.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13869, 'rst66', '2sa', 19, 13, 'И Амессаю скажите: не кость ли моя и плоть моя &mdash; ты? Пусть то и то сделает со мною Бог и еще больше сделает, если ты не будешь военачальником при мне, вместо Иоава, навсегда!');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13868, 'rst66', '2sa', 19, 12, 'Вы братья мои, кости мои и плоть моя &mdash; вы; зачем хотите вы быть последними в возвращении царя в дом его?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13867, 'rst66', '2sa', 19, 11, 'И царь Давид послал сказать священникам Садоку и Авиафару: скажите старейшинам Иудиным: зачем хотите вы быть последними, чтобы возвратить царя в дом его, тогда как слова всего Израиля дошли до царя в дом его?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13866, 'rst66', '2sa', 19, 10, 'Но Авессалом, которого мы помазали <i>в царя</i> над нами, умер на войне; почему же теперь вы медлите возвратить царя?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13865, 'rst66', '2sa', 19, 9, 'И весь народ во всех коленах Израилевых спорил и говорил: царь избавил нас от рук врагов наших и освободил нас от рук Филистимлян, а теперь сам бежал из земли сей от Авессалома.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13864, 'rst66', '2sa', 19, 8, 'И встал царь и сел у ворот, а всему народу возвестили, что царь сидит у ворот. И пришел весь народ пред лицо царя. Израильтяне же разбежались по своим шатрам.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13863, 'rst66', '2sa', 19, 7, 'итак, встань, выйди и поговори к сердцу рабов твоих, ибо, клянусь Господом, что, если ты не выйдешь, в эту ночь не останется у тебя ни одного человека; и это будет для тебя хуже всех бедствий, какие находили на тебя от юности твоей доныне.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13862, 'rst66', '2sa', 19, 6, 'ты любишь ненавидящих тебя и ненавидишь любящих тебя, ибо ты показал сегодня, что ничто для тебя и вожди и слуги; сегодня я узнал, что если бы Авессалом остался жив, а мы все умерли, то тебе было бы приятнее;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13861, 'rst66', '2sa', 19, 5, 'И пришел Иоав к царю в дом и сказал: ты в стыд привел сегодня всех слуг твоих, спасших ныне жизнь твою и жизнь сыновей и дочерей твоих, и жизнь жен и жизнь наложниц твоих;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13860, 'rst66', '2sa', 19, 4, 'А царь закрыл лицо свое и громко взывал: сын мой, Авессалом! Авессалом, сын мой, сын мой!');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13859, 'rst66', '2sa', 19, 3, 'И входил тогда народ в город украдкою, как крадутся люди стыдящиеся, которые во время сражения обратились в бегство.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13858, 'rst66', '2sa', 19, 2, 'И обратилась победа того дня в плач для всего народа; ибо народ услышал в тот день и говорил, что царь скорбит о своем сыне.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13857, 'rst66', '2sa', 19, 1, 'И сказали Иоаву: вот, царь плачет и рыдает об Авессаломе.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13856, 'rst66', '2sa', 18, 33, 'И смутился царь, и пошел в горницу над воротами, и плакал, и когда шел, говорил так: сын мой, Авессалом! сын мой, сын мой, Авессалом! о, кто дал бы мне умереть вместо тебя, Авессалом, сын мой, сын мой!');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13855, 'rst66', '2sa', 18, 32, 'И сказал царь Хусию: благополучен ли отрок Авессалом? И сказал Хусий: да будет с врагами господина моего, царя, и со всеми, злоумышляющими против тебя, то же, что постигло отрока!');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13854, 'rst66', '2sa', 18, 31, 'Вот, пришел и Хусий. И сказал Хусий: добрая весть господину моему, царю! Господь явил тебе ныне правду в избавлении от руки всех восставших против тебя.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13853, 'rst66', '2sa', 18, 30, 'И сказал царь: отойди, стань здесь. Он отошел и стал.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13852, 'rst66', '2sa', 18, 29, 'И сказал царь: благополучен ли отрок Авессалом? И сказал Ахимаас: я видел большое волнение, когда раб царев Иоав посылал раба твоего; но я не знаю, что <i>там</i> было.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13851, 'rst66', '2sa', 18, 28, 'И воскликнул Ахимаас и сказал царю: мир. И поклонился царю лицом своим до земли и сказал: благословен Господь, Бог твой, предавший людей, которые подняли руки свои на господина моего, царя!');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13850, 'rst66', '2sa', 18, 27, 'Сторож сказал: я вижу походку первого, похожую на походку Ахимааса, сына Садокова. И сказал царь: это человек хороший и идет с хорошею вестью.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13849, 'rst66', '2sa', 18, 26, 'Сторож увидел и другого бегущего человека; и закричал сторож привратнику: вот, еще бежит один человек. Царь сказал: и это &mdash; вестник.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13848, 'rst66', '2sa', 18, 25, 'И закричал сторож и известил царя. И сказал царь: если один, то весть в устах его. А тот подходил все ближе и ближе.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13847, 'rst66', '2sa', 18, 24, 'Давид тогда сидел между двумя воротами. И сторож взошел на кровлю ворот к стене и, подняв глаза, увидел: вот, бежит один человек.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13846, 'rst66', '2sa', 18, 23, '<i>И сказал Ахимаас:</i> пусть так, но я побегу. И сказал ему <i>Иоав</i>: беги. И побежал Ахимаас по прямой дороге и опередил Хусия.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13845, 'rst66', '2sa', 18, 22, 'Но Ахимаас, сын Садоков, настаивал и говорил Иоаву: что бы ни было, но и я побегу за Хусием. Иоав же отвечал: зачем бежать тебе, сын мой? не принесешь ты доброй вести.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13844, 'rst66', '2sa', 18, 21, 'И сказал Иоав Хусию: пойди, донеси царю, что видел ты. И поклонился Хусий Иоаву и побежал.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13843, 'rst66', '2sa', 18, 20, 'Но Иоав сказал ему: не будешь ты сегодня добрым вестником; известишь в другой день, а не сегодня, ибо умер сын царя.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13842, 'rst66', '2sa', 18, 19, 'Ахимаас, сын Садоков, сказал Иоаву: побегу я, извещу царя, что Господь судом Своим избавил его от рук врагов его.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13841, 'rst66', '2sa', 18, 18, 'Авессалом еще при жизни своей взял и поставил себе памятник в царской долине; ибо сказал он: нет у меня сына, чтобы сохранилась память имени моего. И назвал памятник своим именем. И называется он «памятник Авессалома» до сего дня.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13840, 'rst66', '2sa', 18, 17, 'И взяли Авессалома, и бросили его в лесу в глубокую яму, и наметали над ним огромную кучу камней. И все Израильтяне разбежались, каждый в шатер свой.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13839, 'rst66', '2sa', 18, 16, 'И затрубил Иоав трубою, и возвратились люди из погони за Израилем, ибо Иоав щадил народ.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13838, 'rst66', '2sa', 18, 15, 'И окружили Авессалома десять отроков, оруженосцев Иоава, и поразили и умертвили его.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13837, 'rst66', '2sa', 18, 14, 'Иоав сказал: нечего мне медлить с тобою. И взял в руки три стрелы и вонзил их в сердце Авессалома, который был еще жив на дубе.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13836, 'rst66', '2sa', 18, 13, 'и если бы я поступил иначе с опасностью жизни моей, то это не скрылось бы от царя, и ты же восстал бы против меня.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13835, 'rst66', '2sa', 18, 12, 'И отвечал тот Иоаву: если бы положили на руки мои и тысячу сиклей серебра, и тогда я не поднял бы руки на царского сына; ибо вслух нас царь приказывал тебе и Авессе и Еффею, говоря: «сберегите мне отрока Авессалома»;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13834, 'rst66', '2sa', 18, 11, 'И сказал Иоав человеку, донесшему об этом: вот, ты видел; зачем же ты не поверг его там на землю? Я дал бы тебе десять сиклей серебра и один пояс.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13833, 'rst66', '2sa', 18, 10, 'И увидел это некто и донес Иоаву, говоря: вот, я видел Авессалома висящим на дубе.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13832, 'rst66', '2sa', 18, 9, 'И встретился Авессалом с рабами Давидовыми; он был на муле. Когда мул вбежал с ним под ветви большого дуба, то <i>Авессалом</i> запутался волосами своими в ветвях дуба и повис между небом и землею, а мул, бывший под ним, убежал.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13831, 'rst66', '2sa', 18, 8, 'Сражение распространилось по всей той стране, и лес погубил народа больше, чем сколько истребил меч, в тот день.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13830, 'rst66', '2sa', 18, 7, 'И был поражен народ Израильский рабами Давида; было там поражение великое в тот день, &mdash; поражены двадцать тысяч <i>человек</i>.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13829, 'rst66', '2sa', 18, 6, 'И вышли люди в поле навстречу Израильтянам, и было сражение в лесу Ефремовом.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13828, 'rst66', '2sa', 18, 5, 'И приказал царь Иоаву и Авессе и Еффею, говоря: сберегите мне отрока Авессалома. И все люди слышали, как приказывал царь всем начальникам об Авессаломе.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13827, 'rst66', '2sa', 18, 4, 'И сказал им царь: что угодно в глазах ваших, то и сделаю. И стал царь у ворот, и весь народ выходил по сотням и по тысячам.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13826, 'rst66', '2sa', 18, 3, 'Но люди отвечали ему: не ходи; ибо, если мы и побежим, то не обратят внимания на это; если и умрет половина из нас, также не обратят внимания; а ты один то же, что нас десять тысяч; итак, для нас лучше, чтобы ты помогал нам из города.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13825, 'rst66', '2sa', 18, 2, 'И отправил Давид людей &mdash; третью часть под предводительством Иоава, третью часть под предводительством Авессы, сына Саруина, брата Иоава, третью часть под предводительством Еффея Гефянина. И сказал царь людям: я сам пойду с вами.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13824, 'rst66', '2sa', 18, 1, 'И осмотрел Давид людей, бывших с ним, и поставил над ними тысяченачальников и сотников.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13823, 'rst66', '2sa', 17, 29, 'и меду, и масла, и овец, и сыра коровьего, принесли Давиду и людям, бывшим с ним, в пищу; ибо говорили они: народ голоден и утомлен и терпел жажду в пустыне.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13822, 'rst66', '2sa', 17, 28, 'принесли постелей, блюд и глиняных сосудов, и пшеницы, и ячменя, и муки, и пшена, и бобов, и чечевицы, и жареных зерен,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13821, 'rst66', '2sa', 17, 27, 'Когда Давид пришел в Маханаим, то Сови, сын Нааса, из Раввы Аммонитской, и Махир, сын Аммиила, из Лодавара, и Верзеллий Галаадитянин, из Роглима,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13820, 'rst66', '2sa', 17, 26, 'И Израиль с Авессаломом расположился станом в земле Галаадской.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13819, 'rst66', '2sa', 17, 25, 'Авессалом поставил Амессая, вместо Иоава, над войском. Амессай был сын одного человека, по имени Иефера из Изрееля, который вошел к Авигее, дочери Нааса, сестре Саруи, матери Иоава.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13818, 'rst66', '2sa', 17, 24, 'И пришел Давид в Маханаим, а Авессалом перешел Иордан, сам и весь Израиль с ним.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13817, 'rst66', '2sa', 17, 23, 'И увидел Ахитофел, что не исполнен совет его, и оседлал осла, и собрался, и пошел в дом свой, в город свой, и сделал завещание дому своему, и удавился, и умер, и был погребен в гробе отца своего.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13816, 'rst66', '2sa', 17, 22, 'И встал Давид и все люди, бывшие с ним, и перешли Иордан; к рассвету не осталось ни одного, который не перешел бы Иордана.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13815, 'rst66', '2sa', 17, 21, 'Когда они ушли, те вышли из колодезя, пошли и известили царя Давида и сказали Давиду: встаньте и поскорее перейдите воду; ибо так и так советовал о вас Ахитофел.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13814, 'rst66', '2sa', 17, 20, 'И пришли рабы Авессалома к женщине в дом, и сказали: где Ахимаас и Ионафан? И сказала им женщина: они перешли вброд реку. И искали они, и не нашли, и возвратились в Иерусалим.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13813, 'rst66', '2sa', 17, 19, 'А женщина взяла и растянула над устьем колодезя покрывало и насыпала на него крупы, так что не было ничего заметно.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13812, 'rst66', '2sa', 17, 18, 'И увидел их отрок и донес Авессалому; но они оба скоро ушли и пришли в Бахурим, в дом одного человека, у которого на дворе был колодезь, и спустились туда.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13811, 'rst66', '2sa', 17, 17, 'Ионафан и Ахимаас стояли у источника Рогель. И пошла служанка и рассказала им, а они пошли и известили царя Давида; ибо они не могли показаться в городе.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13810, 'rst66', '2sa', 17, 16, 'И теперь пошлите поскорее и скажите Давиду так: не оставайся в эту ночь на равнине в пустыне, но поскорее перейди, чтобы не погибнуть царю и всем людям, которые с ним.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13809, 'rst66', '2sa', 17, 15, 'И сказал Хусий Садоку и Авиафару священникам: так и так советовал Ахитофел Авессалому и старейшинам Израилевым, а так и так посоветовал я.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13808, 'rst66', '2sa', 17, 14, 'И сказал Авессалом и весь Израиль: совет Хусия Архитянина лучше совета Ахитофелова. Так Господь судил разрушить лучший совет Ахитофела, чтобы навести Господу бедствие на Авессалома.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13807, 'rst66', '2sa', 17, 13, 'а если он войдет в какой‐либо город, то весь Израиль принесет к тому городу веревки, и мы стащим его в реку, так что не останется ни одного камешка.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13806, 'rst66', '2sa', 17, 12, 'и тогда мы пойдем против него, в каком бы месте он ни находился, и нападем на него, как падает роса на землю; и не останется у него ни одного человека из всех, которые с ним;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13805, 'rst66', '2sa', 17, 11, 'Посему я советую: пусть соберется к тебе весь Израиль, от Дана до Вирсавии, во множестве, как песок при море, и ты сам пойдешь посреди его;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13804, 'rst66', '2sa', 17, 10, 'тогда и самый храбрый, у которого сердце, как сердце львиное, упадет духом; ибо всему Израилю известно, как храбр отец твой и мужественны те, которые с ним.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13803, 'rst66', '2sa', 17, 9, 'Вот, теперь он скрывается в какой‐нибудь пещере или в другом месте, и если кто падет при первом нападении на них, и услышат и скажут: «было поражение людей, последовавших за Авессаломом»,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13802, 'rst66', '2sa', 17, 8, 'И продолжал Хусий: ты знаешь твоего отца и людей его; они храбры и сильно раздражены, как медведица в поле, у которой отняли детей, и отец твой &mdash; человек воинственный; он не остановится ночевать с народом.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13801, 'rst66', '2sa', 17, 7, 'И сказал Хусий Авессалому: нехорош на этот раз совет, который дал Ахитофел.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13800, 'rst66', '2sa', 17, 6, 'И пришел Хусий к Авессалому, и сказал ему Авессалом, говоря: вот что говорит Ахитофел; сделать ли по его словам? а если нет, то говори ты.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13799, 'rst66', '2sa', 17, 5, 'И сказал Авессалом: позовите Хусия Архитянина; послушаем, что он скажет.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13798, 'rst66', '2sa', 17, 4, 'И понравилось это слово Авессалому и всем старейшинам Израилевым.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13797, 'rst66', '2sa', 17, 3, 'и всех людей обращу к тебе; и когда не будет одного, душу которого ты ищешь, тогда весь народ будет в мире.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13796, 'rst66', '2sa', 17, 2, 'и нападу на него, когда он будет утомлен и с опущенными руками, и приведу его в страх; и все люди, которые с ним, разбегутся; и я убью одного царя');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13795, 'rst66', '2sa', 17, 1, 'И сказал Ахитофел Авессалому: выберу я двенадцать тысяч человек и встану и пойду в погоню за Давидом в эту ночь;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13794, 'rst66', '2sa', 16, 23, 'Советы же Ахитофела, которые он давал, в то время <i>считались</i>, как если бы кто спрашивал наставления у Бога. Таков был всякий совет Ахитофела как для Давида, так и для Авессалома.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13793, 'rst66', '2sa', 16, 22, 'И поставили для Авессалома палатку на кровле, и вошел Авессалом к наложницам отца своего пред глазами всего Израиля.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13792, 'rst66', '2sa', 16, 21, 'И сказал Ахитофел Авессалому: войди к наложницам отца твоего, которых он оставил охранять дом свой; и услышат все Израильтяне, что ты сделался ненавистным для отца твоего, и укрепятся руки всех, которые с тобою.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13791, 'rst66', '2sa', 16, 20, 'И сказал Авессалом Ахитофелу: дайте совет, что нам делать.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13790, 'rst66', '2sa', 16, 19, 'И притом кому я буду служить? Не сыну ли его? Как служил я отцу твоему, так буду служить и тебе.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13789, 'rst66', '2sa', 16, 18, 'И сказал Хусий Авессалому: нет, кого избрал Господь и этот народ и весь Израиль, с тем и я, и с ним останусь.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13788, 'rst66', '2sa', 16, 17, 'И сказал Авессалом Хусию: таково‐то усердие твое к твоему другу! отчего ты не пошел с другом твоим?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13787, 'rst66', '2sa', 16, 16, 'Когда Хусий Архитянин, друг Давидов, пришел к Авессалому, то сказал Хусий Авессалому: да живет царь, да живет царь!');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13786, 'rst66', '2sa', 16, 15, 'Авессалом же и весь народ Израильский пришли в Иерусалим, и Ахитофел с ним.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13785, 'rst66', '2sa', 16, 14, 'И пришел царь и весь народ, бывший с ним, утомленный, и отдыхал там.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13784, 'rst66', '2sa', 16, 13, 'И шел Давид и люди его <i>своим</i> путем, а Семей шел по окраине горы, со стороны его, шел и злословил, и бросал камнями на сторону его и пылью.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13783, 'rst66', '2sa', 16, 12, 'может быть, Господь призрит на уничижение мое, и воздаст мне Господь благостью за теперешнее его злословие.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13782, 'rst66', '2sa', 16, 11, 'И сказал Давид Авессе и всем слугам своим: вот, если мой сын, который вышел из чресл моих, ищет души моей, тем больше сын Вениамитянина; оставьте его, пусть злословит, ибо Господь повелел ему;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13781, 'rst66', '2sa', 16, 10, 'И сказал царь: что мне и вам, сыны Саруины? пусть он злословит, ибо Господь повелел ему злословить Давида. Кто же может сказать: «зачем ты так делаешь?»');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13780, 'rst66', '2sa', 16, 9, 'И сказал Авесса, сын Саруин, царю: зачем злословит этот мертвый пес господина моего, царя? пойду я и сниму с него голову.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13779, 'rst66', '2sa', 16, 8, 'Господь обратил на тебя всю кровь дома Саулова, вместо которого ты воцарился, и предал Господь царство в руки Авессалома, сына твоего; и вот, ты в беде, ибо ты &mdash; кровопийца.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13778, 'rst66', '2sa', 16, 7, 'Так говорил Семей, злословя его: уходи, уходи, убийца и беззаконник!');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13777, 'rst66', '2sa', 16, 6, 'и бросал камнями на Давида и на всех рабов царя Давида; все же люди и все храбрые были по правую и по левую сторону <i>царя</i>.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13776, 'rst66', '2sa', 16, 5, 'Когда дошел царь Давид до Бахурима, вот, вышел оттуда человек из рода дома Саулова, по имени Семей, сын Геры; он шел и злословил,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13775, 'rst66', '2sa', 16, 4, 'И сказал царь Сиве: вот тебе все, что у Мемфивосфея. И отвечал Сива, поклонившись: да обрету милость в глазах господина моего, царя!');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13774, 'rst66', '2sa', 16, 3, 'И сказал царь: где сын господина твоего? И отвечал Сива царю: вот, он остался в Иерусалиме и говорит: теперь‐то дом Израилев возвратит мне царство отца моего.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13773, 'rst66', '2sa', 16, 2, 'И сказал царь Сиве: для чего это у тебя? И отвечал Сива: ослы для дома царского, для езды, а хлеб и плоды для пищи отрокам, а вино для питья ослабевшим в пустыне.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13772, 'rst66', '2sa', 16, 1, 'Когда Давид немного сошел с вершины горы, вот встречается ему Сива, слуга Мемфивосфея, с парою навьюченных ослов, и на них двести хлебов, сто связок изюму, сто связок смокв и мех с вином.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13771, 'rst66', '2sa', 15, 37, 'И пришел Хусий, друг Давидов, в город; Авессалом же вступал тогда в Иерусалим.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13770, 'rst66', '2sa', 15, 36, 'Там с ними и два сына их, Ахимаас, сын Садока, и Ионафан, сын Авиафара; через них посылайте ко мне всякое известие, какое услышите.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13769, 'rst66', '2sa', 15, 35, 'Вот, там с тобою Садок и Авиафар священники, и всякое слово, какое услышишь из дома царя, пересказывай Садоку и Авиафару священникам.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13768, 'rst66', '2sa', 15, 34, 'но если возвратишься в город и скажешь Авессалому: «царь, я раб твой; доселе я был рабом отца твоего, а теперь я &mdash; твой раб», то ты расстроишь для меня совет Ахитофела.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13767, 'rst66', '2sa', 15, 33, 'И сказал ему Давид: если ты пойдешь со мною, то будешь мне в тягость;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13766, 'rst66', '2sa', 15, 32, 'Когда Давид взошел на вершину горы, где он поклонялся Богу, вот навстречу ему идет Хусий Архитянин, друг Давидов; одежда на нем была разодрана, и прах на голове его.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13765, 'rst66', '2sa', 15, 31, 'Донесли Давиду и сказали: и Ахитофел в числе заговорщиков с Авессаломом. И сказал Давид: Господи! разрушь совет Ахитофела.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13764, 'rst66', '2sa', 15, 30, 'А Давид пошел на гору Елеонскую, шел и плакал; голова у него была покрыта; он шел босой, и все люди, бывшие с ним, покрыли каждый голову свою, шли и плакали.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13763, 'rst66', '2sa', 15, 29, 'И возвратили Садок и Авиафар ковчег Божий в Иерусалим, и остались там.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13762, 'rst66', '2sa', 15, 28, 'видите ли, я помедлю на равнине в пустыне, доколе не придет известие от вас ко мне.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13761, 'rst66', '2sa', 15, 27, 'И сказал царь Садоку священнику: видишь ли, возвратись в город с миром, и Ахимаас, сын твой, и Ионафан, сын Авиафара, оба сына ваши с вами;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13760, 'rst66', '2sa', 15, 26, 'А если Он скажет так: «нет Моего благоволения к тебе», то вот я; пусть творит со мною, что Ему благоугодно.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13759, 'rst66', '2sa', 15, 25, 'И сказал царь Садоку: возврати ковчег Божий в город. Если я обрету милость пред очами Господа, то Он возвратит меня и даст мне видеть его и жилище его.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13758, 'rst66', '2sa', 15, 24, 'Вот и Садок, и все левиты с ним несли ковчег завета Божия из Вефары и поставили ковчег Божий; Авиафар же стоял на возвышении, доколе весь народ не вышел из города.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13757, 'rst66', '2sa', 15, 23, 'И плакала вся земля громким голосом. И весь народ переходил, и царь перешел поток Кедрон; и пошел весь народ по дороге к пустыне.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13756, 'rst66', '2sa', 15, 22, 'И сказал Давид Еффею: итак, иди и ходи со мною. И пошел Еффей Гефянин и все люди его и все дети, бывшие с ним.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13755, 'rst66', '2sa', 15, 21, 'И отвечал Еффей царю и сказал: жив Господь, и да живет господин мой, царь: где бы ни был господин мой, царь, в жизни ли, в смерти ли, там будет и раб твой.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13754, 'rst66', '2sa', 15, 20, 'вчера ты пришел, а сегодня я заставлю тебя идти с нами? Я иду, куда случится; возвратись и возврати братьев своих с собою, милость и истина <i>с тобою</i>!');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13753, 'rst66', '2sa', 15, 19, 'И сказал царь Еффею Гефянину: зачем и ты идешь с нами? Возвратись и оставайся с тем царем; ибо ты &mdash; чужеземец и пришел сюда из своего места;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13752, 'rst66', '2sa', 15, 18, 'И все слуги его шли по сторонам его, и все Хелефеи, и все Фелефеи, и все Гефяне до шестисот человек, пришедшие вместе с ним из Гефа, шли впереди царя.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13751, 'rst66', '2sa', 15, 17, 'И вышел царь и весь народ пешие, и остановились у Беф‐Мерхата.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13750, 'rst66', '2sa', 15, 16, 'И вышел царь и весь дом его за ним пешком. Оставил же царь десять жен, наложниц <i>своих</i>, для хранения дома.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13749, 'rst66', '2sa', 15, 15, 'И сказали слуги царские царю: во всем, что угодно господину нашему, царю, мы &mdash; рабы твои.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13748, 'rst66', '2sa', 15, 14, 'И сказал Давид всем слугам своим, которые были при нем в Иерусалиме: встаньте, убежим, ибо не будет нам спасения от Авессалома; спешите, чтобы нам уйти, чтоб он не застиг и не захватил нас, и не навел на нас беды и не истребил города мечом.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13747, 'rst66', '2sa', 15, 13, 'И пришел вестник к Давиду, и сказал: сердце Израильтян уклонилось на сторону Авессалома.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13746, 'rst66', '2sa', 15, 12, 'Во время жертвоприношения Авессалом послал и призвал Ахитофела Гилонянина, советника Давидова, из его города Гило. И составился сильный заговор, и народ стекался и умножался около Авессалома.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13745, 'rst66', '2sa', 15, 11, 'С Авессаломом пошли из Иерусалима двести человек, которые были приглашены им, и пошли по простоте своей, не зная, в чем дело.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13744, 'rst66', '2sa', 15, 10, 'И разослал Авессалом лазутчиков во все колена Израилевы, сказав: когда вы услышите звук трубы, то говорите: «Авессалом воцарился в Хевроне».');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13743, 'rst66', '2sa', 15, 9, 'И сказал ему царь: иди с миром. И встал он и пошел в Хеврон.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13742, 'rst66', '2sa', 15, 8, 'ибо я, раб твой, живя в Гессуре в Сирии, дал обет: если Господь возвратит меня в Иерусалим, то я принесу жертву Господу.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13741, 'rst66', '2sa', 15, 7, 'По прошествии сорока лет <i>царствования Давида</i> Авессалом сказал царю: пойду я и исполню обет мой, который я дал Господу, в Хевроне;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13740, 'rst66', '2sa', 15, 6, 'Так поступал Авессалом со всяким Израильтянином, приходившим на суд к царю, и вкрадывался Авессалом в сердце Израильтян.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13739, 'rst66', '2sa', 15, 5, 'И когда подходил кто‐нибудь поклониться ему, то он простирал руку свою и обнимал его и целовал его.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13738, 'rst66', '2sa', 15, 4, 'И говорил Авессалом: о, если бы меня поставили судьею в этой земле! ко мне приходил бы всякий, кто имеет спор и тяжбу, и я судил бы его по правде.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13737, 'rst66', '2sa', 15, 3, 'тогда говорил ему Авессалом: вот, дело твое доброе и справедливое, но у царя некому выслушать тебя.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13736, 'rst66', '2sa', 15, 2, 'И вставал Авессалом рано утром, и становился при дороге у ворот, и когда кто‐нибудь, имея тяжбу, шел к царю на суд, то Авессалом подзывал его к себе и спрашивал: из какого города ты? И когда тот отвечал: «из такого‐то колена Израилева раб твой»,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13735, 'rst66', '2sa', 15, 1, 'После сего Авессалом завел у себя колесницы и лошадей и пятьдесят скороходов.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13734, 'rst66', '2sa', 14, 33, 'И пошел Иоав к царю и пересказал ему <i>это</i>. И позвал <i>царь</i> Авессалома; он пришел к царю и пал лицом своим на землю пред царем; и поцеловал царь Авессалома.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13733, 'rst66', '2sa', 14, 32, 'И сказал Авессалом Иоаву: вот, я посылал за тобою, говоря: приди сюда, и я пошлю тебя к царю сказать: зачем я пришел из Гессура? Лучше было бы мне оставаться там. Я хочу увидеть лицо царя. Если же я виноват, то убей меня.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13732, 'rst66', '2sa', 14, 31, 'И встал Иоав, и пришел к Авессалому в дом, и сказал ему: зачем слуги твои выжгли мой участок огнем?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13731, 'rst66', '2sa', 14, 30, 'И сказал <i>Авессалом</i> слугам своим: видите участок поля Иоава подле моего, и у него там ячмень; пойдите, выжгите его огнем. И выжгли слуги Авессалома тот участок поля огнем.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13730, 'rst66', '2sa', 14, 29, 'И послал Авессалом за Иоавом, чтобы послать его к царю, но тот не захотел прийти к нему. Послал и в другой раз; но тот не захотел прийти.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13729, 'rst66', '2sa', 14, 28, 'И оставался Авессалом в Иерусалиме два года, а лица царского не видал.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13728, 'rst66', '2sa', 14, 27, 'И родились у Авессалома три сына и одна дочь, по имени Фамарь; она была женщина красивая.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13727, 'rst66', '2sa', 14, 26, 'Когда он стриг голову свою, &mdash; а он стриг ее каждый год, потому что она отягощала его, &mdash; то волоса с головы его весили двести сиклей по весу царскому.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13726, 'rst66', '2sa', 14, 25, 'Не было во всем Израиле мужчины столь красивого, как Авессалом, и столько хвалимого, как он; от подошвы ног до верха головы его не было у него недостатка.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13725, 'rst66', '2sa', 14, 24, 'И сказал царь: пусть он возвратится в дом свой, а лица моего не видит. И пошел Авессалом в свой дом, а лица царского не видал.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13724, 'rst66', '2sa', 14, 23, 'И встал Иоав, и пошел в Гессур, и привел Авессалома в Иерусалим.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13723, 'rst66', '2sa', 14, 22, 'Тогда Иоав пал лицом на землю и поклонился, и благословил царя и сказал: теперь знает раб твой, что обрел благоволение пред очами твоими, господин мой, царь, так как царь сделал по слову раба своего.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13722, 'rst66', '2sa', 14, 21, 'И сказал царь Иоаву: вот, я сделал <i>по слову твоему</i>; пойди же, возврати отрока Авессалома.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13721, 'rst66', '2sa', 14, 20, 'чтобы притчею дать делу такой вид, раб твой Иоав научил меня; но господин мой мудр, как мудр Ангел Божий, чтобы знать все, что на земле.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13720, 'rst66', '2sa', 14, 19, 'И сказал царь: не рука ли Иоава во всем этом с тобою? И отвечала женщина и сказала: да живет душа твоя, господин мой, царь; ни направо, ни налево нельзя уклониться от того, что сказал господин мой, царь; точно, раб твой Иоав приказал мне, и он вложил в уста рабы твоей все эти слова;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13719, 'rst66', '2sa', 14, 18, 'И отвечал царь и сказал женщине: не скрой от меня, о чем я спрошу тебя. И сказала женщина: говори, господин мой, царь.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13718, 'rst66', '2sa', 14, 17, 'И сказала раба твоя: да будет слово господина моего, царя, в утешение мне, ибо господин мой, царь, как Ангел Божий, и может выслушать и доброе и худое. И Господь, Бог твой, будет с тобою.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13717, 'rst66', '2sa', 14, 16, 'верно, царь выслушает и избавит рабу свою от руки людей, <i>хотящих</i> истребить меня вместе с сыном моим из наследия Божия.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13716, 'rst66', '2sa', 14, 15, 'И теперь я пришла сказать царю, господину моему, эти слова, потому что народ пугает меня; и раба твоя сказала: поговорю я с царем, не сделает ли он по слову рабы своей;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13715, 'rst66', '2sa', 14, 14, 'Мы умрем и <i>будем</i> как вода, вылитая на землю, которую нельзя собрать; но Бог не желает погубить душу и помышляет, как бы не отвергнуть от Себя и отверженного.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13714, 'rst66', '2sa', 14, 13, 'Он сказал: говори. И сказала женщина: почему ты так мыслишь против народа Божия? Царь, произнеся это слово, обвинил себя самого, потому что не возвращает изгнанника своего.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13713, 'rst66', '2sa', 14, 12, 'И сказала женщина: позволь рабе твоей сказать <i>еще</i> слово господину моему, царю.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13712, 'rst66', '2sa', 14, 11, 'Она сказала: помяни, царь, Господа, Бога твоего, чтобы не умножились мстители за кровь и не погубили сына моего. И сказал <i>царь:</i> жив Господь! не падет и волос сына твоего на землю.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13711, 'rst66', '2sa', 14, 10, 'И сказал царь: того, кто будет против тебя, приведи ко мне, и он более не тронет тебя.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13710, 'rst66', '2sa', 14, 9, 'Но женщина Фекоитянка сказала царю: на мне, господин мой, царь, да будет вина и на доме отца моего, царь же и престол его неповинен.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13709, 'rst66', '2sa', 14, 8, 'И сказал царь женщине: иди спокойно домой, я дам приказание о тебе.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13708, 'rst66', '2sa', 14, 7, 'И вот, восстало все родство на рабу твою, и говорят: «отдай убийцу брата своего; мы убьем его за душу брата его, которую он погубил, и истребим даже наследника». И так они погасят остальную искру мою, чтобы не оставить мужу моему имени и потомства на лице земли.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13707, 'rst66', '2sa', 14, 6, 'и у рабы твоей <i>было</i> два сына; они поссорились в поле, и некому было разнять их, и поразил один другого и умертвил его.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13706, 'rst66', '2sa', 14, 5, 'И сказал ей царь: что тебе? И сказала она: я вдова, муж мой умер;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13705, 'rst66', '2sa', 14, 4, 'И вошла женщина Фекоитянка к царю и пала лицом своим на землю, и поклонилась и сказала: помоги, царь!');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13704, 'rst66', '2sa', 14, 3, 'и пойди к царю и скажи ему так и так. И вложил Иоав в уста ее, что сказать.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13703, 'rst66', '2sa', 14, 2, 'И послал Иоав в Фекою, и взял оттуда умную женщину и сказал ей: притворись плачущею и надень печальную одежду, и не мажься елеем, и представься женщиною, много дней плакавшею по умершем;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13702, 'rst66', '2sa', 14, 1, 'И заметил Иоав, сын Саруи, что сердце царя обратилось к Авессалому.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13701, 'rst66', '2sa', 13, 39, 'И не стал царь Давид преследовать Авессалома; ибо утешился о смерти Амнона.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13700, 'rst66', '2sa', 13, 38, 'Авессалом убежал и пришел в Гессур и пробыл там три года.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13699, 'rst66', '2sa', 13, 37, 'Авессалом же убежал и пошел к Фалмаю, сыну Емиуда, царю Гессурскому. И плакал Давид о сыне своем во все дни.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13698, 'rst66', '2sa', 13, 36, 'И едва только сказал он это, вот, пришли царские сыновья, и подняли вопль и плакали. И сам царь и все слуги его плакали очень великим плачем.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13697, 'rst66', '2sa', 13, 35, 'Тогда Ионадав сказал царю: это идут царские сыновья; как говорил раб твой, так и есть.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13696, 'rst66', '2sa', 13, 34, 'И убежал Авессалом. И поднял отрок, стоявший на страже, глаза свои, и увидел: вот, много народа идет по дороге по скату горы.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13695, 'rst66', '2sa', 13, 33, 'итак, пусть господин мой, царь, не тревожится мыслью о том, будто умерли все царские сыновья: умер один только Амнон.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13694, 'rst66', '2sa', 13, 32, 'Но Ионадав, сын Самая, брата Давидова, сказал: пусть не думает господин мой, что всех отроков, царских сыновей, умертвили; один только Амнон умер, ибо у Авессалома был этот замысел с того дня, как <i>Амнон</i> обесчестил сестру его;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13693, 'rst66', '2sa', 13, 31, 'И встал царь, и разодрал одежды свои, и повергся на землю, и все слуги его, предстоящие ему, разодрали одежды свои.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13692, 'rst66', '2sa', 13, 30, 'Когда они были еще на пути, дошел слух до Давида, что Авессалом умертвил всех царских сыновей, и не осталось ни одного из них.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13691, 'rst66', '2sa', 13, 29, 'И поступили отроки Авессалома с Амноном, как приказал Авессалом. Тогда встали все царские сыновья, сели каждый на мула своего и убежали.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13690, 'rst66', '2sa', 13, 28, 'Авессалом же приказал отрокам своим, сказав: смотрите, как только развеселится сердце Амнона от вина, и я скажу вам: «поразите Амнона», тогда убейте его, не бойтесь; это я приказываю вам, будьте смелы и мужественны.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13689, 'rst66', '2sa', 13, 27, 'Но Авессалом упросил его, и он отпустил с ним Амнона и всех царских сыновей.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13688, 'rst66', '2sa', 13, 26, 'И сказал ему Авессалом: по крайней мере, пусть пойдет с нами Амнон, брат мой. И сказал ему царь: зачем ему идти с тобою?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13687, 'rst66', '2sa', 13, 25, 'Но царь сказал Авессалому: нет, сын мой, мы не пойдем все, чтобы не быть тебе в тягость. И сильно упрашивал его <i>Авессалом;</i> но он не захотел идти, и благословил его.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13686, 'rst66', '2sa', 13, 24, 'И пришел Авессалом к царю и сказал: вот, ныне стрижение <i>овец</i> у раба твоего; пусть пойдет царь и слуги его с рабом твоим.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13685, 'rst66', '2sa', 13, 23, 'Чрез два года было стрижение <i>овец</i> у Авессалома в Ваал‐Гацоре, что у Ефрема, и позвал Авессалом всех сыновей царских.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13684, 'rst66', '2sa', 13, 22, 'Авессалом же не говорил с Амноном ни худого, ни хорошего; ибо возненавидел Авессалом Амнона за то, что он обесчестил Фамарь, сестру его.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13683, 'rst66', '2sa', 13, 21, 'И услышал царь Давид обо всем этом, и сильно разгневался.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13682, 'rst66', '2sa', 13, 20, 'И сказал ей Авессалом, брат ее: не Амнон ли, брат твой, был с тобою? &mdash; но теперь молчи, сестра моя; он &mdash; брат твой; не сокрушайся сердцем твоим об этом деле. И жила Фамарь в одиночестве в доме Авессалома, брата своего.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13681, 'rst66', '2sa', 13, 19, 'И посыпала Фамарь пеплом голову свою, и разодрала разноцветную одежду, которую имела на себе, и положила руки свои на голову свою, и так шла и вопила.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13680, 'rst66', '2sa', 13, 18, 'На ней была разноцветная одежда, ибо такие верхние одежды носили царские дочери‐девицы. И вывел ее слуга вон и запер за нею дверь.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13679, 'rst66', '2sa', 13, 17, 'И позвал отрока своего, который служил ему, и сказал: прогони эту от меня вон и запри дверь за нею.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13678, 'rst66', '2sa', 13, 16, 'И <i>Фамарь</i> сказала ему: нет; прогнать меня &mdash; это зло больше первого, которое ты сделал со мною. Но он не хотел слушать ее.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13677, 'rst66', '2sa', 13, 15, 'Потом возненавидел ее Амнон величайшею ненавистью, так что ненависть, какою он возненавидел ее, была сильнее любви, какую имел к ней; и сказал ей Амнон: встань, уйди.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13676, 'rst66', '2sa', 13, 14, 'Но он не хотел слушать слов ее, и преодолел ее, и изнасиловал ее, и лежал с нею.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13675, 'rst66', '2sa', 13, 13, 'И я, куда пойду я с моим бесчестием? И ты, ты будешь одним из безумных в Израиле. Ты поговори с царем; он не откажет отдать меня тебе.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13674, 'rst66', '2sa', 13, 12, 'Но она сказала: нет, брат мой, не бесчести меня, ибо не делается так в Израиле; не делай этого безумия.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13673, 'rst66', '2sa', 13, 11, 'И когда она поставила пред ним, чтоб он ел, то он схватил ее, и сказал ей: иди, ложись со мною, сестра моя.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13672, 'rst66', '2sa', 13, 10, 'и сказал Амнон Фамари: отнеси кушанье во внутреннюю комнату, и я поем из рук твоих. И взяла Фамарь лепешки, которые приготовила, и отнесла Амнону, брату своему, во внутреннюю комнату.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13671, 'rst66', '2sa', 13, 9, 'и взяла сковороду и выложила пред ним; но он не хотел есть. И сказал Амнон: пусть все выйдут от меня. И вышли от него все люди,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13670, 'rst66', '2sa', 13, 8, 'И пошла она в дом брата своего Амнона; а он лежит. И взяла она муки и замесила, и изготовила пред глазами его и испекла лепешки,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13669, 'rst66', '2sa', 13, 7, 'И послал Давид к Фамари в дом сказать: пойди в дом Амнона, брата твоего, и приготовь ему кушанье.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13668, 'rst66', '2sa', 13, 6, 'И лег Амнон и притворился больным, и пришел царь навестить его; и сказал Амнон царю: пусть придет Фамарь, сестра моя, и испечет при моих глазах лепешку, или две, и я поем из рук ее.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13667, 'rst66', '2sa', 13, 5, 'И сказал ему Ионадав: ложись в постель твою и притворись больным; и когда отец твой придет навестить тебя, скажи ему: «пусть придет Фамарь, сестра моя, и подкрепит меня пищею, приготовив кушанье при моих глазах, чтоб я видел, и ел из рук ее».');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13666, 'rst66', '2sa', 13, 4, 'И он сказал ему: отчего ты так худеешь с каждым днем, сын царев, &mdash; не откроешь ли мне? И сказал ему Амнон: Фамарь, сестру Авессалома, брата моего, люблю я.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13665, 'rst66', '2sa', 13, 3, 'Но у Амнона был друг, по имени Ионадав, сын Самая, брата Давидова; и Ионадав был человек очень хитрый.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13664, 'rst66', '2sa', 13, 2, 'И скорбел Амнон до того, что заболел из‐за Фамари, сестры своей; ибо она была девица, и Амнону казалось трудным что‐нибудь сделать с нею.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13663, 'rst66', '2sa', 13, 1, 'И было после того: у Авессалома, сына Давидова, <i>была</i> сестра красивая, по имени Фамарь, и полюбил ее Амнон, сын Давида.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13662, 'rst66', '2sa', 12, 31, 'А народ, бывший в нем, он вывел и положил их под пилы, под железные молотилки, под железные топоры, и бросил их в обжигательные печи. Так он поступил со всеми городами Аммонитскими. И возвратился после того Давид и весь народ в Иерусалим.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13661, 'rst66', '2sa', 12, 30, 'И взял Давид венец царя их с головы его, &mdash; а в нем было золота талант и драгоценный камень, &mdash; и возложил его Давид на свою голову, и добычи из города вынес очень много.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13660, 'rst66', '2sa', 12, 29, 'И собрал Давид весь народ и пошел к Равве, и воевал против нее и взял ее.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13659, 'rst66', '2sa', 12, 28, 'теперь собери остальной народ и подступи к городу и возьми его; ибо если я возьму его, то мое имя будет наречено ему.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13658, 'rst66', '2sa', 12, 27, 'И послал Иоав к Давиду сказать ему: я нападал на Равву и овладел водою города;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13657, 'rst66', '2sa', 12, 26, 'Иоав воевал против Раввы Аммонитской и взял <i>почти</i> царственный город.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13656, 'rst66', '2sa', 12, 25, 'и послал пророка Нафана, и он нарек ему имя: Иедидиа по слову Господа.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13655, 'rst66', '2sa', 12, 24, 'И утешил Давид Вирсавию, жену свою, и вошел к ней и спал с нею; и она родила сына, и нарекла ему имя: Соломон. И Господь возлюбил его');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13654, 'rst66', '2sa', 12, 23, 'А теперь оно умерло; зачем же мне поститься? Разве я могу возвратить его? Я пойду к нему, а оно не возвратится ко мне.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13653, 'rst66', '2sa', 12, 22, 'И сказал Давид: доколе дитя было живо, я постился и плакал, ибо думал: кто знает, не помилует ли меня Господь, и дитя останется живо?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13652, 'rst66', '2sa', 12, 21, 'И сказали ему слуги его: что значит, что ты так поступаешь: когда дитя было еще живо, ты постился и плакал; а когда дитя умерло, ты встал и ел хлеб?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13651, 'rst66', '2sa', 12, 20, 'Тогда Давид встал с земли и умылся, и помазался, и переменил одежды свои, и пошел в дом Господень, и молился. Возвратившись домой, потребовал, чтобы подали ему хлеба, и он ел.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13650, 'rst66', '2sa', 12, 19, 'И увидел Давид, что слуги его перешептываются между собою, и понял Давид, что дитя умерло, и спросил Давид слуг своих: умерло дитя? И сказали: умерло.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13649, 'rst66', '2sa', 12, 18, 'На седьмой день дитя умерло, и слуги Давидовы боялись донести ему, что умер младенец; ибо, говорили они, когда дитя было еще живо, и мы уговаривали его, и он не слушал голоса нашего, как же мы скажем ему: «умерло дитя»? Он сделает что‐нибудь худое.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13648, 'rst66', '2sa', 12, 17, 'И вошли к нему старейшины дома его, чтобы поднять его с земли; но он не хотел, и не ел с ними хлеба.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13647, 'rst66', '2sa', 12, 16, 'И молился Давид Богу о младенце, и постился Давид, и, уединившись, провел ночь, лежа на земле.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13646, 'rst66', '2sa', 12, 15, 'И пошел Нафан в дом свой. И поразил Господь дитя, которое родила жена Урии Давиду, и оно заболело.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13645, 'rst66', '2sa', 12, 14, 'но как ты этим делом подал повод врагам Господа хулить Его, то умрет родившийся у тебя сын.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13644, 'rst66', '2sa', 12, 13, 'И сказал Давид Нафану: согрешил я пред Господом. И сказал Нафан Давиду: и Господь снял <i>с тебя</i> грех твой; ты не умрешь;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13643, 'rst66', '2sa', 12, 12, 'ты сделал тайно, а Я сделаю это пред всем Израилем и пред солнцем.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13642, 'rst66', '2sa', 12, 11, 'Так говорит Господь: вот, Я воздвигну на тебя зло из дома твоего, и возьму жен твоих пред глазами твоими, и отдам ближнему твоему, и будет он спать с женами твоими пред этим солнцем;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13641, 'rst66', '2sa', 12, 10, 'итак, не отступит меч от дома твоего вовеки, за то, что ты пренебрег Меня и взял жену Урии Хеттеянина, чтоб она была тебе женою.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13640, 'rst66', '2sa', 12, 9, 'зачем же ты пренебрег слово Господа, сделав злое пред очами Его? Урию Хеттеянина ты поразил мечом; жену его взял себе в жену, а его ты убил мечом Аммонитян;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13639, 'rst66', '2sa', 12, 8, 'и дал тебе дом господина твоего и жен господина твоего на лоно твое, и дал тебе дом Израилев и Иудин, и, если этого <i>для тебя</i> мало, прибавил бы тебе еще больше;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13638, 'rst66', '2sa', 12, 7, 'И сказал Нафан Давиду: ты &mdash; тот человек. Так говорит Господь, Бог Израилев: Я помазал тебя в царя над Израилем и Я избавил тебя от руки Саула,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13637, 'rst66', '2sa', 12, 6, 'и за овечку он должен заплатить вчетверо, за то, что он сделал это, и за то, что не имел сострадания.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13636, 'rst66', '2sa', 12, 5, 'Сильно разгневался Давид на этого человека и сказал Нафану: жив Господь! достоин смерти человек, сделавший это;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13635, 'rst66', '2sa', 12, 4, 'и пришел к богатому человеку странник, и тот пожалел взять из своих овец или волов, чтобы приготовить <i>обед</i> для странника, который пришел к нему, а взял овечку бедняка и приготовил ее для человека, который пришел к нему.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13634, 'rst66', '2sa', 12, 3, 'а у бедного ничего, кроме одной овечки, которую он купил маленькую и выкормил, и она выросла у него вместе с детьми его; от хлеба его она ела, и из его чаши пила, и на груди у него спала, и была для него, как дочь;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13633, 'rst66', '2sa', 12, 2, 'у богатого было очень много мелкого и крупного скота,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13632, 'rst66', '2sa', 12, 1, 'И послал Господь Нафана к Давиду, и тот пришел к нему и сказал ему: в одном городе были два человека, один богатый, а другой бедный;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13631, 'rst66', '2sa', 11, 27, 'Когда кончилось время плача, Давид послал, и взял ее в дом свой, и она сделалась его женою и родила ему сына. И было это дело, которое сделал Давид, зло в очах Господа.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13630, 'rst66', '2sa', 11, 26, 'И услышала жена Урии, что умер Урия, муж ее, и плакала по муже своем.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13629, 'rst66', '2sa', 11, 25, 'Тогда сказал Давид посланному: так скажи Иоаву: «пусть не смущает тебя это дело, ибо меч поядает иногда того, иногда сего; усиль войну твою против города и разрушь его». Так ободри его.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13628, 'rst66', '2sa', 11, 24, 'тогда стреляли стрелки со стены на рабов твоих, и умерли <i>некоторые</i> из рабов царя; умер также и раб твой Урия Хеттеянин.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13627, 'rst66', '2sa', 11, 23, 'Тогда посланный сказал Давиду: одолевали нас те люди и вышли к нам в поле, и мы преследовали их до входа в ворота;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13626, 'rst66', '2sa', 11, 22, 'И пошел <i>посланный</i>, и пришел, и рассказал Давиду обо всем, для чего послал его Иоав.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13625, 'rst66', '2sa', 11, 21, 'Кто убил Авимелеха, сына Иероваалова? не женщина ли бросила на него со стены обломок жернова, и он умер в Тевеце? Зачем же вы близко подходили к стене?» тогда ты скажи: и раб твой Урия Хеттеянин также умер.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13624, 'rst66', '2sa', 11, 20, 'и увидишь, что царь разгневается и скажет тебе: «зачем вы так близко подходили к городу сражаться? разве вы не знали, что со стены будут бросать на вас?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13623, 'rst66', '2sa', 11, 19, 'И приказал посланному, говоря: когда ты расскажешь царю о всем ходе сражения');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13622, 'rst66', '2sa', 11, 18, 'И послал Иоав донести Давиду о всем ходе сражения.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13621, 'rst66', '2sa', 11, 17, 'И вышли люди из города и сразились с Иоавом, и пало несколько из народа, из слуг Давидовых; был убит также и Урия Хеттеянин.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13620, 'rst66', '2sa', 11, 16, 'Посему, когда Иоав осаждал город, то поставил он Урию на таком месте, о котором знал, что там храбрые люди.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13619, 'rst66', '2sa', 11, 15, 'В письме он написал так: поставьте Урию там, где <i>будет</i> самое сильное сражение, и отступите от него, чтоб он был поражен и умер.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13618, 'rst66', '2sa', 11, 14, 'Поутру Давид написал письмо к Иоаву и послал <i>его</i> с Уриею.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13617, 'rst66', '2sa', 11, 13, 'И пригласил его Давид, и ел <i>Урия</i> пред ним и пил, и напоил его <i>Давид</i>. Но вечером <i>Урия</i> пошел спать на постель свою с рабами господина своего, а в свой дом не пошел.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13616, 'rst66', '2sa', 11, 12, 'И сказал Давид Урии: останься здесь и на этот день, а завтра я отпущу тебя. И остался Урия в Иерусалиме на этот день до завтра.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13615, 'rst66', '2sa', 11, 11, 'И сказал Урия Давиду: ковчег, и Израиль и Иуда находятся в шатрах, и господин мой Иоав и рабы господина моего пребывают в поле, а я вошел бы в дом свой есть и пить и спать со своею женою! Клянусь твоею жизнью и жизнью души твоей, этого я не сделаю.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13614, 'rst66', '2sa', 11, 10, 'И донесли Давиду, говоря: не пошел Урия в дом свой. И сказал Давид Урии: вот, ты пришел с дороги; отчего же не пошел ты в дом свой?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13613, 'rst66', '2sa', 11, 9, 'Но Урия спал у ворот царского дома со всеми слугами своего господина, и не пошел в свой дом.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13612, 'rst66', '2sa', 11, 8, 'И сказал Давид Урии: иди домой и омой ноги свои. И вышел Урия из дома царского, а вслед за ним понесли и царское кушанье.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13611, 'rst66', '2sa', 11, 7, 'И пришел к нему Урия, и расспросил <i>его</i> Давид о положении Иоава и о положении народа, и о ходе войны.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13610, 'rst66', '2sa', 11, 6, 'И послал Давид <i>сказать</i> Иоаву: пришли ко мне Урию Хеттеянина. И послал Иоав Урию к Давиду.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13609, 'rst66', '2sa', 11, 5, 'Женщина эта сделалась беременною и послала известить Давида, говоря: я беременна.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13608, 'rst66', '2sa', 11, 4, 'Давид послал слуг взять ее; и она пришла к нему, и он спал с нею. Когда же она очистилась от нечистоты своей, возвратилась в дом свой.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13607, 'rst66', '2sa', 11, 3, 'И послал Давид разведать кто эта женщина. И сказали ему: это Вирсавия, дочь Елиама, жена Урии Хеттеянина.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13606, 'rst66', '2sa', 11, 2, 'Однажды под вечер Давид, встав с постели, прогуливался на кровле царского дома и увидел с кровли купающуюся женщину; а та женщина была очень красива.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13605, 'rst66', '2sa', 11, 1, 'Через год, в то время, когда выходят цари <i>в походы</i>, Давид послал Иоава и слуг своих с ним и всех Израильтян; и они поразили Аммонитян и осадили Равву; Давид же оставался в Иерусалиме.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13604, 'rst66', '2sa', 10, 19, 'Когда все цари, покорные Адраазару, увидели, что они поражены Израильтянами, то заключили мир с Израильтянами и покорились им. А Сирийцы боялись более помогать Аммонитянам.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13603, 'rst66', '2sa', 10, 18, 'И побежали Сирийцы от Израильтян. Давид истребил у Сирийцев семьсот колесниц и сорок тысяч всадников; поразил и военачальника Совака, который там и умер.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13602, 'rst66', '2sa', 10, 17, 'Когда донесли <i>об этом</i> Давиду, то он собрал всех Израильтян, и перешел Иордан и пришел к Еламу. Сирийцы выстроились против Давида и сразились с ним.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13601, 'rst66', '2sa', 10, 16, 'И послал Адраазар, и призвал Сирийцев, которые за рекою, и пришли они к Еламу; а Совак, военачальник Адраазаров, предводительствовал ими.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13600, 'rst66', '2sa', 10, 15, 'Сирийцы, видя, что они поражены Израильтянами, собрались вместе.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13599, 'rst66', '2sa', 10, 14, 'Аммонитяне же, увидев, что Сирийцы бегут, побежали от Авессы и ушли в город. И возвратился Иоав от Аммонитян, и пришел в Иерусалим.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13598, 'rst66', '2sa', 10, 13, 'И вступил Иоав и народ, который <i>был</i> у него, в сражение с Сирийцами, и они побежали от него.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13597, 'rst66', '2sa', 10, 12, 'будь мужествен, и будем стоять твердо за народ наш и за города Бога нашего, а Господь сделает, что Ему угодно.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13596, 'rst66', '2sa', 10, 11, 'И сказал <i>Иоав:</i> если Сирийцы будут одолевать меня, ты поможешь мне; а если Аммонитяне тебя будут одолевать, я приду к тебе на помощь;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13595, 'rst66', '2sa', 10, 10, 'остальную же часть людей поручил Авессе, брату своему, чтоб он выстроил их против Аммонитян.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13594, 'rst66', '2sa', 10, 9, 'И увидел Иоав, что неприятельское войско было поставлено против него и спереди и сзади, и избрал <i>воинов</i> из всех отборных в Израиле, и выстроил их против Сирийцев;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13593, 'rst66', '2sa', 10, 8, 'И вышли Аммонитяне, и расположились к сражению у ворот, а Сирийцы Сувы и Рехова, и Истова, и Маахи <i>стали</i> отдельно в поле.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13592, 'rst66', '2sa', 10, 7, 'Когда услышал об этом Давид, то послал Иоава со всем войском храбрых.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13591, 'rst66', '2sa', 10, 6, 'И увидели Аммонитяне, что они сделались ненавистными для Давида; и послали Аммонитяне нанять Сирийцев из Беф‐Рехова и Сирийцев Сувы двадцать тысяч пеших, у царя Маахи тысячу человек и из Истова двенадцать тысяч человек.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13590, 'rst66', '2sa', 10, 5, 'Когда донесли об этом Давиду, то он послал к ним навстречу, так как они были очень обесчещены. И велел царь сказать им: оставайтесь в Иерихоне, пока отрастут бороды ваши, и <i>тогда</i> возвратитесь.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13589, 'rst66', '2sa', 10, 4, 'И взял Аннон слуг Давидовых, и обрил каждому из них половину бороды, и обрезал одежды их наполовину, до чресл, и отпустил их.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13588, 'rst66', '2sa', 10, 3, 'Но князья Аммонитские сказали Аннону, господину своему: неужели ты думаешь, что Давид из уважения к отцу твоему прислал к тебе утешителей? Не для того ли, чтобы осмотреть город и высмотреть в нем и <i>после</i> разрушить его, прислал Давид слуг своих к тебе?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13587, 'rst66', '2sa', 10, 2, 'И сказал Давид: окажу я милость Аннону, сыну Наасову, за благодеяние, которое оказал мне отец его. И послал Давид слуг своих утешить Аннона об отце его. И пришли слуги Давидовы в землю Аммонитскую.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13586, 'rst66', '2sa', 10, 1, 'Спустя несколько времени умер царь Аммонитский, и воцарился вместо него сын его Аннон.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13585, 'rst66', '2sa', 9, 13, 'И жил Мемфивосфей в Иерусалиме, ибо он ел всегда за царским столом. Он был хром на обе ноги.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13584, 'rst66', '2sa', 9, 12, 'У Мемфивосфея был малолетний сын, по имени Миха. Все, живущие в доме Сивы, были рабами Мемфивосфея.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13583, 'rst66', '2sa', 9, 11, 'И сказал Сива царю: все, что приказывает господин мой, царь, рабу своему, исполнит раб твой. Мемфивосфей ел за столом <i>Давида</i>, как один из сыновей царя.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13582, 'rst66', '2sa', 9, 10, 'итак, обрабатывай для него землю ты и сыновья твои и рабы твои, и доставляй <i>плоды ее</i>, чтобы у сына господина твоего был хлеб для пропитания; Мемфивосфей же, сын господина твоего, всегда будет есть за моим столом. У Сивы было пятнадцать сыновей и двадцать рабов.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13581, 'rst66', '2sa', 9, 9, 'И призвал царь Сиву, слугу Саула, и сказал ему: все, что принадлежало Саулу и всему дому его, я отдаю сыну господина твоего;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13580, 'rst66', '2sa', 9, 8, 'И поклонился <i>Мемфивосфей</i> и сказал: что такое раб твой, что ты призрел на такого мертвого пса, как я?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13579, 'rst66', '2sa', 9, 7, 'И сказал ему Давид: не бойся; я окажу тебе милость ради отца твоего Ионафана и возвращу тебе все поля Саула, отца твоего, и ты всегда будешь есть хлеб за моим столом.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13578, 'rst66', '2sa', 9, 6, 'И пришел Мемфивосфей, сын Ионафана, сына Саулова, к Давиду, и пал на лицо свое, и поклонился. И сказал Давид: Мемфивосфей! И сказал тот: вот раб твой.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13577, 'rst66', '2sa', 9, 5, 'И послал царь Давид, и взяли его из дома Махира, сына Аммиэлова, из Лодевара.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13576, 'rst66', '2sa', 9, 4, 'И сказал ему царь: где он? И сказал Сива царю: вот, он в доме Махира, сына Аммиэлова, в Лодеваре.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13575, 'rst66', '2sa', 9, 3, 'И сказал царь: нет ли еще кого‐нибудь из дома Саулова? я оказал бы ему милость Божию. И сказал Сива царю: есть сын Ионафана, хромой ногами.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13574, 'rst66', '2sa', 9, 2, 'В доме Саула был раб, по имени Сива; и позвали его к Давиду, и сказал ему царь: ты ли Сива? И тот сказал: я, раб твой.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13573, 'rst66', '2sa', 9, 1, 'И сказал Давид: не остался ли еще кто‐нибудь из дома Саулова? я оказал бы ему милость ради Ионафана.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13572, 'rst66', '2sa', 8, 18, 'и Ванея, сын Иодая, &mdash; <i>начальником</i> над Хелефеями и Фелефеями, и сыновья Давида &mdash; первыми при дворе.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13571, 'rst66', '2sa', 8, 17, 'Садок, сын Ахитува, и Ахимелех, сын Авиафара, &mdash; священниками, Сераия &mdash; писцом;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13570, 'rst66', '2sa', 8, 16, 'Иоав же, сын Саруи, <i>был начальником</i> войска; и Иосафат, сын Ахилуда, &mdash; дееписателем;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13569, 'rst66', '2sa', 8, 15, 'И царствовал Давид над всем Израилем, и творил Давид суд и правду над всем народом своим.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13568, 'rst66', '2sa', 8, 14, 'И поставил он охранные войска в Идумее; во всей Идумее поставил охранные войска, и все Идумеяне были рабами Давиду. И хранил Господь Давида везде, куда он ни ходил.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13567, 'rst66', '2sa', 8, 13, 'И сделал Давид себе имя, возвращаясь с поражения восемнадцати тысяч Сирийцев в долине Соленой.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13566, 'rst66', '2sa', 8, 12, 'у Сирийцев, и Моавитян, и Аммонитян, и Филистимлян, и Амаликитян, и из отнятого у Адраазара, сына Реховова, царя Сувского.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13565, 'rst66', '2sa', 8, 11, 'Их также посвятил царь Давид Господу, вместе с серебром и золотом, которое посвятил из <i>отнятого</i> у всех покоренных им народов:');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13564, 'rst66', '2sa', 8, 10, 'и послал Фой Иорама, сына своего, к царю Давиду, приветствовать его и благодарить его за то, что он воевал с Адраазаром и поразил его; ибо Адраазар вел войны с Фоем. В руках же <i>Иорама</i> были сосуды серебряные, золотые и медные.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13563, 'rst66', '2sa', 8, 9, 'И услышал Фой, царь Имафа, что Давид поразил все войско Адраазарово,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13562, 'rst66', '2sa', 8, 8, 'А в Бефе и Берофе, городах Адраазаровых, взял царь Давид весьма много меди.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13561, 'rst66', '2sa', 8, 7, 'И взял Давид золотые щиты, которые были у рабов Адраазара, и принес их в Иерусалим.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13560, 'rst66', '2sa', 8, 6, 'И поставил Давид охранные войска в Сирии Дамасской, и стали Сирийцы у Давида рабами, платящими дань. И хранил Господь Давида везде, куда он ни ходил.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13559, 'rst66', '2sa', 8, 5, 'И пришли Сирийцы Дамасские на помощь к Адраазару, царю Сувскому; но Давид поразил двадцать две тысячи человек Сирийцев.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13558, 'rst66', '2sa', 8, 4, 'и взял Давид у него тысячу семьсот всадников и двадцать тысяч человек пеших, и подрезал Давид жилы у всех коней колесничных, оставив <i>себе</i> из них для ста колесниц.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13557, 'rst66', '2sa', 8, 3, 'И поразил Давид Адраазара, сына Реховова, царя Сувского, когда тот шел, чтобы восстановить свое владычество при реке <i>Евфрате</i>;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13556, 'rst66', '2sa', 8, 2, 'И поразил Моавитян и смерил их веревкою, положив их на землю; и отмерил две веревки на умерщвление, а одну веревку на оставление в живых. И сделались Моавитяне у Давида рабами, платящими дань.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13555, 'rst66', '2sa', 8, 1, 'После сего Давид поразил Филистимлян и смирил их, и взял Давид Мефег‐Гаамма из рук Филистимлян.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13554, 'rst66', '2sa', 7, 29, 'И ныне начни и благослови дом раба Твоего, чтоб он был вечно пред лицом Твоим, ибо Ты, Господи мой, Господи, возвестил это, и благословением Твоим соделается дом раба Твоего благословенным вовеки.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13553, 'rst66', '2sa', 7, 28, 'Итак, Господи мой, Господи! Ты Бог, и слова Твои непреложны, и Ты возвестил рабу Твоему такое благо!');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13552, 'rst66', '2sa', 7, 27, 'Так как ты, Господи Саваоф, Боже Израилев, открыл рабу Твоему, говоря: «устрою тебе дом», то раб Твой уготовал сердце свое, чтобы молиться Тебе такою молитвою.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13551, 'rst66', '2sa', 7, 26, 'И да возвеличится имя Твое вовеки, чтобы говорили: «Господь Саваоф &mdash; Бог над Израилем». И дом раба Твоего Давида да будет тверд пред лицом Твоим.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13550, 'rst66', '2sa', 7, 25, 'И ныне, Господи, Боже, утверди навеки слово, которое изрек Ты о рабе Твоем и о доме его, и исполни то, что Ты изрек.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13549, 'rst66', '2sa', 7, 24, 'И Ты укрепил за Собою народ Твой, Израиля, как собственный народ, навеки, и Ты, Господи, сделался его Богом.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13548, 'rst66', '2sa', 7, 23, 'И кто подобен народу Твоему, Израилю, единственному народу на земле, для которого приходил Бог, чтобы приобрести <i>его</i> Себе в народ и прославить Свое имя <i>и</i> совершить великое и страшное пред народом Твоим, который Ты приобрел Себе от Египтян, изгнав народы и богов их?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13547, 'rst66', '2sa', 7, 22, 'По всему велик Ты, Господи мой, Господи! ибо нет подобного Тебе и нет Бога, кроме Тебя, по всему, что слышали мы своими ушами.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13546, 'rst66', '2sa', 7, 21, 'Ради слова Твоего и по сердцу Твоему Ты делаешь это, открывая все это великое рабу Твоему.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13545, 'rst66', '2sa', 7, 20, 'Что еще может сказать Тебе Давид? Ты знаешь раба Твоего, Господи мой, Господи!');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13544, 'rst66', '2sa', 7, 19, 'И этого еще мало показалось в очах Твоих, Господи мой, Господи; но Ты возвестил еще о доме раба Твоего вдаль. Это уже по‐человечески, Господи мой, Господи!');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13543, 'rst66', '2sa', 7, 18, 'И пошел царь Давид, и предстал пред лицом Господа, и сказал: кто я, Господи, мой Господи, и что такое дом мой, что Ты меня так возвеличил!');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13542, 'rst66', '2sa', 7, 17, 'Все эти слова и все это видение Нафан пересказал Давиду.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13541, 'rst66', '2sa', 7, 16, 'И будет непоколебим дом твой и царство твое навеки пред лицом Моим, и престол твой устоит вовеки.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13540, 'rst66', '2sa', 7, 15, 'но милости Моей не отниму от него, как Я отнял от Саула, которого Я отверг пред лицом твоим.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13539, 'rst66', '2sa', 7, 14, 'Я буду ему отцом, и он будет Мне сыном; и если он согрешит, Я накажу его жезлом мужей и ударами сынов человеческих;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13538, 'rst66', '2sa', 7, 13, 'Он построит дом имени Моему, и Я утвержу престол царства его навеки.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13537, 'rst66', '2sa', 7, 12, 'Когда же исполнятся дни твои, и ты почиешь с отцами твоими, то Я восставлю после тебя семя твое, которое произойдет из чресл твоих, и упрочу царство его.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13536, 'rst66', '2sa', 7, 11, 'с того времени, как Я поставил судей над народом Моим, Израилем; и Я успокою тебя от всех врагов твоих. И Господь возвещает тебе, что Он устроит тебе дом.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13535, 'rst66', '2sa', 7, 10, 'И Я устрою место для народа Моего, для Израиля, и укореню его, и будет он спокойно жить на месте своем, и не будет тревожиться больше, и люди нечестивые не станут более теснить его, как прежде,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13534, 'rst66', '2sa', 7, 9, 'и был с тобою везде, куда ни ходил ты, и истребил всех врагов твоих пред лицом твоим, и сделал имя твое великим, как имя великих на земле.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13533, 'rst66', '2sa', 7, 8, 'И теперь так скажи рабу Моему Давиду: так говорит Господь Саваоф: Я взял тебя от стада овец, чтобы ты был вождем народа Моего, Израиля;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13532, 'rst66', '2sa', 7, 7, 'Где Я ни ходил со всеми сынами Израиля, говорил ли Я хотя слово какому‐либо из колен, которому Я назначил пасти народ Мой, Израиля: «почему не построите Мне кедрового дома»?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13531, 'rst66', '2sa', 7, 6, 'когда Я не жил в доме с того времени, как вывел сынов Израилевых из Египта, и до сего дня, но переходил в шатре и в скинии?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13530, 'rst66', '2sa', 7, 5, 'пойди, скажи рабу Моему Давиду: так говорит Господь: ты ли построишь Мне дом для Моего обитания,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13529, 'rst66', '2sa', 7, 4, 'Но в ту же ночь было слово Господа к Нафану:');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13528, 'rst66', '2sa', 7, 3, 'И сказал Нафан царю: все, что у тебя на сердце, иди, делай; ибо Господь с тобою.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13527, 'rst66', '2sa', 7, 2, 'тогда сказал царь пророку Нафану: вот, я живу в доме кедровом, а ковчег Божий находится под шатром.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13526, 'rst66', '2sa', 7, 1, 'Когда царь жил в доме своем, и Господь успокоил его от всех окрестных врагов его,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13525, 'rst66', '2sa', 6, 23, 'И у Мелхолы, дочери Сауловой, не было детей до дня смерти ее.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13524, 'rst66', '2sa', 6, 22, 'и я еще больше уничижусь, и сделаюсь еще ничтожнее в глазах моих, и пред служанками, о которых ты говоришь, я буду славен.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13523, 'rst66', '2sa', 6, 21, 'И сказал Давид Мелхоле: пред Господом, Который предпочел меня отцу твоему и всему дому его, утвердив меня вождем народа Господня, Израиля; пред Господом играть и плясать буду;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13522, 'rst66', '2sa', 6, 20, 'Когда Давид возвратился, чтобы благословить дом свой, то Мелхола, дочь Саула, вышла к нему навстречу и сказала: как отличился сегодня царь Израилев, обнажившись сегодня пред глазами рабынь рабов своих, как обнажается какой‐нибудь пустой человек!');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13521, 'rst66', '2sa', 6, 19, 'и роздал всему народу, всему множеству Израильтян, как мужчинам, так и женщинам, по одному хлебу и по куску жареного мяса и по одной лепешке каждому. И пошел весь народ, каждый в дом свой.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13520, 'rst66', '2sa', 6, 18, 'Когда Давид окончил приношение всесожжений и жертв мирных, то благословил он народ именем Господа Саваофа;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13519, 'rst66', '2sa', 6, 17, 'И принесли ковчег Господень, и поставили его на своем месте посреди скинии, которую устроил для него Давид; и принес Давид всесожжения пред Господом и жертвы мирные.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13518, 'rst66', '2sa', 6, 16, 'Когда входил ковчег Господень в город Давидов, Мелхола, дочь Саула, смотрела в окно и, увидев царя Давида, скачущего и пляшущего пред Господом, уничижила его в сердце своем.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13517, 'rst66', '2sa', 6, 15, 'Так Давид и весь дом Израилев несли ковчег Господень с восклицаниями и трубными звуками.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13516, 'rst66', '2sa', 6, 14, 'Давид скакал из всей силы пред Господом; одет же был Давид в льняной ефод.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13515, 'rst66', '2sa', 6, 13, 'И когда несшие ковчег Господень проходили по шести шагов, он приносил в жертву тельца и овна.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13514, 'rst66', '2sa', 6, 12, 'Когда донесли царю Давиду, говоря: «Господь благословил дом Аведдара и все, что было у него, ради ковчега Божия», то пошел Давид и с торжеством перенес ковчег Божий из дома Аведдара в город Давидов.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13513, 'rst66', '2sa', 6, 11, 'И оставался ковчег Господень в доме Аведдара Гефянина три месяца, и благословил Господь Аведдара и весь дом его.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13512, 'rst66', '2sa', 6, 10, 'И не захотел Давид везти ковчег Господень к себе, в город Давидов, а обратил его в дом Аведдара Гефянина.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13511, 'rst66', '2sa', 6, 9, 'И устрашился Давид в тот день Господа, и сказал: как войти ко мне ковчегу Господню?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13510, 'rst66', '2sa', 6, 8, 'И опечалился Давид, что Господь поразил Озу. Место сие и доныне называется: «поражение Озы».');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13509, 'rst66', '2sa', 6, 7, 'Но Господь прогневался на Озу, и поразил его Бог там же за дерзновение, и умер он там у ковчега Божия.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13508, 'rst66', '2sa', 6, 6, 'И когда дошли до гумна Нахонова, Оза простер руку свою к ковчегу Божию и взялся за него, ибо волы наклонили его.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13507, 'rst66', '2sa', 6, 5, 'А Давид и все сыны Израилевы играли пред Господом на всяких музыкальных орудиях из кипарисового дерева, и на цитрах, и на псалтирях, и на тимпанах, и на систрах, и на кимвалах.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13506, 'rst66', '2sa', 6, 4, 'И повезли ее с ковчегом Божиим из дома Аминадава, что на холме; и Ахио шел пред ковчегом.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13505, 'rst66', '2sa', 6, 3, 'И поставили ковчег Божий на новую колесницу, и вывезли его из дома Аминадава, что на холме. Сыновья же Аминадава, Оза и Ахио, вели новую колесницу.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13504, 'rst66', '2sa', 6, 2, 'И встал и пошел Давид и весь народ, бывший с ним, из Ваала Иудина, чтобы перенести оттуда ковчег Божий, на котором нарицается имя Господа Саваофа, сидящего на херувимах.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13503, 'rst66', '2sa', 6, 1, 'И собрал снова Давид всех отборных <i>людей</i> из Израиля, тридцать тысяч.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13502, 'rst66', '2sa', 5, 25, 'И сделал Давид, как повелел ему Господь, и поразил Филистимлян от Гаваи до Газера.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13501, 'rst66', '2sa', 5, 24, 'и когда услышишь шум как бы идущего по вершинам тутовых дерев, то двинься, ибо тогда пошел Господь пред тобою, чтобы поразить войско Филистимское.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13500, 'rst66', '2sa', 5, 23, 'И вопросил Давид Господа, и Он отвечал ему: не выходи навстречу им, а зайди им с тылу и иди к ним со стороны тутовой рощи;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13499, 'rst66', '2sa', 5, 22, 'И пришли опять Филистимляне, и расположились в долине Рефаим.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13498, 'rst66', '2sa', 5, 21, 'И оставили там <i>Филистимляне</i> истуканов своих, а Давид с людьми своими взял их.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13497, 'rst66', '2sa', 5, 20, 'И пошел Давид в Ваал‐Перацим и поразил их там, и сказал Давид: Господь разнес врагов моих предо мною, как разносит вода. Посему и месту тому дано имя Ваал‐Перацим.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13496, 'rst66', '2sa', 5, 19, 'И вопросил Давид Господа, говоря: идти ли мне против Филистимлян? Предашь ли их в руки мои? И сказал Господь Давиду: иди, ибо Я предам Филистимлян в руки твои.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13495, 'rst66', '2sa', 5, 18, 'А Филистимляне пришли и расположились в долине Рефаим.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13494, 'rst66', '2sa', 5, 17, 'Когда Филистимляне услышали, что Давида помазали на царство над Израилем, то поднялись все Филистимляне искать Давида. И услышал Давид, и пошел в крепость.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13493, 'rst66', '2sa', 5, 16, 'и Елисама, и Елидае, и Елифалеф.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13492, 'rst66', '2sa', 5, 15, 'и Евеар, и Елисуа, и Нафек, и Иафиа,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13491, 'rst66', '2sa', 5, 14, 'И родились еще у Давида сыновья и дочери. И вот имена родившихся у него в Иерусалиме: Самус, и Совав, и Нафан, и Соломон,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13490, 'rst66', '2sa', 5, 13, 'И взял Давид еще наложниц и жен из Иерусалима, после того, как пришел из Хеврона.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13489, 'rst66', '2sa', 5, 12, 'И уразумел Давид, что Господь утвердил его царем над Израилем и что возвысил царство его ради народа Своего, Израиля.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13488, 'rst66', '2sa', 5, 11, 'И прислал Хирам, царь Тирский, послов к Давиду и кедровые деревья и плотников и каменщиков, и они построили дом Давиду.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13487, 'rst66', '2sa', 5, 10, 'И преуспевал Давид и возвышался, и Господь Бог Саваоф <i>был</i> с ним.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13486, 'rst66', '2sa', 5, 9, 'И поселился Давид в крепости, и назвал ее городом Давидовым, и обстроил кругом от Милло и внутри.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13485, 'rst66', '2sa', 5, 8, 'И сказал Давид в тот день: всякий, убивая Иевусеев, пусть поражает копьем и хромых и слепых, ненавидящих душу Давида. Посему и говорится: слепой и хромой не войдет в дом <i>Господень</i>.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13484, 'rst66', '2sa', 5, 7, 'Но Давид взял крепость Сион: это &mdash; город Давидов.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13483, 'rst66', '2sa', 5, 6, 'И пошел царь и люди его на Иерусалим против Иевусеев, жителей той страны; но они говорили Давиду: «ты не войдешь сюда; тебя отгонят слепые и хромые», &mdash; это значило: «не войдет сюда Давид».');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13482, 'rst66', '2sa', 5, 5, 'В Хевроне царствовал над Иудою семь лет и шесть месяцев, и в Иерусалиме царствовал тридцать три года над всем Израилем и Иудою.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13481, 'rst66', '2sa', 5, 4, 'Тридцать лет было Давиду, когда он воцарился; царствовал сорок лет.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13480, 'rst66', '2sa', 5, 3, 'И пришли все старейшины Израиля к царю в Хеврон, и заключил с ними царь Давид завет в Хевроне пред Господом; и помазали Давида в царя над Израилем.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13479, 'rst66', '2sa', 5, 2, 'еще вчера и третьего дня, когда Саул царствовал над нами, ты выводил и вводил Израиля; и сказал Господь тебе: «ты будешь пасти народ Мой, Израиля, и ты будешь вождем Израиля».');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13478, 'rst66', '2sa', 5, 1, 'И пришли все колена Израилевы к Давиду в Хеврон и сказали: вот, мы &mdash; кости твои и плоть твоя;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13477, 'rst66', '2sa', 4, 12, 'И приказал Давид слугам, и убили их, и отрубили им руки и ноги, и повесили их над прудом в Хевроне. А голову Иевосфея взяли и погребли во гробе Авенира, в Хевроне.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13476, 'rst66', '2sa', 4, 11, 'то теперь, когда негодные люди убили человека невинного в его доме на постели его, неужели я не взыщу крови его от руки вашей и не истреблю вас с земли?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13475, 'rst66', '2sa', 4, 10, 'Если того, кто принес мне известие, сказав: «вот, умер Саул», и кто считал себя радостным вестником, я схватил и убил его в Секелаге, вместо того, чтобы дать ему награду,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13474, 'rst66', '2sa', 4, 9, 'И отвечал Давид Рихаву и Баане, брату его, сыновьям Реммона Беерофянина, и сказал им: жив Господь, избавивший душу мою от всякой скорби!');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13473, 'rst66', '2sa', 4, 8, 'и принесли голову Иевосфея к Давиду в Хеврон и сказали царю: вот голова Иевосфея, сына Саула, врага твоего, который искал души твоей; ныне Господь отмстил за господина моего царя Саулу и потомству его.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13472, 'rst66', '2sa', 4, 7, 'Когда они вошли в дом, <i>Иевосфей</i> лежал на постели своей, в спальной комнате своей; и они поразили его, и умертвили его, и отрубили голову его, и взяли голову его с собою, и шли пустынною дорогою всю ночь;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13471, 'rst66', '2sa', 4, 6, 'И Рихав и Баана, брат его, вошли внутрь дома, <i>как бы</i> для того, чтобы взять пшеницы; и поразили его в живот и убежали.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13470, 'rst66', '2sa', 4, 5, 'И пошли сыны Реммона Беерофянина, Рихав и Баана, и пришли в самый жар дня к дому Иевосфея; а он спал на постели в полдень.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13469, 'rst66', '2sa', 4, 4, 'У Ионафана, сына Саулова, был сын хромой. Пять лет было ему, когда пришло известие о Сауле и Ионафане из Изрееля, и нянька, взяв его, побежала. И когда она бежала поспешно, то он упал, и сделался хромым. Имя его Мемфивосфей.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13468, 'rst66', '2sa', 4, 3, 'И убежали Беерофяне в Гиффаим и остались там пришельцами до сего дня.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13467, 'rst66', '2sa', 4, 2, 'У <i>Иевосфея,</i> сына Саулова, два было предводителя войска; имя одного &mdash; Баана и имя другого &mdash; Рихав, сыновья Реммона Беерофянина, из потомков Вениаминовых, ибо и Беероф причислялся к Вениамину.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13466, 'rst66', '2sa', 4, 1, 'И услышал <i>Иевосфей,</i> сын Саулов, что умер Авенир в Хевроне, и опустились руки его, и весь Израиль смутился.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13465, 'rst66', '2sa', 3, 39, 'Я теперь еще слаб, хотя и помазан на царство, а эти люди, сыновья Саруи, сильнее меня; пусть же воздаст Господь делающему злое по злобе его!');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13464, 'rst66', '2sa', 3, 38, 'И сказал царь слугам своим: знаете ли, что вождь и великий муж пал в этот день в Израиле?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13463, 'rst66', '2sa', 3, 37, 'И узнал весь народ и весь Израиль в тот день, что не от царя произошло умерщвление Авенира, сына Нирова.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13462, 'rst66', '2sa', 3, 36, 'И весь народ узнал это, и понравилось ему это, как и все, что делал царь, нравилось всему народу.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13461, 'rst66', '2sa', 3, 35, 'И пришел весь народ предложить Давиду хлеба, когда еще продолжался день; но Давид поклялся, говоря: то и то пусть сделает со мною Бог и еще больше сделает, если я до захождения солнца вкушу хлеба или чего‐нибудь.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13460, 'rst66', '2sa', 3, 34, 'Руки твои не были связаны, и ноги твои не в оковах, и ты пал, как падают от разбойников. И весь народ стал еще более плакать над ним.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13459, 'rst66', '2sa', 3, 33, 'И оплакал царь Авенира, говоря: смертью ли подлого умирать Авениру?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13458, 'rst66', '2sa', 3, 32, 'Когда погребали Авенира в Хевроне, то царь громко плакал над гробом Авенира; плакал и весь народ.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13457, 'rst66', '2sa', 3, 31, 'И сказал Давид Иоаву и всем людям, бывшим с ним: раздерите одежды ваши и оденьтесь во вретища и плачьте над Авениром. И царь Давид шел за гробом <i>его</i>.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13456, 'rst66', '2sa', 3, 30, 'Иоав же и брат его Авесса убили Авенира за то, что он умертвил брата их Асаила в сражении у Гаваона.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13455, 'rst66', '2sa', 3, 29, 'пусть падет она на голову Иоава и на весь дом отца его; пусть никогда не остается дом Иоава без семеноточивого, или прокаженного, или опирающегося на посох, или падающего от меча, или нуждающегося в хлебе.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13454, 'rst66', '2sa', 3, 28, 'И услышал после Давид <i>об этом</i> и сказал: невинен я и царство мое вовек пред Господом в крови Авенира, сына Нирова;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13453, 'rst66', '2sa', 3, 27, 'Когда Авенир возвратился в Хеврон, то Иоав отвел его внутрь ворот, как будто для того, чтобы поговорить с ним тайно, и там поразил его в живот. И умер <i>Авенир</i> за кровь Асаила, брата Иоавова.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13452, 'rst66', '2sa', 3, 26, 'И вышел Иоав от Давида и послал гонцов вслед за Авениром; и возвратили они его от колодезя Сира, без ведома Давида.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13451, 'rst66', '2sa', 3, 25, 'Ты знаешь Авенира, сына Нирова: он приходил обмануть тебя, узнать выход твой и вход твой и разведать все, что ты делаешь.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13450, 'rst66', '2sa', 3, 24, 'И пришел Иоав к царю и сказал: что ты сделал? Вот, приходил к тебе Авенир; зачем ты отпустил его, и он ушел?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13449, 'rst66', '2sa', 3, 23, 'Когда Иоав и все войско, ходившее с ним, пришли, то Иоаву рассказали: приходил Авенир, сын Ниров, к царю, и тот отпустил его, и он ушел с миром.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13448, 'rst66', '2sa', 3, 22, 'И вот, слуги Давидовы с Иоавом пришли из похода и принесли с собою много добычи; но Авенира уже не было с Давидом в Хевроне, ибо <i>Давид</i> отпустил его, и он ушел с миром.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13447, 'rst66', '2sa', 3, 21, 'И сказал Авенир Давиду: я встану и пойду и соберу к господину моему царю весь народ Израильский, и они вступят в завет с тобою, и будешь царствовать над всеми, как желает душа твоя. И отпустил Давид Авенира, и он ушел с миром.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13446, 'rst66', '2sa', 3, 20, 'И пришел Авенир к Давиду в Хеврон и с ним двадцать человек, и сделал Давид пир для Авенира и людей, бывших с ним.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13445, 'rst66', '2sa', 3, 19, 'То же говорил Авенир и Вениамитянам. И пошел Авенир в Хеврон, чтобы пересказать Давиду все, чего желали Израиль и весь дом Вениаминов.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13444, 'rst66', '2sa', 3, 18, 'теперь сделайте <i>это</i>, ибо Господь сказал Давиду: «рукою раба Моего Давида Я спасу народ Мой, Израиля, от руки Филистимлян и от руки всех врагов его».');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13443, 'rst66', '2sa', 3, 17, 'И обратился Авенир к старейшинам Израильским, говоря: и вчера и третьего дня вы желали, чтобы Давид был царем над вами,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13442, 'rst66', '2sa', 3, 16, 'Пошел с нею и муж ее и с плачем провожал ее до Бахурима; но Авенир сказал ему: ступай назад. И он возвратился.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13441, 'rst66', '2sa', 3, 15, 'И послал Иевосфей и взял ее от мужа, от Фалтия, сына Лаишева.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13440, 'rst66', '2sa', 3, 14, 'И отправил Давид послов к Иевосфею, сыну Саулову, сказать: отдай жену мою Мелхолу, которую я получил за сто краеобрезаний Филистимских.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13439, 'rst66', '2sa', 3, 13, 'И сказал <i>Давид</i>: хорошо, я заключу союз с тобою, только прошу тебя об одном, именно &mdash; ты не увидишь лица моего, если не приведешь с собою Мелхолы, дочери Саула, когда придешь увидеться со мною.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13438, 'rst66', '2sa', 3, 12, 'И послал Авенир от себя послов к Давиду сказать: чья эта земля? И еще сказать: заключи союз со мною, и рука моя будет с тобою, чтобы обратить к тебе весь народ Израильский.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13437, 'rst66', '2sa', 3, 11, 'И не мог Иевосфей возразить Авениру, ибо боялся его.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13436, 'rst66', '2sa', 3, 10, 'отниму царство от дома Саулова и поставлю престол Давида над Израилем и над Иудою, от Дана до Вирсавии.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13435, 'rst66', '2sa', 3, 9, 'То и то пусть сделает Бог Авениру, и еще больше сделает ему! Как клялся Господь Давиду, так и сделаю ему:');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13434, 'rst66', '2sa', 3, 8, 'Авенир же сильно разгневался на слова Иевосфея и сказал: разве я &mdash; собачья голова? Я против Иуды оказал ныне милость дому Саула, отца твоего, братьям его и друзьям его, и не предал тебя в руки Давида, а ты взыскиваешь ныне на мне грех из‐за женщины.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13433, 'rst66', '2sa', 3, 7, 'У Саула была наложница, по имени Рицпа, дочь Айя. И сказал <i>Иевосфей</i> Авениру: зачем ты вошел к наложнице отца моего?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13432, 'rst66', '2sa', 3, 6, 'Когда была распря между домом Саула и домом Давида, то Авенир поддерживал дом Саула.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13431, 'rst66', '2sa', 3, 5, 'шестой &mdash; Иефераам от Эглы, жены Давидовой. Они родились у Давида в Хевроне.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13430, 'rst66', '2sa', 3, 4, 'четвертый &mdash; Адония, сын Аггифы; пятый &mdash; Сафатия, сын Авиталы;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13429, 'rst66', '2sa', 3, 3, 'а второй <i>сын</i> его &mdash; Далуиа от Авигеи, <i>бывшей</i> жены Навала, Кармилитянки; третий &mdash; Авессалом, сын Маахи, дочери Фалмая, царя Гессурского;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13428, 'rst66', '2sa', 3, 2, 'И родились у Давида сыновья в Хевроне. Первенец его был Амнон от Ахиноамы Изреелитянки,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13427, 'rst66', '2sa', 3, 1, 'И была продолжительная распря между домом Сауловым и домом Давидовым. Давид все более и более усиливался, а дом Саулов более и более ослабевал.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13426, 'rst66', '2sa', 2, 32, 'И взяли Асаила и похоронили его во гробе отца его, что в Вифлееме. Иоав же с людьми своими шел всю ночь и на рассвете прибыл в Хеврон.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13425, 'rst66', '2sa', 2, 31, 'Слуги же Давидовы поразили Вениамитян и людей Авенировых; пало их триста шестьдесят человек.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13424, 'rst66', '2sa', 2, 30, 'И возвратился Иоав от преследования Авенира и собрал весь народ, и недоставало из слуг Давидовых девятнадцати человек, кроме Асаила.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13423, 'rst66', '2sa', 2, 29, 'Авенир же и люди его шли равниною всю ту ночь и перешли Иордан, и прошли весь Битрон, и пришли в Маханаим.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13422, 'rst66', '2sa', 2, 28, 'И затрубил Иоав трубою, и остановился весь народ, и не преследовали более Израильтян; сражение прекратилось.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13421, 'rst66', '2sa', 2, 27, 'И сказал Иоав: жив Бог! Если бы ты не говорил иначе, то еще утром перестали бы люди преследовать братьев своих.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13420, 'rst66', '2sa', 2, 26, 'И воззвал Авенир к Иоаву, и сказал: вечно ли будет пожирать меч? Или ты не знаешь, что последствия будут горестные? И доколе ты не скажешь людям, чтобы они перестали преследовать братьев своих?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13419, 'rst66', '2sa', 2, 25, 'И собрались Вениамитяне вокруг Авенира, и составили одно ополчение, и стали на вершине одного холма.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13418, 'rst66', '2sa', 2, 24, 'И преследовали Иоав и Авесса Авенира. Солнце уже зашло, когда они пришли к холму Амма, что против Гиаха, на дороге к пустыне Гаваонской.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13417, 'rst66', '2sa', 2, 23, 'Но тот не захотел отстать. Тогда Авенир, поворотив копье, поразил его в живот; копье прошло насквозь его, и он упал там же и умер на месте. Все проходившие чрез то место, где пал и умер Асаил, останавливались.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13416, 'rst66', '2sa', 2, 22, 'И повторил Авенир еще, говоря Асаилу: отстань от меня, чтоб я не поверг тебя на землю; тогда с каким лицом явлюсь я к Иоаву, брату твоему?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13415, 'rst66', '2sa', 2, 21, 'И сказал ему Авенир: уклонись направо или налево, и выбери себе одного из отроков и возьми себе его вооружение. Но Асаил не захотел отстать от него.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13414, 'rst66', '2sa', 2, 20, 'И оглянулся Авенир назад и сказал: ты ли это, Асаил? Тот сказал: я.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13413, 'rst66', '2sa', 2, 19, 'И погнался Асаил за Авениром и преследовал его, не уклоняясь ни направо, ни налево от следов Авенира.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13412, 'rst66', '2sa', 2, 18, 'И были там три сына Саруи: Иоав, и Авесса, и Асаил. Асаил же был легок на ноги, как серна в поле.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13411, 'rst66', '2sa', 2, 17, 'И произошло в тот день жесточайшее сражение, и Авенир с людьми Израильскими был поражен слугами Давида.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13410, 'rst66', '2sa', 2, 16, 'Они схватили друг друга за голову, <i>вонзили</i> меч один другому в бок и пали вместе. И было названо это место Хелкаф‐Хаццурим, что в Гаваоне.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13409, 'rst66', '2sa', 2, 15, 'И встали и пошли числом двенадцать Вениамитян со стороны Иевосфея, сына Саулова, и двенадцать из слуг Давидовых.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13408, 'rst66', '2sa', 2, 14, 'И сказал Авенир Иоаву: пусть встанут юноши и поиграют пред нами. И сказал Иоав: пусть встанут.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13407, 'rst66', '2sa', 2, 13, 'Вышел и Иоав, сын Саруи, со слугами Давида, и встретились у Гаваонского пруда, и засели те на одной стороне пруда, а эти на другой стороне пруда.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13406, 'rst66', '2sa', 2, 12, 'И вышел Авенир, сын Ниров, и слуги Иевосфея, сына Саулова, из Маханаима в Гаваон.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13405, 'rst66', '2sa', 2, 11, 'Всего времени, в которое Давид царствовал в Хевроне над домом Иудиным, было семь лет и шесть месяцев.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13404, 'rst66', '2sa', 2, 10, 'Сорок лет было Иевосфею, сыну Саулову, когда он воцарился над Израилем, и царствовал два года. Только дом Иудин остался с Давидом.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13403, 'rst66', '2sa', 2, 9, 'и воцарил его над Галаадом, и Ашуром, и Изреелем, и Ефремом, и Вениамином, и над всем Израилем.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13402, 'rst66', '2sa', 2, 8, 'Но Авенир, сын Ниров, начальник войска Саулова, взял Иевосфея, сына Саулова, и привел его в Маханаим,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13401, 'rst66', '2sa', 2, 7, 'ныне да укрепятся руки ваши, и будьте мужественны; ибо господин ваш Саул умер, а меня помазал дом Иудин царем над собою.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13400, 'rst66', '2sa', 2, 6, 'И ныне да воздаст вам Господь милостью и истиною; и я сделаю вам благодеяние за то, что вы это сделали;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13399, 'rst66', '2sa', 2, 5, 'И отправил Давид послов к жителям Иависа Галаадского, сказать им: благословенны вы у Господа за то, что оказали эту милость господину своему Саулу и погребли его.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13398, 'rst66', '2sa', 2, 4, 'И пришли мужи Иудины и помазали там Давида на царство над домом Иудиным. И донесли Давиду, что жители Иависа Галаадского погребли Саула.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13397, 'rst66', '2sa', 2, 3, 'И людей, бывших с ним, привел Давид, каждого с семейством его, и поселились в городе Хевроне.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13396, 'rst66', '2sa', 2, 2, 'И пошел туда Давид и обе жены его, Ахиноама Изреелитянка и Авигея, <i>бывшая</i> жена Навала, Кармилитянка.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13395, 'rst66', '2sa', 2, 1, 'После сего Давид вопросил Господа, говоря: идти ли мне в какой‐либо из городов Иудиных? И сказал ему Господь: иди. И сказал Давид: куда идти? И сказал Он: в Хеврон.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13394, 'rst66', '2sa', 1, 27, 'Как пали сильные, погибло оружие бранное!');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13393, 'rst66', '2sa', 1, 26, 'Скорблю о тебе, брат мой Ионафан; ты был очень дорог для меня; любовь твоя была для меня превыше любви женской.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13392, 'rst66', '2sa', 1, 25, 'Как пали сильные на брани! Сражен Ионафан на высотах твоих.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13391, 'rst66', '2sa', 1, 24, 'Дочери Израильские! плачьте о Сауле, который одевал вас в багряницу с украшениями и доставлял на одежды ваши золотые уборы.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13390, 'rst66', '2sa', 1, 23, 'Саул и Ионафан, любезные и согласные в жизни своей, не разлучились и в смерти своей; быстрее орлов, сильнее львов <i>они были</i>.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13389, 'rst66', '2sa', 1, 22, 'Без крови раненых, без тука сильных лук Ионафана не возвращался назад, и меч Саула не возвращался даром.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13388, 'rst66', '2sa', 1, 21, 'Горы Гелвуйские! да <i>не сойдет</i> ни роса, ни дождь на вас, и да не будет <i>на вас</i> полей с плодами, ибо там повержен щит сильных, щит Саула, как бы не был он помазан елеем.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13387, 'rst66', '2sa', 1, 20, 'Не рассказывайте в Гефе, не возвещайте на улицах Аскалона, чтобы не радовались дочери Филистимлян, чтобы не торжествовали дочери необрезанных.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13386, 'rst66', '2sa', 1, 19, 'Краса твоя, о Израиль, поражена на высотах твоих! как пали сильные!');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13385, 'rst66', '2sa', 1, 18, 'и повелел научить сынов Иудиных луку, как написано в книге Праведного, и сказал:');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13384, 'rst66', '2sa', 1, 17, 'И оплакал Давид Саула и сына его Ионафана сею плачевною песнью,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13383, 'rst66', '2sa', 1, 16, 'И <i>тот</i> убил его, и он умер. И сказал к нему Давид: кровь твоя на голове твоей, ибо твои уста свидетельствовали на тебя, когда ты говорил: «я убил помазанника Господня».');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13382, 'rst66', '2sa', 1, 15, 'И призвал Давид одного из отроков и сказал ему: подойди, убей его.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13381, 'rst66', '2sa', 1, 14, 'Тогда Давид сказал ему: как не побоялся ты поднять руку, чтобы убить помазанника Господня?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13380, 'rst66', '2sa', 1, 13, 'И сказал Давид отроку, рассказывавшему ему: откуда ты? И сказал он: я &mdash; сын пришельца Амаликитянина.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13379, 'rst66', '2sa', 1, 12, 'и рыдали и плакали, и постились до вечера о Сауле и о сыне его Ионафане, и о народе Господнем и о доме Израилевом, что пали они от меча.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13378, 'rst66', '2sa', 1, 11, 'Тогда схватил Давид одежды свои и разодрал их, так же и все люди, бывшие с ним,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13377, 'rst66', '2sa', 1, 10, 'И я подошел к нему и убил его, ибо знал, что он не будет жив после своего падения; и взял я венец, бывший на голове его, и запястье, бывшее на руке его, и принес их к господину моему сюда.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13376, 'rst66', '2sa', 1, 9, 'Тогда он сказал мне: «подойди ко мне и убей меня, ибо тоска смертная объяла меня, душа моя все еще во мне».');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13375, 'rst66', '2sa', 1, 8, 'И я сказал: «вот я». Он сказал мне: «кто ты?» И я сказал ему: «я &mdash; Амаликитянин».');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13374, 'rst66', '2sa', 1, 7, 'Тогда он оглянулся назад и, увидев меня, позвал меня.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13373, 'rst66', '2sa', 1, 6, 'И сказал отрок, рассказывавший ему: я случайно пришел на гору Гелвуйскую, и вот, Саул пал на свое копье, колесницы же и всадники настигали его.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13372, 'rst66', '2sa', 1, 5, 'И сказал Давид отроку, рассказывавшему ему: как ты знаешь, что Саул и сын его Ионафан умерли?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13371, 'rst66', '2sa', 1, 4, 'И сказал ему Давид: что произошло? расскажи мне. И тот сказал: народ побежал со сражения, и множество из народа пало и умерло, и умерли и Саул и сын его Ионафан.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13370, 'rst66', '2sa', 1, 3, 'И сказал ему Давид: откуда ты пришел? И сказал тот: я убежал из стана Израильского.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13369, 'rst66', '2sa', 1, 2, 'вот, на третий день приходит человек из стана Саулова; одежда на нем разодрана и прах на голове его. Придя к Давиду, он пал на землю и поклонился <i>ему</i>.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13368, 'rst66', '2sa', 1, 1, 'По смерти Саула, когда Давид возвратился от поражения Амаликитян и пробыл в Секелаге два дня,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13367, 'rst66', '1sa', 31, 13, 'и взяли кости их, и погребли под дубом в Иависе, и постились семь дней.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13366, 'rst66', '1sa', 31, 12, 'и поднялись все люди сильные, и шли всю ночь, и взяли тело Саула и тела сыновей его со стены Беф‐Сана, и пришли в Иавис, и сожгли их там;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13365, 'rst66', '1sa', 31, 11, 'И услышали жители Иависа Галаадского о том, как поступили Филистимляне с Саулом,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13364, 'rst66', '1sa', 31, 10, 'и положили оружие его в капище Астарты, а тело его повесили на стене Беф‐Сана.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13363, 'rst66', '1sa', 31, 9, 'И отсекли ему голову, и сняли с него оружие и послали по всей земле Филистимской, чтобы возвестить о сем в капищах идолов своих и народу;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13362, 'rst66', '1sa', 31, 8, 'На другой день Филистимляне пришли грабить убитых, и нашли Саула и трех сыновей его, павших на горе Гелвуйской.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13361, 'rst66', '1sa', 31, 7, 'Израильтяне, жившие на стороне долины и за Иорданом, видя, что люди Израильские побежали и что умер Саул и сыновья его, оставили города свои и бежали, а Филистимляне пришли и засели в них.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13360, 'rst66', '1sa', 31, 6, 'Так умер в тот день Саул и три сына его, и оруженосец его, а также и все люди его вместе.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13359, 'rst66', '1sa', 31, 5, 'Оруженосец его, увидев, что Саул умер, и сам пал на свой меч и умер с ним.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13358, 'rst66', '1sa', 31, 4, 'И сказал Саул оруженосцу своему: обнажи твой меч и заколи меня им, чтобы не пришли эти необрезанные и не убили меня и не издевались надо мною. Но оруженосец не хотел, ибо очень боялся. Тогда Саул взял меч свой и пал на него.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13357, 'rst66', '1sa', 31, 3, 'И битва против Саула сделалась жестокая, и стрелки из луков поражали его, и он очень изранен был стрелками.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13356, 'rst66', '1sa', 31, 2, 'И догнали Филистимляне Саула и сыновей его, и убили Филистимляне Ионафана, и Аминадава, и Малхисуа, сыновей Саула.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13355, 'rst66', '1sa', 31, 1, 'Филистимляне же воевали с Израильтянами, и побежали мужи Израильские от Филистимлян и пали, пораженные, на горе Гелвуе.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13354, 'rst66', '1sa', 30, 31, 'и в Хевроне, и во всех местах, где ходил Давид сам и люди его.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13353, 'rst66', '1sa', 30, 30, 'и в Хорме, и в Хорашане, и в Атахе,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13352, 'rst66', '1sa', 30, 29, 'и в Рахале, и в городах Иерахмеельских, и в городах Кенейских,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13351, 'rst66', '1sa', 30, 28, 'и в Ароере, и в Шифмофе, и в Естемоа,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13350, 'rst66', '1sa', 30, 27, 'тем, которые в Вефиле, и в Рамофе южном, и в Иаттире,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13349, 'rst66', '1sa', 30, 26, 'И пришел Давид в Секелаг и послал из добычи к старейшинам Иудиным, друзьям своим, говоря: «вот вам подарок из добычи, <i>взятой</i> у врагов Господних», &mdash;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13348, 'rst66', '1sa', 30, 25, 'Так было с этого времени и после; и поставил он это в закон и в правило для Израиля до сего дня.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13347, 'rst66', '1sa', 30, 24, 'И кто послушает вас в этом деле? Какова часть ходившим на войну, такова часть должна быть и оставшимся при обозе: на всех должно разделить.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13346, 'rst66', '1sa', 30, 23, 'Но Давид сказал: не делайте так, братья мои, после того, как Господь дал нам это и сохранил нас и предал в руки наши полчище, приходившее против нас.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13345, 'rst66', '1sa', 30, 22, 'Тогда злые и негодные из людей, ходивших с Давидом, стали говорить: за то, что они не ходили с нами, не дадим им из добычи, которую мы отняли; пусть каждый возьмет только свою жену и детей и идет.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13344, 'rst66', '1sa', 30, 21, 'И пришел Давид к тем двумстам человек, которые не были в силах идти за ним и <i>которых</i> он оставил у потока Восор, и вышли они навстречу Давиду и навстречу людям, бывшим с ним. И подошел Давид к этим людям и приветствовал их.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13343, 'rst66', '1sa', 30, 20, 'И взял Давид весь мелкий и крупный скот, и гнали его перед своим скотом и говорили: это &mdash; добыча Давида.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13342, 'rst66', '1sa', 30, 19, 'И не пропало у них ничего, ни малого, ни большого, ни из сыновей, ни из дочерей, ни из добычи, ни из всего, что <i>Амаликитяне</i> взяли у них; все возвратил Давид.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13341, 'rst66', '1sa', 30, 18, 'И отнял Давид все, что взяли Амаликитяне, и обеих жен своих отнял Давид.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13340, 'rst66', '1sa', 30, 17, 'И поражал их Давид от сумерек до вечера другого дня, и никто из них не спасся, кроме четырехсот юношей, которые сели на верблюдов и убежали.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13339, 'rst66', '1sa', 30, 16, 'И он повел его; и вот, <i>Амаликитяне</i>, рассыпавшись по всей той стране, едят и пьют и празднуют по причине великой добычи, которую они взяли из земли Филистимской и из земли Иудейской.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13338, 'rst66', '1sa', 30, 15, 'И сказал ему Давид: доведешь ли меня до этого полчища? И сказал он: поклянись мне Богом, что ты не умертвишь меня и не предашь меня в руки господина моего, и я доведу тебя до этого полчища.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13337, 'rst66', '1sa', 30, 14, 'мы вторгались в полуденную часть Керети и в область Иудину и в полуденную часть Халева, а Секелаг сожгли огнем.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13336, 'rst66', '1sa', 30, 13, 'И сказал ему Давид: чей ты и откуда ты? И сказал он: я &mdash; отрок Египтянин, раб одного Амаликитянина, и бросил меня господин мой, ибо уже три дня, как я заболел;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13335, 'rst66', '1sa', 30, 12, 'и дали ему часть связки смокв и две связки изюму, и он ел и укрепился, ибо он не ел хлеба и не пил воды три дня и три ночи.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13334, 'rst66', '1sa', 30, 11, 'И нашли Египтянина в поле, и привели его к Давиду, и дали ему хлеба, и он ел, и напоили его водою;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13333, 'rst66', '1sa', 30, 10, 'И преследовал Давид сам и четыреста человек; двести же человек остановились, потому что были не в силах перейти поток Восорский.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13332, 'rst66', '1sa', 30, 9, 'И пошел Давид сам и шестьсот мужей, бывших с ним; и пришли к потоку Восор и, усталые, остановились там.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13331, 'rst66', '1sa', 30, 8, 'И вопросил Давид Господа, говоря: преследовать ли мне это полчище, и догоню ли я их? И сказано ему: преследуй, догонишь и отнимешь.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13330, 'rst66', '1sa', 30, 7, 'Но Давид укрепился <i>надеждою</i> на Господа, Бога своего, и сказал Давид Авиафару священнику, сыну Ахимелехову: принеси мне ефод. И принес Авиафар ефод к Давиду.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13329, 'rst66', '1sa', 30, 6, 'Давид сильно был смущен, так как народ хотел побить его камнями; ибо скорбел душою весь народ, каждый о сыновьях своих и дочерях своих.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13328, 'rst66', '1sa', 30, 5, 'Взяты были в плен и обе жены Давида: Ахиноама Изреелитянка и Авигея, <i>бывшая</i> жена Навала, Кармилитянка.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13327, 'rst66', '1sa', 30, 4, 'И поднял Давид и народ, бывший с ним, вопль, и плакали, доколе не стало в них силы плакать.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13326, 'rst66', '1sa', 30, 3, 'И пришел Давид и люди его к городу, и вот, он сожжен огнем, а жены их и сыновья их и дочери их взяты в плен.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13325, 'rst66', '1sa', 30, 2, 'а женщин <i>и всех</i>, бывших в нем, от малого до большого, не умертвили, но увели в плен, и ушли своим путем.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13324, 'rst66', '1sa', 30, 1, 'В третий день после того, как Давид и люди его пошли в Секелаг, Амаликитяне напали с юга на Секелаг и взяли Секелаг и сожгли его огнем,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13323, 'rst66', '1sa', 29, 11, 'И встал Давид, сам и люди его, чтобы идти утром и возвратиться в землю Филистимскую. А Филистимляне пошли <i>на войну</i> в Изреель.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13322, 'rst66', '1sa', 29, 10, 'Итак, встань утром, ты и рабы господина твоего, которые пришли с тобою, и встаньте поутру, и, когда светло будет, идите.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13321, 'rst66', '1sa', 29, 9, 'И отвечал Анхус Давиду: будь уверен, что в моих глазах ты хорош, как Ангел Божий; но князья Филистимские сказали: «пусть он не идет с нами на войну».');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13320, 'rst66', '1sa', 29, 8, 'Но Давид сказал Анхусу: что я сделал и что ты нашел в рабе твоем с того времени, как я пред лицом твоим, и до сего дня, почему бы мне не идти и не воевать с врагами господина моего, царя?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13319, 'rst66', '1sa', 29, 7, 'Итак, возвратись теперь, и иди с миром и не раздражай князей Филистимских.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13318, 'rst66', '1sa', 29, 6, 'И призвал Анхус Давида и сказал ему: жив Господь! ты честен, и глазам моим приятно было бы, чтобы ты выходил и входил со мною в ополчении; ибо я не заметил в тебе худого со времени прихода твоего ко мне до сего дня; но в глазах князей ты не хорош.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13317, 'rst66', '1sa', 29, 5, 'Не тот ли это Давид, которому пели в хороводах, говоря: «Саул поразил тысячи, а Давид &mdash; десятки тысяч»?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13316, 'rst66', '1sa', 29, 4, 'И вознегодовали на него князья Филистимские, и сказали ему князья Филистимские: отпусти ты этого человека, пусть он сидит в своем месте, которое ты ему назначил, чтоб он не шел с нами на войну и не сделался противником нашим на войне. Чем он может умилостивить господина своего, как не головами сих мужей?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13315, 'rst66', '1sa', 29, 3, 'И говорили князья Филистимские: это что за Евреи? Анхус отвечал князьям Филистимским: разве не знаете, что это Давид, раб Саула, царя Израильского? Он при мне уже более года, и я не нашел в нем ничего худого со времени его прихода до сего дня.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13314, 'rst66', '1sa', 29, 2, 'Князья Филистимские шли с сотнями и тысячами, Давид же и люди его шли позади с Анхусом.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13313, 'rst66', '1sa', 29, 1, 'И собрали Филистимляне все ополчения свои в Афеке, а Израильтяне расположились станом у источника, что в Изрееле.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13312, 'rst66', '1sa', 28, 25, 'и предложила Саулу и слугам его, и они поели, и встали, и ушли в ту же ночь.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13311, 'rst66', '1sa', 28, 24, 'У женщины же был в доме откормленный теленок, и она поспешила заколоть его и, взяв муки, замесила и испекла опресноки,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13310, 'rst66', '1sa', 28, 23, 'Но он отказался и сказал: не буду есть. И стали уговаривать его слуги его, а также и женщина; и он послушался голоса их, и встал с земли и сел на ложе.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13309, 'rst66', '1sa', 28, 22, 'теперь прошу, послушайся и ты голоса рабы твоей: я предложу тебе кусок хлеба, поешь, и будет в тебе крепость, когда пойдешь в путь.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13308, 'rst66', '1sa', 28, 21, 'И подошла женщина та к Саулу, и увидела, что он очень испугался, и сказала: вот, раба твоя послушалась голоса твоего и подвергала жизнь свою опасности и исполнила приказание, которое ты дал мне;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13307, 'rst66', '1sa', 28, 20, 'Тогда Саул вдруг пал всем телом своим на землю, ибо сильно испугался слов Самуила; притом и силы не стало в нем, ибо он не ел хлеба весь тот день и всю ночь.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13306, 'rst66', '1sa', 28, 19, 'И предаст Господь Израиля вместе с тобою в руки Филистимлян: завтра ты и сыны твои <i>будете</i> со мною, и стан Израильский предаст Господь в руки Филистимлян.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13305, 'rst66', '1sa', 28, 18, 'Так как ты не послушал гласа Господня и не выполнил ярости гнева Его на Амалика, то Господь и делает это над тобою ныне.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13304, 'rst66', '1sa', 28, 17, 'Господь сделает то, что говорил чрез меня; отнимет Господь царство из рук твоих и отдаст его ближнему твоему, Давиду.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13303, 'rst66', '1sa', 28, 16, 'И сказал Самуил: для чего же ты спрашиваешь меня, когда Господь отступил от тебя и сделался врагом твоим?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13302, 'rst66', '1sa', 28, 15, 'И сказал Самуил Саулу: для чего ты тревожишь меня, чтобы я вышел? И отвечал Саул: тяжело мне очень; Филистимляне воюют против меня, а Бог отступил от меня и более не отвечает мне ни чрез пророков, ни во сне; потому я вызвал тебя, чтобы ты научил меня, что мне делать.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13301, 'rst66', '1sa', 28, 14, 'Какой он видом? &mdash; спросил у нее <i>Саул</i>. Она сказала: выходит из земли муж престарелый, одетый в длинную одежду. Тогда узнал Саул, что это Самуил, и пал лицом на землю и поклонился.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13300, 'rst66', '1sa', 28, 13, 'И сказал ей царь: не бойся; что ты видишь? И отвечала женщина: вижу как бы бога, выходящего из земли.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13299, 'rst66', '1sa', 28, 12, 'И увидела женщина Самуила, и громко вскрикнула; и обратилась женщина к Саулу, говоря: зачем ты обманул меня? ты &mdash; Саул.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13298, 'rst66', '1sa', 28, 11, 'Тогда женщина спросила: кого же вывести тебе? И отвечал он: Самуила выведи мне.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13297, 'rst66', '1sa', 28, 10, 'И поклялся ей Саул Господом, говоря: жив Господь! Не будет тебе беды за это дело.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13296, 'rst66', '1sa', 28, 9, 'Но женщина отвечала ему: ты знаешь, что сделал Саул, как выгнал он из страны волшебников и гадателей; для чего же ты расставляешь сеть душе моей на погибель мне?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13295, 'rst66', '1sa', 28, 8, 'И снял с себя Саул одежды свои и надел другие, и пошел сам и два человека с ним, и пришли они к женщине ночью. И сказал ей <i>Саул:</i> прошу тебя, поворожи мне и выведи мне, о ком я скажу тебе.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13294, 'rst66', '1sa', 28, 7, 'Тогда Саул сказал слугам своим: сыщите мне женщину волшебницу, и я пойду к ней и спрошу ее. И отвечали ему слуги его: здесь в Аэндоре есть женщина волшебница.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13293, 'rst66', '1sa', 28, 6, 'И вопросил Саул Господа; но Господь не отвечал ему ни во сне, ни чрез урим, ни чрез пророков.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13292, 'rst66', '1sa', 28, 5, 'И увидел Саул стан Филистимский и испугался, и крепко дрогнуло сердце его.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13291, 'rst66', '1sa', 28, 4, 'И собрались Филистимляне и пошли и стали станом в Сонаме; собрал и Саул весь народ Израильский, и стали станом на Гелвуе.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13290, 'rst66', '1sa', 28, 3, 'И умер Самуил, и оплакивали его все Израильтяне и погребли его в Раме, в городе его. Саул же изгнал волшебников и гадателей из страны.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13289, 'rst66', '1sa', 28, 2, 'И сказал Давид Анхусу: ныне ты узнаешь, что сделает раб твой. И сказал Анхус Давиду: за то я сделаю тебя хранителем головы моей на все время.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13288, 'rst66', '1sa', 28, 1, 'В то время Филистимляне собрали войска свои для войны, чтобы воевать с Израилем. И сказал Анхус Давиду: да будет тебе известно, что ты пойдешь со мною в ополчение, ты и люди твои.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13287, 'rst66', '1sa', 27, 12, 'И доверился Анхус Давиду, говоря: он опротивел народу своему Израилю и будет слугою моим вовек.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13286, 'rst66', '1sa', 27, 11, 'И не оставлял Давид в живых ни мужчины, ни женщины, и не приводил в Геф, говоря: они могут донести на нас и сказать: «так поступил Давид, и таков образ действий его во все время пребывания в стране Филистимской».');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13285, 'rst66', '1sa', 27, 10, 'И сказал Анхус Давиду: на кого нападали ныне? Давид сказал: на полуденную страну Иудеи и на полуденную страну Иерахмеела и на полуденную страну Кенеи.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13284, 'rst66', '1sa', 27, 9, 'И опустошал Давид ту страну, и не оставлял в живых ни мужчины, ни женщины, и забирал овец, и волов, и ослов, и верблюдов, и одежду; и возвращался, и приходил к Анхусу.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13283, 'rst66', '1sa', 27, 8, 'И выходил Давид с людьми своими и нападал на Гессурян и Гирзеян и Амаликитян, которые издавна населяли эту страну до Сура и даже до земли Египетской.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13282, 'rst66', '1sa', 27, 7, 'Всего времени, какое прожил Давид в стране Филистимской, было год и четыре месяца.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13281, 'rst66', '1sa', 27, 6, 'Тогда дал ему Анхус Секелаг, посему Секелаг и остался за царями Иудейскими доныне.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13280, 'rst66', '1sa', 27, 5, 'И сказал Давид Анхусу: если я приобрел благоволение в глазах твоих, то пусть дано будет мне место в одном из малых городов, и я буду жить там; для чего рабу твоему жить в царском городе вместе с тобою?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13279, 'rst66', '1sa', 27, 4, 'И донесли Саулу, что Давид убежал в Геф, и не стал он более искать его.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13278, 'rst66', '1sa', 27, 3, 'И жил Давид у Анхуса в Гефе, сам и люди его, каждый с семейством своим, Давид и обе жены его &mdash; Ахиноама Изреелитянка и Авигея, <i>бывшая</i> жена Навала, Кармилитянка.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13277, 'rst66', '1sa', 27, 2, 'И встал Давид, и отправился, сам и шестьсот мужей, бывших с ним, к Анхусу, сыну Маоха, царю Гефскому.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13276, 'rst66', '1sa', 27, 1, 'И сказал Давид в сердце своем: когда‐нибудь попаду я в руки Саула, и нет для меня ничего лучшего, как убежать в землю Филистимскую; и отстанет от меня Саул <i>и не будет</i> искать меня более по всем пределам Израильским, и я спасусь от руки его.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13275, 'rst66', '1sa', 26, 25, 'И сказал Саул Давиду: благословен ты, сын мой, Давид; и дело сделаешь, и превозмочь превозможешь. И пошел Давид своим путем, а Саул возвратился в свое место.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13274, 'rst66', '1sa', 26, 24, 'и пусть, как драгоценна была жизнь твоя ныне в глазах моих, так ценится моя жизнь в очах Господа, и да избавит меня от всякой беды!');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13273, 'rst66', '1sa', 26, 23, 'и да воздаст Господь каждому по правде его и по истине его, так как Господь предавал тебя в руки <i>мои</i>, но я не захотел поднять руки моей на помазанника Господня;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13272, 'rst66', '1sa', 26, 22, 'И отвечал Давид и сказал: вот копье царя; пусть один из отроков придет и возьмет его;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13271, 'rst66', '1sa', 26, 21, 'И сказал Саул: согрешил я; возвратись, сын мой, Давид, ибо я не буду больше делать тебе зла, потому что душа моя была дорога ныне в глазах твоих; безумно поступал я и очень много погрешал.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13270, 'rst66', '1sa', 26, 20, 'Да не прольется же кровь моя на землю пред лицом Господа; ибо царь Израилев вышел искать одну блоху, как гоняются за куропаткою по горам.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13269, 'rst66', '1sa', 26, 19, 'И ныне пусть выслушает господин мой, царь, слова раба своего: если Господь возбудил тебя против меня, то да будет это от тебя благовонною жертвою; если же &mdash; сыны человеческие, то прокляты они пред Господом, ибо они изгнали меня ныне, чтобы не принадлежать мне к наследию Господа, говоря: «ступай, служи богам чужим».');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13268, 'rst66', '1sa', 26, 18, 'И сказал <i>еще:</i> за что господин мой преследует раба своего? что я сделал? какое зло в руке моей?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13267, 'rst66', '1sa', 26, 17, 'И узнал Саул голос Давида и сказал: твой ли это голос, сын мой, Давид? И сказал Давид: мой голос, господин мой, царь.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13266, 'rst66', '1sa', 26, 16, 'Нехорошо ты это делаешь; жив Господь! вы достойны смерти за то, что не бережете господина вашего, помазанника Господня. Посмотри, где копье царя и сосуд с водою, что <i>были</i> у изголовья его?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13265, 'rst66', '1sa', 26, 15, 'И сказал Давид Авениру: не муж ли ты, и кто равен тебе в Израиле? Для чего же ты не бережешь господина твоего, царя? ибо приходил некто из народа, чтобы погубить царя, господина твоего.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13264, 'rst66', '1sa', 26, 14, 'И воззвал Давид к народу и Авениру, сыну Нирову, говоря: отвечай, Авенир. И отвечал Авенир и сказал: кто ты, что кричишь и <i>беспокоишь</i> царя?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13263, 'rst66', '1sa', 26, 13, 'И перешел Давид на другую сторону и стал на вершине горы вдали; большое расстояние <i>было</i> между ними.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13262, 'rst66', '1sa', 26, 12, 'И взял Давид копье и сосуд с водою у изголовья Саула, и пошли они к себе; и никто не видел, и никто не знал, и никто не проснулся, но все спали, ибо сон от Господа напал на них.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13261, 'rst66', '1sa', 26, 11, 'а возьми его копье, которое у изголовья его, и сосуд с водою, и пойдем к себе.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13260, 'rst66', '1sa', 26, 10, 'И сказал Давид: жив Господь! пусть поразит его Господь, или придет день его, и он умрет, или пойдет на войну и погибнет; меня же да не попустит Господь поднять руку мою на помазанника Господня;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13259, 'rst66', '1sa', 26, 9, 'Но Давид сказал Авессе: не убивай его; ибо кто, подняв руку на помазанника Господня, останется ненаказанным?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13258, 'rst66', '1sa', 26, 8, 'Авесса сказал Давиду: предал Бог ныне врага твоего в руки твои; итак, позволь, я пригвожду его копьем к земле одним ударом и не повторю <i>удара</i>.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13257, 'rst66', '1sa', 26, 7, 'И пришел Давид с Авессою к людям <i>Сауловым</i> ночью; и вот, Саул лежит, спит в шатре, и копье его воткнуто в землю у изголовья его; Авенир же и народ лежат вокруг него.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13256, 'rst66', '1sa', 26, 6, 'И обратился Давид и сказал Ахимелеху Хеттеянину и Авессе, сыну Саруину, брату Иоава, говоря: кто пойдет со мною к Саулу в стан? И отвечал Авесса: я пойду с тобою.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13255, 'rst66', '1sa', 26, 5, 'И встал Давид и пошел к месту, на котором Саул расположился станом, и увидел Давид место, где спал Саул и Авенир, сын Ниров, военачальник его. Саул же спал в шатре, а народ расположился вокруг него.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13254, 'rst66', '1sa', 26, 4, 'и послал Давид соглядатаев и узнал, что Саул действительно пришел.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13253, 'rst66', '1sa', 26, 3, 'И расположился Саул на холме Гахила, что направо от Иесимона, при дороге; Давид же находился в пустыне и видел, что Саул шел за ним в пустыню;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13252, 'rst66', '1sa', 26, 2, 'И встал Саул и спустился в пустыню Зиф, и с ним три тысячи отборных мужей Израильских, чтоб искать Давида в пустыне Зиф.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13251, 'rst66', '1sa', 26, 1, 'Пришли Зифеи к Саулу в Гиву и сказали: вот, Давид скрывается у нас на холме Гахила, что направо от Иесимона.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13250, 'rst66', '1sa', 25, 44, 'Саул же отдал дочь свою Мелхолу, жену Давидову, Фалтию, сыну Лаиша, что из Галлима.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13249, 'rst66', '1sa', 25, 43, 'И Ахиноаму из Изрееля взял Давид, и обе они были его женами.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13248, 'rst66', '1sa', 25, 42, 'И собралась Авигея поспешно и села на осла, и пять служанок сопровождали ее; и пошла она за послами Давида и сделалась его женою.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13247, 'rst66', '1sa', 25, 41, 'Она встала и поклонилась лицом до земли и сказала: вот, раба твоя <i>готова</i> быть служанкою, чтобы омывать ноги слуг господина моего.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13246, 'rst66', '1sa', 25, 40, 'И пришли слуги Давидовы к Авигее на Кармил и сказали ей так: Давид послал нас к тебе, чтобы взять тебя ему в жену.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13245, 'rst66', '1sa', 25, 39, 'И услышал Давид, что Навал умер, и сказал: благословен Господь, воздавший за посрамление, нанесенное мне Навалом, и сохранивший раба Своего от зла; Господь обратил злобу Навала на его же голову. И послал Давид сказать Авигее, что он берет ее себе в жену.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13244, 'rst66', '1sa', 25, 38, 'Дней через десять поразил Господь Навала, и он умер.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13243, 'rst66', '1sa', 25, 37, 'Утром же, когда Навал отрезвился, жена его рассказала ему об этом, и замерло в нем сердце его, и стал он как камень.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13242, 'rst66', '1sa', 25, 36, 'И пришла Авигея к Навалу, и вот, у него пир в доме его, как пир царский, и сердце Навала было весело; он же был очень пьян; и не сказала ему ни слова, ни большого, ни малого, до утра.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13241, 'rst66', '1sa', 25, 35, 'И принял Давид из рук ее то, что она принесла ему, и сказал ей: иди с миром в дом твой; вот, я послушался голоса твоего и почтил лицо твое.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13240, 'rst66', '1sa', 25, 34, 'Но, &mdash; жив Господь, Бог Израилев, удержавший меня от нанесения зла тебе, &mdash; если бы ты не поспешила и не пришла навстречу мне, то до рассвета утреннего я не оставил бы Навалу мочащегося к стене.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13239, 'rst66', '1sa', 25, 33, 'и благословен разум твой, и благословенна ты за то, что ты теперь не допустила меня идти на пролитие крови и отмстить за себя.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13238, 'rst66', '1sa', 25, 32, 'И сказал Давид Авигее: благословен Господь, Бог Израилев, Который послал тебя ныне навстречу мне,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13237, 'rst66', '1sa', 25, 31, 'то не будет это сердцу господина моего огорчением и беспокойством, что не пролил напрасно крови и сберег себя от мщения. И Господь облагодетельствует господина моего, и вспомнишь рабу твою.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13236, 'rst66', '1sa', 25, 30, 'И когда сделает Господь господину моему все, что говорил о тебе доброго, и поставит тебя вождем над Израилем,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13235, 'rst66', '1sa', 25, 29, 'Если восстанет человек преследовать тебя и искать души твоей, то душа господина моего будет завязана в узле жизни у Господа, Бога твоего, а душу врагов твоих бросит Он как бы пращою.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13234, 'rst66', '1sa', 25, 28, 'Прости вину рабы твоей; Господь непременно устроит господину моему дом твердый, ибо войны Господа ведет господин мой, и зло не найдется в тебе во всю жизнь твою.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13233, 'rst66', '1sa', 25, 27, 'Вот эти дары, которые принесла раба твоя господину моему, чтобы дать их отрокам, служащим господину моему.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13232, 'rst66', '1sa', 25, 26, 'И ныне, господин мой, жив Господь и жива душа твоя, Господь не попустит тебе идти на пролитие крови и удержит руку твою от мщения, и ныне да будут, как Навал, враги твои и злоумышляющие против господина моего.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13231, 'rst66', '1sa', 25, 25, 'Пусть господин мой не обращает внимания на этого злого человека, на Навала; ибо каково имя его, таков и он. Навал &mdash; имя его, и безумие его с ним. А я, раба твоя, не видела слуг господина моего, которых ты присылал.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13230, 'rst66', '1sa', 25, 24, 'и пала к ногам его, и сказала: на мне грех, господин мой; позволь рабе твоей говорить в уши твои и послушай слов рабы твоей.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13229, 'rst66', '1sa', 25, 23, 'Когда Авигея увидела Давида, то поспешила сойти с осла и пала пред Давидом на лицо свое и поклонилась до земли;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13228, 'rst66', '1sa', 25, 22, 'пусть то и то сделает Бог с врагами Давида, и еще больше сделает, если до рассвета утреннего из всего, что принадлежит Навалу, я оставлю мочащегося к стене.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13227, 'rst66', '1sa', 25, 21, 'И Давид сказал: да, напрасно я охранял в пустыне все имущество этого человека, и ничего не пропало из принадлежащего ему; он платит мне злом за добро;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13226, 'rst66', '1sa', 25, 20, 'Когда же она, сидя на осле, спускалась по извилинам горы, вот, навстречу ей идет Давид и люди его, и она встретилась с ними.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13225, 'rst66', '1sa', 25, 19, 'и сказала слугам своим: ступайте впереди меня, вот, я пойду за вами. А мужу своему Навалу ничего не сказала.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13224, 'rst66', '1sa', 25, 18, 'Тогда Авигея поспешно взяла двести хлебов, и два меха с вином, и пять овец приготовленных, и пять мер сушеных зерен, и сто связок изюму, и двести связок смокв, и навьючила на ослов,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13223, 'rst66', '1sa', 25, 17, 'итак подумай и посмотри, что делать; ибо неминуемо угрожает беда господину нашему и всему дому его, а он &mdash; человек злой, нельзя говорить с ним.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13222, 'rst66', '1sa', 25, 16, 'они были для нас оградою и днем и ночью во все время, когда мы пасли стада вблизи их;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13221, 'rst66', '1sa', 25, 15, 'а эти люди очень добры к нам, не обижали нас, и ничего не пропало у нас во все время, когда мы ходили с ними, будучи в поле;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13220, 'rst66', '1sa', 25, 14, 'Авигею же, жену Навала, известил один из слуг, сказав: вот, Давид присылал из пустыни послов приветствовать нашего господина, но он обошелся с ними грубо;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13219, 'rst66', '1sa', 25, 13, 'Тогда Давид сказал людям своим: опояшьтесь каждый мечом своим. И все опоясались мечами своими, опоясался и сам Давид своим мечом, и пошли за Давидом около четырехсот человек, а двести остались при обозе.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13218, 'rst66', '1sa', 25, 12, 'И пошли назад люди Давида своим путем, и возвратились, и пришли, и пересказали ему все слова сии.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13217, 'rst66', '1sa', 25, 11, 'неужели мне взять хлебы мои и воду мою, и мясо, приготовленное мною для стригущих овец у меня, и отдать людям, о которых не знаю, откуда они?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13216, 'rst66', '1sa', 25, 10, 'И Навал отвечал слугам Давидовым и сказал: кто такой Давид и кто такой сын Иессеев? Ныне стало много рабов, бегающих от господ своих;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13215, 'rst66', '1sa', 25, 9, 'И пошли люди Давидовы, и сказали Навалу от имени Давида все эти слова, и умолкли.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13214, 'rst66', '1sa', 25, 8, 'спроси слуг твоих, и они скажут тебе; итак, да найдут отроки благоволение в глазах твоих, ибо в добрый день пришли мы; дай же рабам твоим и сыну твоему Давиду, что найдет рука твоя».');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13213, 'rst66', '1sa', 25, 7, 'ныне я услышал, что у тебя стригут <i>овец</i>. Вот, пастухи твои были с нами, и мы не обижали их, и ничего у них не пропало во все время их пребывания на Кармиле;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13212, 'rst66', '1sa', 25, 6, 'и скажите так: «мир тебе, мир дому твоему, мир всему твоему;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13211, 'rst66', '1sa', 25, 5, 'И послал Давид десять отроков, и сказал Давид отрокам: взойдите на Кармил и пойдите к Навалу, и приветствуйте его от моего имени,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13210, 'rst66', '1sa', 25, 4, 'И услышал Давид в пустыне, что Навал стрижет овец своих.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13209, 'rst66', '1sa', 25, 3, 'Имя человека того &mdash; Навал, а имя жены его &mdash; Авигея; эта женщина <i>была</i> весьма умная и красивая лицом, а он &mdash; человек жестокий и злой нравом; он был из рода Халева.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13208, 'rst66', '1sa', 25, 2, 'Был некто в Маоне, а имение его на Кармиле, человек очень богатый; у него было три тысячи овец и тысяча коз; и был он при стрижке овец своих на Кармиле.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13207, 'rst66', '1sa', 25, 1, 'И умер Самуил; и собрались все Израильтяне, и плакали по нем, и погребли его в доме его, в Раме. Давид встал и сошел к пустыне Фаран.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13206, 'rst66', '1sa', 24, 23, 'И поклялся Давид Саулу. И пошел Саул в дом свой, Давид же и люди его взошли в место укрепленное.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13205, 'rst66', '1sa', 24, 22, 'Итак, поклянись мне Господом, что ты не искоренишь потомства моего после меня и не уничтожишь имени моего в доме отца моего.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13204, 'rst66', '1sa', 24, 21, 'И теперь я знаю, что ты непременно будешь царствовать, и царство Израилево будет твердо в руке твоей.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13203, 'rst66', '1sa', 24, 20, 'Кто, найдя врага своего, отпустил бы его в добрый путь? Господь воздаст тебе добром за то, что сделал ты мне сегодня.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13202, 'rst66', '1sa', 24, 19, 'ты показал это сегодня, поступив со мною милостиво, когда Господь предавал меня в руки твои, ты не убил меня.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13201, 'rst66', '1sa', 24, 18, 'и сказал Давиду: ты правее меня, ибо ты воздал мне добром, а я воздавал тебе злом;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13200, 'rst66', '1sa', 24, 17, 'Когда кончил Давид говорить слова сии к Саулу, Саул сказал: твой ли это голос, сын мой Давид? И возвысил Саул голос свой, и плакал,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13199, 'rst66', '1sa', 24, 16, 'Господь да будет судьею и рассудит между мною и тобою. Он рассмотрит, разберет дело мое, и спасет меня от руки твоей.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13198, 'rst66', '1sa', 24, 15, 'Против кого вышел царь Израильский? За кем ты гоняешься? За мертвым псом, за одною блохою.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13197, 'rst66', '1sa', 24, 14, 'как говорит древняя притча: «от беззаконных исходит беззаконие». А моя рука не будет на тебе.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13196, 'rst66', '1sa', 24, 13, 'Да рассудит Господь между мною и тобою, и да отмстит тебе Господь за меня; но рука моя не будет на тебе,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13195, 'rst66', '1sa', 24, 12, 'Отец мой! посмотри на край одежды твоей в руке моей; я отрезал край одежды твоей, а тебя не убил: узнай и убедись, что нет в руке моей зла, ни коварства, и я не согрешил против тебя; а ты ищешь души моей, чтоб отнять ее.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13194, 'rst66', '1sa', 24, 11, 'Вот, сегодня видят глаза твои, что Господь предавал тебя ныне в руки мои в пещере; и мне говорили, чтоб убить тебя; но я пощадил тебя и сказал: «не подниму руки моей на господина моего, ибо он помазанник Господа».');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13193, 'rst66', '1sa', 24, 10, 'И сказал Давид Саулу: зачем ты слушаешь речи людей, которые говорят: «вот, Давид умышляет зло на тебя»?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13192, 'rst66', '1sa', 24, 9, 'Потом встал и Давид, и вышел из пещеры, и закричал вслед Саула, говоря: господин мой, царь! Саул оглянулся назад, и Давид пал лицом на землю и поклонился <i>ему</i>.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13191, 'rst66', '1sa', 24, 8, 'И удержал Давид людей своих сими словами и не дал им восстать на Саула. А Саул встал и вышел из пещеры на дорогу.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13190, 'rst66', '1sa', 24, 7, 'И сказал он людям своим: да не попустит мне Господь сделать это господину моему, помазаннику Господню, чтобы наложить руку мою на него, ибо он помазанник Господень.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13189, 'rst66', '1sa', 24, 6, 'Но после сего больно стало сердцу Давида, что он отрезал край от одежды Саула.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13188, 'rst66', '1sa', 24, 5, 'И говорили Давиду люди его: вот день, о котором говорил тебе Господь: «вот, Я предам врага твоего в руки твои, и сделаешь с ним, что тебе угодно». Давид встал и тихонько отрезал край от верхней одежды Саула.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13187, 'rst66', '1sa', 24, 4, 'И пришел к загону овечьему, при дороге; там была пещера, и зашел туда Саул для нужды; Давид же и люди его сидели в глубине пещеры.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13186, 'rst66', '1sa', 24, 3, 'И взял Саул три тысячи отборных мужей из всего Израиля и пошел искать Давида и людей его по горам, где живут серны.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13185, 'rst66', '1sa', 24, 2, 'Когда Саул возвратился от Филистимлян, его известили, говоря: вот, Давид в пустыне Ен‐Гадди.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13184, 'rst66', '1sa', 24, 1, 'И вышел Давид оттуда и жил в безопасных местах Ен‐Гадди.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13183, 'rst66', '1sa', 23, 28, 'И возвратился Саул от преследования Давида и пошел навстречу Филистимлянам; посему и назвали это место: Села‐Гаммахлекоф.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13182, 'rst66', '1sa', 23, 27, 'тогда пришел к Саулу вестник, говоря: поспешай и приходи, ибо Филистимляне напали на землю.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13181, 'rst66', '1sa', 23, 26, 'И шел Саул по одной стороне горы, а Давид с людьми своими был на другой стороне горы. И когда Давид спешил уйти от Саула, а Саул с людьми своими шел в обход Давиду и людям его, чтобы захватить их;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13180, 'rst66', '1sa', 23, 25, 'И пошел Саул с людьми своими искать <i>его</i>. Но Давида известили об этом, и он перешел к скале и оставался в пустыне Маон. И услышал Саул, и погнался за Давидом в пустыню Маон.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13179, 'rst66', '1sa', 23, 24, 'И встали они и пошли в Зиф прежде Саула. Давид же и люди его были в пустыне Маон, на равнине, направо от Иесимона.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13178, 'rst66', '1sa', 23, 23, 'и высмотрите, и разведайте о всех убежищах, в которых он скрывается, и возвратитесь ко мне с верным известием, и я пойду с вами; и если он в этой земле, я буду искать его во всех тысячах Иудиных.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13177, 'rst66', '1sa', 23, 22, 'идите, удостоверьтесь еще, разведайте <i>и</i> высмотрите место его, где будет нога его, <i>и</i> кто видел его там, ибо мне говорят, что он очень хитер;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13176, 'rst66', '1sa', 23, 21, 'И сказал им Саул: благословенны вы у Господа за то, что пожалели о мне;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13175, 'rst66', '1sa', 23, 20, 'итак, по желанию души твоей, царь, иди; а наше дело будет предать его в руки царя.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13174, 'rst66', '1sa', 23, 19, 'И пришли Зифеи к Саулу в Гиву, говоря: вот, Давид скрывается у нас в неприступных местах, в лесу, на холме Гахила, что направо от Иесимона;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13173, 'rst66', '1sa', 23, 18, 'И заключили они между собою завет пред лицом Господа; и Давид остался в лесу, а Ионафан пошел в дом свой.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13172, 'rst66', '1sa', 23, 17, 'и сказал ему: не бойся, ибо не найдет тебя рука отца моего Саула, и ты будешь царствовать над Израилем, а я буду вторым по тебе; и Саул, отец мой, знает это.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13171, 'rst66', '1sa', 23, 16, 'И встал Ионафан, сын Саула, и пришел к Давиду в лес, и укрепил его упованием на Бога,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13170, 'rst66', '1sa', 23, 15, 'И видел Давид, что Саул вышел искать души его; Давид же был в пустыне Зиф в лесу.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13169, 'rst66', '1sa', 23, 14, 'Давид же пребывал в пустыне в неприступных местах и потом на горе в пустыне Зиф. Саул искал его всякий день; но Бог не предал <i>Давида</i> в руки его.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13168, 'rst66', '1sa', 23, 13, 'Тогда поднялся Давид и люди его, около шестисот человек, и вышли из Кеиля и ходили, где могли. Саулу же было донесено, что Давид убежал из Кеиля, и тогда он отменил поход.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13167, 'rst66', '1sa', 23, 12, 'И сказал Давид: предадут ли жители Кеиля меня и людей моих в руки Саула? И сказал Господь: предадут.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13166, 'rst66', '1sa', 23, 11, 'Предадут ли меня жители Кеиля в руки его? И придет ли сюда Саул, как слышал раб Твой? Господи, Боже Израилев! открой рабу Твоему. И сказал Господь: придет.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13165, 'rst66', '1sa', 23, 10, 'И сказал Давид: Господи, Боже Израилев! раб Твой услышал, что Саул хочет прийти в Кеиль, разорить город ради меня.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13164, 'rst66', '1sa', 23, 9, 'Когда узнал Давид, что Саул задумал против него злое, сказал священнику Авиафару: принеси ефод.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13163, 'rst66', '1sa', 23, 8, 'И созвал Саул весь народ на войну, чтоб идти к Кеилю, осадить Давида и людей его.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13162, 'rst66', '1sa', 23, 7, 'И донесли Саулу, что Давид пришел в Кеиль, и Саул сказал: Бог предал его в руки мои, ибо он запер себя, войдя в город с воротами и запорами.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13161, 'rst66', '1sa', 23, 6, 'Когда Авиафар, сын Ахимелеха, прибежал к Давиду в Кеиль, то принес с собою и ефод.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13160, 'rst66', '1sa', 23, 5, 'И пошел Давид с людьми своими в Кеиль, и воевал с Филистимлянами, и угнал скот их, и нанес им великое поражение, и спас Давид жителей Кеиля.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13159, 'rst66', '1sa', 23, 4, 'Тогда снова вопросил Давид Господа, и отвечал ему Господь и сказал: встань и иди в Кеиль, ибо Я предам Филистимлян в руки твои.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13158, 'rst66', '1sa', 23, 3, 'Но бывшие с Давидом сказали ему: вот, мы боимся здесь в Иудее, как же нам идти в Кеиль против ополчений Филистимских?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13157, 'rst66', '1sa', 23, 2, 'И вопросил Давид Господа, говоря: идти ли мне, и поражу ли я этих Филистимлян? И отвечал Господь Давиду: иди, ты поразишь Филистимлян и спасешь Кеиль.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13156, 'rst66', '1sa', 23, 1, 'И известили Давида, говоря: вот, Филистимляне напали на Кеиль и расхищают гумна.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13155, 'rst66', '1sa', 22, 23, 'останься у меня, не бойся, ибо кто будет искать моей души, будет искать и твоей души; ты будешь у меня под охранением.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13154, 'rst66', '1sa', 22, 22, 'И сказал Давид Авиафару: я знал в тот день, когда там был Доик Идумеянин, что он непременно донесет Саулу; я виновен во всех душах дома отца твоего;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13153, 'rst66', '1sa', 22, 21, 'И рассказал Авиафар Давиду, что Саул умертвил священников Господних.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13152, 'rst66', '1sa', 22, 20, 'Спасся один только сын Ахимелеха, сына Ахитува, по имени Авиафар, и убежал к Давиду.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13151, 'rst66', '1sa', 22, 19, 'и Номву, город священников, поразил мечом; и мужчин и женщин, и юношей и младенцев, и волов и ослов и овец поразил мечом.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13150, 'rst66', '1sa', 22, 18, 'И сказал царь Доику: ступай ты и умертви священников. И пошел Доик Идумеянин, и напал на священников, и умертвил в тот день восемьдесят пять мужей, носивших льняной ефод;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13149, 'rst66', '1sa', 22, 17, 'И сказал царь телохранителям, стоявшим при нем: ступайте, умертвите священников Господних, ибо и их рука с Давидом, и они знали, что он убежал, и не открыли мне. Но слуги царя не хотели поднять рук своих на убиение священников Господних.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13148, 'rst66', '1sa', 22, 16, 'И сказал царь: ты должен умереть, Ахимелех, ты и весь дом отца твоего.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13147, 'rst66', '1sa', 22, 15, 'Теперь ли я стал вопрошать для него Бога? Нет, не обвиняй в этом, царь, раба твоего и весь дом отца моего, ибо во всем этом деле не знает раб твой ни малого, ни великого.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13146, 'rst66', '1sa', 22, 14, 'И отвечал Ахимелех царю и сказал: кто из всех рабов твоих верен, как Давид? он и зять царя, и исполнитель повелений твоих, и почтен в доме твоем.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13145, 'rst66', '1sa', 22, 13, 'И сказал ему Саул: для чего вы сговорились против меня, ты и сын Иессея, что ты дал ему хлебы и меч и вопросил о нем Бога, чтоб он восстал против меня и строил мне ковы, как это ныне видно?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13144, 'rst66', '1sa', 22, 12, 'И сказал Саул: послушай, сын Ахитува. И тот отвечал: вот я, господин мой.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13143, 'rst66', '1sa', 22, 11, 'И послал царь призвать Ахимелеха, сына Ахитувова, священника, и весь дом отца его, священников, что в Номве; и пришли они все к царю.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13142, 'rst66', '1sa', 22, 10, 'и тот вопросил о нем Господа, и дал ему продовольствие, и меч Голиафа Филистимлянина отдал ему.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13141, 'rst66', '1sa', 22, 9, 'И отвечал Доик Идумеянин, стоявший со слугами Сауловыми, и сказал: я видел, как сын Иессея приходил в Номву к Ахимелеху, сыну Ахитува,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13140, 'rst66', '1sa', 22, 8, 'что вы все сговорились против меня, и никто не открыл мне, когда сын мой вступил в дружбу с сыном Иессея, и никто из вас не пожалел обо мне и не открыл мне, что сын мой возбудил против меня раба моего строить мне ковы, как это ныне видно?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13139, 'rst66', '1sa', 22, 7, 'И сказал Саул слугам своим, окружавшим его: послушайте, сыны Вениаминовы, неужели всем вам даст сын Иессея поля и виноградники и всех вас поставит тысяченачальниками и сотниками,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13138, 'rst66', '1sa', 22, 6, 'И услышал Саул, что Давид появился и люди, бывшие с ним. Саул сидел тогда в Гиве под дубом на горе, с копьем в руке, и все слуги его окружали его.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13137, 'rst66', '1sa', 22, 5, 'Но пророк Гад сказал Давиду: не оставайся в этом убежище, но ступай, иди в землю Иудину. И пошел Давид и пришел в лес Херет.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13136, 'rst66', '1sa', 22, 4, 'И привел их к царю Моавитскому, и жили они у него все время, доколе Давид был в оном убежище.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13135, 'rst66', '1sa', 22, 3, 'Оттуда пошел Давид в Массифу Моавитскую и сказал царю Моавитскому: пусть отец мой и мать моя побудут у вас, доколе я не узнаю, что сделает со мною Бог.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13134, 'rst66', '1sa', 22, 2, 'И собрались к нему все притесненные и все должники и все огорченные душою, и сделался он начальником над ними; и было с ним около четырехсот человек.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13133, 'rst66', '1sa', 22, 1, 'И вышел Давид оттуда и убежал в пещеру Одолламскую, и услышали братья его и весь дом отца его и пришли к нему туда.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13132, 'rst66', '1sa', 21, 15, 'Разве мало у меня сумасшедших, что вы привели его, чтобы он юродствовал предо мною? Неужели он войдет в дом мой?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13131, 'rst66', '1sa', 21, 14, 'И сказал Анхус рабам своим: видите, он человек сумасшедший; для чего вы привели его ко мне?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13130, 'rst66', '1sa', 21, 13, 'И изменил лицо свое пред ними, и притворился безумным в их глазах, и чертил на дверях, и пускал слюну по бороде своей.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13129, 'rst66', '1sa', 21, 12, 'Давид положил слова эти в сердце своем и сильно боялся Анхуса, царя Гефского.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13128, 'rst66', '1sa', 21, 11, 'И сказали Анхусу слуги его: не это ли Давид, царь той страны? не ему ли пели в хороводах и говорили: «Саул поразил тысячи, а Давид &mdash; десятки тысяч»?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13127, 'rst66', '1sa', 21, 10, 'И встал Давид, и убежал в тот же день от Саула, и пришел к Анхусу, царю Гефскому.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13126, 'rst66', '1sa', 21, 9, 'И сказал священник: вот меч Голиафа Филистимлянина, которого ты поразил в долине дуба, завернутый в одежду, позади ефода; если хочешь, возьми его; другого, кроме этого, нет здесь. И сказал Давид: нет ему подобного, дай мне его.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13125, 'rst66', '1sa', 21, 8, 'И сказал Давид Ахимелеху: нет ли здесь у тебя под рукою копья или меча? ибо я не взял с собою ни меча, ни другого оружия, так как поручение царя было спешное.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13124, 'rst66', '1sa', 21, 7, 'Там находился в тот день пред Господом один из слуг Сауловых, по имени Доик, Идумеянин, начальник пастухов Сауловых.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13123, 'rst66', '1sa', 21, 6, 'И дал ему священник священного хлеба; ибо не было у него хлеба, кроме хлебов предложения, которые взяты были от лица Господа, чтобы по снятии их положить теплые хлебы.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13122, 'rst66', '1sa', 21, 5, 'И отвечал Давид священнику и сказал ему: женщин при нас не было ни вчера, ни третьего дня, со времени, как я вышел, и сосуды отроков чисты, а если дорога нечиста, то <i>хлеб</i> останется чистым в сосудах.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13121, 'rst66', '1sa', 21, 4, 'И отвечал священник Давиду, говоря: нет у меня под рукою простого хлеба, а есть хлеб священный; если только люди <i>твои</i> воздержались от женщин.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13120, 'rst66', '1sa', 21, 3, 'итак, что есть у тебя под рукою, дай мне, хлебов пять или что найдется.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13119, 'rst66', '1sa', 21, 2, 'И сказал Давид Ахимелеху священнику: царь поручил мне дело и сказал мне: «пусть никто не знает, за чем я послал тебя и что поручил тебе»; поэтому людей я оставил на известном месте;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13118, 'rst66', '1sa', 21, 1, 'И пришел Давид в Номву к Ахимелеху священнику, и смутился Ахимелех при встрече с Давидом и сказал ему: почему ты один, и никого нет с тобою?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13117, 'rst66', '1sa', 20, 43, 'И встал <i>Давид</i> и пошел, а Ионафан возвратился в город.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13116, 'rst66', '1sa', 20, 42, 'И сказал Ионафан Давиду: иди с миром; а в чем клялись мы оба именем Господа, говоря: «Господь да будет между мною и между тобою и между семенем моим и семенем твоим», то да будет навеки.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13115, 'rst66', '1sa', 20, 41, 'Отрок пошел, а Давид поднялся с южной стороны и пал лицом своим на землю и трижды поклонился; и целовали они друг друга, и плакали оба вместе, но Давид плакал более.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13114, 'rst66', '1sa', 20, 40, 'И отдал Ионафан оружие свое отроку, бывшему при нем, и сказал ему: ступай, отнеси в город.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13113, 'rst66', '1sa', 20, 39, 'Отрок же не знал ничего; только Ионафан и Давид знали, в чем дело.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13112, 'rst66', '1sa', 20, 38, 'И опять кричал Ионафан вслед отроку: скорей беги, не останавливайся. И собрал отрок Ионафанов стрелы и пришел к своему господину.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13111, 'rst66', '1sa', 20, 37, 'И побежал отрок туда, куда Ионафан пускал стрелы, и закричал Ионафан вслед отроку и сказал: смотри, стрела впереди тебя.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13110, 'rst66', '1sa', 20, 36, 'И сказал он отроку: беги, ищи стрелы, которые я пускаю. Отрок побежал, а он пускал стрелы так, что они летели дальше <i>отрока</i>.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13109, 'rst66', '1sa', 20, 35, 'На другой день утром вышел Ионафан в поле, во время, которое назначил Давиду, и малый отрок с ним.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13108, 'rst66', '1sa', 20, 34, 'И встал Ионафан из‐за стола в великом гневе и не обедал во второй день новомесячия, потому что скорбел о Давиде и потому что обидел его отец его.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13107, 'rst66', '1sa', 20, 33, 'Тогда Саул бросил копье в него, чтобы поразить его. И Ионафан понял, что отец его решился убить Давида.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13106, 'rst66', '1sa', 20, 32, 'И отвечал Ионафан Саулу, отцу своему, и сказал ему: за что умерщвлять его? что он сделал?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13105, 'rst66', '1sa', 20, 31, 'Ибо во все дни, доколе сын Иессеев будет жить на земле, не устоишь ни ты, ни царство твое; теперь же пошли и приведи его ко мне, ибо он обречен на смерть.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13104, 'rst66', '1sa', 20, 30, 'Тогда сильно разгневался Саул на Ионафана и сказал ему: сын негодный и непокорный! разве я не знаю, что ты подружился с сыном Иессеевым на срам себе и на срам матери твоей?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13103, 'rst66', '1sa', 20, 29, 'он говорил: «отпусти меня, ибо у нас в городе родственное жертвоприношение, и мой брат пригласил меня; итак, если я нашел благоволение в очах твоих, схожу я и повидаюсь со своими братьями»; поэтому он и не пришел к обеду царя.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13102, 'rst66', '1sa', 20, 28, 'И отвечал Ионафан Саулу: Давид выпросился у меня в Вифлеем;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13101, 'rst66', '1sa', 20, 27, 'Наступил и второй день новомесячия, а место Давида оставалось праздным. Тогда сказал Саул сыну своему Ионафану: почему сын Иессеев не пришел к обеду ни вчера, ни сегодня?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13100, 'rst66', '1sa', 20, 26, 'И не сказал Саул в тот день ничего, ибо подумал, что это случайность, что <i>Давид</i> нечист, не очистился.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13099, 'rst66', '1sa', 20, 25, 'Царь сел на своем месте, по обычаю, на седалище у стены, и Ионафан встал, и Авенир сел подле Саула; место же Давида осталось праздным.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13098, 'rst66', '1sa', 20, 24, 'И скрылся Давид на поле. И наступило новомесячие, и сел царь обедать.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13097, 'rst66', '1sa', 20, 23, 'а тому, что мы говорили, я и ты, <i>свидетель</i> Господь между мною и тобою вовеки.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13096, 'rst66', '1sa', 20, 22, 'если же так скажу отроку: «вот, стрелы впереди тебя», то ты уходи, ибо отпускает тебя Господь;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13095, 'rst66', '1sa', 20, 21, 'потом пошлю отрока, <i>говоря:</i> «пойди, найди стрелы»; и если я скажу отроку: «вот, стрелы сзади тебя, возьми их», то приди ко мне, ибо мир тебе, и, жив Господь, ничего <i>тебе не будет;</i>');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13094, 'rst66', '1sa', 20, 20, 'а я в ту сторону пущу три стрелы, как будто стреляя в цель;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13093, 'rst66', '1sa', 20, 19, 'поэтому на третий день ты спустись и поспеши на то место, где скрывался ты прежде, и сядь у камня Азель;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13092, 'rst66', '1sa', 20, 18, 'И сказал ему Ионафан: завтра новомесячие, и о тебе спросят, ибо место твое будет не занято;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13091, 'rst66', '1sa', 20, 17, 'И снова Ионафан клялся Давиду своею любовью к нему, ибо любил его, как свою душу.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13090, 'rst66', '1sa', 20, 16, 'Так заключил Ионафан завет с домом Давида <i>и сказал:</i> да взыщет Господь с врагов Давида!');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13089, 'rst66', '1sa', 20, 15, 'А если я умру, то не отними милости твоей от дома моего вовеки, даже и тогда, когда Господь истребит с лица земли всех врагов Давида.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13088, 'rst66', '1sa', 20, 14, 'Но и ты, если я буду еще жив, окажи мне милость Господню.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13087, 'rst66', '1sa', 20, 13, 'пусть то и то сделает Господь с Ионафаном и еще больше сделает. Если же отец мой замышляет сделать тебе зло, и это открою в уши твои, и отпущу тебя, и тогда иди с миром: и да будет Господь с тобою, как был с отцом моим!');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13086, 'rst66', '1sa', 20, 12, 'И сказал Ионафан Давиду: жив Господь, Бог Израилев! я завтра около этого времени, или послезавтра, выпытаю у отца моего; и если он благосклонен к Давиду, и я тогда же не пошлю к тебе и не открою пред ушами твоими,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13085, 'rst66', '1sa', 20, 11, 'И сказал Ионафан Давиду: иди, выйдем в поле. И вышли оба в поле.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13084, 'rst66', '1sa', 20, 10, 'И сказал Давид Ионафану: кто известит меня, если отец твой ответит тебе сурово?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13083, 'rst66', '1sa', 20, 9, 'И сказал Ионафан: никак не будет этого с тобою; ибо, если я узнаю наверное, что у отца моего решено злое дело совершить над тобою, то неужели не извещу тебя об этом?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13082, 'rst66', '1sa', 20, 8, 'Ты же сделай милость рабу твоему, &mdash; ибо ты принял раба твоего в завет Господень с тобою, &mdash; и если есть какая вина на мне, то умертви ты меня; зачем тебе вести меня к отцу твоему?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13081, 'rst66', '1sa', 20, 7, 'Если на это он скажет: «хорошо», то мир рабу твоему; а если он разгневается, то знай, что злое дело решено у него.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13080, 'rst66', '1sa', 20, 6, 'Если отец твой спросит обо мне, ты скажи: «Давид выпросился у меня сходить в свой город Вифлеем; потому что там годичное жертвоприношение всего родства его».');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13079, 'rst66', '1sa', 20, 5, 'И сказал Давид Ионафану: вот, завтра новомесячие, и я должен сидеть с царем за столом; но отпусти меня, и я скроюсь в поле до вечера третьего дня.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13078, 'rst66', '1sa', 20, 4, 'И сказал Ионафан Давиду: чего желает душа твоя, я сделаю для тебя.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13077, 'rst66', '1sa', 20, 3, 'Давид клялся и говорил: отец твой хорошо знает, что я нашел благоволение в очах твоих, и потому говорит сам в себе: «пусть не знает о том Ионафан, чтобы не огорчился»; но жив Господь и жива душа твоя! один только шаг между мною и смертью.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13076, 'rst66', '1sa', 20, 2, 'И сказал ему <i>Ионафан</i>: нет, ты не умрешь; вот, отец мой не делает ни большого, ни малого дела, не открыв ушам моим; для чего же бы отцу моему скрывать от меня это дело? Этого не будет.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13075, 'rst66', '1sa', 20, 1, 'Давид убежал из Навафа в Раме и пришел и сказал Ионафану: что сделал я, в чем неправда моя, чем согрешил я пред отцом твоим, что он ищет души моей?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13074, 'rst66', '1sa', 19, 24, 'И снял и он одежды свои, и пророчествовал пред Самуилом, и весь день тот и всю ту ночь лежал неодетый; поэтому говорят: «неужели и Саул во пророках?»');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13073, 'rst66', '1sa', 19, 23, 'И пошел он туда, в Наваф в Раме, и на него сошел Дух Божий, и он шел и пророчествовал, доколе не пришел в Наваф в Раме.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13072, 'rst66', '1sa', 19, 22, 'Саул сам пошел в Раму, и дошел до большого источника, что в Сефе, и спросил, говоря: где Самуил и Давид? И сказали: вот, в Навафе, в Раме.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13071, 'rst66', '1sa', 19, 21, 'Донесли <i>об этом</i> Саулу, и он послал других слуг, но и эти стали пророчествовать. Потом послал Саул третьих слуг, и эти стали пророчествовать.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13070, 'rst66', '1sa', 19, 20, 'И послал Саул слуг взять Давида, и <i>когда</i> увидели они сонм пророков пророчествующих и Самуила, начальствующего над ними, то Дух Божий сошел на слуг Саула, и они стали пророчествовать.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13069, 'rst66', '1sa', 19, 19, 'И донесли Саулу, говоря: вот, Давид в Навафе, в Раме.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13068, 'rst66', '1sa', 19, 18, 'И убежал Давид и спасся, и пришел к Самуилу в Раму и рассказал ему все, что делал с ним Саул. И пошел он с Самуилом, и остановились они в Навафе.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13067, 'rst66', '1sa', 19, 17, 'Тогда Саул сказал Мелхоле: для чего ты так обманула меня и отпустила врага моего, чтоб он убежал? И сказала Мелхола Саулу: он сказал мне: «отпусти меня, иначе я убью тебя».');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13066, 'rst66', '1sa', 19, 16, 'И пришли слуги, и вот, на постели статуя, а в изголовье ее козья кожа.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13065, 'rst66', '1sa', 19, 15, 'И послал Саул слуг, чтобы осмотреть Давида, говоря: принесите его ко мне на постели, чтоб убить его.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13064, 'rst66', '1sa', 19, 14, 'И послал Саул слуг, чтобы взять Давида; но <i>Мелхола</i> сказала: он болен.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13063, 'rst66', '1sa', 19, 13, 'Мелхола же взяла статую и положила на постель, а в изголовье ее положила козью кожу, и покрыла одеждою.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13062, 'rst66', '1sa', 19, 12, 'И спустила Мелхола Давида из окна, и он пошел, и убежал и спасся.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13061, 'rst66', '1sa', 19, 11, 'И послал Саул слуг в дом к Давиду, чтобы стеречь его и убить его до утра. И сказала Давиду Мелхола, жена его: если ты не спасешь души твоей в эту ночь, то завтра будешь убит.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13060, 'rst66', '1sa', 19, 10, 'И хотел Саул пригвоздить Давида копьем к стене, но Давид отскочил от Саула, и копье вонзилось в стену; Давид же убежал и спасся в ту ночь.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13059, 'rst66', '1sa', 19, 9, 'И злой дух от Бога напал на Саула, и он сидел в доме своем, и копье его было в руке его, а Давид играл рукою своею на струнах.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13058, 'rst66', '1sa', 19, 8, 'Опять началась война, и вышел Давид, и воевал с Филистимлянами, и нанес им великое поражение, и они побежали от него.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13057, 'rst66', '1sa', 19, 7, 'И позвал Ионафан Давида, и пересказал ему Ионафан все слова сии, и привел Ионафан Давида к Саулу, и он был при нем, как вчера и третьего дня.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13056, 'rst66', '1sa', 19, 6, 'И послушал Саул голоса Ионафана, и поклялся Саул: жив Господь, <i>Давид</i> не умрет.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13055, 'rst66', '1sa', 19, 5, 'он подвергал опасности душу свою, чтобы поразить Филистимлянина, и Господь соделал великое спасение всему Израилю; ты видел <i>это</i> и радовался; для чего же ты хочешь согрешить <i>против</i> невинной крови и умертвить Давида без причины?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13054, 'rst66', '1sa', 19, 4, 'И говорил Ионафан доброе о Давиде Саулу, отцу своему, и сказал ему: да не грешит царь против раба своего Давида, ибо он ничем не согрешил против тебя, и дела его весьма полезны для тебя;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13053, 'rst66', '1sa', 19, 3, 'а я выйду и стану подле отца моего на поле, где ты будешь, и поговорю о тебе отцу моему, и что увижу, расскажу тебе.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13052, 'rst66', '1sa', 19, 2, 'И известил Ионафан Давида, говоря: отец мой Саул ищет умертвить тебя; итак, берегись завтра; скройся и будь в потаенном месте;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13051, 'rst66', '1sa', 19, 1, 'И говорил Саул Ионафану, сыну своему, и всем слугам своим, чтобы умертвить Давида; но Ионафан, сын Саула, очень любил Давида.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13050, 'rst66', '1sa', 18, 30, 'И когда вожди Филистимские вышли <i>на войну</i>, Давид, с самого выхода их, действовал благоразумнее всех слуг Сауловых, и весьма прославилось имя его.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13049, 'rst66', '1sa', 18, 29, 'И стал Саул еще больше бояться Давида и сделался врагом его на всю жизнь.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13048, 'rst66', '1sa', 18, 28, 'И увидел Саул и узнал, что Господь с Давидом и что Мелхола, дочь Саула, любила <i>Давида</i>.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13047, 'rst66', '1sa', 18, 27, 'Еще не прошли назначенные дни, как Давид встал и пошел сам и люди его с ним, и убил двести человек Филистимлян, и принес Давид краеобрезания их, и представил их в полном количестве царю, чтобы сделаться зятем царя. И выдал Саул за него Мелхолу, дочь свою, в замужество.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13046, 'rst66', '1sa', 18, 26, 'И пересказали слуги его Давиду эти слова, и понравилось Давиду сделаться зятем царя.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13045, 'rst66', '1sa', 18, 25, 'И сказал Саул: так скажите Давиду: царь не хочет вена, кроме ста краеобрезаний Филистимских, в отмщение врагам царя. Ибо Саул имел в мыслях погубить Давида руками Филистимлян.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13044, 'rst66', '1sa', 18, 24, 'И донесли Саулу слуги его и сказали: вот что говорит Давид.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13043, 'rst66', '1sa', 18, 23, 'И передали слуги Сауловы в уши Давиду все слова эти. И сказал Давид: разве легко кажется вам быть зятем царя? Я &mdash; человек бедный и незначительный.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13042, 'rst66', '1sa', 18, 22, 'И приказал Саул слугам своим: скажите Давиду тайно: вот, царь благоволит к тебе, и все слуги его любят тебя; итак, будь зятем царя.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13041, 'rst66', '1sa', 18, 21, 'Саул думал: отдам ее за него, и она будет ему сетью, и рука Филистимлян будет на нем. И сказал Саул Давиду: чрез другую ты породнишься ныне со мною.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13040, 'rst66', '1sa', 18, 20, 'Но Давида полюбила <i>другая</i> дочь Саула, Мелхола; и когда возвестили <i>об этом</i> Саулу, то это было приятно ему.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13039, 'rst66', '1sa', 18, 19, 'А когда наступило время отдать Мерову, дочь Саула, Давиду, то она выдана была в замужество за Адриэла из Мехолы.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13038, 'rst66', '1sa', 18, 18, 'Но Давид сказал Саулу: кто я, и что жизнь моя и род отца моего в Израиле, чтобы мне быть зятем царя?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13037, 'rst66', '1sa', 18, 17, 'И сказал Саул Давиду: вот старшая дочь моя, Мерова; я дам ее тебе в жену, только будь у меня храбрым и веди войны Господни. Ибо Саул думал: пусть не моя рука будет на нем, но рука Филистимлян будет на нем.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13036, 'rst66', '1sa', 18, 16, 'А весь Израиль и Иуда любили Давида, ибо он выходил и входил пред ними.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13035, 'rst66', '1sa', 18, 15, 'И Саул видел, что он очень благоразумен, и боялся его.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13034, 'rst66', '1sa', 18, 14, 'И Давид во всех делах своих поступал благоразумно, и Господь <i>был</i> с ним.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13033, 'rst66', '1sa', 18, 13, 'И удалил его Саул от себя и поставил его у себя тысяченачальником, и он выходил и входил пред народом.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13032, 'rst66', '1sa', 18, 12, 'И стал бояться Саул Давида, потому что Господь был с ним, а от Саула отступил.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13031, 'rst66', '1sa', 18, 11, 'И бросил Саул копье, подумав: пригвожду Давида к стене; но Давид два раза уклонился от него.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13030, 'rst66', '1sa', 18, 10, 'И было на другой день: напал злой дух от Бога на Саула, и он бесновался в доме своем, а Давид играл рукою своею на струнах, как и в другие дни; в руке у Саула было копье.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13029, 'rst66', '1sa', 18, 9, 'И с того дня и потом подозрительно смотрел Саул на Давида.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13028, 'rst66', '1sa', 18, 8, 'И Саул сильно огорчился, и неприятно было ему это слово, и он сказал: Давиду дали десятки тысяч, а мне тысячи; ему недостает только царства.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13027, 'rst66', '1sa', 18, 7, 'И восклицали игравшие женщины, говоря: Саул победил тысячи, а Давид &mdash; десятки тысяч!');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13026, 'rst66', '1sa', 18, 6, 'Когда они шли, при возвращении Давида с победы над Филистимлянином, то женщины из всех городов Израильских выходили навстречу Саулу царю с пением и плясками, с торжественными тимпанами и с кимвалами.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13025, 'rst66', '1sa', 18, 5, 'И Давид действовал благоразумно везде, куда ни посылал его Саул, и сделал его Саул начальником над военными людьми; и это понравилось всему народу и слугам Сауловым.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13024, 'rst66', '1sa', 18, 4, 'И снял Ионафан верхнюю одежду свою, которая была на нем, и отдал ее Давиду, также и прочие одежды свои, и меч свой, и лук свой, и пояс свой.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13023, 'rst66', '1sa', 18, 3, 'Ионафан же заключил с Давидом союз, ибо полюбил его, как свою душу.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13022, 'rst66', '1sa', 18, 2, 'И взял его Саул в тот день и не позволил ему возвратиться в дом отца его.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13021, 'rst66', '1sa', 18, 1, 'Когда кончил <i>Давид</i> разговор с Саулом, душа Ионафана прилепилась к душе его, и полюбил его Ионафан, как свою душу.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13020, 'rst66', '1sa', 17, 58, 'И спросил его Саул: чей ты сын, юноша? И отвечал Давид: сын раба твоего Иессея из Вифлеема.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13019, 'rst66', '1sa', 17, 57, 'Когда же Давид возвращался после поражения Филистимлянина, то Авенир взял его и привел к Саулу, и голова Филистимлянина была в руке его.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13018, 'rst66', '1sa', 17, 56, 'И сказал царь: так спроси, чей сын этот юноша.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13017, 'rst66', '1sa', 17, 55, 'Когда Саул увидел Давида, выходившего против Филистимлянина, то сказал Авениру, начальнику войска: Авенир, чей сын этот юноша? Авенир сказал: да живет душа твоя, царь; я не знаю.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13016, 'rst66', '1sa', 17, 54, 'И взял Давид голову Филистимлянина и отнес ее в Иерусалим, а оружие его положил в шатре своем.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13015, 'rst66', '1sa', 17, 53, 'И возвратились сыны Израилевы из погони за Филистимлянами и разграбили стан их.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13014, 'rst66', '1sa', 17, 52, 'И поднялись мужи Израильские и Иудейские, и воскликнули и гнали Филистимлян до входа в долину и до ворот Аккарона. И падали поражаемые Филистимляне по дороге Шааримской до Гефа и до Аккарона.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13013, 'rst66', '1sa', 17, 51, 'Тогда Давид подбежал и, наступив на Филистимлянина, взял меч его и вынул его из ножен, ударил его и отсек им голову его. Филистимляне, увидев, что силач их умер, побежали.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13012, 'rst66', '1sa', 17, 50, 'Так одолел Давид Филистимлянина пращою и камнем, и поразил Филистимлянина и убил его; меча же не было в руках Давида.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13011, 'rst66', '1sa', 17, 49, 'И опустил Давид руку свою в сумку и взял оттуда камень, и бросил из пращи и поразил Филистимлянина в лоб, так что камень вонзился в лоб его, и он упал лицом на землю.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13010, 'rst66', '1sa', 17, 48, 'Когда Филистимлянин поднялся и стал подходить и приближаться навстречу Давиду, Давид поспешно побежал к строю навстречу Филистимлянину.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13009, 'rst66', '1sa', 17, 47, 'и узнает весь этот сонм, что не мечом и копьем спасает Господь, ибо это война Господа, и Он предаст вас в руки наши.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13008, 'rst66', '1sa', 17, 46, 'ныне предаст тебя Господь в руку мою, и я убью тебя, и сниму с тебя голову твою, и отдам трупы войска Филистимского птицам небесным и зверям земным, и узнает вся земля, что есть Бог в Израиле;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13007, 'rst66', '1sa', 17, 45, 'А Давид отвечал Филистимлянину: ты идешь против меня с мечом и копьем и щитом, а я иду против тебя во имя Господа Саваофа, Бога воинств Израильских, которые ты поносил;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13006, 'rst66', '1sa', 17, 44, 'И сказал Филистимлянин Давиду: подойди ко мне, и я отдам тело твое птицам небесным и зверям полевым.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13005, 'rst66', '1sa', 17, 43, 'И сказал Филистимлянин Давиду: что ты идешь на меня с палкою? разве я собака? И проклял Филистимлянин Давида своими богами.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13004, 'rst66', '1sa', 17, 42, 'И взглянул Филистимлянин и, увидев Давида, с презрением посмотрел на него, ибо он был молод, белокур и красив лицом.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13003, 'rst66', '1sa', 17, 41, 'Выступил и Филистимлянин, идя и приближаясь к Давиду, и оруженосец шел впереди его.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13002, 'rst66', '1sa', 17, 40, 'И взял посох свой в руку свою, и выбрал себе пять гладких камней из ручья, и положил их в пастушескую сумку, которая была с ним; и с сумкою и с пращою в руке своей выступил против Филистимлянина.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13001, 'rst66', '1sa', 17, 39, 'И опоясался Давид мечом его сверх одежды и начал ходить, ибо не привык <i>к такому вооружению;</i> потом сказал Давид Саулу: я не могу ходить в этом, я не привык. И снял Давид все это с себя.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (13000, 'rst66', '1sa', 17, 38, 'И одел Саул Давида в свои одежды, и возложил на голову его медный шлем, и надел на него броню.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (12999, 'rst66', '1sa', 17, 37, 'И сказал Давид: Господь, Который избавлял меня от льва и медведя, избавит меня и от руки этого Филистимлянина. И сказал Саул Давиду: иди, и да будет Господь с тобою.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (12998, 'rst66', '1sa', 17, 36, 'и льва и медведя убивал раб твой, и с этим Филистимлянином необрезанным будет то же, что с ними, потому что так поносит воинство Бога живого.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (12997, 'rst66', '1sa', 17, 35, 'то я гнался за ним и нападал на него и отнимал из пасти его; а если он бросался на меня, то я брал его за космы и поражал его и умерщвлял его;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (12996, 'rst66', '1sa', 17, 34, 'И сказал Давид Саулу: раб твой пас овец у отца своего, и когда, бывало, приходил лев или медведь и уносил овцу из стада,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (12995, 'rst66', '1sa', 17, 33, 'И сказал Саул Давиду: не можешь ты идти против этого Филистимлянина, чтобы сразиться с ним, ибо ты еще юноша, а он воин от юности своей.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (12994, 'rst66', '1sa', 17, 32, 'И сказал Давид Саулу: пусть никто не падает духом из‐за него; раб твой пойдет и сразится с этим Филистимлянином.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (12993, 'rst66', '1sa', 17, 31, 'И услышали слова, которые говорил Давид, и пересказали Саулу, и тот призвал его.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (12992, 'rst66', '1sa', 17, 30, 'И отворотился от него к другому и говорил те же слова, и отвечал ему народ по‐прежнему.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (12991, 'rst66', '1sa', 17, 29, 'И сказал Давид: что же я сделал? не слова ли это?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (12990, 'rst66', '1sa', 17, 28, 'И услышал Елиав, старший брат Давида, что говорил он с людьми, и рассердился Елиав на Давида и сказал: зачем ты сюда пришел и на кого оставил немногих овец тех в пустыне? Я знаю высокомерие твое и дурное сердце твое, ты пришел посмотреть на сражение.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (12989, 'rst66', '1sa', 17, 27, 'И сказал ему народ те же слова, говоря: вот что сделано будет тому человеку, который убьет его.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (12988, 'rst66', '1sa', 17, 26, 'И сказал Давид людям, стоящим с ним: что сделают тому, кто убьет этого Филистимлянина и снимет поношение с Израиля? ибо кто этот необрезанный Филистимлянин, что так поносит воинство Бога живого?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (12987, 'rst66', '1sa', 17, 25, 'И говорили Израильтяне: видите этого выступающего человека? Он выступает, чтобы поносить Израиля. Если бы кто убил его, одарил бы того царь великим богатством, и дочь свою выдал бы за него, и дом отца его сделал бы свободным в Израиле.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (12986, 'rst66', '1sa', 17, 24, 'И все Израильтяне, увидев этого человека, убегали от него и весьма боялись.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (12985, 'rst66', '1sa', 17, 23, 'И вот, когда он разговаривал с ними, единоборец, по имени Голиаф, Филистимлянин из Гефа, выступает из рядов Филистимских и говорит те слова, и Давид услышал <i>их</i>.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (12984, 'rst66', '1sa', 17, 22, 'Давид оставил свою ношу обозному сторожу и побежал в ряды и, придя, спросил братьев своих о здоровье.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (12983, 'rst66', '1sa', 17, 21, 'И расположили Израильтяне и Филистимляне строй против строя.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (12982, 'rst66', '1sa', 17, 20, 'И встал Давид рано утром, и поручил овец сторожу, и, взяв ношу, пошел, как приказал ему Иессей, и пришел к обозу, когда войско выведено было в строй и с криком готовилось к сражению.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (12981, 'rst66', '1sa', 17, 19, 'Саул и они и все Израильтяне <i>находились</i> в долине дуба и готовились к сражению с Филистимлянами.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (12980, 'rst66', '1sa', 17, 18, 'а эти десять сыров отнеси тысяченачальнику и наведайся о здоровье братьев и узнай о нуждах их.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (12979, 'rst66', '1sa', 17, 17, 'И сказал Иессей Давиду, сыну своему: возьми для братьев своих ефу сушеных зерен и десять этих хлебов и отнеси поскорее в стан к твоим братьям;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (12978, 'rst66', '1sa', 17, 16, 'И выступал Филистимлянин тот утром и вечером и выставлял себя сорок дней.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (12977, 'rst66', '1sa', 17, 15, 'а Давид возвратился от Саула, чтобы пасти овец отца своего в Вифлееме.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (12976, 'rst66', '1sa', 17, 14, 'Давид же был меньший. Трое старших пошли с Саулом,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (12975, 'rst66', '1sa', 17, 13, 'Три старших сына Иессеевы пошли с Саулом на войну; имена трех сыновей его, пошедших на войну: старший &mdash; Елиав, второй за ним &mdash; Аминадав, и третий &mdash; Самма;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (12974, 'rst66', '1sa', 17, 12, 'Давид же был сын Ефрафянина из Вифлеема Иудина, по имени Иессея, у которого было восемь сыновей. Этот человек во дни Саула достиг старости и был старший между мужами.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (12973, 'rst66', '1sa', 17, 11, 'И услышали Саул и все Израильтяне эти слова Филистимлянина, и очень испугались и ужаснулись.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (12972, 'rst66', '1sa', 17, 10, 'И сказал Филистимлянин: сегодня я посрамлю полки Израильские; дайте мне человека, и мы сразимся вдвоем.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (12971, 'rst66', '1sa', 17, 9, 'если он может сразиться со мною и убьет меня, то мы будем вашими рабами; если же я одолею его и убью его, то вы будете нашими рабами и будете служить нам.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (12970, 'rst66', '1sa', 17, 8, 'И стал <i>он</i> и кричал к полкам Израильским, говоря им: зачем вышли вы воевать? Не Филистимлянин ли я, а вы рабы Сауловы? Выберите у себя человека, и пусть сойдет ко мне;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (12969, 'rst66', '1sa', 17, 7, 'и древко копья его, как навой у ткачей; а самое копье его в шестьсот сиклей железа, и пред ним шел оруженосец.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (12968, 'rst66', '1sa', 17, 6, 'медные наколенники на ногах его, и медный щит за плечами его;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (12967, 'rst66', '1sa', 17, 5, 'Медный шлем на голове его; и одет он был в чешуйчатую броню, и вес брони его &mdash; пять тысяч сиклей меди;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (12966, 'rst66', '1sa', 17, 4, 'И выступил из стана Филистимского единоборец, по имени Голиаф, из Гефа; ростом он &mdash; шести локтей и пяди.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (12965, 'rst66', '1sa', 17, 3, 'И стали Филистимляне на горе с одной стороны, и Израильтяне на горе с другой стороны, а между ними была долина.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (12964, 'rst66', '1sa', 17, 2, 'А Саул и Израильтяне собрались и расположились станом в долине дуба и приготовились к войне против Филистимлян.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (12963, 'rst66', '1sa', 17, 1, 'Филистимляне собрали войска свои для войны и собрались в Сокхофе, что в Иудее, и расположились станом между Сокхофом и Азеком в Ефес‐Даммиме.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (12962, 'rst66', '1sa', 16, 23, 'И когда дух от Бога бывал на Сауле, то Давид, взяв гусли, играл, &mdash; и отраднее и лучше становилось Саулу, и дух злой отступал от него.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (12961, 'rst66', '1sa', 16, 22, 'И послал Саул сказать Иессею: пусть Давид служит при мне, ибо он снискал благоволение в глазах моих.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (12960, 'rst66', '1sa', 16, 21, 'И пришел Давид к Саулу и служил пред ним, и очень понравился ему и сделался его оруженосцем.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (12959, 'rst66', '1sa', 16, 20, 'И взял Иессей осла с хлебом и мех с вином и одного козленка, и послал с Давидом, сыном своим, к Саулу.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (12958, 'rst66', '1sa', 16, 19, 'И послал Саул вестников к Иессею и сказал: пошли ко мне Давида, сына твоего, который при стаде.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (12957, 'rst66', '1sa', 16, 18, 'Тогда один из слуг его сказал: вот, я видел у Иессея Вифлеемлянина сына, умеющего играть, человека храброго и воинственного, и разумного в речах и видного собою, и Господь с ним.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (12956, 'rst66', '1sa', 16, 17, 'И отвечал Саул слугам своим: найдите мне человека, хорошо играющего, и представьте его ко мне.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (12955, 'rst66', '1sa', 16, 16, 'пусть господин наш прикажет слугам своим, <i>которые</i> пред тобою, поискать человека, искусного в игре на гуслях, и когда придет на тебя злой дух от Бога, то он, играя рукою своею, будет успокоивать тебя.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (12954, 'rst66', '1sa', 16, 15, 'И сказали слуги Сауловы ему: вот, злой дух от Бога возмущает тебя;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (12953, 'rst66', '1sa', 16, 14, 'А от Саула отступил Дух Господень, и возмущал его злой дух от Господа.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (12952, 'rst66', '1sa', 16, 13, 'И взял Самуил рог с елеем и помазал его среди братьев его, и почивал Дух Господень на Давиде с того дня и после; Самуил же встал и отошел в Раму.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (12951, 'rst66', '1sa', 16, 12, 'И послал <i>Иессей</i>, и привели его. Он был белокур, с красивыми глазами и приятным лицом. И сказал Господь: встань, помажь его, ибо это он.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (12950, 'rst66', '1sa', 16, 11, 'И сказал Самуил Иессею: все ли дети здесь? И отвечал Иессей: есть еще меньший; он пасет овец. И сказал Самуил Иессею: пошли и возьми его, ибо мы не сядем обедать, доколе не придет он сюда.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (12949, 'rst66', '1sa', 16, 10, 'Так подводил Иессей к Самуилу семерых сыновей своих, но Самуил сказал Иессею: <i>никого</i> из этих не избрал Господь.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (12948, 'rst66', '1sa', 16, 9, 'И подвел Иессей Самму, и сказал <i>Самуил:</i> и этого не избрал Господь.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (12947, 'rst66', '1sa', 16, 8, 'И позвал Иессей Аминадава и подвел его к Самуилу, и сказал Самуил: и этого не избрал Господь.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (12946, 'rst66', '1sa', 16, 7, 'Но Господь сказал Самуилу: не смотри на вид его и на высоту роста его; Я отринул его; <i>Я смотрю не так</i>, как смотрит человек; ибо человек смотрит на лицо, а Господь смотрит на сердце.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (12945, 'rst66', '1sa', 16, 6, 'И когда они пришли, он, увидев Елиава, сказал: верно, сей пред Господом помазанник Его!');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (12944, 'rst66', '1sa', 16, 5, 'И отвечал он: мирен, для жертвоприношения Господу пришел я; освятитесь и идите со мною к жертвоприношению. И освятил Иессея и сыновей его и пригласил их к жертве.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (12943, 'rst66', '1sa', 16, 4, 'И сделал Самуил так, как сказал ему Господь. Когда пришел он в Вифлеем, то старейшины города с трепетом вышли навстречу ему и сказали: мирен ли приход твой?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (12942, 'rst66', '1sa', 16, 3, 'и пригласи Иессея к жертве; Я укажу тебе, что делать тебе, и ты помажешь Мне того, о котором Я скажу тебе.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (12941, 'rst66', '1sa', 16, 2, 'И сказал Самуил: как я пойду? Саул услышит и убьет меня. Господь сказал: возьми в руку твою телицу из стада и скажи: «я пришел для жертвоприношения Господу»;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (12940, 'rst66', '1sa', 16, 1, 'И сказал Господь Самуилу: доколе будешь ты печалиться о Сауле, которого Я отверг, чтоб он не был царем над Израилем? Наполни рог твой елеем и пойди; Я пошлю тебя к Иессею Вифлеемлянину, ибо между сыновьями его Я усмотрел Себе царя.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (12939, 'rst66', '1sa', 15, 35, 'И более не видался Самуил с Саулом до дня смерти своей; но печалился Самуил о Сауле, потому что Господь раскаялся, что воцарил Саула над Израилем.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (12938, 'rst66', '1sa', 15, 34, 'И отошел Самуил в Раму, а Саул пошел в дом свой, в Гиву Саулову.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (12937, 'rst66', '1sa', 15, 33, 'Но Самуил сказал: как меч твой жен лишал детей, так мать твоя между женами пусть лишена будет <i>сына</i>. И разрубил Самуил Агага пред Господом в Галгале.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (12936, 'rst66', '1sa', 15, 32, 'Потом сказал Самуил: приведите ко мне Агага, царя Амаликитского. И подошел к нему Агаг дрожащий, и сказал Агаг: конечно, горечь смерти миновалась?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (12935, 'rst66', '1sa', 15, 31, 'И возвратился Самуил за Саулом, и поклонился Саул Господу.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (12934, 'rst66', '1sa', 15, 30, 'И сказал <i>Саул</i>: я согрешил, но почти меня ныне пред старейшинами народа моего и пред Израилем и воротись со мною, и я поклонюсь Господу, Богу твоему.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (12933, 'rst66', '1sa', 15, 29, 'и не скажет неправды и не раскается Верный Израилев; ибо не человек Он, чтобы раскаяться Ему.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (12932, 'rst66', '1sa', 15, 28, 'Тогда сказал Самуил: ныне отторг Господь царство Израильское от тебя и отдал его ближнему твоему, лучшему тебя;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (12931, 'rst66', '1sa', 15, 27, 'И обратился Самуил, чтобы уйти. Но <i>Саул</i> ухватился за край одежды его и разодрал ее.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (12930, 'rst66', '1sa', 15, 26, 'И отвечал Самуил Саулу: не ворочусь я с тобою, ибо ты отверг слово Господа, и Господь отверг тебя, чтобы ты не был царем над Израилем.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (12929, 'rst66', '1sa', 15, 25, 'теперь же сними с меня грех мой и воротись со мною, чтобы я поклонился Господу.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (12928, 'rst66', '1sa', 15, 24, 'И сказал Саул Самуилу: согрешил я, ибо преступил повеление Господа и слово твое; но я боялся народа и послушал голоса их;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (12927, 'rst66', '1sa', 15, 23, 'ибо непокорность есть <i>такой же</i> грех, что волшебство, и противление &mdash; <i>то же, что</i> идолопоклонство; за то, что ты отверг слово Господа, и Он отверг тебя, чтобы ты не был царем.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (12926, 'rst66', '1sa', 15, 22, 'И отвечал Самуил: неужели всесожжения и жертвы столько же приятны Господу, как послушание гласу Господа? Послушание лучше жертвы, и повиновение лучше тука овнов;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (12925, 'rst66', '1sa', 15, 21, 'народ же из добычи, из овец и волов, взял лучшее из заклятого, для жертвоприношения Господу, Богу твоему, в Галгале.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (12924, 'rst66', '1sa', 15, 20, 'И сказал Саул Самуилу: я послушал гласа Господа и пошел в путь, куда послал меня Господь, и привел Агага, царя Амаликитского, а Амалика истребил;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (12923, 'rst66', '1sa', 15, 19, 'Зачем же ты не послушал гласа Господа и бросился на добычу, и сделал зло пред очами Господа?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (12922, 'rst66', '1sa', 15, 18, 'И послал тебя Господь в путь, сказав: «иди и предай заклятию нечестивых Амаликитян и воюй против них, доколе не уничтожишь их».');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (12921, 'rst66', '1sa', 15, 17, 'И сказал Самуил: не малым ли ты был в глазах твоих, когда сделался главою колен Израилевых, и Господь помазал тебя царем над Израилем?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (12920, 'rst66', '1sa', 15, 16, 'И сказал Самуил Саулу: подожди, я скажу тебе, что сказал мне Господь ночью. И сказал ему Саул: говори.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (12919, 'rst66', '1sa', 15, 15, 'И сказал Саул: привели их от Амалика, так как народ пощадил лучших из овец и волов для жертвоприношения Господу, Богу твоему; прочее же мы истребили.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (12918, 'rst66', '1sa', 15, 14, 'И сказал Самуил: а что это за блеяние овец в ушах моих и мычание волов, которое я слышу?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (12917, 'rst66', '1sa', 15, 13, 'Когда пришел Самуил к Саулу, то Саул сказал ему: благословен ты у Господа; я исполнил слово Господа.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (12916, 'rst66', '1sa', 15, 12, 'И встал Самуил рано утром <i>и пошел</i> навстречу Саулу. И известили Самуила, что Саул ходил на Кармил и там поставил себе памятник, и сошел в Галгал.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (12915, 'rst66', '1sa', 15, 11, 'жалею, что поставил Я Саула царем, ибо он отвратился от Меня и слова Моего не исполнил. И опечалился Самуил и взывал к Господу целую ночь.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (12914, 'rst66', '1sa', 15, 10, 'И было слово Господа к Самуилу такое:');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (12913, 'rst66', '1sa', 15, 9, 'Но Саул и народ пощадили Агага и лучших из овец и волов и откормленных ягнят, и все хорошее, и не хотели истребить, а все вещи маловажные и худые истребили.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (12912, 'rst66', '1sa', 15, 8, 'и Агага, царя Амаликова, захватил живого, а народ весь истребил мечом.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (12911, 'rst66', '1sa', 15, 7, 'И поразил Саул Амалика от Хавилы до окрестностей Сура, что пред Египтом;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (12910, 'rst66', '1sa', 15, 6, 'И сказал Саул Кинеянам: пойдите, отделитесь, выйдите из среды Амалика, чтобы мне не погубить вас с ним, ибо вы оказали благосклонность всем Израильтянам, когда они шли из Египта. И отделились Кинеяне из среды Амалика.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (12909, 'rst66', '1sa', 15, 5, 'И дошел Саул до города Амаликова, и сделал засаду в долине.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (12908, 'rst66', '1sa', 15, 4, 'И собрал Саул народ и насчитал их в Телаиме двести тысяч Израильтян пеших и десять тысяч из колена Иудина.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (12907, 'rst66', '1sa', 15, 3, 'теперь иди и порази Амалика, и истреби все, что у него; и не давай пощады ему, но предай смерти от мужа до жены, от отрока до грудного младенца, от вола до овцы, от верблюда до осла.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (12906, 'rst66', '1sa', 15, 2, 'Так говорит Господь Саваоф: вспомнил Я о том, что сделал Амалик Израилю, как он противостал ему на пути, когда он шел из Египта;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (12905, 'rst66', '1sa', 15, 1, 'И сказал Самуил Саулу: Господь послал меня помазать тебя царем над народом Его, над Израилем; теперь послушай гласа Господа.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (12904, 'rst66', '1sa', 14, 52, 'И была упорная война против Филистимлян во все время Саулово. И когда Саул видел какого‐либо человека сильного и воинственного, брал его к себе.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (12903, 'rst66', '1sa', 14, 51, 'Кис, отец Саулов, и Нир, отец Авенира, были сыновьями Авиила.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (12902, 'rst66', '1sa', 14, 50, 'Имя же жены Сауловой &mdash; Ахиноамь, дочь Ахимааца; а имя начальника войска его &mdash; Авенир, сын Нира, дяди Саулова.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (12901, 'rst66', '1sa', 14, 49, 'Сыновья у Саула были: Ионафан, Иессуи и Мелхисуа; а имена двух дочерей его: имя старшей &mdash; Мерова, а имя младшей &mdash; Мелхола.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (12900, 'rst66', '1sa', 14, 48, 'И устроил войско, и поразил Амалика, и освободил Израиля от руки грабителей его.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (12899, 'rst66', '1sa', 14, 47, 'И утвердил Саул свое царствование над Израилем, и воевал со всеми окрестными врагами своими, с Моавом и с Аммонитянами, и с Едомом, и с царями Совы, и с Филистимлянами, и везде, против кого ни обращался, имел успех.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (12898, 'rst66', '1sa', 14, 46, 'И возвратился Саул от преследования Филистимлян; Филистимляне же пошли в свое место.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (12897, 'rst66', '1sa', 14, 45, 'Но народ сказал Саулу: Ионафану ли умереть, который доставил столь великое спасение Израилю? Да не будет этого! Жив Господь, и волос не упадет с головы его на землю, ибо с Богом он действовал ныне. И освободил народ Ионафана, и не умер он.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (12896, 'rst66', '1sa', 14, 44, 'И сказал Саул: пусть то и то сделает мне Бог, и еще больше сделает; ты, Ионафан, должен сегодня умереть!');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (12895, 'rst66', '1sa', 14, 43, 'И сказал Саул Ионафану: расскажи мне, что сделал ты? И рассказал ему Ионафан и сказал: я отведал концом палки, которая в руке моей, немного меду; и вот, я должен умереть.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (12894, 'rst66', '1sa', 14, 42, 'Тогда сказал Саул: бросьте жребий между мною и Ионафаном, сыном моим, и пал жребий на Ионафана.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (12893, 'rst66', '1sa', 14, 41, 'И сказал Саул: Господи, Боже Израилев! дай знамение. И уличены были Ионафан и Саул, а народ вышел <i>правым</i>.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (12892, 'rst66', '1sa', 14, 40, 'И сказал <i>Саул</i> всем Израильтянам: станьте вы по одну сторону, а я и сын мой Ионафан станем по другую сторону. И отвечал народ Саулу: делай, что хорошо в глазах твоих.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (12891, 'rst66', '1sa', 14, 39, 'Ибо, &mdash; жив Господь, спасший Израиля, &mdash; если окажется и на Ионафане, сыне моем, то и он умрет непременно. Но никто не отвечал ему из всего народа.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (12890, 'rst66', '1sa', 14, 38, 'Тогда сказал Саул: пусть подойдут сюда все начальники народа и разведают и узнают, на ком грех ныне?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (12889, 'rst66', '1sa', 14, 37, 'И вопросил Саул Бога: идти ли мне <i>в погоню</i> за Филистимлянами? предашь ли их в руки Израиля? Но Он не отвечал ему в тот день.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (12888, 'rst66', '1sa', 14, 36, 'И сказал Саул: пойдем <i>в погоню</i> за Филистимлянами ночью и оберем их до рассвета и не оставим у них ни одного человека. И сказали: делай все, что хорошо в глазах твоих. Священник же сказал: приступим здесь к Богу.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (12887, 'rst66', '1sa', 14, 35, 'И устроил Саул жертвенник Господу: то был первый жертвенник, поставленный им Господу.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (12886, 'rst66', '1sa', 14, 34, 'Потом сказал Саул: пройдите между народом и скажите ему: пусть каждый приводит ко мне своего вола и каждый свою овцу, и закалайте здесь и ешьте, и не грешите пред Господом, не ешьте с кровью. И приводили все из народа, каждый своею рукою, вола своего ночью, и закалали там.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (12885, 'rst66', '1sa', 14, 33, 'И возвестили Саулу, говоря: вот, народ грешит пред Господом, ест с кровью. И сказал Саул: вы согрешили; привалите ко мне теперь большой камень.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (12884, 'rst66', '1sa', 14, 32, 'И кинулся народ на добычу, и брали овец, волов и телят, и закалали на земле, и ел народ с кровью.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (12883, 'rst66', '1sa', 14, 31, 'И поражали Филистимлян в тот день от Михмаса до Аиалона, и народ очень истомился.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (12882, 'rst66', '1sa', 14, 30, 'если бы поел сегодня народ из добычи, какую нашел у врагов своих, то не большее ли было бы поражение Филистимлян?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (12881, 'rst66', '1sa', 14, 29, 'И сказал Ионафан: смутил отец мой землю; смотрите, у меня просветлели глаза, когда я вкусил немного этого меду;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (12880, 'rst66', '1sa', 14, 28, 'И сказал ему один из народа, говоря: отец твой заклял народ, сказав: «проклят, кто сегодня вкусит пищи»; от этого народ истомился.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (12879, 'rst66', '1sa', 14, 27, 'Ионафан же не слышал, когда отец его заклинал народ, и, протянув конец палки, которая была в руке его, обмакнул ее в сот медовый и обратил рукою к устам своим, и просветлели глаза его.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (12878, 'rst66', '1sa', 14, 26, 'И вошел народ в лес, говоря: вот, течет мед. Но никто не протянул руки своей ко рту своему, ибо народ боялся заклятия.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (12877, 'rst66', '1sa', 14, 25, 'И пошел весь народ в лес, и был там на поляне мед.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (12876, 'rst66', '1sa', 14, 24, 'Люди Израильские были истомлены в тот день; а Саул заклял народ, сказав: проклят, кто вкусит хлеба до вечера, доколе я не отомщу врагам моим. И никто из народа не вкусил пищи.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (12875, 'rst66', '1sa', 14, 23, 'И спас Господь в тот день Израиля; битва же простерлась даже до Беф‐Авена.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (12874, 'rst66', '1sa', 14, 22, 'и все Израильтяне, скрывавшиеся в горе Ефремовой, услышав, что Филистимляне побежали, также пристали к своим в сражении.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (12873, 'rst66', '1sa', 14, 21, 'Тогда и Евреи, которые вчера и третьего дня были у Филистимлян и которые повсюду ходили с ними в стане, пристали к Израильтянам, находившимся с Саулом и Ионафаном;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (12872, 'rst66', '1sa', 14, 20, 'И воскликнул Саул и весь народ, бывший с ним, и пришли к месту сражения, и вот, там меч каждого <i>обращен</i> был против ближнего своего; смятение <i>было</i> очень великое.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (12871, 'rst66', '1sa', 14, 19, 'Саул еще говорил к священнику, как смятение в стане Филистимском более и более <i>распространялось и</i> увеличивалось. Тогда сказал Саул священнику: сложи руки твои.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (12870, 'rst66', '1sa', 14, 18, 'И сказал Саул Ахии: «принеси кивот Божий», ибо кивот Божий в то время был с сынами Израильскими.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (12869, 'rst66', '1sa', 14, 17, 'И сказал Саул к народу, бывшему с ним: пересмотрите и узнайте, кто из наших вышел. И пересмотрели, и вот, нет Ионафана и оруженосца его.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (12868, 'rst66', '1sa', 14, 16, 'И увидели стражи Саула в Гиве Вениаминовой, что толпа рассеивается и бежит туда и сюда.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (12867, 'rst66', '1sa', 14, 15, 'И произошел ужас в стане на поле и во всем народе; передовые отряды и опустошавшие землю пришли в трепет; дрогнула вся земля, и был ужас великий от Господа.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (12866, 'rst66', '1sa', 14, 14, 'И пало от этого первого поражения, нанесенного Ионафаном и оруженосцем его, около двадцати человек, на половине поля, обрабатываемого парою волов в день.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (12865, 'rst66', '1sa', 14, 13, 'И начал всходить Ионафан, <i>цепляясь</i> руками и ногами, и оруженосец его за ним. И падали <i>Филистимляне</i> пред Ионафаном, а оруженосец добивал их за ним.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (12864, 'rst66', '1sa', 14, 12, 'И закричали люди, составлявшие отряд, к Ионафану и оруженосцу его, говоря: взойдите к нам, и мы вам скажем нечто. Тогда Ионафан сказал оруженосцу своему: следуй за мною, ибо Господь предал их в руки Израиля.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (12863, 'rst66', '1sa', 14, 11, 'Когда оба они стали на виду у отряда Филистимского, то Филистимляне сказали: вот, Евреи выходят из ущелий, в которых попрятались они.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (12862, 'rst66', '1sa', 14, 10, 'а если так скажут: «поднимитесь к нам», то мы взойдем, ибо Господь предал их в руки наши; и это будет знаком для нас.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (12861, 'rst66', '1sa', 14, 9, 'если они так скажут нам: «остановитесь, пока мы подойдем к вам», то мы остановимся на своих местах и не взойдем к ним;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (12860, 'rst66', '1sa', 14, 8, 'И сказал Ионафан: вот, мы перейдем к этим людям и станем на виду у них;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (12859, 'rst66', '1sa', 14, 7, 'И отвечал оруженосец: делай все, что на сердце у тебя; иди, вот я с тобою, куда тебе угодно.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (12858, 'rst66', '1sa', 14, 6, 'И сказал Ионафан слуге оруженосцу своему: ступай, перейдем к отряду этих необрезанных; может быть, Господь поможет нам, ибо для Господа нетрудно спасти чрез многих или немногих.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (12857, 'rst66', '1sa', 14, 5, 'одна скала выдавалась с севера к Михмасу, другая с юга к Гиве.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (12856, 'rst66', '1sa', 14, 4, 'Между переходами, по которым Ионафан искал пробраться к отряду Филистимскому, была острая скала с одной стороны и острая скала с другой: имя одной Боцец, а имя другой Сене;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (12855, 'rst66', '1sa', 14, 3, 'и Ахия, сын Ахитува, брата Иохаведа, сына Финееса, сына Илия, священник Господа в Силоме, носивший ефод. Народ же не знал, что Ионафан пошел.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (12854, 'rst66', '1sa', 14, 2, 'Саул же находился в окраине Гивы, под гранатовым деревом, что в Мигроне. С ним было около шестисот человек народа');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (12853, 'rst66', '1sa', 14, 1, 'В один день сказал Ионафан, сын Саулов, слуге оруженосцу своему: ступай, перейдем к отряду Филистимскому, что на той стороне. А отцу своему не сказал <i>об этом</i>.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (12852, 'rst66', '1sa', 13, 23, 'И вышел передовой отряд Филистимский к переправе Михмасской.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (12851, 'rst66', '1sa', 13, 22, 'Поэтому во время войны не было ни меча, ни копья у всего народа, бывшего с Саулом и Ионафаном, а <i>только</i> нашлись они у Саула и Ионафана, сына его.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (12850, 'rst66', '1sa', 13, 21, 'когда сделается щербина на острие у сошников, и у заступов, и у вил, и у топоров, или нужно рожон поправить.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (12849, 'rst66', '1sa', 13, 20, 'И должны были ходить все Израильтяне к Филистимлянам оттачивать свои сошники, и свои заступы, и свои топоры, и свои кирки,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (12848, 'rst66', '1sa', 13, 19, 'Кузнецов не было во всей земле Израильской, ибо Филистимляне опасались, чтобы Евреи не сделали меча или копья.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (12847, 'rst66', '1sa', 13, 18, 'другой отряд направился по дороге Вефоронской, а третий направился по дороге к границе долины Цевоим, к пустыне.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (12846, 'rst66', '1sa', 13, 17, 'И вышли из стана Филистимского три отряда для опустошения земли: один направился по дороге к Офре, в округ Суаль,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (12845, 'rst66', '1sa', 13, 16, 'Саул с сыном своим Ионафаном и людьми, находившимися при них, засели в Гиве Вениаминовой; Филистимляне же стояли станом в Михмасе.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (12844, 'rst66', '1sa', 13, 15, 'И встал Самуил и пошел из Галгала в Гиву Вениаминову; а Саул пересчитал людей, бывших с ним, до шестисот человек.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (12843, 'rst66', '1sa', 13, 14, 'но теперь не устоять царствованию твоему; Господь найдет Себе мужа по сердцу Своему, и повелит ему Господь быть вождем народа Своего, так как ты не исполнил того, что было повелено тебе Господом.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (12842, 'rst66', '1sa', 13, 13, 'И сказал Самуил Саулу: худо поступил ты, что не исполнил повеления Господа, Бога твоего, которое дано было тебе, ибо ныне упрочил бы Господь царствование твое над Израилем навсегда;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (12841, 'rst66', '1sa', 13, 12, 'тогда подумал я: «теперь придут на меня Филистимляне в Галгал, а я еще не вопросил Господа», и потому решился принести всесожжение.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (12840, 'rst66', '1sa', 13, 11, 'Но Самуил сказал: что ты сделал? Саул отвечал: я видел, что народ разбегается от меня, а ты не приходил к назначенному времени; Филистимляне же собрались в Михмасе;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (12839, 'rst66', '1sa', 13, 10, 'Но едва кончил он возношение всесожжения, вот, приходит Самуил; и вышел Саул к нему навстречу, чтобы приветствовать его.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (12838, 'rst66', '1sa', 13, 9, 'И сказал Саул: приведите ко мне, что <i>назначено</i> для жертвы всесожжения и для жертв мирных. И вознес всесожжение.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (12837, 'rst66', '1sa', 13, 8, 'И ждал он семь дней, до срока, <i>назначенного</i> Самуилом, а Самуил не приходил в Галгал; и стал народ разбегаться от него.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (12836, 'rst66', '1sa', 13, 7, 'а <i>некоторые</i> из Евреев переправились за Иордан в страну Гадову и Галаадскую. Саул же находился еще в Галгале, и весь народ, бывший с ним, находился в страхе.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (12835, 'rst66', '1sa', 13, 6, 'Израильтяне, видя, что они в опасности, потому что народ был стеснен, укрывались в пещерах и в ущельях, и между скалами, и в башнях, и во рвах;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (12834, 'rst66', '1sa', 13, 5, 'И собрались Филистимляне на войну против Израиля: тридцать тысяч колесниц и шесть тысяч конницы, и народа множество, как песок на берегу моря; и пришли и расположились станом в Михмасе, с восточной стороны Беф‐Авена.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (12833, 'rst66', '1sa', 13, 4, 'Когда весь Израиль услышал, что разбил Саул охранный отряд Филистимский и что Израиль сделался ненавистным для Филистимлян, то народ собрался к Саулу в Галгал.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (12832, 'rst66', '1sa', 13, 3, 'И разбил Ионафан охранный отряд Филистимский, который был в Гиве; и услышали об этом Филистимляне, а Саул протрубил трубою по всей стране, возглашая: да услышат Евреи!');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (12831, 'rst66', '1sa', 13, 2, 'две тысячи были с Саулом в Михмасе и на горе Вефильской, тысяча же была с Ионафаном в Гиве Вениаминовой; а прочий народ отпустил он по домам своим.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (12830, 'rst66', '1sa', 13, 1, 'Год был по воцарении Саула, и другой год царствовал он над Израилем, как выбрал Саул себе три тысячи из Израильтян:');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (12829, 'rst66', '1sa', 12, 25, 'если же вы будете делать зло, то и вы и царь ваш погибнете.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (12828, 'rst66', '1sa', 12, 24, 'только бойтесь Господа и служите Ему истинно, от всего сердца вашего, ибо вы видели, какие великие дела Он сделал с вами;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (12827, 'rst66', '1sa', 12, 23, 'и я также не допущу себе греха пред Господом, чтобы перестать молиться за вас, и буду наставлять вас на путь добрый и прямой;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (12826, 'rst66', '1sa', 12, 22, 'Господь же не оставит народа Своего ради великого имени Своего, ибо Господу угодно было избрать вас народом Своим;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (12825, 'rst66', '1sa', 12, 21, 'и не обращайтесь вслед ничтожных <i>богов</i>, которые не принесут пользы и не избавят; ибо они &mdash; ничто;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (12824, 'rst66', '1sa', 12, 20, 'И отвечал Самуил народу: не бойтесь, грех этот вами сделан, но вы не отступайте только от Господа и служите Господу всем сердцем вашим');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (12823, 'rst66', '1sa', 12, 19, 'И сказал весь народ Самуилу: помолись о рабах твоих пред Господом, Богом твоим, чтобы не умереть нам; ибо ко всем грехам нашим мы прибавили еще грех, когда просили себе царя.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (12822, 'rst66', '1sa', 12, 18, 'И воззвал Самуил к Господу, и Господь послал гром и дождь в тот день; и пришел весь народ в большой страх от Господа и Самуила.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (12821, 'rst66', '1sa', 12, 17, 'не жатва ли пшеницы ныне? Но я воззову к Господу, и пошлет Он гром и дождь, и вы узнаете и увидите, как велик грех, который вы сделали пред очами Господа, прося себе царя.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (12820, 'rst66', '1sa', 12, 16, 'Теперь станьте и посмотрите на дело великое, которое Господь совершит пред глазами вашими:');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (12819, 'rst66', '1sa', 12, 15, 'а если не будете слушать гласа Господа и станете противиться повелениям Господа, то рука Господа будет против вас, <i>как была</i> против отцов ваших.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (12818, 'rst66', '1sa', 12, 14, 'Если будете бояться Господа и служить Ему и слушать гласа Его, и не станете противиться повелениям Господа, то будете и вы и царь ваш, который царствует над вами, <i>ходить</i> вслед Господа, Бога вашего;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (12817, 'rst66', '1sa', 12, 13, 'Итак, вот царь, которого вы избрали, которого вы требовали: вот, Господь поставил над вами царя.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (12816, 'rst66', '1sa', 12, 12, 'Но увидев, что Наас, царь Аммонитский, идет против вас, вы сказали мне: «нет, царь пусть царствует над нами», тогда как Господь, Бог ваш, &mdash; Царь ваш.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (12815, 'rst66', '1sa', 12, 11, 'тогда Господь послал Иероваала, и Варака, и Иеффая, и Самуила, и избавил вас от руки врагов ваших, окружавших вас, и вы жили безопасно.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (12814, 'rst66', '1sa', 12, 10, 'Но когда они возопили к Господу и сказали: «согрешили мы, ибо оставили Господа и стали служить Ваалам и Астартам, теперь избавь нас от руки врагов наших, и мы будем служить Тебе»,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (12813, 'rst66', '1sa', 12, 9, 'Но они забыли Господа, Бога своего, и Он предал их в руки Сисары, военачальника Асорского, и в руки Филистимлян и в руки царя Моавитского, <i>которые</i> воевали против них.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (12812, 'rst66', '1sa', 12, 8, 'Когда пришел Иаков в Египет, и отцы ваши возопили к Господу, то Господь послал Моисея и Аарона, и они вывели отцов ваших из Египта и поселили их на месте сем.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (12811, 'rst66', '1sa', 12, 7, 'Теперь же предстаньте, и я буду судиться с вами пред Господом о всех благодеяниях, которые оказал Он вам и отцам вашим.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (12810, 'rst66', '1sa', 12, 6, 'Тогда Самуил сказал народу: <i>свидетель</i> Господь, Который поставил Моисея и Аарона и Который вывел отцов ваших из земли Египетской.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (12809, 'rst66', '1sa', 12, 5, 'И сказал он им: свидетель на вас Господь, и свидетель помазанник Его в сей день, что вы не нашли ничего за мною. И сказали: свидетель.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (12808, 'rst66', '1sa', 12, 4, 'И отвечали: ты не обижал нас и не притеснял нас и ничего ни у кого не взял.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (12807, 'rst66', '1sa', 12, 3, 'вот я; свидетельствуйте на меня пред Господом и пред помазанником Его, у кого взял я вола, у кого взял осла, кого обидел и кого притеснил, у кого взял дар и закрыл в <i>деле</i> его глаза мои, &mdash; и я возвращу вам.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (12806, 'rst66', '1sa', 12, 2, 'и вот, царь ходит пред вами; а я состарился и поседел; и сыновья мои с вами; я же ходил пред вами от юности моей и до сего дня;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (12805, 'rst66', '1sa', 12, 1, 'И сказал Самуил всему Израилю: вот, я послушался голоса вашего во всем, что вы говорили мне, и поставил над вами царя,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (12804, 'rst66', '1sa', 11, 15, 'И пошел весь народ в Галгал, и поставили там Саула царем пред Господом в Галгале, и принесли там мирные жертвы пред Господом. И весьма веселились там Саул и все Израильтяне.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (12803, 'rst66', '1sa', 11, 14, 'И сказал Самуил народу: пойдем в Галгал, и обновим там царство.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (12802, 'rst66', '1sa', 11, 13, 'Но Саул сказал: в сей день никого не должно умерщвлять, ибо сегодня Господь совершил спасение в Израиле.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (12801, 'rst66', '1sa', 11, 12, 'Тогда сказал народ Самуилу: кто говорил: «Саулу ли царствовать над нами»? дайте этих людей, и мы умертвим их.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (12800, 'rst66', '1sa', 11, 11, 'В следующий день Саул разделил народ на три отряда, и они проникли в средину стана во время утренней стражи и поразили Аммонитян до дневного зноя; оставшиеся рассеялись, так что не осталось из них двоих вместе.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (12799, 'rst66', '1sa', 11, 10, 'И сказали жители Иависа <i>Наасу</i>: завтра выйдем к вам, и поступайте с нами, как вам угодно.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (12798, 'rst66', '1sa', 11, 9, 'И сказали пришедшим послам: так скажите жителям Иависа Галаадского: завтра будет к вам помощь, когда обогреет солнце. И пришли послы и объявили жителям Иависа, и они обрадовались.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (12797, 'rst66', '1sa', 11, 8, '<i>Саул</i> осмотрел их в Везеке, и нашлось сынов Израилевых триста тысяч и мужей Иудиных тридцать тысяч.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (12796, 'rst66', '1sa', 11, 7, 'и взял он пару волов, и рассек их на части, и послал во все пределы Израильские чрез тех послов, объявляя, что так будет поступлено с волами того, кто не пойдет вслед Саула и Самуила. И напал страх Господень на народ, и выступили <i>все</i>, как один человек.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (12795, 'rst66', '1sa', 11, 6, 'И сошел Дух Божий на Саула, когда он услышал слова сии, и сильно воспламенился гнев его;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (12794, 'rst66', '1sa', 11, 5, 'И вот, пришел Саул позади волов с поля и сказал: что <i>сделалось</i> с народом, что он плачет? И пересказали ему слова жителей Иависа.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (12793, 'rst66', '1sa', 11, 4, 'И пришли послы в Гиву Саулову и пересказали слова сии вслух народа; и весь народ поднял вопль и заплакал.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (12792, 'rst66', '1sa', 11, 3, 'И сказали ему старейшины Иависа: дай нам сроку семь дней, чтобы послать нам послов во все пределы Израильские, и если никто не поможет нам, то мы выйдем к тебе.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (12791, 'rst66', '1sa', 11, 2, 'И сказал им Наас Аммонитянин: я заключу с вами союз, но с тем, чтобы выколоть у каждого из вас правый глаз и тем положить бесчестие на всего Израиля.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (12790, 'rst66', '1sa', 11, 1, 'И пришел Наас Аммонитянин и осадил Иавис Галаадский. И сказали все жители Иависа Наасу: заключи с нами союз, и мы будем служить тебе.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (12789, 'rst66', '1sa', 10, 27, 'А негодные люди говорили: ему ли спасать нас? И презрели его и не поднесли ему даров; но он как бы не замечал того.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (12788, 'rst66', '1sa', 10, 26, 'Так же и Саул пошел в дом свой, в Гиву; и пошли с ним храбрые, которых сердца коснулся Бог.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (12787, 'rst66', '1sa', 10, 25, 'И изложил Самуил народу права царства, и написал в книгу, и положил пред Господом. И отпустил весь народ, каждого в дом свой.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (12786, 'rst66', '1sa', 10, 24, 'И сказал Самуил всему народу: видите ли, кого избрал Господь? подобного ему нет во всем народе. Тогда весь народ воскликнул и сказал: да живет царь!');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (12785, 'rst66', '1sa', 10, 23, 'И побежали и взяли его оттуда, и он стал среди народа и был от плеч своих выше всего народа.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (12784, 'rst66', '1sa', 10, 22, 'И вопросили еще Господа: придет ли еще он сюда? И сказал Господь: вот, он скрывается в обозе.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (12783, 'rst66', '1sa', 10, 21, 'И велел подходить колену Вениаминову по племенам его, и указано племя Матриево; и приводят племя Матриево по мужам, и назван Саул, сын Кисов; и искали его, и не находили.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (12782, 'rst66', '1sa', 10, 20, 'И велел Самуил подходить всем коленам Израилевым, и указано колено Вениаминово.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (12781, 'rst66', '1sa', 10, 19, 'А вы теперь отвергли Бога вашего, Который спасает вас от всех бедствий ваших и скорбей ваших, и сказали Ему: «царя поставь над нами». Итак, предстаньте теперь пред Господом по коленам вашим и по племенам вашим.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (12780, 'rst66', '1sa', 10, 18, 'и сказал сынам Израилевым: так говорит Господь, Бог Израилев: Я вывел Израиля из Египта и избавил вас от руки Египтян и от руки всех царств, угнетавших вас.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (12779, 'rst66', '1sa', 10, 17, 'И созвал Самуил народ к Господу в Массифу,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (12778, 'rst66', '1sa', 10, 16, 'И сказал Саул дяде своему: он объявил нам, что ослицы нашлись. А того, что сказал ему Самуил о царстве, не открыл ему.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (12777, 'rst66', '1sa', 10, 15, 'И сказал дядя Саулов: расскажи мне, что сказал вам Самуил.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (12776, 'rst66', '1sa', 10, 14, 'И сказал дядя Саулов ему и слуге его: куда вы ходили? Он сказал: искать ослиц, но, видя, что <i>их</i> нет, зашли к Самуилу.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (12775, 'rst66', '1sa', 10, 13, 'И перестал он пророчествовать, и пошел на высоту.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (12774, 'rst66', '1sa', 10, 12, 'И отвечал один из бывших там, и сказал: а у тех кто отец? Посему вошло в пословицу: «неужели и Саул во пророках?»');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (12773, 'rst66', '1sa', 10, 11, 'Все, знавшие его вчера и третьего дня, увидев, что он с пророками пророчествует, говорили в народе друг другу: что это сталось с сыном Кисовым? неужели и Саул во пророках?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (12772, 'rst66', '1sa', 10, 10, 'Когда пришли они к холму, вот встречается им сонм пророков, и сошел на него Дух Божий, и он пророчествовал среди них.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (12771, 'rst66', '1sa', 10, 9, 'Как скоро Саул обратился, чтоб идти от Самуила, Бог дал ему иное сердце, и сбылись все те знамения в тот же день.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (12770, 'rst66', '1sa', 10, 8, 'И ты пойди прежде меня в Галгал, куда и я приду к тебе для принесения всесожжений и мирных жертв; семь дней жди, доколе я не приду к тебе, и тогда укажу тебе, что тебе делать.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (12769, 'rst66', '1sa', 10, 7, 'Когда эти знамения сбудутся с тобою, тогда делай, что может рука твоя, ибо с тобою Бог.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (12768, 'rst66', '1sa', 10, 6, 'и найдет на тебя Дух Господень, и ты будешь пророчествовать с ними и сделаешься иным человеком.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (12767, 'rst66', '1sa', 10, 5, 'После того ты придешь на холм Божий, где охранный отряд Филистимский; и когда войдешь там в город, встретишь сонм пророков, сходящих с высоты, и пред ними псалтирь и тимпан, и свирель и гусли, и они пророчествуют;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (12766, 'rst66', '1sa', 10, 4, 'и будут приветствовать они тебя и дадут тебе два хлеба, и ты возьмешь из рук их.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (12765, 'rst66', '1sa', 10, 3, 'И пойдешь оттуда далее и придешь к дубраве Фаворской, и встретят тебя там три человека, идущих к Богу в Вефиль: один несет трех козлят, другой несет три хлеба, а третий несет мех с вином;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (12764, 'rst66', '1sa', 10, 2, 'когда ты теперь пойдешь от меня, то встретишь двух человек близ гроба Рахили на пределах Вениаминовых, в Целцахе, и они скажут тебе: «нашлись ослицы, которых ты ходил искать, и вот отец твой, забыв об ослицах, беспокоится о вас, говоря: что с сыном моим?»');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (12763, 'rst66', '1sa', 10, 1, 'И взял Самуил сосуд с елеем и вылил на голову его, и поцеловал его и сказал: вот, Господь помазывает тебя в правителя наследия Своего:');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (12762, 'rst66', '1sa', 9, 27, 'Когда подходили они к концу города, Самуил сказал Саулу: скажи слуге, чтобы он пошел впереди нас, &mdash; и он пошел вперед; &mdash; а ты остановись теперь, и я открою тебе, что сказал Бог.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (12761, 'rst66', '1sa', 9, 26, 'Утром встали они так: когда взошла заря, Самуил воззвал к Саулу на кровлю и сказал: встань, я провожу тебя. И встал Саул, и вышли оба они из дома, он и Самуил.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (12760, 'rst66', '1sa', 9, 25, 'И сошли они с высоты в город, и Самуил разговаривал с Саулом на кровле.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (12759, 'rst66', '1sa', 9, 24, 'И взял повар плечо и что было при нем и положил пред Саулом. И сказал <i>Самуил</i>: вот это оставлено, положи пред собою <i>и</i> ешь, ибо к сему времени сбережено <i>это</i> для тебя, когда я созывал народ. И обедал Саул с Самуилом в тот день.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (12758, 'rst66', '1sa', 9, 23, 'И сказал Самуил повару: подай ту часть, которую я дал тебе и о которой я сказал тебе: «отложи ее у себя».');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (12757, 'rst66', '1sa', 9, 22, 'И взял Самуил Саула и слугу его, и ввел их в комнату, и дал им первое место между зваными, которых было около тридцати человек.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (12756, 'rst66', '1sa', 9, 21, 'И отвечал Саул и сказал: не сын ли я Вениамина, одного из меньших колен Израилевых? И племя мое не малейшее ли между всеми племенами колена Вениаминова? К чему же ты говоришь мне это?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (12755, 'rst66', '1sa', 9, 20, 'а об ослицах, которые у тебя пропали уже три дня, не заботься; они нашлись. И кому все вожделенное в Израиле? Не тебе ли и всему дому отца твоего?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (12754, 'rst66', '1sa', 9, 19, 'И отвечал Самуил Саулу, и сказал: я прозорливец, иди впереди меня на высоту; и вы будете обедать со мною сегодня, и отпущу тебя утром, и все, что у тебя на сердце, скажу тебе;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (12753, 'rst66', '1sa', 9, 18, 'И подошел Саул к Самуилу в воротах и спросил его: скажи мне, где дом прозорливца?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (12752, 'rst66', '1sa', 9, 17, 'Когда Самуил увидел Саула, то Господь сказал ему: вот человек, о котором Я говорил тебе; он будет управлять народом Моим.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (12751, 'rst66', '1sa', 9, 16, 'завтра в это время Я пришлю к тебе человека из земли Вениаминовой, и ты помажь его в правителя народу Моему &mdash; Израилю, и он спасет народ Мой от руки Филистимлян; ибо Я призрел на народ Мой, так как вопль его достиг до Меня.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (12750, 'rst66', '1sa', 9, 15, 'А Господь открыл Самуилу за день до прихода Саулова и сказал:');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (12749, 'rst66', '1sa', 9, 14, 'И пошли они в город. Когда же вошли в средину города, то вот и Самуил выходит навстречу им, чтоб идти на высоту.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (12748, 'rst66', '1sa', 9, 13, 'когда придете в город, застанете его, пока он еще не пошел на ту высоту, на обед; ибо народ не начнет есть, доколе он не придет; потому что он благословит жертву, и после того станут есть званые; итак, ступайте, теперь еще застанете его.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (12747, 'rst66', '1sa', 9, 12, 'Те отвечали им и сказали: есть; вот, он впереди тебя; только поспешай, ибо он сегодня пришел в город, потому что сегодня у народа жертвоприношение на высоте;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (12746, 'rst66', '1sa', 9, 11, 'Когда они поднимались вверх в город, то встретили девиц, вышедших черпать воду, и сказали им: есть ли здесь прозорливец?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (12745, 'rst66', '1sa', 9, 10, 'И сказал Саул слуге своему: хорошо ты говоришь; пойдем. И пошли в город, где человек Божий.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (12744, 'rst66', '1sa', 9, 9, 'Прежде у Израиля, когда кто‐нибудь шел вопрошать Бога, говорили так: «пойдем к прозорливцу»; ибо тот, кого <i>называют</i> ныне пророком, прежде назывался прозорливцем.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (12743, 'rst66', '1sa', 9, 8, 'И опять отвечал слуга Саулу и сказал: вот в руке моей четверть сикля серебра; я отдам человеку Божию, и он укажет нам путь наш.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (12742, 'rst66', '1sa', 9, 7, 'И сказал Саул слуге своему: вот мы пойдем, а что мы принесем тому человеку? ибо хлеба не стало в сумах наших, и подарка нет, чтобы поднести человеку Божию; что у нас?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (12741, 'rst66', '1sa', 9, 6, 'Но слуга сказал ему: вот в этом городе есть человек Божий, человек уважаемый; все, что он ни скажет, сбывается; сходим теперь туда; может быть, он укажет нам путь наш, по которому нам идти.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (12740, 'rst66', '1sa', 9, 5, 'Когда они пришли в землю Цуф, Саул сказал слуге своему, который был с ним: пойдем назад, чтобы отец мой, оставив ослиц, не стал беспокоиться о нас.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (12739, 'rst66', '1sa', 9, 4, 'И прошел он гору Ефремову, и прошел землю Шалишу, но не нашли; и прошли землю Шаалим, и <i>там их</i> нет; и прошел он землю Вениаминову, и не нашли.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (12738, 'rst66', '1sa', 9, 3, 'И пропали ослицы у Киса, отца Саулова, и сказал Кис Саулу, сыну своему: возьми с собою одного из слуг и встань, пойди, поищи ослиц.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (12737, 'rst66', '1sa', 9, 2, 'У него был сын, имя его Саул, молодой и красивый; и не было никого из Израильтян красивее его; он от плеч своих был выше всего народа.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (12736, 'rst66', '1sa', 9, 1, 'Был некто из сынов Вениамина, имя его Кис, сын Авиила, сына Церона, сына Бехорафа, сына Афия, сына некоего Вениамитянина, человек знатный.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (12735, 'rst66', '1sa', 8, 22, 'И сказал Господь Самуилу: послушай голоса их и поставь им царя. И сказал Самуил Израильтянам: пойдите каждый в свой город.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (12734, 'rst66', '1sa', 8, 21, 'И выслушал Самуил все слова народа, и пересказал их вслух Господа.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (12733, 'rst66', '1sa', 8, 20, 'и мы будем как прочие народы: будет судить нас царь наш, и ходить пред нами, и вести войны наши.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (12732, 'rst66', '1sa', 8, 19, 'Но народ не согласился послушаться голоса Самуила, и сказал: нет, пусть царь будет над нами,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (12731, 'rst66', '1sa', 8, 18, 'и восстенаете тогда от царя вашего, которого вы избрали себе; и не будет Господь отвечать вам тогда.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (12730, 'rst66', '1sa', 8, 17, 'от мелкого скота вашего возьмет десятую часть, и сами вы будете ему рабами;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (12729, 'rst66', '1sa', 8, 16, 'и рабов ваших и рабынь ваших, и юношей ваших лучших, и ослов ваших возьмет и употребит на свои дела;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (12728, 'rst66', '1sa', 8, 15, 'и от посевов ваших и из виноградных садов ваших возьмет десятую часть и отдаст евнухам своим и слугам своим;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (12727, 'rst66', '1sa', 8, 14, 'и поля ваши и виноградные и масличные сады ваши лучшие возьмет, и отдаст слугам своим;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (12726, 'rst66', '1sa', 8, 13, 'и дочерей ваших возьмет, чтоб они составляли масти, варили кушанье и пекли хлебы;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (12725, 'rst66', '1sa', 8, 12, 'и поставит <i>их</i> у себя тысяченачальниками и пятидесятниками, и чтобы они возделывали поля его, и жали хлеб его, и делали ему воинское оружие и колесничный прибор его;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (12724, 'rst66', '1sa', 8, 11, 'и сказал: вот какие будут права царя, который будет царствовать над вами: сыновей ваших он возьмет, и приставит к колесницам своим, и <i>сделает</i> всадниками своими, и будут они бегать пред колесницами его;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (12723, 'rst66', '1sa', 8, 10, 'И пересказал Самуил все слова Господа народу, просящему у него царя,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (12722, 'rst66', '1sa', 8, 9, 'итак, послушай голоса их; только представь им и объяви им права царя, который будет царствовать над ними.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (12721, 'rst66', '1sa', 8, 8, 'как они поступали с того дня, в который Я вывел их из Египта, и до сего дня, оставляли Меня и служили иным богам, так поступают они и с тобою;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (12720, 'rst66', '1sa', 8, 7, 'И сказал Господь Самуилу: послушай голоса народа во всем, что они говорят тебе; ибо не тебя они отвергли, но отвергли Меня, чтоб Я не царствовал над ними;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (12719, 'rst66', '1sa', 8, 6, 'И не понравилось слово сие Самуилу, когда они сказали: дай нам царя, чтобы он судил нас. И молился Самуил Господу.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (12718, 'rst66', '1sa', 8, 5, 'и сказали ему: вот, ты состарился, а сыновья твои не ходят путями твоими; итак, поставь над нами царя, чтобы он судил нас, как у прочих народов.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (12717, 'rst66', '1sa', 8, 4, 'И собрались все старейшины Израиля, и пришли к Самуилу в Раму,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (12716, 'rst66', '1sa', 8, 3, 'Но сыновья его не ходили путями его, а уклонились в корысть и брали подарки, и судили превратно.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (12715, 'rst66', '1sa', 8, 2, 'Имя старшему сыну его Иоиль, а имя второму <i>сыну</i> его Авия; они <i>были</i> судьями в Вирсавии.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (12714, 'rst66', '1sa', 8, 1, 'Когда же состарился Самуил, то поставил сыновей своих судьями над Израилем.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (12713, 'rst66', '1sa', 7, 17, 'потом возвращался в Раму; ибо <i>там</i> был дом его, и там судил он Израиля, и построил там жертвенник Господу.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (12712, 'rst66', '1sa', 7, 16, 'из года в год он ходил и обходил Вефиль, и Галгал, и Массифу; и судил Израиля во всех сих местах;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (12711, 'rst66', '1sa', 7, 15, 'И был Самуил судьею Израиля во все дни жизни своей:');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (12710, 'rst66', '1sa', 7, 14, 'И возвращены были Израилю города, которые взяли Филистимляне у Израиля, от Аккарона и до Гефа, и пределы их освободил Израиль из рук Филистимлян, и был мир между Израилем и Аморреями.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (12709, 'rst66', '1sa', 7, 13, 'Так усмирены были Филистимляне, и не стали более ходить в пределы Израилевы; и была рука Господня на Филистимлянах во все дни Самуила.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (12708, 'rst66', '1sa', 7, 12, 'И взял Самуил один камень, и поставил между Массифою и между Сеном, и назвал его Авен‐Езер, сказав: до сего места помог нам Господь.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (12707, 'rst66', '1sa', 7, 11, 'И выступили Израильтяне из Массифы, и преследовали Филистимлян, и поражали их до места под Вефхором.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (12706, 'rst66', '1sa', 7, 10, 'И когда Самуил возносил всесожжение, Филистимляне пришли воевать с Израилем. Но Господь возгремел в тот день сильным громом над Филистимлянами и навел на них ужас, и они были поражены пред Израилем.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (12705, 'rst66', '1sa', 7, 9, 'И взял Самуил одного ягненка от сосцов, и принес его во всесожжение Господу, и воззвал Самуил к Господу о Израиле, и услышал его Господь.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (12704, 'rst66', '1sa', 7, 8, 'И сказали сыны Израилевы Самуилу: не переставай взывать о нас к Господу, Богу нашему, чтоб Он спас нас от руки Филистимлян.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (12703, 'rst66', '1sa', 7, 7, 'Когда же услышали Филистимляне, что собрались сыны Израилевы в Массифу, тогда пошли владетели Филистимские на Израиля. Израильтяне, услышав <i>о том</i>, убоялись Филистимлян.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (12702, 'rst66', '1sa', 7, 6, 'И собрались в Массифу, и черпали воду, и проливали пред Господом, и постились в тот день, говоря: согрешили мы пред Господом. И судил Самуил сынов Израилевых в Массифе.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (12701, 'rst66', '1sa', 7, 5, 'И сказал Самуил: соберите всех Израильтян в Массифу, и я помолюсь о вас Господу.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (12700, 'rst66', '1sa', 7, 4, 'И удалили сыны Израилевы Ваалов и Астарт и стали служить одному Господу.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (12699, 'rst66', '1sa', 7, 3, 'И сказал Самуил всему дому Израилеву, говоря: если вы всем сердцем своим обращаетесь к Господу, то удалите из среды себя богов иноземных и Астарт и расположите сердце ваше к Господу, и служите Ему одному, и Он избавит вас от руки Филистимлян.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (12698, 'rst66', '1sa', 7, 2, 'С того дня, как остался ковчег в Кириаф‐Иариме, прошло много времени, лет двадцать. И обратился весь дом Израилев к Господу.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (12697, 'rst66', '1sa', 7, 1, 'И пришли жители Кириаф‐Иарима, и взяли ковчег Господа, и принесли его в дом Аминадава, на холм, а Елеазара, сына его, посвятили, чтобы он хранил ковчег Господа.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (12696, 'rst66', '1sa', 6, 21, 'И послали послов к жителям Кириаф‐Иарима сказать: Филистимляне возвратили ковчег Господа; придите, возьмите его к себе.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (12695, 'rst66', '1sa', 6, 20, 'И сказали жители Вефсамиса: кто может стоять пред Господом, сим святым Богом? и к кому Он пойдет от нас?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (12694, 'rst66', '1sa', 6, 19, 'И поразил Он жителей Вефсамиса за то, что они заглядывали в ковчег Господа, и убил из народа пятьдесят тысяч семьдесят человек; и заплакал народ, ибо поразил Господь народ поражением великим.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (12693, 'rst66', '1sa', 6, 18, 'а золотые мыши <i>были</i> по числу всех городов Филистимских &mdash; пяти владетелей, от городов укрепленных и до открытых сел, до большого камня, на котором поставили ковчег Господа и <i>который находится</i> до сего дня на поле Иисуса Вефсамитянина.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (12692, 'rst66', '1sa', 6, 17, 'Золотые эти наросты, которые принесли Филистимляне в жертву повинности Господу, были: один за Азот, один за Газу, один за Аскалон, один за Геф, один за Аккарон;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (12691, 'rst66', '1sa', 6, 16, 'И пять владетелей Филистимских видели <i>это</i> и возвратились в тот день в Аккарон.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (12690, 'rst66', '1sa', 6, 15, 'Левиты сняли ковчег Господа и ящик, бывший при нем, в котором <i>были</i> золотые вещи, и поставили на большом том камне; жители же Вефсамиса принесли в тот день всесожжения и закололи жертвы Господу.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (12689, 'rst66', '1sa', 6, 14, 'Колесница же пришла на поле Иисуса Вефсамитянина и остановилась там; и был тут большой камень, и раскололи колесницу на дрова, а коров принесли во всесожжение Господу.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (12688, 'rst66', '1sa', 6, 13, '<i>Жители</i> Вефсамиса жали тогда пшеницу в долине, и, взглянув, увидели ковчег Господень, и обрадовались, что увидели его.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (12687, 'rst66', '1sa', 6, 12, 'И пошли коровы прямо на дорогу к Вефсамису; одною дорогою шли, шли и мычали, но не уклонялись ни направо, ни налево; владетели же Филистимские следовали за ними до пределов Вефсамиса.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (12686, 'rst66', '1sa', 6, 11, 'и поставили ковчег Господа на колесницу и ящик с золотыми мышами и изваяниями наростов.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (12685, 'rst66', '1sa', 6, 10, 'И сделали они так: и взяли двух первородивших коров и впрягли их в колесницу, а телят их удержали дома;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (12684, 'rst66', '1sa', 6, 9, 'и смотрите, если он пойдет к пределам своим, к Вефсамису, то он великое сие зло сделал нам; если же нет, то мы будем знать, что не его рука поразила нас, а сделалось это с нами случайно.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (12683, 'rst66', '1sa', 6, 8, 'и возьмите ковчег Господень, и поставьте его на колесницу, а золотые вещи, которые принесете Ему в жертву повинности, положите в ящик сбоку его; и отпустите его, и пусть пойдет;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (12682, 'rst66', '1sa', 6, 7, 'итак, возьмите, сделайте одну колесницу новую и возьмите двух первородивших коров, на которых не было ярма, и впрягите коров в колесницу, а телят их отведите от них домой;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (12681, 'rst66', '1sa', 6, 6, 'и для чего вам ожесточать сердце ваше, как ожесточили сердце свое Египтяне и фараон? вот, когда Господь показал силу Свою над ними, то они отпустили их, и те пошли;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (12680, 'rst66', '1sa', 6, 5, 'итак, сделайте изваяния наростов ваших и изваяния мышей ваших, опустошающих землю, и воздайте славу Богу Израилеву; может быть, Он облегчит руку Свою над вами и над богами вашими и над землею вашею;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (12679, 'rst66', '1sa', 6, 4, 'И сказали они: какую жертву повинности должны мы принести Ему? Те сказали: по числу владетелей Филистимских пять наростов золотых и пять мышей золотых; ибо казнь одна на всех вас и на владетелях ваших;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (12678, 'rst66', '1sa', 6, 3, 'Те сказали: если вы хотите отпустить ковчег Бога Израилева, то не отпускайте его ни с чем, но принесите Ему жертву повинности; тогда исцелитесь и узнаете, за что не отступает от вас рука Его.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (12677, 'rst66', '1sa', 6, 2, 'И призвали Филистимляне жрецов и прорицателей, и сказали: что нам делать с ковчегом Господним? научите нас, как нам отпустить его в свое место.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (12676, 'rst66', '1sa', 6, 1, 'И пробыл ковчег Господень в области Филистимской семь месяцев.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (12675, 'rst66', '1sa', 5, 12, 'И те, которые не умерли, поражены были наростами, так что вопль города восходил до небес.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (12674, 'rst66', '1sa', 5, 11, 'И послали, и собрали всех владетелей Филистимских, и сказали: отошлите ковчег Бога Израилева; пусть он возвратится в свое место, чтобы не умертвил он нас и народа нашего. Ибо смертельный ужас был во всем городе; весьма отяготела рука Божия на них.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (12673, 'rst66', '1sa', 5, 10, 'И отослали они ковчег Божий в Аскалон; и когда пришел ковчег Божий в Аскалон, возопили Аскалонитяне, говоря: принесли к нам ковчег Бога Израилева, чтоб умертвить нас и народ наш.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (12672, 'rst66', '1sa', 5, 9, 'После того, как отправили его, была рука Господа на городе &mdash; ужас весьма великий, и поразил Господь жителей города от малого до большого, и показались на них наросты.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (12671, 'rst66', '1sa', 5, 8, 'И послали, и собрали к себе всех владетелей Филистимских, и сказали: что нам делать с ковчегом Бога Израилева? И сказали: пусть ковчег Бога Израилева перейдет <i>в Геф</i>. И отправили ковчег Бога Израилева в Геф.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (12670, 'rst66', '1sa', 5, 7, 'И увидели это Азотяне и сказали: да не останется ковчег Бога Израилева у нас, ибо тяжка рука Его и для нас и для Дагона, бога нашего.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (12669, 'rst66', '1sa', 5, 6, 'И отяготела рука Господня над Азотянами, и Он поражал их и наказал их мучительными наростами, в Азоте и в окрестностях его.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (12668, 'rst66', '1sa', 5, 5, 'Посему жрецы Дагоновы и все приходящие в капище Дагона в Азот не ступают на порог Дагонов до сего дня.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (12667, 'rst66', '1sa', 5, 4, 'И встали они поутру на следующий день, и вот, Дагон лежит ниц на земле пред ковчегом Господним; голова Дагонова и обе руки его <i>лежали</i> отсеченные, каждая особо, на пороге, осталось только туловище Дагона.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (12666, 'rst66', '1sa', 5, 3, 'И встали Азотяне рано на другой день, и вот, Дагон лежит лицом своим к земле пред ковчегом Господним. И взяли они Дагона и опять поставили его на свое место.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (12665, 'rst66', '1sa', 5, 2, 'И взяли Филистимляне ковчег Божий, и внесли его в храм Дагона, и поставили его подле Дагона.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (12664, 'rst66', '1sa', 5, 1, 'Филистимляне же взяли ковчег Божий и принесли его из Авен‐Езера в Азот.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (12663, 'rst66', '1sa', 4, 22, 'Она сказала: отошла слава от Израиля, ибо взят ковчег Божий.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (12662, 'rst66', '1sa', 4, 21, 'И назвала младенца: Ихавод, сказав: «отошла слава от Израиля» &mdash; со взятием ковчега Божия и <i>со смертью</i> свекра ее и мужа ее.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (12661, 'rst66', '1sa', 4, 20, 'И когда умирала она, стоявшие при ней женщины говорили ей: не бойся, ты родила сына. Но она не отвечала и не обращала внимания.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (12660, 'rst66', '1sa', 4, 19, 'Невестка его, жена Финеесова, была беременна, уже пред родами. И когда услышала она известие о взятии ковчега Божия и о смерти свекра своего и мужа своего, то упала на колени и родила, ибо приступили к ней боли ее.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (12659, 'rst66', '1sa', 4, 18, 'Когда упомянул он о ковчеге Божием, <i>Илий</i> упал с седалища навзничь у ворот, сломал себе хребет и умер; ибо он <i>был</i> стар и тяжел. Был же он судьею Израиля сорок лет.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (12658, 'rst66', '1sa', 4, 17, 'И отвечал вестник и сказал: побежал Израиль пред Филистимлянами, и поражение великое произошло в народе, и оба сына твои, Офни и Финеес, умерли, и ковчег Божий взят.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (12657, 'rst66', '1sa', 4, 16, 'И сказал тот человек Илию: я пришел из стана, сегодня же бежал я с места сражения. И сказал <i>Илий:</i> что произошло, сын мой?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (12656, 'rst66', '1sa', 4, 15, 'Илий был тогда девяноста восьми лет; и глаза его померкли, и он не мог видеть.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (12655, 'rst66', '1sa', 4, 14, 'И услышал Илий звуки вопля и сказал: отчего такой шум? И тотчас подошел человек тот и объявил Илию.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (12654, 'rst66', '1sa', 4, 13, 'Когда пришел он, Илий сидел на седалище при дороге у ворот и смотрел, ибо сердце его трепетало за ковчег Божий. И когда человек тот пришел и объявил в городе, то громко восстенал весь город.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (12653, 'rst66', '1sa', 4, 12, 'И побежал один Вениамитянин с места сражения и пришел в Силом в тот же день; одежда на нем была разодрана, и прах на голове его.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (12652, 'rst66', '1sa', 4, 11, 'И ковчег Божий был взят, и два сына Илиевы, Офни и Финеес, умерли.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (12651, 'rst66', '1sa', 4, 10, 'И сразились Филистимляне, и поражены были Израильтяне, и каждый побежал в шатер свой, и было поражение весьма великое, и пало из Израильтян тридцать тысяч пеших.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (12650, 'rst66', '1sa', 4, 9, 'укрепитесь и будьте мужественны, Филистимляне, чтобы вам не быть в порабощении у Евреев, как они у вас в порабощении; будьте мужественны и сразитесь с ними.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (12649, 'rst66', '1sa', 4, 8, 'горе нам! кто избавит нас от руки этого сильного Бога? Это &mdash; тот Бог, Который поразил Египтян всякими казнями в пустыне;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (12648, 'rst66', '1sa', 4, 7, 'И устрашились Филистимляне, ибо сказали: Бог тот пришел к ним в стан. И сказали: горе нам! ибо не бывало подобного ни вчера, ни третьего дня;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (12647, 'rst66', '1sa', 4, 6, 'И услышали Филистимляне шум восклицаний и сказали: отчего такие громкие восклицания в стане Евреев? И узнали, что ковчег Господень прибыл в стан.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (12646, 'rst66', '1sa', 4, 5, 'И когда прибыл ковчег завета Господня в стан, весь Израиль поднял такой сильный крик, что земля стонала.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (12645, 'rst66', '1sa', 4, 4, 'И послал народ в Силом, и принесли оттуда ковчег завета Господа Саваофа, сидящего на херувимах; а при ковчеге завета Божия были и два сына Илиевы, Офни и Финеес.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (12644, 'rst66', '1sa', 4, 3, 'И пришел народ в стан; и сказали старейшины Израилевы: за что поразил нас Господь сегодня пред Филистимлянами? возьмем себе из Силома ковчег завета Господня, и он пойдет среди нас и спасет нас от руки врагов наших.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (12643, 'rst66', '1sa', 4, 2, 'И выстроились Филистимляне против Израильтян, и произошла битва, и были поражены Израильтяне Филистимлянами, которые побили на поле сражения около четырех тысяч человек.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (12642, 'rst66', '1sa', 4, 1, 'И было слово Самуила ко всему Израилю. И выступили Израильтяне против Филистимлян на войну и расположились станом при Авен‐Езере, а Филистимляне расположились при Афеке.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (12641, 'rst66', '1sa', 3, 21, 'И продолжал Господь являться в Силоме после того, как открыл Себя Самуилу в Силоме через слово Господне.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (12640, 'rst66', '1sa', 3, 20, 'И узнал весь Израиль от Дана до Вирсавии, что Самуил удостоен быть пророком Господним.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (12639, 'rst66', '1sa', 3, 19, 'И возрос Самуил, и Господь был с ним; и не осталось ни одного из слов его неисполнившимся.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (12638, 'rst66', '1sa', 3, 18, 'И объявил ему Самуил все и не скрыл от него <i>ничего</i>. Тогда сказал <i>Илий</i>: Он &mdash; Господь; что Ему угодно, то да сотворит.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (12637, 'rst66', '1sa', 3, 17, 'И сказал <i>Илий:</i> что сказано тебе? не скрой от меня; то и то сделает с тобою Бог, и еще больше сделает, если ты утаишь от меня что‐либо из всего того, что сказано тебе.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (12636, 'rst66', '1sa', 3, 16, 'Но Илий позвал Самуила и сказал: сын мой, Самуил! Тот сказал: вот я!');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (12635, 'rst66', '1sa', 3, 15, 'И спал Самуил до утра, и отворил двери дома Господня; и боялся Самуил объявить видение сие Илию.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (12634, 'rst66', '1sa', 3, 14, 'и посему клянусь дому Илия, что вина дома Илиева не загладится ни жертвами, ни приношениями хлебными вовек.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (12633, 'rst66', '1sa', 3, 13, 'Я объявил ему, что Я накажу дом его навеки за ту вину, что он знал, как сыновья его нечествуют, и не обуздывал их;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (12632, 'rst66', '1sa', 3, 12, 'в тот день Я исполню над Илием все то, что Я говорил о доме его; Я начну и окончу;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (12631, 'rst66', '1sa', 3, 11, 'И сказал Господь Самуилу: вот, Я сделаю дело в Израиле, о котором кто услышит, у того зазвенит в обоих ушах;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (12630, 'rst66', '1sa', 3, 10, 'И пришел Господь, и стал, и воззвал, как в тот и другой раз: Самуил, Самуил! И сказал Самуил: говори, <i>Господи,</i> ибо слышит раб Твой.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (12629, 'rst66', '1sa', 3, 9, 'И сказал Илий Самуилу: пойди назад и ложись, и когда <i>Зовущий</i> позовет тебя, ты скажи: «говори, Господи, ибо слышит раб Твой». И пошел Самуил и лег на месте своем.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (12628, 'rst66', '1sa', 3, 8, 'И воззвал Господь к Самуилу еще в третий раз. Он встал и пришел к Илию и сказал: вот я! ты звал меня. Тогда понял Илий, что Господь зовет отрока.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (12627, 'rst66', '1sa', 3, 7, 'Самуил еще не знал тогда <i>голоса</i> Господа, и еще не открывалось ему слово Господне.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (12626, 'rst66', '1sa', 3, 6, 'Но Господь в другой раз воззвал к Самуилу. Он встал, и пришел к Илию вторично, и сказал: вот я! ты звал меня? Но тот сказал: я не звал тебя, сын мой; пойди назад, ложись.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (12625, 'rst66', '1sa', 3, 5, 'И побежал к Илию и сказал: вот я! ты звал меня? Но тот сказал: я не звал тебя; пойди назад, ложись. И он пошел и лег.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (12624, 'rst66', '1sa', 3, 4, 'воззвал Господь к Самуилу. И отвечал он: вот я!');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (12623, 'rst66', '1sa', 3, 3, 'и светильник Божий еще не погас, и Самуил лежал в храме Господнем, где ковчег Божий;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (12622, 'rst66', '1sa', 3, 2, 'И было в то время, когда Илий лежал на своем месте, &mdash; глаза же его начали смежаться, и он не мог видеть, &mdash;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (12621, 'rst66', '1sa', 3, 1, 'Отрок Самуил служил Господу при Илии; слово Господне было редко в те дни, видения <i>были</i> не часты.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (12620, 'rst66', '1sa', 2, 36, 'и всякий, оставшийся из дома твоего, придет кланяться ему из‐за геры серебра и куска хлеба и скажет: «причисли меня к какой‐либо левитской должности, чтоб иметь пропитание».');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (12619, 'rst66', '1sa', 2, 35, 'И поставлю Себе священника верного; он будет поступать по сердцу Моему и по душе Моей; и дом его сделаю твердым, и он будет ходить пред помазанником Моим во все дни;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (12618, 'rst66', '1sa', 2, 34, 'И вот тебе знамение, которое последует с двумя сыновьями твоими, Офни и Финеесом: оба они умрут в один день.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (12617, 'rst66', '1sa', 2, 33, 'Я не отрешу у тебя <i>всех</i> от жертвенника Моего, чтобы томить глаза твои и мучить душу твою; но все потомство дома твоего будет умирать в средних летах.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (12616, 'rst66', '1sa', 2, 32, 'и ты будешь видеть бедствие жилища Моего, при всем том, что <i>Господь</i> благотворит Израилю, и не будет в доме твоем старца во все дни,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (12615, 'rst66', '1sa', 2, 31, 'Вот, наступают дни, в <i>которые</i> Я подсеку мышцу твою и мышцу дома отца твоего, так что не будет старца в доме твоем;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (12614, 'rst66', '1sa', 2, 30, 'Посему так говорит Господь, Бог Израилев: Я сказал <i>тогда:</i> «дом твой и дом отца твоего будут ходить пред лицом Моим вовек». Но теперь говорит Господь: да не будет так, ибо Я прославлю прославляющих Меня, а бесславящие Меня будут посрамлены.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (12613, 'rst66', '1sa', 2, 29, 'Для чего же вы попираете ногами жертвы Мои и хлебные приношения Мои, которые заповедал Я для жилища <i>Моего</i>, и для чего ты предпочитаешь Мне сыновей своих, утучняя себя начатками всех приношений народа Моего &mdash; Израиля?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (12612, 'rst66', '1sa', 2, 28, 'И не избрал ли его из всех колен Израилевых Себе во священника, чтоб он восходил к жертвеннику Моему, чтобы воскурял фимиам, чтобы носил ефод предо Мною? И не дал ли Я дому отца твоего от всех огнем сожигаемых жертв сынов Израилевых?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (12611, 'rst66', '1sa', 2, 27, 'И пришел человек Божий к Илию и сказал ему: так говорит Господь: не открылся ли Я дому отца твоего, когда еще были они в Египте, в доме фараона?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (12610, 'rst66', '1sa', 2, 26, 'Отрок же Самуил более и более приходил в возраст и в благоволение у Господа и у людей.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (12609, 'rst66', '1sa', 2, 25, 'если согрешит человек против человека, то помолятся о нем Богу; если же человек согрешит против Господа, то кто будет ходатаем о нем? Но они не слушали голоса отца своего, ибо Господь решил уже предать их смерти.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (12608, 'rst66', '1sa', 2, 24, 'Нет, дети мои, нехороша молва, которую я слышу; вы развращаете народ Господень;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (12607, 'rst66', '1sa', 2, 23, 'И сказал им: для чего вы делаете такие дела? ибо я слышу худые речи о вас от всего народа.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (12606, 'rst66', '1sa', 2, 22, 'Илий же был весьма стар и слышал все, как поступают сыновья его со всеми Израильтянами, и что они спят с женщинами, собиравшимися у входа в скинию собрания.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (12605, 'rst66', '1sa', 2, 21, 'И посетил Господь Анну, и зачала она и родила еще трех сыновей и двух дочерей; а отрок Самуил возрастал у Господа.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (12604, 'rst66', '1sa', 2, 20, 'И благословил Илий Елкану и жену его и сказал: да даст тебе Господь детей от жены сей вместо данного, которого ты отдал Господу! И пошли они в место свое.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (12603, 'rst66', '1sa', 2, 19, 'Верхнюю одежду малую делала ему мать его и приносила ему ежегодно, когда приходила с мужем своим для принесения положенной жертвы.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (12602, 'rst66', '1sa', 2, 18, 'Отрок же Самуил служил пред Господом, надевая льняной ефод.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (12601, 'rst66', '1sa', 2, 17, 'И грех этих молодых людей был весьма велик пред Господом, ибо они отвращали от жертвоприношений Господу.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (12600, 'rst66', '1sa', 2, 16, 'И <i>если</i> кто говорил ему: «пусть сожгут прежде тук, как должно, и <i>потом</i> возьми себе, сколько пожелает душа твоя», то он говорил: «нет, теперь же дай, а если нет, то силою возьму».');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (12599, 'rst66', '1sa', 2, 15, 'Даже прежде, нежели сожигали тук, приходил отрок священнический и говорил приносившему жертву: «дай мяса на жаркое священнику; он не возьмет у тебя вареного мяса, а дай сырое».');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (12598, 'rst66', '1sa', 2, 14, 'и опускал ее в котел, или в кастрюлю, или на сковороду, или в горшок, и что вынет вилка, то брал себе священник. Так поступали они со всеми Израильтянами, приходившими туда в Силом.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (12597, 'rst66', '1sa', 2, 13, 'и долга священников в отношении к народу. Когда кто приносил жертву, отрок священнический во время варения мяса приходил с вилкой в руке своей');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (12596, 'rst66', '1sa', 2, 12, 'Сыновья же Илия были люди негодные; они не знали Господа');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (12595, 'rst66', '1sa', 2, 11, 'И пошел Елкана в Раму в дом свой, а отрок остался служить Господу при Илии священнике.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (12594, 'rst66', '1sa', 2, 10, 'Господь сотрет препирающихся с Ним; с небес возгремит на них. <i>Господь</i> будет судить концы земли, и даст крепость царю Своему и вознесет рог помазанника Своего.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (12593, 'rst66', '1sa', 2, 9, 'Стопы святых Своих Он блюдет, а беззаконные во тьме исчезают; ибо не силою крепок человек.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (12592, 'rst66', '1sa', 2, 8, 'Из праха подъемлет Он бедного, из брения возвышает нищего, посаждая с вельможами, и престол славы дает им в наследие; ибо у Господа основания земли, и Он утвердил на них вселенную.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (12591, 'rst66', '1sa', 2, 7, 'Господь делает нищим и обогащает, унижает и возвышает.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (12590, 'rst66', '1sa', 2, 6, 'Господь умерщвляет и оживляет, низводит в преисподнюю и возводит;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (12589, 'rst66', '1sa', 2, 5, 'сытые работают из хлеба, а голодные отдыхают; даже бесплодная рождает семь раз, а многочадная изнемогает.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (12588, 'rst66', '1sa', 2, 4, 'Лук сильных преломляется, а немощные препоясываются силою;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (12587, 'rst66', '1sa', 2, 3, 'Не умножайте речей надменных; дерзкие слова да не исходят из уст ваших; ибо Господь есть Бог ведения, и дела у Него взвешены.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (12586, 'rst66', '1sa', 2, 2, 'Нет <i>столь</i> святого, как Господь; ибо нет другого, кроме Тебя; и нет твердыни, как Бог наш.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (12585, 'rst66', '1sa', 2, 1, 'И молилась Анна и говорила: возрадовалось сердце мое в Господе; вознесся рог мой в Боге моем; широко разверзлись уста мои на врагов моих, ибо я радуюсь о спасении Твоем.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (12584, 'rst66', '1sa', 1, 28, 'и я отдаю его Господу на все дни жизни его, служить Господу. И поклонилась там Господу.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (12583, 'rst66', '1sa', 1, 27, 'о сем дитяти молилась я, и исполнил мне Господь прошение мое, чего я просила у Него;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (12582, 'rst66', '1sa', 1, 26, 'и сказала: о, господин мой! да живет душа твоя, господин мой! я &mdash; та самая женщина, которая здесь при тебе стояла и молилась Господу;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (12581, 'rst66', '1sa', 1, 25, 'И закололи тельца; и привела отрока К Илию');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (12580, 'rst66', '1sa', 1, 24, 'Когда же вскормила его, пошла с ним в Силом, <i>взяв</i> три тельца, и одну ефу муки и мех вина, и пришла в дом Господа в Силом; отрок же был еще дитя.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (12579, 'rst66', '1sa', 1, 23, 'И сказал ей Елкана, муж ее: делай, что тебе угодно; оставайся, доколе не вскормишь его грудью; только да утвердит Господь слово, <i>вышедшее из уст твоих</i>. И осталась жена <i>его</i>, и кормила грудью сына своего, доколе не вскормила.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (12578, 'rst66', '1sa', 1, 22, 'Анна же не пошла, сказав мужу своему: когда младенец отнят будет от груди и подрастет, тогда я отведу его, и он явится пред Господом и останется там навсегда.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (12577, 'rst66', '1sa', 1, 21, 'И пошел муж ее Елкана и все семейство его совершить годичную жертву Господу и обеты свои.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (12576, 'rst66', '1sa', 1, 20, 'Чрез несколько времени зачала Анна и родила сына и дала ему имя: Самуил, ибо, <i>говорила она</i>, от Господа я испросила его.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (12575, 'rst66', '1sa', 1, 19, 'И встали они поутру, и поклонились пред Господом, и возвратились, и пришли в дом свой в Раму. И познал Елкана Анну, жену свою, и вспомнил о ней Господь.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (12574, 'rst66', '1sa', 1, 18, 'Она же сказала: да найдет раба твоя милость в очах твоих! И пошла она в путь свой, и ела, и лицо ее не было уже <i>печально</i>, как прежде.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (12573, 'rst66', '1sa', 1, 17, 'И отвечал Илий, и сказал: иди с миром, и Бог Израилев исполнит прошение твое, чего ты просила у Него.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (12572, 'rst66', '1sa', 1, 16, 'не считай рабы твоей негодною женщиною, ибо от великой печали моей и от скорби моей я говорила доселе.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (12571, 'rst66', '1sa', 1, 15, 'И отвечала Анна, и сказала: нет, господин мой; я &mdash; жена, скорбящая духом, вина и сикера я не пила, но изливаю душу мою пред Господом;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (12570, 'rst66', '1sa', 1, 14, 'И сказал ей Илий: доколе ты будешь пьяною? вытрезвись от вина твоего.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (12569, 'rst66', '1sa', 1, 13, 'и как Анна говорила в сердце своем, а уста ее только двигались, и не было слышно голоса ее, то Илий счел ее пьяною.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (12568, 'rst66', '1sa', 1, 12, 'Между тем как она долго молилась пред Господом, Илий смотрел на уста ее;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (12567, 'rst66', '1sa', 1, 11, 'и дала обет, говоря: Господи Саваоф! если Ты призришь на скорбь рабы Твоей и вспомнишь обо мне, и не забудешь рабы Твоей и дашь рабе Твоей дитя мужеского пола, то я отдам его Господу на все дни жизни его, и бритва не коснется головы его.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (12566, 'rst66', '1sa', 1, 10, 'И была она в скорби души, и молилась Господу, и горько плакала,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (12565, 'rst66', '1sa', 1, 9, 'И встала Анна после того, как они ели и пили в Силоме. Илий же священник сидел тогда на седалище у входа в храм Господень.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (12564, 'rst66', '1sa', 1, 8, 'И сказал ей Елкана, муж ее: Анна! что ты плачешь и почему не ешь, и отчего скорбит сердце твое? не лучше ли я для тебя десяти сыновей?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (12563, 'rst66', '1sa', 1, 7, 'Так бывало каждый год, когда ходила она в дом Господень; та огорчала ее, а эта плакала и не ела.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (12562, 'rst66', '1sa', 1, 6, 'Соперница ее сильно огорчала ее, побуждая ее к ропоту на то, что Господь заключил чрево ее.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (12561, 'rst66', '1sa', 1, 5, 'Анне же давал часть особую, ибо любил Анну, хотя Господь заключил чрево ее.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (12560, 'rst66', '1sa', 1, 4, 'В тот день, когда Елкана приносил жертву, давал Феннане, жене своей, и всем сыновьям ее и дочерям ее части;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (12559, 'rst66', '1sa', 1, 3, 'И ходил этот человек из города своего в положенные дни поклоняться и приносить жертву Господу Саваофу в Силом; там <i>были Илий и</i> два сына его, Офни и Финеес, священниками Господа.');",
