"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16729, 'rst66', '2ki', 25, 29, 'и переменил темничные одежды его, и он всегда имел пищу у него, во все дни жизни его.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16728, 'rst66', '2ki', 25, 28, 'и говорил с ним дружелюбно, и поставил престол его выше престола царей, которые были у него в Вавилоне;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16727, 'rst66', '2ki', 25, 27, 'В тридцать седьмой год переселения Иехонии, царя Иудейского, в двенадцатый месяц, в двадцать седьмой день месяца, Евилмеродах, царь Вавилонский, в год своего воцарения, вывел Иехонию, царя Иудейского, из дома темничного');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16726, 'rst66', '2ki', 25, 26, 'И встал весь народ, от малого до большого, и военачальники, и пошли в Египет, потому что боялись Халдеев.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16725, 'rst66', '2ki', 25, 25, 'Но в седьмой месяц пришел Исмаил, сын Нефании, сына Елишамы, из племени царского, с десятью человеками, и поразил Годолию, и он умер, и Иудеев и Халдеев, которые были с ним в Массифе.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16724, 'rst66', '2ki', 25, 24, 'И поклялся Годолия им и людям их, и сказал им: не бойтесь быть подвластными Халдеям, селитесь на земле и служите царю Вавилонскому, и будет хорошо вам.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16723, 'rst66', '2ki', 25, 23, 'Когда услышали все военачальники, они и люди их, что царь Вавилонский поставил начальником Годолию, то пришли к Годолии в Массифу, и <i>именно:</i> Исмаил, сын Нефании, и Иоханан, сын Карея, и Сераия, сын Танхумефа из Нетофафа, и Иезания, сын Маахитянина, они и люди их.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16722, 'rst66', '2ki', 25, 22, 'Над народом же, остававшимся в земле Иудейской, который оставил Навуходоносор, царь Вавилонский, &mdash; над ними поставил начальником Годолию, сына Ахикама, сына Шафанова.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16721, 'rst66', '2ki', 25, 21, 'И поразил их царь Вавилонский, и умертвил их в Ривле, в земле Емаф. И выселены Иудеи из земли своей.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16720, 'rst66', '2ki', 25, 20, 'И взял их Навузардан, начальник телохранителей, и отвел их к царю Вавилонскому в Ривлу.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16719, 'rst66', '2ki', 25, 19, 'И из города взял одного евнуха, который был начальствующим над людьми военными, и пять человек, предстоявших лицу царя, которые находились в городе, и писца главного в войске, записывавшего в войско народ земли, и шестьдесят человек из народа земли, находившихся в городе.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16718, 'rst66', '2ki', 25, 18, 'И взял начальник телохранителей Сераию первосвященника и Цефанию, священника второго, и трех, стоявших на страже у порога.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16717, 'rst66', '2ki', 25, 17, 'Восемнадцать локтей вышины в одном столбе; венец на нем медный, а вышина венца три локтя, и сетка и гранатовые яблоки вокруг венца &mdash; все из меди. То же и на другом столбе с сеткою.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16716, 'rst66', '2ki', 25, 16, 'столбы <i>числом</i> два, море одно, и подставы, которые сделал Соломон в дом Господень, &mdash; меди во всех сих вещах не было весу.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16715, 'rst66', '2ki', 25, 15, 'и кадильницы, и чаши, что было золотое и что было серебряное, взял начальник телохранителей:');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16714, 'rst66', '2ki', 25, 14, 'и тазы, и лопатки, и ножи, и ложки, и все сосуды медные, которые употреблялись при служении, взяли;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16713, 'rst66', '2ki', 25, 13, 'И столбы медные, которые были у дома Господня, и подставы, и море медное, которое в доме Господнем, изломали Халдеи, и отнесли медь их в Вавилон;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16712, 'rst66', '2ki', 25, 12, 'Только несколько из бедного народа земли оставил начальник телохранителей работниками в виноградниках и землепашцами.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16711, 'rst66', '2ki', 25, 11, 'И прочий народ, остававшийся в городе, и переметчиков, которые передались царю Вавилонскому, и прочий простой народ выселил Навузардан, начальник телохранителей.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16710, 'rst66', '2ki', 25, 10, 'и стены вокруг Иерусалима разрушило войско Халдейское, бывшее у начальника телохранителей.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16709, 'rst66', '2ki', 25, 9, 'и сжег дом Господень и дом царя, и все домы в Иерусалиме, и все домы большие сожег огнем;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16708, 'rst66', '2ki', 25, 8, 'В пятый месяц, в седьмой день месяца, то есть в девятнадцатый год Навуходоносора, царя Вавилонского, пришел Навузардан, начальник телохранителей, слуга царя Вавилонского, в Иерусалим');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16707, 'rst66', '2ki', 25, 7, 'и сыновей Седекии закололи пред глазами его, а <i>самому</i> Седекии ослепили глаза и сковали его оковами, и отвели его в Вавилон.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16706, 'rst66', '2ki', 25, 6, 'И взяли царя, и отвели его к царю Вавилонскому в Ривлу, и произвели над ним суд:');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16705, 'rst66', '2ki', 25, 5, 'И погналось войско Халдейское за царем, и настигли его на равнинах Иерихонских, и все войско его разбежалось от него.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16704, 'rst66', '2ki', 25, 4, 'И взят был город, и <i>побежали</i> все военные ночью по дороге к воротам, между двумя стенами, что подле царского сада; Халдеи же стояли вокруг города, и <i>царь</i> ушел дорогою к равнине.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16703, 'rst66', '2ki', 25, 3, 'В девятый день месяца усилился голод в городе, и не было хлеба у народа земли.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16702, 'rst66', '2ki', 25, 2, 'И находился город в осаде до одиннадцатого года царя Седекии.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16701, 'rst66', '2ki', 25, 1, 'В девятый год царствования своего, в десятый месяц, в десятый день месяца, пришел Навуходоносор, царь Вавилонский, со всем войском своим к Иерусалиму, и осадил его, и устроил вокруг него вал.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16700, 'rst66', '2ki', 24, 20, 'Гнев Господень был над Иерусалимом и над Иудою до того, что Он отверг их от лица Своего. И отложился Седекия от царя Вавилонского.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16699, 'rst66', '2ki', 24, 19, 'И делал он неугодное в очах Господних во всем так, как делал Иоаким.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16698, 'rst66', '2ki', 24, 18, 'Двадцати одного года был Седекия, когда воцарился, и одиннадцать лет царствовал в Иерусалиме; имя матери его Хамуталь, дочь Иеремии, из Ливны.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16697, 'rst66', '2ki', 24, 17, 'И воцарил царь Вавилонский Матфанию, дядю <i>Иехонии</i>, вместо него, и переменил имя его на Седекию.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16696, 'rst66', '2ki', 24, 16, 'И все войско <i>числом</i> семь тысяч, и художников и строителей тысячу, всех храбрых, ходящих на войну, отвел царь Вавилонский на поселение в Вавилон.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16695, 'rst66', '2ki', 24, 15, 'И переселил он Иехонию в Вавилон; и мать царя, и жен царя, и евнухов его, и сильных земли отвел на поселение из Иерусалима в Вавилон.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16694, 'rst66', '2ki', 24, 14, 'и выселил весь Иерусалим, и всех князей, и все храброе войско, &mdash; десять тысяч было переселенных, &mdash; и всех плотников и кузнецов; никого не осталось, кроме бедного народа земли.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16693, 'rst66', '2ki', 24, 13, 'И вывез он оттуда все сокровища дома Господня и сокровища царского дома; и изломал, как изрек Господь, все золотые сосуды, которые Соломон, царь Израилев, сделал в храме Господнем;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16692, 'rst66', '2ki', 24, 12, 'И вышел Иехония, царь Иудейский, к царю Вавилонскому, он и мать его, и слуги его, и князья его, и евнухи его, &mdash; и взял его царь Вавилонский в восьмой год своего царствования.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16691, 'rst66', '2ki', 24, 11, 'И пришел Навуходоносор, царь Вавилонский, к городу, когда рабы его осаждали его.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16690, 'rst66', '2ki', 24, 10, 'В то время подступили рабы Навуходоносора, царя Вавилонского, к Иерусалиму, и подвергся город осаде.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16689, 'rst66', '2ki', 24, 9, 'И делал он неугодное в очах Господних во всем так, как делал отец его.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16688, 'rst66', '2ki', 24, 8, 'Восемнадцати лет был Иехония, когда воцарился, и три месяца царствовал в Иерусалиме; имя матери его Нехушта, дочь Елнафана, из Иерусалима.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16687, 'rst66', '2ki', 24, 7, 'Царь Египетский не выходил более из земли своей, потому что взял царь Вавилонский все, от потока Египетского до реки Евфрата, что принадлежало царю Египетскому.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16686, 'rst66', '2ki', 24, 6, 'И почил Иоаким с отцами своими, и воцарился Иехония, сын его, вместо него.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16685, 'rst66', '2ki', 24, 5, 'Прочее об Иоакиме и обо всем, что он сделал, написано в летописи царей Иудейских.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16684, 'rst66', '2ki', 24, 4, 'и за кровь невинную, которую он пролил, наполнив Иерусалим кровью невинною, Господь не захотел простить.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16683, 'rst66', '2ki', 24, 3, 'По повелению Господа было <i>это</i> с Иудою, чтобы отвергнуть <i>его</i> от лица Его за грехи Манассии, за всё, что он сделал;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16682, 'rst66', '2ki', 24, 2, 'И посылал на него Господь полчища Халдеев, и полчища Сириян, и полчища Моавитян, и полчища Аммонитян, &mdash; посылал их на Иуду, чтобы погубить его по слову Господа, которое Он изрек чрез рабов Своих пророков.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16681, 'rst66', '2ki', 24, 1, 'Во дни его выступил Навуходоносор, царь Вавилонский, и сделался Иоаким подвластным ему на три года, но потом отложился от него.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16680, 'rst66', '2ki', 23, 37, 'И делал он неугодное в очах Господних во всем так, как делали отцы его.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16679, 'rst66', '2ki', 23, 36, 'Двадцати пяти лет был Иоаким, когда воцарился, и одиннадцать лет царствовал в Иерусалиме; имя матери его Зебудда, дочь Федаии, из Румы.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16678, 'rst66', '2ki', 23, 35, 'И серебро и золото давал Иоаким фараону; он сделал оценку земле, чтобы давать серебро по приказанию фараона; от каждого из народа земли, по оценке своей, он взыскивал серебро и золото для того, чтобы отдавать фараону Нехао.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16677, 'rst66', '2ki', 23, 34, 'И воцарил фараон Нехао Елиакима, сына Иосиина, вместо Иосии, отца его, и переменил имя его на Иоакима; Иоахаза же взял и отвел в Египет, где он и умер.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16676, 'rst66', '2ki', 23, 33, 'И задержал его фараон Нехао в Ривле, в земле Емафской, чтобы он не царствовал в Иерусалиме, &mdash; и наложил пени на землю сто талантов серебра и талант золота.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16675, 'rst66', '2ki', 23, 32, 'И делал он неугодное в очах Господних во всем так, как делали отцы его.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16674, 'rst66', '2ki', 23, 31, 'Двадцати трех лет был Иоахаз, когда воцарился, и три месяца царствовал в Иерусалиме; имя матери его Хамуталь, дочь Иеремии, из Ливны.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16673, 'rst66', '2ki', 23, 30, 'И рабы его повезли его мертвого из Мегиддона, и привезли его в Иерусалим, и похоронили его в гробнице его. И взял народ земли Иоахаза, сына Иосиина, и помазали его и воцарили его вместо отца его.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16672, 'rst66', '2ki', 23, 29, 'Во дни его пошел фараон Нехао, царь Египетский, против царя Ассирийского на реку Евфрат. И вышел царь Иосия навстречу ему, и тот умертвил его в Мегиддоне, когда увидел его.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16671, 'rst66', '2ki', 23, 28, 'Прочее об Иосии и обо всем, что он сделал, написано в летописи царей Иудейских.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16670, 'rst66', '2ki', 23, 27, 'И сказал Господь: и Иуду отрину от лица Моего, как отринул Я Израиля, и отвергну город сей Иерусалим, который Я избрал, и дом, о котором Я сказал: «будет имя Мое там».');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16669, 'rst66', '2ki', 23, 26, 'Однако ж Господь не отложил великой ярости гнева Своего, какою воспылал гнев Его на Иуду за все оскорбления, какими прогневал Его Манассия.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16668, 'rst66', '2ki', 23, 25, 'Подобного ему не было царя прежде его, который обратился бы к Господу всем сердцем своим, и всею душою своею, и всеми силами своими, по всему закону Моисееву; и после него не восстал подобный ему.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16667, 'rst66', '2ki', 23, 24, 'И вызывателей мертвых, и волшебников, и терафимов, и идолов, и все мерзости, которые появлялись в земле Иудейской и в Иерусалиме, истребил Иосия, чтоб исполнить слова закона, написанные в книге, которую нашел Хелкия священник в доме Господнем.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16666, 'rst66', '2ki', 23, 23, 'а в восемнадцатый год царя Иосии была совершена сия пасха Господу в Иерусалиме.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16665, 'rst66', '2ki', 23, 22, 'потому что не была совершена такая пасха от дней судей, которые судили Израиля, и во все дни царей Израильских и царей Иудейских;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16664, 'rst66', '2ki', 23, 21, 'И повелел царь всему народу, сказав: «совершите пасху Господу, Богу вашему, как написано в сей книге завета», &mdash;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16663, 'rst66', '2ki', 23, 20, 'и заколол всех жрецов высот, которые там были, на жертвенниках, и сожег кости человеческие на них, &mdash; и возвратился в Иерусалим.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16662, 'rst66', '2ki', 23, 19, 'Также и все капища высот в городах Самарийских, которые построили цари Израильские, прогневляя <i>Господа</i>, разрушил Иосия, и сделал с ними то же, что сделал в Вефиле;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16661, 'rst66', '2ki', 23, 18, 'И сказал он: оставьте его в покое, никто не трогай костей его. И сохранили кости его и кости пророка, который приходил из Самарии.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16660, 'rst66', '2ki', 23, 17, 'И сказал <i>Иосия:</i> что это за памятник, который я вижу? И сказали ему жители города: <i>это</i> могила человека Божия, который приходил из Иудеи и провозгласил о том, что ты делаешь над жертвенником Вефильским.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16659, 'rst66', '2ki', 23, 16, 'И взглянул Иосия и увидел могилы, которые <i>были</i> там на горе, и послал и взял кости из могил, и сжег на жертвеннике, и осквернил его по слову Господню, которое провозгласил человек Божий, предрекший события сии.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16658, 'rst66', '2ki', 23, 15, 'Также и жертвенник, который в Вефиле, высоту, устроенную Иеровоамом, сыном Наватовым, который ввел Израиля в грех, &mdash; также и жертвенник тот и высоту он разрушил, и сжег сию высоту, стер в прах, и сжег дубраву.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16657, 'rst66', '2ki', 23, 14, 'и изломал статуи, и срубил дубравы, и наполнил место их костями человеческими.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16656, 'rst66', '2ki', 23, 13, 'И высоты, которые пред Иерусалимом, направо от Масличной горы, которые устроил Соломон, царь Израилев, Астарте, мерзости Сидонской, и Хамосу, мерзости Моавитской, и Милхому, мерзости Аммонитской, осквернил царь;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16655, 'rst66', '2ki', 23, 12, 'И жертвенники на кровле горницы Ахазовой, которые сделали цари Иудейские, и жертвенники, которые сделал Манассия на обоих дворах дома Господня, разрушил царь, и низверг оттуда, и бросил прах их в поток Кедрон.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16654, 'rst66', '2ki', 23, 11, 'и отменил коней, которых ставили цари Иудейские солнцу пред входом в дом Господень близ комнат Нефан‐Мелеха евнуха, что в Фаруриме, колесницы же солнца сжег огнем.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16653, 'rst66', '2ki', 23, 10, 'И осквернил он Тофет, что на долине сыновей Еннома, чтобы никто не проводил сына своего и дочери своей чрез огонь Молоху;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16652, 'rst66', '2ki', 23, 9, 'Впрочем, жрецы высот не приносили жертв на жертвеннике Господнем в Иерусалиме, опресноки же ели вместе с братьями своими.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16651, 'rst66', '2ki', 23, 8, 'и вывел всех жрецов из городов Иудейских, и осквернил высоты, на которых совершали курения жрецы, от Гевы до Вирсавии, и разрушил высоты <i>пред</i> воротами, &mdash; ту, которая у входа в ворота Иисуса градоначальника, и ту, которая на левой стороне у городских ворот.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16650, 'rst66', '2ki', 23, 7, 'и разрушил домы блудилищные, которые <i>были</i> при храме Господнем, где женщины ткали одежды для Астарты;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16649, 'rst66', '2ki', 23, 6, 'и вынес Астарту из дома Господня за Иерусалим к потоку Кедрону, и сжег ее у потока Кедрона, и истер ее в прах, и бросил прах ее на кладбище общенародное;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16648, 'rst66', '2ki', 23, 5, 'И отставил жрецов, которых поставили цари Иудейские, чтобы совершать курения на высотах в городах Иудейских и окрестностях Иерусалима, &mdash; и которые кадили Ваалу, солнцу, и луне, и созвездиям, и всему воинству небесному;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16647, 'rst66', '2ki', 23, 4, 'И повелел царь Хелкии первосвященнику и вторым священникам и стоящим на страже у порога вынести из храма Господня все вещи, сделанные для Ваала и для Астарты и для всего воинства небесного, и сжег их за Иерусалимом в долине Кедрон, и <i>велел</i> прах их отнести в Вефиль.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16646, 'rst66', '2ki', 23, 3, 'Потом стал царь на возвышенное место и заключил пред лицом Господним завет &mdash; последовать Господу и соблюдать заповеди Его и откровения Его и уставы Его от всего сердца и от всей души, чтобы выполнить слова завета сего, написанные в книге сей. И весь народ вступил в завет.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16645, 'rst66', '2ki', 23, 2, 'И пошел царь в дом Господень, и все Иудеи, и все жители Иерусалима с ним, и священники, и пророки, и весь народ, от малого до большого, и прочел вслух их все слова книги завета, найденной в доме Господнем.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16644, 'rst66', '2ki', 23, 1, 'И послал царь, и собрали к нему всех старейшин Иуды и Иерусалима.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16643, 'rst66', '2ki', 22, 20, 'За это, вот, Я приложу тебя к отцам твоим, и ты положен будешь в гробницу твою в мире, и не увидят глаза твои всего того бедствия, которое Я наведу на место сие. И принесли царю ответ.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16642, 'rst66', '2ki', 22, 19, 'так как смягчилось сердце твое, и ты смирился пред Господом, услышав то, что Я изрек на место сие и на жителей его, что они будут предметом ужаса и проклятия, и ты разодрал одежды свои, и плакал предо Мною, то и Я услышал тебя, говорит Господь.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16641, 'rst66', '2ki', 22, 18, 'А царю Иудейскому, пославшему вас вопросить Господа, скажите: так говорит Господь, Бог Израилев, о словах, которые ты слышал:');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16640, 'rst66', '2ki', 22, 17, 'За то, что оставили Меня, и кадят другим богам, чтобы раздражать Меня всеми делами рук своих, воспылал гнев Мой на место сие, и не погаснет.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16639, 'rst66', '2ki', 22, 16, 'так говорит Господь: наведу зло на место сие и на жителей его, &mdash; все слова книги, которую читал царь Иудейский.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16638, 'rst66', '2ki', 22, 15, 'И она сказала им: так говорит Господь, Бог Израилев: скажите человеку, который послал вас ко мне:');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16637, 'rst66', '2ki', 22, 14, 'И пошел Хелкия священник, и Ахикам, и Ахбор, и Шафан, и Асаия к Олдаме пророчице, жене Шаллума, сына Тиквы, сына Хархаса, хранителя одежд, &mdash; жила же она в Иерусалиме, во второй части, &mdash; и говорили с нею.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16636, 'rst66', '2ki', 22, 13, 'пойдите, вопросите Господа за меня и за народ и за всю Иудею о словах сей найденной книги, потому что велик гнев Господень, который воспылал на нас за то, что не слушали отцы наши слов книги сей, чтобы поступать согласно с предписанным нам.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16635, 'rst66', '2ki', 22, 12, 'И повелел царь Хелкии священнику, и Ахикаму, сыну Шафанову, и Ахбору, сыну Михеину, и Шафану писцу, и Асаии, слуге царскому, говоря:');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16634, 'rst66', '2ki', 22, 11, 'Когда услышал царь слова книги закона, то разодрал одежды свои.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16633, 'rst66', '2ki', 22, 10, 'И донес Шафан писец царю, говоря: книгу дал мне Хелкия священник. И читал ее Шафан пред царем.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16632, 'rst66', '2ki', 22, 9, 'И пришел Шафан писец к царю, и принес царю ответ, и сказал: взяли рабы твои серебро, найденное в доме, и передали его в руки производителям работ, приставленным к дому Господню.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16631, 'rst66', '2ki', 22, 8, 'И сказал Хелкия первосвященник Шафану писцу: книгу закона я нашел в доме Господнем. И подал Хелкия книгу Шафану, и он читал ее.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16630, 'rst66', '2ki', 22, 7, 'впрочем, не требовать у них отчета в серебре, переданном в руки их, потому что они поступают честно.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16629, 'rst66', '2ki', 22, 6, 'плотникам и каменщикам, и делателям стен, и на покупку дерев и тесаных камней для исправления дома;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16628, 'rst66', '2ki', 22, 5, 'и пусть отдадут его в руки производителям работ, приставленным к дому Господню, а сии пусть раздают его работающим в доме Господнем, на исправление повреждений дома,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16627, 'rst66', '2ki', 22, 4, 'пойди к Хелкии первосвященнику, пусть он пересчитает серебро, принесенное в дом Господень, которое собрали от народа стоящие на страже у порога,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16626, 'rst66', '2ki', 22, 3, 'В восемнадцатый год царя Иосии послал царь Шафана, сына Ацалии, сына Мешулламова, писца, в дом Господень, сказав:');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16625, 'rst66', '2ki', 22, 2, 'И делал он угодное в очах Господних, и ходил во всем путем Давида, отца своего, и не уклонялся ни направо, ни налево.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16624, 'rst66', '2ki', 22, 1, 'Восьми лет был Иосия, когда воцарился, и тридцать один год царствовал в Иерусалиме; имя матери его Иедида, дочь Адаии, из Боцкафы.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16623, 'rst66', '2ki', 21, 26, 'И похоронили его в гробнице его, в саду Уззы. И воцарился Иосия, сын его, вместо него.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16622, 'rst66', '2ki', 21, 25, 'Прочее об Аммоне, что он сделал, написано в летописи царей Иудейских.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16621, 'rst66', '2ki', 21, 24, 'Но народ земли перебил всех, бывших в заговоре против царя Аммона; и воцарил народ земли Иосию, сына его, вместо него.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16620, 'rst66', '2ki', 21, 23, 'И составили заговор слуги Аммоновы против него, и умертвили царя в доме его.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16619, 'rst66', '2ki', 21, 22, 'и оставил Господа, Бога отцов своих, не ходил путем Господним.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16618, 'rst66', '2ki', 21, 21, 'и ходил тою же точно дорогою, которою ходил отец его, и служил идолам, которым служил отец его, и поклонялся им,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16617, 'rst66', '2ki', 21, 20, 'И делал он неугодное в очах Господних так, как делал Манассия, отец его;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16616, 'rst66', '2ki', 21, 19, 'Двадцати двух лет был Аммон, когда воцарился, и два года царствовал в Иерусалиме; имя матери его Мешуллемеф, дочь Харуца, из Ятбы.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16615, 'rst66', '2ki', 21, 18, 'И почил Манассия с отцами своими, и погребен в саду при доме его, в саду Уззы. И воцарился Аммон, сын его, вместо него.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16614, 'rst66', '2ki', 21, 17, 'Прочее о Манассии и обо всем, что он сделал, и о грехах его, в чем он согрешил, написано в летописи царей Иудейских.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16613, 'rst66', '2ki', 21, 16, 'Еще же пролил Манассия и весьма много невинной крови, так что наполнил <i>ею</i> Иерусалим от края до края, сверх своего греха, что он завлек Иуду в грех &mdash; делать неугодное в очах Господних.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16612, 'rst66', '2ki', 21, 15, 'за то, что они делали неугодное в очах Моих и прогневляли Меня с того дня, как вышли отцы их из Египта, и до сего дня.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16611, 'rst66', '2ki', 21, 14, 'и отвергну остаток удела Моего, и отдам их в руку врагов их, и будут на расхищение и разграбление всем неприятелям своим,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16610, 'rst66', '2ki', 21, 13, 'и протяну на Иерусалим мерную вервь Самарии и отвес дома Ахавова, и вытру Иерусалим так, как вытирают чашу, &mdash; вытрут и опрокинут ее;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16609, 'rst66', '2ki', 21, 12, 'за то, так говорит Господь, Бог Израилев, вот, Я наведу такое зло на Иерусалим и на Иуду, о котором кто услышит, зазвенит в обоих ушах у того;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16608, 'rst66', '2ki', 21, 11, 'за то, что сделал Манассия, царь Иудейский, такие мерзости, хуже всего того, что делали Аморреи, которые были прежде него, и ввел Иуду в грех идолами своими,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16607, 'rst66', '2ki', 21, 10, 'И говорил Господь чрез рабов Своих пророков и сказал:');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16606, 'rst66', '2ki', 21, 9, 'Но они не послушались; и совратил их Манассия до того, что они поступали хуже тех народов, которых истребил Господь от лица сынов Израилевых.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16605, 'rst66', '2ki', 21, 8, 'и не дам впредь выступить ноге Израильтянина из земли, которую Я дал отцам их, если только они будут стараться поступать согласно со всем тем, что Я повелел им, и со всем законом, который заповедал им раб Мой Моисей».');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16604, 'rst66', '2ki', 21, 7, 'И поставил истукан Астарты, который сделал в доме, о котором говорил Господь Давиду и Соломону, сыну его: «в доме сем и в Иерусалиме, который Я избрал из всех колен Израилевых, Я полагаю имя Мое навек;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16603, 'rst66', '2ki', 21, 6, 'и провел сына своего чрез огонь, и гадал, и ворожил, и завел вызывателей мертвецов и волшебников; много сделал неугодного в очах Господа, чтобы прогневать Его.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16602, 'rst66', '2ki', 21, 5, 'И соорудил жертвенники всему воинству небесному на обоих дворах дома Господня,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16601, 'rst66', '2ki', 21, 4, 'И соорудил жертвенники в доме Господнем, о котором сказал Господь: «в Иерусалиме положу имя Мое».');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16600, 'rst66', '2ki', 21, 3, 'И снова устроил высоты, которые уничтожил отец его Езекия, и поставил жертвенники Ваалу, и сделал дубраву, как сделал Ахав, царь Израильский; и поклонялся всему воинству небесному, и служил ему.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16599, 'rst66', '2ki', 21, 2, 'И делал он неугодное в очах Господних, <i>подражая</i> мерзостям народов, которых прогнал Господь от лица сынов Израилевых.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16598, 'rst66', '2ki', 21, 1, 'Двенадцати лет был Манассия, когда воцарился, и пятьдесят лет царствовал в Иерусалиме; имя матери его Хефциба.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16597, 'rst66', '2ki', 20, 21, 'И почил Езекия с отцами своими, и воцарился Манассия, сын его, вместо него.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16596, 'rst66', '2ki', 20, 20, 'Прочее об Езекии и о всех подвигах его, и о том, что он сделал пруд и водопровод и провел воду в город, написано в летописи царей Иудейских.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16595, 'rst66', '2ki', 20, 19, 'И сказал Езекия Исаии: благо слово Господне, которое ты изрек. И продолжал: да будет мир и благосостояние во дни мои!');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16594, 'rst66', '2ki', 20, 18, 'Из сынов твоих, которые произойдут от тебя, которых ты родишь, возьмут, и будут они евнухами во дворце царя Вавилонского.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16593, 'rst66', '2ki', 20, 17, 'вот, придут дни, и взято будет все, что в доме твоем, и что собрали отцы твои до сего дня, в Вавилон; ничего не останется, говорит Господь.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16592, 'rst66', '2ki', 20, 16, 'И сказал Исаия Езекии: выслушай слово Господне:');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16591, 'rst66', '2ki', 20, 15, 'И сказал <i>Исаия:</i> что они видели в доме твоем? И сказал Езекия: все, что в доме моем, они видели, не осталось ни одной вещи, которой я не показал бы им в сокровищницах моих.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16590, 'rst66', '2ki', 20, 14, 'И пришел Исаия пророк к царю Езекии и сказал ему: что говорили эти люди, и откуда они приходили к тебе? И сказал Езекия: из земли далекой они приходили, из Вавилона.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16589, 'rst66', '2ki', 20, 13, 'Езекия, выслушав посланных, показал им кладовые свои, серебро и золото, и ароматы, и масти дорогие, и весь оружейный дом свой и все, что находилось в сокровищницах его; не оставалось ни одной вещи, которой не показал бы им Езекия в доме своем и во всем владении своем.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16588, 'rst66', '2ki', 20, 12, 'В то время послал Беродах Баладан, сын Баладана, царь Вавилонский, письма и подарок Езекии, ибо он слышал, что Езекия был болен.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16587, 'rst66', '2ki', 20, 11, 'И воззвал Исаия пророк к Господу, и возвратил тень назад на ступенях, где она спускалась по ступеням Ахазовым, на десять ступеней.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16586, 'rst66', '2ki', 20, 10, 'И сказал Езекия: легко тени подвинуться вперед на десять ступеней; нет, пусть воротится тень назад на десять ступеней.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16585, 'rst66', '2ki', 20, 9, 'И сказал Исаия: вот тебе знамение от Господа, что исполнит Господь слово, которое Он изрек: вперед ли пройти тени на десять ступеней, или воротиться на десять ступеней?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16584, 'rst66', '2ki', 20, 8, 'И сказал Езекия Исаии: какое знамение, что Господь исцелит меня, и что пойду я на третий день в дом Господень?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16583, 'rst66', '2ki', 20, 7, 'И сказал Исаия: возьмите пласт смокв. И взяли, и приложили к нарыву; и он выздоровел.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16582, 'rst66', '2ki', 20, 6, 'и прибавлю ко дням твоим пятнадцать лет, и от руки царя Ассирийского спасу тебя и город сей, и защищу город сей ради Себя и ради Давида, раба Моего.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16581, 'rst66', '2ki', 20, 5, 'возвратись и скажи Езекии, владыке народа Моего: так говорит Господь, Бог Давида, отца твоего: Я услышал молитву твою, увидел слезы твои. Вот, Я исцелю тебя; в третий день пойдешь в дом Господень;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16580, 'rst66', '2ki', 20, 4, 'Исаия еще не вышел из города, как было к нему слово Господне:');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16579, 'rst66', '2ki', 20, 3, '«О, Господи! вспомни, что я ходил пред лицом Твоим верно и с преданным <i>Тебе</i> сердцем, и делал угодное в очах Твоих». И заплакал Езекия сильно.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16578, 'rst66', '2ki', 20, 2, 'И отворотился <i>Езекия</i> лицом своим к стене и молился Господу, говоря:');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16577, 'rst66', '2ki', 20, 1, 'В те дни заболел Езекия смертельно, и пришел к нему Исаия, сын Амосов, пророк, и сказал ему: так говорит Господь: «сделай завещание для дома твоего, ибо умрешь ты и не выздоровеешь».');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16576, 'rst66', '2ki', 19, 37, 'И когда он поклонялся в доме Нисроха, бога своего, то Адрамелех и Шарецер, сыновья его, убили его мечом, а сами убежали в землю Араратскую. И воцарился Асардан, сын его, вместо него.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16575, 'rst66', '2ki', 19, 36, 'И отправился, и пошел, и возвратился Сеннахирим, царь Ассирийский, и жил в Ниневии.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16574, 'rst66', '2ki', 19, 35, 'И случилось в ту ночь: пошел Ангел Господень и поразил в стане Ассирийском сто восемьдесят пять тысяч. И встали поутру, и вот, все тела мертвые.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16573, 'rst66', '2ki', 19, 34, 'Я буду охранять город сей, чтобы спасти его ради Себя и ради Давида, раба Моего.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16572, 'rst66', '2ki', 19, 33, 'Тою же дорогою, которою пришел, возвратится, и в город сей не войдет, говорит Господь.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16571, 'rst66', '2ki', 19, 32, 'Посему так говорит Господь о царе Ассирийском: не войдет он в сей город, и не бросит туда стрелы, и не приступит к нему со щитом, и не насыплет против него вала.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16570, 'rst66', '2ki', 19, 31, 'ибо из Иерусалима произойдет остаток, и спасенное от горы Сиона. Ревность Господа Саваофа сделает сие.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16569, 'rst66', '2ki', 19, 30, 'И уцелевшее в доме Иудином, оставшееся, пустит опять корень внизу и принесет плод вверху,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16568, 'rst66', '2ki', 19, 29, 'И вот тебе, <i>Езекия,</i> знамение: ешьте в этот год выросшее от упавшего зерна, и в другой год &mdash; самородное, а на третий год сейте и жните, и садите виноградные сады и ешьте плоды их.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16567, 'rst66', '2ki', 19, 28, 'За твою дерзость против Меня и <i>за то, что</i> надмение твое дошло до ушей Моих, Я вложу кольцо Мое в ноздри твои и удила Мои в рот твой, и возвращу тебя назад тою же дорогою, которою пришел ты.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16566, 'rst66', '2ki', 19, 27, 'Сядешь ли ты, выйдешь ли, войдешь ли, Я все знаю; <i>знаю</i> и дерзость твою против Меня.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16565, 'rst66', '2ki', 19, 26, 'И жители их сделались маломощны, трепещут и остаются в стыде. Они стали <i>как</i> трава на поле и нежная зелень, <i>как</i> порост на кровлях и опаленный хлеб, прежде нежели выколосился.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16564, 'rst66', '2ki', 19, 25, 'Разве ты не слышал, что Я издавна сделал это, в древние дни предначертал это, а ныне выполнил тем, что ты опустошаешь укрепленные города, <i>превращая</i> в груды развалин?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16563, 'rst66', '2ki', 19, 24, 'и откапывал я и пил воду чужую, и осушу ступнями ног моих все реки Египетские».');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16562, 'rst66', '2ki', 19, 23, 'Чрез послов твоих ты порицал Господа и сказал: «со множеством колесниц моих я взошел на высоту гор, на ребра Ливана, и срубил рослые кедры его, отличные кипарисы его, и пришел на самое крайнее пристанище его, в рощу сада его;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16561, 'rst66', '2ki', 19, 22, 'Кого ты порицал и поносил? И на кого ты возвысил голос и поднял так высоко глаза свои? На Святого Израилева!');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16560, 'rst66', '2ki', 19, 21, 'Вот слово, которое изрек Господь о нем: презрит тебя, посмеется над тобою девствующая дочь Сиона; вслед тебя покачает головою дочь Иерусалима.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16559, 'rst66', '2ki', 19, 20, 'И послал Исаия, сын Амосов, к Езекии сказать: так говорит Господь, Бог Израилев: то, о чем ты молился Мне против Сеннахирима, царя Ассирийского, Я услышал.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16558, 'rst66', '2ki', 19, 19, 'И ныне, Господи, Боже наш, спаси нас от руки его, и узнают все царства земли, что Ты, Господи, Бог один.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16557, 'rst66', '2ki', 19, 18, 'и побросали богов их в огонь; но это не боги, а изделие рук человеческих, дерево и камень; потому и истребили их.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16556, 'rst66', '2ki', 19, 17, 'Правда, о, Господи, цари Ассирийские разорили народы и земли их,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16555, 'rst66', '2ki', 19, 16, 'Приклони, Господи, ухо Твое и услышь; открой, Господи, очи Твои и воззри, и услышь слова Сеннахирима, который послал поносить Бога живого!');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16554, 'rst66', '2ki', 19, 15, 'и молился Езекия пред лицом Господним и говорил: Господи, Боже Израилев, сидящий на херувимах! Ты один Бог всех царств земли, Ты сотворил небо и землю.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16553, 'rst66', '2ki', 19, 14, 'И взял Езекия письмо из руки послов, и прочитал его, и пошел в дом Господень, и развернул его Езекия пред лицом Господним,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16552, 'rst66', '2ki', 19, 13, 'Где царь Емафа, и царь Арпада, и царь города Сепарваима, Ены и Иввы?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16551, 'rst66', '2ki', 19, 12, 'Боги народов, которых разорили отцы мои, спасли ли их? <i>Спасли ли</i> Гозан, и Харан, и Рецеф, и сынов Едена, что в Фалассаре?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16550, 'rst66', '2ki', 19, 11, 'Ведь ты слышал, что сделали цари Ассирийские со всеми землями, положив на них заклятие, &mdash; и ты ли уцелеешь?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16549, 'rst66', '2ki', 19, 10, 'так скажите Езекии, царю Иудейскому: пусть не обманывает тебя Бог твой, на Которого ты уповаешь, думая: «не будет отдан Иерусалим в руки царя Ассирийского».');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16548, 'rst66', '2ki', 19, 9, 'И услышал он о Тиргаке, царе Ефиопском; ему сказали: вот, он вышел сразиться с тобою. И снова послал он послов к Езекии сказать:');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16547, 'rst66', '2ki', 19, 8, 'И возвратился Рабсак, и нашел царя Ассирийского воюющим против Ливны, ибо он слышал, что тот отошел от Лахиса.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16546, 'rst66', '2ki', 19, 7, 'Вот, Я пошлю в него дух, и он услышит весть, и возвратится в землю свою, и Я поражу его мечом в земле его.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16545, 'rst66', '2ki', 19, 6, 'и сказал им Исаия: так скажите господину вашему: так говорит Господь: не бойся слов, которые ты слышал, которыми поносили Меня слуги царя Ассирийского.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16544, 'rst66', '2ki', 19, 5, 'И пришли слуги царя Езекии к Исаии,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16543, 'rst66', '2ki', 19, 4, 'Может быть, услышит Господь, Бог твой, все слова Рабсака, которого послал царь Ассирийский, господин его, хулить Бога живого и поносить словами, какие слышал Господь, Бог твой. Принеси же молитву об оставшихся, которые находятся еще в живых.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16542, 'rst66', '2ki', 19, 3, 'И они сказали ему: так говорит Езекия: день скорби и наказания и посрамления &mdash; день сей; ибо дошли младенцы до отверстия утробы матерней, а силы нет родить.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16541, 'rst66', '2ki', 19, 2, 'И послал Елиакима, начальника дворца, и Севну писца, и старших священников, покрытых вретищами, к Исаии пророку, сыну Амосову.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16540, 'rst66', '2ki', 19, 1, 'Когда услышал <i>это</i> царь Езекия, то разодрал одежды свои и покрылся вретищем, и пошел в дом Господень.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16539, 'rst66', '2ki', 18, 37, 'И пришел Елиаким, сын Хелкиин, начальник дворца, и Севна писец и Иоах, сын Асафов, дееписатель, к Езекии в разодранных одеждах, и пересказали ему слова Рабсаковы.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16538, 'rst66', '2ki', 18, 36, 'И молчал народ, и не отвечали ему ни слова, потому что было приказание царя: «не отвечайте ему».');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16537, 'rst66', '2ki', 18, 35, 'Кто из всех богов земель сих спас землю свою от руки моей? Так неужели Господь спасет Иерусалим от руки моей?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16536, 'rst66', '2ki', 18, 34, 'Где боги Емафа и Арпада? Где боги Сепарваима, Ены и Иввы? Спасли ли они Самарию от руки моей?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16535, 'rst66', '2ki', 18, 33, 'Спасли ли боги народов, каждый свою землю, от руки царя Ассирийского?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16534, 'rst66', '2ki', 18, 32, 'пока я не приду и не возьму вас в землю такую же, как и ваша земля, в землю хлеба и вина, в землю плодов и виноградников, в землю масличных дерев и меда, и будете жить, и не умрете. Не слушайте же Езекии, который обольщает вас, говоря: «Господь спасет нас».');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16533, 'rst66', '2ki', 18, 31, 'Не слушайте Езекии. Ибо так говорит царь Ассирийский: примиритесь со мною и выйдите ко мне, и пусть каждый ест <i>плоды</i> виноградной лозы своей и смоковницы своей, и пусть каждый пьет воду из своего колодезя,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16532, 'rst66', '2ki', 18, 30, 'и пусть не обнадеживает вас Езекия Господом, говоря: «спасет нас Господь, и не будет город сей отдан в руки царя Ассирийского».');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16531, 'rst66', '2ki', 18, 29, 'Так говорит царь: пусть не обольщает вас Езекия, ибо он не может вас спасти от руки моей;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16530, 'rst66', '2ki', 18, 28, 'И встал Рабсак и возгласил громким голосом по‐иудейски, и говорил, и сказал: слушайте слово царя великого, царя Ассирийского!');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16529, 'rst66', '2ki', 18, 27, 'И сказал им Рабсак: разве <i>только</i> к господину твоему и к тебе послал меня господин мой сказать сии слова? Нет, также и к людям, которые сидят на стене, чтобы есть помет свой и пить мочу свою с вами.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16528, 'rst66', '2ki', 18, 26, 'И сказал Елиаким, сын Хелкиин, и Севна и Иоах Рабсаку: говори рабам твоим по‐арамейски, потому что понимаем мы, а не говори с нами по‐иудейски вслух народа, который на стене.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16527, 'rst66', '2ki', 18, 25, 'Притом же разве я без воли Господней пошел на место сие, чтобы разорить его? Господь сказал мне: «пойди на землю сию и разори ее».');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16526, 'rst66', '2ki', 18, 24, 'Как тебе одолеть и одного вождя из малейших слуг господина моего? И уповаешь на Египет ради колесниц и коней?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16525, 'rst66', '2ki', 18, 23, 'Итак, вступи в союз с господином моим, царем Ассирийским: я дам тебе две тысячи коней, можешь ли достать себе всадников на них?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16524, 'rst66', '2ki', 18, 22, 'А если вы скажете мне: «на Господа, Бога нашего, мы уповаем», то на того ли, которого высоты и жертвенники отменил Езекия, и сказал Иуде и Иерусалиму: «пред сим только жертвенником поклоняйтесь в Иерусалиме»?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16523, 'rst66', '2ki', 18, 21, 'Вот, ты думаешь опереться на Египет, на эту трость надломленную, которая, если кто опрется на нее, войдет ему в руку и проколет ее. Таков фараон, царь Египетский, для всех уповающих на него.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16522, 'rst66', '2ki', 18, 20, 'Ты говорил только пустые слова: для войны нужны совет и сила. Ныне же на кого ты уповаешь, что отложился от меня?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16521, 'rst66', '2ki', 18, 19, 'И сказал им Рабсак: скажите Езекии: так говорит царь великий, царь Ассирийский: что это за упование, на которое ты уповаешь?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16520, 'rst66', '2ki', 18, 18, 'И звали они царя. И вышел к ним Елиаким, сын Хелкиин, начальник дворца, и Севна писец, и Иоах, сын Асафов, дееписатель.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16519, 'rst66', '2ki', 18, 17, 'И послал царь Ассирийский Тартана и Рабсариса и Рабсака из Лахиса к царю Езекии с большим войском в Иерусалим. И пошли, и пришли к Иерусалиму; и пошли, и пришли, и стали у водопровода верхнего пруда, который на дороге поля белильничьего.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16518, 'rst66', '2ki', 18, 16, 'В то время снял Езекия <i>золото</i> с дверей дома Господня и с дверных столбов, которые позолотил Езекия, царь Иудейский, и отдал его царю Ассирийскому.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16517, 'rst66', '2ki', 18, 15, 'И отдал Езекия все серебро, какое нашлось в доме Господнем и в сокровищницах дома царского.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16516, 'rst66', '2ki', 18, 14, 'И послал Езекия, царь Иудейский, к царю Ассирийскому в Лахис сказать: виновен я; отойди от меня; что наложишь на меня, я внесу. И наложил царь Ассирийский на Езекию, царя Иудейского, триста талантов серебра и тридцать талантов золота.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16515, 'rst66', '2ki', 18, 13, 'В четырнадцатый год царя Езекии пошел Сеннахирим, царь Ассирийский, против всех укрепленных городов Иуды и взял их.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16514, 'rst66', '2ki', 18, 12, 'за то, что они не слушали гласа Господа, Бога своего, и преступили завет Его, всё, что заповедал Моисей, раб Господень, они и не слушали и не исполняли.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16513, 'rst66', '2ki', 18, 11, 'И переселил царь Ассирийский Израильтян в Ассирию, и поселил их в Халахе и в Хаворе, при реке Гозан, и в городах Мидийских,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16512, 'rst66', '2ki', 18, 10, 'и взял ее через три года; в шестой год Езекии, то есть в девятый год Осии, царя Израильского, взята Самария.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16511, 'rst66', '2ki', 18, 9, 'В четвертый год царя Езекии, то есть в седьмой год Осии, сына Илы, царя Израильского, пошел Салманассар, царь Ассирийский, на Самарию, и осадил ее,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16510, 'rst66', '2ki', 18, 8, 'Он поразил Филистимлян до Газы и в пределах ее, от сторожевой башни до укрепленного города.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16509, 'rst66', '2ki', 18, 7, 'И был Господь с ним: везде, куда он ни ходил, поступал он благоразумно. И отложился он от царя Ассирийского, и не стал служить ему.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16508, 'rst66', '2ki', 18, 6, 'И прилепился он к Господу и не отступал от Него, и соблюдал заповеди Его, какие заповедал Господь Моисею.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16507, 'rst66', '2ki', 18, 5, 'На Господа, Бога Израилева, уповал он; и такого, как он, не было между всеми царями Иудейскими и после него и прежде него.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16506, 'rst66', '2ki', 18, 4, 'он отменил высоты, разбил статуи, срубил дубраву и истребил медного змея, которого сделал Моисей, потому что до самых тех дней сыны Израилевы кадили ему и называли его Нехуштан.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16505, 'rst66', '2ki', 18, 3, 'И делал он угодное в очах Господних во всем так, как делал Давид, отец его;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16504, 'rst66', '2ki', 18, 2, 'Двадцати пяти лет был он, когда воцарился, и двадцать девять лет царствовал в Иерусалиме; имя матери его Ави, дочь Захарии.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16503, 'rst66', '2ki', 18, 1, 'В третий год Осии, сына Илы, царя Израильского, воцарился Езекия, сын Ахаза, царя Иудейского.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16502, 'rst66', '2ki', 17, 41, 'Народы сии чтили Господа, но и истуканам своим служили. Да и дети их и дети детей их до сего дня поступают так же, как поступали отцы их.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16501, 'rst66', '2ki', 17, 40, 'Но они не послушали, а поступали по прежним своим обычаям.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16500, 'rst66', '2ki', 17, 39, 'только Господа, Бога вашего, чтите, и Он избавит вас от руки всех врагов ваших.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16499, 'rst66', '2ki', 17, 38, 'и завета, который Я заключил с вами, не забывайте, и не чтите богов иных,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16498, 'rst66', '2ki', 17, 37, 'и уставы, и учреждения, и закон, и заповеди, которые Он написал вам, старайтесь исполнять во все дни, и не чтите богов иных;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16497, 'rst66', '2ki', 17, 36, 'но Господа, Который вывел вас из земли Египетской силою великою и мышцею простертою, &mdash; Его чтите и Ему поклоняйтесь, и Ему приносите жертвы,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16496, 'rst66', '2ki', 17, 35, 'Заключил Господь с ними завет и заповедал им, говоря: не чтите богов иных, и не поклоняйтесь им, и не служите им, и не приносите жертв им,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16495, 'rst66', '2ki', 17, 34, 'До сего дня поступают они по прежним своим обычаям: не боятся Господа и не поступают по уставам и по обрядам, и по закону и по заповедям, которые заповедал Господь сынам Иакова, которому дал Он имя Израиля.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16494, 'rst66', '2ki', 17, 33, 'Господа они чтили, и богам своим они служили по обычаю народов, из которых выселили их.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16493, 'rst66', '2ki', 17, 32, 'Между тем чтили и Господа, и сделали у себя священников высот из среды своей, и они служили у них в капищах высот.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16492, 'rst66', '2ki', 17, 31, 'Аввийцы сделали Нивхаза и Тартака, а Сепарваимцы сожигали сыновей своих в огне Адрамелеху и Анамелеху, богам Сепарваимским.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16491, 'rst66', '2ki', 17, 30, 'Вавилоняне сделали Суккот‐Беноф, Кутийцы сделали Нергала, Емафяне сделали Ашиму,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16490, 'rst66', '2ki', 17, 29, 'Притом сделал каждый народ и своих богов и поставил в капищах высот, какие устроили Самаряне, &mdash; каждый народ в своих городах, где живут они.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16489, 'rst66', '2ki', 17, 28, 'И пришел один из священников, которых выселили из Самарии, и жил в Вефиле, и учил их, как чтить Господа.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16488, 'rst66', '2ki', 17, 27, 'И повелел царь Ассирийский, и сказал: отправьте туда одного из священников, которых вы выселили оттуда; пусть пойдет и живет там, и он научит их закону Бога той земли.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16487, 'rst66', '2ki', 17, 26, 'И донесли царю Ассирийскому, и сказали: народы, которых ты переселил и поселил в городах Самарийских, не знают закона Бога той земли, и за то Он посылает на них львов, и вот, они умерщвляют их, потому что они не знают закона Бога той земли.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16486, 'rst66', '2ki', 17, 25, 'И как в начале жительства своего там они не чтили Господа, то Господь посылал на них львов, которые умерщвляли их.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16485, 'rst66', '2ki', 17, 24, 'И перевел царь Ассирийский людей из Вавилона, и из Куты, и из Аввы, и из Емафа, и из Сепарваима, и поселил <i>их</i> в городах Самарийских вместо сынов Израилевых. И они овладели Самариею, и стали жить в городах ее.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16484, 'rst66', '2ki', 17, 23, 'доколе Господь не отверг Израиля от лица Своего, как говорил чрез всех рабов Своих, пророков. И переселен Израиль из земли своей в Ассирию, где он и до сего дня.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16483, 'rst66', '2ki', 17, 22, 'И поступали сыны Израилевы по всем грехам Иеровоама, какие он делал, не отставали от них,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16482, 'rst66', '2ki', 17, 21, 'Израильтяне отторглись от дома Давидова и воцарили Иеровоама, сына Наватова; и отклонил Иеровоам Израильтян от Господа, и вовлек их в великий грех.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16481, 'rst66', '2ki', 17, 20, 'И отвратился Господь от всех потомков Израиля, и смирил их, и отдавал их в руки грабителям, и наконец отверг их от лица Своего.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16480, 'rst66', '2ki', 17, 19, 'И Иуда также не соблюдал заповедей Господа, Бога своего, и поступал по обычаям Израильтян, как поступали они.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16479, 'rst66', '2ki', 17, 18, 'И прогневался Господь сильно на Израильтян, и отверг их от лица Своего. Не осталось никого, кроме одного колена Иудина.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16478, 'rst66', '2ki', 17, 17, 'и проводили сыновей своих и дочерей своих чрез огонь, и гадали, и волшебствовали, и предались тому, чтобы делать неугодное в очах Господа и прогневлять Его.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16477, 'rst66', '2ki', 17, 16, 'и оставили все заповеди Господа, Бога своего, и сделали себе литые изображения двух тельцов, и устроили дубраву, и поклонялись всему воинству небесному, и служили Ваалу,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16476, 'rst66', '2ki', 17, 15, 'и презирали уставы Его, и завет Его, который Он заключил с отцами их, и откровения Его, какими Он предостерегал их, и пошли вслед суеты и осуетились, и вслед народов окрестных, о которых Господь заповедал им, чтобы не поступали так, как они,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16475, 'rst66', '2ki', 17, 14, 'Но они не слушали и ожесточили выю свою, как была выя отцов их, которые не веровали в Господа, Бога своего;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16474, 'rst66', '2ki', 17, 13, 'Тогда Господь чрез всех пророков Своих, чрез всякого прозорливца предостерегал Израиля и Иуду, говоря: возвратитесь со злых путей ваших и соблюдайте заповеди Мои, уставы Мои, по всему учению, которое Я заповедал отцам вашим и которое Я преподал вам чрез рабов Моих, пророков.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16473, 'rst66', '2ki', 17, 12, 'и служили идолам, о которых говорил им Господь: «не делайте сего»;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16472, 'rst66', '2ki', 17, 11, 'и стали там совершать курения на всех высотах, подобно народам, которых изгнал от них Господь, и делали худые дела, прогневляющие Господа,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16471, 'rst66', '2ki', 17, 10, 'и поставили у себя статуи и изображения Астарт на всяком высоком холме и под всяким тенистым деревом,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16470, 'rst66', '2ki', 17, 9, 'и стали делать сыны Израилевы дела неугодные Господу, Богу своему, и построили себе высоты во всех городах своих, <i>начиная</i> от сторожевой башни до укрепленного города,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16469, 'rst66', '2ki', 17, 8, 'и стали поступать по обычаям народов, которых прогнал Господь от лица сынов Израилевых, и <i>по обычаям</i> царей Израильских, как поступали они;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16468, 'rst66', '2ki', 17, 7, 'Когда стали грешить сыны Израилевы пред Господом, Богом своим, Который вывел их из земли Египетской, из‐под руки фараона, царя Египетского, и стали чтить богов иных,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16467, 'rst66', '2ki', 17, 6, 'В девятый год Осии взял царь Ассирийский Самарию, и переселил Израильтян в Ассирию, и поселил их в Халахе и в Хаворе, при реке Гозан, и в городах Мидийских.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16466, 'rst66', '2ki', 17, 5, 'И пошел царь Ассирийский на всю землю, и приступил к Самарии, и держал ее в осаде три года.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16465, 'rst66', '2ki', 17, 4, 'И заметил царь Ассирийский в Осии измену, так как он посылал послов к Сигору, царю Египетскому, и не доставлял дани царю Ассирийскому каждый год; и взял его царь Ассирийский под стражу, и заключил его в дом темничный.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16464, 'rst66', '2ki', 17, 3, 'Против него выступил Салманассар, царь Ассирийский, и сделался Осия подвластным ему и давал ему дань.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16463, 'rst66', '2ki', 17, 2, 'И делал он неугодное в очах Господних, но не так, как цари Израильские, которые были прежде него.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16462, 'rst66', '2ki', 17, 1, 'В двенадцатый год Ахаза, царя Иудейского, воцарился Осия, сын Илы, в Самарии над Израилем <i>и царствовал</i> девять лет.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16461, 'rst66', '2ki', 16, 20, 'И почил Ахаз с отцами своими, и погребен с отцами своими в городе Давидовом. И воцарился Езекия, сын его, вместо него.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16460, 'rst66', '2ki', 16, 19, 'Прочее об Ахазе, что он сделал, написано в летописи царей Иудейских.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16459, 'rst66', '2ki', 16, 18, 'И отменил крытый субботний ход, который построили при храме, и внешний царский вход к дому Господню, ради царя Ассирийского.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16458, 'rst66', '2ki', 16, 17, 'И обломал царь Ахаз ободки у подстав, и снял с них умывальницы, и море снял с медных волов, которые <i>были</i> под ним, и поставил его на каменный пол.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16457, 'rst66', '2ki', 16, 16, 'И сделал священник Урия все так, как приказал царь Ахаз.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16456, 'rst66', '2ki', 16, 15, 'И дал приказание царь Ахаз священнику Урии, сказав: на большом жертвеннике сожигай утреннее всесожжение и вечернее хлебное приношение, и всесожжение от царя и хлебное приношение от него, и всесожжение от всех людей земли и хлебное приношение от них, и возлияние от них, и всякою кровью всесожжений и всякою кровью жертв окропляй его, а жертвенник медный останется до моего усмотрения.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16455, 'rst66', '2ki', 16, 14, 'А медный жертвенник, который пред лицом Господним, он передвинул от лицевой стороны храма, с <i>места</i> между жертвенником <i>новым</i> и домом Господним, и поставил его сбоку <i>сего</i> жертвенника на север.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16454, 'rst66', '2ki', 16, 13, 'и сожег всесожжение свое и хлебное приношение, и совершил возлияние свое, и окропил кровью мирной жертвы своей жертвенник.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16453, 'rst66', '2ki', 16, 12, 'И пришел царь из Дамаска, и увидел царь жертвенник, и подошел царь к жертвеннику, и принес на нем жертву;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16452, 'rst66', '2ki', 16, 11, 'И построил священник Урия жертвенник по образцу, который прислал царь Ахаз из Дамаска; и сделал так священник Урия до прибытия царя Ахаза из Дамаска.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16451, 'rst66', '2ki', 16, 10, 'И пошел царь Ахаз навстречу Феглаффелласару, царю Ассирийскому, в Дамаск, и увидел жертвенник, который в Дамаске, и послал царь Ахаз к Урии священнику изображение жертвенника и чертеж всего устройства его.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16450, 'rst66', '2ki', 16, 9, 'И послушал его царь Ассирийский; и пошел царь Ассирийский в Дамаск, и взял его, и переселил жителей его в Кир, а Рецина умертвил.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16449, 'rst66', '2ki', 16, 8, 'И взял Ахаз серебро и золото, какое нашлось в доме Господнем и в сокровищницах дома царского, и послал царю Ассирийскому в дар.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16448, 'rst66', '2ki', 16, 7, 'И послал Ахаз послов к Феглаффелласару, царю Ассирийскому, сказать: раб твой и сын твой я; приди и защити меня от руки царя Сирийского и от руки царя Израильского, восставших на меня.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16447, 'rst66', '2ki', 16, 6, 'В то время Рецин, царь Сирийский, возвратил Сирии Елаф и изгнал Иудеев из Елафа; и Идумеяне вступили в Елаф, и живут там до сего дня.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16446, 'rst66', '2ki', 16, 5, 'Тогда пошел Рецин, царь Сирийский, и Факей, сын Ремалиин, царь Израильский, против Иерусалима, чтобы завоевать его, и держали Ахаза в осаде, но одолеть не могли.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16445, 'rst66', '2ki', 16, 4, 'и совершал жертвы и курения на высотах и на холмах и под всяким тенистым деревом.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16444, 'rst66', '2ki', 16, 3, 'но ходил путем царей Израильских, и даже сына своего провел чрез огонь, <i>подражая</i> мерзостям народов, которых прогнал Господь от лица сынов Израилевых,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16443, 'rst66', '2ki', 16, 2, 'Двадцати лет был Ахаз, когда воцарился, и шестнадцать лет царствовал в Иерусалиме, и не делал угодного в очах Господа, Бога своего, как Давид, отец его,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16442, 'rst66', '2ki', 16, 1, 'В семнадцатый год Факея, сына Ремалиина, воцарился Ахаз, сын Иоафама, царя Иудейского.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16441, 'rst66', '2ki', 15, 38, 'И почил Иоафам с отцами своими, и погребен с отцами своими в городе Давида, отца его. И воцарился Ахаз, сын его, вместо него.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16440, 'rst66', '2ki', 15, 37, 'В те дни начал Господь посылать на Иудею Рецина, царя Сирийского, и Факея, сына Ремалиина.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16439, 'rst66', '2ki', 15, 36, 'Прочее об Иоафаме и обо всем, что он сделал, написано в летописи царей Иудейских.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16438, 'rst66', '2ki', 15, 35, 'Только высоты не были отменены: народ совершал еще жертвы и курения на высотах. Он построил верхние ворота при доме Господнем.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16437, 'rst66', '2ki', 15, 34, 'Он делал угодное в очах Господних: во всем, как поступал Озия, отец его, так поступал и он.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16436, 'rst66', '2ki', 15, 33, 'Двадцати пяти лет был он, когда воцарился, и шестнадцать лет царствовал в Иерусалиме. Имя матери его Иеруша, дочь Садока.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16435, 'rst66', '2ki', 15, 32, 'Во второй год Факея, сына Ремалиина, царя Израильского, воцарился Иоафам, сын Озии, царя Иудейского.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16434, 'rst66', '2ki', 15, 31, 'Прочее о Факее и обо всем, что он сделал, написано в летописи царей Израильских.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16433, 'rst66', '2ki', 15, 30, 'И составил заговор Осия, сын Илы, против Факея, сына Ремалиина, и поразил его, и умертвил его, и воцарился вместо него в двадцатый год Иоафама, сына Озиина.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16432, 'rst66', '2ki', 15, 29, 'Во дни Факея, царя Израильского, пришел Феглаффелласар, царь Ассирийский, и взял Ион, Авел‐Беф‐Мааху, и Ианох, и Кедес, и Асор, и Галаад, и Галилею, всю землю Неффалимову, и переселил их в Ассирию.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16431, 'rst66', '2ki', 15, 28, 'и делал он неугодное в очах Господних: не отставал от грехов Иеровоама, сына Наватова, который ввел Израиля в грех.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16430, 'rst66', '2ki', 15, 27, 'В пятьдесят второй год Азарии, царя Иудейского, воцарился Факей, сын Ремалии, над Израилем в Самарии <i>и царствовал</i> двадцать лет;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16429, 'rst66', '2ki', 15, 26, 'Прочее о Факии и обо всем, что он сделал, написано в летописи царей Израильских.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16428, 'rst66', '2ki', 15, 25, 'И составил против него заговор Факей, сын Ремалии, сановник его, и поразил его в Самарии в палате царского дома, с Арговом и Арием, имея с собою пятьдесят человек Галаадитян, и умертвил его, и воцарился вместо него.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16427, 'rst66', '2ki', 15, 24, 'и делал он неугодное в очах Господних; не отставал от грехов Иеровоама, сына Наватова, который ввел Израиля в грех.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16426, 'rst66', '2ki', 15, 23, 'В пятидесятый год Азарии, царя Иудейского, воцарился Факия, сын Менаима, над Израилем в Самарии <i>и царствовал</i> два года;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16425, 'rst66', '2ki', 15, 22, 'И почил Менаим с отцами своими. И воцарился Факия, сын его, вместо него.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16424, 'rst66', '2ki', 15, 21, 'Прочее о Менаиме и обо всем, что он сделал, написано в летописи царей Израильских.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16423, 'rst66', '2ki', 15, 20, 'И разложил Менаим это серебро на Израильтян, на всех людей богатых, по пятидесяти сиклей серебра на каждого человека, чтобы отдать царю Ассирийскому. И пошел назад царь Ассирийский и не остался там в земле.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16422, 'rst66', '2ki', 15, 19, 'Тогда пришел Фул, царь Ассирийский, на землю <i>Израилеву</i>. И дал Менаим Фулу тысячу талантов серебра, чтобы руки его были за него и чтобы утвердить царство в руке своей.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16421, 'rst66', '2ki', 15, 18, 'и делал он неугодное в очах Господних; не отставал от грехов Иеровоама, сына Наватова, который ввел Израиля в грех, во все дни свои.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16420, 'rst66', '2ki', 15, 17, 'В тридцать девятом году Азарии, царя Иудейского, воцарился Менаим, сын Гадия, над Израилем <i>и царствовал</i> десять лет в Самарии;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16419, 'rst66', '2ki', 15, 16, 'И поразил Менаим Типсах и всех, которые были в нем и в пределах его, <i>начиная</i> от Фирцы, за то, что <i>город</i> не отворил <i>ворот</i>, и разбил <i>его</i>, и всех беременных женщин в нем разрубил.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16418, 'rst66', '2ki', 15, 15, 'Прочее о Селлуме и о заговоре его, который он составил, написано в летописи царей Израильских.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16417, 'rst66', '2ki', 15, 14, 'И пошел Менаим, сын Гадия из Фирцы, и пришел в Самарию, и поразил Селлума, сына Иависова, в Самарии и умертвил его, и воцарился вместо него.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16416, 'rst66', '2ki', 15, 13, 'Селлум, сын Иависа, воцарился в тридцать девятый год Азарии, царя Иудейского, и царствовал один месяц в Самарии.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16415, 'rst66', '2ki', 15, 12, 'Таково было слово Господа, которое Он изрек Ииую, сказав: сыновья твои до четвертого рода будут сидеть на престоле Израилевом. И сбылось так.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16414, 'rst66', '2ki', 15, 11, 'Прочее о Захарии написано в летописи царей Израильских.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16413, 'rst66', '2ki', 15, 10, 'И составил против него заговор Селлум, сын Иависа, и поразил его пред народом и убил его, и воцарился вместо него.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16412, 'rst66', '2ki', 15, 9, 'Он делал неугодное в очах Господних, как делали отцы его: не отставал от грехов Иеровоама, сына Наватова, который ввел Израиля в грех.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16411, 'rst66', '2ki', 15, 8, 'В тридцать восьмой год Азарии, царя Иудейского, воцарился Захария, сын Иеровоама, над Израилем в Самарии <i>и царствовал</i> шесть месяцев.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16410, 'rst66', '2ki', 15, 7, 'И почил Азария с отцами своими, и похоронили его с отцами его в городе Давидовом. И воцарился Иофам, сын его, вместо него.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16409, 'rst66', '2ki', 15, 6, 'Прочее об Азарии и обо всем, что он сделал, написано в летописи царей Иудейских.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16408, 'rst66', '2ki', 15, 5, 'И поразил Господь царя, и был он прокаженным до дня смерти своей и жил в отдельном доме. А Иофам, сын царя, <i>начальствовал</i> над дворцом и управлял народом земли.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16407, 'rst66', '2ki', 15, 4, 'Только высоты не были отменены: народ совершал еще жертвы и курения на высотах.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16406, 'rst66', '2ki', 15, 3, 'Он делал угодное в очах Господних во всем так, как поступал Амасия, отец его.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16405, 'rst66', '2ki', 15, 2, 'шестнадцати лет был он, когда воцарился, и пятьдесят два года царствовал в Иерусалиме. Имя матери его Иехолия, из Иерусалима.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16404, 'rst66', '2ki', 15, 1, 'В двадцать седьмой год Иеровоама, царя Израильского, воцарился Азария, сын Амасии, царь Иудейский:');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16403, 'rst66', '2ki', 14, 29, 'И почил Иеровоам с отцами своими, с царями Израильскими. И воцарился Захария, сын его, вместо него.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16402, 'rst66', '2ki', 14, 28, 'Прочее об Иеровоаме и обо всем, что он сделал, и о мужественных подвигах его, как он воевал и как возвратил Израилю Дамаск и Емаф, принадлежавшие Иуде, написано в летописи царей Израильских.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16401, 'rst66', '2ki', 14, 27, 'И не восхотел Господь искоренить имя Израильтян из поднебесной, и спас их рукою Иеровоама, сына Иоасова.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16400, 'rst66', '2ki', 14, 26, 'Ибо Господь видел бедствие Израиля, весьма горькое, так что не оставалось ни заключенного, ни оставшегося, и не было помощника у Израиля.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16399, 'rst66', '2ki', 14, 25, 'Он восстановил пределы Израиля, от входа в Емаф до моря пустыни, по слову Господа, Бога Израилева, которое Он изрек чрез раба Своего Иону, сына Амафиина, пророка из Гафхефера,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16398, 'rst66', '2ki', 14, 24, 'и делал он неугодное в очах Господних: не отступал от всех грехов Иеровоама, сына Наватова, который ввел Израиля в грех.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16397, 'rst66', '2ki', 14, 23, 'В пятнадцатый год Амасии, сына Иоасова, царя Иудейского, воцарился Иеровоам, сын Иоасов, царь Израильский, в Самарии, и <i>царствовал</i> сорок один год,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16396, 'rst66', '2ki', 14, 22, 'Он обстроил Елаф, и возвратил его Иуде, после того как царь почил с отцами своими.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16395, 'rst66', '2ki', 14, 21, 'И взял весь народ Иудейский Азарию, которому было шестнадцать лет, и воцарили его вместо отца его Амасии.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16394, 'rst66', '2ki', 14, 20, 'И привезли его на конях, и погребен он был в Иерусалиме с отцами своими в городе Давидовом.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16393, 'rst66', '2ki', 14, 19, 'И составили против него заговор в Иерусалиме, и убежал он в Лахис. И послали за ним в Лахис, и умертвили его там.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16392, 'rst66', '2ki', 14, 18, 'Прочие дела Амасии записаны в летописи царей Иудейских.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16391, 'rst66', '2ki', 14, 17, 'И жил Амасия, сын Иоасов, царь Иудейский, по смерти Иоаса, сына Иоахазова, царя Израильского, пятнадцать лет.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16390, 'rst66', '2ki', 14, 16, 'И почил Иоас с отцами своими, и погребен в Самарии с царями Израильскими. И воцарился Иеровоам, сын его, вместо него.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16389, 'rst66', '2ki', 14, 15, 'Прочее об Иоасе, что он сделал, и о мужественных подвигах его, и как он воевал с Амасиею, царем Иудейским, написано в летописи царей Израильских.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16388, 'rst66', '2ki', 14, 14, 'И взял все золото и серебро, и все сосуды, какие нашлись в доме Господнем и в сокровищницах царского дома, и заложников, и возвратился в Самарию.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16387, 'rst66', '2ki', 14, 13, 'И Амасию, царя Иудейского, сына Иоаса, сына Охозиина, захватил Иоас, царь Израильский, в Вефсамисе, и пошел в Иерусалим и разрушил стену Иерусалимскую от ворот Ефремовых до ворот угольных на четыреста локтей.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16386, 'rst66', '2ki', 14, 12, 'И разбиты были Иудеи Израильтянами, и разбежались по шатрам своим.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16385, 'rst66', '2ki', 14, 11, 'Но не послушался Амасия. И выступил Иоас, царь Израильский, и увиделись лично он и Амасия, царь Иудейский, в Вефсамисе, что в Иудее.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16384, 'rst66', '2ki', 14, 10, 'Ты поразил Идумеян, и возгордилось сердце твое. Величайся и сиди у себя дома. К чему тебе затевать ссору на свою беду? Падешь ты, и Иуда с тобою.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16383, 'rst66', '2ki', 14, 9, 'И послал Иоас, царь Израильский, к Амасии, царю Иудейскому, сказать: терн, который на Ливане, послал к кедру, который на Ливане же, сказать: «отдай дочь свою в жену сыну моему». Но прошли дикие звери, что на Ливане, и истоптали этот терн.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16382, 'rst66', '2ki', 14, 8, 'Тогда послал Амасия послов к Иоасу, царю Израильскому, сыну Иоахаза, сына Ииуева, сказать: выйди, повидаемся лично.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16381, 'rst66', '2ki', 14, 7, 'Он поразил десять тысяч Идумеян на долине Соляной, и взял Селу войною, и дал ей имя Иокфеил, которое <i>остается и</i> до сего дня.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16380, 'rst66', '2ki', 14, 6, 'Но детей убийц не умертвил, так как написано в книге закона Моисеева, в которой заповедал Господь, говоря: «не должны быть наказываемы смертью отцы за детей, и дети не должны быть наказываемы смертью за отцов, но каждый за свое преступление должен быть наказываем смертью».');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16379, 'rst66', '2ki', 14, 5, 'Когда утвердилось царство в руках его, тогда он умертвил слуг своих, убивших царя, отца его.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16378, 'rst66', '2ki', 14, 4, 'Только высоты не были отменены: народ совершал еще жертвы и курения на высотах.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16377, 'rst66', '2ki', 14, 3, 'И делал он угодное в очах Господних, впрочем, не так, как отец его Давид: он во всем поступал так, как отец его Иоас.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16376, 'rst66', '2ki', 14, 2, 'двадцати пяти лет был он, когда воцарился, и двадцать девять лет царствовал в Иерусалиме. Имя матери его Иегоаддань, из Иерусалима.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16375, 'rst66', '2ki', 14, 1, 'Во второй год Иоаса, сына Иоахазова, царя Израильского, воцарился Амасия, сын Иоаса, царь Иудейский:');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16374, 'rst66', '2ki', 13, 25, 'И взял назад Иоас, сын Иоахаза, из руки Венадада, сына Азаила, города, которые он взял войною из руки отца его Иоахаза. Три раза разбил его Иоас и возвратил города Израилевы.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16373, 'rst66', '2ki', 13, 24, 'И умер Азаил, царь Сирийский, и воцарился Венадад, сын его, вместо него.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16372, 'rst66', '2ki', 13, 23, 'Но Господь умилосердился над ними, и помиловал их, и обратился к ним ради завета Своего с Авраамом, Исааком и Иаковом, и не хотел истребить их, и не отверг их от лица Своего доныне.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16371, 'rst66', '2ki', 13, 22, 'Азаил, царь Сирийский, теснил Израильтян во все дни Иоахаза.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16370, 'rst66', '2ki', 13, 21, 'И было, что, когда погребали одного человека, то, увидев это полчище, <i>погребавшие</i> бросили того человека в гроб Елисеев; и он при падении своем коснулся костей Елисея, и ожил, и встал на ноги свои.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16369, 'rst66', '2ki', 13, 20, 'И умер Елисей, и похоронили его. И полчища Моавитян пришли в землю в следующем году.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16368, 'rst66', '2ki', 13, 19, 'И разгневался на него человек Божий, и сказал: надобно было бы бить пять или шесть раз, тогда ты побил бы Сириян совершенно, а теперь <i>только</i> три раза поразишь Сириян.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16367, 'rst66', '2ki', 13, 18, 'И сказал <i>Елисей</i>: возьми стрелы. И он взял. И сказал царю Израильскому: бей по земле. И ударил он три раза, и остановился.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16366, 'rst66', '2ki', 13, 17, 'и сказал: отвори окно на восток. И он отворил. И сказал Елисей: выстрели. И он выстрелил. И сказал: эта стрела избавления от Господа и стрела избавления против Сирии, и ты поразишь Сириян в Афеке вконец.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16365, 'rst66', '2ki', 13, 16, 'И сказал царю Израильскому: положи руку твою на лук. И положил он руку свою. И наложил Елисей руки свои на руки царя,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16364, 'rst66', '2ki', 13, 15, 'И сказал ему Елисей: возьми лук и стрелы. И взял он лук и стрелы.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16363, 'rst66', '2ki', 13, 14, 'Елисей заболел болезнью, от которой <i>потом</i> и умер. И пришел к нему Иоас, царь Израильский, и плакал над ним, и говорил: отец мой! отец мой! колесница Израиля и конница его!');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16362, 'rst66', '2ki', 13, 13, 'И почил Иоас с отцами своими, а Иеровоам сел на престоле его. И погребен Иоас в Самарии с царями Израильскими.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16361, 'rst66', '2ki', 13, 12, 'Прочее об Иоасе и обо всем, что он сделал, и о мужественных подвигах его, как он воевал с Амасиею, царем Иудейским, написано в летописи царей Израильских.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16360, 'rst66', '2ki', 13, 11, 'и делал неугодное в очах Господних; не отставал от всех грехов Иеровоама, сына Наватова, который ввел Израиля в грех, но ходил в них.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16359, 'rst66', '2ki', 13, 10, 'В тридцать седьмой год Иоаса, царя Иудейского, воцарился Иоас, сын Иоахазов, над Израилем в Самарии, <i>и царствовал</i> шестнадцать лет,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16358, 'rst66', '2ki', 13, 9, 'И почил Иоахаз с отцами своими, и похоронили его в Самарии. И воцарился Иоас, сын его, вместо него.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16357, 'rst66', '2ki', 13, 8, 'Прочее об Иоахазе и обо всем, что он сделал, и о мужественных подвигах его, написано в летописи царей Израильских.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16356, 'rst66', '2ki', 13, 7, 'У Иоахаза оставалось войска только пятьдесят всадников, десять колесниц и десять тысяч пеших, оттого, что истребил их царь Сирийский и обратил их в прах на попрание.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16355, 'rst66', '2ki', 13, 6, 'Однако ж не отступали от грехов дома Иеровоама, который ввел Израиля в грех; ходили в них, и дубрава стояла в Самарии.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16354, 'rst66', '2ki', 13, 5, 'И дал Господь Израильтянам избавителя, и вышли они из‐под руки Сириян, и жили сыны Израилевы в шатрах своих, как вчера и третьего дня.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16353, 'rst66', '2ki', 13, 4, 'И помолился Иоахаз лицу Господню, и услышал его Господь, потому что видел стеснение Израильтян, как теснил их царь Сирийский.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16352, 'rst66', '2ki', 13, 3, 'И возгорелся гнев Господа на Израиля, и Он предавал их в руку Азаила, царя Сирийского, и в руку Венадада, сына Азаилова, во все дни.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16351, 'rst66', '2ki', 13, 2, 'и делал неугодное в очах Господних, и ходил в грехах Иеровоама, сына Наватова, который ввел Израиля в грех, и не отставал от них.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16350, 'rst66', '2ki', 13, 1, 'В двадцать третий год Иоаса, сына Охозиина, царя Иудейского, воцарился Иоахаз, сын Ииуя, над Израилем в Самарии, <i>и царствовал</i> семнадцать лет,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16349, 'rst66', '2ki', 12, 21, 'Его убили слуги его: Иозакар, сын Шимеаты, и Иегозавад, сын Шомеры; и он умер, и похоронили его с отцами его в городе Давидовом. И воцарился Амасия, сын его, вместо него.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16348, 'rst66', '2ki', 12, 20, 'И восстали слуги его, и составили заговор, и убили Иоаса в доме Милло, на дороге к Силле.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16347, 'rst66', '2ki', 12, 19, 'Прочее об Иоасе и обо всем, что он сделал, написано в летописи царей Иудейских.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16346, 'rst66', '2ki', 12, 18, 'Но Иоас, царь Иудейский, взял все пожертвованное, что пожертвовали <i>храму</i> Иосафат, и Иорам и Охозия, отцы его, цари Иудейские, и что он сам пожертвовал, и все золото, найденное в сокровищницах дома Господня и дома царского, и послал Азаилу, царю Сирийскому; и он отступил от Иерусалима.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16345, 'rst66', '2ki', 12, 17, 'Тогда выступил в поход Азаил, царь Сирийский, и пошел войною на Геф, и взял его; и вознамерился Азаил идти на Иерусалим.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16344, 'rst66', '2ki', 12, 16, 'Серебро за жертву о преступлении и серебро за жертву о грехе не вносилось в дом Господень: священникам оно принадлежало.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16343, 'rst66', '2ki', 12, 15, 'И не требовали отчета от тех людей, которым поручали серебро для раздачи производителям работ, ибо они действовали честно.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16342, 'rst66', '2ki', 12, 14, 'а производителям работ отдавали его, и починивали им дом Господень.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16341, 'rst66', '2ki', 12, 13, 'Но не сделано было для дома Господня серебряных блюд, ножей, чаш <i>для окропления</i>, труб, всяких сосудов золотых и сосудов серебряных из серебра, приносимого в дом Господень,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16340, 'rst66', '2ki', 12, 12, 'и на делателей стен и на каменотесов, также на покупку дерев и тесаных камней, для починки повреждений в доме Господнем, и на все, что расходовалось для поддержания храма.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16339, 'rst66', '2ki', 12, 11, 'и отдавали сосчитанное серебро в руки производителям работ, приставленным к дому Господню, а сии издерживали его на плотников и строителей, работавших в доме Господнем,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16338, 'rst66', '2ki', 12, 10, 'И когда видели, что много серебра в ящике, приходили писец царский и первосвященник, и завязывали <i>в мешки</i>, и пересчитывали серебро, найденное в доме Господнем;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16337, 'rst66', '2ki', 12, 9, 'И взял священник Иодай один ящик, и сделал отверстие сверху его, и поставил его подле жертвенника на правой стороне, где входили в дом Господень. И полагали туда священники, стоящие на страже у порога, все серебро, приносимое в дом Господень.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16336, 'rst66', '2ki', 12, 8, 'И согласились священники не брать серебра у народа на исправление повреждений в храме.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16335, 'rst66', '2ki', 12, 7, 'то царь Иоас позвал священника Иодая и священников и сказал им: почему вы не исправляете повреждений в храме? Не берите же отныне серебра у знакомых своих, а на <i>починку</i> повреждений в храме отдайте его.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16334, 'rst66', '2ki', 12, 6, 'Но как до двадцать третьего года царя Иоаса священники не исправляли повреждений в храме,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16333, 'rst66', '2ki', 12, 5, 'пусть берут священники себе, каждый от своего знакомого, и пусть исправляют они поврежденное в храме, везде, где найдется повреждение.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16332, 'rst66', '2ki', 12, 4, 'И сказал Иоас священникам: все серебро посвящаемое, которое приносят в дом Господень, серебро от приходящих, серебро, <i>вносимое</i> за каждую душу по оценке, все серебро, сколько кому приходит на сердце принести в дом Господень,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16331, 'rst66', '2ki', 12, 3, 'только высоты не были отменены; народ еще приносил жертвы и курения на высотах.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16330, 'rst66', '2ki', 12, 2, 'И делал Иоас угодное в очах Господних во все дни свои, доколе наставлял его священник Иодай;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16329, 'rst66', '2ki', 12, 1, 'В седьмой год Ииуя воцарился Иоас и сорок лет царствовал в Иерусалиме. Имя матери его Цивья, из Вирсавии.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16328, 'rst66', '2ki', 11, 21, 'Семи лет был Иоас, когда воцарился.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16327, 'rst66', '2ki', 11, 20, 'И веселился весь народ земли, и город успокоился. А Гофолию умертвили мечом в царском доме.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16326, 'rst66', '2ki', 11, 19, 'И взял сотников и телохранителей и скороходов и весь народ земли, и проводили царя из дома Господня, и пришли по дороге через ворота телохранителей в дом царский; и он воссел на престоле царей.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16325, 'rst66', '2ki', 11, 18, 'И пошел весь народ земли в дом Ваала, и разрушили жертвенники его, и изображения его совершенно разбили, и Матфана, жреца Ваалова, убили пред жертвенниками. И учредил священник наблюдение над домом Господним.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16324, 'rst66', '2ki', 11, 17, 'И заключил Иодай завет между Господом и между царем и народом, чтоб он был народом Господним, и между царем и народом.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16323, 'rst66', '2ki', 11, 16, 'И дали ей место, и она прошла через вход конский к дому царскому, и умерщвлена там.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16322, 'rst66', '2ki', 11, 15, 'И дал приказание Иодай священник сотникам, начальствующим над войском, и сказал им: «выведите ее за ряды, а кто пойдет за нею, умерщвляйте мечом», так как думал священник, чтобы не умертвили ее в доме Господнем.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16321, 'rst66', '2ki', 11, 14, 'И видит, и вот, царь стоит на возвышении, по обычаю, и князья и трубы подле царя; и весь народ земли веселится, и трубят трубами. И разодрала Гофолия одежды свои, и закричала: заговор! заговор!');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16320, 'rst66', '2ki', 11, 13, 'И услышала Гофолия голос бегущего народа, и пошла к народу в дом Господень.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16319, 'rst66', '2ki', 11, 12, 'И вывел он царского сына, и возложил на него <i>царский</i> венец и украшения, и воцарили его, и помазали его, и рукоплескали и восклицали: да живет царь!');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16318, 'rst66', '2ki', 11, 11, 'И стали скороходы, каждый с оружием в руке своей, от правой стороны дома до левой стороны дома, у жертвенника и у дома, вокруг царя.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16317, 'rst66', '2ki', 11, 10, 'И раздал священник сотникам копья и щиты царя Давида, которые были в доме Господнем.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16316, 'rst66', '2ki', 11, 9, 'И сделали сотники всё, что приказал Иодай священник, и взяли каждый людей своих, приходящих в субботу и отходящих в субботу, и пришли к Иодаю священнику.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16315, 'rst66', '2ki', 11, 8, 'и окружите царя со всех сторон, каждый с оружием своим в руке своей; и кто вошел бы в ряды, тот да будет умерщвлен. И будьте при царе, когда он выходит и когда входит.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16314, 'rst66', '2ki', 11, 7, 'и две части из вас, из всех, отходящих в субботу, будут содержать стражу при доме Господнем для царя;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16313, 'rst66', '2ki', 11, 6, 'третья часть у ворот Сур, и третья часть у ворот сзади телохранителей, и содержите стражу дома, чтобы не было повреждения;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16312, 'rst66', '2ki', 11, 5, 'И дал им приказание, сказав: вот что вы сделайте: третья часть из вас, из приходящих в субботу, будет содержать стражу при царском доме;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16311, 'rst66', '2ki', 11, 4, 'В седьмой год послал Иодай, и взял сотников из телохранителей и скороходов, и привел их к себе в дом Господень, и сделал с ними договор, и взял с них клятву в доме Господнем, и показал им царского сына.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16310, 'rst66', '2ki', 11, 3, 'И был он с нею скрываем в доме Господнем шесть лет, между тем как Гофолия царствовала над землею.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16309, 'rst66', '2ki', 11, 2, 'Но Иосавеф, дочь царя Иорама, сестра Охозии, взяла Иоаса, сына Охозии, и тайно увела его из среды умерщвляемых сыновей царских, его и кормилицу его, в постельную комнату; и скрыли его от Гофолии, и он не умерщвлен.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16308, 'rst66', '2ki', 11, 1, 'Гофолия, мать Охозии, видя, что сын ее умер, встала и истребила все царское племя.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16307, 'rst66', '2ki', 10, 36, 'Времени же царствования Ииуева над Израилем, в Самарии, было двадцать восемь лет.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16306, 'rst66', '2ki', 10, 35, 'И почил Ииуй с отцами своими, и похоронили его в Самарии. И воцарился Иоахаз, сын его, вместо него.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16305, 'rst66', '2ki', 10, 34, 'Прочее об Ииуе и обо всем, что он сделал, и о мужественных подвигах его написано в летописи царей Израильских.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16304, 'rst66', '2ki', 10, 33, 'на восток от Иордана, всю землю Галаад, <i>колено</i> Гадово, Рувимово, Манассиино, <i>начиная</i> от Ароера, который при потоке Арноне, и Галаад и Васан.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16303, 'rst66', '2ki', 10, 32, 'В те дни начал Господь отрезать части от Израильтян, и поражал их Азаил во всем пределе Израилевом,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16302, 'rst66', '2ki', 10, 31, 'Но Ииуй не старался ходить в законе Господа, Бога Израилева, от всего сердца. Он не отступал от грехов Иеровоама, который ввел Израиля в грех.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16301, 'rst66', '2ki', 10, 30, 'И сказал Господь Ииую: за то, что ты охотно сделал, что было праведно в очах Моих, выполнил над домом Ахавовым все, что было на сердце у Меня, сыновья твои до четвертого рода будут сидеть на престоле Израилевом.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16300, 'rst66', '2ki', 10, 29, 'Впрочем, от грехов Иеровоама, сына Наватова, который ввел Израиля в грех, от них не отступал Ииуй, &mdash; от золотых тельцов, которые в Вефиле и которые в Дане.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16299, 'rst66', '2ki', 10, 28, 'И истребил Ииуй Ваала с земли Израильской.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16298, 'rst66', '2ki', 10, 27, 'И разбили статую Ваала, и разрушили капище Ваалово; и сделали из него место нечистот, до сего дня.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16297, 'rst66', '2ki', 10, 26, 'И вынесли статуи из капища Ваалова и сожгли их.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16296, 'rst66', '2ki', 10, 25, 'Когда кончено было всесожжение, сказал Ииуй скороходам и начальникам: пойдите, бейте их, чтобы ни один не ушел. И поразили их острием меча и бросили <i>их</i> скороходы и начальники, и пошли в город, где было капище Ваалово.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16295, 'rst66', '2ki', 10, 24, 'И приступили они к совершению жертв и всесожжений. А Ииуй поставил вне <i>дома</i> восемьдесят человек и сказал: душа того, у которого спасется кто‐либо из людей, которых я отдаю вам в руки, будет вместо души <i>спасшегося</i>.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16294, 'rst66', '2ki', 10, 23, 'И вошел Ииуй с Ионадавом, сыном Рихавовым, в дом Ваалов, и сказал служителям Ваала: разведайте и разглядите, не находится ли у вас кто‐нибудь из служителей Господних, так как здесь должны находиться только одни служители Ваала.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16293, 'rst66', '2ki', 10, 22, 'И сказал он хранителю одежд: принеси одежду для всех служителей Ваала. И он принес им одежду.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16292, 'rst66', '2ki', 10, 21, 'И послал Ииуй по всему Израилю, и пришли все служители Ваала; не оставалось ни одного человека, кто бы не пришел; и вошли в дом Ваалов, и наполнился дом Ваалов от края до края.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16291, 'rst66', '2ki', 10, 20, 'И сказал Ииуй: назначьте праздничное собрание ради Ваала. И провозгласили <i>собрание</i>.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16290, 'rst66', '2ki', 10, 19, 'Итак, созовите ко мне всех пророков Ваала, всех служителей его и всех священников его, чтобы никто не был в отсутствии, потому что у меня будет великая жертва Ваалу. А всякий, кто не явится, не останется жив. Ииуй делал <i>это</i> с хитрым намерением, чтобы истребить служителей Ваала.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16289, 'rst66', '2ki', 10, 18, 'И собрал Ииуй весь народ и сказал им: Ахав мало служил Ваалу; Ииуй будет служить ему более.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16288, 'rst66', '2ki', 10, 17, 'Прибыв в Самарию, он убил всех, остававшихся у Ахава в Самарии, так что совсем истребил его, по слову Господа, которое Он изрек Илии.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16287, 'rst66', '2ki', 10, 16, 'и сказал: поезжай со мною, и смотри на мою ревность о Господе. И посадили его в колесницу.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16286, 'rst66', '2ki', 10, 15, 'И поехал оттуда, и встретился с Ионадавом, сыном Рихавовым, <i>шедшим</i> навстречу ему, и приветствовал его, и сказал ему: расположено ли твое сердце так, как мое сердце к твоему сердцу? И сказал Ионадав: да. Если так, то дай руку твою. И подал он руку свою, и приподнял он его к себе в колесницу,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16285, 'rst66', '2ki', 10, 14, 'И сказал он: возьмите их живых. И взяли их живых, и закололи их &mdash; сорок два человека, при колодезе Беф‐Екеда, и не осталось из них ни одного.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16284, 'rst66', '2ki', 10, 13, 'встретил Ииуй братьев Охозии, царя Иудейского, и сказал: кто вы? Они сказали: мы братья Охозии, идем узнать о здоровье сыновей царя и сыновей государыни.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16283, 'rst66', '2ki', 10, 12, 'И встал, и пошел, и пришел в Самарию. Находясь на пути при Беф‐Екеде пастушеском,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16282, 'rst66', '2ki', 10, 11, 'И умертвил Ииуй всех оставшихся из дома Ахава в Изрееле, и всех вельмож его, и близких его, и священников его, так что не осталось от него ни одного уцелевшего.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16281, 'rst66', '2ki', 10, 10, 'Знайте же теперь, что не падет на землю ни одно слово Господа, которое Он изрек о доме Ахава; Господь сделал то, что изрек чрез раба Своего Илию.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16280, 'rst66', '2ki', 10, 9, 'Поутру он вышел, и стал, и сказал всему народу: вы невиновны. Вот, я восстал против государя моего и умертвил его, а их всех кто убил?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16279, 'rst66', '2ki', 10, 8, 'И пришел посланный, и донес ему, и сказал: принесли головы сыновей царских. И сказал он: разложите их на две груды у входа в ворота, до утра.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16278, 'rst66', '2ki', 10, 7, 'Когда пришло к ним письмо, они взяли царских сыновей, и закололи их &mdash; семьдесят человек, и положили головы их в корзины, и послали к нему в Изреель.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16277, 'rst66', '2ki', 10, 6, 'И написал он к ним письмо во второй раз такое: если вы мои и слову моему повинуетесь, то возьмите головы сыновей государя своего, и придите ко мне завтра в это время в Изреель. (Царских же сыновей было семьдесят человек; воспитывали их знатнейшие в городе.)');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16276, 'rst66', '2ki', 10, 5, 'И послал начальствующий над домом <i>царским</i>, и градоначальник, и старейшины, и воспитатели к Ииую, сказать: мы рабы твои, и что скажешь нам, то и сделаем; мы никого не поставим царем, что угодно тебе, то и делай.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16275, 'rst66', '2ki', 10, 4, 'Они испугались чрезвычайно и сказали: вот, два царя не устояли перед ним, как же нам устоять?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16274, 'rst66', '2ki', 10, 3, 'выберите лучшего и достойнейшего из сыновей государя своего, и посадите на престол отца его, и воюйте за дом государя своего.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16273, 'rst66', '2ki', 10, 2, 'когда придет это письмо к вам, то, так как у вас и сыновья господина вашего, у вас же и колесницы, и кони, и укрепленный город, и оружие, &mdash;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16272, 'rst66', '2ki', 10, 1, 'У Ахава было семьдесят сыновей в Самарии. И написал Ииуй письма, и послал в Самарию к начальникам Изреельским, старейшинам и воспитателям детей Ахавовых, такого содержания:');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16271, 'rst66', '2ki', 9, 37, 'и будет труп Иезавели на участке Изреельском, как навоз на поле, так что никто не скажет: это Иезавель.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16270, 'rst66', '2ki', 9, 36, 'И возвратились, и донесли ему. И сказал он: таково было слово Господа, которое Он изрек чрез раба Своего Илию Фесвитянина, сказав: на поле Изреельском съедят псы тело Иезавели,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16269, 'rst66', '2ki', 9, 35, 'И пошли хоронить ее, и не нашли от нее ничего, кроме черепа, и ног, и кистей рук.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16268, 'rst66', '2ki', 9, 34, 'И пришел Ииуй, и ел, и пил, и сказал: отыщите эту проклятую и похороните ее, так как царская дочь она.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16267, 'rst66', '2ki', 9, 33, 'И сказал он: выбросьте ее. И выбросили ее. И брызнула кровь ее на стену и на коней, и растоптали ее.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16266, 'rst66', '2ki', 9, 32, 'И поднял он лицо свое к окну и сказал: кто со мною, кто? И выглянули к нему два, три евнуха.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16265, 'rst66', '2ki', 9, 31, 'Когда Ииуй вошел в ворота, она сказала: мир ли Замврию, убийце государя своего?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16264, 'rst66', '2ki', 9, 30, 'И прибыл Ииуй в Изреель. Иезавель же, получив весть, нарумянила лицо свое и украсила голову свою, и глядела в окно.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16263, 'rst66', '2ki', 9, 29, 'В одиннадцатый год Иорама, сына Ахавова, воцарился Охозия в Иудее.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16262, 'rst66', '2ki', 9, 28, 'И отвезли его рабы его в Иерусалим, и похоронили его в гробнице его, с отцами его, в городе Давидовом.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16261, 'rst66', '2ki', 9, 27, 'Охозия, царь Иудейский, увидев сие, побежал по дороге к дому, что в саду. И погнался за ним Ииуй, и сказал: и его бейте на колеснице. <i>Это было</i> на возвышенности Гур, что при Ивлеаме. И побежал он в Мегиддон, и умер там.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16260, 'rst66', '2ki', 9, 26, '«Истинно, кровь Навуфея и кровь сыновей его видел Я вчера, говорит Господь, и отмщу тебе на сем поле». Итак, возьми, брось его на поле, по слову Господню.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16259, 'rst66', '2ki', 9, 25, 'И сказал Ииуй Бидекару, сановнику своему: возьми, брось его на участок поля Навуфея Изреелитянина, ибо вспомни, как мы с тобою ехали вдвоем сзади Ахава, отца его, и как Господь изрек на него такое пророчество:');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16258, 'rst66', '2ki', 9, 24, 'А Ииуй натянул лук рукою своею, и поразил Иорама между плечами его, и прошла стрела чрез сердце его, и пал он на колеснице своей.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16257, 'rst66', '2ki', 9, 23, 'И поворотил Иорам руки свои, и побежал, и сказал Охозии: измена, Охозия!');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16256, 'rst66', '2ki', 9, 22, 'И когда увидел Иорам Ииуя, то сказал: с миром ли, Ииуй? И сказал он: какой мир при любодействе Иезавели, матери твоей, и при многих волхвованиях ее?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16255, 'rst66', '2ki', 9, 21, 'И сказал Иорам: запрягай. И запрягли колесницу его. И выступил Иорам, царь Израильский, и Охозия, царь Иудейский, каждый на колеснице своей. И выступили навстречу Ииую, и встретились с ним на поле Навуфея Изреелитянина.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16254, 'rst66', '2ki', 9, 20, 'И донес сторож, сказав: доехал до них, и не возвращается, а походка, как будто Ииуя, сына Намессиева, потому что он идет стремительно.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16253, 'rst66', '2ki', 9, 19, 'И послали другого всадника, и он приехал к ним, и сказал: так говорит царь: с миром ли? И сказал Ииуй: что тебе до мира? Поезжай за мною.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16252, 'rst66', '2ki', 9, 18, 'И выехал всадник на коне навстречу ему, и сказал: так говорит царь: с миром ли? И сказал Ииуй: что тебе до мира? Поезжай за мною. И донес сторож, и сказал: доехал до них, но не возвращается.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16251, 'rst66', '2ki', 9, 17, 'На башне в Изрееле стоял сторож, и увидел он полчище Ииуево, когда оно шло, и сказал: полчище вижу я. И сказал Иорам: возьми всадника, и пошли навстречу им, и пусть скажет: с миром ли?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16250, 'rst66', '2ki', 9, 16, 'И сел Ииуй на коня, и поехал в Изреель, где лежал Иорам и куда Охозия, царь Иудейский, пришел посетить Иорама.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16249, 'rst66', '2ki', 9, 15, 'Впрочем, сам царь Иорам возвратился, чтобы лечиться в Изрееле от ран, которые причинили ему Сирияне, когда он воевал с Азаилом, царем Сирийским. И сказал Ииуй: если вы согласны <i>со мною</i>, то пусть никто не уходит из города, чтобы идти подать весть в Изрееле.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16248, 'rst66', '2ki', 9, 14, 'И восстал Ииуй, сын Иосафата, сына Намессиева, против Иорама; Иорам же находился со всеми Израильтянами в Рамофе Галаадском на страже против Азаила, царя Сирийского.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16247, 'rst66', '2ki', 9, 13, 'И поспешили они, и взяли каждый одежду свою, и подостлали ему на самых ступенях, и затрубили трубою, и сказали: воцарился Ииуй!');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16246, 'rst66', '2ki', 9, 12, 'И сказали: неправда, скажи нам. И сказал он: то и то он сказал мне, говоря: «так говорит Господь: помазую тебя в царя над Израилем».');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16245, 'rst66', '2ki', 9, 11, 'И вышел Ииуй к слугам господина своего, и сказали ему: с миром ли? Зачем приходил этот неистовый к тебе? И сказал им: вы знаете этого человека и что он говорит.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16244, 'rst66', '2ki', 9, 10, 'Иезавель же съедят псы на поле Изреельском, и никто не похоронит ее». И отворил дверь, и убежал.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16243, 'rst66', '2ki', 9, 9, 'и сделаю дом Ахава, как дом Иеровоама, сына Наватова, и как дом Ваасы, сына Ахиина;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16242, 'rst66', '2ki', 9, 8, 'и погибнет весь дом Ахава, и истреблю у Ахава мочащегося к стене, и заключенного и оставшегося в Израиле,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16241, 'rst66', '2ki', 9, 7, 'и ты истребишь дом Ахава, господина твоего, чтобы Мне отмстить за кровь рабов Моих пророков и за кровь всех рабов Господних, <i>павших</i> от руки Иезавели;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16240, 'rst66', '2ki', 9, 6, 'И встал он, и вошел в дом. И <i>отрок</i> вылил елей на голову его, и сказал ему: так говорит Господь, Бог Израилев: «помазую тебя в царя над народом Господним, над Израилем,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16239, 'rst66', '2ki', 9, 5, 'и пришел, и вот сидят военачальники. И сказал: у меня слово до тебя, военачальник. И сказал Ииуй: до кого из всех нас? И сказал он: до тебя, военачальник.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16238, 'rst66', '2ki', 9, 4, 'И пошел отрок, слуга пророка, в Рамоф Галаадский,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16237, 'rst66', '2ki', 9, 3, 'и возьми сосуд с елеем, и вылей на голову его, и скажи: «так говорит Господь: помазую тебя в царя над Израилем». Потом отвори дверь, и беги, и не жди.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16236, 'rst66', '2ki', 9, 2, 'Придя туда, отыщи там Ииуя, сына Иосафата, сына Намессиева, и подойди, и вели выступить ему из среды братьев своих, и введи его во внутреннюю комнату;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16235, 'rst66', '2ki', 9, 1, 'Елисей пророк призвал одного из сынов пророческих и сказал ему: опояшь чресла твои, и возьми сей сосуд с елеем в руку твою, и пойди в Рамоф Галаадский.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16234, 'rst66', '2ki', 8, 29, 'И возвратился Иорам царь, чтобы лечиться в Изрееле от ран, которые причинили ему Сирияне в Рамофе, когда он воевал с Азаилом, царем Сирийским. И Охозия, сын Иорама, царь Иудейский, пришел посетить Иорама, сына Ахавова, в Изреель, так как он был болен.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16233, 'rst66', '2ki', 8, 28, 'И пошел он с Иорамом, сыном Ахавовым, на войну с Азаилом, царем Сирийским, в Рамоф Галаадский, и ранили Сирияне Иорама.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16232, 'rst66', '2ki', 8, 27, 'И ходил путем дома Ахавова, и делал неугодное в очах Господних, подобно дому Ахавову, потому что он был в родстве с домом Ахавовым.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16231, 'rst66', '2ki', 8, 26, 'Двадцати двух лет был Охозия, когда воцарился, и один год царствовал в Иерусалиме. Имя же матери его Гофолия, дочь Амврия, царя Израильского.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16230, 'rst66', '2ki', 8, 25, 'В двенадцатый год Иорама, сына Ахавова, царя Израильского, воцарился Охозия, сын Иорама, царя Иудейского.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16229, 'rst66', '2ki', 8, 24, 'И почил Иорам с отцами своими, и погребен с отцами своими в городе Давидовом. И воцарился Охозия, сын его, вместо него.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16228, 'rst66', '2ki', 8, 23, 'Прочее об Иораме и обо всем, что он сделал, написано в летописи царей Иудейских.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16227, 'rst66', '2ki', 8, 22, 'И выступил Едом из‐под руки Иуды до сего дня. В то же время выступила и Ливна.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16226, 'rst66', '2ki', 8, 21, 'И пошел Иорам в Цаир, и все колесницы с ним; и встал он ночью, и поразил Идумеян, окружавших его, и начальников над колесницами, но народ убежал в шатры свои.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16225, 'rst66', '2ki', 8, 20, 'Во дни его выступил Едом из‐под руки Иуды, и поставили они над собою царя.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16224, 'rst66', '2ki', 8, 19, 'Однако ж не хотел Господь погубить Иуду, ради Давида, раба Своего, так как Он обещал дать ему светильник в детях его на все времена.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16223, 'rst66', '2ki', 8, 18, 'и ходил путем царей Израильских, как поступал дом Ахавов, потому что дочь Ахава была женою его, и делал неугодное в очах Господних.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16222, 'rst66', '2ki', 8, 17, 'Тридцати двух лет был он, когда воцарился, и восемь лет царствовал в Иерусалиме,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16221, 'rst66', '2ki', 8, 16, 'В пятый год Иорама, сына Ахавова, царя Израильского, за Иосафатом, царем Иудейским, воцарился Иорам, сын Иосафатов, царь Иудейский.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16220, 'rst66', '2ki', 8, 15, 'А на другой день он взял одеяло, намочил его водою и положил на лицо его, и он умер. И воцарился Азаил вместо него.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16219, 'rst66', '2ki', 8, 14, 'И пошел он от Елисея, и пришел к государю своему. И сказал ему <i>этот:</i> что говорил тебе Елисей? И сказал: он говорил мне, что ты выздоровеешь.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16218, 'rst66', '2ki', 8, 13, 'И сказал Азаил: что такое раб твой, пес, чтобы мог сделать такое большое дело? И сказал Елисей: указал мне Господь в тебе царя Сирии.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16217, 'rst66', '2ki', 8, 12, 'И сказал Азаил: отчего господин мой плачет? И сказал он: оттого, что я знаю, какое наделаешь ты сынам Израилевым зло; крепости их предашь огню, и юношей их мечом умертвишь, и грудных детей их побьешь, и беременных <i>женщин</i> у них разрубишь.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16216, 'rst66', '2ki', 8, 11, 'И устремил на него <i>Елисей</i> взор свой, и так оставался до того, что привел его в смущение; и заплакал человек Божий.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16215, 'rst66', '2ki', 8, 10, 'И сказал ему Елисей: пойди, скажи ему: «выздоровеешь»; однако ж открыл мне Господь, что он умрет.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16214, 'rst66', '2ki', 8, 9, 'И пошел Азаил навстречу ему, и взял дар в руку свою и всего лучшего в Дамаске, сколько могут нести сорок верблюдов, и пришел и стал пред лицо его, и сказал: сын твой, Венадад, царь Сирийский, послал меня к тебе спросить: «выздоровею ли я от сей болезни?»');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16213, 'rst66', '2ki', 8, 8, 'И сказал царь Азаилу: возьми в руку твою дар и пойди навстречу человеку Божию, и вопроси Господа чрез него, говоря: выздоровею ли я от сей болезни?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16212, 'rst66', '2ki', 8, 7, 'И пришел Елисей в Дамаск, когда Венадад, царь Сирийский, был болен. И донесли ему, говоря: пришел человек Божий сюда.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16211, 'rst66', '2ki', 8, 6, 'И спросил царь у женщины, и она рассказала ему. И дал ей царь одного из придворных, сказав: возвратить ей все принадлежащее ей и все доходы с поля, с того дня, как она оставила землю, поныне.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16210, 'rst66', '2ki', 8, 5, 'И между тем как он рассказывал царю, что тот воскресил умершего, женщина, которой сына воскресил он, просила царя о доме своем и о поле своем. И сказал Гиезий: господин мой, царь, это та самая женщина и тот самый сын ее, которого воскресил Елисей.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16209, 'rst66', '2ki', 8, 4, 'Царь тогда разговаривал с Гиезием, слугою человека Божия, и сказал: расскажи мне все замечательное, что сделал Елисей.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16208, 'rst66', '2ki', 8, 3, 'По прошествии семи лет возвратилась эта женщина из земли Филистимской и пришла просить царя о доме своем и о поле своем.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16207, 'rst66', '2ki', 8, 2, 'И встала та женщина, и сделала по слову человека Божия; и пошла она и дом ее, и жила в земле Филистимской семь лет.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16206, 'rst66', '2ki', 8, 1, 'И говорил Елисей женщине, сына которой воскресил он, и сказал: встань, и пойди, ты и дом твой, и поживи там, где можешь пожить, ибо призвал Господь голод, и он придет на сию землю на семь лет.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16205, 'rst66', '2ki', 7, 20, 'Так и сбылось с ним; и затоптал его народ в воротах, и он умер.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16204, 'rst66', '2ki', 7, 19, 'тогда отвечал этот сановник человеку Божию и сказал: «если бы Господь и открыл окна на небе, и тогда может ли это быть?» А он сказал: «увидишь твоими глазами, но есть этого не будешь».');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16203, 'rst66', '2ki', 7, 18, 'Когда говорил человек Божий царю так: «две меры ячменя по сиклю, и мера муки лучшей по сиклю будут завтра в это время у ворот Самарии»,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16202, 'rst66', '2ki', 7, 17, 'И царь поставил того сановника, на руку которого опирался, у ворот; и растоптал его народ в воротах, и он умер, как сказал человек Божий, который говорил, когда приходил к нему царь.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16201, 'rst66', '2ki', 7, 16, 'И вышел народ, и разграбил стан Сирийский, и была мера муки лучшей по сиклю, и две меры ячменя по сиклю, по слову Господню.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16200, 'rst66', '2ki', 7, 15, 'И ехали за ним до Иордана, и вот, вся дорога устлана одеждами и вещами, которые побросали Сирияне при торопливом побеге своем. И возвратились посланные, и донесли царю.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16199, 'rst66', '2ki', 7, 14, 'И взяли две пары коней, запряженных в колесницы. И послал царь вслед Сирийского войска, сказав: пойдите, посмотрите.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16198, 'rst66', '2ki', 7, 13, 'И отвечал один из служащих при нем, и сказал: пусть возьмут пять из остальных коней, которые остались в городе (из всего ополчения Израильтян только и осталось в нем, из всего ополчения Израильтян, которое погибло), и пошлем, и посмотрим.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16197, 'rst66', '2ki', 7, 12, 'И встал царь ночью, и сказал слугам своим: скажу вам, что делают с нами Сирияне. Они знают, что мы терпим голод, и вышли из стана, чтобы спрятаться в поле, думая так: «когда они выйдут из города, мы захватим их живыми и вторгнемся в город».');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16196, 'rst66', '2ki', 7, 11, 'И позвали привратников, и они передали весть в самый дворец царский.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16195, 'rst66', '2ki', 7, 10, 'И пришли, и позвали привратников городских, и рассказали им, говоря: мы ходили в стан Сирийский, и вот, нет там ни человека, ни голоса человеческого, а только кони привязанные, и ослы привязанные, и шатры, как быть им.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16194, 'rst66', '2ki', 7, 9, 'И сказали друг другу: не так мы делаем. День сей &mdash; день радостной вести, если мы замедлим и будем дожидаться утреннего света, то падет на нас вина. Пойдем же и уведомим дом царский.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16193, 'rst66', '2ki', 7, 8, 'И пришли те прокаженные к краю стана, и вошли в один шатер, и ели и пили, и взяли оттуда серебро, и золото, и одежды, и пошли и спрятали. Пошли еще в другой шатер, и там взяли, и пошли и спрятали.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16192, 'rst66', '2ki', 7, 7, 'И встали и побежали в сумерки, и оставили шатры свои, и коней своих, и ослов своих, весь стан, как он был, и побежали, спасая себя.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16191, 'rst66', '2ki', 7, 6, 'Господь сделал то, что стану Сирийскому послышался стук колесниц и ржание коней, шум войска большого. И сказали они друг другу: верно, нанял против нас царь Израильский царей Хеттейских и Египетских, чтобы пойти на нас.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16190, 'rst66', '2ki', 7, 5, 'И встали в сумерки, чтобы пойти в стан Сирийский. И пришли к краю стана Сирийского, и вот, нет там ни одного человека.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16189, 'rst66', '2ki', 7, 4, 'Если решиться нам пойти в город, то в городе голод, и мы там умрем; если же сидеть здесь, то также умрем. Пойдем лучше в стан Сирийский. Если оставят нас в живых, будем жить, а если умертвят, умрем.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16188, 'rst66', '2ki', 7, 3, 'Четыре человека прокаженных находились при входе в ворота, и говорили они друг другу: что нам сидеть здесь, ожидая смерти?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16187, 'rst66', '2ki', 7, 2, 'И отвечал сановник, на руку которого царь опирался, человеку Божию, и сказал: если бы Господь и открыл окна на небе, и тогда может ли это быть? И сказал тот: вот, увидишь глазами твоими, но есть этого не будешь.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16186, 'rst66', '2ki', 7, 1, 'И сказал Елисей: выслушайте слово Господне: так говорит Господь: завтра в это время мера муки лучшей <i>будет</i> по сиклю и две меры ячменя по сиклю у ворот Самарии.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16185, 'rst66', '2ki', 6, 33, 'Еще говорил он с ними, и вот посланный приходит к нему, и сказал: вот какое бедствие от Господа! чего мне впредь ждать от Господа?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16184, 'rst66', '2ki', 6, 32, 'Елисей же сидел в своем доме, и старцы сидели у него. И послал <i>царь</i> человека от себя. Прежде нежели пришел посланный к нему, он сказал старцам: видите ли, что этот сын убийцы послал снять с меня голову? Смотрите, когда придет посланный, затворите дверь и прижмите его дверью. А вот и топот ног господина его за ним!');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16183, 'rst66', '2ki', 6, 31, 'И сказал: пусть то и то сделает мне Бог, и еще более сделает, если останется голова Елисея, сына Сафатова, на нем сегодня.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16182, 'rst66', '2ki', 6, 30, 'Царь, выслушав слова женщины, разодрал одежды свои; и проходил он по стене, и народ видел, что вретище на самом теле его.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16181, 'rst66', '2ki', 6, 29, 'И сварили мы моего сына, и съели его. И я сказала ей на другой день: «отдай же твоего сына, и съедим его». Но она спрятала своего сына.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16180, 'rst66', '2ki', 6, 28, 'И сказал ей царь: что тебе? И сказала она: эта женщина говорила мне: «отдай своего сына, съедим его сегодня, а сына моего съедим завтра».');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16179, 'rst66', '2ki', 6, 27, 'И сказал он: если не поможет тебе Господь, из чего я помогу тебе? с гумна ли, с точила ли?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16178, 'rst66', '2ki', 6, 26, 'Однажды царь Израильский проходил по стене, и женщина с воплем говорила ему: помоги, господин мой, царь.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16177, 'rst66', '2ki', 6, 25, 'И был большой голод в Самарии, когда они осадили ее, так что ослиная голова продавалась по восьмидесяти сиклей серебра, и четвертая часть каба голубиного помета &mdash; по пяти сиклей серебра.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16176, 'rst66', '2ki', 6, 24, 'После того собрал Венадад, царь Сирийский, все войско свое и выступил, и осадил Самарию.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16175, 'rst66', '2ki', 6, 23, 'И приготовил им большой обед, и они ели и пили. И отпустил их, и пошли к государю своему. И не ходили более те полчища Сирийские в землю Израилеву.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16174, 'rst66', '2ki', 6, 22, 'И сказал он: не убивай. Разве мечом твоим и луком твоим ты пленил их, чтобы убивать их? Предложи им хлеба и воды; пусть едят и пьют, и пойдут к государю своему.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16173, 'rst66', '2ki', 6, 21, 'И сказал царь Израильский Елисею, увидев их: не избить ли их, отец мой?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16172, 'rst66', '2ki', 6, 20, 'Когда они пришли в Самарию, Елисей сказал: Господи! открой глаза им, чтобы они видели. И открыл Господь глаза их, и увидели, что они в средине Самарии.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16171, 'rst66', '2ki', 6, 19, 'И сказал им Елисей: это не та дорога и не тот город; идите за мною, и я провожу вас к тому человеку, которого вы ищете. И привел их в Самарию.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16170, 'rst66', '2ki', 6, 18, 'Когда пошли к нему Сирияне, Елисей помолился Господу и сказал: порази их слепотою. И Он поразил их слепотою по слову Елисея.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16169, 'rst66', '2ki', 6, 17, 'И молился Елисей, и говорил: Господи! открой ему глаза, чтоб он увидел. И открыл Господь глаза слуге, и он увидел, и вот, вся гора наполнена конями и колесницами огненными кругом Елисея.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16168, 'rst66', '2ki', 6, 16, 'И сказал он: не бойся, потому что тех, которые с нами, больше, нежели тех, которые с ними.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16167, 'rst66', '2ki', 6, 15, 'Поутру служитель человека Божия встал и вышел; и вот, войско вокруг города, и кони и колесницы. И сказал ему слуга его: увы! господин мой, что нам делать?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16166, 'rst66', '2ki', 6, 14, 'И послал туда коней и колесницы и много войска. И пришли ночью и окружили город.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16165, 'rst66', '2ki', 6, 13, 'И сказал он: пойдите, узнайте, где он; я пошлю и возьму его. И донесли ему и сказали: вот, он в Дофаиме.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16164, 'rst66', '2ki', 6, 12, 'И сказал один из слуг его: никто, господин мой, царь; а Елисей пророк, который у Израиля, пересказывает царю Израильскому и те слова, которые ты говоришь в спальной комнате твоей.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16163, 'rst66', '2ki', 6, 11, 'И встревожилось сердце царя Сирийского по сему случаю, и призвал он рабов своих и сказал им: скажите мне, кто из наших <i>в сношении</i> с царем Израильским?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16162, 'rst66', '2ki', 6, 10, 'И посылал царь Израильский на то место, о котором говорил ему человек Божий и предостерегал его; и сберег себя там не раз и не два.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16161, 'rst66', '2ki', 6, 9, 'И посылал человек Божий к царю Израильскому сказать: берегись проходить сим местом, ибо там Сирияне залегли.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16160, 'rst66', '2ki', 6, 8, 'Царь Сирийский пошел войною на Израильтян, и советовался со слугами своими, говоря: в таком‐то и в таком‐то месте я расположу свой стан.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16159, 'rst66', '2ki', 6, 7, 'И сказал он: возьми себе. Он протянул руку свою и взял его.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16158, 'rst66', '2ki', 6, 6, 'И сказал человек Божий: где он упал? Он указал ему место. И отрубил он <i>кусок</i> дерева и бросил туда, и всплыл топор.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16157, 'rst66', '2ki', 6, 5, 'И когда один валил бревно, топор его упал в воду. И закричал он и сказал: ах, господин мой! а он взят был на подержание!');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16156, 'rst66', '2ki', 6, 4, 'И пошел с ними, и пришли к Иордану и стали рубить деревья.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16155, 'rst66', '2ki', 6, 3, 'И сказал один: сделай милость, пойди и ты с рабами твоими. И сказал он: пойду.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16154, 'rst66', '2ki', 6, 2, 'пойдем к Иордану и возьмем оттуда каждый по одному бревну и сделаем себе там место для жительства. Он сказал: пойдите.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16153, 'rst66', '2ki', 6, 1, 'И сказали сыны пророков Елисею: вот, место, где мы живем при тебе, тесно для нас;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16152, 'rst66', '2ki', 5, 27, 'Пусть же проказа Нееманова пристанет к тебе и к потомству твоему навек. И вышел он от него <i>белый</i> от проказы, как снег.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16151, 'rst66', '2ki', 5, 26, 'И сказал он ему: разве сердце мое не сопутствовало тебе, когда обратился навстречу тебе человек тот с колесницы своей? время ли брать серебро и брать одежды, или масличные деревья и виноградники, и мелкий или крупный скот, и рабов или рабынь?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16150, 'rst66', '2ki', 5, 25, 'Когда он пришел и явился к господину своему, Елисей сказал ему: откуда, Гиезий? И сказал он: никуда не ходил раб твой.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16149, 'rst66', '2ki', 5, 24, 'Когда он пришел к холму, то взял из рук их и спрятал дома. И отпустил людей, и они ушли.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16148, 'rst66', '2ki', 5, 23, 'И сказал Нееман: возьми, пожалуй, два таланта. И упрашивал его. И завязал он два таланта серебра в два мешка и две перемены одежд и отдал двум слугам своим, и понесли перед ним.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16147, 'rst66', '2ki', 5, 22, 'Он отвечал: с миром; господин мой послал меня сказать: «вот, теперь пришли ко мне с горы Ефремовой два молодых человека из сынов пророческих; дай им талант серебра и две перемены одежд».');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16146, 'rst66', '2ki', 5, 21, 'И погнался Гиезий за Нееманом. И увидел Нееман бегущего за собою, и сошел с колесницы навстречу ему, и сказал: с миром ли?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16145, 'rst66', '2ki', 5, 20, 'И сказал Гиезий, слуга Елисея, человека Божия: вот, господин мой отказался взять из руки Неемана, этого Сириянина, то, что он приносил. Жив Господь! Побегу я за ним, и возьму у него что‐нибудь.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16144, 'rst66', '2ki', 5, 19, 'И сказал ему: иди с миром. И он отъехал от него на небольшое пространство земли.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16143, 'rst66', '2ki', 5, 18, 'только вот в чем да простит Господь раба твоего: когда пойдет господин мой в дом Риммона для поклонения там и опрется на руку мою, и поклонюсь я в доме Риммона, то, за мое поклонение в доме Риммона, да простит Господь раба твоего в случае сем.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16142, 'rst66', '2ki', 5, 17, 'И сказал Нееман: если уже не так, то пусть рабу твоему дадут земли, сколько снесут два лошака, потому что не будет впредь раб твой приносить всесожжения и жертвы другим богам, кроме Господа;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16141, 'rst66', '2ki', 5, 16, 'И сказал он: жив Господь, пред лицом Которого стою! не приму. И тот принуждал его взять, но он не согласился.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16140, 'rst66', '2ki', 5, 15, 'И возвратился к человеку Божию он и все сопровождавшие его, и пришел, и стал пред ним, и сказал: вот, я узнал, что на всей земле нет Бога, как только у Израиля; итак, прими дар от раба твоего.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16139, 'rst66', '2ki', 5, 14, 'И пошел он и окунулся в Иордане семь раз, по слову человека Божия, и обновилось тело его, как тело малого ребенка, и очистился.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16138, 'rst66', '2ki', 5, 13, 'И подошли рабы его и говорили ему, и сказали: отец мой, <i>если бы</i> что‐нибудь важное сказал тебе пророк, то не сделал ли бы ты? а тем более, когда он сказал тебе только: «омойся, и будешь чист».');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16137, 'rst66', '2ki', 5, 12, 'разве Авана и Фарфар, реки Дамасские, не лучше всех вод Израильских? разве я не мог бы омыться в них и очиститься? И оборотился и удалился в гневе.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16136, 'rst66', '2ki', 5, 11, 'И разгневался Нееман, и пошел, и сказал: вот, я думал, что он выйдет, станет и призовет имя Господа, Бога своего, и возложит руку свою на то место и снимет проказу;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16135, 'rst66', '2ki', 5, 10, 'И выслал к нему Елисей слугу сказать: пойди, омойся семь раз в Иордане, и обновится тело твое у тебя, и будешь чист.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16134, 'rst66', '2ki', 5, 9, 'И прибыл Нееман на конях своих и на колеснице своей, и остановился у входа в дом Елисеев.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16133, 'rst66', '2ki', 5, 8, 'Когда услышал Елисей, человек Божий, что царь Израильский разодрал одежды свои, то послал сказать царю: для чего ты разодрал одежды свои? пусть он придет ко мне, и узнает, что есть пророк в Израиле.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16132, 'rst66', '2ki', 5, 7, 'Царь Израильский, прочитав письмо, разодрал одежды свои и сказал: разве я Бог, чтобы умерщвлять и оживлять, что он посылает ко мне, чтобы я снял с человека проказу его? вот, теперь знайте и смотрите, что он ищет предлога враждовать против меня.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16131, 'rst66', '2ki', 5, 6, 'и принес письмо царю Израильскому, в котором было сказано: вместе с письмом сим, вот, я посылаю к тебе Неемана, слугу моего, чтобы ты снял с него проказу его.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16130, 'rst66', '2ki', 5, 5, 'И сказал царь Сирийский <i>Нееману</i>: пойди, сходи, а я пошлю письмо к царю Израильскому. Он пошел и взял с собою десять талантов серебра и шесть тысяч <i>сиклей</i> золота, и десять перемен одежд;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16129, 'rst66', '2ki', 5, 4, 'И пошел <i>Нееман</i> и передал это господину своему, говоря: так и так говорит девочка, которая из земли Израильской.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16128, 'rst66', '2ki', 5, 3, 'И сказала она госпоже своей: о, если бы господин мой побывал у пророка, который в Самарии, то он снял бы с него проказу его!');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16127, 'rst66', '2ki', 5, 2, 'Сирияне <i>однажды</i> пошли отрядами и взяли в плен из земли Израильской маленькую девочку, и она служила жене Неемановой.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16126, 'rst66', '2ki', 5, 1, 'Нееман, военачальник царя Сирийского, был великий человек у господина своего и уважаемый, потому что чрез него дал Господь победу Сириянам; и человек сей был отличный воин, но прокаженный.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16125, 'rst66', '2ki', 4, 44, 'Он подал им, и они насытились, и еще осталось, по слову Господню.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16124, 'rst66', '2ki', 4, 43, 'И сказал слуга его: что тут я дам ста человекам? И сказал он: отдай людям, пусть едят, ибо так говорит Господь: «насытятся, и останется».');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16123, 'rst66', '2ki', 4, 42, 'Пришел некто из Ваал‐Шалиши, и принес человеку Божию хлебный начаток &mdash; двадцать ячменных хлебцев и сырые зерна в шелухе. И сказал Елисей: отдай людям, пусть едят.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16122, 'rst66', '2ki', 4, 41, 'И сказал он: подайте муки. И всыпал ее в котел и сказал <i>Гиезию</i>: наливай людям, пусть едят. И не стало ничего вредного в котле.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16121, 'rst66', '2ki', 4, 40, 'И налили им есть. Но как скоро они стали есть похлебку, то подняли крик и говорили: смерть в котле, человек Божий! И не могли есть.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16120, 'rst66', '2ki', 4, 39, 'И вышел один из них в поле собирать овощи, и нашел дикое вьющееся растение, и набрал с него диких плодов полную одежду свою; и пришел и накрошил их в котел с похлебкою, так как они не знали <i>их</i>.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16119, 'rst66', '2ki', 4, 38, 'Елисей же возвратился в Галгал. И был голод в земле той, и сыны пророков сидели пред ним. И сказал он слуге своему: поставь большой котел и свари похлебку для сынов пророческих.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16118, 'rst66', '2ki', 4, 37, 'И подошла, и упала ему в ноги, и поклонилась до земли; и взяла сына своего и пошла.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16117, 'rst66', '2ki', 4, 36, 'И позвал он Гиезия и сказал: позови эту Сонамитянку. И тот позвал ее. Она пришла к нему, и он сказал: возьми сына твоего.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16116, 'rst66', '2ki', 4, 35, 'И встал и прошел по горнице взад и вперед; потом опять поднялся и простерся на нем. И чихнул ребенок раз семь, и открыл ребенок глаза свои.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16115, 'rst66', '2ki', 4, 34, 'И поднялся и лег над ребенком, и приложил свои уста к его устам, и свои глаза к его глазам, и свои ладони к его ладоням, и простерся на нем, и согрелось тело ребенка.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16114, 'rst66', '2ki', 4, 33, 'И вошел, и запер дверь за собою, и помолился Господу.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16113, 'rst66', '2ki', 4, 32, 'И вошел Елисей в дом, и вот, ребенок умерший лежит на постели его.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16112, 'rst66', '2ki', 4, 31, 'Гиезий пошел впереди их и положил жезл на лицо ребенка. Но не было ни голоса, ни ответа. И вышел навстречу ему, и донес ему, и сказал: не пробуждается ребенок.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16111, 'rst66', '2ki', 4, 30, 'И сказала мать ребенка: жив Господь, и жива душа твоя! не отстану от тебя. И он встал и пошел за нею.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16110, 'rst66', '2ki', 4, 29, 'И сказал он Гиезию: опояшь чресла твои и возьми жезл мой в руку твою, и пойди; если встретишь кого, не приветствуй его, и если кто будет тебя приветствовать, не отвечай ему; и положи посох мой на лицо ребенка.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16109, 'rst66', '2ki', 4, 28, 'И сказала она: просила ли я сына у господина моего? не говорила ли я: «не обманывай меня»?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16108, 'rst66', '2ki', 4, 27, 'Когда же пришла к человеку Божию на гору, ухватилась за ноги его. И подошел Гиезий, чтобы отвести ее; но человек Божий сказал: оставь ее, душа у нее огорчена, а Господь скрыл от меня и не объявил мне.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16107, 'rst66', '2ki', 4, 26, 'Побеги к ней навстречу и скажи ей: «здорова ли ты? здоров ли муж твой? здоров ли ребенок?» &mdash; Она сказала: здоровы.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16106, 'rst66', '2ki', 4, 25, 'И отправилась и прибыла к человеку Божию, к горе Кармил. И когда увидел человек Божий ее издали, то сказал слуге своему Гиезию: это та Сонамитянка.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16105, 'rst66', '2ki', 4, 24, 'И оседлала ослицу и сказала слуге своему: веди и иди; не останавливайся, доколе не скажу тебе.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16104, 'rst66', '2ki', 4, 23, 'Он сказал: зачем тебе ехать к нему? сегодня не новомесячие и не суббота. Но она сказала: хорошо.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16103, 'rst66', '2ki', 4, 22, 'и позвала мужа своего и сказала: пришли мне одного из слуг и одну из ослиц, я поеду к человеку Божию и возвращусь.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16102, 'rst66', '2ki', 4, 21, 'И пошла она, и положила его на постели человека Божия, и заперла его, и вышла,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16101, 'rst66', '2ki', 4, 20, 'И понес его и принес его к матери его. И он сидел на коленях у нее до полудня, и умер.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16100, 'rst66', '2ki', 4, 19, 'И сказал отцу своему: голова моя! голова моя болит! И сказал тот слуге своему: отнеси его к матери его.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16099, 'rst66', '2ki', 4, 18, 'И подрос ребенок и в один день пошел к отцу своему, к жнецам.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16098, 'rst66', '2ki', 4, 17, 'И женщина стала беременною и родила сына на другой год, в то самое время, как сказал ей Елисей.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16097, 'rst66', '2ki', 4, 16, 'И сказал он: через год, в это самое время, ты будешь держать на руках сына. И сказала она: нет, господин мой, человек Божий, не обманывай рабы твоей.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16096, 'rst66', '2ki', 4, 15, 'И сказал он: позови ее. Он позвал ее, и стала она в дверях.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16095, 'rst66', '2ki', 4, 14, 'И сказал он: что же сделать ей? И сказал Гиезий: да вот, сына нет у нее, а муж ее стар.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16094, 'rst66', '2ki', 4, 13, 'И сказал ему: скажи ей: «вот, ты так заботишься о нас; что сделать бы тебе? не нужно ли поговорить о тебе с царем, или с военачальником?» Она сказала: нет, среди своего народа я живу.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16093, 'rst66', '2ki', 4, 12, 'и сказал Гиезию, слуге своему: позови эту Сонамитянку. И позвал ее, и она стала пред ним.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16092, 'rst66', '2ki', 4, 11, 'В один день он пришел туда, и зашел в горницу, и лег там,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16091, 'rst66', '2ki', 4, 10, 'сделаем небольшую горницу над стеною и поставим ему там постель, и стол, и седалище, и светильник; и когда он будет приходить к нам, пусть заходит туда.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16090, 'rst66', '2ki', 4, 9, 'И сказала она мужу своему: вот, я знаю, что человек Божий, который проходит мимо нас постоянно, святой;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16089, 'rst66', '2ki', 4, 8, 'В один день пришел Елисей в Сонам. Там одна богатая женщина упросила его <i>к себе</i> есть хлеба; и когда он ни проходил, всегда заходил туда есть хлеба.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16088, 'rst66', '2ki', 4, 7, 'И пришла она, и пересказала человеку Божию. Он сказал: пойди, продай масло и заплати долги твои; а что останется, тем будешь жить с сыновьями твоими.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16087, 'rst66', '2ki', 4, 6, 'Когда наполнены были сосуды, она сказала сыну своему: подай мне еще сосуд. Он сказал ей: нет более сосудов. И остановилось масло.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16086, 'rst66', '2ki', 4, 5, 'И пошла от него и заперла дверь за собой и за сыновьями своими. Они подавали ей, а она наливала.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16085, 'rst66', '2ki', 4, 4, 'и пойди, запри дверь за собою и за сыновьями твоими, и наливай во все эти сосуды; полные отставляй.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16084, 'rst66', '2ki', 4, 3, 'И сказал он: пойди, попроси себе сосудов на стороне, у всех соседей твоих, сосудов порожних; набери немало,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16083, 'rst66', '2ki', 4, 2, 'И сказал ей Елисей: что мне сделать тебе? скажи мне, что есть у тебя в доме? Она сказала: нет у рабы твоей ничего в доме, кроме сосуда с елеем.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16082, 'rst66', '2ki', 4, 1, 'Одна из жен сынов пророческих с воплем говорила Елисею: раб твой, мой муж, умер; а ты знаешь, что раб твой боялся Господа; теперь пришел заимодавец взять обоих детей моих в рабы себе.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16081, 'rst66', '2ki', 3, 27, 'И взял он сына своего первенца, которому следовало царствовать вместо него, и вознес его во всесожжение на стене. Это произвело большое негодование в Израильтянах, и они отступили от него и возвратились в свою землю.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16080, 'rst66', '2ki', 3, 26, 'И увидел царь Моавитский, что битва одолевает его, и взял с собою семьсот человек, владеющих мечом, чтобы пробиться к царю Едомскому; но не могли.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16079, 'rst66', '2ki', 3, 25, 'И города разрушили, и на всякий лучший участок в поле бросили каждый по камню и закидали его; и все протоки вод запрудили и все дерева лучшие срубили, так что оставались только каменья в Кир‐Харешете. И обступили его пращники и разрушили его.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16078, 'rst66', '2ki', 3, 24, 'И пришли они к стану Израильскому. И встали Израильтяне и стали бить Моавитян, и те побежали от них, а они продолжали идти на них и бить Моавитян.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16077, 'rst66', '2ki', 3, 23, 'И сказали они: это кровь; сразились цари между собою и истребили друг друга; теперь на добычу, Моав!');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16076, 'rst66', '2ki', 3, 22, 'Поутру встали они рано, и когда солнце воссияло над водою, Моавитянам издали показалась эта вода красною, как кровь.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16075, 'rst66', '2ki', 3, 21, 'Когда Моавитяне услышали, что идут цари воевать с ними, тогда собраны были все, начиная от носящего пояс и старше, и стали на границе.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16074, 'rst66', '2ki', 3, 20, 'Поутру, когда возносят хлебное приношение, вдруг полилась вода по пути от Едома, и наполнилась земля водою.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16073, 'rst66', '2ki', 3, 19, 'и вы поразите все города укрепленные и все города главные, и все лучшие деревья срубите, и все источники водные запрудите, и все лучшие участки полевые испортите каменьями.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16072, 'rst66', '2ki', 3, 18, 'но этого мало пред очами Господа; Он и Моава предаст в руки ваши,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16071, 'rst66', '2ki', 3, 17, 'ибо так говорит Господь: не увидите ветра и не увидите дождя, а долина сия наполнится водою, которую будете пить вы и мелкий и крупный скот ваш;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16070, 'rst66', '2ki', 3, 16, 'и он сказал: так говорит Господь: делайте на сей долине рвы за рвами,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16069, 'rst66', '2ki', 3, 15, 'теперь позовите мне гуслиста. И когда гуслист играл на гуслях, тогда рука Господня коснулась Елисея,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16068, 'rst66', '2ki', 3, 14, 'И сказал Елисей: жив Господь Саваоф, пред Которым я стою! Если бы я не почитал Иосафата, царя Иудейского, то не взглянул бы на тебя и не видел бы тебя;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16067, 'rst66', '2ki', 3, 13, 'И сказал Елисей царю Израильскому: что мне и тебе? пойди к пророкам отца твоего и к пророкам матери твоей. И сказал ему царь Израильский: нет, потому что Господь созвал сюда трех царей сих, чтобы предать их в руку Моава.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16066, 'rst66', '2ki', 3, 12, 'И сказал Иосафат: есть у него слово Господне. И пошли к нему царь Израильский, и Иосафат, и царь Едомский.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16065, 'rst66', '2ki', 3, 11, 'И сказал Иосафат: нет ли здесь пророка Господня, чтобы нам вопросить Господа чрез него? И отвечал один из слуг царя Израильского и сказал: здесь Елисей, сын Сафатов, который подавал воду на руки Илии.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16064, 'rst66', '2ki', 3, 10, 'И сказал царь Израильский: ах! созвал Господь трех царей сих, чтобы предать их в руку Моава.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16063, 'rst66', '2ki', 3, 9, 'И пошел царь Израильский, и царь Иудейский, и царь Едомский, и шли они обходом семь дней, и не было воды для войска и для скота, который <i>шел</i> за ними.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16062, 'rst66', '2ki', 3, 8, 'И сказал: какою дорогою идти нам? Он сказал: дорогою пустыни Едомской.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16061, 'rst66', '2ki', 3, 7, 'и пошел и послал к Иосафату, царю Иудейскому, сказать: царь Моавитский отложился от меня, пойдешь ли со мной на войну против Моава? Он сказал: пойду; как ты, так и я, как твой народ, так и мой народ; как твои кони, так и мои кони.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16060, 'rst66', '2ki', 3, 6, 'И выступил царь Иорам в то время из Самарии и сделал смотр всем Израильтянам;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16059, 'rst66', '2ki', 3, 5, 'Но когда умер Ахав, царь Моавитский отложился от царя Израильского.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16058, 'rst66', '2ki', 3, 4, 'Меса, царь Моавитский, был богат скотом и присылал царю Израильскому по сто тысяч овец и по сто тысяч неостриженных баранов.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16057, 'rst66', '2ki', 3, 3, 'однако же грехов Иеровоама, сына Наватова, который ввел в грех Израиля, он держался, не отставал от них.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16056, 'rst66', '2ki', 3, 2, 'и делал неугодное в очах Господних, хотя не так, как отец его и мать его: он снял статую Ваала, которую сделал отец его;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16055, 'rst66', '2ki', 3, 1, 'Иорам, сын Ахава, воцарился над Израилем в Самарии в восемнадцатый год Иосафата, царя Иудейского, и царствовал двенадцать лет,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16054, 'rst66', '2ki', 2, 25, 'Отсюда пошел он на гору Кармил, а оттуда возвратился в Самарию.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16053, 'rst66', '2ki', 2, 24, 'Он оглянулся и увидел их и проклял их именем Господним. И вышли две медведицы из леса и растерзали из них сорок два ребенка.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16052, 'rst66', '2ki', 2, 23, 'И пошел он оттуда в Вефиль. Когда он шел дорогою, малые дети вышли из города и насмехались над ним и говорили ему: иди, плешивый! иди, плешивый!');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16051, 'rst66', '2ki', 2, 22, 'И вода стала здоровою до сего дня, по слову Елисея, которое он сказал.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16050, 'rst66', '2ki', 2, 21, 'И вышел он к истоку воды, и бросил туда соли, и сказал: так говорит Господь: Я сделал воду сию здоровою, не будет от нее впредь ни смерти, ни бесплодия.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16049, 'rst66', '2ki', 2, 20, 'И сказал он: дайте мне новую чашу и положите туда соли. И дали ему.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16048, 'rst66', '2ki', 2, 19, 'И сказали жители того города Елисею: вот, положение этого города хорошо, как видит господин мой; но вода нехороша и земля бесплодна.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16047, 'rst66', '2ki', 2, 18, 'и возвратились к нему, между тем как он оставался в Иерихоне, и сказал им: не говорил ли я вам: «не ходите»?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16046, 'rst66', '2ki', 2, 17, 'Но они приступали к нему долго, так что наскучили ему, и он сказал: пошлите. И послали пятьдесят человек, и искали три дня, и не нашли его,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16045, 'rst66', '2ki', 2, 16, 'и сказали ему: вот, есть <i>у нас</i>, рабов твоих, человек пятьдесят, люди сильные; пусть бы они пошли и поискали господина твоего; может быть, унес его Дух Господень и поверг его на одной из гор, или на одной из долин. Он же сказал: не посылайте.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16044, 'rst66', '2ki', 2, 15, 'И увидели его сыны пророков, которые в Иерихоне, издали, и сказали: опочил дух Илии на Елисее. И пошли навстречу ему, и поклонились ему до земли,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16043, 'rst66', '2ki', 2, 14, 'и взял милоть Илии, упавшую с него, и ударил ею по воде, и сказал: где Господь, Бог Илии, &mdash; Он Самый? И ударил по воде, и она расступилась туда и сюда, и перешел Елисей.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16042, 'rst66', '2ki', 2, 13, 'И поднял милоть Илии, упавшую с него, и пошел назад, и стал на берегу Иордана;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16041, 'rst66', '2ki', 2, 12, 'Елисей же смотрел и воскликнул: отец мой, отец мой, колесница Израиля и конница его! И не видел его более. И схватил он одежды свои и разодрал их на две части.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16040, 'rst66', '2ki', 2, 11, 'Когда они шли и дорогою разговаривали, вдруг явилась колесница огненная и кони огненные, и разлучили их обоих, и понесся Илия в вихре на небо.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16039, 'rst66', '2ki', 2, 10, 'И сказал он: трудного ты просишь. Если увидишь, как я буду взят от тебя, то будет тебе так, а если не увидишь, не будет.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16038, 'rst66', '2ki', 2, 9, 'Когда они перешли, Илия сказал Елисею: проси, что сделать тебе, прежде нежели я буду взят от тебя. И сказал Елисей: дух, который в тебе, пусть будет на мне вдвойне.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16037, 'rst66', '2ki', 2, 8, 'И взял Илия милоть свою, и свернул, и ударил ею по воде, и расступилась она туда и сюда, и перешли оба посуху.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16036, 'rst66', '2ki', 2, 7, 'Пятьдесят человек из сынов пророческих пошли и стали вдали напротив их, а они оба стояли у Иордана.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16035, 'rst66', '2ki', 2, 6, 'И сказал ему Илия: останься здесь, ибо Господь посылает меня к Иордану. И сказал он: жив Господь и жива душа твоя! не оставлю тебя. И пошли оба.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16034, 'rst66', '2ki', 2, 5, 'И подошли сыны пророков, которые в Иерихоне, к Елисею и сказали ему: знаешь ли, что сегодня Господь берет господина твоего и вознесет над главою твоею? Он сказал: я также знаю, молчите.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16033, 'rst66', '2ki', 2, 4, 'И сказал ему Илия: Елисей, останься здесь, ибо Господь посылает меня в Иерихон. И сказал он: жив Господь и жива душа твоя! не оставлю тебя. И пришли в Иерихон.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16032, 'rst66', '2ki', 2, 3, 'И вышли сыны пророков, которые в Вефиле, к Елисею и сказали ему: знаешь ли, что сегодня Господь вознесет господина твоего над главою твоею? Он сказал: я также знаю, молчите.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16031, 'rst66', '2ki', 2, 2, 'И сказал Илия Елисею: останься здесь, ибо Господь посылает меня в Вефиль. Но Елисей сказал: жив Господь и жива душа твоя! не оставлю тебя. И пошли они в Вефиль.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16030, 'rst66', '2ki', 2, 1, 'В то время, как Господь восхотел вознести Илию в вихре на небо, шел Илия с Елисеем из Галгала.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16029, 'rst66', '2ki', 1, 18, 'Прочее об Охозии, что он сделал, написано в летописи царей Израильских.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16028, 'rst66', '2ki', 1, 17, 'И умер он по слову Господню, которое изрек Илия. И воцарился Иорам вместо него, во второй год Иорама, сына Иосафатова, царя Иудейского, так как сына у того не было.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16027, 'rst66', '2ki', 1, 16, 'И сказал ему: так говорит Господь: за то, что ты посылал послов вопрошать Веельзевула, божество Аккаронское, как будто в Израиле нет Бога, чтобы вопрошать о слове Его, &mdash; с постели, на которую ты лег, не сойдешь с нее, но умрешь.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16026, 'rst66', '2ki', 1, 15, 'И сказал Ангел Господень Илии: пойди с ним, не бойся его. И он встал, и пошел с ним к царю.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16025, 'rst66', '2ki', 1, 14, 'вот, сошел огонь с неба и попалил двух пятидесятников прежних с их пятидесятками; но теперь да не будет презрена душа моя пред очами твоими!');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16024, 'rst66', '2ki', 1, 13, 'И еще послал в третий раз пятидесятника с его пятидесятком. И поднялся, и пришел пятидесятник третий, и пал на колена свои пред Илиею, и умолял его, и говорил ему: человек Божий! да не будет презрена душа моя и душа рабов твоих &mdash; сих пятидесяти &mdash; пред очами твоими;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16023, 'rst66', '2ki', 1, 12, 'И отвечал Илия и сказал ему: если я человек Божий, то пусть сойдет огонь с неба и попалит тебя и твой пятидесяток. И сошел огонь Божий с неба, и попалил его и пятидесяток его.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16022, 'rst66', '2ki', 1, 11, 'И послал к нему царь другого пятидесятника с его пятидесятком. И он стал говорить ему: человек Божий! так сказал царь: сойди скорее.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16021, 'rst66', '2ki', 1, 10, 'И отвечал Илия, и сказал пятидесятнику: если я человек Божий, то пусть сойдет огонь с неба и попалит тебя и твой пятидесяток. И сошел огонь с неба и попалил его и пятидесяток его.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16020, 'rst66', '2ki', 1, 9, 'И послал к нему пятидесятника с его пятидесятком. И он взошел к нему, когда Илия сидел на верху горы, и сказал ему: человек Божий! царь говорит: сойди.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16019, 'rst66', '2ki', 1, 8, 'Они сказали ему: человек тот весь в волосах и кожаным поясом подпоясан по чреслам своим. И сказал он: это Илия Фесвитянин.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16018, 'rst66', '2ki', 1, 7, 'И сказал им: каков видом тот человек, который вышел навстречу вам и говорил вам слова сии?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16017, 'rst66', '2ki', 1, 6, 'И сказали ему: навстречу нам вышел человек и сказал нам: пойдите, возвратитесь к царю, который послал вас, и скажите ему: так говорит Господь: разве нет Бога в Израиле, что ты посылаешь вопрошать Веельзевула, божество Аккаронское? За то с постели, на которую ты лег, не сойдешь с нее, но умрешь.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16016, 'rst66', '2ki', 1, 5, 'И возвратились к <i>Охозии</i> посланные. И он сказал им: что это вы возвратились?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16015, 'rst66', '2ki', 1, 4, 'За это так говорит Господь: с постели, на которую ты лег, не сойдешь с нее, но умрешь. И пошел Илия.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16014, 'rst66', '2ki', 1, 3, 'Тогда Ангел Господень сказал Илии Фесвитянину: встань, пойди навстречу посланным от царя Самарийского и скажи им: разве нет Бога в Израиле, что вы идете вопрошать Веельзевула, божество Аккаронское?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16013, 'rst66', '2ki', 1, 2, 'Охозия же упал чрез решетку с горницы своей, что в Самарии, и занемог. И послал послов, и сказал им: пойдите, спросите у Веельзевула, божества Аккаронского: выздоровею ли я от сей болезни?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16012, 'rst66', '2ki', 1, 1, 'И отложился Моав от Израиля по смерти Ахава.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16011, 'rst66', '1ki', 22, 53, 'он служил Ваалу и поклонялся ему и прогневал Господа, Бога Израилева, всем тем, что делал отец его.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16010, 'rst66', '1ki', 22, 52, 'и делал неугодное пред очами Господа, и ходил путем отца своего и путем матери своей и путем Иеровоама, сына Наватова, который ввел Израиля в грех:');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16009, 'rst66', '1ki', 22, 51, 'Охозия, сын Ахава, воцарился над Израилем в Самарии, в семнадцатый год Иосафата, царя Иудейского, и царствовал над Израилем два года,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16008, 'rst66', '1ki', 22, 50, 'И почил Иосафат с отцами своими и был погребен с отцами своими в городе Давида, отца своего. И воцарился Иорам, сын его, вместо него.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16007, 'rst66', '1ki', 22, 49, 'Тогда сказал Охозия, сын Ахава, Иосафату: пусть мои слуги пойдут с твоими слугами на кораблях. Но Иосафат не согласился.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16006, 'rst66', '1ki', 22, 48, 'Иосафат сделал корабли на море, чтобы ходить в Офир за золотом; но они не дошли, ибо разбились в Ецион‐Гавере.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16005, 'rst66', '1ki', 22, 47, 'В Идумее тогда не было царя; <i>был</i> наместник царский.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16004, 'rst66', '1ki', 22, 46, 'И остатки блудников, которые остались во дни отца его Асы, он истребил с земли.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16003, 'rst66', '1ki', 22, 45, 'Прочие дела Иосафата и подвиги его, какие он совершил, и как он воевал, описаны в летописи царей Иудейских.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16002, 'rst66', '1ki', 22, 44, 'Иосафат заключил мир с царем Израильским.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16001, 'rst66', '1ki', 22, 43, 'Он ходил во всем путем отца своего Асы, не сходил с него, делая угодное пред очами Господними. Только высоты не были отменены; народ еще совершал жертвы и курения на высотах.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (16000, 'rst66', '1ki', 22, 42, 'Тридцати пяти лет был Иосафат, когда воцарился, и двадцать пять лет царствовал в Иерусалиме. Имя матери его Азува, дочь Салаиля.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15999, 'rst66', '1ki', 22, 41, 'Иосафат, сын Асы, воцарился над Иудеею в четвертый год Ахава, царя Израильского.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15998, 'rst66', '1ki', 22, 40, 'И почил Ахав с отцами своими, и воцарился Охозия, сын его, вместо него.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15997, 'rst66', '1ki', 22, 39, 'Прочие дела Ахава, все, что он делал, и дом из слоновой кости, который он построил, и все города, которые он строил, описаны в летописи царей Израильских.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15996, 'rst66', '1ki', 22, 38, 'И обмыли колесницу на пруде Самарийском, и псы лизали кровь его, и омывали блудницы, по слову Господа, которое Он изрек.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15995, 'rst66', '1ki', 22, 37, 'И умер царь, и привезен был в Самарию, и похоронили царя в Самарии.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15994, 'rst66', '1ki', 22, 36, 'И провозглашено было по всему стану при захождении солнца: каждый иди в свой город, каждый в свою землю!');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15993, 'rst66', '1ki', 22, 35, 'Но сражение в тот день усилилось, и царь стоял на колеснице против Сириян, и вечером умер, и кровь из раны лилась в колесницу.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15992, 'rst66', '1ki', 22, 34, 'А один человек случайно натянул лук и ранил царя Израильского сквозь швы лат. И сказал он своему вознице: повороти назад и вывези меня из войска, ибо я ранен.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15991, 'rst66', '1ki', 22, 33, 'Начальники колесниц, видя, что это не Израильский царь, поворотили от него.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15990, 'rst66', '1ki', 22, 32, 'Начальники колесниц, увидев Иосафата, подумали: «верно, это царь Израильский», и поворотили на него, чтобы сразиться <i>с ним</i>. И закричал Иосафат.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15989, 'rst66', '1ki', 22, 31, 'Сирийский царь повелел начальникам колесниц, которых у него было тридцать два, сказав: не сражайтесь ни с малым, ни с великим, а только с одним царем Израильским.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15988, 'rst66', '1ki', 22, 30, 'И сказал царь Израильский Иосафату: я переоденусь и вступлю в сражение, а ты надень твои <i>царские</i> одежды. И переоделся царь Израильский и вступил в сражение.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15987, 'rst66', '1ki', 22, 29, 'И пошел царь Израильский и Иосафат, царь Иудейский, к Рамофу Галаадскому.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15986, 'rst66', '1ki', 22, 28, 'И сказал Михей: если возвратишься в мире, то не Господь говорил чрез меня. И сказал: слушай, весь народ!');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15985, 'rst66', '1ki', 22, 27, 'и скажите: так говорит царь: посадите этого в темницу и кормите его скудно хлебом и скудно водою, доколе я не возвращусь в мире.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15984, 'rst66', '1ki', 22, 26, 'И сказал царь Израильский: возьмите Михея и отведите его к Амону градоначальнику и к Иоасу, сыну царя,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15983, 'rst66', '1ki', 22, 25, 'И сказал Михей: вот, ты увидишь <i>это</i> в тот день, когда будешь бегать из одной комнаты в другую, чтоб укрыться.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15982, 'rst66', '1ki', 22, 24, 'И подошел Седекия, сын Хенааны, и, ударив Михея по щеке, сказал: как, неужели от меня отошел Дух Господень, чтобы говорить в тебе?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15981, 'rst66', '1ki', 22, 23, 'И вот, теперь попустил Господь духа лживого в уста всех сих пророков твоих; но Господь изрек о тебе недоброе.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15980, 'rst66', '1ki', 22, 22, 'Он сказал: я выйду и сделаюсь духом лживым в устах всех пророков его. <i>Господь</i> сказал: ты склонишь его и выполнишь это; пойди и сделай так.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15979, 'rst66', '1ki', 22, 21, 'и выступил один дух, стал пред лицом Господа и сказал: я склоню его. И сказал ему Господь: чем?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15978, 'rst66', '1ki', 22, 20, 'и сказал Господь: кто склонил бы Ахава, чтобы он пошел и пал в Рамофе Галаадском? И один говорил так, другой говорил иначе;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15977, 'rst66', '1ki', 22, 19, 'И сказал <i>Михей</i>: выслушай слово Господне: я видел Господа, сидящего на престоле Своем, и все воинство небесное стояло при Нем, по правую и по левую руку Его;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15976, 'rst66', '1ki', 22, 18, 'И сказал царь Израильский Иосафату: не говорил ли я тебе, что он не пророчествует обо мне доброго, а только худое?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15975, 'rst66', '1ki', 22, 17, 'И сказал он: я вижу всех Израильтян, рассеянных по горам, как овец, у которых нет пастыря. И сказал Господь: нет у них начальника, пусть возвращаются с миром каждый в свой дом.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15974, 'rst66', '1ki', 22, 16, 'И сказал ему царь: еще и еще заклинаю тебя, чтобы ты не говорил мне ничего, кроме истины во имя Господа.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15973, 'rst66', '1ki', 22, 15, 'И пришел он к царю. Царь сказал ему: Михей! идти ли нам войною на Рамоф Галаадский, или нет? И сказал тот ему: иди, будет успех, Господь предаст <i>его</i> в руку царя.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15972, 'rst66', '1ki', 22, 14, 'И сказал Михей: жив Господь! я изреку то, что скажет мне Господь.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15971, 'rst66', '1ki', 22, 13, 'Посланный, который пошел позвать Михея, говорил ему: вот, речи пророков единогласно <i>предвещают</i> царю доброе; пусть бы и твое слово было согласно со словом каждого из них; изреки и ты доброе.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15970, 'rst66', '1ki', 22, 12, 'И все пророки пророчествовали то же, говоря: иди на Рамоф Галаадский, будет успех, Господь предаст <i>его</i> в руку царя.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15969, 'rst66', '1ki', 22, 11, 'И сделал себе Седекия, сын Хенааны, железные рога, и сказал: так говорит Господь: сими избодешь Сириян до истребления их.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15968, 'rst66', '1ki', 22, 10, 'Царь Израильский и Иосафат, царь Иудейский, сидели каждый на седалище своем, одетые в <i>царские</i> одежды, на площади у ворот Самарии, и все пророки пророчествовали пред ними.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15967, 'rst66', '1ki', 22, 9, 'И позвал царь Израильский одного евнуха и сказал: сходи поскорее за Михеем, сыном Иемвлая.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15966, 'rst66', '1ki', 22, 8, 'И сказал царь Израильский Иосафату: есть еще один человек, чрез которого можно вопросить Господа, но я не люблю его, ибо он не пророчествует о мне доброго, а только худое, &mdash; это Михей, сын Иемвлая. И сказал Иосафат: не говори, царь, так.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15965, 'rst66', '1ki', 22, 7, 'И сказал Иосафат: нет ли здесь еще пророка Господня, чтобы нам вопросить чрез него Господа?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15964, 'rst66', '1ki', 22, 6, 'И собрал царь Израильский пророков, около четырехсот человек, и сказал им: идти ли мне войною на Рамоф Галаадский, или нет? Они сказали: иди, Господь предаст <i>его</i> в руки царя.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15963, 'rst66', '1ki', 22, 5, 'И сказал Иосафат царю Израильскому: спроси сегодня, что скажет Господь.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15962, 'rst66', '1ki', 22, 4, 'И сказал он Иосафату: пойдешь ли ты со мною на войну против Рамофа Галаадского? И сказал Иосафат царю Израильскому: как ты, так и я; как твой народ, так и мой народ; как твои кони, так и мои кони.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15961, 'rst66', '1ki', 22, 3, 'И сказал царь Израильский слугам своим: знаете ли, что Рамоф Галаадский наш? А мы так долго молчим, и не берем его из руки царя Сирийского.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15960, 'rst66', '1ki', 22, 2, 'На третий год Иосафат, царь Иудейский, пошел к царю Израильскому.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15959, 'rst66', '1ki', 22, 1, 'Прожили три года, и не было войны между Сириею и Израилем.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15958, 'rst66', '1ki', 21, 29, 'видишь, как смирился предо Мною Ахав? За то, что он смирился предо Мною, Я не наведу бед в его дни; во дни сына его наведу беды на дом его.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15957, 'rst66', '1ki', 21, 28, 'И было слово Господне к Илии Фесвитянину, и сказал Господь:');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15956, 'rst66', '1ki', 21, 27, 'Выслушав все слова сии, Ахав разодрал одежды свои, и возложил на тело свое вретище, и постился, и спал во вретище, и ходил печально.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15955, 'rst66', '1ki', 21, 26, 'он поступал весьма гнусно, последуя идолам, как делали Аморреи, которых Господь прогнал от лица сынов Израилевых.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15954, 'rst66', '1ki', 21, 25, 'Не было еще такого, как Ахав, который предался бы тому, чтобы делать неугодное пред очами Господа, к чему подущала его жена его Иезавель;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15953, 'rst66', '1ki', 21, 24, 'Кто умрет у Ахава в городе, того съедят псы, а кто умрет на поле, того расклюют птицы небесные;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15952, 'rst66', '1ki', 21, 23, 'Также и о Иезавели сказал Господь: псы съедят Иезавель за стеною Изрееля.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15951, 'rst66', '1ki', 21, 22, 'И поступлю с домом твоим так, как поступил Я с домом Иеровоама, сына Наватова, и с домом Ваасы, сына Ахиина, за оскорбление, которым ты раздражил <i>Меня</i> и ввел Израиля в грех.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15950, 'rst66', '1ki', 21, 21, 'Так <i>говорит</i> Господь: вот, Я наведу на тебя беды и вымету за тобою и истреблю у Ахава мочащегося к стене и заключенного и оставшегося в Израиле.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15949, 'rst66', '1ki', 21, 20, 'И сказал Ахав Илии: нашел ты меня, враг мой! Он сказал: нашел, ибо ты предался тому, чтобы делать неугодное пред очами Господа.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15948, 'rst66', '1ki', 21, 19, 'и скажи ему: «так говорит Господь: ты убил, и еще вступаешь в наследство?» и скажи ему: «так говорит Господь: на том месте, где псы лизали кровь Навуфея, псы будут лизать и твою кровь».');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15947, 'rst66', '1ki', 21, 18, 'встань, пойди навстречу Ахаву, царю Израильскому, который в Самарии, вот, он теперь в винограднике Навуфея, куда пришел, чтобы взять <i>его</i> во владение;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15946, 'rst66', '1ki', 21, 17, 'И было слово Господне к Илии Фесвитянину:');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15945, 'rst66', '1ki', 21, 16, 'Когда услышал Ахав, что Навуфей был убит, встал Ахав, чтобы пойти в виноградник Навуфея Изреелитянина и взять его во владение.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15944, 'rst66', '1ki', 21, 15, 'Услышав, что Навуфей побит камнями и умер, Иезавель сказала Ахаву: встань, возьми во владение виноградник Навуфея Изреелитянина, который не хотел отдать тебе за серебро; ибо Навуфея нет в живых, он умер.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15943, 'rst66', '1ki', 21, 14, 'И послали к Иезавели сказать: Навуфей побит камнями и умер.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15942, 'rst66', '1ki', 21, 13, 'и выступили два негодных человека и сели против него, и свидетельствовали на него эти недобрые люди пред народом, и говорили: Навуфей хулил Бога и царя. И вывели его за город, и побили его камнями, и он умер.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15941, 'rst66', '1ki', 21, 12, 'Объявили пост и посадили Навуфея во главе народа;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15940, 'rst66', '1ki', 21, 11, 'И сделали мужи города его, старейшины и знатные, жившие в городе его, как приказала им Иезавель, так, как писано в письмах, которые она послала к ним.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15939, 'rst66', '1ki', 21, 10, 'и против него посадите двух негодных людей, которые свидетельствовали бы на него и сказали: «ты хулил Бога и царя»; и потом выведите его, и побейте его камнями, чтобы он умер.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15938, 'rst66', '1ki', 21, 9, 'В письмах она писала так: объявите пост и посадите Навуфея на первое место в народе;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15937, 'rst66', '1ki', 21, 8, 'И написала она от имени Ахава письма, и запечатала их его печатью, и послала эти письма к старейшинам и знатным в его городе, живущим с Навуфеем.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15936, 'rst66', '1ki', 21, 7, 'И сказала ему Иезавель, жена его: что за царство было бы в Израиле, если бы ты так поступал? встань, ешь хлеб и будь спокоен; я доставлю тебе виноградник Навуфея Изреелитянина.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15935, 'rst66', '1ki', 21, 6, 'Он сказал ей: когда я стал говорить Навуфею Изреелитянину и сказал ему: «отдай мне виноградник твой за серебро, или, если хочешь, я дам тебе <i>другой</i> виноградник вместо него», тогда он сказал: «не отдам тебе виноградника моего».');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15934, 'rst66', '1ki', 21, 5, 'И вошла к нему жена его Иезавель и сказала ему: отчего встревожен дух твой, что ты и хлеба не ешь?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15933, 'rst66', '1ki', 21, 4, 'И пришел Ахав домой встревоженный и огорченный тем словом, которое сказал ему Навуфей Изреелитянин, говоря: не отдам тебе наследства отцов моих. И лег на постель свою, и отворотил лицо свое, и хлеба не ел.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15932, 'rst66', '1ki', 21, 3, 'Но Навуфей сказал Ахаву: сохрани меня Господь, чтоб я отдал тебе наследство отцов моих!');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15931, 'rst66', '1ki', 21, 2, 'И сказал Ахав Навуфею, говоря: отдай мне свой виноградник; из него будет у меня овощной сад, ибо он близко к моему дому; а вместо него я дам тебе виноградник лучше этого, или, если угодно тебе, дам тебе серебра, сколько он стоит.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15930, 'rst66', '1ki', 21, 1, 'И было после сих происшествий: у Навуфея Изреелитянина в Изрееле был виноградник подле дворца Ахава, царя Самарийского.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15929, 'rst66', '1ki', 20, 43, 'И отправился царь Израильский домой встревоженный и огорченный, и прибыл в Самарию.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15928, 'rst66', '1ki', 20, 42, 'И сказал ему: так говорит Господь: за то, что ты выпустил из рук твоих человека, заклятого Мною, душа твоя будет вместо его души, народ твой вместо его народа.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15927, 'rst66', '1ki', 20, 41, 'Он тотчас снял покрывало с глаз своих, и узнал его царь, что он из пророков.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15926, 'rst66', '1ki', 20, 40, 'Когда раб твой занялся теми и другими делами, его не стало. И сказал ему царь Израильский: таков тебе и приговор, ты сам решил.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15925, 'rst66', '1ki', 20, 39, 'Когда царь проезжал мимо, он закричал царю и сказал: раб твой ходил на сражение, и вот, один человек, отошедший в сторону, подвел ко мне человека и сказал: «стереги этого человека; если его не станет, то твоя душа будет за его душу, или ты должен будешь отвесить талант серебра».');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15924, 'rst66', '1ki', 20, 38, 'И пошел пророк и предстал пред царя на дороге, прикрыв покрывалом глаза свои.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15923, 'rst66', '1ki', 20, 37, 'И нашел он другого человека, и сказал: бей меня. Этот человек бил его до того, что изранил побоями.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15922, 'rst66', '1ki', 20, 36, 'И сказал ему: за то, что ты не слушаешь гласа Господня, убьет тебя лев, когда пойдешь от меня. Он пошел от него, и лев, встретив его, убил его.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15921, 'rst66', '1ki', 20, 35, 'Тогда один человек из сынов пророческих сказал другому, по слову Господа: бей меня. Но этот человек не согласился бить его.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15920, 'rst66', '1ki', 20, 34, 'И сказал ему <i>Венадад:</i> города, которые взял мой отец у твоего отца, я возвращу, и площади ты можешь иметь для себя в Дамаске, как отец мой имел в Самарии. <i>Ахав сказал:</i> после договора я отпущу тебя. И, заключив с ним договор, отпустил его.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15919, 'rst66', '1ki', 20, 33, 'Люди сии приняли это за <i>хороший</i> знак и поспешно подхватили слово из уст его и сказали: брат твой Венадад. И сказал он: пойдите, приведите его. И вышел к нему Венадад, и он посадил его <i>с собою</i> на колесницу.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15918, 'rst66', '1ki', 20, 32, 'И опоясали они вретищами чресла свои и возложили веревки на головы свои, и пришли к царю Израильскому и сказали: раб твой Венадад говорит: «пощади жизнь мою». Тот сказал: разве он жив? он брат мой.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15917, 'rst66', '1ki', 20, 31, 'И сказали ему слуги его: мы слышали, что цари дома Израилева цари милостивые; позволь нам возложить вретища на чресла свои и веревки на головы свои и пойти к царю Израильскому; может быть, он пощадит жизнь твою.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15916, 'rst66', '1ki', 20, 30, 'Остальные убежали в город Афек; <i>там</i> упала стена на остальных двадцать семь тысяч человек. А Венадад ушел в город и бегал из одной внутренней комнаты в другую.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15915, 'rst66', '1ki', 20, 29, 'И стояли станом одни против других семь дней. В седьмой день началась битва, и сыны Израилевы поразили сто тысяч пеших Сириян в один день.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15914, 'rst66', '1ki', 20, 28, 'И подошел человек Божий, и сказал царю Израильскому: так говорит Господь: за то, что Сирияне говорят: «Господь есть Бог гор, а не Бог долин», Я все это большое полчище предам в руку твою, чтобы вы знали, что Я &mdash; Господь.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15913, 'rst66', '1ki', 20, 27, 'Собраны были и сыны Израилевы и, взяв продовольствие, пошли навстречу им. И расположились сыны Израилевы станом пред ними, как бы два небольшие стада коз, а Сирияне наполнили землю.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15912, 'rst66', '1ki', 20, 26, 'По прошествии года Венадад собрал Сириян и выступил к Афеку, чтобы сразиться с Израилем.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15911, 'rst66', '1ki', 20, 25, 'и набери себе войска столько, сколько пало у тебя, и коней, сколько было коней, и колесниц, сколько было колесниц; и сразимся с ними на равнине, и тогда верно одолеем их. И послушался он голоса их и сделал так.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15910, 'rst66', '1ki', 20, 24, 'Итак, вот что сделай: удали царей, каждого с места его, и вместо них поставь областеначальников;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15909, 'rst66', '1ki', 20, 23, 'Слуги царя Сирийского сказали ему: Бог их есть Бог гор, поэтому они одолели нас; если же мы сразимся с ними на равнине, то верно одолеем их.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15908, 'rst66', '1ki', 20, 22, 'И подошел пророк к царю Израильскому и сказал ему: пойди, укрепись, и знай и смотри, что тебе делать, ибо по прошествии года царь Сирийский опять пойдет против тебя.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15907, 'rst66', '1ki', 20, 21, 'И вышел царь Израильский, и взял коней и колесницы, и произвел большое поражение у Сириян.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15906, 'rst66', '1ki', 20, 20, 'И поражал каждый противника своего; и побежали Сирияне, а Израильтяне погнались за ними. Венадад же, царь Сирийский, спасся на коне с всадниками.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15905, 'rst66', '1ki', 20, 19, 'Вышли из города слуги областных начальников, и войско за ними.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15904, 'rst66', '1ki', 20, 18, 'Он сказал: если за миром вышли они, то схватите их живыми, и если на войну вышли, также схватите их живыми.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15903, 'rst66', '1ki', 20, 17, 'И выступили прежде слуги областных начальников. И послал Венадад, и донесли ему, что люди вышли из Самарии.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15902, 'rst66', '1ki', 20, 16, 'И они выступили в полдень. Венадад же напился допьяна в палатках вместе с царями, с тридцатью двумя царями, помогавшими ему.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15901, 'rst66', '1ki', 20, 15, '<i>Ахав</i> счел слуг областных начальников, и нашлось их двести тридцать два; после них счел весь народ, всех сынов Израилевых, семь тысяч.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15900, 'rst66', '1ki', 20, 14, 'И сказал Ахав: чрез кого? Он сказал: так говорит Господь: через слуг областных начальников. И сказал <i>Ахав</i>: кто начнет сражение? Он сказал: ты.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15899, 'rst66', '1ki', 20, 13, 'И вот, один пророк подошел к Ахаву, царю Израильскому, и сказал: так говорит Господь: видишь ли все это большое полчище? вот, Я сегодня предам его в руку твою, чтобы ты знал, что Я Господь.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15898, 'rst66', '1ki', 20, 12, 'Услышав это слово, Венадад, который пил вместе с царями в палатках, сказал рабам своим: осаждайте город. И они осадили город.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15897, 'rst66', '1ki', 20, 11, 'И отвечал царь Израильский и сказал: скажите: пусть не хвалится подпоясывающийся, как распоясывающийся.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15896, 'rst66', '1ki', 20, 10, 'И прислал к нему Венадад сказать: пусть то и то сделают мне боги, и еще больше сделают, если праха Самарийского достанет по горсти для всех людей, идущих за мною.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15895, 'rst66', '1ki', 20, 9, 'И сказал он послам Венадада: скажите господину моему, царю: все, о чем ты присылал в первый раз к рабу твоему, я готов сделать, а этого не могу сделать. И пошли послы и отнесли ему ответ.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15894, 'rst66', '1ki', 20, 8, 'И сказали ему все старейшины и весь народ: не слушай и не соглашайся.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15893, 'rst66', '1ki', 20, 7, 'И созвал царь Израильский всех старейшин земли и сказал: замечайте и смотрите, он замышляет зло; когда он присылал ко мне за жёнами моими, и сыновьями моими, и серебром моим, и золотом моим, я ему не отказал.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15892, 'rst66', '1ki', 20, 6, 'поэтому я завтра, к этому времени, пришлю к тебе рабов моих, чтобы они осмотрели твой дом и дома служащих при тебе, и все дорогое для глаз твоих взяли в свои руки и унесли.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15891, 'rst66', '1ki', 20, 5, 'И опять пришли послы и сказали: так говорит Венадад: я послал к тебе сказать: «серебро твое, и золото твое, и жён твоих, и сыновей твоих отдай мне»;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15890, 'rst66', '1ki', 20, 4, 'И отвечал царь Израильский и сказал: да будет по слову твоему, господин мой, царь: я и все мое &mdash; твое.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15889, 'rst66', '1ki', 20, 3, 'и сказал ему: так говорит Венадад: серебро твое и золото твое &mdash; мои, и жены твои и лучшие сыновья твои &mdash; мои.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15888, 'rst66', '1ki', 20, 2, 'И послал послов к Ахаву, царю Израильскому, в город,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15887, 'rst66', '1ki', 20, 1, 'Венадад, царь Сирийский, собрал все свое войско, и с ним были тридцать два царя, и кони и колесницы, и пошел, осадил Самарию и воевал против нее.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15886, 'rst66', '1ki', 19, 21, 'Он, отойдя от него, взял пару волов и заколол их и, зажегши плуг волов, изжарил мясо их, и роздал людям, и они ели. А сам встал и пошел за Илиею, и стал служить ему.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15885, 'rst66', '1ki', 19, 20, 'И оставил <i>Елисей</i> волов, и побежал за Илиею, и сказал: позволь мне поцеловать отца моего и мать мою, и я пойду за тобою. Он сказал ему: пойди и приходи назад, ибо что сделал я тебе?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15884, 'rst66', '1ki', 19, 19, 'И пошел он оттуда, и нашел Елисея, сына Сафатова, когда он орал; двенадцать пар <i>волов</i> было у него, и сам он был при двенадцатой. Илия, проходя мимо него, бросил на него милоть свою.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15883, 'rst66', '1ki', 19, 18, 'Впрочем, Я оставил между Израильтянами семь тысяч <i>мужей</i>; всех сих колени не преклонялись пред Ваалом, и всех сих уста не лобызали его.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15882, 'rst66', '1ki', 19, 17, 'кто убежит от меча Азаилова, того умертвит Ииуй; а кто спасется от меча Ииуева, того умертвит Елисей.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15881, 'rst66', '1ki', 19, 16, 'а Ииуя, сына Намессиина, помажь в царя над Израилем; Елисея же, сына Сафатова, из Авел‐Мехолы, помажь в пророка вместо себя;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15880, 'rst66', '1ki', 19, 15, 'И сказал ему Господь: пойди обратно своею дорогою через пустыню в Дамаск, и когда придешь, то помажь Азаила в царя над Сириею,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15879, 'rst66', '1ki', 19, 14, 'Он сказал: возревновал я о Господе, Боге Саваофе, ибо сыны Израилевы оставили завет Твой, разрушили жертвенники Твои и пророков Твоих убили мечом; остался я один, но и моей души ищут, чтоб отнять ее.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15878, 'rst66', '1ki', 19, 13, 'Услышав <i>сие</i>, Илия закрыл лицо свое милотью своею, и вышел, и стал у входа в пещеру. И был к нему голос и сказал ему: что ты здесь, Илия?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15877, 'rst66', '1ki', 19, 12, 'после землетрясения огонь, но не в огне Господь; после огня веяние тихого ветра.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15876, 'rst66', '1ki', 19, 11, 'И сказал: выйди и стань на горе пред лицом Господним, и вот, Господь пройдет, и большой и сильный ветер, раздирающий горы и сокрушающий скалы пред Господом, но не в ветре Господь; после ветра землетрясение, но не в землетрясении Господь;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15875, 'rst66', '1ki', 19, 10, 'Он сказал: возревновал я о Господе, Боге Саваофе, ибо сыны Израилевы оставили завет Твой, разрушили Твои жертвенники и пророков Твоих убили мечом; остался я один, но и моей души ищут, чтобы отнять ее.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15874, 'rst66', '1ki', 19, 9, 'И вошел он там в пещеру и ночевал в ней. И вот, было к нему слово Господне, и сказал ему <i>Господь:</i> что ты здесь, Илия?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15873, 'rst66', '1ki', 19, 8, 'И встал он, поел и напился, и, подкрепившись той пищею, шел сорок дней и сорок ночей до горы Божией Хорива.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15872, 'rst66', '1ki', 19, 7, 'И возвратился Ангел Господень во второй раз, коснулся его и сказал: встань, ешь, ибо дальняя дорога пред тобою.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15871, 'rst66', '1ki', 19, 6, 'И взглянул Илия, и вот, у изголовья его печеная лепешка и кувшин воды. Он поел и напился и опять заснул.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15870, 'rst66', '1ki', 19, 5, 'И лег и заснул под можжевеловым кустом. И вот, Ангел коснулся его и сказал ему: встань, ешь.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15869, 'rst66', '1ki', 19, 4, 'А сам отошел в пустыню на день пути и, придя, сел под можжевеловым кустом, и просил смерти себе и сказал: довольно уже, Господи; возьми душу мою, ибо я не лучше отцов моих.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15868, 'rst66', '1ki', 19, 3, 'Увидев это, он встал и пошел, чтобы спасти жизнь свою, и пришел в Вирсавию, которая в Иудее, и оставил отрока своего там.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15867, 'rst66', '1ki', 19, 2, 'И послала Иезавель посланца к Илии сказать: пусть то и то сделают мне боги, и еще больше сделают, если я завтра к этому времени не сделаю с твоею душою того, что <i>сделано</i> с душою каждого из них.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15866, 'rst66', '1ki', 19, 1, 'И пересказал Ахав Иезавели всё, что сделал Илия, и то, что он убил всех пророков мечом.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15865, 'rst66', '1ki', 18, 46, 'И была на Илии рука Господня. Он опоясал чресла свои и бежал пред Ахавом до самого Изрееля.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15864, 'rst66', '1ki', 18, 45, 'Между тем небо сделалось мрачно от туч и от ветра, и пошел большой дождь. Ахав же сел в колесницу и поехал в Изреель.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15863, 'rst66', '1ki', 18, 44, 'В седьмой раз тот сказал: вот, небольшое облако поднимается от моря, величиною в ладонь человеческую. Он сказал: пойди, скажи Ахаву: «запрягай <i>колесницу твою</i> и поезжай, чтобы не застал тебя дождь».');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15862, 'rst66', '1ki', 18, 43, 'и сказал отроку своему: пойди, посмотри к морю. Тот пошел и посмотрел, и сказал: ничего нет. Он сказал: продолжай <i>это</i> до семи раз.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15861, 'rst66', '1ki', 18, 42, 'И пошел Ахав есть и пить, а Илия взошел на верх Кармила и наклонился к земле, и положил лицо свое между коленами своими,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15860, 'rst66', '1ki', 18, 41, 'И сказал Илия Ахаву: пойди, ешь и пей, ибо слышен шум дождя.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15859, 'rst66', '1ki', 18, 40, 'И сказал им Илия: схватите пророков Вааловых, чтобы ни один из них не укрылся. И схватили их, и отвел их Илия к потоку Киссону и заколол их там.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15858, 'rst66', '1ki', 18, 39, 'Увидев <i>это</i>, весь народ пал на лицо свое и сказал: Господь есть Бог, Господь есть Бог!');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15857, 'rst66', '1ki', 18, 38, 'И ниспал огонь Господень и пожрал всесожжение, и дрова, и камни, и прах, и поглотил воду, которая во рве.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15856, 'rst66', '1ki', 18, 37, 'Услышь меня, Господи, услышь меня! Да познает народ сей, что Ты, Господи, Бог, и Ты обратишь сердце их <i>к Тебе</i>.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15855, 'rst66', '1ki', 18, 36, 'Во время приношения вечерней жертвы подошел Илия пророк и сказал: Господи, Боже Авраамов, Исааков и Израилев! Да познают в сей день, что Ты один Бог в Израиле, и что я раб Твой и сделал всё по слову Твоему.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15854, 'rst66', '1ki', 18, 35, 'и вода полилась вокруг жертвенника, и ров наполнился водою.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15853, 'rst66', '1ki', 18, 34, 'и сказал: наполните четыре ведра воды и выливайте на всесожигаемую жертву и на дрова. Потом сказал: повторите. И они повторили. И сказал: сделайте <i>то же</i> в третий раз. И сделали в третий раз,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15852, 'rst66', '1ki', 18, 33, 'и положил дрова, и рассек тельца, и возложил его на дрова,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15851, 'rst66', '1ki', 18, 32, 'И построил из сих камней жертвенник во имя Господа, и сделал вокруг жертвенника ров, вместимостью в две саты зерен,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15850, 'rst66', '1ki', 18, 31, 'И взял Илия двенадцать камней, по числу колен сынов Иакова, которому Господь сказал так: Израиль будет имя твое.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15849, 'rst66', '1ki', 18, 30, 'Тогда Илия сказал всему народу: подойдите ко мне. И подошел весь народ к нему. Он восстановил разрушенный жертвенник Господень.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15848, 'rst66', '1ki', 18, 29, 'Прошел полдень, а они всё еще бесновались до самого времени вечернего жертвоприношения; но не было ни голоса, ни ответа, ни слуха.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15847, 'rst66', '1ki', 18, 28, 'И стали они кричать громким голосом, и кололи себя по своему обыкновению ножами и копьями, так что кровь лилась по ним.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15846, 'rst66', '1ki', 18, 27, 'В полдень Илия стал смеяться над ними и говорил: кричите громким голосом, ибо он бог; может быть, он задумался, или занят чем‐либо, или в дороге, а может быть, и спит, так он проснется!');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15845, 'rst66', '1ki', 18, 26, 'И взяли они тельца, который дан был им, и приготовили, и призывали имя Ваала от утра до полудня, говоря: Ваале, услышь нас! Но не было ни голоса, ни ответа. И скакали они у жертвенника, который сделали.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15844, 'rst66', '1ki', 18, 25, 'И сказал Илия пророкам Вааловым: выберите себе одного тельца и приготовьте вы прежде, ибо вас много; и призовите имя бога вашего, но огня не подкладывайте.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15843, 'rst66', '1ki', 18, 24, 'и призовите вы имя бога вашего, а я призову имя Господа, Бога моего. Тот Бог, Который даст ответ посредством огня, есть Бог. И отвечал весь народ и сказал: хорошо.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15842, 'rst66', '1ki', 18, 23, 'Пусть дадут нам двух тельцов, и пусть они выберут себе одного тельца, и рассекут его, и положат на дрова, но огня пусть не подкладывают; а я приготовлю другого тельца и положу на дрова, а огня не подложу;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15841, 'rst66', '1ki', 18, 22, 'И сказал Илия народу: я один остался пророк Господень, а пророков Вааловых четыреста пятьдесят человек.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15840, 'rst66', '1ki', 18, 21, 'И подошел Илия ко всему народу и сказал: долго ли вам хромать на оба колена? если Господь есть Бог, то последуйте Ему; а если Ваал, то ему последуйте. И не отвечал народ ему ни слова.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15839, 'rst66', '1ki', 18, 20, 'И послал Ахав ко всем сынам Израилевым и собрал всех пророков на гору Кармил.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15838, 'rst66', '1ki', 18, 19, 'теперь пошли и собери ко мне всего Израиля на гору Кармил, и четыреста пятьдесят пророков Вааловых, и четыреста пророков дубравных, питающихся от стола Иезавели.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15837, 'rst66', '1ki', 18, 18, 'И сказал Илия: не я смущаю Израиля, а ты и дом отца твоего, тем, что вы презрели повеления Господни и идете вслед Ваалам;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15836, 'rst66', '1ki', 18, 17, 'Когда Ахав увидел Илию, то сказал Ахав ему: ты ли это, смущающий Израиля?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15835, 'rst66', '1ki', 18, 16, 'И пошел Авдий навстречу Ахаву и донес ему. И пошел Ахав навстречу Илии.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15834, 'rst66', '1ki', 18, 15, 'И сказал Илия: жив Господь Саваоф, пред Которым я стою! сегодня я покажусь ему.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15833, 'rst66', '1ki', 18, 14, 'А ты теперь говоришь: «пойди, скажи господину твоему: Илия здесь»; он убьет меня.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15832, 'rst66', '1ki', 18, 13, 'Разве не сказано господину моему, что я сделал, когда Иезавель убивала пророков Господних, как я скрывал сто человек пророков Господних, по пятидесяти человек, в пещерах и питал их хлебом и водою?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15831, 'rst66', '1ki', 18, 12, 'Когда я пойду от тебя, тогда Дух Господень унесет тебя, не знаю, куда; и если я пойду уведомить Ахава, и он не найдет тебя, то он убьет меня; а раб твой богобоязнен от юности своей.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15830, 'rst66', '1ki', 18, 11, 'а ты теперь говоришь: «пойди, скажи господину твоему: Илия здесь».');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15829, 'rst66', '1ki', 18, 10, 'Жив Господь, Бог твой! нет ни одного народа и царства, куда бы не посылал государь мой искать тебя; и когда ему говорили, <i>что тебя</i> нет, он брал клятву с того царства и народа, что не могли отыскать тебя;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15828, 'rst66', '1ki', 18, 9, 'Он сказал: чем я провинился, что ты предаешь раба твоего в руки Ахава, чтоб умертвить меня?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15827, 'rst66', '1ki', 18, 8, 'Тот сказал ему: я; пойди, скажи господину твоему: «Илия здесь».');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15826, 'rst66', '1ki', 18, 7, 'Когда Авдий шел дорогою, вот, навстречу ему идет Илия. Он узнал его и пал на лицо свое и сказал: ты ли это, господин мой Илия?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15825, 'rst66', '1ki', 18, 6, 'И разделили они между собою землю, чтобы обойти ее: Ахав особо пошел одною дорогою, и Авдий особо пошел другою дорогою.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15824, 'rst66', '1ki', 18, 5, 'И сказал Ахав Авдию: пойди по земле ко всем источникам водным и ко всем потокам на земле, не найдем ли где травы, чтобы нам прокормить коней и лошаков и не лишиться скота.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15823, 'rst66', '1ki', 18, 4, 'и когда Иезавель истребляла пророков Господних, Авдий взял сто пророков, и скрывал их, по пятидесяти человек, в пещерах, и питал их хлебом и водою.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15822, 'rst66', '1ki', 18, 3, 'И призвал Ахав Авдия, начальствовавшего над дворцом. Авдий же был человек весьма богобоязненный,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15821, 'rst66', '1ki', 18, 2, 'И пошел Илия, чтобы показаться Ахаву. Голод же сильный был в Самарии.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15820, 'rst66', '1ki', 18, 1, 'По прошествии многих дней было слово Господне к Илии в третий год: пойди и покажись Ахаву, и Я дам дождь на землю.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15819, 'rst66', '1ki', 17, 24, 'И сказала та женщина Илии: теперь‐то я узнала, что ты человек Божий, и что слово Господне в устах твоих истинно.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15818, 'rst66', '1ki', 17, 23, 'И взял Илия отрока, и свел его из горницы в дом, и отдал его матери его, и сказал Илия: смотри, сын твой жив.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15817, 'rst66', '1ki', 17, 22, 'И услышал Господь голос Илии, и возвратилась душа отрока сего в него, и он ожил.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15816, 'rst66', '1ki', 17, 21, 'И простершись над отроком трижды, он воззвал к Господу и сказал: Господи Боже мой! да возвратится душа отрока сего в него!');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15815, 'rst66', '1ki', 17, 20, 'и воззвал к Господу и сказал: Господи Боже мой! неужели Ты и вдове, у которой я пребываю, сделаешь зло, умертвив сына ее?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15814, 'rst66', '1ki', 17, 19, 'И сказал он ей: дай мне сына твоего. И взял его с рук ее, и понес его в горницу, где он жил, и положил его на свою постель,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15813, 'rst66', '1ki', 17, 18, 'И сказала она Илии: что мне и тебе, человек Божий? ты пришел ко мне напомнить грехи мои и умертвить сына моего.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15812, 'rst66', '1ki', 17, 17, 'После этого заболел сын этой женщины, хозяйки дома, и болезнь его была так сильна, что не осталось в нем дыхания.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15811, 'rst66', '1ki', 17, 16, 'Мука в кадке не истощалась, и масло в кувшине не убывало, по слову Господа, которое Он изрек чрез Илию.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15810, 'rst66', '1ki', 17, 15, 'И пошла она и сделала так, как сказал Илия; и кормилась она, и он, и дом ее несколько времени.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15809, 'rst66', '1ki', 17, 14, 'ибо так говорит Господь, Бог Израилев: мука в кадке не истощится, и масло в кувшине не убудет до того дня, когда Господь даст дождь на землю.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15808, 'rst66', '1ki', 17, 13, 'И сказал ей Илия: не бойся, пойди, сделай, что ты сказала; но прежде из этого сделай небольшой опреснок для меня и принеси мне; а для себя и для своего сына сделаешь после;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15807, 'rst66', '1ki', 17, 12, 'Она сказала: жив Господь, Бог твой! у меня ничего нет печеного, а только есть горсть муки в кадке и немного масла в кувшине; и вот, я наберу полена два дров, и пойду, и приготовлю это для себя и для сына моего; съедим это и умрем.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15806, 'rst66', '1ki', 17, 11, 'И пошла она, чтобы взять; а он закричал вслед ей и сказал: возьми для меня и кусок хлеба в руки свои.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15805, 'rst66', '1ki', 17, 10, 'И встал он и пошел в Сарепту; и когда пришел к воротам города, вот, там женщина вдова собирает дрова. И подозвал он ее и сказал: дай мне немного воды в сосуде напиться.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15804, 'rst66', '1ki', 17, 9, 'встань и пойди в Сарепту Сидонскую, и оставайся там; Я повелел там женщине вдове кормить тебя.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15803, 'rst66', '1ki', 17, 8, 'И было к нему слово Господне:');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15802, 'rst66', '1ki', 17, 7, 'По прошествии некоторого времени этот поток высох, ибо не было дождя на землю.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15801, 'rst66', '1ki', 17, 6, 'И во&#769;роны приносили ему хлеб и мясо поутру, и хлеб и мясо по вечеру, а из потока он пил.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15800, 'rst66', '1ki', 17, 5, 'И пошел он и сделал по слову Господню; пошел и остался у потока Хорафа, что против Иордана.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15799, 'rst66', '1ki', 17, 4, 'из этого потока ты будешь пить, а во&#769;ронам Я повелел кормить тебя там.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15798, 'rst66', '1ki', 17, 3, 'пойди отсюда и обратись на восток и скройся у потока Хорафа, что против Иордана;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15797, 'rst66', '1ki', 17, 2, 'И было к нему слово Господне:');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15796, 'rst66', '1ki', 17, 1, 'И сказал Илия Фесвитянин, из жителей Галаадских, Ахаву: жив Господь, Бог Израилев, пред Которым я стою! в сии годы не будет ни росы, ни дождя, разве только по моему слову.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15795, 'rst66', '1ki', 16, 34, 'В его дни Ахиил Вефилянин построил Иерихон: на первенце своем Авираме он положил основание его и на младшем своем <i>сыне</i> Сегубе поставил ворота его, по слову Господа, которое Он изрек чрез Иисуса, сына Навина.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15794, 'rst66', '1ki', 16, 33, 'И сделал Ахав дубраву, и более всех царей Израильских, которые были прежде него, Ахав делал то, что раздражает Господа, Бога Израилева.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15793, 'rst66', '1ki', 16, 32, 'И поставил он Ваалу жертвенник в капище Ваала, который построил в Самарии.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15792, 'rst66', '1ki', 16, 31, 'Мало было для него впадать в грехи Иеровоама, сына Наватова; он взял себе в жену Иезавель, дочь Ефваала, царя Сидонского, и стал служить Ваалу и поклоняться ему.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15791, 'rst66', '1ki', 16, 30, 'И делал Ахав, сын Амврия, неугодное пред очами Господа более всех бывших прежде него.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15790, 'rst66', '1ki', 16, 29, 'Ахав, сын Амвриев, воцарился над Израилем в тридцать восьмой год Асы, царя Иудейского, и царствовал Ахав, сын Амврия, над Израилем в Самарии двадцать два года.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15789, 'rst66', '1ki', 16, 28, 'И почил Амврий с отцами своими и погребен в Самарии. И воцарился Ахав, сын его, вместо него.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15788, 'rst66', '1ki', 16, 27, 'Прочие дела Амврия, которые он сделал, и мужество, которое он показал, описаны в летописи царей Израильских.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15787, 'rst66', '1ki', 16, 26, 'Он во всем ходил путем Иеровоама, сына Наватова, и во грехах его, которыми тот ввел в грех Израильтян, чтобы прогневлять Господа, Бога Израилева, идолами своими.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15786, 'rst66', '1ki', 16, 25, 'И делал Амврий неугодное пред очами Господа и поступал хуже всех бывших перед ним.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15785, 'rst66', '1ki', 16, 24, 'И купил Амврий гору Семерон у Семира за два таланта серебра, и застроил гору, и назвал построенный им город Самариею, по имени Семира, владельца горы.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15784, 'rst66', '1ki', 16, 23, 'В тридцать первый год Асы, царя Иудейского, воцарился Амврий над Израилем <i>и царствовал</i> двенадцать лет. В Фирце он царствовал шесть лет.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15783, 'rst66', '1ki', 16, 22, 'И одержал верх народ, который за Амврия, над народом, который за Фамния, сына Гонафова, и умер Фамний, и воцарился Амврий.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15782, 'rst66', '1ki', 16, 21, 'Тогда разделился народ Израильский надвое: половина народа стояла за Фамния, сына Гонафова, чтобы воцарить его, а половина за Амврия.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15781, 'rst66', '1ki', 16, 20, 'Прочие дела Замврия и заговор его, который он составил, описаны в летописи царей Израильских.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15780, 'rst66', '1ki', 16, 19, 'за свои грехи, в чем он согрешил, делая неугодное пред очами Господними, ходя путем Иеровоама и во грехах его, которые тот сделал, чтобы ввести Израиля в грех.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15779, 'rst66', '1ki', 16, 18, 'Когда увидел Замврий, что город взят, вошел во внутреннюю комнату царского дома и зажег за собою царский дом огнем и погиб');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15778, 'rst66', '1ki', 16, 17, 'И отступили Амврий и все Израильтяне с ним от Гавафона и осадили Фирцу.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15777, 'rst66', '1ki', 16, 16, 'Когда народ осаждавший услышал, что Замврий сделал заговор и умертвил царя, то все Израильтяне воцарили Амврия, военачальника, над Израилем в тот же день, в стане.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15776, 'rst66', '1ki', 16, 15, 'В двадцать седьмой год Асы, царя Иудейского, воцарился Замврий и царствовал семь дней в Фирце, когда народ осаждал Гавафон Филистимский.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15775, 'rst66', '1ki', 16, 14, 'Прочие дела Илы, все, что он сделал, описано в летописи царей Израильских.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15774, 'rst66', '1ki', 16, 13, 'за все грехи Ваасы и за грехи Илы, сына его, которые они сами делали и которыми вводили Израиля в грех, раздражая Господа, Бога Израилева, своими идолами.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15773, 'rst66', '1ki', 16, 12, 'И истребил Замврий весь дом Ваасы, по слову Господа, которое Он изрек о Ваасе чрез Иуя пророка,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15772, 'rst66', '1ki', 16, 11, 'Когда он воцарился и сел на престоле его, то истребил весь дом Ваасы, не оставив ему мочащегося к стене, ни родственников его, ни друзей его.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15771, 'rst66', '1ki', 16, 10, 'тогда вошел Замврий, поразил его и умертвил его, в двадцать седьмой год Асы, царя Иудейского, и воцарился вместо него.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15770, 'rst66', '1ki', 16, 9, 'И составил против него заговор раб его Замврий, начальствовавший над половиною колесниц. Когда он в Фирце напился допьяна в доме Арсы, начальствующего над дворцом в Фирце,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15769, 'rst66', '1ki', 16, 8, 'В двадцать шестой год Асы, царя Иудейского, воцарился Ила, сын Ваасы, над Израилем в Фирце, <i>и царствовал</i> два года.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15768, 'rst66', '1ki', 16, 7, 'Но чрез Иуя, сына Ананиева, уже было <i>сказано</i> слово Господне о Ваасе и о доме его и о всем зле, какое он делал пред очами Господа, раздражая Его делами рук своих, подражая дому Иеровоамову, за что он истреблен был.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15767, 'rst66', '1ki', 16, 6, 'И почил Вааса с отцами своими, и погребен в Фирце. И воцарился Ила, сын его, вместо него.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15766, 'rst66', '1ki', 16, 5, 'Прочие дела Ваасы, всё, что он сделал, и подвиги его описаны в летописи царей Израильских.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15765, 'rst66', '1ki', 16, 4, 'кто умрет у Ваасы в городе, того съедят псы; а кто умрет у него на поле, того склюют птицы небесные.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15764, 'rst66', '1ki', 16, 3, 'вот, Я отвергну дом Ваасы и дом потомства его и сделаю с домом твоим то же, что с домом Иеровоама, сына Наватова;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15763, 'rst66', '1ki', 16, 2, 'за то, что Я поднял тебя из праха и сделал тебя вождем народа Моего, Израиля, ты же пошел путем Иеровоама и ввел в грех народ Мой, Израильтян, чтобы он прогневлял Меня грехами своими,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15762, 'rst66', '1ki', 16, 1, 'И было слово Господне к Иую, сыну Ананиеву, о Ваасе:');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15761, 'rst66', '1ki', 15, 34, 'И делал неугодное пред очами Господними и ходил путем Иеровоама и во грехах его, которыми тот ввел в грех Израиля.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15760, 'rst66', '1ki', 15, 33, 'В третий год Асы, царя Иудейского, воцарился Вааса, сын Ахии, над всеми Израильтянами в Фирце <i>и царствовал</i> двадцать четыре года.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15759, 'rst66', '1ki', 15, 32, 'И война была между Асою и Ваасою, царем Израильским, во все дни их.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15758, 'rst66', '1ki', 15, 31, 'Прочие дела Навата, всё, что он сделал, описано в летописи царей Израильских.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15757, 'rst66', '1ki', 15, 30, 'за грехи Иеровоама, которые он сам делал и которыми ввел в грех Израиля, за оскорбление, которым он прогневал Господа, Бога Израилева.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15756, 'rst66', '1ki', 15, 29, 'Когда он воцарился, то избил весь дом Иеровоамов, не оставил ни души у Иеровоама, доколе не истребил его, по слову Господа, которое Он изрек чрез раба Своего Ахию Силомлянина,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15755, 'rst66', '1ki', 15, 28, 'и умертвил его Вааса в третий год Асы, царя Иудейского, и воцарился вместо него.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15754, 'rst66', '1ki', 15, 27, 'И сделал против него заговор Вааса, сын Ахии, из дома Иссахарова, и убил его Вааса при Гавафоне Филистимском, когда Нават и все Израильтяне осаждали Гавафон:');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15753, 'rst66', '1ki', 15, 26, 'И делал он неугодное пред очами Господа, ходил путем отца своего и во грехах его, которыми тот ввел Израиля в грех.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15752, 'rst66', '1ki', 15, 25, 'Нават же, сын Иеровоамов, воцарился над Израилем во второй год Асы, царя Иудейского, и царствовал над Израилем два года.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15751, 'rst66', '1ki', 15, 24, 'И почил Аса с отцами своими и погребен с отцами своими в городе Давида, отца своего. И воцарился Иосафат, сын его, вместо него.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15750, 'rst66', '1ki', 15, 23, 'Все прочие дела Асы и все подвиги его, и всё, что он сделал, и города, которые он построил, описаны в летописи царей Иудейских, кроме того, что в старости своей он был болен ногами.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15749, 'rst66', '1ki', 15, 22, 'Царь же Аса созвал всех Иудеев, никого не исключая, и вынесли они из Рамы камни и дерева, которые Вааса употреблял для строения. И выстроил из них царь Аса Гиву Вениаминову и Мицпу.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15748, 'rst66', '1ki', 15, 21, 'Услышав <i>о сем</i>, Вааса перестал строить Раму и возвратился в Фирцу.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15747, 'rst66', '1ki', 15, 20, 'И послушался Венадад царя Асы, и послал военачальников своих против городов Израильских, и поразил Аин и Дан и Авел‐Беф‐Мааху и весь Киннероф, по всей земле Неффалима.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15746, 'rst66', '1ki', 15, 19, 'союз да будет между мною и между тобою, <i>как был</i> между отцом моим и между отцом твоим; вот, я посылаю тебе в дар серебро и золото; расторгни союз твой с Ваасою, царем Израильским, чтобы он отошел от меня.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15745, 'rst66', '1ki', 15, 18, 'И взял Аса все серебро и золото, остававшееся в сокровищницах дома Господня и в сокровищницах дома царского, и дал его в руки слуг своих, и послал их царь Аса к Венададу, сыну Тавримона, сына Хезионова, царю Сирийскому, жившему в Дамаске, и сказал:');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15744, 'rst66', '1ki', 15, 17, 'И вышел Вааса, царь Израильский, против Иудеи и начал строить Раму, чтобы никто не выходил и не уходил к Асе, царю Иудейскому.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15743, 'rst66', '1ki', 15, 16, 'И война была между Асою и Ваасою, царем Израильским, во все дни их.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15742, 'rst66', '1ki', 15, 15, 'И внес он в дом Господень вещи, посвященные отцом его, и вещи, посвященные им: серебро и золото и сосуды.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15741, 'rst66', '1ki', 15, 14, 'Высоты же не были уничтожены. Но сердце Асы было предано Господу во все дни его.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15740, 'rst66', '1ki', 15, 13, 'и даже мать свою Ану лишил звания царицы за то, что она сделала истукан Астарты; и изрубил Аса истукан ее и сжег у потока Кедрона.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15739, 'rst66', '1ki', 15, 12, 'Он изгнал блудников из земли и отверг всех идолов, которых сделали отцы его,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15738, 'rst66', '1ki', 15, 11, 'Аса делал угодное пред очами Господа, как Давид, отец его.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15737, 'rst66', '1ki', 15, 10, 'и сорок один год царствовал в Иерусалиме; имя матери его Ана, дочь Авессалома.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15736, 'rst66', '1ki', 15, 9, 'В двадцатый год <i>царствования</i> Иеровоама, царя Израильского, воцарился Аса над Иудеями');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15735, 'rst66', '1ki', 15, 8, 'И почил Авия с отцами своими, и похоронили его в городе Давидовом. И воцарился Аса, сын его, вместо него.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15734, 'rst66', '1ki', 15, 7, 'Прочие дела Авии, всё, что он сделал, описано в летописи царей Иудейских. И была война между Авиею и Иеровоамом.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15733, 'rst66', '1ki', 15, 6, 'И война была между Ровоамом и Иеровоамом во все дни жизни их.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15732, 'rst66', '1ki', 15, 5, 'потому что Давид делал угодное пред очами Господа и не отступал от всего того, что Он заповедал ему, во все дни жизни своей, кроме поступка с Уриею Хеттеянином.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15731, 'rst66', '1ki', 15, 4, 'Но ради Давида Господь, Бог его, дал ему светильник в Иерусалиме, восставив по нем сына его и утвердив Иерусалим,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15730, 'rst66', '1ki', 15, 3, 'Он ходил во всех грехах отца своего, которые тот делал прежде него, и сердце его не было предано Господу, Богу его, как сердце Давида, отца его.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15729, 'rst66', '1ki', 15, 2, 'Три года он царствовал в Иерусалиме; имя матери его Мааха, дочь Авессалома.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15728, 'rst66', '1ki', 15, 1, 'В восемнадцатый год царствования Иеровоама, сына Наватова, Авия воцарился над Иудеями.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15727, 'rst66', '1ki', 14, 31, 'И почил Ровоам с отцами своими и погребен с отцами своими в городе Давидовом. Имя матери его Наама Аммонитянка. И воцарился Авия, сын его, вместо него.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15726, 'rst66', '1ki', 14, 30, 'Между Ровоамом и Иеровоамом была война во все дни <i>жизни их</i>.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15725, 'rst66', '1ki', 14, 29, 'Прочее о Ровоаме и обо всем, что он делал, описано в летописи царей Иудейских.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15724, 'rst66', '1ki', 14, 28, 'Когда царь выходил в дом Господень, телохранители несли их, и потом опять относили их в палату телохранителей.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15723, 'rst66', '1ki', 14, 27, 'И сделал царь Ровоам вместо них медные щиты и отдал их на руки начальникам телохранителей, которые охраняли вход в дом царя.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15722, 'rst66', '1ki', 14, 26, 'и взял сокровища дома Господня и сокровища дома царского. Всё взял; взял и все золотые щиты, которые сделал Соломон.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15721, 'rst66', '1ki', 14, 25, 'На пятом году царствования Ровоамова Сусаким, царь Египетский, вышел против Иерусалима');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15720, 'rst66', '1ki', 14, 24, 'И блудники были также в этой земле и делали все мерзости тех народов, которых Господь прогнал от лица сынов Израилевых.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15719, 'rst66', '1ki', 14, 23, 'И устроили они у себя высоты и статуи и капища на всяком высоком холме и под всяким тенистым деревом.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15718, 'rst66', '1ki', 14, 22, 'И делал Иуда неугодное пред очами Господа, и раздражали Его более всего того, что сделали отцы их своими грехами, какими они грешили.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15717, 'rst66', '1ki', 14, 21, 'Ровоам, сын Соломонов, царствовал в Иудее. Сорок один год было Ровоаму, когда он воцарился, и семнадцать лет царствовал в Иерусалиме, в городе, который избрал Господь из всех колен Израилевых, чтобы пребывало там имя Его. Имя матери его Наама Аммонитянка.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15716, 'rst66', '1ki', 14, 20, 'Времени царствования Иеровоамова было двадцать два года; и почил он с отцами своими, и воцарился Нават, сын его, вместо него.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15715, 'rst66', '1ki', 14, 19, 'Прочие дела Иеровоама, как он воевал и как царствовал, описаны в летописи царей Израильских.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15714, 'rst66', '1ki', 14, 18, 'И похоронили его, и оплакали его все Израильтяне, по слову Господа, которое Он изрек чрез раба Своего Ахию пророка.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15713, 'rst66', '1ki', 14, 17, 'И встала жена Иеровоамова, и пошла, и пришла в Фирцу; и лишь только переступила чрез порог дома, дитя умерло.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15712, 'rst66', '1ki', 14, 16, 'и предаст <i>Господь</i> Израиля за грехи Иеровоама, которые он сам сделал и которыми ввел в грех Израиля.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15711, 'rst66', '1ki', 14, 15, 'И поразит Господь Израиля, и <i>будет он</i>, как тростник, колеблемый в воде, и извергнет Израильтян из этой доброй земли, которую дал отцам их, и развеет их за реку, за то, что они сделали у себя идолов, раздражая Господа;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15710, 'rst66', '1ki', 14, 14, 'И восставит Себе Господь над Израилем царя, который истребит дом Иеровоамов в тот день; и что? даже теперь.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15709, 'rst66', '1ki', 14, 13, 'и оплачут его все Израильтяне и похоронят его, ибо он один у Иеровоама войдет в гробницу, так как в нем, из дома Иеровоамова, нашлось нечто доброе пред Господом, Богом Израилевым.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15708, 'rst66', '1ki', 14, 12, 'Встань и иди в дом твой; и как скоро нога твоя ступит в город, умрет дитя;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15707, 'rst66', '1ki', 14, 11, 'кто умрет у Иеровоама в городе, того съедят псы, а кто умрет на поле, того склюют птицы небесные; так Господь сказал.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15706, 'rst66', '1ki', 14, 10, 'за это Я наведу беды на дом Иеровоамов и истреблю у Иеровоама <i>до</i> мочащегося к стене, заключенного и оставшегося в Израиле, и вымету дом Иеровоамов, как выметают сор, дочиста;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15705, 'rst66', '1ki', 14, 9, 'ты поступал хуже всех, которые были прежде тебя, и пошел, и сделал себе иных богов и истуканов, чтобы раздражить Меня, Меня же отбросил назад;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15704, 'rst66', '1ki', 14, 8, 'и отторг царство от дома Давидова и дал его тебе; а ты не таков, как раб Мой Давид, который соблюдал заповеди Мои и который последовал Мне всем сердцем своим, делая только угодное пред очами Моими;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15703, 'rst66', '1ki', 14, 7, 'Пойди, скажи Иеровоаму: так говорит Господь, Бог Израилев: Я возвысил тебя из среды простого народа и поставил вождем народа Моего, Израиля,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15702, 'rst66', '1ki', 14, 6, 'Ахия, услышав шорох от ног ее, когда она вошла в дверь, сказал: войди, жена Иеровоамова; для чего было тебе переодеваться? Я грозный посланник к тебе.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15701, 'rst66', '1ki', 14, 5, 'И сказал Господь Ахии: вот, идет жена Иеровоамова спросить тебя о сыне своем, ибо он болен; так и так говори ей; она придет переодетая.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15700, 'rst66', '1ki', 14, 4, 'Жена Иеровоама так и сделала: встала, пошла в Силом и пришла в дом Ахии. Ахия уже не мог видеть, ибо глаза его сделались неподвижны от старости.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15699, 'rst66', '1ki', 14, 3, 'И возьми с собою десять хлебов, и лепешек, и кувшин меду, и пойди к нему: он скажет тебе, что будет с отроком.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15698, 'rst66', '1ki', 14, 2, 'И сказал Иеровоам жене своей: встань и переоденься, чтобы не узнали, что ты жена Иеровоамова, и пойди в Силом. Там есть пророк Ахия; он предсказал мне, что я буду царем сего народа.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15697, 'rst66', '1ki', 14, 1, 'В то время заболел Авия, сын Иеровоамов.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15696, 'rst66', '1ki', 13, 34, 'Это вело дом Иеровоамов ко греху и к погибели и к истреблению его с лица земли.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15695, 'rst66', '1ki', 13, 33, 'И после сего события Иеровоам не сошел со своей худой дороги, но продолжал ставить из народа священников высот; кто хотел, того он посвящал, и тот становился священником высот.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15694, 'rst66', '1ki', 13, 32, 'ибо сбудется слово, которое он по повелению Господню произнес о жертвеннике в Вефиле и о всех капищах на высотах, в городах Самарийских.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15693, 'rst66', '1ki', 13, 31, 'После погребения его он сказал сыновьям своим: когда я умру, похороните меня в гробнице, в которой погребен человек Божий; подле костей его положите кости мои;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15692, 'rst66', '1ki', 13, 30, 'И положил тело его в своей гробнице и плакал по нем: увы, брат мой!');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15691, 'rst66', '1ki', 13, 29, 'И поднял пророк тело человека Божия, и положил его на осла, и повез его обратно. И пошел пророк‐старец в город <i>свой</i>, чтобы оплакать и похоронить его.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15690, 'rst66', '1ki', 13, 28, 'Он отправился и нашел тело его, брошенное на дороге; осел же и лев стояли подле тела; лев не съел тела и не изломал осла.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15689, 'rst66', '1ki', 13, 27, 'И сказал сыновьям своим: оседлайте мне осла. И оседлали они.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15688, 'rst66', '1ki', 13, 26, 'Пророк, воротивший его с дороги, услышав <i>это</i>, сказал: это тот человек Божий, который не повиновался устам Господа; Господь предал его льву, который изломал его и умертвил его, по слову Господа, которое Он изрек ему.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15687, 'rst66', '1ki', 13, 25, 'И вот, проходившие мимо люди увидели тело, брошенное на дороге, и льва, стоящего подле тела, и пошли и рассказали в городе, в котором жил пророк‐старец.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15686, 'rst66', '1ki', 13, 24, 'И отправился тот. И встретил его на дороге лев и умертвил его. И лежало тело его, брошенное на дороге; осел же стоял подле него, и лев стоял подле тела.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15685, 'rst66', '1ki', 13, 23, 'После того, как тот поел хлеба и напился, он оседлал осла для пророка, которого он воротил.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15684, 'rst66', '1ki', 13, 22, 'но воротился, ел хлеб и пил воду в том месте, о котором Он сказал тебе: «не ешь хлеба и не пей воды», тело твое не войдет в гробницу отцов твоих.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15683, 'rst66', '1ki', 13, 21, 'И произнес он к человеку Божию, пришедшему из Иудеи, и сказал: так говорит Господь: за то, что ты не повиновался устам Господа и не соблюл повеления, которое заповедал тебе Господь, Бог твой,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15682, 'rst66', '1ki', 13, 20, 'Когда они еще сидели за столом, слово Господне было к пророку, воротившему его.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15681, 'rst66', '1ki', 13, 19, 'И тот воротился с ним, и поел хлеба в его доме, и напился воды.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15680, 'rst66', '1ki', 13, 18, 'И сказал он ему: и я пророк такой же, как ты, и Ангел говорил мне словом Господним, и сказал: «вороти его к себе в дом; пусть поест он хлеба и напьется воды». &mdash; Он солгал ему.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15679, 'rst66', '1ki', 13, 17, 'ибо словом Господним сказано мне: «не ешь хлеба и не пей там воды и не возвращайся тою дорогою, которою ты шел».');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15678, 'rst66', '1ki', 13, 16, 'Тот сказал: я не могу возвратиться с тобою и пойти к тебе; не буду есть хлеба и не буду пить у тебя воды в сем месте,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15677, 'rst66', '1ki', 13, 15, 'И сказал ему: зайди ко мне в дом и поешь хлеба.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15676, 'rst66', '1ki', 13, 14, 'И поехал за человеком Божиим, и нашел его сидящего под дубом, и сказал ему: ты ли человек Божий, пришедший из Иудеи? И сказал тот: я.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15675, 'rst66', '1ki', 13, 13, 'И сказал он сыновьям своим: оседлайте мне осла. И оседлали ему осла, и он сел на него.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15674, 'rst66', '1ki', 13, 12, 'И спросил их отец их: какою дорогою он пошел? И показали сыновья его, какою дорогою пошел человек Божий, приходивший из Иудеи.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15673, 'rst66', '1ki', 13, 11, 'В Вефиле жил один пророк‐старец. Сын его пришел и рассказал ему все, что сделал сегодня человек Божий в Вефиле; и слова, какие он говорил царю, пересказали <i>сыновья</i> отцу своему.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15672, 'rst66', '1ki', 13, 10, 'И пошел он другою дорогою и не пошел обратно тою дорогою, которою пришел в Вефиль.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15671, 'rst66', '1ki', 13, 9, 'ибо так заповедано мне словом Господним: «не ешь там хлеба и не пей воды и не возвращайся тою дорогою, которою ты шел».');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15670, 'rst66', '1ki', 13, 8, 'Но человек Божий сказал царю: хотя бы ты давал мне полдома твоего, я не пойду с тобою и не буду есть хлеба и не буду пить воды в этом месте,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15669, 'rst66', '1ki', 13, 7, 'И сказал царь человеку Божию: зайди со мною в дом и подкрепи себя пищею, и я дам тебе подарок.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15668, 'rst66', '1ki', 13, 6, 'И сказал царь человеку Божию: умилостиви лицо Господа, Бога твоего, и помолись обо мне, чтобы рука моя могла поворотиться ко мне. И умилостивил человек Божий лицо Господа, и рука царя поворотилась к нему и стала как прежде.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15667, 'rst66', '1ki', 13, 5, 'И жертвенник распался, и пепел с жертвенника рассыпался, по знамению, которое дал человек Божий словом Господним.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15666, 'rst66', '1ki', 13, 4, 'Когда царь услышал слово человека Божия, произнесенное к жертвеннику в Вефиле, то простер Иеровоам руку свою от жертвенника, говоря: возьмите его. И одеревенела рука его, которую он простер на него, и не мог он поворотить ее к себе.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15665, 'rst66', '1ki', 13, 3, 'И дал в тот день знамение, сказав: вот знамение того, что это изрек Господь: вот, этот жертвенник распадется, и пепел, который на нем, рассыплется.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15664, 'rst66', '1ki', 13, 2, 'И произнес к жертвеннику слово Господне и сказал: жертвенник, жертвенник! так говорит Господь: вот, родится сын дому Давидову, имя ему Иосия, и принесет на тебе в жертву священников высот, совершающих на тебе курение, и человеческие кости сожжет на тебе.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15663, 'rst66', '1ki', 13, 1, 'И вот, человек Божий пришел из Иудеи по слову Господню в Вефиль, в то время, как Иеровоам стоял у жертвенника, чтобы совершить курение.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15662, 'rst66', '1ki', 12, 33, 'и принес жертвы на жертвеннике, который он сделал в Вефиле, в пятнадцатый день восьмого месяца, месяца, который он произвольно назначил; и установил праздник для сынов Израилевых, и подошел к жертвеннику, чтобы совершить курение.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15661, 'rst66', '1ki', 12, 32, 'И установил Иеровоам праздник в восьмой месяц, в пятнадцатый день месяца, подобный тому празднику, какой был в Иудее, и приносил жертвы на жертвеннике; то же сделал он в Вефиле, чтобы приносить жертву тельцам, которых сделал. И поставил в Вефиле священников высот, которые устроил,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15660, 'rst66', '1ki', 12, 31, 'И построил он капище на высоте и поставил из народа священников, которые не были из сынов Левииных.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15659, 'rst66', '1ki', 12, 30, 'И повело это ко греху, ибо народ стал ходить к одному <i>из них</i>, даже в Дан.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15658, 'rst66', '1ki', 12, 29, 'И поставил одного в Вефиле, а другого в Дане.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15657, 'rst66', '1ki', 12, 28, 'И, посоветовавшись, царь сделал двух золотых тельцов и сказал <i>народу</i>: не нужно вам ходить в Иерусалим; вот боги твои, Израиль, которые вывели тебя из земли Египетской.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15656, 'rst66', '1ki', 12, 27, 'если народ сей будет ходить в Иерусалим для жертвоприношения в доме Господнем, то сердце народа сего обратится к государю своему, к Ровоаму, царю Иудейскому, и убьют они меня и возвратятся к Ровоаму, царю Иудейскому.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15655, 'rst66', '1ki', 12, 26, 'И говорил Иеровоам в сердце своем: царство может опять перейти к дому Давидову;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15654, 'rst66', '1ki', 12, 25, 'И обстроил Иеровоам Сихем на горе Ефремовой и поселился в нем; оттуда пошел и построил Пенуил.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15653, 'rst66', '1ki', 12, 24, 'так говорит Господь: не ходите и не начинайте войны с братьями вашими, сынами Израилевыми; возвратитесь каждый в дом свой, ибо от Меня это было. И послушались они слова Господня и пошли назад по слову Господню.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15652, 'rst66', '1ki', 12, 23, 'скажи Ровоаму, сыну Соломонову, царю Иудейскому, и всему дому Иудину и Вениаминову и прочему народу:');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15651, 'rst66', '1ki', 12, 22, 'И было слово Божие к Самею, человеку Божию, и сказано:');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15650, 'rst66', '1ki', 12, 21, 'Ровоам, прибыв в Иерусалим, собрал из всего дома Иудина и из колена Вениаминова сто восемьдесят тысяч отборных воинов, дабы воевать с домом Израилевым для того, чтобы возвратить царство Ровоаму, сыну Соломонову.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15649, 'rst66', '1ki', 12, 20, 'Когда услышали все Израильтяне, что Иеровоам возвратился, то послали и призвали его в собрание, и воцарили его над всеми Израильтянами. За домом Давидовым не осталось никого, кроме колена Иудина <i>и Вениаминова</i>.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15648, 'rst66', '1ki', 12, 19, 'И отложился Израиль от дома Давидова до сего дня.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15647, 'rst66', '1ki', 12, 18, 'И послал царь Ровоам Адонирама, начальника над податью; но все Израильтяне забросали его каменьями, и он умер; царь же Ровоам поспешно взошел на колесницу, чтоб убежать в Иерусалим.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15646, 'rst66', '1ki', 12, 17, 'Только над сынами Израилевыми, жившими в городах Иудиных, царствовал Ровоам.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15645, 'rst66', '1ki', 12, 16, 'И увидели все Израильтяне, что царь не послушал их. И отвечал народ царю и сказал: какая нам часть в Давиде? нет нам доли в сыне Иессеевом; по шатрам своим, Израиль! теперь знай свой дом, Давид! И разошелся Израиль по шатрам своим.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15644, 'rst66', '1ki', 12, 15, 'И не послушал царь народа, ибо так суждено было Господом, чтобы исполнилось слово Его, которое изрек Господь чрез Ахию Силомлянина Иеровоаму, сыну Наватову.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15643, 'rst66', '1ki', 12, 14, 'и говорил он по совету молодых людей и сказал: отец мой наложил на вас тяжкое иго, а я увеличу иго ваше; отец мой наказывал вас бичами, а я буду наказывать вас скорпионами.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15642, 'rst66', '1ki', 12, 13, 'И отвечал царь народу сурово и пренебрег совет старцев, что они советовали ему;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15641, 'rst66', '1ki', 12, 12, 'Иеровоам и весь народ пришли к Ровоаму на третий день, как приказал царь, сказав: придите ко мне на третий день.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15640, 'rst66', '1ki', 12, 11, 'итак, если отец мой обременял вас тяжким игом, то я увеличу иго ваше; отец мой наказывал вас бичами, а я буду наказывать вас скорпионами».');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15639, 'rst66', '1ki', 12, 10, 'И говорили ему молодые люди, которые выросли вместе с ним, и сказали: так скажи народу сему, который говорил тебе и сказал: «отец твой наложил на нас тяжкое иго, ты же облегчи нас»; так скажи им: «мой мизинец толще чресл отца моего;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15638, 'rst66', '1ki', 12, 9, 'и сказал им: что вы посоветуете мне отвечать народу сему, который говорил мне и сказал: «облегчи иго, которое наложил на нас отец твой»?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15637, 'rst66', '1ki', 12, 8, 'Но он пренебрег совет старцев, что они советовали ему, и советовался с молодыми людьми, которые выросли вместе с ним и которые предстояли пред ним,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15636, 'rst66', '1ki', 12, 7, 'Они говорили ему и сказали: если ты на сей день будешь слугою народу сему и услужишь ему, и удовлетворишь им, и будешь говорить им ласково, то они будут твоими рабами на все дни.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15635, 'rst66', '1ki', 12, 6, 'Царь Ровоам советовался со старцами, которые предстояли пред Соломоном, отцом его, при жизни его, и говорил: как посоветуете вы мне отвечать сему народу?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15634, 'rst66', '1ki', 12, 5, 'И сказал он им: пойдите и через три дня опять придите ко мне. И пошел народ.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15633, 'rst66', '1ki', 12, 4, 'отец твой наложил на нас тяжкое иго, ты же облегчи нам жестокую работу отца твоего и тяжкое иго, которое он наложил на нас, и тогда мы будем служить тебе.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15632, 'rst66', '1ki', 12, 3, 'и послали за ним и призвали его. Тогда Иеровоам и все собрание Израильтян пришли и говорили Ровоаму и сказали:');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15631, 'rst66', '1ki', 12, 2, 'И услышал о том Иеровоам, сын Наватов, когда находился еще в Египте, куда убежал от царя Соломона, и возвратился Иеровоам из Египта;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15630, 'rst66', '1ki', 12, 1, 'И пошел Ровоам в Сихем, ибо в Сихем пришли все Израильтяне, чтобы воцарить его.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15629, 'rst66', '1ki', 11, 43, 'И почил Соломон с отцами своими и погребен был в городе Давида, отца своего, и воцарился вместо него сын его Ровоам.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15628, 'rst66', '1ki', 11, 42, 'Времени царствования Соломонова в Иерусалиме над всем Израилем <i>было</i> сорок лет.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15627, 'rst66', '1ki', 11, 41, 'Прочие события Соломоновы и все, что он делал, и мудрость его описаны в книге дел Соломоновых.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15626, 'rst66', '1ki', 11, 40, 'Соломон же хотел умертвить Иеровоама; но Иеровоам встал и убежал в Египет к Сусакиму, царю Египетскому, и жил в Египте до смерти Соломоновой.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15625, 'rst66', '1ki', 11, 39, 'и смирю Я род Давидов за сие, но не на все дни.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15624, 'rst66', '1ki', 11, 38, 'и если будешь соблюдать все, что Я заповедую тебе, и будешь ходить путями Моими и делать угодное пред очами Моими, соблюдая уставы Мои и заповеди Мои, как делал раб Мой Давид, то Я буду с тобою и устрою тебе дом твердый, как Я устроил Давиду, и отдам тебе Израиля;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15623, 'rst66', '1ki', 11, 37, 'Тебя Я избираю, и ты будешь владычествовать над всем, чего пожелает душа твоя, и будешь царем над Израилем;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15622, 'rst66', '1ki', 11, 36, 'а сыну его дам одно колено, дабы оставался светильник Давида, раба Моего, во все дни пред лицом Моим, в городе Иерусалиме, который Я избрал Себе для пребывания там имени Моего.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15621, 'rst66', '1ki', 11, 35, 'но возьму царство из руки сына его и дам тебе из него десять колен;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15620, 'rst66', '1ki', 11, 34, 'Я не беру всего царства из руки его, но Я оставляю его владыкою на все дни жизни его ради Давида, раба Моего, которого Я избрал, который соблюдал заповеди Мои и уставы Мои;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15619, 'rst66', '1ki', 11, 33, 'Это за то, что они оставили Меня и стали поклоняться Астарте, божеству Сидонскому, и Хамосу, богу Моавитскому, и Милхому, богу Аммонитскому, и не пошли путями Моими, чтобы делать угодное пред очами Моими и <i>соблюдать</i> уставы Мои и заповеди Мои, подобно Давиду, отцу его.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15618, 'rst66', '1ki', 11, 32, 'а одно колено останется за ним ради раба Моего Давида и ради города Иерусалима, который Я избрал из всех колен Израилевых.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15617, 'rst66', '1ki', 11, 31, 'и сказал Иеровоаму: возьми себе десять частей, ибо так говорит Господь, Бог Израилев: вот, Я исторгаю царство из руки Соломоновой и даю тебе десять колен,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15616, 'rst66', '1ki', 11, 30, 'И взял Ахия новую одежду, которая была на нем, и разодрал ее на двенадцать частей,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15615, 'rst66', '1ki', 11, 29, 'В то время случилось Иеровоаму выйти из Иерусалима; и встретил его на дороге пророк Ахия Силомлянин, и на нем была новая одежда. На поле их было только двое.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15614, 'rst66', '1ki', 11, 28, 'Иеровоам был человек мужественный. Соломон, заметив, что этот молодой человек умеет делать дело, поставил его смотрителем над оброчными из дома Иосифова.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15613, 'rst66', '1ki', 11, 27, 'И вот обстоятельство, по которому он поднял руку на царя: Соломон строил Милло, починивал повреждения в городе Давида, отца своего.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15612, 'rst66', '1ki', 11, 26, 'И Иеровоам, сын Наватов, Ефремлянин из Цареды, &mdash; имя матери его вдовы: Церуа, &mdash; раб Соломонов, поднял руку на царя.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15611, 'rst66', '1ki', 11, 25, 'И был он противником Израиля во все дни Соломона. Кроме зла, <i>причиненного</i> Адером, он всегда вредил Израилю и сделался царем Сирии.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15610, 'rst66', '1ki', 11, 24, 'и, собрав около себя людей, сделался начальником шайки, после того, как Давид разбил <i>Адраазара;</i> и пошли они в Дамаск, и водворились там, и владычествовали в Дамаске.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15609, 'rst66', '1ki', 11, 23, 'И воздвиг Бог против Соломона еще противника, Разона, сына Елиады, который убежал от государя своего Адраазара, царя Сувского,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15608, 'rst66', '1ki', 11, 22, 'И сказал ему фараон: разве ты нуждаешься в чем у меня, что хочешь идти в свою землю? Он отвечал: нет, но отпусти меня.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15607, 'rst66', '1ki', 11, 21, 'Когда Адер услышал, что Давид почил с отцами своими и что военачальник Иоав умер, то сказал фараону: отпусти меня, я пойду в свою землю.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15606, 'rst66', '1ki', 11, 20, 'И родила ему сестра Тахпенесы сына Генувата. Тахпенеса воспитывала его в доме фараоновом; и жил Генуват в доме фараоновом вместе с сыновьями фараоновыми.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15605, 'rst66', '1ki', 11, 19, 'Адер снискал у фараона большую милость, так что он дал ему в жену сестру своей жены, сестру царицы Тахпенесы.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15604, 'rst66', '1ki', 11, 18, 'Отправившись из Мадиама, они пришли в Фаран и взяли с собою людей из Фарана и пришли в Египет к фараону, царю Египетскому. Он дал ему дом, и назначил ему содержание, и дал ему землю.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15603, 'rst66', '1ki', 11, 17, 'тогда сей Адер убежал в Египет, и с ним несколько Идумеян, служивших при отце его; Адер <i>был тогда</i> малым ребенком.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15602, 'rst66', '1ki', 11, 16, 'ибо шесть месяцев прожил там Иоав и все Израильтяне, доколе не истребили всего мужеского пола в Идумее, &mdash;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15601, 'rst66', '1ki', 11, 15, 'Когда Давид был в Идумее, и военачальник Иоав пришел для погребения убитых и избил весь мужеский пол в Идумее, &mdash;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15600, 'rst66', '1ki', 11, 14, 'И воздвиг Господь противника на Соломона, Адера Идумеянина, из царского Идумейского рода.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15599, 'rst66', '1ki', 11, 13, 'и не все царство исторгну; одно колено дам сыну твоему ради Давида, раба Моего, и ради Иерусалима, который Я избрал.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15598, 'rst66', '1ki', 11, 12, 'но во дни твои Я не сделаю сего ради Давида, отца твоего; из руки сына твоего исторгну его;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15597, 'rst66', '1ki', 11, 11, 'И сказал Господь Соломону: за то, что так у тебя делается, и ты не сохранил завета Моего и уставов Моих, которые Я заповедал тебе, Я отторгну от тебя царство и отдам его рабу твоему;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15596, 'rst66', '1ki', 11, 10, 'и заповедал ему, чтобы он не следовал иным богам; но он не исполнил того, что заповедал ему Господь.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15595, 'rst66', '1ki', 11, 9, 'И разгневался Господь на Соломона за то, что он уклонил сердце свое от Господа, Бога Израилева, Который два раза являлся ему');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15594, 'rst66', '1ki', 11, 8, 'Так сделал он для всех своих чужестранных жен, которые кадили и приносили жертвы своим богам.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15593, 'rst66', '1ki', 11, 7, 'Тогда построил Соломон капище Хамосу, мерзости Моавитской, на горе, которая пред Иерусалимом, и Молоху, мерзости Аммонитской.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15592, 'rst66', '1ki', 11, 6, 'И делал Соломон неугодное пред очами Господа и не вполне последовал Господу, как Давид, отец его.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15591, 'rst66', '1ki', 11, 5, 'И стал Соломон служить Астарте, божеству Сидонскому, и Милхому, мерзости Аммонитской.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15590, 'rst66', '1ki', 11, 4, 'Во время старости Соломона жены его склонили сердце его к иным богам, и сердце его не было вполне предано Господу, Богу своему, как сердце Давида, отца его.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15589, 'rst66', '1ki', 11, 3, 'И было у него семьсот жен и триста наложниц; и развратили жены его сердце его.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15588, 'rst66', '1ki', 11, 2, 'из тех народов, о которых Господь сказал сынам Израилевым: «не входите к ним, и они пусть не входят к вам, чтобы они не склонили сердца вашего к своим богам»; к ним прилепился Соломон любовью.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15587, 'rst66', '1ki', 11, 1, 'И полюбил царь Соломон многих чужестранных женщин, кроме дочери фараоновой, Моавитянок, Аммонитянок, Идумеянок, Сидонянок, Хеттеянок,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15586, 'rst66', '1ki', 10, 29, 'Колесница из Египта получаема и доставляема была за шестьсот <i>сиклей</i> серебра, а конь за сто пятьдесят. Таким же образом они руками своими доставляли <i>все это</i> царям Хеттейским и царям Арамейским.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15585, 'rst66', '1ki', 10, 28, 'Коней же царю Соломону приводили из Египта и из Кувы; царские купцы покупали их из Кувы за деньги.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15584, 'rst66', '1ki', 10, 27, 'И сделал царь серебро в Иерусалиме равноценным с простыми камнями, а кедры, по их множеству, сделал равноценными с сикоморами, <i>растущими</i> на низких местах.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15583, 'rst66', '1ki', 10, 26, 'И набрал Соломон колесниц и всадников; у него было тысяча четыреста колесниц и двенадцать тысяч всадников; и разместил он их по колесничным городам и при царе в Иерусалиме.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15582, 'rst66', '1ki', 10, 25, 'И они подносили ему, каждый от себя, в дар: сосуды серебряные и сосуды золотые, и одежды, и оружие, и благовония, коней и мулов, каждый год.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15581, 'rst66', '1ki', 10, 24, 'И все <i>цари</i> на земле искали видеть Соломона, чтобы послушать мудрости его, которую вложил Бог в сердце его.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15580, 'rst66', '1ki', 10, 23, 'Царь Соломон превосходил всех царей земли богатством и мудростью.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15579, 'rst66', '1ki', 10, 22, 'ибо у царя был на море Фарсисский корабль с кораблем Хирамовым; в три года раз приходил Фарсисский корабль, привозивший золото и серебро, и слоновую кость, и обезьян, и павлинов.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15578, 'rst66', '1ki', 10, 21, 'И все сосуды для питья у царя Соломона <i>были</i> золотые, и все сосуды в доме из Ливанского дерева были из чистого золота; из серебра ничего не было, потому что серебро во дни Соломоновы считалось ни за что;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15577, 'rst66', '1ki', 10, 20, 'и еще двенадцать львов стояли там на шести ступенях по обе стороны. Подобного сему не бывало ни в одном царстве.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15576, 'rst66', '1ki', 10, 19, 'к престолу было шесть ступеней; верх сзади у престола был круглый, и были с обеих сторон у места сиденья локотники, и два льва стояли у локотников;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15575, 'rst66', '1ki', 10, 18, 'И сделал царь большой престол из слоновой кости и обложил его чистым золотом;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15574, 'rst66', '1ki', 10, 17, 'и триста меньших щитов из кованого золота, по три мины золота пошло на каждый щит; и поставил их царь в доме из Ливанского дерева.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15573, 'rst66', '1ki', 10, 16, 'И сделал царь Соломон двести больших щитов из кованого золота, по шестисот <i>сиклей</i> пошло на каждый щит;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15572, 'rst66', '1ki', 10, 15, 'сверх того, что <i>получаемо было</i> от разносчиков товара и от торговли купцов, и от всех царей Аравийских и от областных начальников.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15571, 'rst66', '1ki', 10, 14, 'В золоте, которое приходило Соломону в каждый год, весу было шестьсот шестьдесят шесть талантов золотых,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15570, 'rst66', '1ki', 10, 13, 'И царь Соломон дал царице Савской все, чего она желала и чего просила, сверх того, что подарил ей царь Соломон своими руками. И отправилась она обратно в свою землю, она и все слуги ее.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15569, 'rst66', '1ki', 10, 12, 'И сделал царь из сего красного дерева перила для храма Господня и для дома царского, и гусли и псалтири для певцов; никогда не приходило столько красного дерева и не видано было до сего дня.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15568, 'rst66', '1ki', 10, 11, 'И корабль Хирамов, который привозил золото из Офира, привез из Офира великое множество красного дерева и драгоценных камней.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15567, 'rst66', '1ki', 10, 10, 'И подарила она царю сто двадцать талантов золота и великое множество благовоний и драгоценные камни; никогда еще не приходило такого множества благовоний, какое подарила царица Савская царю Соломону.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15566, 'rst66', '1ki', 10, 9, 'Да будет благословен Господь, Бог твой, Который благоволил посадить тебя на престол Израилев! Господь, по вечной любви Своей к Израилю, поставил тебя царем, творить суд и правду.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15565, 'rst66', '1ki', 10, 8, 'Блаженны люди твои и блаженны сии слуги твои, которые всегда предстоят пред тобою и слышат мудрость твою!');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15564, 'rst66', '1ki', 10, 7, 'но я не верила словам, доколе не пришла, и не увидели глаза мои: и вот, мне и вполовину не сказано; мудрости и богатства у тебя больше, нежели как я слышала.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15563, 'rst66', '1ki', 10, 6, 'и сказала царю: верно то, что я слышала в земле своей о делах твоих и о мудрости твоей;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15562, 'rst66', '1ki', 10, 5, 'и пищу за столом его, и жилище рабов его, и стройность слуг его, и одежду их, и виночерпиев его, и всесожжения его, которые он приносил в храме Господнем. И не могла она более удержаться');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15561, 'rst66', '1ki', 10, 4, 'И увидела царица Савская всю мудрость Соломона и дом, который он построил,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15560, 'rst66', '1ki', 10, 3, 'И объяснил ей Соломон все слова ее, и не было ничего незнакомого царю, чего бы он не изъяснил ей.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15559, 'rst66', '1ki', 10, 2, 'И пришла она в Иерусалим с весьма большим богатством: верблюды навьючены <i>были</i> благовониями и великим множеством золота и драгоценными камнями; и пришла к Соломону и беседовала с ним обо всем, что было у нее на сердце.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15558, 'rst66', '1ki', 10, 1, 'Царица Савская, услышав о славе Соломона во имя Господа, пришла испытать его загадками.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15557, 'rst66', '1ki', 9, 28, 'и отправились они в Офир, и взяли оттуда золота четыреста двадцать талантов, и привезли царю Соломону.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15556, 'rst66', '1ki', 9, 27, 'И послал Хирам на корабле своих подданных корабельщиков, знающих море, с подданными Соломоновыми;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15555, 'rst66', '1ki', 9, 26, 'Царь Соломон также сделал корабль в Ецион‐Гавере, что при Елафе, на берегу Чермного моря, в земле Идумейской.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15554, 'rst66', '1ki', 9, 25, 'И приносил Соломон три раза в год всесожжения и мирные жертвы на жертвеннике, который он построил Господу, и курение на нем совершал пред Господом. И окончил он <i>строение</i> дома.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15553, 'rst66', '1ki', 9, 24, 'Дочь фараонова перешла из города Давидова в свой дом, который построил для нее Соломон; потом построил он Милло.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15552, 'rst66', '1ki', 9, 23, 'Вот главные приставники над работами Соломоновыми: управлявших народом, который производил работы, было пятьсот пятьдесят.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15551, 'rst66', '1ki', 9, 22, 'Сынов же Израилевых Соломон не делал работниками, но они были его воинами, его слугами, его вельможами, его военачальниками и вождями его колесниц и его всадников.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15550, 'rst66', '1ki', 9, 21, 'детей их, оставшихся после них на земле, которых сыны Израилевы не могли истребить, Соломон сделал оброчными работниками до сего дня.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15549, 'rst66', '1ki', 9, 20, 'Весь народ, оставшийся от Аморреев, Хеттеев, Ферезеев, Евеев и Иевусеев, которые были не из сынов Израилевых,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15548, 'rst66', '1ki', 9, 19, 'и все города для запасов, которые были у Соломона, и города для колесниц, и города для конницы и все то, что Соломон хотел построить в Иерусалиме и на Ливане и во всей земле своего владения.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15547, 'rst66', '1ki', 9, 18, 'и Ваалаф и Фадмор в пустыне,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15546, 'rst66', '1ki', 9, 17, 'И построил Соломон Газер и нижний Бефорон,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15545, 'rst66', '1ki', 9, 16, 'Фараон, царь Египетский, пришел и взял Газер, и сжег его огнем, и Хананеев, живших в городе, побил, и отдал его в приданое дочери своей, жене Соломоновой.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15544, 'rst66', '1ki', 9, 15, 'Вот распоряжение о подати, которую наложил царь Соломон, чтобы построить храм Господень и дом свой, и Милло, и стену Иерусалимскую, Гацор, и Мегиддо, и Газер.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15543, 'rst66', '1ki', 9, 14, 'И послал Хирам царю сто двадцать талантов золота.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15542, 'rst66', '1ki', 9, 13, 'И сказал он: что это за города, которые ты, брат мой, дал мне? И назвал их землею Кавул, <i>как называются они</i> до сего дня.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15541, 'rst66', '1ki', 9, 12, 'И вышел Хирам из Тира посмотреть города, которые дал ему Соломон, и они не понравились ему.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15540, 'rst66', '1ki', 9, 11, 'на что Хирам, царь Тирский, доставлял Соломону дерева кедровые и дерева кипарисовые и золото, по его желанию, &mdash; царь Соломон дал Хираму двадцать городов в земле Галилейской.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15539, 'rst66', '1ki', 9, 10, 'По окончании двадцати лет, в которые Соломон построил два дома &mdash; дом Господень и дом царский, &mdash;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15538, 'rst66', '1ki', 9, 9, 'И скажут: «за то, что они оставили Господа, Бога своего, Который вывел отцов их из земли Египетской, и приняли других богов, и поклонялись им и служили им, за это навел на них Господь все сие бедствие».');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15537, 'rst66', '1ki', 9, 8, 'И о храме сем высоком всякий, проходящий мимо него, ужаснется и свистнет, и скажет: «за что Господь поступил так с сею землею и с сим храмом?»');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15536, 'rst66', '1ki', 9, 7, 'то Я истреблю Израиля с лица земли, которую Я дал ему, и храм, который Я освятил имени Моему, отвергну от лица Моего, и будет Израиль притчею и посмешищем у всех народов.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15535, 'rst66', '1ki', 9, 6, 'Если же вы и сыновья ваши отступите от Меня и не будете соблюдать заповедей Моих и уставов Моих, которые Я дал вам, и пойдете и станете служить иным богам и поклоняться им,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15534, 'rst66', '1ki', 9, 5, 'то Я поставлю царский престол твой над Израилем вовек, как Я сказал отцу твоему Давиду, говоря: «не прекратится у тебя сидящий на престоле Израилевом».');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15533, 'rst66', '1ki', 9, 4, 'И если ты будешь ходить пред лицом Моим, как ходил отец твой Давид, в чистоте сердца и в правоте, исполняя все, что Я заповедал тебе, и если будешь хранить уставы Мои и законы Мои,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15532, 'rst66', '1ki', 9, 3, 'И сказал ему Господь: Я услышал молитву твою и прошение твое, о чем ты просил Меня. Я освятил сей храм, который ты построил, чтобы пребывать имени Моему там вовек; и будут очи Мои и сердце Мое там во все дни.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15531, 'rst66', '1ki', 9, 2, 'явился Соломону Господь во второй раз, как явился ему в Гаваоне.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15530, 'rst66', '1ki', 9, 1, 'После того, как Соломон кончил строение храма Господня и дома царского и все, что Соломон желал сделать,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15529, 'rst66', '1ki', 8, 66, 'В восьмой день Соломон отпустил народ. И благословили царя и пошли в шатры свои, радуясь и веселясь в сердце о всем добром, что сделал Господь рабу Своему Давиду и народу Своему, Израилю.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15528, 'rst66', '1ki', 8, 65, 'И сделал Соломон в это время праздник, и весь Израиль с ним, &mdash; большое собрание, <i>сошедшееся</i> от входа в Емаф до реки Египетской пред Господом, Богом нашим, &mdash; семь дней и еще семь дней, четырнадцать дней.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15527, 'rst66', '1ki', 8, 64, 'В тот же день освятил царь среднюю часть двора, который пред храмом Господним, совершив там всесожжение и хлебное приношение и <i>вознеся</i> тук мирных жертв, потому что медный жертвенник, который пред Господом, был мал для помещения всесожжения и хлебного приношения и тука мирных жертв.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15526, 'rst66', '1ki', 8, 63, 'И принес Соломон в мирную жертву, которую принес он Господу, двадцать две тысячи крупного скота и сто двадцать тысяч мелкого скота. Так освятили храм Господу царь и все сыны Израилевы.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15525, 'rst66', '1ki', 8, 62, 'И царь и все Израильтяне с ним принесли жертву Господу.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15524, 'rst66', '1ki', 8, 61, 'да будет сердце ваше вполне предано Господу, Богу нашему, чтобы ходить по уставам Его и соблюдать заповеди Его, как ныне.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15523, 'rst66', '1ki', 8, 60, 'чтобы все народы познали, что Господь есть Бог и нет кроме Его;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15522, 'rst66', '1ki', 8, 59, 'и да будут слова сии, которыми я молился пред Господом, близки к Господу, Богу нашему, день и ночь, дабы Он делал, что потребно для раба Своего, и что потребно для народа Своего, Израиля, изо дня в день,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15521, 'rst66', '1ki', 8, 58, 'наклоняя к Себе сердце наше, чтобы мы ходили по всем путям Его и соблюдали заповеди Его и уставы Его и законы Его, которые Он заповедал отцам нашим;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15520, 'rst66', '1ki', 8, 57, 'да будет с нами Господь, Бог наш, как был Он с отцами нашими, да не оставит нас, да не покинет нас,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15519, 'rst66', '1ki', 8, 56, 'благословен Господь, Который дал покой народу Своему, Израилю, как говорил! не осталось неисполненным ни одного слова из всех благих слов Его, которые Он изрек чрез раба Своего Моисея;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15518, 'rst66', '1ki', 8, 55, 'И стоя благословил все собрание Израильтян, громким голосом говоря:');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15517, 'rst66', '1ki', 8, 54, 'Когда Соломон произнес все сие моление и прошение к Господу, тогда встал с колен от жертвенника Господня, <i>руки же</i> его были распростерты к небу.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15516, 'rst66', '1ki', 8, 53, 'ибо Ты отделил их Себе в удел из всех народов земли, как Ты изрек чрез Моисея, раба Твоего, когда вывел отцов наших из Египта, Владыка Господи!');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15515, 'rst66', '1ki', 8, 52, 'Да будут очи Твои отверсты на молитву раба Твоего и на молитву народа Твоего, Израиля, чтобы слышать их всегда, когда они будут призывать Тебя,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15514, 'rst66', '1ki', 8, 51, 'ибо они Твой народ и Твой удел, который Ты вывел из Египта, из железной печи.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15513, 'rst66', '1ki', 8, 50, 'и прости народу Твоему, в чем он согрешил пред Тобою, и все проступки его, которые он сделал пред Тобою, и возбуди сострадание к ним в пленивших их, чтобы они были милостивы к ним:');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15512, 'rst66', '1ki', 8, 49, 'тогда услышь с неба, с места обитания Твоего, молитву и прошение их и сделай, что потребно для них;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15511, 'rst66', '1ki', 8, 48, 'и когда обратятся к Тебе всем сердцем своим и всею душою своею в земле врагов, которые пленили их, и будут молиться Тебе, обратившись к земле своей, которую Ты дал отцам их, к городу, который Ты избрал, и к храму, который я построил имени Твоему,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15510, 'rst66', '1ki', 8, 47, 'и когда они в земле, в которой будут находиться в плену, войдут в себя и обратятся и будут молиться Тебе в земле пленивших их, говоря: «мы согрешили, сделали беззаконие, мы виновны»;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15509, 'rst66', '1ki', 8, 46, 'Когда они согрешат пред Тобою, &mdash; ибо нет человека, который не грешил бы, &mdash; и Ты прогневаешься на них и предашь их врагам, и пленившие их отведут их в неприятельскую землю, далекую или близкую;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15508, 'rst66', '1ki', 8, 45, 'тогда услышь с неба молитву их и прошение их и сделай, что потребно для них.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15507, 'rst66', '1ki', 8, 44, 'Когда выйдет народ Твой на войну против врага своего путем, которым Ты пошлешь его, и будет молиться Господу, обратившись к городу, который Ты избрал, и к храму, который я построил имени Твоему,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15506, 'rst66', '1ki', 8, 43, 'услышь с неба, с места обитания Твоего, и сделай все, о чем будет взывать к Тебе иноплеменник, чтобы все народы земли знали имя Твое, чтобы боялись Тебя, как народ Твой, Израиль, чтобы знали, что именем Твоим называется храм сей, который я построил.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15505, 'rst66', '1ki', 8, 42, 'ибо и они услышат о Твоем имени великом и о Твоей руке сильной и о Твоей мышце простертой, &mdash; и придет он и помолится у храма сего,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15504, 'rst66', '1ki', 8, 41, 'Если и иноплеменник, который не от Твоего народа, Израиля, придет из земли далекой ради имени Твоего, &mdash;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15503, 'rst66', '1ki', 8, 40, 'чтобы они боялись Тебя во все дни, доколе живут на земле, которую Ты дал отцам нашим.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15502, 'rst66', '1ki', 8, 39, 'Ты услышь с неба, с места обитания Твоего, и помилуй; соделай и воздай каждому по путям его, как Ты усмотришь сердце его, ибо Ты один знаешь сердце всех сынов человеческих:');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15501, 'rst66', '1ki', 8, 38, 'при всякой молитве, при всяком прошении, какое будет от какого‐либо человека во всем народе Твоем, Израиле, когда они почувствуют бедствие в сердце своем и прострут руки свои к храму сему,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15500, 'rst66', '1ki', 8, 37, 'Будет ли на земле голод, будет ли моровая язва, будет ли палящий ветер, ржавчина, саранча, червь, неприятель ли будет теснить его в земле его, <i>будет ли</i> какое бедствие, какая болезнь, &mdash;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15499, 'rst66', '1ki', 8, 36, 'тогда услышь с неба и прости грех рабов Твоих и народа Твоего, Израиля, указав им добрый путь, по которому идти, и пошли дождь на землю Твою, которую Ты дал народу Твоему в наследие.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15498, 'rst66', '1ki', 8, 35, 'Когда заключится небо и не будет дождя за то, что они согрешат пред Тобою, и когда помолятся на месте сем и исповедают имя Твое и обратятся от греха своего, ибо Ты смирил их,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15497, 'rst66', '1ki', 8, 34, 'тогда Ты услышь с неба и прости грех народа Твоего, Израиля, и возврати их в землю, которую Ты дал отцам их.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15496, 'rst66', '1ki', 8, 33, 'Когда народ Твой, Израиль, будет поражен неприятелем за то, что согрешил пред Тобою, и когда они обратятся к Тебе, и исповедают имя Твое, и будут просить и умолять Тебя в сем храме,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15495, 'rst66', '1ki', 8, 32, 'тогда Ты услышь с неба и произведи суд над рабами Твоими, обвини виновного, возложив поступок его на голову его, и оправдай правого, воздав ему по правде его.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15494, 'rst66', '1ki', 8, 31, 'Когда кто согрешит против ближнего своего, и потребует от него клятвы, чтобы он поклялся, и для клятвы придут пред жертвенник Твой в храм сей,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15493, 'rst66', '1ki', 8, 30, 'Услышь моление раба Твоего и народа Твоего, Израиля, когда они будут молиться на месте сем; услышь на месте обитания Твоего, на небесах, услышь и помилуй.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15492, 'rst66', '1ki', 8, 29, 'Да будут очи Твои отверсты на храм сей день и ночь, на сие место, о котором Ты сказал: «Мое имя будет там»; услышь молитву, которою будет молиться раб Твой на месте сем.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15491, 'rst66', '1ki', 8, 28, 'но призри на молитву раба Твоего и на прошение его, Господи, Боже мой; услышь воззвание и молитву, которою раб Твой умоляет Тебя ныне.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15490, 'rst66', '1ki', 8, 27, 'Поистине, Богу ли жить на земле? Небо и небо небес не вмещают Тебя, тем менее сей храм, который я построил;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15489, 'rst66', '1ki', 8, 26, 'И ныне, Боже Израилев, да будет верно слово Твое, которое Ты изрек рабу Твоему Давиду, отцу моему!');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15488, 'rst66', '1ki', 8, 25, 'И ныне, Господи, Боже Израилев, исполни рабу Твоему Давиду, отцу моему, то, что говорил Ты ему, сказав: «не прекратится у тебя пред лицом Моим сидящий на престоле Израилевом, если только сыновья твои будут держаться пути своего, ходя предо Мною так, как ты ходил предо Мною».');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15487, 'rst66', '1ki', 8, 24, 'Ты исполнил рабу Твоему Давиду, отцу моему, что говорил ему; что изрек Ты устами Твоими, то в сей день совершил рукою Твоею.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15486, 'rst66', '1ki', 8, 23, 'и сказал: Господи, Боже Израилев! нет подобного Тебе Бога на небесах вверху и на земле внизу; Ты хранишь завет и милость к рабам Твоим, ходящим пред Тобою всем сердцем своим.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15485, 'rst66', '1ki', 8, 22, 'И стал Соломон пред жертвенником Господним впереди всего собрания Израильтян, и воздвиг руки свои к небу,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15484, 'rst66', '1ki', 8, 21, 'и приготовил там место для ковчега, в котором завет Господа, заключенный Им с отцами нашими, когда Он вывел их из земли Египетской.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15483, 'rst66', '1ki', 8, 20, 'И исполнил Господь слово Свое, которое изрек. Я вступил на место отца моего Давида и сел на престоле Израилевом, как сказал Господь, и построил храм имени Господа, Бога Израилева;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15482, 'rst66', '1ki', 8, 19, 'однако не ты построишь храм, а сын твой, исшедший из чресл твоих, он построит храм имени Моему».');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15481, 'rst66', '1ki', 8, 18, 'но Господь сказал Давиду, отцу моему: «у тебя есть на сердце построить храм имени Моему; хорошо, что это у тебя лежит на сердце;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15480, 'rst66', '1ki', 8, 17, 'У Давида, отца моего, было на сердце построить храм имени Господа, Бога Израилева;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15479, 'rst66', '1ki', 8, 16, '«С того дня, как Я вывел народ Мой, Израиля, из Египта, Я не избрал города ни в одном из колен Израилевых, чтобы построен был дом, в котором пребывало бы имя Мое; и избрал Давида, чтобы быть ему над народом Моим Израилем».');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15478, 'rst66', '1ki', 8, 15, 'и сказал: благословен Господь, Бог Израилев, Который сказал Своими устами Давиду, отцу моему, и ныне исполнил рукою Своею! Он говорил:');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15477, 'rst66', '1ki', 8, 14, 'И обратился царь лицом своим, и благословил все собрание Израильтян &mdash; все собрание Израильтян стояло, &mdash;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15476, 'rst66', '1ki', 8, 13, 'я построил храм в жилище Тебе, место, чтобы пребывать Тебе вовеки.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15475, 'rst66', '1ki', 8, 12, 'Тогда сказал Соломон: Господь сказал, что Он благоволит обитать во мгле;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15474, 'rst66', '1ki', 8, 11, 'и не могли священники стоять на служении по причине облака, ибо слава Господня наполнила храм Господень.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15473, 'rst66', '1ki', 8, 10, 'Когда священники вышли из святилища, облако наполнило дом Господень;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15472, 'rst66', '1ki', 8, 9, 'В ковчеге ничего не было, кроме двух каменных скрижалей, которые положил туда Моисей на Хориве, когда Господь заключил завет с сынами Израилевыми, по исшествии их из земли Египетской.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15471, 'rst66', '1ki', 8, 8, 'И выдвинулись шесты так, что головки шестов видны были из святилища пред давиром, но не выказывались наружу; они там и до сего дня.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15470, 'rst66', '1ki', 8, 7, 'Ибо херувимы простирали крылья над местом ковчега, и покрывали херувимы сверху ковчег и шесты его.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15469, 'rst66', '1ki', 8, 6, 'И внесли священники ковчег завета Господня на место его, в давир храма, во Святое Святых, под крылья херувимов.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15468, 'rst66', '1ki', 8, 5, 'А царь Соломон и с ним все общество Израилево, собравшееся к нему, шли пред ковчегом, принося жертвы из мелкого и крупного скота, которых невозможно исчислить и определить, по множеству их.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15467, 'rst66', '1ki', 8, 4, 'и понесли ковчег Господень и скинию собрания и все священные вещи, которые были в скинии; и несли их священники и левиты.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15466, 'rst66', '1ki', 8, 3, 'И пришли все старейшины Израилевы; и подняли священники ковчег,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15465, 'rst66', '1ki', 8, 2, 'И собрались к царю Соломону на праздник все Израильтяне в месяце Афаниме, который есть седьмой месяц.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15464, 'rst66', '1ki', 8, 1, 'Тогда созвал Соломон старейшин Израилевых и всех начальников колен, глав поколений сынов Израилевых, к царю Соломону в Иерусалим, чтобы перенести ковчег завета Господня из города Давидова, то есть Сиона.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15463, 'rst66', '1ki', 7, 51, 'Так совершена вся работа, которую производил царь Соломон для храма Господа. И принес Соломон посвященное Давидом, отцом его; серебро и золото и вещи отдал в сокровищницы храма Господня.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15462, 'rst66', '1ki', 7, 50, 'и блюда, и ножи, и чаши, и лотки, и кадильницы из чистого золота, и петли у дверей внутреннего храма во Святом Святых и у дверей в храме из золота же.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15461, 'rst66', '1ki', 7, 49, 'и светильники &mdash; пять по правую сторону и пять по левую сторону, пред задним отделением храма, из чистого золота, и цветы, и лампадки, и щипцы из золота;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15460, 'rst66', '1ki', 7, 48, 'И сделал Соломон все вещи, которые в храме Господа: золотой жертвенник и золотой стол, на котором хлебы предложения;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15459, 'rst66', '1ki', 7, 47, 'И поставил Соломон все сии вещи <i>на место</i>. По причине чрезвычайного их множества вес меди не определен.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15458, 'rst66', '1ki', 7, 46, 'Царь выливал их в глинистой земле, в окрестности Иордана, между Сокхофом и Цартаном.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15457, 'rst66', '1ki', 7, 45, 'и тазы, и лопатки, и чаши. Все вещи, которые сделал Хирам царю Соломону для храма Господа, <i>были</i> из полированной меди.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15456, 'rst66', '1ki', 7, 44, 'одно море и двенадцать волов под морем;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15455, 'rst66', '1ki', 7, 43, 'и десять подстав и десять умывальниц на подставах;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15454, 'rst66', '1ki', 7, 42, 'и четыреста гранатовых яблок на двух сетках; два ряда гранатовых яблок для каждой сетки, для покрытия двух опоясок венцов, которые на столбах;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15453, 'rst66', '1ki', 7, 41, 'два столба и две опояски венцов, которые на верху столбов, и две сетки для покрытия двух опоясок венцов, которые на верху столбов;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15452, 'rst66', '1ki', 7, 40, 'И сделал Хирам умывальницы и лопатки и чаши. И кончил Хирам всю работу, которую производил у царя Соломона для храма Господня:');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15451, 'rst66', '1ki', 7, 39, 'И расставил подставы &mdash; пять на правой стороне храма и пять на левой стороне храма, а море поставил на правой стороне храма, на восточно‐южной стороне.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15450, 'rst66', '1ki', 7, 38, 'И сделал десять медных умывальниц: каждая умывальница вмещала сорок батов, каждая умывальница была в четыре локтя, каждая умывальница стояла на одной из десяти подстав.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15449, 'rst66', '1ki', 7, 37, 'Так сделал он десять подстав: у всех их одно литье, одна мера, один вид.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15448, 'rst66', '1ki', 7, 36, 'И изваял он на дощечках ее рукоятки и на стенках ее херувимов, львов и пальмы, сколько где позволяло место, и вокруг развесистые венки.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15447, 'rst66', '1ki', 7, 35, 'И на верху подставы круглое возвышение на пол‐локтя вышины; и на верху подставы рукоятки ее и стенки ее из одной с нею массы.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15446, 'rst66', '1ki', 7, 34, 'Четыре выступа на четырех углах каждой подставы; из подставы <i>выходили</i> выступы ее.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15445, 'rst66', '1ki', 7, 33, 'Устройство колес такое же, как устройство колес в колеснице; оси их, и ободья их, и спицы их, и ступицы их, все было литое.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15444, 'rst66', '1ki', 7, 32, 'Под стенками было четыре колеса, и оси колес в подставах; вышина каждого колеса &mdash; полтора локтя.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15443, 'rst66', '1ki', 7, 31, 'Отверстие от внутреннего венка до верха в один локоть; отверстие его круглое, подобно подножию столбов, в полтора локтя, и при отверстии его изваяния; но боковые стенки четырехугольные, не круглые.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15442, 'rst66', '1ki', 7, 30, 'у каждой подставы по четыре медных колеса и оси медные. На четырех углах выступы наподобие плеч, выступы литые внизу, под чашею, подле каждого венка.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15441, 'rst66', '1ki', 7, 29, 'на стенках, которые между наугольниками, <i>изображены</i> были львы, волы и херувимы; также и на наугольниках, а выше и ниже львов и волов &mdash; развесистые венки;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15440, 'rst66', '1ki', 7, 28, 'И вот устройство подстав: у них стенки, стенки между наугольными пластинками;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15439, 'rst66', '1ki', 7, 27, 'И сделал он десять медных подстав; длина каждой подставы &mdash; четыре локтя, ширина &mdash; четыре локтя и три локтя &mdash; вышина.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15438, 'rst66', '1ki', 7, 26, 'Толщиною оно было в ладонь, и края его, сделанные подобно краям чаши, <i>походили</i> на распустившуюся лилию. Оно вмещало две тысячи батов.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15437, 'rst66', '1ki', 7, 25, 'Оно стояло на двенадцати волах: три глядели к северу, три глядели к западу, три глядели к югу и три глядели к востоку; море лежало на них, и зады их <i>обращены были</i> внутрь под него.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15436, 'rst66', '1ki', 7, 24, '<i>Подобия</i> огурцов под краями его окружали его по десяти на локоть, окружали море со всех сторон в два ряда; <i>подобия</i> огурцов были вылиты с ним одним литьем.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15435, 'rst66', '1ki', 7, 23, 'И сделал литое <i>из меди</i> море, &mdash; от края его до края его десять локтей, &mdash; совсем круглое, вышиною в пять локтей, и снурок в тридцать локтей обнимал его кругом.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15434, 'rst66', '1ki', 7, 22, 'И над столбами поставил <i>венцы</i>, сделанные <i>наподобие</i> лилии; так окончена работа над столбами.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15433, 'rst66', '1ki', 7, 21, 'И поставил столбы к притвору храма; поставил столб на правой стороне и дал ему имя Иахин, и поставил столб на левой стороне и дал ему имя Воаз.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15432, 'rst66', '1ki', 7, 20, 'и венцы на обоих столбах вверху, прямо над выпуклостью, которая подле сетки; и на другом венце, рядами кругом, двести гранатовых яблок.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15431, 'rst66', '1ki', 7, 19, 'А в притворе венцы на верху столбов сделаны <i>наподобие</i> лилии, в четыре локтя,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15430, 'rst66', '1ki', 7, 18, 'Так сделал он столбы и два ряда гранатовых яблок вокруг сетки, чтобы покрыть венцы, которые на верху столбов; то же сделал и для другого венца.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15429, 'rst66', '1ki', 7, 17, 'сетки плетеной работы и снурки в виде цепочек для венцов, которые были на верху столбов: семь на одном венце и семь на другом венце.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15428, 'rst66', '1ki', 7, 16, 'и два венца, вылитых из меди, он сделал, чтобы положить на верху столбов: пять локтей вышины в одном венце и пять локтей вышины в другом венце;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15427, 'rst66', '1ki', 7, 15, 'И сделал он два медных столба, каждый в восемнадцать локтей вышиною, и снурок в двенадцать локтей обнимал <i>окружность</i> того и другого столба;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15426, 'rst66', '1ki', 7, 14, 'сына одной вдовы, из колена Неффалимова. Отец его Тирянин был медник; он владел способностью, искусством и уменьем выделывать всякие вещи из меди. И пришел он к царю Соломону и производил у него всякие работы:');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15425, 'rst66', '1ki', 7, 13, 'И послал царь Соломон и взял из Тира Хирама,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15424, 'rst66', '1ki', 7, 12, 'Большой двор огорожен был кругом тремя рядами тесаных камней и одним рядом кедровых бревен; также и внутренний двор храма Господа и притвор храма.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15423, 'rst66', '1ki', 7, 11, 'и сверху дорогие камни, обтесанные по размеру, и кедр.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15422, 'rst66', '1ki', 7, 10, 'И в основание положены были камни дорогие, камни большие, камни в десять локтей и камни в восемь локтей,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15421, 'rst66', '1ki', 7, 9, 'Все это сделано было из дорогих камней, обтесанных по размеру, обрезанных пилою, с внутренней и наружной стороны, от основания до выступов, и с наружной стороны до большого двора.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15420, 'rst66', '1ki', 7, 8, 'В доме, где он жил, другой двор позади притвора был такого же устройства. И в доме дочери фараоновой, которую взял за себя Соломон, он сделал такой же притвор.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15419, 'rst66', '1ki', 7, 7, 'Еще притвор с престолом, с которого он судил, притвор для судилища сделал он и покрыл все полы кедром.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15418, 'rst66', '1ki', 7, 6, 'И притвор из столбов сделал он длиною в пятьдесят локтей, шириною в тридцать локтей, и пред ними крыльцо, и столбы, и порог пред ними.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15417, 'rst66', '1ki', 7, 5, 'И все двери и дверные косяки были четырехугольные, и окно против окна, в три ряда.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15416, 'rst66', '1ki', 7, 4, 'Оконных косяков <i>было</i> три ряда; и три ряда <i>окон</i>, окно против окна.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15415, 'rst66', '1ki', 7, 3, 'И настлан был помост из кедра над бревнами на сорока пяти столбах, по пятнадцати в ряд.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15414, 'rst66', '1ki', 7, 2, 'И построил он дом из дерева Ливанского, длиною во сто локтей, шириною в пятьдесят локтей, а вышиною в тридцать локтей, на четырех рядах кедровых столбов; и кедровые бревна <i>положены были</i> на столбах.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15413, 'rst66', '1ki', 7, 1, 'А свой дом Соломон строил тринадцать лет и окончил весь дом свой.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15412, 'rst66', '1ki', 6, 38, 'а на одиннадцатом году, в месяце Буле, &mdash; это месяц восьмой, &mdash; он окончил храм со всеми принадлежностями его и по всем предначертаниям его; строил его семь лет.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15411, 'rst66', '1ki', 6, 37, 'В четвертый год, в месяц Зиф, положил он основание храму Господа,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15410, 'rst66', '1ki', 6, 36, 'И построил внутренний двор из трех рядов обтесанного камня и из ряда кедровых брусьев.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15409, 'rst66', '1ki', 6, 35, 'И вырезал <i>на них</i> херувимов и пальмы и распускающиеся цветы и обложил золотом по резьбе.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15408, 'rst66', '1ki', 6, 34, 'и две двери из кипарисового дерева; обе половинки одной двери были подвижные, и обе половинки другой двери были подвижные.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15407, 'rst66', '1ki', 6, 33, 'И у входа в храм сделал косяки из масличного дерева четырехугольные,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15406, 'rst66', '1ki', 6, 32, 'На двух половинах дверей из масличного дерева он сделал резных херувимов и пальмы и распускающиеся цветы и обложил золотом; покрыл золотом и херувимов и пальмы.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15405, 'rst66', '1ki', 6, 31, 'Для входа в давир сделал двери из масличного дерева, с пятиугольными косяками.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15404, 'rst66', '1ki', 6, 30, 'И пол в храме обложил золотом во внутренней и передней части.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15403, 'rst66', '1ki', 6, 29, 'И на всех стенах храма кругом сделал резные изображения херувимов и пальмовых дерев и распускающихся цветов, внутри и вне.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15402, 'rst66', '1ki', 6, 28, 'И обложил он херувимов золотом.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15401, 'rst66', '1ki', 6, 27, 'И поставил он херувимов среди внутренней части храма. Крылья же херувимов были распростерты, и касалось крыло одного <i>одной</i> стены, а крыло другого херувима касалось другой стены; другие же крылья их среди храма сходились крыло с крылом.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15400, 'rst66', '1ki', 6, 26, 'Высота одного херувима <i>была</i> десять локтей, также и другого херувима.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15399, 'rst66', '1ki', 6, 25, 'В десять локтей <i>был</i> и другой херувим; одинаковой меры и одинакового вида <i>были</i> оба херувима.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15398, 'rst66', '1ki', 6, 24, 'Одно крыло херувима было в пять локтей и другое крыло херувима в пять локтей; десять локтей было от одного конца крыльев его до другого конца крыльев его.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15397, 'rst66', '1ki', 6, 23, 'И сделал в давире двух херувимов из масличного дерева, вышиною в десять локтей.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15396, 'rst66', '1ki', 6, 22, 'Весь храм он обложил золотом, весь храм до конца, и весь жертвенник, который пред давиром, обложил золотом.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15395, 'rst66', '1ki', 6, 21, 'И обложил Соломон храм внутри чистым золотом, и протянул золотые цепи пред давиром, и обложил его золотом.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15394, 'rst66', '1ki', 6, 20, 'И давир был длиною в двадцать локтей, шириною в двадцать локтей и вышиною в двадцать локтей; он обложил его чистым золотом; обложил также и кедровый жертвенник.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15393, 'rst66', '1ki', 6, 19, 'Давир же внутри храма он приготовил для того, чтобы поставить там ковчег завета Господня.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15392, 'rst66', '1ki', 6, 18, 'На кедрах внутри храма были вырезаны <i>подобия</i> огурцов и распускающихся цветов; все было покрыто кедром, камня не видно было.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15391, 'rst66', '1ki', 6, 17, 'Сорока локтей <i>был</i> храм, то есть передняя часть храма.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15390, 'rst66', '1ki', 6, 16, 'И устроил в задней стороне храма, в двадцати локтях от края, стену, и обложил стены и потолок кедровыми досками, и устроил давир для Святого Святых.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15389, 'rst66', '1ki', 6, 15, 'И обложил стены храма внутри кедровыми досками; от пола храма до потолка внутри обложил деревом и покрыл пол храма кипарисовыми досками.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15388, 'rst66', '1ki', 6, 14, 'И построил Соломон храм и кончил его.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15387, 'rst66', '1ki', 6, 13, 'и буду жить среди сынов Израилевых, и не оставлю народа Моего, Израиля.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15386, 'rst66', '1ki', 6, 12, 'вот, ты строишь храм; если ты будешь ходить по уставам Моим, и поступать по определениям Моим и соблюдать все заповеди Мои, поступая по ним, то Я исполню на тебе слово Мое, которое Я сказал Давиду, отцу твоему,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15385, 'rst66', '1ki', 6, 11, 'И было слово Господа к Соломону, и сказано ему:');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15384, 'rst66', '1ki', 6, 10, 'И пристроил ко всему храму боковые комнаты вышиною в пять локтей; они прикреплены были к храму посредством кедровых бревен.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15383, 'rst66', '1ki', 6, 9, 'И построил он храм, и кончил его, и обшил храм кедровыми досками.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15382, 'rst66', '1ki', 6, 8, 'Вход в средний ярус был с правой стороны храма. По круглым лестницам всходили в средний <i>ярус</i>, а от среднего в третий.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15381, 'rst66', '1ki', 6, 7, 'Когда строился храм, на строение употребляемы были обтесанные камни; ни молота, ни тесла, ни всякого другого железного орудия не было слышно в храме при строении его.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15380, 'rst66', '1ki', 6, 6, 'Нижний <i>ярус</i> пристройки шириною был в пять локтей, средний шириною в шесть локтей, а третий шириною в семь локтей; ибо вокруг храма извне сделаны были уступы, дабы пристройка не прикасалась к стенам храма.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15379, 'rst66', '1ki', 6, 5, 'И сделал пристройку вокруг стен храма, вокруг храма и давира; и сделал боковые комнаты кругом.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15378, 'rst66', '1ki', 6, 4, 'И сделал он в доме окна решетчатые, глухие с откосами.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15377, 'rst66', '1ki', 6, 3, 'и притвор пред храмом в двадцать локтей длины, соответственно ширине храма, и в десять локтей ширины пред храмом.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15376, 'rst66', '1ki', 6, 2, 'Храм, который построил царь Соломон Господу, длиною был в шестьдесят локтей, шириною в двадцать и вышиною в тридцать локтей,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15375, 'rst66', '1ki', 6, 1, 'В четыреста восьмидесятом году по исшествии сынов Израилевых из земли Египетской, в четвертый год царствования Соломонова над Израилем, в месяц Зиф, который есть второй месяц, начал он строить храм Господу.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15374, 'rst66', '1ki', 5, 18, 'Обтесывали же их работники Соломоновы и работники Хирамовы и Гивлитяне, и приготовляли дерева и камни для строения дома.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15373, 'rst66', '1ki', 5, 17, 'И повелел царь привозить камни большие, камни дорогие, для основания дома, камни обделанные.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15372, 'rst66', '1ki', 5, 16, 'кроме трех тысяч трехсот начальников, поставленных Соломоном над работою для надзора за народом, который производил работу.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15371, 'rst66', '1ki', 5, 15, 'Еще у Соломона было семьдесят тысяч носящих тяжести и восемьдесят тысяч каменосеков в горах,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15370, 'rst66', '1ki', 5, 14, 'И посылал их на Ливан, по десяти тысяч на месяц, попеременно; месяц они были на Ливане, а два месяца в доме своем. Адонирам же начальствовал над ними.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15369, 'rst66', '1ki', 5, 13, 'И обложил царь Соломон повинностью весь Израиль; повинность же состояла в тридцати тысячах человек.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15368, 'rst66', '1ki', 5, 12, 'Господь дал мудрость Соломону, как обещал ему. И был мир между Хирамом и Соломоном, и они заключили между собою союз.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15367, 'rst66', '1ki', 5, 11, 'А Соломон давал Хираму двадцать тысяч ко&#769;ров пшеницы для продовольствия дома его и двадцать ко&#769;ров оливкового выбитого масла: столько давал Соломон Хираму каждый год.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15366, 'rst66', '1ki', 5, 10, 'И давал Хирам Соломону дерева кедровые и дерева кипарисовые, вполне по его желанию.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15365, 'rst66', '1ki', 5, 9, 'рабы мои свезут их с Ливана к морю, и я плотами доставлю их морем к месту, которое ты назначишь мне, и там сложу их, и ты возьмешь; но и ты исполни мое желание, чтобы доставлять хлеб для моего дома.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15364, 'rst66', '1ki', 5, 8, 'И послал Хирам к Соломону сказать: я выслушал то, за чем ты посылал ко мне, и исполню все желание твое о деревах кедровых и деревах кипарисовых;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15363, 'rst66', '1ki', 5, 7, 'Когда услышал Хирам слова Соломона, очень обрадовался и сказал: благословен ныне Господь, Который дал Давиду сына мудрого <i>для управления</i> этим многочисленным народом!');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15362, 'rst66', '1ki', 5, 6, 'итак, прикажи нарубить для меня кедров с Ливана; и вот, рабы мои будут вместе с твоими рабами, и я буду давать тебе плату за рабов твоих, какую ты назначишь; ибо ты знаешь, что у нас нет людей, которые умели бы рубить дерева так, как Сидоняне.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15361, 'rst66', '1ki', 5, 5, 'и вот, я намерен построить дом имени Господа, Бога моего, как сказал Господь отцу моему Давиду, говоря: «сын твой, которого Я посажу вместо тебя на престоле твоем, он построит дом имени Моему»;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15360, 'rst66', '1ki', 5, 4, 'ныне же Господь, Бог мой, даровал мне покой отовсюду: нет противника, и нет более препон;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15359, 'rst66', '1ki', 5, 3, 'ты знаешь, что Давид, отец мой, не мог построить дом имени Господа, Бога своего, по причине войн с окрестными народами, доколе Господь не покорил их под стопы ног его;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15358, 'rst66', '1ki', 5, 2, 'И послал также и Соломон к Хираму сказать:');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15357, 'rst66', '1ki', 5, 1, 'И послал Хирам, царь Тирский, слуг своих к Соломону, когда услышал, что его помазали в царя на место отца его; ибо Хирам был другом Давида во всю жизнь.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15356, 'rst66', '1ki', 4, 34, 'И приходили от всех народов послушать мудрости Соломона, от всех царей земных, которые слышали о мудрости его.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15355, 'rst66', '1ki', 4, 33, 'и говорил он о деревах, от кедра, что в Ливане, до иссопа, вырастающего из стены; говорил и о животных, и о птицах, и о пресмыкающихся, и о рыбах.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15354, 'rst66', '1ki', 4, 32, 'И изрек он три тысячи притчей, и песней его было тысяча и пять;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15353, 'rst66', '1ki', 4, 31, 'Он был мудрее всех людей, мудрее и Ефана Езрахитянина, и Емана, и Халкола, и Дарды, сыновей Махола, и имя его было в славе у всех окрестных народов.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15352, 'rst66', '1ki', 4, 30, 'И была мудрость Соломона выше мудрости всех сынов востока и всей мудрости Египтян.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15351, 'rst66', '1ki', 4, 29, 'И дал Бог Соломону мудрость и весьма великий разум, и обширный ум, как песок на берегу моря.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15350, 'rst66', '1ki', 4, 28, 'И ячмень и солому для коней и для мулов доставляли каждый в свою очередь на место, где находился царь.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15349, 'rst66', '1ki', 4, 27, 'И те приставники доставляли царю Соломону все принадлежащее к столу царя, каждый в свой месяц, и не допускали недостатка ни в чем.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15348, 'rst66', '1ki', 4, 26, 'И было у Соломона сорок тысяч стойл для коней колесничных и двенадцать тысяч для конницы.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15347, 'rst66', '1ki', 4, 25, 'И жили Иуда и Израиль спокойно, каждый под виноградником своим и под смоковницею своею, от Дана до Вирсавии, во все дни Соломона.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15346, 'rst66', '1ki', 4, 24, 'ибо он владычествовал над всею землею по эту сторону реки, от Типсаха до Газы, над всеми царями по эту сторону реки, и был у него мир со всеми окрестными странами.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15345, 'rst66', '1ki', 4, 23, 'десять волов откормленных и двадцать волов с пастбища, и сто овец, кроме оленей, и серн, и сайгаков, и откормленных птиц;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15344, 'rst66', '1ki', 4, 22, 'Продовольствие Соломона на каждый день составляли: тридцать ко&#769;ров муки пшеничной и шестьдесят ко&#769;ров прочей муки,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15343, 'rst66', '1ki', 4, 21, 'Соломон владел всеми царствами от реки <i>Евфрата</i> до земли Филистимской и до пределов Египта. Они приносили дары и служили Соломону во все дни жизни его.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15342, 'rst66', '1ki', 4, 20, 'Иуда и Израиль, многочисленные, как песок у моря, ели, пили и веселились.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15341, 'rst66', '1ki', 4, 19, 'Гевер, сын Урия, в земле Галаадской, в земле Сигона, царя Аморрейского, и Ога, царя Васанского. Он был приставник в этой земле.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15340, 'rst66', '1ki', 4, 18, 'Шимей, сын Елы, в <i>земле</i> Вениаминовой;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15339, 'rst66', '1ki', 4, 17, 'Иосафат, сын Паруаха, в <i>земле</i> Иссахаровой;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15338, 'rst66', '1ki', 4, 16, 'Ваана, сын Хушая, в <i>земле</i> Асировой и в Баалофе;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15337, 'rst66', '1ki', 4, 15, 'Ахимаас &mdash; в <i>земле</i> Неффалимовой; он взял себе в жену Васемафу, дочь Соломона;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15336, 'rst66', '1ki', 4, 14, 'Ахинадав, сын Гиддо, в Маханаиме;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15335, 'rst66', '1ki', 4, 13, 'Бен‐Гевер &mdash; в Рамофе Галаадском; у него были селения Иаира, сына Манассиина, что в Галааде; у него также область Аргов, что в Васане, шестьдесят больших городов со стенами и медными затворами;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15334, 'rst66', '1ki', 4, 12, 'Ваана, сын Ахилуда, в Фаанахе и Мегиддо и во всем Беф‐Сане, что близ Цартана ниже Иезрееля, от Беф‐Сана до Абел‐Мехола, и даже за Иокмеам;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15333, 'rst66', '1ki', 4, 11, 'Бен‐Авинадав &mdash; <i>над</i> всем Нафаф‐Дором; Тафафь, дочь Соломона, была его женою;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15332, 'rst66', '1ki', 4, 10, 'Бен‐Хесед &mdash; в Арюбофе; ему же принадлежал Соко и вся земля Хефер;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15331, 'rst66', '1ki', 4, 9, 'Бен‐Декер &mdash; в Макаце и в Шаалбиме, в Вефсамисе и в Елоне и в Беф‐Ханане;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15330, 'rst66', '1ki', 4, 8, 'Вот имена их: Бен‐Хур &mdash; на горе Ефремовой;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15329, 'rst66', '1ki', 4, 7, 'И было у Соломона двенадцать приставников над всем Израилем, и они доставляли продовольствие царю и дому его; каждый должен был доставлять продовольствие на один месяц в году.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15328, 'rst66', '1ki', 4, 6, 'Ахисар &mdash; начальник над домом <i>царским</i>, и Адонирам, сын Авды, &mdash; над податями.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15327, 'rst66', '1ki', 4, 5, 'Азария, сын Нафана, начальник над приставниками, и Завуф, сын Нафана священника &mdash; друг царя;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15326, 'rst66', '1ki', 4, 4, 'Ванея, сын Иодая, военачальник; Садок и Авиафар &mdash; священники;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15325, 'rst66', '1ki', 4, 3, 'Елихореф и Ахия, сыновья Сивы, писцы; Иосафат, сын Ахилуда, дееписатель;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15324, 'rst66', '1ki', 4, 2, 'И вот начальники, которые <i>были</i> у него: Азария, сын Садока священника;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15323, 'rst66', '1ki', 4, 1, 'И был царь Соломон царем над всем Израилем.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15322, 'rst66', '1ki', 3, 28, 'И услышал весь Израиль о суде, как рассудил царь; и стали бояться царя, ибо увидели, что мудрость Божия в нем, чтобы производить суд.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15321, 'rst66', '1ki', 3, 27, 'И отвечал царь и сказал: отдайте этой живое дитя, и не умерщвляйте его: она &mdash; его мать.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15320, 'rst66', '1ki', 3, 26, 'И отвечала та женщина, которой сын был живой, царю, ибо взволновалась вся внутренность ее от жалости к сыну своему: о, господин мой! отдайте ей этого ребенка живого и не умерщвляйте его. А другая говорила: пусть же не будет ни мне, ни тебе, рубите.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15319, 'rst66', '1ki', 3, 25, 'И сказал царь: рассеките живое дитя надвое и отдайте половину одной и половину другой.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15318, 'rst66', '1ki', 3, 24, 'И сказал царь: подайте мне меч. И принесли меч к царю.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15317, 'rst66', '1ki', 3, 23, 'И сказал царь: эта говорит: «мой сын живой, а твой сын мертвый»; а та говорит: «нет, твой сын мертвый, а мой сын живой».');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15316, 'rst66', '1ki', 3, 22, 'И сказала другая женщина: нет, мой сын живой, а твой сын мертвый. А та говорила ей: нет, твой сын мертвый, а мой живой. И говорили они так пред царем.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15315, 'rst66', '1ki', 3, 21, 'утром я встала, чтобы покормить сына моего, и вот, он был мертвый; а когда я всмотрелась в него утром, то это был не мой сын, которого я родила.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15314, 'rst66', '1ki', 3, 20, 'и встала она ночью, и взяла сына моего от меня, когда я, раба твоя, спала, и положила его к своей груди, а своего мертвого сына положила к моей груди;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15313, 'rst66', '1ki', 3, 19, 'и умер сын этой женщины ночью, ибо она заспала его;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15312, 'rst66', '1ki', 3, 18, 'на третий день после того, как я родила, родила и эта женщина; и были мы вместе, и в доме никого постороннего с нами не было; только мы две были в доме;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15311, 'rst66', '1ki', 3, 17, 'И сказала одна женщина: о, господин мой! я и эта женщина живем в одном доме; и я родила при ней в этом доме;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15310, 'rst66', '1ki', 3, 16, 'Тогда пришли две женщины блудницы к царю и стали пред ним.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15309, 'rst66', '1ki', 3, 15, 'И пробудился Соломон, и вот, <i>это было</i> сновидение. И пошел он в Иерусалим и стал пред ковчегом завета Господня, и принес всесожжения и совершил <i>жертвы</i> мирные, и сделал большой пир для всех слуг своих.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15308, 'rst66', '1ki', 3, 14, 'и если будешь ходить путем Моим, сохраняя уставы Мои и заповеди Мои, как ходил отец твой Давид, Я продолжу и дни твои.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15307, 'rst66', '1ki', 3, 13, 'и то, чего ты не просил, Я даю тебе, и богатство и славу, так что не будет подобного тебе между царями во все дни твои;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15306, 'rst66', '1ki', 3, 12, 'вот, Я сделаю по слову твоему: вот, Я даю тебе сердце мудрое и разумное, так что подобного тебе не было прежде тебя, и после тебя не восстанет подобный тебе;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15305, 'rst66', '1ki', 3, 11, 'И сказал ему Бог: за то, что ты просил этого и не просил себе долгой жизни, не просил себе богатства, не просил себе душ врагов твоих, но просил себе разума, чтоб уметь судить, &mdash;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15304, 'rst66', '1ki', 3, 10, 'И благоугодно было Господу, что Соломон просил этого.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15303, 'rst66', '1ki', 3, 9, 'даруй же рабу Твоему сердце разумное, чтобы судить народ Твой и различать, что добро и что зло; ибо кто может управлять этим многочисленным народом Твоим?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15302, 'rst66', '1ki', 3, 8, 'и раб Твой &mdash; среди народа Твоего, который избрал Ты, народа столь многочисленного, что по множеству его нельзя ни исчислить его, ни обозреть;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15301, 'rst66', '1ki', 3, 7, 'и ныне, Господи, Боже мой, Ты поставил раба Твоего царем вместо Давида, отца моего; но я отрок малый, не знаю ни моего выхода, ни входа;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15300, 'rst66', '1ki', 3, 6, 'И сказал Соломон: Ты сделал рабу Твоему Давиду, отцу моему, великую милость; и за то, что он ходил пред Тобою в истине и правде и с искренним сердцем пред Тобою, Ты сохранил ему эту великую милость и даровал ему сына, который сидел бы на престоле его, как это и есть ныне;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15299, 'rst66', '1ki', 3, 5, 'В Гаваоне явился Господь Соломону во сне ночью, и сказал Бог: проси, что дать тебе.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15298, 'rst66', '1ki', 3, 4, 'И пошел царь в Гаваон, чтобы принести там жертву, ибо там был главный жертвенник. Тысячу всесожжений вознес Соломон на том жертвеннике.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15297, 'rst66', '1ki', 3, 3, 'И возлюбил Соломон Господа, ходя по уставу Давида, отца своего; но и он приносил жертвы и курения на высотах.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15296, 'rst66', '1ki', 3, 2, 'Народ еще приносил жертвы на высотах, ибо не был построен дом имени Господа до того времени.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15295, 'rst66', '1ki', 3, 1, 'Соломон породнился с фараоном, царем Египетским, и взял за себя дочь фараона и ввел ее в город Давидов, доколе не построил дома своего и дома Господня и стены вокруг Иерусалима.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15294, 'rst66', '1ki', 2, 46, 'И повелел царь Ванее, сыну Иодаеву, и он пошел и поразил Семея, и тот умер.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15293, 'rst66', '1ki', 2, 45, 'А царь Соломон да будет благословен, и престол Давида да будет непоколебим пред Господом вовеки!');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15292, 'rst66', '1ki', 2, 44, 'И сказал царь Семею: ты знаешь, и знает сердце твое все зло, какое ты сделал отцу моему Давиду; да обратит же Господь злобу твою на голову твою!');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15291, 'rst66', '1ki', 2, 43, 'зачем же ты не соблюл приказания, которое я дал тебе пред Господом с клятвою?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15290, 'rst66', '1ki', 2, 42, 'И, послав, призвал царь Семея и сказал ему: не клялся ли я тебе Господом и не объявлял ли тебе, говоря: «знай, что в тот день, в который ты выйдешь и пойдешь куда‐нибудь, непременно умрешь»? и ты сказал мне: «хорошо»;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15289, 'rst66', '1ki', 2, 41, 'И донесли Соломону, что Семей ходил из Иерусалима в Геф и возвратился.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15288, 'rst66', '1ki', 2, 40, 'И встал Семей, и оседлал осла своего, и отправился в Геф к Анхусу искать рабов своих. И возвратился Семей и привел рабов своих из Гефа.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15287, 'rst66', '1ki', 2, 39, 'Но через три года случилось, что у Семея двое рабов убежали к Анхусу, сыну Маахи, царю Гефскому. И сказали Семею, говоря: вот, рабы твои в Гефе.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15286, 'rst66', '1ki', 2, 38, 'И сказал Семей царю: хорошо; как приказал господин мой, царь, так сделает раб твой. И жил Семей в Иерусалиме долгое время.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15285, 'rst66', '1ki', 2, 37, 'и знай, что в тот день, в который ты выйдешь и перейдешь поток Кедрон, непременно умрешь; кровь твоя будет на голове твоей.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15284, 'rst66', '1ki', 2, 36, 'И, послав, царь призвал Семея и сказал ему: построй себе дом в Иерусалиме и живи здесь, и никуда не выходи отсюда;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15283, 'rst66', '1ki', 2, 35, 'И поставил царь Соломон Ванею, сына Иодаева, вместо него над войском; а Садока священника поставил царь вместо Авиафара.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15282, 'rst66', '1ki', 2, 34, 'И пошел Ванея, сын Иодаев, и поразил Иоава, и умертвил его, и он был похоронен в доме своем в пустыне.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15281, 'rst66', '1ki', 2, 33, 'да обратится кровь их на голову Иоава и на голову потомства его навеки, а Давиду и потомству его, и дому его и престолу его да будет мир навеки от Господа!');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15280, 'rst66', '1ki', 2, 32, 'да обратит Господь кровь его на голову его за то, что он убил двух мужей невинных и лучших его: поразил мечом, без ведома отца моего Давида, Авенира, сына Нирова, военачальника Израильского, и Амессая, сына Иеферова, военачальника Иудейского;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15279, 'rst66', '1ki', 2, 31, 'Царь сказал ему: сделай, как он сказал, и умертви его и похорони его, и сними невинную кровь, пролитую Иоавом, с меня и с дома отца моего;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15278, 'rst66', '1ki', 2, 30, 'И пришел Ванея в скинию Господню и сказал ему: так сказал царь: выходи. И сказал тот: нет, я хочу умереть здесь. Ванея передал это царю, говоря: так сказал Иоав, и так отвечал мне.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15277, 'rst66', '1ki', 2, 29, 'И донесли царю Соломону, что Иоав убежал в скинию Господню и что он у жертвенника. И послал Соломон Ванею, сына Иодаева, говоря: пойди, умертви его.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15276, 'rst66', '1ki', 2, 28, 'Слух <i>об этом</i> дошел до Иоава, &mdash; так как Иоав склонялся на сторону Адонии, а на сторону Соломона не склонялся, &mdash; и убежал Иоав в скинию Господню и ухватился за роги жертвенника.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15275, 'rst66', '1ki', 2, 27, 'И удалил Соломон Авиафара от священства Господня, и исполнилось слово Господа, которое сказал Он о доме Илия в Силоме.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15274, 'rst66', '1ki', 2, 26, 'А священнику Авиафару царь сказал: ступай в Анафоф на твое поле; ты достоин смерти, но в настоящее время я не умерщвлю тебя, ибо ты носил ковчег Владыки Господа пред Давидом, отцом моим, и терпел все, что терпел отец мой.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15273, 'rst66', '1ki', 2, 25, 'И послал царь Соломон Ванею, сына Иодаева, который поразил его, и он умер.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15272, 'rst66', '1ki', 2, 24, 'ныне же, &mdash; жив Господь, укрепивший меня и посадивший меня на престоле Давида, отца моего, и устроивший мне дом, как говорил Он, &mdash; ныне же Адония должен умереть.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15271, 'rst66', '1ki', 2, 23, 'И поклялся царь Соломон Господом, говоря: то и то пусть сделает со мною Бог и еще больше сделает, если не на свою душу сказал Адония такое слово;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15270, 'rst66', '1ki', 2, 22, 'И отвечал царь Соломон и сказал матери своей: а зачем ты просишь Ависагу Сунамитянку для Адонии? проси ему <i>также</i> и царства; ибо он мой старший брат, и ему священник Авиафар и Иоав, сын Саруин, <i>друзья</i>.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15269, 'rst66', '1ki', 2, 21, 'И сказала она: дай Ависагу Сунамитянку Адонии, брату твоему, в жену.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15268, 'rst66', '1ki', 2, 20, 'и сказала: я имею к тебе одну небольшую просьбу, не откажи мне. И сказал ей царь: проси, мать моя; я не откажу тебе.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15267, 'rst66', '1ki', 2, 19, 'И вошла Вирсавия к царю Соломону говорить ему об Адонии. Царь встал перед нею, и поклонился ей, и сел на престоле своем. Поставили престол и для матери царя, и она села по правую руку его');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15266, 'rst66', '1ki', 2, 18, 'И сказала Вирсавия: хорошо, я поговорю о тебе царю.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15265, 'rst66', '1ki', 2, 17, 'И сказал он: прошу тебя, поговори царю Соломону, ибо он не откажет тебе, чтоб он дал мне Ависагу Сунамитянку в жену.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15264, 'rst66', '1ki', 2, 16, 'теперь я прошу тебя об одном, не откажи мне. Она сказала ему: говори.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15263, 'rst66', '1ki', 2, 15, 'И сказал он: ты знаешь, что царство принадлежало мне, и весь Израиль обращал на меня взоры свои, как на будущего царя; но царство отошло от меня и досталось брату моему, ибо от Господа это было ему;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15262, 'rst66', '1ki', 2, 14, 'И сказал он: у меня есть слово к тебе. Она сказала: говори.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15261, 'rst66', '1ki', 2, 13, 'И пришел Адония, сын Аггифы, к Вирсавии, матери Соломона. Она сказала: с миром ли приход твой? И сказал он: с миром.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15260, 'rst66', '1ki', 2, 12, 'И сел Соломон на престоле Давида, отца своего, и царствование его было очень твердо.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15259, 'rst66', '1ki', 2, 11, 'Времени царствования Давида над Израилем было сорок лет: в Хевроне царствовал он семь лет и тридцать три года царствовал в Иерусалиме.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15258, 'rst66', '1ki', 2, 10, 'И почил Давид с отцами своими и погребен был в городе Давидовом.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15257, 'rst66', '1ki', 2, 9, 'Ты же не оставь его безнаказанным; ибо ты человек мудрый и знаешь, что тебе сделать с ним, чтобы низвести седину его в крови в преисподнюю.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15256, 'rst66', '1ki', 2, 8, 'Вот, еще у тебя Семей, сын Геры Вениамитянина из Бахурима; он злословил меня тяжким злословием, когда я шел в Маханаим; но он вышел навстречу мне у Иордана, и я поклялся ему Господом, говоря: «я не умерщвлю тебя мечом».');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15255, 'rst66', '1ki', 2, 7, 'А сынам Верзеллия Галаадитянина окажи милость, чтоб они были между питающимися твоим столом, ибо они пришли ко мне, когда я бежал от Авессалома, брата твоего.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15254, 'rst66', '1ki', 2, 6, 'поступи по мудрости твоей, чтобы не отпустить седины его мирно в преисподнюю.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15253, 'rst66', '1ki', 2, 5, 'Еще: ты знаешь, что сделал мне Иоав, сын Саруин, как поступил он с двумя вождями войска Израильского, с Авениром, сыном Нировым, и Амессаем, сыном Иеферовым, как он умертвил их и пролил кровь бранную во время мира, обагрив кровью бранною пояс на чреслах своих и обувь на ногах своих:');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15252, 'rst66', '1ki', 2, 4, 'чтобы Господь исполнил слово Свое, которое Он сказал обо мне, говоря: «если сыны твои будут наблюдать за путями своими, чтобы ходить предо Мною в истине от всего сердца своего и от всей души своей, то не прекратится муж от тебя на престоле Израилевом».');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15251, 'rst66', '1ki', 2, 3, 'и храни завет Господа, Бога твоего, ходя путями Его и соблюдая уставы Его и заповеди Его, и определения Его и постановления Его, как написано в законе Моисеевом, чтобы быть тебе благоразумным во всем, что ни будешь делать, и везде, куда ни обратишься;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15250, 'rst66', '1ki', 2, 2, 'вот, я отхожу в путь всей земли, ты же будь тверд и будь мужествен');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15249, 'rst66', '1ki', 2, 1, 'Приблизилось время умереть Давиду, и завещал он сыну своему Соломону, говоря:');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15248, 'rst66', '1ki', 1, 53, 'И послал царь Соломон, и привели его от жертвенника. И он пришел и поклонился царю Соломону; и сказал ему Соломон: иди в дом свой.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15247, 'rst66', '1ki', 1, 52, 'И сказал Соломон: если он будет человеком честным, то ни один волос его не упадет на землю; если же найдется в нем лукавство, то умрет.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15246, 'rst66', '1ki', 1, 51, 'И донесли Соломону, говоря: вот, Адония боится царя Соломона, и вот, он держится за роги жертвенника, говоря: «пусть поклянется мне теперь царь Соломон, что он не умертвит раба своего мечом».');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15245, 'rst66', '1ki', 1, 50, 'Адония же, боясь Соломона, встал и пошел и ухватился за роги жертвенника.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15244, 'rst66', '1ki', 1, 49, '<i>Тогда</i> испугались и встали все приглашенные, которые были у Адонии, и пошли каждый своею дорогою.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15243, 'rst66', '1ki', 1, 48, 'и сказал царь так: «благословен Господь, Бог Израилев, Который сегодня дал сидящего на престоле моем, и очи мои видят это!»');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15242, 'rst66', '1ki', 1, 47, 'И слуги царя приходили поздравить господина нашего, царя Давида, говоря: «Бог твой да прославит имя Соломона более твоего имени и да возвеличит престол его более твоего престола». И поклонился царь на ложе своем,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15241, 'rst66', '1ki', 1, 46, 'И Соломон уже сел на царском престоле.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15240, 'rst66', '1ki', 1, 45, 'и помазали его Садок священник и Нафан пророк в царя в Гионе, и оттуда отправились с радостью, и пришел в движение город. Вот отчего шум, который вы слышите.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15239, 'rst66', '1ki', 1, 44, 'и послал царь с ним Садока священника и Нафана пророка, и Ванею, сына Иодая, и Хелефеев и Фелефеев, и они посадили его на мула царского;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15238, 'rst66', '1ki', 1, 43, 'И отвечал Ионафан и сказал Адонии: да, господин наш, царь Давид, поставил Соломона царем;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15237, 'rst66', '1ki', 1, 42, 'Еще он говорил, как пришел Ионафан, сын священника Авиафара. И сказал Адония: войди; ты &mdash; честный человек и несешь добрую весть.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15236, 'rst66', '1ki', 1, 41, 'И услышал Адония и все приглашенные им, как только перестали есть; а Иоав, услышав звук трубы, сказал: отчего этот шум волнующегося города?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15235, 'rst66', '1ki', 1, 40, 'И весь народ провожал Соломона, и играл народ на свирелях, и весьма радовался, так что земля расседалась от криков его.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15234, 'rst66', '1ki', 1, 39, 'И взял Садок священник рог с елеем из скинии и помазал Соломона. И затрубили трубою, и весь народ восклицал: да живет царь Соломон!');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15233, 'rst66', '1ki', 1, 38, 'И пошли Садок священник и Нафан пророк и Ванея, сын Иодая, и Хелефеи и Фелефеи, и посадили Соломона на мула царя Давида, и повели его к Гиону.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15232, 'rst66', '1ki', 1, 37, 'Как был Господь, Бог, с господином моим, царем, так да будет Он с Соломоном и да возвеличит престол его более престола господина моего, царя Давида!');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15231, 'rst66', '1ki', 1, 36, 'И отвечал Ванея, сын Иодаев, царю и сказал: аминь, &mdash; да скажет так Господь, Бог господина моего, царя!');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15230, 'rst66', '1ki', 1, 35, 'Потом проводите его назад, и он придет и сядет на престоле моем; он будет царствовать вместо меня; ему завещал я быть вождем Израиля и Иуды.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15229, 'rst66', '1ki', 1, 34, 'и да помажет его там Садок священник и Нафан пророк в царя над Израилем, и затрубите трубою и возгласите: «да живет царь Соломон!»');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15228, 'rst66', '1ki', 1, 33, 'И сказал им царь: возьмите с собою слуг господина вашего и посадите Соломона, сына моего, на мула моего, и сведите его к Гиону,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15227, 'rst66', '1ki', 1, 32, 'И сказал царь Давид: позовите ко мне священника Садока и пророка Нафана и Ванею, сына Иодаева. И вошли они к царю.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15226, 'rst66', '1ki', 1, 31, 'И наклонилась Вирсавия лицом до земли, и поклонилась царю, и сказала: да живет господин мой, царь Давид, вовеки!');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15225, 'rst66', '1ki', 1, 30, 'Как я клялся тебе Господом, Богом Израилевым, говоря, что Соломон, сын твой, будет царствовать после меня и он сядет на престоле моем вместо меня, так я и сделаю это сегодня.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15224, 'rst66', '1ki', 1, 29, 'И клялся царь и сказал: жив Господь, избавлявший душу мою от всякой беды!');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15223, 'rst66', '1ki', 1, 28, 'И отвечал царь Давид и сказал: позовите ко мне Вирсавию. И вошла она и стала пред царем.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15222, 'rst66', '1ki', 1, 27, 'Не сталось ли это по <i>воле</i> господина моего, царя, и для чего ты не открыл рабу твоему, кто сядет на престоле господина моего, царя, после него?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15221, 'rst66', '1ki', 1, 26, 'А меня, раба твоего, и священника Садока, и Ванею, сына Иодаева, и Соломона, раба твоего, не пригласил.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15220, 'rst66', '1ki', 1, 25, 'Потому что он ныне сошел и заколол множество волов, тельцов и овец, и пригласил всех сыновей царских и военачальников и священника Авиафара, и вот, они едят и пьют у него и говорят: «да живет царь Адония!»');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15219, 'rst66', '1ki', 1, 24, 'И сказал Нафан: господин мой, царь! сказал ли ты: «Адония будет царствовать после меня и он сядет на престоле моем»?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15218, 'rst66', '1ki', 1, 23, 'И сказали царю, говоря: вот Нафан пророк. И вошел он к царю и поклонился царю лицом до земли.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15217, 'rst66', '1ki', 1, 22, 'Когда она еще говорила с царем, пришел и пророк Нафан.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15216, 'rst66', '1ki', 1, 21, 'иначе, когда господин мой, царь, почиет с отцами своими, падет обвинение на меня и на сына моего Соломона.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15215, 'rst66', '1ki', 1, 20, 'Но ты, господин мой, &mdash; царь, и глаза всех Израильтян <i>устремлены</i> на тебя, чтобы ты объявил им, кто сядет на престоле господина моего, царя, после него;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15214, 'rst66', '1ki', 1, 19, 'И заколол он множество волов, тельцов и овец, и пригласил всех сыновей царских и священника Авиафара, и военачальника Иоава; Соломона же, раба твоего, не пригласил.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15213, 'rst66', '1ki', 1, 18, 'А теперь, вот, Адония <i>воцарился</i>, и ты, господин мой, царь, не знаешь <i>о том</i>.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15212, 'rst66', '1ki', 1, 17, 'Она сказала ему: господин мой, царь! ты клялся рабе твоей Господом, Богом твоим: «сын твой Соломон будет царствовать после меня, и он сядет на престоле моем».');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15211, 'rst66', '1ki', 1, 16, 'и наклонилась Вирсавия и поклонилась царю; и сказал царь: что тебе?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15210, 'rst66', '1ki', 1, 15, 'Вирсавия пошла к царю в спальню; царь был очень стар, и Ависага Сунамитянка прислуживала царю;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15209, 'rst66', '1ki', 1, 14, 'И вот, когда ты еще будешь говорить там с царем, войду и я вслед за тобою и дополню слова твои.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15208, 'rst66', '1ki', 1, 13, 'Иди и войди к царю Давиду и скажи ему: не клялся ли ты, господин мой, царь, рабе твоей, говоря: «сын твой Соломон будет царем после меня и он сядет на престоле моем»? Почему же воцарился Адония?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15207, 'rst66', '1ki', 1, 12, 'Теперь, вот, я советую тебе: спасай жизнь твою и жизнь сына твоего Соломона.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15206, 'rst66', '1ki', 1, 11, 'Тогда Нафан сказал Вирсавии, матери Соломона, говоря: слышала ли ты, что Адония, сын Аггифин, сделался царем, а господин наш Давид не знает <i>о том?</i>');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15205, 'rst66', '1ki', 1, 10, 'Пророка же Нафана и Ванею, и тех сильных, и Соломона, брата своего, не пригласил.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15204, 'rst66', '1ki', 1, 9, 'И заколол Адония овец и волов и тельцов у камня Зохелет, что у источника Рогель, и пригласил всех братьев своих, сыновей царя, со всеми Иудеянами, служившими у царя.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15203, 'rst66', '1ki', 1, 8, 'Но священник Садок и Ванея, сын Иодаев, и пророк Нафан, и Семей, и Рисий, и сильные Давидовы не были на стороне Адонии.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15202, 'rst66', '1ki', 1, 7, 'И советовался он с Иоавом, сыном Саруиным, и с Авиафаром священником, и они помогали Адонии.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15201, 'rst66', '1ki', 1, 6, 'Отец же никогда не стеснял его вопросом: для чего ты это делаешь? Он же был очень красив и родился ему после Авессалома.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15200, 'rst66', '1ki', 1, 5, 'Адония, сын Аггифы, возгордившись, говорил: я буду царем. И завел себе колесницы и всадников и пятьдесят человек скороходов.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15199, 'rst66', '1ki', 1, 4, 'Девица была очень красива, и ходила она за царем и прислуживала ему; но царь не познал ее.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15198, 'rst66', '1ki', 1, 3, 'И искали красивой девицы во всех пределах Израильских, и нашли Ависагу Сунамитянку, и привели ее к царю.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15197, 'rst66', '1ki', 1, 2, 'И сказали ему слуги его: пусть поищут для господина нашего, царя, молодую девицу, чтоб она предстояла царю и ходила за ним и лежала с ним, &mdash; и будет тепло господину нашему, царю.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (15196, 'rst66', '1ki', 1, 1, 'Когда царь Давид состарился, войдя в <i>преклонные</i> лета, то покрывали его одеждами, но не мог он согреться.');",
        ];

