"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (18500, 'rst66', '1ch', 1, 6, 'Сыновья Гомера: Аскеназ, Рифат и Фогарма.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (18501, 'rst66', '1ch', 1, 7, 'Сыновья Иавана: Елиса, Фарсис, Киттим и Доданим.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (18502, 'rst66', '1ch', 1, 8, 'Сыновья Хама: Хуш, Мицраим, Фут и Ханаан.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (18503, 'rst66', '1ch', 1, 9, 'Сыновья Хуша: Сева, Хавила, Савта, Раама и Савтеха. Сыновья Раамы: Шева и Дедан.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (18504, 'rst66', '1ch', 1, 10, 'Хуш родил <i>также</i> Нимрода: сей начал быть сильным на земле.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (18505, 'rst66', '1ch', 1, 11, 'Мицраим родил: Лудима, Анамима, Легавима, Нафтухима,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (18506, 'rst66', '1ch', 1, 12, 'Патрусима, Каслухима, от которого произошли Филистимляне, и Кафторима.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (18507, 'rst66', '1ch', 1, 13, 'Ханаан родил Сидона, первенца своего, Хета,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (18508, 'rst66', '1ch', 1, 14, 'Иевусея, Аморрея, Гергесея,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (18509, 'rst66', '1ch', 1, 15, 'Евея, Аркея, Синея,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (18510, 'rst66', '1ch', 1, 16, 'Арвадея, Цемарея и Хамафея.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (18511, 'rst66', '1ch', 1, 17, 'Сыновья Сима: Елам, Ассур, Арфаксад, Луд, Арам, Уц, Хул, Гефер и Мешех.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (18512, 'rst66', '1ch', 1, 18, 'Арфаксад родил Салу, Сала же родил Евера.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (18513, 'rst66', '1ch', 1, 19, 'У Евера родились два сына: имя одному Фалек, потому что во дни его разделилась земля; имя брату его Иоктан.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (18514, 'rst66', '1ch', 1, 20, 'Иоктан родил Алмодада, Шалефа, Хацармавета, Иераха,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (18515, 'rst66', '1ch', 1, 21, 'Гадорама, Узала, Диклу,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (18516, 'rst66', '1ch', 1, 22, 'Евала, Авимаила, Шеву,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (18517, 'rst66', '1ch', 1, 23, 'Офира, Хавилу и Иовава. Все эти сыновья Иоктана.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (18518, 'rst66', '1ch', 1, 24, 'Сим, Арфаксад, Сала,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (18519, 'rst66', '1ch', 1, 25, 'Евер, Фалек, Рагав,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (18520, 'rst66', '1ch', 1, 26, 'Серух, Нахор, Фарра,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (18521, 'rst66', '1ch', 1, 27, 'Аврам, он же Авраам.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (18522, 'rst66', '1ch', 1, 28, 'Сыновья Авраама: Исаак и Измаил.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (18523, 'rst66', '1ch', 1, 29, 'Вот родословие их: первенец Измаилов Наваиоф, <i>за ним</i> Кедар, Адбеел, Мивсам,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (18524, 'rst66', '1ch', 1, 30, 'Мишма, Дума, Масса, Хадад, Фема,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (18525, 'rst66', '1ch', 1, 31, 'Иетур, Нафиш и Кедма. Это сыновья Измаиловы.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (18526, 'rst66', '1ch', 1, 32, 'Сыновья Хеттуры, наложницы Авраамовой: она родила Зимрана, Иокшана, Медана, Мадиана, Ишбака и Шуаха. Сыновья Иокшана: Шева и Дедан.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (18527, 'rst66', '1ch', 1, 33, 'Сыновья Мадиана: Ефа, Ефер, Ханох, Авида и Елдага. Все эти сыновья Хеттуры.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (18528, 'rst66', '1ch', 1, 34, 'И родил Авраам Исаака. Сыновья Исаака: Исав и Израиль.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (18529, 'rst66', '1ch', 1, 35, 'Сыновья Исава: Елифаз, Рагуил, Иеус, Иеглом и Корей.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (18530, 'rst66', '1ch', 1, 36, 'Сыновья Елифаза: Феман, Омар, Цефо, Гафам, Кеназ, Фимна, Амалик');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (18531, 'rst66', '1ch', 1, 37, 'Сыновья Рагуила: Нахаф, Зерах, Шамма и Миза.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (18532, 'rst66', '1ch', 1, 38, 'Сыновья Сеира: Лотан, Шовал, Цивеон, Ана, Дишон, Ецер и Дишан.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (18533, 'rst66', '1ch', 1, 39, 'Сыновья Лотана: Хори и Гемам; а сестра у Лотана: Фимна.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (18534, 'rst66', '1ch', 1, 40, 'Сыновья Шовала: Алеан, Манахаф, Евал, Шефо и Онам. Сыновья Цивеона: Аиа и Ана.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (18535, 'rst66', '1ch', 1, 41, 'Дети Аны: Дишон. Сыновья Дишона: Хемдан, Ешбан, Ифран и Херан.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (18536, 'rst66', '1ch', 1, 42, 'Сыновья Ецера: Билган, Зааван и Акан. Сыновья Дишана: Уц и Аран.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (18537, 'rst66', '1ch', 1, 43, 'Сии суть цари, царствовавшие в земле Едома, прежде нежели воцарился царь над сынами Израилевыми: Бела, сын Веора, и имя городу его &mdash; Дингава;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (18538, 'rst66', '1ch', 1, 44, 'и умер Бела, и воцарился по нем Иовав, сын Зераха, из Восоры.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (18539, 'rst66', '1ch', 1, 45, 'И умер Иовав, и воцарился по нем Хушам, из земли Феманитян.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (18540, 'rst66', '1ch', 1, 46, 'И умер Хушам, и воцарился по нем Гадад, сын Бедадов, который поразил Мадианитян на поле Моава; имя городу его: Авив.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (18541, 'rst66', '1ch', 1, 47, 'И умер Гадад, и воцарился по нем Самла, из Масреки.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (18542, 'rst66', '1ch', 1, 48, 'И умер Самла, и воцарился по нем Саул из Реховофа, <i>что</i> при реке.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (18543, 'rst66', '1ch', 1, 49, 'И умер Саул, и воцарился по нем Баал‐Ханан, сын Ахбора.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (18544, 'rst66', '1ch', 1, 50, 'И умер Баал‐Ханан, и воцарился по нем Гадар; имя городу его Пау; имя жене его Мегетавеель, дочь Матреда, дочь Мезагава.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (18545, 'rst66', '1ch', 1, 51, 'И умер Гадар. И были старейшины у Едома: старейшина Фимна, старейшина Алва, старейшина Иетеф,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (18546, 'rst66', '1ch', 1, 52, 'старейшина Оливема, старейшина Эла, старейшина Пинон,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (18547, 'rst66', '1ch', 1, 53, 'старейшина Кеназ, старейшина Феман, старейшина Мивцар,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (18548, 'rst66', '1ch', 1, 54, 'старейшина Магдиил, старейшина Ирам. Вот старейшины Идумейские.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (18549, 'rst66', '1ch', 2, 1, 'Вот сыновья Израиля: Рувим, Симеон, Левий, Иуда, Иссахар, Завулон,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (18550, 'rst66', '1ch', 2, 2, 'Дан, Иосиф, Вениамин, Неффалим, Гад и Асир.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (18551, 'rst66', '1ch', 2, 3, 'Сыновья Иуды: Ир, Онан и Силом, &mdash; трое родились у него от дочери Шуевой, Хананеянки. И был Ир, первенец Иудин, не благоугоден в очах Господа, и Он умертвил его.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (18552, 'rst66', '1ch', 2, 4, 'И Фамарь, невестка его, родила ему Фареса и Зару. Всех сыновей у Иуды было пятеро.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (18553, 'rst66', '1ch', 2, 5, 'Сыновья Фареса: Есром и Хамул.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (18554, 'rst66', '1ch', 2, 6, 'Сыновья Зары: Зимри, Ефан, Еман, Халкол и Дара; всех их пятеро.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (18555, 'rst66', '1ch', 2, 7, 'Сыновья Харми: Ахар, наведший беду на Израиля, нарушив заклятие.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (18556, 'rst66', '1ch', 2, 8, 'Сын Ефана: Азария.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (18557, 'rst66', '1ch', 2, 9, 'Сыновья Есрома, которые родились у него: Иерахмеил, Арам и Хелувай.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (18558, 'rst66', '1ch', 2, 10, 'Арам же родил Аминадава; Аминадав родил Наассона, князя сынов Иудиных;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (18559, 'rst66', '1ch', 2, 11, 'Наассон родил Салмона, Салмон родил Вооза;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (18560, 'rst66', '1ch', 2, 12, 'Вооз родил Овида, Овид родил Иессея;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (18561, 'rst66', '1ch', 2, 13, 'Иессей родил первенца своего Елиава, второго &mdash; Аминадава, третьего &mdash; Самму,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (18562, 'rst66', '1ch', 2, 14, 'четвертого &mdash; Нафанаила, пятого &mdash; Раддая,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (18563, 'rst66', '1ch', 2, 15, 'шестого &mdash; Оцема, седьмого &mdash; Давида.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (18564, 'rst66', '1ch', 2, 16, 'Сестры их: Саруия и Авигея. Сыновья Саруии: Авесса, Иоав и Азаил, трое.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (18565, 'rst66', '1ch', 2, 17, 'Авигея родила Амессу; отец же Амессы &mdash; Иефер, Измаильтянин.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (18566, 'rst66', '1ch', 2, 18, 'Халев, сын Есрома, родил от Азувы, жены <i>своей</i>, и от Иериофы, и вот сыновья его: Иешер, Шовав и Ардон.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (18567, 'rst66', '1ch', 2, 19, 'И умерла Азува; и взял себе Халев Ефрафу, и она родила ему Хура.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (18568, 'rst66', '1ch', 2, 20, 'Хур родил Урия, Урий родил Веселиила.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (18569, 'rst66', '1ch', 2, 21, 'После Есром вошел к дочери Махира, отца Галаадова, и взял ее, будучи шестидесяти лет, и она родила ему Сегува.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (18570, 'rst66', '1ch', 2, 22, 'Сегув родил Иаира, и было у него двадцать три города в земле Галаадской.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (18571, 'rst66', '1ch', 2, 23, 'Но Гессуряне и Сирияне взяли у них селения Иаира, Кенаф и зависящие от него города, &mdash; шестьдесят городов. Все эти города сыновей Махира, отца Галаадова.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (18572, 'rst66', '1ch', 2, 24, 'По смерти Есрома в Халев‐Ефрафе жена Есромова, Авия, родила ему Ашхура, отца Фекои.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (18573, 'rst66', '1ch', 2, 25, 'Сыновья Иерахмеила, первенца Есромова, были: первенец Рам, <i>за ним</i> Вуна, Орен, Оцем и Ахия.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (18574, 'rst66', '1ch', 2, 26, 'Была у Иерахмеила и другая жена, имя ее Афара; она мать Онама.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (18575, 'rst66', '1ch', 2, 27, 'Сыновья Рама, первенца Иерахмеилова, были: Маац, Иамин и Екер.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (18576, 'rst66', '1ch', 2, 28, 'Сыновья Онама были: Шаммай и Иада. Сыновья Шаммая: Надав и Авишур.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (18577, 'rst66', '1ch', 2, 29, 'Имя жене Авишуровой Авихаиль, и она родила ему Ахбана и Молида.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (18578, 'rst66', '1ch', 2, 30, 'Сыновья Надава: Селед и Афаим. И умер Селед бездетным.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (18579, 'rst66', '1ch', 2, 31, 'Сын Афаима: Иший. Сын Ишия: Шешан. Сын Шешана: Ахлай.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (18580, 'rst66', '1ch', 2, 32, 'Сыновья Иады, брата Шаммаева: Иефер и Ионафан. Иефер умер бездетным.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (18581, 'rst66', '1ch', 2, 33, 'Сыновья Ионафана: Пелеф и Заза. Это сыновья Иерахмеила.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (18582, 'rst66', '1ch', 2, 34, 'У Шешана не было сыновей, а только дочери. У Шешана <i>был</i> раб, Египтянин, имя его Иарха;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (18583, 'rst66', '1ch', 2, 35, 'Шешан отдал дочь свою Иархе, рабу своему, в жену: и она родила ему Аттая.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (18584, 'rst66', '1ch', 2, 36, 'Аттай родил Нафана, Нафан родил Завада;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (18585, 'rst66', '1ch', 2, 37, 'Завад родил Ефлала, Ефлал родил Овида;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (18586, 'rst66', '1ch', 2, 38, 'Овид родил Иеуя, Иеуй родил Азарию;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (18587, 'rst66', '1ch', 2, 39, 'Азария родил Хелеца, Хелец родил Елеасу;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (18588, 'rst66', '1ch', 2, 40, 'Елеаса родил Сисмая, Сисмай родил Саллума;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (18589, 'rst66', '1ch', 2, 41, 'Саллум родил Иекамию, Иекамия родил Елишаму.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (18590, 'rst66', '1ch', 2, 42, 'Сыновья Халева, брата Иерахмеилова: Меша, первенец его, &mdash; он отец Зифа; и сыновья Мареши, отца Хеврона.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (18591, 'rst66', '1ch', 2, 43, 'Сыновья Хеврона: Корей и Таппуах, и Рекем и Шема.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (18592, 'rst66', '1ch', 2, 44, 'Шема родил Рахама, отца Иоркеамова, а Рекем родил Шаммая.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (18593, 'rst66', '1ch', 2, 45, 'Сын Шаммая Маон, а Маон &mdash; отец Беф‐Цура.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (18594, 'rst66', '1ch', 2, 46, 'И Ефа, наложница Халевова, родила Харана, Моцу и Газеза. И Харан родил Газеза.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (18595, 'rst66', '1ch', 2, 47, 'Сыновья Иегдая: Регем, Иофам, Гешан, Пелет, Ефа и Шааф.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (18596, 'rst66', '1ch', 2, 48, 'Наложница Халевова, Мааха, родила Шевера и Фирхану;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (18597, 'rst66', '1ch', 2, 49, 'она же родила Шаафа, отца Мадманны, Шеву, отца Махбены и отца Гивеи. Дочь же Халева &mdash; Ахса.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (18598, 'rst66', '1ch', 2, 50, 'Вот сыновья Халева: сын Хур, первенец Ефрафы; Шовал, отец Кириаф‐Иарима;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (18599, 'rst66', '1ch', 2, 51, 'Салма, отец Вифлеема; Хареф, отец Бефгадера.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (18600, 'rst66', '1ch', 2, 52, 'У Шовала, отца Кириаф‐Иарима, были сыновья: Гарое, Хаци, Галменюхот.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (18601, 'rst66', '1ch', 2, 53, 'Племена Кириаф‐Иарима: Ифрияне, Футияне, Шумафане и Мидраитяне. От сих произошли Цоряне и Ештаоляне.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (18602, 'rst66', '1ch', 2, 54, 'Сыновья Салмы: Вифлеемляне и Нетофафяне, венец дома Иоавова и половина Менухотян &mdash; Цоряне,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (18603, 'rst66', '1ch', 2, 55, 'И племена Соферийцев, живших в Иабеце, Тирейцы, Шимейцы, Сухайцы: это Кинеяне, происшедшие от Хамафа, отца Бетрехава.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (18604, 'rst66', '1ch', 3, 1, 'Сыновья Давида, родившиеся у него в Хевроне, были: первенец Амнон, от Ахиноамы Изреелитянки; второй &mdash; Далуия, от Авигеи Кармилитянки;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (18605, 'rst66', '1ch', 3, 2, 'третий &mdash; Авессалом, сын Маахи, дочери Фалмая, царя Гессурского; четвертый &mdash; Адония, сын Аггифы;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (18606, 'rst66', '1ch', 3, 3, 'пятый &mdash; Сафатия, от Авиталы; шестой &mdash; Ифреам, от Аглаи, жены его, &mdash;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (18607, 'rst66', '1ch', 3, 4, 'шесть родившихся у него в Хевроне; царствовал же он там семь лет и шесть месяцев; а тридцать три года царствовал в Иерусалиме.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (18608, 'rst66', '1ch', 3, 5, 'А сии родились у него в Иерусалиме: Шима, Шовав, Нафан и Соломон, четверо от Вирсавии, дочери Аммииловой;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (18609, 'rst66', '1ch', 3, 6, 'Ивхар, Елишама, Елифелет,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (18610, 'rst66', '1ch', 3, 7, 'Ногаг, Нефег, Иафиа,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (18611, 'rst66', '1ch', 3, 8, 'Елишама, Елиада и Елифелет &mdash; девятеро.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (18612, 'rst66', '1ch', 3, 9, '<i>Вот</i> все сыновья Давида, кроме сыновей от наложниц. Сестра их Фамарь.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (18613, 'rst66', '1ch', 3, 10, 'Сын Соломона Ровоам; его сын Авия, его сын Аса, его сын Иосафат,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (18614, 'rst66', '1ch', 3, 11, 'его сын Иорам, его сын Охозия, его сын Иоас,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (18615, 'rst66', '1ch', 3, 12, 'его сын Амасия, его сын Азария, его сын Иофам,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (18616, 'rst66', '1ch', 3, 13, 'его сын Ахаз, его сын Езекия, его сын Манассия,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (18617, 'rst66', '1ch', 3, 14, 'его сын Амон, его сын Иосия.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (18618, 'rst66', '1ch', 3, 15, 'Сыновья Иосии: первенец Иоахаз, второй Иоаким, третий Седекия, четвертый Селлум.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (18619, 'rst66', '1ch', 3, 16, 'Сыновья Иоакима: Иехония, сын его; Седекия, сын его.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (18620, 'rst66', '1ch', 3, 17, 'Сыновья Иехонии: Асир, Салафиил, сын его;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (18621, 'rst66', '1ch', 3, 18, 'Малкирам, Федаия, Шенацар, Иезекия, Гошама и Савадия.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (18622, 'rst66', '1ch', 3, 19, 'И сыновья Федаии: Зоровавель и Шимей. Сыновья же Зоровавеля: Мешуллам и Ханания, и Шеломиф, сестра их.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (18623, 'rst66', '1ch', 3, 20, 'И еще пять: Хашува, Огел, Берехия, Хасадия и Иушав‐Хесед.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (18624, 'rst66', '1ch', 3, 21, 'И сыновья Ханании: Фелатия и Исаия; его сын Рефаия, его сын Арнан, его сын Овадия, его сын Шехания.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (18625, 'rst66', '1ch', 3, 22, 'Сын Шехании: Шемаия; сыновья Шемаии: Хаттуш, Игеал, Бариах, Неария и Шафат, шестеро.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (18626, 'rst66', '1ch', 3, 23, 'Сыновья Неарии: Елиоенай, Езекия и Азрикам, трое.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (18627, 'rst66', '1ch', 3, 24, 'Сыновья Елиоеная: Годавьягу, Елеашив, Фелаия, Аккув, Иоханан, Делаия и Анани, семеро.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (18628, 'rst66', '1ch', 4, 1, 'Сыновья Иуды: Фарес, Есром, Харми, Хур и Шовал.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (18629, 'rst66', '1ch', 4, 2, 'Реаия, сын Шовала, родил Иахафа; Иахаф родил Ахума и Лагада: от них племена Цорян.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (18630, 'rst66', '1ch', 4, 3, 'И сии сыновья Етама: Изреель, Ишма и Идбаш, и сестра их, по имени Гацлелпони,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (18631, 'rst66', '1ch', 4, 4, 'Пенуел, отец Гедора, и Езер, отец Хуша. Вот сыновья Хура, первенца Ефрафы, отца Вифлеема.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (18632, 'rst66', '1ch', 4, 5, 'У Ахшура, отца Фекои, были две жены: Хела и Наара.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (18633, 'rst66', '1ch', 4, 6, 'И родила ему Наара Ахузама, Хефера, Фимни и Ахашфари; это сыновья Наары.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (18634, 'rst66', '1ch', 4, 7, 'Сыновья Хелы: Цереф, Цохар и Ефнан.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (18635, 'rst66', '1ch', 4, 8, 'Коц родил: Анува и Цовева и племена Ахархела, сына Гарумова.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (18636, 'rst66', '1ch', 4, 9, 'Иавис был знаменитее своих братьев. Мать дала ему имя Иавис, сказав: я родила его с болезнью.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (18637, 'rst66', '1ch', 4, 10, 'И воззвал Иавис к Богу Израилеву <i>и</i> сказал: о, если бы Ты благословил меня Твоим благословением, распространил пределы мои, и рука Твоя была со мною, охраняя <i>меня</i> от зла, чтобы я не горевал!.. И Бог ниспослал <i>ему</i>, чего он просил.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (18638, 'rst66', '1ch', 4, 11, 'Хелув же, брат Шухи, родил Махира; он есть отец Ештона.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (18639, 'rst66', '1ch', 4, 12, 'Ештон родил Беф‐Рафу, Пасеаха и Техинну, отца города Нааса; это жители Рехи.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (18640, 'rst66', '1ch', 4, 13, 'Сыновья Кеназа: Гофониил и Сераия. Сын Гофониила: Хафаф.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (18641, 'rst66', '1ch', 4, 14, 'Меонофай родил Офру, а Сераия родил Иоава, родоначальника долины плотников, потому что они были плотники.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (18642, 'rst66', '1ch', 4, 15, 'Сыновья Халева, сына Иефонниина: Ир, Ила и Наам. Сын Илы: Кеназ.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (18643, 'rst66', '1ch', 4, 16, 'Сыновья Иегаллелела: Зиф, Зифа, Фирия и Асареел.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (18644, 'rst66', '1ch', 4, 17, 'Сыновья Езры: Иефер, Меред, Ефер и Иалон; Иефер же родил Мерома, Шаммая и Ишбаха, отца Ешфемои.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (18645, 'rst66', '1ch', 4, 18, 'И жена его Иудия родила Иереда, отца Гедора, и Хевера, отца Сохо, и Иекуфиила, отца Занаоха. Это сыновья Бифьи, дочери фараоновой, которую взял Меред.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (18646, 'rst66', '1ch', 4, 19, 'Сыновья жены его Годии, сестры Нахама, отца Кеилы: Гарми и Ешфемоа &mdash; Маахатянин.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (18647, 'rst66', '1ch', 4, 20, 'Сыновья Симеона: Амнон, Ринна, Бенханан и Филон. Сыновья Ишия: Зохеф и Бензохеф.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (18648, 'rst66', '1ch', 4, 21, 'Сыновья Силома, сына Иудина: Ир, отец Лехи, и Лаеда, отец Мареши, и семейства выделывавших виссон, из дома Ашбеи,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (18649, 'rst66', '1ch', 4, 22, 'и Иоким, и жители Хозевы, и Иоаш и Сараф, которые имели владение в Моаве, и Иашувилехем; но это события древние.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (18650, 'rst66', '1ch', 4, 23, 'Они <i>были</i> горшечники, и жили при садах и в огородах; у царя для работ его жили они там.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (18651, 'rst66', '1ch', 4, 24, 'Сыновья Симеона: Немуил, Иамин, Иарив, Зерах и Саул.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (18652, 'rst66', '1ch', 4, 25, 'Шаллум сын его; его сын Мивсам; его сын Мишма.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (18653, 'rst66', '1ch', 4, 26, 'Сыновья Мишмы: Хаммуил, сын его; его сын Закур; его сын Шимей.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (18654, 'rst66', '1ch', 4, 27, 'У Шимея <i>было</i> шестнадцать сыновей и шесть дочерей; у братьев же его сыновей <i>было</i> немного, и все племя их не так было многочисленно, как племя сынов Иуды.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (18655, 'rst66', '1ch', 4, 28, 'Они жили в Вирсавии, Моладе, Хацаршуале,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (18656, 'rst66', '1ch', 4, 29, 'в Билге, в Ецеме, в Фоладе,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (18657, 'rst66', '1ch', 4, 30, 'в Вефуиле, в Хорме, в Циклаге,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (18658, 'rst66', '1ch', 4, 31, 'в Беф‐Маркавофе, в Хацарсусиме, в Беф‐Биреи и в Шаариме. Вот города их до царствования Давидова,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (18659, 'rst66', '1ch', 4, 32, 'с селами их: Етам, Аин, Риммон, Фокен и Ашан, &mdash; пять городов.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (18660, 'rst66', '1ch', 4, 33, 'И все селения их, которые находились вокруг сих городов до Ваала; вот места жительства их и родословия их.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (18661, 'rst66', '1ch', 4, 34, 'Мешовав, Иамлех и Иосия, сын Амассии,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (18662, 'rst66', '1ch', 4, 35, 'Иоил и Иегу, сын Иошиви, сына Сераии, сына Асиилова,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (18663, 'rst66', '1ch', 4, 36, 'Елиоенай, Иакова, Ишохаия, Асаия, Адиил, Ишимиил и Ванея,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (18664, 'rst66', '1ch', 4, 37, 'и Зиза, сын Шифия, сын Аллона, сын Иедаии, сын Шимрия, сын Шемаии.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (18665, 'rst66', '1ch', 4, 38, 'Сии поименованные <i>были</i> князьями племен своих, и дом отцов их разделился на многие отрасли.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (18666, 'rst66', '1ch', 4, 39, 'Они доходили до Герары и до восточной стороны долины, чтобы найти пастбища для стад своих;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (18667, 'rst66', '1ch', 4, 40, 'и нашли пастбища тучные и хорошие и землю обширную, спокойную и безопасную, потому что до них жило там <i>только</i> немного Хамитян.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (18668, 'rst66', '1ch', 4, 41, 'И пришли сии, по именам записанные, во дни Езекии, царя Иудейского, и перебили кочующих и оседлых, которые там находились, и истребили их навсегда и поселились на месте их, ибо там были пастбища для стад их.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (18669, 'rst66', '1ch', 4, 42, 'Из них же, из сынов Симеоновых, пошли к горе Сеир пятьсот человек: Фелатия, Неария, Рефаия и Узиил, сыновья Ишия, <i>были</i> во главе их;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (18670, 'rst66', '1ch', 4, 43, 'И побили уцелевший там остаток Амаликитян, и живут там до сего дня.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (18671, 'rst66', '1ch', 5, 1, 'Сыновья Рувима, первенца Израилева, &mdash; он первенец; но, когда осквернил он постель отца своего, первенство его отдано сыновьям Иосифа, сына Израилева, с тем, однако ж, чтобы не писаться им первородными;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (18672, 'rst66', '1ch', 5, 2, 'потому что Иуда был сильнейшим из братьев своих, и вождь от него, но первенство <i>перенесено</i> на Иосифа.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (18673, 'rst66', '1ch', 5, 3, 'Сыновья Рувима, первенца Израилева: Ханох, Фаллу, Хецрон и Харми.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (18674, 'rst66', '1ch', 5, 4, 'Сыновья Иоиля: Шемая, сын его; его сын Гог, его сын Шимей,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (18675, 'rst66', '1ch', 5, 5, 'его сын Миха, его сын Реаия, его сын Ваал,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (18676, 'rst66', '1ch', 5, 6, 'его сын Беера, которого отвел в плен Феглафелласар, царь Ассирийский. Он <i>был</i> князем Рувимлян.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (18677, 'rst66', '1ch', 5, 7, 'И братья его, по племенам их, по родословному списку их, были: главный Иеиель, потом Захария,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (18678, 'rst66', '1ch', 5, 8, 'и Бела, сын Азаза, сына Шемы, сына Иоиля. Он обитал в Ароере до Нево и Ваал‐Меона;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (18679, 'rst66', '1ch', 5, 9, 'а к востоку он обитал до входа в пустыню, идущую от реки Евфрата, потому что стада их были многочисленны в земле Галаадской.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (18680, 'rst66', '1ch', 5, 10, 'Во дни Саула они вели войну с Агарянами, которые пали от рук их, а они стали жить в шатрах их по всей восточной стороне Галаада.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (18681, 'rst66', '1ch', 5, 11, 'Сыновья Гада жили напротив их в земле Васанской до Салхи:');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (18682, 'rst66', '1ch', 5, 12, 'в Васане Иоиль был главный, Шафан &mdash; второй, потом Иаанай и Шафат.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (18683, 'rst66', '1ch', 5, 13, 'Братьев их с семействами их было семь: Михаил, Мешуллам, Шева, Иорай, Иаакан, Зия и Евер.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (18684, 'rst66', '1ch', 5, 14, 'Вот сыновья Авихаила, сына Хурия, сына Иароаха, сына Галаада, сына Михаила, сына Иешишая, сына Иахдо, сына Буза.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (18685, 'rst66', '1ch', 5, 15, 'Ахи, сын Авдиила, сына Гуниева, <i>был</i> главою своего рода.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (18686, 'rst66', '1ch', 5, 16, 'Они жили в Галааде, в Васане и в зависящих от него городах и во всех окрестностях Сарона, до исхода их.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (18687, 'rst66', '1ch', 5, 17, 'Все они перечислены во дни Иоафама, царя Иудейского, и во дни Иеровоама, царя Израильского.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (18688, 'rst66', '1ch', 5, 18, 'У потомков Рувима и Гада и полуплемени Манассиина было людей воинственных, мужей, носящих щит и меч, стреляющих из лука и приученных к битве, сорок четыре тысячи семьсот шестьдесят, выходящих на войну.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (18689, 'rst66', '1ch', 5, 19, 'И воевали они с Агарянами, Иетуром, Нафишем и Надавом.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (18690, 'rst66', '1ch', 5, 20, 'И подана была им помощь против них, и преданы были в руки их Агаряне и все, что у них было, потому что они во время сражения воззвали к Богу, и Он услышал их, за то, что они уповали на Него.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (18691, 'rst66', '1ch', 5, 21, 'И взяли они стада их: верблюдов пятьдесят тысяч, из мелкого скота двести пятьдесят тысяч, ослов две тысячи, и сто тысяч душ людей,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (18692, 'rst66', '1ch', 5, 22, 'потому что много пало убитых, так как от Бога было сражение сие. И жили они на месте их до переселения.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (18693, 'rst66', '1ch', 5, 23, 'Потомки полуколена Манассиина жили в той земле, от Васана до Ваал‐Ермона и Сенира и до горы Ермона; и их было много.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (18694, 'rst66', '1ch', 5, 24, 'И вот главы поколений их: Ефер, Ишьи, Елиил, Азриил, Иеремия, Годавия и Иагдиил, мужи мощные, мужи именитые, главы родов своих.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (18695, 'rst66', '1ch', 5, 25, 'Но когда они согрешили против Бога отцов своих и стали блудно ходить вслед богов народов той земли, которых изгнал Бог от лица их,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (18696, 'rst66', '1ch', 5, 26, 'тогда Бог Израилев возбудил дух Фула, царя Ассирийского, и дух Феглафелласара, царя Ассирийского, и он выселил Рувимлян и Гадитян и половину колена Манассиина, и отвел их в Халах, и Хавор, и Ару, и на реку Гозан, &mdash; <i>где они</i> до сего дня.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (18697, 'rst66', '1ch', 6, 1, 'Сыновья Левия: Гирсон, Кааф и Мерари.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (18698, 'rst66', '1ch', 6, 2, 'Сыновья Каафа: Амрам, Ицгар, Хеврон и Узиил.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (18699, 'rst66', '1ch', 6, 3, 'Дети Амрама: Аарон, Моисей и Мариам. Сыновья Аарона: Надав, Авиуд, Елеазар и Ифамар.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (18700, 'rst66', '1ch', 6, 4, 'Елеазар родил Финееса, Финеес родил Авишуя;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (18701, 'rst66', '1ch', 6, 5, 'Авишуй родил Буккия, Буккий родил Озию;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (18702, 'rst66', '1ch', 6, 6, 'Озия родил Зерахию, Зерахия родил Мераиофа;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (18703, 'rst66', '1ch', 6, 7, 'Мераиоф родил Амарию, Амария родил Ахитува;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (18704, 'rst66', '1ch', 6, 8, 'Ахитув родил Садока, Садок родил Ахимааса;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (18705, 'rst66', '1ch', 6, 9, 'Ахимаас родил Азарию, Азария родил Иоанана;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (18706, 'rst66', '1ch', 6, 10, 'Иоанан родил Азарию, &mdash; это тот, который был священником в храме, построенном Соломоном в Иерусалиме.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (18707, 'rst66', '1ch', 6, 11, 'И родил Азария Амарию, Амария родил Ахитува;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (18708, 'rst66', '1ch', 6, 12, 'Ахитув родил Садока, Садок родил Селлума;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (18709, 'rst66', '1ch', 6, 13, 'Селлум родил Хелкию, Хелкия родил Азарию;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (18710, 'rst66', '1ch', 6, 14, 'Азария родил Сераию, Сераия родил Иоседека.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (18711, 'rst66', '1ch', 6, 15, 'Иоседек пошел <i>в плен</i>, когда Господь переселил Иудеев и Иерусалимлян рукою Навуходоносора.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (18712, 'rst66', '1ch', 6, 16, 'Итак, сыновья Левия: Гирсон, Кааф и Мерари.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (18713, 'rst66', '1ch', 6, 17, 'Вот имена сыновей Гирсоновых: Ливни и Шимей.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (18714, 'rst66', '1ch', 6, 18, 'Сыновья Каафа: Амрам, Ицгар, Хеврон и Узиил.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (18715, 'rst66', '1ch', 6, 19, 'Сыновья Мерари: Махли и Муши. Вот потомки Левия по родам их.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (18716, 'rst66', '1ch', 6, 20, 'У Гирсона: Ливни, сын его; Иахав, сын его; Зимма, сын его;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (18717, 'rst66', '1ch', 6, 21, 'Иоах, сын его; Иддо, сын его; Зерах, сын его; Иеафрай, сын его.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (18718, 'rst66', '1ch', 6, 22, 'Сыновья Каафа: Аминадав, сын его; Корей, сын его; Асир, сын его;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (18719, 'rst66', '1ch', 6, 23, 'Елкана, сын его; Евиасаф, сын его; Асир, сын его;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (18720, 'rst66', '1ch', 6, 24, 'Тахаф, сын его; Уриил, сын его; Узия, сын его; Саул, сын его.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (18721, 'rst66', '1ch', 6, 25, 'Сыновья Елканы: Амасай и Ахимоф.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (18722, 'rst66', '1ch', 6, 26, 'Елкана, сын его; Цофай, сын его; Нахаф, сын его;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (18723, 'rst66', '1ch', 6, 27, 'Елиаф, сын его; Иерохам, сын его, Елкана, сын его.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (18724, 'rst66', '1ch', 6, 28, 'Сыновья Самуила: первенец Иоиль, второй &mdash; Авия.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (18725, 'rst66', '1ch', 6, 29, 'Сыновья Мерари: Махли; Ливни, сын его; Шимей, сын его; Уза, сын его;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (18726, 'rst66', '1ch', 6, 30, 'Шима, сын его; Хаггия, сын его; Асаия, сын его.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (18727, 'rst66', '1ch', 6, 31, 'Вот те, которых Давид поставил начальниками над певцами в доме Господнем, со времени поставления в нем ковчега.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (18728, 'rst66', '1ch', 6, 32, 'Они служили певцами пред скиниею собрания, доколе Соломон не построил дома Господня в Иерусалиме. И они становились на службу свою по уставу своему.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (18729, 'rst66', '1ch', 6, 33, 'Вот те, которые становились с сыновьями своими: из сыновей Каафовых &mdash; Еман певец, сын Иоиля, сын Самуила,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (18730, 'rst66', '1ch', 6, 34, 'сын Елканы, сын Иерохама, сын Елиила, сын Тоаха,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (18731, 'rst66', '1ch', 6, 35, 'сын Цуфа, сын Елканы, сын Махафа, сын Амасая,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (18732, 'rst66', '1ch', 6, 36, 'сын Елканы, сын Иоиля, сын Азарии, сын Цефании,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (18733, 'rst66', '1ch', 6, 37, 'сын Тахафа, сын Асира, сын Авиасафа, сын Корея,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (18734, 'rst66', '1ch', 6, 38, 'сын Ицгара, сын Каафа, сын Левия, сын Израиля;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (18735, 'rst66', '1ch', 6, 39, 'и брат его Асаф, стоявший на правой стороне его, &mdash; Асаф, сын Берехии, сын Шимы,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (18736, 'rst66', '1ch', 6, 40, 'сын Михаила, сын Ваасеи, сын Малхии,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (18737, 'rst66', '1ch', 6, 41, 'сын Ефния, сын Зераха, сын Адаии,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (18738, 'rst66', '1ch', 6, 42, 'сын Ефана, сын Зиммы, сын Шимия,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (18739, 'rst66', '1ch', 6, 43, 'сын Иахафа, сын Гирсона, сын Левия.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (18740, 'rst66', '1ch', 6, 44, 'А из сыновей Мерари, братьев их, &mdash; на левой стороне: Ефан, сын Кишия, сын Авдия, сын Маллуха,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (18741, 'rst66', '1ch', 6, 45, 'сын Хашавии, сын Амасии, сын Хелкии,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (18742, 'rst66', '1ch', 6, 46, 'сын Амция, сын Вания, сын Шемера,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (18743, 'rst66', '1ch', 6, 47, 'сын Махлия, сын Мушия, сын Мерари, сын Левия.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (18744, 'rst66', '1ch', 6, 48, 'Братья их левиты определены на всякие службы при доме Божием.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (18745, 'rst66', '1ch', 6, 49, 'Аарон же и сыновья его сожигали на жертвеннике всесожжения и на жертвеннике кадильном, и совершали всякое священнодействие во Святом Святых и для очищения Израиля во всем, как заповедал раб Божий Моисей.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (18746, 'rst66', '1ch', 6, 50, 'Вот сыновья Аарона: Елеазар, сын его; Финеес, сын его; Авиуд, сын его;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (18747, 'rst66', '1ch', 6, 51, 'Буккий, сын его; Уззий, сын его; Зерахия, сын его;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (18748, 'rst66', '1ch', 6, 52, 'Мераиоф, сын его; Амария, сын его; Ахитув, сын его;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (18749, 'rst66', '1ch', 6, 53, 'Садок, сын его; Ахимаас, сын его.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (18750, 'rst66', '1ch', 6, 54, 'И вот жилища их по селениям их в пределах их: сыновьям Аарона из племени Каафова, так как жребий выпал им,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (18751, 'rst66', '1ch', 6, 55, 'дали Хеврон, в земле Иудиной, и предместья его вокруг него;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (18752, 'rst66', '1ch', 6, 56, 'поля же сего города и села его отдали Халеву, сыну Иефонниину.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (18753, 'rst66', '1ch', 6, 57, 'Сыновьям Аарона дали также города убежищ: Хеврон и Ливну с их предместьями, Иаттир и Ештемоа и предместья его,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (18754, 'rst66', '1ch', 6, 58, 'и Хилен и предместья его, Давир и предместья его,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (18755, 'rst66', '1ch', 6, 59, 'и Ашан и предместья его, Вефсамис и предместья его,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (18756, 'rst66', '1ch', 6, 60, 'а от колена Вениаминова &mdash; Геву и предместья ее, и Аллемеф и предместья его, и Анафоф и предместья его: всех городов их в племенах их тринадцать городов.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (18757, 'rst66', '1ch', 6, 61, 'Остальным сыновьям Каафа, из семейств этого колена, <i>дано</i> по жребию десять городов из удела половины колена Манассиина.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (18758, 'rst66', '1ch', 6, 62, 'Сыновьям Гирсона по племенам их, от колена Иссахарова, и от колена Асирова, и от колена Неффалимова, и от колена Манассиина в Васане, <i>дано</i> тринадцать городов.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (18759, 'rst66', '1ch', 6, 63, 'Сыновьям Мерари по племенам их, от колена Рувимова, и от колена Гадова, и от колена Завулонова, <i>дано</i> по жребию двенадцать городов.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (18760, 'rst66', '1ch', 6, 64, 'Так дали сыны Израилевы левитам города и предместья их.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (18761, 'rst66', '1ch', 6, 65, 'Дали они по жребию от колена сыновей Иудиных, и от колена сыновей Симеоновых, и от колена сыновей Вениаминовых те города, которые они назвали по именам.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (18762, 'rst66', '1ch', 6, 66, 'Некоторым же племенам сыновей Каафовых даны были города от колена Ефремова.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (18763, 'rst66', '1ch', 6, 67, 'И дали им города убежищ: Сихем и предместья его на горе Ефремовой, и Гезер и предместья его,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (18764, 'rst66', '1ch', 6, 68, 'и Иокмеам и предместья его, и Беф‐Орон и предместья его,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (18765, 'rst66', '1ch', 6, 69, 'и Аиалон и предместья его, и Гаф‐Риммон и предместья его;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (18766, 'rst66', '1ch', 6, 70, 'от половины колена Манассиина &mdash; Анер и предместья его, Билеам и предместья его. Это поколению остальных сыновей Каафовых.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (18767, 'rst66', '1ch', 6, 71, 'Сыновьям Гирсона от племени полуколена Манассиина <i>дали</i> Голан в Васане и предместья его, и Аштароф и предместья его.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (18768, 'rst66', '1ch', 6, 72, 'От колена Иссахарова &mdash; Кедес и предместья его, Давраф и предместья его,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (18769, 'rst66', '1ch', 6, 73, 'и Рамоф и предместья его, и Анем и предместья его;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (18770, 'rst66', '1ch', 6, 74, 'От колена Асирова &mdash; Машал и предместья его, и Авдон и предместья его,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (18771, 'rst66', '1ch', 6, 75, 'и Хукок и предместья его, и Рехов и предместья его;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (18772, 'rst66', '1ch', 6, 76, 'От колена Неффалимова &mdash; Кедес в Галилее и предместья его, и Хаммон и предместья его, и Кириафаим и предместья его.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (18773, 'rst66', '1ch', 6, 77, 'А прочим сыновьям Мерариным &mdash; от колена Завулонова Риммон и предместья его, Фавор и предместья его.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (18774, 'rst66', '1ch', 6, 78, 'По ту сторону Иордана, против Иерихона, на восток от Иордана, от колена Рувимова <i>дали</i> Восор в пустыне и предместья его, и Иаацу и предместья ее,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (18775, 'rst66', '1ch', 6, 79, 'и Кедемоф и предместья его, и Мефааф и предместья его;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (18776, 'rst66', '1ch', 6, 80, 'От колена Гадова &mdash; Рамоф в Галааде и предместья его, и Маханаим и предместья его,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (18777, 'rst66', '1ch', 6, 81, 'и Есевон и предместья его, и Иазер и предместья его.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (18778, 'rst66', '1ch', 7, 1, 'Сыновья Иссахара: Фола, Фуа, Иашув и Шимрон, четверо.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (18779, 'rst66', '1ch', 7, 2, 'Сыновья Фолы: Уззий, Рефаия, Иериил, Иахмай, Ивсам и Самуил, главные в поколениях Фолы, люди воинственные в своих поколениях; число их во дни Давида было двадцать две тысячи и шестьсот.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (18780, 'rst66', '1ch', 7, 3, 'Сын Уззия: Израхия; а сыновья Израхии: Михаил, Овадиа, Иоиль и Ишшия, пятеро. Все они главные.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (18781, 'rst66', '1ch', 7, 4, 'У них, по родам их, по поколениям их, было готово к сражению войска тридцать шесть тысяч; потому что у них было много жен и сыновей.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (18782, 'rst66', '1ch', 7, 5, 'Братьев же их, во всех поколениях Иссахаровых, людей воинственных, было восемьдесят семь тысяч, внесенных в родословные записи.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (18783, 'rst66', '1ch', 7, 6, 'У Вениамина: Бела, Бехер и Иедиаил, трое.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (18784, 'rst66', '1ch', 7, 7, 'Сыновья Белы: Ецбон, Уззий, Уззиил, Иеримоф и Ири, пятеро, главы поколений, люди воинственные. В родословных списках записано их двадцать две тысячи тридцать четыре.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (18785, 'rst66', '1ch', 7, 8, 'Сыновья Бехера: Земира, Иоаш, Елиезер, Елиоенай, Омри, Иремоф, Авия, Анафоф и Алемеф: все эти сыновья Бехера.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (18786, 'rst66', '1ch', 7, 9, 'В родословных списках записано их по родам их, по главам поколений, людей воинственных &mdash; двадцать тысяч и двести.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (18787, 'rst66', '1ch', 7, 10, 'Сын Иедиаила: Билган. Сыновья Билгана: Иеус, Вениамин, Егуд, Хенаана, Зефан, Фарсис и Ахишахар.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (18788, 'rst66', '1ch', 7, 11, 'Все эти сыновья Иедиаила были главами поколений, люди воинственные; семнадцать тысяч и двести было выходящих на войну.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (18789, 'rst66', '1ch', 7, 12, 'И Шупим и Хупим, сыновья Ира; Хушим, сын Ахера.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (18790, 'rst66', '1ch', 7, 13, 'Сыновья Неффалима: Иахцеил, Гуни, Иецер и Шиллем, дети Валлы.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (18791, 'rst66', '1ch', 7, 14, 'Сыновья Манассии: Асриил, которого родила наложница его Арамеянка; она же родила Махира, отца Галаадова.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (18792, 'rst66', '1ch', 7, 15, 'Махир взял в жену сестру Хупима и Шупима, &mdash; имя сестры их Мааха; имя второму Салпаад. У Салпаада были <i>только</i> дочери.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (18793, 'rst66', '1ch', 7, 16, 'Мааха, жена Махирова, родила сына и нарекла ему имя Кереш, а имя брату его Шереш. Сыновья его: Улам и Рекем.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (18794, 'rst66', '1ch', 7, 17, 'Сын Улама: Бедан. Вот сыновья Галаада, сына Махира, сына Манассиина.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (18795, 'rst66', '1ch', 7, 18, 'Сестра его Молехеф родила Ишгода, Авиезера и Махлу.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (18796, 'rst66', '1ch', 7, 19, 'Сыновья Шемиды были: Ахиан, Шехем, Ликхи и Аниам.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (18797, 'rst66', '1ch', 7, 20, 'Сыновья Ефрема: Шутелах, и Беред, сын его, и Фахаф, сын его, и Елеада, сын его, и Фахаф, сын его,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (18798, 'rst66', '1ch', 7, 21, 'и Завад, сын его, и Шутелах, сын его, и Езер и Елеад. И убили их жители Гефа, уроженцы той земли, за то, что они пошли захватить стада их.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (18799, 'rst66', '1ch', 7, 22, 'И плакал о них Ефрем, отец их, много дней, и приходили братья его утешать его.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (18800, 'rst66', '1ch', 7, 23, 'Потом он вошел к жене своей, и она зачала и родила сына, и он нарек ему имя: Берия, потому что несчастье постигло дом его.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (18801, 'rst66', '1ch', 7, 24, 'И дочь у него <i>была</i> Шеера. Она построила Беф‐Орон нижний и верхний и Уззен‐Шееру.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (18802, 'rst66', '1ch', 7, 25, 'И Рефай, сын его, и Решеф, и Фелах, сын его, и Фахан, сын его,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (18803, 'rst66', '1ch', 7, 26, 'Лаедан, сын его, Аммиуд, сын его, Елишама, сын его,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (18804, 'rst66', '1ch', 7, 27, 'Нон, сын его, Иисус, сын его.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (18805, 'rst66', '1ch', 7, 28, 'Владения их и места жительства их <i>были:</i> Вефиль и зависящие от него города; к востоку Нааран, к западу Гезер и зависящие от него города; Сихем и зависящие от него города до Газы и зависящих от нее городов.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (18806, 'rst66', '1ch', 7, 29, 'А со стороны сыновей Манассииных: Беф‐Сан и зависящие от него города, Фаанах и зависящие от него города, Мегиддо и зависящие от него города, Дор и зависящие от него города. В них жили сыновья Иосифа, сына Израилева.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (18807, 'rst66', '1ch', 7, 30, 'Сыновья Асира: Имна, Ишва, Ишви и Берия, и сестра их Серах.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (18808, 'rst66', '1ch', 7, 31, 'Сыновья Берии: Хевер и Малхиил. Он отец Бирзаифа.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (18809, 'rst66', '1ch', 7, 32, 'Хевер родил Иафлета, Шомера и Хофама, и Шую, сестру их.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (18810, 'rst66', '1ch', 7, 33, 'Сыновья Иафлета: Пасах, Бимгал и Ашваф. Вот сыновья Иафлета.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (18811, 'rst66', '1ch', 7, 34, 'Сыновья Шемера: Ахи, Рохга, Ихубба и Арам.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (18812, 'rst66', '1ch', 7, 35, 'Сыновья Гелема, брата его: Цофах, Имна, Шелеш и Амал.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (18813, 'rst66', '1ch', 7, 36, 'Сыновья Цофаха: Суах, Харнефер, Шуал, Бери, Имра,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (18814, 'rst66', '1ch', 7, 37, 'Бецер, Год, Шамма, Шилша, Ифран и Беера.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (18815, 'rst66', '1ch', 7, 38, 'Сыновья Иефера: Иефунни, Фиспа и Ара.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (18816, 'rst66', '1ch', 7, 39, 'Сыновья Уллы: Арах, Ханниил и Риция.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (18817, 'rst66', '1ch', 7, 40, 'Все эти сыновья Асира, главы поколений, люди отборные, воинственные, главные начальники. Записано у них в родословных списках в войске, для войны, по счету двадцать шесть тысяч человек.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (18818, 'rst66', '1ch', 8, 1, 'Вениамин родил Белу, первенца своего, второго Ашбела, третьего Ахрая,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (18819, 'rst66', '1ch', 8, 2, 'четвертого Ноху и пятого Рафу.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (18820, 'rst66', '1ch', 8, 3, 'Сыновья Белы были: Аддар, Гера, Авиуд,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (18821, 'rst66', '1ch', 8, 4, 'Авишуа, Нааман, Ахоах,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (18822, 'rst66', '1ch', 8, 5, 'Гера, Шефуфан и Хурам.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (18823, 'rst66', '1ch', 8, 6, 'И вот сыновья Егуда, которые были главами родов, живших в Геве и переселенных в Манахаф:');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (18824, 'rst66', '1ch', 8, 7, 'Нааман, Ахия и Гера, который переселил их; он родил Уззу и Ахихуда.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (18825, 'rst66', '1ch', 8, 8, 'Шегараим родил детей в земле Моавитской после того, как отпустил <i>от себя</i> Хушиму и Баару, жен своих.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (18826, 'rst66', '1ch', 8, 9, 'И родил он от Ходеши, жены своей, Иовава, Цивию, Мешу, Малхама,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (18827, 'rst66', '1ch', 8, 10, 'Иеуца, Шахию и Мирму: вот сыновья его, главы поколений.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (18828, 'rst66', '1ch', 8, 11, 'От Хушимы родил он Авитува и Елпаала.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (18829, 'rst66', '1ch', 8, 12, 'Сыновья Елпаала: Евер, Мишам и Шемер, который построил Оно и Лод и зависящие от него города, &mdash;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (18830, 'rst66', '1ch', 8, 13, 'и Берия и Шема. Они были главами поколений жителей Аиалона. Они выгнали жителей Гефа.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (18831, 'rst66', '1ch', 8, 14, 'Ахио, Шашак, Иремоф,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (18832, 'rst66', '1ch', 8, 15, 'Зевадия, Арад, Едер,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (18833, 'rst66', '1ch', 8, 16, 'Михаил, Ишфа и Иоха &mdash; сыновья Берии.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (18834, 'rst66', '1ch', 8, 17, 'Зевадия, Мешуллам, Хизкий, Хевер,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (18835, 'rst66', '1ch', 8, 18, 'Ишмерай, Излия и Иовав &mdash; сыновья Елпаала.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (18836, 'rst66', '1ch', 8, 19, 'Иаким, Зихрий, Завдий,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (18837, 'rst66', '1ch', 8, 20, 'Елиенай, Цилфай, Елиил,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (18838, 'rst66', '1ch', 8, 21, 'Адаия, Бераия и Шимраф &mdash; сыновья Шимея.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (18839, 'rst66', '1ch', 8, 22, 'Ишпан, Евер, Елиил,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (18840, 'rst66', '1ch', 8, 23, 'Авдон, Зихрий, Ханан,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (18841, 'rst66', '1ch', 8, 24, 'Ханания, Елам, Антофия,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (18842, 'rst66', '1ch', 8, 25, 'Ифдия и Фенуил &mdash; сыновья Шашака.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (18843, 'rst66', '1ch', 8, 26, 'Шамшерай, Шехария, Афалия,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (18844, 'rst66', '1ch', 8, 27, 'Иаарешия, Елия и Зихрий, сыновья Иерохама.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (18845, 'rst66', '1ch', 8, 28, 'Это главы поколений, в родах своих главные. Они жили в Иерусалиме.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (18846, 'rst66', '1ch', 8, 29, 'В Гаваоне жили: отец Гаваонитян, &mdash; имя жены его Мааха, &mdash;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (18847, 'rst66', '1ch', 8, 30, 'и сын его, первенец Авдон, <i>за ним</i> Цур, Кис, Ваал, Надав,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (18848, 'rst66', '1ch', 8, 31, 'Гедор, Ахио, Зехер и Миклоф.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (18849, 'rst66', '1ch', 8, 32, 'Миклоф родил Шимея. И они подле братьев своих жили в Иерусалиме, вместе с братьями своими.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (18850, 'rst66', '1ch', 8, 33, 'Нер родил Киса; Кис родил Саула; Саул родил Иоанафана, Мелхисуя, Авинадава и Ешбаала.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (18851, 'rst66', '1ch', 8, 34, 'Сын Ионафана Мериббаал; Мериббаал родил Миху.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (18852, 'rst66', '1ch', 8, 35, 'Сыновья Михи: Пифон, Мелег, Фаарея и Ахаз.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (18853, 'rst66', '1ch', 8, 36, 'Ахаз родил Иоиадду; Иоиадда родил Алемефа, Азмавефа и Замврия; Замврий родил Моцу;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (18854, 'rst66', '1ch', 8, 37, 'Моца родил Бинею. Рефаия, сын его; Елеаса, сын его; Ацел, сын его.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (18855, 'rst66', '1ch', 8, 38, 'У Ацела шесть сыновей, и вот имена их: Азрикам, Бохру, Исмаил, Шеария, Овадия и Ханан; все они сыновья Ацела.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (18856, 'rst66', '1ch', 8, 39, 'Сыновья Ешека, брата его: Улам, первенец его, второй Иеуш, третий Елифелет.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (18857, 'rst66', '1ch', 8, 40, 'Сыновья Улама были люди воинственные, стрелявшие из лука, имевшие много сыновей и внуков: сто пятьдесят. Все они от сынов Вениамина.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (18858, 'rst66', '1ch', 9, 1, 'Так были перечислены по родам своим все Израильтяне, и вот они записаны в книге царей Израильских. Иудеи же за беззакония свои переселены в Вавилон.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (18859, 'rst66', '1ch', 9, 2, 'Первые жители, которые <i>жили</i> во владениях своих, по городам Израильским, были Израильтяне, священники, левиты и нефинеи.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (18860, 'rst66', '1ch', 9, 3, 'В Иерусалиме жили некоторые из сынов Иудиных и из сынов Вениаминовых, и из сынов Ефремовых и Манассииных:');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (18861, 'rst66', '1ch', 9, 4, 'Уфай, сын Аммиуда, сын Омри, сын Имрия, сын Вания, &mdash; из сыновей Фареса, сына Иудина;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (18862, 'rst66', '1ch', 9, 5, 'из сыновей Шилона &mdash; Асаия первенец и сыновья его;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (18863, 'rst66', '1ch', 9, 6, 'из сыновей Зары &mdash; Иеуил и братья их, &mdash; шестьсот девяносто.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (18864, 'rst66', '1ch', 9, 7, 'Из сыновей Вениаминовых Саллу, сын Мешуллама, сын Годавии, сын Гассенуи;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (18865, 'rst66', '1ch', 9, 8, 'и Ивния, сын Иерохама, и Эла, сын Уззия, сына Михриева, и Мешуллам, сын Шефатии, сына Регуила, сына Ивнии,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (18866, 'rst66', '1ch', 9, 9, 'и братья их, по родам их: девятьсот пятьдесят шесть, &mdash; все сии мужи были главы родов в поколениях своих.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (18867, 'rst66', '1ch', 9, 10, 'А из священников: Иедаия, Иоиарив, Иахин,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (18868, 'rst66', '1ch', 9, 11, 'и Азария, сын Хелкии, сын Мешуллама, сын Садока, сын Мераиофа, сын Ахитува, начальствующий в доме Божием;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (18869, 'rst66', '1ch', 9, 12, 'и Адаия, сын Иерохама, сын Пашхура, сын Малхии; и Маасай, сын Адиела, сын Иахзера, сын Мешуллама, сын Мешиллемифа, сын Иммера;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (18870, 'rst66', '1ch', 9, 13, 'и братья их, главы родов своих: тысяча семьсот шестьдесят, &mdash; люди отличные в деле служения в доме Божием.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (18871, 'rst66', '1ch', 9, 14, 'А из левитов: Шемаия, сын Хашува, сын Азрикама, сын Хашавии, &mdash; из сыновей Мерариных;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (18872, 'rst66', '1ch', 9, 15, 'и Вакбакар, Хереш, Галал, и Матфания, сын Михи, сын Зихрия, сын Асафа;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (18873, 'rst66', '1ch', 9, 16, 'и Овадия, сын Шемаии, сын Галала, сын Идифуна, и Берехия, сын Асы, сын Елканы, живший в селениях Нетофафских.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (18874, 'rst66', '1ch', 9, 17, 'А привратники: Шаллум, Аккуб, Талмон и Ахиман, и братья их; Шаллум <i>был</i> главным.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (18875, 'rst66', '1ch', 9, 18, 'И доныне сии привратники у ворот царских, к востоку, содержат стражу сынов Левииных.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (18876, 'rst66', '1ch', 9, 19, 'Шаллум, сын Коре, сын Евиасафа, сын Корея, и братья его из рода его, Кореяне, по делу служения своего, были стражами у порогов скинии, а отцы их охраняли вход в стан Господень.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (18877, 'rst66', '1ch', 9, 20, 'Финеес, сын Елеазаров, был прежде начальником над ними, и Господь был с ним.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (18878, 'rst66', '1ch', 9, 21, 'Захария, сын Мешелемии, <i>был</i> привратником у дверей скинии собрания.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (18879, 'rst66', '1ch', 9, 22, 'Всех их, выбранных в привратники к порогам, было двести двенадцать. Они внесены в список по селениям своим. Их поставил Давид и Самуил‐прозорливец за верность их.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (18880, 'rst66', '1ch', 9, 23, 'И они и сыновья их были на страже у ворот дома Господня, при доме скинии.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (18881, 'rst66', '1ch', 9, 24, 'На четырех сторонах находились привратники: на восточной, западной, северной и южной.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (18882, 'rst66', '1ch', 9, 25, 'Братья же их жили в селениях своих, приходя к ним от времени до времени на семь дней.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (18883, 'rst66', '1ch', 9, 26, 'Сии четыре начальника привратников, левиты, были в доверенности; они же были приставлены к жилищам и к сокровищам дома Божия.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (18884, 'rst66', '1ch', 9, 27, 'Вокруг дома Божия они и ночь проводили, потому что на них <i>лежало</i> охранение, и они должны были каждое утро отпирать двери.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (18885, 'rst66', '1ch', 9, 28, '<i>Одни</i> из них были приставлены к служебным сосудам, так что счетом принимали их и счетом выдавали.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (18886, 'rst66', '1ch', 9, 29, '<i>Другим</i> из них поручена была прочая утварь и все священные потребности: мука лучшая, и вино, и елей, и ладан, и благовония.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (18887, 'rst66', '1ch', 9, 30, 'А из сыновей священнических <i>некоторые</i> составляли миро из веществ благовонных.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (18888, 'rst66', '1ch', 9, 31, 'Маттафии из левитов, &mdash; он первенец Селлума Кореянина, &mdash; вверено было приготовляемое на сковородах.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (18889, 'rst66', '1ch', 9, 32, '<i>Некоторым</i> из братьев их, из сынов Каафовых, поручено было <i>заготовление</i> хлебов предложения, чтобы представлять <i>их</i> каждую субботу.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (18890, 'rst66', '1ch', 9, 33, 'Певцы же, главные в поколениях левитских, в комнатах храма свободны были от занятий, потому что день и ночь они обязаны были <i>заниматься</i> искусством <i>своим</i>.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (18891, 'rst66', '1ch', 9, 34, 'Это главы поколений левитских, в родах своих главные. Они жили в Иерусалиме.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (18892, 'rst66', '1ch', 9, 35, 'В Гаваоне жили: отец Гаваонитян Иеил, &mdash; имя жены его Мааха,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (18893, 'rst66', '1ch', 9, 36, 'и сын его первенец Авдон, <i>за ним</i> Цур, Кис, Ваал, Нер, Надав,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (18894, 'rst66', '1ch', 9, 37, 'Гедор, Ахио, Захария и Миклоф.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (18895, 'rst66', '1ch', 9, 38, 'Миклоф родил Шимеама. И они подле братьев своих жили в Иерусалиме вместе с братьями своими.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (18896, 'rst66', '1ch', 9, 39, 'Нер родил Киса, Кис родил Саула, Саул родил Ионафана, Мелхисуя, Авинадава и Ешбаала.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (18897, 'rst66', '1ch', 9, 40, 'Сын Ионафана Мериббаал; Мериббаал родил Миху.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (18898, 'rst66', '1ch', 9, 41, 'Сыновья Михи: Пифон, Мелех, Фарей <i>и Ахаз</i>.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (18899, 'rst66', '1ch', 9, 42, 'Ахаз родил Иаеру; Иаера родил Алемефа, Азмавефа и Замврия; Замврий родил Моцу;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (18900, 'rst66', '1ch', 9, 43, 'Моца родил Бинею: Рефаия, сын его; Елеаса, сын его; Ацел, сын его.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (18901, 'rst66', '1ch', 9, 44, 'У Ацела шесть сыновей, и вот имена их: Азрикам, Бохру, Исмаил, Шеария, Овадия и Ханан. Это сыновья Ацела.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (18902, 'rst66', '1ch', 10, 1, 'Филистимляне воевали с Израилем, и побежали Израильтяне от Филистимлян, и падали, пораженные, на горе Гелвуе.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (18903, 'rst66', '1ch', 10, 2, 'И погнались Филистимляне за Саулом и сыновьями его, и убили Филистимляне Ионафана и Авинадава и Мелхисуя, сыновей Сауловых.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (18904, 'rst66', '1ch', 10, 3, 'Сражение против Саула усилилось, и стрелки устремились на него, так что он изранен был стрелками.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (18905, 'rst66', '1ch', 10, 4, 'И сказал Саул оруженосцу своему: обнажи меч твой и заколи меня им, чтобы не пришли эти необрезанные и не надругались надо мною. Но оруженосец не решился, потому что очень испугался. Тогда Саул взял меч и пал на него.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (18906, 'rst66', '1ch', 10, 5, 'Оруженосец его, увидев, что Саул умер, и сам пал на меч и умер.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (18907, 'rst66', '1ch', 10, 6, 'И умер Саул, и три сына его, и весь дом его вместе с ним умер.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (18908, 'rst66', '1ch', 10, 7, 'Когда увидели Израильтяне, которые были в долине, что все бегут и что Саул и сыновья его умерли, то оставили города свои и разбежались; а Филистимляне пришли и поселились в них.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (18909, 'rst66', '1ch', 10, 8, 'На другой день пришли Филистимляне обирать убитых, и нашли Саула и сыновей его, павших на горе Гелвуйской,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (18910, 'rst66', '1ch', 10, 9, 'и раздели его, и сняли с него голову его и оружие его, и послали по земле Филистимской, чтобы возвестить <i>о сем</i> пред идолами их и пред народом.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (18911, 'rst66', '1ch', 10, 10, 'И положили оружие его в капище богов своих, и голову его воткнули в доме Дагона.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (18912, 'rst66', '1ch', 10, 11, 'И услышал весь Иавис Галаадский все, что сделали Филистимляне с Саулом.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (18913, 'rst66', '1ch', 10, 12, 'И поднялись все люди сильные, взяли тело Саулово и тела сыновей его, и принесли их в Иавис, и похоронили кости их под дубом в Иависе, и постились семь дней.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (18914, 'rst66', '1ch', 10, 13, 'Так умер Саул за свое беззаконие, которое он сделал пред Господом, за то, что не соблюл слова Господня и обратился к волшебнице с вопросом,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (18915, 'rst66', '1ch', 10, 14, 'а не взыскал Господа. <i>За то</i> Он и умертвил его, и передал царство Давиду, сыну Иессееву.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (18916, 'rst66', '1ch', 11, 1, 'И собрались все Израильтяне к Давиду в Хеврон и сказали: вот, мы кость твоя и плоть твоя;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (18917, 'rst66', '1ch', 11, 2, 'и вчера, и третьего дня, когда еще Саул был царем, ты выводил и вводил Израиля, и Господь, Бог твой, сказал тебе: «ты будешь пасти народ Мой, Израиля, и ты будешь вождем народа Моего, Израиля».');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (18918, 'rst66', '1ch', 11, 3, 'И пришли все старейшины Израилевы к царю в Хеврон, и заключил с ними Давид завет в Хевроне пред лицом Господним; и они помазали Давида в царя над Израилем, по слову Господню, через Самуила.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (18919, 'rst66', '1ch', 11, 4, 'И пошел Давид и весь Израиль к Иерусалиму, то есть к Иевусу. А там были Иевусеи, жители той земли.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (18920, 'rst66', '1ch', 11, 5, 'И сказали жители Иевуса Давиду: не войдешь сюда. Но Давид взял крепость Сион; это город Давидов.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (18921, 'rst66', '1ch', 11, 6, 'И сказал Давид: кто прежде всех поразит Иевусеев, тот будет главою и военачальником. И взошел прежде всех Иоав, сын Саруи, и сделался главою.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (18922, 'rst66', '1ch', 11, 7, 'Давид жил в той крепости, потому и называли ее городом Давидовым.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (18923, 'rst66', '1ch', 11, 8, 'И он обстроил город кругом, <i>начиная</i> от Милло, всю окружность, а Иоав возобновил остальные <i>части</i> города.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (18924, 'rst66', '1ch', 11, 9, 'И преуспевал Давид, и возвышался более и более, и Господь Саваоф <i>был</i> с ним.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (18925, 'rst66', '1ch', 11, 10, 'Вот главные из сильных у Давида, которые крепко подвизались с ним в царстве его, вместе со всем Израилем, чтобы воцарить его, по слову Господню, над Израилем,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (18926, 'rst66', '1ch', 11, 11, 'и вот число храбрых, которые были у Давида: Иесваал, сын Ахамани, главный из тридцати. Он поднял копье свое на триста человек и поразил их в один раз.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (18927, 'rst66', '1ch', 11, 12, 'По нем Елеазар, сын Додо Ахохиянина, из трех храбрых:');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (18928, 'rst66', '1ch', 11, 13, 'он был с Давидом в Фасдамиме, куда Филистимляне собрались на войну. Там часть поля была засеяна ячменем, и народ побежал от Филистимлян;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (18929, 'rst66', '1ch', 11, 14, 'но они стали среди поля, сберегли его и поразили Филистимлян. И даровал Господь спасение великое!');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (18930, 'rst66', '1ch', 11, 15, 'Трое сих главных из тридцати вождей взошли на скалу к Давиду, в пещеру Одоллам, когда стан Филистимлян был расположен в долине Рефаимов.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (18931, 'rst66', '1ch', 11, 16, 'Давид тогда был в укрепленном месте, а охранное войско Филистимлян было тогда в Вифлееме.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (18932, 'rst66', '1ch', 11, 17, 'И сильно захотелось <i>пить</i> Давиду, и он сказал: кто напоит меня водою из колодезя Вифлеемского, что у ворот?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (18933, 'rst66', '1ch', 11, 18, 'Тогда эти трое пробились сквозь стан Филистимский и почерпнули воды из колодезя Вифлеемского, что у ворот, и взяли, и принесли Давиду. Но Давид не захотел пить ее и вылил ее во славу Господа,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (18934, 'rst66', '1ch', 11, 19, 'и сказал: сохрани меня Господь, чтоб я сделал это! Стану ли я пить кровь мужей сих, полагавших души свои! Ибо с опасностью собственной жизни они принесли <i>воду</i>. И не захотел пить ее. Вот что сделали трое этих храбрых.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (18935, 'rst66', '1ch', 11, 20, 'И Авесса, брат Иоава, был главным из трех: он убил копьем своим триста человек, и был в славе у тех троих.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (18936, 'rst66', '1ch', 11, 21, 'Из трех он был знатнейшим и был начальником; но с теми тремя не равнялся.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (18937, 'rst66', '1ch', 11, 22, 'Ванея, сын Иодая, мужа храброго, великий по делам, из Кавцеила: он поразил двух [сыновей] Ариила Моавитского; он же сошел и убил льва во рве, в снежное время;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (18938, 'rst66', '1ch', 11, 23, 'он же убил Египтянина, человека ростом в пять локтей: в руке Египтянина было копье, как навой у ткачей, а он подошел к нему с палкою и, вырвав копье из руки Египтянина, убил его его же копьем:');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (18939, 'rst66', '1ch', 11, 24, 'вот что сделал Ванея, сын Иодая. И он был в славе у тех троих храбрых;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (18940, 'rst66', '1ch', 11, 25, 'он был знатнее тридцати, но с тремя не равнялся, и Давид поставил его ближайшим исполнителем своих приказаний.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (18941, 'rst66', '1ch', 11, 26, 'А главные из воинов: Асаил, брат Иоава; Елханан, сын Додо, из Вифлеема;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (18942, 'rst66', '1ch', 11, 27, 'Шамма Гародитянин; Херец Пелонитянин;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (18943, 'rst66', '1ch', 11, 28, 'Ира, сын Икеша, Фекоитянин; Евиезер Анафофянин;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (18944, 'rst66', '1ch', 11, 29, 'Сивхай Хушатянин; Илай Ахохиянин;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (18945, 'rst66', '1ch', 11, 30, 'Магарай Нетофафянин; Хелед, сын Вааны, Нетофафянин;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (18946, 'rst66', '1ch', 11, 31, 'Иттай, сын Рибая, из Гивы Вениаминовой; Ванея Пирафонянин;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (18947, 'rst66', '1ch', 11, 32, 'Хурай из Нагале‐Гааша; Авиел из Аравы;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (18948, 'rst66', '1ch', 11, 33, 'Азмавеф Бахарумиянин; Елияхба Шаалбонянин.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (18949, 'rst66', '1ch', 11, 34, 'Сыновья Гашема Гизонитянина: Ионафан, сын Шаге, Гараритянин;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (18950, 'rst66', '1ch', 11, 35, 'Ахиам, сын Сахара, Гараритянин; Елифал, сын Уры;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (18951, 'rst66', '1ch', 11, 36, 'Хефер из Махеры; Ахиа Пелонитянин;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (18952, 'rst66', '1ch', 11, 37, 'Хецрой Кармилитянин; Наарай, сын Езбая;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (18953, 'rst66', '1ch', 11, 38, 'Иоиль, брат Нафана; Мивхар, сын Гагрия;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (18954, 'rst66', '1ch', 11, 39, 'Целек Аммонитянин; Нахарай Берофянин, оруженосец Иоава, сына Саруи;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (18955, 'rst66', '1ch', 11, 40, 'Ира Ифриянин; Гареб Ифриянин;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (18956, 'rst66', '1ch', 11, 41, 'Урия Хеттеянин; Завад, сын Ахлая;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (18957, 'rst66', '1ch', 11, 42, 'Адина, сын Шизы, Рувимлянин, глава Рувимлян, и у него <i>было</i> тридцать;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (18958, 'rst66', '1ch', 11, 43, 'Ханан, сын Маахи; Иосафат Мифниянин;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (18959, 'rst66', '1ch', 11, 44, 'Уззия Аштерофянин; Шама и Иеиел, сыновья Хофама Ароерянина;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (18960, 'rst66', '1ch', 11, 45, 'Иедиаел, сын Шимрия, и Иоха, брат его, Фициянин;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (18961, 'rst66', '1ch', 11, 46, 'Елиел из Махавима, и Иеривай и Иошавия, сыновья Елнаама, и Ифма Моавитянин;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (18962, 'rst66', '1ch', 11, 47, 'Елиел, Овед и Иасиел из Мецоваи.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (18963, 'rst66', '1ch', 12, 1, 'И сии также пришли к Давиду в Секелаг, когда он еще укрывался от Саула, сына Кисова, и были из храбрых, помогавших в сражении.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (18964, 'rst66', '1ch', 12, 2, 'Вооруженные луком, правою и левою рукою <i>бросавшие</i> каменья и <i>стрелявшие</i> стрелами из лука, &mdash; из братьев Саула, от Вениамина:');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (18965, 'rst66', '1ch', 12, 3, 'главный Ахиезер, за ним Иоас, сыновья Шемаи, из Гивы; Иезиел и Фелет, сыновья Азмавефа; Бераха и Иегу из Анафофа;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (18966, 'rst66', '1ch', 12, 4, 'Ишмаия Гаваонитянин, храбрый из тридцати и <i>начальствовавший</i> над тридцатью; Иеремия, Иахазиил, Иоханан и Иозавад из Гедеры.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (18967, 'rst66', '1ch', 12, 5, 'Елузай, Иеримоф, Веалия, Шемария, Сафатия Харифиянин;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (18968, 'rst66', '1ch', 12, 6, 'Елкана, Ишшияху, Азариил, Иоезер и Иошавам, Кореяне;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (18969, 'rst66', '1ch', 12, 7, 'и Иоела и Зевадия, сыновья Иерохама, из Гедора.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (18970, 'rst66', '1ch', 12, 8, 'И из Гадитян перешли к Давиду в укрепление, в пустыню, люди мужественные, воинственные, вооруженные щитом и копьем; лица львиные &mdash; лица их, и они быстры, как серны на горах.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (18971, 'rst66', '1ch', 12, 9, 'Главный Езер, второй Овадия, третий Елиав,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (18972, 'rst66', '1ch', 12, 10, 'четвертый Мишманна, пятый Иеремия,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (18973, 'rst66', '1ch', 12, 11, 'шестой Афай, седьмой Елиел,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (18974, 'rst66', '1ch', 12, 12, 'восьмой Иоханан, девятый Елзавад,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (18975, 'rst66', '1ch', 12, 13, 'десятый Иеремия, одиннадцатый Махбанай.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (18976, 'rst66', '1ch', 12, 14, 'Они из сыновей Гадовых <i>были</i> главами в войске: меньший над сотнею, и больший над тысячею.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (18977, 'rst66', '1ch', 12, 15, 'Они‐то перешли Иордан в первый месяц, когда он выступает из берегов своих, и разогнали всех живших в долинах к востоку и западу.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (18978, 'rst66', '1ch', 12, 16, 'Пришли также и из сыновей Вениаминовых и Иудиных в укрепление к Давиду.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (18979, 'rst66', '1ch', 12, 17, 'Давид вышел навстречу им и сказал им: если с миром пришли вы ко мне, чтобы помогать мне, то да будет у меня с вами одно сердце; а если для того, чтобы коварно предать меня врагам моим, тогда как нет порока на руках моих, то да видит Бог отцов наших и рассудит.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (18980, 'rst66', '1ch', 12, 18, 'И объял дух Амасая, главу тридцати, <i>и сказал он:</i> мир тебе, Давид, и с тобою, сын Иессеев; мир тебе, и мир помощникам твоим; ибо помогает тебе Бог твой. Тогда принял их Давид и поставил их во главе войска.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (18981, 'rst66', '1ch', 12, 19, 'И из колена Манассиина перешли <i>некоторые</i> к Давиду, когда он шел с Филистимлянами на войну против Саула, но не помогал им, потому что предводители Филистимские, посоветовавшись, отослали его, говоря: на нашу голову он перейдет к господину своему Саулу.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (18982, 'rst66', '1ch', 12, 20, 'Когда он возвращался в Секелаг, тогда перешли к нему из Манассиян: Аднах, Иозавад, Иедиаел, Михаил, Иозавад, Елигу и Цилльфай, тысяченачальники у Манассиян.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (18983, 'rst66', '1ch', 12, 21, 'И они помогали Давиду против полчищ, ибо все это были люди храбрые и были начальниками в войске.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (18984, 'rst66', '1ch', 12, 22, 'Так с каждым днем приходили к Давиду на помощь до того, что его ополчение стало велико, как ополчение Божие.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (18985, 'rst66', '1ch', 12, 23, 'Вот число главных в войске, которые пришли к Давиду в Хеврон, чтобы передать ему царство Саулово, по слову Господню:');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (18986, 'rst66', '1ch', 12, 24, 'сыновей Иудиных, носящих щит и копье, было шесть тысяч восемьсот готовых к войне;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (18987, 'rst66', '1ch', 12, 25, 'из сыновей Симеоновых, людей храбрых, в войске было семь тысяч и сто;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (18988, 'rst66', '1ch', 12, 26, 'из сыновей Левииных четыре тысячи шестьсот;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (18989, 'rst66', '1ch', 12, 27, 'и Иоддай, князь от <i>племени</i> Аарона, и с ним три тысячи семьсот;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (18990, 'rst66', '1ch', 12, 28, 'и Садок, мужественный юноша, и род его, двадцать два начальника;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (18991, 'rst66', '1ch', 12, 29, 'из сыновей Вениаминовых, братьев Сауловых, три тысячи, &mdash; но еще многие из них держались дома Саулова;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (18992, 'rst66', '1ch', 12, 30, 'из сыновей Ефремовых двадцать тысяч восемьсот людей мужественных, людей именитых в родах своих;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (18993, 'rst66', '1ch', 12, 31, 'из полуколена Манассиина восемнадцать тысяч, которые вызваны были поименно, чтобы пойти воцарить Давида;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (18994, 'rst66', '1ch', 12, 32, 'из сынов Иссахаровых <i>пришли</i> люди разумные, которые знали, что когда надлежало делать Израилю, &mdash; их было двести главных, и все братья их следовали слову их;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (18995, 'rst66', '1ch', 12, 33, 'из <i>колена</i> Завулонова готовых к сражению, вооруженных всякими военными оружиями, пятьдесят тысяч, в строю, единодушных;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (18996, 'rst66', '1ch', 12, 34, 'из <i>колена</i> Неффалимова тысяча вождей и с ними тридцать семь тысяч с щитами и копьями;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (18997, 'rst66', '1ch', 12, 35, 'из <i>колена</i> Данова готовых к войне двадцать восемь тысяч шестьсот;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (18998, 'rst66', '1ch', 12, 36, 'от Асира воинов, готовых к сражению, сорок тысяч;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (18999, 'rst66', '1ch', 12, 37, 'из‐за Иордана, от колена Рувимова, Гадова и полуколена Манассиина, сто двадцать тысяч, со всяким воинским оружием.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19000, 'rst66', '1ch', 12, 38, 'Все эти воины, в строю, от полного сердца пришли в Хеврон воцарить Давида над всем Израилем. Да и все прочие Израильтяне были единодушны, чтобы воцарить Давида.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19001, 'rst66', '1ch', 12, 39, 'И пробыли там у Давида три дня, ели и пили, потому что братья их <i>всё</i> приготовили для них;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19002, 'rst66', '1ch', 12, 40, 'да и близкие к ним, даже до <i>колена</i> Иссахарова, Завулонова и Неффалимова, привозили все съестное на ослах, и верблюдах, и мулах, и волах: муку, смоквы, и изюм, и вино, и елей, и крупного и мелкого скота множество, так как радость была для Израиля.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19003, 'rst66', '1ch', 13, 1, 'И советовался Давид с тысяченачальниками, сотниками и со всеми вождями,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19004, 'rst66', '1ch', 13, 2, 'и сказал <i>Давид</i> всему собранию Израильтян: если угодно вам, и если на то будет воля Господа, Бога нашего, пошлем повсюду к прочим братьям нашим, по всей земле Израильской, и вместе с ними к священникам и левитам, в города и селения их, чтобы они собрались к нам;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19005, 'rst66', '1ch', 13, 3, 'и перенесем к себе ковчег Бога нашего, потому что во дни Саула мы не обращались к нему.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19006, 'rst66', '1ch', 13, 4, 'И сказало все собрание: «да будет так», потому что это дело всему народу казалось справедливым.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19007, 'rst66', '1ch', 13, 5, 'Так собрал Давид всех Израильтян, от Шихора Египетского до входа в Емаф, чтобы перенести ковчег Божий из Кириаф‐Иарима.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19008, 'rst66', '1ch', 13, 6, 'И пошел Давид и весь Израиль в Кириаф‐Иарим, что в Иудее, чтобы перенести оттуда ковчег Бога, Господа, сидящего на херувимах, на котором нарицается имя <i>Его</i>.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19009, 'rst66', '1ch', 13, 7, 'И повезли ковчег Божий на новой колеснице из дома Авинадава; и Оза и Ахия вели колесницу.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19010, 'rst66', '1ch', 13, 8, 'Давид же и все Израильтяне играли пред Богом из всей силы, с пением, на цитрах и псалтирях, и тимпанах, и кимвалах, и трубах.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19011, 'rst66', '1ch', 13, 9, 'Когда дошли до гумна Хидона, Оза простер руку свою, чтобы придержать ковчег, ибо волы наклонили его.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19012, 'rst66', '1ch', 13, 10, 'Но Господь разгневался на Озу, и поразил его за то, что он простер руку свою к ковчегу; и он умер тут же пред лицом Божиим.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19013, 'rst66', '1ch', 13, 11, 'И опечалился Давид, что Господь поразил Озу. И назвал то место поражением Озы; так называется оно и до сего дня.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19014, 'rst66', '1ch', 13, 12, 'И устрашился Давид Бога в день тот, и сказал: как я внесу к себе ковчег Божий?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19015, 'rst66', '1ch', 13, 13, 'И не повез Давид ковчега к себе, в город Давидов, а обратил его к дому Аведдара Гефянина.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19016, 'rst66', '1ch', 13, 14, 'И оставался ковчег Божий у Аведдара, в доме его, три месяца, и благословил Господь дом Аведдара и все, что у него.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19017, 'rst66', '1ch', 14, 1, 'И послал Хирам, царь Тирский, к Давиду послов, и кедровые деревья, и каменщиков, и плотников, чтобы построить ему дом.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19018, 'rst66', '1ch', 14, 2, 'Когда узнал Давид, что утвердил его Господь царем над Израилем, что вознесено высоко царство его, ради народа его Израиля,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19019, 'rst66', '1ch', 14, 3, 'Тогда взял Давид еще жен в Иерусалиме, и родил Давид еще сыновей и дочерей.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19020, 'rst66', '1ch', 14, 4, 'И вот имена родившихся у него в Иерусалиме: Самус, Совав, Нафан, Соломон,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19021, 'rst66', '1ch', 14, 5, 'Евеар, Елисуа, Елфалет,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19022, 'rst66', '1ch', 14, 6, 'Ногах, Нафек, Иафиа,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19023, 'rst66', '1ch', 14, 7, 'и Елисама, Веелиада и Елифалеф.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19024, 'rst66', '1ch', 14, 8, 'И услышали Филистимляне, что помазан Давид в царя над всем Израилем, и поднялись все Филистимляне искать Давида. И услышал Давид <i>об этом</i> и пошел против них.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19025, 'rst66', '1ch', 14, 9, 'И Филистимляне пришли и расположились в долине Рефаимов.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19026, 'rst66', '1ch', 14, 10, 'И вопросил Давид Бога, говоря: идти ли мне против Филистимлян, и предашь ли их в руки мои? И сказал ему Господь: иди, и Я предам их в руки твои.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19027, 'rst66', '1ch', 14, 11, 'И пошли они в Ваал‐Перацим, и поразил их там Давид; и сказал Давид: сломил Бог врагов моих рукою моею, как прорыв воды. Посему и дали имя месту тому: Ваал‐Перацим.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19028, 'rst66', '1ch', 14, 12, 'И оставили там <i>Филистимляне</i> богов своих, и повелел Давид, и сожжены они огнем.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19029, 'rst66', '1ch', 14, 13, 'И <i>пришли</i> опять Филистимляне и расположились по долине.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19030, 'rst66', '1ch', 14, 14, 'И еще вопросил Давид Бога, и сказал ему Бог: не ходи <i>прямо</i> на них, уклонись от них и иди к ним со стороны тутовых дерев;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19031, 'rst66', '1ch', 14, 15, 'и когда услышишь шум как бы шагов на вершинах тутовых дерев, тогда вступи в битву, ибо вышел Бог пред тобою, чтобы поразить стан Филистимлян.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19032, 'rst66', '1ch', 14, 16, 'И сделал Давид, как повелел ему Бог; и поразили стан Филистимский, от Гаваона до Газера.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19033, 'rst66', '1ch', 14, 17, 'И пронеслось имя Давидово по всем землям, и Господь сделал его страшным для всех народов.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19034, 'rst66', '1ch', 15, 1, 'И построил он себе дома в городе Давидовом, и приготовил место для ковчега Божия, и устроил для него скинию.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19035, 'rst66', '1ch', 15, 2, 'Тогда сказал Давид: <i>никто</i> не должен носить ковчега Божия, кроме левитов, потому что их избрал Господь на то, чтобы носить ковчег Божий и служить Ему вовеки.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19036, 'rst66', '1ch', 15, 3, 'И собрал Давид всех Израильтян в Иерусалим, чтобы внести ковчег Господень на место его, которое он для него приготовил.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19037, 'rst66', '1ch', 15, 4, 'И созвал Давид сыновей Аароновых и левитов:');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19038, 'rst66', '1ch', 15, 5, 'из сыновей Каафовых, Уриила начальника и братьев его &mdash; сто двадцать <i>человек;</i>');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19039, 'rst66', '1ch', 15, 6, 'из сыновей Мерариных, Асаию начальника и братьев его &mdash; двести двадцать <i>человек;</i>');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19040, 'rst66', '1ch', 15, 7, 'из сыновей Гирсоновых, Иоиля начальника и братьев его &mdash; сто тридцать <i>человек;</i>');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19041, 'rst66', '1ch', 15, 8, 'из сыновей Елисафановых, Шемаию начальника и братьев его &mdash; двести;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19042, 'rst66', '1ch', 15, 9, 'из сыновей Хевроновых, Елиела начальника и братьев его &mdash; восемьдесят;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19043, 'rst66', '1ch', 15, 10, 'из сыновей Уззииловых, Аминадава начальника и братьев его &mdash; сто двенадцать.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19044, 'rst66', '1ch', 15, 11, 'И призвал Давид священников: Садока и Авиафара, и левитов: Уриила, Асаию, Иоиля, Шемаию, Елиела и Аминадава,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19045, 'rst66', '1ch', 15, 12, 'и сказал им: вы, начальники родов левитских, освятитесь сами и братья ваши, и принесите ковчег Господа, Бога Израилева, на <i>место, которое</i> я приготовил для него;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19046, 'rst66', '1ch', 15, 13, 'ибо как прежде не вы это <i>делали</i>, то Господь, Бог наш, поразил нас за то, что мы не взыскали Его, как должно.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19047, 'rst66', '1ch', 15, 14, 'И освятились священники и левиты для того, чтобы нести ковчег Господа, Бога Израилева.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19048, 'rst66', '1ch', 15, 15, 'И понесли сыновья левитов ковчег Божий, как заповедал Моисей по слову Господа, на плечах, на шестах.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19049, 'rst66', '1ch', 15, 16, 'И приказал Давид начальникам левитов поставить братьев своих певцов с музыкальными орудиями, с псалтирями и цитрами и кимвалами, чтобы они громко возвещали глас радования.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19050, 'rst66', '1ch', 15, 17, 'И поставили левиты Емана, сына Иоилева, и из братьев его, Асафа, сына Верехиина, а из сыновей Мерариных, братьев их, Ефана, сына Кушаии;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19051, 'rst66', '1ch', 15, 18, 'и с ними братьев их второстепенных: Захарию, Бена, Иаазиила, Шемирамофа, Иехиила, Унния, Елиава, Ванею, Маасея, Маттафию, Елифлеуя, Микнея и Овед‐Едома и Иеиела, привратников.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19052, 'rst66', '1ch', 15, 19, 'Еман, Асаф и Ефан играли громко на медных кимвалах,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19053, 'rst66', '1ch', 15, 20, 'а Захария, Азиил, Шемирамоф, Иехиил, Унний, Елиав, Маасей и Ванея &mdash; на псалтирях, тонким голосом.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19054, 'rst66', '1ch', 15, 21, 'Маттафия же, Елифлеуй, Микней, Овед‐Едом, Иеиел и Азазия &mdash; на цитрах, чтобы делать начало.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19055, 'rst66', '1ch', 15, 22, 'А Хенания, начальник левитов, был учитель пения, потому что был искусен в нем.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19056, 'rst66', '1ch', 15, 23, 'Верехия и Елкана были придверниками у ковчега.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19057, 'rst66', '1ch', 15, 24, 'Шевания, Иосафат, Нафанаил, Амасай, Захария, Ванея и Елиезер, священники, трубили трубами пред ковчегом Божиим. Овед‐Едом и Иехия <i>были</i> придверниками у ковчега.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19058, 'rst66', '1ch', 15, 25, 'Так Давид и старейшины Израилевы и тысяченачальники пошли перенести ковчег завета Господня из дома Овед‐Едомова с веселием.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19059, 'rst66', '1ch', 15, 26, 'И когда Бог помог левитам, несшим ковчег завета Господня, тогда закололи в жертву семь тельцов и семь овнов.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19060, 'rst66', '1ch', 15, 27, 'Давид был одет в виссонную одежду, <i>а также</i> и все левиты, несшие ковчег, и певцы, и Хенания, начальник музыкантов и певцов. На Давиде же был <i>еще</i> льняной ефод.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19061, 'rst66', '1ch', 15, 28, 'Так весь Израиль вносил ковчег завета Господня с восклицанием, при звуке рога и труб и кимвалов, играя на псалтирях и цитрах.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19062, 'rst66', '1ch', 15, 29, 'Когда ковчег завета Господня входил в город Давидов, Мелхола, дочь Саулова, смотрела в окно и, увидев царя Давида, скачущего и веселящегося, уничижила его в сердце своем.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19063, 'rst66', '1ch', 16, 1, 'И принесли ковчег Божий, и поставили его среди скинии, которую устроил для него Давид, и вознесли Богу всесожжения и мирные жертвы.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19064, 'rst66', '1ch', 16, 2, 'Когда Давид окончил всесожжения и приношение мирных жертв, то благословил народ именем Господа');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19065, 'rst66', '1ch', 16, 3, 'и раздал всем Израильтянам, и мужчинам и женщинам, по одному хлебу и по куску мяса и по кружке вина,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19066, 'rst66', '1ch', 16, 4, 'и поставил на службу пред ковчегом Господним <i>некоторых</i> из левитов, чтобы они славословили, благодарили и превозносили Господа, Бога Израилева:');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19067, 'rst66', '1ch', 16, 5, 'Асафа главным, вторым по нем Захарию, Иеиела, Шемирамофа, Иехиила, Маттафию, Елиава, и Ванею, Овед‐Едома и Иеиела с псалтирями и цитрами, и Асафа для игры на кимвалах,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19068, 'rst66', '1ch', 16, 6, 'а Ванею и Озиила, священников, <i>чтобы</i> постоянно <i>трубили</i> пред ковчегом завета Божия.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19069, 'rst66', '1ch', 16, 7, 'В этот день Давид в первый раз дал псалом для славословия Господу через Асафа и братьев его.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19070, 'rst66', '1ch', 16, 8, 'Славьте Господа, провозглашайте имя Его; возвещайте в народах дела Его;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19071, 'rst66', '1ch', 16, 9, 'пойте Ему, бряцайте Ему; поведайте о всех чудесах Его;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19072, 'rst66', '1ch', 16, 10, 'хвалитесь именем Его святым; да веселится сердце ищущих Господа;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19073, 'rst66', '1ch', 16, 11, 'взыщите Господа и силы Его, ищите непрестанно лица Его;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19074, 'rst66', '1ch', 16, 12, 'поминайте чудеса, которые Он сотворил, знамения Его и суды уст Его,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19075, 'rst66', '1ch', 16, 13, '<i>вы</i>, семя Израилево, рабы Его, сыны Иакова, избранные Его!');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19076, 'rst66', '1ch', 16, 14, 'Он Господь, Бог наш; суды Его по всей земле.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19077, 'rst66', '1ch', 16, 15, 'Помните вечно завет Его, слово, которое Он заповедал в тысячу родов,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19078, 'rst66', '1ch', 16, 16, 'то, что завещал Аврааму, и в чем клялся Исааку,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19079, 'rst66', '1ch', 16, 17, 'и что поставил Иакову в закон и Израилю в завет вечный,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19080, 'rst66', '1ch', 16, 18, 'говоря: «тебе дам Я землю Ханаанскую, в наследственный удел вам».');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19081, 'rst66', '1ch', 16, 19, 'Они были тогда малочисленны и ничтожны, и пришельцы в ней,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19082, 'rst66', '1ch', 16, 20, 'и переходили от народа к народу и из одного царства к другому народу;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19083, 'rst66', '1ch', 16, 21, 'но Он никому не позволил обижать их, и обличал за них царей:');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19084, 'rst66', '1ch', 16, 22, '«Не прикасайтесь к помазанным Моим, и пророкам Моим не делайте зла».');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19085, 'rst66', '1ch', 16, 23, 'Пойте Господу, вся земля, благовествуйте изо дня в день спасение Его.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19086, 'rst66', '1ch', 16, 24, 'Возвещайте язычникам славу Его, всем народам чудеса Его,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19087, 'rst66', '1ch', 16, 25, 'ибо велик Господь и достохвален, страшен паче всех богов.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19088, 'rst66', '1ch', 16, 26, 'Ибо все боги народов ничто, а Господь небеса сотворил.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19089, 'rst66', '1ch', 16, 27, 'Слава и величие пред лицом Его, могущество и радость на месте Его.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19090, 'rst66', '1ch', 16, 28, 'Воздайте Господу, племена народов, воздайте Господу славу и честь,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19091, 'rst66', '1ch', 16, 29, 'воздайте Господу славу имени Его. Возьмите дар, идите пред лицо Его, поклонитесь Господу в благолепии святыни Его.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19092, 'rst66', '1ch', 16, 30, 'Трепещи пред Ним, вся земля, ибо Он основал вселенную, она не поколеблется.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19093, 'rst66', '1ch', 16, 31, 'Да веселятся небеса, да торжествует земля, и да скажут в народах: Господь царствует!');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19094, 'rst66', '1ch', 16, 32, 'Да плещет море и что наполняет его, да радуется поле и все, что на нем.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19095, 'rst66', '1ch', 16, 33, 'Да ликуют вместе все дерева дубравные пред лицом Господа, ибо Он идет судить землю.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19096, 'rst66', '1ch', 16, 34, 'Славьте Господа, ибо вовек милость Его,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19097, 'rst66', '1ch', 16, 35, 'и скажите: спаси нас, Боже, Спаситель наш! Собери нас и избавь нас от народов, да славим святое имя Твое и да хвалимся славою Твоею!');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19098, 'rst66', '1ch', 16, 36, 'Благословен Господь, Бог Израилев, от века и до века! И сказал весь народ: аминь! аллилуия!');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19099, 'rst66', '1ch', 16, 37, 'Давид оставил там, пред ковчегом завета Господня, Асафа и братьев его, чтоб они служили пред ковчегом постоянно, каждый день,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19100, 'rst66', '1ch', 16, 38, 'и Овед‐Едома и братьев его, шестьдесят восемь <i>человек;</i> Овед‐Едома, сына Идифунова, и Хосу &mdash; привратниками,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19101, 'rst66', '1ch', 16, 39, 'а Садока священника и братьев его священников &mdash; пред жилищем Господним, что на высоте в Гаваоне,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19102, 'rst66', '1ch', 16, 40, 'для возношения всесожжения Господу на жертвеннике всесожжений постоянно, утром и вечером, и для всего, что написано в законе Господа, который Он заповедал Израилю;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19103, 'rst66', '1ch', 16, 41, 'и с ними Емана и Идифуна и прочих избранных, которые назначены поименно, чтобы славить Господа, ибо навек милость Его.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19104, 'rst66', '1ch', 16, 42, 'При них Еман и Идифун прославляли Бога, играя на трубах, кимвалах и разных музыкальных орудиях; сыновей же Идифуна <i>поставил</i> при вратах.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19105, 'rst66', '1ch', 16, 43, 'И пошел весь народ, каждый в свой дом; возвратился и Давид, чтобы благословить дом свой.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19106, 'rst66', '1ch', 17, 1, 'Когда Давид жил в доме своем, то сказал Давид Нафану пророку: вот, я живу в доме кедровом, а ковчег завета Господня под шатром.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19107, 'rst66', '1ch', 17, 2, 'И сказал Нафан Давиду: все, что у тебя на сердце, делай, ибо с тобою Бог.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19108, 'rst66', '1ch', 17, 3, 'Но в ту же ночь было слово Божие к Нафану:');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19109, 'rst66', '1ch', 17, 4, 'пойди и скажи рабу Моему Давиду: так говорит Господь: не ты построишь Мне дом для обитания,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19110, 'rst66', '1ch', 17, 5, 'ибо Я не жил в доме с того дня, как вывел сынов Израиля, и до сего дня, а <i>ходил</i> из скинии в скинию и из жилища <i>в жилище</i>.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19111, 'rst66', '1ch', 17, 6, 'Где ни ходил Я со всем Израилем, сказал ли Я хотя слово которому‐либо из судей Израильских, которым Я повелел пасти народ Мой: «зачем вы не построите Мне дома кедрового?»');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19112, 'rst66', '1ch', 17, 7, 'И теперь так скажи рабу Моему Давиду: так говорит Господь Саваоф: Я взял тебя от стада овец, чтобы ты был вождем народа Моего, Израиля;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19113, 'rst66', '1ch', 17, 8, 'и был с тобою везде, куда ты ни ходил, и истребил всех врагов твоих пред лицом твоим, и сделал имя твое, как имя великих на земле;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19114, 'rst66', '1ch', 17, 9, 'и Я устроил место для народа Моего, Израиля, и укоренил его, и будет он спокойно жить на месте своем, и не будет более тревожим, и нечестивые не станут больше теснить его, как прежде,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19115, 'rst66', '1ch', 17, 10, 'в те дни, когда Я поставил судей над народом Моим, Израилем, и Я смирил всех врагов твоих, и возвещаю тебе, что Господь устроит тебе дом.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19116, 'rst66', '1ch', 17, 11, 'Когда исполнятся дни твои, и ты отойдешь к отцам твоим, тогда Я восставлю семя твое после тебя, которое будет из сынов твоих, и утвержу царство его.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19117, 'rst66', '1ch', 17, 12, 'Он построит Мне дом, и утвержу престол его навеки.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19118, 'rst66', '1ch', 17, 13, 'Я буду ему отцом, и он будет Мне сыном, &mdash; и милости Моей не отниму от него, как Я отнял от того, который был прежде тебя.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19119, 'rst66', '1ch', 17, 14, 'Я поставлю его в доме Моем и в царстве Моем навеки, и престол его будет тверд вечно.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19120, 'rst66', '1ch', 17, 15, 'Все эти слова и все видение точно пересказал Нафан Давиду.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19121, 'rst66', '1ch', 17, 16, 'И пришел царь Давид, и стал пред лицом Господним, и сказал: кто я, Господи Боже, и что такое дом мой, что Ты так возвысил меня?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19122, 'rst66', '1ch', 17, 17, 'Но и этого еще мало показалось в очах Твоих, Боже; Ты возвещаешь о доме раба Твоего вдаль, и взираешь на меня, как на человека великого, Господи Боже!');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19123, 'rst66', '1ch', 17, 18, 'Что еще может прибавить пред Тобою Давид для возвеличения раба Твоего? Ты знаешь раба Твоего!');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19124, 'rst66', '1ch', 17, 19, 'Господи! для раба Твоего, по сердцу Твоему, Ты делаешь все это великое, чтобы явить всякое величие.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19125, 'rst66', '1ch', 17, 20, 'Господи! Нет подобного Тебе, и нет Бога, кроме Тебя, по всему, что слышали мы своими ушами.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19126, 'rst66', '1ch', 17, 21, 'И кто подобен народу Твоему, Израилю, единственному народу на земле, к которому приходил Бог, <i>чтоб</i> искупить его Себе в народ, сделать Себе имя великим и страшным делом &mdash; прогнанием народов от лица народа Твоего, который Ты избавил из Египта.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19127, 'rst66', '1ch', 17, 22, 'Ты соделал народ Твой, Израиля, Своим собственным народом навек, и Ты, Господи, стал Богом его.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19128, 'rst66', '1ch', 17, 23, 'Итак, теперь, о Господи, слово, которое Ты сказал о рабе Твоем и о доме его, утверди навек, и сделай, как Ты сказал.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19129, 'rst66', '1ch', 17, 24, 'И да пребудет и возвеличится имя Твое вовеки, чтобы говорили: «Господь Саваоф, Бог Израилев, есть Бог над Израилем», и дом раба Твоего Давида да будет тверд пред лицом Твоим.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19130, 'rst66', '1ch', 17, 25, 'Ибо Ты, Боже мой, открыл рабу Твоему, что Ты устроишь ему дом, поэтому раб Твой и дерзнул молиться пред Тобою.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19131, 'rst66', '1ch', 17, 26, 'И ныне, Господи, Ты Бог, и Ты сказал о рабе Твоем такое благо.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19132, 'rst66', '1ch', 17, 27, 'Начни же благословлять дом раба Твоего, чтоб он был вечно пред лицом Твоим. Ибо если Ты, Господи, благословишь, то будет он благословен вовек.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19133, 'rst66', '1ch', 18, 1, 'После сего Давид поразил Филистимлян и смирил их, и взял Геф и зависящие от него города из руки Филистимлян.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19134, 'rst66', '1ch', 18, 2, 'Он поразил также Моавитян, &mdash; и сделались Моавитяне рабами Давида, принося ему дань.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19135, 'rst66', '1ch', 18, 3, 'И поразил Давид Адраазара, царя Сувского, в Емафе, когда тот шел утвердить власть свою при реке Евфрате.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19136, 'rst66', '1ch', 18, 4, 'И взял Давид у него тысячу колесниц, семь тысяч всадников и двадцать тысяч пеших, и разрушил Давид все колесницы, оставив из них <i>только</i> сто.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19137, 'rst66', '1ch', 18, 5, 'Сирияне Дамасские пришли было на помощь к Адраазару, царю Сувскому, но Давид поразил двадцать две тысячи Сириян.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19138, 'rst66', '1ch', 18, 6, 'И поставил Давид <i>охранное войско</i> в Сирии Дамасской, и сделались Сирияне рабами Давида, принося ему дань. И помогал Господь Давиду везде, куда он ни ходил.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19139, 'rst66', '1ch', 18, 7, 'И взял Давид золотые щиты, которые были у рабов Адраазара, и принес их в Иерусалим.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19140, 'rst66', '1ch', 18, 8, 'А из Тивхавы и Куна, городов Адраазаровых, взял Давид весьма много меди. Из нее Соломон сделал медное море и столбы и медные сосуды.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19141, 'rst66', '1ch', 18, 9, 'И услышал Фой, царь Имафа, что Давид поразил все войско Адраазара, царя Сувского.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19142, 'rst66', '1ch', 18, 10, 'И послал Иорама, сына своего, к царю Давиду, приветствовать его и благодарить за то, что он воевал с Адраазаром и поразил его, &mdash; ибо Фой был в войне с Адраазаром, &mdash; и <i>с ним</i> всякие сосуды золотые, серебряные и медные.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19143, 'rst66', '1ch', 18, 11, 'И посвятил их царь Давид Господу вместе с серебром и золотом, которое он взял от всех народов: от Идумеян, Моавитян, Аммонитян, Филистимлян и от Амаликитян.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19144, 'rst66', '1ch', 18, 12, 'И Авесса, сын Саруи, поразил Идумеян на долине Соляной восемнадцать тысяч;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19145, 'rst66', '1ch', 18, 13, 'и поставил в Идумее охранное войско, и сделались все Идумеяне рабами Давиду. Господь помогал Давиду везде, куда он ни ходил.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19146, 'rst66', '1ch', 18, 14, 'И царствовал Давид над всем Израилем, и творил суд и правду всему народу своему.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19147, 'rst66', '1ch', 18, 15, 'Иоав, сын Саруи, <i>был</i> начальником войска, Иосафат, сын Ахилуда, &mdash; дееписателем,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19148, 'rst66', '1ch', 18, 16, 'Садок, сын Ахитува, и Авимелех, сын Авиафара, &mdash; священниками, а Суса &mdash; писцом,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19149, 'rst66', '1ch', 18, 17, 'Ванея, сын Иодая, &mdash; над Хелефеями и Фелефеями, а сыновья Давидовы &mdash; первыми при царе.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19150, 'rst66', '1ch', 19, 1, 'После сего умер Наас, царь Аммонитский, и воцарился сын его вместо него.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19151, 'rst66', '1ch', 19, 2, 'И сказал Давид: окажу я милость Аннону, сыну Наасову, за благодеяние, которое отец его оказал мне. И послал Давид послов утешить его об отце его; и пришли слуги Давидовы в землю Аммонитскую, к Аннону, чтобы утешить его.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19152, 'rst66', '1ch', 19, 3, 'Но князья Аммонитские сказали Аннону: неужели ты думаешь, что Давид из уважения к отцу твоему прислал к тебе утешителей? Не для того ли пришли слуги его к тебе, чтобы разведать и высмотреть землю и разорить ее?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19153, 'rst66', '1ch', 19, 4, 'И взял Аннон слуг Давидовых и обрил их, и обрезал одежды их наполовину, до чресл, и отпустил их.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19154, 'rst66', '1ch', 19, 5, 'И пошли они. И донесено было Давиду о людях сих, и он послал им навстречу, так как они были очень обесчещены; и сказал царь: останьтесь в Иерихоне, пока отрастут бороды ваши, и тогда возвратитесь.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19155, 'rst66', '1ch', 19, 6, 'Когда Аммонитяне увидели, что они сделались ненавистными Давиду, тогда послал Аннон и Аммонитяне тысячу талантов серебра, чтобы нанять себе колесниц и всадников из Сирии Месопотамской, и из Сирии Мааха, и из Сувы.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19156, 'rst66', '1ch', 19, 7, 'И наняли себе тридцать две тысячи колесниц и царя Мааха с народом его, которые пришли и расположились станом пред Медевою. И Аммонитяне собрались из городов своих и выступили на войну.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19157, 'rst66', '1ch', 19, 8, 'Когда услышал об этом Давид, то послал Иоава со всем войском храбрых.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19158, 'rst66', '1ch', 19, 9, 'И выступили Аммонитяне и выстроились к сражению у ворот города, а цари, которые пришли, отдельно, в поле.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19159, 'rst66', '1ch', 19, 10, 'Иоав, видя, что предстоит ему сражение спереди и сзади, избрал воинов из всех отборных в Израиле и выстроил <i>их</i> против Сириян.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19160, 'rst66', '1ch', 19, 11, 'А остальную часть народа поручил Авессе, брату своему, чтоб они выстроились против Аммонитян.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19161, 'rst66', '1ch', 19, 12, 'И сказал он: если Сирияне будут одолевать меня, то ты поможешь мне, а если Аммонитяне будут одолевать тебя, то я помогу тебе.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19162, 'rst66', '1ch', 19, 13, 'Будь мужествен, и будем твердо стоять за народ наш и за города Бога нашего, &mdash; и Господь пусть сделает, что Ему угодно.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19163, 'rst66', '1ch', 19, 14, 'И вступил Иоав и люди, которые были у него, в сражение с Сириянами, и они побежали от него.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19164, 'rst66', '1ch', 19, 15, 'Аммонитяне же, увидев, что Сирияне бегут, и сами побежали от Авессы, брата его, и ушли в город. И пришел Иоав в Иерусалим.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19165, 'rst66', '1ch', 19, 16, 'Сирияне, видя, что они поражены Израильтянами, отправили послов и вывели Сириян, которые были по ту сторону реки, и Совак, военачальник Адраазаров, предводительствовал ими.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19166, 'rst66', '1ch', 19, 17, 'Когда донесли об этом Давиду, он собрал всех Израильтян, перешел Иордан и, придя к ним, выстроился против них; и вступил Давид в сражение с Сириянами, и они сразились с ним.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19167, 'rst66', '1ch', 19, 18, 'И Сирияне побежали от Израильтян, и истребил Давид у Сириян семь тысяч колесниц и сорок тысяч пеших, и Совака военачальника умертвил.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19168, 'rst66', '1ch', 19, 19, 'Когда увидели слуги Адраазара, что они поражены Израильтянами, заключили с Давидом мир и подчинились ему. И не хотели Сирияне помогать более Аммонитянам.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19169, 'rst66', '1ch', 20, 1, 'Через год, в то время, когда цари выходят <i>на войну</i>, вывел Иоав войско и стал разорять землю Аммонитян, и пришел и осадил Равву. Давид же оставался в Иерусалиме. Иоав, завоевав Равву, разрушил ее.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19170, 'rst66', '1ch', 20, 2, 'И взял Давид венец царя их с головы его, и в нем оказалось весу талант золота, и драгоценные камни были на нем; и был он возложен на голову Давида. И добычи очень много вынес из города.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19171, 'rst66', '1ch', 20, 3, 'А народ, который был в нем, вывел и умерщвлял их пилами, железными молотилами и секирами. Так поступил Давид со всеми городами Аммонитян, и возвратился Давид и весь народ в Иерусалим.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19172, 'rst66', '1ch', 20, 4, 'После того началась война с Филистимлянами в Газере. Тогда Совохай Хушатянин поразил Сафа, одного из потомков Рефаимов. И они усмирились.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19173, 'rst66', '1ch', 20, 5, 'И опять была война с Филистимлянами. Тогда Елханам, сын Иаира, поразил Лахмия, брата Голиафова, Гефянина, у которого древко копья было, как навой у ткачей.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19174, 'rst66', '1ch', 20, 6, 'Было еще сражение в Гефе. Там был один рослый человек, у которого было по шести пальцев, <i>всего</i> двадцать четыре. И он также был из потомков Рефаимов.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19175, 'rst66', '1ch', 20, 7, 'Он поносил Израиля, но Ионафан, сын Шимы, брата Давидова, поразил его.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19176, 'rst66', '1ch', 20, 8, 'Это были родившиеся от Рефаимов в Гефе, и пали от руки Давида и от руки слуг его.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19177, 'rst66', '1ch', 21, 1, 'И восстал сатана на Израиля, и возбудил Давида сделать счисление Израильтян.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19178, 'rst66', '1ch', 21, 2, 'И сказал Давид Иоаву и начальствующим в народе: пойдите исчислите Израильтян, от Вирсавии до Дана, и представьте мне, чтоб я знал число их.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19179, 'rst66', '1ch', 21, 3, 'И сказал Иоав: да умножит Господь народ Свой во сто раз против того, сколько есть его. Не все ли они, господин мой, царь, рабы господина моего? Для чего же требует сего господин мой? Чтобы вменилось это в вину Израилю?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19180, 'rst66', '1ch', 21, 4, 'Но царское слово превозмогло Иоава; и пошел Иоав, и обошел всего Израиля, и пришел в Иерусалим.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19181, 'rst66', '1ch', 21, 5, 'И подал Иоав Давиду список народной переписи, и было всех Израильтян тысяча тысяч, и сто тысяч мужей, обнажающих меч, и Иудеев &mdash; четыреста семьдесят тысяч, обнажающих меч.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19182, 'rst66', '1ch', 21, 6, 'А левитов и Вениамитян он не исчислял между ними, потому что царское слово противно было Иоаву.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19183, 'rst66', '1ch', 21, 7, 'И не угодно было в очах Божиих дело сие, и Он поразил Израиля.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19184, 'rst66', '1ch', 21, 8, 'И сказал Давид Богу: весьма согрешил я, что сделал это. И ныне прости вину раба Твоего, ибо я поступил очень безрассудно.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19185, 'rst66', '1ch', 21, 9, 'И говорил Господь Гаду, прозорливцу Давидову, и сказал:');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19186, 'rst66', '1ch', 21, 10, 'пойди и скажи Давиду: так говорит Господь: три <i>наказания</i> Я предлагаю тебе, избери себе одно из них, &mdash; и Я пошлю его на тебя.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19187, 'rst66', '1ch', 21, 11, 'И пришел Гад к Давиду и сказал ему: так говорит Господь: избирай себе:');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19188, 'rst66', '1ch', 21, 12, 'или три года &mdash; голод, или три месяца будешь ты преследуем неприятелями твоими и меч врагов твоих будет досягать <i>до тебя</i>; или три дня &mdash; меч Господень и язва на земле и Ангел Господень, истребляющий во всех пределах Израиля. Итак, рассмотри, что мне отвечать Пославшему меня с словом.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19189, 'rst66', '1ch', 21, 13, 'И сказал Давид Гаду: тяжело мне очень, но пусть лучше впаду в руки Господа, ибо весьма велико милосердие Его, только бы не впасть мне в руки человеческие.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19190, 'rst66', '1ch', 21, 14, 'И послал Господь язву на Израиля, и умерло Израильтян семьдесят тысяч человек.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19191, 'rst66', '1ch', 21, 15, 'И послал Бог Ангела в Иерусалим, чтобы истреблять его. И когда он начал истреблять, увидел Господь и пожалел о сем бедствии, и сказал Ангелу‐истребителю: довольно! теперь опусти руку твою. Ангел же Господень стоял <i>тогда</i> над гумном Орны Иевусеянина.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19192, 'rst66', '1ch', 21, 16, 'И поднял Давид глаза свои, и увидел Ангела Господня, стоящего между землею и небом, с обнаженным в руке его мечом, простертым на Иерусалим; и пал Давид и старейшины, покрытые вретищем, на лица свои.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19193, 'rst66', '1ch', 21, 17, 'И сказал Давид Богу: не я ли велел исчислить народ? я согрешил, я сделал зло, а эти овцы что сделали? Господи, Боже мой! да будет рука Твоя на мне и на доме отца моего, а не на народе Твоем, чтобы погубить <i>его</i>.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19194, 'rst66', '1ch', 21, 18, 'И Ангел Господень сказал Гаду, чтобы тот сказал Давиду: пусть Давид придет и поставит жертвенник Господу на гумне Орны Иевусеянина.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19195, 'rst66', '1ch', 21, 19, 'И пошел Давид, по слову Гада, которое он говорил именем Господним.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19196, 'rst66', '1ch', 21, 20, 'Орна обратился, увидел Ангела, и четыре сына его с ним скрылись. Орна молотил тогда пшеницу.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19197, 'rst66', '1ch', 21, 21, 'И пришел Давид к Орне. Орна, взглянув и увидев Давида, вышел из гумна и поклонился Давиду лицом до земли.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19198, 'rst66', '1ch', 21, 22, 'И сказал Давид Орне: отдай мне место под гумном, я построю на нем жертвенник Господу; за настоящую цену отдай мне его, чтобы прекратилось истребление народа.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19199, 'rst66', '1ch', 21, 23, 'И сказал Орна Давиду: возьми себе; пусть делает господин мой, царь, что ему угодно; вот, я отдаю и волов на всесожжение, и молотильные орудия на дрова, и пшеницу на приношение; все это отдаю даром.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19200, 'rst66', '1ch', 21, 24, 'И сказал царь Давид Орне: нет, я хочу купить у тебя за настоящую цену, ибо не стану я приносить твоей собственности Господу, и не буду приносить во всесожжение <i>взятого</i> даром.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19201, 'rst66', '1ch', 21, 25, 'И дал Давид Орне за это место шестьсот сиклей золота.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19202, 'rst66', '1ch', 21, 26, 'И соорудил там Давид жертвенник Господу и вознес всесожжения и мирные жертвы; и призвал Господа, и Он услышал его, <i>послав</i> огонь с неба на жертвенник всесожжения.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19203, 'rst66', '1ch', 21, 27, 'И сказал Господь Ангелу: возврати меч твой в ножны его.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19204, 'rst66', '1ch', 21, 28, 'В это время Давид, видя, что Господь услышал его на гумне Орны Иевусеянина, принес там жертву.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19205, 'rst66', '1ch', 21, 29, 'Скиния же Господня, которую сделал Моисей в пустыне, и жертвенник всесожжения <i>находились</i> в то время на высоте в Гаваоне.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19206, 'rst66', '1ch', 21, 30, 'И не мог Давид пойти туда, чтобы взыскать Бога, потому что устрашен был мечом Ангела Господня.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19207, 'rst66', '1ch', 22, 1, 'И сказал Давид: вот дом Господа Бога и вот жертвенник для всесожжений Израиля.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19208, 'rst66', '1ch', 22, 2, 'И приказал Давид собрать пришельцев, находившихся в земле Израильской, и поставил каменотесов, чтобы обтесывать камни для построения дома Божия.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19209, 'rst66', '1ch', 22, 3, 'И множество железа для гвоздей к дверям ворот и для связей заготовил Давид, и множество меди без весу,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19210, 'rst66', '1ch', 22, 4, 'и кедровых дерев без счету, потому что Сидоняне и Тиряне доставили Давиду множество кедровых дерев.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19211, 'rst66', '1ch', 22, 5, 'И сказал Давид: Соломон, сын мой, молод и малосилен, а дом, который следует выстроить для Господа, должен быть весьма величествен, на славу и украшение пред всеми землями: итак, буду я заготовлять для него. И заготовил Давид до смерти своей много.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19212, 'rst66', '1ch', 22, 6, 'И призвал Соломона, сына своего, и завещал ему построить дом Господу, Богу Израилеву.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19213, 'rst66', '1ch', 22, 7, 'И сказал Давид Соломону: сын мой! у меня было на сердце построить дом во имя Господа, Бога моего,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19214, 'rst66', '1ch', 22, 8, 'но было ко мне слово Господне, и сказано: «ты пролил много крови и вел большие войны; ты не должен строить дома имени Моему, потому что пролил много крови на землю пред лицом Моим.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19215, 'rst66', '1ch', 22, 9, 'Вот, у тебя родится сын: он будет человек мирный; Я дам ему покой от всех врагов его кругом: посему имя ему будет Соломон. И мир и покой дам Израилю во дни его.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19216, 'rst66', '1ch', 22, 10, 'Он построит дом имени Моему, и он будет Мне сыном, а Я ему отцом, и утвержу престол царства его над Израилем навек».');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19217, 'rst66', '1ch', 22, 11, 'И ныне, сын мой! да будет Господь с тобою, чтобы ты был благоуспешен и построил дом Господу, Богу твоему, как Он говорил о тебе.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19218, 'rst66', '1ch', 22, 12, 'Да даст тебе Господь смысл и разум, и поставит тебя над Израилем; и соблюди закон Господа, Бога твоего.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19219, 'rst66', '1ch', 22, 13, 'Тогда ты будешь благоуспешен, если будешь стараться исполнять уставы и законы, которые заповедал Господь Моисею для Израиля. Будь тверд и мужествен, не бойся и не унывай.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19220, 'rst66', '1ch', 22, 14, 'И вот, я при скудости моей приготовил для дома Господня сто тысяч талантов золота и тысячу тысяч талантов серебра, а меди и железу нет веса, потому что их множество; и дерева и камни я также заготовил, а ты еще прибавь к этому.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19221, 'rst66', '1ch', 22, 15, 'У тебя множество рабочих, и каменотесов, резчиков и плотников, и всяких способных на всякое дело;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19222, 'rst66', '1ch', 22, 16, 'золоту, серебру и меди и железу нет счета: начни и делай; Господь будет с тобою.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19223, 'rst66', '1ch', 22, 17, 'И завещал Давид всем князьям Израилевым помогать Соломону, сыну его:');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19224, 'rst66', '1ch', 22, 18, 'не с вами ли Господь, Бог наш, давший вам покой со всех сторон? потому что Он предал в руки мои жителей земли, и покорилась земля пред Господом и пред народом Его.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19225, 'rst66', '1ch', 22, 19, 'Итак, расположите сердце ваше и душу вашу к тому, чтобы взыскать Господа, Бога вашего. Встаньте и постройте святилище Господу Богу, чтобы перенести ковчег завета Господня и священные сосуды Божии в дом, созидаемый имени Господню.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19226, 'rst66', '1ch', 23, 1, 'Давид, состарившись и насытившись <i>жизнью</i>, воцарил над Израилем сына своего Соломона.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19227, 'rst66', '1ch', 23, 2, 'И собрал <i>он</i> всех князей Израилевых и священников и левитов,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19228, 'rst66', '1ch', 23, 3, 'и исчислены были левиты, от тридцати лет и выше, и было число их, считая поголовно, тридцать восемь тысяч человек.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19229, 'rst66', '1ch', 23, 4, 'Из них <i>назначены</i> для дела в доме Господнем двадцать четыре тысячи, писцов же и судей шесть тысяч,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19230, 'rst66', '1ch', 23, 5, 'и четыре тысячи привратников, и четыре тысячи прославляющих Господа на <i>музыкальных</i> орудиях, которые он сделал для прославления.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19231, 'rst66', '1ch', 23, 6, 'И разделил их Давид на череды по сынам Левия &mdash; Гирсону, Каафу и Мерари.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19232, 'rst66', '1ch', 23, 7, 'Из Гирсонян &mdash; Лаедан и Шимей.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19233, 'rst66', '1ch', 23, 8, 'Сыновья Лаедана: первый Иехиил, Зефам и Иоиль, трое.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19234, 'rst66', '1ch', 23, 9, 'Сыновья Шимея: Шеломиф, Хазиил и Гаран, трое. Они главы поколений Лаедановых.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19235, 'rst66', '1ch', 23, 10, 'Еще сыновья Шимея: Иахаф, Зиза, Иеуш и Берия. Это сыновья Шимея, четверо.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19236, 'rst66', '1ch', 23, 11, 'Иахаф был главным, Зиза вторым; Иеуш и Берия имели детей немного, и потому они были в одном счете при доме отца.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19237, 'rst66', '1ch', 23, 12, 'Сыновья Каафа: Амрам, Ицгар, Хеврон и Озиил, четверо.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19238, 'rst66', '1ch', 23, 13, 'Сыновья Амрама: Аарон и Моисей. Аарон отделен был на посвящение ко Святому Святых, он и сыновья его, навеки, чтобы совершать курение пред лицом Господа, чтобы служить Ему и благословлять именем Его навеки.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19239, 'rst66', '1ch', 23, 14, 'А Моисей, человек Божий, <i>и</i> сыновья его причтены к колену Левиину.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19240, 'rst66', '1ch', 23, 15, 'Сыновья Моисея: Гирсон и Елиезер.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19241, 'rst66', '1ch', 23, 16, 'Сыновья Гирсона: первый был Шевуил.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19242, 'rst66', '1ch', 23, 17, 'Сыновья Елиезера были: первый Рехавия. И не было у Елиезера других сыновей; у Рехавии же было очень много сыновей.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19243, 'rst66', '1ch', 23, 18, 'Сыновья Ицгара: первый Шеломиф.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19244, 'rst66', '1ch', 23, 19, 'Сыновья Хеврона: первый Иерия и второй Амария, третий Иахазиил и четвертый Иекамам.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19245, 'rst66', '1ch', 23, 20, 'Сыновья Озиила: первый Миха и второй Ишшия.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19246, 'rst66', '1ch', 23, 21, 'Сыновья Мерарины: Махли и Муши. Сыновья Махлия: Елеазар и Кис.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19247, 'rst66', '1ch', 23, 22, 'И умер Елеазар, и не было у него сыновей, а только дочери; и взяли их за себя сыновья Киса, братья их.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19248, 'rst66', '1ch', 23, 23, 'Сыновья Мушия: Махли, Едер и Иремоф &mdash; трое.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19249, 'rst66', '1ch', 23, 24, 'Вот сыновья Левиины, по домам отцов их, главы семейств, по именному счислению их поголовно, которые отправляли дела служения в доме Господнем, от двадцати лет и выше.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19250, 'rst66', '1ch', 23, 25, 'Ибо Давид сказал: Господь, Бог Израилев, дал покой народу Своему и водворил его в Иерусалиме навеки,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19251, 'rst66', '1ch', 23, 26, 'и левитам не нужно носить скинию и всякие вещи ее для служения в ней.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19252, 'rst66', '1ch', 23, 27, 'Посему, по последним повелениям Давида, исчислены левиты от двадцати лет и выше,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19253, 'rst66', '1ch', 23, 28, 'чтоб они были при сынах Аароновых, для служения дому Господню, во дворе и в пристройках, для соблюдения чистоты всего святилища и для исполнения всякой службы при доме Божием,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19254, 'rst66', '1ch', 23, 29, 'для наблюдения за хлебами предложения и пшеничною мукою для хлебного приношения и пресными лепешками, за печеным, жареным и за всякою мерою и весом,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19255, 'rst66', '1ch', 23, 30, 'и чтобы становились каждое утро благодарить и славословить Господа, также и вечером,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19256, 'rst66', '1ch', 23, 31, 'и при всех всесожжениях, возносимых Господу в субботы, в новомесячия и в праздники по числу, как предписано о них, &mdash; постоянно пред лицом Господа,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19257, 'rst66', '1ch', 23, 32, 'и чтобы охраняли скинию откровения и святилище и сынов Аароновых, братьев своих, при службах дому Господню.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19258, 'rst66', '1ch', 24, 1, 'И вот распределения сыновей Аароновых: сыновья Аарона: Надав, Авиуд, Елеазар и Ифамар.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19259, 'rst66', '1ch', 24, 2, 'Надав и Авиуд умерли прежде отца своего, сыновей же не было у них, и потому священствовали Елеазар и Ифамар.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19260, 'rst66', '1ch', 24, 3, 'И распределил их Давид &mdash; Садока из сыновей Елеазара, и Ахимелеха из сыновей Ифамара, поочередно на службу их.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19261, 'rst66', '1ch', 24, 4, 'И нашлось, что между сынами Елеазара глав поколений более, нежели между сынами Ифамара. И он распределил их <i>так:</i> из сынов Елеазара шестнадцать глав семейств, а из сынов Ифамара восемь.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19262, 'rst66', '1ch', 24, 5, 'Распределял же их по жребиям, потому что главными во святилище и главными пред Богом были из сынов Елеазара и из сынов Ифамара,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19263, 'rst66', '1ch', 24, 6, 'и записывал их Шемаия, сын Нафанаила, писец из левитов, пред лицом царя и князей и пред священником Садоком и Ахимелехом, сыном Авиафара, и пред главами семейств священнических и левитских: брали <i>при бросании жребия</i> одно семейство из <i>рода</i> Елеазарова, потом брали из <i>рода</i> Ифамарова.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19264, 'rst66', '1ch', 24, 7, 'И вышел первый жребий Иегоиариву, второй Иедаии,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19265, 'rst66', '1ch', 24, 8, 'третий Хариму, четвертый Сеориму,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19266, 'rst66', '1ch', 24, 9, 'пятый Малхию, шестой Миямину,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19267, 'rst66', '1ch', 24, 10, 'седьмой Гаккоцу, восьмой Авии,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19268, 'rst66', '1ch', 24, 11, 'девятый Иешую, десятый Шехании,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19269, 'rst66', '1ch', 24, 12, 'одиннадцатый Елиашиву, двенадцатый Иакиму,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19270, 'rst66', '1ch', 24, 13, 'тринадцатый Хушаю, четырнадцатый Иешеваву,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19271, 'rst66', '1ch', 24, 14, 'пятнадцатый Вилге, шестнадцатый Имеру,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19272, 'rst66', '1ch', 24, 15, 'семнадцатый Хезиру, восемнадцатый Гапицецу,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19273, 'rst66', '1ch', 24, 16, 'девятнадцатый Петахии, двадцатый Иезекиилю,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19274, 'rst66', '1ch', 24, 17, 'двадцать первый Иахину, двадцать второй Гамулу,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19275, 'rst66', '1ch', 24, 18, 'двадцать третий Делаии, двадцать четвертый Маазии.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19276, 'rst66', '1ch', 24, 19, 'Вот порядок их при служении их, как <i>им</i> приходить в дом Господень, по уставу их через Аарона, отца их, как заповедал ему Господь, Бог Израилев.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19277, 'rst66', '1ch', 24, 20, 'У прочих сыновей Левия &mdash; <i>распределение:</i> из сынов Амрама: Шуваил; из сынов Шуваила: Иедия;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19278, 'rst66', '1ch', 24, 21, 'от Рехавии: из сынов Рехавии Ишшия был первый;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19279, 'rst66', '1ch', 24, 22, 'от Ицгара: Шеломоф; из сыновей Шеломофа: Иахав;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19280, 'rst66', '1ch', 24, 23, 'из сыновей <i>Хеврона:</i> первый Иерия, второй Амария, третий Иахазиил, четвертый Иекамам.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19281, 'rst66', '1ch', 24, 24, '<i>Из</i> сыновей Озиила: Миха; из сыновей Михи: Шамир.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19282, 'rst66', '1ch', 24, 25, 'Брат Михи Ишшия; из сыновей Ишшии: Захария.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19283, 'rst66', '1ch', 24, 26, 'Сыновья Мерари: Махли и Муши; <i>из</i> сыновей Иаазии: Бено.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19284, 'rst66', '1ch', 24, 27, '<i>Из</i> сыновей Мерари у Иаазии: Бено и Шогам, и Заккур и Иври.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19285, 'rst66', '1ch', 24, 28, 'У Махлия &mdash; Елеазар; у него сыновей не было.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19286, 'rst66', '1ch', 24, 29, 'У Киса: <i>из</i> сыновей Киса: Иерахмиил;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19287, 'rst66', '1ch', 24, 30, 'сыновья Мушия: Махли, Едер и Иеримоф. Вот сыновья левитов по поколениям их.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19288, 'rst66', '1ch', 24, 31, 'Бросали и они жребий, наравне с братьями своими, сыновьями Аароновыми, пред лицом царя Давида и Садока и Ахимелеха, и глав семейств священнических и левитских: глава семейства наравне с меньшим братом своим.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19289, 'rst66', '1ch', 25, 1, 'И отделил Давид и начальники войска на службу сыновей Асафа, Емана и Идифуна, чтобы они провещавали на цитрах, псалтирях и кимвалах; и были отчислены они на дело служения своего:');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19290, 'rst66', '1ch', 25, 2, 'Из сыновей Асафа: Заккур, Иосиф, Нефания и Ашарела, сыновья Асафа, под руководством Асафа, игравшего по наставлению царя.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19291, 'rst66', '1ch', 25, 3, 'От Идифуна сыновья Идифуна: Гедалия, Цери, Исаия, Семей, Хашавия и Маттафия, шестеро, под руководством отца своего Идифуна, игравшего на цитре во славу и хвалу Господа.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19292, 'rst66', '1ch', 25, 4, 'От Емана сыновья Емана: Буккия, Матфания, Озиил, Шевуил и Иеримоф, Ханания, Ханани, Елиафа, Гиддалти, Ромамти‐Езер, Иошбекаша, Маллофи, Гофир и Махазиоф.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19293, 'rst66', '1ch', 25, 5, 'Все эти сыновья Емана, прозорливца царского, по словам Божиим, чтобы возвышать славу его. И дал Бог Еману четырнадцать сыновей и трех дочерей.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19294, 'rst66', '1ch', 25, 6, 'Все они под руководством отца своего пели в доме Господнем с кимвалами, псалтирями и цитрами в служении в доме Божием, по указанию царя, или Асафа, Идифуна и Емана.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19295, 'rst66', '1ch', 25, 7, 'И было число их с братьями их, обученными петь пред Господом, всех, знающих <i>сие дело</i>, двести восемьдесят восемь.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19296, 'rst66', '1ch', 25, 8, 'И бросили они жребий о череде служения, малый наравне с большим, учители <i>наравне</i> с учениками.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19297, 'rst66', '1ch', 25, 9, 'И вышел первый жребий Асафу, для Иосифа; второй Гедалии с братьями его и сыновьями его; их было двенадцать;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19298, 'rst66', '1ch', 25, 10, 'третий Заккуру с сыновьями его и братьями его; их &mdash; двенадцать;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19299, 'rst66', '1ch', 25, 11, 'четвертый Ицрию с сыновьями его и братьями его; их &mdash; двенадцать;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19300, 'rst66', '1ch', 25, 12, 'пятый Нефании с сыновьями его и братьями его; их &mdash; двенадцать;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19301, 'rst66', '1ch', 25, 13, 'шестой Буккии с сыновьями его и братьями его; их &mdash; двенадцать;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19302, 'rst66', '1ch', 25, 14, 'седьмой Иесареле с сыновьями его и братьями его; их &mdash; двенадцать;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19303, 'rst66', '1ch', 25, 15, 'восьмой Исаии с сыновьями его и братьями его; их &mdash; двенадцать;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19304, 'rst66', '1ch', 25, 16, 'девятый Матфании с сыновьями его и братьями его; их &mdash; двенадцать;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19305, 'rst66', '1ch', 25, 17, 'десятый Шимею с сыновьями его и братьями его; их &mdash; двенадцать;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19306, 'rst66', '1ch', 25, 18, 'одиннадцатый Азариилу с сыновьями его и братьями его; их &mdash; двенадцать;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19307, 'rst66', '1ch', 25, 19, 'двенадцатый Хашавии с сыновьями его и братьями его; их &mdash; двенадцать;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19308, 'rst66', '1ch', 25, 20, 'тринадцатый Шуваилу с сыновьями его и братьями его; их &mdash; двенадцать;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19309, 'rst66', '1ch', 25, 21, 'четырнадцатый Маттафии с сыновьями его и братьями его; их &mdash; двенадцать;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19310, 'rst66', '1ch', 25, 22, 'пятнадцатый Иеримофу с сыновьями его и братьями его; их &mdash; двенадцать;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19311, 'rst66', '1ch', 25, 23, 'шестнадцатый Ханании с сыновьями его и братьями его; их &mdash; двенадцать;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19312, 'rst66', '1ch', 25, 24, 'семнадцатый Иошбекаше с сыновьями его и братьями его; их &mdash; двенадцать;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19313, 'rst66', '1ch', 25, 25, 'восемнадцатый Ханани с сыновьями его и братьями его; их &mdash; двенадцать;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19314, 'rst66', '1ch', 25, 26, 'девятнадцатый Маллофию с сыновьями его и братьями его; их &mdash; двенадцать;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19315, 'rst66', '1ch', 25, 27, 'двадцатый Елиафе с сыновьями его и братьями его; их &mdash; двенадцать;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19316, 'rst66', '1ch', 25, 28, 'двадцать первый Гофиру с сыновьями его и братьями его; их &mdash; двенадцать;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19317, 'rst66', '1ch', 25, 29, 'двадцать второй Гиддалтию с сыновьями его и братьями его; их &mdash; двенадцать;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19318, 'rst66', '1ch', 25, 30, 'двадцать третий Махазиофу с сыновьями его и братьями его; их &mdash; двенадцать;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19319, 'rst66', '1ch', 25, 31, 'двадцать четвертый Ромамти‐Езеру с сыновьями его и братьями его; их &mdash; двенадцать.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19320, 'rst66', '1ch', 26, 1, 'Вот распределение привратников: из Кореян: Мешелемия, сын Корея, из сыновей Асафовых.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19321, 'rst66', '1ch', 26, 2, 'Сыновья Мешелемии: первенец Захария, второй Иедиаил, третий Зевадия, четвертый Иафниил,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19322, 'rst66', '1ch', 26, 3, 'пятый Елам, шестой Иегоханан, седьмой Елиегоэнай.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19323, 'rst66', '1ch', 26, 4, 'Сыновья Овед‐Едома: первенец Шемаия, второй Иегозавад, третий Иоах, четвертый Сахар, пятый Нафанаил,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19324, 'rst66', '1ch', 26, 5, 'шестой Аммиил, седьмой Иссахар, восьмой Пеульфай, потому что Бог благословил его.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19325, 'rst66', '1ch', 26, 6, 'У сына его Шемаии родились также сыновья, начальствовавшие в своем роде, потому что они были люди сильные.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19326, 'rst66', '1ch', 26, 7, 'Сыновья Шемаии: Офни, Рефаил, Овед и Елзавад, братья его, люди сильные, Елия и Семахия.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19327, 'rst66', '1ch', 26, 8, 'Все они из сыновей Овед‐Едома; они и сыновья их, и братья их были люди прилежные и к службе способные: их было у Овед‐Едома шестьдесят два.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19328, 'rst66', '1ch', 26, 9, 'У Мешелемии сыновей и братьев, людей способных, <i>было</i> восемнадцать.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19329, 'rst66', '1ch', 26, 10, 'У Хосы, из сыновей Мерариных, сыновья: Шимри главный, &mdash; хотя он не был первенцем, но отец его поставил его главным;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19330, 'rst66', '1ch', 26, 11, 'второй Хелкия, третий Тевалия, четвертый Захария; всех сыновей и братьев у Хосы было тринадцать.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19331, 'rst66', '1ch', 26, 12, 'Вот распределение привратников по главам семейств, способных на службу вместе с братьями их, для служения в доме Господнем.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19332, 'rst66', '1ch', 26, 13, 'И бросили они жребии, как малый, так и большой, по своим семействам, на каждые ворота.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19333, 'rst66', '1ch', 26, 14, 'И выпал жребий на восток Шелемии; и Захарии, сыну его, умному советнику, бросили жребий, и вышел ему жребий на север;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19334, 'rst66', '1ch', 26, 15, 'Овед‐Едому на юг, а сыновьям его при кладовых.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19335, 'rst66', '1ch', 26, 16, 'Шупиму и Хосе на запад, у ворот Шаллехет, где дорога поднимается и где стража против стражи.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19336, 'rst66', '1ch', 26, 17, 'К востоку по шести левитов, к северу по четыре, к югу по четыре, а у кладовых по два.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19337, 'rst66', '1ch', 26, 18, 'К западу у притвора на дороге по четыре, а у самого притвора по два.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19338, 'rst66', '1ch', 26, 19, 'Вот распределение привратников из сыновей Кореевых и сыновей Мерариных.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19339, 'rst66', '1ch', 26, 20, 'Левиты же, братья их, <i>смотрели</i> за сокровищами дома Божия и за сокровищницами посвященных вещей.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19340, 'rst66', '1ch', 26, 21, 'Сыновья Лаедана, сына Герсонова &mdash; от Лаедана, главы семейств от Лаедана Герсонского: Иехиел.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19341, 'rst66', '1ch', 26, 22, 'Сыновья Иехиела: Зефам и Иоиль, брат его, <i>смотрели</i> за сокровищами дома Господня,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19342, 'rst66', '1ch', 26, 23, 'вместе с потомками Амрама, Ицгара, Хеврона, Озиила.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19343, 'rst66', '1ch', 26, 24, 'Шевуил, сын Гирсона, сына Моисеева, <i>был</i> главным смотрителем за сокровищницами.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19344, 'rst66', '1ch', 26, 25, 'У брата его Елиезера сын Рехавия, у него сын Исаия, у него сын Иорам, у него сын Зихрий, у него сын Шеломиф.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19345, 'rst66', '1ch', 26, 26, 'Шеломиф и братья его <i>смотрели</i> за всеми сокровищницами посвященных вещей, которые посвятил царь Давид и главы семейств и тысяченачальники, стоначальники и предводители войска.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19346, 'rst66', '1ch', 26, 27, 'Из завоеваний и из добыч они посвящали на поддержание дома Господня.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19347, 'rst66', '1ch', 26, 28, 'И все, что посвятил Самуил пророк, и Саул, сын Киса, и Авенир, сын Нира, и Иоав, сын Саруи, все посвященное <i>было</i> на руках у Шеломифа и братьев его.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19348, 'rst66', '1ch', 26, 29, 'Из племени Ицгарова: Хенания и сыновья его <i>определены</i> на внешнее служение у Израильтян, писцами и судьями.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19349, 'rst66', '1ch', 26, 30, 'Из племени Хевронова: Хашавия и братья его, люди мужественные, тысяча семьсот, имели надзор над Израилем по эту сторону Иордана к западу, по всяким делам <i>служения</i> Господня и по службе царской.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19350, 'rst66', '1ch', 26, 31, 'У племени Хевронова Иерия <i>был</i> главою Хевронян, в их родах, в поколениях. В сороковой год царствования Давида они исчислены, и найдены между ними люди мужественные в Иазере Галаадском.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19351, 'rst66', '1ch', 26, 32, 'И братья его, люди способные, две тысячи семьсот, были главы семейств. Их поставил царь Давид над коленом Рувимовым и Гадовым и полуколеном Манассииным, по всем делам Божиим и делам царя.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19352, 'rst66', '1ch', 27, 1, 'Вот сыны Израилевы по числу их, главы семейств, тысяченачальники и стоначальники и управители, которые по отделениям служили царю во всех делах, приходя и отходя каждый месяц, во все месяцы года. В каждом отделении было их по двадцать четыре тысячи.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19353, 'rst66', '1ch', 27, 2, 'Над первым отделением, для первого месяца, <i>начальствовал</i> Иашовам, сын Завдиила; в его отделении было двадцать четыре тысячи;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19354, 'rst66', '1ch', 27, 3, 'он был из сынов Фареса, главный над всеми военачальниками в первый месяц.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19355, 'rst66', '1ch', 27, 4, 'Над отделением второго месяца был Додай Ахохиянин; в отделении его был и князь Миклоф, и в его отделении было двадцать четыре тысячи.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19356, 'rst66', '1ch', 27, 5, 'Третий главный военачальник, для третьего месяца, Ванея, сын Иодая, священника, и в его отделении было двадцать четыре тысячи:');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19357, 'rst66', '1ch', 27, 6, 'этот Ванея &mdash; <i>один</i> из тридцати храбрых и <i>начальник</i> над ними, и в его отделе <i>находился</i> Аммизавад, сын его.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19358, 'rst66', '1ch', 27, 7, 'Четвертый, для четвертого месяца, был Асаил, брат Иоава, и по нем Завадия, сын его, и в его отделении двадцать четыре тысячи.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19359, 'rst66', '1ch', 27, 8, 'Пятый, для пятого месяца, князь Шамгуф Израхитянин, и в его отделении двадцать четыре тысячи.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19360, 'rst66', '1ch', 27, 9, 'Шестой, для шестого месяца, Ира, сын Иккеша, Фекоянин, и в его отделении двадцать четыре тысячи.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19361, 'rst66', '1ch', 27, 10, 'Седьмой, для седьмого месяца, Хелец Пелонитянин, из сынов Ефремовых, и в его отделении двадцать четыре тысячи.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19362, 'rst66', '1ch', 27, 11, 'Восьмой, для восьмого месяца, Совохай Хушатянин, из племени Зары, и в его отделении двадцать четыре тысячи.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19363, 'rst66', '1ch', 27, 12, 'Девятый, для девятого месяца, Авиезер Анафофянин, из сыновей Вениаминовых, и в его отделении двадцать четыре тысячи.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19364, 'rst66', '1ch', 27, 13, 'Десятый, для десятого месяца, Магарай Нетофафянин, из племени Зары, и в его отделении двадцать четыре тысячи.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19365, 'rst66', '1ch', 27, 14, 'Одиннадцатый, для одиннадцатого месяца, Ванея Пирафонянин, из сынов Ефремовых, и в его отделении двадцать четыре тысячи.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19366, 'rst66', '1ch', 27, 15, 'Двенадцатый, для двенадцатого месяца, Хелдай Нетофафянин, из потомков Гофониила, и в его отделении двадцать четыре тысячи.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19367, 'rst66', '1ch', 27, 16, 'А над коленами Израилевыми, &mdash; у Рувимлян главным начальником <i>был</i> Елиезер, сын Зихри; у Симеона &mdash; Сафатия, сын Маахи;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19368, 'rst66', '1ch', 27, 17, 'у Левия &mdash; Хашавия, сын Кемуила; у Аарона &mdash; Садок;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19369, 'rst66', '1ch', 27, 18, 'у Иуды &mdash; Елиав, из братьев Давида; у Иссахара &mdash; Омри, сын Михаила;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19370, 'rst66', '1ch', 27, 19, 'у Завулона &mdash; Ишмаия, сын Овадии; у Неффалима &mdash; Иеримоф, сын Азриила;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19371, 'rst66', '1ch', 27, 20, 'у сыновей Ефремовых &mdash; Осия, сын Азазии; у полуколена Манассиина &mdash; Иоиль, сын Федаии;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19372, 'rst66', '1ch', 27, 21, 'у полуколена Манассии в Галааде &mdash; Иддо, сын Захарии; у Вениамина &mdash; Иаасиил, сын Авенира;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19373, 'rst66', '1ch', 27, 22, 'у Дана &mdash; Азариил, сын Иерохама. Вот вожди колен Израилевых.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19374, 'rst66', '1ch', 27, 23, 'Давид не делал счисления тех, которые были от двадцати лет и ниже, потому что Господь сказал, что Он умножит Израиля, как звезды небесные.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19375, 'rst66', '1ch', 27, 24, 'Иоав, сын Саруи, начал делать счисление, но не кончил. И был за это гнев Божий на Израиля, и не вошло то счисление в летопись царя Давида.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19376, 'rst66', '1ch', 27, 25, 'Над сокровищами царскими был Азмавеф, сын Адиилов, а над запасами в поле, в городах, и в селах и в башнях &mdash; Ионафан, сын Уззии;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19377, 'rst66', '1ch', 27, 26, 'над занимающимися полевыми работами, земледелием &mdash; Езрий, сын Хелува;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19378, 'rst66', '1ch', 27, 27, 'над виноградниками &mdash; Шимей из Рамы, а над запасами вина в виноградниках &mdash; Завдий из Шефама;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19379, 'rst66', '1ch', 27, 28, 'над маслинами и смоковницами в долине &mdash; Баал‐Ханан Гедеритянин, а над запасами деревянного масла &mdash; Иоас;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19380, 'rst66', '1ch', 27, 29, 'над крупным скотом, пасущимся в Шароне, &mdash; Шитрай Шаронянин, а над скотом в долинах &mdash; Шафат, сын Адлая;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19381, 'rst66', '1ch', 27, 30, 'над верблюдами &mdash; Овил Исмаильтянин; над ослицами &mdash; Иехдия Меронифянин;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19382, 'rst66', '1ch', 27, 31, 'над мелким скотом &mdash; Иазиз Агаритянин. Все эти были начальниками над имением, которое <i>было</i> у царя Давида.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19383, 'rst66', '1ch', 27, 32, 'Ионафан, дядя Давидов, <i>был</i> советником, человек умный и писец; Иехиил, сын Хахмониев, <i>был</i> при сыновьях царя;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19384, 'rst66', '1ch', 27, 33, 'Ахитофел <i>был</i> советником царя; Хусий Архитянин &mdash; другом царя;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19385, 'rst66', '1ch', 27, 34, 'после же Ахитофела Иодай, сын Ванеи, и Авиафар, а Иоав был военачальником у царя.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19386, 'rst66', '1ch', 28, 1, 'И собрал Давид в Иерусалим всех вождей Израильских, начальников колен и начальников отделов, служивших царю, и тысяченачальников, и стоначальников, и заведовавших всем имением и стадами царя и сыновей его с евнухами, военачальников и всех храбрых мужей.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19387, 'rst66', '1ch', 28, 2, 'И стал Давид царь на ноги свои и сказал: послушайте меня, братья мои и народ мой! <i>было</i> у меня на сердце построить дом покоя для ковчега завета Господня и в подножие ногам Бога нашего, и <i>потребное</i> для строения я приготовил.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19388, 'rst66', '1ch', 28, 3, 'Но Бог сказал мне: «не строй дома имени Моему, потому что ты человек воинственный и проливал кровь».');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19389, 'rst66', '1ch', 28, 4, 'Однако же избрал Господь, Бог Израилев, меня из всего дома отца моего, чтоб быть <i>мне</i> царем над Израилем вечно, потому что Иуду избрал Он князем, а в доме Иуды дом отца моего, а из сыновей отца моего меня благоволил поставить царем над всем Израилем,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19390, 'rst66', '1ch', 28, 5, 'из всех же сыновей моих, &mdash; ибо много сыновей дал мне Господь, &mdash; Он избрал Соломона, сына моего, сидеть на престоле царства Господня над Израилем,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19391, 'rst66', '1ch', 28, 6, 'И сказал мне: «Соломон, сын твой, построит дом Мой и дворы Мои, потому что Я избрал его Себе в сына, и Я буду ему Отцом;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19392, 'rst66', '1ch', 28, 7, 'и утвержу царство его навеки, если он будет тверд в исполнении заповедей Моих и уставов Моих, как до сего дня».');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19393, 'rst66', '1ch', 28, 8, 'И теперь пред очами всего Израиля, собрания Господня, и во уши Бога нашего <i>говорю:</i> соблюдайте и держитесь всех заповедей Господа, Бога вашего, чтобы владеть вам сею доброю землею и оставить ее после себя в наследство детям своим навек;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19394, 'rst66', '1ch', 28, 9, 'И ты, Соломон, сын мой, знай Бога отца твоего и служи Ему от всего сердца и от всей души, ибо Господь испытует все сердца и знает все движения мыслей. Если будешь искать Его, то найдешь Его, а если оставишь Его, Он оставит тебя навсегда.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19395, 'rst66', '1ch', 28, 10, 'Смотри же, когда Господь избрал тебя построить дом для святилища, будь тверд и делай.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19396, 'rst66', '1ch', 28, 11, 'И отдал Давид Соломону, сыну своему, чертеж притвора и домов его, и кладовых его, и горниц его, и внутренних покоев его, и дома для ковчега,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19397, 'rst66', '1ch', 28, 12, 'и чертеж всего, что было у него на душе, дворов дома Господня и всех комнат кругом, сокровищниц дома Божия и сокровищниц вещей посвященных,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19398, 'rst66', '1ch', 28, 13, 'и священнических и левитских отделений, и всякого служебного дела в доме Господнем, и всех служебных сосудов дома Господня,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19399, 'rst66', '1ch', 28, 14, 'золотых вещей, с <i>означением</i> веса, для всякого из служебных сосудов, всех вещей серебряных, с <i>означением</i> веса, для всякого из сосудов служебных.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19400, 'rst66', '1ch', 28, 15, 'И дал золота для светильников и золотых лампад их, с означением веса каждого из светильников и лампад его, также светильников серебряных, с означением веса каждого из светильников и лампад его, смотря по служебному назначению каждого светильника;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19401, 'rst66', '1ch', 28, 16, 'и золота для столов предложения хлебов, для каждого <i>золотого</i> стола, и серебра для столов серебряных,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19402, 'rst66', '1ch', 28, 17, 'и вилок, и чаш и кропильниц из чистого золота, и золотых блюд, с означением веса каждого блюда, и серебряных блюд, с означением веса каждого блюда,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19403, 'rst66', '1ch', 28, 18, 'и для жертвенника курения из литого золота, с означением веса, и устройства колесницы с золотыми херувимами, распростирающими <i>крылья</i> и покрывающими ковчег завета Господня.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19404, 'rst66', '1ch', 28, 19, 'Все сие в письмени от Господа, <i>говорил Давид, как</i> Он вразумил меня на все дела постройки.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19405, 'rst66', '1ch', 28, 20, 'И сказал Давид сыну своему Соломону: будь тверд и мужествен, и приступай к делу, не бойся и не ужасайся, ибо Господь Бог, Бог мой, с тобою; Он не отступит от тебя и не оставит тебя, доколе не совершишь всего дела, требуемого для дома Господня.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19406, 'rst66', '1ch', 28, 21, 'И вот отделы священников и левитов, для всякой службы при доме Божием. И у тебя есть для всякого дела усердные люди, искусные для всякой работы, и начальники и весь народ <i>готовы</i> на все твои приказания.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19407, 'rst66', '1ch', 29, 1, 'И сказал царь Давид всему собранию: Соломон, сын мой, которого одного избрал Бог, молод и малосилен, а дело сие велико, потому что не для человека здание сие, а для Господа Бога.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19408, 'rst66', '1ch', 29, 2, 'Всеми силами я заготовил для дома Бога моего золото для золотых вещей и серебро для серебряных, и медь для медных, железо для железных, и дерева для деревянных, камни оникса и <i>камни</i> вставные, камни красивые и разноцветные, и всякие дорогие камни, и множество мрамора;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19409, 'rst66', '1ch', 29, 3, 'и еще по любви моей к дому Бога моего, есть у меня сокровище собственное из золота и серебра, <i>и его</i> я отдаю для дома Бога моего, сверх всего, что заготовил я для святого дома:');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19410, 'rst66', '1ch', 29, 4, 'три тысячи талантов золота, золота Офирского, и семь тысяч талантов серебра чистого, для обложения стен в домах,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19411, 'rst66', '1ch', 29, 5, 'для каждой из золотых вещей, и для каждой из серебряных, и для всякого изделия рук художнических. Не поусердствует ли <i>еще</i> кто жертвовать сегодня для Господа?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19412, 'rst66', '1ch', 29, 6, 'И стали жертвовать начальники семейств и начальники колен Израилевых, и начальники тысяч и сотен, и начальники над имениями царя.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19413, 'rst66', '1ch', 29, 7, 'И дали на устроение дома Божия пять тысяч талантов и десять тысяч драхм золота, и серебра десять тысяч талантов, и меди восемнадцать тысяч талантов, и железа сто тысяч талантов.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19414, 'rst66', '1ch', 29, 8, 'И у кого нашлись <i>дорогие</i> камни, те отдавали и их в сокровищницу дома Господня, на руки Иехиилу Герсонитянину.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19415, 'rst66', '1ch', 29, 9, 'И радовался народ усердию их, потому что они от всего сердца жертвовали Господу, также и царь Давид весьма радовался.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19416, 'rst66', '1ch', 29, 10, 'И благословил Давид Господа пред всем собранием, и сказал Давид: благословен Ты, Господи, Боже Израиля, отца нашего, от века и до века!');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19417, 'rst66', '1ch', 29, 11, 'Твое, Господи, величие, и могущество, и слава, и победа, и великолепие, и все, <i>что</i> на небе и на земле, <i>Твое:</i> Твое, Господи, царство, и Ты превыше всего, как Владычествующий.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19418, 'rst66', '1ch', 29, 12, 'И богатство и слава от лица Твоего, и Ты владычествуешь над всем, и в руке Твоей сила и могущество, и во власти Твоей возвеличить и укрепить все.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19419, 'rst66', '1ch', 29, 13, 'И ныне, Боже наш, мы славословим Тебя и хвалим величественное имя Твое.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19420, 'rst66', '1ch', 29, 14, 'Ибо кто я и кто народ мой, что мы имели возможность так жертвовать? Но от Тебя все, и от руки Твоей <i>полученное</i> мы отдали Тебе,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19421, 'rst66', '1ch', 29, 15, 'потому что странники мы пред Тобою и пришельцы, как и все отцы наши, как тень дни наши на земле, и нет ничего прочного.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19422, 'rst66', '1ch', 29, 16, 'Господи, Боже наш! все это множество, которое приготовили мы для построения дома Тебе, святому имени Твоему, от руки Твоей оно, и все Твое.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19423, 'rst66', '1ch', 29, 17, 'Знаю, Боже мой, что Ты испытуешь сердце и любишь чистосердечие; я от чистого сердца моего пожертвовал все сие, и ныне вижу, что и народ Твой, здесь находящийся, с радостью жертвует Тебе.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19424, 'rst66', '1ch', 29, 18, 'Господи, Боже Авраама, Исаака и Израиля, отцов наших! сохрани сие навек, <i>сие</i> расположение мыслей сердца народа Твоего, и направь сердце их к Тебе.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19425, 'rst66', '1ch', 29, 19, 'Соломону же, сыну моему, дай сердце правое, чтобы соблюдать заповеди Твои, откровения Твои и уставы Твои, и исполнить все это и построить здание, для которого я сделал приготовление.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19426, 'rst66', '1ch', 29, 20, 'И сказал Давид всему собранию: благословите Господа, Бога нашего. &mdash; И благословило все собрание Господа, Бога отцов своих, и пало, и поклонилось Господу и царю.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19427, 'rst66', '1ch', 29, 21, 'И принесли Господу жертвы, и вознесли всесожжения Господу, на другой после сего день: тысячу тельцов, тысячу овнов, тысячу агнцев с их возлияниями, и множество жертв от всего Израиля.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19428, 'rst66', '1ch', 29, 22, 'И ели и пили пред Господом в тот день, с великою радостью; и в другой раз воцарили Соломона, сына Давидова, и помазали пред Господом в правителя верховного, а Садока во священника.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19429, 'rst66', '1ch', 29, 23, 'И сел Соломон на престоле Господнем, как царь, вместо Давида, отца своего, и был благоуспешен, и весь Израиль повиновался ему.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19430, 'rst66', '1ch', 29, 24, 'И все начальники и сильные, также и все сыновья царя Давида подчинились Соломону царю.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19431, 'rst66', '1ch', 29, 25, 'И возвеличил Господь Соломона пред очами всего Израиля, и даровал ему славу царства, какой не имел прежде его ни один царь у Израиля.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19432, 'rst66', '1ch', 29, 26, 'И Давид, сын Иессеев, царствовал над всем Израилем.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19433, 'rst66', '1ch', 29, 27, 'Времени царствования его над Израилем <i>было</i> сорок лет: в Хевроне царствовал он семь лет, и в Иерусалиме царствовал тридцать три <i>года</i>.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19434, 'rst66', '1ch', 29, 28, 'И умер в доброй старости, насыщенный жизнью, богатством и славою; и воцарился Соломон, сын его, вместо него.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19435, 'rst66', '1ch', 29, 29, 'Дела царя Давида, первые и последние, описаны в записях Самуила провидца и в записях Нафана пророка и в записях Гада прозорливца,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19436, 'rst66', '1ch', 29, 30, 'равно и все царствование его, и мужество его, и происшествия, случившиеся с ним и с Израилем и со всеми земными царствами.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19437, 'rst66', '2ch', 1, 1, 'И утвердился Соломон, сын Давидов, в царстве своем; и Господь, Бог его, <i>был</i> с ним, и вознес его высоко.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19438, 'rst66', '2ch', 1, 2, 'И приказал Соломон <i>собраться</i> всему Израилю: тысяченачальникам и стоначальникам, и судьям, и всем начальствующим во всем Израиле &mdash; главам поколений.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19439, 'rst66', '2ch', 1, 3, 'И пошли Соломон и все собрание с ним на высоту, что в Гаваоне, ибо там была Божия скиния собрания, которую устроил Моисей, раб Господень, в пустыне.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19440, 'rst66', '2ch', 1, 4, 'Ковчег Божий принес Давид из Кириаф‐Иарима на место, которое приготовил для него Давид, устроив для него скинию в Иерусалиме.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19441, 'rst66', '2ch', 1, 5, 'А медный жертвенник, который сделал Веселеил, сын Урия, сына Орова, <i>оставался</i> там, пред скиниею Господнею, и взыскал его Соломон с собранием.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19442, 'rst66', '2ch', 1, 6, 'И там пред лицом Господа, на медном жертвеннике, который пред скиниею собрания, вознес Соломон тысячу всесожжений.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19443, 'rst66', '2ch', 1, 7, 'В ту ночь явился Бог Соломону и сказал ему: проси, что Мне дать тебе.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19444, 'rst66', '2ch', 1, 8, 'И сказал Соломон Богу: Ты сотворил Давиду, отцу моему, великую милость и поставил меня царем вместо него.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19445, 'rst66', '2ch', 1, 9, 'Да исполнится же, Господи Боже, слово Твое к Давиду, отцу моему. Так как Ты воцарил меня над народом многочисленным, как прах земной,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19446, 'rst66', '2ch', 1, 10, 'то ныне дай мне премудрость и знание, чтобы я <i>умел</i> выходить пред народом сим и входить, ибо кто может управлять сим народом Твоим великим?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19447, 'rst66', '2ch', 1, 11, 'И сказал Бог Соломону: за то, что это было на сердце твоем, и ты не просил богатства, имения и славы и души неприятелей твоих, и также не просил ты многих дней, а просил себе премудрости и знания, чтобы управлять народом Моим, над которым Я воцарил тебя,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19448, 'rst66', '2ch', 1, 12, 'премудрость и знание дается тебе, а богатство и имение и славу Я дам тебе такие, подобных которым не бывало у царей прежде тебя и не будет после тебя.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19449, 'rst66', '2ch', 1, 13, 'И пришел Соломон с высоты, что в Гаваоне, от скинии собрания, в Иерусалим и царствовал над Израилем.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19450, 'rst66', '2ch', 1, 14, 'И набрал Соломон колесниц и всадников; и было у него тысяча четыреста колесниц и двенадцать тысяч всадников; и он разместил их в колесничных городах и при царе в Иерусалиме.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19451, 'rst66', '2ch', 1, 15, 'И сделал царь серебро и золото в Иерусалиме равноценным <i>простому</i> камню, а кедры, по множеству их, сделал равноценными сикоморам, которые на низких местах.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19452, 'rst66', '2ch', 1, 16, 'Коней Соломону приводили из Египта и из Кувы; купцы царские из Кувы получали их за деньги.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19453, 'rst66', '2ch', 1, 17, 'Колесница получаема и доставляема была из Египта за шестьсот <i>сиклей</i> серебра, а конь за сто пятьдесят. Таким же образом они руками своими доставляли <i>это</i> всем царям Хеттейским и царям Арамейским.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19454, 'rst66', '2ch', 2, 1, 'И положил Соломон построить дом имени Господню и дом царский для себя.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19455, 'rst66', '2ch', 2, 2, 'И отчислил Соломон семьдесят тысяч носильщиков и восемьдесят тысяч каменосеков в горах, и надзирателей над ними три тысячи шестьсот.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19456, 'rst66', '2ch', 2, 3, 'И послал Соломон к Хираму, царю Тирскому, сказать: как поступал ты с Давидом, отцом моим, и присылал ему кедры на построение дома для его жительства, <i>так поступи и со мною</i>.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19457, 'rst66', '2ch', 2, 4, 'Вот, я строю дом имени Господа, Бога моего, для посвящения Ему, чтобы возжигать пред Ним благовонное курение, представлять постоянно хлебы предложения и <i>возносить там</i> всесожжения утром и вечером в субботы, и в новомесячия, и в праздники Господа, Бога нашего, что навсегда заповедано Израилю.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19458, 'rst66', '2ch', 2, 5, 'И дом, который я строю, велик, потому что велик Бог наш, выше всех богов.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19459, 'rst66', '2ch', 2, 6, 'И достанет ли у кого силы построить Ему дом, когда небо и небеса небес не вмещают Его? И кто я, чтобы мог построить Ему дом? Разве <i>только</i> для курения пред лицом Его.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19460, 'rst66', '2ch', 2, 7, 'Итак, пришли мне человека, умеющего делать <i>изделия</i> из золота, и из серебра, и из меди, и из железа, и из <i>пряжи</i> пурпурового, багряного и яхонтового <i>цвета</i>, и знающего вырезывать резную работу, вместе с художниками, какие есть у меня в Иудее и в Иерусалиме, которых приготовил Давид, отец мой.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19461, 'rst66', '2ch', 2, 8, 'И пришли мне кедровых дерев, и кипарису и певгового дерева с Ливана, ибо я знаю, что рабы твои умеют рубить дерева Ливанские. И вот, рабы мои пойдут с рабами твоими,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19462, 'rst66', '2ch', 2, 9, 'чтобы мне приготовить множество дерев, потому что дом, который я строю, великий и чудный.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19463, 'rst66', '2ch', 2, 10, 'И вот, древосекам, рубящим дерева, рабам твоим, я даю в пищу: пшеницы двадцать тысяч ко&#769;ров, и ячменю двадцать тысяч ко&#769;ров, и вина двадцать тысяч батов, и оливкового масла двадцать тысяч батов.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19464, 'rst66', '2ch', 2, 11, 'И отвечал Хирам, царь Тирский, письмом, которое прислал к Соломону: по любви к народу Своему, Господь поставил тебя царем над ним.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19465, 'rst66', '2ch', 2, 12, 'И <i>еще</i> сказал Хирам: благословен Господь, Бог Израилев, создавший небо и землю, давший царю Давиду сына мудрого, имеющего смысл и разум, который намерен строить дом Господу и дом царский для себя.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19466, 'rst66', '2ch', 2, 13, 'Итак, я посылаю <i>тебе</i> человека умного, имеющего знания, Хирам‐Авия,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19467, 'rst66', '2ch', 2, 14, 'сына <i>одной</i> женщины из дочерей Дановых, &mdash; а отец его Тирянин, &mdash; умеющего делать <i>изделия</i> из золота и из серебра, из меди, из железа, из камней и из дерев, из <i>пряжи</i> пурпурового, яхонтового <i>цвета</i>, и из виссона, и из багряницы, и вырезывать всякую резьбу, и исполнять все, что будет поручено ему вместе с художниками твоими и с художниками господина моего Давида, отца твоего.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19468, 'rst66', '2ch', 2, 15, 'А пшеницу и ячмень, оливковое масло и вино, о которых говорил ты, господин мой, пошли рабам твоим.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19469, 'rst66', '2ch', 2, 16, 'Мы же нарубим дерев с Ливана, сколько нужно тебе, и пригоним их в плотах по морю в Яфу, а ты отвезешь их в Иерусалим.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19470, 'rst66', '2ch', 2, 17, 'И исчислил Соломон всех пришельцев, бывших <i>тогда</i> в земле Израилевой, после исчисления их, сделанного Давидом, отцом его, &mdash; и нашлось их сто пятьдесят три тысячи шестьсот.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19471, 'rst66', '2ch', 2, 18, 'И сделал он из них семьдесят тысяч носильщиков и восемьдесят тысяч каменосеков на горах и три тысячи шестьсот надзирателей, чтобы они побуждали народ к работе.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19472, 'rst66', '2ch', 3, 1, 'И начал Соломон строить дом Господень в Иерусалиме на горе Мориа, которая указана была Давиду, отцу его, на месте, которое приготовил Давид, на гумне Орны Иевусеянина.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19473, 'rst66', '2ch', 3, 2, 'Начал же он строить во второй <i>день</i> второго месяца, в четвертый год царствования своего.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19474, 'rst66', '2ch', 3, 3, 'И вот основание, <i>положенное</i> Соломоном при строении дома Божия: длина <i>его</i> шестьдесят локтей, по прежней мере, а ширина двадцать локтей;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19475, 'rst66', '2ch', 3, 4, 'и притвор, который пред домом, длиною по ширине дома в двадцать локтей, а вышиною во сто двадцать. И обложил его внутри чистым золотом.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19476, 'rst66', '2ch', 3, 5, 'Дом же главный обшил деревом кипарисовым и обложил его лучшим золотом, и выделал на нем пальмы и цепочки.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19477, 'rst66', '2ch', 3, 6, 'И обложил дом дорогими камнями для красоты; золото же <i>было</i> золото Парваимское.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19478, 'rst66', '2ch', 3, 7, 'И покрыл дом, бревна, пороги и стены его, и двери его, золотом, и вырезал на стенах херувимов.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19479, 'rst66', '2ch', 3, 8, 'И сделал Святое Святых: длина его по широте дома в двадцать локтей, и ширина его в двадцать локтей; и покрыл его лучшим золотом на шестьсот талантов.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19480, 'rst66', '2ch', 3, 9, 'В гвоздях весу до пятидесяти сиклей золота. Горницы также покрыл золотом.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19481, 'rst66', '2ch', 3, 10, 'И сделал он во Святом Святых двух херувимов резной работы и покрыл их золотом.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19482, 'rst66', '2ch', 3, 11, 'Крылья херувимов длиною <i>были</i> в двадцать локтей. Одно крыло в пять локтей касалось стены дома, а другое крыло в пять же локтей сходилось с крылом другого херувима;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19483, 'rst66', '2ch', 3, 12, '<i>равно</i> и крыло другого херувима в пять локтей касалось стены дома, а другое крыло в пять локтей сходилось с крылом другого херувима.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19484, 'rst66', '2ch', 3, 13, 'Крылья сих херувимов <i>были</i> распростерты на двадцать локтей; и они стояли на ногах своих, лицами своими к храму.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19485, 'rst66', '2ch', 3, 14, 'И сделал завесу из яхонтовой, пурпуровой и багряной <i>ткани</i> и из виссона и изобразил на ней херувимов.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19486, 'rst66', '2ch', 3, 15, 'И сделал пред храмом два столба, длиною по тридцати пяти локтей, и капитель наверху каждого в пять локтей.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19487, 'rst66', '2ch', 3, 16, 'И сделал цепочки, <i>как</i> во святилище, и положил наверху столбов, и сделал сто гранатовых яблок и положил на цепочки.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19488, 'rst66', '2ch', 3, 17, 'И поставил столбы пред храмом, один по правую сторону, другой по левую, и дал имя правому Иахин, а левому имя Воаз.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19489, 'rst66', '2ch', 4, 1, 'И сделал медный жертвенник: двадцать локтей длина его и двадцать локтей ширина его и десять локтей вышина его.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19490, 'rst66', '2ch', 4, 2, 'И сделал море литое, &mdash; от края его до края его десять локтей, &mdash; все круглое, вышиною в пять локтей; и снурок в тридцать локтей обнимал его кругом;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19491, 'rst66', '2ch', 4, 3, 'и <i>литые</i> подобия волов стояли под ним кругом со всех сторон; на десять локтей окружали море кругом два ряда волов, вылитых одним литьем с ним.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19492, 'rst66', '2ch', 4, 4, 'Стояло оно на двенадцати волах: три глядели к северу и три глядели к западу, и три глядели к югу, и три глядели к востоку, &mdash; и море на них сверху; зады же их были обращены внутрь под него.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19493, 'rst66', '2ch', 4, 5, 'Толщиною оно <i>было</i> в ладонь; и края его, сделанные, как края чаши, <i>походили</i> на распустившуюся лилию. Оно вмещало до трех тысяч батов.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19494, 'rst66', '2ch', 4, 6, 'И сделал десять омывальниц, и поставил пять по правую сторону и пять по левую, чтоб омывать в них, &mdash; приготовляемое ко всесожжению омывали в них; море же &mdash; для священников, чтоб они омывались в нем.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19495, 'rst66', '2ch', 4, 7, 'И сделал десять золотых светильников, как им быть надлежало, и поставил в храме, пять по правую сторону и пять по левую.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19496, 'rst66', '2ch', 4, 8, 'И сделал десять столов, и поставил в храме, пять по правую сторону и пять по левую, и сделал сто золотых чаш.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19497, 'rst66', '2ch', 4, 9, 'И сделал священнический двор и большой двор и двери к двору, и вереи их обложил медью.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19498, 'rst66', '2ch', 4, 10, 'Море поставил на правой стороне, к юго‐востоку.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19499, 'rst66', '2ch', 4, 11, 'И сделал Хирам тазы, и лопатки, и чаши. И кончил Хирам работу, которую производил для царя Соломона в доме Божием:');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19500, 'rst66', '2ch', 4, 12, 'два столба и две опояски венцов наверху столбов, и две сетки для покрытия двух опоясок венцов, которые на главе столбов,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19501, 'rst66', '2ch', 4, 13, 'и четыреста гранатовых яблок на двух сетках, два ряда гранатовых яблок для каждой сетки, для покрытия двух опоясок венцов, которые на столбах.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19502, 'rst66', '2ch', 4, 14, 'И подставы сделал он, и омывальницы сделал на подставах;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19503, 'rst66', '2ch', 4, 15, 'одно море, и двенадцать волов под ним,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19504, 'rst66', '2ch', 4, 16, 'и тазы, и лопатки, и вилки, и весь прибор их сделал Хирам‐Авий царю Соломону для дома Господня из полированной меди.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19505, 'rst66', '2ch', 4, 17, 'В окрестности Иордана выливал их царь, в глинистой земле, между Сокхофом и Цередою.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19506, 'rst66', '2ch', 4, 18, 'И сделал Соломон все вещи сии в великом множестве, так что не знали веса меди.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19507, 'rst66', '2ch', 4, 19, 'Также сделал Соломон все вещи для дома Божия и золотой жертвенник, и столы, на которых хлебы предложения,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19508, 'rst66', '2ch', 4, 20, 'и светильники и лампады их, чтобы возжигать их по уставу пред давиром, из чистого золота;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19509, 'rst66', '2ch', 4, 21, 'и цветы, и лампады, и щипцы из золота, из самого чистого золота,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19510, 'rst66', '2ch', 4, 22, 'и ножи, и кропильницы, и чаши, и лотки из золота самого чистого, и двери храма, &mdash; двери его внутренние во Святое Святых, и двери храма во святилище, &mdash; из золота.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19511, 'rst66', '2ch', 5, 1, 'И окончилась вся работа, которую производил Соломон для дома Господня. И принес Соломон посвященное Давидом, отцом его; и серебро и золото и все вещи отдал в сокровищницы дома Божия.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19512, 'rst66', '2ch', 5, 2, 'Тогда собрал Соломон старейшин Израилевых и всех глав колен, начальников поколений сынов Израилевых, в Иерусалим, для перенесения ковчега завета Господня из города Давидова, то есть <i>с</i> Сиона.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19513, 'rst66', '2ch', 5, 3, 'И собрались к царю все Израильтяне на праздник, в седьмой месяц.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19514, 'rst66', '2ch', 5, 4, 'И пришли все старейшины Израилевы. Левиты взяли ковчег');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19515, 'rst66', '2ch', 5, 5, 'и понесли ковчег и скинию собрания и все вещи священные, которые в скинии, &mdash; понесли их священники и левиты.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19516, 'rst66', '2ch', 5, 6, 'Царь же Соломон и все общество Израилево, собравшееся к нему пред ковчегом, приносили жертвы из овец и волов, которых невозможно исчислить и определить, по причине множества.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19517, 'rst66', '2ch', 5, 7, 'И принесли священники ковчег завета Господня на место его, в давир храма &mdash; во Святое Святых, под крылья херувимов.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19518, 'rst66', '2ch', 5, 8, 'И херувимы распростирали крылья над местом ковчега, и покрывали херувимы ковчег и шесты его сверху.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19519, 'rst66', '2ch', 5, 9, 'И выдвинулись шесты, так что головки шестов ковчега видны были пред давиром, но не выказывались наружу, и они там до сего дня.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19520, 'rst66', '2ch', 5, 10, 'Не было в ковчеге ничего, кроме двух скрижалей, которые положил Моисей на Хориве, когда Господь заключил <i>завет</i> с сынами Израилевыми, по исходе их из Египта.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19521, 'rst66', '2ch', 5, 11, 'Когда священники вышли из святилища, ибо все священники, находившиеся там, освятились без различия отделов;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19522, 'rst66', '2ch', 5, 12, 'и левиты певцы, &mdash; все они, <i>то есть</i> Асаф, Еман, Идифун и сыновья их, и братья их, &mdash; одетые в виссон, с кимвалами и с псалтирями и цитрами стояли на восточной стороне жертвенника, и с ними сто двадцать священников, трубивших трубами,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19523, 'rst66', '2ch', 5, 13, 'и были, как один, трубящие и поющие, издавая один голос к восхвалению и славословию Господа; и когда загремел звук труб и кимвалов и музыкальных орудий, и восхваляли Господа, ибо Он благ, ибо вовек милость Его, тогда дом, дом Господень, наполнило облако,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19524, 'rst66', '2ch', 5, 14, 'и не могли священники стоять на служении по причине облака, потому что слава Господня наполнила дом Божий.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19525, 'rst66', '2ch', 6, 1, 'Тогда сказал Соломон: Господь сказал, что Он благоволит обитать во мгле,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19526, 'rst66', '2ch', 6, 2, 'а я построил дом в жилище Тебе &mdash; место для вечного Твоего пребывания.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19527, 'rst66', '2ch', 6, 3, 'И обратился царь лицом своим и благословил все собрание Израильтян, &mdash; все собрание Израильтян стояло, &mdash;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19528, 'rst66', '2ch', 6, 4, 'и сказал: благословен Господь, Бог Израилев, Который, что сказал устами Своими Давиду, отцу моему, исполнил <i>ныне</i> рукою Своею! Он говорил:');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19529, 'rst66', '2ch', 6, 5, '«С того дня, как Я вывел народ Мой из земли Египетской, Я не избрал города ни в одном из колен Израилевых для построения дома, в котором пребывало бы имя Мое, и не избрал человека, который был бы правителем народа Моего, Израиля,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19530, 'rst66', '2ch', 6, 6, 'но избрал Иерусалим, чтобы там пребывало имя Мое, и избрал Давида, чтоб он был над народом Моим, Израилем».');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19531, 'rst66', '2ch', 6, 7, 'И было на сердце у Давида, отца моего, построить дом имени Господа, Бога Израилева.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19532, 'rst66', '2ch', 6, 8, 'Но Господь сказал Давиду, отцу моему: «у тебя есть на сердце построить храм имени Моему; хорошо, что это на сердце у тебя.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19533, 'rst66', '2ch', 6, 9, 'Однако не ты построишь храм, а сын твой, который произойдет из чресл твоих, &mdash; он построит храм имени Моему».');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19534, 'rst66', '2ch', 6, 10, 'И исполнил Господь слово Свое, которое изрек: я вступил на место Давида, отца моего, и воссел на престоле Израилевом, как сказал Господь, и построил дом имени Господа, Бога Израилева.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19535, 'rst66', '2ch', 6, 11, 'И я поставил там ковчег, в котором завет Господа, заключенный Им с сынами Израилевыми.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19536, 'rst66', '2ch', 6, 12, 'И стал <i>Соломон</i> у жертвенника Господня впереди всего собрания Израильтян, и воздвиг руки свои, &mdash;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19537, 'rst66', '2ch', 6, 13, 'ибо Соломон сделал медный амвон длиною в пять локтей и шириною в пять локтей, а вышиною в три локтя, и поставил его среди двора; и стал на нем, и преклонил колени впереди всего собрания Израильтян, и воздвиг руки свои к небу, &mdash;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19538, 'rst66', '2ch', 6, 14, 'и сказал: Господи, Боже Израилев! Нет Бога, подобного Тебе, ни на небе, ни на земле. Ты хранишь завет и милость к рабам Твоим, ходящим пред Тобою всем сердцем своим:');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19539, 'rst66', '2ch', 6, 15, 'Ты исполнил рабу Твоему Давиду, отцу моему, что Ты говорил ему; что изрек Ты устами Твоими, то в день сей исполнил рукою Твоею.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19540, 'rst66', '2ch', 6, 16, 'И ныне, Господи, Боже Израилев! исполни рабу Твоему Давиду, отцу моему, то, что Ты сказал ему, говоря: «не прекратится у тебя <i>муж,</i> сидящий пред лицом Моим на престоле Израилевом, если только сыновья твои будут наблюдать за путями своими, ходя по закону Моему так, как ты ходил предо Мною».');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19541, 'rst66', '2ch', 6, 17, 'И ныне, Господи, Боже Израилев! да будет верно слово Твое, которое Ты изрек рабу Твоему Давиду.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19542, 'rst66', '2ch', 6, 18, 'Поистине, Богу ли жить с человеками на земле? Если небо и небеса небес не вмещают Тебя, &mdash; тем менее храм сей, который построил я.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19543, 'rst66', '2ch', 6, 19, 'Но призри на молитву раба Твоего и на прошение его, Господи, Боже мой! услышь воззвание и молитву, которою раб Твой молится пред Тобою.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19544, 'rst66', '2ch', 6, 20, 'Да будут очи Твои отверсты на храм сей днем и ночью, на место, где Ты обещал положить имя Твое, чтобы слышать молитву, которою раб Твой будет молиться на месте сем.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19545, 'rst66', '2ch', 6, 21, 'Услышь моления раба Твоего и народа Твоего, Израиля, какими они будут молиться на месте сем; услышь с места обитания Твоего, с небес, услышь и помилуй!');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19546, 'rst66', '2ch', 6, 22, 'Когда кто согрешит против ближнего своего, и потребуют от него клятвы, чтоб он поклялся, и будет совершаться клятва пред жертвенником Твоим в храме сем,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19547, 'rst66', '2ch', 6, 23, 'тогда Ты услышь с неба и соверши суд над рабами Твоими, воздай виновному, возложив поступок его на голову его, и оправдай правого, воздав ему по правде его.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19548, 'rst66', '2ch', 6, 24, 'Когда поражен будет народ Твой, Израиль, неприятелем за то, что согрешил пред Тобою, и они обратятся <i>к Тебе</i>, и исповедают имя Твое, и будут просить и молиться пред Тобою в храме сем,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19549, 'rst66', '2ch', 6, 25, 'тогда Ты услышь с неба, и прости грех народа Твоего, Израиля, и возврати их в землю, которую Ты дал им и отцам их.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19550, 'rst66', '2ch', 6, 26, 'Когда заключится небо и не будет дождя за то, что они согрешили пред Тобою, и будут молиться на месте сем, и исповедают имя Твое, и обратятся от греха своего, потому что Ты смирил их,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19551, 'rst66', '2ch', 6, 27, 'тогда Ты услышь с неба и прости грех рабов Твоих и народа Твоего, Израиля, указав им добрый путь, по которому идти им, и пошли дождь на землю Твою, которую Ты дал народу Твоему в наследие.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19552, 'rst66', '2ch', 6, 28, 'Голод ли будет на земле, будет ли язва моровая, будет ли ветер палящий или ржа, саранча или червь, будут ли теснить его неприятели его на земле владений его, <i>будет ли</i> какое бедствие, какая болезнь,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19553, 'rst66', '2ch', 6, 29, 'всякую молитву, всякое прошение, какое будет от какого‐либо человека или от всего народа Твоего, Израиля, когда они почувствуют каждый бедствие свое и горе свое и прострут руки свои к храму сему,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19554, 'rst66', '2ch', 6, 30, 'Ты услышь с неба &mdash; места обитания Твоего, и прости, и воздай каждому по всем путям его, как Ты знаешь сердце его, &mdash; ибо Ты один знаешь сердце сынов человеческих, &mdash;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19555, 'rst66', '2ch', 6, 31, 'чтобы они боялись Тебя и ходили путями Твоими во все дни, доколе живут на земле, которую Ты дал отцам нашим.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19556, 'rst66', '2ch', 6, 32, 'Даже и иноплеменник, который не от народа Твоего, Израиля, когда он придет из земли далекой ради имени Твоего великого и руки Твоей могущественной и мышцы Твоей простертой, и придет и будет молиться у храма сего,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19557, 'rst66', '2ch', 6, 33, 'Ты услышь с неба, с места обитания Твоего, и сделай все, о чем будет взывать к Тебе иноплеменник, чтобы все народы земли узнали имя Твое, и чтобы боялись Тебя, как народ Твой, Израиль, и знали, что Твоим именем называется дом сей, который построил я.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19558, 'rst66', '2ch', 6, 34, 'Когда выйдет народ Твой на войну против неприятелей своих путем, которым Ты пошлешь его, и будет молиться Тебе, обратившись к городу сему, который избрал Ты, и к храму, который я построил имени Твоему,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19559, 'rst66', '2ch', 6, 35, 'тогда услышь с неба молитву их и прошение их и сделай, что потребно для них.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19560, 'rst66', '2ch', 6, 36, 'Когда они согрешат пред Тобою, &mdash; ибо нет человека, который не согрешил бы, &mdash; и Ты прогневаешься на них, и предашь их врагу, и отведут их пленившие их в землю далекую или близкую,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19561, 'rst66', '2ch', 6, 37, 'и когда они в земле, в которую будут пленены, войдут в себя и обратятся и будут молиться Тебе в земле пленения своего, говоря: «мы согрешили, сделали беззаконие, мы виновны»,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19562, 'rst66', '2ch', 6, 38, 'и обратятся к Тебе всем сердцем своим и всею душою своею в земле пленения своего, куда отведут их в плен, и будут молиться, обратившись к земле своей, которую Ты дал отцам их, и к городу, который избрал Ты, и к храму, который я построил имени Твоему, &mdash;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19563, 'rst66', '2ch', 6, 39, 'тогда услышь с неба, с места обитания Твоего, молитву их и прошение их, и сделай, что потребно для них, и прости народу Твоему, в чем он согрешил пред Тобою.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19564, 'rst66', '2ch', 6, 40, 'Боже мой! да будут очи Твои отверсты и уши Твои внимательны к молитве на месте сем.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19565, 'rst66', '2ch', 6, 41, 'И ныне, Господи Боже, стань на <i>место</i> покоя Твоего, Ты и ковчег могущества Твоего. Священники Твои, Господи Боже, да облекутся во спасение, и преподобные Твои да насладятся благами.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19566, 'rst66', '2ch', 6, 42, 'Господи Боже! не отврати лица помазанника Твоего, помяни милости к Давиду, рабу Твоему.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19567, 'rst66', '2ch', 7, 1, 'Когда окончил Соломон молитву, сошел огонь с неба и поглотил всесожжение и жертвы, и слава Господня наполнила дом.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19568, 'rst66', '2ch', 7, 2, 'И не могли священники войти в дом Господень, потому что слава Господня наполнила дом Господень.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19569, 'rst66', '2ch', 7, 3, 'И все сыны Израилевы, видя, как сошел огонь и слава Господня на дом, пали лицом на землю, на помост, и поклонились, и славословили Господа, ибо Он благ, ибо вовек милость Его.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19570, 'rst66', '2ch', 7, 4, 'Царь же и весь народ стали приносить жертвы пред лицом Господа.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19571, 'rst66', '2ch', 7, 5, 'И принес царь Соломон в жертву двадцать две тысячи волов и сто двадцать тысяч овец: так освятили дом Божий царь и весь народ.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19572, 'rst66', '2ch', 7, 6, 'Священники стояли в служении своем, и левиты с музыкальными орудиями Господа, которые сделал царь Давид для прославления Господа, ибо вечна милость Его, так как Давид славословил через них; священники же трубили перед ним, и весь Израиль стоял.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19573, 'rst66', '2ch', 7, 7, 'Освятил Соломон и внутреннюю часть двора, которая пред домом Господним: ибо принес там всесожжения и тук мирных жертв, так как жертвенник медный, сделанный Соломоном, не мог вмещать всесожжения и хлебного приношения, и туков.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19574, 'rst66', '2ch', 7, 8, 'И сделал Соломон в то время семидневный праздник, и весь Израиль с ним &mdash; собрание весьма большое, <i>сошедшееся</i> от входа в Емаф до реки Египетской;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19575, 'rst66', '2ch', 7, 9, 'а в день восьмой сделали попразднество, ибо освящение жертвенника совершали семь дней и праздник семь дней.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19576, 'rst66', '2ch', 7, 10, 'И в двадцать третий день седьмого месяца <i>царь</i> отпустил народ в шатры их, радующийся и веселящийся в сердце о благе, какое сделал Господь Давиду и Соломону и Израилю, народу Своему.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19577, 'rst66', '2ch', 7, 11, 'И окончил Соломон дом Господень и дом царский; и все, что предположил Соломон в сердце своем сделать в доме Господнем и в доме своем, совершил он успешно.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19578, 'rst66', '2ch', 7, 12, 'И явился Господь Соломону ночью и сказал ему: Я услышал молитву твою и избрал Себе место сие в дом жертвоприношения.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19579, 'rst66', '2ch', 7, 13, 'Если Я заключу небо и не будет дождя, и если повелю саранче поядать землю, или пошлю моровую язву на народ Мой,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19580, 'rst66', '2ch', 7, 14, 'и смирится народ Мой, который именуется именем Моим, и будут молиться, и взыщут лица Моего, и обратятся от худых путей своих, то Я услышу с неба и прощу грехи их и исцелю землю их.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19581, 'rst66', '2ch', 7, 15, 'Ныне очи Мои будут отверсты и уши Мои внимательны к молитве на месте сем.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19582, 'rst66', '2ch', 7, 16, 'И ныне Я избрал и освятил дом сей, чтобы имя Мое было там вовеки; и очи Мои и сердце Мое будут там во все дни.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19583, 'rst66', '2ch', 7, 17, 'И если ты будешь ходить пред лицом Моим, как ходил Давид, отец твой, и будешь делать все, что Я повелел тебе, и будешь хранить уставы Мои и законы Мои,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19584, 'rst66', '2ch', 7, 18, 'то утвержу престол царства твоего, как Я обещал Давиду, отцу твоему, говоря: «не прекратится у тебя <i>муж,</i> владеющий Израилем».');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19585, 'rst66', '2ch', 7, 19, 'Если же вы отступите и оставите уставы Мои и заповеди Мои, которые Я дал вам, и пойдете и станете служить богам иным и поклоняться им,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19586, 'rst66', '2ch', 7, 20, 'то Я истреблю <i>Израиля</i> с лица земли Моей, которую Я дал им, и храм сей, который Я освятил имени Моему, отвергну от лица Моего и сделаю его притчею и посмешищем у всех народов.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19587, 'rst66', '2ch', 7, 21, 'И о храме сем высоком всякий, проходящий мимо него, ужаснется и скажет: «за что поступил так Господь с землею сею и с храмом сим?»');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19588, 'rst66', '2ch', 7, 22, 'И скажут: «за то, что они оставили Господа, Бога отцов своих, Который вывел их из земли Египетской, и прилепились к богам иным, и поклонялись им, и служили им, &mdash; за то Он навел на них все это бедствие».');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19589, 'rst66', '2ch', 8, 1, 'По окончании двадцати лет, в которые Соломон строил дом Господень и свой дом,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19590, 'rst66', '2ch', 8, 2, 'Соломон обстроил и города, которые дал Соломону Хирам, и поселил в них сынов Израилевых.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19591, 'rst66', '2ch', 8, 3, 'И пошел Соломон на Емаф‐Сува и взял его.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19592, 'rst66', '2ch', 8, 4, 'И построил он Фадмор в пустыне, и все города для запасов, какие основал в Емафе.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19593, 'rst66', '2ch', 8, 5, 'Он обстроил Вефорон верхний и Вефорон нижний, города укрепленные, со стенами, воротами и запорами,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19594, 'rst66', '2ch', 8, 6, 'и Ваалаф и все города для запасов, которые были у Соломона, и все города для колесниц, и города для конных, и все, что хотел Соломон построить в Иерусалиме и на Ливане и во всей земле владения своего.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19595, 'rst66', '2ch', 8, 7, 'Весь народ, оставшийся от Хеттеев, и Аморреев, и Ферезеев, и Евеев, и Иевусеев, которые были не из сынов Израилевых, &mdash;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19596, 'rst66', '2ch', 8, 8, 'детей их, оставшихся после них на земле, которых не истребили сыны Израилевы, &mdash; сделал Соломон оброчными до сего дня.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19597, 'rst66', '2ch', 8, 9, 'Сынов же Израилевых не делал Соломон работниками по делам своим, но они были воинами, и начальниками телохранителей его, и вождями колесниц его и всадников его.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19598, 'rst66', '2ch', 8, 10, 'И было главных приставников у царя Соломона, управлявших народом, двести пятьдесят.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19599, 'rst66', '2ch', 8, 11, 'А дочь фараонову перевел Соломон из города Давидова в дом, который построил для нее, потому что, говорил он, не должна жить женщина у меня в доме Давида, царя Израилева, ибо свят он, так как вошел в него ковчег Господень.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19600, 'rst66', '2ch', 8, 12, 'Тогда стал возносить Соломон всесожжения Господу на жертвеннике Господнем, который он устроил пред притвором,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19601, 'rst66', '2ch', 8, 13, 'чтобы по уставу каждого дня приносить всесожжения, по заповеди Моисеевой, в субботы, и в новомесячия, и в праздники три раза в год: в праздник опресноков, и в праздник седмиц, и в праздник кущей.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19602, 'rst66', '2ch', 8, 14, 'И установил он, по распоряжению Давида, отца своего, череды священников по службе их и левитов по стражам их, чтобы они славословили и служили при священниках по уставу каждого дня, и привратников по чередам их, к каждым воротам, потому что таково было завещание Давида, человека Божия.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19603, 'rst66', '2ch', 8, 15, 'И не отступали от повелений царя о священниках и левитах ни в чем, ни в отношении сокровищ.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19604, 'rst66', '2ch', 8, 16, 'Так устроено было все дело Соломоново от дня основания дома Господня до совершенного окончания его &mdash; дома Господня.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19605, 'rst66', '2ch', 8, 17, 'Тогда пошел Соломон в Ецион‐Гавер и в Елаф, который на берегу моря, в земле Идумейской.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19606, 'rst66', '2ch', 8, 18, 'И прислал ему Хирам чрез слуг своих корабли и рабов, знающих море, и отправились они с слугами Соломоновыми в Офир, и добыли оттуда четыреста пятьдесят талантов золота, и привезли царю Соломону.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19607, 'rst66', '2ch', 9, 1, 'Царица Савская, услышав о славе Соломона, пришла испытать Соломона загадками в Иерусалим, с весьма большим богатством, и с верблюдами, навьюченными благовониями и множеством золота и драгоценных камней. И пришла к Соломону и беседовала с ним обо всем, что было на сердце у нее.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19608, 'rst66', '2ch', 9, 2, 'И объяснил ей Соломон все слова ее, и не нашлось ничего незнакомого Соломону, чего он не объяснил бы ей.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19609, 'rst66', '2ch', 9, 3, 'И увидела царица Савская мудрость Соломона и дом, который он построил,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19610, 'rst66', '2ch', 9, 4, 'и пищу за столом его, и жилище рабов его, и чинность служащих ему и одежду их, и виночерпиев его и одежду их, и ход, которым он ходил в дом Господень, &mdash; и была она вне себя.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19611, 'rst66', '2ch', 9, 5, 'И сказала царю: верно то, что я слышала в земле моей о делах твоих и о мудрости твоей,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19612, 'rst66', '2ch', 9, 6, 'но я не верила словам о них, доколе не пришла и не увидела глазами своими. И вот, мне и вполовину не сказано о множестве мудрости твоей: ты превосходишь молву, какую я слышала.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19613, 'rst66', '2ch', 9, 7, 'Блаженны люди твои, и блаженны сии слуги твои, всегда предстоящие пред тобою и слышащие мудрость твою!');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19614, 'rst66', '2ch', 9, 8, 'Да будет благословен Господь, Бог твой, Который благоволил посадить тебя на престол Свой в царя у Господа, Бога твоего. По любви Бога твоего к Израилю, чтоб утвердить его навеки, Он поставил тебя царем над ним &mdash; творить суд и правду.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19615, 'rst66', '2ch', 9, 9, 'И подарила она царю сто двадцать талантов золота и великое множество благовоний и драгоценных камней; и не бывало таких благовоний, какие подарила царица Савская царю Соломону.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19616, 'rst66', '2ch', 9, 10, 'И слуги Хирамовы и слуги Соломоновы, которые привезли золото из Офира, привезли и красного дерева и драгоценных камней.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19617, 'rst66', '2ch', 9, 11, 'И сделал царь из этого красного дерева лестницы к дому Господню и к дому царскому, и цитры и псалтири для певцов. И не видано было подобного сему прежде в земле Иудейской.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19618, 'rst66', '2ch', 9, 12, 'Царь же Соломон дал царице Савской все, чего она желала и чего она просила, кроме таких вещей, какие она привезла царю. И она отправилась обратно в землю свою, она и слуги ее.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19619, 'rst66', '2ch', 9, 13, 'Весу в золоте, которое приходило к Соломону в один год, <i>было</i> шестьсот шестьдесят шесть талантов золота.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19620, 'rst66', '2ch', 9, 14, 'Сверх того, послы и купцы приносили, и все цари Аравийские и начальники областные приносили золото и серебро Соломону.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19621, 'rst66', '2ch', 9, 15, 'И сделал царь Соломон двести больших щитов из кованого золота, &mdash; по шестисот <i>сиклей</i> кованого золота пошло на каждый щит, &mdash;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19622, 'rst66', '2ch', 9, 16, 'и триста щитов меньших из кованого золота, &mdash; по триста <i>сиклей</i> золота пошло на каждый щит; и поставил их царь в доме из Ливанского дерева.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19623, 'rst66', '2ch', 9, 17, 'И сделал царь большой престол из слоновой кости и обложил его чистым золотом,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19624, 'rst66', '2ch', 9, 18, 'и шесть ступеней к престолу и золотое подножие, к престолу приделанное, и локотники по обе стороны у места сидения, и двух львов, стоящих возле локотников,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19625, 'rst66', '2ch', 9, 19, 'и <i>еще</i> двенадцать львов, стоящих там на шести ступенях, по обе стороны. Не бывало такого <i>престола</i> ни в одном царстве.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19626, 'rst66', '2ch', 9, 20, 'И все сосуды для питья у царя Соломона <i>были</i> из золота, и все сосуды в доме из Ливанского дерева <i>были</i> из золота отборного; серебро во дни Соломона вменялось ни во что,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19627, 'rst66', '2ch', 9, 21, 'ибо корабли царя ходили в Фарсис с слугами Хирама, и в три года раз возвращались корабли из Фарсиса и привозили золото и серебро, слоновую кость и обезьян и павлинов.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19628, 'rst66', '2ch', 9, 22, 'И превзошел царь Соломон всех царей земли богатством и мудростью.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19629, 'rst66', '2ch', 9, 23, 'И все цари земли искали видеть Соломона, чтобы послушать мудрости его, которую вложил Бог в сердце его.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19630, 'rst66', '2ch', 9, 24, 'И каждый из них подносил от себя в дар сосуды серебряные и сосуды золотые и одежды, оружие и благовония, коней и лошаков, из года в год.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19631, 'rst66', '2ch', 9, 25, 'И было у Соломона четыре тысячи стойл для коней и колесниц и двенадцать тысяч всадников; и он разместил их в городах колесничных и при царе &mdash; в Иерусалиме;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19632, 'rst66', '2ch', 9, 26, 'и господствовал он над всеми царями, от реки <i>Евфрата</i> до земли Филистимской и до пределов Египта.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19633, 'rst66', '2ch', 9, 27, 'И сделал царь серебро в Иерусалиме равноценным <i>простому</i> камню, а кедры, по их множеству, сделал равноценными сикоморам, которые на низких местах.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19634, 'rst66', '2ch', 9, 28, 'Коней приводили Соломону из Египта и из всех земель.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19635, 'rst66', '2ch', 9, 29, 'Прочие деяния Соломоновы, первые и последние, описаны в записях Нафана пророка и в пророчестве Ахии Силомлянина и в видениях прозорливца Иоиля о Иеровоаме, сыне Наватовом.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19636, 'rst66', '2ch', 9, 30, 'Царствовал же Соломон в Иерусалиме над всем Израилем сорок лет.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19637, 'rst66', '2ch', 9, 31, 'И почил Соломон с отцами своими, и похоронили его в городе Давида, отца его. И воцарился Ровоам, сын его, вместо него.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19638, 'rst66', '2ch', 10, 1, 'И пошел Ровоам в Сихем, потому что в Сихем сошлись все Израильтяне, чтобы поставить его царем.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19639, 'rst66', '2ch', 10, 2, 'Когда услышал <i>о сем</i> Иеровоам, сын Наватов, &mdash; он находился в Египте, куда убежал от царя Соломона, &mdash; то возвратился Иеровоам из Египта.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19640, 'rst66', '2ch', 10, 3, 'И послали и звали его; и пришел Иеровоам и весь Израиль, и говорили Ровоаму так:');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19641, 'rst66', '2ch', 10, 4, 'отец твой наложил на нас тяжкое иго; но ты облегчи жестокую работу отца твоего и тяжкое иго, которое он наложил на нас, и мы будем служить тебе.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19642, 'rst66', '2ch', 10, 5, 'И сказал им <i>Ровоам:</i> через три дня придите опять ко мне. И разошелся народ.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19643, 'rst66', '2ch', 10, 6, 'И советовался царь Ровоам со старейшинами, которые предстояли пред лицом Соломона, отца его, при жизни его, и говорил: как вы посоветуете отвечать народу сему?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19644, 'rst66', '2ch', 10, 7, 'Они сказали ему: если ты будешь добр к народу сему и угодишь им и будешь говорить с ними ласково, то они будут тебе рабами на все дни.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19645, 'rst66', '2ch', 10, 8, 'Но он оставил совет старейшин, который они давали ему, и стал советоваться с людьми молодыми, которые выросли вместе с ним, предстоящими пред лицом его;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19646, 'rst66', '2ch', 10, 9, 'и сказал им: что вы посоветуете мне отвечать народу сему, говорившему мне так: «облегчи иго, которое наложил на нас отец твой»?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19647, 'rst66', '2ch', 10, 10, 'И говорили ему молодые люди, выросшие вместе с ним, и сказали: так скажи народу, говорившему тебе: «отец твой наложил на нас тяжкое иго, а ты облегчи нас», &mdash; так скажи им: «мизинец мой толще чресл отца моего.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19648, 'rst66', '2ch', 10, 11, 'Отец мой наложил на вас тяжкое иго, а я увеличу иго ваше; отец мой наказывал вас бичами, а я <i>буду бить вас</i> скорпионами».');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19649, 'rst66', '2ch', 10, 12, 'И пришел Иеровоам и весь народ к Ровоаму на третий день, как приказал царь, сказав: придите ко мне опять через три дня.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19650, 'rst66', '2ch', 10, 13, 'Тогда царь отвечал им сурово, ибо оставил царь Ровоам совет старейшин, и говорил им по совету молодых людей так:');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19651, 'rst66', '2ch', 10, 14, 'отец мой наложил на вас тяжкое иго, а я увеличу его; отец мой наказывал вас бичами, а я <i>буду бить вас</i> скорпионами.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19652, 'rst66', '2ch', 10, 15, 'И не послушал царь народа, потому что так устроено было от Бога, чтоб исполнить Господу слово Свое, которое изрек Он чрез Ахию Силомлянина Иеровоаму, сыну Наватову.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19653, 'rst66', '2ch', 10, 16, 'Когда весь Израиль увидел, что не слушает его царь, то отвечал народ царю, говоря: какая нам часть в Давиде? Нет нам доли в сыне Иессеевом; по шатрам своим, Израиль! Теперь знай свой дом, Давид. И разошлись все Израильтяне по шатрам своим.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19654, 'rst66', '2ch', 10, 17, 'Только над сынами Израилевыми, жившими в городах Иудиных, остался царем Ровоам.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19655, 'rst66', '2ch', 10, 18, 'И послал царь Ровоам Адонирама, начальника над собиранием даней, и забросали его сыны Израилевы каменьями, и он умер. Царь же Ровоам поспешил сесть на колесницу, чтобы убежать в Иерусалим.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19656, 'rst66', '2ch', 10, 19, 'Так отложились Израильтяне от дома Давидова до сего дня.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19657, 'rst66', '2ch', 11, 1, 'И прибыл Ровоам в Иерусалим и созвал из дома Иудина и Вениаминова сто восемьдесят тысяч отборных воинов, чтобы воевать с Израилем и возвратить царство Ровоаму.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19658, 'rst66', '2ch', 11, 2, 'И было слово Господне к Самею, человеку Божию, и сказано:');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19659, 'rst66', '2ch', 11, 3, 'скажи Ровоаму, сыну Соломонову, царю Иудейскому, и всему Израилю в <i>колене</i> Иудином и Вениаминовом:');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19660, 'rst66', '2ch', 11, 4, 'так говорит Господь: не ходите и не начинайте войны с братьями вашими; возвратитесь каждый в дом свой, ибо Мною сделано это. Они послушались слов Господних и возвратились из похода против Иеровоама.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19661, 'rst66', '2ch', 11, 5, 'Ровоам жил в Иерусалиме; он обнес города в Иудее стенами.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19662, 'rst66', '2ch', 11, 6, 'Он укрепил Вифлеем и Ефам, и Фекою,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19663, 'rst66', '2ch', 11, 7, 'и Вефцур, и Сохо, и Одоллам,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19664, 'rst66', '2ch', 11, 8, 'и Геф, и Марешу, и Зиф,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19665, 'rst66', '2ch', 11, 9, 'и Адораим, и Лахис, и Азеку,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19666, 'rst66', '2ch', 11, 10, 'и Цору, и Аиалон, и Хеврон, находившиеся в колене Иудином и Вениаминовом.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19667, 'rst66', '2ch', 11, 11, 'И утвердил он крепости сии, и устроил в них начальников и хранилища для хлеба и деревянного масла и вина.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19668, 'rst66', '2ch', 11, 12, 'И <i>дал</i> в каждый город щиты и копья и утвердил их весьма сильно. И оставались за ним Иуда и Вениамин.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19669, 'rst66', '2ch', 11, 13, 'И священники и левиты, какие <i>были</i> по всей земле Израильской, собрались к нему из всех пределов,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19670, 'rst66', '2ch', 11, 14, 'ибо оставили левиты свои городские предместья и свои владения и пришли в Иудею и в Иерусалим, так как отставил их Иеровоам и сыновья его от священства Господня,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19671, 'rst66', '2ch', 11, 15, 'и поставил у себя жрецов к высотам, и к козлам, и к тельцам, которых он сделал.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19672, 'rst66', '2ch', 11, 16, 'А за ними и из всех колен Израилевых расположившие сердце свое, чтобы взыскать Господа, Бога Израилева, приходили в Иерусалим, дабы приносить жертвы Господу, Богу отцов своих.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19673, 'rst66', '2ch', 11, 17, 'И укрепили они царство Иудино и поддерживали Ровоама, сына Соломонова, три года, потому что ходили путем Давида и Соломона в сии три года.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19674, 'rst66', '2ch', 11, 18, 'И взял себе Ровоам в жену Махалафу, дочь Иеромофа, сына Давидова, и Авихаиль, дочь Елиава, сына Иессеева,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19675, 'rst66', '2ch', 11, 19, 'и она родила ему сыновей: Иеуса и Шемарию и Загама.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19676, 'rst66', '2ch', 11, 20, 'После нее он взял Мааху, дочь Авессалома, и она родила ему Авию и Аттая, и Зизу и Шеломифа.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19677, 'rst66', '2ch', 11, 21, 'И любил Ровоам Мааху, дочь Авессалома, более всех жен и наложниц своих, ибо он имел восемнадцать жен и шестьдесят наложниц и родил двадцать восемь сыновей и шестьдесят дочерей.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19678, 'rst66', '2ch', 11, 22, 'И поставил Ровоам Авию, сына Маахи, главою <i>и</i> князем над братьями его, потому что <i>хотел</i> воцарить его.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19679, 'rst66', '2ch', 11, 23, 'И действовал благоразумно, и разослал всех сыновей своих по всем землям Иуды и Вениамина во все укрепленные города, и дал им содержание большое и приискал много жен.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19680, 'rst66', '2ch', 12, 1, 'Когда царство Ровоама утвердилось, и он сделался силен, тогда он оставил закон Господень, и весь Израиль с ним.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19681, 'rst66', '2ch', 12, 2, 'На пятом году царствования Ровоама Сусаким, царь Египетский, пошел на Иерусалим, &mdash; потому что они отступили от Господа &mdash;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19682, 'rst66', '2ch', 12, 3, 'с тысячью и двумястами колесниц и шестьюдесятью тысячами всадников; и не было числа народу, который пришел с ним из Египта, Ливиянам, Сукхитам и Ефиоплянам;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19683, 'rst66', '2ch', 12, 4, 'и взял укрепленные города в Иудее и пришел к Иерусалиму.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19684, 'rst66', '2ch', 12, 5, 'Тогда Самей пророк пришел к Ровоаму и князьям Иудеи, которые собрались в Иерусалим, <i>спасаясь</i> от Сусакима, и сказал им: так говорит Господь: вы оставили Меня, за то и Я оставляю вас в руки Сусакиму.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19685, 'rst66', '2ch', 12, 6, 'И смирились князья Израилевы и царь, и сказали: праведен Господь!');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19686, 'rst66', '2ch', 12, 7, 'Когда увидел Господь, что они смирились, тогда было слово Господне к Самею, и сказано: они смирились; не истреблю их и вскоре дам им избавление, и не прольется гнев Мой на Иерусалим рукою Сусакима;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19687, 'rst66', '2ch', 12, 8, 'однако же они будут слугами его, чтобы знали, каково служить Мне и служить царствам земным.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19688, 'rst66', '2ch', 12, 9, 'И пришел Сусаким, царь Египетский, в Иерусалим и взял сокровища дома Господня и сокровища дома царского; всё взял он, взял и щиты золотые, которые сделал Соломон.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19689, 'rst66', '2ch', 12, 10, 'И сделал царь Ровоам вместо них щиты медные, и отдал их на руки начальникам телохранителей, охранявших вход дома царского.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19690, 'rst66', '2ch', 12, 11, 'Когда выходил царь в дом Господень, приходили телохранители и несли их, и потом опять относили их в палату телохранителей.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19691, 'rst66', '2ch', 12, 12, 'И когда он смирился, тогда отвратился от него гнев Господа и не погубил его до конца; притом и в Иудее было нечто доброе.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19692, 'rst66', '2ch', 12, 13, 'И утвердился царь Ровоам в Иерусалиме и царствовал. Сорок один год было Ровоаму, когда он воцарился, и семнадцать лет царствовал в Иерусалиме, в городе, который из всех колен Израилевых избрал Господь, чтобы там пребывало имя Его. Имя матери его Наама, Аммонитянка.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19693, 'rst66', '2ch', 12, 14, 'И делал он зло, потому что не расположил сердца своего к тому, чтобы взыскать Господа.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19694, 'rst66', '2ch', 12, 15, 'Деяния Ровоамовы, первые и последние, описаны в записях Самея пророка и Адды прозорливца при родословиях. И были войны у Ровоама с Иеровоамом во все дни.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19695, 'rst66', '2ch', 12, 16, 'И почил Ровоам с отцами своими и погребен в городе Давидовом. И воцарился Авия, сын его, вместо него.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19696, 'rst66', '2ch', 13, 1, 'В восемнадцатый год царствования Иеровоама воцарился Авия над Иудою.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19697, 'rst66', '2ch', 13, 2, 'Три года он царствовал в Иерусалиме; имя матери его Михаия, дочь Уриилова, из Гивы. И была война у Авии с Иеровоамом.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19698, 'rst66', '2ch', 13, 3, 'И вывел Авия на войну войско, состоявшее из людей храбрых, из четырехсот тысяч человек отборных; а Иеровоам выступил против него на войну с восемьюстами тысяч человек, <i>также</i> отборных, храбрых.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19699, 'rst66', '2ch', 13, 4, 'И стал Авия на вершине горы Цемараимской, одной из гор Ефремовых, и говорил: послушайте меня, Иеровоам и все Израильтяне!');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19700, 'rst66', '2ch', 13, 5, 'Не знаете ли вы, что Господь, Бог Израилев, дал царство Давиду над Израилем навек, ему и сыновьям его, по завету соли?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19701, 'rst66', '2ch', 13, 6, 'Но восстал Иеровоам, сын Наватов, раб Соломона, сына Давидова, и возмутился против господина своего.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19702, 'rst66', '2ch', 13, 7, 'И собрались вокруг него люди пустые, люди развращенные, и укрепились против Ровоама, сына Соломонова; Ровоам же был молод и слаб сердцем и не устоял против них.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19703, 'rst66', '2ch', 13, 8, 'И ныне вы думаете устоять против царства Господня в руке сынов Давидовых, <i>потому что</i> вас великое множество, и у вас золотые тельцы, которых Иеровоам сделал вам богами.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19704, 'rst66', '2ch', 13, 9, 'Не вы ли изгнали священников Господних, сынов Аарона, и левитов, и поставили у себя священников, какие у народов <i>других</i> земель? Всякий, кто приходит для посвящения своего с тельцом и с семью овнами, делается <i>у вас</i> священником лжебогов.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19705, 'rst66', '2ch', 13, 10, 'А у нас &mdash; Господь, Бог наш; мы не оставляли Его, и Господу служат священники, сыны Аароновы, и левиты при <i>своем</i> деле.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19706, 'rst66', '2ch', 13, 11, 'И сожигают они Господу всесожжения каждое утро и каждый вечер, и благовонное курение, и полагают рядами хлебы на столе чистом, и <i>зажигают</i> золотой светильник и лампады его, чтобы горели каждый вечер, потому что мы соблюдаем установление Господа, Бога нашего, а вы оставили Его.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19707, 'rst66', '2ch', 13, 12, 'И вот, у нас во главе Бог, и священники Его, и трубы громогласные, чтобы греметь против вас. Сыны Израилевы! не воюйте с Господом, Богом отцов ваших, ибо не получите успеха.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19708, 'rst66', '2ch', 13, 13, 'Между тем Иеровоам послал отряд в засаду с тыла им, так что <i>сам он</i> был впереди Иудеев, а засада позади их;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19709, 'rst66', '2ch', 13, 14, 'и оглянулись Иудеи, и вот, им битва спереди и сзади; и возопили они к Господу, а священники затрубили трубами.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19710, 'rst66', '2ch', 13, 15, 'И воскликнули Иудеи. И когда воскликнули Иудеи, Бог поразил Иеровоама и всех Израильтян пред лицом Авии и Иуды.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19711, 'rst66', '2ch', 13, 16, 'И побежали сыны Израилевы от Иудеев, и предал их Бог в руки им.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19712, 'rst66', '2ch', 13, 17, 'И произвели у них Авия и народ его поражение сильное; и пало убитых у Израиля пятьсот тысяч человек отборных.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19713, 'rst66', '2ch', 13, 18, 'И смирились тогда сыны Израилевы, и были сильны сыны Иудины, потому что уповали на Господа, Бога отцов своих.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19714, 'rst66', '2ch', 13, 19, 'И преследовал Авия Иеровоама и взял у него города: Вефиль и зависящие от него города, и Иешану и зависящие от нее города, и Ефрон и зависящие от него города.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19715, 'rst66', '2ch', 13, 20, 'И не входил уже в силу Иеровоам во дни Авии. И поразил его Господь, и он умер.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19716, 'rst66', '2ch', 13, 21, 'Авия же усилился; и взял себе четырнадцать жен и родил двадцать два сына и шестнадцать дочерей.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19717, 'rst66', '2ch', 13, 22, 'Прочие деяния Авии и его поступки и слова описаны в сказании пророка Адды.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19718, 'rst66', '2ch', 14, 1, 'И почил Авия с отцами своими, и похоронили его в городе Давидовом. И воцарился Аса, сын его, вместо него. Во дни его покоилась земля десять лет.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19719, 'rst66', '2ch', 14, 2, 'И делал Аса доброе и угодное в очах Господа, Бога своего:');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19720, 'rst66', '2ch', 14, 3, 'и отверг он жертвенники <i>богов</i> чужих и высоты, и разбил статуи, и вырубил <i>посвященные</i> дерева;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19721, 'rst66', '2ch', 14, 4, 'и повелел Иудеям взыскать Господа, Бога отцов своих, и исполнять закон <i>Его</i> и заповеди;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19722, 'rst66', '2ch', 14, 5, 'и отменил он во всех городах Иудиных высоты и статуи солнца. И спокойно было при нем царство.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19723, 'rst66', '2ch', 14, 6, 'И построил он укрепленные города в Иудее, ибо спокойна была земля, и не было у него войны в те годы, так как Господь дал покой ему.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19724, 'rst66', '2ch', 14, 7, 'И сказал он Иудеям: построим города сии и обнесем их стенами с башнями, с воротами и запорами. Земля еще наша, потому что мы взыскали Господа, Бога нашего: мы взыскали Его, &mdash; и Он дал нам покой со всех сторон. И стали строить, и имели успех.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19725, 'rst66', '2ch', 14, 8, 'И было у Асы военной силы: вооруженных щитом и копьем из <i>колена</i> Иудина триста тысяч, и из <i>колена</i> Вениаминова вооруженных щитом и стрелявших из лука двести восемьдесят тысяч, людей храбрых.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19726, 'rst66', '2ch', 14, 9, 'И вышел на них Зарай Ефиоплянин с войском в тысячу тысяч и с тремястами колесниц и дошел до Мареши.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19727, 'rst66', '2ch', 14, 10, 'И выступил Аса против него, и построились к сражению на долине Цефата у Мареши.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19728, 'rst66', '2ch', 14, 11, 'И воззвал Аса к Господу, Богу своему, и сказал: Господи! не в Твоей ли силе помочь сильному или бессильному? помоги же нам, Господи, Боже наш: ибо мы на Тебя уповаем и во имя Твое вышли мы против множества сего. Господи! Ты Бог наш: да не превозможет Тебя человек.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19729, 'rst66', '2ch', 14, 12, 'И поразил Господь Ефиоплян пред лицом Асы и пред лицом Иуды, и побежали Ефиопляне.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19730, 'rst66', '2ch', 14, 13, 'И преследовал их Аса и народ, бывший с ним, до Герара, и пали Ефиопляне, так что у них никого <i>не осталось</i> в живых, потому что они поражены были пред Господом и пред воинством Его. И набрали добычи великое множество.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19731, 'rst66', '2ch', 14, 14, 'И разрушили все города вокруг Герара, потому что напал на них ужас от Господа; и разграбили все города и вынесли из них весьма много добычи.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19732, 'rst66', '2ch', 14, 15, 'Также и пастушеские шалаши разорили и угнали множество стад мелкого скота и верблюдов и возвратились в Иерусалим.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19733, 'rst66', '2ch', 15, 1, 'Тогда на Азарию, сына Одедова, сошел Дух Божий,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19734, 'rst66', '2ch', 15, 2, 'и вышел он навстречу Асе и сказал ему: послушайте меня, Аса и весь Иуда и Вениамин: Господь с вами, когда вы с Ним; и если будете искать Его, Он будет найден вами; если же оставите Его, Он оставит вас.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19735, 'rst66', '2ch', 15, 3, 'Многие дни Израиль <i>будет</i> без Бога истинного, и без священника учащего, и без закона;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19736, 'rst66', '2ch', 15, 4, 'но когда он обратится в тесноте своей к Господу, Богу Израилеву, и взыщет Его, Он даст им найти Себя.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19737, 'rst66', '2ch', 15, 5, 'В те времена не будет мира ни выходящему, ни входящему, ибо великие волнения будут у всех жителей земель;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19738, 'rst66', '2ch', 15, 6, 'народ будет сражаться с народом, и город с городом, потому что Бог приведет их в смятение всякими бедствиями.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19739, 'rst66', '2ch', 15, 7, 'Но вы укрепитесь, и пусть не ослабевают руки ваши, потому что есть возмездие за дела ваши.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19740, 'rst66', '2ch', 15, 8, 'Когда услышал Аса слова сии и пророчество, <i>сына</i> Одеда пророка, то ободрился и изверг мерзости <i>языческие</i> из всей земли Иудиной и Вениаминовой и из городов, которые он взял на горе Ефремовой, и обновил жертвенник Господень, который пред притвором Господним.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19741, 'rst66', '2ch', 15, 9, 'И собрал всего Иуду и Вениамина и живущих с ними переселенцев от Ефрема и Манассии и Симеона; ибо многие от Израиля перешли к нему, когда увидели, что Господь, Бог его, с ним.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19742, 'rst66', '2ch', 15, 10, 'И собрались в Иерусалим в третий месяц, в пятнадцатый год царствования Асы;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19743, 'rst66', '2ch', 15, 11, 'и принесли в день тот жертву Господу из добычи, которую привели, из крупного скота семьсот и из мелкого семь тысяч;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19744, 'rst66', '2ch', 15, 12, 'и вступили в завет, чтобы взыскать Господа, Бога отцов своих, от всего сердца своего и от всей души своей;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19745, 'rst66', '2ch', 15, 13, 'а всякий, кто не станет искать Господа, Бога Израилева, должен умереть, малый ли он или большой, мужчина ли или женщина.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19746, 'rst66', '2ch', 15, 14, 'И клялись Господу громогласно и с восклицанием и при <i>звуке</i> труб и рогов.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19747, 'rst66', '2ch', 15, 15, 'И радовались все Иудеи сей клятве, потому что от всего сердца своего клялись и со всем усердием взыскали Его, и Он дал им найти Себя. И дал им Господь покой со всех сторон.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19748, 'rst66', '2ch', 15, 16, 'И Мааху, мать свою, царь Аса лишил царского достоинства за то, что она сделала истукан для дубравы. И ниспроверг Аса истукан ее, и изрубил в куски, и сжег на долине Кедрона.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19749, 'rst66', '2ch', 15, 17, 'Хотя высоты не были отменены у Израиля, но сердце Асы было вполне предано Господу во все дни его.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19750, 'rst66', '2ch', 15, 18, 'И внес он посвященное отцом его и свое посвящение в дом Божий, серебро и золото и сосуды.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19751, 'rst66', '2ch', 15, 19, 'И не было войны до тридцать пятого года царствования Асы.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19752, 'rst66', '2ch', 16, 1, 'В тридцать шестой год царствования Асы пошел Вааса, царь Израильский, на Иудею и начал строить Раму, чтобы не позволить <i>никому</i> ни уходить от Асы, царя Иудейского, ни приходить <i>к нему</i>.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19753, 'rst66', '2ch', 16, 2, 'И вынес Аса серебро и золото из сокровищниц дома Господня и дома царского и послал к Венададу, царю Сирийскому, жившему в Дамаске, говоря:');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19754, 'rst66', '2ch', 16, 3, 'союз да будет между мною и тобою, как был между отцом моим и отцом твоим; вот, я посылаю тебе серебра и золота: пойди, расторгни союз твой с Ваасою, царем Израильским, чтоб он отступил от меня.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19755, 'rst66', '2ch', 16, 4, 'И послушался Венадад царя Асы и послал военачальников, которые <i>были</i> у него, против городов Израильских, и они опустошили Ийон и Дан и Авелмаим и все запасы в городах Неффалимовых.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19756, 'rst66', '2ch', 16, 5, 'И когда услышал <i>о сем</i> Вааса, то перестал строить Раму и прекратил работу свою.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19757, 'rst66', '2ch', 16, 6, 'Аса же, царь, собрал всех Иудеев, и они вывезли <i>из</i> Рамы камни и дерева, которые употреблял Вааса для строения, &mdash; и выстроил из них Геву и Мицфу.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19758, 'rst66', '2ch', 16, 7, 'В то время пришел Ананий прозорливец к Асе, царю Иудейскому, и сказал ему: так как ты понадеялся на царя Сирийского и не уповал на Господа, Бога твоего, потому и спаслось войско царя Сирийского от руки твоей.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19759, 'rst66', '2ch', 16, 8, 'Не были ли Ефиопляне и Ливияне с силою большею и с колесницами и всадниками весьма многочисленными? Но как ты уповал на Господа, то Он предал их в руку твою,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19760, 'rst66', '2ch', 16, 9, 'ибо очи Господа обозревают всю землю, чтобы поддерживать тех, <i>чье</i> сердце вполне предано Ему. Безрассудно ты поступил теперь. За то отныне будут у тебя войны.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19761, 'rst66', '2ch', 16, 10, 'И разгневался Аса на прозорливца, и заключил его в темницу, так как за это был в раздражении на него; притеснял Аса и <i>некоторых</i> из народа в то время.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19762, 'rst66', '2ch', 16, 11, 'И вот, деяния Асы, первые и последние, описаны в книге царей Иудейских и Израильских.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19763, 'rst66', '2ch', 16, 12, 'И сделался Аса болен ногами на тридцать девятом году царствования своего, и болезнь его поднялась до верхних частей тела; но он в болезни своей взыскал не Господа, а врачей.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19764, 'rst66', '2ch', 16, 13, 'И почил Аса с отцами своими, и умер на сорок первом году царствования своего.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19765, 'rst66', '2ch', 16, 14, 'И похоронили его в гробнице, которую он устроил для себя в городе Давидовом; и положили его на одре, который наполнили благовониями и разными искусственными мастями, и сожгли их для него великое множество.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19766, 'rst66', '2ch', 17, 1, 'И воцарился Иосафат, сын его, вместо него; и укрепился он против Израильтян.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19767, 'rst66', '2ch', 17, 2, 'И поставил он войско во все укрепленные города Иудеи и расставил охранное войско по земле Иудейской и по городам Ефремовым, которыми овладел Аса, отец его.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19768, 'rst66', '2ch', 17, 3, 'И был Господь с Иосафатом, потому что он ходил первыми путями Давида, отца своего, и не взыскал Ваалов,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19769, 'rst66', '2ch', 17, 4, 'но взыскал он Бога отца своего и поступал по заповедям Его, а не по деяниям Израильтян.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19770, 'rst66', '2ch', 17, 5, 'И утвердил Господь царство в руке его, и давали все Иудеи дары Иосафату, и было у него много богатства и славы.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19771, 'rst66', '2ch', 17, 6, 'И возвысилось сердце его на путях Господних; притом и высоты отменил он и дубравы в Иудее.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19772, 'rst66', '2ch', 17, 7, 'И в третий год царствования своего он послал князей своих Бенхаила и Овадию, и Захарию и Нафанаила и Михея, чтоб учили по городам Иудиным народ,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19773, 'rst66', '2ch', 17, 8, 'и с ними левитов: Шемаию и Нефанию, и Зевадию и Азаила, и Шемирамофа и Ионафана, и Адонию и Товию и Тов‐Адонию, и с ними Елишаму и Иорама, священников.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19774, 'rst66', '2ch', 17, 9, 'И они учили в Иудее, имея с собою книгу закона Господня; и обходили все города Иудеи и учили народ.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19775, 'rst66', '2ch', 17, 10, 'И был страх Господень на всех царствах земель, которые вокруг Иудеи, и не воевали с Иосафатом.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19776, 'rst66', '2ch', 17, 11, 'А от Филистимлян приносили Иосафату дары и в дань серебро; также Аравитяне пригоняли к нему мелкий скот: овнов семь тысяч семьсот и козлов семь тысяч семьсот.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19777, 'rst66', '2ch', 17, 12, 'И возвышался Иосафат все более и более и построил в Иудее крепости и города для запасов.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19778, 'rst66', '2ch', 17, 13, 'Много было у него запасов в городах Иудейских, а в Иерусалиме людей военных, храбрых.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19779, 'rst66', '2ch', 17, 14, 'И вот список их по поколениям их: у Иуды начальники тысяч: Адна начальник, и у него отличных воинов триста тысяч;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19780, 'rst66', '2ch', 17, 15, 'за ним Иоханан начальник, и у него двести восемьдесят тысяч;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19781, 'rst66', '2ch', 17, 16, 'за ним Амасия, сын Зихри, посвятивший себя Господу, и у него двести тысяч воинов отличных.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19782, 'rst66', '2ch', 17, 17, 'У Вениамина: отличный воин Елиада, и у него вооруженных луком и щитом двести тысяч;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19783, 'rst66', '2ch', 17, 18, 'за ним Иегозавад, и у него сто восемьдесят тысяч вооруженных воинов.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19784, 'rst66', '2ch', 17, 19, 'Вот служившие царю, сверх тех, которых расставил царь в укрепленных городах по всей Иудее.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19785, 'rst66', '2ch', 18, 1, 'И было у Иосафата много богатства и славы; и породнился он с Ахавом.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19786, 'rst66', '2ch', 18, 2, 'И пошел через несколько лет к Ахаву в Самарию; и заколол для него Ахав множество скота мелкого и крупного, и для людей, бывших с ним, и склонял его идти на Рамоф Галаадский.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19787, 'rst66', '2ch', 18, 3, 'И говорил Ахав, царь Израильский, Иосафату, царю Иудейскому: пойдешь ли со мною в Рамоф Галаадский? Тот сказал ему: как ты, так и я, как твой народ, так и мой народ: <i>иду</i> с тобою на войну!');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19788, 'rst66', '2ch', 18, 4, 'И сказал Иосафат царю Израильскому: вопроси сегодня, что скажет Господь.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19789, 'rst66', '2ch', 18, 5, 'И собрал царь Израильский пророков четыреста человек и сказал им: идти ли нам на Рамоф Галаадский войною, или удержаться? Они сказали: иди, и Бог предаст <i>его</i> в руку царя.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19790, 'rst66', '2ch', 18, 6, 'И сказал Иосафат: нет ли здесь еще пророка Господня? спросим и у него.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19791, 'rst66', '2ch', 18, 7, 'И сказал царь Израильский Иосафату: есть еще один человек, чрез которого можно вопросить Господа; но я не люблю его, потому что он не пророчествует обо мне доброго, а постоянно пророчествует худое; это Михей, сын Иемвлая. И сказал Иосафат: не говори так, царь.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19792, 'rst66', '2ch', 18, 8, 'И позвал царь Израильский одного евнуха, и сказал: сходи поскорее за Михеем, сыном Иемвлая.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19793, 'rst66', '2ch', 18, 9, 'Царь же Израильский и Иосафат, царь Иудейский, сидели каждый на своем престоле, одетые в <i>царские</i> одежды; сидели на площади у ворот Самарии, и все пророки пророчествовали пред ними.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19794, 'rst66', '2ch', 18, 10, 'И сделал себе Седекия, сын Хенааны, железные рога и сказал: так говорит Господь: сими избодешь Сириян до истребления их.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19795, 'rst66', '2ch', 18, 11, 'И все пророки пророчествовали то же, говоря: иди на Рамоф Галаадский; будет успех тебе, и предаст <i>его</i> Господь в руку царя.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19796, 'rst66', '2ch', 18, 12, 'Посланный, который пошел позвать Михея, говорил ему: вот, пророки единогласно предрекают доброе царю; пусть бы и твое слово было такое же, как каждого из них: изреки и ты доброе.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19797, 'rst66', '2ch', 18, 13, 'И сказал Михей: жив Господь, &mdash; что скажет мне Бог мой, то изреку я.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19798, 'rst66', '2ch', 18, 14, 'И пришел он к царю, и сказал ему царь: Михей, идти ли нам войной на Рамоф Галаадский, или удержаться? И сказал тот: идите, будет вам успех, и они преданы будут в руки ваши.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19799, 'rst66', '2ch', 18, 15, 'И сказал ему царь: сколько раз мне заклинать тебя, чтобы ты не говорил мне ничего, кроме истины, во имя Господне?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19800, 'rst66', '2ch', 18, 16, 'Тогда <i>Михей</i> сказал: я видел всех сынов Израиля, рассеянных по горам, как овец, у которых нет пастыря, &mdash; и сказал Господь: «нет у них начальника, пусть возвратятся каждый в дом свой с миром».');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19801, 'rst66', '2ch', 18, 17, 'И сказал царь Израильский Иосафату: не говорил ли я тебе, что он не пророчествует обо мне доброго, а только худое?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19802, 'rst66', '2ch', 18, 18, 'И сказал <i>Михей:</i> так выслушайте слово Господне: я видел Господа, сидящего на престоле Своем, и все воинство небесное стояло по правую и по левую руку Его.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19803, 'rst66', '2ch', 18, 19, 'И сказал Господь: «кто увлек бы Ахава, царя Израильского, чтобы он пошел и пал в Рамофе Галаадском?» И один говорил так, другой говорил иначе.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19804, 'rst66', '2ch', 18, 20, 'И выступил один дух, и стал пред лицом Господа, и сказал: «я увлеку его». И сказал ему Господь: «чем?»');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19805, 'rst66', '2ch', 18, 21, 'Тот сказал: «я выйду и буду духом лжи в устах всех пророков его». И сказал Он: «ты увлечешь его, и успеешь; пойди и сделай так».');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19806, 'rst66', '2ch', 18, 22, 'И теперь, вот, попустил Господь духу лжи <i>войти</i> в уста сих пророков твоих, но Господь изрек о тебе недоброе.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19807, 'rst66', '2ch', 18, 23, 'И подошел Седекия, сын Хенааны, и ударил Михея по щеке, и сказал: по какой это дороге отошел от меня Дух Господень, чтобы говорить в тебе?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19808, 'rst66', '2ch', 18, 24, 'И сказал Михей: вот, ты увидишь <i>это</i> в тот день, когда будешь бегать из комнаты в комнату, чтобы укрыться.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19809, 'rst66', '2ch', 18, 25, 'И сказал царь Израильский: возьмите Михея и отведите его к Амону градоначальнику и к Иоасу, сыну царя,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19810, 'rst66', '2ch', 18, 26, 'и скажите: так говорит царь: посадите этого в темницу и кормите его хлебом и водою скудно, доколе я не возвращусь в мире.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19811, 'rst66', '2ch', 18, 27, 'И сказал Михей: если ты возвратишься в мире, то не Господь говорил чрез меня. И сказал: слушайте <i>это</i>, все люди!');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19812, 'rst66', '2ch', 18, 28, 'И пошел царь Израильский и Иосафат, царь Иудейский, к Рамофу Галаадскому.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19813, 'rst66', '2ch', 18, 29, 'И сказал царь Израильский Иосафату: я переоденусь и вступлю в сражение, а ты надень свои <i>царские</i> одежды. И переоделся царь Израильский, и вступили в сражение.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19814, 'rst66', '2ch', 18, 30, 'И царь Сирийский повелел начальникам колесниц, бывших у него, сказав: не сражайтесь ни с малым, ни с великим, а только с одним царем Израильским.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19815, 'rst66', '2ch', 18, 31, 'И когда увидели Иосафата начальники колесниц, то подумали: «это царь Израильский», &mdash; и окружили его, чтобы сразиться с ним. Но Иосафат закричал, и Господь помог ему, и отвел их Бог от него.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19816, 'rst66', '2ch', 18, 32, 'И когда увидели начальники колесниц, что <i>это</i> не был царь Израильский, то поворотили от него.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19817, 'rst66', '2ch', 18, 33, 'Между тем один человек случайно натянул лук свой и ранил царя Израильского сквозь швы лат. И сказал он вознице: повороти назад, и вези меня от войска, ибо я ранен.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19818, 'rst66', '2ch', 18, 34, 'Но сражение в тот день усилилось; и царь Израильский стоял на колеснице напротив Сириян до вечера, и умер на закате солнца.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19819, 'rst66', '2ch', 19, 1, 'И возвращался Иосафат, царь Иудейский, в мире в дом свой в Иерусалим.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19820, 'rst66', '2ch', 19, 2, 'И выступил навстречу ему Ииуй, сын Анании, прозорливец, и сказал царю Иосафату: <i>следовало</i> ли тебе помогать нечестивцу и любить ненавидящих Господа? За это на тебя гнев от лица Господня.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19821, 'rst66', '2ch', 19, 3, 'Впрочем, и доброе найдено в тебе, потому что ты истребил кумиры в земле <i>Иудейской</i> и расположил сердце свое к тому, чтобы взыскать Бога.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19822, 'rst66', '2ch', 19, 4, 'И жил Иосафат в Иерусалиме. И опять стал он обходить народ <i>свой</i> от Вирсавии до горы Ефремовой, и обращал их к Господу, Богу отцов их.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19823, 'rst66', '2ch', 19, 5, 'И поставил судей на земле по всем укрепленным городам Иудеи в каждом городе,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19824, 'rst66', '2ch', 19, 6, 'и сказал судьям: смотрите, что вы делаете, вы творите не суд человеческий, но суд Господа; и <i>Он</i> с вами в деле суда.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19825, 'rst66', '2ch', 19, 7, 'Итак, да будет страх Господень на вас: действуйте осмотрительно, ибо нет у Господа, Бога нашего, неправды, ни лицеприятия, ни мздоимства.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19826, 'rst66', '2ch', 19, 8, 'И в Иерусалиме приставил Иосафат <i>некоторых</i> из левитов и священников и глав поколений у Израиля &mdash; к суду Господню и к тяжбам. И возвратились в Иерусалим.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19827, 'rst66', '2ch', 19, 9, 'И дал им повеление, говоря: так действуйте в страхе Господнем, с верностью и с чистым сердцем:');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19828, 'rst66', '2ch', 19, 10, 'во всяком деле спорном, какое поступит к вам от братьев ваших, живущих в городах своих, о кровопролитии ли, или о законе, заповеди, уставах и обрядах, наставляйте их, чтобы они не провинились пред Господом, и не было бы гнева <i>Его</i> на вас и на братьев ваших; так действуйте, &mdash; и вы не погрешите.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19829, 'rst66', '2ch', 19, 11, 'И вот Амария, первосвященник, над вами во всяком деле Господнем, а Зевадия, сын Исмаилов, князь дома Иудина, во всяком деле царя, и надзиратели левиты пред вами. Будьте тверды и действуйте, и будет Господь с добрым.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19830, 'rst66', '2ch', 20, 1, 'После сего Моавитяне и Аммонитяне, а с ними некоторые из страны Маонитской, пошли войною на Иосафата.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19831, 'rst66', '2ch', 20, 2, 'И пришли, и донесли Иосафату, говоря: идет на тебя множество великое из‐за моря, от Сирии, и вот они в Хацацон‐Фамаре, то есть в Енгедди.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19832, 'rst66', '2ch', 20, 3, 'И убоялся Иосафат, и обратил лицо свое взыскать Господа, и объявил пост по всей Иудее.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19833, 'rst66', '2ch', 20, 4, 'И собрались Иудеи просить <i>помощи</i> у Господа; из всех городов Иудиных пришли они умолять Господа.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19834, 'rst66', '2ch', 20, 5, 'И стал Иосафат в собрании Иудеев и Иерусалимлян в доме Господнем, пред новым двором,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19835, 'rst66', '2ch', 20, 6, 'и сказал: Господи, Боже отцов наших! Не Ты ли Бог на небе? И Ты владычествуешь над всеми царствами народов, и в Твоей руке сила и крепость, и никто не устоит против Тебя!');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19836, 'rst66', '2ch', 20, 7, 'Не Ты ли, Боже наш, изгнал жителей земли сей пред лицом народа Твоего, Израиля, и отдал ее семени Авраама, друга Твоего, навек?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19837, 'rst66', '2ch', 20, 8, 'И они поселились на ней и построили Тебе на ней святилище во имя Твое, говоря:');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19838, 'rst66', '2ch', 20, 9, '«Если придет на нас бедствие: меч наказующий, или язва, или голод, то мы станем пред домом сим и пред лицом Твоим, ибо имя Твое в доме сем, и воззовем к Тебе в тесноте нашей, и Ты услышишь и спасешь».');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19839, 'rst66', '2ch', 20, 10, 'И ныне вот Аммонитяне и Моавитяне и <i>обитатели</i> горы Сеира, через земли которых Ты не позволил пройти Израильтянам, когда они шли из земли Египетской, а потому они миновали их и не истребили их, &mdash;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19840, 'rst66', '2ch', 20, 11, 'вот они платят нам <i>тем</i>, что пришли выгнать нас из наследственного владения Твоего, которое Ты отдал нам.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19841, 'rst66', '2ch', 20, 12, 'Боже наш! Ты суди их. Ибо нет в нас силы против множества сего великого, пришедшего на нас, и мы не знаем, что делать, но к Тебе очи наши!');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19842, 'rst66', '2ch', 20, 13, 'И все Иудеи стояли пред лицом Господним, и малые дети их, жены их и сыновья их.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19843, 'rst66', '2ch', 20, 14, 'Тогда на Иозиила, сына Захарии, сына Ванеи, сына Иеиела, сына Матфании, левита из сынов Асафовых, сошел Дух Господень среди собрания,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19844, 'rst66', '2ch', 20, 15, 'и сказал он: слушайте, все Иудеи, и жители Иерусалима, и царь Иосафат! Так говорит Господь к вам: не бойтесь и не ужасайтесь множества сего великого, ибо не ваша война, а Божия.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19845, 'rst66', '2ch', 20, 16, 'Завтра выступите против них: вот они всходят на возвышенность Циц, и вы найдете их на конце долины, пред пустынею Иеруилом.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19846, 'rst66', '2ch', 20, 17, 'Не вам сражаться на сей раз; вы станьте, стойте и смотрите на спасение Господне, <i>посылаемое</i> вам. Иуда и Иерусалим! не бойтесь и не ужасайтесь. Завтра выступите навстречу им, и Господь будет с вами.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19847, 'rst66', '2ch', 20, 18, 'И преклонился Иосафат лицом до земли, и все Иудеи и жители Иерусалима пали пред Господом, чтобы поклониться Господу.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19848, 'rst66', '2ch', 20, 19, 'И встали левиты из сынов Каафовых и из сынов Кореевых &mdash; хвалить Господа, Бога Израилева, голосом весьма громким.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19849, 'rst66', '2ch', 20, 20, 'И встали они рано утром, и выступили к пустыне Фекойской; и когда они выступили, стал Иосафат и сказал: послушайте меня, Иудеи и жители Иерусалима! Верьте Господу, Богу вашему, и будьте тверды; верьте пророкам Его, и будет успех вам.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19850, 'rst66', '2ch', 20, 21, 'И совещался он с народом, и поставил певцов Господу, чтобы они в благолепии святыни, выступая впереди вооруженных, славословили и говорили: славьте Господа, ибо вовек милость Его!');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19851, 'rst66', '2ch', 20, 22, 'И в то время <i>как</i> они стали восклицать и славословить, Господь возбудил несогласие между Аммонитянами, Моавитянами и <i>обитателями</i> горы Сеира, пришедшими на Иудею, и были они поражены:');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19852, 'rst66', '2ch', 20, 23, 'ибо восстали Аммонитяне и Моавитяне на обитателей горы Сеира, побивая и истребляя <i>их</i>, а когда покончили с жителями Сеира, тогда стали истреблять друг друга.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19853, 'rst66', '2ch', 20, 24, 'И когда Иудеи пришли на возвышенность к пустыне и взглянули на то многолюдство, и вот &mdash; трупы, лежащие на земле, и нет уцелевшего.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19854, 'rst66', '2ch', 20, 25, 'И пришел Иосафат и народ его забирать добычу, и нашли у них во множестве и имущество, и одежды, и драгоценные вещи, и набрали себе столько, что не <i>могли</i> нести. И три дня они забирали добычу; так велика <i>была</i> она!');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19855, 'rst66', '2ch', 20, 26, 'А в четвертый день собрались на долину благословения, так как там они благословили Господа. Посему и называют то место долиною благословения до сего дня.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19856, 'rst66', '2ch', 20, 27, 'И пошли назад все Иудеи и Иерусалимляне, и Иосафат во главе их, чтобы возвратиться в Иерусалим с веселием, потому что дал им Господь торжество над врагами их.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19857, 'rst66', '2ch', 20, 28, 'И пришли в Иерусалим с псалтирями, и цитрами, и трубами, к дому Господню.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19858, 'rst66', '2ch', 20, 29, 'И был страх Божий на всех царствах земных, когда они услышали, что <i>Сам</i> Господь воевал против врагов Израиля.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19859, 'rst66', '2ch', 20, 30, 'И спокойно стало царство Иосафатово, и дал ему Бог его покой со всех сторон.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19860, 'rst66', '2ch', 20, 31, 'Так царствовал Иосафат над Иудеею: тридцати пяти лет он <i>был</i>, когда воцарился, и двадцать пять лет царствовал в Иерусалиме. Имя матери его Азува, дочь Салаила.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19861, 'rst66', '2ch', 20, 32, 'И ходил он путем отца своего Асы и не уклонился от него, делая угодное в очах Господних.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19862, 'rst66', '2ch', 20, 33, 'Только высоты не были отменены, и народ еще не обратил твердо сердца своего к Богу отцов своих.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19863, 'rst66', '2ch', 20, 34, 'Прочие деяния Иосафата, первые и последние, описаны в записях Ииуя, сына Ананиева, которые внесены в книгу царей Израилевых.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19864, 'rst66', '2ch', 20, 35, 'Но после того вступил Иосафат, царь Иудейский, в общение с Охозиею, царем Израильским, который поступал беззаконно,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19865, 'rst66', '2ch', 20, 36, 'и соединился с ним, чтобы построить корабли для отправления в Фарсис; и построили они корабли в Ецион‐Гавере.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19866, 'rst66', '2ch', 20, 37, 'И изрек <i>тогда</i> Елиезер, сын Додавы из Мареши, пророчество на Иосафата, говоря: так как ты вступил в общение с Охозиею, то разрушил Господь дело твое. &mdash; И разбились корабли, и не могли идти в Фарсис.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19867, 'rst66', '2ch', 21, 1, 'И почил Иосафат с отцами своими, и похоронен с отцами своими в городе Давидовом. И воцарился Иорам, сын его, вместо него.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19868, 'rst66', '2ch', 21, 2, 'И у него <i>были</i> братья, сыновья Иосафата: Азария и Иехиил, и Захария, и Азария, и Михаил, и Сафатия: все сии сыновья Иосафата, царя Израилева.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19869, 'rst66', '2ch', 21, 3, 'И дал им отец их большие подарки серебром и золотом и драгоценностями, вместе с укрепленными городами в Иудее; царство же отдал Иораму, потому что он первенец.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19870, 'rst66', '2ch', 21, 4, 'И вступил Иорам на царство отца своего и утвердился, и умертвил всех братьев своих мечом и также <i>некоторых</i> из князей Израилевых.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19871, 'rst66', '2ch', 21, 5, 'Тридцати двух лет <i>был</i> Иорам, когда воцарился, и восемь лет царствовал в Иерусалиме;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19872, 'rst66', '2ch', 21, 6, 'и ходил он путем царей Израильских, как поступал дом Ахавов, &mdash; потому что дочь Ахава была женою его, &mdash; и делал он неугодное в очах Господних.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19873, 'rst66', '2ch', 21, 7, 'Однако же не хотел Господь погубить дома Давидова ради завета, который заключил с Давидом, и потому что обещал дать ему светильник и сыновьям его на все времена.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19874, 'rst66', '2ch', 21, 8, 'Во дни его вышел Едом из‐под власти Иуды, и поставили над собою царя.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19875, 'rst66', '2ch', 21, 9, 'И пошел Иорам с военачальниками своими, и все колесницы с ним; и, встав ночью, поразил Идумеян, которые окружили его, и начальствующих над колесницами.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19876, 'rst66', '2ch', 21, 10, 'Однако вышел Едом из‐под власти Иуды до сего дня. В то же время вышла и Ливна из‐под власти его, потому что он оставил Господа, Бога отцов своих.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19877, 'rst66', '2ch', 21, 11, 'Также высоты устроил он на горах Иудейских, и ввел в блужение жителей Иерусалима и соблазнил Иудею.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19878, 'rst66', '2ch', 21, 12, 'И пришло к нему письмо от Илии пророка, в котором было сказано: так говорит Господь, Бог Давида, отца твоего: за то, что ты не пошел путями Иосафата, отца твоего, и путями Асы, царя Иудейского,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19879, 'rst66', '2ch', 21, 13, 'а пошел путем царей Израильских и ввел в блужение Иудею и жителей Иерусалима, как вводил в блужение дом Ахавов, еще же и братьев твоих, дом отца твоего, которые лучше тебя, ты умертвил,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19880, 'rst66', '2ch', 21, 14, '<i>за то</i>, вот, Господь поразит поражением великим народ твой и сыновей твоих, и жен твоих, и все имущество твое,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19881, 'rst66', '2ch', 21, 15, 'тебя же <i>самого</i> &mdash; болезнью сильною, болезнью внутренностей твоих до того, что будут выпадать внутренности твои от болезни со дня на день.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19882, 'rst66', '2ch', 21, 16, 'И возбудил Господь против Иорама дух Филистимлян и Аравитян, сопредельных Ефиоплянам;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19883, 'rst66', '2ch', 21, 17, 'и они пошли на Иудею и ворвались в нее, и захватили все имущество, находившееся в доме царя, также и сыновей его и жен его; и не осталось у него сына, кроме Охозии, меньшего из сыновей его.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19884, 'rst66', '2ch', 21, 18, 'А после всего этого поразил Господь внутренности его болезнью неизлечимою.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19885, 'rst66', '2ch', 21, 19, 'Так было со дня на день, а к концу второго года выпали внутренности его от болезни его, и он умер в жестоких страданиях; и не сожег для него народ его <i>благовоний</i>, как делал то для отцов его.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19886, 'rst66', '2ch', 21, 20, 'Тридцати двух <i>лет</i> был он, когда воцарился, и восемь лет царствовал в Иерусалиме, и отошел неоплаканный, и похоронили его в городе Давидовом, но не в царских гробницах.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19887, 'rst66', '2ch', 22, 1, 'И поставили царем жители Иерусалима Охозию, меньшего сына его, вместо него, так как всех старших избило полчище, приходившее с Аравитянами к стану, &mdash; и воцарился Охозия, сын Иорама, царя Иудейского.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19888, 'rst66', '2ch', 22, 2, 'Двадцати двух лет <i>был</i> Охозия, когда воцарился, и один год царствовал в Иерусалиме; имя матери его Гофолия, дочь Амврия.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19889, 'rst66', '2ch', 22, 3, 'Он также ходил путями дома Ахавова, потому что мать его была советницею ему на беззаконные дела.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19890, 'rst66', '2ch', 22, 4, 'И делал он неугодное в очах Господних, подобно дому Ахавову, потому что он был ему советником, по смерти отца его, на погибель ему.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19891, 'rst66', '2ch', 22, 5, 'Также, следуя их совету, он пошел с Иорамом, сыном Ахавовым, царем Израильским, на войну против Азаила, царя Сирийского, в Рамоф Галаадский. И ранили Сирияне Иорама,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19892, 'rst66', '2ch', 22, 6, 'и возвратился он в Изреель лечиться от ран, которые причинили ему в Раме, когда он воевал с Азаилом, царем Сирийским. И Охозия, сын Иорама, царь Иудейский, пришел посетить Иорама, сына Ахавова, в Изреель, потому что тот был болен.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19893, 'rst66', '2ch', 22, 7, 'И от Бога было это на погибель Охозии, что он пришел к Иораму: ибо, по приходе своем, он вышел с Иорамом против Ииуя, сына Намессиева, которого помазал Господь на истребление дома Ахавова.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19894, 'rst66', '2ch', 22, 8, 'Когда совершал Ииуй суд над домом Ахава, тогда он нашел князей Иудейских и сыновей братьев Охозии, служивших Охозии, и умертвил их.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19895, 'rst66', '2ch', 22, 9, 'И <i>велел</i> он искать Охозию, и взяли его, когда он скрывался в Самарии, и привели его к Ииую, и умертвили его, и похоронили его, ибо говорили: он сын Иосафата, который взыскал Господа от всего сердца своего. И не <i>осталось</i> в доме Охозии, <i>кто</i> мог бы царствовать.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19896, 'rst66', '2ch', 22, 10, 'Ибо Гофолия, мать Охозии, увидев, что умер сын ее, встала и истребила все царское племя дома Иудина.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19897, 'rst66', '2ch', 22, 11, 'Но Иосавеф, дочь царя, взяла Иоаса, сына Охозии, и похитила его из среды царских сыновей умерщвляемых, и поместила его и кормилицу его в спальной комнате; и таким образом Иосавеф, дочь царя Иорама, жена Иодая священника, сестра Охозии, скрыла Иоаса от Гофолии, и она не умертвила его.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19898, 'rst66', '2ch', 22, 12, 'И был он у них в доме Божием скрываем шесть лет; Гофолия же царствовала над землею.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19899, 'rst66', '2ch', 23, 1, 'Но в седьмой год ободрился Иодай и принял в союз с собою начальников сотен: Азарию, сына Иерохамова, и Исмаила, сына Иегохананова, и Азарию, сына Оведова, и Маасею, сына Адаии, и Елишафата, сына Зихри.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19900, 'rst66', '2ch', 23, 2, 'И они прошли по Иудее и собрали левитов из всех городов Иудеи и глав поколений Израилевых, и пришли в Иерусалим.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19901, 'rst66', '2ch', 23, 3, 'И заключило все собрание союз в доме Божием с царем. И сказал им <i>Иодай:</i> вот сын царя должен быть царем, как изрек Господь о сыновьях Давидовых.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19902, 'rst66', '2ch', 23, 4, 'Вот что вы сделайте: треть вас, приходящих в субботу, из священников и левитов, <i>будет</i> привратниками у порогов,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19903, 'rst66', '2ch', 23, 5, 'и треть при доме царском, и треть у ворот Иесод, а весь народ на дворах дома Господня.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19904, 'rst66', '2ch', 23, 6, 'И <i>никто</i> пусть не входит в дом Господень, кроме священников и служащих из левитов. Они могут войти, потому что освящены; весь же народ пусть стоит на страже Господней.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19905, 'rst66', '2ch', 23, 7, 'И пусть левиты окружат царя со всех сторон, всякий с оружием своим в руке своей, и кто будет входить в храм, да будет умерщвлен. И будьте вы при царе, когда он будет входить и выходить.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19906, 'rst66', '2ch', 23, 8, 'И сделали левиты и все Иудеи, что приказал Иодай священник; и взяли каждый людей своих, приходящих в субботу с отходящими в субботу, потому что не отпустил священник Иодай <i>сменившихся</i> черед.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19907, 'rst66', '2ch', 23, 9, 'И раздал Иодай священник начальникам сотен копья и малые и большие щиты царя Давида, которые <i>были</i> в доме Божием;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19908, 'rst66', '2ch', 23, 10, 'и поставил весь народ, каждого с оружием его в руке его, от правой стороны храма до левой стороны храма, у жертвенника и у дома, вокруг царя.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19909, 'rst66', '2ch', 23, 11, 'И вывели сына царя, и возложили на него венец и украшения, и поставили его царем; и помазали его Иодай и сыновья его и сказали: да живет царь!');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19910, 'rst66', '2ch', 23, 12, 'И услышала Гофолия голос народа, бегущего и провозглашающего о царе, и вышла к народу в дом Господень,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19911, 'rst66', '2ch', 23, 13, 'и увидела: и вот, царь стоит на возвышении своем при входе, и князья и трубы подле царя, и весь народ земли веселится, и трубят трубами, и певцы с орудиями музыкальными и искусные в славословии. И разодрала Гофолия одежды свои и закричала: заговор! заговор!');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19912, 'rst66', '2ch', 23, 14, 'И вызвал Иодай священник начальников сотен, начальствующих над войском, и сказал им: выведите ее вон, и кто последует за нею, да будет умерщвлен мечом. Потому что священник сказал: не умертвите ее в доме Господнем.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19913, 'rst66', '2ch', 23, 15, 'И дали ей место, и когда она пришла ко входу конских ворот царского дома, там умертвили ее.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19914, 'rst66', '2ch', 23, 16, 'И заключил Иодай завет между собою и между всем народом и царем, чтобы быть <i>им</i> народом Господним.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19915, 'rst66', '2ch', 23, 17, 'И пошел весь народ в капище Ваала, и разрушили его, и жертвенники его и истуканов его сокрушили; и Матфана, жреца Ваалова, умертвили пред жертвенниками.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19916, 'rst66', '2ch', 23, 18, 'И поручил Иодай дела дома Господня священникам и левитам, как распределил Давид в доме Господнем, для возношения всесожжений Господу, как написано в законе Моисеевом, с радостью и пением, по уставу Давидову.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19917, 'rst66', '2ch', 23, 19, 'И поставил он привратников у ворот дома Господня, чтобы не <i>мог</i> входить нечистый почему‐нибудь.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19918, 'rst66', '2ch', 23, 20, 'И взял начальников сотен, и вельмож, и начальствующих в народе, и весь народ земли, и проводил царя из дома Господня, и прошли чрез верхние ворота в дом царский, и посадили царя на царский престол.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19919, 'rst66', '2ch', 23, 21, 'И веселился весь народ земли, и город успокоился. А Гофолию умертвили мечом.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19920, 'rst66', '2ch', 24, 1, 'Семи лет <i>был</i> Иоас, когда воцарился, и сорок лет царствовал в Иерусалиме; имя матери его Цивья из Вирсавии.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19921, 'rst66', '2ch', 24, 2, 'И делал Иоас угодное в очах Господних во все дни Иодая священника.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19922, 'rst66', '2ch', 24, 3, 'И взял ему Иодай двух жен, и он имел <i>от них</i> сыновей и дочерей.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19923, 'rst66', '2ch', 24, 4, 'И после сего пришло на сердце Иоасу обновить дом Господень,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19924, 'rst66', '2ch', 24, 5, 'и собрал он священников и левитов и сказал им: пойдите по городам Иудеи и собирайте со всех Израильтян серебро для поддержания дома Бога вашего из года в год, и поспешите в этом деле. Но не поспешили левиты.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19925, 'rst66', '2ch', 24, 6, 'И призвал царь Иодая, главу <i>их</i>, и сказал ему: почему ты не требуешь от левитов, чтобы они доставляли с Иудеи и Иерусалима дань, <i>установленную</i> Моисеем, рабом Господним, и собранием Израильтян для скинии собрания?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19926, 'rst66', '2ch', 24, 7, 'Ибо нечестивая Гофолия и сыновья ее разорили дом Божий и все посвященное для дома Господня употребили для Ваалов.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19927, 'rst66', '2ch', 24, 8, 'И приказал царь, и сделали один ящик, и поставили его у входа в дом Господень извне.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19928, 'rst66', '2ch', 24, 9, 'И провозгласили по Иудее и Иерусалиму, чтобы приносили Господу дань, <i>наложенную</i> Моисеем, рабом Божиим, на Израильтян в пустыне.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19929, 'rst66', '2ch', 24, 10, 'И обрадовались все начальствующие и весь народ, и приносили и клали в ящик дотоле, доколе он не наполнился.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19930, 'rst66', '2ch', 24, 11, 'В то время, когда приносили ящик к царским чиновникам чрез левитов, и когда они видели, что серебра много, приходил писец царя и поверенный первосвященника, и высыпали из ящика, и относили его и ставили его на свое место. Так делали они изо дня в день, и собрали множество серебра.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19931, 'rst66', '2ch', 24, 12, 'И отдавали его царь и Иодай производителям работ по дому Господню, и они нанимали каменотесов и плотников для подновления дома Господня, также кузнецов и медников для укрепления дома Господня.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19932, 'rst66', '2ch', 24, 13, 'И работали производители работ, и совершилось исправление руками их, и привели дом Божий в надлежащее состояние его, и укрепили его.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19933, 'rst66', '2ch', 24, 14, 'И, кончив <i>все</i>, они представили царю и Иодаю остаток серебра. И сделали из него сосуды для дома Господня, сосуды служебные и <i>для</i> всесожжений, чаши и <i>другие</i> сосуды золотые и серебряные. И приносили всесожжения в доме Господнем постоянно во все дни Иодая.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19934, 'rst66', '2ch', 24, 15, 'И состарился Иодай и, насытившись днями <i>жизни</i>, умер: сто тридцать лет <i>было</i> ему, когда он умер.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19935, 'rst66', '2ch', 24, 16, 'И похоронили его в городе Давидовом с царями, потому что он делал доброе в Израиле и для Бога, и для дома Его.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19936, 'rst66', '2ch', 24, 17, 'Но по смерти Иодая пришли князья Иудейские и поклонились царю; тогда царь стал слушаться их.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19937, 'rst66', '2ch', 24, 18, 'И оставили дом Господа, Бога отцов своих, и стали служить деревам <i>посвященным</i> и идолам, &mdash; и был гнев <i>Господень</i> на Иуду и Иерусалим за сию вину их.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19938, 'rst66', '2ch', 24, 19, 'И Он посылал к ним пророков для обращения их к Господу, и они увещевали их, но те не слушали.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19939, 'rst66', '2ch', 24, 20, 'И Дух Божий облек Захарию, сына Иодая священника, и он стал на возвышении пред народом и сказал им: так говорит Господь: для чего вы преступаете повеления Господни? не будет успеха вам; и как вы оставили Господа, то и Он оставит вас.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19940, 'rst66', '2ch', 24, 21, 'И сговорились против него, и побили его камнями, по приказанию царя, на дворе дома Господня.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19941, 'rst66', '2ch', 24, 22, 'И не вспомнил царь Иоас благодеяния, какое сделал ему Иодай, отец его, и убил сына его. И он, умирая, говорил: да видит Господь и да взыщет!');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19942, 'rst66', '2ch', 24, 23, 'И по истечении года выступило против него войско Сирийское, и вошли в Иудею и в Иерусалим, и истребили из народа всех князей народа, и всю добычу, <i>взятую</i> у них, отослали к царю в Дамаск.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19943, 'rst66', '2ch', 24, 24, 'Хотя в небольшом числе людей приходило войско Сирийское, но Господь предал в руку их весьма многочисленную силу за то, что оставили Господа, Бога отцов своих. И над Иоасом совершили они суд,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19944, 'rst66', '2ch', 24, 25, 'и когда они ушли от него, оставив его в тяжкой болезни, то составили против него заговор рабы его, за кровь сына Иодая священника, и убили его на постели его, и он умер. И похоронили его в городе Давидовом, но не похоронили его в царских гробницах.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19945, 'rst66', '2ch', 24, 26, 'Заговорщиками же против него были: Завад, сын Шимеафы Аммонитянки, и Иегозавад, сын Шимрифы Моавитянки.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19946, 'rst66', '2ch', 24, 27, 'О сыновьях его и о множестве пророчеств против него и об устроении дома Божия написано в книге царей. И воцарился Амасия, сын его, вместо него.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19947, 'rst66', '2ch', 25, 1, 'Двадцати пяти лет воцарился Амасия и двадцать девять лет царствовал в Иерусалиме; имя матери его Иегоаддань из Иерусалима.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19948, 'rst66', '2ch', 25, 2, 'И делал он угодное в очах Господних, но не от полного сердца.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19949, 'rst66', '2ch', 25, 3, 'Когда утвердилось за ним царство, тогда он умертвил рабов своих, убивших царя, отца его.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19950, 'rst66', '2ch', 25, 4, 'Но детей их не умертвил, так как написано в законе, в книге Моисеевой, где заповедал Господь, говоря: «не должны быть умерщвляемы отцы за детей, и дети не должны быть умерщвляемы за отцов, но каждый за свое преступление должен умереть».');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19951, 'rst66', '2ch', 25, 5, 'И собрал Амасия Иудеев и поставил их по поколениям под власть тысяченачальников и стоначальников, всех Иудеев и Вениаминян, и пересчитал их от двадцати лет и выше, и нашел их триста тысяч человек отборных, ходящих на войну, держащих копье и щит.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19952, 'rst66', '2ch', 25, 6, 'И <i>еще</i> нанял из Израильтян сто тысяч храбрых воинов за сто талантов серебра.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19953, 'rst66', '2ch', 25, 7, 'Но человек Божий пришел к нему и сказал: царь! пусть не идет с тобою войско Израильское, потому что нет Господа с Израильтянами, со всеми сынами Ефрема.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19954, 'rst66', '2ch', 25, 8, 'Но иди ты <i>один</i>, делай дело, мужественно подвизайся на войне. <i>Иначе</i> повергнет тебя Бог пред лицом врага, ибо есть сила у Бога поддержать и повергнуть.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19955, 'rst66', '2ch', 25, 9, 'И сказал Амасия человеку Божию: что же делать со ста талантами, которые я отдал войску Израильскому? И сказал человек Божий: может Господь дать тебе более сего.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19956, 'rst66', '2ch', 25, 10, 'И отделил их Амасия, &mdash; войско, пришедшее к нему из <i>земли</i> Ефремовой, &mdash; чтоб они шли в свое место. И возгорелся сильно гнев их на Иудею, и они пошли назад в свое место, в пылу гнева.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19957, 'rst66', '2ch', 25, 11, 'А Амасия отважился и повел народ свой, и пошел на долину Соляную и побил сынов Сеира десять тысяч;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19958, 'rst66', '2ch', 25, 12, 'и десять тысяч живых взяли сыны Иудины в плен, и привели их на вершину скалы, и низринули их с вершины скалы, и все они разбились совершенно.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19959, 'rst66', '2ch', 25, 13, 'Войско же, которое Амасия отослал обратно, чтоб оно не ходило с ним на войну, рассыпалось по городам Иудеи от Самарии до Вефорона и перебило в них три тысячи, и награбило множество добычи.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19960, 'rst66', '2ch', 25, 14, 'Амасия, придя после поражения Идумеян, принес богов сынов Сеира и поставил их у себя богами, и пред ними кланялся и им кадил.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19961, 'rst66', '2ch', 25, 15, 'И воспылал гнев Господа на Амасию, и послал Он к нему пророка, и тот сказал ему: зачем ты прибегаешь к богам народа сего, которые не избавили народа своего от руки твоей?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19962, 'rst66', '2ch', 25, 16, 'Когда он говорил ему, <i>царь</i> отвечал: разве советником царским поставили тебя? перестань, чтобы не убили тебя. И перестал пророк, сказав: знаю, что решил Бог погубить тебя, потому что ты сделал сие и не слушаешь совета моего.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19963, 'rst66', '2ch', 25, 17, 'И посоветовался Амасия, царь Иудейский, и послал к Иоасу, сыну Иоахаза, сына Ииуева, царю Израильскому, сказать: выходи, повидаемся лично.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19964, 'rst66', '2ch', 25, 18, 'И послал Иоас, царь Израильский, к Амасии, царю Иудейскому, сказать: терн, который на Ливане, послал к кедру, который на Ливане же, сказать: «отдай дочь свою в жену сыну моему». Но прошли звери дикие, которые на Ливане, и истоптали этот терн.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19965, 'rst66', '2ch', 25, 19, 'Ты говоришь: «вот, я побил Идумеян», &mdash; и вознеслось сердце твое до тщеславия. Сиди лучше у себя дома. К чему тебе затевать опасное дело? Падешь ты, и Иудея с тобою.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19966, 'rst66', '2ch', 25, 20, 'Но не послушался Амасия, так как от Бога <i>было</i> это, дабы предать их в руку <i>Иоаса</i> за то, что стали прибегать к богам Идумейским.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19967, 'rst66', '2ch', 25, 21, 'И выступил Иоас, царь Израильский, и увиделись лично, он и Амасия, царь Иудейский, в Вефсамисе Иудейском.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19968, 'rst66', '2ch', 25, 22, 'И были разбиты Иудеи Израильтянами, и разбежались каждый в шатер свой.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19969, 'rst66', '2ch', 25, 23, 'И Амасию, царя Иудейского, сына Иоаса, сына Иоахазова, захватил Иоас, царь Израильский, в Вефсамисе и привел его в Иерусалим, и разрушил стену Иерусалимскую от ворот Ефремовых до ворот угольных, на четыреста локтей;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19970, 'rst66', '2ch', 25, 24, 'И <i>взял</i> все золото и серебро, и все сосуды, находившиеся в доме Божием у Овед‐Едома, и сокровища дома царского, и заложников, и возвратился в Самарию.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19971, 'rst66', '2ch', 25, 25, 'И жил Амасия, сын Иоасов, царь Иудейский, по смерти Иоаса, сына Иоахазова, царя Израильского, пятнадцать лет.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19972, 'rst66', '2ch', 25, 26, 'Прочие дела Амасии, первые и последние, описаны в книге царей Иудейских и Израильских.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19973, 'rst66', '2ch', 25, 27, 'И после того времени, как Амасия отступил от Господа, составили против него заговор в Иерусалиме, и он убежал в Лахис. И послали за ним в Лахис, и умертвили его там.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19974, 'rst66', '2ch', 25, 28, 'И привезли его на конях, и похоронили его с отцами его в городе Иудином.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19975, 'rst66', '2ch', 26, 1, 'И взял весь народ Иудейский Озию, которому <i>было</i> шестнадцать лет, и поставили его царем на место отца его Амасии.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19976, 'rst66', '2ch', 26, 2, 'Он обстроил Елаф и возвратил его Иудее, после того как почил царь с отцами своими.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19977, 'rst66', '2ch', 26, 3, 'Шестнадцати лет <i>был</i> Озия, когда воцарился, и пятьдесят два года царствовал в Иерусалиме; имя матери его Иехолия из Иерусалима.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19978, 'rst66', '2ch', 26, 4, 'И делал он угодное в очах Господних точно так, как делал Амасия, отец его;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19979, 'rst66', '2ch', 26, 5, 'и прибегал он к Богу во дни Захарии, поучавшего страху Божию; и в те дни, когда он прибегал к Господу, споспешествовал ему Бог.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19980, 'rst66', '2ch', 26, 6, 'И он вышел и сразился с Филистимлянами, и разрушил стены Гефа и стены Иавнеи и стены Азота; и построил города в <i>области</i> Азотской и у Филистимлян.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19981, 'rst66', '2ch', 26, 7, 'И помогал ему Бог против Филистимлян и против Аравитян, живущих в Гур‐Ваале, и <i>против</i> Меунитян;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19982, 'rst66', '2ch', 26, 8, 'и давали Аммонитяне дань Озии, и дошло имя его до пределов Египта, потому что он был весьма силен.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19983, 'rst66', '2ch', 26, 9, 'И построил Озия башни в Иерусалиме над воротами угольными и над воротами долины и на углу, и укрепил их.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19984, 'rst66', '2ch', 26, 10, 'И построил башни в пустыне, и иссек много водоемов, потому что имел много скота, и на низменности и на равнине, и земледельцев и садовников на горах и на Кармиле, ибо он любил земледелие.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19985, 'rst66', '2ch', 26, 11, 'Было у Озии и войско, выходившее на войну отрядами, по счету в списке их, составленном рукою Иеиела писца и Маасеи надзирателя, под предводительством Ханании, <i>одного</i> из главных сановников царских.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19986, 'rst66', '2ch', 26, 12, 'Все число глав поколений, из храбрых воинов, <i>было</i> две тысячи шестьсот,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19987, 'rst66', '2ch', 26, 13, 'и под рукою их военной силы триста семь тысяч пятьсот, вступавших в сражение с воинским мужеством, на помощь царю против неприятеля.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19988, 'rst66', '2ch', 26, 14, 'И заготовил для них Озия, для всего войска, щиты и копья, и шлемы и латы, и луки и пращные камни.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19989, 'rst66', '2ch', 26, 15, 'И сделал он в Иерусалиме искусно придуманные машины, чтоб они находились на башнях и на углах для метания стрел и больших камней. И пронеслось имя его далеко, потому что он дивно оградил себя и сделался силен.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19990, 'rst66', '2ch', 26, 16, 'Но когда он сделался силен, возгордилось сердце его на погибель <i>его</i>, и он сделался преступником пред Господом, Богом своим, ибо вошел в храм Господень, чтобы воскурить <i>фимиам</i> на алтаре кадильном.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19991, 'rst66', '2ch', 26, 17, 'И пошел за ним Азария священник, и с ним восемьдесят священников Господних, людей отличных,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19992, 'rst66', '2ch', 26, 18, 'и воспротивились Озии царю и сказали ему: не тебе, Озия, кадить Господу; это <i>дело</i> священников, сынов Аароновых, посвященных для каждения; выйди из святилища, ибо ты поступил беззаконно, и не <i>будет</i> тебе это в честь у Господа Бога.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19993, 'rst66', '2ch', 26, 19, 'И разгневался Озия, &mdash; а в руке у него кадильница для каждения; и когда разгневался он на священников, проказа явилась на челе его, пред лицом священников, в доме Господнем, у алтаря кадильного.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19994, 'rst66', '2ch', 26, 20, 'И взглянул на него Азария первосвященник и все священники; и вот, у него проказа на челе его. И понуждали его выйти оттуда, да и сам он спешил удалиться, так как поразил его Господь.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19995, 'rst66', '2ch', 26, 21, 'И был царь Озия прокаженным до дня смерти своей, и жил в отдельном доме и отлучен был от дома Господня. А Иоафам, сын его, начальствовал над домом царским и управлял народом земли.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19996, 'rst66', '2ch', 26, 22, 'Прочие деяния Озии, первые и последние, описал Исаия, сын Амоса, пророк.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19997, 'rst66', '2ch', 26, 23, 'И почил Озия с отцами своими, и похоронили его с отцами его на поле царских гробниц, ибо говорили: он прокаженный. И воцарился Иоафам, сын его, вместо него.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19998, 'rst66', '2ch', 27, 1, 'Двадцати пяти лет <i>был</i> Иоафам, когда воцарился, и шестнадцать лет царствовал в Иерусалиме; имя матери его Иеруша, дочь Садока.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (19999, 'rst66', '2ch', 27, 2, 'И делал он угодное в очах Господних точно так, как делал Озия, отец его, только он не входил в храм Господень, и народ продолжал еще грешить.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20000, 'rst66', '2ch', 27, 3, 'Он построил верхние ворота дома Господня, и многое построил на стене Офела;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20001, 'rst66', '2ch', 27, 4, 'и города построил на горе Иудейской, и в лесах построил дворцы и башни.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20002, 'rst66', '2ch', 27, 5, 'Он воевал с царем Аммонитян и одолел их, и дали ему Аммонитяне в тот год сто талантов серебра и десять тысяч ко&#769;ров пшеницы и ячменя десять тысяч. Это давали ему Аммонитяне и на другой год, и на третий.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20003, 'rst66', '2ch', 27, 6, 'Так силен был Иоафам потому, что устроял пути свои пред лицом Господа, Бога своего.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20004, 'rst66', '2ch', 27, 7, 'Прочие деяния Иоафама и все войны его и поведение его описаны в книге царей Израильских и Иудейских:');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20005, 'rst66', '2ch', 27, 8, 'двадцати пяти лет был он, когда воцарился, и шестнадцать лет царствовал в Иерусалиме.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20006, 'rst66', '2ch', 27, 9, 'И почил Иоафам с отцами своими, и похоронили его в городе Давидовом. И воцарился Ахаз, сын его, вместо него.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20007, 'rst66', '2ch', 28, 1, 'Двадцати лет был Ахаз, когда воцарился, и шестнадцать лет царствовал в Иерусалиме; и он не делал угодного в очах Господних, как <i>делал</i> Давид, отец его:');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20008, 'rst66', '2ch', 28, 2, 'он шел путями царей Израильских, и даже сделал литые статуи Ваалов;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20009, 'rst66', '2ch', 28, 3, 'и он совершал курения на долине сынов Еннома, и проводил сыновей своих через огонь, подражая мерзостям народов, которых изгнал Господь пред лицом сынов Израилевых;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20010, 'rst66', '2ch', 28, 4, 'и приносил жертвы и курения на высотах и на холмах и под всяким ветвистым деревом.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20011, 'rst66', '2ch', 28, 5, 'И предал его Господь, Бог его, в руки царя Сириян, и они поразили его и взяли у него множество пленных и отвели в Дамаск. Также и в руку царя Израильского был предан он, и тот произвел у него великое поражение.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20012, 'rst66', '2ch', 28, 6, 'И избил Факей, сын Ремалиин, Иудеев сто двадцать тысяч в один день, людей воинственных, потому что они оставили Господа, Бога отцов своих.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20013, 'rst66', '2ch', 28, 7, 'Зихрий же, силач из Ефремлян, убил Маасею, сына царя, и Азрикама, начальствующего над дворцом, и Елкану, второго по царе.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20014, 'rst66', '2ch', 28, 8, 'И взяли сыны Израилевы в плен у братьев своих, <i>Иудеев</i>, двести тысяч жен, сыновей и дочерей; также и множество добычи награбили у них, и отправили добычу в Самарию.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20015, 'rst66', '2ch', 28, 9, 'Там был пророк Господень, имя его Одед. Он вышел пред лицо войска, шедшего в Самарию, и сказал им: вот, Господь, Бог отцов ваших, во гневе на Иудеев, предал их в руку вашу, и вы избили их с такою яростью, которая достигла до небес.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20016, 'rst66', '2ch', 28, 10, 'И теперь вы думаете поработить сынов Иуды и Иерусалима в рабы и рабыни себе. А разве на самих вас нет вины пред Господом, Богом вашим?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20017, 'rst66', '2ch', 28, 11, 'Итак, послушайте меня, и возвратите пленных, которых вы захватили из братьев ваших, ибо пламень гнева Господня на вас.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20018, 'rst66', '2ch', 28, 12, 'И встали некоторые из начальников сынов Ефремовых: Азария, сын Иегоханана, Берехия, сын Мешиллемофа, и Езекия, сын Шаллума, и Амаса, сын Хадлая, против шедших с войны,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20019, 'rst66', '2ch', 28, 13, 'и сказали им: не вводите сюда пленных, потому что грех был бы нам пред Господом. Неужели вы думаете прибавить к грехам нашим и к преступлениям нашим? велика вина наша, и пламень гнева <i>Господня</i> над Израилем.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20020, 'rst66', '2ch', 28, 14, 'И оставили вооруженные пленных и добычу у военачальников и всего собрания.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20021, 'rst66', '2ch', 28, 15, 'И встали мужи, упомянутые по именам, и взяли пленных, и всех нагих из них одели из добычи, &mdash; и одели их, и обули их, и накормили их, и напоили их, и помазали их елеем, и посадили на ослов всех слабых из них, и отправили их в Иерихон, город пальм, к братьям их, и возвратились в Самарию.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20022, 'rst66', '2ch', 28, 16, 'В то время послал царь Ахаз к царям Ассирийским, чтоб они помогли ему,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20023, 'rst66', '2ch', 28, 17, 'ибо Идумеяне и еще приходили, и <i>многих</i> побили в Иудее, и взяли в плен;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20024, 'rst66', '2ch', 28, 18, 'и Филистимляне рассыпались по городам низменного края и юга Иудеи и взяли Вефсамис и Аиалон, и Гедероф и Сохо и зависящие от него города, и Фимну и зависящие от нее города, и Гимзо и зависящие от него города, и поселились там.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20025, 'rst66', '2ch', 28, 19, 'Так унизил Господь Иудею за Ахаза, царя Иудейского, потому что он развратил Иудею и тяжко грешил пред Господом.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20026, 'rst66', '2ch', 28, 20, 'И пришел к нему Феглафелласар, царь Ассирийский, но был в тягость ему, вместо того, чтобы помочь ему,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20027, 'rst66', '2ch', 28, 21, 'потому что Ахаз взял <i>сокровища</i> из дома Господня и дома царского и у князей и отдал царю Ассирийскому, но не в помощь себе.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20028, 'rst66', '2ch', 28, 22, 'И в тесное для себя время он продолжал беззаконно поступать пред Господом, он &mdash; царь Ахаз.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20029, 'rst66', '2ch', 28, 23, 'И приносил он жертвы богам Дамасским, <i>думая, что</i> они поражали его, и говорил: боги царей Сирийских помогают им; принесу я жертву им, и они помогут мне. Но они были на падение ему и всему Израилю.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20030, 'rst66', '2ch', 28, 24, 'И собрал Ахаз сосуды дома Божия, и сокрушил сосуды дома Божия, и запер двери дома Господня, и устроил себе жертвенники по всем углам в Иерусалиме,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20031, 'rst66', '2ch', 28, 25, 'и по всем городам Иудиным устроил высоты для каждения богам иным, и раздражал Господа, Бога отцов своих.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20032, 'rst66', '2ch', 28, 26, 'Прочие дела его и все поступки его, первые и последние, описаны в книге царей Иудейских и Израильских.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20033, 'rst66', '2ch', 28, 27, 'И почил Ахаз с отцами своими, и похоронили его в городе, в Иерусалиме, но не внесли его в гробницы царей Израилевых. И воцарился Езекия, сын его, вместо него.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20034, 'rst66', '2ch', 29, 1, 'Езекия воцарился двадцати пяти лет, и двадцать девять лет царствовал в Иерусалиме; имя матери его Авия, дочь Захарии.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20035, 'rst66', '2ch', 29, 2, 'И делал он угодное в очах Господних точно так, как делал Давид, отец его.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20036, 'rst66', '2ch', 29, 3, 'В первый же год царствования своего, в первый месяц, он отворил двери дома Господня и возобновил их,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20037, 'rst66', '2ch', 29, 4, 'и велел прийти священникам и левитам, и собрал их на площади восточной,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20038, 'rst66', '2ch', 29, 5, 'и сказал им: послушайте меня, левиты! Ныне освятитесь <i>сами</i> и освятите дом Господа, Бога отцов ваших, и выбросьте нечистоту из святилища.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20039, 'rst66', '2ch', 29, 6, 'Ибо отцы наши поступали беззаконно, и делали неугодное в очах Господа, Бога нашего, и оставили Его, и отвратили они лица свои от жилища Господня, и оборотились спиною,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20040, 'rst66', '2ch', 29, 7, 'и заперли двери притвора, и погасили светильники, и не сожигали курения, и не возносили всесожжений во святилище Бога Израилева.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20041, 'rst66', '2ch', 29, 8, 'И был гнев Господа на Иудею и на Иерусалим, и Он отдал их на позор, на опустошение и на посмеяние, как вы видите глазами вашими.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20042, 'rst66', '2ch', 29, 9, 'И вот, пали отцы наши от меча, а сыновья наши и дочери наши и жены наши за это в плену доныне.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20043, 'rst66', '2ch', 29, 10, 'Теперь у меня на сердце &mdash; заключить завет с Господом, Богом Израилевым, да отвратит от нас пламень гнева Своего.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20044, 'rst66', '2ch', 29, 11, 'Дети мои! не будьте небрежны, ибо вас избрал Господь предстоять лицу Его, служить Ему и быть у Него служителями и возжигателями курений.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20045, 'rst66', '2ch', 29, 12, 'И встали левиты: Махаф, сын Амасая, и Иоель, сын Азарии, из сыновей Каафовых; и из сыновей Мерариных: Кис, сын Авдия, и Азария, сын Иегаллелела; и из племени Гирсонова: Иоах, сын Зиммы, и Еден, сын Иоаха;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20046, 'rst66', '2ch', 29, 13, 'и из сыновей Елицафановых: Шимри и Иеиел; и из сыновей Асафовых: Захария и Матфания;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20047, 'rst66', '2ch', 29, 14, 'и из сыновей Емановых: Иехиел и Шимей; и из сыновей Идифуновых: Шемаия и Уззиел.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20048, 'rst66', '2ch', 29, 15, 'Они собрали братьев своих и освятились, и пошли по приказанию царя очищать дом Господень по словам Господа.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20049, 'rst66', '2ch', 29, 16, 'И вошли священники внутрь дома Господня для очищения, и вынесли все нечистое, что нашли в храме Господнем, на двор дома Господня, а левиты взяли это, чтобы вынести вон к потоку Кедрону.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20050, 'rst66', '2ch', 29, 17, 'И начали освящать в первый <i>день</i> первого месяца, и в восьмой день <i>того же</i> месяца вошли в притвор Господень; и освящали дом Господень восемь дней, и в шестнадцатый день первого месяца кончили.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20051, 'rst66', '2ch', 29, 18, 'И пришли в дом к царю Езекии и сказали: мы очистили дом Господень, и жертвенник для всесожжения, и все сосуды его, и стол <i>для хлебов</i> предложения, и все сосуды его;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20052, 'rst66', '2ch', 29, 19, 'и все сосуды, которые забросил царь Ахаз во время царствования своего, в беззаконии своем, мы приготовили и освятили, и вот, они пред жертвенником Господним.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20053, 'rst66', '2ch', 29, 20, 'И встал царь Езекия рано утром и собрал начальников города, и пошел в дом Господень.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20054, 'rst66', '2ch', 29, 21, 'И привели семь тельцов и семь овнов, и семь агнцев и семь козлов на жертву о грехе за царство и за святилище и за Иудею; и приказал он сынам Аароновым, священникам, вознести всесожжение на жертвенник Господень.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20055, 'rst66', '2ch', 29, 22, 'И закололи тельцов, и взяли священники кровь, и окропили жертвенник, и закололи овнов, и окропили кровью жертвенник; и закололи агнцев, и окропили кровью жертвенник.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20056, 'rst66', '2ch', 29, 23, 'И привели козлов за грех пред лицо царя и собрания, и они возложили руки свои на них.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20057, 'rst66', '2ch', 29, 24, 'И закололи их священники, и очистили кровью их жертвенник для заглаживания грехов всего Израиля, ибо за всего Израиля приказал царь <i>принести</i> всесожжение и жертву о грехе.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20058, 'rst66', '2ch', 29, 25, 'И поставил он левитов в доме Господнем с кимвалами, псалтирями и цитрами, по уставу Давида и Гада, прозорливца царева, и Нафана пророка, так как от Господа <i>был</i> устав этот чрез пророков Его.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20059, 'rst66', '2ch', 29, 26, 'И стали левиты с <i>музыкальными</i> орудиями Давидовыми и священники с трубами.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20060, 'rst66', '2ch', 29, 27, 'И приказал Езекия вознести всесожжение на жертвенник. И в то время, как началось всесожжение, началось пение Господу, при <i>звуке</i> труб и орудий Давида, царя Израилева.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20061, 'rst66', '2ch', 29, 28, 'И все собрание молилось, и певцы пели, и трубили трубы, доколе не окончилось всесожжение.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20062, 'rst66', '2ch', 29, 29, 'По окончании же всесожжения царь и все находившиеся при нем преклонились и поклонились.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20063, 'rst66', '2ch', 29, 30, 'И сказал царь Езекия и князья левитам, чтоб они славили Господа словами Давида и Асафа прозорливца, и они славили с радостью и преклонялись и поклонялись.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20064, 'rst66', '2ch', 29, 31, 'И продолжал Езекия и сказал: теперь вы посвятили себя Господу; приступайте и приносите жертвы и благодарственные приношения в дом Господень. И понесло <i>все</i> собрание жертвы и благодарственные приношения, и всякий, кто расположен был сердцем, &mdash; всесожжения.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20065, 'rst66', '2ch', 29, 32, 'И было число всесожжений, которые привели собравшиеся: семьдесят волов, сто овнов, двести агнцев &mdash; все это для всесожжения Господу.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20066, 'rst66', '2ch', 29, 33, '<i>Других</i> священных жертв <i>было:</i> шестьсот из крупного скота и три тысячи из мелкого скота.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20067, 'rst66', '2ch', 29, 34, 'Но священников было мало, и они не могли сдирать кож со всех всесожжений, и помогали им братья их левиты, до окончания дела и доколе освятились <i>прочие</i> священники, ибо левиты были более тщательны в освящении себя, нежели священники.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20068, 'rst66', '2ch', 29, 35, 'Притом же всесожжений <i>было</i> множество с туками мирных жертв и с возлияниями при всесожжении. Так восстановлено служение в доме Господнем.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20069, 'rst66', '2ch', 29, 36, 'И радовался Езекия и весь народ о том, что Бог <i>так</i> расположил народ, ибо это сделалось неожиданно.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20070, 'rst66', '2ch', 30, 1, 'И послал Езекия по всей <i>земле</i> Израильской и Иудее, и письма писал к Ефрему и Манассии, чтобы пришли в дом Господень, в Иерусалим, для совершения пасхи Господу, Богу Израилеву.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20071, 'rst66', '2ch', 30, 2, 'И положили на совете царь и князья его и все собрание в Иерусалиме &mdash; совершить пасху во второй месяц,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20072, 'rst66', '2ch', 30, 3, 'ибо не могли совершить ее в свое время, потому что священники <i>еще</i> не освятились в достаточном числе, и народ не собрался в Иерусалим.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20073, 'rst66', '2ch', 30, 4, 'И понравилось это царю и всему собранию.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20074, 'rst66', '2ch', 30, 5, 'И определили объявить по всему Израилю, от Вирсавии до Дана, чтобы шли в Иерусалим для совершения пасхи Господу, Богу Израилеву, потому что давно не совершали <i>ее</i>, как предписано.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20075, 'rst66', '2ch', 30, 6, 'И пошли гонцы с письмами от царя и от князей его по всей <i>земле</i> Израильской и Иудее, и по повелению царя говорили: дети Израиля! обратитесь к Господу, Богу Авраама, Исаака и Израиля, и Он обратится к остатку, уцелевшему у вас от руки царей Ассирийских.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20076, 'rst66', '2ch', 30, 7, 'И не будьте таковы, как отцы ваши и братья ваши, которые беззаконно поступали пред Господом, Богом отцов своих; и Он предал их на опустошение, как вы видите.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20077, 'rst66', '2ch', 30, 8, 'Ныне не будьте жестоковыйны, как отцы ваши, покоритесь Господу и приходите во святилище Его, которое Он освятил навек; и служите Господу, Богу вашему, и Он отвратит от вас пламень гнева Своего.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20078, 'rst66', '2ch', 30, 9, 'Когда вы обратитесь к Господу, тогда братья ваши и дети ваши <i>будут</i> в милости у пленивших их и возвратятся в землю сию, ибо благ и милосерд Господь, Бог ваш, и не отвратит лица от вас, если вы обратитесь к Нему.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20079, 'rst66', '2ch', 30, 10, 'И ходили гонцы из города в город по земле Ефремовой и Манассииной и до Завулоновой, но над ними смеялись и издевались.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20080, 'rst66', '2ch', 30, 11, 'Однако некоторые из <i>колена</i> Асирова, Манассиина и Завулонова смирились и пришли в Иерусалим.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20081, 'rst66', '2ch', 30, 12, 'И над Иудеею была рука Божия, даровавшая им единое сердце, чтоб исполнить повеление царя и князей, по слову Господню.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20082, 'rst66', '2ch', 30, 13, 'И собралось в Иерусалим множество народа для совершения праздника опресноков, во второй месяц, &mdash; собрание весьма многочисленное.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20083, 'rst66', '2ch', 30, 14, 'И встали и ниспровергли жертвенники, которые были в Иерусалиме; и всё, на чем совершаемо было курение <i>идолам</i>, разрушили и бросили в поток Кедрон;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20084, 'rst66', '2ch', 30, 15, 'и закололи пасхального агнца в четырнадцатый <i>день</i> второго месяца. Священники и левиты, устыдившись, освятились и принесли всесожжения в дом Господень,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20085, 'rst66', '2ch', 30, 16, 'и стали на своем месте по уставу своему, по закону Моисея, человека Божия. Священники кропили кровью, <i>принимая ее</i> из рук левитов.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20086, 'rst66', '2ch', 30, 17, 'Так как много <i>было</i> в собрании таких, которые не освятились, то вместо нечистых левиты закололи пасхального агнца, для посвящения Господу.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20087, 'rst66', '2ch', 30, 18, 'Многие из народа, большею частью из колена Ефремова и Манассиина, Иссахарова и Завулонова, не очистились; однако же они ели пасху, не по уставу.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20088, 'rst66', '2ch', 30, 19, 'Но Езекия помолился за них, говоря: Господь благий да простит каждого, кто расположил сердце свое к тому, чтобы взыскать Господа Бога, Бога отцов своих, хотя и без очищения священного!');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20089, 'rst66', '2ch', 30, 20, 'И услышал Господь Езекию и простил народ.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20090, 'rst66', '2ch', 30, 21, 'И совершили сыны Израилевы, находившиеся в Иерусалиме, праздник опресноков в семь дней, с великим веселием; каждый день левиты и священники славили Господа на орудиях, <i>устроенных</i> для славословия Господа.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20091, 'rst66', '2ch', 30, 22, 'И говорил Езекия по сердцу всем левитам, имевшим доброе разумение <i>в служении</i> Господу. И ели праздничное семь дней, принося жертвы мирные и славя Господа, Бога отцов своих.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20092, 'rst66', '2ch', 30, 23, 'И решило все собрание праздновать другие семь дней, и провели эти семь дней в веселии,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20093, 'rst66', '2ch', 30, 24, 'потому что Езекия, царь Иудейский, выставил для собравшихся тысячу тельцов и десять тысяч мелкого скота, и вельможи выставили для собравшихся тысячу тельцов и десять тысяч мелкого скота; и священников освятилось <i>уже</i> много.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20094, 'rst66', '2ch', 30, 25, 'И веселились все собравшиеся из Иудеи, и священники и левиты, и все собрание, пришедшее от Израиля, и пришельцы, пришедшие из земли Израильской и обитавшие в Иудее.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20095, 'rst66', '2ch', 30, 26, 'И было веселие великое в Иерусалиме, потому что со дней Соломона, сына Давидова, царя Израилева, <i>не бывало</i> подобного сему в Иерусалиме.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20096, 'rst66', '2ch', 30, 27, 'И встали священники и левиты, и благословили народ; и услышан был голос их, и взошла молитва их в святое жилище Его на небеса.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20097, 'rst66', '2ch', 31, 1, 'И по окончании всего этого пошли все Израильтяне, <i>там</i> находившиеся, в города Иудейские и разбили статуи, срубили <i>посвященные</i> дерева&#769;, и разрушили высоты и жертвенники во всей Иудее и в <i>земле</i> Вениаминовой, Ефремовой и Манассииной, до конца. И <i>потом</i> возвратились все сыны Израилевы, каждый во владение свое, в города свои.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20098, 'rst66', '2ch', 31, 2, 'И поставил Езекия череды священников и левитов, по их распределению, каждого при деле своем, священническом или левитском, при всесожжении и при жертвах мирных, для службы, для хваления и славословия, у ворот дома Господня.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20099, 'rst66', '2ch', 31, 3, 'И <i>определил</i> царь часть из имущества своего на всесожжения: на всесожжения утренние и вечерние, и на всесожжения в субботы и в новомесячия, и в праздники, как написано в законе Господнем.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20100, 'rst66', '2ch', 31, 4, 'И повелел он народу, живущему в Иерусалиме, давать определенное содержание священникам и левитам, чтоб они были ревностны в законе Господнем.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20101, 'rst66', '2ch', 31, 5, 'Когда обнародовано было это повеление, тогда нанесли сыны Израилевы множество начатков хлеба, вина, и масла, и меду, и всяких произведений полевых; и десятин из всего нанесли множество.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20102, 'rst66', '2ch', 31, 6, 'И Израильтяне и Иудеи, живущие по городам Иудейским, также представили десятины из крупного и мелкого скота и десятины из пожертвований, посвященных Господу, Богу их; и наложили груды, груды.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20103, 'rst66', '2ch', 31, 7, 'В третий месяц начали класть груды, и в седьмой месяц кончили.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20104, 'rst66', '2ch', 31, 8, 'И пришли Езекия и вельможи, и увидели груды, и благодарили Господа и народ Его, Израиля.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20105, 'rst66', '2ch', 31, 9, 'И спросил Езекия священников и левитов об этих грудах.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20106, 'rst66', '2ch', 31, 10, 'И отвечал ему Азария первосвященник из дома Садокова и сказал: с того времени, как начали носить приношения в дом Господень, мы ели досыта, и многое осталось, потому что Господь благословил народ Свой. Из оставшегося <i>составилось</i> такое множество.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20107, 'rst66', '2ch', 31, 11, 'И приказал Езекия приготовить комнаты при доме Господнем. И приготовили.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20108, 'rst66', '2ch', 31, 12, 'И перенесли <i>туда</i> приношения, и десятины, и пожертвования, со <i>всею</i> точностью. И <i>был</i> начальником при них Хонания левит, и Симей, брат его, вторым.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20109, 'rst66', '2ch', 31, 13, 'А Иехиил и Азазия, и Нахаф и Асаил, и Иеримоф и Иозавад, и Елиел и Исмахия, и Махаф и Бенания <i>были</i> смотрителями под рукою Хонании и Симея, брата его, по распоряжению царя Езекии и Азарии, начальника при доме Божием.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20110, 'rst66', '2ch', 31, 14, 'Коре, сын Имны, левит, привратник на восточной стороне, <i>был</i> при добровольных приношениях Богу, для выдачи принесенного Господу и важнейших из вещей посвященных.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20111, 'rst66', '2ch', 31, 15, 'И под его <i>ведением находились</i> Еден, и Миниамин, и Иешуа, и Шемаия, и Амария и Шехания в городах священнических, чтобы верно раздавать братьям своим части, как большому, так и малому,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20112, 'rst66', '2ch', 31, 16, 'сверх списка их, <i>всем</i> мужеского пола от трех лет и выше, всем ходящим в дом Господа для дел ежедневных, для служения их, по должностям их и по отделам их,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20113, 'rst66', '2ch', 31, 17, 'и внесенным в список священникам, по поколениям их, и левитам от двадцати лет и выше, по должностям их, по отделам их,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20114, 'rst66', '2ch', 31, 18, 'и внесенным в список, со всеми малолетними их, с женами их и с сыновьями их и с дочерями их, &mdash; всему обществу, ибо они со <i>всею</i> верностью посвятили себя на священную службу.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20115, 'rst66', '2ch', 31, 19, 'И для сынов Аароновых, священников в селениях вокруг городов их, при каждом городе <i>поставлены были</i> мужи поименованные, чтобы раздавать участки всем мужеского пола у священников и всем внесенным в список у левитов.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20116, 'rst66', '2ch', 31, 20, 'Вот что сделал Езекия во всей Иудее, &mdash; и он делал доброе, и справедливое, и истинное пред лицом Господа, Бога своего.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20117, 'rst66', '2ch', 31, 21, 'И во всем, что он предпринимал на служение дому Божию и для соблюдения закона и заповедей, помышляя о Боге своем, он действовал от всего сердца своего и имел успех.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20118, 'rst66', '2ch', 32, 1, 'После таких дел и верности пришел Сеннахирим, царь Ассирийский, и вступил в Иудею, и осадил укрепленные города, и думал отторгнуть их себе.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20119, 'rst66', '2ch', 32, 2, 'Когда Езекия увидел, что пришел Сеннахирим с намерением воевать против Иерусалима,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20120, 'rst66', '2ch', 32, 3, 'тогда решил с князьями своими и с военными людьми своими засыпать источники воды, которые вне города, и те помогли ему.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20121, 'rst66', '2ch', 32, 4, 'И собралось множество народа, и засыпали все источники и поток, протекавший по стране, говоря: да не найдут цари Ассирийские, придя <i>сюда</i>, много воды.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20122, 'rst66', '2ch', 32, 5, 'И ободрился он, и восстановил всю обрушившуюся стену, и поднял ее до башни, и извне <i>построил</i> другую стену, и укрепил Милло в городе Давидовом, и наготовил множество оружия и щитов.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20123, 'rst66', '2ch', 32, 6, 'И поставил военачальников над народом, и собрал их к себе на площадь у городских ворот, и говорил к сердцу их, и сказал:');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20124, 'rst66', '2ch', 32, 7, 'будьте тверды и мужественны, не бойтесь и не страшитесь царя Ассирийского и всего множества, которое с ним, потому что с нами более, нежели с ним;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20125, 'rst66', '2ch', 32, 8, 'с ним мышца плотская, а с нами Господь, Бог наш, чтобы помогать нам и сражаться на бранях наших. И подкрепился народ словами Езекии, царя Иудейского.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20126, 'rst66', '2ch', 32, 9, 'После сего послал Сеннахирим, царь Ассирийский, рабов своих в Иерусалим, &mdash; сам он <i>стоял</i> против Лахиса, и вся сила его с ним, &mdash; к Езекии, царю Иудейскому, и ко всем Иудеям, которые в Иерусалиме, сказать:');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20127, 'rst66', '2ch', 32, 10, 'так говорит Сеннахирим, царь Ассирийский: на что вы надеетесь и сидите в крепости в Иерусалиме?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20128, 'rst66', '2ch', 32, 11, 'Не обольщает ли вас Езекия, чтобы предать вас смерти от голода и жажды, говоря: «Господь, Бог наш, спасет нас от руки царя Ассирийского»?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20129, 'rst66', '2ch', 32, 12, 'Не этот ли Езекия разрушил высоты Его и жертвенники Его, и сказал Иудее и Иерусалиму: «пред жертвенником единым поклоняйтесь и на нем совершайте курения»?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20130, 'rst66', '2ch', 32, 13, 'Разве вы не знаете, что сделал я и отцы мои со всеми народами земель? Могли ли боги народов земных спасти землю свою от руки моей?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20131, 'rst66', '2ch', 32, 14, 'Кто из всех богов народов, истребленных отцами моими, мог спасти народ свой от руки моей? <i>Как же</i> возможет ваш Бог спасти вас от руки моей?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20132, 'rst66', '2ch', 32, 15, 'И ныне пусть не обольщает вас Езекия и не отклоняет вас таким образом; не верьте ему: если не в силах был ни один бог ни одного народа и царства спасти народ свой от руки моей и от руки отцов моих, то и ваш Бог не спасет вас от руки моей.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20133, 'rst66', '2ch', 32, 16, 'И еще <i>многое</i> говорили рабы его против Господа Бога и против Езекии, раба Его.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20134, 'rst66', '2ch', 32, 17, 'И письма писал он, <i>в которых</i> поносил Господа, Бога Израилева, и говорил против Него такие слова: как боги народов земных не спасли народов своих от руки моей, так Бог Езекии не спасет народа Своего от руки моей.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20135, 'rst66', '2ch', 32, 18, 'И кричали громким голосом на Иудейском языке к народу Иерусалимскому, который <i>был</i> на стене, чтоб устрашить его и напугать его, и взять город.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20136, 'rst66', '2ch', 32, 19, 'И говорили о Боге Иерусалима, как о богах народов земли, &mdash; изделии рук человеческих.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20137, 'rst66', '2ch', 32, 20, 'И помолился царь Езекия и Исаия, сын Амосов, пророк, и возопили к небу.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20138, 'rst66', '2ch', 32, 21, 'И послал Господь Ангела, и он истребил всех храбрых и главноначальствующего и начальствующих в войске царя Ассирийского. И возвратился он со стыдом в землю свою; и когда пришел в дом бога своего, &mdash; исшедшие из чресл его поразили его там мечом.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20139, 'rst66', '2ch', 32, 22, 'Так спас Господь Езекию и жителей Иерусалима от руки Сеннахирима, царя Ассирийского, и от руки всех и оберегал их отовсюду.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20140, 'rst66', '2ch', 32, 23, 'Тогда многие приносили дары Господу в Иерусалим и дорогие вещи Езекии, царю Иудейскому. И он возвеличился после сего в глазах всех народов.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20141, 'rst66', '2ch', 32, 24, 'В те дни заболел Езекия смертельно. И помолился Господу, и Он услышал его и дал ему знамение.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20142, 'rst66', '2ch', 32, 25, 'Но не воздал Езекия за оказанные ему благодеяния, ибо возгордилось сердце его. И был на него гнев <i>Божий</i> и на Иудею, и на Иерусалим.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20143, 'rst66', '2ch', 32, 26, 'Но как смирился Езекия в гордости сердца своего, &mdash; сам и жители Иерусалима, &mdash; то не пришел на них гнев Господень во дни Езекии.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20144, 'rst66', '2ch', 32, 27, 'И было у Езекии богатства и славы весьма много, и хранилище он сделал у себя для серебра и золота, и камней драгоценных, и для ароматов и щитов, и для всяких драгоценных сосудов;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20145, 'rst66', '2ch', 32, 28, 'и кладовые для произведений <i>земли</i>, для хлеба, вина и масла, и стойла для всякого рода скота, и дворы для стад.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20146, 'rst66', '2ch', 32, 29, 'И города построил себе. И стад мелкого и крупного скота <i>было у него</i> множество, потому что дал ему Бог весьма большое имущество.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20147, 'rst66', '2ch', 32, 30, 'Он же, Езекия, запер верхний проток вод Геона и провел их вниз к западной стороне города Давидова. И действовал успешно Езекия во всяком деле своем.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20148, 'rst66', '2ch', 32, 31, 'Только при послах царей Вавилонских, которые присылали к нему спросить о знамении, бывшем на земле, оставил его Бог, чтоб испытать его и открыть все, что у него на сердце.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20149, 'rst66', '2ch', 32, 32, 'Прочие деяния Езекии и добродетели его описаны в видении Исаии, сына Амосова, пророка, и в книге царей Иудейских и Израильских.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20150, 'rst66', '2ch', 32, 33, 'И почил Езекия с отцами своими, и похоронили его над гробницами сыновей Давидовых, и почесть воздали ему по смерти его все Иудеи и жители Иерусалима. И воцарился Манассия, сын его, вместо него.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20151, 'rst66', '2ch', 33, 1, 'Двенадцати лет <i>был</i> Манассия, когда воцарился, и пятьдесят пять лет царствовал в Иерусалиме,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20152, 'rst66', '2ch', 33, 2, 'и делал он неугодное в очах Господних, подражая мерзостям народов, которых прогнал Господь от лица сынов Израилевых,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20153, 'rst66', '2ch', 33, 3, 'и снова построил высоты, которые разрушил Езекия, отец его, и поставил жертвенники Ваалам, и устроил дубравы, и поклонялся всему воинству небесному, и служил ему,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20154, 'rst66', '2ch', 33, 4, 'и соорудил жертвенники в доме Господнем, о котором сказал Господь: «в Иерусалиме будет имя Мое вечно»;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20155, 'rst66', '2ch', 33, 5, 'и соорудил жертвенники всему воинству небесному на обоих дворах дома Господня.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20156, 'rst66', '2ch', 33, 6, 'Он же проводил сыновей своих чрез огонь в долине сына Енномова, и гадал, и ворожил, и чародействовал, и учредил вызывателей мертвецов и волшебников; много делал он неугодного в очах Господа, к прогневлению Его.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20157, 'rst66', '2ch', 33, 7, 'И поставил резного идола, которого сделал, в доме Божием, о котором говорил Бог Давиду и Соломону, сыну его: «в доме сем и в Иерусалиме, который Я избрал из всех колен Израилевых, Я положу имя Мое навек;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20158, 'rst66', '2ch', 33, 8, 'и не дам впредь выступить ноге Израиля из земли сей, которую Я укрепил за отцами их, если только они будут стараться исполнять все, что Я заповедал им, по всему закону и уставам и повелениям, <i>данным</i> рукою Моисея».');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20159, 'rst66', '2ch', 33, 9, 'Но Манассия довел Иудею и жителей Иерусалима до того, что они поступали хуже тех народов, которых истребил Господь от лица сынов Израилевых.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20160, 'rst66', '2ch', 33, 10, 'И говорил Господь к Манассии и к народу его, но они не слушали.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20161, 'rst66', '2ch', 33, 11, 'И привел Господь на них военачальников царя Ассирийского, и заковали они Манассию в кандалы и оковали его цепями, и отвели его в Вавилон.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20162, 'rst66', '2ch', 33, 12, 'И в тесноте своей он стал умолять лицо Господа, Бога своего, и глубоко смирился пред Богом отцов своих.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20163, 'rst66', '2ch', 33, 13, 'И помолился Ему, и <i>Бог</i> преклонился к нему и услышал моление его, и возвратил его в Иерусалим на царство его. И узнал Манассия, что Господь есть Бог.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20164, 'rst66', '2ch', 33, 14, 'И после того построил внешнюю стену города Давидова, на западной стороне Геона, по лощине и до входа в Рыбные ворота, и провел ее вокруг Офела и высоко поднял ее. И поставил военачальников по всем укрепленным городам в Иудее,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20165, 'rst66', '2ch', 33, 15, 'и низверг чужеземных богов и идола из дома Господня, и все капища, которые соорудил на горе дома Господня и в Иерусалиме, и выбросил их за город.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20166, 'rst66', '2ch', 33, 16, 'И восстановил жертвенник Господень и принес на нем жертвы мирные и хвалебные, и сказал Иудеям, чтобы они служили Господу, Богу Израилеву.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20167, 'rst66', '2ch', 33, 17, 'Но народ еще приносил жертвы на высотах, хотя и Господу, Богу своему.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20168, 'rst66', '2ch', 33, 18, 'Прочие дела Манассии, и молитва его к Богу своему, и слова прозорливцев, говоривших к нему именем Господа, Бога Израилева, находятся в записях царей Израилевых.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20169, 'rst66', '2ch', 33, 19, 'И молитва его, и то, что <i>Бог</i> преклонился к нему, и все грехи его и беззакония его, и места, на которых он построил высоты и поставил изображения Астарты и истуканов, прежде нежели смирился, описаны в записях Хозая.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20170, 'rst66', '2ch', 33, 20, 'И почил Манассия с отцами своими, и похоронили его в доме его. И воцарился Амон, сын его, вместо него.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20171, 'rst66', '2ch', 33, 21, 'Двадцати двух лет был Амон, когда воцарился, и два года царствовал в Иерусалиме.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20172, 'rst66', '2ch', 33, 22, 'И делал неугодное в очах Господних так, как делал Манассия, отец его; и всем истуканам, которых сделал Манассия, отец его, приносил Амон жертвы и служил им.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20173, 'rst66', '2ch', 33, 23, 'И не смирился пред лицом Господним, как смирился Манассия, отец его; напротив, Амон умножил <i>свои</i> грехи.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20174, 'rst66', '2ch', 33, 24, 'И составили против него заговор слуги его, и умертвили его в доме его.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20175, 'rst66', '2ch', 33, 25, 'Но народ земли перебил всех, бывших в заговоре против царя Амона, и воцарил народ земли Иосию, сына его, вместо него.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20176, 'rst66', '2ch', 34, 1, 'Восемь лет было Иосии, когда он воцарился, и тридцать один год царствовал в Иерусалиме,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20177, 'rst66', '2ch', 34, 2, 'и делал он угодное в очах Господних, и ходил путями Давида, отца своего, и не уклонялся ни направо, ни налево.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20178, 'rst66', '2ch', 34, 3, 'В восьмой год царствования своего, будучи еще отроком, он начал прибегать к Богу Давида, отца своего, а в двенадцатый год начал очищать Иудею и Иерусалим от высот и <i>посвященных</i> дерев и от резных и литых кумиров.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20179, 'rst66', '2ch', 34, 4, 'И разрушили пред лицом его жертвенники Ваалов и статуи, возвышавшиеся над ними; и <i>посвященные</i> дерева он срубил, и резные и литые кумиры изломал и разбил в прах, и рассыпал на гробах тех, которые приносили им жертвы,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20180, 'rst66', '2ch', 34, 5, 'и кости жрецов сжег на жертвенниках их, и очистил Иудею и Иерусалим,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20181, 'rst66', '2ch', 34, 6, 'и в городах Манассии, и Ефрема, и Симеона, <i>даже</i> до колена Неффалимова, и в опустошенных окрестностях их');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20182, 'rst66', '2ch', 34, 7, 'он разрушил жертвенники и <i>посвященные</i> дерева, и кумиры разбил в прах, и все статуи сокрушил по всей земле Израильской, и возвратился в Иерусалим.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20183, 'rst66', '2ch', 34, 8, 'В восемнадцатый год царствования своего, по очищении земли и дома <i>Божия</i>, он послал Шафана, сына Ацалии, и Маасею градоначальника, и Иоаха, сына Иоахазова, дееписателя, возобновить дом Господа, Бога своего.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20184, 'rst66', '2ch', 34, 9, 'И пришли они к Хелкии первосвященнику, и отдали серебро, принесенное в дом Божий, которое левиты, стоящие на страже у порога, собрали из рук Манассии и Ефрема и всех прочих Израильтян, и от всего Иуды и Вениамина, и от жителей Иерусалима,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20185, 'rst66', '2ch', 34, 10, 'и отдали в руки производителям работ, приставленным к дому Господню, чтоб они раздавали его работникам, которые работали в доме Господнем при исправлении и возобновлении дома.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20186, 'rst66', '2ch', 34, 11, 'И они раздавали плотникам и строителям на покупку тесаных камней и дерев для связей и для покрытия зданий, которые разорили цари Иудейские.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20187, 'rst66', '2ch', 34, 12, 'Люди сии действовали честно при работе, и для надзора над ними поставлены были Иахаф и Овадия, левиты из сыновей Мерариных, и Захария и Мешуллам из сыновей Каафовых, и все левиты, умеющие играть на музыкальных орудиях.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20188, 'rst66', '2ch', 34, 13, 'Они же <i>были</i> приставниками над носильщиками и наблюдали над всеми работниками при каждой работе; из левитов же <i>были и</i> писцы, и надзиратели, и привратники.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20189, 'rst66', '2ch', 34, 14, 'Когда вынимали они серебро, принесенное в дом Господень, тогда Хелкия священник нашел книгу закона Господня, <i>данную</i> рукою Моисея.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20190, 'rst66', '2ch', 34, 15, 'И начал Хелкия, и сказал Шафану писцу: книгу закона нашел я в доме Господнем. И подал Хелкия ту книгу Шафану.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20191, 'rst66', '2ch', 34, 16, 'И понес Шафан книгу к царю, и принес при этом царю известие: все, что поручено рабам твоим, они делают;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20192, 'rst66', '2ch', 34, 17, 'и высыпали серебро, найденное в доме Господнем, и передали его в руки приставникам и в руки производителям работ.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20193, 'rst66', '2ch', 34, 18, 'И <i>также</i> донес Шафан писец царю, говоря: книгу дал мне Хелкия священник. И читал ее Шафан перед царем.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20194, 'rst66', '2ch', 34, 19, 'Когда услышал царь слова закона, то разодрал одежды свои.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20195, 'rst66', '2ch', 34, 20, 'И дал царь повеление Хелкии и Ахикаму, сыну Шафанову, и Авдону, сыну Михея, и Шафану писцу, и Асаии, слуге царскому, говоря:');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20196, 'rst66', '2ch', 34, 21, 'пойдите, вопросите Господа за меня и за оставшихся у Израиля и за Иуду о словах сей найденной книги, потому что велик гнев Господа, который воспылал на нас за то, что не соблюдали отцы наши сло&#769;ва Господня, чтобы поступать по всему, написанному в книге сей.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20197, 'rst66', '2ch', 34, 22, 'И пошел Хелкия, и те, которые от царя, к Олдане пророчице, жене Шаллума, сына Тавкегафа, сына Хасры, хранителя одежд, &mdash; а жила она во второй части Иерусалима &mdash; и говорили с нею об этом.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20198, 'rst66', '2ch', 34, 23, 'И она сказала им: так говорит Господь, Бог Израилев: скажите тому человеку, который послал вас ко мне:');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20199, 'rst66', '2ch', 34, 24, 'так говорит Господь: вот, Я наведу бедствие на место сие и на жителей его все проклятия, написанные в книге, которую читали пред лицом царя Иудейского,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20200, 'rst66', '2ch', 34, 25, 'за то, что они оставили Меня и кадили богам другим, чтобы прогневлять Меня всеми делами рук своих. И гнев Мой возгорится над местом сим и не угаснет.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20201, 'rst66', '2ch', 34, 26, 'А царю Иудейскому, пославшему вас вопросить Господа, так скажите: так говорит Господь, Бог Израилев, о словах, которые ты слышал:');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20202, 'rst66', '2ch', 34, 27, 'так как смягчилось сердце твое, и ты смирился пред Богом, услышав слова Его о месте сем и о жителях его, &mdash; и ты смирился предо Мною, и разодрал одежды свои, и плакал предо Мною, то и Я услышал <i>тебя</i>, говорит Господь.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20203, 'rst66', '2ch', 34, 28, 'Вот, Я приложу тебя к отцам твоим, и положен будешь в гробницу твою в мире, и не увидят глаза твои всего того бедствия, которое Я наведу на место сие и на жителей его. И принесли царю ответ.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20204, 'rst66', '2ch', 34, 29, 'И послал царь, и собрал всех старейшин Иудеи и Иерусалима,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20205, 'rst66', '2ch', 34, 30, 'и пошел царь в дом Господень, и <i>с ним</i> все Иудеи и жители Иерусалима, и священники и левиты, и весь народ, от большого до малого; и он прочитал вслух их все слова книги завета, найденной в доме Господнем.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20206, 'rst66', '2ch', 34, 31, 'И стал царь на месте своем, и заключил завет пред лицом Господа последовать Господу и соблюдать заповеди Его и откровения Его, и уставы Его, от всего сердца своего и от всей души своей, чтобы выполнить слова завета, написанные в книге сей.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20207, 'rst66', '2ch', 34, 32, 'И велел царь подтвердить <i>это</i> всем находившимся в Иерусалиме и в земле Вениаминовой; и стали поступать жители Иерусалима по завету Бога, Бога отцов своих.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20208, 'rst66', '2ch', 34, 33, 'И изверг Иосия все мерзости из всех земель, которые у сынов Израилевых, и повелел всем, находившимся в <i>земле</i> Израилевой, служить Господу, Богу своему. И во все дни <i>жизни</i> его они не отступали от Господа, Бога отцов своих.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20209, 'rst66', '2ch', 35, 1, 'И совершил Иосия в Иерусалиме пасху Господу, и закололи пасхального агнца в четырнадцатый <i>день</i> первого месяца.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20210, 'rst66', '2ch', 35, 2, 'И поставил он священников на местах их, и ободрял их на служение в доме Господнем,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20211, 'rst66', '2ch', 35, 3, 'и сказал левитам, наставникам всех Израильтян, посвященным Господу: поставьте ковчег святой в храме, который построил Соломон, сын Давидов, царь Израилев; нет вам нужды носить <i>его</i> на раменах; служите теперь Господу, Богу нашему, и народу Его, Израилю;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20212, 'rst66', '2ch', 35, 4, 'станьте по поколениям вашим, по чередам вашим, как предписано Давидом, царем Израилевым, и как предписано Соломоном, сыном его,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20213, 'rst66', '2ch', 35, 5, 'и стойте во святилище, по распределениям поколений у братьев ваших, сынов народа, и по разделению поколений у левитов,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20214, 'rst66', '2ch', 35, 6, 'и заколите пасхального агнца, и освятитесь, и приготовьте его для братьев ваших, поступая согласно со словом Господним чрез Моисея.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20215, 'rst66', '2ch', 35, 7, 'И дал Иосия в дар сынам народа, всем, находившимся там, из мелкого скота агнцев и козлов молодых, все для жертвы пасхальной, числом тридцать тысяч, и три тысячи волов. Это из имущества царя.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20216, 'rst66', '2ch', 35, 8, 'И князья его по усердию давали в дар народу, священникам и левитам: Хелкия и Захария и Иехиил, начальствующие в доме Божием, дали священникам для жертвы пасхальной две тысячи шестьсот <i>овец, агнцев и козлов</i> и триста волов;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20217, 'rst66', '2ch', 35, 9, 'и Хонания, и Шемаия, и Нафанаил, братья его, и Хашавия, и Иеиел, и Иозавад, начальники левитов, подарили левитам для жертвы пасхальной <i>овец</i> пять тысяч и пятьсот волов.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20218, 'rst66', '2ch', 35, 10, 'Так устроено было служение. И стали священники на место свое и левиты по чередам своим, по повелению царскому;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20219, 'rst66', '2ch', 35, 11, 'и закололи пасхального агнца. И кропили священники <i>кровью</i>, принимая ее из рук левитов, а левиты снимали кожу;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20220, 'rst66', '2ch', 35, 12, 'и распределили <i>назначенное</i> для всесожжения, чтобы раздать то по отделениям поколений у сынов народа, для принесения Господу, как написано в книге Моисеевой. То же <i>сделали</i> и с волами.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20221, 'rst66', '2ch', 35, 13, 'И испекли пасхального агнца на огне, по уставу; и священные жертвы сварили в котлах, горшках и кастрюлях, и поспешно раздали всему народу,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20222, 'rst66', '2ch', 35, 14, 'а после приготовили для себя и для священников, ибо священники, сыны Аароновы, <i>заняты были</i> приношением всесожжения и туков до ночи; потому‐то и готовили левиты для себя и для священников, сынов Аароновых.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20223, 'rst66', '2ch', 35, 15, 'И певцы, сыновья Асафовы, <i>оставались</i> на местах своих, по установлению Давида и Асафа, и Емана и Идифуна, прозорливца царского, и привратники у каждых ворот: не для чего <i>было</i> им отходить от служения своего, так как братья их левиты готовили для них.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20224, 'rst66', '2ch', 35, 16, 'Так устроено было все служение Господу в тот день, чтобы совершить пасху и принести всесожжения на жертвеннике Господнем, по повелению царя Иосии.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20225, 'rst66', '2ch', 35, 17, 'И совершали сыны Израилевы, находившиеся <i>там</i>, пасху в то время и праздник опресноков в течение семи дней.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20226, 'rst66', '2ch', 35, 18, 'И не была совершаема такая пасха у Израиля от дней Самуила пророка; и из всех царей Израилевых ни один не совершал такой пасхи, какую совершил Иосия, и священники, и левиты, и все Иудеи, и Израильтяне, <i>там</i> находившиеся, и жители Иерусалима.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20227, 'rst66', '2ch', 35, 19, 'В восемнадцатый год царствования Иосии совершена сия пасха.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20228, 'rst66', '2ch', 35, 20, 'После всего того, что сделал Иосия в доме <i>Божием</i>, пошел Нехао, царь Египетский, на войну к Кархемису на Евфрате; и Иосия вышел навстречу ему.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20229, 'rst66', '2ch', 35, 21, 'И послал к нему <i>Нехао</i> послов сказать: что мне и тебе, царь Иудейский? Не против тебя теперь <i>иду я</i>, но туда, где у меня война. И Бог повелел мне поспешать; не противься Богу, Который со мною, чтоб Он не погубил тебя.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20230, 'rst66', '2ch', 35, 22, 'Но Иосия не отстранился от него, а приготовился, чтобы сразиться с ним, и не послушал слов Нехао от лица Божия и выступил на сражение на равнину Мегиддо.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20231, 'rst66', '2ch', 35, 23, 'И выстрелили стрельцы в царя Иосию, и сказал царь слугам своим: уведите меня, потому что я тяжело ранен.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20232, 'rst66', '2ch', 35, 24, 'И свели его слуги его с колесницы, и посадили его в другую повозку, которая <i>была</i> у него, и отвезли его в Иерусалим. И умер он, и похоронен в гробницах отцов своих. И вся Иудея и Иерусалим оплакали Иосию.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20233, 'rst66', '2ch', 35, 25, 'Оплакал Иосию и Иеремия в песне плачевной; и говорили все певцы и певицы об Иосии в плачевных песнях своих, <i>известных</i> до сего дня, и передали их в употребление у Израиля; и вот, они вписаны в <i>книгу</i> плачевных песней.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20234, 'rst66', '2ch', 35, 26, 'Прочие деяния Иосии и добродетели его, согласные с предписанным в законе Господнем,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20235, 'rst66', '2ch', 35, 27, 'и деяния его, первые и последние, описаны в книге царей Израильских и Иудейских.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20236, 'rst66', '2ch', 36, 1, 'И взял народ земли Иоахаза, сына Иосиина, и воцарили его, вместо отца его, в Иерусалиме.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20237, 'rst66', '2ch', 36, 2, 'Двадцати трех лет был Иоахаз, когда воцарился, и три месяца царствовал в Иерусалиме.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20238, 'rst66', '2ch', 36, 3, 'И низложил его царь Египетский в Иерусалиме, и наложил на землю пени сто талантов серебра и талант золота.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20239, 'rst66', '2ch', 36, 4, 'И воцарил царь Египетский над Иудеею и Иерусалимом Елиакима, брата его, и переменил имя его на Иоакима, а Иоахаза, брата его, взял Нехао и отвел его в Египет.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20240, 'rst66', '2ch', 36, 5, 'Двадцати пяти лет <i>был</i> Иоаким, когда воцарился, и одиннадцать лет царствовал в Иерусалиме. И делал он неугодное в очах Господа, Бога своего.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20241, 'rst66', '2ch', 36, 6, 'Против него вышел Навуходоносор, царь Вавилонский, и оковал его оковами, чтоб отвести его в Вавилон.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20242, 'rst66', '2ch', 36, 7, 'И часть сосудов дома Господня перенес Навуходоносор в Вавилон и положил их в капище своем в Вавилоне.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20243, 'rst66', '2ch', 36, 8, 'Прочие дела Иоакима и мерзости его, какие он делал и какие найдены в нем, описаны в книге царей Израильских и Иудейских. И воцарился Иехония, сын его, вместо него.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20244, 'rst66', '2ch', 36, 9, 'Восемнадцати лет <i>был</i> Иехония, когда воцарился, и три месяца и десять дней царствовал в Иерусалиме, и делал он неугодное в очах Господних.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20245, 'rst66', '2ch', 36, 10, 'По прошествии года послал царь Навуходоносор и велел взять его в Вавилон вместе с драгоценными сосудами дома Господня, и воцарил над Иудеею и Иерусалимом Седекию, брата его.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20246, 'rst66', '2ch', 36, 11, 'Двадцати одного года <i>был</i> Седекия, когда воцарился, и одиннадцать лет царствовал в Иерусалиме,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20247, 'rst66', '2ch', 36, 12, 'и делал он неугодное в очах Господа, Бога своего. Он не смирился пред Иеремиею пророком, <i>пророчествовавшим</i> от уст Господних,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20248, 'rst66', '2ch', 36, 13, 'и отложился от царя Навуходоносора, взявшего клятву с него <i>именем</i> Бога, &mdash; и сделал упругою шею свою и ожесточил сердце свое до того, что не обратился к Господу, Богу Израилеву.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20249, 'rst66', '2ch', 36, 14, 'Да и все начальствующие над священниками и над народом много грешили, подражая всем мерзостям язычников, и сквернили дом Господа, который Он освятил в Иерусалиме.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20250, 'rst66', '2ch', 36, 15, 'И посылал к ним Господь, Бог отцов их, посланников Своих от раннего утра, потому что Он жалел Свой народ и Свое жилище.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20251, 'rst66', '2ch', 36, 16, 'Но они издевались над посланными от Бога и пренебрегали словами Его, и ругались над пророками Его, доколе не сошел гнев Господа на народ Его, так что не было <i>ему</i> спасения.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20252, 'rst66', '2ch', 36, 17, 'И Он навел на них царя Халдейского, &mdash; и тот умертвил юношей их мечом в доме святыни их и не пощадил ни юноши, ни девицы, ни старца, ни седовласого: все предал <i>Бог</i> в руку его.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20253, 'rst66', '2ch', 36, 18, 'И все сосуды дома Божия, большие и малые, и сокровища дома Господня, и сокровища царя и князей его, все принес он в Вавилон.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20254, 'rst66', '2ch', 36, 19, 'И сожгли дом Божий, и разрушили стену Иерусалима, и все чертоги его сожгли огнем, и все драгоценности его истребили.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20255, 'rst66', '2ch', 36, 20, 'И переселил он оставшихся от меча в Вавилон, и были они рабами его и сыновей его, до воцарения царя Персидского,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20256, 'rst66', '2ch', 36, 21, 'доколе, во исполнение слова Господня, <i>сказанного</i> устами Иеремии, земля не отпраздновала суббот своих. Во все дни запустения она субботствовала до исполнения семидесяти лет.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20257, 'rst66', '2ch', 36, 22, 'А в первый год Кира, царя Персидского, во исполнение слова Господня, <i>сказанного</i> устами Иеремии, возбудил Господь дух Кира, царя Персидского, и он велел объявить по всему царству своему, словесно и письменно, и сказать:');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20258, 'rst66', '2ch', 36, 23, 'Так говорит Кир, царь Персидский: все царства земли дал мне Господь, Бог небесный, и Он повелел мне построить Ему дом в Иерусалиме, что в Иудее. Кто есть из вас &mdash; из всего народа Его, <i>да будет</i> Господь, Бог его, с ним, и пусть он туда идет.');",
        ];

