    public function up()
    {
        $list = [
            "INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20945, 'rst66', 'est', 1, 1, 'И было во дни Артаксеркса, &mdash; этот Артаксеркс царствовал над ста двадцатью семью областями от Индии и до Ефиопии, &mdash;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20946, 'rst66', 'est', 1, 2, 'в то время, как царь Артаксеркс сел на царский престол свой, что в Сузах, городе престольном,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20947, 'rst66', 'est', 1, 3, 'в третий год своего царствования он сделал пир для всех князей своих и для служащих при нем, для главных начальников войска Персидского и Мидийского и для правителей областей своих,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20948, 'rst66', 'est', 1, 4, 'показывая великое богатство царства своего и отличный блеск величия своего <i>в течение</i> многих дней, ста восьмидесяти дней.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20949, 'rst66', 'est', 1, 5, 'По окончании сих дней сделал царь для народа своего, находившегося в престольном городе Сузах, от большого до малого, пир семидневный на садовом дворе дома царского.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20950, 'rst66', 'est', 1, 6, 'Белые, бумажные, и яхонтового цвета шерстяные ткани, прикрепленные виссонными и пурпуровыми шнурами, <i>висели</i> на серебряных кольцах и мраморных столбах.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20951, 'rst66', 'est', 1, 7, 'Золотые и серебряные ложа <i>были</i> на помосте, устланном камнями зеленого цвета и мрамором, и перламутром, и камнями черного цвета.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20952, 'rst66', 'est', 1, 8, 'Напитки подаваемы <i>были</i> в золотых сосудах и сосудах разнообразных, ценою в тридцать тысяч талантов; и вина царского было множество, по богатству царя. Питье <i>шло</i> чинно, никто не принуждал, потому что царь дал такое приказание всем управляющим в доме его, чтобы делали по воле каждого.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20953, 'rst66', 'est', 1, 9, 'И царица Астинь сделала также пир для женщин в царском доме царя Артаксеркса.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20954, 'rst66', 'est', 1, 10, 'В седьмой день, когда развеселилось сердце царя от вина, он сказал Мегуману, Бизфе, Харбоне, Бигфе и Авагфе, Зефару и Каркасу &mdash; семи евнухам, служившим пред лицом царя Артаксеркса,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20955, 'rst66', 'est', 1, 11, 'чтобы они привели царицу Астинь пред лицо царя в венце царском для того, чтобы показать народам и князьям красоту ее; потому что она была очень красива.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20956, 'rst66', 'est', 1, 12, 'Но царица Астинь не захотела прийти по приказанию царя, <i>объявленному</i> чрез евнухов.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20957, 'rst66', 'est', 1, 13, 'И разгневался царь сильно, и ярость его загорелась в нем. И сказал царь мудрецам, знающим <i>прежние</i> времена &mdash; ибо дела царя <i>делались</i> пред всеми, знающими закон и права, &mdash;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20958, 'rst66', 'est', 1, 14, 'приближенными же к нему <i>тогда были:</i> Каршена, Шефар, Адмафа, Фарсис, Мерес, Марсена, Мемухан &mdash; семь князей Персидских и Мидийских, которые могли видеть лицо царя <i>и</i> сидели первыми в царстве:');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20959, 'rst66', 'est', 1, 15, 'как поступить по закону с царицею Астинь за то, что она не сделала по слову царя Артаксеркса, <i>объявленному</i> чрез евнухов?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20960, 'rst66', 'est', 1, 16, 'И сказал Мемухан пред лицом царя и князей: не пред царем одним виновна царица Астинь, а пред всеми князьями и пред всеми народами, которые по всем областям царя Артаксеркса;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20961, 'rst66', 'est', 1, 17, 'потому что поступок царицы дойдет до всех жен, и они будут пренебрегать мужьями своими и говорить: «царь Артаксеркс велел привести царицу Астинь пред лицо свое, а она не пошла».');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20962, 'rst66', 'est', 1, 18, 'Теперь княгини Персидские и Мидийские, которые услышат о поступке царицы, будут <i>то же</i> говорить всем князьям царя; и пренебрежения и огорчения будет довольно.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20963, 'rst66', 'est', 1, 19, 'Если благоугодно царю, пусть выйдет от него царское постановление и впишется в законы Персидские и Мидийские и не отменяется, о том, что Астинь не будет входить пред лицо царя Артаксеркса, а царское достоинство ее царь передаст другой, которая лучше ее.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20964, 'rst66', 'est', 1, 20, 'Когда услышат о сем постановлении царя, которое разойдется по всему царству его, как оно ни велико, тогда все жены будут почитать мужей своих, от большого до малого.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20965, 'rst66', 'est', 1, 21, 'И угодно было слово сие в глазах царя и князей; и сделал царь по слову Мемухана.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20966, 'rst66', 'est', 1, 22, 'И послал во все области царя письма, писанные в каждую область письменами ее и к каждому народу на языке его, чтобы всякий муж был господином в доме своем, и чтобы это было объявлено каждому на природном языке его.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20967, 'rst66', 'est', 2, 1, 'После сего, когда утих гнев царя Артаксеркса, он вспомнил об Астинь и о том, что она сделала, и что было определено о ней.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20968, 'rst66', 'est', 2, 2, 'И сказали отроки царя, служившие при нем: пусть бы поискали царю молодых красивых девиц,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20969, 'rst66', 'est', 2, 3, 'и пусть бы назначил царь наблюдателей во все области своего царства, которые собрали бы всех молодых девиц, красивых видом, в престольный город Сузы, в дом жен под надзор Гегая, царского евнуха, стража жен, и пусть бы выдавали им притиранья;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20970, 'rst66', 'est', 2, 4, 'и девица, которая понравится глазам царя, пусть будет царицею вместо Астинь. И угодно было слово это в глазах царя, и он так и сделал.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20971, 'rst66', 'est', 2, 5, 'Был в Сузах, городе престольном, один Иудеянин, имя его Мардохей, сын Иаира, сын Семея, сын Киса, из колена Вениаминова.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20972, 'rst66', 'est', 2, 6, 'Он был переселен из Иерусалима вместе с пленниками, выведенными с Иехониею, царем Иудейским, которых переселил Навуходоносор, царь Вавилонский.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20973, 'rst66', 'est', 2, 7, 'И был он воспитателем Гадассы, &mdash; она же Есфирь, &mdash; дочери дяди его, так как не было у нее ни отца, ни матери. Девица эта была красива станом и пригожа лицом. И по смерти отца ее и матери ее Мардохей взял ее к себе вместо дочери.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20974, 'rst66', 'est', 2, 8, 'Когда объявлено было повеление царя и указ его, и когда собраны были многие девицы в престольный город Сузы под надзор Гегая, тогда взята была и Есфирь в царский дом под надзор Гегая, стража жен.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20975, 'rst66', 'est', 2, 9, 'И понравилась эта девица глазам его и приобрела у него благоволение, и он поспешил выдать ей притиранья и <i>все, назначенное на</i> часть ее, и приставить к ней семь девиц, достойных быть при ней, из дома царского, и переместил ее и девиц ее в лучшее отделение женского дома.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20976, 'rst66', 'est', 2, 10, 'Не сказывала Есфирь ни о народе своем, ни о родстве своем, потому что Мардохей дал ей приказание, чтобы она не сказывала.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20977, 'rst66', 'est', 2, 11, 'И всякий день Мардохей приходил ко двору женского дома, чтобы наведываться о здоровье Есфири и о том, что делается с нею.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20978, 'rst66', 'est', 2, 12, 'Когда наступало время каждой девице входить к царю Артаксерксу, после того, как в течение двенадцати месяцев выполнено было над нею все, определенное женщинам, &mdash; ибо столько времени продолжались дни притиранья их: шесть месяцев мирровым маслом и шесть месяцев ароматами и другими притираньями женскими, &mdash;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20979, 'rst66', 'est', 2, 13, 'Тогда девица входила к царю. Чего бы она ни потребовала, ей давали всё для выхода из женского дома в дом царя.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20980, 'rst66', 'est', 2, 14, 'Вечером она входила и утром возвращалась в другой дом женский под надзор Шаазгаза, царского евнуха, стража наложниц; и уже не входила к царю, разве только царь пожелал бы ее, и она призывалась бы по имени.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20981, 'rst66', 'est', 2, 15, 'Когда настало время Есфири, дочери Аминадава, дяди Мардохея, который взял ее к себе вместо дочери, &mdash; идти к царю, тогда она не просила ничего, кроме того, о чем сказал ей Гегай, евнух царский, страж жен. И приобрела Есфирь расположение <i>к себе</i> в глазах всех, видевших ее.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20982, 'rst66', 'est', 2, 16, 'И взята была Есфирь к царю Артаксерксу, в царский дом его, в десятом месяце, то есть в месяце Тебефе, в седьмой год его царствования.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20983, 'rst66', 'est', 2, 17, 'И полюбил царь Есфирь более всех жен, и она приобрела его благоволение и благорасположение более всех девиц; и он возложил царский венец на голову ее и сделал ее царицею на место Астинь.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20984, 'rst66', 'est', 2, 18, 'И сделал царь большой пир для всех князей своих и для служащих при нем, &mdash; пир ради Есфири, и сделал льготу областям и роздал дары с царственною щедростью.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20985, 'rst66', 'est', 2, 19, 'И когда во второй раз собраны были девицы, и Мардохей сидел у ворот царских,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20986, 'rst66', 'est', 2, 20, 'Есфирь все еще не сказывала о родстве своем и о народе своем, как приказал ей Мардохей; а слово Мардохея Есфирь выполняла <i>и теперь</i> так же, как тогда, когда была у него на воспитании.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20987, 'rst66', 'est', 2, 21, 'В это время, как Мардохей сидел у ворот царских, два царских евнуха, Гавафа и Фарра, оберегавшие порог, озлобились и замышляли наложить руку на царя Артаксеркса.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20988, 'rst66', 'est', 2, 22, 'Узнав о том, Мардохей сообщил царице Есфири, а Есфирь сказала царю от имени Мардохея.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20989, 'rst66', 'est', 2, 23, 'Дело было исследовано и найдено <i>верным</i>, и их обоих повесили на дереве. И было вписано о благодеянии Мардохея в книгу дневных записей у царя.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20990, 'rst66', 'est', 3, 1, 'После сего возвеличил царь Артаксеркс Амана, сына Амадафа, Вугеянина, и вознес его, и поставил седалище его выше всех князей, которые у него;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20991, 'rst66', 'est', 3, 2, 'и все, служащие при царе, которые <i>были</i> у царских ворот, кланялись и падали ниц пред Аманом, ибо так приказал царь. А Мардохей не кланялся и не падал ниц.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20992, 'rst66', 'est', 3, 3, 'И говорили служащие при царе, которые у царских ворот, Мардохею: зачем ты преступаешь повеление царское?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20993, 'rst66', 'est', 3, 4, 'И как они говорили ему каждый день, а он не слушал их, то они донесли Аману, чтобы посмотреть, устоит ли в слове <i>своем</i> Мардохей, ибо он сообщил им, что он Иудеянин.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20994, 'rst66', 'est', 3, 5, 'И когда увидел Аман, что Мардохей не кланяется и не падает ниц пред ним, то исполнился гнева Аман.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20995, 'rst66', 'est', 3, 6, 'И показалось ему ничтожным наложить руку на одного Мардохея; но так как сказали ему, из какого народа Мардохей, то задумал Аман истребить всех Иудеев, которые <i>были</i> во всем царстве Артаксеркса, как народ Мардохеев.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20996, 'rst66', 'est', 3, 7, 'В первый месяц, который есть месяц Нисан, в двенадцатый год царя Артаксеркса, бросали пур, то есть жребий, пред лицом Амана изо дня в день и из месяца в месяц, <i>и пал жребий</i> на двенадцатый <i>месяц</i>, то есть на месяц Адар.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20997, 'rst66', 'est', 3, 8, 'И сказал Аман царю Артаксерксу: есть один народ, разбросанный и рассеянный между народами по всем областям царства твоего; и законы их отличны от <i>законов</i> всех народов, и законов царя они не выполняют; и царю не следует <i>так</i> оставлять их.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20998, 'rst66', 'est', 3, 9, 'Если царю благоугодно, то пусть будет предписано истребить их, и десять тысяч талантов серебра я отвешу в руки приставников, чтобы внести в казну царскую.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20999, 'rst66', 'est', 3, 10, 'Тогда снял царь перстень свой с руки своей и отдал его Аману, сыну Амадафа, Вугеянину, чтобы скрепить указ против Иудеев.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21000, 'rst66', 'est', 3, 11, 'И сказал царь Аману: отдаю тебе <i>это</i> серебро и народ; поступи с ним, как тебе угодно.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21001, 'rst66', 'est', 3, 12, 'И призваны были писцы царские в первый месяц, в тринадцатый день его, и написано было, как приказал Аман, к сатрапам царским и к начальствующим над каждою областью, и к князьям у каждого народа, в каждую область письменами ее и к каждому народу на языке его: <i>все</i> было написано от имени царя Артаксеркса и скреплено царским перстнем.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21002, 'rst66', 'est', 3, 13, 'И посланы были письма через гонцов во все области царя, чтобы убить, погубить и истребить всех Иудеев, малого и старого, детей и женщин в один день, в тринадцатый день двенадцатого месяца, то есть месяца Адара, и имение их разграбить.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21003, 'rst66', 'est', 3, 14, 'Список с указа отдать в каждую область <i>как</i> закон, объявляемый для всех народов, чтобы они были готовы к тому дню.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21004, 'rst66', 'est', 3, 15, 'Гонцы отправились быстро с царским повелением. Объявлен был указ и в Сузах, престольном городе; и царь и Аман сидели и пили, а город Сузы <i>был</i> в смятении.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21005, 'rst66', 'est', 4, 1, 'Когда Мардохей узнал все, что делалось, разодрал одежды свои и возложил на себя вретище и пепел, и вышел на средину города и взывал с воплем великим и горьким.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21006, 'rst66', 'est', 4, 2, 'И дошел до царских ворот, так как нельзя было входить в царские ворота во вретище.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21007, 'rst66', 'est', 4, 3, 'Равно и во всякой области и месте, куда <i>только</i> доходило повеление царя и указ его, было большое сетование у Иудеев, и пост, и плач, и вопль; вретище и пепел служили постелью для многих.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21008, 'rst66', 'est', 4, 4, 'И пришли служанки Есфири и евнухи ее и рассказали ей, и сильно встревожилась царица. И послала одежды, чтобы Мардохей надел их и снял с себя вретище свое. Но он не принял.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21009, 'rst66', 'est', 4, 5, 'Тогда позвала Есфирь Гафаха, одного из евнухов царя, которого он приставил к ней, и послала его к Мардохею узнать: что это и отчего это?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21010, 'rst66', 'est', 4, 6, 'И пошел Гафах к Мардохею на городскую площадь, которая пред царскими воротами.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21011, 'rst66', 'est', 4, 7, 'И рассказал ему Мардохей обо всем, что с ним случилось, и об определенном числе серебра, которое обещал Аман отвесить в казну царскую за Иудеев, чтобы истребить их;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21012, 'rst66', 'est', 4, 8, 'и вручил ему список с указа, обнародованного в Сузах, об истреблении их, чтобы показать Есфири и дать ей знать <i>обо всем;</i> притом наказывал ей, чтобы она пошла к царю и молила его о помиловании и просила его за народ свой.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21013, 'rst66', 'est', 4, 9, 'И пришел Гафах и пересказал Есфири слова Мардохея.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21014, 'rst66', 'est', 4, 10, 'И сказала Есфирь Гафаху и послала его <i>сказать</i> Мардохею:');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21015, 'rst66', 'est', 4, 11, 'все, служащие при царе, и народы в областях царских знают, что всякому, и мужчине и женщине, кто войдет к царю во внутренний двор, не быв позван, один суд &mdash; смерть; только тот, к кому прострет царь свой золотой скипетр, останется жив. А я не звана к царю вот уже тридцать дней.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21016, 'rst66', 'est', 4, 12, 'И пересказали Мардохею слова Есфири.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21017, 'rst66', 'est', 4, 13, 'И сказал Мардохей в ответ Есфири: не думай, что ты <i>одна</i> спасешься в доме царском из всех Иудеев.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21018, 'rst66', 'est', 4, 14, 'Если ты промолчишь в это время, то свобода и избавление придет для Иудеев из другого места, а ты и дом отца твоего погибнете. И кто знает, не для такого ли времени ты и достигла достоинства царского?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21019, 'rst66', 'est', 4, 15, 'И сказала Есфирь в ответ Мардохею:');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21020, 'rst66', 'est', 4, 16, 'пойди, собери всех Иудеев, находящихся в Сузах, и поститесь ради меня, и не ешьте и не пейте три дня, ни днем, ни ночью, и я с служанками моими буду также поститься и потом пойду к царю, хотя это против закона, и если погибнуть &mdash; погибну.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21021, 'rst66', 'est', 4, 17, 'И пошел Мардохей и сделал, как приказала ему Есфирь.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21022, 'rst66', 'est', 5, 1, 'На третий день Есфирь оделась по‐царски. И стала она на внутреннем дворе царского дома, перед домом царя; царь же сидел <i>тогда</i> на царском престоле своем, в царском доме, прямо против входа в дом. Когда царь увидел царицу Есфирь, стоящую на дворе, она нашла милость в глазах его.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21023, 'rst66', 'est', 5, 2, 'И простер царь к Есфири золотой скипетр, который был в руке его, и подошла Есфирь и коснулась конца скипетра.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21024, 'rst66', 'est', 5, 3, 'И сказал ей царь: что тебе, царица Есфирь, и какая просьба твоя? Даже до полуцарства будет дано тебе.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21025, 'rst66', 'est', 5, 4, 'И сказала Есфирь: если царю благоугодно, пусть придет царь с Аманом сегодня на пир, который я приготовила ему.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21026, 'rst66', 'est', 5, 5, 'И сказал царь: сходите скорее за Аманом, чтобы сделать по слову Есфири. И пришел царь с Аманом на пир, который приготовила Есфирь.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21027, 'rst66', 'est', 5, 6, 'И сказал царь Есфири при питье вина: какое желание твое? оно будет удовлетворено; и какая просьба твоя? <i>хотя бы</i> до полуцарства, она будет исполнена.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21028, 'rst66', 'est', 5, 7, 'И отвечала Есфирь, и сказала: <i>вот</i> мое желание и моя просьба:');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21029, 'rst66', 'est', 5, 8, 'если я нашла благоволение в очах царя, и если царю благоугодно удовлетворить желание мое и исполнить просьбу мою, то пусть царь с Аманом придет на пир, который я приготовлю для них, и завтра я исполню слово царя.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21030, 'rst66', 'est', 5, 9, 'И вышел Аман в тот день веселый и благодушный. Но когда увидел Аман Мардохея у ворот царских, и тот не встал и с места не тронулся пред ним, тогда исполнился Аман гневом на Мардохея.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21031, 'rst66', 'est', 5, 10, 'Однако же скрепился Аман. А когда пришел в дом свой, то послал позвать друзей своих и Зерешь, жену свою.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21032, 'rst66', 'est', 5, 11, 'И рассказывал им Аман о великом богатстве своем и о множестве сыновей своих и обо всем том, как возвеличил его царь и как вознес его над князьями и слугами царскими.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21033, 'rst66', 'est', 5, 12, 'И сказал Аман: да и царица Есфирь никого не позвала с царем на пир, который она приготовила, кроме меня; так и на завтра я зван к ней с царем.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21034, 'rst66', 'est', 5, 13, 'Но всего этого не довольно для меня, доколе я вижу Мардохея Иудеянина сидящим у ворот царских.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21035, 'rst66', 'est', 5, 14, 'И сказала ему Зерешь, жена его, и все друзья его: пусть приготовят дерево вышиною в пятьдесят локтей, и утром скажи царю, чтобы повесили Мардохея на нем, и тогда весело иди на пир с царем. И понравилось это слово Аману, и он приготовил дерево.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21036, 'rst66', 'est', 6, 1, 'В ту ночь Господь отнял сон от царя, и он велел принести памятную книгу дневных записей; и читали их пред царем,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21037, 'rst66', 'est', 6, 2, 'и найдено записанным <i>там</i>, как донес Мардохей на Гавафу и Фарру, двух евнухов царских, оберегавших порог, которые замышляли наложить руку на царя Артаксеркса.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21038, 'rst66', 'est', 6, 3, 'И сказал царь: какая дана почесть и отличие Мардохею за это? И сказали отроки царя, служившие при нем: ничего не сделано ему.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21039, 'rst66', 'est', 6, 4, 'И сказал царь: кто на дворе? Аман же пришел <i>тогда</i> на внешний двор царского дома поговорить с царем, чтобы повесили Мардохея на дереве, которое он приготовил для него.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21040, 'rst66', 'est', 6, 5, 'И сказали отроки царю: вот, Аман стоит на дворе. И сказал царь: пусть войдет.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21041, 'rst66', 'est', 6, 6, 'И вошел Аман. И сказал ему царь: что сделать бы тому человеку, которого царь хочет отличить почестью? Аман подумал в сердце своем: кому <i>другому</i> захочет царь оказать почесть, кроме меня?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21042, 'rst66', 'est', 6, 7, 'И сказал Аман царю: тому человеку, которого царь хочет отличить почестью,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21043, 'rst66', 'est', 6, 8, 'пусть принесут одеяние царское, в которое одевается царь, и <i>приведут</i> коня, на котором ездит царь, возложат царский венец на голову его,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21044, 'rst66', 'est', 6, 9, 'и пусть подадут одеяние и коня в руки одному из первых князей царских, &mdash; и облекут того человека, которого царь хочет отличить почестью, и выведут его на коне на городскую площадь, и провозгласят пред ним: «так делается тому человеку, которого царь хочет отличить почестью!»');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21045, 'rst66', 'est', 6, 10, 'И сказал царь Аману: тотчас же возьми одеяние и коня, как ты сказал, и сделай это Мардохею Иудеянину, сидящему у царских ворот; ничего не опусти из всего, что ты говорил.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21046, 'rst66', 'est', 6, 11, 'И взял Аман одеяние и коня и облек Мардохея, и вывел его на коне на городскую площадь и провозгласил пред ним: «так делается тому человеку, которого царь хочет отличить почестью!»');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21047, 'rst66', 'est', 6, 12, 'И возвратился Мардохей к царским воротам. Аман же поспешил в дом свой, печальный и закрыв голову.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21048, 'rst66', 'est', 6, 13, 'И пересказал Аман Зереши, жене своей, и всем друзьям своим все, что случилось с ним. И сказали ему мудрецы его и Зерешь, жена его: если из племени Иудеев Мардохей, из‐за которого ты начал падать, то не пересилишь его, а наверно падешь пред ним.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21049, 'rst66', 'est', 6, 14, 'Они еще разговаривали с ним, <i>как</i> пришли евнухи царя и стали торопить Амана идти на пир, который приготовила Есфирь.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21050, 'rst66', 'est', 7, 1, 'И пришел царь с Аманом пировать у Есфири царицы.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21051, 'rst66', 'est', 7, 2, 'И сказал царь Есфири также и в <i>этот</i> второй день во время пира: какое желание твое, царица Есфирь? оно будет удовлетворено; и какая просьба твоя? <i>хотя бы</i> до полуцарства, она будет исполнена.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21052, 'rst66', 'est', 7, 3, 'И отвечала царица Есфирь и сказала: если я нашла благоволение в очах твоих, царь, и если царю благоугодно, то да будут дарованы мне жизнь моя, по желанию моему, и народ мой, по просьбе моей!');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21053, 'rst66', 'est', 7, 4, 'Ибо проданы мы, я и народ мой, на истребление, убиение и погибель. Если бы мы проданы были в рабы и рабыни, я молчала бы, хотя враг не вознаградил бы ущерба царя.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21054, 'rst66', 'est', 7, 5, 'И отвечал царь Артаксеркс и сказал царице Есфири: кто это такой, и где тот, который отважился в сердце своем сделать так?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21055, 'rst66', 'est', 7, 6, 'И сказала Есфирь: враг и неприятель &mdash; этот злобный Аман! И Аман затрепетал пред царем и царицею.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21056, 'rst66', 'est', 7, 7, 'И царь встал во гневе своем с пира <i>и пошел</i> в сад при дворце; Аман же остался умолять о жизни своей царицу Есфирь, ибо видел, что определена ему злая участь от царя.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21057, 'rst66', 'est', 7, 8, 'Когда царь возвратился из сада при дворце в дом пира, Аман был припавшим к ложу, на котором находилась Есфирь. И сказал царь: даже и насиловать царицу <i>хочет</i> в доме у меня! Слово вышло из уст царя, &mdash; и накрыли лицо Аману.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21058, 'rst66', 'est', 7, 9, 'И сказал Харбона, один из евнухов при царе: вот и дерево, которое приготовил Аман для Мардохея, говорившего доброе для царя, стоит у дома Амана, вышиною в пятьдесят локтей. И сказал царь: повесьте его на нем.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21059, 'rst66', 'est', 7, 10, 'И повесили Амана на дереве, которое он приготовил для Мардохея. И гнев царя утих.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21060, 'rst66', 'est', 8, 1, 'В тот день царь Артаксеркс отдал царице Есфири дом Амана, врага Иудеев; а Мардохей вошел пред лицо царя, ибо Есфирь объявила, что&#769; он для нее.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21061, 'rst66', 'est', 8, 2, 'И снял царь перстень свой, который он отнял у Амана, и отдал его Мардохею; Есфирь же поставила Мардохея смотрителем над домом Амана.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21062, 'rst66', 'est', 8, 3, 'И продолжала Есфирь говорить пред царем и пала к ногам его, и плакала и умоляла его отвратить злобу Амана Вугеянина и замысел его, который он замыслил против Иудеев.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21063, 'rst66', 'est', 8, 4, 'И простер царь к Есфири золотой скипетр; и поднялась Есфирь, и стала пред лицом царя,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21064, 'rst66', 'est', 8, 5, 'и сказала: если царю благоугодно, и если я нашла благоволение пред лицом его, и справедливо дело сие пред лицом царя, и нравлюсь я очам его, то пусть было бы написано, чтобы возвращены были письма по замыслу Амана, сына Амадафа, Вугеянина, писанные им об истреблении Иудеев во всех областях царя;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21065, 'rst66', 'est', 8, 6, 'ибо как я могу видеть бедствие, которое постигнет народ мой, и как я могу видеть погибель родных моих?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21066, 'rst66', 'est', 8, 7, 'И сказал царь Артаксеркс царице Есфири и Мардохею Иудеянину: вот, я дом Амана отдал Есфири, и его самого повесили на дереве за то, что он налагал руку свою на Иудеев;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21067, 'rst66', 'est', 8, 8, 'напишите и вы о Иудеях, что вам угодно, от имени царя и скрепите царским перстнем, ибо письма, написанного от имени царя и скрепленного перстнем царским, нельзя изменить.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21068, 'rst66', 'est', 8, 9, 'И позваны были тогда царские писцы в третий месяц, то есть в месяц Сиван, в двадцать третий день его, и написано было все так, как приказал Мардохей, к Иудеям, и к сатрапам, и областеначальникам, и правителям областей от Индии до Ефиопии, ста двадцати семи областей, в каждую область письменами ее и к каждому народу на языке его, и к Иудеям письменами их и на языке их.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21069, 'rst66', 'est', 8, 10, 'И написал он от имени царя Артаксеркса, и скрепил царским перстнем, и послал письма чрез гонцов на конях, на дромадерах и мулах царских,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21070, 'rst66', 'est', 8, 11, 'о том, что царь позволяет Иудеям, находящимся во всяком городе, собраться и стать на защиту жизни своей, истребить, убить и погубить всех сильных в народе и в области, которые во вражде с ними, детей и жен, и имение их разграбить,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21071, 'rst66', 'est', 8, 12, 'в один день по всем областям царя Артаксеркса, в тринадцатый день двенадцатого месяца, то есть месяца Адара.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21072, 'rst66', 'est', 8, 13, 'Список с сего указа отдать в каждую область, <i>как</i> закон, объявляемый для всех народов, чтоб Иудеи готовы были к тому дню мстить врагам своим.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21073, 'rst66', 'est', 8, 14, 'Гонцы, поехавшие верхом на быстрых конях царских, погнали скоро и поспешно, с царским повелением. Объявлен был указ и в Сузах, престольном городе.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21074, 'rst66', 'est', 8, 15, 'И Мардохей вышел от царя в царском одеянии яхонтового и белого цвета и в большом золотом венце, и в мантии виссонной и пурпуровой. И город Сузы возвеселился и возрадовался.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21075, 'rst66', 'est', 8, 16, 'А у Иудеев было <i>тогда</i> освещение и радость, и веселье, и торжество.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21076, 'rst66', 'est', 8, 17, 'И во всякой области и во всяком городе, во <i>всяком</i> месте, куда <i>только</i> доходило повеление царя и указ его, была радость у Иудеев и веселье, пиршество и праздничный день. И многие из народов страны сделались Иудеями, потому что напал на них страх пред Иудеями.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21077, 'rst66', 'est', 9, 1, 'В двенадцатый месяц, то есть в месяц Адар, в тринадцатый день его, в который пришло время исполниться повелению царя и указу его, в тот день, когда надеялись неприятели Иудеев взять власть над ними, а вышло наоборот, что сами Иудеи взяли власть над врагами своими, &mdash;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21078, 'rst66', 'est', 9, 2, 'собрались Иудеи в городах своих по всем областям царя Артаксеркса, чтобы наложить руку на зложелателей своих; и никто не мог устоять пред лицом их, потому что страх пред ними напал на все народы.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21079, 'rst66', 'est', 9, 3, 'И все князья в областях и сатрапы, и областеначальники, и исполнители дел царских поддерживали Иудеев, потому что напал на них страх пред Мардохеем.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21080, 'rst66', 'est', 9, 4, 'Ибо велик был Мардохей в доме у царя, и слава о нем ходила по всем областям, так как сей человек, Мардохей, поднимался выше и выше.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21081, 'rst66', 'est', 9, 5, 'И избивали Иудеи всех врагов своих, побивая мечом, умерщвляя и истребляя, и поступали с неприятелями своими по своей воле.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21082, 'rst66', 'est', 9, 6, 'В Сузах, городе престольном, умертвили Иудеи и погубили пятьсот человек;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21083, 'rst66', 'est', 9, 7, 'и Паршандафу, и Далфона, и Асфафу,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21084, 'rst66', 'est', 9, 8, 'и Порафу, и Адалью, и Аридафу,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21085, 'rst66', 'est', 9, 9, 'и Пармашфу, и Арисая, и Аридая, и Ваиезафу &mdash;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21086, 'rst66', 'est', 9, 10, 'десятерых сыновей Амана, сына Амадафа, врага Иудеев, умертвили они, а на грабеж не простерли руки своей.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21087, 'rst66', 'est', 9, 11, 'В тот же день донесли царю о числе умерщвленных в Сузах, престольном городе.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21088, 'rst66', 'est', 9, 12, 'И сказал царь царице Есфири: в Сузах, городе престольном, умертвили Иудеи и погубили пятьсот человек и десятерых сыновей Амана; что же сделали они в прочих областях царя? Какое желание твое? и оно будет удовлетворено. И какая еще просьба твоя? она будет исполнена.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21089, 'rst66', 'est', 9, 13, 'И сказала Есфирь: если царю благоугодно, то пусть бы позволено было Иудеям, которые в Сузах, делать то же и завтра, что сегодня, и десятерых сыновей Амановых пусть бы повесили на дереве.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21090, 'rst66', 'est', 9, 14, 'И приказал царь сделать так; и дан <i>на это</i> указ в Сузах, и десятерых сыновей Амановых повесили.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21091, 'rst66', 'est', 9, 15, 'И собрались Иудеи, которые в Сузах, также и в четырнадцатый день месяца Адара и умертвили в Сузах триста человек, а на грабеж не простерли руки своей.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21092, 'rst66', 'est', 9, 16, 'И прочие Иудеи, находившиеся в царских областях, собрались, чтобы стать на защиту жизни своей и быть покойными от врагов своих, и умертвили из неприятелей своих семьдесят пять тысяч, а на грабеж не простерли руки своей.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21093, 'rst66', 'est', 9, 17, '<i>Это было</i> в тринадцатый день месяца Адара; а в четырнадцатый день сего же месяца они успокоились и сделали его днем пиршества и веселья.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21094, 'rst66', 'est', 9, 18, 'Иудеи же, которые в Сузах, собирались в тринадцатый день его и в четырнадцатый день его, а в пятнадцатый день его успокоились и сделали его днем пиршества и веселья.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21095, 'rst66', 'est', 9, 19, 'Поэтому Иудеи сельские, живущие в селениях открытых, проводят четырнадцатый день месяца Адара в веселье и пиршестве, как день праздничный, посылая подарки друг ко другу.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21096, 'rst66', 'est', 9, 20, 'И описал Мардохей эти происшествия и послал письма ко всем Иудеям, которые в областях царя Артаксеркса, к близким и к дальним,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21097, 'rst66', 'est', 9, 21, 'о <i>том</i>, чтобы они установили каждогодно празднование у себя четырнадцатого дня месяца Адара и пятнадцатого дня его,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21098, 'rst66', 'est', 9, 22, 'как таких дней, в которые Иудеи сделались покойны от врагов своих, и <i>как</i> такого месяца, в который превратилась у них печаль в радость, и сетование &mdash; в день праздничный, &mdash; чтобы сделали их днями пиршества и веселья, посылая подарки друг другу и подаяния бедным.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21099, 'rst66', 'est', 9, 23, 'И приняли Иудеи то, что уже сами начали делать, и о чем Мардохей написал к ним,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21100, 'rst66', 'est', 9, 24, 'как Аман, сын Амадафа, Вугеянин, враг всех Иудеев, думал погубить Иудеев и бросал пур, <i>жребий</i>, об истреблении и погублении их,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21101, 'rst66', 'est', 9, 25, 'и как Есфирь дошла до царя, и как царь приказал новым письмом, чтобы злой замысел Амана, который он задумал на Иудеев, обратился на голову его, и чтобы повесили его и сыновей его на дереве.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21102, 'rst66', 'est', 9, 26, 'Потому и назвали эти дни Пурим, от имени: пур. Поэтому, согласно со всеми словами сего письма и с тем, что сами видели и до чего доходило у них,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21103, 'rst66', 'est', 9, 27, 'постановили Иудеи и приняли на себя и на детей своих и на всех, присоединяющихся к ним, неотменно, чтобы праздновать эти два дня, по предписанному о них и в свое для них время, каждый год;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21104, 'rst66', 'est', 9, 28, 'и чтобы дни эти были памятны и празднуемы во все роды в каждом племени, в каждой области и в каждом городе; и чтобы дни эти Пурим не отменялись у Иудеев, и память о них не исчезла у детей их.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21105, 'rst66', 'est', 9, 29, 'Написала также царица Есфирь, дочь Абихаила, и Мардохей Иудеянин, со всею настойчивостью, чтобы исполняли это новое письмо о Пуриме;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21106, 'rst66', 'est', 9, 30, 'и послали письма ко всем Иудеям в сто двадцать семь областей царства Артаксерксова со словами мира и правды,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21107, 'rst66', 'est', 9, 31, 'чтобы они твердо наблюдали эти дни Пурим в свое время, какое уставил о них Мардохей Иудеянин и царица Есфирь, и как они сами назначали их для себя и для детей своих в дни пощения и воплей.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21108, 'rst66', 'est', 9, 32, 'Так повеление Есфири подтвердило это слово о Пуриме, и оно вписано в книгу.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21109, 'rst66', 'est', 10, 1, 'Потом наложил царь Артаксеркс подать на землю и на острова морские.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21110, 'rst66', 'est', 10, 2, 'Впрочем, все дела силы его и могущества его и обстоятельное показание о величии Мардохея, которым возвеличил его царь, записаны в книге дневных записей царей Мидийских и Персидских,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21111, 'rst66', 'est', 10, 3, '<i>равно как и то</i>, что Мардохей Иудеянин <i>был</i> вторым по царе Артаксерксе и великим у Иудеев и любимым у множества братьев своих, <i>ибо</i> искал добра народу своему и говорил во благо всего племени своего.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20259, 'rst66', 'ezr', 1, 1, 'В первый год Кира, царя Персидского, во исполнение слова Господня из уст Иеремии, возбудил Господь дух Кира, царя Персидского, и он повелел объявить по всему царству своему, словесно и письменно:');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20260, 'rst66', 'ezr', 1, 2, '«Так говорит Кир, царь Персидский: все царства земли дал мне Господь, Бог небесный, и Он повелел мне построить Ему дом в Иерусалиме, что в Иудее.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20261, 'rst66', 'ezr', 1, 3, 'Кто есть из вас, из всего народа Его, &mdash; да будет Бог его с ним, &mdash; и пусть он идет в Иерусалим, что в Иудее, и строит дом Господа, Бога Израилева, Того Бога, Который в Иерусалиме.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20262, 'rst66', 'ezr', 1, 4, 'А все оставшиеся во всех местах, где бы тот ни жил, пусть помогут ему жители места того серебром и золотом и <i>иным</i> имуществом, и скотом, с доброхотным даянием для дома Божия, что в Иерусалиме».');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20263, 'rst66', 'ezr', 1, 5, 'И поднялись главы поколений Иудиных и Вениаминовых, и священники и левиты, всякий, <i>в ком</i> возбудил Бог дух его, чтобы пойти строить дом Господень, который в Иерусалиме.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20264, 'rst66', 'ezr', 1, 6, 'И все соседи их вспомоществовали им серебряными сосудами, золотом, <i>иным</i> имуществом, и скотом, и дорогими вещами, сверх всякого доброхотного даяния <i>для храма</i>.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20265, 'rst66', 'ezr', 1, 7, 'И царь Кир вынес сосуды дома Господня, которые Навуходоносор взял из Иерусалима и положил в доме бога своего, &mdash;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20266, 'rst66', 'ezr', 1, 8, 'и вынес их Кир, царь Персидский, рукою Мифредата сокровищехранителя, а он счетом сдал их Шешбацару, князю Иудину.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20267, 'rst66', 'ezr', 1, 9, 'И вот число их: блюд золотых тридцать, блюд серебряных тысяча, ножей двадцать девять,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20268, 'rst66', 'ezr', 1, 10, 'чаш золотых тридцать, чаш серебряных двойных четыреста десять, других сосудов тысяча:');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20269, 'rst66', 'ezr', 1, 11, 'всех сосудов, золотых и серебряных, пять тысяч четыреста. Все <i>это</i> взял <i>с собою</i> Шешбацар, при отправлении переселенцев из Вавилона в Иерусалим.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20270, 'rst66', 'ezr', 2, 1, 'Вот сыны страны из пленников переселения, которых Навуходоносор, царь Вавилонский, отвел в Вавилон, возвратившиеся в Иерусалим и Иудею, каждый в свой город, &mdash;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20271, 'rst66', 'ezr', 2, 2, 'пришедшие с Зоровавелем, Иисусом, Неемиею, Сараием, Реелаем, Мардохеем, Билшаном, Мисфаром, Бигваем, Рехумом, Вааном. Число людей народа Израилева:');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20272, 'rst66', 'ezr', 2, 3, 'Сыновей Пароша две тысячи сто семьдесят два;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20273, 'rst66', 'ezr', 2, 4, 'сыновей Сафатии триста семьдесят два;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20274, 'rst66', 'ezr', 2, 5, 'сыновей Араха семьсот семьдесят пять;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20275, 'rst66', 'ezr', 2, 6, 'сыновей Пахаф‐Моава, из сыновей Иисуса <i>и</i> Иоава, две тысячи восемьсот двенадцать;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20276, 'rst66', 'ezr', 2, 7, 'сыновей Елама тысяча двести пятьдесят четыре;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20277, 'rst66', 'ezr', 2, 8, 'сыновей Заттуя девятьсот сорок пять;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20278, 'rst66', 'ezr', 2, 9, 'сыновей Закхая семьсот шестьдесят;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20279, 'rst66', 'ezr', 2, 10, 'сыновей Вания шестьсот сорок два;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20280, 'rst66', 'ezr', 2, 11, 'сыновей Бебая шестьсот двадцать три;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20281, 'rst66', 'ezr', 2, 12, 'сыновей Азгада тысяча двести двадцать два;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20282, 'rst66', 'ezr', 2, 13, 'сыновей Адоникама шестьсот шестьдесят шесть;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20283, 'rst66', 'ezr', 2, 14, 'сыновей Бигвая две тысячи пятьдесят шесть;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20284, 'rst66', 'ezr', 2, 15, 'сыновей Адина четыреста пятьдесят четыре;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20285, 'rst66', 'ezr', 2, 16, 'сыновей Атера, из <i>дома</i> Езекии, девяносто восемь;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20286, 'rst66', 'ezr', 2, 17, 'сыновей Бецая триста двадцать три;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20287, 'rst66', 'ezr', 2, 18, 'сыновей Иоры сто двенадцать;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20288, 'rst66', 'ezr', 2, 19, 'сыновей Хашума двести двадцать три;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20289, 'rst66', 'ezr', 2, 20, 'сыновей Гиббара девяносто пять;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20290, 'rst66', 'ezr', 2, 21, 'Уроженцев Вифлеема сто двадцать три;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20291, 'rst66', 'ezr', 2, 22, 'жителей Нетофы пятьдесят шесть;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20292, 'rst66', 'ezr', 2, 23, 'жителей Анафофа сто двадцать восемь;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20293, 'rst66', 'ezr', 2, 24, 'уроженцев Азмавефа сорок два;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20294, 'rst66', 'ezr', 2, 25, 'уроженцев Кириаф‐Иарима, Кефиры и Беерофа семьсот сорок три;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20295, 'rst66', 'ezr', 2, 26, 'уроженцев Рамы и Гевы шестьсот двадцать один;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20296, 'rst66', 'ezr', 2, 27, 'жителей Михмаса сто двадцать два;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20297, 'rst66', 'ezr', 2, 28, 'жителей Вефиля и Гая двести двадцать три;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20298, 'rst66', 'ezr', 2, 29, 'уроженцев Нево пятьдесят два;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20299, 'rst66', 'ezr', 2, 30, 'уроженцев Магбиша сто пятьдесят шесть;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20300, 'rst66', 'ezr', 2, 31, 'сыновей другого Елама тысяча двести пятьдесят четыре;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20301, 'rst66', 'ezr', 2, 32, 'сыновей Харима триста двадцать;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20302, 'rst66', 'ezr', 2, 33, 'уроженцев Лидды, Хадида и Оно семьсот двадцать пять;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20303, 'rst66', 'ezr', 2, 34, 'уроженцев Иерихона триста сорок пять;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20304, 'rst66', 'ezr', 2, 35, 'уроженцев Сенаи три тысячи шестьсот тридцать.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20305, 'rst66', 'ezr', 2, 36, 'Священников: сыновей Иедаии, из дома Иисусова, девятьсот семьдесят три;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20306, 'rst66', 'ezr', 2, 37, 'сыновей Иммера тысяча пятьдесят два;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20307, 'rst66', 'ezr', 2, 38, 'сыновей Пашхура тысяча двести сорок семь;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20308, 'rst66', 'ezr', 2, 39, 'сыновей Харима тысяча семнадцать.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20309, 'rst66', 'ezr', 2, 40, 'Левитов: сыновей Иисуса и Кадмиила, из сыновей Годавии, семьдесят четыре;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20310, 'rst66', 'ezr', 2, 41, 'певцов: сыновей Асафа сто двадцать восемь;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20311, 'rst66', 'ezr', 2, 42, 'сыновей привратников: сыновья Шаллума, сыновья Атера, сыновья Талмона, сыновья Аккува, сыновья Хатиты, сыновья Шовая, &mdash; всего сто тридцать девять.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20312, 'rst66', 'ezr', 2, 43, 'Нефинеев: сыновья Цихи, сыновья Хасуфы, сыновья Таббаофа,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20313, 'rst66', 'ezr', 2, 44, 'сыновья Кероса, сыновья Сиаги, сыновья Фадона,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20314, 'rst66', 'ezr', 2, 45, 'сыновья Лебаны, сыновья Хагабы, сыновья Аккува,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20315, 'rst66', 'ezr', 2, 46, 'сыновья Хагава, сыновья Шамлая, сыновья Ханана,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20316, 'rst66', 'ezr', 2, 47, 'сыновья Гиддела, сыновья Гахара, сыновья Реаии,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20317, 'rst66', 'ezr', 2, 48, 'сыновья Рецина, сыновья Некоды, сыновья Газзама,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20318, 'rst66', 'ezr', 2, 49, 'сыновья Уззы, сыновья Пасеаха, сыновья Бесая,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20319, 'rst66', 'ezr', 2, 50, 'сыновья Асны, сыновья Меунима, сыновья Нефисима,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20320, 'rst66', 'ezr', 2, 51, 'сыновья Бакбука, сыновья Хакуфы, сыновья Хархура,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20321, 'rst66', 'ezr', 2, 52, 'сыновья Бацлуфа, сыновья Мехиды, сыновья Харши,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20322, 'rst66', 'ezr', 2, 53, 'сыновья Баркоса, сыновья Сисры, сыновья Фамаха,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20323, 'rst66', 'ezr', 2, 54, 'сыновья Нециаха, сыновья Хатифы;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20324, 'rst66', 'ezr', 2, 55, 'сыновья рабов Соломоновых: сыновья Сотая, сыновья Гассоферефа, сыновья Феруды,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20325, 'rst66', 'ezr', 2, 56, 'сыновья Иаалы, сыновья Даркона, сыновья Гиддела,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20326, 'rst66', 'ezr', 2, 57, 'сыновья Сефатии, сыновья Хаттила, сыновья Похереф‐Гаццебайима, сыновья Амия, &mdash;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20327, 'rst66', 'ezr', 2, 58, 'всего &mdash; нефинеев и сыновей рабов Соломоновых триста девяносто два.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20328, 'rst66', 'ezr', 2, 59, 'И вот вышедшие из Тел‐Мелаха, Тел‐Харши, Херуб‐Аддан‐Иммера, которые не могли показать о поколении своем и о племени своем &mdash; от Израиля ли они:');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20329, 'rst66', 'ezr', 2, 60, 'сыновья Делайи, сыновья Товии, сыновья Некоды, шестьсот пятьдесят два.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20330, 'rst66', 'ezr', 2, 61, 'И из сыновей священнических: сыновья Хабайи, сыновья Гаккоца, сыновья Верзеллия, который взял жену из дочерей Верзеллия Галаадитянина и стал называться именем их.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20331, 'rst66', 'ezr', 2, 62, 'Они искали своей записи родословной, и не нашлось ее, а <i>потому</i> исключены из священства.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20332, 'rst66', 'ezr', 2, 63, 'И Тиршафа сказал им, чтоб они не ели великой святыни, доколе не восстанет священник с уримом и туммимом.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20333, 'rst66', 'ezr', 2, 64, 'Все общество вместе <i>состояло</i> из сорока двух тысяч трехсот шестидесяти <i>человек</i>,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20334, 'rst66', 'ezr', 2, 65, 'кроме рабов их и рабынь их, которых <i>было</i> семь тысяч триста тридцать семь; и при них певцов и певиц двести.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20335, 'rst66', 'ezr', 2, 66, 'Коней у них семьсот тридцать шесть, лошаков у них двести сорок пять;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20336, 'rst66', 'ezr', 2, 67, 'верблюдов у них четыреста тридцать пять, ослов шесть тысяч семьсот двадцать.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20337, 'rst66', 'ezr', 2, 68, 'Из глав поколений <i>некоторые</i>, придя к дому Господню, что в Иерусалиме, доброхотно жертвовали на дом Божий, чтобы восстановить его на основании его.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20338, 'rst66', 'ezr', 2, 69, 'По достатку своему, они дали в сокровищницу на <i>производство</i> работ шестьдесят одну тысячу драхм золота и пять тысяч мин серебра и сто священнических одежд.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20339, 'rst66', 'ezr', 2, 70, 'И стали жить священники и левиты, и народ и певцы, и привратники и нефинеи в городах своих, и весь Израиль в городах своих.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20340, 'rst66', 'ezr', 3, 1, 'Когда наступил седьмой месяц, и сыны Израилевы <i>уже были</i> в городах, тогда собрался народ, как один человек, в Иерусалиме.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20341, 'rst66', 'ezr', 3, 2, 'И встал Иисус, сын Иоседеков, и братья его священники, и Зоровавель, сын Салафиилов, и братья его, и соорудили они жертвенник Богу Израилеву, чтобы возносить на нем всесожжения, как написано в законе Моисея, человека Божия.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20342, 'rst66', 'ezr', 3, 3, 'И поставили жертвенник на основании его, так как они <i>были</i> в страхе от иноземных народов; и стали возносить на нем всесожжения Господу, всесожжения утренние и вечерние.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20343, 'rst66', 'ezr', 3, 4, 'И совершили праздник кущей, как предписано, с ежедневным всесожжением в определенном числе, по уставу <i>каждого</i> дня.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20344, 'rst66', 'ezr', 3, 5, 'И после того <i>совершали</i> всесожжение постоянное, и в новомесячия, и во все праздники, посвященные Господу, и добровольное приношение Господу от всякого усердствующего.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20345, 'rst66', 'ezr', 3, 6, 'С первого же дня седьмого месяца начали возносить всесожжения Господу. А храму Господню <i>еще</i> не было положено основание.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20346, 'rst66', 'ezr', 3, 7, 'И стали выдавать серебро каменотесам и плотникам, и пищу и питье и масло Сидонянам и Тирянам, чтоб они доставляли кедровый лес с Ливана по морю в Яфу, с дозволения им Кира, царя Персидского.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20347, 'rst66', 'ezr', 3, 8, 'Во второй год по приходе своем к дому Божию в Иерусалим, во второй месяц, Зоровавель, сын Салафиилов, и Иисус, сын Иоседеков, и прочие братья их, священники и левиты, и все пришедшие из плена в Иерусалим положили начало и поставили левитов от двадцати лет и выше для надзора за работами дома Господня.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20348, 'rst66', 'ezr', 3, 9, 'И стали Иисус, сыновья его и братья его, Кадмиил и сыновья его, сыновья Иуды, как один <i>человек</i>, для надзора за производителями работ в доме Божием, <i>а также и</i> сыновья Хенадада, сыновья их и братья их левиты.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20349, 'rst66', 'ezr', 3, 10, 'Когда строители положили основание храму Господню, тогда поставили священников в облачении их с трубами и левитов, сыновей Асафовых, с кимвалами, чтобы славить Господа по уставу Давида, царя Израилева.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20350, 'rst66', 'ezr', 3, 11, 'И начали они попеременно петь: «хвалите» и: «славьте Господа», «ибо &mdash; благ, ибо вовек милость Его к Израилю». И весь народ восклицал громогласно, славя Господа за то, что положено основание дома Господня.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20351, 'rst66', 'ezr', 3, 12, 'Впрочем, многие из священников и левитов и глав поколений, старики, которые видели прежний храм, при основании этого храма пред глазами их, плакали громко, но многие и восклицали от радости громогласно.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20352, 'rst66', 'ezr', 3, 13, 'И не мог народ распознать восклицаний радости от воплей плача народного, потому что народ восклицал громко, и голос слышен был далеко.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20353, 'rst66', 'ezr', 4, 1, 'И услышали враги Иуды и Вениамина, что возвратившиеся из плена строят храм Господу, Богу Израилеву;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20354, 'rst66', 'ezr', 4, 2, 'и пришли они к Зоровавелю и к главам поколений, и сказали им: будем и мы строить с вами, потому что мы, как и вы, прибегаем к Богу вашему, и Ему приносим жертвы от дней Асардана, царя Сирийского, который перевел нас сюда.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20355, 'rst66', 'ezr', 4, 3, 'И сказал им Зоровавель и Иисус и прочие главы поколений Израильских: не строить вам вместе с нами дом нашему Богу; мы одни будем строить <i>дом</i> Господу, Богу Израилеву, как повелел нам царь Кир, царь Персидский.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20356, 'rst66', 'ezr', 4, 4, 'И стал народ земли той ослаблять руки народа Иудейского и препятствовать ему в строении;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20357, 'rst66', 'ezr', 4, 5, 'и подкупали против них советников, чтобы разрушить предприятие их, во все дни Кира, царя Персидского, и до царствования Дария, царя Персидского.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20358, 'rst66', 'ezr', 4, 6, 'А в царствование Ахашвероша, в начале царствования его, написали обвинение на жителей Иудеи и Иерусалима.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20359, 'rst66', 'ezr', 4, 7, 'И во дни Артаксеркса писали Бишлам, Мифредат, Табеел и прочие товарищи их к Артаксерксу, царю Персидскому. Письмо же написано <i>было</i> буквами Сирийскими и на Сирийском языке.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20360, 'rst66', 'ezr', 4, 8, 'Рехум советник и Шимшай писец писали одно письмо против Иерусалима к царю Артаксерксу такое:');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20361, 'rst66', 'ezr', 4, 9, '«Тогда‐то. Рехум советник и Шимшай писец и прочие товарищи их, &mdash; Динеи и Афарсафхеи, Тарпелеи, Апарсы, Арехьяне, Вавилоняне, Сусанцы, Даги, Еламитяне,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20362, 'rst66', 'ezr', 4, 10, 'и прочие народы, которых переселил Аснафар великий и славный и поселил в городах Самарийских и в прочих <i>городах</i> за рекою», и прочее.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20363, 'rst66', 'ezr', 4, 11, 'И вот список с письма, которое послали к нему: «Царю Артаксерксу &mdash; рабы твои, люди, <i>живущие</i> за рекою», и прочее.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20364, 'rst66', 'ezr', 4, 12, '«Да будет известно царю, что Иудеи, которые вышли от тебя, пришли к нам в Иерусалим, строят <i>этот</i> мятежный и негодный город, и стены делают, и основания <i>их уже</i> исправили.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20365, 'rst66', 'ezr', 4, 13, 'Да будет же известно царю, что если этот город будет построен и стены восстановлены, то <i>ни</i> подати, <i>ни</i> налога, ни пошлины не будут давать, и царской казне сделан будет ущерб.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20366, 'rst66', 'ezr', 4, 14, 'Так как мы едим соль от дворца царского, и ущерб для царя не можем видеть, поэтому мы посылаем донесение к царю:');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20367, 'rst66', 'ezr', 4, 15, 'пусть поищут в памятной книге отцов твоих, &mdash; и найдешь в книге памятной, и узнаешь, что город сей &mdash; город мятежный и вредный для царей и областей, и <i>что</i> отпадения бывали в нем издавна, за что город сей и опустошен.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20368, 'rst66', 'ezr', 4, 16, 'Посему мы уведомляем царя, что если город сей будет достроен и стены его доделаны, то после этого не будет у тебя владения за рекою».');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20369, 'rst66', 'ezr', 4, 17, 'Царь послал ответ Рехуму советнику и Шимшаю писцу и прочим товарищам их, которые живут в Самарии и <i>в</i> прочих <i>городах</i> заречных: «Мир...» и прочее.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20370, 'rst66', 'ezr', 4, 18, '«Письмо, которое вы прислали нам, внятно прочитано предо мною;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20371, 'rst66', 'ezr', 4, 19, 'и от меня дано повеление, &mdash; и разыскивали, и нашли, что город этот издавна восставал против царей, и производились в нем мятежи и волнения,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20372, 'rst66', 'ezr', 4, 20, 'и <i>что были</i> в Иерусалиме цари могущественные и владевшие всем заречьем, и им давали подать, налоги и пошлины.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20373, 'rst66', 'ezr', 4, 21, 'Итак, дайте приказание, чтобы люди сии перестали работать, и <i>чтобы</i> город сей не строился, доколе от меня не будет дано повеление.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20374, 'rst66', 'ezr', 4, 22, 'И будьте осторожны, чтобы не сделать в этом недосмотра. К чему допускать размножение вредного в ущерб царям?»');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20375, 'rst66', 'ezr', 4, 23, 'Как скоро это письмо царя Артаксеркса было прочитано пред Рехумом и Шимшаем писцом и товарищами их, они немедленно пошли в Иерусалим к Иудеям, и сильною вооруженною рукою остановили работу их.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20376, 'rst66', 'ezr', 4, 24, 'Тогда остановилась работа при доме Божием, который в Иерусалиме, и остановка сия продолжалась до второго года царствования Дария, царя Персидского.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20377, 'rst66', 'ezr', 5, 1, 'Но пророк Аггей и пророк Захария, сын Адды, говорили Иудеям, которые в Иудее и Иерусалиме, пророческие речи во имя Бога Израилева.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20378, 'rst66', 'ezr', 5, 2, 'Тогда встали Зоровавель, сын Салафиилов, и Иисус, сын Иоседеков, и начали строить дом Божий в Иерусалиме, и с ними пророки Божии, подкреплявшие их.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20379, 'rst66', 'ezr', 5, 3, 'В то время пришел к ним Фафнай, заречный областеначальник, и Шефар‐Бознай и товарищи их, и так сказали им: кто дал вам разрешение строить дом сей и доделывать стены сии?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20380, 'rst66', 'ezr', 5, 4, 'Тогда мы сказали им имена тех людей, которые строят это здание.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20381, 'rst66', 'ezr', 5, 5, 'Но око Бога их было над старейшинами Иудейскими, и те не возбраняли им, доколе дело не отправили к Дарию, и доколе не пришло решение по этому делу.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20382, 'rst66', 'ezr', 5, 6, 'Вот содержание письма, которое послал Фафнай, заречный областеначальник, и Шефар‐Бознай с товарищами своими Афарсахеями, которые за рекою, к царю Дарию.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20383, 'rst66', 'ezr', 5, 7, 'В донесении, которое они послали к нему, вот что написано: «Дарию царю &mdash; всякий мир!');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20384, 'rst66', 'ezr', 5, 8, 'Да будет известно царю, что мы ходили в Иудейскую область, к дому Бога великого; и строится он из больших камней, и дерево вкладывается в стены; и работа сия производится быстро и с успехом идет в руках их.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20385, 'rst66', 'ezr', 5, 9, 'Тогда мы спросили у старейшин тех и так сказали им: кто дал вам разрешение строить дом сей и стены сии доделывать?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20386, 'rst66', 'ezr', 5, 10, 'И сверх того об именах их мы спросили их, чтобы дать знать тебе и написать имена тех людей, которые главными у них.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20387, 'rst66', 'ezr', 5, 11, 'И они ответили нам такими словами: мы рабы Бога неба и земли и строим дом, который был построен за много лет прежде сего, &mdash; и великий царь у Израиля строил его и довершил его.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20388, 'rst66', 'ezr', 5, 12, 'Когда же отцы наши прогневали Бога небесного, Он предал их в руку Навуходоносора, царя Вавилонского, Халдеянина; и дом сей он разрушил, и народ переселил в Вавилон.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20389, 'rst66', 'ezr', 5, 13, 'Но в первый год Кира, царя Вавилонского, царь Кир дал разрешение построить сей дом Божий;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20390, 'rst66', 'ezr', 5, 14, 'да и сосуды дома Божия, золотые и серебряные, которые Навуходоносор вынес из храма Иерусалимского и отнес в храм Вавилонский, &mdash; вынес Кир царь из храма Вавилонского; и отдали <i>их</i> по имени Шешбацару, которого он назначил областеначальником,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20391, 'rst66', 'ezr', 5, 15, 'и сказал ему: возьми сии сосуды, пойди и отнеси их в храм Иерусалимский, и пусть дом Божий строится на своем месте.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20392, 'rst66', 'ezr', 5, 16, 'Тогда Шешбацар тот пришел, положил основания дома Божия в Иерусалиме; и с тех пор доселе он строится, и еще не кончен.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20393, 'rst66', 'ezr', 5, 17, 'Итак, если царю благоугодно, пусть поищут в доме царских сокровищ, там, в Вавилоне, точно ли царем Киром дано разрешение строить сей дом Божий в Иерусалиме, и царскую волю о сем пусть пришлют к нам».');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20394, 'rst66', 'ezr', 6, 1, 'Тогда царь Дарий дал повеление, и разыскивали в Вавилоне в книгохранилище, куда полагали сокровища.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20395, 'rst66', 'ezr', 6, 2, 'И найден в Екбатане во дворце, который в области Мидии, один свиток, и в нем написано так: «Для памяти:');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20396, 'rst66', 'ezr', 6, 3, 'в первый год царя Кира царь Кир дал повеление о доме Божием в Иерусалиме: пусть строится дом на том месте, где приносят жертвы, и пусть будут положены прочные основания для него; вышина его в шестьдесят локтей, ширина его в шестьдесят локтей;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20397, 'rst66', 'ezr', 6, 4, 'рядов из камней больших три, и ряд из дерева один; издержки же пусть выдаются из царского дома.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20398, 'rst66', 'ezr', 6, 5, 'Да и сосуды дома Божия, золотые и серебряные, которые Навуходоносор вынес из храма Иерусалимского и отнес в Вавилон, пусть возвратятся и пойдут в храм Иерусалимский, <i>каждый</i> на место свое, и помещены будут в доме Божием.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20399, 'rst66', 'ezr', 6, 6, 'Итак, Фафнай, заречный областеначальник, и Шефар‐Бознай, с товарищами вашими Афарсахеями, которые за рекою, &mdash; удалитесь оттуда.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20400, 'rst66', 'ezr', 6, 7, 'Не останавливайте работы при сем доме Божием; пусть Иудейский областеначальник и Иудейские старейшины строят сей дом Божий на месте его.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20401, 'rst66', 'ezr', 6, 8, 'И от меня дается повеление о том, чем вы должны содействовать старейшинам тем Иудейским в построении того дома Божия, и <i>именно:</i> из имущества царского &mdash; <i>из</i> заречной подати &mdash; немедленно берите и давайте тем людям, чтобы работа не останавливалась;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20402, 'rst66', 'ezr', 6, 9, 'и сколько нужно &mdash; тельцов ли, или овнов и агнцев, на всесожжения Богу небесному, также пшеницы, соли, вина и масла, как скажут священники Иерусалимские, пусть будет выдаваемо им изо дня в день без задержки,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20403, 'rst66', 'ezr', 6, 10, 'чтоб они приносили жертву, приятную Богу небесному, и молились о жизни царя и сыновей его.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20404, 'rst66', 'ezr', 6, 11, 'Мною же дается повеление, что <i>если</i> какой человек изменит это определение, то будет вынуто бревно из дома его, и будет поднят он и пригвожден к нему, а дом его за то будет обращен в развалины.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20405, 'rst66', 'ezr', 6, 12, 'И Бог, Которого имя там обитает, да низложит всякого царя и народ, который простер бы руку свою, чтобы изменить <i>сие</i> ко вреду этого дома Божия в Иерусалиме. Я, Дарий, дал это повеление; да будет оно в точности исполняемо».');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20406, 'rst66', 'ezr', 6, 13, 'Тогда Фафнай, заречный областеначальник, Шефар‐Бознай и товарищи их, &mdash; как повелел царь Дарий, так в точности и делали.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20407, 'rst66', 'ezr', 6, 14, 'И старейшины Иудейские строили и преуспевали, по пророчеству Аггея пророка и Захарии, сына Адды. И построили и окончили, по воле Бога Израилева и по воле Кира и Дария и Артаксеркса, царей Персидских.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20408, 'rst66', 'ezr', 6, 15, 'И окончен дом сей к третьему дню месяца Адара, в шестой год царствования царя Дария.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20409, 'rst66', 'ezr', 6, 16, 'И совершили сыны Израилевы, священники и левиты и прочие, возвратившиеся из плена, освящение сего дома Божия с радостью.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20410, 'rst66', 'ezr', 6, 17, 'И принесли при освящении сего дома Божия: сто волов, двести овнов, четыреста агнцев и двенадцать козлов в жертву за грех за всего Израиля, по числу колен Израилевых.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20411, 'rst66', 'ezr', 6, 18, 'И поставили священников по отделениям их, и левитов по чередам их на службу Божию в Иерусалиме, как предписано в книге Моисея.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20412, 'rst66', 'ezr', 6, 19, 'И совершили возвратившиеся из плена пасху в четырнадцатый день первого месяца,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20413, 'rst66', 'ezr', 6, 20, 'потому что очистились священники и левиты, &mdash; все они, как один, <i>были</i> чисты; и закололи агнцев пасхальных для всех, возвратившихся из плена, для братьев своих священников и для себя.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20414, 'rst66', 'ezr', 6, 21, 'И ели сыны Израилевы, возвратившиеся из переселения, и все отделившиеся к ним от нечистоты народов земли, чтобы прибегать к Господу, Богу Израилеву.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20415, 'rst66', 'ezr', 6, 22, 'И праздновали праздник опресноков семь дней в радости, потому что обрадовал их Господь и обратил к ним сердце царя Ассирийского, чтобы подкреплять руки их при строении дома Господа, Бога Израилева.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20416, 'rst66', 'ezr', 7, 1, 'После сих происшествий, в царствование Артаксеркса, царя Персидского, Ездра, сын Сераии, сын Азарии, сын Хелкии,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20417, 'rst66', 'ezr', 7, 2, 'сын Шаллума, сын Садока, сын Ахитува,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20418, 'rst66', 'ezr', 7, 3, 'сын Амарии, сын Азарии, сын Марайофа,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20419, 'rst66', 'ezr', 7, 4, 'сын Захарии, сын Уззия, сын Буккия,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20420, 'rst66', 'ezr', 7, 5, 'сын Авишуя, сын Финееса, сын Елеазара, сын Аарона первосвященника, &mdash;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20421, 'rst66', 'ezr', 7, 6, 'Сей Ездра вышел из Вавилона. Он был книжник, сведущий в законе Моисеевом, который дал Господь, Бог Израилев. И дал ему царь все по желанию его, так как рука Господа Бога его <i>была</i> над ним.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20422, 'rst66', 'ezr', 7, 7, 'С <i>ним</i> пошли в Иерусалим и <i>некоторые</i> из сынов Израилевых, и из священников и левитов, и певцов и привратников и нефинеев в седьмой год царя Артаксеркса.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20423, 'rst66', 'ezr', 7, 8, 'И пришел он в Иерусалим в пятый месяц, &mdash; в седьмой же год царя.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20424, 'rst66', 'ezr', 7, 9, 'Ибо в первый день первого месяца <i>было</i> начало выхода из Вавилона, и в первый день пятого месяца он пришел в Иерусалим, так как благодеющая рука Бога его была над ним,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20425, 'rst66', 'ezr', 7, 10, 'потому что Ездра расположил сердце свое к тому, чтобы изучать закон Господень и исполнять <i>его</i>, и учить в Израиле закону и правде.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20426, 'rst66', 'ezr', 7, 11, 'И вот содержание письма, которое дал царь Артаксеркс Ездре священнику, книжнику, учившему словам заповедей Господа и законов Его в Израиле:');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20427, 'rst66', 'ezr', 7, 12, '«Артаксеркс, царь царей, Ездре священнику, учителю закона Бога небесного совершенному», и прочее.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20428, 'rst66', 'ezr', 7, 13, '«От меня дано повеление, чтобы в царстве моем всякий из народа Израилева и из священников его и левитов, желающий идти в Иерусалим, шел с тобою.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20429, 'rst66', 'ezr', 7, 14, 'Так как ты посылаешься от царя и семи советников его, чтобы обозреть Иудею и Иерусалим по закону Бога твоего, находящемуся в руке твоей,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20430, 'rst66', 'ezr', 7, 15, 'и чтобы доставить серебро и золото, которое царь и советники его пожертвовали Богу Израилеву, Которого жилище в Иерусалиме,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20431, 'rst66', 'ezr', 7, 16, 'и все серебро и золото, которое ты соберешь во всей области Вавилонской, вместе с доброхотными даяниями от народа и священников, которые пожертвуют они для дома Бога своего, что в Иерусалиме;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20432, 'rst66', 'ezr', 7, 17, 'поэтому немедленно купи на эти деньги волов, овнов, агнцев и хлебных приношений к ним и возлияний для них, и принеси их на жертвенник дома Бога вашего в Иерусалиме.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20433, 'rst66', 'ezr', 7, 18, 'И что тебе и братьям твоим заблагорассудится сделать из остального серебра и золота, то по воле Бога вашего делайте.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20434, 'rst66', 'ezr', 7, 19, 'И сосуды, которые даны тебе для служб <i>в</i> доме Бога твоего, поставь пред Богом Иерусалимским.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20435, 'rst66', 'ezr', 7, 20, 'И прочее потребное для дома Бога твоего, что ты признаешь нужным, давай из дома царских сокровищ.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20436, 'rst66', 'ezr', 7, 21, 'И от меня, царя Артаксеркса, дается повеление всем сокровищехранителям, которые за рекою: все, чего потребует у вас Ездра священник, учитель закона Бога небесного, немедленно давайте:');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20437, 'rst66', 'ezr', 7, 22, 'серебра до ста талантов, и пшеницы до ста ко&#769;ров, и вина до ста батов, и до ста же батов масла, а соли без обозначения <i>количества</i>.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20438, 'rst66', 'ezr', 7, 23, 'Все, что повелено Богом небесным, должно делаться со тщанием для дома Бога небесного; дабы не <i>было</i> гнева <i>Его</i> на царство, царя и сыновей его.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20439, 'rst66', 'ezr', 7, 24, 'И даем вам знать, чтобы <i>ни</i> на кого <i>из</i> священников или левитов, певцов, привратников, нефинеев и служащих при этом доме Божием, не налагать <i>ни</i> подати, <i>ни</i> налога, ни пошлины.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20440, 'rst66', 'ezr', 7, 25, 'Ты же, Ездра, по премудрости Бога твоего, которая в руке твоей, поставь правителей и судей, чтоб они судили весь народ за рекою &mdash; всех знающих законы Бога твоего, а кто не знает, тех учите.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20441, 'rst66', 'ezr', 7, 26, 'Кто же не будет исполнять закон Бога твоего и закон царя, над тем немедленно пусть производят суд, на смерть ли, или на изгнание, или на денежную пеню, или на заключение в темницу».');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20442, 'rst66', 'ezr', 7, 27, 'Благословен Господь, Бог отцов наших, вложивший в сердце царя &mdash; украсить дом Господень, который в Иерусалиме,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20443, 'rst66', 'ezr', 7, 28, 'и склонивший на меня милость царя и советников его, и всех могущественных князей царя! И я ободрился, ибо рука Господа, Бога моего, <i>была</i> надо мною, и собрал я глав Израиля, чтоб они пошли со мною.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20444, 'rst66', 'ezr', 8, 1, 'И вот главы поколений и родословие тех, которые вышли со мною из Вавилона, в царствование царя Артаксеркса:');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20445, 'rst66', 'ezr', 8, 2, 'из сыновей Финееса Гирсон; из сыновей Ифамара Даниил; из сыновей Давида Хаттуш;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20446, 'rst66', 'ezr', 8, 3, 'из сыновей Шехании, из сыновей Пароша Захария, и с ним по списку родословному сто пятьдесят <i>человек</i> мужеского пола;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20447, 'rst66', 'ezr', 8, 4, 'из сыновей Пахаф‐Моава Эльегоенай, сын Зерахии, и с ним двести <i>человек</i> мужеского пола;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20448, 'rst66', 'ezr', 8, 5, 'из сыновей [Зафоя] Шехания, сын Яхазиила, и с ним триста <i>человек</i> мужеского пола;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20449, 'rst66', 'ezr', 8, 6, 'из сыновей Адина Евед, сын Ионафана, и с ним пятьдесят <i>человек</i> мужеского пола;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20450, 'rst66', 'ezr', 8, 7, 'из сыновей Елама Иешаия, сын Афалии, и с ним семьдесят <i>человек</i> мужеского пола;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20451, 'rst66', 'ezr', 8, 8, 'из сыновей Сафатии Зевадия, сын Михаилов, и с ним восемьдесят <i>человек</i> мужеского пола;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20452, 'rst66', 'ezr', 8, 9, 'из сыновей Иоава Овадия, сын Иехиелов, и с ним двести восемнадцать <i>человек</i> мужеского пола;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20453, 'rst66', 'ezr', 8, 10, 'из сыновей [Ваания] Шеломиф, сын Иосифии, и с ним сто шестьдесят <i>человек</i> мужеского пола;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20454, 'rst66', 'ezr', 8, 11, 'из сыновей Бевая Захария, сын Бевая, и с ним двадцать восемь <i>человек</i> мужеского пола;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20455, 'rst66', 'ezr', 8, 12, 'из сыновей Азгада Иоханан, сын Гаккатана, и с ним сто десять <i>человек</i> мужеского пола;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20456, 'rst66', 'ezr', 8, 13, 'из сыновей Адоникама последние, и вот имена их: Елифелет, Иеиел и Шемаия, и с ними шестьдесят <i>человек</i> мужеского пола;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20457, 'rst66', 'ezr', 8, 14, 'из сыновей Бигвая, Уфай и Заббуд, и с ними семьдесят <i>человек</i> мужеского пола.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20458, 'rst66', 'ezr', 8, 15, 'Я собрал их у реки, втекающей в Агаву, и мы простояли там три дня, и когда я осмотрел народ и священников, то из сынов Левия <i>никого</i> там не нашел.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20459, 'rst66', 'ezr', 8, 16, 'И послал я позвать Елиезера, Ариэла, Шемаию, и Элнафана, и Иарива, и Элнафана, и Нафана, и Захарию, и Мешуллама &mdash; главных, и Иоярива и Элнафана &mdash; ученых;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20460, 'rst66', 'ezr', 8, 17, 'и дал им поручение к Иддо, главному в местности Касифье, и вложил им в уста, что говорить к Иддо и братьям его, нефинеям в местности Касифье, чтобы они привели к нам служителей для дома Бога нашего.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20461, 'rst66', 'ezr', 8, 18, 'И привели они к нам, так как благодеющая рука Бога нашего была над нами, человека умного из сыновей Махлия, сына Левиина, сына Израилева, именно Шеревию, и сыновей его и братьев его, восемнадцать <i>человек;</i>');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20462, 'rst66', 'ezr', 8, 19, 'и Хашавию и с ним Иешаию из сыновей Мерариных, братьев его и сыновей их двадцать;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20463, 'rst66', 'ezr', 8, 20, 'и из нефинеев, которых дал Давид и князья <i>его</i> на прислугу левитам, двести двадцать нефинеев; все они названы поименно.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20464, 'rst66', 'ezr', 8, 21, 'И провозгласил я там пост у реки Агавы, чтобы смириться нам пред лицом Бога нашего, просить у Него благополучного пути для себя и для детей наших и для всего имущества нашего,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20465, 'rst66', 'ezr', 8, 22, 'так как мне стыдно было просить у царя войска и всадников для охранения нашего от врага на пути, ибо мы, говоря с царем, сказали: «рука Бога нашего для всех, прибегающих к Нему, <i>есть</i> благодеющая, а на всех, оставляющих Его, &mdash; могущество Его и гнев Его!»');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20466, 'rst66', 'ezr', 8, 23, 'Итак, мы постились и просили Бога нашего о сем, и Он услышал нас.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20467, 'rst66', 'ezr', 8, 24, 'И я отделил из начальствующих над священниками двенадцать <i>человек:</i> Шеревию, Хашавию и с ними десять из братьев их;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20468, 'rst66', 'ezr', 8, 25, 'и отдал им весом серебро, и золото, и сосуды, &mdash; все, пожертвованное <i>для</i> дома Бога нашего, что пожертвовали царь, и советники его, и князья его, и все Израильтяне, <i>там</i> находившиеся.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20469, 'rst66', 'ezr', 8, 26, 'И отдал на руки им весом: серебра &mdash; шестьсот пятьдесят талантов, и серебряных сосудов на сто талантов, золота &mdash; сто талантов;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20470, 'rst66', 'ezr', 8, 27, 'и чаш золотых &mdash; двадцать, в тысячу драхм, и два сосуда из лучшей блестящей меди, ценимой как золото.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20471, 'rst66', 'ezr', 8, 28, 'И сказал я им: вы &mdash; святыня Господу, и сосуды &mdash; святыня, и серебро и золото &mdash; доброхотное даяние Господу, Богу отцов ваших.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20472, 'rst66', 'ezr', 8, 29, 'Будьте же бдительны и сберегите <i>это</i>, доколе весом не сдадите начальствующим над священниками и левитами и главам поколений Израилевых в Иерусалиме, в хранилище при доме Господнем.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20473, 'rst66', 'ezr', 8, 30, 'И приняли священники и левиты взвешенное серебро, и золото, и сосуды, чтоб отнести в Иерусалим в дом Бога нашего.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20474, 'rst66', 'ezr', 8, 31, 'И отправились мы от реки Агавы в двенадцатый день первого месяца, чтобы идти в Иерусалим; и рука Бога нашего была над нами, и спасала нас от руки врага и от подстерегающих нас на пути.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20475, 'rst66', 'ezr', 8, 32, 'И пришли мы в Иерусалим, и пробыли там три дня.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20476, 'rst66', 'ezr', 8, 33, 'В четвертый день мы сдали весом серебро, и золото, и сосуды в дом Бога нашего, на руки Меремофу, сыну Урии, священнику, и с ним Елеазару, сыну Финеесову, и с ними Иозаваду, сыну Иисусову, и Ноадии, сыну Виннуя, левитам,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20477, 'rst66', 'ezr', 8, 34, 'все счетом и весом. И все взвешенное записано в то же время.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20478, 'rst66', 'ezr', 8, 35, 'Пришедшие из плена переселенцы принесли во всесожжение Богу Израилеву двенадцать тельцов за всего Израиля, девяносто шесть овнов, семьдесят семь агнцев и двенадцать козлов в жертву за грех: все это во всесожжение Господу.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20479, 'rst66', 'ezr', 8, 36, 'И отдали царские повеления царским сатрапам и заречным областеначальникам, и они почтили народ и дом Божий.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20480, 'rst66', 'ezr', 9, 1, 'По окончании сего подошли ко мне начальствующие и сказали: народ Израилев и священники и левиты не отделились от народов иноплеменных с мерзостями их, от Хананеев, Хеттеев, Ферезеев, Иевусеев, Аммонитян, Моавитян, Египтян и Аморреев,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20481, 'rst66', 'ezr', 9, 2, 'потому что взяли дочерей их за себя и за сыновей своих, и смешалось семя святое с народами иноплеменными, и притом рука знатнейших и главнейших была в сем беззаконии первою.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20482, 'rst66', 'ezr', 9, 3, 'Услышав это слово, я разодрал нижнюю и верхнюю одежду мою и рвал волосы на голове моей и на бороде моей, и сидел печальный.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20483, 'rst66', 'ezr', 9, 4, 'Тогда собрались ко мне все, убоявшиеся слов Бога Израилева по причине преступления переселенцев, и я сидел в печали до вечерней жертвы.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20484, 'rst66', 'ezr', 9, 5, 'А во время вечерней жертвы я встал с места сетования моего, и в разодранной нижней и верхней одежде пал на колени мои и простер руки мои к Господу, Богу моему,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20485, 'rst66', 'ezr', 9, 6, 'и сказал: Боже мой! стыжусь и боюсь поднять лицо мое к Тебе, Боже мой, потому что беззакония наши стали выше головы, и вина наша возросла до небес.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20486, 'rst66', 'ezr', 9, 7, 'Со дней отцов наших мы в великой вине до сего дня, и за беззакония наши преданы были мы, цари наши, священники наши, в руки царей иноземных, под меч, в плен и на разграбление и на посрамление, как это и ныне.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20487, 'rst66', 'ezr', 9, 8, 'И вот, по малом времени, даровано нам помилование от Господа, Бога нашего, и Он оставил у нас <i>несколько</i> уцелевших и дал нам утвердиться на месте святыни Его, и просветил глаза наши Бог наш, и дал нам ожить немного в рабстве нашем.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20488, 'rst66', 'ezr', 9, 9, 'Мы &mdash; рабы, но и в рабстве нашем не оставил нас Бог наш. И склонил Он к нам милость царей Персидских, чтоб они дали нам ожить, воздвигнуть дом Бога нашего и восстановить <i>его</i> из развалин его, и дали нам ограждение в Иудее и в Иерусалиме.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20489, 'rst66', 'ezr', 9, 10, 'И ныне, что скажем мы, Боже наш, после этого? Ибо мы отступили от заповедей Твоих,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20490, 'rst66', 'ezr', 9, 11, 'которые заповедал Ты чрез рабов Твоих пророков, говоря: «земля, в которую идете вы, чтоб овладеть ею, земля нечистая, она осквернена нечистотою иноплеменных народов, их мерзостями, которыми они наполнили ее от края до края в осквернениях своих.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20491, 'rst66', 'ezr', 9, 12, 'Итак, дочерей ваших не выдавайте за сыновей их, и дочерей их не берите за сыновей ваших, и не ищите мира их и блага их вовеки, чтобы укрепиться вам и питаться благами земли той и передать ее в наследие сыновьям вашим навеки».');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20492, 'rst66', 'ezr', 9, 13, 'И после всего, постигшего нас за худые дела наши и за великую вину нашу, &mdash; ибо Ты, Боже наш, пощадил нас не по мере беззакония нашего и дал нам такое избавление, &mdash;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20493, 'rst66', 'ezr', 9, 14, 'неужели мы опять будем нарушать заповеди Твои и вступать в родство с этими отвратительными народами? Не прогневаешься ли Ты на нас даже до истребления <i>нас</i>, так что не будет уцелевших и не будет спасения?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20494, 'rst66', 'ezr', 9, 15, 'Господи Боже Израилев! праведен Ты. Ибо мы остались уцелевшими до сего дня; и вот мы в беззакониях наших пред лицом Твоим, хотя после этого не надлежало бы нам стоять пред лицом Твоим.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20495, 'rst66', 'ezr', 10, 1, 'Когда <i>так</i> молился Ездра и исповедовался, плача и повергаясь пред домом Божиим, стеклось к нему весьма большое собрание Израильтян, мужчин и женщин и детей, потому что и народ много плакал.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20496, 'rst66', 'ezr', 10, 2, 'И отвечал Шехания, сын Иехиила из сыновей Еламовых, и сказал Ездре: мы сделали преступление пред Богом нашим, что взяли <i>себе</i> жен иноплеменных из народов земли, но есть еще надежда для Израиля в этом деле;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20497, 'rst66', 'ezr', 10, 3, 'заключим теперь завет с Богом нашим, что, по совету господина моего и благоговеющих пред заповедями Бога нашего, мы отпустим <i>от себя</i> всех жен и <i>детей</i>, рожденных ими, &mdash; и да будет по закону!');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20498, 'rst66', 'ezr', 10, 4, 'Встань, потому что это твое дело, и мы с тобою: ободрись и действуй!');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20499, 'rst66', 'ezr', 10, 5, 'И встал Ездра, и велел начальствующим над священниками, левитами и всем Израилем дать клятву, что они сделают так. И они дали клятву.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20500, 'rst66', 'ezr', 10, 6, 'И встал Ездра и пошел от дома Божия в жилище Иоханана, сына Елияшивова, и пришел туда. Хлеба он не ел и воды не пил, потому что плакал о преступлении переселенцев.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20501, 'rst66', 'ezr', 10, 7, 'И объявили в Иудее и в Иерусалиме всем <i>бывшим</i> в плену, чтоб они собрались в Иерусалим;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20502, 'rst66', 'ezr', 10, 8, 'а кто не придет чрез три дня, на все имение того, по определению начальствующих и старейшин, будет положено заклятие, и сам он будет отлучен от общества переселенцев.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20503, 'rst66', 'ezr', 10, 9, 'И собрались все жители Иудеи и земли Вениаминовой в Иерусалим в три дня. Это <i>было</i> в девятом месяце, в двадцатый день месяца. И сидел весь народ на площади у дома Божия, дрожа как по этому делу, так и от дождей.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20504, 'rst66', 'ezr', 10, 10, 'И встал Ездра священник и сказал им: вы сделали преступление, взяв себе жен иноплеменных, и тем увеличили вину Израиля.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20505, 'rst66', 'ezr', 10, 11, 'Итак, покайтесь <i>в сем</i> пред Господом, Богом отцов ваших, и исполните волю Его, и отлучите себя от народов земли и от жен иноплеменных.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20506, 'rst66', 'ezr', 10, 12, 'И отвечало все собрание, и сказало громким голосом: как ты сказал, так и сделаем.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20507, 'rst66', 'ezr', 10, 13, 'Однако же народ многочислен и время <i>теперь</i> дождливое, и нет возможности стоять на улице. Да и это дело не одного дня и не двух, потому что мы много в этом деле погрешили.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20508, 'rst66', 'ezr', 10, 14, 'Пусть наши начальствующие заступят место всего общества, и все в городах наших, которые взяли жен иноплеменных, пусть приходят сюда в назначенные времена и с ними старейшины каждого города и судьи его, доколе не отвратится от нас пылающий гнев Бога нашего за это дело.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20509, 'rst66', 'ezr', 10, 15, 'Тогда Ионафан, сын Асаила, и Яхзеия, сын Фиквы, стали над этим делом, и Мешуллам и Шавфай левит были помощниками им.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20510, 'rst66', 'ezr', 10, 16, 'И сделали так вышедшие из плена. И отделены <i>на это</i> Ездра священник, главы поколений, от каждого поколения их, и все они <i>названы</i> поименно. И сделали они заседание в первый день десятого месяца, для исследования сего дела;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20511, 'rst66', 'ezr', 10, 17, 'и окончили <i>исследование</i> о всех, которые взяли жен иноплеменных, к первому дню первого месяца.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20512, 'rst66', 'ezr', 10, 18, 'И нашлись из сыновей священнических, которые взяли жен иноплеменных, &mdash; из сыновей Иисуса, сына Иоседекова, и братьев его: Маасея, Елиезер, Иарив и Гедалия;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20513, 'rst66', 'ezr', 10, 19, 'и они дали руки свои <i>во уверение</i>, что отпустят жен своих, и <i>что они</i> повинны <i>принести</i> в жертву овна за свою вину;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20514, 'rst66', 'ezr', 10, 20, 'и из сыновей Иммера: Хананий и Зевадия;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20515, 'rst66', 'ezr', 10, 21, 'и из сыновей Харима: Маасея, Елия, Шемаия, Иехиил и Уззия;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20516, 'rst66', 'ezr', 10, 22, 'и из сыновей Пашхура: Елиоенай, Маасея, Исмаил, Нафанаил, Иозавад и Эласа;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20517, 'rst66', 'ezr', 10, 23, 'И из левитов: Иозавад, Шимей и Келаия, он же Клита, Пафахия, Иуда и Елиезер;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20518, 'rst66', 'ezr', 10, 24, 'И из певцов: Елияшив; и из привратников: Шаллум, Телем и Урий;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20519, 'rst66', 'ezr', 10, 25, 'А из Израильтян, &mdash; из сыновей Пароша: Рамаия, Иззия, Малхия, Миямин, Елеазар, Малхия и Венаия;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20520, 'rst66', 'ezr', 10, 26, 'и из сыновей Елама: Матфания, Захария, Иехиел, Авдий, Иремоф и Елия;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20521, 'rst66', 'ezr', 10, 27, 'и из сыновей Заффу: Елиоенай, Елияшив, Матфания, Иремоф, Завад и Азиса;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20522, 'rst66', 'ezr', 10, 28, 'и из сыновей Бевая: Иоханан, Ханания, Забвай и Афлай;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20523, 'rst66', 'ezr', 10, 29, 'и из сыновей Вания: Мешуллам, Маллух, Адая, Иашув, Шеал и Иерамоф;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20524, 'rst66', 'ezr', 10, 30, 'и из сыновей Пахаф‐Моава: Адна, Хелал, Венаия, Маасея, Матфания, Веселеил, Биннуй и Манассия;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20525, 'rst66', 'ezr', 10, 31, 'и из сыновей Харима: Елиезер, Ишшия, Малхия, Шемаия, Симеон,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20526, 'rst66', 'ezr', 10, 32, 'Вениамин, Маллух, Шемария;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20527, 'rst66', 'ezr', 10, 33, 'и из сыновей Хашума: Мафнай, Мафафа, Завад, Елифелет, Иеремай, Манассия и Шимей;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20528, 'rst66', 'ezr', 10, 34, 'и из сыновей Вания: Маадай, Амрам и Уел,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20529, 'rst66', 'ezr', 10, 35, 'Бенаия, Бидья, Келуги,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20530, 'rst66', 'ezr', 10, 36, 'Ванея, Меремоф, Елиашив,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20531, 'rst66', 'ezr', 10, 37, 'Матфания, Мафнай, Иаасай,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20532, 'rst66', 'ezr', 10, 38, 'Ваний, Биннуй, Шимей,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20533, 'rst66', 'ezr', 10, 39, 'Шелемия, Нафан, Адаия,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20534, 'rst66', 'ezr', 10, 40, 'Махнадбай, Шашай, Шарай,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20535, 'rst66', 'ezr', 10, 41, 'Азариел, Шелемиягу, Шемария,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20536, 'rst66', 'ezr', 10, 42, 'Шаллум, Амария и Иосиф;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20537, 'rst66', 'ezr', 10, 43, 'и из сыновей Нево: Иеиел, Матфифия, Завад, Зевина, Иаддай, Иоель и Бенаия.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20538, 'rst66', 'ezr', 10, 44, 'Все сии взяли <i>за себя</i> жен иноплеменных, и некоторые из сих жен родили им детей.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21112, 'rst66', 'job', 1, 1, 'Был человек в земле Уц, имя его Иов; и был человек этот непорочен, справедлив и богобоязнен и удалялся от зла.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21113, 'rst66', 'job', 1, 2, 'И родились у него семь сыновей и три дочери.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21114, 'rst66', 'job', 1, 3, 'Имения у него было: семь тысяч мелкого скота, три тысячи верблюдов, пятьсот пар волов и пятьсот ослиц и весьма много прислуги; и был человек этот знаменитее всех сынов Востока.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21115, 'rst66', 'job', 1, 4, 'Сыновья его сходились, делая пиры каждый в своем доме в свой день, и посылали и приглашали трех сестер своих есть и пить с ними.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21116, 'rst66', 'job', 1, 5, 'Когда круг пиршественных дней совершался, Иов посылал <i>за ними</i> и освящал их и, вставая рано утром, возносил всесожжения по числу всех их. Ибо говорил Иов: может быть, сыновья мои согрешили и похулили Бога в сердце своем. Так делал Иов во все <i>такие</i> дни.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21117, 'rst66', 'job', 1, 6, 'И был день, когда пришли сыны Божии предстать пред Господа; между ними пришел и сатана.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21118, 'rst66', 'job', 1, 7, 'И сказал Господь сатане: откуда ты пришел? И отвечал сатана Господу и сказал: я ходил по земле и обошел ее.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21119, 'rst66', 'job', 1, 8, 'И сказал Господь сатане: обратил ли ты внимание твое на раба Моего Иова? ибо нет такого, как он, на земле: человек непорочный, справедливый, богобоязненный и удаляющийся от зла.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21120, 'rst66', 'job', 1, 9, 'И отвечал сатана Господу и сказал: разве даром богобоязнен Иов?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21121, 'rst66', 'job', 1, 10, 'Не Ты ли кругом оградил его и дом его и все, что у него? Дело рук его Ты благословил, и стада его распространяются по земле;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21122, 'rst66', 'job', 1, 11, 'но простри руку Твою и коснись всего, что у него, &mdash; благословит ли он Тебя?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21123, 'rst66', 'job', 1, 12, 'И сказал Господь сатане: вот, все, что у него, в руке твоей; только на него не простирай руки твоей. И отошел сатана от лица Господня.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21124, 'rst66', 'job', 1, 13, 'И был день, когда сыновья его и дочери его ели и вино пили в доме первородного брата своего.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21125, 'rst66', 'job', 1, 14, 'И <i>вот</i>, приходит вестник к Иову и говорит:');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21126, 'rst66', 'job', 1, 15, 'волы орали, и ослицы паслись подле них, как напали Савеяне и взяли их, а отроков поразили острием меча; и спасся только я один, чтобы возвестить тебе.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21127, 'rst66', 'job', 1, 16, 'Еще он говорил, как приходит другой и сказывает: огонь Божий упал с неба и опалил овец и отроков и пожрал их; и спасся только я один, чтобы возвестить тебе.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21128, 'rst66', 'job', 1, 17, 'Еще он говорил, как приходит другой и сказывает: Халдеи расположились тремя отрядами и бросились на верблюдов и взяли их, а отроков поразили острием меча; и спасся только я один, чтобы возвестить тебе.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21129, 'rst66', 'job', 1, 18, 'Еще этот говорил, приходит другой и сказывает: сыновья твои и дочери твои ели и вино пили в доме первородного брата своего;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21130, 'rst66', 'job', 1, 19, 'и вот, большой ветер пришел от пустыни и охватил четыре угла дома, и дом упал на отроков, и они умерли; и спасся только я один, чтобы возвестить тебе.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21131, 'rst66', 'job', 1, 20, 'Тогда Иов встал и разодрал верхнюю одежду свою, остриг голову свою и пал на землю и поклонился');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21132, 'rst66', 'job', 1, 21, 'и сказал: наг я вышел из чрева матери моей, наг и возвращусь. Господь дал, Господь и взял; да будет имя Господне благословенно!');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21133, 'rst66', 'job', 1, 22, 'Во всем этом не согрешил Иов и не произнес ничего неразумного о Боге.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21134, 'rst66', 'job', 2, 1, 'Был день, когда пришли сыны Божии предстать пред Господа; между ними пришел и сатана предстать пред Господа.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21135, 'rst66', 'job', 2, 2, 'И сказал Господь сатане: откуда ты пришел? И отвечал сатана Господу и сказал: я ходил по земле и обошел ее.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21136, 'rst66', 'job', 2, 3, 'И сказал Господь сатане: обратил ли ты внимание твое на раба Моего Иова? ибо нет такого, как он, на земле: человек непорочный, справедливый, богобоязненный и удаляющийся от зла, и доселе тверд в своей непорочности; а ты возбуждал Меня против него, чтобы погубить его безвинно.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21137, 'rst66', 'job', 2, 4, 'И отвечал сатана Господу и сказал: кожу за кожу, а за жизнь свою отдаст человек все, что есть у него;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21138, 'rst66', 'job', 2, 5, 'но простри руку Твою и коснись кости его и плоти его, &mdash; благословит ли он Тебя?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21139, 'rst66', 'job', 2, 6, 'И сказал Господь сатане: вот, он в руке твоей, только душу его сбереги.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21140, 'rst66', 'job', 2, 7, 'И отошел сатана от лица Господня и поразил Иова проказою лютою от подошвы ноги его по самое темя его.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21141, 'rst66', 'job', 2, 8, 'И взял он себе черепицу, чтобы скоблить себя ею, и сел в пепел.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21142, 'rst66', 'job', 2, 9, 'И сказала ему жена его: ты все еще тверд в непорочности твоей! похули Бога и умри.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21143, 'rst66', 'job', 2, 10, 'Но он сказал ей: ты говоришь как одна из безумных: неужели доброе мы будем принимать от Бога, а злого не будем принимать? Во всем этом не согрешил Иов устами своими.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21144, 'rst66', 'job', 2, 11, 'И услышали трое друзей Иова о всех этих несчастьях, постигших его, и пошли каждый из своего места: Елифаз Феманитянин, Вилдад Савхеянин и Софар Наамитянин, и сошлись, чтобы идти вместе сетовать с ним и утешать его.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21145, 'rst66', 'job', 2, 12, 'И, подняв глаза свои издали, они не узнали его; и возвысили голос свой и зарыдали; и разодрал каждый верхнюю одежду свою, и бросали пыль над головами своими к небу.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21146, 'rst66', 'job', 2, 13, 'И сидели с ним на земле семь дней и семь ночей; и никто не говорил ему ни слова, ибо видели, что страдание его весьма велико.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21147, 'rst66', 'job', 3, 1, 'После того открыл Иов уста свои и проклял день свой.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21148, 'rst66', 'job', 3, 2, 'И начал Иов и сказал:');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21149, 'rst66', 'job', 3, 3, 'погибни день, в который я родился, и ночь, в которую сказано: «зачался человек»!');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21150, 'rst66', 'job', 3, 4, 'День тот да будет тьмою; да не взыщет его Бог свыше, и да не воссияет над ним свет!');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21151, 'rst66', 'job', 3, 5, 'Да омрачит его тьма и тень смертная, да обложит его туча, да страшатся его, как палящего зноя!');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21152, 'rst66', 'job', 3, 6, 'Ночь та, &mdash; да обладает ею мрак, да не сочтется она в днях года, да не войдет в число месяцев!');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21153, 'rst66', 'job', 3, 7, 'О! ночь та &mdash; да будет она безлюдна; да не войдет в нее веселье!');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21154, 'rst66', 'job', 3, 8, 'Да проклянут ее проклинающие день, способные разбудить левиафана!');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21155, 'rst66', 'job', 3, 9, 'Да померкнут звезды рассвета ее: пусть ждет она света, и он не приходит, и да не увидит она ресниц денницы');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21156, 'rst66', 'job', 3, 10, 'за то, что не затворила дверей чрева <i>матери</i> моей и не сокрыла горести от очей моих!');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21157, 'rst66', 'job', 3, 11, 'Для чего не умер я, выходя из утробы, и не скончался, когда вышел из чрева?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21158, 'rst66', 'job', 3, 12, 'Зачем приняли меня колени? зачем было мне сосать сосцы?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21159, 'rst66', 'job', 3, 13, 'Теперь бы лежал я и почивал; спал бы, и мне было бы покойно');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21160, 'rst66', 'job', 3, 14, 'с царями и советниками земли, которые застраивали для себя пустыни,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21161, 'rst66', 'job', 3, 15, 'или с князьями, у которых было золото, и которые наполняли домы свои серебром;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21162, 'rst66', 'job', 3, 16, 'или, как выкидыш сокрытый, я не существовал бы, как младенцы, не увидевшие света.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21163, 'rst66', 'job', 3, 17, 'Там беззаконные перестают наводить страх, и там отдыхают истощившиеся в силах.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21164, 'rst66', 'job', 3, 18, 'Там узники вместе наслаждаются покоем и не слышат криков приставника.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21165, 'rst66', 'job', 3, 19, 'Малый и великий там равны, и раб свободен от господина своего.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21166, 'rst66', 'job', 3, 20, 'На что дан страдальцу свет, и жизнь огорченным душою,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21167, 'rst66', 'job', 3, 21, 'которые ждут смерти, и нет ее, которые вырыли бы ее охотнее, нежели клад,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21168, 'rst66', 'job', 3, 22, 'обрадовались бы до восторга, восхитились бы, что нашли гроб?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21169, 'rst66', 'job', 3, 23, '<i>На что дан свет</i> человеку, которого путь закрыт, и которого Бог окружил мраком?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21170, 'rst66', 'job', 3, 24, 'Вздохи мои предупреждают хлеб мой, и стоны мои льются, как вода,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21171, 'rst66', 'job', 3, 25, 'ибо ужасное, чего я ужасался, то и постигло меня; и чего я боялся, то и пришло ко мне.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21172, 'rst66', 'job', 3, 26, 'Нет мне мира, нет покоя, нет отрады: постигло несчастье.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21173, 'rst66', 'job', 4, 1, 'И отвечал Елифаз Феманитянин и сказал:');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21174, 'rst66', 'job', 4, 2, '<i>если</i> попытаемся мы <i>сказать</i> к тебе слово, &mdash; не тяжело ли будет тебе? Впрочем, кто может возбранить слову!');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21175, 'rst66', 'job', 4, 3, 'Вот, ты наставлял многих и опустившиеся руки поддерживал,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21176, 'rst66', 'job', 4, 4, 'падающего восставляли слова твои, и гнущиеся колени ты укреплял.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21177, 'rst66', 'job', 4, 5, 'А теперь дошло до тебя, и ты изнемог; коснулось тебя, и ты упал духом.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21178, 'rst66', 'job', 4, 6, 'Богобоязненность твоя не должна ли быть твоею надеждою, и непорочность путей твоих &mdash; упованием твоим?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21179, 'rst66', 'job', 4, 7, 'Вспомни же, погибал ли кто невинный и где праведные бывали искореняемы?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21180, 'rst66', 'job', 4, 8, 'Как я видал, то оравшие нечестие и сеявшие зло пожинают его;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21181, 'rst66', 'job', 4, 9, 'от дуновения Божия погибают и от духа гнева Его исчезают.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21182, 'rst66', 'job', 4, 10, 'Рев льва и голос рыкающего <i>умолкает</i>, и зубы скимнов сокрушаются;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21183, 'rst66', 'job', 4, 11, 'могучий лев погибает без добычи, и дети львицы рассеиваются.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21184, 'rst66', 'job', 4, 12, 'И вот, ко мне тайно принеслось слово, и ухо мое приняло нечто от него.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21185, 'rst66', 'job', 4, 13, 'Среди размышлений о ночных видениях, когда сон находит на людей,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21186, 'rst66', 'job', 4, 14, 'объял меня ужас и трепет и потряс все кости мои.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21187, 'rst66', 'job', 4, 15, 'И дух прошел надо мною; дыбом стали волосы на мне.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21188, 'rst66', 'job', 4, 16, 'Он стал, &mdash; но я не распознал вида его, &mdash; только облик был пред глазами моими; тихое веяние, &mdash; и я слышу голос:');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21189, 'rst66', 'job', 4, 17, 'человек праведнее ли Бога? и муж чище ли Творца своего?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21190, 'rst66', 'job', 4, 18, 'Вот, Он и слугам Своим не доверяет и в Ангелах Своих усматривает недостатки:');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21191, 'rst66', 'job', 4, 19, 'тем более &mdash; в обитающих в храминах из брения, которых основание прах, которые истребляются скорее моли.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21192, 'rst66', 'job', 4, 20, 'Между утром и вечером они распадаются; не увидишь, как они вовсе исчезнут.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21193, 'rst66', 'job', 4, 21, 'Не погибают ли с ними и достоинства их? Они умирают, не достигнув мудрости.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21194, 'rst66', 'job', 5, 1, 'Взывай, если есть отвечающий тебе. И к кому из святых обратишься ты?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21195, 'rst66', 'job', 5, 2, 'Так, глупца убивает гневливость, и несмысленного губит раздражительность.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21196, 'rst66', 'job', 5, 3, 'Видел я, как глупец укореняется, и тотчас проклял дом его.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21197, 'rst66', 'job', 5, 4, 'Дети его далеки от счастья, их будут бить у ворот, и не будет заступника.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21198, 'rst66', 'job', 5, 5, 'Жатву его съест голодный и из‐за терна возьмет ее, и жаждущие поглотят имущество его.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21199, 'rst66', 'job', 5, 6, 'Так, не из праха выходит горе, и не из земли вырастает беда;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21200, 'rst66', 'job', 5, 7, 'но человек рождается на страдание, <i>как</i> искры, чтобы устремляться вверх.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21201, 'rst66', 'job', 5, 8, 'Но я к Богу обратился бы, предал бы дело мое Богу,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21202, 'rst66', 'job', 5, 9, 'Который творит дела великие и неисследимые, чудные без числа,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21203, 'rst66', 'job', 5, 10, 'дает дождь на лицо земли и посылает воды на лицо полей;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21204, 'rst66', 'job', 5, 11, 'униженных поставляет на высоту, и сетующие возносятся во спасение.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21205, 'rst66', 'job', 5, 12, 'Он разрушает замыслы коварных, и руки их не довершают предприятия.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21206, 'rst66', 'job', 5, 13, 'Он уловляет мудрецов их же лукавством, и совет хитрых становится тщетным:');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21207, 'rst66', 'job', 5, 14, 'днем они встречают тьму и в полдень ходят ощупью, как ночью.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21208, 'rst66', 'job', 5, 15, 'Он спасает бедного от меча, от уст их и от руки сильного.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21209, 'rst66', 'job', 5, 16, 'И есть несчастному надежда, и неправда затворяет уста свои.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21210, 'rst66', 'job', 5, 17, 'Блажен человек, которого вразумляет Бог, и потому наказания Вседержителева не отвергай,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21211, 'rst66', 'job', 5, 18, 'ибо Он причиняет раны и Сам обвязывает их; Он поражает, и Его же руки врачуют.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21212, 'rst66', 'job', 5, 19, 'В шести бедах спасет тебя, и в седьмой не коснется тебя зло.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21213, 'rst66', 'job', 5, 20, 'Во время голода избавит тебя от смерти, и на войне &mdash; от руки меча.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21214, 'rst66', 'job', 5, 21, 'От бича языка укроешь себя и не убоишься опустошения, когда оно придет.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21215, 'rst66', 'job', 5, 22, 'Опустошению и голоду посмеешься и зверей земли не убоишься,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21216, 'rst66', 'job', 5, 23, 'ибо с камнями полевыми у тебя союз, и звери полевые в мире с тобою.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21217, 'rst66', 'job', 5, 24, 'И узнаешь, что шатер твой в безопасности, и будешь смотреть за домом твоим, и не согрешишь.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21218, 'rst66', 'job', 5, 25, 'И увидишь, что семя твое многочисленно, и отрасли твои, как трава на земле.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21219, 'rst66', 'job', 5, 26, 'Войдешь во гроб в зрелости, как укладываются снопы пшеницы в свое время.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21220, 'rst66', 'job', 5, 27, 'Вот, что мы дознали; так оно и есть: выслушай это и заметь для себя.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21221, 'rst66', 'job', 6, 1, 'И отвечал Иов и сказал:');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21222, 'rst66', 'job', 6, 2, 'о, если бы верно взвешены были вопли мои, и вместе с ними положили на весы страдание мое!');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21223, 'rst66', 'job', 6, 3, 'Оно, верно, перетянуло бы песок морей! Оттого слова мои неистовы.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21224, 'rst66', 'job', 6, 4, 'Ибо стрелы Вседержителя во мне; яд их пьет дух мой; ужасы Божии ополчились против меня.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21225, 'rst66', 'job', 6, 5, 'Ревет ли дикий осел на траве? мычит ли бык у месива своего?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21226, 'rst66', 'job', 6, 6, 'Едят ли безвкусное без соли, и есть ли вкус в яичном белке?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21227, 'rst66', 'job', 6, 7, 'До чего не хотела коснуться душа моя, то составляет отвратительную пищу мою.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21228, 'rst66', 'job', 6, 8, 'О, когда бы сбылось желание мое и чаяние мое исполнил Бог!');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21229, 'rst66', 'job', 6, 9, 'О, если бы благоволил Бог сокрушить меня, простер руку Свою и сразил меня!');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21230, 'rst66', 'job', 6, 10, 'Это было бы еще отрадою мне, и я крепился бы в моей беспощадной болезни, ибо я не отвергся изречений Святого.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21231, 'rst66', 'job', 6, 11, 'Что за сила у меня, чтобы надеяться мне? и какой конец, чтобы длить мне жизнь мою?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21232, 'rst66', 'job', 6, 12, 'Твердость ли камней твердость моя? и медь ли плоть моя?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21233, 'rst66', 'job', 6, 13, 'Есть ли во мне помощь для меня, и есть ли для меня какая опора?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21234, 'rst66', 'job', 6, 14, 'К страждущему должно быть сожаление от друга его, если только он не оставил страха к Вседержителю.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21235, 'rst66', 'job', 6, 15, 'Но братья мои неверны, как поток, как быстро текущие ручьи,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21236, 'rst66', 'job', 6, 16, 'которые черны от льда и в которых скрывается снег.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21237, 'rst66', 'job', 6, 17, 'Когда становится тепло, они умаляются, а во время жары исчезают с мест своих.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21238, 'rst66', 'job', 6, 18, 'Уклоняют они направление путей своих, заходят в пустыню и теряются;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21239, 'rst66', 'job', 6, 19, 'смотрят на них дороги Фемайские, надеются на них пути Савейские,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21240, 'rst66', 'job', 6, 20, 'но остаются пристыженными в своей надежде; приходят туда и от стыда краснеют.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21241, 'rst66', 'job', 6, 21, 'Так и вы теперь ничто: увидели страшное и испугались.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21242, 'rst66', 'job', 6, 22, 'Говорил ли я: «дайте мне, или от достатка вашего заплатите за меня;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21243, 'rst66', 'job', 6, 23, 'и избавьте меня от руки врага, и от руки мучителей выкупите меня»?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21244, 'rst66', 'job', 6, 24, 'Научите меня, и я замолчу; укажите, в чем я погрешил.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21245, 'rst66', 'job', 6, 25, 'Как сильны слова правды! Но что доказывают обличения ваши?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21246, 'rst66', 'job', 6, 26, 'Вы придумываете речи для обличения? На ветер пускаете слова ваши.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21247, 'rst66', 'job', 6, 27, 'Вы нападаете на сироту и роете яму другу вашему.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21248, 'rst66', 'job', 6, 28, 'Но прошу вас, взгляните на меня; буду ли я говорить ложь пред лицом вашим?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21249, 'rst66', 'job', 6, 29, 'Пересмотрите, есть ли неправда? пересмотрите, &mdash; правда моя.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21250, 'rst66', 'job', 6, 30, 'Есть ли на языке моем неправда? Неужели гортань моя не может различить горечи?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21251, 'rst66', 'job', 7, 1, 'Не определено ли человеку время на земле, и дни его не то же ли, что дни наемника?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21252, 'rst66', 'job', 7, 2, 'Как раб жаждет тени, и как наемник ждет окончания работы своей,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21253, 'rst66', 'job', 7, 3, 'так я получил в удел месяцы суетные, и ночи горестные отчислены мне.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21254, 'rst66', 'job', 7, 4, 'Когда ложусь, то говорю: «когда‐то встану?», а вечер длится, и я ворочаюсь досыта до самого рассвета.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21255, 'rst66', 'job', 7, 5, 'Тело мое одето червями и пыльными струпами; кожа моя лопается и гноится.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21256, 'rst66', 'job', 7, 6, 'Дни мои бегут скорее челнока и кончаются без надежды.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21257, 'rst66', 'job', 7, 7, 'Вспомни, что жизнь моя дуновение, что око мое не возвратится видеть доброе.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21258, 'rst66', 'job', 7, 8, 'Не увидит меня око видевшего меня; очи Твои на меня, &mdash; и нет меня.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21259, 'rst66', 'job', 7, 9, 'Редеет облако и уходит; так нисшедший в преисподнюю не выйдет,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21260, 'rst66', 'job', 7, 10, 'не возвратится более в дом свой, и место его не будет уже знать его.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21261, 'rst66', 'job', 7, 11, 'Не буду же я удерживать уст моих; буду говорить в стеснении духа моего; буду жаловаться в горести души моей.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21262, 'rst66', 'job', 7, 12, 'Разве я море или морское чудовище, что Ты поставил надо мною стражу?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21263, 'rst66', 'job', 7, 13, 'Когда подумаю: «утешит меня постель моя, унесет горесть мою ложе мое»,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21264, 'rst66', 'job', 7, 14, 'Ты страшишь меня снами и видениями пугаешь меня;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21265, 'rst66', 'job', 7, 15, 'и душа моя желает лучше прекращения дыхания, лучше смерти, нежели <i>сбережения</i> костей моих.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21266, 'rst66', 'job', 7, 16, 'Опротивела мне жизнь. Не вечно жить мне. Отступи от меня, ибо дни мои &mdash; суета.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21267, 'rst66', 'job', 7, 17, 'Что такое человек, что Ты столько ценишь его и обращаешь на него внимание Твое,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21268, 'rst66', 'job', 7, 18, 'посещаешь его каждое утро, каждое мгновение испытываешь его?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21269, 'rst66', 'job', 7, 19, 'Доколе же Ты не оставишь, доколе не отойдешь от меня, доколе не дашь мне проглотить слюну мою?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21270, 'rst66', 'job', 7, 20, 'Если я согрешил, то что я сделаю Тебе, страж человеков! Зачем Ты поставил меня противником Себе, так что я стал самому себе в тягость?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21271, 'rst66', 'job', 7, 21, 'И зачем бы не простить мне греха и не снять с меня беззакония моего? ибо, вот, я лягу в прахе; завтра поищешь меня, и меня нет.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21272, 'rst66', 'job', 8, 1, 'И отвечал Вилдад Савхеянин и сказал:');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21273, 'rst66', 'job', 8, 2, 'долго ли ты будешь говорить так? &mdash; слова уст твоих бурный ветер!');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21274, 'rst66', 'job', 8, 3, 'Неужели Бог извращает суд, и Вседержитель превращает правду?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21275, 'rst66', 'job', 8, 4, 'Если сыновья твои согрешили пред Ним, то Он и предал их в руку беззакония их.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21276, 'rst66', 'job', 8, 5, 'Если же ты взыщешь Бога и помолишься Вседержителю,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21277, 'rst66', 'job', 8, 6, 'и если ты чист и прав, то Он ныне же встанет над тобою и умиротворит жилище правды твоей.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21278, 'rst66', 'job', 8, 7, 'И если вначале у тебя было мало, то впоследствии будет весьма много.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21279, 'rst66', 'job', 8, 8, 'Ибо спроси у прежних родов и вникни в наблюдения отцов их;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21280, 'rst66', 'job', 8, 9, 'а мы &mdash; вчерашние и ничего не знаем, потому что наши дни на земле &mdash; тень.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21281, 'rst66', 'job', 8, 10, 'Вот, они научат тебя, скажут тебе и от сердца своего произнесут слова:');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21282, 'rst66', 'job', 8, 11, 'поднимается ли тростник без влаги? растет ли камыш без воды?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21283, 'rst66', 'job', 8, 12, 'Еще он в свежести своей и не срезан, а прежде всякой травы засыхает.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21284, 'rst66', 'job', 8, 13, 'Таковы пути всех, забывающих Бога, и надежда лицемера погибнет;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21285, 'rst66', 'job', 8, 14, 'упование его подсечено, и уверенность его &mdash; дом паука.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21286, 'rst66', 'job', 8, 15, 'Обопрется о дом свой и не устоит; ухватится за него и не удержится.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21287, 'rst66', 'job', 8, 16, 'Зеленеет он пред солнцем, за сад простираются ветви его;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21288, 'rst66', 'job', 8, 17, 'в кучу <i>камней</i> вплетаются корни его, между камнями врезываются.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21289, 'rst66', 'job', 8, 18, 'Но когда вырвут его с места его, оно откажется от него: «я не видало тебя!»');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21290, 'rst66', 'job', 8, 19, 'Вот радость пути его! а из земли вырастают другие.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21291, 'rst66', 'job', 8, 20, 'Видишь, Бог не отвергает непорочного и не поддерживает руки злодеев.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21292, 'rst66', 'job', 8, 21, 'Он еще наполнит смехом уста твои и губы твои радостным восклицанием.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21293, 'rst66', 'job', 8, 22, 'Ненавидящие тебя облекутся в стыд, и шатра нечестивых не станет.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21294, 'rst66', 'job', 9, 1, 'И отвечал Иов и сказал:');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21295, 'rst66', 'job', 9, 2, 'правда! знаю, что так; но как оправдается человек пред Богом?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21296, 'rst66', 'job', 9, 3, 'Если захочет вступить в прение с Ним, то не ответит Ему ни на одно из тысячи.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21297, 'rst66', 'job', 9, 4, 'Премудр сердцем и могущ силою; кто восставал против Него и оставался в покое?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21298, 'rst66', 'job', 9, 5, 'Он передвигает горы, и не узнают их: Он превращает их в гневе Своем;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21299, 'rst66', 'job', 9, 6, 'сдвигает землю с места ее, и столбы ее дрожат;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21300, 'rst66', 'job', 9, 7, 'скажет солнцу, &mdash; и не взойдет, и на звезды налагает печать.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21301, 'rst66', 'job', 9, 8, 'Он один распростирает небеса и ходит по высотам моря;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21302, 'rst66', 'job', 9, 9, 'сотворил Ас, Кесиль и Хима, и тайники юга;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21303, 'rst66', 'job', 9, 10, 'делает великое, неисследимое и чудное без числа!');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21304, 'rst66', 'job', 9, 11, 'Вот, Он пройдет предо мною, и не увижу Его; пронесется, и не замечу Его.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21305, 'rst66', 'job', 9, 12, 'Возьмет, и кто возбранит Ему? кто скажет Ему: «что Ты делаешь»?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21306, 'rst66', 'job', 9, 13, 'Бог не отвратит гнева Своего; пред Ним падут поборники гордыни.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21307, 'rst66', 'job', 9, 14, 'Тем более могу ли я отвечать Ему и приискивать себе слова пред Ним?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21308, 'rst66', 'job', 9, 15, 'Хотя бы я и прав был, но не буду отвечать, а буду умолять Судию моего.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21309, 'rst66', 'job', 9, 16, 'Если бы я воззвал, и Он ответил мне, &mdash; я не поверил бы, что голос мой услышал Тот,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21310, 'rst66', 'job', 9, 17, 'Кто в вихре разит меня и умножает безвинно мои раны,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21311, 'rst66', 'job', 9, 18, 'не дает мне перевести духа, но пресыщает меня горестями.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21312, 'rst66', 'job', 9, 19, 'Если <i>действовать</i> силою, то Он могуществен; если судом, кто сведет меня с Ним?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21313, 'rst66', 'job', 9, 20, 'Если я буду оправдываться, то мои же уста обвинят меня; <i>если</i> я невинен, то Он признает меня виновным.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21314, 'rst66', 'job', 9, 21, 'Невинен я; не хочу знать души моей, презираю жизнь мою.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21315, 'rst66', 'job', 9, 22, 'Все одно; поэтому я сказал, что Он губит и непорочного и виновного.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21316, 'rst66', 'job', 9, 23, 'Если этого поражает Он бичом вдруг, то пытке невинных посмевается.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21317, 'rst66', 'job', 9, 24, 'Земля отдана в руки нечестивых; лица судей ее Он закрывает. Если не Он, то кто же?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21318, 'rst66', 'job', 9, 25, 'Дни мои быстрее гонца &mdash; бегут, не видят добра,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21319, 'rst66', 'job', 9, 26, 'несутся, как легкие ладьи, как орел стремится на добычу.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21320, 'rst66', 'job', 9, 27, 'Если сказать мне: «забуду я жалобы мои, отложу мрачный вид свой и ободрюсь»;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21321, 'rst66', 'job', 9, 28, 'то трепещу всех страданий моих, зная, что Ты не объявишь меня невинным.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21322, 'rst66', 'job', 9, 29, 'Если же я виновен, то для чего напрасно томлюсь?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21323, 'rst66', 'job', 9, 30, 'Хотя бы я омылся и снежною водою и совершенно очистил руки мои,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21324, 'rst66', 'job', 9, 31, 'то и тогда Ты погрузишь меня в грязь, и возгнушаются мною одежды мои.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21325, 'rst66', 'job', 9, 32, 'Ибо Он не человек, как я, чтоб я мог отвечать Ему и идти вместе с Ним на суд!');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21326, 'rst66', 'job', 9, 33, 'Нет между нами посредника, который положил бы руку свою на обоих нас.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21327, 'rst66', 'job', 9, 34, 'Да отстранит Он от меня жезл Свой, и страх Его да не ужасает меня, &mdash;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21328, 'rst66', 'job', 9, 35, 'и тогда я буду говорить и не убоюсь Его, ибо я не таков сам в себе.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21329, 'rst66', 'job', 10, 1, 'Опротивела душе моей жизнь моя; предамся печали моей; буду говорить в горести души моей.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21330, 'rst66', 'job', 10, 2, 'Скажу Богу: не обвиняй меня; объяви мне, за что Ты со мною борешься?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21331, 'rst66', 'job', 10, 3, 'Хорошо ли для Тебя, что Ты угнетаешь, что презираешь дело рук Твоих, а на совет нечестивых посылаешь свет?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21332, 'rst66', 'job', 10, 4, 'Разве у Тебя плотские очи, и Ты смотришь, как смотрит человек?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21333, 'rst66', 'job', 10, 5, 'Разве дни Твои, как дни человека, или лета Твои, как дни мужа,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21334, 'rst66', 'job', 10, 6, 'что Ты ищешь порока во мне и допытываешься греха во мне,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21335, 'rst66', 'job', 10, 7, 'хотя знаешь, что я не беззаконник, и что некому избавить меня от руки Твоей?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21336, 'rst66', 'job', 10, 8, 'Твои руки трудились надо мною и образовали всего меня кругом, &mdash; и Ты губишь меня?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21337, 'rst66', 'job', 10, 9, 'Вспомни, что Ты, как глину, обделал меня, и в прах обращаешь меня?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21338, 'rst66', 'job', 10, 10, 'Не Ты ли вылил меня, как молоко, и, как творог, сгустил меня,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21339, 'rst66', 'job', 10, 11, 'кожею и плотью одел меня, костями и жилами скрепил меня,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21340, 'rst66', 'job', 10, 12, 'жизнь и милость даровал мне, и попечение Твое хранило дух мой?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21341, 'rst66', 'job', 10, 13, 'Но и то скрывал Ты в сердце Своем, &mdash; знаю, что это было у Тебя, &mdash;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21342, 'rst66', 'job', 10, 14, 'что если я согрешу, Ты заметишь и не оставишь греха моего без наказания.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21343, 'rst66', 'job', 10, 15, 'Если я виновен, горе мне! если и прав, то не осмелюсь поднять головы моей. Я пресыщен унижением; взгляни на бедствие мое:');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21344, 'rst66', 'job', 10, 16, 'оно увеличивается. Ты гонишься за мною, как лев, и снова нападаешь на меня и чудным являешься во мне.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21345, 'rst66', 'job', 10, 17, 'Выводишь новых свидетелей Твоих против меня; усиливаешь гнев Твой на меня; и беды, одни за другими, ополчаются против меня.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21346, 'rst66', 'job', 10, 18, 'И зачем Ты вывел меня из чрева? пусть бы я умер, когда еще ничей глаз не видел меня;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21347, 'rst66', 'job', 10, 19, 'пусть бы я, как небывший, из чрева перенесен был во гроб!');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21348, 'rst66', 'job', 10, 20, 'Не малы ли дни мои? Оставь, отступи от меня, чтобы я немного ободрился,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21349, 'rst66', 'job', 10, 21, 'прежде нежели отойду, &mdash; и уже не возвращусь, &mdash; в страну тьмы и сени смертной,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21350, 'rst66', 'job', 10, 22, 'в страну мрака, каков есть мрак тени смертной, где нет устройства, <i>где</i> темно, как самая тьма.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21351, 'rst66', 'job', 11, 1, 'И отвечал Софар Наамитянин и сказал:');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21352, 'rst66', 'job', 11, 2, 'разве на множество слов нельзя дать ответа, и разве человек многоречивый прав?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21353, 'rst66', 'job', 11, 3, 'Пустословие твое заставит ли молчать мужей, чтобы ты глумился, и некому было постыдить тебя?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21354, 'rst66', 'job', 11, 4, 'Ты сказал: «суждение мое верно, и чист я в очах Твоих».');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21355, 'rst66', 'job', 11, 5, 'Но если бы Бог возглаголал и отверз уста Свои к тебе');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21356, 'rst66', 'job', 11, 6, 'и открыл тебе тайны премудрости, что тебе вдвое больше следовало бы понести! Итак, знай, что Бог для тебя некоторые из беззаконий твоих предал забвению.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21357, 'rst66', 'job', 11, 7, 'Можешь ли ты исследованием найти Бога? Можешь ли совершенно постигнуть Вседержителя?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21358, 'rst66', 'job', 11, 8, 'Он превыше небес, &mdash; что можешь сделать? глубже преисподней, &mdash; что можешь узнать?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21359, 'rst66', 'job', 11, 9, 'Длиннее земли мера Его и шире моря.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21360, 'rst66', 'job', 11, 10, 'Если Он пройдет и заключит кого в оковы и представит на суд, то кто отклонит Его?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21361, 'rst66', 'job', 11, 11, 'Ибо Он знает людей лживых и видит беззаконие, и оставит ли его без внимания?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21362, 'rst66', 'job', 11, 12, 'Но пустой человек мудрствует, хотя человек рождается подобно дикому осленку.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21363, 'rst66', 'job', 11, 13, 'Если ты управишь сердце твое и прострешь к Нему руки твои,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21364, 'rst66', 'job', 11, 14, 'и если есть порок в руке твоей, а ты удалишь его и не дашь беззаконию обитать в шатрах твоих,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21365, 'rst66', 'job', 11, 15, 'то поднимешь незапятнанное лицо твое и будешь тверд и не будешь бояться.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21366, 'rst66', 'job', 11, 16, 'Тогда забудешь горе: как о воде протекшей, будешь вспоминать о нем.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21367, 'rst66', 'job', 11, 17, 'И яснее полдня пойдет жизнь твоя; просветлеешь, как утро.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21368, 'rst66', 'job', 11, 18, 'И будешь спокоен, ибо есть надежда; ты огражден, и можешь спать безопасно.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21369, 'rst66', 'job', 11, 19, 'Будешь лежать, и не будет устрашающего, и многие будут заискивать у тебя.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21370, 'rst66', 'job', 11, 20, 'А глаза беззаконных истают, и убежище пропадет у них, и надежда их исчезнет.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21371, 'rst66', 'job', 12, 1, 'И отвечал Иов и сказал:');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21372, 'rst66', 'job', 12, 2, 'подлинно, <i>только</i> вы люди, и с вами умрет мудрость!');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21373, 'rst66', 'job', 12, 3, 'И у меня <i>есть</i> сердце, как у вас; не ниже я вас; и кто не знает того же?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21374, 'rst66', 'job', 12, 4, 'Посмешищем стал я для друга своего, я, который взывал к Богу, и которому Он отвечал, посмешищем &mdash; <i>человек</i> праведный, непорочный.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21375, 'rst66', 'job', 12, 5, 'Так презрен по мыслям сидящего в покое факел, приготовленный для спотыкающихся ногами.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21376, 'rst66', 'job', 12, 6, 'Покойны шатры у грабителей и безопасны у раздражающих Бога, которые как бы Бога носят в руках своих.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21377, 'rst66', 'job', 12, 7, 'И подлинно: спроси у скота, и научит тебя, у птицы небесной, и возвестит тебе;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21378, 'rst66', 'job', 12, 8, 'или побеседуй с землею, и наставит тебя, и скажут тебе рыбы морские.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21379, 'rst66', 'job', 12, 9, 'Кто во всем этом не узнает, что рука Господа сотворила сие?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21380, 'rst66', 'job', 12, 10, 'В Его руке душа всего живущего и дух всякой человеческой плоти.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21381, 'rst66', 'job', 12, 11, 'Не ухо ли разбирает слова, и не язык ли распознает вкус пищи?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21382, 'rst66', 'job', 12, 12, 'В старцах &mdash; мудрость, и в долголетних &mdash; разум.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21383, 'rst66', 'job', 12, 13, 'У Него премудрость и сила; Его совет и разум.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21384, 'rst66', 'job', 12, 14, 'Что Он разрушит, то не построится; кого Он заключит, тот не высвободится.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21385, 'rst66', 'job', 12, 15, 'Остановит воды, и все высохнет; пустит их, и превратят землю.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21386, 'rst66', 'job', 12, 16, 'У Него могущество и премудрость, пред Ним заблуждающийся и вводящий в заблуждение.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21387, 'rst66', 'job', 12, 17, 'Он приводит советников в необдуманность и судей делает глупыми.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21388, 'rst66', 'job', 12, 18, 'Он лишает перевязей царей и поясом обвязывает чресла их;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21389, 'rst66', 'job', 12, 19, 'князей лишает достоинства и низвергает храбрых;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21390, 'rst66', 'job', 12, 20, 'отнимает язык у велеречивых и старцев лишает смысла;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21391, 'rst66', 'job', 12, 21, 'покрывает стыдом знаменитых и силу могучих ослабляет;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21392, 'rst66', 'job', 12, 22, 'открывает глубокое из среды тьмы и выводит на свет тень смертную;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21393, 'rst66', 'job', 12, 23, 'умножает народы и истребляет их; рассевает народы и собирает их;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21394, 'rst66', 'job', 12, 24, 'отнимает ум у глав народа земли и оставляет их блуждать в пустыне, где нет пути:');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21395, 'rst66', 'job', 12, 25, 'ощупью ходят они во тьме без света и шатаются, как пьяные.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21396, 'rst66', 'job', 13, 1, 'Вот, все <i>это</i> видело око мое, слышало ухо мое и заметило для себя.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21397, 'rst66', 'job', 13, 2, 'Сколько знаете вы, знаю и я: не ниже я вас.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21398, 'rst66', 'job', 13, 3, 'Но я к Вседержителю хотел бы говорить и желал бы состязаться с Богом.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21399, 'rst66', 'job', 13, 4, 'А вы сплетчики лжи; все вы бесполезные врачи.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21400, 'rst66', 'job', 13, 5, 'О, если бы вы только молчали! это было бы <i>вменено</i> вам в мудрость.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21401, 'rst66', 'job', 13, 6, 'Выслушайте же рассуждения мои и вникните в возражение уст моих.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21402, 'rst66', 'job', 13, 7, 'Надлежало ли вам ради Бога говорить неправду и для Него говорить ложь?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21403, 'rst66', 'job', 13, 8, 'Надлежало ли вам быть лицеприятными к Нему и за Бога так препираться?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21404, 'rst66', 'job', 13, 9, 'Хорошо ли будет, когда Он испытает вас? Обманете ли Его, как обманывают человека?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21405, 'rst66', 'job', 13, 10, 'Строго накажет Он вас, хотя вы и скрытно лицемерите.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21406, 'rst66', 'job', 13, 11, 'Неужели величие Его не устрашает вас, и страх Его не нападает на вас?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21407, 'rst66', 'job', 13, 12, 'Напоминания ваши подобны пеплу; оплоты ваши &mdash; оплоты глиняные.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21408, 'rst66', 'job', 13, 13, 'Замолчите предо мною, и я буду говорить, что бы ни постигло меня.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21409, 'rst66', 'job', 13, 14, 'Для чего мне терзать тело мое зубами моими и душу мою полагать в руку мою?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21410, 'rst66', 'job', 13, 15, 'Вот, Он убивает меня, но я буду надеяться; я желал бы только отстоять пути мои пред лицом Его!');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21411, 'rst66', 'job', 13, 16, 'И это уже в оправдание мне, потому что лицемер не пойдет пред лицо Его!');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21412, 'rst66', 'job', 13, 17, 'Выслушайте внимательно слово мое и объяснение мое ушами вашими.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21413, 'rst66', 'job', 13, 18, 'Вот, я завел судебное дело: знаю, что буду прав.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21414, 'rst66', 'job', 13, 19, 'Кто в состоянии оспорить меня? Ибо я скоро умолкну и испущу дух.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21415, 'rst66', 'job', 13, 20, 'Двух только <i>вещей</i> не делай со мною, и тогда я не буду укрываться от лица Твоего:');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21416, 'rst66', 'job', 13, 21, 'удали от меня руку Твою, и ужас Твой да не потрясает меня.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21417, 'rst66', 'job', 13, 22, 'Тогда зови, и я буду отвечать, или буду говорить я, а Ты отвечай мне.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21418, 'rst66', 'job', 13, 23, 'Сколько у меня пороков и грехов? покажи мне беззаконие мое и грех мой.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21419, 'rst66', 'job', 13, 24, 'Для чего скрываешь лицо Твое и считаешь меня врагом Тебе?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21420, 'rst66', 'job', 13, 25, 'Не сорванный ли листок Ты сокрушаешь и не сухую ли соломинку преследуешь?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21421, 'rst66', 'job', 13, 26, 'Ибо Ты пишешь на меня горькое и вменяешь мне грехи юности моей,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21422, 'rst66', 'job', 13, 27, 'и ставишь в колоду ноги мои и подстерегаешь все стези мои, &mdash; гонишься по следам ног моих.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21423, 'rst66', 'job', 13, 28, 'А он, как гниль, распадается, как одежда, изъеденная молью.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21424, 'rst66', 'job', 14, 1, 'Человек, рожденный женою, краткодневен и пресыщен печалями:');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21425, 'rst66', 'job', 14, 2, 'как цветок, он выходит и опадает; убегает, как тень, и не останавливается.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21426, 'rst66', 'job', 14, 3, 'И на него‐то Ты отверзаешь очи Твои, и меня ведешь на суд с Тобою?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21427, 'rst66', 'job', 14, 4, 'Кто родится чистым от нечистого? Ни один.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21428, 'rst66', 'job', 14, 5, 'Если дни ему определены, и число месяцев его у Тебя, если Ты положил ему предел, которого он не перейдет,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21429, 'rst66', 'job', 14, 6, 'то уклонись от него: пусть он отдохнет, доколе не окончит, как наемник, дня своего.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21430, 'rst66', 'job', 14, 7, 'Для дерева есть надежда, что оно, если и будет срублено, снова оживет, и отрасли от него <i>выходить</i> не перестанут:');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21431, 'rst66', 'job', 14, 8, 'если и устарел в земле корень его, и пень его замер в пыли,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21432, 'rst66', 'job', 14, 9, 'но, лишь почуяло воду, оно дает отпрыски и пускает ветви, как бы вновь посаженное.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21433, 'rst66', 'job', 14, 10, 'А человек умирает и распадается; отошел, и где он?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21434, 'rst66', 'job', 14, 11, 'Уходят воды из озера, и река иссякает и высыхает:');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21435, 'rst66', 'job', 14, 12, 'так человек ляжет и не встанет; до скончания неба он не пробудится и не воспрянет от сна своего.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21436, 'rst66', 'job', 14, 13, 'О, если бы Ты в преисподней сокрыл меня и укрывал меня, пока пройдет гнев Твой, положил мне срок и потом вспомнил обо мне!');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21437, 'rst66', 'job', 14, 14, 'Когда умрет человек, то будет ли он опять жить? Во все дни определенного мне времени я ожидал бы, пока придет мне смена.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21438, 'rst66', 'job', 14, 15, 'Воззвал бы Ты, и я дал бы Тебе ответ, и Ты явил бы благоволение творению рук Твоих;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21439, 'rst66', 'job', 14, 16, 'ибо тогда Ты исчислял бы шаги мои и не подстерегал бы греха моего;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21440, 'rst66', 'job', 14, 17, 'в свитке было бы запечатано беззаконие мое, и Ты закрыл бы вину мою.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21441, 'rst66', 'job', 14, 18, 'Но гора, падая, разрушается, и скала сходит с места своего;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21442, 'rst66', 'job', 14, 19, 'вода стирает камни; разлив ее смывает земную пыль: так и надежду человека Ты уничтожаешь.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21443, 'rst66', 'job', 14, 20, 'Теснишь его до конца, и он уходит; изменяешь ему лицо и отсылаешь его.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21444, 'rst66', 'job', 14, 21, 'В чести ли дети его &mdash; он не знает, унижены ли &mdash; он не замечает;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21445, 'rst66', 'job', 14, 22, 'но плоть его на нем болит, и душа его в нем страдает.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21446, 'rst66', 'job', 15, 1, 'И отвечал Елифаз Феманитянин и сказал:');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21447, 'rst66', 'job', 15, 2, 'станет ли мудрый отвечать знанием пустым и наполнять чрево свое ветром палящим,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21448, 'rst66', 'job', 15, 3, 'оправдываться словами бесполезными и речью, не имеющею никакой силы?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21449, 'rst66', 'job', 15, 4, 'Да, ты отложил и страх и за малость считаешь речь к Богу.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21450, 'rst66', 'job', 15, 5, 'Нечестие твое настроило так уста твои, и ты избрал язык лукавых.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21451, 'rst66', 'job', 15, 6, 'Тебя обвиняют уста твои, а не я, и твой язык говорит против тебя.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21452, 'rst66', 'job', 15, 7, 'Разве ты первым человеком родился и прежде холмов создан?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21453, 'rst66', 'job', 15, 8, 'Разве совет Божий ты слышал и привлек к себе премудрость?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21454, 'rst66', 'job', 15, 9, 'Что знаешь ты, чего бы не знали мы? что разумеешь ты, чего не было бы и у нас?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21455, 'rst66', 'job', 15, 10, 'И седовласый и старец есть между нами, днями превышающий отца твоего.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21456, 'rst66', 'job', 15, 11, 'Разве малость для тебя утешения Божии? И это неизвестно тебе?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21457, 'rst66', 'job', 15, 12, 'К чему порывает тебя сердце твое, и к чему так гордо смотришь?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21458, 'rst66', 'job', 15, 13, 'Что устремляешь против Бога дух твой и устами твоими произносишь такие речи?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21459, 'rst66', 'job', 15, 14, 'Что такое человек, чтоб быть ему чистым, и чтобы рожденному женщиною быть праведным?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21460, 'rst66', 'job', 15, 15, 'Вот, Он и святым Своим не доверяет, и небеса нечисты в очах Его:');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21461, 'rst66', 'job', 15, 16, 'тем больше нечист и растлен человек, пьющий беззаконие, как воду.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21462, 'rst66', 'job', 15, 17, 'Я буду говорить тебе, слушай меня; я расскажу тебе, что видел,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21463, 'rst66', 'job', 15, 18, 'что слышали мудрые и не скрыли слышанного от отцов своих,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21464, 'rst66', 'job', 15, 19, 'которым одним отдана была земля, и среди которых чужой не ходил.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21465, 'rst66', 'job', 15, 20, 'Нечестивый мучит себя во все дни свои, и число лет закрыто от притеснителя;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21466, 'rst66', 'job', 15, 21, 'звук ужасов в ушах его; среди мира идет на него губитель.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21467, 'rst66', 'job', 15, 22, 'Он не надеется спастись от тьмы; видит пред собою меч.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21468, 'rst66', 'job', 15, 23, 'Он скитается за куском хлеба повсюду; знает, что уже готов, в руках у него день тьмы.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21469, 'rst66', 'job', 15, 24, 'Устрашает его нужда и теснота; одолевает его, как царь, приготовившийся к битве,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21470, 'rst66', 'job', 15, 25, 'за то, что он простирал против Бога руку свою и противился Вседержителю,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21471, 'rst66', 'job', 15, 26, 'устремлялся против Него с <i>гордою</i> выею, под толстыми щитами своими;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21472, 'rst66', 'job', 15, 27, 'потому что он покрыл лицо свое жиром своим и обложил туком лядвеи свои.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21473, 'rst66', 'job', 15, 28, 'И он селится в городах разоренных, в домах, в которых не живут, которые обречены на развалины.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21474, 'rst66', 'job', 15, 29, 'Не пребудет он богатым, и не уцелеет имущество его, и не распрострется по земле приобретение его.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21475, 'rst66', 'job', 15, 30, 'Не уйдет от тьмы; отрасли его иссушит пламя и дуновением уст своих увлечет его.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21476, 'rst66', 'job', 15, 31, 'Пусть не доверяет суете заблудший, ибо суета будет и воздаянием ему.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21477, 'rst66', 'job', 15, 32, 'Не в свой день он скончается, и ветви его не будут зеленеть.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21478, 'rst66', 'job', 15, 33, 'Сбросит он, как виноградная лоза, недозрелую ягоду свою и, как маслина, стряхнет цвет свой.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21479, 'rst66', 'job', 15, 34, 'Так опустеет дом нечестивого, и огонь пожрет шатры мздоимства.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21480, 'rst66', 'job', 15, 35, 'Он зачал зло и родил ложь, и утроба его приготовляет обман.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21481, 'rst66', 'job', 16, 1, 'И отвечал Иов и сказал:');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21482, 'rst66', 'job', 16, 2, 'слышал я много такого; жалкие утешители все вы!');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21483, 'rst66', 'job', 16, 3, 'Будет ли конец ветреным словам? и что побудило тебя так отвечать?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21484, 'rst66', 'job', 16, 4, 'И я мог бы так же говорить, как вы, если бы душа ваша была на месте души моей; ополчался бы на вас словами и кивал бы на вас головою моею;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21485, 'rst66', 'job', 16, 5, 'подкреплял бы вас языком моим и движением губ утешал бы.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21486, 'rst66', 'job', 16, 6, 'Говорю ли я, не утоляется скорбь моя; перестаю ли, что отходит от меня?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21487, 'rst66', 'job', 16, 7, 'Но ныне Он изнурил меня. Ты разрушил всю семью мою.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21488, 'rst66', 'job', 16, 8, 'Ты покрыл меня морщинами во свидетельство против меня; восстает на меня изможденность моя, в лицо укоряет меня.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21489, 'rst66', 'job', 16, 9, 'Гнев Его терзает и враждует против меня, скрежещет на меня зубами своими; неприятель мой острит на меня глаза свои.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21490, 'rst66', 'job', 16, 10, 'Разинули на меня пасть свою; ругаясь, бьют меня по щекам; все сговорились против меня.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21491, 'rst66', 'job', 16, 11, 'Предал меня Бог беззаконнику и в руки нечестивым бросил меня.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21492, 'rst66', 'job', 16, 12, 'Я был спокоен, но Он потряс меня; взял меня за шею и избил меня и поставил меня целью для Себя.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21493, 'rst66', 'job', 16, 13, 'Окружили меня стрельцы Его; Он рассекает внутренности мои и не щадит, пролил на землю желчь мою,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21494, 'rst66', 'job', 16, 14, 'пробивает во мне пролом за проломом, бежит на меня, как ратоборец.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21495, 'rst66', 'job', 16, 15, 'Вретище сшил я на кожу мою и в прах положил голову мою.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21496, 'rst66', 'job', 16, 16, 'Лицо мое побагровело от плача, и на веждах моих тень смерти,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21497, 'rst66', 'job', 16, 17, 'при всем том, что нет хищения в руках моих, и молитва моя чиста.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21498, 'rst66', 'job', 16, 18, 'Земля! не закрой моей крови, и да не будет места воплю моему.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21499, 'rst66', 'job', 16, 19, 'И ныне, вот, на небесах Свидетель мой, и Заступник мой в вышних!');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21500, 'rst66', 'job', 16, 20, 'Многоречивые друзья мои! К Богу слезит око мое.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21501, 'rst66', 'job', 16, 21, 'О, если бы человек мог иметь состязание с Богом, как сын человеческий с ближним своим!');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21502, 'rst66', 'job', 16, 22, 'Ибо летам моим приходит конец, и я отхожу в путь невозвратный.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21503, 'rst66', 'job', 17, 1, 'Дыхание мое ослабело; дни мои угасают; гробы предо мною.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21504, 'rst66', 'job', 17, 2, 'Если бы не насмешки их, то и среди споров их око мое пребывало бы спокойно.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21505, 'rst66', 'job', 17, 3, 'Заступись, поручись <i>Сам</i> за меня пред Собою! иначе кто поручится за меня?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21506, 'rst66', 'job', 17, 4, 'Ибо Ты закрыл сердце их от разумения, и потому не дашь восторжествовать <i>им</i>.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21507, 'rst66', 'job', 17, 5, 'Кто обрекает друзей своих в добычу, у детей того глаза истают.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21508, 'rst66', 'job', 17, 6, 'Он поставил меня притчею для народа и посмешищем для него.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21509, 'rst66', 'job', 17, 7, 'Помутилось от горести око мое, и все члены мои, как тень.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21510, 'rst66', 'job', 17, 8, 'Изумятся о сем праведные, и невинный вознегодует на лицемера.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21511, 'rst66', 'job', 17, 9, 'Но праведник будет крепко держаться пути своего, и чистый руками будет больше и больше утверждаться.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21512, 'rst66', 'job', 17, 10, 'Выступайте все вы, &mdash; и подойдите; не найду я мудрого между вами.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21513, 'rst66', 'job', 17, 11, 'Дни мои прошли; думы мои &mdash; достояние сердца моего &mdash; разбиты.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21514, 'rst66', 'job', 17, 12, 'А они ночь <i>хотят</i> превратить в день, свет приблизить к лицу тьмы.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21515, 'rst66', 'job', 17, 13, 'Если бы я и ожидать стал, то преисподняя &mdash; дом мой; во тьме постелю я постель мою;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21516, 'rst66', 'job', 17, 14, 'гробу скажу: «ты отец мой», червю: «ты мать моя и сестра моя».');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21517, 'rst66', 'job', 17, 15, 'Где же после этого надежда моя? и ожидаемое мною кто увидит?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21518, 'rst66', 'job', 17, 16, 'В преисподнюю сойдет она и будет покоиться со мною в прахе.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21519, 'rst66', 'job', 18, 1, 'И отвечал Вилдад Савхеянин и сказал:');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21520, 'rst66', 'job', 18, 2, 'когда же положите вы конец таким речам? обдумайте, и потом будем говорить.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21521, 'rst66', 'job', 18, 3, 'Зачем считаться нам за животных и быть униженными в собственных глазах ваших?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21522, 'rst66', 'job', 18, 4, '<i>О ты</i>, раздирающий душу твою в гневе твоем! Неужели для тебя опустеть земле, и скале сдвинуться с места своего?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21523, 'rst66', 'job', 18, 5, 'Да, свет у беззаконного потухнет, и не останется искры от огня его.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21524, 'rst66', 'job', 18, 6, 'Померкнет свет в шатре его, и светильник его угаснет над ним.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21525, 'rst66', 'job', 18, 7, 'Сократятся шаги могущества его, и низложит его собственный замысл его,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21526, 'rst66', 'job', 18, 8, 'ибо он попадет в сеть своими ногами и по тенетам ходить будет.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21527, 'rst66', 'job', 18, 9, 'Петля зацепит за ногу его, и грабитель уловит его.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21528, 'rst66', 'job', 18, 10, 'Скрытно разложены по земле силки для него и западни на дороге.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21529, 'rst66', 'job', 18, 11, 'Со всех сторон будут страшить его ужасы и заставят его бросаться туда и сюда.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21530, 'rst66', 'job', 18, 12, 'Истощится от голода сила его, и гибель готова, сбоку у него.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21531, 'rst66', 'job', 18, 13, 'Съест члены тела его, съест члены его первенец смерти.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21532, 'rst66', 'job', 18, 14, 'Изгнана будет из шатра его надежда его, и это низведет его к царю ужасов.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21533, 'rst66', 'job', 18, 15, 'Поселятся в шатре его, потому что он уже не его; жилище его посыпано будет серою.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21534, 'rst66', 'job', 18, 16, 'Снизу подсохнут корни его, и сверху увянут ветви его.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21535, 'rst66', 'job', 18, 17, 'Память о нем исчезнет с земли, и имени его не будет на площади.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21536, 'rst66', 'job', 18, 18, 'Изгонят его из света во тьму и сотрут его с лица земли.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21537, 'rst66', 'job', 18, 19, 'Ни сына его, ни внука не будет в народе его, и никого не останется в жилищах его.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21538, 'rst66', 'job', 18, 20, 'О дне его ужаснутся потомки, и современники будут объяты трепетом.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21539, 'rst66', 'job', 18, 21, 'Таковы жилища беззаконного, и таково место того, кто не знает Бога.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21540, 'rst66', 'job', 19, 1, 'И отвечал Иов и сказал:');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21541, 'rst66', 'job', 19, 2, 'доколе будете мучить душу мою и терзать меня речами?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21542, 'rst66', 'job', 19, 3, 'Вот, уже раз десять вы срамили меня и не стыдитесь теснить меня.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21543, 'rst66', 'job', 19, 4, 'Если я и действительно погрешил, то погрешность моя при мне остается.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21544, 'rst66', 'job', 19, 5, 'Если же вы хотите повеличаться надо мною и упрекнуть меня позором моим,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21545, 'rst66', 'job', 19, 6, 'то знайте, что Бог ниспроверг меня и обложил меня Своею сетью.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21546, 'rst66', 'job', 19, 7, 'Вот, я кричу: «обида!», и никто не слушает; вопию, и нет суда.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21547, 'rst66', 'job', 19, 8, 'Он преградил мне дорогу, и не могу пройти, и на стези мои положил тьму.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21548, 'rst66', 'job', 19, 9, 'Совлек с меня славу мою и снял венец с головы моей.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21549, 'rst66', 'job', 19, 10, 'Кругом разорил меня, и я отхожу; и, как дерево, Он исторг надежду мою.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21550, 'rst66', 'job', 19, 11, 'Воспылал на меня гневом Своим и считает меня между врагами Своими.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21551, 'rst66', 'job', 19, 12, 'Полки Его пришли вместе и направили путь свой ко мне и расположились вокруг шатра моего.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21552, 'rst66', 'job', 19, 13, 'Братьев моих Он удалил от меня, и знающие меня чуждаются меня.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21553, 'rst66', 'job', 19, 14, 'Покинули меня близкие мои, и знакомые мои забыли меня.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21554, 'rst66', 'job', 19, 15, 'Пришлые в доме моем, и служанки мои чужим считают меня; посторонним стал я в глазах их.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21555, 'rst66', 'job', 19, 16, 'Зову слугу моего, и он не откликается; устами моими я должен умолять его.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21556, 'rst66', 'job', 19, 17, 'Дыхание мое опротивело жене моей, и я должен умолять ее ради детей чрева моего.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21557, 'rst66', 'job', 19, 18, 'Даже малые дети презирают меня: поднимаюсь, и они издеваются надо мною.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21558, 'rst66', 'job', 19, 19, 'Гнушаются мною все наперсники мои, и те, которых я любил, обратились против меня.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21559, 'rst66', 'job', 19, 20, 'Кости мои прилипли к коже моей и плоти моей, и я остался только с кожею около зубов моих.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21560, 'rst66', 'job', 19, 21, 'Помилуйте меня, помилуйте меня вы, друзья мои, ибо рука Божия коснулась меня.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21561, 'rst66', 'job', 19, 22, 'Зачем и вы преследуете меня, как Бог, и плотью моею не можете насытиться?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21562, 'rst66', 'job', 19, 23, 'О, если бы записаны были слова мои! Если бы начертаны были они в книге');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21563, 'rst66', 'job', 19, 24, 'резцом железным с оловом, &mdash; на вечное время на камне вырезаны были!');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21564, 'rst66', 'job', 19, 25, 'А я знаю, Искупитель мой жив, и Он в последний день восставит из праха распадающуюся кожу мою сию,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21565, 'rst66', 'job', 19, 26, 'и я во плоти моей узрю Бога.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21566, 'rst66', 'job', 19, 27, 'Я узрю Его сам; мои глаза, не глаза другого, увидят Его. Истаевает сердце мое в груди моей!');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21567, 'rst66', 'job', 19, 28, 'Вам надлежало бы сказать: «зачем мы преследуем его?» Как будто корень зла найден во мне.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21568, 'rst66', 'job', 19, 29, 'Убойтесь меча, ибо меч есть отмститель неправды, и знайте, что есть суд.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21569, 'rst66', 'job', 20, 1, 'И отвечал Софар Наамитянин и сказал:');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21570, 'rst66', 'job', 20, 2, 'размышления мои побуждают меня отвечать, и я поспешаю выразить их.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21571, 'rst66', 'job', 20, 3, 'Упрек, позорный для меня, выслушал я, и дух разумения моего ответит за меня.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21572, 'rst66', 'job', 20, 4, 'Разве не знаешь ты, что от века &mdash; с того времени, как поставлен человек на земле, &mdash;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21573, 'rst66', 'job', 20, 5, 'веселье беззаконных кратковременно, и радость лицемера мгновенна?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21574, 'rst66', 'job', 20, 6, 'Хотя бы возросло до небес величие его, и голова его касалась облаков, &mdash;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21575, 'rst66', 'job', 20, 7, 'как помет его, навеки пропадает он; видевшие его скажут: «где он?»');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21576, 'rst66', 'job', 20, 8, 'Как сон, улетит, и не найдут его; и, как ночное видение, исчезнет.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21577, 'rst66', 'job', 20, 9, 'Глаз, видевший его, больше не увидит его, и уже не усмотрит его место его.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21578, 'rst66', 'job', 20, 10, 'Сыновья его будут заискивать у нищих, и руки его возвратят похищенное им.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21579, 'rst66', 'job', 20, 11, 'Кости его наполнены грехами юности его, и с ним лягут они в прах.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21580, 'rst66', 'job', 20, 12, 'Если сладко во рту его зло, и он таит его под языком своим,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21581, 'rst66', 'job', 20, 13, 'бережет и не бросает его, а держит его в устах своих,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21582, 'rst66', 'job', 20, 14, 'то эта пища его в утробе его превратится в желчь аспидов внутри его.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21583, 'rst66', 'job', 20, 15, 'Имение, которое он глотал, изблюет: Бог исторгнет его из чрева его.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21584, 'rst66', 'job', 20, 16, 'Змеиный яд он сосет; умертвит его язык ехидны.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21585, 'rst66', 'job', 20, 17, 'Не видать ему ручьев, рек, текущих медом и молоком!');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21586, 'rst66', 'job', 20, 18, 'Нажитое трудом возвратит, не проглотит; по мере имения его будет и расплата его, а он не порадуется.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21587, 'rst66', 'job', 20, 19, 'Ибо он угнетал, отсылал бедных; захватывал домы, которых не строил;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21588, 'rst66', 'job', 20, 20, 'не знал сытости во чреве своем и в жадности своей не щадил ничего.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21589, 'rst66', 'job', 20, 21, 'Ничего не спасалось от обжорства его, за то не устоит счастье его.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21590, 'rst66', 'job', 20, 22, 'В полноте изобилия будет тесно ему; всякая рука обиженного поднимется на него.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21591, 'rst66', 'job', 20, 23, 'Когда будет чем наполнить утробу его, Он пошлет на него ярость гнева Своего и одождит на него болезни в плоти его.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21592, 'rst66', 'job', 20, 24, 'Убежит ли он от оружия железного, &mdash; пронзит его лук медный;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21593, 'rst66', 'job', 20, 25, 'станет вынимать <i>стрелу</i>, &mdash; и она выйдет из тела, выйдет, сверкая сквозь желчь его; ужасы смерти найдут на него!');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21594, 'rst66', 'job', 20, 26, 'Все мрачное сокрыто внутри его; будет пожирать его огонь, никем не раздуваемый; зло постигнет и оставшееся в шатре его.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21595, 'rst66', 'job', 20, 27, 'Небо откроет беззаконие его, и земля восстанет против него.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21596, 'rst66', 'job', 20, 28, 'Исчезнет стяжание дома его; все расплывется в день гнева Его.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21597, 'rst66', 'job', 20, 29, 'Вот удел человеку беззаконному от Бога и наследие, определенное ему Вседержителем!');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21598, 'rst66', 'job', 21, 1, 'И отвечал Иов и сказал:');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21599, 'rst66', 'job', 21, 2, 'выслушайте внимательно речь мою, и это будет мне утешением от вас.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21600, 'rst66', 'job', 21, 3, 'Потерпите меня, и я буду говорить; а после того, как поговорю, насмехайся.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21601, 'rst66', 'job', 21, 4, 'Разве к человеку речь моя? как же мне и не малодушествовать?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21602, 'rst66', 'job', 21, 5, 'Посмотрите на меня и ужаснитесь, и положите перст на уста.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21603, 'rst66', 'job', 21, 6, 'Лишь только я вспомню &mdash; содрогаюсь, и трепет объемлет тело мое.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21604, 'rst66', 'job', 21, 7, 'Почему беззаконные живут, достигают старости, да и силами крепки?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21605, 'rst66', 'job', 21, 8, 'Дети их с ними перед лицом их, и внуки их перед глазами их.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21606, 'rst66', 'job', 21, 9, 'Домы их безопасны от страха, и нет жезла Божия на них.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21607, 'rst66', 'job', 21, 10, 'Вол их оплодотворяет и не извергает, корова их зачинает и не выкидывает.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21608, 'rst66', 'job', 21, 11, 'Как стадо, выпускают они малюток своих, и дети их прыгают.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21609, 'rst66', 'job', 21, 12, 'Восклицают под <i>голос</i> тимпана и цитры и веселятся при <i>звуках</i> свирели;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21610, 'rst66', 'job', 21, 13, 'проводят дни свои в счастье и мгновенно нисходят в преисподнюю.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21611, 'rst66', 'job', 21, 14, 'А между тем они говорят Богу: «отойди от нас, не хотим мы знать путей Твоих!');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21612, 'rst66', 'job', 21, 15, 'Что Вседержитель, чтобы нам служить Ему? и что пользы прибегать к Нему?»');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21613, 'rst66', 'job', 21, 16, 'Видишь, счастье их не от их рук. &mdash; Совет нечестивых будь далек от меня!');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21614, 'rst66', 'job', 21, 17, 'Часто ли угасает светильник у беззаконных, и находит на них беда, и Он дает им в удел страдания во гневе Своем?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21615, 'rst66', 'job', 21, 18, 'Они должны быть, как соломинка пред ветром и как плева, уносимая вихрем.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21616, 'rst66', 'job', 21, 19, '<i>Скажешь:</i> «Бог бережет для детей его несчастье его». &mdash; Пусть воздаст Он ему самому, чтобы он это знал.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21617, 'rst66', 'job', 21, 20, 'Пусть его глаза увидят несчастье его, и пусть он сам пьет от гнева Вседержителева.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21618, 'rst66', 'job', 21, 21, 'Ибо какая ему забота до дома своего после него, когда число месяцев его кончится?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21619, 'rst66', 'job', 21, 22, 'Но Бога ли учить мудрости, когда Он судит и горних?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21620, 'rst66', 'job', 21, 23, 'Один умирает в самой полноте сил своих, совершенно спокойный и мирный;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21621, 'rst66', 'job', 21, 24, 'внутренности его полны жира, и кости его напоены мозгом.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21622, 'rst66', 'job', 21, 25, 'А другой умирает с душою огорченною, не вкусив добра.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21623, 'rst66', 'job', 21, 26, 'И они вместе будут лежать во прахе, и червь покроет их.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21624, 'rst66', 'job', 21, 27, 'Знаю я ваши мысли и ухищрения, какие вы против меня сплетаете.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21625, 'rst66', 'job', 21, 28, 'Вы скажете: «где дом князя, и где шатер, в котором жили беззаконные?»');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21626, 'rst66', 'job', 21, 29, 'Разве вы не спрашивали у путешественников и незнакомы с их наблюдениями,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21627, 'rst66', 'job', 21, 30, 'что в день погибели пощажен бывает злодей, в день гнева отводится в сторону?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21628, 'rst66', 'job', 21, 31, 'Кто представит ему пред лицо путь его, и кто воздаст ему за то, что он делал?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21629, 'rst66', 'job', 21, 32, 'Его провожают ко гробам и на его могиле ставят стражу.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21630, 'rst66', 'job', 21, 33, 'Сладки для него глыбы долины, и за ним идет толпа людей, а идущим перед ним нет числа.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21631, 'rst66', 'job', 21, 34, 'Как же вы хотите утешать меня пустым? В ваших ответах остается <i>одна</i> ложь.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21632, 'rst66', 'job', 22, 1, 'И отвечал Елифаз Феманитянин и сказал:');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21633, 'rst66', 'job', 22, 2, 'разве может человек доставлять пользу Богу? Разумный доставляет пользу себе самому.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21634, 'rst66', 'job', 22, 3, 'Что за удовольствие Вседержителю, что ты праведен? И будет ли Ему выгода от того, что ты содержишь пути твои в непорочности?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21635, 'rst66', 'job', 22, 4, 'Неужели Он, боясь тебя, вступит с тобою в состязание, пойдет судиться с тобою?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21636, 'rst66', 'job', 22, 5, 'Верно, злоба твоя велика, и беззакониям твоим нет конца.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21637, 'rst66', 'job', 22, 6, 'Верно, ты брал залоги от братьев твоих ни за что и с полунагих снимал одежду.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21638, 'rst66', 'job', 22, 7, 'Утомленному жаждою не подавал воды напиться и голодному отказывал в хлебе;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21639, 'rst66', 'job', 22, 8, 'а человеку сильному ты <i>давал</i> землю, и сановитый селился на ней.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21640, 'rst66', 'job', 22, 9, 'Вдов ты отсылал ни с чем и сирот оставлял с пустыми руками.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21641, 'rst66', 'job', 22, 10, 'За то вокруг тебя петли, и возмутил тебя неожиданный ужас,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21642, 'rst66', 'job', 22, 11, 'или тьма, в которой ты ничего не видишь, и множество вод покрыло тебя.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21643, 'rst66', 'job', 22, 12, 'Не превыше ли небес Бог? посмотри вверх на звезды, как они высоко!');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21644, 'rst66', 'job', 22, 13, 'И ты говоришь: «что знает Бог? может ли Он судить сквозь мрак?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21645, 'rst66', 'job', 22, 14, 'Облака &mdash; завеса Его, так что Он не видит, а ходит <i>только</i> по небесному кругу».');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21646, 'rst66', 'job', 22, 15, 'Неужели ты держишься пути древних, по которому шли люди беззаконные,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21647, 'rst66', 'job', 22, 16, 'которые преждевременно были истреблены, когда вода разлилась под основание их?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21648, 'rst66', 'job', 22, 17, 'Они говорили Богу: «отойди от нас!» &mdash; и что сделает им Вседержитель?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21649, 'rst66', 'job', 22, 18, 'А Он наполнял домы их добром. Но совет нечестивых будь далек от меня!');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21650, 'rst66', 'job', 22, 19, 'Видели праведники и радовались, и непорочный смеялся им:');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21651, 'rst66', 'job', 22, 20, '«Враг наш истреблен, а оставшееся после них пожрал огонь».');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21652, 'rst66', 'job', 22, 21, 'Сблизься же с Ним &mdash; и будешь спокоен; чрез это придет к тебе добро.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21653, 'rst66', 'job', 22, 22, 'Прими из уст Его закон и положи слова Его в сердце твое.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21654, 'rst66', 'job', 22, 23, 'Если ты обратишься к Вседержителю, то вновь устроишься, удалишь беззаконие от шатра твоего');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21655, 'rst66', 'job', 22, 24, 'и будешь вменять в прах блестящий металл, и в камни потоков &mdash; <i>золото</i> Офирское.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21656, 'rst66', 'job', 22, 25, 'И будет Вседержитель твоим золотом и блестящим серебром у тебя,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21657, 'rst66', 'job', 22, 26, 'ибо тогда будешь радоваться о Вседержителе и поднимешь к Богу лицо твое.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21658, 'rst66', 'job', 22, 27, 'Помолишься Ему, и Он услышит тебя, и ты исполнишь обеты твои.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21659, 'rst66', 'job', 22, 28, 'Положишь намерение, и оно состоится у тебя, и над путями твоими будет сиять свет.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21660, 'rst66', 'job', 22, 29, 'Когда кто уничижен будет, ты скажешь: «возвышение!» и Он спасет поникшего лицом,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21661, 'rst66', 'job', 22, 30, 'избавит и небезвинного, и он спасется чистотою рук твоих.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21662, 'rst66', 'job', 23, 1, 'И отвечал Иов и сказал:');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21663, 'rst66', 'job', 23, 2, 'еще и ныне горька речь моя: страдания мои тяжелее стонов моих.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21664, 'rst66', 'job', 23, 3, 'О, если бы я знал, где найти Его, и мог подойти к престолу Его!');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21665, 'rst66', 'job', 23, 4, 'Я изложил бы пред Ним дело мое и уста мои наполнил бы оправданиями;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21666, 'rst66', 'job', 23, 5, 'узнал бы слова, какими Он ответит мне, и понял бы, что Он скажет мне.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21667, 'rst66', 'job', 23, 6, 'Неужели Он в полном могуществе стал бы состязаться со мною? О, нет! Пусть Он только обратил бы внимание на меня.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21668, 'rst66', 'job', 23, 7, 'Тогда праведник мог бы состязаться с Ним, &mdash; и я навсегда получил бы свободу от Судии моего.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21669, 'rst66', 'job', 23, 8, 'Но вот, я иду вперед &mdash; и нет Его, назад &mdash; и не нахожу Его;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21670, 'rst66', 'job', 23, 9, 'делает ли Он что на левой стороне, я не вижу; скрывается ли на правой, не усматриваю.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21671, 'rst66', 'job', 23, 10, 'Но Он знает путь мой; пусть испытает меня, &mdash; выйду, как золото.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21672, 'rst66', 'job', 23, 11, 'Нога моя твердо держится стези Его; пути Его я хранил и не уклонялся.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21673, 'rst66', 'job', 23, 12, 'От заповеди уст Его не отступал; глаголы уст Его хранил больше, нежели мои правила.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21674, 'rst66', 'job', 23, 13, 'Но Он тверд; и кто отклонит Его? Он делает, чего хочет душа Его.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21675, 'rst66', 'job', 23, 14, 'Так, Он выполнит положенное мне, и подобного этому много у Него.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21676, 'rst66', 'job', 23, 15, 'Поэтому я трепещу пред лицом Его; размышляю &mdash; и страшусь Его.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21677, 'rst66', 'job', 23, 16, 'Бог расслабил сердце мое, и Вседержитель устрашил меня.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21678, 'rst66', 'job', 23, 17, 'Зачем я не уничтожен прежде этой тьмы, и Он не сокрыл мрака от лица моего!');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21679, 'rst66', 'job', 24, 1, 'Почему не сокрыты от Вседержителя времена, и знающие Его не видят дней Его?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21680, 'rst66', 'job', 24, 2, 'Межи передвигают, угоняют стада и пасут <i>у себя</i>.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21681, 'rst66', 'job', 24, 3, 'У сирот уводят осла, у вдовы берут в залог вола;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21682, 'rst66', 'job', 24, 4, 'бедных сталкивают с дороги, все уничиженные земли принуждены скрываться.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21683, 'rst66', 'job', 24, 5, 'Вот они, <i>как</i> дикие ослы в пустыне, выходят на дело свое, вставая рано на добычу; степь <i>дает</i> хлеб для них и для детей их;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21684, 'rst66', 'job', 24, 6, 'жнут они на поле не своем и собирают виноград у нечестивца;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21685, 'rst66', 'job', 24, 7, 'нагие ночуют без покрова и без одеяния на стуже;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21686, 'rst66', 'job', 24, 8, 'мокнут от горных дождей и, не имея убежища, жмутся к скале;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21687, 'rst66', 'job', 24, 9, 'отторгают от сосцов сироту и с нищего берут залог;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21688, 'rst66', 'job', 24, 10, 'заставляют ходить нагими, без одеяния, и голодных кормят колосьями;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21689, 'rst66', 'job', 24, 11, 'между стенами выжимают масло оливковое, топчут в точилах и жаждут.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21690, 'rst66', 'job', 24, 12, 'В городе люди стонут, и душа убиваемых вопит, и Бог не воспрещает того.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21691, 'rst66', 'job', 24, 13, 'Есть из них враги света, не знают путей его и не ходят по стезям его.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21692, 'rst66', 'job', 24, 14, 'С рассветом встает убийца, умерщвляет бедного и нищего, а ночью бывает вором.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21693, 'rst66', 'job', 24, 15, 'И око прелюбодея ждет сумерков, говоря: «ничей глаз не увидит меня», &mdash; и закрывает лицо.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21694, 'rst66', 'job', 24, 16, 'В темноте подкапываются под домы, которые днем они заметили для себя; не знают света.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21695, 'rst66', 'job', 24, 17, 'Ибо для них утро &mdash; смертная тень, так как они знакомы с ужасами смертной тени.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21696, 'rst66', 'job', 24, 18, 'Легок такой на поверхности воды, проклята часть его на земле, и не смотрит он на дорогу садов виноградных.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21697, 'rst66', 'job', 24, 19, 'Засуха и жара поглощают снежную воду: так преисподняя &mdash; грешников.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21698, 'rst66', 'job', 24, 20, 'Пусть забудет его утроба <i>матери;</i> пусть лакомится им червь; пусть не остается о нем память; как дерево, пусть сломится беззаконник,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21699, 'rst66', 'job', 24, 21, 'который угнетает бездетную, не рождавшую, и вдове не делает добра.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21700, 'rst66', 'job', 24, 22, 'Он и сильных увлекает своею силою; он встает, и никто не уверен за жизнь свою.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21701, 'rst66', 'job', 24, 23, 'А Он дает ему <i>все</i> для безопасности, и он <i>на то</i> опирается, и очи Его видят пути их.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21702, 'rst66', 'job', 24, 24, 'Поднялись высоко, &mdash; и вот, нет их; падают и умирают, как и все, и, как верхушки колосьев, срезываются.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21703, 'rst66', 'job', 24, 25, 'Если это не так, &mdash; кто обличит меня во лжи и в ничто обратит речь мою?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21704, 'rst66', 'job', 25, 1, 'И отвечал Вилдад Савхеянин и сказал:');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21705, 'rst66', 'job', 25, 2, 'держава и страх у Него; Он творит мир на высотах Своих!');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21706, 'rst66', 'job', 25, 3, 'Есть ли счет воинствам Его? и над кем не восходит свет Его?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21707, 'rst66', 'job', 25, 4, 'И как человеку быть правым пред Богом, и как быть чистым рожденному женщиною?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21708, 'rst66', 'job', 25, 5, 'Вот, даже луна, и та не светла, и звезды не чисты пред очами Его.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21709, 'rst66', 'job', 25, 6, 'Тем менее человек, <i>который</i> есть червь, и сын человеческий, <i>который</i> есть моль.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21710, 'rst66', 'job', 26, 1, 'И отвечал Иов и сказал:');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21711, 'rst66', 'job', 26, 2, 'как ты помог бессильному, поддержал мышцу немощного!');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21712, 'rst66', 'job', 26, 3, 'Какой совет подал ты немудрому и как во всей полноте объяснил дело!');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21713, 'rst66', 'job', 26, 4, 'Кому ты говорил эти слова, и чей дух исходил из тебя?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21714, 'rst66', 'job', 26, 5, 'Рефаимы трепещут под водами, и живущие в них.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21715, 'rst66', 'job', 26, 6, 'Преисподняя обнажена пред Ним, и нет покрывала Аваддону.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21716, 'rst66', 'job', 26, 7, 'Он распростер север над пустотою, повесил землю ни на чем.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21717, 'rst66', 'job', 26, 8, 'Он заключает воды в облаках Своих, и облако не расседается под ними.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21718, 'rst66', 'job', 26, 9, 'Он поставил престол Свой, распростер над ним облако Свое.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21719, 'rst66', 'job', 26, 10, 'Черту провел над поверхностью воды, до границ света со тьмою.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21720, 'rst66', 'job', 26, 11, 'Столпы небес дрожат и ужасаются от грозы Его.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21721, 'rst66', 'job', 26, 12, 'Силою Своею волнует море и разумом Своим сражает его дерзость.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21722, 'rst66', 'job', 26, 13, 'От духа Его &mdash; великолепие неба; рука Его образовала быстрого скорпиона.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21723, 'rst66', 'job', 26, 14, 'Вот, это части путей Его; и как мало мы слышали о Нем! А гром могущества Его кто может уразуметь?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21724, 'rst66', 'job', 27, 1, 'И продолжал Иов возвышенную речь свою и сказал:');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21725, 'rst66', 'job', 27, 2, 'жив Бог, лишивший <i>меня</i> суда, и Вседержитель, огорчивший душу мою,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21726, 'rst66', 'job', 27, 3, 'что, доколе еще дыхание мое во мне и дух Божий в ноздрях моих,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21727, 'rst66', 'job', 27, 4, 'не скажут уста мои неправды, и язык мой не произнесет лжи!');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21728, 'rst66', 'job', 27, 5, 'Далек я от того, чтобы признать вас справедливыми; доколе не умру, не уступлю непорочности моей.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21729, 'rst66', 'job', 27, 6, 'Крепко держал я правду мою и не опущу ее; не укорит меня сердце мое во все дни мои.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21730, 'rst66', 'job', 27, 7, 'Враг мой будет, как нечестивец, и восстающий на меня, как беззаконник.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21731, 'rst66', 'job', 27, 8, 'Ибо какая надежда лицемеру, когда возьмет, когда исторгнет Бог душу его?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21732, 'rst66', 'job', 27, 9, 'Услышит ли Бог вопль его, когда придет на него беда?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21733, 'rst66', 'job', 27, 10, 'Будет ли он утешаться Вседержителем и призывать Бога во всякое время?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21734, 'rst66', 'job', 27, 11, 'Возвещу вам, что в руке Божией; что у Вседержителя, не скрою.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21735, 'rst66', 'job', 27, 12, 'Вот, все вы и сами видели; и для чего вы столько пустословите?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21736, 'rst66', 'job', 27, 13, 'Вот доля человеку беззаконному от Бога, и наследие, какое получают от Вседержителя притеснители.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21737, 'rst66', 'job', 27, 14, 'Если умножаются сыновья его, то под меч; и потомки его не насытятся хлебом.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21738, 'rst66', 'job', 27, 15, 'Оставшихся по нем смерть низведет во гроб, и вдовы их не будут плакать.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21739, 'rst66', 'job', 27, 16, 'Если он наберет кучи серебра, как праха, и наготовит одежд, как брение,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21740, 'rst66', 'job', 27, 17, 'то он наготовит, а одеваться будет праведник, и серебро получит себе на долю беспорочный.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21741, 'rst66', 'job', 27, 18, 'Он строит, как моль, дом свой и, как сторож, делает себе шалаш;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21742, 'rst66', 'job', 27, 19, 'ложится спать богачом и таким не встанет; открывает глаза свои, и он уже не тот.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21743, 'rst66', 'job', 27, 20, 'Как воды, постигнут его ужасы; в ночи похитит его буря.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21744, 'rst66', 'job', 27, 21, 'Поднимет его восточный ветер и понесет, и он быстро побежит от него.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21745, 'rst66', 'job', 27, 22, 'Устремится на него и не пощадит, как бы он ни силился убежать от руки его.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21746, 'rst66', 'job', 27, 23, 'Всплеснут о нем руками и посвищут над ним с места его!');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21747, 'rst66', 'job', 28, 1, 'Так! у серебра есть источная жила, и у золота место, <i>где его</i> плавят.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21748, 'rst66', 'job', 28, 2, 'Железо получается из земли; из камня выплавляется медь.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21749, 'rst66', 'job', 28, 3, '<i>Человек</i> полагает предел тьме и тщательно разыскивает камень во мраке и тени смертной.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21750, 'rst66', 'job', 28, 4, 'Вырывают рудокопный колодезь в местах, забытых ногою, спускаются вглубь, висят <i>и</i> зыблются вдали от людей.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21751, 'rst66', 'job', 28, 5, 'Земля, на которой вырастает хлеб, внутри изрыта как бы огнем.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21752, 'rst66', 'job', 28, 6, 'Камни ее &mdash; место сапфира, и в ней песчинки золота.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21753, 'rst66', 'job', 28, 7, 'Стези <i>туда</i> не знает хищная птица, и не видал ее глаз коршуна;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21754, 'rst66', 'job', 28, 8, 'не попирали ее скимны, и не ходил по ней шакал.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21755, 'rst66', 'job', 28, 9, 'На гранит налагает он руку свою, с корнем опрокидывает горы;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21756, 'rst66', 'job', 28, 10, 'в скалах просекает каналы, и все драгоценное видит глаз его;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21757, 'rst66', 'job', 28, 11, 'останавливает течение потоков и сокровенное выносит на свет.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21758, 'rst66', 'job', 28, 12, 'Но где премудрость обретается? и где место разума?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21759, 'rst66', 'job', 28, 13, 'Не знает человек цены ее, и она не обретается на земле живых.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21760, 'rst66', 'job', 28, 14, 'Бездна говорит: «не во мне она»; и море говорит: «не у меня».');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21761, 'rst66', 'job', 28, 15, 'Не дается она за золото и не приобретается она за вес серебра;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21762, 'rst66', 'job', 28, 16, 'не оценивается она золотом Офирским, ни драгоценным ониксом, ни сапфиром;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21763, 'rst66', 'job', 28, 17, 'не равняется с нею золото и кристалл, и не выменяешь ее на сосуды из чистого золота.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21764, 'rst66', 'job', 28, 18, 'А о кораллах и жемчуге и упоминать нечего, и приобретение премудрости выше рубинов.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21765, 'rst66', 'job', 28, 19, 'Не равняется с нею топаз Ефиопский; чистым золотом не оценивается она.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21766, 'rst66', 'job', 28, 20, 'Откуда же исходит премудрость? и где место разума?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21767, 'rst66', 'job', 28, 21, 'Сокрыта она от очей всего живущего и от птиц небесных утаена.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21768, 'rst66', 'job', 28, 22, 'Аваддон и смерть говорят: «ушами нашими слышали мы слух о ней».');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21769, 'rst66', 'job', 28, 23, 'Бог знает путь ее, и Он ведает место ее.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21770, 'rst66', 'job', 28, 24, 'Ибо Он прозирает до концов земли и видит под всем небом.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21771, 'rst66', 'job', 28, 25, 'Когда Он ветру полагал вес и располагал воду по мере,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21772, 'rst66', 'job', 28, 26, 'когда назначал устав дождю и путь для молнии громоносной,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21773, 'rst66', 'job', 28, 27, 'тогда Он видел ее и явил ее, приготовил ее и еще испытал ее');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21774, 'rst66', 'job', 28, 28, 'и сказал человеку: «вот, страх Господень есть истинная премудрость, и удаление от зла &mdash; разум».');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21775, 'rst66', 'job', 29, 1, 'И продолжал Иов возвышенную речь свою и сказал:');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21776, 'rst66', 'job', 29, 2, 'о, если бы я был, как в прежние месяцы, как в те дни, когда Бог хранил меня,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21777, 'rst66', 'job', 29, 3, 'когда светильник Его светил над головою моею, и я при свете Его ходил среди тьмы;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21778, 'rst66', 'job', 29, 4, 'как был я во дни молодости моей, когда милость Божия <i>была</i> над шатром моим,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21779, 'rst66', 'job', 29, 5, 'когда еще Вседержитель <i>был</i> со мною, и дети мои вокруг меня,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21780, 'rst66', 'job', 29, 6, 'когда пути мои обливались молоком, и скала источала для меня ручьи елея!');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21781, 'rst66', 'job', 29, 7, 'Когда я выходил к воротам города и на площади ставил седалище свое, &mdash;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21782, 'rst66', 'job', 29, 8, 'юноши, увидев меня, прятались, а старцы вставали и стояли;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21783, 'rst66', 'job', 29, 9, 'князья удерживались от речи и персты полагали на уста свои;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21784, 'rst66', 'job', 29, 10, 'голос знатных умолкал, и язык их прилипал к гортани их.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21785, 'rst66', 'job', 29, 11, 'Ухо, слышавшее меня, ублажало меня; око видевшее восхваляло меня,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21786, 'rst66', 'job', 29, 12, 'потому что я спасал страдальца вопиющего и сироту беспомощного.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21787, 'rst66', 'job', 29, 13, 'Благословение погибавшего приходило на меня, и сердцу вдовы доставлял я радость.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21788, 'rst66', 'job', 29, 14, 'Я облекался в правду, и суд мой одевал меня, как мантия и увясло.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21789, 'rst66', 'job', 29, 15, 'Я был глазами слепому и ногами хромому;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21790, 'rst66', 'job', 29, 16, 'отцом был я для нищих и тяжбу, которой я не знал, разбирал внимательно.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21791, 'rst66', 'job', 29, 17, 'Сокрушал я беззаконному челюсти и из зубов его исторгал похищенное.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21792, 'rst66', 'job', 29, 18, 'И говорил я: «в гнезде моем скончаюсь, и дни <i>мои</i> будут многи, как песок;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21793, 'rst66', 'job', 29, 19, 'корень мой открыт для воды, и роса ночует на ветвях моих;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21794, 'rst66', 'job', 29, 20, 'слава моя не стареет, лук мой крепок в руке моей».');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21795, 'rst66', 'job', 29, 21, 'Внимали мне и ожидали, и безмолвствовали при совете моем.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21796, 'rst66', 'job', 29, 22, 'После слов моих уже не рассуждали; речь моя капала на них.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21797, 'rst66', 'job', 29, 23, 'Ждали меня, как дождя, и, <i>как</i> дождю позднему, открывали уста свои.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21798, 'rst66', 'job', 29, 24, 'Бывало, улыбнусь им &mdash; они не верят; и света лица моего они не помрачали.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21799, 'rst66', 'job', 29, 25, 'Я назначал пути им и сидел во главе и жил, как царь в кругу воинов, как утешитель плачущих.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21800, 'rst66', 'job', 30, 1, 'А ныне смеются надо мною младшие меня летами, те, которых отцов я не согласился бы поместить с псами стад моих.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21801, 'rst66', 'job', 30, 2, 'И сила рук их к чему мне? Над ними уже прошло время.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21802, 'rst66', 'job', 30, 3, 'Бедностью и голодом истощенные, они убегают в степь безводную, мрачную и опустевшую;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21803, 'rst66', 'job', 30, 4, 'щиплют зелень подле кустов, и ягоды можжевельника &mdash; хлеб их.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21804, 'rst66', 'job', 30, 5, 'Из общества изгоняют их, кричат на них, как на воров,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21805, 'rst66', 'job', 30, 6, 'чтобы жили они в рытвинах потоков, в ущельях земли и утесов.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21806, 'rst66', 'job', 30, 7, 'Ревут между кустами, жмутся под терном.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21807, 'rst66', 'job', 30, 8, 'Люди отверженные, люди без имени, отребье земли!');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21808, 'rst66', 'job', 30, 9, 'Их‐то сделался я ныне песнью и пищею разговора их.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21809, 'rst66', 'job', 30, 10, 'Они гнушаются мною, удаляются от меня и не удерживаются плевать пред лицом моим.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21810, 'rst66', 'job', 30, 11, 'Так как Он развязал повод мой и поразил меня, то они сбросили с себя узду пред лицом моим.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21811, 'rst66', 'job', 30, 12, 'С правого боку встает это исчадие, сбивает меня с ног, направляет гибельные свои пути ко мне.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21812, 'rst66', 'job', 30, 13, 'А мою стезю испортили: всё успели сделать к моей погибели, не имея помощника.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21813, 'rst66', 'job', 30, 14, 'Они пришли ко мне, как сквозь широкий пролом; с шумом бросились на меня.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21814, 'rst66', 'job', 30, 15, 'Ужасы устремились на меня; как ветер, развеялось величие мое, и счастье мое унеслось, как облако.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21815, 'rst66', 'job', 30, 16, 'И ныне изливается душа моя во мне: дни скорби объяли меня.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21816, 'rst66', 'job', 30, 17, 'Ночью ноют во мне кости мои, и жилы мои не имеют покоя.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21817, 'rst66', 'job', 30, 18, 'С великим трудом снимается с меня одежда моя; края хитона моего жмут меня.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21818, 'rst66', 'job', 30, 19, 'Он бросил меня в грязь, и я стал, как прах и пепел.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21819, 'rst66', 'job', 30, 20, 'Я взываю к Тебе, и Ты не внимаешь мне, &mdash; стою, а Ты <i>только</i> смотришь на меня.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21820, 'rst66', 'job', 30, 21, 'Ты сделался жестоким ко мне, крепкою рукою враждуешь против меня.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21821, 'rst66', 'job', 30, 22, 'Ты поднял меня и заставил меня носиться по ветру и сокрушаешь меня.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21822, 'rst66', 'job', 30, 23, 'Так, я знаю, что Ты приведешь меня к смерти и в дом собрания всех живущих.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21823, 'rst66', 'job', 30, 24, 'Верно, Он не прострет руки Своей на дом костей: будут ли они кричать при своем разрушении?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21824, 'rst66', 'job', 30, 25, 'Не плакал ли я о том, кто был в горе? не скорбела ли душа моя о бедных?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21825, 'rst66', 'job', 30, 26, 'Когда я чаял добра, пришло зло; когда ожидал света, пришла тьма.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21826, 'rst66', 'job', 30, 27, 'Мои внутренности кипят и не перестают; встретили меня дни печали.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21827, 'rst66', 'job', 30, 28, 'Я хожу почернелый, но не от солнца; встаю в собрании и кричу.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21828, 'rst66', 'job', 30, 29, 'Я стал братом шакалам и другом страусам.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21829, 'rst66', 'job', 30, 30, 'Моя кожа почернела на мне, и кости мои обгорели от жара.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21830, 'rst66', 'job', 30, 31, 'И цитра моя сделалась унылою, и свирель моя &mdash; голосом плачевным.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21831, 'rst66', 'job', 31, 1, 'Завет положил я с глазами моими, чтобы не помышлять мне о девице.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21832, 'rst66', 'job', 31, 2, 'Какая же участь <i>мне</i> от Бога свыше? И какое наследие от Вседержителя с небес?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21833, 'rst66', 'job', 31, 3, 'Не для нечестивого ли гибель, и не для делающего ли зло напасть?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21834, 'rst66', 'job', 31, 4, 'Не видел ли Он путей моих, и не считал ли всех моих шагов?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21835, 'rst66', 'job', 31, 5, 'Если я ходил в суете, и если нога моя спешила на лукавство, &mdash;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21836, 'rst66', 'job', 31, 6, 'пусть взвесят меня на весах правды, и Бог узнает мою непорочность.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21837, 'rst66', 'job', 31, 7, 'Если стопы мои уклонялись от пути и сердце мое следовало за глазами моими, и если что‐либо <i>нечистое</i> пристало к рукам моим,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21838, 'rst66', 'job', 31, 8, 'то пусть я сею, а другой ест, и пусть отрасли мои искоренены будут.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21839, 'rst66', 'job', 31, 9, 'Если сердце мое прельщалось женщиною и я строил ковы у дверей моего ближнего, &mdash;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21840, 'rst66', 'job', 31, 10, 'пусть моя жена мелет на другого, и пусть другие издеваются над нею,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21841, 'rst66', 'job', 31, 11, 'потому что это &mdash; преступление, это &mdash; беззаконие, подлежащее суду;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21842, 'rst66', 'job', 31, 12, 'это &mdash; огонь, поядающий до истребления, который искоренил бы все добро мое.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21843, 'rst66', 'job', 31, 13, 'Если я пренебрегал правами слуги и служанки моей, когда они имели спор со мною,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21844, 'rst66', 'job', 31, 14, 'то что стал бы я делать, когда бы Бог восстал? И когда бы Он взглянул на меня, что мог бы я отвечать Ему?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21845, 'rst66', 'job', 31, 15, 'Не Он ли, Который создал меня во чреве, создал и его и равно образовал нас в утробе?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21846, 'rst66', 'job', 31, 16, 'Отказывал ли я нуждающимся в их просьбе и томил ли глаза вдовы?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21847, 'rst66', 'job', 31, 17, 'Один ли я съедал кусок мой, и не ел ли от него и сирота?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21848, 'rst66', 'job', 31, 18, 'Ибо с детства он рос со мною, как с отцом, и от чрева матери моей я руководил <i>вдову</i>.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21849, 'rst66', 'job', 31, 19, 'Если я видел кого погибавшим без одежды и бедного без покрова, &mdash;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21850, 'rst66', 'job', 31, 20, 'не благословляли ли меня чресла его, и не был ли он согрет шерстью овец моих?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21851, 'rst66', 'job', 31, 21, 'Если я поднимал руку мою на сироту, когда видел помощь себе у ворот,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21852, 'rst66', 'job', 31, 22, 'то пусть плечо мое отпадет от спины, и рука моя пусть отломится от локтя,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21853, 'rst66', 'job', 31, 23, 'ибо страшно для меня наказание от Бога: пред величием Его не устоял бы я.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21854, 'rst66', 'job', 31, 24, 'Полагал ли я в золоте опору мою и говорил ли сокровищу: «ты &mdash; надежда моя»?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21855, 'rst66', 'job', 31, 25, 'Радовался ли я, что богатство мое было велико, и что рука моя приобрела много?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21856, 'rst66', 'job', 31, 26, 'Смотря на солнце, как оно сияет, и на луну, как она величественно шествует,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21857, 'rst66', 'job', 31, 27, 'прельстился ли я в тайне сердца моего, и целовали ли уста мои руку мою?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21858, 'rst66', 'job', 31, 28, 'Это также было бы преступление, подлежащее суду, потому что я отрекся бы <i>тогда</i> от Бога Всевышнего.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21859, 'rst66', 'job', 31, 29, 'Радовался ли я погибели врага моего и торжествовал ли, когда несчастье постигало его?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21860, 'rst66', 'job', 31, 30, 'Не позволял я устам моим грешить проклятием души его.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21861, 'rst66', 'job', 31, 31, 'Не говорили ли люди шатра моего: «о, если бы мы от мяс его не насытились»?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21862, 'rst66', 'job', 31, 32, 'Странник не ночевал на улице; двери мои я отворял прохожему.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21863, 'rst66', 'job', 31, 33, 'Если бы я скрывал проступки мои, как человек, утаивая в груди моей пороки мои,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21864, 'rst66', 'job', 31, 34, 'то я боялся бы большого общества, и презрение одноплеменников страшило бы меня, и я молчал бы и не выходил бы за двери.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21865, 'rst66', 'job', 31, 35, 'О, если бы кто выслушал меня! Вот мое желание, чтобы Вседержитель отвечал мне, и чтобы защитник мой составил запись.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21866, 'rst66', 'job', 31, 36, 'Я носил бы ее на плечах моих и возлагал бы ее, как венец;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21867, 'rst66', 'job', 31, 37, 'объявил бы ему число шагов моих, сблизился бы с ним, как с князем.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21868, 'rst66', 'job', 31, 38, 'Если вопияла на меня земля моя и жаловались на меня борозды ее;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21869, 'rst66', 'job', 31, 39, 'если я ел плоды ее без платы и отягощал жизнь земледельцев,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21870, 'rst66', 'job', 31, 40, 'то пусть вместо пшеницы вырастает волчец и вместо ячменя куколь. Слова Иова кончились.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21871, 'rst66', 'job', 32, 1, 'Когда те три мужа перестали отвечать Иову, потому что он был прав в глазах своих,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21872, 'rst66', 'job', 32, 2, 'тогда воспылал гнев Елиуя, сына Варахиилова, Вузитянина из племени Рамова: воспылал гнев его на Иова за то, что он оправдывал себя больше, нежели Бога,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21873, 'rst66', 'job', 32, 3, 'а на трех друзей его воспылал гнев его за то, что они не нашли, что отвечать, а между тем обвиняли Иова.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21874, 'rst66', 'job', 32, 4, 'Елиуй ждал, пока Иов говорил, потому что они летами были старше его.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21875, 'rst66', 'job', 32, 5, 'Когда же Елиуй увидел, что нет ответа в устах тех трех мужей, тогда воспылал гнев его.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21876, 'rst66', 'job', 32, 6, 'И отвечал Елиуй, сын Варахиилов, Вузитянин, и сказал: я молод летами, а вы &mdash; старцы; поэтому я робел и боялся объявлять вам мое мнение.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21877, 'rst66', 'job', 32, 7, 'Я говорил сам себе: «пусть говорят дни и многолетие поучает мудрости».');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21878, 'rst66', 'job', 32, 8, 'Но дух в человеке и дыхание Вседержителя дает ему разумение.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21879, 'rst66', 'job', 32, 9, 'Не многолетние <i>только</i> мудры, и не старики разумеют правду.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21880, 'rst66', 'job', 32, 10, 'Поэтому я говорю: выслушайте меня, объявлю вам мое мнение и я.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21881, 'rst66', 'job', 32, 11, 'Вот, я ожидал слов ваших, &mdash; вслушивался в суждения ваши, доколе вы придумывали, что сказать.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21882, 'rst66', 'job', 32, 12, 'Я пристально смотрел на вас, и вот, никто из вас не обличает Иова и не отвечает на слова его.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21883, 'rst66', 'job', 32, 13, 'Не скажите: «мы нашли мудрость: Бог опровергнет его, а не человек».');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21884, 'rst66', 'job', 32, 14, 'Если бы он обращал слова свои ко мне, то я не вашими речами отвечал бы ему.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21885, 'rst66', 'job', 32, 15, 'Испугались, не отвечают более; перестали говорить.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21886, 'rst66', 'job', 32, 16, 'И как я ждал, а они не говорят, остановились и не отвечают более,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21887, 'rst66', 'job', 32, 17, 'то и я отвечу с моей стороны, объявлю мое мнение и я,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21888, 'rst66', 'job', 32, 18, 'ибо я полон речами, и дух во мне теснит меня.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21889, 'rst66', 'job', 32, 19, 'Вот, утроба моя, как вино неоткрытое: она готова прорваться, подобно новым мехам.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21890, 'rst66', 'job', 32, 20, 'Поговорю, и будет легче мне; открою уста мои и отвечу.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21891, 'rst66', 'job', 32, 21, 'На лицо человека смотреть не буду и никакому человеку льстить не стану,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21892, 'rst66', 'job', 32, 22, 'потому что я не умею льстить: сейчас убей меня, Творец мой.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21893, 'rst66', 'job', 33, 1, 'Итак, слушай, Иов, речи мои и внимай всем словам моим.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21894, 'rst66', 'job', 33, 2, 'Вот, я открываю уста мои, язык мой говорит в гортани моей.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21895, 'rst66', 'job', 33, 3, 'Слова мои от искренности моего сердца, и уста мои произнесут знание чистое.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21896, 'rst66', 'job', 33, 4, 'Дух Божий создал меня, и дыхание Вседержителя дало мне жизнь.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21897, 'rst66', 'job', 33, 5, 'Если можешь, отвечай мне и стань передо мною.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21898, 'rst66', 'job', 33, 6, 'Вот я, по желанию твоему, вместо Бога. Я образован также из брения;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21899, 'rst66', 'job', 33, 7, 'поэтому страх передо мною не может смутить тебя, и рука моя не будет тяжела для тебя.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21900, 'rst66', 'job', 33, 8, 'Ты говорил в уши мои, и я слышал звук слов:');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21901, 'rst66', 'job', 33, 9, '«Чист я, без порока, невинен я, и нет во мне неправды;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21902, 'rst66', 'job', 33, 10, 'а Он нашел обвинение против меня и считает меня Своим противником;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21903, 'rst66', 'job', 33, 11, 'поставил ноги мои в колоду, наблюдает за всеми путями моими».');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21904, 'rst66', 'job', 33, 12, 'Вот в этом ты не прав, отвечаю тебе, потому что Бог выше человека.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21905, 'rst66', 'job', 33, 13, 'Для чего тебе состязаться с Ним? Он не дает отчета ни в каких делах Своих.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21906, 'rst66', 'job', 33, 14, 'Бог говорит однажды и, если того не заметят, в другой раз:');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21907, 'rst66', 'job', 33, 15, 'во сне, в ночном видении, когда сон находит на людей, во время дремоты на ложе.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21908, 'rst66', 'job', 33, 16, 'Тогда Он открывает у человека ухо и запечатлевает Свое наставление,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21909, 'rst66', 'job', 33, 17, 'чтобы отвести человека от какого‐либо предприятия и удалить от него гордость,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21910, 'rst66', 'job', 33, 18, 'чтобы отвести душу его от пропасти и жизнь его от поражения мечом.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21911, 'rst66', 'job', 33, 19, 'Или он вразумляется болезнью на ложе своем и жестокою болью во всех костях своих, &mdash;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21912, 'rst66', 'job', 33, 20, 'и жизнь его отвращается от хлеба и душа его от любимой пищи.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21913, 'rst66', 'job', 33, 21, 'Плоть на нем пропадает, так что ее не видно, и показываются кости его, которых не было видно.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21914, 'rst66', 'job', 33, 22, 'И душа его приближается к могиле, и жизнь его &mdash; к смерти.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21915, 'rst66', 'job', 33, 23, 'Если есть у него Ангел‐наставник, один из тысячи, чтобы показать человеку прямой <i>путь</i> его, &mdash;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21916, 'rst66', 'job', 33, 24, '<i>Бог</i> умилосердится над ним и скажет: «освободи его от могилы; Я нашел умилостивление».');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21917, 'rst66', 'job', 33, 25, 'Тогда тело его сделается свежее, нежели в молодости; он возвратится к дням юности своей.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21918, 'rst66', 'job', 33, 26, 'Будет молиться Богу, и Он &mdash; милостив к нему; с радостью взирает на лицо его и возвращает человеку праведность его.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21919, 'rst66', 'job', 33, 27, 'Он будет смотреть на людей и говорить: «грешил я и превращал правду, и не воздано мне;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21920, 'rst66', 'job', 33, 28, 'Он освободил душу мою от могилы, и жизнь моя видит свет».');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21921, 'rst66', 'job', 33, 29, 'Вот, все это делает Бог два‐три раза с человеком,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21922, 'rst66', 'job', 33, 30, 'чтобы отвести душу его от могилы и просветить его светом живых.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21923, 'rst66', 'job', 33, 31, 'Внимай, Иов, слушай меня, молчи, и я буду говорить.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21924, 'rst66', 'job', 33, 32, 'Если имеешь, что сказать, отвечай; говори, потому что я желал бы твоего оправдания;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21925, 'rst66', 'job', 33, 33, 'если же нет, то слушай меня: молчи, и я научу тебя мудрости.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21926, 'rst66', 'job', 34, 1, 'И продолжал Елиуй и сказал:');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21927, 'rst66', 'job', 34, 2, 'выслушайте, мудрые, речь мою, и приклоните ко мне ухо, рассудительные!');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21928, 'rst66', 'job', 34, 3, 'Ибо ухо разбирает слова, как гортань различает вкус в пище.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21929, 'rst66', 'job', 34, 4, 'Установим между собою рассуждение и распознаем, что хорошо.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21930, 'rst66', 'job', 34, 5, 'Вот, Иов сказал: «я прав, но Бог лишил меня суда.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21931, 'rst66', 'job', 34, 6, 'Должен ли я лгать на правду мою? Моя рана неисцелима без вины».');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21932, 'rst66', 'job', 34, 7, 'Есть ли такой человек, как Иов, который пьет глумление, как воду,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21933, 'rst66', 'job', 34, 8, 'вступает в сообщество с делающими беззаконие и ходит с людьми нечестивыми?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21934, 'rst66', 'job', 34, 9, 'Потому что он сказал: «нет пользы для человека в благоугождении Богу».');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21935, 'rst66', 'job', 34, 10, 'Итак, послушайте меня, мужи мудрые! Не может быть у Бога неправда или у Вседержителя неправосудие,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21936, 'rst66', 'job', 34, 11, 'ибо Он по делам человека поступает с ним и по путям мужа воздает ему.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21937, 'rst66', 'job', 34, 12, 'Истинно, Бог не делает неправды и Вседержитель не извращает суда.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21938, 'rst66', 'job', 34, 13, 'Кто, кроме Его, промышляет о земле? И кто управляет всею вселенною?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21939, 'rst66', 'job', 34, 14, 'Если бы Он обратил сердце Свое к Себе и взял к Себе дух ее и дыхание ее, &mdash;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21940, 'rst66', 'job', 34, 15, 'вдруг погибла бы всякая плоть, и человек возвратился бы в прах.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21941, 'rst66', 'job', 34, 16, 'Итак, если ты имеешь разум, то слушай это и внимай словам моим.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21942, 'rst66', 'job', 34, 17, 'Ненавидящий правду может ли владычествовать? И можешь ли ты обвинить Всеправедного?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21943, 'rst66', 'job', 34, 18, 'Можно ли сказать царю: «ты &mdash; нечестивец», и князьям: «вы &mdash; беззаконники»?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21944, 'rst66', 'job', 34, 19, 'Но Он не смотрит и на лица князей и не предпочитает богатого бедному, потому что все они &mdash; дело рук Его.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21945, 'rst66', 'job', 34, 20, 'Внезапно они умирают; среди ночи народ возмутится, и они исчезают; и сильных изгоняют не силою.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21946, 'rst66', 'job', 34, 21, 'Ибо очи Его над путями человека, и Он видит все шаги его.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21947, 'rst66', 'job', 34, 22, 'Нет тьмы, ни тени смертной, где могли бы укрыться делающие беззаконие.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21948, 'rst66', 'job', 34, 23, 'Потому Он уже не требует от человека, чтобы шел на суд с Богом.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21949, 'rst66', 'job', 34, 24, 'Он сокрушает сильных без исследования и поставляет других на их места;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21950, 'rst66', 'job', 34, 25, 'потому что Он делает известными дела их и низлагает их ночью, и они истребляются.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21951, 'rst66', 'job', 34, 26, 'Он поражает их, как беззаконных людей, пред глазами других,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21952, 'rst66', 'job', 34, 27, 'за то, что они отвратились от Него и не уразумели всех путей Его,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21953, 'rst66', 'job', 34, 28, 'так что дошел до Него вопль бедных, и Он услышал стенание угнетенных.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21954, 'rst66', 'job', 34, 29, 'Дарует ли Он тишину, кто может возмутить? скрывает ли Он лицо Свое, кто может увидеть Его? Будет ли это для народа, или для одного человека,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21955, 'rst66', 'job', 34, 30, 'чтобы не царствовал лицемер к соблазну народа.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21956, 'rst66', 'job', 34, 31, 'К Богу должно говорить: «я потерпел, больше не буду грешить.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21957, 'rst66', 'job', 34, 32, 'А чего я не знаю, Ты научи меня; и если я сделал беззаконие, больше не буду».');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21958, 'rst66', 'job', 34, 33, 'По твоему ли <i>рассуждению</i> Он должен воздавать? И как ты отвергаешь, то тебе следует избирать, а не мне; говори, что знаешь.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21959, 'rst66', 'job', 34, 34, 'Люди разумные скажут мне, и муж мудрый, слушающий меня:');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21960, 'rst66', 'job', 34, 35, '«Иов не умно говорит, и слова его не со смыслом».');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21961, 'rst66', 'job', 34, 36, 'Я желал бы, чтобы Иов вполне был испытан, по ответам его, свойственным людям нечестивым.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21962, 'rst66', 'job', 34, 37, 'Иначе он ко греху своему прибавит отступление, будет рукоплескать между нами и еще больше наговорит против Бога.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21963, 'rst66', 'job', 35, 1, 'И продолжал Елиуй и сказал:');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21964, 'rst66', 'job', 35, 2, 'считаешь ли ты справедливым, что сказал: «я правее Бога»?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21965, 'rst66', 'job', 35, 3, 'Ты сказал: «что пользы мне? и какую прибыль я имел бы пред тем, как если бы я и грешил?»');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21966, 'rst66', 'job', 35, 4, 'Я отвечу тебе и твоим друзьям с тобою:');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21967, 'rst66', 'job', 35, 5, 'взгляни на небо и смотри; воззри на облака, они выше тебя.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21968, 'rst66', 'job', 35, 6, 'Если ты грешишь, что делаешь ты Ему? и если преступления твои умножаются, что причиняешь ты Ему?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21969, 'rst66', 'job', 35, 7, 'Если ты праведен, что даешь Ему? или что получает Он от руки твоей?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21970, 'rst66', 'job', 35, 8, 'Нечестие твое относится к человеку, как ты, и праведность твоя к сыну человеческому.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21971, 'rst66', 'job', 35, 9, 'От множества притеснителей стонут притесняемые, и от руки сильных вопиют.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21972, 'rst66', 'job', 35, 10, 'Но никто не говорит: «где Бог, Творец мой, Который дает песни в ночи,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21973, 'rst66', 'job', 35, 11, 'Который научает нас более, нежели скотов земных, и вразумляет нас более, нежели птиц небесных?»');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21974, 'rst66', 'job', 35, 12, 'Там они вопиют, и Он не отвечает им, по причине гордости злых людей.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21975, 'rst66', 'job', 35, 13, 'Но неправда, что Бог не слышит и Вседержитель не взирает на это.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21976, 'rst66', 'job', 35, 14, 'Хотя ты сказал, что ты не видишь Его, но суд пред Ним, и &mdash; жди его.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21977, 'rst66', 'job', 35, 15, 'Но ныне, потому что гнев Его не посетил его и он не познал его во всей строгости,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21978, 'rst66', 'job', 35, 16, 'Иов и открыл легкомысленно уста свои и безрассудно расточает слова.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21979, 'rst66', 'job', 36, 1, 'И продолжал Елиуй и сказал:');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21980, 'rst66', 'job', 36, 2, 'подожди меня немного, и я покажу тебе, что я имею еще что сказать за Бога.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21981, 'rst66', 'job', 36, 3, 'Начну мои рассуждения издалека и воздам Создателю моему справедливость,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21982, 'rst66', 'job', 36, 4, 'потому что слова мои точно не ложь: пред тобою &mdash; совершенный в познаниях.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21983, 'rst66', 'job', 36, 5, 'Вот, Бог могуществен и не презирает сильного крепостью сердца;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21984, 'rst66', 'job', 36, 6, 'Он не поддерживает нечестивых и воздает должное угнетенным;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21985, 'rst66', 'job', 36, 7, 'Он не отвращает очей Своих от праведников, но с царями навсегда посаждает их на престоле, и они возвышаются.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21986, 'rst66', 'job', 36, 8, 'Если же они окованы цепями и содержатся в узах бедствия,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21987, 'rst66', 'job', 36, 9, 'то Он указывает им на дела их и на беззакония их, потому что умножились,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21988, 'rst66', 'job', 36, 10, 'и открывает их ухо для вразумления и говорит им, чтоб они отстали от нечестия.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21989, 'rst66', 'job', 36, 11, 'Если послушают и будут служить Ему, то проведут дни свои в благополучии и лета свои в радости;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21990, 'rst66', 'job', 36, 12, 'если же не послушают, то погибнут от стрелы и умрут в неразумии.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21991, 'rst66', 'job', 36, 13, 'Но лицемеры питают в сердце гнев и не взывают к Нему, когда Он заключает их в узы;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21992, 'rst66', 'job', 36, 14, 'поэтому душа их умирает в молодости и жизнь их с блудниками.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21993, 'rst66', 'job', 36, 15, 'Он спасает бедного от беды его и в угнетении открывает ухо его.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21994, 'rst66', 'job', 36, 16, 'И тебя вывел бы Он из тесноты на простор, где нет стеснения, и поставляемое на стол твой было бы наполнено туком;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21995, 'rst66', 'job', 36, 17, 'но ты преисполнен суждениями нечестивых: суждение и осуждение &mdash; близки.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21996, 'rst66', 'job', 36, 18, 'Да не поразит тебя гнев <i>Божий</i> наказанием! Большой выкуп не спасет тебя.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21997, 'rst66', 'job', 36, 19, 'Даст ли Он какую цену твоему богатству? Нет, &mdash; ни золоту и никакому сокровищу.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21998, 'rst66', 'job', 36, 20, 'Не желай той ночи, когда народы истребляются на своем месте.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (21999, 'rst66', 'job', 36, 21, 'Берегись, не склоняйся к нечестию, которое ты предпочел страданию.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22000, 'rst66', 'job', 36, 22, 'Бог высок могуществом Своим, и кто такой, как Он, наставник?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22001, 'rst66', 'job', 36, 23, 'Кто укажет Ему путь Его; кто может сказать: «Ты поступаешь несправедливо»?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22002, 'rst66', 'job', 36, 24, 'Помни о том, чтобы превозносить дела Его, которые люди видят.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22003, 'rst66', 'job', 36, 25, 'Все люди могут видеть их; человек может усматривать их издали.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22004, 'rst66', 'job', 36, 26, 'Вот, Бог велик, и мы не можем познать Его; число лет Его неисследимо.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22005, 'rst66', 'job', 36, 27, 'Он собирает капли воды; они во множестве изливаются дождем:');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22006, 'rst66', 'job', 36, 28, 'из облаков каплют и изливаются обильно на людей.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22007, 'rst66', 'job', 36, 29, 'Кто может также постигнуть протяжение облаков, треск шатра Его?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22008, 'rst66', 'job', 36, 30, 'Вот, Он распространяет над ним свет Свой и покрывает дно моря.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22009, 'rst66', 'job', 36, 31, 'Оттуда Он судит народы, дает пищу в изобилии.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22010, 'rst66', 'job', 36, 32, 'Он сокрывает в дланях Своих молнию и повелевает ей, кого разить.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22011, 'rst66', 'job', 36, 33, 'Треск ее дает знать о ней; скот также чувствует происходящее.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22012, 'rst66', 'job', 37, 1, 'И от сего трепещет сердце мое и подвиглось с места своего.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22013, 'rst66', 'job', 37, 2, 'Слушайте, слушайте голос Его и гром, исходящий из уст Его.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22014, 'rst66', 'job', 37, 3, 'Под всем небом раскат его, и блистание его &mdash; до краев земли.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22015, 'rst66', 'job', 37, 4, 'За ним гремит глас; гремит Он гласом величества Своего и не останавливает его, когда голос Его услышан.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22016, 'rst66', 'job', 37, 5, 'Дивно гремит Бог гласом Своим, делает дела великие, для нас непостижимые.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22017, 'rst66', 'job', 37, 6, 'Ибо снегу Он говорит: «будь на земле»; равно мелкий дождь и большой дождь в Его власти.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22018, 'rst66', 'job', 37, 7, 'Он полагает печать на руку каждого человека, чтобы все люди знали дело Его.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22019, 'rst66', 'job', 37, 8, 'Тогда зверь уходит в убежище и остается в своих логовищах.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22020, 'rst66', 'job', 37, 9, 'От юга приходит буря, от севера &mdash; стужа.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22021, 'rst66', 'job', 37, 10, 'От дуновения Божия происходит лед, и поверхность воды сжимается.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22022, 'rst66', 'job', 37, 11, 'Также влагою Он наполняет тучи, и облака сыплют свет Его,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22023, 'rst66', 'job', 37, 12, 'и они направляются по намерениям Его, чтоб исполнить то, что Он повелит им на лице обитаемой земли.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22024, 'rst66', 'job', 37, 13, 'Он повелевает им идти или для наказания, или в благоволение, или для помилования.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22025, 'rst66', 'job', 37, 14, 'Внимай сему, Иов; стой и разумевай чудные дела Божии.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22026, 'rst66', 'job', 37, 15, 'Знаешь ли, как Бог располагает ими и повелевает свету блистать из облака Своего?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22027, 'rst66', 'job', 37, 16, 'Разумеешь ли равновесие облаков, чудное дело Совершеннейшего в знании?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22028, 'rst66', 'job', 37, 17, 'Как нагревается твоя одежда, когда Он успокаивает землю от юга?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22029, 'rst66', 'job', 37, 18, 'Ты ли с Ним распростер небеса, твердые, как литое зеркало?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22030, 'rst66', 'job', 37, 19, 'Научи нас, что сказать Ему? Мы в этой тьме ничего не можем сообразить.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22031, 'rst66', 'job', 37, 20, 'Будет ли возвещено Ему, что я говорю? Сказал ли кто, что сказанное доносится Ему?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22032, 'rst66', 'job', 37, 21, 'Теперь не видно яркого света в облаках, но пронесется ветер и расчистит их.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22033, 'rst66', 'job', 37, 22, 'Светлая погода приходит от севера, и окрест Бога страшное великолепие.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22034, 'rst66', 'job', 37, 23, 'Вседержитель! мы не постигаем Его. Он велик силою, судом и полнотою правосудия. Он <i>никого</i> не угнетает.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22035, 'rst66', 'job', 37, 24, 'Посему да благоговеют пред Ним люди, и да трепещут пред Ним все мудрые сердцем!');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22036, 'rst66', 'job', 38, 1, 'Господь отвечал Иову из бури и сказал:');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22037, 'rst66', 'job', 38, 2, 'кто сей, омрачающий Провидение словами без смысла?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22038, 'rst66', 'job', 38, 3, 'Препояшь ныне чресла твои, как муж: Я буду спрашивать тебя, и ты объясняй Мне:');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22039, 'rst66', 'job', 38, 4, 'где был ты, когда Я полагал основания земли? Скажи, если знаешь.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22040, 'rst66', 'job', 38, 5, 'Кто положил меру ей, если знаешь? или кто протягивал по ней вервь?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22041, 'rst66', 'job', 38, 6, 'На чем утверждены основания ее, или кто положил краеугольный камень ее,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22042, 'rst66', 'job', 38, 7, 'при общем ликовании утренних звезд, когда все сыны Божии восклицали от радости?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22043, 'rst66', 'job', 38, 8, 'Кто затворил море воротами, когда оно исторглось, вышло как бы из чрева,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22044, 'rst66', 'job', 38, 9, 'когда Я облака сделал одеждою его и мглу пеленами его,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22045, 'rst66', 'job', 38, 10, 'и утвердил ему Мое определение, и поставил запоры и ворота,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22046, 'rst66', 'job', 38, 11, 'и сказал: «доселе дойдешь и не перейдешь, и здесь предел надменным волнам твоим»?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22047, 'rst66', 'job', 38, 12, 'Давал ли ты когда в жизни своей приказания утру и указывал ли заре место ее,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22048, 'rst66', 'job', 38, 13, 'чтобы она охватила края земли и стряхнула с нее нечестивых,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22049, 'rst66', 'job', 38, 14, 'чтобы <i>земля</i> изменилась, как глина под печатью, и стала, как разноцветная одежда,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22050, 'rst66', 'job', 38, 15, 'и чтобы отнялся у нечестивых свет их и дерзкая рука их сокрушилась?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22051, 'rst66', 'job', 38, 16, 'Нисходил ли ты во глубину моря и входил ли в исследование бездны?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22052, 'rst66', 'job', 38, 17, 'Отворялись ли для тебя врата смерти, и видел ли ты врата тени смертной?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22053, 'rst66', 'job', 38, 18, 'Обозрел ли ты широту земли? Объясни, если знаешь все это.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22054, 'rst66', 'job', 38, 19, 'Где путь к жилищу света, и где место тьмы?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22055, 'rst66', 'job', 38, 20, 'Ты, конечно, доходил до границ ее и знаешь стези к дому ее.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22056, 'rst66', 'job', 38, 21, 'Ты знаешь это, потому что ты был уже тогда рожден, и число дней твоих очень велико.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22057, 'rst66', 'job', 38, 22, 'Входил ли ты в хранилища снега и видел ли сокровищницы града,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22058, 'rst66', 'job', 38, 23, 'которые берегу Я на время смутное, на день битвы и войны?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22059, 'rst66', 'job', 38, 24, 'По какому пути разливается свет и разносится восточный ветер по земле?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22060, 'rst66', 'job', 38, 25, 'Кто проводит протоки для излияния воды и путь для громоносной молнии,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22061, 'rst66', 'job', 38, 26, 'чтобы шел дождь на землю безлюдную, на пустыню, где нет человека,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22062, 'rst66', 'job', 38, 27, 'чтобы насыщать пустыню и степь и возбуждать травные зародыши к возрастанию?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22063, 'rst66', 'job', 38, 28, 'Есть ли у дождя отец? или кто рождает капли росы?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22064, 'rst66', 'job', 38, 29, 'Из чьего чрева выходит лед, и иней небесный, &mdash; кто рождает его?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22065, 'rst66', 'job', 38, 30, 'Воды, как камень, крепнут, и поверхность бездны замерзает.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22066, 'rst66', 'job', 38, 31, 'Можешь ли ты связать узел Хима и разрешить узы Кесиль?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22067, 'rst66', 'job', 38, 32, 'Можешь ли выводить созвездия в свое время и вести Ас с ее детьми?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22068, 'rst66', 'job', 38, 33, 'Знаешь ли ты уставы неба, можешь ли установить господство его на земле?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22069, 'rst66', 'job', 38, 34, 'Можешь ли возвысить голос твой к облакам, чтобы вода в обилии покрыла тебя?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22070, 'rst66', 'job', 38, 35, 'Можешь ли посылать молнии, и пойдут ли они и скажут ли тебе: «вот мы»?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22071, 'rst66', 'job', 38, 36, 'Кто вложил мудрость в сердце, или кто дал смысл разуму?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22072, 'rst66', 'job', 38, 37, 'Кто может расчислить облака своею мудростью и удержать сосуды неба,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22073, 'rst66', 'job', 38, 38, 'когда пыль обращается в грязь и глыбы слипаются?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22074, 'rst66', 'job', 38, 39, 'Ты ли ловишь добычу львице и насыщаешь молодых львов,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22075, 'rst66', 'job', 38, 40, 'когда они лежат в берлогах или покоятся под тенью в засаде?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22076, 'rst66', 'job', 38, 41, 'Кто приготовляет во&#769;рону корм его, когда птенцы его кричат к Богу, бродя без пищи?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22077, 'rst66', 'job', 39, 1, 'Знаешь ли ты время, когда рождают дикие козы на скалах, и замечал ли роды ланей?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22078, 'rst66', 'job', 39, 2, 'Можешь ли расчислить месяцы беременности их? и знаешь ли время родов их?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22079, 'rst66', 'job', 39, 3, 'Они изгибаются, рождая детей своих, выбрасывая свои ноши;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22080, 'rst66', 'job', 39, 4, 'дети их приходят в силу, растут на поле, уходят и не возвращаются к ним.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22081, 'rst66', 'job', 39, 5, 'Кто пустил дикого осла на свободу, и кто разрешил узы онагру,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22082, 'rst66', 'job', 39, 6, 'которому степь Я назначил домом и солончаки &mdash; жилищем?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22083, 'rst66', 'job', 39, 7, 'Он посмевается городскому многолюдству и не слышит криков погонщика,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22084, 'rst66', 'job', 39, 8, 'по горам ищет себе пищи и гоняется за всякою зеленью.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22085, 'rst66', 'job', 39, 9, 'Захочет ли единорог служить тебе и переночует ли у яслей твоих?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22086, 'rst66', 'job', 39, 10, 'Можешь ли веревкою привязать единорога к борозде, и станет ли он боронить за тобою поле?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22087, 'rst66', 'job', 39, 11, 'Понадеешься ли на него, потому что у него сила велика, и предоставишь ли ему работу твою?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22088, 'rst66', 'job', 39, 12, 'Поверишь ли ему, что он семена твои возвратит и сложит на гумно твое?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22089, 'rst66', 'job', 39, 13, 'Ты ли дал красивые крылья павлину и перья и пух страусу?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22090, 'rst66', 'job', 39, 14, 'Он оставляет яйца свои на земле, и на песке согревает их,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22091, 'rst66', 'job', 39, 15, 'и забывает, что нога может раздавить их и полевой зверь может растоптать их;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22092, 'rst66', 'job', 39, 16, 'он жесток к детям своим, как бы не своим, и не опасается, что труд его будет напрасен;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22093, 'rst66', 'job', 39, 17, 'потому что Бог не дал ему мудрости и не уделил ему смысла;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22094, 'rst66', 'job', 39, 18, 'а когда поднимется на высоту, посмевается коню и всаднику его.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22095, 'rst66', 'job', 39, 19, 'Ты ли дал коню силу и облек шею его гривою?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22096, 'rst66', 'job', 39, 20, 'Можешь ли ты испугать его, как саранчу? Храпение ноздрей его &mdash; ужас;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22097, 'rst66', 'job', 39, 21, 'роет ногою землю и восхищается силою; идет навстречу оружию;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22098, 'rst66', 'job', 39, 22, 'он смеется над опасностью и не робеет и не отворачивается от меча;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22099, 'rst66', 'job', 39, 23, 'колчан звучит над ним, сверкает копье и дротик;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22100, 'rst66', 'job', 39, 24, 'в порыве и ярости он глотает землю и не может стоять при звуке трубы;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22101, 'rst66', 'job', 39, 25, 'при трубном звуке он издает голос: «гу! гу!» и издалека чует битву, громкие голоса вождей и крик.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22102, 'rst66', 'job', 39, 26, 'Твоею ли мудростью летает ястреб и направляет крылья свои на полдень?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22103, 'rst66', 'job', 39, 27, 'По твоему ли слову возносится орел и устрояет на высоте гнездо свое?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22104, 'rst66', 'job', 39, 28, 'Он живет на скале и ночует на зубце утесов и на местах неприступных;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22105, 'rst66', 'job', 39, 29, 'оттуда высматривает себе пищу: глаза его смотрят далеко;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22106, 'rst66', 'job', 39, 30, 'птенцы его пьют кровь, и где труп, там и он.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22107, 'rst66', 'job', 39, 31, 'И продолжал Господь и сказал Иову:');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22108, 'rst66', 'job', 39, 32, 'будет ли состязающийся со Вседержителем еще учить? Обличающий Бога пусть отвечает Ему.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22109, 'rst66', 'job', 39, 33, 'И отвечал Иов Господу и сказал:');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22110, 'rst66', 'job', 39, 34, 'вот, я ничтожен; что буду я отвечать Тебе? Руку мою полагаю на уста мои.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22111, 'rst66', 'job', 39, 35, 'Однажды я говорил &mdash; теперь отвечать не буду, даже дважды, но более не буду.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22112, 'rst66', 'job', 40, 1, 'И отвечал Господь Иову из бури и сказал:');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22113, 'rst66', 'job', 40, 2, 'препояшь, как муж, чресла твои: Я буду спрашивать тебя, а ты объясняй Мне.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22114, 'rst66', 'job', 40, 3, 'Ты хочешь ниспровергнуть суд Мой, обвинить Меня, чтобы оправдать себя?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22115, 'rst66', 'job', 40, 4, 'Такая ли у тебя мышца, как у Бога? И можешь ли возгреметь голосом, как Он?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22116, 'rst66', 'job', 40, 5, 'Укрась же себя величием и славою, облекись в блеск и великолепие;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22117, 'rst66', 'job', 40, 6, 'излей ярость гнева твоего, посмотри на все гордое и смири его;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22118, 'rst66', 'job', 40, 7, 'взгляни на всех высокомерных и унизь их, и сокруши нечестивых на местах их;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22119, 'rst66', 'job', 40, 8, 'зарой всех их в землю и лица их покрой тьмою.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22120, 'rst66', 'job', 40, 9, 'Тогда и Я признаю, что десница твоя может спасать тебя.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22121, 'rst66', 'job', 40, 10, 'Вот бегемот, которого Я создал, как и тебя; он ест траву, как вол;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22122, 'rst66', 'job', 40, 11, 'вот, его сила в чреслах его и крепость его в мускулах чрева его;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22123, 'rst66', 'job', 40, 12, 'поворачивает хвостом своим, как кедром; жилы же на бедрах его переплетены;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22124, 'rst66', 'job', 40, 13, 'ноги у него, как медные трубы; кости у него, как железные прутья;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22125, 'rst66', 'job', 40, 14, 'это &mdash; верх путей Божиих; только Сотворивший его может приблизить к нему меч Свой;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22126, 'rst66', 'job', 40, 15, 'горы приносят ему пищу, и там все звери полевые играют;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22127, 'rst66', 'job', 40, 16, 'он ложится под тенистыми деревьями, под кровом тростника и в болотах;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22128, 'rst66', 'job', 40, 17, 'тенистые дерева покрывают его своею тенью; ивы при ручьях окружают его;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22129, 'rst66', 'job', 40, 18, 'вот, он пьет из реки и не торопится; остается спокоен, хотя бы Иордан устремился ко рту его.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22130, 'rst66', 'job', 40, 19, 'Возьмет ли кто его в глазах его и проколет ли ему нос багром?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22131, 'rst66', 'job', 40, 20, 'Можешь ли ты удою вытащить левиафана и веревкою схватить за язык его?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22132, 'rst66', 'job', 40, 21, 'Вденешь ли кольцо в ноздри его? проколешь ли иглою челюсть его?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22133, 'rst66', 'job', 40, 22, 'Будет ли он много умолять тебя и будет ли говорить с тобою кротко?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22134, 'rst66', 'job', 40, 23, 'Сделает ли он договор с тобою, и возьмешь ли его навсегда себе в рабы?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22135, 'rst66', 'job', 40, 24, 'Станешь ли забавляться им, как птичкою, и свяжешь ли его для девочек твоих?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22136, 'rst66', 'job', 40, 25, 'Будут ли продавать его товарищи ловли, разделят ли его между Хананейскими купцами?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22137, 'rst66', 'job', 40, 26, 'Можешь ли пронзить кожу его копьем и голову его рыбачьею острогою?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22138, 'rst66', 'job', 40, 27, 'Клади на него руку твою, и помни о борьбе: вперед не будешь.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22139, 'rst66', 'job', 41, 1, 'Надежда тщетна: не упадешь ли от одного взгляда его?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22140, 'rst66', 'job', 41, 2, 'Нет столь отважного, который осмелился бы потревожить его; кто же может устоять перед Моим лицом?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22141, 'rst66', 'job', 41, 3, 'Кто предварил Меня, чтобы Мне воздавать ему? под всем небом все Мое.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22142, 'rst66', 'job', 41, 4, 'Не умолчу о членах его, о силе и красивой соразмерности их.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22143, 'rst66', 'job', 41, 5, 'Кто может открыть верх одежды его, кто подойдет к двойным челюстям его?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22144, 'rst66', 'job', 41, 6, 'Кто может отворить двери лица его? круг зубов его &mdash; ужас;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22145, 'rst66', 'job', 41, 7, 'крепкие щиты его &mdash; великолепие; они скреплены как бы твердою печатью;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22146, 'rst66', 'job', 41, 8, 'один к другому прикасается близко, так что и воздух не проходит между ними;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22147, 'rst66', 'job', 41, 9, 'один с другим лежат плотно, сцепились и не раздвигаются.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22148, 'rst66', 'job', 41, 10, 'От его чихания показывается свет; глаза у него как ресницы зари;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22149, 'rst66', 'job', 41, 11, 'из пасти его выходят пламенники, выскакивают огненные искры;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22150, 'rst66', 'job', 41, 12, 'из ноздрей его выходит дым, как из кипящего горшка или котла.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22151, 'rst66', 'job', 41, 13, 'Дыхание его раскаляет угли, и из пасти его выходит пламя.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22152, 'rst66', 'job', 41, 14, 'На шее его обитает сила, и перед ним бежит ужас.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22153, 'rst66', 'job', 41, 15, 'Мясистые части тела его сплочены между собою твердо, не дрогнут.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22154, 'rst66', 'job', 41, 16, 'Сердце его твердо, как камень, и жестко, как нижний жернов.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22155, 'rst66', 'job', 41, 17, 'Когда он поднимается, силачи в страхе, совсем теряются от ужаса.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22156, 'rst66', 'job', 41, 18, 'Меч, коснувшийся его, не устоит, ни копье, ни дротик, ни латы.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22157, 'rst66', 'job', 41, 19, 'Железо он считает за солому, медь &mdash; за гнилое дерево.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22158, 'rst66', 'job', 41, 20, 'Дочь лука не обратит его в бегство; пращные камни обращаются для него в плеву.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22159, 'rst66', 'job', 41, 21, 'Булава считается у него за соломину; свисту дротика он смеется.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22160, 'rst66', 'job', 41, 22, 'Под ним острые камни, и он на острых камнях лежит в грязи.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22161, 'rst66', 'job', 41, 23, 'Он кипятит пучину, как котел, и море претворяет в кипящую мазь;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22162, 'rst66', 'job', 41, 24, 'оставляет за собою светящуюся стезю; бездна кажется сединою.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22163, 'rst66', 'job', 41, 25, 'Нет на земле подобного ему; он сотворен бесстрашным;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22164, 'rst66', 'job', 41, 26, 'на все высокое смотрит смело; он царь над всеми сынами гордости.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22165, 'rst66', 'job', 42, 1, 'И отвечал Иов Господу и сказал:');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22166, 'rst66', 'job', 42, 2, 'знаю, что Ты все можешь, и что намерение Твое не может быть остановлено.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22167, 'rst66', 'job', 42, 3, 'Кто сей, помрачающий	 Провидение, ничего не разумея? &mdash; Так, я говорил о том, чего не разумел, о делах чудных для меня, которых я не знал.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22168, 'rst66', 'job', 42, 4, 'Выслушай, <i>взывал я</i>, и я буду говорить, и что буду спрашивать у Тебя, объясни мне.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22169, 'rst66', 'job', 42, 5, 'Я слышал о Тебе слухом уха; теперь же мои глаза видят Тебя;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22170, 'rst66', 'job', 42, 6, 'поэтому я отрекаюсь и раскаиваюсь в прахе и пепле.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22171, 'rst66', 'job', 42, 7, 'И было после того, как Господь сказал слова те Иову, сказал Господь Елифазу Феманитянину: горит гнев Мой на тебя и на двух друзей твоих за то, что вы говорили о Мне не так верно, как раб Мой Иов.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22172, 'rst66', 'job', 42, 8, 'Итак возьмите себе семь тельцов и семь овнов и пойдите к рабу Моему Иову и принесите за себя жертву; и раб Мой Иов помолится за вас, ибо только лицо его Я приму, дабы не отвергнуть вас за то, что вы говорили о Мне не так верно, как раб Мой Иов.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22173, 'rst66', 'job', 42, 9, 'И пошли Елифаз Феманитянин и Вилдад Савхеянин и Софар Наамитянин, и сделали так, как Господь повелел им, &mdash; и Господь принял лицо Иова.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22174, 'rst66', 'job', 42, 10, 'И возвратил Господь потерю Иова, когда он помолился за друзей своих; и дал Господь Иову вдвое больше того, что он имел прежде.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22175, 'rst66', 'job', 42, 11, 'Тогда пришли к нему все братья его и все сестры его и все прежние знакомые его, и ели с ним хлеб в доме его, и тужили с ним, и утешали его за все зло, которое Господь навел на него, и дали ему каждый по кесите и по золотому кольцу.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22176, 'rst66', 'job', 42, 12, 'И благословил Бог последние дни Иова более, нежели прежние: у него было четырнадцать тысяч мелкого скота, шесть тысяч верблюдов, тысяча пар волов и тысяча ослиц.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22177, 'rst66', 'job', 42, 13, 'И было у него семь сыновей и три дочери.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22178, 'rst66', 'job', 42, 14, 'И нарек он имя первой Емима, имя второй &mdash; Кассия, а имя третьей &mdash; Керенгаппух.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22179, 'rst66', 'job', 42, 15, 'И не было на всей земле таких прекрасных женщин, как дочери Иова, и дал им отец их наследство между братьями их.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22180, 'rst66', 'job', 42, 16, 'После того Иов жил сто сорок лет, и видел сыновей своих и сыновей сыновних до четвертого рода;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22181, 'rst66', 'job', 42, 17, 'и умер Иов в старости, насыщенный днями.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20539, 'rst66', 'neh', 1, 1, 'Слова Неемии, сына Ахалиина. В месяце Кислеве, в двадцатом году, я находился в Сузах, престольном городе.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20540, 'rst66', 'neh', 1, 2, 'И пришел Ханани, один из братьев моих, он и <i>несколько</i> человек из Иудеи. И спросил я их об уцелевших Иудеях, которые остались от плена, и об Иерусалиме.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20541, 'rst66', 'neh', 1, 3, 'И сказали они мне: оставшиеся, которые остались от плена, <i>находятся</i> там, в стране <i>своей</i>, в великом бедствии и в уничижении; и стена Иерусалима разрушена, и ворота его сожжены огнем.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20542, 'rst66', 'neh', 1, 4, 'Услышав эти слова, я сел и заплакал, и печален был несколько дней, и постился и молился пред Богом небесным');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20543, 'rst66', 'neh', 1, 5, 'и говорил: Господи, Боже небес, Боже великий и страшный, хранящий завет и милость к любящим Тебя и соблюдающим заповеди Твои!');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20544, 'rst66', 'neh', 1, 6, 'Да будут уши Твои внимательны и очи Твои отверсты, чтобы услышать молитву раба Твоего, которою я теперь день и ночь молюсь пред Тобою о сынах Израилевых, рабах Твоих, и исповедуюсь во грехах сынов Израилевых, которыми согрешили мы пред Тобою, согрешили &mdash; и я и дом отца моего.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20545, 'rst66', 'neh', 1, 7, 'Мы стали преступны пред Тобою и не сохранили заповедей и уставов и определений, которые Ты заповедал Моисею, рабу Твоему.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20546, 'rst66', 'neh', 1, 8, 'Но помяни слово, которое Ты заповедал Моисею, рабу Твоему, говоря: «<i>если</i> вы сделаетесь преступниками, то Я рассею вас по народам;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20547, 'rst66', 'neh', 1, 9, '<i>когда</i> же обратитесь ко Мне и будете хранить заповеди Мои и исполнять их, то хотя бы вы изгнаны были на край неба, и оттуда соберу вас и приведу вас на место, которое избрал Я, чтобы водворить там имя Мое».');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20548, 'rst66', 'neh', 1, 10, 'Они же рабы Твои и народ Твой, который Ты искупил силою Твоею великою и рукою Твоею могущественною.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20549, 'rst66', 'neh', 1, 11, 'Молю Тебя, Господи! Да будет ухо Твое внимательно к молитве раба Твоего и к молитве рабов Твоих, любящих благоговеть пред именем Твоим. И благопоспеши рабу Твоему теперь, и введи его в милость у человека сего. Я был виночерпием у царя.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20550, 'rst66', 'neh', 2, 1, 'В месяце Нисане, в двадцатый год царя Артаксеркса, <i>было</i> перед ним вино. И я взял вино и подал царю, и, казалось, не был печален перед ним.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20551, 'rst66', 'neh', 2, 2, 'Но царь сказал мне: отчего лицо у тебя печально; ты не болен, этого нет, а верно, печаль на сердце? Я сильно испугался');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20552, 'rst66', 'neh', 2, 3, 'и сказал царю: да живет царь вовеки! Как не быть печальным лицу моему, когда город, дом гробов отцов моих, в запустении, и ворота его сожжены огнем!');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20553, 'rst66', 'neh', 2, 4, 'И сказал мне царь: чего же ты желаешь? Я помолился Богу небесному');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20554, 'rst66', 'neh', 2, 5, 'и сказал царю: если царю благоугодно, и если в благоволении раб твой пред лицом твоим, то пошли меня в Иудею, в город, <i>где</i> гробы отцов моих, чтоб я обстроил его.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20555, 'rst66', 'neh', 2, 6, 'И сказал мне царь и царица, которая сидела подле него: сколько времени продлится путь твой, и когда возвратишься? И благоугодно было царю послать меня, после того как я назначил время.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20556, 'rst66', 'neh', 2, 7, 'И сказал я царю: если царю благоугодно, то дал бы мне письма к заречным областеначальникам, чтоб они давали мне пропуск, доколе я не дойду до Иудеи,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20557, 'rst66', 'neh', 2, 8, 'и письмо к Асафу, хранителю царских лесов, чтоб он дал мне дерев для ворот крепости, которая при доме <i>Божием</i>, и для городской стены, и для дома, в котором бы мне жить. И дал мне царь, так как благодеющая рука Бога моего была надо мною.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20558, 'rst66', 'neh', 2, 9, 'И пришел я к заречным областеначальникам и отдал им царские письма. Послал же со мною царь воинских начальников со всадниками.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20559, 'rst66', 'neh', 2, 10, 'Когда услышал <i>сие</i> Санаваллат, Хоронит и Товия, Аммонитский раб, то им было весьма досадно, что пришел человек заботиться о благе сынов Израилевых.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20560, 'rst66', 'neh', 2, 11, 'И пришел я в Иерусалим. И, пробыв там три дня,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20561, 'rst66', 'neh', 2, 12, 'встал я ночью с немногими людьми, <i>бывшими</i> при мне, и никому не сказал, что Бог мой положил мне на сердце сделать для Иерусалима; животного же не было со мною никакого, кроме того, на котором я ехал.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20562, 'rst66', 'neh', 2, 13, 'И проехал я ночью через ворота Долины перед источником Драконовым к воротам Навозным, и осмотрел я стены Иерусалима разрушенные и его ворота, сожженные огнем.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20563, 'rst66', 'neh', 2, 14, 'И подъехал я к воротам Источника и к царскому водоему, но <i>там</i> не было места пройти животному, которое было подо мною, &mdash;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20564, 'rst66', 'neh', 2, 15, 'и я поднялся назад по лощине ночью и осматривал стену, и, проехав <i>опять</i> воротами Долины, возвратился.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20565, 'rst66', 'neh', 2, 16, 'И начальствующие не знали, куда я ходил и что я делаю: ни Иудеям, ни священникам, ни знатнейшим, ни начальствующим, ни прочим производителям работ я дотоле ничего не открывал.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20566, 'rst66', 'neh', 2, 17, 'И сказал я им: вы видите бедствие, в каком мы находимся; Иерусалим пуст, и ворота его сожжены огнем; пойдем, построим стену Иерусалима, и не будем впредь <i>в таком</i> уничижении.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20567, 'rst66', 'neh', 2, 18, 'И я рассказал им о благодеявшей мне руке Бога моего, а также и слова царя, которые он говорил мне. И сказали они: «будем строить», &mdash; и укрепили руки свои на благое <i>дело</i>.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20568, 'rst66', 'neh', 2, 19, 'Услышав это, Санаваллат, Хоронит и Товия, Аммонитский раб, и Гешем Аравитянин смеялись над нами и с презрением говорили: что это за дело, которое вы делаете? уж не думаете ли возмутиться против царя?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20569, 'rst66', 'neh', 2, 20, 'Я дал им ответ и сказал им: Бог Небесный, Он благопоспешит нам, и мы, рабы Его, станем строить, а вам нет части и права и памяти в Иерусалиме.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20570, 'rst66', 'neh', 3, 1, 'И встал Елияшив, великий священник, и братья его священники и построили Овечьи ворота: они освятили их и вставили двери их, и от башни Меа освятили их до башни Хананела.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20571, 'rst66', 'neh', 3, 2, 'И подле него строили Иерихонцы, а подле них строил Закхур, сын Имрия.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20572, 'rst66', 'neh', 3, 3, 'Ворота Рыбные строили уроженцы Сенаи: они покрыли их, и вставили двери их, замки их и засовы их.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20573, 'rst66', 'neh', 3, 4, 'Подле них чинил <i>стену</i> Меремоф, сын Урии, сын Гаккоца; подле них чинил Мешуллам, сын Берехии, сын Мешизабела; подле них чинил Садок, сын Бааны;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20574, 'rst66', 'neh', 3, 5, 'подле них чинили Фекойцы; впрочем, знатнейшие из них не наклонили шеи своей поработать для Господа своего.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20575, 'rst66', 'neh', 3, 6, 'Старые ворота чинили Иоиада, сын Пасеаха, и Мешуллам, сын Бесодии: они покрыли их и вставили двери их, и замки их и засовы их.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20576, 'rst66', 'neh', 3, 7, 'Подле них чинил Мелатия Гаваонитянин, и Иадон из Меронофа, с жителями Гаваона и Мицфы, подвластными заречному областеначальнику.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20577, 'rst66', 'neh', 3, 8, 'Подле него чинил Уззиил, сын Харгаии, серебряник, а подле него чинил Ханания, сын Гараккахима. И восстановили Иерусалим до стены широкой.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20578, 'rst66', 'neh', 3, 9, 'Подле них чинил Рефаия, сын Хура, начальник полуокруга Иерусалимского.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20579, 'rst66', 'neh', 3, 10, 'Подле них и против дома своего чинил Иедаия, сын Харумафа, а подле него чинил Хаттуш, сын Хашавнии.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20580, 'rst66', 'neh', 3, 11, 'На втором участке чинил Малхия, сын Харима, и Хашшув, сын Пахаф‐Моава; <i>они же чинили</i> и башню Печную.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20581, 'rst66', 'neh', 3, 12, 'Подле них чинил Шаллум, сын Галлохеша, начальник полуокруга Иерусалимского, он и дочери его.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20582, 'rst66', 'neh', 3, 13, 'Ворота Долины чинил Ханун и жители Заноаха: они построили их, и вставили двери их, замки их и засовы их, и <i>еще чинили</i> они тысячу локтей стены до ворот Навозных.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20583, 'rst66', 'neh', 3, 14, 'А ворота Навозные чинил Малхия, сын Рехава, начальник Бефкаремского округа: он построил их и вставил двери их, замки их и засовы их.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20584, 'rst66', 'neh', 3, 15, 'Ворота Источника чинил Шаллум, сын Колхозея, начальник округа Мицфы: он построил их, и покрыл их, и вставил двери их, замки их и засовы их, &mdash; <i>он же чинил</i> стену у водоема Селах против царского сада и до ступеней, спускающихся из города Давидова.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20585, 'rst66', 'neh', 3, 16, 'За ним чинил Неемия, сын Азбука, начальник полуокруга Бефцурского, до гробниц Давидовых и до выкопанного пруда и до дома храбрых.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20586, 'rst66', 'neh', 3, 17, 'За ним чинили левиты: Рехум, сын Вания; подле него чинил Хашавия, начальник полуокруга Кеильского, за свой округ.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20587, 'rst66', 'neh', 3, 18, 'За ним чинили братья их: Баввай, сын Хенадада, начальник Кеильского полуокруга.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20588, 'rst66', 'neh', 3, 19, 'А подле него чинил Езер, сын Иисуса, начальник Мицфы, на втором участке, напротив всхода к оружейне на углу.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20589, 'rst66', 'neh', 3, 20, 'За ним ревностно чинил Варух, сын Забвая, на втором участке, от угла до дверей дома Елияшива, великого священника.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20590, 'rst66', 'neh', 3, 21, 'За ним чинил Меремоф, сын Урии, сын Гаккоца, на втором участке, от дверей дома Елияшивова до конца дома Елияшивова.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20591, 'rst66', 'neh', 3, 22, 'За ним чинили священники из окрестностей.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20592, 'rst66', 'neh', 3, 23, 'За ними чинил Вениамин и Хашшув, против дома своего; за ними чинил Азария, сын Маасеи, сын Анании, возле дома своего.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20593, 'rst66', 'neh', 3, 24, 'За ним чинил Биннуй, сын Хенадада, на втором участке, от дома Азарии до угла и поворота.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20594, 'rst66', 'neh', 3, 25, '<i>За ним</i> Фалал, сын Узая, напротив угла и башни, выступающей от верхнего дома царского, которая у двора темничного. За ним Федаия, сын Пароша.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20595, 'rst66', 'neh', 3, 26, 'Нефинеи же, <i>которые</i> жили в Офеле, <i>починяли</i> напротив Водяных ворот к востоку и до выступающей башни.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20596, 'rst66', 'neh', 3, 27, 'За ними чинили Фекойцы, на втором участке, от <i>места</i> напротив большой выступающей башни до стены Офела.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20597, 'rst66', 'neh', 3, 28, 'Далее ворот Конских чинили священники, каждый против своего дома.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20598, 'rst66', 'neh', 3, 29, 'За ними чинил Садок, сын Иммера, против своего дома, а за ним чинил Шемаия, сын Шехании, сторож восточных ворот.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20599, 'rst66', 'neh', 3, 30, 'За ним чинил Ханания, сын Шелемии, и Ханун, шестой сын Цалафа, на втором участке. За ним чинил Мешуллам, сын Берехии, против комнаты своей.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20600, 'rst66', 'neh', 3, 31, 'За ним чинил Малхия, сын Гацорфия, до дома нефинеев и торговцев, против ворот Гаммифкад и до угольного жилья.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20601, 'rst66', 'neh', 3, 32, 'А между угольным жильем до ворот Овечьих чинили серебряники и торговцы.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20602, 'rst66', 'neh', 4, 1, 'Когда услышал Санаваллат, что мы строим стену, он рассердился и много досадовал и издевался над Иудеями;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20603, 'rst66', 'neh', 4, 2, 'и говорил при братьях своих и при Самарийских военных людях, и сказал: что делают эти жалкие Иудеи? неужели им это дозволят? неужели будут они приносить жертвы? неужели они когда‐либо кончат? неужели они оживят камни из груд праха, и притом пожженные?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20604, 'rst66', 'neh', 4, 3, 'А Товия Аммонитянин, <i>бывший</i> подле него, сказал: пусть их строят; пойдет лисица, и разрушит их каменную стену.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20605, 'rst66', 'neh', 4, 4, 'Услыши, Боже наш, в каком мы презрении, и обрати ругательство их на их голову, и предай их презрению в земле пленения;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20606, 'rst66', 'neh', 4, 5, 'и не покрой беззаконий их, и грех их да не изгладится пред лицом Твоим, потому что они огорчили строящих!');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20607, 'rst66', 'neh', 4, 6, 'Мы, однако же, строили стену, и сложена была вся стена до половины ее. И у народа доставало усердия работать.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20608, 'rst66', 'neh', 4, 7, 'Когда услышал Санаваллат и Товия, и Аравитяне, и Аммонитяне, и Азотяне, что стены Иерусалимские восстановляются, что повреждения начали заделываться, то им было весьма досадно.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20609, 'rst66', 'neh', 4, 8, 'И сговорились все вместе пойти войною на Иерусалим и разрушить его.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20610, 'rst66', 'neh', 4, 9, 'И мы молились Богу нашему, и ставили против них стражу днем и ночью, для спасения от них.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20611, 'rst66', 'neh', 4, 10, 'Но Иудеи сказали: ослабела сила у носильщиков, а мусору много; мы не в состоянии строить стену.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20612, 'rst66', 'neh', 4, 11, 'А неприятели наши говорили: не узнают и не увидят, как <i>вдруг</i> мы войдем в средину их и перебьем их, и остановим дело.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20613, 'rst66', 'neh', 4, 12, 'Когда приходили Иудеи, жившие подле них, и говорили нам раз десять, со всех мест, что они нападут на нас:');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20614, 'rst66', 'neh', 4, 13, 'тогда в низменных местах у города, за стеною, на местах сухих поставил я народ поплеменно с мечами их, с копьями их и луками их.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20615, 'rst66', 'neh', 4, 14, 'И осмотрел я, и стал, и сказал знатнейшим и начальствующим и прочему народу: не бойтесь их; помните Господа великого и страшного и сражайтесь за братьев своих, за сыновей своих и за дочерей своих, за жен своих и за домы свои.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20616, 'rst66', 'neh', 4, 15, 'Когда услышали неприятели наши, что нам известно <i>намерение их</i>, тогда разорил Бог замысел их, и все мы возвратились к стене, каждый на свою работу.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20617, 'rst66', 'neh', 4, 16, 'С того дня половина молодых людей у меня занималась работою, а <i>другая</i> половина их держала копья, щиты и луки и латы; и начальствующие <i>находились</i> позади всего дома Иудина.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20618, 'rst66', 'neh', 4, 17, 'Строившие стену и носившие тяжести, которые налагали <i>на них</i>, одною рукою производили работу, а другою держали копье.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20619, 'rst66', 'neh', 4, 18, 'Каждый из строивших препоясан был мечом по чреслам своим, и <i>так</i> они строили. Возле меня находился трубач.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20620, 'rst66', 'neh', 4, 19, 'И сказал я знатнейшим и начальствующим и прочему народу: работа велика и обширна, и мы рассеяны по стене и отдалены друг от друга;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20621, 'rst66', 'neh', 4, 20, 'поэтому, откуда услышите вы звук трубы, в то место собирайтесь к нам: Бог наш будет сражаться за нас.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20622, 'rst66', 'neh', 4, 21, 'Так производили мы работу; и половина держала копья от восхода зари до появления звезд.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20623, 'rst66', 'neh', 4, 22, 'Сверх сего, в то же время я сказал народу, чтобы в Иерусалиме ночевали все с рабами своими, &mdash; и будут они у нас ночью на страже, а днем на работе.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20624, 'rst66', 'neh', 4, 23, 'И ни я, ни братья мои, ни слуги мои, ни стражи, сопровождавшие меня, не снимали с себя одеяния своего, у каждого были под рукою меч и вода.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20625, 'rst66', 'neh', 5, 1, 'И сделался большой ропот в народе и у жен его на братьев своих Иудеев.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20626, 'rst66', 'neh', 5, 2, 'Были такие, которые говорили: нас, сыновей наших и дочерей наших много; и мы желали бы доставать хлеб и кормиться и жить.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20627, 'rst66', 'neh', 5, 3, 'Были и такие, которые говорили: поля свои, и виноградники свои, и домы свои мы закладываем, чтобы достать хлеба от голода.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20628, 'rst66', 'neh', 5, 4, 'Были и такие, которые говорили: мы занимаем серебро на подать царю <i>под залог</i> полей наших и виноградников наших;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20629, 'rst66', 'neh', 5, 5, 'у нас такие же тела, какие тела у братьев наших, и сыновья наши такие же, как их сыновья; а вот, мы должны отдавать сыновей наших и дочерей наших в рабы, и некоторые из дочерей наших уже находятся в порабощении. Нет никаких средств для выкупа в руках наших; и поля наши и виноградники наши у других.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20630, 'rst66', 'neh', 5, 6, 'Когда я услышал ропот их и такие слова, я очень рассердился.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20631, 'rst66', 'neh', 5, 7, 'Сердце мое возмутилось, и я строго выговорил знатнейшим и начальствующим и сказал им: вы берете лихву с братьев своих. И созвал я против них большое собрание');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20632, 'rst66', 'neh', 5, 8, 'и сказал им: мы выкупали братьев своих, Иудеев, проданных народам, сколько было сил у нас, а вы продаете братьев своих, и они продаются нам? Они молчали и не находили ответа.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20633, 'rst66', 'neh', 5, 9, 'И сказал я: нехорошо вы делаете. Не в страхе ли Бога нашего должны ходить вы, дабы избегнуть поношения от народов, врагов наших?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20634, 'rst66', 'neh', 5, 10, 'И я также, братья мои и <i>служащие</i> при мне давали им взаем и серебро и хлеб: оставим им долг сей.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20635, 'rst66', 'neh', 5, 11, 'Возвратите им ныне же поля их, виноградные и масличные сады их, и домы их, и рост с серебра и хлеба, и вина и масла, за который вы ссудили их.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20636, 'rst66', 'neh', 5, 12, 'И сказали они: возвратим и не будем с них требовать; сделаем так, как ты говоришь. И позвал я священников и велел им дать клятву, что они так сделают.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20637, 'rst66', 'neh', 5, 13, 'И вытряхнул я <i>одежду</i> мою и сказал: так пусть вытряхнет Бог всякого человека, который не сдержит слова сего, из дома его и из имения его, и так да будет у него вытрясено и пусто! И сказало все собрание: аминь. И прославили Бога; и народ выполнил слово сие.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20638, 'rst66', 'neh', 5, 14, 'Еще: с того дня, как определен я был областеначальником их в земле Иудейской, от двадцатого года до тридцать второго года царя Артаксеркса, в продолжение двенадцати лет я и братья мои не ели хлеба областеначальнического.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20639, 'rst66', 'neh', 5, 15, 'А прежние областеначальники, которые <i>были</i> до меня, отягощали народ и брали с них хлеб и вино, кроме сорока сиклей серебра; даже и слуги их господствовали над народом. Я же не делал так по страху Божию.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20640, 'rst66', 'neh', 5, 16, 'При этом работы на стене сей я поддерживал; и полей мы не закупали, и все слуги мои собирались туда на работу.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20641, 'rst66', 'neh', 5, 17, 'Иудеев и начальствующих по сто пятидесяти человек <i>бывало</i> за столом у меня, кроме приходивших к нам из окрестных народов.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20642, 'rst66', 'neh', 5, 18, 'И <i>вот</i> что было приготовляемо на один день: один бык, шесть отборных овец и птицы приготовлялись у меня; и в десять дней <i>издерживалось</i> множество всякого вина. И при <i>всем</i> том, хлеба областеначальнического я не требовал, так как тяжелая служба <i>лежала</i> на народе сем.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20643, 'rst66', 'neh', 5, 19, 'Помяни, Боже мой, во благо мне все, что я сделал для народа сего!');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20644, 'rst66', 'neh', 6, 1, 'Когда дошло до слуха Санаваллата и Товии и Гешема Аравитянина и прочих неприятелей наших, что я отстроил стену, и не оставалось в ней повреждений &mdash; впрочем, до того времени я еще не ставил дверей в ворота, &mdash;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20645, 'rst66', 'neh', 6, 2, 'тогда прислал Санаваллат и Гешем ко мне сказать: приди, и сойдемся в одном из сел на равнине Оно. Они замышляли сделать мне зло.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20646, 'rst66', 'neh', 6, 3, 'Но я послал к ним послов сказать: я занят большим делом, не могу сойти; дело остановилось бы, если бы я оставил его и сошел к вам.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20647, 'rst66', 'neh', 6, 4, 'Четыре раза присылали они ко мне с таким же приглашением, и я отвечал им то же.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20648, 'rst66', 'neh', 6, 5, 'Тогда прислал ко мне Санаваллат в пятый раз своего слугу, у которого в руке было открытое письмо.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20649, 'rst66', 'neh', 6, 6, 'В нем было написано: слух носится у народов, и Гешем говорит, будто ты и Иудеи задумали отпасть, для чего и строишь стену и хочешь быть у них царем, по тем же слухам;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20650, 'rst66', 'neh', 6, 7, 'и пророков поставил ты, чтоб они разглашали о тебе в Иерусалиме и говорили: «царь Иудейский!» И такие речи дойдут до царя. Итак, приходи, и посоветуемся вместе.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20651, 'rst66', 'neh', 6, 8, 'Но я послал к нему сказать: ничего такого не было, о чем ты говоришь; ты выдумал это своим умом.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20652, 'rst66', 'neh', 6, 9, 'Ибо все они стращали нас, думая: «опустятся руки их от дела сего, и оно не состоится»; но я тем более укрепил руки мои.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20653, 'rst66', 'neh', 6, 10, 'Пришел я в дом Шемаии, сына Делаии, сына Мегетавелова, и он заперся и сказал: пойдем в дом Божий, внутрь храма, и запрем за собою двери храма, потому что придут убить тебя, и придут убить тебя ночью.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20654, 'rst66', 'neh', 6, 11, 'Но я сказал: может ли бежать такой человек, как я? Может ли такой, как я, войти в храм, чтобы остаться живым? Не пойду.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20655, 'rst66', 'neh', 6, 12, 'Я знал, что не Бог послал его, хотя он пророчески говорил мне, но что Товия и Санаваллат подкупили его.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20656, 'rst66', 'neh', 6, 13, 'Для того он был подкуплен, чтоб я устрашился и сделал так и согрешил, и чтобы имели о мне худое мнение и преследовали меня за это укоризнами.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20657, 'rst66', 'neh', 6, 14, 'Помяни, Боже мой, Товию и Санаваллата по сим делам их, а также пророчицу Ноадию и прочих пророков, которые хотели устрашить меня!');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20658, 'rst66', 'neh', 6, 15, 'Стена была совершена в двадцать пятый день месяца Елула, в пятьдесят два дня.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20659, 'rst66', 'neh', 6, 16, 'Когда услышали об этом все неприятели наши, и увидели это все народы, которые вокруг нас, тогда они очень упали в глазах своих и познали, что это дело сделано Богом нашим.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20660, 'rst66', 'neh', 6, 17, 'Сверх того в те дни знатнейшие Иудеи много писали писем, которые посылались к Товии, а Товиины письма приходили к ним.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20661, 'rst66', 'neh', 6, 18, 'Ибо многие в Иудее были в клятвенном союзе с ним, потому что он был зять Шехании, сына Арахова, а сын его Иоханан взял <i>за себя</i> дочь Мешуллама, сына Верехии.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20662, 'rst66', 'neh', 6, 19, 'Даже о доброте его они говорили при мне, и мои слова переносились к нему. Товия присылал письма, чтоб устрашить меня.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20663, 'rst66', 'neh', 7, 1, 'Когда стена была построена, и я вставил двери, и поставлены были на свое служение привратники и певцы и левиты,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20664, 'rst66', 'neh', 7, 2, 'тогда приказал я брату моему Ханани и начальнику Иерусалимской крепости Хананию, ибо он более многих других был человек верный и богобоязненный,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20665, 'rst66', 'neh', 7, 3, 'и сказал я им: пусть не отворяют ворот Иерусалимских, доколе не обогреет солнце, и доколе они стоят, пусть замыкают и запирают двери. И поставил я стражами жителей Иерусалима, каждого на свою стражу и каждого напротив дома его.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20666, 'rst66', 'neh', 7, 4, 'Но город был пространен и велик, а народа в нем было немного, и домы не были построены.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20667, 'rst66', 'neh', 7, 5, 'И положил мне Бог мой на сердце собрать знатнейших и начальствующих и народ, чтобы сделать перепись. И нашел я родословную перепись тех, которые сначала пришли, и в ней написано:');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20668, 'rst66', 'neh', 7, 6, 'вот жители страны, которые отправились из пленников, переселенных Навуходоносором, царем Вавилонским, и возвратились в Иерусалим и Иудею, каждый в свой город, &mdash;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20669, 'rst66', 'neh', 7, 7, 'те, которые пошли с Зоровавелем, Иисусом, Неемиею, Азариею, Раамиею, Нахманием, Мардохеем, Билшаном, Мисферефом, Бигваем, Нехумом, Вааною. Число людей народа Израилева:');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20670, 'rst66', 'neh', 7, 8, 'сыновей Пароша две тысячи сто семьдесят два.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20671, 'rst66', 'neh', 7, 9, 'Сыновей Сафатии триста семьдесят два.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20672, 'rst66', 'neh', 7, 10, 'Сыновей Араха шестьсот пятьдесят два.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20673, 'rst66', 'neh', 7, 11, 'Сыновей Пахаф‐Моава, из сыновей Иисуса и Иоава, две тысячи восемьсот восемнадцать.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20674, 'rst66', 'neh', 7, 12, 'Сыновей Елама тысяча двести пятьдесят четыре.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20675, 'rst66', 'neh', 7, 13, 'Сыновей Заффу восемьсот сорок пять.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20676, 'rst66', 'neh', 7, 14, 'Сыновей Закхая семьсот шестьдесят.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20677, 'rst66', 'neh', 7, 15, 'Сыновей Биннуя шестьсот сорок восемь.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20678, 'rst66', 'neh', 7, 16, 'Сыновей Бевая шестьсот двадцать восемь.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20679, 'rst66', 'neh', 7, 17, 'Сыновей Азгада две тысячи триста двадцать два.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20680, 'rst66', 'neh', 7, 18, 'Сыновей Адоникама шестьсот шестьдесят семь.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20681, 'rst66', 'neh', 7, 19, 'Сыновей Бигвая две тысячи шестьсот семь.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20682, 'rst66', 'neh', 7, 20, 'Сыновей Адина шестьсот пятьдесят пять.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20683, 'rst66', 'neh', 7, 21, 'Сыновей Атера из <i>дома</i> Езекии девяносто восемь.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20684, 'rst66', 'neh', 7, 22, 'Сыновей Хашума триста двадцать восемь.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20685, 'rst66', 'neh', 7, 23, 'Сыновей Вецая триста двадцать четыре.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20686, 'rst66', 'neh', 7, 24, 'Сыновей Харифа сто двенадцать.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20687, 'rst66', 'neh', 7, 25, 'Уроженцев Гаваона девяносто пять.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20688, 'rst66', 'neh', 7, 26, 'Жителей Вифлеема и Нетофы сто восемьдесят восемь.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20689, 'rst66', 'neh', 7, 27, 'Жителей Анафофа сто двадцать восемь.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20690, 'rst66', 'neh', 7, 28, 'Жителей Беф‐Азмавефа сорок два.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20691, 'rst66', 'neh', 7, 29, 'Жителей Кириаф‐Иарима, Кефиры и Беерофа семьсот сорок три.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20692, 'rst66', 'neh', 7, 30, 'Жителей Рамы и Гевы шестьсот двадцать один.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20693, 'rst66', 'neh', 7, 31, 'Жителей Михмаса сто двадцать два.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20694, 'rst66', 'neh', 7, 32, 'Жителей Вефиля и Гая сто двадцать три.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20695, 'rst66', 'neh', 7, 33, 'Жителей Нево другого пятьдесят два.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20696, 'rst66', 'neh', 7, 34, 'Сыновей Елама другого тысяча двести пятьдесят четыре.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20697, 'rst66', 'neh', 7, 35, 'Сыновей Харима триста двадцать.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20698, 'rst66', 'neh', 7, 36, 'Уроженцев Иерихона триста сорок пять.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20699, 'rst66', 'neh', 7, 37, 'Уроженцев Лода, Хадида и Оно семьсот двадцать один.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20700, 'rst66', 'neh', 7, 38, 'Уроженцев Сенаи три тысячи девятьсот тридцать.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20701, 'rst66', 'neh', 7, 39, 'Священников, сыновей Иедаии, из дома Иисусова, девятьсот семьдесят три.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20702, 'rst66', 'neh', 7, 40, 'Сыновей Иммера тысяча пятьдесят два.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20703, 'rst66', 'neh', 7, 41, 'Сыновей Пашхура тысяча двести сорок семь.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20704, 'rst66', 'neh', 7, 42, 'Сыновей Харима тысяча семнадцать.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20705, 'rst66', 'neh', 7, 43, 'Левитов: сыновей Иисуса, из <i>дома</i> Кадмиилова, из дома сыновей Годевы, семьдесят четыре.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20706, 'rst66', 'neh', 7, 44, 'Певцов: сыновей Асафа сто сорок восемь.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20707, 'rst66', 'neh', 7, 45, 'Привратники: сыновья Шаллума, сыновья Атера, сыновья Талмона, сыновья Аккува, сыновья Хатиты, сыновья Шовая &mdash; сто тридцать восемь.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20708, 'rst66', 'neh', 7, 46, 'Нефинеи: сыновья Цихи, сыновья Хасуфы, сыновья Таббаофа,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20709, 'rst66', 'neh', 7, 47, 'сыновья Кироса, сыновья Сии, сыновья Фадона,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20710, 'rst66', 'neh', 7, 48, 'сыновья Леваны, сыновья Хагавы, сыновья Салмая,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20711, 'rst66', 'neh', 7, 49, 'сыновья Ханана, сыновья Гиддела, сыновья Гахара,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20712, 'rst66', 'neh', 7, 50, 'сыновья Реаии, сыновья Рецина, сыновья Некоды,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20713, 'rst66', 'neh', 7, 51, 'сыновья Газзама, сыновья Уззы, сыновья Пасеаха,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20714, 'rst66', 'neh', 7, 52, 'сыновья Весая, сыновья Меунима, сыновья Нефишсима,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20715, 'rst66', 'neh', 7, 53, 'сыновья Бакбука, сыновья Хакуфы, сыновья Хархура,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20716, 'rst66', 'neh', 7, 54, 'сыновья Бацлифа, сыновья Мехиды, сыновья Харши,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20717, 'rst66', 'neh', 7, 55, 'сыновья Баркоса, сыновья Сисары, сыновья Фамаха,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20718, 'rst66', 'neh', 7, 56, 'сыновья Нециаха, сыновья Хатифы.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20719, 'rst66', 'neh', 7, 57, 'Сыновья рабов Соломоновых: сыновья Сотая, сыновья Соферефа, сыновья Фериды,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20720, 'rst66', 'neh', 7, 58, 'сыновья Иаалы, сыновья Даркона, сыновья Гиддела,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20721, 'rst66', 'neh', 7, 59, 'сыновья Сафатии, сыновья Хаттила, сыновья Похереф‐Гаццевайима, сыновья Амона.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20722, 'rst66', 'neh', 7, 60, 'Всех нефинеев и сыновей рабов Соломоновых триста девяносто два.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20723, 'rst66', 'neh', 7, 61, 'И вот вышедшие из Тел‐Мелаха, Тел‐Харши, Херув‐Аддона и Иммера; но они не могли показать о поколении своем и о племени своем, от Израиля ли они.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20724, 'rst66', 'neh', 7, 62, 'Сыновья Делаии, сыновья Товии, сыновья Некоды &mdash; шестьсот сорок два.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20725, 'rst66', 'neh', 7, 63, 'И из священников: сыновья Ховаии, сыновья Гаккоца, сыновья Верзеллия, который взял жену из дочерей Верзеллия Галаадитянина и стал называться их именем.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20726, 'rst66', 'neh', 7, 64, 'Они искали родословной своей записи, и не нашлось, и потому исключены из священства.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20727, 'rst66', 'neh', 7, 65, 'И Тиршафа сказал им, чтоб они не ели великой святыни, доколе не восстанет священник с уримом и туммимом.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20728, 'rst66', 'neh', 7, 66, 'Все общество вместе <i>состояло</i> из сорока двух тысяч трехсот шестидесяти <i>человек</i>,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20729, 'rst66', 'neh', 7, 67, 'кроме рабов их и рабынь их, которых было семь тысяч триста тридцать семь; и при них певцов и певиц двести сорок пять.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20730, 'rst66', 'neh', 7, 68, 'Коней у них было семьсот тридцать шесть, лошаков у них двести сорок пять,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20731, 'rst66', 'neh', 7, 69, 'верблюдов четыреста тридцать пять, ослов шесть тысяч семьсот двадцать.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20732, 'rst66', 'neh', 7, 70, 'Некоторые главы поколений дали вклады на производство работ. Тиршафа дал в сокровищницу золотом тысячу драхм, пятьдесят чаш, пятьсот тридцать священнических одежд.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20733, 'rst66', 'neh', 7, 71, 'И некоторые из глав поколений дали в сокровищницу на производство работ двадцать тысяч драхм золота и две тысячи двести мин серебра.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20734, 'rst66', 'neh', 7, 72, 'Прочие из народа дали двадцать тысяч драхм золота и две тысячи мин серебра и шестьдесят семь священнических одежд.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20735, 'rst66', 'neh', 7, 73, 'И стали жить священники и левиты, и привратники и певцы, и народ и нефинеи, и весь Израиль в городах своих.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20736, 'rst66', 'neh', 8, 1, 'Когда наступил седьмой месяц, и сыны Израилевы <i>жили</i> по городам своим, тогда собрался весь народ, как один человек, на площадь, которая пред Водяными воротами, и сказали книжнику Ездре, чтобы он принес книгу закона Моисеева, который заповедал Господь Израилю.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20737, 'rst66', 'neh', 8, 2, 'И принес священник Ездра закон пред собрание мужчин и женщин, и всех, которые могли понимать, в первый день седьмого месяца;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20738, 'rst66', 'neh', 8, 3, 'и читал из него на площади, которая пред Водяными воротами, от рассвета до полудня, пред мужчинами и женщинами и всеми, которые могли понимать; и уши всего народа <i>были приклонены</i> к книге закона.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20739, 'rst66', 'neh', 8, 4, 'Книжник Ездра стоял на деревянном возвышении, которое для сего сделали, а подле него, по правую руку его, стояли Маттифия и Шема, и Анаия и Урия, и Хелкия и Маасея, а по левую руку его Федаия и Мисаил, и Малхия и Хашум, и Хашбаддана, и Захария и Мешуллам.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20740, 'rst66', 'neh', 8, 5, 'И открыл Ездра книгу пред глазами всего народа, потому что он стоял выше всего народа. И когда он открыл ее, весь народ встал.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20741, 'rst66', 'neh', 8, 6, 'И благословил Ездра Господа, Бога великого. И весь народ отвечал: «аминь, аминь», &mdash; поднимая вверх руки свои, &mdash; и поклонялись и повергались пред Господом лицом до земли.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20742, 'rst66', 'neh', 8, 7, 'Иисус, Ванаия, Шеревия, Иамин, Аккув, Шавтай, Годия, Маасея, Клита, Азария, Иозавад, Ханан, Фелаия и левиты поясняли народу закон, между тем как народ стоял на своем месте.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20743, 'rst66', 'neh', 8, 8, 'И читали из книги, из закона Божия, внятно, и присоединяли толкование, и <i>народ</i> понимал прочитанное.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20744, 'rst66', 'neh', 8, 9, 'Тогда Неемия, он же Тиршафа, и книжник Ездра, священник, и левиты, учившие народ, сказали всему народу: «день сей свят Господу, Богу вашему; не печальтесь и не плачьте», потому что весь народ плакал, слушая слова закона.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20745, 'rst66', 'neh', 8, 10, 'И сказал им: пойдите, ешьте тучное и пейте сладкое, и посылайте части тем, у кого ничего не приготовлено, потому что день сей свят Господу нашему. И не печальтесь, потому что радость пред Господом &mdash; подкрепление для вас.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20746, 'rst66', 'neh', 8, 11, 'И левиты успокаивали весь народ, говоря: перестаньте, ибо день сей свят, не печальтесь.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20747, 'rst66', 'neh', 8, 12, 'И пошел весь народ есть, и пить, и посылать части, и праздновать с великим веселием, ибо поняли слова, которые сказали им.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20748, 'rst66', 'neh', 8, 13, 'На другой день собрались главы поколений от всего народа, священники и левиты к книжнику Ездре, чтобы он изъяснял им слова закона.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20749, 'rst66', 'neh', 8, 14, 'И нашли написанное в законе, который Господь дал чрез Моисея, чтобы сыны Израилевы в седьмом месяце, в праздник, жили в кущах.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20750, 'rst66', 'neh', 8, 15, 'И потому объявили и провозгласили по всем городам своим и в Иерусалиме, говоря: пойдите на гору и несите ветви маслины садовой и ветви маслины дикой, и ветви миртовые и ветви пальмовые, и ветви <i>других</i> широколиственных дерев, чтобы сделать кущи по написанному.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20751, 'rst66', 'neh', 8, 16, 'И пошел народ, и принесли, и сделали себе кущи, каждый на своей кровле и на дворах своих, и на дворах дома Божия, и на площади у Водяных ворот, и на площади у Ефремовых ворот.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20752, 'rst66', 'neh', 8, 17, 'Все общество возвратившихся из плена сделало кущи и жило в кущах. От дней Иисуса, сына Навина, до этого дня не делали так сыны Израилевы. Радость была весьма великая.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20753, 'rst66', 'neh', 8, 18, 'И читали из книги закона Божия каждый день, от первого дня до последнего дня. И праздновали праздник семь дней, а в восьмой день попразднество по уставу.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20754, 'rst66', 'neh', 9, 1, 'В двадцать четвертый день этого месяца собрались все сыны Израилевы, постящиеся и во вретищах и с пеплом на головах своих.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20755, 'rst66', 'neh', 9, 2, 'И отделилось семя Израилево от всех инородных, и встали и исповедовались во грехах своих и в преступлениях отцов своих.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20756, 'rst66', 'neh', 9, 3, 'И стояли на своем месте, и четверть дня читали из книги закона Господа, Бога своего, и четверть исповедовались и поклонялись Господу, Богу своему.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20757, 'rst66', 'neh', 9, 4, 'И стали на возвышенное место левитов: Иисус, Вания, Кадмиил, Шевания, Вунний, Шеревия, Вания, Хенани, и громко взывали к Господу, Богу своему.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20758, 'rst66', 'neh', 9, 5, 'И сказали левиты &mdash; Иисус, Кадмиил, Вания, Хашавния, Шеревия, Годия, Шевания, Петахия: встаньте, славьте Господа, Бога вашего, от века и до века. Да славословят достославное и превысшее всякого славословия и хвалы имя Твое!');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20759, 'rst66', 'neh', 9, 6, 'Ты, Господи, един, Ты создал небо, небеса небес и все воинство их, землю и все, что на ней, моря и все, что в них, и Ты живишь все сие, и небесные воинства Тебе поклоняются.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20760, 'rst66', 'neh', 9, 7, 'Ты Сам, Господи Боже, избрал Аврама, и вывел его из Ура Халдейского, и дал ему имя Авраама,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20761, 'rst66', 'neh', 9, 8, 'и нашел сердце его верным пред Тобою, и заключил с ним завет, чтобы дать семени его землю Хананеев, Хеттеев, Аморреев, Ферезеев, Иевусеев и Гергесеев. И Ты исполнил слово Свое, потому что Ты праведен.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20762, 'rst66', 'neh', 9, 9, 'Ты увидел бедствие отцов наших в Египте и услышал вопль их у Чермного моря,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20763, 'rst66', 'neh', 9, 10, 'и явил знамения и чудеса над фараоном и над всеми рабами его, и над всем народом земли его, так как Ты знал, что они надменно поступали с ними, и сделал Ты Себе имя до сего дня.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20764, 'rst66', 'neh', 9, 11, 'Ты рассек пред ними море, и они среди моря прошли посуху, и гнавшихся за ними Ты поверг в глубины, как камень в сильные воды.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20765, 'rst66', 'neh', 9, 12, 'В столпе облачном Ты вел их днем и в столпе огненном &mdash; ночью, чтоб освещать им путь, по которому идти им.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20766, 'rst66', 'neh', 9, 13, 'И снисшел Ты на гору Синай и говорил с ними с неба, и дал им суды справедливые, законы верные, уставы и заповеди добрые.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20767, 'rst66', 'neh', 9, 14, 'И указал им святую Твою субботу и заповеди, и уставы и закон преподал им чрез раба Твоего Моисея.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20768, 'rst66', 'neh', 9, 15, 'И хлеб с неба Ты давал им в голоде их, и воду из камня источал им в жажде их, и сказал им, чтоб они пошли и овладели землею, которую Ты, подняв руку Твою, <i>клялся</i> дать им.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20769, 'rst66', 'neh', 9, 16, 'Но они и отцы наши упрямствовали, и шею свою держали упруго, и не слушали заповедей Твоих;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20770, 'rst66', 'neh', 9, 17, 'не захотели повиноваться и не вспомнили чудных дел Твоих, которые Ты делал с ними, и держали шею свою упруго, и, по упорству своему, поставили над собою вождя, чтобы возвратиться в рабство свое. Но Ты Бог, любящий прощать, благий и милосердый, долготерпеливый и многомилостивый, и Ты не оставил их.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20771, 'rst66', 'neh', 9, 18, 'И хотя они сделали себе литого тельца, и сказали: «вот бог твой, который вывел тебя из Египта», и хотя делали великие оскорбления,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20772, 'rst66', 'neh', 9, 19, 'но Ты, по великому милосердию Твоему, не оставлял их в пустыне; столп облачный не отходил от них днем, чтобы вести их по пути, и столп огненный &mdash; ночью, чтобы светить им на пути, по которому им идти.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20773, 'rst66', 'neh', 9, 20, 'И Ты дал им Духа Твоего благого, чтобы наставлять их, и манну Твою не отнимал от уст их, и воду давал им для утоления жажды их.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20774, 'rst66', 'neh', 9, 21, 'Сорок лет Ты питал их в пустыне; они ни в чем не терпели недостатка; одежды их не ветшали, и ноги их не пухли.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20775, 'rst66', 'neh', 9, 22, 'И Ты дал им царства и народы и разделил им, и они овладели землею Сигона, и землею царя Есевонского, и землею Ога, царя Васанского.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20776, 'rst66', 'neh', 9, 23, 'И сыновей их Ты размножил, как звезды небесные, и ввел их в землю, о которой Ты говорил отцам их, что они придут владеть <i>ею</i>.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20777, 'rst66', 'neh', 9, 24, 'И вошли сыновья их, и овладели землею. И Ты покорил им жителей земли, Хананеев, и отдал их в руки их, и царей их, и народы земли, чтобы они поступали с ними по своей воле.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20778, 'rst66', 'neh', 9, 25, 'И заняли они укрепленные города и тучную землю, и взяли во владение домы, наполненные всяким добром, водоемы, высеченные <i>из камня</i>, виноградные и масличные сады и множество дерев <i>с плодами</i> для пищи. Они ели, насыщались, тучнели и наслаждались по великой благости Твоей;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20779, 'rst66', 'neh', 9, 26, 'и сделались упорны и возмутились против Тебя, и презрели закон Твой, убивали пророков Твоих, которые увещевали их обратиться к Тебе, и делали великие оскорбления.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20780, 'rst66', 'neh', 9, 27, 'И Ты отдал их в руки врагов их, которые теснили их. Но когда, в тесное для них время, они взывали к Тебе, Ты выслушивал их с небес и, по великому милосердию Твоему, давал им спасителей, и они спасали их от рук врагов их.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20781, 'rst66', 'neh', 9, 28, 'Когда же успокаивались, то снова начинали делать зло пред лицом Твоим, и Ты отдавал их в руки неприятелей их, и они господствовали над ними. Но когда они опять взывали к Тебе, Ты выслушивал их с небес и, по великому милосердию Твоему, избавлял их многократно.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20782, 'rst66', 'neh', 9, 29, 'Ты напоминал им обратиться к закону Твоему, но они упорствовали и не слушали заповедей Твоих, и отклонялись от уставов Твоих, которыми жил бы человек, если бы исполнял их, и хребет <i>свой</i> сделали упорным, и шею свою держали упруго, и не слушали.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20783, 'rst66', 'neh', 9, 30, 'Ожидая их <i>обращения</i>, Ты медлил многие годы и напоминал им Духом Твоим чрез пророков Твоих, но они не слушали. И Ты предал их в руки иноземных народов.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20784, 'rst66', 'neh', 9, 31, 'Но, по великому милосердию Твоему, Ты не истребил их до конца, и не оставлял их, потому что Ты Бог благий и милостивый.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20785, 'rst66', 'neh', 9, 32, 'И ныне, Боже наш, Боже великий, сильный и страшный, хранящий завет и милость! да не будет малым пред лицом Твоим все страдание, которое постигало нас, царей наших, князей наших, и священников наших, и пророков наших, и отцов наших и весь народ Твой от дней царей Ассирийских до сего дня.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20786, 'rst66', 'neh', 9, 33, 'Во всем, постигшем нас, Ты праведен, потому что Ты делал по правде, а мы виновны.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20787, 'rst66', 'neh', 9, 34, 'Цари наши, князья наши, священники наши и отцы наши не исполняли закона Твоего, и не внимали заповедям Твоим и напоминаниям Твоим, которыми Ты напоминал им.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20788, 'rst66', 'neh', 9, 35, 'И в царстве своем, при великом добре Твоем, которое Ты давал им, и на обширной и тучной земле, которую Ты отделил им, они не служили Тебе и не обращались от злых дел своих.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20789, 'rst66', 'neh', 9, 36, 'И вот, мы ныне рабы; на той земле, которую Ты дал отцам нашим, чтобы питаться ее плодами и ее добром, вот, мы рабствуем.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20790, 'rst66', 'neh', 9, 37, 'И произведения свои она во множестве приносит для царей, которым Ты покорил нас за грехи наши. И телами нашими и скотом нашим они владеют по своему произволу, и мы в великом стеснении.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20791, 'rst66', 'neh', 9, 38, 'По всему этому мы даем твердое обязательство и подписываем, и на подписи печать князей наших, левитов наших и священников наших.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20792, 'rst66', 'neh', 10, 1, 'Приложившие печати были: Неемия‐Тиршафа, сын Гахалии, и Седекия,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20793, 'rst66', 'neh', 10, 2, 'Сераия, Азария, Иеремия,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20794, 'rst66', 'neh', 10, 3, 'Пашхур, Амария, Малхия,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20795, 'rst66', 'neh', 10, 4, 'Хаттуш, Шевания, Маллух,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20796, 'rst66', 'neh', 10, 5, 'Харим, Меремоф, Овадия,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20797, 'rst66', 'neh', 10, 6, 'Даниил, Гиннефон, Варух,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20798, 'rst66', 'neh', 10, 7, 'Мешуллам, Авия, Миямин,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20799, 'rst66', 'neh', 10, 8, 'Маазия, Вилгай, Шемаия: это священники.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20800, 'rst66', 'neh', 10, 9, 'Левиты: Иисус, сын Азании, Биннуй, из сыновей Хенадада, Кадмиил,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20801, 'rst66', 'neh', 10, 10, 'и братья их: Шевания, Годия, Клита, Фелаия, Ханан,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20802, 'rst66', 'neh', 10, 11, 'Миха, Рехов, Хашавия,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20803, 'rst66', 'neh', 10, 12, 'Закхур, Шеревия, Шевания,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20804, 'rst66', 'neh', 10, 13, 'Годия, Ваний, Венинуй.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20805, 'rst66', 'neh', 10, 14, 'Главы народа: Парош, Пахаф‐Моав, Елам, Заффу, Вания,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20806, 'rst66', 'neh', 10, 15, 'Вунний, Азгар, Бевай,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20807, 'rst66', 'neh', 10, 16, 'Адония, Бигвай, Адин,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20808, 'rst66', 'neh', 10, 17, 'Атер, Езекия, Азур,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20809, 'rst66', 'neh', 10, 18, 'Годия, Хашум, Бецай,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20810, 'rst66', 'neh', 10, 19, 'Хариф, Анафоф, Невай,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20811, 'rst66', 'neh', 10, 20, 'Магпиаш, Мешуллам, Хезир,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20812, 'rst66', 'neh', 10, 21, 'Мешезавел, Садок, Иаддуй,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20813, 'rst66', 'neh', 10, 22, 'Фелатия, Ханан, Анаия,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20814, 'rst66', 'neh', 10, 23, 'Осия, Ханания, Хашшув,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20815, 'rst66', 'neh', 10, 24, 'Лохеш, Пилха, Шовек,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20816, 'rst66', 'neh', 10, 25, 'Рехум, Хашавна, Маасея,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20817, 'rst66', 'neh', 10, 26, 'Ахия, Ханан, Анан,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20818, 'rst66', 'neh', 10, 27, 'Маллух, Харим, Ваана.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20819, 'rst66', 'neh', 10, 28, 'И прочий народ, священники, левиты, привратники, певцы, нефинеи и все, отделившиеся от народов иноземных к закону Божию, жены их, сыновья их и дочери их, все, которые могли понимать,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20820, 'rst66', 'neh', 10, 29, 'пристали к братьям своим, к почетнейшим из них, и вступили в обязательство с клятвою и проклятием &mdash; поступать по закону Божию, который дан рукою Моисея, раба Божия, и соблюдать и исполнять все заповеди Господа, Бога нашего, и уставы Его и предписания Его.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20821, 'rst66', 'neh', 10, 30, 'И не отдавать дочерей своих иноземным народам, и их дочерей не брать за сыновей своих;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20822, 'rst66', 'neh', 10, 31, 'и когда иноземные народы будут привозить товары и все продажное в субботу, не брать у них в субботу и в священный день, и в седьмой год оставлять долги всякого рода.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20823, 'rst66', 'neh', 10, 32, 'И поставили мы себе в закон давать от себя по трети сикля в год на потребности для дома Бога нашего:');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20824, 'rst66', 'neh', 10, 33, 'на хлебы предложения, на всегдашнее хлебное приношение и на всегдашнее всесожжение, на субботы, на новомесячия, на праздники, на священные вещи и на жертвы за грех для очищения Израиля, и на все, совершаемое в доме Бога нашего.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20825, 'rst66', 'neh', 10, 34, 'И бросили мы жребии о доставке дров, священники, левиты и народ, когда которому поколению нашему в назначенные времена, из года в год, привозить <i>их</i> к дому Бога нашего, чтоб они горели на жертвеннике Господа, Бога нашего, по написанному в законе.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20826, 'rst66', 'neh', 10, 35, '<i>И обязались мы</i> каждый год приносить в дом Господень начатки с земли нашей и начатки всяких плодов со всякого дерева;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20827, 'rst66', 'neh', 10, 36, 'также приводить в дом Бога нашего к священникам, служащим в доме Бога нашего, первенцев из сыновей наших и из скота нашего, как написано в законе, и первородное от крупного и мелкого скота нашего.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20828, 'rst66', 'neh', 10, 37, 'И начатки из молотого хлеба нашего и приношений наших, и плодов со всякого дерева, вина и масла мы будем доставлять священникам в кладовые при доме Бога нашего и десятину с земли нашей левитам. Они, левиты, будут брать десятину во всех городах, где у нас земледелие.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20829, 'rst66', 'neh', 10, 38, 'При левитах, когда они будут брать левитскую десятину, будет находиться священник, сын Аарона, чтобы левиты десятину из своих десятин отвозили в дом Бога нашего в комнаты, <i>отделенные</i> для кладовой,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20830, 'rst66', 'neh', 10, 39, 'потому что в эти комнаты как сыны Израилевы, так и левиты должны доставлять приносимое в дар: хлеб, вино и масло. Там священные сосуды, и служащие священники, и привратники, и певцы. И мы не оставим дома Бога нашего.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20831, 'rst66', 'neh', 11, 1, 'И жили начальники народа в Иерусалиме, а прочие из народа бросили жребии, чтоб одна из десяти частей их шла на жительство в святой город Иерусалим, а девять <i>оставались</i> в <i>прочих</i> городах.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20832, 'rst66', 'neh', 11, 2, 'И благословил народ всех, которые добровольно согласились жить в Иерусалиме.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20833, 'rst66', 'neh', 11, 3, 'Вот главы страны, которые жили в Иерусалиме, &mdash; а в городах Иудеи жили, всякий в своем владении, по городам своим: Израильтяне, священники, левиты и нефинеи и сыновья рабов Соломоновых;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20834, 'rst66', 'neh', 11, 4, 'в Иерусалиме жили из сыновей Иуды и из сыновей Вениамина. Из сыновей Иуды: Афаия, сын Уззии, сын Захарии, сын Амарии, сын Сафатии, сын Малелеила, из сыновей Фареса,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20835, 'rst66', 'neh', 11, 5, 'и Маасея, сын Варуха, сын Колхозея, сын Хазаии, сын Адаии, сын Иоиарива, сын Захарии, сын Шилония.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20836, 'rst66', 'neh', 11, 6, 'Всех сыновей Фареса, живших в Иерусалиме, четыреста шестьдесят восемь, люди отличные.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20837, 'rst66', 'neh', 11, 7, 'И вот сыновья Вениамина: Саллу, сын Мешуллама, сын Иоеда, сын Федаии, сын Колаии, сын Маасеи, сын Ифиила, сын Исаии,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20838, 'rst66', 'neh', 11, 8, 'и за ним Габбай, Саллай &mdash; девятьсот двадцать восемь.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20839, 'rst66', 'neh', 11, 9, 'Иоиль, сын Зихри, был начальником над ними, а Иуда, сын Сенуи, был вторым над городом.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20840, 'rst66', 'neh', 11, 10, 'Из священников: Иедаия, сын Иоиарива, Иахин,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20841, 'rst66', 'neh', 11, 11, 'Сераия, сын Хелкии, сын Мешуллама, сын Садока, сын Мераиофа, сын Ахитува, начальствующий в доме Божием,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20842, 'rst66', 'neh', 11, 12, 'и братья их, отправлявшие службу в доме <i>Божием</i>, &mdash; восемьсот двадцать два; и Адаия, сын Иерохама, сын Фелалии, сын Амция, сын Захарии, сын Пашхура, сын Малхии,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20843, 'rst66', 'neh', 11, 13, 'и братья его, главы поколений &mdash; двести сорок два; и Амашсай, сын Азариила, сын Ахзая, сын Мешиллемофа, сын Иммера,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20844, 'rst66', 'neh', 11, 14, 'и братья его, люди отличные &mdash; сто двадцать восемь. Начальником над ними был Завдиил, сын Гагедолима.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20845, 'rst66', 'neh', 11, 15, 'А из левитов: Шемаия, сын Хашшува, сын Азрикама, сын Хашавии, сын Вунния,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20846, 'rst66', 'neh', 11, 16, 'и Шавфай, и Иозавад, из глав левитов по внешним делам дома Божия,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20847, 'rst66', 'neh', 11, 17, 'и Матфания, сын Михи, сын Завдия, сын Асафа, главный начинатель славословия при молитве, и Бакбукия, второй <i>по нем</i> из братьев его, и Авда, сын Шаммуя, сын Галала, сын Идифуна.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20848, 'rst66', 'neh', 11, 18, 'Всех левитов во святом городе двести восемьдесят четыре.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20849, 'rst66', 'neh', 11, 19, 'А привратники: Аккув, Талмон и братья их, содержавшие стражу у ворот, &mdash; сто семьдесят два.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20850, 'rst66', 'neh', 11, 20, 'Прочие Израильтяне, священники, левиты <i>жили</i> по всем городам Иудеи, каждый в своем уделе.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20851, 'rst66', 'neh', 11, 21, 'А нефинеи жили в Офеле; над нефинеями Циха и Гишфа.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20852, 'rst66', 'neh', 11, 22, 'Начальником над левитами в Иерусалиме был Уззий, сын Вания, сын Хашавии, сын Матфании, сын Михи, из сыновей Асафовых, которые были певцами при служении в доме Божием,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20853, 'rst66', 'neh', 11, 23, 'потому что от царя <i>было</i> о них <i>особое</i> повеление, и назначено было на каждый день для певцов определенное содержание.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20854, 'rst66', 'neh', 11, 24, 'И Петахия, сын Мешезавела, из сыновей Зары, сына Иуды, был доверенным от царя по всяким делам, <i>касающимся</i> до народа.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20855, 'rst66', 'neh', 11, 25, 'Из <i>живших</i> же в селах, на полях своих, сыновья Иуды жили в Кириаф‐Арбе и зависящих от нее городах, в Дивоне и зависящих от него городах, в Иекавцеиле и селах его,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20856, 'rst66', 'neh', 11, 26, 'в Иешуе, в Моладе и в Беф‐Палете,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20857, 'rst66', 'neh', 11, 27, 'в Хацар‐Шуале, в Вирсавии и зависящих от нее городах,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20858, 'rst66', 'neh', 11, 28, 'в Секелаге, в Мехоне и зависящих от нее городах,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20859, 'rst66', 'neh', 11, 29, 'в Ен‐Риммоне, в Цоре и в Иармуфе,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20860, 'rst66', 'neh', 11, 30, 'в Заноахе, Одолламе и селах их, в Лахисе и на полях его, в Азеке и зависящих от нее городах. Они расположились от Вирсавии и до долины Енномовой.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20861, 'rst66', 'neh', 11, 31, 'Сыновья Вениаминовы, <i>начиная</i> от Гевы, в Михмасе, Гае, в Вефиле и зависящих от него городах,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20862, 'rst66', 'neh', 11, 32, 'в Анафофе, Нове, Анании,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20863, 'rst66', 'neh', 11, 33, 'Гацоре, Раме, Гиффаиме,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20864, 'rst66', 'neh', 11, 34, 'Хадиде, Цевоиме, Неваллате,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20865, 'rst66', 'neh', 11, 35, 'Лоде, Оно, в долине Харашиме.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20866, 'rst66', 'neh', 11, 36, 'И левиты имели жилища свои в участках Иуды и Вениамина.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20867, 'rst66', 'neh', 12, 1, 'Вот священники и левиты, которые пришли с Зоровавелем, сыном Салафииловым, и с Иисусом: Сераия, Иеремия, Ездра,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20868, 'rst66', 'neh', 12, 2, 'Амария, Маллух, Хаттуш,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20869, 'rst66', 'neh', 12, 3, 'Шехания, Рехум, Меремоф,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20870, 'rst66', 'neh', 12, 4, 'Иддо, Гиннефой, Авия,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20871, 'rst66', 'neh', 12, 5, 'Миямин, Маадия, Вилга,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20872, 'rst66', 'neh', 12, 6, 'Шемаия, Иоиарив, Иедаия,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20873, 'rst66', 'neh', 12, 7, 'Саллу, Амок, Хелкия, Иедаия. Это главы священников и братья их во дни Иисуса.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20874, 'rst66', 'neh', 12, 8, 'А левиты: Иисус, Биннуй, Кадмиил, Шеревия, Иуда, Матфания, <i>главный</i> при славословии, он и братья его,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20875, 'rst66', 'neh', 12, 9, 'и Бакбукия и Унний, братья их, наряду с ними <i>державшие</i> стражу.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20876, 'rst66', 'neh', 12, 10, 'Иисус родил Иоакима, Иоаким родил Елиашива, Елиашив родил Иоиаду,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20877, 'rst66', 'neh', 12, 11, 'Иоиада родил Ионафана, Ионафан родил Иаддуя.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20878, 'rst66', 'neh', 12, 12, 'Во дни Иоакима были священники, главы поколений: из <i>дома</i> Сераии Мераия, из <i>дома</i> Иеремии Ханания,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20879, 'rst66', 'neh', 12, 13, 'из <i>дома</i> Ездры Мешуллам, из <i>дома</i> Амарии Иоханан,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20880, 'rst66', 'neh', 12, 14, 'из <i>дома</i> Мелиху Ионафан, из <i>дома</i> Шевании Иосиф,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20881, 'rst66', 'neh', 12, 15, 'из <i>дома</i> Харима Адна, из <i>дома</i> Мераиофа Хелкия,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20882, 'rst66', 'neh', 12, 16, 'из <i>дома</i> Иддо Захария, из <i>дома</i> Гиннефона Мешуллам,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20883, 'rst66', 'neh', 12, 17, 'из <i>дома</i> Авии Зихрий, из <i>дома</i> Миниамина, из <i>дома</i> Моадии Пилтай,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20884, 'rst66', 'neh', 12, 18, 'из <i>дома</i> Вилги Шаммуй, из <i>дома</i> Шемаии Ионафан,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20885, 'rst66', 'neh', 12, 19, 'из <i>дома</i> Иоиарива Мафнай, из <i>дома</i> Иедаии Уззий,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20886, 'rst66', 'neh', 12, 20, 'из <i>дома</i> Саллая Каллай, из <i>дома</i> Амока Евер,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20887, 'rst66', 'neh', 12, 21, 'из <i>дома</i> Хелкии Хашавия, из <i>дома</i> Иедаии Нафанаил.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20888, 'rst66', 'neh', 12, 22, 'Левиты, главы поколений, внесены в запись во дни Елиашива, Иоиады, Иоханана и Иаддуя, и также священники в царствование Дария Персидского.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20889, 'rst66', 'neh', 12, 23, 'Сыновья Левия, главы поколений, вписаны в летописи до дней Иоханана, сына Елиашивова.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20890, 'rst66', 'neh', 12, 24, 'Главы левитов: Хашавия, Шеревия, и Иисус, сын Кадмиила, и братья их, при них <i>поставленные</i> для славословия при благодарениях, по установлению Давида, человека Божия &mdash; смена за сменою.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20891, 'rst66', 'neh', 12, 25, 'Матфания, Бакбукия, Овадия, Мешуллам, Талмон, Аккув &mdash; стражи, привратники на страже у порогов ворот.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20892, 'rst66', 'neh', 12, 26, 'Они были во дни Иоакима, сына Иисусова, сына Иоседекова, и во дни областеначальника Неемии и книжника Ездры, священника.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20893, 'rst66', 'neh', 12, 27, 'При освящении стены Иерусалимской потребовали левитов из всех мест их, приказывая им прийти в Иерусалим для совершения освящения и радостного празднества со славословиями и песнями при <i>звуке</i> кимвалов, псалтирей и гуслей.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20894, 'rst66', 'neh', 12, 28, 'И собрались сыновья певцов из округа Иерусалимского и из сел Нетофафских,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20895, 'rst66', 'neh', 12, 29, 'и из Беф‐Гаггилгала, и с полей Гевы и Азмавета, потому что певцы выстроили себе села в окрестностях Иерусалима.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20896, 'rst66', 'neh', 12, 30, 'И очистились священники и левиты, и очистили народ и ворота, и стену.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20897, 'rst66', 'neh', 12, 31, 'Тогда я повел начальствующих в Иудее на стену и поставил два больших хора для шествия, и один из них шел по правой стороне стены к Навозным воротам.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20898, 'rst66', 'neh', 12, 32, 'За ними шел Гошаия и половина начальствующих в Иудее,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20899, 'rst66', 'neh', 12, 33, 'Азария, Ездра и Мешуллам,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20900, 'rst66', 'neh', 12, 34, 'Иуда и Вениамин, и Шемаия и Иеремия,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20901, 'rst66', 'neh', 12, 35, 'а из сыновей священнических с трубами: Захария, сын Ионафана, сын Шемаии, сын Матфании, сын Михея, сын Закхура, сын Асафа,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20902, 'rst66', 'neh', 12, 36, 'и братья его: Шемаия, Азариил, Милалай, Гилалай, Маай, Нафанаил, Иуда и Хананий с музыкальными орудиями Давида, человека Божия, и книжник Ездра впереди них.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20903, 'rst66', 'neh', 12, 37, 'Подле ворот Источника, против них, они взошли по ступеням города Давидова, по лестнице, ведущей на стену сверх дома Давидова до Водяных ворот к востоку.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20904, 'rst66', 'neh', 12, 38, 'Другой хор шел напротив них, и за ним я и половина народа, по стене от Печной башни и до широкой стены,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20905, 'rst66', 'neh', 12, 39, 'и от ворот Ефремовых, мимо старых ворот и ворот Рыбных, и башни Хананела, и башни Меа, к Овечьим воротам, и остановились у ворот Темничных.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20906, 'rst66', 'neh', 12, 40, 'Потом оба хора стали у дома Божия, и я и половина начальствующих со мною,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20907, 'rst66', 'neh', 12, 41, 'и священники: Елиаким, Маасея, Миниамин, Михей, Елиоенай, Захария, Ханания с трубами,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20908, 'rst66', 'neh', 12, 42, 'и Маасея и Шемаия, и Елеазар и Уззий, и Иоханан и Малхия, и Елам и Езер. И пели певцы громко; главным <i>у них был</i> Израхия.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20909, 'rst66', 'neh', 12, 43, 'И приносили в тот день большие жертвы и веселились, потому что Бог дал им великую радость. Веселились и жены и дети, и веселие Иерусалима далеко было слышно.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20910, 'rst66', 'neh', 12, 44, 'В тот же день приставлены были люди к кладовым комнатам для приношений начатков и десятин, чтобы собирать с полей при городах части, положенные законом для священников и левитов, потому что Иудеям радостно было <i>смотреть</i> на стоящих священников и левитов,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20911, 'rst66', 'neh', 12, 45, 'которые совершали службу Богу своему и дела очищения и были певцами и привратниками по установлению Давида и сына его Соломона.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20912, 'rst66', 'neh', 12, 46, 'Ибо издавна во дни Давида и Асафа были установлены главы певцов и песни Богу, хвалебные и благодарственные.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20913, 'rst66', 'neh', 12, 47, 'Все Израильтяне во дни Зоровавеля и во дни Неемии давали части певцам и привратникам на каждый день и отдавали святыни левитам, а левиты отдавали святыни сынам Аарона.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20914, 'rst66', 'neh', 13, 1, 'В тот день читано было из книги Моисеевой вслух народа и найдено написанное в ней: «Аммонитянин и Моавитянин не может войти в общество Божие вовеки,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20915, 'rst66', 'neh', 13, 2, 'потому что они не встретили сынов Израиля с хлебом и водою и наняли против него Валаама, чтобы проклясть его, но Бог наш обратил проклятие в благословение».');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20916, 'rst66', 'neh', 13, 3, 'Услышав этот закон, они отделили все иноплеменное от Израиля.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20917, 'rst66', 'neh', 13, 4, 'А прежде того священник Елиашив, приставленный к комнатам при доме Бога нашего, близкий родственник Товии,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20918, 'rst66', 'neh', 13, 5, 'отделал для него большую комнату, в которую прежде клали хлебное приношение, ладан и сосуды, и десятины хлеба, вина и масла, положенные законом для левитов, певцов и привратников, и приношения для священников.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20919, 'rst66', 'neh', 13, 6, 'Когда все это <i>происходило</i>, я не был в Иерусалиме, потому что в тридцать втором году Вавилонского царя Артаксеркса я ходил к царю, и по прошествии нескольких дней <i>опять</i> выпросился у царя.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20920, 'rst66', 'neh', 13, 7, 'Когда я пришел в Иерусалим и узнал о худом деле, которое сделал Елиашив, отделав для Товии комнату на дворах дома Божия,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20921, 'rst66', 'neh', 13, 8, 'тогда мне было весьма неприятно, и я выбросил все домашние вещи Товиины вон из комнаты');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20922, 'rst66', 'neh', 13, 9, 'и сказал, чтобы очистили комнаты, и велел опять внести туда сосуды дома Божия, хлебное приношение и ладан.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20923, 'rst66', 'neh', 13, 10, 'Еще узнал я, что части левитам не отдаются, и что левиты и певцы, делавшие <i>свое</i> дело, разбежались, каждый на свое поле.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20924, 'rst66', 'neh', 13, 11, 'Я сделал <i>за это</i> выговор начальствующим и сказал: зачем оставлен нами дом Божий? И я собрал их и поставил их на место их.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20925, 'rst66', 'neh', 13, 12, 'И все Иудеи стали приносить десятины хлеба, вина и масла в кладовые.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20926, 'rst66', 'neh', 13, 13, 'И приставил я к кладовым Шелемию священника и Садока книжника и Федаию из левитов, и при них Ханана, сына Закхура, сына Матфании, потому что они считались верными. И на них <i>возложено</i> раздавать части братьям своим.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20927, 'rst66', 'neh', 13, 14, 'Помяни меня за это, Боже мой, и не изгладь усердных дел моих, которые я сделал для дома Бога моего и для служения при нем!');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20928, 'rst66', 'neh', 13, 15, 'В те дни я увидел в Иудее, что в субботу топчут точила, возят снопы и навьючивают ослов вином, виноградом, смоквами и всяким грузом, и отвозят в субботний день в Иерусалим. И я строго выговорил <i>им</i> в тот же день, когда они продавали съестное.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20929, 'rst66', 'neh', 13, 16, 'И Тиряне жили в <i>Иудее</i> и привозили рыбу и всякий товар и продавали в субботу жителям Иудеи и в Иерусалиме.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20930, 'rst66', 'neh', 13, 17, 'И я сделал выговор знатнейшим из Иудеев и сказал им: зачем вы делаете такое зло и оскверняете день субботний?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20931, 'rst66', 'neh', 13, 18, 'Не так ли поступали отцы ваши, и за то Бог наш навел на нас и на город сей все это бедствие? А вы увеличиваете гнев <i>Его</i> на Израиля, оскверняя субботу.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20932, 'rst66', 'neh', 13, 19, 'После сего, когда смеркалось, у ворот Иерусалимских, перед субботою, я велел запирать двери и сказал, чтобы не отпирали их до <i>утра</i> после субботы. И слуг моих я ставил у ворот, чтобы никакая ноша не проходила в день субботний.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20933, 'rst66', 'neh', 13, 20, 'И ночевали торговцы и продавцы всякого товара вне Иерусалима раз и два.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20934, 'rst66', 'neh', 13, 21, 'Но я строго выговорил им и сказал им: зачем вы ночуете возле стены? Если сделаете это в другой раз, я наложу руку на вас. С того времени они не приходили в субботу.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20935, 'rst66', 'neh', 13, 22, 'И сказал я левитам, чтобы они очистились и пришли содержать стражу у ворот, дабы святить день субботний. И за сие помяни меня, Боже мой, и пощади меня по великой милости Твоей!');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20936, 'rst66', 'neh', 13, 23, 'Еще в те дни я видел Иудеев, которые взяли себе жен из Азотянок, Аммонитянок и Моавитянок;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20937, 'rst66', 'neh', 13, 24, 'и оттого сыновья их вполовину говорят по‐азотски, или языком других народов, и не умеют говорить по‐иудейски.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20938, 'rst66', 'neh', 13, 25, 'Я сделал за это выговор и проклинал их, и некоторых из мужей бил, рвал у них волоса и заклинал их Богом, чтобы они не отдавали дочерей своих за сыновей их и не брали дочерей их за сыновей своих и за себя.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20939, 'rst66', 'neh', 13, 26, 'Не из‐за них ли, <i>говорил я</i>, грешил Соломон, царь Израилев? У многих народов не было такого царя, как он. Он был любим Богом своим, и Бог поставил его царем над всеми Израильтянами; и, однако же, чужеземные жены ввели в грех и его.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20940, 'rst66', 'neh', 13, 27, 'И можно ли нам слышать о вас, что вы делаете все сие великое зло, грешите пред Богом нашим, принимая в сожительство чужеземных жен?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20941, 'rst66', 'neh', 13, 28, 'И из сыновей Иоиады, сына великого священника Елиашива, один был зятем Санаваллата, Хоронита. Я прогнал его от себя.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20942, 'rst66', 'neh', 13, 29, 'Воспомяни им, Боже мой, что они опорочили священство и завет священнический и левитский!');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20943, 'rst66', 'neh', 13, 30, 'Так очистил я их от всего чужеземного и восстановил службы священников и левитов, каждого в деле его,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (20944, 'rst66', 'neh', 13, 31, 'и доставку дров в назначенные времена и начатки. Помяни меня, Боже мой, во благо <i>мне!</i>');",
        ];

