        $list = [
            "INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22182, 'rst66', 'psa', 1, 0, 'Псалом Давида.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22183, 'rst66', 'psa', 1, 1, 'Блажен муж, который не ходит на совет нечестивых и не стоит на пути грешных и не сидит в собрании развратителей,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22184, 'rst66', 'psa', 1, 2, 'но в законе Господа воля его, и о законе Его размышляет он день и ночь!');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22185, 'rst66', 'psa', 1, 3, 'И будет он как дерево, посаженное при потоках вод, которое приносит плод свой во время свое и лист которого не вянет; и во всем, что он ни делает, успеет.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22186, 'rst66', 'psa', 1, 4, 'Не так &mdash; нечестивые: но они &mdash; как прах, возметаемый ветром.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22187, 'rst66', 'psa', 1, 5, 'Потому не устоят нечестивые на суде и грешники &mdash; в собрании праведных.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22188, 'rst66', 'psa', 1, 6, 'Ибо знает Господь путь праведных, а путь нечестивых погибнет.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22189, 'rst66', 'psa', 2, 0, 'Псалом Давида.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22190, 'rst66', 'psa', 2, 1, 'Зачем мятутся народы, и племена замышляют тщетное?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22191, 'rst66', 'psa', 2, 2, 'Восстают цари земли, и князья совещаются вместе против Господа и против Помазанника Его:');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22192, 'rst66', 'psa', 2, 3, '«Расторгнем узы их, и свергнем с себя оковы их».');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22193, 'rst66', 'psa', 2, 4, 'Живущий на небесах посмеется, Господь поругается им.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22194, 'rst66', 'psa', 2, 5, 'Тогда скажет им во гневе Своем и яростью Своею приведет их в смятение:');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22195, 'rst66', 'psa', 2, 6, '«Я помазал Царя Моего над Сионом, святою горою Моею;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22196, 'rst66', 'psa', 2, 7, 'возвещу определение: Господь сказал Мне: Ты Сын Мой; Я ныне родил Тебя;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22197, 'rst66', 'psa', 2, 8, 'проси у Меня, и дам народы в наследие Тебе и пределы земли во владение Тебе;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22198, 'rst66', 'psa', 2, 9, 'Ты поразишь их жезлом железным; сокрушишь их, как сосуд горшечника».');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22199, 'rst66', 'psa', 2, 10, 'Итак, вразумитесь, цари; научитесь, судьи земли!');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22200, 'rst66', 'psa', 2, 11, 'Служите Господу со страхом и радуйтесь с трепетом.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22201, 'rst66', 'psa', 2, 12, 'Почтите Сына, чтобы Он не прогневался и чтобы вам не погибнуть в пути <i>вашем</i>, ибо гнев Его возгорится вскоре. Блаженны все, уповающие на Него.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22202, 'rst66', 'psa', 3, 1, 'Псалом Давида, когда он бежал от Авессалома, сына своего.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22203, 'rst66', 'psa', 3, 2, 'Господи! как умножились враги мои! Многие восстают на меня,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22204, 'rst66', 'psa', 3, 3, 'многие говорят душе моей: «нет ему спасения в Боге».');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22205, 'rst66', 'psa', 3, 4, 'Но Ты, Господи, щит предо мною, слава моя, и Ты возносишь голову мою.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22206, 'rst66', 'psa', 3, 5, 'Гласом моим взываю к Господу, и Он слышит меня со святой горы Своей.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22207, 'rst66', 'psa', 3, 6, 'Ложусь я, сплю и встаю, ибо Господь защищает меня.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22208, 'rst66', 'psa', 3, 7, 'Не убоюсь тем народа, которые со всех сторон ополчились на меня.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22209, 'rst66', 'psa', 3, 8, 'Восстань, Господи! спаси меня, Боже мой! ибо Ты поражаешь в ланиту всех врагов моих; сокрушаешь зубы нечестивых.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22210, 'rst66', 'psa', 3, 9, 'От Господа спасение. Над народом Твоим благословение Твое.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22211, 'rst66', 'psa', 4, 1, 'Начальнику хора. На струнных <i>орудиях</i>. Псалом Давида.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22212, 'rst66', 'psa', 4, 2, 'Когда я взываю, услышь меня, Боже правды моей! В тесноте Ты давал мне простор. Помилуй меня и услышь молитву мою.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22213, 'rst66', 'psa', 4, 3, 'Сыны мужей! доколе слава моя будет в поругании? доколе будете любить суету и искать лжи?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22214, 'rst66', 'psa', 4, 4, 'Знайте, что Господь отделил для Себя святого Своего; Господь слышит, когда я призываю Его.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22215, 'rst66', 'psa', 4, 5, 'Гневаясь, не согрешайте: размыслите в сердцах ваших на ложах ваших, и утишитесь;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22216, 'rst66', 'psa', 4, 6, 'приносите жертвы правды и уповайте на Господа.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22217, 'rst66', 'psa', 4, 7, 'Многие говорят: «кто покажет нам благо?» Яви нам свет лица Твоего, Господи!');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22218, 'rst66', 'psa', 4, 8, 'Ты исполнил сердце мое веселием с того времени, как у них хлеб и вино умножились.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22219, 'rst66', 'psa', 4, 9, 'Спокойно ложусь я и сплю, ибо Ты, Господи, един даешь мне жить в безопасности.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22220, 'rst66', 'psa', 5, 1, 'Начальнику хора. На духовых <i>орудиях</i>. Псалом Давида.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22221, 'rst66', 'psa', 5, 2, 'Услышь, Господи, слова мои, уразумей помышления мои.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22222, 'rst66', 'psa', 5, 3, 'Внемли гласу вопля моего, Царь мой и Бог мой! ибо я к Тебе молюсь.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22223, 'rst66', 'psa', 5, 4, 'Господи! рано услышь голос мой, &mdash; рано предстану пред Тобою и буду ожидать,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22224, 'rst66', 'psa', 5, 5, 'ибо Ты Бог, не любящий беззакония; у Тебя не водворится злой;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22225, 'rst66', 'psa', 5, 6, 'нечестивые не пребудут пред очами Твоими: Ты ненавидишь всех, делающих беззаконие.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22226, 'rst66', 'psa', 5, 7, 'Ты погубишь говорящих ложь; кровожадного и коварного гнушается Господь.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22227, 'rst66', 'psa', 5, 8, 'А я, по множеству милости Твоей, войду в дом Твой, поклонюсь святому храму Твоему в страхе Твоем.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22228, 'rst66', 'psa', 5, 9, 'Господи! путеводи меня в правде Твоей, ради врагов моих; уровняй предо мною путь Твой.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22229, 'rst66', 'psa', 5, 10, 'Ибо нет в устах их истины: сердце их &mdash; пагуба, гортань их &mdash; открытый гроб, языком своим льстят.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22230, 'rst66', 'psa', 5, 11, 'Осуди их, Боже, да падут они от замыслов своих; по множеству нечестия их, отвергни их, ибо они возмутились против Тебя.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22231, 'rst66', 'psa', 5, 12, 'И возрадуются все, уповающие на Тебя, вечно будут ликовать, и Ты будешь покровительствовать им; и будут хвалиться Тобою любящие имя Твое.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22232, 'rst66', 'psa', 5, 13, 'Ибо Ты благословляешь праведника, Господи; благоволением, как щитом, венчаешь его.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22233, 'rst66', 'psa', 6, 1, 'Начальнику хора. На восьмиструнном. Псалом Давида.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22234, 'rst66', 'psa', 6, 2, 'Господи! не в ярости Твоей обличай меня и не во гневе Твоем наказывай меня.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22235, 'rst66', 'psa', 6, 3, 'Помилуй меня, Господи, ибо я немощен; исцели меня, Господи, ибо кости мои потрясены;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22236, 'rst66', 'psa', 6, 4, 'и душа моя сильно потрясена; Ты же, Господи, доколе?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22237, 'rst66', 'psa', 6, 5, 'Обратись, Господи, избавь душу мою, спаси меня ради милости Твоей,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22238, 'rst66', 'psa', 6, 6, 'ибо в смерти нет памятования о Тебе: во гробе кто будет славить Тебя?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22239, 'rst66', 'psa', 6, 7, 'Утомлен я воздыханиями моими: каждую ночь омываю ложе мое, слезами моими омочаю постель мою.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22240, 'rst66', 'psa', 6, 8, 'Иссохло от печали око мое, обветшало от всех врагов моих.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22241, 'rst66', 'psa', 6, 9, 'Удалитесь от меня все, делающие беззаконие, ибо услышал Господь голос плача моего,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22242, 'rst66', 'psa', 6, 10, 'услышал Господь моление мое; Господь примет молитву мою.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22243, 'rst66', 'psa', 6, 11, 'Да будут постыжены и жестоко поражены все враги мои; да возвратятся и постыдятся мгновенно.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22244, 'rst66', 'psa', 7, 1, 'Плачевная песнь, которую Давид воспел Господу по делу Хуса, из племени Вениаминова.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22245, 'rst66', 'psa', 7, 2, 'Господи, Боже мой! на Тебя я уповаю; спаси меня от всех гонителей моих и избавь меня;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22246, 'rst66', 'psa', 7, 3, 'да не исторгнет он, подобно льву, души моей, терзая, когда нет избавляющего.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22247, 'rst66', 'psa', 7, 4, 'Господи, Боже мой! если я что сделал, если есть неправда в руках моих,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22248, 'rst66', 'psa', 7, 5, 'если я платил злом тому, кто был со мною в мире, &mdash; я, который спасал даже того, кто без причины стал моим врагом, &mdash;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22249, 'rst66', 'psa', 7, 6, 'то пусть враг преследует душу мою и настигнет, пусть втопчет в землю жизнь мою и славу мою повергнет в прах.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22250, 'rst66', 'psa', 7, 7, 'Восстань, Господи, во гневе Твоем; подвигнись против неистовства врагов моих, пробудись для меня на суд, который Ты заповедал, &mdash;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22251, 'rst66', 'psa', 7, 8, 'сонм людей станет вокруг Тебя; над ним поднимись на высоту.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22252, 'rst66', 'psa', 7, 9, 'Господь судит народы. Суди меня, Господи, по правде моей и по непорочности моей во мне.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22253, 'rst66', 'psa', 7, 10, 'Да прекратится злоба нечестивых, а праведника подкрепи, ибо Ты испытуешь сердца и утробы, праведный Боже!');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22254, 'rst66', 'psa', 7, 11, 'Щит мой в Боге, спасающем правых сердцем.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22255, 'rst66', 'psa', 7, 12, 'Бог &mdash; судия праведный, и Бог, всякий день строго взыскивающий,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22256, 'rst66', 'psa', 7, 13, 'если <i>кто</i> не обращается. Он изощряет Свой меч, напрягает лук Свой и направляет его,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22257, 'rst66', 'psa', 7, 14, 'приготовляет для него сосуды смерти, стрелы Свои делает палящими.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22258, 'rst66', 'psa', 7, 15, 'Вот, <i>нечестивый</i> зачал неправду, был чреват злобою и родил себе ложь;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22259, 'rst66', 'psa', 7, 16, 'рыл ров, и выкопал его, и упал в яму, которую приготовил:');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22260, 'rst66', 'psa', 7, 17, 'злоба его обратится на его голову, и злодейство его упадет на его темя.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22261, 'rst66', 'psa', 7, 18, 'Славлю Господа по правде Его и пою имени Господа Всевышнего.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22262, 'rst66', 'psa', 8, 1, 'Начальнику хора. На Гефском <i>орудии</i>. Псалом Давида.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22263, 'rst66', 'psa', 8, 2, 'Господи, Боже наш! как величественно имя Твое по всей земле! Слава Твоя простирается превыше небес!');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22264, 'rst66', 'psa', 8, 3, 'Из уст младенцев и грудных детей Ты устроил хвалу, ради врагов Твоих, дабы сделать безмолвным врага и мстителя.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22265, 'rst66', 'psa', 8, 4, 'Когда взираю я на небеса Твои &mdash; дело Твоих перстов, на луну и звезды, которые Ты поставил,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22266, 'rst66', 'psa', 8, 5, 'то что <i>есть</i> человек, что Ты помнишь его, и сын человеческий, что Ты посещаешь его?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22267, 'rst66', 'psa', 8, 6, 'Не много Ты умалил его пред Ангелами: славою и честью увенчал его;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22268, 'rst66', 'psa', 8, 7, 'поставил его владыкою над делами рук Твоих; всё положил под ноги его:');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22269, 'rst66', 'psa', 8, 8, 'овец и волов всех, и также полевых зверей,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22270, 'rst66', 'psa', 8, 9, 'птиц небесных и рыб морских, все, преходящее морскими стезями.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22271, 'rst66', 'psa', 8, 10, 'Господи, Боже наш! Как величественно имя Твое по всей земле!');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22272, 'rst66', 'psa', 9, 1, 'Начальнику хора. По смерти Лабена. Псалом Давида.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22273, 'rst66', 'psa', 9, 2, 'Буду славить <i>Тебя</i>, Господи, всем сердцем моим, возвещать все чудеса Твои.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22274, 'rst66', 'psa', 9, 3, 'Буду радоваться и торжествовать о Тебе, петь имени Твоему, Всевышний.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22275, 'rst66', 'psa', 9, 4, 'Когда враги мои обращены назад, то преткнутся и погибнут пред лицом Твоим,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22276, 'rst66', 'psa', 9, 5, 'ибо Ты производил мой суд и мою тяжбу; Ты воссел на престоле, Судия праведный.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22277, 'rst66', 'psa', 9, 6, 'Ты вознегодовал на народы, погубил нечестивого, имя их изгладил на веки и веки.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22278, 'rst66', 'psa', 9, 7, 'У врага совсем не стало оружия, и города Ты разрушил; погибла память их с ними.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22279, 'rst66', 'psa', 9, 8, 'Но Господь пребывает вовек; Он приготовил для суда престол Свой,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22280, 'rst66', 'psa', 9, 9, 'и Он будет судить вселенную по правде, совершит суд над народами по правоте.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22281, 'rst66', 'psa', 9, 10, 'И будет Господь прибежищем угнетенному, прибежищем во времена скорби;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22282, 'rst66', 'psa', 9, 11, 'и будут уповать на Тебя знающие имя Твое, потому что Ты не оставляешь ищущих Тебя, Господи.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22283, 'rst66', 'psa', 9, 12, 'Пойте Господу, живущему на Сионе, возвещайте между народами дела Его,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22284, 'rst66', 'psa', 9, 13, 'ибо Он взыскивает за кровь; помнит их, не забывает вопля угнетенных.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22285, 'rst66', 'psa', 9, 14, 'Помилуй меня, Господи; воззри на страдание мое от ненавидящих меня, &mdash; Ты, Который возносишь меня от врат смерти,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22286, 'rst66', 'psa', 9, 15, 'чтобы я возвещал все хвалы Твои во вратах дщери Сионовой: буду радоваться о спасении Твоем.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22287, 'rst66', 'psa', 9, 16, 'Обрушились народы в яму, которую выкопали; в сети, которую скрыли они, запуталась нога их.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22288, 'rst66', 'psa', 9, 17, 'Познан был Господь по суду, который Он совершил; нечестивый уловлен делами рук своих.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22289, 'rst66', 'psa', 9, 18, 'Да обратятся нечестивые в ад, &mdash; все народы, забывающие Бога.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22290, 'rst66', 'psa', 9, 19, 'Ибо не навсегда забыт будет нищий, и надежда бедных не до конца погибнет.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22291, 'rst66', 'psa', 9, 20, 'Восстань, Господи, да не преобладает человек, да судятся народы пред лицом Твоим.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22292, 'rst66', 'psa', 9, 21, 'Наведи, Господи, страх на них; да знают народы, что человеки они.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22293, 'rst66', 'psa', 9, 22, 'Для чего, Господи, стоишь вдали, скрываешь Себя во время скорби?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22294, 'rst66', 'psa', 9, 23, 'По гордости своей нечестивый преследует бедного: да уловятся они ухищрениями, которые сами вымышляют.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22295, 'rst66', 'psa', 9, 24, 'Ибо нечестивый хвалится похотью души своей; корыстолюбец ублажает себя.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22296, 'rst66', 'psa', 9, 25, 'В надмении своем нечестивый пренебрегает Господа: «не взыщет»; во всех помыслах его: «нет Бога!»');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22297, 'rst66', 'psa', 9, 26, 'Во всякое время пути его гибельны; суды Твои далеки для него; на всех врагов своих он смотрит с пренебрежением;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22298, 'rst66', 'psa', 9, 27, 'говорит в сердце своем: «не поколеблюсь; в род и род не приключится <i>мне</i> зла»;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22299, 'rst66', 'psa', 9, 28, 'уста его полны проклятия, коварства и лжи; под языком его &mdash; мучение и пагуба;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22300, 'rst66', 'psa', 9, 29, 'сидит в засаде за двором, в потаенных местах убивает невинного; глаза его подсматривают за бедным;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22301, 'rst66', 'psa', 9, 30, 'подстерегает в потаенном месте, как лев в логовище; подстерегает в засаде, чтобы схватить бедного; хватает бедного, увлекая в сети свои;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22302, 'rst66', 'psa', 9, 31, 'сгибается, прилегает, &mdash; и бедные падают в сильные когти его;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22303, 'rst66', 'psa', 9, 32, 'говорит в сердце своем: «забыл Бог, закрыл лицо Свое, не увидит никогда».');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22304, 'rst66', 'psa', 9, 33, 'Восстань, Господи, Боже <i>мой</i>, вознеси руку Твою, не забудь угнетенных.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22305, 'rst66', 'psa', 9, 34, 'Зачем нечестивый пренебрегает Бога, говоря в сердце своем: «Ты не взыщешь»?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22306, 'rst66', 'psa', 9, 35, 'Ты видишь, ибо Ты взираешь на обиды и притеснения, чтобы воздать Твоею рукою. Тебе предает себя бедный; сироте Ты помощник.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22307, 'rst66', 'psa', 9, 36, 'Сокруши мышцу нечестивому и злому, так чтобы искать и не найти его нечестия.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22308, 'rst66', 'psa', 9, 37, 'Господь &mdash; царь навеки, навсегда; исчезнут язычники с земли Его.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22309, 'rst66', 'psa', 9, 38, 'Господи! Ты слышишь желания смиренных; укрепи сердце их; открой ухо Твое,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22310, 'rst66', 'psa', 9, 39, 'чтобы дать суд сироте и угнетенному, да не устрашает более человек на земле.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22311, 'rst66', 'psa', 10, 0, 'Начальнику хора. Псалом Давида.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22312, 'rst66', 'psa', 10, 1, 'На Господа уповаю; как же вы говорите душе моей: «улетай на гору вашу, <i>как</i> птица»?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22313, 'rst66', 'psa', 10, 2, 'Ибо вот, нечестивые натянули лук, стрелу свою приложили к тетиве, чтобы во тьме стрелять в правых сердцем.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22314, 'rst66', 'psa', 10, 3, 'Когда разрушены основания, что сделает праведник?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22315, 'rst66', 'psa', 10, 4, 'Господь во святом храме Своем, Господь, &mdash; престол Его на небесах, очи Его зрят; вежды Его испытывают сынов человеческих.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22316, 'rst66', 'psa', 10, 5, 'Господь испытывает праведного, а нечестивого и любящего насилие ненавидит душа Его.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22317, 'rst66', 'psa', 10, 6, 'Дождем прольет Он на нечестивых горящие угли, огонь и серу; и палящий ветер &mdash; их доля из чаши;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22318, 'rst66', 'psa', 10, 7, 'ибо Господь праведен, любит правду; лицо Его видит праведника.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22319, 'rst66', 'psa', 11, 1, 'Начальнику хора. На восьмиструнном. Псалом Давида.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22320, 'rst66', 'psa', 11, 2, 'Спаси, Господи, ибо не стало праведного, ибо нет верных между сынами человеческими.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22321, 'rst66', 'psa', 11, 3, 'Ложь говорит каждый своему ближнему; уста льстивы, говорят от сердца притворного.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22322, 'rst66', 'psa', 11, 4, 'Истребит Господь все уста льстивые, язык велеречивый,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22323, 'rst66', 'psa', 11, 5, '<i>тех</i>, которые говорят: «языком нашим пересилим, уста наши с нами; кто нам господин?»');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22324, 'rst66', 'psa', 11, 6, 'Ради страдания нищих и воздыхания бедных ныне восстану, говорит Господь, поставлю в безопасности того, кого уловить хотят.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22325, 'rst66', 'psa', 11, 7, 'Слова Господни &mdash; слова чистые, серебро, очищенное от земли в горниле, семь раз переплавленное.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22326, 'rst66', 'psa', 11, 8, 'Ты, Господи, сохранишь их, соблюдешь от рода сего вовек.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22327, 'rst66', 'psa', 11, 9, 'Повсюду ходят нечестивые, когда ничтожные из сынов человеческих возвысились.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22328, 'rst66', 'psa', 12, 1, 'Начальнику хора. Псалом Давида.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22329, 'rst66', 'psa', 12, 2, 'Доколе, Господи, будешь забывать меня вконец, доколе будешь скрывать лицо Твое от меня?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22330, 'rst66', 'psa', 12, 3, 'Доколе мне слагать советы в душе моей, скорбь в сердце моем день <i>и ночь</i>? Доколе врагу моему возноситься надо мною?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22331, 'rst66', 'psa', 12, 4, 'Призри, услышь меня, Господи, Боже мой! Просвети очи мои, да не усну я <i>сном</i> смертным;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22332, 'rst66', 'psa', 12, 5, 'да не скажет враг мой: «я одолел его». Да не возрадуются гонители мои, если я поколеблюсь.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22333, 'rst66', 'psa', 12, 6, 'Я же уповаю на милость Твою; сердце мое возрадуется о спасении Твоем; воспою Господу, облагодетельствовавшему меня.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22334, 'rst66', 'psa', 13, 0, 'Начальнику хора. Псалом Давида.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22335, 'rst66', 'psa', 13, 1, 'Сказал безумец в сердце своем: «нет Бога». Они развратились, совершили гнусные дела; нет делающего добро.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22336, 'rst66', 'psa', 13, 2, 'Господь с небес призрел на сынов человеческих, чтобы видеть, есть ли разумеющий, ищущий Бога.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22337, 'rst66', 'psa', 13, 3, 'Все уклонились, сделались равно непотребными; нет делающего добро, нет ни одного.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22338, 'rst66', 'psa', 13, 4, 'Неужели не вразумятся все, делающие беззаконие, съедающие народ мой, <i>как</i> едят хлеб, и не призывающие Господа?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22339, 'rst66', 'psa', 13, 5, 'Там убоятся они страха, ибо Бог в роде праведных.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22340, 'rst66', 'psa', 13, 6, 'Вы посмеялись над мыслью нищего, что Господь &mdash; упование его.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22341, 'rst66', 'psa', 13, 7, '«Кто даст с Сиона спасение Израилю!» Когда Господь возвратит пленение народа Своего, тогда возрадуется Иаков и возвеселится Израиль.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22342, 'rst66', 'psa', 14, 0, 'Псалом Давида.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22343, 'rst66', 'psa', 14, 1, 'Господи! кто может пребывать в жилище Твоем? кто может обитать на святой горе Твоей?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22344, 'rst66', 'psa', 14, 2, 'Тот, кто ходит непорочно, и делает правду, и говорит истину в сердце своем;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22345, 'rst66', 'psa', 14, 3, 'кто не клевещет языком своим, не делает искреннему своему зла и не принимает поношения на ближнего своего;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22346, 'rst66', 'psa', 14, 4, 'тот, в глазах которого презрен отверженный, но который боящихся Господа славит; кто клянется, <i>хотя бы</i> злому, и не изменяет;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22347, 'rst66', 'psa', 14, 5, 'кто серебра своего не отдает в рост и не принимает даров против невинного. Поступающий так не поколеблется вовек.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22348, 'rst66', 'psa', 15, 0, 'Песнь Давида.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22349, 'rst66', 'psa', 15, 1, 'Храни меня, Боже, ибо я на Тебя уповаю.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22350, 'rst66', 'psa', 15, 2, 'Я сказал Господу: Ты &mdash; Господь мой; блага мои Тебе не нужны.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22351, 'rst66', 'psa', 15, 3, 'К святым, которые на земле, и к дивным <i>Твоим</i> &mdash; к ним все желание мое.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22352, 'rst66', 'psa', 15, 4, 'Пусть умножаются скорби у тех, которые текут к <i>богу</i> чужому; я не возлию кровавых возлияний их и не помяну имен их устами моими.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22353, 'rst66', 'psa', 15, 5, 'Господь есть часть наследия моего и чаши моей. Ты держишь жребий мой.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22354, 'rst66', 'psa', 15, 6, 'Межи мои прошли по прекрасным <i>местам</i>, и наследие мое приятно для меня.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22355, 'rst66', 'psa', 15, 7, 'Благословлю Господа, вразумившего меня; даже и ночью учит меня внутренность моя.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22356, 'rst66', 'psa', 15, 8, 'Всегда видел я пред собою Господа, ибо Он одесную меня; не поколеблюсь.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22357, 'rst66', 'psa', 15, 9, 'Оттого возрадовалось сердце мое и возвеселился язык мой; даже и плоть моя успокоится в уповании,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22358, 'rst66', 'psa', 15, 10, 'ибо Ты не оставишь души моей в аде и не дашь святому Твоему увидеть тление,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22359, 'rst66', 'psa', 15, 11, 'Ты укажешь мне путь жизни: полнота радостей пред лицом Твоим, блаженство в деснице Твоей вовек.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22360, 'rst66', 'psa', 16, 0, 'Молитва Давида.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22361, 'rst66', 'psa', 16, 1, 'Услышь, Господи, правду, внемли воплю моему, прими мольбу из уст нелживых.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22362, 'rst66', 'psa', 16, 2, 'От Твоего лица суд мне да изыдет; да воззрят очи Твои на правоту.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22363, 'rst66', 'psa', 16, 3, 'Ты испытал сердце мое, посетил меня ночью, искусил меня и ничего не нашел; от мыслей моих не отступают уста мои.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22364, 'rst66', 'psa', 16, 4, 'В делах человеческих, по слову уст Твоих, я охранял себя от путей притеснителя.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22365, 'rst66', 'psa', 16, 5, 'Утверди шаги мои на путях Твоих, да не колеблются стопы мои.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22366, 'rst66', 'psa', 16, 6, 'К Тебе взываю я, ибо Ты услышишь меня, Боже; приклони ухо Твое ко мне, услышь слова мои.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22367, 'rst66', 'psa', 16, 7, 'Яви дивную милость Твою, Спаситель уповающих <i>на Тебя</i> от противящихся деснице Твоей.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22368, 'rst66', 'psa', 16, 8, 'Храни меня, как зеницу ока; в тени крыл Твоих укрой меня');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22369, 'rst66', 'psa', 16, 9, 'от лица нечестивых, нападающих на меня, &mdash; от врагов души моей, окружающих меня:');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22370, 'rst66', 'psa', 16, 10, 'они заключились в туке своем, надменно говорят устами своими.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22371, 'rst66', 'psa', 16, 11, 'На всяком шагу нашем ныне окружают нас; они устремили глаза свои, чтобы низложить <i>меня</i> на землю;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22372, 'rst66', 'psa', 16, 12, 'они подобны льву, жаждущему добычи, подобны скимну, сидящему в местах скрытных.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22373, 'rst66', 'psa', 16, 13, 'Восстань, Господи, предупреди их, низложи их. Избавь душу мою от нечестивого мечом Твоим,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22374, 'rst66', 'psa', 16, 14, 'от людей &mdash; рукою Твоею, Господи, от людей мира, которых удел в <i>этой</i> жизни, которых чрево Ты наполняешь из сокровищниц Твоих; сыновья их сыты и оставят остаток детям своим.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22375, 'rst66', 'psa', 16, 15, 'А я в правде буду взирать на лицо Твое; пробудившись, буду насыщаться образом Твоим.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22376, 'rst66', 'psa', 17, 1, 'Начальнику хора. Раба Господня Давида, который произнес слова песни сей к Господу, когда Господь избавил его от рук всех врагов его и от руки Саула. И он сказал:');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22377, 'rst66', 'psa', 17, 2, 'Возлюблю тебя, Господи, крепость моя!');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22378, 'rst66', 'psa', 17, 3, 'Господь &mdash; твердыня моя и прибежище мое, Избавитель мой, Бог мой &mdash; скала моя; на Него я уповаю; щит мой, рог спасения моего и убежище мое.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22379, 'rst66', 'psa', 17, 4, 'Призову достопоклоняемого Господа и от врагов моих спасусь.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22380, 'rst66', 'psa', 17, 5, 'Объяли меня муки смертные, и потоки беззакония устрашили меня;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22381, 'rst66', 'psa', 17, 6, 'цепи ада облегли меня, и сети смерти опутали меня.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22382, 'rst66', 'psa', 17, 7, 'В тесноте моей я призвал Господа и к Богу моему воззвал. И Он услышал от чертога Своего голос мой, и вопль мой дошел до слуха Его.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22383, 'rst66', 'psa', 17, 8, 'Потряслась и всколебалась земля, дрогнули и подвиглись основания гор, ибо разгневался <i>Бог</i>;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22384, 'rst66', 'psa', 17, 9, 'поднялся дым от гнева Его и из уст Его огонь поядающий; горячие угли <i>сыпались</i> от Него.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22385, 'rst66', 'psa', 17, 10, 'Наклонил Он небеса и сошел, &mdash; и мрак под ногами Его.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22386, 'rst66', 'psa', 17, 11, 'И воссел на херувимов и полетел, и понесся на крыльях ветра.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22387, 'rst66', 'psa', 17, 12, 'И мрак сделал покровом Своим, сению вокруг Себя мрак вод, облаков воздушных.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22388, 'rst66', 'psa', 17, 13, 'От блистания пред Ним бежали облака Его, град и угли огненные.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22389, 'rst66', 'psa', 17, 14, 'Возгремел на небесах Господь, и Всевышний дал глас Свой, град и угли огненные.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22390, 'rst66', 'psa', 17, 15, 'Пустил стрелы Свои, и рассеял их, множество молний, и рассыпал их.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22391, 'rst66', 'psa', 17, 16, 'И явились источники вод, и открылись основания вселенной от грозного <i>гласа</i> Твоего, Господи, от дуновения духа гнева Твоего.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22392, 'rst66', 'psa', 17, 17, 'Он простер <i>руку</i> с высоты и взял меня, и извлек меня из вод многих;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22393, 'rst66', 'psa', 17, 18, 'избавил меня от врага моего сильного и от ненавидящих меня, которые были сильнее меня.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22394, 'rst66', 'psa', 17, 19, 'Они восстали на меня в день бедствия моего, но Господь был мне опорою.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22395, 'rst66', 'psa', 17, 20, 'Он вывел меня на пространное место и избавил меня, ибо Он благоволит ко мне.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22396, 'rst66', 'psa', 17, 21, 'Воздал мне Господь по правде моей, по чистоте рук моих вознаградил меня,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22397, 'rst66', 'psa', 17, 22, 'ибо я хранил пути Господни и не был нечестивым пред Богом моим;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22398, 'rst66', 'psa', 17, 23, 'ибо все заповеди Его предо мною, и от уставов Его я не отступал.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22399, 'rst66', 'psa', 17, 24, 'Я был непорочен пред Ним и остерегался, чтобы не согрешить мне;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22400, 'rst66', 'psa', 17, 25, 'и воздал мне Господь по правде моей, по чистоте рук моих пред очами Его.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22401, 'rst66', 'psa', 17, 26, 'С милостивым Ты поступаешь милостиво, с мужем искренним &mdash; искренно,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22402, 'rst66', 'psa', 17, 27, 'с чистым &mdash; чисто, а с лукавым &mdash; по лукавству его,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22403, 'rst66', 'psa', 17, 28, 'ибо Ты людей угнетенных спасаешь, а очи надменные унижаешь.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22404, 'rst66', 'psa', 17, 29, 'Ты возжигаешь светильник мой, Господи; Бог мой просвещает тьму мою.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22405, 'rst66', 'psa', 17, 30, 'С Тобою я поражаю войско, с Богом моим восхожу на стену.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22406, 'rst66', 'psa', 17, 31, 'Бог! &mdash; Непорочен путь Его, чисто слово Господа; щит Он для всех, уповающих на Него.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22407, 'rst66', 'psa', 17, 32, 'Ибо кто Бог, кроме Господа, и кто защита, кроме Бога нашего?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22408, 'rst66', 'psa', 17, 33, 'Бог препоясывает меня силою и устрояет мне верный путь;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22409, 'rst66', 'psa', 17, 34, 'делает ноги мои, как оленьи, и на высотах моих поставляет меня;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22410, 'rst66', 'psa', 17, 35, 'научает руки мои брани, и мышцы мои сокрушают медный лук.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22411, 'rst66', 'psa', 17, 36, 'Ты дал мне щит спасения Твоего, и десница Твоя поддерживает меня, и милость Твоя возвеличивает меня.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22412, 'rst66', 'psa', 17, 37, 'Ты расширяешь шаг мой подо мною, и не колеблются ноги мои.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22413, 'rst66', 'psa', 17, 38, 'Я преследую врагов моих и настигаю их, и не возвращаюсь, доколе не истреблю их;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22414, 'rst66', 'psa', 17, 39, 'поражаю их, и они не могут встать, падают под ноги мои,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22415, 'rst66', 'psa', 17, 40, 'ибо Ты препоясал меня силою для войны и низложил под ноги мои восставших на меня;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22416, 'rst66', 'psa', 17, 41, 'Ты обратил ко мне тыл врагов моих, и я истребляю ненавидящих меня:');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22417, 'rst66', 'psa', 17, 42, 'они вопиют, но нет спасающего; ко Господу, &mdash; но Он не внемлет им;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22418, 'rst66', 'psa', 17, 43, 'я рассеваю их, как прах пред лицом ветра, как уличную грязь попираю их.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22419, 'rst66', 'psa', 17, 44, 'Ты избавил меня от мятежа народа, поставил меня главою иноплеменников; народ, которого я не знал, служит мне;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22420, 'rst66', 'psa', 17, 45, 'по одному слуху о мне повинуются мне; иноплеменники ласкательствуют предо мною;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22421, 'rst66', 'psa', 17, 46, 'иноплеменники бледнеют и трепещут в укреплениях своих.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22422, 'rst66', 'psa', 17, 47, 'Жив Господь и благословен защитник мой! Да будет превознесен Бог спасения моего,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22423, 'rst66', 'psa', 17, 48, 'Бог, мстящий за меня, и покоряющий мне народы,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22424, 'rst66', 'psa', 17, 49, 'и избавляющий меня от врагов моих! Ты вознес меня над восстающими против меня и от человека жестокого избавил меня.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22425, 'rst66', 'psa', 17, 50, 'За то буду славить Тебя, Господи, между иноплеменниками и буду петь имени Твоему,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22426, 'rst66', 'psa', 17, 51, 'величественно спасающий царя и творящий милость помазаннику Твоему Давиду и потомству его вовеки.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22427, 'rst66', 'psa', 18, 1, 'Начальнику хора. Псалом Давида.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22428, 'rst66', 'psa', 18, 2, 'Небеса проповедуют славу Божию, и о делах рук Его вещает твердь.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22429, 'rst66', 'psa', 18, 3, 'День дню передает речь, и ночь ночи открывает знание.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22430, 'rst66', 'psa', 18, 4, 'Нет языка, и нет наречия, где не слышался бы голос их.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22431, 'rst66', 'psa', 18, 5, 'По всей земле проходит звук их, и до пределов вселенной слова их. Он поставил в них жилище солнцу,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22432, 'rst66', 'psa', 18, 6, 'и оно выходит, как жених из брачного чертога своего, радуется, как исполин, пробежать поприще.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22433, 'rst66', 'psa', 18, 7, 'От края небес исход его, и шествие его до края их, и ничто не укрыто от теплоты его.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22434, 'rst66', 'psa', 18, 8, 'Закон Господа совершен, укрепляет душу; откровение Господа верно, умудряет простых.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22435, 'rst66', 'psa', 18, 9, 'Повеления Господа праведны, веселят сердце; заповедь Господа светла, просвещает очи.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22436, 'rst66', 'psa', 18, 10, 'Страх Господень чист, пребывает вовек. Суды Господни &mdash; истина, все праведны;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22437, 'rst66', 'psa', 18, 11, 'они вожделеннее золота и даже множества золота чистого, слаще меда и капель сота;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22438, 'rst66', 'psa', 18, 12, 'и раб Твой охраняется ими, в соблюдении их великая награда.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22439, 'rst66', 'psa', 18, 13, 'Кто усмотрит погрешности свои? От тайных <i>моих</i> очисти меня');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22440, 'rst66', 'psa', 18, 14, 'и от умышленных удержи раба Твоего, чтобы не возобладали мною. Тогда я буду непорочен и чист от великого развращения.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22441, 'rst66', 'psa', 18, 15, 'Да будут слова уст моих и помышление сердца моего благоугодны пред Тобою, Господи, твердыня моя и Избавитель мой!');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22442, 'rst66', 'psa', 19, 1, 'Начальнику хора. Псалом Давида.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22443, 'rst66', 'psa', 19, 2, 'Да услышит тебя Господь в день печали, да защитит тебя имя Бога Иаковлева.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22444, 'rst66', 'psa', 19, 3, 'Да пошлет тебе помощь из Святилища и с Сиона да подкрепит тебя.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22445, 'rst66', 'psa', 19, 4, 'Да воспомянет все жертвоприношения твои и всесожжение твое да соделает тучным.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22446, 'rst66', 'psa', 19, 5, 'Да даст тебе по сердцу твоему и все намерения твои да исполнит.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22447, 'rst66', 'psa', 19, 6, 'Мы возрадуемся о спасении твоем и во имя Бога нашего поднимем знамя. Да исполнит Господь все прошения твои.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22448, 'rst66', 'psa', 19, 7, 'Ныне познал я, что Господь спасает помазанника Своего, отвечает ему со святых небес Своих могуществом спасающей десницы Своей.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22449, 'rst66', 'psa', 19, 8, 'Иные колесницами, иные конями, а мы именем Господа Бога нашего хвалимся.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22450, 'rst66', 'psa', 19, 9, 'Они поколебались и пали, а мы встали и стоим прямо.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22451, 'rst66', 'psa', 19, 10, 'Господи! спаси царя и услышь нас, когда будем взывать <i>к Тебе</i>.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22452, 'rst66', 'psa', 20, 1, 'Начальнику хора. Псалом Давида.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22453, 'rst66', 'psa', 20, 2, 'Господи! силою Твоею веселится царь и о спасении Твоем безмерно радуется.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22454, 'rst66', 'psa', 20, 3, 'Ты дал ему, чего желало сердце его, и прошения уст его не отринул,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22455, 'rst66', 'psa', 20, 4, 'ибо Ты встретил его благословениями благости, возложил на голову его венец из чистого золота.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22456, 'rst66', 'psa', 20, 5, 'Он просил у Тебя жизни; Ты дал ему долгоденствие на век и век.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22457, 'rst66', 'psa', 20, 6, 'Велика слава его в спасении Твоем; Ты возложил на него честь и величие.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22458, 'rst66', 'psa', 20, 7, 'Ты положил на него благословения навеки, возвеселил его радостью лица Твоего,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22459, 'rst66', 'psa', 20, 8, 'ибо царь уповает на Господа, и во благости Всевышнего не поколеблется.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22460, 'rst66', 'psa', 20, 9, 'Рука Твоя найдет всех врагов Твоих, десница Твоя найдет ненавидящих Тебя.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22461, 'rst66', 'psa', 20, 10, 'Во время гнева Твоего Ты сделаешь их, как печь огненную; во гневе Своем Господь погубит их, и пожрет их огонь.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22462, 'rst66', 'psa', 20, 11, 'Ты истребишь плод их с земли и семя их &mdash; из среды сынов человеческих,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22463, 'rst66', 'psa', 20, 12, 'ибо они предприняли против Тебя злое, составили замыслы, но не могли <i>выполнить их</i>.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22464, 'rst66', 'psa', 20, 13, 'Ты поставишь их целью, из луков Твоих пустишь стрелы в лицо их.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22465, 'rst66', 'psa', 20, 14, 'Вознесись, Господи, силою Твоею: мы будем воспевать и прославлять Твое могущество.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22466, 'rst66', 'psa', 21, 1, 'Начальнику хора. При появлении зари. Псалом Давида.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22467, 'rst66', 'psa', 21, 2, 'Боже мой! Боже мой! для чего Ты оставил меня? Далеки от спасения моего слова&#769; вопля моего.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22468, 'rst66', 'psa', 21, 3, 'Боже мой! я вопию днем, &mdash; и Ты не внемлешь мне, ночью, &mdash; и нет мне успокоения.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22469, 'rst66', 'psa', 21, 4, 'Но Ты, Святой, живешь среди славословий Израиля.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22470, 'rst66', 'psa', 21, 5, 'На Тебя уповали отцы наши; уповали, и Ты избавлял их;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22471, 'rst66', 'psa', 21, 6, 'к Тебе взывали они, и были спасаемы; на Тебя уповали, и не оставались в стыде.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22472, 'rst66', 'psa', 21, 7, 'Я же червь, а не человек, поношение у людей и презрение в народе.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22473, 'rst66', 'psa', 21, 8, 'Все, видящие меня, ругаются надо мною, говорят устами, кивая головою:');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22474, 'rst66', 'psa', 21, 9, '«Он уповал на Господа; пусть избавит его, пусть спасет, если он угоден Ему».');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22475, 'rst66', 'psa', 21, 10, 'Но Ты извел меня из чрева, вложил в меня упование у грудей матери моей.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22476, 'rst66', 'psa', 21, 11, 'На Тебя оставлен я от утробы; от чрева матери моей Ты &mdash; Бог мой.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22477, 'rst66', 'psa', 21, 12, 'Не удаляйся от меня, ибо скорбь близка, а помощника нет.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22478, 'rst66', 'psa', 21, 13, 'Множество тельцов обступили меня; тучные Васанские окружили меня,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22479, 'rst66', 'psa', 21, 14, 'раскрыли на меня пасть свою, <i>как</i> лев, алчущий добычи и рыкающий.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22480, 'rst66', 'psa', 21, 15, 'Я пролился, как вода; все кости мои рассыпались; сердце мое сделалось как воск, растаяло посреди внутренности моей.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22481, 'rst66', 'psa', 21, 16, 'Сила моя иссохла, как черепок; язык мой прильпнул к гортани моей, и Ты свел меня к персти смертной.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22482, 'rst66', 'psa', 21, 17, 'Ибо псы окружили меня, скопище злых обступило меня, пронзили руки мои и ноги мои.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22483, 'rst66', 'psa', 21, 18, 'Можно было бы перечесть все кости мои; а они смотрят и делают из меня зрелище;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22484, 'rst66', 'psa', 21, 19, 'делят ризы мои между собою и об одежде моей бросают жребий.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22485, 'rst66', 'psa', 21, 20, 'Но Ты, Господи, не удаляйся от меня; сила моя! поспеши на помощь мне;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22486, 'rst66', 'psa', 21, 21, 'избавь от меча душу мою и от псов одинокую мою;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22487, 'rst66', 'psa', 21, 22, 'спаси меня от пасти льва и от рогов единорогов, услышав, <i>избавь</i> меня.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22488, 'rst66', 'psa', 21, 23, 'Буду возвещать имя Твое братьям моим, посреди собрания восхвалять Тебя.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22489, 'rst66', 'psa', 21, 24, 'Боящиеся Господа! восхвалите Его. Всё семя Иакова! прославь Его. Да благоговеет пред Ним всё семя Израиля,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22490, 'rst66', 'psa', 21, 25, 'ибо Он не презрел и не пренебрег скорби страждущего, не скрыл от него лица Своего, но услышал его, когда сей воззвал к Нему.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22491, 'rst66', 'psa', 21, 26, 'О Тебе хвала моя в собрании великом; воздам обеты мои пред боящимися Его.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22492, 'rst66', 'psa', 21, 27, 'Да едят бедные и насыщаются; да восхвалят Господа ищущие Его; да живут сердца ваши вовеки!');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22493, 'rst66', 'psa', 21, 28, 'Вспомнят, и обратятся к Господу все концы земли, и поклонятся пред Тобою все племена язычников,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22494, 'rst66', 'psa', 21, 29, 'ибо Господне есть царство, и Он &mdash; Владыка над народами.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22495, 'rst66', 'psa', 21, 30, 'Будут есть и поклоняться все тучные земли; преклонятся пред Ним все нисходящие в персть и не могущие сохранить жизни своей.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22496, 'rst66', 'psa', 21, 31, 'Потомство <i>мое</i> будет служить Ему, и будет называться Господним вовек:');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22497, 'rst66', 'psa', 21, 32, 'придут и будут возвещать правду Его людям, которые родятся, что сотворил Господь.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22498, 'rst66', 'psa', 22, 0, 'Псалом Давида.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22499, 'rst66', 'psa', 22, 1, 'Господь &mdash; Пастырь мой; я ни в чем не буду нуждаться:');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22500, 'rst66', 'psa', 22, 2, 'Он покоит меня на злачных пажитях и водит меня к водам тихим,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22501, 'rst66', 'psa', 22, 3, 'подкрепляет душу мою, направляет меня на стези правды ради имени Своего.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22502, 'rst66', 'psa', 22, 4, 'Если я пойду и долиною смертной тени, не убоюсь зла, потому что Ты со мной; Твой жезл и Твой посох &mdash; они успокаивают меня.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22503, 'rst66', 'psa', 22, 5, 'Ты приготовил предо мною трапезу в виду врагов моих; умастил елеем голову мою; чаша моя преисполнена.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22504, 'rst66', 'psa', 22, 6, 'Так, благость и милость да сопровождают меня во все дни жизни моей, и я пребуду в доме Господнем многие дни.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22505, 'rst66', 'psa', 23, 0, 'Псалом Давида.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22506, 'rst66', 'psa', 23, 1, 'Господня &mdash; земля и что наполняет ее, вселенная и все живущее в ней,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22507, 'rst66', 'psa', 23, 2, 'ибо Он основал ее на морях и на реках утвердил ее.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22508, 'rst66', 'psa', 23, 3, 'Кто взойдет на гору Господню, или кто станет на святом месте Его?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22509, 'rst66', 'psa', 23, 4, 'Тот, у которого руки неповинны и сердце чисто, кто не клялся душою своею напрасно и не божился ложно, &mdash;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22510, 'rst66', 'psa', 23, 5, '<i>тот</i> получит благословение от Господа и милость от Бога, Спасителя своего.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22511, 'rst66', 'psa', 23, 6, 'Таков род ищущих Его, ищущих лица Твоего, Боже Иакова!');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22512, 'rst66', 'psa', 23, 7, 'Поднимите, врата, верхи ваши, и поднимитесь, двери вечные, и войдет Царь славы!');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22513, 'rst66', 'psa', 23, 8, 'Кто сей Царь славы? &mdash; Господь крепкий и сильный, Господь, сильный в брани.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22514, 'rst66', 'psa', 23, 9, 'Поднимите, врата, верхи ваши, и поднимитесь, двери вечные, и войдет Царь славы!');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22515, 'rst66', 'psa', 23, 10, 'Кто сей Царь славы? &mdash; Господь сил, Он &mdash; Царь славы.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22516, 'rst66', 'psa', 24, 0, 'Псалом Давида.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22517, 'rst66', 'psa', 24, 1, 'К Тебе, Господи, возношу душу мою.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22518, 'rst66', 'psa', 24, 2, 'Боже мой! на Тебя уповаю, да не постыжусь, да не восторжествуют надо мною враги мои,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22519, 'rst66', 'psa', 24, 3, 'да не постыдятся и все надеющиеся на Тебя; да постыдятся беззаконнующие втуне.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22520, 'rst66', 'psa', 24, 4, 'Укажи мне, Господи, пути Твои и научи меня стезям Твоим.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22521, 'rst66', 'psa', 24, 5, 'Направь меня на истину Твою и научи меня, ибо Ты Бог спасения моего; на Тебя надеюсь всякий день.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22522, 'rst66', 'psa', 24, 6, 'Вспомни щедроты Твои, Господи, и милости Твои, ибо они от века.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22523, 'rst66', 'psa', 24, 7, 'Грехов юности моей и преступлений моих не вспоминай; по милости Твоей вспомни меня Ты, ради благости Твоей, Господи!');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22524, 'rst66', 'psa', 24, 8, 'Благ и праведен Господь, посему наставляет грешников на путь,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22525, 'rst66', 'psa', 24, 9, 'направляет кротких к правде и научает кротких путям Своим.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22526, 'rst66', 'psa', 24, 10, 'Все пути Господни &mdash; милость и истина к хранящим завет Его и откровения Его.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22527, 'rst66', 'psa', 24, 11, 'Ради имени Твоего, Господи, прости согрешение мое, ибо велико оно.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22528, 'rst66', 'psa', 24, 12, 'Кто есть человек, боящийся Господа? Ему укажет Он путь, который избрать.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22529, 'rst66', 'psa', 24, 13, 'Душа его пребудет во благе, и семя его наследует землю.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22530, 'rst66', 'psa', 24, 14, 'Тайна Господня &mdash; боящимся Его, и завет Свой Он открывает им.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22531, 'rst66', 'psa', 24, 15, 'Очи мои всегда к Господу, ибо Он извлекает из сети ноги мои.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22532, 'rst66', 'psa', 24, 16, 'Призри на меня и помилуй меня, ибо я одинок и угнетен.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22533, 'rst66', 'psa', 24, 17, 'Скорби сердца моего умножились; выведи меня из бед моих,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22534, 'rst66', 'psa', 24, 18, 'призри на страдание мое и на изнеможение мое и прости все грехи мои.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22535, 'rst66', 'psa', 24, 19, 'Посмотри на врагов моих, как много их, и <i>какою</i> лютою ненавистью они ненавидят меня.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22536, 'rst66', 'psa', 24, 20, 'Сохрани душу мою и избавь меня, да не постыжусь, что я на Тебя уповаю.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22537, 'rst66', 'psa', 24, 21, 'Непорочность и правота да охраняют меня, ибо я на Тебя надеюсь.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22538, 'rst66', 'psa', 24, 22, 'Избавь, Боже, Израиля от всех скорбей его.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22539, 'rst66', 'psa', 25, 0, 'Псалом Давида.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22540, 'rst66', 'psa', 25, 1, 'Рассуди меня, Господи, ибо я ходил в непорочности моей, и, уповая на Господа, не поколеблюсь.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22541, 'rst66', 'psa', 25, 2, 'Искуси меня, Господи, и испытай меня; расплавь внутренности мои и сердце мое,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22542, 'rst66', 'psa', 25, 3, 'ибо милость Твоя пред моими очами, и я ходил в истине Твоей,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22543, 'rst66', 'psa', 25, 4, 'не сидел я с людьми лживыми, и с коварными не пойду;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22544, 'rst66', 'psa', 25, 5, 'возненавидел я сборище злонамеренных, и с нечестивыми не сяду;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22545, 'rst66', 'psa', 25, 6, 'буду омывать в невинности руки мои и обходить жертвенник Твой, Господи,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22546, 'rst66', 'psa', 25, 7, 'чтобы возвещать гласом хвалы и поведать все чудеса Твои.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22547, 'rst66', 'psa', 25, 8, 'Господи! возлюбил я обитель дома Твоего и место жилища славы Твоей.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22548, 'rst66', 'psa', 25, 9, 'Не погуби души моей с грешниками и жизни моей с кровожадными,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22549, 'rst66', 'psa', 25, 10, 'у которых в руках злодейство и которых правая рука полна мздоимства.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22550, 'rst66', 'psa', 25, 11, 'А я хожу в моей непорочности; избавь меня, и помилуй меня.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22551, 'rst66', 'psa', 25, 12, 'Моя нога стоит на прямом <i>пути;</i> в собраниях благословлю Господа.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22552, 'rst66', 'psa', 26, 0, 'Псалом Давида.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22553, 'rst66', 'psa', 26, 1, 'Господь &mdash; свет мой и спасение мое: кого мне бояться? Господь &mdash; крепость жизни моей: кого мне страшиться?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22554, 'rst66', 'psa', 26, 2, 'Если будут наступать на меня злодеи, противники и враги мои, чтобы пожрать плоть мою, то они сами преткнутся и падут.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22555, 'rst66', 'psa', 26, 3, 'Если ополчится против меня полк, не убоится сердце мое; если восстанет на меня война, и тогда буду надеяться.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22556, 'rst66', 'psa', 26, 4, 'Одного просил я у Господа, того только ищу, чтобы пребывать мне в доме Господнем во все дни жизни моей, созерцать красоту Господню и посещать храм Его,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22557, 'rst66', 'psa', 26, 5, 'ибо Он укрыл бы меня в скинии Своей в день бедствия, скрыл бы меня в потаенном месте селения Своего, вознес бы меня на скалу.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22558, 'rst66', 'psa', 26, 6, 'Тогда вознеслась бы голова моя над врагами, окружающими меня; и я принес бы в Его скинии жертвы славословия, стал бы петь и воспевать пред Господом.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22559, 'rst66', 'psa', 26, 7, 'Услышь, Господи, голос мой, которым я взываю, помилуй меня и внемли мне.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22560, 'rst66', 'psa', 26, 8, 'Сердце мое говорит от Тебя: «ищите лица Моего»; и я буду искать лица Твоего, Господи.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22561, 'rst66', 'psa', 26, 9, 'Не скрой от меня лица Твоего; не отринь во гневе раба Твоего. Ты был помощником моим; не отвергни меня и не оставь меня, Боже, Спаситель мой!');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22562, 'rst66', 'psa', 26, 10, 'Ибо отец мой и мать моя оставили меня, но Господь примет меня.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22563, 'rst66', 'psa', 26, 11, 'Научи меня, Господи, пути Твоему и наставь меня на стезю правды, ради врагов моих;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22564, 'rst66', 'psa', 26, 12, 'не предавай меня на произвол врагам моим, ибо восстали на меня свидетели лживые и дышат злобою.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22565, 'rst66', 'psa', 26, 13, 'Но я верую, что увижу благость Господа на земле живых.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22566, 'rst66', 'psa', 26, 14, 'Надейся на Господа, мужайся, и да укрепляется сердце твое, и надейся на Господа.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22567, 'rst66', 'psa', 27, 0, 'Псалом Давида.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22568, 'rst66', 'psa', 27, 1, 'К тебе, Господи, взываю: твердыня моя! не будь безмолвен для меня, чтобы при безмолвии Твоем я не уподобился нисходящим в могилу.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22569, 'rst66', 'psa', 27, 2, 'Услышь голос молений моих, когда я взываю к Тебе, когда поднимаю руки мои к святому храму Твоему.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22570, 'rst66', 'psa', 27, 3, 'Не погуби меня с нечестивыми и с делающими неправду, которые с ближними своими говорят о мире, а в сердце у них зло.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22571, 'rst66', 'psa', 27, 4, 'Воздай им по делам их, по злым поступкам их; по делам рук их воздай им, отдай им заслуженное ими.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22572, 'rst66', 'psa', 27, 5, 'За то, что они невнимательны к действиям Господа и к делу рук Его, Он разрушит их и не созиждет их.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22573, 'rst66', 'psa', 27, 6, 'Благословен Господь, ибо Он услышал голос молений моих.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22574, 'rst66', 'psa', 27, 7, 'Господь &mdash; крепость моя и щит мой; на Него уповало сердце мое, и Он помог мне, и возрадовалось сердце мое; и я прославлю Его песнью моею.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22575, 'rst66', 'psa', 27, 8, 'Господь &mdash; крепость народа Своего и спасительная защита помазанника Своего.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22576, 'rst66', 'psa', 27, 9, 'Спаси народ Твой и благослови наследие Твое; паси их и возвышай их вовеки!');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22577, 'rst66', 'psa', 28, 0, 'Псалом Давида.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22578, 'rst66', 'psa', 28, 1, 'Воздайте Господу, сыны Божии, воздайте Господу славу и честь,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22579, 'rst66', 'psa', 28, 2, 'воздайте Господу славу имени Его; поклонитесь Господу в благолепном святилище <i>Его</i>.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22580, 'rst66', 'psa', 28, 3, 'Глас Господень над водами; Бог славы возгремел, Господь над водами многими.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22581, 'rst66', 'psa', 28, 4, 'Глас Господа силен, глас Господа величествен.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22582, 'rst66', 'psa', 28, 5, 'Глас Господа сокрушает кедры; Господь сокрушает кедры Ливанские');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22583, 'rst66', 'psa', 28, 6, 'и заставляет их скакать подобно тельцу, Ливан и Сирион, подобно молодому единорогу.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22584, 'rst66', 'psa', 28, 7, 'Глас Господа высекает пламень огня.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22585, 'rst66', 'psa', 28, 8, 'Глас Господа потрясает пустыню; потрясает Господь пустыню Кадес.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22586, 'rst66', 'psa', 28, 9, 'Глас Господа разрешает от бремени ланей и обнажает леса; и во храме Его все возвещает о <i>Его</i> славе.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22587, 'rst66', 'psa', 28, 10, 'Господь восседал над потопом, и будет восседать Господь царем вовек.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22588, 'rst66', 'psa', 28, 11, 'Господь даст силу народу Своему, Господь благословит народ Свой миром.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22589, 'rst66', 'psa', 29, 1, 'Псалом Давида; песнь при обновлении дома.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22590, 'rst66', 'psa', 29, 2, 'Превознесу Тебя, Господи, что Ты поднял меня и не дал моим врагам восторжествовать надо мною.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22591, 'rst66', 'psa', 29, 3, 'Господи, Боже мой! я воззвал к Тебе, и Ты исцелил меня.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22592, 'rst66', 'psa', 29, 4, 'Господи! Ты вывел из ада душу мою и оживил меня, чтобы я не сошел в могилу.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22593, 'rst66', 'psa', 29, 5, 'Пойте Господу, святые Его, славьте память святыни Его,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22594, 'rst66', 'psa', 29, 6, 'ибо на мгновение гнев Его, на <i>всю</i> жизнь благоволение Его: вечером водворяется плач, а наутро радость.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22595, 'rst66', 'psa', 29, 7, 'И я говорил в благоденствии моем: «не поколеблюсь вовек».');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22596, 'rst66', 'psa', 29, 8, 'По благоволению Твоему, Господи, Ты укрепил гору мою; но Ты сокрыл лицо Твое, <i>и</i> я смутился.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22597, 'rst66', 'psa', 29, 9, '<i>Тогда</i> к Тебе, Господи, взывал я, и Господа умолял:');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22598, 'rst66', 'psa', 29, 10, '«Что пользы в крови моей, когда я сойду в могилу? будет ли прах славить Тебя? будет ли возвещать истину Твою?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22599, 'rst66', 'psa', 29, 11, 'Услышь, Господи, и помилуй меня; Господи! будь мне помощником».');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22600, 'rst66', 'psa', 29, 12, 'И Ты обратил сетование мое в ликование, снял с меня вретище и препоясал меня веселием,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22601, 'rst66', 'psa', 29, 13, 'да славит Тебя душа моя и да не умолкает. Господи, Боже мой! буду славить Тебя вечно.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22602, 'rst66', 'psa', 30, 1, 'Начальнику хора. Псалом Давида.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22603, 'rst66', 'psa', 30, 2, 'На Тебя, Господи, уповаю, да не постыжусь вовек; по правде Твоей избавь меня;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22604, 'rst66', 'psa', 30, 3, 'приклони ко мне ухо Твое, поспеши избавить меня. Будь мне каменною твердынею, домом прибежища, чтобы спасти меня,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22605, 'rst66', 'psa', 30, 4, 'ибо Ты &mdash; каменная гора моя и ограда моя; ради имени Твоего води меня и управляй мною.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22606, 'rst66', 'psa', 30, 5, 'Выведи меня из сети, которую тайно поставили мне, ибо Ты &mdash; крепость моя.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22607, 'rst66', 'psa', 30, 6, 'В Твою руку предаю дух мой; Ты избавлял меня, Господи, Боже истины.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22608, 'rst66', 'psa', 30, 7, 'Ненавижу почитателей суетных идолов, но на Господа уповаю.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22609, 'rst66', 'psa', 30, 8, 'Буду радоваться и веселиться о милости Твоей, потому что Ты призрел на бедствие мое, узнал горесть души моей');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22610, 'rst66', 'psa', 30, 9, 'и не предал меня в руки врага; поставил ноги мои на пространном месте.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22611, 'rst66', 'psa', 30, 10, 'Помилуй меня, Господи, ибо тесно мне; иссохло от горести око мое, душа моя и утроба моя.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22612, 'rst66', 'psa', 30, 11, 'Истощилась в печали жизнь моя и лета мои в стенаниях; изнемогла от грехов моих сила моя, и кости мои иссохли.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22613, 'rst66', 'psa', 30, 12, 'От всех врагов моих я сделался поношением даже у соседей моих и страшилищем для знакомых моих; видящие меня на улице бегут от меня.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22614, 'rst66', 'psa', 30, 13, 'Я забыт в сердцах, как мертвый; я &mdash; как сосуд разбитый,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22615, 'rst66', 'psa', 30, 14, 'ибо слышу злоречие многих; отвсюду ужас, когда они сговариваются против меня, умышляют исторгнуть душу мою.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22616, 'rst66', 'psa', 30, 15, 'А я на Тебя, Господи, уповаю; я говорю: Ты &mdash; мой Бог.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22617, 'rst66', 'psa', 30, 16, 'В Твоей руке дни мои; избавь меня от руки врагов моих и от гонителей моих.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22618, 'rst66', 'psa', 30, 17, 'Яви светлое лицо Твое рабу Твоему; спаси меня милостью Твоею.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22619, 'rst66', 'psa', 30, 18, 'Господи! да не постыжусь, что я к Тебе взываю; нечестивые же да посрамятся, да умолкнут в аде.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22620, 'rst66', 'psa', 30, 19, 'Да онемеют уста лживые, которые против праведника говорят злое с гордостью и презрением.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22621, 'rst66', 'psa', 30, 20, 'Как много у Тебя благ, которые Ты хранишь для боящихся Тебя и которые приготовил уповающим на Тебя пред сынами человеческими!');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22622, 'rst66', 'psa', 30, 21, 'Ты укрываешь их под покровом лица Твоего от мятежей людских, скрываешь их под сенью от пререкания языков.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22623, 'rst66', 'psa', 30, 22, 'Благословен Господь, что явил мне дивную милость Свою в укрепленном городе!');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22624, 'rst66', 'psa', 30, 23, 'В смятении моем я думал: «отвержен я от очей Твоих»; но Ты услышал голос молитвы моей, когда я воззвал к Тебе.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22625, 'rst66', 'psa', 30, 24, 'Любите Господа, все праведные Его; Господь хранит верных и поступающим надменно воздает с избытком.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22626, 'rst66', 'psa', 30, 25, 'Мужайтесь, и да укрепляется сердце ваше, все надеющиеся на Господа!');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22627, 'rst66', 'psa', 31, 0, 'Псалом Давида. Учение.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22628, 'rst66', 'psa', 31, 1, 'Блажен, кому отпущены беззакония, и чьи грехи покрыты!');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22629, 'rst66', 'psa', 31, 2, 'Блажен человек, которому Господь не вменит греха, и в чьем духе нет лукавства!');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22630, 'rst66', 'psa', 31, 3, 'Когда я молчал, обветшали кости мои от вседневного стенания моего,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22631, 'rst66', 'psa', 31, 4, 'ибо день и ночь тяготела надо мною рука Твоя; свежесть моя исчезла, как в летнюю засуху.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22632, 'rst66', 'psa', 31, 5, 'Но я открыл Тебе грех мой и не скрыл беззакония моего; я сказал: «исповедаю Господу преступления мои», и Ты снял с меня вину греха моего.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22633, 'rst66', 'psa', 31, 6, 'За то помолится Тебе каждый праведник во время благопотребное, и тогда разлитие многих вод не достигнет его.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22634, 'rst66', 'psa', 31, 7, 'Ты покров мой: Ты охраняешь меня от скорби, окружаешь меня радостями избавления.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22635, 'rst66', 'psa', 31, 8, '«Вразумлю тебя, наставлю тебя на путь, по которому тебе идти; буду руководить тебя, око Мое над тобою».');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22636, 'rst66', 'psa', 31, 9, '«Не будьте как конь, как лошак несмысленный, которых челюсти нужно обуздывать уздою и удилами, чтобы они покорялись тебе».');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22637, 'rst66', 'psa', 31, 10, 'Много скорбей нечестивому, а уповающего на Господа окружает милость.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22638, 'rst66', 'psa', 31, 11, 'Веселитесь о Господе и радуйтесь, праведные; торжествуйте, все правые сердцем.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22639, 'rst66', 'psa', 32, 1, 'Радуйтесь, праведные, о Господе: правым прилично славословить.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22640, 'rst66', 'psa', 32, 2, 'Славьте Господа на гуслях, пойте Ему на десятиструнной псалтири;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22641, 'rst66', 'psa', 32, 3, 'пойте Ему новую песнь; пойте Ему стройно, с восклицанием,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22642, 'rst66', 'psa', 32, 4, 'ибо слово Господне право и все дела Его верны.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22643, 'rst66', 'psa', 32, 5, 'Он любит правду и суд; милости Господней полна земля.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22644, 'rst66', 'psa', 32, 6, 'Словом Господа сотворены небеса, и духом уст Его &mdash; все воинство их:');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22645, 'rst66', 'psa', 32, 7, 'Он собрал, будто груды, морские воды, положил бездны в хранилищах.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22646, 'rst66', 'psa', 32, 8, 'Да боится Господа вся земля; да трепещут пред Ним все, живущие во вселенной,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22647, 'rst66', 'psa', 32, 9, 'ибо Он сказал, &mdash; и сделалось; Он повелел, &mdash; и явилось.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22648, 'rst66', 'psa', 32, 10, 'Господь разрушает советы язычников, уничтожает замыслы народов.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22649, 'rst66', 'psa', 32, 11, 'Совет же Господень стоит вовек; помышления сердца Его &mdash; в род и род.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22650, 'rst66', 'psa', 32, 12, 'Блажен народ, у которого Господь есть Бог, &mdash; племя, которое Он избрал в наследие Себе.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22651, 'rst66', 'psa', 32, 13, 'С небес призирает Господь, видит всех сынов человеческих;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22652, 'rst66', 'psa', 32, 14, 'с престола, на котором восседает, Он призирает на всех, живущих на земле:');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22653, 'rst66', 'psa', 32, 15, 'Он создал сердца всех их и вникает во все дела их.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22654, 'rst66', 'psa', 32, 16, 'Не спасется царь множеством воинства; исполина не защитит великая сила.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22655, 'rst66', 'psa', 32, 17, 'Ненадежен конь для спасения, не избавит великою силою своею.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22656, 'rst66', 'psa', 32, 18, 'Вот, око Господне над боящимися Его и уповающими на милость Его,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22657, 'rst66', 'psa', 32, 19, 'что Он душу их спасет от смерти и во время голода пропитает их.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22658, 'rst66', 'psa', 32, 20, 'Душа наша уповает на Господа: Он &mdash; помощь наша и защита наша;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22659, 'rst66', 'psa', 32, 21, 'о Нем веселится сердце наше, ибо на святое имя Его мы уповали.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22660, 'rst66', 'psa', 32, 22, 'Да будет милость Твоя, Господи, над нами, как мы уповаем на Тебя.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22661, 'rst66', 'psa', 33, 1, 'Псалом Давида, когда он притворился безумным пред Авимелехом и был изгнан от него и удалился.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22662, 'rst66', 'psa', 33, 2, 'Благословлю Господа во всякое время; хвала Ему непрестанно в устах моих.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22663, 'rst66', 'psa', 33, 3, 'Господом будет хвалиться душа моя; услышат кроткие и возвеселятся.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22664, 'rst66', 'psa', 33, 4, 'Величайте Господа со мною, и превознесем имя Его вместе.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22665, 'rst66', 'psa', 33, 5, 'Я взыскал Господа, и Он услышал меня, и от всех опасностей моих избавил меня.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22666, 'rst66', 'psa', 33, 6, 'Кто обращал взор к Нему, те просвещались, и лица их не постыдятся.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22667, 'rst66', 'psa', 33, 7, 'Сей нищий воззвал, &mdash; и Господь услышал и спас его от всех бед его.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22668, 'rst66', 'psa', 33, 8, 'Ангел Господень ополчается вокруг боящихся Его и избавляет их.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22669, 'rst66', 'psa', 33, 9, 'Вкусите и увидите, как благ Господь! Блажен человек, который уповает на Него!');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22670, 'rst66', 'psa', 33, 10, 'Бойтесь Господа, святые Его, ибо нет скудости у боящихся Его.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22671, 'rst66', 'psa', 33, 11, 'Скимны бедствуют и терпят голод, а ищущие Господа не терпят нужды ни в каком благе.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22672, 'rst66', 'psa', 33, 12, 'Придите, дети, послушайте меня: страху Господню научу вас.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22673, 'rst66', 'psa', 33, 13, 'Хочет ли человек жить и любит ли долгоденствие, чтобы видеть благо?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22674, 'rst66', 'psa', 33, 14, 'Удерживай язык свой от зла и уста свои от коварных слов.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22675, 'rst66', 'psa', 33, 15, 'Уклоняйся от зла и делай добро; ищи мира и следуй за ним.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22676, 'rst66', 'psa', 33, 16, 'Очи Господни <i>обращены</i> на праведников, и уши Его &mdash; к воплю их.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22677, 'rst66', 'psa', 33, 17, 'Но лицо Господне против делающих зло, чтобы истребить с земли память о них.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22678, 'rst66', 'psa', 33, 18, 'Взывают <i>праведные</i>, и Господь слышит, и от всех скорбей их избавляет их.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22679, 'rst66', 'psa', 33, 19, 'Близок Господь к сокрушенным сердцем и смиренных духом спасет.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22680, 'rst66', 'psa', 33, 20, 'Много скорбей у праведного, и от всех их избавит его Господь.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22681, 'rst66', 'psa', 33, 21, 'Он хранит все кости его; ни одна из них не сокрушится.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22682, 'rst66', 'psa', 33, 22, 'Убьет грешника зло, и ненавидящие праведного погибнут.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22683, 'rst66', 'psa', 33, 23, 'Избавит Господь душу рабов Своих, и никто из уповающих на Него не погибнет.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22684, 'rst66', 'psa', 34, 0, 'Псалом Давида.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22685, 'rst66', 'psa', 34, 1, 'Вступись, Господи, в тяжбу с тяжущимися со мною, побори борющихся со мною;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22686, 'rst66', 'psa', 34, 2, 'возьми щит и латы и восстань на помощь мне;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22687, 'rst66', 'psa', 34, 3, 'обнажи меч и прегради <i>путь</i> преследующим меня; скажи душе моей: «Я &mdash; спасение твое!»');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22688, 'rst66', 'psa', 34, 4, 'Да постыдятся и посрамятся ищущие души моей; да обратятся назад и покроются бесчестием умышляющие мне зло;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22689, 'rst66', 'psa', 34, 5, 'да будут они, как прах пред лицом ветра, и Ангел Господень да прогоняет <i>их;</i>');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22690, 'rst66', 'psa', 34, 6, 'да будет путь их темен и скользок, и Ангел Господень да преследует их,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22691, 'rst66', 'psa', 34, 7, 'ибо они без вины скрыли для меня яму &mdash; сеть свою, без вины выкопали <i>ее</i> для души моей.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22692, 'rst66', 'psa', 34, 8, 'Да придет на него гибель неожиданная, и сеть его, которую он скрыл <i>для меня</i>, да уловит его самого; да впадет в нее на погибель.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22693, 'rst66', 'psa', 34, 9, 'А моя душа будет радоваться о Господе, будет веселиться о спасении от Него.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22694, 'rst66', 'psa', 34, 10, 'Все кости мои скажут: «Господи! кто подобен Тебе, избавляющему слабого от сильного, бедного и нищего от грабителя его?»');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22695, 'rst66', 'psa', 34, 11, 'Восстали на меня свидетели неправедные: чего я не знаю, о том допрашивают меня;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22696, 'rst66', 'psa', 34, 12, 'воздают мне злом за добро, сиротством душе моей.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22697, 'rst66', 'psa', 34, 13, 'Я во время болезни их одевался во вретище, изнурял постом душу мою, и молитва моя возвращалась в недро мое.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22698, 'rst66', 'psa', 34, 14, 'Я поступал, как бы это был друг мой, брат мой; я ходил скорбный, с поникшею головою, как бы оплакивающий мать.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22699, 'rst66', 'psa', 34, 15, 'А когда я претыкался, они радовались и собирались; собирались ругатели против меня, не знаю за что, поносили и не переставали;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22700, 'rst66', 'psa', 34, 16, 'с лицемерными насмешниками скрежетали на меня зубами своими.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22701, 'rst66', 'psa', 34, 17, 'Господи! долго ли будешь смотреть <i>на это?</i> Отведи душу мою от злодейств их, от львов &mdash; одинокую мою.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22702, 'rst66', 'psa', 34, 18, 'Я прославлю Тебя в собрании великом, среди народа многочисленного восхвалю Тебя,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22703, 'rst66', 'psa', 34, 19, 'чтобы не торжествовали надо мною враждующие против меня неправедно, и не перемигивались глазами ненавидящие меня безвинно;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22704, 'rst66', 'psa', 34, 20, 'ибо не о мире говорят они, но против мирных земли составляют лукавые замыслы;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22705, 'rst66', 'psa', 34, 21, 'расширяют на меня уста свои; говорят: «хорошо! хорошо! видел глаз наш».');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22706, 'rst66', 'psa', 34, 22, 'Ты видел, Господи, не умолчи; Господи! не удаляйся от меня.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22707, 'rst66', 'psa', 34, 23, 'Подвигнись, пробудись для суда моего, для тяжбы моей, Боже мой и Господи мой!');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22708, 'rst66', 'psa', 34, 24, 'Суди меня по правде Твоей, Господи, Боже мой, и да не торжествуют они надо мною;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22709, 'rst66', 'psa', 34, 25, 'да не говорят в сердце своем: «хорошо! по душе нашей!» Да не говорят: «мы поглотили его».');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22710, 'rst66', 'psa', 34, 26, 'Да постыдятся и посрамятся все, радующиеся моему несчастью; да облекутся в стыд и позор величающиеся надо мною.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22711, 'rst66', 'psa', 34, 27, 'Да радуются и веселятся желающие правоты моей и говорят непрестанно: «да возвеличится Господь, желающий мира рабу Своему!»');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22712, 'rst66', 'psa', 34, 28, 'И язык мой будет проповедовать правду Твою и хвалу Твою всякий день.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22713, 'rst66', 'psa', 35, 1, 'Начальнику хора. Раба Господня Давида.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22714, 'rst66', 'psa', 35, 2, 'Нечестие беззаконного говорит в сердце моем: нет страха Божия пред глазами его,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22715, 'rst66', 'psa', 35, 3, 'ибо он льстит себе в глазах своих, будто отыскивает беззаконие свое, чтобы возненавидеть его;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22716, 'rst66', 'psa', 35, 4, 'слова уст его &mdash; неправда и лукавство; не хочет он вразумиться, чтобы делать добро;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22717, 'rst66', 'psa', 35, 5, 'на ложе своем замышляет беззаконие, становится на путь недобрый, не гнушается злом.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22718, 'rst66', 'psa', 35, 6, 'Господи! милость Твоя до небес, истина Твоя до облаков!');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22719, 'rst66', 'psa', 35, 7, 'Правда Твоя, как горы Божии, и судьбы Твои &mdash; бездна великая! Человеков и скотов хранишь Ты, Господи!');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22720, 'rst66', 'psa', 35, 8, 'Как драгоценна милость Твоя, Боже! Сыны человеческие в тени крыл Твоих покойны:');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22721, 'rst66', 'psa', 35, 9, 'насыщаются от тука дома Твоего, и из потока сладостей Твоих Ты напояешь их,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22722, 'rst66', 'psa', 35, 10, 'ибо у Тебя источник жизни; во свете Твоем мы видим свет.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22723, 'rst66', 'psa', 35, 11, 'Продли милость Твою к знающим Тебя и правду Твою к правым сердцем,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22724, 'rst66', 'psa', 35, 12, 'да не наступит на меня нога гордыни, и рука грешника да не изгонит меня:');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22725, 'rst66', 'psa', 35, 13, 'там пали делающие беззаконие, низринуты и не могут встать.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22726, 'rst66', 'psa', 36, 0, 'Псалом Давида.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22727, 'rst66', 'psa', 36, 1, 'Не ревнуй злодеям, не завидуй делающим беззаконие,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22728, 'rst66', 'psa', 36, 2, 'ибо они, как трава, скоро будут подкошены и, как зеленеющий злак, увянут.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22729, 'rst66', 'psa', 36, 3, 'Уповай на Господа и делай добро; живи на земле и храни истину.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22730, 'rst66', 'psa', 36, 4, 'Утешайся Господом, и Он исполнит желания сердца твоего.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22731, 'rst66', 'psa', 36, 5, 'Предай Господу путь твой и уповай на Него, и Он совершит,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22732, 'rst66', 'psa', 36, 6, 'и выведет, как свет, правду твою и справедливость твою, как полдень.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22733, 'rst66', 'psa', 36, 7, 'Покорись Господу и надейся на Него. Не ревнуй успевающему в пути своем, человеку лукавствующему.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22734, 'rst66', 'psa', 36, 8, 'Перестань гневаться и оставь ярость; не ревнуй до того, чтобы делать зло,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22735, 'rst66', 'psa', 36, 9, 'ибо делающие зло истребятся, уповающие же на Господа наследуют землю.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22736, 'rst66', 'psa', 36, 10, 'Еще немного, и не станет нечестивого; посмотришь на его место, и нет его.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22737, 'rst66', 'psa', 36, 11, 'А кроткие наследуют землю и насладятся множеством мира.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22738, 'rst66', 'psa', 36, 12, 'Нечестивый злоумышляет против праведника и скрежещет на него зубами своими:');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22739, 'rst66', 'psa', 36, 13, 'Господь же посмевается над ним, ибо видит, что приходит день его.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22740, 'rst66', 'psa', 36, 14, 'Нечестивые обнажают меч и натягивают лук свой, чтобы низложить бедного и нищего, чтобы пронзить <i>идущих</i> прямым путем:');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22741, 'rst66', 'psa', 36, 15, 'меч их войдет в их же сердце, и луки их сокрушатся.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22742, 'rst66', 'psa', 36, 16, 'Малое у праведника &mdash; лучше богатства многих нечестивых,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22743, 'rst66', 'psa', 36, 17, 'ибо мышцы нечестивых сокрушатся, а праведников подкрепляет Господь.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22744, 'rst66', 'psa', 36, 18, 'Господь знает дни непорочных, и достояние их пребудет вовек:');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22745, 'rst66', 'psa', 36, 19, 'не будут они постыжены во время лютое и во дни голода будут сыты;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22746, 'rst66', 'psa', 36, 20, 'а нечестивые погибнут, и враги Господни, как тук агнцев, исчезнут, в дыме исчезнут.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22747, 'rst66', 'psa', 36, 21, 'Нечестивый берет взаймы и не отдает, а праведник милует и дает,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22748, 'rst66', 'psa', 36, 22, 'ибо благословенные Им наследуют землю, а проклятые Им истребятся.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22749, 'rst66', 'psa', 36, 23, 'Господом утверждаются стопы <i>такого</i> человека, и Он благоволит к пути его:');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22750, 'rst66', 'psa', 36, 24, 'когда он будет падать, не упадет, ибо Господь поддерживает его за руку.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22751, 'rst66', 'psa', 36, 25, 'Я был молод и состарился, и не видал праведника оставленным и потомков его просящими хлеба:');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22752, 'rst66', 'psa', 36, 26, 'он всякий день милует и взаймы дает, и потомство его в благословении будет.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22753, 'rst66', 'psa', 36, 27, 'Уклоняйся от зла, и делай добро, и будешь жить вовек:');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22754, 'rst66', 'psa', 36, 28, 'ибо Господь любит правду и не оставляет святых Своих; вовек сохранятся они; и потомство нечестивых истребится.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22755, 'rst66', 'psa', 36, 29, 'Праведники наследуют землю и будут жить на ней вовек.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22756, 'rst66', 'psa', 36, 30, 'Уста праведника изрекают премудрость, и язык его произносит правду.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22757, 'rst66', 'psa', 36, 31, 'Закон Бога его в сердце у него; не поколеблются стопы его.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22758, 'rst66', 'psa', 36, 32, 'Нечестивый подсматривает за праведником и ищет умертвить его;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22759, 'rst66', 'psa', 36, 33, 'но Господь не отдаст его в руки его и не допустит обвинить его, когда он будет судим.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22760, 'rst66', 'psa', 36, 34, 'Уповай на Господа и держись пути Его: и Он вознесет тебя, чтобы ты наследовал землю; и когда будут истребляемы нечестивые, ты увидишь.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22761, 'rst66', 'psa', 36, 35, 'Видел я нечестивца грозного, расширявшегося, подобно укоренившемуся многоветвистому дереву;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22762, 'rst66', 'psa', 36, 36, 'но он прошел, и вот нет его; ищу его и не нахожу.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22763, 'rst66', 'psa', 36, 37, 'Наблюдай за непорочным и смотри на праведного, ибо будущность <i>такого</i> человека есть мир;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22764, 'rst66', 'psa', 36, 38, 'а беззаконники все истребятся; будущность нечестивых погибнет.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22765, 'rst66', 'psa', 36, 39, 'От Господа спасение праведникам, Он &mdash; защита их во время скорби;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22766, 'rst66', 'psa', 36, 40, 'и поможет им Господь и избавит их; избавит их от нечестивых и спасет их, ибо они на Него уповают.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22767, 'rst66', 'psa', 37, 1, 'Псалом Давида. В воспоминание.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22768, 'rst66', 'psa', 37, 2, 'Господи! не в ярости Твоей обличай меня и не во гневе Твоем наказывай меня,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22769, 'rst66', 'psa', 37, 3, 'ибо стрелы Твои вонзились в меня, и рука Твоя тяготеет на мне.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22770, 'rst66', 'psa', 37, 4, 'Нет целого места в плоти моей от гнева Твоего; нет мира в костях моих от грехов моих,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22771, 'rst66', 'psa', 37, 5, 'ибо беззакония мои превысили голову мою, как тяжелое бремя отяготели на мне,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22772, 'rst66', 'psa', 37, 6, 'смердят, гноятся раны мои от безумия моего.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22773, 'rst66', 'psa', 37, 7, 'Я согбен и совсем поник, весь день сетуя хожу,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22774, 'rst66', 'psa', 37, 8, 'ибо чресла мои полны воспалениями, и нет целого места в плоти моей.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22775, 'rst66', 'psa', 37, 9, 'Я изнемог и сокрушен чрезмерно; кричу от терзания сердца моего.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22776, 'rst66', 'psa', 37, 10, 'Господи! пред Тобою все желания мои, и воздыхание мое не сокрыто от Тебя.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22777, 'rst66', 'psa', 37, 11, 'Сердце мое трепещет; оставила меня сила моя, и свет очей моих, &mdash; и того нет у меня.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22778, 'rst66', 'psa', 37, 12, 'Друзья мои и искренние отступили от язвы моей, и ближние мои стоят вдали.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22779, 'rst66', 'psa', 37, 13, 'Ищущие же души моей ставят сети, и желающие мне зла говорят о погибели <i>моей</i> и замышляют всякий день козни;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22780, 'rst66', 'psa', 37, 14, 'а я, как глухой, не слышу, и как немой, который не открывает уст своих;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22781, 'rst66', 'psa', 37, 15, 'и стал я, как человек, который не слышит и не имеет в устах своих ответа,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22782, 'rst66', 'psa', 37, 16, 'ибо на Тебя, Господи, уповаю я; Ты услышишь, Господи, Боже мой.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22783, 'rst66', 'psa', 37, 17, 'И я сказал: да не восторжествуют надо мною <i>враги мои</i>; когда колеблется нога моя, они величаются надо мною.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22784, 'rst66', 'psa', 37, 18, 'Я близок к падению, и скорбь моя всегда предо мною.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22785, 'rst66', 'psa', 37, 19, 'Беззаконие мое я сознаю, сокрушаюсь о грехе моем.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22786, 'rst66', 'psa', 37, 20, 'А враги мои живут и укрепляются, и умножаются ненавидящие меня безвинно;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22787, 'rst66', 'psa', 37, 21, 'и воздающие мне злом за добро враждуют против меня за то, что я следую добру.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22788, 'rst66', 'psa', 37, 22, 'Не оставь меня, Господи, Боже мой! Не удаляйся от меня;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22789, 'rst66', 'psa', 37, 23, 'поспеши на помощь мне, Господи, Спаситель мой!');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22790, 'rst66', 'psa', 38, 1, 'Начальнику хора, Идифуму. Псалом Давида.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22791, 'rst66', 'psa', 38, 2, 'Я сказал: буду я наблюдать за путями моими, чтобы не согрешать мне языком моим; буду обуздывать уста мои, доколе нечестивый предо мною.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22792, 'rst66', 'psa', 38, 3, 'Я был нем и безгласен, и молчал <i>даже</i> о добром; и скорбь моя подвиглась.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22793, 'rst66', 'psa', 38, 4, 'Воспламенилось сердце мое во мне; в мыслях моих возгорелся огонь; я стал говорить языком моим:');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22794, 'rst66', 'psa', 38, 5, 'скажи мне, Господи, кончину мою и число дней моих, какое оно, дабы я знал, какой век мой.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22795, 'rst66', 'psa', 38, 6, 'Вот, Ты дал мне дни, <i>как</i> пяди, и век мой как ничто пред Тобою. Подлинно, совершенная суета &mdash; всякий человек живущий.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22796, 'rst66', 'psa', 38, 7, 'Подлинно, человек ходит подобно призраку; напрасно он суетится, собирает и не знает, кому достанется то.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22797, 'rst66', 'psa', 38, 8, 'И ныне чего ожидать мне, Господи? надежда моя &mdash; на Тебя.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22798, 'rst66', 'psa', 38, 9, 'От всех беззаконий моих избавь меня, не предавай меня на поругание безумному.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22799, 'rst66', 'psa', 38, 10, 'Я стал нем, не открываю уст моих; потому что Ты соделал это.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22800, 'rst66', 'psa', 38, 11, 'Отклони от меня удары Твои; я исчезаю от поражающей руки Твоей.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22801, 'rst66', 'psa', 38, 12, 'Если Ты обличениями будешь наказывать человека за преступления, то рассыплется, как от моли, краса его. Так, суетен всякий человек!');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22802, 'rst66', 'psa', 38, 13, 'Услышь, Господи, молитву мою и внемли воплю моему; не будь безмолвен к слезам моим, ибо странник я у Тебя <i>и</i> пришлец, как и все отцы мои.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22803, 'rst66', 'psa', 38, 14, 'Отступи от меня, чтобы я мог подкрепиться, прежде нежели отойду и не будет меня.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22804, 'rst66', 'psa', 39, 1, 'Начальнику хора. Псалом Давида.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22805, 'rst66', 'psa', 39, 2, 'Твердо уповал я на Господа, и Он приклонился ко мне и услышал вопль мой;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22806, 'rst66', 'psa', 39, 3, 'извлек меня из страшного рва, из тинистого болота, и поставил на камне ноги мои и утвердил стопы мои;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22807, 'rst66', 'psa', 39, 4, 'и вложил в уста мои новую песнь &mdash; хвалу Богу нашему. Увидят многие и убоятся и будут уповать на Господа.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22808, 'rst66', 'psa', 39, 5, 'Блажен человек, который на Господа возлагает надежду свою и не обращается к гордым и к уклоняющимся ко лжи.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22809, 'rst66', 'psa', 39, 6, 'Много соделал Ты, Господи, Боже мой: о чудесах и помышлениях Твоих о нас &mdash; кто уподобится Тебе! &mdash; хотел бы я проповедовать и говорить, но они превышают число.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22810, 'rst66', 'psa', 39, 7, 'Жертвы и приношения Ты не восхотел; Ты открыл мне уши; всесожжения и жертвы за грех Ты не потребовал.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22811, 'rst66', 'psa', 39, 8, 'Тогда я сказал: вот, иду; в свитке книжном написано о мне:');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22812, 'rst66', 'psa', 39, 9, 'я желаю исполнить волю Твою, Боже мой, и закон Твой у меня в сердце.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22813, 'rst66', 'psa', 39, 10, 'Я возвещал правду Твою в собрании великом; я не возбранял устам моим: Ты, Господи, знаешь.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22814, 'rst66', 'psa', 39, 11, 'Правды Твоей не скрывал в сердце моем, возвещал верность Твою и спасение Твое, не утаивал милости Твоей и истины Твоей пред собранием великим.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22815, 'rst66', 'psa', 39, 12, 'Не удерживай, Господи, щедрот Твоих от меня; милость Твоя и истина Твоя да охраняют меня непрестанно,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22816, 'rst66', 'psa', 39, 13, 'ибо окружили меня беды неисчислимые; постигли меня беззакония мои, так что видеть не могу: их более, нежели волос на голове моей; сердце мое оставило меня.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22817, 'rst66', 'psa', 39, 14, 'Благоволи, Господи, избавить меня; Господи! поспеши на помощь мне.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22818, 'rst66', 'psa', 39, 15, 'Да постыдятся и посрамятся все, ищущие погибели душе моей! Да будут обращены назад и преданы посмеянию желающие мне зла!');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22819, 'rst66', 'psa', 39, 16, 'Да смятутся от посрамления своего говорящие мне: «хорошо! хорошо!»');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22820, 'rst66', 'psa', 39, 17, 'Да радуются и веселятся Тобою все, ищущие Тебя, и любящие спасение Твое да говорят непрестанно: «велик Господь!»');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22821, 'rst66', 'psa', 39, 18, 'Я же беден и нищ, но Господь печется о мне. Ты &mdash; помощь моя и избавитель мой, Боже мой! не замедли.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22822, 'rst66', 'psa', 40, 1, 'Начальнику хора. Псалом Давида.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22823, 'rst66', 'psa', 40, 2, 'Блажен, кто помышляет о бедном! В день бедствия избавит его Господь.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22824, 'rst66', 'psa', 40, 3, 'Господь сохранит его и сбережет ему жизнь; блажен будет он на земле. И Ты не отдашь его на волю врагов его.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22825, 'rst66', 'psa', 40, 4, 'Господь укрепит его на одре болезни его. Ты изменишь все ложе его в болезни его.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22826, 'rst66', 'psa', 40, 5, 'Я сказал: Господи! помилуй меня, исцели душу мою, ибо согрешил я пред Тобою.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22827, 'rst66', 'psa', 40, 6, 'Враги мои говорят обо мне злое: «когда он умрет и погибнет имя его?»');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22828, 'rst66', 'psa', 40, 7, 'И если приходит кто видеть меня, говорит ложь; сердце его слагает в себе неправду, и он, выйдя вон, толкует.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22829, 'rst66', 'psa', 40, 8, 'Все ненавидящие меня шепчут между собою против меня, замышляют на меня зло:');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22830, 'rst66', 'psa', 40, 9, '«Слово велиала пришло на него; он слег; не встать ему более».');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22831, 'rst66', 'psa', 40, 10, 'Даже человек мирный со мною, на которого я полагался, который ел хлеб мой, поднял на меня пяту.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22832, 'rst66', 'psa', 40, 11, 'Ты же, Господи, помилуй меня и восставь меня, и я воздам им.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22833, 'rst66', 'psa', 40, 12, 'Из того узнаю, что Ты благоволишь ко мне, если враг мой не восторжествует надо мною,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22834, 'rst66', 'psa', 40, 13, 'а меня сохранишь в целости моей и поставишь пред лицом Твоим навеки.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22835, 'rst66', 'psa', 40, 14, 'Благословен Господь Бог Израилев от века и до века! Аминь, аминь!');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22836, 'rst66', 'psa', 41, 1, 'Начальнику хора. Учение. Сынов Кореевых.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22837, 'rst66', 'psa', 41, 2, 'Как лань желает к потокам воды, так желает душа моя к Тебе, Боже!');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22838, 'rst66', 'psa', 41, 3, 'Жаждет душа моя к Богу крепкому, живому: когда приду и явлюсь пред лицо Божие!');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22839, 'rst66', 'psa', 41, 4, 'Слезы мои были для меня хлебом день и ночь, когда говорили мне всякий день: «где Бог твой?»');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22840, 'rst66', 'psa', 41, 5, 'Вспоминая об этом, изливаю душу мою, потому что я ходил в многолюдстве, вступал с ними в дом Божий со гласом радости и славословия празднующего сонма.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22841, 'rst66', 'psa', 41, 6, 'Что унываешь ты, душа моя, и что смущаешься? Уповай на Бога, ибо я буду еще славить Его, Спасителя моего и Бога моего.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22842, 'rst66', 'psa', 41, 7, 'Унывает во мне душа моя; посему я воспоминаю о Тебе с земли Иорданской, с Ермона, с горы Цоар.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22843, 'rst66', 'psa', 41, 8, 'Бездна бездну призывает голосом водопадов Твоих; все воды Твои и волны Твои прошли надо мною.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22844, 'rst66', 'psa', 41, 9, 'Днем явит Господь милость Свою, и ночью песнь Ему у меня, молитва к Богу жизни моей.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22845, 'rst66', 'psa', 41, 10, 'Скажу Богу, заступнику моему: для чего Ты забыл меня? Для чего я сетуя хожу от оскорблений врага?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22846, 'rst66', 'psa', 41, 11, 'Как бы поражая кости мои, ругаются надо мною враги мои, когда говорят мне всякий день: «где Бог твой?»');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22847, 'rst66', 'psa', 41, 12, 'Что унываешь ты, душа моя, и что смущаешься? Уповай на Бога, ибо я буду еще славить Его, Спасителя моего и Бога моего.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22848, 'rst66', 'psa', 42, 1, 'Суди меня, Боже, и вступись в тяжбу мою с народом недобрым. От человека лукавого и несправедливого избавь меня,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22849, 'rst66', 'psa', 42, 2, 'ибо Ты Бог крепости моей. Для чего Ты отринул меня? для чего я сетуя хожу от оскорблений врага?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22850, 'rst66', 'psa', 42, 3, 'Пошли свет Твой и истину Твою; да ведут они меня и приведут на святую гору Твою и в обители Твои.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22851, 'rst66', 'psa', 42, 4, 'И подойду я к жертвеннику Божию, к Богу радости и веселия моего, и на гуслях буду славить Тебя, Боже, Боже мой!');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22852, 'rst66', 'psa', 42, 5, 'Что унываешь ты, душа моя, и что смущаешься? Уповай на Бога; ибо я буду еще славить Его, Спасителя моего и Бога моего.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22853, 'rst66', 'psa', 43, 1, 'Начальнику хора. Учение. Сынов Кореевых.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22854, 'rst66', 'psa', 43, 2, 'Боже, мы слышали ушами своими, отцы наши рассказывали нам о деле, какое Ты соделал во дни их, во дни древние:');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22855, 'rst66', 'psa', 43, 3, 'Ты рукою Твоею истребил народы, а их насадил; поразил племена и изгнал их;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22856, 'rst66', 'psa', 43, 4, 'ибо они не мечом своим приобрели землю, и не их мышца спасла их, но Твоя десница и Твоя мышца и свет лица Твоего, ибо Ты благоволил к ним.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22857, 'rst66', 'psa', 43, 5, 'Боже, Царь мой! Ты &mdash; тот же; даруй спасение Иакову.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22858, 'rst66', 'psa', 43, 6, 'С Тобою избодаем рогами врагов наших; во имя Твое попрем ногами восстающих на нас:');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22859, 'rst66', 'psa', 43, 7, 'ибо не на лук мой уповаю, и не меч мой спасет меня;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22860, 'rst66', 'psa', 43, 8, 'но Ты спасешь нас от врагов наших и посрамишь ненавидящих нас.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22861, 'rst66', 'psa', 43, 9, 'О Боге похвалимся всякий день, и имя Твое будем прославлять вовек.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22862, 'rst66', 'psa', 43, 10, 'Но ныне Ты отринул и посрамил нас, и не выходишь с войсками нашими;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22863, 'rst66', 'psa', 43, 11, 'обратил нас в бегство от врага, и ненавидящие нас грабят нас;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22864, 'rst66', 'psa', 43, 12, 'Ты отдал нас, как овец, на съедение и рассеял нас между народами;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22865, 'rst66', 'psa', 43, 13, 'без выгоды Ты продал народ Твой и не возвысил цены его;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22866, 'rst66', 'psa', 43, 14, 'отдал нас на поношение соседям нашим, на посмеяние и поругание живущим вокруг нас;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22867, 'rst66', 'psa', 43, 15, 'Ты сделал нас притчею между народами, покиванием головы между иноплеменниками.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22868, 'rst66', 'psa', 43, 16, 'Всякий день посрамление мое предо мною, и стыд покрывает лицо мое');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22869, 'rst66', 'psa', 43, 17, 'от голоса поносителя и клеветника, от взоров врага и мстителя:');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22870, 'rst66', 'psa', 43, 18, 'Все это пришло на нас, но мы не забыли Тебя и не нарушили завета Твоего.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22871, 'rst66', 'psa', 43, 19, 'Не отступило назад сердце наше, и стопы наши не уклонились от пути Твоего,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22872, 'rst66', 'psa', 43, 20, 'когда Ты сокрушил нас в земле драконов и покрыл нас тенью смертною.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22873, 'rst66', 'psa', 43, 21, 'Если бы мы забыли имя Бога нашего и простерли руки наши к богу чужому,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22874, 'rst66', 'psa', 43, 22, 'то не взыскал ли бы сего Бог? Ибо Он знает тайны сердца.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22875, 'rst66', 'psa', 43, 23, 'Но за Тебя умерщвляют нас всякий день, считают нас за овец, <i>обреченных</i> на заклание.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22876, 'rst66', 'psa', 43, 24, 'Восстань, что спишь, Господи! пробудись, не отринь навсегда.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22877, 'rst66', 'psa', 43, 25, 'Для чего скрываешь лицо Твое, забываешь скорбь нашу и угнетение наше?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22878, 'rst66', 'psa', 43, 26, 'Ибо душа наша унижена до праха, утроба наша прильнула к земле.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22879, 'rst66', 'psa', 43, 27, 'Восстань на помощь нам и избавь нас ради милости Твоей.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22880, 'rst66', 'psa', 44, 1, 'Начальнику хора. На <i>музыкальном орудии</i> Шошан. Учение. Сынов Кореевых. Песнь любви.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22881, 'rst66', 'psa', 44, 2, 'Излилось из сердца моего слово благое; я говорю: песнь моя о Царе; язык мой &mdash; трость скорописца.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22882, 'rst66', 'psa', 44, 3, 'Ты прекраснее сынов человеческих; благодать излилась из уст Твоих; посему благословил Тебя Бог навеки.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22883, 'rst66', 'psa', 44, 4, 'Препояшь Себя по бедру мечом Твоим, Сильный, славою Твоею и красотою Твоею,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22884, 'rst66', 'psa', 44, 5, 'и в сем украшении Твоем поспеши, воссядь на колесницу ради истины и кротости и правды, и десница Твоя покажет Тебе дивные дела.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22885, 'rst66', 'psa', 44, 6, 'Остры стрелы Твои, &mdash; народы падут пред Тобою, &mdash; они &mdash; в сердце врагов Царя.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22886, 'rst66', 'psa', 44, 7, 'Престол Твой, Боже, вовек; жезл правоты &mdash; жезл царства Твоего.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22887, 'rst66', 'psa', 44, 8, 'Ты возлюбил правду и возненавидел беззаконие, посему помазал Тебя, Боже, Бог Твой елеем радости более соучастников Твоих.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22888, 'rst66', 'psa', 44, 9, 'Все одежды Твои, как смирна и алой и касия; из чертогов слоновой кости увеселяют Тебя.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22889, 'rst66', 'psa', 44, 10, 'Дочери царей между почетными у Тебя; стала царица одесную Тебя в Офирском золоте.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22890, 'rst66', 'psa', 44, 11, 'Слыши, дщерь, и смотри, и приклони ухо твое, и забудь народ твой и дом отца твоего.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22891, 'rst66', 'psa', 44, 12, 'И возжелает Царь красоты твоей; ибо Он &mdash; Господь твой, и ты поклонись Ему.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22892, 'rst66', 'psa', 44, 13, 'И дочь Тира с дарами, и богатейшие из народа будут умолять лицо Твое.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22893, 'rst66', 'psa', 44, 14, 'Вся слава дщери Царя внутри; одежда ее шита золотом;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22894, 'rst66', 'psa', 44, 15, 'в испещренной одежде ведется она к Царю; за нею ведутся к Тебе девы, подруги ее,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22895, 'rst66', 'psa', 44, 16, 'приводятся с весельем и ликованьем, входят в чертог Царя.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22896, 'rst66', 'psa', 44, 17, 'Вместо отцов Твоих, будут сыновья Твои; Ты поставишь их князьями по всей земле.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22897, 'rst66', 'psa', 44, 18, 'Сделаю имя Твое памятным в род и род; посему народы будут славить Тебя во веки и веки.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22898, 'rst66', 'psa', 45, 1, 'Начальнику хора. Сынов Кореевых. На <i>музыкальном орудии</i> Аламоф. Песнь.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22899, 'rst66', 'psa', 45, 2, 'Бог нам прибежище и сила, скорый помощник в бедах,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22900, 'rst66', 'psa', 45, 3, 'посему не убоимся, хотя бы поколебалась земля и горы двинулись в сердце морей.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22901, 'rst66', 'psa', 45, 4, 'Пусть шумят, вздымаются воды их, трясутся горы от волнения их.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22902, 'rst66', 'psa', 45, 5, 'Речные потоки веселят град Божий, святое жилище Всевышнего.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22903, 'rst66', 'psa', 45, 6, 'Бог посреди его; он не поколеблется: Бог поможет ему с раннего утра.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22904, 'rst66', 'psa', 45, 7, 'Восшумели народы; двинулись царства: <i>Всевышний</i> дал глас Свой, и растаяла земля.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22905, 'rst66', 'psa', 45, 8, 'Господь сил с нами, Бог Иакова &mdash; заступник наш.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22906, 'rst66', 'psa', 45, 9, 'Придите и видите дела Господа, &mdash; какие произвел Он опустошения на земле:');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22907, 'rst66', 'psa', 45, 10, 'прекращая брани до края земли, сокрушил лук и переломил копье, колесницы сжег огнем.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22908, 'rst66', 'psa', 45, 11, 'Остановитесь и познайте, что Я &mdash; Бог: буду превознесен в народах, превознесен на земле.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22909, 'rst66', 'psa', 45, 12, 'Господь сил с нами, заступник наш &mdash; Бог Иакова.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22910, 'rst66', 'psa', 46, 1, 'Начальнику хора. Сынов Кореевых. Псалом.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22911, 'rst66', 'psa', 46, 2, 'Восплещите руками все народы, воскликните Богу гласом радости;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22912, 'rst66', 'psa', 46, 3, 'ибо Господь Всевышний страшен, &mdash; великий Царь над всею землею;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22913, 'rst66', 'psa', 46, 4, 'покорил нам народы и племена под ноги наши;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22914, 'rst66', 'psa', 46, 5, 'избрал нам наследие наше, красу Иакова, которого возлюбил.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22915, 'rst66', 'psa', 46, 6, 'Восшел Бог при восклицаниях, Господь при звуке трубном.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22916, 'rst66', 'psa', 46, 7, 'Пойте Богу нашему, пойте; пойте Царю нашему, пойте,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22917, 'rst66', 'psa', 46, 8, 'ибо Бог &mdash; Царь всей земли; пойте все разумно.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22918, 'rst66', 'psa', 46, 9, 'Бог воцарился над народами, Бог воссел на святом престоле Своем;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22919, 'rst66', 'psa', 46, 10, 'князья народов собрались к народу Бога Авраамова, ибо щиты земли &mdash; Божии; Он превознесен <i>над ними</i>.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22920, 'rst66', 'psa', 47, 1, 'Песнь. Псалом. Сынов Кореевых.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22921, 'rst66', 'psa', 47, 2, 'Велик Господь и всехвален во граде Бога нашего, на святой горе Его.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22922, 'rst66', 'psa', 47, 3, 'Прекрасная возвышенность, радость всей земли гора Сион; на северной стороне <i>ее</i> город великого Царя.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22923, 'rst66', 'psa', 47, 4, 'Бог в жилищах его ведом, как заступник:');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22924, 'rst66', 'psa', 47, 5, 'Ибо вот, сошлись цари и прошли все мимо;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22925, 'rst66', 'psa', 47, 6, 'увидели и изумились, смутились и обратились в бегство;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22926, 'rst66', 'psa', 47, 7, 'страх объял их там и мука, как у женщин в родах;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22927, 'rst66', 'psa', 47, 8, 'восточным ветром Ты сокрушил Фарсийские корабли.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22928, 'rst66', 'psa', 47, 9, 'Как слышали мы, так и увидели во граде Господа сил, во граде Бога нашего: Бог утвердит его навеки.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22929, 'rst66', 'psa', 47, 10, 'Мы размышляли, Боже, о благости Твоей посреди храма Твоего.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22930, 'rst66', 'psa', 47, 11, 'Как имя Твое, Боже, так и хвала Твоя до концов земли; десница Твоя полна правды.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22931, 'rst66', 'psa', 47, 12, 'Да веселится гора Сион, да радуются дщери Иудейские ради судов Твоих, <i>Господи</i>.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22932, 'rst66', 'psa', 47, 13, 'Пойдите вокруг Сиона и обойдите его, пересчитайте башни его;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22933, 'rst66', 'psa', 47, 14, 'обратите сердце ваше к укреплениям его, рассмотрите домы его, чтобы пересказать грядущему роду,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22934, 'rst66', 'psa', 47, 15, 'ибо сей Бог есть Бог наш на веки и веки: Он будет вождем нашим до самой смерти.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22935, 'rst66', 'psa', 48, 1, 'Начальнику хора. Сынов Кореевых. Псалом.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22936, 'rst66', 'psa', 48, 2, 'Слушайте сие, все народы; внимайте сему, все живущие во вселенной, &mdash;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22937, 'rst66', 'psa', 48, 3, 'и простые и знатные, богатый, равно как бедный.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22938, 'rst66', 'psa', 48, 4, 'Уста мои изрекут премудрость, и размышления сердца моего &mdash; знание.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22939, 'rst66', 'psa', 48, 5, 'Приклоню ухо мое к притче, на гуслях открою загадку мою:');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22940, 'rst66', 'psa', 48, 6, '«Для чего бояться мне во дни бедствия, <i>когда</i> беззаконие путей моих окружит меня?»');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22941, 'rst66', 'psa', 48, 7, 'Надеющиеся на силы свои и хвалящиеся множеством богатства своего!');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22942, 'rst66', 'psa', 48, 8, 'Человек никак не искупит брата своего и не даст Богу выкупа за него:');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22943, 'rst66', 'psa', 48, 9, 'дорога цена искупления души их, и не будет того вовек,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22944, 'rst66', 'psa', 48, 10, 'чтобы остался <i>кто</i> жить навсегда и не увидел могилы.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22945, 'rst66', 'psa', 48, 11, 'Каждый видит, что и мудрые умирают, равно как и невежды и бессмысленные погибают и оставляют имущество свое другим.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22946, 'rst66', 'psa', 48, 12, 'В мыслях у них, что домы их вечны и что жилища их в род и род, и земли свои они называют своими именами.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22947, 'rst66', 'psa', 48, 13, 'Но человек в чести не пребудет; он уподобится животным, которые погибают.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22948, 'rst66', 'psa', 48, 14, 'Этот путь их есть безумие их, хотя последующие за ними одобряют мнение их.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22949, 'rst66', 'psa', 48, 15, 'Как овец, заключат их в преисподнюю; смерть будет пасти их, и наутро праведники будут владычествовать над ними; сила их истощится; могила &mdash; жилище их.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22950, 'rst66', 'psa', 48, 16, 'Но Бог избавит душу мою от власти преисподней, когда примет меня.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22951, 'rst66', 'psa', 48, 17, 'Не бойся, когда богатеет человек, когда слава дома его умножается:');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22952, 'rst66', 'psa', 48, 18, 'ибо, умирая, не возьмет ничего; не пойдет за ним слава его;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22953, 'rst66', 'psa', 48, 19, 'хотя при жизни он ублажает душу свою, и прославляют тебя, что ты удовлетворяешь себе,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22954, 'rst66', 'psa', 48, 20, 'но он пойдет к роду отцов своих, которые никогда не увидят света.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22955, 'rst66', 'psa', 48, 21, 'Человек, который в чести и неразумен, подобен животным, которые погибают.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22956, 'rst66', 'psa', 49, 0, 'Псалом Асафа.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22957, 'rst66', 'psa', 49, 1, 'Бог богов, Господь возглаголал и призывает землю, от восхода солнца до запада.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22958, 'rst66', 'psa', 49, 2, 'С Сиона, который есть верх красоты, является Бог,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22959, 'rst66', 'psa', 49, 3, 'грядет Бог наш, и не в безмолвии: пред Ним огонь поядающий, и вокруг Его сильная буря.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22960, 'rst66', 'psa', 49, 4, 'Он призывает свыше небо и землю, судить народ Свой:');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22961, 'rst66', 'psa', 49, 5, '«Соберите ко Мне святых Моих, вступивших в завет со Мною при жертве».');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22962, 'rst66', 'psa', 49, 6, 'И небеса провозгласят правду Его, ибо судия сей есть Бог.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22963, 'rst66', 'psa', 49, 7, '«Слушай, народ Мой, Я буду говорить; Израиль! Я буду свидетельствовать против тебя: Я Бог, твой Бог.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22964, 'rst66', 'psa', 49, 8, 'Не за жертвы твои Я буду укорять тебя; всесожжения твои всегда предо Мною;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22965, 'rst66', 'psa', 49, 9, 'не приму тельца из дома твоего, ни козлов из дворов твоих,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22966, 'rst66', 'psa', 49, 10, 'ибо Мои все звери в лесу, и скот на тысяче гор,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22967, 'rst66', 'psa', 49, 11, 'знаю всех птиц на горах, и животные на полях предо Мною.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22968, 'rst66', 'psa', 49, 12, 'Если бы Я взалкал, то не сказал бы тебе, ибо Моя вселенная и все, что наполняет ее.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22969, 'rst66', 'psa', 49, 13, 'Ем ли Я мясо волов и пью ли кровь козлов?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22970, 'rst66', 'psa', 49, 14, 'Принеси в жертву Богу хвалу и воздай Всевышнему обеты твои,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22971, 'rst66', 'psa', 49, 15, 'и призови Меня в день скорби; Я избавлю тебя, и ты прославишь Меня».');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22972, 'rst66', 'psa', 49, 16, 'Грешнику же говорит Бог: «что ты проповедуешь уставы Мои и берешь завет Мой в уста твои,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22973, 'rst66', 'psa', 49, 17, 'а сам ненавидишь наставление Мое и слова Мои бросаешь за себя?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22974, 'rst66', 'psa', 49, 18, 'Когда видишь вора, сходишься с ним, и с прелюбодеями сообщаешься;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22975, 'rst66', 'psa', 49, 19, 'уста твои открываешь на злословие, и язык твой сплетает коварство;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22976, 'rst66', 'psa', 49, 20, 'сидишь и говоришь на брата твоего, на сына матери твоей клевещешь;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22977, 'rst66', 'psa', 49, 21, 'ты это делал, и Я молчал; ты подумал, что Я такой же, как ты. Изобличу тебя и представлю пред глаза твои <i>грехи твои</i>.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22978, 'rst66', 'psa', 49, 22, 'Уразумейте это, забывающие Бога, дабы Я не восхитил, &mdash; и не будет избавляющего.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22979, 'rst66', 'psa', 49, 23, 'Кто приносит в жертву хвалу, тот чтит Меня, и кто наблюдает за путем своим, тому явлю Я спасение Божие».');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22980, 'rst66', 'psa', 50, 1, 'Начальнику хора. Псалом Давида,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22981, 'rst66', 'psa', 50, 2, 'когда приходил к нему пророк Нафан, после того, как Давид вошел к Вирсавии.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22982, 'rst66', 'psa', 50, 3, 'Помилуй меня, Боже, по великой милости Твоей, и по множеству щедрот Твоих изгладь беззакония мои.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22983, 'rst66', 'psa', 50, 4, 'Многократно омой меня от беззакония моего, и от греха моего очисти меня,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22984, 'rst66', 'psa', 50, 5, 'ибо беззакония мои я сознаю, и грех мой всегда предо мною.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22985, 'rst66', 'psa', 50, 6, 'Тебе, Тебе единому согрешил я и лукавое пред очами Твоими сделал, так что Ты праведен в приговоре Твоем и чист в суде Твоем.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22986, 'rst66', 'psa', 50, 7, 'Вот, я в беззаконии зачат, и во грехе родила меня мать моя.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22987, 'rst66', 'psa', 50, 8, 'Вот, Ты возлюбил истину в сердце и внутрь меня явил мне мудрость.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22988, 'rst66', 'psa', 50, 9, 'Окропи меня иссопом, и буду чист; омой меня, и буду белее снега.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22989, 'rst66', 'psa', 50, 10, 'Дай мне услышать радость и веселие, и возрадуются кости, Тобою сокрушенные.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22990, 'rst66', 'psa', 50, 11, 'Отврати лицо Твое от грехов моих и изгладь все беззакония мои.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22991, 'rst66', 'psa', 50, 12, 'Сердце чистое сотвори во мне, Боже, и дух правый обнови внутри меня.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22992, 'rst66', 'psa', 50, 13, 'Не отвергни меня от лица Твоего и Духа Твоего Святого не отними от меня.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22993, 'rst66', 'psa', 50, 14, 'Возврати мне радость спасения Твоего и Духом владычественным утверди меня.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22994, 'rst66', 'psa', 50, 15, 'Научу беззаконных путям Твоим, и нечестивые к Тебе обратятся.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22995, 'rst66', 'psa', 50, 16, 'Избавь меня от кровей, Боже, Боже спасения моего, и язык мой восхвалит правду Твою.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22996, 'rst66', 'psa', 50, 17, 'Господи! отверзи уста мои, и уста мои возвестят хвалу Твою:');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22997, 'rst66', 'psa', 50, 18, 'ибо жертвы Ты не желаешь, &mdash; я дал бы ее; к всесожжению не благоволишь.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22998, 'rst66', 'psa', 50, 19, 'Жертва Богу &mdash; дух сокрушенный; сердца сокрушенного и смиренного Ты не презришь, Боже.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (22999, 'rst66', 'psa', 50, 20, 'Облагодетельствуй по благоволению Твоему Сион; воздвигни стены Иерусалима:');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23000, 'rst66', 'psa', 50, 21, 'тогда благоугодны будут Тебе жертвы правды, возношение и всесожжение; тогда возложат на алтарь Твой тельцов.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23001, 'rst66', 'psa', 51, 1, 'Начальнику хора. Учение Давида,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23002, 'rst66', 'psa', 51, 2, 'после того, как приходил Доик Идумеянин и донес Саулу и сказал ему, что Давид пришел в дом Ахимелеха.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23003, 'rst66', 'psa', 51, 3, 'Что хвалишься злодейством, сильный? милость Божия всегда <i>со мною</i>;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23004, 'rst66', 'psa', 51, 4, 'гибель вымышляет язык твой; как изощренная бритва, он <i>у тебя</i>, коварный!');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23005, 'rst66', 'psa', 51, 5, 'Ты любишь больше зло, нежели добро, больше ложь, нежели говорить правду;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23006, 'rst66', 'psa', 51, 6, 'ты любишь всякие гибельные речи, язык коварный:');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23007, 'rst66', 'psa', 51, 7, 'За то Бог сокрушит тебя вконец, изринет тебя и исторгнет тебя из жилища <i>твоего</i> и корень твой из земли живых.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23008, 'rst66', 'psa', 51, 8, 'Увидят праведники и убоятся, посмеются над ним <i>и скажут</i>:');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23009, 'rst66', 'psa', 51, 9, '«Вот человек, который не в Боге полагал крепость свою, а надеялся на множество богатства своего, укреплялся в злодействе своем».');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23010, 'rst66', 'psa', 51, 10, 'А я, как зеленеющая маслина, в доме Божием, и уповаю на милость Божию во веки веков,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23011, 'rst66', 'psa', 51, 11, 'вечно буду славить Тебя за то, что Ты соделал, и уповать на имя Твое, ибо оно благо пред святыми Твоими.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23012, 'rst66', 'psa', 52, 1, 'Начальнику хора. На духовом <i>орудии</i>. Учение Давида.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23013, 'rst66', 'psa', 52, 2, 'Сказал безумец в сердце своем: «нет Бога». Развратились они и совершили гнусные преступления; нет делающего добро.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23014, 'rst66', 'psa', 52, 3, 'Бог с небес призрел на сынов человеческих, чтобы видеть, есть ли разумеющий, ищущий Бога.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23015, 'rst66', 'psa', 52, 4, 'Все уклонились, сделались равно непотребными; нет делающего добро, нет ни одного.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23016, 'rst66', 'psa', 52, 5, 'Неужели не вразумятся делающие беззаконие, съедающие народ мой, <i>как</i> едят хлеб, и не призывающие Бога?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23017, 'rst66', 'psa', 52, 6, 'Там убоятся они страха, где нет страха, ибо рассыплет Бог кости ополчающихся против тебя. Ты постыдишь их, потому что Бог отверг их.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23018, 'rst66', 'psa', 52, 7, 'Кто даст с Сиона спасение Израилю! Когда Бог возвратит пленение народа Своего, тогда возрадуется Иаков и возвеселится Израиль.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23019, 'rst66', 'psa', 53, 1, 'Начальнику хора. На струнных <i>орудиях</i>. Учение Давида,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23020, 'rst66', 'psa', 53, 2, 'когда пришли Зифеи и сказали Саулу: «не у нас ли скрывается Давид?»');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23021, 'rst66', 'psa', 53, 3, 'Боже! именем Твоим спаси меня, и силою Твоею суди меня.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23022, 'rst66', 'psa', 53, 4, 'Боже! услышь молитву мою, внемли словам уст моих,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23023, 'rst66', 'psa', 53, 5, 'ибо чужие восстали на меня, и сильные ищут души моей; они не имеют Бога пред собою.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23024, 'rst66', 'psa', 53, 6, 'Вот, Бог &mdash; помощник мой; Господь подкрепляет душу мою.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23025, 'rst66', 'psa', 53, 7, 'Он воздаст за зло врагам моим; истиною Твоею истреби их.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23026, 'rst66', 'psa', 53, 8, 'Я усердно принесу Тебе жертву, прославлю имя Твое, Господи, ибо оно благо,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23027, 'rst66', 'psa', 53, 9, 'ибо Ты избавил меня от всех бед, и на врагов моих смотрело око мое.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23028, 'rst66', 'psa', 54, 1, 'Начальнику хора. На струнных <i>орудиях</i>. Учение Давида.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23029, 'rst66', 'psa', 54, 2, 'Услышь, Боже, молитву мою и не скрывайся от моления моего;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23030, 'rst66', 'psa', 54, 3, 'внемли мне и услышь меня; я стенаю в горести моей, и смущаюсь');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23031, 'rst66', 'psa', 54, 4, 'от голоса врага, от притеснения нечестивого, ибо они возводят на меня беззаконие и в гневе враждуют против меня.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23032, 'rst66', 'psa', 54, 5, 'Сердце мое трепещет во мне, и смертные ужасы напали на меня;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23033, 'rst66', 'psa', 54, 6, 'страх и трепет нашел на меня, и ужас объял меня.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23034, 'rst66', 'psa', 54, 7, 'И я сказал: «кто дал бы мне крылья, как у голубя? я улетел бы и успокоился бы;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23035, 'rst66', 'psa', 54, 8, 'далеко удалился бы я, и оставался бы в пустыне;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23036, 'rst66', 'psa', 54, 9, 'поспешил бы укрыться от вихря, от бури».');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23037, 'rst66', 'psa', 54, 10, 'Расстрой, Господи, и раздели языки их, ибо я вижу насилие и распри в городе;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23038, 'rst66', 'psa', 54, 11, 'днем и ночью ходят они кругом по стенам его; злодеяния и бедствие посреди его;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23039, 'rst66', 'psa', 54, 12, 'посреди его пагуба; обман и коварство не сходят с улиц его:');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23040, 'rst66', 'psa', 54, 13, 'ибо не враг поносит меня, &mdash; это я перенес бы; не ненавистник мой величается надо мною, &mdash; от него я укрылся бы;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23041, 'rst66', 'psa', 54, 14, 'но ты, который был для меня то же, что я, друг мой и близкий мой,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23042, 'rst66', 'psa', 54, 15, 'с которым мы разделяли искренние беседы и ходили вместе в дом Божий.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23043, 'rst66', 'psa', 54, 16, 'Да найдет на них смерть; да сойдут они живыми в ад, ибо злодейство в жилищах их, посреди их.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23044, 'rst66', 'psa', 54, 17, 'Я же воззову к Богу, и Господь спасет меня.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23045, 'rst66', 'psa', 54, 18, 'Вечером и утром и в полдень буду умолять и вопиять, и Он услышит голос мой,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23046, 'rst66', 'psa', 54, 19, 'избавит в мире душу мою от восстающих на меня, ибо их много у меня;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23047, 'rst66', 'psa', 54, 20, 'Услышит Бог, и смирит их от века Живущий, потому что нет в них перемены; они не боятся Бога,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23048, 'rst66', 'psa', 54, 21, 'простерли руки свои на тех, которые с ними в мире, нарушили союз свой;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23049, 'rst66', 'psa', 54, 22, 'уста их мягче масла, а в сердце их вражда; слова их нежнее елея, но они суть обнаженные мечи.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23050, 'rst66', 'psa', 54, 23, 'Возложи на Господа заботы твои, и Он поддержит тебя. Никогда не даст Он поколебаться праведнику.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23051, 'rst66', 'psa', 54, 24, 'Ты, Боже, низведешь их в ров погибели; кровожадные и коварные не доживут и до половины дней своих. А я на Тебя, <i>Господи,</i> уповаю.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23052, 'rst66', 'psa', 55, 1, 'Начальнику хора. О голубице, безмолвствующей в удалении. Писание Давида, когда Филистимляне захватили его в Гефе.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23053, 'rst66', 'psa', 55, 2, 'Помилуй меня, Боже! ибо человек хочет поглотить меня; нападая всякий день, теснит меня.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23054, 'rst66', 'psa', 55, 3, 'Враги мои всякий день ищут поглотить меня, ибо много восстающих на меня, о Всевышний!');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23055, 'rst66', 'psa', 55, 4, 'Когда я в страхе, на Тебя я уповаю.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23056, 'rst66', 'psa', 55, 5, 'В Боге восхвалю я слово Его; на Бога уповаю, не боюсь; что сделает мне плоть?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23057, 'rst66', 'psa', 55, 6, 'Всякий день извращают слова мои; все помышления их обо мне &mdash; на зло:');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23058, 'rst66', 'psa', 55, 7, 'собираются, притаиваются, наблюдают за моими пятами, чтобы уловить душу мою.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23059, 'rst66', 'psa', 55, 8, 'Неужели они избегнут воздаяния за неправду <i>свою</i>? Во гневе низложи, Боже, народы.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23060, 'rst66', 'psa', 55, 9, 'У Тебя исчислены мои скитания; положи слезы мои в сосуд у Тебя, &mdash; не в книге ли они Твоей?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23061, 'rst66', 'psa', 55, 10, 'Враги мои обращаются назад, когда я взываю к Тебе, из этого я узнаю, что Бог за меня.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23062, 'rst66', 'psa', 55, 11, 'В Боге восхвалю я слово <i>Его</i>, в Господе восхвалю слово <i>Его</i>.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23063, 'rst66', 'psa', 55, 12, 'На Бога уповаю, не боюсь; что сделает мне человек?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23064, 'rst66', 'psa', 55, 13, 'На мне, Боже, обеты Тебе; Тебе воздам хвалы,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23065, 'rst66', 'psa', 55, 14, 'ибо Ты избавил душу мою от смерти, да и ноги мои от преткновения, чтобы я ходил пред лицом Божиим во свете живых.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23066, 'rst66', 'psa', 56, 1, 'Начальнику хора. Не погуби. Писание Давида, когда он убежал от Саула в пещеру.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23067, 'rst66', 'psa', 56, 2, 'Помилуй меня, Боже, помилуй меня, ибо на Тебя уповает душа моя, и в тени крыл Твоих я укроюсь, доколе не пройдут беды.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23068, 'rst66', 'psa', 56, 3, 'Воззову к Богу Всевышнему, Богу, благодетельствующему мне;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23069, 'rst66', 'psa', 56, 4, 'Он пошлет с небес и спасет меня; посрамит ищущего поглотить меня; пошлет Бог милость Свою и истину Свою.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23070, 'rst66', 'psa', 56, 5, 'Душа моя среди львов; я лежу среди дышащих пламенем, среди сынов человеческих, у которых зубы &mdash; копья и стрелы, и у которых язык &mdash; острый меч.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23071, 'rst66', 'psa', 56, 6, 'Будь превознесен выше небес, Боже, и над всею землею да будет слава Твоя!');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23072, 'rst66', 'psa', 56, 7, 'Приготовили сеть ногам моим; душа моя поникла; выкопали предо мною яму, и <i>сами</i> упали в нее.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23073, 'rst66', 'psa', 56, 8, 'Готово сердце мое, Боже, готово сердце мое: буду петь и славить.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23074, 'rst66', 'psa', 56, 9, 'Воспрянь, слава моя, воспрянь, псалтирь и гусли! Я встану рано.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23075, 'rst66', 'psa', 56, 10, 'Буду славить Тебя, Господи, между народами; буду воспевать Тебя среди племен,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23076, 'rst66', 'psa', 56, 11, 'ибо до небес велика милость Твоя и до облаков истина Твоя.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23077, 'rst66', 'psa', 56, 12, 'Будь превознесен выше небес, Боже, и над всею землею да будет слава Твоя!');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23078, 'rst66', 'psa', 57, 1, 'Начальнику хора. Не погуби. Писание Давида.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23079, 'rst66', 'psa', 57, 2, 'Подлинно ли правду говорите вы, судьи, и справедливо судите, сыны человеческие?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23080, 'rst66', 'psa', 57, 3, 'Беззаконие составляете в сердце, кладете на весы злодеяния рук ваших на земле.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23081, 'rst66', 'psa', 57, 4, 'С самого рождения отступили нечестивые, от утробы <i>матери</i> заблуждаются, говоря ложь.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23082, 'rst66', 'psa', 57, 5, 'Яд у них &mdash; как яд змеи, как глухого аспида, который затыкает уши свои');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23083, 'rst66', 'psa', 57, 6, 'и не слышит голоса заклинателя, самого искусного в заклинаниях.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23084, 'rst66', 'psa', 57, 7, 'Боже! сокруши зубы их в устах их; разбей, Господи, челюсти львов!');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23085, 'rst66', 'psa', 57, 8, 'Да исчезнут, как вода протекающая; когда напрягут стрелы, пусть они будут как переломленные.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23086, 'rst66', 'psa', 57, 9, 'Да исчезнут, как распускающаяся улитка; да не видят солнца, как выкидыш женщины.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23087, 'rst66', 'psa', 57, 10, 'Прежде нежели котлы ваши ощутят горящий терн, и свежее и обгоревшее да разнесет вихрь.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23088, 'rst66', 'psa', 57, 11, 'Возрадуется праведник, когда увидит отмщение; омоет стопы свои в крови нечестивого.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23089, 'rst66', 'psa', 57, 12, 'И скажет человек: «подлинно есть плод праведнику! итак, есть Бог, судящий на земле!»');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23090, 'rst66', 'psa', 58, 1, 'Начальнику хора. Не погуби. Писание Давида, когда Саул послал стеречь дом его, чтобы умертвить его.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23091, 'rst66', 'psa', 58, 2, 'Избавь меня от врагов моих, Боже мой! защити меня от восстающих на меня;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23092, 'rst66', 'psa', 58, 3, 'избавь меня от делающих беззаконие; спаси от кровожадных,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23093, 'rst66', 'psa', 58, 4, 'ибо вот, они подстерегают душу мою; собираются на меня сильные не за преступление мое и не за грех мой, Господи;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23094, 'rst66', 'psa', 58, 5, 'без вины <i>моей</i> сбегаются и вооружаются; подвигнись на помощь мне и воззри.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23095, 'rst66', 'psa', 58, 6, 'Ты, Господи, Боже сил, Боже Израилев, восстань посетить все народы, не пощади ни одного из нечестивых беззаконников:');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23096, 'rst66', 'psa', 58, 7, 'вечером возвращаются они, воют, как псы, и ходят вокруг города;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23097, 'rst66', 'psa', 58, 8, 'вот они изрыгают хулу языком своим; в устах их мечи: «ибо», <i>думают они</i>, «кто слышит?»');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23098, 'rst66', 'psa', 58, 9, 'Но Ты, Господи, посмеешься над ними; Ты посрамишь все народы.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23099, 'rst66', 'psa', 58, 10, 'Сила &mdash; у них, но я к Тебе прибегаю, ибо Бог &mdash; заступник мой.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23100, 'rst66', 'psa', 58, 11, 'Бог мой, милующий меня, предварит меня; Бог даст мне смотреть на врагов моих.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23101, 'rst66', 'psa', 58, 12, 'Не умерщвляй их, чтобы не забыл народ мой; расточи их силою Твоею и низложи их, Господи, защитник наш.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23102, 'rst66', 'psa', 58, 13, 'Слово языка их есть грех уст их, да уловятся они в гордости своей за клятву и ложь, которую произносят.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23103, 'rst66', 'psa', 58, 14, 'Расточи их во гневе, расточи, чтобы их не было; и да познают, что Бог владычествует над Иаковом до пределов земли.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23104, 'rst66', 'psa', 58, 15, 'Пусть возвращаются вечером, воют, как псы, и ходят вокруг города;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23105, 'rst66', 'psa', 58, 16, 'пусть бродят, чтобы найти пищу, и несытые проводят ночи.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23106, 'rst66', 'psa', 58, 17, 'А я буду воспевать силу Твою и с раннего утра провозглашать милость Твою, ибо Ты был мне защитою и убежищем в день бедствия моего.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23107, 'rst66', 'psa', 58, 18, 'Сила моя! Тебя буду воспевать я, ибо Бог &mdash; заступник мой, Бог мой, милующий меня.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23108, 'rst66', 'psa', 59, 1, 'Начальнику хора. На <i>музыкальном орудии</i> Шушан‐Эдуф. Писание Давида для изучения,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23109, 'rst66', 'psa', 59, 2, 'когда он воевал с Сириею Месопотамскою и с Сириею Цованскою, и когда Иоав, возвращаясь, поразил двенадцать тысяч Идумеев в долине Соляной.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23110, 'rst66', 'psa', 59, 3, 'Боже! Ты отринул нас, Ты сокрушил нас, Ты прогневался: обратись к нам.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23111, 'rst66', 'psa', 59, 4, 'Ты потряс землю, разбил ее: исцели повреждения ее, ибо она колеблется.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23112, 'rst66', 'psa', 59, 5, 'Ты дал испытать народу твоему жестокое, напоил нас вином изумления.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23113, 'rst66', 'psa', 59, 6, 'Даруй боящимся Тебя знамя, чтобы они подняли его ради истины,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23114, 'rst66', 'psa', 59, 7, 'чтобы избавились возлюбленные Твои; спаси десницею Твоею и услышь меня.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23115, 'rst66', 'psa', 59, 8, 'Бог сказал во святилище Своем: «восторжествую, разделю Сихем и долину Сокхоф размерю:');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23116, 'rst66', 'psa', 59, 9, 'Мой Галаад, Мой Манассия, Ефрем крепость главы Моей, Иуда скипетр Мой,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23117, 'rst66', 'psa', 59, 10, 'Моав умывальная чаша Моя; на Едома простру сапог Мой. Восклицай Мне, земля Филистимская!»');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23118, 'rst66', 'psa', 59, 11, 'Кто введет меня в укрепленный город? Кто доведет меня до Едома?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23119, 'rst66', 'psa', 59, 12, 'Не Ты ли, Боже, <i>Который</i> отринул нас, и не выходишь, Боже, с войсками нашими?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23120, 'rst66', 'psa', 59, 13, 'Подай нам помощь в тесноте, ибо защита человеческая суетна.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23121, 'rst66', 'psa', 59, 14, 'С Богом мы окажем силу, Он низложит врагов наших.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23122, 'rst66', 'psa', 60, 1, 'Начальнику хора. На струнном <i>орудии</i>. Псалом Давида.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23123, 'rst66', 'psa', 60, 2, 'Услышь, Боже, вопль мой, внемли молитве моей!');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23124, 'rst66', 'psa', 60, 3, 'От конца земли взываю к Тебе в унынии сердца моего; возведи меня на скалу, для меня недосягаемую,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23125, 'rst66', 'psa', 60, 4, 'ибо Ты прибежище мое, Ты крепкая защита от врага.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23126, 'rst66', 'psa', 60, 5, 'Да живу я вечно в жилище Твоем и покоюсь под кровом крыл Твоих,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23127, 'rst66', 'psa', 60, 6, 'ибо Ты, Боже, услышал обеты мои и дал <i>мне</i> наследие боящихся имени Твоего.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23128, 'rst66', 'psa', 60, 7, 'Приложи дни ко дням царя, лета его <i>продли</i> в род и род,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23129, 'rst66', 'psa', 60, 8, 'да пребудет он вечно пред Богом; заповедуй милости и истине охранять его.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23130, 'rst66', 'psa', 60, 9, 'И я буду петь имени Твоему вовек, исполняя обеты мои всякий день.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23131, 'rst66', 'psa', 61, 1, 'Начальнику хора Идифумова. Псалом Давида.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23132, 'rst66', 'psa', 61, 2, 'Только в Боге успокаивается душа моя: от Него спасение мое.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23133, 'rst66', 'psa', 61, 3, 'Только Он &mdash; твердыня моя, спасение мое, убежище мое: не поколеблюсь более.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23134, 'rst66', 'psa', 61, 4, 'Доколе вы будете налегать на человека? Вы будете низринуты, все вы, как наклонившаяся стена, как ограда пошатнувшаяся.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23135, 'rst66', 'psa', 61, 5, 'Они задумали свергнуть его с высоты, прибегли ко лжи; устами благословляют, а в сердце своем клянут.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23136, 'rst66', 'psa', 61, 6, 'Только в Боге успокаивайся, душа моя! ибо на Него надежда моя.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23137, 'rst66', 'psa', 61, 7, 'Только Он &mdash; твердыня моя и спасение мое, убежище мое: не поколеблюсь.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23138, 'rst66', 'psa', 61, 8, 'В Боге спасение мое и слава моя; крепость силы моей и упование мое в Боге.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23139, 'rst66', 'psa', 61, 9, 'Народ! надейтесь на Него во всякое время; изливайте пред Ним сердце ваше: Бог нам прибежище.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23140, 'rst66', 'psa', 61, 10, 'Сыны человеческие &mdash; только суета; сыны мужей &mdash; ложь; если положить их на весы, все они вместе легче пустоты.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23141, 'rst66', 'psa', 61, 11, 'Не надейтесь на грабительство и не тщеславьтесь хищением; когда богатство умножается, не прилагайте <i>к нему</i> сердца.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23142, 'rst66', 'psa', 61, 12, 'Однажды сказал Бог, и дважды слышал я это, что сила у Бога,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23143, 'rst66', 'psa', 61, 13, 'и у Тебя, Господи, милость, ибо Ты воздаешь каждому по делам его.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23144, 'rst66', 'psa', 62, 1, 'Псалом Давида, когда он был в пустыне Иудейской.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23145, 'rst66', 'psa', 62, 2, 'Боже! Ты Бог мой, Тебя от ранней зари ищу я; Тебя жаждет душа моя, по Тебе томится плоть моя в земле пустой, иссохшей и безводной,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23146, 'rst66', 'psa', 62, 3, 'чтобы видеть силу Твою и славу Твою, как я видел Тебя во святилище:');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23147, 'rst66', 'psa', 62, 4, 'ибо милость Твоя лучше, нежели жизнь. Уста мои восхвалят Тебя.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23148, 'rst66', 'psa', 62, 5, 'Так благословлю Тебя в жизни моей; во имя Твое вознесу руки мои.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23149, 'rst66', 'psa', 62, 6, 'Как туком и елеем насыщается душа моя, и радостным гласом восхваляют Тебя уста мои,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23150, 'rst66', 'psa', 62, 7, 'когда я вспоминаю о Тебе на постели моей, размышляю о Тебе в <i>ночные</i> стражи,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23151, 'rst66', 'psa', 62, 8, 'ибо Ты помощь моя, и в тени крыл Твоих я возрадуюсь;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23152, 'rst66', 'psa', 62, 9, 'к Тебе прилепилась душа моя; десница Твоя поддерживает меня.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23153, 'rst66', 'psa', 62, 10, 'А те, которые ищут погибели душе моей, сойдут в преисподнюю земли;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23154, 'rst66', 'psa', 62, 11, 'сразят их силою меча; достанутся они в добычу лисицам.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23155, 'rst66', 'psa', 62, 12, 'Царь же возвеселится о Боге, восхвален будет всякий, клянущийся Им, ибо заградятся уста говорящих неправду.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23156, 'rst66', 'psa', 63, 1, 'Начальнику хора. Псалом Давида.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23157, 'rst66', 'psa', 63, 2, 'Услышь, Боже, голос мой в молитве моей, сохрани жизнь мою от страха врага;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23158, 'rst66', 'psa', 63, 3, 'укрой меня от замысла коварных, от мятежа злодеев,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23159, 'rst66', 'psa', 63, 4, 'которые изострили язык свой, как меч; напрягли лук свой &mdash; язвительное слово,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23160, 'rst66', 'psa', 63, 5, 'чтобы втайне стрелять в непорочного; они внезапно стреляют в него и не боятся.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23161, 'rst66', 'psa', 63, 6, 'Они утвердились в злом намерении, совещались скрыть сеть, говорили: кто их увидит?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23162, 'rst66', 'psa', 63, 7, 'Изыскивают неправду, делают расследование за расследованием даже до внутренней жизни человека и до глубины сердца.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23163, 'rst66', 'psa', 63, 8, 'Но поразит их Бог стрелою: внезапно будут они уязвлены;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23164, 'rst66', 'psa', 63, 9, 'языком своим они поразят самих себя; все, видящие их, удалятся <i>от них</i>.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23165, 'rst66', 'psa', 63, 10, 'И убоятся все человеки, и возвестят дело Божие, и уразумеют, что это Его дело.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23166, 'rst66', 'psa', 63, 11, 'А праведник возвеселится о Господе и будет уповать на Него; и похвалятся все правые сердцем.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23167, 'rst66', 'psa', 64, 1, 'Начальнику хора. Псалом Давида для пения.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23168, 'rst66', 'psa', 64, 2, 'Тебе, Боже, принадлежит хвала на Сионе, и Тебе воздастся обет <i>в Иерусалиме</i>.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23169, 'rst66', 'psa', 64, 3, 'Ты слышишь молитву; к Тебе прибегает всякая плоть.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23170, 'rst66', 'psa', 64, 4, 'Дела беззаконий превозмогают меня; Ты очистишь преступления наши.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23171, 'rst66', 'psa', 64, 5, 'Блажен, кого Ты избрал и приблизил, чтобы он жил во дворах Твоих. Насытимся благами дома Твоего, святого храма Твоего.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23172, 'rst66', 'psa', 64, 6, 'Страшный в правосудии, услышь нас, Боже, Спаситель наш, упование всех концов земли и находящихся в море далеко,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23173, 'rst66', 'psa', 64, 7, 'поставивший горы силою Своею, препоясанный могуществом,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23174, 'rst66', 'psa', 64, 8, 'укрощающий шум морей, шум волн их и мятеж народов!');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23175, 'rst66', 'psa', 64, 9, 'И убоятся знамений Твоих живущие на пределах <i>земли</i>. Утро и вечер возбудишь к славе <i>Твоей</i>.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23176, 'rst66', 'psa', 64, 10, 'Ты посещаешь землю и утоляешь жажду ее, обильно обогащаешь ее: поток Божий полон воды; Ты приготовляешь хлеб, ибо так устроил ее;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23177, 'rst66', 'psa', 64, 11, 'напояешь борозды ее, уравниваешь глыбы ее, размягчаешь ее каплями дождя, благословляешь произрастания ее;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23178, 'rst66', 'psa', 64, 12, 'венчаешь лето благости Твоей, и стези Твои источают тук,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23179, 'rst66', 'psa', 64, 13, 'источают на пустынные пажити, и холмы препоясываются радостью;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23180, 'rst66', 'psa', 64, 14, 'луга одеваются стадами, и долины покрываются хлебом, восклицают и поют.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23181, 'rst66', 'psa', 65, 0, 'Начальнику хора. Песнь.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23182, 'rst66', 'psa', 65, 1, 'Воскликните Богу, вся земля.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23183, 'rst66', 'psa', 65, 2, 'Пойте славу имени Его, воздайте славу, хвалу Ему.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23184, 'rst66', 'psa', 65, 3, 'Скажите Богу: как страшен Ты в делах Твоих! По множеству силы Твоей, покорятся Тебе враги Твои.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23185, 'rst66', 'psa', 65, 4, 'Вся земля да поклонится Тебе и поет Тебе, да поет имени Твоему!');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23186, 'rst66', 'psa', 65, 5, 'Придите и воззрите на дела Бога, страшного в делах над сынами человеческими.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23187, 'rst66', 'psa', 65, 6, 'Он превратил море в сушу; через реку перешли стопами, там веселились мы о Нем.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23188, 'rst66', 'psa', 65, 7, 'Могуществом Своим владычествует Он вечно; очи Его зрят на народы, да не возносятся мятежники.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23189, 'rst66', 'psa', 65, 8, 'Благословите, народы, Бога нашего и провозгласите хвалу Ему.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23190, 'rst66', 'psa', 65, 9, 'Он сохранил душе нашей жизнь и ноге нашей не дал поколебаться.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23191, 'rst66', 'psa', 65, 10, 'Ты испытал нас, Боже, переплавил нас, как переплавляют серебро.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23192, 'rst66', 'psa', 65, 11, 'Ты ввел нас в сеть, положил оковы на чресла наши,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23193, 'rst66', 'psa', 65, 12, 'посадил человека на главу нашу. Мы вошли в огонь и в воду, и Ты вывел нас на свободу.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23194, 'rst66', 'psa', 65, 13, 'Войду в дом Твой со всесожжениями, воздам Тебе обеты мои,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23195, 'rst66', 'psa', 65, 14, 'которые произнесли уста мои и изрек язык мой в скорби моей.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23196, 'rst66', 'psa', 65, 15, 'Всесожжения тучные вознесу Тебе с воскурением тука овнов, принесу в жертву волов и козлов.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23197, 'rst66', 'psa', 65, 16, 'Придите, послушайте, все боящиеся Бога, и я возвещу <i>вам</i>, что сотворил Он для души моей.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23198, 'rst66', 'psa', 65, 17, 'Я воззвал к Нему устами моими и превознес Его языком моим.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23199, 'rst66', 'psa', 65, 18, 'Если бы я видел беззаконие в сердце моем, то не услышал бы меня Господь.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23200, 'rst66', 'psa', 65, 19, 'Но Бог услышал, внял гласу моления моего.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23201, 'rst66', 'psa', 65, 20, 'Благословен Бог, Который не отверг молитвы моей и не отвратил от меня милости Своей.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23202, 'rst66', 'psa', 66, 1, 'Начальнику хора. На струнных <i>орудиях</i>. Псалом. Песнь.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23203, 'rst66', 'psa', 66, 2, 'Боже! будь милостив к нам и благослови нас, освети нас лицом Твоим,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23204, 'rst66', 'psa', 66, 3, 'дабы познали на земле путь Твой, во всех народах спасение Твое.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23205, 'rst66', 'psa', 66, 4, 'Да восхвалят Тебя народы, Боже; да восхвалят Тебя народы все.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23206, 'rst66', 'psa', 66, 5, 'Да веселятся и радуются племена, ибо Ты судишь народы праведно и управляешь на земле племенами.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23207, 'rst66', 'psa', 66, 6, 'Да восхвалят Тебя народы, Боже, да восхвалят Тебя народы все.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23208, 'rst66', 'psa', 66, 7, 'Земля дала плод свой; да благословит нас Бог, Бог наш.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23209, 'rst66', 'psa', 66, 8, 'Да благословит нас Бог, и да убоятся Его все пределы земли.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23210, 'rst66', 'psa', 67, 1, 'Начальнику хора. Псалом Давида. Песнь.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23211, 'rst66', 'psa', 67, 2, 'Да восстанет Бог, и расточатся враги Его, и да бегут от лица Его ненавидящие Его.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23212, 'rst66', 'psa', 67, 3, 'Как рассеивается дым, Ты рассей их; как тает воск от огня, так нечестивые да погибнут от лица Божия.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23213, 'rst66', 'psa', 67, 4, 'А праведники да возвеселятся, да возрадуются пред Богом и восторжествуют в радости.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23214, 'rst66', 'psa', 67, 5, 'Пойте Богу нашему, пойте имени Его, превозносите Шествующего на небесах; имя Ему: Господь, и радуйтесь пред лицом Его.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23215, 'rst66', 'psa', 67, 6, 'Отец сирот и судья вдов Бог во святом Своем жилище.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23216, 'rst66', 'psa', 67, 7, 'Бог одиноких вводит в дом, освобождает узников от оков, а непокорные остаются в знойной пустыне.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23217, 'rst66', 'psa', 67, 8, 'Боже! когда Ты выходил пред народом Твоим, когда Ты шествовал пустынею,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23218, 'rst66', 'psa', 67, 9, 'земля тряслась, даже небеса таяли от лица Божия, и этот Синай &mdash; от лица Бога, Бога Израилева.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23219, 'rst66', 'psa', 67, 10, 'Обильный дождь проливал Ты, Боже, на наследие Твое, и когда оно изнемогало от труда, Ты подкреплял его.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23220, 'rst66', 'psa', 67, 11, 'Народ Твой обитал там; по благости Твоей, Боже, Ты готовил <i>необходимое</i> для бедного.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23221, 'rst66', 'psa', 67, 12, 'Господь даст слово: провозвестниц великое множество.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23222, 'rst66', 'psa', 67, 13, 'Цари воинств бегут, бегут, а сидящая дома делит добычу.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23223, 'rst66', 'psa', 67, 14, 'Расположившись в уделах <i>своих</i>, вы стали, как голубица, которой крылья покрыты серебром, а перья &mdash; чистым золотом:');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23224, 'rst66', 'psa', 67, 15, 'когда Всемогущий рассеял царей на сей <i>земле</i>, она забелела, как снег на Селмоне.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23225, 'rst66', 'psa', 67, 16, 'Гора Божия &mdash; гора Васанская! гора высокая &mdash; гора Васанская!');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23226, 'rst66', 'psa', 67, 17, 'Что вы завистливо смотрите, горы высокие, на гору, на которой Бог благоволит обитать и будет Господь обитать вечно?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23227, 'rst66', 'psa', 67, 18, 'Колесниц Божиих тьмы, тысячи тысяч; среди их Господь на Синае, во святилище.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23228, 'rst66', 'psa', 67, 19, 'Ты восшел на высоту, пленил плен, принял дары для человеков, так чтоб и из противящихся могли обитать у Господа Бога.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23229, 'rst66', 'psa', 67, 20, 'Благословен Господь всякий день. Бог возлагает на нас бремя, но Он же и спасает нас.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23230, 'rst66', 'psa', 67, 21, 'Бог для нас &mdash; Бог во спасение; во власти Господа Вседержителя врата смерти.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23231, 'rst66', 'psa', 67, 22, 'Но Бог сокрушит голову врагов Своих, волосатое темя закоснелого в своих беззакониях.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23232, 'rst66', 'psa', 67, 23, 'Господь сказал: «от Васана возвращу, выведу из глубины морской,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23233, 'rst66', 'psa', 67, 24, 'чтобы ты погрузил ногу твою, как и псы твои язык свой, в крови врагов».');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23234, 'rst66', 'psa', 67, 25, 'Видели шествие Твое, Боже, шествие Бога моего, Царя моего во святыне:');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23235, 'rst66', 'psa', 67, 26, 'впереди шли поющие, позади играющие на орудиях, в средине девы с тимпанами:');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23236, 'rst66', 'psa', 67, 27, '«В собраниях благословите <i>Бога Господа</i>, вы &mdash; от семени Израилева!»');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23237, 'rst66', 'psa', 67, 28, 'Там Вениамин младший &mdash; князь их; князья Иудины &mdash; владыки их, князья Завулоновы, князья Неффалимовы.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23238, 'rst66', 'psa', 67, 29, 'Бог твой предназначил тебе силу. Утверди, Боже, то, что Ты соделал для нас!');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23239, 'rst66', 'psa', 67, 30, 'Ради храма Твоего в Иерусалиме цари принесут Тебе дары.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23240, 'rst66', 'psa', 67, 31, 'Укроти зверя в тростнике, стадо волов среди тельцов народов, хвалящихся слитками серебра; рассыпь народы, желающие браней.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23241, 'rst66', 'psa', 67, 32, 'Придут вельможи из Египта; Ефиопия прострет руки свои к Богу.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23242, 'rst66', 'psa', 67, 33, 'Царства земные! пойте Богу, воспевайте Господа,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23243, 'rst66', 'psa', 67, 34, 'шествующего на небесах небес от века. Вот, Он дает гласу Своему глас силы.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23244, 'rst66', 'psa', 67, 35, 'Воздайте славу Богу! величие Его &mdash; над Израилем, и могущество Его &mdash; на облаках.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23245, 'rst66', 'psa', 67, 36, 'Страшен Ты, Боже, во святилище Твоем. Бог Израилев &mdash; Он дает силу и крепость народу <i>Своему</i>. Благословен Бог!');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23246, 'rst66', 'psa', 68, 1, 'Начальнику хора. На Шошанниме. Псалом Давида.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23247, 'rst66', 'psa', 68, 2, 'Спаси меня, Боже, ибо воды дошли до души <i>моей</i>.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23248, 'rst66', 'psa', 68, 3, 'Я погряз в глубоком болоте, и не на чем стать; вошел во глубину вод, и быстрое течение их увлекает меня.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23249, 'rst66', 'psa', 68, 4, 'Я изнемог от вопля, засохла гортань моя, истомились глаза мои от ожидания Бога <i>моего</i>.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23250, 'rst66', 'psa', 68, 5, 'Ненавидящих меня без вины больше, нежели волос на голове моей; враги мои, преследующие меня несправедливо, усилились; чего я не отнимал, то должен отдать.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23251, 'rst66', 'psa', 68, 6, 'Боже! Ты знаешь безумие мое, и грехи мои не сокрыты от Тебя.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23252, 'rst66', 'psa', 68, 7, 'Да не постыдятся во мне все, надеющиеся на Тебя, Господи, Боже сил. Да не посрамятся во мне ищущие Тебя, Боже Израилев,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23253, 'rst66', 'psa', 68, 8, 'ибо ради Тебя несу я поношение, и бесчестием покрывают лицо мое.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23254, 'rst66', 'psa', 68, 9, 'Чужим стал я для братьев моих и посторонним для сынов матери моей,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23255, 'rst66', 'psa', 68, 10, 'ибо ревность по доме Твоем снедает меня, и злословия злословящих Тебя падают на меня;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23256, 'rst66', 'psa', 68, 11, 'и плачу, постясь душою моею, и это ставят в поношение мне;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23257, 'rst66', 'psa', 68, 12, 'и возлагаю на себя вместо одежды вретище &mdash; и делаюсь для них притчею;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23258, 'rst66', 'psa', 68, 13, 'о мне толкуют сидящие у ворот, и поют в песнях пьющие вино.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23259, 'rst66', 'psa', 68, 14, 'А я с молитвою моею к Тебе, Господи; во время благоугодное, Боже, по великой благости Твоей услышь меня в истине спасения Твоего;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23260, 'rst66', 'psa', 68, 15, 'извлеки меня из тины, чтобы не погрязнуть мне; да избавлюсь от ненавидящих меня и от глубоких вод;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23261, 'rst66', 'psa', 68, 16, 'да не увлечет меня стремление вод, да не поглотит меня пучина, да не затворит надо мною пропасть зева своего.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23262, 'rst66', 'psa', 68, 17, 'Услышь меня, Господи, ибо блага милость Твоя; по множеству щедрот Твоих призри на меня;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23263, 'rst66', 'psa', 68, 18, 'не скрывай лица Твоего от раба Твоего, ибо я скорблю; скоро услышь меня;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23264, 'rst66', 'psa', 68, 19, 'приблизься к душе моей, избавь ее; ради врагов моих спаси меня.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23265, 'rst66', 'psa', 68, 20, 'Ты знаешь поношение мое, стыд мой и посрамление мое: враги мои все пред Тобою.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23266, 'rst66', 'psa', 68, 21, 'Поношение сокрушило сердце мое, и я изнемог, ждал сострадания, но нет его, &mdash; утешителей, но не нахожу.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23267, 'rst66', 'psa', 68, 22, 'И дали мне в пищу желчь, и в жажде моей напоили меня уксусом.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23268, 'rst66', 'psa', 68, 23, 'Да будет трапеза их сетью им, и мирное пиршество их &mdash; западнею;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23269, 'rst66', 'psa', 68, 24, 'да помрачатся глаза их, чтоб им не видеть, и чресла их расслабь навсегда;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23270, 'rst66', 'psa', 68, 25, 'излей на них ярость Твою, и пламень гнева Твоего да обымет их;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23271, 'rst66', 'psa', 68, 26, 'жилище их да будет пусто, и в шатрах их да не будет живущих,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23272, 'rst66', 'psa', 68, 27, 'ибо, кого Ты поразил, они <i>еще</i> преследуют, и страдания уязвленных Тобою умножают.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23273, 'rst66', 'psa', 68, 28, 'Приложи беззаконие к беззаконию их, и да не войдут они в правду Твою;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23274, 'rst66', 'psa', 68, 29, 'да изгладятся они из книги живых и с праведниками да не напишутся.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23275, 'rst66', 'psa', 68, 30, 'А я беден и страдаю; помощь Твоя, Боже, да восставит меня.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23276, 'rst66', 'psa', 68, 31, 'Я буду славить имя Бога <i>моего</i> в песни, буду превозносить Его в славословии,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23277, 'rst66', 'psa', 68, 32, 'и будет это благоугоднее Господу, нежели вол, нежели телец с рогами и с копытами.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23278, 'rst66', 'psa', 68, 33, 'Увидят <i>это</i> страждущие и возрадуются. И оживет сердце ваше, ищущие Бога,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23279, 'rst66', 'psa', 68, 34, 'ибо Господь внемлет нищим и не пренебрегает узников Своих.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23280, 'rst66', 'psa', 68, 35, 'Да восхвалят Его небеса и земля, моря и все движущееся в них;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23281, 'rst66', 'psa', 68, 36, 'ибо спасет Бог Сион, создаст города Иудины, и поселятся там и наследуют его,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23282, 'rst66', 'psa', 68, 37, 'и потомство рабов Его утвердится в нем, и любящие имя Его будут поселяться на нем.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23283, 'rst66', 'psa', 69, 1, 'Начальнику хора. Псалом Давида. В воспоминание.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23284, 'rst66', 'psa', 69, 2, 'Поспеши, Боже, избавить меня, <i>поспеши</i>, Господи, на помощь мне.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23285, 'rst66', 'psa', 69, 3, 'Да постыдятся и посрамятся ищущие души моей! Да будут обращены назад и преданы посмеянию желающие мне зла!');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23286, 'rst66', 'psa', 69, 4, 'Да будут обращены назад за поношение меня говорящие <i>мне</i>: «хорошо! хорошо!»');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23287, 'rst66', 'psa', 69, 5, 'Да возрадуются и возвеселятся о Тебе все, ищущие Тебя, и любящие спасение Твое да говорят непрестанно: «велик Бог!»');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23288, 'rst66', 'psa', 69, 6, 'Я же беден и нищ; Боже, поспеши ко мне! Ты помощь моя и Избавитель мой; Господи! не замедли.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23289, 'rst66', 'psa', 70, 1, 'На Тебя, Господи, уповаю, да не постыжусь вовек.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23290, 'rst66', 'psa', 70, 2, 'По правде Твоей избавь меня и освободи меня; приклони ухо Твое ко мне и спаси меня.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23291, 'rst66', 'psa', 70, 3, 'Будь мне твердым прибежищем, куда я всегда мог бы укрываться; Ты заповедал спасти меня, ибо твердыня моя и крепость моя &mdash; Ты.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23292, 'rst66', 'psa', 70, 4, 'Боже мой! избавь меня из руки нечестивого, из руки беззаконника и притеснителя,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23293, 'rst66', 'psa', 70, 5, 'ибо Ты &mdash; надежда моя, Господи Боже, упование мое от юности моей.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23294, 'rst66', 'psa', 70, 6, 'На Тебе утверждался я от утробы; Ты извел меня из чрева матери моей; Тебе хвала моя не престанет.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23295, 'rst66', 'psa', 70, 7, 'Для многих я был как бы дивом, но Ты твердая моя надежда.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23296, 'rst66', 'psa', 70, 8, 'Да наполнятся уста мои хвалою, <i>чтобы воспевать</i> всякий день великолепие Твое.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23297, 'rst66', 'psa', 70, 9, 'Не отвергни меня во время старости; когда будет оскудевать сила моя, не оставь меня,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23298, 'rst66', 'psa', 70, 10, 'ибо враги мои говорят против меня, и подстерегающие душу мою советуются между собою,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23299, 'rst66', 'psa', 70, 11, 'говоря: «Бог оставил его; преследуйте и схватите его, ибо нет избавляющего».');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23300, 'rst66', 'psa', 70, 12, 'Боже! не удаляйся от меня; Боже мой! поспеши на помощь мне.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23301, 'rst66', 'psa', 70, 13, 'Да постыдятся и исчезнут враждующие против души моей, да покроются стыдом и бесчестием ищущие мне зла!');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23302, 'rst66', 'psa', 70, 14, 'А я всегда буду уповать <i>на Тебя</i> и умножать всякую хвалу Тебе.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23303, 'rst66', 'psa', 70, 15, 'Уста мои будут возвещать правду Твою, всякий день благодеяния Твои; ибо я не знаю им числа.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23304, 'rst66', 'psa', 70, 16, 'Войду в <i>размышление</i> о силах Господа Бога; воспомяну правду Твою &mdash; единственно Твою.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23305, 'rst66', 'psa', 70, 17, 'Боже! Ты наставлял меня от юности моей, и доныне я возвещаю чудеса Твои.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23306, 'rst66', 'psa', 70, 18, 'И до старости, и до седины не оставь меня, Боже, доколе не возвещу силы Твоей роду сему и всем грядущим могущества Твоего.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23307, 'rst66', 'psa', 70, 19, 'Правда Твоя, Боже, до превыспренних; великие дела соделал Ты; Боже, кто подобен Тебе?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23308, 'rst66', 'psa', 70, 20, 'Ты посылал на меня многие и лютые беды, но и опять оживлял меня и из бездн земли опять выводил меня.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23309, 'rst66', 'psa', 70, 21, 'Ты возвышал меня и утешал меня.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23310, 'rst66', 'psa', 70, 22, 'И я буду славить Тебя на псалтири, Твою истину, Боже мой; буду воспевать Тебя на гуслях, Святой Израилев!');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23311, 'rst66', 'psa', 70, 23, 'Радуются уста мои, когда я пою Тебе, и душа моя, которую Ты избавил;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23312, 'rst66', 'psa', 70, 24, 'и язык мой всякий день будет возвещать правду Твою, ибо постыжены и посрамлены ищущие мне зла.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23313, 'rst66', 'psa', 71, 0, 'О Соломоне.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23314, 'rst66', 'psa', 71, 1, 'Боже! даруй царю Твой суд и сыну царя Твою правду,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23315, 'rst66', 'psa', 71, 2, 'да судит праведно людей Твоих и нищих Твоих на суде;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23316, 'rst66', 'psa', 71, 3, 'да принесут горы мир людям и холмы правду;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23317, 'rst66', 'psa', 71, 4, 'да судит нищих народа, да спасет сынов убогого и смирит притеснителя, &mdash;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23318, 'rst66', 'psa', 71, 5, 'и будут бояться Тебя, доколе пребудут солнце и луна, в роды родов.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23319, 'rst66', 'psa', 71, 6, 'Он сойдет, как дождь на скошенный луг, как капли, орошающие землю;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23320, 'rst66', 'psa', 71, 7, 'во дни его процветет праведник, и будет обилие мира, доколе не престанет луна;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23321, 'rst66', 'psa', 71, 8, 'он будет обладать от моря до моря и от реки до концов земли;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23322, 'rst66', 'psa', 71, 9, 'падут пред ним жители пустынь, и враги его будут лизать прах;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23323, 'rst66', 'psa', 71, 10, 'цари Фарсиса и островов поднесут ему дань; цари Аравии и Савы принесут дары;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23324, 'rst66', 'psa', 71, 11, 'и поклонятся ему все цари; все народы будут служить ему;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23325, 'rst66', 'psa', 71, 12, 'Ибо он избавит нищего, вопиющего и угнетенного, у которого нет помощника.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23326, 'rst66', 'psa', 71, 13, 'Будет милосерд к нищему и убогому, и души убогих спасет;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23327, 'rst66', 'psa', 71, 14, 'от коварства и насилия избавит души их, и драгоценна будет кровь их пред очами его;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23328, 'rst66', 'psa', 71, 15, 'И будет жить, и будут давать ему от золота Аравии, и будут молиться о нем непрестанно, всякий день благословлять его;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23329, 'rst66', 'psa', 71, 16, 'будет обилие хлеба на земле, наверху гор; плоды его будут волноваться, как <i>лес</i> на Ливане, и в городах размножатся люди, как трава на земле;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23330, 'rst66', 'psa', 71, 17, 'будет имя его вовек; доколе пребывает солнце, будет передаваться имя его; и благословятся в нем <i>все племена земные</i>, все народы ублажат его.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23331, 'rst66', 'psa', 71, 18, 'Благословен Господь Бог, Бог Израилев, един творящий чудеса,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23332, 'rst66', 'psa', 71, 19, 'и благословенно имя славы Его вовек, и наполнится славою Его вся земля. Аминь и аминь.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23333, 'rst66', 'psa', 71, 20, 'Кончились молитвы Давида, сына Иесеева.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23334, 'rst66', 'psa', 72, 0, 'Псалом Асафа.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23335, 'rst66', 'psa', 72, 1, 'Как благ Бог к Израилю, к чистым сердцем!');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23336, 'rst66', 'psa', 72, 2, 'А я &mdash; едва не пошатнулись ноги мои, едва не поскользнулись стопы мои, &mdash;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23337, 'rst66', 'psa', 72, 3, 'я позавидовал безумным, видя благоденствие нечестивых,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23338, 'rst66', 'psa', 72, 4, 'ибо им нет страданий до смерти их, и крепки силы их;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23339, 'rst66', 'psa', 72, 5, 'на работе человеческой нет их, и с <i>прочими</i> людьми не подвергаются ударам.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23340, 'rst66', 'psa', 72, 6, 'Оттого гордость, как ожерелье, обложила их, и дерзость, <i>как</i> наряд, одевает их;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23341, 'rst66', 'psa', 72, 7, 'выкатились от жира глаза их, бродят помыслы в сердце;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23342, 'rst66', 'psa', 72, 8, 'над всем издеваются, злобно разглашают клевету, говорят свысока;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23343, 'rst66', 'psa', 72, 9, 'поднимают к небесам уста свои, и язык их расхаживает по земле.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23344, 'rst66', 'psa', 72, 10, 'Потому туда же обращается народ Его, и пьют воду полною чашею,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23345, 'rst66', 'psa', 72, 11, 'и говорят: «как узнает Бог? и есть ли ве&#769;дение у Вышнего?»');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23346, 'rst66', 'psa', 72, 12, 'И вот, эти нечестивые благоденствуют в веке сем, умножают богатство.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23347, 'rst66', 'psa', 72, 13, 'Так не напрасно ли я очищал сердце мое и омывал в невинности руки мои,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23348, 'rst66', 'psa', 72, 14, 'и подвергал себя ранам всякий день и обличениям всякое утро?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23349, 'rst66', 'psa', 72, 15, '<i>Но</i> если бы я сказал: «буду рассуждать так», &mdash; то я виновен был бы пред родом сынов Твоих.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23350, 'rst66', 'psa', 72, 16, 'И думал я, как бы уразуметь это, но это трудно было в глазах моих,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23351, 'rst66', 'psa', 72, 17, 'доколе не вошел я во святилище Божие и не уразумел конца их.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23352, 'rst66', 'psa', 72, 18, 'Так! на скользких путях поставил Ты их и низвергаешь их в пропасти.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23353, 'rst66', 'psa', 72, 19, 'Как нечаянно пришли они в разорение, исчезли, погибли от ужасов!');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23354, 'rst66', 'psa', 72, 20, 'Как сновидение по пробуждении, так Ты, Господи, пробудив <i>их</i>, уничтожишь мечты их.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23355, 'rst66', 'psa', 72, 21, 'Когда кипело сердце мое и терзалась внутренность моя,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23356, 'rst66', 'psa', 72, 22, 'тогда я был невежда и не разумел; как скот был я пред Тобою.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23357, 'rst66', 'psa', 72, 23, 'Но я всегда с Тобою: Ты держишь меня за правую руку;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23358, 'rst66', 'psa', 72, 24, 'Ты руководишь меня советом Твоим и потом примешь меня в славу.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23359, 'rst66', 'psa', 72, 25, 'Кто мне на небе? и с Тобою ничего не хочу на земле.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23360, 'rst66', 'psa', 72, 26, 'Изнемогает плоть моя и сердце мое: Бог &mdash; твердыня сердца моего и часть моя вовек.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23361, 'rst66', 'psa', 72, 27, 'Ибо вот, удаляющие себя от Тебя гибнут; Ты истребляешь всякого отступающего от Тебя.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23362, 'rst66', 'psa', 72, 28, 'А мне благо приближаться к Богу! На Господа Бога я возложил упование мое, чтобы возвещать все дела Твои.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23363, 'rst66', 'psa', 73, 0, 'Учение Асафа.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23364, 'rst66', 'psa', 73, 1, 'Для чего, Боже, отринул нас навсегда? возгорелся гнев Твой на овец пажити Твоей?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23365, 'rst66', 'psa', 73, 2, 'Вспомни сонм Твой, <i>который</i> Ты стяжал издревле, искупил в жезл достояния Твоего, &mdash; эту гору Сион, на которой Ты вселился.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23366, 'rst66', 'psa', 73, 3, 'Подвигни стопы Твои к вековым развалинам: все разрушил враг во святилище.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23367, 'rst66', 'psa', 73, 4, 'Рыкают враги Твои среди собраний Твоих; поставили знаки свои вместо знамений <i>наших</i>;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23368, 'rst66', 'psa', 73, 5, 'показывали себя подобными поднимающему вверх секиру на сплетшиеся ветви дерева;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23369, 'rst66', 'psa', 73, 6, 'и ныне все резьбы в нем в один раз разрушили секирами и бердышами;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23370, 'rst66', 'psa', 73, 7, 'предали огню святилище Твое; совсем осквернили жилище имени Твоего;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23371, 'rst66', 'psa', 73, 8, 'сказали в сердце своем: «разорим их совсем», &mdash; и сожгли все места собраний Божиих на земле.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23372, 'rst66', 'psa', 73, 9, 'Знамений наших мы не видим, нет уже пророка, и нет с нами, кто знал бы, доколе <i>это будет</i>.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23373, 'rst66', 'psa', 73, 10, 'Доколе, Боже, будет поносить враг? вечно ли будет хулить противник имя Твое?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23374, 'rst66', 'psa', 73, 11, 'Для чего отклоняешь руку Твою и десницу Твою? Из среды недра Твоего порази <i>их</i>.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23375, 'rst66', 'psa', 73, 12, 'Боже, Царь мой от века, устрояющий спасение посреди земли!');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23376, 'rst66', 'psa', 73, 13, 'Ты расторг силою Твоею море, Ты сокрушил головы змиев в воде;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23377, 'rst66', 'psa', 73, 14, 'Ты сокрушил голову левиафана, отдал его в пищу людям пустыни;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23378, 'rst66', 'psa', 73, 15, 'Ты иссек источник и поток, Ты иссушил сильные реки.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23379, 'rst66', 'psa', 73, 16, 'Твой день и Твоя ночь: Ты уготовал светила и солнце;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23380, 'rst66', 'psa', 73, 17, 'Ты установил все пределы земли, лето и зиму Ты учредил.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23381, 'rst66', 'psa', 73, 18, 'Вспомни же: враг поносит Господа, и люди безумные хулят имя Твое.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23382, 'rst66', 'psa', 73, 19, 'Не предай зверям душу горлицы Твоей; собрания убогих Твоих не забудь навсегда.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23383, 'rst66', 'psa', 73, 20, 'Призри на завет Твой; ибо наполнились все мрачные места земли жилищами насилия.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23384, 'rst66', 'psa', 73, 21, 'Да не возвратится угнетенный посрамленным; нищий и убогий да восхвалят имя Твое.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23385, 'rst66', 'psa', 73, 22, 'Восстань, Боже, защити дело Твое, вспомни вседневное поношение Твое от безумного;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23386, 'rst66', 'psa', 73, 23, 'не забудь крика врагов Твоих; шум восстающих против Тебя непрестанно поднимается.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23387, 'rst66', 'psa', 74, 1, 'Начальнику хора. Не погуби. Псалом Асафа. Песнь.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23388, 'rst66', 'psa', 74, 2, 'Славим Тебя, Боже, славим, ибо близко имя Твое; возвещают чудеса Твои.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23389, 'rst66', 'psa', 74, 3, '«Когда изберу время, Я произведу суд по правде.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23390, 'rst66', 'psa', 74, 4, 'Колеблется земля и все живущие на ней: Я утвержу столпы ее».');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23391, 'rst66', 'psa', 74, 5, 'Говорю безумствующим: «не безумствуйте», и нечестивым: «не поднимайте рога,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23392, 'rst66', 'psa', 74, 6, 'не поднимайте высоко рога вашего, <i>не</i> говорите жестоковыйно»,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23393, 'rst66', 'psa', 74, 7, 'Ибо не от востока и не от запада и не от пустыни возвышение,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23394, 'rst66', 'psa', 74, 8, 'но Бог есть судия: одного унижает, а другого возносит;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23395, 'rst66', 'psa', 74, 9, 'ибо чаша в руке Господа, вино кипит в ней, полное смешения, и Он наливает из нее. Даже дрожжи ее будут выжимать и пить все нечестивые земли.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23396, 'rst66', 'psa', 74, 10, 'А я буду возвещать вечно, буду воспевать Бога Иаковлева,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23397, 'rst66', 'psa', 74, 11, 'все роги нечестивых сломлю, и вознесутся роги праведника.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23398, 'rst66', 'psa', 75, 1, 'Начальнику хора. На струнных <i>орудиях</i>. Псалом Асафа. Песнь.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23399, 'rst66', 'psa', 75, 2, 'Ведом в Иудее Бог; у Израиля велико имя Его.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23400, 'rst66', 'psa', 75, 3, 'И было в Салиме жилище Его и пребывание Его на Сионе.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23401, 'rst66', 'psa', 75, 4, 'Там сокрушил Он стрелы лука, щит и меч и брань.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23402, 'rst66', 'psa', 75, 5, 'Ты славен, могущественнее гор хищнических.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23403, 'rst66', 'psa', 75, 6, 'Крепкие сердцем стали добычею, уснули сном своим, и не нашли все мужи силы рук своих.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23404, 'rst66', 'psa', 75, 7, 'От прещения Твоего, Боже Иакова, вздремали и колесница и конь.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23405, 'rst66', 'psa', 75, 8, 'Ты страшен, и кто устоит пред лицом Твоим во время гнева Твоего?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23406, 'rst66', 'psa', 75, 9, 'С небес Ты возвестил суд; земля убоялась и утихла,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23407, 'rst66', 'psa', 75, 10, 'когда восстал Бог на суд, чтобы спасти всех угнетенных земли.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23408, 'rst66', 'psa', 75, 11, 'И гнев человеческий обратится во славу Тебе: остаток гнева Ты укротишь.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23409, 'rst66', 'psa', 75, 12, 'Делайте и воздавайте обеты Господу, Богу вашему; все, которые вокруг Него, да принесут дары Страшному:');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23410, 'rst66', 'psa', 75, 13, 'Он укрощает дух князей, Он страшен для царей земных.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23411, 'rst66', 'psa', 76, 1, 'Начальнику хора Идифумова. Псалом Асафа.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23412, 'rst66', 'psa', 76, 2, 'Глас мой к Богу, и я буду взывать; глас мой к Богу, и Он услышит меня.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23413, 'rst66', 'psa', 76, 3, 'В день скорби моей ищу Господа; рука моя простерта ночью и не опускается; душа моя отказывается от утешения.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23414, 'rst66', 'psa', 76, 4, 'Вспоминаю о Боге и трепещу; помышляю, и изнемогает дух мой.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23415, 'rst66', 'psa', 76, 5, 'Ты не даешь мне сомкнуть очей моих; я потрясен и не могу говорить.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23416, 'rst66', 'psa', 76, 6, 'Размышляю о днях древних, о летах веков <i>минувших;</i>');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23417, 'rst66', 'psa', 76, 7, 'припоминаю песни мои в ночи, беседую с сердцем моим, и дух мой испытывает:');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23418, 'rst66', 'psa', 76, 8, 'неужели навсегда отринул Господь, и не будет более благоволить?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23419, 'rst66', 'psa', 76, 9, 'Неужели навсегда престала милость Его и пресеклось слово Его в род и род?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23420, 'rst66', 'psa', 76, 10, 'Неужели Бог забыл миловать? Неужели во гневе затворил щедроты Свои?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23421, 'rst66', 'psa', 76, 11, 'И сказал я: «вот мое горе &mdash; изменение десницы Всевышнего».');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23422, 'rst66', 'psa', 76, 12, 'Буду вспоминать о делах Господа; буду вспоминать о чудесах Твоих древних;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23423, 'rst66', 'psa', 76, 13, 'буду вникать во все дела Твои, размышлять о великих Твоих деяниях.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23424, 'rst66', 'psa', 76, 14, 'Боже! свят путь Твой. Кто Бог так великий, как Бог <i>наш</i>!');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23425, 'rst66', 'psa', 76, 15, 'Ты &mdash; Бог, творящий чудеса; Ты явил могущество Свое среди народов;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23426, 'rst66', 'psa', 76, 16, 'Ты избавил мышцею народ Твой, сынов Иакова и Иосифа.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23427, 'rst66', 'psa', 76, 17, 'Видели Тебя, Боже, воды, видели Тебя воды и убоялись, и вострепетали бездны.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23428, 'rst66', 'psa', 76, 18, 'Облака изливали воды, тучи издавали гром, и стрелы Твои летали.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23429, 'rst66', 'psa', 76, 19, 'Глас грома Твоего в круге небесном; молнии освещали вселенную; земля содрогалась и тряслась.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23430, 'rst66', 'psa', 76, 20, 'Путь Твой в море, и стезя Твоя в водах великих, и следы Твои неведомы.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23431, 'rst66', 'psa', 76, 21, 'Как стадо, вел Ты народ Твой рукою Моисея и Аарона.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23432, 'rst66', 'psa', 77, 0, 'Учение Асафа.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23433, 'rst66', 'psa', 77, 1, 'Внимай, народ мой, закону моему, приклоните ухо ваше к словам уст моих.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23434, 'rst66', 'psa', 77, 2, 'Открою уста мои в притче и произнесу гадания из древности.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23435, 'rst66', 'psa', 77, 3, 'Что слышали мы и узнали, и отцы наши рассказали нам,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23436, 'rst66', 'psa', 77, 4, 'не скроем от детей их, возвещая роду грядущему славу Господа, и силу Его, и чудеса Его, которые Он сотворил.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23437, 'rst66', 'psa', 77, 5, 'Он постановил устав в Иакове и положил закон в Израиле, который заповедал отцам нашим возвещать детям их,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23438, 'rst66', 'psa', 77, 6, 'чтобы знал грядущий род, дети, которые родятся, и чтобы они в свое время возвещали своим детям, &mdash;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23439, 'rst66', 'psa', 77, 7, 'возлагать надежду свою на Бога, и не забывать дел Божиих, и хранить заповеди Его,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23440, 'rst66', 'psa', 77, 8, 'и не быть подобными отцам их, роду упорному и мятежному, неустроенному сердцем и неверному Богу духом своим.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23441, 'rst66', 'psa', 77, 9, 'Сыны Ефремовы, вооруженные, стреляющие из луков, обратились назад в день брани:');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23442, 'rst66', 'psa', 77, 10, 'они не сохранили завета Божия и отреклись ходить в законе Его;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23443, 'rst66', 'psa', 77, 11, 'забыли дела Его и чудеса, которые Он явил им.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23444, 'rst66', 'psa', 77, 12, 'Он пред глазами отцов их сотворил чудеса в земле Египетской, на поле Цоан:');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23445, 'rst66', 'psa', 77, 13, 'разделил море, и провел их чрез него, и поставил воды стеною;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23446, 'rst66', 'psa', 77, 14, 'и днем вел их облаком, а во всю ночь светом огня;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23447, 'rst66', 'psa', 77, 15, 'рассек камень в пустыне и напоил их, как из великой бездны;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23448, 'rst66', 'psa', 77, 16, 'из скалы извел потоки, и воды потекли, как реки.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23449, 'rst66', 'psa', 77, 17, 'Но они продолжали грешить пред Ним и раздражать Всевышнего в пустыне:');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23450, 'rst66', 'psa', 77, 18, 'искушали Бога в сердце своем, требуя пищи по душе своей,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23451, 'rst66', 'psa', 77, 19, 'и говорили против Бога и сказали: «может ли Бог приготовить трапезу в пустыне?»');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23452, 'rst66', 'psa', 77, 20, 'Вот, Он ударил в камень, и потекли воды, и полились ручьи. «Может ли Он дать и хлеб, может ли приготовлять мясо народу Своему?»');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23453, 'rst66', 'psa', 77, 21, 'Господь услышал и воспламенился гневом, и огонь возгорелся на Иакова, и гнев подвигнулся на Израиля');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23454, 'rst66', 'psa', 77, 22, 'за то, что не веровали в Бога и не уповали на спасение Его.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23455, 'rst66', 'psa', 77, 23, 'Он повелел облакам свыше и отверз двери неба,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23456, 'rst66', 'psa', 77, 24, 'и одождил на них манну в пищу, и хлеб небесный дал им.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23457, 'rst66', 'psa', 77, 25, 'Хлеб ангельский ел человек; послал Он им пищу до сытости.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23458, 'rst66', 'psa', 77, 26, 'Он возбудил на небе восточный ветер и навел южный силою Своею');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23459, 'rst66', 'psa', 77, 27, 'и, как пыль, одождил на них мясо и, как песок морской, птиц пернатых:');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23460, 'rst66', 'psa', 77, 28, 'поверг их среди стана их, около жилищ их, &mdash;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23461, 'rst66', 'psa', 77, 29, 'и они ели и пресытились; и желаемое ими дал им.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23462, 'rst66', 'psa', 77, 30, 'Но еще не прошла прихоть их, еще пища была в устах их,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23463, 'rst66', 'psa', 77, 31, 'гнев Божий пришел на них, убил тучных их и юношей Израилевых низложил.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23464, 'rst66', 'psa', 77, 32, 'При всем этом они продолжали грешить и не верили чудесам Его.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23465, 'rst66', 'psa', 77, 33, 'И погубил дни их в суете и лета их в смятении.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23466, 'rst66', 'psa', 77, 34, 'Когда Он убивал их, они искали Его и обращались, и с раннего утра прибегали к Богу,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23467, 'rst66', 'psa', 77, 35, 'и вспоминали, что Бог &mdash; их прибежище, и Бог Всевышний &mdash; Избавитель их,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23468, 'rst66', 'psa', 77, 36, 'и льстили Ему устами своими и языком своим лгали пред Ним;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23469, 'rst66', 'psa', 77, 37, 'сердце же их было неправо пред Ним, и они не были верны завету Его.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23470, 'rst66', 'psa', 77, 38, 'Но Он, Милостивый, прощал грех и не истреблял их, многократно отвращал гнев Свой и не возбуждал всей ярости Своей:');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23471, 'rst66', 'psa', 77, 39, 'Он помнил, что они плоть, дыхание, которое уходит и не возвращается.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23472, 'rst66', 'psa', 77, 40, 'Сколько раз они раздражали Его в пустыне и прогневляли Его в <i>стране</i> необитаемой!');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23473, 'rst66', 'psa', 77, 41, 'И снова искушали Бога и оскорбляли Святого Израилева,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23474, 'rst66', 'psa', 77, 42, 'не помнили руки Его, дня, когда Он избавил их от угнетения,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23475, 'rst66', 'psa', 77, 43, 'когда сотворил в Египте знамения Свои и чудеса Свои на поле Цоан;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23476, 'rst66', 'psa', 77, 44, 'и превратил реки их и потоки их в кровь, чтобы они не могли пить;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23477, 'rst66', 'psa', 77, 45, 'послал на них насекомых, чтобы жалили их, и жаб, чтобы губили их;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23478, 'rst66', 'psa', 77, 46, 'земные произрастения их отдал гусенице и труд их &mdash; саранче;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23479, 'rst66', 'psa', 77, 47, 'виноград их побил градом и сикоморы их &mdash; льдом;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23480, 'rst66', 'psa', 77, 48, 'скот их предал граду и стада их &mdash; молниям;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23481, 'rst66', 'psa', 77, 49, 'послал на них пламень гнева Своего, и негодование, и ярость и бедствие, посольство злых ангелов;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23482, 'rst66', 'psa', 77, 50, 'уровнял стезю гневу Своему, не охранял души их от смерти, и скот их предал моровой язве;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23483, 'rst66', 'psa', 77, 51, 'Поразил всякого первенца в Египте, начатки сил в шатрах Хамовых;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23484, 'rst66', 'psa', 77, 52, 'и повел народ Свой, как овец, и вел их, как стадо, пустынею;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23485, 'rst66', 'psa', 77, 53, 'вел их безопасно, и они не страшились, а врагов их покрыло море;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23486, 'rst66', 'psa', 77, 54, 'и привел их в область святую Свою, на гору сию, которую стяжала десница Его;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23487, 'rst66', 'psa', 77, 55, 'прогнал от лица их народы и землю их разделил в наследие им, и колена Израилевы поселил в шатрах их.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23488, 'rst66', 'psa', 77, 56, 'Но они еще искушали и огорчали Бога Всевышнего, и уставов Его не сохраняли;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23489, 'rst66', 'psa', 77, 57, 'отступали и изменяли, как отцы их, обращались назад, как неверный лук;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23490, 'rst66', 'psa', 77, 58, 'огорчали Его высотами своими и истуканами своими возбуждали ревность Его.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23491, 'rst66', 'psa', 77, 59, 'Услышал Бог и воспламенился гневом и сильно вознегодовал на Израиля;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23492, 'rst66', 'psa', 77, 60, 'отринул жилище в Силоме, скинию, в которой обитал Он между человеками;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23493, 'rst66', 'psa', 77, 61, 'и отдал в плен крепость Свою и славу Свою в руки врага,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23494, 'rst66', 'psa', 77, 62, 'и предал мечу народ Свой и прогневался на наследие Свое.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23495, 'rst66', 'psa', 77, 63, 'Юношей его поядал огонь, и девицам его не пели брачных песен;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23496, 'rst66', 'psa', 77, 64, 'священники его падали от меча, и вдовы его не плакали.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23497, 'rst66', 'psa', 77, 65, 'Но, как бы от сна, воспрянул Господь, как бы исполин, побежденный вином,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23498, 'rst66', 'psa', 77, 66, 'и поразил врагов его в тыл, вечному сраму предал их;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23499, 'rst66', 'psa', 77, 67, 'и отверг шатер Иосифов и колена Ефремова не избрал,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23500, 'rst66', 'psa', 77, 68, 'а избрал колено Иудино, гору Сион, которую возлюбил.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23501, 'rst66', 'psa', 77, 69, 'И устроил, как небо, святилище Свое и, как землю, утвердил его навек,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23502, 'rst66', 'psa', 77, 70, 'и избрал Давида, раба Своего, и взял его от дворов овчих');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23503, 'rst66', 'psa', 77, 71, 'и от доящих привел его пасти народ Свой, Иакова, и наследие Свое, Израиля.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23504, 'rst66', 'psa', 77, 72, 'И он пас их в чистоте сердца своего и руками мудрыми водил их.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23505, 'rst66', 'psa', 78, 0, 'Псалом Асафа.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23506, 'rst66', 'psa', 78, 1, 'Боже! язычники пришли в наследие Твое, осквернили святой храм Твой, Иерусалим превратили в развалины;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23507, 'rst66', 'psa', 78, 2, 'трупы рабов Твоих отдали на съедение птицам небесным, тела святых Твоих &mdash; зверям земным;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23508, 'rst66', 'psa', 78, 3, 'пролили кровь их, как воду, вокруг Иерусалима, и некому было похоронить их.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23509, 'rst66', 'psa', 78, 4, 'Мы сделались посмешищем у соседей наших, поруганием и посрамлением у окружающих нас.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23510, 'rst66', 'psa', 78, 5, 'Доколе, Господи, будешь гневаться непрестанно, будет пылать ревность Твоя, как огонь?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23511, 'rst66', 'psa', 78, 6, 'Пролей гнев Твой на народы, которые не знают Тебя, и на царства, которые имени Твоего не призывают,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23512, 'rst66', 'psa', 78, 7, 'ибо они пожрали Иакова и жилище его опустошили.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23513, 'rst66', 'psa', 78, 8, 'Не помяни нам грехов <i>наших</i> предков; скоро да предварят нас щедроты Твои, ибо мы весьма истощены.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23514, 'rst66', 'psa', 78, 9, 'Помоги нам, Боже, Спаситель наш, ради славы имени Твоего; избавь нас и прости нам грехи наши ради имени Твоего.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23515, 'rst66', 'psa', 78, 10, 'Для чего язычникам говорить: «где Бог их?» Да сделается известным между язычниками пред глазами нашими отмщение за пролитую кровь рабов Твоих.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23516, 'rst66', 'psa', 78, 11, 'Да придет пред лицо Твое стенание узника; могуществом мышцы Твоей сохрани обреченных на смерть.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23517, 'rst66', 'psa', 78, 12, 'Семикратно возврати соседям нашим в недро их поношение, которым они Тебя, Господи, поносили.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23518, 'rst66', 'psa', 78, 13, 'А мы, народ Твой и Твоей пажити овцы, вечно будем славить Тебя и в род и род возвещать хвалу Тебе.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23519, 'rst66', 'psa', 79, 1, 'Начальнику хора. На музыкальном <i>орудии</i> Шошанним‐Эдуф. Псалом Асафа.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23520, 'rst66', 'psa', 79, 2, 'Пастырь Израиля! внемли; водящий, как овец, Иосифа, восседающий на херувимах, яви Себя.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23521, 'rst66', 'psa', 79, 3, 'Пред Ефремом и Вениамином и Манассиею воздвигни силу Твою, и приди спасти нас.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23522, 'rst66', 'psa', 79, 4, 'Боже! восстанови нас; да воссияет лицо Твое, и спасемся!');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23523, 'rst66', 'psa', 79, 5, 'Господи, Боже сил! доколе будешь гневен к молитвам народа Твоего?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23524, 'rst66', 'psa', 79, 6, 'Ты напитал их хлебом слезным, и напоил их слезами в большой мере,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23525, 'rst66', 'psa', 79, 7, 'положил нас в пререкание соседям нашим, и враги наши издеваются <i>над нами</i>.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23526, 'rst66', 'psa', 79, 8, 'Боже сил! восстанови нас; да воссияет лицо Твое, и спасемся!');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23527, 'rst66', 'psa', 79, 9, 'Из Египта перенес Ты виноградную лозу, выгнал народы и посадил ее;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23528, 'rst66', 'psa', 79, 10, 'очистил для нее место, и утвердил корни ее, и она наполнила землю.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23529, 'rst66', 'psa', 79, 11, 'Горы покрылись тенью ее, и ветви ее, как кедры Божии;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23530, 'rst66', 'psa', 79, 12, 'она пустила ветви свои до моря и отрасли свои до реки.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23531, 'rst66', 'psa', 79, 13, 'Для чего разрушил Ты ограды ее, так что обрывают ее все, проходящие по пути?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23532, 'rst66', 'psa', 79, 14, 'Лесной вепрь подрывает ее, и полевой зверь объедает ее.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23533, 'rst66', 'psa', 79, 15, 'Боже сил! обратись же, призри с неба, и воззри, и посети виноград сей;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23534, 'rst66', 'psa', 79, 16, 'охрани то, что насадила десница Твоя, и отрасли, которые Ты укрепил Себе.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23535, 'rst66', 'psa', 79, 17, 'Он пожжен огнем, обсечен; от прещения лица Твоего погибнут.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23536, 'rst66', 'psa', 79, 18, 'Да будет рука Твоя над мужем десницы Твоей, над сыном человеческим, которого Ты укрепил Себе,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23537, 'rst66', 'psa', 79, 19, 'и мы не отступим от Тебя; оживи нас, и мы будем призывать имя Твое.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23538, 'rst66', 'psa', 79, 20, 'Господи, Боже сил! восстанови нас; да воссияет лицо Твое, и спасемся!');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23539, 'rst66', 'psa', 80, 1, 'Начальнику хора. На Гефском орудии. Псалом Асафа.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23540, 'rst66', 'psa', 80, 2, 'Радостно пойте Богу, твердыне нашей; восклицайте Богу Иакова;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23541, 'rst66', 'psa', 80, 3, 'возьмите псалом, дайте тимпан, сладкозвучные гусли с псалтирью;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23542, 'rst66', 'psa', 80, 4, 'трубите в новомесячие трубою, в определенное время, в день праздника нашего;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23543, 'rst66', 'psa', 80, 5, 'ибо это закон для Израиля, устав от Бога Иаковлева.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23544, 'rst66', 'psa', 80, 6, 'Он установил это во свидетельство для Иосифа, когда он вышел из земли Египетской, где услышал звуки языка, которого не знал:');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23545, 'rst66', 'psa', 80, 7, '«Я снял с рамен его тяжести, и руки его освободились от корзин.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23546, 'rst66', 'psa', 80, 8, 'В бедствии ты призвал Меня, и Я избавил тебя; из среды грома Я услышал тебя, при водах Меривы испытал тебя.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23547, 'rst66', 'psa', 80, 9, 'Слушай, народ Мой, и Я буду свидетельствовать тебе: Израиль! о, если бы ты послушал Меня!');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23548, 'rst66', 'psa', 80, 10, 'Да не будет у тебя иного бога, и не поклоняйся богу чужеземному.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23549, 'rst66', 'psa', 80, 11, 'Я Господь, Бог твой, изведший тебя из земли Египетской; открой уста твои, и Я наполню их».');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23550, 'rst66', 'psa', 80, 12, 'Но народ Мой не слушал гласа Моего, и Израиль не покорялся Мне;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23551, 'rst66', 'psa', 80, 13, 'потому Я оставил их упорству сердца их, пусть ходят по своим помыслам.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23552, 'rst66', 'psa', 80, 14, 'О, если бы народ Мой слушал Меня и Израиль ходил Моими путями!');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23553, 'rst66', 'psa', 80, 15, 'Я скоро смирил бы врагов их и обратил бы руку Мою на притеснителей их:');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23554, 'rst66', 'psa', 80, 16, 'ненавидящие Господа раболепствовали бы им, а их благоденствие продолжалось бы навсегда;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23555, 'rst66', 'psa', 80, 17, 'Я питал бы их туком пшеницы и насыщал бы их медом из скалы.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23556, 'rst66', 'psa', 81, 0, 'Псалом Асафа.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23557, 'rst66', 'psa', 81, 1, 'Бог стал в сонме богов; среди богов произнес суд:');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23558, 'rst66', 'psa', 81, 2, 'доколе будете вы судить неправедно и оказывать лицеприятие нечестивым?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23559, 'rst66', 'psa', 81, 3, 'Давайте суд бедному и сироте; угнетенному и нищему оказывайте справедливость;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23560, 'rst66', 'psa', 81, 4, 'избавляйте бедного и нищего; исторгайте <i>его</i> из руки нечестивых.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23561, 'rst66', 'psa', 81, 5, 'Не знают, не разумеют, во тьме ходят; все основания земли колеблются.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23562, 'rst66', 'psa', 81, 6, 'Я сказал: вы &mdash; боги, и сыны Всевышнего &mdash; все вы;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23563, 'rst66', 'psa', 81, 7, 'но вы умрете, как человеки, и падете, как всякий из князей.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23564, 'rst66', 'psa', 81, 8, 'Восстань, Боже, суди землю, ибо Ты наследуешь все народы.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23565, 'rst66', 'psa', 82, 1, 'Песнь. Псалом Асафа.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23566, 'rst66', 'psa', 82, 2, 'Боже! Не премолчи, не безмолвствуй и не оставайся в покое, Боже,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23567, 'rst66', 'psa', 82, 3, 'ибо вот, враги Твои шумят, и ненавидящие Тебя подняли голову;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23568, 'rst66', 'psa', 82, 4, 'против народа Твоего составили коварный умысел и совещаются против хранимых Тобою;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23569, 'rst66', 'psa', 82, 5, 'сказали: «пойдем и истребим их из народов, чтобы не вспоминалось более имя Израиля».');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23570, 'rst66', 'psa', 82, 6, 'Сговорились единодушно, заключили против Тебя союз:');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23571, 'rst66', 'psa', 82, 7, 'селения Едомовы и Измаильтяне, Моав и Агаряне,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23572, 'rst66', 'psa', 82, 8, 'Гевал и Аммон и Амалик, Филистимляне с жителями Тира.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23573, 'rst66', 'psa', 82, 9, 'И Ассур пристал к ним: они стали мышцею для сынов Лотовых.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23574, 'rst66', 'psa', 82, 10, 'Сделай им то же, что Мадиаму, что Сисаре, что Иавину у потока Киссона,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23575, 'rst66', 'psa', 82, 11, 'которые истреблены в Аендоре, сделались навозом для земли.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23576, 'rst66', 'psa', 82, 12, 'Поступи с ними, с князьями их, как с Оривом и Зивом и со всеми вождями их, как с Зевеем и Салманом,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23577, 'rst66', 'psa', 82, 13, 'которые говорили: «возьмем себе во владение селения Божии».');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23578, 'rst66', 'psa', 82, 14, 'Боже мой! Да будут они, как пыль в вихре, как солома перед ветром.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23579, 'rst66', 'psa', 82, 15, 'Как огонь сжигает лес, и как пламя опаляет горы,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23580, 'rst66', 'psa', 82, 16, 'так погони их бурею Твоею и вихрем Твоим приведи их в смятение;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23581, 'rst66', 'psa', 82, 17, 'исполни лица их бесчестием, чтобы они взыскали имя Твое, Господи!');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23582, 'rst66', 'psa', 82, 18, 'Да постыдятся и смятутся навеки, да посрамятся и погибнут,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23583, 'rst66', 'psa', 82, 19, 'и да познают, что Ты, Которого одного имя Господь, Всевышний над всею землею.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23584, 'rst66', 'psa', 83, 1, 'Начальнику хора. На Гефском <i>орудии</i>. Кореевых сынов. Псалом.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23585, 'rst66', 'psa', 83, 2, 'Как вожделенны жилища Твои, Господи сил!');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23586, 'rst66', 'psa', 83, 3, 'Истомилась душа моя, желая во дворы Господни; сердце мое и плоть моя восторгаются к Богу живому.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23587, 'rst66', 'psa', 83, 4, 'И птичка находит себе жилье, и ласточка гнездо себе, где положить птенцов своих, у алтарей Твоих, Господи сил, Царь мой и Бог мой!');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23588, 'rst66', 'psa', 83, 5, 'Блаженны живущие в доме Твоем: они непрестанно будут восхвалять Тебя.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23589, 'rst66', 'psa', 83, 6, 'Блажен человек, которого сила в Тебе и у которого в сердце стези направлены <i>к Тебе</i>.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23590, 'rst66', 'psa', 83, 7, 'Проходя долиною плача, они открывают в ней источники, и дождь покрывает ее благословением;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23591, 'rst66', 'psa', 83, 8, 'приходят от силы в силу, являются пред Богом на Сионе.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23592, 'rst66', 'psa', 83, 9, 'Господи, Боже сил! Услышь молитву мою, внемли, Боже Иаковлев!');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23593, 'rst66', 'psa', 83, 10, 'Боже, защитник наш! Приникни и призри на лицо помазанника Твоего.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23594, 'rst66', 'psa', 83, 11, 'Ибо один день во дворах Твоих лучше тысячи. Желаю лучше быть у порога в доме Божием, нежели жить в шатрах нечестия.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23595, 'rst66', 'psa', 83, 12, 'Ибо Господь Бог есть солнце и щит, Господь дает благодать и славу; ходящих в непорочности Он не лишает благ.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23596, 'rst66', 'psa', 83, 13, 'Господи сил! Блажен человек, уповающий на Тебя!');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23597, 'rst66', 'psa', 84, 1, 'Начальнику хора. Кореевых сынов. Псалом.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23598, 'rst66', 'psa', 84, 2, 'Господи! Ты умилосердился к земле Твоей, возвратил плен Иакова;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23599, 'rst66', 'psa', 84, 3, 'простил беззаконие народа Твоего, покрыл все грехи его,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23600, 'rst66', 'psa', 84, 4, 'отъял всю ярость Твою, отвратил лютость гнева Твоего.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23601, 'rst66', 'psa', 84, 5, 'Восстанови нас, Боже спасения нашего, и прекрати негодование Твое на нас.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23602, 'rst66', 'psa', 84, 6, 'Неужели вечно будешь гневаться на нас, прострешь гнев Твой от рода в род?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23603, 'rst66', 'psa', 84, 7, 'Неужели снова не оживишь нас, чтобы народ Твой возрадовался о Тебе?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23604, 'rst66', 'psa', 84, 8, 'Яви нам, Господи, милость Твою, и спасение Твое даруй нам.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23605, 'rst66', 'psa', 84, 9, 'Послушаю, что скажет Господь Бог. Он скажет мир народу Своему и избранным Своим, но да не впадут они снова в безрассудство.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23606, 'rst66', 'psa', 84, 10, 'Так, близко к боящимся Его спасение Его, чтобы обитала слава в земле нашей!');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23607, 'rst66', 'psa', 84, 11, 'Милость и истина сретятся, правда и мир облобызаются;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23608, 'rst66', 'psa', 84, 12, 'истина возникнет из земли, и правда приникнет с небес;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23609, 'rst66', 'psa', 84, 13, 'и Господь даст благо, и земля наша даст плод свой;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23610, 'rst66', 'psa', 84, 14, 'правда пойдет пред Ним и поставит на путь стопы свои.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23611, 'rst66', 'psa', 85, 0, 'Молитва Давида.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23612, 'rst66', 'psa', 85, 1, 'Приклони, Господи, ухо Твое и услышь меня, ибо я беден и нищ.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23613, 'rst66', 'psa', 85, 2, 'Сохрани душу мою, ибо я благоговею пред Тобою; спаси, Боже мой, раба Твоего, уповающего на Тебя.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23614, 'rst66', 'psa', 85, 3, 'Помилуй меня, Господи, ибо к Тебе взываю каждый день.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23615, 'rst66', 'psa', 85, 4, 'Возвесели душу раба Твоего, ибо к Тебе, Господи, возношу душу мою,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23616, 'rst66', 'psa', 85, 5, 'ибо Ты, Господи, благ и милосерд и многомилостив ко всем, призывающим Тебя.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23617, 'rst66', 'psa', 85, 6, 'Услышь, Господи, молитву мою и внемли гласу моления моего.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23618, 'rst66', 'psa', 85, 7, 'В день скорби моей взываю к Тебе, потому что Ты услышишь меня.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23619, 'rst66', 'psa', 85, 8, 'Нет между богами, как Ты, Господи, и нет дел, как Твои.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23620, 'rst66', 'psa', 85, 9, 'Все народы, Тобою сотворенные, приидут и поклонятся пред Тобою, Господи, и прославят имя Твое,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23621, 'rst66', 'psa', 85, 10, 'ибо Ты велик и творишь чудеса, &mdash; Ты, Боже, един Ты.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23622, 'rst66', 'psa', 85, 11, 'Наставь меня, Господи, на путь Твой, и буду ходить в истине Твоей; утверди сердце мое в страхе имени Твоего.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23623, 'rst66', 'psa', 85, 12, 'Буду восхвалять Тебя, Господи, Боже мой, всем сердцем моим и славить имя Твое вечно,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23624, 'rst66', 'psa', 85, 13, 'ибо велика милость Твоя ко мне: Ты избавил душу мою от ада преисподнего.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23625, 'rst66', 'psa', 85, 14, 'Боже! гордые восстали на меня, и скопище мятежников ищет души моей: не представляют они Тебя пред собою.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23626, 'rst66', 'psa', 85, 15, 'Но Ты, Господи, Боже щедрый и благосердый, долготерпеливый и многомилостивый и истинный,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23627, 'rst66', 'psa', 85, 16, 'призри на меня и помилуй меня; даруй крепость Твою рабу Твоему, и спаси сына рабы Твоей;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23628, 'rst66', 'psa', 85, 17, 'покажи на мне знамение во благо, да видят ненавидящие меня и устыдятся, потому что Ты, Господи, помог мне и утешил меня.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23629, 'rst66', 'psa', 86, 1, 'Сынов Кореевых. Псалом. Песнь.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23630, 'rst66', 'psa', 86, 2, 'Основание его на горах святых. Господь любит врата Сиона более всех селений Иакова.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23631, 'rst66', 'psa', 86, 3, 'Славное возвещается о тебе, град Божий!');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23632, 'rst66', 'psa', 86, 4, 'Упомяну знающим меня о Рааве и Вавилоне; вот Филистимляне и Тир с Ефиопиею, &mdash; <i>скажут:</i> «такой‐то родился там».');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23633, 'rst66', 'psa', 86, 5, 'О Сионе же будут говорить: «такой‐то и такой‐то муж родился в нем, и Сам Всевышний укрепил его».');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23634, 'rst66', 'psa', 86, 6, 'Господь в переписи народов напишет: «такой‐то родился там».');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23635, 'rst66', 'psa', 86, 7, 'И поющие и играющие &mdash; все источники мои в тебе.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23636, 'rst66', 'psa', 87, 1, 'Песнь. Псалом. Сынов Кореевых. Начальнику хора на Махалаф, для пения. Учение Емана Езрахита.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23637, 'rst66', 'psa', 87, 2, 'Господи, Боже спасения моего! днем вопию и ночью пред Тобою:');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23638, 'rst66', 'psa', 87, 3, 'да внидет пред лицо Твое молитва моя; приклони ухо Твое к молению моему,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23639, 'rst66', 'psa', 87, 4, 'ибо душа моя насытилась бедствиями, и жизнь моя приблизилась к преисподней.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23640, 'rst66', 'psa', 87, 5, 'Я сравнялся с нисходящими в могилу; я стал, как человек без силы,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23641, 'rst66', 'psa', 87, 6, 'между мертвыми брошенный, &mdash; как убитые, лежащие во гробе, о которых Ты уже не вспоминаешь и которые от руки Твоей отринуты.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23642, 'rst66', 'psa', 87, 7, 'Ты положил меня в ров преисподний, во мрак, в бездну.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23643, 'rst66', 'psa', 87, 8, 'Отяготела на мне ярость Твоя, и всеми волнами Твоими Ты поразил <i>меня</i>.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23644, 'rst66', 'psa', 87, 9, 'Ты удалил от меня знакомых моих, сделал меня отвратительным для них; я заключен и не могу выйти.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23645, 'rst66', 'psa', 87, 10, 'Око мое истомилось от горести: весь день я взывал к Тебе, Господи, простирал к Тебе руки мои.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23646, 'rst66', 'psa', 87, 11, 'Разве над мертвыми Ты сотворишь чудо? Разве мертвые встанут и будут славить Тебя?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23647, 'rst66', 'psa', 87, 12, 'Или во гробе будет возвещаема милость Твоя, и истина Твоя &mdash; в месте тления?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23648, 'rst66', 'psa', 87, 13, 'Разве во мраке познают чудеса Твои, и в земле забвения &mdash; правду Твою?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23649, 'rst66', 'psa', 87, 14, 'Но я к Тебе, Господи, взываю, и рано утром молитва моя предваряет Тебя.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23650, 'rst66', 'psa', 87, 15, 'Для чего, Господи, отреваешь душу мою, скрываешь лицо Твое от меня?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23651, 'rst66', 'psa', 87, 16, 'Я несчастен и истаеваю с юности; несу ужасы Твои и изнемогаю.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23652, 'rst66', 'psa', 87, 17, 'Надо мною прошла ярость Твоя, устрашения Твои сокрушили меня,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23653, 'rst66', 'psa', 87, 18, 'всякий день окружают меня, как вода: облегают меня все вместе.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23654, 'rst66', 'psa', 87, 19, 'Ты удалил от меня друга и искреннего; знакомых моих не видно.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23655, 'rst66', 'psa', 88, 1, 'Учение Ефама Езрахита.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23656, 'rst66', 'psa', 88, 2, 'Милости <i>Твои</i>, Господи, буду петь вечно, в род и род возвещать истину Твою устами моими.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23657, 'rst66', 'psa', 88, 3, 'Ибо говорю: навек основана милость, на небесах утвердил Ты истину Твою, <i>когда сказал:</i>');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23658, 'rst66', 'psa', 88, 4, '«Я поставил завет с избранным Моим, клялся Давиду, рабу Моему:');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23659, 'rst66', 'psa', 88, 5, 'навек утвержу семя твое, в род и род устрою престол твой».');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23660, 'rst66', 'psa', 88, 6, 'И небеса прославят чудные дела Твои, Господи, и истину Твою в собрании святых.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23661, 'rst66', 'psa', 88, 7, 'Ибо кто на небесах сравнится с Господом? кто между сынами Божиими уподобится Господу?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23662, 'rst66', 'psa', 88, 8, 'Страшен Бог в великом сонме святых, страшен Он для всех окружающих Его.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23663, 'rst66', 'psa', 88, 9, 'Господи, Боже сил! кто силен, как Ты, Господи? И истина Твоя окрест Тебя.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23664, 'rst66', 'psa', 88, 10, 'Ты владычествуешь над яростью моря: когда воздымаются волны его, Ты укрощаешь их.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23665, 'rst66', 'psa', 88, 11, 'Ты низложил Раава, как пораженного; крепкою мышцею Твоею рассеял врагов Твоих.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23666, 'rst66', 'psa', 88, 12, 'Твои небеса и Твоя земля; вселенную и что наполняет ее Ты основал.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23667, 'rst66', 'psa', 88, 13, 'Север и юг Ты сотворил; Фавор и Ермон о имени Твоем радуются.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23668, 'rst66', 'psa', 88, 14, 'Крепка мышца Твоя, сильна рука Твоя, высока десница Твоя!');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23669, 'rst66', 'psa', 88, 15, 'Правосудие и правота &mdash; основание престола Твоего; милость и истина предходят пред лицом Твоим.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23670, 'rst66', 'psa', 88, 16, 'Блажен народ, знающий трубный зов! Они ходят во свете лица Твоего, Господи,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23671, 'rst66', 'psa', 88, 17, 'о имени Твоем радуются весь день и правдою Твоею возносятся,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23672, 'rst66', 'psa', 88, 18, 'ибо Ты украшение силы их, и благоволением Твоим возвышается рог наш.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23673, 'rst66', 'psa', 88, 19, 'От Господа &mdash; щит наш, и от Святого Израилева &mdash; царь наш.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23674, 'rst66', 'psa', 88, 20, 'Некогда говорил Ты в видении святому Твоему, и сказал: «Я оказал помощь мужественному, вознес избранного из народа.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23675, 'rst66', 'psa', 88, 21, 'Я обрел Давида, раба Моего, святым елеем Моим помазал его.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23676, 'rst66', 'psa', 88, 22, 'Рука Моя пребудет с ним, и мышца Моя укрепит его.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23677, 'rst66', 'psa', 88, 23, 'Враг не превозможет его, и сын беззакония не притеснит его.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23678, 'rst66', 'psa', 88, 24, 'Сокрушу пред ним врагов его и поражу ненавидящих его.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23679, 'rst66', 'psa', 88, 25, 'И истина Моя и милость Моя с ним, и Моим именем возвысится рог его.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23680, 'rst66', 'psa', 88, 26, 'И положу на море руку его, и на реки &mdash; десницу его.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23681, 'rst66', 'psa', 88, 27, 'Он будет звать Меня: Ты отец мой, Бог мой и твердыня спасения моего.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23682, 'rst66', 'psa', 88, 28, 'И Я сделаю его первенцем, превыше царей земли,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23683, 'rst66', 'psa', 88, 29, 'вовек сохраню ему милость Мою, и завет Мой с ним будет верен.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23684, 'rst66', 'psa', 88, 30, 'И продолжу вовек семя его, и престол его &mdash; как дни неба.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23685, 'rst66', 'psa', 88, 31, 'Если сыновья его оставят закон Мой и не будут ходить по заповедям Моим;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23686, 'rst66', 'psa', 88, 32, 'если нарушат уставы Мои и повелений Моих не сохранят:');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23687, 'rst66', 'psa', 88, 33, 'посещу жезлом беззаконие их, и ударами &mdash; неправду их;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23688, 'rst66', 'psa', 88, 34, 'милости же Моей не отниму от него, и не изменю истины Моей.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23689, 'rst66', 'psa', 88, 35, 'Не нарушу завета Моего, и не переменю того, что вышло из уст Моих.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23690, 'rst66', 'psa', 88, 36, 'Однажды Я поклялся святостью Моею: солгу ли Давиду?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23691, 'rst66', 'psa', 88, 37, 'Семя его пребудет вечно, и престол его, как солнце, предо Мною,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23692, 'rst66', 'psa', 88, 38, 'вовек будет тверд, как луна, и верный свидетель на небесах».');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23693, 'rst66', 'psa', 88, 39, 'Но <i>ныне</i> Ты отринул и презрел, прогневался на помазанника Твоего;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23694, 'rst66', 'psa', 88, 40, 'пренебрег завет с рабом Твоим, поверг на землю венец его;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23695, 'rst66', 'psa', 88, 41, 'разрушил все ограды его, превратил в развалины крепости его.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23696, 'rst66', 'psa', 88, 42, 'Расхищают его все проходящие путем; он сделался посмешищем у соседей своих.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23697, 'rst66', 'psa', 88, 43, 'Ты возвысил десницу противников его, обрадовал всех врагов его;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23698, 'rst66', 'psa', 88, 44, 'Ты обратил назад острие меча его и не укрепил его на брани;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23699, 'rst66', 'psa', 88, 45, 'отнял у него блеск и престол его поверг на землю;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23700, 'rst66', 'psa', 88, 46, 'сократил дни юности его и покрыл его стыдом.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23701, 'rst66', 'psa', 88, 47, 'Доколе, Господи, будешь скрываться непрестанно, будет пылать ярость Твоя, как огонь?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23702, 'rst66', 'psa', 88, 48, 'Вспомни, какой мой век: на какую суету сотворил Ты всех сынов человеческих?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23703, 'rst66', 'psa', 88, 49, 'Кто из людей жил &mdash; и не видел смерти, избавил душу свою от руки преисподней?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23704, 'rst66', 'psa', 88, 50, 'Где прежние милости Твои, Господи? Ты клялся Давиду истиною Твоею.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23705, 'rst66', 'psa', 88, 51, 'Вспомни, Господи, поругание рабов Твоих, которое я ношу в недре моем от всех сильных народов;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23706, 'rst66', 'psa', 88, 52, 'как поносят враги Твои, Господи, как бесславят следы помазанника Твоего.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23707, 'rst66', 'psa', 88, 53, 'Благословен Господь вовек! Аминь, аминь.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23708, 'rst66', 'psa', 89, 1, 'Молитва Моисея, человека Божия.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23709, 'rst66', 'psa', 89, 2, 'Господи! Ты нам прибежище в род и род.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23710, 'rst66', 'psa', 89, 3, 'Прежде нежели родились горы, и Ты образовал землю и вселенную, и от века и до века Ты &mdash; Бог.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23711, 'rst66', 'psa', 89, 4, 'Ты возвращаешь человека в тление и говоришь: «возвратитесь, сыны человеческие!»');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23712, 'rst66', 'psa', 89, 5, 'Ибо пред очами Твоими тысяча лет, как день вчерашний, когда он прошел, и <i>как</i> стража в ночи.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23713, 'rst66', 'psa', 89, 6, 'Ты <i>как</i> наводнением уносишь их; они &mdash; <i>как</i> сон, как трава, которая утром вырастает, утром цветет и зеленеет, вечером подсекается и засыхает;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23714, 'rst66', 'psa', 89, 7, 'ибо мы исчезаем от гнева Твоего, и от ярости Твоей мы в смятении.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23715, 'rst66', 'psa', 89, 8, 'Ты положил беззакония наши пред Тобою и тайное наше пред светом лица Твоего.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23716, 'rst66', 'psa', 89, 9, 'Все дни наши прошли во гневе Твоем; мы теряем лета наши, как звук.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23717, 'rst66', 'psa', 89, 10, 'Дней лет наших &mdash; семьдесят лет, а при большей крепости &mdash; восемьдесят лет; и самая лучшая пора их &mdash; труд и болезнь, ибо проходят быстро, и мы летим.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23718, 'rst66', 'psa', 89, 11, 'Кто знает силу гнева Твоего, и ярость Твою по мере страха Твоего?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23719, 'rst66', 'psa', 89, 12, 'Научи нас так счислять дни наши, чтобы нам приобрести сердце мудрое.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23720, 'rst66', 'psa', 89, 13, 'Обратись, Господи! Доколе? Умилосердись над рабами Твоими.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23721, 'rst66', 'psa', 89, 14, 'Рано насыти нас милостью Твоею, и мы будем радоваться и веселиться во все дни наши.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23722, 'rst66', 'psa', 89, 15, 'Возвесели нас за дни, <i>в которые</i> Ты поражал нас, за лета, <i>в которые</i> мы видели бедствие.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23723, 'rst66', 'psa', 89, 16, 'Да явится на рабах Твоих дело Твое и на сынах их слава Твоя;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23724, 'rst66', 'psa', 89, 17, 'и да будет благоволение Господа Бога нашего на нас, и в деле рук наших споспешествуй нам, в деле рук наших споспешествуй.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23725, 'rst66', 'psa', 90, 1, 'Живущий под кровом Всевышнего под сенью Всемогущего покоится,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23726, 'rst66', 'psa', 90, 2, 'говорит Господу: «прибежище мое и защита моя, Бог мой, на Которого я уповаю!»');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23727, 'rst66', 'psa', 90, 3, 'Он избавит тебя от сети ловца, от гибельной язвы,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23728, 'rst66', 'psa', 90, 4, 'перьями Своими осенит тебя, и под крыльями Его будешь безопасен; щит и ограждение &mdash; истина Его.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23729, 'rst66', 'psa', 90, 5, 'Не убоишься ужасов в ночи, стрелы, летящей днем,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23730, 'rst66', 'psa', 90, 6, 'язвы, ходящей во мраке, заразы, опустошающей в полдень.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23731, 'rst66', 'psa', 90, 7, 'Падут подле тебя тысяча и десять тысяч одесную тебя; но к тебе не приблизится:');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23732, 'rst66', 'psa', 90, 8, 'только смотреть будешь очами твоими и видеть возмездие нечестивым.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23733, 'rst66', 'psa', 90, 9, 'Ибо ты <i>сказал:</i> «Господь &mdash; упование мое»; Всевышнего избрал ты прибежищем твоим;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23734, 'rst66', 'psa', 90, 10, 'не приключится тебе зло, и язва не приблизится к жилищу твоему;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23735, 'rst66', 'psa', 90, 11, 'ибо Ангелам Своим заповедает о тебе &mdash; охранять тебя на всех путях твоих:');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23736, 'rst66', 'psa', 90, 12, 'на руках понесут тебя, да не преткнешься о камень ногою твоею;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23737, 'rst66', 'psa', 90, 13, 'на аспида и василиска наступишь; попирать будешь льва и дракона.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23738, 'rst66', 'psa', 90, 14, '«За то, что он возлюбил Меня, избавлю его; защищу его, потому что он познал имя Мое.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23739, 'rst66', 'psa', 90, 15, 'Воззовет ко Мне, и услышу его; с ним Я в скорби; избавлю его и прославлю его,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23740, 'rst66', 'psa', 90, 16, 'долготою дней насыщу его, и явлю ему спасение Мое».');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23741, 'rst66', 'psa', 91, 1, 'Псалом. Песнь на день субботний.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23742, 'rst66', 'psa', 91, 2, 'Благо есть славить Господа и петь имени Твоему, Всевышний,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23743, 'rst66', 'psa', 91, 3, 'возвещать утром милость Твою и истину Твою в ночи,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23744, 'rst66', 'psa', 91, 4, 'на десятиструнном и псалтири, с песнью на гуслях.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23745, 'rst66', 'psa', 91, 5, 'Ибо Ты возвеселил меня, Господи, творением Твоим: я восхищаюсь делами рук Твоих.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23746, 'rst66', 'psa', 91, 6, 'Как велики дела Твои, Господи! дивно глубоки помышления Твои!');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23747, 'rst66', 'psa', 91, 7, 'Человек несмысленный не знает, и невежда не разумеет того.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23748, 'rst66', 'psa', 91, 8, 'Тогда как нечестивые возникают, как трава, и делающие беззаконие цветут, чтобы исчезнуть навеки, &mdash;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23749, 'rst66', 'psa', 91, 9, 'Ты, Господи, высок вовеки!');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23750, 'rst66', 'psa', 91, 10, 'Ибо вот, враги Твои, Господи, &mdash; вот, враги Твои гибнут, и рассыпаются все делающие беззаконие;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23751, 'rst66', 'psa', 91, 11, 'а мой рог Ты возносишь, как рог единорога, и я умащен свежим елеем;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23752, 'rst66', 'psa', 91, 12, 'и око мое смотрит на врагов моих, и уши мои слышат о восстающих на меня злодеях.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23753, 'rst66', 'psa', 91, 13, 'Праведник цветет, как пальма, возвышается подобно кедру на Ливане.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23754, 'rst66', 'psa', 91, 14, 'Насажденные в доме Господнем, они цветут во дворах Бога нашего;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23755, 'rst66', 'psa', 91, 15, 'они и в старости плодовиты, сочны и свежи,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23756, 'rst66', 'psa', 91, 16, 'чтобы возвещать, что праведен Господь, твердыня моя, и нет неправды в Нем.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23757, 'rst66', 'psa', 92, 1, 'Господь царствует; Он облечен величием, облечен Господь могуществом <i>и</i> препоясан: потому вселенная тверда, не подвигнется.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23758, 'rst66', 'psa', 92, 2, 'Престол Твой утвержден искони: Ты &mdash; от века.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23759, 'rst66', 'psa', 92, 3, 'Возвышают реки, Господи, возвышают реки голос свой, возвышают реки волны свои.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23760, 'rst66', 'psa', 92, 4, 'Но паче шума вод многих, сильных волн морских, силен в вышних Господь.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23761, 'rst66', 'psa', 92, 5, 'Откровения Твои несомненно верны. Дому Твоему, Господи, принадлежит святость на долгие дни.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23762, 'rst66', 'psa', 93, 1, 'Боже отмщений, Господи, Боже отмщений, яви Себя!');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23763, 'rst66', 'psa', 93, 2, 'Восстань, Судия земли, воздай возмездие гордым.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23764, 'rst66', 'psa', 93, 3, 'Доколе, Господи, нечестивые, доколе нечестивые торжествовать будут?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23765, 'rst66', 'psa', 93, 4, 'Они изрыгают дерзкие речи; величаются все делающие беззаконие;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23766, 'rst66', 'psa', 93, 5, 'попирают народ Твой, Господи, угнетают наследие Твое;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23767, 'rst66', 'psa', 93, 6, 'вдову и пришельца убивают, и сирот умерщвляют');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23768, 'rst66', 'psa', 93, 7, 'и говорят: «не увидит Господь, и не узнает Бог Иаковлев».');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23769, 'rst66', 'psa', 93, 8, 'Образумьтесь, бессмысленные люди! когда вы будете умны, невежды?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23770, 'rst66', 'psa', 93, 9, 'Насадивший ухо не услышит ли? и образовавший глаз не увидит ли?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23771, 'rst66', 'psa', 93, 10, 'Вразумляющий народы неужели не обличит, &mdash; Тот, Кто учит человека разумению?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23772, 'rst66', 'psa', 93, 11, 'Господь знает мысли человеческие, что они суетны.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23773, 'rst66', 'psa', 93, 12, 'Блажен человек, которого вразумляешь Ты, Господи, и наставляешь законом Твоим,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23774, 'rst66', 'psa', 93, 13, 'чтобы дать ему покой в бедственные дни, доколе нечестивому выроется яма!');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23775, 'rst66', 'psa', 93, 14, 'Ибо не отринет Господь народа Своего и не оставит наследия Своего.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23776, 'rst66', 'psa', 93, 15, 'Ибо суд возвратится к правде, и за ним <i>последуют</i> все правые сердцем.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23777, 'rst66', 'psa', 93, 16, 'Кто восстанет за меня против злодеев? кто станет за меня против делающих беззаконие?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23778, 'rst66', 'psa', 93, 17, 'Если бы не Господь был мне помощником, вскоре вселилась бы душа моя в <i>страну</i> молчания.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23779, 'rst66', 'psa', 93, 18, 'Когда я говорил: «колеблется нога моя», &mdash; милость Твоя, Господи, поддерживала меня.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23780, 'rst66', 'psa', 93, 19, 'При умножении скорбей моих в сердце моем утешения Твои услаждают душу мою.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23781, 'rst66', 'psa', 93, 20, 'Станет ли близ Тебя седалище губителей, умышляющих насилие вопреки закону?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23782, 'rst66', 'psa', 93, 21, 'Толпою устремляются они на душу праведника и осуждают кровь неповинную.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23783, 'rst66', 'psa', 93, 22, 'Но Господь &mdash; защита моя, и Бог мой &mdash; твердыня убежища моего,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23784, 'rst66', 'psa', 93, 23, 'и обратит на них беззаконие их, и злодейством их истребит их, истребит их Господь Бог наш.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23785, 'rst66', 'psa', 94, 1, 'Приидите, воспоем Господу, воскликнем твердыне спасения нашего;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23786, 'rst66', 'psa', 94, 2, 'предстанем лицу Его со славословием, в песнях воскликнем Ему,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23787, 'rst66', 'psa', 94, 3, 'ибо Господь есть Бог великий и Царь великий над всеми богами.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23788, 'rst66', 'psa', 94, 4, 'В Его руке глубины земли, и вершины гор &mdash; Его же;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23789, 'rst66', 'psa', 94, 5, 'Его &mdash; море, и Он создал его, и сушу образовали руки Его.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23790, 'rst66', 'psa', 94, 6, 'Приидите, поклонимся и припадем, преклоним колени пред лицом Господа, Творца нашего;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23791, 'rst66', 'psa', 94, 7, 'ибо Он есть Бог наш, и мы &mdash; народ паствы Его и овцы руки Его. О, если бы вы ныне послушали гласа Его:');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23792, 'rst66', 'psa', 94, 8, '«Не ожесточите сердца вашего, как в Мериве, как в день искушения в пустыне,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23793, 'rst66', 'psa', 94, 9, 'где искушали Меня отцы ваши, испытывали Меня, и видели дело Мое.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23794, 'rst66', 'psa', 94, 10, 'Сорок лет Я был раздражаем родом сим, и сказал: это народ, заблуждающийся сердцем; они не познали путей Моих,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23795, 'rst66', 'psa', 94, 11, 'и потому Я поклялся во гневе Моем, что они не войдут в покой Мой».');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23796, 'rst66', 'psa', 95, 1, 'Воспойте Господу песнь новую; воспойте Господу, вся земля;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23797, 'rst66', 'psa', 95, 2, 'пойте Господу, благословляйте имя Его, благовествуйте со дня на день спасение Его;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23798, 'rst66', 'psa', 95, 3, 'возвещайте в народах славу Его, во всех племенах чудеса Его;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23799, 'rst66', 'psa', 95, 4, 'ибо велик Господь и достохвален, страшен Он паче всех богов.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23800, 'rst66', 'psa', 95, 5, 'Ибо все боги народов &mdash; идолы, а Господь небеса сотворил.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23801, 'rst66', 'psa', 95, 6, 'Слава и величие пред лицом Его, сила и великолепие во святилище Его.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23802, 'rst66', 'psa', 95, 7, 'Воздайте Господу, племена народов, воздайте Господу славу и честь;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23803, 'rst66', 'psa', 95, 8, 'воздайте Господу славу имени Его, несите дары и идите во дворы Его;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23804, 'rst66', 'psa', 95, 9, 'поклонитесь Господу во благолепии святыни. Трепещи пред лицом Его, вся земля!');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23805, 'rst66', 'psa', 95, 10, 'Скажите народам: Господь царствует! потому тверда вселенная, не поколеблется. Он будет судить народы по правде.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23806, 'rst66', 'psa', 95, 11, 'Да веселятся небеса и да торжествует земля; да шумит море и что наполняет его;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23807, 'rst66', 'psa', 95, 12, 'да радуется поле и все, что на нем, и да ликуют все дерева дубравные');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23808, 'rst66', 'psa', 95, 13, 'пред лицом Господа; ибо идет, ибо идет судить землю. Он будет судить вселенную по правде, и народы &mdash; по истине Своей.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23809, 'rst66', 'psa', 96, 1, 'Господь царствует: да радуется земля; да веселятся многочисленные острова.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23810, 'rst66', 'psa', 96, 2, 'Облако и мрак окрест Его; правда и суд &mdash; основание престола Его.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23811, 'rst66', 'psa', 96, 3, 'Пред Ним идет огонь и вокруг попаляет врагов Его.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23812, 'rst66', 'psa', 96, 4, 'Молнии Его освещают вселенную; земля видит и трепещет.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23813, 'rst66', 'psa', 96, 5, 'Горы, как воск, тают от лица Господа, от лица Господа всей земли.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23814, 'rst66', 'psa', 96, 6, 'Небеса возвещают правду Его, и все народы видят славу Его.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23815, 'rst66', 'psa', 96, 7, 'Да постыдятся все служащие истуканам, хвалящиеся идолами. Поклонитесь пред Ним, все боги.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23816, 'rst66', 'psa', 96, 8, 'Слышит Сион и радуется, и веселятся дщери Иудины ради судов Твоих, Господи,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23817, 'rst66', 'psa', 96, 9, 'ибо Ты, Господи, высок над всею землею, превознесен над всеми богами.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23818, 'rst66', 'psa', 96, 10, 'Любящие Господа, ненавидьте зло! Он хранит души святых Своих; из руки нечестивых избавляет их.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23819, 'rst66', 'psa', 96, 11, 'Свет сияет на праведника, и на правых сердцем &mdash; веселие.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23820, 'rst66', 'psa', 96, 12, 'Радуйтесь, праведные, о Господе и славьте память святыни Его.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23821, 'rst66', 'psa', 97, 0, 'Псалом.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23822, 'rst66', 'psa', 97, 1, 'Воспойте Господу новую песнь, ибо Он сотворил чудеса. Его десница и святая мышца Его доставили Ему победу.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23823, 'rst66', 'psa', 97, 2, 'Явил Господь спасение Свое, открыл пред очами народов правду Свою.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23824, 'rst66', 'psa', 97, 3, 'Вспомнил Он милость Свою и верность Свою к дому Израилеву. Все концы земли увидели спасение Бога нашего.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23825, 'rst66', 'psa', 97, 4, 'Восклицайте Господу, вся земля; торжествуйте, веселитесь и пойте;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23826, 'rst66', 'psa', 97, 5, 'пойте Господу с гуслями, с гуслями и с гласом псалмопения;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23827, 'rst66', 'psa', 97, 6, 'при звуке труб и рога торжествуйте пред Царем Господом.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23828, 'rst66', 'psa', 97, 7, 'Да шумит море и что наполняет его, вселенная и живущие в ней;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23829, 'rst66', 'psa', 97, 8, 'да рукоплещут реки, да ликуют вместе горы');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23830, 'rst66', 'psa', 97, 9, 'пред лицом Господа, ибо Он идет судить землю. Он будет судить вселенную праведно и народы &mdash; верно.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23831, 'rst66', 'psa', 98, 1, 'Господь царствует: да трепещут народы! Он восседает на херувимах: да трясется земля!');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23832, 'rst66', 'psa', 98, 2, 'Господь на Сионе велик, и высок Он над всеми народами.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23833, 'rst66', 'psa', 98, 3, 'Да славят великое и страшное имя Твое: свято оно!');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23834, 'rst66', 'psa', 98, 4, 'И могущество царя любит суд. Ты утвердил справедливость; суд и правду Ты совершил в Иакове.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23835, 'rst66', 'psa', 98, 5, 'Превозносите Господа, Бога нашего, и поклоняйтесь подножию Его: свято оно!');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23836, 'rst66', 'psa', 98, 6, 'Моисей и Аарон между священниками и Самуил между призывающими имя Его взывали к Господу, и Он внимал им.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23837, 'rst66', 'psa', 98, 7, 'В столпе облачном говорил Он к ним; они хранили Его заповеди и устав, который Он дал им.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23838, 'rst66', 'psa', 98, 8, 'Господи, Боже наш! Ты внимал им; Ты был для них Богом прощающим и наказывающим за дела их.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23839, 'rst66', 'psa', 98, 9, 'Превозносите Господа, Бога нашего, и поклоняйтесь на святой горе Его, ибо свят Господь, Бог наш.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23840, 'rst66', 'psa', 99, 0, 'Псалом хвалебный.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23841, 'rst66', 'psa', 99, 1, 'Воскликните Господу, вся земля!');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23842, 'rst66', 'psa', 99, 2, 'Служите Господу с веселием; идите пред лицо Его с восклицанием!');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23843, 'rst66', 'psa', 99, 3, 'Познайте, что Господь есть Бог, что Он сотворил нас, и мы &mdash; Его, Его народ и овцы паствы Его.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23844, 'rst66', 'psa', 99, 4, 'Входите во врата Его со славословием, во дворы Его &mdash; с хвалою. Славьте Его, благословляйте имя Его,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23845, 'rst66', 'psa', 99, 5, 'ибо благ Господь: милость Его вовек, и истина Его в род и род.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23846, 'rst66', 'psa', 100, 0, 'Псалом Давида.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23847, 'rst66', 'psa', 100, 1, 'Милость и суд буду петь; Тебе, Господи, буду петь.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23848, 'rst66', 'psa', 100, 2, 'Буду размышлять о пути непорочном: «когда ты придешь ко мне?» Буду ходить в непорочности моего сердца посреди дома моего.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23849, 'rst66', 'psa', 100, 3, 'Не положу пред очами моими вещи непотребной; дело преступное я ненавижу: не прилепится оно ко мне.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23850, 'rst66', 'psa', 100, 4, 'Сердце развращенное будет удалено от меня; злого я не буду знать.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23851, 'rst66', 'psa', 100, 5, 'Тайно клевещущего на ближнего своего изгоню; гордого очами и надменного сердцем не потерплю.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23852, 'rst66', 'psa', 100, 6, 'Глаза мои на верных земли, чтобы они пребывали при мне; кто ходит путем непорочности, тот будет служить мне.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23853, 'rst66', 'psa', 100, 7, 'Не будет жить в доме моем поступающий коварно; говорящий ложь не останется пред глазами моими.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23854, 'rst66', 'psa', 100, 8, 'С раннего утра буду истреблять всех нечестивцев земли, дабы искоренить из града Господня всех делающих беззаконие.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23855, 'rst66', 'psa', 101, 1, 'Молитва страждущего, когда он унывает и изливает пред Господом печаль свою.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23856, 'rst66', 'psa', 101, 2, 'Господи! услышь молитву мою, и вопль мой да придет к Тебе.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23857, 'rst66', 'psa', 101, 3, 'Не скрывай лица Твоего от меня; в день скорби моей приклони ко мне ухо Твое; в день, когда воззову к Тебе, скоро услышь меня;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23858, 'rst66', 'psa', 101, 4, 'Ибо исчезли, как дым, дни мои, и кости мои обожжены, как головня;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23859, 'rst66', 'psa', 101, 5, 'сердце мое поражено, и иссохло, как трава, так что я забываю есть хлеб мой;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23860, 'rst66', 'psa', 101, 6, 'от голоса стенания моего кости мои прильпнули к плоти моей.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23861, 'rst66', 'psa', 101, 7, 'Я уподобился пеликану в пустыне; я стал как филин на развалинах;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23862, 'rst66', 'psa', 101, 8, 'не сплю и сижу, как одинокая птица на кровле.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23863, 'rst66', 'psa', 101, 9, 'Всякий день поносят меня враги мои, и злобствующие на меня клянут мною.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23864, 'rst66', 'psa', 101, 10, 'Я ем пепел, как хлеб, и питье мое растворяю слезами,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23865, 'rst66', 'psa', 101, 11, 'от гнева Твоего и негодования Твоего, ибо Ты вознес меня и низверг меня.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23866, 'rst66', 'psa', 101, 12, 'Дни мои &mdash; как уклоняющаяся тень, и я иссох, как трава.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23867, 'rst66', 'psa', 101, 13, 'Ты же, Господи, вовек пребываешь, и память о Тебе в род и род.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23868, 'rst66', 'psa', 101, 14, 'Ты восстанешь, умилосердишься над Сионом, ибо время помиловать его, &mdash; ибо пришло время;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23869, 'rst66', 'psa', 101, 15, 'ибо рабы Твои возлюбили и камни его, и о прахе его жалеют.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23870, 'rst66', 'psa', 101, 16, 'И убоятся народы имени Господня, и все цари земные &mdash; славы Твоей.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23871, 'rst66', 'psa', 101, 17, 'Ибо созиждет Господь Сион и явится во славе Своей;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23872, 'rst66', 'psa', 101, 18, 'призрит на молитву беспомощных и не презрит моления их.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23873, 'rst66', 'psa', 101, 19, 'Напишется о сем для рода последующего, и поколение грядущее восхвалит Господа,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23874, 'rst66', 'psa', 101, 20, 'ибо Он приникнул со святой высоты Своей, с небес призрел Господь на землю,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23875, 'rst66', 'psa', 101, 21, 'чтобы услышать стон узников, разрешить сынов смерти,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23876, 'rst66', 'psa', 101, 22, 'дабы возвещали на Сионе имя Господне и хвалу Его &mdash; в Иерусалиме,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23877, 'rst66', 'psa', 101, 23, 'когда соберутся народы вместе и царства для служения Господу.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23878, 'rst66', 'psa', 101, 24, 'Изнурил Он на пути силы мои, сократил дни мои.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23879, 'rst66', 'psa', 101, 25, 'Я сказал: Боже мой! не восхити меня в половине дней моих. Твои лета в роды родов.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23880, 'rst66', 'psa', 101, 26, 'В начале Ты, основал землю, и небеса &mdash; дело Твоих рук;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23881, 'rst66', 'psa', 101, 27, 'они погибнут, а Ты пребудешь; и все они, как риза, обветшают, и, как одежду, Ты переменишь их, и изменятся;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23882, 'rst66', 'psa', 101, 28, 'но Ты &mdash; тот же, и лета Твои не кончатся.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23883, 'rst66', 'psa', 101, 29, 'Сыны рабов Твоих будут жить, и семя их утвердится пред лицом Твоим.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23884, 'rst66', 'psa', 102, 0, 'Псалом Давида.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23885, 'rst66', 'psa', 102, 1, 'Благослови, душа моя, Господа, и вся внутренность моя &mdash; святое имя Его.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23886, 'rst66', 'psa', 102, 2, 'Благослови, душа моя, Господа и не забывай всех благодеяний Его.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23887, 'rst66', 'psa', 102, 3, 'Он прощает все беззакония твои, исцеляет все недуги твои;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23888, 'rst66', 'psa', 102, 4, 'избавляет от могилы жизнь твою, венчает тебя милостью и щедротами;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23889, 'rst66', 'psa', 102, 5, 'насыщает благами желание твое: обновляется, подобно орлу, юность твоя.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23890, 'rst66', 'psa', 102, 6, 'Господь творит правду и суд всем обиженным.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23891, 'rst66', 'psa', 102, 7, 'Он показал пути Свои Моисею, сынам Израилевым &mdash; дела Свои.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23892, 'rst66', 'psa', 102, 8, 'Щедр и милостив Господь, долготерпелив и многомилостив:');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23893, 'rst66', 'psa', 102, 9, 'не до конца гневается, и не вовек негодует.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23894, 'rst66', 'psa', 102, 10, 'Не по беззакониям нашим сотворил нам, и не по грехам нашим воздал нам:');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23895, 'rst66', 'psa', 102, 11, 'ибо как высоко небо над землею, так велика милость <i>Господа</i> к боящимся Его;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23896, 'rst66', 'psa', 102, 12, 'как далеко восток от запада, так удалил Он от нас беззакония наши;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23897, 'rst66', 'psa', 102, 13, 'как отец милует сынов, так милует Господь боящихся Его.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23898, 'rst66', 'psa', 102, 14, 'Ибо Он знает состав наш, помнит, что мы &mdash; персть.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23899, 'rst66', 'psa', 102, 15, 'Дни человека &mdash; как трава; как цвет полевой, так он цветет.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23900, 'rst66', 'psa', 102, 16, 'Пройдет над ним ветер, и нет его, и место его уже не узнает его.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23901, 'rst66', 'psa', 102, 17, 'Милость же Господня от века и до века к боящимся Его,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23902, 'rst66', 'psa', 102, 18, 'и правда Его на сынах сынов, хранящих завет Его и помнящих заповеди Его, чтобы исполнять их.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23903, 'rst66', 'psa', 102, 19, 'Господь на небесах поставил престол Свой, и царство Его всем обладает.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23904, 'rst66', 'psa', 102, 20, 'Благословите Господа, <i>все</i> Ангелы Его, крепкие силою, исполняющие слово Его, повинуясь гласу слова Его;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23905, 'rst66', 'psa', 102, 21, 'благословите Господа, все воинства Его, служители Его, исполняющие волю Его;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23906, 'rst66', 'psa', 102, 22, 'благословите Господа, все дела Его, во всех местах владычества Его. Благослови, душа моя, Господа!');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23907, 'rst66', 'psa', 103, 1, 'Благослови, душа моя, Господа! Господи, Боже мой! Ты дивно велик, Ты облечен славою и величием;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23908, 'rst66', 'psa', 103, 2, 'Ты одеваешься светом, как ризою, простираешь небеса, как шатер;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23909, 'rst66', 'psa', 103, 3, 'устрояешь над водами горние чертоги Твои, делаешь облака Твоею колесницею, шествуешь на крыльях ветра.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23910, 'rst66', 'psa', 103, 4, 'Ты творишь Ангелами Твоими духов, служителями Твоими &mdash; огонь пылающий.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23911, 'rst66', 'psa', 103, 5, 'Ты поставил землю на твердых основах: не поколеблется она во веки и веки.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23912, 'rst66', 'psa', 103, 6, 'Бездною, как одеянием, покрыл Ты ее, на горах стоят воды.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23913, 'rst66', 'psa', 103, 7, 'От прещения Твоего бегут они, от гласа грома Твоего быстро уходят;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23914, 'rst66', 'psa', 103, 8, 'восходят на горы, нисходят в долины, на место, которое Ты назначил для них.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23915, 'rst66', 'psa', 103, 9, 'Ты положил предел, которого не перейдут, и не возвратятся покрыть землю.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23916, 'rst66', 'psa', 103, 10, 'Ты послал источники в долины: между горами текут,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23917, 'rst66', 'psa', 103, 11, 'поят всех полевых зверей; дикие ослы утоляют жажду свою.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23918, 'rst66', 'psa', 103, 12, 'При них обитают птицы небесные, из среды ветвей издают голос.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23919, 'rst66', 'psa', 103, 13, 'Ты напояешь горы с высот Твоих, плодами дел Твоих насыщается земля.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23920, 'rst66', 'psa', 103, 14, 'Ты произращаешь траву для скота, и зелень на пользу человека, чтобы произвести из земли пищу,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23921, 'rst66', 'psa', 103, 15, 'и вино, которое веселит сердце человека, и елей, от которого блистает лицо его, и хлеб, который укрепляет сердце человека.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23922, 'rst66', 'psa', 103, 16, 'Насыщаются древа Господа, кедры Ливанские, которые Он насадил;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23923, 'rst66', 'psa', 103, 17, 'на них гнездятся птицы: ели &mdash; жилище аисту,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23924, 'rst66', 'psa', 103, 18, 'высокие горы &mdash; сернам; каменные утесы &mdash; убежище зайцам.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23925, 'rst66', 'psa', 103, 19, 'Он сотворил луну для <i>указания</i> времен, солнце знает свой запад.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23926, 'rst66', 'psa', 103, 20, 'Ты простираешь тьму &mdash; и бывает ночь: во время нее бродят все лесные звери;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23927, 'rst66', 'psa', 103, 21, 'львы рыкают о добыче и просят у Бога пищу себе.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23928, 'rst66', 'psa', 103, 22, 'Восходит солнце, <i>и</i> они собираются и ложатся в свои логовища;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23929, 'rst66', 'psa', 103, 23, 'выходит человек на дело свое и на работу свою до вечера.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23930, 'rst66', 'psa', 103, 24, 'Как многочисленны дела Твои, Господи! Все соделал Ты премудро; земля полна произведений Твоих.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23931, 'rst66', 'psa', 103, 25, 'Это &mdash; море великое и пространное: там пресмыкающиеся, которым нет числа, животные малые с большими;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23932, 'rst66', 'psa', 103, 26, 'там плавают корабли, там этот левиафан, которого Ты сотворил играть в нем.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23933, 'rst66', 'psa', 103, 27, 'Все они от Тебя ожидают, чтобы Ты дал им пищу их в свое время.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23934, 'rst66', 'psa', 103, 28, 'Даешь им &mdash; принимают, отверзаешь руку Твою &mdash; насыщаются благом;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23935, 'rst66', 'psa', 103, 29, 'скроешь лицо Твое &mdash; мятутся, отнимешь дух их &mdash; умирают и в персть свою возвращаются;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23936, 'rst66', 'psa', 103, 30, 'пошлешь дух Твой &mdash; созидаются, и Ты обновляешь лицо земли.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23937, 'rst66', 'psa', 103, 31, 'Да будет Господу слава вовеки; да веселится Господь о делах Своих!');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23938, 'rst66', 'psa', 103, 32, 'Призирает на землю, и она трясется; прикасается к горам, и дымятся.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23939, 'rst66', 'psa', 103, 33, 'Буду петь Господу во <i>всю</i> жизнь мою, буду петь Богу моему, доколе есмь.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23940, 'rst66', 'psa', 103, 34, 'Да будет благоприятна Ему песнь моя; буду веселиться о Господе.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23941, 'rst66', 'psa', 103, 35, 'Да исчезнут грешники с земли, и беззаконных да не будет более. Благослови, душа моя, Господа! Аллилуия!');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23942, 'rst66', 'psa', 104, 1, 'Славьте Господа; призывайте имя Его; возвещайте в народах дела Его;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23943, 'rst66', 'psa', 104, 2, 'воспойте Ему и пойте Ему; поведайте о всех чудесах Его.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23944, 'rst66', 'psa', 104, 3, 'Хвалитесь именем Его святым; да веселится сердце ищущих Господа.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23945, 'rst66', 'psa', 104, 4, 'Ищите Господа и силы Его, ищите лица Его всегда.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23946, 'rst66', 'psa', 104, 5, 'Воспоминайте чудеса Его, которые сотворил, знамения Его и суды уст Его,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23947, 'rst66', 'psa', 104, 6, 'вы, семя Авраамово, рабы Его, сыны Иакова, избранные Его.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23948, 'rst66', 'psa', 104, 7, 'Он Господь Бог наш: по всей земле суды Его.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23949, 'rst66', 'psa', 104, 8, 'Вечно помнит завет Свой, слово, <i>которое</i> заповедал в тысячу родов,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23950, 'rst66', 'psa', 104, 9, 'которое завещал Аврааму, и клятву Свою Исааку,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23951, 'rst66', 'psa', 104, 10, 'и поставил то Иакову в закон и Израилю в завет вечный,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23952, 'rst66', 'psa', 104, 11, 'говоря: «тебе дам землю Ханаанскую в удел наследия вашего».');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23953, 'rst66', 'psa', 104, 12, 'Когда их было еще мало числом, очень мало, и они были пришельцами в ней');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23954, 'rst66', 'psa', 104, 13, 'и переходили от народа к народу, из царства к иному племени,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23955, 'rst66', 'psa', 104, 14, 'никому не позволял обижать их и возбранял о них царям:');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23956, 'rst66', 'psa', 104, 15, '«Не прикасайтесь к помазанным Моим, и пророкам Моим не делайте зла».');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23957, 'rst66', 'psa', 104, 16, 'И призвал голод на землю; всякий стебель хлебный истребил.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23958, 'rst66', 'psa', 104, 17, 'Послал пред ними человека: в рабы продан был Иосиф.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23959, 'rst66', 'psa', 104, 18, 'Стеснили оковами ноги его; в железо вошла душа его,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23960, 'rst66', 'psa', 104, 19, 'доколе исполнилось слово Его: слово Господне испытало его.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23961, 'rst66', 'psa', 104, 20, 'Послал царь, и разрешил его владетель народов и освободил его;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23962, 'rst66', 'psa', 104, 21, 'поставил его господином над домом своим и правителем над всем владением своим,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23963, 'rst66', 'psa', 104, 22, 'чтобы он наставлял вельмож его по своей душе и старейшин его учил мудрости.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23964, 'rst66', 'psa', 104, 23, 'Тогда пришел Израиль в Египет, и переселился Иаков в землю Хамову.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23965, 'rst66', 'psa', 104, 24, 'И весьма размножил <i>Бог</i> народ Свой и сделал его сильнее врагов его.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23966, 'rst66', 'psa', 104, 25, 'Возбудил в сердце их ненависть против народа Его и ухищрение против рабов Его.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23967, 'rst66', 'psa', 104, 26, 'Послал Моисея, раба Своего, Аарона, которого избрал.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23968, 'rst66', 'psa', 104, 27, 'Они показали между ними слова знамений Его и чудеса <i>Его</i> в земле Хамовой.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23969, 'rst66', 'psa', 104, 28, 'Послал тьму и сделал мрак, и не воспротивились слову Его.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23970, 'rst66', 'psa', 104, 29, 'Преложил воду их в кровь, и уморил рыбу их.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23971, 'rst66', 'psa', 104, 30, 'Земля их произвела множество жаб <i>даже</i> в спальне царей их.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23972, 'rst66', 'psa', 104, 31, 'Он сказал, и пришли разные насекомые, скнипы во все пределы их.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23973, 'rst66', 'psa', 104, 32, 'Вместо дождя послал на них град, палящий огонь на землю их,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23974, 'rst66', 'psa', 104, 33, 'и побил виноград их и смоковницы их, и сокрушил дерева в пределах их.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23975, 'rst66', 'psa', 104, 34, 'Сказал, и пришла саранча и гусеницы без числа;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23976, 'rst66', 'psa', 104, 35, 'и съели всю траву на земле их, и съели плоды на полях их.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23977, 'rst66', 'psa', 104, 36, 'И поразил всякого первенца в земле их, начатки всей силы их.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23978, 'rst66', 'psa', 104, 37, 'И вывел <i>Израильтян</i> с серебром и золотом, и не было в коленах их болящего.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23979, 'rst66', 'psa', 104, 38, 'Обрадовался Египет исшествию их; ибо страх от них напал на него.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23980, 'rst66', 'psa', 104, 39, 'Простер облако в покров <i>им</i> и огонь, чтобы светить <i>им</i> ночью.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23981, 'rst66', 'psa', 104, 40, 'Просили, и Он послал перепелов, и хлебом небесным насыщал их.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23982, 'rst66', 'psa', 104, 41, 'Разверз камень, и потекли воды, потекли рекою по местам сухим,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23983, 'rst66', 'psa', 104, 42, 'ибо вспомнил Он святое слово Свое к Аврааму, рабу Своему,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23984, 'rst66', 'psa', 104, 43, 'и вывел народ Свой в радости, избранных Своих в веселии,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23985, 'rst66', 'psa', 104, 44, 'и дал им земли народов, и они наследовали труд иноплеменных,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23986, 'rst66', 'psa', 104, 45, 'чтобы соблюдали уставы Его и хранили законы Его. Аллилуия!');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23987, 'rst66', 'psa', 105, 0, 'Аллилуия.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23988, 'rst66', 'psa', 105, 1, 'Славьте Господа, ибо Он благ, ибо вовек милость Его.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23989, 'rst66', 'psa', 105, 2, 'Кто изречет могущество Господа, возвестит все хвалы Его?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23990, 'rst66', 'psa', 105, 3, 'Блаженны хранящие суд и творящие правду во всякое время!');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23991, 'rst66', 'psa', 105, 4, 'Вспомни о мне, Господи, в благоволении к народу Твоему; посети меня спасением Твоим,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23992, 'rst66', 'psa', 105, 5, 'дабы мне видеть благоденствие избранных Твоих, веселиться веселием народа Твоего, хвалиться с наследием Твоим.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23993, 'rst66', 'psa', 105, 6, 'Согрешили мы с отцами нашими, совершили беззаконие, соделали неправду.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23994, 'rst66', 'psa', 105, 7, 'Отцы наши в Египте не уразумели чудес Твоих, не помнили множества милостей Твоих, и возмутились у моря, у Чермного моря.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23995, 'rst66', 'psa', 105, 8, 'Но Он спас их ради имени Своего, дабы показать могущество Свое.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23996, 'rst66', 'psa', 105, 9, 'Грозно рек морю Чермному, и оно иссохло; и провел их по безднам, как по суше;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23997, 'rst66', 'psa', 105, 10, 'и спас их от руки ненавидящего и избавил их от руки врага.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23998, 'rst66', 'psa', 105, 11, 'Воды покрыли врагов их, ни одного из них не осталось.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (23999, 'rst66', 'psa', 105, 12, 'И поверили они словам Его, <i>и</i> воспели хвалу Ему.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24000, 'rst66', 'psa', 105, 13, '<i>Но</i> скоро забыли дела Его, не дождались Его изволения;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24001, 'rst66', 'psa', 105, 14, 'увлеклись похотением в пустыне, и искусили Бога в необитаемой.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24002, 'rst66', 'psa', 105, 15, 'И Он исполнил прошение их, <i>но</i> послал язву на души их.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24003, 'rst66', 'psa', 105, 16, 'И позавидовали в стане Моисею <i>и</i> Аарону, святому Господню.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24004, 'rst66', 'psa', 105, 17, 'Разверзлась земля, и поглотила Дафана и покрыла скопище Авирона.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24005, 'rst66', 'psa', 105, 18, 'И возгорелся огонь в скопище их, пламень попалил нечестивых.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24006, 'rst66', 'psa', 105, 19, 'Сделали тельца у Хорива и поклонились истукану;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24007, 'rst66', 'psa', 105, 20, 'и променяли славу свою на изображение вола, ядущего траву.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24008, 'rst66', 'psa', 105, 21, 'Забыли Бога, Спасителя своего, совершившего великое в Египте,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24009, 'rst66', 'psa', 105, 22, 'дивное в земле Хамовой, страшное у Чермного моря.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24010, 'rst66', 'psa', 105, 23, 'И хотел истребить их, если бы Моисей, избранный Его, не стал пред Ним в расселине, чтобы отвратить ярость Его, да не погубит <i>их</i>.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24011, 'rst66', 'psa', 105, 24, 'И презрели они землю желанную, не верили слову Его;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24012, 'rst66', 'psa', 105, 25, 'и роптали в шатрах своих, не слушались гласа Господня.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24013, 'rst66', 'psa', 105, 26, 'И поднял Он руку Свою на них, чтобы низложить их в пустыне,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24014, 'rst66', 'psa', 105, 27, 'низложить племя их в народах и рассеять их по землям.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24015, 'rst66', 'psa', 105, 28, 'Они прилепились к Ваалфегору и ели жертвы бездушным,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24016, 'rst66', 'psa', 105, 29, 'и раздражали <i>Бога</i> делами своими, и вторглась к ним язва.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24017, 'rst66', 'psa', 105, 30, 'И восстал Финеес и произвел суд, &mdash; и остановилась язва.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24018, 'rst66', 'psa', 105, 31, 'И <i>это</i> вменено ему в праведность в роды и роды вовеки.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24019, 'rst66', 'psa', 105, 32, 'И прогневали <i>Бога</i> у вод Меривы, и Моисей потерпел за них,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24020, 'rst66', 'psa', 105, 33, 'ибо они огорчили дух его, и он погрешил устами своими.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24021, 'rst66', 'psa', 105, 34, 'Не истребили народов, о которых сказал им Господь,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24022, 'rst66', 'psa', 105, 35, 'но смешались с язычниками и научились делам их;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24023, 'rst66', 'psa', 105, 36, 'служили истуканам их, <i>которые</i> были для них сетью,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24024, 'rst66', 'psa', 105, 37, 'и приносили сыновей своих и дочерей своих в жертву бесам;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24025, 'rst66', 'psa', 105, 38, 'проливали кровь невинную, кровь сыновей своих и дочерей своих, которых приносили в жертву идолам Ханаанским, &mdash; и осквернилась земля кровью;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24026, 'rst66', 'psa', 105, 39, 'оскверняли себя делами своими, блудодействовали поступками своими.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24027, 'rst66', 'psa', 105, 40, 'И воспылал гнев Господа на народ Его, и возгнушался Он наследием Своим');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24028, 'rst66', 'psa', 105, 41, 'и предал их в руки язычников, и ненавидящие их стали обладать ими.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24029, 'rst66', 'psa', 105, 42, 'Враги их утесняли их, и они смирялись под рукою их.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24030, 'rst66', 'psa', 105, 43, 'Много раз Он избавлял их; они же раздражали <i>Его</i> упорством своим, и были уничижаемы за беззаконие свое.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24031, 'rst66', 'psa', 105, 44, 'Но Он призирал на скорбь их, когда слышал вопль их,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24032, 'rst66', 'psa', 105, 45, 'и вспоминал завет Свой с ними и раскаивался по множеству милости Своей;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24033, 'rst66', 'psa', 105, 46, 'и возбуждал к ним сострадание во всех, пленявших их.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24034, 'rst66', 'psa', 105, 47, 'Спаси нас, Господи, Боже наш, и собери нас от народов, дабы славить святое имя Твое, хвалиться Твоею славою.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24035, 'rst66', 'psa', 105, 48, 'Благословен Господь, Бог Израилев, от века и до века! И да скажет весь народ: аминь! Аллилуия!');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24036, 'rst66', 'psa', 106, 1, 'Славьте Господа, ибо Он благ, ибо вовек милость Его!');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24037, 'rst66', 'psa', 106, 2, 'Так да скажут избавленные Господом, которых избавил Он от руки врага,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24038, 'rst66', 'psa', 106, 3, 'и собрал от стран, от востока и запада, от севера и моря.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24039, 'rst66', 'psa', 106, 4, 'Они блуждали в пустыне по безлюдному пути и не находили населенного города;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24040, 'rst66', 'psa', 106, 5, 'терпели голод и жажду, душа их истаевала в них.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24041, 'rst66', 'psa', 106, 6, 'Но воззвали к Господу в скорби своей, и Он избавил их от бедствий их,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24042, 'rst66', 'psa', 106, 7, 'и повел их прямым путем, чтобы они шли к населенному городу.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24043, 'rst66', 'psa', 106, 8, 'Да славят Господа за милость Его и за чудные дела Его для сынов человеческих:');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24044, 'rst66', 'psa', 106, 9, 'ибо Он насытил душу жаждущую и душу алчущую исполнил благами.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24045, 'rst66', 'psa', 106, 10, 'Они сидели во тьме и тени смертной, окованные скорбью и железом;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24046, 'rst66', 'psa', 106, 11, 'ибо не покорялись словам Божиим и небрегли о воле Всевышнего.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24047, 'rst66', 'psa', 106, 12, 'Он смирил сердце их работами; они преткнулись, и не было помогающего.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24048, 'rst66', 'psa', 106, 13, 'Но воззвали к Господу в скорби своей, и Он спас их от бедствий их;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24049, 'rst66', 'psa', 106, 14, 'вывел их из тьмы и тени смертной, и расторгнул узы их.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24050, 'rst66', 'psa', 106, 15, 'Да славят Господа за милость Его и за чудные дела Его для сынов человеческих:');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24051, 'rst66', 'psa', 106, 16, 'ибо Он сокрушил врата медные и вереи железные сломил.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24052, 'rst66', 'psa', 106, 17, 'Безрассудные страдали за беззаконные пути свои и за неправды свои;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24053, 'rst66', 'psa', 106, 18, 'от всякой пищи отвращалась душа их, и они приближались ко вратам смерти.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24054, 'rst66', 'psa', 106, 19, 'Но воззвали к Господу в скорби своей, и Он спас их от бедствий их;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24055, 'rst66', 'psa', 106, 20, 'послал слово Свое и исцелил их, и избавил их от могил их.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24056, 'rst66', 'psa', 106, 21, 'Да славят Господа за милость Его и за чудные дела Его для сынов человеческих!');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24057, 'rst66', 'psa', 106, 22, 'Да приносят Ему жертву хвалы и да возвещают о делах Его с пением!');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24058, 'rst66', 'psa', 106, 23, 'Отправляющиеся на кораблях в море, производящие дела на больших водах,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24059, 'rst66', 'psa', 106, 24, 'видят дела Господа и чудеса Его в пучине:');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24060, 'rst66', 'psa', 106, 25, 'Он речет, &mdash; и восстает бурный ветер и высоко поднимает волны его:');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24061, 'rst66', 'psa', 106, 26, 'восходят до небес, нисходят до бездны; душа их истаевает в бедствии;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24062, 'rst66', 'psa', 106, 27, 'они кружатся и шатаются, как пьяные, и вся мудрость их исчезает.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24063, 'rst66', 'psa', 106, 28, 'Но воззвали к Господу в скорби своей, и Он вывел их из бедствия их.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24064, 'rst66', 'psa', 106, 29, 'Он превращает бурю в тишину, и волны умолкают.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24065, 'rst66', 'psa', 106, 30, 'И веселятся, что они утихли, и Он приводит их к желаемой пристани.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24066, 'rst66', 'psa', 106, 31, 'Да славят Господа за милость Его и за чудные дела Его для сынов человеческих!');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24067, 'rst66', 'psa', 106, 32, 'Да превозносят Его в собрании народном и да славят Его в сонме старейшин!');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24068, 'rst66', 'psa', 106, 33, 'Он превращает реки в пустыню и источники вод &mdash; в сушу,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24069, 'rst66', 'psa', 106, 34, 'землю плодородную &mdash; в солончатую, за нечестие живущих на ней.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24070, 'rst66', 'psa', 106, 35, 'Он превращает пустыню в озеро, и землю иссохшую &mdash; в источники вод;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24071, 'rst66', 'psa', 106, 36, 'и поселяет там алчущих, и они строят город для обитания;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24072, 'rst66', 'psa', 106, 37, 'засевают поля, насаждают виноградники, которые приносят им обильные плоды.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24073, 'rst66', 'psa', 106, 38, 'Он благословляет их, и они весьма размножаются, и скота их не умаляет.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24074, 'rst66', 'psa', 106, 39, 'Уменьшились они и упали от угнетения, бедствия и скорби, &mdash;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24075, 'rst66', 'psa', 106, 40, 'Он изливает бесчестие на князей и оставляет их блуждать в пустыне, где нет путей.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24076, 'rst66', 'psa', 106, 41, 'Бедного же извлекает из бедствия и умножает род его, как стада овец.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24077, 'rst66', 'psa', 106, 42, 'Праведники видят сие и радуются, а всякое нечестие заграждает уста свои.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24078, 'rst66', 'psa', 106, 43, 'Кто мудр, тот заметит сие и уразумеет милость Господа.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24079, 'rst66', 'psa', 107, 1, 'Песнь. Псалом Давида.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24080, 'rst66', 'psa', 107, 2, 'Готово сердце мое, Боже; буду петь и воспевать во славе моей.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24081, 'rst66', 'psa', 107, 3, 'Воспрянь, псалтирь и гусли! Я встану рано.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24082, 'rst66', 'psa', 107, 4, 'Буду славить Тебя, Господи, между народами; буду воспевать Тебя среди племен,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24083, 'rst66', 'psa', 107, 5, 'ибо превыше небес милость Твоя и до облаков истина Твоя.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24084, 'rst66', 'psa', 107, 6, 'Будь превознесен выше небес, Боже; над всею землею <i>да будет</i> слава Твоя,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24085, 'rst66', 'psa', 107, 7, 'дабы избавились возлюбленные Твои: спаси десницею Твоею и услышь меня.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24086, 'rst66', 'psa', 107, 8, 'Бог сказал во святилище Своем: «восторжествую, разделю Сихем и долину Сокхоф размерю;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24087, 'rst66', 'psa', 107, 9, 'Мой Галаад, Мой Манассия, Ефрем &mdash; крепость главы Моей, Иуда &mdash; скипетр Мой,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24088, 'rst66', 'psa', 107, 10, 'Моав &mdash; умывальная чаша Моя, на Едома простру сапог Мой, над землею Филистимскою восклицать буду».');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24089, 'rst66', 'psa', 107, 11, 'Кто введет меня в укрепленный город? Кто доведет меня до Едома?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24090, 'rst66', 'psa', 107, 12, 'Не Ты ли, Боже, <i>Который</i> отринул нас и не выходишь, Боже, с войсками нашими?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24091, 'rst66', 'psa', 107, 13, 'Подай нам помощь в тесноте, ибо защита человеческая суетна.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24092, 'rst66', 'psa', 107, 14, 'С Богом мы окажем силу: Он низложит врагов наших.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24093, 'rst66', 'psa', 108, 0, 'Начальнику хора. Псалом Давида.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24094, 'rst66', 'psa', 108, 1, 'Боже хвалы моей! не премолчи,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24095, 'rst66', 'psa', 108, 2, 'ибо отверзлись на меня уста нечестивые и уста коварные; говорят со мною языком лживым;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24096, 'rst66', 'psa', 108, 3, 'отвсюду окружают меня словами ненависти, вооружаются против меня без причины;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24097, 'rst66', 'psa', 108, 4, 'за любовь мою они враждуют на меня, а я молюсь;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24098, 'rst66', 'psa', 108, 5, 'воздают мне за добро злом, за любовь мою &mdash; ненавистью.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24099, 'rst66', 'psa', 108, 6, 'Поставь над ним нечестивого, и диавол да станет одесную его.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24100, 'rst66', 'psa', 108, 7, 'Когда будет судиться, да выйдет виновным, и молитва его да будет в грех;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24101, 'rst66', 'psa', 108, 8, 'да будут дни его кратки, и достоинство его да возьмет другой;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24102, 'rst66', 'psa', 108, 9, 'дети его да будут сиротами, и жена его &mdash; вдовою;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24103, 'rst66', 'psa', 108, 10, 'да скитаются дети его и нищенствуют, и просят <i>хлеба</i> из развалин своих;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24104, 'rst66', 'psa', 108, 11, 'да захватит заимодавец все, что есть у него, и чужие да расхитят труд его;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24105, 'rst66', 'psa', 108, 12, 'да не будет сострадающего ему, да не будет милующего сирот его;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24106, 'rst66', 'psa', 108, 13, 'да будет потомство его на погибель, и да изгладится имя их в следующем роде;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24107, 'rst66', 'psa', 108, 14, 'да будет воспомянуто пред Господом беззаконие отцов его, и грех матери его да не изгладится;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24108, 'rst66', 'psa', 108, 15, 'да будут они всегда в очах Господа, и да истребит Он память их на земле');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24109, 'rst66', 'psa', 108, 16, 'за то, что он не думал оказывать милость, но преследовал человека бедного и нищего и сокрушенного сердцем, чтобы умертвить его;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24110, 'rst66', 'psa', 108, 17, 'возлюбил проклятие, &mdash; оно и придет на него; не восхотел благословения, &mdash; оно и удалится от него;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24111, 'rst66', 'psa', 108, 18, 'да облечется проклятием, как ризою, и да войдет оно, как вода, во внутренность его и, как елей, в кости его;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24112, 'rst66', 'psa', 108, 19, 'да будет оно ему, как одежда, в которую он одевается, и как пояс, которым всегда опоясывается.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24113, 'rst66', 'psa', 108, 20, 'Таково воздаяние от Господа врагам моим и говорящим злое на душу мою!');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24114, 'rst66', 'psa', 108, 21, 'Со мною же, Господи, Господи, твори ради имени Твоего, ибо блага милость Твоя; спаси меня,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24115, 'rst66', 'psa', 108, 22, 'ибо я беден и нищ, и сердце мое уязвлено во мне.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24116, 'rst66', 'psa', 108, 23, 'Я исчезаю, как уклоняющаяся тень; гонят меня, как саранчу.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24117, 'rst66', 'psa', 108, 24, 'Колени мои изнемогли от поста, и тело мое лишилось тука.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24118, 'rst66', 'psa', 108, 25, 'Я стал для них посмешищем: увидев меня, кивают головами.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24119, 'rst66', 'psa', 108, 26, 'Помоги мне, Господи, Боже мой, спаси меня по милости Твоей,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24120, 'rst66', 'psa', 108, 27, 'да познают, что это &mdash; Твоя рука, и что Ты, Господи, соделал это.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24121, 'rst66', 'psa', 108, 28, 'Они проклинают, а Ты благослови; они восстают, но да будут постыжены; раб же Твой да возрадуется.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24122, 'rst66', 'psa', 108, 29, 'Да облекутся противники мои бесчестьем и, как одеждою, покроются стыдом своим.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24123, 'rst66', 'psa', 108, 30, 'И я громко буду устами моими славить Господа и среди множества прославлять Его,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24124, 'rst66', 'psa', 108, 31, 'ибо Он стоит одесную бедного, чтобы спасти его от судящих душу его.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24125, 'rst66', 'psa', 109, 0, 'Псалом Давида.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24126, 'rst66', 'psa', 109, 1, 'Сказал Господь Господу моему: седи одесную Меня, доколе положу врагов Твоих в подножие ног Твоих.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24127, 'rst66', 'psa', 109, 2, 'Жезл силы Твоей пошлет Господь с Сиона: господствуй среди врагов Твоих.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24128, 'rst66', 'psa', 109, 3, 'В день силы Твоей народ Твой готов во благолепии святыни; из чрева прежде денницы подобно росе рождение Твое.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24129, 'rst66', 'psa', 109, 4, 'Клялся Господь и не раскается: Ты священник вовек по чину Мелхиседека.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24130, 'rst66', 'psa', 109, 5, 'Господь одесную Тебя. Он в день гнева Своего поразит царей;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24131, 'rst66', 'psa', 109, 6, 'совершит суд над народами, наполнит <i>землю</i> трупами, сокрушит голову в земле обширной.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24132, 'rst66', 'psa', 109, 7, 'Из потока на пути будет пить, и потому вознесет главу.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24133, 'rst66', 'psa', 110, 0, 'Аллилуия.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24134, 'rst66', 'psa', 110, 1, 'Славлю <i>Тебя</i>, Господи, всем сердцем <i>моим</i> в совете праведных и в собрании.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24135, 'rst66', 'psa', 110, 2, 'Велики дела Господни, вожделенны для всех, любящих оные.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24136, 'rst66', 'psa', 110, 3, 'Дело Его &mdash; слава и красота, и правда Его пребывает вовек.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24137, 'rst66', 'psa', 110, 4, 'Памятными соделал Он чудеса Свои; милостив и щедр Господь.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24138, 'rst66', 'psa', 110, 5, 'Пищу дает боящимся Его; вечно помнит завет Свой.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24139, 'rst66', 'psa', 110, 6, 'Силу дел Своих явил Он народу Своему, чтобы дать ему наследие язычников.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24140, 'rst66', 'psa', 110, 7, 'Дела рук Его &mdash; истина и суд; все заповеди Его верны,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24141, 'rst66', 'psa', 110, 8, 'тверды на веки и веки, основаны на истине и правоте.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24142, 'rst66', 'psa', 110, 9, 'Избавление послал Он народу Своему; заповедал навеки завет Свой. Свято и страшно имя Его!');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24143, 'rst66', 'psa', 110, 10, 'Начало мудрости &mdash; страх Господень; разум верный у всех, исполняющих <i>заповеди Его</i>. Хвала Ему пребудет вовек.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24144, 'rst66', 'psa', 111, 0, 'Аллилуия.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24145, 'rst66', 'psa', 111, 1, 'Блажен муж, боящийся Господа и крепко любящий заповеди Его.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24146, 'rst66', 'psa', 111, 2, 'Сильно будет на земле семя его; род правых благословится.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24147, 'rst66', 'psa', 111, 3, 'Обилие и богатство в доме его, и правда его пребывает вовек.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24148, 'rst66', 'psa', 111, 4, 'Во тьме восходит свет правым; благ он и милосерд и праведен.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24149, 'rst66', 'psa', 111, 5, 'Добрый человек милует и взаймы дает; он даст твердость словам своим на суде.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24150, 'rst66', 'psa', 111, 6, 'Он вовек не поколеблется; в вечной памяти будет праведник.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24151, 'rst66', 'psa', 111, 7, 'Не убоится худой молвы: сердце его твердо, уповая на Господа.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24152, 'rst66', 'psa', 111, 8, 'Утверждено сердце его: он не убоится, когда посмотрит на врагов своих.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24153, 'rst66', 'psa', 111, 9, 'Он расточил, раздал нищим; правда его пребывает вовеки; рог его вознесется во славе.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24154, 'rst66', 'psa', 111, 10, 'Нечестивый увидит <i>это</i> и будет досадовать, заскрежещет зубами своими и истает. Желание нечестивых погибнет.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24155, 'rst66', 'psa', 112, 0, 'Аллилуия.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24156, 'rst66', 'psa', 112, 1, 'Хвалите, рабы Господни, хвалите имя Господне.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24157, 'rst66', 'psa', 112, 2, 'Да будет имя Господне благословенно отныне и вовек.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24158, 'rst66', 'psa', 112, 3, 'От восхода солнца до запада <i>да будет</i> прославляемо имя Господне.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24159, 'rst66', 'psa', 112, 4, 'Высок над всеми народами Господь; над небесами слава Его.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24160, 'rst66', 'psa', 112, 5, 'Кто, как Господь, Бог наш, Который, обитая на высоте,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24161, 'rst66', 'psa', 112, 6, 'приклоняется, чтобы призирать на небо и на землю;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24162, 'rst66', 'psa', 112, 7, 'из праха поднимает бедного, из брения возвышает нищего,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24163, 'rst66', 'psa', 112, 8, 'чтобы посадить его с князьями, с князьями народа его;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24164, 'rst66', 'psa', 112, 9, 'неплодную вселяет в дом матерью, радующеюся о детях? Аллилуия!');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24165, 'rst66', 'psa', 113, 1, 'Когда вышел Израиль из Египта, дом Иакова &mdash; из народа иноплеменного,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24166, 'rst66', 'psa', 113, 2, 'Иуда сделался святынею Его, Израиль &mdash; владением Его.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24167, 'rst66', 'psa', 113, 3, 'Море увидело и побежало; Иордан обратился назад.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24168, 'rst66', 'psa', 113, 4, 'Горы прыгали, как овны, и холмы, как агнцы.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24169, 'rst66', 'psa', 113, 5, 'Что с тобою, море, что ты побежало, и <i>с тобою</i>, Иордан, что ты обратился назад?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24170, 'rst66', 'psa', 113, 6, 'Что вы прыгаете, горы, как овны, и вы, холмы, как агнцы?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24171, 'rst66', 'psa', 113, 7, 'Пред лицом Господа трепещи, земля, пред лицом Бога Иаковлева,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24172, 'rst66', 'psa', 113, 8, 'превращающего скалу в озеро воды и камень в источник вод.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24173, 'rst66', 'psa', 113, 9, 'Не нам, Господи, не нам, но имени Твоему дай славу, ради милости Твоей, ради истины Твоей.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24174, 'rst66', 'psa', 113, 10, 'Для чего язычникам говорить: «где же Бог их»?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24175, 'rst66', 'psa', 113, 11, 'Бог наш на небесах; творит все, что хочет.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24176, 'rst66', 'psa', 113, 12, 'А их идолы &mdash; серебро и золото, дело рук человеческих.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24177, 'rst66', 'psa', 113, 13, 'Есть у них уста, но не говорят; есть у них глаза, но не видят;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24178, 'rst66', 'psa', 113, 14, 'есть у них уши, но не слышат; есть у них ноздри, но не обоняют;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24179, 'rst66', 'psa', 113, 15, 'есть у них руки, но не осязают; есть у них ноги, но не ходят; и они не издают голоса гортанью своею.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24180, 'rst66', 'psa', 113, 16, 'Подобны им да будут делающие их и все, надеющиеся на них.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24181, 'rst66', 'psa', 113, 17, '<i>Дом</i> Израилев! уповай на Господа: Он наша помощь и щит.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24182, 'rst66', 'psa', 113, 18, 'Дом Ааронов! уповай на Господа: Он наша помощь и щит.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24183, 'rst66', 'psa', 113, 19, 'Боящиеся Господа! уповайте на Господа: Он наша помощь и щит.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24184, 'rst66', 'psa', 113, 20, 'Господь помнит нас, благословляет нас, благословляет дом Израилев, благословляет дом Ааронов;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24185, 'rst66', 'psa', 113, 21, 'благословляет боящихся Господа, малых с великими.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24186, 'rst66', 'psa', 113, 22, 'Да приложит вам Господь более и более, вам и детям вашим.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24187, 'rst66', 'psa', 113, 23, 'Благословенны вы Господом, сотворившим небо и землю.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24188, 'rst66', 'psa', 113, 24, 'Небо &mdash; небо Господу, а землю Он дал сынам человеческим.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24189, 'rst66', 'psa', 113, 25, 'Не мертвые восхвалят Господа, ни все нисходящие в могилу;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24190, 'rst66', 'psa', 113, 26, 'но мы будем благословлять Господа отныне и вовек. Аллилуия.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24191, 'rst66', 'psa', 114, 1, 'Я радуюсь, что Господь услышал голос мой, моление мое;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24192, 'rst66', 'psa', 114, 2, 'приклонил ко мне ухо Свое, и потому буду призывать Его во <i>все</i> дни мои.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24193, 'rst66', 'psa', 114, 3, 'Объяли меня болезни смертные, муки адские постигли меня; я встретил тесноту и скорбь.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24194, 'rst66', 'psa', 114, 4, 'Тогда призвал я имя Господне: Господи! избавь душу мою.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24195, 'rst66', 'psa', 114, 5, 'Милостив Господь и праведен, и милосерд Бог наш.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24196, 'rst66', 'psa', 114, 6, 'Хранит Господь простодушных: я изнемог, и Он помог мне.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24197, 'rst66', 'psa', 114, 7, 'Возвратись, душа моя, в покой твой, ибо Господь облагодетельствовал тебя.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24198, 'rst66', 'psa', 114, 8, 'Ты избавил душу мою от смерти, очи мои от слез и ноги мои от преткновения.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24199, 'rst66', 'psa', 114, 9, 'Буду ходить пред лицом Господним на земле живых.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24200, 'rst66', 'psa', 115, 1, 'Я веровал, и потому говорил: я сильно сокрушен.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24201, 'rst66', 'psa', 115, 2, 'Я сказал в опрометчивости моей: всякий человек ложь.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24202, 'rst66', 'psa', 115, 3, 'Что воздам Господу за все благодеяния Его ко мне?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24203, 'rst66', 'psa', 115, 4, 'Чашу спасения прииму и имя Господне призову.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24204, 'rst66', 'psa', 115, 5, 'Обеты мои воздам Господу пред всем народом Его.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24205, 'rst66', 'psa', 115, 6, 'Дорога&#769; в очах Господних смерть святых Его!');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24206, 'rst66', 'psa', 115, 7, 'О, Господи! я раб Твой, я раб Твой и сын рабы Твоей; Ты разрешил узы мои.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24207, 'rst66', 'psa', 115, 8, 'Тебе принесу жертву хвалы, и имя Господне призову.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24208, 'rst66', 'psa', 115, 9, 'Обеты мои воздам Господу пред всем народом Его,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24209, 'rst66', 'psa', 115, 10, 'во дворах дома Господня, посреди тебя, Иерусалим! Аллилуия.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24210, 'rst66', 'psa', 116, 1, 'Хвалите Господа, все народы, прославляйте Его, все племена;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24211, 'rst66', 'psa', 116, 2, 'ибо велика милость Его к нам, и истина Господня &mdash; вовек. Аллилуия.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24212, 'rst66', 'psa', 117, 1, 'Славьте Господа, ибо Он благ, ибо вовек милость Его.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24213, 'rst66', 'psa', 117, 2, 'Да скажет ныне <i>дом</i> Израилев: ибо вовек милость Его.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24214, 'rst66', 'psa', 117, 3, 'Да скажет ныне дом Ааронов: ибо вовек милость Его.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24215, 'rst66', 'psa', 117, 4, 'Да скажут ныне боящиеся Господа: ибо вовек милость Его.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24216, 'rst66', 'psa', 117, 5, 'Из тесноты воззвал я к Господу, &mdash; и услышал меня, и на пространное место <i>вывел меня</i> Господь.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24217, 'rst66', 'psa', 117, 6, 'Господь за меня &mdash; не устрашусь: что сделает мне человек?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24218, 'rst66', 'psa', 117, 7, 'Господь мне помощник: буду смотреть на врагов моих.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24219, 'rst66', 'psa', 117, 8, 'Лучше уповать на Господа, нежели надеяться на человека.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24220, 'rst66', 'psa', 117, 9, 'Лучше уповать на Господа, нежели надеяться на князей.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24221, 'rst66', 'psa', 117, 10, 'Все народы окружили меня, но именем Господним я низложил их;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24222, 'rst66', 'psa', 117, 11, 'обступили меня, окружили меня, но именем Господним я низложил их;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24223, 'rst66', 'psa', 117, 12, 'окружили меня, как пчелы, и угасли, как огонь в терне: именем Господним я низложил их.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24224, 'rst66', 'psa', 117, 13, 'Сильно толкнули меня, чтобы я упал, но Господь поддержал меня.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24225, 'rst66', 'psa', 117, 14, 'Господь &mdash; сила моя и песнь; Он соделался моим спасением.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24226, 'rst66', 'psa', 117, 15, 'Глас радости и спасения в жилищах праведников: десница Господня творит силу!');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24227, 'rst66', 'psa', 117, 16, 'Десница Господня высока, десница Господня творит силу!');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24228, 'rst66', 'psa', 117, 17, 'Не умру, но буду жить и возвещать дела Господни.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24229, 'rst66', 'psa', 117, 18, 'Строго наказал меня Господь, но смерти не предал меня.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24230, 'rst66', 'psa', 117, 19, 'Отворите мне врата правды; войду в них, прославлю Господа.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24231, 'rst66', 'psa', 117, 20, 'Вот врата Господа; праведные войдут в них.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24232, 'rst66', 'psa', 117, 21, 'Славлю Тебя, что Ты услышал меня и соделался моим спасением.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24233, 'rst66', 'psa', 117, 22, 'Камень, который отвергли строители, соделался главою угла:');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24234, 'rst66', 'psa', 117, 23, 'это &mdash; от Господа, и есть дивно в очах наших.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24235, 'rst66', 'psa', 117, 24, 'Сей день сотворил Господь: возрадуемся и возвеселимся в оный!');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24236, 'rst66', 'psa', 117, 25, 'О, Господи, спаси же! О, Господи, споспешествуй же!');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24237, 'rst66', 'psa', 117, 26, 'Благословен грядущий во имя Господне! Благословляем вас из дома Господня.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24238, 'rst66', 'psa', 117, 27, 'Бог &mdash; Господь, и осиял нас; вяжите вервями жертву, <i>ведите</i> к рогам жертвенника.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24239, 'rst66', 'psa', 117, 28, 'Ты &mdash; Бог мой: буду славить Тебя; Ты &mdash; Бог мой: буду превозносить Тебя.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24240, 'rst66', 'psa', 117, 29, 'Славьте Господа, ибо Он благ, ибо вовек милость Его.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24241, 'rst66', 'psa', 118, 1, 'Блаженны непорочные в пути, ходящие в законе Господнем.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24242, 'rst66', 'psa', 118, 2, 'Блаженны хранящие откровения Его, всем сердцем ищущие Его.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24243, 'rst66', 'psa', 118, 3, 'Они не делают беззакония, ходят путями Его.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24244, 'rst66', 'psa', 118, 4, 'Ты заповедал повеления Твои хранить твердо.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24245, 'rst66', 'psa', 118, 5, 'О, если бы направлялись пути мои к соблюдению уставов Твоих!');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24246, 'rst66', 'psa', 118, 6, 'Тогда я не постыдился бы, взирая на все заповеди Твои:');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24247, 'rst66', 'psa', 118, 7, 'я славил бы Тебя в правоте сердца, поучаясь судам правды Твоей.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24248, 'rst66', 'psa', 118, 8, 'Буду хранить уставы Твои; не оставляй меня совсем.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24249, 'rst66', 'psa', 118, 9, 'Как юноше содержать в чистоте путь свой? &mdash; Хранением себя по слову Твоему.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24250, 'rst66', 'psa', 118, 10, 'Всем сердцем моим ищу Тебя; не дай мне уклониться от заповедей Твоих.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24251, 'rst66', 'psa', 118, 11, 'В сердце моем сокрыл я слово Твое, чтобы не грешить пред Тобою.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24252, 'rst66', 'psa', 118, 12, 'Благословен Ты, Господи! научи меня уставам Твоим.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24253, 'rst66', 'psa', 118, 13, 'Устами моими возвещал я все суды уст Твоих.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24254, 'rst66', 'psa', 118, 14, 'На пути откровений Твоих я радуюсь, как во всяком богатстве.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24255, 'rst66', 'psa', 118, 15, 'О заповедях Твоих размышляю, и взираю на пути Твои.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24256, 'rst66', 'psa', 118, 16, 'Уставами Твоими утешаюсь, не забываю слова Твоего.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24257, 'rst66', 'psa', 118, 17, 'Яви милость рабу Твоему, и буду жить и хранить слово Твое.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24258, 'rst66', 'psa', 118, 18, 'Открой очи мои, и увижу чудеса закона Твоего.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24259, 'rst66', 'psa', 118, 19, 'Странник я на земле; не скрывай от меня заповедей Твоих.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24260, 'rst66', 'psa', 118, 20, 'Истомилась душа моя желанием судов Твоих во всякое время.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24261, 'rst66', 'psa', 118, 21, 'Ты укротил гордых, проклятых, уклоняющихся от заповедей Твоих.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24262, 'rst66', 'psa', 118, 22, 'Сними с меня поношение и посрамление, ибо я храню откровения Твои.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24263, 'rst66', 'psa', 118, 23, 'Князья сидят и сговариваются против меня, а раб Твой размышляет об уставах Твоих.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24264, 'rst66', 'psa', 118, 24, 'Откровения Твои &mdash; утешение мое, советники мои.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24265, 'rst66', 'psa', 118, 25, 'Душа моя повержена в прах; оживи меня по слову Твоему.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24266, 'rst66', 'psa', 118, 26, 'Объявил я пути мои, и Ты услышал меня; научи меня уставам Твоим.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24267, 'rst66', 'psa', 118, 27, 'Дай мне уразуметь путь повелений Твоих, и буду размышлять о чудесах Твоих.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24268, 'rst66', 'psa', 118, 28, 'Душа моя истаевает от скорби: укрепи меня по слову Твоему.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24269, 'rst66', 'psa', 118, 29, 'Удали от меня путь лжи, и закон Твой даруй мне.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24270, 'rst66', 'psa', 118, 30, 'Я избрал путь истины, поставил пред собою суды Твои.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24271, 'rst66', 'psa', 118, 31, 'Я прилепился к откровениям Твоим, Господи; не постыди меня.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24272, 'rst66', 'psa', 118, 32, 'Потеку путем заповедей Твоих, когда Ты расширишь сердце мое.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24273, 'rst66', 'psa', 118, 33, 'Укажи мне, Господи, путь уставов Твоих, и я буду держаться его до конца.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24274, 'rst66', 'psa', 118, 34, 'Вразуми меня, и буду соблюдать закон Твой и хранить его всем сердцем.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24275, 'rst66', 'psa', 118, 35, 'Поставь меня на стезю заповедей Твоих, ибо я возжелал ее.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24276, 'rst66', 'psa', 118, 36, 'Приклони сердце мое к откровениям Твоим, а не к корысти.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24277, 'rst66', 'psa', 118, 37, 'Отврати очи мои, чтобы не видеть суеты; животвори меня на пути Твоем.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24278, 'rst66', 'psa', 118, 38, 'Утверди слово Твое рабу Твоему, ради благоговения пред Тобою.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24279, 'rst66', 'psa', 118, 39, 'Отврати поношение мое, которого я страшусь, ибо суды Твои благи.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24280, 'rst66', 'psa', 118, 40, 'Вот, я возжелал повелений Твоих; животвори меня правдою Твоею.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24281, 'rst66', 'psa', 118, 41, 'Да придут ко мне милости Твои, Господи, спасение Твое по слову Твоему, &mdash;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24282, 'rst66', 'psa', 118, 42, 'и я дам ответ поносящему меня, ибо уповаю на слово Твое.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24283, 'rst66', 'psa', 118, 43, 'Не отнимай совсем от уст моих слова истины, ибо я уповаю на суды Твои');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24284, 'rst66', 'psa', 118, 44, 'и буду хранить закон Твой всегда, во веки и веки;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24285, 'rst66', 'psa', 118, 45, 'буду ходить свободно, ибо я взыскал повелений Твоих;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24286, 'rst66', 'psa', 118, 46, 'буду говорить об откровениях Твоих пред царями и не постыжусь;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24287, 'rst66', 'psa', 118, 47, 'буду утешаться заповедями Твоими, которые возлюбил;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24288, 'rst66', 'psa', 118, 48, 'руки мои буду простирать к заповедям Твоим, которые возлюбил, и размышлять об уставах Твоих.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24289, 'rst66', 'psa', 118, 49, 'Вспомни слово Твое к рабу Твоему, на которое Ты повелел мне уповать:');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24290, 'rst66', 'psa', 118, 50, 'это &mdash; утешение в бедствии моем, что слово Твое оживляет меня.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24291, 'rst66', 'psa', 118, 51, 'Гордые крайне ругались надо мною, но я не уклонился от закона Твоего.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24292, 'rst66', 'psa', 118, 52, 'Вспоминал суды Твои, Господи, от века, и утешался.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24293, 'rst66', 'psa', 118, 53, 'Ужас овладевает мною при виде нечестивых, оставляющих закон Твой.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24294, 'rst66', 'psa', 118, 54, 'Уставы Твои были песнями моими на месте странствований моих.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24295, 'rst66', 'psa', 118, 55, 'Ночью вспоминал я имя Твое, Господи, и хранил закон Твой.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24296, 'rst66', 'psa', 118, 56, 'Он стал моим, ибо повеления Твои храню.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24297, 'rst66', 'psa', 118, 57, 'Удел мой, Господи, сказал я, соблюдать слова Твои.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24298, 'rst66', 'psa', 118, 58, 'Молился я Тебе всем сердцем: помилуй меня по слову Твоему.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24299, 'rst66', 'psa', 118, 59, 'Размышлял о путях моих и обращал стопы мои к откровениям Твоим.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24300, 'rst66', 'psa', 118, 60, 'Спешил и не медлил соблюдать заповеди Твои.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24301, 'rst66', 'psa', 118, 61, 'Сети нечестивых окружили меня, но я не забывал закона Твоего.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24302, 'rst66', 'psa', 118, 62, 'В полночь вставал славословить Тебя за праведные суды Твои.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24303, 'rst66', 'psa', 118, 63, 'Общник я всем боящимся Тебя и хранящим повеления Твои.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24304, 'rst66', 'psa', 118, 64, 'Милости Твоей, Господи, полна земля; научи меня уставам Твоим.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24305, 'rst66', 'psa', 118, 65, 'Благо сотворил Ты рабу Твоему, Господи, по слову Твоему.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24306, 'rst66', 'psa', 118, 66, 'Доброму разумению и ведению научи меня, ибо заповедям Твоим я верую.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24307, 'rst66', 'psa', 118, 67, 'Прежде страдания моего я заблуждался; а ныне слово Твое храню.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24308, 'rst66', 'psa', 118, 68, 'Благ и благодетелен Ты, научи меня уставам Твоим.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24309, 'rst66', 'psa', 118, 69, 'Гордые сплетают на меня ложь; я же всем сердцем буду хранить повеления Твои.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24310, 'rst66', 'psa', 118, 70, 'Ожирело сердце их, как тук; я же законом Твоим утешаюсь.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24311, 'rst66', 'psa', 118, 71, 'Благо мне, что я пострадал, дабы научиться уставам Твоим.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24312, 'rst66', 'psa', 118, 72, 'Закон уст Твоих для меня лучше тысяч золота и серебра.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24313, 'rst66', 'psa', 118, 73, 'Руки Твои сотворили меня и устроили меня; вразуми меня, и научусь заповедям Твоим.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24314, 'rst66', 'psa', 118, 74, 'Боящиеся Тебя увидят меня &mdash; и возрадуются, что я уповаю на слово Твое.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24315, 'rst66', 'psa', 118, 75, 'Знаю, Господи, что суды Твои праведны и по справедливости Ты наказал меня.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24316, 'rst66', 'psa', 118, 76, 'Да будет же милость Твоя утешением моим, по слову Твоему к рабу Твоему.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24317, 'rst66', 'psa', 118, 77, 'Да придет ко мне милосердие Твое, и я буду жить; ибо закон Твой &mdash; утешение мое.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24318, 'rst66', 'psa', 118, 78, 'Да будут постыжены гордые, ибо безвинно угнетают меня; я размышляю о повелениях Твоих.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24319, 'rst66', 'psa', 118, 79, 'Да обратятся ко мне боящиеся Тебя и знающие откровения Твои.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24320, 'rst66', 'psa', 118, 80, 'Да будет сердце мое непорочно в уставах Твоих, чтобы я не посрамился.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24321, 'rst66', 'psa', 118, 81, 'Истаевает душа моя о спасении Твоем; уповаю на слово Твое.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24322, 'rst66', 'psa', 118, 82, 'Истаевают очи мои о слове Твоем; я говорю: когда Ты утешишь меня?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24323, 'rst66', 'psa', 118, 83, 'Я стал как мех в дыму, <i>но</i> уставов Твоих не забыл.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24324, 'rst66', 'psa', 118, 84, 'Сколько дней раба Твоего? Когда произведешь суд над гонителями моими?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24325, 'rst66', 'psa', 118, 85, 'Яму вырыли мне гордые, вопреки закону Твоему.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24326, 'rst66', 'psa', 118, 86, 'Все заповеди Твои &mdash; истина; несправедливо преследуют меня: помоги мне;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24327, 'rst66', 'psa', 118, 87, 'едва не погубили меня на земле, но я не оставил повелений Твоих.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24328, 'rst66', 'psa', 118, 88, 'По милости Твоей оживляй меня, и буду хранить откровения уст Твоих.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24329, 'rst66', 'psa', 118, 89, 'Навеки, Господи, слово Твое утверждено на небесах;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24330, 'rst66', 'psa', 118, 90, 'истина Твоя в род и род. Ты поставил землю, и она стоит.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24331, 'rst66', 'psa', 118, 91, 'По определениям Твоим все стоит доныне, ибо все служит Тебе.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24332, 'rst66', 'psa', 118, 92, 'Если бы не закон Твой был утешением моим, погиб бы я в бедствии моем.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24333, 'rst66', 'psa', 118, 93, 'Вовек не забуду повелений Твоих, ибо ими Ты оживляешь меня.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24334, 'rst66', 'psa', 118, 94, 'Твой я, спаси меня; ибо я взыскал повелений Твоих.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24335, 'rst66', 'psa', 118, 95, 'Нечестивые подстерегают меня, чтобы погубить; <i>а</i> я углубляюсь в откровения Твои.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24336, 'rst66', 'psa', 118, 96, 'Я видел предел всякого совершенства, <i>но</i> Твоя заповедь безмерно обширна.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24337, 'rst66', 'psa', 118, 97, 'Как люблю я закон Твой! весь день размышляю о нем.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24338, 'rst66', 'psa', 118, 98, 'Заповедью Твоею Ты соделал меня мудрее врагов моих, ибо она всегда со мною.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24339, 'rst66', 'psa', 118, 99, 'Я стал разумнее всех учителей моих, ибо размышляю об откровениях Твоих.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24340, 'rst66', 'psa', 118, 100, 'Я сведущ более старцев, ибо повеления Твои храню.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24341, 'rst66', 'psa', 118, 101, 'От всякого злого пути удерживаю ноги мои, чтобы хранить слово Твое;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24342, 'rst66', 'psa', 118, 102, 'от судов Твоих не уклоняюсь, ибо Ты научаешь меня.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24343, 'rst66', 'psa', 118, 103, 'Как сладки гортани моей слова Твои! лучше меда устам моим.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24344, 'rst66', 'psa', 118, 104, 'Повелениями Твоими я вразумлен; потому ненавижу всякий путь лжи.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24345, 'rst66', 'psa', 118, 105, 'Слово Твое &mdash; светильник ноге моей и свет стезе моей.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24346, 'rst66', 'psa', 118, 106, 'Я клялся хранить праведные суды Твои, и исполню.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24347, 'rst66', 'psa', 118, 107, 'Сильно угнетен я, Господи; оживи меня по слову Твоему.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24348, 'rst66', 'psa', 118, 108, 'Благоволи же, Господи, принять добровольную жертву уст моих, и судам Твоим научи меня.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24349, 'rst66', 'psa', 118, 109, 'Душа моя непрестанно в руке моей, но закона Твоего не забываю.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24350, 'rst66', 'psa', 118, 110, 'Нечестивые поставили для меня сеть, но я не уклонился от повелений Твоих.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24351, 'rst66', 'psa', 118, 111, 'Откровения Твои я принял, как наследие навеки, ибо они &mdash; веселие сердца моего.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24352, 'rst66', 'psa', 118, 112, 'Я приклонил сердце мое к исполнению уставов Твоих навек, до конца.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24353, 'rst66', 'psa', 118, 113, 'Вымыслы <i>человеческие</i> ненавижу, а закон Твой люблю.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24354, 'rst66', 'psa', 118, 114, 'Ты покров мой и щит мой; на слово Твое уповаю.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24355, 'rst66', 'psa', 118, 115, 'Удалитесь от меня, беззаконные, и буду хранить заповеди Бога моего.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24356, 'rst66', 'psa', 118, 116, 'Укрепи меня по слову Твоему, и буду жить; не посрами меня в надежде моей;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24357, 'rst66', 'psa', 118, 117, 'поддержи меня, и спасусь; и в уставы Твои буду вникать непрестанно.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24358, 'rst66', 'psa', 118, 118, 'Всех, отступающих от уставов Твоих, Ты низлагаешь, ибо ухищрения их &mdash; ложь.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24359, 'rst66', 'psa', 118, 119, '<i>Как</i> изгарь, отметаешь Ты всех нечестивых земли; потому я возлюбил откровения Твои.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24360, 'rst66', 'psa', 118, 120, 'Трепещет от страха Твоего плоть моя, и судов Твоих я боюсь.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24361, 'rst66', 'psa', 118, 121, 'Я совершал суд и правду; не предай меня гонителям моим.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24362, 'rst66', 'psa', 118, 122, 'Заступи раба Твоего ко благу <i>его</i>, чтобы не угнетали меня гордые.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24363, 'rst66', 'psa', 118, 123, 'Истаевают очи мои, ожидая спасения Твоего и слова правды Твоей.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24364, 'rst66', 'psa', 118, 124, 'Сотвори с рабом Твоим по милости Твоей, и уставам Твоим научи меня.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24365, 'rst66', 'psa', 118, 125, 'Я раб Твой: вразуми меня, и познаю откровения Твои.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24366, 'rst66', 'psa', 118, 126, 'Время Господу действовать: закон Твой разорили.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24367, 'rst66', 'psa', 118, 127, 'А я люблю заповеди Твои более золота, и золота чистого.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24368, 'rst66', 'psa', 118, 128, 'Все повеления Твои &mdash; все признаю справедливыми; всякий путь лжи ненавижу.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24369, 'rst66', 'psa', 118, 129, 'Дивны откровения Твои; потому хранит их душа моя.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24370, 'rst66', 'psa', 118, 130, 'Откровение слов Твоих просвещает, вразумляет простых.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24371, 'rst66', 'psa', 118, 131, 'Открываю уста мои и вздыхаю, ибо заповедей Твоих жажду.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24372, 'rst66', 'psa', 118, 132, 'Призри на меня и помилуй меня, как поступаешь с любящими имя Твое.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24373, 'rst66', 'psa', 118, 133, 'Утверди стопы мои в слове Твоем и не дай овладеть мною никакому беззаконию;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24374, 'rst66', 'psa', 118, 134, 'избавь меня от угнетения человеческого, и буду хранить повеления Твои;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24375, 'rst66', 'psa', 118, 135, 'осияй раба Твоего светом лица Твоего и научи меня уставам Твоим.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24376, 'rst66', 'psa', 118, 136, 'Из глаз моих текут потоки вод оттого, что не хранят закона Твоего.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24377, 'rst66', 'psa', 118, 137, 'Праведен Ты, Господи, и справедливы суды Твои.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24378, 'rst66', 'psa', 118, 138, 'Откровения Твои, которые Ты заповедал, &mdash; правда и совершенная истина.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24379, 'rst66', 'psa', 118, 139, 'Ревность моя снедает меня, потому что мои враги забыли слова Твои.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24380, 'rst66', 'psa', 118, 140, 'Слово Твое весьма чисто, и раб Твой возлюбил его.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24381, 'rst66', 'psa', 118, 141, 'Мал я и презрен, <i>но</i> повелений Твоих не забываю.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24382, 'rst66', 'psa', 118, 142, 'Правда Твоя &mdash; правда вечная, и закон Твой &mdash; истина.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24383, 'rst66', 'psa', 118, 143, 'Скорбь и горесть постигли меня; заповеди Твои &mdash; утешение мое.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24384, 'rst66', 'psa', 118, 144, 'Правда откровений Твоих вечна: вразуми меня, и буду жить.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24385, 'rst66', 'psa', 118, 145, 'Взываю всем сердцем <i>моим</i>: услышь меня, Господи, &mdash; и сохраню уставы Твои.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24386, 'rst66', 'psa', 118, 146, 'Призываю Тебя: спаси меня, и буду хранить откровения Твои.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24387, 'rst66', 'psa', 118, 147, 'Предваряю рассвет и взываю; на слово Твое уповаю.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24388, 'rst66', 'psa', 118, 148, 'Очи мои предваряют <i>утреннюю</i> стражу, чтобы мне углубляться в слово Твое.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24389, 'rst66', 'psa', 118, 149, 'Услышь голос мой по милости Твоей, Господи; по суду Твоему оживи меня.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24390, 'rst66', 'psa', 118, 150, 'Приблизились замышляющие лукавство; далеки они от закона Твоего.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24391, 'rst66', 'psa', 118, 151, 'Близок Ты, Господи, и все заповеди Твои &mdash; истина.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24392, 'rst66', 'psa', 118, 152, 'Издавна узнал я об откровениях Твоих, что Ты утвердил их навеки.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24393, 'rst66', 'psa', 118, 153, 'Воззри на бедствие мое и избавь меня, ибо я не забываю закона Твоего.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24394, 'rst66', 'psa', 118, 154, 'Вступись в дело мое и защити меня; по слову Твоему оживи меня.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24395, 'rst66', 'psa', 118, 155, 'Далеко от нечестивых спасение, ибо они уставов Твоих не ищут.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24396, 'rst66', 'psa', 118, 156, 'Много щедрот Твоих, Господи; по суду Твоему оживи меня.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24397, 'rst66', 'psa', 118, 157, 'Много у меня гонителей и врагов, <i>но</i> от откровений Твоих я не удаляюсь.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24398, 'rst66', 'psa', 118, 158, 'Вижу отступников и сокрушаюсь, ибо они не хранят слова Твоего.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24399, 'rst66', 'psa', 118, 159, 'Зри, как я люблю повеления Твои; по милости Твоей, Господи, оживи меня.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24400, 'rst66', 'psa', 118, 160, 'Основание слова Твоего истинно, и вечен всякий суд правды Твоей.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24401, 'rst66', 'psa', 118, 161, 'Князья гонят меня безвинно, но сердце мое боится слова Твоего.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24402, 'rst66', 'psa', 118, 162, 'Радуюсь я слову Твоему, как получивший великую прибыль.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24403, 'rst66', 'psa', 118, 163, 'Ненавижу ложь и гнушаюсь ею; закон же Твой люблю.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24404, 'rst66', 'psa', 118, 164, 'Семикратно в день прославляю Тебя за суды правды Твоей.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24405, 'rst66', 'psa', 118, 165, 'Велик мир у любящих закон Твой, и нет им преткновения.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24406, 'rst66', 'psa', 118, 166, 'Уповаю на спасение Твое, Господи, и заповеди Твои исполняю.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24407, 'rst66', 'psa', 118, 167, 'Душа моя хранит откровения Твои, и я люблю их крепко.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24408, 'rst66', 'psa', 118, 168, 'Храню повеления Твои и откровения Твои, ибо все пути мои пред Тобою.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24409, 'rst66', 'psa', 118, 169, 'Да приблизится вопль мой пред лицо Твое, Господи; по слову Твоему вразуми меня.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24410, 'rst66', 'psa', 118, 170, 'Да придет моление мое пред лицо Твое; по слову Твоему избавь меня.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24411, 'rst66', 'psa', 118, 171, 'Уста мои произнесут хвалу, когда Ты научишь меня уставам Твоим.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24412, 'rst66', 'psa', 118, 172, 'Язык мой возгласит слово Твое, ибо все заповеди Твои праведны.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24413, 'rst66', 'psa', 118, 173, 'Да будет рука Твоя в помощь мне, ибо я повеления Твои избрал.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24414, 'rst66', 'psa', 118, 174, 'Жажду спасения Твоего, Господи, и закон Твой &mdash; утешение мое.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24415, 'rst66', 'psa', 118, 175, 'Да живет душа моя и славит Тебя, и суды Твои да помогут мне.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24416, 'rst66', 'psa', 118, 176, 'Я заблудился, как овца потерянная: взыщи раба Твоего, ибо я заповедей Твоих не забыл.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24417, 'rst66', 'psa', 119, 0, 'Песнь восхождения.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24418, 'rst66', 'psa', 119, 1, 'К Господу воззвал я в скорби моей, и Он услышал меня.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24419, 'rst66', 'psa', 119, 2, 'Господи! избавь душу мою от уст лживых, от языка лукавого.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24420, 'rst66', 'psa', 119, 3, 'Что даст тебе и что прибавит тебе язык лукавый?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24421, 'rst66', 'psa', 119, 4, 'Изощренные стрелы сильного, с горящими углями дроковыми.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24422, 'rst66', 'psa', 119, 5, 'Горе мне, что я пребываю у Мосоха, живу у шатров Кидарских.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24423, 'rst66', 'psa', 119, 6, 'Долго жила душа моя с ненавидящими мир.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24424, 'rst66', 'psa', 119, 7, 'Я мирен: но только заговорю, они &mdash; к войне.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24425, 'rst66', 'psa', 120, 0, 'Песнь восхождения.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24426, 'rst66', 'psa', 120, 1, 'Возвожу очи мои к горам, откуда придет помощь моя.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24427, 'rst66', 'psa', 120, 2, 'Помощь моя от Господа, сотворившего небо и землю.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24428, 'rst66', 'psa', 120, 3, 'Не даст Он поколебаться ноге твоей, не воздремлет хранящий тебя;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24429, 'rst66', 'psa', 120, 4, 'не дремлет и не спит хранящий Израиля.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24430, 'rst66', 'psa', 120, 5, 'Господь &mdash; хранитель твой; Господь &mdash; сень твоя с правой руки твоей.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24431, 'rst66', 'psa', 120, 6, 'Днем солнце не поразит тебя, ни луна ночью.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24432, 'rst66', 'psa', 120, 7, 'Господь сохранит тебя от всякого зла; сохранит душу твою <i>Господь</i>.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24433, 'rst66', 'psa', 120, 8, 'Господь будет охранять выхождение твое и вхождение твое отныне и вовек.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24434, 'rst66', 'psa', 121, 0, 'Песнь восхождения. Давида.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24435, 'rst66', 'psa', 121, 1, 'Возрадовался я, когда сказали мне: «пойдем в дом Господень».');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24436, 'rst66', 'psa', 121, 2, 'Вот, стоят ноги наши во вратах твоих, Иерусалим, &mdash;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24437, 'rst66', 'psa', 121, 3, 'Иерусалим, устроенный как город, слитый в одно,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24438, 'rst66', 'psa', 121, 4, 'куда восходят колена, колена Господни, по закону Израилеву, славить имя Господне.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24439, 'rst66', 'psa', 121, 5, 'Там стоят престолы суда, престолы дома Давидова.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24440, 'rst66', 'psa', 121, 6, 'Просите мира Иерусалиму: да благоденствуют любящие тебя!');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24441, 'rst66', 'psa', 121, 7, 'Да будет мир в стенах твоих, благоденствие &mdash; в чертогах твоих!');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24442, 'rst66', 'psa', 121, 8, 'Ради братьев моих и ближних моих говорю я: «мир тебе!»');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24443, 'rst66', 'psa', 121, 9, 'Ради дома Господа, Бога нашего, желаю блага тебе.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24444, 'rst66', 'psa', 122, 0, 'Песнь восхождения.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24445, 'rst66', 'psa', 122, 1, 'К Тебе возвожу очи мои, Живущий на небесах!');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24446, 'rst66', 'psa', 122, 2, 'Вот, как очи рабов <i>обращены</i> на руку господ их, как очи рабы &mdash; на руку госпожи ее, так очи наши &mdash; к Господу, Богу нашему, доколе Он помилует нас.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24447, 'rst66', 'psa', 122, 3, 'Помилуй нас, Господи, помилуй нас, ибо довольно мы насыщены презрением;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24448, 'rst66', 'psa', 122, 4, 'довольно насыщена душа наша поношением от надменных и уничижением от гордых.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24449, 'rst66', 'psa', 123, 0, 'Песнь восхождения. Давида.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24450, 'rst66', 'psa', 123, 1, 'Если бы не Господь был с нами, &mdash; да скажет Израиль, &mdash;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24451, 'rst66', 'psa', 123, 2, 'если бы не Господь был с нами, когда восстали на нас люди,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24452, 'rst66', 'psa', 123, 3, 'то живых они поглотили бы нас, когда возгорелась ярость их на нас;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24453, 'rst66', 'psa', 123, 4, 'воды потопили бы нас, поток прошел бы над душою нашею;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24454, 'rst66', 'psa', 123, 5, 'прошли бы над душою нашею воды бурные.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24455, 'rst66', 'psa', 123, 6, 'Благословен Господь, Который не дал нас в добычу зубам их!');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24456, 'rst66', 'psa', 123, 7, 'Душа наша избавилась, как птица, из сети ловящих: сеть расторгнута, и мы избавились.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24457, 'rst66', 'psa', 123, 8, 'Помощь наша &mdash; в имени Господа, сотворившего небо и землю.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24458, 'rst66', 'psa', 124, 0, 'Песнь восхождения.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24459, 'rst66', 'psa', 124, 1, 'Надеющийся на Господа, как гора Сион, не подвигнется: пребывает вовек.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24460, 'rst66', 'psa', 124, 2, 'Горы окрест Иерусалима, а Господь окрест народа Своего отныне и вовек.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24461, 'rst66', 'psa', 124, 3, 'Ибо не оставит <i>Господь</i> жезла нечестивых над жребием праведных, дабы праведные не простерли рук своих к беззаконию.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24462, 'rst66', 'psa', 124, 4, 'Благотвори, Господи, добрым и правым в сердцах своих;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24463, 'rst66', 'psa', 124, 5, 'а совращающихся на кривые пути свои да оставит Господь ходить с делающими беззаконие. Мир на Израиля!');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24464, 'rst66', 'psa', 125, 0, 'Песнь восхождения.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24465, 'rst66', 'psa', 125, 1, 'Когда возвращал Господь плен Сиона, мы были как бы видящие во сне:');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24466, 'rst66', 'psa', 125, 2, 'тогда уста наши были полны веселья, и язык наш &mdash; пения; тогда между народами говорили: «великое сотворил Господь над ними!»');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24467, 'rst66', 'psa', 125, 3, 'Великое сотворил Господь над нами: мы радовались.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24468, 'rst66', 'psa', 125, 4, 'Возврати, Господи, пленников наших, как потоки на полдень.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24469, 'rst66', 'psa', 125, 5, 'Сеявшие со слезами будут пожинать с радостью.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24470, 'rst66', 'psa', 125, 6, 'С плачем несущий семена возвратится с радостью, неся снопы свои.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24471, 'rst66', 'psa', 126, 0, 'Песнь восхождения. Соломона.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24472, 'rst66', 'psa', 126, 1, 'Если Господь не созиждет до&#769;ма, напрасно трудятся строящие его; если Господь не охранит города, напрасно бодрствует страж.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24473, 'rst66', 'psa', 126, 2, 'Напрасно вы рано встаете, поздно просиживаете, едите хлеб печали, тогда как возлюбленному Своему Он дает сон.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24474, 'rst66', 'psa', 126, 3, 'Вот наследие от Господа: дети; награда от Него &mdash; плод чрева.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24475, 'rst66', 'psa', 126, 4, 'Что стрелы в руке сильного, то сыновья молодые.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24476, 'rst66', 'psa', 126, 5, 'Блажен человек, который наполнил ими колчан свой! Не останутся они в стыде, когда будут говорить с врагами в воротах.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24477, 'rst66', 'psa', 127, 0, 'Песнь восхождения.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24478, 'rst66', 'psa', 127, 1, 'Блажен всякий боящийся Господа, ходящий путями Его!');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24479, 'rst66', 'psa', 127, 2, 'Ты будешь есть от трудов рук твоих: блажен ты, и благо тебе!');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24480, 'rst66', 'psa', 127, 3, 'Жена твоя, как плодовитая лоза, в доме твоем; сыновья твои, как масличные ветви, вокруг трапезы твоей:');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24481, 'rst66', 'psa', 127, 4, 'так благословится человек, боящийся Господа!');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24482, 'rst66', 'psa', 127, 5, 'Благословит тебя Господь с Сиона, и увидишь благоденствие Иерусалима во все дни жизни твоей;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24483, 'rst66', 'psa', 127, 6, 'увидишь сыновей у сыновей твоих. Мир на Израиля!');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24484, 'rst66', 'psa', 128, 0, 'Песнь восхождения.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24485, 'rst66', 'psa', 128, 1, 'Много теснили меня от юности моей, да скажет Израиль:');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24486, 'rst66', 'psa', 128, 2, 'много теснили меня от юности моей, но не одолели меня.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24487, 'rst66', 'psa', 128, 3, 'На хребте моем орали оратаи, проводили длинные борозды свои.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24488, 'rst66', 'psa', 128, 4, 'Но Господь праведен: Он рассек узы нечестивых.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24489, 'rst66', 'psa', 128, 5, 'Да постыдятся и обратятся назад все ненавидящие Сион!');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24490, 'rst66', 'psa', 128, 6, 'Да будут как трава на кровлях, которая прежде, нежели будет исторгнута, засыхает,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24491, 'rst66', 'psa', 128, 7, 'которою жнец не наполнит руки своей, и вяжущий снопы &mdash; горсти своей;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24492, 'rst66', 'psa', 128, 8, 'и проходящие мимо не скажут: «благословение Господне на вас; благословляем вас именем Господним!»');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24493, 'rst66', 'psa', 129, 0, 'Песнь восхождения.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24494, 'rst66', 'psa', 129, 1, 'Из глубины взываю к Тебе, Господи.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24495, 'rst66', 'psa', 129, 2, 'Господи! услышь голос мой. Да будут уши Твои внимательны к голосу молений моих.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24496, 'rst66', 'psa', 129, 3, 'Если Ты, Господи, будешь замечать беззакония, &mdash; Господи! кто устоит?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24497, 'rst66', 'psa', 129, 4, 'Но у Тебя прощение, да благоговеют пред Тобою.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24498, 'rst66', 'psa', 129, 5, 'Надеюсь на Господа, надеется душа моя; на слово Его уповаю.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24499, 'rst66', 'psa', 129, 6, 'Душа моя ожидает Господа более, нежели стражи &mdash; утра, более, нежели стражи &mdash; утра.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24500, 'rst66', 'psa', 129, 7, 'Да уповает Израиль на Господа, ибо у Господа милость и многое у Него избавление,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24501, 'rst66', 'psa', 129, 8, 'и Он избавит Израиля от всех беззаконий его.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24502, 'rst66', 'psa', 130, 0, 'Песнь восхождения. Давида.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24503, 'rst66', 'psa', 130, 1, 'Господи! не надмевалось сердце мое и не возносились очи мои, и я не входил в великое и для меня недосягаемое.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24504, 'rst66', 'psa', 130, 2, 'Не смирял ли я и не успокаивал ли души моей, как дитяти, отнятого от груди матери? душа моя была во мне, как дитя, отнятое от груди.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24505, 'rst66', 'psa', 130, 3, 'Да уповает Израиль на Господа отныне и вовек.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24506, 'rst66', 'psa', 131, 0, 'Песнь восхождения.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24507, 'rst66', 'psa', 131, 1, 'Вспомни, Господи, Давида и все сокрушение его:');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24508, 'rst66', 'psa', 131, 2, 'как он клялся Господу, давал обет Сильному Иакова:');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24509, 'rst66', 'psa', 131, 3, '«Не войду в шатер дома моего, не взойду на ложе мое;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24510, 'rst66', 'psa', 131, 4, 'не дам сна очам моим и веждам моим дремания,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24511, 'rst66', 'psa', 131, 5, 'доколе не найду места Господу, жилища &mdash; Сильному Иакова».');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24512, 'rst66', 'psa', 131, 6, 'Вот, мы слышали о нем в Ефрафе, нашли его на полях Иарима.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24513, 'rst66', 'psa', 131, 7, 'Пойдем к жилищу Его, поклонимся подножию ног Его.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24514, 'rst66', 'psa', 131, 8, 'Стань, Господи, на <i>место</i> покоя Твоего, &mdash; Ты и ковчег могущества Твоего.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24515, 'rst66', 'psa', 131, 9, 'Священники Твои облекутся правдою, и святые Твои возрадуются.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24516, 'rst66', 'psa', 131, 10, 'Ради Давида, раба Твоего, не отврати лица помазанника Твоего.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24517, 'rst66', 'psa', 131, 11, 'Клялся Господь Давиду в истине, и не отречется ее: «от плода чрева твоего посажу на престоле твоем.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24518, 'rst66', 'psa', 131, 12, 'Если сыновья твои будут сохранять завет Мой и откровения Мои, которым Я научу их, то и их сыновья вовеки будут сидеть на престоле твоем».');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24519, 'rst66', 'psa', 131, 13, 'Ибо избрал Господь Сион, возжелал <i>его</i> в жилище Себе.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24520, 'rst66', 'psa', 131, 14, '«Это покой Мой навеки: здесь вселюсь, ибо Я возжелал его.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24521, 'rst66', 'psa', 131, 15, 'Пищу его благословляя благословлю, нищих его насыщу хлебом;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24522, 'rst66', 'psa', 131, 16, 'священников его облеку во спасение, и святые его радостью возрадуются.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24523, 'rst66', 'psa', 131, 17, 'Там возращу рог Давиду, поставлю светильник помазаннику Моему.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24524, 'rst66', 'psa', 131, 18, 'Врагов его облеку стыдом, а на нем будет сиять венец его».');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24525, 'rst66', 'psa', 132, 0, 'Песнь восхождения. Давида.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24526, 'rst66', 'psa', 132, 1, 'Как хорошо и как приятно жить братьям вместе!');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24527, 'rst66', 'psa', 132, 2, '<i>Это</i> &mdash; как драгоценный елей на голове, стекающий на бороду, бороду Ааронову, стекающий на края одежды его;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24528, 'rst66', 'psa', 132, 3, 'как роса Ермонская, сходящая на горы Сионские, ибо там заповедал Господь благословение и жизнь навеки.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24529, 'rst66', 'psa', 133, 0, 'Песнь восхождения.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24530, 'rst66', 'psa', 133, 1, 'Благословите ныне Господа все рабы Господни, стоящие в доме Господнем, во время ночи.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24531, 'rst66', 'psa', 133, 2, 'Воздвигните руки ваши к святилищу и благословите Господа.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24532, 'rst66', 'psa', 133, 3, 'Благословит тебя Господь с Сиона, сотворивший небо и землю.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24533, 'rst66', 'psa', 134, 0, 'Аллилуия.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24534, 'rst66', 'psa', 134, 1, 'Хвалите имя Господне, хвалите, рабы Господни,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24535, 'rst66', 'psa', 134, 2, 'стоящие в доме Господнем, во дворах дома Бога нашего.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24536, 'rst66', 'psa', 134, 3, 'Хвалите Господа, ибо Господь благ; пойте имени Его, ибо это сладостно,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24537, 'rst66', 'psa', 134, 4, 'ибо Господь избрал Себе Иакова, Израиля в собственность Свою.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24538, 'rst66', 'psa', 134, 5, 'Я познал, что велик Господь, и Господь наш превыше всех богов.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24539, 'rst66', 'psa', 134, 6, 'Господь творит все, что хочет, на небесах и на земле, на морях и во всех безднах;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24540, 'rst66', 'psa', 134, 7, 'возводит облака от края земли, творит молнии при дожде, изводит ветер из хранилищ Своих.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24541, 'rst66', 'psa', 134, 8, 'Он поразил первенцев Египта, от человека до скота,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24542, 'rst66', 'psa', 134, 9, 'послал знамения и чудеса среди тебя, Египет, на фараона и на всех рабов его,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24543, 'rst66', 'psa', 134, 10, 'поразил народы многие и истребил царей сильных:');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24544, 'rst66', 'psa', 134, 11, 'Сигона, царя Аморрейского, и Ога, царя Васанского, и все царства Ханаанские;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24545, 'rst66', 'psa', 134, 12, 'и отдал землю их в наследие, в наследие Израилю, народу Своему.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24546, 'rst66', 'psa', 134, 13, 'Господи! имя Твое вовек; Господи! память о Тебе в род и род.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24547, 'rst66', 'psa', 134, 14, 'Ибо Господь будет судить народ Свой и над рабами Своими умилосердится.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24548, 'rst66', 'psa', 134, 15, 'Идолы язычников &mdash; серебро и золото, дело рук человеческих:');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24549, 'rst66', 'psa', 134, 16, 'есть у них уста, но не говорят; есть у них глаза, но не видят;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24550, 'rst66', 'psa', 134, 17, 'есть у них уши, но не слышат, и нет дыхания в устах их.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24551, 'rst66', 'psa', 134, 18, 'Подобны им будут делающие их и всякий, кто надеется на них.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24552, 'rst66', 'psa', 134, 19, 'Дом Израилев! благословите Господа. Дом Ааронов! благословите Господа.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24553, 'rst66', 'psa', 134, 20, 'Дом Левиин! благословите Господа. Боящиеся Господа! благословите Господа.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24554, 'rst66', 'psa', 134, 21, 'Благословен Господь от Сиона, живущий в Иерусалиме! Аллилуия!');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24555, 'rst66', 'psa', 135, 1, 'Славьте Господа, ибо Он благ, ибо вовек милость Его.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24556, 'rst66', 'psa', 135, 2, 'Славьте Бога богов, ибо вовек милость Его.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24557, 'rst66', 'psa', 135, 3, 'Славьте Господа господствующих, ибо вовек милость Его;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24558, 'rst66', 'psa', 135, 4, 'Того, Который один творит чудеса великие, ибо вовек милость Его;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24559, 'rst66', 'psa', 135, 5, 'Который сотворил небеса премудро, ибо вовек милость Его;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24560, 'rst66', 'psa', 135, 6, 'утвердил землю на водах, ибо вовек милость Его;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24561, 'rst66', 'psa', 135, 7, 'сотворил светила великие, ибо вовек милость Его;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24562, 'rst66', 'psa', 135, 8, 'солнце &mdash; для управления днем, ибо вовек милость Его;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24563, 'rst66', 'psa', 135, 9, 'луну и звезды &mdash; для управления ночью, ибо вовек милость Его;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24564, 'rst66', 'psa', 135, 10, 'Поразил Египет в первенцах его, ибо вовек милость Его;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24565, 'rst66', 'psa', 135, 11, 'и вывел Израиля из среды его, ибо вовек милость Его;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24566, 'rst66', 'psa', 135, 12, 'рукою крепкою и мышцею простертою, ибо вовек милость Его;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24567, 'rst66', 'psa', 135, 13, 'разделил Чермное море, ибо вовек милость Его;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24568, 'rst66', 'psa', 135, 14, 'и провел Израиля посреди его, ибо вовек милость Его;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24569, 'rst66', 'psa', 135, 15, 'и низверг фараона и войско его в море Чермное, ибо вовек милость Его;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24570, 'rst66', 'psa', 135, 16, 'Провел народ Свой чрез пустыню, ибо вовек милость Его;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24571, 'rst66', 'psa', 135, 17, 'поразил царей великих, ибо вовек милость Его;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24572, 'rst66', 'psa', 135, 18, 'и убил царей сильных, ибо вовек милость Его;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24573, 'rst66', 'psa', 135, 19, 'Сигона, царя Аморрейского, ибо вовек милость Его;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24574, 'rst66', 'psa', 135, 20, 'и Ога, царя Васанского, ибо вовек милость Его;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24575, 'rst66', 'psa', 135, 21, 'и отдал землю их в наследие, ибо вовек милость Его;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24576, 'rst66', 'psa', 135, 22, 'в наследие Израилю, рабу Своему, ибо вовек милость Его;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24577, 'rst66', 'psa', 135, 23, 'Вспомнил нас в унижении нашем, ибо вовек милость Его;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24578, 'rst66', 'psa', 135, 24, 'и избавил нас от врагов наших, ибо вовек милость Его;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24579, 'rst66', 'psa', 135, 25, 'дает пищу всякой плоти, ибо вовек милость Его.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24580, 'rst66', 'psa', 135, 26, 'Славьте Бога небес, ибо вовек милость Его.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24581, 'rst66', 'psa', 136, 1, 'При реках Вавилона, там сидели мы и плакали, когда вспоминали о Сионе;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24582, 'rst66', 'psa', 136, 2, 'на вербах, посреди его, повесили мы наши арфы.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24583, 'rst66', 'psa', 136, 3, 'Там пленившие нас требовали от нас слов песней, и притеснители наши &mdash; веселья: «пропойте нам из песней Сионских».');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24584, 'rst66', 'psa', 136, 4, 'Как нам петь песнь Господню на земле чужой?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24585, 'rst66', 'psa', 136, 5, 'Если я забуду тебя, Иерусалим, &mdash; забудь меня, десница моя;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24586, 'rst66', 'psa', 136, 6, 'прилипни язык мой к гортани моей, если не буду помнить тебя, если не поставлю Иерусалима во главе веселия моего.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24587, 'rst66', 'psa', 136, 7, 'Припомни, Господи, сынам Едомовым день Иерусалима, когда они говорили: «разрушайте, разрушайте до основания его».');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24588, 'rst66', 'psa', 136, 8, 'Дочь Вавилона, опустошительница! блажен, кто воздаст тебе за то, что ты сделала нам!');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24589, 'rst66', 'psa', 136, 9, 'Блажен, кто возьмет и разобьет младенцев твоих о камень!');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24590, 'rst66', 'psa', 137, 0, 'Давида.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24591, 'rst66', 'psa', 137, 1, 'Славлю Тебя всем сердцем моим, пред богами пою Тебе.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24592, 'rst66', 'psa', 137, 2, 'Поклоняюсь пред святым храмом Твоим и славлю имя Твое за милость Твою и за истину Твою, ибо Ты возвеличил слово Твое превыше всякого имени Твоего.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24593, 'rst66', 'psa', 137, 3, 'В день, когда я воззвал, Ты услышал меня, вселил в душу мою бодрость.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24594, 'rst66', 'psa', 137, 4, 'Прославят Тебя, Господи, все цари земные, когда услышат слова уст Твоих,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24595, 'rst66', 'psa', 137, 5, 'и воспоют пути Господни, ибо велика слава Господня.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24596, 'rst66', 'psa', 137, 6, 'Высок Господь: и смиренного видит, и гордого узнает издали.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24597, 'rst66', 'psa', 137, 7, 'Если я пойду посреди напастей, Ты оживишь меня, прострешь на ярость врагов моих руку Твою, и спасет меня десница Твоя.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24598, 'rst66', 'psa', 137, 8, 'Господь совершит за меня! Милость Твоя, Господи, вовек: дело рук Твоих не оставляй.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24599, 'rst66', 'psa', 138, 0, 'Начальнику хора. Псалом Давида.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24600, 'rst66', 'psa', 138, 1, 'Господи! Ты испытал меня и знаешь.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24601, 'rst66', 'psa', 138, 2, 'Ты знаешь, когда я сажусь и когда встаю; Ты разумеешь помышления мои издали.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24602, 'rst66', 'psa', 138, 3, 'Иду ли я, отдыхаю ли &mdash; Ты окружаешь меня, и все пути мои известны Тебе.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24603, 'rst66', 'psa', 138, 4, 'Еще нет слова на языке моем, &mdash; Ты, Господи, уже знаешь его совершенно.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24604, 'rst66', 'psa', 138, 5, 'Сзади и спереди Ты объемлешь меня, и полагаешь на мне руку Твою.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24605, 'rst66', 'psa', 138, 6, 'Дивно для меня ведение <i>Твое</i>, &mdash; высоко, не могу постигнуть его!');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24606, 'rst66', 'psa', 138, 7, 'Куда пойду от Духа Твоего, и от лица Твоего куда убегу?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24607, 'rst66', 'psa', 138, 8, 'Взойду ли на небо &mdash; Ты там; сойду ли в преисподнюю &mdash; и там Ты.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24608, 'rst66', 'psa', 138, 9, 'Возьму ли крылья зари и переселюсь на край моря, &mdash;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24609, 'rst66', 'psa', 138, 10, 'и там рука Твоя поведет меня, и удержит меня десница Твоя.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24610, 'rst66', 'psa', 138, 11, 'Скажу ли: «может быть, тьма скроет меня, и свет вокруг меня <i>сделается</i> ночью»;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24611, 'rst66', 'psa', 138, 12, 'но и тьма не затмит от Тебя, и ночь светла, как день: как тьма, так и свет.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24612, 'rst66', 'psa', 138, 13, 'Ибо Ты устроил внутренности мои и соткал меня во чреве матери моей.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24613, 'rst66', 'psa', 138, 14, 'Славлю Тебя, потому что я дивно устроен. Дивны дела Твои, и душа моя вполне сознает это.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24614, 'rst66', 'psa', 138, 15, 'Не сокрыты были от Тебя кости мои, когда я созидаем был в тайне, образуем был во глубине утробы.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24615, 'rst66', 'psa', 138, 16, 'Зародыш мой видели очи Твои; в Твоей книге записаны все дни, для меня назначенные, когда ни одного из них еще не было.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24616, 'rst66', 'psa', 138, 17, 'Как возвышенны для меня помышления Твои, Боже, и как велико число их!');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24617, 'rst66', 'psa', 138, 18, 'Стану ли исчислять их, но они многочисленнее песка; когда я пробуждаюсь, я все еще с Тобою.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24618, 'rst66', 'psa', 138, 19, 'О, если бы Ты, Боже, поразил нечестивого! Удалитесь от меня, кровожадные!');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24619, 'rst66', 'psa', 138, 20, 'Они говорят против Тебя нечестиво; суетное замышляют враги Твои.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24620, 'rst66', 'psa', 138, 21, 'Мне ли не возненавидеть ненавидящих Тебя, Господи, и не возгнушаться восстающими на Тебя?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24621, 'rst66', 'psa', 138, 22, 'Полною ненавистью ненавижу их: враги они мне.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24622, 'rst66', 'psa', 138, 23, 'Испытай меня, Боже, и узнай сердце мое; испытай меня и узнай помышления мои;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24623, 'rst66', 'psa', 138, 24, 'и зри, не на опасном ли я пути, и направь меня на путь вечный.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24624, 'rst66', 'psa', 139, 0, 'Псалом.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24625, 'rst66', 'psa', 139, 1, 'Начальнику хора. Псалом Давида.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24626, 'rst66', 'psa', 139, 2, 'Избавь меня, Господи, от человека злого; сохрани меня от притеснителя:');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24627, 'rst66', 'psa', 139, 3, 'они злое мыслят в сердце, всякий день ополчаются на брань,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24628, 'rst66', 'psa', 139, 4, 'изощряют язык свой, как змея; яд аспида под устами их.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24629, 'rst66', 'psa', 139, 5, 'Соблюди меня, Господи, от рук нечестивого, сохрани меня от притеснителей, которые замыслили поколебать стопы мои.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24630, 'rst66', 'psa', 139, 6, 'Гордые скрыли силки для меня и петли, раскинули сеть по дороге, тенета разложили для меня.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24631, 'rst66', 'psa', 139, 7, 'Я сказал Господу: Ты Бог мой; услышь, Господи, голос молений моих!');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24632, 'rst66', 'psa', 139, 8, 'Господи, Господи, сила спасения моего! Ты покрыл голову мою в день брани.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24633, 'rst66', 'psa', 139, 9, 'Не дай, Господи, желаемого нечестивому; не дай успеха злому замыслу его: они возгордятся.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24634, 'rst66', 'psa', 139, 10, 'Да покроет головы окружающих меня зло собственных уст их.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24635, 'rst66', 'psa', 139, 11, 'Да падут на них горящие угли; да будут они повержены в огонь, в пропасти, так, чтобы не встали.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24636, 'rst66', 'psa', 139, 12, 'Человек злоязычный не утвердится на земле; зло увлечет притеснителя в погибель.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24637, 'rst66', 'psa', 139, 13, 'Знаю, что Господь сотворит суд угнетенным и справедливость бедным.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24638, 'rst66', 'psa', 139, 14, 'Так! праведные будут славить имя Твое; непорочные будут обитать пред лицом Твоим.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24639, 'rst66', 'psa', 140, 0, 'Псалом Давида.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24640, 'rst66', 'psa', 140, 1, 'Господи! к тебе взываю: поспеши ко мне, внемли голосу моления моего, когда взываю к Тебе.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24641, 'rst66', 'psa', 140, 2, 'Да направится молитва моя, как фимиам, пред лицо Твое, воздеяние рук моих &mdash; как жертва вечерняя.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24642, 'rst66', 'psa', 140, 3, 'Положи, Господи, охрану устам моим, и огради двери уст моих;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24643, 'rst66', 'psa', 140, 4, 'не дай уклониться сердцу моему к словам лукавым для извинения дел греховных вместе с людьми, делающими беззаконие, и да не вкушу я от сластей их.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24644, 'rst66', 'psa', 140, 5, 'Пусть наказывает меня праведник: это милость; пусть обличает меня: это лучший елей, который не повредит голове моей; но мольбы мои &mdash; против злодейств их.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24645, 'rst66', 'psa', 140, 6, 'Вожди их рассыпались по утесам и слышат слова мои, что они кротки.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24646, 'rst66', 'psa', 140, 7, 'Как будто землю рассекают и дробят нас; сыплются кости наши в челюсти преисподней.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24647, 'rst66', 'psa', 140, 8, 'Но к Тебе, Господи, Господи, очи мои; на Тебя уповаю, не отринь души моей!');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24648, 'rst66', 'psa', 140, 9, 'Сохрани меня от силков, поставленных для меня, от тенет беззаконников.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24649, 'rst66', 'psa', 140, 10, 'Падут нечестивые в сети свои, а я перейду.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24650, 'rst66', 'psa', 141, 0, 'Учение Давида. Молитва его, когда он был в пещере.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24651, 'rst66', 'psa', 141, 1, 'Голосом моим к Господу воззвал я, голосом моим к Господу помолился;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24652, 'rst66', 'psa', 141, 2, 'излил пред Ним моление мое; печаль мою открыл Ему.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24653, 'rst66', 'psa', 141, 3, 'Когда изнемогал во мне дух мой, Ты знал стезю мою. На пути, которым я ходил, они скрытно поставили сети для меня.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24654, 'rst66', 'psa', 141, 4, 'Смотрю на правую сторону, и вижу, что никто не признаёт меня: не стало для меня убежища, никто не заботится о душе моей.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24655, 'rst66', 'psa', 141, 5, 'Я воззвал к Тебе, Господи, я сказал: Ты прибежище мое и часть моя на земле живых.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24656, 'rst66', 'psa', 141, 6, 'Внемли воплю моему, ибо я очень изнемог; избавь меня от гонителей моих, ибо они сильнее меня.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24657, 'rst66', 'psa', 141, 7, 'Выведи из темницы душу мою, чтобы мне славить имя Твое. Вокруг меня соберутся праведные, когда Ты явишь мне благодеяние.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24658, 'rst66', 'psa', 142, 0, 'Псалом Давида.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24659, 'rst66', 'psa', 142, 1, 'Господи! услышь молитву мою, внемли молению моему по истине Твоей; услышь меня по правде Твоей');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24660, 'rst66', 'psa', 142, 2, 'и не входи в суд с рабом Твоим, потому что не оправдается пред Тобой ни один из живущих.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24661, 'rst66', 'psa', 142, 3, 'Враг преследует душу мою, втоптал в землю жизнь мою, принудил меня жить во тьме, как давно умерших, &mdash;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24662, 'rst66', 'psa', 142, 4, 'и уныл во мне дух мой, онемело во мне сердце мое.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24663, 'rst66', 'psa', 142, 5, 'Вспоминаю дни древние, размышляю о всех делах Твоих, рассуждаю о делах рук Твоих.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24664, 'rst66', 'psa', 142, 6, 'Простираю к Тебе руки мои; душа моя &mdash; к Тебе, как жаждущая земля.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24665, 'rst66', 'psa', 142, 7, 'Скоро услышь меня, Господи: дух мой изнемогает; не скрывай лица Твоего от меня, чтобы я не уподобился нисходящим в могилу.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24666, 'rst66', 'psa', 142, 8, 'Даруй мне рано услышать милость Твою, ибо я на Тебя уповаю. Укажи мне путь, по которому мне идти, ибо к Тебе возношу я душу мою.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24667, 'rst66', 'psa', 142, 9, 'Избавь меня, Господи, от врагов моих; к Тебе прибегаю.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24668, 'rst66', 'psa', 142, 10, 'Научи меня исполнять волю Твою, потому что Ты Бог мой; Дух Твой благий да ведет меня в землю правды.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24669, 'rst66', 'psa', 142, 11, 'Ради имени Твоего, Господи, оживи меня; ради правды Твоей выведи из напасти душу мою.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24670, 'rst66', 'psa', 142, 12, 'И по милости Твоей истреби врагов моих и погуби всех, угнетающих душу мою, ибо я Твой раб.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24671, 'rst66', 'psa', 143, 0, 'Давида.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24672, 'rst66', 'psa', 143, 1, 'Благословен Господь, твердыня моя, научающий руки мои битве и персты мои &mdash; брани,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24673, 'rst66', 'psa', 143, 2, 'милость моя и ограждение мое, прибежище мое и Избавитель мой, щит мой, &mdash; и я на Него уповаю; Он подчиняет мне народ мой.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24674, 'rst66', 'psa', 143, 3, 'Господи! что есть человек, что Ты знаешь о нем, и сын человеческий, что обращаешь на него внимание?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24675, 'rst66', 'psa', 143, 4, 'Человек подобен дуновению; дни его &mdash; как уклоняющаяся тень.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24676, 'rst66', 'psa', 143, 5, 'Господи! Приклони небеса Твои и сойди; коснись гор, и воздымятся;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24677, 'rst66', 'psa', 143, 6, 'блесни молниею и рассей их; пусти стрелы Твои и расстрой их;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24678, 'rst66', 'psa', 143, 7, 'простри с высоты руку Твою, избавь меня и спаси меня от вод многих, от руки сынов иноплеменных,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24679, 'rst66', 'psa', 143, 8, 'которых уста говорят суетное и которых десница &mdash; десница лжи.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24680, 'rst66', 'psa', 143, 9, 'Боже! новую песнь воспою Тебе, на десятиструнной псалтири воспою Тебе,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24681, 'rst66', 'psa', 143, 10, 'дарующему спасение царям и избавляющему Давида, раба Твоего, от лютого меча.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24682, 'rst66', 'psa', 143, 11, 'Избавь меня и спаси меня от руки сынов иноплеменных, которых уста говорят суетное и которых десница &mdash; десница лжи.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24683, 'rst66', 'psa', 143, 12, 'Да будут сыновья наши, как разросшиеся растения в их молодости; дочери наши &mdash; как искусно изваянные столпы в чертогах.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24684, 'rst66', 'psa', 143, 13, 'Да будут житницы наши полны, обильны всяким хлебом; да плодятся овцы наши тысячами и тьмами на пажитях наших;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24685, 'rst66', 'psa', 143, 14, '<i>да будут</i> волы наши тучны; да не будет ни расхищения, ни пропажи, ни воплей на улицах наших.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24686, 'rst66', 'psa', 143, 15, 'Блажен народ, у которого это есть. Блажен народ, у которого Господь есть Бог.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24687, 'rst66', 'psa', 144, 0, 'Хвала Давида.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24688, 'rst66', 'psa', 144, 1, 'Буду превозносить Тебя, Боже мой, Царь <i>мой</i>, и благословлять имя Твое во веки и веки.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24689, 'rst66', 'psa', 144, 2, 'Всякий день буду благословлять Тебя и восхвалять имя Твое во веки и веки.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24690, 'rst66', 'psa', 144, 3, 'Велик Господь и достохвален, и величие Его неисследимо.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24691, 'rst66', 'psa', 144, 4, 'Род роду будет восхвалять дела Твои и возвещать о могуществе Твоем.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24692, 'rst66', 'psa', 144, 5, 'А я буду размышлять о высокой славе величия Твоего и о дивных делах Твоих.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24693, 'rst66', 'psa', 144, 6, 'Будут говорить о могуществе страшных дел Твоих, и я буду возвещать о величии Твоем.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24694, 'rst66', 'psa', 144, 7, 'Будут провозглашать память великой благости Твоей и воспевать правду Твою.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24695, 'rst66', 'psa', 144, 8, 'Щедр и милостив Господь, долготерпелив и многомилостив.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24696, 'rst66', 'psa', 144, 9, 'Благ Господь ко всем, и щедроты Его &mdash; на всех делах Его.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24697, 'rst66', 'psa', 144, 10, 'Да славят Тебя, Господи, все дела Твои, и да благословляют Тебя святые Твои;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24698, 'rst66', 'psa', 144, 11, 'да проповедуют славу царства Твоего, и да повествуют о могуществе Твоем,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24699, 'rst66', 'psa', 144, 12, 'чтобы дать знать сынам человеческим о могуществе Твоем и о славном величии царства Твоего.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24700, 'rst66', 'psa', 144, 13, 'Царство Твое &mdash; царство всех веков, и владычество Твое во все роды.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24701, 'rst66', 'psa', 144, 14, 'Господь поддерживает всех падающих и восставляет всех низверженных.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24702, 'rst66', 'psa', 144, 15, 'Очи всех уповают на Тебя, и Ты даешь им пищу их в свое время;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24703, 'rst66', 'psa', 144, 16, 'открываешь руку Твою и насыщаешь все живущее по благоволению.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24704, 'rst66', 'psa', 144, 17, 'Праведен Господь во всех путях Своих и благ во всех делах Своих.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24705, 'rst66', 'psa', 144, 18, 'Близок Господь ко всем призывающим Его, ко всем призывающим Его в истине.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24706, 'rst66', 'psa', 144, 19, 'Желание боящихся Его Он исполняет, вопль их слышит и спасает их.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24707, 'rst66', 'psa', 144, 20, 'Хранит Господь всех любящих Его, а всех нечестивых истребит.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24708, 'rst66', 'psa', 144, 21, 'Уста мои изрекут хвалу Господню, и да благословляет всякая плоть святое имя Его во веки и веки.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24709, 'rst66', 'psa', 145, 0, 'Аллилуия.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24710, 'rst66', 'psa', 145, 1, 'Хвали, душа моя, Господа.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24711, 'rst66', 'psa', 145, 2, 'Буду восхвалять Господа, доколе жив; буду петь Богу моему, доколе есмь.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24712, 'rst66', 'psa', 145, 3, 'Не надейтесь на князей, на сына человеческого, в котором нет спасения.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24713, 'rst66', 'psa', 145, 4, 'Выходит дух его, и он возвращается в землю свою: в тот день исчезают <i>все</i> помышления его.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24714, 'rst66', 'psa', 145, 5, 'Блажен, кому помощник Бог Иаковлев, у кого надежда на Господа Бога его,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24715, 'rst66', 'psa', 145, 6, 'сотворившего небо и землю, море и все, что в них, вечно хранящего верность,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24716, 'rst66', 'psa', 145, 7, 'творящего суд обиженным, дающего хлеб алчущим. Господь разрешает узников,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24717, 'rst66', 'psa', 145, 8, 'Господь отверзает очи слепым, Господь восставляет согбенных, Господь любит праведных.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24718, 'rst66', 'psa', 145, 9, 'Господь хранит пришельцев, поддерживает сироту и вдову, а путь нечестивых извращает.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24719, 'rst66', 'psa', 145, 10, 'Господь будет царствовать вовеки, Бог твой, Сион, в род и род. Аллилуия.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24720, 'rst66', 'psa', 146, 1, 'Хвалите Господа, ибо благо петь Богу нашему, ибо это сладостно, &mdash; хвала подобающая.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24721, 'rst66', 'psa', 146, 2, 'Господь созидает Иерусалим, собирает изгнанников Израиля.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24722, 'rst66', 'psa', 146, 3, 'Он исцеляет сокрушенных сердцем и врачует скорби их;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24723, 'rst66', 'psa', 146, 4, 'исчисляет количество звезд; всех их называет именами их.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24724, 'rst66', 'psa', 146, 5, 'Велик Господь наш, и велика крепость <i>Его</i>, и разум Его неизмерим.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24725, 'rst66', 'psa', 146, 6, 'Смиренных возвышает Господь, а нечестивых унижает до земли.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24726, 'rst66', 'psa', 146, 7, 'Пойте поочередно славословие Господу; пойте Богу нашему на гуслях.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24727, 'rst66', 'psa', 146, 8, 'Он покрывает небо облаками, приготовляет для земли дождь, произращает на горах траву;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24728, 'rst66', 'psa', 146, 9, 'дает скоту пищу его и птенцам во&#769;рона, взывающим <i>к Нему</i>.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24729, 'rst66', 'psa', 146, 10, 'Не на силу коня смотрит Он, не к <i>быстроте</i> ног человеческих благоволит, &mdash;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24730, 'rst66', 'psa', 146, 11, 'благоволит Господь к боящимся Его, к уповающим на милость Его.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24731, 'rst66', 'psa', 147, 1, 'Хвали, Иерусалим, Господа; хвали, Сион, Бога твоего,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24732, 'rst66', 'psa', 147, 2, 'ибо Он укрепляет вереи ворот твоих, благословляет сынов твоих среди тебя;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24733, 'rst66', 'psa', 147, 3, 'утверждает в пределах твоих мир; туком пшеницы насыщает тебя;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24734, 'rst66', 'psa', 147, 4, 'посылает слово Свое на землю; быстро течет слово Его;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24735, 'rst66', 'psa', 147, 5, 'дает снег, как во&#769;лну; сыплет иней, как пепел;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24736, 'rst66', 'psa', 147, 6, 'бросает град Свой кусками; перед морозом Его кто устоит?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24737, 'rst66', 'psa', 147, 7, 'Пошлет слово Свое, и все растает; подует ветром Своим, и потекут воды.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24738, 'rst66', 'psa', 147, 8, 'Он возвестил слово Свое Иакову, уставы Свои и суды Свои Израилю.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24739, 'rst66', 'psa', 147, 9, 'Не сделал Он того никакому <i>другому</i> народу, и судов Его они не знают. Аллилуия.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24740, 'rst66', 'psa', 148, 0, 'Аллилуия.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24741, 'rst66', 'psa', 148, 1, 'Хвалите Господа с небес, хвалите Его в вышних.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24742, 'rst66', 'psa', 148, 2, 'Хвалите Его, все Ангелы Его, хвалите Его, все воинства Его.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24743, 'rst66', 'psa', 148, 3, 'Хвалите Его, солнце и луна, хвалите Его, все звезды света.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24744, 'rst66', 'psa', 148, 4, 'Хвалите Его, небеса небес и воды, которые превыше небес.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24745, 'rst66', 'psa', 148, 5, 'Да хвалят имя Господа, ибо Он повелел, и сотворились;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24746, 'rst66', 'psa', 148, 6, 'поставил их на веки и веки; дал устав, который не прейдет.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24747, 'rst66', 'psa', 148, 7, 'Хвалите Господа от земли, великие рыбы и все бездны,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24748, 'rst66', 'psa', 148, 8, 'огонь и град, снег и туман, бурный ветер, исполняющий слово Его,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24749, 'rst66', 'psa', 148, 9, 'горы и все холмы, дерева плодоносные и все кедры,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24750, 'rst66', 'psa', 148, 10, 'звери и всякий скот, пресмыкающиеся и птицы крылатые,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24751, 'rst66', 'psa', 148, 11, 'цари земные и все народы, князья и все судьи земные,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24752, 'rst66', 'psa', 148, 12, 'юноши и девицы, старцы и отроки');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24753, 'rst66', 'psa', 148, 13, 'да хвалят имя Господа, ибо имя Его единого превознесенно, слава Его на земле и на небесах.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24754, 'rst66', 'psa', 148, 14, 'Он возвысил рог народа Своего, славу всех святых Своих, сынов Израилевых, народа, близкого к Нему. Аллилуия.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24755, 'rst66', 'psa', 149, 0, 'Аллилуия.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24756, 'rst66', 'psa', 149, 1, 'Пойте Господу песнь новую; хвала Ему в собрании святых.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24757, 'rst66', 'psa', 149, 2, 'Да веселится Израиль о Создателе своем; сыны Сиона да радуются о Царе своем.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24758, 'rst66', 'psa', 149, 3, 'Да хвалят имя Его с ликами, на тимпане и гуслях да поют Ему,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24759, 'rst66', 'psa', 149, 4, 'ибо благоволит Господь к народу Своему, прославляет смиренных спасением.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24760, 'rst66', 'psa', 149, 5, 'Да торжествуют святые во славе, да радуются на ложах своих.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24761, 'rst66', 'psa', 149, 6, 'Да будут славословия Богу в устах их, и меч обоюдоострый в руке их,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24762, 'rst66', 'psa', 149, 7, 'для того, чтобы совершать мщение над народами, наказание над племенами,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24763, 'rst66', 'psa', 149, 8, 'заключать царей их в узы и вельмож их в оковы железные,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24764, 'rst66', 'psa', 149, 9, 'производить над ними суд писанный. Честь сия &mdash; всем святым Его. Аллилуия.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24765, 'rst66', 'psa', 150, 0, 'Аллилуия.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24766, 'rst66', 'psa', 150, 1, 'Хвалите Бога во святыне Его, хвалите Его на тверди силы Его.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24767, 'rst66', 'psa', 150, 2, 'Хвалите Его по могуществу Его, хвалите Его по множеству величия Его.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24768, 'rst66', 'psa', 150, 3, 'Хвалите Его со звуком трубным, хвалите Его на псалтири и гуслях.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24769, 'rst66', 'psa', 150, 4, 'Хвалите Его с тимпаном и ликами, хвалите Его на струнах и орга&#769;не.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24770, 'rst66', 'psa', 150, 5, 'Хвалите Его на звучных кимвалах, хвалите Его на кимвалах громогласных.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24771, 'rst66', 'psa', 150, 6, 'Все дышащее да хвалит Господа! Аллилуия.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24772, 'rst66', 'pro', 1, 1, 'Притчи Соломона, сына Давидова, царя Израильского,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24773, 'rst66', 'pro', 1, 2, 'чтобы познать мудрость и наставление, понять изречения разума;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24774, 'rst66', 'pro', 1, 3, 'усвоить правила благоразумия, правосудия, суда и правоты;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24775, 'rst66', 'pro', 1, 4, 'простым дать смышленость, юноше &mdash; знание и рассудительность;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24776, 'rst66', 'pro', 1, 5, '(послушает мудрый &mdash; и умножит познания, и разумный найдет мудрые советы);');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24777, 'rst66', 'pro', 1, 6, 'чтобы разуметь притчу и замысловатую речь, слова мудрецов и загадки их.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24778, 'rst66', 'pro', 1, 7, 'Начало мудрости &mdash; страх Господень; глупцы только презирают мудрость и наставление.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24779, 'rst66', 'pro', 1, 8, 'Слушай, сын мой, наставление отца твоего и не отвергай завета матери твоей,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24780, 'rst66', 'pro', 1, 9, 'потому что это &mdash; прекрасный венок для головы твоей и украшение для шеи твоей.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24781, 'rst66', 'pro', 1, 10, 'Сын мой! если будут склонять тебя грешники, не соглашайся;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24782, 'rst66', 'pro', 1, 11, 'если будут говорить: «иди с нами, сделаем засаду для убийства, подстережем непорочного без вины,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24783, 'rst66', 'pro', 1, 12, 'живых проглотим их, как преисподняя, и &mdash; целых, как нисходящих в могилу;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24784, 'rst66', 'pro', 1, 13, 'наберем всякого драгоценного имущества, наполним домы наши добычею;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24785, 'rst66', 'pro', 1, 14, 'жребий твой ты будешь бросать вместе с нами, склад один будет у всех нас», &mdash;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24786, 'rst66', 'pro', 1, 15, 'Сын мой! не ходи в путь с ними, удержи ногу твою от стези их,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24787, 'rst66', 'pro', 1, 16, 'потому что ноги их бегут ко злу и спешат на пролитие крови.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24788, 'rst66', 'pro', 1, 17, 'В глазах всех птиц напрасно расставляется сеть,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24789, 'rst66', 'pro', 1, 18, 'а делают засаду для их крови и подстерегают их души.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24790, 'rst66', 'pro', 1, 19, 'Таковы пути всякого, кто алчет чужого добра: оно отнимает жизнь у завладевшего им.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24791, 'rst66', 'pro', 1, 20, 'Премудрость возглашает на улице, на площадях возвышает голос свой,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24792, 'rst66', 'pro', 1, 21, 'в главных местах собраний проповедует, при входах в городские ворота говорит речь свою:');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24793, 'rst66', 'pro', 1, 22, '«Доколе, невежды, будете любить невежество? <i>доколе</i> буйные будут услаждаться буйством? доколе глупцы будут ненавидеть знание?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24794, 'rst66', 'pro', 1, 23, 'Обратитесь к моему обличению: вот, я изолью на вас дух мой, возвещу вам слова мои.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24795, 'rst66', 'pro', 1, 24, 'Я звала, и вы не послушались; простирала руку мою, и не было внимающего;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24796, 'rst66', 'pro', 1, 25, 'и вы отвергли все мои советы, и обличений моих не приняли.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24797, 'rst66', 'pro', 1, 26, 'За то и я посмеюсь вашей погибели; порадуюсь, когда придет на вас ужас;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24798, 'rst66', 'pro', 1, 27, 'когда придет на вас ужас, как буря, и беда, как вихрь, принесется на вас; когда постигнет вас скорбь и теснота,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24799, 'rst66', 'pro', 1, 28, 'тогда будут звать меня, и я не услышу; с утра будут искать меня, и не найдут меня.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24800, 'rst66', 'pro', 1, 29, 'За то, что они возненавидели знание и не избрали <i>для себя</i> страха Господня,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24801, 'rst66', 'pro', 1, 30, 'не приняли совета моего, презрели все обличения мои;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24802, 'rst66', 'pro', 1, 31, 'за то и будут они вкушать от плодов путей своих и насыщаться от помыслов их.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24803, 'rst66', 'pro', 1, 32, 'Потому что упорство невежд убьет их, и беспечность глупцов погубит их,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24804, 'rst66', 'pro', 1, 33, 'а слушающий меня будет жить безопасно и спокойно, не страшась зла».');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24805, 'rst66', 'pro', 2, 1, 'Сын мой! если ты примешь слова мои и сохранишь при себе заповеди мои,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24806, 'rst66', 'pro', 2, 2, 'так что ухо твое сделаешь внимательным к мудрости и наклонишь сердце твое к размышлению;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24807, 'rst66', 'pro', 2, 3, 'если будешь призывать знание и взывать к разуму;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24808, 'rst66', 'pro', 2, 4, 'если будешь искать его, как серебра, и отыскивать его, как сокровище,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24809, 'rst66', 'pro', 2, 5, 'то уразумеешь страх Господень и найдешь познание о Боге.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24810, 'rst66', 'pro', 2, 6, 'Ибо Господь дает мудрость; из уст Его &mdash; знание и разум;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24811, 'rst66', 'pro', 2, 7, 'Он сохраняет для праведных спасение; Он &mdash; щит для ходящих непорочно;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24812, 'rst66', 'pro', 2, 8, 'Он охраняет пути правды и оберегает стезю святых Своих.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24813, 'rst66', 'pro', 2, 9, 'Тогда ты уразумеешь правду и правосудие и прямоту, всякую добрую стезю.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24814, 'rst66', 'pro', 2, 10, 'Когда мудрость войдет в сердце твое, и знание будет приятно душе твоей,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24815, 'rst66', 'pro', 2, 11, 'тогда рассудительность будет оберегать тебя, разум будет охранять тебя,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24816, 'rst66', 'pro', 2, 12, 'дабы спасти тебя от пути злого, от человека, говорящего ложь,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24817, 'rst66', 'pro', 2, 13, 'от тех, которые оставляют стези прямые, чтобы ходить путями тьмы;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24818, 'rst66', 'pro', 2, 14, 'от тех, которые радуются, делая зло, восхищаются злым развратом,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24819, 'rst66', 'pro', 2, 15, 'которых пути кривы, и которые блуждают на стезях своих;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24820, 'rst66', 'pro', 2, 16, 'дабы спасти тебя от жены другого, от чужой, которая умягчает речи свои,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24821, 'rst66', 'pro', 2, 17, 'которая оставила руководителя юности своей и забыла завет Бога своего.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24822, 'rst66', 'pro', 2, 18, 'Дом ее ведет к смерти, и стези ее &mdash; к мертвецам;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24823, 'rst66', 'pro', 2, 19, 'никто из вошедших к ней не возвращается и не вступает на путь жизни.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24824, 'rst66', 'pro', 2, 20, 'Посему ходи путем добрых и держись стезей праведников,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24825, 'rst66', 'pro', 2, 21, 'потому что праведные будут жить на земле, и непорочные пребудут на ней;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24826, 'rst66', 'pro', 2, 22, 'а беззаконные будут истреблены с земли, и вероломные искоренены из нее.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24827, 'rst66', 'pro', 3, 1, 'Сын мой! наставления моего не забывай, и заповеди мои да хранит сердце твое;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24828, 'rst66', 'pro', 3, 2, 'ибо долготы дней, лет жизни и мира они приложат тебе.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24829, 'rst66', 'pro', 3, 3, 'Милость и истина да не оставляют тебя: обвяжи ими шею твою, напиши их на скрижали сердца твоего,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24830, 'rst66', 'pro', 3, 4, 'и обретешь милость и благоволение в очах Бога и людей.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24831, 'rst66', 'pro', 3, 5, 'Надейся на Господа всем сердцем твоим, и не полагайся на разум твой.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24832, 'rst66', 'pro', 3, 6, 'Во всех путях твоих познавай Его, и Он направит стези твои.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24833, 'rst66', 'pro', 3, 7, 'Не будь мудрецом в глазах твоих; бойся Господа и удаляйся от зла:');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24834, 'rst66', 'pro', 3, 8, 'это будет здравием для тела твоего и питанием для костей твоих.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24835, 'rst66', 'pro', 3, 9, 'Чти Господа от имения твоего и от начатков всех прибытков твоих,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24836, 'rst66', 'pro', 3, 10, 'и наполнятся житницы твои до избытка, и точила твои будут переливаться новым вином.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24837, 'rst66', 'pro', 3, 11, 'Наказания Господня, сын мой, не отвергай, и не тяготись обличением Его;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24838, 'rst66', 'pro', 3, 12, 'ибо кого любит Господь, того наказывает и благоволит к тому, как отец к сыну своему.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24839, 'rst66', 'pro', 3, 13, 'Блажен человек, который снискал мудрость, и человек, который приобрел разум, &mdash;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24840, 'rst66', 'pro', 3, 14, 'потому что приобретение ее лучше приобретения серебра, и прибыли от нее больше, нежели от золота:');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24841, 'rst66', 'pro', 3, 15, 'она дороже драгоценных камней; и ничто из желаемого тобою не сравнится с нею.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24842, 'rst66', 'pro', 3, 16, 'Долгоденствие &mdash; в правой руке ее, а в левой у нее &mdash; богатство и слава;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24843, 'rst66', 'pro', 3, 17, 'пути ее &mdash; пути приятные, и все стези ее &mdash; мирные.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24844, 'rst66', 'pro', 3, 18, 'Она &mdash; древо жизни для тех, которые приобретают ее, &mdash; и блаженны, которые сохраняют ее!');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24845, 'rst66', 'pro', 3, 19, 'Господь премудростью основал землю, небеса утвердил разумом;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24846, 'rst66', 'pro', 3, 20, 'Его премудростью разверзлись бездны, и облака кропят росою.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24847, 'rst66', 'pro', 3, 21, 'Сын мой! не упускай их из глаз твоих; храни здравомыслие и рассудительность,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24848, 'rst66', 'pro', 3, 22, 'и они будут жизнью для души твоей и украшением для шеи твоей.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24849, 'rst66', 'pro', 3, 23, 'Тогда безопасно пойдешь по пути твоему, и нога твоя не споткнется.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24850, 'rst66', 'pro', 3, 24, 'Когда ляжешь спать, &mdash; не будешь бояться; и когда уснешь, &mdash; сон твой приятен будет.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24851, 'rst66', 'pro', 3, 25, 'Не убоишься внезапного страха и пагубы от нечестивых, когда она придет;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24852, 'rst66', 'pro', 3, 26, 'потому что Господь будет упованием твоим и сохранит ногу твою от уловления.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24853, 'rst66', 'pro', 3, 27, 'Не отказывай в благодеянии нуждающемуся, когда рука твоя в силе сделать его.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24854, 'rst66', 'pro', 3, 28, 'Не говори другу твоему: «пойди и приди опять, и завтра я дам», когда ты имеешь при себе.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24855, 'rst66', 'pro', 3, 29, 'Не замышляй против ближнего твоего зла, когда он без опасения живет с тобою.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24856, 'rst66', 'pro', 3, 30, 'Не ссорься с человеком без причины, когда он не сделал зла тебе.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24857, 'rst66', 'pro', 3, 31, 'Не соревнуй человеку, поступающему насильственно, и не избирай ни одного из путей его;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24858, 'rst66', 'pro', 3, 32, 'потому что мерзость пред Господом развратный, а с праведными у Него общение.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24859, 'rst66', 'pro', 3, 33, 'Проклятие Господне на доме нечестивого, а жилище благочестивых Он благословляет.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24860, 'rst66', 'pro', 3, 34, 'Если над кощунниками Он посмевается, то смиренным дает благодать.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24861, 'rst66', 'pro', 3, 35, 'Мудрые наследуют славу, а глупые &mdash; бесславие.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24862, 'rst66', 'pro', 4, 1, 'Слушайте, дети, наставление отца, и внимайте, чтобы научиться разуму,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24863, 'rst66', 'pro', 4, 2, 'потому что я преподал вам доброе учение. Не оставляйте заповеди моей.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24864, 'rst66', 'pro', 4, 3, 'Ибо и я был сын у отца моего, нежно любимый и единственный у матери моей,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24865, 'rst66', 'pro', 4, 4, 'и он учил меня и говорил мне: да удержит сердце твое слова мои; храни заповеди мои, и живи.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24866, 'rst66', 'pro', 4, 5, 'Приобретай мудрость, приобретай разум: не забывай этого и не уклоняйся от слов уст моих.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24867, 'rst66', 'pro', 4, 6, 'Не оставляй ее, и она будет охранять тебя; люби ее, и она будет оберегать тебя.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24868, 'rst66', 'pro', 4, 7, 'Главное &mdash; мудрость: приобретай мудрость, и всем имением твоим приобретай разум.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24869, 'rst66', 'pro', 4, 8, 'Высоко цени ее, и она возвысит тебя; она прославит тебя, если ты прилепишься к ней;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24870, 'rst66', 'pro', 4, 9, 'возложит на голову твою прекрасный венок, доставит тебе великолепный венец.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24871, 'rst66', 'pro', 4, 10, 'Слушай, сын мой, и прими слова мои, &mdash; и умножатся тебе лета жизни.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24872, 'rst66', 'pro', 4, 11, 'Я указываю тебе путь мудрости, веду тебя по стезям прямым.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24873, 'rst66', 'pro', 4, 12, 'Когда пойдешь, не будет стеснен ход твой, и когда побежишь, не споткнешься.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24874, 'rst66', 'pro', 4, 13, 'Крепко держись наставления, не оставляй, храни его, потому что оно &mdash; жизнь твоя.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24875, 'rst66', 'pro', 4, 14, 'Не вступай на стезю нечестивых и не ходи по пути злых;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24876, 'rst66', 'pro', 4, 15, 'оставь его, не ходи по нему, уклонись от него и пройди мимо;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24877, 'rst66', 'pro', 4, 16, 'потому что они не заснут, если не сделают зла; пропадает сон у них, если они не доведут кого до падения;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24878, 'rst66', 'pro', 4, 17, 'ибо они едят хлеб беззакония и пьют вино хищения.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24879, 'rst66', 'pro', 4, 18, 'Стезя праведных &mdash; как светило лучезарное, которое более и более светлеет до полного дня.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24880, 'rst66', 'pro', 4, 19, 'Путь же беззаконных &mdash; как тьма; они не знают, обо что споткнутся.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24881, 'rst66', 'pro', 4, 20, 'Сын мой! словам моим внимай, и к речам моим приклони ухо твое;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24882, 'rst66', 'pro', 4, 21, 'да не отходят они от глаз твоих; храни их внутри сердца твоего:');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24883, 'rst66', 'pro', 4, 22, 'потому что они жизнь для того, кто нашел их, и здравие для всего тела его.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24884, 'rst66', 'pro', 4, 23, 'Больше всего хранимого храни сердце твое, потому что из него источники жизни.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24885, 'rst66', 'pro', 4, 24, 'Отвергни от себя лживость уст, и лукавство языка удали от себя.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24886, 'rst66', 'pro', 4, 25, 'Глаза твои пусть прямо смотрят, и ресницы твои да направлены будут прямо пред тобою.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24887, 'rst66', 'pro', 4, 26, 'Обдумай стезю для ноги твоей, и все пути твои да будут тверды.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24888, 'rst66', 'pro', 4, 27, 'Не уклоняйся ни направо, ни налево; удали ногу твою от зла.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24889, 'rst66', 'pro', 5, 1, 'Сын мой! внимай мудрости моей, и приклони ухо твое к разуму моему,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24890, 'rst66', 'pro', 5, 2, 'чтобы соблюсти рассудительность и чтобы уста твои сохранили знание.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24891, 'rst66', 'pro', 5, 3, 'Ибо мед источают уста чужой жены, и мягче елея речь ее;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24892, 'rst66', 'pro', 5, 4, 'но последствия от нее горьки, как полынь, остры, как меч обоюдоострый;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24893, 'rst66', 'pro', 5, 5, 'ноги ее нисходят к смерти, стопы ее достигают преисподней.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24894, 'rst66', 'pro', 5, 6, 'Если бы ты захотел постигнуть стезю жизни ее, то пути ее непостоянны, и ты не узнаешь их.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24895, 'rst66', 'pro', 5, 7, 'Итак, дети, слушайте меня и не отступайте от слов уст моих.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24896, 'rst66', 'pro', 5, 8, 'Держи дальше от нее путь твой и не подходи близко к дверям дома ее,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24897, 'rst66', 'pro', 5, 9, 'чтобы здоровья твоего не отдать другим и лет твоих мучителю;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24898, 'rst66', 'pro', 5, 10, 'чтобы не насыщались силою твоею чужие, и труды твои не были для чужого дома.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24899, 'rst66', 'pro', 5, 11, 'И ты будешь стонать после, когда плоть твоя и тело твое будут истощены, &mdash;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24900, 'rst66', 'pro', 5, 12, 'и скажешь: «зачем я ненавидел наставление, и сердце мое пренебрегало обличением,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24901, 'rst66', 'pro', 5, 13, 'и я не слушал голоса учителей моих, не приклонял уха моего к наставникам моим:');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24902, 'rst66', 'pro', 5, 14, 'едва не впал я во всякое зло среди собрания и общества!»');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24903, 'rst66', 'pro', 5, 15, 'Пей воду из твоего водоема и текущую из твоего колодезя.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24904, 'rst66', 'pro', 5, 16, 'Пусть <i>не</i> разливаются источники твои по улице, потоки вод &mdash; по площадям;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24905, 'rst66', 'pro', 5, 17, 'пусть они будут принадлежать тебе одному, а не чужим с тобою.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24906, 'rst66', 'pro', 5, 18, 'Источник твой да будет благословен; и утешайся женою юности твоей,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24907, 'rst66', 'pro', 5, 19, 'любезною ланью и прекрасною серною: груди ее да упоявают тебя во всякое время, любовью ее услаждайся постоянно.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24908, 'rst66', 'pro', 5, 20, 'И для чего тебе, сын мой, увлекаться постороннею и обнимать груди чужой?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24909, 'rst66', 'pro', 5, 21, 'Ибо пред очами Господа пути человека, и Он измеряет все стези его.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24910, 'rst66', 'pro', 5, 22, 'Беззаконного уловляют собственные беззакония его, и в узах греха своего он содержится:');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24911, 'rst66', 'pro', 5, 23, 'он умирает без наставления и от множества безумия своего теряется.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24912, 'rst66', 'pro', 6, 1, 'Сын мой! если ты поручился за ближнего твоего и дал руку твою за другого, &mdash;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24913, 'rst66', 'pro', 6, 2, 'ты опутал себя словами уст твоих, пойман словами уст твоих.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24914, 'rst66', 'pro', 6, 3, 'Сделай же, сын мой, вот что, и избавь себя, так как ты попался в руки ближнего твоего: пойди, пади к ногам и умоляй ближнего твоего;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24915, 'rst66', 'pro', 6, 4, 'не давай сна глазам твоим и дремания веждам твоим;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24916, 'rst66', 'pro', 6, 5, 'спасайся, как серна из руки и как птица из руки птицелова.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24917, 'rst66', 'pro', 6, 6, 'Пойди к муравью, ленивец, посмотри на действия его, и будь мудрым.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24918, 'rst66', 'pro', 6, 7, 'Нет у него ни начальника, ни приставника, ни повелителя;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24919, 'rst66', 'pro', 6, 8, 'но он заготовляет летом хлеб свой, собирает во время жатвы пищу свою.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24920, 'rst66', 'pro', 6, 9, 'Доколе ты, ленивец, будешь спать? когда ты встанешь от сна твоего?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24921, 'rst66', 'pro', 6, 10, 'Немного поспишь, немного подремлешь, немного, сложив руки, полежишь:');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24922, 'rst66', 'pro', 6, 11, 'и придет, как прохожий, бедность твоя, и нужда твоя, как разбойник.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24923, 'rst66', 'pro', 6, 12, 'Человек лукавый, человек нечестивый ходит со лживыми устами,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24924, 'rst66', 'pro', 6, 13, 'мигает глазами своими, говорит ногами своими, дает знаки пальцами своими;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24925, 'rst66', 'pro', 6, 14, 'коварство в сердце его: он умышляет зло во всякое время, сеет раздоры.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24926, 'rst66', 'pro', 6, 15, 'За то внезапно придет погибель его, вдруг будет разбит &mdash; без исцеления.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24927, 'rst66', 'pro', 6, 16, 'Вот шесть, что ненавидит Господь, даже семь, что мерзость душе Его:');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24928, 'rst66', 'pro', 6, 17, 'глаза гордые, язык лживый; и руки, проливающие кровь невинную,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24929, 'rst66', 'pro', 6, 18, 'сердце, кующее злые замыслы, ноги, быстро бегущие к злодейству,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24930, 'rst66', 'pro', 6, 19, 'лжесвидетель, наговаривающий ложь и сеющий раздор между братьями.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24931, 'rst66', 'pro', 6, 20, 'Сын мой! храни заповедь отца твоего и не отвергай наставления матери твоей;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24932, 'rst66', 'pro', 6, 21, 'навяжи их навсегда на сердце твое, обвяжи ими шею твою.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24933, 'rst66', 'pro', 6, 22, 'Когда ты пойдешь, они будут руководить тебя; когда ляжешь спать, будут охранять тебя; когда пробудишься, будут беседовать с тобою:');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24934, 'rst66', 'pro', 6, 23, 'ибо заповедь есть светильник, и наставление &mdash; свет, и назидательные поучения &mdash; путь к жизни,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24935, 'rst66', 'pro', 6, 24, 'чтобы остерегать тебя от негодной женщины, от льстивого языка чужой.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24936, 'rst66', 'pro', 6, 25, 'Не пожелай красоты ее в сердце твоем, и да не увлечет она тебя ресницами своими;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24937, 'rst66', 'pro', 6, 26, 'потому что из‐за жены блудной <i>обнищевают</i> до куска хлеба, а замужняя жена уловляет дорогую душу.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24938, 'rst66', 'pro', 6, 27, 'Может ли кто взять себе огонь в пазуху, чтобы не прогорело платье его?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24939, 'rst66', 'pro', 6, 28, 'Может ли кто ходить по горящим угольям, чтобы не обжечь ног своих?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24940, 'rst66', 'pro', 6, 29, 'То же бывает и с тем, кто входит к жене ближнего своего: кто прикоснется к ней, не останется без вины.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24941, 'rst66', 'pro', 6, 30, 'Не спускают вору, если он крадет, чтобы насытить душу свою, когда он голоден;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24942, 'rst66', 'pro', 6, 31, 'но, будучи пойман, он заплатит всемеро, отдаст все имущество дома своего.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24943, 'rst66', 'pro', 6, 32, 'Кто же прелюбодействует с женщиною, у того нет ума; тот губит душу свою, кто делает это:');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24944, 'rst66', 'pro', 6, 33, 'побои и позор найдет он, и бесчестие его не изгладится,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24945, 'rst66', 'pro', 6, 34, 'потому что ревность &mdash; ярость мужа, и не пощадит он в день мщения,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24946, 'rst66', 'pro', 6, 35, 'не примет никакого выкупа и не удовольствуется, сколько бы ты ни умножал даров.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24947, 'rst66', 'pro', 7, 1, 'Сын мой! храни слова мои и заповеди мои сокрой у себя.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24948, 'rst66', 'pro', 7, 2, 'Храни заповеди мои, и живи, и учение мое, как зрачок глаз твоих.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24949, 'rst66', 'pro', 7, 3, 'Навяжи их на персты твои, напиши их на скрижали сердца твоего.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24950, 'rst66', 'pro', 7, 4, 'Скажи мудрости: «Ты сестра моя!» и разум назови родным твоим,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24951, 'rst66', 'pro', 7, 5, 'чтобы они охраняли тебя от жены другого, от чужой, которая умягчает слова свои.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24952, 'rst66', 'pro', 7, 6, 'Вот, однажды смотрел я в окно дома моего, сквозь решетку мою,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24953, 'rst66', 'pro', 7, 7, 'и увидел среди неопытных, заметил между молодыми людьми неразумного юношу,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24954, 'rst66', 'pro', 7, 8, 'переходившего площадь близ угла ее и шедшего по дороге к дому ее,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24955, 'rst66', 'pro', 7, 9, 'в сумерки, в вечер дня, в ночной темноте и во мраке.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24956, 'rst66', 'pro', 7, 10, 'И вот &mdash; навстречу к нему женщина, в наряде блудницы, с коварным сердцем,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24957, 'rst66', 'pro', 7, 11, 'шумливая и необузданная; ноги ее не живут в доме ее:');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24958, 'rst66', 'pro', 7, 12, 'то на улице, то на площадях, и у каждого угла строит она ковы.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24959, 'rst66', 'pro', 7, 13, 'Она схватила его, целовала его, и с бесстыдным лицом говорила ему:');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24960, 'rst66', 'pro', 7, 14, '«Мирная жертва у меня: сегодня я совершила обеты мои;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24961, 'rst66', 'pro', 7, 15, 'поэтому и вышла навстречу тебе, чтобы отыскать тебя, и &mdash; нашла тебя;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24962, 'rst66', 'pro', 7, 16, 'коврами я убрала постель мою, разноцветными тканями Египетскими;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24963, 'rst66', 'pro', 7, 17, 'спальню мою надушила смирною, алоем и корицею;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24964, 'rst66', 'pro', 7, 18, 'зайди, будем упиваться нежностями до утра, насладимся любовью,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24965, 'rst66', 'pro', 7, 19, 'потому что мужа нет дома: он отправился в дальнюю дорогу;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24966, 'rst66', 'pro', 7, 20, 'кошелек серебра взял с собою; придет домой ко дню полнолуния».');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24967, 'rst66', 'pro', 7, 21, 'Множеством ласковых слов она увлекла его, мягкостью уст своих овладела им.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24968, 'rst66', 'pro', 7, 22, 'Тотчас он пошел за нею, как вол идет на убой, и как олень &mdash; на выстрел,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24969, 'rst66', 'pro', 7, 23, 'доколе стрела не пронзит печени его; как птичка кидается в силки и не знает, что они &mdash; на погибель ее.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24970, 'rst66', 'pro', 7, 24, 'Итак, дети, слушайте меня и внимайте словам уст моих.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24971, 'rst66', 'pro', 7, 25, 'Да не уклоняется сердце твое на пути ее, не блуждай по стезям ее,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24972, 'rst66', 'pro', 7, 26, 'потому что многих повергла она ранеными и много сильных убиты ею:');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24973, 'rst66', 'pro', 7, 27, 'дом ее &mdash; пути в преисподнюю, нисходящие во внутренние жилища смерти.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24974, 'rst66', 'pro', 8, 1, 'Не премудрость ли взывает? и не разум ли возвышает голос свой?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24975, 'rst66', 'pro', 8, 2, 'Она становится на возвышенных местах, при дороге, на распутиях;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24976, 'rst66', 'pro', 8, 3, 'она взывает у ворот при входе в город, при входе в двери:');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24977, 'rst66', 'pro', 8, 4, '«К вам, люди, взываю я, и к сынам человеческим голос мой!');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24978, 'rst66', 'pro', 8, 5, 'Научитесь, неразумные, благоразумию, и глупые &mdash; разуму.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24979, 'rst66', 'pro', 8, 6, 'Слушайте, потому что я буду говорить важное, и изречение уст моих &mdash; правда;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24980, 'rst66', 'pro', 8, 7, 'ибо истину произнесет язык мой, и нечестие &mdash; мерзость для уст моих;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24981, 'rst66', 'pro', 8, 8, 'все слова уст моих справедливы; нет в них коварства и лукавства;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24982, 'rst66', 'pro', 8, 9, 'все они ясны для разумного и справедливы для приобретших знание.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24983, 'rst66', 'pro', 8, 10, 'Примите учение мое, а не серебро; лучше знание, нежели отборное золото;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24984, 'rst66', 'pro', 8, 11, 'потому что мудрость лучше жемчуга, и ничто из желаемого не сравнится с нею.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24985, 'rst66', 'pro', 8, 12, 'Я, премудрость, обитаю с разумом и ищу рассудительного знания.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24986, 'rst66', 'pro', 8, 13, 'Страх Господень &mdash; ненавидеть зло; гордость и высокомерие и злой путь и коварные уста я ненавижу.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24987, 'rst66', 'pro', 8, 14, 'У меня совет и правда; я &mdash; разум, у меня сила.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24988, 'rst66', 'pro', 8, 15, 'Мною цари царствуют и повелители узаконяют правду;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24989, 'rst66', 'pro', 8, 16, 'мною начальствуют начальники и вельможи и все судьи земли.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24990, 'rst66', 'pro', 8, 17, 'Любящих меня я люблю, и ищущие меня найдут меня;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24991, 'rst66', 'pro', 8, 18, 'богатство и слава у меня, сокровище непогибающее и правда;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24992, 'rst66', 'pro', 8, 19, 'плоды мои лучше золота, и золота самого чистого, и пользы от меня больше, нежели от отборного серебра.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24993, 'rst66', 'pro', 8, 20, 'Я хожу по пути правды, по стезям правосудия,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24994, 'rst66', 'pro', 8, 21, 'чтобы доставить любящим меня существенное благо, и сокровищницы их я наполняю.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24995, 'rst66', 'pro', 8, 22, 'Господь имел меня началом пути Своего, прежде созданий Своих, искони;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24996, 'rst66', 'pro', 8, 23, 'от века я помазана, от начала, прежде бытия земли.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24997, 'rst66', 'pro', 8, 24, 'Я родилась, когда еще не существовали бездны, когда еще не было источников, обильных водою.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24998, 'rst66', 'pro', 8, 25, 'Я родилась прежде, нежели водружены были горы, прежде холмов,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (24999, 'rst66', 'pro', 8, 26, 'когда еще Он не сотворил ни земли, ни полей, ни начальных пылинок вселенной.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25000, 'rst66', 'pro', 8, 27, 'Когда Он уготовлял небеса, <i>я была</i> там. Когда Он проводил круговую черту по лицу бездны,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25001, 'rst66', 'pro', 8, 28, 'когда утверждал вверху облака, когда укреплял источники бездны,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25002, 'rst66', 'pro', 8, 29, 'когда давал морю устав, чтобы воды не переступали пределов его, когда полагал основания земли:');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25003, 'rst66', 'pro', 8, 30, 'тогда я была при Нем художницею, и была радостью всякий день, веселясь пред лицом Его во все время,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25004, 'rst66', 'pro', 8, 31, 'веселясь на земном кругу Его, и радость моя <i>была</i> с сынами человеческими.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25005, 'rst66', 'pro', 8, 32, 'Итак, дети, послушайте меня; и блаженны те, которые хранят пути мои!');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25006, 'rst66', 'pro', 8, 33, 'Послушайте наставления и будьте мудры, и не отступайте <i>от него</i>.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25007, 'rst66', 'pro', 8, 34, 'Блажен человек, который слушает меня, бодрствуя каждый день у ворот моих и стоя на страже у дверей моих!');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25008, 'rst66', 'pro', 8, 35, 'Потому что, кто нашел меня, тот нашел жизнь и получит благодать от Господа;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25009, 'rst66', 'pro', 8, 36, 'а согрешающий против меня наносит вред душе своей: все, ненавидящие меня, любят смерть».');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25010, 'rst66', 'pro', 9, 1, 'Премудрость построила себе дом, вытесала семь столбов его,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25011, 'rst66', 'pro', 9, 2, 'заколола жертву, растворила вино свое и приготовила у себя трапезу;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25012, 'rst66', 'pro', 9, 3, 'послала слуг своих провозгласить с возвышенностей городских:');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25013, 'rst66', 'pro', 9, 4, '«Кто неразумен, обратись сюда!» И скудоумному она сказала:');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25014, 'rst66', 'pro', 9, 5, '«Идите, ешьте хлеб мой и пейте вино, мною растворенное;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25015, 'rst66', 'pro', 9, 6, 'оставьте неразумие, и живите, и ходите путем разума».');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25016, 'rst66', 'pro', 9, 7, 'Поучающий кощунника наживет себе бесславие, и обличающий нечестивого &mdash; пятно себе.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25017, 'rst66', 'pro', 9, 8, 'Не обличай кощунника, чтобы он не возненавидел тебя; обличай мудрого, и он возлюбит тебя;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25018, 'rst66', 'pro', 9, 9, 'дай <i>наставление</i> мудрому, и он будет еще мудрее; научи правдивого, и он приумножит знание.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25019, 'rst66', 'pro', 9, 10, 'Начало мудрости &mdash; страх Господень, и познание Святого &mdash; разум;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25020, 'rst66', 'pro', 9, 11, 'потому что чрез меня умножатся дни твои, и прибавится тебе лет жизни.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25021, 'rst66', 'pro', 9, 12, 'Если ты мудр, то мудр для себя; и если буен, то один потерпишь.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25022, 'rst66', 'pro', 9, 13, 'Женщина безрассудная, шумливая, глупая и ничего не знающая');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25023, 'rst66', 'pro', 9, 14, 'садится у дверей дома своего на стуле, на возвышенных местах города,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25024, 'rst66', 'pro', 9, 15, 'чтобы звать проходящих дорогою, идущих прямо своими путями:');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25025, 'rst66', 'pro', 9, 16, '«Кто глуп, обратись сюда!» И скудоумному сказала она:');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25026, 'rst66', 'pro', 9, 17, '«Воды краденые сладки, и утаенный хлеб приятен».');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25027, 'rst66', 'pro', 9, 18, 'И он не знает, что мертвецы там и что в глубине преисподней зазванные ею.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25028, 'rst66', 'pro', 10, 1, 'Притчи Соломона. Сын мудрый радует отца, а сын глупый &mdash; огорчение для его матери.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25029, 'rst66', 'pro', 10, 2, 'Не доставляют пользы сокровища неправедные, правда же избавляет от смерти.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25030, 'rst66', 'pro', 10, 3, 'Не допустит Господь терпеть голод душе праведного, стяжание же нечестивых исторгнет.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25031, 'rst66', 'pro', 10, 4, 'Ленивая рука делает бедным, а рука прилежных обогащает.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25032, 'rst66', 'pro', 10, 5, 'Собирающий во время лета &mdash; сын разумный, спящий же во время жатвы &mdash; сын беспутный.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25033, 'rst66', 'pro', 10, 6, 'Благословения &mdash; на голове праведника, уста же беззаконных заградит насилие.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25034, 'rst66', 'pro', 10, 7, 'Память праведника пребудет благословенна, а имя нечестивых омерзеет.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25035, 'rst66', 'pro', 10, 8, 'Мудрый сердцем принимает заповеди, а глупый устами преткнется.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25036, 'rst66', 'pro', 10, 9, 'Кто ходит в непорочности, тот ходит безопасно; а кто превращает пути свои, тот будет наказан.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25037, 'rst66', 'pro', 10, 10, 'Кто мигает глазами, тот причиняет досаду, а глупый устами преткнется.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25038, 'rst66', 'pro', 10, 11, 'Уста праведника &mdash; источник жизни, уста же беззаконных заградит насилие.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25039, 'rst66', 'pro', 10, 12, 'Ненависть возбуждает раздоры, но любовь покрывает все грехи.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25040, 'rst66', 'pro', 10, 13, 'В устах разумного находится мудрость, но на теле глупого &mdash; розга.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25041, 'rst66', 'pro', 10, 14, 'Мудрые сберегают знание, но уста глупого &mdash; близкая погибель.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25042, 'rst66', 'pro', 10, 15, 'Имущество богатого &mdash; крепкий город его, беда для бедных &mdash; скудость их.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25043, 'rst66', 'pro', 10, 16, 'Труды праведного &mdash; к жизни, успех нечестивого &mdash; ко греху.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25044, 'rst66', 'pro', 10, 17, 'Кто хранит наставление, тот на пути к жизни; а отвергающий обличение &mdash; блуждает.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25045, 'rst66', 'pro', 10, 18, 'Кто скрывает ненависть, у того уста лживые; и кто разглашает клевету, тот глуп.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25046, 'rst66', 'pro', 10, 19, 'При многословии не миновать греха, а сдерживающий уста свои &mdash; разумен.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25047, 'rst66', 'pro', 10, 20, 'Отборное серебро &mdash; язык праведного, сердце же нечестивых &mdash; ничтожество.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25048, 'rst66', 'pro', 10, 21, 'Уста праведного пасут многих, а глупые умирают от недостатка разума.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25049, 'rst66', 'pro', 10, 22, 'Благословение Господне &mdash; оно обогащает и печали с собою не приносит.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25050, 'rst66', 'pro', 10, 23, 'Для глупого преступное деяние как бы забава, а человеку разумному свойственна мудрость.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25051, 'rst66', 'pro', 10, 24, 'Чего страшится нечестивый, то и постигнет его, а желание праведников исполнится.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25052, 'rst66', 'pro', 10, 25, 'Как проносится вихрь, <i>так</i> нет более нечестивого; а праведник &mdash; на вечном основании.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25053, 'rst66', 'pro', 10, 26, 'Что уксус для зубов и дым для глаз, то ленивый для посылающих его.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25054, 'rst66', 'pro', 10, 27, 'Страх Господень прибавляет дней, лета же нечестивых сократятся.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25055, 'rst66', 'pro', 10, 28, 'Ожидание праведников &mdash; радость, а надежда нечестивых погибнет.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25056, 'rst66', 'pro', 10, 29, 'Путь Господень &mdash; твердыня для непорочного и страх для делающих беззаконие.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25057, 'rst66', 'pro', 10, 30, 'Праведник вовеки не поколеблется, нечестивые же не поживут на земле.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25058, 'rst66', 'pro', 10, 31, 'Уста праведника источают мудрость, а язык зловредный отсечется.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25059, 'rst66', 'pro', 10, 32, 'Уста праведного знают благоприятное, а уста нечестивых &mdash; развращенное.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25060, 'rst66', 'pro', 11, 1, 'Неверные весы &mdash; мерзость пред Господом, но правильный вес угоден Ему.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25061, 'rst66', 'pro', 11, 2, 'Придет гордость, придет и посрамление; но со смиренными &mdash; мудрость.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25062, 'rst66', 'pro', 11, 3, 'Непорочность прямодушных будет руководить их, а лукавство коварных погубит их.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25063, 'rst66', 'pro', 11, 4, 'Не поможет богатство в день гнева, правда же спасет от смерти.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25064, 'rst66', 'pro', 11, 5, 'Правда непорочного уравнивает путь его, а нечестивый падет от нечестия своего.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25065, 'rst66', 'pro', 11, 6, 'Правда прямодушных спасет их, а беззаконники будут уловлены беззаконием своим.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25066, 'rst66', 'pro', 11, 7, 'Со смертью человека нечестивого исчезает надежда, и ожидание беззаконных погибает.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25067, 'rst66', 'pro', 11, 8, 'Праведник спасается от беды, а вместо него попадает <i>в нее</i> нечестивый.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25068, 'rst66', 'pro', 11, 9, 'Устами лицемер губит ближнего своего, но праведники прозорливостью спасаются.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25069, 'rst66', 'pro', 11, 10, 'При благоденствии праведников веселится город, и при погибели нечестивых <i>бывает</i> торжество.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25070, 'rst66', 'pro', 11, 11, 'Благословением праведных возвышается город, а устами нечестивых разрушается.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25071, 'rst66', 'pro', 11, 12, 'Скудоумный высказывает презрение к ближнему своему; но разумный человек молчит.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25072, 'rst66', 'pro', 11, 13, 'Кто ходит переносчиком, тот открывает тайну; но верный человек таит дело.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25073, 'rst66', 'pro', 11, 14, 'При недостатке попечения падает народ, а при многих советниках благоденствует.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25074, 'rst66', 'pro', 11, 15, 'Зло причиняет себе, кто ручается за постороннего; а кто ненавидит ручательство, тот безопасен.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25075, 'rst66', 'pro', 11, 16, 'Благонравная жена приобретает славу, а трудолюбивые приобретают богатство.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25076, 'rst66', 'pro', 11, 17, 'Человек милосердый благотворит душе своей, а жестокосердый разрушает плоть свою.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25077, 'rst66', 'pro', 11, 18, 'Нечестивый делает дело ненадежное, а сеющему правду &mdash; награда верная.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25078, 'rst66', 'pro', 11, 19, 'Праведность <i>ведет</i> к жизни, а стремящийся к злу <i>стремится</i> к смерти своей.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25079, 'rst66', 'pro', 11, 20, 'Мерзость пред Господом &mdash; коварные сердцем; но благоугодны Ему непорочные в пути.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25080, 'rst66', 'pro', 11, 21, 'Можно поручиться, что порочный не останется ненаказанным; семя же праведных спасется.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25081, 'rst66', 'pro', 11, 22, 'Что золотое кольцо в носу у свиньи, то женщина красивая и &mdash; безрассудная.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25082, 'rst66', 'pro', 11, 23, 'Желание праведных <i>есть</i> одно добро, ожидание нечестивых &mdash; гнев.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25083, 'rst66', 'pro', 11, 24, 'Иной сыплет щедро, и <i>ему</i> еще прибавляется; а другой сверх меры бережлив, и, однако же, беднеет.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25084, 'rst66', 'pro', 11, 25, 'Благотворительная душа будет насыщена, и кто напояет <i>других</i>, тот и сам напоен будет.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25085, 'rst66', 'pro', 11, 26, 'Кто удерживает у себя хлеб, того клянет народ; а на голове продающего &mdash; благословение.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25086, 'rst66', 'pro', 11, 27, 'Кто стремится к добру, тот ищет благоволения; а кто ищет зла, к тому оно и приходит.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25087, 'rst66', 'pro', 11, 28, 'Надеющийся на богатство свое упадет; а праведники, как лист, будут зеленеть.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25088, 'rst66', 'pro', 11, 29, 'Расстроивающий дом свой получит в удел ветер, и глупый будет рабом мудрого сердцем.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25089, 'rst66', 'pro', 11, 30, 'Плод праведника &mdash; древо жизни, и мудрый привлекает души.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25090, 'rst66', 'pro', 11, 31, 'Так праведнику воздается на земле, тем паче нечестивому и грешнику.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25091, 'rst66', 'pro', 12, 1, 'Кто любит наставление, тот любит знание; а кто ненавидит обличение, тот невежда.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25092, 'rst66', 'pro', 12, 2, 'Добрый приобретает благоволение от Господа; а человека коварного Он осудит.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25093, 'rst66', 'pro', 12, 3, 'Не утвердит себя человек беззаконием; корень же праведников неподвижен.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25094, 'rst66', 'pro', 12, 4, 'Добродетельная жена &mdash; венец для мужа своего; а позорная &mdash; как гниль в костях его.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25095, 'rst66', 'pro', 12, 5, 'Помышления праведных &mdash; правда, а замыслы нечестивых &mdash; коварство.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25096, 'rst66', 'pro', 12, 6, 'Речи нечестивых &mdash; засада для пролития крови, уста же праведных спасают их.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25097, 'rst66', 'pro', 12, 7, 'Коснись нечестивых несчастие &mdash; и нет их, а дом праведных стоит.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25098, 'rst66', 'pro', 12, 8, 'Хвалят человека по мере разума его, а развращенный сердцем будет в презрении.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25099, 'rst66', 'pro', 12, 9, 'Лучше простой, но работающий на себя, нежели выдающий себя за знатного, но нуждающийся в хлебе.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25100, 'rst66', 'pro', 12, 10, 'Праведный печется и о жизни скота своего, сердце же нечестивых жестоко.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25101, 'rst66', 'pro', 12, 11, 'Кто возделывает землю свою, тот будет насыщаться хлебом; а кто идет по следам празднолюбцев, тот скудоумен.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25102, 'rst66', 'pro', 12, 12, 'Нечестивый желает уловить в сеть зла; но корень праведных тверд.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25103, 'rst66', 'pro', 12, 13, 'Нечестивый уловляется грехами уст своих; но праведник выйдет из беды.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25104, 'rst66', 'pro', 12, 14, 'От плода уст <i>своих</i> человек насыщается добром, и воздаяние человеку &mdash; по делам рук его.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25105, 'rst66', 'pro', 12, 15, 'Путь глупого прямой в его глазах; но кто слушает совета, тот мудр.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25106, 'rst66', 'pro', 12, 16, 'У глупого тотчас же выкажется гнев его, а благоразумный скрывает оскорбление.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25107, 'rst66', 'pro', 12, 17, 'Кто говорит то, что знает, тот говорит правду; а у свидетеля ложного &mdash; обман.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25108, 'rst66', 'pro', 12, 18, 'Иной пустослов уязвляет как мечом, а язык мудрых &mdash; врачует.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25109, 'rst66', 'pro', 12, 19, 'Уста правдивые вечно пребывают, а лживый язык &mdash; только на мгновение.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25110, 'rst66', 'pro', 12, 20, 'Коварство &mdash; в сердце злоумышленников, радость &mdash; у миротворцев.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25111, 'rst66', 'pro', 12, 21, 'Не приключится праведнику никакого зла, нечестивые же будут преисполнены зол.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25112, 'rst66', 'pro', 12, 22, 'Мерзость пред Господом &mdash; уста лживые, а говорящие истину благоугодны Ему.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25113, 'rst66', 'pro', 12, 23, 'Человек рассудительный скрывает знание, а сердце глупых высказывает глупость.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25114, 'rst66', 'pro', 12, 24, 'Рука прилежных будет господствовать, а ленивая будет под данью.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25115, 'rst66', 'pro', 12, 25, 'Тоска на сердце человека подавляет его, а доброе слово развеселяет его.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25116, 'rst66', 'pro', 12, 26, 'Праведник указывает ближнему своему путь, а путь нечестивых вводит их в заблуждение.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25117, 'rst66', 'pro', 12, 27, 'Ленивый не жарит своей дичи; а имущество человека прилежного многоценно.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25118, 'rst66', 'pro', 12, 28, 'На пути правды &mdash; жизнь, и на стезе ее нет смерти.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25119, 'rst66', 'pro', 13, 1, 'Мудрый сын <i>слушает</i> наставление отца, а буйный не слушает обличения.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25120, 'rst66', 'pro', 13, 2, 'От плода уст <i>своих</i> человек вкусит добро, душа же законопреступников &mdash; зло.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25121, 'rst66', 'pro', 13, 3, 'Кто хранит уста свои, тот бережет душу свою; а кто широко раскрывает свой рот, тому беда.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25122, 'rst66', 'pro', 13, 4, 'Душа ленивого желает, но тщетно; а душа прилежных насытится.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25123, 'rst66', 'pro', 13, 5, 'Праведник ненавидит ложное слово, а нечестивый срамит и бесчестит <i>себя</i>.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25124, 'rst66', 'pro', 13, 6, 'Правда хранит непорочного в пути, а нечестие губит грешника.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25125, 'rst66', 'pro', 13, 7, 'Иной выдает себя за богатого, а у него ничего нет; другой выдает себя за бедного, а у него богатства много.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25126, 'rst66', 'pro', 13, 8, 'Богатством своим человек выкупает жизнь <i>свою</i>, а бедный и угрозы не слышит.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25127, 'rst66', 'pro', 13, 9, 'Свет праведных весело горит, светильник же нечестивых угасает.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25128, 'rst66', 'pro', 13, 10, 'От высокомерия происходит раздор, а у советующихся &mdash; мудрость.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25129, 'rst66', 'pro', 13, 11, 'Богатство от суетности истощается, а собирающий трудами умножает его.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25130, 'rst66', 'pro', 13, 12, 'Надежда, долго не сбывающаяся, томит сердце, а исполнившееся желание &mdash; <i>как</i> древо жизни.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25131, 'rst66', 'pro', 13, 13, 'Кто пренебрегает словом, тот причиняет вред себе; а кто боится заповеди, тому воздается.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25132, 'rst66', 'pro', 13, 14, 'Учение мудрого &mdash; источник жизни, удаляющий от сетей смерти.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25133, 'rst66', 'pro', 13, 15, 'Добрый разум доставляет приятность, путь же беззаконных жесток.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25134, 'rst66', 'pro', 13, 16, 'Всякий благоразумный действует с знанием, а глупый выставляет напоказ глупость.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25135, 'rst66', 'pro', 13, 17, 'Худой посол попадает в беду, а верный посланник &mdash; спасение.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25136, 'rst66', 'pro', 13, 18, 'Нищета и посрамление отвергающему учение; а кто соблюдает наставление, будет в чести.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25137, 'rst66', 'pro', 13, 19, 'Желание исполнившееся &mdash; приятно для души; но несносно для глупых уклоняться от зла.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25138, 'rst66', 'pro', 13, 20, 'Общающийся с мудрыми будет мудр, а кто дружит с глупыми, развратится.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25139, 'rst66', 'pro', 13, 21, 'Грешников преследует зло, а праведникам воздается добром.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25140, 'rst66', 'pro', 13, 22, 'Добрый оставляет наследство <i>и</i> внукам, а богатство грешника сберегается для праведного.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25141, 'rst66', 'pro', 13, 23, 'Много хлеба <i>бывает</i> и на ниве бедных; но некоторые гибнут от беспорядка.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25142, 'rst66', 'pro', 13, 24, 'Кто жалеет розги своей, тот ненавидит сына; а кто любит, тот с детства наказывает его.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25143, 'rst66', 'pro', 13, 25, 'Праведник ест до сытости, а чрево беззаконных терпит лишение.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25144, 'rst66', 'pro', 14, 1, 'Мудрая жена устроит дом свой, а глупая разрушит его своими руками.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25145, 'rst66', 'pro', 14, 2, 'Идущий прямым путем боится Господа; но чьи пути кривы, тот небрежет о Нем.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25146, 'rst66', 'pro', 14, 3, 'В устах глупого &mdash; бич гордости; уста же мудрых охраняют их.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25147, 'rst66', 'pro', 14, 4, 'Где нет волов, <i>там</i> ясли пусты; а много прибыли от силы волов.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25148, 'rst66', 'pro', 14, 5, 'Верный свидетель не лжет, а свидетель ложный наговорит много лжи.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25149, 'rst66', 'pro', 14, 6, 'Распутный ищет мудрости, и не находит; а для разумного знание легко.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25150, 'rst66', 'pro', 14, 7, 'Отойди от человека глупого, у которого ты не замечаешь разумных уст.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25151, 'rst66', 'pro', 14, 8, 'Мудрость разумного &mdash; знание пути своего, глупость же безрассудных &mdash; заблуждение.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25152, 'rst66', 'pro', 14, 9, 'Глупые смеются над грехом, а посреди праведных &mdash; благоволение.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25153, 'rst66', 'pro', 14, 10, 'Сердце знает горе души своей, и в радость его не вмешается чужой.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25154, 'rst66', 'pro', 14, 11, 'Дом беззаконных разорится, а жилище праведных процветет.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25155, 'rst66', 'pro', 14, 12, 'Есть пути, которые кажутся человеку прямыми; но конец их &mdash; путь к смерти.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25156, 'rst66', 'pro', 14, 13, 'И при смехе <i>иногда</i> болит сердце, и концом радости бывает печаль.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25157, 'rst66', 'pro', 14, 14, 'Человек с развращенным сердцем насытится от путей своих, и добрый &mdash; от своих.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25158, 'rst66', 'pro', 14, 15, 'Глупый верит всякому слову, благоразумный же внимателен к путям своим.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25159, 'rst66', 'pro', 14, 16, 'Мудрый боится и удаляется от зла, а глупый раздражителен и самонадеян.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25160, 'rst66', 'pro', 14, 17, 'Вспыльчивый может сделать глупость; но человек, умышленно делающий зло, ненавистен.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25161, 'rst66', 'pro', 14, 18, 'Невежды получают в удел себе глупость, а благоразумные увенчаются знанием.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25162, 'rst66', 'pro', 14, 19, 'Преклонятся злые пред добрыми и нечестивые &mdash; у ворот праведника.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25163, 'rst66', 'pro', 14, 20, 'Бедный ненавидим бывает даже близким своим, а у богатого много друзей.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25164, 'rst66', 'pro', 14, 21, 'Кто презирает ближнего своего, тот грешит; а кто милосерд к бедным, тот блажен.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25165, 'rst66', 'pro', 14, 22, 'Не заблуждаются ли умышляющие зло? Но милость и верность у благомыслящих.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25166, 'rst66', 'pro', 14, 23, 'От всякого труда есть прибыль, а от пустословия только ущерб.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25167, 'rst66', 'pro', 14, 24, 'Венец мудрых &mdash; богатство их, а глупость невежд глупость <i>и есть</i>.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25168, 'rst66', 'pro', 14, 25, 'Верный свидетель спасает души, а лживый наговорит много лжи.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25169, 'rst66', 'pro', 14, 26, 'В страхе пред Господом &mdash; надежда твердая, и сынам Своим Он прибежище.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25170, 'rst66', 'pro', 14, 27, 'Страх Господень &mdash; источник жизни, удаляющий от сетей смерти.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25171, 'rst66', 'pro', 14, 28, 'Во множестве народа &mdash; величие царя, а при малолюдстве народа беда государю.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25172, 'rst66', 'pro', 14, 29, 'У терпеливого человека много разума, а раздражительный выказывает глупость.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25173, 'rst66', 'pro', 14, 30, 'Кроткое сердце &mdash; жизнь для тела, а зависть &mdash; гниль для костей.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25174, 'rst66', 'pro', 14, 31, 'Кто теснит бедного, тот хулит Творца его; чтущий же Его благотворит нуждающемуся.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25175, 'rst66', 'pro', 14, 32, 'За зло свое нечестивый будет отвергнут, а праведный и при смерти своей имеет надежду.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25176, 'rst66', 'pro', 14, 33, 'Мудрость почиет в сердце разумного, и среди глупых дает знать о себе.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25177, 'rst66', 'pro', 14, 34, 'Праведность возвышает народ, а беззаконие &mdash; бесчестие народов.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25178, 'rst66', 'pro', 14, 35, 'Благоволение царя &mdash; к рабу разумному, а гнев его &mdash; против того, кто позорит его.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25179, 'rst66', 'pro', 15, 1, 'Кроткий ответ отвращает гнев, а оскорбительное слово возбуждает ярость.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25180, 'rst66', 'pro', 15, 2, 'Язык мудрых сообщает добрые знания, а уста глупых изрыгают глупость.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25181, 'rst66', 'pro', 15, 3, 'На всяком месте очи Господни: они видят злых и добрых.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25182, 'rst66', 'pro', 15, 4, 'Кроткий язык &mdash; древо жизни, но необузданный &mdash; сокрушение духа.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25183, 'rst66', 'pro', 15, 5, 'Глупый пренебрегает наставлением отца своего; а кто внимает обличениям, тот благоразумен.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25184, 'rst66', 'pro', 15, 6, 'В доме праведника &mdash; обилие сокровищ, а в прибытке нечестивого &mdash; расстройство.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25185, 'rst66', 'pro', 15, 7, 'Уста мудрых распространяют знание, а сердце глупых не так.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25186, 'rst66', 'pro', 15, 8, 'Жертва нечестивых &mdash; мерзость пред Господом, а молитва праведных благоугодна Ему.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25187, 'rst66', 'pro', 15, 9, 'Мерзость пред Господом &mdash; путь нечестивого, а идущего путем правды Он любит.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25188, 'rst66', 'pro', 15, 10, 'Злое наказание &mdash; уклоняющемуся от пути, и ненавидящий обличение погибнет.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25189, 'rst66', 'pro', 15, 11, 'Преисподняя и Аваддон <i>открыты</i> пред Господом, тем более &mdash; сердца сынов человеческих.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25190, 'rst66', 'pro', 15, 12, 'Не любит распутный обличающих его, и к мудрым не пойдет.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25191, 'rst66', 'pro', 15, 13, 'Веселое сердце делает лицо веселым, а при сердечной скорби дух унывает.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25192, 'rst66', 'pro', 15, 14, 'Сердце разумного ищет знания, уста же глупых питаются глупостью.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25193, 'rst66', 'pro', 15, 15, 'Все дни несчастного печальны; а у кого сердце весело, у того всегда пир.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25194, 'rst66', 'pro', 15, 16, 'Лучше немногое при страхе Господнем, нежели большое сокровище, и при нем тревога.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25195, 'rst66', 'pro', 15, 17, 'Лучше блюдо зелени, и при нем любовь, нежели откормленный бык, и при нем ненависть.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25196, 'rst66', 'pro', 15, 18, 'Вспыльчивый человек возбуждает раздор, а терпеливый утишает распрю.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25197, 'rst66', 'pro', 15, 19, 'Путь ленивого &mdash; как терновый плетень, а путь праведных &mdash; гладкий.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25198, 'rst66', 'pro', 15, 20, 'Мудрый сын радует отца, а глупый человек пренебрегает мать свою.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25199, 'rst66', 'pro', 15, 21, 'Глупость &mdash; радость для малоумного, а человек разумный идет прямою дорогою.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25200, 'rst66', 'pro', 15, 22, 'Без совета предприятия расстроятся, а при множестве советников они состоятся.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25201, 'rst66', 'pro', 15, 23, 'Радость человеку в ответе уст его, и как хорошо слово вовремя!');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25202, 'rst66', 'pro', 15, 24, 'Путь жизни мудрого вверх, чтобы уклониться от преисподней внизу.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25203, 'rst66', 'pro', 15, 25, 'Дом надменных разорит Господь, а межу вдовы укрепит.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25204, 'rst66', 'pro', 15, 26, 'Мерзость пред Господом &mdash; помышления злых, слова же непорочных угодны Ему.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25205, 'rst66', 'pro', 15, 27, 'Корыстолюбивый расстроит дом свой, а ненавидящий подарки будет жить.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25206, 'rst66', 'pro', 15, 28, 'Сердце праведного обдумывает ответ, а уста нечестивых изрыгают зло.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25207, 'rst66', 'pro', 15, 29, 'Далек Господь от нечестивых, а молитву праведников слышит.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25208, 'rst66', 'pro', 15, 30, 'Светлый взгляд радует сердце, добрая весть утучняет кости.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25209, 'rst66', 'pro', 15, 31, 'Ухо, внимательное к учению жизни, пребывает между мудрыми.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25210, 'rst66', 'pro', 15, 32, 'Отвергающий наставление не радеет о своей душе; а кто внимает обличению, тот приобретает разум.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25211, 'rst66', 'pro', 15, 33, 'Страх Господень научает мудрости, и славе предшествует смирение.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25212, 'rst66', 'pro', 16, 1, 'Человеку <i>принадлежат</i> предположения сердца, но от Господа ответ языка.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25213, 'rst66', 'pro', 16, 2, 'Все пути человека чисты в его глазах, но Господь взвешивает души.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25214, 'rst66', 'pro', 16, 3, 'Предай Господу дела твои, и предприятия твои совершатся.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25215, 'rst66', 'pro', 16, 4, 'Все сделал Господь ради Себя; и даже нечестивого <i>блюдет</i> на день бедствия.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25216, 'rst66', 'pro', 16, 5, 'Мерзость пред Господом всякий надменный сердцем; можно поручиться, что он не останется ненаказанным.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25217, 'rst66', 'pro', 16, 6, 'Милосердием и правдою очищается грех, и страх Господень отводит от зла.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25218, 'rst66', 'pro', 16, 7, 'Когда Господу угодны пути человека, Он и врагов его примиряет с ним.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25219, 'rst66', 'pro', 16, 8, 'Лучше немногое с правдою, нежели множество прибытков с неправдою.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25220, 'rst66', 'pro', 16, 9, 'Сердце человека обдумывает свой путь, но Господь управляет шествием его.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25221, 'rst66', 'pro', 16, 10, 'В устах царя &mdash; слово вдохновенное; уста его не должны погрешать на суде.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25222, 'rst66', 'pro', 16, 11, 'Верные весы и весовые чаши &mdash; от Господа; от Него же все гири в суме.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25223, 'rst66', 'pro', 16, 12, 'Мерзость для царей &mdash; дело беззаконное, потому что правдою утверждается престол.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25224, 'rst66', 'pro', 16, 13, 'Приятны царю уста правдивые, и говорящего истину он любит.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25225, 'rst66', 'pro', 16, 14, 'Царский гнев &mdash; вестник смерти; но мудрый человек умилостивит его.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25226, 'rst66', 'pro', 16, 15, 'В светлом взоре царя &mdash; жизнь, и благоволение его &mdash; как облако с поздним дождем.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25227, 'rst66', 'pro', 16, 16, 'Приобретение мудрости гораздо лучше золота, и приобретение разума предпочтительнее отборного серебра.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25228, 'rst66', 'pro', 16, 17, 'Путь праведных &mdash; уклонение от зла: тот бережет душу свою, кто хранит путь свой.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25229, 'rst66', 'pro', 16, 18, 'Погибели предшествует гордость, и падению &mdash; надменность.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25230, 'rst66', 'pro', 16, 19, 'Лучше смиряться духом с кроткими, нежели разделять добычу с гордыми.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25231, 'rst66', 'pro', 16, 20, 'Кто ведет дело разумно, тот найдет благо, и кто надеется на Господа, тот блажен.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25232, 'rst66', 'pro', 16, 21, 'Мудрый сердцем прозовется благоразумным, и сладкая речь прибавит к учению.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25233, 'rst66', 'pro', 16, 22, 'Разум для имеющих его &mdash; источник жизни, а ученость глупых &mdash; глупость.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25234, 'rst66', 'pro', 16, 23, 'Сердце мудрого делает язык его мудрым и умножает знание в устах его.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25235, 'rst66', 'pro', 16, 24, 'Приятная речь &mdash; сотовый мед, сладка для души и целебна для костей.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25236, 'rst66', 'pro', 16, 25, 'Есть пути, которые кажутся человеку прямыми, но конец их &mdash; путь к смерти.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25237, 'rst66', 'pro', 16, 26, 'Трудящийся трудится для себя, потому что понуждает его <i>к тому</i> рот его.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25238, 'rst66', 'pro', 16, 27, 'Человек лукавый замышляет зло, и на устах его как бы огонь палящий.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25239, 'rst66', 'pro', 16, 28, 'Человек коварный сеет раздор, и наушник разлучает друзей.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25240, 'rst66', 'pro', 16, 29, 'Человек неблагонамеренный развращает ближнего своего и ведет его на путь недобрый;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25241, 'rst66', 'pro', 16, 30, 'прищуривает глаза свои, чтобы придумать коварство; закусывая себе губы, совершает злодейство.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25242, 'rst66', 'pro', 16, 31, 'Венец славы &mdash; седина, которая находится на пути правды.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25243, 'rst66', 'pro', 16, 32, 'Долготерпеливый лучше храброго, и владеющий собою <i>лучше</i> завоевателя города.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25244, 'rst66', 'pro', 16, 33, 'В по&#769;лу бросается жребий, но все решение его &mdash; от Господа.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25245, 'rst66', 'pro', 17, 1, 'Лучше кусок сухого хлеба, и с ним мир, нежели дом, полный заколотого скота, с раздором.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25246, 'rst66', 'pro', 17, 2, 'Разумный раб господствует над беспутным сыном и между братьями разделит наследство.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25247, 'rst66', 'pro', 17, 3, 'Плавильня &mdash; для серебра, и горнило &mdash; для золота, а сердца испытывает Господь.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25248, 'rst66', 'pro', 17, 4, 'Злодей внимает устам беззаконным, лжец слушается языка пагубного.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25249, 'rst66', 'pro', 17, 5, 'Кто ругается над нищим, тот хулит Творца его; кто радуется несчастью, тот не останется ненаказанным.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25250, 'rst66', 'pro', 17, 6, 'Венец стариков &mdash; сыновья сыновей, и слава детей &mdash; родители их.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25251, 'rst66', 'pro', 17, 7, 'Неприлична глупому важная речь, тем паче знатному &mdash; уста лживые.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25252, 'rst66', 'pro', 17, 8, 'Подарок &mdash; драгоценный камень в глазах владеющего им: куда ни обратится он, успеет.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25253, 'rst66', 'pro', 17, 9, 'Прикрывающий проступок ищет любви; а кто снова напоминает о нем, тот удаляет друга.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25254, 'rst66', 'pro', 17, 10, 'На разумного сильнее действует выговор, нежели на глупого сто ударов.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25255, 'rst66', 'pro', 17, 11, 'Возмутитель ищет только зла; поэтому жестокий ангел будет послан против него.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25256, 'rst66', 'pro', 17, 12, 'Лучше встретить человеку медведицу, лишенную детей, нежели глупца с его глупостью.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25257, 'rst66', 'pro', 17, 13, 'Кто за добро воздает злом, от дома того не отойдет зло.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25258, 'rst66', 'pro', 17, 14, 'Начало ссоры &mdash; как прорыв воды; оставь ссору прежде, нежели разгорелась она.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25259, 'rst66', 'pro', 17, 15, 'Оправдывающий нечестивого и обвиняющий праведного &mdash; оба мерзость пред Господом.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25260, 'rst66', 'pro', 17, 16, 'К чему сокровище в руках глупца? Для приобретения мудрости <i>у него</i> нет разума.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25261, 'rst66', 'pro', 17, 17, 'Друг любит во всякое время и, как брат, явится во время несчастья.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25262, 'rst66', 'pro', 17, 18, 'Человек малоумный дает руку и ручается за ближнего своего.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25263, 'rst66', 'pro', 17, 19, 'Кто любит ссоры, любит грех, и кто высоко поднимает ворота свои, тот ищет падения.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25264, 'rst66', 'pro', 17, 20, 'Коварное сердце не найдет добра, и лукавый язык попадет в беду.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25265, 'rst66', 'pro', 17, 21, 'Родил кто глупого, &mdash; себе на горе, и отец глупого не порадуется.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25266, 'rst66', 'pro', 17, 22, 'Веселое сердце благотворно, как врачевство, а унылый дух сушит кости.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25267, 'rst66', 'pro', 17, 23, 'Нечестивый берет подарок из пазухи, чтобы извратить пути правосудия.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25268, 'rst66', 'pro', 17, 24, 'Мудрость &mdash; пред лицом у разумного, а глаза глупца &mdash; на конце земли.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25269, 'rst66', 'pro', 17, 25, 'Глупый сын &mdash; досада отцу своему и огорчение для матери своей.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25270, 'rst66', 'pro', 17, 26, 'Нехорошо и обвинять правого, <i>и</i> бить вельмож за правду.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25271, 'rst66', 'pro', 17, 27, 'Разумный воздержан в словах своих, и благоразумный хладнокровен.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25272, 'rst66', 'pro', 17, 28, 'И глупец, когда молчит, может показаться мудрым, и затворяющий уста свои &mdash; благоразумным.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25273, 'rst66', 'pro', 18, 1, 'Прихоти ищет своенравный, восстает против всего умного.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25274, 'rst66', 'pro', 18, 2, 'Глупый не любит знания, а только бы выказать свой ум.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25275, 'rst66', 'pro', 18, 3, 'С приходом нечестивого приходит и презрение, а с бесславием &mdash; поношение.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25276, 'rst66', 'pro', 18, 4, 'Слова уст человеческих &mdash; глубокие воды; источник мудрости &mdash; струящийся поток.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25277, 'rst66', 'pro', 18, 5, 'Нехорошо быть лицеприятным к нечестивому, чтобы ниспровергнуть праведного на суде.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25278, 'rst66', 'pro', 18, 6, 'Уста глупого идут в ссору, и слова его вызывают побои.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25279, 'rst66', 'pro', 18, 7, 'Язык глупого &mdash; гибель для него, и уста его &mdash; сеть для души его.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25280, 'rst66', 'pro', 18, 8, 'Слова наушника &mdash; как лакомства, и они входят во внутренность чрева.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25281, 'rst66', 'pro', 18, 9, 'Нерадивый в работе своей &mdash; брат расточителю.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25282, 'rst66', 'pro', 18, 10, 'Имя Господа &mdash; крепкая башня: убегает в нее праведник &mdash; и безопасен.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25283, 'rst66', 'pro', 18, 11, 'Имение богатого &mdash; крепкий город его, и как высокая ограда в его воображении.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25284, 'rst66', 'pro', 18, 12, 'Перед падением возносится сердце человека, а смирение предшествует славе.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25285, 'rst66', 'pro', 18, 13, 'Кто дает ответ, не выслушав, тот глуп, и стыд ему.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25286, 'rst66', 'pro', 18, 14, 'Дух человека переносит его немощи; а пораженный дух &mdash; кто может подкрепить его?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25287, 'rst66', 'pro', 18, 15, 'Сердце разумного приобретает знание, и ухо мудрых ищет знания.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25288, 'rst66', 'pro', 18, 16, 'Подарок у человека дает ему простор и до вельмож доведет его.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25289, 'rst66', 'pro', 18, 17, 'Первый в тяжбе своей прав, но приходит соперник его и исследует его.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25290, 'rst66', 'pro', 18, 18, 'Жребий прекращает споры и решает между сильными.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25291, 'rst66', 'pro', 18, 19, 'Озлобившийся брат <i>неприступнее</i> крепкого города, и ссоры подобны запорам замка.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25292, 'rst66', 'pro', 18, 20, 'От плода уст человека наполняется чрево его; произведением уст своих он насыщается.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25293, 'rst66', 'pro', 18, 21, 'Смерть и жизнь &mdash; во власти языка, и любящие его вкусят от плодов его.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25294, 'rst66', 'pro', 18, 22, 'Кто нашел <i>добрую</i> жену, тот нашел благо и получил благодать от Господа.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25295, 'rst66', 'pro', 18, 23, 'С мольбою говорит нищий, а богатый отвечает грубо.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25296, 'rst66', 'pro', 18, 24, 'Кто хочет иметь друзей, тот и сам должен быть дружелюбным; и бывает друг более привязанный, нежели брат.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25297, 'rst66', 'pro', 19, 1, 'Лучше бедный, ходящий в своей непорочности, нежели <i>богатый</i> со лживыми устами, и притом глупый.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25298, 'rst66', 'pro', 19, 2, 'Нехорошо душе без знания, и торопливый ногами оступится.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25299, 'rst66', 'pro', 19, 3, 'Глупость человека извращает путь его, а сердце его негодует на Господа.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25300, 'rst66', 'pro', 19, 4, 'Богатство прибавляет много друзей, а бедный оставляется и другом своим.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25301, 'rst66', 'pro', 19, 5, 'Лжесвидетель не останется ненаказанным, и кто говорит ложь, не спасется.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25302, 'rst66', 'pro', 19, 6, 'Многие заискивают у знатных, и всякий &mdash; друг человеку, делающему подарки.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25303, 'rst66', 'pro', 19, 7, 'Бедного ненавидят все братья его, тем паче друзья его удаляются от него: гонится за ними, чтобы поговорить, но и этого нет.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25304, 'rst66', 'pro', 19, 8, 'Кто приобретает разум, тот любит душу свою; кто наблюдает благоразумие, тот находит благо.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25305, 'rst66', 'pro', 19, 9, 'Лжесвидетель не останется ненаказанным, и кто говорит ложь, погибнет.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25306, 'rst66', 'pro', 19, 10, 'Неприлична глупцу пышность, тем паче рабу господство над князьями.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25307, 'rst66', 'pro', 19, 11, 'Благоразумие делает человека медленным на гнев, и слава для него &mdash; быть снисходительным к проступкам.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25308, 'rst66', 'pro', 19, 12, 'Гнев царя &mdash; как рев льва, а благоволение его &mdash; как роса на траву.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25309, 'rst66', 'pro', 19, 13, 'Глупый сын &mdash; сокрушение для отца своего, и сварливая жена &mdash; сточная труба.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25310, 'rst66', 'pro', 19, 14, 'Дом и имение &mdash; наследство от родителей, а разумная жена &mdash; от Господа.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25311, 'rst66', 'pro', 19, 15, 'Леность погружает в сонливость, и нерадивая душа будет терпеть голод.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25312, 'rst66', 'pro', 19, 16, 'Хранящий заповедь хранит душу свою, а нерадящий о путях своих погибнет.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25313, 'rst66', 'pro', 19, 17, 'Благотворящий бедному дает взаймы Господу, и Он воздаст ему за благодеяние его.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25314, 'rst66', 'pro', 19, 18, 'Наказывай сына своего, доколе есть надежда, и не возмущайся криком его.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25315, 'rst66', 'pro', 19, 19, 'Гневливый пусть терпит наказание, потому что, если пощадишь <i>его</i>, придется тебе еще больше наказывать его.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25316, 'rst66', 'pro', 19, 20, 'Слушайся совета и принимай обличение, чтобы сделаться тебе впоследствии мудрым.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25317, 'rst66', 'pro', 19, 21, 'Много замыслов в сердце человека, но состоится только определенное Господом.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25318, 'rst66', 'pro', 19, 22, 'Радость человеку &mdash; благотворительность его, и бедный человек лучше, нежели лживый.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25319, 'rst66', 'pro', 19, 23, 'Страх Господень <i>ведет</i> к жизни, и <i>кто имеет его</i>, всегда будет доволен, и зло не постигнет его.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25320, 'rst66', 'pro', 19, 24, 'Ленивый опускает руку свою в чашу и не хочет донести ее до рта своего.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25321, 'rst66', 'pro', 19, 25, 'Если ты накажешь кощунника, то и простой сделается благоразумным; и <i>если</i> обличишь разумного, то он поймет наставление.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25322, 'rst66', 'pro', 19, 26, 'Разоряющий отца и выгоняющий мать &mdash; сын срамной и бесчестный.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25323, 'rst66', 'pro', 19, 27, 'Перестань, сын мой, слушать внушения об уклонении от изречений разума.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25324, 'rst66', 'pro', 19, 28, 'Лукавый свидетель издевается над судом, и уста беззаконных глотают неправду.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25325, 'rst66', 'pro', 19, 29, 'Готовы для кощунствующих суды, и побои &mdash; на тело глупых.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25326, 'rst66', 'pro', 20, 1, 'Вино &mdash; глумливо, сикера &mdash; буйна; и всякий, увлекающийся ими, неразумен.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25327, 'rst66', 'pro', 20, 2, 'Гроза царя &mdash; как бы рев льва: кто раздражает его, тот грешит против самого себя.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25328, 'rst66', 'pro', 20, 3, 'Честь для человека &mdash; отстать от ссоры; а всякий глупец задорен.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25329, 'rst66', 'pro', 20, 4, 'Ленивец зимою не пашет: поищет летом &mdash; и нет ничего.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25330, 'rst66', 'pro', 20, 5, 'Помыслы в сердце человека &mdash; глубокие воды, но человек разумный вычерпывает их.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25331, 'rst66', 'pro', 20, 6, 'Многие хвалят человека за милосердие, но правдивого человека кто находит?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25332, 'rst66', 'pro', 20, 7, 'Праведник ходит в своей непорочности: блаженны дети его после него!');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25333, 'rst66', 'pro', 20, 8, 'Царь, сидящий на престоле суда, разгоняет очами своими все злое.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25334, 'rst66', 'pro', 20, 9, 'Кто может сказать: «я очистил мое сердце, я чист от греха моего»?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25335, 'rst66', 'pro', 20, 10, 'Неодинаковые весы, неодинаковая мера, то и другое &mdash; мерзость пред Господом.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25336, 'rst66', 'pro', 20, 11, 'Можно узнать даже отрока по занятиям его, чисто ли и правильно ли будет поведение его.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25337, 'rst66', 'pro', 20, 12, 'Ухо слышащее и глаз видящий &mdash; и то и другое создал Господь.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25338, 'rst66', 'pro', 20, 13, 'Не люби спать, чтобы тебе не обеднеть; держи открытыми глаза твои, и будешь досыта есть хлеб.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25339, 'rst66', 'pro', 20, 14, '«Дурно, дурно», &mdash; говорит покупатель, а когда отойдет, хвалится.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25340, 'rst66', 'pro', 20, 15, 'Есть золото и много жемчуга, но драгоценная утварь &mdash; уста разумные.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25341, 'rst66', 'pro', 20, 16, 'Возьми платье его, так как он поручился за чужого; и за стороннего возьми от него залог.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25342, 'rst66', 'pro', 20, 17, 'Сладок для человека хлеб, <i>приобретенный</i> неправдою; но после рот его наполнится дресвою.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25343, 'rst66', 'pro', 20, 18, 'Предприятия получают твердость чрез совещание, и по совещании веди войну.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25344, 'rst66', 'pro', 20, 19, 'Кто ходит переносчиком, тот открывает тайну; и кто широко раскрывает рот, с тем не сообщайся.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25345, 'rst66', 'pro', 20, 20, 'Кто злословит отца своего и свою мать, того светильник погаснет среди глубокой тьмы.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25346, 'rst66', 'pro', 20, 21, 'Наследство, поспешно захваченное вначале, не благословится впоследствии.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25347, 'rst66', 'pro', 20, 22, 'Не говори: «я отплачу за зло»; предоставь Господу, и Он сохранит тебя.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25348, 'rst66', 'pro', 20, 23, 'Мерзость пред Господом &mdash; неодинаковые гири, и неверные весы &mdash; не добро.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25349, 'rst66', 'pro', 20, 24, 'От Господа направляются шаги человека; человеку же как узнать путь свой?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25350, 'rst66', 'pro', 20, 25, 'Сеть для человека &mdash; поспешно давать обет, и после обета обдумывать.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25351, 'rst66', 'pro', 20, 26, 'Мудрый царь вывеет нечестивых и обратит на них колесо.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25352, 'rst66', 'pro', 20, 27, 'Светильник Господень &mdash; дух человека, испытывающий все глубины сердца.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25353, 'rst66', 'pro', 20, 28, 'Милость и истина охраняют царя, и милостью он поддерживает престол свой.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25354, 'rst66', 'pro', 20, 29, 'Слава юношей &mdash; сила их, а украшение стариков &mdash; седина.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25355, 'rst66', 'pro', 20, 30, 'Раны от побоев &mdash; врачевство против зла и удары, проникающие во внутренности чрева.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25356, 'rst66', 'pro', 21, 1, 'Сердце царя &mdash; в руке Господа, как потоки вод: куда захочет, Он направляет его.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25357, 'rst66', 'pro', 21, 2, 'Всякий путь человека прям в глазах его; но Господь взвешивает сердца.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25358, 'rst66', 'pro', 21, 3, 'Соблюдение правды и правосудия более угодно Господу, нежели жертва.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25359, 'rst66', 'pro', 21, 4, 'Гордость очей и надменность сердца, отличающие нечестивых, &mdash; грех.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25360, 'rst66', 'pro', 21, 5, 'Помышления прилежного стремятся к изобилию, а всякий торопливый терпит лишение.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25361, 'rst66', 'pro', 21, 6, 'Приобретение сокровища лживым языком &mdash; мимолетное дуновение ищущих смерти.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25362, 'rst66', 'pro', 21, 7, 'Насилие нечестивых обрушится на них, потому что они отреклись соблюдать правду.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25363, 'rst66', 'pro', 21, 8, 'Превратен путь человека развращенного; а кто чист, того действие прямо.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25364, 'rst66', 'pro', 21, 9, 'Лучше жить в углу на кровле, нежели со сварливою женою в пространном доме.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25365, 'rst66', 'pro', 21, 10, 'Душа нечестивого желает зла: не найдет милости в глазах его и друг его.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25366, 'rst66', 'pro', 21, 11, 'Когда наказывается кощунник, простой делается мудрым; и когда вразумляется мудрый, то он приобретает знание.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25367, 'rst66', 'pro', 21, 12, 'Праведник наблюдает за домом нечестивого: как повергаются нечестивые в несчастие.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25368, 'rst66', 'pro', 21, 13, 'Кто затыкает ухо свое от вопля бедного, тот и сам будет вопить &mdash; и не будет услышан.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25369, 'rst66', 'pro', 21, 14, 'Подарок тайный тушит гнев, и дар в пазуху &mdash; сильную ярость.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25370, 'rst66', 'pro', 21, 15, 'Соблюдение правосудия &mdash; радость для праведника и страх для делающих зло.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25371, 'rst66', 'pro', 21, 16, 'Человек, сбившийся с пути разума, водворится в собрании мертвецов.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25372, 'rst66', 'pro', 21, 17, 'Кто любит веселье, обеднеет; а кто любит вино и тук, не разбогатеет.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25373, 'rst66', 'pro', 21, 18, 'Выкупом будет за праведного нечестивый и за прямодушного &mdash; лукавый.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25374, 'rst66', 'pro', 21, 19, 'Лучше жить в земле пустынной, нежели с женою сварливою и сердитою.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25375, 'rst66', 'pro', 21, 20, 'Вожделенное сокровище и тук &mdash; в доме мудрого; а глупый человек расточает их.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25376, 'rst66', 'pro', 21, 21, 'Соблюдающий правду и милость найдет жизнь, правду и славу.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25377, 'rst66', 'pro', 21, 22, 'Мудрый входит в город сильных и ниспровергает крепость, на которую они надеялись.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25378, 'rst66', 'pro', 21, 23, 'Кто хранит уста свои и язык свой, тот хранит от бед душу свою.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25379, 'rst66', 'pro', 21, 24, 'Надменный злодей &mdash; кощунник имя ему &mdash; действует в пылу гордости.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25380, 'rst66', 'pro', 21, 25, 'Алчба ленивца убьет его, потому что руки его отказываются работать;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25381, 'rst66', 'pro', 21, 26, 'всякий день он сильно алчет, а праведник дает и не жалеет.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25382, 'rst66', 'pro', 21, 27, 'Жертва нечестивых &mdash; мерзость, особенно когда с лукавством приносят ее.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25383, 'rst66', 'pro', 21, 28, 'Лжесвидетель погибнет; а человек, который говорит, что знает, будет говорить всегда.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25384, 'rst66', 'pro', 21, 29, 'Человек нечестивый дерзок лицом своим, а праведный держит прямо путь свой.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25385, 'rst66', 'pro', 21, 30, 'Нет мудрости, и нет разума, и нет совета вопреки Господу.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25386, 'rst66', 'pro', 21, 31, 'Коня приготовляют на день битвы, но победа &mdash; от Господа.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25387, 'rst66', 'pro', 22, 1, 'Доброе имя лучше большого богатства, и добрая слава лучше серебра и золота.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25388, 'rst66', 'pro', 22, 2, 'Богатый и бедный встречаются друг с другом: того и другого создал Господь.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25389, 'rst66', 'pro', 22, 3, 'Благоразумный видит беду и укрывается; а неопытные идут вперед и наказываются.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25390, 'rst66', 'pro', 22, 4, 'За смирением следует страх Господень, богатство и слава и жизнь.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25391, 'rst66', 'pro', 22, 5, 'Терны и сети на пути коварного; кто бережет душу свою, удались от них.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25392, 'rst66', 'pro', 22, 6, 'Наставь юношу при начале пути его: он не уклонится от него, когда и состарится.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25393, 'rst66', 'pro', 22, 7, 'Богатый господствует над бедным, и должник <i>делается</i> рабом заимодавца.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25394, 'rst66', 'pro', 22, 8, 'Сеющий неправду пожнет беду, и трости гнева его не станет.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25395, 'rst66', 'pro', 22, 9, 'Милосердый будет благословляем, потому что дает бедному от хлеба своего.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25396, 'rst66', 'pro', 22, 10, 'Прогони кощунника, и удалится раздор, и прекратятся ссора и брань.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25397, 'rst66', 'pro', 22, 11, 'Кто любит чистоту сердца, у того приятность на устах, тому царь &mdash; друг.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25398, 'rst66', 'pro', 22, 12, 'Очи Господа охраняют знание, а слова законопреступника Он ниспровергает.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25399, 'rst66', 'pro', 22, 13, 'Ленивец говорит: «лев на улице! посреди площади убьют меня!»');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25400, 'rst66', 'pro', 22, 14, 'Глубокая пропасть &mdash; уста блудниц: на кого прогневается Господь, тот упадет туда.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25401, 'rst66', 'pro', 22, 15, 'Глупость привязалась к сердцу юноши, но исправительная розга удалит ее от него.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25402, 'rst66', 'pro', 22, 16, 'Кто обижает бедного, чтобы умножить свое богатство, и кто дает богатому, тот обеднеет.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25403, 'rst66', 'pro', 22, 17, 'Приклони ухо твое, и слушай слова мудрых, и сердце твое обрати к моему знанию;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25404, 'rst66', 'pro', 22, 18, 'потому что утешительно будет, если ты будешь хранить их в сердце твоем, и они будут также в устах твоих.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25405, 'rst66', 'pro', 22, 19, 'Чтобы упование твое было на Господа, я учу тебя и сегодня, и ты <i>помни</i>.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25406, 'rst66', 'pro', 22, 20, 'Не писал ли я тебе трижды в советах и наставлении,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25407, 'rst66', 'pro', 22, 21, 'чтобы научить тебя точным словам истины, дабы ты мог передавать слова истины посылающим тебя?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25408, 'rst66', 'pro', 22, 22, 'Не будь грабителем бедного, потому что он беден, и не притесняй несчастного у ворот,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25409, 'rst66', 'pro', 22, 23, 'потому что Господь вступится в дело их и исхитит душу у грабителей их.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25410, 'rst66', 'pro', 22, 24, 'Не дружись с гневливым и не сообщайся с человеком вспыльчивым,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25411, 'rst66', 'pro', 22, 25, 'чтобы не научиться путям его и не навлечь петли на душу твою.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25412, 'rst66', 'pro', 22, 26, 'Не будь из тех, которые дают руки и поручаются за долги:');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25413, 'rst66', 'pro', 22, 27, 'если тебе нечем заплатить, то для чего доводить себя, чтобы взяли постель твою из‐под тебя?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25414, 'rst66', 'pro', 22, 28, 'Не передвигай межи давней, которую провели отцы твои.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25415, 'rst66', 'pro', 22, 29, 'Видел ли ты человека проворного в своем деле? Он будет стоять перед царями, он не будет стоять перед простыми.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25416, 'rst66', 'pro', 23, 1, 'Когда сядешь вкушать пищу с властелином, то тщательно наблюдай, что перед тобою,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25417, 'rst66', 'pro', 23, 2, 'и поставь преграду в гортани твоей, если ты алчен.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25418, 'rst66', 'pro', 23, 3, 'Не прельщайся лакомыми яствами его; это &mdash; обманчивая пища.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25419, 'rst66', 'pro', 23, 4, 'Не заботься о том, чтобы нажить богатство; оставь такие мысли твои.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25420, 'rst66', 'pro', 23, 5, 'Устремишь глаза твои на него, и &mdash; его уже нет; потому что оно сделает себе крылья и, как орел, улетит к небу.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25421, 'rst66', 'pro', 23, 6, 'Не вкушай пищи у человека завистливого и не прельщайся лакомыми яствами его;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25422, 'rst66', 'pro', 23, 7, 'потому что, каковы мысли в душе его, таков и он; «ешь и пей», &mdash; говорит он тебе, а сердце его не с тобою.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25423, 'rst66', 'pro', 23, 8, 'Кусок, который ты съел, изблюешь, и добрые слова твои ты потратишь напрасно.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25424, 'rst66', 'pro', 23, 9, 'В уши глупого не говори, потому что он презрит разумные слова твои.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25425, 'rst66', 'pro', 23, 10, 'Не передвигай межи давней и на поля сирот не заходи,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25426, 'rst66', 'pro', 23, 11, 'потому что Защитник их силен; Он вступится в дело их с тобою.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25427, 'rst66', 'pro', 23, 12, 'Приложи сердце твое к учению и уши твои &mdash; к умным словам.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25428, 'rst66', 'pro', 23, 13, 'Не оставляй юноши без наказания: если накажешь его розгою, он не умрет;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25429, 'rst66', 'pro', 23, 14, 'ты накажешь его розгою и спасешь душу его от преисподней.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25430, 'rst66', 'pro', 23, 15, 'Сын мой! если сердце твое будет мудро, то порадуется и мое сердце;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25431, 'rst66', 'pro', 23, 16, 'и внутренности мои будут радоваться, когда уста твои будут говорить правое.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25432, 'rst66', 'pro', 23, 17, 'Да не завидует сердце твое грешникам, но да пребудет оно во все дни в страхе Господнем;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25433, 'rst66', 'pro', 23, 18, 'потому что есть будущность, и надежда твоя не потеряна.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25434, 'rst66', 'pro', 23, 19, 'Слушай, сын мой, и будь мудр, и направляй сердце твое на прямой путь.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25435, 'rst66', 'pro', 23, 20, 'Не будь между упивающимися вином, между пресыщающимися мясом:');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25436, 'rst66', 'pro', 23, 21, 'потому что пьяница и пресыщающийся обеднеют, и сонливость оденет в рубище.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25437, 'rst66', 'pro', 23, 22, 'Слушайся отца твоего: он родил тебя; и не пренебрегай матери твоей, когда она и состарится.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25438, 'rst66', 'pro', 23, 23, 'Купи истину и не продавай мудрости и учения и разума.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25439, 'rst66', 'pro', 23, 24, 'Торжествует отец праведника, и родивший мудрого радуется о нем.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25440, 'rst66', 'pro', 23, 25, 'Да веселится отец твой и да торжествует мать твоя, родившая тебя.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25441, 'rst66', 'pro', 23, 26, 'Сын мой! отдай сердце твое мне, и глаза твои да наблюдают пути мои,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25442, 'rst66', 'pro', 23, 27, 'потому что блудница &mdash; глубокая пропасть, и чужая жена &mdash; тесный колодезь;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25443, 'rst66', 'pro', 23, 28, 'она, как разбойник, сидит в засаде и умножает между людьми законопреступников.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25444, 'rst66', 'pro', 23, 29, 'У кого вой? у кого стон? у кого ссоры? у кого горе? у кого раны без причины? у кого багровые глаза?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25445, 'rst66', 'pro', 23, 30, 'У тех, которые долго сидят за вином, которые приходят отыскивать вина приправленного.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25446, 'rst66', 'pro', 23, 31, 'Не смотри на вино, как оно краснеет, как оно искрится в чаше, как оно ухаживается ровно:');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25447, 'rst66', 'pro', 23, 32, 'впоследствии, как змей, оно укусит, и ужалит, как аспид;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25448, 'rst66', 'pro', 23, 33, 'глаза твои будут смотреть на чужих жен, и сердце твое заговорит развратное,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25449, 'rst66', 'pro', 23, 34, 'и ты будешь, как спящий среди моря и как спящий на верху мачты.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25450, 'rst66', 'pro', 23, 35, '<i>И скажешь:</i> «били меня, мне не было больно; толкали меня, я не чувствовал. Когда проснусь, опять буду искать того же».');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25451, 'rst66', 'pro', 24, 1, 'Не ревнуй злым людям и не желай быть с ними,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25452, 'rst66', 'pro', 24, 2, 'потому что о насилии помышляет сердце их, и о злом говорят уста их.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25453, 'rst66', 'pro', 24, 3, 'Мудростью устрояется дом и разумом утверждается,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25454, 'rst66', 'pro', 24, 4, 'и с уменьем внутренности его наполняются всяким драгоценным и прекрасным имуществом.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25455, 'rst66', 'pro', 24, 5, 'Человек мудрый силен, и человек разумный укрепляет силу свою.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25456, 'rst66', 'pro', 24, 6, 'Поэтому с обдуманностью веди войну твою, и успех <i>будет</i> при множестве совещаний.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25457, 'rst66', 'pro', 24, 7, 'Для глупого слишком высока мудрость; у ворот не откроет он уст своих.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25458, 'rst66', 'pro', 24, 8, 'Кто замышляет сделать зло, того называют злоумышленником.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25459, 'rst66', 'pro', 24, 9, 'Помысел глупости &mdash; грех, и кощунник &mdash; мерзость для людей.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25460, 'rst66', 'pro', 24, 10, 'Если ты в день бедствия оказался слабым, то бедна сила твоя.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25461, 'rst66', 'pro', 24, 11, 'Спасай взятых на смерть, и неужели откажешься от обреченных на убиение?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25462, 'rst66', 'pro', 24, 12, 'Скажешь ли: «вот, мы не знали этого»? А Испытующий сердца разве не знает? Наблюдающий над душою твоею знает это, и воздаст человеку по делам его.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25463, 'rst66', 'pro', 24, 13, 'Ешь, сын мой, мед, потому что он приятен, и сот, который сладок для гортани твоей:');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25464, 'rst66', 'pro', 24, 14, 'таково и познание мудрости для души твоей. Если ты нашел <i>ее</i>, то есть будущность, и надежда твоя не потеряна.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25465, 'rst66', 'pro', 24, 15, 'Не злоумышляй, нечестивый, против жилища праведника, не опустошай места покоя его,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25466, 'rst66', 'pro', 24, 16, 'ибо семь раз упадет праведник и встанет; а нечестивые впадут в погибель.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25467, 'rst66', 'pro', 24, 17, 'Не радуйся, когда упадет враг твой, и да не веселится сердце твое, когда он споткнется.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25468, 'rst66', 'pro', 24, 18, 'Иначе увидит Господь, и неугодно будет это в очах Его, и Он отвратит от него гнев Свой.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25469, 'rst66', 'pro', 24, 19, 'Не негодуй на злодеев и не завидуй нечестивым,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25470, 'rst66', 'pro', 24, 20, 'потому что злой не имеет будущности, &mdash; светильник нечестивых угаснет.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25471, 'rst66', 'pro', 24, 21, 'Бойся, сын мой, Господа и царя; с мятежниками не сообщайся,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25472, 'rst66', 'pro', 24, 22, 'потому что внезапно придет погибель от них, и беду от них обоих кто предузнает?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25473, 'rst66', 'pro', 24, 23, 'Сказано также мудрыми: иметь лицеприятие на суде &mdash; нехорошо.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25474, 'rst66', 'pro', 24, 24, 'Кто говорит виновному: «ты прав», того будут проклинать народы, того будут ненавидеть племена;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25475, 'rst66', 'pro', 24, 25, 'а обличающие будут любимы, и на них придет благословение.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25476, 'rst66', 'pro', 24, 26, 'В уста целует, кто отвечает словами верными.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25477, 'rst66', 'pro', 24, 27, 'Соверши дела твои вне дома, окончи их на поле твоем, и потом устрояй и дом твой.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25478, 'rst66', 'pro', 24, 28, 'Не будь лжесвидетелем на ближнего твоего: к чему тебе обманывать устами твоими?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25479, 'rst66', 'pro', 24, 29, 'Не говори: «как он поступил со мною, так и я поступлю с ним, воздам человеку по делам его».');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25480, 'rst66', 'pro', 24, 30, 'Проходил я мимо поля человека ленивого и мимо виноградника человека скудоумного:');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25481, 'rst66', 'pro', 24, 31, 'и вот, все это заросло терном, поверхность его покрылась крапивою, и каменная ограда его обрушилась.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25482, 'rst66', 'pro', 24, 32, 'И посмотрел я, и обратил сердце мое, и посмотрел и получил урок:');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25483, 'rst66', 'pro', 24, 33, '«Немного поспишь, немного подремлешь, немного, сложив руки, полежишь, &mdash;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25484, 'rst66', 'pro', 24, 34, 'и придет, <i>как</i> прохожий, бедность твоя, и нужда твоя &mdash; как человек вооруженный».');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25485, 'rst66', 'pro', 25, 1, 'И это притчи Соломона, которые собрали мужи Езекии, царя Иудейского.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25486, 'rst66', 'pro', 25, 2, 'Слава Божия &mdash; облекать тайною дело, а слава царей &mdash; исследовать дело.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25487, 'rst66', 'pro', 25, 3, 'Как небо в высоте и земля в глубине, так сердце царей &mdash; неисследимо.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25488, 'rst66', 'pro', 25, 4, 'Отдели примесь от серебра, и выйдет у серебряника сосуд:');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25489, 'rst66', 'pro', 25, 5, 'удали неправедного от царя, и престол его утвердится правдою.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25490, 'rst66', 'pro', 25, 6, 'Не величайся пред лицом царя, и на месте великих не становись;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25491, 'rst66', 'pro', 25, 7, 'потому что лучше, когда скажут тебе: «пойди сюда повыше», нежели когда понизят тебя пред знатным, которого видели глаза твои.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25492, 'rst66', 'pro', 25, 8, 'Не вступай поспешно в тяжбу: иначе что будешь делать при окончании, когда соперник твой осрамит тебя?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25493, 'rst66', 'pro', 25, 9, 'Веди тяжбу с соперником твоим, но тайны другого не открывай,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25494, 'rst66', 'pro', 25, 10, 'дабы не укорил тебя услышавший это, и тогда бесчестие твое не отойдет от тебя.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25495, 'rst66', 'pro', 25, 11, 'Золотые яблоки в серебряных прозрачных сосудах &mdash; слово, сказанное прилично.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25496, 'rst66', 'pro', 25, 12, 'Золотая серьга и украшение из чистого золота &mdash; мудрый обличитель для внимательного уха.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25497, 'rst66', 'pro', 25, 13, 'Что прохлада от снега во время жатвы, то верный посол для посылающего его: он доставляет душе господина своего отраду.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25498, 'rst66', 'pro', 25, 14, 'Что тучи и ветры без дождя, то человек, хвастающий ложными подарками.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25499, 'rst66', 'pro', 25, 15, 'Кротостью склоняется к милости вельможа, и мягкий язык переламывает кость.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25500, 'rst66', 'pro', 25, 16, 'Нашел ты мед &mdash; ешь, сколько тебе потребно, чтобы не пресытиться им и не изблевать его.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25501, 'rst66', 'pro', 25, 17, 'Не учащай входить в дом друга твоего, чтобы он не наскучил тобою и не возненавидел тебя.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25502, 'rst66', 'pro', 25, 18, 'Что молот и меч и острая стрела, то человек, произносящий ложное свидетельство против ближнего своего.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25503, 'rst66', 'pro', 25, 19, 'Что сломанный зуб и расслабленная нога, то надежда на ненадежного <i>человека</i> в день бедствия.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25504, 'rst66', 'pro', 25, 20, 'Что снимающий с себя одежду в холодный день, что уксус на рану, то поющий песни печальному сердцу.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25505, 'rst66', 'pro', 25, 21, 'Если голоден враг твой, накорми его хлебом; и если он жаждет, напои его водою:');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25506, 'rst66', 'pro', 25, 22, 'ибо, <i>делая сие,</i> ты собираешь горящие угли на голову его, и Господь воздаст тебе.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25507, 'rst66', 'pro', 25, 23, 'Северный ветер производит дождь, а тайный язык &mdash; недовольные лица.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25508, 'rst66', 'pro', 25, 24, 'Лучше жить в углу на кровле, нежели со сварливою женою в пространном доме.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25509, 'rst66', 'pro', 25, 25, 'Что холодная вода для истомленной жаждой души, то добрая весть из дальней страны.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25510, 'rst66', 'pro', 25, 26, 'Что возмущенный источник и поврежденный родник, то праведник, падающий пред нечестивым.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25511, 'rst66', 'pro', 25, 27, 'Как нехорошо есть много меду, так домогаться славы не есть слава.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25512, 'rst66', 'pro', 25, 28, 'Что город разрушенный, без стен, то человек, не владеющий духом своим.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25513, 'rst66', 'pro', 26, 1, 'Как снег летом и дождь во время жатвы, так честь неприлична глупому.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25514, 'rst66', 'pro', 26, 2, 'Как воробей вспорхнет, как ласточка улетит, так незаслуженное проклятие не сбудется.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25515, 'rst66', 'pro', 26, 3, 'Бич &mdash; для коня, узда &mdash; для осла, а палка &mdash; для глупых.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25516, 'rst66', 'pro', 26, 4, 'Не отвечай глупому по глупости его, чтобы и тебе не сделаться подобным ему;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25517, 'rst66', 'pro', 26, 5, 'но отвечай глупому по глупости его, чтобы он не стал мудрецом в глазах своих.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25518, 'rst66', 'pro', 26, 6, 'Подрезывает себе ноги, терпит неприятность тот, кто дает словесное поручение глупцу.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25519, 'rst66', 'pro', 26, 7, 'Неровно поднимаются ноги у хромого, &mdash; и притча в устах глупцов.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25520, 'rst66', 'pro', 26, 8, 'Что влагающий драгоценный камень в пращу, то воздающий глупому честь.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25521, 'rst66', 'pro', 26, 9, 'Что <i>колючий</i> терн в руке пьяного, то притча в устах глупцов.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25522, 'rst66', 'pro', 26, 10, 'Сильный делает все произвольно: и глупого награждает, и всякого прохожего награждает.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25523, 'rst66', 'pro', 26, 11, 'Как пес возвращается на блевотину свою, так глупый повторяет глупость свою.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25524, 'rst66', 'pro', 26, 12, 'Видал ли ты человека, мудрого в глазах его? На глупого больше надежды, нежели на него.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25525, 'rst66', 'pro', 26, 13, 'Ленивец говорит: «лев на дороге! лев на площадях!»');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25526, 'rst66', 'pro', 26, 14, 'Дверь ворочается на крючьях своих, а ленивец на постели своей.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25527, 'rst66', 'pro', 26, 15, 'Ленивец опускает руку свою в чашу, и ему тяжело донести ее до рта своего.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25528, 'rst66', 'pro', 26, 16, 'Ленивец в глазах своих мудрее семерых, отвечающих обдуманно.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25529, 'rst66', 'pro', 26, 17, 'Хватает пса за уши, кто, проходя мимо, вмешивается в чужую ссору.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25530, 'rst66', 'pro', 26, 18, 'Как притворяющийся помешанным бросает огонь, стрелы и смерть,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25531, 'rst66', 'pro', 26, 19, 'так &mdash; человек, который коварно вредит другу своему и потом говорит: «я только пошутил».');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25532, 'rst66', 'pro', 26, 20, 'Где нет больше дров, огонь погасает, и где нет наушника, раздор утихает.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25533, 'rst66', 'pro', 26, 21, 'Уголь &mdash; для жара, и дрова &mdash; для огня, а человек сварливый &mdash; для разжжения ссоры.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25534, 'rst66', 'pro', 26, 22, 'Слова наушника &mdash; как лакомства, и они входят во внутренность чрева.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25535, 'rst66', 'pro', 26, 23, 'Что нечистым серебром обложенный глиняный сосуд, то пламенные уста и сердце злобное.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25536, 'rst66', 'pro', 26, 24, 'Устами своими притворяется враг, а в сердце своем замышляет коварство.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25537, 'rst66', 'pro', 26, 25, 'Если он говорит и нежным голосом, не верь ему, потому что семь мерзостей в сердце его.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25538, 'rst66', 'pro', 26, 26, 'Если ненависть прикрывается наедине, то откроется злоба его в народном собрании.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25539, 'rst66', 'pro', 26, 27, 'Кто роет яму, тот упадет в нее, и кто покатит вверх камень, к тому он воротится.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25540, 'rst66', 'pro', 26, 28, 'Лживый язык ненавидит уязвляемых им, и льстивые уста готовят падение.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25541, 'rst66', 'pro', 27, 1, 'Не хвались завтрашним днем, потому что не знаешь, что родит тот день.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25542, 'rst66', 'pro', 27, 2, 'Пусть хвалит тебя другой, а не уста твои, &mdash; чужой, а не язык твой.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25543, 'rst66', 'pro', 27, 3, 'Тяжел камень, весок и песок; но гнев глупца тяжелее их обоих.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25544, 'rst66', 'pro', 27, 4, 'Жесток гнев, неукротима ярость; но кто устоит против ревности?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25545, 'rst66', 'pro', 27, 5, 'Лучше открытое обличение, нежели скрытая любовь.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25546, 'rst66', 'pro', 27, 6, 'Искренни укоризны от любящего, и лживы поцелуи ненавидящего.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25547, 'rst66', 'pro', 27, 7, 'Сытая душа попирает и сот, а голодной душе все горькое сладко.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25548, 'rst66', 'pro', 27, 8, 'Как птица, покинувшая гнездо свое, так человек, покинувший место свое.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25549, 'rst66', 'pro', 27, 9, 'Масть и курение радуют сердце; так сладок <i>всякому</i> друг сердечным советом своим.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25550, 'rst66', 'pro', 27, 10, 'Не покидай друга твоего и друга отца твоего, и в дом брата твоего не ходи в день несчастья твоего: лучше сосед вблизи, нежели брат вдали.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25551, 'rst66', 'pro', 27, 11, 'Будь мудр, сын мой, и радуй сердце мое; и я буду иметь, что отвечать злословящему меня.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25552, 'rst66', 'pro', 27, 12, 'Благоразумный видит беду и укрывается; а неопытные идут вперед <i>и</i> наказываются.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25553, 'rst66', 'pro', 27, 13, 'Возьми у него платье его, потому что он поручился за чужого, и за стороннего возьми от него залог.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25554, 'rst66', 'pro', 27, 14, 'Кто громко хвалит друга своего с раннего утра, того сочтут за злословящего.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25555, 'rst66', 'pro', 27, 15, 'Непрестанная капель в дождливый день и сварливая жена &mdash; равны:');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25556, 'rst66', 'pro', 27, 16, 'кто хочет скрыть ее, тот хочет скрыть ветер и масть в правой руке своей, дающую знать о себе.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25557, 'rst66', 'pro', 27, 17, 'Железо железо острит, и человек изощряет взгляд друга своего.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25558, 'rst66', 'pro', 27, 18, 'Кто стережет смоковницу, тот будет есть плоды ее; и кто бережет господина своего, тот будет в чести.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25559, 'rst66', 'pro', 27, 19, 'Как в воде лицо &mdash; к лицу, так сердце человека &mdash; к человеку.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25560, 'rst66', 'pro', 27, 20, 'Преисподняя и Аваддон &mdash; ненасытимы; так ненасытимы и глаза человеческие.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25561, 'rst66', 'pro', 27, 21, 'Что плавильня &mdash; для серебра, горнило &mdash; для золота, то для человека уста, которые хвалят его.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25562, 'rst66', 'pro', 27, 22, 'Толки глупого в ступе пестом вместе с зерном, не отделится от него глупость его.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25563, 'rst66', 'pro', 27, 23, 'Хорошо наблюдай за скотом твоим, имей попечение о стадах;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25564, 'rst66', 'pro', 27, 24, 'потому что <i>богатство</i> не навек, да и власть разве из рода в род?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25565, 'rst66', 'pro', 27, 25, 'Прозябает трава, и является зелень, и собирают горные травы.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25566, 'rst66', 'pro', 27, 26, 'Овцы &mdash; на одежду тебе, и козлы &mdash; на покупку поля.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25567, 'rst66', 'pro', 27, 27, 'И довольно козьего молока в пищу тебе, в пищу домашним твоим и на продовольствие служанкам твоим.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25568, 'rst66', 'pro', 28, 1, 'Нечестивый бежит, когда никто не гонится <i>за ним;</i> а праведник смел, как лев.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25569, 'rst66', 'pro', 28, 2, 'Когда страна отступит от закона, тогда много в ней начальников; а при разумном и знающем муже она долговечна.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25570, 'rst66', 'pro', 28, 3, 'Человек бедный и притесняющий слабых <i>то же, что</i> проливной дождь, смывающий хлеб.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25571, 'rst66', 'pro', 28, 4, 'Отступники от закона хвалят нечестивых, а соблюдающие закон негодуют на них.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25572, 'rst66', 'pro', 28, 5, 'Злые люди не разумеют справедливости, а ищущие Господа разумеют всё.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25573, 'rst66', 'pro', 28, 6, 'Лучше бедный, ходящий в своей непорочности, нежели тот, кто извращает пути свои, хотя он и богат.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25574, 'rst66', 'pro', 28, 7, 'Хранящий закон &mdash; сын разумный, а знающийся с расточителями срамит отца своего.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25575, 'rst66', 'pro', 28, 8, 'Умножающий имение свое ростом и лихвою соберет его для благотворителя бедных.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25576, 'rst66', 'pro', 28, 9, 'Кто отклоняет ухо свое от слушания закона, того и молитва &mdash; мерзость.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25577, 'rst66', 'pro', 28, 10, 'Совращающий праведных на путь зла сам упадет в свою яму, а непорочные наследуют добро.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25578, 'rst66', 'pro', 28, 11, 'Человек богатый &mdash; мудрец в глазах своих, но умный бедняк обличит его.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25579, 'rst66', 'pro', 28, 12, 'Когда торжествуют праведники, великая слава, но когда возвышаются нечестивые, люди укрываются.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25580, 'rst66', 'pro', 28, 13, 'Скрывающий свои преступления не будет иметь успеха; а кто сознается и оставляет их, тот будет помилован.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25581, 'rst66', 'pro', 28, 14, 'Блажен человек, который всегда пребывает в благоговении; а кто ожесточает сердце свое, тот попадет в беду.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25582, 'rst66', 'pro', 28, 15, 'Как рыкающий лев и голодный медведь, так нечестивый властелин над бедным народом.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25583, 'rst66', 'pro', 28, 16, 'Неразумный правитель много делает притеснений, а ненавидящий корысть продолжит дни.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25584, 'rst66', 'pro', 28, 17, 'Человек, виновный в пролитии человеческой крови, будет бегать до могилы, чтобы кто не схватил его.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25585, 'rst66', 'pro', 28, 18, 'Кто ходит непорочно, тот будет невредим; а ходящий кривыми путями упадет на одном из них.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25586, 'rst66', 'pro', 28, 19, 'Кто возделывает землю свою, тот будет насыщаться хлебом, а кто подражает праздным, тот насытится нищетою.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25587, 'rst66', 'pro', 28, 20, 'Верный человек богат благословениями, а кто спешит разбогатеть, тот не останется ненаказанным.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25588, 'rst66', 'pro', 28, 21, 'Быть лицеприятным &mdash; нехорошо: такой человек и за кусок хлеба сделает неправду.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25589, 'rst66', 'pro', 28, 22, 'Спешит к богатству завистливый человек, и не думает, что нищета постигнет его.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25590, 'rst66', 'pro', 28, 23, 'Обличающий человека найдет после большую приязнь, нежели тот, кто льстит языком.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25591, 'rst66', 'pro', 28, 24, 'Кто обкрадывает отца своего и мать свою и говорит: «это не грех», тот &mdash; сообщник грабителям.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25592, 'rst66', 'pro', 28, 25, 'Надменный разжигает ссору, а надеющийся на Господа будет благоденствовать.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25593, 'rst66', 'pro', 28, 26, 'Кто надеется на себя, тот глуп; а кто ходит в мудрости, тот будет цел.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25594, 'rst66', 'pro', 28, 27, 'Дающий нищему не обеднеет; а кто закрывает глаза свои от него, на том много проклятий.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25595, 'rst66', 'pro', 28, 28, 'Когда возвышаются нечестивые, люди укрываются, а когда они падают, умножаются праведники.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25596, 'rst66', 'pro', 29, 1, 'Человек, который, будучи обличаем, ожесточает выю свою, внезапно сокрушится, и не будет <i>ему</i> исцеления.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25597, 'rst66', 'pro', 29, 2, 'Когда умножаются праведники, веселится народ, а когда господствует нечестивый, народ стенает.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25598, 'rst66', 'pro', 29, 3, 'Человек, любящий мудрость, радует отца своего; а кто знается с блудницами, тот расточает имение.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25599, 'rst66', 'pro', 29, 4, 'Царь правосудием утверждает землю, а любящий подарки разоряет ее.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25600, 'rst66', 'pro', 29, 5, 'Человек, льстящий другу своему, расстилает сеть ногам его.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25601, 'rst66', 'pro', 29, 6, 'В грехе злого человека &mdash; сеть <i>для него</i>, а праведник веселится и радуется.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25602, 'rst66', 'pro', 29, 7, 'Праведник тщательно вникает в тяжбу бедных, а нечестивый не разбирает дела.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25603, 'rst66', 'pro', 29, 8, 'Люди развратные возмущают город, а мудрые утишают мятеж.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25604, 'rst66', 'pro', 29, 9, 'Умный человек, судясь с человеком глупым, сердится ли, смеется ли, &mdash; не имеет покоя.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25605, 'rst66', 'pro', 29, 10, 'Кровожадные люди ненавидят непорочного, а праведные заботятся о его жизни.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25606, 'rst66', 'pro', 29, 11, 'Глупый весь гнев свой изливает, а мудрый сдерживает его.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25607, 'rst66', 'pro', 29, 12, 'Если правитель слушает ложные речи, то и все служащие у него нечестивы.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25608, 'rst66', 'pro', 29, 13, 'Бедный и лихоимец встречаются друг с другом; но свет глазам того и другого дает Господь.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25609, 'rst66', 'pro', 29, 14, 'Если царь судит бедных по правде, то престол его навсегда утвердится.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25610, 'rst66', 'pro', 29, 15, 'Розга и обличение дают мудрость; но отрок, оставленный в небрежении, делает стыд своей матери.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25611, 'rst66', 'pro', 29, 16, 'При умножении нечестивых умножается беззаконие; но праведники увидят падение их.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25612, 'rst66', 'pro', 29, 17, 'Наказывай сына твоего, и он даст тебе покой, и доставит радость душе твоей.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25613, 'rst66', 'pro', 29, 18, 'Без откровения свыше народ необуздан, а соблюдающий закон блажен.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25614, 'rst66', 'pro', 29, 19, 'Словами не научится раб, потому что, хотя он понимает <i>их</i>, но не слушается.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25615, 'rst66', 'pro', 29, 20, 'Видал ли ты человека опрометчивого в словах своих? на глупого больше надежды, нежели на него.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25616, 'rst66', 'pro', 29, 21, 'Если с детства воспитывать раба в неге, то впоследствии он захочет быть сыном.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25617, 'rst66', 'pro', 29, 22, 'Человек гневливый заводит ссору, и вспыльчивый много грешит.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25618, 'rst66', 'pro', 29, 23, 'Гордость человека унижает его, а смиренный духом приобретает честь.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25619, 'rst66', 'pro', 29, 24, 'Кто делится с вором, тот ненавидит душу свою; слышит он проклятие, но не объявляет о том.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25620, 'rst66', 'pro', 29, 25, 'Боязнь пред людьми ставит сеть; а надеющийся на Господа будет безопасен.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25621, 'rst66', 'pro', 29, 26, 'Многие ищут <i>благосклонного</i> лица правителя, но судьба человека &mdash; от Господа.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25622, 'rst66', 'pro', 29, 27, 'Мерзость для праведников &mdash; человек неправедный, и мерзость для нечестивого &mdash; идущий прямым путем.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25623, 'rst66', 'pro', 30, 1, 'Слова Агура, сына Иакеева. Вдохновенные изречения, <i>которые</i> сказал этот человек Ифиилу, Ифиилу и Укалу:');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25624, 'rst66', 'pro', 30, 2, 'подлинно, я более невежда, нежели кто‐либо из людей, и разума человеческого нет у меня,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25625, 'rst66', 'pro', 30, 3, 'и не научился я мудрости, и познания святых не имею.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25626, 'rst66', 'pro', 30, 4, 'Кто восходил на небо и нисходил? кто собрал ветер в пригоршни свои? кто завязал воду в одежду? кто поставил все пределы земли? какое имя ему? и какое имя сыну его? знаешь ли?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25627, 'rst66', 'pro', 30, 5, 'Всякое слово Бога чисто; Он &mdash; щит уповающим на Него.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25628, 'rst66', 'pro', 30, 6, 'Не прибавляй к словам Его, чтобы Он не обличил тебя и ты не оказался лжецом.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25629, 'rst66', 'pro', 30, 7, 'Двух вещей я прошу у Тебя, не откажи мне, прежде нежели я умру:');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25630, 'rst66', 'pro', 30, 8, 'суету и ложь удали от меня, нищеты и богатства не давай мне, питай меня насущным хлебом,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25631, 'rst66', 'pro', 30, 9, 'дабы, пресытившись, я не отрекся <i>Тебя</i> и не сказал: «кто Господь?» и чтобы, обеднев, не стал красть и употреблять имя Бога моего всуе.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25632, 'rst66', 'pro', 30, 10, 'Не злословь раба пред господином его, чтобы он не проклял тебя и ты не остался виноватым.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25633, 'rst66', 'pro', 30, 11, 'Есть род, который проклинает отца своего и не благословляет матери своей.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25634, 'rst66', 'pro', 30, 12, 'Есть род, который чист в глазах своих, тогда как не омыт от нечистот своих.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25635, 'rst66', 'pro', 30, 13, 'Есть род &mdash; о, как высокомерны глаза его и как подняты ресницы его!');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25636, 'rst66', 'pro', 30, 14, 'Есть род, у которого зубы &mdash; мечи, и челюсти &mdash; ножи, чтобы пожирать бедных на земле и нищих между людьми.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25637, 'rst66', 'pro', 30, 15, 'У ненасытимости две дочери: «давай, давай!» Вот три ненасытимых, и четыре, которые не скажут: «довольно!» &mdash;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25638, 'rst66', 'pro', 30, 16, 'преисподняя и утроба бесплодная &mdash; земля, которая не насыщается водою, и огонь, который не говорит: «довольно!»');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25639, 'rst66', 'pro', 30, 17, 'Глаз, насмехающийся над отцом и пренебрегающий покорностью к матери, выклюют вороны дольные и сожрут птенцы орлиные!');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25640, 'rst66', 'pro', 30, 18, 'Три вещи непостижимы для меня, и четырех я не понимаю:');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25641, 'rst66', 'pro', 30, 19, 'пути орла на небе, пути змея на скале, пути корабля среди моря и пути мужчины к девице.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25642, 'rst66', 'pro', 30, 20, 'Таков путь и жены прелюбодейной; поела и обтерла рот свой, и говорит: «я ничего худого не сделала».');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25643, 'rst66', 'pro', 30, 21, 'От трех трясется земля, четырех она не может носить:');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25644, 'rst66', 'pro', 30, 22, 'раба, когда он делается царем; глупого, когда он досыта ест хлеб;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25645, 'rst66', 'pro', 30, 23, 'позорную женщину, когда она выходит замуж, и служанку, когда она занимает место госпожи своей.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25646, 'rst66', 'pro', 30, 24, 'Вот четыре малых на земле, но они мудрее мудрых:');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25647, 'rst66', 'pro', 30, 25, 'муравьи &mdash; народ не сильный, но летом заготовляют пищу свою;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25648, 'rst66', 'pro', 30, 26, 'горные мыши &mdash; народ слабый, но ставят домы свои на скале;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25649, 'rst66', 'pro', 30, 27, 'у саранчи нет царя, но выступает вся она стройно;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25650, 'rst66', 'pro', 30, 28, 'паук лапками цепляется, но бывает в царских чертогах.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25651, 'rst66', 'pro', 30, 29, 'Вот трое имеют стройную походку, и четверо стройно выступают:');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25652, 'rst66', 'pro', 30, 30, 'лев, силач между зверями, не посторонится ни перед кем;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25653, 'rst66', 'pro', 30, 31, 'конь и козел, и царь среди народа своего.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25654, 'rst66', 'pro', 30, 32, 'Если ты в заносчивости своей сделал глупость и помыслил злое, то <i>положи</i> руку на уста;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25655, 'rst66', 'pro', 30, 33, 'потому что, как сбивание молока производит масло, толчок в нос производит кровь, так и возбуждение гнева производит ссору.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25656, 'rst66', 'pro', 31, 1, 'Слова Лемуила царя. Наставление, которое преподала ему мать его:');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25657, 'rst66', 'pro', 31, 2, 'что, сын мой? что, сын чрева моего? что, сын обетов моих?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25658, 'rst66', 'pro', 31, 3, 'Не отдавай женщинам сил твоих, ни путей твоих губительницам царей.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25659, 'rst66', 'pro', 31, 4, 'Не царям, Лемуил, не царям пить вино, и не князьям &mdash; сикеру,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25660, 'rst66', 'pro', 31, 5, 'чтобы, напившись, они не забыли закона и не превратили суда всех угнетаемых.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25661, 'rst66', 'pro', 31, 6, 'Дайте сикеру погибающему и вино огорченному душою;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25662, 'rst66', 'pro', 31, 7, 'пусть он выпьет и забудет бедность свою и не вспомнит больше о своем страдании.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25663, 'rst66', 'pro', 31, 8, 'Открывай уста твои за безгласного и для защиты всех сирот.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25664, 'rst66', 'pro', 31, 9, 'Открывай уста твои для правосудия и для дела бедного и нищего.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25665, 'rst66', 'pro', 31, 10, 'Кто найдет добродетельную жену? цена ее выше жемчугов;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25666, 'rst66', 'pro', 31, 11, 'уверено в ней сердце мужа ее, и он не останется без прибытка;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25667, 'rst66', 'pro', 31, 12, 'она воздает ему добром, а не злом, во все дни жизни своей.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25668, 'rst66', 'pro', 31, 13, 'Добывает шерсть и лен, и с охотою работает своими руками.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25669, 'rst66', 'pro', 31, 14, 'Она, как купеческие корабли, издалека добывает хлеб свой.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25670, 'rst66', 'pro', 31, 15, 'Она встает еще ночью и раздает пищу в доме своем и урочное служанкам своим.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25671, 'rst66', 'pro', 31, 16, 'Задумает она о поле, и приобретает его; от плодов рук своих насаждает виноградник.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25672, 'rst66', 'pro', 31, 17, 'Препоясывает силою чресла свои и укрепляет мышцы свои.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25673, 'rst66', 'pro', 31, 18, 'Она чувствует, что занятие ее хорошо, и &mdash; светильник ее не гаснет и ночью.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25674, 'rst66', 'pro', 31, 19, 'Протягивает руки свои к прялке, и персты ее берутся за веретено.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25675, 'rst66', 'pro', 31, 20, 'Длань свою она открывает бедному, и руку свою подает нуждающемуся.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25676, 'rst66', 'pro', 31, 21, 'Не боится стужи для семьи своей, потому что вся семья ее одета в двойные одежды.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25677, 'rst66', 'pro', 31, 22, 'Она делает себе ковры; виссон и пурпур &mdash; одежда ее.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25678, 'rst66', 'pro', 31, 23, 'Муж ее известен у ворот, когда сидит со старейшинами земли.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25679, 'rst66', 'pro', 31, 24, 'Она делает покрывала и продает, и поясы доставляет купцам Финикийским.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25680, 'rst66', 'pro', 31, 25, 'Крепость и красота &mdash; одежда ее, и весело смотрит она на будущее.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25681, 'rst66', 'pro', 31, 26, 'Уста свои открывает с мудростью, и кроткое наставление на языке ее.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25682, 'rst66', 'pro', 31, 27, 'Она наблюдает за хозяйством в доме своем и не ест хлеба праздности.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25683, 'rst66', 'pro', 31, 28, 'Встают дети и ублажают ее, &mdash; муж, и хвалит ее:');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25684, 'rst66', 'pro', 31, 29, '«Много было жен добродетельных, но ты превзошла всех их».');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25685, 'rst66', 'pro', 31, 30, 'Миловидность обманчива, и красота суетна; но жена, боящаяся Господа, достойна хвалы.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25686, 'rst66', 'pro', 31, 31, 'Дайте ей от плода рук ее, и да прославят ее у ворот дела ее!');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25687, 'rst66', 'ecc', 1, 1, 'Слова Екклесиаста, сына Давидова, царя в Иерусалиме.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25688, 'rst66', 'ecc', 1, 2, 'Суета сует, сказал Екклесиаст, суета сует, &mdash; всё суета!');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25689, 'rst66', 'ecc', 1, 3, 'Что пользы человеку от всех трудов его, которыми трудится он под солнцем?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25690, 'rst66', 'ecc', 1, 4, 'Род проходит, и род приходит, а земля пребывает вовеки.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25691, 'rst66', 'ecc', 1, 5, 'Восходит солнце, и заходит солнце, и спешит к месту своему, где оно восходит.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25692, 'rst66', 'ecc', 1, 6, 'Идет ветер к югу, и переходит к северу, кружится, кружится на ходу своем, и возвращается ветер на круги свои.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25693, 'rst66', 'ecc', 1, 7, 'Все реки текут в море, но море не переполняется: к тому месту, откуда реки текут, они возвращаются, чтобы опять течь.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25694, 'rst66', 'ecc', 1, 8, 'Все вещи &mdash; в труде: не может человек пересказать всего; не насытится око зрением, не наполнится ухо слушанием.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25695, 'rst66', 'ecc', 1, 9, 'Что было, то и будет; и что делалось, то и будет делаться, и нет ничего нового под солнцем.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25696, 'rst66', 'ecc', 1, 10, 'Бывает нечто, о чем говорят: «смотри, вот, это новое»; но <i>это</i> было уже в веках, бывших прежде нас.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25697, 'rst66', 'ecc', 1, 11, 'Нет памяти о прежнем; да и о том, что будет, не останется памяти у тех, которые будут после.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25698, 'rst66', 'ecc', 1, 12, 'Я, Екклесиаст, был царем над Израилем в Иерусалиме;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25699, 'rst66', 'ecc', 1, 13, 'и предал я сердце мое тому, чтобы исследовать и испытать мудростью все, что делается под небом: это тяжелое занятие дал Бог сынам человеческим, чтобы они упражнялись в нем.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25700, 'rst66', 'ecc', 1, 14, 'Видел я все дела, какие делаются под солнцем, и вот, всё &mdash; суета и томление духа!');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25701, 'rst66', 'ecc', 1, 15, 'Кривое не может сделаться прямым, и чего нет, того нельзя считать.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25702, 'rst66', 'ecc', 1, 16, 'Говорил я с сердцем моим так: вот, я возвеличился и приобрел мудрости больше всех, которые были прежде меня над Иерусалимом, и сердце мое видело много мудрости и знания.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25703, 'rst66', 'ecc', 1, 17, 'И предал я сердце мое тому, чтобы познать мудрость и познать безумие и глупость: узнал, что и это &mdash; томление духа;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25704, 'rst66', 'ecc', 1, 18, 'потому что во многой мудрости много печали; и кто умножает познания, умножает скорбь.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25705, 'rst66', 'ecc', 2, 1, 'Сказал я в сердце моем: «дай, испытаю я тебя весельем, и насладись добром»; но и это &mdash; суета!');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25706, 'rst66', 'ecc', 2, 2, 'О смехе сказал я: «глупость!», а о веселье: «что оно делает?»');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25707, 'rst66', 'ecc', 2, 3, 'Вздумал я в сердце моем услаждать вином тело мое и, между тем, как сердце мое руководилось мудростью, придержаться и глупости, доколе не увижу, что хорошо для сынов человеческих, что должны были бы они делать под небом в немногие дни жизни своей.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25708, 'rst66', 'ecc', 2, 4, 'Я предпринял большие дела: построил себе домы, посадил себе виноградники,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25709, 'rst66', 'ecc', 2, 5, 'устроил себе сады и рощи и насадил в них всякие плодовитые дерева;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25710, 'rst66', 'ecc', 2, 6, 'сделал себе водоемы для орошения из них рощей, произращающих деревья;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25711, 'rst66', 'ecc', 2, 7, 'приобрел себе слуг и служанок, и домочадцы были у меня; также крупного и мелкого скота было у меня больше, нежели у всех, бывших прежде меня в Иерусалиме;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25712, 'rst66', 'ecc', 2, 8, 'собрал себе серебра и золота и драгоценностей от царей и областей; завел у себя певцов и певиц и услаждения сынов человеческих &mdash; разные музыкальные орудия.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25713, 'rst66', 'ecc', 2, 9, 'И сделался я великим и богатым больше всех, бывших прежде меня в Иерусалиме; и мудрость моя пребыла со мною.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25714, 'rst66', 'ecc', 2, 10, 'Чего бы глаза мои ни пожелали, я не отказывал им, не возбранял сердцу моему никакого веселья, потому что сердце мое радовалось во всех трудах моих, и это было моею долею от всех трудов моих.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25715, 'rst66', 'ecc', 2, 11, 'И оглянулся я на все дела мои, которые сделали руки мои, и на труд, которым трудился я, делая <i>их:</i> и вот, всё &mdash; суета и томление духа, и нет <i>от них</i> пользы под солнцем!');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25716, 'rst66', 'ecc', 2, 12, 'И обратился я, чтобы взглянуть на мудрость и безумие и глупость: ибо что <i>может сделать</i> человек после царя <i>сверх того</i>, что уже сделано?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25717, 'rst66', 'ecc', 2, 13, 'И увидел я, что преимущество мудрости перед глупостью такое же, как преимущество света перед тьмою:');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25718, 'rst66', 'ecc', 2, 14, 'у мудрого глаза его &mdash; в голове его, а глупый ходит во тьме; но узнал я, что одна участь постигает их всех.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25719, 'rst66', 'ecc', 2, 15, 'И сказал я в сердце моем: «и меня постигнет та же участь, как и глупого: к чему же я сделался очень мудрым?» И сказал я в сердце моем, что и это &mdash; суета;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25720, 'rst66', 'ecc', 2, 16, 'потому что мудрого не будут помнить вечно, как и глупого; в грядущие дни все будет забыто, и увы! мудрый умирает наравне с глупым.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25721, 'rst66', 'ecc', 2, 17, 'И возненавидел я жизнь, потому что противны стали мне дела, которые делаются под солнцем; ибо всё &mdash; суета и томление духа!');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25722, 'rst66', 'ecc', 2, 18, 'И возненавидел я весь труд мой, которым трудился под солнцем, потому что должен оставить его человеку, который будет после меня.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25723, 'rst66', 'ecc', 2, 19, 'И кто знает: мудрый ли будет он, или глупый? А он будет распоряжаться всем трудом моим, которым я трудился и которым показал себя мудрым под солнцем. И это &mdash; суета!');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25724, 'rst66', 'ecc', 2, 20, 'И обратился я, чтобы внушить сердцу моему отречься от всего труда, которым я трудился под солнцем,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25725, 'rst66', 'ecc', 2, 21, 'потому что иной человек трудится мудро, с знанием и успехом, и должен отдать всё человеку, не трудившемуся в том, как бы часть его. И это &mdash; суета и зло великое!');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25726, 'rst66', 'ecc', 2, 22, 'Ибо что будет иметь человек от всего труда своего и заботы сердца своего, что трудится он под солнцем?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25727, 'rst66', 'ecc', 2, 23, 'Потому что все дни его &mdash; скорби, и его труды &mdash; беспокойство; даже и ночью сердце его не знает покоя. И это &mdash; суета!');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25728, 'rst66', 'ecc', 2, 24, 'Не во власти человека и то благо, чтобы есть и пить и услаждать душу свою от труда своего. Я увидел, что и это &mdash; от руки Божией;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25729, 'rst66', 'ecc', 2, 25, 'потому что кто может есть и кто может наслаждаться без Него?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25730, 'rst66', 'ecc', 2, 26, 'Ибо человеку, который добр пред лицом Его, Он дает мудрость и знание и радость; а грешнику дает заботу собирать и копить, чтобы <i>после</i> отдать доброму пред лицом Божиим. И это &mdash; суета и томление духа!');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25731, 'rst66', 'ecc', 3, 1, 'Всему свое время, и время всякой вещи под небом:');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25732, 'rst66', 'ecc', 3, 2, 'время рождаться, и время умирать; время насаждать, и время вырывать посаженное;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25733, 'rst66', 'ecc', 3, 3, 'время убивать, и время врачевать; время разрушать, и время строить;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25734, 'rst66', 'ecc', 3, 4, 'время плакать, и время смеяться; время сетовать, и время плясать;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25735, 'rst66', 'ecc', 3, 5, 'время разбрасывать камни, и время собирать камни; время обнимать, и время уклоняться от объятий;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25736, 'rst66', 'ecc', 3, 6, 'время искать, и время терять; время сберегать, и время бросать;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25737, 'rst66', 'ecc', 3, 7, 'время раздирать, и время сшивать; время молчать, и время говорить;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25738, 'rst66', 'ecc', 3, 8, 'время любить, и время ненавидеть; время войне, и время миру.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25739, 'rst66', 'ecc', 3, 9, 'Что пользы работающему от того, над чем он трудится?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25740, 'rst66', 'ecc', 3, 10, 'Видел я эту заботу, которую дал Бог сынам человеческим, чтобы они упражнялись в том.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25741, 'rst66', 'ecc', 3, 11, 'Всё соделал Он прекрасным в свое время, и вложил мир в сердце их, хотя человек не может постигнуть дел, которые Бог делает, от начала до конца.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25742, 'rst66', 'ecc', 3, 12, 'Познал я, что нет для них ничего лучшего, как веселиться и делать доброе в жизни своей.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25743, 'rst66', 'ecc', 3, 13, 'И если какой человек ест и пьет, и видит доброе во всяком труде своем, то это &mdash; дар Божий.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25744, 'rst66', 'ecc', 3, 14, 'Познал я, что всё, что делает Бог, пребывает вовек: к тому нечего прибавлять и от того нечего убавить, &mdash; и Бог делает так, чтобы благоговели пред лицом Его.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25745, 'rst66', 'ecc', 3, 15, 'Что было, то и теперь есть, и что будет, то уже было, &mdash; и Бог воззовет прошедшее.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25746, 'rst66', 'ecc', 3, 16, 'Еще видел я под солнцем: место суда, а там беззаконие; место правды, а там неправда.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25747, 'rst66', 'ecc', 3, 17, 'И сказал я в сердце своем: «праведного и нечестивого будет судить Бог; потому что время для всякой вещи и <i>суд</i> над всяким делом там».');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25748, 'rst66', 'ecc', 3, 18, 'Сказал я в сердце своем о сынах человеческих, чтобы испытал их Бог, и чтобы они видели, что они сами по себе животные;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25749, 'rst66', 'ecc', 3, 19, 'потому что участь сынов человеческих и участь животных &mdash; участь одна: как те умирают, так умирают и эти, и одно дыхание у всех, и нет у человека преимущества перед скотом, потому что всё &mdash; суета!');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25750, 'rst66', 'ecc', 3, 20, 'Все идет в одно место: все произошло из праха и все возвратится в прах.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25751, 'rst66', 'ecc', 3, 21, 'Кто знает: дух сынов человеческих восходит ли вверх, и дух животных сходит ли вниз, в землю?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25752, 'rst66', 'ecc', 3, 22, 'Итак, увидел я, что нет ничего лучше, как наслаждаться человеку делами своими: потому что это &mdash; доля его; ибо кто приведет его посмотреть на то, что будет после него?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25753, 'rst66', 'ecc', 4, 1, 'И обратился я и увидел всякие угнетения, какие делаются под солнцем: и вот слезы угнетенных, а утешителя у них нет; и в руке угнетающих их &mdash; сила, а утешителя у них нет.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25754, 'rst66', 'ecc', 4, 2, 'И ублажил я мертвых, которые давно умерли, более живых, которые живут доселе;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25755, 'rst66', 'ecc', 4, 3, 'а блаженнее их обоих тот, кто еще не существовал, кто не видал злых дел, какие делаются под солнцем.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25756, 'rst66', 'ecc', 4, 4, 'Видел я также, что всякий труд и всякий успех в делах производят взаимную между людьми зависть. И это &mdash; суета и томление духа!');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25757, 'rst66', 'ecc', 4, 5, 'Глупый <i>сидит</i>, сложив свои руки, и съедает плоть свою.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25758, 'rst66', 'ecc', 4, 6, 'Лучше горсть с покоем, нежели пригоршни с трудом и томлением духа.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25759, 'rst66', 'ecc', 4, 7, 'И обратился я и увидел еще суету под солнцем;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25760, 'rst66', 'ecc', 4, 8, '<i>человек</i> одинокий, и другого нет; ни сына, ни брата нет у него; а всем трудам его нет конца, и глаз его не насыщается богатством. «Для кого же я тружусь и лишаю душу мою блага?» И это &mdash; суета и недоброе дело!');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25761, 'rst66', 'ecc', 4, 9, 'Двоим лучше, нежели одному; потому что у них есть доброе вознаграждение в труде их:');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25762, 'rst66', 'ecc', 4, 10, 'ибо если упадет один, то другой поднимет товарища своего. Но горе одному, когда упадет, а другого нет, который поднял бы его.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25763, 'rst66', 'ecc', 4, 11, 'Также, если лежат двое, то тепло им; а одному как согреться?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25764, 'rst66', 'ecc', 4, 12, 'И если станет преодолевать кто‐либо одного, то двое устоят против него: и нитка, втрое скрученная, не скоро порвется.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25765, 'rst66', 'ecc', 4, 13, 'Лучше бедный, но умный юноша, нежели старый, но неразумный царь, который не умеет принимать советы;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25766, 'rst66', 'ecc', 4, 14, 'ибо тот из темницы выйдет на царство, хотя родился в царстве своем бедным.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25767, 'rst66', 'ecc', 4, 15, 'Видел я всех живущих, которые ходят под солнцем, с этим другим юношею, который займет место того.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25768, 'rst66', 'ecc', 4, 16, 'Не было числа всему народу, который был перед ним, хотя позднейшие не порадуются им. И это &mdash; суета и томление духа!');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25769, 'rst66', 'ecc', 4, 17, 'Наблюдай за ногою твоею, когда идешь в дом Божий, и будь готов более к слушанию, нежели к жертвоприношению; ибо они не думают, что худо делают.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25770, 'rst66', 'ecc', 5, 1, 'Не торопись языком твоим, и сердце твое да не спешит произнести слово пред Богом; потому что Бог на небе, а ты на земле; поэтому слова твои да будут немноги.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25771, 'rst66', 'ecc', 5, 2, 'Ибо, как сновидения бывают при множестве забот, так голос глупого познается при множестве слов.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25772, 'rst66', 'ecc', 5, 3, 'Когда даешь обет Богу, то не медли исполнить его, потому что Он не благоволит к глупым: что обещал, исполни.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25773, 'rst66', 'ecc', 5, 4, 'Лучше тебе не обещать, нежели обещать и не исполнить.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25774, 'rst66', 'ecc', 5, 5, 'Не дозволяй устам твоим вводить в грех плоть твою, и не говори пред Ангелом <i>Божиим</i> «это &mdash; ошибка!» Для чего тебе <i>делать</i>, чтобы Бог прогневался на слово твое и разрушил дело рук твоих?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25775, 'rst66', 'ecc', 5, 6, 'Ибо во множестве сновидений, как и во множестве слов, &mdash; много суеты; но ты бойся Бога.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25776, 'rst66', 'ecc', 5, 7, 'Если ты увидишь в какой области притеснение бедному и нарушение суда и правды, то не удивляйся этому: потому что над высоким наблюдает высший, а над ними еще высший;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25777, 'rst66', 'ecc', 5, 8, 'превосходство же страны в целом есть царь, заботящийся о стране.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25778, 'rst66', 'ecc', 5, 9, 'Кто любит серебро, тот не насытится серебром, и кто любит богатство, тому нет пользы от того. И это &mdash; суета!');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25779, 'rst66', 'ecc', 5, 10, 'Умножается имущество, умножаются и потребляющие его; и какое благо для владеющего им: разве только смотреть своими глазами?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25780, 'rst66', 'ecc', 5, 11, 'Сладок сон трудящегося, мало ли, много ли он съест; но пресыщение богатого не дает ему уснуть.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25781, 'rst66', 'ecc', 5, 12, 'Есть мучительный недуг, который видел я под солнцем: богатство, сберегаемое владетелем его во вред ему.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25782, 'rst66', 'ecc', 5, 13, 'И гибнет богатство это от несчастных случаев: родил он сына, и ничего нет в руках у него.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25783, 'rst66', 'ecc', 5, 14, 'Как вышел он нагим из утробы матери своей, таким и отходит, каким пришел, и ничего не возьмет от труда своего, что мог бы он понести в руке своей.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25784, 'rst66', 'ecc', 5, 15, 'И это тяжкий недуг: каким пришел он, таким и отходит. Какая же польза ему, что он трудился на ветер?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25785, 'rst66', 'ecc', 5, 16, 'А он во все дни свои ел впотьмах, в большом раздражении, в огорчении и досаде.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25786, 'rst66', 'ecc', 5, 17, 'Вот еще, что я нашел доброго и приятного: есть и пить и наслаждаться добром во всех трудах своих, какими кто трудится под солнцем во все дни жизни своей, которые дал ему Бог; потому что это его доля.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25787, 'rst66', 'ecc', 5, 18, 'И если какому человеку Бог дал богатство и имущество, и дал ему власть пользоваться от них и брать свою долю и наслаждаться от трудов своих, то это &mdash; дар Божий.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25788, 'rst66', 'ecc', 5, 19, 'Недолго будут у него в памяти дни жизни его; поэтому Бог и вознаграждает его радостью сердца его.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25789, 'rst66', 'ecc', 6, 1, 'Есть зло, которое видел я под солнцем, и оно часто бывает между людьми:');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25790, 'rst66', 'ecc', 6, 2, 'Бог дает человеку богатство и имущество и славу, и нет для души его недостатка ни в чем, чего не пожелал бы он; но не дает ему Бог пользоваться этим, а пользуется тем чужой человек: это &mdash; суета и тяжкий недуг!');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25791, 'rst66', 'ecc', 6, 3, 'Если бы какой человек родил сто <i>детей</i>, и прожил многие годы, и еще умножились дни жизни его, но душа его не наслаждалась бы добром и не было бы ему и погребения, то я сказал бы: выкидыш счастливее его,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25792, 'rst66', 'ecc', 6, 4, 'потому что он напрасно пришел и отошел во тьму, и его имя покрыто мраком.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25793, 'rst66', 'ecc', 6, 5, 'Он даже не видал и не знал солнца: ему покойнее, нежели тому.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25794, 'rst66', 'ecc', 6, 6, 'А тот, хотя бы прожил две тысячи лет и не наслаждался добром, не все ли пойдет в одно место?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25795, 'rst66', 'ecc', 6, 7, 'Все труды человека &mdash; для рта его, а душа его не насыщается.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25796, 'rst66', 'ecc', 6, 8, 'Какое же преимущество мудрого перед глупым, какое &mdash; бедняка, умеющего ходить перед живущими?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25797, 'rst66', 'ecc', 6, 9, 'Лучше видеть глазами, нежели бродить душою. И это &mdash; также суета и томление духа!');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25798, 'rst66', 'ecc', 6, 10, 'Что существует, тому уже наречено имя, и известно, что это &mdash; человек, и что он не может препираться с тем, кто сильнее его.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25799, 'rst66', 'ecc', 6, 11, 'Много таких вещей, которые умножают суету: что же для человека лучше?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25800, 'rst66', 'ecc', 6, 12, 'Ибо кто знает, что хорошо для человека в жизни, во все дни суетной жизни его, которые он проводит как тень? И кто скажет человеку, что будет после него под солнцем?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25801, 'rst66', 'ecc', 7, 1, 'Доброе имя лучше дорогой масти, и день смерти &mdash; дня рождения.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25802, 'rst66', 'ecc', 7, 2, 'Лучше ходить в дом плача об умершем, нежели ходить в дом пира; ибо таков конец всякого человека, и живой приложит <i>это</i> к своему сердцу.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25803, 'rst66', 'ecc', 7, 3, 'Сетование лучше смеха; потому что при печали лица сердце делается лучше.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25804, 'rst66', 'ecc', 7, 4, 'Сердце мудрых &mdash; в доме плача, а сердце глупых &mdash; в доме веселья.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25805, 'rst66', 'ecc', 7, 5, 'Лучше слушать обличения от мудрого, нежели слушать песни глупых;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25806, 'rst66', 'ecc', 7, 6, 'потому что смех глупых то же, что треск тернового хвороста под котлом. И это &mdash; суета!');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25807, 'rst66', 'ecc', 7, 7, 'Притесняя других, мудрый делается глупым, и подарки портят сердце.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25808, 'rst66', 'ecc', 7, 8, 'Конец дела лучше начала его; терпеливый лучше высокомерного.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25809, 'rst66', 'ecc', 7, 9, 'Не будь духом твоим поспешен на гнев, потому что гнев гнездится в сердце глупых.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25810, 'rst66', 'ecc', 7, 10, 'Не говори: «отчего это прежние дни были лучше нынешних?», потому что не от мудрости ты спрашиваешь об этом.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25811, 'rst66', 'ecc', 7, 11, 'Хороша мудрость с наследством, и особенно для видящих солнце:');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25812, 'rst66', 'ecc', 7, 12, 'потому что под сенью ее <i>то же, что</i> под сенью серебра; но превосходство знания в <i>том, что</i> мудрость дает жизнь владеющему ею.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25813, 'rst66', 'ecc', 7, 13, 'Смотри на действование Божие: ибо кто может выпрямить то, что Он сделал кривым?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25814, 'rst66', 'ecc', 7, 14, 'Во дни благополучия пользуйся благом, а во дни несчастья размышляй: то и другое соделал Бог для того, чтобы человек ничего не мог сказать против Него.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25815, 'rst66', 'ecc', 7, 15, 'Всего насмотрелся я в суетные дни мои: праведник гибнет в праведности своей; нечестивый живет долго в нечестии своем.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25816, 'rst66', 'ecc', 7, 16, 'Не будь слишком строг, и не выставляй себя слишком мудрым; зачем тебе губить себя?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25817, 'rst66', 'ecc', 7, 17, 'Не предавайся греху, и не будь безумен: зачем тебе умирать не в свое время?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25818, 'rst66', 'ecc', 7, 18, 'Хорошо, если ты будешь держаться одного и не отнимать руки от другого; потому что кто боится Бога, тот избежит всего того.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25819, 'rst66', 'ecc', 7, 19, 'Мудрость делает мудрого сильнее десяти властителей, которые в городе.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25820, 'rst66', 'ecc', 7, 20, 'Нет человека праведного на земле, который делал бы добро и не грешил бы;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25821, 'rst66', 'ecc', 7, 21, 'поэтому не на всякое слово, которое говорят, обращай внимание, чтобы не услышать тебе раба твоего, когда он злословит тебя;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25822, 'rst66', 'ecc', 7, 22, 'ибо сердце твое знает много случаев, когда и сам ты злословил других.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25823, 'rst66', 'ecc', 7, 23, 'Все это испытал я мудростью; я сказал: «буду я мудрым»; но мудрость далека от меня.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25824, 'rst66', 'ecc', 7, 24, 'Далеко то, что было, и глубоко‐глубоко: кто постигнет его?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25825, 'rst66', 'ecc', 7, 25, 'Обратился я сердцем моим к тому, чтобы узнать, исследовать и изыскать мудрость и разум, и познать нечестие глупости, невежества и безумия, &mdash;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25826, 'rst66', 'ecc', 7, 26, 'и нашел я, что горше смерти женщина, потому что она &mdash; сеть, и сердце ее &mdash; силки, руки ее &mdash; оковы; добрый пред Богом спасется от нее, а грешник уловлен будет ею.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25827, 'rst66', 'ecc', 7, 27, 'Вот это нашел я, сказал Екклесиаст, испытывая одно за другим.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25828, 'rst66', 'ecc', 7, 28, 'Чего еще искала душа моя, и я не нашел? &mdash; Мужчину одного из тысячи я нашел, а женщину между всеми ими не нашел.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25829, 'rst66', 'ecc', 7, 29, 'Только это я нашел, что Бог сотворил человека правым, а люди пустились во многие помыслы.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25830, 'rst66', 'ecc', 8, 1, 'Кто &mdash; как мудрый, и кто понимает значение вещей? Мудрость человека просветляет лицо его, и суровость лица его изменяется.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25831, 'rst66', 'ecc', 8, 2, '<i>Я говорю:</i> слово царское храни, и <i>это</i> ради клятвы пред Богом.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25832, 'rst66', 'ecc', 8, 3, 'Не спеши уходить от лица его, и не упорствуй в худом деле; потому что он, что захочет, все может сделать.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25833, 'rst66', 'ecc', 8, 4, 'Где слово царя, там власть; и кто скажет ему: «что ты делаешь?»');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25834, 'rst66', 'ecc', 8, 5, 'Соблюдающий заповедь не испытает никакого зла: сердце мудрого знает и время и устав;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25835, 'rst66', 'ecc', 8, 6, 'потому что для всякой вещи есть свое время и устав; а человеку великое зло оттого,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25836, 'rst66', 'ecc', 8, 7, 'что он не знает, что будет; и как это будет &mdash; кто скажет ему?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25837, 'rst66', 'ecc', 8, 8, 'Человек не властен над духом, чтобы удержать дух, и нет власти у него над днем смерти, и нет избавления в этой борьбе, и не спасет нечестие нечестивого.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25838, 'rst66', 'ecc', 8, 9, 'Все это я видел, и обращал сердце мое на всякое дело, какое делается под солнцем. Бывает время, когда человек властвует над человеком во вред ему.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25839, 'rst66', 'ecc', 8, 10, 'Видел я тогда, что хоронили нечестивых, и приходили и отходили от святого места, и они забываемы были в городе, где они так поступали. И это &mdash; суета!');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25840, 'rst66', 'ecc', 8, 11, 'Не скоро совершается суд над худыми делами; от этого и не страшится сердце сынов человеческих делать зло.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25841, 'rst66', 'ecc', 8, 12, 'Хотя грешник сто раз делает зло и коснеет в нем, но я знаю, что благо будет боящимся Бога, которые благоговеют пред лицом Его;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25842, 'rst66', 'ecc', 8, 13, 'а нечестивому не будет добра, и, подобно тени, недолго продержится тот, кто не благоговеет пред Богом.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25843, 'rst66', 'ecc', 8, 14, 'Есть и такая суета на земле: праведников постигает то, чего заслуживали бы дела нечестивых, а с нечестивыми бывает то, чего заслуживали бы дела праведников. И сказал я: и это &mdash; суета!');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25844, 'rst66', 'ecc', 8, 15, 'И похвалил я веселье; потому что нет лучшего для человека под солнцем, как есть, пить и веселиться: это сопровождает его в трудах во дни жизни его, которые дал ему Бог под солнцем.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25845, 'rst66', 'ecc', 8, 16, 'Когда я обратил сердце мое на то, чтобы постигнуть мудрость и обозреть дела, которые делаются на земле, и среди которых <i>человек</i> ни днем, ни ночью не знает сна, &mdash;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25846, 'rst66', 'ecc', 8, 17, 'тогда я увидел все дела Божии и <i>нашел</i>, что человек не может постигнуть дел, которые делаются под солнцем. Сколько бы человек ни трудился в исследовании, он все‐таки не постигнет этого; и если бы какой мудрец сказал, что он знает, он не может постигнуть <i>этого</i>.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25847, 'rst66', 'ecc', 9, 1, 'На все это я обратил сердце мое для исследования, что праведные и мудрые и деяния их &mdash; в руке Божией, и что человек ни любви, ни ненависти не знает во всем том, что перед ним.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25848, 'rst66', 'ecc', 9, 2, 'Всему и всем &mdash; одно: одна участь праведнику и нечестивому, доброму и <i>злому</i>, чистому и нечистому, приносящему жертву и не приносящему жертвы; как добродетельному, так и грешнику; как клянущемуся, так и боящемуся клятвы.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25849, 'rst66', 'ecc', 9, 3, 'Это‐то и худо во всем, что делается под солнцем, что одна участь всем, и сердце сынов человеческих исполнено зла, и безумие в сердце их, в жизни их; а после того они <i>отходят</i> к умершим.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25850, 'rst66', 'ecc', 9, 4, 'Кто находится между живыми, тому есть еще надежда, так как и псу живому лучше, нежели мертвому льву.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25851, 'rst66', 'ecc', 9, 5, 'Живые знают, что умрут, а мертвые ничего не знают, и уже нет им воздаяния, потому что и память о них предана забвению,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25852, 'rst66', 'ecc', 9, 6, 'и любовь их и ненависть их и ревность их уже исчезли, и нет им более части вовеки ни в чем, что делается под солнцем.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25853, 'rst66', 'ecc', 9, 7, '<i>Итак</i> иди, ешь с весельем хлеб твой, и пей в радости сердца вино твое, когда Бог благоволит к делам твоим.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25854, 'rst66', 'ecc', 9, 8, 'Да будут во всякое время одежды твои светлы, и да не оскудевает елей на голове твоей.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25855, 'rst66', 'ecc', 9, 9, 'Наслаждайся жизнью с женою, которую любишь, во все дни суетной жизни твоей, и которую дал тебе Бог под солнцем на все суетные дни твои; потому что это &mdash; доля твоя в жизни и в трудах твоих, какими ты трудишься под солнцем.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25856, 'rst66', 'ecc', 9, 10, 'Все, что может рука твоя делать, по силам делай; потому что в могиле, куда ты пойдешь, нет ни работы, ни размышления, ни знания, ни мудрости.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25857, 'rst66', 'ecc', 9, 11, 'И обратился я, и видел под солнцем, что не проворным достается успешный бег, не храбрым &mdash; победа, не мудрым &mdash; хлеб, и не у разумных &mdash; богатство, и не искусным &mdash; благорасположение, но время и случай для всех их.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25858, 'rst66', 'ecc', 9, 12, 'Ибо человек не знает своего времени. Как рыбы попадаются в пагубную сеть, и как птицы запутываются в силках, так сыны человеческие уловляются в бедственное время, когда оно неожиданно находит на них.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25859, 'rst66', 'ecc', 9, 13, 'Вот еще какую мудрость видел я под солнцем, и она показалась мне важною:');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25860, 'rst66', 'ecc', 9, 14, 'город небольшой, и людей в нем немного; к нему подступил великий царь и обложил его и произвел против него большие осадные работы;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25861, 'rst66', 'ecc', 9, 15, 'но в нем нашелся мудрый бедняк, и он спас своею мудростью этот город; и, однако же, никто не вспоминал об этом бедном человеке.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25862, 'rst66', 'ecc', 9, 16, 'И сказал я: мудрость лучше силы, и, однако же, мудрость бедняка пренебрегается, и слов его не слушают.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25863, 'rst66', 'ecc', 9, 17, 'Слова мудрых, <i>высказанные</i> спокойно, выслушиваются <i>лучше</i>, нежели крик властелина между глупыми.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25864, 'rst66', 'ecc', 9, 18, 'Мудрость лучше воинских орудий; но один погрешивший погубит много доброго.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25865, 'rst66', 'ecc', 10, 1, 'Мертвые мухи портят и делают зловонною благовонную масть мироварника: то же делает небольшая глупость уважаемого человека с его мудростью и честью.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25866, 'rst66', 'ecc', 10, 2, 'Сердце мудрого &mdash; на правую сторону, а сердце глупого &mdash; на левую.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25867, 'rst66', 'ecc', 10, 3, 'По какой бы дороге ни шел глупый, у него <i>всегда</i> недостает смысла, и всякому он выскажет, что он глуп.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25868, 'rst66', 'ecc', 10, 4, 'Если гнев начальника вспыхнет на тебя, то не оставляй места твоего; потому что кротость покрывает и большие проступки.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25869, 'rst66', 'ecc', 10, 5, 'Есть зло, которое видел я под солнцем, это &mdash; как бы погрешность, происходящая от властелина;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25870, 'rst66', 'ecc', 10, 6, 'невежество поставляется на большой высоте, а богатые сидят низко.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25871, 'rst66', 'ecc', 10, 7, 'Видел я рабов на конях, а князей, ходящих, подобно рабам, пешком.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25872, 'rst66', 'ecc', 10, 8, 'Кто копает яму, тот упадет в нее, и кто разрушает ограду, того ужалит змей.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25873, 'rst66', 'ecc', 10, 9, 'Кто передвигает камни, тот может надсадить себя, и кто колет дрова, тот может подвергнуться опасности от них.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25874, 'rst66', 'ecc', 10, 10, 'Если притупится топор, и если лезвие его не будет отточено, то надобно будет напрягать силы; мудрость умеет это исправить.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25875, 'rst66', 'ecc', 10, 11, 'Если змей ужалит без заговаривания, то не лучше его и злоязычный.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25876, 'rst66', 'ecc', 10, 12, 'Слова из уст мудрого &mdash; благодать, а уста глупого губят его же:');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25877, 'rst66', 'ecc', 10, 13, 'начало слов из уст его &mdash; глупость, <i>а</i> конец речи из уст его &mdash; безумие.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25878, 'rst66', 'ecc', 10, 14, 'Глупый наговорит много, <i>хотя</i> человек не знает, что будет, и кто скажет ему, что будет после него?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25879, 'rst66', 'ecc', 10, 15, 'Труд глупого утомляет его, потому что не знает <i>даже</i> дороги в город.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25880, 'rst66', 'ecc', 10, 16, 'Горе тебе, земля, когда царь твой отрок, и когда князья твои едят рано!');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25881, 'rst66', 'ecc', 10, 17, 'Благо тебе, земля, когда царь у тебя из благородного рода, и князья твои едят вовремя, для подкрепления, а не для пресыщения!');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25882, 'rst66', 'ecc', 10, 18, 'От лености обвиснет потолок, и когда опустятся руки, то протечет дом.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25883, 'rst66', 'ecc', 10, 19, 'Пиры устраиваются для удовольствия, и вино веселит жизнь; а за все отвечает серебро.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25884, 'rst66', 'ecc', 10, 20, 'Даже и в мыслях твоих не злословь царя, и в спальной комнате твоей не злословь богатого; потому что птица небесная может перенести слово <i>твое</i>, и крылатая &mdash; пересказать речь <i>твою</i>.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25885, 'rst66', 'ecc', 11, 1, 'Отпускай хлеб твой по водам, потому что по прошествии многих дней опять найдешь его.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25886, 'rst66', 'ecc', 11, 2, 'Давай часть семи и даже восьми, потому что не знаешь, какая беда будет на земле.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25887, 'rst66', 'ecc', 11, 3, 'Когда облака будут полны, то они прольют на землю дождь; и если упадет дерево на юг или на север, то оно там и останется, куда упадет.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25888, 'rst66', 'ecc', 11, 4, 'Кто наблюдает ветер, тому не сеять; и кто смотрит на облака, тому не жать.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25889, 'rst66', 'ecc', 11, 5, 'Как ты не знаешь путей ветра и того, как <i>образуются</i> кости во чреве беременной, так не можешь знать дело Бога, Который делает все.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25890, 'rst66', 'ecc', 11, 6, 'Утром сей семя твое, и вечером не давай отдыха руке твоей, потому что ты не знаешь, то или другое будет удачнее, или то и другое равно хорошо будет.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25891, 'rst66', 'ecc', 11, 7, 'Сладок свет, и приятно для глаз видеть солнце.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25892, 'rst66', 'ecc', 11, 8, 'Если человек проживет <i>и</i> много лет, то пусть веселится он в продолжение всех их, и пусть помнит о днях темных, которых будет много: все, что будет, &mdash; суета!');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25893, 'rst66', 'ecc', 11, 9, 'Веселись, юноша, в юности твоей, и да вкушает сердце твое радости во дни юности твоей, и ходи по путям сердца твоего и по видению очей твоих; только знай, что за все это Бог приведет тебя на суд.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25894, 'rst66', 'ecc', 11, 10, 'И удаляй печаль от сердца твоего, и уклоняй злое от тела твоего, потому что детство и юность &mdash; суета.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25895, 'rst66', 'ecc', 12, 1, 'И помни Создателя твоего в дни юности твоей, доколе не пришли тяжелые дни и не наступили годы, о которых ты будешь говорить: «нет мне удовольствия в них!»');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25896, 'rst66', 'ecc', 12, 2, 'Доколе не померкли солнце и свет и луна и звезды, и не нашли новые тучи вслед за дождем.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25897, 'rst66', 'ecc', 12, 3, 'В тот день, когда задрожат стерегущие дом и согнутся мужи силы; и перестанут молоть мелющие, потому что их немного осталось; и помрачатся смотрящие в окно;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25898, 'rst66', 'ecc', 12, 4, 'и запираться будут двери на улицу; когда замолкнет звук жернова, и будет вставать <i>человек</i> по крику петуха и замолкнут дщери пения;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25899, 'rst66', 'ecc', 12, 5, 'и высоты будут им страшны, и на дороге ужасы; и зацветет миндаль, и отяжелеет кузнечик, и рассыплется каперс. Ибо отходит человек в вечный дом свой, и готовы окружить его по улице плакальщицы; &mdash;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25900, 'rst66', 'ecc', 12, 6, 'доколе не порвалась серебряная цепочка, и не разорвалась золотая повязка, и не разбился кувшин у источника, и не обрушилось колесо над колодезем.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25901, 'rst66', 'ecc', 12, 7, 'И возвратится прах в землю, чем он и был; а дух возвратится к Богу, Который дал его.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25902, 'rst66', 'ecc', 12, 8, 'Суета сует, сказал Екклесиаст, всё &mdash; суета!');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25903, 'rst66', 'ecc', 12, 9, 'Кроме того, что Екклесиаст был мудр, он учил еще народ знанию. Он <i>все</i> испытывал, исследовал, <i>и</i> составил много притчей.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25904, 'rst66', 'ecc', 12, 10, 'Старался Екклесиаст приискивать изящные изречения, и слова истины написаны <i>им</i> верно.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25905, 'rst66', 'ecc', 12, 11, 'Слова мудрых &mdash; как иглы и как вбитые гвозди, и составители их &mdash; от единого пастыря.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25906, 'rst66', 'ecc', 12, 12, 'А что сверх всего этого, сын мой, того берегись: составлять много книг &mdash; конца не будет, и много читать &mdash; утомительно для тела.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25907, 'rst66', 'ecc', 12, 13, 'Выслушаем сущность всего: бойся Бога и заповеди Его соблюдай, потому что в этом всё для человека;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25908, 'rst66', 'ecc', 12, 14, 'ибо всякое дело Бог приведет на суд, и все тайное, хорошо ли оно, или худо.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25909, 'rst66', 'sng', 1, 1, 'Да лобзает он меня лобзанием уст своих! Ибо ласки твои лучше вина.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25910, 'rst66', 'sng', 1, 2, 'От благовония мастей твоих имя твое &mdash; как разлитое миро; поэтому девицы любят тебя.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25911, 'rst66', 'sng', 1, 3, 'Влеки меня, мы побежим за тобою; &mdash; царь ввел меня в чертоги свои, &mdash; будем восхищаться и радоваться тобою, превозносить ласки твои больше, нежели вино; достойно любят тебя!');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25912, 'rst66', 'sng', 1, 4, '&mdash; Дщери Иерусалимские! черна я, но красива, как шатры Кидарские, как завесы Соломоновы.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25913, 'rst66', 'sng', 1, 5, 'Не смотрите на меня, что я смугла, ибо солнце опалило меня: сыновья матери моей разгневались на меня, поставили меня стеречь виноградники, &mdash; моего собственного виноградника я не стерегла.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25914, 'rst66', 'sng', 1, 6, '&mdash; Скажи мне, ты, которого любит душа моя: где пасешь ты? где отдыхаешь в полдень? к чему мне быть скиталицею возле стад товарищей твоих?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25915, 'rst66', 'sng', 1, 7, '&mdash; Если ты не знаешь этого, прекраснейшая из женщин, то иди себе по следам овец и паси козлят твоих подле шатров пастушеских.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25916, 'rst66', 'sng', 1, 8, '&mdash; Кобылице моей в колеснице фараоновой я уподобил тебя, возлюбленная моя.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25917, 'rst66', 'sng', 1, 9, 'Прекрасны ланиты твои под подвесками, шея твоя в ожерельях;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25918, 'rst66', 'sng', 1, 10, 'золотые подвески мы сделаем тебе с серебряными блестками.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25919, 'rst66', 'sng', 1, 11, '&mdash; Доколе царь был за столом своим, нард мой издавал благовоние свое.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25920, 'rst66', 'sng', 1, 12, 'Мирровый пучок &mdash; возлюбленный мой у меня, у грудей моих пребывает.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25921, 'rst66', 'sng', 1, 13, 'Как кисть кипера, возлюбленный мой у меня в виноградниках Енгедских.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25922, 'rst66', 'sng', 1, 14, '&mdash; О, ты прекрасна, возлюбленная моя, ты прекрасна! глаза твои голубиные.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25923, 'rst66', 'sng', 1, 15, '&mdash; О, ты прекрасен, возлюбленный мой, и любезен! и ложе у нас &mdash; зелень;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25924, 'rst66', 'sng', 1, 16, 'кровли домов наших &mdash; кедры, потолки наши &mdash; кипарисы.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25925, 'rst66', 'sng', 2, 1, '&mdash; Я нарцисс Саронский, лилия долин!');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25926, 'rst66', 'sng', 2, 2, '&mdash; Что лилия между тернами, то возлюбленная моя между девицами.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25927, 'rst66', 'sng', 2, 3, '&mdash; Что яблоня между лесными деревьями, то возлюбленный мой между юношами. В тени ее люблю я сидеть, и плоды ее сладки для гортани моей.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25928, 'rst66', 'sng', 2, 4, 'Он ввел меня в дом пира, и знамя его надо мною &mdash; любовь.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25929, 'rst66', 'sng', 2, 5, 'Подкрепите меня вином, освежите меня яблоками, ибо я изнемогаю от любви.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25930, 'rst66', 'sng', 2, 6, 'Левая рука его у меня под головою, а правая обнимает меня.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25931, 'rst66', 'sng', 2, 7, 'Заклинаю вас, дщери Иерусалимские, сернами или полевыми ланями: не будите и не тревожьте возлюбленной, доколе ей угодно.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25932, 'rst66', 'sng', 2, 8, '&mdash; Голос возлюбленного моего! вот, он идет, скачет по горам, прыгает по холмам.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25933, 'rst66', 'sng', 2, 9, 'Друг мой похож на серну или на молодого оленя. Вот, он стоит у нас за стеною, заглядывает в окно, мелькает сквозь решетку.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25934, 'rst66', 'sng', 2, 10, 'Возлюбленный мой начал говорить мне: встань, возлюбленная моя, прекрасная моя, выйди!');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25935, 'rst66', 'sng', 2, 11, 'Вот, зима уже прошла; дождь миновал, перестал;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25936, 'rst66', 'sng', 2, 12, 'цветы показались на земле; время пения настало, и голос горлицы слышен в стране нашей;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25937, 'rst66', 'sng', 2, 13, 'смоковницы распустили свои почки, и виноградные лозы, расцветая, издают благовоние. Встань, возлюбленная моя, прекрасная моя, выйди!');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25938, 'rst66', 'sng', 2, 14, 'Голубица моя в ущелье скалы под кровом утеса! покажи мне лицо твое, дай мне услышать голос твой, потому что голос твой сладок и лицо твое приятно.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25939, 'rst66', 'sng', 2, 15, '&mdash; Ловите нам лисиц, лисенят, которые портят виноградники, а виноградники наши в цвете.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25940, 'rst66', 'sng', 2, 16, '&mdash; Возлюбленный мой принадлежит мне, а я ему; он пасет между лилиями.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25941, 'rst66', 'sng', 2, 17, 'Доколе день дышит <i>прохладою</i>, и убегают тени, возвратись, будь подобен серне или молодому оленю на расселинах гор.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25942, 'rst66', 'sng', 3, 1, '&mdash; На ложе моем ночью искала я того, которого любит душа моя, искала его и не нашла его.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25943, 'rst66', 'sng', 3, 2, 'Встану же я, пойду по городу, по улицам и площадям, и буду искать того, которого любит душа моя; искала я его и не нашла его.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25944, 'rst66', 'sng', 3, 3, 'Встретили меня стражи, обходящие город: «не видали ли вы того, которого любит душа моя?»');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25945, 'rst66', 'sng', 3, 4, 'Но едва я отошла от них, как нашла того, которого любит душа моя, ухватилась за него, и не отпустила его, доколе не привела его в дом матери моей и во внутренние комнаты родительницы моей.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25946, 'rst66', 'sng', 3, 5, 'Заклинаю вас, дщери Иерусалимские, сернами или полевыми ланями: не будите и не тревожьте возлюбленной, доколе ей угодно.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25947, 'rst66', 'sng', 3, 6, '&mdash; Кто эта, восходящая от пустыни как бы столбы дыма, окуриваемая миррою и фимиамом, всякими порошками мироварника?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25948, 'rst66', 'sng', 3, 7, 'Вот одр его &mdash; Соломона: шестьдесят сильных вокруг него, из сильных Израилевых.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25949, 'rst66', 'sng', 3, 8, 'Все они держат по мечу, опытны в бою; у каждого меч при бедре его ради страха ночного.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25950, 'rst66', 'sng', 3, 9, 'Носильный одр сделал себе царь Соломон из дерев Ливанских;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25951, 'rst66', 'sng', 3, 10, 'столпцы его сделал из серебра, локотники его из золота, седалище его из пурпуровой ткани; внутренность его убрана с любовью дщерями Иерусалимскими.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25952, 'rst66', 'sng', 3, 11, 'Пойдите и посмотрите, дщери Сионские, на царя Соломона в венце, которым увенчала его мать его в день бракосочетания его, в день, радостный для сердца его.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25953, 'rst66', 'sng', 4, 1, '&mdash; О, ты прекрасна, возлюбленная моя, ты прекрасна! глаза твои голубиные под кудрями твоими; волосы твои &mdash; как стадо коз, сходящих с горы Галаадской;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25954, 'rst66', 'sng', 4, 2, 'зубы твои &mdash; как стадо выстриженных овец, выходящих из купальни, из которых у каждой пара ягнят, и бесплодной нет между ними;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25955, 'rst66', 'sng', 4, 3, 'как лента алая губы твои, и уста твои любезны; как половинки гранатового яблока &mdash; ланиты твои под кудрями твоими;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25956, 'rst66', 'sng', 4, 4, 'шея твоя &mdash; как столп Давидов, сооруженный для оружий, тысяча щитов висит на нем &mdash; все щиты сильных;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25957, 'rst66', 'sng', 4, 5, 'два сосца твои &mdash; как двойни молодой серны, пасущиеся между лилиями.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25958, 'rst66', 'sng', 4, 6, 'Доколе день дышит <i>прохладою</i>, и убегают тени, пойду я на гору мирровую и на холм фимиама.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25959, 'rst66', 'sng', 4, 7, 'Вся ты прекрасна, возлюбленная моя, и пятна нет на тебе!');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25960, 'rst66', 'sng', 4, 8, 'Со мною с Ливана, невеста! со мною иди с Ливана! спеши с вершины Аманы, с вершины Сенира и Ермона, от логовищ львиных, от гор барсовых!');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25961, 'rst66', 'sng', 4, 9, 'Пленила ты сердце мое, сестра моя, невеста! пленила ты сердце мое одним взглядом очей твоих, одним ожерельем на шее твоей.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25962, 'rst66', 'sng', 4, 10, 'О, как любезны ласки твои, сестра моя, невеста! о, как много ласки твои лучше вина, и благовоние мастей твоих лучше всех ароматов!');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25963, 'rst66', 'sng', 4, 11, 'Сотовый мед каплет из уст твоих, невеста; мед и молоко под языком твоим, и благоухание одежды твоей подобно благоуханию Ливана!');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25964, 'rst66', 'sng', 4, 12, 'Запертый сад &mdash; сестра моя, невеста, заключенный колодезь, запечатанный источник:');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25965, 'rst66', 'sng', 4, 13, 'рассадники твои &mdash; сад с гранатовыми яблоками, с превосходными плодами, киперы с нардами,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25966, 'rst66', 'sng', 4, 14, 'нард и шафран, аир и корица со всякими благовонными деревами, мирра и алой со всякими лучшими ароматами;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25967, 'rst66', 'sng', 4, 15, 'садовый источник &mdash; колодезь живых вод и потоки с Ливана.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25968, 'rst66', 'sng', 4, 16, 'Поднимись, <i>ветер</i>, с севера и принесись с юга, повей на сад мой, &mdash; и польются ароматы его! &mdash; Пусть придет возлюбленный мой в сад свой и вкушает сладкие плоды его.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25969, 'rst66', 'sng', 5, 1, 'Пришел я в сад мой, сестра моя, невеста; набрал мирры моей с ароматами моими, поел сотов моих с медом моим, напился вина моего с молоком моим. &mdash; Ешьте, друзья, пейте и насыщайтесь, возлюбленные!');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25970, 'rst66', 'sng', 5, 2, '&mdash; Я сплю, а сердце мое бодрствует; <i>вот</i>, голос моего возлюбленного, который стучится: «отвори мне, сестра моя, возлюбленная моя, голубица моя, чистая моя! потому что голова моя вся покрыта росою, кудри мои &mdash; ночною влагою».');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25971, 'rst66', 'sng', 5, 3, 'Я скинула хитон мой; как же мне опять надевать его? Я вымыла ноги мои; как же мне марать их?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25972, 'rst66', 'sng', 5, 4, 'Возлюбленный мой протянул руку свою сквозь скважину, и внутренность моя взволновалась от него.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25973, 'rst66', 'sng', 5, 5, 'Я встала, чтобы отпереть возлюбленному моему, и с рук моих капала мирра, и с перстов моих мирра капала на ручки замка.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25974, 'rst66', 'sng', 5, 6, 'Отперла я возлюбленному моему, а возлюбленный мой повернулся и ушел. Души во мне не стало, когда он говорил; я искала его и не находила его; звала его, и он не отзывался мне.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25975, 'rst66', 'sng', 5, 7, 'Встретили меня стражи, обходящие город, избили меня, изранили меня; сняли с меня покрывало стерегущие стены.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25976, 'rst66', 'sng', 5, 8, 'Заклинаю вас, дщери Иерусалимские: если вы встретите возлюбленного моего, что скажете вы ему? что я изнемогаю от любви.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25977, 'rst66', 'sng', 5, 9, '&mdash; Чем возлюбленный твой лучше других возлюбленных, прекраснейшая из женщин? Чем возлюбленный твой лучше других, что ты так заклинаешь нас?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25978, 'rst66', 'sng', 5, 10, '&mdash; Возлюбленный мой бел и румян, лучше десяти тысяч других:');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25979, 'rst66', 'sng', 5, 11, 'голова его &mdash; чистое золото; кудри его волнистые, черные, как ворон;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25980, 'rst66', 'sng', 5, 12, 'глаза его &mdash; как голуби при потоках вод, купающиеся в молоке, сидящие в довольстве;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25981, 'rst66', 'sng', 5, 13, 'щеки его &mdash; цветник ароматный, гряды благовонных растений; губы его &mdash; лилии, источают текучую мирру;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25982, 'rst66', 'sng', 5, 14, 'руки его &mdash; золотые кругляки, усаженные топазами; живот его &mdash; как изваяние из слоновой кости, обложенное сапфирами;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25983, 'rst66', 'sng', 5, 15, 'голени его &mdash; мраморные столбы, поставленные на золотых подножиях; вид его подобен Ливану, величествен, как кедры;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25984, 'rst66', 'sng', 5, 16, 'уста его &mdash; сладость, и весь он &mdash; любезность. Вот кто возлюбленный мой, и вот кто друг мой, дщери Иерусалимские!');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25985, 'rst66', 'sng', 6, 1, '&mdash; Куда пошел возлюбленный твой, прекраснейшая из женщин? куда обратился возлюбленный твой? мы поищем его с тобою.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25986, 'rst66', 'sng', 6, 2, '&mdash; Мой возлюбленный пошел в сад свой, в цветники ароматные, чтобы пасти в садах и собирать лилии.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25987, 'rst66', 'sng', 6, 3, 'Я принадлежу возлюбленному моему, а возлюбленный мой &mdash; мне; он пасет между лилиями.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25988, 'rst66', 'sng', 6, 4, '&mdash; Прекрасна ты, возлюбленная моя, как Фирца, любезна, как Иерусалим, грозна, как полки со знаменами.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25989, 'rst66', 'sng', 6, 5, 'Уклони очи твои от меня, потому что они волнуют меня.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25990, 'rst66', 'sng', 6, 6, 'Волосы твои &mdash; как стадо коз, сходящих с Галаада; зубы твои &mdash; как стадо овец, выходящих из купальни, из которых у каждой пара ягнят, и бесплодной нет между ними;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25991, 'rst66', 'sng', 6, 7, 'как половинки гранатового яблока &mdash; ланиты твои под кудрями твоими.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25992, 'rst66', 'sng', 6, 8, 'Есть шестьдесят цариц и восемьдесят наложниц и девиц без числа,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25993, 'rst66', 'sng', 6, 9, 'но единственная &mdash; она, голубица моя, чистая моя; единственная она у матери своей, отличенная у родительницы своей. Увидели ее девицы, и &mdash; превознесли ее, царицы и наложницы, и &mdash; восхвалили ее.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25994, 'rst66', 'sng', 6, 10, 'Кто эта, блистающая, как заря, прекрасная, как луна, светлая, как солнце, грозная, как полки со знаменами?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25995, 'rst66', 'sng', 6, 11, '&mdash; Я сошла в ореховый сад посмотреть на зелень долины, поглядеть, распустилась ли виноградная лоза, расцвели ли гранатовые яблоки?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25996, 'rst66', 'sng', 6, 12, 'Не знаю, как душа моя влекла меня к колесницам знатных народа моего.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25997, 'rst66', 'sng', 7, 1, '&mdash; «Оглянись, оглянись, Суламита! оглянись, оглянись, &mdash; и мы посмотрим на тебя». &mdash; Что вам смотреть на Суламиту, как на хоровод Манаимский?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25998, 'rst66', 'sng', 7, 2, '&mdash; О, как прекрасны ноги твои в сандалиях, дщерь именитая! Округление бедр твоих, как ожерелье, дело рук искусного художника;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (25999, 'rst66', 'sng', 7, 3, 'живот твой &mdash; круглая чаша, <i>в которой</i> не истощается ароматное вино; чрево твое &mdash; ворох пшеницы, обставленный лилиями;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (26000, 'rst66', 'sng', 7, 4, 'два сосца твои &mdash; как два козленка, двойни серны;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (26001, 'rst66', 'sng', 7, 5, 'шея твоя &mdash; как столп из слоновой кости; глаза твои &mdash; озерки Есевонские, что у ворот Батраббима; нос твой &mdash; башня Ливанская, обращенная к Дамаску;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (26002, 'rst66', 'sng', 7, 6, 'голова твоя на тебе, как Кармил, и волосы на голове твоей, как пурпур; царь увлечен <i>твоими</i> кудрями.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (26003, 'rst66', 'sng', 7, 7, 'Как ты прекрасна, как привлекательна, возлюбленная, твоею миловидностью!');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (26004, 'rst66', 'sng', 7, 8, 'Этот стан твой похож на пальму, и груди твои &mdash; на виноградные кисти.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (26005, 'rst66', 'sng', 7, 9, 'Подумал я: влез бы я на пальму, ухватился бы за ветви ее; и груди твои были бы вместо кистей винограда, и запах от ноздрей твоих, как от яблок;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (26006, 'rst66', 'sng', 7, 10, 'уста твои &mdash; как отличное вино. Оно течет прямо к другу моему, услаждает уста утомленных.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (26007, 'rst66', 'sng', 7, 11, '&mdash; Я принадлежу другу моему, и ко мне <i>обращено</i> желание его.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (26008, 'rst66', 'sng', 7, 12, 'Приди, возлюбленный мой, выйдем в поле, побудем в селах;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (26009, 'rst66', 'sng', 7, 13, 'поутру пойдем в виноградники, посмотрим, распустилась ли виноградная лоза, раскрылись ли почки, расцвели ли гранатовые яблони; там я окажу ласки мои тебе.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (26010, 'rst66', 'sng', 7, 14, 'Мандрагоры уже пустили благовоние, и у дверей наших всякие превосходные плоды, новые и старые: <i>это</i> сберегла я для тебя, мой возлюбленный!');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (26011, 'rst66', 'sng', 8, 1, 'О, если бы ты был мне брат, сосавший груди матери моей! тогда я, встретив тебя на улице, целовала бы тебя, и меня не осуждали бы.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (26012, 'rst66', 'sng', 8, 2, 'Повела бы я тебя, привела бы тебя в дом матери моей. Ты учил бы меня, а я поила бы тебя ароматным вином, соком гранатовых яблок моих.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (26013, 'rst66', 'sng', 8, 3, 'Левая рука его у меня под головою, а правая обнимает меня.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (26014, 'rst66', 'sng', 8, 4, 'Заклинаю вас, дщери Иерусалимские, не будите и не тревожьте возлюбленной, доколе ей угодно.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (26015, 'rst66', 'sng', 8, 5, '&mdash; Кто это восходит от пустыни, опираясь на своего возлюбленного? &mdash; Под яблоней разбудила я тебя: там родила тебя мать твоя, там родила тебя родительница твоя.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (26016, 'rst66', 'sng', 8, 6, 'Положи меня, как печать, на сердце твое, как перстень, на руку твою: ибо крепка, как смерть, любовь; люта, как преисподняя, ревность; стрелы ее &mdash; стрелы огненные; она пламень весьма сильный.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (26017, 'rst66', 'sng', 8, 7, 'Большие воды не могут потушить любви, и реки не зальют ее. Если бы кто давал все богатство дома своего за любовь, то он был бы отвергнут с презреньем.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (26018, 'rst66', 'sng', 8, 8, '&mdash; Есть у нас сестра, которая еще мала, и сосцов нет у нее; что нам будет делать с сестрою нашею, когда будут свататься за нее?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (26019, 'rst66', 'sng', 8, 9, 'Если бы она была стена, то мы построили бы на ней палаты из серебра; если бы она была дверь, то мы обложили бы ее кедровыми досками.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (26020, 'rst66', 'sng', 8, 10, '&mdash; Я стена, и сосцы у меня, как башни; потому я буду в глазах его, как достигшая полноты.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (26021, 'rst66', 'sng', 8, 11, '&mdash; Виноградник был у Соломона в Ваал‐Гамоне; он отдал этот виноградник сторожам; каждый должен был доставлять за плоды его тысячу сребреников.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (26022, 'rst66', 'sng', 8, 12, 'А мой виноградник у меня при себе. Тысяча пусть тебе, Соломон, а двести &mdash; стерегущим плоды его.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (26023, 'rst66', 'sng', 8, 13, '&mdash; Жительница садов! товарищи внимают голосу твоему, дай и мне послушать его.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (26024, 'rst66', 'sng', 8, 14, '&mdash; Беги, возлюбленный мой; будь подобен серне или молодому оленю на горах бальзамических!');",
