        $list = [
            "INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36626, 'rst66', 'act', 28, 31, 'проповедуя Царствие Божие и уча о Господе Иисусе Христе со всяким дерзновением невозбранно.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36625, 'rst66', 'act', 28, 30, 'И жил Павел целых два года на своем иждивении и принимал всех, приходивших к нему,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36624, 'rst66', 'act', 28, 29, 'Когда он сказал это, Иудеи ушли, много споря между собою.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36623, 'rst66', 'act', 28, 28, 'Итак, да будет вам известно, что спасение Божие послано язычникам: они и услышат.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36622, 'rst66', 'act', 28, 27, 'Ибо огрубело сердце людей сих, и ушами с трудом слышат, и очи свои сомкнули, да не узрят очами, и не услышат ушами, и не уразумеют сердцем, и не обратятся, чтобы Я исцелил их».');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36621, 'rst66', 'act', 28, 26, '«пойди к народу сему и скажи: слухом услышите и не уразумеете, и очами смотреть будете и не увидите.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36620, 'rst66', 'act', 28, 25, 'Будучи же не согласны между собою, они уходили, когда Павел сказал следующие слова: хорошо Дух Святой сказал отцам нашим через пророка Исаию:');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36619, 'rst66', 'act', 28, 24, 'Одни убеждались словами его, а другие не верили.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36618, 'rst66', 'act', 28, 23, 'И, назначив ему день, очень многие пришли к нему в гостиницу; и он от утра до вечера излагал им <i>учение</i> о Царствии Божием, приводя свидетельства и удостоверяя их о Иисусе из закона Моисеева и пророков.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36617, 'rst66', 'act', 28, 22, 'Впрочем, желательно нам слышать от тебя, как ты мыслишь; ибо известно нам, что об этом учении везде спорят.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36616, 'rst66', 'act', 28, 21, 'Они же сказали ему: мы ни писем не получали о тебе из Иудеи, ни из приходящих братьев никто не известил о тебе и не сказал чего‐либо худого.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36615, 'rst66', 'act', 28, 20, 'По этой причине я и призвал вас, чтобы увидеться и поговорить с вами, ибо за надежду Израилеву обложен я этими узами.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36614, 'rst66', 'act', 28, 19, 'но так как Иудеи противоречили, то я принужден был потребовать суда у кесаря, впрочем, не с тем, чтобы обвинить в чем‐либо мой народ.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36613, 'rst66', 'act', 28, 18, 'Они, судив меня, хотели освободить, потому что нет во мне никакой вины, достойной смерти;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36612, 'rst66', 'act', 28, 17, 'Через три дня Павел созвал знатнейших из Иудеев и, когда они сошлись, говорил им: мужи братия! не сделав ничего против народа или отеческих обычаев, я в узах из Иерусалима предан в руки Римлян.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36611, 'rst66', 'act', 28, 16, 'Когда же пришли мы в Рим, то сотник передал узников военачальнику, а Павлу позволено жить особо с воином, стерегущим его.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36610, 'rst66', 'act', 28, 15, 'Тамошние братья, услышав о нас, вышли нам навстречу до Аппиевой площади и Трех Гостиниц. Увидев их, Павел возблагодарил Бога и ободрился.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36609, 'rst66', 'act', 28, 14, 'где нашли братьев, и были упрошены пробыть у них семь дней, а потом пошли в Рим.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36608, 'rst66', 'act', 28, 13, 'Оттуда отплыв, прибыли в Ригию; и как через день подул южный ветер, прибыли на второй день в Путеол,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36607, 'rst66', 'act', 28, 12, 'и, приплыв в Сиракузы, пробыли там три дня.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36606, 'rst66', 'act', 28, 11, 'Через три месяца мы отплыли на Александрийском корабле, называемом Диоскуры, зимовавшем на том острове,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36605, 'rst66', 'act', 28, 10, 'и оказывали нам много почести и при отъезде снабдили нужным.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36604, 'rst66', 'act', 28, 9, 'После сего события и прочие на острове, имевшие болезни, приходили и были исцеляемы,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36603, 'rst66', 'act', 28, 8, 'Отец Публия лежал, страдая горячкою и болью в животе; Павел вошел к нему, помолился и, возложив на него руки свои, исцелил его.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36602, 'rst66', 'act', 28, 7, 'Около того места были поместья начальника острова, именем Публия; он принял нас и три дня дружелюбно угощал.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36601, 'rst66', 'act', 28, 6, 'Они ожидали было, что у него будет воспаление или он внезапно упадет мертвым; но, ожидая долго и видя, что не случилось с ним никакой беды, переменили мысли и говорили, что он Бог.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36600, 'rst66', 'act', 28, 5, 'Но он, стряхнув змею в огонь, не потерпел никакого вреда.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36599, 'rst66', 'act', 28, 4, 'Иноплеменники, когда увидели висящую на руке его змею, говорили друг другу: верно, этот человек убийца, когда его, спасшегося от моря, суд <i>Божий</i> не оставляет жить.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36598, 'rst66', 'act', 28, 3, 'Когда же Павел набрал множество хвороста и клал на огонь, тогда ехидна, выйдя от жара, повисла на руке его.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36597, 'rst66', 'act', 28, 2, 'Иноплеменники оказали нам немалое человеколюбие, ибо они, по причине бывшего дождя и холода, разложили огонь и приняли всех нас.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36596, 'rst66', 'act', 28, 1, 'Спасшись же, бывшие с Павлом узнали, что остров называется Мелит.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36595, 'rst66', 'act', 27, 44, 'прочим же <i>спасаться</i> кому на досках, а кому на чем‐нибудь от корабля; и таким образом все спаслись на землю.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36594, 'rst66', 'act', 27, 43, 'Но сотник, желая спасти Павла, удержал их от сего намерения и велел умеющим плавать первым броситься и выйти на землю,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36593, 'rst66', 'act', 27, 42, 'Воины согласились было умертвить узников, чтобы кто‐нибудь, выплыв, не убежал.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36592, 'rst66', 'act', 27, 41, 'Попали на косу, и корабль сел на мель. Нос увяз и остался недвижим, а корма разбивалась силою волн.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36591, 'rst66', 'act', 27, 40, 'И, подняв якори, пошли по морю и, развязав рули и подняв малый парус по ветру, держали к берегу.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36590, 'rst66', 'act', 27, 39, 'Когда настал день, земли&#769; не узнавали, а усмотрели только некоторый залив, имеющий <i>отлогий</i> берег, к которому и решились, если можно, пристать с кораблем.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36589, 'rst66', 'act', 27, 38, 'Насытившись же пищею, стали облегчать корабль, выкидывая пшеницу в море.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36588, 'rst66', 'act', 27, 37, 'Было же всех нас на корабле двести семьдесят шесть душ.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36587, 'rst66', 'act', 27, 36, 'Тогда все ободрились и также приняли пищу.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36586, 'rst66', 'act', 27, 35, 'Сказав это и взяв хлеб, он возблагодарил Бога перед всеми и, разломив, начал есть.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36585, 'rst66', 'act', 27, 34, 'Потому прошу вас принять пищу: это послужит к сохранению вашей жизни; ибо ни у кого из вас не пропадет волос с головы.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36584, 'rst66', 'act', 27, 33, 'Перед наступлением дня Павел уговаривал всех принять пищу, говоря: сегодня четырнадцатый день, как вы, в ожидании, остаетесь без пищи, не вкушая ничего.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36583, 'rst66', 'act', 27, 32, 'Тогда воины отсекли веревки у лодки, и она упала.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36582, 'rst66', 'act', 27, 31, 'Павел сказал сотнику и воинам: если они не останутся на корабле, то вы не можете спастись.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36581, 'rst66', 'act', 27, 30, 'Когда же корабельщики хотели бежать с корабля и спускали на море лодку, делая вид, будто хотят бросить якоря с носа,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36580, 'rst66', 'act', 27, 29, 'Опасаясь, чтобы не попасть на каменистые места, бросили с кормы четыре якоря и ожидали дня.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36579, 'rst66', 'act', 27, 28, 'и, вымерив глубину, нашли двадцать сажен; потом на небольшом расстоянии, вымерив опять, нашли пятнадцать сажен.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36578, 'rst66', 'act', 27, 27, 'В четырнадцатую ночь, как мы носимы были в Адриатическом море, около полуночи корабельщики стали догадываться, что приближаются к какой‐то земле,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36577, 'rst66', 'act', 27, 26, 'нам должно быть выброшенными на какой‐нибудь остров.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36576, 'rst66', 'act', 27, 25, 'Посему ободритесь, мужи, ибо я верю Богу, что будет так, как мне сказано:');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36575, 'rst66', 'act', 27, 24, 'и сказал: «не бойся, Павел! тебе должно предстать пред кесаря, и вот, Бог даровал тебе всех плывущих с тобою».');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36574, 'rst66', 'act', 27, 23, 'Ибо Ангел Бога, Которому принадлежу я и Которому служу, явился мне в эту ночь');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36573, 'rst66', 'act', 27, 22, 'Теперь же убеждаю вас ободриться, потому что ни одна душа из вас не погибнет, а только корабль.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36572, 'rst66', 'act', 27, 21, 'И как долго не ели, то Павел, став посреди них, сказал: мужи! надлежало послушаться меня и не отходить от Крита, чем и избежали бы сих затруднений и вреда.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36571, 'rst66', 'act', 27, 20, 'Но как многие дни не видно было ни солнца, ни звезд и продолжалась немалая буря, то наконец исчезала всякая надежда к нашему спасению.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36570, 'rst66', 'act', 27, 19, 'а на третий мы своими руками побросали с корабля вещи.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36569, 'rst66', 'act', 27, 18, 'На другой день, по причине сильного обуревания, начали выбрасывать <i>груз</i>,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36568, 'rst66', 'act', 27, 17, 'Подняв ее, стали употреблять пособия и обвязывать корабль; боясь же, чтобы не сесть на мель, спустили парус и таким образом носились.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36567, 'rst66', 'act', 27, 16, 'И, набежав на один островок, называемый Кла&#769;вдой, мы едва могли удержать лодку.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36566, 'rst66', 'act', 27, 15, 'Корабль схватило так, что он не мог противиться ветру, и мы носились, отдавшись волнам.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36565, 'rst66', 'act', 27, 14, 'Но скоро поднялся против него ветер бурный, называемый эвроклидон.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36564, 'rst66', 'act', 27, 13, 'Подул южный ветер, и они, подумав, что уже получили желаемое, отправились и поплыли поблизости Крита.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36563, 'rst66', 'act', 27, 12, 'А как пристань не была приспособлена к зимовке, то многие давали совет отправиться оттуда, чтобы, если можно, дойти до Финика, пристани Критской, лежащей против юго‐западного и северо‐западного ветра, и <i>там</i> перезимовать.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36562, 'rst66', 'act', 27, 11, 'Но сотник более доверял кормчему и начальнику корабля, нежели словам Павла.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36561, 'rst66', 'act', 27, 10, 'говоря им: мужи! я вижу, что плавание будет с затруднениями и с большим вредом не только для груза и корабля, но и для нашей жизни.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36560, 'rst66', 'act', 27, 9, 'Но как прошло довольно времени, и плавание было уже опасно, потому что и пост уже прошел, то Павел советовал,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36559, 'rst66', 'act', 27, 8, 'Пробравшись же с трудом мимо него, прибыли к одному месту, называемому Хорошие Пристани, близ которого был город Ласея.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36558, 'rst66', 'act', 27, 7, 'Медленно плавая многие дни и едва поравнявшись с Книдом, по причине неблагоприятного нам ветра, мы подплыли к Криту при Салмоне.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36557, 'rst66', 'act', 27, 6, 'Там сотник нашел Александрийский корабль, плывущий в Италию, и посадил нас на него.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36556, 'rst66', 'act', 27, 5, 'и, переплыв море против Киликии и Памфилии, прибыли в Миры Ликийские.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36555, 'rst66', 'act', 27, 4, 'Отправившись оттуда, мы приплыли в Кипр, по причине противных ветров,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36554, 'rst66', 'act', 27, 3, 'На другой <i>день</i> пристали к Сидону. Юлий, поступая с Павлом человеколюбиво, позволил ему сходить к друзьям и воспользоваться их усердием.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36553, 'rst66', 'act', 27, 2, 'Мы взошли на Адрамитский корабль и отправились, намереваясь плыть около Асийских мест. С нами был Аристарх, Македонянин из Фессалоники.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36552, 'rst66', 'act', 27, 1, 'Когда решено было плыть нам в Италию, то отдали Павла и некоторых других узников сотнику Августова полка, именем Юлию.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36551, 'rst66', 'act', 26, 32, 'И сказал Агриппа Фесту: можно было бы освободить этого человека, если бы он не потребовал суда у кесаря. Посему и решился правитель послать его к кесарю.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36550, 'rst66', 'act', 26, 31, 'и, отойдя в сторону, говорили между собою, что этот человек ничего, достойного смерти или уз, не делает.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36549, 'rst66', 'act', 26, 30, 'Когда он сказал это, царь и правитель, Вереника и сидевшие с ними встали;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36548, 'rst66', 'act', 26, 29, 'Павел сказал: молил бы я Бога, чтобы мало ли, много ли, не только ты, но и все, слушающие меня сегодня, сделались такими, как я, кроме этих уз.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36547, 'rst66', 'act', 26, 28, 'Агриппа сказал Павлу: ты немного не убеждаешь меня сделаться Христианином.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36546, 'rst66', 'act', 26, 27, 'Веришь ли, царь Агриппа, пророкам? Знаю, что веришь.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36545, 'rst66', 'act', 26, 26, 'Ибо знает об этом царь, перед которым и говорю смело. Я отнюдь не верю, чтобы от него было что‐нибудь из сего скрыто, ибо это не в углу происходило.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36544, 'rst66', 'act', 26, 25, 'Нет, достопочтенный Фест, сказал он, я не безумствую, но говорю слова истины и здравого смысла.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36543, 'rst66', 'act', 26, 24, 'Когда он так защищался, Фест громким голосом сказал: безумствуешь ты, Павел! большая ученость доводит тебя до сумасшествия.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36542, 'rst66', 'act', 26, 23, '<i>то есть</i>, что Христос имел пострадать и, восстав первый из мертвых, возвестить свет народу [Иудейскому] и язычникам.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36541, 'rst66', 'act', 26, 22, 'Но, получив помощь от Бога, я до сего дня стою&#769;, свидетельствуя малому и великому, ничего не говоря, кроме того, о чем пророки и Моисей говорили, что это будет,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36540, 'rst66', 'act', 26, 21, 'За это схватили меня Иудеи в храме и покушались растерзать.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36539, 'rst66', 'act', 26, 20, 'но сперва жителям Дамаска и Иерусалима, потом всей земле Иудейской и язычникам проповедовал, чтобы они покаялись и обратились к Богу, делая дела, достойные покаяния.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36538, 'rst66', 'act', 26, 19, 'Поэтому, царь Агриппа, я не воспротивился небесному видению,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36537, 'rst66', 'act', 26, 18, 'открыть глаза им, чтобы они обратились от тьмы к свету и от власти сатаны к Богу, и верою в Меня получили прощение грехов и жребий с освященными».');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36536, 'rst66', 'act', 26, 17, 'избавляя тебя от народа Иудейского и от язычников, к которым Я теперь посылаю тебя');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36535, 'rst66', 'act', 26, 16, 'Но встань и стань на ноги твои; ибо Я для того и явился тебе, чтобы поставить тебя служителем и свидетелем того, что ты видел и что Я открою тебе,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36534, 'rst66', 'act', 26, 15, 'Я сказал: «кто Ты, Господи?» Он сказал: «Я Иисус, Которого ты гонишь.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36533, 'rst66', 'act', 26, 14, 'Все мы упали на землю, и я услышал голос, говоривший мне на еврейском языке: «Савл, Савл! что&#769; ты гонишь Меня? Трудно тебе идти против рожна».');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36532, 'rst66', 'act', 26, 13, 'среди дня на дороге я увидел, государь, с неба свет, превосходящий солнечное сияние, осиявший меня и шедших со мною.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36531, 'rst66', 'act', 26, 12, 'Для сего, идя в Дамаск со властью и поручением от первосвященников,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36530, 'rst66', 'act', 26, 11, 'и по всем синагогам я многократно мучил их и принуждал хулить <i>Иисуса</i> и, в чрезмерной против них ярости, преследовал даже и в чужих городах.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36529, 'rst66', 'act', 26, 10, 'Это я и делал в Иерусалиме: получив власть от первосвященников, я многих святых заключал в темницы, и, когда убивали их, я подавал на то голос;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36528, 'rst66', 'act', 26, 9, 'Правда, и я думал, что мне должно много действовать против имени Иисуса Назорея.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36527, 'rst66', 'act', 26, 8, 'Что же? Неужели вы невероятным почитаете, что Бог воскрешает мертвых?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36526, 'rst66', 'act', 26, 7, 'которого исполнение надеются увидеть наши двенадцать колен, усердно служа <i>Богу</i> день и ночь. За сию‐то надежду, царь Агриппа, обвиняют меня Иудеи.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36525, 'rst66', 'act', 26, 6, 'И ныне я стою&#769; перед судом за надежду на обетование, данное от Бога нашим отцам,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36524, 'rst66', 'act', 26, 5, 'они издавна знают обо мне, если захотят свидетельствовать, что я жил фарисеем по строжайшему в нашем вероисповедании учению.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36523, 'rst66', 'act', 26, 4, 'Жизнь мою от юности <i>моей</i>, которую сначала проводил я среди народа моего в Иерусалиме, знают все Иудеи;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36522, 'rst66', 'act', 26, 3, 'тем более, что ты знаешь все обычаи и спорные мнения Иудеев. Посему прошу тебя выслушать меня великодушно.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36521, 'rst66', 'act', 26, 2, 'царь Агриппа! почитаю себя счастливым, что сегодня могу защищаться перед тобою во всем, в чем обвиняют меня Иудеи,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36520, 'rst66', 'act', 26, 1, 'Агриппа сказал Павлу: позволяется тебе говорить за себя. Тогда Павел, простерши руку, стал говорить в свою защиту:');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36519, 'rst66', 'act', 25, 27, 'Ибо, мне кажется, нерассудительно послать узника и не показать обвинений на него.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36518, 'rst66', 'act', 25, 26, 'Я не имею ничего верного написать о нем государю; посему привел его пред вас, и особенно пред тебя, царь Агриппа, дабы, по рассмотрении, было мне что написать.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36517, 'rst66', 'act', 25, 25, 'Но я нашел, что он не сделал ничего, достойного смерти; и как он сам потребовал суда у Августа, то я решился послать его <i>к нему</i>.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36516, 'rst66', 'act', 25, 24, 'И сказал Фест: царь Агриппа и все присутствующие с нами мужи! вы видите того, против которого всё множество Иудеев приступали ко мне в Иерусалиме и здесь и кричали, что ему не должно более жить.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36515, 'rst66', 'act', 25, 23, 'На другой день, когда Агриппа и Вереника пришли с великою пышностью и вошли в судебную палату с тысяченачальниками и знатнейшими гражданами, по приказанию Феста приведен был Павел.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36514, 'rst66', 'act', 25, 22, 'Агриппа же сказал Фесту: хотел бы и я послушать этого человека. Завтра же, отвечал тот, услышишь его.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36513, 'rst66', 'act', 25, 21, 'Но как Павел потребовал, чтобы он оставлен был на рассмотрение Августово, то я велел содержать его под стражею до тех пор, как пошлю его к кесарю.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36512, 'rst66', 'act', 25, 20, 'Затрудняясь в решении этого вопроса, я сказал: хочет ли он идти в Иерусалим и там быть судимым в этом?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36511, 'rst66', 'act', 25, 19, 'но они имели некоторые споры с ним об их Богопочитании и о каком‐то Иисусе умершем, о Котором Павел утверждал, что Он жив.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36510, 'rst66', 'act', 25, 18, 'Обступив его, обвинители не представили ни одного из обвинений, какие я предполагал;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36509, 'rst66', 'act', 25, 17, 'Когда же они пришли сюда, то, без всякого отлагательства, на другой же день сел я на судейское место и повелел привести того человека.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36508, 'rst66', 'act', 25, 16, 'Я отвечал им, что у Римлян нет обыкновения выдавать какого‐нибудь человека на смерть, прежде нежели обвиняемый будет иметь обвинителей налицо и получит свободу защищаться против обвинения.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36507, 'rst66', 'act', 25, 15, 'на которого, в бытность мою в Иерусалиме, <i>с жалобою</i> явились первосвященники и старейшины Иудейские, требуя осуждения его.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36506, 'rst66', 'act', 25, 14, 'И как они провели там много дней, то Фест предложил царю дело Павлово, говоря: <i>здесь</i> есть человек, оставленный Феликсом в узах,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36505, 'rst66', 'act', 25, 13, 'Через несколько дней царь Агриппа и Вереника прибыли в Кесарию поздравить Феста.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36504, 'rst66', 'act', 25, 12, 'Тогда Фест, поговорив с советом, отвечал: ты потребовал суда кесарева, к кесарю и отправишься.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36503, 'rst66', 'act', 25, 11, 'Ибо, если я неправ и сделал что‐нибудь, достойное смерти, то не отрекаюсь умереть; а если ничего того нет, в чем сии обвиняют меня, то никто не может выдать меня им. Требую суда кесарева.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36502, 'rst66', 'act', 25, 10, 'Павел сказал: я стою&#769; перед судом кесаревым, где мне и следует быть судиму. Иудеев я ничем не обидел, как и ты хорошо знаешь.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36501, 'rst66', 'act', 25, 9, 'Фест, желая сделать угождение Иудеям, сказал в ответ Павлу: хочешь ли идти в Иерусалим, чтобы я там судил тебя в этом?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36500, 'rst66', 'act', 25, 8, 'Он же в оправдание свое сказал: я не сделал никакого преступления ни против закона Иудейского, ни против храма, ни против кесаря.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36499, 'rst66', 'act', 25, 7, 'Когда он явился, стали кругом пришедшие из Иерусалима Иудеи, принося на Павла многие и тяжкие обвинения, которых не могли доказать.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36498, 'rst66', 'act', 25, 6, 'Пробыв же у них не больше восьми или десяти дней, возвратился в Кесарию, и на другой день, сев на судейское место, повелел привести Павла.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36497, 'rst66', 'act', 25, 5, 'Итак, сказал он, которые из вас могут, пусть пойдут со мною, и если есть что‐нибудь за этим человеком, пусть обвиняют его.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36496, 'rst66', 'act', 25, 4, 'Но Фест отвечал, что Павел содержится в Кесарии под стражею и что он сам скоро отправится туда.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36495, 'rst66', 'act', 25, 3, 'прося, чтобы он сделал милость, вызвал его в Иерусалим; и злоумышляли убить его на дороге.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36494, 'rst66', 'act', 25, 2, 'Тогда первосвященник и знатнейшие из Иудеев явились к нему <i>с жалобою</i> на Павла и убеждали его,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36493, 'rst66', 'act', 25, 1, 'Фест, прибыв в область, через три дня отправился из Кесарии в Иерусалим.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36492, 'rst66', 'act', 24, 27, 'Но по прошествии двух лет на место Феликса поступил Порций Фест. Желая доставить удовольствие Иудеям, Феликс оставил Павла в узах.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36491, 'rst66', 'act', 24, 26, 'Притом же надеялся он, что Павел даст ему денег, чтобы отпустил его: посему часто призывал его и беседовал с ним.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36490, 'rst66', 'act', 24, 25, 'И как он говорил о правде, о воздержании и о будущем суде, то Феликс пришел в страх и отвечал: теперь пойди, а когда найду время, позову тебя.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36489, 'rst66', 'act', 24, 24, 'Через несколько дней Феликс, придя с Друзиллою, женою своею, Иудеянкою, призвал Павла, и слушал его о вере во Христа Иисуса.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36488, 'rst66', 'act', 24, 23, 'А Павла приказал сотнику стеречь, но не стеснять его и не запрещать никому из его близких служить ему или приходить к нему.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36487, 'rst66', 'act', 24, 22, 'Выслушав это, Феликс отсрочил <i>дело</i> их, сказав: рассмотрю ваше дело, когда придет тысяченачальник Лисий, и я обстоятельно узнаю об этом учении.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36486, 'rst66', 'act', 24, 21, 'разве только то&#769; одно слово, которое громко произнес я, стоя между ними, что за <i>учение</i> о воскресении мертвых я ныне судим вами.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36485, 'rst66', 'act', 24, 20, 'Или пусть сии самые скажут, какую нашли они во мне неправду, когда я стоял перед синедрионом,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36484, 'rst66', 'act', 24, 19, '<i>Это были</i> некоторые Асийские Иудеи, которым надлежало бы предстать пред тебя и обвинять меня, если что имеют против меня.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36483, 'rst66', 'act', 24, 18, 'При сем нашли меня очистившегося в храме не с народом и не с шумом.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36482, 'rst66', 'act', 24, 17, 'После многих лет я пришел, чтобы доставить милостыню народу моему и приношения.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36481, 'rst66', 'act', 24, 16, 'Посему и сам подвизаюсь всегда иметь непорочную совесть пред Богом и людьми.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36480, 'rst66', 'act', 24, 15, 'имея надежду на Бога, что будет воскресение мертвых, праведных и неправедных, чего и сами они ожидают.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36479, 'rst66', 'act', 24, 14, 'Но в том призна&#769;юсь тебе, что по учению, которое они называют ересью, я действительно служу Богу отцов <i>моих</i>, веруя всему, написанному в законе и пророках,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36478, 'rst66', 'act', 24, 13, 'и не могут доказать того, в чем теперь обвиняют меня.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36477, 'rst66', 'act', 24, 12, 'И ни в святилище, ни в синагогах, ни по городу они не находили меня с кем‐либо спорящим или производящим народное возмущение,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36476, 'rst66', 'act', 24, 11, 'Ты можешь узнать, что не более двенадцати дней тому, как я пришел в Иерусалим для поклонения.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36475, 'rst66', 'act', 24, 10, 'Павел же, когда правитель дал ему знак говорить, отвечал: зная, что ты многие годы справедливо судишь народ сей, я тем свободнее буду защищать мое дело.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36474, 'rst66', 'act', 24, 9, 'И Иудеи подтвердили, сказав, что это так.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36473, 'rst66', 'act', 24, 8, 'повелев и нам, обвинителям его, идти к тебе. Ты можешь сам, разобрав, узнать от него о всем том, в чем мы обвиняем его.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36472, 'rst66', 'act', 24, 7, 'Но тысяченачальник Лисий, придя, с великим насилием взял его из рук наших и послал к тебе,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36471, 'rst66', 'act', 24, 6, 'который отважился даже осквернить храм, мы взяли его и хотели судить его по нашему закону.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36470, 'rst66', 'act', 24, 5, 'Найдя сего человека язвою <i>общества</i>, возбудителем мятежа между Иудеями, живущими по вселенной, и представителем Назорейской ереси,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36469, 'rst66', 'act', 24, 4, 'Но, чтобы много не утруждать тебя, прошу тебя выслушать нас кратко, со свойственным тебе снисхождением.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36468, 'rst66', 'act', 24, 3, 'всегда и везде со всякою благодарностью признаём мы, что тебе, достопочтенный Феликс, обязаны мы многим миром, и твоему попечению благоустроением сего народа.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36467, 'rst66', 'act', 24, 2, 'Когда же он был призван, то Тертулл начал обвинять его, говоря:');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36466, 'rst66', 'act', 24, 1, 'Через пять дней пришел первосвященник Анания со старейшинами и с некоторым ритором Тертуллом, которые жаловались правителю на Павла.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36465, 'rst66', 'act', 23, 35, 'я выслушаю тебя, когда явятся твои обвинители. И повелел ему быть под стражею в Иродовой претории.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36464, 'rst66', 'act', 23, 34, 'Правитель, прочитав письмо, спросил, из какой он области, и, узнав, что из Киликии, сказал:');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36463, 'rst66', 'act', 23, 33, 'А те, придя в Кесарию и отдав письмо правителю, представили ему и Павла.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36462, 'rst66', 'act', 23, 32, 'А на другой день, предоставив конным идти с ним, возвратились в крепость.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36461, 'rst66', 'act', 23, 31, 'Итак, воины, по <i>данному</i> им приказанию, взяв Павла, повели ночью в Антипатриду.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36460, 'rst66', 'act', 23, 30, 'А как до меня дошло, что Иудеи злоумышляют на этого человека, то я немедленно послал его к тебе, приказав и обвинителям говорить на него перед тобою. Будь здоров».');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36459, 'rst66', 'act', 23, 29, 'и нашел, что его обвиняют в спорных мнениях, касающихся закона их, но что нет в нем никакой вины, достойной смерти или оков.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36458, 'rst66', 'act', 23, 28, 'Потом, желая узнать, в чем обвиняли его, привел его в синедрион их');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36457, 'rst66', 'act', 23, 27, 'Сего человека Иудеи схватили и готовы были убить; я, придя с воинами, отнял его, узнав, что он Римский гражданин.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36456, 'rst66', 'act', 23, 26, '«Клавдий Лисий достопочтенному правителю Феликсу радоваться.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36455, 'rst66', 'act', 23, 25, 'Написал и письмо следующего содержания:');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36454, 'rst66', 'act', 23, 24, 'Приготовьте также ослов, чтобы, посадив Павла, препроводить его к правителю Феликсу.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36453, 'rst66', 'act', 23, 23, 'И, призвав двух сотников, сказал: приготовьте мне воинов <i>пеших</i> двести, конных семьдесят и стрелков двести, чтобы с третьего часа ночи шли в Кесарию.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36452, 'rst66', 'act', 23, 22, 'Тогда тысяченачальник отпустил юношу, сказав: никому не говори, что ты объявил мне это.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36451, 'rst66', 'act', 23, 21, 'Но ты не слушай их; ибо его подстерегают более сорока человек из них, которые заклялись не есть и не пить, доколе не убьют его; и они теперь готовы, ожидая твоего распоряжения.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36450, 'rst66', 'act', 23, 20, 'Он отвечал, что Иудеи согласились просить тебя, чтобы ты завтра вывел Павла пред синедрион, как будто они хотят точнее исследовать дело о нем.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36449, 'rst66', 'act', 23, 19, 'Тысяченачальник, взяв его за руку и отойдя с ним в сторону, спрашивал: что&#769; такое имеешь ты сказать мне?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36448, 'rst66', 'act', 23, 18, 'Тот, взяв его, привел к тысяченачальнику и сказал: узник Павел, призвав меня, просил отвести к тебе этого юношу, который имеет нечто сказать тебе.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36447, 'rst66', 'act', 23, 17, 'Павел же, призвав одного из сотников, сказал: отведи этого юношу к тысяченачальнику, ибо он имеет нечто сказать ему.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36446, 'rst66', 'act', 23, 16, 'Услышав о сем умысле, сын сестры Павловой пришел и, войдя в крепость, уведомил Павла.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36445, 'rst66', 'act', 23, 15, 'Итак, ныне же вы с синедрионом дайте знать тысяченачальнику, чтобы он завтра вывел его к вам, как будто вы хотите точнее рассмотреть дело о нем; мы же, прежде нежели он приблизится, готовы убить его.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36444, 'rst66', 'act', 23, 14, 'Они, придя к первосвященникам и старейшинам, сказали: мы клятвою заклялись не есть ничего, пока не убьем Павла.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36443, 'rst66', 'act', 23, 13, 'Было же более сорока сделавших такое заклятие.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36442, 'rst66', 'act', 23, 12, 'С наступлением дня некоторые Иудеи сделали умысел и заклялись не есть и не пить, доколе не убьют Павла.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36441, 'rst66', 'act', 23, 11, 'В следующую ночь Господь, явившись ему, сказал: дерзай, Павел; ибо как ты свидетельствовал о Мне в Иерусалиме, так надлежит тебе свидетельствовать и в Риме.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36440, 'rst66', 'act', 23, 10, 'Но как раздор увеличился, то тысяченачальник, опасаясь, чтобы они не растерзали Павла, повелел воинам сойти взять его из среды их и отвести в крепость.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36439, 'rst66', 'act', 23, 9, 'Сделался большой крик; и, встав, книжники фарисейской стороны спорили, говоря: ничего худого мы не находим в этом человеке; если же дух или Ангел говорил ему, не будем противиться Богу.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36438, 'rst66', 'act', 23, 8, 'Ибо саддукеи говорят, что нет воскресения, ни Ангела, ни духа; а фарисеи признаю&#769;т и то и другое.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36437, 'rst66', 'act', 23, 7, 'Когда же он сказал это, произошла распря между фарисеями и саддукеями, и собрание разделилось.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36436, 'rst66', 'act', 23, 6, 'Узнав же Павел, что <i>тут</i> одна часть саддукеев, а другая фарисеев, возгласил в синедрионе: мужи братия! я фарисей, сын фарисея; за чаяние воскресения мертвых меня судят.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36435, 'rst66', 'act', 23, 5, 'Павел сказал: я не знал, братия, что он первосвященник; ибо написано: «начальствующего в народе твоем не злословь».');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36434, 'rst66', 'act', 23, 4, 'Предстоящие же сказали: первосвященника Божия поносишь?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36433, 'rst66', 'act', 23, 3, 'Тогда Павел сказал ему: Бог будет бить тебя, стена подбеленная! ты сидишь, чтобы судить по закону, и, вопреки закону, велишь бить меня.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36432, 'rst66', 'act', 23, 2, 'Первосвященник же Анания стоявшим перед ним приказал бить его по устам.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36431, 'rst66', 'act', 23, 1, 'Павел, устремив взор на синедрион, сказал: мужи братия! я всею доброю совестью жил пред Богом до сего дня.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36430, 'rst66', 'act', 22, 30, 'На другой день, желая достоверно узнать, в чем обвиняют его Иудеи, освободил его от оков и повелел собраться первосвященникам и всему синедриону и, выведя Павла, поставил его перед ними.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36429, 'rst66', 'act', 22, 29, 'Тогда тотчас отступили от него хотевшие пытать его. А тысяченачальник, узнав, что он Римский гражданин, испугался, что связал его.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36428, 'rst66', 'act', 22, 28, 'Тысяченачальник отвечал: я за большие деньги приобрел это гражданство. Павел же сказал: а я и родился в нем.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36427, 'rst66', 'act', 22, 27, 'Тогда тысяченачальник, подойдя к нему, сказал: скажи мне, ты Римский гражданин? Он сказал: да.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36426, 'rst66', 'act', 22, 26, 'Услышав это, сотник подошел и донес тысяченачальнику, говоря: смотри, что&#769; ты хочешь делать? этот человек Римский гражданин.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36425, 'rst66', 'act', 22, 25, 'Но когда растянули его ремнями, Павел сказал стоявшему сотнику: разве вам позволено бичевать Римского гражданина, да и без суда?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36424, 'rst66', 'act', 22, 24, 'тысяченачальник повелел ввести его в крепость, приказав бичевать его, чтобы узнать, по какой причине так кричали против него.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36423, 'rst66', 'act', 22, 23, 'Между тем как они кричали, метали одежды и бросали пыль на воздух,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36422, 'rst66', 'act', 22, 22, 'До этого слова слушали его; а за сим подняли крик, говоря: истреби от земли такого! ибо ему не должно жить.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36421, 'rst66', 'act', 22, 21, 'И Он сказал мне: «иди; Я пошлю тебя далеко к язычникам».');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36420, 'rst66', 'act', 22, 20, 'и когда проливалась кровь Стефана, свидетеля Твоего, я там стоял, одобрял убиение его и стерег одежды побивавших его».');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36419, 'rst66', 'act', 22, 19, 'Я сказал: «Господи! им известно, что я верующих в Тебя заключал в темницы и бил в синагогах,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36418, 'rst66', 'act', 22, 18, 'и увидел Его, и Он сказал мне: «поспеши и выйди скорее из Иерусалима, потому что <i>здесь</i> не примут твоего свидетельства о Мне».');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36417, 'rst66', 'act', 22, 17, 'Когда же я возвратился в Иерусалим и молился в храме, пришел я в исступление');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36416, 'rst66', 'act', 22, 16, 'Итак, что ты медлишь? Встань, крестись и омой грехи твои, призвав имя Господа Иисуса».');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36415, 'rst66', 'act', 22, 15, 'потому что ты будешь Ему свидетелем пред всеми людьми о том, что ты видел и слышал.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36414, 'rst66', 'act', 22, 14, 'Он же сказал мне: «Бог отцов наших предызбрал тебя, чтобы ты познал волю Его, увидел Праведника и услышал глас из уст Его,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36413, 'rst66', 'act', 22, 13, 'пришел ко мне и, подойдя, сказал мне: «брат Савл! прозри». И я тотчас увидел его.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36412, 'rst66', 'act', 22, 12, 'Некто Анания, муж благочестивый по закону, одобряемый всеми Иудеями, живущими в Дамаске,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36411, 'rst66', 'act', 22, 11, 'А как я от славы света того лишился зрения, то бывшие со мною за руку привели меня в Дамаск.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36410, 'rst66', 'act', 22, 10, 'Тогда я сказал: «Господи! что мне делать?» Господь же сказал мне: «встань и иди в Дамаск, и там тебе сказано будет всё, что назначено тебе делать».');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36409, 'rst66', 'act', 22, 9, 'Бывшие же со мною свет видели и пришли в страх; но голоса Говорившего мне не слыхали.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36408, 'rst66', 'act', 22, 8, 'Я отвечал: «кто Ты, Господи?» Он сказал мне: «Я Иисус Назорей, Которого ты гонишь».');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36407, 'rst66', 'act', 22, 7, 'Я упал на землю и услышал голос, говоривший мне: «Савл, Савл! что&#769; ты гонишь Меня?»');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36406, 'rst66', 'act', 22, 6, 'Когда же я был в пути и приближался к Дамаску, около полудня вдруг осиял меня великий свет с неба.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36405, 'rst66', 'act', 22, 5, 'как засвидетельствует о мне первосвященник и все старейшины, от которых и письма взяв к братиям, живущим в Дамаске, я шел, чтобы тамошних привести в оковах в Иерусалим на истязание.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36404, 'rst66', 'act', 22, 4, 'Я даже до смерти гнал <i>последователей</i> сего учения, связывая и предавая в темницу и мужчин и женщин,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36403, 'rst66', 'act', 22, 3, 'Я Иудеянин, родившийся в Тарсе Киликийском, воспитанный в сем городе при ногах Гамалиила, тщательно наставленный в отеческом законе, ревнитель по Боге, как и все вы ныне.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36402, 'rst66', 'act', 22, 2, 'Услышав же, что он заговорил с ними на еврейском языке, они еще более утихли. Он сказал:');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36401, 'rst66', 'act', 22, 1, 'Мужи братия и отцы! выслушайте теперь мое оправдание перед вами.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36400, 'rst66', 'act', 21, 40, 'Когда же тот позволил, Павел, стоя на лестнице, дал знак рукою народу; и, когда сделалось глубокое молчание, начал говорить на еврейском языке так:');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36399, 'rst66', 'act', 21, 39, 'Павел же сказал: я Иудеянин, Тарсянин, гражданин небезызвестного Киликийского города; прошу тебя, позволь мне говорить к народу.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36398, 'rst66', 'act', 21, 38, 'Так не ты ли тот Египтянин, который перед сими днями произвел возмущение и вывел в пустыню четыре тысячи человек разбойников?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36397, 'rst66', 'act', 21, 37, 'При входе в крепость Павел сказал тысяченачальнику: можно ли мне сказать тебе нечто? А тот сказал: ты знаешь по‐гречески?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36396, 'rst66', 'act', 21, 36, 'ибо множество народа следовало и кричало: смерть ему!');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36395, 'rst66', 'act', 21, 35, 'Когда же он был на лестнице, то воинам пришлось нести его по причине стеснения от народа,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36394, 'rst66', 'act', 21, 34, 'В народе одни кричали одно, а другие другое. Он же, не могши по причине смятения узнать ничего верного, повелел вести его в крепость.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36393, 'rst66', 'act', 21, 33, 'Тогда тысяченачальник, приблизившись, взял его и велел сковать двумя цепями, и спрашивал: кто он и что сделал.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36392, 'rst66', 'act', 21, 32, 'Он, тотчас взяв воинов и сотников, устремился на них; они же, увидев тысяченачальника и воинов, перестали бить Павла.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36391, 'rst66', 'act', 21, 31, 'Когда же они хотели убить его, до тысяченачальника полка дошла весть, что весь Иерусалим возмутился.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36390, 'rst66', 'act', 21, 30, 'Весь город пришел в движение, и сделалось стечение народа; и, схватив Павла, повлекли его вон из храма, и тотчас заперты были двери.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36389, 'rst66', 'act', 21, 29, 'Ибо перед тем они видели с ним в городе Трофима Ефесянина и думали, что Павел его ввел в храм.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36388, 'rst66', 'act', 21, 28, 'крича: мужи Израильские, помогите! этот человек всех повсюду учит против народа и закона и места сего; притом и Еллинов ввел в храм и осквернил святое место сие.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36387, 'rst66', 'act', 21, 27, 'Когда же семь дней оканчивались, тогда Асийские Иудеи, увидев его в храме, возмутили весь народ и наложили на него руки,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36386, 'rst66', 'act', 21, 26, 'Тогда Павел, взяв тех мужей и очистившись с ними, в следующий день вошел в храм и объявил окончание дней очищения, когда должно быть принесено за каждого из них приношение.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36385, 'rst66', 'act', 21, 25, 'А об уверовавших язычниках мы писали, положив, чтобы они ничего такого не наблюдали, а только хранили себя от идоложертвенного, от крови, от удавленины и от блуда.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36384, 'rst66', 'act', 21, 24, 'Взяв их, очистись с ними и возьми на себя издержки на <i>жертву</i> за них, чтобы остригли себе голову, и узна&#769;ют все, что слышанное ими о тебе несправедливо, но что и сам ты продолжаешь соблюдать закон.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36383, 'rst66', 'act', 21, 23, 'Сделай же, что мы скажем тебе: есть у нас четыре человека, имеющие на себе обет.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36382, 'rst66', 'act', 21, 22, 'Итак, что&#769; же? Верно, соберется народ; ибо услышат, что ты пришел.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36381, 'rst66', 'act', 21, 21, 'А о тебе наслышались они, что ты всех Иудеев, живущих между язычниками, учишь отступлению от Моисея, говоря, чтобы они не обрезывали детей своих и не поступали по обычаям.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36380, 'rst66', 'act', 21, 20, 'Они же, выслушав, прославили Бога и сказали ему: видишь, брат, сколько тысяч уверовавших Иудеев, и все они ревнители закона.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36379, 'rst66', 'act', 21, 19, 'Приветствовав их, <i>Павел</i> рассказывал подробно, что сотворил Бог у язычников служением его.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36378, 'rst66', 'act', 21, 18, 'На другой день Павел пришел с нами к Иакову; пришли и все пресвитеры.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36377, 'rst66', 'act', 21, 17, 'По прибытии нашем в Иерусалим братия радушно приняли нас.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36376, 'rst66', 'act', 21, 16, 'С нами шли и некоторые ученики из Кесарии, провожая <i>нас</i> к некоему давнему ученику, Мна&#769;сону Кипрянину, у которого можно было бы нам жить.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36375, 'rst66', 'act', 21, 15, 'После сих дней, приготовившись, пошли мы в Иерусалим.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36374, 'rst66', 'act', 21, 14, 'Когда же мы не могли уговорить его, то успокоились, сказав: да будет воля Господня!');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36373, 'rst66', 'act', 21, 13, 'Но Павел в ответ сказал: что вы делаете? что&#769; плачете и сокрушаете сердце мое? я не только хочу быть узником, но готов умереть в Иерусалиме за имя Господа Иисуса.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36372, 'rst66', 'act', 21, 12, 'Когда же мы услышали это, то и мы и тамошние просили, чтобы он не ходил в Иерусалим.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36371, 'rst66', 'act', 21, 11, 'и, войдя к нам, взял пояс Павлов и, связав себе руки и ноги, сказал: так говорит Дух Святой: мужа, чей этот пояс, так свяжут в Иерусалиме Иудеи и предадут в руки язычников.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36370, 'rst66', 'act', 21, 10, 'Между тем как мы пребывали у них многие дни, пришел из Иудеи некто пророк, именем Агав,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36369, 'rst66', 'act', 21, 9, 'У него были четыре дочери девицы, пророчествующие.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36368, 'rst66', 'act', 21, 8, 'А на другой день Павел и мы, бывшие с ним, выйдя, пришли в Кесарию и, войдя в дом Филиппа благовестника, одного из семи <i>диаконов</i>, остались у него.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36367, 'rst66', 'act', 21, 7, 'Мы же, совершив плавание, прибыли из Тира в Птолемаиду, где, приветствовав братьев, пробыли у них один день.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36366, 'rst66', 'act', 21, 6, 'И, простившись друг с другом, мы вошли в корабль, а они возвратились домой.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36365, 'rst66', 'act', 21, 5, 'Проведя эти дни, мы вышли и пошли, и нас провожали все с женами и детьми даже за город; а на берегу, преклонив колени, помолились.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36364, 'rst66', 'act', 21, 4, 'И, найдя учеников, пробыли там семь дней. Они, по <i>внушению</i> Духа, говорили Павлу, чтобы он не ходил в Иерусалим.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36363, 'rst66', 'act', 21, 3, 'Быв в виду Кипра и оставив его слева, мы плыли в Сирию, и пристали в Тире, ибо тут надлежало сложить груз с корабля.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36362, 'rst66', 'act', 21, 2, 'и, найдя корабль, идущий в Финикию, взошли на него и отплыли.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36361, 'rst66', 'act', 21, 1, 'Когда же мы, расставшись с ними, отплыли, то прямо пришли в Кос, на другой день в Родос и оттуда в Патару,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36360, 'rst66', 'act', 20, 38, 'скорбя особенно от сказанного им слова, что они уже не увидят лица его. И провожали его до корабля.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36359, 'rst66', 'act', 20, 37, 'Тогда немалый плач был у всех, и, падая на выю Павла, целовали его,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36358, 'rst66', 'act', 20, 36, 'Сказав это, он преклонил колени свои и со всеми ими помолился.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36357, 'rst66', 'act', 20, 35, 'Во всем показал я вам, что, так трудясь, надобно поддерживать слабых и памятовать слова Господа Иисуса, ибо Он Сам сказал: «блаженнее давать, нежели принимать».');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36356, 'rst66', 'act', 20, 34, 'сами знаете, что нуждам моим и <i>нуждам</i> бывших при мне послужили руки мои сии.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36355, 'rst66', 'act', 20, 33, 'Ни серебра, ни золота, ни одежды я ни от кого не пожелал:');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36354, 'rst66', 'act', 20, 32, 'И ныне предаю вас, братия, Богу и слову благодати Его, могущему назидать <i>вас</i> более и дать вам наследие со всеми освященными.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36353, 'rst66', 'act', 20, 31, 'Посему бодрствуйте, памятуя, что я три года день и ночь непрестанно со слезами учил каждого из вас.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36352, 'rst66', 'act', 20, 30, 'и из вас самих восстанут люди, которые будут говорить превратно, дабы увлечь учеников за собою.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36351, 'rst66', 'act', 20, 29, 'Ибо я знаю, что, по отшествии моем, войдут к вам лютые волки, не щадящие стада;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36350, 'rst66', 'act', 20, 28, 'Итак, внимайте себе и всему стаду, в котором Дух Святой поставил вас блюстителями, пасти Церковь Господа и Бога, которую Он приобрел Себе Кровию Своею.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36349, 'rst66', 'act', 20, 27, 'ибо я не упускал возвещать вам всю волю Божию.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36348, 'rst66', 'act', 20, 26, 'Посему свидетельствую вам в нынешний день, что чист я от крови всех,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36347, 'rst66', 'act', 20, 25, 'И ныне, вот, я знаю, что уже не увидите лица моего все вы, между которыми ходил я, проповедуя Царствие Божие.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36346, 'rst66', 'act', 20, 24, 'Но я ни на что не взираю и не дорожу своею жизнью, только бы с радостью совершить поприще мое и служение, которое я принял от Господа Иисуса, проповедать Евангелие благодати Божией.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36345, 'rst66', 'act', 20, 23, 'только Дух Святой по всем городам свидетельствует, говоря, что узы и скорби ждут меня.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36344, 'rst66', 'act', 20, 22, 'И вот, ныне я, по влечению Духа, иду в Иерусалим, не зная, что&#769; там встретится со мною;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36343, 'rst66', 'act', 20, 21, 'возвещая Иудеям и Еллинам покаяние пред Богом и веру в Господа нашего Иисуса Христа.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36342, 'rst66', 'act', 20, 20, 'как я не пропустил ничего полезного, о чем вам не проповедовал бы и чему не учил бы вас всенародно и по домам,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36341, 'rst66', 'act', 20, 19, 'работая Господу со всяким смиренномудрием и многими слезами, среди искушений, приключавшихся мне по злоумышлениям Иудеев;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36340, 'rst66', 'act', 20, 18, 'и, когда они пришли к нему, он сказал им: вы знаете, как я с первого дня, в который пришел в Асию, все время был с вами,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36339, 'rst66', 'act', 20, 17, 'Из Милита же послав в Ефес, он призвал пресвитеров церкви,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36338, 'rst66', 'act', 20, 16, 'ибо Павлу рассудилось миновать Ефес, чтобы не замедлить ему в Асии, потому что он поспешал, если можно, в день Пятидесятницы быть в Иерусалиме.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36337, 'rst66', 'act', 20, 15, 'И, отплыв оттуда, в следующий день мы остановились против Хиоса, а на другой пристали к Самосу и, побывав в Трогиллии, в следующий <i>день</i> прибыли в Милит,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36336, 'rst66', 'act', 20, 14, 'Когда же он сошелся с нами в Ассе, то, взяв его, мы прибыли в Митилину.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36335, 'rst66', 'act', 20, 13, 'Мы пошли вперед на корабль и поплыли в Асс, чтобы взять оттуда Павла; ибо он так приказал нам, намереваясь сам идти пешком.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36334, 'rst66', 'act', 20, 12, 'Между тем отрока привели живого и немало утешились.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36333, 'rst66', 'act', 20, 11, 'Взойдя же и преломив хлеб и вкусив, беседовал довольно, даже до рассвета, и потом вышел.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36332, 'rst66', 'act', 20, 10, 'Павел, сойдя, пал на него и, обняв его, сказал: не тревожьтесь, ибо душа его в нем.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36331, 'rst66', 'act', 20, 9, 'Во время продолжительной беседы Павловой один юноша, именем Евтих, сидевший на окне, погрузился в глубокий сон и, пошатнувшись, сонный, упал вниз с третьего жилья, и поднят мертвым.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36330, 'rst66', 'act', 20, 8, 'В горнице, где мы собрались, было довольно светильников.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36329, 'rst66', 'act', 20, 7, 'В первый же день недели, когда ученики собрались для преломления хлеба, Павел, намереваясь отправиться в следующий день, беседовал с ними и продолжил слово до полуночи.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36328, 'rst66', 'act', 20, 6, 'А мы, после дней опресночных, отплыли из Филипп и дней в пять прибыли к ним в Троаду, где пробыли семь дней.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36327, 'rst66', 'act', 20, 5, 'Они, пойдя вперед, ожидали нас в Троаде.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36326, 'rst66', 'act', 20, 4, 'Его сопровождали до Асии Сосипатр Пирров, Вериянин, и из Фессалоникийцев Аристарх и Секунд, и Гаий Дервянин и Тимофей, и Асийцы Тихик и Трофим.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36325, 'rst66', 'act', 20, 3, '<i>Там</i> пробыл он три месяца. Когда же, по случаю возмущения, сделанного против него Иудеями, он хотел отправиться в Сирию, то пришло ему на мысль возвратиться через Македонию.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36324, 'rst66', 'act', 20, 2, 'Пройдя же те места и преподав <i>верующим</i> обильные наставления, пришел в Елладу.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36323, 'rst66', 'act', 20, 1, 'По прекращении мятежа Павел, призвав учеников и дав им наставления и простившись с ними, вышел и пошел в Македонию.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36322, 'rst66', 'act', 19, 40, 'Ибо мы находимся в опасности за происшедшее ныне быть обвиненными в возмущении, так как нет никакой причины, которою мы могли бы оправдать такое сборище. Сказав это, он распустил собрание.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36321, 'rst66', 'act', 19, 39, 'А если вы ищете чего‐нибудь другого, то это будет решено в законном собрании.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36320, 'rst66', 'act', 19, 38, 'Если же Димитрий и другие с ним художники имеют жалобу на кого‐нибудь, то есть судебные собрания и есть проконсулы: пусть жалуются друг на друга.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36319, 'rst66', 'act', 19, 37, 'А вы привели этих мужей, которые ни храма Артемидина не обокрали, ни богини вашей не хулили.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36318, 'rst66', 'act', 19, 36, 'Если же в этом нет спора, то надобно вам быть спокойными и не поступать опрометчиво.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36317, 'rst66', 'act', 19, 35, 'Блюститель же порядка, утишив народ, сказал: мужи Ефесские! какой человек не знает, что город Ефес есть служитель великой богини Артемиды и Диопета?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36316, 'rst66', 'act', 19, 34, 'Когда же узнали, что он Иудей, то закричали все в один голос и около двух часов кричали: велика Артемида Ефесская!');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36315, 'rst66', 'act', 19, 33, 'По предложению Иудеев, из народа вызван был Александр. Дав знак рукою, Александр хотел говорить к народу.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36314, 'rst66', 'act', 19, 32, 'Между тем одни кричали одно, а другие другое, ибо собрание было беспорядочное, и бо&#769;льшая часть <i>собравшихся</i> не знали, зачем собрались.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36313, 'rst66', 'act', 19, 31, 'Также и некоторые из Асийских начальников, будучи друзьями его, послав к нему, просили не показываться на зрелище.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36312, 'rst66', 'act', 19, 30, 'Когда же Павел хотел войти в народ, ученики не допустили его.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36311, 'rst66', 'act', 19, 29, 'И весь город наполнился смятением. Схватив Македонян Гаия и Аристарха, спутников Павловых, они единодушно устремились на зрелище.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36310, 'rst66', 'act', 19, 28, 'Выслушав это, они исполнились ярости и стали кричать, говоря: велика Артемида Ефесская!');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36309, 'rst66', 'act', 19, 27, 'А это нам угрожает тем, что не только ремесло наше придет в презрение, но и храм великой богини Артемиды ничего не будет значить, и испровергнется величие той, которую почитает вся Асия и вселенная.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36308, 'rst66', 'act', 19, 26, 'между тем вы видите и слышите, что не только в Ефесе, но почти во всей Асии этот Павел своими убеждениями совратил немалое число людей, говоря, что делаемые руками человеческими не суть боги.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36307, 'rst66', 'act', 19, 25, 'собрав их и других подобных ремесленников, сказал: друзья! вы знаете, что от этого ремесла зависит благосостояние наше;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36306, 'rst66', 'act', 19, 24, 'ибо некто серебряник, именем Димитрий, делавший серебряные храмы Артемиды и доставлявший художникам немалую прибыль,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36305, 'rst66', 'act', 19, 23, 'В то время произошел немалый мятеж против пути Господня,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36304, 'rst66', 'act', 19, 22, 'И, послав в Македонию двоих из служивших ему, Тимофея и Ераста, сам остался на время в Асии.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36303, 'rst66', 'act', 19, 21, 'Когда же это совершилось, Павел положил в духе, пройдя Македонию и Ахаию, идти в Иерусалим, сказав: побывав там, я должен видеть и Рим.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36302, 'rst66', 'act', 19, 20, 'С такою силою возрастало и возмогало слово Господне.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36301, 'rst66', 'act', 19, 19, 'А из занимавшихся чародейством довольно многие, собрав книги свои, сожгли перед всеми, и сложили цены их, и оказалось их на пятьдесят тысяч <i>драхм</i>.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36300, 'rst66', 'act', 19, 18, 'Многие же из уверовавших приходили, исповедуя и открывая дела свои.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36299, 'rst66', 'act', 19, 17, 'Это сделалось известно всем живущим в Ефесе Иудеям и Еллинам, и напал страх на всех их, и величаемо было имя Господа Иисуса.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36298, 'rst66', 'act', 19, 16, 'И бросился на них человек, в котором был злой дух, и, одолев их, взял над ними такую силу, что они, нагие и избитые, выбежали из того дома.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36297, 'rst66', 'act', 19, 15, 'Но злой дух сказал в ответ: Иисуса знаю, и Павел мне известен, а вы кто?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36296, 'rst66', 'act', 19, 14, 'Это делали какие‐то семь сынов Иудейского первосвященника Скевы.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36295, 'rst66', 'act', 19, 13, 'Даже некоторые из скитающихся Иудейских заклинателей стали употреблять над имеющими злых духов имя Господа Иисуса, говоря: заклинаем вас Иисусом, Которого Павел проповедует.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36294, 'rst66', 'act', 19, 12, 'так что на больных возлагали платки и опоясания с тела его, и у них прекращались болезни, и злые духи выходили из них.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36293, 'rst66', 'act', 19, 11, 'Бог же творил немало чудес руками Павла,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36292, 'rst66', 'act', 19, 10, 'Это продолжалось до двух лет, так что все жители Асии слышали проповедь о Господе Иисусе, как Иудеи, так и Еллины.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36291, 'rst66', 'act', 19, 9, 'Но как некоторые ожесточились и не верили, злословя путь Господень перед народом, то он, оставив их, отделил учеников и ежедневно проповедовал в училище некоего Тиранна.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36290, 'rst66', 'act', 19, 8, 'Придя в синагогу, он небоязненно проповедовал три месяца, беседуя и удостоверяя о Царствии Божием.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36289, 'rst66', 'act', 19, 7, 'Всех их было человек около двенадцати.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36288, 'rst66', 'act', 19, 6, 'и, когда Павел возложил на них руки, нисшел на них Дух Святой, и они стали говорить <i>иными</i> языками и пророчествовать.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36287, 'rst66', 'act', 19, 5, 'Услышав это, они крестились во имя Господа Иисуса,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36286, 'rst66', 'act', 19, 4, 'Павел сказал: Иоанн крестил крещением покаяния, говоря людям, чтобы веровали в Грядущего по нем, то есть во Христа Иисуса.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36285, 'rst66', 'act', 19, 3, 'Он сказал им: во что&#769; же вы крестились? Они отвечали: во Иоанново крещение.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36284, 'rst66', 'act', 19, 2, 'сказал им: приняли ли вы Святого Духа, уверовав? Они же сказали ему: мы даже и не слыхали, есть ли Дух Святой.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36283, 'rst66', 'act', 19, 1, 'Во время пребывания Аполлоса в Коринфе Павел, пройдя верхние страны, прибыл в Ефес и, найдя <i>там</i> некоторых учеников,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36282, 'rst66', 'act', 18, 28, 'ибо он сильно опровергал Иудеев всенародно, доказывая Писаниями, что Иисус есть Христос.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36281, 'rst66', 'act', 18, 27, 'А когда он вознамерился идти в Ахаию, то братия послали к <i>тамошним</i> ученикам, располагая их принять его; и он, прибыв туда, много содействовал уверовавшим благодатью,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36280, 'rst66', 'act', 18, 26, 'Он начал смело говорить в синагоге. Услышав его, Акила и Прискилла приняли его и точнее объяснили ему путь Господень.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36279, 'rst66', 'act', 18, 25, 'Он был наставлен в начатках пути Господня и, горя духом, говорил и учил о Господе правильно, зная только крещение Иоанново.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36278, 'rst66', 'act', 18, 24, 'Некто Иудей, именем Аполлос, родом из Александрии, муж красноречивый и сведущий в Писаниях, пришел в Ефес.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36277, 'rst66', 'act', 18, 23, 'И, проведя <i>там</i> несколько времени, вышел, и проходил по порядку страну Галатийскую и Фригию, утверждая всех учеников.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36276, 'rst66', 'act', 18, 22, 'Побывав в Кесарии, он приходил <i>в Иерусалим</i>, приветствовал церковь и отошел в Антиохию.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36275, 'rst66', 'act', 18, 21, 'а простился с ними, сказав: мне нужно непременно провести приближающийся праздник в Иерусалиме; к вам же возвращусь опять, если будет угодно Богу. И отправился из Ефеса. [Акила же и Прискилла остались в Ефесе.]');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36274, 'rst66', 'act', 18, 20, 'Когда же они просили его побыть у них долее, он не согласился,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36273, 'rst66', 'act', 18, 19, 'Достигнув Ефеса, оставил их там, а сам вошел в синагогу и рассуждал с Иудеями.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36272, 'rst66', 'act', 18, 18, 'Павел, пробыв еще довольно дней, простился с братиями и отплыл в Сирию, и с ним Акила и Прискилла, остригши голову в Кенхреях, по обету.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36271, 'rst66', 'act', 18, 17, 'А все Еллины, схватив Сосфена, начальника синагоги, били его перед судилищем; и Галлион нимало не беспокоился о том.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36270, 'rst66', 'act', 18, 16, 'И прогнал их от судилища.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36269, 'rst66', 'act', 18, 15, 'но когда идет спор об учении, и об именах, и о законе вашем, то разбирайте сами; я не хочу быть судьею в этом.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36268, 'rst66', 'act', 18, 14, 'Когда же Павел хотел открыть уста, Галлион сказал Иудеям: Иудеи! если бы какая‐нибудь была обида или злой умысел, то я имел бы причину выслушать вас,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36267, 'rst66', 'act', 18, 13, 'говоря, что он учит людей чтить Бога не по закону.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36266, 'rst66', 'act', 18, 12, 'Между тем, во время проконсульства Галлиона в Ахаии, напали Иудеи единодушно на Павла и привели его пред судилище,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36265, 'rst66', 'act', 18, 11, 'И он оставался <i>там</i> год и шесть месяцев, поучая их слову Божию.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36264, 'rst66', 'act', 18, 10, 'ибо Я с тобою, и никто не сделает тебе зла, потому что у Меня много людей в этом городе.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36263, 'rst66', 'act', 18, 9, 'Господь же в видении ночью сказал Павлу: не бойся, но говори и не умолкай,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36262, 'rst66', 'act', 18, 8, 'Крисп же, начальник синагоги, уверовал в Господа со всем домом своим, и многие из Коринфян, слушая, уверовали и крестились.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36261, 'rst66', 'act', 18, 7, 'И пошел оттуда, и пришел к некоторому чтущему Бога, именем Иусту, которого дом был подле синагоги.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36260, 'rst66', 'act', 18, 6, 'Но как они противились и злословили, то он, отрясши одежды свои, сказал к ним: кровь ваша на главах ваших; я чист; отныне иду к язычникам.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36259, 'rst66', 'act', 18, 5, 'А когда пришли из Македонии Сила и Тимофей, то Павел понуждаем был духом свидетельствовать Иудеям, что Иисус есть Христос.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36258, 'rst66', 'act', 18, 4, 'Во всякую же субботу он говорил в синагоге и убеждал Иудеев и Еллинов.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36257, 'rst66', 'act', 18, 3, 'и, по одинаковости ремесла, остался у них и работал; ибо ремеслом их было делание палаток.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36256, 'rst66', 'act', 18, 2, 'и, найдя некоторого Иудея, именем Акилу, родом Понтянина, недавно пришедшего из Италии, и Прискиллу, жену его, потому что Клавдий повелел всем Иудеям удалиться из Рима, пришел к ним;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36255, 'rst66', 'act', 18, 1, 'После сего Павел, оставив Афины, пришел в Коринф;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36254, 'rst66', 'act', 17, 34, 'Некоторые же мужи, пристав к нему, уверовали; между ними был Дионисий Ареопагит и женщина, именем Да&#769;марь, и другие с ними.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36253, 'rst66', 'act', 17, 33, 'Итак, Павел вышел из среды их.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36252, 'rst66', 'act', 17, 32, 'Услышав о воскресении мертвых, одни насмехались, а другие говорили: об этом послушаем тебя в другое время.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36251, 'rst66', 'act', 17, 31, 'ибо Он назначил день, в который будет праведно судить вселенную, посредством предопределенного Им Мужа, подав удостоверение всем, воскресив Его из мертвых.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36250, 'rst66', 'act', 17, 30, 'Итак, оставляя времена неведения, Бог ныне повелевает людям всем повсюду покаяться,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36249, 'rst66', 'act', 17, 29, 'Итак, мы, будучи родом Божиим, не должны думать, что Божество подобно золоту, или серебру, или камню, получившему образ от искусства и вымысла человеческого.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36248, 'rst66', 'act', 17, 28, 'ибо мы Им живем и движемся и существуем, как и некоторые из ваших стихотворцев говорили: «мы Его и род».');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36247, 'rst66', 'act', 17, 27, 'дабы они искали Бога, не ощутят ли Его и не найдут ли, хотя Он и недалеко от каждого из нас,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36246, 'rst66', 'act', 17, 26, 'От одной крови Он произвел весь род человеческий для обитания по всему лицу земли, назначив предопределенные времена и пределы их обитанию,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36245, 'rst66', 'act', 17, 25, 'и не требует служения рук человеческих, <i>как бы</i> имеющий в чем‐либо нужду, Сам давая всему жизнь и дыхание и всё.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36244, 'rst66', 'act', 17, 24, 'Бог, сотворивший мир и всё, что в нем, Он, будучи Господом неба и земли, не в рукотворенных храмах живет');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36243, 'rst66', 'act', 17, 23, 'Ибо, проходя и осматривая ваши святыни, я нашел и жертвенник, на котором написано: «неведомому Богу». Сего‐то, Которого вы, не зная, чтите, я проповедую вам.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36242, 'rst66', 'act', 17, 22, 'И, став Павел среди ареопага, сказал: Афиняне! по всему вижу я, что вы как бы особенно набожны.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36241, 'rst66', 'act', 17, 21, 'Афиняне же все и живущие <i>у них</i> иностранцы ни в чем охотнее не проводили время, как в том, чтобы говорить или слушать что‐нибудь новое.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36240, 'rst66', 'act', 17, 20, 'Ибо что‐то странное ты влагаешь в уши наши. Посему хотим знать, что это такое?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36239, 'rst66', 'act', 17, 19, 'И, взяв его, привели в ареопаг и говорили: можем ли мы знать, что это за новое учение, проповедуемое тобою?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36238, 'rst66', 'act', 17, 18, 'Некоторые из эпикурейских и стоических философов стали спорить с ним; и одни говорили: «что хочет сказать этот суеслов?», а другие: «кажется, он проповедует о чужих божествах», потому что он благовествовал им Иисуса и воскресение.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36237, 'rst66', 'act', 17, 17, 'Итак, он рассуждал в синагоге с Иудеями и с чтущими <i>Бога</i>, и ежедневно на площади со встречающимися.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36236, 'rst66', 'act', 17, 16, 'В ожидании их в Афинах Павел возмутился духом при виде этого города, полного идолов.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36235, 'rst66', 'act', 17, 15, 'Сопровождавшие Павла проводили его до Афин и, получив приказание к Силе и Тимофею, чтобы они скорее пришли к нему, отправились.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36234, 'rst66', 'act', 17, 14, 'Тогда братия тотчас отпустили Павла, как будто идущего к морю; а Сила и Тимофей остались там.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36233, 'rst66', 'act', 17, 13, 'Но когда Фессалоникские Иудеи узнали, что и в Верии проповедано Павлом слово Божие, то пришли и туда, возбуждая и возмущая народ.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36232, 'rst66', 'act', 17, 12, 'И многие из них уверовали, и из Еллинских почетных женщин и из мужчин немало.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36231, 'rst66', 'act', 17, 11, 'Здешние были благомысленнее Фессалоникских: они приняли слово со всем усердием, ежедневно разбирая Писания, точно ли это так.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36230, 'rst66', 'act', 17, 10, 'Братия же немедленно ночью отправили Павла и Силу в Верию, куда они прибыв, пошли в синагогу Иудейскую.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36229, 'rst66', 'act', 17, 9, 'Но <i>сии</i>, получив удостоверение от Иасона и прочих, отпустили их.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36228, 'rst66', 'act', 17, 8, 'И встревожили народ и городских начальников, слушавших это.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36227, 'rst66', 'act', 17, 7, 'а Иасон принял их, и все они поступают против повелений кесаря, почитая другого царем, Иисуса.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36226, 'rst66', 'act', 17, 6, 'Не найдя же их, повлекли Иасона и некоторых братьев к городским начальникам, крича, что эти всесветные возмутители пришли и сюда,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36225, 'rst66', 'act', 17, 5, 'Но неуверовавшие Иудеи, возревновав и взяв с площади некоторых негодных людей, собрались толпою и возмущали город и, приступив к дому Иасона, домогались вывести их к народу.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36224, 'rst66', 'act', 17, 4, 'И некоторые из них уверовали и присоединились к Павлу и Силе, как из Еллинов, чтущих <i>Бога</i>, великое множество, так и из знатных женщин немало.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36223, 'rst66', 'act', 17, 3, 'открывая и доказывая им, что Христу надлежало пострадать и воскреснуть из мертвых и что Сей Христос есть Иисус, Которого я проповедую вам.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36222, 'rst66', 'act', 17, 2, 'Павел, по своему обыкновению, вошел к ним и три субботы говорил с ними из Писаний,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36221, 'rst66', 'act', 17, 1, 'Пройдя через Амфиполь и Аполлонию, они пришли в Фессалонику, где была Иудейская синагога.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36220, 'rst66', 'act', 16, 40, 'Они же, выйдя из темницы, пришли к Лидии и, увидев братьев, поучали их, и отправились.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36219, 'rst66', 'act', 16, 39, 'И, придя, извинились перед ними и, выведя, просили удалиться из города.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36218, 'rst66', 'act', 16, 38, 'Городские служители пересказали эти слова воеводам, и те испугались, услышав, что это Римские граждане.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36217, 'rst66', 'act', 16, 37, 'Но Павел сказал к ним: нас, Римских граждан, без суда всенародно били и бросили в темницу, а теперь тайно выпускают? нет, пусть придут и сами выведут нас.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36216, 'rst66', 'act', 16, 36, 'Темничный страж объявил о сем Павлу: воеводы прислали отпустить вас; итак выйдите теперь и идите с миром.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36215, 'rst66', 'act', 16, 35, 'Когда же настал день, воеводы послали городских служителей сказать: отпусти тех людей.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36214, 'rst66', 'act', 16, 34, 'И, приведя их в дом свой, предложил трапезу и возрадовался со всем домом своим, что уверовал в Бога.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36213, 'rst66', 'act', 16, 33, 'И, взяв их в тот час ночи, он омыл раны их и немедленно крестился сам и все <i>домашние</i> его.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36212, 'rst66', 'act', 16, 32, 'И проповедали слово Господне ему и всем, бывшим в доме его.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36211, 'rst66', 'act', 16, 31, 'Они же сказали: веруй в Господа Иисуса Христа, и спасешься ты и весь дом твой.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36210, 'rst66', 'act', 16, 30, 'и, выведя их вон, сказал: государи <i>мои!</i> что мне делать, чтобы спастись?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36209, 'rst66', 'act', 16, 29, 'Он потребовал огня, вбежал <i>в темницу</i> и в трепете припал к Павлу и Силе,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36208, 'rst66', 'act', 16, 28, 'Но Павел возгласил громким голосом, говоря: не делай себе никакого зла, ибо все мы здесь.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36207, 'rst66', 'act', 16, 27, 'Темничный же страж, пробудившись и увидев, что двери темницы отворены, извлек меч и хотел умертвить себя, думая, что узники убежали.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36206, 'rst66', 'act', 16, 26, 'Вдруг сделалось великое землетрясение, так что поколебалось основание темницы; тотчас отворились все двери, и у всех узы ослабели.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36205, 'rst66', 'act', 16, 25, 'Около полуночи Павел и Сила, молясь, воспевали Бога; узники же слушали их.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36204, 'rst66', 'act', 16, 24, 'Получив такое приказание, он ввергнул их во внутреннюю темницу и ноги их забил в колоду.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36203, 'rst66', 'act', 16, 23, 'и, дав им много ударов, ввергли в темницу, приказав темничному стражу крепко стеречь их.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36202, 'rst66', 'act', 16, 22, 'Народ также восстал на них, а воеводы, сорвав с них одежды, велели бить их палками');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36201, 'rst66', 'act', 16, 21, 'и проповедуют обычаи, которых нам, Римлянам, не следует ни принимать, ни исполнять.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36200, 'rst66', 'act', 16, 20, 'И, приведя их к воеводам, сказали: сии люди, будучи Иудеями, возмущают наш город');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36199, 'rst66', 'act', 16, 19, 'Тогда господа ее, видя, что исчезла надежда дохода их, схватили Павла и Силу и повлекли на площадь к начальникам.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36198, 'rst66', 'act', 16, 18, 'Это она делала много дней. Павел, вознегодовав, обратился и сказал духу: именем Иисуса Христа повелеваю тебе выйти из нее. И <i>дух</i> вышел в тот же час.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36197, 'rst66', 'act', 16, 17, 'Идя за Павлом и за нами, она кричала, говоря: сии человеки рабы Бога Всевышнего, которые возвещают нам путь спасения.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36196, 'rst66', 'act', 16, 16, 'Случилось, что, когда мы шли в молитвенный дом, встретилась нам одна служанка, одержимая духом прорицательным, которая через прорицание доставляла большой доход господам своим.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36195, 'rst66', 'act', 16, 15, 'Когда же крестилась она и домашние ее, то просила нас, говоря: если вы признали меня верною Господу, то войдите в дом мой и живите <i>у меня</i>. И убедила нас.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36194, 'rst66', 'act', 16, 14, 'И одна женщина из города Фиатир, именем Лидия, торговавшая багряницею, чтущая Бога, слушала; и Господь отверз сердце ее внимать тому, что говорил Павел.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36193, 'rst66', 'act', 16, 13, 'В день же субботний мы вышли за город к реке, где, по обыкновению, был молитвенный дом, и, сев, разговаривали с собравшимися <i>там</i> женщинами.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36192, 'rst66', 'act', 16, 12, 'оттуда же в Филиппы: это первый город в той части Македонии, колония. В этом городе мы пробыли несколько дней.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36191, 'rst66', 'act', 16, 11, 'Итак, отправившись из Троады, мы прямо прибыли в Самофракию, а на другой день в Неаполь,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36190, 'rst66', 'act', 16, 10, 'После сего видения тотчас мы положили отправиться в Македонию, заключая, что призывал нас Господь благовествовать там.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36189, 'rst66', 'act', 16, 9, 'И было ночью видение Павлу: предстал некий муж, Македонянин, прося его и говоря: приди в Македонию и помоги нам.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36188, 'rst66', 'act', 16, 8, 'Миновав же Мисию, сошли они в Троаду.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36187, 'rst66', 'act', 16, 7, 'Дойдя до Мисии, предпринимали идти в Вифинию; но Дух не допустил их.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36186, 'rst66', 'act', 16, 6, 'Пройдя через Фригию и Галатийскую страну, они не были допущены Духом Святым проповедовать слово в Асии.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36185, 'rst66', 'act', 16, 5, 'И церкви утверждались верою и ежедневно увеличивались числом.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36184, 'rst66', 'act', 16, 4, 'Проходя же по городам, они предавали <i>верным</i> соблюдать определения, постановленные Апостолами и пресвитерами в Иерусалиме.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36183, 'rst66', 'act', 16, 3, 'Его пожелал Павел взять с собою; и, взяв, обрезал его ради Иудеев, находившихся в тех местах; ибо все знали об отце его, что он был Еллин.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36182, 'rst66', 'act', 16, 2, 'и о котором свидетельствовали братия, находившиеся в Листре и Иконии.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36181, 'rst66', 'act', 16, 1, 'Дошел он до Дервии и Листры. И вот, там был некоторый ученик, именем Тимофей, которого мать была Иудеянка уверовавшая, а отец Еллин,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36180, 'rst66', 'act', 15, 41, 'и проходил Сирию и Киликию, утверждая церкви.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36179, 'rst66', 'act', 15, 40, 'а Павел, избрав себе Силу, отправился, быв поручен братиями благодати Божией,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36178, 'rst66', 'act', 15, 39, 'Отсюда произошло огорчение, так что они разлучились друг с другом; и Варнава, взяв Марка, отплыл в Кипр;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36177, 'rst66', 'act', 15, 38, 'Но Павел полагал не брать отставшего от них в Памфилии и не шедшего с ними на дело, на которое они были посланы.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36176, 'rst66', 'act', 15, 37, 'Варнава хотел взять с собою Иоанна, называемого Марком.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36175, 'rst66', 'act', 15, 36, 'По некотором времени Павел сказал Варнаве: пойдем опять, посетим братьев наших по всем городам, в которых мы проповедали слово Господне, как они живут.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36174, 'rst66', 'act', 15, 35, 'Павел же и Варнава жили в Антиохии, уча и благовествуя, вместе с другими многими, слово Господне.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36173, 'rst66', 'act', 15, 34, 'Но Силе рассудилось остаться там. [А Иуда возвратился в Иерусалим.]');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36172, 'rst66', 'act', 15, 33, 'Пробыв там <i>некоторое</i> время, они с миром отпущены были братиями к Апостолам.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36171, 'rst66', 'act', 15, 32, 'Иуда и Сила, будучи также пророками, обильным словом преподали наставление братиям и утвердили их.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36170, 'rst66', 'act', 15, 31, 'Они же, прочитав, возрадовались о сем наставлении.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36169, 'rst66', 'act', 15, 30, 'Итак, отправленные пришли в Антиохию и, собрав людей, вручили письмо.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36168, 'rst66', 'act', 15, 29, 'воздерживаться от идоложертвенного, и крови, и удавленины, и блуда, и не делать другим того, чего себе не хотите. Соблюдая сие, хорошо сделаете. Будьте здравы».');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36167, 'rst66', 'act', 15, 28, 'Ибо угодно Святому Духу и нам не возлагать на вас никакого бремени более, кроме сего необходимого:');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36166, 'rst66', 'act', 15, 27, 'Итак, мы послали Иуду и Силу, которые изъяснят вам то же и словесно.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36165, 'rst66', 'act', 15, 26, 'человеками, предавшими души свои за имя Господа нашего Иисуса Христа.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36164, 'rst66', 'act', 15, 25, 'то мы, собравшись, единодушно рассудили, избрав мужей, послать их к вам с возлюбленными нашими Варнавою и Павлом,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36163, 'rst66', 'act', 15, 24, 'Поелику мы услышали, что некоторые, вышедшие от нас, смутили вас <i>своими</i> речами и поколебали ваши души, говоря, что должно обрезываться и соблюдать закон, чего мы им не поручали,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36162, 'rst66', 'act', 15, 23, 'написав и вручив им следующее: «Апостолы и пресвитеры и братия находящимся в Антиохии, Сирии и Киликии братиям из язычников: радоваться.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36161, 'rst66', 'act', 15, 22, 'Тогда Апостолы и пресвитеры со всею церковью рассудили, избрав из среды себя мужей, послать их в Антиохию с Павлом и Варнавою, <i>именно:</i> Иуду, прозываемого Варсавою, и Силу, мужей, начальствующих между братиями,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36160, 'rst66', 'act', 15, 21, 'Ибо <i>закон</i> Моисеев от древних родов по всем городам имеет проповедующих его и читается в синагогах каждую субботу.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36159, 'rst66', 'act', 15, 20, 'а написать им, чтобы они воздерживались от оскверненного идолами, от блуда, удавленины и крови, и чтобы не делали другим того, чего не хотят себе.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36158, 'rst66', 'act', 15, 19, 'Посему я полагаю не затруднять обращающихся к Богу из язычников,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36157, 'rst66', 'act', 15, 18, 'Ведомы Богу от вечности все дела Его.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36156, 'rst66', 'act', 15, 17, 'чтобы взыскали Господа прочие человеки и все народы, между которыми возвестится имя Мое, говорит Господь, творящий все сие».');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36155, 'rst66', 'act', 15, 16, '«потом обращусь и воссоздам скинию Давидову падшую, и то, что в ней разрушено, воссоздам, и исправлю ее,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36154, 'rst66', 'act', 15, 15, 'И с сим согласны слова пророков, как написано:');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36153, 'rst66', 'act', 15, 14, 'Симон изъяснил, как Бог первоначально призрел на язычников, чтобы составить из них народ во имя Свое.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36152, 'rst66', 'act', 15, 13, 'После же того, как они умолкли, начал речь Иаков и сказал: мужи братия! послушайте меня.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36151, 'rst66', 'act', 15, 12, 'Тогда умолкло все собрание и слушало Варнаву и Павла, рассказывавших, какие знамения и чудеса сотворил Бог через них среди язычников.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36150, 'rst66', 'act', 15, 11, 'Но мы веруем, что благодатию Господа Иисуса Христа спасемся, как и они.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36149, 'rst66', 'act', 15, 10, 'Что же вы ныне искушаете Бога, <i>желая</i> возложить на выи учеников иго, которого не могли понести ни отцы наши, ни мы?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36148, 'rst66', 'act', 15, 9, 'и не положил никакого различия между нами и ими, верою очистив сердца их.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36147, 'rst66', 'act', 15, 8, 'и Сердцеведец Бог дал им свидетельство, даровав им Духа Святого, как и нам;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36146, 'rst66', 'act', 15, 7, 'По долгом рассуждении Петр, встав, сказал им: мужи братия! вы знаете, что Бог от дней первых избрал из нас <i>меня</i>, чтобы из уст моих язычники услышали слово Евангелия и уверовали;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36145, 'rst66', 'act', 15, 6, 'Апостолы и пресвитеры собрались для рассмотрения сего дела.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36144, 'rst66', 'act', 15, 5, 'Тогда восстали некоторые из фарисейской ереси уверовавшие и говорили, что должно обрезывать <i>язычников</i> и заповедовать соблюдать закон Моисеев.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36143, 'rst66', 'act', 15, 4, 'По прибытии же в Иерусалим они были приняты церковью, Апостолами и пресвитерами, и возвестили всё, что Бог сотворил с ними и как отверз дверь веры язычникам.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36142, 'rst66', 'act', 15, 3, 'Итак, быв провожены церковью, они проходили Финикию и Самарию, рассказывая об обращении язычников, и производили радость великую во всех братиях.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36141, 'rst66', 'act', 15, 2, 'Когда же произошло разногласие и немалое состязание у Павла и Варнавы с ними, то положили Павлу и Варнаве и некоторым другим из них отправиться по сему делу к Апостолам и пресвитерам в Иерусалим.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36140, 'rst66', 'act', 15, 1, 'Некоторые, пришедшие из Иудеи, учили братьев: если не обрежетесь по обряду Моисееву, не можете спастись.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36139, 'rst66', 'act', 14, 28, 'И пребывали там немалое время с учениками.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36138, 'rst66', 'act', 14, 27, 'Прибыв туда и собрав церковь, они рассказали всё, что сотворил Бог с ними и как Он отверз дверь веры язычникам.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36137, 'rst66', 'act', 14, 26, 'а оттуда отплыли в Антиохию, откуда были преданы благодати Божией на дело, которое и исполнили.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36136, 'rst66', 'act', 14, 25, 'и, проповедав слово Господне в Пергии, сошли в Атталию;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36135, 'rst66', 'act', 14, 24, 'Потом, пройдя через Писидию, пришли в Памфилию,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36134, 'rst66', 'act', 14, 23, 'Рукоположив же им пресвитеров к каждой церкви, они помолились с постом и предали их Господу, в Которого уверовали.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36133, 'rst66', 'act', 14, 22, 'утверждая души учеников, увещевая пребывать в вере и <i>поучая</i>, что многими скорбями надлежит нам войти в Царствие Божие.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36132, 'rst66', 'act', 14, 21, 'Проповедав Евангелие сему городу и приобретя довольно учеников, они обратно проходили Листру, Иконию и Антиохию,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36131, 'rst66', 'act', 14, 20, 'Когда же ученики собрались около него, он встал и пошел в город, а на другой день удалился с Варнавою в Дервию.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36130, 'rst66', 'act', 14, 19, 'из Антиохии и Иконии пришли некоторые Иудеи и, когда <i>Апостолы</i> смело проповедовали, убедили народ отстать от них, говоря: они не говорят ничего истинного, а все лгут. И, возбудив народ, побили Павла камнями и вытащили за город, почитая его умершим.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36129, 'rst66', 'act', 14, 18, 'И, говоря сие, они едва убедили народ не приносить им жертвы и идти каждому домой. Между тем как они, оставаясь там, учили,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36128, 'rst66', 'act', 14, 17, 'хотя и не переставал свидетельствовать о Себе благодеяниями, подавая нам с неба дожди и времена плодоносные и исполняя пищею и веселием сердца наши.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36127, 'rst66', 'act', 14, 16, 'Который в прошедших родах попустил всем народам ходить своими путями,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36126, 'rst66', 'act', 14, 15, 'мужи! что вы это делаете? И мы подобные вам человеки, и благовествуем вам, чтобы вы обратились от сих ложных к Богу Живому, Который сотворил небо и землю, и море, и всё, что в них,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36125, 'rst66', 'act', 14, 14, 'Но Апостолы Варнава и Павел, услышав <i>о сем</i>, разодрали свои одежды и, бросившись в народ, громогласно говорили:');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36124, 'rst66', 'act', 14, 13, 'Жрец же <i>идола</i> Зевса, находившегося перед их городом, приведя к воротам волов и <i>принеся</i> венки, хотел вместе с народом совершить жертвоприношение.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36123, 'rst66', 'act', 14, 12, 'И называли Варнаву Зевсом, а Павла Ермием, потому что он начальствовал в слове.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36122, 'rst66', 'act', 14, 11, 'Народ же, увидев, что сделал Павел, возвысил свой голос, говоря по‐ликаонски: боги в образе человеческом сошли к нам.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36121, 'rst66', 'act', 14, 10, 'сказал громким голосом: тебе говорю во имя Господа Иисуса Христа: стань на ноги твои прямо. И он тотчас вскочил и стал ходить.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36120, 'rst66', 'act', 14, 9, 'Он слушал говорившего Павла, который, взглянув на него и увидев, что он имеет веру для получения исцеления,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36119, 'rst66', 'act', 14, 8, 'В Листре некоторый муж, не владевший ногами, сидел, будучи хром от чрева матери своей, и никогда не ходил.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36118, 'rst66', 'act', 14, 7, 'и там благовествовали.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36117, 'rst66', 'act', 14, 6, 'они, узнав <i>о сем</i>, удалились в Ликаонские города Листру и Дервию и в окрестности их,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36116, 'rst66', 'act', 14, 5, 'Когда же язычники и Иудеи со своими начальниками устремились на них, чтобы посрамить и побить их камнями,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36115, 'rst66', 'act', 14, 4, 'Между тем народ в городе разделился: и одни были на стороне Иудеев, а другие на стороне Апостолов.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36114, 'rst66', 'act', 14, 3, 'Впрочем, они пробыли <i>здесь</i> довольно времени, смело действуя о Господе, Который, во свидетельство слову благодати Своей, творил руками их знамения и чудеса.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36113, 'rst66', 'act', 14, 2, 'А неверующие Иудеи возбудили и раздражили против братьев сердца язычников.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36112, 'rst66', 'act', 14, 1, 'В Иконии они вошли вместе в Иудейскую синагогу и говорили так, что уверовало великое множество Иудеев и Еллинов.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36111, 'rst66', 'act', 13, 52, 'А ученики исполнялись радости и Духа Святого.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36110, 'rst66', 'act', 13, 51, 'Они же, отрясши на них прах от ног своих, пошли в Иконию.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36109, 'rst66', 'act', 13, 50, 'Но Иудеи, подстрекнув набожных и почетных женщин и первых в городе <i>людей</i>, воздвигли гонение на Павла и Варнаву и изгнали их из своих пределов.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36108, 'rst66', 'act', 13, 49, 'И слово Господне распространялось по всей стране.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36107, 'rst66', 'act', 13, 48, 'Язычники, слыша это, радовались и прославляли слово Господне, и уверовали все, которые были предуставлены к вечной жизни.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36106, 'rst66', 'act', 13, 47, 'Ибо так заповедал нам Господь: «Я положил Тебя во свет язычникам, чтобы Ты был во спасение до края земли».');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36105, 'rst66', 'act', 13, 46, 'Тогда Павел и Варнава с дерзновением сказали: вам первым надлежало быть проповедану слову Божию, но как вы отвергаете его и сами себя делаете недостойными вечной жизни, то вот, мы обращаемся к язычникам.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36104, 'rst66', 'act', 13, 45, 'Но Иудеи, увидев народ, исполнились зависти и, противореча и злословя, сопротивлялись тому, что говорил Павел.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36103, 'rst66', 'act', 13, 44, 'В следующую субботу почти весь город собрался слушать слово Божие.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36102, 'rst66', 'act', 13, 43, 'Когда же собрание было распущено, то многие Иудеи и чтители <i>Бога</i>, обращенные из язычников, последовали за Павлом и Варнавою, которые, беседуя с ними, убеждали их пребывать в благодати Божией.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36101, 'rst66', 'act', 13, 42, 'При выходе их из Иудейской синагоги язычники просили их говорить о том же в следующую субботу.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36100, 'rst66', 'act', 13, 41, '«смотрите, презрители, подивитесь и исчезните; ибо Я делаю дело во дни ваши, дело, которому не поверили бы вы, если бы кто рассказывал вам».');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36099, 'rst66', 'act', 13, 40, 'Берегитесь же, чтобы не пришло на вас сказанное у пророков:');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36098, 'rst66', 'act', 13, 39, 'и во всем, в чем вы не могли оправдаться законом Моисеевым, оправдывается Им всякий верующий.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36097, 'rst66', 'act', 13, 38, 'Итак, да будет известно вам, мужи братия, что ради Него возвещается вам прощение грехов;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36096, 'rst66', 'act', 13, 37, 'а Тот, Которого Бог воскресил, не увидел тления.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36095, 'rst66', 'act', 13, 36, 'Давид, в свое время послужив изволению Божию, почил, и приложился к отцам своим, и увидел тление;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36094, 'rst66', 'act', 13, 35, 'Посему и в другом <i>месте</i> говорит: «не дашь Святому Твоему увидеть тление».');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36093, 'rst66', 'act', 13, 34, 'А что воскресил Его из мертвых, так что Он уже не обратится в тление, <i>о сем</i> сказал так: «Я дам вам милости, <i>обещанные</i> Давиду, верно».');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36092, 'rst66', 'act', 13, 33, 'как и во втором псалме написано: «Ты Сын Мой: Я ныне родил Тебя».');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36091, 'rst66', 'act', 13, 32, 'И мы благовествуем вам, что обетование, данное отцам, Бог исполнил нам, детям их, воскресив Иисуса,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36090, 'rst66', 'act', 13, 31, 'Он в продолжение многих дней являлся тем, которые вышли с Ним из Галилеи в Иерусалим и которые ныне суть свидетели Его перед народом.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36089, 'rst66', 'act', 13, 30, 'Но Бог воскресил Его из мертвых.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36088, 'rst66', 'act', 13, 29, 'Когда же исполнили всё написанное о Нем, то, сняв с древа, положили Его во гроб.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36087, 'rst66', 'act', 13, 28, 'и, не найдя в Нем никакой вины, достойной смерти, просили Пилата убить Его.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36086, 'rst66', 'act', 13, 27, 'Ибо жители Иерусалима и начальники их, не узнав Его и осудив, исполнили слова пророческие, читаемые каждую субботу,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36085, 'rst66', 'act', 13, 26, 'Мужи братия, дети рода Авраамова, и боящиеся Бога между вами! вам послано слово спасения сего.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36084, 'rst66', 'act', 13, 25, 'При окончании же поприща своего Иоанн говорил: «за кого почитаете вы меня? я не тот; но вот, идет за мною, у Которого я недостоин развязать обувь на ногах».');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36083, 'rst66', 'act', 13, 24, 'Перед самым явлением Его Иоанн проповедовал крещение покаяния всему народу Израильскому.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36082, 'rst66', 'act', 13, 23, 'Из его‐то потомства Бог по обетованию воздвиг Израилю Спасителя Иисуса.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36081, 'rst66', 'act', 13, 22, 'Отринув его, поставил им царем Давида, о котором и сказал, свидетельствуя: «нашел Я мужа по сердцу Моему, Давида, сына Иессеева, который исполнит все хотения Мои».');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36080, 'rst66', 'act', 13, 21, 'Потом просили они царя, и Бог дал им Саула, сына Кисова, мужа из колена Вениаминова. <i>Так прошло</i> лет сорок.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36079, 'rst66', 'act', 13, 20, 'И после сего, около четырехсот пятидесяти лет, давал им судей до пророка Самуила.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36078, 'rst66', 'act', 13, 19, 'И, истребив семь народов в земле Ханаанской, разделил им в наследие землю их.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36077, 'rst66', 'act', 13, 18, 'и около сорока лет времени питал их в пустыне.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36076, 'rst66', 'act', 13, 17, 'Бог народа сего избрал отцов наших и возвысил сей народ во время пребывания в земле Египетской, и мышцею вознесенною вывел их из нее,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36075, 'rst66', 'act', 13, 16, 'Павел, встав и дав знак рукою, сказал: мужи Израильтяне и боящиеся Бога! послушайте.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36074, 'rst66', 'act', 13, 15, 'После чтения закона и пророков начальники синагоги послали сказать им: мужи братия! если у вас есть слово наставления к народу, говорите.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36073, 'rst66', 'act', 13, 14, 'Они же, проходя от Пергии, прибыли в Антиохию Писидийскую и, войдя в синагогу в день субботний, сели.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36072, 'rst66', 'act', 13, 13, 'Отплыв из Пафа, Павел и бывшие при нем прибыли в Пергию, в Памфилии. Но Иоанн, отделившись от них, возвратился в Иерусалим.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36071, 'rst66', 'act', 13, 12, 'Тогда проконсул, увидев происшедшее, уверовал, дивясь учению Господню.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36070, 'rst66', 'act', 13, 11, 'И ныне вот, рука Господня на тебя: ты будешь слеп и не увидишь солнца до времени. И вдруг напал на него мрак и тьма, и он, обращаясь туда и сюда, искал вожатого.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36069, 'rst66', 'act', 13, 10, 'сказал: о, исполненный всякого коварства и всякого злодейства, сын диавола, враг всякой правды! перестанешь ли ты совращать с прямых путей Господних?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36068, 'rst66', 'act', 13, 9, 'Но Савл, он же и Павел, исполнившись Духа Святого и устремив на него взор,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36067, 'rst66', 'act', 13, 8, 'А Елима волхв (ибо то&#769; значит имя его) противился им, стараясь отвратить проконсула от веры.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36066, 'rst66', 'act', 13, 7, 'который находился с проконсулом Сергием Павлом, мужем разумным. Сей, призвав Варнаву и Савла, пожелал услышать слово Божие.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36065, 'rst66', 'act', 13, 6, 'Пройдя весь остров до Пафа, нашли они некоторого волхва, лжепророка, Иудеянина, именем Вариисуса,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36064, 'rst66', 'act', 13, 5, 'и, быв в Саламине, проповедовали слово Божие в синагогах Иудейских; имели же при себе и Иоанна для служения.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36063, 'rst66', 'act', 13, 4, 'Сии, быв посланы Духом Святым, пришли в Селевкию, а оттуда отплыли в Кипр;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36062, 'rst66', 'act', 13, 3, 'Тогда они, совершив пост и молитву и возложив на них руки, отпустили их.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36061, 'rst66', 'act', 13, 2, 'Когда они служили Господу и постились, Дух Святой сказал: отделите Мне Варнаву и Савла на дело, к которому Я призвал их.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36060, 'rst66', 'act', 13, 1, 'В Антиохии, в тамошней церкви, были некоторые пророки и учители: Варнава, и Симеон, называемый Нигер, и Луций Киринеянин, и Манаил, совоспитанник Ирода четвертовластника, и Савл.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36059, 'rst66', 'act', 12, 25, 'А Варнава и Савл, по исполнении поручения, возвратились из Иерусалима [в Антиохию], взяв с собою и Иоанна, прозванного Марком.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36058, 'rst66', 'act', 12, 24, 'Слово же Божие росло и распространялось.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36057, 'rst66', 'act', 12, 23, 'Но вдруг Ангел Господень поразил его за то, что он не воздал славы Богу; и он, быв изъеден червями, умер.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36056, 'rst66', 'act', 12, 22, 'а народ восклицал: <i>это</i> голос Бога, а не человека.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36055, 'rst66', 'act', 12, 21, 'В назначенный день Ирод, одевшись в царскую одежду, сел на возвышенном месте и говорил к ним;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36054, 'rst66', 'act', 12, 20, 'Ирод был раздражен на Тирян и Сидонян; они же, согласившись, пришли к нему и, склонив на свою сторону Власта, постельника царского, просили мира, потому что область их питалась от <i>области</i> царской.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36053, 'rst66', 'act', 12, 19, 'Ирод же, поискав его и не найдя, судил стражей и велел казнить их. Потом он отправился из Иудеи в Кесарию и <i>там</i> оставался.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36052, 'rst66', 'act', 12, 18, 'По наступлении дня между воинами сделалась большая тревога о том, что сделалось с Петром.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36051, 'rst66', 'act', 12, 17, 'Он же, дав знак рукою, чтобы молчали, рассказал им, как Господь вывел его из темницы, и сказал: уведомьте о сем Иакова и братьев. Потом, выйдя, пошел в другое место.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36050, 'rst66', 'act', 12, 16, 'Между тем Петр продолжал стучать. Когда же отворили, то увидели его и изумились.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36049, 'rst66', 'act', 12, 15, 'А те сказали ей: в своем ли ты уме? Но она утверждала свое. Они же говорили: это Ангел его.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36048, 'rst66', 'act', 12, 14, 'и, узнав голос Петра, от радости не отворила ворот, но, вбежав, объявила, что Петр стоит у ворот.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36047, 'rst66', 'act', 12, 13, 'Когда же Петр постучался у ворот, то вышла послушать служанка, именем Рода;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36046, 'rst66', 'act', 12, 12, 'И, осмотревшись, пришел к дому Марии, матери Иоанна, называемого Марком, где многие собрались и молились.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36045, 'rst66', 'act', 12, 11, 'Тогда Петр, придя в себя, сказал: теперь я вижу воистину, что Господь послал Ангела Своего и избавил меня из руки Ирода и от всего, чего ждал народ Иудейский.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36044, 'rst66', 'act', 12, 10, 'Пройдя первую и вторую стражу, они пришли к железным воротам, ведущим в город, которые сами собою отворились им; они вышли и прошли одну улицу, и вдруг Ангела не стало с ним.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36043, 'rst66', 'act', 12, 9, '<i>Петр</i> вышел и следовал за ним, не зная, что делаемое Ангелом было действительно, а думая, что видит видение.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36042, 'rst66', 'act', 12, 8, 'И сказал ему Ангел: опояшься и обуйся. Он сделал так. Потом говорит ему: надень одежду твою и иди за мною.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36041, 'rst66', 'act', 12, 7, 'И вот, Ангел Господень предстал, и свет осиял темницу. <i>Ангел</i>, толкнув Петра в бок, пробудил его и сказал: встань скорее. И цепи упали с рук его.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36040, 'rst66', 'act', 12, 6, 'Когда же Ирод хотел вывести его, в ту ночь Петр спал между двумя воинами, скованный двумя цепями, и стражи у дверей стерегли темницу.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36039, 'rst66', 'act', 12, 5, 'Итак, Петра стерегли в темнице, между тем церковь прилежно молилась о нем Богу.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36038, 'rst66', 'act', 12, 4, 'и, задержав его, посадил в темницу, и приказал четырем четверицам воинов стеречь его, намереваясь после Пасхи вывести его к народу.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36037, 'rst66', 'act', 12, 3, 'Видя же, что это приятно Иудеям, вслед за тем взял и Петра, тогда были дни опресноков,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36036, 'rst66', 'act', 12, 2, 'и убил Иакова, брата Иоаннова, мечом.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36035, 'rst66', 'act', 12, 1, 'В то время царь Ирод поднял руки на некоторых из принадлежащих к церкви, чтобы сделать им зло,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36034, 'rst66', 'act', 11, 30, 'что и сделали, послав <i>собранное</i> к пресвитерам через Варнаву и Савла.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36033, 'rst66', 'act', 11, 29, 'Тогда ученики положили, каждый по достатку своему, послать пособие братьям, живущим в Иудее,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36032, 'rst66', 'act', 11, 28, 'И один из них, по имени Агав, встав, предвозвестил Духом, что по всей вселенной будет великий голод, который и был при кесаре Клавдии.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36031, 'rst66', 'act', 11, 27, 'В те дни пришли из Иерусалима в Антиохию пророки.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36030, 'rst66', 'act', 11, 26, 'Целый год собирались они в церкви и учили немалое число людей, и ученики в Антиохии в первый раз стали называться Христианами.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36029, 'rst66', 'act', 11, 25, 'Потом Варнава пошел в Тарс искать Савла и, найдя его, привел в Антиохию.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36028, 'rst66', 'act', 11, 24, 'ибо он был муж добрый и исполненный Духа Святого и веры. И приложилось довольно народа к Господу.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36027, 'rst66', 'act', 11, 23, 'Он, прибыв и увидев благодать Божию, возрадовался и убеждал всех держаться Господа искренним сердцем;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36026, 'rst66', 'act', 11, 22, 'Дошел слух о сем до церкви Иерусалимской, и поручили Варнаве идти в Антиохию.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36025, 'rst66', 'act', 11, 21, 'И была рука Господня с ними, и великое число, уверовав, обратилось к Господу.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36024, 'rst66', 'act', 11, 20, 'Были же некоторые из них Кипряне и Киринейцы, которые, придя в Антиохию, говорили Еллинам, благовествуя Господа Иисуса.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36023, 'rst66', 'act', 11, 19, 'Между тем рассеявшиеся от гонения, бывшего после Стефана, прошли до Финикии и Кипра и Антиохии, никому не проповедуя слово, кроме Иудеев.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36022, 'rst66', 'act', 11, 18, 'Выслушав это, они успокоились и прославили Бога, говоря: видно, и язычникам дал Бог покаяние в жизнь.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36021, 'rst66', 'act', 11, 17, 'Итак, если Бог дал им такой же дар, как и нам, уверовавшим в Господа Иисуса Христа, то кто же я, чтобы мог воспрепятствовать Богу?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36020, 'rst66', 'act', 11, 16, 'Тогда вспомнил я слово Господа, как Он говорил: «Иоанн крестил водою, а вы будете крещены Духом Святым».');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36019, 'rst66', 'act', 11, 15, 'Когда же начал я говорить, сошел на них Дух Святой, как и на нас вначале.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36018, 'rst66', 'act', 11, 14, 'он скажет тебе слова, которыми спасешься ты и весь дом твой».');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36017, 'rst66', 'act', 11, 13, 'Он рассказал нам, как он видел в доме своем Ангела [святого], который стал и сказал ему: «пошли в Иоппию людей и призови Симона, называемого Петром;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36016, 'rst66', 'act', 11, 12, 'Дух сказал мне, чтобы я шел с ними, нимало не сомневаясь. Пошли со мною и сии шесть братьев, и мы пришли в дом <i>того</i> человека.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36015, 'rst66', 'act', 11, 11, 'И вот, в тот самый час три человека стали перед домом, в котором я был, посланные из Кесарии ко мне.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36014, 'rst66', 'act', 11, 10, 'Это было трижды, и опять поднялось всё на небо.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36013, 'rst66', 'act', 11, 9, 'И отвечал мне голос вторично с неба: «что Бог очистил, того ты не почитай нечистым».');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36012, 'rst66', 'act', 11, 8, 'Я же сказал: «нет, Господи, ничего скверного или нечистого никогда не входило в уста мои».');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36011, 'rst66', 'act', 11, 7, 'И услышал я голос, говорящий мне: «встань, Петр, заколи и ешь».');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36010, 'rst66', 'act', 11, 6, 'Я посмотрел в него и, рассматривая, увидел четвероногих земных, зверей, пресмыкающихся и птиц небесных.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36009, 'rst66', 'act', 11, 5, 'в городе Иоппии я молился и в исступлении видел видение: сходил некоторый сосуд, как бы большое полотно, за четыре угла спускаемое с неба, и спустилось ко мне.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36008, 'rst66', 'act', 11, 4, 'Петр же начал пересказывать им по порядку, говоря:');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36007, 'rst66', 'act', 11, 3, 'говоря: ты ходил к людям необрезанным и ел с ними.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36006, 'rst66', 'act', 11, 2, 'И когда Петр пришел в Иерусалим, обрезанные упрекали его,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36005, 'rst66', 'act', 11, 1, 'Услышали Апостолы и братия, бывшие в Иудее, что и язычники приняли слово Божие.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36004, 'rst66', 'act', 10, 48, 'И велел им креститься во имя Иисуса Христа. Потом они просили его пробыть у них несколько дней.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36003, 'rst66', 'act', 10, 47, 'кто может запретить креститься водою тем, которые, как и мы, получили Святого Духа?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36002, 'rst66', 'act', 10, 46, 'ибо слышали их, говорящих языками и величающих Бога. Тогда Петр сказал:');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36001, 'rst66', 'act', 10, 45, 'И верующие из обрезанных, пришедшие с Петром, изумились, что дар Святого Духа излился и на язычников;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (36000, 'rst66', 'act', 10, 44, 'Когда Петр еще продолжал эту речь, Дух Святой сошел на всех, слушавших слово.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35999, 'rst66', 'act', 10, 43, 'О Нем все пророки свидетельствуют, что всякий верующий в Него получит прощение грехов именем Его.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35998, 'rst66', 'act', 10, 42, 'И Он повелел нам проповедовать людям и свидетельствовать, что Он есть определенный от Бога Судия живых и мертвых.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35997, 'rst66', 'act', 10, 41, 'не всему народу, но свидетелям, предызбранным от Бога, нам, которые с Ним ели и пили, по воскресении Его из мертвых.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35996, 'rst66', 'act', 10, 40, 'Сего Бог воскресил в третий день, и дал Ему являться');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35995, 'rst66', 'act', 10, 39, 'И мы свидетели всего, что сделал Он в стране Иудейской и в Иерусалиме, и что наконец Его убили, повесив на древе.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35994, 'rst66', 'act', 10, 38, 'как Бог Духом Святым и силою помазал Иисуса из Назарета, и Он ходил, благотворя и исцеляя всех, обладаемых диаволом, потому что Бог был с Ним.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35993, 'rst66', 'act', 10, 37, 'Вы знаете происходившее по всей Иудее, начиная от Галилеи, после крещения, проповеданного Иоанном:');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35992, 'rst66', 'act', 10, 36, 'Он послал сынам Израилевым слово, благовествуя мир чрез Иисуса Христа; Сей есть Господь всех.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35991, 'rst66', 'act', 10, 35, 'но во всяком народе боящийся Его и поступающий по правде приятен Ему.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35990, 'rst66', 'act', 10, 34, 'Петр отверз уста и сказал: истинно познаю&#769;, что Бог нелицеприятен,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35989, 'rst66', 'act', 10, 33, 'Тотчас послал я к тебе, и ты хорошо сделал, что пришел. Теперь все мы предстоим пред Богом, чтобы выслушать все, что повелено тебе от Бога.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35988, 'rst66', 'act', 10, 32, 'Итак пошли в Иоппию и призови Симона, называемого Петром: он гостит в доме кожевника Симона при море; он придет и скажет тебе».');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35987, 'rst66', 'act', 10, 31, 'и говорит: «Корнилий! услышана молитва твоя, и милостыни твои воспомянулись пред Богом.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35986, 'rst66', 'act', 10, 30, 'Корнилий сказал: четвертого дня я постился до теперешнего часа и в девятом часу молился в своем доме; и вот, стал предо мною муж в светлой одежде');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35985, 'rst66', 'act', 10, 29, 'Посему я, будучи позван, и пришел беспрекословно. Итак, спрашиваю: для какого дела вы призвали меня?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35984, 'rst66', 'act', 10, 28, 'И сказал им: вы знаете, что Иудею возбранено сообщаться или сближаться с иноплеменником; но мне Бог открыл, чтобы я не почитал ни одного человека скверным или нечистым.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35983, 'rst66', 'act', 10, 27, 'И, беседуя с ним, вошел <i>в дом</i>, и нашел многих собравшихся.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35982, 'rst66', 'act', 10, 26, 'Петр же поднял его, говоря: встань; я тоже человек.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35981, 'rst66', 'act', 10, 25, 'Когда Петр входил, Корнилий встретил его и поклонился, пав к ногам его.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35980, 'rst66', 'act', 10, 24, 'В следующий день пришли они в Кесарию. Корнилий же ожидал их, созвав родственников своих и близких друзей.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35979, 'rst66', 'act', 10, 23, 'Тогда Петр, пригласив их, угостил. А на другой день, встав, пошел с ними, и некоторые из братий Иоппийских пошли с ним.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35978, 'rst66', 'act', 10, 22, 'Они же сказали: Корнилий сотник, муж добродетельный и боящийся Бога, одобряемый всем народом Иудейским, получил от святого Ангела повеление призвать тебя в дом свой и послушать речей твоих.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35977, 'rst66', 'act', 10, 21, 'Петр, сойдя к людям, присланным к нему от Корнилия, сказал: я тот, которого вы ищете; за каким делом пришли вы?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35976, 'rst66', 'act', 10, 20, 'встань, сойди и иди с ними, нимало не сомневаясь; ибо Я послал их.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35975, 'rst66', 'act', 10, 19, 'Между тем, как Петр размышлял о видении, Дух сказал ему: вот, три человека ищут тебя;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35974, 'rst66', 'act', 10, 18, 'и, крикнув, спросили: здесь ли Симон, называемый Петром?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35973, 'rst66', 'act', 10, 17, 'Когда же Петр недоумевал в себе, что бы значило видение, которое он видел, вот, мужи, посланные Корнилием, расспросив о доме Симона, остановились у ворот,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35972, 'rst66', 'act', 10, 16, 'Это было трижды, и сосуд опять поднялся на небо.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35971, 'rst66', 'act', 10, 15, 'Тогда в другой раз <i>был</i> глас к нему: что Бог очистил, того ты не почитай нечистым.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35970, 'rst66', 'act', 10, 14, 'Но Петр сказал: нет, Господи, я никогда не ел ничего скверного или нечистого.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35969, 'rst66', 'act', 10, 13, 'И был глас к нему: встань, Петр, заколи и ешь.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35968, 'rst66', 'act', 10, 12, 'в нем находились всякие четвероногие земные, звери, пресмыкающиеся и птицы небесные.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35967, 'rst66', 'act', 10, 11, 'и видит отверстое небо и сходящий к нему некоторый сосуд, как бы большое полотно, привязанное за четыре угла и опускаемое на землю;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35966, 'rst66', 'act', 10, 10, 'И почувствовал он голод, и хотел есть. Между тем, как приготовляли, он пришел в исступление');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35965, 'rst66', 'act', 10, 9, 'На другой день, когда они шли и приближались к городу, Петр около шестого часа взошел на верх дома помолиться.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35964, 'rst66', 'act', 10, 8, 'и рассказав им все, послал их в Иоппию.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35963, 'rst66', 'act', 10, 7, 'Когда Ангел, говоривший с Корнилием, отошел, то он, призвав двоих из своих слуг и благочестивого воина из находившихся при нем');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35962, 'rst66', 'act', 10, 6, 'Он гостит у некоего Симона кожевника, которого дом находится при море; он скажет тебе слова, которыми спасешься ты и весь дом твой.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35961, 'rst66', 'act', 10, 5, 'Итак, пошли людей в Иоппию и призови Симона, называемого Петром.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35960, 'rst66', 'act', 10, 4, 'Он же, взглянув на него и испугавшись, сказал: что&#769;, Господи? <i>Ангел</i> отвечал ему: молитвы твои и милостыни твои пришли на память пред Богом.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35959, 'rst66', 'act', 10, 3, 'Он в видении ясно видел около девятого часа дня Ангела Божия, который вошел к нему и сказал ему: Корнилий!');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35958, 'rst66', 'act', 10, 2, 'благочестивый и боящийся Бога со всем домом своим, творивший много милостыни народу и всегда молившийся Богу.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35957, 'rst66', 'act', 10, 1, 'В Кесарии был некоторый муж, именем Корнилий, сотник из полка, называемого Италийским,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35956, 'rst66', 'act', 9, 43, 'И довольно дней пробыл он в Иоппии у некоторого Симона кожевника.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35955, 'rst66', 'act', 9, 42, 'Это сделалось известным по всей Иоппии, и многие уверовали в Господа.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35954, 'rst66', 'act', 9, 41, 'Он, подав ей руку, поднял ее и, призвав святых и вдовиц, поставил ее перед ними живою.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35953, 'rst66', 'act', 9, 40, 'Петр выслал всех вон и, преклонив колени, помолился, и, обратившись к телу, сказал: Тавифа! встань. И она открыла глаза свои и, увидев Петра, села.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35952, 'rst66', 'act', 9, 39, 'Петр, встав, пошел с ними; и когда он прибыл, ввели его в горницу, и все вдовицы со слезами предстали перед ним, показывая рубашки и платья, какие делала Серна, живя с ними.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35951, 'rst66', 'act', 9, 38, 'А как Лидда была близ Иоппии, то ученики, услышав, что Петр находится там, послали к нему двух человек просить, чтобы он не замедлил прийти к ним.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35950, 'rst66', 'act', 9, 37, 'Случилось в те дни, что она занемогла и умерла. Ее омыли и положили в горнице.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35949, 'rst66', 'act', 9, 36, 'В Иоппии находилась одна ученица, именем Тавифа, что значит: «серна»; она была исполнена добрых дел и творила много милостынь.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35948, 'rst66', 'act', 9, 35, 'И видели его все, живущие в Лидде и в Сароне, которые и обратились к Господу.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35947, 'rst66', 'act', 9, 34, 'Петр сказал ему: Эней! исцеляет тебя Иисус Христос; встань с постели твоей. И он тотчас встал.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35946, 'rst66', 'act', 9, 33, 'Там нашел он одного человека, именем Энея, который восемь уже лет лежал в постели в расслаблении.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35945, 'rst66', 'act', 9, 32, 'Случилось, что Петр, обходя всех, пришел и к святым, живущим в Лидде.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35944, 'rst66', 'act', 9, 31, 'Церкви же по всей Иудее, Галилее и Самарии были в покое, назидаясь и ходя в страхе Господнем; и, при утешении от Святого Духа, умножались.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35943, 'rst66', 'act', 9, 30, 'Братия, узнав <i>о сем</i>, отправили его в Кесарию и препроводили в Тарс.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35942, 'rst66', 'act', 9, 29, 'Говорил также и состязался с Еллинистами; а они покушались убить его.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35941, 'rst66', 'act', 9, 28, 'И пребывал он с ними, входя и исходя, в Иерусалиме, и смело проповедовал во имя Господа Иисуса.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35940, 'rst66', 'act', 9, 27, 'Варнава же, взяв его, пришел к Апостолам и рассказал им, как на пути он видел Господа, и что говорил ему Господь, и как он в Дамаске смело проповедовал во имя Иисуса.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35939, 'rst66', 'act', 9, 26, 'Савл прибыл в Иерусалим и старался пристать к ученикам; но все боялись его, не веря, что он ученик.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35938, 'rst66', 'act', 9, 25, 'Ученики же ночью, взяв его, спустили по стене в корзине.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35937, 'rst66', 'act', 9, 24, 'Но Савл узнал об этом умысле их. А они день и ночь стерегли у ворот, чтобы убить его.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35936, 'rst66', 'act', 9, 23, 'Когда же прошло довольно времени, Иудеи согласились убить его.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35935, 'rst66', 'act', 9, 22, 'А Савл более и более укреплялся и приводил в замешательство Иудеев, живущих в Дамаске, доказывая, что Сей есть Христос.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35934, 'rst66', 'act', 9, 21, 'И все слышавшие дивились и говорили: не тот ли это самый, который гнал в Иерусалиме призывающих имя сие? да и сюда за тем пришел, чтобы вязать их и вести к первосвященникам.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35933, 'rst66', 'act', 9, 20, 'И тотчас стал проповедовать в синагогах об Иисусе, что Он есть Сын Божий.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35932, 'rst66', 'act', 9, 19, 'и, приняв пищи, укрепился. И был Савл несколько дней с учениками в Дамаске.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35931, 'rst66', 'act', 9, 18, 'И тотчас как бы чешуя отпала от глаз его, и вдруг он прозрел; и, встав, крестился,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35930, 'rst66', 'act', 9, 17, 'Анания пошел и вошел в дом и, возложив на него руки, сказал: брат Савл! Господь Иисус, явившийся тебе на пути, которым ты шел, послал меня, чтобы ты прозрел и исполнился Святого Духа.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35929, 'rst66', 'act', 9, 16, 'И Я покажу ему, сколько он должен пострадать за имя Мое.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35928, 'rst66', 'act', 9, 15, 'Но Господь сказал ему: иди, ибо он есть Мой избранный сосуд, чтобы возвещать имя Мое перед народами и царями и сынами Израилевыми.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35927, 'rst66', 'act', 9, 14, 'и здесь имеет от первосвященников власть вязать всех, призывающих имя Твое.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35926, 'rst66', 'act', 9, 13, 'Анания отвечал: Господи! я слышал от многих о сем человеке, сколько зла сделал он святым Твоим в Иерусалиме;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35925, 'rst66', 'act', 9, 12, 'и видел в видении мужа, именем Ананию, пришедшего к нему и возложившего на него руку, чтобы он прозрел.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35924, 'rst66', 'act', 9, 11, 'Господь же <i>сказал</i> ему: встань и пойди на улицу, так называемую Прямую, и спроси в Иудином доме Тарсянина, по имени Савла; он теперь молится,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35923, 'rst66', 'act', 9, 10, 'В Дамаске был один ученик, именем Анания; и Господь в видении сказал ему: Анания! Он сказал: я, Господи.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35922, 'rst66', 'act', 9, 9, 'И три дня он не видел, и не ел, и не пил.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35921, 'rst66', 'act', 9, 8, 'Савл встал с земли и с открытыми глазами никого не видел. И повели его за руку, и привели в Дамаск.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35920, 'rst66', 'act', 9, 7, 'Люди же, шедшие с ним, стояли в оцепенении, слыша голос, а никого не видя.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35919, 'rst66', 'act', 9, 6, 'Он в трепете и ужасе сказал: Господи! что&#769; повелишь мне делать? И Господь <i>сказал</i> ему: встань и иди в город; и сказано будет тебе, что&#769; тебе надобно делать.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35918, 'rst66', 'act', 9, 5, 'Он сказал: кто Ты, Господи? Господь же сказал: Я Иисус, Которого ты гонишь. Трудно тебе идти против рожна.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35917, 'rst66', 'act', 9, 4, 'Он упал на землю и услышал голос, говорящий ему: Савл, Савл! что ты гонишь Меня?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35916, 'rst66', 'act', 9, 3, 'Когда же он шел и приближался к Дамаску, внезапно осиял его свет с неба.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35915, 'rst66', 'act', 9, 2, 'и выпросил у него письма в Дамаск к синагогам, чтобы, кого найдет последующих сему учению, и мужчин и женщин, связав, приводить в Иерусалим.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35914, 'rst66', 'act', 9, 1, 'Савл же, еще дыша угрозами и убийством на учеников Господа, пришел к первосвященнику');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35913, 'rst66', 'act', 8, 40, 'А Филипп оказался в Азоте и, проходя, благовествовал всем городам, пока пришел в Кесарию.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35912, 'rst66', 'act', 8, 39, 'Когда же они вышли из воды, Дух Святой сошел на евнуха, а Филиппа восхитил Ангел Господень, и евнух уже не видел его, и продолжал путь, радуясь.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35911, 'rst66', 'act', 8, 38, 'И приказал остановить колесницу, и сошли оба в воду, Филипп и евнух; и крестил его.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35910, 'rst66', 'act', 8, 37, 'Филипп же сказал ему: если веруешь от всего сердца, можно. Он сказал в ответ: верую, что Иисус Христос есть Сын Божий.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35909, 'rst66', 'act', 8, 36, 'Между тем, продолжая путь, они приехали к воде; и евнух сказал: вот вода; что препятствует мне креститься?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35908, 'rst66', 'act', 8, 35, 'Филипп отверз уста свои и, начав от сего Писания, благовествовал ему об Иисусе.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35907, 'rst66', 'act', 8, 34, 'Евнух же сказал Филиппу: прошу тебя <i>сказать:</i> о ком пророк говорит это? о себе ли или о ком другом?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35906, 'rst66', 'act', 8, 33, 'В уничижении Его суд Его совершился. Но род Его кто разъяснит? ибо вземлется от земли жизнь Его».');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35905, 'rst66', 'act', 8, 32, 'А место из Писания, которое он читал, было сие: «как овца, веден был Он на заклание, и, как агнец пред стригущим его безгласен, так Он не отверзает уст Своих.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35904, 'rst66', 'act', 8, 31, 'Он сказал: как могу разуметь, если кто не наставит меня? и попросил Филиппа взойти и сесть с ним.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35903, 'rst66', 'act', 8, 30, 'Филипп подошел и, услышав, что он читает пророка Исаию, сказал: разумеешь ли, что&#769; читаешь?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35902, 'rst66', 'act', 8, 29, 'Дух сказал Филиппу: подойди и пристань к сей колеснице.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35901, 'rst66', 'act', 8, 28, 'возвращался и, сидя на колеснице своей, читал пророка Исаию.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35900, 'rst66', 'act', 8, 27, 'Он встал и пошел. И вот, муж Ефиоплянин, евнух, вельможа Кандакии, царицы Ефиопской, хранитель всех сокровищ ее, приезжавший в Иерусалим для поклонения,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35899, 'rst66', 'act', 8, 26, 'А Филиппу Ангел Господень сказал: встань и иди на полдень, на дорогу, идущую из Иерусалима в Газу, на ту, которая пуста.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35898, 'rst66', 'act', 8, 25, 'Они же, засвидетельствовав и проповедав слово Господне, обратно пошли в Иерусалим и во многих селениях Самарийских проповедали Евангелие.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35897, 'rst66', 'act', 8, 24, 'Симон же сказал в ответ: помолитесь вы за меня Господу, дабы не постигло меня ничто из сказанного вами.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35896, 'rst66', 'act', 8, 23, 'ибо вижу тебя исполненного горькой желчи и в узах неправды.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35895, 'rst66', 'act', 8, 22, 'Итак, покайся в сем грехе твоем и молись Богу: может быть, отпустится тебе помысел сердца твоего;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35894, 'rst66', 'act', 8, 21, 'Нет тебе в сем части и жребия, ибо сердце твое неправо пред Богом.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35893, 'rst66', 'act', 8, 20, 'Но Петр сказал ему: серебро твое да будет в погибель с тобою, потому что ты помыслил дар Божий получить за деньги.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35892, 'rst66', 'act', 8, 19, 'говоря: дайте и мне власть сию, чтобы тот, на кого я возложу руки, получал Духа Святого.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35891, 'rst66', 'act', 8, 18, 'Симон же, увидев, что через возложение рук Апостольских подается Дух Святой, принес им деньги,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35890, 'rst66', 'act', 8, 17, 'Тогда возложили руки на них, и они приняли Духа Святого.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35889, 'rst66', 'act', 8, 16, 'Ибо Он не сходил еще ни на одного из них, а только были они крещены во имя Господа Иисуса.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35888, 'rst66', 'act', 8, 15, 'которые, придя, помолились о них, чтобы они приняли Духа Святого.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35887, 'rst66', 'act', 8, 14, 'Находившиеся в Иерусалиме Апостолы, услышав, что Самаряне приняли слово Божие, послали к ним Петра и Иоанна,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35886, 'rst66', 'act', 8, 13, 'Уверовал и сам Симон и, крестившись, не отходил от Филиппа; и, видя совершающиеся великие силы и знамения, изумлялся.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35885, 'rst66', 'act', 8, 12, 'Но, когда поверили Филиппу, благовествующему о Царствии Божием и о имени Иисуса Христа, то крестились и мужчины и женщины.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35884, 'rst66', 'act', 8, 11, 'А внимали ему потому, что он немалое время изумлял их волхвованиями.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35883, 'rst66', 'act', 8, 10, 'Ему внимали все, от малого до большого, говоря: сей есть великая сила Божия.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35882, 'rst66', 'act', 8, 9, 'Находился же в городе некоторый муж, именем Симон, который перед тем волхвовал и изумлял народ Самарийский, выдавая себя за кого‐то великого.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35881, 'rst66', 'act', 8, 8, 'И была радость великая в том городе.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35880, 'rst66', 'act', 8, 7, 'Ибо нечистые духи из многих, одержимых ими, выходили с великим воплем, а многие расслабленные и хромые исцелялись.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35879, 'rst66', 'act', 8, 6, 'Народ единодушно внимал тому, что говорил Филипп, слыша и видя, какие он творил чудеса.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35878, 'rst66', 'act', 8, 5, 'Так, Филипп пришел в город Самарийский и проповедовал им Христа.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35877, 'rst66', 'act', 8, 4, 'Между тем рассеявшиеся ходили и благовествовали слово.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35876, 'rst66', 'act', 8, 3, 'А Савл терзал церковь, входя в домы и влача мужчин и женщин, отдавал в темницу.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35875, 'rst66', 'act', 8, 2, 'Стефана же погребли мужи благоговейные и сделали великий плач по нем.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35874, 'rst66', 'act', 8, 1, 'Савл же одобрял убиение его. В те дни произошло великое гонение на церковь в Иерусалиме; и все, кроме Апостолов, рассеялись по разным местам Иудеи и Самарии.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35873, 'rst66', 'act', 7, 60, 'И, преклонив колени, воскликнул громким голосом: Господи! не вмени им греха сего. И, сказав сие, почил.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35872, 'rst66', 'act', 7, 59, 'и побивали камнями Стефана, который молился и говорил: Господи Иисусе! приими дух мой.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35871, 'rst66', 'act', 7, 58, 'и, выведя за город, стали побивать его камнями. Свидетели же положили свои одежды у ног юноши, именем Савла,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35870, 'rst66', 'act', 7, 57, 'Но они, закричав громким голосом, затыкали уши свои, и единодушно устремились на него,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35869, 'rst66', 'act', 7, 56, 'и сказал: вот, я вижу небеса отверстые и Сына Человеческого, стоящего одесную Бога.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35868, 'rst66', 'act', 7, 55, 'Стефан же, будучи исполнен Духа Святого, воззрев на небо, увидел славу Божию и Иисуса, стоящего одесную Бога,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35867, 'rst66', 'act', 7, 54, 'Слушая сие, они рвались сердцами своими и скрежетали на него зубами.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35866, 'rst66', 'act', 7, 53, 'вы, которые приняли закон при служении Ангелов и не сохранили.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35865, 'rst66', 'act', 7, 52, 'Кого из пророков не гнали отцы ваши? Они убили предвозвестивших пришествие Праведника, Которого предателями и убийцами сделались ныне вы,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35864, 'rst66', 'act', 7, 51, 'Жестоковыйные! люди с необрезанным сердцем и ушами! вы всегда противитесь Духу Святому, как отцы ваши, так и вы.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35863, 'rst66', 'act', 7, 50, 'Не Моя ли рука сотворила всё сие?»');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35862, 'rst66', 'act', 7, 49, '«небо престол Мой, и земля подножие ног Моих. Какой дом созиждете Мне, говорит Господь, или какое место для покоя Моего?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35861, 'rst66', 'act', 7, 48, 'Но Всевышний не в рукотворенных храмах живет, как говорит пророк:');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35860, 'rst66', 'act', 7, 47, 'Соломон же построил Ему дом.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35859, 'rst66', 'act', 7, 46, 'Сей обрел благодать пред Богом и молил, <i>чтобы</i> найти жилище Богу Иакова.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35858, 'rst66', 'act', 7, 45, 'Отцы наши с Иисусом, взяв ее, внесли во владения народов, изгнанных Богом от лица отцов наших. <i>Так было</i> до дней Давида.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35857, 'rst66', 'act', 7, 44, 'Скиния свидетельства была у отцов наших в пустыне, как повелел Говоривший Моисею сделать ее по образцу, им виденному.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35856, 'rst66', 'act', 7, 43, 'Вы приняли скинию Молохову и звезду бога вашего Ремфана, изображения, которые вы сделали, чтобы поклоняться им: и Я переселю вас далее Вавилона».');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35855, 'rst66', 'act', 7, 42, 'Бог же отвратился и оставил их служить воинству небесному, как написано в книге пророков: «дом Израилев! приносили ли вы Мне заколения и жертвы в продолжение сорока лет в пустыне?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35854, 'rst66', 'act', 7, 41, 'И сделали в те дни тельца, и принесли жертву идолу, и веселились перед делом рук своих.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35853, 'rst66', 'act', 7, 40, 'сказав Аарону: «сделай нам богов, которые предшествовали бы нам; ибо с Моисеем, который вывел нас из земли Египетской, не знаем, что случилось».');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35852, 'rst66', 'act', 7, 39, 'которому отцы наши не хотели быть послушными, но отринули его и обратились сердцами своими к Египту,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35851, 'rst66', 'act', 7, 38, 'Это тот, который был в собрании в пустыне с Ангелом, говорившим ему на горе Синае, и с отцами нашими, и который принял живые слова, чтобы передать нам,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35850, 'rst66', 'act', 7, 37, 'Это тот Моисей, который сказал сынам Израилевым: «Пророка воздвигнет вам Господь Бог ваш из братьев ваших, как меня; Его слушайте».');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35849, 'rst66', 'act', 7, 36, 'Сей вывел их, сотворив чудеса и знамения в земле Египетской, и в Чермном море, и в пустыне в продолжение сорока лет.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35848, 'rst66', 'act', 7, 35, 'Сего Моисея, которого они отвергли, сказав: «кто тебя поставил начальником и судьею?», сего Бог чрез Ангела, явившегося ему в терновом кусте, послал начальником и избавителем.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35847, 'rst66', 'act', 7, 34, 'Я вижу притеснение народа Моего в Египте, и слышу стенание его, и нисшел избавить его: итак, пойди, Я пошлю тебя в Египет».');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35846, 'rst66', 'act', 7, 33, 'И сказал ему Господь: «сними обувь с ног твоих, ибо место, на котором ты стоишь, есть земля святая.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35845, 'rst66', 'act', 7, 32, '«Я Бог отцов твоих, Бог Авраама и Бог Исаака и Бог Иакова». Моисей, объятый трепетом, не смел смотреть.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35844, 'rst66', 'act', 7, 31, 'Моисей, увидев, дивился видению; а когда подходил рассмотреть, был к нему глас Господень:');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35843, 'rst66', 'act', 7, 30, 'По исполнении сорока лет явился ему в пустыне горы Синая Ангел Господень в пламени горящего тернового куста.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35842, 'rst66', 'act', 7, 29, 'От сих слов Моисей убежал и сделался пришельцем в земле Мадиамской, где родились от него два сына.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35841, 'rst66', 'act', 7, 28, 'Не хочешь ли ты убить и меня, как вчера убил Египтянина?»');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35840, 'rst66', 'act', 7, 27, 'Но обижающий ближнего оттолкнул его, сказав: «кто тебя поставил начальником и судьею над нами?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35839, 'rst66', 'act', 7, 26, 'На следующий день, когда некоторые из них дрались, он явился и склонял их к миру, говоря: «вы братья; зачем обижаете друг друга?»');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35838, 'rst66', 'act', 7, 25, 'Он думал, поймут братья его, что Бог рукою его дает им спасение; но они не поняли.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35837, 'rst66', 'act', 7, 24, 'И, увидев одного из них обижаемого, вступился и отомстил за оскорбленного, поразив Египтянина.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35836, 'rst66', 'act', 7, 23, 'Когда же исполнилось ему сорок лет, пришло ему на сердце посетить братьев своих, сынов Израилевых.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35835, 'rst66', 'act', 7, 22, 'И научен был Моисей всей мудрости Египетской, и был силен в словах и делах.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35834, 'rst66', 'act', 7, 21, 'А когда был брошен, взяла его дочь фараонова и воспитала его у себя, как сына.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35833, 'rst66', 'act', 7, 20, 'В это время родился Моисей, и был прекрасен пред Богом. Три месяца он был питаем в доме отца своего.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35832, 'rst66', 'act', 7, 19, 'Сей, ухищряясь против рода нашего, притеснял отцов наших, принуждая их бросать детей своих, чтобы не оставались в живых.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35831, 'rst66', 'act', 7, 18, 'до тех пор, как восстал иной царь, который не знал Иосифа.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35830, 'rst66', 'act', 7, 17, 'А по мере, как приближалось время <i>исполниться</i> обетованию, о котором клялся Бог Аврааму, народ возрастал и умножался в Египте,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35829, 'rst66', 'act', 7, 16, 'и перенесены были в Сихем и положены во гробе, который купил Авраам ценою серебра у сынов Еммора Сихемова.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35828, 'rst66', 'act', 7, 15, 'Иаков перешел в Египет, и скончался сам и отцы наши;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35827, 'rst66', 'act', 7, 14, 'Иосиф, послав, призвал отца своего Иакова и все родство свое, душ семьдесят пять.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35826, 'rst66', 'act', 7, 13, 'А когда [они пришли] во второй раз, Иосиф открылся братьям своим, и известен стал фараону род Иосифов.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35825, 'rst66', 'act', 7, 12, 'Иаков же, услышав, что есть хлеб в Египте, послал <i>туда</i> отцов наших в первый раз.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35824, 'rst66', 'act', 7, 11, 'И пришел голод и великая скорбь на всю землю Египетскую и Ханаанскую, и отцы наши не находили пропитания.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35823, 'rst66', 'act', 7, 10, 'и избавил его от всех скорбей его, и даровал мудрость ему и благоволение царя Египетского фараона, <i>который</i> и поставил его начальником над Египтом и над всем домом своим.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35822, 'rst66', 'act', 7, 9, 'Патриархи, по зависти, продали Иосифа в Египет; но Бог был с ним,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35821, 'rst66', 'act', 7, 8, 'И дал ему завет обрезания. По сем родил он Исаака и обрезал его в восьмой день; а Исаак <i>родил</i> Иакова, Иаков же двенадцать патриархов.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35820, 'rst66', 'act', 7, 7, '«Но Я, сказал Бог, произведу суд над тем народом, у которого они будут в порабощении; и после того они выйдут и будут служить Мне на сем месте».');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35819, 'rst66', 'act', 7, 6, 'И сказал ему Бог, что потомки его будут переселенцами в чужой земле и будут в порабощении и притеснении лет четыреста.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35818, 'rst66', 'act', 7, 5, 'И не дал ему на ней наследства ни на стопу ноги, а обещал дать ее во владение ему и потомству его по нем, когда еще был он бездетен.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35817, 'rst66', 'act', 7, 4, 'Тогда он вышел из земли Халдейской и поселился в Харране; а оттуда, по смерти отца его, переселил его <i>Бог</i> в сию землю, в которой вы ныне живете.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35816, 'rst66', 'act', 7, 3, 'и сказал ему: «выйди из земли твоей и из родства твоего и из дома отца твоего и пойди в землю, которую покажу тебе».');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35815, 'rst66', 'act', 7, 2, 'Но он сказал: мужи братия и отцы! послушайте. Бог славы явился отцу нашему Аврааму в Месопотамии, прежде переселения его в Харран,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35814, 'rst66', 'act', 7, 1, 'Тогда сказал первосвященник: так ли это?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35813, 'rst66', 'act', 6, 15, 'И все, сидящие в синедрионе, смотря на него, видели лицо его, как лицо Ангела.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35812, 'rst66', 'act', 6, 14, 'Ибо мы слышали, как он говорил, что Иисус Назорей разрушит место сие и переменит обычаи, которые передал нам Моисей.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35811, 'rst66', 'act', 6, 13, 'И представили ложных свидетелей, которые говорили: этот человек не перестает говорить хульные слова на святое место сие и на закон.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35810, 'rst66', 'act', 6, 12, 'И возбудили народ, и старейшин, и книжников и, напав, схватили его и повели в синедрион.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35809, 'rst66', 'act', 6, 11, 'Тогда научили они некоторых сказать: мы слышали, как он говорил хульные слова на Моисея и на Бога.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35808, 'rst66', 'act', 6, 10, 'но не могли противостоять мудрости и Духу, Которым он говорил.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35807, 'rst66', 'act', 6, 9, 'Некоторые из так называемой синагоги Либертинцев, и Киринейцев, и Александрийцев, и некоторые из Киликии и Асии вступили в спор со Стефаном;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35806, 'rst66', 'act', 6, 8, 'А Стефан, исполненный веры и силы, совершал великие чудеса и знамения в народе.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35805, 'rst66', 'act', 6, 7, 'И слово Божие росло, и число учеников весьма умножалось в Иерусалиме; и из священников очень многие покорились вере.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35804, 'rst66', 'act', 6, 6, 'их поставили перед Апостолами, и <i>сии</i>, помолившись, возложили на них руки.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35803, 'rst66', 'act', 6, 5, 'И угодно было это предложение всему собранию; и избрали Стефана, мужа, исполненного веры и Духа Святого, и Филиппа, и Прохора, и Никанора, и Тимона, и Пармена, и Николая Антиохийца, обращенного из язычников;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35802, 'rst66', 'act', 6, 4, 'а мы постоянно пребудем в молитве и служении слова.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35801, 'rst66', 'act', 6, 3, 'Итак, братия, выберите из среды себя семь человек изведанных, исполненных Святого Духа и мудрости: их поставим на эту службу;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35800, 'rst66', 'act', 6, 2, 'Тогда двенадцать <i>Апостолов</i>, созвав множество учеников, сказали: нехорошо нам, оставив слово Божие, пещись о столах.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35799, 'rst66', 'act', 6, 1, 'В эти дни, когда умножились ученики, произошел у Еллинистов ропот на Евреев за то, что вдовицы их пренебрегаемы были в ежедневном раздаянии потребностей.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35798, 'rst66', 'act', 5, 42, 'И всякий день в храме и по домам не переставали учить и благовествовать об Иисусе Христе.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35797, 'rst66', 'act', 5, 41, 'Они же пошли из синедриона, радуясь, что за имя Господа Иисуса удостоились принять бесчестие.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35796, 'rst66', 'act', 5, 40, 'Они послушались его; и, призвав Апостолов, били <i>их</i> и, запретив им говорить об имени Иисуса, отпустили их.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35795, 'rst66', 'act', 5, 39, 'а если от Бога, то вы не можете разрушить его; <i>берегитесь</i>, чтобы вам не оказаться и богопротивниками.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35794, 'rst66', 'act', 5, 38, 'И ныне, говорю вам, отстаньте от людей сих и оставьте их; ибо если это предприятие и это дело от человеков, то оно разрушится,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35793, 'rst66', 'act', 5, 37, 'После него во время переписи явился Иуда Галилеянин и увлек за собою довольно народа; но он погиб, и все, которые слушались его, рассыпались.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35792, 'rst66', 'act', 5, 36, 'Ибо незадолго перед сим явился Февда, выдавая себя за кого‐то великого, и к нему пристало около четырехсот человек; но он был убит, и все, которые слушались его, рассеялись и исчезли.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35791, 'rst66', 'act', 5, 35, 'а им сказал: мужи Израильские! подумайте сами с собою о людях сих, что&#769; вам с ними делать.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35790, 'rst66', 'act', 5, 34, 'Встав же в синедрионе, некто фарисей, именем Гамалиил, законоучитель, уважаемый всем народом, приказал вывести Апостолов на короткое время,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35789, 'rst66', 'act', 5, 33, 'Слыша это, они разрывались от гнева и умышляли умертвить их.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35788, 'rst66', 'act', 5, 32, 'Свидетели Ему в сем мы и Дух Святой, Которого Бог дал повинующимся Ему.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35787, 'rst66', 'act', 5, 31, 'Его возвысил Бог десницею Своею в Начальника и Спасителя, дабы дать Израилю покаяние и прощение грехов.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35786, 'rst66', 'act', 5, 30, 'Бог отцов наших воскресил Иисуса, Которого вы умертвили, повесив на древе.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35785, 'rst66', 'act', 5, 29, 'Петр же и Апостолы в ответ сказали: должно повиноваться больше Богу, нежели человекам.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35784, 'rst66', 'act', 5, 28, 'не запретили ли мы вам накрепко учить о имени сем? и вот, вы наполнили Иерусалим учением вашим и хотите навести на нас кровь Того Человека.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35783, 'rst66', 'act', 5, 27, 'Приведя же их, поставили в синедрионе; и спросил их первосвященник, говоря:');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35782, 'rst66', 'act', 5, 26, 'Тогда начальник стражи пошел со служителями и привел их без принуждения, потому что боялись народа, чтобы не побили их камнями.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35781, 'rst66', 'act', 5, 25, 'Пришел же некто и донес им, говоря: вот, мужи, которых вы заключили в темницу, стоят в храме и учат народ.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35780, 'rst66', 'act', 5, 24, 'Когда услышали эти слова первосвященник, начальник стражи и <i>прочие</i> первосвященники, недоумевали, что бы это значило.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35779, 'rst66', 'act', 5, 23, 'говоря: темницу мы нашли запертою со всею предосторожностью и стражей стоящими перед дверями; но, отворив, не нашли в ней никого.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35778, 'rst66', 'act', 5, 22, 'Но служители, придя, не нашли их в темнице и, возвратившись, донесли,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35777, 'rst66', 'act', 5, 21, 'Они, выслушав, вошли утром в храм и учили. Между тем первосвященник и которые с ним, придя, созвали синедрион и всех старейшин из сынов Израилевых и послали в темницу привести <i>Апостолов</i>.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35776, 'rst66', 'act', 5, 20, 'идите и, став в храме, говорите народу все сии слова жизни.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35775, 'rst66', 'act', 5, 19, 'Но Ангел Господень ночью отворил двери темницы и, выведя их, сказал:');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35774, 'rst66', 'act', 5, 18, 'и наложили руки свои на Апостолов, и заключили их в народную темницу.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35773, 'rst66', 'act', 5, 17, 'Первосвященник же и с ним все, принадлежавшие к ереси саддукейской, исполнились зависти,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35772, 'rst66', 'act', 5, 16, 'Сходились также в Иерусалим многие из окрестных городов, неся больных и нечистыми духами одержимых, которые и исцелялись все.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35771, 'rst66', 'act', 5, 15, 'так что выносили больных на улицы и полагали на постелях и кроватях, дабы хотя тень проходящего Петра осенила кого из них.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35770, 'rst66', 'act', 5, 14, 'Верующих же более и более присоединялось к Господу, множество мужчин и женщин,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35769, 'rst66', 'act', 5, 13, 'Из посторонних же никто не смел пристать к ним, а народ прославлял их.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35768, 'rst66', 'act', 5, 12, 'Руками же Апостолов совершались в народе многие знамения и чудеса; и все единодушно пребывали в притворе Соломоновом.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35767, 'rst66', 'act', 5, 11, 'И великий страх объял всю церковь и всех слышавших это.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35766, 'rst66', 'act', 5, 10, 'Вдруг она упала у ног его и испустила дух. И юноши, войдя, нашли ее мертвою и, вынеся, похоронили подле мужа ее.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35765, 'rst66', 'act', 5, 9, 'Но Петр сказал ей: что это согласились вы искусить Духа Господня? вот, входят в двери погребавшие мужа твоего; и тебя вынесут.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35764, 'rst66', 'act', 5, 8, 'Петр же спросил ее: скажи мне, за столько ли продали вы землю? Она сказала: да, за столько.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35763, 'rst66', 'act', 5, 7, 'Часа через три после сего пришла и жена его, не зная о случившемся.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35762, 'rst66', 'act', 5, 6, 'И, встав, юноши приготовили его к погребению и, вынеся, похоронили.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35761, 'rst66', 'act', 5, 5, 'Услышав сии слова, Анания пал бездыханен; и великий страх объял всех, слышавших это.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35760, 'rst66', 'act', 5, 4, 'Чем ты владел, не твое ли было, и приобретенное продажею не в твоей ли власти находилось? Для чего ты положил это в сердце твоем? Ты солгал не человекам, а Богу.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35759, 'rst66', 'act', 5, 3, 'Но Петр сказал: Анания! Для чего <i>ты допустил</i> сатане вложить в сердце твое <i>мысль</i> солгать Духу Святому и утаить из цены земли?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35758, 'rst66', 'act', 5, 2, 'утаил из цены, с ведома и жены своей, а некоторую часть принес и положил к ногам Апостолов.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35757, 'rst66', 'act', 5, 1, 'Некоторый же муж, именем Анания, с женою своею Сапфирою, продав имение,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35756, 'rst66', 'act', 4, 37, 'у которого была своя земля, продав ее, принес деньги и положил к ногам Апостолов.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35755, 'rst66', 'act', 4, 36, 'Так Иосия, прозванный от Апостолов Варнавою (что значит: «сын утешения»), левит, родом Кипрянин,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35754, 'rst66', 'act', 4, 35, 'и полагали к ногам Апостолов; и каждому давалось, в чем кто имел нужду.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35753, 'rst66', 'act', 4, 34, 'Не было между ними никого нуждающегося; ибо все, которые владели землями или домами, продавая их, приносили цену проданного');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35752, 'rst66', 'act', 4, 33, 'Апостолы же с великою силою свидетельствовали о воскресении Господа Иисуса Христа; и великая благодать была на всех их.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35751, 'rst66', 'act', 4, 32, 'У множества же уверовавших было одно сердце и одна душа; и никто ничего из имения своего не называл своим, но всё у них было общее.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35750, 'rst66', 'act', 4, 31, 'И, по молитве их, поколебалось место, где они были собраны, и исполнились все Духа Святого, и говорили слово Божие с дерзновением.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35749, 'rst66', 'act', 4, 30, 'тогда как Ты простираешь руку Твою на исцеления и на соделание знамений и чудес именем Святого Сына Твоего Иисуса.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35748, 'rst66', 'act', 4, 29, 'И ныне, Господи, воззри на угрозы их, и дай рабам Твоим со всею смелостью говорить слово Твое,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35747, 'rst66', 'act', 4, 28, 'чтобы сделать то, чему быть предопределила рука Твоя и совет Твой.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35746, 'rst66', 'act', 4, 27, 'Ибо поистине собрались в городе сем на Святого Сына Твоего Иисуса, помазанного Тобою, Ирод и Понтий Пилат с язычниками и народом Израильским,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35745, 'rst66', 'act', 4, 26, 'Восстали цари земные, и князи собрались вместе на Господа и на Христа Его».');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35744, 'rst66', 'act', 4, 25, 'Ты устами отца нашего Давида, раба Твоего, сказал Духом Святым: «что мятутся язычники, и народы замышляют тщетное?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35743, 'rst66', 'act', 4, 24, 'Они же, выслушав, единодушно возвысили голос к Богу и сказали: Владыко Боже, сотворивший небо и землю и море и всё, что в них!');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35742, 'rst66', 'act', 4, 23, 'Быв отпущены, они пришли к своим и пересказали, что говорили им первосвященники и старейшины.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35741, 'rst66', 'act', 4, 22, 'Ибо лет более сорока было тому человеку, над которым сделалось сие чудо исцеления.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35740, 'rst66', 'act', 4, 21, 'Они же, пригрозив, отпустили их, не находя возможности наказать их, по причине народа; потому что все прославляли Бога за происшедшее.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35739, 'rst66', 'act', 4, 20, 'Мы не можем не говорить того, что видели и слышали.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35738, 'rst66', 'act', 4, 19, 'Но Петр и Иоанн сказали им в ответ: суди&#769;те, справедливо ли пред Богом слушать вас более, нежели Бога?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35737, 'rst66', 'act', 4, 18, 'И, призвав их, приказали им отнюдь не говорить и не учить о имени Иисуса.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35736, 'rst66', 'act', 4, 17, 'но, чтобы более не разгласилось это в народе, с угрозою запретим им, чтобы не говорили об имени сем никому из людей.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35735, 'rst66', 'act', 4, 16, 'говоря: что нам делать с этими людьми? Ибо всем, живущим в Иерусалиме, известно, что ими сделано явное чудо, и мы не можем отвергнуть <i>сего</i>;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35734, 'rst66', 'act', 4, 15, 'И, приказав им выйти вон из синедриона, рассуждали между собою,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35733, 'rst66', 'act', 4, 14, 'видя же исцеленного человека, стоящего с ними, ничего не могли сказать вопреки.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35732, 'rst66', 'act', 4, 13, 'Видя смелость Петра и Иоанна и приметив, что они люди некнижные и простые, они удивлялись; между тем узнавали их, что они были с Иисусом;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35731, 'rst66', 'act', 4, 12, 'ибо нет другого имени под небом, данного человекам, которым надлежало бы нам спастись.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35730, 'rst66', 'act', 4, 11, 'Он есть камень, пренебреженный вами, зиждущими, но сделавшийся главою угла, и нет ни в ком ином спасения;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35729, 'rst66', 'act', 4, 10, 'то да будет известно всем вам и всему народу Израильскому, что именем Иисуса Христа Назорея, Которого вы распяли, Которого Бог воскресил из мертвых, Им поставлен он перед вами здрав.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35728, 'rst66', 'act', 4, 9, 'Если от нас сегодня требуют ответа в благодеянии человеку немощному, как он исцелен,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35727, 'rst66', 'act', 4, 8, 'Тогда Петр, исполнившись Духа Святого, сказал им: начальники народа и старейшины Израильские!');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35726, 'rst66', 'act', 4, 7, 'и, поставив их посреди, спрашивали: какою силою или каким именем вы сделали это?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35725, 'rst66', 'act', 4, 6, 'и Анна первосвященник, и Каиафа, и Иоанн, и Александр, и прочие из рода первосвященнического;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35724, 'rst66', 'act', 4, 5, 'На другой день собрались в Иерусалим начальники их, и старейшины, и книжники,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35723, 'rst66', 'act', 4, 4, 'Многие же из слушавших слово уверовали; и было число таковых людей около пяти тысяч.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35722, 'rst66', 'act', 4, 3, 'и наложили на них руки и отдали <i>их</i> под стражу до утра; ибо уже был вечер.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35721, 'rst66', 'act', 4, 2, 'досадуя на то, что они учат народ и проповедуют в Иисусе воскресение из мертвых;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35720, 'rst66', 'act', 4, 1, 'Когда они говорили к народу, к ним приступили священники и начальники стражи при храме и саддукеи,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35719, 'rst66', 'act', 3, 26, 'Бог, воскресив Сына Своего Иисуса, к вам первым послал Его благословить вас, отвращая каждого от злых дел ваших.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35718, 'rst66', 'act', 3, 25, 'Вы сыны пророков и завета, который завещевал Бог отцам вашим, говоря Аврааму: «и в семени твоем благословятся все племена земные».');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35717, 'rst66', 'act', 3, 24, 'И все пророки, от Самуила и после него, сколько их ни говорили, также предвозвестили дни сии.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35716, 'rst66', 'act', 3, 23, 'и будет, что всякая душа, которая не послушает Пророка того, истребится из народа».');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35715, 'rst66', 'act', 3, 22, 'Моисей сказал отцам: «Господь Бог ваш воздвигнет вам из братьев ваших Пророка, как меня; слушайтесь Его во всем, что Он ни будет говорить вам;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35714, 'rst66', 'act', 3, 21, 'Которого небо должно было принять до времен совершения всего, что говорил Бог устами всех святых Своих пророков от века.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35713, 'rst66', 'act', 3, 20, 'да придут времена отрады от лица Господа, и да пошлет Он предназначенного вам Иисуса Христа,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35712, 'rst66', 'act', 3, 19, 'Итак, покайтесь и обратитесь, чтобы загладились грехи ваши,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35711, 'rst66', 'act', 3, 18, 'Бог же как предвозвестил устами всех Своих пророков пострадать Христу, так и исполнил.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35710, 'rst66', 'act', 3, 17, 'Впрочем, я знаю, братия, что вы, как и начальники ваши, сделали это по неведению;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35709, 'rst66', 'act', 3, 16, 'И ради веры во имя Его, имя Его укрепило сего, которого вы видите и знаете, и вера, которая от Него, даровала ему исцеление сие пред всеми вами.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35708, 'rst66', 'act', 3, 15, 'а Начальника жизни убили. Сего Бог воскресил из мертвых, чему мы свидетели.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35707, 'rst66', 'act', 3, 14, 'Но вы от Святого и Праведного отреклись и просили даровать вам человека убийцу,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35706, 'rst66', 'act', 3, 13, 'Бог Авраама и Исаака и Иакова, Бог отцов наших, прославил Сына Своего Иисуса, Которого вы предали и от Которого отреклись пред лицом Пилата, когда он полагал освободить Его.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35705, 'rst66', 'act', 3, 12, 'Увидев это, Петр сказал народу: мужи Израильские! что дивитесь сему, или что смотрите на нас, как будто бы мы своею силою или благочестием сделали то, что он ходит?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35704, 'rst66', 'act', 3, 11, 'И как исцеленный хромой не отходил от Петра и Иоанна, то весь народ в изумлении сбежался к ним в притвор, называемый Соломонов.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35703, 'rst66', 'act', 3, 10, 'и узнали его, что это был тот, который сидел у Красных дверей храма для милостыни; и исполнились ужаса и изумления от случившегося с ним.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35702, 'rst66', 'act', 3, 9, 'И весь народ видел его ходящим и хвалящим Бога;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35701, 'rst66', 'act', 3, 8, 'и, вскочив, стал, и начал ходить, и вошел с ними в храм, ходя и скача и хваля Бога.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35700, 'rst66', 'act', 3, 7, 'И, взяв его за правую руку, поднял; и вдруг укрепились его ступни и колени,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35699, 'rst66', 'act', 3, 6, 'Но Петр сказал: серебра и золота нет у меня; а что имею, то даю тебе: во имя Иисуса Христа Назорея встань и ходи.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35698, 'rst66', 'act', 3, 5, 'И он пристально смотрел на них, надеясь получить от них что‐нибудь.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35697, 'rst66', 'act', 3, 4, 'Петр с Иоанном, всмотревшись в него, сказал: взгляни на нас.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35696, 'rst66', 'act', 3, 3, 'Он, увидев Петра и Иоанна перед входом в храм, просил у них милостыни.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35695, 'rst66', 'act', 3, 2, 'И был человек, хромой от чрева матери его, которого носили и сажали каждый день при дверях храма, называемых Красными, просить милостыни у входящих в храм.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35694, 'rst66', 'act', 3, 1, 'Петр и Иоанн шли вместе в храм в час молитвы девятый.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35693, 'rst66', 'act', 2, 47, 'хваля Бога и находясь в любви у всего народа. Господь же ежедневно прилагал спасаемых к Церкви.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35692, 'rst66', 'act', 2, 46, 'И каждый день единодушно пребывали в храме и, преломляя по домам хлеб, принимали пищу в веселии и простоте сердца,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35691, 'rst66', 'act', 2, 45, 'И продавали имения и всякую собственность, и разделяли всем, смотря по нужде каждого.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35690, 'rst66', 'act', 2, 44, 'Все же верующие были вместе и имели всё общее.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35689, 'rst66', 'act', 2, 43, 'Был же страх на всякой душе; и много чудес и знамений совершилось через Апостолов в Иерусалиме.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35688, 'rst66', 'act', 2, 42, 'И они постоянно пребывали в учении Апостолов, в общении и преломлении хлеба и в молитвах.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35687, 'rst66', 'act', 2, 41, 'Итак охотно принявшие слово его крестились, и присоединилось в тот день душ около трех тысяч.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35686, 'rst66', 'act', 2, 40, 'И другими многими словами он свидетельствовал и увещевал, говоря: спасайтесь от рода сего развращенного.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35685, 'rst66', 'act', 2, 39, 'Ибо вам принадлежит обетование и детям вашим и всем дальним, кого ни призовет Господь Бог наш.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35684, 'rst66', 'act', 2, 38, 'Петр же сказал им: покайтесь, и да крестится каждый из вас во имя Иисуса Христа для прощения грехов; и полу&#769;чите дар Святого Духа.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35683, 'rst66', 'act', 2, 37, 'Услышав это, они умилились сердцем и сказали Петру и прочим Апостолам: что нам делать, мужи братия?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35682, 'rst66', 'act', 2, 36, 'Итак, твердо знай, весь дом Израилев, что Бог соделал Господом и Христом Сего Иисуса, Которого вы распяли.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35681, 'rst66', 'act', 2, 35, 'доколе положу врагов Твоих в подножие ног Твоих».');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35680, 'rst66', 'act', 2, 34, 'Ибо Давид не восшел на небеса; но сам говорит: «сказал Господь Господу моему: седи одесную Меня,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35679, 'rst66', 'act', 2, 33, 'Итак, Он, быв вознесен десницею Божиею и приняв от Отца обетование Святого Духа, излил то, что вы ныне видите и слышите.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35678, 'rst66', 'act', 2, 32, 'Сего Иисуса Бог воскресил, чему все мы свидетели.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35677, 'rst66', 'act', 2, 31, 'он прежде сказал о воскресении Христа, что не оставлена душа Его в аде, и плоть Его не видела тления.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35676, 'rst66', 'act', 2, 30, 'Будучи же пророком и зная, что Бог с клятвою обещал ему от плода чресл его воздвигнуть Христа во плоти и посадить на престоле его,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35675, 'rst66', 'act', 2, 29, 'Мужи братия! да будет позволено с дерзновением сказать вам о праотце Давиде, что он и умер и погребен, и гроб его у нас до сего дня.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35674, 'rst66', 'act', 2, 28, 'Ты дал мне познать путь жизни, Ты исполнишь меня радостью пред лицом Твоим».');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35673, 'rst66', 'act', 2, 27, 'ибо Ты не оставишь души моей в аде и не дашь святому Твоему увидеть тления.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35672, 'rst66', 'act', 2, 26, 'Оттого возрадовалось сердце мое и возвеселился язык мой; даже и плоть моя упокоится в уповании,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35671, 'rst66', 'act', 2, 25, 'Ибо Давид говорит о Нем: «видел я пред собою Господа всегда, ибо Он одесную меня, дабы я не поколебался.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35670, 'rst66', 'act', 2, 24, 'но Бог воскресил Его, расторгнув узы смерти, потому что ей невозможно было удержать Его.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35669, 'rst66', 'act', 2, 23, 'Сего, по определенному совету и предведению Божию преданного, вы взяли и, пригвоздив руками беззаконных, убили;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35668, 'rst66', 'act', 2, 22, 'Мужи Израильские! выслушайте слова сии: Иисуса Назорея, Мужа, засвидетельствованного вам от Бога силами и чудесами и знамениями, которые Бог сотворил через Него среди вас, как и сами знаете,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35667, 'rst66', 'act', 2, 21, 'И будет: всякий, кто призовет имя Господне, спасется».');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35666, 'rst66', 'act', 2, 20, 'Солнце превратится во тьму, и луна в кровь, прежде нежели наступит день Господень, великий и славный.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35665, 'rst66', 'act', 2, 19, 'И покажу чудеса на небе вверху и знамения на земле внизу, кровь и огонь и курение дыма.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35664, 'rst66', 'act', 2, 18, 'И на рабов Моих и на рабынь Моих в те дни излию от Духа Моего, и будут пророчествовать.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35663, 'rst66', 'act', 2, 17, '«и будет в последние дни, говорит Бог, излию от Духа Моего на всякую плоть, и будут пророчествовать сыны ваши и дочери ваши; и юноши ваши будут видеть видения, и старцы ваши сновидениями вразумляемы будут.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35662, 'rst66', 'act', 2, 16, 'но это есть предреченное пророком Иоилем:');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35661, 'rst66', 'act', 2, 15, 'они не пьяны, как вы думаете, ибо теперь третий час дня;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35660, 'rst66', 'act', 2, 14, 'Петр же, став с одиннадцатью, возвысил голос свой и возгласил им: мужи Иудейские и все живущие в Иерусалиме! сие да будет вам известно, и внимайте словам моим:');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35659, 'rst66', 'act', 2, 13, 'А иные, насмехаясь, говорили: они напились сладкого вина.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35658, 'rst66', 'act', 2, 12, 'И изумлялись все и, недоумевая, говорили друг другу: что это значит?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35657, 'rst66', 'act', 2, 11, 'Критяне и Аравитяне, слышим их, нашими языками говорящих о великих <i>делах</i> Божиих?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35656, 'rst66', 'act', 2, 10, 'Фригии и Памфилии, Египта и частей Ливии, прилежащих к Киринее, и пришедшие из Рима, Иудеи и прозелиты,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35655, 'rst66', 'act', 2, 9, 'Парфяне, и Мидяне, и Еламиты, и жители Месопотамии, Иудеи и Каппадокии, Понта и Асии,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35654, 'rst66', 'act', 2, 8, 'Как же мы слышим каждый собственное наречие, в котором родились.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35653, 'rst66', 'act', 2, 7, 'И все изумлялись и дивились, говоря между собою: сии говорящие не все ли Галилеяне?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35652, 'rst66', 'act', 2, 6, 'Когда сделался этот шум, собрался народ и пришел в смятение, ибо каждый слышал их говорящих его наречием.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35651, 'rst66', 'act', 2, 5, 'В Иерусалиме же находились Иудеи, люди набожные, из всякого народа под небесами.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35650, 'rst66', 'act', 2, 4, 'И исполнились все Духа Святого, и начали говорить на иных языках, как Дух давал им провещевать.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35649, 'rst66', 'act', 2, 3, 'И явились им разделяющиеся языки, как бы огненные, и почили по одному на каждом из них.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35648, 'rst66', 'act', 2, 2, 'И внезапно сделался шум с неба, как бы от несущегося сильного ветра, и наполнил весь дом, где они находились.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35647, 'rst66', 'act', 2, 1, 'При наступлении дня Пятидесятницы все они были единодушно вместе.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35646, 'rst66', 'act', 1, 26, 'И бросили о них жребий, и выпал жребий Матфию, и он сопричислен к одиннадцати Апостолам.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35645, 'rst66', 'act', 1, 25, 'принять жребий сего служения и Апостольства, от которого отпал Иуда, чтобы идти в свое место.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35644, 'rst66', 'act', 1, 24, 'и помолились и сказали: Ты, Господи, Сердцеведец всех, покажи из сих двоих одного, которого Ты избрал');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35643, 'rst66', 'act', 1, 23, 'И поставили двоих: Иосифа, называемого Варсавою, который прозван Иустом, и Матфия;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35642, 'rst66', 'act', 1, 22, 'начиная от крещения Иоаннова до того дня, в который Он вознесся от нас, был вместе с нами свидетелем воскресения Его.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35641, 'rst66', 'act', 1, 21, 'Итак надобно, чтобы один из тех, которые находились с нами во всё время, когда пребывал и обращался с нами Господь Иисус,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35640, 'rst66', 'act', 1, 20, 'В книге же Псалмов написано: «да будет двор его пуст, и да не будет живущего в нем»; и: «достоинство его да приимет другой».');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35639, 'rst66', 'act', 1, 19, 'и это сделалось известно всем жителям Иерусалима, так что земля та на отечественном их наречии названа Акелдама&#769;, то есть «земля крови».');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35638, 'rst66', 'act', 1, 18, 'но приобрел землю неправедною мздою, и, когда низринулся, расселось чрево его, и выпали все внутренности его;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35637, 'rst66', 'act', 1, 17, 'он был сопричислен к нам и получил жребий служения сего;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35636, 'rst66', 'act', 1, 16, '(было же собрание человек около ста двадцати): мужи братия! Надлежало исполниться тому, что в Писании предрек Дух Святой устами Давида об Иуде, бывшем вожде тех, которые взяли Иисуса;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35635, 'rst66', 'act', 1, 15, 'И в те дни Петр, став посреди учеников, сказал');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35634, 'rst66', 'act', 1, 14, 'Все они единодушно пребывали в молитве и молении с <i>некоторыми</i> женами и Мариею, Матерью Иисуса, и с братьями Его.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35633, 'rst66', 'act', 1, 13, 'И, придя, взошли в горницу, где и пребывали, Петр и Иаков, Иоанн и Андрей, Филипп и Фома, Варфоломей и Матфей, Иаков Алфеев и Симон Зилот, и Иуда, <i>брат</i> Иакова.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35632, 'rst66', 'act', 1, 12, 'Тогда они возвратились в Иерусалим с горы, называемой Елеон, которая находится близ Иерусалима, в расстоянии субботнего пути.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35631, 'rst66', 'act', 1, 11, 'и сказали: мужи Галилейские! что вы стоите и смотрите на небо? Сей Иисус, вознесшийся от вас на небо, придет таким же образом, как вы видели Его восходящим на небо.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35630, 'rst66', 'act', 1, 10, 'И когда они смотрели на небо, во время восхождения Его, вдруг предстали им два мужа в белой одежде');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35629, 'rst66', 'act', 1, 9, 'Сказав сие, Он поднялся в глазах их, и облако взяло Его из вида их.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35628, 'rst66', 'act', 1, 8, 'но вы примете силу, когда сойдет на вас Дух Святой; и будете Мне свидетелями в Иерусалиме и во всей Иудее и Самарии и даже до края земли.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35627, 'rst66', 'act', 1, 7, 'Он же сказал им: не ваше дело знать времена или сроки, которые Отец положил в Своей власти,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35626, 'rst66', 'act', 1, 6, 'Посему они, сойдясь, спрашивали Его, говоря: не в сие ли время, Господи, восстановляешь Ты царство Израилю?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35625, 'rst66', 'act', 1, 5, 'ибо Иоанн крестил водою, а вы, через несколько дней после сего, будете крещены Духом Святым.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35624, 'rst66', 'act', 1, 4, 'И, собрав их, Он повелел им: не отлучайтесь из Иерусалима, но ждите обещанного от Отца, о чем вы слышали от Меня,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35623, 'rst66', 'act', 1, 3, 'которым и явил Себя живым, по страдании Своем, со многими верными доказательствами, в продолжение сорока дней являясь им и говоря о Царствии Божием.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35622, 'rst66', 'act', 1, 2, 'до того дня, в который Он вознесся, дав Святым Духом повеления Апостолам, которых Он избрал,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35621, 'rst66', 'act', 1, 1, 'Первую книгу написал я <i>к тебе</i>, Фео&#769;фил, о всем, что Иисус делал и чему учил от начала');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35620, 'rst66', 'jhn', 21, 25, 'Многое и другое сотворил Иисус; но если бы писать о том подробно, то, думаю, и самому миру не вместить бы написанных книг. Аминь.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35619, 'rst66', 'jhn', 21, 24, 'Сей ученик и свидетельствует о сем, и написал сие; и знаем, что истинно свидетельство его.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35618, 'rst66', 'jhn', 21, 23, 'И пронеслось это слово между братиями, что ученик тот не умрет. Но Иисус не сказал ему, что не умрет, но: «если Я хочу, чтобы он пребыл, пока приду, что тебе <i>до того?»</i>');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35617, 'rst66', 'jhn', 21, 22, 'Иисус говорит ему: если Я хочу, чтобы он пребыл, пока приду, что&#769; тебе <i>до того?</i> ты иди за Мною.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35616, 'rst66', 'jhn', 21, 21, 'Его увидев, Петр говорит Иисусу: Господи! а он что?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35615, 'rst66', 'jhn', 21, 20, 'Петр же, обратившись, видит идущего за ним ученика, которого любил Иисус и который на вечери, приклонившись к груди Его, сказал: «Господи! кто предаст Тебя?»');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35614, 'rst66', 'jhn', 21, 19, 'Сказал же это, давая разуметь, какою смертью <i>Петр</i> прославит Бога. И, сказав сие, говорит ему: иди за Мною.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35613, 'rst66', 'jhn', 21, 18, 'Истинно, истинно говорю тебе: когда ты был молод, то препоясывался сам и ходил, куда хотел; а когда состаришься, то прострешь руки твои, и другой препояшет тебя и поведет, куда не хочешь.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35612, 'rst66', 'jhn', 21, 17, 'Говорит ему в третий раз: Симон Ионин! любишь ли ты Меня? Петр опечалился, что в третий раз спросил его: «любишь ли Меня?», и сказал Ему: Господи! Ты все знаешь; Ты знаешь, что я люблю Тебя. Иисус говорит ему: паси овец Моих.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35611, 'rst66', 'jhn', 21, 16, 'Еще говорит ему в другой раз: Симон Ионин! любишь ли ты Меня? <i>Петр</i> говорит Ему: так, Господи! Ты знаешь, что я люблю Тебя. <i>Иисус</i> говорит ему: паси овец Моих.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35610, 'rst66', 'jhn', 21, 15, 'Когда же они обедали, Иисус говорит Симону Петру: Симон Ионин! любишь ли ты Меня больше, нежели они? <i>Петр</i> говорит Ему: так, Господи! Ты знаешь, что я люблю Тебя. <i>Иисус</i> говорит ему: паси агнцев Моих.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35609, 'rst66', 'jhn', 21, 14, 'Это уже в третий раз явился Иисус ученикам Своим по воскресении Своем из мертвых.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35608, 'rst66', 'jhn', 21, 13, 'Иисус приходит, берет хлеб и дает им, также и рыбу.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35607, 'rst66', 'jhn', 21, 12, 'Иисус говорит им: придите, обедайте. Из учеников же никто не смел спросить Его: «кто Ты?», зная, что это Господь.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35606, 'rst66', 'jhn', 21, 11, 'Симон Петр пошел и вытащил на землю сеть, наполненную большими рыбами, <i>которых было</i> сто пятьдесят три; и при таком множестве не прорвалась сеть.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35605, 'rst66', 'jhn', 21, 10, 'Иисус говорит им: принесите рыбы, которую вы теперь поймали.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35604, 'rst66', 'jhn', 21, 9, 'Когда же вышли на землю, видят разложенный огонь и на нем лежащую рыбу и хлеб.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35603, 'rst66', 'jhn', 21, 8, 'А другие ученики приплыли в лодке, &mdash; ибо недалеко были от земли, локтей около двухсот, &mdash; таща сеть с рыбою.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35602, 'rst66', 'jhn', 21, 7, 'Тогда ученик, которого любил Иисус, говорит Петру: это Господь. Симон же Петр, услышав, что это Господь, опоясался одеждою, &mdash; ибо он был наг, &mdash; и бросился в море.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35601, 'rst66', 'jhn', 21, 6, 'Он же сказал им: закиньте сеть по правую сторону лодки, и поймаете. Они закинули, и уже не могли вытащить <i>сети</i> от множества рыбы.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35600, 'rst66', 'jhn', 21, 5, 'Иисус говорит им: дети! есть ли у вас какая пища? Они отвечали Ему: нет.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35599, 'rst66', 'jhn', 21, 4, 'А когда уже настало утро, Иисус стоял на берегу; но ученики не узнали, что это Иисус.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35598, 'rst66', 'jhn', 21, 3, 'Симон Петр говорит им: иду ловить рыбу. Говорят ему: идем и мы с тобою. Пошли и тотчас вошли в лодку, и не поймали в ту ночь ничего.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35597, 'rst66', 'jhn', 21, 2, 'были вместе Симон Петр, и Фома, называемый Близнец, и Нафанаил из Каны Галилейской, и сыновья Зеведеевы, и двое других из учеников Его.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35596, 'rst66', 'jhn', 21, 1, 'После того опять явился Иисус ученикам Своим при море Тивериадском. Явился же так:');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35595, 'rst66', 'jhn', 20, 31, 'Сие же написано, дабы вы уверовали, что Иисус есть Христос, Сын Божий, и, веруя, имели жизнь во имя Его.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35594, 'rst66', 'jhn', 20, 30, 'Много сотворил Иисус пред учениками Своими и других чудес, о которых не писано в книге сей.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35593, 'rst66', 'jhn', 20, 29, 'Иисус говорит ему: ты поверил, потому что увидел Меня; блаженны невидевшие и уверовавшие.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35592, 'rst66', 'jhn', 20, 28, 'Фома сказал Ему в ответ: Господь мой и Бог мой!');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35591, 'rst66', 'jhn', 20, 27, 'Потом говорит Фоме: подай перст твой сюда и посмотри руки Мои; подай руку твою и вложи в ребра Мои; и не будь неверующим, но верующим.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35590, 'rst66', 'jhn', 20, 26, 'После восьми дней опять были в доме ученики Его, и Фома с ними. Пришел Иисус, когда двери были заперты, стал посреди них и сказал: мир вам!');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35589, 'rst66', 'jhn', 20, 25, 'Другие ученики сказали ему: мы видели Господа. Но он сказал им: если не увижу на руках Его ран от гвоздей, и не вложу перста моего в раны от гвоздей, и не вложу руки моей в ребра Его, не поверю.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35588, 'rst66', 'jhn', 20, 24, 'Фома же, один из Двенадцати, называемый Близнец, не был тут с ними, когда приходил Иисус.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35587, 'rst66', 'jhn', 20, 23, 'Кому простите грехи, тому простятся; на ком оставите, на том останутся.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35586, 'rst66', 'jhn', 20, 22, 'Сказав это, дунул, и говорит им: примите Духа Святого.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35585, 'rst66', 'jhn', 20, 21, 'Иисус же сказал им вторично: мир вам! как послал Меня Отец, <i>так</i> и Я посылаю вас.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35584, 'rst66', 'jhn', 20, 20, 'Сказав это, Он показал им руки и ноги и ребра Свои. Ученики обрадовались, увидев Господа.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35583, 'rst66', 'jhn', 20, 19, 'В тот же первый день недели вечером, когда двери <i>дома</i>, где собирались ученики Его, были заперты из опасения от Иудеев, пришел Иисус, и стал посреди, и говорит им: мир вам!');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35582, 'rst66', 'jhn', 20, 18, 'Мария Магдалина идет и возвещает ученикам, что видела Господа и <i>что</i> Он это сказал ей.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35581, 'rst66', 'jhn', 20, 17, 'Иисус говорит ей: не прикасайся ко Мне, ибо Я еще не восшел к Отцу Моему; а иди к братьям Моим и скажи им: «восхожу к Отцу Моему и Отцу вашему, и к Богу Моему и Богу вашему».');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35580, 'rst66', 'jhn', 20, 16, 'Иисус говорит ей: Мария! Она, обратившись, говорит Ему: Раввуни&#769;! &mdash; что значит: «Учитель!»');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35579, 'rst66', 'jhn', 20, 15, 'Иисус говорит ей: жена! что ты плачешь? кого ищешь? Она, думая, что это садовник, говорит Ему: господин! если ты вынес Его, скажи мне, где ты положил Его, и я возьму Его.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35578, 'rst66', 'jhn', 20, 14, 'Сказав сие, обратилась назад и увидела Иисуса стоящего; но не узнала, что это Иисус.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35577, 'rst66', 'jhn', 20, 13, 'И они говорят ей: жена! что ты плачешь? Говорит им: унесли Господа моего, и не знаю, где положили Его.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35576, 'rst66', 'jhn', 20, 12, 'и видит двух Ангелов, в белом одеянии сидящих, одного у главы и другого у ног, где лежало тело Иисуса.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35575, 'rst66', 'jhn', 20, 11, 'А Мария стояла у гроба и плакала. И, когда плакала, наклонилась во гроб,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35574, 'rst66', 'jhn', 20, 10, 'Итак, ученики опять возвратились к себе.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35573, 'rst66', 'jhn', 20, 9, 'Ибо они еще не знали из Писания, что Ему надлежало воскреснуть из мертвых.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35572, 'rst66', 'jhn', 20, 8, 'Тогда вошел и другой ученик, прежде пришедший ко гробу, и увидел, и уверовал.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35571, 'rst66', 'jhn', 20, 7, 'и плат, который был на главе Его, не с пеленами лежащий, но особо свитый на другом месте.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35570, 'rst66', 'jhn', 20, 6, 'Вслед за ним приходит Симон Петр, и входит во гроб, и видит одни пелены лежащие,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35569, 'rst66', 'jhn', 20, 5, 'И, наклонившись, увидел лежащие пелены; но не вошел <i>во гроб</i>.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35568, 'rst66', 'jhn', 20, 4, 'Они побежали оба вместе; но другой ученик бежал скорее Петра и пришел ко гробу первый.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35567, 'rst66', 'jhn', 20, 3, 'Тотчас вышел Петр и другой ученик, и пошли ко гробу.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35566, 'rst66', 'jhn', 20, 2, 'Итак, бежит и приходит к Симону Петру и к другому ученику, которого любил Иисус, и говорит им: унесли Господа из гроба, и не знаем, где положили Его.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35565, 'rst66', 'jhn', 20, 1, 'В первый же <i>день</i> недели Мария Магдалина приходит ко гробу рано, когда было еще темно, и видит, что камень отвален от гроба.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35564, 'rst66', 'jhn', 19, 42, 'Там положили Иисуса ради пятницы Иудейской, потому что гроб был близко.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35563, 'rst66', 'jhn', 19, 41, 'На том месте, где Он распят, был сад, и в саду гроб новый, в котором еще никто не был положен.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35562, 'rst66', 'jhn', 19, 40, 'Итак, они взяли тело Иисуса и обвили его пеленами с благовониями, как обыкновенно погребают Иудеи.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35561, 'rst66', 'jhn', 19, 39, 'Пришел также и Никодим, &mdash; приходивший прежде к Иисусу ночью, &mdash; и принес состав из смирны и алоя, литр около ста.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35560, 'rst66', 'jhn', 19, 38, 'После сего Иосиф из Аримафеи &mdash; ученик Иисуса, но тайный из страха от Иудеев, &mdash; просил Пилата, чтобы снять тело Иисуса; и Пилат позволил. Он пошел и снял тело Иисуса.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35559, 'rst66', 'jhn', 19, 37, 'Также и в другом <i>месте</i> Писание говорит: «воззрят на Того, Которого пронзили».');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35558, 'rst66', 'jhn', 19, 36, 'Ибо сие произошло, да сбудется Писание: «кость Его да не сокрушится».');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35557, 'rst66', 'jhn', 19, 35, 'И видевший засвидетельствовал, и истинно свидетельство его; он знает, что говорит истину, дабы вы поверили.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35556, 'rst66', 'jhn', 19, 34, 'но один из воинов копьем пронзил Ему ребра, и тотчас истекла кровь и вода.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35555, 'rst66', 'jhn', 19, 33, 'Но, придя к Иисусу, как увидели Его уже умершим, не перебили у Него голеней,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35554, 'rst66', 'jhn', 19, 32, 'Итак, пришли воины, и у первого перебили голени, и у другого, распятого с Ним.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35553, 'rst66', 'jhn', 19, 31, 'Но так как <i>тогда</i> была пятница, то Иудеи, дабы не оставить тел на кресте в субботу, &mdash; ибо та суббота была день великий, &mdash; просили Пилата, чтобы перебить у них голени и снять их.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35552, 'rst66', 'jhn', 19, 30, 'Когда же Иисус вкусил уксуса, сказал: совершилось! И, преклонив главу, предал дух.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35551, 'rst66', 'jhn', 19, 29, 'Тут стоял сосуд, полный уксуса. <i>Воины</i>, напоив уксусом губку и наложив на иссоп, поднесли к устам Его.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35550, 'rst66', 'jhn', 19, 28, 'После того Иисус, зная, что уже все совершилось, да сбудется Писание, говорит: жажду.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35549, 'rst66', 'jhn', 19, 27, 'Потом говорит ученику: се, Матерь твоя! И с этого времени ученик сей взял Ее к себе.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35548, 'rst66', 'jhn', 19, 26, 'Иисус, увидев Матерь и ученика, тут стоящего, которого любил, говорит Матери Своей: Же&#769;но! се, сын Твой.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35547, 'rst66', 'jhn', 19, 25, 'При кресте Иисуса стояли Матерь Его и сестра Матери Его, Мария Клеопова, и Мария Магдалина.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35546, 'rst66', 'jhn', 19, 24, 'Итак сказали друг другу: не станем раздирать его, а бросим о нем жребий, чей будет, &mdash; да сбудется реченное в Писании: «разделили ризы Мои между собою и об одежде Моей бросали жребий». Так поступили воины.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35545, 'rst66', 'jhn', 19, 23, 'Воины же, когда распяли Иисуса, взяли одежды Его и разделили на четыре части, каждому воину по части, и хитон; хитон же был не сшитый, а весь тканый сверху.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35544, 'rst66', 'jhn', 19, 22, 'Пилат отвечал: что я написал, то написал.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35543, 'rst66', 'jhn', 19, 21, 'Первосвященники же Иудейские сказали Пилату: не пиши: «Царь Иудейский», но что Он говорил: «Я &mdash; Царь Иудейский».');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35542, 'rst66', 'jhn', 19, 20, 'Эту надпись читали многие из Иудеев, потому что место, где был распят Иисус, было недалеко от города, и написано было по‐еврейски, по‐гречески, по‐римски.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35541, 'rst66', 'jhn', 19, 19, 'Пилат же написал и надпись и поставил на кресте. Написано было: «Иисус Назорей, Царь Иудейский».');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35540, 'rst66', 'jhn', 19, 18, 'там распяли Его и с Ним двух других, по ту и по другую сторону, а посреди Иисуса.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35539, 'rst66', 'jhn', 19, 17, 'И, неся крест Свой, Он вышел на место, называемое Лобное, по‐еврейски Голгофа;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35538, 'rst66', 'jhn', 19, 16, 'Тогда наконец он предал Его им на распятие. И взяли Иисуса и повели.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35537, 'rst66', 'jhn', 19, 15, 'Но они закричали: возьми, возьми, распни Его! Пилат говорит им: Царя ли вашего распну? Первосвященники отвечали: нет у нас царя, кроме кесаря.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35536, 'rst66', 'jhn', 19, 14, 'Тогда была пятница перед Пасхою, и час шестой. И сказал <i>Пилат</i> Иудеям: се, Царь ваш!');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35535, 'rst66', 'jhn', 19, 13, 'Пилат, услышав это слово, вывел вон Иисуса и сел на судилище, на месте, называемом Лифо&#769;стротон, а по‐еврейски Гаввафа.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35534, 'rst66', 'jhn', 19, 12, 'С этого <i>времени</i> Пилат искал отпустить Его. Иудеи же кричали: если отпустишь Его, ты не друг кесарю; всякий, делающий себя царем, &mdash; противник кесарю.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35533, 'rst66', 'jhn', 19, 11, 'Иисус отвечал: ты не имел бы надо Мною никакой власти, если бы не было дано тебе свыше; посему более греха на том, кто предал Меня тебе.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35532, 'rst66', 'jhn', 19, 10, 'Пилат говорит Ему: мне ли не отвечаешь? не знаешь ли, что я имею власть распять Тебя и власть имею отпустить Тебя?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35531, 'rst66', 'jhn', 19, 9, 'И опять вошел в преторию и сказал Иисусу: откуда Ты? Но Иисус не дал ему ответа.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35530, 'rst66', 'jhn', 19, 8, 'Пилат, услышав это слово, больше убоялся.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35529, 'rst66', 'jhn', 19, 7, 'Иудеи отвечали ему: мы имеем закон, и по закону нашему Он должен умереть, потому что сделал Себя Сыном Божиим.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35528, 'rst66', 'jhn', 19, 6, 'Когда же увидели Его первосвященники и служители, то закричали: распни, распни Его! Пилат говорит им: возьмите Его вы и распните, ибо я не нахожу в Нем вины.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35527, 'rst66', 'jhn', 19, 5, 'Тогда вышел Иисус в терновом венце и в багрянице. И сказал им <i>Пилат:</i> се, Человек!');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35526, 'rst66', 'jhn', 19, 4, 'Пилат опять вышел и сказал им: вот, я вывожу Его к вам, чтобы вы знали, что я не нахожу в Нем никакой вины.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35525, 'rst66', 'jhn', 19, 3, 'и говорили: радуйся, Царь Иудейский! И били Его по ланитам.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35524, 'rst66', 'jhn', 19, 2, 'И воины, сплетши венец из терна, возложили Ему на голову, и одели Его в багряницу,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35523, 'rst66', 'jhn', 19, 1, 'Тогда Пилат взял Иисуса и <i>велел</i> бить Его.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35522, 'rst66', 'jhn', 18, 40, 'Тогда опять закричали все, говоря: не Его, но Варавву. Варавва же был разбойник.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35521, 'rst66', 'jhn', 18, 39, 'Есть же у вас обычай, чтобы я одного отпускал вам на Пасху; хотите ли, отпущу вам Царя Иудейского?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35520, 'rst66', 'jhn', 18, 38, 'Пилат сказал Ему: что есть истина? И, сказав это, опять вышел к Иудеям и сказал им: я никакой вины не нахожу в Нем.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35519, 'rst66', 'jhn', 18, 37, 'Пилат сказал Ему: итак, Ты Царь? Иисус отвечал: ты говоришь, что Я Царь. Я на то родился и на то пришел в мир, чтобы свидетельствовать об истине; всякий, кто от истины, слушает гласа Моего.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35518, 'rst66', 'jhn', 18, 36, 'Иисус отвечал: Царство Мое не от мира сего; если бы от мира сего было Царство Мое, то служители Мои подвизались бы за Меня, чтобы Я не был предан Иудеям; но ныне Царство Мое не отсюда.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35517, 'rst66', 'jhn', 18, 35, 'Пилат отвечал: разве я Иудей? Твой народ и первосвященники предали Тебя мне; что&#769; Ты сделал?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35516, 'rst66', 'jhn', 18, 34, 'Иисус отвечал ему: от себя ли ты говоришь это или другие сказали тебе о Мне?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35515, 'rst66', 'jhn', 18, 33, 'Тогда Пилат опять вошел в преторию, и призвал Иисуса, и сказал Ему: Ты Царь Иудейский?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35514, 'rst66', 'jhn', 18, 32, 'да сбудется слово Иисусово, которое сказал Он, давая разуметь, какою смертью Он умрет.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35513, 'rst66', 'jhn', 18, 31, 'Пилат сказал им: возьмите Его вы и по закону вашему судите Его. Иудеи сказали ему: нам не позволено предавать смерти никого, &mdash;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35512, 'rst66', 'jhn', 18, 30, 'Они сказали ему в ответ: если бы Он не был злодей, мы не предали бы Его тебе.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35511, 'rst66', 'jhn', 18, 29, 'Пилат вышел к ним и сказал: в чем вы обвиняете Человека Сего?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35510, 'rst66', 'jhn', 18, 28, 'От Каиафы повели Иисуса в преторию. Было утро; и они не вошли в преторию, чтобы не оскверниться, но чтобы <i>можно было</i> есть пасху.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35509, 'rst66', 'jhn', 18, 27, 'Петр опять отрекся; и тотчас запел петух.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35508, 'rst66', 'jhn', 18, 26, 'Один из рабов первосвященнических, родственник тому, которому Петр отсек ухо, говорит: не я ли видел тебя с Ним в саду?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35507, 'rst66', 'jhn', 18, 25, 'Симон же Петр стоял и грелся. Тут сказали ему: не из учеников ли Его и ты? Он отрекся и сказал: нет.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35506, 'rst66', 'jhn', 18, 24, 'Анна послал Его, связанного, к первосвященнику Каиафе.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35505, 'rst66', 'jhn', 18, 23, 'Иисус отвечал ему: если Я сказал худо, покажи, что худо; а если хорошо, что ты бьешь Меня?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35504, 'rst66', 'jhn', 18, 22, 'Когда Он сказал это, один из служителей, стоявший близко, ударил Иисуса по щеке, сказав: так отвечаешь Ты первосвященнику?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35503, 'rst66', 'jhn', 18, 21, 'Что спрашиваешь Меня? спроси слышавших, что&#769; Я говорил им; вот, они знают, что&#769; Я говорил.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35502, 'rst66', 'jhn', 18, 20, 'Иисус отвечал ему: Я говорил явно миру; Я всегда учил в синагоге и в храме, где всегда Иудеи сходятся, и тайно не говорил ничего.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35501, 'rst66', 'jhn', 18, 19, 'Первосвященник же спросил Иисуса об учениках Его и об учении Его.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35500, 'rst66', 'jhn', 18, 18, 'Между тем рабы и служители, разведя огонь, потому что было холодно, стояли и грелись. Петр также стоял с ними и грелся.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35499, 'rst66', 'jhn', 18, 17, 'Тут раба придверница говорит Петру: и ты не из учеников ли Этого Человека? Он сказал: нет.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35498, 'rst66', 'jhn', 18, 16, 'а Петр стоял вне за дверями. Потом другой ученик, который был знаком первосвященнику, вышел, и сказал придвернице, и ввел Петра.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35497, 'rst66', 'jhn', 18, 15, 'За Иисусом следовали Симон Петр и другой ученик; ученик же сей был знаком первосвященнику и вошел с Иисусом во двор первосвященнический,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35496, 'rst66', 'jhn', 18, 14, 'Это был Каиафа, который подал совет Иудеям, что лучше одному человеку умереть за народ.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35495, 'rst66', 'jhn', 18, 13, 'и отвели Его сперва к Анне, ибо он был тесть Каиафе, который был на тот год первосвященником.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35494, 'rst66', 'jhn', 18, 12, 'Тогда воины, и тысяченачальник, и служители Иудейские взяли Иисуса и связали Его,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35493, 'rst66', 'jhn', 18, 11, 'Но Иисус сказал Петру: вложи меч в ножны; неужели Мне не пить чаши, которую дал Мне Отец?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35492, 'rst66', 'jhn', 18, 10, 'Симон же Петр, имея меч, извлек его, и ударил первосвященнического раба, и отсек ему правое ухо. Имя рабу было Малх.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35491, 'rst66', 'jhn', 18, 9, 'да сбудется слово, реченное Им: «из тех, которых Ты Мне дал, Я не погубил никого».');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35490, 'rst66', 'jhn', 18, 8, 'Иисус отвечал: Я сказал вам, что это Я; итак, если Меня ищете, оставьте их, пусть идут, &mdash;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35489, 'rst66', 'jhn', 18, 7, 'Опять спросил их: кого ищете? Они сказали: Иисуса Назорея.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35488, 'rst66', 'jhn', 18, 6, 'И когда сказал им: «это Я», &mdash; они отступили назад и пали на землю.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35487, 'rst66', 'jhn', 18, 5, 'Ему отвечали: Иисуса Назорея. Иисус говорит им: это Я. Стоял же с ними и Иуда, предатель Его.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35486, 'rst66', 'jhn', 18, 4, 'Иисус же, зная все, что с Ним будет, вышел и сказал им: кого ищете?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35485, 'rst66', 'jhn', 18, 3, 'Итак, Иуда, взяв отряд <i>воинов</i> и служителей от первосвященников и фарисеев, приходит туда с фонарями и светильниками и оружием.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35484, 'rst66', 'jhn', 18, 2, 'Знал же это место и Иуда, предатель Его, потому что Иисус часто собирался там с учениками Своими.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35483, 'rst66', 'jhn', 18, 1, 'Сказав сие, Иисус вышел с учениками Своими за поток Кедрон, где был сад, в который вошел Сам и ученики Его.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35482, 'rst66', 'jhn', 17, 26, 'И Я открыл им имя Твое и открою, да любовь, которою Ты возлюбил Меня, в них будет, и Я в них.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35481, 'rst66', 'jhn', 17, 25, 'Отче праведный! и мир Тебя не познал; а Я познал Тебя, и сии познали, что Ты послал Меня.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35480, 'rst66', 'jhn', 17, 24, 'Отче! которых Ты дал Мне, хочу, чтобы там, где Я, и они были со Мною, да видят славу Мою, которую Ты дал Мне, потому что возлюбил Меня прежде основания мира.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35479, 'rst66', 'jhn', 17, 23, 'Я в них, и Ты во Мне; да будут совершены&#769; воедино, и да познает мир, что Ты послал Меня и возлюбил их, как возлюбил Меня.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35478, 'rst66', 'jhn', 17, 22, 'И славу, которую Ты дал Мне, Я дал им: да будут едино, как Мы едино.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35477, 'rst66', 'jhn', 17, 21, 'да будут все едино, как Ты, Отче, во Мне, и Я в Тебе, <i>так</i> и они да будут в Нас едино, &mdash; да уверует мир, что Ты послал Меня.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35476, 'rst66', 'jhn', 17, 20, 'Не о них же только молю, но и о верующих в Меня по слову их,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35475, 'rst66', 'jhn', 17, 19, 'И за них Я посвящаю Себя, чтобы и они были освящены истиною.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35474, 'rst66', 'jhn', 17, 18, 'Как Ты послал Меня в мир, <i>так</i> и Я послал их в мир.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35473, 'rst66', 'jhn', 17, 17, 'Освяти их истиною Твоею: слово Твое есть истина.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35472, 'rst66', 'jhn', 17, 16, 'Они не от мира, как и Я не от мира.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35471, 'rst66', 'jhn', 17, 15, 'Не молю, чтобы Ты взял их из мира, но чтобы сохранил их от зла.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35470, 'rst66', 'jhn', 17, 14, 'Я передал им слово Твое; и мир возненавидел их, потому что они не от мира, как и Я не от мира.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35469, 'rst66', 'jhn', 17, 13, 'Ныне же к Тебе иду, и сие говорю в мире, чтобы они имели в себе радость Мою совершенную.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35468, 'rst66', 'jhn', 17, 12, 'Когда Я был с ними в мире, Я соблюдал их во имя Твое; тех, которых Ты дал Мне, Я сохранил, и никто из них не погиб, кроме сына погибели, да сбудется Писание.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35467, 'rst66', 'jhn', 17, 11, 'Я уже не в мире, но они в мире, а Я к Тебе иду. Отче Святой! соблюди их во имя Твое, <i>тех</i>, которых Ты Мне дал, чтобы они были едино, как и Мы.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35466, 'rst66', 'jhn', 17, 10, 'И все Мое &mdash; Твое, и Твое &mdash; Мое; и Я прославился в них.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35465, 'rst66', 'jhn', 17, 9, 'Я о них молю: не о всем мире молю, но о тех, которых Ты дал Мне, потому что они Твои.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35464, 'rst66', 'jhn', 17, 8, 'ибо слова, которые Ты дал Мне, Я передал им, и они приняли, и уразумели истинно, что Я исшел от Тебя, и уверовали, что Ты послал Меня.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35463, 'rst66', 'jhn', 17, 7, 'Ныне уразумели они, что все, что Ты дал Мне, от Тебя есть,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35462, 'rst66', 'jhn', 17, 6, 'Я открыл имя Твое человекам, которых Ты дал Мне от мира; они были Твои, и Ты дал их Мне, и они сохранили слово Твое.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35461, 'rst66', 'jhn', 17, 5, 'И ныне прославь Меня Ты, Отче, у Тебя Самого славою, которую Я имел у Тебя прежде бытия мира.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35460, 'rst66', 'jhn', 17, 4, 'Я прославил Тебя на земле, совершил дело, которое Ты поручил Мне исполнить.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35459, 'rst66', 'jhn', 17, 3, 'Сия же есть жизнь вечная, да знают Тебя, единого истинного Бога, и посланного Тобою Иисуса Христа.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35458, 'rst66', 'jhn', 17, 2, 'так как Ты дал Ему власть над всякою плотью, да всему, что Ты дал Ему, даст Он жизнь вечную.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35457, 'rst66', 'jhn', 17, 1, 'После сих слов Иисус возвел очи Свои на небо и сказал: Отче! пришел час, прославь Сына Твоего, да и Сын Твой прославит Тебя,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35456, 'rst66', 'jhn', 16, 33, 'Сие сказал Я вам, чтобы вы имели во Мне мир. В мире будете иметь скорбь; но мужайтесь: Я победил мир.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35455, 'rst66', 'jhn', 16, 32, 'Вот, наступает час, и настал уже, что вы рассеетесь каждый в свою <i>сторону</i> и Меня оставите одного; но Я не один, потому что Отец со Мною.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35454, 'rst66', 'jhn', 16, 31, 'Иисус отвечал им: теперь веруете?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35453, 'rst66', 'jhn', 16, 30, 'Теперь видим, что Ты знаешь все и не имеешь нужды, чтобы кто спрашивал Тебя. Посему веруем, что Ты от Бога исшел.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35452, 'rst66', 'jhn', 16, 29, 'Ученики Его сказали Ему: вот, теперь Ты прямо говоришь и притчи не говоришь никакой.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35451, 'rst66', 'jhn', 16, 28, 'Я исшел от Отца и пришел в мир; и опять оставляю мир и иду к Отцу.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35450, 'rst66', 'jhn', 16, 27, 'ибо Сам Отец любит вас, потому что вы возлюбили Меня и уверовали, что Я исшел от Бога.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35449, 'rst66', 'jhn', 16, 26, 'В тот день будете просить во имя Мое, и не говорю вам, что Я буду просить Отца о вас:');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35448, 'rst66', 'jhn', 16, 25, 'Доселе Я говорил вам притчами; но наступает время, когда уже не буду говорить вам притчами, но прямо возвещу вам об Отце.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35447, 'rst66', 'jhn', 16, 24, 'Доныне вы ничего не просили во имя Мое; проси&#769;те и полу&#769;чите, чтобы радость ваша была совершенна.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35446, 'rst66', 'jhn', 16, 23, 'и в тот день вы не спросите Меня ни о чем. Истинно, истинно говорю вам: о чем ни попросите Отца во имя Мое, даст вам.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35445, 'rst66', 'jhn', 16, 22, 'Так и вы теперь имеете печаль; но Я увижу вас опять, и возрадуется сердце ваше, и радости вашей никто не отнимет у вас;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35444, 'rst66', 'jhn', 16, 21, 'Женщина, когда рождает, терпит скорбь, потому что пришел час ее; но когда родит младенца, уже не помнит скорби от радости, потому что родился человек в мир.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35443, 'rst66', 'jhn', 16, 20, 'Истинно, истинно говорю вам: вы восплачете и возрыдаете, а мир возрадуется; вы печальны будете, но печаль ваша в радость будет.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35442, 'rst66', 'jhn', 16, 19, 'Иисус, уразумев, что хотят спросить Его, сказал им: о том ли спрашиваете вы один другого, что Я сказал: «вскоре не увидите Меня, и опять вскоре увидите Меня»?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35441, 'rst66', 'jhn', 16, 18, 'Итак, они говорили: что это говорит Он: «вскоре»? Не знаем, что говорит.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35440, 'rst66', 'jhn', 16, 17, 'Тут <i>некоторые</i> из учеников Его сказали один другому: что это Он говорит нам: «вскоре не увидите Меня, и опять вскоре увидите Меня», и: «Я иду к Отцу»?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35439, 'rst66', 'jhn', 16, 16, 'Вскоре вы не увидите Меня, и опять вскоре увидите Меня, ибо Я иду к Отцу.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35438, 'rst66', 'jhn', 16, 15, 'Все, что имеет Отец, есть Мое; потому Я сказал, что от Моего возьмет и возвестит вам.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35437, 'rst66', 'jhn', 16, 14, 'Он прославит Меня, потому что от Моего возьмет и возвестит вам.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35436, 'rst66', 'jhn', 16, 13, 'Когда же придет Он, Дух истины, то наставит вас на всякую истину; ибо не от Себя говорить будет, но будет говорить, что услышит, и будущее возвестит вам.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35435, 'rst66', 'jhn', 16, 12, 'Еще многое имею сказать вам; но вы теперь не можете вместить.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35434, 'rst66', 'jhn', 16, 11, 'о суде же, что князь мира сего осужден.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35433, 'rst66', 'jhn', 16, 10, 'о правде, что Я иду к Отцу Моему, и уже не увидите Меня;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35432, 'rst66', 'jhn', 16, 9, 'о грехе, что не веруют в Меня;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35431, 'rst66', 'jhn', 16, 8, 'и Он, придя, обличит мир о грехе и о правде и о суде:');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35430, 'rst66', 'jhn', 16, 7, 'Но Я истину говорю вам: лучше для вас, чтобы Я пошел; ибо, если Я не пойду, Утешитель не придет к вам; а если пойду, то пошлю Его к вам,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35429, 'rst66', 'jhn', 16, 6, 'Но оттого, что Я сказал вам это, печалью исполнилось сердце ваше.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35428, 'rst66', 'jhn', 16, 5, 'А теперь иду к Пославшему Меня, и никто из вас не спрашивает Меня: «куда идешь?»');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35427, 'rst66', 'jhn', 16, 4, 'Но Я сказал вам сие для того, чтобы вы, когда придет то время, вспомнили, что Я сказывал вам о том; не говорил же сего вам сначала, потому что был с вами.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35426, 'rst66', 'jhn', 16, 3, 'Так будут поступать, потому что не познали ни Отца, ни Меня.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35425, 'rst66', 'jhn', 16, 2, 'Изгонят вас из синагог; даже наступает время, когда всякий, убивающий вас, будет думать, что он <i>тем</i> служит Богу.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35424, 'rst66', 'jhn', 16, 1, 'Сие сказал Я вам, чтобы вы не соблазнились.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35423, 'rst66', 'jhn', 15, 27, 'а также и вы будете свидетельствовать, потому что вы сначала со Мною.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35422, 'rst66', 'jhn', 15, 26, 'Когда же придет Утешитель, Которого Я пошлю вам от Отца, Дух истины, Который от Отца исходит, Он будет свидетельствовать о Мне;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35421, 'rst66', 'jhn', 15, 25, 'Но да сбудется слово, написанное в законе их: «возненавидели Меня напрасно».');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35420, 'rst66', 'jhn', 15, 24, 'Если бы Я не сотворил между ними дел, каких никто другой не делал, то не имели бы греха; а теперь и видели, и возненавидели и Меня и Отца Моего.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35419, 'rst66', 'jhn', 15, 23, 'Ненавидящий Меня ненавидит и Отца Моего.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35418, 'rst66', 'jhn', 15, 22, 'Если бы Я не пришел и не говорил им, то не имели бы греха; а теперь не имеют извинения во грехе своем.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35417, 'rst66', 'jhn', 15, 21, 'Но все то сделают вам за имя Мое, потому что не знают Пославшего Меня.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35416, 'rst66', 'jhn', 15, 20, 'Помните слово, которое Я сказал вам: раб не больше господина своего. Если Меня гнали, будут гнать и вас; если Мое слово соблюдали, будут соблюдать и ваше.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35415, 'rst66', 'jhn', 15, 19, 'Если бы вы были от мира, то мир любил бы свое; а как вы не от мира, но Я избрал вас от мира, потому ненавидит вас мир.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35414, 'rst66', 'jhn', 15, 18, 'Если мир вас ненавидит, знайте, что Меня прежде вас возненавидел.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35413, 'rst66', 'jhn', 15, 17, 'Сие заповедаю вам, да любите друг друга.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35412, 'rst66', 'jhn', 15, 16, 'Не вы Меня избрали, а Я вас избрал и поставил вас, чтобы вы шли и приносили плод и чтобы плод ваш пребывал, дабы, чего ни попросите от Отца во имя Мое, Он дал вам.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35411, 'rst66', 'jhn', 15, 15, 'Я уже не называю вас рабами, ибо раб не знает, что&#769; делает господин его; но Я назвал вас друзьями, потому что сказал вам все, что слышал от Отца Моего.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35410, 'rst66', 'jhn', 15, 14, 'Вы &mdash; друзья Мои, если исполняете то, что Я заповедую вам.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35409, 'rst66', 'jhn', 15, 13, 'Нет больше той любви, как если кто положит душу свою за друзей своих.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35408, 'rst66', 'jhn', 15, 12, 'Сия есть заповедь Моя, да любите друг друга, как Я возлюбил вас.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35407, 'rst66', 'jhn', 15, 11, 'Сие сказал Я вам, да радость Моя в вас пребудет и радость ваша будет совершенна.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35406, 'rst66', 'jhn', 15, 10, 'Если заповеди Мои соблюдете, пребудете в любви Моей, как и Я соблюл заповеди Отца Моего и пребываю в Его любви.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35405, 'rst66', 'jhn', 15, 9, 'Как возлюбил Меня Отец, и Я возлюбил вас; пребудьте в любви Моей.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35404, 'rst66', 'jhn', 15, 8, 'Тем прославится Отец Мой, если вы принесете много плода и будете Моими учениками.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35403, 'rst66', 'jhn', 15, 7, 'Если пребудете во Мне и слова Мои в вас пребудут, то, чего ни пожелаете, проси&#769;те, и будет вам.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35402, 'rst66', 'jhn', 15, 6, 'Кто не пребудет во Мне, извергнется вон, как ветвь, и засохнет; а такие <i>ветви</i> собирают и бросают в огонь, и они сгорают.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35401, 'rst66', 'jhn', 15, 5, 'Я есмь лоза, а вы &mdash; ветви; кто пребывает во Мне, и Я в нем, тот приносит много плода; ибо без Меня не можете делать ничего.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35400, 'rst66', 'jhn', 15, 4, 'Пребудьте во Мне, и Я в вас. Как ветвь не может приносить плода сама собою, если не будет на лозе, так и вы, если не будете во Мне.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35399, 'rst66', 'jhn', 15, 3, 'Вы уже очищены через слово, которое Я проповедал вам.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35398, 'rst66', 'jhn', 15, 2, 'Всякую у Меня ветвь, не приносящую плода, Он отсекает; и всякую, приносящую плод, очищает, чтобы более принесла плода.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35397, 'rst66', 'jhn', 15, 1, 'Я есмь истинная виноградная лоза, а Отец Мой &mdash; виноградарь.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35396, 'rst66', 'jhn', 14, 31, 'Но чтобы мир знал, что Я люблю Отца и как заповедал Мне Отец, так и творю: встаньте, пойдем отсюда.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35395, 'rst66', 'jhn', 14, 30, 'Уже немного Мне говорить с вами; ибо идет князь мира сего и во Мне не имеет ничего.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35394, 'rst66', 'jhn', 14, 29, 'И вот, Я сказал вам <i>о том</i>, прежде нежели сбылось, дабы вы поверили, когда сбудется.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35393, 'rst66', 'jhn', 14, 28, 'Вы слышали, что Я сказал вам: «иду от вас и приду к вам». Если бы вы любили Меня, то возрадовались бы, что Я сказал: «иду к Отцу»; ибо Отец Мой более Меня.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35392, 'rst66', 'jhn', 14, 27, 'Мир оставляю вам, мир Мой даю вам; не так, как мир дает, Я даю вам. Да не смущается сердце ваше и да не устрашается.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35391, 'rst66', 'jhn', 14, 26, 'Утешитель же, Дух Святой, Которого пошлет Отец во имя Мое, научит вас всему и напомнит вам все, что Я говорил вам.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35390, 'rst66', 'jhn', 14, 25, 'Сие сказал Я вам, находясь с вами.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35389, 'rst66', 'jhn', 14, 24, 'Нелюбящий Меня не соблюдает слов Моих; слово же, которое вы слышите, не есть Мое, но пославшего Меня Отца.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35388, 'rst66', 'jhn', 14, 23, 'Иисус сказал ему в ответ: кто любит Меня, тот соблюдет слово Мое; и Отец Мой возлюбит его, и Мы придем к нему и обитель у него сотворим.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35387, 'rst66', 'jhn', 14, 22, 'Иуда (не Искариот) говорит Ему: Господи! что&#769; это, что Ты хочешь явить Себя нам, а не миру?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35386, 'rst66', 'jhn', 14, 21, 'Кто имеет заповеди Мои и соблюдает их, тот любит Меня; а кто любит Меня, тот возлюблен будет Отцом Моим; и Я возлюблю его и явлюсь ему Сам.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35385, 'rst66', 'jhn', 14, 20, 'В тот день узнаете вы, что Я в Отце Моем, и вы во Мне, и Я в вас.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35384, 'rst66', 'jhn', 14, 19, 'Еще немного, и мир уже не увидит Меня; а вы увидите Меня, ибо Я живу, и вы будете жить.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35383, 'rst66', 'jhn', 14, 18, 'Не оставлю вас сиротами; приду к вам.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35382, 'rst66', 'jhn', 14, 17, 'Духа истины, Которого мир не может принять, потому что не видит Его и не знает Его; а вы знаете Его, ибо Он с вами пребывает и в вас будет.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35381, 'rst66', 'jhn', 14, 16, 'И Я умолю Отца, и даст вам другого Утешителя, да пребудет с вами вовек,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35380, 'rst66', 'jhn', 14, 15, 'Если любите Меня, соблюдите Мои заповеди.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35379, 'rst66', 'jhn', 14, 14, 'Если чего попросите во имя Мое, Я то сделаю.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35378, 'rst66', 'jhn', 14, 13, 'И если чего попросите у Отца во имя Мое, то сделаю, да прославится Отец в Сыне.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35377, 'rst66', 'jhn', 14, 12, 'Истинно, истинно говорю вам: верующий в Меня, дела, которые творю Я, и он сотворит; и больше сих сотворит, потому что Я к Отцу Моему иду.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35376, 'rst66', 'jhn', 14, 11, 'Верьте Мне, что Я в Отце и Отец во Мне; а если не так, то верьте Мне по самым делам.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35375, 'rst66', 'jhn', 14, 10, 'Разве ты не веришь, что Я в Отце и Отец во Мне? Слова, которые говорю Я вам, говорю не от Себя; Отец, пребывающий во Мне, Он творит дела.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35374, 'rst66', 'jhn', 14, 9, 'Иисус сказал ему: столько времени Я с вами, и ты не знаешь Меня, Филипп? Видевший Меня видел Отца; как же ты говоришь: «покажи нам Отца»?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35373, 'rst66', 'jhn', 14, 8, 'Филипп сказал Ему: Господи! покажи нам Отца, и довольно для нас.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35372, 'rst66', 'jhn', 14, 7, 'Если бы вы знали Меня, то знали бы и Отца Моего. И отныне знаете Его и видели Его.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35371, 'rst66', 'jhn', 14, 6, 'Иисус сказал ему: Я есмь путь и истина и жизнь; никто не приходит к Отцу, как только через Меня.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35370, 'rst66', 'jhn', 14, 5, 'Фома сказал Ему: Господи! не знаем, куда идешь; и как можем знать путь?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35369, 'rst66', 'jhn', 14, 4, 'А куда Я иду, вы знаете, и путь знаете.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35368, 'rst66', 'jhn', 14, 3, 'И когда пойду и приготовлю вам место, приду опять и возьму вас к Себе, чтобы и вы были, где Я».');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35367, 'rst66', 'jhn', 14, 2, 'В доме Отца Моего обителей много. А если бы не так, Я сказал бы вам: «Я иду приготовить место вам.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35366, 'rst66', 'jhn', 14, 1, 'Да не смущается сердце ваше; веруйте в Бога и в Меня веруйте.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35365, 'rst66', 'jhn', 13, 38, 'Иисус отвечал ему: душу твою за Меня положишь? истинно, истинно говорю тебе: не пропоет петух, как отречешься от Меня трижды.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35364, 'rst66', 'jhn', 13, 37, 'Петр сказал Ему: Господи! почему я не могу идти за Тобою теперь? я душу мою положу за Тебя.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35363, 'rst66', 'jhn', 13, 36, 'Симон Петр сказал Ему: Господи! куда Ты идешь? Иисус отвечал ему: куда Я иду, ты не можешь теперь за Мною идти, а после пойдешь за Мною.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35362, 'rst66', 'jhn', 13, 35, 'По тому узна&#769;ют все, что вы Мои ученики, если будете иметь любовь между собою.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35361, 'rst66', 'jhn', 13, 34, 'Заповедь новую даю вам, да любите друг друга; как Я возлюбил вас, <i>так</i> и вы да любите друг друга.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35360, 'rst66', 'jhn', 13, 33, 'Дети! недолго уже быть Мне с вами. Будете искать Меня, и, как сказал Я Иудеям, что, куда Я иду, вы не можете прийти, <i>так</i> и вам говорю теперь.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35359, 'rst66', 'jhn', 13, 32, 'Если Бог прославился в Нем, то и Бог прославит Его в Себе, и вскоре прославит Его.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35358, 'rst66', 'jhn', 13, 31, 'Когда он вышел, Иисус сказал: ныне прославился Сын Человеческий, и Бог прославился в Нем.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35357, 'rst66', 'jhn', 13, 30, 'Он, приняв кусок, тотчас вышел; а была ночь.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35356, 'rst66', 'jhn', 13, 29, 'А как у Иуды был ящик, то некоторые думали, что Иисус говорит ему: «купи, что нам нужно к празднику», или чтобы дал что‐нибудь нищим.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35355, 'rst66', 'jhn', 13, 28, 'Но никто из возлежавших не понял, к чему Он это сказал ему.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35354, 'rst66', 'jhn', 13, 27, 'И после сего куска вошел в него сатана. Тогда Иисус сказал ему: что делаешь, делай скорее.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35353, 'rst66', 'jhn', 13, 26, 'Иисус отвечал: тот, кому Я, обмакнув кусок хлеба, подам. И, обмакнув кусок, подал Иуде Симонову Искариоту.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35352, 'rst66', 'jhn', 13, 25, 'Он, припав к груди Иисуса, сказал Ему: Господи! кто это?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35351, 'rst66', 'jhn', 13, 24, 'Ему Симон Петр сделал знак, чтобы спросил, кто это, о котором говорит.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35350, 'rst66', 'jhn', 13, 23, 'Один же из учеников Его, которого любил Иисус, возлежал у груди Иисуса.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35349, 'rst66', 'jhn', 13, 22, 'Тогда ученики озирались друг на друга, недоумевая, о ком Он говорит.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35348, 'rst66', 'jhn', 13, 21, 'Сказав это, Иисус возмутился духом, и засвидетельствовал, и сказал: истинно, истинно говорю вам, что один из вас предаст Меня.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35347, 'rst66', 'jhn', 13, 20, 'Истинно, истинно говорю вам: принимающий того, кого Я пошлю, Меня принимает; а принимающий Меня принимает Пославшего Меня.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35346, 'rst66', 'jhn', 13, 19, 'Теперь сказываю вам, прежде нежели <i>то</i> сбылось, дабы, когда сбудется, вы поверили, что это Я.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35345, 'rst66', 'jhn', 13, 18, 'Не о всех вас говорю; Я знаю, которых избрал. Но да сбудется Писание: «ядущий со Мною хлеб поднял на Меня пяту свою».');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35344, 'rst66', 'jhn', 13, 17, 'Если это знаете, блаженны вы, когда исполняете.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35343, 'rst66', 'jhn', 13, 16, 'Истинно, истинно говорю вам: раб не больше господина своего, и посланник не больше пославшего его.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35342, 'rst66', 'jhn', 13, 15, 'Ибо Я дал вам пример, чтобы и вы делали то же, что Я сделал вам.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35341, 'rst66', 'jhn', 13, 14, 'Итак, если Я, Господь и Учитель, умыл ноги вам, то и вы должны умывать ноги друг другу.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35340, 'rst66', 'jhn', 13, 13, 'Вы называете Меня Учителем и Господом, и правильно говорите, ибо Я точно то.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35339, 'rst66', 'jhn', 13, 12, 'Когда же умыл им ноги и надел одежду Свою, то, возлегши опять, сказал им: знаете ли, что&#769; Я сделал вам?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35338, 'rst66', 'jhn', 13, 11, 'Ибо знал Он предателя Своего, потому <i>и</i> сказал: не все вы чисты.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35337, 'rst66', 'jhn', 13, 10, 'Иисус говорит ему: омытому нужно только ноги умыть, потому что чист весь; и вы чисты, но не все.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35336, 'rst66', 'jhn', 13, 9, 'Симон Петр говорит Ему: Господи! не только ноги мои, но и руки и голову.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35335, 'rst66', 'jhn', 13, 8, 'Петр говорит Ему: не умоешь ног моих вовек. Иисус отвечал ему: если не умою тебя, не имеешь части со Мною.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35334, 'rst66', 'jhn', 13, 7, 'Иисус сказал ему в ответ: что&#769; Я делаю, теперь ты не знаешь, а уразумеешь после.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35333, 'rst66', 'jhn', 13, 6, 'Подходит к Симону Петру, и тот говорит Ему: Господи! Тебе ли умывать мои ноги?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35332, 'rst66', 'jhn', 13, 5, 'Потом влил воды в умывальницу и начал умывать ноги ученикам и отирать полотенцем, которым был препоясан.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35331, 'rst66', 'jhn', 13, 4, 'встал с вечери, снял <i>с Себя верхнюю</i> одежду и, взяв полотенце, препоясался.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35330, 'rst66', 'jhn', 13, 3, 'Иисус, зная, что Отец все отдал в руки Его и что Он от Бога исшел и к Богу отходит,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35329, 'rst66', 'jhn', 13, 2, 'И во время ве&#769;чери, когда диавол уже вложил в сердце Иуде Симонову Искариоту предать Его,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35328, 'rst66', 'jhn', 13, 1, 'Перед праздником Пасхи Иисус, зная, что пришел час Его перейти от мира сего к Отцу, <i>явил делом, что</i>, возлюбив Своих сущих в мире, до конца возлюбил их.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35327, 'rst66', 'jhn', 12, 50, 'И Я знаю, что заповедь Его есть жизнь вечная. Итак, что&#769; Я говорю, говорю, как сказал Мне Отец.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35326, 'rst66', 'jhn', 12, 49, 'Ибо Я говорил не от Себя; но пославший Меня Отец, Он дал Мне заповедь, что&#769; сказать и что&#769; говорить.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35325, 'rst66', 'jhn', 12, 48, 'Отвергающий Меня и не принимающий слов Моих имеет судью себе: слово, которое Я говорил, оно будет судить его в последний день.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35324, 'rst66', 'jhn', 12, 47, 'И если кто услышит Мои слова и не поверит, Я не сужу его, ибо Я пришел не судить мир, но спасти мир.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35323, 'rst66', 'jhn', 12, 46, 'Я, свет, пришел в мир, чтобы всякий верующий в Меня не оставался во тьме.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35322, 'rst66', 'jhn', 12, 45, 'И видящий Меня видит Пославшего Меня.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35321, 'rst66', 'jhn', 12, 44, 'Иисус же возгласил и сказал: верующий в Меня не в Меня верует, но в Пославшего Меня.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35320, 'rst66', 'jhn', 12, 43, 'ибо возлюбили больше славу человеческую, нежели славу Божию.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35319, 'rst66', 'jhn', 12, 42, 'Впрочем, и из начальников многие уверовали в Него; но ради фарисеев не исповедовали, чтобы не быть отлученными от синагоги,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35318, 'rst66', 'jhn', 12, 41, 'Сие сказал Исаия, когда видел славу Его и говорил о Нем.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35317, 'rst66', 'jhn', 12, 40, '«народ сей ослепил глаза свои и окаменил сердце свое, да не видят глазами, и не уразумеют сердцем, и не обратятся, чтобы Я исцелил их».');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35316, 'rst66', 'jhn', 12, 39, 'Потому не могли они веровать, что, как еще сказал Исаия,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35315, 'rst66', 'jhn', 12, 38, 'да сбудется слово Исаии пророка: «Господи! кто поверил слышанному от нас? и кому открылась мышца Господня?»');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35314, 'rst66', 'jhn', 12, 37, 'Столько чудес сотворил Он пред ними, и они не веровали в Него,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35313, 'rst66', 'jhn', 12, 36, 'Доколе свет с вами, веруйте в свет, да будете сынами света. Сказав это, Иисус отошел и скрылся от них.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35312, 'rst66', 'jhn', 12, 35, 'Тогда Иисус сказал им: еще на малое время свет есть с вами; ходите, пока есть свет, чтобы не объяла вас тьма, &mdash; а ходящий во тьме не знает, куда идет.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35311, 'rst66', 'jhn', 12, 34, 'Народ отвечал Ему: мы слышали из закона, что Христос пребывает вовек; как же Ты говоришь, что должно вознесену быть Сыну Человеческому? кто Этот Сын Человеческий?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35310, 'rst66', 'jhn', 12, 33, 'Сие говорил Он, давая разуметь, какою смертью Он умрет.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35309, 'rst66', 'jhn', 12, 32, 'И когда Я вознесен буду от земли, всех привлеку к Себе.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35308, 'rst66', 'jhn', 12, 31, 'Ныне суд миру сему; ныне князь мира сего изгнан будет вон.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35307, 'rst66', 'jhn', 12, 30, 'Иисус на это сказал: не для Меня был глас сей, но для народа.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35306, 'rst66', 'jhn', 12, 29, 'Народ, стоявший и слышавший <i>то</i>, говорил: это гром; а другие говорили: Ангел говорил Ему.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35305, 'rst66', 'jhn', 12, 28, 'Отче! прославь имя Твое. Тогда пришел с неба глас: и прославил и еще прославлю.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35304, 'rst66', 'jhn', 12, 27, 'Душа Моя теперь возмутилась; и что&#769; Мне сказать? Отче! избавь Меня от часа сего! Но на сей час Я и пришел.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35303, 'rst66', 'jhn', 12, 26, 'Кто Мне служит, Мне да последует; и где Я, там и слуга Мой будет. И кто Мне служит, того почтит Отец Мой.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35302, 'rst66', 'jhn', 12, 25, 'Любящий душу свою погубит ее; а ненавидящий душу свою в мире сем сохранит ее в жизнь вечную.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35301, 'rst66', 'jhn', 12, 24, 'Истинно, истинно говорю вам: если пшеничное зерно, пав в землю, не умрет, то останется одно; а если умрет, то принесет много плода.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35300, 'rst66', 'jhn', 12, 23, 'Иисус же сказал им в ответ: пришел час прославиться Сыну Человеческому.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35299, 'rst66', 'jhn', 12, 22, 'Филипп идет и говорит о том Андрею; и потом Андрей и Филипп сказывают о том Иисусу.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35298, 'rst66', 'jhn', 12, 21, 'Они подошли к Филиппу, который был из Вифсаиды Галилейской, и просили его, говоря: господин! нам хочется видеть Иисуса.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35297, 'rst66', 'jhn', 12, 20, 'Из пришедших на поклонение в праздник были некоторые Еллины.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35296, 'rst66', 'jhn', 12, 19, 'Фарисеи же говорили между собою: видите ли, что не успеваете ничего? весь мир идет за Ним.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35295, 'rst66', 'jhn', 12, 18, 'Потому и встретил Его народ, ибо слышал, что Он сотворил это чудо.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35294, 'rst66', 'jhn', 12, 17, 'Народ, бывший с Ним прежде, свидетельствовал, что Он вызвал из гроба Лазаря и воскресил его из мертвых.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35293, 'rst66', 'jhn', 12, 16, 'Ученики Его сперва не поняли этого; но когда прославился Иисус, тогда вспомнили, что та&#769;к было о Нем написано, и это сделали Ему.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35292, 'rst66', 'jhn', 12, 15, '«не бойся, дщерь Сионова! се, Царь твой грядет, сидя на молодом осле».');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35291, 'rst66', 'jhn', 12, 14, 'Иисус же, найдя молодого осла, сел на него, как написано:');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35290, 'rst66', 'jhn', 12, 13, 'взяли пальмовые ветви, вышли навстречу Ему и восклицали: осанна! благословен грядущий во имя Господне, Царь Израилев!');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35289, 'rst66', 'jhn', 12, 12, 'На другой день множество народа, пришедшего на праздник, услышав, что Иисус идет в Иерусалим,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35288, 'rst66', 'jhn', 12, 11, 'потому что ради него многие из Иудеев приходили и веровали в Иисуса.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35287, 'rst66', 'jhn', 12, 10, 'Первосвященники же положили убить и Лазаря,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35286, 'rst66', 'jhn', 12, 9, 'Многие из Иудеев узнали, что Он там, и пришли не только для Иисуса, но чтобы видеть и Лазаря, которого Он воскресил из мертвых.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35285, 'rst66', 'jhn', 12, 8, 'Ибо нищих всегда имеете с собою, а Меня не всегда.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35284, 'rst66', 'jhn', 12, 7, 'Иисус же сказал: оставьте ее; она сберегла это на день погребения Моего.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35283, 'rst66', 'jhn', 12, 6, 'Сказал же он это не потому, чтобы заботился о нищих, но потому, что был вор. Он имел <i>при себе денежный</i> ящик и носил, что туда опускали.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35282, 'rst66', 'jhn', 12, 5, 'для чего бы не продать это миро за триста динариев и не раздать нищим?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35281, 'rst66', 'jhn', 12, 4, 'Тогда один из учеников Его, Иуда Симонов Искариот, который хотел предать Его, сказал:');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35280, 'rst66', 'jhn', 12, 3, 'Мария же, взяв фунт нардового чистого драгоценного мира, помазала ноги Иисуса и отерла волосами своими ноги Его; и дом наполнился благоуханием от мира.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35279, 'rst66', 'jhn', 12, 2, 'Там приготовили Ему ве&#769;черю, и Марфа служила, и Лазарь был одним из возлежавших с Ним.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35278, 'rst66', 'jhn', 12, 1, 'За шесть дней до Пасхи пришел Иисус в Вифанию, где был Лазарь умерший, которого Он воскресил из мертвых.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35277, 'rst66', 'jhn', 11, 57, 'Первосвященники же и фарисеи дали приказание, что если кто узнает, где Он будет, то объявил бы, дабы взять Его.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35276, 'rst66', 'jhn', 11, 56, 'Тогда искали Иисуса и, стоя в храме, говорили друг другу: как вы думаете? не придет ли Он на праздник?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35275, 'rst66', 'jhn', 11, 55, 'Приближалась Пасха Иудейская, и многие из всей страны пришли в Иерусалим перед Пасхою, чтобы очиститься.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35274, 'rst66', 'jhn', 11, 54, 'Посему Иисус уже не ходил явно между Иудеями, а пошел оттуда в страну близ пустыни, в город, называемый Ефраим, и там оставался с учениками Своими.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35273, 'rst66', 'jhn', 11, 53, 'С этого дня положили убить Его.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35272, 'rst66', 'jhn', 11, 52, 'и не только за народ, но чтобы и рассеянных чад Божиих собрать воедино.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35271, 'rst66', 'jhn', 11, 51, 'Сие же он сказал не от себя, но, будучи на тот год первосвященником, предсказал, что Иисус умрет за народ,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35270, 'rst66', 'jhn', 11, 50, 'и не подумаете, что лучше нам, чтобы один человек умер за людей, нежели чтобы весь народ погиб.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35269, 'rst66', 'jhn', 11, 49, 'Один же из них, некто Каиафа, будучи на тот год первосвященником, сказал им: вы ничего не знаете,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35268, 'rst66', 'jhn', 11, 48, 'Если оставим Его так, то все уверуют в Него, и придут Римляне и овладеют и местом нашим, и народом.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35267, 'rst66', 'jhn', 11, 47, 'Тогда первосвященники и фарисеи собрали совет и говорили: что нам делать? Этот Человек много чудес творит.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35266, 'rst66', 'jhn', 11, 46, 'А некоторые из них пошли к фарисеям и сказали им, что&#769; сделал Иисус.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35265, 'rst66', 'jhn', 11, 45, 'Тогда многие из Иудеев, пришедших к Марии и видевших, что&#769; сотворил Иисус, уверовали в Него.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35264, 'rst66', 'jhn', 11, 44, 'И вышел умерший, обвитый по рукам и ногам погребальными пеленами, и лицо его обвязано было платком. Иисус говорит им: развяжите его, пусть идет.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35263, 'rst66', 'jhn', 11, 43, 'Сказав это, Он воззвал громким голосом: Лазарь! иди вон.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35262, 'rst66', 'jhn', 11, 42, 'Я и знал, что Ты всегда услышишь Меня; но сказал <i>сие</i> для народа, здесь стоящего, чтобы поверили, что Ты послал Меня.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35261, 'rst66', 'jhn', 11, 41, 'Итак, отняли камень <i>от пещеры</i>, где лежал умерший. Иисус же возвел очи к небу и сказал: Отче! благодарю Тебя, что Ты услышал Меня.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35260, 'rst66', 'jhn', 11, 40, 'Иисус говорит ей: не сказал ли Я тебе, что, если будешь веровать, увидишь славу Божию?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35259, 'rst66', 'jhn', 11, 39, 'Иисус говорит: отнимите камень. Сестра умершего, Марфа, говорит Ему: Господи! уже смердит; ибо четыре дня, как он во гробе.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35258, 'rst66', 'jhn', 11, 38, 'Иисус же, опять скорбя внутренно, приходит ко гробу. То была пещера, и камень лежал на ней.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35257, 'rst66', 'jhn', 11, 37, 'А некоторые из них сказали: не мог ли Сей, отверзший очи слепому, сделать, чтобы и этот не умер?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35256, 'rst66', 'jhn', 11, 36, 'Тогда Иудеи говорили: смотри, как Он любил его.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35255, 'rst66', 'jhn', 11, 35, 'Иисус прослезился.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35254, 'rst66', 'jhn', 11, 34, 'и сказал: где вы положили его? Говорят Ему: Господи! пойди и посмотри.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35253, 'rst66', 'jhn', 11, 33, 'Иисус, когда увидел ее плачущую и пришедших с нею Иудеев плачущих, Сам восскорбел духом и возмутился');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35252, 'rst66', 'jhn', 11, 32, 'Мария же, придя туда, где был Иисус, и увидев Его, пала к ногам Его и сказала Ему: Господи! если бы Ты был здесь, не умер бы брат мой.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35251, 'rst66', 'jhn', 11, 31, 'Иудеи, которые были с нею в доме и утешали ее, видя, что Мария поспешно встала и вышла, пошли за нею, полагая, что она пошла на гроб &mdash; плакать там.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35250, 'rst66', 'jhn', 11, 30, 'Иисус еще не входил в селение, но был на том месте, где встретила Его Марфа.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35249, 'rst66', 'jhn', 11, 29, 'Она, как скоро услышала, поспешно встала и пошла к Нему.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35248, 'rst66', 'jhn', 11, 28, 'Сказав это, пошла и позвала тайно Марию, сестру свою, говоря: Учитель здесь и зовет тебя.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35247, 'rst66', 'jhn', 11, 27, 'Она говорит Ему: так, Господи! я верую, что Ты Христос, Сын Божий, грядущий в мир.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35246, 'rst66', 'jhn', 11, 26, 'И всякий, живущий и верующий в Меня, не умрет вовек. Веришь ли сему?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35245, 'rst66', 'jhn', 11, 25, 'Иисус сказал ей: Я есмь воскресение и жизнь; верующий в Меня, если и умрет, оживет.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35244, 'rst66', 'jhn', 11, 24, 'Марфа сказала Ему: знаю, что воскреснет в воскресение, в последний день.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35243, 'rst66', 'jhn', 11, 23, 'Иисус говорит ей: воскреснет брат твой.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35242, 'rst66', 'jhn', 11, 22, 'Но и теперь знаю, что чего Ты попросишь у Бога, даст Тебе Бог.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35241, 'rst66', 'jhn', 11, 21, 'Тогда Марфа сказала Иисусу: Господи! если бы Ты был здесь, не умер бы брат мой.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35240, 'rst66', 'jhn', 11, 20, 'Марфа, услышав, что идет Иисус, пошла навстречу Ему; Мария же сидела дома.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35239, 'rst66', 'jhn', 11, 19, 'и многие из Иудеев пришли к Марфе и Марии утешать их <i>в печали</i> о брате их.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35238, 'rst66', 'jhn', 11, 18, 'Вифания же была близ Иерусалима, стадиях в пятнадцати;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35237, 'rst66', 'jhn', 11, 17, 'Иисус, придя, нашел, что он уже четыре дня в гробе.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35236, 'rst66', 'jhn', 11, 16, 'Тогда Фома, иначе называемый Близнец, сказал ученикам: пойдем и мы умрем с ним.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35235, 'rst66', 'jhn', 11, 15, 'и радуюсь за вас, что Меня не было там, дабы вы уверовали; но пойдем к нему.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35234, 'rst66', 'jhn', 11, 14, 'Тогда Иисус сказал им прямо: Лазарь умер;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35233, 'rst66', 'jhn', 11, 13, 'Иисус говорил о смерти его, а они думали, что Он говорит о сне обыкновенном.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35232, 'rst66', 'jhn', 11, 12, 'Ученики Его сказали: Господи! если уснул, то выздоровеет.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35231, 'rst66', 'jhn', 11, 11, 'Сказав это, говорит им потом: Лазарь, друг наш, уснул; но Я иду разбудить его.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35230, 'rst66', 'jhn', 11, 10, 'а кто ходит ночью, спотыкается, потому что нет света с ним.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35229, 'rst66', 'jhn', 11, 9, 'Иисус отвечал: не двенадцать ли часов во дне? кто ходит днем, тот не спотыкается, потому что видит свет мира сего;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35228, 'rst66', 'jhn', 11, 8, 'Ученики сказали Ему: Равви&#769;! давно ли Иудеи искали побить Тебя камнями, и Ты опять идешь туда?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35227, 'rst66', 'jhn', 11, 7, 'После этого сказал ученикам: пойдем опять в Иудею.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35226, 'rst66', 'jhn', 11, 6, 'Когда же услышал, что он болен, то пробыл два дня на том месте, где находился.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35225, 'rst66', 'jhn', 11, 5, 'Иисус же любил Марфу и сестру ее и Лазаря.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35224, 'rst66', 'jhn', 11, 4, 'Иисус, услышав <i>то</i>, сказал: эта болезнь не к смерти, но к славе Божией, да прославится через нее Сын Божий.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35223, 'rst66', 'jhn', 11, 3, 'Сестры послали сказать Ему: Господи! вот, кого Ты любишь, болен.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35222, 'rst66', 'jhn', 11, 2, 'Мария же, которой брат Лазарь был болен, была <i>та</i>, которая помазала Господа миром и отерла ноги Его волосами своими.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35221, 'rst66', 'jhn', 11, 1, 'Был болен некто Лазарь из Вифании, из селения, <i>где жили</i> Мария и Марфа, сестра ее.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35220, 'rst66', 'jhn', 10, 42, 'И многие там уверовали в Него.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35219, 'rst66', 'jhn', 10, 41, 'Многие пришли к Нему и говорили, что Иоанн не сотворил никакого чуда, но всё, что сказал Иоанн о Нем, было истинно.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35218, 'rst66', 'jhn', 10, 40, 'и пошел опять за Иордан, на то место, где прежде крестил Иоанн, и остался там.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35217, 'rst66', 'jhn', 10, 39, 'Тогда опять искали схватить Его; но Он уклонился от рук их,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35216, 'rst66', 'jhn', 10, 38, 'а если творю, то, когда не верите Мне, верьте делам Моим, чтобы узнать и поверить, что Отец во Мне и Я в Нем.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35215, 'rst66', 'jhn', 10, 37, 'Если Я не творю дел Отца Моего, не верьте Мне;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35214, 'rst66', 'jhn', 10, 36, 'Тому ли, Которого Отец освятил и послал в мир, вы говорите: «богохульствуешь», потому что Я сказал: «Я &mdash; Сын Божий»?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35213, 'rst66', 'jhn', 10, 35, 'Если Он назвал богами тех, к которым было слово Божие, и не может нарушиться Писание, &mdash;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35212, 'rst66', 'jhn', 10, 34, 'Иисус отвечал им: не написано ли в законе вашем: «Я сказал: вы &mdash; боги»?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35211, 'rst66', 'jhn', 10, 33, 'Иудеи сказали Ему в ответ: не за доброе дело хотим побить Тебя камнями, но за богохульство и за то, что Ты, будучи человек, делаешь Себя Богом.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35210, 'rst66', 'jhn', 10, 32, 'Иисус отвечал им: много добрых дел показал Я вам от Отца Моего; за которое из них хотите побить Меня камнями?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35209, 'rst66', 'jhn', 10, 31, 'Тут опять Иудеи схватили каменья, чтобы побить Его.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35208, 'rst66', 'jhn', 10, 30, 'Я и Отец &mdash; одно.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35207, 'rst66', 'jhn', 10, 29, 'Отец Мой, Который дал Мне их, больше всех; и никто не может похитить их из руки Отца Моего.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35206, 'rst66', 'jhn', 10, 28, 'И Я даю им жизнь вечную, и не погибнут вовек; и никто не похитит их из руки Моей.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35205, 'rst66', 'jhn', 10, 27, 'Овцы Мои слушаются голоса Моего, и Я знаю их; и они идут за Мною.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35204, 'rst66', 'jhn', 10, 26, 'Но вы не верите, ибо вы не из овец Моих, как Я сказал вам.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35203, 'rst66', 'jhn', 10, 25, 'Иисус отвечал им: Я сказал вам, и не верите; дела, которые творю Я во имя Отца Моего, они свидетельствуют о Мне.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35202, 'rst66', 'jhn', 10, 24, 'Тут Иудеи обступили Его и говорили Ему: долго ли Тебе держать нас в недоумении? если Ты Христос, скажи нам прямо.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35201, 'rst66', 'jhn', 10, 23, 'И ходил Иисус в храме, в притворе Соломоновом.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35200, 'rst66', 'jhn', 10, 22, 'Настал же тогда в Иерусалиме <i>праздник</i> обновления, и была зима.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35199, 'rst66', 'jhn', 10, 21, 'Другие говорили: это слова не бесноватого; может ли бес отверзать очи слепым?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35198, 'rst66', 'jhn', 10, 20, 'Многие из них говорили: Он одержим бесом и безумствует; что слушаете Его?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35197, 'rst66', 'jhn', 10, 19, 'От этих слов опять произошла между Иудеями распря.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35196, 'rst66', 'jhn', 10, 18, 'Никто не отнимает ее у Меня, но Я Сам отдаю ее. Имею власть отдать ее и власть имею опять принять ее. Сию заповедь получил Я от Отца Моего.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35195, 'rst66', 'jhn', 10, 17, 'Потому любит Меня Отец, что Я отдаю жизнь Мою, чтобы опять принять ее.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35194, 'rst66', 'jhn', 10, 16, 'Есть у Меня и другие овцы, которые не сего двора, и тех надлежит Мне привести: и они услышат голос Мой, и будет одно стадо и один Пастырь.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35193, 'rst66', 'jhn', 10, 15, 'Как Отец знает Меня, <i>так</i> и Я знаю Отца, и жизнь Мою полагаю за овец.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35192, 'rst66', 'jhn', 10, 14, 'Я есмь пастырь добрый, и знаю Моих, и Мои знают Меня.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35191, 'rst66', 'jhn', 10, 13, 'А наемник бежит, потому что наемник, и нерадит об овцах.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35190, 'rst66', 'jhn', 10, 12, 'А наемник, не пастырь, которому овцы не свои, видит приходящего волка, и оставляет овец, и бежит; и волк расхищает овец и разгоняет их.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35189, 'rst66', 'jhn', 10, 11, 'Я есмь пастырь добрый: пастырь добрый полагает жизнь свою за овец.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35188, 'rst66', 'jhn', 10, 10, 'Вор приходит только для того, чтобы украсть, убить и погубить. Я пришел для того, чтобы имели жизнь и имели с избытком.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35187, 'rst66', 'jhn', 10, 9, 'Я есмь дверь: кто войдет Мною, тот спасется, и войдет, и выйдет, и пажить найдет.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35186, 'rst66', 'jhn', 10, 8, 'Все, сколько их ни приходило предо Мною, суть воры и разбойники; но овцы не послушали их.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35185, 'rst66', 'jhn', 10, 7, 'Итак, опять Иисус сказал им: истинно, истинно говорю вам, что Я &mdash; дверь овцам.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35184, 'rst66', 'jhn', 10, 6, 'Сию притчу сказал им Иисус; но они не поняли, что такое Он говорил им.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35183, 'rst66', 'jhn', 10, 5, 'За чужим же не идут, но бегут от него, потому что не знают чужого голоса.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35182, 'rst66', 'jhn', 10, 4, 'И когда выведет своих овец, идет перед ними; а овцы за ним идут, потому что знают голос его.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35181, 'rst66', 'jhn', 10, 3, 'Ему придверник отворяет, и овцы слушаются голоса его, и он зовет своих овец по имени и выводит их.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35180, 'rst66', 'jhn', 10, 2, 'а входящий дверью есть пастырь овцам.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35179, 'rst66', 'jhn', 10, 1, 'Истинно, истинно говорю вам: кто не дверью входит во двор овчий, но перелазит инде, тот вор и разбойник;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35178, 'rst66', 'jhn', 9, 41, 'Иисус сказал им: если бы вы были слепы, то не имели бы <i>на себе</i> греха; но как вы говорите, что видите, то грех остается на вас.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35177, 'rst66', 'jhn', 9, 40, 'Услышав это, некоторые из фарисеев, бывших с Ним, сказали Ему: неужели и мы слепы?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35176, 'rst66', 'jhn', 9, 39, 'И сказал Иисус: на суд пришел Я в мир сей, чтобы невидящие видели, а видящие стали слепы.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35175, 'rst66', 'jhn', 9, 38, 'Он же сказал: верую, Господи! И поклонился Ему.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35174, 'rst66', 'jhn', 9, 37, 'Иисус сказал ему: и видел ты Его, и Он говорит с тобою.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35173, 'rst66', 'jhn', 9, 36, 'Он отвечал и сказал: а кто Он, Господи, чтобы мне веровать в Него?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35172, 'rst66', 'jhn', 9, 35, 'Иисус, услышав, что выгнали его вон, и найдя его, сказал ему: ты веруешь ли в Сына Божия?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35171, 'rst66', 'jhn', 9, 34, 'Сказали ему в ответ: во грехах ты весь родился, и ты ли нас учишь? И выгнали его вон.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35170, 'rst66', 'jhn', 9, 33, 'Если бы Он не был от Бога, не мог бы творить ничего.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35169, 'rst66', 'jhn', 9, 32, 'От века не слыхано, чтобы кто отверз очи слепорожденному.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35168, 'rst66', 'jhn', 9, 31, 'Но мы знаем, что грешников Бог не слушает; но кто чтит Бога и творит волю Его, того слушает.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35167, 'rst66', 'jhn', 9, 30, 'Человек <i>прозревший</i> сказал им в ответ: это и удивительно, что вы не знаете, откуда Он, а Он отверз мне очи.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35166, 'rst66', 'jhn', 9, 29, 'Мы знаем, что с Моисеем говорил Бог; Сего же не знаем, откуда Он.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35165, 'rst66', 'jhn', 9, 28, 'Они же укорили его и сказали: ты ученик Его, а мы Моисеевы ученики.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35164, 'rst66', 'jhn', 9, 27, 'Отвечал им: я уже сказал вам, и вы не слушали; что&#769; еще хотите слышать? или и вы хотите сделаться Его учениками?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35163, 'rst66', 'jhn', 9, 26, 'Снова спросили его: что&#769; сделал Он с тобою? как отверз твои очи?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35162, 'rst66', 'jhn', 9, 25, 'Он сказал им в ответ: грешник ли Он, не знаю; одно знаю, что я был слеп, а теперь вижу.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35161, 'rst66', 'jhn', 9, 24, 'Итак, вторично призвали человека, который был слеп, и сказали ему: воздай славу Богу; мы знаем, что Человек Тот &mdash; грешник.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35160, 'rst66', 'jhn', 9, 23, 'Посему‐то родители его и сказали: он в совершенных летах; самого спроси&#769;те.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35159, 'rst66', 'jhn', 9, 22, 'Так отвечали родители его, потому что боялись Иудеев; ибо Иудеи сговорились уже, чтобы, кто признает Его за Христа, того отлучать от синагоги.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35158, 'rst66', 'jhn', 9, 21, 'а как теперь видит, не знаем, или кто отверз ему очи, мы не знаем. Сам в совершенных летах; самого спроси&#769;те; пусть сам о себе скажет.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35157, 'rst66', 'jhn', 9, 20, 'Родители его сказали им в ответ: мы знаем, что это сын наш и что он родился слепым,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35156, 'rst66', 'jhn', 9, 19, 'и спросили их: это ли сын ваш, о котором вы говорите, что родился слепым? как же он теперь видит?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35155, 'rst66', 'jhn', 9, 18, 'Тогда Иудеи не поверили, что он был слеп и прозрел, доколе не призвали родителей сего прозревшего');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35154, 'rst66', 'jhn', 9, 17, 'Опять говорят слепому: ты что&#769; скажешь о Нем, потому что Он отверз тебе очи? Он сказал: это пророк.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35153, 'rst66', 'jhn', 9, 16, 'Тогда некоторые из фарисеев говорили: не от Бога Этот Человек, потому что не хранит субботы. Другие говорили: как может человек грешный творить такие чудеса? И была между ними распря.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35152, 'rst66', 'jhn', 9, 15, 'Спросили его также и фарисеи, как он прозрел. Он сказал им: брение положил Он на мои глаза, и я умылся и вижу.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35151, 'rst66', 'jhn', 9, 14, 'А была суббота, когда Иисус сделал брение и отверз ему очи.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35150, 'rst66', 'jhn', 9, 13, 'Повели сего бывшего слепца к фарисеям.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35149, 'rst66', 'jhn', 9, 12, 'Тогда сказали ему: где Он? Он отвечал: не знаю.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35148, 'rst66', 'jhn', 9, 11, 'Он сказал в ответ: Человек, называемый Иисус, сделал брение, помазал глаза мои и сказал мне: «пойди на купальню Силоам и умойся». Я пошел, умылся и прозрел.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35147, 'rst66', 'jhn', 9, 10, 'Тогда спрашивали у него: как открылись у тебя глаза?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35146, 'rst66', 'jhn', 9, 9, 'Иные говорили: это он, а иные: похож на него. Он же говорил: это я.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35145, 'rst66', 'jhn', 9, 8, 'Тут соседи и видевшие прежде, что он был слеп, говорили: не тот ли это, который сидел и просил милостыни?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35144, 'rst66', 'jhn', 9, 7, 'и сказал ему: пойди, умойся в купальне Силоам, что значит: «посланный». Он пошел и умылся, и пришел зрячим.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35143, 'rst66', 'jhn', 9, 6, 'Сказав это, Он плюнул на землю, сделал брение из плюновения и помазал брением глаза слепому,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35142, 'rst66', 'jhn', 9, 5, 'Доколе Я в мире, Я &mdash; свет миру.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35141, 'rst66', 'jhn', 9, 4, 'Мне должно делать дела Пославшего Меня, доколе есть день; приходит ночь, когда никто не может делать.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35140, 'rst66', 'jhn', 9, 3, 'Иисус отвечал: не согрешил ни он, ни родители его, но <i>это для того</i>, чтобы на нем явились дела Божии.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35139, 'rst66', 'jhn', 9, 2, 'Ученики Его спросили у Него: Равви&#769;! кто согрешил, он или родители его, что родился слепым?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35138, 'rst66', 'jhn', 9, 1, 'И, проходя, увидел человека, слепого от рождения.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35137, 'rst66', 'jhn', 8, 59, 'Тогда взяли каменья, чтобы бросить на Него; но Иисус скрылся и вышел из храма, пройдя посреди них, и пошел далее.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35136, 'rst66', 'jhn', 8, 58, 'Иисус сказал им: истинно, истинно говорю вам: прежде нежели был Авраам, Я есмь.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35135, 'rst66', 'jhn', 8, 57, 'На это сказали Ему Иудеи: Тебе нет еще пятидесяти лет, &mdash; и Ты видел Авраама?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35134, 'rst66', 'jhn', 8, 56, 'Авраам, отец ваш, рад был увидеть день Мой; и увидел и возрадовался.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35133, 'rst66', 'jhn', 8, 55, 'И вы не познали Его, а Я знаю Его; и если скажу, что не знаю Его, то буду подобный вам лжец. Но Я знаю Его и соблюдаю слово Его.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35132, 'rst66', 'jhn', 8, 54, 'Иисус отвечал: если Я Сам Себя славлю, то слава Моя &mdash; ничто. Меня прославляет Отец Мой, о Котором вы говорите, что Он Бог ваш.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35131, 'rst66', 'jhn', 8, 53, 'Неужели Ты больше отца нашего Авраама, который умер? и пророки умерли: чем Ты Себя делаешь?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35130, 'rst66', 'jhn', 8, 52, 'Иудеи сказали Ему: теперь узнали мы, что бес в Тебе. Авраам умер и пророки, а Ты говоришь: «кто соблюдет слово Мое, тот не вкусит смерти вовек».');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35129, 'rst66', 'jhn', 8, 51, 'Истинно, истинно говорю вам: кто соблюдет слово Мое, тот не увидит смерти вовек.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35128, 'rst66', 'jhn', 8, 50, 'Впрочем, Я не ищу Моей славы: есть Ищущий и Судящий.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35127, 'rst66', 'jhn', 8, 49, 'Иисус отвечал: во Мне беса нет; но Я чту Отца Моего, а вы бесчестите Меня.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35126, 'rst66', 'jhn', 8, 48, 'На это Иудеи отвечали и сказали Ему: не правду ли мы говорим, что Ты Самарянин и что бес в Тебе?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35125, 'rst66', 'jhn', 8, 47, 'Кто от Бога, тот слушает слова Божии. Вы потому не слушаете, что вы не от Бога.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35124, 'rst66', 'jhn', 8, 46, 'Кто из вас обличит Меня в неправде? Если же Я говорю истину, почему вы не верите Мне?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35123, 'rst66', 'jhn', 8, 45, 'А как Я истину говорю, то не верите Мне.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35122, 'rst66', 'jhn', 8, 44, 'Ваш отец &mdash; диавол, и вы хотите исполнять похоти отца вашего. Он был человекоубийца от начала и не устоял в истине, ибо нет в нем истины. Когда говорит он ложь, говорит свое, ибо он &mdash; лжец и отец лжи.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35121, 'rst66', 'jhn', 8, 43, 'Почему вы не понимаете речи Моей? Потому что не можете слышать слова Моего.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35120, 'rst66', 'jhn', 8, 42, 'Иисус сказал им: если бы Бог был Отец ваш, то вы любили бы Меня, потому что Я от Бога исшел и пришел; ибо Я не Сам от Себя пришел, но Он послал Меня.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35119, 'rst66', 'jhn', 8, 41, 'Вы делаете дела отца вашего. На это сказали Ему: мы не от любодеяния рождены; одного Отца имеем, Бога.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35118, 'rst66', 'jhn', 8, 40, 'А теперь ищете убить Меня, Человека, сказавшего вам истину, которую слышал от Бога: Авраам этого не делал.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35117, 'rst66', 'jhn', 8, 39, 'Сказали Ему в ответ: отец наш есть Авраам. Иисус сказал им: если бы вы были дети Авраама, то дела Авраамовы делали бы.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35116, 'rst66', 'jhn', 8, 38, 'Я говорю то, что видел у Отца Моего; а вы делаете то, что видели у отца вашего.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35115, 'rst66', 'jhn', 8, 37, 'Знаю, что вы &mdash; семя Авраамово; однако ищете убить Меня, потому что слово Мое не вмещается в вас.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35114, 'rst66', 'jhn', 8, 36, 'Итак, если Сын освободит вас, то истинно свободны будете.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35113, 'rst66', 'jhn', 8, 35, 'Но раб не пребывает в доме вечно; сын пребывает вечно.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35112, 'rst66', 'jhn', 8, 34, 'Иисус отвечал им: истинно, истинно говорю вам: всякий, делающий грех, есть раб греха.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35111, 'rst66', 'jhn', 8, 33, 'Ему отвечали: мы &mdash; семя Авраамово и не были рабами никому никогда; как же Ты говоришь: «сделаетесь свободными»?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35110, 'rst66', 'jhn', 8, 32, 'и позна&#769;ете истину, и истина сделает вас свободными.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35109, 'rst66', 'jhn', 8, 31, 'Тогда сказал Иисус к уверовавшим в Него Иудеям: если пребудете в слове Моем, то вы истинно Мои ученики,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35108, 'rst66', 'jhn', 8, 30, 'Когда Он говорил это, многие уверовали в Него.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35107, 'rst66', 'jhn', 8, 29, 'Пославший Меня есть со Мною; Отец не оставил Меня одного, ибо Я всегда делаю то, что Ему угодно.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35106, 'rst66', 'jhn', 8, 28, 'Итак, Иисус сказал им: когда вознесете Сына Человеческого, тогда узнаете, что это Я и что ничего не делаю от Себя, но как научил Меня Отец Мой, так и говорю.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35105, 'rst66', 'jhn', 8, 27, 'Не поняли, что Он говорил им об Отце.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35104, 'rst66', 'jhn', 8, 26, 'Много имею говорить и судить о вас; но Пославший Меня есть истинен, и что Я слышал от Него, то и говорю миру.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35103, 'rst66', 'jhn', 8, 25, 'Тогда сказали Ему: кто же Ты? Иисус сказал им: от начала Сущий, как и говорю вам.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35102, 'rst66', 'jhn', 8, 24, 'Потому Я и сказал вам, что вы умрете во грехах ваших; ибо если не уверуете, что это Я, то умрете во грехах ваших.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35101, 'rst66', 'jhn', 8, 23, 'Он сказал им: вы от нижних, Я от вышних; вы от мира сего, Я не от сего мира.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35100, 'rst66', 'jhn', 8, 22, 'Тут Иудеи говорили: неужели Он убьет Сам Себя, что говорит: «куда Я иду, вы не можете прийти»?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35099, 'rst66', 'jhn', 8, 21, 'Опять сказал им Иисус: Я отхожу, и будете искать Меня, и умрете во грехе вашем. Куда Я иду, <i>туда</i> вы не можете прийти.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35098, 'rst66', 'jhn', 8, 20, 'Сии слова говорил Иисус у сокровищницы, когда учил в храме; и никто не взял Его, потому что еще не пришел час Его.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35097, 'rst66', 'jhn', 8, 19, 'Тогда сказали Ему: где Твой Отец? Иисус отвечал: вы не знаете ни Меня, ни Отца Моего; если бы вы знали Меня, то знали бы и Отца Моего.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35096, 'rst66', 'jhn', 8, 18, 'Я Сам свидетельствую о Себе, и свидетельствует о Мне Отец, пославший Меня.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35095, 'rst66', 'jhn', 8, 17, 'А и в законе вашем написано, что двух человек свидетельство истинно.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35094, 'rst66', 'jhn', 8, 16, 'А если и сужу Я, то суд Мой истинен, потому что Я не один, но Я и Отец, пославший Меня.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35093, 'rst66', 'jhn', 8, 15, 'Вы су&#769;дите по плоти; Я не сужу никого.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35092, 'rst66', 'jhn', 8, 14, 'Иисус сказал им в ответ: если Я и Сам о Себе свидетельствую, свидетельство Мое истинно; потому что Я знаю, откуда пришел и куда иду; а вы не знаете, откуда Я и куда иду.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35091, 'rst66', 'jhn', 8, 13, 'Тогда фарисеи сказали Ему: Ты Сам о Себе свидетельствуешь, свидетельство Твое не истинно.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35090, 'rst66', 'jhn', 8, 12, 'Опять говорил Иисус <i>к народу</i> и сказал им: Я &mdash; свет миру; кто последует за Мною, тот не будет ходить во тьме, но будет иметь свет жизни.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35089, 'rst66', 'jhn', 8, 11, 'Она отвечала: никто, Господи. Иисус сказал ей: и Я не осуждаю тебя; иди и впредь не греши.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35088, 'rst66', 'jhn', 8, 10, 'Иисус, восклонившись и не видя никого, кроме женщины, сказал ей: женщина! где твои обвинители? никто не осудил тебя?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35087, 'rst66', 'jhn', 8, 9, 'Они же, услышав <i>то</i> и будучи обличаемы совестью, стали уходить один за другим, начиная от старших до последних; и остался один Иисус и женщина, стоящая посреди.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35086, 'rst66', 'jhn', 8, 8, 'И опять, наклонившись низко, писал на земле.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35085, 'rst66', 'jhn', 8, 7, 'Когда же продолжали спрашивать Его, Он, восклонившись, сказал им: кто из вас без греха, первый брось на нее камень.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35084, 'rst66', 'jhn', 8, 6, 'Говорили же это, искушая Его, чтобы найти что‐нибудь к обвинению Его. Но Иисус, наклонившись низко, писал перстом на земле, не обращая на них внимания.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35083, 'rst66', 'jhn', 8, 5, 'а Моисей в законе заповедал нам побивать таких камнями: Ты что скажешь?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35082, 'rst66', 'jhn', 8, 4, 'сказали Ему: Учитель! эта женщина взята в прелюбодеянии;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35081, 'rst66', 'jhn', 8, 3, 'Тут книжники и фарисеи привели к Нему женщину, взятую в прелюбодеянии, и, поставив ее посреди,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35080, 'rst66', 'jhn', 8, 2, 'А утром опять пришел в храм, и весь народ шел к Нему. Он сел и учил их.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35079, 'rst66', 'jhn', 8, 1, 'Иисус же пошел на гору Елеонскую.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35078, 'rst66', 'jhn', 7, 53, 'И разошлись все по домам.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35077, 'rst66', 'jhn', 7, 52, 'На это сказали ему: и ты не из Галилеи ли? рассмотри и увидишь, что из Галилеи не приходит пророк.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35076, 'rst66', 'jhn', 7, 51, 'судит ли закон наш человека, если прежде не выслушают его и не узнают, что&#769; он делает?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35075, 'rst66', 'jhn', 7, 50, 'Никодим, приходивший к Нему ночью, будучи один из них, говорит им:');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35074, 'rst66', 'jhn', 7, 49, 'Но этот народ &mdash; невежда в законе, проклят он.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35073, 'rst66', 'jhn', 7, 48, 'Уверовал ли в Него кто из начальников или из фарисеев?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35072, 'rst66', 'jhn', 7, 47, 'Фарисеи сказали им: неужели и вы прельстились?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35071, 'rst66', 'jhn', 7, 46, 'Служители отвечали: никогда человек не говорил так, как Этот Человек.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35070, 'rst66', 'jhn', 7, 45, 'Итак, служители возвратились к первосвященникам и фарисеям, и сии сказали им: для чего вы не привели Его?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35069, 'rst66', 'jhn', 7, 44, 'Некоторые из них хотели схватить Его; но никто не наложил на Него рук.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35068, 'rst66', 'jhn', 7, 43, 'Итак произошла о Нем распря в народе.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35067, 'rst66', 'jhn', 7, 42, 'Не сказано ли в Писании, что Христос придет от семени Давидова и из Вифлеема, из того места, откуда был Давид?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35066, 'rst66', 'jhn', 7, 41, 'Другие говорили: это &mdash; Христос. А иные говорили: разве из Галилеи Христос придет?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35065, 'rst66', 'jhn', 7, 40, 'Многие из народа, услышав сии слова, говорили: Он точно пророк.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35064, 'rst66', 'jhn', 7, 39, 'Сие сказал Он о Духе, Которого имели принять верующие в Него: ибо еще не было на них Духа Святого, потому что Иисус еще не был прославлен.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35063, 'rst66', 'jhn', 7, 38, 'Кто верует в Меня, у того, как сказано в Писании, из чрева потекут реки воды живой.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35062, 'rst66', 'jhn', 7, 37, 'В последний же великий день праздника стоял Иисус и возгласил, говоря: кто жаждет, иди ко Мне и пей.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35061, 'rst66', 'jhn', 7, 36, 'Что значат сии слова, которые Он сказал: «будете искать Меня, и не найдете; и где буду Я, <i>туда</i> вы не можете прийти»?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35060, 'rst66', 'jhn', 7, 35, 'При сем Иудеи говорили между собою: куда Он хочет идти, так что мы не найдем Его? Не хочет ли Он идти в Еллинское рассеяние и учить Еллинов?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35059, 'rst66', 'jhn', 7, 34, 'Будете искать Меня, и не найдете; и где буду Я, <i>туда</i> вы не можете прийти.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35058, 'rst66', 'jhn', 7, 33, 'Иисус же сказал им: еще недолго быть Мне с вами, и пойду к Пославшему Меня;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35057, 'rst66', 'jhn', 7, 32, 'Услышали фарисеи такие толки о Нем в народе, и послали фарисеи и первосвященники служителей схватить Его.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35056, 'rst66', 'jhn', 7, 31, 'Многие же из народа уверовали в Него и говорили: когда придет Христос, неужели сотворит больше знамений, нежели сколько Сей сотворил?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35055, 'rst66', 'jhn', 7, 30, 'И искали схватить Его, но никто не наложил на Него руки&#769;, потому что еще не пришел час Его.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35054, 'rst66', 'jhn', 7, 29, 'Я знаю Его, потому что Я от Него, и Он послал Меня.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35053, 'rst66', 'jhn', 7, 28, 'Тогда Иисус возгласил в храме, уча и говоря: и знаете Меня, и знаете, откуда Я; и Я пришел не Сам от Себя, но истинен Пославший Меня, Которого вы не знаете.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35052, 'rst66', 'jhn', 7, 27, 'Но мы знаем Его, откуда Он; Христос же когда придет, никто не будет знать, откуда Он.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35051, 'rst66', 'jhn', 7, 26, 'Вот, Он говорит явно, и ничего не говорят Ему: не удостоверились ли начальники, что Он &mdash; подлинно Христос?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35050, 'rst66', 'jhn', 7, 25, 'Тут некоторые из Иерусалимлян говорили: не Тот ли это, Которого ищут убить?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35049, 'rst66', 'jhn', 7, 24, 'Не суди&#769;те по наружности, но суди&#769;те судом праведным.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35048, 'rst66', 'jhn', 7, 23, 'Если в субботу принимает человек обрезание, чтобы не был нарушен закон Моисеев, &mdash; на Меня ли негодуете за то, что Я всего человека исцелил в субботу?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35047, 'rst66', 'jhn', 7, 22, 'Моисей дал вам обрезание (хотя оно не от Моисея, но от отцов), и в субботу вы обрезываете человека.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35046, 'rst66', 'jhn', 7, 21, 'Иисус, продолжая речь, сказал им: одно дело сделал Я, и все вы дивитесь.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35045, 'rst66', 'jhn', 7, 20, 'Народ сказал в ответ: не бес ли в Тебе? кто ищет убить Тебя?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35044, 'rst66', 'jhn', 7, 19, 'Не дал ли вам Моисей закона? и никто из вас не поступает по закону. За что ищете убить Меня?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35043, 'rst66', 'jhn', 7, 18, 'Говорящий сам от себя ищет славы себе; а Кто ищет славы Пославшему Его, Тот истинен, и нет неправды в Нем.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35042, 'rst66', 'jhn', 7, 17, 'кто хочет творить волю Его, тот узнает о сем учении, от Бога ли оно или Я Сам от Себя говорю.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35041, 'rst66', 'jhn', 7, 16, 'Иисус, отвечая им, сказал: Мое учение &mdash; не Мое, но Пославшего Меня;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35040, 'rst66', 'jhn', 7, 15, 'И дивились Иудеи, говоря: как Он знает Писания, не учившись?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35039, 'rst66', 'jhn', 7, 14, 'Но в половине уже праздника вошел Иисус в храм и учил.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35038, 'rst66', 'jhn', 7, 13, 'Впрочем, никто не говорил о Нем явно, боясь Иудеев.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35037, 'rst66', 'jhn', 7, 12, 'И много толков было о Нем в народе: одни говорили, что Он добр; а другие говорили: нет, но обольщает народ.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35036, 'rst66', 'jhn', 7, 11, 'Иудеи же искали Его на празднике и говорили: где Он?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35035, 'rst66', 'jhn', 7, 10, 'Но когда пришли братья Его, тогда и Он пришел на праздник не явно, а как бы тайно.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35034, 'rst66', 'jhn', 7, 9, 'Сие сказав им, остался в Галилее.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35033, 'rst66', 'jhn', 7, 8, 'Вы пойдите на праздник сей; а Я еще не пойду на сей праздник, потому что Мое время еще не исполнилось.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35032, 'rst66', 'jhn', 7, 7, 'Вас мир не может ненавидеть, а Меня ненавидит, потому что Я свидетельствую о нем, что дела его злы.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35031, 'rst66', 'jhn', 7, 6, 'На это Иисус сказал им: Мое время еще не настало, а для вас всегда время.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35030, 'rst66', 'jhn', 7, 5, 'Ибо и братья Его не веровали в Него.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35029, 'rst66', 'jhn', 7, 4, 'Ибо никто не делает чего‐либо втайне и ищет сам быть известным. Если Ты творишь такие дела, то яви Себя миру.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35028, 'rst66', 'jhn', 7, 3, 'Тогда братья Его сказали Ему: выйди отсюда и пойди в Иудею, чтобы и ученики Твои видели дела, которые Ты делаешь.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35027, 'rst66', 'jhn', 7, 2, 'Приближался праздник Иудейский &mdash; поставление кущей.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35026, 'rst66', 'jhn', 7, 1, 'После сего Иисус ходил по Галилее, ибо по Иудее не хотел ходить, потому что Иудеи искали убить Его.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35025, 'rst66', 'jhn', 6, 71, 'Это говорил Он об Иуде Симонове Искариоте, ибо сей хотел предать Его, будучи один из Двенадцати.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35024, 'rst66', 'jhn', 6, 70, 'Иисус отвечал им: не двенадцать ли вас избрал Я? но один из вас &mdash; диавол.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35023, 'rst66', 'jhn', 6, 69, 'и мы уверовали и познали, что Ты Христос, Сын Бога живого.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35022, 'rst66', 'jhn', 6, 68, 'Симон Петр отвечал Ему: Господи! к кому нам идти? Ты имеешь глаголы вечной жизни,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35021, 'rst66', 'jhn', 6, 67, 'Тогда Иисус сказал Двенадцати: не хотите ли и вы отойти?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35020, 'rst66', 'jhn', 6, 66, 'С этого времени многие из учеников Его отошли от Него и уже не ходили с Ним.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35019, 'rst66', 'jhn', 6, 65, 'И сказал: для того‐то и говорил Я вам, что никто не может прийти ко Мне, если то не дано будет ему от Отца Моего.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35018, 'rst66', 'jhn', 6, 64, 'Но есть из вас некоторые неверующие. (Ибо Иисус от начала знал, кто суть неверующие и кто предаст Его.)');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35017, 'rst66', 'jhn', 6, 63, 'Дух животворит; плоть не пользует нимало. Слова, которые говорю Я вам, суть дух и жизнь.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35016, 'rst66', 'jhn', 6, 62, 'Что ж, если увидите Сына Человеческого, восходящего <i>туда</i>, где был прежде?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35015, 'rst66', 'jhn', 6, 61, 'Но Иисус, зная Сам в Себе, что ученики Его ропщут на то, сказал им: это ли соблазняет вас?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35014, 'rst66', 'jhn', 6, 60, 'Многие из учеников Его, слыша то, говорили: какие странные слова! кто может это слушать?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35013, 'rst66', 'jhn', 6, 59, 'Сие говорил Он в синагоге, уча в Капернауме.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35012, 'rst66', 'jhn', 6, 58, 'Сей‐то есть хлеб, сшедший с небес. Не так, как отцы ваши ели манну и умерли: ядущий хлеб сей жить будет вовек.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35011, 'rst66', 'jhn', 6, 57, 'Как послал Меня живой Отец, и Я живу Отцом, <i>так</i> и ядущий Меня жить будет Мною.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35010, 'rst66', 'jhn', 6, 56, 'Ядущий Мою Плоть и пиющий Мою Кровь пребывает во Мне, и Я в нем.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35009, 'rst66', 'jhn', 6, 55, 'Ибо Плоть Моя истинно есть пища, и Кровь Моя истинно есть питие.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35008, 'rst66', 'jhn', 6, 54, 'Ядущий Мою Плоть и пиющий Мою Кровь имеет жизнь вечную, и Я воскрешу его в последний день.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35007, 'rst66', 'jhn', 6, 53, 'Иисус же сказал им: истинно, истинно говорю вам: если не будете есть Плоти Сына Человеческого и пить Крови Его, то не будете иметь в себе жизни.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35006, 'rst66', 'jhn', 6, 52, 'Тогда Иудеи стали спорить между собою, говоря: как Он может дать нам есть Плоть Свою?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35005, 'rst66', 'jhn', 6, 51, 'Я хлеб живой, сшедший с небес; ядущий хлеб сей будет жить вовек; хлеб же, который Я дам, есть Плоть Моя, которую Я отдам за жизнь мира.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35004, 'rst66', 'jhn', 6, 50, 'хлеб же, сходящий с небес, таков, что ядущий его не умрет.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35003, 'rst66', 'jhn', 6, 49, 'Отцы ваши ели манну в пустыне и умерли;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35002, 'rst66', 'jhn', 6, 48, 'Я есмь хлеб жизни.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35001, 'rst66', 'jhn', 6, 47, 'Истинно, истинно говорю вам: верующий в Меня имеет жизнь вечную.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (35000, 'rst66', 'jhn', 6, 46, 'Это не то, чтобы кто видел Отца, кроме Того, Кто есть от Бога: Он видел Отца.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34999, 'rst66', 'jhn', 6, 45, 'У пророков написано: «и будут все научены Богом». Всякий, слышавший от Отца и научившийся, приходит ко Мне.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34998, 'rst66', 'jhn', 6, 44, 'Никто не может прийти ко Мне, если не привлечет его Отец, пославший Меня; и Я воскрешу его в последний день.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34997, 'rst66', 'jhn', 6, 43, 'Иисус сказал им в ответ: не ропщите между собою.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34996, 'rst66', 'jhn', 6, 42, 'И говорили: не Иисус ли это, сын Иосифов, Которого отца и Мать мы знаем? Как же говорит Он: «Я сшел с небес»?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34995, 'rst66', 'jhn', 6, 41, 'Возроптали на Него Иудеи за то, что Он сказал: «Я есмь хлеб, сшедший с небес».');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34994, 'rst66', 'jhn', 6, 40, 'Воля Пославшего Меня есть та, чтобы всякий, видящий Сына и верующий в Него, имел жизнь вечную; и Я воскрешу его в последний день.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34993, 'rst66', 'jhn', 6, 39, 'Воля же пославшего Меня Отца есть та, чтобы из того, что Он Мне дал, ничего не погубить, но все то воскресить в последний день.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34992, 'rst66', 'jhn', 6, 38, 'ибо Я сошел с небес не для того, чтобы творить волю Мою, но волю пославшего Меня Отца.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34991, 'rst66', 'jhn', 6, 37, 'Все, что дает Мне Отец, ко Мне придет; и приходящего ко Мне не изгоню вон,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34990, 'rst66', 'jhn', 6, 36, 'Но Я сказал вам, что вы и видели Меня, и не веруете.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34989, 'rst66', 'jhn', 6, 35, 'Иисус же сказал им: Я есмь хлеб жизни; приходящий ко Мне не будет алкать, и верующий в Меня не будет жаждать никогда.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34988, 'rst66', 'jhn', 6, 34, 'На это сказали Ему: Господи! подавай нам всегда такой хлеб.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34987, 'rst66', 'jhn', 6, 33, 'Ибо хлеб Божий есть тот, который сходит с небес и дает жизнь миру.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34986, 'rst66', 'jhn', 6, 32, 'Иисус же сказал им: истинно, истинно говорю вам: не Моисей дал вам хлеб с неба, а Отец Мой дает вам истинный хлеб с небес.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34985, 'rst66', 'jhn', 6, 31, 'Отцы наши ели манну в пустыне, как написано: «хлеб с неба дал им есть».');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34984, 'rst66', 'jhn', 6, 30, 'На это сказали Ему: какое же Ты дашь знамение, чтобы мы увидели и поверили Тебе? что&#769; Ты делаешь?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34983, 'rst66', 'jhn', 6, 29, 'Иисус сказал им в ответ: вот дело Божие, чтобы вы веровали в Того, Кого Он послал.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34982, 'rst66', 'jhn', 6, 28, 'Итак сказали Ему: что нам делать, чтобы творить дела Божии?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34981, 'rst66', 'jhn', 6, 27, 'Старайтесь не о пище тленной, но о пище, пребывающей в жизнь вечную, которую даст вам Сын Человеческий, ибо на Нем положил печать <i>Свою</i> Отец, Бог.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34980, 'rst66', 'jhn', 6, 26, 'Иисус сказал им в ответ: истинно, истинно говорю вам: вы ищете Меня не потому, что видели чудеса, но потому, что ели хлеб и насытились.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34979, 'rst66', 'jhn', 6, 25, 'И, найдя Его на той стороне моря, сказали Ему: Равви&#769;! когда Ты сюда пришел?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34978, 'rst66', 'jhn', 6, 24, 'Итак, когда народ увидел, что тут нет Иисуса, ни учеников Его, то вошли в лодки и приплыли в Капернаум, ища Иисуса.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34977, 'rst66', 'jhn', 6, 23, 'Между тем пришли из Тивериады другие лодки близко к тому месту, где ели хлеб по благословении Господнем.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34976, 'rst66', 'jhn', 6, 22, 'На другой день народ, стоявший по ту сторону моря, видел, что там, кроме одной лодки, в которую вошли ученики Его, иной не было и что Иисус не входил в лодку с учениками Своими, а отплыли одни ученики Его.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34975, 'rst66', 'jhn', 6, 21, 'Они хотели принять Его в лодку; и тотчас лодка пристала к берегу, куда плыли.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34974, 'rst66', 'jhn', 6, 20, 'Но Он сказал им: это Я; не бойтесь.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34973, 'rst66', 'jhn', 6, 19, 'Проплыв около двадцати пяти или тридцати стадий, они увидели Иисуса, идущего по морю и приближающегося к лодке, и испугались.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34972, 'rst66', 'jhn', 6, 18, 'Дул сильный ветер, и море волновалось.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34971, 'rst66', 'jhn', 6, 17, 'и, войдя в лодку, отправились на ту сторону моря, в Капернаум. Становилось темно, а Иисус не приходил к ним.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34970, 'rst66', 'jhn', 6, 16, 'Когда же настал вечер, то ученики Его сошли к морю');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34969, 'rst66', 'jhn', 6, 15, 'Иисус же, узнав, что хотят прийти, нечаянно взять Его и сделать царем, опять удалился на гору один.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34968, 'rst66', 'jhn', 6, 14, 'Тогда люди, видевшие чудо, сотворенное Иисусом, сказали: это истинно Тот Пророк, Которому должно прийти в мир.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34967, 'rst66', 'jhn', 6, 13, 'И собрали, и наполнили двенадцать коробов кусками от пяти ячменных хлебов, оставшимися у тех, которые ели.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34966, 'rst66', 'jhn', 6, 12, 'И когда насытились, то сказал ученикам Своим: соберите оставшиеся куски, чтобы ничего не пропало.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34965, 'rst66', 'jhn', 6, 11, 'Иисус, взяв хлебы и воздав благодарение, раздал ученикам, а ученики возлежавшим, также и рыбы, сколько кто хотел.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34964, 'rst66', 'jhn', 6, 10, 'Иисус сказал: велите им возлечь. Было же на том месте много травы. Итак возлегло людей числом около пяти тысяч.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34963, 'rst66', 'jhn', 6, 9, 'здесь есть у одного мальчика пять хлебов ячменных и две рыбки; но что это для такого множества?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34962, 'rst66', 'jhn', 6, 8, 'Один из учеников Его, Андрей, брат Симона Петра, говорит Ему:');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34961, 'rst66', 'jhn', 6, 7, 'Филипп отвечал Ему: им на двести динариев не довольно будет хлеба, чтобы каждому из них досталось хотя понемногу.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34960, 'rst66', 'jhn', 6, 6, 'Говорил же это, испытывая его; ибо Сам знал, что&#769; хотел сделать.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34959, 'rst66', 'jhn', 6, 5, 'Иисус, возведя очи и увидев, что множество народа идет к Нему, говорит Филиппу: где нам купить хлебов, чтобы их накормить?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34958, 'rst66', 'jhn', 6, 4, 'Приближалась же Пасха, праздник Иудейский.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34957, 'rst66', 'jhn', 6, 3, 'Иисус взошел на гору и там сидел с учениками Своими.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34956, 'rst66', 'jhn', 6, 2, 'За Ним последовало множество народа, потому что видели чудеса, которые Он творил над больными.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34955, 'rst66', 'jhn', 6, 1, 'После сего пошел Иисус на ту сторону моря Галилейского, <i>в окрестности</i> Тивериады.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34954, 'rst66', 'jhn', 5, 47, 'Если же его писаниям не верите, как поверите Моим словам?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34953, 'rst66', 'jhn', 5, 46, 'Ибо если бы вы верили Моисею, то поверили бы и Мне, потому что он писал о Мне.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34952, 'rst66', 'jhn', 5, 45, 'Не думайте, что Я буду обвинять вас пред Отцом: есть на вас обвинитель Моисей, на которого вы уповаете.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34951, 'rst66', 'jhn', 5, 44, 'Как вы можете веровать, когда друг от друга принимаете славу, а славы, которая от Единого Бога, не ищете?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34950, 'rst66', 'jhn', 5, 43, 'Я пришел во имя Отца Моего, и не принимаете Меня; а если иной придет во имя свое, его примете.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34949, 'rst66', 'jhn', 5, 42, 'но знаю вас: вы не имеете в себе любви к Богу.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34948, 'rst66', 'jhn', 5, 41, 'Не принимаю славы от человеков,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34947, 'rst66', 'jhn', 5, 40, 'Но вы не хотите прийти ко Мне, чтобы иметь жизнь.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34946, 'rst66', 'jhn', 5, 39, 'Исследуйте Писания, ибо вы думаете чрез них иметь жизнь вечную; а они свидетельствуют о Мне.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34945, 'rst66', 'jhn', 5, 38, 'и не имеете слова Его, пребывающего в вас, потому что вы не веруете Тому, Которого Он послал.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34944, 'rst66', 'jhn', 5, 37, 'И пославший Меня Отец Сам засвидетельствовал о Мне. А вы ни гласа Его никогда не слышали, ни лица Его не видели;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34943, 'rst66', 'jhn', 5, 36, 'Я же имею свидетельство больше Иоаннова: ибо дела, которые Отец дал Мне совершить, самые дела сии, Мною творимые, свидетельствуют о Мне, что Отец послал Меня.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34942, 'rst66', 'jhn', 5, 35, 'Он был светильник, горящий и светящий; а вы хотели малое время порадоваться при свете его.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34941, 'rst66', 'jhn', 5, 34, 'Впрочем, Я не от человека принимаю свидетельство, но говорю это для того, чтобы вы спаслись.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34940, 'rst66', 'jhn', 5, 33, 'Вы посылали к Иоанну, и он засвидетельствовал об истине.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34939, 'rst66', 'jhn', 5, 32, 'Есть другой, свидетельствующий о Мне; и Я знаю, что истинно то свидетельство, которым он свидетельствует о Мне.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34938, 'rst66', 'jhn', 5, 31, 'Если Я свидетельствую Сам о Себе, то свидетельство Мое не есть истинно.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34937, 'rst66', 'jhn', 5, 30, 'Я ничего не могу творить Сам от Себя. Как слышу, так и сужу, и суд Мой праведен; ибо не ищу Моей воли, но воли пославшего Меня Отца.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34936, 'rst66', 'jhn', 5, 29, 'и изыдут творившие добро в воскресение жизни, а делавшие зло &mdash; в воскресение осуждения.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34935, 'rst66', 'jhn', 5, 28, 'Не дивитесь сему; ибо наступает время, в которое все, находящиеся в гробах, услышат глас Сына Божия;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34934, 'rst66', 'jhn', 5, 27, 'И дал Ему власть производить и суд, потому что Он есть Сын Человеческий.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34933, 'rst66', 'jhn', 5, 26, 'Ибо как Отец имеет жизнь в Самом Себе, так и Сыну дал иметь жизнь в Самом Себе.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34932, 'rst66', 'jhn', 5, 25, 'Истинно, истинно говорю вам: наступает время, и настало уже, когда мертвые услышат глас Сына Божия и, услышав, оживут.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34931, 'rst66', 'jhn', 5, 24, 'Истинно, истинно говорю вам: слушающий слово Мое и верующий в Пославшего Меня имеет жизнь вечную и на суд не приходит, но перешел от смерти в жизнь.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34930, 'rst66', 'jhn', 5, 23, 'дабы все чтили Сына, как чтут Отца. Кто не чтит Сына, тот не чтит и Отца, пославшего Его.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34929, 'rst66', 'jhn', 5, 22, 'Ибо Отец и не судит никого, но весь суд отдал Сыну,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34928, 'rst66', 'jhn', 5, 21, 'Ибо как Отец воскрешает мертвых и оживляет, так и Сын оживляет, кого хочет.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34927, 'rst66', 'jhn', 5, 20, 'Ибо Отец любит Сына и показывает Ему все, что творит Сам; и покажет Ему дела больше сих, так что вы удивитесь.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34926, 'rst66', 'jhn', 5, 19, 'На это Иисус сказал: истинно, истинно говорю вам: Сын ничего не может творить Сам от Себя, если не увидит Отца творящего: ибо что творит Он, то и Сын творит также.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34925, 'rst66', 'jhn', 5, 18, 'И еще более искали убить Его Иудеи за то, что Он не только нарушал субботу, но и Отцом Своим называл Бога, делая Себя равным Богу.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34924, 'rst66', 'jhn', 5, 17, 'Иисус же говорил им: Отец Мой доныне делает, и Я делаю.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34923, 'rst66', 'jhn', 5, 16, 'И стали Иудеи гнать Иисуса и искали убить Его за то, что Он делал такие <i>дела</i> в субботу.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34922, 'rst66', 'jhn', 5, 15, 'Человек сей пошел и объявил Иудеям, что исцеливший его есть Иисус.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34921, 'rst66', 'jhn', 5, 14, 'Потом Иисус встретил его в храме и сказал ему: вот, ты выздоровел; не греши больше, чтобы не случилось с тобою чего хуже.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34920, 'rst66', 'jhn', 5, 13, 'Исцеленный же не знал, кто Он, ибо Иисус скрылся в народе, бывшем на том месте.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34919, 'rst66', 'jhn', 5, 12, 'Его спросили: кто Тот Человек, Который сказал тебе: «возьми постель твою и ходи»?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34918, 'rst66', 'jhn', 5, 11, 'Он отвечал им: Кто меня исцелил, Тот мне сказал: «возьми постель твою и ходи».');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34917, 'rst66', 'jhn', 5, 10, 'Посему Иудеи говорили исцеленному: сегодня суббота; не должно тебе брать постели.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34916, 'rst66', 'jhn', 5, 9, 'И он тотчас выздоровел, и взял постель свою и пошел. Было же это в день субботний.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34915, 'rst66', 'jhn', 5, 8, 'Иисус говорит ему: встань, возьми постель твою и ходи.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34914, 'rst66', 'jhn', 5, 7, 'Больной отвечал Ему: так, Господи; но не имею человека, который опустил бы меня в купальню, когда возмутится вода; когда же я прихожу, другой уже сходит прежде меня.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34913, 'rst66', 'jhn', 5, 6, 'Иисус, увидев его лежащего и узнав, что он лежит уже долгое время, говорит ему: хочешь ли быть здоров?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34912, 'rst66', 'jhn', 5, 5, 'Тут был человек, находившийся в болезни тридцать восемь лет.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34911, 'rst66', 'jhn', 5, 4, 'ибо Ангел Господень по временам сходил в купальню и возмущал воду, и кто первый входил <i>в нее</i> по возмущении воды, тот выздоравливал, какою бы ни был одержим болезнью.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34910, 'rst66', 'jhn', 5, 3, 'В них лежало великое множество больных, слепых, хромых, иссохших, ожидающих движения воды,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34909, 'rst66', 'jhn', 5, 2, 'Есть же в Иерусалиме у Овечьих <i>ворот</i> купальня, называемая по‐еврейски Вифезда, при которой было пять крытых ходов.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34908, 'rst66', 'jhn', 5, 1, 'После сего был праздник Иудейский, и пришел Иисус в Иерусалим.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34907, 'rst66', 'jhn', 4, 54, 'Это второе чудо сотворил Иисус, возвратившись из Иудеи в Галилею.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34906, 'rst66', 'jhn', 4, 53, 'Из этого отец узнал, что это был тот час, в который Иисус сказал ему: «сын твой здоров». И уверовал сам и весь дом его.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34905, 'rst66', 'jhn', 4, 52, 'Он спросил у них: в котором часу стало ему легче? Ему сказали: вчера в седьмом часу горячка оставила его.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34904, 'rst66', 'jhn', 4, 51, 'На дороге встретили его слуги его и сказали: сын твой здоров.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34903, 'rst66', 'jhn', 4, 50, 'Иисус говорит ему: пойди, сын твой здоров. Он поверил слову, которое сказал ему Иисус, и пошел.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34902, 'rst66', 'jhn', 4, 49, 'Царедворец говорит Ему: Господи! приди, пока не умер сын мой.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34901, 'rst66', 'jhn', 4, 48, 'Иисус сказал ему: вы не уверуете, если не увидите знамений и чудес.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34900, 'rst66', 'jhn', 4, 47, 'Он, услышав, что Иисус пришел из Иудеи в Галилею, пришел к Нему и просил Его прийти и исцелить сына его, который был при смерти.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34899, 'rst66', 'jhn', 4, 46, 'Итак, Иисус опять пришел в Кану Галилейскую, где претворил воду в вино. В Капернауме был некоторый царедворец, у которого сын был болен.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34898, 'rst66', 'jhn', 4, 45, 'Когда пришел Он в Галилею, то Галилеяне приняли Его, видев всё, что Он сделал в Иерусалиме в праздник, &mdash; ибо и они ходили на праздник.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34897, 'rst66', 'jhn', 4, 44, 'ибо Сам Иисус свидетельствовал, что пророк не имеет чести в своем отечестве.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34896, 'rst66', 'jhn', 4, 43, 'По прошествии же двух дней Он вышел оттуда и пошел в Галилею,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34895, 'rst66', 'jhn', 4, 42, 'А женщине той говорили: уже не по твоим речам веруем, ибо сами слышали и узнали, что Он &mdash; истинно Спаситель мира, Христос.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34894, 'rst66', 'jhn', 4, 41, 'И еще большее число уверовали по Его слову.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34893, 'rst66', 'jhn', 4, 40, 'И потому, когда пришли к Нему Самаряне, то просили Его побыть у них; и Он пробыл там два дня.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34892, 'rst66', 'jhn', 4, 39, 'И многие Самаряне из города того уверовали в Него по слову женщины, свидетельствовавшей, что Он сказал ей всё, что она сделала.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34891, 'rst66', 'jhn', 4, 38, 'Я послал вас жать то, над чем вы не трудились: другие трудились, а вы вошли в труд их.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34890, 'rst66', 'jhn', 4, 37, 'ибо в этом случае справедливо изречение: «один сеет, а другой жнет».');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34889, 'rst66', 'jhn', 4, 36, 'Жнущий получает награду и собирает плод в жизнь вечную, так что и сеющий и жнущий вместе радоваться будут,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34888, 'rst66', 'jhn', 4, 35, 'Не говорите ли вы, что еще четыре месяца, и наступит жатва? А Я говорю вам: возведите очи ваши и посмотрите на нивы, как они побелели и поспели к жатве.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34887, 'rst66', 'jhn', 4, 34, 'Иисус говорит им: Моя пища есть творить волю Пославшего Меня и совершить дело Его.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34886, 'rst66', 'jhn', 4, 33, 'Посему ученики говорили между собою: разве кто принес Ему есть?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34885, 'rst66', 'jhn', 4, 32, 'Но Он сказал им: у Меня есть пища, которой вы не знаете.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34884, 'rst66', 'jhn', 4, 31, 'Между тем ученики просили Его, говоря: Равви&#769;! ешь.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34883, 'rst66', 'jhn', 4, 30, 'Они вышли из города и пошли к Нему.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34882, 'rst66', 'jhn', 4, 29, 'пойдите, посмотрите Человека, Который сказал мне все, что я сделала: не Он ли Христос?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34881, 'rst66', 'jhn', 4, 28, 'Тогда женщина оставила водонос свой и пошла в город, и говорит людям:');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34880, 'rst66', 'jhn', 4, 27, 'В это время пришли ученики Его и удивились, что Он разговаривал с женщиною; однако ж ни один не сказал: «чего Ты требуешь?» или: «о чем говоришь с нею?»');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34879, 'rst66', 'jhn', 4, 26, 'Иисус говорит ей: это Я, Который говорю с тобою.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34878, 'rst66', 'jhn', 4, 25, 'Женщина говорит Ему: знаю, что придет Мессия, то есть Христос; когда Он придет, то возвестит нам все.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34877, 'rst66', 'jhn', 4, 24, 'Бог есть дух, и поклоняющиеся Ему должны поклоняться в духе и истине.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34876, 'rst66', 'jhn', 4, 23, 'Но настанет время, и настало уже, когда истинные поклонники будут поклоняться Отцу в духе и истине, ибо таких поклонников Отец ищет Себе.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34875, 'rst66', 'jhn', 4, 22, 'Вы не знаете, чему кланяетесь, а мы знаем, чему кланяемся, ибо спасение &mdash; от Иудеев.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34874, 'rst66', 'jhn', 4, 21, 'Иисус говорит ей: поверь Мне, что наступает время, когда и не на горе сей, и не в Иерусалиме будете поклоняться Отцу.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34873, 'rst66', 'jhn', 4, 20, 'Отцы наши поклонялись на этой горе, а вы говорите, что место, где должно поклоняться, находится в Иерусалиме.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34872, 'rst66', 'jhn', 4, 19, 'Женщина говорит Ему: Господи! вижу, что Ты пророк.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34871, 'rst66', 'jhn', 4, 18, 'ибо у тебя было пять мужей, и тот, которого ныне имеешь, не муж тебе; это справедливо ты сказала.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34870, 'rst66', 'jhn', 4, 17, 'Женщина сказала в ответ: у меня нет мужа. Иисус говорит ей: правду ты сказала, что у тебя нет мужа,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34869, 'rst66', 'jhn', 4, 16, 'Иисус говорит ей: пойди, позови мужа твоего и приди сюда.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34868, 'rst66', 'jhn', 4, 15, 'Женщина говорит Ему: господин! дай мне этой воды, чтобы мне не иметь жажды и не приходить сюда черпать.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34867, 'rst66', 'jhn', 4, 14, 'а кто будет пить воду, которую Я дам ему, тот не будет жаждать вовек; но вода, которую Я дам ему, сделается в нем источником воды, текущей в жизнь вечную.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34866, 'rst66', 'jhn', 4, 13, 'Иисус сказал ей в ответ: всякий, пьющий воду сию, возжаждет опять,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34865, 'rst66', 'jhn', 4, 12, 'Неужели ты больше отца нашего Иакова, который дал нам этот колодезь и сам из него пил, и дети его, и скот его?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34864, 'rst66', 'jhn', 4, 11, 'Женщина говорит Ему: господин! тебе и почерпнуть нечем, а колодезь глубок; откуда же у тебя вода живая?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34863, 'rst66', 'jhn', 4, 10, 'Иисус сказал ей в ответ: если бы ты знала дар Божий и Кто говорит тебе: «дай Мне пить», то ты сама просила бы у Него, и Он дал бы тебе воду живую.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34862, 'rst66', 'jhn', 4, 9, 'Женщина Самарянская говорит Ему: как ты, будучи Иудей, просишь пить у меня, Самарянки? ибо Иудеи с Самарянами не сообщаются.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34861, 'rst66', 'jhn', 4, 8, 'Ибо ученики Его отлучились в город купить пищи.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34860, 'rst66', 'jhn', 4, 7, 'Приходит женщина из Самарии почерпнуть воды. Иисус говорит ей: дай Мне пить.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34859, 'rst66', 'jhn', 4, 6, 'Там был колодезь Иаковлев. Иисус, утрудившись от пути, сел у колодезя. Было около шестого часа.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34858, 'rst66', 'jhn', 4, 5, 'Итак, приходит Он в город Самарийский, называемый Сихарь, близ участка земли, данного Иаковом сыну своему Иосифу.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34857, 'rst66', 'jhn', 4, 4, 'Надлежало же Ему проходить через Самарию.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34856, 'rst66', 'jhn', 4, 3, 'то оставил Иудею и пошел опять в Галилею.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34855, 'rst66', 'jhn', 4, 2, 'хотя Сам Иисус не крестил, а ученики Его, &mdash;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34854, 'rst66', 'jhn', 4, 1, 'Когда же узнал Иисус о <i>дошедшем до</i> фарисеев слухе, что Он более приобретает учеников и крестит, нежели Иоанн, &mdash;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34853, 'rst66', 'jhn', 3, 36, 'Верующий в Сына имеет жизнь вечную, а не верующий в Сына не увидит жизни, но гнев Божий пребывает на нем.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34852, 'rst66', 'jhn', 3, 35, 'Отец любит Сына и все дал в руку Его.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34851, 'rst66', 'jhn', 3, 34, 'ибо Тот, Которого послал Бог, говорит слова Божии; ибо не мерою дает Бог Духа.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34850, 'rst66', 'jhn', 3, 33, 'Принявший Его свидетельство сим запечатлел, что Бог истинен,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34849, 'rst66', 'jhn', 3, 32, 'и что Он видел и слышал, о том и свидетельствует; и никто не принимает свидетельства Его.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34848, 'rst66', 'jhn', 3, 31, 'Приходящий свыше и есть выше всех; а сущий от земли &mdash; земной и есть, и говорит, как сущий от земли; Приходящий с небес есть выше всех,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34847, 'rst66', 'jhn', 3, 30, 'Ему должно расти, а мне умаляться.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34846, 'rst66', 'jhn', 3, 29, 'Имеющий невесту есть жених, а друг жениха, стоящий и внимающий ему, радостью радуется, слыша голос жениха. Сия‐то радость моя исполнилась.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34845, 'rst66', 'jhn', 3, 28, 'Вы сами мне свидетели в том, что я сказал: «не я Христос, но я послан пред Ним».');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34844, 'rst66', 'jhn', 3, 27, 'Иоанн сказал в ответ: не может человек ничего принимать <i>на себя</i>, если не будет дано ему с неба.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34843, 'rst66', 'jhn', 3, 26, 'И пришли к Иоанну и сказали ему: равви&#769;! Тот, Который был с тобою при Иордане и о Котором ты свидетельствовал, вот, Он крестит, и все идут к Нему.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34842, 'rst66', 'jhn', 3, 25, 'Тогда у Иоанновых учеников произошел спор с Иудеями об очищении.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34841, 'rst66', 'jhn', 3, 24, 'ибо Иоанн еще не был заключен в темницу.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34840, 'rst66', 'jhn', 3, 23, 'А Иоанн также крестил в Еноне, близ Салима, потому что там было много воды; и приходили <i>туда</i> и крестились,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34839, 'rst66', 'jhn', 3, 22, 'После сего пришел Иисус с учениками Своими в землю Иудейскую и там жил с ними и крестил.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34838, 'rst66', 'jhn', 3, 21, 'а поступающий по правде идет к свету, дабы явны были дела его, потому что они в Боге соделаны.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34837, 'rst66', 'jhn', 3, 20, 'Ибо всякий, делающий злое, ненавидит свет и не идет к свету, чтобы не обличились дела его, потому что они злы;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34836, 'rst66', 'jhn', 3, 19, 'Суд же состоит в том, что свет пришел в мир; но люди более возлюбили тьму, нежели свет, потому что дела их были злы.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34835, 'rst66', 'jhn', 3, 18, 'Верующий в Него не судится, а неверующий уже осужден, потому что не уверовал во имя Единородного Сына Божия.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34834, 'rst66', 'jhn', 3, 17, 'Ибо не послал Бог Сына Своего в мир, чтобы судить мир, но чтобы мир спасен был чрез Него.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34833, 'rst66', 'jhn', 3, 16, 'Ибо так возлюбил Бог мир, что отдал Сына Своего Единородного, дабы всякий, верующий в Него, не погиб, но имел жизнь вечную.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34832, 'rst66', 'jhn', 3, 15, 'дабы всякий, верующий в Него, не погиб, но имел жизнь вечную.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34831, 'rst66', 'jhn', 3, 14, 'И как Моисей вознес змию в пустыне, так должно вознесену быть Сыну Человеческому,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34830, 'rst66', 'jhn', 3, 13, 'Никто не восходил на небо, как только сшедший с небес Сын Человеческий, сущий на небесах.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34829, 'rst66', 'jhn', 3, 12, 'Если Я сказал вам о земном, и вы не верите, &mdash; как поверите, если буду говорить вам о небесном?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34828, 'rst66', 'jhn', 3, 11, 'Истинно, истинно говорю тебе: Мы говорим о том, что знаем, и свидетельствуем о том, что видели, а вы свидетельства Нашего не принимаете.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34827, 'rst66', 'jhn', 3, 10, 'Иисус отвечал и сказал ему: ты &mdash; учитель Израилев, и этого ли не знаешь?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34826, 'rst66', 'jhn', 3, 9, 'Никодим сказал Ему в ответ: как это может быть?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34825, 'rst66', 'jhn', 3, 8, 'Дух дышит, где хочет, и голос его слышишь, а не знаешь, откуда приходит и куда уходит: так бывает со всяким, рожденным от Духа.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34824, 'rst66', 'jhn', 3, 7, 'Не удивляйся тому, что Я сказал тебе: «должно вам родиться свыше».');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34823, 'rst66', 'jhn', 3, 6, 'Рожденное от плоти есть плоть, а рожденное от Духа есть дух.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34822, 'rst66', 'jhn', 3, 5, 'Иисус отвечал: истинно, истинно говорю тебе, если кто не родится от воды и Духа, не может войти в Царствие Божие.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34821, 'rst66', 'jhn', 3, 4, 'Никодим говорит Ему: как может человек родиться, будучи стар? неужели может он в другой раз войти в утробу матери своей и родиться?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34820, 'rst66', 'jhn', 3, 3, 'Иисус сказал ему в ответ: истинно, истинно говорю тебе, если кто не родится свыше, не может увидеть Царствия Божия.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34819, 'rst66', 'jhn', 3, 2, 'Он пришел к Иисусу ночью и сказал Ему: Равви&#769;! мы знаем, что Ты &mdash; учитель, пришедший от Бога; ибо таких чудес, какие Ты творишь, никто не может творить, если не будет с ним Бог.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34818, 'rst66', 'jhn', 3, 1, 'Между фарисеями был некто, именем Никодим, <i>один</i> из начальников Иудейских.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34817, 'rst66', 'jhn', 2, 25, 'и не имел нужды, чтобы кто засвидетельствовал о человеке, ибо Сам знал, что&#769; в человеке.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34816, 'rst66', 'jhn', 2, 24, 'Но Сам Иисус не вверял Себя им, потому что знал всех');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34815, 'rst66', 'jhn', 2, 23, 'И когда Он был в Иерусалиме на празднике Пасхи, то многие, видя чудеса, которые Он творил, уверовали во имя Его.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34814, 'rst66', 'jhn', 2, 22, 'Когда же воскрес Он из мертвых, то ученики Его вспомнили, что Он говорил это, и поверили Писанию и слову, которое сказал Иисус.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34813, 'rst66', 'jhn', 2, 21, 'А Он говорил о храме тела Своего.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34812, 'rst66', 'jhn', 2, 20, 'На это сказали Иудеи: сей храм строился сорок шесть лет, и Ты в три дня воздвигнешь его?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34811, 'rst66', 'jhn', 2, 19, 'Иисус сказал им в ответ: разрушьте храм сей, и Я в три дня воздвигну его.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34810, 'rst66', 'jhn', 2, 18, 'На это Иудеи сказали: каким знамением докажешь Ты нам, что <i>имеешь власть</i> так поступать?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34809, 'rst66', 'jhn', 2, 17, 'При сем ученики Его вспомнили, что написано: «ревность по доме Твоем снедает Меня».');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34808, 'rst66', 'jhn', 2, 16, 'И сказал продающим голубей: возьмите это отсюда и до&#769;ма Отца Моего не делайте домом торговли.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34807, 'rst66', 'jhn', 2, 15, 'И, сделав бич из веревок, выгнал из храма всех, <i>также</i> и овец и волов; и деньги у меновщиков рассыпал, а столы их опрокинул.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34806, 'rst66', 'jhn', 2, 14, 'и нашел, что в храме продавали волов, овец и голубей, и сидели меновщики денег.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34805, 'rst66', 'jhn', 2, 13, 'Приближалась Пасха Иудейская, и Иисус пришел в Иерусалим');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34804, 'rst66', 'jhn', 2, 12, 'После сего пришел Он в Капернаум, Сам и Матерь Его, и братья Его, и ученики Его; и там пробыли немного дней.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34803, 'rst66', 'jhn', 2, 11, 'Так положил Иисус начало чудесам в Кане Галилейской и явил славу Свою; и уверовали в Него ученики Его.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34802, 'rst66', 'jhn', 2, 10, 'и говорит ему: всякий человек подает сперва хорошее вино, а когда напьются, тогда худшее; а ты хорошее вино сберег доселе.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34801, 'rst66', 'jhn', 2, 9, 'Когда же распорядитель отведал воды, сделавшейся вином, &mdash; а он не знал, откуда <i>это вино</i>, знали только служители, почерпавшие воду, &mdash; тогда распорядитель зовет жениха');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34800, 'rst66', 'jhn', 2, 8, 'И говорит им: теперь почерпните и несите к распорядителю пира. И понесли.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34799, 'rst66', 'jhn', 2, 7, 'Иисус говорит им: наполните сосуды водою. И наполнили их доверху.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34798, 'rst66', 'jhn', 2, 6, 'Было же тут шесть каменных водоносов, стоявших <i>по обычаю</i> очищения Иудейского, вмещавших по две или по три меры.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34797, 'rst66', 'jhn', 2, 5, 'Матерь Его сказала служителям: что скажет Он вам, то сделайте.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34796, 'rst66', 'jhn', 2, 4, 'Иисус говорит Ей: что&#769; Мне и Тебе, Же&#769;но? еще не пришел час Мой.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34795, 'rst66', 'jhn', 2, 3, 'И как недоставало вина, то Матерь Иисуса говорит Ему: вина нет у них.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34794, 'rst66', 'jhn', 2, 2, 'Был также зван Иисус и ученики Его на брак.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34793, 'rst66', 'jhn', 2, 1, 'На третий день был брак в Кане Галилейской, и Матерь Иисуса была там.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34792, 'rst66', 'jhn', 1, 51, 'И говорит ему: истинно, истинно говорю вам: отныне будете видеть небо отверстым и Ангелов Божиих восходящих и нисходящих к Сыну Человеческому.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34791, 'rst66', 'jhn', 1, 50, 'Иисус сказал ему в ответ: ты веришь, потому что Я тебе сказал: «Я видел тебя под смоковницею»; увидишь больше сего.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34790, 'rst66', 'jhn', 1, 49, 'Нафанаил отвечал Ему: Равви&#769;! Ты &mdash; Сын Божий, Ты &mdash; Царь Израилев.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34789, 'rst66', 'jhn', 1, 48, 'Нафанаил говорит Ему: почему Ты знаешь меня? Иисус сказал ему в ответ: прежде нежели позвал тебя Филипп, когда ты был под смоковницею, Я видел тебя.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34788, 'rst66', 'jhn', 1, 47, 'Иисус, увидев идущего к Нему Нафанаила, говорит о нем: вот подлинно Израильтянин, в котором нет лукавства.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34787, 'rst66', 'jhn', 1, 46, 'Но Нафанаил сказал ему: из Назарета может ли быть что доброе? Филипп говорит ему: пойди и посмотри.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34786, 'rst66', 'jhn', 1, 45, 'Филипп находит Нафанаила и говорит ему: мы нашли Того, о Котором писали Моисей в законе и пророки, Иисуса, сына Иосифова, из Назарета.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34785, 'rst66', 'jhn', 1, 44, 'Филипп же был из Вифсаиды, из <i>одного</i> города с Андреем и Петром.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34784, 'rst66', 'jhn', 1, 43, 'На другой день <i>Иисус</i> восхотел идти в Галилею, и находит Филиппа и говорит ему: иди за Мною.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34783, 'rst66', 'jhn', 1, 42, 'и привел его к Иисусу. Иисус же, взглянув на него, сказал: ты &mdash; Симон, сын Ионин; ты наречешься Кифа, что значит: «камень» (Петр).');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34782, 'rst66', 'jhn', 1, 41, 'Он первый находит брата своего Симона и говорит ему: мы нашли Мессию (что значит: «Христос»);');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34781, 'rst66', 'jhn', 1, 40, 'Один из двух, слышавших от Иоанна <i>об Иисусе</i> и последовавших за Ним, был Андрей, брат Симона Петра.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34780, 'rst66', 'jhn', 1, 39, 'Говорит им: пойдите и увидите. Они пошли и увидели, где Он живет; и пробыли у Него день тот. Было около десятого часа.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34779, 'rst66', 'jhn', 1, 38, 'Иисус же, обратившись и увидев их идущих, говорит им: что вам надобно? Они сказали Ему: Равви&#769; (что значит: «учитель»), где живешь?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34778, 'rst66', 'jhn', 1, 37, 'Услышав от него сии слова, оба ученика пошли за Иисусом.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34777, 'rst66', 'jhn', 1, 36, 'И, увидев идущего Иисуса, сказал: вот Агнец Божий.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34776, 'rst66', 'jhn', 1, 35, 'На другой день опять стоял Иоанн и двое из учеников его.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34775, 'rst66', 'jhn', 1, 34, 'И я видел и засвидетельствовал, что Сей есть Сын Божий.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34774, 'rst66', 'jhn', 1, 33, 'Я не знал Его; но Пославший меня крестить в воде сказал мне: «на Кого увидишь Духа сходящего и пребывающего на Нем, Тот есть крестящий Духом Святым».');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34773, 'rst66', 'jhn', 1, 32, 'И свидетельствовал Иоанн, говоря: я видел Духа, сходящего с неба, как голубя, и пребывающего на Нем.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34772, 'rst66', 'jhn', 1, 31, 'Я не знал Его; но для того пришел крестить в воде, чтобы Он явлен был Израилю.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34771, 'rst66', 'jhn', 1, 30, 'Сей есть, о Котором я сказал: «за мною идет Муж, Который стал впереди меня, потому что Он был прежде меня».');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34770, 'rst66', 'jhn', 1, 29, 'На другой день видит Иоанн идущего к нему Иисуса и говорит: вот Агнец Божий, Который берет <i>на Себя</i> грех мира.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34769, 'rst66', 'jhn', 1, 28, 'Это происходило в Вифаваре при Иордане, где крестил Иоанн.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34768, 'rst66', 'jhn', 1, 27, 'Он‐то Идущий за мною, но Который стал впереди меня. Я недостоин развязать ремень у обуви Его.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34767, 'rst66', 'jhn', 1, 26, 'Иоанн сказал им в ответ: я крещу в воде; но стоит среди вас <i>Некто</i>, Которого вы не знаете.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34766, 'rst66', 'jhn', 1, 25, 'и они спросили его: что же ты крестишь, если ты ни Христос, ни Илия, ни пророк?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34765, 'rst66', 'jhn', 1, 24, 'А посланные были из фарисеев;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34764, 'rst66', 'jhn', 1, 23, 'Он сказал: я глас вопиющего в пустыне: «исправьте путь Господу», как сказал пророк Исаия.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34763, 'rst66', 'jhn', 1, 22, 'Сказали ему: кто же ты? чтобы нам дать ответ пославшим нас: что&#769; ты скажешь о себе самом?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34762, 'rst66', 'jhn', 1, 21, 'И спросили его: что же? ты Илия? Он сказал: нет. Пророк? Он отвечал: нет.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34761, 'rst66', 'jhn', 1, 20, 'Он объявил, и не отрекся, и объявил, что я не Христос.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34760, 'rst66', 'jhn', 1, 19, 'И вот свидетельство Иоанна, когда Иудеи прислали из Иерусалима священников и левитов спросить его: кто ты?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34759, 'rst66', 'jhn', 1, 18, 'Бога не видел никто никогда; Единородный Сын, сущий в недре Отчем, Он явил.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34758, 'rst66', 'jhn', 1, 17, 'ибо закон дан чрез Моисея; благодать же и истина произошли чрез Иисуса Христа.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34757, 'rst66', 'jhn', 1, 16, 'И от полноты Его все мы приняли и благодать на благодать,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34756, 'rst66', 'jhn', 1, 15, 'Иоанн свидетельствует о Нем и, восклицая, говорит: Сей был Тот, о Котором я сказал, что Идущий за мною стал впереди меня, потому что был прежде меня.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34755, 'rst66', 'jhn', 1, 14, 'И Слово стало плотию и обитало с нами, полное благодати и истины; и мы видели славу Его, славу, как Единородного от Отца.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34754, 'rst66', 'jhn', 1, 13, 'которые ни от крови, ни от хотения плоти, ни от хотения мужа, но от Бога родились.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34753, 'rst66', 'jhn', 1, 12, 'А тем, которые приняли Его, верующим во имя Его, дал власть быть чадами Божиими,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34752, 'rst66', 'jhn', 1, 11, 'Пришел к своим, и свои Его не приняли.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34751, 'rst66', 'jhn', 1, 10, 'В мире был, и мир чрез Него на&#769;чал быть, и мир Его не познал.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34750, 'rst66', 'jhn', 1, 9, 'Был Свет истинный, Который просвещает всякого человека, приходящего в мир.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34749, 'rst66', 'jhn', 1, 8, 'Он не был свет, но <i>был послан</i>, чтобы свидетельствовать о Свете.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34748, 'rst66', 'jhn', 1, 7, 'Он пришел для свидетельства, чтобы свидетельствовать о Свете, дабы все уверовали чрез него.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34747, 'rst66', 'jhn', 1, 6, 'Был человек, посланный от Бога; имя ему Иоанн.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34746, 'rst66', 'jhn', 1, 5, 'И свет во тьме светит, и тьма не объяла его.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34745, 'rst66', 'jhn', 1, 4, 'В Нем была жизнь, и жизнь была свет человеков.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34744, 'rst66', 'jhn', 1, 3, 'Все чрез Него на&#769;чало быть, и без Него ничто не на&#769;чало быть, что на&#769;чало быть.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34743, 'rst66', 'jhn', 1, 2, 'Оно было в начале у Бога.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34742, 'rst66', 'jhn', 1, 1, 'В начале было Слово, и Слово было у Бога, и Слово было Бог.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34741, 'rst66', 'luk', 24, 53, 'И пребывали всегда в храме, прославляя и благословляя Бога. Аминь.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34740, 'rst66', 'luk', 24, 52, 'Они поклонились Ему и возвратились в Иерусалим с великою радостью.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34739, 'rst66', 'luk', 24, 51, 'И, когда благословлял их, стал отдаляться от них и возноситься на небо.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34738, 'rst66', 'luk', 24, 50, 'И вывел их вон <i>из города</i> до Вифании и, подняв руки Свои, благословил их.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34737, 'rst66', 'luk', 24, 49, 'И Я пошлю обетование Отца Моего на вас; вы же оставайтесь в городе Иерусалиме, доколе не облечетесь силою свыше.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34736, 'rst66', 'luk', 24, 48, 'Вы же свидетели сему.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34735, 'rst66', 'luk', 24, 47, 'и проповедану быть во имя Его покаянию и прощению грехов во всех народах, начиная с Иерусалима.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34734, 'rst66', 'luk', 24, 46, 'И сказал им: так написано, и так надлежало пострадать Христу, и воскреснуть из мертвых в третий день,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34733, 'rst66', 'luk', 24, 45, 'Тогда отверз им ум к уразумению Писаний.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34732, 'rst66', 'luk', 24, 44, 'И сказал им: вот то, о чем Я вам говорил, еще быв с вами, что надлежит исполниться всему, написанному о Мне в законе Моисеевом и в пророках и псалмах.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34731, 'rst66', 'luk', 24, 43, 'И, взяв, ел пред ними.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34730, 'rst66', 'luk', 24, 42, 'Они подали Ему часть печеной рыбы и сотового меда.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34729, 'rst66', 'luk', 24, 41, 'Когда же они от радости еще не верили и дивились, Он сказал им: есть ли у вас здесь какая пища?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34728, 'rst66', 'luk', 24, 40, 'И, сказав это, показал им руки и ноги.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34727, 'rst66', 'luk', 24, 39, 'Посмотрите на руки Мои и на ноги Мои; это Я Сам; осяжите Меня и рассмотри&#769;те; ибо дух плоти и костей не имеет, как видите у Меня.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34726, 'rst66', 'luk', 24, 38, 'Но Он сказал им: что смущаетесь, и для чего такие мысли входят в сердца ваши?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34725, 'rst66', 'luk', 24, 37, 'Они, смутившись и испугавшись, подумали, что видят духа.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34724, 'rst66', 'luk', 24, 36, 'Когда они говорили о сем, Сам Иисус стал посреди них и сказал им: мир вам.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34723, 'rst66', 'luk', 24, 35, 'И они рассказывали о происшедшем на пути, и как Он был узнан ими в преломлении хлеба.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34722, 'rst66', 'luk', 24, 34, 'которые говорили, что Господь истинно воскрес и явился Симону.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34721, 'rst66', 'luk', 24, 33, 'И, встав в тот же час, возвратились в Иерусалим и нашли вместе одиннадцать <i>Апостолов</i> и бывших с ними,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34720, 'rst66', 'luk', 24, 32, 'И они сказали друг другу: не горело ли в нас сердце наше, когда Он говорил нам на дороге и когда изъяснял нам Писание?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34719, 'rst66', 'luk', 24, 31, 'Тогда открылись у них глаза, и они узнали Его. Но Он стал невидим для них.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34718, 'rst66', 'luk', 24, 30, 'И когда Он возлежал с ними, то, взяв хлеб, благословил, преломил и подал им.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34717, 'rst66', 'luk', 24, 29, 'Но они удерживали Его, говоря: останься с нами, потому что день уже склонился к вечеру. И Он вошел и остался с ними.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34716, 'rst66', 'luk', 24, 28, 'И приблизились они к тому селению, в которое шли; и Он показывал им вид, что хочет идти далее.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34715, 'rst66', 'luk', 24, 27, 'И, начав от Моисея, из всех пророков изъяснял им сказанное о Нем во всем Писании.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34714, 'rst66', 'luk', 24, 26, 'Не так ли надлежало пострадать Христу и войти в славу Свою?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34713, 'rst66', 'luk', 24, 25, 'Тогда Он сказал им: о, несмысленные и медлительные сердцем, чтобы веровать всему, что предсказывали пророки!');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34712, 'rst66', 'luk', 24, 24, 'И пошли некоторые из наших ко гробу и нашли так, как и женщины говорили, но Его не видели.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34711, 'rst66', 'luk', 24, 23, 'и не нашли тела Его и, придя, сказывали, что они видели и явление Ангелов, которые говорят, что Он жив.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34710, 'rst66', 'luk', 24, 22, 'Но и некоторые женщины из наших изумили нас: они были рано у гроба');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34709, 'rst66', 'luk', 24, 21, 'А мы надеялись было, что Он есть Тот, Который должен избавить Израиля; но со всем тем, уже третий день ныне, как это произошло.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34708, 'rst66', 'luk', 24, 20, 'как предали Его первосвященники и начальники наши для осуждения на смерть и распяли Его.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34707, 'rst66', 'luk', 24, 19, 'И сказал им: о чем? Они сказали Ему: что было с Иисусом Назарянином, Который был пророк, сильный в деле и слове пред Богом и всем народом;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34706, 'rst66', 'luk', 24, 18, 'Один из них, именем Клеопа, сказал Ему в ответ: неужели Ты один из пришедших в Иерусалим не знаешь о происшедшем в нем в эти дни?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34705, 'rst66', 'luk', 24, 17, 'Он же сказал им: о чем это вы, идя, рассуждаете между собою, и отчего вы печальны?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34704, 'rst66', 'luk', 24, 16, 'Но глаза их были удержаны, так что они не узнали Его.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34703, 'rst66', 'luk', 24, 15, 'И когда они разговаривали и рассуждали между собою, и Сам Иисус, приблизившись, пошел с ними.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34702, 'rst66', 'luk', 24, 14, 'и разговаривали между собою о всех сих событиях.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34701, 'rst66', 'luk', 24, 13, 'В тот же день двое из них шли в селение, отстоящее стадий на шестьдесят от Иерусалима, называемое Эммаус;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34700, 'rst66', 'luk', 24, 12, 'Но Петр, встав, побежал ко гробу и, наклонившись, увидел только пелены лежащие, и пошел назад, дивясь сам в себе происшедшему.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34699, 'rst66', 'luk', 24, 11, 'И показались им слова их пустыми, и не поверили им.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34698, 'rst66', 'luk', 24, 10, 'То были Магдалина Мария, и Иоанна, и Мария, <i>мать</i> Иакова, и другие с ними, которые сказали о сем Апостолам.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34697, 'rst66', 'luk', 24, 9, 'и, возвратившись от гроба, возвестили всё это одиннадцати и всем прочим.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34696, 'rst66', 'luk', 24, 8, 'И вспомнили они слова Его;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34695, 'rst66', 'luk', 24, 7, 'сказывая, что Сыну Человеческому надлежит быть предану в руки человеков грешников, и быть распяту, и в третий день воскреснуть.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34694, 'rst66', 'luk', 24, 6, 'Его нет здесь: Он воскрес; вспомните, как Он говорил вам, когда был еще в Галилее,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34693, 'rst66', 'luk', 24, 5, 'И когда они были в страхе и наклонили лица <i>свои</i> к земле, сказали им: что вы ищете живого между мертвыми?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34692, 'rst66', 'luk', 24, 4, 'Когда же недоумевали они о сем, вдруг предстали перед ними два мужа в одеждах блистающих.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34691, 'rst66', 'luk', 24, 3, 'И, войдя, не нашли тела Господа Иисуса.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34690, 'rst66', 'luk', 24, 2, 'но нашли камень отваленным от гроба.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34689, 'rst66', 'luk', 24, 1, 'В первый же день недели, очень рано, неся приготовленные ароматы, пришли они ко гробу, и вместе с ними некоторые другие;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34688, 'rst66', 'luk', 23, 56, 'возвратившись же, приготовили благовония и масти; и в субботу остались в покое по заповеди.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34687, 'rst66', 'luk', 23, 55, 'Последовали также и женщины, пришедшие с Иисусом из Галилеи, и смотрели гроб и как полагалось тело Его;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34686, 'rst66', 'luk', 23, 54, 'День тот был пятница, и наступала суббота.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34685, 'rst66', 'luk', 23, 53, 'и, сняв его, обвил плащаницею и положил его в гробе, высеченном <i>в скале</i>, где еще никто не был положен.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34684, 'rst66', 'luk', 23, 52, 'пришел к Пилату и просил тела Иисусова;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34683, 'rst66', 'luk', 23, 51, 'не участвовавший в совете и в деле их, из Аримафеи, города Иудейского, ожидавший также Царствия Божия,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34682, 'rst66', 'luk', 23, 50, 'Тогда некто, именем Иосиф, член совета, человек добрый и правдивый,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34681, 'rst66', 'luk', 23, 49, 'Все же, знавшие Его, и женщины, следовавшие за Ним из Галилеи, стояли вдали и смотрели на это.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34680, 'rst66', 'luk', 23, 48, 'И весь народ, сшедшийся на сие зрелище, видя происходившее, возвращался, бия себя в грудь.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34679, 'rst66', 'luk', 23, 47, 'Сотник же, видев происходившее, прославил Бога и сказал: истинно человек этот был праведник.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34678, 'rst66', 'luk', 23, 46, 'Иисус, возгласив громким голосом, сказал: Отче! в руки Твои предаю дух Мой. И, сие сказав, испустил дух.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34677, 'rst66', 'luk', 23, 45, 'и померкло солнце, и завеса в храме раздралась по средине.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34676, 'rst66', 'luk', 23, 44, 'Было же около шестого часа дня, и сделалась тьма по всей земле до часа девятого:');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34675, 'rst66', 'luk', 23, 43, 'И сказал ему Иисус: истинно говорю тебе, ныне же будешь со Мною в раю.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34674, 'rst66', 'luk', 23, 42, 'И сказал Иисусу: помяни меня, Господи, когда приидешь в Царствие Твое!');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34673, 'rst66', 'luk', 23, 41, 'И мы <i>осуждены</i> справедливо, потому что достойное по делам нашим приняли, а Он ничего худого не сделал.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34672, 'rst66', 'luk', 23, 40, 'Другой же, напротив, унимал его и говорил: или ты не боишься Бога, когда и сам осужден на то же?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34671, 'rst66', 'luk', 23, 39, 'Один из повешенных злодеев злословил Его и говорил: если Ты Христос, спаси Себя и нас.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34670, 'rst66', 'luk', 23, 38, 'И была над Ним надпись, написанная словами греческими, римскими и еврейскими: «Сей есть Царь Иудейский».');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34669, 'rst66', 'luk', 23, 37, 'и говоря: если Ты Царь Иудейский, спаси Себя Самого.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34668, 'rst66', 'luk', 23, 36, 'Также и воины ругались над Ним, подходя и поднося Ему уксус');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34667, 'rst66', 'luk', 23, 35, 'И стоял народ, и смотрел. Насмехались же вместе с ними и начальники, говоря: других спасал; пусть спасет Себя Самого, если Он Христос, избранный Божий.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34666, 'rst66', 'luk', 23, 34, 'Иисус же говорил: Отче! прости им, ибо не знают, что делают. И делили одежды Его, бросая жребий.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34665, 'rst66', 'luk', 23, 33, 'И когда пришли на место, называемое Лобное, там распяли Его и злодеев, одного по правую, а другого по левую сторону.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34664, 'rst66', 'luk', 23, 32, 'Вели с Ним на смерть и двух злодеев.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34663, 'rst66', 'luk', 23, 31, 'Ибо если с зеленеющим деревом это делают, то с сухим что будет?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34662, 'rst66', 'luk', 23, 30, 'Тогда начнут говорить горам: «падите на нас!», и холмам: «покройте нас!»');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34661, 'rst66', 'luk', 23, 29, 'ибо приходят дни, в которые скажут: «блаженны неплодные, и утробы неродившие, и сосцы непитавшие!»');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34660, 'rst66', 'luk', 23, 28, 'Иисус же, обратившись к ним, сказал: дщери Иерусалимские! не плачьте обо Мне, но плачьте о себе и о детях ваших,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34659, 'rst66', 'luk', 23, 27, 'И шло за Ним великое множество народа и женщин, которые плакали и рыдали о Нем.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34658, 'rst66', 'luk', 23, 26, 'И когда повели Его, то, захватив некоего Симона Киринеянина, шедшего с поля, возложили на него крест, чтобы нес за Иисусом.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34657, 'rst66', 'luk', 23, 25, 'и отпустил им посаженного за возмущение и убийство в темницу, которого они просили; а Иисуса предал в их волю.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34656, 'rst66', 'luk', 23, 24, 'И Пилат решил быть по прошению их,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34655, 'rst66', 'luk', 23, 23, 'Но они продолжали с великим криком требовать, чтобы Он был распят; и превозмог крик их и первосвященников.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34654, 'rst66', 'luk', 23, 22, 'Он в третий раз сказал им: какое же зло сделал Он? я ничего, достойного смерти, не нашел в Нем; итак, наказав Его, отпущу.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34653, 'rst66', 'luk', 23, 21, 'Но они кричали: распни, распни Его!');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34652, 'rst66', 'luk', 23, 20, 'Пилат снова возвысил голос, желая отпустить Иисуса.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34651, 'rst66', 'luk', 23, 19, 'Варавва был посажен в темницу за произведенное в городе возмущение и убийство.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34650, 'rst66', 'luk', 23, 18, 'Но весь народ стал кричать: смерть Ему! а отпусти нам Варавву.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34649, 'rst66', 'luk', 23, 17, 'А ему и нужно было для праздника отпустить им одного <i>узника</i>.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34648, 'rst66', 'luk', 23, 16, 'итак, наказав Его, отпущу.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34647, 'rst66', 'luk', 23, 15, 'и Ирод также, ибо я посылал Его к нему; и ничего не найдено в Нем, достойного смерти;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34646, 'rst66', 'luk', 23, 14, 'сказал им: вы привели ко мне человека сего, как развращающего народ; и вот, я при вас исследовал и не нашел человека сего виновным ни в чем том, в чем вы обвиняете Его;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34645, 'rst66', 'luk', 23, 13, 'Пилат же, созвав первосвященников и начальников и народ,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34644, 'rst66', 'luk', 23, 12, 'И сделались в тот день Пилат и Ирод друзьями между собою, ибо прежде были во вражде друг с другом.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34643, 'rst66', 'luk', 23, 11, 'Но Ирод со своими воинами, уничижив Его и насмеявшись над Ним, одел Его в светлую одежду и отослал обратно к Пилату.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34642, 'rst66', 'luk', 23, 10, 'Первосвященники же и книжники стояли и усильно обвиняли Его.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34641, 'rst66', 'luk', 23, 9, 'и предлагал Ему многие вопросы, но Он ничего не отвечал ему.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34640, 'rst66', 'luk', 23, 8, 'Ирод, увидев Иисуса, очень обрадовался, ибо давно желал видеть Его, потому что много слышал о Нем, и надеялся увидеть от Него какое‐нибудь чудо,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34639, 'rst66', 'luk', 23, 7, 'И, узнав, что Он из области Иродовой, послал Его к Ироду, который в эти дни был также в Иерусалиме.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34638, 'rst66', 'luk', 23, 6, 'Пилат, услышав о Галилее, спросил: разве Он Галилеянин?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34637, 'rst66', 'luk', 23, 5, 'Но они настаивали, говоря, что Он возмущает народ, уча по всей Иудее, начиная от Галилеи до сего места.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34636, 'rst66', 'luk', 23, 4, 'Пилат сказал первосвященникам и народу: я не нахожу никакой вины в этом человеке.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34635, 'rst66', 'luk', 23, 3, 'Пилат спросил Его: Ты Царь Иудейский? Он сказал ему в ответ: ты говоришь.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34634, 'rst66', 'luk', 23, 2, 'и начали обвинять Его, говоря: мы нашли, что Он развращает народ наш и запрещает давать подать кесарю, называя Себя Христом Царем.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34633, 'rst66', 'luk', 23, 1, 'И поднялось все множество их, и повели Его к Пилату,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34632, 'rst66', 'luk', 22, 71, 'Они же сказали: какое еще нужно нам свидетельство? ибо мы сами слышали из уст Его.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34631, 'rst66', 'luk', 22, 70, 'И сказали все: итак, Ты Сын Божий? Он отвечал им: вы говорите, что Я.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34630, 'rst66', 'luk', 22, 69, 'отныне Сын Человеческий воссядет одесную силы Божией.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34629, 'rst66', 'luk', 22, 68, 'если же и спрошу вас, не будете отвечать Мне и не отпустите <i>Меня</i>;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34628, 'rst66', 'luk', 22, 67, 'и сказали: Ты ли Христос? скажи нам. Он сказал им: если скажу вам, вы не поверите;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34627, 'rst66', 'luk', 22, 66, 'И как настал день, собрались старейшины народа, первосвященники и книжники, и ввели Его в свой синедрион,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34626, 'rst66', 'luk', 22, 65, 'И много иных хулений произносили против Него.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34625, 'rst66', 'luk', 22, 64, 'и, закрыв Его, ударяли Его по лицу и спрашивали Его: прореки, кто ударил Тебя?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34624, 'rst66', 'luk', 22, 63, 'Люди, державшие Иисуса, ругались над Ним и били Его;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34623, 'rst66', 'luk', 22, 62, 'И, выйдя вон, горько заплакал.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34622, 'rst66', 'luk', 22, 61, 'Тогда Господь, обратившись, взглянул на Петра, и Петр вспомнил слово Господа, как Он сказал ему: «прежде нежели пропоет петух, отречешься от Меня трижды».');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34621, 'rst66', 'luk', 22, 60, 'Но Петр сказал тому человеку: не знаю, что ты говоришь. И тотчас, когда еще говорил он, запел петух.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34620, 'rst66', 'luk', 22, 59, 'Прошло с час времени, еще некто настоятельно говорил: точно и этот был с Ним, ибо он Галилеянин.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34619, 'rst66', 'luk', 22, 58, 'Вскоре потом другой, увидев его, сказал: и ты из них. Но Петр сказал этому человеку: нет!');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34618, 'rst66', 'luk', 22, 57, 'Но он отрекся от Него, сказав женщине: я не знаю Его.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34617, 'rst66', 'luk', 22, 56, 'Одна служанка, увидев его сидящего у огня и всмотревшись в него, сказала: и этот был с Ним.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34616, 'rst66', 'luk', 22, 55, 'Когда они развели огонь среди двора и сели вместе, сел и Петр между ними.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34615, 'rst66', 'luk', 22, 54, 'Взяв Его, повели и привели в дом первосвященника. Петр же следовал издали.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34614, 'rst66', 'luk', 22, 53, 'Каждый день бывал Я с вами в храме, и вы не поднимали на Меня рук, но теперь ваше время и власть тьмы.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34613, 'rst66', 'luk', 22, 52, 'Первосвященникам же и начальникам храма и старейшинам, собравшимся против Него, сказал Иисус: как будто на разбойника вышли вы с мечами и кольями, чтобы взять Меня.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34612, 'rst66', 'luk', 22, 51, 'Тогда Иисус сказал: оставьте, довольно. И, коснувшись уха его, исцелил его.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34611, 'rst66', 'luk', 22, 50, 'И один из них ударил раба первосвященникова, и отсек ему правое ухо.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34610, 'rst66', 'luk', 22, 49, 'Бывшие же с Ним, видя, к чему идет дело, сказали Ему: Господи! не ударить ли нам мечом?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34609, 'rst66', 'luk', 22, 48, 'Иисус же сказал ему: Иуда! целованием ли предаешь Сына Человеческого?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34608, 'rst66', 'luk', 22, 47, 'Когда Он еще говорил это, появился народ, а впереди его шел один из Двенадцати, называемый Иуда, и он подошел к Иисусу, чтобы поцеловать Его. Ибо он такой им дал знак: Кого я поцелую, Тот и есть.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34607, 'rst66', 'luk', 22, 46, 'и сказал им: что вы спите? встаньте и молитесь, чтобы не впасть в искушение.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34606, 'rst66', 'luk', 22, 45, 'Встав от молитвы, Он пришел к ученикам, и нашел их спящими от печали,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34605, 'rst66', 'luk', 22, 44, 'И, находясь в борении, прилежнее молился, и был пот Его, как капли крови, падающие на землю.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34604, 'rst66', 'luk', 22, 43, 'Явился же Ему Ангел с небес и укреплял Его.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34603, 'rst66', 'luk', 22, 42, 'говоря: Отче! о, если бы Ты благоволил пронести чашу сию мимо Меня! впрочем, не Моя воля, но Твоя да будет.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34602, 'rst66', 'luk', 22, 41, 'И Сам отошел от них на вержение камня, и, преклонив колени, молился,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34601, 'rst66', 'luk', 22, 40, 'Придя же на место, сказал им: молитесь, чтобы не впасть в искушение.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34600, 'rst66', 'luk', 22, 39, 'И, выйдя, пошел по обыкновению на гору Елеонскую, за Ним последовали и ученики Его.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34599, 'rst66', 'luk', 22, 38, 'Они сказали: Господи! вот, здесь два меча. Он сказал им: довольно.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34598, 'rst66', 'luk', 22, 37, 'ибо сказываю вам, что должно исполниться на Мне и сему написанному: «и к злодеям причтен». Ибо то, что о Мне, приходит к концу.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34597, 'rst66', 'luk', 22, 36, 'Тогда Он сказал им: но теперь, кто имеет мешок, тот возьми его, также и суму&#769;; а у кого нет, продай одежду свою и купи меч;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34596, 'rst66', 'luk', 22, 35, 'И сказал им: когда Я посылал вас без мешка и без сумы&#769; и без обуви, имели ли вы в чем недостаток? Они отвечали: ни в чем.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34595, 'rst66', 'luk', 22, 34, 'Но Он сказал: говорю тебе, Петр, не пропоет петух сегодня, как ты трижды отречешься, что не знаешь Меня.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34594, 'rst66', 'luk', 22, 33, 'Он отвечал Ему: Господи! с Тобою я готов и в темницу и на смерть идти.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34593, 'rst66', 'luk', 22, 32, 'но Я молился о тебе, чтобы не оскудела вера твоя; и ты некогда, обратившись, утверди братьев твоих.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34592, 'rst66', 'luk', 22, 31, 'И сказал Господь: Симон! Симон! се, сатана просил, чтобы сеять вас как пшеницу,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34591, 'rst66', 'luk', 22, 30, 'да ядите и пиете за трапезою Моею в Царстве Моем, и сядете на престолах судить двенадцать колен Израилевых.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34590, 'rst66', 'luk', 22, 29, 'и Я завещаваю вам, как завещал Мне Отец Мой, Царство,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34589, 'rst66', 'luk', 22, 28, 'Но вы пребыли со Мною в напастях Моих,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34588, 'rst66', 'luk', 22, 27, 'Ибо кто больше: возлежащий или служащий? не возлежащий ли? А Я посреди вас, как служащий.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34587, 'rst66', 'luk', 22, 26, 'а вы не так: но кто из вас больше, будь как меньший, и начальствующий &mdash; как служащий.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34586, 'rst66', 'luk', 22, 25, 'Он же сказал им: цари господствуют над народами, и владеющие ими благодетелями называются,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34585, 'rst66', 'luk', 22, 24, 'Был же и спор между ними, кто из них должен почитаться бо&#769;льшим.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34584, 'rst66', 'luk', 22, 23, 'И они начали спрашивать друг друга, кто бы из них был, который это сделает.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34583, 'rst66', 'luk', 22, 22, 'впрочем, Сын Человеческий идет по предназначению, но горе тому человеку, которым Он предается.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34582, 'rst66', 'luk', 22, 21, 'И вот, рука предающего Меня со Мною за столом;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34581, 'rst66', 'luk', 22, 20, 'Также и чашу после вечери, говоря: сия чаша <i>есть</i> новый завет в Моей Крови, которая за вас проливается.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34580, 'rst66', 'luk', 22, 19, 'И, взяв хлеб и благодарив, преломил и подал им, говоря: сие есть Тело Мое, которое за вас предается; сие творите в Мое воспоминание.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34579, 'rst66', 'luk', 22, 18, 'ибо сказываю вам, что не буду пить от плода виноградного, доколе не придет Царствие Божие.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34578, 'rst66', 'luk', 22, 17, 'И, взяв чашу и благодарив, сказал: приимите ее и разделите между собою,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34577, 'rst66', 'luk', 22, 16, 'ибо сказываю вам, что уже не буду есть ее, пока она не совершится в Царствии Божием.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34576, 'rst66', 'luk', 22, 15, 'и сказал им: очень желал Я есть с вами сию пасху прежде Моего страдания,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34575, 'rst66', 'luk', 22, 14, 'И, когда настал час, Он возлег, и двенадцать Апостолов с Ним,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34574, 'rst66', 'luk', 22, 13, 'Они пошли, и нашли, как сказал им, и приготовили пасху.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34573, 'rst66', 'luk', 22, 12, 'И он покажет вам горницу большую устланную; там приготовьте.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34572, 'rst66', 'luk', 22, 11, 'и скажите хозяину дома: «Учитель говорит тебе: где комната, в которой бы Мне есть пасху с учениками Моими?»');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34571, 'rst66', 'luk', 22, 10, 'Он сказал им: вот, при входе вашем в город, встретится с вами человек, несущий кувшин воды; последуйте за ним в дом, в который войдет он,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34570, 'rst66', 'luk', 22, 9, 'Они же сказали Ему: где велишь нам приготовить?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34569, 'rst66', 'luk', 22, 8, 'и послал <i>Иисус</i> Петра и Иоанна, сказав: пойдите, приготовьте нам есть пасху.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34568, 'rst66', 'luk', 22, 7, 'Настал же день опресноков, в который надлежало закалать пасхального <i>агнца</i>,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34567, 'rst66', 'luk', 22, 6, 'и он обещал, и искал удобного времени, чтобы предать Его им не при народе.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34566, 'rst66', 'luk', 22, 5, 'Они обрадовались и согласились дать ему денег;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34565, 'rst66', 'luk', 22, 4, 'и он пошел, и говорил с первосвященниками и начальниками, как Его предать им.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34564, 'rst66', 'luk', 22, 3, 'Вошел же сатана в Иуду, прозванного Искариотом, одного из числа Двенадцати,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34563, 'rst66', 'luk', 22, 2, 'и искали первосвященники и книжники, как бы погубить Его, потому что боялись народа.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34562, 'rst66', 'luk', 22, 1, 'Приближался праздник опресноков, называемый Пасхою,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34561, 'rst66', 'luk', 21, 38, 'И весь народ с утра приходил к Нему в храм слушать Его.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34560, 'rst66', 'luk', 21, 37, 'Днем Он учил в храме, а ночи, выходя, проводил на горе, называемой Елеонскою.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34559, 'rst66', 'luk', 21, 36, 'итак, бодрствуйте на всякое время и молитесь, да сподобитесь избежать всех сих будущих <i>бедствий</i> и предстать пред Сына Человеческого.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34558, 'rst66', 'luk', 21, 35, 'ибо он, как сеть, найдет на всех живущих по всему лицу земному;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34557, 'rst66', 'luk', 21, 34, 'Смотрите же за собою, чтобы сердца ваши не отягчались объядением и пьянством и заботами житейскими, и чтобы день тот не постиг вас внезапно,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34556, 'rst66', 'luk', 21, 33, 'небо и земля прейдут, но слова Мои не прейдут.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34555, 'rst66', 'luk', 21, 32, 'Истинно говорю вам: не прейдет род сей, как все это будет;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34554, 'rst66', 'luk', 21, 31, 'Так, и когда вы увидите то сбывающимся, знайте, что близко Царствие Божие.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34553, 'rst66', 'luk', 21, 30, 'когда они уже распускаются, то, видя это, знаете сами, что уже близко лето.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34552, 'rst66', 'luk', 21, 29, 'И сказал им притчу: посмотрите на смоковницу и на все деревья:');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34551, 'rst66', 'luk', 21, 28, 'Когда же начнет это сбываться, тогда восклонитесь и поднимите головы ваши, потому что приближается избавление ваше.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34550, 'rst66', 'luk', 21, 27, 'и тогда увидят Сына Человеческого, грядущего на облаке с силою и славою великою.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34549, 'rst66', 'luk', 21, 26, 'люди будут издыхать от страха и ожидания <i>бедствий</i>, грядущих на вселенную, ибо силы небесные поколеблются,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34548, 'rst66', 'luk', 21, 25, 'И будут знамения в солнце и луне и звездах, а на земле уныние народов и недоумение; и море восшумит и возмутится;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34547, 'rst66', 'luk', 21, 24, 'и падут от острия меча, и отведутся в плен во все народы; и Иерусалим будет попираем язычниками, доколе не окончатся времена язычников.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34546, 'rst66', 'luk', 21, 23, 'Горе же беременным и питающим сосцами в те дни; ибо великое будет бедствие на земле и гнев на народ сей:');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34545, 'rst66', 'luk', 21, 22, 'потому что это дни отмщения, да исполнится все написанное.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34544, 'rst66', 'luk', 21, 21, 'тогда находящиеся в Иудее да бегут в горы; и кто в городе, выходи из него; и кто в окрестностях, не входи в него,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34543, 'rst66', 'luk', 21, 20, 'Когда же увидите Иерусалим, окруженный войсками, тогда знайте, что приблизилось запустение его:');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34542, 'rst66', 'luk', 21, 19, 'терпением вашим спасайте души ваши.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34541, 'rst66', 'luk', 21, 18, 'но и волос с головы вашей не пропадет, &mdash;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34540, 'rst66', 'luk', 21, 17, 'и будете ненавидимы всеми за имя Мое,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34539, 'rst66', 'luk', 21, 16, 'Преданы также будете и родителями, и братьями, и родственниками, и друзьями, и некоторых из вас умертвят;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34538, 'rst66', 'luk', 21, 15, 'ибо Я дам вам уста и премудрость, которой не возмогут противоречить, ни противостоять все, противящиеся вам.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34537, 'rst66', 'luk', 21, 14, 'Итак, положите себе на сердце не обдумывать заранее, что отвечать,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34536, 'rst66', 'luk', 21, 13, 'будет же это вам для свидетельства.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34535, 'rst66', 'luk', 21, 12, 'Прежде же всего того возложат на вас руки и будут гнать <i>вас</i>, предавая в синагоги и в темницы, и поведут пред царей и правителей за имя Мое;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34534, 'rst66', 'luk', 21, 11, 'будут большие землетрясения по местам, и глады, и моры, и ужасные явления, и великие знамения с неба.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34533, 'rst66', 'luk', 21, 10, 'Тогда сказал им: восстанет народ на народ, и царство на царство;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34532, 'rst66', 'luk', 21, 9, 'Когда же услышите о войнах и смятениях, не ужасайтесь, ибо этому надлежит быть прежде; но не тотчас конец.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34531, 'rst66', 'luk', 21, 8, 'Он сказал: берегитесь, чтобы вас не ввели в заблуждение, ибо многие придут под именем Моим, говоря, что это Я; и это время близко: не ходите вслед их.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34530, 'rst66', 'luk', 21, 7, 'И спросили Его: Учитель! когда же это будет? и какой признак, когда это должно произойти?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34529, 'rst66', 'luk', 21, 6, 'придут дни, в которые из того, что вы здесь видите, не останется камня на камне; все будет разрушено.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34528, 'rst66', 'luk', 21, 5, 'И когда некоторые говорили о храме, что он украшен дорогими камнями и вкладами, Он сказал:');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34527, 'rst66', 'luk', 21, 4, 'ибо все те от избытка своего положили в дар Богу, а она от скудости своей положила все пропитание свое, какое имела.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34526, 'rst66', 'luk', 21, 3, 'и сказал: истинно говорю вам, что эта бедная вдова больше всех положила;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34525, 'rst66', 'luk', 21, 2, 'увидел также и бедную вдову, положившую туда две лепты,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34524, 'rst66', 'luk', 21, 1, 'Взглянув же, Он увидел богатых, клавших дары свои в сокровищницу;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34523, 'rst66', 'luk', 20, 47, 'которые поядают до&#769;мы вдов и лицемерно долго молятся; они примут тем большее осуждение.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34522, 'rst66', 'luk', 20, 46, 'остерегайтесь книжников, которые любят ходить в длинных одеждах и любят приветствия в народных собраниях, председания в синагогах и предвозлежания на пиршествах,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34521, 'rst66', 'luk', 20, 45, 'И когда слушал весь народ, Он сказал ученикам Своим:');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34520, 'rst66', 'luk', 20, 44, 'Итак, Давид Господом называет Его; как же Он Сын ему?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34519, 'rst66', 'luk', 20, 43, 'доколе положу врагов Твоих в подножие ног Твоих»?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34518, 'rst66', 'luk', 20, 42, 'а сам Давид говорит в книге псалмов: «сказал Господь Господу моему: седи одесную Меня,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34517, 'rst66', 'luk', 20, 41, 'ка&#769;к говорят, что Христос есть Сын Давидов,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34516, 'rst66', 'luk', 20, 40, 'И уже не смели спрашивать Его ни о чем. Он же сказал им:');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34515, 'rst66', 'luk', 20, 39, 'На это некоторые из книжников сказали: Учитель! Ты хорошо сказал.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34514, 'rst66', 'luk', 20, 38, 'Бог же не есть <i>Бог</i> мертвых, но живых, ибо у Него все живы.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34513, 'rst66', 'luk', 20, 37, 'А что мертвые воскреснут, и Моисей показал при купине, когда назвал Господа Богом Авраама и Богом Исаака и Богом Иакова.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34512, 'rst66', 'luk', 20, 36, 'и умереть уже не могут, ибо они равны Ангелам и суть сыны Божии, будучи сынами воскресения.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34511, 'rst66', 'luk', 20, 35, 'а сподобившиеся достигнуть того века и воскресения из мертвых ни женятся, ни замуж не выходят,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34510, 'rst66', 'luk', 20, 34, 'Иисус сказал им в ответ: чада века сего женятся и выходят замуж;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34509, 'rst66', 'luk', 20, 33, 'итак, в воскресение которого из них будет она женою, ибо семеро имели ее женою?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34508, 'rst66', 'luk', 20, 32, 'после всех умерла и жена;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34507, 'rst66', 'luk', 20, 31, 'взял ее третий; также и все семеро, и умерли, не оставив детей;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34506, 'rst66', 'luk', 20, 30, 'взял ту жену второй, и тот умер бездетным;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34505, 'rst66', 'luk', 20, 29, 'Было семь братьев, первый, взяв жену, умер бездетным;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34504, 'rst66', 'luk', 20, 28, 'Учитель! Моисей написал нам, что если у кого умрет брат, имевший жену, и умрет бездетным, то брат его должен взять его жену и восставить семя брату своему.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34503, 'rst66', 'luk', 20, 27, 'Тогда пришли некоторые из саддукеев, отвергающих воскресение, и спросили Его:');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34502, 'rst66', 'luk', 20, 26, 'И не могли уловить Его в слове перед народом, и, удивившись ответу Его, замолчали.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34501, 'rst66', 'luk', 20, 25, 'Он сказал им: итак, отдавайте кесарево кесарю, а Божие Богу.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34500, 'rst66', 'luk', 20, 24, 'Покажите Мне динарий: чье на нем изображение и надпись? Они отвечали: кесаревы.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34499, 'rst66', 'luk', 20, 23, 'Он же, уразумев лукавство их, сказал им: что вы Меня искушаете?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34498, 'rst66', 'luk', 20, 22, 'позволительно ли нам давать подать кесарю, или нет?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34497, 'rst66', 'luk', 20, 21, 'И они спросили Его: Учитель! мы знаем, что Ты правдиво говоришь и учишь и не смотришь на лицо, но истинно пути Божию учишь;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34496, 'rst66', 'luk', 20, 20, 'И, наблюдая за Ним, подослали лукавых людей, которые, притворившись благочестивыми, уловили бы Его в каком‐либо слове, чтобы предать Его начальству и власти правителя.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34495, 'rst66', 'luk', 20, 19, 'И искали в это время первосвященники и книжники, чтобы наложить на Него руки, но побоялись народа, ибо поняли, что о них сказал Он эту притчу.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34494, 'rst66', 'luk', 20, 18, 'Всякий, кто упадет на тот камень, разобьется, а на кого он упадет, того раздавит.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34493, 'rst66', 'luk', 20, 17, 'Но Он, взглянув на них, сказал: что значит сие написанное: «камень, который отвергли строители, тот самый сделался главою угла»?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34492, 'rst66', 'luk', 20, 16, 'Придет и погубит виноградарей тех, и отдаст виноградник другим. Слышавшие же это сказали: да не будет!');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34491, 'rst66', 'luk', 20, 15, 'И, выведя его вон из виноградника, убили. Что же сделает с ними господин виноградника?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34490, 'rst66', 'luk', 20, 14, 'Но виноградари, увидев его, рассуждали между собою, говоря: «это наследник; пойдем, убьем его, и наследство его будет наше».');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34489, 'rst66', 'luk', 20, 13, 'Тогда сказал господин виноградника: «что мне делать? Пошлю сына моего возлюбленного; может быть, увидев его, постыдятся».');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34488, 'rst66', 'luk', 20, 12, 'И еще послал третьего; но они и того, изранив, выгнали.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34487, 'rst66', 'luk', 20, 11, 'Еще послал другого раба; но они и этого, прибив и обругав, отослали ни с чем.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34486, 'rst66', 'luk', 20, 10, 'и в свое время послал к виноградарям раба, чтобы они дали ему плодов из виноградника; но виноградари, прибив его, отослали ни с чем.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34485, 'rst66', 'luk', 20, 9, 'И начал Он говорить к народу притчу сию: один человек насадил виноградник и отдал его виноградарям, и отлучился на долгое время;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34484, 'rst66', 'luk', 20, 8, 'Иисус сказал им: и Я не скажу вам, какою властью это делаю.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34483, 'rst66', 'luk', 20, 7, 'И отвечали: не знаем откуда.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34482, 'rst66', 'luk', 20, 6, 'А если скажем: «от человеков», то весь народ побьет нас камнями, ибо он уверен, что Иоанн есть пророк.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34481, 'rst66', 'luk', 20, 5, 'Они же, рассуждая между собою, говорили: если скажем: «с небес», то скажет: «почему же вы не поверили ему?»');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34480, 'rst66', 'luk', 20, 4, 'крещение Иоанново с небес было, или от человеков?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34479, 'rst66', 'luk', 20, 3, 'Он сказал им в ответ: спрошу и Я вас об одном, и скажите Мне:');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34478, 'rst66', 'luk', 20, 2, 'и сказали Ему: скажи нам, какою властью Ты это делаешь, или кто дал Тебе власть сию?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34477, 'rst66', 'luk', 20, 1, 'В один из тех дней, когда Он учил народ в храме и благовествовал, приступили первосвященники и книжники со старейшинами,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34476, 'rst66', 'luk', 19, 48, 'и не находили, что бы сделать с Ним; потому что весь народ неотступно слушал Его.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34475, 'rst66', 'luk', 19, 47, 'И учил каждый день в храме. Первосвященники же и книжники и старейшины народа искали погубить Его,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34474, 'rst66', 'luk', 19, 46, 'говоря им: написано: «дом Мой есть дом молитвы», а вы сделали его вертепом разбойников.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34473, 'rst66', 'luk', 19, 45, 'И, войдя в храм, начал выгонять продающих в нем и покупающих,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34472, 'rst66', 'luk', 19, 44, 'и разорят тебя, и побьют детей твоих в тебе, и не оставят в тебе камня на камне за то&#769;, что ты не узнал времени посещения твоего.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34471, 'rst66', 'luk', 19, 43, 'ибо придут на тебя дни, когда враги твои обложат тебя окопами и окружат тебя, и стеснят тебя отовсюду,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34470, 'rst66', 'luk', 19, 42, 'и сказал: о, если бы и ты хотя в сей твой день узнал, что служит к миру твоему! но это сокрыто ныне от глаз твоих,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34469, 'rst66', 'luk', 19, 41, 'И когда приблизился к городу, то, смотря на него, заплакал о нем');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34468, 'rst66', 'luk', 19, 40, 'Но Он сказал им в ответ: сказываю вам, что если они умолкнут, то камни возопиют.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34467, 'rst66', 'luk', 19, 39, 'И некоторые фарисеи из среды народа сказали Ему: Учитель! запрети ученикам Твоим.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34466, 'rst66', 'luk', 19, 38, 'говоря: благословен Царь, грядущий во имя Господне! Мир на небесах и слава в вышних!');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34465, 'rst66', 'luk', 19, 37, 'А когда Он приблизился к спуску с горы Елеонской, всё множество учеников начало в радости велегласно славить Бога за все чудеса, какие видели они,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34464, 'rst66', 'luk', 19, 36, 'И, когда Он ехал, постилали одежды свои по дороге.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34463, 'rst66', 'luk', 19, 35, 'И привели его к Иисусу, и, накинув одежды свои на осленка, посадили на него Иисуса.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34462, 'rst66', 'luk', 19, 34, 'Они отвечали: он надобен Господу.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34461, 'rst66', 'luk', 19, 33, 'Когда же они отвязывали молодого осла, хозяева его сказали им: зачем отвязываете осленка?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34460, 'rst66', 'luk', 19, 32, 'Посланные пошли и нашли, как Он сказал им.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34459, 'rst66', 'luk', 19, 31, 'и если кто спросит вас: «зачем отвязываете?», скажите ему так: «он надобен Господу».');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34458, 'rst66', 'luk', 19, 30, 'сказав: пойдите в противолежащее селение; войдя в него, найдете молодого осла привязанного, на которого никто из людей никогда не садился; отвязав его, приведите;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34457, 'rst66', 'luk', 19, 29, 'И когда приблизился к Виффагии и Вифании, к горе, называемой Елеонскою, послал двух учеников Своих,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34456, 'rst66', 'luk', 19, 28, 'Сказав это, Он пошел далее, восходя в Иерусалим.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34455, 'rst66', 'luk', 19, 27, 'врагов же моих тех, которые не хотели, чтобы я царствовал над ними, приведите сюда и избейте предо мною».');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34454, 'rst66', 'luk', 19, 26, '«Сказываю вам, что всякому имеющему дано будет, а у неимеющего отнимется и то, что имеет;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34453, 'rst66', 'luk', 19, 25, 'И сказали ему: «господин! у него есть десять мин».');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34452, 'rst66', 'luk', 19, 24, 'И сказал предстоящим: «возьмите у него мину и дайте имеющему десять мин».');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34451, 'rst66', 'luk', 19, 23, 'для чего же ты не отдал серебра моего в оборот, чтобы я, придя, получил его с прибылью?»');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34450, 'rst66', 'luk', 19, 22, '<i>Господин</i> сказал ему: «твоими устами буду судить тебя, лукавый раб! ты знал, что я человек жестокий, беру, чего не клал, и жну, чего не сеял;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34449, 'rst66', 'luk', 19, 21, 'ибо я боялся тебя, потому что ты человек жестокий: берешь, чего не клал, и жнешь, чего не сеял».');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34448, 'rst66', 'luk', 19, 20, 'Пришел третий и сказал: «господин! вот твоя мина, которую я хранил, завернув в платок,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34447, 'rst66', 'luk', 19, 19, 'Сказал и этому: «и ты будь над пятью городами».');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34446, 'rst66', 'luk', 19, 18, 'Пришел второй и сказал: «господин! мина твоя принесла пять мин».');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34445, 'rst66', 'luk', 19, 17, 'И сказал ему: «хорошо, добрый раб! за то, что ты в малом был верен, возьми в управление десять городов».');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34444, 'rst66', 'luk', 19, 16, 'Пришел первый и сказал: «господин! мина твоя принесла десять мин».');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34443, 'rst66', 'luk', 19, 15, 'И когда возвратился, получив царство, велел призвать к себе рабов тех, которым дал серебро, чтобы узнать, кто что приобрел.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34442, 'rst66', 'luk', 19, 14, 'Но граждане ненавидели его и отправили вслед за ним посольство, сказав: «не хотим, чтобы он царствовал над нами».');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34441, 'rst66', 'luk', 19, 13, 'призвав же десять рабов своих, дал им десять мин и сказал им: «употребляйте их в оборот, пока я возвращусь».');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34440, 'rst66', 'luk', 19, 12, 'Итак сказал: некоторый человек высокого рода отправлялся в дальнюю страну, чтобы получить себе царство и возвратиться;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34439, 'rst66', 'luk', 19, 11, 'Когда же они слушали это, присовокупил притчу: ибо Он был близ Иерусалима, и они думали, что скоро должно открыться Царствие Божие.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34438, 'rst66', 'luk', 19, 10, 'ибо Сын Человеческий пришел взыскать и спасти погибшее.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34437, 'rst66', 'luk', 19, 9, 'Иисус сказал ему: ныне пришло спасение дому сему, потому что и он сын Авраама,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34436, 'rst66', 'luk', 19, 8, 'Закхей же, став, сказал Господу: Господи! половину имения моего я отдам нищим, и, если кого чем обидел, воздам вчетверо.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34435, 'rst66', 'luk', 19, 7, 'И все, видя то, начали роптать, и говорили, что Он зашел к грешному человеку;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34434, 'rst66', 'luk', 19, 6, 'И он поспешно сошел и принял Его с радостью.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34433, 'rst66', 'luk', 19, 5, 'Иисус, когда пришел на это место, взглянув, увидел его и сказал ему: Закхей! сойди скорее, ибо сегодня надобно Мне быть у тебя в доме.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34432, 'rst66', 'luk', 19, 4, 'и, забежав вперед, взлез на смоковницу, чтобы увидеть Его, потому что Ему надлежало проходить мимо нее.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34431, 'rst66', 'luk', 19, 3, 'искал видеть Иисуса, кто Он, но не мог за народом, потому что мал был ростом,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34430, 'rst66', 'luk', 19, 2, 'И вот, некто, именем Закхей, начальник мытарей и человек богатый,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34429, 'rst66', 'luk', 19, 1, 'Потом <i>Иисус</i> вошел в Иерихон и проходил через него.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34428, 'rst66', 'luk', 18, 43, 'И он тотчас прозрел и пошел за Ним, славя Бога; и весь народ, видя это, воздал хвалу Богу.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34427, 'rst66', 'luk', 18, 42, 'Иисус сказал ему: прозри! вера твоя спасла тебя.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34426, 'rst66', 'luk', 18, 41, 'чего ты хочешь от Меня? Он сказал: Господи! чтобы мне прозреть.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34425, 'rst66', 'luk', 18, 40, 'Иисус, остановившись, велел привести его к Себе: и, когда тот подошел к Нему, спросил его:');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34424, 'rst66', 'luk', 18, 39, 'Шедшие впереди заставляли его молчать; но он еще громче кричал: Сын Давидов! помилуй меня.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34423, 'rst66', 'luk', 18, 38, 'Тогда он закричал: Иисус, Сын Давидов! помилуй меня.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34422, 'rst66', 'luk', 18, 37, 'Ему сказали, что Иисус Назорей идет.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34421, 'rst66', 'luk', 18, 36, 'и, услышав, что мимо него проходит народ, спросил: что это такое?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34420, 'rst66', 'luk', 18, 35, 'Когда же подходил Он к Иерихону, один слепой сидел у дороги, прося милостыни,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34419, 'rst66', 'luk', 18, 34, 'Но они ничего из этого не поняли; слова сии были для них сокровенны, и они не разумели сказанного.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34418, 'rst66', 'luk', 18, 33, 'и будут бить, и убьют Его: и в третий день воскреснет.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34417, 'rst66', 'luk', 18, 32, 'ибо предадут Его язычникам, и поругаются над Ним, и оскорбят Его, и оплюют Его,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34416, 'rst66', 'luk', 18, 31, 'Отозвав же двенадцать учеников Своих, сказал им: вот, мы восходим в Иерусалим, и совершится все, написанное через пророков о Сыне Человеческом,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34415, 'rst66', 'luk', 18, 30, 'и не получил бы гораздо более в сие время, и в век будущий жизни вечной.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34414, 'rst66', 'luk', 18, 29, 'Он сказал им: истинно говорю вам: нет никого, кто оставил бы дом, или родителей, или братьев, или сестер, или жену, или детей для Царствия Божия,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34413, 'rst66', 'luk', 18, 28, 'Петр же сказал: вот, мы оставили все и последовали за Тобою.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34412, 'rst66', 'luk', 18, 27, 'Но Он сказал: невозможное человекам возможно Богу.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34411, 'rst66', 'luk', 18, 26, 'Слышавшие сие сказали: кто же может спастись?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34410, 'rst66', 'luk', 18, 25, 'Ибо удобнее верблюду пройти сквозь игольные уши, нежели богатому войти в Царствие Божие.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34409, 'rst66', 'luk', 18, 24, 'Иисус, видя, что он опечалился, сказал: как трудно имеющим богатство войти в Царствие Божие!');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34408, 'rst66', 'luk', 18, 23, 'Он же, услышав сие, опечалился, потому что был очень богат.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34407, 'rst66', 'luk', 18, 22, 'Услышав это, Иисус сказал ему: еще одного недостает тебе: все, что имеешь, продай и раздай нищим, и будешь иметь сокровище на небесах, и приходи, следуй за Мною.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34406, 'rst66', 'luk', 18, 21, 'Он же сказал: все это сохранил я от юности моей.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34405, 'rst66', 'luk', 18, 20, 'знаешь заповеди: «не прелюбодействуй», «не убивай», «не кради», «не лжесвидетельствуй», «почитай отца твоего и матерь твою».');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34404, 'rst66', 'luk', 18, 19, 'Иисус сказал ему: что ты называешь Меня благим? никто не благ, как только один Бог;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34403, 'rst66', 'luk', 18, 18, 'И спросил Его некто из начальствующих: Учитель благий! что мне делать, чтобы наследовать жизнь вечную?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34402, 'rst66', 'luk', 18, 17, 'Истинно говорю вам: кто не примет Царствия Божия, как дитя, тот не войдет в него.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34401, 'rst66', 'luk', 18, 16, 'Но Иисус, подозвав их, сказал: пустите детей приходить ко Мне и не возбраняйте им, ибо таковых есть Царствие Божие.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34400, 'rst66', 'luk', 18, 15, 'Приносили к Нему и младенцев, чтобы Он прикоснулся к ним; ученики же, видя то, возбраняли им.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34399, 'rst66', 'luk', 18, 14, 'Сказываю вам, что сей пошел оправданным в дом свой более, нежели тот: ибо всякий, возвышающий сам себя, унижен будет, а унижающий себя возвысится.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34398, 'rst66', 'luk', 18, 13, 'Мытарь же, стоя вдали, не смел даже поднять глаз на небо; но, ударяя себя в грудь, говорил: «Боже! будь милостив ко мне, грешнику!»');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34397, 'rst66', 'luk', 18, 12, 'пощусь два раза в неделю, даю десятую часть из всего, что приобретаю».');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34396, 'rst66', 'luk', 18, 11, 'Фарисей, став, молился сам в себе так: «Боже! благодарю Тебя, что я не таков, как прочие люди, грабители, обидчики, прелюбодеи, или как этот мытарь:');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34395, 'rst66', 'luk', 18, 10, 'два человека вошли в храм помолиться: один фарисей, а другой мытарь.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34394, 'rst66', 'luk', 18, 9, 'Сказал также к некоторым, которые уверены были о себе, что они праведны, и уничижали других, следующую притчу:');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34393, 'rst66', 'luk', 18, 8, 'Сказываю вам, что подаст им защиту вскоре. Но Сын Человеческий, придя, найдет ли веру на земле?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34392, 'rst66', 'luk', 18, 7, 'Бог ли не защитит избранных Своих, вопиющих к Нему день и ночь, хотя и медлит защищать их?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34391, 'rst66', 'luk', 18, 6, 'И сказал Господь: слышите, что говорит судья неправедный?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34390, 'rst66', 'luk', 18, 5, 'но, как эта вдова не дает мне покоя, защищу ее, чтобы она не приходила больше докучать мне».');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34389, 'rst66', 'luk', 18, 4, 'Но он долгое время не хотел. А после сказал сам в себе: «хотя я и Бога не боюсь и людей не стыжусь,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34388, 'rst66', 'luk', 18, 3, 'В том же городе была одна вдова, и она, приходя к нему, говорила: «защити меня от соперника моего».');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34387, 'rst66', 'luk', 18, 2, 'говоря: в одном городе был судья, который Бога не боялся и людей не стыдился.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34386, 'rst66', 'luk', 18, 1, 'Сказал также им притчу о том, что должно всегда молиться и не унывать,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34385, 'rst66', 'luk', 17, 37, 'На это сказали Ему: где, Господи? Он же сказал им: где труп, там соберутся и орлы.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34384, 'rst66', 'luk', 17, 36, 'двое будут на поле: один возьмется, а другой оставится.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34383, 'rst66', 'luk', 17, 35, 'две будут молоть вместе: одна возьмется, а другая оставится;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34382, 'rst66', 'luk', 17, 34, 'Сказываю вам: в ту ночь будут двое на одной постели: один возьмется, а другой оставится;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34381, 'rst66', 'luk', 17, 33, 'Кто станет сберегать душу свою, тот погубит ее; а кто погубит ее, тот оживит ее.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34380, 'rst66', 'luk', 17, 32, 'Вспоминайте жену Лотову.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34379, 'rst66', 'luk', 17, 31, 'В тот день, кто будет на кровле, а вещи его в доме, тот не сходи взять их; и кто будет на поле, также не обращайся назад.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34378, 'rst66', 'luk', 17, 30, 'так будет и в тот день, когда Сын Человеческий явится.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34377, 'rst66', 'luk', 17, 29, 'но в день, в который Лот вышел из Содома, пролился с неба дождь огненный и серный и истребил всех;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34376, 'rst66', 'luk', 17, 28, 'Та&#769;к же, ка&#769;к было и во дни Лота: ели, пили, покупали, продавали, садили, строили;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34375, 'rst66', 'luk', 17, 27, 'ели, пили, женились, выходили замуж, до того дня, как вошел Ной в ковчег, и пришел потоп и погубил всех.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34374, 'rst66', 'luk', 17, 26, 'И как было во дни Ноя, так будет и во дни Сына Человеческого:');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34373, 'rst66', 'luk', 17, 25, 'Но прежде надлежит Ему много пострадать и быть отвержену родом сим.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34372, 'rst66', 'luk', 17, 24, 'ибо, как молния, сверкнувшая от одного края неба, блистает до другого края неба, так будет Сын Человеческий в день Свой.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34371, 'rst66', 'luk', 17, 23, 'и скажут вам: «вот, здесь», или: «вот, там», &mdash; не ходи&#769;те и не гоняйтесь,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34370, 'rst66', 'luk', 17, 22, 'Сказал также ученикам: придут дни, когда пожелаете видеть хотя один из дней Сына Человеческого, и не увидите;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34369, 'rst66', 'luk', 17, 21, 'и не скажут: «вот, оно здесь», или: «вот, там». Ибо вот, Царствие Божие внутрь вас есть.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34368, 'rst66', 'luk', 17, 20, 'Быв же спрошен фарисеями, когда придет Царствие Божие, отвечал им: не придет Царствие Божие приметным образом,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34367, 'rst66', 'luk', 17, 19, 'И сказал ему: встань, иди; вера твоя спасла тебя.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34366, 'rst66', 'luk', 17, 18, 'как они не возвратились воздать славу Богу, кроме сего иноплеменника?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34365, 'rst66', 'luk', 17, 17, 'Тогда Иисус сказал: не десять ли очистились? где же девять?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34364, 'rst66', 'luk', 17, 16, 'и пал ниц к ногам Его, благодаря Его; и это был Самарянин.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34363, 'rst66', 'luk', 17, 15, 'Один же из них, видя, что исцелен, возвратился, громким голосом прославляя Бога,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34362, 'rst66', 'luk', 17, 14, 'Увидев <i>их</i>, Он сказал им: пойдите, покажитесь священникам. И когда они шли, очистились.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34361, 'rst66', 'luk', 17, 13, 'и громким голосом говорили: Иисус Наставник! помилуй нас.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34360, 'rst66', 'luk', 17, 12, 'И когда входил Он в одно селение, встретили Его десять человек прокаженных, которые остановились вдали');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34359, 'rst66', 'luk', 17, 11, 'Идя в Иерусалим, Он проходил между Самариею и Галилеею.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34358, 'rst66', 'luk', 17, 10, 'Так и вы, когда исполните всё повеленное вам, говорите: «мы рабы ничего не стоящие, потому что сделали, что&#769; должны были сделать».');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34357, 'rst66', 'luk', 17, 9, 'Станет ли он благодарить раба сего за то, что он исполнил приказание? не думаю.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34356, 'rst66', 'luk', 17, 8, 'Напротив, не скажет ли ему: «приготовь мне поужинать и, подпоясавшись, служи мне, пока буду есть и пить, и потом ешь и пей сам»?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34355, 'rst66', 'luk', 17, 7, 'Кто из вас, имея раба па&#769;шущего или пасущего, по возвращении его с поля, скажет ему: «пойди скорее, садись за стол»?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34354, 'rst66', 'luk', 17, 6, 'Господь сказал: если бы вы имели веру с зерно горчичное и сказали смоковнице сей: «исторгнись и пересадись в море», то она послушалась бы вас.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34353, 'rst66', 'luk', 17, 5, 'И сказали Апостолы Господу: умножь в нас веру.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34352, 'rst66', 'luk', 17, 4, 'и если семь раз в день согрешит против тебя и семь раз в день обратится, и скажет: «каюсь», &mdash; прости ему.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34351, 'rst66', 'luk', 17, 3, 'Наблюдайте за собою. Если же согрешит против тебя брат твой, выговори ему; и если покается, прости ему;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34350, 'rst66', 'luk', 17, 2, 'лучше было бы ему, если бы мельничный жернов повесили ему на шею и бросили его в море, нежели чтобы он соблазнил одного из малых сих.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34349, 'rst66', 'luk', 17, 1, 'Сказал также <i>Иисус</i> ученикам: невозможно не прийти соблазнам, но горе тому, через кого они приходят;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34348, 'rst66', 'luk', 16, 31, 'Тогда <i>Авраам</i> сказал ему: «если Моисея и пророков не слушают, то, если бы кто и из мертвых воскрес, не поверят».');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34347, 'rst66', 'luk', 16, 30, 'Он же сказал: «нет, отче Аврааме, но если кто из мертвых придет к ним, покаются».');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34346, 'rst66', 'luk', 16, 29, 'Авраам сказал ему: «у них есть Моисей и пророки; пусть слушают их».');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34345, 'rst66', 'luk', 16, 28, 'ибо у меня пять братьев; пусть он засвидетельствует им, чтобы и они не пришли в это место мучения».');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34344, 'rst66', 'luk', 16, 27, 'Тогда сказал он: «так прошу тебя, отче, пошли его в дом отца моего,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34343, 'rst66', 'luk', 16, 26, 'и сверх всего того между нами и вами утверждена великая пропасть, так что хотящие перейти отсюда к вам не могут, также и оттуда к нам не переходят».');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34342, 'rst66', 'luk', 16, 25, 'Но Авраам сказал: «чадо! вспомни, что ты получил уже доброе твое в жизни твоей, а Лазарь &mdash; злое; ныне же он здесь утешается, а ты страдаешь;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34341, 'rst66', 'luk', 16, 24, 'и, возопив, сказал: «отче Аврааме! умилосердись надо мною и пошли Лазаря, чтобы омочил конец перста своего в воде и прохладил язык мой, ибо я мучаюсь в пламени сем».');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34340, 'rst66', 'luk', 16, 23, 'И в аде, будучи в муках, он поднял глаза свои, увидел вдали Авраама и Лазаря на лоне его');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34339, 'rst66', 'luk', 16, 22, 'Умер нищий и отнесен был Ангелами на лоно Авраамово. Умер и богач, и похоронили его.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34338, 'rst66', 'luk', 16, 21, 'и желал напитаться крошками, падающими со стола богача, и псы, приходя, лизали струпья его.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34337, 'rst66', 'luk', 16, 20, 'Был также некоторый нищий, именем Лазарь, который лежал у ворот его в струпьях');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34336, 'rst66', 'luk', 16, 19, 'Некоторый человек был богат, одевался в порфиру и виссон и каждый день пиршествовал блистательно.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34335, 'rst66', 'luk', 16, 18, 'Всякий, разводящийся с женою своею и женящийся на другой, прелюбодействует, и всякий, женящийся на разведенной с мужем, прелюбодействует.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34334, 'rst66', 'luk', 16, 17, 'Но скорее небо и земля прейдут, нежели одна черта из закона пропадет.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34333, 'rst66', 'luk', 16, 16, 'Закон и пророки до Иоанна; с сего времени Царствие Божие благовествуется, и всякий усилием входит в него.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34332, 'rst66', 'luk', 16, 15, 'Он сказал им: вы выказываете себя праведниками пред людьми, но Бог знает сердца&#769; ваши, ибо что высоко у людей, то&#769; мерзость пред Богом.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34331, 'rst66', 'luk', 16, 14, 'Слышали всё это и фарисеи, которые были сребролюбивы, и они смеялись над Ним.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34330, 'rst66', 'luk', 16, 13, 'Никакой слуга не может служить двум господам, ибо или одного будет ненавидеть, а другого любить, или одному станет усердствовать, а о другом нерадеть. Не можете служить Богу и маммоне.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34329, 'rst66', 'luk', 16, 12, 'И если в чужом не были верны, кто даст вам ваше?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34328, 'rst66', 'luk', 16, 11, 'Итак, если вы в неправедном богатстве не были верны, кто поверит вам истинное?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34327, 'rst66', 'luk', 16, 10, 'Верный в малом и во многом верен, а неверный в малом неверен и во многом.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34326, 'rst66', 'luk', 16, 9, 'И Я говорю вам: приобретайте себе друзей богатством неправедным, чтобы они, когда обнищаете, приняли вас в вечные обители.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34325, 'rst66', 'luk', 16, 8, 'И похвалил господин управителя неверного, что догадливо поступил; ибо сыны века сего догадливее сынов света в своем роде.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34324, 'rst66', 'luk', 16, 7, 'Потом другому сказал: «а ты сколько должен?» Он отвечал: «сто мер пшеницы». И сказал ему: «возьми твою расписку и напиши: восемьдесят».');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34323, 'rst66', 'luk', 16, 6, 'Он сказал: «сто мер масла». И сказал ему: «возьми твою расписку и садись скорее, напиши: пятьдесят».');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34322, 'rst66', 'luk', 16, 5, 'И, призвав должников господина своего, каждого порознь, сказал первому: «сколько ты должен господину моему?»');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34321, 'rst66', 'luk', 16, 4, 'знаю, что сделать, чтобы приняли меня в домы свои, когда отставлен буду от управления домом».');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34320, 'rst66', 'luk', 16, 3, 'Тогда управитель сказал сам в себе: «что мне делать? господин мой отнимает у меня управление домом; копать не могу, просить стыжусь;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34319, 'rst66', 'luk', 16, 2, 'и, призвав его, сказал ему: «что это я слышу о тебе? дай отчет в управлении твоем, ибо ты не можешь более управлять».');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34318, 'rst66', 'luk', 16, 1, 'Сказал же и к ученикам Своим: один человек был богат и имел управителя, на которого донесено было ему, что расточает имение его;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34317, 'rst66', 'luk', 15, 32, 'а о том надобно было радоваться и веселиться, что брат твой сей был мертв, и ожил, пропадал, и нашелся».');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34316, 'rst66', 'luk', 15, 31, 'Он же сказал ему: «сын мой! ты всегда со мною, и всё мое твое,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34315, 'rst66', 'luk', 15, 30, 'а когда этот сын твой, расточивший имение своё с блудницами, пришел, ты заколол для него откормленного теленка».');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34314, 'rst66', 'luk', 15, 29, 'Но он сказал в ответ отцу: «вот, я столько лет служу тебе и никогда не преступал приказания твоего, но ты никогда не дал мне и козлёнка, чтобы мне повеселиться с друзьями моими;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34313, 'rst66', 'luk', 15, 28, 'Он осердился и не хотел войти. Отец же его, выйдя, звал его.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34312, 'rst66', 'luk', 15, 27, 'Он сказал ему: «брат твой пришел, и отец твой заколол откормленного теленка, потому что принял его здоровым».');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34311, 'rst66', 'luk', 15, 26, 'и, призвав одного из слуг, спросил: «что это такое?»');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34310, 'rst66', 'luk', 15, 25, 'Старший же сын его был на поле; и, возвращаясь, когда приблизился к дому, услышал пение и ликование;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34309, 'rst66', 'luk', 15, 24, 'Ибо этот сын мой был мертв и ожил, пропадал и нашелся». И начали веселиться.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34308, 'rst66', 'luk', 15, 23, 'и приведите откормленного теленка, и заколите; станем есть и веселиться!');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34307, 'rst66', 'luk', 15, 22, 'А отец сказал рабам своим: «принесите лучшую одежду и оденьте его, и дайте перстень на руку его и обувь на ноги;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34306, 'rst66', 'luk', 15, 21, 'Сын же сказал ему: «отче! я согрешил против неба и пред тобою и уже недостоин называться сыном твоим».');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34305, 'rst66', 'luk', 15, 20, 'Встал и пошел к отцу своему. И когда он был еще далеко, увидел его отец его и сжалился; и, побежав, пал ему на шею и целовал его.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34304, 'rst66', 'luk', 15, 19, 'и уже недостоин называться сыном твоим; прими меня в число наемников твоих».');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34303, 'rst66', 'luk', 15, 18, 'встану, пойду к отцу моему и скажу ему: отче! я согрешил против неба и пред тобою');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34302, 'rst66', 'luk', 15, 17, 'Придя же в себя, сказал: «сколько наемников у отца моего избыточествуют хлебом, а я умираю от голода;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34301, 'rst66', 'luk', 15, 16, 'и он рад был наполнить чрево свое рожка&#769;ми, которые ели свиньи, но никто не давал ему.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34300, 'rst66', 'luk', 15, 15, 'и пошел, пристал к одному из жителей страны той, а тот послал его на поля свои пасти свиней;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34299, 'rst66', 'luk', 15, 14, 'Когда же он прожил всё, настал великий голод в той стране, и он начал нуждаться;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34298, 'rst66', 'luk', 15, 13, 'По прошествии немногих дней младший сын, собрав всё, пошел в дальнюю сторону и там расточил имение свое, живя распутно.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34297, 'rst66', 'luk', 15, 12, 'и сказал младший из них отцу: «отче! дай мне следующую <i>мне</i> часть имения». И <i>отец</i> разделил им имение.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34296, 'rst66', 'luk', 15, 11, 'Еще сказал: у некоторого человека было два сына;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34295, 'rst66', 'luk', 15, 10, 'Так, говорю вам, бывает радость у Ангелов Божиих и об одном грешнике кающемся.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34294, 'rst66', 'luk', 15, 9, 'а найдя, созовет подруг и соседок и скажет: «порадуйтесь со мною: я нашла потерянную драхму».');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34293, 'rst66', 'luk', 15, 8, 'Или какая женщина, имея десять драхм, если потеряет одну драхму, не зажжет свечи&#769; и не станет мести комнату и искать тщательно, пока не найдет,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34292, 'rst66', 'luk', 15, 7, 'Сказываю вам, что так на небесах более радости будет об одном грешнике кающемся, нежели о девяноста девяти праведниках, не имеющих нужды в покаянии.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34291, 'rst66', 'luk', 15, 6, 'и, придя домой, созовет друзей и соседей и скажет им: «порадуйтесь со мною: я нашел мою пропавшую овцу».');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34290, 'rst66', 'luk', 15, 5, 'А найдя, возьмет ее на плечи свои с радостью');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34289, 'rst66', 'luk', 15, 4, 'кто из вас, имея сто овец и потеряв одну из них, не оставит девяноста девяти в пустыне и не пойдет за пропавшею, пока не найдет ее?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34288, 'rst66', 'luk', 15, 3, 'Но Он сказал им следующую притчу:');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34287, 'rst66', 'luk', 15, 2, 'Фарисеи же и книжники роптали, говоря: Он принимает грешников и ест с ними.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34286, 'rst66', 'luk', 15, 1, 'Приближались к Нему все мытари и грешники слушать Его.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34285, 'rst66', 'luk', 14, 35, 'Ни в землю, ни в навоз не годится; вон выбрасывают ее. Кто имеет уши слышать, да слышит!');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34284, 'rst66', 'luk', 14, 34, 'Соль &mdash; добрая вещь; но если соль потеряет силу, чем исправить ее?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34283, 'rst66', 'luk', 14, 33, 'Так всякий из вас, кто не отрешится от всего, что имеет, не может быть Моим учеником.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34282, 'rst66', 'luk', 14, 32, 'Иначе, пока тот еще далеко, он пошлет к нему посольство просить о мире.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34281, 'rst66', 'luk', 14, 31, 'Или какой царь, идя на войну против другого царя, не сядет и не посоветуется прежде, силен ли он с десятью тысячами противостать идущему на него с двадцатью тысячами?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34280, 'rst66', 'luk', 14, 30, 'говоря: «этот человек начал строить и не мог окончить?»');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34279, 'rst66', 'luk', 14, 29, 'дабы, когда положит основание и не возможет совершить, все видящие не стали смеяться над ним,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34278, 'rst66', 'luk', 14, 28, 'Ибо кто из вас, желая построить башню, не сядет прежде и не вычислит издержек, имеет ли он, что нужно для совершения ее,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34277, 'rst66', 'luk', 14, 27, 'и кто не несет креста своего и идёт за Мною, не может быть Моим учеником.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34276, 'rst66', 'luk', 14, 26, 'если кто приходит ко Мне и не возненавидит отца своего и матери, и жены и детей, и братьев и сестер, а притом и самой жизни своей, тот не может быть Моим учеником;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34275, 'rst66', 'luk', 14, 25, 'С Ним шло множество народа; и Он, обратившись, сказал им:');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34274, 'rst66', 'luk', 14, 24, 'Ибо сказываю вам, что никто из тех званых не вкусит моего ужина, ибо много званых, но мало избранных».');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34273, 'rst66', 'luk', 14, 23, 'Господин сказал рабу: «пойди по дорогам и изгородям и убеди прийти, чтобы наполнился дом мой.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34272, 'rst66', 'luk', 14, 22, 'И сказал раб: «господин! исполнено, как приказал ты, и еще есть место».');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34271, 'rst66', 'luk', 14, 21, 'И, возвратившись, раб тот донес о сем господину своему. Тогда, разгневавшись, хозяин дома сказал рабу своему: «пойди скорее по улицам и переулкам города и приведи сюда нищих, увечных, хромых и слепых».');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34270, 'rst66', 'luk', 14, 20, 'Третий сказал: «я женился и потому не могу прийти».');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34269, 'rst66', 'luk', 14, 19, 'Другой сказал: «я купил пять пар волов и иду испытать их; прошу тебя, извини меня».');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34268, 'rst66', 'luk', 14, 18, 'И начали все, как бы сговорившись, извиняться. Первый сказал ему: «я купил землю, и мне нужно пойти посмотреть ее; прошу тебя, извини меня».');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34267, 'rst66', 'luk', 14, 17, 'и когда наступило время ужина, послал раба своего сказать званым: «идите, ибо уже всё готово».');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34266, 'rst66', 'luk', 14, 16, 'Он же сказал ему: один человек сделал большой ужин и звал многих,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34265, 'rst66', 'luk', 14, 15, 'Услышав это, некто из возлежащих с Ним сказал Ему: блажен, кто вкусит хлеба в Царствии Божием!');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34264, 'rst66', 'luk', 14, 14, 'и блажен будешь, что они не могут воздать тебе, ибо воздастся тебе в воскресение праведных.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34263, 'rst66', 'luk', 14, 13, 'Но, когда делаешь пир, зови нищих, увечных, хромых, слепых,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34262, 'rst66', 'luk', 14, 12, 'Сказал же и позвавшему Его: когда делаешь обед или ужин, не зови друзей твоих, ни братьев твоих, ни родственников твоих, ни соседей богатых, чтобы и они тебя когда не позвали, и не получил ты воздаяния.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34261, 'rst66', 'luk', 14, 11, 'ибо всякий, возвышающий сам себя, унижен будет, а унижающий себя возвысится.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34260, 'rst66', 'luk', 14, 10, 'Но, когда зван будешь, придя, садись на последнее место, чтобы звавший тебя, подойдя, сказал: «друг! пересядь выше»; тогда будет тебе честь пред сидящими с тобою,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34259, 'rst66', 'luk', 14, 9, 'и звавший тебя и его, подойдя, не сказал бы тебе: «уступи ему место»; и тогда со стыдом должен будешь занять последнее место.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34258, 'rst66', 'luk', 14, 8, 'когда ты будешь позван кем на брак, не садись на первое место, чтобы не случился кто из званых им почетнее тебя,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34257, 'rst66', 'luk', 14, 7, 'Замечая же, как званые выбирали первые места, сказал им притчу:');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34256, 'rst66', 'luk', 14, 6, 'И не могли отвечать Ему на это.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34255, 'rst66', 'luk', 14, 5, 'При сем сказал им: если у кого из вас осёл или вол упадет в колодезь, не тотчас ли вытащит его и в субботу?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34254, 'rst66', 'luk', 14, 4, 'Они молчали. И, прикоснувшись, исцелил его и отпустил.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34253, 'rst66', 'luk', 14, 3, 'По сему случаю Иисус спросил законников и фарисеев: позволительно ли врачевать в субботу?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34252, 'rst66', 'luk', 14, 2, 'И вот, предстал пред Него человек, страждущий водяною болезнью.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34251, 'rst66', 'luk', 14, 1, 'Случилось Ему в субботу прийти в дом одного из начальников фарисейских вкусить хлеба, и они наблюдали за Ним.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34250, 'rst66', 'luk', 13, 35, 'Се, оставляется вам дом ваш пуст. Сказываю же вам, что вы не увидите Меня, пока не придет время, когда скажете: «благословен Грядый во имя Господне!»');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34249, 'rst66', 'luk', 13, 34, 'Иерусалим! Иерусалим! избивающий пророков и камнями побивающий посланных к тебе! сколько раз хотел Я собрать чад твоих, как птица птенцов своих под крылья, и вы не захотели!');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34248, 'rst66', 'luk', 13, 33, 'а впрочем, Мне должно ходить сегодня, завтра и в последующий день, потому что не бывает, чтобы пророк погиб вне Иерусалима.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34247, 'rst66', 'luk', 13, 32, 'И сказал им: пойдите, скажите этой лисице: се, изгоняю бесов и совершаю исцеления сегодня и завтра, и в третий <i>день</i> кончу;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34246, 'rst66', 'luk', 13, 31, 'В тот день пришли некоторые из фарисеев и говорили Ему: выйди и удались отсюда, ибо Ирод хочет убить Тебя.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34245, 'rst66', 'luk', 13, 30, 'И вот, есть последние, которые будут первыми, и есть первые, которые будут последними.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34244, 'rst66', 'luk', 13, 29, 'И придут от востока и запада, и севера и юга, и возлягут в Царствии Божием.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34243, 'rst66', 'luk', 13, 28, 'Там будет плач и скрежет зубов, когда увидите Авраама, Исаака и Иакова и всех пророков в Царствии Божием, а себя изгоняемыми вон.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34242, 'rst66', 'luk', 13, 27, 'Но Он скажет: «говорю вам: не знаю вас, откуда вы; отойдите от Меня, все делатели неправды».');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34241, 'rst66', 'luk', 13, 26, 'Тогда станете говорить: «мы ели и пили пред Тобою, и на улицах наших учил Ты».');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34240, 'rst66', 'luk', 13, 25, 'Когда хозяин дома встанет и затворит двери, тогда вы, стоя вне, станете стучать в двери и говорить: «Господи! Господи! отвори нам»; но Он скажет вам в ответ: «не знаю вас, откуда вы».');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34239, 'rst66', 'luk', 13, 24, 'подвизайтесь войти сквозь тесные врата, ибо, сказываю вам, многие поищут войти, и не возмогут.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34238, 'rst66', 'luk', 13, 23, 'Некто сказал Ему: Господи! неужели мало спасающихся? Он же сказал им:');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34237, 'rst66', 'luk', 13, 22, 'И проходил по городам и селениям, уча и направляя путь к Иерусалиму.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34236, 'rst66', 'luk', 13, 21, 'Оно подобно закваске, которую женщина, взяв, положила в три меры муки, доколе не вскисло всё.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34235, 'rst66', 'luk', 13, 20, 'Ещё сказал: чему уподоблю Царствие Божие?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34234, 'rst66', 'luk', 13, 19, 'Оно подобно зерну горчичному, которое, взяв, человек посадил в саду своем; и выросло, и стало большим деревом, и птицы небесные укрывались в ветвях его.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34233, 'rst66', 'luk', 13, 18, 'Он же сказал: чему подобно Царствие Божие? и чему уподоблю его?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34232, 'rst66', 'luk', 13, 17, 'И, когда говорил Он это, все противившиеся Ему стыдились; и весь народ радовался о всех славных делах Его.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34231, 'rst66', 'luk', 13, 16, 'Сию же дочь Авраамову, которую связал сатана вот уже восемнадцать лет, не надлежало ли освободить от уз сих в день субботний?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34230, 'rst66', 'luk', 13, 15, 'Господь сказал ему в ответ: лицемер! не отвязывает ли каждый из вас вола своего или осла от яслей в субботу и не ведет ли поить?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34229, 'rst66', 'luk', 13, 14, 'При этом начальник синагоги, негодуя, что Иисус исцелил в субботу, сказал народу: есть шесть дней, в которые должно делать; в те и приходи&#769;те исцеляться, а не в день субботний.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34228, 'rst66', 'luk', 13, 13, 'И возложил на нее руки, и она тотчас выпрямилась и стала славить Бога.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34227, 'rst66', 'luk', 13, 12, 'Иисус, увидев ее, подозвал и сказал ей: женщина! ты освобождаешься от недуга твоего.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34226, 'rst66', 'luk', 13, 11, 'Там была женщина, восемнадцать лет имевшая духа немощи: она была скорчена и не могла выпрямиться.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34225, 'rst66', 'luk', 13, 10, 'В одной из синагог учил Он в субботу.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34224, 'rst66', 'luk', 13, 9, 'не принесет ли плода; если же нет, то в следующий <i>год</i> срубишь ее».');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34223, 'rst66', 'luk', 13, 8, 'Но он сказал ему в ответ: «господин! оставь ее и на этот год, пока я окопаю ее и обложу навозом, &mdash;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34222, 'rst66', 'luk', 13, 7, 'и сказал виноградарю: «вот, я третий год прихожу искать плода на этой смоковнице и не нахожу; сруби ее: на что она и землю занимает?»');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34221, 'rst66', 'luk', 13, 6, 'И сказал сию притчу: некто имел в винограднике своем посаженную смоковницу, и пришел искать плода на ней, и не нашел;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34220, 'rst66', 'luk', 13, 5, 'Нет, говорю вам, но, если не покаетесь, все та&#769;к же погибнете.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34219, 'rst66', 'luk', 13, 4, 'Или думаете ли, что те восемнадцать человек, на которых упала башня Силоамская и побила их, виновнее были всех, живущих в Иерусалиме?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34218, 'rst66', 'luk', 13, 3, 'Нет, говорю вам, но, если не покаетесь, все та&#769;к же погибнете.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34217, 'rst66', 'luk', 13, 2, 'Иисус сказал им на это: думаете ли вы, что эти Галилеяне были грешнее всех Галилеян, что так пострадали?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34216, 'rst66', 'luk', 13, 1, 'В это время пришли некоторые и рассказали Ему о Галилеянах, которых кровь Пилат смешал с жертвами их.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34215, 'rst66', 'luk', 12, 59, 'Сказываю тебе: не выйдешь оттуда, пока не отдашь и последней полушки.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34214, 'rst66', 'luk', 12, 58, 'Когда ты идешь с соперником своим к начальству, то на дороге постарайся освободиться от него, чтобы он не привел тебя к судье, а судья не отдал тебя истязателю, а истязатель не вверг тебя в темницу.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34213, 'rst66', 'luk', 12, 57, 'Зачем же вы и по самим себе не судите, чему быть должно?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34212, 'rst66', 'luk', 12, 56, 'Лицемеры! лицо земли и неба распознавать умеете, как же времени сего не узнаете?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34211, 'rst66', 'luk', 12, 55, 'и когда дует южный ветер, говорите: «зной будет», и бывает.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34210, 'rst66', 'luk', 12, 54, 'Сказал же и народу: когда вы видите облако, поднимающееся с запада, тотчас говорите: «дождь будет», и бывает так;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34209, 'rst66', 'luk', 12, 53, 'отец будет против сына, и сын против отца; мать против дочери, и дочь против матери; свекровь против невестки своей, и невестка против свекрови своей.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34208, 'rst66', 'luk', 12, 52, 'ибо отныне пятеро в одном доме станут разделяться, трое против двух, и двое против трех:');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34207, 'rst66', 'luk', 12, 51, 'Думаете ли вы, что Я пришел дать мир земле? нет, говорю вам, но разделение;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34206, 'rst66', 'luk', 12, 50, 'Крещением должен Я креститься; и как Я томлюсь, пока сие совершится!');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34205, 'rst66', 'luk', 12, 49, 'Огонь пришел Я низвести на землю, и как желал бы, чтобы он уже возгорелся!');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34204, 'rst66', 'luk', 12, 48, 'а который не знал, и сделал достойное наказания, бит будет меньше. И от всякого, кому дано много, много и потребуется, и кому много вверено, с того больше взыщут.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34203, 'rst66', 'luk', 12, 47, 'Раб же тот, который знал волю господина своего, и не был готов, и не делал по воле его, бит будет много;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34202, 'rst66', 'luk', 12, 46, 'то придет господин раба того в день, в который он не ожидает, и в час, в который не думает, и рассечет его, и подвергнет его одной участи с неверными.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34201, 'rst66', 'luk', 12, 45, 'Если же раб тот скажет в сердце своем: «не скоро придет господин мой», и начнет бить слуг и служанок, есть и пить и напиваться,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34200, 'rst66', 'luk', 12, 44, 'Истинно говорю вам, что над всем имением своим поставит его.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34199, 'rst66', 'luk', 12, 43, 'Блажен раб тот, которого господин его, придя, найдет поступающим так.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34198, 'rst66', 'luk', 12, 42, 'Господь же сказал: кто&#769; верный и благоразумный домоправитель, которого господин поставил над слугами своими раздавать им в своё время меру хлеба?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34197, 'rst66', 'luk', 12, 41, 'Тогда сказал Ему Петр: Господи! к нам ли притчу сию говоришь, или и ко всем?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34196, 'rst66', 'luk', 12, 40, 'Будьте же и вы готовы, ибо, в который час не думаете, приидет Сын Человеческий.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34195, 'rst66', 'luk', 12, 39, 'Вы знаете, что если бы ведал хозяин дома, в который час придет вор, то бодрствовал бы и не допустил бы подкопать дом свой.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34194, 'rst66', 'luk', 12, 38, 'И если придет во вторую стражу, и в третью стражу придет, и найдет их так, то блаженны рабы те.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34193, 'rst66', 'luk', 12, 37, 'Блаженны рабы те, которых господин, придя, найдёт бодрствующими; истинно говорю вам, он препояшется и посадит их, и, подходя, станет служить им.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34192, 'rst66', 'luk', 12, 36, 'И вы будьте подобны людям, ожидающим возвращения господина своего с брака, дабы, когда придёт и постучит, тотчас отворить ему.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34191, 'rst66', 'luk', 12, 35, 'Да будут чресла ваши препоясаны и светильники горящи.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34190, 'rst66', 'luk', 12, 34, 'ибо где сокровище ваше, там и сердце ваше будет.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34189, 'rst66', 'luk', 12, 33, 'Продавайте имения ваши и давайте милостыню. Приготовляйте себе вместилища неветшающие, сокровище неоскудевающее на небесах, куда вор не приближается и где моль не съедает,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34188, 'rst66', 'luk', 12, 32, 'Не бойся, малое стадо! ибо Отец ваш благоволил дать вам Царство.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34187, 'rst66', 'luk', 12, 31, 'наипаче ищите Царствия Божия, и это всё приложится вам.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34186, 'rst66', 'luk', 12, 30, 'потому что всего этого ищут люди мира сего; ваш же Отец знает, что вы имеете нужду в том;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34185, 'rst66', 'luk', 12, 29, 'Итак, не ищите, что&#769; вам есть, или что&#769; пить, и не беспокойтесь,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34184, 'rst66', 'luk', 12, 28, 'Если же траву на поле, которая сегодня есть, а завтра будет брошена в печь, Бог так одевает, то кольми паче вас, маловеры!');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34183, 'rst66', 'luk', 12, 27, 'Посмотрите на лилии, как они растут: не трудятся, не прядут; но говорю вам, что и Соломон во всей славе своей не одевался так, как всякая из них.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34182, 'rst66', 'luk', 12, 26, 'Итак, если и малейшего сделать не можете, что&#769; заботитесь о прочем?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34181, 'rst66', 'luk', 12, 25, 'Да и кто из вас, заботясь, может прибавить себе роста хотя на один локоть?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34180, 'rst66', 'luk', 12, 24, 'Посмотрите на во&#769;ронов: они не сеют, не жнут; нет у них ни хранилищ, ни житниц, и Бог питает их; сколько же вы лучше птиц?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34179, 'rst66', 'luk', 12, 23, 'душа больше пищи, и тело &mdash; одежды.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34178, 'rst66', 'luk', 12, 22, 'И сказал ученикам Своим: посему говорю вам, &mdash; не заботьтесь для души вашей, что вам есть, ни для тела, во что одеться:');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34177, 'rst66', 'luk', 12, 21, 'Так <i>бывает с тем</i>, кто собирает сокровища для себя, а не в Бога богатеет.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34176, 'rst66', 'luk', 12, 20, 'Но Бог сказал ему: «безумный! в сию ночь душу твою возьмут у тебя; кому же достанется то, что ты заготовил?»');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34175, 'rst66', 'luk', 12, 19, 'и скажу душе моей: душа! много добра лежит у тебя на многие годы: покойся, ешь, пей, веселись».');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34174, 'rst66', 'luk', 12, 18, 'И сказал: «вот что сделаю: сломаю житницы мои и построю бо&#769;льшие, и соберу туда весь хлеб мой и всё добро мое,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34173, 'rst66', 'luk', 12, 17, 'и он рассуждал сам с собою: «что мне делать? некуда мне собрать плодов моих».');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34172, 'rst66', 'luk', 12, 16, 'И сказал им притчу: у одного богатого человека был хороший урожай в поле;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34171, 'rst66', 'luk', 12, 15, 'При этом сказал им: смотрите, берегитесь любостяжания, ибо жизнь человека не зависит от изобилия его имения.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34170, 'rst66', 'luk', 12, 14, 'Он же сказал человеку тому: кто поставил Меня судить или делить вас?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34169, 'rst66', 'luk', 12, 13, 'Некто из народа сказал Ему: Учитель! скажи брату моему, чтобы он разделил со мною наследство.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34168, 'rst66', 'luk', 12, 12, 'ибо Святой Дух научит вас в тот час, что&#769; должно говорить.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34167, 'rst66', 'luk', 12, 11, 'Когда же приведут вас в синагоги, к начальствам и властям, не заботьтесь, ка&#769;к или что&#769; отвечать, или что&#769; говорить,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34166, 'rst66', 'luk', 12, 10, 'И всякому, кто скажет слово на Сына Человеческого, прощено будет; а кто скажет хулу на Святого Духа, тому не простится.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34165, 'rst66', 'luk', 12, 9, 'а кто отвергнется Меня пред человеками, тот отвержен будет пред Ангелами Божиими.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34164, 'rst66', 'luk', 12, 8, 'Сказываю же вам: всякого, кто исповедает Меня пред человеками, и Сын Человеческий исповедает пред Ангелами Божиими;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34163, 'rst66', 'luk', 12, 7, 'А у вас и волосы на голове все сочтены. Итак, не бойтесь: вы дороже многих малых птиц.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34162, 'rst66', 'luk', 12, 6, 'Не пять ли малых птиц продаются за два ассария? и ни одна из них не забыта у Бога.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34161, 'rst66', 'luk', 12, 5, 'но скажу вам, кого бояться: бойтесь Того, Кто, по убиении, может ввергнуть в геенну: ей, говорю вам, того бойтесь.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34160, 'rst66', 'luk', 12, 4, 'Говорю же вам, друзьям Моим: не бойтесь убивающих тело и потом не могущих ничего более сделать;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34159, 'rst66', 'luk', 12, 3, 'Посему, что&#769; вы сказали в темноте, то&#769; услышится во свете; и что&#769; говорили на ухо внутри дома, то&#769; будет провозглашено на кровлях.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34158, 'rst66', 'luk', 12, 2, 'Нет ничего сокровенного, что не открылось бы, и тайного, чего не узнали бы.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34157, 'rst66', 'luk', 12, 1, 'Между тем, когда собрались тысячи народа, так что теснили друг друга, Он начал говорить сперва ученикам Своим: берегитесь закваски фарисейской, которая есть лицемерие.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34156, 'rst66', 'luk', 11, 54, 'подыскиваясь под Него и стараясь уловить что‐нибудь из уст Его, чтобы обвинить Его.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34155, 'rst66', 'luk', 11, 53, 'Когда Он говорил им это, книжники и фарисеи начали сильно приступать к Нему, вынуждая у Него ответы на многое,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34154, 'rst66', 'luk', 11, 52, 'Горе вам, законникам, что вы взяли ключ разумения: сами не вошли, и входящим воспрепятствовали.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34153, 'rst66', 'luk', 11, 51, 'от крови Авеля до крови Захарии, убитого между жертвенником и храмом. Ей, говорю вам, взыщется от рода сего.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34152, 'rst66', 'luk', 11, 50, 'да взыщется от рода сего кровь всех пророков, пролитая от создания мира,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34151, 'rst66', 'luk', 11, 49, 'Потому и премудрость Божия сказала: пошлю к ним пророков и Апостолов, и из них одних убьют, а других изгонят;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34150, 'rst66', 'luk', 11, 48, 'сим вы свидетельствуете о делах отцов ваших и соглашаетесь с ними, ибо они избили пророков, а вы строите им гробницы.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34149, 'rst66', 'luk', 11, 47, 'Горе вам, что строите гробницы пророкам, которых избили отцы ваши:');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34148, 'rst66', 'luk', 11, 46, 'Но Он сказал: и вам, законникам, горе, что налагаете на людей бремена неудобоносимые, а сами и одним перстом своим не дотрагиваетесь до них.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34147, 'rst66', 'luk', 11, 45, 'На это некто из законников сказал Ему: Учитель! говоря это, Ты и нас обижаешь.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34146, 'rst66', 'luk', 11, 44, 'Горе вам, книжники и фарисеи, лицемеры, что вы &mdash; как гробы скрытые, над которыми люди ходят и не знают того.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34145, 'rst66', 'luk', 11, 43, 'Горе вам, фарисеям, что любите председания в синагогах и приветствия в народных собраниях.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34144, 'rst66', 'luk', 11, 42, 'Но горе вам, фарисеям, что даете десятину с мяты, руты и всяких овощей, и нерадите о суде и любви Божией: сие надлежало делать, и того не оставлять.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34143, 'rst66', 'luk', 11, 41, 'Подавайте лучше милостыню из того, что у вас есть, тогда всё будет у вас чисто.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34142, 'rst66', 'luk', 11, 40, 'Неразумные! не Тот же ли, Кто сотворил внешнее, сотворил и внутреннее?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34141, 'rst66', 'luk', 11, 39, 'Но Господь сказал ему: ныне вы, фарисеи, внешность чаши и блюда очищаете, а внутренность ваша исполнена хищения и лукавства.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34140, 'rst66', 'luk', 11, 38, 'Фарисей же удивился, увидев, что Он не умыл <i>рук</i> перед обедом.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34139, 'rst66', 'luk', 11, 37, 'Когда Он говорил это, один фарисей просил Его к себе обедать. Он пришел и возлег.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34138, 'rst66', 'luk', 11, 36, 'Если же тело твое всё светло и не имеет ни одной темной части, то будет светло всё та&#769;к, как бы светильник освещал тебя сиянием.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34137, 'rst66', 'luk', 11, 35, 'Итак, смотри: свет, который в тебе, не есть ли тьма?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34136, 'rst66', 'luk', 11, 34, 'Светильник тела есть око; итак, если око твое будет чисто, то и все тело твое будет светло; а если оно будет худо, то и тело твое будет темно.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34135, 'rst66', 'luk', 11, 33, 'Никто, зажегши свечу, не ставит ее в сокровенном месте, ни под сосудом, но на подсвечнике, чтобы входящие видели свет.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34134, 'rst66', 'luk', 11, 32, 'Ниневитяне восстанут на суд с родом сим и осудят его, ибо они покаялись от проповеди Иониной, и вот, здесь больше Ионы.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34133, 'rst66', 'luk', 11, 31, 'Царица южная восстанет на суд с людьми рода сего и осудит их, ибо она приходила от пределов земли послушать мудрости Соломоновой; и вот, здесь больше Соломона.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34132, 'rst66', 'luk', 11, 30, 'ибо как Иона был знамением для Ниневитян, так будет и Сын Человеческий для рода сего.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34131, 'rst66', 'luk', 11, 29, 'Когда же народ стал сходиться во множестве, Он начал говорить: род сей лукав, он ищет знамения, и знамение не дастся ему, кроме знамения Ионы пророка;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34130, 'rst66', 'luk', 11, 28, 'А Он сказал: блаженны слышащие слово Божие и соблюдающие его.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34129, 'rst66', 'luk', 11, 27, 'Когда же Он говорил это, одна женщина, возвысив голос из народа, сказала Ему: блаженно чрево, носившее Тебя, и сосцы, Тебя питавшие!');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34128, 'rst66', 'luk', 11, 26, 'тогда идет и берет с собою семь других духов, злейших себя, и, войдя, живут там, &mdash; и бывает для человека того последнее хуже первого.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34127, 'rst66', 'luk', 11, 25, 'и, придя, находит его выметенным и убранным;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34126, 'rst66', 'luk', 11, 24, 'Когда нечистый дух выйдет из человека, то ходит по безводным местам, ища покоя, и, не находя, говорит: «возвращусь в дом мой, откуда вышел»;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34125, 'rst66', 'luk', 11, 23, 'Кто не со Мною, тот против Меня; и кто не собирает со Мною, тот расточает.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34124, 'rst66', 'luk', 11, 22, 'когда же сильнейший его нападет на него и победит его, тогда возьмет всё оружие его, на которое он надеялся, и разделит похищенное у него.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34123, 'rst66', 'luk', 11, 21, 'Когда сильный с оружием охраняет свой дом, тогда в безопасности его имение;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34122, 'rst66', 'luk', 11, 20, 'Если же Я перстом Божиим изгоняю бесов, то, конечно, достигло до вас Царствие Божие.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34121, 'rst66', 'luk', 11, 19, 'и если Я силою веельзевула изгоняю бесов, то сыновья ваши чьею силою изгоняют их? Посему они будут вам судьями.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34120, 'rst66', 'luk', 11, 18, 'если же и сатана разделится сам в себе, то ка&#769;к устоит царство его? а вы говорите, что Я силою веельзевула изгоняю бесов;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34119, 'rst66', 'luk', 11, 17, 'Но Он, зная помышления их, сказал им: всякое царство, разделившееся само в себе, опустеет, и дом, <i>разделившийся</i> сам в себе, падет;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34118, 'rst66', 'luk', 11, 16, 'А другие, искушая, требовали от Него знамения с неба.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34117, 'rst66', 'luk', 11, 15, 'Некоторые же из них говорили: Он изгоняет бесов силою веельзевула, князя бесовского.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34116, 'rst66', 'luk', 11, 14, 'Однажды изгнал Он беса, который был нем; и когда бес вышел, немой стал говорить; и народ удивился.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34115, 'rst66', 'luk', 11, 13, 'Итак, если вы, будучи злы, умеете даяния благие давать детям вашим, тем более Отец Небесный даст Духа Святого просящим у Него.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34114, 'rst66', 'luk', 11, 12, 'Или, если попросит яйца, подаст ему скорпиона?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34113, 'rst66', 'luk', 11, 11, 'Какой из вас отец, <i>когда</i> сын попросит у него хлеба, подаст ему камень? или, <i>когда попросит</i> рыбы, подаст ему змею вместо рыбы?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34112, 'rst66', 'luk', 11, 10, 'ибо всякий просящий получает, и ищущий находит, и стучащему отворят.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34111, 'rst66', 'luk', 11, 9, 'И Я скажу вам: проси&#769;те, и дано будет вам; ищите, и найдете; стучи&#769;те, и отворят вам,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34110, 'rst66', 'luk', 11, 8, 'Если, говорю вам, он не встанет и не даст ему по дружбе с ним, то по неотступности его, встав, даст ему, сколько просит.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34109, 'rst66', 'luk', 11, 7, 'а тот изнутри скажет ему в ответ: «не беспокой меня, двери уже заперты, и дети мои со мною на постели; не могу встать и дать тебе».');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34108, 'rst66', 'luk', 11, 6, 'ибо друг мой с дороги зашел ко мне, и мне нечего предложить ему»;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34107, 'rst66', 'luk', 11, 5, 'И сказал им: <i>положим</i>, <i>что</i> кто‐нибудь из вас, имея друга, придёт к нему в полночь и скажет ему: «друг! дай мне взаймы три хлеба,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34106, 'rst66', 'luk', 11, 4, 'и прости нам грехи наши, ибо и мы прощаем всякому должнику нашему; и не введи нас в искушение, но избавь нас от лукавого».');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34105, 'rst66', 'luk', 11, 3, 'хлеб наш насущный подавай нам на каждый день;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34104, 'rst66', 'luk', 11, 2, 'Он сказал им: когда мо&#769;литесь, говорите: «Отче наш, сущий на небесах! да святится имя Твое; да приидет Царствие Твое; да будет воля Твоя и на земле, как на небе;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34103, 'rst66', 'luk', 11, 1, 'Случилось, что, когда Он в одном месте молился, и перестал, один из учеников Его сказал Ему: Господи! научи нас молиться, как и Иоанн научил учеников своих.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34102, 'rst66', 'luk', 10, 42, 'а одно только нужно; Мария же избрала благую часть, которая не отнимется у неё.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34101, 'rst66', 'luk', 10, 41, 'Иисус же сказал ей в ответ: Марфа! Марфа! ты заботишься и суетишься о многом,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34100, 'rst66', 'luk', 10, 40, 'Марфа же заботилась о большом угощении и, подойдя, сказала: Господи! или Тебе нужды нет, что сестра моя одну меня оставила служить? скажи ей, чтобы помогла мне.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34099, 'rst66', 'luk', 10, 39, 'у неё была сестра, именем Мария, которая села у ног Иисуса и слушала слово Его.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34098, 'rst66', 'luk', 10, 38, 'В продолжение пути их пришел Он в одно селение; здесь женщина, именем Марфа, приняла Его в дом свой;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34097, 'rst66', 'luk', 10, 37, 'Он сказал: оказавший ему милость. Тогда Иисус сказал ему: иди, и ты поступай так же.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34096, 'rst66', 'luk', 10, 36, 'Кто из этих троих, думаешь ты, был ближний попавшемуся разбойникам?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34095, 'rst66', 'luk', 10, 35, 'а на другой день, отъезжая, вынул два динария, дал содержателю гостиницы и сказал ему: «позаботься о нем; и если издержишь что более, я, когда возвращусь, отдам тебе».');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34094, 'rst66', 'luk', 10, 34, 'и, подойдя, перевязал ему раны, возливая масло и вино; и, посадив его на своего осла, привез его в гостиницу и позаботился о нем;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34093, 'rst66', 'luk', 10, 33, 'Самарянин же некто, проезжая, нашел на него и, увидев его, сжалился');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34092, 'rst66', 'luk', 10, 32, 'Также и левит, быв на том месте, подошел, посмотрел и прошел мимо.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34091, 'rst66', 'luk', 10, 31, 'По случаю один священник шел тою дорогою и, увидев его, прошел мимо.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34090, 'rst66', 'luk', 10, 30, 'На это сказал Иисус: некоторый человек шел из Иерусалима в Иерихон и попался разбойникам, которые сняли с него одежду, изранили его и ушли, оставив его едва живым.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34089, 'rst66', 'luk', 10, 29, 'Но он, желая оправдать себя, сказал Иисусу: а кто мой ближний?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34088, 'rst66', 'luk', 10, 28, '<i>Иисус</i> сказал ему: правильно ты отвечал; так поступай, и будешь жить.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34087, 'rst66', 'luk', 10, 27, 'Он сказал в ответ: «возлюби Господа Бога твоего всем сердцем твоим, и всею душою твоею, и всею крепостию твоею, и всем разумением твоим, и ближнего твоего, как самого себя».');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34086, 'rst66', 'luk', 10, 26, 'Он же сказал ему: в законе что&#769; написано? ка&#769;к читаешь?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34085, 'rst66', 'luk', 10, 25, 'И вот, один законник встал и, искушая Его, сказал: Учитель! что мне делать, чтобы наследовать жизнь вечную?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34084, 'rst66', 'luk', 10, 24, 'Ибо сказываю вам, что многие пророки и цари желали видеть, что&#769; вы видите, и не видели, и слышать, что&#769; вы слышите, и не слышали.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34083, 'rst66', 'luk', 10, 23, 'И, обратившись к ученикам, сказал им особо: блаженны очи, видящие то, что вы видите!');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34082, 'rst66', 'luk', 10, 22, 'И, обратившись к ученикам, сказал: всё предано Мне Отцом Моим; и кто есть Сын, не знает никто, кроме Отца, и кто есть Отец, <i>не знает никто</i>, кроме Сына, и кому Сын хочет открыть.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34081, 'rst66', 'luk', 10, 21, 'В тот час возрадовался духом Иисус и сказал: славлю Тебя, Отче, Господи неба и земли, что Ты утаил сие от мудрых и разумных и открыл младенцам. Ей, Отче! Ибо таково было Твое благоволение.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34080, 'rst66', 'luk', 10, 20, 'однако ж тому не радуйтесь, что духи вам повинуются, но радуйтесь тому, что имена ваши написаны на небесах.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34079, 'rst66', 'luk', 10, 19, 'се, даю вам власть наступать на змей и скорпионов и на всю силу вражью, и ничто не повредит вам;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34078, 'rst66', 'luk', 10, 18, 'Он же сказал им: Я видел сатану, спадшего с неба, как молнию;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34077, 'rst66', 'luk', 10, 17, 'Семьдесят <i>учеников</i> возвратились с радостью и говорили: Господи! и бесы повинуются нам о имени Твоем.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34076, 'rst66', 'luk', 10, 16, 'Слушающий вас Меня слушает, и отвергающийся вас Меня отвергается; а отвергающийся Меня отвергается Пославшего Меня.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34075, 'rst66', 'luk', 10, 15, 'И ты, Капернаум, до неба вознесшийся, до ада низвергнешься.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34074, 'rst66', 'luk', 10, 14, 'но и Тиру и Сидону отраднее будет на суде, нежели вам.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34073, 'rst66', 'luk', 10, 13, 'Горе тебе, Хоразин! горе тебе, Вифсаида! ибо если бы в Тире и Сидоне явлены были силы, явленные в вас, то давно бы они, сидя во вретище и пепле, покаялись;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34072, 'rst66', 'luk', 10, 12, 'Сказываю вам, что Содому в день оный будет отраднее, нежели городу тому.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34071, 'rst66', 'luk', 10, 11, '«и прах, прилипший к нам от вашего города, отрясаем вам; однако же знайте, что приблизилось к вам Царствие Божие».');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34070, 'rst66', 'luk', 10, 10, 'Если же придете в какой город и не примут вас, то, выйдя на улицу, скажите:');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34069, 'rst66', 'luk', 10, 9, 'и исцеляйте находящихся в нём больных, и говорите им: «приблизилось к вам Царствие Божие».');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34068, 'rst66', 'luk', 10, 8, 'И если придёте в какой город и примут вас, ешьте, что вам предложат,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34067, 'rst66', 'luk', 10, 7, 'В доме же том оставайтесь, ешьте и пейте, что у них есть, ибо трудящийся достоин награды за труды свои; не переходите из дома в дом.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34066, 'rst66', 'luk', 10, 6, 'и если будет там сын мира, то почиет на нём мир ваш, а если нет, то к вам возвратится.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34065, 'rst66', 'luk', 10, 5, 'В какой дом войдете, сперва говорите: «мир дому сему»;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34064, 'rst66', 'luk', 10, 4, 'Не берите ни мешка, ни сумы&#769;, ни обуви, и никого на дороге не приветствуйте.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34063, 'rst66', 'luk', 10, 3, 'Идите! Я посылаю вас, как агнцев среди волков.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34062, 'rst66', 'luk', 10, 2, 'и сказал им: жатвы много, а делателей мало; итак, молите Господина жатвы, чтобы выслал делателей на жатву Свою.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34061, 'rst66', 'luk', 10, 1, 'После сего избрал Господь и других семьдесят <i>учеников</i>, и послал их по два пред лицом Своим во всякий город и место, куда Сам хотел идти,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34060, 'rst66', 'luk', 9, 62, 'Но Иисус сказал ему: никто, возложивший руку свою на плуг и озирающийся назад, не благонадежен для Царствия Божия.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34059, 'rst66', 'luk', 9, 61, 'Еще другой сказал: я пойду за Тобою, Господи! но прежде позволь мне проститься с домашними моими.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34058, 'rst66', 'luk', 9, 60, 'Но Иисус сказал ему: предоставь мертвым погребать своих мертвецов, а ты иди, благовествуй Царствие Божие.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34057, 'rst66', 'luk', 9, 59, 'А другому сказал: следуй за Мною. Тот сказал: Господи! позволь мне прежде пойти и похоронить отца моего.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34056, 'rst66', 'luk', 9, 58, 'Иисус сказал ему: лисицы имеют норы, и птицы небесные &mdash; гнезда; а Сын Человеческий не имеет, где приклонить голову.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34055, 'rst66', 'luk', 9, 57, 'Случилось, что, когда они были в пути, некто сказал Ему: Господи! я пойду за Тобою, куда бы Ты ни пошел.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34054, 'rst66', 'luk', 9, 56, 'ибо Сын Человеческий пришел не губить души человеческие, а спасать. И пошли в другое селение.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34053, 'rst66', 'luk', 9, 55, 'Но Он, обратившись к ним, запретил им и сказал: не знаете, какого вы духа;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34052, 'rst66', 'luk', 9, 54, 'Видя то&#769;, ученики Его, Иаков и Иоанн, сказали: Господи! хочешь ли, мы скажем, чтобы огонь сошел с неба и истребил их, как и Илия сделал?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34051, 'rst66', 'luk', 9, 53, 'но <i>там</i> не приняли Его, потому что Он имел вид путешествующего в Иерусалим.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34050, 'rst66', 'luk', 9, 52, 'и послал вестников пред лицом Своим; и они пошли и вошли в селение Самарянское, чтобы приготовить для Него;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34049, 'rst66', 'luk', 9, 51, 'Когда же приближались дни взятия Его <i>от мира</i>, Он восхотел идти в Иерусалим;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34048, 'rst66', 'luk', 9, 50, 'Иисус сказал ему: не запрещайте, ибо кто не против вас, тот за вас.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34047, 'rst66', 'luk', 9, 49, 'При сем Иоанн сказал: Наставник! мы видели человека, именем Твоим изгоняющего бесов, и запретили ему, потому что он не ходит с нами.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34046, 'rst66', 'luk', 9, 48, 'и сказал им: кто примет сие дитя во имя Мое, тот Меня принимает; а кто примет Меня, тот принимает Пославшего Меня; ибо кто из вас меньше всех, тот будет велик.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34045, 'rst66', 'luk', 9, 47, 'Иисус же, видя помышление сердца их, взяв дитя, поставил его пред Собою');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34044, 'rst66', 'luk', 9, 46, 'Пришла же им мысль: кто бы из них был больше?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34043, 'rst66', 'luk', 9, 45, 'Но они не поняли сло&#769;ва сего, и оно было закрыто от них, так что они не постигли его, а спросить Его о сем слове боялись.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34042, 'rst66', 'luk', 9, 44, 'вложите вы себе в уши слова сии: Сын Человеческий будет предан в руки человеческие.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34041, 'rst66', 'luk', 9, 43, 'И все удивлялись величию Божию. Когда же все дивились всему, что творил Иисус, Он сказал ученикам Своим:');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34040, 'rst66', 'luk', 9, 42, 'Когда же тот еще шел, бес поверг его и стал бить; но Иисус запретил нечистому духу, и исцелил отрока, и отдал его отцу его.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34039, 'rst66', 'luk', 9, 41, 'Иисус же, отвечая, сказал: о, род неверный и развращенный! доколе буду с вами и буду терпеть вас? приведи сюда сына твоего.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34038, 'rst66', 'luk', 9, 40, 'Я просил учеников Твоих изгнать его, и они не могли.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34037, 'rst66', 'luk', 9, 39, 'его схватывает дух, и он внезапно вскрикивает, и терзает его, так что он испускает пену; и насилу отступает от него, измучив его.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34036, 'rst66', 'luk', 9, 38, 'Вдруг некто из народа воскликнул: Учитель! умоляю Тебя взглянуть на сына моего, он один у меня:');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34035, 'rst66', 'luk', 9, 37, 'В следующий же день, когда они сошли с горы, встретило Его много народа.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34034, 'rst66', 'luk', 9, 36, 'Когда был глас сей, остался Иисус один. И они умолчали, и никому не говорили в те дни о том, что видели.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34033, 'rst66', 'luk', 9, 35, 'И был из облака глас, глаголющий: Сей есть Сын Мой Возлюбленный, Его слушайте.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34032, 'rst66', 'luk', 9, 34, 'Когда же он говорил это, явилось облако и осенило их; и устрашились, когда вошли в облако.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34031, 'rst66', 'luk', 9, 33, 'И когда они отходили от Него, сказал Петр Иисусу: Наставник! хорошо нам здесь быть; сделаем три кущи: одну Тебе, одну Моисею и одну Илии, &mdash; не зная, что&#769; говорил.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34030, 'rst66', 'luk', 9, 32, 'Петр же и бывшие с ним отягчены были сном; но, пробудившись, увидели славу Его и двух мужей, стоявших с Ним.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34029, 'rst66', 'luk', 9, 31, 'явившись во славе, они говорили об исходе Его, который Ему надлежало совершить в Иерусалиме.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34028, 'rst66', 'luk', 9, 30, 'И вот, два мужа беседовали с Ним, которые были Моисей и Илия;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34027, 'rst66', 'luk', 9, 29, 'И когда молился, вид лица Его изменился, и одежда Его сделалась белою, блистающею.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34026, 'rst66', 'luk', 9, 28, 'После сих слов, дней через восемь, взяв Петра, Иоанна и Иакова, взошел Он на гору помолиться.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34025, 'rst66', 'luk', 9, 27, 'Говорю же вам истинно: есть некоторые из стоящих здесь, которые не вкусят смерти, как уже увидят Царствие Божие.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34024, 'rst66', 'luk', 9, 26, 'Ибо кто постыдится Меня и Моих слов, того Сын Человеческий постыдится, когда приидет во славе Своей и Отца и святых Ангелов.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34023, 'rst66', 'luk', 9, 25, 'Ибо что пользы человеку приобрести весь мир, а себя самого погубить или повредить себе?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34022, 'rst66', 'luk', 9, 24, 'Ибо кто хочет душу свою сберечь, тот потеряет ее; а кто потеряет душу свою ради Меня, тот сбережет ее.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34021, 'rst66', 'luk', 9, 23, 'Ко всем же сказал: если кто хочет идти за Мною, отвергнись себя, и возьми крест свой, и следуй за Мною.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34020, 'rst66', 'luk', 9, 22, 'сказав, что Сыну Человеческому должно много пострадать, и быть отвержену старейшинами, первосвященниками и книжниками, и быть убиту, и в третий день воскреснуть.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34019, 'rst66', 'luk', 9, 21, 'Но Он строго приказал им никому не говорить о сем,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34018, 'rst66', 'luk', 9, 20, 'Он же спросил их: а вы за кого почитаете Меня? Отвечал Петр: за Христа Божия.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34017, 'rst66', 'luk', 9, 19, 'Они сказали в ответ: за Иоанна Крестителя, а иные за Илию; другие же <i>говорят</i>, что один из древних пророков воскрес.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34016, 'rst66', 'luk', 9, 18, 'В одно время, когда Он молился в уединенном месте и ученики были с Ним, Он спросил их: за кого почитает Меня народ?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34015, 'rst66', 'luk', 9, 17, 'И ели, и насытились все; и оставшихся у них кусков набрано двенадцать коробов.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34014, 'rst66', 'luk', 9, 16, 'Он же, взяв пять хлебов и две рыбы и воззрев на небо, благословил их, преломил и дал ученикам, чтобы раздать народу.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34013, 'rst66', 'luk', 9, 15, 'И сделали так, и рассадили всех.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34012, 'rst66', 'luk', 9, 14, 'Ибо их было около пяти тысяч человек. Но Он сказал ученикам Своим: рассадите их рядами по пятидесяти.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34011, 'rst66', 'luk', 9, 13, 'Но Он сказал им: вы дайте им есть. Они сказали: у нас нет более пяти хлебов и двух рыб; разве нам пойти купить пищи для всех сих людей?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34010, 'rst66', 'luk', 9, 12, 'День же начал склоняться к вечеру. И, приступив к Нему, Двенадцать говорили Ему: отпусти народ, чтобы они пошли в окрестные селения и деревни ночевать и достали пищи; потому что мы здесь в пустом месте.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34009, 'rst66', 'luk', 9, 11, 'Но народ, узнав, пошел за Ним; и Он, приняв их, беседовал с ними о Царствии Божием и требовавших исцеления исцелял.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34008, 'rst66', 'luk', 9, 10, 'Апостолы, возвратившись, рассказали Ему, что&#769; они сделали; и Он, взяв их с Собою, удалился особо в пустое место, близ города, называемого Вифсаидою.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34007, 'rst66', 'luk', 9, 9, 'И сказал Ирод: Иоанна я обезглавил; кто же Этот, о Котором я слышу такое? И искал увидеть Его.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34006, 'rst66', 'luk', 9, 8, 'другие, что Илия явился, а иные, что один из древних пророков воскрес.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34005, 'rst66', 'luk', 9, 7, 'Услышал Ирод четвертовластник о всём, что делал <i>Иисус</i>, и недоумевал: ибо одни говорили, что это Иоанн восстал из мертвых;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34004, 'rst66', 'luk', 9, 6, 'Они пошли и проходили по селениям, благовествуя и исцеляя повсюду.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34003, 'rst66', 'luk', 9, 5, 'А если где не примут вас, то, выходя из того города, отрясите и прах от ног ваших во свидетельство на них.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34002, 'rst66', 'luk', 9, 4, 'и в какой дом войдете, там оставайтесь и оттуда отправляйтесь <i>в путь</i>.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34001, 'rst66', 'luk', 9, 3, 'И сказал им: ничего не берите на дорогу: ни посоха, ни сумы&#769;, ни хлеба, ни серебра, и не имейте по две одежды;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (34000, 'rst66', 'luk', 9, 2, 'и послал их проповедовать Царствие Божие и исцелять больных.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33999, 'rst66', 'luk', 9, 1, 'Созвав же Двенадцать, дал им силу и власть над всеми бесами и врачевать от болезней,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33998, 'rst66', 'luk', 8, 56, 'И удивились родители ее. Он же повелел им не сказывать никому о происшедшем.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33997, 'rst66', 'luk', 8, 55, 'И возвратился дух ее; она тотчас встала, и Он велел дать ей есть.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33996, 'rst66', 'luk', 8, 54, 'Он же, выслав всех вон и взяв ее за руку, возгласил: девица! встань.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33995, 'rst66', 'luk', 8, 53, 'И смеялись над Ним, зная, что она умерла.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33994, 'rst66', 'luk', 8, 52, 'Все плакали и рыдали о ней. Но Он сказал: не плачьте; она не умерла, но спит.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33993, 'rst66', 'luk', 8, 51, 'Придя же в дом, не позволил войти никому, кроме Петра, Иоанна и Иакова, и отца девицы, и матери.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33992, 'rst66', 'luk', 8, 50, 'Но Иисус, услышав это, сказал ему: не бойся, только веруй, и спасена будет.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33991, 'rst66', 'luk', 8, 49, 'Когда Он еще говорил это, приходит некто из дома начальника синагоги и говорит ему: дочь твоя умерла; не утруждай Учителя.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33990, 'rst66', 'luk', 8, 48, 'Он сказал ей: дерзай, дщерь! вера твоя спасла тебя; иди с миром.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33989, 'rst66', 'luk', 8, 47, 'Женщина, видя, что она не утаилась, с трепетом подошла и, пав пред Ним, объявила Ему перед всем народом, по какой причине прикоснулась к Нему и как тотчас исцелилась.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33988, 'rst66', 'luk', 8, 46, 'Но Иисус сказал: прикоснулся ко Мне некто, ибо Я чувствовал силу, исшедшую из Меня.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33987, 'rst66', 'luk', 8, 45, 'И сказал Иисус: кто прикоснулся ко Мне? Когда же все отрицались, Петр сказал и бывшие с Ним: Наставник! народ окружает Тебя и теснит, &mdash; и Ты говоришь: «кто прикоснулся ко Мне?»');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33986, 'rst66', 'luk', 8, 44, 'подойдя сзади, коснулась края одежды Его; и тотчас течение крови у ней остановилось.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33985, 'rst66', 'luk', 8, 43, 'И женщина, страдавшая кровотечением двенадцать лет, которая, издержав на врачей всё имение, ни одним не могла быть вылечена,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33984, 'rst66', 'luk', 8, 42, 'потому что у него была одна дочь, лет двенадцати, и та была при смерти. Когда же Он шел, народ теснил Его.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33983, 'rst66', 'luk', 8, 41, 'И вот, пришел человек, именем Иаир, который был начальником синагоги; и, пав к ногам Иисуса, просил Его войти к нему в дом,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33982, 'rst66', 'luk', 8, 40, 'Когда же возвратился Иисус, народ принял Его, потому что все ожидали Его.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33981, 'rst66', 'luk', 8, 39, 'возвратись в дом твой и расскажи, что&#769; сотворил тебе Бог. Он пошел и проповедовал по всему городу, что сотворил ему Иисус.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33980, 'rst66', 'luk', 8, 38, 'Человек же, из которого вышли бесы, просил Его, чтобы быть с Ним. Но Иисус отпустил его, сказав:');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33979, 'rst66', 'luk', 8, 37, 'И просил Его весь народ Гадаринской окрестности удалиться от них, потому что они объяты были великим страхом. Он вошел в лодку и возвратился.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33978, 'rst66', 'luk', 8, 36, 'Видевшие же рассказали им, как исцелился бесновавшийся.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33977, 'rst66', 'luk', 8, 35, 'И вышли видеть происшедшее; и, придя к Иисусу, нашли человека, из которого вышли бесы, сидящего у ног Иисуса, одетого и в здравом уме; и ужаснулись.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33976, 'rst66', 'luk', 8, 34, 'Пастухи, видя происшедшее, побежали и рассказали в городе и в селениях.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33975, 'rst66', 'luk', 8, 33, 'Бесы, выйдя из человека, вошли в свиней, и бросилось стадо с крутизны в озеро и потонуло.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33974, 'rst66', 'luk', 8, 32, 'Тут же на горе паслось большое стадо свиней; и <i>бесы</i> просили Его, чтобы позволил им войти в них. Он позволил им.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33973, 'rst66', 'luk', 8, 31, 'И они просили Иисуса, чтобы не повелел им идти в бездну.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33972, 'rst66', 'luk', 8, 30, 'Иисус спросил его: как тебе имя? Он сказал: «легион», &mdash; потому что много бесов вошло в него.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33971, 'rst66', 'luk', 8, 29, 'Ибо <i>Иисус</i> повелел нечистому духу выйти из сего человека, потому что он долгое время мучил его, так что его связывали цепями и узами, сберегая его; но он разрывал узы и был гоним бесом в пустыни.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33970, 'rst66', 'luk', 8, 28, 'Он, увидев Иисуса, вскричал, пал пред Ним и громким голосом сказал: что Тебе до меня, Иисус, Сын Бога Всевышнего? умоляю Тебя, не мучь меня.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33969, 'rst66', 'luk', 8, 27, 'Когда же вышел Он на берег, встретил Его один человек из города, одержимый бесами с давнего времени, и в одежду не одевавшийся, и живший не в доме, а в гробах.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33968, 'rst66', 'luk', 8, 26, 'И приплыли в страну Гадаринскую, лежащую против Галилеи.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33967, 'rst66', 'luk', 8, 25, 'Тогда Он сказал им: где вера ваша? Они же в страхе и удивлении говорили друг другу: кто же это, что и ветрам повелевает и воде, и повинуются Ему?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33966, 'rst66', 'luk', 8, 24, 'И, подойдя, разбудили Его и сказали: Наставник! Наставник! погибаем. Но Он, встав, запретил ветру и волнению воды; и перестали, и сделалась тишина.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33965, 'rst66', 'luk', 8, 23, 'Во время плавания их Он заснул. На озере поднялся бурный ветер, и заливало их <i>волнами</i>, и они были в опасности.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33964, 'rst66', 'luk', 8, 22, 'В один день Он вошел с учениками Своими в лодку и сказал им: переправимся на ту сторону озера. И отправились.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33963, 'rst66', 'luk', 8, 21, 'Он сказал им в ответ: матерь Моя и братья Мои суть слушающие слово Божие и исполняющие его.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33962, 'rst66', 'luk', 8, 20, 'И дали знать Ему: Матерь и братья Твои стоят вне, желая видеть Тебя.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33961, 'rst66', 'luk', 8, 19, 'И пришли к Нему Матерь и братья Его, и не могли подойти к Нему по причине народа.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33960, 'rst66', 'luk', 8, 18, 'Итак, наблюдайте, как вы слушаете: ибо, кто имеет, тому дано будет, а кто не имеет, у того отнимется и то&#769;, что он думает иметь.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33959, 'rst66', 'luk', 8, 17, 'Ибо нет ничего тайного, что не сделалось бы явным, ни сокровенного, что не сделалось бы известным и не обнаружилось бы.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33958, 'rst66', 'luk', 8, 16, 'Никто, зажегши свечу, не покрывает ее сосудом, или не ставит под кровать, а ставит на подсвечник, чтобы входящие видели свет.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33957, 'rst66', 'luk', 8, 15, 'а упавшее на добрую землю, это те, которые, услышав слово, хранят его в добром и чистом сердце и приносят плод в терпении. Сказав это, Он возгласил: кто имеет уши слышать, да слышит!');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33956, 'rst66', 'luk', 8, 14, 'а упавшее в терние, это те, которые слушают слово, но, отходя, заботами, богатством и наслаждениями житейскими подавляются и не приносят плода;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33955, 'rst66', 'luk', 8, 13, 'а упавшее на камень, это те, которые, когда услышат слово, с радостью принимают, но которые не имеют корня, и временем веруют, а во время искушения отпадают;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33954, 'rst66', 'luk', 8, 12, 'а упавшее при пути, это суть слушающие, к которым пото&#769;м приходит диавол и уносит слово из сердца их, чтобы они не уверовали и не спаслись;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33953, 'rst66', 'luk', 8, 11, 'Вот что значит притча сия: семя есть слово Божие;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33952, 'rst66', 'luk', 8, 10, 'Он сказал: вам дано знать тайны Царствия Божия, а прочим в притчах, так что они видя не видят и слыша не разумеют.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33951, 'rst66', 'luk', 8, 9, 'Ученики же Его спросили у Него: что бы значила притча сия?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33950, 'rst66', 'luk', 8, 8, 'а иное упало на добрую землю и, взойдя, принесло плод сторичный. Сказав сие, возгласил: кто имеет уши слышать, да слышит!');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33949, 'rst66', 'luk', 8, 7, 'а иное упало между тернием, и выросло терние и заглушило его;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33948, 'rst66', 'luk', 8, 6, 'а иное упало на камень и, взойдя, засохло, потому что не имело влаги;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33947, 'rst66', 'luk', 8, 5, 'вышел сеятель сеять семя свое, и когда он сеял, иное упало при дороге и было потоптано, и птицы небесные поклевали его;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33946, 'rst66', 'luk', 8, 4, 'Когда же собралось множество народа, и из всех городов жители сходились к Нему, Он начал говорить притчею:');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33945, 'rst66', 'luk', 8, 3, 'и Иоанна, жена Хузы, домоправителя Иродова, и Сусанна, и многие другие, которые служили Ему имением своим.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33944, 'rst66', 'luk', 8, 2, 'и некоторые женщины, которых Он исцелил от злых духов и болезней: Мария, называемая Магдалиною, из которой вышли семь бесов,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33943, 'rst66', 'luk', 8, 1, 'После сего Он проходил по городам и селениям, проповедуя и благовествуя Царствие Божие, и с Ним Двенадцать,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33942, 'rst66', 'luk', 7, 50, 'Он же сказал женщине: вера твоя спасла тебя, иди с миром.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33941, 'rst66', 'luk', 7, 49, 'И возлежавшие с Ним начали говорить про себя: кто это, что и грехи прощает?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33940, 'rst66', 'luk', 7, 48, 'Ей же сказал: прощаются тебе грехи.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33939, 'rst66', 'luk', 7, 47, 'А потому сказываю тебе: прощаются грехи её многие за то, что она возлюбила много, а кому мало прощается, тот мало любит.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33938, 'rst66', 'luk', 7, 46, 'ты головы Мне маслом не помазал, а она миром помазала Мне ноги.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33937, 'rst66', 'luk', 7, 45, 'ты целования Мне не дал, а она, с тех пор как Я пришел, не перестает целовать у Меня ноги;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33936, 'rst66', 'luk', 7, 44, 'И, обратившись к женщине, сказал Симону: видишь ли ты эту женщину? Я пришел в дом твой, и ты воды Мне на ноги не дал, а она слезами облила Мне ноги и волосами головы своей отёрла;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33935, 'rst66', 'luk', 7, 43, 'Симон отвечал: думаю, тот, которому более простил. Он сказал ему: правильно ты рассудил.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33934, 'rst66', 'luk', 7, 42, 'но как они не имели чем заплатить, он простил обоим. Скажи же, который из них более возлюбит его?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33933, 'rst66', 'luk', 7, 41, 'Иисус сказал: у одного заимодавца было два должника: один должен был пятьсот динариев, а другой пятьдесят,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33932, 'rst66', 'luk', 7, 40, 'Обратившись к нему, Иисус сказал: Симон! Я имею нечто сказать тебе. Он говорит: скажи, Учитель.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33931, 'rst66', 'luk', 7, 39, 'Видя это, фарисей, пригласивший Его, сказал сам в себе: если бы Он был пророк, то знал бы, кто и какая женщина прикасается к Нему, ибо она грешница.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33930, 'rst66', 'luk', 7, 38, 'и, став позади у ног Его и плача, начала обливать ноги Его слезами и отирать волосами головы своей, и целовала ноги Его, и мазала миром.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33929, 'rst66', 'luk', 7, 37, 'И вот, женщина того города, которая была грешница, узнав, что Он возлежит в доме фарисея, принесла алавастровый сосуд с миром');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33928, 'rst66', 'luk', 7, 36, 'Некто из фарисеев просил Его вкусить с ним пищи; и Он, войдя в дом фарисея, возлег.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33927, 'rst66', 'luk', 7, 35, 'И оправдана премудрость всеми чадами ее.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33926, 'rst66', 'luk', 7, 34, 'Пришел Сын Человеческий: ест и пьет; и говорите: «вот человек, который любит есть и пить вино, друг мытарям и грешникам».');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33925, 'rst66', 'luk', 7, 33, 'Ибо пришел Иоанн Креститель: ни хлеба не ест, ни вина не пьет; и говорите: «в нем бес».');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33924, 'rst66', 'luk', 7, 32, 'Они подобны детям, которые сидят на улице, кличут друг друга и говорят: «мы играли вам на свирели, и вы не плясали; мы пели вам плачевные песни, и вы не плакали».');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33923, 'rst66', 'luk', 7, 31, 'Тогда Господь сказал: с кем сравню людей рода сего? и кому они подобны?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33922, 'rst66', 'luk', 7, 30, 'а фарисеи и законники отвергли волю Божию о себе, не крестившись от него.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33921, 'rst66', 'luk', 7, 29, 'И весь народ, слушавший <i>Его</i>, и мытари воздали славу Богу, крестившись крещением Иоанновым;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33920, 'rst66', 'luk', 7, 28, 'Ибо говорю вам: из рожденных женами нет ни одного пророка больше Иоанна Крестителя; но меньший в Царствии Божием больше его.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33919, 'rst66', 'luk', 7, 27, 'Сей есть, о котором написано: «вот, Я посылаю Ангела Моего пред лицом Твоим, который приготовит путь Твой пред Тобою».');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33918, 'rst66', 'luk', 7, 26, 'Что&#769; же смотреть ходили вы? пророка ли? Да, говорю вам, и больше пророка.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33917, 'rst66', 'luk', 7, 25, 'Что&#769; же смотреть ходили вы? человека ли, одетого в мягкие одежды? Но одевающиеся пышно и роскошно живущие находятся при дворах царских.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33916, 'rst66', 'luk', 7, 24, 'По отшествии же посланных Иоанном, начал говорить к народу об Иоанне: что&#769; смотреть ходили вы в пустыню? трость ли, ветром колеблемую?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33915, 'rst66', 'luk', 7, 23, 'и блажен, кто не соблазнится о Мне!');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33914, 'rst66', 'luk', 7, 22, 'И сказал им Иисус в ответ: пойдите, скажите Иоанну, что&#769; вы видели и слышали: слепые прозревают, хромые ходят, прокаженные очищаются, глухие слышат, мертвые воскресают, нищие благовествуют;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33913, 'rst66', 'luk', 7, 21, 'А в это время Он многих исцелил от болезней и недугов и от злых духов, и многим слепым даровал зрение.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33912, 'rst66', 'luk', 7, 20, 'Они, придя к <i>Иисусу</i>, сказали: Иоанн Креститель послал нас к Тебе спросить: Ты ли Тот, Которому должно прийти, или другого ожидать нам?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33911, 'rst66', 'luk', 7, 19, 'Иоанн, призвав двоих из учеников своих, послал к Иисусу спросить: Ты ли Тот, Который должен прийти, или ожидать нам другого?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33910, 'rst66', 'luk', 7, 18, 'И возвестили Иоанну ученики его о всём том.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33909, 'rst66', 'luk', 7, 17, 'Такое мнение о Нём распространилось по всей Иудее и по всей окрестности.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33908, 'rst66', 'luk', 7, 16, 'И всех объял страх, и славили Бога, говоря: великий пророк восстал между нами, и Бог посетил народ Свой.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33907, 'rst66', 'luk', 7, 15, 'Мертвый, поднявшись, сел и стал говорить; и отдал его <i>Иисус</i> матери его.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33906, 'rst66', 'luk', 7, 14, 'И, подойдя, прикоснулся к одру; несшие остановились, и Он сказал: юноша! тебе говорю, встань!');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33905, 'rst66', 'luk', 7, 13, 'Увидев ее, Господь сжалился над нею и сказал ей: не плачь.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33904, 'rst66', 'luk', 7, 12, 'Когда же Он приблизился к городским воротам, тут выносили умершего, единственного сына у матери, а она была вдова; и много народа шло с нею из города.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33903, 'rst66', 'luk', 7, 11, 'После сего Иисус пошел в город, называемый Наин; и с Ним шли многие из учеников Его и множество народа.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33902, 'rst66', 'luk', 7, 10, 'Посланные, возвратившись в дом, нашли больного слугу выздоровевшим.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33901, 'rst66', 'luk', 7, 9, 'Услышав сие, Иисус удивился ему и, обратившись, сказал идущему за Ним народу: сказываю вам, что и в Израиле не нашел Я такой веры.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33900, 'rst66', 'luk', 7, 8, 'Ибо я и подвластный человек, но, имея у себя в подчинении воинов, говорю одному: «пойди», и идет; и другому: «приди», и приходит; и слуге моему: «сделай то&#769;», и делает.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33899, 'rst66', 'luk', 7, 7, 'потому и себя самого не почел я достойным прийти к Тебе; но скажи слово, и выздоровеет слуга мой.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33898, 'rst66', 'luk', 7, 6, 'Иисус пошел с ними. И когда Он недалеко уже был от дома, сотник прислал к Нему друзей сказать Ему: не трудись, Господи! ибо я недостоин, чтобы Ты вошел под кров мой;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33897, 'rst66', 'luk', 7, 5, 'ибо он любит народ наш и построил нам синагогу.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33896, 'rst66', 'luk', 7, 4, 'И они, придя к Иисусу, просили Его убедительно, говоря: он достоин, чтобы Ты сделал для него это,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33895, 'rst66', 'luk', 7, 3, 'Услышав об Иисусе, он послал к Нему Иудейских старейшин просить Его, чтобы пришел исцелить слугу его.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33894, 'rst66', 'luk', 7, 2, 'У одного сотника слуга, которым он дорожил, был болен при смерти.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33893, 'rst66', 'luk', 7, 1, 'Когда Он окончил все слова Свои к слушавшему народу, то вошел в Капернаум.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33892, 'rst66', 'luk', 6, 49, 'А слушающий и неисполняющий подобен человеку, построившему дом на земле без основания, который, когда напёрла на него вода, тотчас обрушился; и разрушение дома сего было великое.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33891, 'rst66', 'luk', 6, 48, 'Он подобен человеку, строящему дом, который копал, углубился, и положил основание на камне; почему, когда случилось наводнение и вода напёрла на этот дом, то не могла поколебать его, потому что он основан был на камне.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33890, 'rst66', 'luk', 6, 47, 'Всякий, приходящий ко Мне и слушающий слова Мои и исполняющий их, скажу вам, кому подобен.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33889, 'rst66', 'luk', 6, 46, 'Что&#769; вы зовете Меня: «Господи! Господи!» &mdash; и не делаете того, что Я говорю?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33888, 'rst66', 'luk', 6, 45, 'Добрый человек из доброго сокровища сердца своего выносит доброе, а злой человек из злого сокровища сердца своего выносит злое, ибо от избытка сердца говорят уста его.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33887, 'rst66', 'luk', 6, 44, 'ибо всякое дерево познаётся по плоду своему, потому что не собирают смокв с терновника и не снимают винограда с кустарника.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33886, 'rst66', 'luk', 6, 43, 'Нет доброго дерева, которое приносило бы худой плод; и нет худого дерева, которое приносило бы плод добрый,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33885, 'rst66', 'luk', 6, 42, 'Или, как можешь сказать брату твоему: «брат! дай, я выну сучок из глаза твоего», когда сам не видишь бревна в твоем глазе? Лицемер! вынь прежде бревно из твоего глаза, и тогда увидишь, как вынуть сучок из глаза брата твоего.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33884, 'rst66', 'luk', 6, 41, 'Что ты смотришь на сучок в глазе брата твоего, а бревна в твоем глазе не чувствуешь?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33883, 'rst66', 'luk', 6, 40, 'Ученик не бывает выше своего учителя; но, и усовершенствовавшись, будет всякий, как учитель его.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33882, 'rst66', 'luk', 6, 39, 'Сказал также им притчу: может ли слепой водить слепого? не оба ли упадут в яму?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33881, 'rst66', 'luk', 6, 38, 'давайте, и дастся вам: мерою доброю, утрясенною, нагнетенною и переполненною отсыплют вам в лоно ваше; ибо, какою мерою мерите, такою же отмерится и вам.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33880, 'rst66', 'luk', 6, 37, 'Не судите, и не будете судимы; не осуждайте, и не будете осуждены; прощайте, и прощены будете;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33879, 'rst66', 'luk', 6, 36, 'Итак, будьте милосерды, как и Отец ваш милосерд.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33878, 'rst66', 'luk', 6, 35, 'Но вы люби&#769;те врагов ваших, и благотворите, и взаймы давайте, не ожидая ничего; и будет вам награда великая, и будете сынами Всевышнего; ибо Он благ и к неблагодарным и злым.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33877, 'rst66', 'luk', 6, 34, 'И если взаймы даёте тем, от которых надеетесь получить обратно, какая вам за то благодарность? ибо и грешники дают взаймы грешникам, чтобы получить обратно столько же.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33876, 'rst66', 'luk', 6, 33, 'И если делаете добро тем, которые вам делают добро, какая вам за то благодарность? ибо и грешники то&#769; же делают.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33875, 'rst66', 'luk', 6, 32, 'И если любите любящих вас, какая вам за то благодарность? ибо и грешники любящих их любят.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33874, 'rst66', 'luk', 6, 31, 'И ка&#769;к хотите, чтобы с вами поступали люди, та&#769;к и вы поступайте с ними.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33873, 'rst66', 'luk', 6, 30, 'Всякому, просящему у тебя, давай, и от взявшего твое не требуй назад.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33872, 'rst66', 'luk', 6, 29, 'Ударившему тебя по щеке подставь и другую, и отнимающему у тебя верхнюю одежду не препятствуй взять и рубашку.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33871, 'rst66', 'luk', 6, 28, 'благословляйте проклинающих вас и молитесь за обижающих вас.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33870, 'rst66', 'luk', 6, 27, 'Но вам, слушающим, говорю: любите врагов ваших, благотворите ненавидящим вас,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33869, 'rst66', 'luk', 6, 26, 'Горе вам, когда все люди будут говорить о вас хорошо! ибо так поступали с лжепророками отцы их.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33868, 'rst66', 'luk', 6, 25, 'Горе вам, пресыщенные ныне! ибо взалчете. Горе вам, смеющиеся ныне! ибо восплачете и возрыдаете.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33867, 'rst66', 'luk', 6, 24, 'Напротив, горе вам, богатые! ибо вы уже получили свое утешение.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33866, 'rst66', 'luk', 6, 23, 'Возрадуйтесь в тот день и возвеселитесь, ибо велика вам награда на небесах. Так поступали с пророками отцы их.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33865, 'rst66', 'luk', 6, 22, 'Блаженны вы, когда возненавидят вас люди и когда отлучат вас, и будут поносить, и пронесут имя ваше, как бесчестное, за Сына Человеческого.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33864, 'rst66', 'luk', 6, 21, 'Блаженны алчущие ныне, ибо насытитесь. Блаженны плачущие ныне, ибо воссмеетесь.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33863, 'rst66', 'luk', 6, 20, 'И Он, возведя очи Свои на учеников Своих, говорил: блаженны нищие духом, ибо ваше есть Царствие Божие.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33862, 'rst66', 'luk', 6, 19, 'И весь народ искал прикасаться к Нему, потому что от Него исходила сила и исцеляла всех.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33861, 'rst66', 'luk', 6, 18, 'которые пришли послушать Его и исцелиться от болезней своих, также и страждущие от нечистых духов; и исцелялись.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33860, 'rst66', 'luk', 6, 17, 'И, сойдя с ними, стал Он на ровном месте, и множество учеников Его, и много народа из всей Иудеи и Иерусалима и приморских мест Тирских и Сидонских,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33859, 'rst66', 'luk', 6, 16, 'Иуду Иаковлева и Иуду Искариота, который потом сделался предателем.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33858, 'rst66', 'luk', 6, 15, 'Матфея и Фому, Иакова Алфеева и Симона, прозываемого Зилотом,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33857, 'rst66', 'luk', 6, 14, 'Симона, которого и назвал Петром, и Андрея, брата его, Иакова и Иоанна, Филиппа и Варфоломея,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33856, 'rst66', 'luk', 6, 13, 'Когда же настал день, призвал учеников Своих и избрал из них двенадцать, которых и наименовал Апостолами:');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33855, 'rst66', 'luk', 6, 12, 'В те дни взошел Он на гору помолиться и пробыл всю ночь в молитве к Богу.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33854, 'rst66', 'luk', 6, 11, 'Они же пришли в бешенство и говорили между собою, что&#769; бы им сделать с Иисусом.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33853, 'rst66', 'luk', 6, 10, 'И, посмотрев на всех их, сказал тому человеку: протяни руку твою. Он так и сделал; и стала рука его здорова, как другая.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33852, 'rst66', 'luk', 6, 9, 'Тогда сказал им Иисус: спрошу Я вас: что&#769; должно делать в субботу? добро, или зло? спасти душу, или погубить? Они молчали.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33851, 'rst66', 'luk', 6, 8, 'Но Он, зная помышления их, сказал человеку, имеющему сухую руку: встань и выступи на средину. И он встал и выступил.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33850, 'rst66', 'luk', 6, 7, 'Книжники же и фарисеи наблюдали за Ним, не исцелит ли в субботу, чтобы найти обвинение против Него.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33849, 'rst66', 'luk', 6, 6, 'Случилось же и в другую субботу войти Ему в синагогу и учить. Там был человек, у которого правая рука была сухая.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33848, 'rst66', 'luk', 6, 5, 'И сказал им: Сын Человеческий есть господин и субботы.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33847, 'rst66', 'luk', 6, 4, 'Ка&#769;к он вошел в дом Божий, взял хлебы предложения, которых не должно было есть никому, кроме одних священников, и ел, и дал бывшим с ним?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33846, 'rst66', 'luk', 6, 3, 'Иисус сказал им в ответ: разве вы не читали, что сделал Давид, когда взалкал сам и бывшие с ним?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33845, 'rst66', 'luk', 6, 2, 'Некоторые же из фарисеев сказали им: зачем вы делаете то, чего не должно делать в субботы?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33844, 'rst66', 'luk', 6, 1, 'В субботу, первую по втором дне Пасхи, случилось Ему проходить засеянными полями, и ученики Его срывали колосья и ели, растирая руками.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33843, 'rst66', 'luk', 5, 39, 'И никто, пив старое <i>вино</i>, не захочет тотчас молодого, ибо говорит: «старое лучше».');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33842, 'rst66', 'luk', 5, 38, 'но молодое вино должно вливать в мехи новые; тогда сбережется и то и другое.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33841, 'rst66', 'luk', 5, 37, 'И никто не вливает молодого вина в мехи ветхие; а иначе молодое вино прорвет мехи, и само вытечет, и мехи пропадут;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33840, 'rst66', 'luk', 5, 36, 'При сем сказал им притчу: никто не приставляет заплаты к ветхой одежде, отодрав от новой одежды; а иначе и новую раздерет, и к старой не подойдет заплата от новой.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33839, 'rst66', 'luk', 5, 35, 'Но придут дни, когда отнимется у них жених, и тогда будут поститься в те дни.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33838, 'rst66', 'luk', 5, 34, 'Он сказал им: можете ли заставить сынов чертога брачного поститься, когда с ними жених?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33837, 'rst66', 'luk', 5, 33, 'Они же сказали Ему: почему ученики Иоанновы постятся часто и молитвы творят, также и фарисейские, а Твои едят и пьют?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33836, 'rst66', 'luk', 5, 32, 'Я пришел призвать не праведников, а грешников к покаянию.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33835, 'rst66', 'luk', 5, 31, 'Иисус же сказал им в ответ: не здоровые имеют нужду во враче, но больные;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33834, 'rst66', 'luk', 5, 30, 'Книжники же и фарисеи роптали и говорили ученикам Его: зачем вы едите и пьете с мытарями и грешниками?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33833, 'rst66', 'luk', 5, 29, 'И сделал для Него Левий в доме своем большое угощение; и там было множество мытарей и других, которые возлежали с ними.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33832, 'rst66', 'luk', 5, 28, 'И он, оставив всё, встал и последовал за Ним.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33831, 'rst66', 'luk', 5, 27, 'После сего <i>Иисус</i> вышел и увидел мытаря, именем Левия, сидящего у сбора пошлин, и говорит ему: следуй за Мною.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33830, 'rst66', 'luk', 5, 26, 'И ужас объял всех, и славили Бога и, быв исполнены страха, говорили: чу&#769;дные дела видели мы ныне.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33829, 'rst66', 'luk', 5, 25, 'И он тотчас встал перед ними, взял на чём лежал и пошел в дом свой, славя Бога.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33828, 'rst66', 'luk', 5, 24, 'Но чтобы вы знали, что Сын Человеческий имеет власть на земле прощать грехи, &mdash; сказал Он расслабленному: тебе говорю: встань, возьми постель твою и иди в дом твой.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33827, 'rst66', 'luk', 5, 23, 'Что&#769; легче сказать: «прощаются тебе грехи твои», или сказать: «встань и ходи»?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33826, 'rst66', 'luk', 5, 22, 'Иисус, уразумев помышления их, сказал им в ответ: что&#769; вы помышляете в сердцах ваших?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33825, 'rst66', 'luk', 5, 21, 'Книжники и фарисеи начали рассуждать, говоря: кто это, который богохульствует? кто может прощать грехи, кроме одного Бога?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33824, 'rst66', 'luk', 5, 20, 'И Он, видя веру их, сказал человеку тому: прощаются тебе грехи твои.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33823, 'rst66', 'luk', 5, 19, 'и, не найдя, где пронести его за многолюдством, влезли на верх дома и сквозь кровлю спустили его с постелью на средину пред Иисуса.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33822, 'rst66', 'luk', 5, 18, 'вот, принесли некоторые на постели человека, который был расслаблен, и старались внести его <i>в дом</i> и положить перед Иисусом;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33821, 'rst66', 'luk', 5, 17, 'В один день, когда Он учил, и сидели тут фарисеи и законоучители, пришедшие из всех мест Галилеи и Иудеи и из Иерусалима, и сила Господня являлась в исцелении <i>больных</i>, &mdash;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33820, 'rst66', 'luk', 5, 16, 'Но Он уходил в пустынные места и молился.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33819, 'rst66', 'luk', 5, 15, 'Но тем более распространялась молва о Нём, и великое множество народа стекалось к Нему слушать и врачеваться у Него от болезней своих.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33818, 'rst66', 'luk', 5, 14, 'И Он повелел ему никому не сказывать, а пойти показаться священнику и принести <i>жертву</i> за очищение свое, как повелел Моисей, во свидетельство им.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33817, 'rst66', 'luk', 5, 13, 'Он простер руку, прикоснулся к нему и сказал: хочу, очистись. И тотчас проказа сошла с него.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33816, 'rst66', 'luk', 5, 12, 'Когда Иисус был в одном городе, пришел человек весь в проказе и, увидев Иисуса, пал ниц, умоляя Его и говоря: Господи! если хочешь, можешь меня очистить.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33815, 'rst66', 'luk', 5, 11, 'И, вытащив обе лодки на берег, оставили всё и последовали за Ним.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33814, 'rst66', 'luk', 5, 10, 'также и Иакова и Иоанна, сыновей Зеведеевых, бывших товарищами Симону. И сказал Симону Иисус: не бойся; отныне будешь ловить человеков.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33813, 'rst66', 'luk', 5, 9, 'Ибо ужас объял его и всех, бывших с ним, от этого лова рыб, ими пойманных;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33812, 'rst66', 'luk', 5, 8, 'Увидев это, Симон Петр припал к коленям Иисуса и сказал: выйди от меня, Господи! потому что я человек грешный.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33811, 'rst66', 'luk', 5, 7, 'И дали знак товарищам, находившимся на другой лодке, чтобы пришли помочь им; и пришли, и наполнили обе лодки, так что они начинали тонуть.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33810, 'rst66', 'luk', 5, 6, 'Сделав это, они поймали великое множество рыбы, и даже сеть у них прорывалась.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33809, 'rst66', 'luk', 5, 5, 'Симон сказал Ему в ответ: Наставник! мы трудились всю ночь и ничего не поймали, но по слову Твоему закину сеть.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33808, 'rst66', 'luk', 5, 4, 'Когда же перестал учить, сказал Симону: отплыви на глубину, и закиньте сети свои для лова.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33807, 'rst66', 'luk', 5, 3, 'Войдя в одну лодку, которая была Симонова, Он просил его отплыть несколько от берега и, сев, учил народ из лодки.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33806, 'rst66', 'luk', 5, 2, 'увидел Он две лодки, стоящие на озере; а рыболовы, выйдя из них, вымывали сети.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33805, 'rst66', 'luk', 5, 1, 'Однажды, когда народ теснился к Нему, чтобы слышать слово Божие, а Он стоял у озера Геннисаретского,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33804, 'rst66', 'luk', 4, 44, 'И проповедовал в синагогах Галилейских.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33803, 'rst66', 'luk', 4, 43, 'Но Он сказал им: и другим городам благовествовать Я должен Царствие Божие, ибо на то Я послан.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33802, 'rst66', 'luk', 4, 42, 'Когда же настал день, Он, выйдя <i>из дома</i>, пошел в пустынное место, и народ искал Его и, придя к Нему, удерживал Его, чтобы не уходил от них.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33801, 'rst66', 'luk', 4, 41, 'Выходили также и бесы из многих с криком и говорили: Ты Христос, Сын Божий. А Он запрещал им сказывать, что они знают, что Он Христос.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33800, 'rst66', 'luk', 4, 40, 'При захождении же солнца все, имевшие больных различными болезнями, приводили их к Нему, и Он, возлагая на каждого из них руки, исцелял их.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33799, 'rst66', 'luk', 4, 39, 'Подойдя к ней, Он запретил горячке; и оставила ее. Она тотчас встала и служила им.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33798, 'rst66', 'luk', 4, 38, 'Выйдя из синагоги, Он вошел в дом Симона; тёща же Симонова была одержима сильною горячкою; и просили Его о ней.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33797, 'rst66', 'luk', 4, 37, 'И разнесся слух о Нем по всем окрестным местам.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33796, 'rst66', 'luk', 4, 36, 'И напал на всех ужас, и рассуждали между собою: что это значит, что Он со властью и силою повелевает нечистым духам, и они выходят?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33795, 'rst66', 'luk', 4, 35, 'Иисус запретил ему, сказав: замолчи и выйди из него. И бес, повергнув его посреди <i>синагоги</i>, вышел из него, нимало не повредив ему.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33794, 'rst66', 'luk', 4, 34, 'оставь; что&#769; Тебе до нас, Иисус Назарянин? Ты пришел погубить нас; знаю Тебя, кто Ты, Святой Божий.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33793, 'rst66', 'luk', 4, 33, 'Был в синагоге человек, имевший нечистого духа бесовского, и он закричал громким голосом:');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33792, 'rst66', 'luk', 4, 32, 'И дивились учению Его, ибо слово Его было со властью.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33791, 'rst66', 'luk', 4, 31, 'И пришел в Капернаум, город Галилейский, и учил их в дни субботние.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33790, 'rst66', 'luk', 4, 30, 'но Он, пройдя посреди них, удалился.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33789, 'rst66', 'luk', 4, 29, 'и, встав, выгнали Его вон из города, и повели на вершину горы, на которой город их был построен, чтобы свергнуть Его;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33788, 'rst66', 'luk', 4, 28, 'Услышав это, все в синагоге исполнились ярости');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33787, 'rst66', 'luk', 4, 27, 'много также было прокаженных в Израиле при пророке Елисее, и ни один из них не очистился, кроме Неемана Сириянина.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33786, 'rst66', 'luk', 4, 26, 'и ни к одной из них не был послан Илия, а только ко вдове в Сарепту Сидонскую;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33785, 'rst66', 'luk', 4, 25, 'Поистине говорю вам: много вдов было в Израиле во дни Илии, когда заключено было небо три года и шесть месяцев, так что сделался большой голод по всей земле,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33784, 'rst66', 'luk', 4, 24, 'И сказал: истинно говорю вам: никакой пророк не принимается в своем отечестве.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33783, 'rst66', 'luk', 4, 23, 'Он сказал им: конечно, вы скажете Мне присловие: «врач! исцели Самого Себя; сделай и здесь, в Твоем отечестве, то&#769;, что, мы слышали, было в Капернауме».');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33782, 'rst66', 'luk', 4, 22, 'И все засвидетельствовали Ему это, и дивились словам благодати, исходившим из уст Его, и говорили: не Иосифов ли это сын?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33781, 'rst66', 'luk', 4, 21, 'И Он начал говорить им: ныне исполнилось писание сие, слышанное вами.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33780, 'rst66', 'luk', 4, 20, 'И, закрыв книгу и отдав служителю, сел; и глаза всех в синагоге были устремлены на Него.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33779, 'rst66', 'luk', 4, 19, 'проповедовать лето Господне благоприятное».');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33778, 'rst66', 'luk', 4, 18, '«Дух Господень на Мне; ибо Он помазал Меня благовествовать нищим, и послал Меня исцелять сокрушенных сердцем, проповедовать пленным освобождение, слепым прозрение, отпустить измученных на свободу,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33777, 'rst66', 'luk', 4, 17, 'Ему подали книгу пророка Исаии; и Он, раскрыв книгу, нашел место, где было написано:');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33776, 'rst66', 'luk', 4, 16, 'И пришел в Назарет, где был воспитан, и вошел, по обыкновению Своему, в день субботний в синагогу, и встал читать.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33775, 'rst66', 'luk', 4, 15, 'Он учил в синагогах их, и от всех был прославляем.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33774, 'rst66', 'luk', 4, 14, 'И возвратился Иисус в силе духа в Галилею; и разнеслась молва о Нем по всей окрестной стране.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33773, 'rst66', 'luk', 4, 13, 'И, окончив всё искушение, диавол отошел от Него до времени.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33772, 'rst66', 'luk', 4, 12, 'Иисус сказал ему в ответ: сказано: «не искушай Господа Бога твоего».');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33771, 'rst66', 'luk', 4, 11, 'и на руках понесут Тебя, да не преткнешься о камень ногою Твоею».');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33770, 'rst66', 'luk', 4, 10, 'ибо написано: «Ангелам Своим заповедает о Тебе сохранить Тебя;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33769, 'rst66', 'luk', 4, 9, 'И повел Его в Иерусалим, и поставил Его на крыле храма, и сказал Ему: если Ты Сын Божий, бросься отсюда вниз,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33768, 'rst66', 'luk', 4, 8, 'Иисус сказал ему в ответ: отойди от Меня, сатана; написано: «Господу Богу твоему поклоняйся, и Ему одному служи».');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33767, 'rst66', 'luk', 4, 7, 'итак, если Ты поклонишься мне, то всё будет Твое.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33766, 'rst66', 'luk', 4, 6, 'и сказал Ему диавол: Тебе дам власть над всеми сими <i>царствами</i> и славу их, ибо она предана мне, и я, кому хочу, даю ее;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33765, 'rst66', 'luk', 4, 5, 'И, возведя Его на высокую гору, диавол показал Ему все царства вселенной во мгновение времени,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33764, 'rst66', 'luk', 4, 4, 'Иисус сказал ему в ответ: написано, что не хлебом одним будет жить человек, но всяким словом Божиим.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33763, 'rst66', 'luk', 4, 3, 'И сказал Ему диавол: если Ты Сын Божий, то вели этому камню сделаться хлебом.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33762, 'rst66', 'luk', 4, 2, 'Там сорок дней Он был искушаем от диавола и ничего не ел в эти дни, а по прошествии их напоследок взалкал.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33761, 'rst66', 'luk', 4, 1, 'Иисус, исполненный Духа Святого, возвратился от Иордана и поведен был Духом в пустыню.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33760, 'rst66', 'luk', 3, 38, 'Еносов, Сифов, Адамов, Божий.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33759, 'rst66', 'luk', 3, 37, 'Мафусалов, Енохов, Иаредов, Малелеилов, Каинанов,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33758, 'rst66', 'luk', 3, 36, 'Каинанов, Арфаксадов, Симов, Ноев, Ламехов,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33757, 'rst66', 'luk', 3, 35, 'Серухов, Рагавов, Фалеков, Еверов, Салин,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33756, 'rst66', 'luk', 3, 34, 'Иаковлев, Исааков, Авраамов, Фаррин, Нахоров,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33755, 'rst66', 'luk', 3, 33, 'Аминадавов, Арамов, Есромов, Фаресов, Иудин,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33754, 'rst66', 'luk', 3, 32, 'Иессеев, Овидов, Воозов, Салмонов, Наассонов,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33753, 'rst66', 'luk', 3, 31, 'Мелеаев, Маинанов, Маттафаев, Нафанов, Давидов,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33752, 'rst66', 'luk', 3, 30, 'Симеонов, Иудин, Иосифов, Ионанов, Елиакимов,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33751, 'rst66', 'luk', 3, 29, 'Иосиев, Елиезеров, Иоримов, Матфатов, Левиин,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33750, 'rst66', 'luk', 3, 28, 'Мелхиев, Аддиев, Косамов, Елмодамов, Иров,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33749, 'rst66', 'luk', 3, 27, 'Иоаннанов, Рисаев, Зоровавелев, Салафиилев, Нириев,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33748, 'rst66', 'luk', 3, 26, 'Маафов, Маттафиев, Семеиев, Иосифов, Иудин,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33747, 'rst66', 'luk', 3, 25, 'Маттафиев, Амосов, Наумов, Еслимов, Наггеев,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33746, 'rst66', 'luk', 3, 24, 'Матфатов, Левиин, Мелхиев, Ианнаев, Иосифов,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33745, 'rst66', 'luk', 3, 23, 'Иисус, начиная <i>Своё служение</i>, был лет тридцати, и был, как думали, Сын Иосифов, Илиев,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33744, 'rst66', 'luk', 3, 22, 'и Дух Святой нисшел на Него в телесном виде, как голубь, и был глас с небес, глаголющий: Ты Сын Мой Возлюбленный; в Тебе Мое благоволение!');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33743, 'rst66', 'luk', 3, 21, 'Когда же крестился весь народ, и Иисус, крестившись, молился: отверзлось небо,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33742, 'rst66', 'luk', 3, 20, 'прибавил ко всему прочему и то&#769;, что заключил Иоанна в темницу.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33741, 'rst66', 'luk', 3, 19, 'Ирод же четвертовластник, обличаемый от него за Иродиаду, жену брата своего, и за всё, что сделал Ирод худого,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33740, 'rst66', 'luk', 3, 18, 'Многое и другое благовествовал он народу, поучая его.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33739, 'rst66', 'luk', 3, 17, 'Лопата Его в руке Его, и Он очистит гумно Свое и соберет пшеницу в житницу Свою, а солому сожжет огнем неугасимым.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33738, 'rst66', 'luk', 3, 16, 'Иоанн всем отвечал: я крещу вас водою, но идёт Сильнейший меня, у Которого я недостоин развязать ремень обуви; Он будет крестить вас Духом Святым и огнем.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33737, 'rst66', 'luk', 3, 15, 'Когда же народ был в ожидании, и все помышляли в сердцах своих об Иоанне, не Христос ли он, &mdash;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33736, 'rst66', 'luk', 3, 14, 'Спрашивали его также и воины: а нам что делать? И сказал им: никого не обижайте, не клевещите, и довольствуйтесь своим жалованьем.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33735, 'rst66', 'luk', 3, 13, 'Он отвечал им: ничего не требуйте более определенного вам.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33734, 'rst66', 'luk', 3, 12, 'Пришли и мытари креститься, и сказали ему: учитель! что нам делать?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33733, 'rst66', 'luk', 3, 11, 'Он сказал им в ответ: у кого две одежды, тот дай неимущему, и у кого есть пища, делай то же.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33732, 'rst66', 'luk', 3, 10, 'И спрашивал его народ: что же нам делать?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33731, 'rst66', 'luk', 3, 9, 'Уже и секира при корне дерев лежит: всякое дерево, не приносящее доброго плода, срубают и бросают в огонь.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33730, 'rst66', 'luk', 3, 8, 'Сотворите же достойные плоды покаяния и не думайте говорить в себе: «отец у нас Авраам», ибо говорю вам, что Бог может из камней сих воздвигнуть детей Аврааму.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33729, 'rst66', 'luk', 3, 7, '<i>Иоанн</i> приходившему креститься от него народу говорил: порождения ехиднины! кто внушил вам бежать от будущего гнева?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33728, 'rst66', 'luk', 3, 6, 'и узрит всякая плоть спасение Божие».');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33727, 'rst66', 'luk', 3, 5, 'всякий дол да наполнится, и всякая гора и холм да понизятся, кривизны выпрямятся и неровные пути сделаются гладкими;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33726, 'rst66', 'luk', 3, 4, 'как написано в книге слов пророка Исаии, который говорит: «Глас вопиющего в пустыне: приготовьте путь Господу, прямыми сделайте стези Ему;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33725, 'rst66', 'luk', 3, 3, 'И он проходил по всей окрестной стране Иорданской, проповедуя крещение покаяния для прощения грехов,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33724, 'rst66', 'luk', 3, 2, 'при первосвященниках Анне и Каиафе, был глагол Божий к Иоанну, сыну Захарии, в пустыне.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33723, 'rst66', 'luk', 3, 1, 'В пятнадцатый же год правления Тиверия кесаря, когда Понтий Пилат начальствовал в Иудее, Ирод был четвертовластником в Галилее, Филипп, брат его, четвертовластником в Итурее и Трахонитской области, а Лисаний четвертовластником в Авилинее,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33722, 'rst66', 'luk', 2, 52, 'Иисус же преуспевал в премудрости и возрасте и в любви у Бога и человеков.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33721, 'rst66', 'luk', 2, 51, 'И Он пошел с ними и пришел в Назарет; и был в повиновении у них. И Матерь Его сохраняла все слова сии в сердце Своем.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33720, 'rst66', 'luk', 2, 50, 'Но они не поняли сказанных Им слов.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33719, 'rst66', 'luk', 2, 49, 'Он сказал им: зачем было вам искать Меня? или вы не знали, что Мне должно быть в том, что принадлежит Отцу Моему?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33718, 'rst66', 'luk', 2, 48, 'И, увидев Его, удивились; и Матерь Его сказала Ему: Чадо! что&#769; Ты сделал с нами? Вот, отец Твой и Я с великою скорбью искали Тебя.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33717, 'rst66', 'luk', 2, 47, 'все слушавшие Его дивились разуму и ответам Его.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33716, 'rst66', 'luk', 2, 46, 'Через три дня нашли Его в храме, сидящего посреди учителей, слушающего их и спрашивающего их;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33715, 'rst66', 'luk', 2, 45, 'и, не найдя Его, возвратились в Иерусалим, ища Его.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33714, 'rst66', 'luk', 2, 44, 'но думали, что Он идет с другими. Пройдя же дневной путь, стали искать Его между родственниками и знакомыми');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33713, 'rst66', 'luk', 2, 43, 'Когда же, по окончании дней <i>праздника</i>, возвращались, остался Отрок Иисус в Иерусалиме; и не заметили того Иосиф и Матерь Его,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33712, 'rst66', 'luk', 2, 42, 'И когда Он был двенадцати лет, пришли они также по обычаю в Иерусалим на праздник.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33711, 'rst66', 'luk', 2, 41, 'Каждый год родители Его ходили в Иерусалим на праздник Пасхи.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33710, 'rst66', 'luk', 2, 40, 'Младенец же возрастал и укреплялся духом, исполняясь премудрости, и благодать Божия была на Нем.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33709, 'rst66', 'luk', 2, 39, 'И когда они совершили всё по закону Господню, возвратились в Галилею, в город свой Назарет.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33708, 'rst66', 'luk', 2, 38, 'И она в то время, подойдя, славила Господа и говорила о Нем всем, ожидавшим избавления в Иерусалиме.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33707, 'rst66', 'luk', 2, 37, 'вдова лет восьмидесяти четырех, которая не отходила от храма, постом и молитвою служа Богу день и ночь.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33706, 'rst66', 'luk', 2, 36, 'Тут была также Анна пророчица, дочь Фану&#769;илова, от колена Асирова, достигшая глубокой старости, прожив с мужем от девства своего семь лет,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33705, 'rst66', 'luk', 2, 35, 'и Тебе Самой оружие пройдет душу, &mdash; да откроются помышления многих сердец.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33704, 'rst66', 'luk', 2, 34, 'И благословил их Симеон, и сказал Марии, Матери Его: се, лежит Сей на падение и на восстание многих в Израиле и в предмет пререканий, &mdash;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33703, 'rst66', 'luk', 2, 33, 'Иосиф же и Матерь Его дивились сказанному о Нем.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33702, 'rst66', 'luk', 2, 32, 'свет к просвещению язычников и славу народа Твоего Израиля.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33701, 'rst66', 'luk', 2, 31, 'которое Ты уготовал пред лицом всех народов,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33700, 'rst66', 'luk', 2, 30, 'ибо видели очи мои спасение Твое,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33699, 'rst66', 'luk', 2, 29, 'ныне отпускаешь раба Твоего, Владыко, по слову Твоему, с миром,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33698, 'rst66', 'luk', 2, 28, 'он взял Его на руки, благословил Бога и сказал:');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33697, 'rst66', 'luk', 2, 27, 'И пришел он по вдохновению в храм. И, когда родители принесли Младенца Иисуса, чтобы совершить над Ним законный обряд,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33696, 'rst66', 'luk', 2, 26, 'Ему было предсказано Духом Святым, что он не увидит смерти, доколе не увидит Христа Господня.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33695, 'rst66', 'luk', 2, 25, 'Тогда был в Иерусалиме человек, именем Симеон. Он был муж праведный и благочестивый, чающий утешения Израилева; и Дух Святой был на нем.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33694, 'rst66', 'luk', 2, 24, 'и чтобы принести в жертву, по реченному в законе Господнем, две горлицы или двух птенцов голубиных.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33693, 'rst66', 'luk', 2, 23, 'как предписано в законе Господнем, чтобы всякий младенец мужеского пола, разверзающий ложесна, был посвящен Господу,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33692, 'rst66', 'luk', 2, 22, 'А когда исполнились дни очищения их по закону Моисееву, принесли Его в Иерусалим, чтобы представить пред Господа,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33691, 'rst66', 'luk', 2, 21, 'По прошествии восьми дней, когда надлежало обрезать <i>Младенца</i>, дали Ему имя Иисус, нареченное Ангелом прежде зачатия Его во чреве.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33690, 'rst66', 'luk', 2, 20, 'И возвратились пастухи, славя и хваля Бога за всё то&#769;, что слышали и видели, как им сказано было.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33689, 'rst66', 'luk', 2, 19, 'А Мария сохраняла все слова сии, слагая в сердце Своем.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33688, 'rst66', 'luk', 2, 18, 'И все слышавшие дивились тому, что рассказывали им пастухи.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33687, 'rst66', 'luk', 2, 17, 'Увидев же, рассказали о том, что было возвещено им о Младенце Сем.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33686, 'rst66', 'luk', 2, 16, 'И, поспешив, пришли и нашли Марию и Иосифа, и Младенца, лежащего в яслях.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33685, 'rst66', 'luk', 2, 15, 'Когда Ангелы отошли от них на небо, пастухи сказали друг другу: пойдем в Вифлеем и посмотрим, что&#769; там случилось, о чем возвестил нам Господь.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33684, 'rst66', 'luk', 2, 14, 'слава в вышних Богу, и на земле мир, в человеках благоволение!');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33683, 'rst66', 'luk', 2, 13, 'И внезапно явилось с Ангелом многочисленное воинство небесное, славящее Бога и взывающее:');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33682, 'rst66', 'luk', 2, 12, 'и вот вам знак: вы найдете Младенца в пеленах, лежащего в яслях.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33681, 'rst66', 'luk', 2, 11, 'ибо ныне родился вам в городе Давидовом Спаситель, Который есть Христос Господь;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33680, 'rst66', 'luk', 2, 10, 'И сказал им Ангел: не бойтесь; я возвещаю вам великую радость, которая будет всем людям:');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33679, 'rst66', 'luk', 2, 9, 'Вдруг предстал им Ангел Господень, и слава Господня осияла их; и убоялись страхом великим.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33678, 'rst66', 'luk', 2, 8, 'В той стране были на поле пастухи, которые содержали ночную стражу у стада своего.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33677, 'rst66', 'luk', 2, 7, 'и родила Сына Своего Первенца, и спеленала Его, и положила Его в ясли, потому что не было им места в гостинице.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33676, 'rst66', 'luk', 2, 6, 'Когда же они были там, наступило время родить Ей;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33675, 'rst66', 'luk', 2, 5, 'записаться с Мариею, обрученною ему женою, которая была беременна.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33674, 'rst66', 'luk', 2, 4, 'Пошел также и Иосиф из Галилеи, из города Назарета, в Иудею, в город Давидов, называемый Вифлеем, потому что он был из дома и рода Давидова,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33673, 'rst66', 'luk', 2, 3, 'И пошли все записываться, каждый в свой город.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33672, 'rst66', 'luk', 2, 2, 'Эта перепись была первая в правление Квириния Сириею.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33671, 'rst66', 'luk', 2, 1, 'В те дни вышло от кесаря Августа повеление сделать перепись по всей земле.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33670, 'rst66', 'luk', 1, 80, 'Младенец же возрастал и укреплялся духом, и был в пустынях до дня явления своего Израилю.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33669, 'rst66', 'luk', 1, 79, 'просветить сидящих во тьме и тени смертной, направить ноги наши на путь мира.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33668, 'rst66', 'luk', 1, 78, 'по благоутробному милосердию Бога нашего, которым посетил нас Восток свыше,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33667, 'rst66', 'luk', 1, 77, 'дать уразуметь народу Его спасение в прощении грехов их,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33666, 'rst66', 'luk', 1, 76, 'И ты, младенец, наречешься пророком Всевышнего, ибо предыдешь пред лицом Господа приготовить пути Ему,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33665, 'rst66', 'luk', 1, 75, 'служить Ему в святости и правде пред Ним, во все дни жизни нашей.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33664, 'rst66', 'luk', 1, 74, 'небоязненно, по избавлении от руки врагов наших,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33663, 'rst66', 'luk', 1, 73, 'клятву, которою клялся Он Аврааму, отцу нашему, дать нам,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33662, 'rst66', 'luk', 1, 72, 'сотворит милость с отцами нашими и помянет святой завет Свой,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33661, 'rst66', 'luk', 1, 71, 'что спасет нас от врагов наших и от руки всех ненавидящих нас;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33660, 'rst66', 'luk', 1, 70, 'как возвестил устами бывших от века святых пророков Своих,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33659, 'rst66', 'luk', 1, 69, 'и воздвиг рог спасения нам в дому Давида, отрока Своего,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33658, 'rst66', 'luk', 1, 68, 'благословен Господь Бог Израилев, что посетил народ Свой и сотворил избавление ему,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33657, 'rst66', 'luk', 1, 67, 'И Захария, отец его, исполнился Святого Духа и пророчествовал, говоря:');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33656, 'rst66', 'luk', 1, 66, 'Все слышавшие положили это на сердце своем и говорили: что&#769; будет младенец сей? И рука Господня была с ним.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33655, 'rst66', 'luk', 1, 65, 'И был страх на всех, живущих вокруг них; и рассказывали обо всем этом по всей нагорной стране Иудейской.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33654, 'rst66', 'luk', 1, 64, 'И тотчас разрешились уста его и язык его, и он стал говорить, благословляя Бога.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33653, 'rst66', 'luk', 1, 63, 'Он потребовал дощечку и написал: «Иоанн имя ему». И все удивились.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33652, 'rst66', 'luk', 1, 62, 'И спрашивали знаками у отца его, ка&#769;к бы он хотел назвать его.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33651, 'rst66', 'luk', 1, 61, 'И сказали ей: никого нет в родстве твоем, кто назывался бы сим именем.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33650, 'rst66', 'luk', 1, 60, 'На это мать его сказала: нет, а назвать его Иоанном.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33649, 'rst66', 'luk', 1, 59, 'В восьмой день пришли обрезать младенца и хотели назвать его, по имени отца его, Захариею.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33648, 'rst66', 'luk', 1, 58, 'И услышали соседи и родственники ее, что возвеличил Господь милость Свою над нею, и радовались с нею.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33647, 'rst66', 'luk', 1, 57, 'Елисавете же настало время родить, и она родила сына.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33646, 'rst66', 'luk', 1, 56, 'Пребыла же Мария с нею около трех месяцев, и возвратилась в дом свой.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33645, 'rst66', 'luk', 1, 55, 'ка&#769;к говорил отцам нашим, к Аврааму и семени его до века.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33644, 'rst66', 'luk', 1, 54, 'воспринял Израиля, отрока Своего, воспомянув милость,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33643, 'rst66', 'luk', 1, 53, 'алчущих исполнил благ, и богатящихся отпустил ни с чем;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33642, 'rst66', 'luk', 1, 52, 'низложил сильных с престолов, и вознес смиренных;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33641, 'rst66', 'luk', 1, 51, 'явил силу мышцы Своей; рассеял надменных помышлениями се&#769;рдца их;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33640, 'rst66', 'luk', 1, 50, 'и милость Его в роды родов к боящимся Его;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33639, 'rst66', 'luk', 1, 49, 'что сотворил Мне величие Сильный, и свято имя Его;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33638, 'rst66', 'luk', 1, 48, 'что призрел Он на смирение Рабы Своей, ибо отныне будут ублажать Меня все роды;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33637, 'rst66', 'luk', 1, 47, 'и возрадовался дух Мой о Боге, Спасителе Моем,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33636, 'rst66', 'luk', 1, 46, 'И сказала Мария: величит душа Моя Господа,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33635, 'rst66', 'luk', 1, 45, 'И блаженна Уверовавшая, потому что совершится сказанное Ей от Господа.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33634, 'rst66', 'luk', 1, 44, 'Ибо когда голос приветствия Твоего дошел до слуха моего, взыграл младенец радостно во чреве моем.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33633, 'rst66', 'luk', 1, 43, 'И откуда это мне, что пришла Матерь Господа моего ко мне?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33632, 'rst66', 'luk', 1, 42, 'и воскликнула громким голосом, и сказала: благословенна Ты между женами, и благословен плод чрева Твоего!');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33631, 'rst66', 'luk', 1, 41, 'Когда Елисавета услышала приветствие Марии, взыграл младенец во чреве ее; и Елисавета исполнилась Святого Духа,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33630, 'rst66', 'luk', 1, 40, 'и вошла в дом Захарии, и приветствовала Елисавету.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33629, 'rst66', 'luk', 1, 39, 'Встав же Мария во дни сии, с поспешностью пошла в нагорную страну, в город Иудин,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33628, 'rst66', 'luk', 1, 38, 'Тогда Мария сказала: се, Раба Господня; да будет Мне по слову твоему. И отошел от Нее Ангел.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33627, 'rst66', 'luk', 1, 37, 'ибо у Бога не останется бессильным никакое слово.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33626, 'rst66', 'luk', 1, 36, 'Вот и Елисавета, родственница Твоя, называемая неплодною, и она зачала сына в старости своей, и ей уже шестой месяц,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33625, 'rst66', 'luk', 1, 35, 'Ангел сказал Ей в ответ: Дух Святой найдет на Тебя, и сила Всевышнего осенит Тебя; посему и рождаемое Святое наречется Сыном Божиим.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33624, 'rst66', 'luk', 1, 34, 'Мария же сказала Ангелу: ка&#769;к будет это, когда Я мужа не знаю?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33623, 'rst66', 'luk', 1, 33, 'и будет царствовать над домом Иакова вовеки, и Царству Его не будет конца.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33622, 'rst66', 'luk', 1, 32, 'Он будет велик и наречется Сыном Всевышнего, и даст Ему Господь Бог престол Давида, отца Его;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33621, 'rst66', 'luk', 1, 31, 'и вот, зачнешь во чреве, и родишь Сына, и наречешь Ему имя: Иисус.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33620, 'rst66', 'luk', 1, 30, 'И сказал Ей Ангел: не бойся, Мария, ибо Ты обрела благодать у Бога;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33619, 'rst66', 'luk', 1, 29, 'Она же, увидев его, смутилась от слов его и размышляла, что&#769; бы это было за приветствие.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33618, 'rst66', 'luk', 1, 28, 'Ангел, войдя к ней, сказал: радуйся, Благодатная! Господь с Тобою; благословенна Ты между женами.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33617, 'rst66', 'luk', 1, 27, 'к Деве, обрученной мужу, именем Иосифу, из дома Давидова; имя же Деве: Мария.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33616, 'rst66', 'luk', 1, 26, 'В шестой же месяц послан был Ангел Гавриил от Бога в город Галилейский, называемый Назарет,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33615, 'rst66', 'luk', 1, 25, 'так сотворил мне Господь во дни сии, в которые призрел на меня, чтобы снять с меня поношение между людьми.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33614, 'rst66', 'luk', 1, 24, 'После сих дней зачала Елисавета, жена его, и таилась пять месяцев и говорила:');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33613, 'rst66', 'luk', 1, 23, 'А когда окончились дни службы его, возвратился в дом свой.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33612, 'rst66', 'luk', 1, 22, 'Он же, выйдя, не мог говорить к ним; и они поняли, что он видел видение в храме; и он объяснялся с ними знаками, и оставался нем.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33611, 'rst66', 'luk', 1, 21, 'Между тем народ ожидал Захарию и дивился, что он медлит в храме.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33610, 'rst66', 'luk', 1, 20, 'и вот, ты будешь молчать и не будешь иметь возможности говорить до того дня, как это сбудется, за то, что ты не поверил словам моим, которые сбудутся в свое время.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33609, 'rst66', 'luk', 1, 19, 'Ангел сказал ему в ответ: я Гавриил, предстоящий пред Богом, и послан говорить с тобою и благовестить тебе сие;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33608, 'rst66', 'luk', 1, 18, 'И сказал Захария Ангелу: по чему я узна&#769;ю это? ибо я стар, и жена моя в летах преклонных.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33607, 'rst66', 'luk', 1, 17, 'и предыдет пред Ним в духе и силе Илии, чтобы возвратить сердца отцов детям, и непокоривым образ мыслей праведников, дабы представить Господу народ приготовленный.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33606, 'rst66', 'luk', 1, 16, 'и многих из сынов Израилевых обратит к Господу Богу их;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33605, 'rst66', 'luk', 1, 15, 'ибо он будет велик пред Господом; не будет пить вина и сикера, и Духа Святого исполнится еще от чрева матери своей;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33604, 'rst66', 'luk', 1, 14, 'и будет тебе радость и веселие, и многие о рождении его возрадуются,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33603, 'rst66', 'luk', 1, 13, 'Ангел же сказал ему: не бойся, Захария, ибо услышана молитва твоя, и жена твоя Елисавета родит тебе сына, и наречешь ему имя: Иоанн;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33602, 'rst66', 'luk', 1, 12, 'Захария, увидев его, смутился, и страх напал на него.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33601, 'rst66', 'luk', 1, 11, 'тогда явился ему Ангел Господень, стоя по правую сторону жертвенника кадильного.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33600, 'rst66', 'luk', 1, 10, 'а всё множество народа молилось вне во время каждения, &mdash;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33599, 'rst66', 'luk', 1, 9, 'по жребию, как обыкновенно было у священников, досталось ему войти в храм Господень для каждения,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33598, 'rst66', 'luk', 1, 8, 'Однажды, когда он в порядке своей чреды служил пред Богом,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33597, 'rst66', 'luk', 1, 7, 'У них не было детей, ибо Елисавета была неплодна, и оба были уже в летах преклонных.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33596, 'rst66', 'luk', 1, 6, 'Оба они были праведны пред Богом, поступая по всем заповедям и уставам Господним беспорочно.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33595, 'rst66', 'luk', 1, 5, 'Во дни Ирода, царя Иудейского, был священник из Авиевой чреды, именем Захария, и жена его из рода Ааронова, имя ей Елисавета.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33594, 'rst66', 'luk', 1, 4, 'чтобы ты узнал твердое основание того учения, в котором был наставлен.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33593, 'rst66', 'luk', 1, 3, 'то рассудилось и мне, по тщательном исследовании всего сначала, по порядку описать тебе, достопочтенный Феофил,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33592, 'rst66', 'luk', 1, 2, 'как передали нам то&#769; бывшие с самого начала очевидцами и служителями Слова,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33591, 'rst66', 'luk', 1, 1, 'Как уже многие начали составлять повествования о совершенно известных между нами событиях,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33590, 'rst66', 'mrk', 16, 20, 'А они пошли и проповедовали везде, при Господнем содействии и подкреплении слова последующими знамениями. Аминь.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33589, 'rst66', 'mrk', 16, 19, 'И так Господь, после беседования с ними, вознесся на небо и воссел одесную Бога.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33588, 'rst66', 'mrk', 16, 18, 'Будут брать змей; и если что смертоносное выпьют, не повредит им; возложат руки на больных, и они будут здоровы.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33587, 'rst66', 'mrk', 16, 17, 'Уверовавших же будут сопровождать сии знамения: именем Моим будут изгонять бесов; будут говорить новыми языками;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33586, 'rst66', 'mrk', 16, 16, 'Кто будет веровать и креститься, спасен будет; а кто не будет веровать, осужден будет.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33585, 'rst66', 'mrk', 16, 15, 'И сказал им: идите по всему миру и проповедуйте Евангелие всей твари.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33584, 'rst66', 'mrk', 16, 14, 'Наконец, явился самим одиннадцати, возлежавшим <i>на вечери</i>, и упрекал их за неверие и жестокосердие, что видевшим Его воскресшего не поверили.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33583, 'rst66', 'mrk', 16, 13, 'И те, возвратившись, возвестили прочим; но и им не поверили.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33582, 'rst66', 'mrk', 16, 12, 'После сего явился в ином образе двум из них на дороге, когда они шли в селение.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33581, 'rst66', 'mrk', 16, 11, 'но они, услышав, что Он жив и она видела Его, &mdash; не поверили.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33580, 'rst66', 'mrk', 16, 10, 'Она пошла и возвестила бывшим с Ним, плачущим и рыдающим;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33579, 'rst66', 'mrk', 16, 9, 'Воскреснув рано в первый <i>день</i> недели, <i>Иисус</i> явился сперва Марии Магдалине, из которой изгнал семь бесов.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33578, 'rst66', 'mrk', 16, 8, 'И, выйдя, побежали от гроба; их объял трепет и ужас, и никому ничего не сказали, потому что боялись.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33577, 'rst66', 'mrk', 16, 7, 'Но идите, скажите ученикам Его и Петру, что Он предваряет вас в Галилее; там Его увидите, как Он сказал вам.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33576, 'rst66', 'mrk', 16, 6, 'Он же говорит им: не ужасайтесь. Иисуса ищете Назарянина, распятого; Он воскрес, Его нет здесь. Вот место, где Он был положен.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33575, 'rst66', 'mrk', 16, 5, 'И, войдя во гроб, увидели юношу, сидящего на правой стороне, облеченного в белую одежду; и ужаснулись.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33574, 'rst66', 'mrk', 16, 4, 'И, взглянув, видят, что камень отвален; а он был весьма велик.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33573, 'rst66', 'mrk', 16, 3, 'и говорят между собою: кто отвалит нам камень от двери гроба?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33572, 'rst66', 'mrk', 16, 2, 'И весьма рано, в первый <i>день</i> недели, приходят ко гробу, при восходе солнца,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33571, 'rst66', 'mrk', 16, 1, 'По прошествии субботы Мария Магдалина и Мария Иаковлева и Саломия купили ароматы, чтобы идти помазать Его.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33570, 'rst66', 'mrk', 15, 47, 'Мария же Магдалина и Мария Иосиева смотрели, где Его полагали.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33569, 'rst66', 'mrk', 15, 46, 'Он, купив плащаницу и сняв Его, обвил плащаницею, и положил Его во гробе, который был высечен в скале, и привалил камень к двери гроба.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33568, 'rst66', 'mrk', 15, 45, 'И, узнав от сотника, отдал тело Иосифу.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33567, 'rst66', 'mrk', 15, 44, 'Пилат удивился, что Он уже умер, и, призвав сотника, спросил его, давно ли умер?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33566, 'rst66', 'mrk', 15, 43, 'пришел Иосиф из Аримафеи, знаменитый член совета, который и сам ожидал Царствия Божия, осмелился войти к Пилату, и просил тела Иисусова.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33565, 'rst66', 'mrk', 15, 42, 'И как уже настал вечер, &mdash; потому что была пятница, то есть <i>день</i> перед субботою, &mdash;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33564, 'rst66', 'mrk', 15, 41, 'которые и тогда, как Он был в Галилее, следовали за Ним и служили Ему, и другие многие, вместе с Ним пришедшие в Иерусалим.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33563, 'rst66', 'mrk', 15, 40, 'Были <i>тут</i> и женщины, которые смотрели издали: между ними была и Мария Магдалина, и Мария, мать Иакова меньшего и Иосии, и Саломия,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33562, 'rst66', 'mrk', 15, 39, 'Сотник, стоявший напротив Его, увидев, что Он, та&#769;к возгласив, испустил дух, сказал: истинно Человек Сей был Сын Божий.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33561, 'rst66', 'mrk', 15, 38, 'И завеса в храме раздралась надвое, сверху донизу.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33560, 'rst66', 'mrk', 15, 37, 'Иисус же, возгласив громко, испустил дух.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33559, 'rst66', 'mrk', 15, 36, 'А один побежал, наполнил губку уксусом и, наложив на трость, давал Ему пить, говоря: постойте, посмотрим, придет ли Илия снять Его.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33558, 'rst66', 'mrk', 15, 35, 'Некоторые из стоявших тут, услышав, говорили: вот, Илию зовет.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33557, 'rst66', 'mrk', 15, 34, 'В девятом часу возопил Иисус громким голосом: Элои&#769;! Элои&#769;! ламма&#769; савахфани&#769;? &mdash; что значит: «Боже Мой! Боже Мой! для чего Ты Меня оставил?»');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33556, 'rst66', 'mrk', 15, 33, 'В шестом же часу настала тьма по всей земле <i>и продолжалась</i> до часа девятого.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33555, 'rst66', 'mrk', 15, 32, 'Христос, Царь Израилев, пусть сойдет теперь с креста, чтобы мы видели, и уверуем. И распятые с Ним поносили Его.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33554, 'rst66', 'mrk', 15, 31, 'Подобно и первосвященники с книжниками, насмехаясь, говорили друг другу: других спасал, а Себя не может спасти.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33553, 'rst66', 'mrk', 15, 30, 'Спаси Себя Самого и сойди со креста.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33552, 'rst66', 'mrk', 15, 29, 'Проходящие злословили Его, кивая головами своими и говоря: э! разрушающий храм и в три дня созидающий!');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33551, 'rst66', 'mrk', 15, 28, 'И сбылось слово Писания: «и к злодеям причтен».');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33550, 'rst66', 'mrk', 15, 27, 'С Ним распяли двух разбойников, одного по правую, а другого по левую <i>сторону</i> Его.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33549, 'rst66', 'mrk', 15, 26, 'И была надпись вины Его: «Царь Иудейский».');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33548, 'rst66', 'mrk', 15, 25, 'Был час третий, и распяли Его.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33547, 'rst66', 'mrk', 15, 24, 'Распявшие Его делили одежды Его, бросая жребий, кому что взять.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33546, 'rst66', 'mrk', 15, 23, 'И давали Ему пить вино со смирною; но Он не принял.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33545, 'rst66', 'mrk', 15, 22, 'И привели Его на место Голгофу, что значит: «Лобное место».');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33544, 'rst66', 'mrk', 15, 21, 'И заставили проходящего некоего Киринеянина Симона, отца Александрова и Руфова, идущего с поля, нести крест Его.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33543, 'rst66', 'mrk', 15, 20, 'Когда же насмеялись над Ним, сняли с Него багряницу, одели Его в собственные одежды Его и повели Его, чтобы распять Его.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33542, 'rst66', 'mrk', 15, 19, 'И били Его по голове тростью, и плевали на Него, и, становясь на колени, кланялись Ему.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33541, 'rst66', 'mrk', 15, 18, 'и начали приветствовать Его: радуйся, Царь Иудейский!');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33540, 'rst66', 'mrk', 15, 17, 'и одели Его в багряницу, и, сплетши терновый венец, возложили на Него;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33539, 'rst66', 'mrk', 15, 16, 'А воины отвели Его внутрь двора, то есть в преторию, и собрали весь полк,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33538, 'rst66', 'mrk', 15, 15, 'Тогда Пилат, желая сделать угодное народу, отпустил им Варавву, а Иисуса, бив, предал на распятие.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33537, 'rst66', 'mrk', 15, 14, 'Пилат сказал им: какое же зло сделал Он? Но они еще сильнее закричали: распни Его.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33536, 'rst66', 'mrk', 15, 13, 'Они опять закричали: распни Его.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33535, 'rst66', 'mrk', 15, 12, 'Пилат, отвечая, опять сказал им: что же хотите, чтобы я сделал с Тем, Которого вы называете Царем Иудейским?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33534, 'rst66', 'mrk', 15, 11, 'Но первосвященники возбудили народ <i>просить</i>, чтобы отпустил им лучше Варавву.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33533, 'rst66', 'mrk', 15, 10, 'Ибо знал, что первосвященники предали Его из зависти.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33532, 'rst66', 'mrk', 15, 9, 'Он сказал им в ответ: хотите ли, отпущу вам Царя Иудейского?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33531, 'rst66', 'mrk', 15, 8, 'И народ начал кричать и просить <i>Пилата</i> о том, что&#769; он всегда делал для них.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33530, 'rst66', 'mrk', 15, 7, 'Тогда был в узах <i>некто</i>, по имени Варавва, со своими сообщниками, которые во время мятежа сделали убийство.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33529, 'rst66', 'mrk', 15, 6, 'На всякий же праздник отпускал он им одного узника, о котором просили.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33528, 'rst66', 'mrk', 15, 5, 'Но Иисус и на это ничего не отвечал, так что Пилат дивился.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33527, 'rst66', 'mrk', 15, 4, 'Пилат же опять спросил Его: Ты ничего не отвечаешь? видишь, как много против Тебя обвинений.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33526, 'rst66', 'mrk', 15, 3, 'И первосвященники обвиняли Его во многом.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33525, 'rst66', 'mrk', 15, 2, 'Пилат спросил Его: Ты Царь Иудейский? Он же сказал ему в ответ: ты говоришь.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33524, 'rst66', 'mrk', 15, 1, 'Немедленно поутру первосвященники со старейшинами и книжниками и весь синедрион составили совещание и, связав Иисуса, отвели и предали Пилату.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33523, 'rst66', 'mrk', 14, 72, 'Тогда петух запел во второй раз. И вспомнил Петр слово, сказанное ему Иисусом: «прежде нежели петух пропоет дважды, трижды отречешься от Меня»; и начал плакать.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33522, 'rst66', 'mrk', 14, 71, 'Он же начал клясться и божиться: не знаю Человека Сего, о Котором говорите.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33521, 'rst66', 'mrk', 14, 70, 'Он опять отрекся. Спустя немного, стоявшие тут опять стали говорить Петру: точно ты из них; ибо ты Галилеянин, и наречие твое сходно.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33520, 'rst66', 'mrk', 14, 69, 'Служанка, увидев его опять, начала говорить стоявшим тут: этот из них.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33519, 'rst66', 'mrk', 14, 68, 'Но он отрекся, сказав: не знаю и не понимаю, что ты говоришь. И вышел вон на передний двор; и запел петух.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33518, 'rst66', 'mrk', 14, 67, 'и, увидев Петра, греющегося, и всмотревшись в него, сказала: и ты был с Иисусом Назарянином.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33517, 'rst66', 'mrk', 14, 66, 'Когда Петр был на дворе внизу, пришла одна из служанок первосвященника');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33516, 'rst66', 'mrk', 14, 65, 'И некоторые начали плевать на Него и, закрывая Ему лицо, ударять Его и говорить Ему: прореки. И слуги били Его по ланитам.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33515, 'rst66', 'mrk', 14, 64, 'Вы слышали богохульство; как вам кажется? Они же все признали Его повинным смерти.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33514, 'rst66', 'mrk', 14, 63, 'Тогда первосвященник, разодрав одежды свои, сказал: на что еще нам свидетелей?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33513, 'rst66', 'mrk', 14, 62, 'Иисус сказал: Я, и вы у&#769;зрите Сына Человеческого, сидящего одесную Силы и грядущего на облаках небесных.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33512, 'rst66', 'mrk', 14, 61, 'Но Он молчал и не отвечал ничего. Опять первосвященник спросил Его и сказал Ему: Ты ли Христос, Сын Благословенного?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33511, 'rst66', 'mrk', 14, 60, 'Тогда первосвященник стал посреди и спросил Иисуса: что&#769; Ты ничего не отвечаешь? что&#769; они против Тебя свидетельствуют?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33510, 'rst66', 'mrk', 14, 59, 'Но и такое свидетельство их не было достаточно.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33509, 'rst66', 'mrk', 14, 58, 'мы слышали, как Он говорил: «Я разрушу храм сей рукотворенный, и через три дня воздвигну другой, нерукотворенный».');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33508, 'rst66', 'mrk', 14, 57, 'И некоторые, встав, лжесвидетельствовали против Него и говорили:');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33507, 'rst66', 'mrk', 14, 56, 'Ибо многие лжесвидетельствовали на Него, но свидетельства сии не были достаточны.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33506, 'rst66', 'mrk', 14, 55, 'Первосвященники же и весь синедрион искали свидетельства на Иисуса, чтобы предать Его смерти; и не находили.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33505, 'rst66', 'mrk', 14, 54, 'Петр издали следовал за Ним, даже внутрь двора первосвященникова; и сидел со служителями, и грелся у огня.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33504, 'rst66', 'mrk', 14, 53, 'И привели Иисуса к первосвященнику; и собрались к нему все первосвященники и старейшины и книжники.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33503, 'rst66', 'mrk', 14, 52, 'Но он, оставив покрывало, нагой убежал от них.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33502, 'rst66', 'mrk', 14, 51, 'Один юноша, завернувшись по нагому телу в покрывало, следовал за Ним; и воины схватили его.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33501, 'rst66', 'mrk', 14, 50, 'Тогда, оставив Его, все бежали.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33500, 'rst66', 'mrk', 14, 49, 'Каждый день бывал Я с вами в храме и учил, и вы не брали Меня. Но да сбудутся Писания.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33499, 'rst66', 'mrk', 14, 48, 'Тогда Иисус сказал им: как будто на разбойника вышли вы с мечами и кольями, чтобы взять Меня.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33498, 'rst66', 'mrk', 14, 47, 'Один же из стоявших тут извлек меч, ударил раба первосвященникова и отсек ему ухо.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33497, 'rst66', 'mrk', 14, 46, 'А они возложили на Него руки свои и взяли Его.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33496, 'rst66', 'mrk', 14, 45, 'И, придя, тотчас подошел к Нему и говорит: Равви&#769;! Равви&#769;! и поцеловал Его.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33495, 'rst66', 'mrk', 14, 44, 'Предающий же Его дал им знак, сказав: Кого я поцелую, Тот и есть, возьмите Его и ведите осторожно.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33494, 'rst66', 'mrk', 14, 43, 'И тотчас, как Он еще говорил, приходит Иуда, один из Двенадцати, и с ним множество народа с мечами и кольями, от первосвященников и книжников и старейшин.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33493, 'rst66', 'mrk', 14, 42, 'Встаньте, пойдем; вот, приблизился предающий Меня.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33492, 'rst66', 'mrk', 14, 41, 'И приходит в третий раз и говорит им: вы всё еще спите и почиваете? Кончено, пришел час: вот, предается Сын Человеческий в руки грешников.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33491, 'rst66', 'mrk', 14, 40, 'И, возвратившись, опять нашел их спящими, ибо глаза у них отяжелели, и они не знали, что&#769; Ему отвечать.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33490, 'rst66', 'mrk', 14, 39, 'И, опять отойдя, молился, сказав то же слово.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33489, 'rst66', 'mrk', 14, 38, 'Бодрствуйте и молитесь, чтобы не впасть в искушение: дух бодр, плоть же немощна.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33488, 'rst66', 'mrk', 14, 37, 'Возвращается и находит их спящими, и говорит Петру: Симон! ты спишь? не мог ты бодрствовать один час?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33487, 'rst66', 'mrk', 14, 36, 'и говорил: Авва Отче! всё возможно Тебе; пронеси чашу сию мимо Меня; но не чего Я хочу, а чего Ты.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33486, 'rst66', 'mrk', 14, 35, 'И, отойдя немного, пал на землю и молился, чтобы, если возможно, миновал Его час сей;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33485, 'rst66', 'mrk', 14, 34, 'И сказал им: душа Моя скорбит смертельно; побудьте здесь и бодрствуйте.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33484, 'rst66', 'mrk', 14, 33, 'И взял с Собою Петра, Иакова и Иоанна; и начал ужасаться и тосковать.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33483, 'rst66', 'mrk', 14, 32, 'Пришли в селение, называемое Гефсимания; и Он сказал ученикам Своим: посидите здесь, пока Я помолюсь.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33482, 'rst66', 'mrk', 14, 31, 'Но он еще с бо&#769;льшим усилием говорил: хотя бы мне надлежало и умереть с Тобою, не отрекусь от Тебя. То&#769; же и все говорили.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33481, 'rst66', 'mrk', 14, 30, 'И говорит ему Иисус: истинно говорю тебе, что ты ныне, в эту ночь, прежде нежели дважды пропоет петух, трижды отречешься от Меня.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33480, 'rst66', 'mrk', 14, 29, 'Петр сказал Ему: если и все соблазнятся, но не я.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33479, 'rst66', 'mrk', 14, 28, 'По воскресении же Моем, Я предварю вас в Галилее.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33478, 'rst66', 'mrk', 14, 27, 'И говорит им Иисус: все вы соблазнитесь о Мне в эту ночь; ибо написано: «поражу пастыря, и рассеются овцы».');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33477, 'rst66', 'mrk', 14, 26, 'И, воспев, пошли на гору Елеонскую.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33476, 'rst66', 'mrk', 14, 25, 'Истинно говорю вам: Я уже не буду пить от плода виноградного до того дня, когда буду пить новое вино в Царствии Божием.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33475, 'rst66', 'mrk', 14, 24, 'И сказал им: сие есть Кровь Моя нового завета, за многих изливаемая.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33474, 'rst66', 'mrk', 14, 23, 'И, взяв чашу, благодарив, подал им: и пили из нее все.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33473, 'rst66', 'mrk', 14, 22, 'И когда они ели, Иисус, взяв хлеб, благословил, преломил, дал им и сказал: приимите, ядите; сие есть Тело Мое.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33472, 'rst66', 'mrk', 14, 21, 'Впрочем, Сын Человеческий идет, как писано о Нем; но горе тому человеку, которым Сын Человеческий предается: лучше было бы тому человеку не родиться.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33471, 'rst66', 'mrk', 14, 20, 'Он же сказал им в ответ: один из Двенадцати, обмакивающий со Мною в блюдо.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33470, 'rst66', 'mrk', 14, 19, 'Они опечалились и стали говорить Ему, один за другим: не я ли? и другой: не я ли?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33469, 'rst66', 'mrk', 14, 18, 'И, когда они возлежали и ели, Иисус сказал: истинно говорю вам, один из вас, ядущий со Мною, предаст Меня.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33468, 'rst66', 'mrk', 14, 17, 'Когда настал вечер, Он приходит с Двенадцатью.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33467, 'rst66', 'mrk', 14, 16, 'И пошли ученики Его, и пришли в город, и нашли, как сказал им; и приготовили пасху.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33466, 'rst66', 'mrk', 14, 15, 'И он покажет вам горницу большую, устланную, готовую: там приготовьте нам.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33465, 'rst66', 'mrk', 14, 14, 'и, куда он войдет, скажите хозяину дома того: «Учитель говорит: где комната, в которой бы Мне есть пасху с учениками Моими?»');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33464, 'rst66', 'mrk', 14, 13, 'И посылает двух из учеников Своих, и говорит им: пойдите в город; и встретится вам человек, несущий кувшин воды; последуйте за ним');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33463, 'rst66', 'mrk', 14, 12, 'В первый день опресноков, когда закалали пасхального <i>агнца</i>, говорят Ему ученики Его: где хочешь есть пасху? мы пойдем и приготовим.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33462, 'rst66', 'mrk', 14, 11, 'Они же, услышав, обрадовались, и обещали дать ему сребреники. И он искал, как бы в удобное время предать Его.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33461, 'rst66', 'mrk', 14, 10, 'И пошел Иуда Искариот, один из Двенадцати, к первосвященникам, чтобы предать Его им.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33460, 'rst66', 'mrk', 14, 9, 'Истинно говорю вам: где ни будет проповедано Евангелие сие, в целом мире, сказано будет, в память ее, и о том, что&#769; она сделала.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33459, 'rst66', 'mrk', 14, 8, 'Она сделала, что могла: предварила помазать тело Мое к погребению.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33458, 'rst66', 'mrk', 14, 7, 'Ибо нищих всегда имеете с собою и, когда захотите, можете им благотворить; а Меня не всегда имеете.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33457, 'rst66', 'mrk', 14, 6, 'Но Иисус сказал: оставьте ее; что&#769; ее смущаете? Она доброе дело сделала для Меня.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33456, 'rst66', 'mrk', 14, 5, 'Ибо можно было бы продать его более нежели за триста динариев и раздать нищим. И роптали на нее.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33455, 'rst66', 'mrk', 14, 4, 'Некоторые же вознегодовали и говорили между собою: к чему сия трата мира?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33454, 'rst66', 'mrk', 14, 3, 'И когда был Он в Вифании, в доме Симона прокаженного, и возлежал, &mdash; пришла женщина с алавастровым сосудом мира из нарда чистого, драгоценного и, разбив сосуд, возлила Ему на голову.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33453, 'rst66', 'mrk', 14, 2, 'но говорили: <i>только</i> не в праздник, чтобы не произошло возмущения в народе.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33452, 'rst66', 'mrk', 14, 1, 'Через два дня <i>надлежало</i> быть <i>празднику</i> Пасхи и опресноков. И искали первосвященники и книжники, как бы взять Его хитростью и убить;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33451, 'rst66', 'mrk', 13, 37, 'А что вам говорю, говорю всем: бодрствуйте.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33450, 'rst66', 'mrk', 13, 36, 'чтобы, придя внезапно, не нашел вас спящими.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33449, 'rst66', 'mrk', 13, 35, 'Итак, бодрствуйте, ибо не знаете, когда придет хозяин дома: вечером, или в полночь, или в пение петухов, или поутру;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33448, 'rst66', 'mrk', 13, 34, 'Подобно как бы кто, отходя в путь и оставляя дом свой, дал слугам своим власть и каждому свое дело, и приказал привратнику бодрствовать.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33447, 'rst66', 'mrk', 13, 33, 'Смотрите, бодрствуйте, молитесь, ибо не знаете, когда наступит это время.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33446, 'rst66', 'mrk', 13, 32, 'О дне же том, или часе, никто не знает, ни Ангелы небесные, ни Сын, но только Отец.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33445, 'rst66', 'mrk', 13, 31, 'Небо и земля прейдут, но слова Мои не прейдут.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33444, 'rst66', 'mrk', 13, 30, 'Истинно говорю вам: не прейдет род сей, как всё это будет.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33443, 'rst66', 'mrk', 13, 29, 'Так и когда вы увидите то&#769; сбывающимся, знайте, что близко, при дверях.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33442, 'rst66', 'mrk', 13, 28, 'От смоковницы возьмите подобие: когда ветви ее становятся уже мягки и пускают листья, то знаете, что близко лето.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33441, 'rst66', 'mrk', 13, 27, 'И тогда Он пошлет Ангелов Своих и соберет избранных Своих от четырех ветров, от края земли до края неба.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33440, 'rst66', 'mrk', 13, 26, 'Тогда увидят Сына Человеческого, грядущего на облаках с силою многою и славою.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33439, 'rst66', 'mrk', 13, 25, 'и звезды спадут с неба, и силы небесные поколеблются.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33438, 'rst66', 'mrk', 13, 24, 'Но в те дни, после скорби той, солнце померкнет, и луна не даст света своего,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33437, 'rst66', 'mrk', 13, 23, 'Вы же берегитесь. Вот, Я наперед сказал вам всё.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33436, 'rst66', 'mrk', 13, 22, 'Ибо восстанут лжехристы и лжепророки и дадут знамения и чудеса, чтобы прельстить, если возможно, и избранных.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33435, 'rst66', 'mrk', 13, 21, 'Тогда, если кто вам скажет: «вот, здесь Христос», или: «вот, там», &mdash; не верьте.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33434, 'rst66', 'mrk', 13, 20, 'И если бы Господь не сократил тех дней, то не спаслась бы никакая плоть; но ради избранных, которых Он избрал, сократил те дни.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33433, 'rst66', 'mrk', 13, 19, 'Ибо в те дни будет такая скорбь, какой не было от начала творения, которое сотворил Бог, даже доныне, и не будет.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33432, 'rst66', 'mrk', 13, 18, 'Моли&#769;тесь, чтобы не случилось бегство ваше зимою.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33431, 'rst66', 'mrk', 13, 17, 'Горе беременным и питающим сосцами в те дни.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33430, 'rst66', 'mrk', 13, 16, 'и кто на поле, не обращайся назад взять одежду свою.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33429, 'rst66', 'mrk', 13, 15, 'а кто на кровле, тот не сходи в дом и не входи взять что‐нибудь из дома своего;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33428, 'rst66', 'mrk', 13, 14, 'Когда же увидите мерзость запустения, реченную пророком Даниилом, стоящую, где не должно, &mdash; читающий да разумеет, &mdash; тогда находящиеся в Иудее да бегут в горы;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33427, 'rst66', 'mrk', 13, 13, 'И будете ненавидимы всеми за имя Мое; претерпевший же до конца спасется.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33426, 'rst66', 'mrk', 13, 12, 'Предаст же брат брата на смерть, и отец &mdash; детей; и восстанут дети на родителей, и умертвят их.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33425, 'rst66', 'mrk', 13, 11, 'Когда же поведут предавать вас, не заботьтесь наперед, что&#769; вам говорить, и не обдумывайте; но что дано будет вам в тот час, то&#769; и говорите, ибо не вы будете говорить, но Дух Святой.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33424, 'rst66', 'mrk', 13, 10, 'И во всех народах прежде должно быть проповедано Евангелие.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33423, 'rst66', 'mrk', 13, 9, 'Но вы смотри&#769;те за собою, ибо вас будут предавать в судилища и бить в синагогах, и перед правителями и царями поставят вас за Меня, для свидетельства перед ними.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33422, 'rst66', 'mrk', 13, 8, 'Ибо восстанет народ на народ и царство на царство; и будут землетрясения по местам, и будут глады и смятения. Это &mdash; начало болезней.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33421, 'rst66', 'mrk', 13, 7, 'Когда же услышите о войнах и о военных слухах, не ужасайтесь: ибо надлежит <i>сему</i> быть, &mdash; но <i>это</i> еще не конец.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33420, 'rst66', 'mrk', 13, 6, 'ибо многие придут под именем Моим и будут говорить, что это Я; и многих прельстят.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33419, 'rst66', 'mrk', 13, 5, 'Отвечая им, Иисус начал говорить: берегитесь, чтобы кто не прельстил вас,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33418, 'rst66', 'mrk', 13, 4, 'скажи нам, когда это будет, и какой признак, когда всё сие должно совершиться?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33417, 'rst66', 'mrk', 13, 3, 'И когда Он сидел на горе Елеонской против храма, спрашивали Его наедине Петр, и Иаков, и Иоанн, и Андрей:');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33416, 'rst66', 'mrk', 13, 2, 'Иисус сказал ему в ответ: видишь сии великие здания? всё это будет разрушено, так что не останется здесь камня на камне.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33415, 'rst66', 'mrk', 13, 1, 'И когда выходил Он из храма, говорит Ему один из учеников Его: Учитель! посмотри, какие камни и какие здания!');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33414, 'rst66', 'mrk', 12, 44, 'ибо все клали от избытка своего, а она от скудости своей положила всё, что имела, всё пропитание свое.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33413, 'rst66', 'mrk', 12, 43, 'Подозвав учеников Своих, <i>Иисус</i> сказал им: истинно говорю вам, что эта бедная вдова положила больше всех, клавших в сокровищницу,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33412, 'rst66', 'mrk', 12, 42, 'Придя же, одна бедная вдова положила две лепты, что составляет кодрант.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33411, 'rst66', 'mrk', 12, 41, 'И сел Иисус против сокровищницы, и смотрел, как народ кладет деньги в сокровищницу. Многие богатые клали много.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33410, 'rst66', 'mrk', 12, 40, 'сии, поядающие домы вдов и напоказ долго молящиеся, примут тягчайшее осуждение.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33409, 'rst66', 'mrk', 12, 39, 'сидеть впереди в синагогах и возлежать на первом <i>месте</i> на пиршествах, &mdash;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33408, 'rst66', 'mrk', 12, 38, 'И говорил им в учении Своем: остерегайтесь книжников, любящих ходить в длинных одеждах и <i>принимать</i> приветствия в народных собраниях,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33407, 'rst66', 'mrk', 12, 37, 'Итак, сам Давид называет Его Господом: как же Он Сын ему? И множество народа слушало Его с услаждением.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33406, 'rst66', 'mrk', 12, 36, 'Ибо сам Давид сказал Духом Святым: «сказал Господь Господу моему: седи одесную Меня, доколе положу врагов Твоих в подножие ног Твоих».');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33405, 'rst66', 'mrk', 12, 35, 'Продолжая учить в храме, Иисус говорил: как говорят книжники, что Христос есть Сын Давидов?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33404, 'rst66', 'mrk', 12, 34, 'Иисус, видя, что он разумно отвечал, сказал ему: недалеко ты от Царствия Божия. После того никто уже не смел спрашивать Его.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33403, 'rst66', 'mrk', 12, 33, 'и любить Его всем сердцем, и всем умом, и всею душою, и всею крепостью, и любить ближнего, как самого себя, есть больше всех всесожжений и жертв.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33402, 'rst66', 'mrk', 12, 32, 'Книжник сказал Ему: хорошо, Учитель! истину сказал Ты, что один есть Бог и нет иного, кроме Его;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33401, 'rst66', 'mrk', 12, 31, 'Вторая подобная ей: «возлюби ближнего твоего, как самого себя». Иной большей сих заповеди нет.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33400, 'rst66', 'mrk', 12, 30, 'и возлюби Господа Бога твоего всем сердцем твоим, и всею душою твоею, и всем разумением твоим, и всею крепостию твоею», &mdash; вот первая заповедь!');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33399, 'rst66', 'mrk', 12, 29, 'Иисус отвечал ему: первая из всех заповедей: «слушай, Израиль! Господь Бог наш есть Господь единый;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33398, 'rst66', 'mrk', 12, 28, 'Один из книжников, слыша их прения и видя, что <i>Иисус</i> хорошо им отвечал, подошел и спросил Его: какая первая из всех заповедей?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33397, 'rst66', 'mrk', 12, 27, '<i>Бог</i> не есть Бог мертвых, но Бог живых. Итак, вы весьма заблуждаетесь.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33396, 'rst66', 'mrk', 12, 26, 'А о мертвых, что они воскреснут, разве не читали вы в книге Моисея, как Бог при купине сказал ему: «Я Бог Авраама, и Бог Исаака, и Бог Иакова?»');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33395, 'rst66', 'mrk', 12, 25, 'Ибо, когда из мертвых воскреснут, <i>тогда</i> не будут ни жениться, ни замуж выходить, но будут, как Ангелы на небесах.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33394, 'rst66', 'mrk', 12, 24, 'Иисус сказал им в ответ: этим ли приводитесь вы в заблуждение, не зная Писаний, ни силы Божией?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33393, 'rst66', 'mrk', 12, 23, 'Итак, в воскресении, когда воскреснут, которого из них будет она женою? Ибо семеро имели ее женою.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33392, 'rst66', 'mrk', 12, 22, 'Брали ее <i>за себя</i> семеро и не оставили детей. После всех умерла и жена.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33391, 'rst66', 'mrk', 12, 21, 'Взял ее второй и умер, и он не оставил детей; также и третий.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33390, 'rst66', 'mrk', 12, 20, 'Было семь братьев: первый взял жену и, умирая, не оставил детей.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33389, 'rst66', 'mrk', 12, 19, 'Учитель! Моисей написал нам: «если у кого умрет брат и оставит жену, а детей не оставит, то брат его пусть возьмет жену его и восстановит семя брату своему».');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33388, 'rst66', 'mrk', 12, 18, 'Потом пришли к Нему саддукеи, которые говорят, что нет воскресения, и спросили Его, говоря:');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33387, 'rst66', 'mrk', 12, 17, 'Иисус сказал им в ответ: отдавайте кесарево кесарю, а Божие Богу. И дивились Ему.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33386, 'rst66', 'mrk', 12, 16, 'Они принесли. Тогда говорит им: чье это изображение и надпись? Они сказали Ему: кесаревы.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33385, 'rst66', 'mrk', 12, 15, 'Но Он, зная их лицемерие, сказал им: что искушаете Меня? принесите Мне динарий, чтобы Мне видеть его.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33384, 'rst66', 'mrk', 12, 14, 'Они же, придя, говорят Ему: Учитель! мы знаем, что Ты справедлив и не заботишься об угождении кому‐либо, ибо не смотришь ни на какое лицо, но истинно пути Божию учишь. Позволительно ли давать по&#769;дать кесарю или нет? давать ли нам или не давать?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33383, 'rst66', 'mrk', 12, 13, 'И посылают к Нему некоторых из фарисеев и иродиан, чтобы уловить Его в слове.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33382, 'rst66', 'mrk', 12, 12, 'И старались схватить Его, но побоялись народа, ибо поняли, что о них сказал притчу; и, оставив Его, отошли.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33381, 'rst66', 'mrk', 12, 11, 'это от Господа, и есть дивно в очах наших»?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33380, 'rst66', 'mrk', 12, 10, 'Неужели вы не читали сего в Писании: «камень, который отвергли строители, тот самый сделался главою угла:');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33379, 'rst66', 'mrk', 12, 9, 'Что же сделает хозяин виноградника? &mdash; Придет и предаст смерти виноградарей, и отдаст виноградник другим.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33378, 'rst66', 'mrk', 12, 8, 'И, схватив его, убили и выбросили вон из виноградника.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33377, 'rst66', 'mrk', 12, 7, 'Но виноградари сказали друг другу: «это наследник; пойдем, убьем его, и наследство будет наше».');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33376, 'rst66', 'mrk', 12, 6, 'Имея же еще одного сына, любезного ему, напоследок послал и его к ним, говоря: «постыдятся сына моего».');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33375, 'rst66', 'mrk', 12, 5, 'И опять иного послал: и того убили; и многих других то били, то убивали.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33374, 'rst66', 'mrk', 12, 4, 'Опять послал к ним другого слугу; и тому камнями разбили голову и отпустили его с бесчестьем.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33373, 'rst66', 'mrk', 12, 3, 'Они же, схватив его, били и отослали ни с чем.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33372, 'rst66', 'mrk', 12, 2, 'И послал в свое время к виноградарям слугу &mdash; принять от виноградарей плодов из виноградника.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33371, 'rst66', 'mrk', 12, 1, 'И начал говорить им притчами: некоторый человек насадил виноградник и обнес оградою, и выкопал точило, и построил башню, и, отдав его виноградарям, отлучился.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33370, 'rst66', 'mrk', 11, 33, 'И сказали в ответ Иисусу: не знаем. Тогда Иисус сказал им в ответ: и Я не скажу вам, какою властью это делаю.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33369, 'rst66', 'mrk', 11, 32, 'А сказать: «от человеков» &mdash; боялись народа, потому что все полагали, что Иоанн точно был пророк.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33368, 'rst66', 'mrk', 11, 31, 'Они рассуждали между собою: если скажем: «с небес», &mdash; то Он скажет: «почему же вы не поверили ему?»');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33367, 'rst66', 'mrk', 11, 30, 'Крещение Иоанново с небес было, или от человеков? отвечайте Мне.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33366, 'rst66', 'mrk', 11, 29, 'Иисус сказал им в ответ: спрошу и Я вас об одном, отвечайте Мне; <i>тогда</i> и Я скажу вам, какою властью это делаю.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33365, 'rst66', 'mrk', 11, 28, 'и говорили Ему: какою властью Ты это делаешь? и кто Тебе дал власть делать это?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33364, 'rst66', 'mrk', 11, 27, 'Пришли опять в Иерусалим. И когда Он ходил в храме, подошли к Нему первосвященники и книжники, и старейшины');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33363, 'rst66', 'mrk', 11, 26, 'Если же не прощаете, то и Отец ваш Небесный не простит вам согрешений ваших.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33362, 'rst66', 'mrk', 11, 25, 'И когда стоите на молитве, прощайте, если что имеете на кого, дабы и Отец ваш Небесный простил вам согрешения ваши.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33361, 'rst66', 'mrk', 11, 24, 'Потому говорю вам: всё, чего ни будете просить в молитве, верьте, что получите, &mdash; и будет вам.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33360, 'rst66', 'mrk', 11, 23, 'имейте веру Божию, ибо истинно говорю вам, если кто скажет горе сей: «поднимись и ввергнись в море», и не усомнится в сердце своем, но поверит, что сбудется по словам его, &mdash; будет ему, что ни скажет.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33359, 'rst66', 'mrk', 11, 22, 'Иисус, отвечая, говорит им:');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33358, 'rst66', 'mrk', 11, 21, 'И, вспомнив, Петр говорит Ему: Равви&#769;! посмотри, смоковница, которую Ты проклял, засохла.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33357, 'rst66', 'mrk', 11, 20, 'Поутру, проходя мимо, увидели, что смоковница засохла до корня.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33356, 'rst66', 'mrk', 11, 19, 'Когда же стало поздно, Он вышел вон из города.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33355, 'rst66', 'mrk', 11, 18, 'Услышали <i>это</i> книжники и первосвященники, и искали, как бы погубить Его, ибо боялись Его, потому что весь народ удивлялся учению Его.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33354, 'rst66', 'mrk', 11, 17, 'И учил их, говоря: не написано ли: «дом Мой домом молитвы наречется для всех народов»? А вы сделали его вертепом разбойников.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33353, 'rst66', 'mrk', 11, 16, 'и не позволял, чтобы кто пронес через храм какую‐либо вещь.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33352, 'rst66', 'mrk', 11, 15, 'Пришли в Иерусалим. Иисус, войдя в храм, начал выгонять продающих и покупающих в храме; и столы меновщиков и скамьи продающих голубей опрокинул;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33351, 'rst66', 'mrk', 11, 14, 'И сказал ей Иисус: отныне да не вкушает никто от тебя плода вовек! И слышали то ученики Его.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33350, 'rst66', 'mrk', 11, 13, 'и, увидев издалека смоковницу, покрытую листьями, пошел, не найдет ли чего на ней; но, придя к ней, ничего не нашел, кроме листьев, ибо еще не время было <i>собирания</i> смокв.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33349, 'rst66', 'mrk', 11, 12, 'На другой день, когда они вышли из Вифании, Он взалкал;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33348, 'rst66', 'mrk', 11, 11, 'И вошел Иисус в Иерусалим и в храм; и, осмотрев всё, как время уже было позднее, вышел в Вифанию с Двенадцатью.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33347, 'rst66', 'mrk', 11, 10, 'Благословенно грядущее во имя Господа царство отца нашего Давида! осанна в вышних!');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33346, 'rst66', 'mrk', 11, 9, 'И предшествовавшие и сопровождавшие восклицали: осанна! благословен Грядущий во имя Господне!');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33345, 'rst66', 'mrk', 11, 8, 'Многие же постилали одежды свои по дороге; а другие резали ветви с дерев и постилали по дороге.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33344, 'rst66', 'mrk', 11, 7, 'И привели осленка к Иисусу, и возложили на него одежды свои; <i>Иисус</i> сел на него.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33343, 'rst66', 'mrk', 11, 6, 'Они отвечали им, как повелел Иисус; и те отпустили их.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33342, 'rst66', 'mrk', 11, 5, 'И некоторые из стоявших там говорили им: что делаете? <i>зачем</i> отвязываете осленка?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33341, 'rst66', 'mrk', 11, 4, 'Они пошли, и нашли молодого осла, привязанного у ворот на улице, и отвязали его.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33340, 'rst66', 'mrk', 11, 3, 'И если кто скажет вам: «что вы это делаете?» &mdash; отвечайте, что он надобен Господу; и тотчас пошлет его сюда.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33339, 'rst66', 'mrk', 11, 2, 'и говорит им: пойдите в селение, которое прямо перед вами; входя в него, тотчас найдете привязанного молодого осла, на которого никто из людей не садился; отвязав его, приведите.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33338, 'rst66', 'mrk', 11, 1, 'Когда приблизились к Иерусалиму, к Виффагии и Вифании, к горе Елеонской, <i>Иисус</i> посылает двух из учеников Своих');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33337, 'rst66', 'mrk', 10, 52, 'Иисус сказал ему: иди, вера твоя спасла тебя. И он тотчас прозрел и пошел за Иисусом по дороге.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33336, 'rst66', 'mrk', 10, 51, 'Отвечая ему, Иисус спросил: чего ты хочешь от Меня? Слепой сказал Ему: Учитель! чтобы мне прозреть.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33335, 'rst66', 'mrk', 10, 50, 'Он сбросил с себя верхнюю одежду, встал и пришел к Иисусу.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33334, 'rst66', 'mrk', 10, 49, 'Иисус остановился и велел его позвать. Зовут слепого и говорят ему: не бойся, вставай, зовет тебя.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33333, 'rst66', 'mrk', 10, 48, 'Многие заставляли его молчать; но он еще более стал кричать: Сын Давидов! помилуй меня.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33332, 'rst66', 'mrk', 10, 47, 'Услышав, что это Иисус Назорей, он начал кричать и говорить: Иисус, Сын Давидов! помилуй меня.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33331, 'rst66', 'mrk', 10, 46, 'Приходят в Иерихон. И когда выходил Он из Иерихона с учениками Своими и множеством народа, Вартимей, сын Тимеев, слепой сидел у дороги, прося <i>милостыни</i>.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33330, 'rst66', 'mrk', 10, 45, 'Ибо и Сын Человеческий не для того пришел, чтобы Ему служили, но чтобы послужить и отдать душу Свою для искупления многих.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33329, 'rst66', 'mrk', 10, 44, 'и кто хочет быть первым между вами, да будет всем рабом.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33328, 'rst66', 'mrk', 10, 43, 'Но между вами да не будет так: а кто хочет быть бо&#769;льшим между вами, да будет вам слугою;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33327, 'rst66', 'mrk', 10, 42, 'Иисус же, подозвав их, сказал им: вы знаете, что почитающиеся князьями народов господствуют над ними, и вельможи их властвуют ими.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33326, 'rst66', 'mrk', 10, 41, 'И, услышав, десять начали негодовать на Иакова и Иоанна.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33325, 'rst66', 'mrk', 10, 40, 'а дать сесть у Меня по правую сторону и по левую &mdash; не от Меня <i>зависит</i>, но кому уготовано.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33324, 'rst66', 'mrk', 10, 39, 'Они отвечали: можем. Иисус же сказал им: чашу, которую Я пью, будете пить, и крещением, которым Я крещусь, будете креститься;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33323, 'rst66', 'mrk', 10, 38, 'Но Иисус сказал им: не знаете, чего просите. Можете ли пить чашу, которую Я пью, и креститься крещением, которым Я крещусь?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33322, 'rst66', 'mrk', 10, 37, 'Они сказали Ему: дай нам сесть у Тебя, одному по правую сторону, а другому по левую в славе Твоей.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33321, 'rst66', 'mrk', 10, 36, 'Он сказал им: что хотите, чтобы Я сделал вам?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33320, 'rst66', 'mrk', 10, 35, '<i>Тогда</i> подошли к Нему сыновья Зеведеевы Иаков и Иоанн и сказали: Учитель! мы желаем, чтобы Ты сделал нам, о чем попросим.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33319, 'rst66', 'mrk', 10, 34, 'и поругаются над Ним, и будут бить Его, и оплюют Его, и убьют Его; и в третий день воскреснет.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33318, 'rst66', 'mrk', 10, 33, 'вот, мы восходим в Иерусалим, и Сын Человеческий предан будет первосвященникам и книжникам, и осудят Его на смерть, и предадут Его язычникам,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33317, 'rst66', 'mrk', 10, 32, 'Когда были они на пути, восходя в Иерусалим, Иисус шел впереди их, а они ужасались и, следуя за Ним, были в страхе. Подозвав Двенадцать, Он опять начал им говорить о том, что будет с Ним:');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33316, 'rst66', 'mrk', 10, 31, 'Многие же будут первые последними, и последние первыми.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33315, 'rst66', 'mrk', 10, 30, 'и не получил бы ныне, во время сие, среди гонений, во сто крат более домов, и братьев и сестер, и отцов, и матерей, и детей, и земель, а в веке грядущем жизни вечной.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33314, 'rst66', 'mrk', 10, 29, 'Иисус сказал в ответ: истинно говорю вам: нет никого, кто оставил бы дом, или братьев, или сестер, или отца, или мать, или жену, или детей, или зе&#769;мли, ради Меня и Евангелия,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33313, 'rst66', 'mrk', 10, 28, 'И начал Петр говорить Ему: вот, мы оставили всё и последовали за Тобою.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33312, 'rst66', 'mrk', 10, 27, 'Иисус, воззрев на них, говорит: человекам это невозможно, но не Богу, ибо всё возможно Богу.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33311, 'rst66', 'mrk', 10, 26, 'Они же чрезвычайно изумлялись и говорили между собою: кто же может спастись?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33310, 'rst66', 'mrk', 10, 25, 'Удобнее верблюду пройти сквозь игольные уши, нежели богатому войти в Царствие Божие.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33309, 'rst66', 'mrk', 10, 24, 'Ученики ужаснулись от слов Его. Но Иисус опять говорит им в ответ: дети! как трудно надеющимся на богатство войти в Царствие Божие!');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33308, 'rst66', 'mrk', 10, 23, 'И, посмотрев вокруг, Иисус говорит ученикам Своим: как трудно имеющим богатство войти в Царствие Божие!');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33307, 'rst66', 'mrk', 10, 22, 'Он же, смутившись от сего слова, отошел с печалью, потому что у него было большое имение.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33306, 'rst66', 'mrk', 10, 21, 'Иисус, взглянув на него, полюбил его и сказал ему: одного тебе недостает: пойди, всё, что имеешь, продай и раздай нищим, и будешь иметь сокровище на небесах; и приходи, последуй за Мною, взяв крест.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33305, 'rst66', 'mrk', 10, 20, 'Он же сказал Ему в ответ: Учитель! всё это сохранил я от юности моей.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33304, 'rst66', 'mrk', 10, 19, 'Знаешь заповеди: «не прелюбодействуй», «не убивай», «не кради», «не лжесвидетельствуй», «не обижай», «почитай отца твоего и мать».');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33303, 'rst66', 'mrk', 10, 18, 'Иисус сказал ему: что ты называешь Меня благим? Никто не благ, как только один Бог.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33302, 'rst66', 'mrk', 10, 17, 'Когда выходил Он в путь, подбежал некто, пал пред Ним на колени и спросил Его: Учитель благий! что мне делать, чтобы наследовать жизнь вечную?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33301, 'rst66', 'mrk', 10, 16, 'И, обняв их, возложил руки на них и благословил их.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33300, 'rst66', 'mrk', 10, 15, 'Истинно говорю вам: кто не примет Царствия Божия, как дитя, тот не войдет в него.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33299, 'rst66', 'mrk', 10, 14, 'Увидев <i>то</i>, Иисус вознегодовал и сказал им: пустите детей приходить ко Мне и не препятствуйте им, ибо таковых есть Царствие Божие.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33298, 'rst66', 'mrk', 10, 13, 'Приносили к Нему детей, чтобы Он прикоснулся к ним; ученики же не допускали приносящих.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33297, 'rst66', 'mrk', 10, 12, 'и если жена разведется с мужем своим и выйдет за другого, прелюбодействует.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33296, 'rst66', 'mrk', 10, 11, 'Он сказал им: кто разведется с женою своею и женится на другой, тот прелюбодействует от нее;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33295, 'rst66', 'mrk', 10, 10, 'В доме ученики Его опять спросили Его о том же.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33294, 'rst66', 'mrk', 10, 9, 'Итак, что Бог сочетал, того человек да не разлучает.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33293, 'rst66', 'mrk', 10, 8, 'и прилепится к жене своей, и будут два одною плотью; так что они уже не двое, но одна плоть.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33292, 'rst66', 'mrk', 10, 7, 'Посему оставит человек отца своего и мать');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33291, 'rst66', 'mrk', 10, 6, 'В начале же создания, Бог мужчину и женщину сотворил их.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33290, 'rst66', 'mrk', 10, 5, 'Иисус сказал им в ответ: по жестокосердию вашему он написал вам сию заповедь.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33289, 'rst66', 'mrk', 10, 4, 'Они сказали: Моисей позволил писать разводное письмо и разводиться.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33288, 'rst66', 'mrk', 10, 3, 'Он сказал им в ответ: что заповедал вам Моисей?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33287, 'rst66', 'mrk', 10, 2, 'Подошли фарисеи и спросили, искушая Его: позволительно ли разводиться мужу с женою?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33286, 'rst66', 'mrk', 10, 1, 'Отправившись оттуда, приходит в пределы Иудейские за Иорданскою стороною. Опять собирается к Нему народ, и, по обычаю Своему, Он опять учил их.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33285, 'rst66', 'mrk', 9, 50, 'Соль &mdash; добрая <i>вещь;</i> но ежели соль не солона будет, чем вы ее поправите? Имейте в себе соль, и мир имейте между собою.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33284, 'rst66', 'mrk', 9, 49, 'Ибо всякий огнем осолится, и всякая жертва солью осолится.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33283, 'rst66', 'mrk', 9, 48, 'где червь их не умирает и огонь не угасает.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33282, 'rst66', 'mrk', 9, 47, 'И если глаз твой соблазняет тебя, вырви его: лучше тебе с одним глазом войти в Царствие Божие, нежели с двумя глазами быть ввержену в геенну огненную,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33281, 'rst66', 'mrk', 9, 46, 'где червь их не умирает и огонь не угасает.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33280, 'rst66', 'mrk', 9, 45, 'И если нога твоя соблазняет тебя, отсеки ее: лучше тебе войти в жизнь хромому, нежели с двумя ногами быть ввержену в геенну, в огонь неугасимый,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33279, 'rst66', 'mrk', 9, 44, 'где червь их не умирает и огонь не угасает.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33278, 'rst66', 'mrk', 9, 43, 'И если соблазняет тебя рука твоя, отсеки ее: лучше тебе увечному войти в жизнь, нежели с двумя руками идти в геенну, в огонь неугасимый,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33277, 'rst66', 'mrk', 9, 42, 'А кто соблазнит одного из малых сих, верующих в Меня, тому лучше было бы, если бы повесили ему жерновный камень на шею и бросили его в море.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33276, 'rst66', 'mrk', 9, 41, 'И кто напоит вас чашею воды во имя Мое, потому что вы Христовы, истинно говорю вам, не потеряет награды своей.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33275, 'rst66', 'mrk', 9, 40, 'Ибо кто не против вас, тот за вас.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33274, 'rst66', 'mrk', 9, 39, 'Иисус сказал: не запрещайте ему, ибо никто, сотворивший чудо именем Моим, не может вскоре злословить Меня.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33273, 'rst66', 'mrk', 9, 38, 'При сем Иоанн сказал: Учитель! мы видели человека, который именем Твоим изгоняет бесов, а не ходит за нами; и запретили ему, потому что не ходит за нами.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33272, 'rst66', 'mrk', 9, 37, 'кто примет одно из таких детей во имя Мое, тот принимает Меня; а кто Меня примет, тот не Меня принимает, но Пославшего Меня.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33271, 'rst66', 'mrk', 9, 36, 'И, взяв дитя, поставил его посреди них и, обняв его, сказал им:');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33270, 'rst66', 'mrk', 9, 35, 'И, сев, призвал Двенадцать и сказал им: кто хочет быть первым, будь из всех последним и всем слугою.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33269, 'rst66', 'mrk', 9, 34, 'Они молчали; потому что дорогою рассуждали между собою, кто больше.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33268, 'rst66', 'mrk', 9, 33, 'Пришел в Капернаум; и когда был в доме, спросил их: о чем дорогою вы рассуждали между собою?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33267, 'rst66', 'mrk', 9, 32, 'Но они не разумели сих слов, а спросить Его боялись.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33266, 'rst66', 'mrk', 9, 31, 'Ибо учил Своих учеников и говорил им, что Сын Человеческий предан будет в руки человеческие и убьют Его, и, по убиении, в третий день воскреснет.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33265, 'rst66', 'mrk', 9, 30, 'Выйдя оттуда, проходили через Галилею; и Он не хотел, чтобы кто узнал.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33264, 'rst66', 'mrk', 9, 29, 'И сказал им: сей род не может выйти иначе, как от молитвы и поста.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33263, 'rst66', 'mrk', 9, 28, 'И как вошел <i>Иисус</i> в дом, ученики Его спрашивали Его наедине: почему мы не могли изгнать его?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33262, 'rst66', 'mrk', 9, 27, 'Но Иисус, взяв его за руку, поднял его; и он встал.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33261, 'rst66', 'mrk', 9, 26, 'И, вскрикнув и сильно сотрясши его, вышел; и он сделался как мертвый, так что многие говорили, что он умер.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33260, 'rst66', 'mrk', 9, 25, 'Иисус, видя, что сбегается народ, запретил духу нечистому, сказав ему: дух немой и глухой! Я повелеваю тебе, выйди из него и впредь не входи в него.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33259, 'rst66', 'mrk', 9, 24, 'И тотчас отец отрока воскликнул со слезами: верую, Господи! помоги моему неверию.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33258, 'rst66', 'mrk', 9, 23, 'Иисус сказал ему: если сколько‐нибудь можешь веровать, всё возможно верующему.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33257, 'rst66', 'mrk', 9, 22, 'и многократно <i>дух</i> бросал его и в огонь и в воду, чтобы погубить его; но, если что можешь, сжалься над нами и помоги нам.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33256, 'rst66', 'mrk', 9, 21, 'И спросил <i>Иисус</i> отца его: как давно это сделалось с ним? Он сказал: с детства;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33255, 'rst66', 'mrk', 9, 20, 'И привели его к Нему. Как скоро <i>бесноватый</i> увидел Его, дух сотряс его; он упал на землю и валялся, испуская пену.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33254, 'rst66', 'mrk', 9, 19, 'Отвечая ему, Иисус сказал: о, род неверный! доколе буду с вами? доколе буду терпеть вас? Приведите его ко Мне.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33253, 'rst66', 'mrk', 9, 18, 'где ни схватывает его, повергает его на землю, и он испускает пену, и скрежещет зубами своими, и цепенеет. Говорил я ученикам Твоим, чтобы изгнали его, и они не могли.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33252, 'rst66', 'mrk', 9, 17, 'Один из народа сказал в ответ: Учитель! я привел к Тебе сына моего, одержимого духом немым:');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33251, 'rst66', 'mrk', 9, 16, 'Он спросил книжников: о чем спорите с ними?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33250, 'rst66', 'mrk', 9, 15, 'Тотчас, увидев Его, весь народ изумился, и, подбегая, приветствовали Его.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33249, 'rst66', 'mrk', 9, 14, 'Придя к ученикам, увидел много народа около них и книжников, спорящих с ними.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33248, 'rst66', 'mrk', 9, 13, 'Но говорю вам, что и Илия пришел, и поступили с ним, как хотели, как написано о нем.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33247, 'rst66', 'mrk', 9, 12, 'Он сказал им в ответ: правда, Илия должен прийти прежде и устроить всё; и Сыну Человеческому, как написано о Нем, <i>надлежит</i> много пострадать и быть уничижену.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33246, 'rst66', 'mrk', 9, 11, 'И спросили Его: как же книжники говорят, что Илии надлежит прийти прежде?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33245, 'rst66', 'mrk', 9, 10, 'И они удержали это слово, спрашивая друг друга, что значит: воскреснуть из мертвых.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33244, 'rst66', 'mrk', 9, 9, 'Когда же сходили они с горы, Он не велел никому рассказывать о том, что видели, доколе Сын Человеческий не воскреснет из мертвых.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33243, 'rst66', 'mrk', 9, 8, 'И, внезапно посмотрев вокруг, никого более с собою не видели, кроме одного Иисуса.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33242, 'rst66', 'mrk', 9, 7, 'И явилось облако, осеняющее их, и из облака исшел глас, глаголющий: Сей есть Сын Мой возлюбленный; Его слушайте.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33241, 'rst66', 'mrk', 9, 6, 'Ибо не знал, что сказать; потому что они были в страхе.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33240, 'rst66', 'mrk', 9, 5, 'При сем Петр сказал Иисусу: Равви&#769;! хорошо нам здесь быть; сделаем три кущи: Тебе одну, Моисею одну, и одну Илии.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33239, 'rst66', 'mrk', 9, 4, 'И явился им Илия с Моисеем; и беседовали с Иисусом.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33238, 'rst66', 'mrk', 9, 3, 'Одежды Его сделались блистающими, весьма белыми, как снег, как на земле белильщик не может выбелить.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33237, 'rst66', 'mrk', 9, 2, 'И, по прошествии дней шести, взял Иисус Петра, Иакова и Иоанна, и возвел на гору высокую особо их одних, и преобразился перед ними.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33236, 'rst66', 'mrk', 9, 1, 'И сказал им: истинно говорю вам: есть некоторые из стоящих здесь, которые не вкусят смерти, как уже увидят Царствие Божие, пришедшее в силе.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33235, 'rst66', 'mrk', 8, 38, 'Ибо кто постыдится Меня и Моих слов в роде сем прелюбодейном и грешном, того постыдится и Сын Человеческий, когда приидет в славе Отца Своего со святыми Ангелами.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33234, 'rst66', 'mrk', 8, 37, 'Или какой выкуп даст человек за душу свою?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33233, 'rst66', 'mrk', 8, 36, 'Ибо какая польза человеку, если он приобретет весь мир, а душе своей повредит?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33232, 'rst66', 'mrk', 8, 35, 'Ибо кто хочет душу свою сберечь, тот потеряет ее, а кто потеряет душу свою ради Меня и Евангелия, тот сбережет ее.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33231, 'rst66', 'mrk', 8, 34, 'И, подозвав народ с учениками Своими, сказал им: кто хочет идти за Мною, отвергнись себя, и возьми крест свой, и следуй за Мною.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33230, 'rst66', 'mrk', 8, 33, 'Он же, обратившись и взглянув на учеников Своих, воспретил Петру, сказав: отойди от Меня, сатана, потому что ты думаешь не о том, что Божие, но что человеческое.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33229, 'rst66', 'mrk', 8, 32, 'И говорил о сем открыто. Но Петр, отозвав Его, начал прекословить Ему.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33228, 'rst66', 'mrk', 8, 31, 'И начал учить их, что Сыну Человеческому много должно пострадать, быть отвержену старейшинами, первосвященниками и книжниками, и быть убиту, и в третий день воскреснуть.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33227, 'rst66', 'mrk', 8, 30, 'И запретил им, чтобы никому не говорили о Нем.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33226, 'rst66', 'mrk', 8, 29, 'Он говорит им: а вы за кого почитаете Меня? Петр сказал Ему в ответ: Ты &mdash; Христос.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33225, 'rst66', 'mrk', 8, 28, 'Они отвечали: за Иоанна Крестителя; другие же &mdash; за Илию; а иные &mdash; за одного из пророков.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33224, 'rst66', 'mrk', 8, 27, 'И пошел Иисус с учениками Своими в селения Кесарии Филипповой. Дорогою Он спрашивал учеников Своих: за кого почитают Меня люди?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33223, 'rst66', 'mrk', 8, 26, 'И послал его домой, сказав: не заходи в селение и не рассказывай никому в селении.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33222, 'rst66', 'mrk', 8, 25, 'Потом опять возложил руки на глаза ему и велел ему взглянуть. И он исцелел и стал видеть все ясно.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33221, 'rst66', 'mrk', 8, 24, 'Он, взглянув, сказал: вижу проходящих людей, как деревья.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33220, 'rst66', 'mrk', 8, 23, 'Он, взяв слепого за руку, вывел его вон из селения и, плюнув ему на глаза, возложил на него руки и спросил его: видит ли что?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33219, 'rst66', 'mrk', 8, 22, 'Приходит в Вифсаиду; и приводят к Нему слепого, и просят, чтобы прикоснулся к нему.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33218, 'rst66', 'mrk', 8, 21, 'И сказал им: как же не разумеете?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33217, 'rst66', 'mrk', 8, 20, 'А когда семь для четырех тысяч, сколько корзин набрали вы оставшихся кусков? Сказали: семь.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33216, 'rst66', 'mrk', 8, 19, 'Когда Я пять хлебов преломил для пяти тысяч <i>человек</i>, сколько полных коробов набрали вы кусков? Говорят Ему: двенадцать.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33215, 'rst66', 'mrk', 8, 18, 'Имея очи, не видите? имея уши, не слышите? и не помните?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33214, 'rst66', 'mrk', 8, 17, 'Иисус, уразумев, говорит им: что рассуждаете о том, что нет у вас хлебов? Еще ли не понимаете и не разумеете? Еще ли окаменено у вас сердце?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33213, 'rst66', 'mrk', 8, 16, 'И, рассуждая между собою, говорили: <i>это значит</i>, что хлебов нет у нас.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33212, 'rst66', 'mrk', 8, 15, 'А Он заповедал им, говоря: смотрите, берегитесь закваски фарисейской и закваски Иродовой.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33211, 'rst66', 'mrk', 8, 14, 'При сем ученики Его забыли взять хлебов и кроме одного хлеба не имели с собою в лодке.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33210, 'rst66', 'mrk', 8, 13, 'И, оставив их, опять вошел в лодку и отправился на ту сторону.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33209, 'rst66', 'mrk', 8, 12, 'И Он, глубоко вздохнув, сказал: для чего род сей требует знамения? Истинно говорю вам, не дастся роду сему знамение.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33208, 'rst66', 'mrk', 8, 11, 'Вышли фарисеи, начали с Ним спорить и требовали от Него знамения с неба, искушая Его.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33207, 'rst66', 'mrk', 8, 10, 'И, тотчас войдя в лодку с учениками Своими, прибыл в пределы Далмануфские.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33206, 'rst66', 'mrk', 8, 9, 'Евших же было около четырех тысяч. И отпустил их.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33205, 'rst66', 'mrk', 8, 8, 'И ели, и насытились; и набрали оставшихся кусков семь корзин.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33204, 'rst66', 'mrk', 8, 7, 'Было у них и немного рыбок: благословив, Он велел раздать и их.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33203, 'rst66', 'mrk', 8, 6, 'Тогда велел народу возлечь на землю; и, взяв семь хлебов и воздав благодарение, преломил и дал ученикам Своим, чтобы они раздали; и они раздали народу.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33202, 'rst66', 'mrk', 8, 5, 'И спросил их: сколько у вас хлебов? Они сказали: семь.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33201, 'rst66', 'mrk', 8, 4, 'Ученики Его отвечали Ему: откуда мог бы кто <i>взять</i> здесь в пустыне хлебов, чтобы накормить их?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33200, 'rst66', 'mrk', 8, 3, 'Если неевшими отпущу их в домы их, ослабеют в дороге, ибо некоторые из них пришли издалека.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33199, 'rst66', 'mrk', 8, 2, 'жаль Мне народа, что уже три дня находятся при Мне, и нечего им есть.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33198, 'rst66', 'mrk', 8, 1, 'В те дни, когда собралось весьма много народа и нечего было им есть, Иисус, призвав учеников Своих, сказал им:');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33197, 'rst66', 'mrk', 7, 37, 'И чрезвычайно дивились, и говорили: всё хорошо делает, &mdash; и глухих делает слышащими, и немых &mdash; говорящими.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33196, 'rst66', 'mrk', 7, 36, 'И повелел им не сказывать никому. Но сколько Он ни запрещал им, они еще более разглашали.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33195, 'rst66', 'mrk', 7, 35, 'И тотчас отверзся у него слух и разрешились узы его языка, и стал говорить чисто.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33194, 'rst66', 'mrk', 7, 34, 'и, воззрев на небо, вздохнул и сказал ему: «еффафа&#769;» (то есть: «отверзись»).');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33193, 'rst66', 'mrk', 7, 33, '<i>Иисус</i>, отведя его в сторону от народа, вложил персты Свои в уши ему и, плюнув, коснулся языка его;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33192, 'rst66', 'mrk', 7, 32, 'Привели к Нему глухого, косноязычного и просили Его возложить на него руку.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33191, 'rst66', 'mrk', 7, 31, 'Выйдя из пределов Тирских и Сидонских, <i>Иисус</i> опять пошел к морю Галилейскому через пределы Десятиградия.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33190, 'rst66', 'mrk', 7, 30, 'И, придя в свой дом, она нашла, что бес вышел и дочь лежит на постели.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33189, 'rst66', 'mrk', 7, 29, 'И сказал ей: за это слово, пойди; бес вышел из твоей дочери.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33188, 'rst66', 'mrk', 7, 28, 'Она же сказала Ему в ответ: так, Господи; но и псы под столом едят крохи у детей.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33187, 'rst66', 'mrk', 7, 27, 'Но Иисус сказал ей: дай прежде насытиться детям, ибо нехорошо взять хлеб у детей и бросить псам.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33186, 'rst66', 'mrk', 7, 26, 'а женщина та была язычница, родом Сирофиникиянка; и просила Его, чтобы изгнал беса из ее дочери.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33185, 'rst66', 'mrk', 7, 25, 'Ибо услышала о Нем женщина, у которой дочь одержима была нечистым духом, и, придя, припала к ногам Его;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33184, 'rst66', 'mrk', 7, 24, 'И, отправившись оттуда, пришел в пределы Тирские и Сидонские; и, войдя в дом, не хотел, чтобы кто узнал; но не мог утаиться.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33183, 'rst66', 'mrk', 7, 23, 'всё это зло извнутрь исходит и оскверняет человека.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33182, 'rst66', 'mrk', 7, 22, 'кражи, лихоимство, злоба, коварство, непотребство, завистливое око, богохульство, гордость, безумство, &mdash;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33181, 'rst66', 'mrk', 7, 21, 'Ибо извнутрь, из сердца человеческого, исходят злые помыслы, прелюбодеяния, любодеяния, убийства,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33180, 'rst66', 'mrk', 7, 20, 'Далее сказал: исходящее из человека оскверняет человека.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33179, 'rst66', 'mrk', 7, 19, 'Потому что не в сердце его входит, а в чрево, и выходит вон, <i>чем</i> очищается всякая пища.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33178, 'rst66', 'mrk', 7, 18, 'Он сказал им: неужели и вы так непонятливы? Неужели не разумеете, что ничто, извне входящее в человека, не может осквернить его?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33177, 'rst66', 'mrk', 7, 17, 'И когда Он от народа вошел в дом, ученики Его спросили Его о притче.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33176, 'rst66', 'mrk', 7, 16, 'Если кто имеет уши слышать, да слышит!');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33175, 'rst66', 'mrk', 7, 15, 'ничто, входящее в человека извне, не может осквернить его; но что исходит из него, то оскверняет человека.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33174, 'rst66', 'mrk', 7, 14, 'И, призвав весь народ, говорил им: слушайте Меня все и разумейте:');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33173, 'rst66', 'mrk', 7, 13, 'устраняя слово Божие преданием вашим, которое вы установили; и делаете многое, сему подобное.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33172, 'rst66', 'mrk', 7, 12, 'тому вы уже попускаете ничего не делать для отца своего или матери своей,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33171, 'rst66', 'mrk', 7, 11, 'А вы говорите: «кто скажет отцу или матери: корван (то есть: «дар <i>Богу</i>») то&#769;, чем бы ты от меня пользовался»,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33170, 'rst66', 'mrk', 7, 10, 'Ибо Моисей сказал: «почитай отца своего и мать свою»; и: «злословящий отца или мать смертью да умрет».');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33169, 'rst66', 'mrk', 7, 9, 'И сказал им: хорошо ли, <i>что</i> вы отменяете заповедь Божию, чтобы соблюсти свое предание?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33168, 'rst66', 'mrk', 7, 8, 'Ибо вы, оставив заповедь Божию, держитесь предания человеческого, омовения кружек и чаш, и делаете многое другое, сему подобное.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33167, 'rst66', 'mrk', 7, 7, 'но тщетно чтут Меня, уча учениям, заповедям человеческим».');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33166, 'rst66', 'mrk', 7, 6, 'Он сказал им в ответ: хорошо пророчествовал о вас, лицемерах, Исаия, как написано: «люди сии чтут Меня устами, сердце же их далеко отстоит от Меня,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33165, 'rst66', 'mrk', 7, 5, 'Потом спрашивают Его фарисеи и книжники: зачем ученики Твои не поступают по преданию старцев, но неумытыми руками едят хлеб?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33164, 'rst66', 'mrk', 7, 4, 'и, <i>придя</i> с торга, не едят, не омывшись. Есть и многое другое, чего они приняли держаться: наблюдать омовение чаш, кружек, котлов и скамей.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33163, 'rst66', 'mrk', 7, 3, 'Ибо фарисеи и все Иудеи, держась предания старцев, не едят, не умыв тщательно рук;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33162, 'rst66', 'mrk', 7, 2, 'и, увидев некоторых из учеников Его, евших хлеб нечистыми, то есть неумытыми, руками, укоряли.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33161, 'rst66', 'mrk', 7, 1, 'Собрались к Нему фарисеи и некоторые из книжников, пришедшие из Иерусалима,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33160, 'rst66', 'mrk', 6, 56, 'И куда ни приходил Он, в селения ли, в города ли, в деревни ли, клали больных на открытых местах и просили Его, чтобы им прикоснуться хотя к краю одежды Его; и которые прикасались к Нему, исцелялись.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33159, 'rst66', 'mrk', 6, 55, 'обежали всю окрестность ту и начали на постелях приносить больных туда, где Он, как слышно было, находился.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33158, 'rst66', 'mrk', 6, 54, 'Когда вышли они из лодки, тотчас <i>жители</i>, узнав Его,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33157, 'rst66', 'mrk', 6, 53, 'И, переправившись, прибыли в землю Геннисаретскую и пристали <i>к берегу</i>.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33156, 'rst66', 'mrk', 6, 52, 'ибо не вразумились <i>чудом</i> над хлебами, потому что сердце их было окаменено.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33155, 'rst66', 'mrk', 6, 51, 'И вошел к ним в лодку, и ветер утих. И они чрезвычайно изумлялись в себе и дивились,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33154, 'rst66', 'mrk', 6, 50, 'Ибо все видели Его и испугались. И тотчас заговорил с ними и сказал им: ободритесь; это Я, не бойтесь.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33153, 'rst66', 'mrk', 6, 49, 'Они, увидев Его, идущего по морю, подумали, что это призрак, и вскричали.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33152, 'rst66', 'mrk', 6, 48, 'И увидел их бедствующих в плавании, потому что ветер им был противный; около же четвертой стражи ночи подошел к ним, идя по морю, и хотел миновать их.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33151, 'rst66', 'mrk', 6, 47, 'Вечером лодка была посреди моря, а Он один на земле.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33150, 'rst66', 'mrk', 6, 46, 'И, отпустив их, пошел на гору помолиться.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33149, 'rst66', 'mrk', 6, 45, 'И тотчас понудил учеников Своих войти в лодку и отправиться вперед на другую сторону к Вифсаиде, пока Он отпустит народ.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33148, 'rst66', 'mrk', 6, 44, 'Было же евших хлебы около пяти тысяч мужей.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33147, 'rst66', 'mrk', 6, 43, 'И набрали кусков хлеба и <i>остатков</i> от рыб двенадцать полных коробов.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33146, 'rst66', 'mrk', 6, 42, 'И ели все, и насытились.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33145, 'rst66', 'mrk', 6, 41, 'Он взял пять хлебов и две рыбы, воззрев на небо, благословил и преломил хлебы и дал ученикам Своим, чтобы они раздали им; и две рыбы разделил на всех.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33144, 'rst66', 'mrk', 6, 40, 'И сели рядами, по сто и по пятидесяти.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33143, 'rst66', 'mrk', 6, 39, 'Тогда повелел им рассадить всех отделениями на зеленой траве.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33142, 'rst66', 'mrk', 6, 38, 'Но Он спросил их: сколько у вас хлебов? пойдите, посмотрите. Они, узнав, сказали: пять хлебов и две рыбы.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33141, 'rst66', 'mrk', 6, 37, 'Он сказал им в ответ: вы дайте им есть. И сказали Ему: разве нам пойти купить хлеба динариев на двести и дать им есть?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33140, 'rst66', 'mrk', 6, 36, 'отпусти их, чтобы они пошли в окрестные деревни и селения и купили себе хлеба, ибо им нечего есть.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33139, 'rst66', 'mrk', 6, 35, 'И как времени прошло много, ученики Его, приступив к Нему, говорят: место <i>здесь</i> пустынное, а времени уже много, &mdash;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33138, 'rst66', 'mrk', 6, 34, 'Иисус, выйдя, увидел множество народа и сжалился над ними, потому что они были как овцы, не имеющие пастыря; и начал учить их много.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33137, 'rst66', 'mrk', 6, 33, 'Народ увидел, <i>как</i> они отправлялись, и многие узнали их; и бежали туда пешие из всех городов, и предупредили их, и собрались к Нему.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33136, 'rst66', 'mrk', 6, 32, 'И отправились в пустынное место в лодке одни.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33135, 'rst66', 'mrk', 6, 31, 'Он сказал им: пойдите вы одни в пустынное место и отдохните немного, &mdash; ибо много было приходящих и отходящих, так что и есть им было некогда.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33134, 'rst66', 'mrk', 6, 30, 'И собрались Апостолы к Иисусу, и рассказали Ему всё, и что сделали, и чему научили.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33133, 'rst66', 'mrk', 6, 29, 'Ученики его, услышав, пришли и взяли тело его, и положили его во гробе.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33132, 'rst66', 'mrk', 6, 28, 'Он пошел, отсек ему голову в темнице, и принес голову его на блюде, и отдал ее девице, а девица отдала ее матери своей.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33131, 'rst66', 'mrk', 6, 27, 'И тотчас, послав оруженосца, царь повелел принести голову его.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33130, 'rst66', 'mrk', 6, 26, 'Царь опечалился, но ради клятвы и возлежавших с ним не захотел отказать ей.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33129, 'rst66', 'mrk', 6, 25, 'И она тотчас пошла с поспешностью к царю и просила, говоря: хочу, чтобы ты дал мне теперь же на блюде голову Иоанна Крестителя.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33128, 'rst66', 'mrk', 6, 24, 'Она вышла и спросила у матери своей: чего просить? Та отвечала: головы Иоанна Крестителя.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33127, 'rst66', 'mrk', 6, 23, 'и клялся ей: чего ни попросишь у меня, дам тебе, даже до половины моего царства.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33126, 'rst66', 'mrk', 6, 22, 'дочь Иродиады вошла, плясала и угодила Ироду и возлежавшим с ним; царь сказал девице: проси у меня, чего хочешь, и дам тебе;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33125, 'rst66', 'mrk', 6, 21, 'Настал удобный день, когда Ирод, по случаю <i>дня</i> рождения своего, делал пир вельможам своим, тысяченачальникам и старейшинам Галилейским, &mdash;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33124, 'rst66', 'mrk', 6, 20, 'Ибо Ирод боялся Иоанна, зная, что он муж праведный и святой, и берёг его; многое делал, слушаясь его, и с удовольствием слушал его.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33123, 'rst66', 'mrk', 6, 19, 'Иродиада же, злобясь на него, желала убить его; но не могла.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33122, 'rst66', 'mrk', 6, 18, 'Ибо Иоанн говорил Ироду: не должно тебе иметь жену брата твоего.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33121, 'rst66', 'mrk', 6, 17, 'Ибо сей Ирод, послав, взял Иоанна и заключил его в темницу за Иродиаду, жену Филиппа, брата своего, потому что женился на ней.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33120, 'rst66', 'mrk', 6, 16, 'Ирод же, услышав, сказал: это Иоанн, которого я обезглавил; он воскрес из мертвых.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33119, 'rst66', 'mrk', 6, 15, 'Другие говорили: это Илия, а иные говорили: это пророк, или как один из пророков.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33118, 'rst66', 'mrk', 6, 14, 'Царь Ирод, услышав <i>об Иисусе</i> (ибо имя Его стало гласно), говорил: это Иоанн Креститель воскрес из мертвых, и потому чудеса делаются им.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33117, 'rst66', 'mrk', 6, 13, 'изгоняли многих бесов и многих больных мазали маслом и исцеляли.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33116, 'rst66', 'mrk', 6, 12, 'Они пошли и проповедовали покаяние;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33115, 'rst66', 'mrk', 6, 11, 'И если кто не примет вас и не будет слушать вас, то, выходя оттуда, отрясите прах от ног ваших, во свидетельство на них. Истинно говорю вам: отраднее будет Содому и Гоморре в день суда, нежели тому городу.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33114, 'rst66', 'mrk', 6, 10, 'И сказал им: если где войдете в дом, оставайтесь в нем, доколе не выйдете из того места.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33113, 'rst66', 'mrk', 6, 9, 'но обуваться в простую обувь и не носить двух одежд.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33112, 'rst66', 'mrk', 6, 8, 'И заповедал им ничего не брать в дорогу, кроме одного посоха: ни сумы, ни хлеба, ни меди в поясе,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33111, 'rst66', 'mrk', 6, 7, 'И, призвав Двенадцать, начал посылать их по два, и дал им власть над нечистыми духами.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33110, 'rst66', 'mrk', 6, 6, 'И дивился неверию их; потом ходил по окрестным селениям и учил.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33109, 'rst66', 'mrk', 6, 5, 'И не мог совершить там никакого чуда, только на немногих больных возложив руки, исцелил <i>их</i>.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33108, 'rst66', 'mrk', 6, 4, 'Иисус же сказал им: не бывает пророк без чести, разве только в отечестве своем и у сродников и в доме своем.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33107, 'rst66', 'mrk', 6, 3, 'Не плотник ли Он, сын Марии, брат Иакова, Иосии, Иуды и Симона? Не здесь ли, между нами, Его сестры? И соблазнялись о Нем.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33106, 'rst66', 'mrk', 6, 2, 'Когда наступила суббота, Он начал учить в синагоге; и многие слышавшие с изумлением говорили: откуда у Него это? что за премудрость дана Ему, и как такие чудеса совершаются руками Его?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33105, 'rst66', 'mrk', 6, 1, 'Оттуда вышел Он и пришел в Свое отечество; за Ним следовали ученики Его.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33104, 'rst66', 'mrk', 5, 43, 'И Он строго приказал им, чтобы никто об этом не знал, и сказал, чтобы дали ей есть.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33103, 'rst66', 'mrk', 5, 42, 'И девица тотчас встала и начала ходить, ибо была лет двенадцати. <i>Видевшие</i> пришли в великое изумление.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33102, 'rst66', 'mrk', 5, 41, 'И, взяв девицу за руку, говорит ей: «талифа&#769; куми&#769;», что значит: «девица, тебе говорю, встань».');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33101, 'rst66', 'mrk', 5, 40, 'И смеялись над Ним. Но Он, выслав всех, берет с Собою отца и мать девицы и бывших с Ним и входит туда, где девица лежала.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33100, 'rst66', 'mrk', 5, 39, 'И, войдя, говорит им: что смущаетесь и плачете? девица не умерла, но спит.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33099, 'rst66', 'mrk', 5, 38, 'Приходит в дом начальника синагоги и видит смятение и плачущих и вопиющих громко.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33098, 'rst66', 'mrk', 5, 37, 'И не позволил никому следовать за Собою, кроме Петра, Иакова и Иоанна, брата Иакова.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33097, 'rst66', 'mrk', 5, 36, 'Но Иисус, услышав сии слова, тотчас говорит начальнику синагоги: не бойся, только веруй.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33096, 'rst66', 'mrk', 5, 35, 'Когда Он еще говорил сие, приходят от начальника синагоги и говорят: дочь твоя умерла; что еще утруждаешь Учителя?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33095, 'rst66', 'mrk', 5, 34, 'Он же сказал ей: дщерь! вера твоя спасла тебя; иди в мире и будь здорова от болезни твоей.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33094, 'rst66', 'mrk', 5, 33, 'Женщина в страхе и трепете, зная, что с нею произошло, подошла, пала пред Ним и сказала Ему всю истину.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33093, 'rst66', 'mrk', 5, 32, 'Но Он смотрел вокруг, чтобы видеть ту, которая сделала это.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33092, 'rst66', 'mrk', 5, 31, 'Ученики сказали Ему: Ты видишь, что народ теснит Тебя, и говоришь: «кто прикоснулся ко Мне?»');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33091, 'rst66', 'mrk', 5, 30, 'В то же время Иисус, почувствовав Сам в Себе, что вышла из Него сила, обратился в народе и сказал: кто прикоснулся к Моей одежде?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33090, 'rst66', 'mrk', 5, 29, 'И тотчас иссяк у ней источник крови, и она ощутила в теле, что исцелена от болезни.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33089, 'rst66', 'mrk', 5, 28, 'ибо говорила: если хотя к одежде Его прикоснусь, то выздоровею.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33088, 'rst66', 'mrk', 5, 27, 'услышав об Иисусе, подошла сзади в народе и прикоснулась к одежде Его,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33087, 'rst66', 'mrk', 5, 26, 'много потерпела от многих врачей, истощила всё, что было у ней, и не получила никакой пользы, но пришла еще в худшее состояние, &mdash;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33086, 'rst66', 'mrk', 5, 25, 'Одна женщина, которая страдала кровотечением двенадцать лет,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33085, 'rst66', 'mrk', 5, 24, '<i>Иисус</i> пошел с ним. За Ним следовало множество народа, и теснили Его.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33084, 'rst66', 'mrk', 5, 23, 'и усильно просит Его, говоря: дочь моя при смерти; приди и возложи на нее руки, чтобы она выздоровела и осталась жива.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33083, 'rst66', 'mrk', 5, 22, 'И вот, приходит один из начальников синагоги, по имени Иаир, и, увидев Его, падает к ногам Его');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33082, 'rst66', 'mrk', 5, 21, 'Когда Иисус опять переправился в лодке на другой берег, собралось к Нему множество народа. Он был у моря.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33081, 'rst66', 'mrk', 5, 20, 'И пошел и начал проповедовать в Десятиградии, что сотворил с ним Иисус; и все дивились.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33080, 'rst66', 'mrk', 5, 19, 'Но Иисус не дозволил ему, а сказал: иди домой к своим и расскажи им, что сотворил с тобою Господь и <i>как</i> помиловал тебя.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33079, 'rst66', 'mrk', 5, 18, 'И когда Он вошел в лодку, бесновавшийся просил Его, чтобы быть с Ним.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33078, 'rst66', 'mrk', 5, 17, 'И начали просить Его, чтобы отошел от пределов их.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33077, 'rst66', 'mrk', 5, 16, 'Видевшие рассказали им о том, как это произошло с бесноватым, и о свиньях.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33076, 'rst66', 'mrk', 5, 15, 'Приходят к Иисусу и видят, что бесновавшийся, в котором был легион, сидит и одет, и в здравом уме; и устрашились.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33075, 'rst66', 'mrk', 5, 14, 'Пасущие же свиней побежали и рассказали в городе и в деревнях. И <i>жители</i> вышли посмотреть, что случилось.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33074, 'rst66', 'mrk', 5, 13, 'Иисус тотчас позволил им. И нечистые духи, выйдя, вошли в свиней; и устремилось стадо с крутизны в море, а их было около двух тысяч; и потонули в море.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33073, 'rst66', 'mrk', 5, 12, 'И просили Его все бесы, говоря: пошли нас в свиней, чтобы нам войти в них.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33072, 'rst66', 'mrk', 5, 11, 'Паслось же там при горе большое стадо свиней.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33071, 'rst66', 'mrk', 5, 10, 'И много просили Его, чтобы не высылал их вон из страны той.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33070, 'rst66', 'mrk', 5, 9, 'И спросил его: как тебе имя? И он сказал в ответ: легион имя мне, потому что нас много.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33069, 'rst66', 'mrk', 5, 8, 'Ибо <i>Иисус</i> сказал ему: выйди, дух нечистый, из сего человека.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33068, 'rst66', 'mrk', 5, 7, 'и, вскричав громким голосом, сказал: что Тебе до меня, Иисус, Сын Бога Всевышнего? заклинаю Тебя Богом, не мучь меня!');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33067, 'rst66', 'mrk', 5, 6, 'увидев же Иисуса издалека, прибежал и поклонился Ему,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33066, 'rst66', 'mrk', 5, 5, 'всегда, ночью и днем, в горах и гробах, кричал он и бился о камни;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33065, 'rst66', 'mrk', 5, 4, 'потому что многократно был он скован оковами и цепями, но разрывал цепи и разбивал оковы, и никто не в силах был укротить его;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33064, 'rst66', 'mrk', 5, 3, 'он имел жилище в гробах, и никто не мог его связать даже цепями,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33063, 'rst66', 'mrk', 5, 2, 'И когда вышел Он из лодки, тотчас встретил Его вышедший из гробов человек, <i>одержимый</i> нечистым духом,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33062, 'rst66', 'mrk', 5, 1, 'И пришли на другой берег моря, в страну Гадаринскую.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33061, 'rst66', 'mrk', 4, 41, 'И убоялись страхом великим и говорили между собою: кто же Сей, что и ветер и море повинуются Ему?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33060, 'rst66', 'mrk', 4, 40, 'И сказал им: что вы так боязливы? как у вас нет веры?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33059, 'rst66', 'mrk', 4, 39, 'И, встав, Он запретил ветру и сказал морю: умолкни, перестань. И ветер утих, и сделалась великая тишина.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33058, 'rst66', 'mrk', 4, 38, 'А Он спал на корме на возглавии. Его будят и говорят Ему: Учитель! неужели Тебе нужды нет, что мы погибаем?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33057, 'rst66', 'mrk', 4, 37, 'И поднялась великая буря; волны били в лодку, так что она уже наполнялась <i>водою</i>.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33056, 'rst66', 'mrk', 4, 36, 'И они, отпустив народ, взяли Его с собою, как Он был в лодке; с Ним были и другие лодки.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33055, 'rst66', 'mrk', 4, 35, 'Вечером того дня сказал им: переправимся на ту сторону.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33054, 'rst66', 'mrk', 4, 34, 'Без притчи же не говорил им, а ученикам наедине изъяснял всё.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33053, 'rst66', 'mrk', 4, 33, 'И таковыми многими притчами проповедовал им слово, сколько они могли слышать.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33052, 'rst66', 'mrk', 4, 32, 'а когда посеяно, всходит и становится больше всех злаков, и пускает большие ветви, так что под тенью его могут укрываться птицы небесные.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33051, 'rst66', 'mrk', 4, 31, 'Оно &mdash; как зерно горчичное, которое, когда сеется в землю, есть меньше всех семян на земле;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33050, 'rst66', 'mrk', 4, 30, 'И сказал: чему уподобим Царствие Божие? или какою притчею изобразим его?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33049, 'rst66', 'mrk', 4, 29, 'Когда же созреет плод, немедленно посылает серп, потому что настала жатва.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33048, 'rst66', 'mrk', 4, 28, 'ибо земля сама собою производит сперва зелень, потом колос, потом полное зерно в колосе.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33047, 'rst66', 'mrk', 4, 27, 'и спит, и встает ночью и днем; и ка&#769;к семя всходит и растет, не знает он,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33046, 'rst66', 'mrk', 4, 26, 'И сказал: Царствие Божие подобно тому, как если человек бросит семя в землю,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33045, 'rst66', 'mrk', 4, 25, 'Ибо кто имеет, тому дано будет, а кто не имеет, у того отнимется и то, что имеет.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33044, 'rst66', 'mrk', 4, 24, 'И сказал им: замечайте, что слышите: какою мерою мерите, такою отмерено будет вам и прибавлено будет вам, слушающим.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33043, 'rst66', 'mrk', 4, 23, 'Если кто имеет уши слышать, да слышит!');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33042, 'rst66', 'mrk', 4, 22, 'Нет ничего тайного, что не сделалось бы явным, и ничего не бывает потаенного, что не вышло бы наружу.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33041, 'rst66', 'mrk', 4, 21, 'И сказал им: для того ли приносится свеча, чтобы поставить ее под сосуд или под кровать? не для того ли, чтобы поставить ее на подсвечнике?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33040, 'rst66', 'mrk', 4, 20, 'А посеянное на доброй земле означает тех, которые слушают слово и принимают, и приносят плод, один в тридцать, другой в шестьдесят, иной во сто крат.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33039, 'rst66', 'mrk', 4, 19, 'но в которых заботы века сего, обольщение богатством и другие пожелания, входя в них, заглушают слово, и оно бывает без плода.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33038, 'rst66', 'mrk', 4, 18, 'Посеянное в тернии означает слышащих слово,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33037, 'rst66', 'mrk', 4, 17, 'но не имеют в себе корня и непостоянны; потом, когда настанет скорбь или гонение за слово, тотчас соблазняются.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33036, 'rst66', 'mrk', 4, 16, 'Подобным образом и посеянное на каменистом <i>месте</i> означает тех, которые, когда услышат слово, тотчас с радостью принимают его,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33035, 'rst66', 'mrk', 4, 15, '<i>Посеянное</i> при дороге означает тех, в которых сеется слово, но <i>к которым</i>, когда услышат, тотчас приходит сатана и похищает слово, посеянное в сердцах их.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33034, 'rst66', 'mrk', 4, 14, 'Сеятель слово сеет.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33033, 'rst66', 'mrk', 4, 13, 'И говорит им: не понимаете этой притчи? Как же вам уразуметь все притчи?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33032, 'rst66', 'mrk', 4, 12, 'так что они своими глазами смотрят, и не видят; своими ушами слышат, и не разумеют, да не обратятся, и прощены будут им грехи.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33031, 'rst66', 'mrk', 4, 11, 'И сказал им: вам дано знать тайны Царствия Божия, а тем внешним все бывает в притчах;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33030, 'rst66', 'mrk', 4, 10, 'Когда же остался без народа, окружающие Его, вместе с Двенадцатью, спросили Его о притче.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33029, 'rst66', 'mrk', 4, 9, 'И сказал им: кто имеет уши слышать, да слышит!');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33028, 'rst66', 'mrk', 4, 8, 'И иное упало на добрую землю и дало плод, который взошел и вырос, и принесло иное тридцать, иное шестьдесят, и иное сто.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33027, 'rst66', 'mrk', 4, 7, 'Иное упало в терние, и терние выросло, и заглушило <i>семя</i>, и оно не дало плода.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33026, 'rst66', 'mrk', 4, 6, 'когда же взошло солнце, увяло и, как не имело корня, засохло.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33025, 'rst66', 'mrk', 4, 5, 'Иное упало на каменистое <i>место</i>, где немного было земли, и скоро взошло, потому что земля была неглубока;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33024, 'rst66', 'mrk', 4, 4, 'и, когда сеял, случилось, что иное упало при дороге, и налетели птицы и поклевали то.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33023, 'rst66', 'mrk', 4, 3, 'Слушайте: вот, вышел сеятель сеять;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33022, 'rst66', 'mrk', 4, 2, 'И учил их притчами много, и в учении Своем говорил им:');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33021, 'rst66', 'mrk', 4, 1, 'И опять начал учить при море; и собралось к Нему множество народа, так что Он вошел в лодку и сидел на море, а весь народ был на земле, у моря.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33020, 'rst66', 'mrk', 3, 35, 'ибо кто будет исполнять волю Божию, тот Мне брат, и сестра, и матерь.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33019, 'rst66', 'mrk', 3, 34, 'И обозрев сидящих вокруг Себя, говорит: вот матерь Моя и братья Мои;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33018, 'rst66', 'mrk', 3, 33, 'И отвечал им: кто матерь Моя и братья Мои?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33017, 'rst66', 'mrk', 3, 32, 'Около Него сидел народ. И сказали Ему: вот, Матерь Твоя и братья Твои и сестры Твои, вне <i>дома</i>, спрашивают Тебя.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33016, 'rst66', 'mrk', 3, 31, 'И пришли Матерь и братья Его и, стоя вне <i>дома</i>, послали к Нему звать Его.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33015, 'rst66', 'mrk', 3, 30, '<i>Сие сказал Он</i>, потому что говорили: в Нем нечистый дух.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33014, 'rst66', 'mrk', 3, 29, 'но кто будет хулить Духа Святого, тому не будет прощения вовек, но подлежит он вечному осуждению.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33013, 'rst66', 'mrk', 3, 28, 'Истинно говорю вам: будут прощены сынам человеческим все грехи и хуления, какими бы ни хулили;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33012, 'rst66', 'mrk', 3, 27, 'Никто, войдя в дом сильного, не может расхитить вещей его, если прежде не свяжет сильного, и тогда расхитит дом его.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33011, 'rst66', 'mrk', 3, 26, 'и если сатана восстал на самого себя и разделился, не может устоять, но пришел конец его.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33010, 'rst66', 'mrk', 3, 25, 'и если дом разделится сам в себе, не может устоять дом тот;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33009, 'rst66', 'mrk', 3, 24, 'Если царство разделится само в себе, не может устоять царство то;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33008, 'rst66', 'mrk', 3, 23, 'И, призвав их, говорил им притчами: ка&#769;к может сатана изгонять сатану?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33007, 'rst66', 'mrk', 3, 22, 'А книжники, пришедшие из Иерусалима, говорили, что Он имеет <i>в Себе</i> веельзевула и что изгоняет бесов силою бесовского князя.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33006, 'rst66', 'mrk', 3, 21, 'И, услышав, ближние Его пошли взять Его, ибо говорили, что Он вышел из себя.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33005, 'rst66', 'mrk', 3, 20, 'Приходят в дом; и опять сходится народ, так что им невозможно было и хлеба есть.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33004, 'rst66', 'mrk', 3, 19, 'и Иуду Искариотского, который и предал Его.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33003, 'rst66', 'mrk', 3, 18, 'Андрея, Филиппа, Варфоломея, Матфея, Фому, Иакова Алфеева, Фаддея, Симона Кананита');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33002, 'rst66', 'mrk', 3, 17, 'Иакова Зеведеева и Иоанна, брата Иакова, нарекши им имена Воанергес, то есть «сыны громовы»,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33001, 'rst66', 'mrk', 3, 16, '<i>Поставил</i> Симона, нарекши ему имя Петр,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (33000, 'rst66', 'mrk', 3, 15, 'и чтобы они имели власть исцелять от болезней и изгонять бесов;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32999, 'rst66', 'mrk', 3, 14, 'И поставил <i>из них</i> двенадцать, чтобы с Ним были и чтобы посылать их на проповедь,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32998, 'rst66', 'mrk', 3, 13, 'Потом взошел на гору и позвал к Себе, кого Сам хотел; и пришли к Нему.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32997, 'rst66', 'mrk', 3, 12, 'Но Он строго запрещал им, чтобы не делали Его известным.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32996, 'rst66', 'mrk', 3, 11, 'И духи нечистые, когда видели Его, падали пред Ним и кричали: Ты Сын Божий.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32995, 'rst66', 'mrk', 3, 10, 'Ибо многих Он исцелил, так что имевшие язвы бросались к Нему, чтобы коснуться Его.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32994, 'rst66', 'mrk', 3, 9, 'И сказал ученикам Своим, чтобы готова была для Него лодка по причине многолюдства, дабы не теснили Его.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32993, 'rst66', 'mrk', 3, 8, 'Иерусалима, Идумеи и из‐за Иордана. И <i>живущие</i> в окрестностях Тира и Сидона, услышав, что Он делал, шли к Нему в великом множестве.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32992, 'rst66', 'mrk', 3, 7, 'Но Иисус с учениками Своими удалился к морю; и за Ним последовало множество народа из Галилеи, Иудеи,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32991, 'rst66', 'mrk', 3, 6, 'Фарисеи, выйдя, немедленно составили с иродианами совещание против Него, как бы погубить Его.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32990, 'rst66', 'mrk', 3, 5, 'И, воззрев на них с гневом, скорбя об ожесточении сердец их, говорит тому человеку: протяни руку твою. Он протянул, и стала рука его здорова, как другая.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32989, 'rst66', 'mrk', 3, 4, 'А им говорит: должно ли в субботу добро делать, или зло делать? душу спасти, или погубить? Но они молчали.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32988, 'rst66', 'mrk', 3, 3, 'Он же говорит человеку, имевшему иссохшую руку: стань на средину.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32987, 'rst66', 'mrk', 3, 2, 'И наблюдали за Ним, не исцелит ли его в субботу, чтобы обвинить Его.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32986, 'rst66', 'mrk', 3, 1, 'И пришел опять в синагогу; там был человек, имевший иссохшую руку.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32985, 'rst66', 'mrk', 2, 28, 'посему Сын Человеческий есть господин и субботы.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32984, 'rst66', 'mrk', 2, 27, 'И сказал им: суббота для человека, а не человек для субботы;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32983, 'rst66', 'mrk', 2, 26, 'Как вошел он в дом Божий при первосвященнике Авиафаре и ел хлебы предложения, которых не должно было есть никому, кроме священников, и дал и бывшим с ним?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32982, 'rst66', 'mrk', 2, 25, 'Он сказал им: неужели вы не читали никогда, что&#769; сделал Давид, когда имел нужду и взалкал сам и бывшие с ним?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32981, 'rst66', 'mrk', 2, 24, 'И фарисеи сказали Ему: смотри, что&#769; они делают в субботу, чего не должно <i>делать</i>?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32980, 'rst66', 'mrk', 2, 23, 'И случилось Ему в субботу проходить засеянными <i>полями</i>, и ученики Его дорогою начали срывать колосья.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32979, 'rst66', 'mrk', 2, 22, 'Никто не вливает вина молодого в мехи ветхие: иначе молодое вино прорвет мехи, и вино вытечет, и мехи пропадут; но вино молодое надобно вливать в мехи новые.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32978, 'rst66', 'mrk', 2, 21, 'Никто к ветхой одежде не приставляет заплаты из небеленой ткани: иначе вновь пришитое отдерет от старого, и дыра будет еще хуже.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32977, 'rst66', 'mrk', 2, 20, 'но придут дни, когда отнимется у них жених, и тогда будут поститься в те дни.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32976, 'rst66', 'mrk', 2, 19, 'И сказал им Иисус: могут ли поститься сыны чертога брачного, когда с ними жених? Доколе с ними жених, не могут поститься,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32975, 'rst66', 'mrk', 2, 18, 'Ученики Иоанновы и фарисейские постились. Приходят к Нему и говорят: почему ученики Иоанновы и фарисейские постятся, а Твои ученики не постятся?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32974, 'rst66', 'mrk', 2, 17, 'Услышав <i>сие</i>, Иисус говорит им: не здоровые имеют нужду во враче, но больные; Я пришел призвать не праведников, но грешников к покаянию.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32973, 'rst66', 'mrk', 2, 16, 'Книжники и фарисеи, увидев, что Он ест с мытарями и грешниками, говорили ученикам Его: как это Он ест и пьет с мытарями и грешниками?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32972, 'rst66', 'mrk', 2, 15, 'И когда Иисус возлежал в доме его, возлежали с Ним и ученики Его и многие мытари и грешники: ибо много их было, и они следовали за Ним.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32971, 'rst66', 'mrk', 2, 14, 'Проходя, увидел Он Левия Алфеева, сидящего у сбора пошлин, и говорит ему: следуй за Мною. И <i>он</i>, встав, последовал за Ним.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32970, 'rst66', 'mrk', 2, 13, 'И вышел <i>Иисус</i> опять к морю; и весь народ пошел к Нему, и Он учил их.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32969, 'rst66', 'mrk', 2, 12, 'Он тотчас встал и, взяв постель, вышел перед всеми, так что все изумлялись и прославляли Бога, говоря: никогда ничего такого мы не видали.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32968, 'rst66', 'mrk', 2, 11, 'тебе говорю: встань, возьми постель твою и иди в дом твой.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32967, 'rst66', 'mrk', 2, 10, 'Но чтобы вы знали, что Сын Человеческий имеет власть на земле прощать грехи, &mdash; говорит расслабленному:');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32966, 'rst66', 'mrk', 2, 9, 'Что легче? сказать ли расслабленному: «прощаются тебе грехи»? или сказать: «встань, возьми свою постель и ходи»?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32965, 'rst66', 'mrk', 2, 8, 'Иисус, тотчас узнав духом Своим, что они так помышляют в себе, сказал им: для чего так помышляете в сердцах ваших?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32964, 'rst66', 'mrk', 2, 7, 'что Он так богохульствует? кто может прощать грехи, кроме одного Бога?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32963, 'rst66', 'mrk', 2, 6, 'Тут сидели некоторые из книжников и помышляли в сердцах своих:');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32962, 'rst66', 'mrk', 2, 5, 'Иисус, видя веру их, говорит расслабленному: чадо! прощаются тебе грехи твои.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32961, 'rst66', 'mrk', 2, 4, 'и, не имея возможности приблизиться к Нему за многолюдством, раскрыли кровлю <i>дома</i>, где Он находился, и, прокопав ее, спустили постель, на которой лежал расслабленный.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32960, 'rst66', 'mrk', 2, 3, 'И пришли к Нему с расслабленным, которого несли четверо;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32959, 'rst66', 'mrk', 2, 2, 'Тотчас собрались многие, так что уже и у дверей не было места; и Он говорил им слово.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32958, 'rst66', 'mrk', 2, 1, 'Через <i>несколько</i> дней опять пришел Он в Капернаум; и слышно стало, что Он в доме.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32957, 'rst66', 'mrk', 1, 45, 'А он, выйдя, начал провозглашать и рассказывать о происшедшем, так что <i>Иисус</i> не мог уже явно войти в город, но находился вне, в местах пустынных. И приходили к Нему отовсюду.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32956, 'rst66', 'mrk', 1, 44, 'и сказал ему: смотри, никому ничего не говори, но пойди, покажись священнику и принеси за очищение твое, что повелел Моисей, во свидетельство им.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32955, 'rst66', 'mrk', 1, 43, 'И, посмотрев на него строго, тотчас отослал его');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32954, 'rst66', 'mrk', 1, 42, 'После сего слова проказа тотчас сошла с него, и он стал чист.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32953, 'rst66', 'mrk', 1, 41, 'Иисус, умилосердившись над ним, простер руку, коснулся его и сказал ему: хочу, очистись.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32952, 'rst66', 'mrk', 1, 40, 'Приходит к Нему прокаженный и, умоляя Его и падая пред Ним на колени, говорит Ему: если хочешь, можешь меня очистить.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32951, 'rst66', 'mrk', 1, 39, 'И Он проповедовал в синагогах их по всей Галилее и изгонял бесов.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32950, 'rst66', 'mrk', 1, 38, 'Он говорит им: пойдем в ближние селения и города, чтобы Мне и там проповедовать, ибо Я для того пришел.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32949, 'rst66', 'mrk', 1, 37, 'и, найдя Его, говорят Ему: все ищут Тебя.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32948, 'rst66', 'mrk', 1, 36, 'Симон и бывшие с ним пошли за Ним');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32947, 'rst66', 'mrk', 1, 35, 'А утром, встав весьма рано, вышел и удалился в пустынное место, и там молился.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32946, 'rst66', 'mrk', 1, 34, 'И Он исцелил многих, страдавших различными болезнями; изгнал многих бесов, и не позволял бесам говорить, что они знают, что Он Христос.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32945, 'rst66', 'mrk', 1, 33, 'И весь город собрался к дверям.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32944, 'rst66', 'mrk', 1, 32, 'При наступлении же вечера, когда заходило солнце, приносили к Нему всех больных и бесноватых.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32943, 'rst66', 'mrk', 1, 31, 'Подойдя, Он поднял ее, взяв ее за руку; и горячка тотчас оставила ее, и она стала служить им.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32942, 'rst66', 'mrk', 1, 30, 'Теща же Симонова лежала в горячке; и тотчас говорят Ему о ней.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32941, 'rst66', 'mrk', 1, 29, 'Выйдя вскоре из синагоги, пришли в дом Симона и Андрея, с Иаковом и Иоанном.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32940, 'rst66', 'mrk', 1, 28, 'И скоро разошлась о Нем молва по всей окрестности в Галилее.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32939, 'rst66', 'mrk', 1, 27, 'И все ужаснулись, так что друг друга спрашивали: что это? что это за новое учение, что Он и духам нечистым повелевает со властью, и они повинуются Ему?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32938, 'rst66', 'mrk', 1, 26, 'Тогда дух нечистый, сотрясши его и вскричав громким голосом, вышел из него.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32937, 'rst66', 'mrk', 1, 25, 'Но Иисус запретил ему, говоря: замолчи и выйди из него.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32936, 'rst66', 'mrk', 1, 24, 'оставь! что Тебе до нас, Иисус Назарянин? Ты пришел погубить нас! знаю Тебя, кто Ты, Святой Божий.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32935, 'rst66', 'mrk', 1, 23, 'В синагоге их был человек, <i>одержимый</i> духом нечистым, и вскричал:');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32934, 'rst66', 'mrk', 1, 22, 'И дивились Его учению, ибо Он учил их, как власть имеющий, а не как книжники.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32933, 'rst66', 'mrk', 1, 21, 'И приходят в Капернаум; и вскоре в субботу вошел Он в синагогу и учил.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32932, 'rst66', 'mrk', 1, 20, 'и тотчас призвал их. И они, оставив отца своего Зеведея в лодке с работниками, последовали за Ним.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32931, 'rst66', 'mrk', 1, 19, 'И, пройдя оттуда немного, Он увидел Иакова Зеведеева и Иоанна, брата его, также в лодке починивающих сети;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32930, 'rst66', 'mrk', 1, 18, 'И они тотчас, оставив свои сети, последовали за Ним.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32929, 'rst66', 'mrk', 1, 17, 'И сказал им Иисус: идите за Мною, и Я сделаю, что вы будете ловцами человеков.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32928, 'rst66', 'mrk', 1, 16, 'Проходя же близ моря Галилейского, увидел Симона и Андрея, брата его, закидывающих сети в море, ибо они были рыболовы.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32927, 'rst66', 'mrk', 1, 15, 'и говоря, что исполнилось время и приблизилось Царствие Божие: покайтесь и веруйте в Евангелие.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32926, 'rst66', 'mrk', 1, 14, 'После же того как предан был Иоанн, пришел Иисус в Галилею, проповедуя Евангелие Царствия Божия');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32925, 'rst66', 'mrk', 1, 13, 'И был Он там в пустыне сорок дней, искушаемый сатаною, и был со зверями; и Ангелы служили Ему.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32924, 'rst66', 'mrk', 1, 12, 'Немедленно после того Дух ведет Его в пустыню.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32923, 'rst66', 'mrk', 1, 11, 'И глас был с небес: Ты Сын Мой возлюбленный, в Котором Мое благоволение.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32922, 'rst66', 'mrk', 1, 10, 'И когда выходил из воды, тотчас увидел <i>Иоанн</i> разверзающиеся небеса и Духа, как голубя, сходящего на Него.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32921, 'rst66', 'mrk', 1, 9, 'И было в те дни, пришел Иисус из Назарета Галилейского и крестился от Иоанна в Иордане.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32920, 'rst66', 'mrk', 1, 8, 'я крестил вас водою, а Он будет крестить вас Духом Святым.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32919, 'rst66', 'mrk', 1, 7, 'И проповедовал, говоря: идет за мною Сильнейший меня, у Которого я недостоин, наклонившись, развязать ремень обуви Его;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32918, 'rst66', 'mrk', 1, 6, 'Иоанн же носил одежду из верблюжьего волоса и пояс кожаный на чреслах своих и ел акриды и дикий мед.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32917, 'rst66', 'mrk', 1, 5, 'И выходили к нему вся страна Иудейская и Иерусалимляне, и крестились от него все в реке Иордане, исповедуя грехи свои.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32916, 'rst66', 'mrk', 1, 4, 'Явился Иоанн, крестя в пустыне и проповедуя крещение покаяния для прощения грехов.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32915, 'rst66', 'mrk', 1, 3, '«Глас вопиющего в пустыне: приготовьте путь Господу, прямыми сделайте стези Ему».');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32914, 'rst66', 'mrk', 1, 2, 'как написано у пророков: «Вот, Я посылаю Ангела Моего пред лицом Твоим, который приготовит путь Твой пред Тобою».');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32913, 'rst66', 'mrk', 1, 1, 'Начало Евангелия Иисуса Христа, Сына Божия,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32912, 'rst66', 'mat', 28, 20, 'уча их соблюдать всё, что Я повелел вам; и се, Я с вами во все дни до скончания века. Аминь.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32911, 'rst66', 'mat', 28, 19, 'Итак, идите, научите все народы, крестя их во имя Отца и Сына и Святого Духа,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32910, 'rst66', 'mat', 28, 18, 'И, приблизившись, Иисус сказал им: дана Мне всякая власть на небе и на земле.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32909, 'rst66', 'mat', 28, 17, 'и, увидев Его, поклонились Ему, а иные усомнились.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32908, 'rst66', 'mat', 28, 16, 'Одиннадцать же учеников пошли в Галилею, на гору, куда повелел им Иисус,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32907, 'rst66', 'mat', 28, 15, 'Они, взяв деньги, поступили, как научены были; и пронеслось слово сие между Иудеями до сего дня.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32906, 'rst66', 'mat', 28, 14, 'и, если слух об этом дойдет до правителя, мы убедим его, и вас от неприятности избавим.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32905, 'rst66', 'mat', 28, 13, 'и сказали: скажите, что ученики Его, придя ночью, украли Его, когда мы спали;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32904, 'rst66', 'mat', 28, 12, 'И сии, собравшись со старейшинами и сделав совещание, довольно денег дали воинам,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32903, 'rst66', 'mat', 28, 11, 'Когда же они шли, то некоторые из стражи, войдя в город, объявили первосвященникам о всем бывшем.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32902, 'rst66', 'mat', 28, 10, 'Тогда говорит им Иисус: не бойтесь; пойдите, возвестите братьям Моим, чтобы шли в Галилею, и там они увидят Меня.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32901, 'rst66', 'mat', 28, 9, 'Когда же шли они возвестить ученикам Его, и се, Иисус встретил их и сказал: радуйтесь! И они, приступив, ухватились за ноги Его и поклонились Ему.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32900, 'rst66', 'mat', 28, 8, 'И, выйдя поспешно из гроба, они со страхом и радостью великою побежали возвестить ученикам Его.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32899, 'rst66', 'mat', 28, 7, 'и пойдите скорее, скажите ученикам Его, что Он воскрес из мертвых и предваряет вас в Галилее; там Его увидите. Вот, я сказал вам.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32898, 'rst66', 'mat', 28, 6, 'Его нет здесь &mdash; Он воскрес, как сказал. Подойдите, посмотрите место, где лежал Господь,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32897, 'rst66', 'mat', 28, 5, 'Ангел же, обратив речь к женщинам, сказал: не бойтесь, ибо знаю, что вы ищете Иисуса распятого;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32896, 'rst66', 'mat', 28, 4, 'устрашившись его, стерегущие пришли в трепет и стали как мертвые;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32895, 'rst66', 'mat', 28, 3, 'вид его был, как молния, и одежда его бела, как снег;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32894, 'rst66', 'mat', 28, 2, 'И вот, сделалось великое землетрясение, ибо Ангел Господень, сошедший с небес, приступив, отвалил камень от двери гроба и сидел на нем;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32893, 'rst66', 'mat', 28, 1, 'По прошествии же субботы, на рассвете первого дня недели, пришла Мария Магдалина и другая Мария посмотреть гроб.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32892, 'rst66', 'mat', 27, 66, 'Они пошли и поставили у гроба стражу, и приложили к камню печать.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32891, 'rst66', 'mat', 27, 65, 'Пилат сказал им: имеете стражу; пойдите, охраняйте, как знаете.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32890, 'rst66', 'mat', 27, 64, 'итак, прикажи охранять гроб до третьего дня, чтобы ученики Его, придя ночью, не украли Его и не сказали народу: «воскрес из мертвых»; и будет последний обман хуже первого.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32889, 'rst66', 'mat', 27, 63, 'и говорили: господин! Мы вспомнили, что обманщик тот, еще будучи в живых, сказал: «после трех дней воскресну»;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32888, 'rst66', 'mat', 27, 62, 'На другой день, который следует за пятницею, собрались первосвященники и фарисеи к Пилату');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32887, 'rst66', 'mat', 27, 61, 'Была же там Мария Магдалина и другая Мария, которые сидели против гроба.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32886, 'rst66', 'mat', 27, 60, 'и положил его в новом своем гробе, который высек он в скале; и, привалив большой камень к двери гроба, удалился.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32885, 'rst66', 'mat', 27, 59, 'и, взяв тело, Иосиф обвил его чистою плащаницею');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32884, 'rst66', 'mat', 27, 58, 'он, придя к Пилату, просил тела Иисусова. Тогда Пилат приказал отдать тело;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32883, 'rst66', 'mat', 27, 57, 'Когда же настал вечер, пришел богатый человек из Аримафеи, именем Иосиф, который также учился у Иисуса;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32882, 'rst66', 'mat', 27, 56, 'между ними были Мария Магдалина и Мария, мать Иакова и Иосии, и мать сыновей Зеведеевых.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32881, 'rst66', 'mat', 27, 55, 'Там были также и смотрели издали многие женщины, которые следовали за Иисусом из Галилеи, служа Ему;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32880, 'rst66', 'mat', 27, 54, 'Сотник же и те, которые с ним стерегли Иисуса, видя землетрясение и все бывшее, устрашились весьма и говорили: воистину Он был Сын Божий.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32879, 'rst66', 'mat', 27, 53, 'и, выйдя из гробов по воскресении Его, вошли во святой град и явились многим.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32878, 'rst66', 'mat', 27, 52, 'и гробы отверзлись; и многие тела усопших святых воскресли');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32877, 'rst66', 'mat', 27, 51, 'И вот, завеса в храме раздралась надвое, сверху донизу; и земля потряслась; и камни расселись;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32876, 'rst66', 'mat', 27, 50, 'Иисус же, опять возопив громким голосом, испустил дух.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32875, 'rst66', 'mat', 27, 49, 'а другие говорили: постой, посмотрим, придет ли Илия спасти Его.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32874, 'rst66', 'mat', 27, 48, 'И тотчас побежал один из них, взял губку, наполнил уксусом и, наложив на трость, давал Ему пить;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32873, 'rst66', 'mat', 27, 47, 'Некоторые из стоявших там, слыша это, говорили: Илию зовет Он.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32872, 'rst66', 'mat', 27, 46, 'а около девятого часа возопил Иисус громким голосом: Или&#769;, Или&#769;! лама&#769; савахфани&#769;? то есть: «Боже Мой, Боже Мой! &mdash; для чего Ты Меня оставил?»');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32871, 'rst66', 'mat', 27, 45, 'От шестого же часа тьма была по всей земле до часа девятого;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32870, 'rst66', 'mat', 27, 44, 'Также и разбойники, распятые с Ним, поносили Его.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32869, 'rst66', 'mat', 27, 43, 'уповал на Бога; пусть теперь избавит Его, если Он угоден Ему. Ибо Он сказал: «Я Божий Сын».');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32868, 'rst66', 'mat', 27, 42, 'других спасал, а Себя Самого не может спасти; если Он Царь Израилев, пусть теперь сойдет с креста, и уверуем в Него;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32867, 'rst66', 'mat', 27, 41, 'Подобно и первосвященники с книжниками и старейшинами и фарисеями, насмехаясь, говорили:');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32866, 'rst66', 'mat', 27, 40, 'и говоря: Разрушающий храм и в три дня Созидающий! спаси Себя Самого; если Ты Сын Божий, сойди с креста.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32865, 'rst66', 'mat', 27, 39, 'Проходящие же злословили Его, кивая головами своими');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32864, 'rst66', 'mat', 27, 38, 'Тогда распяты с Ним два разбойника: один по правую сторону, а другой по левую.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32863, 'rst66', 'mat', 27, 37, 'и поставили над головою Его надпись, означающую вину Его: «Сей есть Иисус, Царь Иудейский».');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32862, 'rst66', 'mat', 27, 36, 'и, сидя, стерегли Его там;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32861, 'rst66', 'mat', 27, 35, 'Распявшие же Его делили одежды его, бросая жребий;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32860, 'rst66', 'mat', 27, 34, 'дали Ему пить уксуса, смешанного с желчью; и, отведав, не хотел пить.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32859, 'rst66', 'mat', 27, 33, 'И, придя на место, называемое Голгофа, что значит: «лобное место»,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32858, 'rst66', 'mat', 27, 32, 'Выходя, они встретили одного Киринеянина, по имени Симона; сего заставили нести крест Его.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32857, 'rst66', 'mat', 27, 31, 'И когда насмеялись над Ним, сняли с Него багряницу, и одели Его в одежды Его, и повели Его на распятие.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32856, 'rst66', 'mat', 27, 30, 'И плевали на Него и, взяв трость, били Его по голове.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32855, 'rst66', 'mat', 27, 29, 'и, сплетши венец из терна, возложили Ему на голову и дали Ему в правую руку трость; и, становясь пред Ним на колени, насмехались над Ним, говоря: радуйся, Царь Иудейский!');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32854, 'rst66', 'mat', 27, 28, 'и, раздев Его, надели на Него багряницу;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32853, 'rst66', 'mat', 27, 27, 'Тогда воины правителя, взяв Иисуса в преторию, собрали на Него весь полк');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32852, 'rst66', 'mat', 27, 26, 'Тогда отпустил им Варавву, а Иисуса, бив, предал на распятие.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32851, 'rst66', 'mat', 27, 25, 'И, отвечая, весь народ сказал: кровь Его на нас и на детях наших.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32850, 'rst66', 'mat', 27, 24, 'Пилат, видя, что ничто не помогает, но смятение увеличивается, взял воды и умыл руки перед народом, и сказал: невиновен я в крови Праведника Сего; смотрите вы.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32849, 'rst66', 'mat', 27, 23, 'Правитель сказал: какое же зло сделал Он? Но они еще сильнее кричали: да будет распят.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32848, 'rst66', 'mat', 27, 22, 'Пилат говорит им: что&#769; же я сделаю Иисусу, называемому Христом? Говорят ему все: да будет распят.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32847, 'rst66', 'mat', 27, 21, 'Тогда правитель спросил их: кого из двух хотите, чтобы я отпустил вам? Они сказали: Варавву.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32846, 'rst66', 'mat', 27, 20, 'Но первосвященники и старейшины возбудили народ просить Варавву, а Иисуса погубить.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32845, 'rst66', 'mat', 27, 19, 'Между тем как сидел он на судейском месте, жена его послала ему сказать: не делай ничего Праведнику Тому, потому что я ныне во сне много пострадала за Него.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32844, 'rst66', 'mat', 27, 18, 'Ибо знал, что предали Его из зависти.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32843, 'rst66', 'mat', 27, 17, 'итак, когда собрались они, сказал им Пилат: кого хотите, чтобы я отпустил вам: Варавву, или Иисуса, называемого Христом?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32842, 'rst66', 'mat', 27, 16, 'Был тогда у них известный узник, называемый Варавва;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32841, 'rst66', 'mat', 27, 15, 'На праздник же <i>Пасхи</i> правитель имел обычай отпускать народу одного узника, которого хотели.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32840, 'rst66', 'mat', 27, 14, 'И не отвечал ему ни на одно слово, так что правитель весьма дивился.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32839, 'rst66', 'mat', 27, 13, 'Тогда говорит Ему Пилат: не слышишь, сколько свидетельствуют против Тебя?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32838, 'rst66', 'mat', 27, 12, 'И когда обвиняли Его первосвященники и старейшины, Он ничего не отвечал.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32837, 'rst66', 'mat', 27, 11, 'Иисус же стал перед правителем. И спросил Его правитель: Ты Царь Иудейский? Иисус сказал ему: ты говоришь.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32836, 'rst66', 'mat', 27, 10, 'и дали их за землю горшечника, как сказал мне Господь».');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32835, 'rst66', 'mat', 27, 9, 'Тогда сбылось реченное через пророка Иеремию, который говорит: «и взяли тридцать сребреников, цену Оцененного, Которого оценили сыны Израиля,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32834, 'rst66', 'mat', 27, 8, 'посему и называется земля та «землею крови» до сего дня.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32833, 'rst66', 'mat', 27, 7, 'Сделав же совещание, купили на них землю горшечника, для погребения странников;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32832, 'rst66', 'mat', 27, 6, 'Первосвященники, взяв сребреники, сказали: непозволительно положить их в сокровищницу церковную, потому что это цена крови.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32831, 'rst66', 'mat', 27, 5, 'И, бросив сребреники в храме, он вышел, пошел и удавился.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32830, 'rst66', 'mat', 27, 4, 'говоря: согрешил я, предав кровь невинную. Они же сказали ему: что&#769; нам до того? смотри сам.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32829, 'rst66', 'mat', 27, 3, 'Тогда Иуда, предавший Его, увидев, что Он осужден, и, раскаявшись, возвратил тридцать сребреников первосвященникам и старейшинам,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32828, 'rst66', 'mat', 27, 2, 'и, связав Его, отвели и предали Его Понтию Пилату, правителю.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32827, 'rst66', 'mat', 27, 1, 'Когда же настало утро, все первосвященники и старейшины народа имели совещание об Иисусе, чтобы предать Его смерти;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32826, 'rst66', 'mat', 26, 75, 'И вспомнил Петр слово, сказанное ему Иисусом: «прежде нежели пропоет петух, трижды отречешься от Меня». И, выйдя вон, плакал горько.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32825, 'rst66', 'mat', 26, 74, 'Тогда он начал клясться и божиться, что не знает Сего Человека. И вдруг запел петух.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32824, 'rst66', 'mat', 26, 73, 'Немного спустя подошли стоявшие там и сказали Петру: точно и ты из них, ибо и речь твоя обличает тебя.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32823, 'rst66', 'mat', 26, 72, 'И он опять отрекся с клятвою, что не знает Сего Человека.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32822, 'rst66', 'mat', 26, 71, 'Когда же он выходил за ворота, увидела его другая, и говорит бывшим там: и этот был с Иисусом Назореем.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32821, 'rst66', 'mat', 26, 70, 'Но он отрекся перед всеми, сказав: не знаю, что ты говоришь.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32820, 'rst66', 'mat', 26, 69, 'Петр же сидел вне на дворе. И подошла к нему одна служанка и сказала: и ты был с Иисусом Галилеянином.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32819, 'rst66', 'mat', 26, 68, 'и говорили: прореки нам, Христос, кто ударил Тебя?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32818, 'rst66', 'mat', 26, 67, 'Тогда плевали Ему в лицо и заушали Его; другие же ударяли Его по ланитам');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32817, 'rst66', 'mat', 26, 66, 'Как вам кажется? Они же сказали в ответ: повинен смерти.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32816, 'rst66', 'mat', 26, 65, 'Тогда первосвященник разодрал одежды свои и сказал: Он богохульствует! на что&#769; еще нам свидетелей? вот, теперь вы слышали богохульство Его!');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32815, 'rst66', 'mat', 26, 64, 'Иисус говорит ему: ты сказал; даже сказываю вам: отныне у&#769;зрите Сына Человеческого, сидящего одесную Силы и грядущего на облаках небесных.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32814, 'rst66', 'mat', 26, 63, 'Иисус молчал. И первосвященник сказал Ему: заклинаю Тебя Богом Живым, скажи нам, Ты ли Христос, Сын Божий?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32813, 'rst66', 'mat', 26, 62, 'И, встав, первосвященник сказал Ему: <i>что же</i> ничего не отвечаешь? что&#769; они против Тебя свидетельствуют?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32812, 'rst66', 'mat', 26, 61, 'и сказали: Он говорил: «могу разрушить храм Божий и в три дня создать его».');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32811, 'rst66', 'mat', 26, 60, 'и не находили; и, хотя много лжесвидетелей приходило, не нашли. Но наконец пришли два лжесвидетеля');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32810, 'rst66', 'mat', 26, 59, 'Первосвященники и старейшины и весь синедрион искали лжесвидетельства против Иисуса, чтобы предать Его смерти,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32809, 'rst66', 'mat', 26, 58, 'Петр же следовал за Ним издали, до двора первосвященникова; и, войдя внутрь, сел со служителями, чтобы видеть конец.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32808, 'rst66', 'mat', 26, 57, 'А взявшие Иисуса отвели Его к Каиафе первосвященнику, куда собрались книжники и старейшины.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32807, 'rst66', 'mat', 26, 56, 'Сие же всё было, да сбудутся писания пророков. Тогда все ученики, оставив Его, бежали.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32806, 'rst66', 'mat', 26, 55, 'В тот час сказал Иисус народу: как будто на разбойника вышли вы с мечами и кольями взять Меня; каждый день с вами сидел Я, уча в храме, и вы не брали Меня.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32805, 'rst66', 'mat', 26, 54, 'Как же сбудутся Писания, что так должно быть?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32804, 'rst66', 'mat', 26, 53, 'или думаешь, что Я не могу теперь умолить Отца Моего, и Он представит Мне более, нежели двенадцать легионов Ангелов?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32803, 'rst66', 'mat', 26, 52, 'Тогда говорит ему Иисус: возврати меч твой в его место, ибо все, взявшие меч, мечом погибнут;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32802, 'rst66', 'mat', 26, 51, 'И вот, один из бывших с Иисусом, простерши руку, извлек меч свой и, ударив раба первосвященникова, отсек ему ухо.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32801, 'rst66', 'mat', 26, 50, 'Иисус же сказал ему: друг, для чего ты пришел? Тогда подошли и возложили руки на Иисуса, и взяли Его.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32800, 'rst66', 'mat', 26, 49, 'И, тотчас подойдя к Иисусу, сказал: радуйся, Равви&#769;! И поцеловал Его.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32799, 'rst66', 'mat', 26, 48, 'Предающий же Его дал им знак, сказав: Кого я поцелую, Тот и есть, возьмите Его.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32798, 'rst66', 'mat', 26, 47, 'И, когда еще говорил Он, вот, Иуда, один из Двенадцати, пришел, и с ним множество народа с мечами и кольями, от первосвященников и старейшин народных.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32797, 'rst66', 'mat', 26, 46, 'встаньте, пойдем: вот, приблизился предающий Меня.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32796, 'rst66', 'mat', 26, 45, 'Тогда приходит к ученикам Своим и говорит им: вы всё еще спите и почиваете? вот, приблизился час, и Сын Человеческий предается в руки грешников;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32795, 'rst66', 'mat', 26, 44, 'И, оставив их, отошел опять и помолился в третий раз, сказав то же слово.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32794, 'rst66', 'mat', 26, 43, 'И, придя, находит их опять спящими, ибо у них глаза отяжелели.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32793, 'rst66', 'mat', 26, 42, 'Еще, отойдя в другой раз, молился, говоря: Отче Мой! если не может чаша сия миновать Меня, чтобы Мне не пить ее, да будет воля Твоя.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32792, 'rst66', 'mat', 26, 41, 'Бодрствуйте и молитесь, чтобы не впасть в искушение: дух бодр, плоть же немощна.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32791, 'rst66', 'mat', 26, 40, 'И приходит к ученикам и находит их спящими, и говорит Петру: та&#769;к ли не могли вы один час бодрствовать со Мною?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32790, 'rst66', 'mat', 26, 39, 'И, отойдя немного, пал на лицо Свое, молился и говорил: Отче Мой! если возможно, да минует Меня чаша сия; впрочем, не как Я хочу, но как Ты.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32789, 'rst66', 'mat', 26, 38, 'Тогда говорит им Иисус: душа Моя скорбит смертельно; побудьте здесь и бодрствуйте со Мною.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32788, 'rst66', 'mat', 26, 37, 'И, взяв с Собою Петра и обоих сыновей Зеведеевых, начал скорбеть и тосковать.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32787, 'rst66', 'mat', 26, 36, 'Потом приходит с ними Иисус на место, называемое Гефсимания, и говорит ученикам: посидите тут, пока Я пойду, помолюсь там.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32786, 'rst66', 'mat', 26, 35, 'Говорит Ему Петр: хотя бы надлежало мне и умереть с Тобою, не отрекусь от Тебя. Подобное говорили и все ученики.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32785, 'rst66', 'mat', 26, 34, 'Иисус сказал ему: истинно говорю тебе, что в эту ночь, прежде нежели пропоет петух, трижды отречешься от Меня.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32784, 'rst66', 'mat', 26, 33, 'Петр сказал Ему в ответ: если и все соблазнятся о Тебе, я никогда не соблазнюсь.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32783, 'rst66', 'mat', 26, 32, 'по воскресении же Моем предварю вас в Галилее.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32782, 'rst66', 'mat', 26, 31, 'Тогда говорит им Иисус: все вы соблазнитесь о Мне в эту ночь, ибо написано: «поражу пастыря, и рассеются овцы стада»;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32781, 'rst66', 'mat', 26, 30, 'И, воспев, пошли на гору Елеонскую.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32780, 'rst66', 'mat', 26, 29, 'Сказываю же вам, что отныне не буду пить от плода сего виноградного до того дня, когда буду пить с вами новое <i>вино</i> в Царстве Отца Моего.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32779, 'rst66', 'mat', 26, 28, 'ибо сие есть Кровь Моя нового завета, за многих изливаемая во оставление грехов.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32778, 'rst66', 'mat', 26, 27, 'И, взяв чашу и благодарив, подал им и сказал: пейте из нее все,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32777, 'rst66', 'mat', 26, 26, 'И когда они ели, Иисус взял хлеб и, благословив, преломил и, раздавая ученикам, сказал: приимите, ядите: сие есть Тело Мое.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32776, 'rst66', 'mat', 26, 25, 'При сем и Иуда, предающий Его, сказал: не я ли, Равви&#769;? <i>Иисус</i> говорит ему: ты сказал.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32775, 'rst66', 'mat', 26, 24, 'впрочем, Сын Человеческий идет, как писано о Нем, но горе тому человеку, которым Сын Человеческий предается: лучше было бы этому человеку не родиться.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32774, 'rst66', 'mat', 26, 23, 'Он же сказал в ответ: опустивший со Мною руку в блюдо, этот предаст Меня;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32773, 'rst66', 'mat', 26, 22, 'Они весьма опечалились и начали говорить Ему, каждый из них: не я ли, Господи?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32772, 'rst66', 'mat', 26, 21, 'и когда они ели, сказал: истинно говорю вам, что один из вас предаст Меня.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32771, 'rst66', 'mat', 26, 20, 'Когда же настал вечер, Он возлег с двенадцатью учениками;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32770, 'rst66', 'mat', 26, 19, 'Ученики сделали, как повелел им Иисус, и приготовили пасху.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32769, 'rst66', 'mat', 26, 18, 'Он сказал: пойдите в город к такому‐то и скажите ему: «Учитель говорит: время Мое близко; у тебя совершу пасху с учениками Моими».');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32768, 'rst66', 'mat', 26, 17, 'В первый же день опресночный приступили ученики к Иисусу и сказали Ему: где велишь нам приготовить Тебе пасху?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32767, 'rst66', 'mat', 26, 16, 'и с того времени он искал удобного случая предать Его.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32766, 'rst66', 'mat', 26, 15, 'и сказал: что вы дадите мне, и я вам предам Его? Они предложили ему тридцать сребреников;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32765, 'rst66', 'mat', 26, 14, 'Тогда один из Двенадцати, называемый Иуда Искариот, пошел к первосвященникам');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32764, 'rst66', 'mat', 26, 13, 'истинно говорю вам: где ни будет проповедано Евангелие сие в целом мире, сказано будет в память ее и о том, что она сделала.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32763, 'rst66', 'mat', 26, 12, 'возлив миро сие на тело Мое, она приготовила Меня к погребению;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32762, 'rst66', 'mat', 26, 11, 'ибо нищих всегда имеете с собою, а Меня не всегда имеете;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32761, 'rst66', 'mat', 26, 10, 'Но Иисус, уразумев сие, сказал им: что смущаете женщину? она доброе дело сделала для Меня:');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32760, 'rst66', 'mat', 26, 9, 'Ибо можно было бы продать это миро за большую цену и дать нищим.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32759, 'rst66', 'mat', 26, 8, 'Увидев это, ученики Его вознегодовали и говорили: к чему такая трата?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32758, 'rst66', 'mat', 26, 7, 'приступила к Нему женщина с алавастровым сосудом мира драгоценного и возливала Ему возлежащему на голову.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32757, 'rst66', 'mat', 26, 6, 'Когда же Иисус был в Вифании, в доме Симона прокаженного,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32756, 'rst66', 'mat', 26, 5, 'но говорили: только не в праздник, чтобы не сделалось возмущения в народе.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32755, 'rst66', 'mat', 26, 4, 'и положили в совете взять Иисуса хитростью и убить;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32754, 'rst66', 'mat', 26, 3, 'Тогда собрались первосвященники и книжники и старейшины народа во двор первосвященника, по имени Каиафы,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32753, 'rst66', 'mat', 26, 2, 'вы знаете, что через два дня будет Пасха, и Сын Человеческий предан будет на распятие.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32752, 'rst66', 'mat', 26, 1, 'Когда Иисус окончил все слова сии, то сказал ученикам Своим:');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32751, 'rst66', 'mat', 25, 46, 'И пойдут сии в му&#769;ку вечную, а праведники в жизнь вечную.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32750, 'rst66', 'mat', 25, 45, 'Тогда скажет им в ответ: «истинно говорю вам: так как вы не сделали этого одному из сих меньших, то не сделали Мне».');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32749, 'rst66', 'mat', 25, 44, 'Тогда и они скажут Ему в ответ: «Господи! когда мы видели Тебя алчущим, или жаждущим, или странником, или нагим, или больным, или в темнице, и не послужили Тебе?»');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32748, 'rst66', 'mat', 25, 43, 'был странником, и не приняли Меня; был наг, и не одели Меня; болен и в темнице, и не посетили Меня».');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32747, 'rst66', 'mat', 25, 42, 'ибо алкал Я, и вы не дали Мне есть; жаждал, и вы не напоили Меня;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32746, 'rst66', 'mat', 25, 41, 'Тогда скажет и тем, которые по левую сторону: «идите от Меня, проклятые, в огонь вечный, уготованный диаволу и ангелам его:');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32745, 'rst66', 'mat', 25, 40, 'И Царь скажет им в ответ: «истинно говорю вам: так как вы сделали это одному из сих братьев Моих меньших, то сделали Мне».');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32744, 'rst66', 'mat', 25, 39, 'Когда мы видели Тебя больным, или в темнице, и пришли к Тебе?»');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32743, 'rst66', 'mat', 25, 38, 'Когда мы видели Тебя странником, и приняли? или нагим, и одели?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32742, 'rst66', 'mat', 25, 37, 'Тогда праведники скажут Ему в ответ: «Господи! когда мы видели Тебя алчущим, и накормили? или жаждущим, и напоили?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32741, 'rst66', 'mat', 25, 36, 'был наг, и вы одели Меня; был болен, и вы посетили Меня; в темнице был, и вы пришли ко Мне».');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32740, 'rst66', 'mat', 25, 35, 'ибо алкал Я, и вы дали Мне есть; жаждал, и вы напоили Меня; был странником, и вы приняли Меня;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32739, 'rst66', 'mat', 25, 34, 'Тогда скажет Царь тем, которые по правую сторону Его: «приидите, благословенные Отца Моего, наследуйте Царство, уготованное вам от создания мира:');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32738, 'rst66', 'mat', 25, 33, 'и поставит овец по правую Свою сторону, а козлов &mdash; по левую.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32737, 'rst66', 'mat', 25, 32, 'и соберутся пред Ним все народы; и отделит одних от других, как пастырь отделяет овец от козлов;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32736, 'rst66', 'mat', 25, 31, 'Когда же приидет Сын Человеческий во славе Своей и все святые Ангелы с Ним, тогда сядет на Престоле славы Своей,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32735, 'rst66', 'mat', 25, 30, 'а негодного раба выбросьте во тьму внешнюю: там будет плач и скрежет зубов». Сказав сие, <i>Иисус</i> возгласил: кто имеет уши слышать, да слышит!');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32734, 'rst66', 'mat', 25, 29, 'ибо всякому имеющему дастся и приумножится, а у неимеющего отнимется и то, что имеет;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32733, 'rst66', 'mat', 25, 28, 'итак, возьмите у него талант и дайте имеющему десять талантов,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32732, 'rst66', 'mat', 25, 27, 'посему надлежало тебе отдать серебро мое торгующим, и я, придя, получил бы мое с прибылью;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32731, 'rst66', 'mat', 25, 26, 'Господин же его сказал ему в ответ: «лукавый раб и ленивый! ты знал, что я жну, где не сеял, и собираю, где не рассыпа&#769;л;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32730, 'rst66', 'mat', 25, 25, 'и, убоявшись, пошел и скрыл талант твой в земле; вот тебе твое».');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32729, 'rst66', 'mat', 25, 24, 'Подошел и получивший один талант и сказал: «господин! я знал тебя, что ты человек жестокий, жнешь, где не сеял, и собираешь, где не рассыпа&#769;л,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32728, 'rst66', 'mat', 25, 23, 'Господин его сказал ему: «хорошо, добрый и верный раб! в малом ты был верен, над многим тебя поставлю; войди в радость господина твоего».');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32727, 'rst66', 'mat', 25, 22, 'Подошел также и получивший два таланта и сказал: «господин! два таланта ты дал мне; вот, другие два таланта я приобрел на них».');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32726, 'rst66', 'mat', 25, 21, 'Господин его сказал ему: «хорошо, добрый и верный раб! в малом ты был верен, над многим тебя поставлю; войди в радость господина твоего».');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32725, 'rst66', 'mat', 25, 20, 'И, подойдя, получивший пять талантов принес другие пять талантов и говорит: «господин! пять талантов ты дал мне; вот, другие пять талантов я приобрел на них».');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32724, 'rst66', 'mat', 25, 19, 'По долгом времени, приходит господин рабов тех и требует у них отчета.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32723, 'rst66', 'mat', 25, 18, 'получивший же один талант пошел и закопал <i>его</i> в землю и скрыл серебро господина своего.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32722, 'rst66', 'mat', 25, 17, 'точно так же и получивший два таланта приобрел другие два;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32721, 'rst66', 'mat', 25, 16, 'Получивший пять талантов пошел, употребил их в дело и приобрел другие пять талантов;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32720, 'rst66', 'mat', 25, 15, 'и одному дал он пять талантов, другому два, иному один, каждому по его силе; и тотчас отправился.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32719, 'rst66', 'mat', 25, 14, 'Ибо <i>Он поступит</i>, как человек, который, отправляясь в чужую страну, призвал рабов своих и поручил им имение свое:');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32718, 'rst66', 'mat', 25, 13, 'Итак, бодрствуйте, потому что не знаете ни дня, ни часа, в который приидет Сын Человеческий.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32717, 'rst66', 'mat', 25, 12, 'Он же сказал им в ответ: «истинно говорю вам: не знаю вас».');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32716, 'rst66', 'mat', 25, 11, 'после приходят и прочие девы, и говорят: «Господи! Господи! отвори нам».');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32715, 'rst66', 'mat', 25, 10, 'Когда же пошли они покупать, пришел жених, и готовые вошли с ним на брачный пир, и двери затворились;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32714, 'rst66', 'mat', 25, 9, 'А мудрые отвечали: «чтобы не случилось недостатка и у нас и у вас, пойдите лучше к продающим и купите себе».');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32713, 'rst66', 'mat', 25, 8, 'Неразумные же сказали мудрым: «дайте нам вашего масла, потому что светильники наши гаснут».');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32712, 'rst66', 'mat', 25, 7, 'Тогда встали все девы те и поправили светильники свои.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32711, 'rst66', 'mat', 25, 6, 'Но в полночь раздался крик: «вот, жених идет, выходите навстречу ему».');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32710, 'rst66', 'mat', 25, 5, 'И как жених замедлил, то задремали все и уснули.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32709, 'rst66', 'mat', 25, 4, 'Мудрые же, вместе со светильниками своими, взяли масла в сосудах своих.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32708, 'rst66', 'mat', 25, 3, 'Неразумные, взяв светильники свои, не взяли с собою масла.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32707, 'rst66', 'mat', 25, 2, 'Из них пять было мудрых и пять неразумных.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32706, 'rst66', 'mat', 25, 1, 'Тогда подобно будет Царство Небесное десяти девам, которые, взяв светильники свои, вышли навстречу жениху.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32705, 'rst66', 'mat', 24, 51, 'и рассечет его, и подвергнет его одной участи с лицемерами; там будет плач и скрежет зубов.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32704, 'rst66', 'mat', 24, 50, 'то придет господин раба того в день, в который он не ожидает, и в час, в который не думает,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32703, 'rst66', 'mat', 24, 49, 'и начнет бить товарищей своих и есть и пить с пьяницами, &mdash;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32702, 'rst66', 'mat', 24, 48, 'Если же раб тот, будучи зол, скажет в сердце своем: «не скоро придет господин мой»,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32701, 'rst66', 'mat', 24, 47, 'истинно говорю вам, что над всем имением своим поставит его.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32700, 'rst66', 'mat', 24, 46, 'Блажен тот раб, которого господин его, придя, найдет поступающим так;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32699, 'rst66', 'mat', 24, 45, 'Кто&#769; же верный и благоразумный раб, которого господин его поставил над слугами своими, чтобы давать им пищу вовремя?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32698, 'rst66', 'mat', 24, 44, 'Потому и вы будьте готовы, ибо в который час не думаете, приидет Сын Человеческий.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32697, 'rst66', 'mat', 24, 43, 'Но это вы знаете, что, если бы ведал хозяин дома, в какую стражу придет вор, то бодрствовал бы и не дал бы подкопать дома своего.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32696, 'rst66', 'mat', 24, 42, 'Итак, бодрствуйте, потому что не знаете, в который час Господь ваш приидет.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32695, 'rst66', 'mat', 24, 41, 'две мелющие в жерновах: одна берется, а другая оставляется.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32694, 'rst66', 'mat', 24, 40, 'тогда будут двое на поле: один берется, а другой оставляется;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32693, 'rst66', 'mat', 24, 39, 'и не думали, пока не пришел потоп и не истребил всех, &mdash; так будет и пришествие Сына Человеческого;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32692, 'rst66', 'mat', 24, 38, 'ибо, ка&#769;к во дни перед потопом ели, пили, женились и выходили замуж, до того дня, как вошел Ной в ковчег,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32691, 'rst66', 'mat', 24, 37, 'но, ка&#769;к было во дни Ноя, так будет и в пришествие Сына Человеческого:');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32690, 'rst66', 'mat', 24, 36, 'О дне же том и часе никто не знает, ни Ангелы небесные, а только Отец Мой один;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32689, 'rst66', 'mat', 24, 35, 'небо и земля прейдут, но слова Мои не прейдут.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32688, 'rst66', 'mat', 24, 34, 'Истинно говорю вам: не прейдет род сей, как всё сие будет;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32687, 'rst66', 'mat', 24, 33, 'так, когда вы увидите всё сие, знайте, что близко, при дверях.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32686, 'rst66', 'mat', 24, 32, 'От смоковницы возьмите подобие: когда ветви ее становятся уже мягки и пускают листья, то знаете, что близко лето;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32685, 'rst66', 'mat', 24, 31, 'и пошлет Ангелов Своих с трубою громогласною, и соберут избранных Его от четырех ветров, от края небес до края их.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32684, 'rst66', 'mat', 24, 30, 'тогда явится знамение Сына Человеческого на небе; и тогда восплачутся все племена земные и увидят Сына Человеческого, грядущего на облаках небесных с силою и славою великою;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32683, 'rst66', 'mat', 24, 29, 'И вдруг, после скорби дней тех, солнце померкнет, и луна не даст света своего, и звезды спадут с неба, и силы небесные поколеблются;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32682, 'rst66', 'mat', 24, 28, 'ибо, где будет труп, там соберутся орлы.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32681, 'rst66', 'mat', 24, 27, 'ибо, как молния исходит от востока и видна бывает даже до запада, так будет пришествие Сына Человеческого;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32680, 'rst66', 'mat', 24, 26, 'Итак, если скажут вам: «вот, <i>Он</i> в пустыне», &mdash; не выходи&#769;те; «вот, <i>Он</i> в потаенных комнатах», &mdash; не верьте;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32679, 'rst66', 'mat', 24, 25, 'Вот, Я наперед сказал вам.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32678, 'rst66', 'mat', 24, 24, 'Ибо восстанут лжехристы и лжепророки, и дадут великие знамения и чудеса, чтобы прельстить, если возможно, и избранных.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32677, 'rst66', 'mat', 24, 23, 'Тогда, если кто скажет вам: «вот, здесь Христос», или «там», &mdash; не верьте.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32676, 'rst66', 'mat', 24, 22, 'И если бы не сократились те дни, то не спаслась бы никакая плоть; но ради избранных сократятся те дни.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32675, 'rst66', 'mat', 24, 21, 'ибо тогда будет великая скорбь, какой не было от начала мира доныне, и не будет.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32674, 'rst66', 'mat', 24, 20, 'Моли&#769;тесь, чтобы не случилось бегство ваше зимою или в субботу,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32673, 'rst66', 'mat', 24, 19, 'Горе же беременным и питающим сосцами в те дни!');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32672, 'rst66', 'mat', 24, 18, 'и кто на поле, тот да не обращается назад взять одежды свои.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32671, 'rst66', 'mat', 24, 17, 'и кто на кровле, тот да не сходит взять что‐нибудь из дома своего;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32670, 'rst66', 'mat', 24, 16, 'тогда находящиеся в Иудее да бегут в горы;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32669, 'rst66', 'mat', 24, 15, 'Итак, когда увидите мерзость запустения, реченную через пророка Даниила, стоящую на святом месте, &mdash; читающий да разумеет, &mdash;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32668, 'rst66', 'mat', 24, 14, 'И проповедано будет сие Евангелие Царствия по всей вселенной, во свидетельство всем народам; и тогда придет конец.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32667, 'rst66', 'mat', 24, 13, 'претерпевший же до конца спасется.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32666, 'rst66', 'mat', 24, 12, 'и, по причине умножения беззакония, во многих охладеет любовь;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32665, 'rst66', 'mat', 24, 11, 'и многие лжепророки восстанут, и прельстят многих;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32664, 'rst66', 'mat', 24, 10, 'и тогда соблазнятся многие, и друг друга будут предавать, и возненавидят друг друга;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32663, 'rst66', 'mat', 24, 9, 'Тогда будут предавать вас на мучения и убивать вас; и вы будете ненавидимы всеми народами за имя Мое;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32662, 'rst66', 'mat', 24, 8, 'всё же это &mdash; начало болезней.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32661, 'rst66', 'mat', 24, 7, 'ибо восстанет народ на народ, и царство на царство; и будут глады, моры и землетрясения по местам;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32660, 'rst66', 'mat', 24, 6, 'Также услышите о войнах и о военных слухах. Смотрите, не ужасайтесь, ибо надлежит всему тому быть, но это еще не конец:');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32659, 'rst66', 'mat', 24, 5, 'ибо многие придут под именем Моим, и будут говорить: «я Христос», и многих прельстят.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32658, 'rst66', 'mat', 24, 4, 'Иисус сказал им в ответ: берегитесь, чтобы кто не прельстил вас,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32657, 'rst66', 'mat', 24, 3, 'Когда же сидел Он на горе Елеонской, то приступили к Нему ученики наедине и спросили: скажи нам, когда это будет? и какой признак Твоего пришествия и кончины века?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32656, 'rst66', 'mat', 24, 2, 'Иисус же сказал им: видите ли всё это? Истинно говорю вам: не останется здесь камня на камне; всё будет разрушено.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32655, 'rst66', 'mat', 24, 1, 'И выйдя, Иисус шел от храма; и приступили ученики Его, чтобы показать Ему здания храма.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32654, 'rst66', 'mat', 23, 39, 'Ибо сказываю вам: не увидите Меня отныне, доколе не воскликнете: «благословен Грядый во имя Господне!»');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32653, 'rst66', 'mat', 23, 38, 'Се, оставляется вам дом ваш пуст.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32652, 'rst66', 'mat', 23, 37, 'Иерусалим, Иерусалим, избивающий пророков и камнями побивающий посланных к тебе! сколько раз хотел Я собрать детей твоих, как птица собирает птенцов своих под крылья, и вы не захотели!');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32651, 'rst66', 'mat', 23, 36, 'Истинно говорю вам, что всё сие придет на род сей.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32650, 'rst66', 'mat', 23, 35, 'да придет на вас вся кровь праведная, пролитая на земле, от крови Авеля праведного до крови Захарии, сына Варахиина, которого вы убили между храмом и жертвенником.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32649, 'rst66', 'mat', 23, 34, 'Посему, вот, Я посылаю к вам пророков, и мудрых, и книжников; и вы иных убьете и распнете, а иных будете бить в синагогах ваших и гнать из города в город;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32648, 'rst66', 'mat', 23, 33, 'Змии, порождения ехиднины! как убежите вы от осуждения в геенну?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32647, 'rst66', 'mat', 23, 32, 'дополняйте же меру отцов ваших.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32646, 'rst66', 'mat', 23, 31, 'таким образом вы сами против себя свидетельствуете, что вы сыновья тех, которые избили пророков;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32645, 'rst66', 'mat', 23, 30, 'и говорите: «если бы мы были во дни отцов наших, то не были бы сообщниками их в <i>пролитии</i> крови пророков»;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32644, 'rst66', 'mat', 23, 29, 'Горе вам, книжники и фарисеи, лицемеры, что строите гробницы пророкам и украшаете памятники праведников,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32643, 'rst66', 'mat', 23, 28, 'так и вы по наружности кажетесь людям праведными, а внутри исполнены лицемерия и беззакония.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32642, 'rst66', 'mat', 23, 27, 'Горе вам, книжники и фарисеи, лицемеры, что уподобляетесь окрашенным гробам, которые снаружи кажутся красивыми, а внутри полны костей мертвых и всякой нечистоты;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32641, 'rst66', 'mat', 23, 26, 'Фарисей слепой! очисти прежде внутренность чаши и блюда, чтобы чиста была и внешность их.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32640, 'rst66', 'mat', 23, 25, 'Горе вам, книжники и фарисеи, лицемеры, что очищаете внешность чаши и блюда, между тем как внутри они полны хищения и неправды.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32639, 'rst66', 'mat', 23, 24, 'Вожди слепые, оцеживающие комара, а верблюда поглощающие!');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32638, 'rst66', 'mat', 23, 23, 'Горе вам, книжники и фарисеи, лицемеры, что даете десятину с мяты, аниса и тмина, и оставили важнейшее в законе: суд, милость и веру; сие надлежало делать, и того не оставлять.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32637, 'rst66', 'mat', 23, 22, 'и клянущийся небом клянется Престолом Божиим и Сидящим на нем.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32636, 'rst66', 'mat', 23, 21, 'и клянущийся храмом клянется им и Живущим в нем;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32635, 'rst66', 'mat', 23, 20, 'Итак, клянущийся жертвенником клянется им и всем, что на нем;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32634, 'rst66', 'mat', 23, 19, 'Безумные и слепые! что больше: дар, или жертвенник, освящающий дар?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32633, 'rst66', 'mat', 23, 18, 'Также: «если кто поклянется жертвенником, то ничего, если же кто поклянется даром, который на нем, то повинен».');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32632, 'rst66', 'mat', 23, 17, 'Безумные и слепые! что больше: золото, или храм, освящающий золото?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32631, 'rst66', 'mat', 23, 16, 'Горе вам, вожди слепые, которые говорите: «если кто поклянется храмом, то ничего, а если кто поклянется золотом храма, то повинен».');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32630, 'rst66', 'mat', 23, 15, 'Горе вам, книжники и фарисеи, лицемеры, что обходите море и сушу, дабы обратить хотя одного; и когда это случится, делаете его сыном геенны, вдвое худшим вас.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32629, 'rst66', 'mat', 23, 14, 'Горе вам, книжники и фарисеи, лицемеры, что поедаете домы вдов и лицемерно долго мо&#769;литесь: за то&#769; примете тем большее осуждение.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32628, 'rst66', 'mat', 23, 13, 'Горе вам, книжники и фарисеи, лицемеры, что затворяете Царство Небесное человекам, ибо сами не вхо&#769;дите и хотящих войти не допускаете.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32627, 'rst66', 'mat', 23, 12, 'ибо, кто возвышает себя, тот унижен будет, а кто унижает себя, тот возвысится.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32626, 'rst66', 'mat', 23, 11, 'Больший из вас да будет вам слуга:');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32625, 'rst66', 'mat', 23, 10, 'и не называйтесь наставниками, ибо один у вас Наставник &mdash; Христос.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32624, 'rst66', 'mat', 23, 9, 'и отцом себе не называйте никого на земле, ибо один у вас Отец, Который на небесах;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32623, 'rst66', 'mat', 23, 8, 'А вы не называйтесь учителями, ибо один у вас Учитель &mdash; Христос, все же вы &mdash; братья;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32622, 'rst66', 'mat', 23, 7, 'и приветствия в народных собраниях, и чтобы люди звали их: «учитель! учитель!»');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32621, 'rst66', 'mat', 23, 6, 'также любят предвозлежания на пиршествах, и председания в синагогах,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32620, 'rst66', 'mat', 23, 5, 'все же дела свои делают с тем, чтобы видели их люди: расширяют хранилища свои и увеличивают воскрилия одежд своих;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32619, 'rst66', 'mat', 23, 4, 'связывают бремена тяжелые и неудобоносимые и возлагают на плечи людям, а сами не хотят и перстом двинуть их;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32618, 'rst66', 'mat', 23, 3, 'итак, всё, что они велят вам соблюдать, соблюдайте и делайте; по делам же их не поступайте, ибо они говорят, и не делают:');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32617, 'rst66', 'mat', 23, 2, 'и сказал: на Моисеевом седалище сели книжники и фарисеи;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32616, 'rst66', 'mat', 23, 1, 'Тогда Иисус начал говорить народу и ученикам Своим');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32615, 'rst66', 'mat', 22, 46, 'И никто не мог отвечать Ему ни слова; и с того дня никто уже не смел спрашивать Его.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32614, 'rst66', 'mat', 22, 45, 'Итак, если Давид называет Его Господом, как же Он Сын ему?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32613, 'rst66', 'mat', 22, 44, '«сказал Господь Господу моему: седи одесную Меня, доколе положу врагов Твоих в подножие ног Твоих»?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32612, 'rst66', 'mat', 22, 43, 'Говорит им: как же Давид, по вдохновению, называет Его Господом, когда говорит:');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32611, 'rst66', 'mat', 22, 42, 'что&#769; вы думаете о Христе? чей Он Сын? Говорят Ему: Давидов.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32610, 'rst66', 'mat', 22, 41, 'Когда же собрались фарисеи, Иисус спросил их:');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32609, 'rst66', 'mat', 22, 40, 'на сих двух заповедях утверждается весь закон и пророки.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32608, 'rst66', 'mat', 22, 39, 'вторая же подобная ей: «возлюби ближнего твоего, как самого себя»;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32607, 'rst66', 'mat', 22, 38, 'сия есть первая и наибольшая заповедь;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32606, 'rst66', 'mat', 22, 37, 'Иисус сказал ему: «возлюби Господа Бога твоего всем сердцем твоим и всею душою твоею и всем разумением твоим» &mdash;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32605, 'rst66', 'mat', 22, 36, 'Учитель! какая наибольшая заповедь в законе?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32604, 'rst66', 'mat', 22, 35, 'И один из них, законник, искушая Его, спросил, говоря:');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32603, 'rst66', 'mat', 22, 34, 'А фарисеи, услышав, что Он привел саддукеев в молчание, собрались вместе.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32602, 'rst66', 'mat', 22, 33, 'И, слыша, народ дивился учению Его.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32601, 'rst66', 'mat', 22, 32, '«Я Бог Авраама, и Бог Исаака, и Бог Иакова»? Бог не есть Бог мертвых, но живых.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32600, 'rst66', 'mat', 22, 31, 'А о воскресении мертвых не читали ли вы реченного вам Богом:');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32599, 'rst66', 'mat', 22, 30, 'ибо в воскресении ни женятся, ни выходят замуж, но пребывают, как Ангелы Божии на небесах.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32598, 'rst66', 'mat', 22, 29, 'Иисус сказал им в ответ: заблуждаетесь, не зная Писаний, ни силы Божией,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32597, 'rst66', 'mat', 22, 28, 'итак, в воскресении, которого из семи будет она женою? ибо все имели ее.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32596, 'rst66', 'mat', 22, 27, 'после же всех умерла и жена;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32595, 'rst66', 'mat', 22, 26, 'подобно и второй, и третий, даже до седьмого;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32594, 'rst66', 'mat', 22, 25, 'было у нас семь братьев; первый, женившись, умер и, не имея детей, оставил жену свою брату своему;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32593, 'rst66', 'mat', 22, 24, 'Учитель! Моисей сказал: «если кто умрет, не имея детей, то брат его пусть возьмет за себя жену его и восстановит семя брату своему»;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32592, 'rst66', 'mat', 22, 23, 'В тот день приступили к Нему саддукеи, которые говорят, что нет воскресения, и спросили Его:');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32591, 'rst66', 'mat', 22, 22, 'Услышав это, они удивились и, оставив Его, ушли.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32590, 'rst66', 'mat', 22, 21, 'Говорят Ему: кесаревы. Тогда говорит им: итак, отдавайте кесарево кесарю, а Божие Богу.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32589, 'rst66', 'mat', 22, 20, 'И говорит им: чье это изображение и надпись?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32588, 'rst66', 'mat', 22, 19, 'Покажите Мне монету, которою платится по&#769;дать. Они принесли Ему динарий.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32587, 'rst66', 'mat', 22, 18, 'Но Иисус, видя лукавство их, сказал: что искушаете Меня, лицемеры?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32586, 'rst66', 'mat', 22, 17, 'итак, скажи нам, как Тебе кажется, позволительно ли давать по&#769;дать кесарю, или нет?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32585, 'rst66', 'mat', 22, 16, 'И посылают к Нему учеников своих с иродианами, говоря: Учитель! мы знаем, что Ты справедлив, и истинно пути Божию учишь, и не заботишься об угождении кому‐либо, ибо не смотришь ни на какое лицо;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32584, 'rst66', 'mat', 22, 15, 'Тогда фарисеи пошли и совещались, как бы уловить Его в словах.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32583, 'rst66', 'mat', 22, 14, 'ибо много званых, а мало избранных.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32582, 'rst66', 'mat', 22, 13, 'Тогда сказал царь слугам: «связав ему руки и ноги, возьмите его и бросьте во тьму внешнюю; там будет плач и скрежет зубов»;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32581, 'rst66', 'mat', 22, 12, 'и говорит ему: «друг! как ты вошел сюда не в брачной одежде?» Он же молчал.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32580, 'rst66', 'mat', 22, 11, 'Царь, войдя посмотреть возлежащих, увидел там человека, одетого не в брачную одежду,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32579, 'rst66', 'mat', 22, 10, 'И рабы те, выйдя на дороги, собрали всех, кого только нашли, и злых и добрых; и брачный пир наполнился возлежащими.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32578, 'rst66', 'mat', 22, 9, 'итак, пойдите на распутия и всех, кого найдете, зовите на брачный пир».');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32577, 'rst66', 'mat', 22, 8, 'Тогда говорит он рабам своим: «брачный пир готов, а званые не были достойны;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32576, 'rst66', 'mat', 22, 7, 'Услышав о сем, царь разгневался, и, послав войска свои, истребил убийц оных и сжег город их.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32575, 'rst66', 'mat', 22, 6, 'прочие же, схватив рабов его, оскорбили и убили <i>их</i>.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32574, 'rst66', 'mat', 22, 5, 'Но они, пренебрегши то, пошли, кто на поле свое, а кто на торговлю свою;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32573, 'rst66', 'mat', 22, 4, 'Опять послал других рабов, сказав: «скажите званым: вот, я приготовил обед мой, тельцы мои и что откормлено, заколото, и всё готово; приходите на брачный пир».');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32572, 'rst66', 'mat', 22, 3, 'и послал рабов своих звать званых на брачный пир; и не хотели прийти.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32571, 'rst66', 'mat', 22, 2, 'Царство Небесное подобно человеку царю, который сделал брачный пир для сына своего');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32570, 'rst66', 'mat', 22, 1, 'Иисус, продолжая говорить им притчами, сказал:');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32569, 'rst66', 'mat', 21, 46, 'и старались схватить Его, но побоялись народа, потому что Его почитали за Пророка.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32568, 'rst66', 'mat', 21, 45, 'И, слышав притчи Его, первосвященники и фарисеи поняли, что Он о них говорит,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32567, 'rst66', 'mat', 21, 44, 'и тот, кто упадет на этот камень, разобьется, а на кого он упадет, того раздавит.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32566, 'rst66', 'mat', 21, 43, 'Потому сказываю вам, что отнимется от вас Царство Божие и дано будет народу, приносящему плоды его;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32565, 'rst66', 'mat', 21, 42, 'Иисус говорит им: неужели вы никогда не читали в Писании: «камень, который отвергли строители, тот самый сделался главою угла; это от Господа, и есть дивно в очах наших»?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32564, 'rst66', 'mat', 21, 41, 'Говорят Ему: злодеев сих предаст злой смерти, а виноградник отдаст другим виноградарям, которые будут отдавать ему плоды во времена свои.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32563, 'rst66', 'mat', 21, 40, 'Итак, когда придет хозяин виноградника, что сделает он с этими виноградарями?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32562, 'rst66', 'mat', 21, 39, 'И, схватив его, вывели вон из виноградника и убили.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32561, 'rst66', 'mat', 21, 38, 'Но виноградари, увидев сына, сказали друг другу: «это наследник; пойдем, убьем его и завладеем наследством его».');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32560, 'rst66', 'mat', 21, 37, 'Наконец, послал он к ним своего сына, говоря: «постыдятся сына моего».');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32559, 'rst66', 'mat', 21, 36, 'Опять послал он других слуг, больше прежнего; и с ними поступили так же.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32558, 'rst66', 'mat', 21, 35, 'виноградари, схватив слуг его, иного прибили, иного убили, а иного побили камнями.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32557, 'rst66', 'mat', 21, 34, 'Когда же приблизилось время плодов, он послал своих слуг к виноградарям взять свои плоды;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32556, 'rst66', 'mat', 21, 33, 'Выслушайте другую притчу: был некоторый хозяин дома, который насадил виноградник, обнес его оградою, выкопал в нем точило, построил башню и, отдав его виноградарям, отлучился.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32555, 'rst66', 'mat', 21, 32, 'ибо пришел к вам Иоанн путем праведности, и вы не поверили ему, а мытари и блудницы поверили ему; вы же, и видев это, не раскаялись после, чтобы поверить ему.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32554, 'rst66', 'mat', 21, 31, 'Который из двух исполнил волю отца? Говорят Ему: первый. Иисус говорит им: истинно говорю вам, что мытари и блудницы вперед вас идут в Царство Божие,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32553, 'rst66', 'mat', 21, 30, 'И подойдя к другому, он сказал то&#769; же. Этот сказал в ответ: «иду, государь», и не пошел.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32552, 'rst66', 'mat', 21, 29, 'Но он сказал в ответ: «не хочу»; а после, раскаявшись, пошел.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32551, 'rst66', 'mat', 21, 28, 'А как вам кажется? У одного человека было два сына; и он, подойдя к первому, сказал: «сын! пойди сегодня работай в винограднике моем».');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32550, 'rst66', 'mat', 21, 27, 'И сказали в ответ Иисусу: не знаем. Сказал им и Он: и Я вам не скажу, какою властью это делаю.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32549, 'rst66', 'mat', 21, 26, 'А если сказать: «от человеков», &mdash; боимся народа, ибо все почитают Иоанна за пророка.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32548, 'rst66', 'mat', 21, 25, 'крещение Иоанново откуда было: с небес, или от человеков? Они же рассуждали между собою: если скажем: «с небес», то Он скажет нам: «почему же вы не поверили ему?»');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32547, 'rst66', 'mat', 21, 24, 'Иисус сказал им в ответ: спрошу и Я вас об одном; если о том скажете Мне, то и Я вам скажу, какою властью это делаю;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32546, 'rst66', 'mat', 21, 23, 'И когда пришел Он в храм и учил, приступили к Нему первосвященники и старейшины народа и сказали: какою властью Ты это делаешь? и кто Тебе дал такую власть?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32545, 'rst66', 'mat', 21, 22, 'и всё, чего ни попросите в молитве с верою, полу&#769;чите.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32544, 'rst66', 'mat', 21, 21, 'Иисус же сказал им в ответ: истинно говорю вам, если будете иметь веру и не усомнитесь, не только сделаете то, что <i>сделано</i> со смоковницею, но если и горе сей скажете: «поднимись и ввергнись в море», &mdash; будет;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32543, 'rst66', 'mat', 21, 20, 'Увидев это, ученики удивились и говорили: как это тотчас засохла смоковница?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32542, 'rst66', 'mat', 21, 19, 'и увидев при дороге одну смоковницу, подошел к ней и, ничего не найдя на ней, кроме одних листьев, говорит ей: да не будет же впредь от тебя плода вовек. И смоковница тотчас засохла.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32541, 'rst66', 'mat', 21, 18, 'Поутру же, возвращаясь в город, взалкал;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32540, 'rst66', 'mat', 21, 17, 'И, оставив их, вышел вон из города в Вифанию и провел там ночь.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32539, 'rst66', 'mat', 21, 16, 'и сказали Ему: слышишь ли, что они говорят? Иисус же говорит им: да! разве вы никогда не читали: «из уст младенцев и грудных детей Ты устроил хвалу»?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32538, 'rst66', 'mat', 21, 15, 'Видев же первосвященники и книжники чудеса, которые Он сотворил, и детей, восклицающих в храме и говорящих: «осанна Сыну Давидову!» &mdash; вознегодовали');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32537, 'rst66', 'mat', 21, 14, 'И приступили к Нему в храме слепые и хромые, и Он исцелил их.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32536, 'rst66', 'mat', 21, 13, 'и говорил им: написано: «дом Мой домом молитвы наречется»; а вы сделали его вертепом разбойников.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32535, 'rst66', 'mat', 21, 12, 'И вошел Иисус в храм Божий и выгнал всех продающих и покупающих в храме, и опрокинул столы меновщиков и скамьи продающих голубей,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32534, 'rst66', 'mat', 21, 11, 'Народ же говорил: Сей есть Иисус, Пророк из Назарета Галилейского.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32533, 'rst66', 'mat', 21, 10, 'И когда вошел Он в Иерусалим, весь город пришел в движение и говорил: кто Сей?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32532, 'rst66', 'mat', 21, 9, 'народ же, предшествовавший и сопровождавший, восклицал: осанна Сыну Давидову! благословен Грядущий во имя Господне! осанна в вышних!');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32531, 'rst66', 'mat', 21, 8, 'Множество же народа постилали свои одежды по дороге, а другие резали ветви с дерев и постилали по дороге;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32530, 'rst66', 'mat', 21, 7, 'привели ослицу и молодого осла и положили на них одежды свои, и Он сел поверх их.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32529, 'rst66', 'mat', 21, 6, 'Ученики пошли и поступили так, как повелел им Иисус:');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32528, 'rst66', 'mat', 21, 5, '«скажите дщери Сионовой: се, Царь твой грядет к тебе кроткий, сидя на ослице и молодом осле, сыне подъяремной».');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32527, 'rst66', 'mat', 21, 4, 'Всё же сие было, да сбудется реченное через пророка, который говорит:');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32526, 'rst66', 'mat', 21, 3, 'и если кто скажет вам что‐нибудь, отвечайте, что они надобны Господу; и тотчас пошлет их.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32525, 'rst66', 'mat', 21, 2, 'сказав им: пойдите в селение, которое прямо перед вами; и тотчас найдете ослицу привязанную и молодого осла с нею; отвязав, приведите ко Мне;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32524, 'rst66', 'mat', 21, 1, 'И когда приблизились к Иерусалиму и пришли в Виффагию к горе Елеонской, тогда Иисус послал двух учеников,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32523, 'rst66', 'mat', 20, 34, 'Иисус же, умилосердившись, прикоснулся к глазам их; и тотчас прозрели глаза их, и они пошли за Ним.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32522, 'rst66', 'mat', 20, 33, 'Они говорят Ему: Господи! чтобы открылись глаза наши.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32521, 'rst66', 'mat', 20, 32, 'Иисус, остановившись, подозвал их и сказал: чего вы хотите от Меня?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32520, 'rst66', 'mat', 20, 31, 'Народ же заставлял их молчать; но они еще громче стали кричать: помилуй нас, Господи, Сын Давидов!');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32519, 'rst66', 'mat', 20, 30, 'И вот, двое слепых, сидевшие у дороги, услышав, что Иисус идет мимо, начали кричать: помилуй нас, Господи, Сын Давидов!');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32518, 'rst66', 'mat', 20, 29, 'И когда выходили они из Иерихона, за Ним следовало множество народа.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32517, 'rst66', 'mat', 20, 28, 'так как Сын Человеческий не <i>для того</i> пришел, чтобы Ему служили, но чтобы послужить и отдать душу Свою для искупления многих.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32516, 'rst66', 'mat', 20, 27, 'и кто хочет между вами быть первым, да будет вам рабом;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32515, 'rst66', 'mat', 20, 26, 'но между вами да не будет так: а кто хочет между вами быть бо&#769;льшим, да будет вам слугою;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32514, 'rst66', 'mat', 20, 25, 'Иисус же, подозвав их, сказал: вы знаете, что князья народов господствуют над ними, и вельможи властвуют ими;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32513, 'rst66', 'mat', 20, 24, 'Услышав <i>сие, прочие</i> десять <i>учеников</i> вознегодовали на двух братьев.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32512, 'rst66', 'mat', 20, 23, 'И говорит им: чашу Мою будете пить, и крещением, которым Я крещусь, будете креститься, но дать сесть у Меня по правую сторону и по левую &mdash; не от Меня <i>зависит</i>, но кому уготовано Отцом Моим.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32511, 'rst66', 'mat', 20, 22, 'Иисус сказал в ответ: не знаете, чего просите. Можете ли пить чашу, которую Я буду пить, или креститься крещением, которым Я крещусь? Они говорят Ему: можем.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32510, 'rst66', 'mat', 20, 21, 'Он сказал ей: чего ты хочешь? Она говорит Ему: скажи, чтобы сии два сына мои сели у Тебя один по правую сторону, а другой по левую в Царстве Твоем.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32509, 'rst66', 'mat', 20, 20, 'Тогда приступила к Нему мать сыновей Зеведеевых с сыновьями своими, кланяясь и чего‐то прося у Него.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32508, 'rst66', 'mat', 20, 19, 'и предадут Его язычникам на поругание и биение и распятие; и в третий день воскреснет.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32507, 'rst66', 'mat', 20, 18, 'вот, мы восходим в Иерусалим, и Сын Человеческий предан будет первосвященникам и книжникам, и осудят Его на смерть;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32506, 'rst66', 'mat', 20, 17, 'И, восходя в Иерусалим, Иисус дорогою отозвал двенадцать учеников одних, и сказал им:');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32505, 'rst66', 'mat', 20, 16, 'Так будут последние первыми, и первые последними, ибо много званых, а мало избранных.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32504, 'rst66', 'mat', 20, 15, 'разве я не властен в своем делать, что хочу? или глаз твой завистлив оттого, что я добр?»');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32503, 'rst66', 'mat', 20, 14, 'Возьми свое и пойди; я же хочу дать этому последнему <i>то&#769; же</i>, что и тебе;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32502, 'rst66', 'mat', 20, 13, 'Он же в ответ сказал одному из них: «друг! я не обижаю тебя; не за динарий ли ты договорился со мною?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32501, 'rst66', 'mat', 20, 12, 'и говорили: «эти последние работали один час, и ты сравнял их с нами, перенесшими тягость дня и зной».');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32500, 'rst66', 'mat', 20, 11, 'и, получив, стали роптать на хозяина дома');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32499, 'rst66', 'mat', 20, 10, 'Пришедшие же первыми думали, что они получат больше, но получили и они по динарию;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32498, 'rst66', 'mat', 20, 9, 'И пришедшие около одиннадцатого часа получили по динарию.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32497, 'rst66', 'mat', 20, 8, 'Когда же наступил вечер, говорит господин виноградника управителю своему: «позови работников и отдай им плату, начав с последних до первых».');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32496, 'rst66', 'mat', 20, 7, 'Они говорят ему: «никто нас не нанял». Он говорит им: «идите и вы в виноградник мой, и что следовать будет, полу&#769;чите».');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32495, 'rst66', 'mat', 20, 6, 'Наконец, выйдя около одиннадцатого часа, он нашел других, стоящих праздно, и говорит им: «что&#769; вы стоите здесь целый день праздно?»');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32494, 'rst66', 'mat', 20, 5, 'Опять выйдя около шестого и девятого часа, сделал то&#769; же.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32493, 'rst66', 'mat', 20, 4, 'и им сказал: «идите и вы в виноградник мой, и что следовать будет, дам вам». Они пошли.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32492, 'rst66', 'mat', 20, 3, 'выйдя около третьего часа, он увидел других, стоящих на торжище праздно,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32491, 'rst66', 'mat', 20, 2, 'и, договорившись с работниками по динарию на день, послал их в виноградник свой;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32490, 'rst66', 'mat', 20, 1, 'Ибо Царство Небесное подобно хозяину дома, который вышел рано поутру нанять работников в виноградник свой');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32489, 'rst66', 'mat', 19, 30, 'Многие же будут первые последними, и последние первыми.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32488, 'rst66', 'mat', 19, 29, 'И всякий, кто оставит до&#769;мы, или братьев, или сестер, или отца, или мать, или жену, или детей, или зе&#769;мли, ради имени Моего, получит во сто крат и наследует жизнь вечную.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32487, 'rst66', 'mat', 19, 28, 'Иисус же сказал им: истинно говорю вам, что вы, последовавшие за Мною, &mdash; в пакибытии, когда сядет Сын Человеческий на престоле славы Своей, сядете и вы на двенадцати престолах судить двенадцать колен Израилевых.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32486, 'rst66', 'mat', 19, 27, 'Тогда Петр, отвечая, сказал Ему: вот, мы оставили всё и последовали за Тобою; что же будет нам?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32485, 'rst66', 'mat', 19, 26, 'А Иисус, воззрев, сказал им: человекам это невозможно, Богу же всё возможно.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32484, 'rst66', 'mat', 19, 25, 'Услышав это, ученики Его весьма изумились и сказали: так кто же может спастись?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32483, 'rst66', 'mat', 19, 24, 'и еще говорю вам: удобнее верблюду пройти сквозь игольные уши, нежели богатому войти в Царство Божие.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32482, 'rst66', 'mat', 19, 23, 'Иисус же сказал ученикам Своим: истинно говорю вам, что трудно богатому войти в Царство Небесное;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32481, 'rst66', 'mat', 19, 22, 'Услышав слово сие, юноша отошел с печалью, потому что у него было большое имение.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32480, 'rst66', 'mat', 19, 21, 'Иисус сказал ему: если хочешь быть совершенным, пойди, продай имение твое и раздай нищим; и будешь иметь сокровище на небесах; и приходи и следуй за Мною.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32479, 'rst66', 'mat', 19, 20, 'Юноша говорит Ему: всё это сохранил я от юности моей; чего еще недостает мне?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32478, 'rst66', 'mat', 19, 19, '«почитай отца и мать»; и: «люби ближнего твоего, как самого себя».');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32477, 'rst66', 'mat', 19, 18, 'Говорит Ему: какие? Иисус же сказал: «не убивай»; «не прелюбодействуй»; «не кради»; «не лжесвидетельствуй»;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32476, 'rst66', 'mat', 19, 17, 'Он же сказал ему: что ты называешь Меня благим? Никто не благ, как только один Бог. Если же хочешь войти в жизнь <i>вечную</i>, соблюди заповеди.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32475, 'rst66', 'mat', 19, 16, 'И вот, некто, подойдя, сказал Ему: Учитель благий! что сделать мне доброго, чтобы иметь жизнь вечную?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32474, 'rst66', 'mat', 19, 15, 'И, возложив на них руки, пошел оттуда.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32473, 'rst66', 'mat', 19, 14, 'Но Иисус сказал: пустите детей и не препятствуйте им приходить ко Мне, ибо таковых есть Царство Небесное.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32472, 'rst66', 'mat', 19, 13, 'Тогда приведены были к Нему дети, чтобы Он возложил на них руки и помолился; ученики же возбраняли им.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32471, 'rst66', 'mat', 19, 12, 'ибо есть скопцы, которые из чрева матернего родились так; и есть скопцы, которые оскоплены от людей; и есть скопцы, которые сделали сами себя скопцами для Царства Небесного. Кто может вместить, да вместит.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32470, 'rst66', 'mat', 19, 11, 'Он же сказал им: не все вмещают слово сие, но кому дано,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32469, 'rst66', 'mat', 19, 10, 'Говорят Ему ученики Его: если такова обязанность человека к жене, то лучше не жениться.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32468, 'rst66', 'mat', 19, 9, 'но Я говорю вам: кто разведется с женою своею не за прелюбодеяние и женится на другой, <i>тот</i> прелюбодействует; и женившийся на разведенной прелюбодействует.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32467, 'rst66', 'mat', 19, 8, 'Он говорит им: Моисей по жестокосердию вашему позволил вам разводиться с женами вашими, а сначала не было так;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32466, 'rst66', 'mat', 19, 7, 'Они говорят Ему: как же Моисей заповедал давать разводное письмо и разводиться с нею?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32465, 'rst66', 'mat', 19, 6, 'так что они уже не двое, но одна плоть. Итак, что Бог сочетал, того человек да не разлучает.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32464, 'rst66', 'mat', 19, 5, 'И сказал: посему оставит человек отца и мать и прилепится к жене своей, и будут два одною плотью,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32463, 'rst66', 'mat', 19, 4, 'Он сказал им в ответ: не читали ли вы, что Сотворивший в начале мужчину и женщину сотворил их?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32462, 'rst66', 'mat', 19, 3, 'И приступили к Нему фарисеи и, искушая Его, говорили Ему: по всякой ли причине позволительно человеку разводиться с женою своею?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32461, 'rst66', 'mat', 19, 2, 'За Ним последовало много людей, и Он исцелил их там.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32460, 'rst66', 'mat', 19, 1, 'Когда Иисус окончил слова сии, то вышел из Галилеи и пришел в пределы Иудейские, Заиорданскою стороною.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32459, 'rst66', 'mat', 18, 35, 'Так и Отец Мой Небесный поступит с вами, если не простит каждый из вас от сердца своего брату своему согрешений его.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32458, 'rst66', 'mat', 18, 34, 'И, разгневавшись, государь его отдал его истязателям, пока не отдаст ему всего долга.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32457, 'rst66', 'mat', 18, 33, 'не надлежало ли и тебе помиловать товарища твоего, как и я помиловал тебя?»');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32456, 'rst66', 'mat', 18, 32, 'Тогда государь его призывает его и говорит: «злой раб! весь долг тот я простил тебе, потому что ты упросил меня;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32455, 'rst66', 'mat', 18, 31, 'Товарищи его, видев происшедшее, очень огорчились и, придя, рассказали государю своему всё бывшее.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32454, 'rst66', 'mat', 18, 30, 'Но тот не захотел, а пошел и посадил его в темницу, пока не отдаст долга.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32453, 'rst66', 'mat', 18, 29, 'Тогда товарищ его пал к ногам его, умолял его и говорил: «потерпи на мне, и всё отдам тебе».');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32452, 'rst66', 'mat', 18, 28, 'Раб же тот, выйдя, нашел одного из товарищей своих, который должен был ему сто динариев, и, схватив его, душил, говоря: «отдай мне, что должен».');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32451, 'rst66', 'mat', 18, 27, 'Государь, умилосердившись над рабом тем, отпустил его и долг простил ему.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32450, 'rst66', 'mat', 18, 26, 'тогда раб тот пал, и, кланяясь ему, говорил: «государь! потерпи на мне, и всё тебе заплачу».');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32449, 'rst66', 'mat', 18, 25, 'а как он не имел чем заплатить, то государь его приказал продать его, и жену его, и детей, и всё, что он имел, и заплатить;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32448, 'rst66', 'mat', 18, 24, 'когда начал он считаться, приведен был к нему некто, который должен был ему десять тысяч талантов;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32447, 'rst66', 'mat', 18, 23, 'Посему Царство Небесное подобно царю, который захотел сосчитаться с рабами своими;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32446, 'rst66', 'mat', 18, 22, 'Иисус говорит ему: не говорю тебе: «до семи», но до седмижды семидесяти раз.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32445, 'rst66', 'mat', 18, 21, 'Тогда Петр приступил к Нему и сказал: Господи! сколько раз прощать брату моему, согрешающему против меня? до семи ли раз?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32444, 'rst66', 'mat', 18, 20, 'ибо, где двое или трое собраны во имя Мое, там Я посреди них.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32443, 'rst66', 'mat', 18, 19, 'Истинно также говорю вам, что если двое из вас согласятся на земле просить о всяком деле, то, чего бы ни попросили, будет им от Отца Моего Небесного,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32442, 'rst66', 'mat', 18, 18, 'Истинно говорю вам: что вы свяжете на земле, то будет связано на небе; и что разрешите на земле, то будет разрешено на небе.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32441, 'rst66', 'mat', 18, 17, 'если же не послушает их, скажи церкви; а если и церкви не послушает, то да будет он тебе как язычник и мыта&#769;рь.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32440, 'rst66', 'mat', 18, 16, 'если же не послушает, возьми с собою еще одного или двух, дабы устами двух или трех свидетелей подтвердилось всякое слово;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32439, 'rst66', 'mat', 18, 15, 'Если же согрешит против тебя брат твой, пойди и обличи его между тобою и им одним; если послушает тебя, то приобрел ты брата твоего;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32438, 'rst66', 'mat', 18, 14, 'Так, нет воли Отца вашего Небесного, чтобы погиб один из малых сих.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32437, 'rst66', 'mat', 18, 13, 'И если случится найти ее, то, истинно говорю вам, он радуется о ней более, нежели о девяноста девяти незаблудившихся.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32436, 'rst66', 'mat', 18, 12, 'Как вам кажется? Если бы у кого было сто овец и одна из них заблудилась, то не оставит ли он девяносто девять в горах и не пойдет ли искать заблудившуюся?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32435, 'rst66', 'mat', 18, 11, 'Ибо Сын Человеческий пришел взыскать и спасти погибшее.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32434, 'rst66', 'mat', 18, 10, 'Смотрите, не презирайте ни одного из малых сих; ибо говорю вам, что Ангелы их на небесах всегда видят лицо Отца Моего Небесного.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32433, 'rst66', 'mat', 18, 9, 'и если глаз твой соблазняет тебя, вырви его и брось от себя: лучше тебе с одним глазом войти в жизнь, нежели с двумя глазами быть ввержену в геенну огненную.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32432, 'rst66', 'mat', 18, 8, 'Если же рука твоя или нога твоя соблазняет тебя, отсеки их и брось от себя: лучше тебе войти в жизнь без руки или без ноги, нежели с двумя руками и с двумя ногами быть ввержену в огонь вечный;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32431, 'rst66', 'mat', 18, 7, 'Горе миру от соблазнов, ибо надобно прийти соблазнам; но горе тому человеку, через которого соблазн приходит.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32430, 'rst66', 'mat', 18, 6, 'А кто соблазнит одного из малых сих, верующих в Меня, тому лучше было бы, если бы повесили ему мельничный жернов на шею и потопили его во глубине морской.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32429, 'rst66', 'mat', 18, 5, 'и кто примет одно такое дитя во имя Мое, тот Меня принимает.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32428, 'rst66', 'mat', 18, 4, 'итак, кто умалится, как это дитя, тот и больше в Царстве Небесном;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32427, 'rst66', 'mat', 18, 3, 'и сказал: истинно говорю вам, если не обратитесь и не будете как дети, не войдете в Царство Небесное;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32426, 'rst66', 'mat', 18, 2, 'Иисус, призвав дитя, поставил его посреди них');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32425, 'rst66', 'mat', 18, 1, 'В то время ученики приступили к Иисусу и сказали: кто больше в Царстве Небесном?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32424, 'rst66', 'mat', 17, 27, 'но, чтобы нам не соблазнить их, пойди на море, брось уду, и первую рыбу, которая попадется, возьми, и, открыв у ней рот, найдешь статир; возьми его и отдай им за Меня и за себя.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32423, 'rst66', 'mat', 17, 26, 'Петр говорит Ему: с посторонних. Иисус сказал ему: итак, сыны свободны;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32422, 'rst66', 'mat', 17, 25, 'Он говорит: да. И когда вошел он в дом, то Иисус, предупредив его, сказал: как тебе кажется, Симон, цари земные с кого берут пошлины или подати? с сынов ли своих, или с посторонних?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32421, 'rst66', 'mat', 17, 24, 'Когда же пришли они в Капернаум, то подошли к Петру собиратели дидрахм и сказали: Учитель ваш не даст ли дидрахмы?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32420, 'rst66', 'mat', 17, 23, 'и убьют Его, и в третий день воскреснет. И они весьма опечалились.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32419, 'rst66', 'mat', 17, 22, 'Во время пребывания их в Галилее, Иисус сказал им: Сын Человеческий предан будет в руки человеческие,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32418, 'rst66', 'mat', 17, 21, 'сей же род изгоняется только молитвою и постом.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32417, 'rst66', 'mat', 17, 20, 'Иисус же сказал им: по неверию вашему; ибо истинно говорю вам: если вы будете иметь веру с горчичное зерно и скажете горе сей: «перейди отсюда туда», и она перейдет; и ничего не будет невозможного для вас;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32416, 'rst66', 'mat', 17, 19, 'Тогда ученики, приступив к Иисусу наедине, сказали: почему мы не могли изгнать его?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32415, 'rst66', 'mat', 17, 18, 'И запретил ему Иисус, и бес вышел из него; и отрок исцелился в тот час.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32414, 'rst66', 'mat', 17, 17, 'Иисус же, отвечая, сказал: о, род неверный и развращенный! доколе буду с вами? доколе буду терпеть вас? приведите его ко Мне сюда.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32413, 'rst66', 'mat', 17, 16, 'я приводил его к ученикам Твоим, и они не могли исцелить его.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32412, 'rst66', 'mat', 17, 15, 'сказал: Господи! помилуй сына моего; он в новолуния <i>беснуется</i> и тяжко страдает, ибо часто бросается в огонь и часто в воду;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32411, 'rst66', 'mat', 17, 14, 'Когда они пришли к народу, то подошел к Нему человек и, преклоняя пред Ним колени,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32410, 'rst66', 'mat', 17, 13, 'Тогда ученики поняли, что Он говорил им об Иоанне Крестителе.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32409, 'rst66', 'mat', 17, 12, 'но говорю вам, что Илия уже пришел, и не узнали его, а поступили с ним, как хотели; так и Сын Человеческий пострадает от них.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32408, 'rst66', 'mat', 17, 11, 'Иисус сказал им в ответ: правда, Илия <i>должен</i> прийти прежде и устроить всё;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32407, 'rst66', 'mat', 17, 10, 'И спросили Его ученики Его: как же книжники говорят, что Илии надлежит прийти прежде?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32406, 'rst66', 'mat', 17, 9, 'И когда сходили они с горы, Иисус запретил им, говоря: никому не сказывайте о сем видении, доколе Сын Человеческий не воскреснет из мертвых.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32405, 'rst66', 'mat', 17, 8, 'Возведя же очи свои, они никого не увидели, кроме одного Иисуса.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32404, 'rst66', 'mat', 17, 7, 'Но Иисус, приступив, коснулся их и сказал: встаньте и не бойтесь.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32403, 'rst66', 'mat', 17, 6, 'И, услышав, ученики пали на лица свои и очень испугались.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32402, 'rst66', 'mat', 17, 5, 'Когда он еще говорил, се, облако светлое осенило их; и се, глас из облака глаголющий: Сей есть Сын Мой Возлюбленный, в Котором Мое благоволение; Его слушайте.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32401, 'rst66', 'mat', 17, 4, 'При сем Петр сказал Иисусу: Господи! хорошо нам здесь быть; если хочешь, сделаем здесь три кущи: Тебе одну, и Моисею одну, и одну Илии.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32400, 'rst66', 'mat', 17, 3, 'И вот, явились им Моисей и Илия, с Ним беседующие.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32399, 'rst66', 'mat', 17, 2, 'и преобразился пред ними: и просияло лицо Его, как солнце, одежды же Его сделались белыми, как свет.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32398, 'rst66', 'mat', 17, 1, 'По прошествии дней шести, взял Иисус Петра, Иакова и Иоанна, брата его, и возвел их на гору высокую одних,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32397, 'rst66', 'mat', 16, 28, 'Истинно говорю вам: есть некоторые из стоящих здесь, которые не вкусят смерти, как уже увидят Сына Человеческого, грядущего в Царствии Своем.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32396, 'rst66', 'mat', 16, 27, 'Ибо приидет Сын Человеческий во славе Отца Своего с Ангелами Своими и тогда воздаст каждому по делам его.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32395, 'rst66', 'mat', 16, 26, 'какая польза человеку, если он приобретет весь мир, а душе своей повредит? или какой выкуп даст человек за душу свою?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32394, 'rst66', 'mat', 16, 25, 'ибо кто хочет душу свою сберечь, тот потеряет ее, а кто потеряет душу свою ради Меня, тот обретет ее;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32393, 'rst66', 'mat', 16, 24, 'Тогда Иисус сказал ученикам Своим: если кто хочет идти за Мною, отвергнись себя, и возьми крест свой, и следуй за Мною,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32392, 'rst66', 'mat', 16, 23, 'Он же, обратившись, сказал Петру: отойди от Меня, сатана! ты Мне соблазн! потому что думаешь не о том, что&#769; Божие, но что&#769; человеческое.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32391, 'rst66', 'mat', 16, 22, 'И, отозвав Его, Петр начал прекословить Ему: будь милостив к Себе, Господи! да не будет этого с Тобою!');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32390, 'rst66', 'mat', 16, 21, 'С того времени Иисус начал открывать ученикам Своим, что Ему должно идти в Иерусалим, и много пострадать от старейшин и первосвященников и книжников, и быть убиту, и в третий день воскреснуть.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32389, 'rst66', 'mat', 16, 20, 'Тогда [Иисус] запретил ученикам Своим, чтобы никому не сказывали, что Он есть Иисус Христос.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32388, 'rst66', 'mat', 16, 19, 'и дам тебе ключи Царства Небесного: и что&#769; свяжешь на земле, то&#769; будет связано на небесах, и что&#769; разрешишь на земле, то&#769; будет разрешено на небесах.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32387, 'rst66', 'mat', 16, 18, 'и Я говорю тебе: ты &mdash; Петр, и на сем камне Я создам Церковь Мою, и врата ада не одолеют ее;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32386, 'rst66', 'mat', 16, 17, 'Тогда Иисус сказал ему в ответ: блажен ты, Симон, сын Ионин, потому что не плоть и кровь открыли тебе это, но Отец Мой, Сущий на небесах;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32385, 'rst66', 'mat', 16, 16, 'Симон же Петр, отвечая, сказал: Ты &mdash; Христос, Сын Бога Живого.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32384, 'rst66', 'mat', 16, 15, 'Он говорит им: а вы за кого почитаете Меня?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32383, 'rst66', 'mat', 16, 14, 'Они сказали: одни за Иоанна Крестителя, другие за Илию, а иные за Иеремию, или за одного из пророков.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32382, 'rst66', 'mat', 16, 13, 'Придя же в страны Кесарии Филипповой, Иисус спрашивал учеников Своих: за кого люди почитают Меня, Сына Человеческого?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32381, 'rst66', 'mat', 16, 12, 'Тогда они поняли, что Он говорил им беречься не закваски хлебной, но учения фарисейского и саддукейского.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32380, 'rst66', 'mat', 16, 11, 'Как не разумеете, что не о хлебе сказал Я вам: «берегитесь закваски фарисейской и саддукейской»?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32379, 'rst66', 'mat', 16, 10, 'Ни о семи хлебах на четыре тысячи, и сколько корзин вы набрали?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32378, 'rst66', 'mat', 16, 9, 'Еще ли не понимаете и не помните о пяти хлебах на пять тысяч <i>человек</i>, и сколько коробов вы набрали?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32377, 'rst66', 'mat', 16, 8, 'Уразумев то, Иисус сказал им: что помышляете в себе, маловерные, что хлебов не взяли?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32376, 'rst66', 'mat', 16, 7, 'Они же помышляли в себе и говорили: <i>это значит</i>, что хлебов мы не взяли.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32375, 'rst66', 'mat', 16, 6, 'Иисус сказал им: смотри&#769;те, берегитесь закваски фарисейской и саддукейской.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32374, 'rst66', 'mat', 16, 5, 'Переправившись на другую сторону, ученики Его забыли взять хлебов.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32373, 'rst66', 'mat', 16, 4, 'Род лукавый и прелюбодейный знамения ищет, и знамение не дастся ему, кроме знамения Ионы пророка. И, оставив их, отошел.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32372, 'rst66', 'mat', 16, 3, 'и поутру: «сегодня ненастье, потому что небо багрово». Лицемеры! различать лицо неба вы умеете, а знамений времен не можете?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32371, 'rst66', 'mat', 16, 2, 'Он же сказал им в ответ: вечером вы говорите: «будет вёдро, потому что небо красно»;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32370, 'rst66', 'mat', 16, 1, 'И приступили фарисеи и саддукеи и, искушая Его, просили показать им знамение с неба.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32369, 'rst66', 'mat', 15, 39, 'И, отпустив народ, Он вошел в лодку и прибыл в пределы Магдалинские.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32368, 'rst66', 'mat', 15, 38, 'а евших было четыре тысячи человек, кроме женщин и детей.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32367, 'rst66', 'mat', 15, 37, 'И ели все, и насытились; и набрали оставшихся кусков семь корзин полных,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32366, 'rst66', 'mat', 15, 36, 'И, взяв семь хлебов и рыбы, воздал благодарение, преломил и дал ученикам Своим, а ученики &mdash; народу.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32365, 'rst66', 'mat', 15, 35, 'Тогда велел народу возлечь на землю.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32364, 'rst66', 'mat', 15, 34, 'Говорит им Иисус: сколько у вас хлебов? Они же сказали: семь, и немного рыбок.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32363, 'rst66', 'mat', 15, 33, 'И говорят Ему ученики Его: откуда нам взять в пустыне столько хлебов, чтобы накормить столько народа?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32362, 'rst66', 'mat', 15, 32, 'Иисус же, призвав учеников Своих, сказал им: жаль Мне народа, что уже три дня находятся при Мне, и нечего им есть; отпустить же их неевшими не хочу, чтобы не ослабели в дороге.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32361, 'rst66', 'mat', 15, 31, 'так что народ дивился, видя немых говорящими, увечных здоровыми, хромых ходящими и слепых видящими; и прославлял Бога Израилева.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32360, 'rst66', 'mat', 15, 30, 'И приступило к Нему множество народа, имея с собою хромых, слепых, немых, увечных и иных многих, и повергли их к ногам Иисусовым; и Он исцелил их;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32359, 'rst66', 'mat', 15, 29, 'Перейдя оттуда, пришел Иисус к морю Галилейскому и, взойдя на гору, сел там.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32358, 'rst66', 'mat', 15, 28, 'Тогда Иисус сказал ей в ответ: о, женщина! велика&#769; вера твоя; да будет тебе по желанию твоему. И исцелилась дочь ее в тот час.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32357, 'rst66', 'mat', 15, 27, 'Она сказала: так, Господи! но и псы едят крохи, которые падают со стола господ их.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32356, 'rst66', 'mat', 15, 26, 'Он же сказал в ответ: нехорошо взять хлеб у детей и бросить псам.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32355, 'rst66', 'mat', 15, 25, 'А она, подойдя, кланялась Ему и говорила: Господи! помоги мне.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32354, 'rst66', 'mat', 15, 24, 'Он же сказал в ответ: Я послан только к погибшим овцам дома Израилева.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32353, 'rst66', 'mat', 15, 23, 'Но Он не отвечал ей ни слова. И ученики Его, приступив, просили Его: отпусти ее, потому что кричит за нами.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32352, 'rst66', 'mat', 15, 22, 'И вот, женщина Хананеянка, выйдя из тех мест, кричала Ему: помилуй меня, Господи, сын Давидов, дочь моя жестоко беснуется.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32351, 'rst66', 'mat', 15, 21, 'И, выйдя оттуда, Иисус удалился в страны Тирские и Сидонские.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32350, 'rst66', 'mat', 15, 20, 'это оскверняет человека; а есть неумытыми руками &mdash; не оскверняет человека.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32349, 'rst66', 'mat', 15, 19, 'ибо из сердца исходят злые помыслы, убийства, прелюбодеяния, любодеяния, кражи, лжесвидетельства, хуления &mdash;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32348, 'rst66', 'mat', 15, 18, 'А исходящее из уст &mdash; из сердца исходит &mdash; сие оскверняет человека,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32347, 'rst66', 'mat', 15, 17, 'Еще ли не понимаете, что всё, входящее в уста, проходит в чрево и извергается вон?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32346, 'rst66', 'mat', 15, 16, 'Иисус сказал: неужели и вы еще не разумеете?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32345, 'rst66', 'mat', 15, 15, 'Петр же, отвечая, сказал Ему: изъясни нам притчу сию.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32344, 'rst66', 'mat', 15, 14, 'оставьте их: они &mdash; слепые вожди слепых; а если слепой ведет слепого, то оба упадут в яму.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32343, 'rst66', 'mat', 15, 13, 'Он же сказал в ответ: всякое растение, которое не Отец Мой Небесный насадил, искоренится;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32342, 'rst66', 'mat', 15, 12, 'Тогда ученики Его, приступив, сказали Ему: знаешь ли, что фарисеи, услышав слово сие, соблазнились?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32341, 'rst66', 'mat', 15, 11, 'Не то&#769;, что&#769; входит в уста, оскверняет человека, но то&#769;, что&#769; выходит из уст, оскверняет человека.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32340, 'rst66', 'mat', 15, 10, 'И, призвав народ, сказал им: слушайте и разумейте!');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32339, 'rst66', 'mat', 15, 9, 'но тщетно чтут Меня, уча учениям, заповедям человеческим».');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32338, 'rst66', 'mat', 15, 8, '«приближаются ко Мне люди сии устами своими, и чтут Меня языком, сердце же их далеко отстоит от Меня;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32337, 'rst66', 'mat', 15, 7, 'Лицемеры! хорошо пророчествовал о вас Исаия, говоря:');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32336, 'rst66', 'mat', 15, 6, 'тот может и не почтить отца своего или мать свою; таким образом вы устранили заповедь Божию преданием вашим.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32335, 'rst66', 'mat', 15, 5, 'А вы говорите: если кто скажет отцу или матери: «дар <i>Богу</i> то, чем бы ты от меня пользовался»,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32334, 'rst66', 'mat', 15, 4, 'Ибо Бог заповедал: «почитай отца и мать»; и: «злословящий отца или мать смертью да умрет».');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32333, 'rst66', 'mat', 15, 3, 'Он же сказал им в ответ: зачем и вы преступаете заповедь Божию ради предания вашего?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32332, 'rst66', 'mat', 15, 2, 'зачем ученики Твои преступают предание старцев? ибо не умывают рук своих, когда едят хлеб.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32331, 'rst66', 'mat', 15, 1, 'Тогда приходят к Иисусу Иерусалимские книжники и фарисеи и говорят:');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32330, 'rst66', 'mat', 14, 36, 'и просили Его, чтобы только прикоснуться к краю одежды Его; и которые прикасались, исцелялись.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32329, 'rst66', 'mat', 14, 35, 'Жители того места, узнав Его, послали во всю окрестность ту и принесли к Нему всех больных,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32328, 'rst66', 'mat', 14, 34, 'И, переправившись, прибыли в землю Геннисаретскую.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32327, 'rst66', 'mat', 14, 33, 'Бывшие же в лодке подошли, поклонились Ему и сказали: истинно Ты Сын Божий.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32326, 'rst66', 'mat', 14, 32, 'И, когда вошли они в лодку, ветер утих.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32325, 'rst66', 'mat', 14, 31, 'Иисус тотчас простер руку, поддержал его и говорит ему: маловерный! зачем ты усомнился?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32324, 'rst66', 'mat', 14, 30, 'но, видя сильный ветер, испугался и, начав утопать, закричал: Господи! спаси меня.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32323, 'rst66', 'mat', 14, 29, 'Он же сказал: иди. И, выйдя из лодки, Петр пошел по воде, чтобы подойти к Иисусу,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32322, 'rst66', 'mat', 14, 28, 'Петр сказал Ему в ответ: Господи! если это Ты, повели мне прийти к Тебе по воде.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32321, 'rst66', 'mat', 14, 27, 'Но Иисус тотчас заговорил с ними и сказал: ободритесь; это Я, не бойтесь.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32320, 'rst66', 'mat', 14, 26, 'И ученики, увидев Его, идущего по морю, встревожились и говорили: это призрак; и от страха вскричали.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32319, 'rst66', 'mat', 14, 25, 'В четвертую же стражу ночи пошел к ним Иисус, идя по морю.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32318, 'rst66', 'mat', 14, 24, 'А лодка была уже на средине моря, и ее било волнами, потому что ветер был противный.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32317, 'rst66', 'mat', 14, 23, 'И, отпустив народ, Он взошел на гору помолиться наедине; и вечером оставался там один.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32316, 'rst66', 'mat', 14, 22, 'И тотчас понудил Иисус учеников Своих войти в лодку и отправиться прежде Его на другую сторону, пока Он отпустит народ.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32315, 'rst66', 'mat', 14, 21, 'а евших было около пяти тысяч человек, кроме женщин и детей.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32314, 'rst66', 'mat', 14, 20, 'И ели все, и насытились; и набрали оставшихся кусков двенадцать коробов полных;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32313, 'rst66', 'mat', 14, 19, 'И велел народу возлечь на траву и, взяв пять хлебов и две рыбы, воззрел на небо, благословил и, преломив, дал хлебы ученикам, а ученики &mdash; народу.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32312, 'rst66', 'mat', 14, 18, 'Он сказал: принесите их Мне сюда.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32311, 'rst66', 'mat', 14, 17, 'Они же говорят Ему: у нас здесь только пять хлебов и две рыбы.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32310, 'rst66', 'mat', 14, 16, 'Но Иисус сказал им: не нужно им идти, вы дайте им есть.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32309, 'rst66', 'mat', 14, 15, 'Когда же настал вечер, приступили к Нему ученики Его и сказали: место здесь пустынное, и время уже позднее; отпусти народ, чтобы они пошли в селения и купили себе пищи.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32308, 'rst66', 'mat', 14, 14, 'И, выйдя, Иисус увидел множество людей и сжалился над ними, и исцелил больных их.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32307, 'rst66', 'mat', 14, 13, 'И, услышав, Иисус удалился оттуда на лодке в пустынное место один; а народ, услышав о том, пошел за Ним из городов пешком.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32306, 'rst66', 'mat', 14, 12, 'Ученики же его, придя, взяли тело его и погребли его; и пошли, возвестили Иисусу.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32305, 'rst66', 'mat', 14, 11, 'И принесли голову его на блюде и дали девице, а она отнесла матери своей.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32304, 'rst66', 'mat', 14, 10, 'и послал отсечь Иоанну голову в темнице.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32303, 'rst66', 'mat', 14, 9, 'И опечалился царь, но, ради клятвы и возлежащих с ним, повелел дать ей,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32302, 'rst66', 'mat', 14, 8, 'Она же, по наущению матери своей, сказала: дай мне здесь на блюде голову Иоанна Крестителя.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32301, 'rst66', 'mat', 14, 7, 'посему он с клятвою обещал ей дать, чего она ни попросит.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32300, 'rst66', 'mat', 14, 6, 'Во время же <i>празднования</i> дня рождения Ирода дочь Иродиады плясала перед собранием и угодила Ироду,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32299, 'rst66', 'mat', 14, 5, 'И хотел убить его, но боялся народа, потому что его почитали за пророка.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32298, 'rst66', 'mat', 14, 4, 'потому что Иоанн говорил ему: не должно тебе иметь ее.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32297, 'rst66', 'mat', 14, 3, 'Ибо Ирод, взяв Иоанна, связал его и посадил в темницу за Иродиаду, жену Филиппа, брата своего,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32296, 'rst66', 'mat', 14, 2, 'и сказал служащим при нем: это Иоанн Креститель; он воскрес из мертвых, и потому чудеса делаются им.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32295, 'rst66', 'mat', 14, 1, 'В то время Ирод четвертовластник услышал молву об Иисусе');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32294, 'rst66', 'mat', 13, 58, 'И не совершил там многих чудес по неверию их.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32293, 'rst66', 'mat', 13, 57, 'И соблазнялись о Нем. Иисус же сказал им: не бывает пророк без чести, разве только в отечестве своем и в доме своем.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32292, 'rst66', 'mat', 13, 56, 'И сестры Его не все ли между нами? откуда же у Него всё это?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32291, 'rst66', 'mat', 13, 55, 'Не плотников ли Он сын? не Его ли Мать называется Мария, и братья Его Иаков и Иосий, и Симон, и Иуда?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32290, 'rst66', 'mat', 13, 54, 'И, придя в отечество Свое, учил их в синагоге их, так что они изумлялись и говорили: откуда у Него такая премудрость и силы?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32289, 'rst66', 'mat', 13, 53, 'И, когда окончил Иисус притчи сии, пошел оттуда.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32288, 'rst66', 'mat', 13, 52, 'Он же сказал им: поэтому всякий книжник, наученный Царству Небесному, подобен хозяину, который выносит из сокровищницы своей новое и старое.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32287, 'rst66', 'mat', 13, 51, 'И спросил их Иисус: поняли ли вы всё это? Они говорят Ему: та&#769;к, Господи!');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32286, 'rst66', 'mat', 13, 50, 'и ввергнут их в печь огненную: там будет плач и скрежет зубов.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32285, 'rst66', 'mat', 13, 49, 'Так будет при кончине века: изыдут Ангелы, и отделят злых из среды праведных,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32284, 'rst66', 'mat', 13, 48, 'который, когда наполнился, вытащили на берег и, сев, хорошее собрали в сосуды, а худое выбросили вон.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32283, 'rst66', 'mat', 13, 47, 'Еще: подобно Царство Небесное неводу, закинутому в море и захватившему рыб всякого рода,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32282, 'rst66', 'mat', 13, 46, 'который, найдя одну драгоценную жемчужину, пошел и продал всё, что имел, и купил ее.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32281, 'rst66', 'mat', 13, 45, 'Еще: подобно Царство Небесное купцу, ищущему хороших жемчужин,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32280, 'rst66', 'mat', 13, 44, 'Еще: подобно Царство Небесное сокровищу, скрытому на поле, которое, найдя, человек утаил, и от радости о нем идет и продает всё, что имеет, и покупает поле то.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32279, 'rst66', 'mat', 13, 43, 'тогда праведники воссияют, как солнце, в Царстве Отца их. Кто имеет уши слышать, да слышит!');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32278, 'rst66', 'mat', 13, 42, 'и ввергнут их в печь огненную; там будет плач и скрежет зубов;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32277, 'rst66', 'mat', 13, 41, 'пошлет Сын Человеческий Ангелов Своих, и соберут из Царства Его все соблазны и делающих беззаконие,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32276, 'rst66', 'mat', 13, 40, 'Посему как собирают плевелы и огнем сжигают, так будет при кончине века сего:');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32275, 'rst66', 'mat', 13, 39, 'враг, посеявший их, есть диавол; жатва есть кончина века, а жнецы суть Ангелы.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32274, 'rst66', 'mat', 13, 38, 'поле есть мир; доброе семя &mdash; это сыны Царствия, а плевелы &mdash; сыны лукавого;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32273, 'rst66', 'mat', 13, 37, 'Он же сказал им в ответ: сеющий доброе семя есть Сын Человеческий;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32272, 'rst66', 'mat', 13, 36, 'Тогда Иисус, отпустив народ, вошел в дом. И, приступив к Нему, ученики Его сказали: изъясни нам притчу о плевелах на поле.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32271, 'rst66', 'mat', 13, 35, 'да сбудется реченное через пророка, который говорит: «отверзу в притчах уста Мои; изреку сокровенное от создания мира».');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32270, 'rst66', 'mat', 13, 34, 'Всё сие Иисус говорил народу притчами, и без притчи не говорил им,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32269, 'rst66', 'mat', 13, 33, 'Иную притчу сказал Он им: Царство Небесное подобно закваске, которую женщина, взяв, положила в три меры муки, доколе не вскисло всё.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32268, 'rst66', 'mat', 13, 32, 'которое, хотя меньше всех семян, но, когда вырастет, бывает больше всех злаков и становится деревом, так что прилетают птицы небесные и укрываются в ветвях его.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32267, 'rst66', 'mat', 13, 31, 'Иную притчу предложил Он им, говоря: Царство Небесное подобно зерну горчичному, которое человек взял и посеял на поле своем,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32266, 'rst66', 'mat', 13, 30, 'оставьте расти вместе то и другое до жатвы; и во время жатвы я скажу жнецам: соберите прежде плевелы и свяжите их в связки, чтобы сжечь их, а пшеницу уберите в житницу мою».');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32265, 'rst66', 'mat', 13, 29, 'Но он сказал: «нет, &mdash; чтобы, выбирая плевелы, вы не выдергали вместе с ними пшеницы,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32264, 'rst66', 'mat', 13, 28, 'Он же сказал им: «враг человек сделал это». А рабы сказали ему: «хочешь ли, мы пойдем, выберем их?»');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32263, 'rst66', 'mat', 13, 27, 'Придя же, рабы домовладыки сказали ему: «господин! не доброе ли семя сеял ты на поле твоем? откуда же на нем плевелы?»');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32262, 'rst66', 'mat', 13, 26, 'когда взошла зелень и показался плод, тогда явились и плевелы.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32261, 'rst66', 'mat', 13, 25, 'когда же люди спали, пришел враг его и посеял между пшеницею плевелы и ушел;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32260, 'rst66', 'mat', 13, 24, 'Другую притчу предложил Он им, говоря: Царство Небесное подобно человеку, посеявшему доброе семя на поле своем;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32259, 'rst66', 'mat', 13, 23, 'Посеянное же на доброй земле означает слышащего слово и разумеющего, который и бывает плодоносен, так что иной приносит плод во сто крат, иной в шестьдесят, а иной в тридцать.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32258, 'rst66', 'mat', 13, 22, 'А посеянное в тернии означает того, кто слышит слово, но забота века сего и обольщение богатства заглушает слово, и оно бывает бесплодно.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32257, 'rst66', 'mat', 13, 21, 'но не имеет в себе корня и непостоянен: когда настанет скорбь или гонение за слово, тотчас соблазняется.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32256, 'rst66', 'mat', 13, 20, 'А посеянное на каменистых местах означает того, кто слышит слово и тотчас с радостью принимает его;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32255, 'rst66', 'mat', 13, 19, 'ко всякому, слушающему слово о Царствии и не разумеющему, приходит лукавый и похищает посеянное в сердце его &mdash; вот кого означает посеянное при дороге.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32254, 'rst66', 'mat', 13, 18, 'Вы же выслушайте <i>значение</i> притчи о сеятеле:');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32253, 'rst66', 'mat', 13, 17, 'ибо истинно говорю вам, что многие пророки и праведники желали видеть, что вы видите, и не видели, и слышать, что вы слышите, и не слышали.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32252, 'rst66', 'mat', 13, 16, 'Ваши же блаженны очи, что видят, и уши ваши, что слышат,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32251, 'rst66', 'mat', 13, 15, 'ибо огрубело сердце людей сих, и ушами с трудом слышат, и глаза свои сомкнули, да не увидят глазами, и не услышат ушами, и не уразумеют сердцем, и да не обратятся, чтобы Я исцелил их».');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32250, 'rst66', 'mat', 13, 14, 'и сбывается над ними пророчество Исаии, которое говорит: «слухом услышите &mdash; и не уразумеете, и глазами смотреть будете &mdash; и не увидите,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32249, 'rst66', 'mat', 13, 13, 'потому говорю им притчами, что они видя не видят, и слыша не слышат, и не разумеют;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32248, 'rst66', 'mat', 13, 12, 'ибо кто имеет, тому дано будет и приумножится, а кто не имеет, у того отнимется и то, что имеет;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32247, 'rst66', 'mat', 13, 11, 'Он сказал им в ответ: для того, что вам дано знать тайны Царствия Небесного, а им не дано,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32246, 'rst66', 'mat', 13, 10, 'И, приступив, ученики сказали Ему: для чего притчами говоришь им?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32245, 'rst66', 'mat', 13, 9, 'Кто имеет уши слышать, да слышит!');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32244, 'rst66', 'mat', 13, 8, 'иное упало на добрую землю и принесло плод: одно во сто крат, а другое в шестьдесят, иное же в тридцать.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32243, 'rst66', 'mat', 13, 7, 'иное упало в терние, и выросло терние и заглушило его;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32242, 'rst66', 'mat', 13, 6, 'Когда же взошло солнце, увяло, и, как не имело корня, засохло;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32241, 'rst66', 'mat', 13, 5, 'иное упало на места каменистые, где немного было земли, и скоро взошло, потому что земля была неглубока.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32240, 'rst66', 'mat', 13, 4, 'и когда он сеял, иное упало при дороге, и налетели птицы и поклевали то;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32239, 'rst66', 'mat', 13, 3, 'И поучал их много притчами, говоря: Вот, вышел сеятель сеять;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32238, 'rst66', 'mat', 13, 2, 'И собралось к Нему множество народа, так что Он вошел в лодку и сел; а весь народ стоял на берегу.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32237, 'rst66', 'mat', 13, 1, 'Выйдя же в день тот из дома, Иисус сел у моря.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32236, 'rst66', 'mat', 12, 50, 'ибо, кто будет исполнять волю Отца Моего Небесного, тот Мне брат, и сестра, и матерь.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32235, 'rst66', 'mat', 12, 49, 'И, указав рукою Своею на учеников Своих, сказал: вот матерь Моя и братья Мои;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32234, 'rst66', 'mat', 12, 48, 'Он же сказал в ответ говорившему: кто Матерь Моя? и кто братья Мои?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32233, 'rst66', 'mat', 12, 47, 'И некто сказал Ему: вот Матерь Твоя и братья Твои стоят вне, желая говорить с Тобою.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32232, 'rst66', 'mat', 12, 46, 'Когда же Он еще говорил к народу, Матерь и братья Его стояли вне <i>дома</i>, желая говорить с Ним.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32231, 'rst66', 'mat', 12, 45, 'тогда идет и берет с собою семь других духов, злейших себя, и, войдя, живут там; и бывает для человека того последнее хуже первого. Так будет и с этим злым родом.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32230, 'rst66', 'mat', 12, 44, 'тогда говорит: «возвращусь в дом мой, откуда я вышел». И, придя, находит <i>его</i> незанятым, выметенным и убранным;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32229, 'rst66', 'mat', 12, 43, 'Когда нечистый дух выйдет из человека, то ходит по безводным местам, ища покоя, и не находит;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32228, 'rst66', 'mat', 12, 42, 'Царица южная восстанет на суд с родом сим и осудит его, ибо она приходила от пределов земли послушать мудрости Соломоновой; и вот, здесь больше Соломона.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32227, 'rst66', 'mat', 12, 41, 'Ниневитяне восстанут на суд с родом сим и осудят его, ибо они покаялись от проповеди Иониной; и вот, здесь больше Ионы.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32226, 'rst66', 'mat', 12, 40, 'ибо как Иона был во чреве кита три дня и три ночи, так и Сын Человеческий будет в сердце земли три дня и три ночи.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32225, 'rst66', 'mat', 12, 39, 'Но Он сказал им в ответ: род лукавый и прелюбодейный ищет знамения; и знамение не дастся ему, кроме знамения Ионы пророка;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32224, 'rst66', 'mat', 12, 38, 'Тогда некоторые из книжников и фарисеев сказали: Учитель! хотелось бы нам видеть от Тебя знамение.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32223, 'rst66', 'mat', 12, 37, 'ибо от слов своих оправдаешься, и от слов своих осудишься.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32222, 'rst66', 'mat', 12, 36, 'Говорю же вам, что за всякое праздное слово, какое скажут люди, дадут они ответ в день суда:');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32221, 'rst66', 'mat', 12, 35, 'Добрый человек из доброго сокровища выносит доброе, а злой человек из злого сокровища выносит злое.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32220, 'rst66', 'mat', 12, 34, 'Порождения ехиднины! как вы можете говорить доброе, будучи злы? Ибо от избытка сердца говорят уста.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32219, 'rst66', 'mat', 12, 33, 'Или признайте дерево хорошим и плод его хорошим; или признайте дерево худым и плод его худым, ибо дерево познается по плоду.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32218, 'rst66', 'mat', 12, 32, 'если кто скажет слово на Сына Человеческого, простится ему; если же кто скажет на Духа Святого, не простится ему ни в сем веке, ни в будущем.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32217, 'rst66', 'mat', 12, 31, 'Посему говорю вам: всякий грех и хула простятся человекам, а хула на Духа не простится человекам;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32216, 'rst66', 'mat', 12, 30, 'Кто не со Мною, тот против Меня; и кто не собирает со Мною, тот расточает.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32215, 'rst66', 'mat', 12, 29, 'Или, как может кто войти в дом сильного и расхитить вещи его, если прежде не свяжет сильного? и тогда расхитит дом его.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32214, 'rst66', 'mat', 12, 28, 'Если же Я Духом Божиим изгоняю бесов, то, конечно, достигло до вас Царствие Божие.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32213, 'rst66', 'mat', 12, 27, 'И если Я <i>силою</i> веельзевула изгоняю бесов, то сыновья ваши чьею <i>силою</i> изгоняют? Посему они будут вам судьями.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32212, 'rst66', 'mat', 12, 26, 'И если сатана сатану изгоняет, то он разделился сам с собою: как же устоит царство его?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32211, 'rst66', 'mat', 12, 25, 'Но Иисус, зная помышления их, сказал им: всякое царство, разделившееся само в себе, опустеет; и всякий город или дом, разделившийся сам в себе, не устоит.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32210, 'rst66', 'mat', 12, 24, 'Фарисеи же, услышав <i>сие</i>, сказали: Он изгоняет бесов не иначе, как <i>силою</i> веельзевула, князя бесовского.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32209, 'rst66', 'mat', 12, 23, 'И дивился весь народ и говорил: не это ли Христос, сын Давидов?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32208, 'rst66', 'mat', 12, 22, 'Тогда привели к Нему бесноватого слепого и немого; и исцелил его, так что слепой и немой стал и говорить и видеть.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32207, 'rst66', 'mat', 12, 21, 'и на имя Его будут уповать народы».');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32206, 'rst66', 'mat', 12, 20, 'трости надломленной не переломит, и льна курящегося не угасит, доколе не доставит суду победы;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32205, 'rst66', 'mat', 12, 19, 'не воспрекословит, не возопиет, и никто не услышит на улицах голоса Его;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32204, 'rst66', 'mat', 12, 18, '«се, Отрок Мой, Которого Я избрал, Возлюбленный Мой, Которому благоволит душа Моя. Положу дух Мой на Него, и возвестит народам суд;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32203, 'rst66', 'mat', 12, 17, 'да сбудется реченное через пророка Исаию, который говорит:');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32202, 'rst66', 'mat', 12, 16, 'и запретил им объявлять о Нем,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32201, 'rst66', 'mat', 12, 15, 'И последовало за Ним множество народа, и Он исцелил их всех');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32200, 'rst66', 'mat', 12, 14, 'Фарисеи же, выйдя, имели совещание против Него, как бы погубить Его. Но Иисус, узнав, удалился оттуда.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32199, 'rst66', 'mat', 12, 13, 'Тогда говорит человеку тому: протяни руку твою. И он протянул, и стала она здорова, как другая.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32198, 'rst66', 'mat', 12, 12, 'Сколько же лучше человек овцы! Итак, можно в субботы делать добро.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32197, 'rst66', 'mat', 12, 11, 'Он же сказал им: кто из вас, имея одну овцу, если она в субботу упадет в яму, не возьмет ее и не вытащит?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32196, 'rst66', 'mat', 12, 10, 'И вот, там был человек, имеющий сухую руку. И спросили Иисуса, чтобы обвинить Его: можно ли исцелять в субботы?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32195, 'rst66', 'mat', 12, 9, 'И, отойдя оттуда, вошел Он в синагогу их.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32194, 'rst66', 'mat', 12, 8, 'ибо Сын Человеческий есть господин и субботы.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32193, 'rst66', 'mat', 12, 7, 'если бы вы знали, что&#769; значит: «милости хочу, а не жертвы», то не осудили бы невиновных,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32192, 'rst66', 'mat', 12, 6, 'Но говорю вам, что здесь Тот, Кто больше храма;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32191, 'rst66', 'mat', 12, 5, 'Или не читали ли вы в законе, что в субботы священники в храме нарушают субботу, однако невиновны?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32190, 'rst66', 'mat', 12, 4, 'Как он вошел в дом Божий и ел хлебы предложения, которых не должно было есть ни ему, ни бывшим с ним, а только одним священникам?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32189, 'rst66', 'mat', 12, 3, 'Он же сказал им: разве вы не читали, что сделал Давид, когда взалкал сам и бывшие с ним?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32188, 'rst66', 'mat', 12, 2, 'Фарисеи, увидев это, сказали Ему: вот, ученики Твои делают, чего не должно делать в субботу.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32187, 'rst66', 'mat', 12, 1, 'В то время проходил Иисус в субботу засеянными полями; ученики же Его взалкали и начали срывать колосья и есть.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32186, 'rst66', 'mat', 11, 30, 'ибо иго Мое благо, и бремя Мое легко.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32185, 'rst66', 'mat', 11, 29, 'возьмите иго Мое на себя и научитесь от Меня, ибо Я кроток и смирен сердцем, и найдете покой душам вашим;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32184, 'rst66', 'mat', 11, 28, 'Придите ко Мне, все труждающиеся и обремененные, и Я успокою вас;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32183, 'rst66', 'mat', 11, 27, 'Все предано Мне Отцом Моим, и никто не знает Сына, кроме Отца; и Отца не знает никто, кроме Сына, и кому Сын хочет открыть.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32182, 'rst66', 'mat', 11, 26, 'ей, Отче! ибо таково было Твое благоволение.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32181, 'rst66', 'mat', 11, 25, 'В то время, продолжая речь, Иисус сказал: славлю Тебя, Отче, Господи неба и земли, что Ты утаил сие от мудрых и разумных и открыл то младенцам;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32180, 'rst66', 'mat', 11, 24, 'но говорю вам, что земле Содомской отраднее будет в день суда, нежели тебе.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32179, 'rst66', 'mat', 11, 23, 'И ты, Капернаум, до неба вознесшийся, до ада низвергнешься, ибо если бы в Содоме явлены были силы, явленные в тебе, то он оставался бы до сего дня;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32178, 'rst66', 'mat', 11, 22, 'но говорю вам: Тиру и Сидону отраднее будет в день суда, нежели вам.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32177, 'rst66', 'mat', 11, 21, 'горе тебе, Хоразин! горе тебе, Вифсаида! ибо если бы в Тире и Сидоне явлены были силы, явленные в вас, то давно бы они во вретище и пепле покаялись,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32176, 'rst66', 'mat', 11, 20, 'Тогда начал Он укорять города, в которых наиболее явлено было сил Его, за то, что они не покаялись:');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32175, 'rst66', 'mat', 11, 19, 'Пришел Сын Человеческий, ест и пьет; и говорят: «вот человек, который любит есть и пить вино, друг мытарям и грешникам». И оправдана премудрость чадами ее.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32174, 'rst66', 'mat', 11, 18, 'Ибо пришел Иоанн, ни ест, ни пьет; и говорят: «в нем бес».');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32173, 'rst66', 'mat', 11, 17, 'говорят: «мы играли вам на свирели, и вы не плясали; мы пели вам печальные песни, и вы не рыдали».');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32172, 'rst66', 'mat', 11, 16, 'Но кому уподоблю род сей? Он подобен детям, которые сидят на улице и, обращаясь к своим товарищам,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32171, 'rst66', 'mat', 11, 15, 'Кто имеет уши слышать, да слышит!');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32170, 'rst66', 'mat', 11, 14, 'И если хотите принять, он есть Илия, которому должно прийти.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32169, 'rst66', 'mat', 11, 13, 'ибо все пророки и закон прорекли до Иоанна.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32168, 'rst66', 'mat', 11, 12, 'От дней же Иоанна Крестителя доныне Царство Небесное силою берется, и употребляющие усилие восхищают его,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32167, 'rst66', 'mat', 11, 11, 'Истинно говорю вам: из рожденных женами не восставал больший Иоанна Крестителя; но меньший в Царстве Небесном больше его.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32166, 'rst66', 'mat', 11, 10, 'Ибо он тот, о котором написано: «се, Я посылаю Ангела Моего пред лицом Твоим, который приготовит путь Твой пред Тобою».');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32165, 'rst66', 'mat', 11, 9, 'Что&#769; же смотреть ходили вы? пророка? Да, говорю вам, и больше пророка.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32164, 'rst66', 'mat', 11, 8, 'Что&#769; же смотреть ходили вы? человека ли, одетого в мягкие одежды? Носящие мягкие одежды находятся в чертогах царских.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32163, 'rst66', 'mat', 11, 7, 'Когда же они пошли, Иисус начал говорить народу об Иоанне: что&#769; смотреть ходили вы в пустыню? трость ли, ветром колеблемую?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32162, 'rst66', 'mat', 11, 6, 'и блажен, кто не соблазнится о Мне.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32161, 'rst66', 'mat', 11, 5, 'слепые прозревают и хромые ходят, прокаженные очищаются и глухие слышат, мертвые воскресают и нищие благовествуют;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32160, 'rst66', 'mat', 11, 4, 'И сказал им Иисус в ответ: пойдите, скажите Иоанну, что слышите и видите:');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32159, 'rst66', 'mat', 11, 3, 'сказать Ему: Ты ли Тот, Который должен прийти, или ожидать нам другого?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32158, 'rst66', 'mat', 11, 2, 'Иоанн же, услышав в темнице о делах Христовых, послал двоих из учеников своих');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32157, 'rst66', 'mat', 11, 1, 'И когда окончил Иисус наставления двенадцати ученикам Своим, перешел оттуда учить и проповедовать в городах их.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32156, 'rst66', 'mat', 10, 42, 'И кто напоит одного из малых сих только чашею холодной воды, во имя ученика, истинно говорю вам, не потеряет награды своей.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32155, 'rst66', 'mat', 10, 41, 'кто принимает пророка, во имя пророка, получит награду пророка; и кто принимает праведника, во имя праведника, получит награду праведника.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32154, 'rst66', 'mat', 10, 40, 'Кто принимает вас, принимает Меня, а кто принимает Меня, принимает Пославшего Меня;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32153, 'rst66', 'mat', 10, 39, 'Сберегший душу свою потеряет ее, а потерявший душу свою ради Меня сбережет ее.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32152, 'rst66', 'mat', 10, 38, 'и кто не берет креста своего и следует за Мною, тот не достоин Меня.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32151, 'rst66', 'mat', 10, 37, 'Кто любит отца или мать более, нежели Меня, не достоин Меня; и кто любит сына или дочь более, нежели Меня, не достоин Меня;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32150, 'rst66', 'mat', 10, 36, 'И враги человеку &mdash; домашние его.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32149, 'rst66', 'mat', 10, 35, 'ибо Я пришел разделить человека с отцом его, и дочь с матерью ее, и невестку со свекровью ее.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32148, 'rst66', 'mat', 10, 34, 'Не думайте, что Я пришел принести мир на землю; не мир пришел Я принести, но меч,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32147, 'rst66', 'mat', 10, 33, 'а кто отречется от Меня пред людьми, отрекусь от того и Я пред Отцом Моим Небесным.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32146, 'rst66', 'mat', 10, 32, 'Итак, всякого, кто исповедает Меня пред людьми, того исповедаю и Я пред Отцом Моим Небесным;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32145, 'rst66', 'mat', 10, 31, 'не бойтесь же: вы лучше многих малых птиц.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32144, 'rst66', 'mat', 10, 30, 'у вас же и волосы на голове все сочтены;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32143, 'rst66', 'mat', 10, 29, 'Не две ли малые птицы продаются за ассарий? И ни одна из них не упадет на землю без <i>воли</i> Отца вашего;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32142, 'rst66', 'mat', 10, 28, 'И не бойтесь убивающих тело, души&#769; же не могущих убить; а бойтесь более Того, Кто может и душу и тело погубить в геенне.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32141, 'rst66', 'mat', 10, 27, 'Что говорю вам в темноте, говорите при свете; и что на ухо слышите, проповедуйте на кровлях.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32140, 'rst66', 'mat', 10, 26, 'Итак, не бойтесь их, ибо нет ничего сокровенного, что не открылось бы, и тайного, что не было бы узнано.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32139, 'rst66', 'mat', 10, 25, 'довольно для ученика, чтобы он был как учитель его, и для слуги, чтобы он был как господин его. Если хозяина дома назвали веельзевулом, не тем ли более домашних его?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32138, 'rst66', 'mat', 10, 24, 'Ученик не выше учителя, и слуга не выше господина своего:');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32137, 'rst66', 'mat', 10, 23, 'Когда же будут гнать вас в одном городе, бегите в другой. Ибо истинно говорю вам: не успеете обойти городов Израилевых, как приидет Сын Человеческий.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32136, 'rst66', 'mat', 10, 22, 'и будете ненавидимы всеми за имя Мое; претерпевший же до конца спасется.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32135, 'rst66', 'mat', 10, 21, 'Предаст же брат брата на смерть, и отец &mdash; сына; и восстанут дети на родителей, и умертвят их;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32134, 'rst66', 'mat', 10, 20, 'ибо не вы будете говорить, но Дух Отца вашего будет говорить в вас.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32133, 'rst66', 'mat', 10, 19, 'Когда же будут предавать вас, не заботьтесь, как или что&#769; сказать; ибо в тот час дано будет вам, что&#769; сказать,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32132, 'rst66', 'mat', 10, 18, 'и поведут вас к правителям и царям за Меня, для свидетельства перед ними и язычниками.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32131, 'rst66', 'mat', 10, 17, 'Остерегайтесь же людей: ибо они будут отдавать вас в судилища и в синагогах своих будут бить вас,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32130, 'rst66', 'mat', 10, 16, 'Вот, Я посылаю вас, как овец среди волков: итак, будьте мудры, как змии, и просты, как голуби.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32129, 'rst66', 'mat', 10, 15, 'истинно говорю вам: отраднее будет земле Содомской и Гоморрской в день суда, нежели городу тому.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32128, 'rst66', 'mat', 10, 14, 'А если кто не примет вас и не послушает слов ваших, то, выходя из дома или из города того, отрясите прах от ног ваших;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32127, 'rst66', 'mat', 10, 13, 'и если дом будет достоин, то мир ваш придет на него; если же не будет достоин, то мир ваш к вам возвратится.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32126, 'rst66', 'mat', 10, 12, 'а входя в дом, приветствуйте его, говоря: «мир дому сему»;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32125, 'rst66', 'mat', 10, 11, 'В какой бы город или селение ни вошли вы, наведывайтесь, кто в нем достоин, и там оставайтесь, пока не выйдете;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32124, 'rst66', 'mat', 10, 10, 'ни сумы&#769; на дорогу, ни двух одежд, ни обуви, ни посоха, ибо трудящийся достоин пропитания.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32123, 'rst66', 'mat', 10, 9, 'Не берите с собою ни золота, ни серебра, ни меди в поясы свои,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32122, 'rst66', 'mat', 10, 8, 'больных исцеляйте, прокаженных очищайте, мертвых воскрешайте, бесов изгоняйте; даром получили, даром давайте.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32121, 'rst66', 'mat', 10, 7, 'ходя же, проповедуйте, что приблизилось Царство Небесное;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32120, 'rst66', 'mat', 10, 6, 'а идите наипаче к погибшим овцам дома Израилева;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32119, 'rst66', 'mat', 10, 5, 'Сих двенадцать послал Иисус и заповедал им, говоря: на путь к язычникам не ходите и в город Самарянский не входите;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32118, 'rst66', 'mat', 10, 4, 'Симон Кананит и Иуда Искариот, который и предал Его.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32117, 'rst66', 'mat', 10, 3, 'Филипп и Варфоломей, Фома и Матфей мытарь, Иаков Алфеев и Леввей, прозванный Фаддеем,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32116, 'rst66', 'mat', 10, 2, 'Двенадцати же Апостолов имена суть сии: первый Симон, называемый Петром, и Андрей, брат его, Иаков Зеведеев и Иоанн, брат его,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32115, 'rst66', 'mat', 10, 1, 'И призвав двенадцать учеников Своих, Он дал им власть над нечистыми духами, чтобы изгонять их и врачевать всякую болезнь и всякую немощь.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32114, 'rst66', 'mat', 9, 38, 'итак, моли&#769;те Господина жатвы, чтобы выслал делателей на жатву Свою.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32113, 'rst66', 'mat', 9, 37, 'Тогда говорит ученикам Своим: жатвы много, а делателей мало;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32112, 'rst66', 'mat', 9, 36, 'Видя толпы народа, Он сжалился над ними, что они были изнурены и рассеяны, как овцы, не имеющие пастыря.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32111, 'rst66', 'mat', 9, 35, 'И ходил Иисус по всем городам и селениям, уча в синагогах их, проповедуя Евангелие Царствия и исцеляя всякую болезнь и всякую немощь в людях.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32110, 'rst66', 'mat', 9, 34, 'А фарисеи говорили: Он изгоняет бесов силою князя бесовского.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32109, 'rst66', 'mat', 9, 33, 'И когда бес был изгнан, немой стал говорить. И народ, удивляясь, говорил: никогда не бывало такого явления в Израиле.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32108, 'rst66', 'mat', 9, 32, 'Когда же те выходили, то привели к Нему человека немого бесноватого.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32107, 'rst66', 'mat', 9, 31, 'А они, выйдя, разгласили о Нем по всей земле той.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32106, 'rst66', 'mat', 9, 30, 'И открылись глаза их; и Иисус строго сказал им: смотрите, чтобы никто не узнал.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32105, 'rst66', 'mat', 9, 29, 'Тогда Он коснулся глаз их и сказал: по вере вашей да будет вам.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32104, 'rst66', 'mat', 9, 28, 'Когда же Он пришел в дом, слепые приступили к Нему. И говорит им Иисус: веруете ли, что Я могу это сделать? Они говорят Ему: ей, Господи!');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32103, 'rst66', 'mat', 9, 27, 'Когда Иисус шел оттуда, за Ним следовали двое слепых и кричали: помилуй нас, Иисус, сын Давидов!');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32102, 'rst66', 'mat', 9, 26, 'И разнесся слух о сем по всей земле той.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32101, 'rst66', 'mat', 9, 25, 'Когда же народ был выслан, Он, войдя, взял ее за руку, и девица встала.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32100, 'rst66', 'mat', 9, 24, 'сказал им: выйдите вон, ибо не умерла девица, но спит. И смеялись над Ним.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32099, 'rst66', 'mat', 9, 23, 'И когда пришел Иисус в дом начальника и увидел свирельщиков и народ в смятении,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32098, 'rst66', 'mat', 9, 22, 'Иисус же, обратившись и увидев ее, сказал: дерзай, дщерь! вера твоя спасла тебя. Женщина с того часа стала здорова.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32097, 'rst66', 'mat', 9, 21, 'ибо она говорила сама в себе: если только прикоснусь к одежде Его, выздоровею.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32096, 'rst66', 'mat', 9, 20, 'И вот, женщина, двенадцать лет страдавшая кровотечением, подойдя сзади, прикоснулась к краю одежды Его,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32095, 'rst66', 'mat', 9, 19, 'И встав, Иисус пошел за ним, и ученики Его.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32094, 'rst66', 'mat', 9, 18, 'Когда Он говорил им сие, подошел к Нему некоторый начальник и, кланяясь Ему, говорил: дочь моя теперь умирает; но приди, возложи на нее руку Твою, и она будет жива.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32093, 'rst66', 'mat', 9, 17, 'Не вливают также вина молодого в мехи ветхие; а иначе прорываются мехи, и вино вытекает, и мехи пропадают, но вино молодое вливают в новые мехи, и сберегается то и другое.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32092, 'rst66', 'mat', 9, 16, 'И никто к ветхой одежде не приставляет заплаты из небеленой ткани, ибо вновь пришитое отдерет от старого и дыра будет еще хуже.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32091, 'rst66', 'mat', 9, 15, 'И сказал им Иисус: могут ли печалиться сыны чертога брачного, пока с ними жених? Но придут дни, когда отнимется у них жених, и тогда будут поститься.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32090, 'rst66', 'mat', 9, 14, 'Тогда приходят к Нему ученики Иоанновы и говорят: почему мы и фарисеи постимся много, а Твои ученики не постятся?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32089, 'rst66', 'mat', 9, 13, 'пойдите, научитесь, что&#769; значит: «милости хочу, а не жертвы»? Ибо Я пришел призвать не праведников, но грешников к покаянию.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32088, 'rst66', 'mat', 9, 12, 'Иисус же, услышав это, сказал им: не здоровые имеют нужду во враче, но больные,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32087, 'rst66', 'mat', 9, 11, 'Увидев то, фарисеи сказали ученикам Его: для чего Учитель ваш ест и пьет с мытарями и грешниками?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32086, 'rst66', 'mat', 9, 10, 'И когда Иисус возлежал в доме, многие мытари и грешники пришли и возлегли с Ним и учениками Его.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32085, 'rst66', 'mat', 9, 9, 'Проходя оттуда, Иисус увидел человека, сидящего у сбора пошлин, по имени Матфея, и говорит ему: следуй за Мною. И он встал и последовал за Ним.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32084, 'rst66', 'mat', 9, 8, 'Народ же, видев это, удивился и прославил Бога, давшего такую власть человекам.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32083, 'rst66', 'mat', 9, 7, 'И он встал, <i>взял постель свою</i> и пошел в дом свой.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32082, 'rst66', 'mat', 9, 6, 'Но чтобы вы знали, что Сын Человеческий имеет власть на земле прощать грехи, &mdash; тогда говорит расслабленному: встань, возьми постель твою, и иди в дом твой.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32081, 'rst66', 'mat', 9, 5, 'Ибо что легче сказать: «прощаются тебе грехи», или сказать: «встань и ходи»?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32080, 'rst66', 'mat', 9, 4, 'Иисус же, видя помышления их, сказал: для чего вы мыслите худое в сердцах ваших?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32079, 'rst66', 'mat', 9, 3, 'При сем некоторые из книжников сказали сами в себе: Он богохульствует.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32078, 'rst66', 'mat', 9, 2, 'И вот, принесли к Нему расслабленного, положенного на постели. И, видя Иисус веру их, сказал расслабленному: дерзай, чадо! прощаются тебе грехи твои.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32077, 'rst66', 'mat', 9, 1, 'Тогда Он, войдя в лодку, переправился <i>обратно</i> и прибыл в Свой город.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32076, 'rst66', 'mat', 8, 34, 'И вот, весь город вышел навстречу Иисусу; и, увидев Его, просили, чтобы Он отошел от пределов их.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32075, 'rst66', 'mat', 8, 33, 'Пастухи же побежали и, придя в город, рассказали обо всем, и о том, что было с бесноватыми.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32074, 'rst66', 'mat', 8, 32, 'И Он сказал им: идите. И они, выйдя, пошли в стадо свиное. И вот, всё стадо свиней бросилось с крутизны в море и погибло в воде.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32073, 'rst66', 'mat', 8, 31, 'И бесы просили Его: если выгонишь нас, то пошли нас в стадо свиней.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32072, 'rst66', 'mat', 8, 30, 'Вдали же от них паслось большое стадо свиней.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32071, 'rst66', 'mat', 8, 29, 'И вот, они закричали: что Тебе до нас, Иисус, Сын Божий? пришел Ты сюда прежде времени мучить нас.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32070, 'rst66', 'mat', 8, 28, 'И когда Он прибыл на другой берег в страну Гергесинскую, Его встретили два бесноватые, вышедшие из гробов, весьма свирепые, так что никто не смел проходить тем путем.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32069, 'rst66', 'mat', 8, 27, 'Люди же, удивляясь, говорили: кто это, что и ветры и море повинуются Ему?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32068, 'rst66', 'mat', 8, 26, 'И говорит им: что вы <i>так</i> боязливы, маловерные? Потом, встав, запретил ветрам и морю, и сделалась великая тишина.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32067, 'rst66', 'mat', 8, 25, 'Тогда ученики Его, подойдя к Нему, разбудили Его и сказали: Господи! спаси нас, погибаем.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32066, 'rst66', 'mat', 8, 24, 'И вот, сделалось великое волнение на море, так что лодка покрывалась волнами; а Он спал.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32065, 'rst66', 'mat', 8, 23, 'И когда вошел Он в лодку, за Ним последовали ученики Его.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32064, 'rst66', 'mat', 8, 22, 'Но Иисус сказал ему: иди за Мною, и предоставь мертвым погребать своих мертвецов.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32063, 'rst66', 'mat', 8, 21, 'Другой же из учеников Его сказал Ему: Господи! позволь мне прежде пойти и похоронить отца моего.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32062, 'rst66', 'mat', 8, 20, 'И говорит ему Иисус: лисицы имеют норы и птицы небесные &mdash; гнезда, а Сын Человеческий не имеет, где приклонить голову.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32061, 'rst66', 'mat', 8, 19, 'Тогда один книжник, подойдя, сказал Ему: Учитель! я пойду за Тобою, куда бы Ты ни пошел.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32060, 'rst66', 'mat', 8, 18, 'Увидев же Иисус вокруг Себя множество народа, велел [ученикам] отплыть на другую сторону.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32059, 'rst66', 'mat', 8, 17, 'да сбудется реченное через пророка Исаию, который говорит: «Он взял на Себя наши немощи и понес болезни».');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32058, 'rst66', 'mat', 8, 16, 'Когда же настал вечер, к Нему привели многих бесноватых, и Он изгнал духов словом и исцелил всех больных,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32057, 'rst66', 'mat', 8, 15, 'и коснулся руки ее, и горячка оставила ее; и она встала и служила им.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32056, 'rst66', 'mat', 8, 14, 'Придя в дом Петров, Иисус увидел тещу его, лежащую в горячке,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32055, 'rst66', 'mat', 8, 13, 'И сказал Иисус сотнику: иди, и, как ты веровал, да будет тебе. И выздоровел слуга его в тот час.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32054, 'rst66', 'mat', 8, 12, 'а сыны царства извержены будут во тьму внешнюю: там будет плач и скрежет зубов.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32053, 'rst66', 'mat', 8, 11, 'Говорю же вам, что многие придут с востока и запада и возлягут с Авраамом, Исааком и Иаковом в Царстве Небесном;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32052, 'rst66', 'mat', 8, 10, 'Услышав сие, Иисус удивился и сказал идущим за Ним: истинно говорю вам, и в Израиле не нашел Я такой веры.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32051, 'rst66', 'mat', 8, 9, 'ибо я и подвластный человек, но, имея у себя в подчинении воинов, говорю одному: «пойди», и идет; и другому: «приди», и приходит; и слуге моему: «сделай то», и делает.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32050, 'rst66', 'mat', 8, 8, 'Сотник же, отвечая, сказал: Господи! я недостоин, чтобы Ты вошел под кров мой, но скажи только слово, и выздоровеет слуга мой;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32049, 'rst66', 'mat', 8, 7, 'Иисус говорит ему: Я приду и исцелю его.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32048, 'rst66', 'mat', 8, 6, 'Господи! слуга мой лежит дома в расслаблении и жестоко страдает.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32047, 'rst66', 'mat', 8, 5, 'Когда же вошел Иисус в Капернаум, к Нему подошел сотник и просил Его:');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32046, 'rst66', 'mat', 8, 4, 'И говорит ему Иисус: смотри, никому не сказывай, но пойди, покажи себя священнику и принеси дар, какой повелел Моисей, во свидетельство им.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32045, 'rst66', 'mat', 8, 3, 'Иисус, простерши руку, коснулся его и сказал: хочу, очистись. И он тотчас очистился от проказы.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32044, 'rst66', 'mat', 8, 2, 'И вот подошел прокаженный и, кланяясь Ему, сказал: Господи! если хочешь, можешь меня очистить.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32043, 'rst66', 'mat', 8, 1, 'Когда же сошел Он с горы, за Ним последовало множество народа.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32042, 'rst66', 'mat', 7, 29, 'ибо Он учил их, как власть имеющий, а не как книжники и фарисеи.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32041, 'rst66', 'mat', 7, 28, 'И когда Иисус окончил слова сии, народ дивился учению Его,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32040, 'rst66', 'mat', 7, 27, 'и пошел дождь, и разлились реки, и подули ветры, и налегли на дом тот; и он упал, и было падение его великое.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32039, 'rst66', 'mat', 7, 26, 'А всякий, кто слушает сии слова Мои и не исполняет их, уподобится человеку безрассудному, который построил дом свой на песке;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32038, 'rst66', 'mat', 7, 25, 'и пошел дождь, и разлились реки, и подули ветры, и устремились на дом тот, и он не упал, потому что основан был на камне.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32037, 'rst66', 'mat', 7, 24, 'Итак, всякого, кто слушает слова Мои сии и исполняет их, уподоблю мужу благоразумному, который построил дом свой на камне;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32036, 'rst66', 'mat', 7, 23, 'И тогда объявлю им: «Я никогда не знал вас; отойдите от Меня, делающие беззаконие».');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32035, 'rst66', 'mat', 7, 22, 'Многие скажут Мне в тот день: «Господи! Господи! не от Твоего ли имени мы пророчествовали? и не Твоим ли именем бесов изгоняли? и не Твоим ли именем многие чудеса творили?»');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32034, 'rst66', 'mat', 7, 21, 'Не всякий, говорящий Мне: «Господи! Господи!», войдет в Царство Небесное, но исполняющий волю Отца Моего Небесного.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32033, 'rst66', 'mat', 7, 20, 'Итак, по плодам их узна&#769;ете их.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32032, 'rst66', 'mat', 7, 19, 'Всякое дерево, не приносящее плода доброго, срубают и бросают в огонь.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32031, 'rst66', 'mat', 7, 18, 'Не может дерево доброе приносить плоды худые, ни дерево худое приносить плоды добрые.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32030, 'rst66', 'mat', 7, 17, 'Так всякое дерево доброе приносит и плоды добрые, а худое дерево приносит и плоды худые.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32029, 'rst66', 'mat', 7, 16, 'По плодам их узна&#769;ете их. Собирают ли с терновника виноград или с репейника смоквы?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32028, 'rst66', 'mat', 7, 15, 'Берегитесь лжепророков, которые приходят к вам в овечьей одежде, а внутри суть волки хищные.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32027, 'rst66', 'mat', 7, 14, 'потому что тесны&#769; врата и узок путь, ведущие в жизнь, и немногие находят их.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32026, 'rst66', 'mat', 7, 13, 'Входи&#769;те тесными вратами, потому что широки врата и пространен путь, ведущие в погибель, и многие идут ими;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32025, 'rst66', 'mat', 7, 12, 'Итак, во всем, как хотите, чтобы с вами поступали люди, так поступайте и вы с ними, ибо в этом закон и пророки.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32024, 'rst66', 'mat', 7, 11, 'Итак, если вы, будучи злы, умеете даяния благие давать детям вашим, тем более Отец ваш Небесный даст блага просящим у Него.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32023, 'rst66', 'mat', 7, 10, 'И когда попросит рыбы, подал бы ему змею?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32022, 'rst66', 'mat', 7, 9, 'Есть ли между вами такой человек, который, когда сын его попросит у него хлеба, подал бы ему камень?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32021, 'rst66', 'mat', 7, 8, 'ибо всякий просящий получает, и ищущий находит, и стучащему отворят.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32020, 'rst66', 'mat', 7, 7, 'Просите, и дано будет вам; ищите, и найдете; стучите, и отворят вам;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32019, 'rst66', 'mat', 7, 6, 'Не давайте святыни псам и не бросайте жемчуга вашего перед свиньями, чтобы они не попрали его ногами своими и, обратившись, не растерзали вас.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32018, 'rst66', 'mat', 7, 5, 'Лицемер! вынь прежде бревно из твоего глаза и тогда увидишь, <i>как</i> вынуть сучок из глаза брата твоего.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32017, 'rst66', 'mat', 7, 4, 'Или как скажешь брату твоему: «дай, я выну сучок из глаза твоего», а вот, в твоем глазе бревно?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32016, 'rst66', 'mat', 7, 3, 'И что ты смотришь на сучок в глазе брата твоего, а бревна в твоем глазе не чувствуешь?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32015, 'rst66', 'mat', 7, 2, 'ибо каким судом судите, <i>таким</i> будете судимы; и какою мерою мерите, <i>такою</i> и вам будут мерить.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32014, 'rst66', 'mat', 7, 1, 'Не судите, да не судимы будете,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32013, 'rst66', 'mat', 6, 34, 'Итак, не заботьтесь о завтрашнем дне, ибо завтрашний <i>сам</i> будет заботиться о своем: довольно для <i>каждого</i> дня своей заботы.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32012, 'rst66', 'mat', 6, 33, 'Ищите же прежде Царства Божия и правды Его, и это все приложится вам.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32011, 'rst66', 'mat', 6, 32, 'Потому что всего этого ищут язычники, и потому что Отец ваш Небесный знает, что вы имеете нужду во всем этом.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32010, 'rst66', 'mat', 6, 31, 'Итак, не заботьтесь и не говорите: «что нам есть?» или «что пить?» или «во что одеться?»');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32009, 'rst66', 'mat', 6, 30, 'если же траву полевую, которая сегодня есть, а завтра будет брошена в печь, Бог так одевает, кольми паче вас, маловеры!');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32008, 'rst66', 'mat', 6, 29, 'но говорю вам, что и Соломон во всей славе своей не одевался так, как всякая из них;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32007, 'rst66', 'mat', 6, 28, 'И об одежде что заботитесь? Посмотрите на полевые лилии, как они растут: ни трудятся, ни прядут;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32006, 'rst66', 'mat', 6, 27, 'Да и кто из вас, заботясь, может прибавить себе росту <i>хотя</i> на один локоть?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32005, 'rst66', 'mat', 6, 26, 'Взгляните на птиц небесных: они ни сеют, ни жнут, ни собирают в житницы; и Отец ваш Небесный питает их. Вы не гораздо ли лучше их?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32004, 'rst66', 'mat', 6, 25, 'Посему говорю вам: не заботьтесь для души вашей, что вам есть и что пить, ни для тела вашего, во что одеться. Душа не больше ли пищи, и тело &mdash; одежды?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32003, 'rst66', 'mat', 6, 24, 'Никто не может служить двум господам: ибо или одного будет ненавидеть, а другого любить; или одному станет усердствовать, а о другом нерадеть. Не можете служить Богу и маммоне.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32002, 'rst66', 'mat', 6, 23, 'если же око твое будет худо, то всё тело твое будет темно. Итак, если свет, который в тебе, &mdash; тьма, то какова же тьма?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32001, 'rst66', 'mat', 6, 22, 'Светильник для тела есть око. Итак, если око твое будет чисто, то всё тело твое будет светло;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (32000, 'rst66', 'mat', 6, 21, 'ибо где сокровище ваше, там будет и сердце ваше.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (31999, 'rst66', 'mat', 6, 20, 'но собирайте себе сокровища на небе, где ни моль, ни ржа не истребляют и где воры не подкапывают и не крадут,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (31998, 'rst66', 'mat', 6, 19, 'Не собирайте себе сокровищ на земле, где моль и ржа истребляют и где воры подкапывают и крадут,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (31997, 'rst66', 'mat', 6, 18, 'чтобы явиться постящимся не пред людьми, но пред Отцом твоим, Который втайне; и Отец твой, видящий тайное, воздаст тебе явно.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (31996, 'rst66', 'mat', 6, 17, 'А ты, когда постишься, помажь голову твою и умой лицо твое,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (31995, 'rst66', 'mat', 6, 16, 'Также, когда поститесь, не будьте унылы, как лицемеры, ибо они принимают на себя мрачные лица, чтобы показаться людям постящимися. Истинно говорю вам, что они уже получают награду свою.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (31994, 'rst66', 'mat', 6, 15, 'а если не будете прощать людям согрешения их, то и Отец ваш не простит вам согрешений ваших.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (31993, 'rst66', 'mat', 6, 14, 'Ибо если вы будете прощать людям согрешения их, то простит и вам Отец ваш Небесный,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (31992, 'rst66', 'mat', 6, 13, 'и не введи нас в искушение, но избавь нас от лукавого. Ибо Твое есть Царство и сила и слава вовеки. Аминь».');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (31991, 'rst66', 'mat', 6, 12, 'и прости нам долги наши, как и мы прощаем должникам нашим;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (31990, 'rst66', 'mat', 6, 11, 'хлеб наш насущный дай нам на сей день;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (31989, 'rst66', 'mat', 6, 10, 'да приидет Царствие Твое; да будет воля Твоя и на земле, как на небе;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (31988, 'rst66', 'mat', 6, 9, 'Моли&#769;тесь же так: «Отче наш, сущий на небесах! да святится имя Твое;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (31987, 'rst66', 'mat', 6, 8, 'не уподобляйтесь им, ибо знает Отец ваш, в чем вы имеете нужду, прежде вашего прошения у Него.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (31986, 'rst66', 'mat', 6, 7, 'А молясь, не говорите лишнего, как язычники, ибо они думают, что в многословии своем будут услышаны;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (31985, 'rst66', 'mat', 6, 6, 'Ты же, когда молишься, войди в комнату твою и, затворив дверь твою, помолись Отцу твоему, Который втайне; и Отец твой, видящий тайное, воздаст тебе явно.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (31984, 'rst66', 'mat', 6, 5, 'И, когда молишься, не будь, как лицемеры, которые любят в синагогах и на углах улиц, останавливаясь, молиться, чтобы показаться перед людьми. Истинно говорю вам, что они уже получают награду свою.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (31983, 'rst66', 'mat', 6, 4, 'чтобы милостыня твоя была втайне; и Отец твой, видящий тайное, воздаст тебе явно.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (31982, 'rst66', 'mat', 6, 3, 'У тебя же, когда творишь милостыню, пусть левая рука твоя не знает, что делает правая,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (31981, 'rst66', 'mat', 6, 2, 'Итак, когда творишь милостыню, не труби перед собою, как делают лицемеры в синагогах и на улицах, чтобы прославляли их люди. Истинно говорю вам: они уже получают награду свою.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (31980, 'rst66', 'mat', 6, 1, 'Смотрите, не творите милостыни вашей пред людьми с тем, чтобы они видели вас: иначе не будет вам награды от Отца вашего Небесного.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (31979, 'rst66', 'mat', 5, 48, 'Итак, будьте совершенны, как совершен Отец ваш Небесный.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (31978, 'rst66', 'mat', 5, 47, 'И если вы приветствуете только братьев ваших, что особенного делаете? Не так же ли поступают и язычники?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (31977, 'rst66', 'mat', 5, 46, 'Ибо если вы будете любить любящих вас, какая вам награда? Не то же ли делают и мытари?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (31976, 'rst66', 'mat', 5, 45, 'да будете сынами Отца вашего Небесного, ибо Он повелевает солнцу Своему восходить над злыми и добрыми и посылает дождь на праведных и неправедных.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (31975, 'rst66', 'mat', 5, 44, 'А Я говорю вам: любите врагов ваших, благословляйте проклинающих вас, благотворите ненавидящим вас и молитесь за обижающих вас и гонящих вас,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (31974, 'rst66', 'mat', 5, 43, 'Вы слышали, что сказано: «люби ближнего твоего и ненавидь врага твоего».');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (31973, 'rst66', 'mat', 5, 42, 'Просящему у тебя дай, и от хотящего занять у тебя не отвращайся.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (31972, 'rst66', 'mat', 5, 41, 'и кто принудит тебя идти с ним одно поприще, иди с ним два.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (31971, 'rst66', 'mat', 5, 40, 'и кто захочет судиться с тобою и взять у тебя рубашку, отдай ему и верхнюю одежду;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (31970, 'rst66', 'mat', 5, 39, 'А Я говорю вам: не противься злому. Но кто ударит тебя в правую щеку твою, обрати к нему и другую;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (31969, 'rst66', 'mat', 5, 38, 'Вы слышали, что сказано: «око за око и зуб за зуб».');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (31968, 'rst66', 'mat', 5, 37, 'Но да будет слово ваше: «да, да»; «нет, нет»; а что сверх этого, то от лукавого.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (31967, 'rst66', 'mat', 5, 36, 'ни головою твоею не клянись, потому что не можешь ни одного волоса сделать белым или черным.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (31966, 'rst66', 'mat', 5, 35, 'ни землею, потому что она подножие ног Его; ни Иерусалимом, потому что он город великого Царя;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (31965, 'rst66', 'mat', 5, 34, 'А Я говорю вам: не клянись вовсе: ни небом, потому что оно престол Божий;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (31964, 'rst66', 'mat', 5, 33, 'Еще слышали вы, что сказано древним: «не преступай клятвы, но исполняй пред Господом клятвы твои».');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (31963, 'rst66', 'mat', 5, 32, 'А Я говорю вам: кто разводится с женою своею, кроме вины любодеяния, тот подает ей повод прелюбодействовать; и кто женится на разведенной, тот прелюбодействует.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (31962, 'rst66', 'mat', 5, 31, 'Сказано также, что если кто разведется с женою своею, пусть даст ей разводную.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (31961, 'rst66', 'mat', 5, 30, 'И если правая твоя рука соблазняет тебя, отсеки ее и брось от себя, ибо лучше для тебя, чтобы погиб один из членов твоих, а не все тело твое было ввержено в геенну.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (31960, 'rst66', 'mat', 5, 29, 'Если же правый глаз твой соблазняет тебя, вырви его и брось от себя, ибо лучше для тебя, чтобы погиб один из членов твоих, а не все тело твое было ввержено в геенну.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (31959, 'rst66', 'mat', 5, 28, 'А Я говорю вам, что всякий, кто смотрит на женщину с вожделением, уже прелюбодействовал с нею в сердце своем.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (31958, 'rst66', 'mat', 5, 27, 'Вы слышали, что сказано древним: «не прелюбодействуй».');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (31957, 'rst66', 'mat', 5, 26, 'истинно говорю тебе: ты не выйдешь оттуда, пока не отдашь до последнего кодранта.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (31956, 'rst66', 'mat', 5, 25, 'Мирись с соперником твоим скорее, пока ты еще на пути с ним, чтобы соперник не отдал тебя судье, а судья не отдал бы тебя слуге, и не ввергли бы тебя в темницу;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (31955, 'rst66', 'mat', 5, 24, 'оставь там дар твой пред жертвенником, и пойди прежде примирись с братом твоим, и тогда приди и принеси дар твой.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (31954, 'rst66', 'mat', 5, 23, 'Итак, если ты принесешь дар твой к жертвеннику и там вспомнишь, что брат твой имеет что‐нибудь против тебя,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (31953, 'rst66', 'mat', 5, 22, 'А Я говорю вам, что всякий, гневающийся на брата своего напрасно, подлежит суду; кто же скажет брату своему: «рака&#769;», подлежит синедриону; а кто скажет: «безумный», подлежит геенне огненной.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (31952, 'rst66', 'mat', 5, 21, 'Вы слышали, что сказано древним: «не убивай, кто же убьет, подлежит суду».');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (31951, 'rst66', 'mat', 5, 20, 'Ибо, говорю вам, если праведность ваша не превзойдет праведности книжников и фарисеев, то вы не войдете в Царство Небесное.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (31950, 'rst66', 'mat', 5, 19, 'Итак, кто нарушит одну из заповедей сих малейших и научит так людей, тот малейшим наречется в Царстве Небесном; а кто сотворит и научит, тот великим наречется в Царстве Небесном.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (31949, 'rst66', 'mat', 5, 18, 'Ибо истинно говорю вам: доколе не прейдет небо и земля, ни одна иота или ни одна черта не прейдет из закона, пока не исполнится все.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (31948, 'rst66', 'mat', 5, 17, 'Не думайте, что Я пришел нарушить закон или пророков: не нарушить пришел Я, но исполнить.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (31947, 'rst66', 'mat', 5, 16, 'Так да светит свет ваш пред людьми, чтобы они видели ваши добрые дела и прославляли Отца вашего Небесного.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (31946, 'rst66', 'mat', 5, 15, 'И, зажегши свечу, не ставят ее под сосудом, но на подсвечнике, и светит всем в доме.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (31945, 'rst66', 'mat', 5, 14, 'Вы &mdash; свет мира. Не может укрыться город, стоящий на верху горы.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (31944, 'rst66', 'mat', 5, 13, 'Вы &mdash; соль земли. Если же соль потеряет силу, то чем сделаешь ее соленою? Она уже ни к чему не годна, как разве выбросить ее вон на попрание людям.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (31943, 'rst66', 'mat', 5, 12, 'Радуйтесь и веселитесь, ибо велика ваша награда на небесах: так гнали <i>и</i> пророков, бывших прежде вас.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (31942, 'rst66', 'mat', 5, 11, 'Блаженны вы, когда будут поносить вас и гнать и всячески неправедно злословить за Меня.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (31941, 'rst66', 'mat', 5, 10, 'Блаженны изгнанные за правду, ибо их есть Царство Небесное.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (31940, 'rst66', 'mat', 5, 9, 'Блаженны миротворцы, ибо они будут наречены сынами Божиими.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (31939, 'rst66', 'mat', 5, 8, 'Блаженны чистые сердцем, ибо они Бога узрят.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (31938, 'rst66', 'mat', 5, 7, 'Блаженны милостивые, ибо они помилованы будут.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (31937, 'rst66', 'mat', 5, 6, 'Блаженны алчущие и жаждущие правды, ибо они насытятся.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (31936, 'rst66', 'mat', 5, 5, 'Блаженны кроткие, ибо они наследуют землю.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (31935, 'rst66', 'mat', 5, 4, 'Блаженны плачущие, ибо они утешатся.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (31934, 'rst66', 'mat', 5, 3, 'Блаженны нищие духом, ибо их есть Царство Небесное.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (31933, 'rst66', 'mat', 5, 2, 'И Он, отверзши уста Свои, учил их, говоря:');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (31932, 'rst66', 'mat', 5, 1, 'Увидев народ, Он взошел на гору; и, когда сел, приступили к Нему ученики Его.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (31931, 'rst66', 'mat', 4, 25, 'И следовало за Ним множество народа из Галилеи и Десятиградия, и Иерусалима, и Иудеи, и из‐за Иордана.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (31930, 'rst66', 'mat', 4, 24, 'И прошел о Нем слух по всей Сирии; и приводили к Нему всех немощных, одержимых различными болезнями и припадками, и бесноватых, и лунатиков, и расслабленных, и Он исцелял их.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (31929, 'rst66', 'mat', 4, 23, 'И ходил Иисус по всей Галилее, уча в синагогах их и проповедуя Евангелие Царствия, и исцеляя всякую болезнь и всякую немощь в людях.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (31928, 'rst66', 'mat', 4, 22, 'И они тотчас, оставив лодку и отца своего, последовали за Ним.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (31927, 'rst66', 'mat', 4, 21, 'Оттуда, идя далее, увидел Он других двух братьев, Иакова Зеведеева и Иоанна, брата его, в лодке с Зеведеем, отцом их, починивающих сети свои, и призвал их.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (31926, 'rst66', 'mat', 4, 20, 'И они тотчас, оставив сети, последовали за Ним.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (31925, 'rst66', 'mat', 4, 19, 'и говорит им: идите за Мною, и Я сделаю вас ловцами человеков.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (31924, 'rst66', 'mat', 4, 18, 'Проходя же близ моря Галилейского, Он увидел двух братьев: Симона, называемого Петром, и Андрея, брата его, закидывающих сети в море, ибо они были рыболовы,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (31923, 'rst66', 'mat', 4, 17, 'С того времени Иисус начал проповедовать и говорить: покайтесь, ибо приблизилось Царство Небесное.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (31922, 'rst66', 'mat', 4, 16, 'народ, сидящий во тьме, увидел свет великий, и сидящим в стране и тени смертной воссиял свет».');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (31921, 'rst66', 'mat', 4, 15, '«земля Завулонова и земля Неффалимова, на пути приморском, за Иорданом, Галилея языческая,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (31920, 'rst66', 'mat', 4, 14, 'да сбудется реченное через пророка Исаию, который говорит:');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (31919, 'rst66', 'mat', 4, 13, 'и, оставив Назарет, пришел и поселился в Капернауме приморском, в пределах Завулоновых и Неффалимовых,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (31918, 'rst66', 'mat', 4, 12, 'Услышав же Иисус, что Иоанн отдан <i>под стражу</i>, удалился в Галилею');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (31917, 'rst66', 'mat', 4, 11, 'Тогда оставляет Его диавол, и се, Ангелы приступили и служили Ему.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (31916, 'rst66', 'mat', 4, 10, 'Тогда Иисус говорит ему: отойди от Меня, сатана, ибо написано: «Господу Богу твоему поклоняйся и Ему одному служи».');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (31915, 'rst66', 'mat', 4, 9, 'и говорит Ему: всё это дам Тебе, если, пав, поклонишься мне.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (31914, 'rst66', 'mat', 4, 8, 'Опять берет Его диавол на весьма высокую гору и показывает Ему все царства мира и славу их,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (31913, 'rst66', 'mat', 4, 7, 'Иисус сказал ему: написано также: «не искушай Господа Бога твоего».');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (31912, 'rst66', 'mat', 4, 6, 'и говорит Ему: если Ты Сын Божий, бросься вниз, ибо написано: «Ангелам Своим заповедает о Тебе, и на руках понесут Тебя, да не преткнешься о камень ногою Твоею».');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (31911, 'rst66', 'mat', 4, 5, 'Потом берет Его диавол в святой город и поставляет Его на крыле храма,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (31910, 'rst66', 'mat', 4, 4, 'Он же сказал ему в ответ: написано: «не хлебом одним будет жить человек, но всяким словом, исходящим из уст Божиих».');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (31909, 'rst66', 'mat', 4, 3, 'И приступил к Нему искуситель и сказал: если Ты Сын Божий, скажи, чтобы камни сии сделались хлебами.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (31908, 'rst66', 'mat', 4, 2, 'и, постившись сорок дней и сорок ночей, напоследок взалкал.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (31907, 'rst66', 'mat', 4, 1, 'Тогда Иисус возведен был Духом в пустыню, для искушения от диавола,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (31906, 'rst66', 'mat', 3, 17, 'И се, глас с небес глаголющий: Сей есть Сын Мой возлюбленный, в Котором Мое благоволение.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (31905, 'rst66', 'mat', 3, 16, 'И, крестившись, Иисус тотчас вышел из воды, &mdash; и се, отверзлись Ему небеса, и увидел <i>Иоанн</i> Духа Божия, Который сходил, как голубь, и ниспускался на Него.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (31904, 'rst66', 'mat', 3, 15, 'Но Иисус сказал ему в ответ: оставь теперь, ибо так надлежит нам исполнить всякую правду. Тогда <i>Иоанн</i> допускает Его.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (31903, 'rst66', 'mat', 3, 14, 'Иоанн же удерживал Его и говорил: мне надобно креститься от Тебя, и Ты ли приходишь ко мне?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (31902, 'rst66', 'mat', 3, 13, 'Тогда приходит Иисус из Галилеи на Иордан к Иоанну креститься от него.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (31901, 'rst66', 'mat', 3, 12, 'лопата Его в руке Его, и Он очистит гумно Свое и соберет пшеницу Свою в житницу, а солому сожжет огнем неугасимым.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (31900, 'rst66', 'mat', 3, 11, 'Я крещу вас в воде в покаяние, но Идущий за мною сильнее меня; я не достоин понести обувь Его; Он будет крестить вас Духом Святым и огнем;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (31899, 'rst66', 'mat', 3, 10, 'Уже и секира при корне дерев лежит: всякое дерево, не приносящее доброго плода, срубают и бросают в огонь.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (31898, 'rst66', 'mat', 3, 9, 'и не думайте говорить в себе: «отец у нас Авраам», ибо говорю вам, что Бог может из камней сих воздвигнуть детей Аврааму.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (31897, 'rst66', 'mat', 3, 8, 'Сотворите же достойный плод покаяния');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (31896, 'rst66', 'mat', 3, 7, 'Увидев же Иоанн многих фарисеев и саддукеев, идущих к нему креститься, сказал им: порождения ехиднины! кто внушил вам бежать от будущего гнева?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (31895, 'rst66', 'mat', 3, 6, 'и крестились от него в Иордане, исповедуя грехи свои.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (31894, 'rst66', 'mat', 3, 5, 'Тогда Иерусалим и вся Иудея и вся окрестность Иорданская выходили к нему');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (31893, 'rst66', 'mat', 3, 4, 'Сам же Иоанн имел одежду из верблюжьего волоса и пояс кожаный на чреслах своих, а пищею его были акриды и дикий мед.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (31892, 'rst66', 'mat', 3, 3, 'Ибо он тот, о котором сказал пророк Исаия: «глас вопиющего в пустыне: приготовьте путь Господу, прямыми сделайте стези Ему».');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (31891, 'rst66', 'mat', 3, 2, 'и говорит: покайтесь, ибо приблизилось Царство Небесное.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (31890, 'rst66', 'mat', 3, 1, 'В те дни приходит Иоанн Креститель, и проповедует в пустыне Иудейской,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (31889, 'rst66', 'mat', 2, 23, 'и, придя, поселился в городе, называемом Назарет, да сбудется реченное через пророков, что Он Назореем наречется.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (31888, 'rst66', 'mat', 2, 22, 'Услышав же, что Архелай царствует в Иудее вместо Ирода, отца своего, убоялся туда идти; но, получив во сне откровение, пошел в пределы Галилейские');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (31887, 'rst66', 'mat', 2, 21, 'Он встал, взял Младенца и Матерь Его и пришел в землю Израилеву.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (31886, 'rst66', 'mat', 2, 20, 'и говорит: встань, возьми Младенца и Матерь Его и иди в землю Израилеву, ибо умерли искавшие души Младенца.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (31885, 'rst66', 'mat', 2, 19, 'По смерти же Ирода, &mdash; се, Ангел Господень во сне является Иосифу в Египте');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (31884, 'rst66', 'mat', 2, 18, '«глас в Раме слышен, плач и рыдание и вопль великий; Рахиль плачет о детях своих и не хочет утешиться, ибо их нет».');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (31883, 'rst66', 'mat', 2, 17, 'Тогда сбылось реченное через пророка Иеремию, который говорит:');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (31882, 'rst66', 'mat', 2, 16, 'Тогда Ирод, увидев себя осмеянным волхвами, весьма разгневался и послал избить всех младенцев в Вифлееме и во всех пределах его, от двух лет и ниже, по времени, которое выведал от волхвов.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (31881, 'rst66', 'mat', 2, 15, 'и там был до смерти Ирода, да сбудется реченное Господом через пророка, который говорит: «из Египта воззвал Я Сына Моего».');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (31880, 'rst66', 'mat', 2, 14, 'Он встал, взял Младенца и Матерь Его ночью и пошел в Египет,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (31879, 'rst66', 'mat', 2, 13, 'Когда же они отошли, &mdash; се, Ангел Господень является во сне Иосифу и говорит: встань, возьми Младенца и Матерь Его и беги в Египет, и будь там, доколе не скажу тебе, ибо Ирод хочет искать Младенца, чтобы погубить Его.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (31878, 'rst66', 'mat', 2, 12, 'И, получив во сне откровение не возвращаться к Ироду, иным путем отошли в страну свою.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (31877, 'rst66', 'mat', 2, 11, 'и, войдя в дом, увидели Младенца с Мариею, Матерью Его, и, пав, поклонились Ему; и, открыв сокровища свои, принесли Ему дары: золото, ладан и смирну.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (31876, 'rst66', 'mat', 2, 10, 'Увидев же звезду, они возрадовались радостью весьма великою,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (31875, 'rst66', 'mat', 2, 9, 'Они, выслушав царя, пошли. И се, звезда, которую видели они на востоке, шла перед ними, <i>как</i> наконец пришла и остановилась над <i>местом</i>, где был Младенец.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (31874, 'rst66', 'mat', 2, 8, 'и, послав их в Вифлеем, сказал: пойдите, тщательно разведайте о Младенце и, когда найдете, известите меня, чтобы и мне пойти поклониться Ему.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (31873, 'rst66', 'mat', 2, 7, 'Тогда Ирод, тайно призвав волхвов, выведал от них время появления звезды');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (31872, 'rst66', 'mat', 2, 6, '«и ты, Вифлеем, земля Иудина, ничем не меньше воеводств Иудиных, ибо из тебя произойдет Вождь, Который упасет народ Мой, Израиля».');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (31871, 'rst66', 'mat', 2, 5, 'Они же сказали ему: в Вифлееме Иудейском, ибо так написано через пророка:');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (31870, 'rst66', 'mat', 2, 4, 'И, собрав всех первосвященников и книжников народных, спрашивал у них: где должно родиться Христу?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (31869, 'rst66', 'mat', 2, 3, 'Услышав это, Ирод царь встревожился, и весь Иерусалим с ним.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (31868, 'rst66', 'mat', 2, 2, 'где родившийся Царь Иудейский? ибо мы видели звезду Его на востоке и пришли поклониться Ему.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (31867, 'rst66', 'mat', 2, 1, 'Когда же Иисус родился в Вифлееме Иудейском во дни царя Ирода, пришли в Иерусалим волхвы с востока и говорят:');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (31866, 'rst66', 'mat', 1, 25, 'и не знал Ее, как наконец Она родила Сына Своего первенца, и он нарек Ему имя Иисус.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (31865, 'rst66', 'mat', 1, 24, 'Встав от сна, Иосиф поступил, как повелел ему Ангел Господень, и принял жену свою,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (31864, 'rst66', 'mat', 1, 23, '«се, Дева во чреве приимет и родит Сына, и нарекут имя Ему Еммануил, что значит: с нами Бог».');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (31863, 'rst66', 'mat', 1, 22, 'А все сие произошло, да сбудется реченное Господом через пророка, который говорит:');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (31862, 'rst66', 'mat', 1, 21, 'родит же Сына, и наречешь Ему имя Иисус, ибо Он спасет людей Своих от грехов их.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (31861, 'rst66', 'mat', 1, 20, 'Но когда он помыслил это, &mdash; се, Ангел Господень явился ему во сне и сказал: Иосиф, сын Давидов! не бойся принять Марию, жену твою, ибо родившееся в Ней есть от Духа Святого;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (31860, 'rst66', 'mat', 1, 19, 'Иосиф же, муж Ее, будучи праведен и не желая огласить Ее, хотел тайно отпустить Ее.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (31859, 'rst66', 'mat', 1, 18, 'Рождество Иисуса Христа было так: по обручении Матери Его Марии с Иосифом, прежде нежели сочетались они, оказалось, что Она имеет во чреве от Духа Святого.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (31858, 'rst66', 'mat', 1, 17, 'Итак, всех родов от Авраама до Давида четырнадцать родов; и от Давида до переселения в Вавилон четырнадцать родов; и от переселения в Вавилон до Христа четырнадцать родов.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (31857, 'rst66', 'mat', 1, 16, 'Иаков родил Иосифа, мужа Марии, от Которой родился Иисус, называемый Христос.');",
