        $list = [
            "INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (40203, 'rst66', 'rev', 22, 21, 'Благодать Господа нашего Иисуса Христа со всеми вами. Аминь.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (40202, 'rst66', 'rev', 22, 20, 'Свидетельствующий сие говорит: ей, гряду скоро! Аминь. Ей, гряди, Господи Иисусе!');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (40201, 'rst66', 'rev', 22, 19, 'и если кто отнимет что от слов книги пророчества сего, у того отнимет Бог участие в книге жизни, и в святом граде, и в том, что написано в книге сей.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (40200, 'rst66', 'rev', 22, 18, 'И я также свидетельствую всякому слышащему слова пророчества книги сей: если кто приложит что к ним, на того наложит Бог язвы, о которых написано в книге сей;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (40199, 'rst66', 'rev', 22, 17, 'И Дух и невеста говорят: прииди! И слышавший да скажет: прииди! Жаждущий пусть приходит, и желающий пусть берет воду жизни даром.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (40198, 'rst66', 'rev', 22, 16, 'Я, Иисус, послал Ангела Моего засвидетельствовать вам сие в церквах. Я есмь корень и потомок Давида, звезда светлая и утренняя.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (40197, 'rst66', 'rev', 22, 15, 'А вне &mdash; псы и чародеи, и любодеи, и убийцы, и идолослужители, и всякий любящий и делающий неправду.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (40196, 'rst66', 'rev', 22, 14, 'Блаженны те, которые соблюдают заповеди Его, чтобы иметь им право на древо жизни и войти в город воротами.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (40195, 'rst66', 'rev', 22, 13, 'Я есмь Альфа и Омега, начало и конец, Первый и Последний.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (40194, 'rst66', 'rev', 22, 12, 'Се, гряду скоро, и возмездие Мое со Мною, чтобы воздать каждому по делам его.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (40193, 'rst66', 'rev', 22, 11, 'Неправедный пусть еще делает неправду, нечистый пусть еще сквернится; праведный да творит правду еще, и святой да освящается еще.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (40192, 'rst66', 'rev', 22, 10, 'И сказал мне: не запечатывай слов пророчества книги сей; ибо время близко.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (40191, 'rst66', 'rev', 22, 9, 'но он сказал мне: смотри, не делай сего; ибо я &mdash; сослужитель тебе и братьям твоим пророкам и соблюдающим слова книги сей; Богу поклонись.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (40190, 'rst66', 'rev', 22, 8, 'Я, Иоанн, видел и слышал сие. Когда же услышал и увидел, пал к ногам Ангела, показывающего мне сие, чтобы поклониться <i>ему</i>;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (40189, 'rst66', 'rev', 22, 7, 'Се, гряду скоро: блажен соблюдающий слова пророчества книги сей.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (40188, 'rst66', 'rev', 22, 6, 'И сказал мне: сии слова верны и истинны; и Господь Бог святых пророков послал Ангела Своего показать рабам Своим то, чему надлежит быть вскоре.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (40187, 'rst66', 'rev', 22, 5, 'И ночи не будет там, и не будут иметь нужды ни в светильнике, ни в свете солнечном, ибо Господь Бог освещает их; и будут царствовать во веки веков.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (40186, 'rst66', 'rev', 22, 4, 'И узрят лицо Его, и имя Его будет на челах их.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (40185, 'rst66', 'rev', 22, 3, 'И ничего уже не будет проклятого; но престол Бога и Агнца будет в нем, и рабы Его будут служить Ему.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (40184, 'rst66', 'rev', 22, 2, 'Среди улицы его, и по ту и по другую сторону реки, древо жизни, двенадцать <i>раз</i> приносящее плоды, дающее на каждый месяц плод свой; и листья дерева &mdash; для исцеления народов.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (40183, 'rst66', 'rev', 22, 1, 'И показал мне чистую реку воды жизни, светлую, как кристалл, исходящую от престола Бога и Агнца.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (40182, 'rst66', 'rev', 21, 27, 'И не войдет в него ничто нечистое и никто преданный мерзости и лжи, а только те, которые написаны у Агнца в книге жизни.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (40181, 'rst66', 'rev', 21, 26, 'И принесут в него славу и честь народов.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (40180, 'rst66', 'rev', 21, 25, 'Ворота его не будут запираться днем; а ночи там не будет.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (40179, 'rst66', 'rev', 21, 24, 'Спасенные народы будут ходить во свете его, и цари земные принесут в него славу и честь свою.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (40178, 'rst66', 'rev', 21, 23, 'И город не имеет нужды ни в солнце, ни в луне для освещения своего, ибо слава Божия осветила его, и светильник его &mdash; Агнец.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (40177, 'rst66', 'rev', 21, 22, 'Храма же я не видел в нем, ибо Господь Бог Вседержитель &mdash; храм его и Агнец.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (40176, 'rst66', 'rev', 21, 21, 'А двенадцать ворот &mdash; двенадцать жемчужин: каждые ворота были из одной жемчужины. Улица города &mdash; чистое золото, как прозрачное стекло.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (40175, 'rst66', 'rev', 21, 20, 'пятое &mdash; сардоникс, шестое &mdash; сердолик, седьмое &mdash; хризолит, восьмое &mdash; вирилл, девятое &mdash; топаз, десятое &mdash; хризопрас, одиннадцатое &mdash; гиацинт, двенадцатое &mdash; аметист.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (40174, 'rst66', 'rev', 21, 19, 'Основания стены города украшены всякими драгоценными камнями: основание первое &mdash; яспис, второе &mdash; сапфир, третье &mdash; халкидон, четвертое &mdash; смарагд,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (40173, 'rst66', 'rev', 21, 18, 'Стена его построена из ясписа, а город был чистое золото, подобен чистому стеклу.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (40172, 'rst66', 'rev', 21, 17, 'И стену его измерил во сто сорок четыре локтя, мерою человеческою, какова мера и Ангела.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (40171, 'rst66', 'rev', 21, 16, 'Город расположен четвероугольником, и длина его такая же, как и широта. И измерил он город тростью на двенадцать тысяч стадий; длина и широта и высота его равны.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (40170, 'rst66', 'rev', 21, 15, 'Говоривший со мною имел золотую трость для измерения города и ворот его и стены&#769; его.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (40169, 'rst66', 'rev', 21, 14, 'Стена города имеет двенадцать оснований, и на них имена двенадцати Апостолов Агнца.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (40168, 'rst66', 'rev', 21, 13, 'с востока трое ворот, с севера трое ворот, с юга трое ворот, с запада трое ворот.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (40167, 'rst66', 'rev', 21, 12, 'Он имеет большую и высокую стену, имеет двенадцать ворот и на них двенадцать Ангелов; на воротах написаны имена двенадцати колен сынов Израилевых:');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (40166, 'rst66', 'rev', 21, 11, 'Он имеет славу Божию. Светило его подобно драгоценнейшему камню, как бы камню яспису кристалловидному.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (40165, 'rst66', 'rev', 21, 10, 'И вознес меня в духе на великую и высокую гору, и показал мне великий город, святой Иерусалим, который нисходил с неба от Бога.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (40164, 'rst66', 'rev', 21, 9, 'И пришел ко мне один из семи Ангелов, у которых было семь чаш, наполненных семью последними язвами, и сказал мне: пойди, я покажу тебе жену, невесту Агнца.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (40163, 'rst66', 'rev', 21, 8, 'Боязливых же и неверных, и скверных и убийц, и любодеев и чародеев, и идолослужителей и всех лжецов участь &mdash; в озере, горящем огнем и серою. Это &mdash; смерть вторая.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (40162, 'rst66', 'rev', 21, 7, 'Побеждающий наследует все, и буду ему Богом, и он будет Мне сыном.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (40161, 'rst66', 'rev', 21, 6, 'И сказал мне: совершилось! Я есмь Альфа и Омега, начало и конец; жаждущему дам даром от источника воды живой.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (40160, 'rst66', 'rev', 21, 5, 'И сказал Сидящий на престоле: се, творю все новое. И говорит мне: напиши; ибо слова сии истинны и верны.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (40159, 'rst66', 'rev', 21, 4, 'И отрет Бог всякую слезу с очей их, и смерти не будет уже; ни плача, ни вопля, ни болезни уже не будет, ибо прежнее прошло.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (40158, 'rst66', 'rev', 21, 3, 'И услышал я громкий голос с неба, говорящий: се, скиния Бога с человеками, и Он будет обитать с ними; они будут Его народом, и Сам Бог с ними будет Богом их.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (40157, 'rst66', 'rev', 21, 2, 'И я, Иоанн, увидел святой город Иерусалим, новый, сходящий от Бога с неба, приготовленный как невеста, украшенная для мужа своего.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (40156, 'rst66', 'rev', 21, 1, 'И увидел я новое небо и новую землю, ибо прежнее небо и прежняя земля миновали, и моря уже нет.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (40155, 'rst66', 'rev', 20, 15, 'И кто не был записан в книге жизни, тот был брошен в озеро огненное.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (40154, 'rst66', 'rev', 20, 14, 'И смерть и ад повержены в озеро огненное. Это &mdash; смерть вторая.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (40153, 'rst66', 'rev', 20, 13, 'Тогда отдало море мертвых, бывших в нем, и смерть и ад отдали мертвых, которые были в них; и судим был каждый по делам своим.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (40152, 'rst66', 'rev', 20, 12, 'И увидел я мертвых, малых и великих, стоящих пред Богом, и книги раскрыты были, и иная книга раскрыта, которая есть книга жизни; и судимы были мертвые по написанному в книгах, сообразно с делами своими.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (40151, 'rst66', 'rev', 20, 11, 'И увидел я великий белый престол и Сидящего на нем, от лица Которого бежало небо и земля, и не нашлось им места.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (40150, 'rst66', 'rev', 20, 10, 'а диавол, прельщавший их, ввержен в озеро огненное и серное, где зверь и лжепророк, и будут мучиться день и ночь во веки веков.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (40149, 'rst66', 'rev', 20, 9, 'И ниспал огонь с неба от Бога и пожрал их;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (40148, 'rst66', 'rev', 20, 8, 'И вышли на широту земли, и окружили стан святых и город возлюбленный.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (40147, 'rst66', 'rev', 20, 7, 'Когда же окончится тысяча лет, сатана будет освобожден из темницы своей и выйдет обольщать народы, находящиеся на четырех углах земли, Гога и Магога, и собирать их на брань; число их &mdash; как песок морской.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (40146, 'rst66', 'rev', 20, 6, 'Блажен и свят имеющий участие в воскресении первом: над ними смерть вторая не имеет власти, но они будут священниками Бога и Христа и будут царствовать с Ним тысячу лет.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (40145, 'rst66', 'rev', 20, 5, 'Прочие же из умерших не ожили, доколе не окончится тысяча лет. Это &mdash; первое воскресение.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (40144, 'rst66', 'rev', 20, 4, 'И увидел я престолы и сидящих на них, которым дано было судить, и души обезглавленных за свидетельство Иисуса и за слово Божие, которые не поклонились зверю, ни образу его и не приняли начертания на чело свое и на руку свою. Они ожили и царствовали со Христом тысячу лет.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (40143, 'rst66', 'rev', 20, 3, 'и низверг его в бездну, и заключил его, и положил над ним печать, дабы не прельщал уже народы, доколе не окончится тысяча лет; после же сего ему должно быть освобожденным на малое время.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (40142, 'rst66', 'rev', 20, 2, 'Он взял дракона, змия древнего, который есть диавол и сатана, и сковал его на тысячу лет,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (40141, 'rst66', 'rev', 20, 1, 'И увидел я Ангела, сходящего с неба, который имел ключ от бездны и большую цепь в руке своей.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (40140, 'rst66', 'rev', 19, 21, 'а прочие убиты мечом Сидящего на коне, исходящим из уст Его, и все птицы напитались их трупами.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (40139, 'rst66', 'rev', 19, 20, 'И схвачен был зверь и с ним лжепророк, производивший чудеса пред ним, которыми он обольстил принявших начертание зверя и поклоняющихся его изображению: оба живые брошены в озеро огненное, горящее серою;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (40138, 'rst66', 'rev', 19, 19, 'И увидел я зверя, и царей земных, и воинства их, собранные, чтобы сразиться с Сидящим на коне и с воинством Его.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (40137, 'rst66', 'rev', 19, 18, 'чтобы пожрать трупы царей, трупы сильных, трупы тысяченачальников, трупы коней и сидящих на них, трупы всех свободных и рабов, и малых и великих.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (40136, 'rst66', 'rev', 19, 17, 'И увидел я одного Ангела, стоящего на солнце; и он воскликнул громким голосом, говоря всем птицам, летающим посредине неба: летите, собирайтесь на великую вечерю Божию,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (40135, 'rst66', 'rev', 19, 16, 'На одежде и на бедре Его написано имя: «Царь царей и Господь господствующих».');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (40134, 'rst66', 'rev', 19, 15, 'Из уст же Его исходит острый меч, чтобы им поражать народы. Он пасет их жезлом железным; Он топчет точило вина ярости и гнева Бога Вседержителя.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (40133, 'rst66', 'rev', 19, 14, 'И воинства небесные следовали за Ним на конях белых, облеченные в виссон белый и чистый.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (40132, 'rst66', 'rev', 19, 13, '<i>Он был</i> облечен в одежду, обагренную кровью. Имя Ему: «Слово Божие».');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (40131, 'rst66', 'rev', 19, 12, 'Очи у Него как пламень огненный, и на голове Его много диадим. <i>Он</i> имел имя написанное, которого никто не знал, кроме Его Самого.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (40130, 'rst66', 'rev', 19, 11, 'И увидел я отверстое небо, и вот, конь белый, и сидящий на нем называется Верный и Истинный, Который праведно судит и воинствует.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (40129, 'rst66', 'rev', 19, 10, 'Я пал к ногам его, чтобы поклониться ему; но он сказал мне: смотри, не делай сего; я &mdash; сослужитель тебе и братьям твоим, имеющим свидетельство Иисусово; Богу поклонись, ибо свидетельство Иисусово есть дух пророчества.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (40128, 'rst66', 'rev', 19, 9, 'И сказал мне <i>Ангел:</i> напиши: блаженны званые на брачную вечерю Агнца. И сказал мне: сии суть истинные слова Божии.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (40127, 'rst66', 'rev', 19, 8, 'И дано было ей облечься в виссон чистый и светлый; виссон же есть праведность святых.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (40126, 'rst66', 'rev', 19, 7, 'Возрадуемся и возвеселимся и воздадим Ему славу; ибо наступил брак Агнца, и жена Его приготовила себя.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (40125, 'rst66', 'rev', 19, 6, 'И слышал я как бы голос многочисленного народа, как бы шум вод многих, как бы голос громов сильных, говорящих: аллилуия! ибо воцарился Господь Бог Вседержитель.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (40124, 'rst66', 'rev', 19, 5, 'И голос от престола исшел, говорящий: хвалите Бога нашего, все рабы Его и боящиеся Его, малые и великие.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (40123, 'rst66', 'rev', 19, 4, 'Тогда двадцать четыре старца и четыре животных пали и поклонились Богу, сидящему на престоле, говоря: аминь! аллилуия!');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (40122, 'rst66', 'rev', 19, 3, 'И вторично сказали: аллилуия! И дым ее восходил во веки веков.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (40121, 'rst66', 'rev', 19, 2, 'Ибо истинны и праведны суды Его: потому что Он осудил ту великую любодеицу, которая растлила землю любодейством своим, и взыскал кровь рабов Своих от руки ее.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (40120, 'rst66', 'rev', 19, 1, 'После сего я услышал на небе громкий голос как бы многочисленного народа, который говорил: аллилуия! спасение, и слава, и честь, и сила Господу нашему!');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (40119, 'rst66', 'rev', 18, 24, 'И в нем найдена кровь пророков и святых и всех убитых на земле.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (40118, 'rst66', 'rev', 18, 23, 'и свет светильника уже не появится в тебе; и го&#769;лоса жениха и невесты не будет уже слышно в тебе: ибо купцы твои были вельможи земли и волшебством твоим введены в заблуждение все народы.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (40117, 'rst66', 'rev', 18, 22, 'И го&#769;лоса играющих на гуслях, и поющих, и играющих на свирелях, и трубящих трубами в тебе уже не слышно будет; не будет уже в тебе никакого художника, никакого художества, и шума от жерновов не слышно уже будет в тебе;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (40116, 'rst66', 'rev', 18, 21, 'И один сильный Ангел взял камень, подобный большому жернову, и поверг в море, говоря: с таким стремлением повержен будет Вавилон, великий город, и уже не будет его.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (40115, 'rst66', 'rev', 18, 20, 'Веселись о сем, небо и святые Апостолы и пророки; ибо совершил Бог суд ваш над ним.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (40114, 'rst66', 'rev', 18, 19, 'И посы&#769;пали пеплом головы свои, и вопили, плача и рыдая: горе, горе <i>тебе</i>, город великий, драгоценностями которого обогатились все, имеющие корабли на море, ибо опустел в один час!');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (40113, 'rst66', 'rev', 18, 18, 'и, видя дым от пожара ее, возопили, говоря: какой город подобен городу великому!');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (40112, 'rst66', 'rev', 18, 17, 'ибо в один час погибло такое богатство! И все кормчие, и все плывущие на кораблях, и все корабельщики, и все торгующие на море стали вдали');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (40111, 'rst66', 'rev', 18, 16, 'и говоря: горе, горе <i>тебе</i>, великий город, одетый в виссон и порфиру и багряницу, украшенный золотом и камнями драгоценными и жемчугом,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (40110, 'rst66', 'rev', 18, 15, 'Торговавшие всем сим, обогатившиеся от нее, станут вдали от страха мучений ее, плача и рыдая');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (40109, 'rst66', 'rev', 18, 14, 'И плодов, угодных для души твоей, не стало у тебя, и все тучное и блистательное удалилось от тебя &mdash; ты уже не найдешь его.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (40108, 'rst66', 'rev', 18, 13, 'корицы и фимиама, и мира и ладана, и вина и елея, и муки и пшеницы, и скота и овец, и коней и колесниц, и тел и душ человеческих.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (40107, 'rst66', 'rev', 18, 12, 'товаров золотых и серебряных, и камней драгоценных и жемчуга, и виссона и порфиры, и шелка и багряницы, и всякого благовонного дерева, и всяких изделий из слоновой кости, и всяких изделий из дорогих дерев, из меди и железа и мрамора,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (40106, 'rst66', 'rev', 18, 11, 'И купцы земные восплачут и возрыдают о ней, потому что товаров их никто уже не покупает,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (40105, 'rst66', 'rev', 18, 10, 'стоя издали от страха мучений ее <i>и</i> говоря: горе, горе <i>тебе</i>, великий город Вавилон, город крепкий! ибо в один час пришел суд твой.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (40104, 'rst66', 'rev', 18, 9, 'И восплачут и возрыдают о ней цари земные, блудодействовавшие и роскошествовавшие с нею, когда увидят дым от пожара ее,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (40103, 'rst66', 'rev', 18, 8, 'За то в один день придут на нее казни, смерть, и плач, и голод, и будет сожжена огнем, потому что силен Господь Бог, судящий ее.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (40102, 'rst66', 'rev', 18, 7, 'Сколько славилась она и роскошествовала, столько воздайте ей мучений и горестей. Ибо она говорит в сердце своем: «сижу царицею, я не вдова и не увижу горести!»');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (40101, 'rst66', 'rev', 18, 6, 'Воздайте ей так, как и она воздала вам, и вдвое воздайте ей по делам ее; в чаше, в которой она приготовляла вам вино, приготовьте ей вдвое.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (40100, 'rst66', 'rev', 18, 5, 'ибо грехи ее дошли до неба, и Бог воспомянул неправды ее.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (40099, 'rst66', 'rev', 18, 4, 'И услышал я иной голос с неба, говорящий: выйди от нее, народ Мой, чтобы не участвовать вам в грехах ее и не подвергнуться язвам ее;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (40098, 'rst66', 'rev', 18, 3, 'и цари земные любодействовали с нею, и купцы земные разбогатели от великой роскоши ее.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (40097, 'rst66', 'rev', 18, 2, 'И воскликнул он сильно, громким голосом говоря: пал, пал Вавилон, великая <i>блудница</i>, сделался жилищем бесов и пристанищем всякому нечистому духу, пристанищем всякой нечистой и отвратительной птице; ибо яростным вином блудодеяния своего она напоила все народы,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (40096, 'rst66', 'rev', 18, 1, 'После сего я увидел иного Ангела, сходящего с неба и имеющего власть великую; земля осветилась от славы его.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (40095, 'rst66', 'rev', 17, 18, 'Жена же, которую ты видел, есть великий город, царствующий над земными царями.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (40094, 'rst66', 'rev', 17, 17, 'потому что Бог положил им на сердце исполнить волю Его, исполнить одну волю, и отдать царство их зверю, доколе не исполнятся слова Божии.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (40093, 'rst66', 'rev', 17, 16, 'И десять рогов, которые ты видел на звере, сии возненавидят блудницу, и разорят ее, и обнажат, и плоть ее съедят, и сожгут ее в огне;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (40092, 'rst66', 'rev', 17, 15, 'И говорит мне: во&#769;ды, которые ты видел, где сидит блудница, суть люди, и народы, и племена, и языки.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (40091, 'rst66', 'rev', 17, 14, 'Они будут вести брань с Агнцем, и Агнец победит их; ибо Он есть Господь господствующих и Царь царей, и те, которые с Ним, суть званые и избранные и верные.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (40090, 'rst66', 'rev', 17, 13, 'Они имеют одни мысли и передадут силу и власть свою зверю.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (40089, 'rst66', 'rev', 17, 12, 'И десять рогов, которые ты видел, суть десять царей, которые еще не получили царства, но примут власть со зверем, как цари, на один час.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (40088, 'rst66', 'rev', 17, 11, 'И зверь, который был и которого нет, есть восьмой, и из числа семи, и пойдет в погибель.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (40087, 'rst66', 'rev', 17, 10, 'и семь царей, из которых пять пали, один есть, а другой еще не пришел, и когда придет, недолго ему быть.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (40086, 'rst66', 'rev', 17, 9, 'Здесь ум, имеющий мудрость. Семь голов суть семь гор, на которых сидит жена,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (40085, 'rst66', 'rev', 17, 8, 'Зверь, которого ты видел, был, и нет его, и выйдет из бездны, и пойдет в погибель; и удивятся те из живущих на земле, имена которых не вписаны в книгу жизни от начала мира, видя, что зверь был, и нет его, и явится.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (40084, 'rst66', 'rev', 17, 7, 'И сказал мне Ангел: что ты дивишься? я скажу тебе тайну жены сей и зверя, носящего ее, имеющего семь голов и десять рогов.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (40083, 'rst66', 'rev', 17, 6, 'Я видел, что жена упоена была кровью святых и кровью свидетелей Иисусовых, и, видя ее, дивился удивлением великим.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (40082, 'rst66', 'rev', 17, 5, 'и на челе ее написано имя, тайна: Вавилон великий, мать блудницам и мерзостям земным.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (40081, 'rst66', 'rev', 17, 4, 'И жена облечена была в порфиру и багряницу, украшена золотом, драгоценными камнями и жемчугом, и держала золотую чашу в руке своей, наполненную мерзостями и нечистотою блудодейства ее;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (40080, 'rst66', 'rev', 17, 3, 'И повел меня в духе в пустыню; и я увидел жену, сидящую на звере багряном, преисполненном именами богохульными, с семью головами и десятью рогами.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (40079, 'rst66', 'rev', 17, 2, 'с нею блудодействовали цари земные, и вином ее блудодеяния упивались живущие на земле.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (40078, 'rst66', 'rev', 17, 1, 'И пришел один из семи Ангелов, имеющих семь чаш, и, говоря со мною, сказал мне: подойди, я покажу тебе суд над великою блудницею, сидящею на водах многих;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (40077, 'rst66', 'rev', 16, 21, 'и град, величиною в талант, пал с неба на людей; и хулили люди Бога за язвы от града, потому что язва от него была весьма тяжкая.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (40076, 'rst66', 'rev', 16, 20, 'И всякий остров убежал, и гор не стало;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (40075, 'rst66', 'rev', 16, 19, 'И город великий распался на три части, и города языческие пали, и Вавилон великий воспомянут пред Богом, чтобы дать ему чашу вина ярости гнева Его.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (40074, 'rst66', 'rev', 16, 18, 'И произошли молнии, громы и голоса, и сделалось великое землетрясение, какого не бывало с тех пор, как люди на земле. Такое землетрясение! Так великое!');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (40073, 'rst66', 'rev', 16, 17, 'Седьмой Ангел вылил чашу свою на воздух: и из храма небесного от престола раздался громкий голос, говорящий: совершилось!');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (40072, 'rst66', 'rev', 16, 16, 'И он собрал их на место, называемое по‐еврейски Армагеддон.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (40071, 'rst66', 'rev', 16, 15, 'Се, иду как тать: блажен бодрствующий и хранящий одежду свою, чтобы не ходить ему нагим и чтобы не увидели срамоты его.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (40070, 'rst66', 'rev', 16, 14, 'это &mdash; бесовские духи, творящие знамения; они выходят к царям земли всей вселенной, чтобы собрать их на брань в оный великий день Бога Вседержителя.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (40069, 'rst66', 'rev', 16, 13, 'И видел я <i>выходящих</i> из уст дракона, и из уст зверя, и из уст лжепророка трех духов нечистых, подобных жабам:');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (40068, 'rst66', 'rev', 16, 12, 'Шестой Ангел вылил чашу свою в великую реку Евфрат: и высохла в ней вода, чтобы готов был путь царям от восхода солнечного.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (40067, 'rst66', 'rev', 16, 11, 'и хулили Бога небесного от страданий своих и язв своих; и не раскаялись в делах своих.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (40066, 'rst66', 'rev', 16, 10, 'Пятый Ангел вылил чашу свою на престол зверя: и сделалось царство его мрачно, и они кусали языки свои от страдания,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (40065, 'rst66', 'rev', 16, 9, 'И жег людей сильный зной, и они хулили имя Бога, имеющего власть над сими язвами, и не вразумились, чтобы воздать Ему славу.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (40064, 'rst66', 'rev', 16, 8, 'Четвертый Ангел вылил чашу свою на солнце: и дано было ему жечь людей огнем.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (40063, 'rst66', 'rev', 16, 7, 'И услышал я другого от жертвенника говорящего: ей, Господи Боже Вседержитель, истинны и праведны суды Твои.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (40062, 'rst66', 'rev', 16, 6, 'за то, что они пролили кровь святых и пророков, Ты дал им пить кровь: они достойны того.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (40061, 'rst66', 'rev', 16, 5, 'И услышал я Ангела вод, который говорил: праведен Ты, Господи, Который еси и был, и свят, потому что так судил;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (40060, 'rst66', 'rev', 16, 4, 'Третий Ангел вылил чашу свою в реки и источники вод: и сделалась кровь.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (40059, 'rst66', 'rev', 16, 3, 'Второй Ангел вылил чашу свою в море: и сделалась кровь, как бы мертвеца, и все одушевленное умерло в море.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (40058, 'rst66', 'rev', 16, 2, 'Пошел первый Ангел и вылил чашу свою на землю: и сделались жестокие и отвратительные гнойные раны на людях, имеющих начертание зверя и поклоняющихся образу его.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (40057, 'rst66', 'rev', 16, 1, 'И услышал я из храма громкий голос, говорящий семи Ангелам: идите и вылейте семь чаш гнева Божия на землю.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (40056, 'rst66', 'rev', 15, 8, 'И наполнился храм дымом от славы Божией и от силы Его, и никто не мог войти в храм, доколе не окончились семь язв семи Ангелов.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (40055, 'rst66', 'rev', 15, 7, 'И одно из четырех животных дало семи Ангелам семь золотых чаш, наполненных гневом Бога, живущего во веки веков.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (40054, 'rst66', 'rev', 15, 6, 'И вышли из храма семь Ангелов, имеющие семь язв, облеченные в чистую и светлую льняную одежду и опоясанные по персям золотыми поясами.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (40053, 'rst66', 'rev', 15, 5, 'И после сего я взглянул, и вот, отверзся храм скинии свидетельства на небе.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (40052, 'rst66', 'rev', 15, 4, 'Кто не убоится Тебя, Господи, и не прославит имени Твоего? ибо Ты един свят. Все народы придут и поклонятся пред Тобою, ибо открылись суды Твои.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (40051, 'rst66', 'rev', 15, 3, 'и поют песнь Моисея, раба Божия, и песнь Агнца, говоря: велики и чудны дела Твои, Господи Боже Вседержитель! праведны и истинны пути Твои, Царь святых!');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (40050, 'rst66', 'rev', 15, 2, 'И видел я как бы стеклянное море, смешанное с огнем; и победившие зверя и образ его, и начертание его, и число имени его стоят на этом стеклянном море, держа гусли Божии,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (40049, 'rst66', 'rev', 15, 1, 'И увидел я иное знамение на небе, великое и чудное: семь Ангелов, имеющих семь последних язв, которыми оканчивалась ярость Божия.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (40048, 'rst66', 'rev', 14, 20, 'И истоптаны <i>ягоды</i> в точиле за городом, и потекла кровь из точила даже до узд конских, на тысячу шестьсот стадий.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (40047, 'rst66', 'rev', 14, 19, 'И поверг Ангел серп свой на землю, и обрезал виноград на земле, и бросил в великое точило гнева Божия.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (40046, 'rst66', 'rev', 14, 18, 'И иной Ангел, имеющий власть над огнем, вышел от жертвенника и с великим криком воскликнул к имеющему острый серп, говоря: пусти острый серп твой и обрежь гроздья винограда на земле, потому что созрели на нем ягоды.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (40045, 'rst66', 'rev', 14, 17, 'И другой Ангел вышел из храма, находящегося на небе, также с острым серпом.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (40044, 'rst66', 'rev', 14, 16, 'И поверг сидящий на облаке серп свой на землю, и земля была пожата.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (40043, 'rst66', 'rev', 14, 15, 'И вышел другой Ангел из храма и воскликнул громким голосом к сидящему на облаке: пусти серп твой и пожни, потому что пришло время жатвы, ибо жатва на земле созрела.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (40042, 'rst66', 'rev', 14, 14, 'И взглянул я, и вот светлое облако, и на облаке сидит подобный Сыну Человеческому; на голове его золотой венец, и в руке его острый серп.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (40041, 'rst66', 'rev', 14, 13, 'И услышал я голос с неба, говорящий мне: напиши: отныне блаженны мертвые, умирающие в Господе; ей, говорит Дух, они успокоятся от трудов своих, и дела их идут вслед за ними.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (40040, 'rst66', 'rev', 14, 12, 'Здесь терпение святых, соблюдающих заповеди Божии и веру в Иисуса.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (40039, 'rst66', 'rev', 14, 11, 'и дым мучения их будет восходить во веки веков, и не будут иметь покоя ни днем, ни ночью поклоняющиеся зверю и образу его и принимающие начертание имени его.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (40038, 'rst66', 'rev', 14, 10, 'тот будет пить вино ярости Божией, вино цельное, приготовленное в чаше гнева Его, и будет мучим в огне и сере пред святыми Ангелами и пред Агнцем;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (40037, 'rst66', 'rev', 14, 9, 'И третий Ангел последовал за ними, говоря громким голосом: кто поклоняется зверю и образу его и принимает начертание на чело свое или на руку свою,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (40036, 'rst66', 'rev', 14, 8, 'И другой Ангел следовал за ним, говоря: пал, пал Вавилон, город великий, потому что он яростным вином блуда своего напоил все народы.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (40035, 'rst66', 'rev', 14, 7, 'и говорил он громким голосом: убойтесь Бога и воздайте Ему славу, ибо наступил час суда Его, и поклонитесь Сотворившему небо, и землю, и море, и источники вод.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (40034, 'rst66', 'rev', 14, 6, 'И увидел я другого Ангела, летящего посредине неба, который имел вечное Евангелие, чтобы благовествовать живущим на земле, и всякому племени, и колену, и языку, и народу;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (40033, 'rst66', 'rev', 14, 5, 'и в устах их нет лукавства; они непорочны пред престолом Божиим.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (40032, 'rst66', 'rev', 14, 4, 'Это те, которые не осквернились с женами, ибо они девственники; это те, которые следуют за Агнцем, куда бы Он ни пошел. Они искуплены из людей, как первенцы Богу и Агнцу,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (40031, 'rst66', 'rev', 14, 3, 'Они поют как бы новую песнь пред престолом и пред четырьмя животными и старцами; и никто не мог научиться сей песни, кроме сих ста сорока четырех тысяч, искупленных от земли.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (40030, 'rst66', 'rev', 14, 2, 'И услышал я голос с неба, как шум от множества вод и как звук сильного грома; и услышал голос как бы гуслистов, играющих на гуслях своих.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (40029, 'rst66', 'rev', 14, 1, 'И взглянул я, и вот, Агнец стоит на горе Сионе, и с Ним сто сорок четыре тысячи, у которых имя Отца Его написано на челах.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (40028, 'rst66', 'rev', 13, 18, 'Здесь мудрость. Кто имеет ум, тот сочти число зверя, ибо это число человеческое; число его &mdash; шестьсот шестьдесят шесть.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (40027, 'rst66', 'rev', 13, 17, 'и что никому нельзя будет ни покупать, ни продавать, кроме того, кто имеет это начертание, или имя зверя, или число имени его.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (40026, 'rst66', 'rev', 13, 16, 'И он сделает то, что всем, малым и великим, богатым и нищим, свободным и рабам, положено будет начертание на правую руку их или на чело их,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (40025, 'rst66', 'rev', 13, 15, 'И дано ему было вложить дух в образ зверя, чтобы образ зверя и говорил и действовал так, чтобы убиваем был всякий, кто не будет поклоняться образу зверя.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (40024, 'rst66', 'rev', 13, 14, 'И чудесами, которые дано было ему творить перед зверем, он обольщает живущих на земле, говоря живущим на земле, чтобы они сделали образ зверя, который имеет рану от меча и жив.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (40023, 'rst66', 'rev', 13, 13, 'и творит великие знамения, так что и огонь низводит с неба на землю перед людьми.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (40022, 'rst66', 'rev', 13, 12, 'Он действует перед ним со всею властью первого зверя и заставляет всю землю и живущих на ней поклоняться первому зверю, у которого смертельная рана исцелела;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (40021, 'rst66', 'rev', 13, 11, 'И увидел я другого зверя, выходящего из земли; он имел два рога, подобные агнчим, и говорил как дракон.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (40020, 'rst66', 'rev', 13, 10, 'Кто ведет в плен, тот сам пойдет в плен; кто мечом убивает, тому самому&#769; надлежит быть убиту мечом. Здесь терпение и вера святых.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (40019, 'rst66', 'rev', 13, 9, 'Кто имеет ухо, да слышит.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (40018, 'rst66', 'rev', 13, 8, 'И поклонятся ему все живущие на земле, которых имена не написаны в книге жизни у Агнца, закланного от создания мира.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (40017, 'rst66', 'rev', 13, 7, 'И дано было ему вести войну со святыми и победить их; и дана была ему власть над всяким коленом, и народом, и языком, и племенем.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (40016, 'rst66', 'rev', 13, 6, 'И отверз он уста свои для хулы на Бога, чтобы хулить имя Его, и жилище Его, и живущих на небе.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (40015, 'rst66', 'rev', 13, 5, 'И даны были ему уста, говорящие гордо и богохульно, и дана ему власть действовать сорок два месяца.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (40014, 'rst66', 'rev', 13, 4, 'и поклонились зверю, говоря: кто подобен зверю сему? и кто может сразиться с ним?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (40013, 'rst66', 'rev', 13, 3, 'И видел я, что одна из голов его как бы смертельно была ранена, но эта смертельная рана исцелела. И дивилась вся земля, следя за зверем, и поклонились дракону, который дал власть зверю,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (40012, 'rst66', 'rev', 13, 2, 'Зверь, которого я видел, был подобен барсу; ноги у него &mdash; как у медведя, а пасть у него &mdash; как пасть у льва; и дал ему дракон силу свою и престол свой и великую власть.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (40011, 'rst66', 'rev', 13, 1, 'И стал я на песке морском, и увидел выходящего из моря зверя с семью головами и десятью рогами: на рогах его было десять диадим, а на головах его &mdash; имена богохульные.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (40010, 'rst66', 'rev', 12, 17, 'И рассвирепел дракон на жену, и пошел, чтобы вступить в брань с прочими от семени ее, сохраняющими заповеди Божии и имеющими свидетельство Иисуса Христа.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (40009, 'rst66', 'rev', 12, 16, 'Но земля помогла жене, и разверзла земля уста свои и поглотила реку, которую пустил дракон из пасти своей.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (40008, 'rst66', 'rev', 12, 15, 'И пустил змий из пасти своей вслед жены воду, как реку, дабы увлечь ее рекою.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (40007, 'rst66', 'rev', 12, 14, 'И даны были жене два крыла большого орла, чтобы она летела в пустыню, в свое место, от лица змия и там питалась в продолжение времени, времен и полвремени.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (40006, 'rst66', 'rev', 12, 13, 'Когда же дракон увидел, что низвержен на землю, начал преследовать жену, которая родила младенца мужеского пола.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (40005, 'rst66', 'rev', 12, 12, 'Итак, веселитесь, небеса и обитающие на них! Горе живущим на земле и на море! потому что к вам сошел диавол в сильной ярости, зная, что немного ему остается времени.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (40004, 'rst66', 'rev', 12, 11, 'Они победили его кровию Агнца и словом свидетельства своего, и не возлюбили души своей даже до смерти.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (40003, 'rst66', 'rev', 12, 10, 'И услышал я громкий голос, говорящий на небе: ныне настало спасение и сила и царство Бога нашего и власть Христа Его, потому что низвержен клеветник братий наших, клеветавший на них пред Богом нашим день и ночь.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (40002, 'rst66', 'rev', 12, 9, 'И низвержен был великий дракон, древний змий, называемый диаволом и сатаною, обольщающий всю вселенную, низвержен на землю, и ангелы его низвержены с ним.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (40001, 'rst66', 'rev', 12, 8, 'но не устояли, и не нашлось уже для них места на небе.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (40000, 'rst66', 'rev', 12, 7, 'И произошла на небе война: Михаил и Ангелы его воевали против дракона, и дракон и ангелы его воевали <i>против них</i>,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39999, 'rst66', 'rev', 12, 6, 'А жена убежала в пустыню, где приготовлено было для нее место от Бога, чтобы питали ее там тысячу двести шестьдесят дней.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39998, 'rst66', 'rev', 12, 5, 'И родила она младенца мужеского пола, которому надлежит пасти все народы жезлом железным; и восхищено было дитя ее к Богу и престолу Его.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39997, 'rst66', 'rev', 12, 4, 'Хвост его увлек с неба третью часть звезд и поверг их на землю. Дракон сей стал перед женою, которой надлежало родить, дабы, когда она родит, пожрать ее младенца.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39996, 'rst66', 'rev', 12, 3, 'И другое знамение явилось на небе: вот, большой красный дракон с семью головами и десятью рогами, и на головах его семь диадим.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39995, 'rst66', 'rev', 12, 2, 'Она имела во чреве и кричала от болей и мук рождения.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39994, 'rst66', 'rev', 12, 1, 'И явилось на небе великое знамение: жена, облеченная в солнце; под ногами ее луна, и на главе ее венец из двенадцати звезд.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39993, 'rst66', 'rev', 11, 19, 'И отверзся храм Божий на небе, и явился ковчег завета Его в храме Его; и произошли молнии, и голоса, и громы, и землетрясение, и великий град.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39992, 'rst66', 'rev', 11, 18, 'И рассвирепели язычники; и пришел гнев Твой и время судить мертвых и дать возмездие рабам Твоим, пророкам и святым и боящимся имени Твоего, малым и великим, и погубить губивших землю.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39991, 'rst66', 'rev', 11, 17, 'говоря: благодарим Тебя, Господи Боже Вседержитель, Который еси и был и грядешь, что Ты приял силу Твою великую и воцарился.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39990, 'rst66', 'rev', 11, 16, 'И двадцать четыре старца, сидящие пред Богом на престолах своих, пали на лица свои и поклонились Богу,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39989, 'rst66', 'rev', 11, 15, 'И седьмой Ангел вострубил, и раздались на небе громкие голоса, говорящие: царство мира соделалось <i>царством</i> Господа нашего и Христа Его и будет царствовать во веки веков.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39988, 'rst66', 'rev', 11, 14, 'Второе горе прошло; вот, идет скоро третье горе.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39987, 'rst66', 'rev', 11, 13, 'И в тот же час произошло великое землетрясение, и десятая часть города пала, и погибло при землетрясении семь тысяч имен человеческих; и прочие объяты были страхом и воздали славу Богу небесному.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39986, 'rst66', 'rev', 11, 12, 'И услышали они с неба громкий голос, говоривший им: взойдите сюда. И они взошли на небо на облаке; и смотрели на них враги их.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39985, 'rst66', 'rev', 11, 11, 'Но после трех дней с половиною вошел в них дух жизни от Бога, и они оба стали на ноги свои; и великий страх напал на тех, которые смотрели на них.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39984, 'rst66', 'rev', 11, 10, 'И живущие на земле будут радоваться сему и веселиться, и пошлют дары друг другу, потому что два пророка сии мучили живущих на земле.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39983, 'rst66', 'rev', 11, 9, 'И <i>многие</i> из народов, и колен, и языков, и племен будут смотреть на трупы их три дня с половиною, и не позволят положить трупы их во гробы.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39982, 'rst66', 'rev', 11, 8, 'и трупы их оставит на улице великого города, который духовно называется Содом и Египет, где и Господь наш распят.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39981, 'rst66', 'rev', 11, 7, 'И когда кончат они свидетельство свое, зверь, выходящий из бездны, сразится с ними, и победит их, и убьет их,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39980, 'rst66', 'rev', 11, 6, 'Они имеют власть затворить небо, чтобы не шел дождь на землю во дни пророчествования их, и имеют власть над водами превращать их в кровь, и поражать землю всякою язвою, когда только захотят.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39979, 'rst66', 'rev', 11, 5, 'И если кто захочет их обидеть, то огонь выйдет из уст их и пожрет врагов их; если кто захочет их обидеть, тому надлежит быть убиту.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39978, 'rst66', 'rev', 11, 4, 'Это суть две маслины и два светильника, стоящие пред Богом земли.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39977, 'rst66', 'rev', 11, 3, 'И дам двум свидетелям Моим, и они будут пророчествовать тысячу двести шестьдесят дней, будучи облечены во вретище.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39976, 'rst66', 'rev', 11, 2, 'а внешний двор храма исключи и не измеряй его, ибо он дан язычникам: они будут попирать святой город сорок два месяца.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39975, 'rst66', 'rev', 11, 1, 'И дана мне трость, подобная жезлу, и сказано: встань и измерь храм Божий, и жертвенник, и поклоняющихся в нем,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39974, 'rst66', 'rev', 10, 11, 'И сказал он мне: тебе надлежит опять пророчествовать о народах, и племенах, и языках, и царях многих.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39973, 'rst66', 'rev', 10, 10, 'И взял я книжку из руки Ангела и съел ее; и она в устах моих была сладка, как мед; когда же съел ее, то горько стало во чреве моем.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39972, 'rst66', 'rev', 10, 9, 'И я пошел к Ангелу и сказал ему: дай мне книжку. Он сказал мне: возьми и съешь ее; она будет горька во чреве твоем, но в устах твоих будет сладка, как мед.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39971, 'rst66', 'rev', 10, 8, 'И голос, который я слышал с неба, опять стал говорить со мною и сказал: пойди, возьми раскрытую книжку из руки Ангела, стоящего на море и на земле.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39970, 'rst66', 'rev', 10, 7, 'но в те дни, когда возгласит седьмой Ангел, когда он вострубит, совершится тайна Божия, как Он благовествовал рабам Своим пророкам.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39969, 'rst66', 'rev', 10, 6, 'и клялся Живущим во веки веков, Который сотворил небо и все, что на нем, землю и все, что на ней, и море и все, что в нем, что времени уже не будет;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39968, 'rst66', 'rev', 10, 5, 'И Ангел, которого я видел стоящим на море и на земле, поднял руку свою к небу');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39967, 'rst66', 'rev', 10, 4, 'И когда семь громов проговорили голосами своими, я хотел было писать; но услышал голос с неба, говорящий мне: скрой, что говорили семь громов, и не пиши сего.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39966, 'rst66', 'rev', 10, 3, 'и воскликнул громким голосом, как рыкает лев; и когда он воскликнул, тогда семь громов проговорили голосами своими.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39965, 'rst66', 'rev', 10, 2, 'в руке у него была книжка раскрытая. И поставил он правую ногу свою на море, а левую на землю,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39964, 'rst66', 'rev', 10, 1, 'И видел я другого Ангела сильного, сходящего с неба, облеченного облаком; над головою его была радуга, и лицо его как солнце, и ноги его как столпы огненные,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39963, 'rst66', 'rev', 9, 21, 'И не раскаялись они в убийствах своих, ни в чародействах своих, ни в блудодеянии своем, ни в воровстве своем.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39962, 'rst66', 'rev', 9, 20, 'Прочие же люди, которые не умерли от этих язв, не раскаялись в делах рук своих, так чтобы не поклоняться бесам и золотым, серебряным, медным, каменным и деревянным идолам, которые не могут ни видеть, ни слышать, ни ходить.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39961, 'rst66', 'rev', 9, 19, 'ибо сила коней заключалась во рту их и в хвостах их; а хвосты их были подобны змеям и имели головы, и ими они вредили.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39960, 'rst66', 'rev', 9, 18, 'От этих трех язв &mdash; от огня, дыма и серы, выходящих изо рта их, &mdash; умерла третья часть людей;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39959, 'rst66', 'rev', 9, 17, 'Так видел я в видении коней и на них всадников, которые имели на себе брони огненные, гиацинтовые и серные; головы у коней &mdash; как головы у львов, и изо рта их выходил огонь, дым и сера.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39958, 'rst66', 'rev', 9, 16, 'Число конного войска было две тьмы тем; и я слышал число его.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39957, 'rst66', 'rev', 9, 15, 'И освобождены были четыре Ангела, приготовленные на час и день, и месяц и год, для того, чтобы умертвить третью часть людей.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39956, 'rst66', 'rev', 9, 14, 'говоривший шестому Ангелу, имевшему трубу: освободи четырех Ангелов, связанных при великой реке Евфрате.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39955, 'rst66', 'rev', 9, 13, 'Шестой Ангел вострубил, и я услышал один голос от четырех рогов золотого жертвенника, стоящего пред Богом,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39954, 'rst66', 'rev', 9, 12, 'Одно горе прошло; вот, идут за ним еще два горя.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39953, 'rst66', 'rev', 9, 11, 'Царем над собою она имела ангела бездны; имя ему по‐еврейски Аваддон, а по‐гречески Аполлион.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39952, 'rst66', 'rev', 9, 10, 'у ней были хвосты, как у скорпионов, и в хвостах ее были жала; власть же ее была &mdash; вредить людям пять месяцев.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39951, 'rst66', 'rev', 9, 9, 'На ней были брони, как бы брони железные, а шум от крыльев ее &mdash; как стук от колесниц, когда множество коней бежит на войну;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39950, 'rst66', 'rev', 9, 8, 'и волосы у ней &mdash; как волосы у женщин, а зубы у ней были как у львов.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39949, 'rst66', 'rev', 9, 7, 'По виду своему саранча была подобна коням, приготовленным на войну; и на головах у ней &mdash; как бы венцы, похожие на золотые, лица же ее &mdash; как лица человеческие;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39948, 'rst66', 'rev', 9, 6, 'В те дни люди будут искать смерти, но не найдут ее; пожелают умереть, но смерть убежит от них.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39947, 'rst66', 'rev', 9, 5, 'И дано ей не убивать их, а только мучить пять месяцев; и мучение от нее подобно мучению от скорпиона, когда ужалит человека.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39946, 'rst66', 'rev', 9, 4, 'И сказано было ей, чтобы не делала вреда траве земной, и никакой зелени, и никакому дереву, а только одним людям, которые не имеют печати Божией на челах своих.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39945, 'rst66', 'rev', 9, 3, 'И из дыма вышла саранча на землю, и дана была ей власть, какую имеют земные скорпионы.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39944, 'rst66', 'rev', 9, 2, 'Она отворила кладязь бездны, и вышел дым из кладязя, как дым из большой печи; и помрачилось солнце и воздух от дыма из кладязя.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39943, 'rst66', 'rev', 9, 1, 'Пятый Ангел вострубил, и я увидел звезду, падшую с неба на землю, и дан был ей ключ от кладязя бездны.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39942, 'rst66', 'rev', 8, 13, 'И видел я и слышал одного Ангела, летящего посреди неба и говорящего громким голосом: горе, горе, горе живущим на земле от остальных трубных голосов трех Ангелов, которые будут трубить!');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39941, 'rst66', 'rev', 8, 12, 'Четвертый Ангел вострубил, и поражена была третья часть солнца и третья часть луны и третья часть звезд, так что затмилась третья часть их, и третья часть дня не светла была &mdash; так, как и ночи.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39940, 'rst66', 'rev', 8, 11, 'Имя сей звезде «полынь»; и третья часть вод сделалась полынью, и многие из людей умерли от вод, потому что они стали горьки.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39939, 'rst66', 'rev', 8, 10, 'Третий Ангел вострубил, и упала с неба большая звезда, горящая подобно светильнику, и пала на третью часть рек и на источники вод.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39938, 'rst66', 'rev', 8, 9, 'и умерла третья часть одушевленных тварей, живущих в море, и третья часть судов погибла.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39937, 'rst66', 'rev', 8, 8, 'Второй Ангел вострубил, и как бы большая гора, пылающая огнем, низверглась в море; и третья часть моря сделалась кровью,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39936, 'rst66', 'rev', 8, 7, 'Первый Ангел вострубил, и сделались град и огонь, смешанные с кровью, и пали на землю; и третья часть дерев сгорела, и вся трава зеленая сгорела.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39935, 'rst66', 'rev', 8, 6, 'И семь Ангелов, имеющие семь труб, приготовились трубить.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39934, 'rst66', 'rev', 8, 5, 'И взял Ангел кадильницу, и наполнил ее огнем с жертвенника, и поверг на землю: и произошли голоса, и громы, и молнии, и землетрясение.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39933, 'rst66', 'rev', 8, 4, 'И вознесся дым фимиама с молитвами святых от руки Ангела пред Бога.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39932, 'rst66', 'rev', 8, 3, 'И пришел иной Ангел, и стал перед жертвенником, держа золотую кадильницу; и дано было ему множество фимиама, чтобы он с молитвами всех святых возложил его на золотой жертвенник, который перед престолом.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39931, 'rst66', 'rev', 8, 2, 'И я видел семь Ангелов, которые стояли пред Богом; и дано им семь труб.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39930, 'rst66', 'rev', 8, 1, 'И когда Он снял седьмую печать, сделалось безмолвие на небе, как бы на полчаса.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39929, 'rst66', 'rev', 7, 17, 'ибо Агнец, Который среди престола, будет пасти их и водить их на живые источники вод; и отрет Бог всякую слезу с очей их.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39928, 'rst66', 'rev', 7, 16, 'Они не будут уже ни алкать, ни жаждать, и не будет палить их солнце и никакой зной:');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39927, 'rst66', 'rev', 7, 15, 'За это они пребывают <i>ныне</i> перед престолом Бога и служат Ему день и ночь в храме Его, и Сидящий на престоле будет обитать в них.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39926, 'rst66', 'rev', 7, 14, 'Я сказал ему: ты знаешь, господин. И он сказал мне: это те, которые пришли от великой скорби; они омыли одежды свои и убелили одежды свои Кровию Агнца.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39925, 'rst66', 'rev', 7, 13, 'И, начав речь, один из старцев спросил меня: сии облеченные в белые одежды кто и откуда пришли?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39924, 'rst66', 'rev', 7, 12, 'говоря: аминь! благословение и слава, и премудрость и благодарение, и честь и сила и крепость Богу нашему во веки веков! Аминь.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39923, 'rst66', 'rev', 7, 11, 'И все Ангелы стояли вокруг престола, и старцев, и четырех животных, и пали перед престолом на лица свои, и поклонились Богу,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39922, 'rst66', 'rev', 7, 10, 'И восклицали громким голосом, говоря: спасение Богу нашему, сидящему на престоле, и Агнцу!');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39921, 'rst66', 'rev', 7, 9, 'После сего взглянул я, и вот, великое множество людей, которого никто не мог перечесть, из всех племен, и колен, и народов, и языков, стояло пред престолом и пред Агнцем в белых одеждах и с пальмовыми ветвями в руках своих.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39920, 'rst66', 'rev', 7, 8, 'из колена Завулонова запечатлено двенадцать тысяч; из колена Иосифова запечатлено двенадцать тысяч; из колена Вениаминова запечатлено двенадцать тысяч.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39919, 'rst66', 'rev', 7, 7, 'из колена Симеонова запечатлено двенадцать тысяч; из колена Левиина запечатлено двенадцать тысяч; из колена Иссахарова запечатлено двенадцать тысяч;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39918, 'rst66', 'rev', 7, 6, 'из колена Асирова запечатлено двенадцать тысяч; из колена Неффалимова запечатлено двенадцать тысяч; из колена Манассиина запечатлено двенадцать тысяч;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39917, 'rst66', 'rev', 7, 5, 'Из колена Иудина запечатлено двенадцать тысяч; из колена Рувимова запечатлено двенадцать тысяч; из колена Гадова запечатлено двенадцать тысяч;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39916, 'rst66', 'rev', 7, 4, 'И я слышал число запечатленных: запечатленных было сто сорок четыре тысячи из всех колен сынов Израилевых.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39915, 'rst66', 'rev', 7, 3, 'не делайте вреда ни земле, ни морю, ни деревам, доколе не положим печати на челах рабов Бога нашего.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39914, 'rst66', 'rev', 7, 2, 'И видел я иного Ангела, восходящего от востока солнца и имеющего печать Бога живого. И воскликнул он громким голосом к четырем Ангелам, которым дано вредить земле и морю, говоря:');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39913, 'rst66', 'rev', 7, 1, 'И после сего видел я четырех Ангелов, стоящих на четырех углах земли, держащих четыре ветра земли, чтобы не дул ветер ни на землю, ни на море, ни на какое дерево.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39912, 'rst66', 'rev', 6, 17, 'ибо пришел великий день гнева Его, и кто может устоять?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39911, 'rst66', 'rev', 6, 16, 'и говорят горам и камням: падите на нас и сокройте нас от лица Сидящего на престоле и от гнева Агнца;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39910, 'rst66', 'rev', 6, 15, 'И цари земные, и вельможи, и богатые, и тысяченачальники, и сильные, и всякий раб, и всякий свободный скрылись в пещеры и в ущелья гор,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39909, 'rst66', 'rev', 6, 14, 'И небо скрылось, свившись как свиток; и всякая гора и остров двинулись с мест своих.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39908, 'rst66', 'rev', 6, 13, 'И звезды небесные пали на землю, как смоковница, потрясаемая сильным ветром, роняет незрелые смоквы свои.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39907, 'rst66', 'rev', 6, 12, 'И когда Он снял шестую печать, я взглянул, и вот, произошло великое землетрясение, и солнце стало мрачно как власяница, и луна сделалась как кровь.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39906, 'rst66', 'rev', 6, 11, 'И даны были каждому из них одежды белые, и сказано им, чтобы они успокоились еще на малое время, пока и сотрудники их и братья их, которые будут убиты, как и они, дополнят число.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39905, 'rst66', 'rev', 6, 10, 'И возопили они громким голосом, говоря: доколе, Владыка Святой и Истинный, не судишь и не мстишь живущим на земле за кровь нашу?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39904, 'rst66', 'rev', 6, 9, 'И когда Он снял пятую печать, я увидел под жертвенником души убиенных за слово Божие и за свидетельство, которое они имели.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39903, 'rst66', 'rev', 6, 8, 'И я взглянул, и вот, конь бледный, и на нем всадник, которому имя «смерть»; и ад следовал за ним; и дана ему власть над четвертою частью земли &mdash; умерщвлять мечом, и голодом, и мором, и зверями земными.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39902, 'rst66', 'rev', 6, 7, 'И когда Он снял четвертую печать, я слышал голос четвертого животного, говорящий: иди и смотри.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39901, 'rst66', 'rev', 6, 6, 'И слышал я голос посреди четырех животных, говорящий: хиникс пшеницы за динарий, и три хиникса ячменя за динарий; елея же и вина не повреждай.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39900, 'rst66', 'rev', 6, 5, 'И когда Он снял третью печать, я слышал третье животное, говорящее: иди и смотри. Я взглянул, и вот, конь вороной, и на нем всадник, имеющий меру в руке своей.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39899, 'rst66', 'rev', 6, 4, 'И вышел другой конь, рыжий; и сидящему на нем дано взять мир с земли, и чтобы убивали друг друга; и дан ему большой меч.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39898, 'rst66', 'rev', 6, 3, 'И когда Он снял вторую печать, я слышал второе животное, говорящее: иди и смотри.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39897, 'rst66', 'rev', 6, 2, 'Я взглянул, и вот, конь белый, и на нем всадник, имеющий лук, и дан был ему венец; и вышел он <i>как</i> победоносный и чтобы победить.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39896, 'rst66', 'rev', 6, 1, 'И я видел, что Агнец снял первую из семи печатей, и я услышал одно из четырех животных, говорящее как бы громовым голосом: иди и смотри.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39895, 'rst66', 'rev', 5, 14, 'И четыре животных говорили: аминь. И двадцать четыре старца пали и поклонились Живущему во веки веков.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39894, 'rst66', 'rev', 5, 13, 'И всякое создание, находящееся на небе и на земле, и под землею, и на море, и все, что в них, слышал я, говорило: Сидящему на престоле и Агнцу &mdash; благословение и честь, и слава и держава во веки веков.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39893, 'rst66', 'rev', 5, 12, 'которые говорили громким голосом: достоин Агнец закланный принять силу и богатство, и премудрость и крепость, и честь и славу и благословение.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39892, 'rst66', 'rev', 5, 11, 'И я видел и слышал голос многих Ангелов вокруг престола и животных и старцев, и число их было тьмы тем и тысячи тысяч,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39891, 'rst66', 'rev', 5, 10, 'и соделал нас царями и священниками Богу нашему; и мы будем царствовать на земле.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39890, 'rst66', 'rev', 5, 9, 'И поют новую песнь, говоря: достоин Ты взять книгу и снять с нее печати, ибо Ты был заклан, и Кровию Своею искупил нас Богу из всякого колена и языка, и народа, и племени,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39889, 'rst66', 'rev', 5, 8, 'И когда Он взял книгу, тогда четыре животных и двадцать четыре старца пали пред Агнцем, имея каждый гусли и золотые чаши, полные фимиама, которые суть молитвы святых.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39888, 'rst66', 'rev', 5, 7, 'И Он пришел и взял книгу из десницы Сидящего на престоле.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39887, 'rst66', 'rev', 5, 6, 'И я взглянул, и вот, посреди престола и четырех животных и посреди старцев стоял Агнец как бы закланный, имеющий семь рогов и семь очей, которые суть семь духов Божиих, посланных во всю землю.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39886, 'rst66', 'rev', 5, 5, 'И один из старцев сказал мне: не плачь; вот, лев от колена Иудина, корень Давидов, победил <i>и может</i> раскрыть сию книгу и снять семь печатей ее.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39885, 'rst66', 'rev', 5, 4, 'И я много плакал о том, что никого не нашлось достойного раскрыть и читать сию книгу, и даже посмотреть в нее.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39884, 'rst66', 'rev', 5, 3, 'И никто не мог, ни на небе, ни на земле, ни под землею, раскрыть сию книгу, ни посмотреть в нее.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39883, 'rst66', 'rev', 5, 2, 'И видел я Ангела сильного, провозглашающего громким голосом: кто достоин раскрыть сию книгу и снять печати ее?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39882, 'rst66', 'rev', 5, 1, 'И видел я в деснице у Сидящего на престоле книгу, написанную внутри и отвне, запечатанную семью печатями.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39881, 'rst66', 'rev', 4, 11, 'достоин Ты, Господи, приять славу, и честь, и силу: ибо Ты сотворил все, и <i>все</i> по Твоей воле существует и сотворено.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39880, 'rst66', 'rev', 4, 10, 'тогда двадцать четыре старца падают пред Сидящим на престоле, и поклоняются Живущему во веки веков, и полагают венцы свои перед престолом, говоря:');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39879, 'rst66', 'rev', 4, 9, 'И когда животные воздают славу, и честь, и благодарение Сидящему на престоле, Живущему во веки веков,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39878, 'rst66', 'rev', 4, 8, 'И каждое из четырех животных имело по шести крыл вокруг, а внутри они исполнены очей; и ни днем, ни ночью не имеют покоя, взывая: свят, свят, свят Господь Бог Вседержитель, Который был, есть и грядет.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39877, 'rst66', 'rev', 4, 7, 'И первое животное было подобно льву, и второе животное подобно тельцу, и третье животное имело лицо, как человек, и четвертое животное подобно орлу летящему.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39876, 'rst66', 'rev', 4, 6, 'и перед престолом &mdash; море стеклянное, подобное кристаллу; и посреди престола и вокруг престола &mdash; четыре животных, исполненных очей спереди и сзади.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39875, 'rst66', 'rev', 4, 5, 'И от престола исходили молнии и громы и гласы, и семь светильников огненных горели перед престолом, которые суть семь духов Божиих;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39874, 'rst66', 'rev', 4, 4, 'И вокруг престола двадцать четыре престола; а на престолах видел я сидевших двадцать четыре старца, которые облечены были в белые одежды и имели на головах своих золотые венцы.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39873, 'rst66', 'rev', 4, 3, 'и Сей Сидящий видом был подобен камню яспису и сардису; и радуга вокруг престола, видом подобная смарагду.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39872, 'rst66', 'rev', 4, 2, 'И тотчас я был в духе; и вот, престол стоял на небе, и на престоле был Сидящий;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39871, 'rst66', 'rev', 4, 1, 'После сего я взглянул, и вот, дверь отверста на небе, и прежний голос, который я слышал как бы звук трубы, говоривший со мною, сказал: взойди сюда, и покажу тебе, чему надлежит быть после сего.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39870, 'rst66', 'rev', 3, 22, 'Имеющий ухо да слышит, что&#769; Дух говорит церквам.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39869, 'rst66', 'rev', 3, 21, 'Побеждающему дам сесть со Мною на престоле Моем, как и Я победил и сел с Отцом Моим на престоле Его.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39868, 'rst66', 'rev', 3, 20, 'Се, стою у двери и стучу: если кто услышит голос Мой и отворит дверь, войду к нему и буду вечерять с ним, и он со Мною.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39867, 'rst66', 'rev', 3, 19, 'Кого Я люблю, тех обличаю и наказываю. Итак, будь ревностен и покайся.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39866, 'rst66', 'rev', 3, 18, 'Советую тебе купить у Меня золото, огнем очищенное, чтобы тебе обогатиться, и белую одежду, чтобы одеться и чтобы не видна была срамота наготы твоей, и глазною мазью помажь глаза твои, чтобы видеть.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39865, 'rst66', 'rev', 3, 17, 'Ибо ты говоришь: «я богат, разбогател и ни в чем не имею нужды»; а не знаешь, что ты несчастен, и жалок, и нищ, и слеп, и наг.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39864, 'rst66', 'rev', 3, 16, 'Но, как ты тепл, а не горяч и не холоден, то извергну тебя из уст Моих.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39863, 'rst66', 'rev', 3, 15, 'знаю твои дела; ты ни холоден, ни горяч; о, если бы ты был холоден или горяч!');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39862, 'rst66', 'rev', 3, 14, 'И Ангелу Лаодикийской церкви напиши: так говорит Аминь, свидетель верный и истинный, начало создания Божия:');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39861, 'rst66', 'rev', 3, 13, 'Имеющий ухо да слышит, что&#769; Дух говорит церквам.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39860, 'rst66', 'rev', 3, 12, 'Побеждающего сделаю столпом в храме Бога Моего, и он уже не выйдет вон; и напишу на нем имя Бога Моего и имя града Бога Моего, нового Иерусалима, нисходящего с неба от Бога Моего, и имя Мое новое.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39859, 'rst66', 'rev', 3, 11, 'Се, гряду скоро; держи, что имеешь, дабы кто не восхитил венца твоего.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39858, 'rst66', 'rev', 3, 10, 'И как ты сохранил слово терпения Моего, то и Я сохраню тебя от годины искушения, которая придет на всю вселенную, чтобы испытать живущих на земле.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39857, 'rst66', 'rev', 3, 9, 'Вот, Я сделаю, что из сатанинского сборища, из тех, которые говорят о себе, что они Иудеи, но не суть таковы, а лгут, &mdash; вот, Я сделаю то, что они придут и поклонятся пред ногами твоими, и познают, что Я возлюбил тебя.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39856, 'rst66', 'rev', 3, 8, 'знаю твои дела; вот, Я отворил перед тобою дверь, и никто не может затворить ее; ты не много имеешь силы, и сохранил слово Мое, и не отрекся имени Моего.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39855, 'rst66', 'rev', 3, 7, 'И Ангелу Филадельфийской церкви напиши: так говорит Святой, Истинный, имеющий ключ Давидов, Который отворяет &mdash; и никто не затворит, затворяет &mdash; и никто не отворит:');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39854, 'rst66', 'rev', 3, 6, 'Имеющий ухо да слышит, что&#769; Дух говорит церквам.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39853, 'rst66', 'rev', 3, 5, 'Побеждающий облечется в белые одежды; и не изглажу имени его из книги жизни, и исповедаю имя его пред Отцом Моим и пред Ангелами Его.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39852, 'rst66', 'rev', 3, 4, 'Впрочем, у тебя в Сардисе есть несколько человек, которые не осквернили одежд своих, и будут ходить со Мною в белых <i>одеждах</i>, ибо они достойны.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39851, 'rst66', 'rev', 3, 3, 'Вспомни, что&#769; ты принял и слышал, и храни и покайся. Если же не будешь бодрствовать, то Я найду на тебя, как тать, и ты не узнаешь, в который час найду на тебя.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39850, 'rst66', 'rev', 3, 2, 'Бодрствуй и утверждай прочее близкое к смерти; ибо Я не нахожу, чтобы дела твои были совершенны пред Богом Моим.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39849, 'rst66', 'rev', 3, 1, 'И Ангелу Сардийской церкви напиши: так говорит Имеющий семь духов Божиих и семь звезд: знаю твои дела; ты носишь имя, будто жив, но ты мертв.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39848, 'rst66', 'rev', 2, 29, 'Имеющий ухо [слышать] да слышит, что&#769; Дух говорит церквам.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39847, 'rst66', 'rev', 2, 28, 'и дам ему звезду утреннюю.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39846, 'rst66', 'rev', 2, 27, 'и будет пасти их жезлом железным; как сосуды глиняные, они сокрушатся, как и Я получил <i>власть</i> от Отца Моего;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39845, 'rst66', 'rev', 2, 26, 'Кто побеждает и соблюдает дела Мои до конца, тому дам власть над язычниками,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39844, 'rst66', 'rev', 2, 25, 'только то, что имеете, держи&#769;те, пока приду.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39843, 'rst66', 'rev', 2, 24, 'Вам же и прочим, находящимся в Фиатире, которые не держат сего учения и которые не знают так называемых глубин сатанинских, сказываю, что не наложу на вас иного бремени;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39842, 'rst66', 'rev', 2, 23, 'и детей ее поражу смертью; и уразумеют все церкви, что Я есмь испытующий сердца и внутренности; и воздам каждому из вас по делам вашим.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39841, 'rst66', 'rev', 2, 22, 'Вот, Я повергаю ее на одр и любодействующих с нею в великую скорбь, если не покаются в делах своих,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39840, 'rst66', 'rev', 2, 21, 'Я дал ей время покаяться в любодеянии ее, но она не покаялась.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39839, 'rst66', 'rev', 2, 20, 'Но имею немного против тебя, потому что ты попускаешь жене Иезавели, называющей себя пророчицею, учить и вводить в заблуждение рабов Моих, любодействовать и есть идоложертвенное.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39838, 'rst66', 'rev', 2, 19, 'знаю твои дела, и любовь, и служение, и веру, и терпение твое, и то, что последние дела твои больше первых.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39837, 'rst66', 'rev', 2, 18, 'И Ангелу Фиатирской церкви напиши: так говорит Сын Божий, у Которого очи, как пламень огненный, и ноги подобны халколивану:');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39836, 'rst66', 'rev', 2, 17, 'Имеющий ухо [слышать] да слышит, что&#769; Дух говорит церквам: побеждающему дам вкушать сокровенную манну, и дам ему белый камень и на камне написанное новое имя, которого никто не знает, кроме того, кто получает.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39835, 'rst66', 'rev', 2, 16, 'Покайся; а если не так, скоро приду к тебе и сражусь с ними мечом уст Моих.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39834, 'rst66', 'rev', 2, 15, 'Так и у тебя есть держащиеся учения Николаитов, которое Я ненавижу.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39833, 'rst66', 'rev', 2, 14, 'Но имею немного против тебя, потому что есть у тебя там держащиеся учения Валаама, который научил Валака ввести в соблазн сынов Израилевых, чтобы они ели идоложертвенное и любодействовали.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39832, 'rst66', 'rev', 2, 13, 'знаю твои дела, и что ты живешь там, где престол сатаны, и что содержишь имя Мое, и не отрекся от веры Моей даже в те дни, в которые у вас, где живет сатана, умерщвлен верный свидетель Мой Антипа.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39831, 'rst66', 'rev', 2, 12, 'И Ангелу Пергамской церкви напиши: так говорит Имеющий острый с обеих сторон меч:');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39830, 'rst66', 'rev', 2, 11, 'Имеющий ухо [слышать] да слышит, что&#769; Дух говорит церквам: побеждающий не потерпит вреда от второй смерти.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39829, 'rst66', 'rev', 2, 10, 'Не бойся ничего, что тебе надобно будет претерпеть. Вот, диавол будет ввергать из среды вас в темницу, чтобы искусить вас, и будете иметь скорбь дней десять. Будь верен до смерти, и дам тебе венец жизни.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39828, 'rst66', 'rev', 2, 9, 'знаю твои дела, и скорбь, и нищету (впрочем, ты богат), и злословие от тех, которые говорят о себе, что они Иудеи, а они не таковы, но сборище сатанинское.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39827, 'rst66', 'rev', 2, 8, 'И Ангелу Смирнской церкви напиши: так говорит Первый и Последний, Который был мертв, и се, жив:');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39826, 'rst66', 'rev', 2, 7, 'Имеющий ухо да слышит, что&#769; Дух говорит церквам: побеждающему дам вкушать от древа жизни, которое посреди рая Божия.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39825, 'rst66', 'rev', 2, 6, 'Впрочем, то в тебе <i>хорошо</i>, что ты ненавидишь дела Николаитов, которые и Я ненавижу.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39824, 'rst66', 'rev', 2, 5, 'Итак, вспомни, откуда ты ниспал, и покайся, и твори прежние дела; а если не так, скоро приду к тебе и сдвину светильник твой с места его, если не покаешься.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39823, 'rst66', 'rev', 2, 4, 'Но имею против тебя то, что ты оставил первую любовь твою.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39822, 'rst66', 'rev', 2, 3, 'ты много переносил и имеешь терпение, и для имени Моего трудился и не изнемогал.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39821, 'rst66', 'rev', 2, 2, 'знаю дела твои, и труд твой, и терпение твое, и то, что ты не можешь сносить развратных, и испытал тех, которые называют себя апостолами, а они не таковы, и нашел, что они лжецы;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39820, 'rst66', 'rev', 2, 1, 'Ангелу Ефесской церкви напиши: так говорит Держащий семь звезд в деснице Своей, Ходящий посреди семи золотых светильников:');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39819, 'rst66', 'rev', 1, 20, 'Тайна семи звезд, которые ты видел в деснице Моей, и семи золотых светильников <i>есть сия:</i> семь звезд суть Ангелы семи церквей; а семь светильников, которые ты видел, суть семь церквей.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39818, 'rst66', 'rev', 1, 19, 'Итак, напиши, что ты видел, и что есть, и что будет после сего.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39817, 'rst66', 'rev', 1, 18, 'и живой; и был мертв, и се, жив во веки веков, аминь; и имею ключи ада и смерти.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39816, 'rst66', 'rev', 1, 17, 'И когда я увидел Его, то пал к ногам Его, как мертвый. И Он положил на меня десницу Свою и сказал мне: не бойся; Я есмь Первый и Последний,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39815, 'rst66', 'rev', 1, 16, 'Он держал в деснице Своей семь звезд, и из уст Его выходил острый с обеих сторон меч; и лицо Его, как солнце, сияющее в силе своей.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39814, 'rst66', 'rev', 1, 15, 'и ноги Его подобны халколивану, как раскаленные в печи, и голос Его, как шум вод многих.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39813, 'rst66', 'rev', 1, 14, 'глава Его и волосы белы, как белая во&#769;лна, как снег; и очи Его, как пламень огненный;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39812, 'rst66', 'rev', 1, 13, 'и, посреди семи светильников, подобного Сыну Человеческому, облеченного в подир и по персям опоясанного золотым поясом:');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39811, 'rst66', 'rev', 1, 12, 'Я обратился, чтобы увидеть, чей голос, говоривший со мною; и, обратившись, увидел семь золотых светильников');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39810, 'rst66', 'rev', 1, 11, 'то, что видишь, напиши в книгу и пошли церквам, находящимся в Асии: в Ефес, и в Смирну, и в Пергам, и в Фиатиру, и в Сардис, и в Филадельфию, и в Лаодикию.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39809, 'rst66', 'rev', 1, 10, 'Я был в духе в день воскресный и слышал позади себя громкий голос, как бы трубный, который говорил: Я есмь Альфа и Омега, Первый и Последний;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39808, 'rst66', 'rev', 1, 9, 'Я, Иоанн, брат ваш и соучастник в скорби, и в царствии, и в терпении Иисуса Христа, был на острове, называемом Патмос, за слово Божие и за свидетельство Иисуса Христа.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39807, 'rst66', 'rev', 1, 8, 'Я есмь Альфа и Омега, начало и конец, говорит Господь, Который есть и был и грядет, Вседержитель.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39806, 'rst66', 'rev', 1, 7, 'Се, грядет с облаками, и узрит Его всякое око и те, которые пронзили Его; и возрыдают пред Ним все племена земные. Ей, аминь.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39805, 'rst66', 'rev', 1, 6, 'и соделавшему нас царями и священниками Богу и Отцу Своему, слава и держава во веки веков, аминь.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39804, 'rst66', 'rev', 1, 5, 'и от Иисуса Христа, Который есть свидетель верный, первенец из мертвых и владыка царей земных. Ему, возлюбившему нас и омывшему нас от грехов наших Кровию Своею');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39803, 'rst66', 'rev', 1, 4, 'Иоанн &mdash; семи церквам, находящимся в Асии: благодать вам и мир от Того, Который есть и был и грядет, и от семи духов, находящихся перед престолом Его,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39802, 'rst66', 'rev', 1, 3, 'Блажен читающий и слушающие слова пророчества сего и соблюдающие написанное в нем; ибо время близко.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39801, 'rst66', 'rev', 1, 2, 'который свидетельствовал слово Божие, и свидетельство Иисуса Христа и что он видел.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39800, 'rst66', 'rev', 1, 1, 'Откровение Иисуса Христа, которое дал Ему Бог, чтобы показать рабам Своим, чему надлежит быть вскоре. И Он показал, послав <i>оное</i> через Ангела Своего рабу Своему Иоанну,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39799, 'rst66', 'heb', 13, 25, 'Благодать со всеми вами. Аминь.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39798, 'rst66', 'heb', 13, 24, 'Приветствуйте всех наставников ваших и всех святых. Приветствуют вас Италийские.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39797, 'rst66', 'heb', 13, 23, 'Знайте, что брат наш Тимофей освобожден, и я вместе с ним, если он скоро придет, увижу вас.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39796, 'rst66', 'heb', 13, 22, 'Прошу вас, братия, примите сие слово увещания; я же не много и написал вам.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39795, 'rst66', 'heb', 13, 21, 'да усовершит вас во всяком добром деле, к исполнению воли Его, производя в вас благоугодное Ему через Иисуса Христа. Ему слава во веки веков! Аминь.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39794, 'rst66', 'heb', 13, 20, 'Бог же мира, воздвигший из мертвых Пастыря овец великого Кровию завета вечного, Господа нашего Иисуса [Христа],');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39793, 'rst66', 'heb', 13, 19, 'Особенно же прошу делать это, дабы я скорее возвращен был вам.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39792, 'rst66', 'heb', 13, 18, 'Молитесь о нас; ибо мы уверены, что имеем добрую совесть, потому что во всем желаем вести себя честно.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39791, 'rst66', 'heb', 13, 17, 'Повинуйтесь наставникам вашим и будьте покорны, ибо они неусыпно пекутся о душах ваших, как обязанные дать отчет; чтобы они делали это с радостью, а не воздыхая, ибо это для вас неполезно.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39790, 'rst66', 'heb', 13, 16, 'Не забывайте также благотворения и общительности, ибо таковые жертвы благоугодны Богу.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39789, 'rst66', 'heb', 13, 15, 'Итак, будем через Него непрестанно приносить Богу жертву хвалы, то есть плод уст, прославляющих имя Его.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39788, 'rst66', 'heb', 13, 14, 'ибо не имеем здесь постоянного града, но ищем будущего.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39787, 'rst66', 'heb', 13, 13, 'Итак, выйдем к Нему за стан, нося Его поругание;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39786, 'rst66', 'heb', 13, 12, 'то и Иисус, дабы освятить людей Кровию Своею, пострадал вне врат.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39785, 'rst66', 'heb', 13, 11, 'Так как тела животных, которых кровь для <i>очищения</i> греха вносится первосвященником во святилище, сжигаются вне стана, &mdash;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39784, 'rst66', 'heb', 13, 10, 'Мы имеем жертвенник, от которого не имеют права питаться служащие скинии.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39783, 'rst66', 'heb', 13, 9, 'Учениями различными и чуждыми не увлекайтесь; ибо хорошо благодатью укреплять сердца, а не яствами, от которых не получили пользы занимающиеся ими.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39782, 'rst66', 'heb', 13, 8, 'Иисус Христос вчера и сегодня и вовеки Тот же.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39781, 'rst66', 'heb', 13, 7, 'Поминайте наставников ваших, которые проповедовали вам слово Божие, и, взирая на кончину их жизни, подражайте вере их.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39780, 'rst66', 'heb', 13, 6, 'так что мы смело говорим: «Господь мне помощник, и не убоюсь: что сделает мне человек?»');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39779, 'rst66', 'heb', 13, 5, 'Имейте нрав несребролюбивый, довольствуясь тем, что есть. Ибо Сам сказал: «не оставлю тебя и не покину тебя»,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39778, 'rst66', 'heb', 13, 4, 'Брак у всех <i>да будет</i> честен и ложе непорочно; блудников же и прелюбодеев судит Бог.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39777, 'rst66', 'heb', 13, 3, 'Помните узников, как бы и вы с ними были в узах, и страждущих, как и сами находитесь в теле.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39776, 'rst66', 'heb', 13, 2, 'Страннолюбия не забывайте, ибо через него некоторые, не зная, оказали гостеприимство Ангелам.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39775, 'rst66', 'heb', 13, 1, 'Братолюбие <i>между вами</i> да пребывает.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39774, 'rst66', 'heb', 12, 29, 'потому что Бог наш есть огнь поядающий.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39773, 'rst66', 'heb', 12, 28, 'Итак, мы, приемля царство непоколебимое, будем хранить благодать, которою будем служить благоугодно Богу, с благоговением и страхом,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39772, 'rst66', 'heb', 12, 27, 'Слова: «еще раз» означают изменение колеблемого, как сотворенного, чтобы пребыло непоколебимое.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39771, 'rst66', 'heb', 12, 26, 'Которого глас тогда поколебал землю и Который ныне дал такое обещание: «еще раз поколеблю не только землю, но и небо».');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39770, 'rst66', 'heb', 12, 25, 'Смотрите, не отвратитесь и вы от говорящего. Если те, не послушав глаголавшего на земле, не избегли <i>наказания</i>, то тем более <i>не избежим</i> мы, если отвратимся от <i>Глаголющего</i> с небес,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39769, 'rst66', 'heb', 12, 24, 'и к Ходатаю нового завета &mdash; Иисусу, и к Крови кропления, говорящей лучше, нежели Авелева.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39768, 'rst66', 'heb', 12, 23, 'к торжествующему собору и церкви первенцев, написанных на небесах, и к Судии всех &mdash; Богу, и к духам праведников, достигших совершенства,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39767, 'rst66', 'heb', 12, 22, 'Но вы приступили к горе Сиону и ко граду Бога живого, к небесному Иерусалиму и тьмам Ангелов,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39766, 'rst66', 'heb', 12, 21, 'и столь ужасно было это видение, <i>что и</i> Моисей сказал: «я в страхе и трепете».');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39765, 'rst66', 'heb', 12, 20, 'ибо они не могли стерпеть того, что заповедуемо было: «если и зверь прикоснется к горе, будет побит камнями [или поражен стрелою]»;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39764, 'rst66', 'heb', 12, 19, 'не к трубному звуку и гласу глаголов, который слышавшие просили, чтобы к ним более не было продолжаемо слово,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39763, 'rst66', 'heb', 12, 18, 'Вы приступили не к горе, осязаемой и пылающей огнем, не ко тьме и мраку и буре,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39762, 'rst66', 'heb', 12, 17, 'Ибо вы знаете, что после того он, желая наследовать благословение, был отвержен; не мог переменить мыслей <i>отца</i>, хотя и просил о том со слезами.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39761, 'rst66', 'heb', 12, 16, 'чтобы не было <i>между вами</i> какого блудника или нечестивца, который бы, как Исав, за одну снедь отказался от своего первородства.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39760, 'rst66', 'heb', 12, 15, 'Наблюдайте, чтобы кто не лишился благодати Божией; чтобы какой горький корень, возникнув, не причинил вреда, и чтобы им не осквернились многие;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39759, 'rst66', 'heb', 12, 14, 'Старайтесь иметь мир со всеми и святость, без которой никто не увидит Господа.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39758, 'rst66', 'heb', 12, 13, 'и ходите прямо ногами вашими, дабы хромлющее не совратилось, а лучше исправилось.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39757, 'rst66', 'heb', 12, 12, 'Итак, укрепите опустившиеся руки и ослабевшие колени');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39756, 'rst66', 'heb', 12, 11, 'Всякое наказание в настоящее время кажется не радостью, а печалью; но после наученным через него доставляет мирный плод праведности.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39755, 'rst66', 'heb', 12, 10, 'Те наказывали нас по своему произволу для немногих дней; а Сей &mdash; для пользы, чтобы нам иметь участие в святости Его.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39754, 'rst66', 'heb', 12, 9, 'Притом, <i>если</i> мы, будучи наказываемы плотскими родителями нашими, боялись их, то не гораздо ли более должны покориться Отцу духов, чтобы жить?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39753, 'rst66', 'heb', 12, 8, 'Если же остаетесь без наказания, которое всем обще, то вы &mdash; незаконные дети, а не сыны.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39752, 'rst66', 'heb', 12, 7, 'Если вы терпите наказание, то Бог поступает с вами, как с сынами. Ибо есть ли какой сын, которого бы не наказывал отец?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39751, 'rst66', 'heb', 12, 6, 'Ибо Господь, кого любит, того наказывает; бьет же всякого сына, которого принимает».');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39750, 'rst66', 'heb', 12, 5, 'и забыли утешение, которое предлагается вам, как сынам: «сын мой! не пренебрегай наказания Господня, и не унывай, когда Он обличает тебя.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39749, 'rst66', 'heb', 12, 4, 'Вы еще не до крови сражались, подвизаясь против греха,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39748, 'rst66', 'heb', 12, 3, 'Помыслите о Претерпевшем такое над Собою поругание от грешников, чтобы вам не изнемочь и не ослабеть душами вашими.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39747, 'rst66', 'heb', 12, 2, 'взирая на начальника и совершителя веры Иисуса, Который, вместо предлежавшей Ему радости, претерпел крест, пренебрегши посрамление, и воссел одесную престола Божия.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39746, 'rst66', 'heb', 12, 1, 'Посему и мы, имея вокруг себя такое облако свидетелей, свергнем с себя всякое бремя и запинающий нас грех и с терпением будем проходить предлежащее нам поприще,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39745, 'rst66', 'heb', 11, 40, 'потому что Бог предусмотрел о нас нечто лучшее, дабы они не без нас достигли совершенства.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39744, 'rst66', 'heb', 11, 39, 'И все сии, свидетельствованные в вере, не получили обещанного,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39743, 'rst66', 'heb', 11, 38, 'те, которых весь мир не был достоин, скитались по пустыням и горам, по пещерам и ущельям земли.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39742, 'rst66', 'heb', 11, 37, 'были побиваемы камнями, перепиливаемы, подвергаемы пытке, умирали от меча, скитались в ми&#769;лотях и козьих кожах, терпя недостатки, скорби, озлобления;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39741, 'rst66', 'heb', 11, 36, 'другие испытали поругания и побои, а также узы и темницу,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39740, 'rst66', 'heb', 11, 35, 'жены получали умерших своих воскресшими; иные же замучены были, не приняв освобождения, дабы получить лучшее воскресение;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39739, 'rst66', 'heb', 11, 34, 'угашали силу огня, избегали острия меча, укреплялись от немощи, были крепки на войне, прогоняли полки чужих;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39738, 'rst66', 'heb', 11, 33, 'которые верою побеждали царства, творили правду, получали обетования, заграждали уста львов,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39737, 'rst66', 'heb', 11, 32, 'И что еще скажу? Недостанет мне времени, чтобы повествовать о Гедеоне, о Вараке, о Самсоне и Иеффае, о Давиде, Самуиле и [других] пророках,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39736, 'rst66', 'heb', 11, 31, 'Верою Раав блудница, с миром приняв соглядатаев [и проводив их другим путем], не погибла с неверными.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39735, 'rst66', 'heb', 11, 30, 'Верою пали стены Иерихонские, по семидневном обхождении.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39734, 'rst66', 'heb', 11, 29, 'Верою перешли они Чермное море, как по суше, &mdash; на что покусившись, Египтяне потонули.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39733, 'rst66', 'heb', 11, 28, 'Верою совершил он Пасху и пролитие крови, дабы истребитель первенцев не коснулся их.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39732, 'rst66', 'heb', 11, 27, 'Верою оставил он Египет, не убоявшись гнева царского, ибо он, как бы видя Невидимого, был тверд.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39731, 'rst66', 'heb', 11, 26, 'и поношение Христово почел бо&#769;льшим для себя богатством, нежели Египетские сокровища; ибо он взирал на воздаяние.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39730, 'rst66', 'heb', 11, 25, 'и лучше захотел страдать с народом Божиим, нежели иметь временное греховное наслаждение,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39729, 'rst66', 'heb', 11, 24, 'Верою Моисей, придя в возраст, отказался называться сыном дочери фараоновой,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39728, 'rst66', 'heb', 11, 23, 'Верою Моисей по рождении три месяца скрываем был родителями своими, ибо видели они, что дитя прекрасно, и не устрашились царского повеления.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39727, 'rst66', 'heb', 11, 22, 'Верою Иосиф, при кончине, напоминал об исходе сынов Израилевых и завещал о костях своих.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39726, 'rst66', 'heb', 11, 21, 'Верою Иаков, умирая, благословил каждого сына Иосифова и поклонился на верх жезла своего.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39725, 'rst66', 'heb', 11, 20, 'Верою в будущее Исаак благословил Иакова и Исава.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39724, 'rst66', 'heb', 11, 19, 'Ибо он думал, что Бог силен и из мертвых воскресить, почему и получил его в предзнаменование.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39723, 'rst66', 'heb', 11, 18, 'о котором было сказано: «в Исааке наречется тебе семя».');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39722, 'rst66', 'heb', 11, 17, 'Верою Авраам, будучи искушаем, принес в жертву Исаака и, имея обетование, принес единородного,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39721, 'rst66', 'heb', 11, 16, 'но они стремились к лучшему, то есть к небесному; посему и Бог не стыдится их, называя Себя их Богом: ибо Он приготовил им город.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39720, 'rst66', 'heb', 11, 15, 'И если бы они в мыслях имели то <i>отечество</i>, из которого вышли, то имели бы время возвратиться;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39719, 'rst66', 'heb', 11, 14, 'ибо те, которые так говорят, показывают, что они ищут отечества.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39718, 'rst66', 'heb', 11, 13, 'Все сии умерли в вере, не получив обетований, а только издали видели оные, и радовались, и говорили о себе, что они странники и пришельцы на земле;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39717, 'rst66', 'heb', 11, 12, 'И потому от одного, и притом омертвелого, родилось так много, как <i>много</i> звезд на небе и как бесчислен песок на берегу морском.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39716, 'rst66', 'heb', 11, 11, 'Верою и сама Сарра [будучи неплодна] получила силу к принятию семени, и не по времени возраста родила, ибо знала, что верен Обещавший.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39715, 'rst66', 'heb', 11, 10, 'ибо он ожидал города, имеющего основание, которого художник и строитель &mdash; Бог.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39714, 'rst66', 'heb', 11, 9, 'Верою обитал он на земле обетованной, как на чужой, и жил в шатрах с Исааком и Иаковом, сонаследниками того же обетования;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39713, 'rst66', 'heb', 11, 8, 'Верою Авраам повиновался призванию идти в страну, которую имел получить в наследие, и пошел, не зная, куда идет.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39712, 'rst66', 'heb', 11, 7, 'Верою Ной, получив откровение о том, что еще не было видимо, благоговея, приготовил ковчег для спасения дома своего; ею осудил он [весь] мир, и сделался наследником праведности по вере.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39711, 'rst66', 'heb', 11, 6, 'А без веры угодить Богу невозможно; ибо надобно, чтобы приходящий к Богу веровал, что Он есть, и ищущим Его воздает.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39710, 'rst66', 'heb', 11, 5, 'Верою Енох переселен был так, что не видел смерти; и не стало его, потому что Бог переселил его. Ибо прежде переселения своего получил он свидетельство, что угодил Богу.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39709, 'rst66', 'heb', 11, 4, 'Верою Авель принес Богу жертву лучшую, нежели Каин; ею получил свидетельство, что он праведен, как засвидетельствовал Бог о дарах его; ею он и по смерти говорит еще.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39708, 'rst66', 'heb', 11, 3, 'Верою познаём, что веки устроены словом Божиим, так что из невидимого произошло видимое.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39707, 'rst66', 'heb', 11, 2, 'В ней свидетельствованы древние.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39706, 'rst66', 'heb', 11, 1, 'Вера же есть осуществление ожидаемого и уверенность в невидимом.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39705, 'rst66', 'heb', 10, 39, 'Мы же не из колеблющихся на погибель, но <i>стои&#769;м</i> в вере к спасению души.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39704, 'rst66', 'heb', 10, 38, 'Праведный верою жив будет; а если <i>кто</i> поколеблется, не благоволит к тому душа Моя.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39703, 'rst66', 'heb', 10, 37, 'ибо еще немного, очень немного, и Грядущий придет и не умедлит.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39702, 'rst66', 'heb', 10, 36, 'Терпение нужно вам, чтобы, исполнив волю Божию, получить обещанное;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39701, 'rst66', 'heb', 10, 35, 'Итак, не оставляйте упования вашего, которому предстоит великое воздаяние.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39700, 'rst66', 'heb', 10, 34, 'ибо вы и моим узам сострадали, и расхищение имения вашего приняли с радостью, зная, что есть у вас на небесах имущество лучшее и непреходящее.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39699, 'rst66', 'heb', 10, 33, 'то сами среди поношений и скорбей служа зрелищем <i>для других</i>, то принимая участие в других, находившихся в таком же <i>состоянии</i>;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39698, 'rst66', 'heb', 10, 32, 'Вспомните прежние дни ваши, когда вы, быв просвещены, выдержали великий подвиг страданий,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39697, 'rst66', 'heb', 10, 31, 'Страшно впасть в руки Бога живого!');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39696, 'rst66', 'heb', 10, 30, 'Мы знаем Того, Кто сказал: «у Меня отмщение, Я воздам, говорит Господь». И еще: «Господь будет судить народ Свой».');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39695, 'rst66', 'heb', 10, 29, 'то сколь тягчайшему, думаете, наказанию повинен будет тот, кто попирает Сына Божия и не почитает за святыню Кровь завета, которою освящен, и Духа благодати оскорбляет?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39694, 'rst66', 'heb', 10, 28, '<i>Если</i> отвергшийся закона Моисеева, при двух или трех свидетелях, без милосердия <i>наказывается</i> смертью,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39693, 'rst66', 'heb', 10, 27, 'но некое страшное ожидание суда и ярость огня, готового пожрать противников.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39692, 'rst66', 'heb', 10, 26, 'Ибо если мы, получив познание истины, произвольно грешим, то не остается более жертвы за грехи,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39691, 'rst66', 'heb', 10, 25, 'Не будем оставлять собрания своего, как есть у некоторых обычай; но будем увещевать <i>друг друга</i>, и тем более, чем более усматриваете приближение дня оного.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39690, 'rst66', 'heb', 10, 24, 'Будем внимательны друг ко другу, поощряя к любви и добрым делам.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39689, 'rst66', 'heb', 10, 23, 'будем держаться исповедания упования неуклонно, ибо верен Обещавший.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39688, 'rst66', 'heb', 10, 22, 'да приступаем с искренним сердцем, с полною верою, кроплением очистив сердца от порочной совести и омыв тело водою чистою,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39687, 'rst66', 'heb', 10, 21, 'и <i>имея</i> великого Священника над домом Божиим,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39686, 'rst66', 'heb', 10, 20, 'который Он вновь открыл нам через завесу, то есть плоть Свою,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39685, 'rst66', 'heb', 10, 19, 'Итак, братия, имея дерзновение входить во святилище посредством Крови Иисуса Христа, путем новым и живым,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39684, 'rst66', 'heb', 10, 18, 'А где прощение грехов, там не нужно приношение за них.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39683, 'rst66', 'heb', 10, 17, 'и грехов их и беззаконий их не воспомяну более».');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39682, 'rst66', 'heb', 10, 16, '«вот завет, который завещаю им после тех дней, говорит Господь: вложу законы Мои в сердца их, и в мыслях их напишу их,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39681, 'rst66', 'heb', 10, 15, '<i>О сем</i> свидетельствует нам и Дух Святой; ибо сказано:');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39680, 'rst66', 'heb', 10, 14, 'Ибо Он одним приношением навсегда сделал совершенными освящаемых.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39679, 'rst66', 'heb', 10, 13, 'ожидая затем, доколе враги Его будут положены в подножие ног Его.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39678, 'rst66', 'heb', 10, 12, 'Он же, принеся одну жертву за грехи, навсегда воссел одесную Бога,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39677, 'rst66', 'heb', 10, 11, 'И всякий священник ежедневно стои&#769;т в служении и многократно приносит одни и те же жертвы, которые никогда не могут истребить грехов.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39676, 'rst66', 'heb', 10, 10, 'По сей‐то воле освящены мы единократным принесением тела Иисуса Христа.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39675, 'rst66', 'heb', 10, 9, 'потом прибавил: «вот, иду исполнить волю Твою, Боже». Отменяет первое, чтобы постановить второе.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39674, 'rst66', 'heb', 10, 8, 'Сказав прежде, что «ни жертвы, ни приношения, ни всесожжений, ни <i>жертвы</i> за грех, &mdash; которые приносятся по закону, &mdash; Ты не восхотел и не благоизволил»,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39673, 'rst66', 'heb', 10, 7, 'Тогда Я сказал: вот, иду, <i>как</i> в начале книги написано о Мне, исполнить волю Твою, Боже».');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39672, 'rst66', 'heb', 10, 6, 'Всесожжения и <i>жертвы</i> за грех неугодны Тебе.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39671, 'rst66', 'heb', 10, 5, 'Посему <i>Христос</i>, входя в мир, говорит: «жертвы и приношения Ты не восхотел, но тело уготовал Мне.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39670, 'rst66', 'heb', 10, 4, 'ибо невозможно, чтобы кровь тельцов и козлов уничтожала грехи.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39669, 'rst66', 'heb', 10, 3, 'Но жертвами каждогодно напоминается о грехах,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39668, 'rst66', 'heb', 10, 2, 'Иначе перестали бы приносить <i>их</i>, потому что приносящие жертву, быв очищены однажды, не имели бы уже никакого сознания грехов.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39667, 'rst66', 'heb', 10, 1, 'Закон, имея тень будущих благ, а не самый образ вещей, одними и теми же жертвами, каждый год постоянно приносимыми, никогда не может сделать совершенными приходящих <i>с ними</i>.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39666, 'rst66', 'heb', 9, 28, 'так и Христос, однажды принеся Себя в жертву, чтобы подъять грехи многих, во второй раз явится не <i>для очищения</i> греха, а для ожидающих Его во спасение.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39665, 'rst66', 'heb', 9, 27, 'И как человекам положено однажды умереть, а потом суд,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39664, 'rst66', 'heb', 9, 26, 'иначе надлежало бы Ему многократно страдать от начала мира; Он же однажды, к концу веков, явился для уничтожения греха жертвою Своею.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39663, 'rst66', 'heb', 9, 25, 'и не для того, чтобы многократно приносить Себя, как первосвященник входит во святилище каждогодно с чужою кровью;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39662, 'rst66', 'heb', 9, 24, 'Ибо Христос вошел не в рукотворенное святилище, по образу истинного <i>устроенное</i>, но в самое небо, чтобы предстать ныне за нас пред лицо Божие,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39661, 'rst66', 'heb', 9, 23, 'Итак, образы небесного должны были очищаться сими, самое же небесное &mdash; лучшими сих жертвами.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39660, 'rst66', 'heb', 9, 22, 'Да и все почти по закону очищается кровью, и без пролития крови не бывает прощения.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39659, 'rst66', 'heb', 9, 21, 'Также окропил кровью и скинию и все сосуды Богослужебные.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39658, 'rst66', 'heb', 9, 20, 'говоря: «это кровь завета, который заповедал вам Бог».');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39657, 'rst66', 'heb', 9, 19, 'Ибо Моисей, произнеся все заповеди по закону перед всем народом, взял кровь тельцов и козлов с водою и шерстью червленою и иссопом и окропил как самую книгу, так и весь народ,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39656, 'rst66', 'heb', 9, 18, 'Почему и первый <i>завет</i> был утвержден не без крови.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39655, 'rst66', 'heb', 9, 17, 'потому что завещание действительно после умерших: оно не имеет силы, когда завещатель жив.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39654, 'rst66', 'heb', 9, 16, 'Ибо где завещание, там необходимо, чтобы последовала смерть завещателя,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39653, 'rst66', 'heb', 9, 15, 'И потому Он есть ходатай нового завета, дабы вследствие смерти <i>Его</i>, бывшей для искупления от преступлений, сделанных в первом завете, призванные к вечному наследию получили обетованное.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39652, 'rst66', 'heb', 9, 14, 'то кольми паче Кровь Христа, Который Духом Святым принес Себя, непорочного, Богу, очистит совесть нашу от мертвых дел для служения Богу живому и истинному!');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39651, 'rst66', 'heb', 9, 13, 'Ибо если кровь тельцов и козлов и пепел телицы, через окропление, освящает оскверненных, дабы чисто было тело,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39650, 'rst66', 'heb', 9, 12, 'и не с кровью козлов и тельцов, но со Своею Кровию, однажды вошел во святилище и приобрел вечное искупление.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39649, 'rst66', 'heb', 9, 11, 'Но Христос, Первосвященник будущих благ, придя с большею и совершеннейшею скиниею, нерукотворенною, то есть не такового устроения,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39648, 'rst66', 'heb', 9, 10, 'и которые с яствами и питиями, и различными омовениями и обрядами, <i>относящимися</i> до плоти, установлены были только до времени исправления.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39647, 'rst66', 'heb', 9, 9, 'Она есть образ настоящего времени, в которое приносятся дары и жертвы, не могущие сделать в совести совершенным приносящего,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39646, 'rst66', 'heb', 9, 8, '<i>Сим</i> Дух Святой показывает, что еще не открыт путь во святилище, доколе стои&#769;т прежняя скиния.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39645, 'rst66', 'heb', 9, 7, 'а во вторую &mdash; однажды в год один только первосвященник, не без крови, которую приносит за себя и за грехи неведения народа.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39644, 'rst66', 'heb', 9, 6, 'При таком устройстве в первую скинию всегда входят священники совершать Богослужение,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39643, 'rst66', 'heb', 9, 5, 'а над ним &mdash; херувимы славы, осеняющие очистилище; о чем не нужно теперь говорить подробно.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39642, 'rst66', 'heb', 9, 4, 'имевшая золотую кадильницу и обложенный со всех сторон золотом ковчег завета, где были золотой сосуд с манною, жезл Ааронов расцветший и скрижали завета,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39641, 'rst66', 'heb', 9, 3, 'За второю же завесою была скиния, называемая «Святое святых»,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39640, 'rst66', 'heb', 9, 2, 'ибо устроена была скиния первая, в которой был светильник, и трапеза, и предложение хлебов, и которая называется «святое».');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39639, 'rst66', 'heb', 9, 1, 'И первый завет имел постановление о Богослужении и святилище земное:');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39638, 'rst66', 'heb', 8, 13, 'Говоря «новый», показал ветхость первого; а ветшающее и стареющее близко к уничтожению.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39637, 'rst66', 'heb', 8, 12, 'потому что Я буду милостив к неправдам их, и грехов их и беззаконий их не воспомяну более».');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39636, 'rst66', 'heb', 8, 11, 'И не будет учить каждый ближнего своего и каждый брата своего, говоря: познай Господа; потому что все, от малого до большого, будут знать Меня,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39635, 'rst66', 'heb', 8, 10, 'Вот завет, который завещаю дому Израилеву после тех дней, говорит Господь: вложу законы Мои в мысли их, и напишу их на сердцах их; и буду их Богом, а они будут Моим народом.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39634, 'rst66', 'heb', 8, 9, 'не такой завет, какой Я заключил с отцами их в то время, когда взял их за руку, чтобы вывести их из земли Египетской, потому что они не пребыли в том завете Моем, и Я пренебрег их, говорит Господь.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39633, 'rst66', 'heb', 8, 8, 'Но <i>пророк</i>, укоряя их, говорит: «вот, наступают дни, говорит Господь, когда Я заключу с домом Израиля и с домом Иуды новый завет,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39632, 'rst66', 'heb', 8, 7, 'Ибо если бы первый <i>завет</i> был без недостатка, то не было бы нужды искать места другому.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39631, 'rst66', 'heb', 8, 6, 'Но Сей <i>Первосвященник</i> получил служение тем превосходнейшее, чем лучшего Он ходатай завета, который утвержден на лучших обетованиях.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39630, 'rst66', 'heb', 8, 5, 'которые служат образу и тени небесного, как сказано было Моисею, когда он приступал к совершению скинии: «смотри», сказано, «сделай все по образу, показанному тебе на горе».');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39629, 'rst66', 'heb', 8, 4, 'Если бы Он оставался на земле, то не был бы и священником, потому что <i>здесь</i> такие священники, которые по закону приносят дары,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39628, 'rst66', 'heb', 8, 3, 'Всякий первосвященник поставляется для приношения даров и жертв; а потому нужно было, чтобы и Сей также имел что принести.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39627, 'rst66', 'heb', 8, 2, 'и <i>есть</i> священнодействователь святилища и скинии истинной, которую воздвиг Господь, а не человек.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39626, 'rst66', 'heb', 8, 1, 'Главное же в том, о чем говорим, есть то: мы имеем такого Первосвященника, Который воссел одесную престола величия на небесах');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39625, 'rst66', 'heb', 7, 28, 'Ибо закон поставляет первосвященниками человеков, имеющих немощи; а слово клятвенное, после закона, <i>поставило</i> Сына, навеки совершенного.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39624, 'rst66', 'heb', 7, 27, 'который не имеет нужды ежедневно, как те первосвященники, приносить жертвы сперва за свои грехи, потом за грехи народа, ибо Он совершил это однажды, принеся <i>в жертву</i> Себя Самого.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39623, 'rst66', 'heb', 7, 26, 'Таков и должен быть у нас Первосвященник: святой, непричастный злу, непорочный, отделенный от грешников и превознесенный выше небес,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39622, 'rst66', 'heb', 7, 25, 'посему и может всегда спасать приходящих чрез Него к Богу, будучи всегда жив, чтобы ходатайствовать за них.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39621, 'rst66', 'heb', 7, 24, 'а Сей, как пребывающий вечно, имеет и священство непреходящее,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39620, 'rst66', 'heb', 7, 23, 'Притом тех священников было много, потому что смерть не допускала пребывать одному;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39619, 'rst66', 'heb', 7, 22, 'то лучшего завета поручителем соделался Иисус.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39618, 'rst66', 'heb', 7, 21, 'ибо те были священниками без клятвы, а Сей с клятвою, потому что о Нем сказано: «клялся Господь, и не раскается: Ты священник вовек по чину Мелхиседека», &mdash;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39617, 'rst66', 'heb', 7, 20, 'И как <i>сие было</i> не без клятвы, &mdash;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39616, 'rst66', 'heb', 7, 19, 'ибо закон ничего не довел до совершенства; но вводится лучшая надежда, посредством которой мы приближаемся к Богу.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39615, 'rst66', 'heb', 7, 18, 'Отменение же прежде бывшей заповеди бывает по причине ее немощи и бесполезности,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39614, 'rst66', 'heb', 7, 17, 'Ибо засвидетельствовано: «Ты священник вовек по чину Мелхиседека».');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39613, 'rst66', 'heb', 7, 16, 'который таков не по закону заповеди плотской, но по силе жизни непрестающей.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39612, 'rst66', 'heb', 7, 15, 'И это еще яснее видно <i>из того</i>, что по подобию Мелхиседека восстает Священник иной,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39611, 'rst66', 'heb', 7, 14, 'Ибо известно, что Господь наш воссиял из колена Иудина, о котором Моисей ничего не сказал относительно священства.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39610, 'rst66', 'heb', 7, 13, 'Ибо Тот, о Котором говорится сие, принадлежал к иному колену, из которого никто не приступал к жертвеннику.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39609, 'rst66', 'heb', 7, 12, 'Потому что с переменою священства необходимо быть перемене и закона.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39608, 'rst66', 'heb', 7, 11, 'Итак, если бы совершенство достигалось посредством левитского священства, &mdash; ибо с ним сопряжен закон народа, &mdash; то какая бы еще нужда была восставать иному священнику по чину Мелхиседека, а не по чину Аарона именоваться?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39607, 'rst66', 'heb', 7, 10, 'ибо он был еще в чреслах отца, когда Мелхиседек встретил его.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39606, 'rst66', 'heb', 7, 9, 'И, так сказать, сам Левий, принимающий десятины, в <i>лице</i> Авраама дал десятину:');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39605, 'rst66', 'heb', 7, 8, 'И здесь десятины берут человеки смертные, а там &mdash; имеющий о себе свидетельство, что он живет.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39604, 'rst66', 'heb', 7, 7, 'Без всякого же прекословия меньший благословляется бо&#769;льшим.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39603, 'rst66', 'heb', 7, 6, 'Но сей, не происходящий от рода их, получил десятину от Авраама и благословил имевшего обетования.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39602, 'rst66', 'heb', 7, 5, 'Получающие священство из сынов Левииных имеют заповедь &mdash; брать по закону десятину с народа, то есть со своих братьев, хотя и сии произошли от чресл Авраамовых.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39601, 'rst66', 'heb', 7, 4, 'Видите, как велик тот, которому и Авраам патриарх дал десятину из лучших добыч своих.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39600, 'rst66', 'heb', 7, 3, 'без отца, без матери, без родословия, не имеющий ни начала дней, ни конца жизни, уподобляясь Сыну Божию, пребывает священником навсегда.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39599, 'rst66', 'heb', 7, 2, 'которому и десятину отделил Авраам от всего, &mdash; во‐первых, по знаменованию <i>имени</i> царь правды, а потом и царь Салима, то есть царь мира,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39598, 'rst66', 'heb', 7, 1, 'Ибо Мелхиседек, царь Салима, священник Бога Всевышнего, тот, который встретил Авраама и благословил его, возвращающегося после поражения царей,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39597, 'rst66', 'heb', 6, 20, 'куда предтечею за нас вошел Иисус, сделавшись Первосвященником навек по чину Мелхиседека.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39596, 'rst66', 'heb', 6, 19, 'которая для души есть как бы якорь безопасный и крепкий, и входит во внутреннейшее за завесу,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39595, 'rst66', 'heb', 6, 18, 'дабы в двух непреложных вещах, в которых невозможно Богу солгать, твердое утешение имели мы, прибегшие взяться за предлежащую надежду,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39594, 'rst66', 'heb', 6, 17, 'Посему и Бог, желая преимущественнее показать наследникам обетования непреложность Своей воли, употребил в посредство клятву,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39593, 'rst66', 'heb', 6, 16, 'Люди клянутся высшим, и клятва во удостоверение оканчивает всякий спор их.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39592, 'rst66', 'heb', 6, 15, 'И так <i>Авраам</i>, долготерпев, получил обещанное.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39591, 'rst66', 'heb', 6, 14, 'говоря: «истинно благословляя благословлю тебя и размножая размножу тебя».');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39590, 'rst66', 'heb', 6, 13, 'Бог, давая обетование Аврааму, как не мог никем высшим клясться, клялся Самим Собою,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39589, 'rst66', 'heb', 6, 12, 'дабы вы не обленились, но подражали тем, которые верою и долготерпением наследуют обетования.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39588, 'rst66', 'heb', 6, 11, 'Желаем же, чтобы каждый из вас, для совершенной уверенности в надежде, оказывал такую же ревность до конца,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39587, 'rst66', 'heb', 6, 10, 'Ибо не неправеден Бог, чтобы забыл дело ваше и труд любви, которую вы оказали во имя Его, послужив и служа святым.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39586, 'rst66', 'heb', 6, 9, 'Впрочем, о вас, возлюбленные, мы надеемся, что вы в лучшем <i>состоянии</i> и де&#769;ржитесь спасения, хотя и говорим так.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39585, 'rst66', 'heb', 6, 8, 'а производящая терния и волчцы &mdash; негодна и близка к проклятию, которого конец &mdash; сожжение.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39584, 'rst66', 'heb', 6, 7, 'Земля, пившая многократно сходящий на нее дождь и произращающая злак, полезный тем, для которых и возделывается, получает благословение от Бога;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39583, 'rst66', 'heb', 6, 6, 'и отпадших опять обновлять покаянием, когда они снова распинают в себе Сына Божия и ругаются <i>Ему</i>.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39582, 'rst66', 'heb', 6, 5, 'и вкусивших благого глагола Божия и сил будущего века,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39581, 'rst66', 'heb', 6, 4, 'Ибо невозможно &mdash; однажды просвещенных, и вкусивших дара небесного, и соделавшихся причастниками Духа Святого,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39580, 'rst66', 'heb', 6, 3, 'И это сделаем, если Бог позволит.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39579, 'rst66', 'heb', 6, 2, 'учению о крещениях, о возложении рук, о воскресении мертвых и о суде вечном.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39578, 'rst66', 'heb', 6, 1, 'Посему, оставив начатки учения Христова, поспешим к совершенству; и не станем снова полагать основание обращению от мертвых дел и вере в Бога,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39577, 'rst66', 'heb', 5, 14, 'твердая же пища свойственна совершенным, у которых чувства навыком приучены к различению добра и зла.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39576, 'rst66', 'heb', 5, 13, 'Всякий, питаемый молоком, несведущ в слове правды, потому что он младенец;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39575, 'rst66', 'heb', 5, 12, 'Ибо, <i>судя</i> по времени, вам надлежало быть учителями; но вас снова нужно учить первым началам слова Божия, и для вас нужно молоко, а не твердая пища.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39574, 'rst66', 'heb', 5, 11, 'О сем надлежало бы нам говорить много; но трудно истолковать, потому что вы сделались не способны слушать.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39573, 'rst66', 'heb', 5, 10, 'быв наречен от Бога Первосвященником по чину Мелхиседека.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39572, 'rst66', 'heb', 5, 9, 'и, совершившись, сделался для всех послушных Ему виновником спасения вечного,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39571, 'rst66', 'heb', 5, 8, 'хотя Он и Сын, однако страданиями навык послушанию,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39570, 'rst66', 'heb', 5, 7, 'Он, во дни плоти Своей, с сильным воплем и со слезами принес молитвы и моления Могущему спасти Его от смерти; и услышан был за <i>Свое</i> благоговение;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39569, 'rst66', 'heb', 5, 6, 'как и в другом <i>месте</i> говорит: «Ты священник вовек по чину Мелхиседека».');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39568, 'rst66', 'heb', 5, 5, 'Так и Христос не Сам Себе присвоил славу быть первосвященником, но Тот, Кто сказал Ему: «Ты Сын Мой, Я ныне родил Тебя»;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39567, 'rst66', 'heb', 5, 4, 'И никто сам собою не приемлет этой чести, но призываемый Богом, как и Аарон.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39566, 'rst66', 'heb', 5, 3, 'и посему он должен как за народ, так и за себя приносить <i>жертвы</i> о грехах.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39565, 'rst66', 'heb', 5, 2, 'могущий снисходить невежествующим и заблуждающим, потому что и сам обложен немощью,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39564, 'rst66', 'heb', 5, 1, 'Ибо всякий первосвященник, из человеков избираемый, для человеков поставляется на служение Богу, чтобы приносить дары и жертвы за грехи,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39563, 'rst66', 'heb', 4, 16, 'Посему да приступаем с дерзновением к престолу благодати, чтобы получить милость и обрести благодать для благовременной помощи.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39562, 'rst66', 'heb', 4, 15, 'Ибо мы имеем не такого первосвященника, который не может сострадать нам в немощах наших, но Который, подобно <i>нам</i>, искушен во всем, кроме греха.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39561, 'rst66', 'heb', 4, 14, 'Итак, имея Первосвященника великого, прошедшего небеса, Иисуса Сына Божия, будем твердо держаться исповедания <i>нашего</i>.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39560, 'rst66', 'heb', 4, 13, 'И нет твари, сокровенной от Него, но все обнажено и открыто перед очами Его: Ему дадим отчет.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39559, 'rst66', 'heb', 4, 12, 'Ибо слово Божие живо и действенно и острее всякого меча обоюдоострого: оно проникает до разделения души и духа, составов и мозгов и судит помышления и намерения сердечные.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39558, 'rst66', 'heb', 4, 11, 'Итак, постараемся войти в покой оный, чтобы кто по тому же примеру не впал в непокорность.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39557, 'rst66', 'heb', 4, 10, 'Ибо кто вошел в покой Его, тот и сам успокоился от дел своих, как и Бог от Своих.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39556, 'rst66', 'heb', 4, 9, 'Посему для народа Божия еще остается субботство.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39555, 'rst66', 'heb', 4, 8, 'Ибо если бы Иисус <i>Навин</i> доставил им покой, то не было бы сказано после того о другом дне.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39554, 'rst66', 'heb', 4, 7, '<i>то</i> еще определяет некоторый день, «ныне», говоря через Давида, после столь долгого времени, как выше сказано: «ныне, когда услышите глас Его, не ожесточите сердец ваших».');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39553, 'rst66', 'heb', 4, 6, 'Итак, как некоторым остается войти в него, а те, которым прежде возвещено, не вошли в него за непокорность,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39552, 'rst66', 'heb', 4, 5, 'И еще здесь: «не войдут в покой Мой».');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39551, 'rst66', 'heb', 4, 4, 'Ибо негде сказано о седьмом <i>дне</i> так: «и почил Бог в день седьмой от всех дел Своих».');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39550, 'rst66', 'heb', 4, 3, 'А входим в покой мы уверовавшие, так как Он сказал: «Я поклялся в гневе Моем, что они не войдут в покой Мой», хотя дела <i>Его</i> были совершены еще в начале мира.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39549, 'rst66', 'heb', 4, 2, 'Ибо и нам оно возвещено, как и тем; но не принесло им пользы слово слышанное, не растворенное верою слышавших.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39548, 'rst66', 'heb', 4, 1, 'Посему будем опасаться, чтобы, когда еще остается обетование войти в покой Его, не оказался кто из вас опоздавшим.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39547, 'rst66', 'heb', 3, 19, 'Итак, видим, что они не могли войти за неверие.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39546, 'rst66', 'heb', 3, 18, 'Против кого же клялся, что не войдут в покой Его, как не против непокорных?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39545, 'rst66', 'heb', 3, 17, 'На кого же негодовал Он сорок лет? Не на согрешивших ли, которых кости пали в пустыне?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39544, 'rst66', 'heb', 3, 16, 'Ибо некоторые из слышавших возроптали; но не все вышедшие из Египта с Моисеем.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39543, 'rst66', 'heb', 3, 15, 'доколе говорится: «ныне, когда услышите глас Его, не ожесточите сердец ваших, как во время ропота».');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39542, 'rst66', 'heb', 3, 14, 'Ибо мы сделались причастниками Христу, если только начатую жизнь твердо сохраним до конца,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39541, 'rst66', 'heb', 3, 13, 'Но наставляйте друг друга каждый день, доколе можно говорить: «ныне», чтобы кто из вас не ожесточился, обольстившись грехом.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39540, 'rst66', 'heb', 3, 12, 'Смотрите, братия, чтобы не было в ком из вас сердца лукавого и неверного, дабы вам не отступить от Бога живого.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39539, 'rst66', 'heb', 3, 11, 'посему Я поклялся во гневе Моем, что они не войдут в покой Мой».');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39538, 'rst66', 'heb', 3, 10, 'Посему Я вознегодовал на оный род и сказал: непрестанно заблуждаются сердцем, не познали они путей Моих;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39537, 'rst66', 'heb', 3, 9, 'где искушали Меня отцы ваши, испытывали Меня и видели дела Мои сорок лет.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39536, 'rst66', 'heb', 3, 8, 'не ожесточите сердец ваших, как во время ропота, в день искушения в пустыне,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39535, 'rst66', 'heb', 3, 7, 'Почему, как говорит Дух Святой, «ныне, когда услышите глас Его,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39534, 'rst66', 'heb', 3, 6, 'а Христос &mdash; как Сын в доме Его; дом же Его &mdash; мы, если только дерзновение и упование, которым хвалимся, твердо сохраним до конца.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39533, 'rst66', 'heb', 3, 5, 'И Моисей верен во всем доме Его, как служитель, для засвидетельствования того, что надлежало возвестить;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39532, 'rst66', 'heb', 3, 4, 'ибо всякий дом устрояется кем‐либо; а устроивший всё <i>есть</i> Бог.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39531, 'rst66', 'heb', 3, 3, 'Ибо Он достоин тем большей славы пред Моисеем, чем бо&#769;льшую честь имеет в сравнении с домом тот, кто устроил его,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39530, 'rst66', 'heb', 3, 2, 'Который верен Поставившему Его, как и Моисей во всем доме Его.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39529, 'rst66', 'heb', 3, 1, 'Итак, братия святые, участники в небесном звании, уразумейте Посланника и Первосвященника исповедания нашего, Иисуса Христа,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39528, 'rst66', 'heb', 2, 18, 'Ибо, как Сам Он претерпел, быв искушен, то может и искушаемым помочь.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39527, 'rst66', 'heb', 2, 17, 'Посему Он должен был во всем уподобиться братиям, чтобы быть милостивым и верным первосвященником пред Богом, для умилостивления за грехи народа.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39526, 'rst66', 'heb', 2, 16, 'Ибо не Ангелов восприемлет Он, но восприемлет семя Авраамово.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39525, 'rst66', 'heb', 2, 15, 'и избавить тех, которые от страха смерти через всю жизнь были подвержены рабству.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39524, 'rst66', 'heb', 2, 14, 'А как дети причастны плоти и крови, то и Он также воспринял оные, дабы смертью лишить силы имеющего державу смерти, то есть диавола,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39523, 'rst66', 'heb', 2, 13, 'И еще: «Я буду уповать на Него». И еще: «вот Я и дети, которых дал Мне Бог».');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39522, 'rst66', 'heb', 2, 12, '«возвещу имя Твое братиям Моим, посреди церкви воспою Тебя».');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39521, 'rst66', 'heb', 2, 11, 'Ибо и освящающий и освящаемые, все &mdash; от Единого; поэтому Он не стыдится называть их братиями, говоря:');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39520, 'rst66', 'heb', 2, 10, 'Ибо надлежало, чтобы Тот, для Которого все и от Которого все, приводящего многих сынов в славу, вождя спасения их совершил через страдания.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39519, 'rst66', 'heb', 2, 9, 'но видим, что за претерпение смерти увенчан славою и честью Иисус, Который не много был унижен пред Ангелами, дабы Ему, по благодати Божией, вкусить смерть за всех.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39518, 'rst66', 'heb', 2, 8, 'все покорил под ноги его». Когда же покорил ему все, то не оставил ничего не покоренным ему. Ныне же еще не видим, чтобы все было ему покорено;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39517, 'rst66', 'heb', 2, 7, 'Не много Ты унизил его пред Ангелами; славою и честью увенчал его и поставил его над делами рук Твоих,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39516, 'rst66', 'heb', 2, 6, 'напротив, некто негде засвидетельствовал, говоря: «что значит человек, что Ты помнишь его? или сын человеческий, что Ты посещаешь его?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39515, 'rst66', 'heb', 2, 5, 'Ибо не Ангелам Бог покорил будущую вселенную, о которой говорим;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39514, 'rst66', 'heb', 2, 4, 'при засвидетельствовании от Бога знамениями и чудесами, и различными силами, и раздаянием Духа Святого по Его воле?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39513, 'rst66', 'heb', 2, 3, 'то как мы избежим, вознерадев о толиком спасении, которое, быв сначала проповедано Господом, в нас утвердилось слышавшими <i>от Него</i>,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39512, 'rst66', 'heb', 2, 2, 'Ибо, если через Ангелов возвещенное слово было твердо и всякое преступление и непослушание получало праведное воздаяние,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39511, 'rst66', 'heb', 2, 1, 'Посему мы должны быть особенно внимательны к слышанному, чтобы не отпасть.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39510, 'rst66', 'heb', 1, 14, 'Не все ли они суть служебные духи, посылаемые на служение для тех, которые имеют наследовать спасение?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39509, 'rst66', 'heb', 1, 13, 'Кому когда из Ангелов сказал <i>Бог:</i> «седи одесную Меня, доколе положу врагов Твоих в подножие ног Твоих»?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39508, 'rst66', 'heb', 1, 12, 'и как одежду свернешь их, и изменятся; но Ты тот же, и лета Твои не кончатся».');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39507, 'rst66', 'heb', 1, 11, 'они погибнут, а Ты пребываешь; и все обветшают, как риза,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39506, 'rst66', 'heb', 1, 10, 'И: «в начале Ты, Господи, основал землю, и небеса &mdash; дело рук Твоих;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39505, 'rst66', 'heb', 1, 9, 'Ты возлюбил правду и возненавидел беззаконие, посему помазал Тебя, Боже, Бог Твой елеем радости более соучастников Твоих».');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39504, 'rst66', 'heb', 1, 8, 'А о Сыне: «престол Твой, Боже, в век века; жезл царствия Твоего &mdash; жезл правоты.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39503, 'rst66', 'heb', 1, 7, 'Об Ангелах сказано: «Ты творишь Ангелами Своими духов и служителями Своими пламенеющий огонь».');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39502, 'rst66', 'heb', 1, 6, 'Также, когда вводит Первородного во вселенную, говорит: «и да поклонятся Ему все Ангелы Божии».');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39501, 'rst66', 'heb', 1, 5, 'Ибо кому когда из Ангелов сказал <i>Бог</i>: «Ты Сын Мой, Я ныне родил Тебя»? И еще: «Я буду Ему Отцом, и Он будет Мне Сыном»?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39500, 'rst66', 'heb', 1, 4, 'будучи столько превосходнее Ангелов, сколько славнейшее пред ними наследовал имя.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39499, 'rst66', 'heb', 1, 3, 'Сей, будучи сияние славы и образ ипостаси Его и держа все словом силы Своей, совершив Собою очищение грехов наших, воссел одесную [престола] величия на высоте,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39498, 'rst66', 'heb', 1, 2, 'в последние дни сии говорил нам в Сыне, Которого поставил наследником всего, чрез Которого и веки сотворил.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39497, 'rst66', 'heb', 1, 1, 'Бог, многократно и многообразно говоривший издревле отцам в пророках,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39496, 'rst66', 'phm', 1, 25, 'Благодать Господа нашего Иисуса Христа со духом вашим. Аминь.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39495, 'rst66', 'phm', 1, 24, 'Марк, Аристарх, Димас, Лука, сотрудники мои.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39494, 'rst66', 'phm', 1, 23, 'Приветствует тебя Епафрас, узник вместе со мною ради Христа Иисуса,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39493, 'rst66', 'phm', 1, 22, 'А вместе приготовь для меня и помещение; ибо надеюсь, что по молитвам вашим я буду дарован вам.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39492, 'rst66', 'phm', 1, 21, 'Надеясь на послушание твое, я написал к тебе, зная, что ты сделаешь и более, нежели говорю.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39491, 'rst66', 'phm', 1, 20, 'Так, брат, дай мне воспользоваться от тебя в Господе; успокой мое сердце в Господе.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39490, 'rst66', 'phm', 1, 19, 'Я, Павел, написал моею рукою: я заплачу&#769;; не говорю тебе о том, что ты и самим собою мне должен.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39489, 'rst66', 'phm', 1, 18, 'Если же он чем обидел тебя или должен, считай это на мне.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39488, 'rst66', 'phm', 1, 17, 'Итак, если ты имеешь общение со мною, то прими его, как меня.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39487, 'rst66', 'phm', 1, 16, 'не как уже раба, но выше раба, брата возлюбленного, особенно мне, а тем больше тебе, и по плоти и в Господе.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39486, 'rst66', 'phm', 1, 15, 'Ибо, может быть, он для того на время отлучился, чтобы тебе принять его навсегда,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39485, 'rst66', 'phm', 1, 14, 'но без твоего согласия ничего не хотел сделать, чтобы доброе дело твое было не вынужденно, а добровольно.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39484, 'rst66', 'phm', 1, 13, 'Я хотел при себе удержать его, дабы он вместо тебя послужил мне в узах <i>за</i> благовествование;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39483, 'rst66', 'phm', 1, 12, 'ты же прими его, как мое сердце.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39482, 'rst66', 'phm', 1, 11, 'он был некогда негоден для тебя, а теперь годен тебе и мне; я возвращаю его;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39481, 'rst66', 'phm', 1, 10, 'прошу тебя о сыне моем Онисиме, которого родил я в узах моих:');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39480, 'rst66', 'phm', 1, 9, 'по любви лучше прошу, не иной кто, как я, Павел старец, а теперь и узник Иисуса Христа;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39479, 'rst66', 'phm', 1, 8, 'Посему, имея великое во Христе дерзновение приказывать тебе, что должно,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39478, 'rst66', 'phm', 1, 7, 'Ибо мы имеем великую радость и утешение в любви твоей, потому что тобою, брат, успокоены сердца святых.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39477, 'rst66', 'phm', 1, 6, 'дабы общение веры твоей оказалось деятельным в познании всякого у вас добра во Христе Иисусе.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39476, 'rst66', 'phm', 1, 5, 'слыша о твоей любви и вере, которую имеешь к Господу Иисусу и ко всем святым,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39475, 'rst66', 'phm', 1, 4, 'Благодарю Бога моего, всегда вспоминая о тебе в молитвах моих,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39474, 'rst66', 'phm', 1, 3, 'благодать вам и мир от Бога Отца нашего и Господа Иисуса Христа.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39473, 'rst66', 'phm', 1, 2, 'и Апфии, [сестре] возлюбленной, и Архиппу, сподвижнику нашему, и домашней твоей церкви:');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39472, 'rst66', 'phm', 1, 1, 'Павел, узник Иисуса Христа, и Тимофей брат &mdash; Филимону возлюбленному и сотруднику нашему,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39471, 'rst66', 'tit', 3, 15, 'Приветствуют тебя все находящиеся со мною. Приветствуй любящих нас в вере. Благодать со всеми вами. Аминь.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39470, 'rst66', 'tit', 3, 14, 'Пусть и наши учатся упражняться в добрых делах, <i>в удовлетворении</i> необходимым нуждам, дабы не были бесплодны.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39469, 'rst66', 'tit', 3, 13, 'Зину законника и Аполлоса позаботься отправить так, чтобы у них ни в чем не было недостатка.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39468, 'rst66', 'tit', 3, 12, 'Когда пришлю к тебе Артему или Тихика, поспеши прийти ко мне в Никополь, ибо я положил там провести зиму.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39467, 'rst66', 'tit', 3, 11, 'зная, что таковой развратился и грешит, будучи самоосужден.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39466, 'rst66', 'tit', 3, 10, 'Еретика, после первого и второго вразумления, отвращайся,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39465, 'rst66', 'tit', 3, 9, 'Глупых же состязаний и родословий, и споров и распрей о законе удаляйся, ибо они бесполезны и суетны.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39464, 'rst66', 'tit', 3, 8, 'Слово это верно; и я желаю, чтобы ты подтверждал о сем, дабы уверовавшие в Бога старались быть прилежными к добрым делам: это хорошо и полезно человекам.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39463, 'rst66', 'tit', 3, 7, 'чтобы, оправдавшись Его благодатью, мы по упованию соделались наследниками вечной жизни.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39462, 'rst66', 'tit', 3, 6, 'Которого излил на нас обильно через Иисуса Христа, Спасителя нашего,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39461, 'rst66', 'tit', 3, 5, 'Он спас нас не по делам праведности, которые бы мы сотворили, а по Своей милости, банею возрождения и обновления Святым Духом,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39460, 'rst66', 'tit', 3, 4, 'Когда же явилась благодать и человеколюбие Спасителя нашего, Бога,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39459, 'rst66', 'tit', 3, 3, 'Ибо и мы были некогда несмысленны, непокорны, заблудшие, были рабы похотей и различных удовольствий, жили в злобе и зависти, были гнусны, ненавидели друг друга.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39458, 'rst66', 'tit', 3, 2, 'никого не злословить, быть не сварливыми, но тихими, и оказывать всякую кротость ко всем человекам.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39457, 'rst66', 'tit', 3, 1, 'Напоминай им повиноваться и покоряться начальству и властям, быть готовыми на всякое доброе дело,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39456, 'rst66', 'tit', 2, 15, 'Сие говори, увещевай и обличай со всякою властью, чтобы никто не пренебрегал тебя.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39455, 'rst66', 'tit', 2, 14, 'Который дал Себя за нас, чтобы избавить нас от всякого беззакония и очистить Себе народ особенный, ревностный к добрым делам.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39454, 'rst66', 'tit', 2, 13, 'ожидая блаженного упования и явления славы великого Бога и Спасителя нашего Иисуса Христа,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39453, 'rst66', 'tit', 2, 12, 'научающая нас, чтобы мы, отвергнув нечестие и мирские похоти, целомудренно, праведно и благочестиво жили в нынешнем веке,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39452, 'rst66', 'tit', 2, 11, 'Ибо явилась благодать Божия, спасительная для всех человеков,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39451, 'rst66', 'tit', 2, 10, 'не красть, но оказывать всю добрую верность, дабы они во всем были украшением учению Спасителя нашего, Бога.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39450, 'rst66', 'tit', 2, 9, 'Рабов <i>увещевай</i> повиноваться своим господам, угождать им во всем, не прекословить,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39449, 'rst66', 'tit', 2, 8, 'слово здравое, неукоризненное, чтобы противник был посрамлен, не имея ничего сказать о нас худого.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39448, 'rst66', 'tit', 2, 7, 'Во всем показывай в себе образец добрых дел, в учительстве &mdash; чистоту, степенность, неповрежденность,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39447, 'rst66', 'tit', 2, 6, 'Юношей также увещевай быть целомудренными.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39446, 'rst66', 'tit', 2, 5, 'быть целомудренными, чистыми, попечительными о доме, добрыми, покорными своим мужьям, да не порицается слово Божие.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39445, 'rst66', 'tit', 2, 4, 'чтобы вразумляли молодых любить мужей, любить детей,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39444, 'rst66', 'tit', 2, 3, 'чтобы старицы также одевались прилично святым, не были клеветницы, не порабощались пьянству, учили добру;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39443, 'rst66', 'tit', 2, 2, 'Чтобы старцы были бдительны, степенны, целомудренны, здравы в вере, в любви, в терпении;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39442, 'rst66', 'tit', 2, 1, 'Ты же говори то, что сообразно с здравым учением:');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39441, 'rst66', 'tit', 1, 16, 'Они говорят, что знают Бога, а делами отрекаются, будучи гнусны и непокорны и не способны ни к какому доброму делу.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39440, 'rst66', 'tit', 1, 15, 'Для чистых все чисто; а для оскверненных и неверных нет ничего чистого, но осквернены и ум их и совесть.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39439, 'rst66', 'tit', 1, 14, 'не внимая Иудейским басням и постановлениям людей, отвращающихся от истины.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39438, 'rst66', 'tit', 1, 13, 'Свидетельство это справедливо. По сей причине обличай их строго, дабы они были здравы в вере,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39437, 'rst66', 'tit', 1, 12, 'Из них же самих один стихотворец сказал: «Критяне всегда лжецы, злые звери, утробы ленивые».');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39436, 'rst66', 'tit', 1, 11, 'каковым должно заграждать уста: они развращают целые дома, уча, чему не должно, из постыдной корысти.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39435, 'rst66', 'tit', 1, 10, 'Ибо есть много и непокорных, пустословов и обманщиков, особенно из обрезанных,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39434, 'rst66', 'tit', 1, 9, 'держащийся истинного слова, согласного с учением, чтобы он был силен и наставлять в здравом учении, и противящихся обличать.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39433, 'rst66', 'tit', 1, 8, 'но страннолюбив, любящий добро, целомудрен, справедлив, благочестив, воздержан,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39432, 'rst66', 'tit', 1, 7, 'Ибо епископ должен быть непорочен, как Божий домостроитель, не дерзок, не гневлив, не пьяница, не бийца, не корыстолюбец,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39431, 'rst66', 'tit', 1, 6, 'если кто непорочен, муж одной жены, детей имеет верных, не укоряемых в распутстве или непокорности.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39430, 'rst66', 'tit', 1, 5, 'Для того я оставил тебя в Крите, чтобы ты довершил недоконченное и поставил по всем городам пресвитеров, как я тебе приказывал:');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39429, 'rst66', 'tit', 1, 4, 'Титу, истинному сыну по общей вере: благодать, милость и мир от Бога Отца и Господа Иисуса Христа, Спасителя нашего.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39428, 'rst66', 'tit', 1, 3, 'а в свое время явил Свое слово в проповеди, вверенной мне по повелению Спасителя нашего, Бога, &mdash;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39427, 'rst66', 'tit', 1, 2, 'в надежде вечной жизни, которую обещал неизменный в слове Бог прежде вековых времен,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39426, 'rst66', 'tit', 1, 1, 'Павел, раб Божий, Апостол же Иисуса Христа, по вере избранных Божиих и познанию истины, <i>относящейся</i> к благочестию,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39425, 'rst66', '2ti', 4, 22, 'Господь Иисус Христос со духом твоим. Благодать с вами. Аминь.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39424, 'rst66', '2ti', 4, 21, 'Постарайся прийти до зимы. Приветствуют тебя Еввул, и Пуд, и Лин, и Клавдия, и все братия.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39423, 'rst66', '2ti', 4, 20, 'Ераст остался в Коринфе; Трофима же я оставил больного в Милите.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39422, 'rst66', '2ti', 4, 19, 'Приветствуй Прискиллу и Акилу и дом Онисифоров.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39421, 'rst66', '2ti', 4, 18, 'И избавит меня Господь от всякого злого дела и сохранит для Своего Небесного Царства, Ему слава во веки веков. Аминь.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39420, 'rst66', '2ti', 4, 17, 'Господь же предстал мне и укрепил меня, дабы через меня утвердилось благовестие и услышали все язычники; и я избавился из львиных челюстей.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39419, 'rst66', '2ti', 4, 16, 'При первом моем ответе никого не было со мною, но все меня оставили. Да не вменится им!');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39418, 'rst66', '2ti', 4, 15, 'Берегись его и ты, ибо он сильно противился нашим словам.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39417, 'rst66', '2ti', 4, 14, 'Александр медник много сделал мне зла. Да воздаст ему Господь по делам его!');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39416, 'rst66', '2ti', 4, 13, 'Когда пойдешь, принеси фелонь, который я оставил в Троаде у Карпа, и книги, особенно кожаные.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39415, 'rst66', '2ti', 4, 12, 'Тихика я послал в Ефес.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39414, 'rst66', '2ti', 4, 11, 'Марка возьми и приведи с собою, ибо он мне нужен для служения.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39413, 'rst66', '2ti', 4, 10, 'Ибо Димас оставил меня, возлюбив нынешний век, и пошел в Фессалонику, Крискент &mdash; в Галатию, Тит &mdash; в Далматию; один Лука со мною.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39412, 'rst66', '2ti', 4, 9, 'Постарайся прийти ко мне скоро.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39411, 'rst66', '2ti', 4, 8, 'а теперь готовится мне венец правды, который даст мне Господь, праведный Судия, в день оный; и не только мне, но и всем, возлюбившим явление Его.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39410, 'rst66', '2ti', 4, 7, 'Подвигом добрым я подвизался, течение совершил, веру сохранил;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39409, 'rst66', '2ti', 4, 6, 'Ибо я уже становлюсь жертвою, и время моего отшествия настало.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39408, 'rst66', '2ti', 4, 5, 'Но ты будь бдителен во всем, переноси скорби, совершай дело благовестника, исполняй служение твое.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39407, 'rst66', '2ti', 4, 4, 'и от истины отвратят слух и обратятся к басням.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39406, 'rst66', '2ti', 4, 3, 'Ибо будет время, когда здравого учения принимать не будут, но по своим прихотям будут избирать себе учителей, которые льстили бы слуху;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39405, 'rst66', '2ti', 4, 2, 'проповедуй слово, настой во время и не во время, обличай, запрещай, увещевай со всяким долготерпением и назиданием.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39404, 'rst66', '2ti', 4, 1, 'Итак, заклинаю тебя пред Богом и Господом нашим Иисусом Христом, Который будет судить живых и мертвых в явление Его и Царствие Его:');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39403, 'rst66', '2ti', 3, 17, 'да будет совершен Божий человек, ко всякому доброму делу приготовлен.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39402, 'rst66', '2ti', 3, 16, 'Все Писание богодухновенно и полезно для научения, для обличения, для исправления, для наставления в праведности,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39401, 'rst66', '2ti', 3, 15, 'Притом же ты из детства знаешь священные писания, которые могут умудрить тебя во спасение верою во Христа Иисуса.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39400, 'rst66', '2ti', 3, 14, 'А ты пребывай в том, чему научен и что тебе вверено, зная, кем ты научен.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39399, 'rst66', '2ti', 3, 13, 'Злые же люди и обманщики будут преуспевать во зле, вводя в заблуждение и заблуждаясь.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39398, 'rst66', '2ti', 3, 12, 'Да и все, желающие жить благочестиво во Христе Иисусе, будут гонимы.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39397, 'rst66', '2ti', 3, 11, 'в гонениях, страданиях, постигших меня в Антиохии, Иконии, Листрах; каковые гонения я перенес, и от всех избавил меня Господь.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39396, 'rst66', '2ti', 3, 10, 'А ты последовал мне в учении, житии, расположении, вере, великодушии, любви, терпении,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39395, 'rst66', '2ti', 3, 9, 'Но они не много успеют; ибо их безумие обнаружится перед всеми, как и с теми случилось.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39394, 'rst66', '2ti', 3, 8, 'Как Ианний и Иамврий противились Моисею, так и сии противятся истине, люди, развращенные умом, невежды в вере.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39393, 'rst66', '2ti', 3, 7, 'всегда учащихся и никогда не могущих дойти до познания истины.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39392, 'rst66', '2ti', 3, 6, 'К сим принадлежат те, которые вкрадываются в дома и обольщают женщин, утопающих во грехах, водимых различными похотями,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39391, 'rst66', '2ti', 3, 5, 'имеющие вид благочестия, силы же его отрекшиеся. Таковых удаляйся.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39390, 'rst66', '2ti', 3, 4, 'предатели, наглы, напыщенны, более сластолюбивы, нежели боголюбивы,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39389, 'rst66', '2ti', 3, 3, 'непримирительны, клеветники, невоздержны, жестоки, не любящие добра,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39388, 'rst66', '2ti', 3, 2, 'Ибо люди будут самолюбивы, сребролюбивы, горды, надменны, злоречивы, родителям непокорны, неблагодарны, нечестивы, недружелюбны,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39387, 'rst66', '2ti', 3, 1, 'Знай же, что в последние дни наступят времена тяжкие.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39386, 'rst66', '2ti', 2, 26, 'чтобы они освободились от сети диавола, который уловил их в свою волю.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39385, 'rst66', '2ti', 2, 25, 'с кротостью наставлять противников, не даст ли им Бог покаяния к познанию истины,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39384, 'rst66', '2ti', 2, 24, 'рабу же Господа не должно ссориться, но быть приветливым ко всем, учительным, незлобивым,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39383, 'rst66', '2ti', 2, 23, 'От глупых и невежественных состязаний уклоняйся, зная, что они рождают ссоры;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39382, 'rst66', '2ti', 2, 22, 'Юношеских похотей убегай, а держись правды, веры, любви, мира со всеми призывающими Господа от чистого сердца.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39381, 'rst66', '2ti', 2, 21, 'Итак, кто будет чист от сего, тот будет сосудом в чести, освященным и благопотребным Владыке, годным на всякое доброе дело.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39380, 'rst66', '2ti', 2, 20, 'А в большом доме есть сосуды не только золотые и серебряные, но и деревянные и глиняные; и одни в почетном, а другие в низком употреблении.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39379, 'rst66', '2ti', 2, 19, 'Но твердое основание Божие стои&#769;т, имея печать сию: «познал Господь Своих»; и: «да отступит от неправды всякий, исповедующий имя Господа».');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39378, 'rst66', '2ti', 2, 18, 'которые отступили от истины, говоря, что воскресение уже было, и разрушают в некоторых веру.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39377, 'rst66', '2ti', 2, 17, 'и слово их, как рак, будет распространяться. Таковы Именей и Филит,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39376, 'rst66', '2ti', 2, 16, 'А непотребного пустословия удаляйся; ибо они еще более будут преуспевать в нечестии,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39375, 'rst66', '2ti', 2, 15, 'Старайся представить себя Богу достойным, делателем неукоризненным, верно преподающим слово истины.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39374, 'rst66', '2ti', 2, 14, 'Сие напоминай, заклиная пред Господом не вступать в словопрения, что нимало не служит к пользе, а к расстройству слушающих.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39373, 'rst66', '2ti', 2, 13, 'если мы неверны, Он пребывает верен, ибо Себя отречься не может.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39372, 'rst66', '2ti', 2, 12, 'если терпим, то с Ним и царствовать будем; если отречемся, и Он отречется от нас;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39371, 'rst66', '2ti', 2, 11, 'Верно слово: если мы с Ним умерли, то с Ним и оживем;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39370, 'rst66', '2ti', 2, 10, 'Посему я все терплю ради избранных, дабы и они получили спасение во Христе Иисусе с вечною славою.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39369, 'rst66', '2ti', 2, 9, 'за которое я страдаю даже до уз, как злодей; но для слова Божия нет уз.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39368, 'rst66', '2ti', 2, 8, 'Помни Господа Иисуса Христа от семени Давидова, воскресшего из мертвых, по благовествованию моему,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39367, 'rst66', '2ti', 2, 7, 'Разумей, что я говорю. Да даст тебе Господь разумение во всем.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39366, 'rst66', '2ti', 2, 6, 'Трудящемуся земледельцу первому должно вкусить от плодов.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39365, 'rst66', '2ti', 2, 5, 'Если же кто и подвизается, не увенчивается, если незаконно будет подвизаться.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39364, 'rst66', '2ti', 2, 4, 'Никакой воин не связывает себя делами житейскими, чтобы угодить военачальнику.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39363, 'rst66', '2ti', 2, 3, 'Итак, переноси страдания, как добрый воин Иисуса Христа.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39362, 'rst66', '2ti', 2, 2, 'и что слышал от меня при многих свидетелях, то передай верным людям, которые были бы способны и других научить.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39361, 'rst66', '2ti', 2, 1, 'Итак, укрепляйся, сын мой, в благодати Христом Иисусом,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39360, 'rst66', '2ti', 1, 18, 'Да даст ему Господь обрести милость у Господа в оный день; а сколько он служил мне в Ефесе, ты лучше знаешь.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39359, 'rst66', '2ti', 1, 17, 'но, быв в Риме, с великим тщанием искал меня и нашел.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39358, 'rst66', '2ti', 1, 16, 'Да даст Господь милость дому Онисифора за то, что он многократно покоил меня и не стыдился уз моих,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39357, 'rst66', '2ti', 1, 15, 'Ты знаешь, что все Асийские оставили меня; в числе их Фигелл и Ермоген.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39356, 'rst66', '2ti', 1, 14, 'Храни добрый залог Духом Святым, живущим в нас.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39355, 'rst66', '2ti', 1, 13, 'Держись образца здравого учения, которое ты слышал от меня, с верою и любовью во Христе Иисусе.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39354, 'rst66', '2ti', 1, 12, 'По сей причине я и страдаю так; но не стыжусь. Ибо я знаю, в Кого уверовал, и уверен, что Он силен сохранить залог мой на оный день.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39353, 'rst66', '2ti', 1, 11, 'для которого я поставлен проповедником и Апостолом и учителем язычников.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39352, 'rst66', '2ti', 1, 10, 'открывшейся же ныне явлением Спасителя нашего Иисуса Христа, разрушившего смерть и явившего жизнь и нетление через благовестие,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39351, 'rst66', '2ti', 1, 9, 'спасшего нас и призвавшего званием святым, не по делам нашим, но по Своему изволению и благодати, данной нам во Христе Иисусе прежде вековых времен,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39350, 'rst66', '2ti', 1, 8, 'Итак, не стыдись свидетельства Господа нашего Иисуса Христа, ни меня, узника Его; но страдай с благовестием Христовым силою Бога,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39349, 'rst66', '2ti', 1, 7, 'ибо дал нам Бог духа не боязни, но силы и любви и целомудрия.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39348, 'rst66', '2ti', 1, 6, 'По сей причине напоминаю тебе возгревать дар Божий, который в тебе через мое рукоположение;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39347, 'rst66', '2ti', 1, 5, 'приводя на память нелицемерную веру твою, которая прежде обитала в бабке твоей Лоиде и матери твоей Евнике; уверен, что она и в тебе.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39346, 'rst66', '2ti', 1, 4, 'и желаю видеть тебя, вспоминая о слезах твоих, дабы мне исполниться радости,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39345, 'rst66', '2ti', 1, 3, 'Благодарю Бога, Которому служу от прародителей с чистою совестью, что непрестанно вспоминаю о тебе в молитвах моих днем и ночью,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39344, 'rst66', '2ti', 1, 2, 'Тимофею, возлюбленному сыну: благодать, милость, мир от Бога Отца и Христа Иисуса, Господа нашего.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39343, 'rst66', '2ti', 1, 1, 'Павел, волею Божиею Апостол Иисуса Христа, по обетованию жизни во Христе Иисусе,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39342, 'rst66', '1ti', 6, 21, 'которому предавшись, некоторые уклонились от веры. Благодать с тобою. Аминь.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39341, 'rst66', '1ti', 6, 20, 'О, Тимофей! храни преданное тебе, отвращаясь негодного пустословия и прекословий лжеименного знания,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39340, 'rst66', '1ti', 6, 19, 'собирая себе сокровище, доброе основание для будущего, чтобы достигнуть вечной жизни.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39339, 'rst66', '1ti', 6, 18, 'чтобы они благодетельствовали, богатели добрыми делами, были щедры и общительны,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39338, 'rst66', '1ti', 6, 17, 'Богатых в настоящем веке увещевай, чтобы они не высоко думали <i>о себе</i> и уповали не на богатство неверное, но на Бога живого, дающего нам всё обильно для наслаждения;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39337, 'rst66', '1ti', 6, 16, 'единый имеющий бессмертие, Который обитает в неприступном свете, Которого никто из человеков не видел и видеть не может. Ему честь и держава вечная! Аминь.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39336, 'rst66', '1ti', 6, 15, 'которое в свое время откроет блаженный и единый сильный Царь царствующих и Господь господствующих,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39335, 'rst66', '1ti', 6, 14, 'соблюсти заповедь чисто и неукоризненно, даже до явления Господа нашего Иисуса Христа,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39334, 'rst66', '1ti', 6, 13, 'Пред Богом, все животворящим, и пред Христом Иисусом, Который засвидетельствовал пред Понтием Пилатом доброе исповедание, завещеваю тебе');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39333, 'rst66', '1ti', 6, 12, 'Подвизайся добрым подвигом веры, держись вечной жизни, к которой ты и призван, и исповедал доброе исповедание перед многими свидетелями.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39332, 'rst66', '1ti', 6, 11, 'Ты же, человек Божий, убегай сего, а преуспевай в правде, благочестии, вере, любви, терпении, кротости.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39331, 'rst66', '1ti', 6, 10, 'ибо корень всех зол есть сребролюбие, которому предавшись, некоторые уклонились от веры и сами себя подвергли многим скорбям.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39330, 'rst66', '1ti', 6, 9, 'А желающие обогащаться впадают в искушение и в сеть и во многие безрассудные и вредные похоти, которые погружают людей в бедствие и пагубу;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39329, 'rst66', '1ti', 6, 8, 'Имея пропитание и одежду, будем довольны тем.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39328, 'rst66', '1ti', 6, 7, 'Ибо мы ничего не принесли в мир; явно, что ничего не можем и вынести <i>из него</i>.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39327, 'rst66', '1ti', 6, 6, 'Великое приобретение &mdash; быть благочестивым и довольным.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39326, 'rst66', '1ti', 6, 5, 'Пустые споры между людьми поврежденного ума, чуждыми истины, которые думают, будто благочестие служит для прибытка. Удаляйся от таких.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39325, 'rst66', '1ti', 6, 4, 'тот горд, ничего не знает, но заражен <i>страстью</i> к состязаниям и словопрениям, от которых происходят зависть, распри, злоречия, лукавые подозрения.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39324, 'rst66', '1ti', 6, 3, 'Кто учит иному и не следует здравым словам Господа нашего Иисуса Христа и учению о благочестии,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39323, 'rst66', '1ti', 6, 2, 'Те, которые имеют господами верных, не должны обращаться с ними небрежно, потому что они братья; но тем более должны служить им, что они верные и возлюбленные и благодетельствуют <i>им</i>. Учи сему и увещевай.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39322, 'rst66', '1ti', 6, 1, 'Рабы, под игом находящиеся, должны почитать господ своих достойными всякой чести, дабы не было хулы на имя Божие и учение.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39321, 'rst66', '1ti', 5, 25, 'Равным образом и добрые дела явны; а если и не таковы, скрыться не могут.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39320, 'rst66', '1ti', 5, 24, 'Грехи некоторых людей явны и прямо ведут к осуждению, а некоторых <i>открываются</i> впоследствии.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39319, 'rst66', '1ti', 5, 23, 'Впредь пей не <i>одну</i> воду, но употребляй немного вина, ради желудка твоего и частых твоих недугов.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39318, 'rst66', '1ti', 5, 22, 'Рук ни на кого не возлагай поспешно и не делайся участником в чужих грехах. Храни себя чистым.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39317, 'rst66', '1ti', 5, 21, 'Пред Богом и Господом Иисусом Христом и избранными Ангелами заклинаю тебя сохранить сие без предубеждения, ничего не делая по пристрастию.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39316, 'rst66', '1ti', 5, 20, 'Согрешающих обличай перед всеми, чтобы и прочие страх имели.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39315, 'rst66', '1ti', 5, 19, 'Обвинение на пресвитера не иначе принимай, как при двух или трех свидетелях.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39314, 'rst66', '1ti', 5, 18, 'Ибо Писание говорит: «не заграждай рта у вола молотящего»; и: «трудящийся достоин награды своей».');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39313, 'rst66', '1ti', 5, 17, 'Достойно начальствующим пресвитерам до&#769;лжно оказывать сугубую честь, особенно тем, которые трудятся в слове и учении.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39312, 'rst66', '1ti', 5, 16, 'Если какой верный или верная имеет вдов, то должны их довольствовать и не обременять Церкви, чтобы она могла довольствовать истинных вдовиц.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39311, 'rst66', '1ti', 5, 15, 'ибо некоторые уже совратились вслед сатаны.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39310, 'rst66', '1ti', 5, 14, 'Итак, я желаю, чтобы молодые вдовы вступали в брак, рождали детей, управляли домом и не подавали противнику никакого повода к злоречию;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39309, 'rst66', '1ti', 5, 13, 'притом же они, будучи праздны, приучаются ходить по домам и <i>бывают</i> не только праздны, но и болтливы, любопытны и говорят, чего не должно.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39308, 'rst66', '1ti', 5, 12, 'Они подлежат осуждению, потому что отвергли прежнюю веру;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39307, 'rst66', '1ti', 5, 11, 'Молодых же вдовиц не принимай, ибо они, впадая в роскошь в противность Христу, желают вступать в брак.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39306, 'rst66', '1ti', 5, 10, 'известная по добрым делам, если она воспитала детей, принимала странников, умывала ноги святым, помогала бедствующим и была усердна ко всякому доброму делу.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39305, 'rst66', '1ti', 5, 9, 'Вдовица должна быть избираема не менее как шестидесятилетняя, бывшая женою одного мужа,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39304, 'rst66', '1ti', 5, 8, 'Если же кто о своих и особенно о домашних не печется, тот отрекся от веры и хуже неверного.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39303, 'rst66', '1ti', 5, 7, 'И сие внушай им, чтобы были беспорочны.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39302, 'rst66', '1ti', 5, 6, 'а сластолюбивая заживо умерла.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39301, 'rst66', '1ti', 5, 5, 'Истинная вдовица и одинокая надеется на Бога и пребывает в молениях и молитвах день и ночь;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39300, 'rst66', '1ti', 5, 4, 'Если же какая вдовица имеет детей или внучат, то они прежде пусть учатся почитать свою семью и воздавать должное родителям, ибо сие угодно Богу.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39299, 'rst66', '1ti', 5, 3, 'Вдовиц почитай, истинных вдовиц.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39298, 'rst66', '1ti', 5, 2, 'стариц &mdash; как матерей; молодых &mdash; как сестер, со всякою чистотою.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39297, 'rst66', '1ti', 5, 1, 'Старца не укоряй, но увещевай, как отца; младших &mdash; как братьев;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39296, 'rst66', '1ti', 4, 16, 'Вникай в себя и в учение; занимайся сим постоянно: ибо, так поступая, и себя спасешь, и слушающих тебя.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39295, 'rst66', '1ti', 4, 15, 'О сем заботься, в сем пребывай, дабы успех твой для всех был очевиден.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39294, 'rst66', '1ti', 4, 14, 'Не неради о пребывающем в тебе даровании, которое дано тебе по пророчеству с возложением рук священства.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39293, 'rst66', '1ti', 4, 13, 'Доколе не приду, занимайся чтением, наставлением, учением.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39292, 'rst66', '1ti', 4, 12, 'Никто да не пренебрегает юностью твоею; но будь образцом для верных в слове, в житии, в любви, в духе, в вере, в чистоте.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39291, 'rst66', '1ti', 4, 11, 'Проповедуй сие и учи.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39290, 'rst66', '1ti', 4, 10, 'Ибо мы для того и трудимся и поношения терпим, что уповаем на Бога живого, Который есть Спаситель всех человеков, а наипаче верных.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39289, 'rst66', '1ti', 4, 9, 'Слово сие верно и всякого принятия достойно.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39288, 'rst66', '1ti', 4, 8, 'ибо телесное упражнение мало полезно, а благочестие на все полезно, имея обетование жизни настоящей и будущей.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39287, 'rst66', '1ti', 4, 7, 'Негодных же и бабьих басен отвращайся, а упражняй себя в благочестии,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39286, 'rst66', '1ti', 4, 6, 'Внушая сие братиям, будешь добрый служитель Иисуса Христа, питаемый словами веры и добрым учением, которому ты последовал.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39285, 'rst66', '1ti', 4, 5, 'потому что освящается словом Божиим и молитвою.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39284, 'rst66', '1ti', 4, 4, 'Ибо всякое творение Божие хорошо, и ничто не предосудительно, если принимается с благодарением,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39283, 'rst66', '1ti', 4, 3, 'запрещающих вступать в брак <i>и</i> употреблять в пищу то, что Бог сотворил, дабы верные и познавшие истину вкушали с благодарением.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39282, 'rst66', '1ti', 4, 2, 'через лицемерие лжесловесников, сожженных в совести своей,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39281, 'rst66', '1ti', 4, 1, 'Дух же ясно говорит, что в последние времена отступят некоторые от веры, внимая духам‐обольстителям и учениям бесовским,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39280, 'rst66', '1ti', 3, 16, 'И беспрекословно &mdash; великая благочестия тайна: Бог явился во плоти, оправдал Себя в Духе, показал Себя Ангелам, проповедан в народах, принят верою в мире, вознесся во славе.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39279, 'rst66', '1ti', 3, 15, 'чтобы, если замедлю, ты знал, как должно поступать в доме Божием, который есть Церковь Бога живого, столп и утверждение истины.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39278, 'rst66', '1ti', 3, 14, 'Сие пишу тебе, надеясь вскоре прийти к тебе,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39277, 'rst66', '1ti', 3, 13, 'Ибо хорошо служившие приготовляют себе высшую степень и великое дерзновение в вере во Христа Иисуса.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39276, 'rst66', '1ti', 3, 12, 'Диакон должен быть муж одной жены, хорошо управляющий детьми и домом своим.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39275, 'rst66', '1ti', 3, 11, 'Равно и жены <i>их должны быть</i> честны, не клеветницы, трезвы, верны во всем.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39274, 'rst66', '1ti', 3, 10, 'И таких надобно прежде испытывать, потом, если беспорочны, <i>допускать</i> до служения.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39273, 'rst66', '1ti', 3, 9, 'хранящие таинство веры в чистой совести.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39272, 'rst66', '1ti', 3, 8, 'Диаконы также <i>должны быть</i> честны, не двоязычны, не пристрастны к вину, не корыстолюбивы,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39271, 'rst66', '1ti', 3, 7, 'Надлежит ему также иметь доброе свидетельство от внешних, чтобы не впасть в нарекание и сеть диавольскую.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39270, 'rst66', '1ti', 3, 6, 'Не <i>должен быть</i> из новообращенных, чтобы не возгордился и не подпал осуждению с диаволом.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39269, 'rst66', '1ti', 3, 5, 'ибо кто не умеет управлять собственным домом, тот будет ли пещись о Церкви Божией?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39268, 'rst66', '1ti', 3, 4, 'хорошо управляющий домом своим, детей содержащий в послушании со всякою честностью;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39267, 'rst66', '1ti', 3, 3, 'не пьяница, не бийца, не сварлив, не корыстолюбив, но тих, миролюбив, не сребролюбив,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39266, 'rst66', '1ti', 3, 2, 'Но епископ должен быть непорочен, одной жены муж, трезв, целомудрен, благочинен, честен, страннолюбив, учителен,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39265, 'rst66', '1ti', 3, 1, 'Верно слово: если кто епископства желает, доброго дела желает.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39264, 'rst66', '1ti', 2, 15, 'впрочем, спасется через чадородие, если пребудет в вере и любви и в святости с целомудрием.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39263, 'rst66', '1ti', 2, 14, 'и не Адам прельщен; но жена, прельстившись, впала в преступление;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39262, 'rst66', '1ti', 2, 13, 'Ибо прежде создан Адам, а потом Ева;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39261, 'rst66', '1ti', 2, 12, 'а учить жене не позволяю, ни властвовать над мужем, но быть в безмолвии.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39260, 'rst66', '1ti', 2, 11, 'Жена да учится в безмолвии, со всякою покорностью;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39259, 'rst66', '1ti', 2, 10, 'но добрыми делами, как прилично женам, посвящающим себя благочестию.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39258, 'rst66', '1ti', 2, 9, 'чтобы также и жены, в приличном одеянии, со стыдливостью и целомудрием, украшали себя не плетением <i>волос</i>, не золотом, не жемчугом, не многоценною одеждою,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39257, 'rst66', '1ti', 2, 8, 'Итак, желаю, чтобы на всяком месте произносили молитвы мужи, воздевая чистые руки без гнева и сомнения;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39256, 'rst66', '1ti', 2, 7, 'для которого я поставлен проповедником и Апостолом, &mdash; истину говорю во Христе, не лгу, &mdash; учителем язычников в вере и истине.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39255, 'rst66', '1ti', 2, 6, 'предавший Себя для искупления всех. <i>Таково было</i> в свое время свидетельство,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39254, 'rst66', '1ti', 2, 5, 'Ибо един Бог, един и посредник между Богом и человеками, человек Христос Иисус,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39253, 'rst66', '1ti', 2, 4, 'Который хочет, чтобы все люди спаслись и достигли познания истины.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39252, 'rst66', '1ti', 2, 3, 'ибо это хорошо и угодно Спасителю нашему Богу,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39251, 'rst66', '1ti', 2, 2, 'за царей и за всех начальствующих, дабы проводить нам жизнь тихую и безмятежную во всяком благочестии и чистоте,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39250, 'rst66', '1ti', 2, 1, 'Итак, прежде всего прошу совершать молитвы, прошения, моления, благодарения за всех человеков,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39249, 'rst66', '1ti', 1, 20, 'таковы Именей и Александр, которых я предал сатане, чтобы они научились не богохульствовать.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39248, 'rst66', '1ti', 1, 19, 'имея веру и добрую совесть, которую некоторые отвергнув, потерпели кораблекрушение в вере;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39247, 'rst66', '1ti', 1, 18, 'Преподаю тебе, сын <i>мой</i> Тимофей, сообразно с бывшими о тебе пророчествами, такое завещание, чтобы ты воинствовал согласно с ними, как добрый воин,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39246, 'rst66', '1ti', 1, 17, 'Царю же веков нетленному, невидимому, единому премудрому Богу честь и слава во веки веков. Аминь.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39245, 'rst66', '1ti', 1, 16, 'Но для того я и помилован, чтобы Иисус Христос во мне первом показал все долготерпение, в пример тем, которые будут веровать в Него к жизни вечной.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39244, 'rst66', '1ti', 1, 15, 'Верно и всякого принятия достойно слово, что Христос Иисус пришел в мир спасти грешников, из которых я первый.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39243, 'rst66', '1ti', 1, 14, 'благодать же Господа нашего [Иисуса Христа] открылась <i>во мне</i> обильно с верою и любовью во Христе Иисусе.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39242, 'rst66', '1ti', 1, 13, 'меня, который прежде был хулитель и гонитель и обидчик, но помилован потому, что <i>так</i> поступал по неведению, в неверии;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39241, 'rst66', '1ti', 1, 12, 'Благодарю давшего мне силу, Христа Иисуса, Господа нашего, что Он признал меня верным, определив на служение,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39240, 'rst66', '1ti', 1, 11, 'по славному благовестию блаженного Бога, которое мне вверено.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39239, 'rst66', '1ti', 1, 10, 'для блудников, мужеложников, человекохищников, [клеветников, скотоложников,] лжецов, клятвопреступников и для всего, что противно здравому учению,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39238, 'rst66', '1ti', 1, 9, 'зная, что закон положен не для праведника, но для беззаконных и непокоривых, нечестивых и грешников, развратных и оскверненных, для оскорбителей отца и матери, для человекоубийц,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39237, 'rst66', '1ti', 1, 8, 'А мы знаем, что закон добр, если кто законно употребляет его,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39236, 'rst66', '1ti', 1, 7, 'желая быть законоучителями, но не разумея ни того, о чем говорят, ни того, что утверждают.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39235, 'rst66', '1ti', 1, 6, 'от чего отступив, некоторые уклонились в пустословие,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39234, 'rst66', '1ti', 1, 5, 'Цель же увещания есть любовь от чистого сердца и доброй совести и нелицемерной веры,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39233, 'rst66', '1ti', 1, 4, 'и не занимались баснями и родословиями бесконечными, которые производят больше споры, нежели Божие назидание в вере.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39232, 'rst66', '1ti', 1, 3, 'Отходя в Македонию, я просил тебя пребыть в Ефесе и увещевать некоторых, чтобы они не учили иному');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39231, 'rst66', '1ti', 1, 2, 'Тимофею, истинному сыну в вере: благодать, милость, мир от Бога, Отца нашего, и Христа Иисуса, Господа нашего.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39230, 'rst66', '1ti', 1, 1, 'Павел, Апостол Иисуса Христа по повелению Бога, Спасителя нашего, и Господа Иисуса Христа, надежды нашей,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39229, 'rst66', '2th', 3, 18, 'Благодать Господа нашего Иисуса Христа со всеми вами. Аминь.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39228, 'rst66', '2th', 3, 17, 'Приветствие моею рукою, Павловою, что служит знаком во всяком послании; пишу я так:');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39227, 'rst66', '2th', 3, 16, 'Сам же Господь мира да даст вам мир всегда во всем. Господь со всеми вами!');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39226, 'rst66', '2th', 3, 15, 'Но не считайте его за врага, а вразумляйте, как брата.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39225, 'rst66', '2th', 3, 14, 'Если же кто не послушает слова нашего в сем послании, того имейте на замечании и не сообщайтесь с ним, чтобы устыдить его.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39224, 'rst66', '2th', 3, 13, 'Вы же, братия, не унывайте, делая добро.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39223, 'rst66', '2th', 3, 12, 'Таковых увещеваем и убеждаем Господом нашим Иисусом Христом, чтобы они, работая в безмолвии, ели свой хлеб.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39222, 'rst66', '2th', 3, 11, 'Но слышим, что некоторые у вас поступают бесчинно, ничего не делают, а суетятся.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39221, 'rst66', '2th', 3, 10, 'Ибо когда мы были у вас, то завещевали вам сие: если кто не хочет трудиться, тот и не ешь.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39220, 'rst66', '2th', 3, 9, 'не потому, чтобы мы не имели власти, но чтобы себя самих дать вам в образец для подражания нам.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39219, 'rst66', '2th', 3, 8, 'ни у кого не ели хлеба даром, но занимались трудом и работою ночь и день, чтобы не обременить кого из вас, &mdash;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39218, 'rst66', '2th', 3, 7, 'ибо вы сами знаете, как должны вы подражать нам; ибо мы не бесчинствовали у вас,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39217, 'rst66', '2th', 3, 6, 'Завещеваем же вам, братия, именем Господа нашего Иисуса Христа, удаляться от всякого брата, поступающего бесчинно, а не по преданию, которое приняли от нас,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39216, 'rst66', '2th', 3, 5, 'Господь же да управит сердца ваши в любовь Божию и в терпение Христово.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39215, 'rst66', '2th', 3, 4, 'Мы уверены о вас в Господе, что вы исполняете и будете исполнять то, что мы вам повелеваем.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39214, 'rst66', '2th', 3, 3, 'Но верен Господь, Который утвердит вас и сохранит от лукавого.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39213, 'rst66', '2th', 3, 2, 'и чтобы нам избавиться от беспорядочных и лукавых людей, ибо не во всех вера.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39212, 'rst66', '2th', 3, 1, 'Итак, молитесь за нас, братия, чтобы слово Господне распространялось и прославлялось, как и у вас,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39211, 'rst66', '2th', 2, 17, 'да утешит ваши сердца и да утвердит вас во всяком слове и деле благом.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39210, 'rst66', '2th', 2, 16, 'Сам же Господь наш Иисус Христос и Бог и Отец наш, возлюбивший нас и давший утешение вечное и надежду благую во благодати,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39209, 'rst66', '2th', 2, 15, 'Итак, братия, стойте и держите предания, которым вы научены или словом, или посланием нашим.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39208, 'rst66', '2th', 2, 14, 'к которому и призвал вас благовествованием нашим, для достижения славы Господа нашего Иисуса Христа.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39207, 'rst66', '2th', 2, 13, 'Мы же всегда должны благодарить Бога за вас, возлюбленные Господом братия, что Бог от начала, через освящение Духа и веру истине, избрал вас ко спасению,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39206, 'rst66', '2th', 2, 12, 'да будут осуждены все, не веровавшие истине, но возлюбившие неправду.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39205, 'rst66', '2th', 2, 11, 'И за сие пошлет им Бог действие заблуждения, так что они будут верить лжи,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39204, 'rst66', '2th', 2, 10, 'и со всяким неправедным обольщением погибающих за то, что они не приняли любви истины для своего спасения.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39203, 'rst66', '2th', 2, 9, 'того, которого пришествие, по действию сатаны, будет со всякою силою и знамениями и чудесами ложными,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39202, 'rst66', '2th', 2, 8, 'И тогда откроется беззаконник, которого Господь Иисус убьет духом уст Своих и истребит явлением пришествия Своего &mdash;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39201, 'rst66', '2th', 2, 7, 'Ибо тайна беззакония уже в действии, только <i>не совершится</i> до тех пор, пока не будет взят от среды удерживающий теперь.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39200, 'rst66', '2th', 2, 6, 'И ныне вы знаете, что&#769; не допускает открыться ему в свое время.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39199, 'rst66', '2th', 2, 5, 'Не помните ли, что я, еще находясь у вас, говорил вам это?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39198, 'rst66', '2th', 2, 4, 'противящийся и превозносящийся выше всего, называемого Богом или святынею, так что в храме Божием сядет он, как Бог, выдавая себя за Бога.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39197, 'rst66', '2th', 2, 3, 'Да не обольстит вас никто никак: <i>ибо день тот не придет</i>, доколе не придет прежде отступление и не откроется человек греха, сын погибели,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39196, 'rst66', '2th', 2, 2, 'не спешить колебаться умом и смущаться ни от духа, ни от слова, ни от послания, как бы нами посланного, будто уже наступает день Христов.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39195, 'rst66', '2th', 2, 1, 'Молим вас, братия, о пришествии Господа нашего Иисуса Христа и нашем собрании к Нему,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39194, 'rst66', '2th', 1, 12, 'да прославится имя Господа нашего Иисуса Христа в вас, и вы в Нем, по благодати Бога нашего и Господа Иисуса Христа.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39193, 'rst66', '2th', 1, 11, 'Для сего и молимся всегда за вас, чтобы Бог наш соделал вас достойными звания и совершил всякое благоволение благости и дело веры в силе,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39192, 'rst66', '2th', 1, 10, 'когда Он приидет прославиться во святых Своих и явиться дивным в день оный во всех веровавших, так как вы поверили нашему свидетельству.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39191, 'rst66', '2th', 1, 9, 'которые подвергнутся наказанию, вечной погибели, от лица Господа и от славы могущества Его,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39190, 'rst66', '2th', 1, 8, 'в пламенеющем огне совершающего отмщение не познавшим Бога и не покоряющимся благовествованию Господа нашего Иисуса Христа,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39189, 'rst66', '2th', 1, 7, 'а вам, оскорбляемым, отрадою вместе с нами, в явление Господа Иисуса с неба, с Ангелами силы Его,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39188, 'rst66', '2th', 1, 6, 'Ибо праведно пред Богом &mdash; оскорбляющим вас воздать скорбью,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39187, 'rst66', '2th', 1, 5, 'в доказательство того, что будет праведный суд Божий, чтобы вам удостоиться Царствия Божия, для которого и страдаете.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39186, 'rst66', '2th', 1, 4, 'так что мы сами хвалимся вами в церквах Божиих, терпением вашим и верою во всех гонениях и скорбях, переносимых вами');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39185, 'rst66', '2th', 1, 3, 'Всегда по справедливости мы должны благодарить Бога за вас, братия, потому что возрастает вера ваша, и умножается любовь каждого друг ко другу между всеми вами,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39184, 'rst66', '2th', 1, 2, 'благодать вам и мир от Бога Отца нашего и Господа Иисуса Христа.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39183, 'rst66', '2th', 1, 1, 'Павел и Силуан и Тимофей &mdash; Фессалоникской церкви в Боге Отце нашем и Господе Иисусе Христе:');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39182, 'rst66', '1th', 5, 28, 'Благодать Господа нашего Иисуса Христа с вами. Аминь.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39181, 'rst66', '1th', 5, 27, 'Заклинаю вас Господом прочитать сие послание всем святым братиям.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39180, 'rst66', '1th', 5, 26, 'Приветствуйте всех братьев лобзанием святым.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39179, 'rst66', '1th', 5, 25, 'Братия! молитесь о нас.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39178, 'rst66', '1th', 5, 24, 'Верен Призывающий вас, Который и сотворит <i>сие</i>.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39177, 'rst66', '1th', 5, 23, 'Сам же Бог мира да освятит вас во всей полноте, и ваш дух и душа и тело во всей целости да сохранится без порока в пришествие Господа нашего Иисуса Христа.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39176, 'rst66', '1th', 5, 22, 'Удерживайтесь от всякого рода зла.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39175, 'rst66', '1th', 5, 21, 'Все испытывайте, хорошего держитесь.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39174, 'rst66', '1th', 5, 20, 'Пророчества не уничижайте.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39173, 'rst66', '1th', 5, 19, 'Духа не угашайте.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39172, 'rst66', '1th', 5, 18, 'За все благодарите: ибо такова о вас воля Божия во Христе Иисусе.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39171, 'rst66', '1th', 5, 17, 'Непрестанно молитесь.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39170, 'rst66', '1th', 5, 16, 'Всегда радуйтесь.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39169, 'rst66', '1th', 5, 15, 'Смотрите, чтобы кто кому не воздавал злом за зло; но всегда ищите добра и друг другу и всем.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39168, 'rst66', '1th', 5, 14, 'Умоляем также вас, братия, вразумляйте бесчинных, утешайте малодушных, поддерживайте слабых, будьте долготерпеливы ко всем.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39167, 'rst66', '1th', 5, 13, 'и почитать их преимущественно с любовью за дело их; будьте в мире между собою.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39166, 'rst66', '1th', 5, 12, 'Просим же вас, братия, уважать трудящихся у вас, и предстоятелей ваших в Господе, и вразумляющих вас,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39165, 'rst66', '1th', 5, 11, 'Посему увещевайте друг друга и назидайте один другого, как вы и делаете.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39164, 'rst66', '1th', 5, 10, 'умершего за нас, чтобы мы &mdash; бодрствуем ли или спим &mdash; жили вместе с Ним.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39163, 'rst66', '1th', 5, 9, 'потому что Бог определил нас не на гнев, но к получению спасения через Господа нашего Иисуса Христа,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39162, 'rst66', '1th', 5, 8, 'Мы же, будучи <i>сынами</i> дня, да трезвимся, облекшись в броню веры и любви и в шлем надежды спасения,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39161, 'rst66', '1th', 5, 7, 'Ибо спящие спят ночью, и упивающиеся упиваются ночью.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39160, 'rst66', '1th', 5, 6, 'Итак, не будем спать, как и прочие, но будем бодрствовать и трезвиться.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39159, 'rst66', '1th', 5, 5, 'Ибо все вы &mdash; сыны света и сыны дня: мы &mdash; не <i>сыны</i> ночи, ни тьмы.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39158, 'rst66', '1th', 5, 4, 'Но вы, братия, не во тьме, чтобы день застал вас, как тать.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39157, 'rst66', '1th', 5, 3, 'Ибо, когда будут говорить: «мир и безопасность», тогда внезапно постигнет их пагуба, подобно как мука родами <i>постигает</i> имеющую во чреве, и не избегнут.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39156, 'rst66', '1th', 5, 2, 'ибо сами вы достоверно знаете, что день Господень так придет, как тать ночью.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39155, 'rst66', '1th', 5, 1, 'О временах же и сроках нет нужды писать к вам, братия,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39154, 'rst66', '1th', 4, 18, 'Итак, утешайте друг друга сими словами.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39153, 'rst66', '1th', 4, 17, 'потом мы, оставшиеся в живых, вместе с ними восхищены будем на облаках в сретение Господу на воздухе, и так всегда с Господом будем.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39152, 'rst66', '1th', 4, 16, 'потому что Сам Господь при возвещении, при гласе Архангела и трубе Божией, сойдет с неба, и мертвые во Христе воскреснут прежде;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39151, 'rst66', '1th', 4, 15, 'Ибо сие говорим вам словом Господним, что мы, живущие, оставшиеся до пришествия Господня, не предупредим умерших,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39150, 'rst66', '1th', 4, 14, 'Ибо, если мы веруем, что Иисус умер и воскрес, то и умерших в Иисусе Бог приведет с Ним.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39149, 'rst66', '1th', 4, 13, 'Не хочу же оставить вас, братия, в неведении об умерших, дабы вы не скорбели, как прочие, не имеющие надежды.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39148, 'rst66', '1th', 4, 12, 'чтобы вы поступали благоприлично перед внешними и ни в чем не нуждались.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39147, 'rst66', '1th', 4, 11, 'и усердно стараться о том, чтобы жить тихо, делать свое <i>дело</i> и работать своими собственными руками, как мы заповедовали вам;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39146, 'rst66', '1th', 4, 10, 'ибо вы так и поступаете со всеми братиями по всей Македонии. Умоляем же вас, братия, более преуспевать');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39145, 'rst66', '1th', 4, 9, 'О братолюбии же нет нужды писать к вам; ибо вы сами научены Богом любить друг друга,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39144, 'rst66', '1th', 4, 8, 'Итак, непокорный непокорен не человеку, но Богу, Который и дал нам Духа Своего Святого.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39143, 'rst66', '1th', 4, 7, 'Ибо призвал нас Бог не к нечистоте, но к святости.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39142, 'rst66', '1th', 4, 6, 'чтобы вы ни в чем не поступали с братом своим противозаконно и корыстолюбиво: потому что Господь &mdash; мститель за все это, как и прежде мы говорили вам и свидетельствовали.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39141, 'rst66', '1th', 4, 5, 'а не в страсти похотения, как и язычники, не знающие Бога;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39140, 'rst66', '1th', 4, 4, 'чтобы каждый из вас умел соблюдать свой сосуд в святости и чести,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39139, 'rst66', '1th', 4, 3, 'Ибо воля Божия есть освящение ваше, чтобы вы воздерживались от блуда;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39138, 'rst66', '1th', 4, 2, 'ибо вы знаете, какие мы дали вам заповеди от Господа Иисуса.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39137, 'rst66', '1th', 4, 1, 'За сим, братия, просим и умоляем вас Христом Иисусом, чтобы вы, приняв от нас, как должно вам поступать и угождать Богу, более в том преуспевали,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39136, 'rst66', '1th', 3, 13, 'чтобы утвердить сердца ваши непорочными во святыне пред Богом и Отцом нашим в пришествие Господа нашего Иисуса Христа со всеми святыми Его. Аминь.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39135, 'rst66', '1th', 3, 12, 'А вас Господь да исполнит и преисполнит любовью друг к другу и ко всем, какою мы исполнены к вам,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39134, 'rst66', '1th', 3, 11, 'Сам же Бог и Отец наш и Господь наш Иисус Христос да управит путь наш к вам.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39133, 'rst66', '1th', 3, 10, 'ночь и день всеусердно молясь о том, чтобы видеть лицо ваше и дополнить, чего недоставало вере вашей?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39132, 'rst66', '1th', 3, 9, 'Какую благодарность можем мы воздать Богу за вас, за всю радость, которою радуемся о вас пред Богом нашим,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39131, 'rst66', '1th', 3, 8, 'ибо теперь мы живы, когда вы стоите в Господе.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39130, 'rst66', '1th', 3, 7, 'то мы, при всей скорби и нужде нашей, утешились вами, братия, ради вашей веры;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39129, 'rst66', '1th', 3, 6, 'Теперь же, когда пришел к нам от вас Тимофей и принес нам добрую весть о вере и любви вашей, и что вы всегда имеете добрую память о нас, желая нас видеть, как и мы вас,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39128, 'rst66', '1th', 3, 5, 'Посему и я, не терпя более, послал узнать о вере вашей, чтобы как не искусил вас искуситель и не сделался тщетным труд наш.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39127, 'rst66', '1th', 3, 4, 'Ибо мы и тогда, как были у вас, предсказывали вам, что будем страдать, как и случилось, и вы знаете.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39126, 'rst66', '1th', 3, 3, 'чтобы никто не поколебался в скорбях сих: ибо вы сами знаете, что так нам суждено.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39125, 'rst66', '1th', 3, 2, 'и послали Тимофея, брата нашего и служителя Божия и сотрудника нашего в благовествовании Христовом, чтобы утвердить вас и утешить в вере вашей,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39124, 'rst66', '1th', 3, 1, 'И потому, не терпя более, мы восхотели остаться в Афинах одни,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39123, 'rst66', '1th', 2, 20, 'Ибо вы &mdash; слава наша и радость.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39122, 'rst66', '1th', 2, 19, 'Ибо кто наша надежда, или радость, или венец похвалы? Не и вы ли пред Господом нашим Иисусом Христом в пришествие Его?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39121, 'rst66', '1th', 2, 18, 'И потому мы, я, Павел, и раз и два хотели прийти к вам, но воспрепятствовал нам сатана.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39120, 'rst66', '1th', 2, 17, 'Мы же, братия, быв разлучены с вами на короткое время лицом, а не сердцем, тем с бо&#769;льшим желанием старались увидеть лицо ваше.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39119, 'rst66', '1th', 2, 16, 'которые препятствуют нам говорить язычникам, чтобы спаслись, и через это всегда наполняют меру грехов своих; но приближается на них гнев до конца.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39118, 'rst66', '1th', 2, 15, 'которые убили и Господа Иисуса и Его пророков, и нас изгнали, и Богу не угождают, и всем человекам противятся,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39117, 'rst66', '1th', 2, 14, 'Ибо вы, братия, сделались подражателями церквам Божиим во Христе Иисусе, находящимся в Иудее, потому что и вы то же претерпели от своих единоплеменников, что и те от Иудеев,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39116, 'rst66', '1th', 2, 13, 'Посему и мы непрестанно благодарим Бога, что, приняв от нас слышанное слово Божие, вы приняли не <i>ка&#769;к</i> слово человеческое, но <i>как</i> слово Божие, &mdash; каково оно есть по истине, &mdash; которое и действует в вас, верующих.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39115, 'rst66', '1th', 2, 12, 'мы просили и убеждали и умоляли поступать достойно Бога, призвавшего вас в Свое Царство и славу.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39114, 'rst66', '1th', 2, 11, 'потому что вы знаете, как каждого из вас, как отец детей своих,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39113, 'rst66', '1th', 2, 10, 'Свидетели вы и Бог, как свято и праведно и безукоризненно поступали мы перед вами, верующими,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39112, 'rst66', '1th', 2, 9, 'Ибо вы помните, братия, труд наш и изнурение: ночью и днем работая, чтобы не отяготить кого из вас, мы проповедовали у вас благовестие Божие.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39111, 'rst66', '1th', 2, 8, 'Так мы, из усердия к вам, восхотели передать вам не только благовестие Божие, но и души наши, потому что вы стали нам любезны.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39110, 'rst66', '1th', 2, 7, 'мы могли явиться с важностью, как Апостолы Христовы, но были тихи среди вас, подобно как кормилица нежно обходится с детьми своими.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39109, 'rst66', '1th', 2, 6, 'Не ищем славы человеческой ни от вас, ни от других:');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39108, 'rst66', '1th', 2, 5, 'Ибо никогда не было у нас перед вами ни слов ласкательства, как вы знаете, ни видов корысти: Бог свидетель!');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39107, 'rst66', '1th', 2, 4, 'но, как Бог удостоил нас того, чтобы вверить <i>нам</i> благовестие, так мы и говорим, угождая не человекам, но Богу, испытующему сердца наши.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39106, 'rst66', '1th', 2, 3, 'Ибо в учении нашем нет ни заблуждения, ни нечистых <i>побуждений</i>, ни лукавства;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39105, 'rst66', '1th', 2, 2, 'но, прежде пострадав и быв поруганы в Филиппах, как вы знаете, мы дерзнули в Боге нашем проповедать вам благовестие Божие с великим подвигом.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39104, 'rst66', '1th', 2, 1, 'Вы сами знаете, братия, о нашем входе к вам, что он был не бездейственный;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39103, 'rst66', '1th', 1, 10, 'и ожидать с небес Сына Его, Которого Он воскресил из мертвых, Иисуса, избавляющего нас от грядущего гнева.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39102, 'rst66', '1th', 1, 9, 'Ибо сами они сказывают о нас, какой вход имели мы к вам, и как вы обратились к Богу от идолов, <i>чтобы</i> служить Богу живому и истинному');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39101, 'rst66', '1th', 1, 8, 'Ибо от вас пронеслось слово Господне не только в Македонии и Ахаии, но и во всяком месте прошла <i>слава</i> о вере вашей в Бога, так что нам ни о чем не нужно рассказывать.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39100, 'rst66', '1th', 1, 7, 'так что вы стали образцом для всех верующих в Македонии и Ахаии.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39099, 'rst66', '1th', 1, 6, 'И вы сделались подражателями нам и Господу, приняв слово при многих скорбях с радостью Духа Святого,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39098, 'rst66', '1th', 1, 5, 'потому что наше благовествование у вас было не в слове только, но и в силе и во Святом Духе, и со многим удостоверением, как вы <i>сами</i> знаете, каковы были мы для вас между вами.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39097, 'rst66', '1th', 1, 4, 'зная избрание ваше, возлюбленные Богом братия;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39096, 'rst66', '1th', 1, 3, 'непрестанно памятуя ваше дело веры и труд любви и терпение упования на Господа нашего Иисуса Христа пред Богом и Отцом нашим,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39095, 'rst66', '1th', 1, 2, 'Всегда благодарим Бога за всех вас, вспоминая о вас в молитвах наших,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39094, 'rst66', '1th', 1, 1, 'Павел и Силуан и Тимофей &mdash; церкви Фессалоникской в Боге Отце и Господе Иисусе Христе: благодать вам и мир от Бога Отца нашего и Господа Иисуса Христа.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39093, 'rst66', 'col', 4, 18, 'Приветствие моею рукою, Павловою. Помните мои узы. Благодать со всеми вами. Аминь.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39092, 'rst66', 'col', 4, 17, 'Скажите Архиппу: смотри, чтобы тебе исполнить служение, которое ты принял в Господе.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39091, 'rst66', 'col', 4, 16, 'Когда это послание прочитано будет у вас, то распорядитесь, чтобы оно было прочитано и в Лаодикийской церкви; а то, которое из Лаодикии, прочитайте и вы.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39090, 'rst66', 'col', 4, 15, 'Приветствуйте братьев в Лаодикии, и Нимфана, и домашнюю церковь его.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39089, 'rst66', 'col', 4, 14, 'Приветствует вас Лука, врач возлюбленный, и Димас.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39088, 'rst66', 'col', 4, 13, 'Свидетельствую о нем, что он имеет великую ревность и заботу о вас и о находящихся в Лаодикии и Иераполе.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39087, 'rst66', 'col', 4, 12, 'Приветствует вас Епафрас ваш, раб Иисуса Христа, всегда подвизающийся за вас в молитвах, чтобы вы пребыли совершенны и исполнены всем, что угодно Богу.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39086, 'rst66', 'col', 4, 11, 'также Иисус, прозываемый Иустом, оба из обрезанных. Они &mdash; единственные сотрудники для Царствия Божия, бывшие мне отрадою.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39085, 'rst66', 'col', 4, 10, 'Приветствует вас Аристарх, заключенный вместе со мною, и Марк, племянник Варнавы (о котором вы получили приказания: если придет к вам, примите его),');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39084, 'rst66', 'col', 4, 9, 'с Онисимом, верным и возлюбленным братом нашим, который от вас. Они расскажут вам о всем здешнем.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39083, 'rst66', 'col', 4, 8, 'которого я для того послал к вам, чтобы он узнал о ваших <i>обстоятельствах</i> и утешил сердца ваши,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39082, 'rst66', 'col', 4, 7, 'О мне всё скажет вам Тихик, возлюбленный брат и верный служитель и сотрудник в Господе,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39081, 'rst66', 'col', 4, 6, 'Слово ваше <i>да будет</i> всегда с благодатию, приправлено солью, дабы вы знали, как отвечать каждому.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39080, 'rst66', 'col', 4, 5, 'Со внешними обходитесь благоразумно, пользуясь временем.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39079, 'rst66', 'col', 4, 4, 'дабы я открыл ее, как должно мне возвещать.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39078, 'rst66', 'col', 4, 3, 'Молитесь также и о нас, чтобы Бог отверз нам дверь для слова, возвещать тайну Христову, за которую я и в узах,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39077, 'rst66', 'col', 4, 2, 'Будьте постоянны в молитве, бодрствуя в ней с благодарением.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39076, 'rst66', 'col', 4, 1, 'Господа&#769;, оказывайте рабам должное и справедливое, зная, что и вы имеете Господа на небесах.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39075, 'rst66', 'col', 3, 25, 'А кто неправо поступит, тот получит по своей неправде, <i>у Него</i> нет лицеприятия.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39074, 'rst66', 'col', 3, 24, 'зная, что в воздаяние от Господа получите наследие, ибо вы служите Господу Христу.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39073, 'rst66', 'col', 3, 23, 'И всё, что делаете, делайте от души, как для Господа, а не для человеков,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39072, 'rst66', 'col', 3, 22, 'Рабы, во всем повинуйтесь господам вашим по плоти, не в глазах только служа <i>им</i>, как человекоугодники, но в простоте сердца, боясь Бога.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39071, 'rst66', 'col', 3, 21, 'Отцы, не раздражайте детей ваших, дабы они не унывали.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39070, 'rst66', 'col', 3, 20, 'Дети, будьте послушны родителям вашим во всем, ибо это благоугодно Господу.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39069, 'rst66', 'col', 3, 19, 'Мужья, любите своих жен и не будьте к ним суровы.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39068, 'rst66', 'col', 3, 18, 'Жены, повинуйтесь мужьям своим, как прилично в Господе.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39067, 'rst66', 'col', 3, 17, 'И всё, что вы делаете, словом или делом, всё <i>делайте</i> во имя Господа Иисуса Христа, благодаря через Него Бога и Отца.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39066, 'rst66', 'col', 3, 16, 'Слово Христово да вселяется в вас обильно, со всякою премудростью; научайте и вразумляйте друг друга псалмами, славословием и духовными песнями, во благодати воспевая в сердцах ваших Господу.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39065, 'rst66', 'col', 3, 15, 'И да владычествует в сердцах ваших мир Божий, к которому вы и призваны в одном теле, и будьте дружелюбны.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39064, 'rst66', 'col', 3, 14, 'Более же всего <i>облекитесь</i> в любовь, которая есть совокупность совершенства.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39063, 'rst66', 'col', 3, 13, 'снисходя друг другу и прощая взаимно, если кто на кого имеет жалобу: как Христос простил вас, так и вы.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39062, 'rst66', 'col', 3, 12, 'Итак, облекитесь, как избранные Божии, святые и возлюбленные, в милосердие, благость, смиренномудрие, кротость, долготерпение,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39061, 'rst66', 'col', 3, 11, 'где нет ни Еллина, ни Иудея, ни обрезания, ни необрезания, варвара, Скифа, раба, свободного, но все и во всем &mdash; Христос.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39060, 'rst66', 'col', 3, 10, 'и облекшись в нового, который обновляется в познании по образу Создавшего его,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39059, 'rst66', 'col', 3, 9, 'не говорите лжи друг другу, совлекшись ветхого человека с делами его');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39058, 'rst66', 'col', 3, 8, 'А теперь вы отложите все: гнев, ярость, злобу, злоречие, сквернословие уст ваших;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39057, 'rst66', 'col', 3, 7, 'в которых и вы некогда обращались, когда жили между ними.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39056, 'rst66', 'col', 3, 6, 'за которые гнев Божий грядет на сынов противления,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39055, 'rst66', 'col', 3, 5, 'Итак, умертвите земные члены ваши: блуд, нечистоту, страсть, злую похоть и любостяжание, которое есть идолослужение,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39054, 'rst66', 'col', 3, 4, 'Когда же явится Христос, жизнь ваша, тогда и вы явитесь с Ним во славе.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39053, 'rst66', 'col', 3, 3, 'Ибо вы умерли, и жизнь ваша сокрыта со Христом в Боге.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39052, 'rst66', 'col', 3, 2, 'о горнем помышляйте, а не о земном.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39051, 'rst66', 'col', 3, 1, 'Итак, если вы воскресли со Христом, то ищите горнего, где Христос сидит одесную Бога;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39050, 'rst66', 'col', 2, 23, 'Это имеет только вид мудрости в самовольном служении, смиренномудрии и изнурении тела, в некотором небрежении о насыщении плоти.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39049, 'rst66', 'col', 2, 22, 'что все истлевает от употребления, &mdash; по заповедям и учению человеческому?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39048, 'rst66', 'col', 2, 21, '«не прикасайся», «не вкушай», «не дотрагивайся», &mdash;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39047, 'rst66', 'col', 2, 20, 'Итак, если вы со Христом умерли для стихий мира, то для чего вы, как живущие в мире, держитесь постановлений:');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39046, 'rst66', 'col', 2, 19, 'и не держась главы, от которой все тело, составами и связями будучи соединяемо и скрепляемо, растет возрастом Божиим.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39045, 'rst66', 'col', 2, 18, 'Никто да не обольщает вас самовольным смиренномудрием и служением Ангелов, вторгаясь в то, чего не видел, безрассудно надмеваясь плотским своим умом');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39044, 'rst66', 'col', 2, 17, 'это есть тень будущего, а тело &mdash; во Христе.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39043, 'rst66', 'col', 2, 16, 'Итак, никто да не осуждает вас за пищу, или питие, или за какой‐нибудь праздник, или новомесячие, или субботу:');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39042, 'rst66', 'col', 2, 15, 'отняв силы у начальств и властей, властно подверг их позору, восторжествовав над ними Собою.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39041, 'rst66', 'col', 2, 14, 'истребив учением бывшее о нас рукописание, которое было против нас, и Он взял его от среды и пригвоздил ко кресту;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39040, 'rst66', 'col', 2, 13, 'и вас, которые были мертвы во грехах и в необрезании плоти вашей, оживил вместе с Ним, простив нам все грехи,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39039, 'rst66', 'col', 2, 12, 'быв погребены с Ним в крещении, в Нем вы и совоскресли верою в силу Бога, Который воскресил Его из мертвых,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39038, 'rst66', 'col', 2, 11, 'В Нем вы и обрезаны обрезанием нерукотворенным, совлечением греховного тела плоти, обрезанием Христовым;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39037, 'rst66', 'col', 2, 10, 'и вы имеете полноту в Нем, Который есть глава всякого начальства и власти.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39036, 'rst66', 'col', 2, 9, 'ибо в Нем обитает вся полнота Божества телесно,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39035, 'rst66', 'col', 2, 8, 'Смотрите, братия, чтобы кто не увлек вас философиею и пустым обольщением, по преданию человеческому, по стихиям мира, а не по Христу;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39034, 'rst66', 'col', 2, 7, 'будучи укоренены и утверждены в Нем и укреплены в вере, как вы научены, преуспевая в ней с благодарением.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39033, 'rst66', 'col', 2, 6, 'Посему, как вы приняли Христа Иисуса Господа, <i>так</i> и ходите в Нем,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39032, 'rst66', 'col', 2, 5, 'ибо хотя я и отсутствую телом, но духом нахожусь с вами, радуясь и видя ваше благоустройство и твердость веры вашей во Христа.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39031, 'rst66', 'col', 2, 4, 'Это говорю я для того, чтобы кто‐нибудь не прельстил вас вкрадчивыми словами;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39030, 'rst66', 'col', 2, 3, 'в Котором сокрыты все сокровища премудрости и ведения.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39029, 'rst66', 'col', 2, 2, 'дабы утешились сердца их, соединенные в любви для всякого богатства совершенного разумения, для познания тайны Бога и Отца и Христа,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39028, 'rst66', 'col', 2, 1, 'Желаю, чтобы вы знали, какой подвиг имею я ради вас и ради тех, которые в Лаодикии и Иераполе, и ради всех, кто не видел лица моего в плоти,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39027, 'rst66', 'col', 1, 29, 'для чего я и тружусь и подвизаюсь силою Его, действующею во мне могущественно.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39026, 'rst66', 'col', 1, 28, 'Которого мы проповедуем, вразумляя всякого человека и научая всякой премудрости, чтобы представить всякого человека совершенным во Христе Иисусе;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39025, 'rst66', 'col', 1, 27, 'которым благоволил Бог показать, какое богатство славы в тайне сей для язычников, которая есть Христос в вас, упование славы,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39024, 'rst66', 'col', 1, 26, 'тайну, сокрытую от веков и родов, ныне же открытую святым Его,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39023, 'rst66', 'col', 1, 25, 'которой сделался я служителем по домостроительству Божию, вверенному мне для вас, <i>чтобы</i> исполнить слово Божие,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39022, 'rst66', 'col', 1, 24, 'Ныне радуюсь в страданиях моих за вас и восполняю недостаток в плоти моей скорбей Христовых за Тело Его, которое есть Церковь,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39021, 'rst66', 'col', 1, 23, 'если только пребываете тверды и непоколебимы в вере и не отпадаете от надежды благовествования, которое вы слышали, которое возвещено всей твари поднебесной, которого я, Павел, сделался служителем.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39020, 'rst66', 'col', 1, 22, 'ныне примирил в теле Плоти Его, смертью Его, <i>чтобы</i> представить вас святыми и непорочными и неповинными пред Собою,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39019, 'rst66', 'col', 1, 21, 'И вас, бывших некогда отчужденными и врагами, по расположению к злым делам,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39018, 'rst66', 'col', 1, 20, 'и чтобы посредством Его примирить с Собою все, умиротворив через Него, Кровию креста Его, и земное и небесное.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39017, 'rst66', 'col', 1, 19, 'ибо благоугодно было <i>Отцу</i>, чтобы в Нем обитала всякая полнота,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39016, 'rst66', 'col', 1, 18, 'И Он есть глава тела Церкви; Он &mdash; начаток, первенец из мертвых, дабы иметь Ему во всем первенство,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39015, 'rst66', 'col', 1, 17, 'и Он есть прежде всего, и все Им стои&#769;т.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39014, 'rst66', 'col', 1, 16, 'Ибо Им создано всё, что на небесах и что на земле, видимое и невидимое: престолы ли, господства ли, начальства ли, власти ли, &mdash; все Им и для Него создано;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39013, 'rst66', 'col', 1, 15, 'Который есть образ Бога невидимого, рожденный прежде всякой твари;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39012, 'rst66', 'col', 1, 14, 'в Котором мы имеем искупление Кровию Его и прощение грехов,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39011, 'rst66', 'col', 1, 13, 'избавившего нас от власти тьмы и введшего в Царство возлюбленного Сына Своего,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39010, 'rst66', 'col', 1, 12, 'благодаря Бога и Отца, призвавшего нас к участию в наследии святых во свете,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39009, 'rst66', 'col', 1, 11, 'укрепляясь всякою силою по могуществу славы Его, во всяком терпении и великодушии с радостью,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39008, 'rst66', 'col', 1, 10, 'чтобы поступали достойно Бога, во всем угождая <i>Ему</i>, принося плод во всяком деле благом и возрастая в познании Бога,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39007, 'rst66', 'col', 1, 9, 'Посему и мы с того дня, как <i>о сем</i> услышали, не перестаем молиться о вас и просить, чтобы вы исполнялись познанием воли Его, во всякой премудрости и разумении духовном,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39006, 'rst66', 'col', 1, 8, 'который и известил нас о вашей любви в духе.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39005, 'rst66', 'col', 1, 7, 'как и научились от Епафраса, возлюбленного сотрудника нашего, верного для вас служителя Христова,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39004, 'rst66', 'col', 1, 6, 'которое пребывает у вас, как и во всем мире, и приносит плод, и возрастает, как и между вами, с того дня, как вы услышали и познали благодать Божию в истине,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39003, 'rst66', 'col', 1, 5, 'в надежде на уготованное вам на небесах, о чем вы прежде слышали в истинном слове благовествования,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39002, 'rst66', 'col', 1, 4, 'услышав о вере вашей во Христа Иисуса и о любви ко всем святым,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39001, 'rst66', 'col', 1, 3, 'благодать вам и мир от Бога Отца нашего и Господа Иисуса Христа. Благодарим Бога и Отца Господа нашего Иисуса Христа, всегда молясь о вас,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (39000, 'rst66', 'col', 1, 2, 'находящимся в Колоссах святым и верным братиям во Христе Иисусе:');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38999, 'rst66', 'col', 1, 1, 'Павел, волею Божиею Апостол Иисуса Христа, и Тимофей брат &mdash;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38998, 'rst66', 'php', 4, 23, 'Благодать Господа нашего Иисуса Христа со всеми вами. Аминь.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38997, 'rst66', 'php', 4, 22, 'Приветствуют вас все святые, а наипаче из кесарева дома.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38996, 'rst66', 'php', 4, 21, 'Приветствуйте всякого святого во Христе Иисусе. Приветствуют вас находящиеся со мною братия.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38995, 'rst66', 'php', 4, 20, 'Богу же и Отцу нашему слава во веки веков! Аминь.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38994, 'rst66', 'php', 4, 19, 'Бог мой да восполнит всякую нужду вашу, по богатству Своему в славе, Христом Иисусом.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38993, 'rst66', 'php', 4, 18, 'Я получил все, и избыточествую; я доволен, получив от Епафродита посланное вами, <i>как</i> благовонное курение, жертву приятную, благоугодную Богу.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38992, 'rst66', 'php', 4, 17, '<i>Говорю это</i> не потому, чтобы я искал даяния; но ищу плода, умножающегося в пользу вашу.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38991, 'rst66', 'php', 4, 16, 'вы и в Фессалонику и раз и два присылали мне на нужду.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38990, 'rst66', 'php', 4, 15, 'Вы знаете, Филиппийцы, что в начале благовествования, когда я вышел из Македонии, ни одна церковь не оказала мне участия подаянием и принятием, кроме вас одних;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38989, 'rst66', 'php', 4, 14, 'Впрочем, вы хорошо поступили, приняв участие в моей скорби.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38988, 'rst66', 'php', 4, 13, 'Все могу в укрепляющем меня Иисусе Христе.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38987, 'rst66', 'php', 4, 12, 'Умею жить и в скудости, умею жить и в изобилии; научился всему и во всем: насыщаться и терпеть голод, быть и в обилии и в недостатке.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38986, 'rst66', 'php', 4, 11, 'Говорю это не потому, что нуждаюсь, ибо я научился быть довольным тем, что у меня есть.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38985, 'rst66', 'php', 4, 10, 'Я весьма возрадовался в Господе, что вы уже вновь начали заботиться о мне; вы и прежде заботились, но вам не благоприятствовали обстоятельства.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38984, 'rst66', 'php', 4, 9, 'Чему вы научились, что&#769; приняли и слышали и видели во мне, то&#769; исполняйте, &mdash; и Бог мира будет с вами.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38983, 'rst66', 'php', 4, 8, 'Наконец, братия мои, что&#769; только истинно, что&#769; честно, что&#769; справедливо, что&#769; чисто, что&#769; любезно, что&#769; достославно, что&#769; только добродетель и похвала, о том помышляйте.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38982, 'rst66', 'php', 4, 7, 'и мир Божий, который превыше всякого ума, соблюдет сердца ваши и помышления ваши во Христе Иисусе.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38981, 'rst66', 'php', 4, 6, 'Не заботьтесь ни о чем, но всегда в молитве и прошении с благодарением открывайте свои желания пред Богом,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38980, 'rst66', 'php', 4, 5, 'Кротость ваша да будет известна всем человекам. Господь близко.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38979, 'rst66', 'php', 4, 4, 'Радуйтесь всегда в Господе; и еще говорю: радуйтесь.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38978, 'rst66', 'php', 4, 3, 'Ей, прошу и тебя, искренний сотрудник, помогай им, подвизавшимся в благовествовании вместе со мною и с Климентом и с прочими сотрудниками моими, которых имена &mdash; в книге жизни.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38977, 'rst66', 'php', 4, 2, 'Умоляю Еводию, умоляю Синтихию мыслить то же о Господе.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38976, 'rst66', 'php', 4, 1, 'Итак, братия мои возлюбленные и вожделенные, радость и венец мой, стойте так в Господе, возлюбленные.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38975, 'rst66', 'php', 3, 21, 'Который уничиженное тело наше преобразит так, что оно будет сообразно славному Телу Его, силою, <i>которою</i> Он действует и покоряет Себе всё.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38974, 'rst66', 'php', 3, 20, 'Наше же жительство &mdash; на небесах, откуда мы ожидаем и Спасителя, Господа нашего Иисуса Христа,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38973, 'rst66', 'php', 3, 19, 'Их конец &mdash; погибель, их бог &mdash; чрево, и слава их &mdash; в сраме, они мыслят о земном.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38972, 'rst66', 'php', 3, 18, 'Ибо многие, о которых я часто говорил вам, а теперь даже со слезами говорю, поступают как враги креста Христова.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38971, 'rst66', 'php', 3, 17, 'Подражайте, братия, мне и смотрите на тех, которые поступают по образу, какой имеете в нас.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38970, 'rst66', 'php', 3, 16, 'Впрочем, до чего мы достигли, так и должны мыслить и по тому правилу жить.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38969, 'rst66', 'php', 3, 15, 'Итак, кто из нас совершен, так должен мыслить; если же вы о чем иначе мыслите, то и это Бог вам откроет.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38968, 'rst66', 'php', 3, 14, 'стремлюсь к цели, к почести вышнего звания Божия во Христе Иисусе.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38967, 'rst66', 'php', 3, 13, 'Братия, я не почитаю себя достигшим; а только, забывая заднее и простираясь вперед,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38966, 'rst66', 'php', 3, 12, '<i>Говорю так</i> не потому, чтобы я уже достиг или усовершился; но стремлюсь, не достигну ли и я, как достиг меня Христос Иисус.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38965, 'rst66', 'php', 3, 11, 'чтобы достигнуть воскресения мертвых.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38964, 'rst66', 'php', 3, 10, 'чтобы познать Его, и силу воскресения Его, и участие в страданиях Его, сообразуясь смерти Его,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38963, 'rst66', 'php', 3, 9, 'и найтись в Нем не со своею праведностью, которая от закона, но с тою, которая через веру во Христа, с праведностью от Бога по вере;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38962, 'rst66', 'php', 3, 8, 'Да и все почитаю тщетою ради превосходства познания Христа Иисуса, Господа моего: для Него я от всего отказался, и все почитаю за сор, чтобы приобрести Христа');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38961, 'rst66', 'php', 3, 7, 'Но что для меня было преимуществом, то ради Христа я почел тщетою.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38960, 'rst66', 'php', 3, 6, 'по ревности &mdash; гонитель Церкви Божией, по правде законной &mdash; непорочный.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38959, 'rst66', 'php', 3, 5, 'обрезанный в восьмой день, из рода Израилева, колена Вениаминова, Еврей от Евреев, по учению &mdash; фарисей,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38958, 'rst66', 'php', 3, 4, 'хотя я могу надеяться и на плоть. Если кто другой думает надеяться на плоть, то более я,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38957, 'rst66', 'php', 3, 3, 'потому что обрезание &mdash; мы, служащие Богу духом, и хвалящиеся Христом Иисусом, и не на плоть надеющиеся,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38956, 'rst66', 'php', 3, 2, 'Берегитесь псов, берегитесь злых делателей, берегитесь обрезания,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38955, 'rst66', 'php', 3, 1, 'Впрочем, братия мои, радуйтесь о Господе. Писать вам о том же для меня не тягостно, а для вас назидательно.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38954, 'rst66', 'php', 2, 30, 'ибо он за дело Христово был близок к смерти, подвергая опасности жизнь, дабы восполнить недостаток ваших услуг мне.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38953, 'rst66', 'php', 2, 29, 'Примите же его в Господе со всякою радостью, и таких имейте в уважении,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38952, 'rst66', 'php', 2, 28, 'Посему я скорее послал его, чтобы вы, увидев его снова, возрадовались, и я был менее печален.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38951, 'rst66', 'php', 2, 27, 'Ибо он был болен при смерти; но Бог помиловал его, и не его только, но и меня, чтобы не прибавилась мне печаль к печали.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38950, 'rst66', 'php', 2, 26, 'потому что он сильно желал видеть всех вас и тяжко скорбел о том, что до вас дошел слух о его болезни.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38949, 'rst66', 'php', 2, 25, 'Впрочем, я почел нужным послать к вам Епафродита, брата и сотрудника и сподвижника моего, а вашего посланника и служителя в нужде моей,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38948, 'rst66', 'php', 2, 24, 'Я уверен в Господе, что и сам скоро приду к вам.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38947, 'rst66', 'php', 2, 23, 'Итак, я надеюсь послать его тотчас же, как скоро узна&#769;ю, что будет со мною.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38946, 'rst66', 'php', 2, 22, 'А его верность вам известна, потому что он, как сын отцу, служил мне в благовествовании.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38945, 'rst66', 'php', 2, 21, 'потому что все ищут своего, а не того, что <i>угодно</i> Иисусу Христу.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38944, 'rst66', 'php', 2, 20, 'Ибо я не имею никого равно усердного, кто бы столь искренно заботился о вас,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38943, 'rst66', 'php', 2, 19, 'Надеюсь же в Господе Иисусе вскоре послать к вам Тимофея, дабы и я, узнав о ваших обстоятельствах, утешился духом.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38942, 'rst66', 'php', 2, 18, 'О сем самом и вы радуйтесь и сорадуйтесь мне.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38941, 'rst66', 'php', 2, 17, 'Но если я и соделываюсь жертвою за жертву и служение веры вашей, то радуюсь и сорадуюсь всем вам.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38940, 'rst66', 'php', 2, 16, 'содержа слово жизни, к похвале моей в день Христов, что я не тщетно подвизался и не тщетно трудился.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38939, 'rst66', 'php', 2, 15, 'чтобы вам быть неукоризненными и чистыми, чадами Божиими непорочными среди строптивого и развращенного рода, в котором вы сияете, как светила в мире,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38938, 'rst66', 'php', 2, 14, 'Всё делайте без ропота и сомнения,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38937, 'rst66', 'php', 2, 13, 'потому что Бог производит в вас и хотение и действие по <i>Своему</i> благоволению.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38936, 'rst66', 'php', 2, 12, 'Итак, возлюбленные мои, как вы всегда были послушны, не только в присутствии моем, но гораздо более ныне во время отсутствия моего, со страхом и трепетом совершайте свое спасение,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38935, 'rst66', 'php', 2, 11, 'и всякий язык исповедал, что Господь Иисус Христос в славу Бога Отца.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38934, 'rst66', 'php', 2, 10, 'дабы пред именем Иисуса преклонилось всякое колено небесных, земных и преисподних,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38933, 'rst66', 'php', 2, 9, 'Посему и Бог превознес Его и дал Ему имя выше всякого имени,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38932, 'rst66', 'php', 2, 8, 'смирил Себя, быв послушным даже до смерти, и смерти крестной.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38931, 'rst66', 'php', 2, 7, 'но уничижил Себя Самого, приняв образ раба, сделавшись подобным человекам и по виду став как человек;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38930, 'rst66', 'php', 2, 6, 'Он, будучи образом Божиим, не почитал хищением быть равным Богу;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38929, 'rst66', 'php', 2, 5, 'Ибо в вас должны быть те же чувствования, какие и во Христе Иисусе:');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38928, 'rst66', 'php', 2, 4, 'Не о себе <i>только</i> каждый заботься, но каждый и о других.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38927, 'rst66', 'php', 2, 3, 'ничего <i>не делайте</i> по любопрению или по тщеславию, но по смиренномудрию почитайте один другого высшим себя.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38926, 'rst66', 'php', 2, 2, 'то дополните мою радость: имейте одни мысли, имейте ту же любовь, будьте единодушны и единомысленны;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38925, 'rst66', 'php', 2, 1, 'Итак, если <i>есть</i> какое утешение во Христе, если <i>есть</i> какая отрада любви, если <i>есть</i> какое общение духа, если <i>есть</i> какое милосердие и сострадательность,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38924, 'rst66', 'php', 1, 30, 'таким же подвигом, какой вы видели во мне и ныне слышите о мне.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38923, 'rst66', 'php', 1, 29, 'потому что вам дано ради Христа не только веровать в Него, но и страдать за Него');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38922, 'rst66', 'php', 1, 28, 'и не страшитесь ни в чем противников: это для них есть предзнаменование погибели, а для вас &mdash; спасения. И сие от Бога,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38921, 'rst66', 'php', 1, 27, 'Только живите достойно благовествования Христова, чтобы мне, приду ли я и увижу вас, или не приду, слышать о вас, что вы стоите в одном духе, подвизаясь единодушно за веру Евангельскую,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38920, 'rst66', 'php', 1, 26, 'дабы похвала ваша во Христе Иисусе умножилась через меня, при моем вторичном к вам пришествии.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38919, 'rst66', 'php', 1, 25, 'И я верно знаю, что останусь и пребуду со всеми вами для вашего успеха и радости в вере,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38918, 'rst66', 'php', 1, 24, 'а оставаться во плоти нужнее для вас.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38917, 'rst66', 'php', 1, 23, 'Влечет меня то и другое: имею желание разрешиться и быть со Христом, потому что это несравненно лучше;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38916, 'rst66', 'php', 1, 22, 'Если же жизнь во плоти <i>доставляет</i> плод моему делу, то не знаю, что избрать.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38915, 'rst66', 'php', 1, 21, 'Ибо для меня жизнь &mdash; Христос, и смерть &mdash; приобретение.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38914, 'rst66', 'php', 1, 20, 'при уверенности и надежде моей, что я ни в чем посрамлен не буду, но при всяком дерзновении и ныне, как и всегда, возвеличится Христос в теле моем, жизнью ли то, или смертью.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38913, 'rst66', 'php', 1, 19, 'ибо знаю, что это послужит мне во спасение по вашей молитве и содействием Духа Иисуса Христа,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38912, 'rst66', 'php', 1, 18, 'Но что до того? Как бы ни проповедали Христа, притворно или искренно, я и тому радуюсь и буду радоваться,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38911, 'rst66', 'php', 1, 17, 'а другие &mdash; из любви, зная, что я поставлен защищать благовествование.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38910, 'rst66', 'php', 1, 16, 'Одни по любопрению проповедуют Христа не чисто, думая увеличить тяжесть уз моих;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38909, 'rst66', 'php', 1, 15, 'Некоторые, правда, по зависти и любопрению, а другие с добрым расположением проповедуют Христа.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38908, 'rst66', 'php', 1, 14, 'и бо&#769;льшая часть из братьев в Господе, ободрившись узами моими, начали с большею смелостью, безбоязненно проповедовать слово Божие.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38907, 'rst66', 'php', 1, 13, 'так что узы мои о Христе сделались известными всей претории и всем прочим,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38906, 'rst66', 'php', 1, 12, 'Желаю, братия, чтобы вы знали, что обстоятельства мои послужили к большему успеху благовествования,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38905, 'rst66', 'php', 1, 11, 'исполнены плодов праведности Иисусом Христом, в славу и похвалу Божию.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38904, 'rst66', 'php', 1, 10, 'чтобы, познавая лучшее, вы были чисты и непреткновенны в день Христов,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38903, 'rst66', 'php', 1, 9, 'и молюсь о том, чтобы любовь ваша еще более и более возрастала в познании и всяком чувстве,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38902, 'rst66', 'php', 1, 8, 'Бог &mdash; свидетель, что я люблю всех вас любовью Иисуса Христа;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38901, 'rst66', 'php', 1, 7, 'как и должно мне помышлять о всех вас, потому что я имею вас в сердце в узах моих, при защищении и утверждении благовествования, вас всех, как соучастников моих в благодати.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38900, 'rst66', 'php', 1, 6, 'будучи уверен в том, что начавший в вас доброе дело будет совершать его даже до дня Иисуса Христа,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38899, 'rst66', 'php', 1, 5, 'за ваше участие в благовествовании от первого дня даже доныне,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38898, 'rst66', 'php', 1, 4, 'всегда во всякой молитве моей за всех вас принося с радостью молитву мою,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38897, 'rst66', 'php', 1, 3, 'Благодарю Бога моего при всяком воспоминании о вас,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38896, 'rst66', 'php', 1, 2, 'благодать вам и мир от Бога Отца нашего и Господа Иисуса Христа.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38895, 'rst66', 'php', 1, 1, 'Павел и Тимофей, рабы Иисуса Христа, всем святым во Христе Иисусе, находящимся в Филиппах, с епископами и диаконами:');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38894, 'rst66', 'eph', 6, 24, 'Благодать со всеми, неизменно любящими Господа нашего Иисуса Христа. Аминь.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38893, 'rst66', 'eph', 6, 23, 'Мир братиям и любовь с верою от Бога Отца и Господа Иисуса Христа.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38892, 'rst66', 'eph', 6, 22, 'которого я и послал к вам для того самого, чтобы вы узнали о нас и чтобы он утешил сердца ваши.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38891, 'rst66', 'eph', 6, 21, 'А дабы и вы знали о моих обстоятельствах и делах, обо всем известит вас Тихик, возлюбленный брат и верный в Господе служитель,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38890, 'rst66', 'eph', 6, 20, 'для которого я исполняю посольство в узах, дабы я смело проповедовал, ка&#769;к мне должно.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38889, 'rst66', 'eph', 6, 19, 'и о мне, дабы мне дано было слово &mdash; устами моими открыто с дерзновением возвещать тайну благовествования,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38888, 'rst66', 'eph', 6, 18, 'Всякою молитвою и прошением моли&#769;тесь во всякое время духом, и старайтесь о сем самом со всяким постоянством и молением о всех святых');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38887, 'rst66', 'eph', 6, 17, 'и шлем спасения возьмите, и меч духовный, который есть Слово Божие.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38886, 'rst66', 'eph', 6, 16, 'а паче всего возьмите щит веры, которым возможете угасить все раскаленные стрелы лукавого;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38885, 'rst66', 'eph', 6, 15, 'и обув ноги в готовность благовествовать мир;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38884, 'rst66', 'eph', 6, 14, 'Итак, станьте, препоясав чресла ваши истиною и облекшись в броню праведности,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38883, 'rst66', 'eph', 6, 13, 'Для сего приимите всеоружие Божие, дабы вы могли противостать в день злой и, все преодолев, устоять.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38882, 'rst66', 'eph', 6, 12, 'потому что наша брань не против крови и плоти, но против начальств, против властей, против мироправителей тьмы века сего, против духов злобы поднебесных.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38881, 'rst66', 'eph', 6, 11, 'Облекитесь во всеоружие Божие, чтобы вам можно было стать против козней диавольских,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38880, 'rst66', 'eph', 6, 10, 'Наконец, братия мои, укрепляйтесь Господом и могуществом силы Его.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38879, 'rst66', 'eph', 6, 9, 'И вы, господа, поступайте с ними так же, умеряя строгость, зная, что и над вами самими и над ними есть на небесах Господь, у Которого нет лицеприятия.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38878, 'rst66', 'eph', 6, 8, 'зная, что каждый получит от Господа по мере добра, которое он сделал, раб ли, или свободный.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38877, 'rst66', 'eph', 6, 7, 'служа с усердием, как Господу, а не как человекам,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38876, 'rst66', 'eph', 6, 6, 'не с видимою только услужливостью, как человекоугодники, но как рабы Христовы, исполняя волю Божию от души,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38875, 'rst66', 'eph', 6, 5, 'Рабы, повинуйтесь господам своим по плоти со страхом и трепетом, в простоте сердца вашего, как Христу,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38874, 'rst66', 'eph', 6, 4, 'И вы, отцы, не раздражайте детей ваших, но воспитывайте их в учении и наставлении Господнем.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38873, 'rst66', 'eph', 6, 3, '«да будет тебе благо, и будешь долголетен на земле».');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38872, 'rst66', 'eph', 6, 2, '«Почитай отца твоего и мать» &mdash; это первая заповедь с обетованием:');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38871, 'rst66', 'eph', 6, 1, 'Дети, повинуйтесь своим родителям в Господе, ибо сего <i>требует</i> справедливость.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38870, 'rst66', 'eph', 5, 33, 'Так каждый из вас да любит свою жену, как самого себя; а жена да боится своего мужа.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38869, 'rst66', 'eph', 5, 32, 'Тайна сия велика; я говорю по отношению ко Христу и к Церкви.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38868, 'rst66', 'eph', 5, 31, 'Посему оставит человек отца своего и мать и прилепится к жене своей, и будут двое одна плоть.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38867, 'rst66', 'eph', 5, 30, 'потому что мы члены Тела Его, от плоти Его и от костей Его.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38866, 'rst66', 'eph', 5, 29, 'Ибо никто никогда не имел ненависти к своей плоти, но питает и греет ее, как и Господь Церковь,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38865, 'rst66', 'eph', 5, 28, 'Так должны мужья любить своих жен, как свои тела: любящий свою жену любит самого себя.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38864, 'rst66', 'eph', 5, 27, 'чтобы представить ее Себе славною Церковью, не имеющею пятна, или порока, или чего‐либо подобного, но дабы она была свята и непорочна.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38863, 'rst66', 'eph', 5, 26, 'чтобы освятить ее, очистив банею водною, посредством слова;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38862, 'rst66', 'eph', 5, 25, 'Мужья, любите своих жен, как и Христос возлюбил Церковь и предал Себя за нее,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38861, 'rst66', 'eph', 5, 24, 'Но как Церковь повинуется Христу, так и жены своим мужьям во всем.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38860, 'rst66', 'eph', 5, 23, 'потому что муж есть глава жены, как и Христос глава Церкви, и Он же Спаситель тела.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38859, 'rst66', 'eph', 5, 22, 'Жены, повинуйтесь своим мужьям, как Господу,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38858, 'rst66', 'eph', 5, 21, 'повинуясь друг другу в страхе Божием.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38857, 'rst66', 'eph', 5, 20, 'благодаря всегда за все Бога и Отца, во имя Господа нашего Иисуса Христа,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38856, 'rst66', 'eph', 5, 19, 'назидая самих себя псалмами и славословиями и песнопениями духовными, поя и воспевая в сердцах ваших Господу,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38855, 'rst66', 'eph', 5, 18, 'И не упивайтесь вином, от которого бывает распутство; но исполняйтесь Духом,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38854, 'rst66', 'eph', 5, 17, 'Итак, не будьте нерассудительны, но познавайте, что&#769; есть воля Божия.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38853, 'rst66', 'eph', 5, 16, 'дорожа временем, потому что дни лукавы.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38852, 'rst66', 'eph', 5, 15, 'Итак, смотри&#769;те, поступайте осторожно, не как неразумные, но как мудрые,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38851, 'rst66', 'eph', 5, 14, 'Посему сказано: «встань, спящий, и воскресни из мертвых, и осветит тебя Христос».');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38850, 'rst66', 'eph', 5, 13, 'Все же обнаруживаемое делается явным от света, ибо все, делающееся явным, свет есть.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38849, 'rst66', 'eph', 5, 12, 'Ибо о том, что&#769; они делают тайно, стыдно и говорить.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38848, 'rst66', 'eph', 5, 11, 'и не участвуйте в бесплодных делах тьмы, но и обличайте.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38847, 'rst66', 'eph', 5, 10, 'Испытывайте, что благоугодно Богу,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38846, 'rst66', 'eph', 5, 9, 'потому что плод Духа состоит во всякой благости, праведности и истине.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38845, 'rst66', 'eph', 5, 8, 'Вы были некогда тьма, а теперь &mdash; свет в Господе: поступайте, как чада света,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38844, 'rst66', 'eph', 5, 7, 'итак, не будьте сообщниками их.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38843, 'rst66', 'eph', 5, 6, 'Никто да не обольщает вас пустыми словами, ибо за это приходит гнев Божий на сынов противления;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38842, 'rst66', 'eph', 5, 5, 'ибо знайте, что никакой блудник, или нечистый, или любостяжатель, который есть идолослужитель, не имеет наследия в Царстве Христа и Бога.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38841, 'rst66', 'eph', 5, 4, 'Также сквернословие и пустословие и смехотворство не приличны <i>вам</i>, а, напротив, благодарение;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38840, 'rst66', 'eph', 5, 3, 'А блуд и всякая нечистота и любостяжание не должны даже именоваться у вас, как прилично святым.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38839, 'rst66', 'eph', 5, 2, 'и живите в любви, как и Христос возлюбил нас и предал Себя за нас в приношение и жертву Богу, в благоухание приятное.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38838, 'rst66', 'eph', 5, 1, 'Итак, подражайте Богу, как чада возлюбленные,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38837, 'rst66', 'eph', 4, 32, 'но будьте друг ко другу добры, сострадательны, прощайте друг друга, как и Бог во Христе простил вас.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38836, 'rst66', 'eph', 4, 31, 'Всякое раздражение и ярость, и гнев, и крик, и злоречие со всякою злобою да будут удалены от вас;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38835, 'rst66', 'eph', 4, 30, 'И не оскорбляйте Святого Духа Божия, Которым вы запечатлены в день искупления.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38834, 'rst66', 'eph', 4, 29, 'Никакое гнилое слово да не исходит из уст ваших, а только доброе для назидания в вере, дабы оно доставляло благодать слушающим.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38833, 'rst66', 'eph', 4, 28, 'Кто крал, вперед не кради, а лучше трудись, делая своими руками полезное, чтобы было из чего уделять нуждающемуся.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38832, 'rst66', 'eph', 4, 27, 'и не давайте места диаволу.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38831, 'rst66', 'eph', 4, 26, 'Гневаясь, не согрешайте: солнце да не зайдет во гневе вашем;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38830, 'rst66', 'eph', 4, 25, 'Посему, отвергнув ложь, говорите истину каждый ближнему своему, потому что мы члены друг другу.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38829, 'rst66', 'eph', 4, 24, 'и облечься в нового человека, созданного по Богу, в праведности и святости истины.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38828, 'rst66', 'eph', 4, 23, 'а обновиться духом ума вашего');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38827, 'rst66', 'eph', 4, 22, 'отложить прежний образ жизни ветхого человека, истлевающего в обольстительных похотях,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38826, 'rst66', 'eph', 4, 21, 'потому что вы слышали о Нем и в Нем научились, &mdash; так как истина во Иисусе, &mdash;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38825, 'rst66', 'eph', 4, 20, 'Но вы не так познали Христа;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38824, 'rst66', 'eph', 4, 19, 'Они, дойдя до бесчувствия, предались распутству так, что делают всякую нечистоту с ненасытимостью.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38823, 'rst66', 'eph', 4, 18, 'будучи помрачены в разуме, отчуждены от жизни Божией, по причине их невежества и ожесточения сердца их.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38822, 'rst66', 'eph', 4, 17, 'Посему я говорю и заклинаю Господом, чтобы вы более не поступали, как поступают прочие народы, по суетности ума своего,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38821, 'rst66', 'eph', 4, 16, 'из Которого все тело, составляемое и совокупляемое посредством всяких взаимно скрепляющих связей, при действии в свою меру каждого члена, получает приращение для созидания самого себя в любви.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38820, 'rst66', 'eph', 4, 15, 'но истинною любовью все возращали в Того, Который есть глава Христос,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38819, 'rst66', 'eph', 4, 14, 'дабы мы не были более младенцами, колеблющимися и увлекающимися всяким ветром учения, по лукавству человеков, по хитрому искусству обольщения,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38818, 'rst66', 'eph', 4, 13, 'доколе все придем в единство веры и познания Сына Божия, в мужа совершенного, в меру полного возраста Христова;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38817, 'rst66', 'eph', 4, 12, 'к совершению святых, на дело служения, для созидания Тела Христова,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38816, 'rst66', 'eph', 4, 11, 'И Он поставил одних Апостолами, других &mdash; пророками, иных &mdash; Евангелистами, иных &mdash; пастырями и учителями,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38815, 'rst66', 'eph', 4, 10, 'Нисшедший, Он же есть и восшедший превыше всех небес, дабы наполнить все.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38814, 'rst66', 'eph', 4, 9, 'А «восшел» что означает, как не то, что Он и нисходил прежде в преисподние места земли?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38813, 'rst66', 'eph', 4, 8, 'Посему и сказано: «восшед на высоту, пленил плен и дал дары человекам».');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38812, 'rst66', 'eph', 4, 7, 'Каждому же из нас дана благодать по мере дара Христова.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38811, 'rst66', 'eph', 4, 6, 'один Бог и Отец всех, Который над всеми, и через всех, и во всех нас.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38810, 'rst66', 'eph', 4, 5, 'один Господь, одна вера, одно крещение,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38809, 'rst66', 'eph', 4, 4, 'Одно тело и один дух, как вы и призваны к одной надежде вашего звания;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38808, 'rst66', 'eph', 4, 3, 'стараясь сохранять единство духа в союзе мира.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38807, 'rst66', 'eph', 4, 2, 'со всяким смиренномудрием и кротостью и долготерпением, снисходя друг ко другу любовью,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38806, 'rst66', 'eph', 4, 1, 'Итак, я, узник в Господе, умоляю вас поступать достойно звания, в которое вы призваны,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38805, 'rst66', 'eph', 3, 21, 'Тому слава в Церкви во Христе Иисусе во все роды, от века до века. Аминь.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38804, 'rst66', 'eph', 3, 20, 'А Тому, Кто действующею в нас силою может сделать несравненно больше всего, чего мы просим, или о чем помышляем,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38803, 'rst66', 'eph', 3, 19, 'и уразуметь превосходящую разумение любовь Христову, дабы вам исполниться всею полнотою Божиею.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38802, 'rst66', 'eph', 3, 18, 'чтобы вы, укорененные и утвержденные в любви, могли постигнуть со всеми святыми, что&#769; широта и долгота, и глубина и высота,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38801, 'rst66', 'eph', 3, 17, 'верою вселиться Христу в сердца ваши,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38800, 'rst66', 'eph', 3, 16, 'да даст вам, по богатству славы Своей, крепко утвердиться Духом Его во внутреннем человеке,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38799, 'rst66', 'eph', 3, 15, 'от Которого именуется всякое отечество на небесах и на земле,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38798, 'rst66', 'eph', 3, 14, 'Для сего преклоняю колени мои пред Отцом Господа нашего Иисуса Христа,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38797, 'rst66', 'eph', 3, 13, 'Посему прошу вас не унывать при моих ради вас скорбях, которые суть ваша слава.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38796, 'rst66', 'eph', 3, 12, 'в Котором мы имеем дерзновение и надежный доступ через веру в Него.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38795, 'rst66', 'eph', 3, 11, 'по предвечному определению, которое Он исполнил во Христе Иисусе, Господе нашем,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38794, 'rst66', 'eph', 3, 10, 'дабы ныне соделалась известною через Церковь начальствам и властям на небесах многоразличная премудрость Божия,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38793, 'rst66', 'eph', 3, 9, 'и открыть всем, в чем состоит домостроительство тайны, сокрывавшейся от вечности в Боге, создавшем все Иисусом Христом,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38792, 'rst66', 'eph', 3, 8, 'Мне, наименьшему из всех святых, дана благодать сия &mdash; благовествовать язычникам неисследимое богатство Христово');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38791, 'rst66', 'eph', 3, 7, 'которого служителем сделался я по дару благодати Божией, данной мне действием силы Его.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38790, 'rst66', 'eph', 3, 6, 'чтобы и язычникам быть сонаследниками, составляющими одно тело, и сопричастниками обетования Его во Христе Иисусе посредством благовествования,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38789, 'rst66', 'eph', 3, 5, 'которая не была возвещена прежним поколениям сынов человеческих, как ныне открыта святым Апостолам Его и пророкам Духом Святым,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38788, 'rst66', 'eph', 3, 4, 'то вы, читая, можете усмотреть мое разумение тайны Христовой,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38787, 'rst66', 'eph', 3, 3, 'потому что мне через откровение возвещена тайна (о чем я и выше писал кратко),');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38786, 'rst66', 'eph', 3, 2, 'Как вы слышали о домостроительстве благодати Божией, данной мне для вас,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38785, 'rst66', 'eph', 3, 1, 'Для сего‐то я, Павел, <i>сделался</i> узником Иисуса Христа за вас язычников.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38784, 'rst66', 'eph', 2, 22, 'на котором и вы устрояетесь в жилище Божие Духом.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38783, 'rst66', 'eph', 2, 21, 'на котором все здание, слагаясь стройно, возрастает в святой храм в Господе,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38782, 'rst66', 'eph', 2, 20, 'быв утверждены на основании Апостолов и пророков, имея Самого Иисуса Христа краеугольным <i>камнем</i>,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38781, 'rst66', 'eph', 2, 19, 'Итак, вы уже не чужие и не пришельцы, но сограждане святым и свои Богу,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38780, 'rst66', 'eph', 2, 18, 'потому что через Него и те и другие имеем доступ к Отцу, в одном Духе.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38779, 'rst66', 'eph', 2, 17, 'И, придя, благовествовал мир вам, дальним и близким,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38778, 'rst66', 'eph', 2, 16, 'и в одном теле примирить обоих с Богом посредством креста, убив вражду на нем.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38777, 'rst66', 'eph', 2, 15, 'упразднив вражду Плотию Своею, а закон заповедей учением, дабы из двух создать в Себе Самом одного нового человека, устрояя мир,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38776, 'rst66', 'eph', 2, 14, 'Ибо Он есть мир наш, соделавший из обоих одно и разрушивший стоявшую посреди преграду,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38775, 'rst66', 'eph', 2, 13, 'А теперь во Христе Иисусе вы, бывшие некогда далеко, стали близки Кровию Христовою.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38774, 'rst66', 'eph', 2, 12, 'что вы были в то время без Христа, отчуждены от общества Израильского, чужды заветов обетования, не имели надежды и были безбожники в мире.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38773, 'rst66', 'eph', 2, 11, 'Итак, помните, что вы, некогда язычники по плоти, которых называли необрезанными так называемые обрезанные плотским <i>обрезанием</i>, совершаемым руками,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38772, 'rst66', 'eph', 2, 10, 'Ибо мы &mdash; Его творение, созданы во Христе Иисусе на добрые дела, которые Бог предназначил нам исполнять.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38771, 'rst66', 'eph', 2, 9, 'не от дел, чтобы никто не хвалился.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38770, 'rst66', 'eph', 2, 8, 'Ибо благодатью вы спасены через веру, и сие не от вас, Божий дар:');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38769, 'rst66', 'eph', 2, 7, 'дабы явить в грядущих веках преизобильное богатство благодати Своей в благости к нам во Христе Иисусе.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38768, 'rst66', 'eph', 2, 6, 'и воскресил с Ним, и посадил на небесах во Христе Иисусе,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38767, 'rst66', 'eph', 2, 5, 'и нас, мертвых по преступлениям, оживотворил со Христом, &mdash; благодатью вы спасены, &mdash;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38766, 'rst66', 'eph', 2, 4, 'Бог, богатый милостью, по Своей великой любви, которою возлюбил нас,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38765, 'rst66', 'eph', 2, 3, 'между которыми и мы все жили некогда по нашим плотским похотям, исполняя желания плоти и помыслов, и были по природе чадами гнева, как и прочие,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38764, 'rst66', 'eph', 2, 2, 'в которых вы некогда жили, по обычаю мира сего, по <i>воле</i> князя, господствующего в воздухе, духа, действующего ныне в сынах противления,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38763, 'rst66', 'eph', 2, 1, 'И вас, мертвых по преступлениям и грехам вашим,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38762, 'rst66', 'eph', 1, 23, 'которая есть Тело Его, полнота Наполняющего все во всем.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38761, 'rst66', 'eph', 1, 22, 'и все покорил под ноги Его, и поставил Его выше всего, главою Церкви,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38760, 'rst66', 'eph', 1, 21, 'превыше всякого начальства, и власти, и силы, и господства, и всякого имени, именуемого не только в сем веке, но и в будущем,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38759, 'rst66', 'eph', 1, 20, 'которою Он воздействовал во Христе, воскресив Его из мертвых и посадив одесную Себя на небесах,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38758, 'rst66', 'eph', 1, 19, 'и как безмерно величие могущества Его в нас, верующих по действию державной силы Его,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38757, 'rst66', 'eph', 1, 18, 'и просветил очи сердца вашего, дабы вы познали, в чем состоит надежда призвания Его, и какое богатство славного наследия Его для святых,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38756, 'rst66', 'eph', 1, 17, 'чтобы Бог Господа нашего Иисуса Христа, Отец славы, дал вам Духа премудрости и откровения к познанию Его,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38755, 'rst66', 'eph', 1, 16, 'непрестанно благодарю за вас <i>Бога</i>, вспоминая о вас в молитвах моих,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38754, 'rst66', 'eph', 1, 15, 'Посему и я, услышав о вашей вере во Христа Иисуса и о любви ко всем святым,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38753, 'rst66', 'eph', 1, 14, 'Который есть залог наследия нашего, для искупления удела <i>Его</i>, в похвалу славы Его.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38752, 'rst66', 'eph', 1, 13, 'В Нем и вы, услышав слово истины, благовествование вашего спасения, и уверовав в Него, запечатлены обетованным Святым Духом,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38751, 'rst66', 'eph', 1, 12, 'дабы послужить к похвале славы Его нам, которые ранее уповали на Христа.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38750, 'rst66', 'eph', 1, 11, 'В Нем мы и сделались наследниками, быв предназначены <i>к тому</i> по определению Совершающего все по изволению воли Своей,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38749, 'rst66', 'eph', 1, 10, 'в устроении полноты времен, дабы все небесное и земное соединить под главою Христом.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38748, 'rst66', 'eph', 1, 9, 'открыв нам тайну Своей воли по Своему благоволению, которое Он прежде положил в Нем,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38747, 'rst66', 'eph', 1, 8, 'каковую Он в преизбытке даровал нам во всякой премудрости и разумении,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38746, 'rst66', 'eph', 1, 7, 'в Котором мы имеем искупление Кровию Его, прощение грехов, по богатству благодати Его,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38745, 'rst66', 'eph', 1, 6, 'в похвалу славы благодати Своей, которою Он облагодатствовал нас в Возлюбленном,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38744, 'rst66', 'eph', 1, 5, 'предопределив усыновить нас Себе чрез Иисуса Христа, по благоволению воли Своей,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38743, 'rst66', 'eph', 1, 4, 'так как Он избрал нас в Нем прежде создания мира, чтобы мы были святы и непорочны пред Ним в любви,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38742, 'rst66', 'eph', 1, 3, 'Благословен Бог и Отец Господа нашего Иисуса Христа, благословивший нас во Христе всяким духовным благословением в небесах,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38741, 'rst66', 'eph', 1, 2, 'благодать вам и мир от Бога Отца нашего и Господа Иисуса Христа.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38740, 'rst66', 'eph', 1, 1, 'Павел, волею Божиею Апостол Иисуса Христа, находящимся в Ефесе святым и верным во Христе Иисусе:');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38739, 'rst66', 'gal', 6, 18, 'Благодать Господа нашего Иисуса Христа со духом вашим, братия. Аминь.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38738, 'rst66', 'gal', 6, 17, 'Впрочем, никто не отягощай меня, ибо я ношу язвы Господа Иисуса на теле моем.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38737, 'rst66', 'gal', 6, 16, 'Тем, которые поступают по сему правилу, мир им и милость, и Израилю Божию.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38736, 'rst66', 'gal', 6, 15, 'Ибо во Христе Иисусе ничего не значит ни обрезание, ни необрезание, а новая тварь.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38735, 'rst66', 'gal', 6, 14, 'А я не желаю хвалиться, разве только крестом Господа нашего Иисуса Христа, которым для меня мир распят, и я для мира.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38734, 'rst66', 'gal', 6, 13, 'ибо и сами обрезывающиеся не соблюдают закона, но хотят, чтобы вы обрезывались, дабы похвалиться в вашей плоти.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38733, 'rst66', 'gal', 6, 12, 'Желающие хвалиться по плоти принуждают вас обрезываться только для того, чтобы не быть гонимыми за крест Христов,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38732, 'rst66', 'gal', 6, 11, 'Видите, как много написал я вам своею рукою.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38731, 'rst66', 'gal', 6, 10, 'Итак, доколе есть время, будем делать добро всем, а наипаче своим по вере.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38730, 'rst66', 'gal', 6, 9, 'Делая добро, да не унываем, ибо в свое время пожнем, если не ослабеем.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38729, 'rst66', 'gal', 6, 8, 'сеющий в плоть свою от плоти пожнет тление, а сеющий в дух от духа пожнет жизнь вечную.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38728, 'rst66', 'gal', 6, 7, 'Не обманывайтесь: Бог поругаем не бывает. Что посеет человек, то и пожнет:');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38727, 'rst66', 'gal', 6, 6, 'Наставляемый словом, делись всяким добром с наставляющим.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38726, 'rst66', 'gal', 6, 5, 'ибо каждый понесет свое бремя.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38725, 'rst66', 'gal', 6, 4, 'Каждый да испытывает свое дело, и тогда будет иметь похвалу только в себе, а не в другом,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38724, 'rst66', 'gal', 6, 3, 'Ибо кто почитает себя чем‐нибудь, будучи ничто, тот обольщает сам себя.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38723, 'rst66', 'gal', 6, 2, 'Носите бремена друг друга, и таким образом исполните закон Христов.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38722, 'rst66', 'gal', 6, 1, 'Братия! если и впадет человек в какое согрешение, вы, духовные, исправляйте такового в духе кротости, наблюдая каждый за собою, чтобы не быть искушенным.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38721, 'rst66', 'gal', 5, 26, 'Не будем тщеславиться, друг друга раздражать, друг другу завидовать.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38720, 'rst66', 'gal', 5, 25, 'Если мы живем духом, то по духу и поступать должны.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38719, 'rst66', 'gal', 5, 24, 'Но те, которые Христовы, распяли плоть со страстями и похотями.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38718, 'rst66', 'gal', 5, 23, 'кротость, воздержание. На таковых нет закона.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38717, 'rst66', 'gal', 5, 22, 'Плод же духа: любовь, радость, мир, долготерпение, благость, милосердие, вера,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38716, 'rst66', 'gal', 5, 21, 'ненависть, убийства, пьянство, бесчинство и тому подобное. Предваряю вас, как и прежде предварял, что поступающие так Царствия Божия не наследуют.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38715, 'rst66', 'gal', 5, 20, 'идолослужение, волшебство, вражда, ссоры, зависть, гнев, распри, разногласия, [соблазны,] ереси,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38714, 'rst66', 'gal', 5, 19, 'Дела плоти известны; они суть: прелюбодеяние, блуд, нечистота, непотребство,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38713, 'rst66', 'gal', 5, 18, 'Если же вы духом водитесь, то вы не под законом.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38712, 'rst66', 'gal', 5, 17, 'ибо плоть желает противного духу, а дух &mdash; противного плоти: они друг другу противятся, так что вы не то делаете, что хотели бы.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38711, 'rst66', 'gal', 5, 16, 'Я говорю: поступайте по духу, и вы не будете исполнять вожделений плоти,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38710, 'rst66', 'gal', 5, 15, 'Если же друг друга угрызаете и съедаете, берегитесь, чтобы вы не были истреблены друг другом.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38709, 'rst66', 'gal', 5, 14, 'Ибо весь закон в одном слове заключается: «люби ближнего твоего, как самого себя».');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38708, 'rst66', 'gal', 5, 13, 'К свободе призваны вы, братия, только бы свобода ваша не была поводом к <i>угождению</i> плоти, но любовью служите друг другу.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38707, 'rst66', 'gal', 5, 12, 'О, если бы удалены были возмущающие вас!');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38706, 'rst66', 'gal', 5, 11, 'За что же гонят меня, братия, если я и теперь проповедую обрезание? Тогда соблазн креста прекратился бы.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38705, 'rst66', 'gal', 5, 10, 'Я уверен о вас в Господе, что вы не будете мыслить иначе; а смущающий вас, кто бы он ни был, понесет на себе осуждение.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38704, 'rst66', 'gal', 5, 9, 'Малая закваска заквашивает все тесто.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38703, 'rst66', 'gal', 5, 8, 'Такое убеждение не от Призывающего вас.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38702, 'rst66', 'gal', 5, 7, 'Вы шли хорошо: кто остановил вас, чтобы вы не покорялись истине?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38701, 'rst66', 'gal', 5, 6, 'Ибо во Христе Иисусе не имеет силы ни обрезание, ни необрезание, но вера, действующая любовью.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38700, 'rst66', 'gal', 5, 5, 'а мы духом ожидаем и надеемся праведности от веры.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38699, 'rst66', 'gal', 5, 4, 'Вы, оправдывающие себя законом, остались без Христа, отпали от благодати,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38698, 'rst66', 'gal', 5, 3, 'Еще свидетельствую всякому человеку обрезывающемуся, что он должен исполнить весь закон.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38697, 'rst66', 'gal', 5, 2, 'Вот, я, Павел, говорю вам: если вы обрезываетесь, не будет вам никакой пользы от Христа.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38696, 'rst66', 'gal', 5, 1, 'Итак, стойте в свободе, которую даровал нам Христос, и не подвергайтесь опять игу рабства.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38695, 'rst66', 'gal', 4, 31, 'Итак, братия, мы дети не рабы, но свободной.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38694, 'rst66', 'gal', 4, 30, 'Что же говорит Писание? «Изгони рабу и сына ее, ибо сын рабы не будет наследником вместе с сыном свободной».');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38693, 'rst66', 'gal', 4, 29, 'Но как тогда рожденный по плоти гнал <i>рожденного</i> по духу, так и ныне.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38692, 'rst66', 'gal', 4, 28, 'Мы, братия, дети обетования по Исааку.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38691, 'rst66', 'gal', 4, 27, 'Ибо написано: «возвеселись, неплодная, нерождающая; воскликни и возгласи, не мучившаяся родами; потому что у оставленной гораздо более детей, нежели у имеющей мужа».');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38690, 'rst66', 'gal', 4, 26, 'а вышний Иерусалим свободен: он &mdash; матерь всем нам.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38689, 'rst66', 'gal', 4, 25, 'ибо Агарь означает гору Синай в Аравии и соответствует нынешнему Иерусалиму, потому что он с детьми своими в рабстве;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38688, 'rst66', 'gal', 4, 24, 'В этом есть иносказание. Это два завета: один от горы Синайской, рождающий в рабство, который есть Агарь,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38687, 'rst66', 'gal', 4, 23, 'Но который от рабы, тот рожден по плоти; а который от свободной, тот по обетованию.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38686, 'rst66', 'gal', 4, 22, 'Ибо написано: «Авраам имел двух сынов, одного от рабы, а другого от свободной».');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38685, 'rst66', 'gal', 4, 21, 'Скажите мне вы, желающие быть под законом: разве вы не слушаете закона?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38684, 'rst66', 'gal', 4, 20, 'Хотел бы я теперь быть у вас и изменить голос мой, потому что я в недоумении о вас.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38683, 'rst66', 'gal', 4, 19, 'Дети мои, для которых я снова в муках рождения, доколе не изобразится в вас Христос!');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38682, 'rst66', 'gal', 4, 18, 'Хорошо ревновать в добром всегда, а не в моем только присутствии у вас.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38681, 'rst66', 'gal', 4, 17, 'Ревнуют по вас нечисто, а хотят вас отлучить, чтобы вы ревновали по них.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38680, 'rst66', 'gal', 4, 16, 'Итак, неужели я сделался врагом вашим, говоря вам истину?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38679, 'rst66', 'gal', 4, 15, 'Как вы были блаженны! Свидетельствую о вас, что, если бы возможно было, вы исторгли бы очи свои и отдали мне.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38678, 'rst66', 'gal', 4, 14, 'но вы не презрели искушения моего во плоти моей и не возгнушались <i>им</i>, а приняли меня, как Ангела Божия, как Христа Иисуса.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38677, 'rst66', 'gal', 4, 13, 'знаете, что, <i>хотя</i> я в немощи плоти благовествовал вам в первый раз,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38676, 'rst66', 'gal', 4, 12, 'Прошу вас, братия, будьте, как я, потому что и я, как вы. Вы ничем не обидели меня:');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38675, 'rst66', 'gal', 4, 11, 'Боюсь за вас, не напрасно ли я трудился у вас.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38674, 'rst66', 'gal', 4, 10, 'Наблюдаете дни, месяцы, времена и годы.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38673, 'rst66', 'gal', 4, 9, 'Ныне же, познав Бога, или, лучше, получив познание от Бога, для чего возвращаетесь опять к немощным и бедным вещественным началам и хотите еще снова поработить себя им?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38672, 'rst66', 'gal', 4, 8, 'Но тогда, не знав Бога, вы служили <i>богам</i>, которые в существе не боги.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38671, 'rst66', 'gal', 4, 7, 'Посему ты уже не раб, но сын; а если сын, то и наследник Божий через Иисуса Христа.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38670, 'rst66', 'gal', 4, 6, 'А как вы &mdash; сыны, то Бог послал в сердца ваши Духа Сына Своего, вопиющего: «авва, Отче!»');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38669, 'rst66', 'gal', 4, 5, 'чтобы искупить подзаконных, дабы нам получить усыновление.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38668, 'rst66', 'gal', 4, 4, 'но когда пришла полнота времени, Бог послал Сына Своего [Единородного], Который родился от жены, подчинился закону,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38667, 'rst66', 'gal', 4, 3, 'Так и мы, доколе были в детстве, были порабощены вещественным началам мира;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38666, 'rst66', 'gal', 4, 2, 'он подчинен попечителям и домоправителям до срока, отцом <i>назначенного</i>.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38665, 'rst66', 'gal', 4, 1, 'Еще скажу: наследник, доколе в детстве, ничем не отличается от раба, хотя и господин всего:');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38664, 'rst66', 'gal', 3, 29, 'Если же вы Христовы, то вы семя Авраамово и по обетованию наследники.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38663, 'rst66', 'gal', 3, 28, 'Нет уже Иудея, ни язычника; нет раба, ни свободного; нет мужеского пола, ни женского: ибо все вы одно во Христе Иисусе.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38662, 'rst66', 'gal', 3, 27, 'все вы, во Христа крестившиеся, во Христа облеклись.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38661, 'rst66', 'gal', 3, 26, 'Ибо все вы сыны Божии по вере во Христа Иисуса;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38660, 'rst66', 'gal', 3, 25, 'по пришествии же веры, мы уже не под <i>руководством</i> детоводителя.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38659, 'rst66', 'gal', 3, 24, 'Итак, закон был для нас детоводителем ко Христу, дабы нам оправдаться верою;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38658, 'rst66', 'gal', 3, 23, 'А до пришествия веры мы заключены были под стражею закона, до того <i>времени</i>, как надлежало открыться вере.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38657, 'rst66', 'gal', 3, 22, 'но Писание всех заключило под грехом, дабы обетование верующим дано было по вере в Иисуса Христа.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38656, 'rst66', 'gal', 3, 21, 'Итак, закон противен обетованиям Божиим? Никак! Ибо если бы дан был закон, могущий животворить, то подлинно праведность была бы от закона;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38655, 'rst66', 'gal', 3, 20, 'Но посредник при одном не бывает, а Бог один.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38654, 'rst66', 'gal', 3, 19, 'Для чего же закон? Он дан после по причине преступлений, до времени пришествия семени, к которому <i>относится</i> обетование, и преподан через Ангелов рукою посредника.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38653, 'rst66', 'gal', 3, 18, 'Ибо если по закону наследство, то уже не по обетованию; но Аврааму Бог даровал <i>оное</i> по обетованию.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38652, 'rst66', 'gal', 3, 17, 'Я говорю то, что завета о Христе, прежде Богом утвержденного, закон, явившийся спустя четыреста тридцать лет, не отменяет та&#769;к, чтобы обетование потеряло силу.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38651, 'rst66', 'gal', 3, 16, 'Но Аврааму даны были обетования и семени его. Не сказано: «и потомкам», как бы о многих, но как об одном: «и семени твоему», которое есть Христос.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38650, 'rst66', 'gal', 3, 15, 'Братия! говорю по <i>рассуждению</i> человеческому: даже человеком утвержденного завещания никто не отменяет и не прибавляет <i>к нему</i>.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38649, 'rst66', 'gal', 3, 14, 'дабы благословение Авраамово через Христа Иисуса распространилось на язычников, чтобы нам получить обещанного Духа верою.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38648, 'rst66', 'gal', 3, 13, 'Христос искупил нас от клятвы закона, сделавшись за нас клятвою (ибо написано: «проклят всяк, висящий на древе»),');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38647, 'rst66', 'gal', 3, 12, 'А закон не по вере; но кто исполняет его, тот жив будет им.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38646, 'rst66', 'gal', 3, 11, 'А что законом никто не оправдывается пред Богом, это ясно, потому что праведный верою жив будет.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38645, 'rst66', 'gal', 3, 10, 'А все, утверждающиеся на делах закона, находятся под клятвою. Ибо написано: «проклят всяк, кто не исполняет постоянно всего, что написано в книге закона».');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38644, 'rst66', 'gal', 3, 9, 'Итак, верующие благословляются с верным Авраамом.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38643, 'rst66', 'gal', 3, 8, 'И Писание, провидя, что Бог верою оправдает язычников, предвозвестило Аврааму: «в тебе благословятся все народы».');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38642, 'rst66', 'gal', 3, 7, 'Познайте же, что верующие суть сыны Авраама.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38641, 'rst66', 'gal', 3, 6, 'Так Авраам поверил Богу, и это вменилось ему в праведность.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38640, 'rst66', 'gal', 3, 5, 'Подающий вам Духа и совершающий между вами чудеса через дела ли закона <i>сие производит</i> или через наставление в вере?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38639, 'rst66', 'gal', 3, 4, 'Столь многое потерпели вы неужели без пользы? О, если бы только без пользы!');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38638, 'rst66', 'gal', 3, 3, 'Так ли вы несмысленны, что, начав духом, теперь оканчиваете плотью?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38637, 'rst66', 'gal', 3, 2, 'Сие только хочу знать от вас: через дела ли закона вы получили Духа или через наставление в вере?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38636, 'rst66', 'gal', 3, 1, 'О, несмысленные Галаты! кто прельстил вас не покоряться истине, <i>вас</i>, у которых перед глазами предначертан был Иисус Христос, <i>как бы</i> у вас распятый?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38635, 'rst66', 'gal', 2, 21, 'Не отвергаю благодати Божией; а если законом оправдание, то Христос напрасно умер.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38634, 'rst66', 'gal', 2, 20, 'и уже не я живу, но живет во мне Христос. А что ныне живу во плоти, то живу верою в Сына Божия, возлюбившего меня и предавшего Себя за меня.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38633, 'rst66', 'gal', 2, 19, 'Законом я умер для закона, чтобы жить для Бога. Я сораспялся Христу,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38632, 'rst66', 'gal', 2, 18, 'Ибо если я снова созидаю, что разрушил, то сам себя делаю преступником.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38631, 'rst66', 'gal', 2, 17, 'Если же, ища оправдания во Христе, мы и сами оказались грешниками, то неужели Христос есть служитель греха? Никак.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38630, 'rst66', 'gal', 2, 16, 'однако же, узнав, что человек оправдывается не делами закона, а только верою в Иисуса Христа, и мы уверовали во Христа Иисуса, чтобы оправдаться верою во Христа, а не делами закона; ибо делами закона не оправдается никакая плоть.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38629, 'rst66', 'gal', 2, 15, 'Мы по природе Иудеи, а не из язычников грешники;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38628, 'rst66', 'gal', 2, 14, 'Но когда я увидел, что они не прямо поступают по истине Евангельской, то сказал Петру при всех: если ты, будучи Иудеем, живешь по‐язычески, а не по‐иудейски, то для чего язычников принуждаешь жить по‐иудейски?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38627, 'rst66', 'gal', 2, 13, 'Вместе с ним лицемерили и прочие Иудеи, так что даже Варнава был увлечен их лицемерием.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38626, 'rst66', 'gal', 2, 12, 'Ибо, до прибытия некоторых от Иакова, ел вместе с язычниками; а когда те пришли, стал таиться и устраняться, опасаясь обрезанных.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38625, 'rst66', 'gal', 2, 11, 'Когда же Петр пришел в Антиохию, то я лично противостал ему, потому что он подвергался нареканию.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38624, 'rst66', 'gal', 2, 10, 'только чтобы мы помнили нищих, что и старался я исполнять в точности.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38623, 'rst66', 'gal', 2, 9, 'и, узнав о благодати, данной мне, Иаков и Кифа и Иоанн, почитаемые столпами, подали мне и Варнаве руку общения, чтобы нам <i>идти</i> к язычникам, а им &mdash; к обрезанным,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38622, 'rst66', 'gal', 2, 8, 'ибо Содействовавший Петру в апостольстве у обрезанных содействовал и мне у язычников, &mdash;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38621, 'rst66', 'gal', 2, 7, 'Напротив того, увидев, что мне вверено благовестие для необрезанных, как Петру для обрезанных, &mdash;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38620, 'rst66', 'gal', 2, 6, 'И в знаменитых чем‐либо, какими бы ни были они когда‐либо, для меня нет ничего особенного: Бог не взирает на лицо человека. И знаменитые не возложили на меня ничего более.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38619, 'rst66', 'gal', 2, 5, 'мы ни на час не уступили и не покорились, дабы истина благовествования сохранилась у вас.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38618, 'rst66', 'gal', 2, 4, 'а вкравшимся лжебратиям, скрытно приходившим подсмотреть за нашею свободою, которую мы имеем во Христе Иисусе, чтобы поработить нас,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38617, 'rst66', 'gal', 2, 3, 'Но они и Тита, бывшего со мною, хотя и Еллина, не принуждали обрезаться,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38616, 'rst66', 'gal', 2, 2, 'Ходил же по откровению и предложил там, и особо знаменитейшим, благовествование, проповедуемое мною язычникам, не напрасно ли я подвизаюсь или подвизался.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38615, 'rst66', 'gal', 2, 1, 'Потом, через четырнадцать лет, опять ходил я в Иерусалим с Варнавою, взяв с собою и Тита.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38614, 'rst66', 'gal', 1, 24, 'и прославляли за меня Бога.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38613, 'rst66', 'gal', 1, 23, 'а только слышали они, что гнавший их некогда ныне благовествует веру, которую прежде истреблял, &mdash;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38612, 'rst66', 'gal', 1, 22, 'Церквам Христовым в Иудее лично я не был известен,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38611, 'rst66', 'gal', 1, 21, 'После сего отошел я в страны Сирии и Киликии.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38610, 'rst66', 'gal', 1, 20, 'А в том, что&#769; пишу вам, &mdash; пред Богом, не лгу.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38609, 'rst66', 'gal', 1, 19, 'Другого же из Апостолов я не видел <i>никого</i>, кроме Иакова, брата Господня.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38608, 'rst66', 'gal', 1, 18, 'Потом, спустя три года, ходил я в Иерусалим видеться с Петром и пробыл у него дней пятнадцать.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38607, 'rst66', 'gal', 1, 17, 'и не пошел в Иерусалим к предшествовавшим мне Апостолам, а пошел в Аравию и опять возвратился в Дамаск.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38606, 'rst66', 'gal', 1, 16, 'открыть во мне Сына Своего, чтобы я благовествовал Его язычникам, &mdash; я не стал тогда же советоваться с плотью и кровью,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38605, 'rst66', 'gal', 1, 15, 'Когда же Бог, избравший меня от утробы матери моей и призвавший благодатью Своею, благоволил');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38604, 'rst66', 'gal', 1, 14, 'и преуспевал в Иудействе более многих сверстников в роде моем, будучи неумеренным ревнителем отеческих моих преданий.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38603, 'rst66', 'gal', 1, 13, 'Вы слышали о моем прежнем образе жизни в Иудействе, что я жестоко гнал Церковь Божию и опустошал ее,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38602, 'rst66', 'gal', 1, 12, 'ибо и я принял его и научился не от человека, но через откровение Иисуса Христа.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38601, 'rst66', 'gal', 1, 11, 'Возвещаю вам, братия, что Евангелие, которое я благовествовал, не есть человеческое,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38600, 'rst66', 'gal', 1, 10, 'У людей ли я ныне ищу благоволения или у Бога? людям ли угождать стараюсь? Если бы я и поныне угождал людям, то не был бы рабом Христовым.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38599, 'rst66', 'gal', 1, 9, 'Как прежде мы сказали, <i>так</i> и теперь еще говорю: кто благовествует вам не то, что вы приняли, да будет анафема.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38598, 'rst66', 'gal', 1, 8, 'Но если бы даже мы или Ангел с неба стал благовествовать вам не то, что мы благовествовали вам, да будет анафема.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38597, 'rst66', 'gal', 1, 7, 'которое, <i>впрочем</i>, не иное, а только есть люди, смущающие вас и желающие превратить благовествование Христово.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38596, 'rst66', 'gal', 1, 6, 'Удивляюсь, что вы от призвавшего вас благодатью Христовою так скоро переходите к иному благовествованию,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38595, 'rst66', 'gal', 1, 5, 'Ему слава во веки веков. Аминь.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38594, 'rst66', 'gal', 1, 4, 'Который отдал Себя Самого за грехи наши, чтобы избавить нас от настоящего лукавого века, по воле Бога и Отца нашего;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38593, 'rst66', 'gal', 1, 3, 'благодать вам и мир от Бога Отца и Господа нашего Иисуса Христа,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38592, 'rst66', 'gal', 1, 2, 'и все находящиеся со мною братия &mdash; церквам Галатийским:');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38591, 'rst66', 'gal', 1, 1, 'Павел Апостол, <i>избранный</i> не человеками и не через человека, но Иисусом Христом и Богом Отцом, воскресившим Его из мертвых,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38590, 'rst66', '2co', 13, 13, 'Благодать Господа нашего Иисуса Христа, и любовь Бога Отца, и общение Святого Духа со всеми вами. Аминь.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38589, 'rst66', '2co', 13, 12, 'Приветствуйте друг друга лобзанием святым. Приветствуют вас все святые.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38588, 'rst66', '2co', 13, 11, 'Впрочем, братия, радуйтесь, усовершайтесь, утешайтесь, будьте единомысленны, мирны, &mdash; и Бог любви и мира будет с вами.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38587, 'rst66', '2co', 13, 10, 'Для того я и пишу сие в отсутствии, чтобы в присутствии не употребить строгости по власти, данной мне Господом к созиданию, а не к разорению.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38586, 'rst66', '2co', 13, 9, 'Мы радуемся, когда мы немощны, а вы сильны; о сем‐то и молимся, о вашем совершенстве.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38585, 'rst66', '2co', 13, 8, 'Ибо мы не сильны против истины, но сильны за истину.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38584, 'rst66', '2co', 13, 7, 'Молим Бога, чтобы вы не делали никакого зла, не для того, чтобы нам показаться, чем должны быть; но чтобы вы делали добро, хотя бы мы казались и не тем, чем должны быть.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38583, 'rst66', '2co', 13, 6, 'О нас же, надеюсь, узна&#769;ете, что мы то, чем быть должны.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38582, 'rst66', '2co', 13, 5, 'Испытывайте самих себя, в вере ли вы; самих себя исследывайте. Или вы не знаете самих себя, что Иисус Христос в вас? Разве только вы не то, чем должны быть.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38581, 'rst66', '2co', 13, 4, 'Ибо хотя Он и распят в немощи, но жив силою Божиею; и мы также, <i>хотя</i> немощны в Нем, но будем живы с Ним силою Божиею в вас.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38580, 'rst66', '2co', 13, 3, 'Вы ищете доказательства на то, Христос ли говорит во мне: Он не бессилен для вас, но силен в вас.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38579, 'rst66', '2co', 13, 2, 'Я предварял и предваряю, как бы находясь <i>у вас</i> во второй раз, и теперь, отсутствуя, пишу прежде согрешившим и всем прочим, что, когда опять приду, не пощажу.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38578, 'rst66', '2co', 13, 1, 'В третий уже раз иду к вам. При устах двух или трех свидетелей будет твердо всякое слово.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38577, 'rst66', '2co', 12, 21, 'чтобы опять, когда приду, не уничижил меня у вас Бог мой и <i>чтобы</i> не оплакивать мне многих, которые согрешили прежде и не покаялись в нечистоте, блудодеянии и непотребстве, какое делали.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38576, 'rst66', '2co', 12, 20, 'Ибо я опасаюсь, чтобы мне, по пришествии моем, не найти вас такими, какими не желаю, также чтобы и вам не найти меня таким, каким не желаете: чтобы <i>не найти у вас</i> раздоров, зависти, гнева, ссор, клевет, ябед, гордости, беспорядков,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38575, 'rst66', '2co', 12, 19, 'Не думаете ли еще, что мы <i>только</i> оправдываемся перед вами? Мы говорим пред Богом, во Христе, и все это, возлюбленные, к вашему назиданию.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38574, 'rst66', '2co', 12, 18, 'Я упросил Тита и послал с ним одного из братьев: Тит воспользовался ли чем от вас? Не в одном ли духе мы действовали? Не одним ли путем ходили?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38573, 'rst66', '2co', 12, 17, 'Но пользовался ли я <i>чем</i> от вас через кого‐нибудь из тех, кого посылал к вам?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38572, 'rst66', '2co', 12, 16, 'Положим, <i>что</i> сам я не обременял вас, но, будучи хитр, лукавством брал с вас.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38571, 'rst66', '2co', 12, 15, 'Я охотно буду издерживать <i>свое</i> и истощать себя за души ваши, несмотря на то, что, чрезвычайно любя вас, я менее любим вами.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38570, 'rst66', '2co', 12, 14, 'Вот, в третий раз я готов идти к вам, и не буду отягощать вас, ибо я ищу не вашего, а вас. Не дети должны собирать имение для родителей, но родители для детей.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38569, 'rst66', '2co', 12, 13, 'Ибо чего у вас недостает перед прочими церквами, разве только того, что сам я не был вам в тягость? Простите мне такую вину.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38568, 'rst66', '2co', 12, 12, 'Признаки Апостола оказались перед вами всяким терпением, знамениями, чудесами и силами.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38567, 'rst66', '2co', 12, 11, 'Я дошел до неразумия, хвалясь; вы меня <i>к сему</i> принудили. Вам бы надлежало хвалить меня, ибо у меня ни в чем нет недостатка против высших Апостолов, хотя я и ничто.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38566, 'rst66', '2co', 12, 10, 'Посему я благодушествую в немощах, в обидах, в нуждах, в гонениях, в притеснениях за Христа, ибо, когда я немощен, тогда силен.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38565, 'rst66', '2co', 12, 9, 'но <i>Господь</i> сказал мне: «довольно для тебя благодати Моей, ибо сила Моя совершается в немощи». И потому я гораздо охотнее буду хвалиться своими немощами, чтобы обитала во мне сила Христова.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38564, 'rst66', '2co', 12, 8, 'Трижды молил я Господа о том, чтобы удалил его от меня,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38563, 'rst66', '2co', 12, 7, 'И чтобы я не превозносился чрезвычайностью откровений, дано мне жало в плоть, ангел сатаны, удручать меня, чтобы я не превозносился.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38562, 'rst66', '2co', 12, 6, 'Впрочем, если захочу хвалиться, не буду неразумен, потому что скажу истину; но я удерживаюсь, чтобы кто не подумал о мне более, нежели сколько во мне видит или слышит от меня.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38561, 'rst66', '2co', 12, 5, 'Таким <i>человеком</i> могу хвалиться; собою же не похвалюсь, разве только немощами моими.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38560, 'rst66', '2co', 12, 4, 'что он был восхищен в рай и слышал неизреченные слова, которых человеку нельзя пересказать.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38559, 'rst66', '2co', 12, 3, 'И знаю о таком человеке (<i>только</i> не знаю &mdash; в теле или вне тела: Бог знает),');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38558, 'rst66', '2co', 12, 2, 'Знаю человека во Христе, который назад тому четырнадцать лет (в теле ли &mdash; не знаю, вне ли тела &mdash; не знаю: Бог знает) восхищен был до третьего неба.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38557, 'rst66', '2co', 12, 1, 'Не полезно хвалиться мне, ибо я приду к видениям и откровениям Господним.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38556, 'rst66', '2co', 11, 32, 'В Дамаске областной правитель царя Ареты стерег город Дамаск, чтобы схватить меня; и я в корзине был спущен из окна по стене и избежал его рук.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38555, 'rst66', '2co', 11, 31, 'Бог и Отец Господа нашего Иисуса Христа, благословенный вовеки, знает, что я не лгу.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38554, 'rst66', '2co', 11, 30, 'Если должно мне хвалиться, то буду хвалиться немощью моею.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38553, 'rst66', '2co', 11, 29, 'Кто изнемогает, с кем бы и я не изнемогал? Кто соблазняется, за кого бы я не воспламенялся?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38552, 'rst66', '2co', 11, 28, 'Кроме посторонних <i>приключений</i>, у меня ежедневно стечение <i>людей</i>, забота о всех церквах.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38551, 'rst66', '2co', 11, 27, 'в труде и в изнурении, часто в бдении, в голоде и жажде, часто в посте, на стуже и в наготе.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38550, 'rst66', '2co', 11, 26, 'много раз <i>был</i> в путешествиях, в опасностях на реках, в опасностях от разбойников, в опасностях от единоплеменников, в опасностях от язычников, в опасностях в городе, в опасностях в пустыне, в опасностях на море, в опасностях между лжебратиями,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38549, 'rst66', '2co', 11, 25, 'три раза меня били палками, однажды камнями побивали, три раза я терпел кораблекрушение, ночь и день пробыл во глубине <i>морской;</i>');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38548, 'rst66', '2co', 11, 24, 'От Иудеев пять раз дано мне было по сорока <i>ударов</i> без одного;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38547, 'rst66', '2co', 11, 23, 'Христовы служители? в безумии говорю: я больше. Я гораздо более <i>был</i> в трудах, безмерно в ранах, более в темницах и многократно при смерти.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38546, 'rst66', '2co', 11, 22, 'Они Евреи? и я. Израильтяне? и я. Семя Авраамово? и я.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38545, 'rst66', '2co', 11, 21, 'К стыду говорю, что <i>на это</i> у нас недоставало сил. А если кто смеет <i>хвалиться</i> чем‐либо, то (скажу по неразумию) смею и я.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38544, 'rst66', '2co', 11, 20, 'вы терпите, когда кто вас порабощает, когда кто объедает, когда кто обирает, когда кто превозносится, когда кто бьет вас в лицо.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38543, 'rst66', '2co', 11, 19, 'Ибо вы, люди разумные, охотно терпите неразумных:');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38542, 'rst66', '2co', 11, 18, 'Как многие хвалятся по плоти, то и я буду хвалиться.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38541, 'rst66', '2co', 11, 17, 'Что скажу, то скажу не в Господе, но как бы в неразумии при такой отважности на похвалу.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38540, 'rst66', '2co', 11, 16, 'Еще скажу: не почти кто‐нибудь меня неразумным; а если не так, то примите меня хотя как неразумного, чтобы и мне сколько‐нибудь похвалиться.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38539, 'rst66', '2co', 11, 15, 'а потому не великое дело, если и служители его принимают вид служителей правды; но конец их будет по делам их.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38538, 'rst66', '2co', 11, 14, 'И неудивительно: потому что сам сатана принимает вид Ангела света,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38537, 'rst66', '2co', 11, 13, 'Ибо таковые лжеапостолы, лукавые делатели, принимают вид Апостолов Христовых.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38536, 'rst66', '2co', 11, 12, 'чтобы не дать повода ищущим повода, дабы они, чем хвалятся, в том оказались <i>такими же</i>, как и мы.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38535, 'rst66', '2co', 11, 11, 'Почему же <i>так поступаю?</i> Потому ли, что не люблю вас? Богу известно! Но как поступаю, так и буду поступать,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38534, 'rst66', '2co', 11, 10, 'По истине Христовой во мне <i>скажу</i>, что похвала сия не отнимется у меня в странах Ахаии.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38533, 'rst66', '2co', 11, 9, 'ибо недостаток мой восполнили братия, пришедшие из Македонии; да и во всем я старался и постараюсь не быть вам в тягость.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38532, 'rst66', '2co', 11, 8, 'Другим церквам я причинял издержки, получая <i>от них</i> содержание для служения вам; и, будучи у вас, хотя терпел недостаток, никому не докучал,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38531, 'rst66', '2co', 11, 7, 'Согрешил ли я тем, что унижал себя, чтобы возвысить вас, потому что безмездно проповедовал вам Евангелие Божие?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38530, 'rst66', '2co', 11, 6, 'хотя я и невежда в слове, но не в познании. Впрочем, мы во всем совершенно известны вам.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38529, 'rst66', '2co', 11, 5, 'Но я думаю, что у меня ни в чем нет недостатка против высших Апостолов:');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38528, 'rst66', '2co', 11, 4, 'Ибо если бы кто, придя, начал проповедовать другого Иисуса, которого мы не проповедовали, или если бы вы получили иного Духа, которого не получили, или иное благовестие, которого не принимали, &mdash; то вы были бы очень снисходительны <i>к тому</i>.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38527, 'rst66', '2co', 11, 3, 'Но боюсь, чтобы, как змий хитростью своею прельстил Еву, так и ваши умы не повредились, <i>уклонившись</i> от простоты во Христе.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38526, 'rst66', '2co', 11, 2, 'Ибо я ревную о вас ревностью Божиею; потому что я обручил вас единому мужу, чтобы представить Христу чистою девою.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38525, 'rst66', '2co', 11, 1, 'О, если бы вы несколько были снисходительны к моему неразумию! Но вы и снисходите ко мне.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38524, 'rst66', '2co', 10, 18, 'Ибо не тот достоин, кто сам себя хвалит, но кого хвалит Господь.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38523, 'rst66', '2co', 10, 17, 'Хвалящийся хвались о Господе.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38522, 'rst66', '2co', 10, 16, 'так чтобы и далее вас проповедовать Евангелие, а не хвалиться готовым в чужом уделе.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38521, 'rst66', '2co', 10, 15, 'Мы не без меры хвалимся, не чужими трудами, но надеемся, с возрастанием веры вашей, с избытком увеличить в вас удел наш,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38520, 'rst66', '2co', 10, 14, 'Ибо мы не напрягаем себя, как не достигшие до вас, потому что достигли и до вас благовествованием Христовым.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38519, 'rst66', '2co', 10, 13, 'А мы не без меры хвалиться будем, но по мере удела, какой назначил нам Бог в такую меру, чтобы достигнуть и до вас.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38518, 'rst66', '2co', 10, 12, 'Ибо мы не смеем сопоставлять или сравнивать себя с теми, которые сами себя выставляют: они измеряют себя самими собою и сравнивают себя с собою неразумно.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38517, 'rst66', '2co', 10, 11, 'такой пусть знает, что, каковы мы на словах в посланиях заочно, таковы и на деле лично.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38516, 'rst66', '2co', 10, 10, 'Так как <i>некто</i> говорит: «в посланиях он строг и силен, а в личном присутствии слаб, и речь <i>его</i> незначительна», &mdash;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38515, 'rst66', '2co', 10, 9, 'Впрочем, да не покажется, что я устрашаю вас <i>только</i> посланиями.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38514, 'rst66', '2co', 10, 8, 'Ибо если бы я и более стал хвалиться нашею властью, которую Господь дал нам к созиданию, а не к расстройству вашему, то не остался бы в стыде.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38513, 'rst66', '2co', 10, 7, 'На личность ли смо&#769;трите? Кто уверен в себе, что он Христов, тот сам по себе суди, что, как он Христов, так и мы Христовы.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38512, 'rst66', '2co', 10, 6, 'и готовы наказать всякое непослушание, когда ваше послушание исполнится.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38511, 'rst66', '2co', 10, 5, 'и всякое превозношение, восстающее против познания Божия, и пленяем всякое помышление в послушание Христу,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38510, 'rst66', '2co', 10, 4, 'Оружия воинствования нашего не плотские, но сильные Богом на разрушение твердынь: <i>ими</i> ниспровергаем замыслы');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38509, 'rst66', '2co', 10, 3, 'Ибо мы, ходя во плоти, не по плоти воинствуем.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38508, 'rst66', '2co', 10, 2, 'Прошу, чтобы мне по пришествии моем не прибегать к той твердой смелости, которую думаю употребить против некоторых, помышляющих о нас, что мы поступаем по плоти.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38507, 'rst66', '2co', 10, 1, 'Я же, Павел, который лично между вами скромен, а заочно против вас отважен, убеждаю вас кротостью и снисхождением Христовым.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38506, 'rst66', '2co', 9, 15, 'Благодарение Богу за неизреченный дар Его!');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38505, 'rst66', '2co', 9, 14, 'молясь за вас, по расположению к вам, за преизбыточествующую в вас благодать Божию.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38504, 'rst66', '2co', 9, 13, 'ибо, видя опыт сего служения, они прославляют Бога за покорность исповедуемому вами Евангелию Христову и за искреннее общение с ними и со всеми,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38503, 'rst66', '2co', 9, 12, 'Ибо дело служения сего не только восполняет скудость святых, но и производит во многих обильные благодарения Богу;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38502, 'rst66', '2co', 9, 11, 'так чтобы вы всем богаты были на всякую щедрость, которая через нас производит благодарение Богу.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38501, 'rst66', '2co', 9, 10, 'Дающий же семя сеющему и хлеб в пищу подаст обилие посеянному вами и умножит плоды правды вашей,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38500, 'rst66', '2co', 9, 9, 'как написано: «расточил, ро&#769;здал нищим; правда его пребывает в век».');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38499, 'rst66', '2co', 9, 8, 'Бог же силен обогатить вас всякою благодатью, чтобы вы, всегда и во всем имея всякое довольство, были богаты на всякое доброе дело,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38498, 'rst66', '2co', 9, 7, 'Каждый <i>уделяй</i> по расположению сердца, не с огорчением и не с принуждением; ибо доброхотно дающего любит Бог.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38497, 'rst66', '2co', 9, 6, 'При сем скажу: кто сеет скупо, тот скупо и пожнет; а кто сеет щедро, тот щедро и пожнет.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38496, 'rst66', '2co', 9, 5, 'Посему я почел за нужное упросить братьев, чтобы они наперед пошли к вам и предварительно озаботились, дабы возвещенное уже благословение ваше было готово, как благословение, а не как побор.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38495, 'rst66', '2co', 9, 4, '<i>и</i> чтобы, когда придут со мною Македоняне и найдут вас неготовыми, не остались в стыде мы, &mdash; не говорю «вы», &mdash; похвалившись с такою уверенностью.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38494, 'rst66', '2co', 9, 3, 'Братьев же послал я для того, чтобы похвала моя о вас не оказалась тщетною в сем случае, но чтобы вы, как я говорил, были приготовлены,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38493, 'rst66', '2co', 9, 2, 'ибо я знаю усердие ваше и хвалюсь вами перед Македонянами, что Ахаия приготовлена еще с прошедшего года; и ревность ваша поощрила многих.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38492, 'rst66', '2co', 9, 1, 'Для меня, впрочем, излишне писать вам о вспоможении святым,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38491, 'rst66', '2co', 8, 24, 'Итак, перед лицом церквей дайте им доказательство любви вашей и того, что мы <i>справедливо</i> хвалимся вами.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38490, 'rst66', '2co', 8, 23, 'Что касается до Тита, это &mdash; мой товарищ и сотрудник у вас; а что до братьев наших, это &mdash; посланники церквей, слава Христова.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38489, 'rst66', '2co', 8, 22, 'Мы послали с ними и брата нашего, которого усердие много раз испытали во многом и который ныне еще усерднее по великой уверенности в вас.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38488, 'rst66', '2co', 8, 21, 'ибо мы стараемся о добром не только пред Господом, но и пред людьми.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38487, 'rst66', '2co', 8, 20, 'остерегаясь, чтобы нам не подвергнуться от кого нареканию при таком обилии приношений, вверяемых нашему служению;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38486, 'rst66', '2co', 8, 19, 'и притом избранного от церквей сопутствовать нам для сего благотворения, которому мы служим во славу Самого Господа и <i>в соответствие</i> вашему усердию,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38485, 'rst66', '2co', 8, 18, 'С ним послали мы также брата, во всех церквах похваляемого за благовествование,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38484, 'rst66', '2co', 8, 17, 'Ибо, хотя и я просил его, впрочем, он, будучи очень усерден, пошел к вам добровольно.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38483, 'rst66', '2co', 8, 16, 'Благодарение Богу, вложившему в сердце Титово такое усердие к вам.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38482, 'rst66', '2co', 8, 15, 'как написано: «кто собрал много, не имел лишнего; и кто мало, не имел недостатка».');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38481, 'rst66', '2co', 8, 14, 'Ныне ваш избыток в <i>восполнение</i> их недостатка; а после их избыток в <i>восполнение</i> вашего недостатка, чтобы была равномерность,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38480, 'rst66', '2co', 8, 13, 'Не <i>требуется</i>, чтобы другим <i>было</i> облегчение, а вам тяжесть, но чтобы была равномерность.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38479, 'rst66', '2co', 8, 12, 'Ибо если есть усердие, то оно принимается смотря по тому, кто что имеет, а не по тому, чего не имеет.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38478, 'rst66', '2co', 8, 11, 'Совершите же теперь самое дело, дабы, чего усердно желали, то и исполнено было по достатку.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38477, 'rst66', '2co', 8, 10, 'Я даю на это совет: ибо это полезно вам, которые не только начали делать сие, но и желали того еще с прошедшего года.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38476, 'rst66', '2co', 8, 9, 'Ибо вы знаете благодать Господа нашего Иисуса Христа, что Он, будучи богат, обнищал ради вас, дабы вы обогатились Его нищетою.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38475, 'rst66', '2co', 8, 8, 'Говорю это не в виде повеления, но усердием других испытываю искренность и вашей любви.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38474, 'rst66', '2co', 8, 7, 'А как вы изобилуете всем: верою и словом, и познанием, и всяким усердием, и любовью вашею к нам, &mdash; так изобилуйте и сею добродетелью.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38473, 'rst66', '2co', 8, 6, 'поэтому мы просили Тита, чтобы он, как начал, так и окончил у вас и это доброе дело.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38472, 'rst66', '2co', 8, 5, 'и не только то, чего мы надеялись, но они отдали самих себя, во‐первых, Господу, <i>потом</i> и нам по воле Божией;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38471, 'rst66', '2co', 8, 4, 'они весьма убедительно просили нас принять дар и участие <i>их</i> в служении святым;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38470, 'rst66', '2co', 8, 3, 'Ибо они доброхотны по силам и сверх сил &mdash; я свидетель:');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38469, 'rst66', '2co', 8, 2, 'ибо они среди великого испытания скорбями преизобилуют радостью; и глубокая нищета их преизбыточествует в богатстве их радушия.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38468, 'rst66', '2co', 8, 1, 'Уведомляем вас, братия, о благодати Божией, данной церквам Македонским,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38467, 'rst66', '2co', 7, 16, 'Итак, радуюсь, что во всем могу положиться на вас.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38466, 'rst66', '2co', 7, 15, 'и сердце его весьма расположено к вам, при воспоминании о послушании всех вас, как вы приняли его со страхом и трепетом.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38465, 'rst66', '2co', 7, 14, 'Итак, я не остался в стыде, если чем‐либо о вас похвалился перед ним, но как вам мы говорили все истину, так и перед Титом похвала наша оказалась истинною;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38464, 'rst66', '2co', 7, 13, 'Посему мы утешились утешением вашим; а еще более обрадованы мы радостью Тита, что вы все успокоили дух его.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38463, 'rst66', '2co', 7, 12, 'Итак, если я писал к вам, то не ради оскорбителя и не ради оскорбленного, но чтобы вам открылось попечение наше о вас пред Богом.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38462, 'rst66', '2co', 7, 11, 'Ибо то самое, что вы опечалились ради Бога, смотри&#769;те, какое произвело в вас усердие, какие извинения, какое негодование <i>на виновного</i>, какой страх, какое желание, какую ревность, какое взыскание! По всему вы показали себя чистыми в этом деле.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38461, 'rst66', '2co', 7, 10, 'Ибо печаль ради Бога производит неизменное покаяние ко спасению, а печаль мирская производит смерть.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38460, 'rst66', '2co', 7, 9, 'Теперь я радуюсь не потому, что вы опечалились, но что вы опечалились к покаянию; ибо опечалились ради Бога, так что нисколько не понесли от нас вреда.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38459, 'rst66', '2co', 7, 8, 'Посему, если я опечалил вас посланием, не жалею, хотя и пожалел было; ибо вижу, что послание то опечалило вас, впрочем, на время.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38458, 'rst66', '2co', 7, 7, 'и не только прибытием его, но и утешением, которым он утешался о вас, пересказывая нам о вашем усердии, о вашем плаче, о вашей ревности по мне, так что я еще более обрадовался.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38457, 'rst66', '2co', 7, 6, 'Но Бог, утешающий смиренных, утешил нас прибытием Тита,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38456, 'rst66', '2co', 7, 5, 'Ибо, когда пришли мы в Македонию, плоть наша не имела никакого покоя, но мы были стеснены отовсюду: отвне &mdash; нападения, внутри &mdash; страхи.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38455, 'rst66', '2co', 7, 4, 'Я много надеюсь на вас, много хвалюсь вами; я исполнен утешением, преизобилую радостью, при всей скорби нашей.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38454, 'rst66', '2co', 7, 3, 'Не в осуждение говорю; ибо я прежде сказал, что вы в сердцах наших, так чтобы вместе и умереть и жить.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38453, 'rst66', '2co', 7, 2, 'Вместите нас. Мы никого не обидели, никому не повредили, ни от кого не искали корысти.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38452, 'rst66', '2co', 7, 1, 'Итак, возлюбленные, имея такие обетования, очистим себя от всякой скверны плоти и духа, совершая святыню в страхе Божием.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38451, 'rst66', '2co', 6, 18, '«И буду вам Отцом, и вы будете Моими сынами и дщерями, говорит Господь Вседержитель».');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38450, 'rst66', '2co', 6, 17, '«И потому выйдите из среды их и отделитесь, говорит Господь, и не прикасайтесь к нечистому; и Я прииму вас».');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38449, 'rst66', '2co', 6, 16, 'Какая совместность храма Божия с идолами? Ибо вы &mdash; храм Бога живого, как сказал Бог: «вселюсь в них и буду ходить <i>в них;</i> и буду их Богом, и они будут Моим народом».');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38448, 'rst66', '2co', 6, 15, 'Какое согласие между Христом и Велиаром? Или какое соучастие верного с неверным?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38447, 'rst66', '2co', 6, 14, 'Не преклоняйтесь под чужое ярмо с неверными, ибо какое общение праведности с беззаконием? Что общего у света с тьмою?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38446, 'rst66', '2co', 6, 13, 'В равное возмездие, &mdash; говорю, как детям, &mdash; распространитесь и вы.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38445, 'rst66', '2co', 6, 12, 'Вам не тесно в нас; но в сердцах ваших тесно.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38444, 'rst66', '2co', 6, 11, 'Уста наши отверсты к вам, Коринфяне, сердце наше расширено.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38443, 'rst66', '2co', 6, 10, 'нас огорчают, а мы всегда радуемся; мы нищи, но многих обогащаем; мы ничего не имеем, но всем обладаем.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38442, 'rst66', '2co', 6, 9, 'мы неизвестны, но нас узнаю&#769;т; нас почитают умершими, но вот, мы живы; нас наказывают, но мы не умираем;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38441, 'rst66', '2co', 6, 8, 'в чести и бесчестии, при порицаниях и похвалах: нас почитают обманщиками, но мы верны;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38440, 'rst66', '2co', 6, 7, 'в слове истины, в силе Божией, с оружием правды в правой и левой руке,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38439, 'rst66', '2co', 6, 6, 'в чистоте, в благоразумии, в великодушии, в благости, в Духе Святом, в нелицемерной любви,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38438, 'rst66', '2co', 6, 5, 'под ударами, в темницах, в изгнаниях, в трудах, в бдениях, в постах,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38437, 'rst66', '2co', 6, 4, 'но во всем являем себя, как служители Божии, в великом терпении, в бедствиях, в нуждах, в тесных обстоятельствах,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38436, 'rst66', '2co', 6, 3, 'Мы никому ни в чем не полагаем претыкания, чтобы не было порицаемо служение,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38435, 'rst66', '2co', 6, 2, 'Ибо сказано: «во время благоприятное Я услышал тебя и в день спасения помог тебе». Вот, теперь время благоприятное, вот, теперь день спасения.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38434, 'rst66', '2co', 6, 1, 'Мы же, как споспешники, умоляем вас, чтобы благодать Божия не тщетно была принята вами.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38433, 'rst66', '2co', 5, 21, 'Ибо не знавшего греха Он сделал для нас <i>жертвою за</i> грех, чтобы мы в Нем сделались праведными пред Богом.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38432, 'rst66', '2co', 5, 20, 'Итак, мы &mdash; посланники от имени Христова, и как бы Сам Бог увещевает через нас; от имени Христова просим: примиритесь с Богом.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38431, 'rst66', '2co', 5, 19, 'потому что Бог во Христе примирил с Собою мир, не вменяя <i>людям</i> преступлений их, и дал нам слово примирения.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38430, 'rst66', '2co', 5, 18, 'Все же от Бога, Иисусом Христом примирившего нас с Собою и давшего нам служение примирения,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38429, 'rst66', '2co', 5, 17, 'Итак, кто во Христе, <i>тот</i> новая тварь; древнее прошло, теперь все новое.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38428, 'rst66', '2co', 5, 16, 'Потому отныне мы никого не знаем по плоти; если же и знали Христа по плоти, то ныне уже не знаем.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38427, 'rst66', '2co', 5, 15, 'А Христос за всех умер, чтобы живущие уже не для себя жили, но для умершего за них и воскресшего.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38426, 'rst66', '2co', 5, 14, 'Ибо любовь Христова объемлет нас, рассуждающих так: если один умер за всех, то все умерли.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38425, 'rst66', '2co', 5, 13, 'Если мы выходим из себя, то для Бога; если же скромны, то для вас.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38424, 'rst66', '2co', 5, 12, 'Не снова представляем себя вам, но даем вам повод хвалиться нами, дабы имели вы <i>что сказать</i> тем, которые хвалятся лицом, а не сердцем.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38423, 'rst66', '2co', 5, 11, 'Итак, зная страх Господень, мы вразумляем людей, Богу же мы открыты; надеюсь, что открыты и вашим совестям.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38422, 'rst66', '2co', 5, 10, 'ибо всем нам должно явиться пред судилище Христово, чтобы каждому получить <i>соответственно тому</i>, что он делал, живя в теле, &mdash; доброе или худое.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38421, 'rst66', '2co', 5, 9, 'И потому ревностно стараемся, водворяясь ли, выходя ли, быть Ему угодными;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38420, 'rst66', '2co', 5, 8, 'то мы благодушествуем и желаем лучше выйти из тела и водвориться у Господа.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38419, 'rst66', '2co', 5, 7, 'ибо мы ходим верою, а не ви&#769;дением, &mdash;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38418, 'rst66', '2co', 5, 6, 'Итак, мы всегда благодушествуем; и как знаем, что, водворяясь в теле, мы устранены от Господа, &mdash;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38417, 'rst66', '2co', 5, 5, 'На сие самое и создал нас Бог и дал нам залог Духа.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38416, 'rst66', '2co', 5, 4, 'Ибо мы, находясь в этой хижине, воздыхаем под бременем, потому что не хотим совлечься, но облечься, чтобы смертное поглощено было жизнью.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38415, 'rst66', '2co', 5, 3, 'только бы нам и одетым не оказаться нагими.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38414, 'rst66', '2co', 5, 2, 'Оттого мы и воздыхаем, желая облечься в небесное наше жилище;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38413, 'rst66', '2co', 5, 1, 'Ибо знаем, что, когда земной наш дом, эта хижина, разрушится, мы имеем от Бога жилище на небесах, дом нерукотворенный, вечный.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38412, 'rst66', '2co', 4, 18, 'когда мы смотрим не на видимое, но на невидимое: ибо видимое временно, а невидимое вечно.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38411, 'rst66', '2co', 4, 17, 'Ибо кратковременное легкое страдание наше производит в безмерном преизбытке вечную славу,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38410, 'rst66', '2co', 4, 16, 'Посему мы не унываем; но если внешний наш человек и тлеет, то внутренний со дня на день обновляется.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38409, 'rst66', '2co', 4, 15, 'Ибо всё для вас, дабы обилие благодати тем бо&#769;льшую во многих произвело благодарность во славу Божию.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38408, 'rst66', '2co', 4, 14, 'зная, что Воскресивший Господа Иисуса воскресит через Иисуса и нас и поставит перед <i>Собою</i> с вами.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38407, 'rst66', '2co', 4, 13, 'Но, имея тот же дух веры, как написано: «я веровал и потому говорил», и мы веруем, потому и говорим,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38406, 'rst66', '2co', 4, 12, 'так что смерть действует в нас, а жизнь &mdash; в вас.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38405, 'rst66', '2co', 4, 11, 'Ибо мы, живые, непрестанно предаемся на смерть ради Иисуса, чтобы и жизнь Иисусова открылась в смертной плоти нашей,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38404, 'rst66', '2co', 4, 10, 'Всегда носим в теле мертвость Господа Иисуса, чтобы и жизнь Иисусова открылась в теле нашем.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38403, 'rst66', '2co', 4, 9, 'мы гонимы, но не оставлены; низлагаемы, но не погибаем.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38402, 'rst66', '2co', 4, 8, 'Мы отовсюду притесняемы, но не стеснены; мы в отчаянных обстоятельствах, но не отчаиваемся;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38401, 'rst66', '2co', 4, 7, 'Но сокровище сие мы носим в глиняных сосудах, чтобы преизбыточная сила была <i>приписываема</i> Богу, а не нам.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38400, 'rst66', '2co', 4, 6, 'потому что Бог, повелевший из тьмы воссиять свету, озарил наши сердца, дабы просветить <i>нас</i> познанием славы Божией в лице Иисуса Христа.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38399, 'rst66', '2co', 4, 5, 'Ибо мы не себя проповедуем, но Христа Иисуса, Господа; а мы &mdash; рабы ваши для Иисуса,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38398, 'rst66', '2co', 4, 4, 'для неверующих, у которых бог века сего ослепил умы, чтобы для них не воссиял свет благовествования о славе Христа, Который есть образ Бога невидимого.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38397, 'rst66', '2co', 4, 3, 'Если же и закрыто благовествование наше, то закрыто для погибающих,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38396, 'rst66', '2co', 4, 2, 'но, отвергнув скрытные, постыдные <i>дела</i>, не прибегая к хитрости и не искажая слова Божия, а открывая истину, представляем себя совести всякого человека пред Богом.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38395, 'rst66', '2co', 4, 1, 'Посему, имея по милости <i>Божией</i> такое служение, мы не унываем;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38394, 'rst66', '2co', 3, 18, 'Мы же все, открытым лицом, как в зеркале, взирая на славу Господню, преображаемся в тот же образ от славы в славу, как от Господня Духа.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38393, 'rst66', '2co', 3, 17, 'Господь есть Дух; а где Дух Господень, там свобода.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38392, 'rst66', '2co', 3, 16, 'но когда обращаются к Господу, тогда это покрывало снимается.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38391, 'rst66', '2co', 3, 15, 'Доныне, когда они читают Моисея, покрывало лежит на сердце их;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38390, 'rst66', '2co', 3, 14, 'Но умы их ослеплены: ибо то же самое покрывало доныне остается неснятым при чтении Ветхого Завета, потому что оно снимается Христом.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38389, 'rst66', '2co', 3, 13, 'а не так, как Моисей, <i>который</i> полагал покрывало на лицо свое, чтобы сыны Израилевы не взирали на конец преходящего.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38388, 'rst66', '2co', 3, 12, 'Имея такую надежду, мы действуем с великим дерзновением,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38387, 'rst66', '2co', 3, 11, 'Ибо, если преходящее славно, тем более славно пребывающее.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38386, 'rst66', '2co', 3, 10, 'То&#769; прославленное даже не оказывается славным с сей стороны, по причине преимущественной славы <i>последующего</i>.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38385, 'rst66', '2co', 3, 9, 'Ибо если служение осуждения славно, то тем паче изобилует славою служение оправдания.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38384, 'rst66', '2co', 3, 8, 'то не гораздо ли более должно быть славно служение духа?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38383, 'rst66', '2co', 3, 7, 'Если же служение смертоносным буквам, начертанное на камнях, было так славно, что сыны Израилевы не могли смотреть на лицо Моисеево по причине славы лица его преходящей,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38382, 'rst66', '2co', 3, 6, 'Он дал нам способность быть служителями нового завета, не буквы, но духа, потому что буква убивает, а дух животворит.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38381, 'rst66', '2co', 3, 5, 'не потому, чтобы мы сами способны были помыслить что от себя, как бы от себя, но способность наша от Бога.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38380, 'rst66', '2co', 3, 4, 'Такую уверенность мы имеем в Боге через Христа,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38379, 'rst66', '2co', 3, 3, 'вы показываете собою, что вы &mdash; письмо Христово, через служение наше написанное не чернилами, но Духом Бога живого, не на скрижалях каменных, но на плотяных скрижалях сердца.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38378, 'rst66', '2co', 3, 2, 'Вы &mdash; наше письмо, написанное в сердцах наших, узнаваемое и читаемое всеми человеками;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38377, 'rst66', '2co', 3, 1, 'Неужели нам снова знакомиться с вами? Неужели нужны для нас, как для некоторых, одобрительные письма к вам или от вас?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38376, 'rst66', '2co', 2, 17, 'Ибо мы не повреждаем слова Божия, как многие, но проповедуем искренно, как от Бога, пред Богом, во Христе.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38375, 'rst66', '2co', 2, 16, 'для одних запах смертоносный на смерть, а для других запах живительный на жизнь. И кто способен к сему?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38374, 'rst66', '2co', 2, 15, 'Ибо мы &mdash; Христово благоухание Богу в спасаемых и в погибающих:');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38373, 'rst66', '2co', 2, 14, 'Но благодарение Богу, Который всегда дает нам торжествовать во Христе и благоухание познания о Себе распространяет нами во всяком месте.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38372, 'rst66', '2co', 2, 13, 'я не имел покоя духу моему, потому что не нашел <i>там</i> брата моего Тита; но, простившись с ними, я пошел в Македонию.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38371, 'rst66', '2co', 2, 12, 'Придя в Троаду для благовествования о Христе, хотя мне и отверста была дверь Господом,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38370, 'rst66', '2co', 2, 11, 'чтобы не сделал нам ущерба сатана, ибо нам небезызвестны его умыслы.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38369, 'rst66', '2co', 2, 10, 'А кого вы в чем прощаете, того и я; ибо и я, если в чем простил кого, простил для вас от лица Христова,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38368, 'rst66', '2co', 2, 9, 'Ибо я для того и писал, чтобы узнать на опыте, во всем ли вы послушны.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38367, 'rst66', '2co', 2, 8, 'И потому прошу вас оказать ему любовь.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38366, 'rst66', '2co', 2, 7, 'так что вам лучше уже простить его и утешить, дабы он не был поглощен чрезмерною печалью.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38365, 'rst66', '2co', 2, 6, 'Для такого довольно сего наказания от многих,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38364, 'rst66', '2co', 2, 5, 'Если же кто огорчил, то не меня огорчил, но частью, &mdash; чтобы не сказать много, &mdash; и всех вас.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38363, 'rst66', '2co', 2, 4, 'От великой скорби и стесненного сердца я писал вам со многими слезами, не для того, чтобы огорчить вас, но чтобы вы познали любовь, какую я в избытке имею к вам.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38362, 'rst66', '2co', 2, 3, 'Это самое и писал я вам, дабы, придя, не иметь огорчения от тех, о которых мне надлежало радоваться: ибо я во всех вас уверен, что моя радость есть <i>радость</i> и для всех вас.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38361, 'rst66', '2co', 2, 2, 'Ибо если я огорчаю вас, то кто обрадует меня, как не тот, кто огорчен мною?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38360, 'rst66', '2co', 2, 1, 'Итак, я рассудил сам в себе не приходить к вам опять с огорчением.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38359, 'rst66', '2co', 1, 24, 'не потому, будто мы берем власть над верою вашею; но мы споспешествуем радости вашей: ибо верою вы тверды.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38358, 'rst66', '2co', 1, 23, 'Бога призываю во свидетели на душу мою, что, щадя вас, я доселе не приходил в Коринф,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38357, 'rst66', '2co', 1, 22, 'Который и запечатлел нас и дал залог Духа в сердца наши.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38356, 'rst66', '2co', 1, 21, 'Утверждающий же нас с вами во Христе и помазавший нас <i>есть</i> Бог,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38355, 'rst66', '2co', 1, 20, 'ибо все обетования Божии в Нем «да», и в Нем «аминь», &mdash; в славу Божию, через нас.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38354, 'rst66', '2co', 1, 19, 'Ибо Сын Божий, Иисус Христос, проповеданный у вас нами, мною и Силуаном и Тимофеем, не был «да» и «нет»; но в Нем было «да», &mdash;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38353, 'rst66', '2co', 1, 18, 'Верен Бог, что слово наше к вам не было то «да», то «нет».');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38352, 'rst66', '2co', 1, 17, 'Имея такое намерение, легкомысленно ли я поступил? Или, что я предпринимаю, по плоти предпринимаю, так что у меня то «да, да», то «нет, нет»?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38351, 'rst66', '2co', 1, 16, 'и через вас пройти в Македонию, из Македонии же опять прийти к вам; а вы проводили бы меня в Иудею.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38350, 'rst66', '2co', 1, 15, 'И в этой уверенности я намеревался прийти к вам ранее, чтобы вы вторично получили благодать,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38349, 'rst66', '2co', 1, 14, 'так как вы отчасти и уразумели уже, что мы будем вашею похвалою, равно и вы нашею, в день Господа нашего Иисуса Христа.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38348, 'rst66', '2co', 1, 13, 'И мы пишем вам не иное, как то, что вы читаете или разумеете и что, как надеюсь, до конца уразумеете,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38347, 'rst66', '2co', 1, 12, 'Ибо похвала наша сия есть свидетельство совести нашей, что мы в простоте и богоугодной искренности, не по плотской мудрости, но по благодати Божией, жили в мире, особенно же у вас.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38346, 'rst66', '2co', 1, 11, 'при содействии и вашей молитвы за нас, дабы за дарованное нам, по ходатайству многих, многие возблагодарили за нас.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38345, 'rst66', '2co', 1, 10, 'Который и избавил нас от столь <i>близкой</i> смерти, и избавляет, и на Которого надеемся, что и еще избавит,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38344, 'rst66', '2co', 1, 9, 'но сами в себе имели приговор к смерти, для того, чтобы надеяться не на самих себя, но на Бога, воскрешающего мертвых,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38343, 'rst66', '2co', 1, 8, 'Ибо мы не хотим оставить вас, братия, в неведении о скорби нашей, бывшей с нами в Асии, потому что мы отягчены были чрезмерно и сверх силы, так что не надеялись остаться в живых,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38342, 'rst66', '2co', 1, 7, 'И надежда наша о вас тверда. Утешаемся ли, <i>утешаемся</i> для вашего утешения и спасения, зная, что вы участвуете как в страданиях наших, так и в утешении.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38341, 'rst66', '2co', 1, 6, 'Скорбим ли мы, <i>скорбим</i> для вашего утешения и спасения, которое совершается перенесением тех же страданий, какие и мы терпим.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38340, 'rst66', '2co', 1, 5, 'Ибо по мере, как умножаются в нас страдания Христовы, умножается Христом и утешение наше.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38339, 'rst66', '2co', 1, 4, 'утешающий нас во всякой скорби нашей, чтобы и мы могли утешать находящихся во всякой скорби тем утешением, которым Бог утешает нас самих!');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38338, 'rst66', '2co', 1, 3, 'Благословен Бог и Отец Господа нашего Иисуса Христа, Отец милосердия и Бог всякого утешения,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38337, 'rst66', '2co', 1, 2, 'благодать вам и мир от Бога Отца нашего и Господа Иисуса Христа.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38336, 'rst66', '2co', 1, 1, 'Павел, волею Божиею Апостол Иисуса Христа, и Тимофей брат &mdash; церкви Божией, находящейся в Коринфе, со всеми святыми по всей Ахаии:');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38335, 'rst66', '1co', 16, 24, 'и любовь моя со всеми вами во Христе Иисусе. Аминь.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38334, 'rst66', '1co', 16, 23, 'Благодать Господа нашего Иисуса Христа с вами,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38333, 'rst66', '1co', 16, 22, 'Кто не любит Господа Иисуса Христа, &mdash; анафема, маранафа&#769;.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38332, 'rst66', '1co', 16, 21, 'Мое, Павлово, приветствие собственноручно.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38331, 'rst66', '1co', 16, 20, 'Приветствуют вас все братия. Приветствуйте друг друга святым целованием.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38330, 'rst66', '1co', 16, 19, 'Приветствуют вас церкви Асийские; приветствуют вас усердно в Господе Акила и Прискилла с домашнею их церковью.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38329, 'rst66', '1co', 16, 18, 'ибо они мой и ваш дух успокоили. Почитайте таковых.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38328, 'rst66', '1co', 16, 17, 'Я рад прибытию Стефана, Фортуната и Ахаика: они восполнили для меня отсутствие ваше,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38327, 'rst66', '1co', 16, 16, 'будьте и вы почтительны к таковым и ко всякому содействующему и трудящемуся.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38326, 'rst66', '1co', 16, 15, 'Прошу вас, братия (вы знаете семейство Стефаново, что оно есть начаток Ахаии и что они посвятили себя на служение святым),');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38325, 'rst66', '1co', 16, 14, 'Все у вас да будет с любовью.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38324, 'rst66', '1co', 16, 13, 'Бодрствуйте, стойте в вере, будьте мужественны, тверды.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38323, 'rst66', '1co', 16, 12, 'А что до брата Аполлоса, я очень просил его, чтобы он с братиями пошел к вам; но он никак не хотел идти ныне, а придет, когда ему будет удобно.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38322, 'rst66', '1co', 16, 11, 'Посему никто не пренебрегай его, но проводите его с миром, чтобы он пришел ко мне, ибо я жду его с братиями.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38321, 'rst66', '1co', 16, 10, 'Если же придет к вам Тимофей, смотри&#769;те, чтобы он был у вас безопасен; ибо он делает дело Господне, как и я.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38320, 'rst66', '1co', 16, 9, 'ибо для меня отверста великая и широкая дверь, и противников много.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38319, 'rst66', '1co', 16, 8, 'В Ефесе же я пробуду до Пятидесятницы,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38318, 'rst66', '1co', 16, 7, 'Ибо я не хочу видеться с вами теперь мимоходом, а надеюсь пробыть у вас несколько времени, если Господь позволит.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38317, 'rst66', '1co', 16, 6, 'У вас же, может быть, поживу, или и перезимую, чтобы вы меня проводили, куда пойду.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38316, 'rst66', '1co', 16, 5, 'Я приду к вам, когда пройду Македонию; ибо я иду через Македонию.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38315, 'rst66', '1co', 16, 4, 'А если прилично будет и мне отправиться, то они со мной пойдут.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38314, 'rst66', '1co', 16, 3, 'Когда же приду, то, которых вы изберете, тех отправлю с письмами, для доставления вашего подаяния в Иерусалим.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38313, 'rst66', '1co', 16, 2, 'В первый день недели каждый из вас пусть отлагает у себя и сберегает, сколько позволит ему состояние, чтобы не делать сборов, когда я приду.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38312, 'rst66', '1co', 16, 1, 'При сборе же для святых поступайте так, как я установил в церквах Галатийских.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38311, 'rst66', '1co', 15, 58, 'Итак, братия мои возлюбленные, будьте тверды, непоколебимы, всегда преуспевайте в деле Господнем, зная, что труд ваш не тщетен пред Господом.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38310, 'rst66', '1co', 15, 57, 'Благодарение Богу, даровавшему нам победу Господом нашим Иисусом Христом!');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38309, 'rst66', '1co', 15, 56, 'Жало же смерти &mdash; грех; а сила греха &mdash; закон.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38308, 'rst66', '1co', 15, 55, '«Смерть! где твое жало? ад! где твоя победа?»');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38307, 'rst66', '1co', 15, 54, 'Когда же тленное сие облечется в нетление и смертное сие облечется в бессмертие, тогда сбудется слово написанное: «поглощена смерть победою».');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38306, 'rst66', '1co', 15, 53, 'Ибо тленному сему надлежит облечься в нетление, и смертному сему облечься в бессмертие.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38305, 'rst66', '1co', 15, 52, 'вдруг, во мгновение ока, при последней трубе; ибо вострубит, и мертвые воскреснут нетленными, а мы изменимся.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38304, 'rst66', '1co', 15, 51, 'Говорю вам тайну: не все мы умрем, но все изменимся');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38303, 'rst66', '1co', 15, 50, 'Но то скажу <i>вам</i>, братия, что плоть и кровь не могут наследовать Царствия Божия, и тление не наследует нетления.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38302, 'rst66', '1co', 15, 49, 'И как мы носили образ перстного, будем носить и образ небесного.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38301, 'rst66', '1co', 15, 48, 'Каков перстный, таковы и перстные; и каков небесный, таковы и небесные.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38300, 'rst66', '1co', 15, 47, 'Первый человек &mdash; из земли, перстный; второй человек &mdash; Господь с неба.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38299, 'rst66', '1co', 15, 46, 'Но не духовное прежде, а душевное, потом духовное.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38298, 'rst66', '1co', 15, 45, 'Так и написано: «первый человек Адам стал душою живущею»; а последний Адам есть дух животворящий.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38297, 'rst66', '1co', 15, 44, 'сеется тело душевное, восстает тело духовное. Есть тело душевное, есть тело и духовное.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38296, 'rst66', '1co', 15, 43, 'сеется в уничижении, восстает в славе; сеется в немощи, восстает в силе;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38295, 'rst66', '1co', 15, 42, 'Так и при воскресении мертвых: сеется в тлении, восстает в нетлении;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38294, 'rst66', '1co', 15, 41, 'Иная слава солнца, иная слава луны, иная звезд; и звезда от звезды разнится в славе.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38293, 'rst66', '1co', 15, 40, 'Есть тела небесные и тела земные; но иная слава небесных, иная земных.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38292, 'rst66', '1co', 15, 39, 'Не всякая плоть такая же плоть; но иная плоть у человеков, иная плоть у скотов, иная у рыб, иная у птиц.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38291, 'rst66', '1co', 15, 38, 'но Бог дает ему тело, как хочет, и каждому семени свое тело.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38290, 'rst66', '1co', 15, 37, 'И когда ты сеешь, то сеешь не тело будущее, а голое зерно, какое случится, пшеничное или другое какое;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38289, 'rst66', '1co', 15, 36, 'Безрассудный! то, что ты сеешь, не оживет, если не умрет.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38288, 'rst66', '1co', 15, 35, 'Но скажет кто‐нибудь: «как воскреснут мертвые? и в каком теле придут?»');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38287, 'rst66', '1co', 15, 34, 'Отрезвитесь, как должно, и не грешите; ибо, к стыду вашему скажу, некоторые из вас не знают Бога.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38286, 'rst66', '1co', 15, 33, 'Не обманывайтесь: худые сообщества развращают добрые нравы.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38285, 'rst66', '1co', 15, 32, 'По <i>рассуждению</i> человеческому, когда я боролся со зверями в Ефесе, какая мне польза, если мертвые не воскресают? Станем есть и пить, ибо завтра умрем!');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38284, 'rst66', '1co', 15, 31, 'Я каждый день умираю: свидетельствуюсь в том похвалою вашею, братия, которую я имею во Христе Иисусе, Господе нашем.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38283, 'rst66', '1co', 15, 30, 'Для чего и мы ежечасно подвергаемся бедствиям?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38282, 'rst66', '1co', 15, 29, 'Иначе, что делают крестящиеся для мертвых? Если мертвые совсем не воскресают, то для чего и крестятся для мертвых?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38281, 'rst66', '1co', 15, 28, 'Когда же все покорит Ему, тогда и Сам Сын покорится Покорившему все Ему, да будет Бог все во всем.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38280, 'rst66', '1co', 15, 27, 'потому что все покорил под ноги Его. Когда же сказано, что <i>Ему</i> все покорено, то ясно, что кроме Того, Который покорил Ему все.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38279, 'rst66', '1co', 15, 26, 'Последний же враг истребится &mdash; смерть,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38278, 'rst66', '1co', 15, 25, 'Ибо Ему надлежит царствовать, доколе низложит всех врагов под ноги Свои.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38277, 'rst66', '1co', 15, 24, 'а затем конец, когда Он предаст Царство Богу и Отцу, когда упразднит всякое начальство и всякую власть и силу.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38276, 'rst66', '1co', 15, 23, 'каждый в своем порядке: первенец Христос, потом Христовы, в пришествие Его;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38275, 'rst66', '1co', 15, 22, 'Как в Адаме все умирают, так во Христе все оживут,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38274, 'rst66', '1co', 15, 21, 'Ибо, как смерть через человека, <i>так</i> через человека и воскресение мертвых.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38273, 'rst66', '1co', 15, 20, 'Но Христос воскрес из мертвых, первенец из умерших.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38272, 'rst66', '1co', 15, 19, 'И если мы в этой только жизни надеемся на Христа, то мы несчастнее всех человеков.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38271, 'rst66', '1co', 15, 18, 'Поэтому и умершие во Христе погибли.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38270, 'rst66', '1co', 15, 17, 'А если Христос не воскрес, то вера ваша тщетна: вы еще во грехах ваших.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38269, 'rst66', '1co', 15, 16, 'ибо если мертвые не воскресают, то и Христос не воскрес.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38268, 'rst66', '1co', 15, 15, 'Притом мы оказались бы и лжесвидетелями о Боге, потому что свидетельствовали бы о Боге, что Он воскресил Христа, Которого Он не воскрешал, если, <i>то есть</i>, мертвые не воскресают;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38267, 'rst66', '1co', 15, 14, 'а если Христос не воскрес, то и проповедь наша тщетна, тщетна и вера ваша.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38266, 'rst66', '1co', 15, 13, 'Если нет воскресения мертвых, то и Христос не воскрес;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38265, 'rst66', '1co', 15, 12, 'Если же о Христе проповедуется, что Он воскрес из мертвых, то ка&#769;к некоторые из вас говорят, что нет воскресения мертвых?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38264, 'rst66', '1co', 15, 11, 'Итак, я ли, они ли, мы так проповедуем, и вы так уверовали.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38263, 'rst66', '1co', 15, 10, 'Но благодатию Божиею есмь то, что есмь; и благодать Его во мне не была тщетна, но я более всех их потрудился: не я, впрочем, а благодать Божия, которая со мною.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38262, 'rst66', '1co', 15, 9, 'Ибо я наименьший из Апостолов, и недостоин называться Апостолом, потому что гнал церковь Божию.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38261, 'rst66', '1co', 15, 8, 'а после всех явился и мне, как некоему извергу.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38260, 'rst66', '1co', 15, 7, 'потом явился Иакову, также всем Апостолам;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38259, 'rst66', '1co', 15, 6, 'потом явился более нежели пятистам братий в одно время, из которых большая часть доныне в живых, а некоторые и почили;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38258, 'rst66', '1co', 15, 5, 'и что явился Кифе, потом Двенадцати;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38257, 'rst66', '1co', 15, 4, 'и что Он погребен был, и что воскрес в третий день, по Писанию,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38256, 'rst66', '1co', 15, 3, 'Ибо я первоначально преподал вам, что и <i>сам</i> принял, <i>то есть</i> что Христос умер за грехи наши, по Писанию,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38255, 'rst66', '1co', 15, 2, 'которым и спасаетесь, если преподанное удерживаете так, как я благовествовал вам, если только не тщетно уверовали.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38254, 'rst66', '1co', 15, 1, 'Напоминаю вам, братия, Евангелие, которое я благовествовал вам, которое вы и приняли, в котором и утвердились,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38253, 'rst66', '1co', 14, 40, 'только всё должно быть благопристойно и чинно.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38252, 'rst66', '1co', 14, 39, 'Итак, братия, ревнуйте о том, чтобы пророчествовать, но не запрещайте говорить и языками;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38251, 'rst66', '1co', 14, 38, 'А кто не разумеет, пусть не разумеет.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38250, 'rst66', '1co', 14, 37, 'Если кто почитает себя пророком или духовным, тот да разумеет, что&#769; я пишу вам, ибо это заповеди Господни.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38249, 'rst66', '1co', 14, 36, 'Разве от вас вышло слово Божие? Или до вас одних достигло?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38248, 'rst66', '1co', 14, 35, 'Если же они хотят чему научиться, пусть спрашивают <i>о том</i> дома у мужей своих; ибо неприлично жене говорить в церкви.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38247, 'rst66', '1co', 14, 34, 'Жены ваши в церквах да молчат, ибо не позволено им говорить, а быть в подчинении, как и закон говорит.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38246, 'rst66', '1co', 14, 33, 'потому что Бог не есть <i>Бог</i> неустройства, но мира. Так <i>бывает</i> во всех церквах у святых.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38245, 'rst66', '1co', 14, 32, 'И духи пророческие послушны пророкам,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38244, 'rst66', '1co', 14, 31, 'Ибо все один за другим можете пророчествовать, чтобы всем поучаться и всем получать утешение.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38243, 'rst66', '1co', 14, 30, 'Если же другому из сидящих будет откровение, то первый молчи.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38242, 'rst66', '1co', 14, 29, 'И пророки пусть говорят двое или трое, а прочие пусть рассуждают.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38241, 'rst66', '1co', 14, 28, 'Если же не будет истолкователя, то молчи в церкви, а говори себе и Богу.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38240, 'rst66', '1co', 14, 27, 'Если кто говорит на <i>незнакомом</i> языке, <i>говорите</i> двое, или много трое, и то порознь, а один изъясняй.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38239, 'rst66', '1co', 14, 26, 'Итак, что же, братия? Когда вы сходитесь, и у каждого из вас есть псалом, есть поучение, есть язык, есть откровение, есть истолкование, &mdash; все сие да будет к назиданию.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38238, 'rst66', '1co', 14, 25, 'И таким образом тайны сердца его обнаруживаются, и он падет ниц, поклонится Богу и скажет: «истинно с вами Бог».');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38237, 'rst66', '1co', 14, 24, 'Но когда все пророчествуют, и войдет кто неверующий или незнающий, то он всеми обличается, всеми судится.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38236, 'rst66', '1co', 14, 23, 'Если вся церковь сойдется вместе, и все станут говорить <i>незнакомыми</i> языками, и войдут к вам незнающие или неверующие, то не скажут ли, что вы беснуетесь?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38235, 'rst66', '1co', 14, 22, 'Итак, языки суть знамение не для верующих, а для неверующих; пророчество же не для неверующих, а для верующих.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38234, 'rst66', '1co', 14, 21, 'В законе написано: «иными языками и иными устами буду говорить народу сему; но и тогда не послушают Меня, говорит Господь».');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38233, 'rst66', '1co', 14, 20, 'Братия! не будьте дети умом: на злое будьте младенцы, а по уму будьте совершеннолетни.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38232, 'rst66', '1co', 14, 19, 'но в церкви хочу лучше пять слов сказать умом моим, чтобы и других наставить, нежели тьму слов на <i>незнакомом</i> языке.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38231, 'rst66', '1co', 14, 18, 'Благодарю Бога моего: я более всех вас говорю языками;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38230, 'rst66', '1co', 14, 17, 'Ты хорошо благодаришь, но другой не назидается.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38229, 'rst66', '1co', 14, 16, 'Ибо если ты будешь благословлять духом, то стоящий на месте простолюдина как скажет: «аминь» при твоем благодарении? Ибо он не понимает, что&#769; ты говоришь.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38228, 'rst66', '1co', 14, 15, 'Что же делать? Стану молиться духом, стану молиться и умом; буду петь духом, буду петь и умом.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38227, 'rst66', '1co', 14, 14, 'Ибо когда я молюсь на <i>незнакомом</i> языке, то хотя дух мой и молится, но ум мой остается без плода.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38226, 'rst66', '1co', 14, 13, 'А потому, говорящий на <i>незнакомом</i> языке, молись о даре истолкования.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38225, 'rst66', '1co', 14, 12, 'Так и вы, ревнуя о <i>дарах</i> духовных, старайтесь обогатиться <i>ими</i> к назиданию церкви.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38224, 'rst66', '1co', 14, 11, 'Но если я не разумею значения слов, то я для говорящего чужестранец, и говорящий для меня чужестранец.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38223, 'rst66', '1co', 14, 10, 'Сколько, например, различных слов в мире, и ни одного из них нет без значения.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38222, 'rst66', '1co', 14, 9, 'Так, если и вы языком произносите невразумительные слова, то как узна&#769;ют, что&#769; вы говорите? Вы будете говорить на ветер.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38221, 'rst66', '1co', 14, 8, 'И если труба будет издавать неопределенный звук, кто станет готовиться к сражению?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38220, 'rst66', '1co', 14, 7, 'И бездушные <i>вещи</i>, издающие звук, свирель или гусли, если не производят раздельных тонов, как распознать то, что&#769; играют на свирели или на гуслях?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38219, 'rst66', '1co', 14, 6, 'Теперь, если я приду к вам, братия, и стану говорить на <i>незнакомых</i> языках, то какую принесу вам пользу, когда не изъяснюсь вам или откровением, или познанием, или пророчеством, или учением?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38218, 'rst66', '1co', 14, 5, 'Желаю, чтобы вы все говорили языками; но лучше, чтобы вы пророчествовали; ибо пророчествующий превосходнее того, кто говорит языками, разве он притом будет и изъяснять, чтобы церковь получила назидание.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38217, 'rst66', '1co', 14, 4, 'Кто говорит на <i>незнакомом</i> языке, тот назидает себя; а кто пророчествует, тот назидает церковь.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38216, 'rst66', '1co', 14, 3, 'а кто пророчествует, тот говорит людям в назидание, увещание и утешение.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38215, 'rst66', '1co', 14, 2, 'Ибо кто говорит на <i>незнакомом</i> языке, тот говорит не людям, а Богу; потому что никто не понимает <i>его</i>, он тайны говорит духом;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38214, 'rst66', '1co', 14, 1, 'Достигайте любви; ревнуйте о <i>дарах</i> духовных, особенно же о том, чтобы пророчествовать.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38213, 'rst66', '1co', 13, 13, 'А теперь пребывают сии три: вера, надежда, любовь; но любовь из них больше.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38212, 'rst66', '1co', 13, 12, 'Теперь мы видим как бы сквозь <i>тусклое</i> стекло, гадательно, тогда же лицом к лицу; теперь знаю я отчасти, а тогда позна&#769;ю, подобно как я познан.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38211, 'rst66', '1co', 13, 11, 'Когда я был младенцем, то по‐младенчески говорил, по‐младенчески мыслил, по‐младенчески рассуждал; а как стал мужем, то оставил младенческое.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38210, 'rst66', '1co', 13, 10, 'когда же настанет совершенное, тогда то, что отчасти, прекратится.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38209, 'rst66', '1co', 13, 9, 'Ибо мы отчасти знаем и отчасти пророчествуем;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38208, 'rst66', '1co', 13, 8, 'Любовь никогда не перестает, хотя и пророчества прекратятся, и языки умолкнут, и знание упразднится.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38207, 'rst66', '1co', 13, 7, 'все покрывает, всему верит, всего надеется, все переносит.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38206, 'rst66', '1co', 13, 6, 'не радуется неправде, а сорадуется истине;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38205, 'rst66', '1co', 13, 5, 'не бесчинствует, не ищет своего, не раздражается, не мыслит зла,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38204, 'rst66', '1co', 13, 4, 'Любовь долготерпит, милосердствует, любовь не завидует, любовь не превозносится, не гордится,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38203, 'rst66', '1co', 13, 3, 'И если я раздам все имение мое и отдам тело мое на сожжение, а любви не имею, нет мне в том никакой пользы.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38202, 'rst66', '1co', 13, 2, 'Если имею <i>дар</i> пророчества, и знаю все тайны, и имею всякое познание и всю веру, так что <i>могу</i> и горы переставлять, а не имею любви, &mdash; то я ничто.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38201, 'rst66', '1co', 13, 1, 'Если я говорю языками человеческими и ангельскими, а любви не имею, то я &mdash; медь звенящая или кимвал звучащий.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38200, 'rst66', '1co', 12, 31, 'Ревнуйте о дарах бо&#769;льших, и я покажу вам путь еще превосходнейший.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38199, 'rst66', '1co', 12, 30, 'Все ли имеют дары исцелений? Все ли говорят языками? Все ли истолкователи?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38198, 'rst66', '1co', 12, 29, 'Все ли Апостолы? Все ли пророки? Все ли учители? Все ли чудотворцы?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38197, 'rst66', '1co', 12, 28, 'И иных Бог поставил в Церкви, во‐первых, Апостолами, во‐вторых, пророками, в‐третьих, учителями; далее, <i>иным дал</i> силы <i>чудодейственные</i>, также дары исцелений, вспоможения, управления, разные языки.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38196, 'rst66', '1co', 12, 27, 'И вы &mdash; тело Христово, а порознь &mdash; члены.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38195, 'rst66', '1co', 12, 26, 'Посему, страдает ли один член &mdash; страдают с ним все члены; славится ли один член &mdash; с ним радуются все члены.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38194, 'rst66', '1co', 12, 25, 'дабы не было разделения в теле, а все члены одинаково заботились друг о друге.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38193, 'rst66', '1co', 12, 24, 'и неблагообразные наши более благовидно покрываются, а благообразные наши не имеют <i>в том</i> нужды. Но Бог соразмерил тело, внушив о менее совершенном большее попечение,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38192, 'rst66', '1co', 12, 23, 'и которые нам кажутся менее благородными в теле, о тех более прилагаем попечения;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38191, 'rst66', '1co', 12, 22, 'Напротив, члены тела, которые кажутся слабейшими, гораздо нужнее,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38190, 'rst66', '1co', 12, 21, 'Не может глаз сказать руке: «ты мне не надобна»; или также голова ногам: «вы мне не нужны».');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38189, 'rst66', '1co', 12, 20, 'Но теперь членов много, а тело одно.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38188, 'rst66', '1co', 12, 19, 'А если бы все были один член, то где <i>было бы</i> тело?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38187, 'rst66', '1co', 12, 18, 'Но Бог расположил члены, каждый в <i>составе</i> тела, как Ему было угодно.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38186, 'rst66', '1co', 12, 17, 'Если все тело глаз, то где слух? Если все слух, то где обоняние?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38185, 'rst66', '1co', 12, 16, 'И если ухо скажет: «я не принадлежу к телу, потому что я не глаз», то неужели оно потому не принадлежит к телу?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38184, 'rst66', '1co', 12, 15, 'Если нога скажет: «я не принадлежу к телу, потому что я не рука», то неужели она потому не принадлежит к телу?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38183, 'rst66', '1co', 12, 14, 'Тело же не из одного члена, но из многих.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38182, 'rst66', '1co', 12, 13, 'Ибо все мы одним Духом крестились в одно тело, Иудеи или Еллины, рабы или свободные, и все напоены одним Духом.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38181, 'rst66', '1co', 12, 12, 'Ибо, как тело одно, но имеет многие члены, и все члены одного тела, хотя их и много, составляют одно тело, &mdash; так и Христос.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38180, 'rst66', '1co', 12, 11, 'Все же сие производит один и тот же Дух, разделяя каждому особо, как Ему угодно.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38179, 'rst66', '1co', 12, 10, 'иному чудотворения, иному пророчество, иному различение духов, иному разные языки, иному истолкование языков.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38178, 'rst66', '1co', 12, 9, 'иному вера, тем же Духом; иному дары исцелений, тем же Духом;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38177, 'rst66', '1co', 12, 8, 'Одному дается Духом слово мудрости, другому слово знания, тем же Духом;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38176, 'rst66', '1co', 12, 7, 'Но каждому дается проявление Духа на пользу.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38175, 'rst66', '1co', 12, 6, 'и действия различны, а Бог один и тот же, производящий всё во всех.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38174, 'rst66', '1co', 12, 5, 'и служения различны, а Господь один и тот же;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38173, 'rst66', '1co', 12, 4, 'Дары различны, но Дух один и тот же;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38172, 'rst66', '1co', 12, 3, 'Потому сказываю вам, что никто, говорящий Духом Божиим, не произнесет анафемы на Иисуса, и никто не может назвать Иисуса Господом, как только Духом Святым.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38171, 'rst66', '1co', 12, 2, 'Знаете, что когда вы были язычниками, то ходили к безгласным идолам, так, как бы вели вас.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38170, 'rst66', '1co', 12, 1, 'Не хочу оставить вас, братия, в неведении и о <i>дарах</i> духовных.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38169, 'rst66', '1co', 11, 34, 'А если кто голоден, пусть ест дома, чтобы собираться вам не на осуждение. Прочее устрою, когда приду.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38168, 'rst66', '1co', 11, 33, 'Посему, братия мои, собираясь на вечерю, друг друга ждите.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38167, 'rst66', '1co', 11, 32, 'Будучи же судимы, наказываемся от Господа, чтобы не быть осужденными с миром.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38166, 'rst66', '1co', 11, 31, 'Ибо если бы мы судили сами себя, то не были бы судимы.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38165, 'rst66', '1co', 11, 30, 'Оттого многие из вас немощны и больны и немало умирает.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38164, 'rst66', '1co', 11, 29, 'Ибо кто ест и пьет недостойно, тот ест и пьет осуждение себе, не рассуждая о Теле Господнем.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38163, 'rst66', '1co', 11, 28, 'Да испытывает же себя человек, и таким образом пусть ест от хлеба сего и пьет из чаши сей.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38162, 'rst66', '1co', 11, 27, 'Посему, кто будет есть хлеб сей или пить чашу Господню недостойно, виновен будет против Тела и Крови Господней.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38161, 'rst66', '1co', 11, 26, 'Ибо всякий раз, когда вы едите хлеб сей и пьете чашу сию, смерть Господню возвещаете, доколе Он придет.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38160, 'rst66', '1co', 11, 25, 'Также и чашу после вечери, и сказал: «сия чаша есть новый завет в Моей Крови; сие творите, когда только будете пить, в Мое воспоминание».');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38159, 'rst66', '1co', 11, 24, 'и, возблагодарив, преломил и сказал: «приимите, ядите, сие есть Тело Мое, за вас ломимое; сие творите в Мое воспоминание».');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38158, 'rst66', '1co', 11, 23, 'Ибо я от <i>Самого</i> Господа принял то, что и вам передал, что Господь Иисус в ту ночь, в которую предан был, взял хлеб');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38157, 'rst66', '1co', 11, 22, 'Разве у вас нет домов на то, чтобы есть и пить? Или пренебрегаете церковь Божию и унижаете неимущих? Что сказать вам? похвалить ли вас за это? Не похвалю.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38156, 'rst66', '1co', 11, 21, 'ибо всякий поспешает прежде <i>других</i> есть свою пищу, <i>так что</i> иной бывает голоден, а иной упивается.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38155, 'rst66', '1co', 11, 20, 'Далее, вы собираетесь <i>так</i>, <i>что это</i> не значит вкушать вечерю Господню;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38154, 'rst66', '1co', 11, 19, 'Ибо надлежит быть и разномыслиям между вами, дабы открылись между вами искусные.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38153, 'rst66', '1co', 11, 18, 'Ибо, во‐первых, слышу, что, когда вы собираетесь в церковь, между вами бывают разделения, чему отчасти и верю.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38152, 'rst66', '1co', 11, 17, 'Но, предлагая сие, не хвалю <i>вас</i>, что вы собираетесь не на лучшее, а на худшее.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38151, 'rst66', '1co', 11, 16, 'А если бы кто захотел спорить, то мы не имеем такого обычая, ни церкви Божии.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38150, 'rst66', '1co', 11, 15, 'но если жена растит волосы, для нее это честь, так как волосы даны ей вместо покрывала?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38149, 'rst66', '1co', 11, 14, 'Не сама ли природа учит вас, что если муж растит волосы, то это бесчестье для него,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38148, 'rst66', '1co', 11, 13, 'Рассудите сами, прилично ли жене молиться Богу с непокрытою <i>головою?</i>');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38147, 'rst66', '1co', 11, 12, 'Ибо как жена от мужа, так и муж через жену; все же &mdash; от Бога.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38146, 'rst66', '1co', 11, 11, 'Впрочем, ни муж без жены, ни жена без мужа, в Господе.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38145, 'rst66', '1co', 11, 10, 'Посему жена и должна иметь на голове своей <i>знак</i> власти <i>над нею</i>, для Ангелов.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38144, 'rst66', '1co', 11, 9, 'и не муж создан для жены, но жена для мужа.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38143, 'rst66', '1co', 11, 8, 'Ибо не муж от жены, но жена от мужа;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38142, 'rst66', '1co', 11, 7, 'Итак, муж не должен покрывать голову, потому что он есть образ и слава Божия; а жена есть слава мужа.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38141, 'rst66', '1co', 11, 6, 'Ибо если жена не хочет покрываться, то пусть и стрижется; а если жене стыдно быть остриженной или обритой, пусть покрывается.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38140, 'rst66', '1co', 11, 5, 'И всякая жена, молящаяся или пророчествующая с открытою головою, постыжает свою голову, ибо <i>это</i> то же, как если бы она была обритая.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38139, 'rst66', '1co', 11, 4, 'Всякий муж, молящийся или пророчествующий с покрытою головою, постыжает свою голову.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38138, 'rst66', '1co', 11, 3, 'Хочу также, чтобы вы знали, что всякому мужу глава &mdash; Христос, жене глава &mdash; муж, а Христу глава &mdash; Бог.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38137, 'rst66', '1co', 11, 2, 'Хвалю вас, братия, что вы все мое помните и держите предания так, как я передал вам.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38136, 'rst66', '1co', 11, 1, 'Будьте подражателями мне, как я Христу.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38135, 'rst66', '1co', 10, 33, 'так, как и я угождаю всем во всем, ища не своей пользы, но <i>пользы</i> многих, чтобы они спаслись.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38134, 'rst66', '1co', 10, 32, 'Не подавайте соблазна ни Иудеям, ни Еллинам, ни церкви Божией,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38133, 'rst66', '1co', 10, 31, 'Итак, едите ли, пьете ли или иное что делаете, все делайте в славу Божию.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38132, 'rst66', '1co', 10, 30, 'Если я с благодарением принимаю <i>пищу</i>, то для чего порицать меня за то, за что я благодарю?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38131, 'rst66', '1co', 10, 29, 'Совесть же разумею не свою, а другого: ибо для чего моей свободе быть судимой чужою совестью?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38130, 'rst66', '1co', 10, 28, 'Но если кто скажет вам: «это идоложертвенное», &mdash; то не ешьте ради того, кто объявил вам, и ради совести. Ибо Господня земля и что наполняет ее.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38129, 'rst66', '1co', 10, 27, 'Если кто из неверных позовет вас, и вы захотите пойти, то все, предлагаемое вам, ешьте без всякого исследования, для <i>спокойствия</i> совести.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38128, 'rst66', '1co', 10, 26, 'ибо Господня земля и что наполняет ее.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38127, 'rst66', '1co', 10, 25, 'Все, что продается на торгу, ешьте без всякого исследования, для <i>спокойствия</i> совести;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38126, 'rst66', '1co', 10, 24, 'Никто не ищи своего, но каждый <i>пользы</i> другого.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38125, 'rst66', '1co', 10, 23, 'Все мне позволительно, но не все полезно; все мне позволительно, но не все назидает.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38124, 'rst66', '1co', 10, 22, 'Неужели мы <i>решимся</i> раздражать Господа? Разве мы сильнее Его?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38123, 'rst66', '1co', 10, 21, 'Не можете пить чашу Господню и чашу бесовскую; не можете быть участниками в трапезе Господней и в трапезе бесовской.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38122, 'rst66', '1co', 10, 20, 'Нет, но что язычники, принося жертвы, приносят бесам, а не Богу. Но я не хочу, чтобы вы были в общении с бесами.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38121, 'rst66', '1co', 10, 19, 'Что же я говорю? То ли, что идол есть что‐нибудь или идоложертвенное значит что‐нибудь?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38120, 'rst66', '1co', 10, 18, 'Посмотри&#769;те на Израиля по плоти: те, которые едят жертвы, не участники ли жертвенника?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38119, 'rst66', '1co', 10, 17, 'Один хлеб, и мы многие одно тело; ибо все причащаемся от одного хлеба.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38118, 'rst66', '1co', 10, 16, 'Чаша благословения, которую благословляем, не есть ли приобщение Крови Христовой? Хлеб, который преломляем, не есть ли приобщение Тела Христова?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38117, 'rst66', '1co', 10, 15, 'Я говорю <i>вам</i> как рассудительным; сами рассуди&#769;те о том, что говорю.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38116, 'rst66', '1co', 10, 14, 'Итак, возлюбленные мои, убегайте идолослужения.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38115, 'rst66', '1co', 10, 13, 'Вас постигло искушение не иное, как человеческое; и верен Бог, Который не попустит вам быть искушаемыми сверх сил, но при искушении даст и облегчение, так чтобы вы могли перенести.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38114, 'rst66', '1co', 10, 12, 'Посему, кто думает, что он стои&#769;т, берегись, чтобы не упасть.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38113, 'rst66', '1co', 10, 11, 'Все это происходило с ними, <i>как</i> образы; а описано в наставление нам, достигшим последних веков.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38112, 'rst66', '1co', 10, 10, 'Не ропщите, как некоторые из них роптали и погибли от истребителя.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38111, 'rst66', '1co', 10, 9, 'Не станем искушать Христа, как некоторые из них искушали и погибли от змей.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38110, 'rst66', '1co', 10, 8, 'Не станем блудодействовать, как некоторые из них блудодействовали, и в один день погибло их двадцать три тысячи.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38109, 'rst66', '1co', 10, 7, 'Не будьте также идолопоклонниками, как некоторые из них, о которых написано: «народ сел есть и пить, и встал играть».');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38108, 'rst66', '1co', 10, 6, 'А это были образы для нас, чтобы мы не были похотливы на злое, как они были похотливы.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38107, 'rst66', '1co', 10, 5, 'Но не о многих из них благоволил Бог, ибо они поражены были в пустыне.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38106, 'rst66', '1co', 10, 4, 'и все пили одно и то же духовное питие: ибо пили из духовного последующего камня; камень же был Христос.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38105, 'rst66', '1co', 10, 3, 'и все ели одну и ту же духовную пищу;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38104, 'rst66', '1co', 10, 2, 'и все крестились в Моисея в облаке и в море;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38103, 'rst66', '1co', 10, 1, 'Не хочу оставить вас, братия, в неведении, что отцы наши все были под облаком, и все прошли сквозь море;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38102, 'rst66', '1co', 9, 27, 'но усмиряю и порабощаю тело мое, дабы, проповедуя другим, самому не остаться недостойным.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38101, 'rst66', '1co', 9, 26, 'И потому я бегу не так, как на неверное, бьюсь не так, чтобы только бить воздух;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38100, 'rst66', '1co', 9, 25, 'Все подвижники воздерживаются от всего: те для получения венца тленного, а мы &mdash; нетленного.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38099, 'rst66', '1co', 9, 24, 'Не знаете ли, что бегущие на ристалище бегут все, но один получает награду? Так бегите, чтобы получить.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38098, 'rst66', '1co', 9, 23, 'Сие же делаю для Евангелия, чтобы быть соучастником его.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38097, 'rst66', '1co', 9, 22, 'для немощных был как немощный, чтобы приобрести немощных. Для всех я сделался всем, чтобы спасти по крайней мере некоторых.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38096, 'rst66', '1co', 9, 21, 'для чуждых закона &mdash; как чуждый закона, &mdash; не будучи чужд закона пред Богом, но подзаконен Христу, &mdash; чтобы приобрести чуждых закона;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38095, 'rst66', '1co', 9, 20, 'для Иудеев я был как Иудей, чтобы приобрести Иудеев; для подзаконных был как подзаконный, чтобы приобрести подзаконных;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38094, 'rst66', '1co', 9, 19, 'Ибо, будучи свободен от всех, я всем поработил себя, дабы больше приобрести:');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38093, 'rst66', '1co', 9, 18, 'За что же мне награда? За то, что, проповедуя Евангелие, благовествую о Христе безмездно, не пользуясь моею властью в благовествовании.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38092, 'rst66', '1co', 9, 17, 'Ибо если делаю это добровольно, то <i>буду</i> иметь награду; а если не добровольно, то <i>исполняю только</i> вверенное мне служение.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38091, 'rst66', '1co', 9, 16, 'Ибо если я благовествую, то нечем мне хвалиться, потому что это необходимая <i>обязанность</i> моя, и горе мне, если не благовествую!');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38090, 'rst66', '1co', 9, 15, 'Но я не пользовался ничем таковым. И написал это не для того, чтобы та&#769;к было для меня. Ибо для меня лучше умереть, нежели чтобы кто уничтожил похвалу мою.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38089, 'rst66', '1co', 9, 14, 'Так и Господь повелел проповедующим Евангелие жить от благовествования.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38088, 'rst66', '1co', 9, 13, 'Разве не знаете, что священнодействующие питаются от святилища? что служащие жертвеннику берут долю от жертвенника?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38087, 'rst66', '1co', 9, 12, 'Если другие имеют у вас власть, не паче ли мы? Однако мы не пользовались сею властью, но все переносим, дабы не поставить какой преграды благовествованию Христову.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38086, 'rst66', '1co', 9, 11, 'Если мы посеяли в вас духовное, велико ли то, если пожнем у вас телесное?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38085, 'rst66', '1co', 9, 10, 'Или, конечно, для нас говорится? Так, для нас это написано; ибо кто пашет, должен пахать с надеждою, и кто молотит, <i>должен молотить</i> с надеждою получить ожидаемое.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38084, 'rst66', '1co', 9, 9, 'Ибо в Моисеевом законе написано: «не заграждай рта у вола молотящего». О волах ли печется Бог?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38083, 'rst66', '1co', 9, 8, 'По человеческому ли только <i>рассуждению</i> я это говорю? Не то же ли говорит и закон?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38082, 'rst66', '1co', 9, 7, 'Какой воин служит когда‐либо на своем содержании? Кто, насадив виноград, не ест плодов его? Кто, пася стадо, не ест молока от стада?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38081, 'rst66', '1co', 9, 6, 'Или один я и Варнава не имеем власти не работать?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38080, 'rst66', '1co', 9, 5, 'Или не имеем власти иметь спутницею сестру жену, как и прочие Апостолы, и братья Господни, и Кифа?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38079, 'rst66', '1co', 9, 4, 'Или мы не имеем власти есть и пить?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38078, 'rst66', '1co', 9, 3, 'Вот мое защищение против осуждающих меня.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38077, 'rst66', '1co', 9, 2, 'Если для других я не Апостол, то для вас <i>Апостол;</i> ибо печать моего апостольства &mdash; вы в Господе.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38076, 'rst66', '1co', 9, 1, 'Не Апостол ли я? Не свободен ли я? Не видел ли я Иисуса Христа, Господа нашего? Не мое ли дело вы в Господе?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38075, 'rst66', '1co', 8, 13, 'И потому, если пища соблазняет брата моего, не буду есть мяса вовек, чтобы не соблазнить брата моего.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38074, 'rst66', '1co', 8, 12, 'А согрешая таким образом против братьев и уязвляя немощную совесть их, вы согрешаете против Христа.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38073, 'rst66', '1co', 8, 11, 'И от знания твоего погибнет немощный брат, за которого умер Христос.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38072, 'rst66', '1co', 8, 10, 'Ибо если кто‐нибудь увидит, что ты, имея знание, сидишь за столом в капище, то совесть его, как немощного, не расположит ли и его есть идоложертвенное?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38071, 'rst66', '1co', 8, 9, 'Берегитесь, однако же, чтобы эта свобода ваша не послужила соблазном для немощных.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38070, 'rst66', '1co', 8, 8, 'Пища не приближает нас к Богу: ибо, едим ли мы, ничего не приобретаем; не едим ли, ничего не теряем.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38069, 'rst66', '1co', 8, 7, 'Но не у всех <i>такое</i> знание: некоторые и доныне с совестью, <i>признающею</i> идолов, едят <i>идоложертвенное</i>, как жертвы идольские, и совесть их, будучи немощна, оскверняется.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38068, 'rst66', '1co', 8, 6, 'но у нас один Бог Отец, из Которого все, и мы для Него, и один Господь Иисус Христос, Которым все, и мы Им.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38067, 'rst66', '1co', 8, 5, 'Ибо хотя и есть так называемые боги, или на небе, или на земле, &mdash; так как есть много богов и господ много, &mdash;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38066, 'rst66', '1co', 8, 4, 'Итак, об употреблении в пищу идоложертвенного мы знаем, что идол в мире ничто и что нет иного Бога, кроме Единого.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38065, 'rst66', '1co', 8, 3, 'Но кто любит Бога, тому дано знание от Него.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38064, 'rst66', '1co', 8, 2, 'Кто думает, что он знает что‐нибудь, тот ничего еще не знает так, как должно знать.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38063, 'rst66', '1co', 8, 1, 'О идоложертвенных <i>яствах</i> мы знаем, потому что мы все имеем знание; но знание надмевает, а любовь назидает.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38062, 'rst66', '1co', 7, 40, 'Но она блаженнее, если останется так, по моему совету; а думаю, и я имею Духа Божия.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38061, 'rst66', '1co', 7, 39, 'Жена связана законом, доколе жив муж ее; если же муж ее умрет, свободна выйти, за кого хочет, только в Господе.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38060, 'rst66', '1co', 7, 38, 'Посему выдающий замуж свою девицу поступает хорошо; а не выдающий поступает лучше.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38059, 'rst66', '1co', 7, 37, 'Но кто непоколебимо тверд в сердце своем и, не будучи стесняем нуждою, но будучи властен в своей воле, решился в сердце своем соблюдать свою деву, тот хорошо поступает.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38058, 'rst66', '1co', 7, 36, 'Если же кто почитает неприличным для своей девицы то, чтобы она, будучи в зрелом возрасте, оставалась так, тот пусть делает как хочет: не согрешит; пусть <i>таковые</i> выходят замуж.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38057, 'rst66', '1co', 7, 35, 'Говорю это для вашей же пользы, не с тем, чтобы наложить на вас узы, но чтобы вы благочинно и непрестанно <i>служили</i> Господу без развлечения.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38056, 'rst66', '1co', 7, 34, 'незамужняя заботится о Господнем, как угодить Господу, чтобы быть святою и телом и духом; а замужняя заботится о мирском, как угодить мужу.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38055, 'rst66', '1co', 7, 33, 'а женатый заботится о мирском, как угодить жене. Есть разность между замужнею и девицею:');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38054, 'rst66', '1co', 7, 32, 'А я хочу, чтобы вы были без забот. Неженатый заботится о Господнем, как угодить Господу;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38053, 'rst66', '1co', 7, 31, 'и пользующиеся миром сим, как не пользующиеся; ибо проходит образ мира сего.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38052, 'rst66', '1co', 7, 30, 'и плачущие, как не плачущие; и радующиеся, как не радующиеся; и покупающие, как не приобретающие;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38051, 'rst66', '1co', 7, 29, 'Я вам сказываю, братия: время уже коротко, так что имеющие жен должны быть, как не имеющие;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38050, 'rst66', '1co', 7, 28, 'Впрочем, если и женишься, не согрешишь; и если девица выйдет замуж, не согрешит. Но таковые будут иметь скорби по плоти; а мне вас жаль.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38049, 'rst66', '1co', 7, 27, 'Соединен ли ты с женою? не ищи развода. Остался ли без жены? не ищи жены.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38048, 'rst66', '1co', 7, 26, 'По настоящей нужде за лучшее признаю&#769;, что хорошо человеку оставаться та&#769;к.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38047, 'rst66', '1co', 7, 25, 'Относительно девства я не имею повеления Господня, а даю совет, как получивший от Господа милость быть <i>Ему</i> верным.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38046, 'rst66', '1co', 7, 24, 'В каком <i>звании</i> кто призван, братия, в том каждый и оставайся пред Богом.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38045, 'rst66', '1co', 7, 23, 'Вы куплены <i>дорогою</i> ценою; не делайтесь рабами человеков.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38044, 'rst66', '1co', 7, 22, 'Ибо раб, призванный в Господе, есть свободный Господа; равно и призванный свободным есть раб Христов.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38043, 'rst66', '1co', 7, 21, 'Рабом ли ты призван, не смущайся; но если и можешь сделаться свободным, то лучшим воспользуйся.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38042, 'rst66', '1co', 7, 20, 'Каждый оставайся в том звании, в котором призван.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38041, 'rst66', '1co', 7, 19, 'Обрезание ничто и необрезание ничто, но <i>всё</i> в соблюдении заповедей Божиих.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38040, 'rst66', '1co', 7, 18, 'Призван ли кто обрезанным, не скрывайся; призван ли кто необрезанным, не обрезывайся.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38039, 'rst66', '1co', 7, 17, 'Только каждый поступай так, как Бог ему определил, и каждый, как Господь призвал. Так я повелеваю по всем церквам.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38038, 'rst66', '1co', 7, 16, 'Почему ты знаешь, жена, не спасешь ли мужа? Или ты, муж, почему знаешь, не спасешь ли жены?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38037, 'rst66', '1co', 7, 15, 'Если же неверующий <i>хочет</i> развестись, пусть разводится; брат или сестра в таких <i>случаях</i> не связаны; к миру призвал нас Господь.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38036, 'rst66', '1co', 7, 14, 'Ибо неверующий муж освящается женою верующею, и жена неверующая освящается мужем верующим. Иначе дети ваши были бы нечисты, а теперь &mdash; святы.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38035, 'rst66', '1co', 7, 13, 'и жена, которая имеет мужа неверующего, и он согласен жить с нею, не должна оставлять его.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38034, 'rst66', '1co', 7, 12, 'Прочим же я говорю, а не Господь: если какой брат имеет жену неверующую, и она согласна жить с ним, то он не должен оставлять ее;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38033, 'rst66', '1co', 7, 11, 'если же разведется, то должна оставаться безбрачною, или примириться с мужем своим, &mdash; и мужу не оставлять жены <i>своей</i>.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38032, 'rst66', '1co', 7, 10, 'А вступившим в брак не я повелеваю, а Господь: жене не разводиться с мужем, &mdash;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38031, 'rst66', '1co', 7, 9, 'Но если <i>не могут</i> воздержаться, пусть вступают в брак; ибо лучше вступить в брак, нежели разжигаться.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38030, 'rst66', '1co', 7, 8, 'Безбрачным же и вдовам говорю: хорошо им оставаться как я.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38029, 'rst66', '1co', 7, 7, 'Ибо желаю, чтобы все люди были как и я; но каждый имеет свое дарование от Бога, один так, другой иначе.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38028, 'rst66', '1co', 7, 6, 'Впрочем, это сказано мною как позволение, а не как повеление.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38027, 'rst66', '1co', 7, 5, 'Не уклоняйтесь друг от друга, разве по согласию, на время, для упражнения в посте и молитве, а <i>потом</i> опять будьте вместе, чтобы не искушал вас сатана невоздержанием вашим.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38026, 'rst66', '1co', 7, 4, 'Жена не властна над своим телом, но муж; равно и муж не властен над своим телом, но жена.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38025, 'rst66', '1co', 7, 3, 'Муж оказывай жене должное благорасположение; подобно и жена &mdash; мужу.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38024, 'rst66', '1co', 7, 2, 'Но, <i>во избежание</i> блуда, каждый имей свою жену, и каждая имей своего мужа.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38023, 'rst66', '1co', 7, 1, 'А о чем вы писали ко мне, то хорошо человеку не касаться женщины.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38022, 'rst66', '1co', 6, 20, 'Ибо вы куплены <i>дорогою</i> ценою. Посему прославляйте Бога и в телах ваших и в душах ваших, которые суть Божии.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38021, 'rst66', '1co', 6, 19, 'Не знаете ли, что тела&#769; ваши суть храм живущего в вас Святого Духа, Которого имеете вы от Бога, и вы не свои?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38020, 'rst66', '1co', 6, 18, 'Бегайте блуда; всякий грех, какой делает человек, есть вне тела, а блудник грешит против собственного тела.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38019, 'rst66', '1co', 6, 17, 'А соединяющийся с Господом есть один дух с Господом.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38018, 'rst66', '1co', 6, 16, 'Или не знаете, что совокупляющийся с блудницею становится одно тело <i>с нею?</i> ибо сказано: «два будут одна плоть».');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38017, 'rst66', '1co', 6, 15, 'Разве не знаете, что тела&#769; ваши суть члены Христовы? Итак, отниму ли члены у Христа, чтобы сделать <i>их</i> членами блудницы? Да не будет!');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38016, 'rst66', '1co', 6, 14, 'Бог воскресил Господа, воскресит и нас силою Своею.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38015, 'rst66', '1co', 6, 13, 'Пища для чрева, и чрево для пищи; но Бог уничтожит и то и другое. Тело же не для блуда, но для Господа, и Господь для тела.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38014, 'rst66', '1co', 6, 12, 'Все мне позволительно, но не все полезно; все мне позволительно, но ничто не должно обладать мною.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38013, 'rst66', '1co', 6, 11, 'И такими были некоторые из вас; но омылись, но освятились, но оправдались именем Господа нашего Иисуса Христа и Духом Бога нашего.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38012, 'rst66', '1co', 6, 10, 'ни воры, ни лихоимцы, ни пьяницы, ни злоречивые, ни хищники &mdash; Царства Божия не наследуют.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38011, 'rst66', '1co', 6, 9, 'Или не знаете, что неправедные Царства Божия не наследуют? Не обманывайтесь: ни блудники, ни идолослужители, ни прелюбодеи, ни малакии, ни мужеложники,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38010, 'rst66', '1co', 6, 8, 'Но вы <i>сами</i> обижаете и отнимаете, и притом у братьев.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38009, 'rst66', '1co', 6, 7, 'И то уже весьма унизительно для вас, что вы имеете тяжбы между собою. Для чего бы вам лучше не оставаться обиженными? для чего бы вам лучше не терпеть лишения?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38008, 'rst66', '1co', 6, 6, 'Но брат с братом судится, и притом перед неверными.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38007, 'rst66', '1co', 6, 5, 'К стыду вашему говорю: неужели нет между вами ни одного разумного, который мог бы рассудить между братьями своими?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38006, 'rst66', '1co', 6, 4, 'А вы, когда имеете житейские тяжбы, поставляете <i>своими судьями</i> ничего не значащих в церкви.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38005, 'rst66', '1co', 6, 3, 'Разве не знаете, что мы будем судить ангелов, не тем ли более <i>дела</i> житейские?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38004, 'rst66', '1co', 6, 2, 'Разве не знаете, что святые будут судить мир? Если же вами будет судим мир, то неужели вы недостойны судить маловажные <i>дела?</i>');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38003, 'rst66', '1co', 6, 1, 'Как смеет кто у вас, имея дело с другим, судиться у нечестивых, а не у святых?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38002, 'rst66', '1co', 5, 13, 'Внешних же судит Бог. Итак, извергните развращенного из среды вас.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38001, 'rst66', '1co', 5, 12, 'Ибо что мне судить и внешних? Не внутренних ли вы судите?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (38000, 'rst66', '1co', 5, 11, 'но я писал вам не сообщаться с тем, кто, называясь братом, остается блудником, или лихоимцем, или идолослужителем, или злоречивым, или пьяницею, или хищником; с таким даже и не есть вместе.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37999, 'rst66', '1co', 5, 10, 'впрочем, не вообще с блудниками мира сего, или лихоимцами, или хищниками, или идолослужителями, ибо иначе надлежало бы вам выйти из мира <i>сего</i>,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37998, 'rst66', '1co', 5, 9, 'Я писал вам в послании &mdash; не сообщаться с блудниками;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37997, 'rst66', '1co', 5, 8, 'Посему станем праздновать не со старою закваскою, не с закваскою порока и лукавства, но с опресноками чистоты и истины.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37996, 'rst66', '1co', 5, 7, 'Итак, очистите старую закваску, чтобы быть вам новым тестом, так как вы бесквасны, ибо Пасха наша, Христос, заклан за нас.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37995, 'rst66', '1co', 5, 6, 'Нечем вам хвалиться. Разве не знаете, что малая закваска квасит все тесто?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37994, 'rst66', '1co', 5, 5, 'предать сатане во измождение плоти, чтобы дух был спасен в день Господа нашего Иисуса Христа.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37993, 'rst66', '1co', 5, 4, 'в собрании вашем во имя Господа нашего Иисуса Христа, обще с моим духом, силою Господа нашего Иисуса Христа,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37992, 'rst66', '1co', 5, 3, 'А я, отсутствуя телом, но присутствуя <i>у вас</i> духом, уже решил, как бы находясь у вас: сделавшего такое дело,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37991, 'rst66', '1co', 5, 2, 'И вы возгордились, вместо того, чтобы лучше плакать, дабы изъят был из среды вас сделавший такое дело.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37990, 'rst66', '1co', 5, 1, 'Есть верный слух, что у вас <i>появилось</i> блудодеяние, и притом такое блудодеяние, какого не слышно даже у язычников, что некто <i>вместо жены</i> имеет жену отца своего.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37989, 'rst66', '1co', 4, 21, 'Чего вы хотите? с жезлом прийти к вам или с любовью и духом кротости?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37988, 'rst66', '1co', 4, 20, 'ибо Царство Божие не в слове, а в силе.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37987, 'rst66', '1co', 4, 19, 'но я скоро приду к вам, если угодно будет Господу, и испытаю не слова возгордившихся, а силу,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37986, 'rst66', '1co', 4, 18, 'Как я не иду к вам, то некоторые <i>у вас</i> возгордились;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37985, 'rst66', '1co', 4, 17, 'Для сего я послал к вам Тимофея, моего возлюбленного и верного в Господе сына, который напомнит вам о путях моих во Христе, как я учу везде, во всякой церкви.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37984, 'rst66', '1co', 4, 16, 'Посему умоляю вас: подражайте мне, как я Христу.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37983, 'rst66', '1co', 4, 15, 'Ибо, хотя у вас тысячи наставников во Христе, но не много отцов; я родил вас во Христе Иисусе благовествованием.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37982, 'rst66', '1co', 4, 14, 'Не к постыжению вашему пишу сие, но вразумляю вас, как возлюбленных детей моих.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37981, 'rst66', '1co', 4, 13, 'хулят нас, мы молим; мы как сор для мира, <i>как</i> прах, всеми <i>попираемый</i> доныне.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37980, 'rst66', '1co', 4, 12, 'и трудимся, работая своими руками. Злословят нас, мы благословляем; гонят нас, мы терпим;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37979, 'rst66', '1co', 4, 11, 'Даже доныне терпим голод и жажду, и наготу и побои, и скитаемся,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37978, 'rst66', '1co', 4, 10, 'Мы безумны Христа ради, а вы мудры во Христе; мы немощны, а вы крепки; вы в славе, а мы в бесчестии.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37977, 'rst66', '1co', 4, 9, 'Ибо я думаю, что нам, последним посланникам, Бог судил быть как бы приговоренными к смерти, потому что мы сделались позорищем для мира, для Ангелов и человеков.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37976, 'rst66', '1co', 4, 8, 'Вы уже пресытились, вы уже обогатились, вы стали царствовать без нас. О, если бы вы <i>и в самом деле</i> царствовали, чтобы и нам с вами царствовать!');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37975, 'rst66', '1co', 4, 7, 'Ибо кто отличает тебя? Что ты имеешь, чего бы не получил? А если получил, что хвалишься, как будто не получил?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37974, 'rst66', '1co', 4, 6, 'Это, братия, приложил я к себе и Аполлосу ради вас, чтобы вы научились от нас не мудрствовать сверх того, что написано, и не превозносились один перед другим.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37973, 'rst66', '1co', 4, 5, 'Посему не суди&#769;те никак прежде времени, пока не придет Господь, Который и осветит скрытое во мраке, и обнаружит сердечные намерения, и тогда каждому будет похвала от Бога.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37972, 'rst66', '1co', 4, 4, 'Ибо <i>хотя</i> я ничего не знаю за собою, но тем не оправдываюсь; судия же мне &mdash; Господь.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37971, 'rst66', '1co', 4, 3, 'Для меня очень мало значит, как судите обо мне вы или <i>как судят</i> другие люди; я и сам не сужу о себе.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37970, 'rst66', '1co', 4, 2, 'От домостроителей же требуется, чтобы каждый оказался верным.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37969, 'rst66', '1co', 4, 1, 'Итак, каждый должен разуметь нас, как служителей Христовых и домостроителей таин Божиих.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37968, 'rst66', '1co', 3, 23, 'вы же &mdash; Христовы, а Христос &mdash; Божий.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37967, 'rst66', '1co', 3, 22, 'Павел ли, или Аполлос, или Кифа, или мир, или жизнь, или смерть, или настоящее, или будущее, &mdash; все ваше;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37966, 'rst66', '1co', 3, 21, 'Итак, никто не хвались человеками, ибо все ваше:');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37965, 'rst66', '1co', 3, 20, 'И еще: «Господь знает умствования мудрецов, что они суетны».');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37964, 'rst66', '1co', 3, 19, 'Ибо мудрость мира сего есть безумие пред Богом, как написано: «уловляет мудрых в лукавстве их».');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37963, 'rst66', '1co', 3, 18, 'Никто не обольщай самого себя. Если кто из вас думает быть мудрым в веке сем, тот будь безумным, чтобы быть мудрым.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37962, 'rst66', '1co', 3, 17, 'Если кто разорит храм Божий, того покарает Бог: ибо храм Божий свят; а этот <i>храм</i> &mdash; вы.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37961, 'rst66', '1co', 3, 16, 'Разве не знаете, что вы &mdash; храм Божий, и Дух Божий живет в вас?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37960, 'rst66', '1co', 3, 15, 'А у кого дело сгорит, тот потерпит урон; впрочем, сам спасется, но так, как бы из огня.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37959, 'rst66', '1co', 3, 14, 'У кого дело, которое он строил, устоит, тот получит награду.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37958, 'rst66', '1co', 3, 13, 'каждого дело обнаружится; ибо день покажет, потому что в огне открывается, и огонь испытает дело каждого, каково оно есть.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37957, 'rst66', '1co', 3, 12, 'Строит ли кто на этом основании из золота, серебра, драгоценных камней, дерева, сена, соломы, &mdash;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37956, 'rst66', '1co', 3, 11, 'Ибо никто не может положить другого основания, кроме положенного, которое есть Иисус Христос.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37955, 'rst66', '1co', 3, 10, 'Я, по данной мне от Бога благодати, как мудрый строитель, положил основание, а другой строит на <i>нем;</i> но каждый смотри, как строит.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37954, 'rst66', '1co', 3, 9, 'Ибо мы соработники у Бога, <i>а</i> вы Божия нива, Божие строение.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37953, 'rst66', '1co', 3, 8, 'Насаждающий же и поливающий суть одно; но каждый получит свою награду по своему труду.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37952, 'rst66', '1co', 3, 7, 'посему и насаждающий и поливающий есть ничто, а <i>все</i> Бог возращающий.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37951, 'rst66', '1co', 3, 6, 'Я насадил, Аполлос поливал, но возрастил Бог;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37950, 'rst66', '1co', 3, 5, 'Кто Павел? кто Аполлос? Они только служители, через которых вы уверовали, и притом поскольку каждому дал Господь.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37949, 'rst66', '1co', 3, 4, 'Ибо когда один говорит: «я Павлов», а другой: «я Аполлосов», то не плотские ли вы?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37948, 'rst66', '1co', 3, 3, 'потому что вы еще плотские. Ибо если между вами зависть, споры и разногласия, то не плотские ли вы? и не по человеческому ли <i>обычаю</i> поступаете?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37947, 'rst66', '1co', 3, 2, 'Я питал вас молоком, а не <i>твердою</i> пищею, ибо вы были еще не в силах, да и теперь не в силах,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37946, 'rst66', '1co', 3, 1, 'И я не мог говорить с вами, братия, как с духовными, но как с плотскими, как с младенцами во Христе.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37945, 'rst66', '1co', 2, 16, 'Ибо кто познал ум Господень, чтобы <i>мог</i> судить его? А мы имеем ум Христов.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37944, 'rst66', '1co', 2, 15, 'Но духовный судит о всем, а о нем судить никто не может.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37943, 'rst66', '1co', 2, 14, 'Душевный человек не принимает того, что от Духа Божия, потому что он почитает это безумием, и не может разуметь, потому что о сем <i>надобно</i> судить духовно.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37942, 'rst66', '1co', 2, 13, 'что и возвещаем не от человеческой мудрости изученными словами, но изученными от Духа Святого, соображая духовное с духовным.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37941, 'rst66', '1co', 2, 12, 'Но мы приняли не духа мира сего, а Духа от Бога, дабы знать дарованное нам от Бога,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37940, 'rst66', '1co', 2, 11, 'Ибо кто из человеков знает, что в человеке, кроме духа человеческого, живущего в нем? Так и Божьего никто не знает, кроме Духа Божия.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37939, 'rst66', '1co', 2, 10, 'А нам Бог открыл <i>это</i> Духом Своим; ибо Дух все проницает, и глубины Божии.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37938, 'rst66', '1co', 2, 9, 'Но, как написано: «не видел того глаз, не слышало ухо, и не приходило то на сердце человеку, что приготовил Бог любящим Его».');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37937, 'rst66', '1co', 2, 8, 'которой никто из властей века сего не познал; ибо если бы познали, то не распяли бы Господа славы.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37936, 'rst66', '1co', 2, 7, 'но проповедуем премудрость Божию, тайную, сокровенную, которую предназначил Бог прежде веков к славе нашей,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37935, 'rst66', '1co', 2, 6, 'Мудрость же мы проповедуем между совершенными, но мудрость не века сего и не властей века сего преходящих,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37934, 'rst66', '1co', 2, 5, 'чтобы вера ваша <i>утверждалась</i> не на мудрости человеческой, но на силе Божией.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37933, 'rst66', '1co', 2, 4, 'И слово мое и проповедь моя не в убедительных словах человеческой мудрости, но в явлении духа и силы,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37932, 'rst66', '1co', 2, 3, 'и был я у вас в немощи и в страхе и в великом трепете.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37931, 'rst66', '1co', 2, 2, 'ибо я рассудил быть у вас не знающим ничего, кроме Иисуса Христа, и притом распятого,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37930, 'rst66', '1co', 2, 1, 'И когда я приходил к вам, братия, приходил возвещать вам свидетельство Божие не в превосходстве слова или мудрости,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37929, 'rst66', '1co', 1, 31, 'чтобы <i>было</i> как написано: «хвалящийся хвались Господом».');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37928, 'rst66', '1co', 1, 30, 'От Него и вы во Христе Иисусе, Который сделался для нас премудростью от Бога, праведностью и освящением и искуплением,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37927, 'rst66', '1co', 1, 29, 'для того, чтобы никакая плоть не хвалилась пред Богом.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37926, 'rst66', '1co', 1, 28, 'и незнатное мира и уничиженное и ничего не значащее избрал Бог, чтобы упразднить значащее, &mdash;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37925, 'rst66', '1co', 1, 27, 'но Бог избрал немудрое мира, чтобы посрамить мудрых, и немощное мира избрал Бог, чтобы посрамить сильное;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37924, 'rst66', '1co', 1, 26, 'Посмотрите, братия, кто вы, призванные: не много <i>из вас</i> мудрых по плоти, не много сильных, не много благородных;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37923, 'rst66', '1co', 1, 25, 'потому что немудрое Божие премудрее человеков, и немощное Божие сильнее человеков.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37922, 'rst66', '1co', 1, 24, 'для самих же призванных, Иудеев и Еллинов, &mdash; Христа, Божию силу и Божию премудрость;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37921, 'rst66', '1co', 1, 23, 'а мы проповедуем Христа распятого, для Иудеев &mdash; соблазн, а для Еллинов &mdash; безумие,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37920, 'rst66', '1co', 1, 22, 'Ибо и Иудеи требуют чудес, и Еллины ищут мудрости;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37919, 'rst66', '1co', 1, 21, 'Ибо когда мир <i>своею</i> мудростью не познал Бога в премудрости Божией, то благоугодно было Богу юродством проповеди спасти верующих.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37918, 'rst66', '1co', 1, 20, 'Где мудрец? где книжник? где совопросник века сего? Не обратил ли Бог мудрость мира сего в безумие?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37917, 'rst66', '1co', 1, 19, 'Ибо написано: «погублю мудрость мудрецов, и разум разумных отвергну».');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37916, 'rst66', '1co', 1, 18, 'Ибо слово о кресте для погибающих юродство есть, а для нас, спасаемых, &mdash; сила Божия.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37915, 'rst66', '1co', 1, 17, 'Ибо Христос послал меня не крестить, а благовествовать, не в премудрости слова, чтобы не упразднить креста Христова.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37914, 'rst66', '1co', 1, 16, 'Крестил я также Стефанов дом; а крестил ли еще кого, не знаю.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37913, 'rst66', '1co', 1, 15, 'дабы не сказал кто, что я крестил в мое имя.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37912, 'rst66', '1co', 1, 14, 'Благодарю Бога, что я никого из вас не крестил, кроме Криспа и Гаия,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37911, 'rst66', '1co', 1, 13, 'Разве разделился Христос? разве Павел распялся за вас? или во имя Павла вы крестились?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37910, 'rst66', '1co', 1, 12, 'Я разумею то, что у вас говорят: «я Павлов»; «я Аполлосов»; «я Кифин»; «а я Христов».');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37909, 'rst66', '1co', 1, 11, 'Ибо от <i>домашних</i> Хлоиных сделалось мне известным о вас, братия мои, что между вами есть споры.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37908, 'rst66', '1co', 1, 10, 'Умоляю вас, братия, именем Господа нашего Иисуса Христа, чтобы все вы говорили одно и не было между вами разделений, но чтобы вы соединены были в одном духе и в одних мыслях.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37907, 'rst66', '1co', 1, 9, 'Верен Бог, Которым вы призваны в общение Сына Его Иисуса Христа, Господа нашего.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37906, 'rst66', '1co', 1, 8, 'Который и утвердит вас до конца, <i>чтобы вам быть</i> неповинными в день Господа нашего Иисуса Христа.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37905, 'rst66', '1co', 1, 7, 'так что вы не имеете недостатка ни в каком даровании, ожидая явления Господа нашего Иисуса Христа,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37904, 'rst66', '1co', 1, 6, 'ибо свидетельство Христово утвердилось в вас, &mdash;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37903, 'rst66', '1co', 1, 5, 'потому что в Нем вы обогатились всем, всяким словом и всяким познанием, &mdash;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37902, 'rst66', '1co', 1, 4, 'Непрестанно благодарю Бога моего за вас, ради благодати Божией, дарованной вам во Христе Иисусе,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37901, 'rst66', '1co', 1, 3, 'благодать вам и мир от Бога Отца нашего и Господа Иисуса Христа.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37900, 'rst66', '1co', 1, 2, 'Церкви Божией, находящейся в Коринфе, освященным во Христе Иисусе, призванным святым, со всеми призывающими имя Господа нашего Иисуса Христа, во всяком месте, у них и у нас:');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37899, 'rst66', '1co', 1, 1, 'Павел, волею Божиею призванный Апостол Иисуса Христа, и Сосфен брат &mdash;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37898, 'rst66', 'rom', 16, 24, 'Благодать Господа нашего Иисуса Христа со всеми вами. Аминь.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37897, 'rst66', 'rom', 16, 23, 'Приветствует вас Гаий, странноприимец мой и всей церкви. Приветствует вас Ераст, городской казнохранитель, и брат Кварт.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37896, 'rst66', 'rom', 16, 22, 'Приветствую вас в Господе <i>и</i> я, Тертий, писавший сие послание.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37895, 'rst66', 'rom', 16, 21, 'Приветствуют вас Тимофей, сотрудник мой, и Луций, Иасон и Сосипатр, сродники мои.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37894, 'rst66', 'rom', 16, 20, 'Бог же мира сокрушит сатану под ногами вашими вскоре. Благодать Господа нашего Иисуса Христа с вами! Аминь.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37893, 'rst66', 'rom', 16, 19, 'Ваша покорность <i>вере</i> всем известна; посему я радуюсь за вас, но желаю, чтобы вы были мудры на добро и просты на зло.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37892, 'rst66', 'rom', 16, 18, 'ибо такие <i>люди</i> служат не Господу нашему Иисусу Христу, а своему чреву, и ласкательством и красноречием обольщают сердца простодушных.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37891, 'rst66', 'rom', 16, 17, 'Умоляю вас, братия, остерегайтесь производящих разделения и соблазны, вопреки учению, которому вы научились, и уклоняйтесь от них;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37890, 'rst66', 'rom', 16, 16, 'Приветствуйте друг друга с целованием святым. Приветствуют вас все церкви Христовы.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37889, 'rst66', 'rom', 16, 15, 'Приветствуйте Филолога и Юлию, Нирея и сестру его, и Олимпана, и всех с ними святых.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37888, 'rst66', 'rom', 16, 14, 'Приветствуйте Асинкрита, Флегонта, Ерма, Патрова, Ермия и других с ними братьев.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37887, 'rst66', 'rom', 16, 13, 'Приветствуйте Руфа, избранного в Господе, и матерь его и мою.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37886, 'rst66', 'rom', 16, 12, 'Приветствуйте Трифену и Трифосу, трудящихся о Господе. Приветствуйте Персиду возлюбленную, которая много потрудилась о Господе.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37885, 'rst66', 'rom', 16, 11, 'Приветствуйте Иродиона, сродника моего. Приветствуйте из домашних Наркисса тех, которые в Господе.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37884, 'rst66', 'rom', 16, 10, 'Приветствуйте Апеллеса, испытанного во Христе. Приветствуйте <i>верных</i> из дома Аристовулова.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37883, 'rst66', 'rom', 16, 9, 'Приветствуйте Урбана, сотрудника нашего во Христе, и Стахия, возлюбленного мне.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37882, 'rst66', 'rom', 16, 8, 'Приветствуйте Амплия, возлюбленного мне в Господе.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37881, 'rst66', 'rom', 16, 7, 'Приветствуйте Андроника и Юнию, сродников моих и узников со мною, прославившихся между Апостолами и прежде меня еще уверовавших во Христа.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37880, 'rst66', 'rom', 16, 6, 'Приветствуйте Мариам, которая много трудилась для нас.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37879, 'rst66', 'rom', 16, 5, 'Приветствуйте возлюбленного моего Епенета, который есть начаток Ахаии для Христа.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37878, 'rst66', 'rom', 16, 4, 'которые голову свою полагали за мою душу, которых не я один благодарю, но и все церкви из язычников, &mdash; и домашнюю их церковь.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37877, 'rst66', 'rom', 16, 3, 'Приветствуйте Прискиллу и Акилу, сотрудников моих во Христе Иисусе, &mdash;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37876, 'rst66', 'rom', 16, 2, 'Примите ее для Господа, как прилично святым, и помогите ей, в чем она будет иметь нужду у вас, ибо и она была помощницею многим и мне самому.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37875, 'rst66', 'rom', 16, 1, 'Представляю вам Фиву, сестру нашу, диакониссу церкви Кенхрейской.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37874, 'rst66', 'rom', 15, 33, 'Бог же мира да будет со всеми вами, аминь.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37873, 'rst66', 'rom', 15, 32, 'дабы мне в радости, если Богу угодно, прийти к вам и успокоиться с вами.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37872, 'rst66', 'rom', 15, 31, 'чтобы избавиться мне от неверующих в Иудее и чтобы служение мое для Иерусалима было благоприятно святым,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37871, 'rst66', 'rom', 15, 30, 'Между тем умоляю вас, братия, Господом нашим Иисусом Христом и любовью Духа, подвизаться со мною в молитвах за меня к Богу,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37870, 'rst66', 'rom', 15, 29, 'и уверен, что когда приду к вам, то приду с полным благословением благовествования Христова.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37869, 'rst66', 'rom', 15, 28, 'Исполнив это и верно доставив им сей плод <i>усердия</i>, я отправлюсь через ваши <i>места</i> в Испанию,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37868, 'rst66', 'rom', 15, 27, 'Усердствуют, да и должники они перед ними. Ибо если язычники сделались участниками в их духовном, то должны и им послужить в телесном.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37867, 'rst66', 'rom', 15, 26, 'ибо Македония и Ахаия усердствуют некоторым подаянием для бедных между святыми в Иерусалиме.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37866, 'rst66', 'rom', 15, 25, 'А теперь я иду в Иерусалим, чтобы послужить святым,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37865, 'rst66', 'rom', 15, 24, 'как только предприму путь в Испанию, приду к вам. Ибо надеюсь, что, проходя, увижусь с вами и что вы проводите меня туда, как скоро наслажусь <i>общением</i> с вами, хотя отчасти.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37864, 'rst66', 'rom', 15, 23, 'Ныне же, не имея <i>такого</i> места в сих странах, а с давних лет имея желание прийти к вам,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37863, 'rst66', 'rom', 15, 22, 'Сие‐то много раз и препятствовало мне прийти к вам.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37862, 'rst66', 'rom', 15, 21, 'но, как написано: «не имевшие о Нем известия увидят, и не слышавшие узна&#769;ют».');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37861, 'rst66', 'rom', 15, 20, 'Притом я старался благовествовать не там, где <i>уже</i> было известно имя Христово, дабы не созидать на чужом основании,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37860, 'rst66', 'rom', 15, 19, 'силою знамений и чудес, силою Духа Божия, так что благовествование Христово распространено мною от Иерусалима и окрестности до Иллирика.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37859, 'rst66', 'rom', 15, 18, 'ибо не осмелюсь сказать что‐нибудь такое, чего не совершил Христос через меня, в покорении язычников <i>вере</i>, словом и делом,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37858, 'rst66', 'rom', 15, 17, 'Итак, я могу похвалиться в Иисусе Христе в том, что <i>относится</i> к Богу,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37857, 'rst66', 'rom', 15, 16, 'быть служителем Иисуса Христа у язычников и <i>совершать</i> священнодействие благовествования Божия, дабы сие приношение язычников, будучи освящено Духом Святым, было благоприятно <i>Богу</i>.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37856, 'rst66', 'rom', 15, 15, 'но писал вам, братия, с некоторою смелостью, отчасти как бы в напоминание вам, по данной мне от Бога благодати');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37855, 'rst66', 'rom', 15, 14, 'И сам я уверен о вас, братия мои, что и вы полны благости, исполнены всякого познания и можете наставлять друг друга;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37854, 'rst66', 'rom', 15, 13, 'Бог же надежды да исполнит вас всякой радости и мира в вере, дабы вы, силою Духа Святого, обогатились надеждою.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37853, 'rst66', 'rom', 15, 12, 'Исаия также говорит: «будет корень Иессеев, и восстанет владеть народами; на Него язычники надеяться будут».');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37852, 'rst66', 'rom', 15, 11, 'И еще: «хвали&#769;те Господа, все язычники, и прославляйте Его, все народы».');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37851, 'rst66', 'rom', 15, 10, 'И еще сказано: «возвеселитесь, язычники, с народом Его».');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37850, 'rst66', 'rom', 15, 9, 'а для язычников &mdash; из милости, чтобы славили Бога, как написано: «за то буду славить Тебя [Господи] между язычниками и буду петь имени Твоему».');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37849, 'rst66', 'rom', 15, 8, 'Разумею то, что Иисус Христос сделался служителем для обрезанных &mdash; ради истины Божией, чтобы исполнить обещанное отцам,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37848, 'rst66', 'rom', 15, 7, 'Посему принимайте друг друга, как и Христос принял вас в славу Божию.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37847, 'rst66', 'rom', 15, 6, 'дабы вы единодушно, едиными устами славили Бога и Отца Господа нашего Иисуса Христа.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37846, 'rst66', 'rom', 15, 5, 'Бог же терпения и утешения да дарует вам быть в единомыслии между собою, по <i>учению</i> Христа Иисуса,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37845, 'rst66', 'rom', 15, 4, 'А все, что писано было прежде, написано нам в наставление, чтобы мы терпением и утешением из Писаний сохраняли надежду.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37844, 'rst66', 'rom', 15, 3, 'Ибо и Христос не Себе угождал, но, как написано: «злословия злословящих Тебя пали на Меня».');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37843, 'rst66', 'rom', 15, 2, 'Каждый из нас должен угождать ближнему, во благо, к назиданию.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37842, 'rst66', 'rom', 15, 1, 'Мы, сильные, должны сносить немощи бессильных и не себе угождать.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37841, 'rst66', 'rom', 14, 26, 'Единому Премудрому Богу, через Иисуса Христа, слава вовеки. Аминь.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37840, 'rst66', 'rom', 14, 25, 'но которая ныне явлена, и через писания пророческие, по повелению вечного Бога, возвещена всем народам для покорения их вере,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37839, 'rst66', 'rom', 14, 24, 'Могущему же утвердить вас, по благовествованию моему и проповеди Иисуса Христа, по откровению тайны, о которой от вечных времен было умолчано,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37838, 'rst66', 'rom', 14, 23, 'А сомневающийся, если ест, осуждается, потому что не по вере; а все, что не по вере, &mdash; грех.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37837, 'rst66', 'rom', 14, 22, 'Ты имеешь веру? имей ее сам в себе, пред Богом. Блажен, кто не осуждает себя в том, что избирает.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37836, 'rst66', 'rom', 14, 21, 'Лучше не есть мяса, не пить вина и не <i>делать</i> ничего <i>такого</i>, от чего брат твой претыкается, или соблазняется, или изнемогает.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37835, 'rst66', 'rom', 14, 20, 'Ради пищи не разрушай де&#769;ла Божия. Все чисто, но худо человеку, который ест на соблазн.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37834, 'rst66', 'rom', 14, 19, 'Итак, будем искать того, что служит к миру и ко взаимному назиданию.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37833, 'rst66', 'rom', 14, 18, 'Кто сим служит Христу, тот угоден Богу и <i>достоин</i> одобрения от людей.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37832, 'rst66', 'rom', 14, 17, 'Ибо Царствие Божие не пища и питие, но праведность и мир и радость во Святом Духе.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37831, 'rst66', 'rom', 14, 16, 'Да не хулится ваше доброе.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37830, 'rst66', 'rom', 14, 15, 'Если же за пищу огорчается брат твой, то ты уже не по любви поступаешь. Не губи твоею пищею того, за кого Христос умер.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37829, 'rst66', 'rom', 14, 14, 'Я знаю и уверен в Господе Иисусе, что нет ничего в себе самом нечистого; только почитающему что‐либо нечистым, тому нечисто.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37828, 'rst66', 'rom', 14, 13, 'Не станем же более судить друг друга, а лучше суди&#769;те о том, как бы не подавать брату <i>случая к</i> преткновению или соблазну.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37827, 'rst66', 'rom', 14, 12, 'Итак, каждый из нас за себя даст отчет Богу.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37826, 'rst66', 'rom', 14, 11, 'Ибо написано: «живу Я, говорит Господь, предо Мною преклонится всякое колено, и всякий язык будет исповедовать Бога».');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37825, 'rst66', 'rom', 14, 10, 'А ты что&#769; осуждаешь брата твоего? Или и ты, что&#769; унижаешь брата твоего? Все мы предстанем на суд Христов.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37824, 'rst66', 'rom', 14, 9, 'Ибо Христос для того и умер, и воскрес, и ожил, чтобы владычествовать и над мертвыми и над живыми.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37823, 'rst66', 'rom', 14, 8, 'а живем ли &mdash; для Господа живем; умираем ли &mdash; для Господа умираем: и потому, живем ли или умираем, &mdash; <i>всегда</i> Господни.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37822, 'rst66', 'rom', 14, 7, 'Ибо никто из нас не живет для себя, и никто не умирает для себя;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37821, 'rst66', 'rom', 14, 6, 'Кто различает дни &mdash; для Господа различает; и кто не различает дней &mdash; для Господа не различает. Кто ест &mdash; для Господа ест, ибо благодарит Бога; и кто не ест &mdash; для Господа не ест, и благодарит Бога.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37820, 'rst66', 'rom', 14, 5, 'Иной отличает день от дня, а другой судит о всяком дне <i>равно</i>. Всякий <i>поступай</i> по удостоверению своего ума.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37819, 'rst66', 'rom', 14, 4, 'Кто ты, осуждающий чужого раба? Перед своим Господом стои&#769;т он или падает. И будет восставлен, ибо силен Бог восставить его.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37818, 'rst66', 'rom', 14, 3, 'Кто ест, не уничижай того, кто не ест; и кто не ест, не осуждай того, кто ест, потому что Бог принял его.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37817, 'rst66', 'rom', 14, 2, 'Ибо иной уверен, <i>что можно</i> есть все, а немощный ест овощи.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37816, 'rst66', 'rom', 14, 1, 'Немощного в вере принимайте без споров о мнениях.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37815, 'rst66', 'rom', 13, 14, 'но облекитесь в Господа нашего Иисуса Христа, и попечения о плоти не превращайте в похоти.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37814, 'rst66', 'rom', 13, 13, 'Как днем, будем вести себя благочинно, не <i>предаваясь</i> ни пированиям и пьянству, ни сладострастию и распутству, ни ссорам и зависти;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37813, 'rst66', 'rom', 13, 12, 'Ночь прошла, а день приблизился: итак, отвергнем дела тьмы и облечемся в оружия света.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37812, 'rst66', 'rom', 13, 11, 'Так <i>поступайте</i>, зная время, что наступил уже час пробудиться нам от сна. Ибо ныне ближе к нам спасение, нежели когда мы уверовали.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37811, 'rst66', 'rom', 13, 10, 'Любовь не делает ближнему зла; итак, любовь есть исполнение закона.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37810, 'rst66', 'rom', 13, 9, 'Ибо заповеди: «не прелюбодействуй», «не убивай», «не кради», «не лжесвидетельствуй», «не пожелай <i>чужого</i>» и все другие заключаются в сем слове: «люби ближнего твоего, как самого себя».');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37809, 'rst66', 'rom', 13, 8, 'Не оставайтесь должными никому ничем, кроме взаимной любви; ибо любящий другого исполнил закон.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37808, 'rst66', 'rom', 13, 7, 'Итак, отдавайте всякому должное: кому по&#769;дать &mdash; по&#769;дать; кому оброк &mdash; оброк; кому страх &mdash; страх; кому честь &mdash; честь.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37807, 'rst66', 'rom', 13, 6, 'Для сего вы и подати пла&#769;тите, ибо они &mdash; Божии служители, сим самым постоянно занятые.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37806, 'rst66', 'rom', 13, 5, 'И потому надобно повиноваться не только из <i>страха</i> наказания, но и по совести.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37805, 'rst66', 'rom', 13, 4, 'ибо <i>начальник</i> есть Божий слуга, тебе на добро. Если же делаешь зло, бойся, ибо он не напрасно носит меч: он Божий слуга, отмститель в наказание делающему злое.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37804, 'rst66', 'rom', 13, 3, 'Ибо начальствующие страшны не для добрых дел, но для злых. Хочешь ли не бояться власти? Делай добро, и получишь похвалу от нее,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37803, 'rst66', 'rom', 13, 2, 'Посему противящийся власти противится Божию установлению. А противящиеся сами навлекут на себя осуждение.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37802, 'rst66', 'rom', 13, 1, 'Всякая душа да будет покорна высшим властям, ибо нет власти не от Бога; существующие же власти от Бога установлены.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37801, 'rst66', 'rom', 12, 21, 'Не будь побежден злом, но побеждай зло добром.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37800, 'rst66', 'rom', 12, 20, 'Итак, если враг твой голоден, накорми его; если жаждет, напои его: ибо, делая сие, ты соберешь ему на голову горящие уголья.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37799, 'rst66', 'rom', 12, 19, 'Не мстите за себя, возлюбленные, но дайте место гневу <i>Божию</i>. Ибо написано: «Мне отмщение, Я воздам, говорит Господь».');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37798, 'rst66', 'rom', 12, 18, 'Если возможно с вашей стороны, будьте в мире со всеми людьми.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37797, 'rst66', 'rom', 12, 17, 'Никому не воздавайте злом за зло, но пекитесь о добром перед всеми человеками.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37796, 'rst66', 'rom', 12, 16, 'Будьте единомысленны между собою; не высокомудрствуйте, но последуйте смиренным; не мечтайте о себе;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37795, 'rst66', 'rom', 12, 15, 'Радуйтесь с радующимися и плачьте с плачущими.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37794, 'rst66', 'rom', 12, 14, 'Благословляйте гонителей ваших; благословляйте, а не проклинайте.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37793, 'rst66', 'rom', 12, 13, 'в нуждах святых принимайте участие; ревнуйте о странноприимстве.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37792, 'rst66', 'rom', 12, 12, 'утешайтесь надеждою; в скорби <i>будьте</i> терпеливы, в молитве &mdash; постоянны;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37791, 'rst66', 'rom', 12, 11, 'в усердии не ослабевайте; духом пламенейте; Господу служи&#769;те;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37790, 'rst66', 'rom', 12, 10, 'будьте братолюбивы друг к другу с нежностью; в почтительности друг друга предупреждайте;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37789, 'rst66', 'rom', 12, 9, 'Любовь <i>да будет</i> непритворна; отвращайтесь зла, прилепляйтесь к добру;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37788, 'rst66', 'rom', 12, 8, 'увещатель ли &mdash; увещевай; раздаватель ли &mdash; <i>раздавай</i> в простоте; начальник ли &mdash; <i>начальствуй</i> с усердием; благотворитель ли &mdash; <i>благотвори</i> с радушием.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37787, 'rst66', 'rom', 12, 7, '<i>имеешь ли</i> служение &mdash; <i>пребывай</i> в служении; учитель ли &mdash; в учении;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37786, 'rst66', 'rom', 12, 6, 'И как, по данной нам благодати, имеем различные дарования, <i>то</i>, <i>имеешь ли</i> пророчество &mdash; <i>пророчествуй</i> по мере веры;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37785, 'rst66', 'rom', 12, 5, 'так мы, многие, составляем одно тело во Христе, а порознь один для другого члены.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37784, 'rst66', 'rom', 12, 4, 'Ибо как в одном теле у нас много членов, но не у всех членов одно и то же дело,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37783, 'rst66', 'rom', 12, 3, 'По данной мне благодати, всякому из вас говорю: не думайте <i>о себе</i> более, нежели должно думать; но думайте скромно, по мере веры, какую каждому Бог уделил.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37782, 'rst66', 'rom', 12, 2, 'и не сообразуйтесь с веком сим, но преобразуйтесь обновлением ума вашего, чтобы вам познавать, что&#769; есть воля Божия, благая, угодная и совершенная.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37781, 'rst66', 'rom', 12, 1, 'Итак, умоляю вас, братия, милосердием Божиим, представьте тела ваши в жертву живую, святую, благоугодную Богу, <i>для</i> разумного служения вашего,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37780, 'rst66', 'rom', 11, 36, 'Ибо все из Него, Им и к Нему. Ему слава вовеки, аминь.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37779, 'rst66', 'rom', 11, 35, 'Или кто дал Ему наперед, чтобы Он должен был воздать?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37778, 'rst66', 'rom', 11, 34, 'ибо кто познал ум Господень? Или кто был советником Ему?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37777, 'rst66', 'rom', 11, 33, 'О, бездна богатства и премудрости и ведения Божия! Как непостижимы судьбы Его и неисследимы пути Его,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37776, 'rst66', 'rom', 11, 32, 'Ибо всех заключил Бог в непослушание, чтобы всех помиловать.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37775, 'rst66', 'rom', 11, 31, 'так и они теперь непослушны для помилования вас, чтобы и сами они были помилованы.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37774, 'rst66', 'rom', 11, 30, 'Как и вы некогда были непослушны Богу, а ныне помилованы, по непослушанию их,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37773, 'rst66', 'rom', 11, 29, 'Ибо дары и призвание Божие непреложны.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37772, 'rst66', 'rom', 11, 28, 'В отношении к благовестию, они враги ради вас; а в отношении к избранию &mdash; возлюбленные <i>Божии</i> ради отцов.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37771, 'rst66', 'rom', 11, 27, 'И сей завет им от Меня, когда сниму с них грехи их».');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37770, 'rst66', 'rom', 11, 26, 'и так весь Израиль спасется, как написано: «придет от Сиона Избавитель, и отвратит нечестие от Иакова.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37769, 'rst66', 'rom', 11, 25, 'Ибо не хочу оставить вас, братия, в неведении о тайне сей, &mdash; чтобы вы не мечтали о себе, &mdash; что ожесточение произошло в Израиле отчасти, <i>до времени</i>, пока войдет полное <i>число</i> язычников;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37768, 'rst66', 'rom', 11, 24, 'Ибо если ты отсечен от дикой по природе маслины и не по природе привился к хорошей маслине, то тем более сии природные привьются к своей маслине.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37767, 'rst66', 'rom', 11, 23, 'Но и те, если не пребудут в неверии, привьются, потому что Бог силен опять привить их.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37766, 'rst66', 'rom', 11, 22, 'Итак, видишь благость и строгость Божию: строгость к отпадшим, а благость к тебе, если пребудешь в благости <i>Божией</i>; иначе и ты будешь отсечен.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37765, 'rst66', 'rom', 11, 21, 'Ибо если Бог не пощадил природных ветвей, то смотри, пощадит ли и тебя.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37764, 'rst66', 'rom', 11, 20, 'Хорошо. Они отломились неверием, а ты держишься верою: не гордись, но бойся.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37763, 'rst66', 'rom', 11, 19, 'Скажешь: «ветви отломились, чтобы мне привиться».');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37762, 'rst66', 'rom', 11, 18, 'то не превозносись перед ветвями. Если же превозносишься, <i>то вспомни</i>, <i>что</i> не ты корень держишь, но корень тебя.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37761, 'rst66', 'rom', 11, 17, 'Если же некоторые из ветвей отломились, а ты, дикая маслина, привился на место их и стал общником корня и сока маслины,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37760, 'rst66', 'rom', 11, 16, 'Если начаток свят, то и целое; и если корень свят, то и ветви.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37759, 'rst66', 'rom', 11, 15, 'Ибо если отвержение их &mdash; примирение мира, то что&#769; <i>будет</i> принятие, как не жизнь из мертвых?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37758, 'rst66', 'rom', 11, 14, 'Не возбужу ли ревность в <i>сродниках</i> моих по плоти и не спасу ли некоторых из них?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37757, 'rst66', 'rom', 11, 13, 'Вам говорю, язычникам. Как Апостол язычников, я прославляю служение мое.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37756, 'rst66', 'rom', 11, 12, 'Если же падение их &mdash; богатство миру, и оскудение их &mdash; богатство язычникам, то тем более полнота их.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37755, 'rst66', 'rom', 11, 11, 'Итак, спрашиваю: неужели они преткнулись, чтобы <i>совсем</i> пасть? Никак. Но от их падения спасение язычникам, чтобы возбудить в них ревность.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37754, 'rst66', 'rom', 11, 10, 'да помрачатся глаза их, чтобы не видеть, и хребет их да будет согбен навсегда».');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37753, 'rst66', 'rom', 11, 9, 'И Давид говорит: «да будет трапеза их сетью, тенетами и петлею в возмездие им;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37752, 'rst66', 'rom', 11, 8, 'как написано: «Бог дал им дух усыпления, глаза, которыми не видят, и уши, которыми не слышат, даже до сего дня».');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37751, 'rst66', 'rom', 11, 7, 'Что же? Израиль, чего искал, того не получил; избранные же получили, а прочие ожесточились,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37750, 'rst66', 'rom', 11, 6, 'Но если по благодати, то не по делам; иначе благодать не была бы уже благодатью. А если по делам, то это уже не благодать; иначе дело не есть уже дело.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37749, 'rst66', 'rom', 11, 5, 'Так и в нынешнее время, по избранию благодати, сохранился остаток.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37748, 'rst66', 'rom', 11, 4, 'Что же говорит ему Божеский ответ? «Я соблюл Себе семь тысяч человек, которые не преклонили колени перед Ваалом».');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37747, 'rst66', 'rom', 11, 3, '«Господи! пророков Твоих убили, жертвенники Твои разрушили; остался я один, и моей души ищут».');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37746, 'rst66', 'rom', 11, 2, 'Не отверг Бог народа Своего, который Он наперед знал. Или не знаете, что говорит Писание в <i>повествовании об</i> Илии? как он жалуется Богу на Израиля, говоря:');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37745, 'rst66', 'rom', 11, 1, 'Итак, спрашиваю: неужели Бог отверг народ Свой? Никак. Ибо и я Израильтянин, от семени Авраамова, из колена Вениаминова.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37744, 'rst66', 'rom', 10, 21, 'Об Израиле же говорит: «целый день Я простирал руки Мои к народу непослушному и упорному».');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37743, 'rst66', 'rom', 10, 20, 'А Исаия смело говорит: «Меня нашли не искавшие Меня; Я открылся не вопрошавшим о Мне».');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37742, 'rst66', 'rom', 10, 19, 'Еще спрашиваю: разве Израиль не знал? Но первый Моисей говорит: «Я возбужу в вас ревность не народом, раздражу вас народом несмысленным».');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37741, 'rst66', 'rom', 10, 18, 'Но спрашиваю: разве они не слышали? Напротив, «по всей земле прошел голос их, и до пределов вселенной слова&#769; их».');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37740, 'rst66', 'rom', 10, 17, 'Итак, вера &mdash; от слышания, а слышание &mdash; от слова Божия.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37739, 'rst66', 'rom', 10, 16, 'Но не все послушались благовествования. Ибо Исаия говорит: «Господи! кто поверил слышанному от нас?»');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37738, 'rst66', 'rom', 10, 15, 'И как проповедовать, если не будут посланы? как написано: «как прекрасны ноги благовествующих мир, благовествующих благое!»');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37737, 'rst66', 'rom', 10, 14, 'Но как призывать <i>Того</i>, в Кого не уверовали? как веровать <i>в Того</i>, о Ком не слыхали? как слышать без проповедующего?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37736, 'rst66', 'rom', 10, 13, 'Ибо «всякий, кто призовет имя Господне, спасется».');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37735, 'rst66', 'rom', 10, 12, 'Здесь нет различия между Иудеем и Еллином, потому что один Господь у всех, богатый для всех, призывающих Его.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37734, 'rst66', 'rom', 10, 11, 'Ибо Писание говорит: «всякий, верующий в Него, не постыдится».');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37733, 'rst66', 'rom', 10, 10, 'потому что сердцем веруют к праведности, а устами исповедуют ко спасению.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37732, 'rst66', 'rom', 10, 9, 'Ибо если устами твоими будешь исповедовать Иисуса Господом и сердцем твоим веровать, что Бог воскресил Его из мертвых, то спасешься,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37731, 'rst66', 'rom', 10, 8, 'Но что говорит Писание? «Близко к тебе слово, в устах твоих и в сердце твоем», то есть слово веры, которое проповедуем.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37730, 'rst66', 'rom', 10, 7, 'Или: «кто сойдет в бездну?», то есть Христа из мертвых возвести.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37729, 'rst66', 'rom', 10, 6, 'А праведность от веры так говорит: «не говори в сердце твоем: кто взойдет на небо?», то есть Христа свести.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37728, 'rst66', 'rom', 10, 5, 'Моисей пишет о праведности от закона: «исполнивший его человек жив будет им».');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37727, 'rst66', 'rom', 10, 4, 'потому что конец закона &mdash; Христос, к праведности всякого верующего.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37726, 'rst66', 'rom', 10, 3, 'Ибо, не разумея праведности Божией и усиливаясь поставить собственную праведность, они не покорились праведности Божией,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37725, 'rst66', 'rom', 10, 2, 'Ибо свидетельствую им, что имеют ревность по Боге, но не по рассуждению.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37724, 'rst66', 'rom', 10, 1, 'Братия! желание моего сердца и молитва к Богу об Израиле во спасение.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37723, 'rst66', 'rom', 9, 33, 'как написано: «вот, полагаю в Сионе камень преткновения и камень соблазна; но всякий, верующий в Него, не постыдится».');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37722, 'rst66', 'rom', 9, 32, 'Почему? потому что <i>искали</i> не в вере, а в делах закона. Ибо преткнулись о камень преткновения,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37721, 'rst66', 'rom', 9, 31, 'а Израиль, искавший закона праведности, не достиг до закона праведности.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37720, 'rst66', 'rom', 9, 30, 'Что же скажем? Язычники, не искавшие праведности, получили праведность, праведность от веры,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37719, 'rst66', 'rom', 9, 29, 'И, как предсказал Исаия: «если бы Господь Саваоф не оставил нам семени, то мы сделались бы как Содом и были бы подобны Гоморре».');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37718, 'rst66', 'rom', 9, 28, 'ибо дело оканчивает и скоро решит по правде, дело решительное совершит Господь на земле».');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37717, 'rst66', 'rom', 9, 27, 'А Исаия провозглашает об Израиле: «хотя бы сыны Израилевы были числом, как песок морской, <i>только</i> остаток спасется;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37716, 'rst66', 'rom', 9, 26, '«И на том месте, где сказано им: вы не Мой народ, там названы будут сынами Бога живого».');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37715, 'rst66', 'rom', 9, 25, 'Как и у Осии говорит: «не Мой народ назову Моим народом, и не возлюбленную &mdash; возлюбленною».');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37714, 'rst66', 'rom', 9, 24, 'над нами, которых Он призвал не только из Иудеев, но и из язычников?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37713, 'rst66', 'rom', 9, 23, 'дабы вместе явить богатство славы Своей над сосудами милосердия, которые Он приготовил к славе,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37712, 'rst66', 'rom', 9, 22, 'Что же, если Бог, желая показать гнев и явить могущество Свое, с великим долготерпением щадил сосуды гнева, готовые к погибели,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37711, 'rst66', 'rom', 9, 21, 'Не властен ли горшечник над глиною, чтобы из той же смеси сделать один сосуд для почетного <i>употребления</i>, а другой &mdash; для низкого?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37710, 'rst66', 'rom', 9, 20, 'А ты кто, человек, что споришь с Богом? Изделие скажет ли сделавшему его: «зачем ты меня так сделал?»');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37709, 'rst66', 'rom', 9, 19, 'Ты скажешь мне: «за что же еще обвиняет? Ибо кто противостанет воле Его?»');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37708, 'rst66', 'rom', 9, 18, 'Итак, кого хочет, милует; а кого хочет, ожесточает.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37707, 'rst66', 'rom', 9, 17, 'Ибо Писание говорит фараону: «для того самого Я и поставил тебя, чтобы показать над тобою силу Мою и чтобы проповедано было имя Мое по всей земле».');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37706, 'rst66', 'rom', 9, 16, 'Итак, <i>помилование зависит</i> не от желающего и не от подвизающегося, но от Бога милующего.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37705, 'rst66', 'rom', 9, 15, 'Ибо Он говорит Моисею: «кого миловать, помилую; кого жалеть, пожалею».');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37704, 'rst66', 'rom', 9, 14, 'Что же скажем? Неужели неправда у Бога? Никак.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37703, 'rst66', 'rom', 9, 13, 'как и написано: «Иакова Я возлюбил, а Исава возненавидел».');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37702, 'rst66', 'rom', 9, 12, 'не от дел, но от Призывающего, &mdash; сказано было ей: «бо&#769;льший будет в порабощении у меньшего»,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37701, 'rst66', 'rom', 9, 11, 'Ибо, когда они еще не родились и не сделали ничего доброго или худого, &mdash; дабы изволение Божие в избрании происходило');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37700, 'rst66', 'rom', 9, 10, 'И не одно это; но <i>так было</i> и с Ревеккою, когда она зачала в одно время <i>двух сыновей</i> от Исаака, отца нашего.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37699, 'rst66', 'rom', 9, 9, 'А слово обетования таково: «в это же время приду, и у Сарры будет сын».');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37698, 'rst66', 'rom', 9, 8, 'То есть не плотские дети суть дети Божии, но дети обетования признаю&#769;тся за семя.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37697, 'rst66', 'rom', 9, 7, 'и не все дети Авраама, которые от семени его, но сказано: «в Исааке наречется тебе семя».');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37696, 'rst66', 'rom', 9, 6, 'Но не то, чтобы слово Божие не сбылось: ибо не все те Израильтяне, которые от Израиля;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37695, 'rst66', 'rom', 9, 5, 'их и отцы, и от них Христос по плоти, сущий над всем Бог, благословенный вовеки, аминь.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37694, 'rst66', 'rom', 9, 4, 'то есть Израильтян, которым принадлежат усыновление и слава, и заветы, и законоположение, и богослужение, и обетования;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37693, 'rst66', 'rom', 9, 3, 'я желал бы сам быть отлученным от Христа за братьев моих, родных мне по плоти,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37692, 'rst66', 'rom', 9, 2, 'что великая для меня печаль и непрестанное мучение сердцу моему:');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37691, 'rst66', 'rom', 9, 1, 'Истину говорю во Христе, не лгу, свидетельствует мне совесть моя в Духе Святом,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37690, 'rst66', 'rom', 8, 39, 'ни высота, ни глубина, ни другая какая тварь не может отлучить нас от любви Божией во Христе Иисусе, Господе нашем.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37689, 'rst66', 'rom', 8, 38, 'Ибо я уверен, что ни смерть, ни жизнь, ни Ангелы, ни Начала, ни Силы, ни настоящее, ни будущее,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37688, 'rst66', 'rom', 8, 37, 'Но все сие преодолеваем силою Возлюбившего нас.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37687, 'rst66', 'rom', 8, 36, '«за Тебя умерщвляют нас всякий день, считают нас за овец, <i>обреченных</i> на заклание».');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37686, 'rst66', 'rom', 8, 35, 'Кто отлучит нас от любви Божией: скорбь, или теснота, или гонение, или голод, или нагота, или опасность, или меч? как написано:');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37685, 'rst66', 'rom', 8, 34, 'Кто осуждает? Христос Иисус умер, но и воскрес: Он и одесную Бога, Он и ходатайствует за нас.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37684, 'rst66', 'rom', 8, 33, 'Кто будет обвинять избранных Божиих? Бог оправдывает <i>их</i>.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37683, 'rst66', 'rom', 8, 32, 'Тот, Который Сына Своего не пощадил, но предал Его за всех нас, как с Ним не дарует нам и всего?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37682, 'rst66', 'rom', 8, 31, 'Что же сказать на это? Если Бог за нас, кто против нас?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37681, 'rst66', 'rom', 8, 30, 'А кого Он предопределил, тех и призвал, а кого призвал, тех и оправдал; а кого оправдал, тех и прославил.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37680, 'rst66', 'rom', 8, 29, 'Ибо кого Он предузнал, тем и предопределил быть подобными образу Сына Своего, дабы Он был первородным между многими братиями.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37679, 'rst66', 'rom', 8, 28, 'Притом знаем, что любящим Бога, призванным по <i>Его</i> изволению, все содействует ко благу.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37678, 'rst66', 'rom', 8, 27, 'Испытующий же сердца знает, какая мысль у Духа, потому что Он ходатайствует за святых по <i>воле</i> Божией.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37677, 'rst66', 'rom', 8, 26, 'Также и Дух подкрепляет нас в немощах наших; ибо мы не знаем, о чем молиться, как должно, но Сам Дух ходатайствует за нас воздыханиями неизреченными.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37676, 'rst66', 'rom', 8, 25, 'Но когда надеемся того, чего не видим, тогда ожидаем в терпении.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37675, 'rst66', 'rom', 8, 24, 'Ибо мы спасены в надежде. Надежда же, когда видит, не есть надежда; ибо если кто видит, то чего ему и надеяться?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37674, 'rst66', 'rom', 8, 23, 'и не только <i>она</i>, но и мы сами, имея начаток Духа, и мы в себе стенаем, ожидая усыновления, искупления тела нашего.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37673, 'rst66', 'rom', 8, 22, 'Ибо знаем, что вся тварь совокупно стенает и мучится доныне;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37672, 'rst66', 'rom', 8, 21, 'что и сама тварь освобождена будет от рабства тлению в свободу славы детей Божиих.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37671, 'rst66', 'rom', 8, 20, 'потому что тварь покорилась суете не добровольно, но по воле покорившего ее, в надежде,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37670, 'rst66', 'rom', 8, 19, 'Ибо тварь с надеждою ожидает откровения сынов Божиих,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37669, 'rst66', 'rom', 8, 18, 'Ибо думаю, что нынешние временные страдания ничего не стоят в сравнении с тою славою, которая откроется в нас.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37668, 'rst66', 'rom', 8, 17, 'А если дети, то и наследники, наследники Божии, сонаследники же Христу, если только с Ним страдаем, чтобы с Ним и прославиться.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37667, 'rst66', 'rom', 8, 16, 'Сей самый Дух свидетельствует духу нашему, что мы &mdash; дети Божии.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37666, 'rst66', 'rom', 8, 15, 'Потому что вы не приняли духа рабства, <i>чтобы</i> опять <i>жить</i> в страхе, но приняли Духа усыновления, Которым взываем: «Авва, Отче!»');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37665, 'rst66', 'rom', 8, 14, 'Ибо все, водимые Духом Божиим, суть сыны Божии.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37664, 'rst66', 'rom', 8, 13, 'ибо если живете по плоти, то умрете, а если духом умерщвляете дела плотские, то живы будете.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37663, 'rst66', 'rom', 8, 12, 'Итак, братия, мы не должники плоти, чтобы жить по плоти;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37662, 'rst66', 'rom', 8, 11, 'Если же Дух Того, Кто воскресил из мертвых Иисуса, живет в вас, то Воскресивший Христа из мертвых оживит и ваши смертные тела Духом Своим, живущим в вас.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37661, 'rst66', 'rom', 8, 10, 'А если Христос в вас, то тело мертво для греха, но дух жив для праведности.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37660, 'rst66', 'rom', 8, 9, 'Но вы не по плоти живете, а по духу, если только Дух Божий живет в вас. Если же кто Духа Христова не имеет, тот <i>и</i> не Его.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37659, 'rst66', 'rom', 8, 8, 'Посему живущие по плоти Богу угодить не могут.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37658, 'rst66', 'rom', 8, 7, 'потому что плотские помышления суть вражда против Бога; ибо закону Божию не покоряются, да и не могут.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37657, 'rst66', 'rom', 8, 6, 'Помышления плотские суть смерть, а помышления духовные &mdash; жизнь и мир,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37656, 'rst66', 'rom', 8, 5, 'Ибо живущие по плоти о плотском помышляют, а живущие по духу &mdash; о духовном.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37655, 'rst66', 'rom', 8, 4, 'чтобы оправдание закона исполнилось в нас, живущих не по плоти, но по духу.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37654, 'rst66', 'rom', 8, 3, 'Как закон, ослабленный плотию, был бессилен, то Бог послал Сына Своего в подобии плоти греховной <i>в жертву</i> за грех и осудил грех во плоти,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37653, 'rst66', 'rom', 8, 2, 'потому что закон духа жизни во Христе Иисусе освободил меня от закона греха и смерти.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37652, 'rst66', 'rom', 8, 1, 'Итак, нет ныне никакого осуждения тем, которые во Христе Иисусе живут не по плоти, но по духу,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37651, 'rst66', 'rom', 7, 25, 'Благодарю Бога моего Иисусом Христом, Господом нашим. Итак, тот же самый я умом моим служу закону Божию, а плотию &mdash; закону греха.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37650, 'rst66', 'rom', 7, 24, 'Бедный я человек! кто избавит меня от сего тела смерти?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37649, 'rst66', 'rom', 7, 23, 'но в членах моих вижу иной закон, противоборствующий закону ума моего и делающий меня пленником закона греховного, находящегося в членах моих.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37648, 'rst66', 'rom', 7, 22, 'Ибо по внутреннему человеку нахожу удовольствие в законе Божием,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37647, 'rst66', 'rom', 7, 21, 'Итак, я нахожу закон, что, когда хочу делать доброе, прилежит мне злое.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37646, 'rst66', 'rom', 7, 20, 'Если же делаю то, чего не хочу, уже не я делаю то, но живущий во мне грех.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37645, 'rst66', 'rom', 7, 19, 'Доброго, которого хочу, не делаю, а злое, которого не хочу, делаю.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37644, 'rst66', 'rom', 7, 18, 'Ибо знаю, что не живет во мне, то есть в плоти моей, доброе; потому что желание добра есть во мне, но чтобы сделать оное, того не нахожу.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37643, 'rst66', 'rom', 7, 17, 'а потому уже не я делаю то, но живущий во мне грех.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37642, 'rst66', 'rom', 7, 16, 'Если же делаю то, чего не хочу, то соглашаюсь с законом, что он добр,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37641, 'rst66', 'rom', 7, 15, 'Ибо не понимаю, что делаю: потому что не то делаю, что хочу, а что ненавижу, то делаю.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37640, 'rst66', 'rom', 7, 14, 'Ибо мы знаем, что закон духовен, а я плотян, продан греху.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37639, 'rst66', 'rom', 7, 13, 'Итак, неужели доброе сделалось мне смертоносным? Никак; но грех, оказывающийся грехом потому, что посредством доброго причиняет мне смерть, так что грех становится крайне грешен посредством заповеди.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37638, 'rst66', 'rom', 7, 12, 'Посему закон свят, и заповедь свята и праведна и добра.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37637, 'rst66', 'rom', 7, 11, 'потому что грех, взяв повод от заповеди, обольстил меня и умертвил ею.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37636, 'rst66', 'rom', 7, 10, 'а я умер; и таким образом заповедь, <i>данная</i> для жизни, послужила мне к смерти,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37635, 'rst66', 'rom', 7, 9, 'Я жил некогда без закона; но когда пришла заповедь, то грех ожил,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37634, 'rst66', 'rom', 7, 8, 'Но грех, взяв повод от заповеди, произвел во мне всякое пожелание: ибо без закона грех мертв.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37633, 'rst66', 'rom', 7, 7, 'Что же скажем? Неужели <i>от</i> закона грех? Никак. Но я не иначе узнал грех, как посредством закона. Ибо я не понимал бы и пожелания, если бы закон не говорил: «не пожелай».');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37632, 'rst66', 'rom', 7, 6, 'но ныне, умерши для закона, которым были связаны, мы освободились от него, чтобы нам служить Богу в обновлении духа, а не по ветхой букве.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37631, 'rst66', 'rom', 7, 5, 'Ибо, когда мы жили по плоти, тогда страсти греховные, <i>обнаруживаемые</i> законом, действовали в членах наших, чтобы приносить плод смерти;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37630, 'rst66', 'rom', 7, 4, 'Так и вы, братия мои, умерли для закона телом Христовым, чтобы принадлежать другому, Воскресшему из мертвых, да приносим плод Богу.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37629, 'rst66', 'rom', 7, 3, 'Посему, если при живом муже выйдет за другого, называется прелюбодеицею; если же умрет муж, она свободна от закона, и не будет прелюбодеицею, выйдя за другого мужа.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37628, 'rst66', 'rom', 7, 2, 'Замужняя женщина привязана законом к живому мужу; а если умрет муж, она освобождается от закона замужества.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37627, 'rst66', 'rom', 7, 1, 'Разве вы не знаете, братия, &mdash; ибо говорю знающим закон, &mdash; что закон имеет власть над человеком, пока он жив?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37626, 'rst66', 'rom', 6, 23, 'Ибо возмездие за грех &mdash; смерть, а дар Божий &mdash; жизнь вечная во Христе Иисусе, Господе нашем.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37625, 'rst66', 'rom', 6, 22, 'Но ныне, когда вы освободились от греха и стали рабами Богу, плод ваш есть святость, а конец &mdash; жизнь вечная.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37624, 'rst66', 'rom', 6, 21, 'Какой же плод вы имели тогда? <i>Такие дела</i>, каких ныне сами стыдитесь, потому что конец их &mdash; смерть.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37623, 'rst66', 'rom', 6, 20, 'Ибо, когда вы были рабами греха, тогда были свободны от праведности.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37622, 'rst66', 'rom', 6, 19, 'Говорю по <i>рассуждению</i> человеческому, ради немощи плоти вашей. Как предавали вы члены ваши в рабы нечистоте и беззаконию на <i>дела</i> беззаконные, так ныне представьте члены ваши в рабы праведности на <i>дела</i> святые.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37621, 'rst66', 'rom', 6, 18, 'Освободившись же от греха, вы стали рабами праведности.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37620, 'rst66', 'rom', 6, 17, 'Благодарение Богу, что вы, быв прежде рабами греха, от сердца стали послушны тому образу учения, которому предали себя.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37619, 'rst66', 'rom', 6, 16, 'Неужели вы не знаете, что кому вы отдаете себя в рабы для послушания, того вы и рабы, кому повинуетесь, или <i>рабы</i> греха к смерти, или послушания к праведности?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37618, 'rst66', 'rom', 6, 15, 'Что же? станем ли грешить, потому что мы не под законом, а под благодатью? Никак.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37617, 'rst66', 'rom', 6, 14, 'Грех не должен над вами господствовать, ибо вы не под законом, но под благодатью.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37616, 'rst66', 'rom', 6, 13, 'и не предавайте членов ваших греху в орудия неправды, но представьте себя Богу, как оживших из мертвых, и члены ваши Богу в орудия праведности.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37615, 'rst66', 'rom', 6, 12, 'Итак, да не царствует грех в смертном вашем теле, чтобы вам повиноваться ему в похотях его;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37614, 'rst66', 'rom', 6, 11, 'Так и вы почитайте себя мертвыми для греха, живыми же для Бога во Христе Иисусе, Господе нашем.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37613, 'rst66', 'rom', 6, 10, 'Ибо что Он умер, то умер однажды для греха; а что живет, то живет для Бога.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37612, 'rst66', 'rom', 6, 9, 'зная, что Христос, воскреснув из мертвых, уже не умирает: смерть уже не имеет над Ним власти.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37611, 'rst66', 'rom', 6, 8, 'Если же мы умерли со Христом, то веруем, что и жить будем с Ним,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37610, 'rst66', 'rom', 6, 7, 'ибо умерший освободился от греха.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37609, 'rst66', 'rom', 6, 6, 'зная то, что ветхий наш человек распят с Ним, чтобы упразднено было тело греховное, дабы нам не быть уже рабами греху;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37608, 'rst66', 'rom', 6, 5, 'Ибо если мы соединены с Ним подобием смерти Его, то должны быть <i>соединены</i> и <i>подобием</i> воскресения,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37607, 'rst66', 'rom', 6, 4, 'Итак, мы погреблись с Ним крещением в смерть, дабы, как Христос воскрес из мертвых славою Отца, так и нам ходить в обновленной жизни.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37606, 'rst66', 'rom', 6, 3, 'Неужели не знаете, что все мы, крестившиеся во Христа Иисуса, в смерть Его крестились?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37605, 'rst66', 'rom', 6, 2, 'Мы умерли для греха: как же нам жить в нем?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37604, 'rst66', 'rom', 6, 1, 'Что же скажем? оставаться ли нам в грехе, чтобы умножилась благодать? Никак.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37603, 'rst66', 'rom', 5, 21, 'дабы, как грех царствовал к смерти, так и благодать воцарилась через праведность к жизни вечной Иисусом Христом, Господом нашим.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37602, 'rst66', 'rom', 5, 20, 'Закон же пришел после, и таким образом умножилось преступление. А когда умножился грех, стала преизобиловать благодать,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37601, 'rst66', 'rom', 5, 19, 'Ибо как непослушанием одного человека сделались многие грешными, так и послушанием одного сделаются праведными многие.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37600, 'rst66', 'rom', 5, 18, 'Посему как преступлением одного &mdash; всем человекам осуждение, так правдою одного &mdash; всем человекам оправдание к жизни.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37599, 'rst66', 'rom', 5, 17, 'Ибо если преступлением одного смерть царствовала посредством одного, то тем более приемлющие обилие благодати и дар праведности будут царствовать в жизни посредством единого Иисуса Христа.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37598, 'rst66', 'rom', 5, 16, 'И дар не как <i>суд</i> за одного согрешившего; ибо суд за одно <i>преступление</i> &mdash; к осуждению; а дар благодати &mdash; к оправданию от многих преступлений.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37597, 'rst66', 'rom', 5, 15, 'Но дар благодати не как преступление. Ибо если преступлением одного подверглись смерти многие, то тем более благодать Божия и дар по благодати одного Человека, Иисуса Христа, преизбыточествуют для многих.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37596, 'rst66', 'rom', 5, 14, 'Однако же смерть царствовала от Адама до Моисея и над несогрешившими подобно преступлению Адама, который есть образ будущего.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37595, 'rst66', 'rom', 5, 13, 'Ибо <i>и</i> до закона грех был в мире; но грех не вменяется, когда нет закона.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37594, 'rst66', 'rom', 5, 12, 'Посему, как одним человеком грех вошел в мир, и грехом &mdash; смерть, так и смерть перешла во всех человеков, <i>потому что</i> в нем все согрешили.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37593, 'rst66', 'rom', 5, 11, 'И не довольно сего, но и хвалимся Богом чрез Господа нашего Иисуса Христа, посредством Которого мы получили ныне примирение.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37592, 'rst66', 'rom', 5, 10, 'Ибо если, будучи врагами, мы примирились с Богом смертью Сына Его, то тем более, примирившись, спасемся жизнью Его.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37591, 'rst66', 'rom', 5, 9, 'Посему тем более ныне, будучи оправданы Кровию Его, спасемся Им от гнева.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37590, 'rst66', 'rom', 5, 8, 'Но Бог Свою любовь к нам доказывает тем, что Христос умер за нас, когда мы были еще грешниками.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37589, 'rst66', 'rom', 5, 7, 'Ибо едва ли кто умрет за праведника; разве за благодетеля, может быть, кто и решится умереть.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37588, 'rst66', 'rom', 5, 6, 'Ибо Христос, когда еще мы были немощны, в определенное время умер за нечестивых.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37587, 'rst66', 'rom', 5, 5, 'а надежда не постыжает, потому что любовь Божия излилась в сердца наши Духом Святым, данным нам.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37586, 'rst66', 'rom', 5, 4, 'от терпения опытность, от опытности надежда,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37585, 'rst66', 'rom', 5, 3, 'И не сим только, но хвалимся и скорбями, зная, что от скорби происходит терпение,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37584, 'rst66', 'rom', 5, 2, 'через Которого верою и получили мы доступ к той благодати, в которой стоим и хвалимся надеждою славы Божией.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37583, 'rst66', 'rom', 5, 1, 'Итак, оправдавшись верою, мы имеем мир с Богом через Господа нашего Иисуса Христа,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37582, 'rst66', 'rom', 4, 25, 'Который предан за грехи наши и воскрес для оправдания нашего.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37581, 'rst66', 'rom', 4, 24, 'но и в отношении к нам: вменится и нам, верующим в Того, Кто воскресил из мертвых Иисуса Христа, Господа нашего,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37580, 'rst66', 'rom', 4, 23, 'А впрочем, не в отношении к нему одному написано, что вменилось ему,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37579, 'rst66', 'rom', 4, 22, 'Потому и вменилось ему в праведность.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37578, 'rst66', 'rom', 4, 21, 'и будучи вполне уверен, что Он силен и исполнить обещанное.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37577, 'rst66', 'rom', 4, 20, 'не поколебался в обетовании Божием неверием, но пребыл тверд в вере, воздав славу Богу');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37576, 'rst66', 'rom', 4, 19, 'И, не изнемогши в вере, он не помышлял, что тело его, почти столетнего, уже омертвело, и утроба Саррина в омертвении;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37575, 'rst66', 'rom', 4, 18, 'Он, сверх надежды, поверил с надеждою, через что сделался отцом многих народов, по сказанному: «так <i>многочисленно</i> будет семя твое».');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37574, 'rst66', 'rom', 4, 17, 'как написано: «Я поставил тебя отцом многих народов», &mdash; пред Богом, Которому он поверил, животворящим мертвых и называющим несуществующее, как существующее.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37573, 'rst66', 'rom', 4, 16, 'Итак, по вере, чтобы <i>было</i> по милости, дабы обетование было непреложно для всех, не только по закону, но и по вере потомков Авраама, который есть отец всем нам, &mdash;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37572, 'rst66', 'rom', 4, 15, 'ибо закон производит гнев, потому что где нет закона, нет и преступления.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37571, 'rst66', 'rom', 4, 14, 'Если утверждающиеся на законе суть наследники, то тщетна вера, бездейственно обетование;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37570, 'rst66', 'rom', 4, 13, 'Ибо не законом <i>даровано</i> Аврааму, или семени его, обетование &mdash; быть наследником мира, но праведностью веры.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37569, 'rst66', 'rom', 4, 12, 'и отцом обрезанных, не только <i>принявших</i> обрезание, но и ходящих по следам веры отца нашего Авраама, которую <i>имел он</i> в необрезании.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37568, 'rst66', 'rom', 4, 11, 'И знак обрезания он получил, <i>как</i> печать праведности через веру, которую <i>имел</i> в необрезании, так что он стал отцом всех верующих в необрезании, чтобы и им вменилась праведность,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37567, 'rst66', 'rom', 4, 10, 'Когда вменилась? по обрезании или до обрезания? Не по обрезании, а до обрезания.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37566, 'rst66', 'rom', 4, 9, 'Блаженство сие <i>относится</i> к обрезанию или и к необрезанию? Мы говорим, что Аврааму вера вменилась в праведность.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37565, 'rst66', 'rom', 4, 8, 'Блажен человек, которому Господь не вменит греха».');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37564, 'rst66', 'rom', 4, 7, '«блаженны, чьи беззакония прощены и чьи грехи покрыты.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37563, 'rst66', 'rom', 4, 6, 'Так и Давид называет блаженным человека, которому Бог вменяет праведность независимо от дел:');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37562, 'rst66', 'rom', 4, 5, 'А не делающему, но верующему в Того, Кто оправдывает нечестивого, вера его вменяется в праведность.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37561, 'rst66', 'rom', 4, 4, 'Воздаяние делающему вменяется не по милости, но по долгу.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37560, 'rst66', 'rom', 4, 3, 'Ибо что&#769; говорит Писание? «Поверил Авраам Богу, и это вменилось ему в праведность».');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37559, 'rst66', 'rom', 4, 2, 'Если Авраам оправдался делами, он имеет похвалу, но не пред Богом.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37558, 'rst66', 'rom', 4, 1, 'Что&#769; же, скажем, Авраам, отец наш, приобрел по плоти?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37557, 'rst66', 'rom', 3, 31, 'Итак, мы уничтожаем закон верою? Никак; но закон утверждаем.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37556, 'rst66', 'rom', 3, 30, 'потому что один Бог, Который оправдает обрезанных по вере и необрезанных через веру.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37555, 'rst66', 'rom', 3, 29, 'Неужели Бог <i>есть Бог</i> Иудеев только, а не и язычников? Конечно, и язычников,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37554, 'rst66', 'rom', 3, 28, 'Ибо мы признаём, что человек оправдывается верою, независимо от дел закона.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37553, 'rst66', 'rom', 3, 27, 'Где же то&#769;, чем бы хвалиться? уничтожено. Каким законом? <i>законом</i> дел? Нет, но законом веры.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37552, 'rst66', 'rom', 3, 26, 'во <i>время</i> долготерпения Божия, к показанию правды Его в настоящее время, да <i>явится</i> Он праведным и оправдывающим верующего в Иисуса.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37551, 'rst66', 'rom', 3, 25, 'Которого Бог предложил в жертву умилостивления в Крови Его через веру, для показания правды Его в прощении грехов, соделанных прежде,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37550, 'rst66', 'rom', 3, 24, 'получая оправдание даром, по благодати Его, искуплением во Христе Иисусе,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37549, 'rst66', 'rom', 3, 23, 'потому что все согрешили и лишены славы Божией,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37548, 'rst66', 'rom', 3, 22, 'правда Божия через веру в Иисуса Христа во всех и на всех верующих, ибо нет различия,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37547, 'rst66', 'rom', 3, 21, 'Но ныне, независимо от закона, явилась правда Божия, о которой свидетельствуют закон и пророки,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37546, 'rst66', 'rom', 3, 20, 'потому что делами закона не оправдается пред Ним никакая плоть; ибо законом познаётся грех.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37545, 'rst66', 'rom', 3, 19, 'Но мы знаем, что закон, если что говорит, говорит к состоящим под законом, так что заграждаются всякие уста, и весь мир становится виновен пред Богом,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37544, 'rst66', 'rom', 3, 18, '«Нет страха Божия перед глазами их».');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37543, 'rst66', 'rom', 3, 17, 'они не знают пути мира».');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37542, 'rst66', 'rom', 3, 16, 'разрушение и пагуба на путях их;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37541, 'rst66', 'rom', 3, 15, '«Ноги их быстры на пролитие крови;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37540, 'rst66', 'rom', 3, 14, '«Уста их полны злословия и горечи».');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37539, 'rst66', 'rom', 3, 13, '«Гортань их &mdash; открытый гроб; языком своим обманывают; яд аспидов на губах их».');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37538, 'rst66', 'rom', 3, 12, 'все совратились с пути, до одного негодны; нет делающего добро, нет ни одного».');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37537, 'rst66', 'rom', 3, 11, 'нет разумевающего; никто не ищет Бога;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37536, 'rst66', 'rom', 3, 10, 'как написано: «нет праведного ни одного;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37535, 'rst66', 'rom', 3, 9, 'Итак, что же? имеем ли мы преимущество? Нисколько. Ибо мы уже доказали, что как Иудеи, так и Еллины, все под грехом,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37534, 'rst66', 'rom', 3, 8, 'И не делать ли нам зло, чтобы вышло добро, как некоторые злословят нас и говорят, будто мы так учим? Праведен суд на таковых.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37533, 'rst66', 'rom', 3, 7, 'Ибо, если верность Божия возвышается моею неверностью к славе Божией, за что еще меня же судить, как грешника?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37532, 'rst66', 'rom', 3, 6, 'Никак. Ибо <i>иначе</i> как Богу судить мир?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37531, 'rst66', 'rom', 3, 5, 'Если же наша неправда открывает правду Божию, то что скажем? не будет ли Бог несправедлив, когда изъявляет гнев? (говорю по человеческому <i>рассуждению</i>).');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37530, 'rst66', 'rom', 3, 4, 'Никак. Бог верен, а всякий человек лжив, как написано: «Ты праведен в словах Твоих и победишь в суде Твоем».');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37529, 'rst66', 'rom', 3, 3, 'Ибо что же? если некоторые и неверны были, неверность их уничтожит ли верность Божию?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37528, 'rst66', 'rom', 3, 2, 'Великое преимущество во всех отношениях, а наипаче <i>в том</i>, что им вверено слово Божие.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37527, 'rst66', 'rom', 3, 1, 'Итак, какое преимущество <i>быть</i> Иудеем, или какая польза от обрезания?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37526, 'rst66', 'rom', 2, 29, 'но <i>тот</i> Иудей, кто внутренно <i>таков</i>, и <i>то</i> обрезание, <i>которое</i> в сердце, по духу, <i>а</i> не по букве: ему и похвала не от людей, но от Бога.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37525, 'rst66', 'rom', 2, 28, 'Ибо не тот Иудей, кто <i>таков</i> по наружности, и не то обрезание, которое наружно, на плоти;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37524, 'rst66', 'rom', 2, 27, 'И необрезанный по природе, исполняющий закон, не осудит ли тебя, преступника закона при Писании и обрезании?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37523, 'rst66', 'rom', 2, 26, 'Итак, если необрезанный соблюдает постановления закона, то его необрезание не вменится ли ему в обрезание?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37522, 'rst66', 'rom', 2, 25, 'Обрезание полезно, если исполняешь закон; а если ты преступник закона, то обрезание твое стало необрезанием.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37521, 'rst66', 'rom', 2, 24, 'Ибо ради вас, как написано, имя Божие хулится у язычников.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37520, 'rst66', 'rom', 2, 23, 'Хвалишься законом, а преступлением закона бесчестишь Бога?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37519, 'rst66', 'rom', 2, 22, 'Проповедуя не красть, крадешь? говоря: «не прелюбодействуй», прелюбодействуешь? гнушаясь идолов, святотатствуешь?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37518, 'rst66', 'rom', 2, 21, 'как же ты, уча другого, не учишь себя самого?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37517, 'rst66', 'rom', 2, 20, 'наставник невежд, учитель младенцев, имеющий в законе образец ведения и истины:');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37516, 'rst66', 'rom', 2, 19, 'и уверен о себе, что ты путеводитель слепых, свет для находящихся во тьме,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37515, 'rst66', 'rom', 2, 18, 'и знаешь волю <i>Его</i>, и разумеешь лучшее, научаясь из закона,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37514, 'rst66', 'rom', 2, 17, 'Вот, ты называешься Иудеем, и успокаиваешь себя законом, и хвалишься Богом,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37513, 'rst66', 'rom', 2, 16, 'в день, когда, по благовествованию моему, Бог будет судить тайные <i>дела</i> человеков через Иисуса Христа.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37512, 'rst66', 'rom', 2, 15, 'они показывают, что дело закона у них написано в сердцах, о чем свидетельствует совесть их и мысли их, то обвиняющие, то оправдывающие одна другую)');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37511, 'rst66', 'rom', 2, 14, 'ибо когда язычники, не имеющие закона, по природе законное делают, то, не имея закона, они сами себе закон:');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37510, 'rst66', 'rom', 2, 13, '(потому что не слушатели закона праведны пред Богом, но исполнители закона оправданы будут,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37509, 'rst66', 'rom', 2, 12, 'Те, которые, не <i>имея</i> закона, согрешили, вне закона и погибнут; а те, которые под законом согрешили, по закону осудятся, &mdash;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37508, 'rst66', 'rom', 2, 11, 'Ибо нет лицеприятия у Бога.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37507, 'rst66', 'rom', 2, 10, 'Напротив, слава и честь и мир всякому, делающему доброе, во‐первых, Иудею, <i>потом</i> и Еллину!');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37506, 'rst66', 'rom', 2, 9, 'Скорбь и теснота всякой душе человека, делающего злое, во‐первых, Иудея, <i>потом</i> и Еллина!');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37505, 'rst66', 'rom', 2, 8, 'а тем, которые упорствуют и не покоряются истине, но предаются неправде, &mdash; ярость и гнев.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37504, 'rst66', 'rom', 2, 7, 'тем, которые постоянством в добром деле ищут славы, чести и бессмертия, &mdash; жизнь вечную;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37503, 'rst66', 'rom', 2, 6, 'Который воздаст каждому по делам его:');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37502, 'rst66', 'rom', 2, 5, 'Но, по упорству твоему и нераскаянному сердцу, ты сам себе собираешь гнев на день гнева и откровения праведного суда от Бога,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37501, 'rst66', 'rom', 2, 4, 'Или пренебрегаешь богатство благости, кротости и долготерпения Божия, не разумея, что благость Божия ведет тебя к покаянию?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37500, 'rst66', 'rom', 2, 3, 'Неужели думаешь ты, человек, что избежишь суда Божия, осуждая делающих такие <i>дела</i> и сам делая то же?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37499, 'rst66', 'rom', 2, 2, 'А мы знаем, что поистине есть суд Божий на делающих такие <i>дела</i>.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37498, 'rst66', 'rom', 2, 1, 'Итак, неизвинителен ты, всякий человек, судящий <i>другого</i>, ибо тем же судом, каким судишь другого, осуждаешь себя, потому что, судя <i>другого</i>, делаешь то же.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37497, 'rst66', 'rom', 1, 32, 'Они знают праведный <i>суд</i> Божий, что делающие такие <i>дела</i> достойны смерти; однако не только <i>их</i> делают, но и делающих одобряют.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37496, 'rst66', 'rom', 1, 31, 'безрассудны, вероломны, нелюбовны, непримиримы, немилостивы.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37495, 'rst66', 'rom', 1, 30, 'злоречивы, клеветники, богоненавистники, обидчики, самохвалы, горды, изобретательны на зло, непослушны родителям,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37494, 'rst66', 'rom', 1, 29, 'так что они исполнены всякой неправды, блуда, лукавства, корыстолюбия, злобы, исполнены зависти, убийства, распрей, обмана, злонравия,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37493, 'rst66', 'rom', 1, 28, 'И как они не заботились иметь Бога в разуме, то предал их Бог превратному уму &mdash; делать непотребства,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37492, 'rst66', 'rom', 1, 27, 'подобно и мужчины, оставив естественное употребление женского пола, разжигались похотью друг на друга, мужчины на мужчинах делая срам и получая в самих себе должное возмездие за свое заблуждение.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37491, 'rst66', 'rom', 1, 26, 'Потому предал их Бог постыдным страстям: женщины их заменили естественное употребление противоестественным;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37490, 'rst66', 'rom', 1, 25, 'Они заменили истину Божию ложью, и поклонялись, и служили твари вместо Творца, Который благословен вовеки, аминь.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37489, 'rst66', 'rom', 1, 24, 'то и предал их Бог в похотях сердец их нечистоте, так что они сквернили сами свои тела.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37488, 'rst66', 'rom', 1, 23, 'и славу нетленного Бога изменили в образ, подобный тленному человеку, и птицам, и четвероногим, и пресмыкающимся, &mdash;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37487, 'rst66', 'rom', 1, 22, 'называя себя мудрыми, обезумели,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37486, 'rst66', 'rom', 1, 21, 'Но как они, познав Бога, не прославили Его, как Бога, и не возблагодарили, но осуетились в умствованиях своих, и омрачилось несмысленное их сердце;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37485, 'rst66', 'rom', 1, 20, 'Ибо невидимое Его, вечная сила Его и Божество, от создания мира через рассматривание творений видимы, так что они безответны.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37484, 'rst66', 'rom', 1, 19, 'Ибо, что&#769; можно знать о Боге, явно для них, потому что Бог явил им.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37483, 'rst66', 'rom', 1, 18, 'Ибо открывается гнев Божий с неба на всякое нечестие и неправду человеков, подавляющих истину неправдою.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37482, 'rst66', 'rom', 1, 17, 'В нем открывается правда Божия от веры в веру, как написано: «праведный верою жив будет».');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37481, 'rst66', 'rom', 1, 16, 'Ибо я не стыжусь благовествования Христова, потому что <i>оно</i> есть сила Божия ко спасению всякому верующему, во‐первых, Иудею, <i>потом</i> и Еллину.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37480, 'rst66', 'rom', 1, 15, 'Итак, что до меня, я готов благовествовать и вам, находящимся в Риме.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37479, 'rst66', 'rom', 1, 14, 'Я должен и Еллинам и варварам, мудрецам и невеждам.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37478, 'rst66', 'rom', 1, 13, 'Не хочу, братия, <i>оставить</i> вас в неведении, что я многократно намеревался прийти к вам (но встречал препятствия даже доныне), чтобы иметь некий плод и у вас, как и у прочих народов.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37477, 'rst66', 'rom', 1, 12, 'то есть утешиться с вами верою общею, вашею и моею.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37476, 'rst66', 'rom', 1, 11, 'ибо я весьма желаю увидеть вас, чтобы преподать вам некое дарование духовное к утверждению вашему,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37475, 'rst66', 'rom', 1, 10, 'всегда прося в молитвах моих, чтобы воля Божия когда‐нибудь благопоспешила мне прийти к вам,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37474, 'rst66', 'rom', 1, 9, 'Свидетель мне Бог, Которому служу духом моим в благовествовании Сына Его, что непрестанно воспоминаю о вас,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37473, 'rst66', 'rom', 1, 8, 'Прежде всего благодарю Бога моего через Иисуса Христа за всех вас, что вера ваша возвещается во всем мире.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37472, 'rst66', 'rom', 1, 7, 'всем находящимся в Риме возлюбленным Божиим, призванным святым: благодать вам и мир от Бога Отца нашего и Господа Иисуса Христа.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37471, 'rst66', 'rom', 1, 6, 'между которыми находитесь и вы, призванные Иисусом Христом, &mdash;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37470, 'rst66', 'rom', 1, 5, 'через Которого мы получили благодать и апостольство, чтобы во имя Его покорять вере все народы,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37469, 'rst66', 'rom', 1, 4, 'и открылся Сыном Божиим в силе, по духу святыни, через воскресение из мертвых, о Иисусе Христе, Господе нашем,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37468, 'rst66', 'rom', 1, 3, 'о Сыне Своем, Который родился от семени Давидова по плоти');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37467, 'rst66', 'rom', 1, 2, 'которое Бог прежде обещал через пророков Своих, в святых писаниях,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37466, 'rst66', 'rom', 1, 1, 'Павел, раб Иисуса Христа, призванный Апостол, избранный к благовестию Божию,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37465, 'rst66', 'jud', 1, 25, 'Единому Премудрому Богу, Спасителю нашему чрез Иисуса Христа Господа нашего, слава и величие, сила и власть прежде всех веков, ныне и во все веки. Аминь.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37464, 'rst66', 'jud', 1, 24, 'Могущему же соблюсти вас от падения и поставить пред славою Своею непорочными в радости,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37463, 'rst66', 'jud', 1, 23, 'а других страхом спасайте, исторгая из огня, обличайте же со страхом, гнушаясь даже одеждою, которая осквернена плотью.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37462, 'rst66', 'jud', 1, 22, 'И к одним будьте милостивы, с рассмотрением,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37461, 'rst66', 'jud', 1, 21, 'сохраняйте себя в любви Божией, ожидая милости от Господа нашего Иисуса Христа, для вечной жизни.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37460, 'rst66', 'jud', 1, 20, 'А вы, возлюбленные, назидая себя на святейшей вере вашей, молясь Духом Святым,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37459, 'rst66', 'jud', 1, 19, 'Это люди, отделяющие себя [от единства веры], душевные, не имеющие духа.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37458, 'rst66', 'jud', 1, 18, 'Они говорили вам, что в последнее время появятся ругатели, поступающие по своим нечестивым похотям.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37457, 'rst66', 'jud', 1, 17, 'Но вы, возлюбленные, помните предсказанное Апостолами Господа нашего Иисуса Христа.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37456, 'rst66', 'jud', 1, 16, 'Это ропотники, ничем не довольные, поступающие по своим похотям [нечестиво и беззаконно]; уста их произносят надутые слова; они оказывают лицеприятие для корысти.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37455, 'rst66', 'jud', 1, 15, 'сотворить суд над всеми и обличить всех между ними нечестивых во всех делах, которые произвело их нечестие, и во всех жестоких словах, которые произносили на Него нечестивые грешники».');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37454, 'rst66', 'jud', 1, 14, 'О них пророчествовал и Енох, седьмой от Адама, говоря: «се, идет Господь со тьмами святых Ангелов Своих &mdash;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37453, 'rst66', 'jud', 1, 13, 'свирепые морские волны, пенящиеся срамотами своими; звезды блуждающие, которым блюдется мрак тьмы навеки.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37452, 'rst66', 'jud', 1, 12, 'Таковые бывают соблазном на ваших вечерях любви; пиршествуя с вами, без страха утучняют себя. Это безводные облака, носимые ветром; осенние деревья, бесплодные, дважды умершие, исторгнутые;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37451, 'rst66', 'jud', 1, 11, 'Горе им, потому что идут путем Каиновым, предаются обольщению мзды, как Валаам, и в упорстве погибают, как Корей.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37450, 'rst66', 'jud', 1, 10, 'А сии злословят то, чего не знают; что же по природе, как бессловесные животные, знают, тем растлевают себя.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37449, 'rst66', 'jud', 1, 9, 'Михаил Архангел, когда говорил с диаволом, споря о Моисеевом теле, не смел произнести укоризненного суда, но сказал: «да запретит тебе Господь».');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37448, 'rst66', 'jud', 1, 8, 'так точно будет и с сими мечтателями, которые оскверняют плоть, отвергают начальства и злословят высокие власти.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37447, 'rst66', 'jud', 1, 7, 'Как Содом и Гоморра и окрестные города, подобно им блудодействовавшие и ходившие за иною плотию, подвергшись казни огня вечного, поставлены в пример, &mdash;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37446, 'rst66', 'jud', 1, 6, 'и ангелов, не сохранивших своего достоинства, но оставивших свое жилище, соблюдает в вечных узах, под мраком, на суд великого дня.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37445, 'rst66', 'jud', 1, 5, 'Я хочу напомнить вам, уже знающим это, что Господь, избавив народ из земли Египетской, потом неверовавших погубил,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37444, 'rst66', 'jud', 1, 4, 'Ибо вкрались некоторые люди, издревле предназначенные к сему осуждению, нечестивые, обращающие благодать Бога нашего в <i>повод к</i> распутству и отвергающиеся единого Владыки Бога и Господа нашего Иисуса Христа.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37443, 'rst66', 'jud', 1, 3, 'Возлюбленные! имея все усердие писать вам об общем спасении, я почел за нужное написать вам увещание &mdash; подвизаться за веру, однажды преданную святым.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37442, 'rst66', 'jud', 1, 2, 'милость вам и мир и любовь да умножатся.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37441, 'rst66', 'jud', 1, 1, 'Иуда, раб Иисуса Христа, брат Иакова, призванным, которые освящены Богом Отцом и сохранены Иисусом Христом:');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37440, 'rst66', '3jn', 1, 15, 'Мир тебе. Приветствуют тебя друзья; приветствуй друзей поименно. Аминь.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37439, 'rst66', '3jn', 1, 14, 'а надеюсь скоро увидеть тебя и поговорить устами к устам.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37438, 'rst66', '3jn', 1, 13, 'Многое имел я писать; но не хочу писать к тебе чернилами и тростью,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37437, 'rst66', '3jn', 1, 12, 'О Димитрии засвидетельствовано всеми и самою истиною; свидетельствуем также и мы, и вы знаете, что свидетельство наше истинно.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37436, 'rst66', '3jn', 1, 11, 'Возлюбленный! не подражай злу, но добру. Кто делает добро, тот от Бога; а делающий зло не видел Бога.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37435, 'rst66', '3jn', 1, 10, 'Посему, если я приду, то напомню о делах, которые он делает, понося нас злыми словами, и, не довольствуясь тем, и сам не принимает братьев, и запрещает желающим, и изгоняет из церкви.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37434, 'rst66', '3jn', 1, 9, 'Я писал церкви; но любящий первенствовать у них Диотреф не принимает нас.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37433, 'rst66', '3jn', 1, 8, 'Итак, мы должны принимать таковых, чтобы сделаться споспешниками истине.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37432, 'rst66', '3jn', 1, 7, 'ибо они ради имени Его пошли, не взяв ничего от язычников.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37431, 'rst66', '3jn', 1, 6, 'Они засвидетельствовали перед церковью о твоей любви. Ты хорошо поступишь, если отпустишь их, как должно ради Бога,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37430, 'rst66', '3jn', 1, 5, 'Возлюбленный! ты как верный поступаешь в том, что делаешь для братьев и для странников.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37429, 'rst66', '3jn', 1, 4, 'Для меня нет большей радости, как слышать, что дети мои ходят в истине.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37428, 'rst66', '3jn', 1, 3, 'Ибо я весьма обрадовался, когда пришли братия и засвидетельствовали о твоей верности, как ты ходишь в истине.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37427, 'rst66', '3jn', 1, 2, 'Возлюбленный! молюсь, чтобы ты здравствовал и преуспевал во всем, как преуспевает душа твоя.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37426, 'rst66', '3jn', 1, 1, 'Старец &mdash; возлюбленному Гаию, которого я люблю по истине.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37425, 'rst66', '2jn', 1, 13, 'Приветствуют тебя дети сестры твоей избранной. Аминь.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37424, 'rst66', '2jn', 1, 12, 'Многое имею писать вам, но не хочу на бумаге чернилами, а надеюсь прийти к вам и говорить устами к устам, чтобы радость ваша была полна.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37423, 'rst66', '2jn', 1, 11, 'Ибо приветствующий его участвует в злых делах его.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37422, 'rst66', '2jn', 1, 10, 'Кто приходит к вам и не приносит сего учения, того не принимайте в дом и не приветствуйте его.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37421, 'rst66', '2jn', 1, 9, 'Всякий, преступающий учение Христово и не пребывающий в нем, не имеет Бога; пребывающий в учении Христовом имеет и Отца и Сына.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37420, 'rst66', '2jn', 1, 8, 'Наблюдайте за собою, чтобы нам не потерять того, над чем мы трудились, но чтобы получить полную награду.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37419, 'rst66', '2jn', 1, 7, 'Ибо многие обольстители вошли в мир, не исповедующие Иисуса Христа, пришедшего во плоти: такой <i>человек</i> есть обольститель и антихрист.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37418, 'rst66', '2jn', 1, 6, 'Любовь же состоит в том, чтобы мы поступали по заповедям Его. Это та заповедь, которую вы слышали от начала, чтобы поступали по ней.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37417, 'rst66', '2jn', 1, 5, 'И ныне прошу тебя, госпожа, не как новую заповедь предписывая тебе, но ту, которую имеем от начала, чтобы мы любили друг друга.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37416, 'rst66', '2jn', 1, 4, 'Я весьма обрадовался, что нашел из детей твоих, ходящих в истине, как мы получили заповедь от Отца.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37415, 'rst66', '2jn', 1, 3, 'Да будет с вами благодать, милость, мир от Бога Отца и от Господа Иисуса Христа, Сына Отчего, в истине и любви.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37414, 'rst66', '2jn', 1, 2, 'ради истины, которая пребывает в нас и будет с нами вовек.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37413, 'rst66', '2jn', 1, 1, 'Старец &mdash; избранной госпоже и детям ее, которых я люблю по истине, и не только я, но и все, познавшие истину,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37412, 'rst66', '1jn', 5, 21, 'Дети! храните себя от идолов. Аминь.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37411, 'rst66', '1jn', 5, 20, 'Знаем также, что Сын Божий пришел и дал нам свет и разум, да позна&#769;ем Бога истинного и да будем в истинном Сыне Его Иисусе Христе. Сей есть истинный Бог и жизнь вечная.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37410, 'rst66', '1jn', 5, 19, 'Мы знаем, что мы от Бога и что весь мир лежит во зле.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37409, 'rst66', '1jn', 5, 18, 'Мы знаем, что всякий, рожденный от Бога, не грешит; но рожденный от Бога хранит себя, и лукавый не прикасается к нему.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37408, 'rst66', '1jn', 5, 17, 'Всякая неправда есть грех; но есть грех не к смерти.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37407, 'rst66', '1jn', 5, 16, 'Если кто видит брата своего согрешающего грехом не к смерти, то пусть молится, и <i>Бог</i> даст ему жизнь, <i>то есть</i> согрешающему <i>грехом</i> не к смерти. Есть грех к смерти: не о том говорю, чтобы он молился.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37406, 'rst66', '1jn', 5, 15, 'А когда мы знаем, что Он слушает нас во всем, чего бы мы ни просили, &mdash; знаем и то, что получаем просимое от Него.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37405, 'rst66', '1jn', 5, 14, 'И вот какое дерзновение мы имеем к Нему, что, когда просим чего по воле Его, Он слушает нас.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37404, 'rst66', '1jn', 5, 13, 'Сие написал я вам, верующим во имя Сына Божия, дабы вы знали, что вы, веруя в Сына Божия, имеете жизнь вечную.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37403, 'rst66', '1jn', 5, 12, 'Имеющий Сына [Божия] имеет жизнь; не имеющий Сына Божия не имеет жизни.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37402, 'rst66', '1jn', 5, 11, 'Свидетельство сие состоит в том, что Бог даровал нам жизнь вечную, и сия жизнь &mdash; в Сыне Его.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37401, 'rst66', '1jn', 5, 10, 'Верующий в Сына Божия имеет свидетельство в себе самом; не верующий Богу представляет Его лживым, потому что не верует в свидетельство, которым Бог свидетельствовал о Сыне Своем.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37400, 'rst66', '1jn', 5, 9, 'Если мы принимаем свидетельство человеческое, свидетельство Божие &mdash; больше, ибо это есть свидетельство Божие, которым Бог свидетельствовал о Сыне Своем.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37399, 'rst66', '1jn', 5, 8, 'И три свидетельствуют на земле: дух, вода и кровь; и сии три об одном.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37398, 'rst66', '1jn', 5, 7, 'Ибо три свидетельствуют на небе: Отец, Слово и Святой Дух; и Сии три суть едино.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37397, 'rst66', '1jn', 5, 6, 'Сей есть Иисус Христос, пришедший водою и кровию и Духом, не водою только, но водою и кровию, и Дух свидетельствует <i>о Нем</i>, потому что Дух есть истина.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37396, 'rst66', '1jn', 5, 5, 'Кто побеждает мир, как не тот, кто верует, что Иисус есть Сын Божий?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37395, 'rst66', '1jn', 5, 4, 'Ибо всякий, рожденный от Бога, побеждает мир; и сия есть победа, победившая мир, вера наша.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37394, 'rst66', '1jn', 5, 3, 'Ибо это есть любовь к Богу, чтобы мы соблюдали заповеди Его; и заповеди Его не тяжки.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37393, 'rst66', '1jn', 5, 2, 'Что мы любим детей Божиих, узнаём из того, когда любим Бога и соблюдаем заповеди Его.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37392, 'rst66', '1jn', 5, 1, 'Всякий верующий, что Иисус есть Христос, от Бога рожден, и всякий, любящий Родившего, любит и Рожденного от Него.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37391, 'rst66', '1jn', 4, 21, 'И мы имеем от Него такую заповедь, чтобы любящий Бога любил и брата своего.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37390, 'rst66', '1jn', 4, 20, 'Кто говорит: «я люблю Бога», а брата своего ненавидит, тот лжец: ибо не любящий брата своего, которого видит, как может любить Бога, Которого не видит?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37389, 'rst66', '1jn', 4, 19, 'Будем любить Его, потому что Он прежде возлюбил нас.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37388, 'rst66', '1jn', 4, 18, 'В любви нет страха, но совершенная любовь изгоняет страх, потому что в страхе есть мучение. Боящийся несовершен в любви.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37387, 'rst66', '1jn', 4, 17, 'Любовь до того совершенства достигает в нас, что мы имеем дерзновение в день суда, потому что поступаем в мире сем, как Он.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37386, 'rst66', '1jn', 4, 16, 'И мы познали любовь, которую имеет к нам Бог, и уверовали в нее. Бог есть любовь, и пребывающий в любви пребывает в Боге, и Бог в нем.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37385, 'rst66', '1jn', 4, 15, 'Кто исповедует, что Иисус есть Сын Божий, в том пребывает Бог, и он в Боге.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37384, 'rst66', '1jn', 4, 14, 'И мы видели и свидетельствуем, что Отец послал Сына Спасителем миру.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37383, 'rst66', '1jn', 4, 13, 'Что мы пребываем в Нем и Он в нас, узнаём из того, что Он дал нам от Духа Своего.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37382, 'rst66', '1jn', 4, 12, 'Бога никто никогда не видел. Если мы любим друг друга, то Бог в нас пребывает, и любовь Его совершенна есть в нас.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37381, 'rst66', '1jn', 4, 11, 'Возлюбленные! если так возлюбил нас Бог, то и мы должны любить друг друга.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37380, 'rst66', '1jn', 4, 10, 'В том любовь, что не мы возлюбили Бога, но Он возлюбил нас и послал Сына Своего в умилостивление за грехи наши.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37379, 'rst66', '1jn', 4, 9, 'Любовь Божия к нам открылась в том, что Бог послал в мир Единородного Сына Своего, чтобы мы получили жизнь через Него.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37378, 'rst66', '1jn', 4, 8, 'Кто не любит, тот не познал Бога, потому что Бог есть любовь.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37377, 'rst66', '1jn', 4, 7, 'Возлюбленные! будем любить друг друга, потому что любовь от Бога, и всякий любящий рожден от Бога и знает Бога.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37376, 'rst66', '1jn', 4, 6, 'Мы от Бога; знающий Бога слушает нас; кто не от Бога, тот не слушает нас. По сему‐то узнаём духа истины и духа заблуждения.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37375, 'rst66', '1jn', 4, 5, 'Они от мира, потому и говорят по‐мирски, и мир слушает их.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37374, 'rst66', '1jn', 4, 4, 'Дети! вы от Бога, и победили их; ибо Тот, Кто в вас, больше того, кто в мире.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37373, 'rst66', '1jn', 4, 3, 'а всякий дух, который не исповедует Иисуса Христа, пришедшего во плоти, не есть от Бога, но это дух антихриста, о котором вы слышали, что он придет и теперь есть уже в мире.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37372, 'rst66', '1jn', 4, 2, 'Духа Божия [и духа заблуждения] узнавайте так: всякий дух, который исповедует Иисуса Христа, пришедшего во плоти, есть от Бога;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37371, 'rst66', '1jn', 4, 1, 'Возлюбленные! не всякому духу верьте, но испытывайте духов, от Бога ли они, потому что много лжепророков появилось в мире.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37370, 'rst66', '1jn', 3, 24, 'И кто сохраняет заповеди Его, тот пребывает в Нем, и Он в том. А что Он пребывает в нас, узнаём по духу, который Он дал нам.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37369, 'rst66', '1jn', 3, 23, 'А заповедь Его та, чтобы мы веровали во имя Сына Его Иисуса Христа и любили друг друга, как Он заповедал нам.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37368, 'rst66', '1jn', 3, 22, 'и, чего ни попросим, получим от Него, потому что соблюдаем заповеди Его и делаем благоугодное пред Ним.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37367, 'rst66', '1jn', 3, 21, 'Возлюбленные! если сердце наше не осуждает нас, то мы имеем дерзновение к Богу,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37366, 'rst66', '1jn', 3, 20, 'ибо если сердце наше осуждает нас, то <i>кольми паче Бог</i>, потому что Бог больше сердца нашего и знает всё.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37365, 'rst66', '1jn', 3, 19, 'И вот по чему узнаём, что мы от истины, и успокаиваем пред Ним сердца наши;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37364, 'rst66', '1jn', 3, 18, 'Дети мои! станем любить не словом или языком, но делом и истиною.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37363, 'rst66', '1jn', 3, 17, 'А кто имеет достаток в мире, но, видя брата своего в нужде, затворяет от него сердце свое, &mdash; как пребывает в том любовь Божия?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37362, 'rst66', '1jn', 3, 16, 'Любовь познали мы в том, что Он положил за нас душу Свою: и мы должны полагать души свои за братьев.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37361, 'rst66', '1jn', 3, 15, 'Всякий, ненавидящий брата своего, есть человекоубийца; а вы знаете, что никакой человекоубийца не имеет жизни вечной, в нем пребывающей.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37360, 'rst66', '1jn', 3, 14, 'Мы знаем, что мы перешли из смерти в жизнь, потому что любим братьев; не любящий брата пребывает в смерти.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37359, 'rst66', '1jn', 3, 13, 'Не дивитесь, братия мои, если мир ненавидит вас.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37358, 'rst66', '1jn', 3, 12, 'не так, как Каин, <i>который</i> был от лукавого и убил брата своего. А за что убил его? За то, что дела его были злы, а дела брата его праведны.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37357, 'rst66', '1jn', 3, 11, 'Ибо таково благовествование, которое вы слышали от начала, чтобы мы любили друг друга,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37356, 'rst66', '1jn', 3, 10, 'Дети Божии и дети диавола узнаю&#769;тся так: всякий, не делающий правды, не есть от Бога, равно и не любящий брата своего.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37355, 'rst66', '1jn', 3, 9, 'Всякий, рожденный от Бога, не делает греха, потому что семя Его пребывает в нем; и он не может грешить, потому что рожден от Бога.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37354, 'rst66', '1jn', 3, 8, 'Кто делает грех, тот от диавола, потому что сначала диавол согрешил. Для сего‐то и явился Сын Божий, чтобы разрушить дела диавола.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37353, 'rst66', '1jn', 3, 7, 'Дети! да не обольщает вас никто. Кто делает правду, тот праведен, подобно как Он праведен.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37352, 'rst66', '1jn', 3, 6, 'Всякий, пребывающий в Нем, не согрешает; всякий согрешающий не видел Его и не познал Его.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37351, 'rst66', '1jn', 3, 5, 'И вы знаете, что Он явился для того, чтобы взять грехи наши, и что в Нем нет греха.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37350, 'rst66', '1jn', 3, 4, 'Всякий, делающий грех, делает и беззаконие; и грех есть беззаконие.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37349, 'rst66', '1jn', 3, 3, 'И всякий, имеющий сию надежду на Него, очищает себя так, как Он чист.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37348, 'rst66', '1jn', 3, 2, 'Возлюбленные! мы теперь дети Божии; но еще не открылось, что&#769; будем. Знаем только, что, когда откроется, будем подобны Ему, потому что увидим Его как Он есть.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37347, 'rst66', '1jn', 3, 1, 'Смотри&#769;те, какую любовь дал нам Отец, чтобы нам называться и быть детьми Божиими. Мир потому не знает нас, что не познал Его.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37346, 'rst66', '1jn', 2, 29, 'Если вы знаете, что Он праведник, знайте и то&#769;, что всякий, делающий правду, рожден от Него.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37345, 'rst66', '1jn', 2, 28, 'Итак, дети, пребывайте в Нем, чтобы, когда Он явится, иметь нам дерзновение и не постыдиться пред Ним в пришествие Его.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37344, 'rst66', '1jn', 2, 27, 'Впрочем, помазание, которое вы получили от Него, в вас пребывает, и вы не имеете нужды, чтобы кто учил вас; но как самое сие помазание учит вас всему, и оно истинно и неложно, то чему оно научило вас, в том пребывайте.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37343, 'rst66', '1jn', 2, 26, 'Это я написал вам об обольщающих вас.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37342, 'rst66', '1jn', 2, 25, 'Обетование же, которое Он обещал нам, есть жизнь вечная.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37341, 'rst66', '1jn', 2, 24, 'Итак, что вы слышали от начала, то и да пребывает в вас; если пребудет в вас то, что вы слышали от начала, то и вы пребудете в Сыне и в Отце.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37340, 'rst66', '1jn', 2, 23, 'Всякий, отвергающий Сына, не имеет и Отца; а исповедующий Сына имеет и Отца.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37339, 'rst66', '1jn', 2, 22, 'Кто лжец, если не тот, кто отвергает, что Иисус есть Христос? Это антихрист, отвергающий Отца и Сына.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37338, 'rst66', '1jn', 2, 21, 'Я написал вам не потому, чтобы вы не знали истины, но потому, что вы знаете ее, <i>равно как</i> и то&#769;, что всякая ложь не от истины.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37337, 'rst66', '1jn', 2, 20, 'Впрочем, вы имеете помазание от Святого и знаете всё.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37336, 'rst66', '1jn', 2, 19, 'Они вышли от нас, но не были наши: ибо если бы они были наши, то остались бы с нами; но <i>они вышли</i>, <i>и</i> через то открылось, что не все наши.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37335, 'rst66', '1jn', 2, 18, 'Дети! последнее время. И как вы слышали, что придет антихрист, и теперь появилось много антихристов, то мы и познаём из того, что последнее время.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37334, 'rst66', '1jn', 2, 17, 'И мир проходит, и похоть его, а исполняющий волю Божию пребывает вовек.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37333, 'rst66', '1jn', 2, 16, 'Ибо всё, что в мире: похоть плоти, похоть очей и гордость житейская, не есть от Отца, но от мира сего.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37332, 'rst66', '1jn', 2, 15, 'Не люби&#769;те мира, ни того, что в мире: кто любит мир, в том нет любви Отчей.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37331, 'rst66', '1jn', 2, 14, 'Я написал вам, отцы, потому что вы познали Безначального. Я написал вам, юноши, потому что вы сильны, и слово Божие пребывает в вас, и вы победили лукавого.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37330, 'rst66', '1jn', 2, 13, 'Пишу вам, отцы, потому что вы познали Сущего от начала. Пишу вам, юноши, потому что вы победили лукавого. Пишу вам, отроки, потому что вы познали Отца.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37329, 'rst66', '1jn', 2, 12, 'Пишу вам, дети, потому что прощены вам грехи ради имени Его.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37328, 'rst66', '1jn', 2, 11, 'А кто ненавидит брата своего, тот находится во тьме, и во тьме ходит, и не знает, куда идет, потому что тьма ослепила ему глаза.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37327, 'rst66', '1jn', 2, 10, 'Кто любит брата своего, тот пребывает во свете, и нет в нем соблазна.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37326, 'rst66', '1jn', 2, 9, 'Кто говорит, что он во свете, а ненавидит брата своего, тот еще во тьме.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37325, 'rst66', '1jn', 2, 8, 'Но притом и новую заповедь пишу вам, что&#769; есть истинно и в Нем и в вас: потому что тьма проходит и истинный свет уже светит.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37324, 'rst66', '1jn', 2, 7, 'Возлюбленные! пишу вам не новую заповедь, но заповедь древнюю, которую вы имели от начала. Заповедь древняя есть слово, которое вы слышали от начала.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37323, 'rst66', '1jn', 2, 6, 'Кто говорит, что пребывает в Нем, тот должен поступать так, как Он поступал.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37322, 'rst66', '1jn', 2, 5, 'а кто соблюдает слово Его, в том истинно любовь Божия совершилась: из сего узнаём, что мы в Нем.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37321, 'rst66', '1jn', 2, 4, 'Кто говорит: «я познал Его», но заповедей Его не соблюдает, тот лжец, и нет в нем истины;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37320, 'rst66', '1jn', 2, 3, 'А что мы познали Его, узнаём из того, что соблюдаем Его заповеди.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37319, 'rst66', '1jn', 2, 2, 'Он есть умилостивление за грехи наши, и не только за наши, но и за <i>грехи</i> всего мира.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37318, 'rst66', '1jn', 2, 1, 'Дети мои! сие пишу вам, чтобы вы не согрешали; а если бы кто согрешил, то мы имеем ходатая пред Отцом, Иисуса Христа, праведника;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37317, 'rst66', '1jn', 1, 10, 'Если говорим, что мы не согрешили, то представляем Его лживым, и сло&#769;ва Его нет в нас.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37316, 'rst66', '1jn', 1, 9, 'Если исповедуем грехи наши, то Он, будучи верен и праведен, простит нам грехи наши и очистит нас от всякой неправды.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37315, 'rst66', '1jn', 1, 8, 'Если говорим, что не имеем греха, &mdash; обманываем самих себя, и истины нет в нас.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37314, 'rst66', '1jn', 1, 7, 'если же ходим во свете, подобно как Он во свете, то имеем общение друг с другом, и Кровь Иисуса Христа, Сына Его, очищает нас от всякого греха.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37313, 'rst66', '1jn', 1, 6, 'Если мы говорим, что имеем общение с Ним, а ходим во тьме, то мы лжем и не поступаем по истине;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37312, 'rst66', '1jn', 1, 5, 'И вот благовестие, которое мы слышали от Него и возвещаем вам: Бог есть свет, и нет в Нем никакой тьмы.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37311, 'rst66', '1jn', 1, 4, 'И сие пишем вам, чтобы радость ваша была совершенна.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37310, 'rst66', '1jn', 1, 3, 'о том, что мы видели и слышали, возвещаем вам, чтобы и вы имели общение с нами: а наше общение &mdash; с Отцом и Сыном Его, Иисусом Христом.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37309, 'rst66', '1jn', 1, 2, 'ибо жизнь явилась, и мы видели и свидетельствуем, и возвещаем вам сию вечную жизнь, которая была у Отца и явилась нам, &mdash;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37308, 'rst66', '1jn', 1, 1, 'О том, что было от начала, что мы слышали, что видели своими очами, что рассматривали и что осязали руки наши, о Слове жизни, &mdash;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37307, 'rst66', '2pe', 3, 18, 'но возрастайте в благодати и познании Господа нашего и Спасителя Иисуса Христа. Ему слава и ныне и в день вечный. Аминь.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37306, 'rst66', '2pe', 3, 17, 'Итак, вы, возлюбленные, будучи предварены о сем, берегитесь, чтобы вам не увлечься заблуждением беззаконников и не отпасть от своего утверждения,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37305, 'rst66', '2pe', 3, 16, 'как он говорит об этом и во всех посланиях, в которых есть нечто неудобовразумительное, что невежды и неутвержденные, к собственной своей погибели, превращают, как и прочие Писания.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37304, 'rst66', '2pe', 3, 15, 'и долготерпение Господа нашего почитайте спасением, как и возлюбленный брат наш Павел, по данной ему премудрости, написал вам,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37303, 'rst66', '2pe', 3, 14, 'Итак, возлюбленные, ожидая сего, потщитесь явиться пред Ним неоскверненными и непорочными в мире;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37302, 'rst66', '2pe', 3, 13, 'Впрочем, мы, по обетованию Его, ожидаем нового неба и новой земли, на которых обитает правда.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37301, 'rst66', '2pe', 3, 12, 'ожидающим и желающим пришествия дня Божия, в который воспламененные небеса разрушатся и разгоревшиеся стихии растают?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37300, 'rst66', '2pe', 3, 11, 'Если так всё это разрушится, то какими до&#769;лжно быть в святой жизни и благочестии вам,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37299, 'rst66', '2pe', 3, 10, 'Придет же день Господень, как тать ночью, и тогда небеса с шумом прейдут, стихии же, разгоревшись, разрушатся, земля и все дела на ней сгорят.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37298, 'rst66', '2pe', 3, 9, 'Не медлит Господь <i>исполнением</i> обетования, как некоторые почитают то медлением; но долготерпит нас, не желая, чтобы кто погиб, но чтобы все пришли к покаянию.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37297, 'rst66', '2pe', 3, 8, 'Одно то&#769; не должно быть сокрыто от вас, возлюбленные, что у Господа один день, как тысяча лет, и тысяча лет, как один день.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37296, 'rst66', '2pe', 3, 7, 'А нынешние небеса и земля, содержимые тем же Словом, сберегаются огню на день суда и погибели нечестивых человеков.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37295, 'rst66', '2pe', 3, 6, 'потому тогдашний мир погиб, быв потоплен водою.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37294, 'rst66', '2pe', 3, 5, 'Думающие так не знают, что вначале словом Божиим небеса и земля составлены из воды и водою:');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37293, 'rst66', '2pe', 3, 4, 'и говорящие: «где обетование пришествия Его? Ибо с тех пор, как стали умирать отцы, от начала творения, всё остается так же».');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37292, 'rst66', '2pe', 3, 3, 'Прежде всего знайте, что в последние дни явятся наглые ругатели, поступающие по собственным своим похотям');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37291, 'rst66', '2pe', 3, 2, 'чтобы вы помнили слова, прежде реченные святыми пророками, и заповедь Господа и Спасителя, преданную Апостолами вашими.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37290, 'rst66', '2pe', 3, 1, 'Это уже второе послание пишу к вам, возлюбленные; в них напоминанием возбуждаю ваш чистый смысл,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37289, 'rst66', '2pe', 2, 22, 'Но с ними случается по верной пословице: «пес возвращается на свою блевотину», и: «вымытая свинья <i>идет</i> валяться в грязи».');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37288, 'rst66', '2pe', 2, 21, 'Лучше бы им не познать пути правды, нежели, познав, возвратиться назад от преданной им святой заповеди.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37287, 'rst66', '2pe', 2, 20, 'Ибо если, избегнув скверн мира через познание Господа и Спасителя нашего Иисуса Христа, опять запутываются в них и побеждаются ими, то последнее бывает для таковых хуже первого.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37286, 'rst66', '2pe', 2, 19, 'Обещают им свободу, будучи сами рабы тления; ибо кто кем побежден, тот тому и раб.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37285, 'rst66', '2pe', 2, 18, 'Ибо, произнося надутое пустословие, они уловляют в плотские похоти и разврат тех, которые едва отстали от находящихся в заблуждении.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37284, 'rst66', '2pe', 2, 17, 'Это безводные источники, облака и мглы, гонимые бурею: им приготовлен мрак вечной тьмы.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37283, 'rst66', '2pe', 2, 16, 'но был обличен в своем беззаконии: бессловесная ослица, проговорив человеческим голосом, остановила безумие пророка.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37282, 'rst66', '2pe', 2, 15, 'Оставив прямой путь, они заблудились, идя по следам Валаама, сына Восорова, который возлюбил мзду неправедную,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37281, 'rst66', '2pe', 2, 14, 'Глаза у них исполнены любострастия и непрестанного греха; они прельщают неутвержденные души; сердце их приучено к любостяжанию: это сыны проклятия.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37280, 'rst66', '2pe', 2, 13, 'Они получат возмездие за беззаконие, ибо они полагают удовольствие во вседневной роскоши; срамники и осквернители, они наслаждаются обманами своими, пиршествуя с вами.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37279, 'rst66', '2pe', 2, 12, 'Они, как бессловесные животные, водимые природою, рожденные на уловление и истребление, злословя то, чего не понимают, в растлении своем истребятся.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37278, 'rst66', '2pe', 2, 11, 'тогда как и Ангелы, превосходя их крепостью и силою, не произносят на них пред Господом укоризненного суда.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37277, 'rst66', '2pe', 2, 10, 'а наипаче тех, которые идут вслед скверных похотей плоти, презирают начальства, дерзки, своевольны и не страшатся злословить высших,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37276, 'rst66', '2pe', 2, 9, 'то, конечно, знает Господь, как избавлять благочестивых от искушения, а беззаконников соблюдать ко дню суда, для наказания,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37275, 'rst66', '2pe', 2, 8, 'ибо сей праведник, живя между ними, ежедневно мучился в праведной душе, видя и слыша дела беззаконные, &mdash;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37274, 'rst66', '2pe', 2, 7, 'а праведного Лота, утомленного обращением между людьми неистово развратными, избавил, &mdash;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37273, 'rst66', '2pe', 2, 6, 'и если города Содомские и Гоморрские, осудив на истребление, превратил в пепел, показав пример будущим нечестивцам,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37272, 'rst66', '2pe', 2, 5, 'и если не пощадил первого мира, но в восьми душах сохранил семейство Ноя, проповедника правды, когда навел потоп на мир нечестивых;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37271, 'rst66', '2pe', 2, 4, 'Ибо, если Бог ангелов согрешивших не пощадил, но, связав узами адского мрака, предал блюсти на суд для наказания;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37270, 'rst66', '2pe', 2, 3, 'И из любостяжания будут уловлять вас льстивыми словами; суд им давно готов, и погибель их не дремлет.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37269, 'rst66', '2pe', 2, 2, 'И многие последуют их разврату, и через них путь истины будет в поношении.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37268, 'rst66', '2pe', 2, 1, 'Были и лжепророки в народе, как и у вас будут лжеучители, которые введут пагубные ереси и, отвергаясь искупившего их Господа, навлекут сами на себя скорую погибель.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37267, 'rst66', '2pe', 1, 21, 'Ибо никогда пророчество не было произносимо по воле человеческой, но изрекали его святые Божии человеки, будучи движимы Духом Святым.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37266, 'rst66', '2pe', 1, 20, 'зная прежде всего то, что никакого пророчества в Писании нельзя разрешить самому собою.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37265, 'rst66', '2pe', 1, 19, 'И притом мы имеем вернейшее пророческое слово; и вы хорошо делаете, что обращаетесь к нему, как к светильнику, сияющему в темном месте, доколе не начнет рассветать день и не взойдет утренняя звезда в сердцах ваших,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37264, 'rst66', '2pe', 1, 18, 'И этот глас, принесшийся с небес, мы слышали, будучи с Ним на святой горе.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37263, 'rst66', '2pe', 1, 17, 'Ибо Он принял от Бога Отца честь и славу, когда от велелепной славы принесся к Нему такой глас: «Сей есть Сын Мой возлюбленный, в Котором Мое благоволение».');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37262, 'rst66', '2pe', 1, 16, 'Ибо мы возвестили вам силу и пришествие Господа нашего Иисуса Христа, не хитросплетенным басням последуя, но быв очевидцами Его величия.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37261, 'rst66', '2pe', 1, 15, 'Буду же стараться, чтобы вы и после моего отшествия всегда приводили это на память.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37260, 'rst66', '2pe', 1, 14, 'зная, что скоро должен оставить храмину мою, как и Господь наш Иисус Христос открыл мне.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37259, 'rst66', '2pe', 1, 13, 'Справедливым же почитаю, доколе нахожусь в этой <i>телесной</i> храмине, возбуждать вас напоминанием,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37258, 'rst66', '2pe', 1, 12, 'Для того я никогда не перестану напоминать вам о сем, хотя вы то&#769; и знаете, и утверждены в настоящей истине.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37257, 'rst66', '2pe', 1, 11, 'ибо так откроется вам свободный вход в вечное Царство Господа нашего и Спасителя Иисуса Христа.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37256, 'rst66', '2pe', 1, 10, 'Посему, братия, более и более старайтесь делать твердым ваше звание и избрание; так поступая, никогда не преткнетесь,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37255, 'rst66', '2pe', 1, 9, 'А в ком нет сего, тот слеп, закрыл глаза, забыл об очищении прежних грехов своих.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37254, 'rst66', '2pe', 1, 8, 'Если это в вас есть и умножается, то вы не останетесь без успеха и плода в познании Господа нашего Иисуса Христа.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37253, 'rst66', '2pe', 1, 7, 'в благочестии братолюбие, в братолюбии любовь.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37252, 'rst66', '2pe', 1, 6, 'в рассудительности воздержание, в воздержании терпение, в терпении благочестие,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37251, 'rst66', '2pe', 1, 5, 'то вы, прилагая к сему все старание, покажите в вере вашей добродетель, в добродетели рассудительность,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37250, 'rst66', '2pe', 1, 4, 'которыми дарованы нам великие и драгоценные обетования, дабы вы через них соделались причастниками Божеского естества, удалившись от господствующего в мире растления похотью, &mdash;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37249, 'rst66', '2pe', 1, 3, 'Как от Божественной силы Его даровано нам все потребное для жизни и благочестия, через познание Призвавшего нас славою и благостию,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37248, 'rst66', '2pe', 1, 2, 'благодать и мир вам да умножится в познании Бога и Христа Иисуса, Господа нашего.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37247, 'rst66', '2pe', 1, 1, 'Симон Петр, раб и Апостол Иисуса Христа, принявшим с нами равно драгоценную веру по правде Бога нашего и Спасителя Иисуса Христа:');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37246, 'rst66', '1pe', 5, 14, 'Приветствуйте друг друга лобзанием любви. Мир вам всем во Христе Иисусе. Аминь.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37245, 'rst66', '1pe', 5, 13, 'Приветствует вас избранная, подобно <i>вам, церковь</i> в Вавилоне и Марк, сын мой.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37244, 'rst66', '1pe', 5, 12, 'Сие кратко написал я вам чрез Силуана, верного, как думаю, вашего брата, чтобы уверить вас, утешая и свидетельствуя, что это истинная благодать Божия, в которой вы стоите.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37243, 'rst66', '1pe', 5, 11, 'Ему слава и держава во веки веков. Аминь.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37242, 'rst66', '1pe', 5, 10, 'Бог же всякой благодати, призвавший нас в вечную славу Свою во Христе Иисусе, Сам, по кратковременном страдании вашем, да совершит вас, да утвердит, да укрепит, да соделает непоколебимыми.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37241, 'rst66', '1pe', 5, 9, 'Противостойте ему твердою верою, зная, что такие же страдания случаются и с братьями вашими в мире.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37240, 'rst66', '1pe', 5, 8, 'Трезвитесь, бодрствуйте, потому что противник ваш диавол ходит, как рыкающий лев, ища, кого поглотить.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37239, 'rst66', '1pe', 5, 7, 'Все заботы ваши возложите на Него, ибо Он печется о вас.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37238, 'rst66', '1pe', 5, 6, 'Итак, смиритесь под крепкую руку Божию, да вознесет вас в свое время.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37237, 'rst66', '1pe', 5, 5, 'Также и младшие, повинуйтесь пастырям; все же, подчиняясь друг другу, облекитесь смиренномудрием, потому что Бог гордым противится, а смиренным дает благодать.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37236, 'rst66', '1pe', 5, 4, 'и когда явится Пастыреначальник, вы получите неувядающий венец славы.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37235, 'rst66', '1pe', 5, 3, 'и не господствуя над наследием <i>Божиим</i>, но подавая пример стаду;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37234, 'rst66', '1pe', 5, 2, 'пасите Божие стадо, какое у вас, надзирая за ним не принужденно, но охотно и богоугодно, не для гнусной корысти, но из усердия,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37233, 'rst66', '1pe', 5, 1, 'Пастырей ваших умоляю я, сопастырь и свидетель страданий Христовых и соучастник в славе, которая должна открыться:');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37232, 'rst66', '1pe', 4, 19, 'Итак, страждущие по воле Божией да предадут Ему, как верному Создателю, души свои, делая добро.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37231, 'rst66', '1pe', 4, 18, 'И если праведник едва спасается, то нечестивый и грешный где явится?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37230, 'rst66', '1pe', 4, 17, 'Ибо время начаться суду с дома Божия; если же прежде с нас <i>начнется</i>, то какой конец непокоряющимся Евангелию Божию?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37229, 'rst66', '1pe', 4, 16, 'а если как Христианин, то не стыдись, но прославляй Бога за такую участь.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37228, 'rst66', '1pe', 4, 15, 'Только бы не пострадал кто из вас, как убийца, или вор, или злодей, или как посягающий на чужое;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37227, 'rst66', '1pe', 4, 14, 'Если злословят вас за имя Христово, то вы блаженны, ибо Дух славы, Дух Божий почивает на вас. Теми Он хулится, а вами прославляется.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37226, 'rst66', '1pe', 4, 13, 'но как вы участвуете в Христовых страданиях, радуйтесь, да и в явление славы Его возрадуетесь и восторжествуете.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37225, 'rst66', '1pe', 4, 12, 'Возлюбленные! огненного искушения, для испытания вам посылаемого, не чуждайтесь, как приключения для вас странного,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37224, 'rst66', '1pe', 4, 11, 'Говорит ли кто, <i>говори</i> как слова Божии; служит ли кто, <i>служи</i> по силе, какую дает Бог, дабы во всем прославлялся Бог через Иисуса Христа, Которому слава и держава во веки веков. Аминь.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37223, 'rst66', '1pe', 4, 10, 'Служите друг другу, каждый тем даром, какой получил, как добрые домостроители многоразличной благодати Божией.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37222, 'rst66', '1pe', 4, 9, 'Будьте страннолюбивы друг ко другу без ропота.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37221, 'rst66', '1pe', 4, 8, 'Более же всего имейте усердную любовь друг ко другу, потому что любовь покрывает множество грехов.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37220, 'rst66', '1pe', 4, 7, 'Впрочем, близок всему конец. Итак, будьте благоразумны и бодрствуйте в молитвах.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37219, 'rst66', '1pe', 4, 6, 'Ибо для того и мертвым было благовествуемо, чтобы они, подвергшись суду по человеку плотию, жили по Богу духом.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37218, 'rst66', '1pe', 4, 5, 'Они дадут ответ Имеющему вскоре судить живых и мертвых.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37217, 'rst66', '1pe', 4, 4, 'почему они и дивятся, что вы не участвуете с ними в том же распутстве, и злословят вас.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37216, 'rst66', '1pe', 4, 3, 'Ибо довольно, что вы в прошедшее время жизни поступали по воле языческой, предаваясь нечистотам, похотям [мужеложству, скотоложству, помыслам], пьянству, излишеству в пище и питии и нелепому идолослужению;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37215, 'rst66', '1pe', 4, 2, 'чтобы остальное во плоти время жить уже не по человеческим похотям, но по воле Божией.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37214, 'rst66', '1pe', 4, 1, 'Итак, как Христос пострадал за нас плотию, то и вы вооружитесь тою же мыслью; ибо страдающий плотию перестает грешить,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37213, 'rst66', '1pe', 3, 22, 'Который, взойдя на небо, пребывает одесную Бога и Которому покорились Ангелы и власти и силы.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37212, 'rst66', '1pe', 3, 21, 'Так и нас ныне подобное сему образу крещение, не плотской нечистоты омытие, но обещание Богу доброй совести, спасает воскресением Иисуса Христа,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37211, 'rst66', '1pe', 3, 20, 'некогда непокорным ожидавшему их Божию долготерпению, во дни Ноя, во время строения ковчега, в котором немногие, то есть восемь душ, спаслись от воды.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37210, 'rst66', '1pe', 3, 19, 'которым Он и находящимся в темнице духам, сойдя, проповедал,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37209, 'rst66', '1pe', 3, 18, 'потому что и Христос, чтобы привести нас к Богу, однажды пострадал за грехи наши, праведник за неправедных, быв умерщвлен по плоти, но ожив духом,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37208, 'rst66', '1pe', 3, 17, 'Ибо, если угодно воле Божией, лучше пострадать за добрые дела, нежели за злые;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37207, 'rst66', '1pe', 3, 16, 'Имейте добрую совесть, дабы тем, за что злословят вас, как злодеев, были постыжены порицающие ваше доброе житие во Христе.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37206, 'rst66', '1pe', 3, 15, 'Господа Бога святите в сердцах ваших; <i>будьте</i> всегда готовы всякому, требующему у вас отчета в вашем уповании, дать ответ с кротостью и благоговением.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37205, 'rst66', '1pe', 3, 14, 'Но если и страдаете за правду, то вы блаженны; а страха их не бойтесь и не смущайтесь.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37204, 'rst66', '1pe', 3, 13, 'И кто сделает вам зло, если вы будете ревнителями доброго?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37203, 'rst66', '1pe', 3, 12, 'потому что очи Господа <i>обращены</i> к праведным и уши Его к молитве их, но лицо Господне против делающих зло, [чтобы истребить их с земли].');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37202, 'rst66', '1pe', 3, 11, 'уклоняйся от зла и делай добро; ищи мира и стремись к нему,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37201, 'rst66', '1pe', 3, 10, 'Ибо, кто любит жизнь и хочет видеть добрые дни, тот удерживай язык свой от зла и уста свои от лукавых речей;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37200, 'rst66', '1pe', 3, 9, 'не воздавайте злом за зло или ругательством за ругательство; напротив, благословляйте, зная, что вы к тому призваны, чтобы наследовать благословение.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37199, 'rst66', '1pe', 3, 8, 'Наконец будьте все единомысленны, сострадательны, братолюбивы, милосерды, дружелюбны, смиренномудры;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37198, 'rst66', '1pe', 3, 7, 'Также и вы, мужья, обращайтесь благоразумно с женами, как с немощнейшим сосудом, оказывая им честь, как сонаследницам благодатной жизни, дабы не было вам препятствия в молитвах.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37197, 'rst66', '1pe', 3, 6, 'Так Сарра повиновалась Аврааму, называя его господином. Вы &mdash; дети ее, если делаете добро и не смущаетесь ни от какого страха.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37196, 'rst66', '1pe', 3, 5, 'Так некогда и святые жены, уповавшие на Бога, украшали себя, повинуясь своим мужьям.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37195, 'rst66', '1pe', 3, 4, 'но сокровенный сердца человек в нетленной <i>красоте</i> кроткого и молчаливого духа, что драгоценно пред Богом.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37194, 'rst66', '1pe', 3, 3, 'Да будет украшением вашим не внешнее плетение волос, не золотые уборы или нарядность в одежде,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37193, 'rst66', '1pe', 3, 2, 'когда увидят ваше чистое, богобоязненное житие.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37192, 'rst66', '1pe', 3, 1, 'Также и вы, жены, повинуйтесь своим мужьям, чтобы те из них, которые не покоряются слову, житием жен своих без слова приобретаемы были,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37191, 'rst66', '1pe', 2, 25, 'Ибо вы были как овцы, блуждающие [не имея пастыря], но возвратились ныне к Пастырю и Блюстителю душ ваших.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37190, 'rst66', '1pe', 2, 24, 'Он грехи наши Сам вознес телом Своим на древо, дабы мы, избавившись от грехов, жили для правды: ранами Его вы исцелились.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37189, 'rst66', '1pe', 2, 23, 'Будучи злословим, Он не злословил взаимно; страдая, не угрожал, но предавал то Судии Праведному.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37188, 'rst66', '1pe', 2, 22, 'Он не сделал никакого греха, и не было лести в устах Его.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37187, 'rst66', '1pe', 2, 21, 'Ибо вы к тому призваны, потому что и Христос пострадал за нас, оставив нам пример, дабы мы шли по следам Его.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37186, 'rst66', '1pe', 2, 20, 'Ибо что&#769; за похвала, если вы терпите, когда вас бьют за проступки? Но если, делая добро и страдая, терпите, это угодно Богу.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37185, 'rst66', '1pe', 2, 19, 'Ибо то угодно Богу, если кто, помышляя о Боге, переносит скорби, страдая несправедливо.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37184, 'rst66', '1pe', 2, 18, 'Слуги, со всяким страхом повинуйтесь господам, не только добрым и кротким, но и суровым.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37183, 'rst66', '1pe', 2, 17, 'Всех почитайте, братство любите, Бога бойтесь, царя чтите.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37182, 'rst66', '1pe', 2, 16, 'как свободные, не как употребляющие свободу для прикрытия зла, но как рабы Божии.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37181, 'rst66', '1pe', 2, 15, 'ибо такова есть воля Божия, чтобы мы, делая добро, заграждали уста невежеству безумных людей, &mdash;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37180, 'rst66', '1pe', 2, 14, 'правителям ли, как от него посылаемым для наказания преступников и для поощрения делающих добро, &mdash;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37179, 'rst66', '1pe', 2, 13, 'Итак, будьте покорны всякому человеческому начальству, для Господа: царю ли, как верховной власти,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37178, 'rst66', '1pe', 2, 12, 'и провождать добродетельную жизнь между язычниками, дабы они за то, за что злословят вас, как злодеев, увидя добрые дела ваши, прославили Бога в день посещения.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37177, 'rst66', '1pe', 2, 11, 'Возлюбленные! прошу вас, как пришельцев и странников, удаляться от плотских похотей, восстающих на душу,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37176, 'rst66', '1pe', 2, 10, 'некогда не народ, а ныне народ Божий; <i>некогда</i> непомилованные, а ныне помилованы.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37175, 'rst66', '1pe', 2, 9, 'Но вы &mdash; род избранный, царственное священство, народ святой, люди, взятые в удел, дабы возвещать совершенства Призвавшего вас из тьмы в чудный Свой свет;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37174, 'rst66', '1pe', 2, 8, 'о который они претыкаются, не покоряясь слову, на что они и оставлены.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37173, 'rst66', '1pe', 2, 7, 'Итак, Он для вас, верующих, драгоценность, а для неверующих &mdash; камень, который отвергли строители, но который сделался главою угла, камень претыкания и камень соблазна,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37172, 'rst66', '1pe', 2, 6, 'Ибо сказано в Писании: «вот, Я полагаю в Сионе камень краеугольный, избранный, драгоценный; и верующий в Него не постыдится».');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37171, 'rst66', '1pe', 2, 5, 'и сами, как живые камни, устрояйте из себя дом духовный, священство святое, чтобы приносить духовные жертвы, благоприятные Богу Иисусом Христом.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37170, 'rst66', '1pe', 2, 4, 'Приступая к Нему, камню живому, человеками отверженному, но Богом избранному, драгоценному,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37169, 'rst66', '1pe', 2, 3, 'ибо вы вкусили, что благ Господь.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37168, 'rst66', '1pe', 2, 2, 'как новорожденные младенцы, возлюби&#769;те чистое словесное молоко, дабы от него возрасти вам во спасение;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37167, 'rst66', '1pe', 2, 1, 'Итак, отложив всякую злобу и всякое коварство, и лицемерие, и зависть, и всякое злословие,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37166, 'rst66', '1pe', 1, 25, 'но слово Господне пребывает вовек; а это есть то слово, которое вам проповедано.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37165, 'rst66', '1pe', 1, 24, 'Ибо всякая плоть &mdash; как трава, и всякая слава человеческая &mdash; как цвет на траве: засохла трава, и цвет ее опал;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37164, 'rst66', '1pe', 1, 23, 'как возрожденные не от тленного семени, но от нетленного, от слова Божия, живого и пребывающего вовек.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37163, 'rst66', '1pe', 1, 22, 'Послушанием истине чрез Духа, очистив души ваши к нелицемерному братолюбию, постоянно люби&#769;те друг друга от чистого сердца,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37162, 'rst66', '1pe', 1, 21, 'уверовавших чрез Него в Бога, Который воскресил Его из мертвых и дал Ему славу, чтобы вы имели веру и упование на Бога.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37161, 'rst66', '1pe', 1, 20, 'предназначенного еще прежде создания мира, но явившегося в последние времена для вас,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37160, 'rst66', '1pe', 1, 19, 'но драгоценною Кровию Христа, как непорочного и чистого Агнца,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37159, 'rst66', '1pe', 1, 18, 'зная, что не тленным серебром или золотом искуплены вы от суетной жизни, преданной вам от отцов,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37158, 'rst66', '1pe', 1, 17, 'И если вы называете Отцом Того, Который нелицеприятно судит каждого по делам, то со страхом проводи&#769;те время странствования вашего,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37157, 'rst66', '1pe', 1, 16, 'Ибо написано: «будьте святы, потому что Я свят».');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37156, 'rst66', '1pe', 1, 15, 'но, по примеру призвавшего вас Святого, и сами будьте святы во всех поступках.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37155, 'rst66', '1pe', 1, 14, 'Как послушные дети, не сообразуйтесь с прежними похотями, бывшими в неведении вашем,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37154, 'rst66', '1pe', 1, 13, 'Посему, [возлюбленные], препоясав чресла ума вашего, бодрствуя, совершенно уповайте на подаваемую вам благодать в явлении Иисуса Христа.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37153, 'rst66', '1pe', 1, 12, 'Им открыто было, что не им самим, а нам служило то, что ныне проповедано вам благовествовавшими Духом Святым, посланным с небес, во что желают проникнуть Ангелы.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37152, 'rst66', '1pe', 1, 11, 'исследуя, на которое и на какое время указывал сущий в них Дух Христов, когда Он предвозвещал Христовы страдания и последующую за ними славу.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37151, 'rst66', '1pe', 1, 10, 'К сему‐то спасению относились изыскания и исследования пророков, которые предсказывали о назначенной вам благодати,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37150, 'rst66', '1pe', 1, 9, 'достигая наконец верою вашею спасения душ.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37149, 'rst66', '1pe', 1, 8, 'Которого, не видев, лю&#769;бите, и Которого доселе не видя, но веруя в Него, радуетесь радостью неизреченною и преславною,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37148, 'rst66', '1pe', 1, 7, 'дабы испытанная вера ваша оказалась драгоценнее гибнущего, хотя и огнем испытываемого золота, к похвале и чести и славе в явление Иисуса Христа,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37147, 'rst66', '1pe', 1, 6, 'О сем радуйтесь, поскорбев теперь немного, если нужно, от различных искушений,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37146, 'rst66', '1pe', 1, 5, 'силою Божиею через веру соблюдаемых ко спасению, готовому открыться в последнее время.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37145, 'rst66', '1pe', 1, 4, 'к наследству нетленному, чистому, неувядаемому, хранящемуся на небесах для вас,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37144, 'rst66', '1pe', 1, 3, 'Благословен Бог и Отец Господа нашего Иисуса Христа, по великой Своей милости возродивший нас воскресением Иисуса Христа из мертвых к упованию живому,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37143, 'rst66', '1pe', 1, 2, 'по предведению Бога Отца, при освящении от Духа, к послушанию и окроплению Кровию Иисуса Христа: благодать вам и мир да умножится.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37142, 'rst66', '1pe', 1, 1, 'Петр, Апостол Иисуса Христа, пришельцам, рассеянным в Понте, Галатии, Каппадокии, Асии и Вифинии, избранным,');",

"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37141, 'rst66', 'jas', 5, 20, 'пусть тот знает, что обративший грешника от ложного пути его спасет душу от смерти и покроет множество грехов.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37140, 'rst66', 'jas', 5, 19, 'Братия! если кто из вас уклонится от истины, и обратит кто его,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37139, 'rst66', 'jas', 5, 18, 'И опять помолился: и небо дало дождь, и земля произрастила плод свой.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37138, 'rst66', 'jas', 5, 17, 'Илия был человек, подобный нам, и молитвою помолился, чтобы не было дождя: и не было дождя на землю три года и шесть месяцев.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37137, 'rst66', 'jas', 5, 16, 'Признавайтесь друг пред другом в проступках и молитесь друг за друга, чтобы исцелиться: много может усиленная молитва праведного.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37136, 'rst66', 'jas', 5, 15, 'И молитва веры исцелит болящего, и восставит его Господь; и если он соделал грехи, простятся ему.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37135, 'rst66', 'jas', 5, 14, 'Болен ли кто из вас, пусть призовет пресвитеров Церкви, и пусть помолятся над ним, помазав его елеем во имя Господне.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37134, 'rst66', 'jas', 5, 13, 'Злостраждет ли кто из вас, пусть молится. Весел ли кто, пусть поет псалмы.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37133, 'rst66', 'jas', 5, 12, 'Прежде же всего, братия мои, не клянитесь ни небом, ни землею и никакою другою клятвою, но да будет у вас: «да, да» и «нет, нет», дабы вам не подпасть осуждению.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37132, 'rst66', 'jas', 5, 11, 'Вот, мы ублажаем тех, которые терпели. Вы слышали о терпении Иова и видели конец <i>оного</i> от Господа, ибо Господь весьма милосерд и сострадателен.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37131, 'rst66', 'jas', 5, 10, 'В пример злострадания и долготерпения возьмите, братия мои, пророков, которые говорили именем Господним.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37130, 'rst66', 'jas', 5, 9, 'Не сетуйте, братия, друг на друга, чтобы не быть осужденными: вот, Судия стоит у дверей.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37129, 'rst66', 'jas', 5, 8, 'Долготерпи&#769;те и вы, укрепите сердца ваши, потому что пришествие Господне приближается.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37128, 'rst66', 'jas', 5, 7, 'Итак, братия, будьте долготерпеливы до пришествия Господня. Вот, земледелец ждет драгоценного плода от земли и для него терпит долго, пока получит дождь ранний и поздний.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37127, 'rst66', 'jas', 5, 6, 'Вы осудили, убили Праведника; Он не противился вам.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37126, 'rst66', 'jas', 5, 5, 'Вы роскошествовали на земле и наслаждались; напитали сердца ваши, как бы на день заклания.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37125, 'rst66', 'jas', 5, 4, 'Вот, плата, удержанная вами у работников, пожавших поля ваши, вопиет, и вопли жнецов дошли до слуха Господа Саваофа.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37124, 'rst66', 'jas', 5, 3, 'Золото ваше и серебро изоржавело, и ржавчина их будет свидетельством против вас и съест плоть вашу, как огонь: вы собрали себе сокровище на последние дни.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37123, 'rst66', 'jas', 5, 2, 'Богатство ваше сгнило, и одежды ваши изъедены молью.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37122, 'rst66', 'jas', 5, 1, 'Послушайте вы, богатые: плачьте и рыдайте о бедствиях ваших, находящих на вас.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37121, 'rst66', 'jas', 4, 17, 'Итак, кто разумеет делать добро и не делает, тому грех.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37120, 'rst66', 'jas', 4, 16, 'вы, по своей надменности, тщеславитесь: всякое такое тщеславие есть зло.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37119, 'rst66', 'jas', 4, 15, 'Вместо того чтобы вам говорить: «если угодно будет Господу и живы будем, то сделаем то или другое», &mdash;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37118, 'rst66', 'jas', 4, 14, 'вы, которые не знаете, что случится завтра: ибо что такое жизнь ваша? пар, являющийся на малое время, а потом исчезающий.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37117, 'rst66', 'jas', 4, 13, 'Теперь послушайте вы, говорящие: «сегодня или завтра отправимся в такой‐то город, и проживем там один год, и будем торговать и получать прибыль»;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37116, 'rst66', 'jas', 4, 12, 'Един Законодатель и Судия, могущий спасти и погубить; а ты кто, который судишь другого?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37115, 'rst66', 'jas', 4, 11, 'Не злословьте друг друга, братия: кто злословит брата или судит брата своего, тот злословит закон и судит закон; а если ты судишь закон, то ты не исполнитель закона, но судья.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37114, 'rst66', 'jas', 4, 10, 'Смиритесь пред Господом, и вознесет вас.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37113, 'rst66', 'jas', 4, 9, 'Сокрушайтесь, плачьте и рыдайте; смех ваш да обратится в плач, и радость &mdash; в печаль.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37112, 'rst66', 'jas', 4, 8, 'Приблизьтесь к Богу, и приблизится к вам; очистите руки, грешники, исправьте сердца, двоедушные.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37111, 'rst66', 'jas', 4, 7, 'Итак, покоритесь Богу; противостаньте диаволу, и убежит от вас.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37110, 'rst66', 'jas', 4, 6, 'Но тем бо&#769;льшую дает благодать; посему и сказано: «Бог гордым противится, а смиренным дает благодать».');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37109, 'rst66', 'jas', 4, 5, 'Или вы думаете, что напрасно говорит Писание: «до ревности любит дух, живущий в нас»?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37108, 'rst66', 'jas', 4, 4, 'Прелюбодеи и прелюбодеицы! не знаете ли, что дружба с миром есть вражда против Бога? Итак, кто хочет быть другом миру, тот становится врагом Богу.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37107, 'rst66', 'jas', 4, 3, 'Про&#769;сите и не получаете, потому что про&#769;сите не на добро, а чтобы употребить для ваших вожделений.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37106, 'rst66', 'jas', 4, 2, 'Желаете &mdash; и не имеете; убиваете и завидуете &mdash; и не можете достигнуть; препираетесь и враждуете &mdash; и не имеете, потому что не про&#769;сите.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37105, 'rst66', 'jas', 4, 1, 'Откуда у вас вражды и распри? не отсюда ли, от вожделений ваших, воюющих в членах ваших?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37104, 'rst66', 'jas', 3, 18, 'Плод же правды в мире сеется у тех, которые хранят мир.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37103, 'rst66', 'jas', 3, 17, 'Но мудрость, сходящая свыше, во‐первых, чиста, потом мирна, скромна, послушлива, полна милосердия и добрых плодов, беспристрастна и нелицемерна.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37102, 'rst66', 'jas', 3, 16, 'ибо где зависть и сварливость, там неустройство и всё худое.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37101, 'rst66', 'jas', 3, 15, 'Это не есть мудрость, нисходящая свыше, но земная, душевная, бесовская,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37100, 'rst66', 'jas', 3, 14, 'Но если в вашем сердце вы имеете горькую зависть и сварливость, то не хвалитесь и не лгите на истину.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37099, 'rst66', 'jas', 3, 13, 'Мудр ли и разумен кто из вас, докажи это на самом деле добрым поведением с мудрою кротостью.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37098, 'rst66', 'jas', 3, 12, 'Не может, братия мои, смоковница приносить маслины или виноградная лоза смоквы. Также и один источник не <i>может</i> изливать соленую и сладкую воду.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37097, 'rst66', 'jas', 3, 11, 'Течет ли из одного отверстия источника сладкая и горькая <i>вода?</i>');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37096, 'rst66', 'jas', 3, 10, 'Из тех же уст исходит благословение и проклятие: не должно, братия мои, сему так быть.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37095, 'rst66', 'jas', 3, 9, 'Им благословляем Бога и Отца, и им проклинаем человеков, сотворенных по подобию Божию.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37094, 'rst66', 'jas', 3, 8, 'а язык укротить никто из людей не может: это &mdash; неудержимое зло; он исполнен смертоносного яда.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37093, 'rst66', 'jas', 3, 7, 'Ибо всякое естество зверей и птиц, пресмыкающихся и морских животных укрощается и укрощено естеством человеческим,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37092, 'rst66', 'jas', 3, 6, 'И язык &mdash; огонь, прикраса неправды; язык в таком положении находится между членами нашими, что оскверняет все тело и воспаляет круг жизни, будучи сам воспаляем от геенны.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37091, 'rst66', 'jas', 3, 5, 'так и язык &mdash; небольшой член, но много делает. Посмотри, небольшой огонь как много вещества зажигает!');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37090, 'rst66', 'jas', 3, 4, 'Вот, и корабли, как ни велики они и как ни сильными ветрами носятся, небольшим рулем направляются, куда хочет кормчий;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37089, 'rst66', 'jas', 3, 3, 'Вот, мы влагаем удила в рот коням, чтобы они повиновались нам, и управляем всем телом их.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37088, 'rst66', 'jas', 3, 2, 'ибо все мы много согрешаем. Кто не согрешает в слове, тот человек совершенный, могущий обуздать и все тело.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37087, 'rst66', 'jas', 3, 1, 'Братия мои! не многие делайтесь учителями, зная, что мы подвергнемся большему осуждению,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37086, 'rst66', 'jas', 2, 26, 'Ибо, как тело без духа мертво, так и вера без дел мертва.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37085, 'rst66', 'jas', 2, 25, 'Подобно и Раав блудница не делами ли оправдалась, приняв соглядатаев и отпустив их другим путем?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37084, 'rst66', 'jas', 2, 24, 'Видите ли, что человек оправдывается делами, а не верою только?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37083, 'rst66', 'jas', 2, 23, 'И исполнилось слово Писания: «веровал Авраам Богу, и это вменилось ему в праведность, и он наречен другом Божиим».');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37082, 'rst66', 'jas', 2, 22, 'Видишь ли, что вера содействовала делам его и делами вера достигла совершенства?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37081, 'rst66', 'jas', 2, 21, 'Не делами ли оправдался Авраам, отец наш, возложив на жертвенник Исаака, сына своего?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37080, 'rst66', 'jas', 2, 20, 'Но хочешь ли знать, неосновательный человек, что вера без дел мертва?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37079, 'rst66', 'jas', 2, 19, 'Ты веруешь, что Бог един: хорошо делаешь; и бесы веруют, и трепещут.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37078, 'rst66', 'jas', 2, 18, 'Но скажет кто‐нибудь: «ты имеешь веру, а я имею дела»: покажи мне веру твою без дел твоих, а я покажу тебе веру мою из дел моих.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37077, 'rst66', 'jas', 2, 17, 'Так и вера, если не имеет дел, мертва сама по себе.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37076, 'rst66', 'jas', 2, 16, 'а кто‐нибудь из вас скажет им: «идите с миром, грейтесь и питайтесь», но не даст им потребного для тела: что пользы?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37075, 'rst66', 'jas', 2, 15, 'Если брат или сестра наги и не имеют дневного пропитания,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37074, 'rst66', 'jas', 2, 14, 'Что&#769; пользы, братия мои, если кто говорит, что он имеет веру, а дел не имеет? может ли эта вера спасти его?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37073, 'rst66', 'jas', 2, 13, 'Ибо суд без милости не оказавшему милости; милость превозносится над судом.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37072, 'rst66', 'jas', 2, 12, 'Та&#769;к говорите и та&#769;к поступайте, как имеющие быть судимы по закону свободы.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37071, 'rst66', 'jas', 2, 11, 'Ибо Тот же, Кто сказал: «не прелюбодействуй», сказал и: «не убей»; посему, если ты не прелюбодействуешь, но убьешь, то ты также преступник закона.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37070, 'rst66', 'jas', 2, 10, 'Кто соблюдает весь закон и согрешит в одном чем‐нибудь, тот становится виновным во всем.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37069, 'rst66', 'jas', 2, 9, 'Но если поступаете с лицеприятием, то грех делаете и перед законом оказываетесь преступниками.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37068, 'rst66', 'jas', 2, 8, 'Если вы исполняете закон царский, по Писанию: «возлюби ближнего твоего, как себя самого», &mdash; хорошо делаете.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37067, 'rst66', 'jas', 2, 7, 'Не они ли бесславят доброе имя, которым вы называетесь?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37066, 'rst66', 'jas', 2, 6, 'А вы презрели бедного. Не богатые ли притесняют вас, и не они ли влекут вас в суды?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37065, 'rst66', 'jas', 2, 5, 'Послушайте, братия мои возлюбленные: не бедных ли мира избрал Бог быть богатыми верою и наследниками Царствия, которое Он обещал любящим Его?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37064, 'rst66', 'jas', 2, 4, 'то не пересуживаете ли вы в себе и не становитесь ли судьями с худыми мыслями?');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37063, 'rst66', 'jas', 2, 3, 'и вы, смотря на одетого в богатую одежду, скажете ему: «тебе хорошо сесть здесь», а бедному скажете: «ты стань там» или «садись здесь, у ног моих»,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37062, 'rst66', 'jas', 2, 2, 'Ибо, если в собрание ваше войдет человек с золотым перстнем, в богатой одежде, войдет же и бедный в скудной одежде,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37061, 'rst66', 'jas', 2, 1, 'Братия мои! имейте веру в Иисуса Христа нашего Господа славы, невзирая на лица.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37060, 'rst66', 'jas', 1, 27, 'Чистое и непорочное благочестие пред Богом и Отцом есть то, чтобы призирать сирот и вдов в их скорбях и хранить себя неоскверненным от мира.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37059, 'rst66', 'jas', 1, 26, 'Если кто из вас думает, что он благочестив, и не обуздывает своего языка, но обольщает свое сердце, у того пустое благочестие.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37058, 'rst66', 'jas', 1, 25, 'Но кто вникнет в закон совершенный, <i>закон</i> свободы, и пребудет в нем, тот, будучи не слушателем забывчивым, но исполнителем дела, блажен будет в своем действовании.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37057, 'rst66', 'jas', 1, 24, 'он посмотрел на себя, отошел и тотчас забыл, каков он.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37056, 'rst66', 'jas', 1, 23, 'Ибо кто слушает слово и не исполняет, тот подобен человеку, рассматривающему природные черты лица своего в зеркале:');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37055, 'rst66', 'jas', 1, 22, 'Будьте же исполнители слова, а не слышатели только, обманывающие самих себя.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37054, 'rst66', 'jas', 1, 21, 'Посему, отложив всякую нечистоту и остаток злобы, в кротости примите насаждаемое слово, могущее спасти ваши души.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37053, 'rst66', 'jas', 1, 20, 'ибо гнев человека не творит правды Божией.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37052, 'rst66', 'jas', 1, 19, 'Итак, братия мои возлюбленные, всякий человек да будет скор на слышание, медлен на слова, медлен на гнев,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37051, 'rst66', 'jas', 1, 18, 'Восхотев, родил Он нас словом истины, чтобы нам быть некоторым начатком Его созданий.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37050, 'rst66', 'jas', 1, 17, 'Всякое даяние доброе и всякий дар совершенный нисходит свыше, от Отца светов, у Которого нет изменения и ни тени перемены.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37049, 'rst66', 'jas', 1, 16, 'Не обманывайтесь, братия мои возлюбленные.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37048, 'rst66', 'jas', 1, 15, 'похоть же, зачав, рождает грех, а сделанный грех рождает смерть.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37047, 'rst66', 'jas', 1, 14, 'но каждый искушается, увлекаясь и обольщаясь собственною похотью;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37046, 'rst66', 'jas', 1, 13, 'В искушении никто не говори: «Бог меня искушает»; потому что Бог не искушается злом и Сам не искушает никого,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37045, 'rst66', 'jas', 1, 12, 'Блажен человек, который переносит искушение, потому что, быв испытан, он получит венец жизни, который обещал Господь любящим Его.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37044, 'rst66', 'jas', 1, 11, 'Восходит солнце, <i>настает</i> зной, и зноем иссушает траву, цвет ее опадает, исчезает красота вида ее; так увядает и богатый в путях своих.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37043, 'rst66', 'jas', 1, 10, 'а богатый &mdash; унижением своим, потому что он прейдет, как цвет на траве.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37042, 'rst66', 'jas', 1, 9, 'Да хвалится брат униженный высотою своею,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37041, 'rst66', 'jas', 1, 8, 'Человек с двоящимися мыслями не тверд во всех путях своих.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37040, 'rst66', 'jas', 1, 7, 'Да не думает такой человек получить что‐нибудь от Господа.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37039, 'rst66', 'jas', 1, 6, 'Но да просит с верою, нимало не сомневаясь, потому что сомневающийся подобен морской волне, ветром поднимаемой и развеваемой.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37038, 'rst66', 'jas', 1, 5, 'Если же у кого из вас недостает мудрости, да просит у Бога, дающего всем просто и без упреков, &mdash; и дастся ему.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37037, 'rst66', 'jas', 1, 4, 'терпение же должно иметь совершенное действие, чтобы вы были совершенны во всей полноте, без всякого недостатка.');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37036, 'rst66', 'jas', 1, 3, 'зная, что испытание вашей веры производит терпение;');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37035, 'rst66', 'jas', 1, 2, 'С великою радостью принимайте, братия мои, когда впадаете в различные искушения,');",
"INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (37034, 'rst66', 'jas', 1, 1, 'Иаков, раб Бога и Господа Иисуса Христа, двенадцати коленам, находящимся в рассеянии, &mdash; радоваться.');",
