            "INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (40234, 'ubio', 'gen', 1, 31, 'И увидел Бог все, что Он создал, и вот, хорошо весьма. И был вечер, и было утро: день шестой.');",
            "INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (40235, 'ubio', 'gen', 2, 1, 'Так совершены небо и земля и все воинство их.');",
            "INSERT INTO bible_verses (id, translation_code, book_code, chapter, verse_number, verse_text) VALUES (40236, 'ubio', 'gen', 2, 2, 'И совершил Бог к седьмому дню дела Свои, которые Он делал, и почил в день седьмой от всех дел Своих, которые делал.');",
